INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(280,'P','2007-11-22',0,NULL,'Note','Чудо творить Миколай','<p>Президент України Віктор Ющенко ініціює проведення акції &laquo;Чудо творить Миколай&raquo;, щоб привернути увагу до потреб дітей з багатодітних родин, сиріт та дітей, позбавлених батьківського піклування.</p>\r\n<p>&nbsp;</p>','<p>Президент України Віктор Ющенко ініціює проведення акції &laquo;Чудо творить Миколай&raquo;, щоб привернути увагу до потреб дітей з багатодітних родин, сиріт та дітей, позбавлених батьківського піклування.</p>\r\n<p>&nbsp;</p>\r\n<p>&laquo;Хрещатик&raquo; повідомив, що Глава держави наголосив про це учора у Київській області під час відвідання Центру соціальної опіки дитини &laquo;Отчий дім&raquo; однойменної Міжнародної благодійної фундації.</p>\r\n<p>&nbsp;</p>\r\n<p>У рамках акції &laquo;Чудо творить Миколай&raquo;, яка стартувала 21 листопада і триватиме до 19 грудня, такі діти по всій Україні отримають президентські подарунки, в комплект яких входитимуть: теплий одяг, курточка, шкіряні чобітки тощо. Сьогодні Президент вручив подарунки вихованцям Центру &laquo;Отчий дім&raquo;.</p>\r\n<p>&nbsp;</p>\r\n<p>- Моя мета через це благочинство привернути увагу українського бізнесу, влади, людей доброї волі &ndash; давайте звернемо увагу на цих дітей, &ndash; сказав Віктор Ющенко. Глава держави також висловив переконання, що в нашій країні Акція без сумніву має знайти підтримку серед широких кіл громадськості.</p>\r\n<p>&nbsp;</p>\r\n<p>Віктор Ющенко також оглянув навчальні та житлові приміщення Центру, поспілкувався з його вихователями та дітьми. Президент Міжнародної благодійної фундації Роман Корнійко розповів Вікторові Ющенку про діяльність Центру &laquo;Отчий дім&raquo; протягом останніх трьох років. За його словами, сьогодні робота з дітьми здійснюється у кілька етапів, серед яких &ndash; спілкування з так званими &laquo;вуличними дітьми&raquo; безпосередньо на вулицях, первинна реабілітація дітей у навчальному таборі, духовна та соціальна реабілітація у Центрі опіки та перебування дітей у Центрі сімейного виховання. На сьогодні в закладі перебуває 63 дитини.</p>\r\n<p>&nbsp;</p>',NULL,NULL,236,NULL,1,30),
(281,'P','2007-11-30',0,NULL,'Note','24 листопада у поминальному Гаю постав Хрест і запалало 500 лампадок. Продовження розповіді Віталя Соболівського','<p><a href=\"image,1223,DSC_lampady5_0.jpg\"><img align=\"left\" src=\"image,1223,150,DSC_lampady5_0.jpg\"></a>Продовження розповіді Віталя Соболівського. Початок у рубриці Газета \"Оранта\"<p/>','<p><a href=\"image,1223,DSC_lampady5_0.jpg\"><img align=\"left\" src=\"image,1223,250,DSC_lampady5_0.jpg\"></a>Продовження розповіді Віталя Соболівського. Початок у рубриці Газета \"Оранта\"<p/><p>Отець Ігор Онишкевич, настоятель церкви св. Миколая, в цей час відслужив панахиду по Жертвах Голодомору, керував церемонією покладання пластунами і парафіянами 500 лампадок у поминальному гаю. <p/><p>Потім смиренно чекав із почтом парафіян з хоругвами, щоб освятити Хрест, щойно він постане на цьому святому місці. Його присутність і молитва надавала усім нам впевненості. Гадаю, коли б він мав змогу супроводжувати нас в дорозі, вантажівки б на виходили з ладу.<p/>',NULL,NULL,243,NULL,1,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(282,'P','2008-10-07',1,NULL,'Note','Першоієрархи Церков та Міністр охорони здоров\'я обговорили стратегію протидії абортам в Україні  ','<p style=\"text-align: justify;\"><span style=\"font-size: 10pt; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; color: #403152;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">7 жовтня 2008 року у приміщенні Василіянського монастиря у Києві під головуванням Глави УГКЦ Блаженнішого Любомира відбулася зустріч керівників Церков України в форматі Наради представників християнських Церков України.</span></span><br /></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #403152;\"><img style=\"float: right;\" src=\"image,1445,RTEmagicC_nxt.jpg.jpg\" alt=\"\" />7 жовтня 2008 року у приміщенні Василіянського монастиря у Києві під головуванням Глави УГКЦ Блаженнішого Любомира відбулася зустріч керівників Церков України в форматі Наради представників християнських Церков України. Одним із основних питань зустрічі була разробка стратегії християн у захисті життя (протидії абортам) в України, до обговорення якого було запрошено Міністра охорони здоров\'я України В. Князевича та відповідальних представників міністерства, &ndash; повідомляють в Інституті релігійної свободи. </span></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #403152;\">У своєму вступному слові Василь Князевич наголосив на важливості морально-етичного виховання молоді ще з шкільного віку, зважаючи на негативні тенденції, пов\'язані зі зменшенням вікових показників початку молодими людьми статевого життя та завагітнення. У свою чергу першоієрархи Церков акцентували увагу на потребі вироблення принципів на захист цінності життя ненароджених дітей, їх пропагування під час підготовки медичних працівників та безпосередньо у медичній практиці. Духовні лідери країни також зауважили на необхідності подолання духовно-моральної кризи у суспільстві, яка є першопричиною частих випадків безвідповідального відношення чоловіка і жінки до стосунків між собою та у подальшому до життя їх ненародженої дитини. Певним підсумком розгляду цієї теми стало рішення про необхідність створення робочого органу, у який мали б увійти представники зацікавлених міністерств та відомств з одного боку, і представники християнських Церков з другого, для вироблення пропозицій щодо реалізації в Україні програми \"За життя\".</span></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #403152;\">Крім цього,</span><span style=\"color: #403152;\"> </span><span style=\"color: #403152;\">під час</span><span style=\"color: #403152;\"> </span><span style=\"color: #403152;\">зустрічі глави Церков обмінялися думками щодо існуючої суспільно-політичної ситуації в Україні. За наслідками обговорення було ухвалено спільне звернення до українського народу, в якому було висловлено погляд християнських Церков на проблему загострення політичного суперництва в Україні.</span></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #403152;\">Учасники зустрічі також обговорили актуальні питання державно-церковних відносин, наявні проблеми законодавчого забезпечення суспільно-важливої діяльності Церков, зокрема стану виконання урядового доручення від 15.04.2008 року (за результатами зустрічі Прем\'єр-міністра України з Всеукраїнською Радою Церков і релігійних організацій). Учасники зустрічі також узгодили заходи щодо спільного вшанування жертв Голодоморів в Україні.</span></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #403152;\">Іншими темами зустрічі були питання інтенсифікації співпраці християнських Церков з Міністерством </span><span style=\"color: #403152;\">охорони навколишнього природного середовища України</span><span style=\"color: #403152;\"> у справі екології, та актуалізації співпраці з Міністерством внутрішніх справ України головно у питаннях безпеки дорожнього руху. Не залишили поза своєю увагою учасники засідання і питання переслідування християн в Індії</span><span style=\"color: red;\">*</span><span style=\"color: #403152;\">. Ієрархи висловили свою стурбованість цією ситуацією та свою солідарність із тими, хто зараз терпить через свою віру в Ісуса Христа.</span></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #403152;\">Зустріч завершилась спілкуванням у неформальній обстановці у дружній атмосфері. У зустрічі глав Церков взяли участь керівники та повноважні представники християнських Церков України, зокрема Української Православної Церкви Київського Патріархату, Української Православної Церкви, Української Греко-Католицької Церкви, Римсько-Католицької Церкви в Україні, Всеукраїнського Союзу Об\'єднань євангельських християн - баптистів, Церкви християн віри євангельської України, Української Християнської Євангельської Церкви та Української Лютеранської Церкви.</span></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #403152;\">Департамент інформації УГКЦ<br /> (За матеріалами Інституту релігійної свободи)</span><span style=\"color: #403152;\"> </span></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: red;\">*</span><span style=\"color: #403152;\"> Понад чотири тижні тому розпочалися погроми та агресії проти християн в Індії, передусім у штаті Орісса. Згідно із повідомленням прес-агентства AsiaNews переслідування поширились на інші індійські штати, а тільки у штаті Орісса вбито 37 християн, вандалами спалено і сплюндровано сотні церков, серед яких і кілька катедральних храмів, спалено понад 4 000 будинків, в яких жили християни, нараховується більше, ніж 50 000 біженців, які переховуються у лісах. Найчастіше жертвами нападів стають священики та сестри-монахині, а також переслідують їхні родини. Переслідування зазнають також і протестанти, недавно вбито двох протестантських пасторів</span></span></span></p>',NULL,NULL,465,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(283,'P','2008-10-10',1,NULL,'Note','Робоча зустріч головних в’язничних капеланів країн-учасниць Європейської філії ICCPPC  ','<p style=\"text-align: justify;\"><span style=\"color: #000000;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">9 жовтня 2007 року у центрі підготовки Польської в&rsquo;язничної служби у Попово, що під Варшавою, відбулася робоча зустріч представників країн, які здебільшого входять до Європейської філії Міжнародної католицької комісії душпастирської опіки у тюрмах.</span> </span></span></p>','<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #000000;\">9 жовтня 2007 року у центрі підготовки Польської в&rsquo;язничної служби у Попово, що під Варшавою, відбулася робоча зустріч представників країн, які здебільшого входять до Європейської філії Міжнародної католицької комісії душпастирської опіки у тюрмах. Цю регіональну конференцію головних в&rsquo;язничних капеланів очолив віце-президент ICCPPC о. Браян Ґованс. Серед запрошених був також о. Едвард Гаґес, керівник Північноамериканської філії ICCPPC.</span></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #000000;\"><img style=\"float: right;\" src=\"image,1449,RTEmagicC_pl_kapelany2_02.jpg.jpg\" alt=\"\" />Темою конференції стали три сфери розвитку діяльності ICCPPC: </span></span></span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #000000;\">богословська &ndash; щодо основ душпастирської праці священиків і мирян в умовах ізоляції засуджених за скоєні злочини;</span></span></span></li>\r\n</ul>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #000000;\">юридична &ndash; щодо впливу Церкви на формування пенітенціарного права;</span></span></span></li>\r\n</ul>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #000000;\">соціальна &ndash; про служіння Церкви щодо відродження справедливості, збереження родин та повернення звільнених після ув\'язнення до суспільного життя.</span></span></span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #000000;\">Окрім того, зустріч стала нагодою для живого обміну практичними досягненнями та пастирськими потребами, що викликані зростанням числа мігрантів, які перебувають за ґратами.</span></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #000000;\">У рамках програми учасники зустрічі ICCPPC відвідали Освенцім-Біркенау, де вшанували пам\'ять мільйонів жертв нацизму, серед яких і св. Максиміліана Кольбе. Гості відвідали пенітенціарні установи в околицях Варшави, ознайомилися з душпастирським служінням, діяльністю Польського в&rsquo;язничного братства, а також двох закладів постпенітенціарної опіки (для осіб після звільнення) в околицях Вроцлава й Ґданська, що розвинули діяльність упродовж років сучасного відродження Польщі. Щоденну працю з особами, звільненими з ув\'язнення,&nbsp; здійснюють священики, християнські психологи й педагоги, соціальні працівники та волонтери-миряни.</span></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #000000;\"><img style=\"float: left;\" src=\"image,1449,RTEmagicC_pl_kapelany1_02.jpg.jpg\" alt=\"\" />Спеціальною нагодою для цієї пасторальної конференції став щорічний збір близько двохсот польських католицьких капеланів під проводом головного капелана Павла Войтаса. Участь у роботі конференції узяв Примас Польщі Кардинал Йозеф Глемп. Деякі сесії душпастирства ICCPPC проводилися спільно з польськими капеланами для представлення капеланського служіння кожної з країн. Українську Греко-Католицьку Церкву представляв диякон Костянтин Пантелей, референт душпастирства у пенітенціарній системі України. Особливої уваги був удостоєний документальний фільм добродія Анатолія Цибана про прощу Пенітенціарної служби України до Зарваниці.<br />На думку диякона Костянтина, найбільш цікавим досвідом польських колег є участь засуджених, що відбули більшу частину терміну позбавлення волі, у прощах до святих місць. В одній з недавніх прощ, за спеціальними звільненнями директорів тюрем, брали участь численні засуджені. В Україні ж серед пріоритетів розвитку пастирського служіння Церкви є увага до адаптації осіб, звільнених з ув\'язнення. </span></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #000000;\">&nbsp;</span></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #000000;\"><em>Додаток</em> </span></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #000000;\">Поміж конгресами ICCPPC, які відбуваються раз на чотири роки, для регулярного обміну досвідом та задумами щодо пенітенціарного служіння Церкви відбуваються робочі регіональні зустрічі представників країн-учасниць організації. 2007 року в Римі відбувся ХІІ конгрес Міжнародної католицької комісії в\'язничної пасторальної опіки (ICCPPC). Конгрес закликав синоди й конференції єпископів Католицької Церкви усього світу звернути увагу на великі проблеми тюрем, пов\'язані з перенаселенням, насиллям, дітьми у конфлікті із законом, особами із психічними розладами, людьми похилого віку, нужденними, потребами опіки в\'язнів з інших країн. </span></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #000000;\">У зверненні до капеланів Святіший Отець Венедикт XVI зокрема наголосив: &laquo;Церква намагається служити усім людям, як чинив Господь, особливо уважний до найбільш нужденних, із якими Ісус ототожнював себе: &laquo;Я був у темниці, і ви відвідали мене&raquo; (Mт. 25.36). Це покликання глибоко закорінене в науці Церкви. Євангелізація ув\'язнених &ndash; це тривала подорож особистого навернення. Частина пасторальної місії Церкви &ndash; захищати права людини й долати різницю між літерою й духом цих прав... Збільшення кількості тяжких злочинів й навіть актів тероризму не означає, що від засуджених суспільство має відгородитися мурами й &laquo;викинути ключі&raquo;, ставитися без пошани до їхньої людської гідності&raquo;. </span></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #000000;\">Департамент інформації УГКЦ</span></span></span></p>',NULL,NULL,469,5,1,43);
