INSERT INTO `publications_publication` VALUES
(1511,'P','2014-03-13',1,NULL,'Rss','Той, що біжить по воді (14 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5617,200,image.jpg\" />Жив собі місіонер, який уславився на весь християнський світ тим, що привів у лоно Церкви чимало людей, відвідуючи найглухіші куточки світу. Одного разу його корабель пристав до маленького острівця, де жив тільки один чоловік. Місіонера вразили його ясні очі, але ще більше те, що той нічого не чув про Бога.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1932:to-shcho-bizhyt-po-vodi-14-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5621,NULL,1,1,26),
(1512,'P','2014-03-13',1,NULL,'Rss','«Бог є з нами і промовляє до нас через Єпископа Рима».','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5617,200,z_frantsyskom3.jpg\" />Святіший Отче, 13 березня цього року виповнюється перша річниця Вашого понтифікату. Весь Божий народ з великою радістю згадує той день, коли Архиєпископ з Буенос- Айреса, «майже з кінця світу», був покликаний Господом для виконання служіння апостола Петра.</p>\n<p>Сьогодні Церква Христова хоче подякувати Господу за багаті божественні ласки, даровані вірним у Вашій особі. Ваша Святосте, ми глибоко вдячні Вам за Вашу батьківську турботу та невпинне служіння<em> Ad majorem Dei gloriam</em> (на славу Божої величі) і на благо Церкви Христової.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1931:2014-03-13-20-21-33&catid=28:tema-nomera-cat&Itemid=2',5620,NULL,1,1,26),
(1513,'P','2014-03-13',1,NULL,'Note','Лист6 ув\'язненої Ірини В., качанівська виправна колонія КВК-54, м.Харків','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вона живе у Божих палатах</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Одіта у розкішних білих шатах</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А ще вона Ісусику служить</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хмаринка Його дуже-дуже любить</span></p>','<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Слава Ісусу Христу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З найкращими вітаннями та своїм шануванням до Вас і Вашої церковної громади пише сестра Ірина.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дорогий о.Ігор, пишу Вам напередодні Свята Святого Миколая, тому що висилаю 2 вірша, один на російській мові, другий я написала на українській дитячий віршик &laquo;Хмаринка&raquo;. З цього віршика можна поставити сценку, в якій прийматимуть участь дітки від 3-х до 7-ми років. Сценка може бути і на свято Миколая і на Різдво. Це мій подарунок до святого Миколая.</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Хмаринка</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Живе собі на небі хмаринка</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Маленька жартівниця, пушинка</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">То з подружками біга, стрибає</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">То з сонечком у жмурки гуляє</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">То губки надува, вередує</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">То слізоньки пуска, капризує</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Вона живе у Божих палатах</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Одіта у розкішних білих шатах</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А ще вона Ісусику служить</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Хмаринка Його дуже-дуже любить</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ісусику престол прикрашає</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Собою Йому ніжки зігріває</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У неї чистота і порядок,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Все зроблено вміло і до ладу,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вона така вправна майстриня,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Зірочок у неї повна скриня,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Коли сонечко спатоньки лягає,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Она зіроньки по небу розкладає.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Такі вже творить гарні малюнки,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Склдає з них чудові візерунки,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; І дружить вона з місяцем ясним,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Удвох співають пісеньки прекрасні,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Веселі співаночки чудові,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Для діточок усіх колискові.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Живе собі на небі хмаринка,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Маленьк веселунка, смішинка,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Виводить хороводи, стрибає,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Усіх вона добряче розважає,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В Ісусика їй добре живеться</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Тому вона радіє сміється</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>М</strong><strong>ы с тобою дети Бога</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мы с тобою дети Бога</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нам светит чистая дорога</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Его пути</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мы Его закон читаем</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мы с любовью принимая</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В сердце чтим.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Позади у нас печали, слёзы</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Горькие рыданья, всё позади</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Предоставь всё в руки Бога</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Пусть уйдёт твоя тревога навсегда</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Он за нас всё порешает на места свои расставит.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Он судья!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А ты приди к Нему раскайся</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У грехах своих признайся расскажи</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ведь Он примет тебя знаю</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Тихо скажет: &laquo;Я прощаю, ступай, иди!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Будь смиренна и послушна</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Будь добра, великодушна</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Впредь не греши!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Прогони свой страх небойся</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Одному Ему доверься,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вперёд иди</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мы с тобою дети Бога</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нам светит чистая дорога</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Его пути!&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; З повагою сестра Ірина &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 26.11.2013 р.</span></p>',NULL,5624,5,1,NULL,19);
INSERT INTO `publications_publication` VALUES
(1514,'P','2014-03-13',1,NULL,'Rss','«Якщо українське суспільство хоче бути демократичним, то має пам’ятати сталінські репресії»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5617,200,hqdefault.jpg\" />Днями (8–10 березня) Українська Греко-Католицька Церква згадувала сумну подію в своїй історії – 68-ту річницю Львівського псевдособору, на якому за наказом Сталіна було ліквідовано УГКЦ. Отець Полікарп Марцелюк, ЧСВВ, очолює Місію «Постуляційний центр беатифікації УГКЦ». Його завдання – супроводжувати процеси беатифікації на парафіяльному рівні УГКЦ та поширення славної пам’яті ісповідників віри. Отець Полікарп відповів на запитання Департаменту інформації УГКЦ.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1930:2014-03-13-20-15-33&catid=27:gk-v-ukrajini-cat&Itemid=57',5619,NULL,1,1,26),
(1515,'P','2014-03-13',1,NULL,'Note','Звіт №2 про НАДХОДЖЕННЯ  ЛИСТІВ 15.02.2014 р. — 31 лист','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... хочу подякувати вас за вашу допомогу моїм діткам. І донька Поліночка, і синок Євген отримали посилочки з подарунками на день св.Миколая. Я бажаю вам здоровя всім за вашу сумлінну працю в такий тяжкий час, за те що не залишаєте нас без уваги всіх хто звертається до вас за допомогою. Хай Господь вас благословляє на всіх ваших життєвих шляхах&rdquo;</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Листи від ув\'язнених </span></em></strong><strong><em><span style=\"text-decoration: underline;\">&nbsp;(з них вперше звернулись 3особи)</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Чернігівська виправна колонія ЧВК-44 (м.Чернігів) &mdash; </strong><strong>15</strong><strong> лист</strong><strong>ів</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Кача</strong><strong>нівська виправна колонія </strong><strong>К</strong><strong>ВК-</strong><strong>5</strong><strong>4 (м.</strong><strong>Харк</strong><strong>ів) &mdash; </strong><strong>6</strong><strong> лист</strong><strong>ів</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Збаразька виправна колонія ЗВК-63 (Тернопільська обл.) &ndash; 2 листи</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Селезнівська виправна колонія СВК-143 (Луганська обл.) </strong><strong>&mdash;</strong><strong> 1 лист</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">&nbsp;</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Подяка за посилки для дітей &ndash;6 листів</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">&nbsp;</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Листи звільнених з ув</span></em></strong><strong><em><span style=\"text-decoration: underline;\">&rsquo;</span></em></strong><strong><em><span style=\"text-decoration: underline;\">язнення &ndash; 1 лист</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Я сейчас нахожусь на сильных препаратах и с Господней помощью я живу и дышу. <strong>Я стала на терапию по ВИЧу. Если есть у Вас возможность помочь мне с медикаментами, то буду очень Вам благодарна. И если будет возможность вышлите пожалуста &laquo;Крестика&raquo;...</strong> Здесь все ходят на собрания. Но я один раз пошла к ним и мне превиделось нечто ужасное от чего стало плохо. Я непереношу лицемерия и то по сколько там всё наиграно. Вот я больше не хожу туда. Я с Господом и своими поступками творю добро здесь... Как Вы думаете я правильно поступаю?.. Отец Игорь, можно ли мне будет с детками, когда я освобожусь приехать к Вам, в церковь?&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ірина Р. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Хочу Вас поблагодарить от всего чистого сердца за то, что Вы сделали для меня. За то, что Вы помогли мне найти мою дочь и помирить с ней.</strong> <strong>Скоро я освобождаюсь в этом году 11 июня. Очень Вас прошу если у Вас есть возможность помогите мне пожалуста вещами и деньгами на освобождение</strong>. Я выхожу без квартиры, без паспорта, я выхожу в 56 лет, а через год мне на пенсию&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Надія П. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>помогите </strong><strong>пожалуста лекарством</strong> очень заболела, а у нас в сан части нету таких лекарств. И мне некому обратиться сама я сирота, а здесь работаю намашины где сквозняки и очень притянуло. Я Вам высилаю рецеп на лекарство, если у Вас не получеться, очень прошу вышлить потом рецеп назад&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Аліна Б. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>хочу подякувати вас за вашу допомогу моїм діткам. І донька Поліночка, і синок Євген отримали посилочки з подарунками на день св.Миколая.</strong> Я бажаю вам здоровя всім за вашу сумлінну працю в такий тяжкий час, за те що не залишаєте нас без уваги всіх хто звертається до вас за допомогою. <strong>Хай Господь вас благословляє на всіх ваших життєвих шляхах</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Олена П. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... мне 57 лет строк 8 лет. <strong>Сильно больна, я перенисла инсульд, парализована речь, нарушена кординация тела, рук и ног, болное сердечко, сердечная недостаточность. Сама гепертоник больная вся внутренность. Я одинока и мне некому больше обратиться</strong>. И на даный момент я нахожуся в отряде инвалидов пинсионеров, у миня вторая група инвалидности. Я молитвенно обращаюся к Вам за вашей помощью. <strong>Пожалуста помогите деньгами, на лекарство.</strong> Лекарство дорогое и мне нехватает денег его купить&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Любов П. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... я инвалид ІІ группы (<strong>трофическая язва на ноге</strong>), на ногу практически стать немогу, постоянная боль, вообщем постоянно пью обезбаливающие. В лагере медикаментов тех, что мне надо нет, а больше помочь мне некому. Я очень Вас <strong>прошу и умоляю помочь мне с медикаментами</strong>. Я очень сильно нуждаюсь в них, <strong>высылаю Вам рецепт. Хочу Вас попросить, чтобы Вы помолились за моё выздоровление и за женщин находящихся в этих местах.</strong> Спасибо Вам большое за Вашу поддержку и сострадание. <strong>Если есть возможность, вышлите конверты и гигиенические средства</strong>, ещё раз огромное Вам спасибо&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ірина Н. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Получила Ваше письмо с иконками за которое &ndash; сердечно благодарю</strong>. Я вообще люблю иконы. Я могу длительное время смотреть и молиться про себя, про людей, про страну нашу, размышляю о Боге, о Богородице, о Святых. Высылаю Вам стих о Богородице, это не я сочинила, просто он мне нравится очень и я решила написать вам его (<em>див.листи ув</em><em>&rsquo;</em><em>язнених</em>). <strong>Посылку для детей получили. Низкий Вам поклон. Всё очень понравилось. О Вас и прихожанах церкви молюсь. Прошу Ваших сердечных и святых молитв обо мне грешной...</strong> Я нуждаюсь в тетрадке, клей нужен, очки и кипятильник, если есть возможность &ndash; помогите Христа ради, беруши (для ушей, т.к. в секции где мы живём более 60 человек и вечерами сложно молиться и читать Библию, т.к. громкие разговоры вокруг)&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Валерія О. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Если у Вас есть возможность, то я Вам буду очень благодарна за лекарства</strong>, потому как обратиться я больше не знаю к кому. Благодарю Бога за таких людей, как Вы&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Тетяна П. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>я просила Вас помочь мне лекарствами, Вы написали мне выслать рецепт. В этом письме я его высылаю и очень прошу по возможности помогите пожалуйста</strong>. Я очень благодарна Вам за то, что Вы молитесь за здоровье моего сына Вячеслава и за моё здоровье&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ольга Д. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Я Вам дуже вдячна за помощь діточок</strong>, шо для Вас мої діти небайдужі. Дуже переживаю і скучаю за ними. Мої кровинки маленькі лишились без батька скучають за мамой. Меньшой донькі Машенькі 8 лютого день народження. С Вашой помощю Ви сможете привітати від мами Люби. Я дуже вдячна Вам і церкві що допомагаєте дітям сиротам. Я хожу в церковь в православную и всегда молюсь за всех&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Любов Д. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Я дуже і дуже чекатиму Вас, для мене всі ці дні будуть дуже радісні, з надією Вас побачити. Це дуже мені потрібно. Я обміркувала свою прошлу жизнь і на перід хочу, щоб було усе добре, Господнє, не вредне для мене і для окружающих. Щоб не було ні в кого лихого задуму, не було рани на душі. <strong>Все яке я прожила життя &ndash; це дуже і дуже болюча і слізна, пекуча і тяжка рана</strong>. І немає до когось порадиться, чесно, открито поговорити.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Поздоровляю Вас з Різдвом Христовим, миру, злагоди, любові, поваги Вам дорогеньки. Я дуже Вам вдячна і Вас чекатиму</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Наталія А. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Моему сыну 11 лет зовут Михаил болеет диабетом</strong> мать старенькая воспитывает сама тяжело ей... <strong>помогите чем сможете</strong> размер ноги 37, 38 рост 157... По возможности <strong>заменитель сахара для деабетикав</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Світлана С. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Я відбуваю покарання за скоєння важкого злочина. Срок 10 років. Просиділа 2 </strong>ще з Божою допомогою прошу сил і терпіння досидіти скільки буде Воля Божа. У мене двоє діточок:&rdquo;хлопчику 4 роки, а дівчинці 13 років. Вони знаходятся з моїм рідним братом. <strong>Звертаюся до Вас, тому що не маю належної допомоги із волі від рідних</strong>. А хороші люди порадили і дали Вашу адресу... Мені 34 роки, розкаююсь перед Богом за вчинення злочину та прошу прощення за те що недумаємо що робимо</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Рита З. (Качанівська ВК, м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Дуже дякую Вам за те, що Ви допомогли моїй дитині отримати подарунок від св.Миколая</strong>. Моє серце розривається від того що я не можу зробити сама своїй дитині подарунок, зказати йому хоть слово не то щоб поцілувати його обійняти... Відіслала вітання з новим роком своєму синочку але не знаю отримав він його чи ні бо в мене не має дозволу на листування з ним&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Тетяна Х. (Качанівська ВК, м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Поздравляю Вас со Светлыми праздниками Рождеством и Крещением Христовым!.. Я хочу Вас ещё раз поблагодарить за ваше внимание ко мне...</strong> Каждое утро я хожу в нашу церковь, молюсь и надеюсь на то, что всё в моей жизни изменится. <strong>В январе месяце &ndash; у меня уже есть половина срока</strong>, и это меня радует. Даст Бог может уйду скорее... Если у вас есть такая возможность, помогите мне, <strong>пришлите мне, пожалуйста, посылку с банными мыльными принадлежностями, средствами гигиены и что-нибудь сладкого, конфет или печенье</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Олена М. (Качанівська ВК, м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>отримала від Вас посилочку за яку Вам усім дуже-дуже іще раз дуже дякую</strong>. Хай Господь благословляє Ваші рученьки і ноги, щоб Ви здорові ходили і надалі з теплом і любов&rsquo;ю допомагали людям, які потребують Вашої допомоги. Мир Вашому дому, мир Вашим&nbsp; сім&rsquo;ям та Божого благословення&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ірина В. (Качанівська ВК, м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Я <strong>дуже вдячна за подарунки і низько вклоняюсь тим людям що допомагають нам, я знаю, що в вас багато є діток</strong> які потребують допомогу я завжди молюсь за них і за вашу працю. <strong>Я напишу вам вірш</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пане мій царю</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мій Иисус коханий</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Я схиляюся до ніг Твоїх</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З вічною подякою</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">За ту кров що капала</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І за прощення гріхів моїх.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Довго блукали ми</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Слухали лукавого</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Покривились наші путі</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А коли все згаснуло,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Зіронькою ясною</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти зійшов у нашому житті.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">За нас знеславлений</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Був життя позбавлений</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Він дав владу пекла зруйнувать</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Богом був воскрешений</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вище всіх піднесений</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І навіки нашим царем став.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вся слава Йому</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вся велич Йому</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Він один гідний хвалу всю прийняти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Він Цар над царями</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Він Пан над панами</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Його будем завжди прославляти&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Тетяна С. (Качанівська ВК, м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Выражаю Вам огромную благодарность за то, что Вы не забываете обо мне и заботитесь обо мне в то время, когда все меня бросили, и родные и близкие.</strong> Непрестанно всем сердцем благодарю Господа за Вас и за заботу и поддержку Вашу. Да возвратится к Вам всё то доброе что Вы дарите другим. Аминь! И впредь буду молиться о том, чтобы благодать Божья всегда пребывала с Вами. Аминь!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Благослови же Вас Господь</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На жизнь победную,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Чтоб дух Ваш мог торжествовать</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Над всеми бедами.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Благослови же Вас Господь</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На жизнь служения,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Чтоб битву с дьяволом вести</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Без поражения.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Благослови же Вас Господь</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На дело славное,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Чтоб мог ты мелкое забыть,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А помнить главное!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Благослови же Вас Господь</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На жизнь спасения.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Его над Вами будут пусть</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Благословения!!!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Благослови же Вас Господь...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дорогие братья и сестры! <strong>Существует ли у Вас литература Фомы Аквинского или Блаженного Августина,</strong> - где эти отцы Церкви говорят о проблеме боли и страданий, отпущенных нам людям здесь на земле. Хотелось-бы понять и найти ответы на волнующие вопросы дабы возрастать духовно, но для этого нужна духовная литература. Помогите мне Христа ради кто сможет, или житие святых есть у кого, я читаю и на старословянском свободно. Храни Вас Бог возлюбленные! Аминь!&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; С уважением к Вам&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; р.Б.Ира&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ірина Р. (Качанівська ВК, м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Получила Вашу посылку, как раз на рождество, большое Вам спасибо,</strong> и дай Бог здоровья... я даже расплакалась... <strong>Мне хотелось у Вас попросить мозольный пластырь</strong>, я так мучаюсь больно ходить. И если можно <strong>порошка, чая, кофе, сахара и сладенького. Я хотела у Вас попросить крестик и четки</strong> если можно&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Галина І. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Христос народився!!! Славімо Його!!!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">... <strong>Дорогий отче Ігорю, особисто Вам велика подяка від мене за дарунок (посилку) і всім хто брав участь у відправці. Щиро дякую за все!</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Я дуже рада що Ви є в мене і пишете мені, допомагаєте ліками і необхідним<strong>. Це велика підтримка для мене</strong>. Поки не листувалася з Вами, то повірте, мої погляди на все що бачила навколо себе були сірими і скучними, якщо можна так виразитись. Сьогодні, навпаки, <strong>чекаю Ваших листів з великою радістю</strong> читаю і деколи сльози надходять з моїх очей. Сльози і скорбі і радості. За все Слава Богу! <strong>Навчилась розуміти і відчула серцем і душею що я і тут не одна, зі мною Бог і допомагає мені</strong>. Молюся і прошу прощення, бо дуже є багато такого чого не розуміла раніше і мені казалось що все правильно, і я нічого поганого незробила. Але тут з Вами, дорогий отче, зрозуміла, що дуже помиляюсь. <strong>Завдяки літератури, яку одержувала і одержую з Вашої церкви почала подругому дивитись на життя.</strong> Все своє життя мені казалось що я вірую свято бо з дитячих років ходила з бабусею до церкви, але я ніколи не бачила стільки духовної літератури бо при Союзі її небуло. Але з Вашою допомогою все змінилось &ndash; читаю і дуже рада що Ви мені допомагаєте зрозуміти реальність в житті, а не мнима писанина про щастя на землі. <strong>Я почала задумуватись що прийде час і я покину цю земну суєту, а що тоді мене чекає там? </strong>І чи то правда де пишуть за ті муки які чекають наші душі там? Отакі думки постійно непокидають мене і стає страшно, бо я є грішниця, і прошусь у Господа простити мене бо не тямила що робила. Цей лист пишу Вам дорогі мої 7-го січня і описую все дуже чесно від всієї душі, все що відчуваю. Сьогодні і у нас Святе рождество Христове. Ходять з відділення в відділення співають колядки, пригощаємо одна одну хто чим може... Ну на цьому буду закінчувати.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Щиро дякую за посилку за медикаменти дуже велика Вам подяка<strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Любов С. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Сказать правду <strong>я стихи неумею сочинять,</strong> ведь это о Боге, вдруг что то нетак напишу. Вот <strong>знаю один стих</strong>, именно из жизни, может он плохой, <strong>может не о Боге, но эти слова глубоко затрагивают душу</strong>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Весь мир &ndash; амбар поставщика,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Где всё так ветхо и прогнило,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Собрала времени рука</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">И в кучу мусора свалила.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Такой там находили хлам,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Добро и зло лежат там вместе,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">То здесь валяется, то там</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Куски любви, обрывки чести.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А гдето там в сыром углу</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Совсем-совсем на дальнем плане,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Кусочек правды на полу</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">И тот завёрнутый в обмане.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Я конечно прошу прощения, может не тот стих написала который бы вы хотели услышать, но он действительно из жизни, которую мы сделали сами&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Людмила Н. (Селезнівська ВК, Луганська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Пишу що <strong>я уже на волі </strong>проживаю в центрі відоптації &laquo;Світ надії&raquo;. Господи милосердний допомагає устраювать житьевий устрой&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Світлана К. (Центр адаптації &laquo;Світ надії&raquo;, м.Полтава)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&ldquo;</strong>Уважаемые добродеятели! Поздравляю Вас с Рождеством Христовым и Новым годом! Я очень хорошо провел каникулы. Только снега у нас не было. На день рождения мне подарили компьютер. Мы с бабушкой нашли в компьютере историю вашей церкви и посмотрели видео. <strong>Спасибо за подарок и поздравление.</strong><strong>Подарок </strong><strong>мне очень понравился</strong><strong>.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">До свидания, счастья, здоровья, успехов и Божей благодати<strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Людмила Б., м.Дніпропетровськ, Кирило-10р., син Тетяни Б., (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Шановні друзі! <strong>Ми щиро дякуємо Вам за те, що ви переймаєтесь долями дітей, матері яких знаходяться в колоніях.</strong> Женя вже 1,5 роки живе в нашій сімї. <strong>Наша сімя взяла на виховання 8 дітей, які не могли жити з батьками. Також маємо і своїх 6 дітей. У нас організовався дитячий будинок сімейного типу.</strong> По милості Божій діти всі ростуть і дякують Богу за все що мають. А головне мають батьків, які за них переживають. <strong>Женю ж мама його забере, як вийде на свободу. Ще раз щиро дякуємо, що ви займаєтесь цією Богоугодною справою</strong>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З повагою сімя К.&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Оксана К. Чернігівська обл., Євген-5р., син Олени П. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&ldquo;Я дуже Вам вдячна. Дякую Вам&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Лариса Ф. (Донецька обл., Аліна-2р., дочка Ольги Ш., Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Слава Ісусу Христу! <strong>Дякую Вам за посилку и</strong><strong> за листа. Дітям дуже сподобалось вони теж ходят в молитвений дом їм дуже подобается. Дуже вам дякуем</strong>. Хай хоронить Вас Господь. Вашу парафію и одельное спасибі о.Ігорю за добріе дела. Хай Бог Вас оберігае і благословляет.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">С ув.см.К.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Добрий день або вечір <strong>дякую вам за посилку вона нам дуже сподобалася</strong> і вам хочу дещо приклеїти...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Пасіба за посилку я хочу подякувати. І вона нам сильно подравилась.</strong> І я хочу тобі намалювати малюнок&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Вячеслав К., Київ.обл., Діана-9р., Олександра-7р., діти Інни К. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Добрый &nbsp;&nbsp; Вам день. <strong>Хочю Вам поблагодарить за посылочку.</strong> Извините пожалуста, что сразу не ответила я болела. Большое Вам спасибо. Я буду молится за Вас.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; С уважением Надежда О. и Ирочка&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Надія О., Кіровоградська обл., Іра-11р., дочка Оксани Б. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Шановний отец Ігор!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вітаємо Вас та усіх хто Вам допомагає з Новим 2014 роком! Хай здійсняться всі Ваші мрії, здоров&rsquo;я, радості та щастя, мирного неба та миру у Вашіх серцях та родинах. Хай Господь Вас береже та благословляє.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Велике Вам спасибі за те що Ви не забуваєте про нас, та за те що прикладаєте зусиль чинити угодну Богу справу. Щиро Вам дякуємо. Слава Господу за те що в Нього є такі служителя.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Будьте в мирі та благословінні. Ще раз велике Вам спасибі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З повагою Віка &ndash; 11 років та Тетяна<strong>&rdquo;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Херсонська обл., Тетяна С. (звільнена з Дніпродзержинської ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Шановний о.Ігор Онишкевич!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Вітаю Вас і всю Вашу громаду з Новим роком та Різдвом Христовим!</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Бажаю всього найкращого та всіх земних благ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хай Бог допомагає Вам у всіх всіх Ваших справах.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З повагою Світлана Л.&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Світлана Л. (Дніпропетровська обл., Євген-12р., син Алли Б., Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;<strong>Спасибо</strong><strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Тетяна С. (Харківська обл., Софія-3р., дочка Світлани С., Селезнівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo; Доброго дня! <strong>Дякую Вам за подарунок до Святого Миколая. Мені дуже приємно, що Ви турбуєтесь про мене</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Рада Б., Луганська обл., 9р., дочка Анни Б., (Селезнівська ВК, Луганська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&ldquo;Дуже дякуємо веселих свят!&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Марія Б. (Тернопільська обл., Іванна-6,5р., Марія-4,5р., діти Любові Д., Чернігівська ВК)</em></span></p>',NULL,5623,5,1,NULL,19);
INSERT INTO `publications_publication` VALUES
(1516,'P','2014-03-13',1,NULL,'Rss','Рік з Папою Франциском...','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5617,200,1386599209.jpg\" />Середа, 13 березня 20013 року, похмура й дощова погода.... На площі Святого Петра десятки тисяч людей, хоч цього дня і не відбувається загальна аудієнція... А це тому, що два тижні перед тим Папа Венедикт XVI провів прощальну аудієнцію, в якій взяли участь коло 200 тисяч паломників, і наступного дня, 28 лютого, вступило в дію його зречення з Петрового служіння.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1929:rik-z-papoju-francyskom&catid=32:gk-svit-cat&Itemid=58',5618,NULL,1,1,26),
(1517,'P','2014-03-13',1,NULL,'Note','Звіт №1 про НАДХОДЖЕННЯ  ЛИСТІВ 14.01.2014 р. — 31 лист','<p style=\"text-align: right;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Уважаемый Игорь Онишкевич и вся церковная громада! Дай Бог Вам здоровья, пусть верой и любовью Вас Бог обильно одарит! Большое спасибо за милость, понимание, поддержку. Я была очень удивленна. Ведь я не знала от кого, кто к нам так милосерден.</span></p>\r\n<p>&nbsp;</p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Листи від ув\'язнених </span></em></strong><strong><em><span style=\"text-decoration: underline;\">&nbsp;(з них вперше звернулись </span></em></strong><strong><em><span style=\"text-decoration: underline;\">3</span></em></strong><strong><em><span style=\"text-decoration: underline;\">ос</span></em></strong><strong><em><span style=\"text-decoration: underline;\">о</span></em></strong><strong><em><span style=\"text-decoration: underline;\">би)</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Чернігівська виправна колонія ЧВК-44 (м.Чернігів) &mdash; </strong><strong>6</strong><strong> лист</strong><strong>ів</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Кача</strong><strong>нівська виправна колонія </strong><strong>К</strong><strong>ВК-</strong><strong>5</strong><strong>4 (м.</strong><strong>Харк</strong><strong>ів) &mdash; </strong><strong>3</strong><strong> лист</strong><strong>и</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Збаразька виправна колонія ЗВК-63 (Тернопільська обл.) &ndash; 5 листів</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Селезнівська виправна колонія СВК-143 (Луганська обл.) </strong><strong>&mdash;</strong><strong> 2 листи</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Ч</strong><strong>о</strong><strong>рн</strong><strong>омор</strong><strong>ська виправна колонія ЧВК-</strong><strong>7</strong><strong>4 (м.</strong><strong>Одеса</strong><strong>) &mdash; </strong><strong>1</strong><strong> лист</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Орджонікідзевський виправний центр ОВЦ-79 (Дніпропетр. обл.) </strong><strong>&mdash;</strong><strong> 1 лист</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">&nbsp;</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Подяка за посилки для дітей &ndash;12 листів</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">&nbsp;</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Листи звільнених з ув</span></em></strong><strong><em><span style=\"text-decoration: underline;\">&rsquo;</span></em></strong><strong><em><span style=\"text-decoration: underline;\">язнення &ndash; 1 лист</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Шановний отець Ігорь, Братья Сестрички! <strong>Вітаю Вас з Новим роком 2014. Бажаю щастя добра здоров&rsquo;я! З Новим роком!</strong><strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Галина А. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Слава Исусу Христу! <strong>Поздравляю Вас и Ваших всех прихожан с Рождеством Христовым! Да благословит Вас Господь Бог!</strong> &rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Етель Б. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... очень болею, перенесла 2 инсульта, инфаркт, которак обеих глаз и много разных заболеваний... хочу поздравить Вас с праздником. <strong>С Новым годом и с Рождеством Христовым!.. </strong>У меня никого нет, по возможности <strong>помогите пожалуста гр.200 и карточку таксофоновую... покушать:</strong> сала, колбаски, конфет, свежих овощей, сильно пониженый белоглобин, вафли, печенье если можно сахар, какао... <strong>пожалуста крем БОРО-НОРМ и для лица календула алоевый, шампунь, линор для стирки... и если сможите часы ручные</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Раїса Ч. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Спасиба большое, что беспокоитесь обо мне и моих детках</strong>... очень Вас прошу <strong>вышлите хоть две таксофонные карточки по 24 минуты</strong>, что бы я могла поговорить со своими детками&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Олена Т. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Обращаюсь к Вам с огромной просьбой, так как на старости лет осталась в одиночестве. И совершила большой грех, наверное Господь меня и наказал,<strong> у меня вторая группа инвалидности, почти ничего невижу</strong>. Прошу людей, чтобы написали письмо. <strong>Высылаю Вам рецепт, будьте так добры, вышлите пожалуста эти лекарства. </strong>Я Вам за ранее очень благодарна&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Зінаїда Б. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... мне 25 лет. Отец Игорь <strong>помогите пожалуйста конвертами, мою дочь В</strong><strong>икторию забрали в детский дом не с колонии а на свободе и где она я не знаю,</strong> я подаю запрос в свой город в райисполком жду ответа, вдруг прийдется ещё куда то подавать, да и вообще ей написать как только узнаю адрес, в нашем магазине нет конвертов и марок тоже нету... Моей дочери в апреле будет 5 лет&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Лілія К. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&ldquo;Маленький Ісусик не спить, не дрімає. Своїми руками весь світ обнімає. І Вашу хатину і Вашу родину і всю Україну ХРИСТОС СЯ РОЖДАЄ!</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Дорогий о.Ігор та Ваша церковна громада, вітаю Вас з Різдвом Христовим&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ірина В. (Качанівська ВК, м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Дорогий о.Игор и вся церква вітаю Вас з святом Різдвом Христовим. Бажаю Вам світлих днів і сонячних ранків і чистих помислів і вдячність людей за Вашу допомогу, любов до ближнього. Щиро дякую Вам низький уклін</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Слава Христу!!!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Щиро дякую за Вашого листа і за те що незабуваєте мене. <strong>15.12.13р. в мене був день народження я його ніколи не святкувала навіть і дома тому що в цей день померла моя мама коли мене народила, а їй було 29 років і я її ніколи небачила</strong> я росла до трьох років в дитячому будинку тоді мене забрала мачуха але і там був не мед батько пив і мачуха теж <strong>з дев&rsquo;яти років я вчилась заробляти собі гроші, </strong>щоб купити собі зошити, книжки, портфель в нас в селі копали торф і я ходила перевертати торф і заробляла в день 3 рублі це були великі гроші для мене <strong>в мене не було іграшок тоді ходила обривати хміль і за місяць я получила 20 рублів і купила собі ляльку за 14 рублів яка говорила Ма-ма </strong>я така була рада і мені жалко зараз молодь як вони гинуть вони не хочуть трудитись ці комютори і телевізори роблять їх серця темними&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Тетяна С. (Качанівська ВК, м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Вы просите, чтоб я написала о себе. <strong>Сама я сирота, воспитывали меня чужие люди </strong>у меня никого нет. Закончила школу, училище, вышла замуж и уехала из деревни в горд Феодосию. <strong>С мужем прожила 5 лет потом разошлась, я после развода начала сильно выпивать, вот так я и попала в эти места</strong>, конечно я сама виновата но если бы была какая то поддержка может ничиго и не произошло. Я надеюсь что это будет последний раз. <strong>Поздравляю Вас всех с наступающими праздниками&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Галина І. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Слава Ісусу Христу! <strong>Вітаю Вас з Різдвом Христовим! Нехай новонароджений Богомладенець благословляє Вас і дарує масу духовної радості і спасіння.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Спасибі Вам всім дорогі хто мені пише ці гарні листи. Запитуєте чи є в нас бібліотека? Звичайно, є дуже хороша література і Кобзар маємо. І також література на теми духовної тематики, кому цікаво, то є що почитати, головне бажання... Мою одну ікону відправили в Київ на конкурс творчості. Хоча я точно неможу сказати де буде відбуватись цей конкурс... Я незнаю як получився вірш, але це прийшло мені на думку сьогодні вночі. Довго думала, згадувала своє дитинство. Свою любиму бабусю (покійну) яка щонеділі водила мене за ручку до церкви і як було приємно і добре тоді.</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Як далеко лишилось дитинство,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Спогад про це навертає сльозу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Молюсь до Тебе Господи щогодинно</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сама себе простити не можу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А час летить неумолимо,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І вже ми виросли давно</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І радості в маленькому забули</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нам все не так, нам всеодно.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ми розучились розуміти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Причину радості в усьому</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І ми уже давно не діти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І хто зарадить горю цьому.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Уже не в радість нам погода,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І без радості спливає час,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І сіро все, і все убого</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І зачерствіла душа у нас.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Але надію маю і молю</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Бо Ти є наш Господь Всевишній,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Молюсь щоночі, плачу і терплю,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Прости мене по Милості Своїй Спаситель!!!<strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Любов С. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Вітаю Вас з Різдвом Христовим і Новим Роком!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З повагою до Вас Ліля і всі жінки мого відділення!</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Письмо Отца</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Пс.138:1</em> Ты можешь не знать Меня, но Я знаю всё о тебе...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Быт.1:27</em> Ты был создан по Моему подобию...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Пс.138:14</em> Ты сотворен чудно и прекрасно...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Иоан.8:41-44</em> Меня представляют не таким, каков Я есть на самом деле, это потому что не знают Меня...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>1Иоан.4:16</em> Я не злой, Я всегда рядом, Я &ndash; совершенное выражение любви...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Иакова 1:17</em> Каждый хороший подарок, который ты получаешь, это от Меня...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Матв.6:31-33</em> Потому что Я тот, кто обеспечивает тебя всем и выполняет все твои нужды...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Иер.31:3</em> Потому что Я люблю тебя любовью вечной...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Втор.4:29</em> Если ты ищешь Меня всем сердцем, то обязательно найдешь...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Пс.36:4</em> Радуйся во Мне и Я исполню желания твоего сердца...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>2Кор.1:3-4</em> Я также Отец, который утешает тебя во всех горестях...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Иоан.17:26</em> Ибо Моя любовь открыта тебе в Иисусе...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>1Иоан.4:10</em> Его смерть была наивысшим выражением Моей любви...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>1Иоан.2:23</em> Если ты принимаешь подарок Иисуса, ты принимаешь меня...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Римл.8:38-39</em> И ничего больше не отделит тебя от Моей любви...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Еф.3:14-15</em> Я всегда был и буду Отцом...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Иоан.1:12:13</em> Мой вопрос: будешь Моим дитём?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Я жду тебя...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Любящий Отец, Всемогущий Бог&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Лілія П. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Спасіба дорогі мої. Посилочку получила... Я Вам дуже благодарна за Вашу поміч.</strong> Да благословить Вас всіх і ваші сім&rsquo;ї Господь и хранить Вас, да воздасть Вам по доброті вашій... Привозили ікону &laquo;Молитви до Пресвятої Богородиці&raquo; на честь Її Чудотворної ікони Чернігівської &laquo;Дубинська&raquo;... Мої дорогі <strong>я 24 травня 2014 р.освобождаюся, я із дому буду із вами листуватись,</strong> я дуже бажаю із Вами встрітися в свої домівки і Вам дуже отблагодарити за ваше дуже добре відношення до нас потіряних. Скільки я тут горя зазнала за ціх 3 роки і скільки переплакала. <strong>Уже додому готовлю вещі но ще нима кофти верхньої і тапочки кімнатні 38р</strong>., а так необходиме все в чому доїхати є. Ще Пасху отпраздную тут і через місяць буду у свої рідні домівкі. <strong>Я молюся за вас всіх, за всіх людей, начальство, щоб був мир і спокій</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Віра Ц. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>дякую Ісусу Христу що Він дав мені спілкування із Вами</strong>... напишу про себе... <strong>Мама мене покинула ще маленькою, коло своєї мами, бо мама вела розпусний образ життя. Бабушка також полюбляла пити і мене сільська Рада відправили в школу-інтернат</strong>. <strong>Батька я свого незнала і досі незнаю хто він у мене є. </strong>В свої 18 років я вийшла замуж, но довго не прожила із першим чоловіком, він у мене помер, а другий брак тоже не склався, бо любив гуляти, друзів, частенько до мене прикладав руки.<strong> Маю я троє діточок но вони мені не пишуть і не допомагають, а я із свого боку не хочу їм себе нав</strong><strong>&rsquo;</strong><strong>язувати. Бо незнаю може в них немає і на кусок хліба</strong>, раніше я робила тут в зоні, то мала можливість купити карточку таксофону то розмовляла завжди із діточками. А тепер вже другий рік як я не працюю, по состоянію здоров&rsquo;я, в мене діагноз нозі лівій &laquo;коксатрос&raquo; третього ступеня. Хожу я із паличкою... Я до закривання під варту проживала в м.Калинівка Вінницької обл Зараз там проживають мої діточки самі без мене. Праювала я із молодих літ в колгоспі дояркою а потім колгосп розпався і приходилось робити на одного хазяїна в магазині. Про рідніх я немаю що написати бо їх вже немає в живих, осталась я одна із своїми дітками. Дуже важко перебувати в цих місцях коли тобі ніхто не пише і не допомагає. <strong>За мої 46 років даже немає нічого вспомнити хорошого</strong>... В мене бувають такі часи що немає даже чим помити голову а щоб тут в когось щось просити то потрібно повернути. <strong>Уже скоро буде 7 років як я сиджу (<em>термін 10 років</em>), але одно що не втрачаю надію і кожний день читаю молитви і прошу у Господа дай мені терпіння і здоров</strong><strong>&rsquo;</strong><strong>ячко і за всіх засуджених прошу благословення</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Олена К. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Несколько лет назад вы мне помогли как духовно так и материально. Да воздаст Вам Господь за добрые деяния!.. Уважаемый отец Игорь! <strong>Прошу Вас помочь женщине которая очень нуждается в помощи. Если у Вас есть такая возможность помогите ей самыми необходимыми в этих местах предметами гигиены, мыльными принадлежностями</strong>. С самого раннего возраста она осталась сиротой, какое-то время её воспитывала тётя. <strong>Она отчаянно нуждается в помощи, но не верит что кто-то ей может помочь.. А главное ей нужна не только материальная помощь но и духовная пища</strong>. В своё время благодаря верующим людям и их помощи я пришла к Вере и Богу. И выйдя из этих мест я очень хочу помогать людям таким как я , как Лилия заблудшим и потерявшим смысл в жизни. Большая к Вам просьба напишите ей ответ, <strong>очень хотелось бы что-бы она не замыкалась в себе а наоборот начала верить в хорошее и в то что с Господом намного легче и хорошо жить</strong>. Жить полноценно и радоваться каждому новому дню&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Лілія Г. (Мартинова Н.) (Селезнівська ВК, Луганська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Мне 41 год, есть у меня дочка Елена, ей 19 лет. Её отца убили, за 2 недели до её рождения. Мама &ndash; пенсионерка, папа в том году умер от инсульта, брат сидит. <strong>Я бывшая наркоманка. В 2000 году, отсидев за кражу, приехала домой, устроилась на работу в г.Новая Каховка. Всё в моей жизни наладилось, как говориться, но не на долго</strong>. Дьявол, &laquo;как рыкающий лев&raquo; наступал на пяты и вот я <strong>через 10 лет снова в тюрьме. Я осуждена на 7 лет, за преступление, которого не совершала</strong>. Я сошлась с мужчиной, который сильно стал избивать меня, я начала колоться, и вся жизнь дальнейшая покатилась по наклонной. Он сейчас на свободе, а я сижу в тюрьме, обещал помогать, но обманул... <strong>У меня сейчас большая проблема. Я лишилась передних зубов, так как в зонах их не ремонтируют, а вырывают,</strong> мне пришлось из-за боли в зубах, по чуть чуть вырвать. <strong>На свободе я поставила мост, продержался он 13 лет, а в этом году спал. Врач по месту насчитал за ремонт 2 тысячи 300 гривен. Для меня это очень большие деньги, мама и рада бы помочь, но не может. Мне сидеть ещё 3 года, кушать нечем</strong>, разговаривать, да улыбнуться даже неудобно. Многие пишут верующим, просят о помощи, вот и я решила написать. <strong>Если сможете, помогите чем сможете, буду очень благодарна</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Марина Ю. (Селезнівська ВК, Луганська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Поздравляю з святым Николаем. Братья сёстры <strong>миня дострочное освобождения отложылы на пивгода </strong>потому что непрышла прыпыска... Так значить Богови угодно... <strong>Прошу помощь потому что мне некому помочь. </strong>У меня всё позаканчивалось, вышлить пожалуста если сможите шампунь, мыло, зубна паста и счётка, очкы 4+, взуця 37 бо я геть боса и штук двое штанов, крем для рук бо сильно трискаюця а помазать нечим, ручку, титрадку, сухарыкив или бублыкив пожалуста если сможите баночку згущеного молока... Я багато чытаю все у нас у библиотеки есть</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Уж слышу я шум как бы множества вод</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">И стон ожидания мира.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Готовся нивеста! Жених твой идёт</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">И близок час брачного пира!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Гряди, мой Спаситель, отрада очей!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Готова войти я в обитель.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Омыта в пречистой крови я Твоей,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">О Боже, мой Царь, мой Спаситель!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Забудь всё, чем мир твоё сердце мутил,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Все слёзы, печаль, ожиданья.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Люби, как один Он любить Тибя мог,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Страдай, на Исуса взирая.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Прощай, как Спаситель твой дивный и Бог</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Простил, за тебя умирая.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">И верь как поверил любви Он твоей</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">О верь, что Он любит безмерно,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Что выше и глубже всех гор и морей</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Любовь Его вечна и верна.</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Призыв к покаянию</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">К водам живым поспешите</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сношей греха и забот,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В воду её погрузите &ndash;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сердце отраду найдёт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Поторопись! Можно ли ждать?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Можно ль спасеньем души рисковать?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Руки Господь простирает,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Всякого хочет принять.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">К водам живым поспешите,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хоть вы покрыты виной.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Там вас омоет Спаситель,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Там облечет белизной.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Слушай, покайся и верь!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Кто Божью слову внимает,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Входит в открытую дверь.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Воды живые чудесно</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Всё оживляют кругом</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Если раскается грешник,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Небо ликует о том&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Людмила В. (Орджонікідзевський ВЦ, Дніпропетровська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&ldquo;</strong>Добрий день!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Отче Ігорю, спасибі Вам за Вашу доброту до моєї дочки Олени і її дітей. За допомогу, що підтримуєте піддаєте сили і надії на все найкраще.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З Новим роком і Різдвом Христовим вітаю Вас і щоб наш Господь Спаситель допомагав Вам, а головне здоров&rsquo;я.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Спасибі Вам всим дуже велике, з повагою Л.М.<strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Любов М. (Кіровоградська обл., Назар-14р., Роксана-11р., Роман-6р., Настя-5р., діти Олени Т., Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;<strong>Щиро дякую</strong><strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Надія С. (Харківська обл., Поліна-6,5р., дочка Олени П., Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&ldquo;Я дуже Вам вдячна. Дякую Вам&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Лариса Ф. (Донецька обл., Аліна-2р., дочка Ольги Ш., Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Уважаемый Игорь Онишкевич и вся церковная громада!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дай Бог Вам здоровья, пусть верой и любовью Вас Бог обильно одарит!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Большое спасибо за милость, понимание, поддержку. Я была очень удивленна. Ведь я не знала от кого, кто к нам так милосерден. Бог нам дал радость внука и мы живем для него и он не одинок. Господь Бог нас не оставляет и Он нам уже не один раз помог. Муж мой 10 лет на группе. Я тоже болею, часто лежу в больнице, мне поставили диагноз эпелепсия. От нервных стрессов. Все удевлялись почему я не делаю группу, что меня удерживает. А удерживал меня Господь Бог. И теперь я понимаю почему. Ведь Он заранее знал, что у нас будет внук, а если два человека на группе нам бы его не дали. Дочь моя лишена родительских прав. Она вела неправильный образ жизни, употребляла наркотики. Вернувшись домой беременной мы ей опять поверили, думали что станет матерью и все старое забудется. Она родила Лешу, побыла с ним пока не получила на него деньги, и ушла. Приходила несколько раз и это только чтобы опять нас обворовать. И опять Господь Бог нам помог, ее образ жизни не отразился на здоровье нашего внука. И мы благодарны Ему за здоровье Леши&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Людмила К. (Дн-петровська обл., Олексій-8р., син Наталії К., Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Шановна Громада!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вашу посилку отримали за що дякуєм.&nbsp; Саша&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Олександр О. (Вінницька обл., 14р., син Раїси О., Чорноморська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Ув. Ігор Онишкевич!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">От имени всех деток и их родителей благодарим Вас за вашу доброту и заботу. Поздравляем Вас с Новым 2014 годом и желаем Вам здоровья и долголетия, успехов всегда и везде. Будьте счастливы.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; С ув.семья О. &rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Валентина О. (м.Запоріжжя, Владислав-12р., Вероніка-5р., діти Вікторії Г., Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&ldquo;Дуже дякуємо веселих свят!&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Марія Б. (Тернопільсь обл., Іванна-6,5р., Марія-4,5р., діти Любові Д., Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Шановний о.Ігор Онишкевич!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вітаю Вас і всю Вашу громаду з Новим роком та Різдвом Христовим!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Бажаю всього найкращого та всіх земних благ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хай Бог допомагає Вам у всіх всіх Ваших справах.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З повагою Світлана Л.&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Світлана Л. (Дніпропетровська обл., Євген-12р., син Алли Б., Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;<strong>Спасибо</strong><strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Тетяна С. (Харківська обл., Софія-3р., дочка Світлани С., Селезнівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Шановний отец Ігор!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вітаємо Вас та усіх хто Вам допомагає з Новим 2014 роком! Хай здійсняться всі Ваші мрії, здоров&rsquo;я, радості та щастя, мирного неба та миру у Вашіх серцях та родинах. Хай Господь Вас береже та благословляє.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Велике Вам спасибі за те що Ви не забуваєте про нас, та за те що прикладаєте зусиль чинити угодну Богу справу. Щиро Вам дякуємо. Слава Господу за те що в Нього є такі служителя.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Будьте в мирі та благословінні. Ще раз велике Вам спасибі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З повагою Віка &ndash; 11 років та Тетяна<strong>&rdquo;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Херсонська обл., Тетяна С. (звільнена з Дніпродзержинської ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Сразу когда приехала пошла в Церковь взяла свечки за здравие и за упокой зажгла помолилась. Теперь ходить буду всегда сейчас возьму пост среда и пятница до конца своей жизни. Перед выходом поклялась перед Богом что в своей жизни никогда не коснусь к спиртному... <strong>Я искренне уверовала уже 7 лет и так до конца своей жизни буду жить с Богом и людей учу и даже маленьких деток... Я Вам очень благодарна что все эти годы Вы были рядом, дай Бог всем здоровья</strong>. А сын Яша очень больной нет ни одного органа здорового. Помолитесь за Якова за исцеление я только уповаю на Господа&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Світлана Ш. (звільнена з Чернігівської ВК)</em></span></p>',NULL,5622,5,1,NULL,19);
INSERT INTO `publications_publication` VALUES
(1518,'P','2014-03-12',1,NULL,'Rss','Украдена пожива (13 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5614,200,2ba39ae2.jpg\" />Учитель та його учні мандрували і в дорозі не&nbsp; могли як слід поїсти. Учитель попросив їх піти знайти їжу. Учні повернулися наприкінці дня. Кожен приніс те, чим погребували інші: фрукти, що от-от мали зіпсутися, черствий хліб, гірке вино. Один з учнів, проте, приніс кошик доспілих яблук.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я мусив скоїти щось, аби допомогти учителю і своїм братам, - сказав він, розділивши яблука на всіх.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1928:ukradena-pozhyva-13-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5617,NULL,1,1,26),
(1519,'P','2014-03-12',1,NULL,'Rss','7 мільйонів паломників на канонізацію Івана Павла ІІ та Івана ХХІІІ','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5614,200,2013-12-10-12.02.51.jpg\" />Вже не 5 мільйонів паломників, як повідомлялося раніше, а 7 очікують у Римі в період від Страсного Тижня до канонізації Івана Павла ІІ та Івана ХХІІІ.</p>\n<p>Як передбачає влада Рима, масовий наплив паломників розпочнеться у Страсну П’ятницю 18 квітня. Вірні з цілого світу приїдуть, щоб взяти участь у Хресній Дорозі в Колізеї, яку очолить Папа Франциск. Багато з них залишаться у Римі на Великдень.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1927:7-miloniv-palomnykiv-na-kanonizaciju-ivana-pavla-ii-ta-ivana-hhiii&catid=32:gk-svit-cat&Itemid=58',5616,NULL,1,1,26),
(1520,'P','2014-03-11',1,NULL,'Note',' «Таємниці Генія Шевченка»','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,5626,200,U91f691VJ_A.jpg\" alt=\"\" width=\"200\" height=\"112\" />Телевізійна студія \"1+1\" відзняла документальний фільм про таємниці життя Шевченка.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Актуальність Шевченка не вмирає століттями. Але наскільки добре ми знаємо його як звичайну людину? Фільм розкриває розмаїті аспекти із життя Кобзаря. У фільмі бачимо Шевченка нового - неканонічного, нешаблонного.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,5626,200,U91f691VJ_A.jpg\" alt=\"\" width=\"200\" height=\"112\" />Телевізійна студія \"1+1\" відзняла документальний фільм про таємниці життя Шевченка.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Актуальність Шевченка не вмирає століттями. Але наскільки добре ми знаємо його як звичайну людину? Фільм розкриває розмаїті аспекти із життя Кобзаря. У фільмі бачимо Шевченка нового - неканонічного, нешаблонного.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/HfU2lotlQCg\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>',NULL,5626,5,1,NULL,48),
(1521,'P','2014-03-11',1,NULL,'Rss','Чотири дружини (12 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5613,200,1375696.jpg\" />Жив собі багатий купець і було в нього чотири дружини.</p>\n<p>Першу купець любив понад усе, одягав її у пречудові сукні і прикрашав коштовностями. Він піклувався про неї. милував і леліяв.</p>\n<p>Дугу купець також сильно любив. Він невимовно нею гордився і при кожній нагоді старався показувати її друзям. Але завжди боявся, що колись вона піде від нього до когось іншого.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1925:chotyry-druzhyny-12-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5614,NULL,1,1,26);
INSERT INTO `publications_publication` VALUES
(1522,'P','2014-03-11',1,NULL,'Rss',' Голос Церкви зараз важливий як ніколи','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5613,200,article.jpg\" />Виконуючий обов\'язки міністра оборони України Ігор Тенюх звернувся зі словами подяки до представників Церков і релігійних організацій за підтримку українських військовослужбовців і просить їх не зупинятися.</p>\n<p>«Збройні Сили України з великою пошаною і повагою ставляться до священнослужителів Церков і релігійних організацій, які виконують важливу духовну місію в суспільстві. Висловлюю слова поваги за ті зусилля, які проявляє Церкву з метою підтримки Збройних Сил України і всього українського народу.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1926:-golos-cerkvy-zaraz-vazhlyvy-jak-nikoly&catid=37:spilnota-cat&Itemid=49',5615,NULL,1,1,26),
(1523,'P','2014-03-10',1,NULL,'Rss','Рибалка і бізнесмен (11 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5610,200,ryba2-700x325.jpg\" />Якось одного разу бізнесмен стояв на причалі у маленькому селі і стежив як рибалка у вутлому човнику спіймав велетенського тунця. Бізнесмен привітав рибалку з тим успіхом і запитав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Скільки часу потрібно щоб спіймати таку рибу?</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1924:rybalka-i-biznesmen-11-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5613,NULL,1,1,26),
(1524,'P','2014-03-10',1,NULL,'Rss','«Коло сім`ї»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5610,200,DSC00089.JPG\" />«Нещодавно працівники БФ «Карітас Київ» взяли участь у семінарі, проведеному спеціалістами центру розвитку дитини «Коло сім`ї» Лідією Романчук та Ольгою Іваненко у Львові», - повідомляє психолог БФ «Карітас Київ» Павлюк Аліна.</p>\n<p>Проведений семінар був присвячений роботі з дітьми, які зазнали скривдження та емоційного занедбання. Основна увага була зосереджена на проблемах психологічних особливостей дітей, які опинились в складних життєвих ситуаціях, а також на питаннях специфіки поведінки і праці з такими дітьми.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1923:2014-03-10-18-03-48&catid=27:gk-v-ukrajini-cat&Itemid=57',5612,NULL,1,1,26),
(1525,'P','2014-03-10',1,NULL,'Rss','Як правильно хреститися і кланятися','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5610,200,1319304902_300.jpg\" />Пошана, яку людина складає Господу, має походити з її серця. Ісус Христос вчив: «Бог – Дух. Ті, що Йому поклоняються, повинні у дусі й правді поклонятися» (Йо. 4:24). Часто людина виявляє назовні все те, що діється в її душі, словами чи поведінкою. І християни виявляють назовні шану Богові спільною молитвою, піснями, хресним знаменням, поклонами тощо. Спаситель, коли молився, підносив Свої очі до неба і прихиляв коліна.</p>\n<p>Піст – час для покаяння, тому під час Великого посту читаються покаянні молитви і робляться поклони. Дуже важливо вміти правильно виконувати обрядові елементи нашої віри, зокрема, правильно хреститися і кланятися.\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1922:jak-pravylno-hrestytysja-i-klanjatysja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5611,NULL,1,1,26);
INSERT INTO `publications_publication` VALUES
(1526,'P','2014-03-10',1,NULL,'Rss','Увіковічнення Кобзаря на Аскольдовій','<p align=\"right\"><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5608,200,6Zg0AMMu-ao.jpg\" />«Борітеся — поборете!</strong><strong> Вам Бог помагає!</strong><strong> За вас правда, за вас слава</strong><strong> і воля святая!»</strong><strong></strong></p>\n<p>Дев’ятого березня цього року вся Україна та українство світу святкує 200 – ліття з дня народження Тараса Шевченка. В ці трагічні та водночас багатообіцяючі дні, дні боротьби, і дні суспільних змін в нашій країні Тараса Шевченко постає перед нами, як геній – пророк.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=1921:uvikovichnennja-kobzarja-na-askoldovi&catid=25:parafija-cat&Itemid=27',5609,NULL,1,1,26),
(1527,'P','2014-03-09',1,NULL,'Rss','Мудрий пенсіонер (10 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5607,200,Na1.JPEG\" />Один мудрий старий пан вийшов на пенсію і купив собі скромний будиночок поряд зі школою.</p>\n<p>Уперше за кілька тижнів свого відпочинку він жив у тиші і привіллі, поки не почався навчальний рік. Уже наступного дня троє хлопчаків повні сили і енергії почали ходити вулицею і калатати по всіх сміттєвих контейнерах. що там стояли.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1920:mudry-pensioner-10-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5608,NULL,1,1,26),
(1528,'P','2014-03-09',1,NULL,'Rss','Голос Божий через слово Тараса відкриває сьогодні нам значення того, що Бог є з нами','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5575,200,pravoslavja.jpg\" />Про це сказав сьогодні, у Неділю православ’я, Отець і Глава Української Греко-Католицької Церкви Блаженніший Святослав (Шевчук) під час Архиєрейської Літургії в соборі Воскресіння Христового.</p>\n<p>Предстоятель УГКЦ пояснив, що в Неділю православ’я ми особливо відчуваємо повноту апостольської віри Христової Церкви, істинність цієї віри.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1919:golos-bozhy-cherez-slovo-tarasa-vidkryvaje-sogodni-nam-znachennja-togo-shcho-bog-je-z-namy&catid=28:tema-nomera-cat&Itemid=2',5607,NULL,1,1,26),
(1529,'P','2014-03-09',1,NULL,'Rss','Блаженніший Святослав (Шевчук) говорить правду світській владі','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5575,200,u_zadumi.jpg\" />Глава Української Греко-Католицької Церкви дає таке свідчення віри, яке дав Блаженний Іван Павло II. Під час розпалу громадянської, політичної, а тепер вже й міжнародної кризи в Україні Глава УГКЦ є відважним свідком, який постійно твердить про моральний закон, права людини та соціальне вчення Католицької Церкви.</p>\n<p>Церква повинна захищати мир і справедливість, подібно як Блаженний Іван Павло II підтримував рух солідарності у своїх пастирських візитах до рідної Польщі, яка в той час була під комуністичним режимом.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1918:blazhennishy-svjatoslav-shevchuk-govoryt-pravdu-svitski-vladi&catid=32:gk-svit-cat&Itemid=58',5606,NULL,1,1,26),
(1530,'P','2014-03-09',1,NULL,'Rss','«Це не ти несеш хрест, це хрест несе тебе»','<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5575,200,240220141764.jpg\" />Ці слова відіграли одну з ключових ролей у появі Подячного хреста, який подорожує столицями світу у подяку Господу за наше спасіння. Хрест відвідав 34 держави, біля нього молився король Бельгії, Йоан Павло ІІ і Бенедикт XVI. Тепер Подячний хрест у Києві на головному Майдані країни.</em></p>\n<p><em>Хрест уже відвідав 34 держави і залишилось ще 170 столиць світу, інші міста Хрест відвідує за запрошенням. У столиці він обов’язково має бути на Святій Месі, а після свого візиту там встановлюють інший – менший хрест.</em></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1917:2014-03-09-19-33-11&catid=27:gk-v-ukrajini-cat&Itemid=57',5605,NULL,1,1,26);
INSERT INTO `publications_publication` VALUES
(1531,'P','2014-03-09',0,NULL,'Note','День Кобзаря','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5424,200,O-AFgJGqNRE.jpg\" alt=\"\" width=\"200\" height=\"99\" />Парафія церкви св.. Миколая Чудотворця на Аскольдовій могилі&nbsp;<strong>запрошує</strong>&nbsp;<strong>9 березня 2014 р.Б.</strong>&nbsp;запрошує на святкування 200-т ліття від дня народження українського Пророка, Кобзаря &ndash;&nbsp;<strong>Тараса Григоровича Шевченка</strong>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5424,200,O-AFgJGqNRE.jpg\" alt=\"\" width=\"200\" height=\"99\" />Парафія церкви св.. Миколая Чудотворця на Аскольдовій могилі <strong>запрошує</strong> <strong>9 березня 2014 р.Б.</strong> запрошує на святкування 200-т ліття від дня народження українського Пророка, Кобзаря &ndash; <strong>Тараса Григоровича Шевченка</strong>.</span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">10:00 Архиєрейська Служба Божа.</span></strong></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">11:30 Посвята пам&rsquo;ятного знаку*</span></strong></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">12:00 Кобзарські читання, концерт, гостина</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,5424,200,1.jpg\" alt=\"\" width=\"200\" height=\"112\" />*</strong>До святкування 200 річчя з дня народження Тараса Григоровича Шевченка у парковому комплексі &laquo;Аскольдова Могила&raquo; поблизу церкви святого Миколая Мирлікійського освятиться пам&rsquo;ятний знак.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Пам&rsquo;ятний знак виконано на честь увіковічення пам&rsquo;яті Тараса &nbsp;Григоровича Шевченка, як видатного українського художника. Знак встановлено на місці з якого Шевченко малював Аскольдову Могилу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5424,200,3.jpg\" alt=\"\" width=\"200\" height=\"170\" />Вд тепер символічний камінь стане постаментом та одночасно місцем на якому сидів художник. Етюдник, є точною копією етюдника Тараса Григоровича, &nbsp;на якому стоїть вже закінчений етюд з зображенням Аскольдової Могили.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дана композиція виконана з бронзи.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">На камені символічний напис, та підпис Тараса Шевченка.</span></p>',NULL,5597,5,1,NULL,17),
(1532,'P','2014-03-09',0,NULL,'Note','Розклад Богослужінь на 1-ий тиждень Великого посту','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>03.02.2014 понеділок Строгий піст</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00&nbsp; Часи.Літургія св.Йоана Золотоустого&nbsp;Панахида</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">17:00 Хресна Дорога від Дніпра</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00&nbsp;&nbsp;Велике повечір\'я з каноном прп.&nbsp;Адрея Крітського поклони</span></p>','<p>&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>03.02.2014 понеділок Строгий піст</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00&nbsp; Часи.Літургія св.Йоана Золотоустого&nbsp;Панахида</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">17:00 Хресна Дорога від Дніпра</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00&nbsp;&nbsp;Велике повечір\'я з каноном прп.&nbsp;Адрея Крітського поклони</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>04.02.2014 вівторок</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Часи, Літургія св.Йоана Золотоустого&nbsp; Панахида</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 Велике повечір\'я з каноном прп.&nbsp;Адрея Крітського, поклони</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>05.02.2014 середа</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Вечірня з Літургією Напередосвячених Дарів.&nbsp;Прастас-сорокоусти.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 Велике повечір\'я з каноном прп.&nbsp;Адрея Крітського, поклони</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>06.02.2014 четвер</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Часи, Літургія св.Йоана Золотоустого&nbsp; Панахида</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 Велике повечір\'я з каноном прп.&nbsp;Адрея Крітського, поклони</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>07.02.2014 п\'ятниця</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Вечірня з Літургією Напередосвячених Дарів Парастас-сорокоусти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">17:00 Хресна Дорога від Дніпра</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 Вечірня з Літургією Напередосвячених Дарів, Хресна дорога</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>08.02.2014 Субота Заупокійна.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Утрення</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">09-00 Літургія Святого Івана Золотоустого.&nbsp;Парастас-сорокоусти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 Літургія Святого Івана Золотоустого Вечірня</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>09.02.2014 неділя 1-ша Великого посту, Православ\'я</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-15 10-00 Літургія Святого Василія Великого.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">телефон для довідок:&nbsp;&nbsp;<a href=\"tel:%28050%29%204473157\" target=\"_blank\"><strong>(050) 447 31 57</strong></a></span></p>\r\n<p>&nbsp;</p>',NULL,5575,5,1,NULL,38);
INSERT INTO `publications_publication` VALUES
(1533,'P','2014-03-08',1,NULL,'Rss','Життя після пологів (9 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5575,200,ozhidaete-dvoynyu.jpg\" />У животі вагітної жінки розмовляли двоє малюків. Один каже:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ти віриш у життя після пологів?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Певна річ. Усім очевидно, що життя після пологів існує. Ми тут аби стати досить сильними і готовими до того, що нас чекає згодом.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1916:zhyttja-pislja-pologiv-9-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5604,NULL,1,1,26),
(1534,'P','2014-03-08',1,NULL,'Rss','Неділя 1-ша Великого посту, Православія','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5575,200,030.jpg\" />Євангелія від Йоана 1, 43-51</strong></p>\n<p><em>43 Другого дня вирішив піти в Галилею; і знайшовши Филипа, мовив до нього: «Іди за мною.» 44 А був Филип з Витсаїди, з міста Андрієвого та Петрового. 45 Зустрів Филип Натанаїла і сказав до нього: «Ми знайшли того, про кого Мойсей у законі писав і пророки, - Ісуса Йосифового сина, з Назарету.»</em></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1915:nedilja-1-sha-velykogo-postu-pravoslavija&catid=27:gk-v-ukrajini-cat&Itemid=57',5603,NULL,1,1,26),
(1535,'P','2014-03-07',1,NULL,'Rss','Хто автор (8 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5597,200,228227.jpg\" />Один священик прочитав монахам прекрасний текст, що зворушив усіх. Монахи відразу спитали:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Хто його написав?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Якщо я скажу, що Ісус, то ви будете умлівати над текстом, щоранку класти на нього квіти і бити поклони. Якщо я скажу, що написав його патріарх, то ви відчуєте велику шанобу але не будете поклонятися перед ним так. як перед словами Ісуса.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1914:hto-avtor-8-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5602,NULL,1,1,26),
(1536,'P','2014-03-07',1,NULL,'Rss','Молитва – фундаментальний чинник на шляху до єдності','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5597,200,1_0_779458.jpg\" />7 березня 2014 року Папа зустрівся у Ватикані з делегацією Всесвітньої Ради Церков. Він назвав зустріч «черговим важливим етапом у давніх і плідних взаєминах» між Католицькою Церквою та екуменічною асоціацією та подякував за роботу «у підтримці християнської єдності».</p>\n<p>За словами Понтифіка, Всесвітня Рада зробила великий внесок у прийняття з боку всіх християн факту, що їхня роз\'єднаність є серйозною перешкодою для проповідування Євангелія у світі.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1913:molytva--fundamentalny-chynnyk-na-shljahu-do-jednosti&catid=32:gk-svit-cat&Itemid=58',5601,NULL,1,1,26),
(1537,'P','2014-03-06',1,NULL,'Rss','Четвертий мудрець (7 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5597,200,1619599.jpg\" />Четвертий мудрець також бачив зорю, що сяяла над Вифлеємом. Але він завжди спізнювався прийти туди, де мав бути Ісус, тому що на дорозі його повсякчас спиняли бідні й убогі, що просили допомоги.</p>\n<p>Тридцять років ходячи дорогами Ісуса Христа, мудрець нарешті прийшов до Єрусалиму, ал знову спізнився. Ісус став уже чоловіком, і мудрець прийшов у місто в день розп’яття.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1912:chetverty-mudrec-7-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5600,NULL,1,1,26);
INSERT INTO `publications_publication` VALUES
(1538,'P','2014-03-06',1,NULL,'Rss','9 березня весь світ читатиме Шевченка','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5597,200,karta-svitu-vsja-1024x561.jpg\" />Міжнародний флешмоб з нагоди 200-річчя з дня народження Тараса Шевченка ініціюють студенти Українського католицького університету та Університету банківської справи НБУ. Молодь пропонує українцям світу 9 березня збиратися, аби декламувати поезії Кобзаря, а саме о 12.00 год за місцевим часом виконати «Заповіт». Саме в цей час «Заповітом» розпочнуться віче на майданах у Києві та Львові.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1911:9-bereznja-ves-svit-chytatyme-shevchenka&catid=37:spilnota-cat&Itemid=49',5599,NULL,1,1,26),
(1539,'P','2014-03-05',1,NULL,'Rss','Священна страва (6 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5575,200,631658.jpg\" />Якось уранці учитель і його учень йшли полями. Учень запитав його, яка їжа наблизить його до Бога. Хоча учитель завжди говорив, що всяка страва, благословенна молитвою, корисна для людини, учень йому не вірив.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Мусить бути якась особлива їжа, що приведе до Бога, - сказав учень.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Наприклад гриби, що ростуть ось там, - промовив учитель.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1910:svjashchenna-strava-6-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5598,NULL,1,1,26),
(1540,'P','2014-03-05',1,NULL,'Rss','Великий піст — час для зміни курсу','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,5595,200,pope.jpg\" style=\"margin: 10px; float: right;\" />«Великопісному шляху», який розпочинається у Попільну Середу, Папа присвятив своє повчання під час загальної аудієнції, яка відбулася 5 березня на площі Святого Петра у Ватикані.</strong><strong><o:p></o:p></strong></p>\n<p>Він зауважив, що цей сорокаденний шлях веде нас до Пасхального Тридення, спомину про страсті, смерть і воскресіння Господа Ісуса, що є серцем Таїнства нашого спасіння.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1909:velyky-pist-chas-dlja-zminy-kursu&catid=32:gk-svit-cat&Itemid=58',5596,NULL,1,1,26),
(1541,'P','2014-03-05',1,NULL,'Rss','Жінка на мості (5 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5594,200,images.jpg\" />Жінка переходила річку по тоненькому мосту і трусилася від страху.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Боже! – думає вона. – Якщо перейду, то віддам тисячу карбованців першому жебракові.</p>\n<p>Міст перестав хитатися.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1908:zhinka-na-mosti-5-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5595,NULL,1,1,26),
(1542,'P','2014-03-04',1,NULL,'Note','Спільна молитва за збереження миру','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5594,200,10004061.jpg\" alt=\"\" width=\"200\" height=\"150\" />У перший день Великого Посту, уся Україна, по всіх Хресних Дорогах спільно, переживаючи теперішні важкі часи рідної країни, молились у намірі збереження миру і кращої долі українського народу, добрих державних і духовних провідників та становлення вільної незалежної єдиної України.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">До спільної молитви за державу долучились парафіяни церкви св. Миколая Чудотворця на Аскольдовій Могилі.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У перший день Великого Посту, уся Україна, по всіх Хресних Дорогах спільно, переживаючи теперішні важкі часи рідної країни, молились у намірі збереження миру і кращої долі українського народу, добрих державних і духовних провідників та становлення вільної незалежної єдиної України.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">До спільної молитви за державу долучились парафіяни церкви св. Миколая Чудотворця на Аскольдовій Могилі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Єдина у Київській Єпархії Хресна Дорога, що веде київськими кручами від Дніпра до першої християнської церкви на Аскольдовій Могилі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У часі посту, щоп&rsquo;ятниці о 17:00 просимо долучатися до розважань над страстями Христовими на цій Хресній Дорозі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>P.S.</strong> І-ша стаця знаходиться за 200 метрів вздовж дніпрових круч від станції метро &laquo;Дніпро&raquo; (м. Київ) у бік Подолу, зліва.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">фото &laquo;Оранта&raquo;: <a href=\"https://www.facebook.com/media/set/?set=a.729878863711397.1073741872.198406070192015&amp;type=3\">https://www.facebook.com/media/set/?set=a.729878863711397.1073741872.198406070192015&amp;type=3</a></span></p>',NULL,5594,5,1,NULL,17);
INSERT INTO `publications_publication` VALUES
(1543,'P','2014-03-04',1,NULL,'Rss','«Великий піст стане дієтою, якщо ми, відмовляючись від певної їжі, не матимемо часу для Бога»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5592,200,o_v_rudejko2.jpg\" />Розпочався Великий піст, який є найбільшим і найстрогішим у нашому літургійному році. Нам варто усвідомити, що він полягає не лише у відмові від певних страв чи від звичного ритму життя. Піст покликаний поглибити наші духовні стосунки з Богом. Про те, як правильно дотримуватися Великого посту, і про його літургійні особливості розповідає о. Василь Рудейко, доктор богослов\'я, заступник голови Патріаршої літургійної комісії УГКЦ, священик храму Священномученика Климентія Шептицького у Львові.<strong></strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1907:2014-03-04-10-56-54&catid=28:tema-nomera-cat&Itemid=2',5593,NULL,1,1,26),
(1544,'P','2014-03-04',1,NULL,'Rss',' «Лише у Бозі спочивай, душе моя!»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5591,200,4574.jpg\" />Апостольський нунцій Томас Едвард Ґалліксон у своєму <a href=\"http://deovolenteexanimo.blogspot.com/\">блозі</a> поділився думками з приводу ситуації в Україні, звертаючись до старозавітних історій.<strong> </strong></p>\n<p>«Сьогодні я прочитав статус <a href=\"https://www.facebook.com/lesyaorobets?ref=ts&amp;fref=ts\">Лесі Оробець</a> у Фейсбуці, — пише архиєпископ Ґалліксон, -&nbsp; в якому вона стисло, але красномовно закликає Україну піднятися ще раз, тепер вже проти іноземного диктатора. Вона шкодує, що Україна не має часу оплакувати мертвих або надто опікуватися пораненими, вдовами, сиротами або батьками та сім\'ями, що сумують за втратами дітей.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1906:2014-03-04-10-33-31&catid=27:gk-v-ukrajini-cat&Itemid=57',5592,NULL,1,1,26),
(1545,'P','2014-03-04',1,NULL,'Note','«Критичну ситуацію в Криму треба розв’язати без кровопролиття»','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5591,200,ljubomyr_zmi.jpg\" alt=\"\" width=\"200\" height=\"133\" />&laquo;Той, хто погрожує посягнути з мечем, мусить пам&rsquo;ятати, що меч може повернутися і проти нього. І Бог тут є суддею. Життя людей остаточно є під Божим покровом і Бог ним керує. І думати про суто людські успіхи, не врахувавши, що Бог є володарем людського життя, легкодушно&raquo;, &ndash; зазначив Блаженніший Любомир.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5591,200,ljubomyr_zmi.jpg\" alt=\"\" width=\"200\" height=\"133\" />Ситуацію, яка склалася між Україною та Росією, коли Росія невиправдано, агресивно ввела війська на територію України, треба розв&rsquo;язати без кровопролиття.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І для цього потрібно застосувати всі можливі способи, бо кровопролиття &ndash; це остаточна річ, яка може бути дуже небезпечна і дуже страшна.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Таке переконання висловив сьогодні Архиєпископ-емерит Блаженніший Любомир (Гузар), спілкуючись з журналістами.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Архиєрей нагадує, що в збройному конфлікті гине багато цілком невинних людей. Невинних гине набагато більше, ніж тих, що стоять зі зброєю. &laquo;Безумовно, коли є агресія, кожен з нас має право боронитися. Це нормальна річ. Але треба використати всі можливі засоби, аби цього не трапилося&raquo;, &ndash; наголосив Блаженніший Любомир.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Водночас Блаженніший Любомир не береться пропонувати можливі розв&rsquo;язки, каже, що на це є компетентні українські органи влади.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Той, хто погрожує посягнути з мечем, мусить пам&rsquo;ятати, що меч може повернутися і проти нього. І Бог тут є суддею. Життя людей остаточно є під Божим покровом і Бог ним керує. І думати про суто людські успіхи, не врахувавши, що Бог є володарем людського життя, легкодушно&raquo;, &ndash; зазначив він.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Хто мечем воює, від меча може загинути&raquo;, &ndash; нагадує духовний лідер українців.</span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Департамент інформації УГКЦ</span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/BEzvzh8ny3w\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>',NULL,5591,5,1,NULL,42);
INSERT INTO `publications_publication` VALUES
(1546,'P','2014-03-04',1,NULL,'Rss','Глава УГКЦ попросив світових церковних лідерів зробити свій внесок у мирне врегулювання ситуації в Україні','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5588,200,svyatoslav_rym2.jpg\" />Ми закликаємо вас бути солідарними з нами у цей важкий час, коли війська Російської Федерації, порушивши всі угоди, вторгнулися на суверенну територію України. У нашій країні немає мовної, національної та релігійної дискримінації. Наше глибоке і щире бажання полягає у створенні і постійному покращенні наших братерських та дружніх відносин з Росією.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1905:glava-ugkc-poprosyv-svitovyh-cerkovnyh-lideriv-zrobyty-svi-vnesok-u-myrne-vreguljuvannja-sytuaciji-v-ukrajini&catid=32:gk-svit-cat&Itemid=58',5590,NULL,1,1,26),
(1547,'P','2014-03-04',1,NULL,'Rss','Ми тут проїздом (4 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5588,200,0_cbc4_50ea1d75_L.jpg\" />Якось чоловік вирушив у містом до великого мудреця. Виявилося, щ живе мудрець у старезній халупі на околиці. В оселі не було нічого, крім прогнутого ліжка і заваленого книгами стола, за яким сидів старий дід, заглибившись у читання. Гість спитав його:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Де живе мудрець?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ви мене шукаєте, - пояснив йому старий. Що вас так здивувало?</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1904:my-tut-projizdom-4-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5589,NULL,1,1,26),
(1548,'P','2014-03-03',0,NULL,'Note','Першу «Хресну Дорогу» жертвуймо за Україну','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,5537,200,575302_418972191468734_281770158_n.jpg\" alt=\"\" width=\"200\" height=\"133\" />3 березня</strong> 2014 року Божого о <strong>17:00</strong> годині жертвуймо свою молитву у наміренні кращої долі українського народу, добрих державних і духовних провідників для народу та вільної незалежної єдиної України.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>03.0314 р.Б. о 17:00 &ndash; І-ша стаця Хресної Дороги</strong> на дніпровських кручах</span></p>','<p style=\"text-align: center;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Українці, дорогі брати і сестри у Христі!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,5537,200,575302_418972191468734_281770158_n.jpg\" alt=\"\" width=\"200\" height=\"133\" />Спільно переживаючи важкі часи теперішньої незалежної України запрошуємо Вас у перший день Великого Посту долучатися до спільної молитви на першій <strong>&laquo;Хресній Дорозі&raquo;</strong> по всіх парохіях і Хресних дорогах України.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>3 березня</strong> 2014 року Божого о <strong>17:00</strong> годині жертвуймо свою молитву у наміренні кращої долі українського народу, добрих державних і духовних провідників для народу та вільної незалежної єдиної України.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Парафія св. Миколая Чудотворця на Аскольдовій Могилі запрошує і закликає парафіян усіх київських парохій, гостей столиці з&rsquo;єднатися у цій молитві на єдиній у Київській Єпархії Хресній дорозі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І-ша стаця знаходиться за 200 метрів вздовж дніпрових круч від станції метро &laquo;Дніпро&raquo; (м. Київ) у бік Подолу, зліва.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>03.0314 р.Б. о 17:00 &ndash; І-ша стаця Хресної Дороги</strong> на дніпровських кручах</span></p>',NULL,5537,5,1,NULL,47);
INSERT INTO `publications_publication` VALUES
(1549,'P','2014-03-03',1,NULL,'Rss','«Якщо потрібно буде разом стати на полі бою зі своїми солдатами, — Українська Церква, зокрема УГКЦ, готова»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5586,200,ArticleImage.jpg\" />Звернення Глави УГКЦ Блаженнішого Святослава (Шевчука), сказане 1 березня 2014&nbsp;р. в прямому ефірі Громадського Телебачення.</p>\n<p>«З прикрістю ми можемо сказати, що Україна, на жаль, втягнута у військовий конфлікт. Поки що ще не стріляють, поки що ще не гинуть люди, але очевидно, що військова інтервенція вже почалася. І&nbsp;тому, справді, уся світова громадськість і все світове співтовариство є по стороні України, оскільки Росія є агресором.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1903:2014-03-02-22-13-36&catid=27:gk-v-ukrajini-cat&Itemid=57',5588,NULL,1,1,26),
(1550,'P','2014-03-02',1,NULL,'Rss','Ідеальна жінка (3 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5586,200,12120_2.jpg\" />Чоловік сидів у чайній крамничці, коли до нього підійшов сусід:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я, одружуюся, - сказав він другові, - і тому дуже схвильований. А ти ніколи не думав одружуватися?</p>\n<p>Чоловік відповів:</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1902:idealna-zhinka-3-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5587,NULL,1,1,26),
(1551,'P','2014-03-02',1,NULL,'Note','Боротьба - 2014','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Очевидно, що вірні гаслу св. Бенедикта Нурсійського: <em>Ora et labora!</em>, ми, як буде відповідне розпорядження й будуть створені відповідні структури, повинні з молитвою в серці і на устах взяти до рук зброю й захищати Україну. &laquo;Ніхто більшої любови не має над ту, як хто свою душу поклав би за друзів своїх&raquo; (Ів 15, 13).</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Майдан-2014 &laquo;плавно&raquo; переріс у Крим-2014 та відкриту агресію Росії проти України. Не маючи боєздатної армії та рішучого командування &ndash; бо ж українська армія, починаючи від Леоніда Кравчука, через &laquo;жінку, всю в білому і з косою&raquo; (і передовсім завдяки їй!) і до бандитського режиму Януковича &ndash; методично й систематично руйнувалася: розформовувалася, роззброювалася, позбавлялася фінансування та комплектувалася антиукраїнським командним складом, &ndash; не маючи патріотично налаштованих спецслужб та структур МВС, нам, простим громадянам і християнам, залишається наймогутніша зброя: <strong>молитва.</strong> Із мого досвіду Майдану протягом грудня-лютого 2013-14 р.Б., надзвичайно ефективним було моління т.зв. <em>малих екзорцизмів</em> (молитися їх на Майдані та на барикадах порадила мені одна побожна особа) та <em>Правила старця Софронії</em> (відомого&nbsp; серед греко-католиків як <em>Богородична вервиця</em>, а серед римо-католиків &ndash; як <em>Розарій</em>). Як молитву малого екзорцизму я обрав дещо змінений текст молитви із <em>Вечірніх молитв</em> з Часослова, побудованої На Псалмі 66 (67): &laquo;Нехай воскресне Бог і розбіжаться вороги Його&raquo; (див. нижче). Свідчу, що після трикратного промовляння цієї молитви (з хрестиком у руках) втишувалася перепалка &ndash; кидання гранат, каміння і коктейлів &ndash; як на Грушевського, так і на Майдані у середу, 19 лютого, коли лінія солдат ВВ стояла на рівні стели, практично від будинку консерваторії і до вул. Інститутської. Після трикратного промовляння цієї молитви під Українським домом, у якому бійці Самооборони затисли солдат строкової служби ВВ, бійці перестали стриляти всередину фейєрверки, зробили коридор і дозволили вивести двох поранених. Що ж до Богородичної вервиці, то після того, як Сестри Василіянки закінчили її молитися у ще не спаленій тоді каплиці-наметі, Віталій Кличко вивів усіх солдат строкової служби ВВ з Українського дому практично неушкодженими (а ще годину тому їм загрожувало буквальне розтрощення). Подібно, коли я з нашим православним братом прочитали в інтернеті про бої у вівторок, 18 лютого, ми, будучи на той час ще у Львові, вирішили помолитися у цьому намірі частину <em>Правила старця Софронії </em>(Богородичну вервицю). Пілля того, як ми її закінчили, ми прочитали, що захисникам Майдану вдалося зупинити і відбити атаку. Це не тому, що саме ми такі потужні молитвенники (навпаки, я усвідомлюю всю мою грішність і слабкість): Бог це робить! &laquo;Усе, чого ви в молитві попросите, вірте, що одержите, і сповниться вам&raquo; (Мр 11, 24). Вважаю, що й тепер, коли Україна зазнає прямої агресії з боку хворих на <em>Комплекс Едмунда</em> московитів (в трагедії Шекспіра &laquo;Король Лір&raquo; є такий персонаж &ndash; бастард, незаконно народжений син графа Глостера, який прагне знищити і батька, і законного сина, щоб заволодіти спадщиною), яким для задоволення цього комплексу конче потрібен Київ і престол Князя Володимира, Бог завдяки цим молитвам виявить Свою силу. Очевидно, що вірні гаслу св. Бенедикта Нурсійського: <em>Ora et labora!</em>, ми, як буде відповідне розпорядження й будуть створені відповідні структури, повинні з молитвою в серці і на устах взяти до рук зброю й захищати Україну. &laquo;Ніхто більшої любови не має над ту, як хто свою душу поклав би за друзів своїх&raquo; (Ів 15, 13).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Петро Гусак</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Молитва малого екзорцизму:</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нехай воскресне Бог і розбіжаться вороги Його, і нехай тікають від лиця Його ті, що ненавидять Його. Як іщезає дим, нехай іщезнуть, як тане віск від лиця вогню! Так нехай щезнуть біси від лиця тих, хто любить Бога, і знаменується хресним знаменням, і в радості промовляє: &laquo;Радуйся, Господи Ісусе Христе, Ти, що зійшов в ад і переміг силу диявола; Ти, що проганяєш бісів силою Своєю й дарував нам Хрест Свій чесний на прогнання всякого ворога і супостата!&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Господи Ісусе Христе, Боже наш, помагай нам зі святою Владичицею Богородицею, молитвами всіх святих Твоїх і силою чесного Животворящого Хреста Твого! Амінь.</span></p>',NULL,5586,5,1,NULL,42);
INSERT INTO `publications_publication` VALUES
(1552,'P','2014-03-02',1,NULL,'Rss','УКРАЇНА - за дев\'ять місяців до народження','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5582,200,1459688.jpg\" />Природа нічого не створює просто так. Більше того, все має свою черговість та етапність. Навіть громадянином не зразу стають.</p>\n<p>Як формується людина? Як не банально, але цей складний процес становлення людини як особистості розпочинається із запліднення. Далі виношування плоду, народження, виховання, добра згадка про себе і на завершення вічна пам\'ять.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1901:ukrajina-za-devjat-misjaciv-do-narodzhennja&catid=37:spilnota-cat&Itemid=49',5584,NULL,1,1,26),
(1553,'P','2014-03-02',1,NULL,'Note','«Основи християнської віри та служіння у парафіях УГКЦ»','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">КАТЕХИТИЧНО-ПЕДАГОГІЧНИЙ ІНСТИТУТ</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Набір студентів на навчання за програмою</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Основи християнської віри та служіння у парафіях УГКЦ&raquo;</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Навчання проводиться на базі інституту Св. Томи Аквінського (м. Київ)</span></p>\r\n<p>&nbsp;</p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">КАТЕХИТИЧНО-ПЕДАГОГІЧНИЙ ІНСТИТУТ</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Українського Католицького Університету</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Україна, м. Львів, вул. Іларіона Свєнціцького, 17. тел./факс (0322) 75.58.90</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Набір студентів на навчання за програмою</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&laquo;Основи християнської віри та служіння у парафіях УГКЦ&raquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><span style=\"text-decoration: underline;\">Навчання проводиться на базі інституту Св. Томи Аквінського (м. Київ)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Набір на перший курс проводиться до 21 березня 2014 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Після виконання навчальної програми&nbsp; учасники отримують сертифікат УКУ</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>За детальнішою інформацією просимо звертатися</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>за телефоном:</strong> 097&nbsp;709 69 32097&nbsp;709 69 32 (бр. Тарас Пшеничний), e-mail: taras-2@ukr.net</span></p>',NULL,5585,5,1,NULL,40),
(1554,'P','2014-03-01',1,NULL,'Rss','Бабуся в аптеці (2 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5581,200,images.jpg\" />Якось в аптеці у черзі стояв молодий чоловік. Перед ним було двоє покупців: не до сезону одягнута бабуся і молода матір, що зігнулася під вагою дитини на руках. Бабуся підійшла до віконечка, простягнула рецепт і промовила:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Доню, мені тільки ті, перші ліки, бо грошей не вистачить.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1900:babusja-v-apteci-2-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5582,NULL,1,1,26);
INSERT INTO `publications_publication` VALUES
(1555,'P','2014-03-01',1,NULL,'Rss','Неділя сиропусна','<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5581,200,cheesefare-p.jpg\" />Мт. 6, 14-21 Ти ж, коли постиш, намасти свою голову і вмий своє лице.</em></p>\n<p>Сьогоднішня неділя, яку називають сиропусною чи також неділею прощення, розпочинає особливий час, який триває десяту частину від цілого року - час покаяння, покути та особливої молитви, час, котрий християни всіх віків, неначе ту євангельську десятину, віддають Богові.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1899:nedilja-syropusna&catid=27:gk-v-ukrajini-cat&Itemid=57',5583,NULL,1,1,26),
(1556,'P','2014-02-28',1,NULL,'Rss','Люди у вашому місці (1 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5537,200,_IdI4cEmq8Q.jpg\" />На узбіччі дороги, що вела до міста, сиділа стара жінка.</p>\n<p>До неї підійшов мандрівник і спитав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Що за люди живуть у цьому місті?</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1898:ljudy-u-vashomu-misci-1-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5581,NULL,1,1,26),
(1557,'P','2014-02-28',1,NULL,'Rss','Не плачте за мною, тільки помоліться','<strong><img style=\"margin: 10px; float: right;\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_55521_00.jpg\" alt=\"9-й день героїв Небесної сотні\" title=\"9-й день героїв Небесної сотні\" height=\"152\" width=\"200\" /></strong>\n<p>Сьогодні, 28 лютого, в останній  день зими, вся Україна згадує  загиблих в Україні героїв у дев’ятий день  після трагічних подій на  Майдані. Хода людей зі свічками та квітами на  головну площу країни не  спиняється ні вдень, ні вночі.</p>\n<p><strong></strong>«Це  біль великий для кожного, хто є українцем. Тому що Україна не пам’ятає,  щоб за сучаснсті було стільки загиблих. То тільки умовно сотня, а їх  набагато більше…», - розповідає мешканець Львівської області Василь,  який викладає катехизм та вже третій місяць допомагає у Міжконфесійній  капличці. Перед тим, як їхати востаннє на Майдан, заповів своїм дітям та  онукам: «Не плачте за мною, а тільки помоліться».</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1897:ne-plachte-za-mnoju-tilky-pomolitsja&catid=37:spilnota-cat&Itemid=49',5580,NULL,1,1,26),
(1558,'P','2014-02-28',1,NULL,'Rss','Послання Блаженнішого Святослава і Постійного Синоду на Великий піст 2014 року','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5537,200,r018.jpg\" />Високопреосвященні та Преосвященні владики! Всесвітліші, високопреподобні та всечесні отці! Дорогі в Христі брати і сестри!</p>\n<p>У цьогорічний Великий піст наша Церква, наші вірні та весь український народ входять із відчуттям болю, страху, терпіння і тремтливої надії. Закінчилася одна з найдовших і найстрашніших зим в історії нашого народу, – зима, в якій ми глибоко і особисто відчули пітьму гріха, побачили зло, що яскраво увиразнилося в нашій новітній історії й оголило своє вбивче нутро. Гріх постав перед нами у своїй огиднiй, смертоносній природі.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1896:poslannja-blazhennishogo-svjatoslava-i-postinogo-synodu-na-velyky-pist-2014-roku&catid=27:gk-v-ukrajini-cat&Itemid=57',5579,NULL,1,1,26);
INSERT INTO `publications_publication` VALUES
(1559,'P','2014-02-28',1,NULL,'Note','Програма допомоги потерпілими під час акцій протесту на Майдані «Люди для Майдану»','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,5576,200,1303402_0.jpg\" alt=\"\" width=\"200\" height=\"149\" />Благодійна організація &laquo;Фундація духовного відродження&raquo; УГКЦ</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Код ЄДРПОУ 38172005</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">МФО 325365</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Р/р: 2600701394169 у ЦФ ПАТ &laquo;КРЕДОБАНК&raquo; м. Львова</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Призначення платежу:</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Благодійна пожертва на програму &laquo;Люди для Майдану&raquo;</span></p>','<p style=\"text-align: center;\">&nbsp;<span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>УКРАЇНСЬКА ГРЕКО-КАТОЛИЦЬКА ЦЕРКВА &laquo;ФУНДАЦІЯ ДУХОВНОГО ВІДРОДЖЕННЯ&raquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5576,200,zapisati_18.jpg\" alt=\"\" width=\"143\" height=\"200\" />Програма надання допомоги особам, що потерпіли під час акцій протесту на Майдані: сім&rsquo;ї полеглих на Майдані, особи, що постраждали фізично та психологічно і потребують реабілітації, духовної підтримки.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Благодійна організація &laquo;Фундація духовного відродження&raquo; УГКЦ</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Код ЄДРПОУ 38172005</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">МФО 325365</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Р/р: 2600701394169 у ЦФ ПАТ &laquo;КРЕДОБАНК&raquo; м. Львова</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Призначення платежу:</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Благодійна пожертва на програму &laquo;Люди для Майдану&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>контакти Фундації Духовного Відродження:</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Львів, пл. Св. Юра, 5</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">096&nbsp;955 93 31 096&nbsp;955 93 31 о. Тарас Милян, керівник Фундації</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><a href=\"mailto:foundation.la.ugcc@gmail.com\">foundation.la.ugcc@gmail.com</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p>&nbsp;</p>',NULL,5576,5,1,NULL,42),
(1560,'P','2014-02-28',1,NULL,'Note','Рада Церков висловила свою підтримку легітимній державній владі України','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5578,200,2014.02.25.jpg\" alt=\"\" width=\"200\" height=\"133\" />Глави українських Церков і релігійних організацій засудили злочини попередньої влади проти українського народу та провокації, спрямовані на розкол України.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Єпископ-помічник київської архієпархії УГКЦ <strong>Йосиф</strong> та єпископ Української Лютеранської Церкви <strong>В&rsquo;ячеслав Горпінчук</strong> говорили про необхідність гідного вшанування полеглих героїв Майдану, а також державної допомоги пораненим і постраждалим, в тому числі задля їх лікування за кордоном.</span></p>','<p style=\"text-align: justify;\">&nbsp;<img style=\"float: left;\" src=\"image,5578,200,2014.02.25.jpg\" alt=\"\" width=\"200\" height=\"133\" /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Глави українських Церков і релігійних організацій засудили злочини попередньої влади проти українського народу та провокації, спрямовані на розкол України.<br /><br />Про це члени Всеукраїнської Ради Церков і релігійних організацій заявили під час зустрічі з Головою Верховної Ради, в.о. Президента України <strong>Олександром Турчиновим</strong>, яка відбулася 25 лютого 2014 року, повідомляє <a href=\"http://www.irs.in.ua/\" target=\"_blank\">Інститут релігійної свободи</a>.<br /><br />Під час прямої, часом гострої розмови церковні ієрархи і релігійні лідери особливо наголошували на необхідності збереження територіальної цілісності країни, відновлення конституційного ладу, консолідації суспільства, недопущення свавілля й анархії.<br /><br />Вони закликали керівництво країни зробити все можливе, щоби виразки, які іржею роз&rsquo;їдали суспільний організм, були видалені, а рани, нанесені українському народові &ndash; якнайшвидше уздоровлені. Глави конфесій запевнили, що зі свого боку докладуть для цього усіх можливих зусиль.<br /><br />Предстоятель УПЦ КП Патріарх <strong>Філарет</strong>, голова Всеукраїнського союзу об&rsquo;єднань євангельських християн-баптистів <strong>В&rsquo;ячеслав Нестерук</strong>, муфтій Духовного управління мусульман України шейх <strong>Ахмед Тамім</strong>, головний рабин Києва і України <strong>Яків Дов Блайх</strong> особливо зупинились на відповідальності Церков і релігійних організацій за мир в Україні. Вони наголосили на важливості єдності українського народу незалежно від конфесійних, мовних, регіональних відмінностей, за обережність у вчинках і словах, які в ситуації важкої травми, завданої режимом українському суспільству, можуть призводити до невиправних наслідків.<br /><br />При цьому голова Всеукраїнського союзу церков християн віри євангельської <strong>Михайло Паночко</strong> заявив: \"Релігійні діячі мають визнати і свою частину провини за те, що відбулося в країні, адже зустрічаючись із Януковичем, не всі з них і не завжди наважувалися говорити йому в очі всю правду\".</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Місцеблюститель Київського митрополичого престолу УПЦ митрополит <strong>Онуфрій</strong>, глава Церкви адвентистів сьомого дня <strong>Віктор Алєксєєнко</strong>, старший єпископ Української Християнської Євангельської Церкви<strong> Леонід Падун</strong> підкреслили життєву важливість порозуміння між регіонами країни, недопущення будь-яких дій та заяв, які могли б бути витлумачені як неповага одних українців до інших.<br /><br />Єпископ-помічник київської архієпархії УГКЦ <strong>Йосиф</strong> та єпископ Української Лютеранської Церкви <strong>В&rsquo;ячеслав Горпінчук</strong> говорили про необхідність гідного вшанування полеглих героїв Майдану, а також державної допомоги пораненим і постраждалим, в тому числі задля їх лікування за кордоном.<br /><br />Всі виступаючі наголошували, що загиблі під час протестів люди своїм життям і кров&rsquo;ю вибороли для України <a href=\"http://mvasin.org.ua/2014/02/247\" target=\"_blank\">шанс для фундаментальних реформ</a>, який ніхто не вправі змарнувати.<br /><br />У заключному слові Олександр Турчинов відверто розповів членам Всеукраїнської Ради Церков і релігійних організацій про масштаби тих викликів, \"які кинуті сьогодні Українській державі і кожному українцеві\". Він подякував релігійним діячам за беззастережну готовність відповідати на ці виклики разом із новою владою.<br /><br /><a href=\"http://www.irs.in.ua/index.php?option=com_content&amp;view=article&amp;id=1362%3A1&amp;catid=62%3Ainterv&amp;Itemid=85&amp;lang=uk\" target=\"_blank\">В.о. Президента України підкреслив</a>, що кровопролиття, кричуща корупція та звірства поваленого режиму були результатом бездуховності, тому &ldquo;відродження духовності неодмінно стане відродженням України&rdquo;.<br /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: center;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">ЗАЯВА</span></strong></p>\r\n<p style=\"text-align: center;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Всеукраїнської Ради Церков і релігійних організацій</span></strong></p>\r\n<p style=\"text-align: center;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">за підсумками зустрічі з Головою Верховної Ради,</span></strong></p>\r\n<p style=\"text-align: center;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">в.о. Президента України О.В. Турчиновим</span></strong></p>\r\n<p style=\"text-align: justify;\"><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Всеукраїнська Рада Церков і релігійних організацій, яка упродовж останніх майже трьох місяців, послідовно виступала на захист мирних протестів, проти застосування сили й за покарання винуватих, <a href=\"http://www.irs.in.ua/index.php?option=com_content&amp;view=article&amp;id=1362%3A1&amp;catid=62%3Ainterv&amp;Itemid=85&amp;lang=uk\" target=\"_blank\">за підсумками зустрічі з керівництвом Верховної Ради України</a>:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>1. </strong>Висловлює свою підтримку легітимній державній владі України і звертається до неї із закликом якнайшвидше забезпечити повне відновлення конституційного ладу та утвердження політичних, економічних та інших фундаментальних прав і свобод громадян. Особливу увагу ми звертаємо на необхідність рішучого, послідовного і системного поборювання корупції, яка роз&rsquo;їдає суспільний організм і є порушенням заповідей наших релігій.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>2.</strong> Ми ще раз підтверджуємо свою заяву від 22 лютого цього року, де&nbsp; категорично засуджується будь-яка дискусія щодо можливого розділення нашої Батьківщини, будь-які спроби сепаратизму й наголошується, що &laquo;територіальна цілісність України, незалежність якої дарована нам Богом, є цінністю для нашого народу, а тому допустити її розділення ми не маємо права, бо це гріх перед Богом та майбутніми поколіннями нашого народу&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ми&nbsp; засуджуємо провокування протистоянь та ворожнечу між жителями різних регіонів України, представниками різних національних меншин та віросповідань і закликаємо владу утриматися від будь-яких кроків, які б могли бути витлумачені як такі, що спрямовані на розділення українців за релігійною, мовною, національною, регіональною чи будь-якою іншою ознакою. В умовах важкого випробування ми маємо зберегти єдину Українську державу та запобігти усім можливим спробам її розділення. Звертаємося до всіх із закликом не піддаватися на провокації та ні в якому разі не підтримувати гасел, спрямованих проти територіальної цілісності України.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>3. </strong>В нинішній суспільно-політичній ситуації, ми, і Церкви та релігійні організації, і органи державної влади, маємо докласти максимум зусиль для збереження в Україні релігійного миру. У жодному випадку не можна допустити протистояння на релігійному ґрунті. Наша велика українська родина має бути єдиною в різноманітті. У вільній державі кожен має право на вільне висловлювання своїх релігійних переконань та не перешкоджати такому ж вільному сповідуванню інших поглядів. Держава має бути гарантом свободи віросповідання, а ми готові допомагати державі в реалізації цієї важливої конституційної функції.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>4.</strong> Закликаємо усіх громадян нашої Держави сумлінно виконувати свої службові та громадські обов&rsquo;язки, дотримуючись чинного законодавства та відкидати надлишкову емоційність чи острах перед змінами, які можуть бути тільки до кращого майбуття. Ми за цим пильно слідкуватимемо, та, при потребі, задіюватимемо увесь наш авторитет і потенціал. &nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>5. </strong>Ми готові до подальшої співпраці з оновленою владою та очікуємо від неї мудрості, наполегливості й чесності, що приведе нашу Батьківщину до духовного та економічного благополуччя, яке наш народ заслужив своєю вірою, стійкістю та героїчною боротьбою.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">За дорученням Головуючого у ВРЦіРО</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>+ Антоній</strong></span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Митрополит Бориспільський і Броварський,</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Керуючий справами Української Православної Церкви</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em><br /></em><a href=\"http://vrciro.org.ua/index.php?option=com_content&amp;task=view&amp;id=219&amp;Itemid=1\" target=\"_blank\"><em>26 лютого 2014 року</em></a></span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>',NULL,5578,5,1,NULL,30);
INSERT INTO `publications_publication` VALUES
(1561,'P','2014-02-28',1,NULL,'Rss','«Особи, яким буде доручена влада, мають розуміти, що повинні служити, згідно з Божою волею…»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5537,200,huzar_blog.jpg\" />Ми стоїмо на порозі нової ери в історії нашого народу. Народ, а головно молоде покоління, болісно відчуло обман колишньої влади. Проте завдяки рішучим діям народу, що вийшов на майдани по всій Україні, та невинно пролитій крові прийшла зміна. Ми щиро надіємося, що це справжня зміна до кращого майбутнього.</p>\n<p>Про це сказав Блаженніший Любомир (Гузар), Архиєпископ-емерит УГКЦ, у черговому відеоблозі.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1895:2014-02-27-23-50-51&catid=37:spilnota-cat&Itemid=49',5577,NULL,1,1,26),
(1562,'P','2014-02-27',1,NULL,'Rss','Батько і син (28 лютого)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5569,200,bild_11.jpg\" />Якось батько однієї родини як і щодень прийшов з роботи. Із серйозним виглядом лиця він сів за стіл і взявся за якісь справи. Маленький синочок побачивши, що його татусь сидить сам в кімнаті каже мамі:-</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я піду до татка, йому, напевно, самому сумно.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Дай татові спокій, хіба не бачиш скільки в нього роботи?</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1893:batko-i-syn-28-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5574,NULL,1,1,26),
(1563,'P','2014-02-27',1,NULL,'Rss','Послання Святішого Отця Франциска на Великий піст 2014 року','<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5572,200,s1.reutersmedia.net_.jpg\" />Зубожів, щоб збагатити нас Своїм убозтвом </em>(пор. 2 Кор. 8,9)</p>\n<p>Дорогі брати і сестри,</p>\n<p>З нагоди Великого Посту я пропоную деякі роздуми, щоб вони могли послужити особистому та спільнотному шляху навернення. У цих роздумах я виходжу зі слів св. Павла: «Бо ви знаєте благодать Господа нашого Ісуса Христа, що Він, будучи багатим, збіднів заради вас, щоб ви збагатились Його убозтвом» (2 Кор. 8,9 ).</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1894:poslannja-svjatishogo-otcja-francyska-na-velyky-pist-2014-roku&catid=32:gk-svit-cat&Itemid=58',5573,NULL,1,1,26),
(1564,'P','2014-02-26',1,NULL,'Rss','Бабуся (27 лютого)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5569,200,_DSC6707.jpg\" />На нещодавно відремонтованій лавці під розпуклою тополею сховавшись у затишній тіні біля входу до під’їзду сиділа добра бабуся.</p>\n<p>Сьогодні їй цілісінький день дзвонили онуки. Вона їх дуже любила і свого часу тішила чаєм та пригощала цукерками. Учора приїжджали діти щоб подякувати їй. Сусідка померла – більше ворогів у неї не було, тільки другі. І п’ять кішок – їй особливо подобалися ці смугасті створіння.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1892:babusja-27-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5572,NULL,1,1,26),
(1565,'P','2014-02-26',1,NULL,'Rss','Звернення Українського католицького університету','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5569,200,12-05-3.jpg\" />Дорога спільното УКУ! Дорогі співвітчизники! Диктатора скинуто. Безумство влади припинено. Найгірші сценарії цілковитої втрати незалежності з Божою поміччю відвернено. Здобуто те, у чому дехто з нас уже починав зневірюватися.</strong></p>\n<p>Ще тиждень тому ця вістка викликала б у нас бурхливу радість. Але сьогодні ця очевидна радість затемнена болем за понесені втрати й непевністю за майбутнє.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1891:zvernennja-ukrajinskogo-katolyckogo-universytetu&catid=37:spilnota-cat&Itemid=49',5571,NULL,1,1,26);
INSERT INTO `publications_publication` VALUES
(1566,'P','2014-02-26',1,NULL,'Rss','Рада має сформувати владу, спроможну врятувати цілісність та суверенітет України','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5569,200,index.jpg\" />Про це йдеться у&nbsp;<a href=\"http://1-12.org.ua/2014/02/20/2972\" target=\"_blank\">спільному повідомленні</a> Глав українських церков, попередніх Глав Держави та відомих суспільних авторитетів з Ініціативної групи “Першого грудня”.</strong></p>\n<p>Святійший Патріарх Київський і всієї Руси-України&nbsp;<strong>Філарет</strong> та Глава Української греко-католицької церкви Блаженнійший&nbsp;<strong>Святослав (Шевчук)</strong>,</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1890:rada-maje-sformuvaty-vladu-spromozhnu-vrjatuvaty-cilisnist-ta-suverenitet-ukrajiny&catid=28:tema-nomera-cat&Itemid=2',5570,NULL,1,1,26),
(1567,'P','2014-02-25',1,NULL,'Rss','«Ми є різні, але не поділені. Поділ між українцями намагаються створити політики»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5568,200,shevchuk_donetsk.jpg\" />Блаженніший Святослав закликав міжнародну спільноту до максимальної уваги до подій, що зараз відбуваються в Україні, наголосивши, що «небезпека ще не минула».</p>\n<p>Про це він <a href=\"http://news.ugcc.org.\" target=\"_blank\">сказав у Римі під час прес-конференції</a> для представників італійських та міжнародних ЗМІ, акредитованих при Апостольській Столиці.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1889:2014-02-25-21-48-43&catid=27:gk-v-ukrajini-cat&Itemid=57',5569,NULL,1,1,26),
(1568,'P','2014-02-25',1,NULL,'Rss','Дівчина, що подарувала квітку (26 лютого)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5564,200,463.jpg\" />Одного разу юнак зустрів у місті чудову дівчину, що продавала квіти і відразу ж закохався в неї. Він усміхнувся до неї і вона також відповіла йому усмішкою. а опісля пішла подарувавши квітку. Дізнавшись її ім’я юнак послав їй записку сподіваючись на зустріч.</p>\n<p>Минали дні. а відповіді не надходило, всі його думки були звернені до коханої, щохвилини перед його очима повстав її образ.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1888:divchyna-shcho-podaruvala-kvitku-26-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5568,NULL,1,1,26),
(1569,'P','2014-02-25',1,NULL,'Rss','Потрібно добиватися справедливості, але не носити в серці ненависті','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5564,200,646868.jpg\" />Останні події на Майдані сколихнули всю Україну. Понад 80 хлопців полягло під час протистоянь та під кулями снайперів у боротьбі з режимом. Чимало людей зараз прагне помсти та палає ненавистю до силовиків.</strong></p>\n<p>Безумовно, злочинці повинні бути покарані, однак варто втамувати свій гнів, щоби не допустити до ще однієї кривавої розправи, – наголошує речник УГКЦ о. Ігор Яців.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1887:potribno-dobyvatysja-spravedlyvosti-ale-ne-nosyty-v-serci-nenavysti&catid=28:tema-nomera-cat&Itemid=2',5566,NULL,1,1,26),
(1570,'P','2014-02-25',1,NULL,'Rss','Інтелігенція Львова висловилася проти цькування російськомовних українців','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5564,200,13932603.jpg\" />У своєму листі до влади інтелігенція Львова засудила нав\'язування донеччанам та кримчанам галицького способу життя.</strong></p>\n<p>Текст листа львівської інтелігенції:</p>\n<p>Ми вимагаємо від Верховної Ради, новопризначених членів уряду та в.о. Президента України проводити зважену культурну і мовну політику.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1886:inteligencija-lvova-vyslovylasja-proty-ckuvannja-rosiskomovnyh-ukrajinciv&catid=37:spilnota-cat&Itemid=49',5565,NULL,1,1,26);
INSERT INTO `publications_publication` VALUES
(1571,'P','2014-02-25',1,NULL,'Note','Небесна Сотня','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,5567,200,CE7F61E.jpg\" alt=\"\" width=\"200\" height=\"112\" />Гей, якби ж мені, синку, не жаль?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Якби ж мені, синку, не жаль?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти ж на моїм серцю лежав,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти ж на моїм серцю лежав.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/gD1ob199EaE#aid=P-nmxTdQbh4\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Список загиблих складений на основі даних, зібраних &laquo;Євромайданом SOS&raquo;, МОЗ, МВС, медиками Майдану, журналістами Радіо Свобода та інших ЗМІ.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Список загиблих внаслідок подій на Грушевського</strong></span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Вербицький Юрій</strong>, 50 років, активіст Євромайдану зі Львова. Його викрали 21 січня разом з активістом Ігорем Луценком з Олександрівської лікарні в Києві. Наступного дня в лісі біля Києва було виявлено тіло Юрія Вербицького, який помер від катувань на сильному морозі, його руки були зв&rsquo;язані скотчем. В МВС заявили, що Юрій Вербицький помер &laquo;від переохолодження, а тілесні ушкодження, які він отримав, не пов&rsquo;язані зі смертю&raquo;. </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Горошишин Максим</strong>, 25 років, з села Грушківка Кам&rsquo;янського району Черкаської області. Максим отримав сильне отруєння газами на Грушевського, внаслідок чого почалася пневмонія. До лікарні ще потрапив у свідомості. Місяць боровся за життя, лежав у 17 лікарні Києва. Помер у ніч на 20 лютого.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Жизневський Михайло</strong>, 25 років, громадянин Білорусі, входив до самооборони Майдану. Загинув 22 січня під час подій на Грушевського від наскрізного вогнепального поранення в серце.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Калиняк Богдан</strong>,&nbsp; 52 роки, родом з Коломиї Івано-Франківської області. Працював слюсарем на заводі &laquo;Сільмаш&raquo;, потім став приватним підприємцем. Допомагав Євромайдану грошима та товаром. 28 січня 2014&nbsp;&nbsp; помер у шпиталі в Івано-Франківську. Міський голова Коломиї Ігор Слюзар <strong><a href=\"http://tsn.ua/politika/biznesmen-z-kolomiyi-pomer-pislya-dvoh-misyaciv-na-maydani-332028.html\" target=\"_blank\">розповів</a></strong>, що Богдан Калиняк два місяці з маленькими перервами стояв на Майдані і, маючи рідну сестру в Києві, не ходив до неї, щоб відігріватися. За словами мера, після того, як на Грушевського чоловіка полили крижаною водою з водомета, він захворів, запустив хворобу. При цьому брав на себе найважчі завдання: нічні чергування, допомагав пораненим.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Нігоян Сергій</strong>, один з охоронців Майдану, 20 років, родом з Дніпропетровської області. Його родина переїхала до України, рятуючись від війни в Нагорному Карабаху. Займався легкою атлетикою та східними єдиноборствами. Провчився два курси в Дніпродзержинському коледжі спортивного виховання. Загинув 22 січня 2014 року від поранення, спричиненого свинцевою картеччю під час подій біля стадіону &laquo;Динамо&raquo; на Грушевського.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сеник Роман</strong>, 45 років, родом з Яворівщини Львівської області, помер у київській лікарні. Його тяжко поранили 22 січня на Грушевського. У нього було важке поранення в легені, переніс кілька операцій, довелося ампутувати руку, кілька діб лікарі боролися за життя Романа.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Загиблі внаслідок бійні та відстрілу снайперами, влаштованих силовиками 18-20 лютого<br /> <br /> Арутюнян Георгій</strong>, 53 роки, з Рівного, вірменин, громадянин Грузії, отримав вид на проживання в Україні, активіст ВО &laquo;Свобода&raquo;. 20 лютого 2014 року був убитий пострілом снайпера біля Монумента Незалежності у Києві. Має трьох дочок. Найменшій дочці від другого шлюбу &not;лише три роки. У 2012 році його дружина померла в Рівному, відтак тепер трьохлітня Ашхен Арутюнян Ашхен (2010 р. н.) залишилася круглою сиротою.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Байдовський Сергій</strong>, 23 роки, з Волині. З 2007 року навчався у Луцькому інституті розвитку людини університету &laquo;Україна&raquo;. Працівник магістральних нафтопроводів &laquo;Дружба&raquo;. Жив у Луцьку, любив своє місто, понад усе любив рідну Україну. Одна із останніх цитат на його сторінці в соціальній мережі слова Левка Лук\'яненка: &laquo;Нація, яка не готова посилати синів на смерть, не виживе&hellip;&raquo; Загинув від вогнепального поранення на Майдані у Києві 20 лютого. Був похований в селі Менчичі Іваницівського району Волинської області. В останню дорогу його проводжало 20 тисяч населення міста Луцька та весь рідний Нововолинськ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Бльок Іван</strong>, (або Тур&nbsp; це прізвище його дружини,але так його теж називали ), 40 років. Жив у місті Городок Львівської області. Приватний підприємець. Його побратими розповідають, що Іван кожного разу їхав до Києва тоді, коли була найбільша небезпека. Уперше вирушив після того, як &laquo;беркутівці&raquo; напали на студентів у ніч проти 30 листопада, потім після 11 грудня й в інші найнебезпечніші періоди. Коли 18 лютого знову почали вбивати, сів з друзями на бус і поїхав на Майдан. 20 лютого вранці на Інститутській він загинув від кулі снайпера.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Бондарєв Сергій</strong>, 32 роки, з Краматорська Донецької області, останні роки жив&nbsp; і працював в Києві, програміст GlobalLogic. Загинув під час першого штурму від чотирьох кульових поранень біля Будинку профспілок 18 лютого.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Бондарчук Сергій</strong>, 52 роки, зі Старокостянтинова Хмельницької області. Викладав фізику в Старокостянтинівській гімназії. Помічник-консультант народного депутата України Ігоря Сабія. З 2009 року голова Старокостянтинівської міської організації ВО &laquo;Свобода&raquo;.&nbsp; Загинув 20 лютого 2014 року на вулиці Інститутській.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Брезденюк Валерій</strong>, 50 років, м. Жмеринка Вінницької області, український підприємець, художник у техніці ебру (малюнки на воді). Вбитий пострілом у спину 18 лютого 2014 року.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Не буде більше малюнків на воді</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Вайда Богдан</strong>, 48 років, з села Летня Дрогобицького раqону, Львівська область. На київський Майдан поїхав 19 лютого. Вже зранку наступного дня, приїхавши до Києва, <strong><a href=\"http://drohobychanyn.io.ua/s672314/u_letni_pohovali_novitnogo_geroya_bogdana_vaydu\" target=\"_blank\">зателефонував сестрі</a></strong>: &laquo;Люба, тут справжня війна! Стріляють...&raquo;, &ndash; після цього розмова обірвалася. Того ж дня по обіді повідомили про смерть Богдана, який загинув від кулі снайпера-вбивці. &laquo;Він віддав життя за мене, за кожного з нас, за те, щоб ми були вільними людьми&raquo;, &mdash; сказав отець Олексій Дем\'яновський на прощанні з Богданом Вайдою.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Войтович Назар</strong>, 17 років, з села Травневе Збаразького району, Тернопільська область. Студент-третьокурсник Кооперативного коледжу в Тернополі. Загинув 20 лютого 2014 року. Хлопець навчався на третьому курсі відділення дизайну, обожнював живопис. Усі роботи Назара мали український дух, на малюнках зображав калину, тризуб чи козаків. Ще в середу, 19 лютого, Назар був на парах, а ввечері мав віднести до автобуса, який їхав на Київ, речі для столичного Майдану. Та в останню хвилину щось змінилось, і Назар вирішив&nbsp; їхати на&nbsp; Майдан особисто. &laquo;Останній раз, коли його чули, це було&nbsp; близько сьомої вечора, і він дуже спішив на автобус. Дуже спішив з гуртожитку. Йому нетерпілось поїхати до Києва, навіть підготував колишню каску&hellip;. Ми його ніколи не забудемо&raquo;, &ndash; <strong><a href=\"http://gazeta-misto.te.ua/news/17-%D1%80%D1%96%D1%87%D0%BD%D0%B8%D0%B9-%D0%B7%D0%B1%D0%B0%D1%80%D0%B0%D0%B6%D0%B0%D0%BD%D0%B8%D0%BD-%D0%BD%D0%B0%D0%B7%D0%B0%D1%80-%D0%B2%D0%BE%D0%B9%D1%82%D0%BE%D0%B2%D0%B8%D1%87-%D0%BF%D1%96%D1%88/\" target=\"_blank\">розповідає одногрупниця. </a></strong>Назар поїхав до Києва вночі 19 лютого, а вже наступного дня під час протистоянь на Майдані хлопця вбили з вогнепальної зброї. Він у батьків єдиний.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Голоднюк Устим</strong>, 19 років, зі Збаража Тернопільської області. Учасник самооборони, стрілець сотні &laquo;Свободи&raquo;, волонтер &laquo;Демократичного Альянсу&raquo;. Був захисником Майдану з листопада 2013 року. 30 листопада &laquo;Беркут&raquo; побив його, на потилиці був шрам, наклали 12 швів. Тільки-но зажило, то знову поїхав на Майдан. 20 лютого 2014 року внаслідок вогнепального поранення в голову загинув. Отримав вогнепальне поранення зі снайперської гвинтівки, допомагаючи забирати загиблих біля верхнього виходу станцїї метро &laquo;Хрещатик&raquo;. Куля снайпера влучила у праве око. Коли його доправили у готель &laquo;Україна&raquo;, лікарі вже не могли допомогти хлопцю. О 9 ранку 20 лютого Устим домовився зустрітися з батьком об 11, але не дожив. Опізнаючи сина, батько Володимир Голоднюк, <strong><a href=\"http://narodne-slovo.te.ua/2014/02/20/volodymyr-holodnyuk-ya-ne-znayu-chy-yanukovych-mae-stoyaty-peredi-mnoyu-na-kolina-ale-ya-znayu-tochno-scho-vin-mae-sydity-pered-mizhnarodnym-trybunalom-za-te-scho-vin-zrobyv-z-mojim-synom-z-moeyu/\" target=\"_blank\">колишній міліціонер, сказав</a></strong>: &laquo;Я не знаю, чи має Янукович стояти переді мною на колінах, але я знаю точно, що він має сидіти перед міжнародним трибуналом за те, що він зробив з моєю країною та з мохм сином&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Городнюк Іван</strong>, 29 років, з Березного Рівненської області. 19 лютого Іван повернувся додому з Майдану, під час штурму &laquo;беркутівцями&raquo; його облили водою з водометів і побили. Звернувся до лікарів, <strong><a href=\"http://4vlada.com/rivne/33537\" target=\"_blank\">почав лікуватися</a></strong>. Але вночі серце молодого чоловіка не витримало і зупинилося. Його ховало все Березне. Він активно займався громадською діяльністю, захоплювався музикою та танцями.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Гриневич Едуард</strong>, 28 років, родом з села Деревки Любешівського району, Волинська область. Громадський активіст, член партії ВО &laquo;Свобода&raquo;, член &laquo;Волинської сотні&raquo;. Едуард Гриневич зростав і виховувався у сім&rsquo;ї без батька. Зі слів близьких, звик у житті досягати всього сам, був наполегливим і цілеспрямованим. Захоплювався інформаційними технологіями. Певний час працював в &laquo;Укртелекомі&raquo; міста Луцька. Потім вирішив розпочати власну справу. Був на Майдані тричі. <strong><a href=\"http://uk.wikipedia.org/wiki/%D0%93%D1%80%D0%B8%D0%BD%D0%B5%D0%B2%D0%B8%D1%87_%D0%95%D0%B4%D1%83%D0%B0%D1%80%D0%B4_%D0%9C%D0%B8%D0%BA%D0%BE%D0%BB%D0%B0%D0%B9%D0%BE%D0%B2%D0%B8%D1%87\" target=\"_blank\">Вважав це справою честі та гідності.</a></strong> Востаннє поїхав у Київ 16 лютого, а вже за чотири дні куля снайпера обірвала його життя. Хлопець залишив по собі щоденник.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Варениця Роман</strong>, 35 років, з Яворівського району Львівської області. Застрелений на вулиці Великій Житомирській, імовірно, тітушками.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Васильцов Віталій</strong>, 1977 р. н. Уродженець с. Гаврілівці, Хмельницької області, що мешкав у с. Жорнівка Києво-Святошинського району Київській області. Був застрелений на вулиці Великій Житомирській 18 лютого. Мав двох дітей.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Веремій В&rsquo;ячеслав</strong>, не дожив три дні до свого 34-річчя, український журналіст, кореспондент газети &laquo;Вести&raquo;. У 2003 році закінчив Інститут журналістики Київського національного університету імені Тараса Шевченка.Працював журналістом у &laquo;Газеті по-київськи&raquo;, на сайті &laquo;Обозреватель&raquo;, газеті &laquo;Сегодня&raquo;. Він був одним із провідних журналістів видання, писав на гострі соціальні теми, проводив журналістські розслідування.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> 19 січня 2014 року В&rsquo;ячеслав отримав поранення лівого ока від світлошумової гранати. У ніч проти 19 лютого В\'ячеслав разом із колегою їхали з роботи на таксі додому. На розі Володимирської та Великої Житомирської він помітив озброєних ті тушок у камуфляжі та масках і намагався їх сфотографувати з машини. Озброєні люди тут же напали на автомобіль, почали його трощити, витягнули водія та пасажирів. Журналіста з криками &laquo;Кого снимал? Зачем снимал?&raquo; сильно побили, після чого вистрелили в груди та залишили стікати кров\'ю. При цьому присутні на місці працівники міліції залишалися осторонь та не втручалися в події. У В&rsquo;ячеслава залишилися дружина та син.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Гурик Роман</strong>, 19 років, з Івано-Франківська, студент Прикарпатського національного університету імені Василя Стефаника. Романа. Загинув у Києві 20 лютого від кулі снайпера. Хлопця поховали в <strong><a href=\"http://www.radiosvoboda.org/content/article/25274092.html\" target=\"_blank\">Меморіальному сквері по вулиці Мельника в Івано-Франківську</a></strong>, де розташовані могили січових стрільців та вояків УПА.Як повідомив ведучий івано-франківського Майдану Ростислав Микитюк, жителі міста пропонують назвати одну з вулиць обласного центру Прикарпаття на честь Романа Гурика. З такою ініціативою звернуться до міської ради.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Дворянець Антоніна</strong>, 62 роки. Тіло виявили на барикаді, розташованій на вулиці Інститутській біля верхнього входу в метро &laquo;Хрещатик&raquo;. У неї знайшли посвідчення ліквідатора чорнобильської катастрофи № 073155. Загинула від забиття кийками спецпризначенцями міліції.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Дзявульський Микола</strong>, 56 років, з Шепетівки. Вчитель географії та біології, член ВО &laquo;Свобода&raquo;. Застрелений на Інститутській 20 лютого снайпером. Брав активну участь у громадсько-політичному житті міста: обирався депутатом міської ради 1994-1998 років, керівник громадської організації &laquo;Шепетівська спілка підприємців&raquo;. Довгий час працював на викладацькій роботі вчителем географії шепетівської школи-ліцею, останній час був помічником народного депутата від ВО &laquo;Свобода&raquo; Ігоря Сабія. Земляки говорять, що усі зусилля Миколи Дзявульського були спрямовані на патріотичне виховання молоді, він переконував, що в цей нелегкий час неможливо стояти осторонь від будь-яких подій.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Дідич Сергій</strong>, 44 роки. Депутат Городенківської районної ради Івано-Франківської області від ВО &laquo;Свобода&raquo;, директор туристичного клубу &laquo;Золоте Руно&raquo;. Загинув 18 лютого 2014 року.Сотник івано-франківської сотні на столичному Майдані. У Жовтневому палаці він відповідав за порядок на другому поверсі.Сергій Дідич більшу частину свого життя присв\'ячував туристичному розвитку рідного краю. Його громадську організацію &laquo;Золоте Руно&raquo;, яка займалася перш за все сплавами по Дністру, знають в усій Україні і за її межами.Він перший почав розробляти туристичні маршрути сплавів по рідному Дністру. Дністер, за планами Сергія, мав стати другим масштабним центром туристичного розвитку Івано-Франківщини. Будучи депутатом райради, їздив на стареньких &laquo;Жигулях&raquo; і завжди намагався допомагати людям. У загиблого - дорослий син та донька-школярка, батьки.&nbsp; Сергій із дружиною на Майдані був з початку грудня, Різдво відсвяткував на передовій.18 лютого 2014 граната розірвала йому сонну артерію на шиї.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Дигдалович Андрій</strong>, 40 років, з села Сокільники Пустомитівського району, Львівська область, 40 років. Вбитий 20 лютого від пострілу снайпера. Куля пройшла навиліт через бронежилет. Андрій Дигдалович <strong><a href=\"http://lvivexpres.com/news/2014/02/23/58730-tysyachi-lvivyan-provely-ostannyu-dorogu-geroya-nebesnoyi-sotni-andriya\" target=\"_blank\">був на Майдані три місяці</a></strong>. Ще у грудні був одним з перших, хто стримував натиск &laquo;Беркуту&raquo;. Комендатура Майдану нагородила його &laquo;Орденом за оборону Майдану&raquo;. Родич Андрія Ігор Менкуш розповів, що того було поранено на Грушевського: на одне око Андрій майже втратив зір. Проте довго не лікувався і повернувся до побратимів з афганської сотні. Наче передчуваючи біду, напередодні Андрій зателефонував до дружини і сказав: &laquo;Ти сильна, ти знаєш, що робити...&raquo; А загинув Андрій, рятуючи молодого хлопця.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Дмитрів Ігор</strong>, 30 років, з села Копанки Калуського району, Івано-Франківська область. Загинув 20 лютого від пострілу снайпера в легені. Нещодавно закінчив Одеську юридичну академію. Розлучений, дітей не мав. У Ігоря нещодавно помер батько, мати перебуває в Італії на заробітках. Ігор Дмитрів був членом Рожнятівської районної організації ВО &laquo;Свобода&raquo;. Самотужки звів будинок у Копанках, <strong><a href=\"http://vikna.if.ua/news/category/kl/2014/02/22/16609/view\" target=\"_blank\">проте жити у ньому &mdash; не довелося.</a></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Жаловага Анатолій</strong>, 1980 р. н., з села ДубляниЖовківського району Львівської області. Загинув 20 лютого.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Зайків Яків</strong>, 73 роки, з Житомира. Народний депутат України 12(1) скликання від Народного руху, головний редактор журналу &laquo;Голос громадянина&raquo;. 18 лютого 2014 року помер від інфаркту, втікаючи від &laquo;Беркуту&raquo; на вул. Інститутській.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Захаров Володимир</strong>, 57 років. ІТ-спеціаліст. Загинув під час пожежі в офісі Партії регіонів на Липській. За одними даними, чоловік задихнувся від димових шашок після того, як його побили бітами, обрізками труб і ногами. За іншими даними, застрелений вистрілом в голову.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Капінос Олександр</strong>, 29 років, фермер з Кременця, мав наречену, з якою зустрічався на Майдані. У 2012 році витримав 12 днів без їжі на знак протесту проти &laquo;мовного закону&raquo;. Любив дуже працювати на землі, також організовував патріотичні табори для дітей і молоді, створив власну громадську організацію, був членом ВО &laquo;Свобода&raquo;. На Майдан Сашко їздив періодично. &laquo;Було стільки скепсису щодо майбутнього Майдану: люди могли розійтися, його могли розігнати. Але Сашко казав був такий впевнений, що все буде, вірив тільки в добре&raquo;, &ndash; розповідає його подруга Оксана Неживенко. Під час сутичок на Грушевського 18 лютого Сашко був сильно поранений прицільний пострілом з рушниці,&nbsp; каже його друг Михайло Згар. За іншими свідченнями, у нього влучила граната біля Будинку профспілок.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Кемський Сергій</strong>, 33 роки, з Керчі. Він працював в Інституті політичних та економічних ризиків і перспектив, <strong><a href=\"http://www.pravda.com.ua/columns/2013/12/19/7007716/\" target=\"_blank\">писав для &laquo;Україснької правди&raquo;</a></strong>. Загинув 20 лютого.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Ось що Сергій написав у своїй статті від 23 грудня 2013 під назвою &laquo;Чуєш, Майдане?&raquo;: &laquo;Майдан скандує &laquo;Банду геть!&raquo; і дійсно бажає, щоб теперішні керманичі звільнили крісла. Разом із тим, кожен погоджується, що ми тут зібрались не для того, аби обирати нового доброго царя.Вимога громади полягає в перетворенні держави з феодального батога на інструмент самоорганізації суспільства. Нам більше не потрібні пастухи &ndash; нам потрібні виконавці волі громади, які ефективно координуватимуть суспільні ресурси для досягнення спільних цілей. Майдан вимагає, щоб люди, наділені повноваженнями, дбали про громадські цінності, а не про сімейні&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Кіпіані Давід</strong>, 33 роки, громадянин Грузії. Був на Майдані три місяці. Його тіло знайшли поряд з барикадою біля ЦУМу з двома кульовими пораненнями в ніч проти 21 лютого. Помер у &laquo;швидкій&raquo;. Залишився однорічний син. Його друзі кажуть, що українці дали їм надзвичайний приклад...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Кіщук Володимир</strong>,&nbsp;1956 р.н., із села Димар Вишгородського району, Київська область. Загинув 18 лютого у Києві під час сутичок між мітингувальниками та &laquo;беркутівцями&raquo;. Проломлена голова в районі потилиці. Тіло виявили у Будинку офіцерів. &laquo;Він передзвонив з Майдану. Такий дуже щасливий був. Каже, я &ndash; на Майдані, відстоюю наші права&raquo;, - розповідає його донька Тетяна Петрівська. На мітинг під парламент 18 лютого пішов без каски і без щита. &laquo;Поранення було в голову. Нам сказали, що вистріл був зверху. Десь зверху стріляв якийсь снайпер&raquo;, - каже донька. Мав трьох доньок та двох онуків.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Корнеєв Анатолій</strong>, голова Рудської сільської ради в Кам&rsquo;янець-Подільському районі Хмельницької області.&nbsp; 20 лютого вбив снайпер на Майдані. &laquo;Його не можна було втримати вдома, батько усім серцем рвався у Київ на Майдан. Говорив: &laquo;Як можна ховатися, коли там вбивають дітей&hellip;&raquo;, - <strong><a href=\"http://ye.ua/news/news_15347.html\" target=\"_blank\">каже донька Дарина</a></strong>. &laquo;Уявляєте, у нього було близько 50 сортів лише однієї картоплі. А, якщо у Анатолія хтось брав саджанець, то той неодмінно приживався. Він дуже часто їздив на Майдан у Київ, возив туди картоплю, буряк, моркву та інші овочі, які сам вирощував. Не міг залишатися осторонь, не така він людина. Не був байдужим&raquo;, - розповівгазеті &laquo;Є&raquo; колега Анатолія Корнєєва голова Княжпільської сільської ради Микола Брянгін.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Корчак Андрій</strong>, 49 років, був неодружений. Загинув 18 лютого під час сутичок на вул. Інститутській. &laquo;Беркутівці&raquo; йому проламали голову. На Майдані був ще з листопада. Завжди був на передових. Робив усе: і в Самообороні охороняв Майдан, і дрова рубав. Напередодні загибелі Корчак зателефонував до брата Юрія.&ensp;Говорив, що відпочиває в наметі, але щось його всередині муляє &mdash; якесь нехороше передчуття, <strong><a href=\"http://gazeta.ua/articles/politics-newspaper/_andrij-korchak-buv-na-majdani-z-listopada/543144\" target=\"_blank\">розповідає Юрій Корчак</a></strong>. Перед обідом 18 лютого Юрій востаннє розмовляв з братом. Той&nbsp; сказав, що на Інститутській на них наступає &laquo;Беркут&raquo;. За дві години слухавку взяв медбрат. Сказав, що в Андрія розбита голова. Схоже на черепно-мозкову травму, і його не можуть реанімувати. Помер Андрій Корчак у столичній лікарні №17. Він походив із сім\'ї колишніх репресованих. Працював на місцевих будівництвах. Після звільнення їздив на заробітки до Чехії. У нього залишилися 81-річна мати.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Костенко Ігор</strong>, 22 роки, студент географічного факультету Львівського університету, вчився за спеціальністю &laquo;Менеджмент організація&raquo;, був одним з кращих студентів,&nbsp; родом з Тернопільщини, працював журналістом інтернет-видання &laquo;Спортаналітика&raquo;. Хлопець був активним редактором української Вікіпедії, написав понад 280 статей, зробив понад 1600 редагувань. Був убитий в районі Жовтневого палацу вистрілами в голову та груди. За словами його друга Володимира, який переносив тіло Ігоря, його ноги були побиті &laquo;так, що їх можна було зав\'язати на вузол&raquo;.Загинув 20 лютого 2014 року.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Котляр Євген</strong>, 33 роки, з Харкова. Альпініст, природоохоронець, активіст екологічного руху &laquo;Зелений фронт&raquo;, запам&rsquo;ятався тим, що боровся за порятунок парка Горького в Харкові від вирубки, критикував діяльність Добкіна та Кернеса. Буввбитий 20 лютого, потрапивпідкулібіля метро Хрещатик, біляверхньоговиходу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Коцюба Віталій</strong>, 31 рік, зі Львова. Народився в селі Вороблячин Яворівського району Львівської області. Загинув 20 лютого 2014 року від вогнепального поранення. Залишив дружину та двох малих дітей. Близький родич Віталія Володимира <strong><a href=\"http://zik.ua/ua/news/2014/02/23/lvivshchyna_proshchaietsya_z_ukrainskym_geroiem_vitaliiem_kotsyuboyu_463943\" target=\"_blank\">розповів zik.ua</a></strong>: &laquo;Коли загинув Віталій, то його дружина на той час поїхала в Польщу на заробітки, а дітей доглядала тітка Ірина. Самі розумієте, як жити молодій сім&rsquo;ї в нашій державі без роботи&hellip; Треба якось собі раду давати. Ось Віталій і поїхав на Майдан, бо вже сили не мав терпіти знущання цієї клятої влади над народом. Першого разу все обійшлося. А другого &ndash; поплатився життям за мрію бачити молодих українців щасливими на рідній землі, а не рабами на закордонних заробітках. Віталій дуже любив своїх дітей, кохав дружину, хотів для них доброї долі&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Креман Іван</strong>, з Кременчука. Загинув 20 лютого на Інститутській. Прихильник ВО &laquo;Свобода&raquo;.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Кульчицький Володимир</strong>, 64 роки, з Києва. Загинув 18 лютого. Поховали в селі Нові Петрівці. Куля пройшла на виліт в серце зі спини, застрягла в одязі. Друга влучила йому в живіт. У нього був тільки шолом для захисту. Кулю з тіла витягнув син Ігор, коли батько впав на землю, розповів брат загиблого.</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Максимов Дмитро, 19 років. Срібний та бронзовий призер з дзюдо Дефлімпійських ігор у Софії. Він був надією для національної дефлімпійської збірної команди України по дзюдо, у Софії-2013. Від вибуху гранати йому відірвало руку. Хлопця хтось відвів у Будинок профспілок, де він помер від втрати крові. Його тіло декілька днів лежало в Михайлівському соборі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Мельничук Володимир</strong>, 39 років. Приносив на Майдан теплі речі, їжу, у сутичках участі не брав, розповідають його друзі. Помер у 17-тій лікарні від вогнепальних поранень у шию 20 лютого. У чоловіка вистрелив снайпер. Чоловік не мав на собі жодної захисної амуніції і просто спостерігав за протистояннями під стінами Жовтневого палацу. Олена, знайома Володимира, <strong><a href=\"http://ntn.ua/uk/news/murders/2014/02/23/13001\" target=\"_blank\">розповіла</a></strong>: &laquo;Дуже гарний хлопець був, він у той день майданівців вивозив своєю машиною до лікарень. Він там стояв, до нього підійшла дружина, у цей момент в нього вистрелив снайпер&raquo;. &laquo;Він був без захисту, без каски... не знаю, як... певно, цьому снайперу було зручно відстрелювати людей, які не захищені. Не вкладається в голові... такі люди пішли. Молодь&raquo;, &ndash; розповідає знайомий Олександр.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Мовчан Андрій</strong>, 34 роки, Київ. Працював у театрі ім. Франка освітлювачем, член Демократичного Альянсу. Загинув від вогнепального поранення в голову 20 лютого 2014 року. На фото, прикріпленому на дверях під&rsquo;їзду будинку, де жив Андрій, є припис, що загинув він, <strong><a href=\"http://gazeta.ua/articles/np/_bilya-pidyizdu-de-zhiv-zastrelenij-geroj-andrij-movchan-rozsipali-chervoni-gvozdiki/543674\" target=\"_blank\">прикриваючи від кулі дівчину.</a></strong></span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Мойсей Василь</strong>, 21 рік. Активіст Ківерцівської міської організації ВО &laquo;Свобода&raquo; Волинської області. Загинув 20 лютого від поранення в грудну клітку.Вранці 20 лютого 2014 року на вулиці Інститутській у нього влучив снайпер. Хлопця не врятувало те, що він був у цивільному бронежилеті. <strong><a href=\"http://uk.wikipedia.org/wiki/%D0%9C%D0%BE%D0%B9%D1%81%D0%B5%D0%B9_%D0%92%D0%B0%D1%81%D0%B8%D0%BB%D1%8C_%D0%9C%D0%B8%D1%85%D0%B0%D0%B9%D0%BB%D0%BE%D0%B2%D0%B8%D1%87\" target=\"_blank\">Навчався </a></strong>в Луцькому інституті розвитку людини Університету &laquo;Україна&raquo; (м. Луцьк) на 4-му курсі факультету соціальних комунікацій і реабілітації. На своїй сторінці у соціальній мережі, незадовго до смерті написав: &laquo;Краще вмерти вовком &mdash; ніж жити псом&raquo;.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Наумов Володимир</strong>, 43 роки.Народивсь в с. Шевченки Добропільського району в день народження Шевченка. Знав Кобзар напам\'ять. Працював водієм. В селі залишилась 85-річна мама, дружина та троє дітей. За позовом серця поїхав на своїй машині на Майдан. Його знайшли загиблим на Трухановому острові вранці 18 лютого. Мав на собі хусточку Самооборони Майдану. За версією міліції &ndash; самогубство. За іншою версією, неміліцейською: був викрадений і задушений.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Опанасюк Валерій</strong>, 42 роки, з Рівного. Загинув 20 лютого від снайперської кулі. Депутат Рівненської ради Олександр Лащук<strong><a href=\"http://4vlada.com/rivne/33546\" target=\"_blank\"> познайомився з Валерієм у рейсовій маршрутці, коли їхали на Київській Майдан</a></strong>:&laquo;Життєрадісний, розумний і дуже добрий чоловік. В майданівському наметі в Києві, де зупинялисярівняни, вінбував постійно, причому завжди у найскладніші часи протистояння. Його знало багато рівнян, які бували на Майдані. Цілими днями чергував на барикадах, допомагав новоприбулим протестувальникам. Якщо хтось легко вбраний, завжди сам шукав і пропонував тепліший одяг. А сам ходив завжди, навіть в найлютіші морози, в сіренькій утепленій кофтині, в якій і приїхав&raquo;. Коли на Грушевського були складнімоменти,вінказав, що треба там стояти, щоб не було мало людей, тому що &laquo;Беркут&raquo; почне атакувати. У нього залишилися дружина та четверо дітей, найменшому &ndash; два роки.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Павлюк Володимир</strong>, з Коломиї, вік біля 40 років. Загинув 20 лютого. Отримавчотирипоранення: три у ногу, одне у ліве око.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Пагор Дмитро</strong>, 21 рік. Загинув увечері в Хмельницькому біля будівлі СБУ, звідки була відкрита стрілянина. Був поранений у голову, невдовзі помер в лікарні. &laquo;У морзі нам сказали, що в ньогокульовепоранення, ніби то стрілялиіз автомата Калашникова, але кулі не показали. Дімазакривав собою дівчину, коли луналипостріли&raquo;, &ndash; <strong><a href=\"http://ye.ua/news/news_15321.html\" target=\"_blank\">розповів</a></strong> дядько загиблого.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Пантєлєєв Іван</strong>, 32 роки, з Краматорська Донецької області, рок-музикант, автор пісень, гітарист гурту &laquo;Небо мінуса&raquo; http://vk.com/nebo_minusa. Іван мріяв про те, щоб його гурт та пісні стали відомими, доглядав за своєю 90-річною бабусею в селі в Донецькій області. На Майдан приїхав 10 грудня після того, як побачив, як &laquo;Беркут&raquo; побив студентів у ніч проти 30 листопада. &laquo;Він шкодував, що не поїхав раніше. Ваня говорив, що там образили людей, і він хоче допомогти їм, захистити. Тому й поїхав&raquo;, &ndash; <strong><a href=\"http://www.vp.donetsk.ua/gorod/10388-pogibshij-v-kieve-ivan-panteleev-ya-dolzhen-byt-tam.html\" target=\"_blank\">розповіла </a></strong>сестра Світлана. Другу, який мав їхати з ним до Києва, але не зміг, сказав на прощання: &laquo;Я відчуваю, що маю бути там&raquo;. Приватбанк Пантелеева Людмила Ивановна 5168757224009979.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Паньків Микола-Олег</strong>, 39 років, з села Лапаївка Пустомитівського району Львівської області. Брав участь у блокуванні військових частин, щоб не допустити виїзд силовиків до Києва. &laquo;Востаннє він поїхав до Києва у вівторок ввечері. Я його просила: Сину не їдь! Він відповів: &laquo;Я не поїду, другий не поїде, а в цій державі будуть жити моїдіти&raquo;, &ndash; <strong><a href=\"http://lvivexpres.com/news/2014/02/24/58743-lapayivci-geroyem-nebesnoyi-sotni-proshchalysya-pisnyamy-foto\" target=\"_blank\">розповіла</a></strong> його матір. Помер від кулі снайпера в груди, 20 лютого. Це трапилось, коли він витягував мітингувальників, поранених та вбитих снайперами з передової. Без батька залишилось двоє неповнолітніх дітей.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Паращук Юрій</strong>, 48 років, мешкав Харкові. Загинув 20 лютого 2014 року на Майдані від снайперської кулі. Був на Майдані з 1 грудня 2013 року. Останнім часом був волонтером, намагаючись врятувати людей. Він не мав жодних захисних засобів, навіть шолома. Снайпер поцілив йому в потилицю під час відступу силовиків з барикад на вул. Інститутській, повідомив народний депутат Ігор Швайка.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Пасхалін Юрій</strong>, 30 років, з села Носачів Черкаської області. Закінчив школу у м. Сміла. Був і вихованцем Школи олімпійського резерву в Харкові, займався штангою. В останні роки проживав у Києві, працював на приватному підприємстві комірником. Похований у рідному селі. Залишився семирічний син. Отримав три вогнепальні поранення в спину 18 лютого й одне пневматичне поранення, першу допомогу було надано в Будинку профспілок. Після підпалу будинку його евакуйювали до 18-й лікарні на бульварі Шевченка, проте врятувати не вдалося. Юрій удень 18 лютого був на Майдані, потім пішов додому, але коли почув, що його розганяють, то <strong><a href=\"http://provce.ck.ua/u-holodnomu-yaru-pohovaly-dvoh-kozakiv-z-nebesnoji-sotni-majdanu/\" target=\"_blank\">повернувся й загинув.</a></strong></span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Плеханов Олександр</strong>, 22 роки, студент архітектурного факультету КНУБіА, наступного дня мав отримати диплом бакалавра. Загинув 18 лютого.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Полянський Леонід</strong>, 38 років, з Києва, родом з Вінниччини.&nbsp; Рідні розповідають, що він був життєрадісним чоловіком і не міг стояти осторонь беззаконня, що відбувається в Україні - тому і ходив на Майдан. У нього залишились діти від двох шлюбів. В останні роки жив і працював у Києві, але похорон відбувся у Жмеринці, звідки родом.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Прохорський Василій</strong>, 33 роки. Допомагав ліками Майдану. 18 лютого був там&nbsp; і з того моменту не виходив на зв\'язок, додому не повернувся, на роботу не вийшов.&nbsp; Друзі знайшли його речі біля Михайлівського собору. Тіло упізнали 22 лютого 2014 року. Був дуже сильно скалічений, зуби вибиті.</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Саєнко Андрій, 51 рік, з Фастова Київської області. Належав до Самооборони Майдану. Загинув 20 лютого.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Саєнко Андрій</strong>, 1962 р.н., з Фастова Київської області. </span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Сердюк Ігор</strong>, 40 або 44 роки, з Кременчуга, підприємець, займався ремонтами. До Києва їздив з перших днів протестів, вступив до Самооборони, був знаменоносцем 9 сотні. Майже два місяці пробув на Майдані, повертався тільки час від часу додому, щоб побачитися з дружиною та донькою, <strong><a href=\"http://okrain.net.ua/news/read/Pri-stolknovenijah-v-Kieve-pogib-Kremenchuzhanin-Igor-Serdjuk.html\" target=\"_blank\">розповів</a></strong>&nbsp; активіст кременчуцького &laquo;Майдану&raquo; Сергій Полюхович. Сердюку Ігорю&nbsp; вистрелили з обрізу в обличчя. 18 лютого він будував барикаду, коли на нього напали тітушки та &laquo;беркутівці&raquo;, повідомив Сергій Полюхович. Друзі просять усіх, хто може мати відео-, фото- подій на перехресті Кріпосного провулку та Інститутської 18 лютого, допомогти з&rsquo;ясувати, як саме його вбили, kosovann@gmail.com</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сміленко Віктор</strong>, 1961 р.н., з села Борисівка Бобринецького району, Кіровоградська область. Загинув 20 лютого.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Смолинський Віталій</strong>, село Фурманівка Уманського району. Загинув 20 лютого. Він жив та працював у Києві. Віталій, стоячи на передовій, <strong><a href=\"http://vch-uman.in.ua/material.php?id=626\" target=\"_blank\">захищав майбутнє </a></strong>своїх двох маленьких синів та дружини.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Сольчаник Богдан</strong>, 28 років, історик, викладач Українського католицького університету. Працював на кафедрі нової та новітньої історії України УКУ. Готувався до захисту дисертації з історії на тему &laquo;Виборчі практики в малому західноукраїнському місті 1965&ndash;2006 роки&raquo;. Мав ступінь бакалавра історії, магістра соціології, закінчив докторантуру в Польській академії наук, Graduate School for Social Research, був стипендіатом Міністерства національної освіти Польщі. Убитий на Інститутській 20 лютого. Його друг Максим Мірєєв, з яким познайомилися в Криму, розповів: &laquo;Сьогодні його вбил снайпер, коли він віднолював барикаду на Майдані, зруйновану за попередні дві ночі. Коли почалися протести на Євромайдані, він не міг залишатися осторонь. Покинув викладацьку роботу й приїхав захищати свою країну від Президента, який зґвалтував власну країну&raquo;.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Тарасюк Іван</strong>, 21 рік, з села Олика Ківерцівського району, Волинська область. 20 лютого загинув на Майдані від вогнепального поранення, стріляв у хлопця снайпер.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Ткачук Ігор</strong>, 1975 р. н. Мешканець Великої Кам\'янки Коломийського району Івано-Франківської області. Загинув від пострілу снайпера 20 лютого 2014 року. Мав трьох дітей - 17 років, 11&nbsp; та кілька місяців.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Точин Роман</strong>, 44 роки, з міста Ходорів Львівської області. Загинув на Майдані 20 лютого 2014 року від вогнепального поранення в голову. Був членом ВО &laquo;Свобода&raquo;.Його сотня охороняла барикаду на Інститутській. Залишилась дружина і двоє доньок.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Ушневич Олег</strong>, 1982 р.н., з Дрогобича. Загинув 20 лютого. Застрелений спайпером на Інститутській біля Жовтневого палацу.Народився в Дрогобичі в сім&rsquo;ї працівників ВАТ НПК &laquo;Галичина&raquo; Михайла та Євгенії Ушневич. Багатьом працівникам нафтопереробного активіст запам&rsquo;ятався під час похорону його матері у 1985 році: Євгенія Ушневич померла під час пологів, народивши Олегові брата. Тоді трирічний Олег, не усвідомлюючи, що сталося, ніжно та беззахисно тулився до своєї бабці.На Майдані в Києві Олег Ушневич був з першого дня. Його добре знають на Сколівщині, де після закінчення вишу вчителював. В останні роки виїжджав на заробітки за кордоном.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Храпаченко Олександр</strong>, 26 років, театральний режисер, з Рівного. Загинув 20 лютого від снайперського пострілу просто в око, що спричинило миттєву смерть.</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Цепун Андрій, 35 років. Андрій загинув у ніч на 21 лютого.Він був у команді активістів, які ввечері 20 лютого блокували в&rsquo;їзд до столиці з Гостомельської траси. Після цього стомлений Андрій пішов сам додому. Його знайшли вбитим на вулиці Верболозній з ознаками побиття й смертельної черепно-мозгової травми. За свідченнями друзів, його швидше за все <strong><a href=\"http://crime.in.ua/news/20140223/zepun\" target=\"_blank\">викрали по дорозі додому</a></strong>. Є свідчення людей, які бачили, що на вулиці, де жив Андрій, невідомі заштовхали в автівку чоловіка. Андрій мешкав на Мінському масиві, а знайшли його &ndash; аж на Куренівці. У нього залишилися дружина та 8-річний син. Всі, хто хоче допомогти його родині, можуть перерахувати гроші на картку&nbsp; ПриватБанка 5168742312081288 (Галайда Олексій Олексійович), друг Андрій, перевірено.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Царьок Олександр</strong>, з с. Калінін Васильківського району, Київська область. Загинув біля Жовтневого палацу.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Хурція Зураб</strong>, 54 роки, з Грузії, проживав у Кіровограді. Тіло виявили на барикаді, розташованій на вулиці Інститутській, біля верхнього входу в метро &laquo;Хрещатик&raquo; 18 лютого. Помер від серцевого нападу.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Чаплінський Володимир</strong>, з Обухова Київської області. Його дружина працює в дитячому садку, у сім\'ї - двоє дітей: син та донька.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Черненко Андрій</strong>, 35 років, з села Слобо-Петрівка Гребінківського району, Полтавська область. Загинув 19 лютого. Без батька залишилася семимісячна дитина.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Чернець Віктор</strong>, 37 років, з села Подібна Черкаської області. На трасі Одеса-Київ жителі Маньківки та Умані біля села Подібна встановили блок-пост на дорозі, аби тітушки та спецпризначенці не дісталися до столиці. Водій невстановленого джипа, який пробивав дорогу внутрішнім військам, на шаленій швидкості 19 лютого збив на смерть Віктора Чернця.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Чміленко Віктор</strong>, 53 роки, з села Борисівка Бобринецького району, Кіровоградська область. 20 лютого на Майдані застрелив снайпер.</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Шаповал Сергій, 1969 р. н. з Києва, активіст Самооборони. Знайшли померлим в Будинку офіцерів 18 лютого.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Швець Віктор</strong>, 56 років,з села Гатне Києво-Святошинського району Київської області. Kолишній міліціонер, був уже на пенсії. 18 лютого пішов на Майдан, як сам казав, захищати молодь. О 23:00 подзвонив сім\'ї і сказав, що з ним усе добре. Близько першої години ночі з 18 на 19 лютого отримав три вогнепальні поранення в черевну порожнину на Майдані. Швидка забрала до лікарні, але він помер по дорозі. Без батька залишилась дитина.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Шеремета Людмила</strong>, 75 років. Поранена в голову в Хмельницькому біля будівлі СБУ під час акції протесту. З будівлі СБУ була відкрита стрілянина з автомата. Померла в лікарні.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Шилінг Йосип</strong>, 61 рік, з Дрогобича Львівської області. Виховав двох дочок, має чотирьох внучок. За професією &ndash; будівельник. Був членом Української гельсінської спілки та Української республіканської партії в часи здобуття незалежності України. Останні роки часто їздив на заробітки до Італії. Дружина Анна працює в Італії. На київський Майдан їздив кілька разів, затримувався там надовго. Хотів жити у вільній, заможній країні &ndash; аби внуки не шукали щастя по закордонах. Загинув 20 лютого від пострілу в голову біля Жовтневого палацу. Під час прощання з Йосипом Шилінгом громадський діяч Мирослав Глубіш <a href=\"http://zik.ua/ua/news/2014/02/23/na_drogobychchyni_pohovaly_geroya_maydanu_yosypa_shylinga_464029\" target=\"_blank\"><strong>сказав</strong></a>: &laquo;І тепер, коли над Україною нависла загроза втрати державності, він знову став в перші лави борців з режимом і загинув, відкупивши нас усіх найдорожчим &ndash; своїм життям&raquo;.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Шимко Максим</strong>,&nbsp;33 роки, з Вінниці. Загинув 20 лютого 2014. Останній запис, який чоловік залишив на своїй сторінці у &laquo;ВКонтакте&raquo; 18 лютого &ndash; &laquo;За Україну!!!!!!!!!!!!! Всі хто може їдьте в Київ !!!&raquo; Був учасником клубу історичної реконструкції &laquo;Білий вовк&raquo;.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Щербанюк Олександр</strong>, 46 років, з Чернівців. Загинув 20 лютого на Інститутській. Учасник усіх виборчих перегонів, член ВО &laquo;Батьківщина&raquo;. Снайпер вцілив у нього просто біля чернівецького намету &laquo;Батьківщини&raquo;, повідомляє прес-служба обласної організації партії. &laquo;Буковинська правда&raquo; пише, що в Чернівцях Олександр Щербанюк працював приватним підприємцем, займався будівництвом. У нього залишилась дружина, хвора мама і двоє дітей - син, який навчається в 9-класі та 20-річна донька. На Майдані був з самого початку з перервами. Перед тим, як їхати на Майдан востаннє, <strong><a href=\"http://vidido.ua/index.php/pogliad/article/kicjunju_mi_vzjali_instituts_ku_ja_ne_mozhu_govoriti_oleksandr_erbanjuk/\" target=\"_blank\">сказав </a></strong>дружині: &laquo;Щоби мої слова не розходилися з ділом. А я кажу, що справжній мужчина повинен бути там, де потрібні зараз люди, на передовій&raquo;.</span><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <br /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загиблі міліціонери</strong></span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Булітка Василь</strong>,Київ, 1986 р. н.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Власенко Дмитро</strong>, Крим, 1982 р. н.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Гончаров Віталій</strong>, Крим, 1989 р.н.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Євтушок Володимир</strong>, Київ, 1971 р.н.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Іваненко Олексій</strong>, Харків, 1977 р.н.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Савицький Петро</strong>, Київ, 1972 р.н.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Спічак Сергій</strong>, Бердянськ</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Теплюк Іван</strong>, Чернігів, 1993 р.н.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Третяк Максим</strong>, Чернігів, 1993 р.н.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Федюкін Андрій</strong>, Крим, 1972 р.н.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>Цвігун Сергій</strong>, Запоріжжя, 1990 р.н.</span></p>\r\n<div class=\"expandSmall floatCenter\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></div>',NULL,5567,5,1,NULL,13);
INSERT INTO `publications_publication` VALUES
(1572,'P','2014-02-25',1,NULL,'Note','Вулицями Києва пройшла Хресна Дорога','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5564,200,xresna.jpg\" alt=\"\" width=\"200\" height=\"141\" />24 лютого центральними вулицями Києва пройшла Хресна Дорога. Молилися за загиблих, які полягли останніми днями на Майдані, за їхні родини, за всіх медиків та волонтерів, за кожного українця.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Молебень розпочався від барикад, що на вул. Грушевського, де загинули перші. Натомість наступні стояння вели повз Європейську площу по вул. Хрещатик, а завершилися на вул. Інститутській.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">24 лютого центральними вулицями Києва пройшла Хресна Дорога. Молилися за загиблих, які полягли останніми днями на київському Майдані, за їхні родини, за всіх медиків та волонтерів, за кожного українця, а особливо за тих, хто ще досі бореться за свободу та правду.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ще декілька місяців тому ніхто і не думав, що на головній вулиці країни відбудеться Хресна Дорога з тисячами українців, де не потрібно було б питати дозволу у влади міста.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Близько 20 чоловіків несли великий Подячний хрест, що з&rsquo;явився на Майдані вранці 22 лютого за сприянням владики Станіслава Широкорадюка, який звернувся із проханням про збереження миру серед народу. &laquo;Цей хрест освячений в 2004 році ще блаженним Йоаном Павлом ІІ. Тоді він започаткував таку місію, сказав, що сьогодні світ потребує Божого милосердя. Він освятив цей хрест і попросив людей, щоб вони його возили по усім столицям світу. І там давали людям дивитися на нього, щоб зрозуміти ціну нашого спасіння. І на сьогодні хрест об&rsquo;їздив вже 34 столиці світу&raquo;, - сказав єпископ Станіслав.</span><br /><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Молебень розпочався від барикад, що на вул. Грушевського, де 19 січня загинули перші три особи. Натомість наступні стояння вели повз Європейську площу по вул. Хрещатик, а завершилися на вул. Інститутській.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Слід відзначити те, що на початку Хресної Дороги було всього-на-всього кілька сотень осіб, які знали про дійство, яке має відбуватися. Але вже досить швидко зійшлися тисячі людей, які приєдналися до молитви, - повідомляє Християнський портал <a href=\"http://kyrios.org.ua/\" target=\"_blank\">КІРІОС</a> з посиланням на Католицький <a href=\"http://www.catholic-media.org/ua/\" rel=\"nofollow\" target=\"_blank\">медіа-центр</a>.</span></p>',NULL,5564,5,1,NULL,42),
(1573,'P','2014-02-25',1,NULL,'Rss','«Ми живемо в критичний, але не поганий момент…»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5562,200,huzar_blogy.jpg\" />Дотепер ми ішли шляхом посткомунізму, постбільшовизму, пострадянського. Ще дуже багато радянського є в нас сьогодні. Нинішня ситуація для нас дуже прикра, критична. Проте, якщо ми з Божою допомогою будемо добре працювати, то звернемо на добрий шлях.</p>\n<p>Про це сказав Блаженніший Любомир (Гузар), Архиєпископ-емерит УГКЦ, в інтерв’ю для <a href=\"http://news.ugcc.org.ua/go/aHR0cDovL2t5cmlvcy5vcmcudWEvbmV3cy9pbnRlcnZqdS8xMTg4NS1kdWhvdm5pLW5hc3Rhbm92aS1zdWNoYXNub211LWhyaXN0aWphbmludS12aWQtYmxhemhlbm5pc2hvZ28tbGp1Ym9taXJhLWd1emFyYS5odG1s\" target=\"_blank\">християнського порталу «Киріос»</a>.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1885:2014-02-25-16-40-49&catid=28:tema-nomera-cat&Itemid=2',5563,NULL,1,1,26);
INSERT INTO `publications_publication` VALUES
(1574,'P','2014-02-25',1,NULL,'Rss','Три приятелі і щастя (25 лютого)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5560,200,dti.jpeg\" />Три приятелі мріяли про щастя, кожен про своє. Але вони уявляли його по різному. Перший думав, що щастя – це багатство, другому щастям видався талант, третій вважав. що щастя – це сім’я.</p>\n<p>Минув час, перед смертною годиною зібралися друзі, щоб підсумувати своє життя.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1883:try-pryjateli-i-shchastja-25-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5562,NULL,1,1,26),
(1575,'P','2014-02-24',1,NULL,'Rss','«Ходжу по барикадах і прошу хлопців, щоб вони жили, - жили для своєї землі»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5560,200,o_i_halo.jpg\" />З отцем Іваном Гальом (із Самбірсько-Дрогобицької єпархії, парафія Велика Сущиця, Хирівський деканат) ми сіли розмовляти одразу після панахиди, яку він відправив за десятьма загиблими героями, які майже цілий день пролежали в готелі «Україна», чекаючи, поки їх відправлять на судово-медичну експертизу. Полеглих героїв проводжають Гімном України…</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1884:2014-02-24-16-01-22&catid=28:tema-nomera-cat&Itemid=2',5561,NULL,1,1,26),
(1576,'P','2014-02-24',1,NULL,'Rss','Молитва виганяє злого духа, але тільки щира молитва','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5559,200,header_img.png\" />Святий Марко детально описує змагання Ісуса зі злим духом, якого  апостоли не змогли вигнати. Пояснює також причину поразки Його учнів:  щоб перемогти злого духа, бажання замало, його слід перемагати  відповідною зброєю&nbsp;— постом і молитвою.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1882:molytva-vyganjaje-zlogo-duha-ale-tilky-shchyra-molytva&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5560,NULL,1,1,26),
(1577,'P','2014-02-24',1,NULL,'Rss','Не питай ... живи ','<p style=\"text-align: justify;\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5556,200,1009025.jpg\" />Не питай чому я мусив вмерти</p>\n<p style=\"text-align: justify;\">Не питай взагалі \"чому?\"</p>\n<p style=\"text-align: justify;\">Моя кров зупинила смерті</p>\n<p style=\"text-align: justify;\">Кров моя зупинила війну.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1881:ne-pyta--zhyvy-&catid=1:mystecka-storinka-cat&Itemid=50',5559,NULL,1,1,26),
(1578,'P','2014-02-24',1,NULL,'Rss','Корисна перерва (24 лютого)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5556,200,849405.jpg\" />Якось учень і його вчитель посперечалися, хто за годину нарубає більше дров у лісі.</p>\n<p>Кожен вибрав собі ділянку. Учень взявся рубати і його сокира не змовкала ні на хвилину. Так, він хотів перемогти. Тоді як сокира учителя. час від часу змовкала.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1880:korysna-pererva-24-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5558,NULL,1,1,26),
(1579,'P','2014-02-24',1,NULL,'Rss','«Територію Майдану потрібно поширювати далі…»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5556,200,panaxyda_u_sobori.jpg\" />Великий суд відбувався останніми місяцями  на нашій землі. Під час нього спадали маски та виявлялися приховані  вчинки, як достойні і величні, так і ниці та нелюдські. Господь у своєму  великому милосерді скоротив дні нашого випробування і вже те, що було  вчора, поволі відходить в історію. Проте через ці події історія України  змінилася назавжди.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1879:2014-02-24-09-27-16&catid=28:tema-nomera-cat&Itemid=2',5557,NULL,1,1,26);
INSERT INTO `publications_publication` VALUES
(1580,'P','2014-02-23',1,NULL,'Rss','Голка і нитка (23 лютого)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5553,200,756.jpg\" />Голка і нитка лежали в одній кравецькій коробці. Якось у них виникла суперечка, почали з’ясовувати – хто більше дає користі.</p>\n<p>Голка:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я з’єдную краї тканини і прокладаю дорогу для Тебе.</p>\n<p>Нитка почала заперечувати:</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1878:golka-i-nytka-23-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5556,NULL,1,1,26),
(1581,'P','2014-02-22',1,NULL,'Rss','Неділя про Страшний суд','<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5553,200,oz09.jpg\" />\"Господь гряде, і хто стерпить Його страх, хто явиться перед лицем Його, але, душе моя, будь готова на стрічу\" </em></p>\n<p>Третя пісня канону утрені М\'ясопусної неділі</p>\n<p>Притча про блудного сина минулої неділі символічно показала нам  незглибиме милосердя Бога, який приймає навіть найбільшого грішника,  коли той кається і шкодує, що здійснив гріхи.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1877:nedilja-pro-strashny-sud&catid=27:gk-v-ukrajini-cat&Itemid=57',5555,NULL,1,1,26),
(1582,'P','2014-02-22',1,NULL,'Rss','Молодь і майбутнє України','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5551,200,700x463.jpg\" />Переживаємо трагічні і водночас великі дні. </strong></p>\n<p>Ці дні великі, бо в них ми пізнали нашу державу, нашу владу та власний народ.</p>\n<p>Ці дні великі, бо сповнені трагізмом, позначені терпінням, ба навіть смертю молодих невинних людей.</p>\n<p>Ці дні великі, бо саме в них народжується наше майбутнє.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1876:molod-i-mabutnje-ukrajiny&catid=28:tema-nomera-cat&Itemid=2',5553,NULL,1,1,26),
(1583,'P','2014-02-22',1,NULL,'Rss','ВІДБУВАЄТЬСЯ КОЛОСАЛЬНЕ ОЧИЩЕННЯ УКРАЇНСЬКОГО СУСПІЛЬСТВА','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5551,200,r026.jpg\" /></p>\n<p>Життя кожної людини, спілкування людей, наша гідність – це Божі дари. В Україні ці дари по-звірячому потоптали. Мільйони наших вірних та інших відданих громадян України майже три місяці мирно вимагали гідного життя. Проте цього тижня більше від сотні людей було холоднокровно вбито, а ще тисячу – поранено. Наша країна перебуває в розпалі великої трагедії. Небезпека дальшого насилля та фрагментації нависає над нашою землею.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1875:vidbuvajetsja-kolosalne-ochyshchennja-ukrajinskogo-suspilstva&catid=27:gk-v-ukrajini-cat&Itemid=57',5554,NULL,1,1,26),
(1584,'P','2014-02-22',1,NULL,'Rss','Секрет успішного подружжя (22 лютого)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5551,200,images.jpg\" />Ви одруженні вже чимало років. яка таємниця успішного подружжя.</p>\n<p>- Я повсякчас захоплюються своєю жінкою. Якщо у твого сусіда трава зеленіша - це значить, що ти не поливаєш свою траву. Я кажу жінці раз у раз: ти прекрасна, ти жадання мого серця. ти пречудова. Навіщо?</p>\n<p> </p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1873:sekret-uspishnogo-podruzhzhja-22-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5551,NULL,1,1,26),
(1585,'P','2014-02-21',1,NULL,'Rss','НУНЦІЙ ҐАЛЛІКСОН: БУДЬТЕ ВІДВАЖНІ!','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5551,200,apostolskiy-nunciy.jpg\" />У своєму</strong><strong> </strong><strong><a href=\"http://deovolenteexanimo.blogspot.com/\" target=\"_blank\">блозі</a></strong><strong> </strong><strong>Апостольський Нунцій в Україні Томас Едвард Ґалліксон продовжує публікувати думки про ситуацію в Україні. Цього разу він торкнувся історії заснування Києва, щоб роздумувати над його майбутнім. У псалмі 54(55) Нунцій знаходить ключ до розв’язання теперішньої нелегкої ситуації.</strong></p>\n<p>Всі величні міста мають свою легенду заснування. Рим має Ромула та Рема. Київ має свого однойменця, який оселився тут, прибувши по Дніпру, з двома братами та сестрою.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1874:nunci-gallikson-budte-vidvazhni&catid=28:tema-nomera-cat&Itemid=2',5552,NULL,1,1,26);
INSERT INTO `publications_publication` VALUES
(1586,'P','2014-02-21',1,NULL,'Rss','Плавці (21 лютого)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5548,200,1037.jpg\" />Якось троє друзів купалися в ріці над водоспадом. Перепливаючи через річку. вони помітили, що їх несе вода. Течія була слабка. але що ближчим був водоспад тим швидшою ставала течія. Занепокоївшись двоє приятелів повернулися на берег і почали кликати третього. а він тільки сміявся у відповідь і кричав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Мені пливеться так легко.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1872:plavci-21-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5550,NULL,1,1,26),
(1587,'P','2014-02-20',1,NULL,'Rss',' «Русскій мір» і Майдан як загроза','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5547,200,7576.png\" />У церковно-державній мові робить фурор формула «русскій мір» («російський світ»). Представники Церков і державні інституції використовують його для визначення пострадянських територій.</strong></p>\n<p>Цим поняттям визначають не тільки мешканців Російської Федерації чи російську діаспору, розсіяну на території колишнього Радянського Союзу, але також православних з України, Білорусі, Молдови і Казахстану.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1871:2014-02-20-08-09-14&catid=32:gk-svit-cat&Itemid=58',5548,NULL,1,1,26),
(1588,'P','2014-02-20',1,NULL,'Rss',' Заява Українського католицького університету ','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5546,200,header.logo.gif\" />“Ми однозначно стверджуємо: відповідальність за нинішню ескалацію лягає виключно на владу – особисто на Віктора Януковича та його «яструбину» команду.</p>\n<p>Кожен випадок ескалації конфлікту, який щоразу вражає своєю цілковитою безглуздістю, перекреслює наші надії на мирне й мудре подолання кризи й наближає нас до гуманітарної катастрофи. Кожен такий випадок ніби ставить галочку «виконано» у таємних планах «кризових менеджерів».</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1870:-zajava-ukrajinskogo-katolyckogo-universytetu-&catid=37:spilnota-cat&Itemid=49',5547,NULL,1,1,26),
(1589,'P','2014-02-19',1,NULL,'Rss','Маленький ліхтарник (20 лютого)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5539,200,Wl3MXEEV8Os.jpg\" />У ті часи, коли ліхтарі запалювали вогнем. вулицями щовечора ходили ліхтарники і приносили світло у кожний провулок. Жив тоді маленький ліхтарник: щуплий дідок низького зросту. Щовечора він обходив провулки, черкав сірником по підошві і запалював ліхтарі. І кожна темна вуличка ставала яснішою ніж зазвичай.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1865:malenky-lihtarnyk-20-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5546,NULL,1,1,26),
(1590,'P','2014-02-19',1,NULL,'Rss',' Заява ВРЦіРО з приводу ескалації насильства у Києві ','<p align=\"justify\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5544,200,676.jpg\" />Всеукраїнська Рада Церков і релігійних організацій рішуче засуджує  насильство, кровопролиття та вбивства, що мали місце у Києві 18 лютого  2014 року та тривають досі.</p>\n<p align=\"justify\">Ми знову і знову закликаємо, просимо й благаємо сторони протистояння  припинити силові дії, а представників влади й опозиції продовжити  переговори. До завершення переговорів ми закликаємо владу і опозицію  негайно зупинити силові дії з обох боків.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1869:-zajava-vrciro-z-pryvodu-eskalaciji-nasylstva-u-kyjevi-&catid=28:tema-nomera-cat&Itemid=2',5545,NULL,1,1,26);
INSERT INTO `publications_publication` VALUES
(1591,'P','2014-02-19',1,NULL,'Rss','Розпорядження Отця і Глави УГКЦ щодо позиції і поведінки духовенства в теперішній суспільно-політичній ситуації в Україні','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5543,200,u_rymi.jpg\" />Дорогі брати у священичому служінні! У зв’язку із драматичною ситуацією в нашій країні хочу нагадати всім вам фундаментальні норми священичого служіння:</p>\n<p>1.&nbsp;&nbsp;&nbsp; Церква – спільнота дітей Божих, об’єднаних вірою в Триєдиного Бога, а священик є служителем цієї спільноти, образом Христа - Доброго Пастиря, діяльно присутнім серед свого народу.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1868:rozporjadzhennja-otcja-i-glavy-ugkc-shchodo-pozyciji-i-povedinky-duhovenstva-v-teperishni-suspilno-politychni-sytuaciji-v-ukrajini&catid=28:tema-nomera-cat&Itemid=2',5544,NULL,1,1,26),
(1592,'P','2014-02-19',1,NULL,'Rss',' Чи ти чесний сам перед собою?','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5542,200,45356.png\" />«Чесність&nbsp;— це перший розділ у книзі мудрості» (Томас Джефферсон).</strong></p>\n<p>Великі філософи і мудреці виводять свої вчення з отакої істини: «Будьте чесні, ’а все інше вам додасться’». Бо вони розуміють, що всі люди прагнуть до заспокоєння потреб любові, свободи, поваги і почуття сенсу життя. Ключ до розуміння людської поведінки знаходиться у найглибших закапелках серця. Треби лиш мати достатньо відваги, аби туди зазирнути.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1867:-chy-ty-chesny-sam-pered-soboju',5543,NULL,1,1,26),
(1593,'P','2014-02-19',1,NULL,'Rss',' Чи ти чесний сам перед собою?','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5542,200,45356.png\" />«Чесність&nbsp;— це перший розділ у книзі мудрості» (Томас Джефферсон).</strong></p>\n<p>Великі філософи і мудреці виводять свої вчення з отакої істини: «Будьте чесні, ’а все інше вам додасться’». Бо вони розуміють, що всі люди прагнуть до заспокоєння потреб любові, свободи, поваги і почуття сенсу життя. Ключ до розуміння людської поведінки знаходиться у найглибших закапелках серця. Треби лиш мати достатньо відваги, аби туди зазирнути.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1867:-chy-ty-chesny-sam-pered-soboju&catid=37:spilnota-cat&Itemid=49',5549,NULL,1,1,26),
(1594,'P','2014-02-18',1,NULL,'Rss','Притча про трьох будівників (19 лютого)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5539,200,pan2.jpg\" />Сталося це в середньовіччі. Чернець, що керував будівництвом собору, вирішив подивитися як працюють каменярі. Він підійшов до першого і попросив його розповісти про свою роботу.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1864:prytcha-pro-troh-budivnykiv-19-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5542,NULL,1,1,26),
(1595,'P','2014-02-18',1,NULL,'Rss',' Майдан: «Цирк Батерфляй», чи Хрестовий похід ','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5538,200,1456682.jpg\" />Архієпископ  Томас Ґалліксон, Апостольський нунцій в Україні, продовжує у своєму  блозі роздуми про Україну. Цього разу він порівняв ситуацію в Україні з  короткометражним фільмом «Цирк Батерфляй». Пропонуємо вам повний текст  його роздумів.</p>\n<p><strong>Архієпископ Томас Едвард Ґалліксон у своєму <a href=\"http://deovolenteexanimo.blogspot.com/\">блозі</a> пише:</strong></p>\n<p>Я  би хотів, щоби люди за межами України мали більшу пошану до людей, яких  я тепер почав глибоко поважати та любити так як незабаром буде два з  половиною роки як я живу в Києві та околицях. Ваш пересічний іноземний  кореспондент, який навіть жив довгі роки біля якоїсь столиці-сусіда не  розуміє: українці є унікальними й такою ж є їхня країна.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1866:2014-02-17-21-39-09&catid=28:tema-nomera-cat&Itemid=2',5541,NULL,1,1,26);
INSERT INTO `publications_publication` VALUES
(1596,'P','2014-02-17',1,NULL,'Rss','Чому жінка плаче (18 лютого)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5539,200,thumb.jpg\" />Маленький хлопчик спитав у мами:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чому Ти плачеш?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тому, що я жінка.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Не розумію.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1863:chomu-zhinka-plache-18-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5540,NULL,1,1,26),
(1597,'P','2014-02-17',1,NULL,'Note','Заповіт Данила Братковського - поета, шляхтича, учасника повстання Палія','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5539,200,f4-1.jpg\" alt=\"\" width=\"200\" height=\"113\" />Синам і дочкам моїм нагадую, щоб тієї віри трималися, за яку я вмираю, якщо хочуть мати благословення від Бога.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пригадую собі: чотири тисячі книжок видання мого і надто ще сто, які залишилися у Кракові, у Цезарего, за котрі винний тому ж пану Цезарему двісті тинфів; ті книжки дружині моїй відписую, якщо побажає, може погодити з паном Цезарим.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,5539,200,f4-1.jpg\" alt=\"\" width=\"200\" height=\"113\" />Року 1707, місяця червня 27 дня.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На уряді гродському, в замку його королівської милості луцькому, переді мною, Миколаєм Анатазієм Глібовичем Пероцьким, намісником на цей час бурграбства і замку луцького і книгами теперішніми, гродськими луцькими особисто стала шляхетна її милість пані Братковська, вдова, для запису до книг нинішніх подала остаточним поданням заповіт останньої волі небіжчика, що зійшов у певний час зі світу, його милості пана Данила Братковського, підчашого венденського, з підписами його самого і приятелів, про що цей заповіт, з повним і детальним описом, такими словами до книг теперішніх вписаний: в ім&rsquo;я Отця і Сина і Святого Духа амінь. Я, порох, попіл Творця мого, Спасителя мого беру собі на Страшний суд за правдивого свідка, що не для жодних бунтів, про що Він сам відає, що їх не було в моєму серці і лише для ревності Бога мого, для честі Православ&rsquo;я святого смерть прийняти мушу, прошу його святого захисту, аби прийняв в руки дух мій, прошу і Матір Найсвятішу його, для якої чуда пулконовських святих у Києві в світ видавав, через те бути мусив в його милості пана гетьмана задніпровського, тому що хочу порятувати мою душу усіх тих святих, про яких писав, закликаю на поміч, аби захотіли при муках і смерті моїй стати і мені опорою і душу моїй. Така є душа моя грішна, яку в руки Бога мого віддаю, а тіло моє, як землю до землі віддаю, що має бути поховане синами моїми, особливо старшим, у місці святому, в луцькій братській церкві.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А тепер насамперед про суму тридцяти талерів битих і двох, які при синові старшому є готівкою і двісті шістнадцять золотих, без тих двох талерів: найперше, тридцять золотих боргу віддати тому ж братству луцькому; потім інші тридцять золотих, якщо жодного кревного той хлоп в Певгах не має, у якого я наказав шкапу відібрати в чагарниках; коли був у Малині, віддавав їх (гроші), але взяти не хотів, про це син старший потурбуватися повинен, якщо кревні будуть, то кревним віддати, а якщо таких не буде, то на те ж місце святе, до Чесного Хреста за душу його на півтора сорокоуста той син мій віддати повинен; також з тих самих грошей на те ж місце святе на два сорокоусти за душу мою; також до місця святого Загоровського на сорокоуст двадцять золотих, як і на місце святе до Почаєва двадцять золотих; як і на місце святе Панни найсвятішої Покрови святої в Луцьку, також на сорокоуст двадцять золотих; до храму святого Михайла, який у Луцьку, на пів сорокоуста десять золотих; двадцять золотих на духовенство, на обід жалобний для убогих, оскільки немає кому обід готувати, тому той таки син мій на обід після похорону золотих десять щоб дав. Залишилося золотих шістдесят один, цей залишок дівчатам моїм на потребу зимову; як і в його милості пана стольника, дядька їхнього, двадцять і чотири золотих, аби його милість одній віддав дванадцять злотих, другій теж дванадцять злотих, або збіжжям для прожиття. Синів моїх долю так наділяю: оскільки син мій старший видав мені сімсот шістдесят золотих з тисячі, а син молодший з другої тисячі,спершу залишивши в себе тільки сто золотих на панів Пасевичеву і Пасевича та інших сто золотих, що йому належали, повинен потім старшому братові своєму залишок по-приятельськи віддати. Дівчат моїх так наділяю: розписка від львівських євреїв, що знаходяться в отця Самуеля, старшого львівського братства на десять сотень золотих, які разом з процентами можуть скласти й більшу суму. З тієї суми, спершу, п&rsquo;ятсот золотих старшій дівчині моїй як посаг, сто золотих на постіль, сто золотих на різні потреби, як-то на пошиття сукенки комкової, кунтуша та іншого, дай Боже швидко, на весілля. Тисяча золотих польських на луцькому кагалі &ndash; є документ і екзекуція за бані цією від його милості короля у того ж отця Самуеля від літ з десяти, шукати в Луцьку, вилучити і з тим їхати до милості його пана підскарбія. Свідомий того, що якщо і не повну суму, принаймні цю тисячу злотих і п&rsquo;ятсот злотих, котрі на середопістя римське через львівського єврея віддати повинний.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Так тепер сукна на два кунтушики темно-зелені дівчатам моїм, старшій &ndash; спершу розкроїти; рисяче хутро моє на підклад тій же старшій даю (може залишиться і молодшій Камілії на щось подрібніше). Атлас молодшій до зросту. Челядникові, що зі мною їздив до Києва &ndash; штучку полотна Московського; другу штучку такого ж полотна московського &ndash; дівчині молодшій; третя штучка такого ж полотна &ndash; старшій дівчині; дівчатам, онукам моїм, даю штучку такого ж полотна. Сину моєму старшому того коня, що маю від нього; синові молодшому другого коня, з клячею; синові моєму старшому кунтуш темно-зелений; синові молодшому півтори штуки китаю московського; синові моєму старшому півтори штуки такого ж китаю московського; синові молодшому кунтуш цинамовий.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приступаю до суми, яка на кагалі луцькому була, на котрій півтори тисячі. Дівчині моїй молодшій сто золотих на посаг записую, як є давній запис з донацією від двох років, оригінал донації, як і всі права, відшукати. (Старшій), якщо пан Бог благословить мати швидко приятеля, щоби ту молодшу при собі тримала. Якщо ті півтори тисячі з кагалу луцького були б знайдені, тоді син старший повинен дати у заставу євреям львівським під проценти, з яких тій дівчині було б на потреби. Його милості пану Богучевичеві, племінникові моєму, якому тепер востаннє вклоняюся, прошу дуже, аби з сином моїм старшим був виконавцями цього заповіту і яким &ndash; його милості пану Богушевичові і синові моєму старшому, права в руки віддаю, розпорядження моє таке: щоби якнайперше вони рятували душу мою. Тому ж його милості пану племінникові, аби й далі був ласкавим до дітей моїх, допоки дасть пан Бог, як маю надію швидко в Бозі моїм тій старшій дівчині приятеля, - півтори штучки того ж китаю московського офірую, а півтори штучки його милості пану Бобрикевичеві, зятеві моєму, офірую, просячи, аби ввів чоловіка до донечки моєї; на це призначаю двісті злотих.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Синам і дочкам моїм нагадую, щоб тієї віри трималися, за яку я вмираю, якщо хочуть мати благословення від Бога.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приступаю наприкінці до милої дружини моєї, яка після оплакування, з усяких записів, навіть із тих п&rsquo;ятсот злотих, котрі котрі мені пан Зотович, зять її, за мої збитки, що сума моя &ndash; чотири тисячі, чекаючи такої ж суми від неї, даремно лежала, як про це процес правний в його милості пана Бжовського свідчить, тепер з усякого, від малого до великого, милій дружині моїй і діткам її цією останньою волею квитую борги і вічно визволяю; також милу дружину мою прошу, закликаючи до милосердя Божого, щоб тій сироті, дівчині моїй старшій, ті сукенки віддала, спокій вічний зі мною на суд Страшний тим зберегла.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Залишається дві суми &ndash; двісті і кілька десятків злотих у львівських євреїв, корі старший мій син, як другу так і першу, на середопістя римське відшукавши, злотих п&rsquo;ятдесят злотих собі взяв, а ті двісті злотих, порадившись з дружиною моєю, куди б на які місця сто золотих роздати на сорокоусти? Син мій, відібравши сорок злотих, на Волинь відвезти має для тих бідних сиріт моїх і знову двадцять поновляю, що разом буде шістдесят злотих, а залишок злотих, а залишок злотих сорок має віддати дружині моїй милій, щоб обід, один і другий, за душу мою убогим справила. Також убогим щоб за звичаєм давала. Амінь.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Для ліпшої ваги ту останню воля рукою моєю підписую. Їх милостей панів приятелів до підпису впросив. У Луцьку, в ув&rsquo;язненні, дня тринадцятого листопада, тисяча сімсот другого року.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пригадую собі: чотири тисячі книжок видання мого і надто ще сто, які залишилися у Кракові, у Цезарего, за котрі винний тому ж пану Цезарему двісті тинфів; ті книжки дружині моїй відписую, якщо побажає, може погодити з паном Цезарим.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Шабля молодшому синові.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В того тестаменту, для останнього подання, підписи рук тими словами:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Данило Братковський, підчаший Венденський. При тому були приятелі, які за проханням пана Братковського, підчашого венденського, підписалися. Ян Вацлав Бобрикевич. Як запрошений приятель приятель підписався на тому тестаменті Данило з Брах. Гулевич-Перекальський. Той тестамент, для остаточного подання поданий, за поданням і проханням вищеназваної особи, що подавала, а за моїм урядовим прийняттям, до книг нинішніхгродських луцьких є вписаний.</span></p>',NULL,5539,5,1,NULL,13);
INSERT INTO `publications_publication` VALUES
(1598,'P','2014-02-17',1,NULL,'Rss','У чому полягає праведність Христового учня?','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5536,200,Cosimo.jpg\" />Щоб добре і чесно поводитися, не вистачить юридичних норм, для цього потрібні глибокі спонуки. Таке зауваження висловив Папа Франциск перед проказуванням молитви «Ангел Господній» у неділю, 16 лютого 2014 р., коментуючи євангельський уривок, в якому прозвучали частини з нагірної проповіді Христа – першої Його великої проповіді. Цього разу темою було ставлення Ісуса до єврейського закону у світлі Його слів: «Не думайте, що я прийшов усунути закон чи пророків: я прийшов їх не усунути, а доповнити».</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1862:u-chomu-poljagaje-pravednist-hrystovogo-uchnja&catid=32:gk-svit-cat&Itemid=58',5538,NULL,1,1,26),
(1599,'P','2014-02-17',1,NULL,'Note','Лист1 Олени К., яка відбуває покарання в Качанівській виправній колонії, м.Харків','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">... Я зараз все больше стараюсь перебывать с Богом и читать Слово Боже бо мене вібрали старшой нашей церкви и мы по воскресениям собираемся и я розказую о Боге...&nbsp;</span></p>','<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава Ісусу Христу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Здравствуйте о.Ігорь, получила от Вас пісьмо которому очень обрадовалась, я Вам очень благодарна за Ваше пониманиё, поддержку и за Ваше вниманиё...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Нащет литературного конкурса... я писала и пишу стихи для доці, мами, сестре, племянникам просто я хочу когда я освобожусь, чтоб мои родниё взяли мою тетрадь и почитали как сильно я их люблю. Ну я напишу Вам пару стихов може какие нибудь подойдут до литературного конкурсу... етот стих я написала 5.12.2013г.</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>&laquo;Самому родному человеку&raquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">На улице лето дождик идёт</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">А я всё сижу и скучаю</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И время уходит, и время идёт</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">А сколько уйдёт я незнаю.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Назад невернутся ушедшие дни</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И в етом лишь я виновата</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Сама совершила ошибки свои</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И вот мне за ето расплата.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">А дома скучает живой человек</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Он ждёт меня, ждёт и скучает</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Роднее него нет на свете вовек</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Надеюсь что он ето знает.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Он знает, что мама на свете одна,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Что мама души в нём не чает,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Что мама скучает и любит всегда</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Надеюсь, что он ето знает.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Хочу сократить я минуты свои,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Что тянутся очень жестоко</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Минуты же сложатся скоро в часы</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">А сердце болит одиноко.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Мой милый ребёнок, тебя я прошу</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Я просто тебя умоляю</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Прости непутёвую маму свою</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Тебя я люблю очень сильно поверь</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">У нас будет всё как и прежде</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Тебя никому не отдам я теперь</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Живи лишь в любви и надежде.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Быть может спасибо мне нужно сказать</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">За дни проведённые ети.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ведь только сейчас стала я понимать,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Что Ты мне важней всех на свете,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Что Ты самый лучший и самый родной</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ты самый любимый на свете</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ты знай я вернусь очень скоро домой</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Тебя расцелую при встречи.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Я скоро вернусь мой родной человек,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Я скоро вернусь обещаю.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Тебя не оставлю теперь я вовек</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ты знаешь и я ето знаю</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И снова луч сонца на небе блеснет</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Обиды уйдут и тривоги</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">С тобой я пойду теперь только вперёд</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">По длинной широкой дороге.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ручёнку твою не пущу никогда</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И больше тебя не обижу</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Викуся ты знай я любить тебя буду всегда</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И края любви я не вижу.</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>***</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>МАМЕ</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Милая родная мама</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Снова я пишу тебе письмо в тиши.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ты прости, что я была упряма</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И судить так строго не спеши.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Да, не слушала я, не понимала</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Были мне твои советы не нужны</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">В етой жизни сколько потеряла</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И остались мне лишь только сны.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Только в снах я возвращаюсь к дому,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Только в снах грущу и вновь смеюсь,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Только в снах я вижу лик знакомый</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И Бога я больше всех ищу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ровно на восток ложатс я строки</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Знаю любишь, понимаешь, ждешь</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ты письма от непослушной дочки</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Снова плачет за окошком дождь.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Нелегко мне быть с тобой в разлуке</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Жаль что ничего вернуть нельзя</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Вспоминаю ласковые руки</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И твои печальные глаза!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Я вернусь, я обещаю мама</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Камень упадет с твоей души</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ты прости, что я была упряма</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И судить так строго не спеши.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Еслиб только все вернуть сначала</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">На руках тебя буду носить</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Мама милая, родная мама</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Как свою вину мне искупить?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Виновата так перед тобою</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Что словами всё не передать,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ето я пишу тебе с любовью</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Чтоб себя хоть как-то оправдать.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И когда к тебе вернусь я мама</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Я хочу прощенья попросить</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Перед тобою на колени встану</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И обиду сможешь ты забыть?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ты обиду забудешь родная</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Потому как с обидой нам жить</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Мать и дочка обиду прощают</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Мы друг другом должны дорожить!</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Не отвергай!</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Не отвергайте Святого Духа,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Он скажет больше, чем слышит ухо</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Откроет шире, чем прочитали</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Для Бога сблизит чужих и дальних</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Чрез ваши руки поможет людям</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Чрез вас спасенных много будет!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">В себе лишь Духа не угашайте,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">В обрядность Веру не превращайте.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">С Ним лицемерить душой не надо</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Он может больше, чем нам понятно.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Христос сказал нам: в Нём утешенье.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Не бойтесь принять Его Крещенье.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Просите в сердце Его вселиться,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Чтоб свыше, в Боге, на свет родиться!</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Тихо. Только стучат часы,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">А за окнами снежную сырь</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Разметает тревожный ветер.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Мне тепло и уютно в доме</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Счастье &ndash; здесь, я в кругу семьи</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И лишь с болью смотрю на мир &ndash;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Он друг другу обиды помнит</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Нас обязывает уже</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Не о зле, а о Боге помнить</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Не с коварством &ndash; с добром идти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И дарить не шипы, а розы.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Не проклятие, не угрозы &ndash;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Примиренье и свет нести!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Догорают огни в квартирах</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Спит мой дом, я ночник гашу</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И в молитве Творца прошу:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- Боже правый, пошли нам мира!</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>О, как хочется</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дар любви Твоей иметь</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Прекратятся все пророчества.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И умолкнут на земле</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Языки, не станет знаний</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Но лишь ей всегда пребыть.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">О, умножь во мне старание</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слову истины служить.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Звать тебя в сердечной радости</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Разделить её со мной,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ждать Тебя в минуты слабости</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">С подкрепляющей рукой.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ради тех, кто здесь не верит,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Без надежды губит дни.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дай Твоею щедрой мерою</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Быть надеждою для них.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Достигать любви свершившего</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Достигать любви с терпением,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Как желаемых вершин,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Чтоб Тебе хвалебным пением</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Стала жизнь моей души.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">... Я зараз все больше стараюсь перебывать с Богом и читать Слово Боже бо мене вібрали старшой нашей церкви и мы по воскресениям собираемся и я розказую о Боге... Хочу у Вас попросить по возможності вишліть мені пожалуйста носки, перчатки, теплу кофту, колготи, сапожки зімні, титрадку, ручку, шампунь, мило, зубну щетку, пасту, резинові шлепки, кипятільник, прокладки... Я получила пісьмо мама лежить в реанімациё в тяжолом состояние и врачи говорят 50 на 50. Если шото с мамой случится я никогда не смогу себе етого простить. Пожалуйста помоліться за мою маму. О.Ігорь я молюсь за Вас, за маму я уже и пости брала, только чтоб всё было хорошо...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Будьте благословенны и пусть хранит Вас Господь!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; С уважением с. Алёна</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;23.12.2013 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>',NULL,5536,5,1,NULL,19);
INSERT INTO `publications_publication` VALUES
(1600,'P','2014-02-17',1,NULL,'Rss','Учні (17 лютого)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5525,200,73525.gif\" />Новий учитель прийшов до класу і помітив, що одного хлопця беруть на глузи і називають дурником.</p>\n<p>У перерві, він запитав хлопців, чому вони так прозивають його?</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1861:uchni-17-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5531,NULL,1,1,26),
(1601,'P','2014-02-17',1,NULL,'Note','Лист 7 ув\'язненої Ірини В., Качанівська виправна колонія КВК-54, м.Харків','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дай Бог, щоб кожна людина із нас, яка вийде з цих стін пам&rsquo;ятала про Вас і Вашу турботу про засуджених людей. І колись з Божою допомогою відвідала Вас і також вложила хоч часточку своєї помочі.</span></p>','<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава Ісусу Христу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Доброго дня Вам, доброго здоров&rsquo;я шановний о.Ігор та вся Ваша церковна громада церкви святого Миколая на Аскольдовій могилі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Зичу добра, здоров&rsquo;я, многая літ, Божої любові і благодаті всім людям доброї волі, волонтерам помічникам, які Вам допомагають в хороших, добрих справах... Дай Бог, щоб кожна людина із нас, яка вийде з цих стін пам&rsquo;ятала про Вас і Вашу турботу про засуджених людей. І колись з Божою допомогою відвідала Вас і також вложила хоч часточку своєї помочі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дорогий о.Ігор я написала вірша Різдво, по-російськи Рождество. Я знаю, що Вам більш хотілось би читати українські мої вірші, я пишу і укр. і російськ, ну так ось прийшло мені натхнення.</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Рождество</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Тихая ночь, святая</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Землю всю укрывает</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">В небе ярко зажглась звезда</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Добрую весть нам всем принесла.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">О, ликуй! Веселись! Народ!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Сегодня дивная, чудная ночь</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Человечеству радость пришла</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Рождение Мессии, Иисуса Христа</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">В небе ангелы песни поют</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Господа славят, честь отдают</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Кругом радость, веселье и торжество</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Празднуйте люди! Пришло Рождество!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Он Царь над Царями Царей</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Он Владыка земель и морей</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Всемогущий, нет равных Ему</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Он родился в сарае, в хлеву</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">К нему волхвы с Востока идут</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Смирну, золото, ладан несут</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Поклоняясь Ему до земли</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Каждый дарит подарки свои.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Он пришел человечеству дать</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Шанс &ndash; поступки свои осознать</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Научить нас прощать и любить</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Научить нас по-Божески жить.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">А пока, Он маленький спит на руках</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Не чувствуя зла, не зная преград</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Не предчувствуя страшной беды</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Что Ему уготовили мы</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дева Мария Его колыхает,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Песни поёт, к груди прижимает,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Спи мой сыночек маленький, спи,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Я укрою Тебя от зла и беды.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Хай Господь Вас всіх благословить! З Різдвом Христовим Вас! З Новим роком!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; З повагою Ірина &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 21.12.2013 р.</span></p>',NULL,5535,5,1,NULL,19);
INSERT INTO `publications_publication` VALUES
(1602,'P','2014-02-16',1,NULL,'Rss','«НА РІКАХ ВАВИЛОНСЬКИХ»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5525,200,index.jpg\" />Документальна новела (За&nbsp;&nbsp; Квінтіліаном, Готлібом&nbsp; Фіхте)</p>\n<p>Псалом Давида&nbsp; 136. «На реках Вавилонских, Тамо&nbsp; седохом&nbsp; и&nbsp; плакахом, Внегда&nbsp; помянути&nbsp; нам Сиона. Аллилуйя!»</p>\n<p><a href=\"http://www.youtube.com/watch?v=PmhuoTRMNRQ\">http://www.youtube.com/watch?v=PmhuoTRMNRQ</a></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1860:2014-02-16-22-37-55&catid=1:mystecka-storinka-cat&Itemid=50',5530,NULL,1,1,26),
(1603,'P','2014-02-16',1,NULL,'Note','Лист1 Керекеші Світлани, яка відбуває покарання в Надержинщинській ВК, Полтавська обл.','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ніколи не думала, що через 14 років моє дежавю буде тут.&nbsp;<strong>Кінець срока у мене 17 января 2014 г</strong>. Поки що освобождаюсь у світ надії. Храни Вас Господь</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... Народилась я у Кіровоградській обл. у Новомиргородському районі. <strong>У свекрухи моя дочка Вікторія. Господу слава що допомогли на 1 вересня &laquo;Школьний портфель&raquo; я Світлана молитимусь. </strong>Ніколи не думала, що через 14 років моє дежавю буде тут. <strong>Кінець срока у мене 17 января 2014 г</strong>. Поки що освобождаюсь у світ надії. Храни Вас Господь</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Память мамы</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">В марте, поздней ночью,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Долго ты молилась</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">В болях и бессилии:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&laquo;Милый мой Господь!&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Угасала медленно,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Тихо уходила...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Душа воскресала,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Умирала плоть.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Две седьмицы лет</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Пролетели быстро.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Правнуков своих</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Видишь ты с небес</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дочери твои</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">На седьмом десятке,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Сын любимый Вася</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Уж давно воскрес.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Помним тебя, мама,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Доброту, заботу,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Бескорыстный труд твой...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И благодарим.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">А Господь твой милый</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Стал Отцом и другом</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Он хранит и кормит,,,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Счастливы мы с Ним!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И когда состарились,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Лучше понимая</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Боль и одиночество, радость и любовь.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И обнять тебя, и просить прощения</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">За непонимание</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Хочется нам вновь.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Да спасибо Господу за моих родителей,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Что вошли их души в вечные обители.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Вспоминаем мы с теплом об отце и маме</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ярче просветление приходит с годами.</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Небесный ручеек</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- Почему Ты родился в хлеву?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Столько пышных дворцов и палат</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Бог Отец мог избрать для Тебя...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ты ведь &ndash; Царь, был бы этому рад?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- Да, Господь я, и Царь, и твой Бог,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Но избрал тот Сарай для Себя,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Чтобы лучше понять ты мог,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Где рождаюсь внутри тебя.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ты есть хижина, ветхий дом,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Весь из дерева, сена. Соломы;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И пронизанный насквозь грехом...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Очень мрачны твои хоромы!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">В доме этом живут ослы.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Догадался? &ndash; Твоё упрямство;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Овцы, козы, коровы, быки...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Неприглядно твоё убранство.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Если пламя охватит сей дом,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Превратится он в прах и золу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Но решил поселиться я в нём</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И внести жизнь молитву, хвалу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Я желаю очистить хлев.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ты сейчас есть моя обитель.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Не ропщи если с болью порой</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Освящает тебя Спаситель.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Приготовить хочу для Небес,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Взять детей Своих в Царствие всех.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Прежде вымету весь этот мусор:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Гордость, лень, себялюбие... &ndash; грех.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Чтоб наполнился дом красотой,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Засветился небесным светом,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Миром, радостью, чистотой</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И моею любовью при этом.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Если хижина будет дворцом,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">А Господь &ndash; управителем станет,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">То в ворота златые войдём</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слово Божие нас не обманет&rdquo;</span></p>',NULL,5534,5,1,NULL,19);
INSERT INTO `publications_publication` VALUES
(1604,'P','2014-02-16',1,NULL,'Rss','Росіяни і білоруси стають католиками не через прозелітизм','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5525,200,087048-crop.jpg\" /></strong>У Білорусі тепер проживає 1,4 млн. католиків — вони становлять 14,5% від населення. У Росії їх приблизно 300 тис. у європейській частині і 100 — в азіатській. Тільки у Москві, за деякими даними, проживають 60-100 тис. католиків. Із них лише 35 тис. — іноземці.&nbsp;</p>\n<p>Водночас і білоруська, і російська статистика подає неповну статистичну інформацію про католиків.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1859:rosijany-i-bilorusy-stajut-katolykamy-ne-cherez-prozelityzm&catid=32:gk-svit-cat&Itemid=58',5529,NULL,1,1,26),
(1605,'P','2014-02-16',1,NULL,'Rss','«Свободу та гідність нам не надає президент чи закон. Ці цінності нам дані Богом…»','<p><img src=\"http://askoldova-mohyla.org/uk/image,5525,200,bludnoho_syna.jpg\" style=\"margin: 10px; float: right;\" />16 лютого, в Неділю про блудного сина, Отець і Глава УГКЦ Блаженніший Святослав (Шевчук) під час проповіді на Божественній Літургії в Патріаршому соборі Воскресіння Христового УГКЦ, що у Києві, наголосив, що сьогодні ми переживаємо особливий момент віднайдення гідності українця.</p>\n<p>«Цей час багато мислителів окреслило як «революція гідності». Та свободу та гідність не надає нам певний президент, чи якийсь закон. Ці цінності нам дані Богом.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1856:2014-02-16-21-53-27&catid=28:tema-nomera-cat&Itemid=2',5526,NULL,1,1,26),
(1606,'P','2014-02-16',1,NULL,'Rss','Хлоп’ячий страх (16 лютого)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5515,200,106407_or.jpg\" />Закінчивши проповідь священик помітив хлопця, який вже не вперше затримувався в церкві довше від усіх парафіян. Було видно, що його непокоїть якась річ. Підійшовши до хлопця, він запитав, чи може чимось допомогти йому.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Скажіть, а пекло справді існує, - хлопець підвів очі сповнені страху.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; З тобою сталося щось? Покайся, розкажи мені, - запропонував священик.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1851:hlopjachy-strah-16-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5525,NULL,1,1,26),
(1607,'P','2014-02-15',1,NULL,'Rss','МАЙДАН – БОЖА ВКАЗІВКА НА ТЕ, ЯКИМ МАЄ БУТИ СУСПІЛЬСТВО?','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5525,200,71c31ff-10.jpg\" />Теперішній Майдан показав себе і Божим знаком, і Божим ділом. Майдан став Божим знаком для нашого суспільства. І чи не найголовніший аспект цього Божого знаку <a href=\"http://catholicnews.org.ua/vladika-venedikt-narod-bozhiy-v-ukrayini-gliboko-usvidomlyuie-potrebu-opertisya-v-ciy-situaciyi-na\">надзвичайно влучно окреслив владика Венедикт</a>, єпископ-помічник Львівський: «Здається мені, що цією ситуацією Бог каже нам: «Стоп! Так більше не можна! Ви мусите змінитися, змінити свою поведінку, бо інакше пропадете». І саме Бог, на моє глибоке переконання,&nbsp; має відповідь на цю ситуацію. Ми ж у свою чергу покликанні цю відповідь шукати».</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1857:madan--bozha-vkazivka-na-te-jakym-maje-buty-suspilstvo&catid=37:spilnota-cat&Itemid=49',5527,NULL,1,1,26);
INSERT INTO `publications_publication` VALUES
(1608,'P','2014-02-15',1,NULL,'Note','Лист1 Омеляненко Марії, Яка відбуває ув’язнення в Качанівській ВК, м.Харків','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... Тут є велика бібліотека в якій є і творчість Тараса Григоровича Шевченка, в тому числі і &laquo;Кобзар&raquo;. <strong>Кожен тиждень я ходжу в бібліотеку брати книжки</strong>. Я якось ще зі школи люблю книги... <strong>я дуже багато читала про голодомор</strong>.&nbsp;</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... Тут є велика бібліотека в якій є і творчість Тараса Григоровича Шевченка, в тому числі і &laquo;Кобзар&raquo;. <strong>Кожен тиждень я ходжу в бібліотеку брати книжки</strong>. Я якось ще зі школи люблю книги... <strong>я дуже багато читала про голодомор</strong>. Пам&rsquo;ятаю я ходила в 8-й клас то наш класний керівник нам давала такі домашні завдання наприклад ходити до старожилів, ну взагалі до стареньких людей та розпитувати про Велику Вітчизняну Війну, про голодомор, а потім писали на цю тему. На жаль зараз дуже багато хто помер, з тих хто все це пережив чи знав з розповіді батьків. Вчителі зараз мало залучають дітвору до творчості.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Нещодавно написала вірша.</strong> Тому Ви будете перві хто його прочитає</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Життя нам дане Богом тільки раз,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Та не цінуєм ми свого життя,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Поспішаєм знищити його враз &ndash;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Не дивлячись вперед, у майбуття.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">І не думаєм що там нас чекає,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Та й життя не завжди &ndash; казка</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Та на все є Божа ласка.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ви лиш подивіться люди добрі</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Як Всевишній всіх нас любить</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Не важливо чи здорові ви чи хворі</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Всіх на шлях на правильний направить.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">До Нього ти прийди скоріше,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Віру в Бога в серці укріпи!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">То цей світ стане красивіше і добріше</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Лиш з Богом ти вперед іди!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Так я згодна щоби Ви друкували мої листи, вірші, оповідання.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Велике Вам спасибі за розуміння і допомогу&rdquo;</span></p>',NULL,5533,5,1,NULL,19);
INSERT INTO `publications_publication` VALUES
(1609,'P','2014-02-15',1,NULL,'Rss','Звернення Українських Церков щодо мирного розв’язання суспільно-політичної кризи та припинення силового протистояння в Україні','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5525,200,81565.jpg\" />Українські Церкви ще раз підкреслюють свою відданість виключно мирному врегулюванню громадянського конфлікту в Україні та підтверджують принципи, викладені у «Зверненні керівників Церков і релігійних організацій до своїх вірних та всіх людей доброї волі у зв’язку з суспільно-політичною ситуацією в Україні» від 10 грудня 2013 р., у «Зверненні Всеукраїнської Ради Церков і релігійних організацій з нагоди Дня Соборності та Свободи України» від 22 січня 2014 р., у Комюніке екстреного засідання Всеукраїнської Ради Церков і релігійних організацій від 22 січня 2014 р. та у «Заяві Всеукраїнської Ради Церков і релігійних організацій за результатами зустрічі з Президентом і опозицією» від 25 січня 2014 р.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1858:zvernennja-ukrajinskyh-cerkov-shchodo-myrnogo-rozvjazannja-suspilno-politychnoji-kryzy-ta-prypynennja-sylovogo-protystojannja-v-ukrajini&catid=27:gk-v-ukrajini-cat&Itemid=57',5528,NULL,1,1,26),
(1610,'P','2014-02-15',1,NULL,'Note','Лист1 Свіржевської Любові, яка відбуває покарання в Збаразькій ВК-63 (Тернопільська обл.)','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Спасибі Вам всім дорогі хто мені пише ці гарні листи. Запитуєте чи є в нас бібліотека? Звичайно, є дуже хороша література і Кобзар маємо. І також література на теми духовної тематики, кому цікаво, то є що почитати, головне бажання... Мою одну ікону відправили в Київ на конкурс творчості.&nbsp;</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;Слава Ісусу Христу! <strong>Вітаю Вас з Різдвом Христовим! Нехай новонароджений Богомладенець благословляє Вас і дарує масу духовної радості і спасіння.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Спасибі Вам всім дорогі хто мені пише ці гарні листи. Запитуєте чи є в нас бібліотека? Звичайно, є дуже хороша література і Кобзар маємо. І також література на теми духовної тематики, кому цікаво, то є що почитати, головне бажання... Мою одну ікону відправили в Київ на конкурс творчості. Хоча я точно неможу сказати де буде відбуватись цей конкурс... Я незнаю як получився вірш, але це прийшло мені на думку сьогодні вночі. Довго думала, згадувала своє дитинство. Свою любиму бабусю (покійну) яка щонеділі водила мене за ручку до церкви і як було приємно і добре тоді.</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Як далеко лишилось дитинство,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Спогад про це навертає сльозу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Молюсь до Тебе Господи щогодинно</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Сама себе простити не можу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">А час летить неумолимо,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Івже ми виросли давно</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">І радості в маленькому забули</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Нам все не так, нам всеодно.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ми розучились розуміти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Причину радості в усьому</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">І ми уже давно не діти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">І хто зарадить горю цьому.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Уже не в радість нам погода,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">І без радості спливає час,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">І сіро все, і все убого</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">І зачерствіла душа у нас.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Але надію маю і молю</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Бо Ти є наш Господь Всевишній,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Молюсь щоночі, плачу і терплю,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Прости мене по Милості Своїй Спаситель!!!<strong>&rdquo;</strong></span></p>',NULL,5532,5,1,NULL,19);
INSERT INTO `publications_publication` VALUES
(1611,'P','2014-02-15',1,NULL,'Rss','Стрітення Господнє','<p><strong><span class=\"main\"><em><img src=\"http://askoldova-mohyla.org/uk/image,5519,200,00197421_n1.jpg\" style=\"margin: 10px; float: right;\" />\"Радуйся благодатна, Богородице Діво, бо з Тебе засяяло Сонце правди&nbsp;</em></span><span class=\"main\"><em>Христос Бог наш, що просвічує тих, що в темряві\" </em><em>(Тропар празника).</em></span></strong></p>\n<p>Різдвяне коло свят завершується празником Господнього Стрітення, який&nbsp;  святкуємо 2 лютого, на сороковий день після Христового Різдва. Цей&nbsp;  празник своїм змістом тісно пов\'язаний з Христовим Різдвом, бо як у&nbsp;  Різдві, так і тут Христос при своїм пожертвуванні у святині об\'являє  нам&nbsp; Свою божественність. Від зустрічі Божої Дитини і Його Пресвятої  Матері з&nbsp; праведним Симеоном і сам празник у Східній Церкві називається&nbsp;  Стрітення. Візьмемо до уваги три питання: основу празника, його&nbsp;  встановлення та дух богослуження.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1855:stritennja-gospodnje&catid=27:gk-v-ukrajini-cat&Itemid=57',5524,NULL,1,1,26),
(1612,'P','2014-02-15',1,NULL,'Rss','Розсудливий хлібороб (15 лютого)','<p><img src=\"http://askoldova-mohyla.org/uk/image,5515,200,pidgotovka-front.jpg\" style=\"margin: 10px; float: left;\" />Надумав молодий хлібороб женитися і вирушив з приятелями на свято щоб вибрати собі наречену. Такі красуні пішли в танок, що у парубків очі забігали: одна краща за іншу. Ступають наче пави, плечима поводжують, красуються. І тільки одна сидить скромно збоку, схиливши голову і опустивши очі.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Он моя суджена, - показав на неї хлібороб.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1850:rozsudlyvy-hliborob-15-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5523,NULL,1,1,26),
(1613,'P','2014-02-14',1,NULL,'Note','ЗВІТ №1 про матеріальну допомогу ув’язненим жінкам та Різдвяні подарунки дітям, матері яких відбувають покарання в місцях позбавлення волі, (02.01.2014 р.)','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;...&nbsp;<strong>В ціх місцях я зустріла жінку яка сказала мені вирішення твоїх всіх бід в твоїх руках &ndash; звернись до Господа з цих пір я почала читати духовну літературу, молитись і мені стало краще на душі та в житті. Я знайшла свого сина Володимира через стільки років&nbsp;</strong>та виплачую на нього аліменти...</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Дякуючи пожертвам парафіян, надана матеріальна допомога жінкам, які відбувають покарання в місцях позбавлення волі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Допомога надавалась гігієнічними засобами, продуктами і солодощами, пожертвуваними парафіянами, а також придбаними за пожертви, та речами особистого вжитку, що надійшли від жертводавців та БФ &laquo;Тріумф серця&raquo;. Крім того, одна з ув&rsquo;язнених отримає схему для вишивання та книгу &laquo;Живопись крестиком&raquo; (подарунок посл.п.Валентини).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Відправлено також дві посилки з Різдвяними дарунками дітям ув&rsquo;язнених жінок, які не встигли зголоситися до св.Миколая. Діти отримали солодощі, шкільне приладдя, дитячі книжечки, одяг, духовну літературу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">В кожну посилку вкладена Різдвяна листівочка.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Силами волонтерів спаковано і відправлено адресатам 9 посилок.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Посилки доставлені до поштового відділення настоятелем церкви св.Миколая о.Ігорем Онишкевичем.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Посилки отримали:</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>1.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув</strong><strong>&rsquo;</strong><strong>язнена Любов С. (Збаразька ВК, Тернопільська обл.) на суму 80 грн. 49 коп.+пожертви</strong><strong>),</strong><strong><span style=\"text-decoration: underline;\">в т.ч.ліки</span></strong><strong> &ndash; 61грн.05коп.</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>мені уже надіслали полотно і кольорові нитки, але немаю схем. Якщо Ви будете мати змогу то пришліть деякі схеми (на папері),</strong> а я вишию на полотно... Відносно мого здоров&rsquo;я, то <strong>мені лікар терапевт виписала рецепт. Ложу рецепт, а що зможете то пришліть. Якщо надішлете всі препарати (уколи) то буду мати можливість на госпіталізацію</strong>. Займаюсь вишиванням цим скорочую час. Одну вишивку уже забрали на конкурс до 19-го грудня (Св.Миколая Чудотворця)...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Я люблю нашого Господа і ніколи не поміняю свою віру на щось інше. Дякую Вам дорогі сестри за Ваші молитви. Людина в молитві як істочник чистої води, дає змогу напитись другим втамовуючи жаждущих</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Перший лист - 05.08.2011 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Допомагали Любові С.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">23.02.2012 р. на суму 50 грн. &ndash; Біблія</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">04.04.2012 р. на суму 25 грн.90 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">02.02.2013 р. на суму 60 грн.73 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Загальна сума допомоги &mdash; 217 грн.12 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>2.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язнена Тетяна С. (Качанівська ВК, м.Харків) на суму 75грн. 62коп. + пожертви, <span style=\"text-decoration: underline;\">в.т.ч.ліки</span> &ndash; 50 грн.00 коп.</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Я дуже Вам вдячна за листи і підтримку якщо можна то вишліт старі в&rsquo;язані речі щоб мені пов&rsquo;язати носочки і рукавички діткам на подарунки до миколая в дитячий будинок</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Перший лист <strong>&mdash;</strong> 01.03.2011 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Допомагали Тетяні С.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">04.11.2010 р. на суму 65 грн.21 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">10.02.2011 р. на суму 89 грн. 44 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">01.11.2011 р. на суму 70 грн.20 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">06.01.2012 р. &ndash; &laquo;Різдвяний подарунок&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">05.04.2012 р. на суму 38 грн.90 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">12.05.2012 р. на суму 23 грн.68 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">02.02.2013 р. на суму 41 грн.82 коп., в т.ч.ліки &ndash; 24 грн.48 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Загальна сума допомоги &mdash; 404 грн.87 коп</strong><strong>.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>3.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язнена Галина І. (Збаразька ВК, Тернопільська обл.) на суму 28 грн. 10 коп.+пожертви)</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... мне 46 лет... со слезами и мольбою <strong>прошу Вас о помощи. Я нахожусь на туб-больнице и мне нужны теплые вещи</strong> &ndash; курточка, теплый свитер, носочки, спортивные штны или теплые колготки, халат, <strong>постельное</strong> (только цветное), полотенце, теплый платок, <strong>кипятильник &ndash; 05</strong>. Размер вещей &ndash; 48, <strong>теплую обувь</strong> и тапочки для бани &ndash; 39 размер, <strong>мыло, порошок, шампунь, зубная щетка,</strong> туалетная бумага, гигиенические прокладки, <strong>по возможности помогите пожалуста продуктами питания: чай, сахар, лук, чеснок чем сможете</strong>... Да благословит Вас Господь за Ваши Добрые Дела&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Перший лист &ndash; 18.09.2013 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Загальна сума допомоги &mdash; 28 грн. 10 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>4.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Діти (Назар-14р., Роксана-11р., Роман-6р., Настя-5р.) ув\'язненої Олени Т. (Чернігівська ВК) &ndash; Різдвяний подарунок (пожертви) в Кіровоградську обл.</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;...<strong> Я в июне ездила на ознакомление в свой город и отвезла доченьку Ингочку отдала маме</strong>. Мама очень загружена<strong> дети очень болеют у Настеньки &ndash; 27.09.2008 г.р. косоглазие, у Роксаночки &ndash; 04.02.2003 г.р. &laquo;гайморит и больные почьки&raquo;, у Назара &ndash; 02.07.1999 г.р. &laquo;паховая грыжа&raquo;, у Ромочки &ndash; 05.10.2007 г.р. ножка левая короче правой на 5 см после трудной операции и он через каждых три месяца лежит в больнице по месяцу</strong>. А <strong>у Ингочки &ndash; 04.04.2011р. &laquo;врождённый порок сердца</strong>&raquo;. Мама плохо видит, что-то с глазом произошло. Детки все с мамой по адресу прежнему.<strong> Вам очень благодарны за Ваше внимание и поддержку моим деткам</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Перший лист &mdash; 10.11.2010 р.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Допомагали:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Олені Т. &mdash; 23.11.2010 р. на суму 36 грн.63 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дітям в Кіровоградську обл. :</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">10.02.2011 р. на суму 102грн. 37 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18.04.2011 р. на суму 134 грн.87 коп. (&ldquo;Пасхальний кошик&rdquo;) &nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">29.08.2011 р. на суму 259 грн. 53 коп. (&ldquo;Шкільний портфелик&rdquo;)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">13.12.2011 р. &laquo;Подарунок від св.Миколая&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18.04.2012 р. на суму 151 грн.59 коп. (&ldquo;Пасхальний кошик&rdquo;)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">03.09.2012 р. на суму 213 грн.75 коп. (&ldquo;Шкільний портфелик&rdquo;)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">15.12.2012 р. &ndash; &laquo;Подарунок від св.Миколая&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">27.04.2013р. &ndash; &laquo;Пасхальний кошик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Загальна сума допомоги &mdash; 898 грн. 74 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>5.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Дитина (Кирило-8р.) ув\'язненої Марії О. (м.Харків, Качанівська ВК) &ndash; Різдвяний подарунок (пожертви) в Чернігівську обл.</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Мені 24 роки. Я відбуваю покарання за скоєння тяжкого злочину</strong>. Працюю в швейному цеху, шию за машинкою. По вихідним відвідую молитовні зібрання. <strong>Вдома в мене є маленький син Кирило. Йому 8 років</strong>. Є мати, але вона нігде не працює, дуже часто хворіє, пенсію не отримує, бо ще рано. Їй 52 роки. <strong>Хлопчик знаходиться в інтернаті проте на вихідні: суботу та неділю мама забирає додому і на канікули також</strong>... <strong>Сидіти мені залишилоя 4,5 роки&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Перший лист &ndash; 26.09.2013 р.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>6.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув</strong><strong>&rsquo;</strong><strong>язнена Тетяна Х. (Качанівська ВК, м.Харків) на суму 27грн. 70коп.+пожертви</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>В ціх місцях я зустріла жінку яка сказала мені вирішення твоїх всіх бід в твоїх руках &ndash; звернись до Господа з цих пір я почала читати духовну літературу, молитись і мені стало краще на душі та в житті. Я знайшла свого сина Володимира через стільки років </strong>та виплачую на нього аліменти...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Не щодавно відправила запрос щодо мого сина на інтернат щоб мені дозволили з ним листування.</strong> Якщо ви можете мені допомогти налаштувати стосунки з моїм сином я буду вдячна вам всю жизнь&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Перший лист &ndash; 04.07.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Допомагали Тетяні Х.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">02.02.2013 р. на суму 07 грн.04 коп.+пожертви</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 34 грн.74 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>7.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Ув</strong><strong>&rsquo;</strong><strong>язнена Віра Ц. (Збаразька ВК, Тернопільська обл.) на суму 24 грн.45 коп.+пожертви</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Мені ще нада добувати скільки осталось до весни. На все Божья воля</strong>, Він за нас все время беспокоєтся, нипокидає в біді. <strong>Ми нашого Бога все славимо ми без него нікуди негодимось.</strong> Мені <strong>В. із Луцька</strong> із дома пристарелих 33 роки, <strong>інвалід на коляскі</strong>, пише листи присилає газети за Бога нашего молитви я всігда рада получити і відповісти на їй листи, таких людей жалко но вона <strong>пише, що 7 років лежача була, а Бог поміг вона в даний момент може сама с кроваті коляску сісти і листи писати. Пише Бог мене спас, ізцілив </strong>і все мені поміг. <strong>Ми всігда молимося за Вас за Ваші сімї. А я благодарю Господу за то, що криша над головою, за хліб за жизню. Слава Богу</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Перший лист &ndash; 16.11.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Допомагали Вірі Ц. і Юзепі К.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">01.06.2013 р. на суму 29 грн.82 коп.+пожертви</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Загальна сума допомоги &mdash; 54 грн.27 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>8.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Ув</strong><strong>&rsquo;</strong><strong>язнена Ірина Р. (Качанівська ВК, м.Харків) на суму 19грн.78коп.</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Непрестанно благодарю Бога за Вас, за то, что Вы есть, и что помогаете таким обездоленным как я,</strong> не взирая ни на внешний облик, ни на то, что люди несут наказание за тяжкие грехи и находятся на самом дне пропасти. <strong>Вы даруете ни просто помощь, ни просто материальную поддержку, &ndash; но саму Надежду, Веру и Упование на Бога.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Я готовлюсь к Рождественскому посту</strong>. 2 декабря в колонии должна быть Литургия и причастие. Даст Бог, и мне дозволят выйти с сектора (под охраной) в церковь, да и совет мне нужен, - <strong>не знаю как правильно поститься, что кушать, а что нет, и какие молитвы читать надо</strong>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Не могу не благодарить Христа за то, что у меня теперь новая жизнь, что я преобрела любящих меня людей. <strong>Вы и Ваши помощники на ниве Божьей, - свидетельство о живом, справедливом, любящем Боге!</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Перший лист &ndash; 07.11.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Допомагали Ірині Р.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">06.02.2013 р. на суму 17 грн.01 коп.+пожертви</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">28.06.2013 р. на суму 13 грн.27 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">02.11.2013 р. на суму 108 грн.80 коп., в т.ч.ліки &ndash; 107 грн.35 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 158 грн.86 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>9.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув</strong><strong>&rsquo;</strong><strong>язнена Ірина В. (Качанівська ВК, м.Харків) на суму 12грн.25коп.</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... Дякую людям доброї волі, які занімаються такою доброчинністю і допомагають людям в місцях позбавлення волі. Хай будуть благословенні Ваші руки і Божа любов огортає ваші сім&rsquo;ї, родини. Заодно <strong>хочу Вас всіх поздоровити з наближенням свята святого Миколая</strong>. Здоров&rsquo;я міцного Вам, добра, тепла та Божої благодаті&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Перший лист &ndash; 28.09.2011 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Допомагали Ірині В.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">06.01.2012 р. вислали &laquo;Різдвяний подарунок&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">05.04.2012 р. на суму 25 грн.90 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">07.05.2012 р. на суму 23 грн.68 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">25.10.2012 р.на суму 128 грн.31 коп., в т.ч.ліки &mdash; 77 грн.65 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">01.06.2013 р. на суму 18 грн.76 коп.+пожертви</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">02.11.2013 р. на суму 14 грн.72 коп.+пожертви</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">30.12.2013 р. на суму 12 грн.25 коп.+пожертви</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">дитині Ірини В. в Кіровоградську обл.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">13.12.2011 р. - &laquo;Подарунок від св.Миколая&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">19.04.2012 р. на суму 91 грн.46 коп. <strong>&mdash;</strong> &laquo;Пасхальний кошик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">31.08.2012 р. на суму 97 грн.23 коп. &ldquo;Шкільний портфелик&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">15.12.2012 р. - &laquo;Подарунок від св.Миколая&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">29.04.2013 р. &ndash; &laquo;Пасхальний кошик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">02.09.2013 р. &ndash; &laquo;Шкільний портфелик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Загальна сума допомоги &mdash; 412 грн.31 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Загальна сума матеріальної допомоги </strong><strong>02.01.2014</strong><strong> р. </strong>&mdash;<strong> 268 грн.39 коп.,</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; в т.ч.ліки &ndash; 111 грн.05 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Крім того., вартість:&nbsp;&nbsp;&nbsp;&nbsp; поштових конвертів, вкладених у посилки &mdash; <strong>22 грн.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">пересилання &mdash;<strong> 219 грн.60 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Загальна сума витрат від&nbsp; 02.01.2014 р. &mdash; 510 грн. 99 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Загальна вага поштових відправлень від </strong><strong>02.01</strong><strong>.2014 р. &ndash;42,</strong><strong>698</strong><strong> кг.</strong></span></p>',NULL,5522,5,1,NULL,19);
INSERT INTO `publications_publication` VALUES
(1614,'P','2014-02-14',1,NULL,'Rss','Національний Акт Свободи','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5519,200,safe_image.jpg\" />Найвищою і непорушною цінністю людей в Україні є свобода.</p>\n<p>Прагнення свободи, захисту гідності і справедливості, обурення проти обману, насильства, приниження і свавілля з боку влади змусили мільйони громадян України вийти на мирний протест.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1854:nacionalny-akt-svobody&catid=28:tema-nomera-cat&Itemid=2',5520,NULL,1,1,26),
(1615,'P','2014-02-14',0,NULL,'Note','','<p style=\"text-align: center;\"><span style=\"font-size: x-small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Молитва оптинських старців</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: x-small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Господи, дай мені з душевним спокоєм прийняти все, що принесе мені новий день. Провадь і підтримай мене впродовж цього дня. Відкрий мені Свою волю і керуй усіма моїми ділами, словами, думками і почуттями. В усіх непередбачених обставинах дай мені пам\'ятати, що все походить від Тебе. Навчи мене поводитись з кожною людиною чесно і мудро, нікого не бентежачи і не засмучуючи. Господи, дай мені терпеливості перенести те, що я не можу змінити, і мужності в боротьбі з тим, що я можу подолати. Дай мені вміння відрізняти перше від другого, важливе від другорядного. Господи, дай мені сили перенести втому і все, що станеться протягом цього дня. Навчи мене каятися, молитися, вірити, надіятися, терпіти, прощати, дякувати і любити. Амінь</span></span></p>','',NULL,1605,5,1,NULL,50),
(1616,'P','2014-02-14',1,NULL,'Rss','День святого Валентина не дорівнює Дню закоханих','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5518,200,no-v-day-1.jpg\" />Більшість магазинів у кожному місті щороку перед 14 лютого наповнюються яскравими листівками, іграшками, шоколадками і безліччю інших дрібничок.</p>\n<p>Чого тільки не зробиш, коли душа бажає свята. А коли таких душ назбирується багато, то і бажання створити їм свято збільшується. А коли за таке ще й платять, то чому б і ні? За таким життєвим принципом живе, мабуть, дуже значна частина світового бізнесу. Саме такі дії дискредитують святого, який загинув мученицькою смертю заради добра.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1853:den-svjatogo-valentyna-ne-dorivnjuje-dnju-zakohanyh&catid=37:spilnota-cat&Itemid=49',5519,NULL,1,1,26),
(1617,'P','2014-02-14',0,NULL,'Note','Звіт №19 про НАДХОДЖЕННЯ ЛИСТІВ 24.12.2013 р. — 29 листів','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Отец Игорь, помогите пожалуста моей маме. Пожалуста поздравте с Новым годом моего сына Сашу и внука Женю. А для себя прошу одного, моральной поддержки и общения с Вами</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ірина Р. (Чернігівська ВК)</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Листи від ув\'язнених </span></em></strong><strong><em><span style=\"text-decoration: underline;\">&nbsp;(з них вперше звернулись 2 особи)</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Чернігівська виправна колонія ЧВК-44 (м.Чернігів) &mdash; 1</strong><strong>9</strong><strong> лист</strong><strong>ів</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Збараз</strong><strong>ька виправна колонія </strong><strong>З</strong><strong>ВК-</strong><strong>63</strong><strong> (</strong><strong>Тернопільська обл.</strong><strong>) &mdash; </strong><strong>1</strong><strong> лист</strong><strong>и </strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Кача</strong><strong>нівська виправна колонія </strong><strong>К</strong><strong>ВК-</strong><strong>5</strong><strong>4 (м.</strong><strong>Харк</strong><strong>ів) &mdash; </strong><strong>4</strong><strong> лист</strong><strong>и </strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Селезнів</strong><strong>ська </strong><strong>виправна колонія С</strong><strong>ВК</strong><strong>-143</strong><strong>(Луган</strong><strong>ська обл.</strong><strong>)</strong><strong> &mdash; </strong><strong>1 лист</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Надержинщинська виправна колонія ЧВК-</strong><strong>7</strong><strong>4 (Полтавська обл.) &mdash; 1 лист</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Орджонікідзевський виправний центр ОВЦ-79 (Дніпропетр. обл.) </strong><strong>&mdash;</strong><strong>1 лист</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Подяка за посилки для дітей &ndash;</span></em></strong><strong><em><span style=\"text-decoration: underline;\">2</span></em></strong><strong><em><span style=\"text-decoration: underline;\"> лист</span></em></strong><strong><em><span style=\"text-decoration: underline;\">и</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Скоро я звільняюсь, залишилось зовсім трішки. Пишу Вам лист щоб подякувати за підтримку. Що ці роки нам допомагали. Я дуже вдячна Вам за листи. Що підтримували мене своїми духовними словами</strong>. Я звільняюсь 6 грудня. <strong>Якщо у Вас буде можливість то вишліт Настінькі якийсь подарунок.</strong> Вона дуже хоче колясочку і посудку. Я тільки звільнюсь і цього подарувати їй незможу. Но я їй говорю, якщо будеш слухати маму, то Миколай принесе тобі подарунок. <strong>Настінька уже знає Оченаш</strong>. Вона у мене росте послушною дівчинкою. Даже коли дітки ругаються, вона їм говорить, що такі слова говорити не можна, це гріх. <strong>У Настіньки нема сапожечок</strong>. Ми знайшли в ДМР сапожки новони маленькі, 26 розмір, а <strong>потрібен 28</strong>... <strong>Мені невіриться що все закінчилось. Но буде все поновому. І буде все з Господом, з Божим благословінням</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Людмила К. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Я получила Вашу посилочку с ликарствами, за которою Вам очень благодарная</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ольга Л. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo; <strong>Спасибо за внимание, за тёплые слова, за поддержку.</strong> Звонила своим деткам, рассказывали что бабушка к ним приходит, приносит подарки, на выходные ходят в церковь на служение, и очень скучают по мне. <strong>Дианочка ходит в школу,</strong> учиться хорошо, любит петь песни про Иисуса. <strong>Никита ходит в садик</strong>, послушный, хороший мальчик. <strong>Очень скучаю за детками, хочется чтобы время пролетело, и наша семья восстановилась</strong>. Когда освобожусь, будет много трудностей, забрать детишек, создать условия для проживания, квартира есть, а вот с мебелью проблема, и всё это пугает. <strong>Тяжело начинать всё с самого начала, но другого выбора у меня нету, я должна всё это пройти и стремится к лучшему. Спасибо Вам большое что поддерживаете моих деток, они очень радуются когда получают подарки.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Спасибо что Вы есть, я безумно счастлива что у меня есть возможность с Вами вести переписку. Пусть хранит Вас Господь.&nbsp;&nbsp;&nbsp; С теплом Ирина А.&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ірина А. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>&nbsp;</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Получила вашу бандероль с лекарствами и вещами первой необходимости. Огромное вам спасибо, и за лекарство но больше всего за тепло и заботу. </strong><strong><em>Благодаря вашей деятельности люди видят &ndash; Бог жив.</em></strong><strong> И пусть прославится и возвеличится Его имя на этой земле &ndash; имя вечного Бога: Отца и Сына и Святого Духа, аминь!</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Тетяна П. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Спасибо Вам огромное, за то что помогаете нашим деткам</strong>. Отец Игорь, <strong>Вы не представляете какую радость Вы приносите деткам</strong>. Я когда звонила домой, <strong>моя Настя была так довольна, она меня благодарила, ведь она думает, что это я ей выслала посылку.</strong> Рассказывает что с куклой спит, и рисует в альбоме. Вообщем её радости нет придела. Благодарю Вас сердечно. Ещё раз <strong>спасибо Вам огромное от всех мам, деткам которых Вы помогаете</strong>. Дай Вам Бог здоровья и всего самого наилучшего&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Юлія Є. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>я ВИЧ инфецированная и сейчас стою на тубазитной терапии. Мне 41 год. Родом с Чернобыля</strong>, после аварии на ЧАЭС нас эвакуировали с г.Припяти в Белую Церковь. <strong>Мама моя </strong>всю жизнь проработала в Чернобыле на вахтах. Сейчас находится <strong>на пенсии по болезни</strong>. У неё было 2 инсульта сейчас очень плохо с ногами. <strong>У неё на руках находятся 2 маленьких ребёнка, мой сын Саша &ndash; 3,7 г. и мой внук Женя &ndash; 2,7 г. Дочька пошла по плохой дороге, бросив малыша на бабушку</strong>, а мама моя не в состоянии сходить даже в город, что-бы купить детям колготки а себе лекарства.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Отец Игорь, помогите пожалуста моей маме. Пожалуста поздравте с Новым годом моего сына Сашу и внука Женю. А для себя прошу одного, моральной поддержки и общения с Вами</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ірина Р. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Я Вам так благодарна за то, что Вы мне помогали с лекарствами и поддерживали меня морально и духовно, когда умер мой сын</strong> Рома. Вы знаете, что из родных у меня не осталось ни кого, вот я и вынуждена к Вам обратиться. <strong>Эти витамины мне очень нужны, ведь я нахожусь в колонии почти 4 года</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ірина С. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>отбываю срок наказания уже 5-й год, конец срока в 2017 г. Мне 57 лет, </strong>пенсионерка. Пенсию в колонии не оформляют, поэтому мы не имеем возможности купить самое необходимое, лекарств<strong>. Санчасть у нас очень бедная, здесь нет таблеток даже аналгина. Я гипертоник, из-за высокого давления у меня катаракта, левый глаз ослеп. </strong>Очень прошу вас если у вас есть возможность, <strong>помогите пожалуйста приобрести таблетки от давления &laquo;Тенорик&raquo;, капли от катаракты &laquo;Офтан&raquo;, анальгин</strong>. В колонии я хожу в церковь, к нам приходит батюшка, мы молимся, исповедуемся, причащаемся... Ещё я <strong>очень прошу ваших святых молитв о здравии моего сыночка Вячеслава, 30 лет, инвалид </strong><strong>ІІ</strong><strong> гр., болен туберкулёзом лёгких</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ольга Д. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>По-поводу вашей посылки на 1 сентября. Сердечное спасибо вам отец Игорь и низкий поклон от меня и моих детей. Посылку они получили и всем довольны</strong>. У нас в семье горе, <strong>у нас умерла бабушка,</strong> она была старенькая. Ей было 92 года. <strong>Помолитесь Христа ради об упокоении её души р.Б. Нины</strong>. По-поводу участия в конкурсе. У меня нет таланта к творчеству. Я могу помолиться о вас и ваших близких, присылайте имена для молитв. Я буду молиться. Я постоянно читаю молитвы Пресвятой Троице, Отче Наш, Царю Небесный.<strong> Храни вас Господь и Богородица. С наступающим вас праздником Св.Николая</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Валерія О. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Я очень! <span style=\"text-decoration: underline;\">Очень</span> Вам благодарна и признательна за то, что Вы с Господом меня не оставляете. </strong>Знаете, когда я ездила за добавкой на Донецк, <strong>я там лежала на санчасти</strong><strong>, абсолютно никаких лекарств, никаких обезбаливающих, одни только перевязки на сухую (даже без каких либо мазей).</strong> При этом всём сломался телевизор, в камере я была одна, <strong>я постоянно целыми днями читала Новый Завет</strong>, чифир я не пью, <strong>почти голодала</strong> &ndash; кормят там ужасно, постоянно одна кукурузная каша. Но! Но всё это было такой <span style=\"text-decoration: underline;\">мелочью</span>!!! Самое главное &ndash; это то, что <strong>у меня на глазах начала заживать нога. Я за этим исцелением постоянно как смотрела на заживающие раны &ndash; сама себе не могла поверить.</strong> <strong>Перед тем</strong> как ехать на этап я настолько устала от этих болей, от бессонных ночей, что я уже просто не выдержала, я плюнула, махнула на всё рукой и <strong>сказала &laquo;Господи! Я уже не могу, всё! Я полностью и себя, и своё здоровье, и душу и сердце и всю свою жизнь, Господи отдаю в Твои руки! Как Ты мой Бог, распорядишься &ndash; так и будет</strong>! И вот! Дорогой отец! Ну <strong>ведь это-же и есть <span style=\"text-decoration: underline;\">Чудо</span>&raquo;! То что произошло со мной, какое исцеление &ndash; этого уже никто не отнимет.</strong> У меня даже клетки поднялись на 100 единиц... <strong>Огромное Вам спасибо за крестик и за иконку</strong>. Благодаря Господу и Вашей заботе во мне сейчас столько светлых чувств &ndash; не сравнимых ни с чем... <strong>Если есть у Вас возможность &ndash; помогите мне пожалуйста продуктовой посылкой. Чем сможете</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Оксана К. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Я в и</strong><strong>юне ездила на ознакомление в свой город и отвезла доченьку Ингочку отдала маме</strong> 29.07.2013г. но с колонии до сих пор не отправили документов на доченьку и мама неможет из-за этого оформить опеку и отдать её в детский сад. Мама очень загружена<strong> дети очень болеют у Настеньки &ndash; 27.09.2008 г.р. косоглазие, у Роксаночки &ndash; 04.02.2003 г.р. &laquo;гайморит и больные почьки&raquo;, у Назара &ndash; 02.07.1999 г.р. &laquo;паховая грыжа&raquo;, у Ромочки &ndash; 05.10.2007 г.р. ножка левая короче правой на 5 см после трудной операции и он через каждых три месяца лежит в больнице по месяцу</strong> и вот в декабре мама снова с Ромой будит в Кировограде в больнице. А <strong>у Ингочки &ndash; 04.04.2011р. &laquo;врождённый порок сердца</strong>&raquo;. Мама плохо видит, что-то с глазом произошло. Детки все с мамой по адресу прежнему. <strong>Посылки детки конечно же получили. Вам очень благодарны за Ваше внимание и поддержку моим деткам</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Олена Т. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Я одержала від Вас посилочку</strong>.<strong> Дуже Вам вдячна, спасибі Вам велике, низький уклін Вам усім за вашу доброту, щірі серця. Хай Вас Бог оберігає і завжди допомагає. Я їх приймаю одразу ліки.</strong> Спасибі. Мені освобождатися у 2014 році 13 жовтня. Діти розійшлися, живуть сім&rsquo;ями я сама, одна однісінька. Чоловік (каліка) помер, мені немає куда іти. Я хочу Вас кого-нібудь побачити, дуже надо мені усе розказати, я не можу усе описати у листі. <strong>Дуже прошу Вас шановні відвідайте мене, мені нічого не потрібно, нічого, просто чесно поговорити &ndash; це моє крик і плач душі. Богом Вас прошу допоможіть</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Наталія А. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Я хочу поблагодарить Вас за то</strong><strong> &ndash; </strong><strong>что Вы выслали мне необходимые медикаменты, большое Вам спасибо. </strong>Хочется постоянно повторять спасибо Господу Богу, за то, что посылает таких людей как Вы, которые не оставляют нас в трудную минуту... <strong>если у Вас есть возможность помогите мне пожалуйста ещё медикаментами мне необходимыми, я прилагаю к письму рецепт</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ольга К. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Получила от Вас лекарство. Очень-очень сильно благодарна за Вашу помощь, за Вашу поддержку. </strong>Пусть Господь от щедрости Своей даёт Вам Своё благословение, крепкого здоровья, мира, любви и взаимопонимания. <strong>Пусть Господь шлёт Вам только радость, счастье, благополучия за Ваши отзывы, за Ваши добрые дела</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Світлана Б. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Получила </strong><strong>Вашу посылку с медикаментами, большое сердечное Вам спасибо. Пусть Господь Бог и Пресвятая Дева Мария всегда будит с Вами и помогать Вам в Ваших делах</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Олена Б. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;Уважаемые и благословенные отец Игорь и все братья и сёстры во Христе! Разрешите вас всех от чистого сердца поздравить с наступающими праздниками с Новым 2014 Годом и Рождеством Христовым!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Событиями радостными, яркими</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Пускай наполнен будет Новый год,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Становится прекрасными подарками</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Всё то, что он с собою принесёт!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Пусть ждут дела и встречи интересные,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И будет много добрых новостей,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Как можно больше светлого, чудесного</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И самых замечательных идей!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Сьогодні я отримала від усієї громади Церкви посилочку з ліками та необхідними речами... Моє серце переповнено вдячністю</strong>... Нехай Господь щиро благословляє вас і усіх ваших рідних та близьких. Щоб ви ніколи не хворіли і завжди мали сили, слова, можливості, щоб підтримати тих, хто в цьому потребує. Я також від щирого серця <strong>надсилаю вам усім вітання з наступними святами. Надсилаю вам своє вдячне серце і самі найкращі побажання, які є у нашого Господа для кожної людини: миру, тепла, світла, доброти, натхнення, мудрості, віри, надії, любові та сил, щоб відважно зустрічати усі тяготи на життєвому шляху. Нехай береже вас Господь!</strong><strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Алла Б. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>хочу всіх Громодян Церкви Миколи Чудотворця привітати зі святом Миколи</strong>, та побажати здоровья та успіхів у ваших добрих ділах. Хай Вам допомогае Ісус а Ви по возможності помогаете нам. <strong>Я дуже Вам вдячна за підтримку та молитви, що Ви молитесь за нас</strong>. 31 жовтня до нашої колонії привозили Ікону Присвятої Богородиці Чернігівської &laquo;Дубинська&raquo;.Нам теж дали образ Ікони я читаю кожен вечер молитву. <strong>Вис. розмір взуття 42, одягу 50-52р., рост 4. Якщо можливо то з мильного та гігієни жін. Куртку, штани, хустку, светра, тут дуже холодно... будь ласка вишліть літератури &laquo;Католіческий вісник&raquo; журнали і книги</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Віра Н. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>отримала від Вас посилочку, за яку Вам дуже вдячна</strong>. Дякую людям доброї волі, які занімаються такою доброчинністю і допомагають людям в місцях позбавлення волі. Там ще був <strong>найдорожчий для мене подаруночок книжечка &laquo;Таємниця щастя&raquo; </strong>такий молитвенничок Отців Василіян. <strong>Читаю і буду читати надалі ці молитвочки, вони мені потрібні як воздух</strong>. Дуже Вам за все щиро дякую хай будуть благословенні Ваші руки і Божа любов огортає ваші сім&rsquo;ї, родини. Заодно <strong>хочу Вас всіх поздоровити з наближенням свята святого Миколая</strong>. Здоров&rsquo;я міцного Вам, добра, тепла та Божої благодаті&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ірина В. (Качанівська ВК, м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... Тут є велика бібліотека в якій є і творчість Тараса Григоровича Шевченка, в тому числі і &laquo;Кобзар&raquo;. <strong>Кожен тиждень я ходжу в бібліотеку брати книжки</strong>. Я якось ще зі школи люблю книги... <strong>я дуже багато читала про голодомор</strong>. Пам&rsquo;ятаю я ходила в 8-й клас то наш класний керівник нам давала такі домашні завдання наприклад ходити до старожилів, ну взагалі до стареньких людей та розпитувати про Велику Вітчизняну Війну, про голодомор, а потім писали на цю тему. На жаль зараз дуже багато хто помер, з тих хто все це пережив чи знав з розповіді батьків. Вчителі зараз мало залучають дітвору до творчості.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Нещодавно написала вірша.</strong> Тому Ви будете перві хто його прочитає</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Життя нам дане Богом тільки раз,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Та не цінуєм ми свого життя,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Поспішаєм знищити його враз &ndash;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Не дивлячись вперед, у майбуття.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">І не думаєм що там нас чекає,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Та й життя не завжди &ndash; казка</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Та на все є Божа ласка.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ви лиш подивіться люди добрі</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Як Всевишній всіх нас любить</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Не важливо чи здорові ви чи хворі</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Всіх на шлях на правильний направить.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">До Нього ти прийди скоріше,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Віру в Бога в серці укріпи!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">То цей світ стане красивіше і добріше</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Лиш з Богом ти вперед іди!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Так я згодна щоби Ви друкували мої листи, вірші, оповідання.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Велике Вам спасибі за розуміння і допомогу&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Марія О. (Качанівська ВК, м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... Народилась я у Кіровоградській обл. у Новомиргородському районі. <strong>У свекрухи моя дочка Вікторія. Господу слава що допомогли на 1 вересня &laquo;Школьний портфель&raquo; я Світлана молитимусь. </strong>Ніколи не думала, що через 14 років моє дежавю буде тут. <strong>Кінець срока у мене 17 января 2014 г</strong>. Поки що освобождаюсь у світ надії. Храни Вас Господь</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Память мамы</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">В марте, поздней ночью,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Долго ты молилась</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">В болях и бессилии:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&laquo;Милый мой Господь!&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Угасала медленно,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Тихо уходила...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Душа воскресала,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Умирала плоть.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Две седьмицы лет</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Пролетели быстро.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Правнуков своих</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Видишь ты с небес</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дочери твои</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">На седьмом десятке,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Сын любимый Вася</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Уж давно воскрес.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Помним тебя, мама,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Доброту, заботу,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Бескорыстный труд твой...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И благодарим.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">А Господь твой милый</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Стал Отцом и другом</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Он хранит и кормит,,,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Счастливы мы с Ним!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И когда состарились,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Лучше понимая</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Боль и одиночество, радость и любовь.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И обнять тебя, и просить прощения</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">За непонимание</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Хочется нам вновь.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Да спасибо Господу за моих родителей,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Что вошли их души в вечные обители.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Вспоминаем мы с теплом об отце и маме</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ярче просветление приходит с годами.</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Небесный ручеек</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- Почему Ты родился в хлеву?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Столько пышных дворцов и палат</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Бог Отец мог избрать для Тебя...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ты ведь &ndash; Царь, был бы этому рад?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- Да, Господь я, и Царь, и твой Бог,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Но избрал тот Сарай для Себя,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Чтобы лучше понять ты мог,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Где рождаюсь внутри тебя.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ты есть хижина, ветхий дом,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Весь из дерева, сена. Соломы;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И пронизанный насквозь грехом...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Очень мрачны твои хоромы!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">В доме этом живут ослы.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Догадался? &ndash; Твоё упрямство;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Овцы, козы, коровы, быки...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Неприглядно твоё убранство.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Если пламя охватит сей дом,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Превратится он в прах и золу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Но решил поселиться я в нём</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И внести жизнь молитву, хвалу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Я желаю очистить хлев.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ты сейчас есть моя обитель.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Не ропщи если с болью порой</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Освящает тебя Спаситель.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Приготовить хочу для Небес,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Взять детей Своих в Царствие всех.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Прежде вымету весь этот мусор:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Гордость, лень, себялюбие... &ndash; грех.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Чтоб наполнился дом красотой,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Засветился небесным светом,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Миром, радостью, чистотой</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">И моею любовью при этом.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Если хижина будет дворцом,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">А Господь &ndash; управителем станет,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">То в ворота златые войдём</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слово Божие нас не обманет&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Світлана К. (Надержинщинська ВК, Полтавська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Непрестанно благодарю Бога за Вас, за то, что Вы есть, и что помогаете таким обездоленным как я,</strong> не взирая ни на внешний облик, ни на то, что люди несут наказание за тяжкие грехи и находятся на самом дне пропасти. <strong>Вы даруете ни просто помощь, ни просто материальную поддержку, &ndash; но саму Надежду, Веру и Упование на Бога, </strong>что Он Верен обещанному нам грешным, Он умер за грехи наши, дабы мы могли спастись. Аминь!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Получила Вашу посылку, &ndash; даже слов не хватает передать Вам всю мою благодарность за помощь и заботу.</strong> Прошу Вас, предайте всем людям, кто пожертвовал на эту помощь мне, &ndash; мою благодарность, ибо я не знаю их имён, &ndash; но Господь &ndash; знает, &ndash; <strong>я молюсь о всех Вас</strong>. 28 числа прийдёт доктор, сделает необходимое назначение, и я смогу пройти курс лечения. <strong>Храни Вас Бог</strong>!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Особая благодарность душе, кто положил мне Акафест к Пресвятой Богородице, - у меня нет молитвослова, и я молюсь &laquo;своими&raquo; словами, а это пожалуй неправельно.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Я готовлюсь к Рождественскому посту</strong>. 2 декабря в колонии должна быть Литургия и причастие. Даст Бог, и мне дозволят выйти с сектора (под охраной) в церковь, да и совет мне нужен, - <strong>не знаю как правильно поститься, что кушать, а что нет, и какие молитвы читать надо</strong>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дорогие братья и сёстры! Мне подсказали, что, когда мы молимся выбранному Святому, то своей любовью входим с ним в духовное единение (общение) и он молится о нас пред Богом. <strong>Я из детства помню только, что бабушка говорила мне, что моя икона, - Матерь Божия Нерушимая стена</strong>... И икону немного помню, на ней Богородица была изображена с поднятыми руками... И уж тем более я не помню молитв. <strong>Может у кого-либо есть возможность поделиться со мной такой иконкой и молитвами?</strong> Помогите мне, хотя бы подскажите где можно достать, мне очень нужно. О.Игорь! Не могу не благодарить Христа за то, что у меня теперь новая жизнь, что я преобрела любящих меня людей. <strong>Вы и Ваши помощники на ниве Божьей, - свидетельство о живом, справедливом, любящем Боге!</strong> Бог видит Ваши труды, - и да будете благословенны. Аминь!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Простите меня Христа ради, коль огорчила Вас чем, видит Бог, - душа моя чиста&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ірина Р. (Качанівська ВК, м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Скоро Новый год</strong>, и нам очень нужны краски для рисования (любые) и ватман, и ещё ёлочные украшенияв виде дождика, ёлочной мишуры, маски, гирлянды<strong>. Мы очень слёзно Вас просим помогите нам пожалуста, чтоб в Новый год хоть на несколько часов побыть в сказке, вспомнить детство, и нам хоть на короткое время всем стать чуть чуть добрее к себе и к окружающим нас</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ольга М. (Селезнівська ВК, Луганська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>у меня псориаз</strong>, мне больно ходить у меня <strong>ноги в ранах и раны лопают и мучение конечно невыносимые. </strong>Мне в больнице сказали какие надо лекарства, только обратится нет к кому... <strong>если у Вас есть возможность помогите.</strong> Пусть Господь благословит Вас. За ранее благодарю. <strong>Влаживаю листочки с названиями лекарств</strong><strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Вікторія Ш.(Орджонікідзевський ВЦ, Дніпропетровська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;<strong>Спасибо</strong><strong> за поддержку и помощь. Хотелось бы поздравить Вас со всеми наближающимися праздниками</strong>. Пусть Вас всегда сопутствует удача, добро, мирное небо. <strong>Пусть Ваше милосердие незнает границ для всех нуждающихся людей</strong>. Спасибо и с праздниками Вас.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">С уважением &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Елена Р.&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>Олена Р. (Луганська обл., діти Марини С.,Селезнівська ВК, Луганська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;Шановний о.Ігор та всі ті люди які Вам допомагають. <strong>Ми вітаємо Вас з Новим 2014 роком та Різдвом Христовим. </strong>Нихай цей рік дасть Вам ще більше допомогати людям, служити Господу та приносити для Нього ще більше плодів Вашої праці. <strong>Ми з моєю донечкою Вікою Вам дуже вдячні. Хай Господь блогословить Вас.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">З повагою &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Віка й Тетяна&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>Тетяна С. (Херсонська обл., звільн. з Дніпродзержинської ВК, дочка Вікторія, 11р.)</em></span></p>',NULL,5521,5,1,NULL,19);
INSERT INTO `publications_publication` VALUES
(1618,'P','2014-02-14',1,NULL,'Rss','Святий великомученик Валентин','<p><span style=\"font-size: 0.875em;\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5517,200,1.jpg\" /><strong>\"Господи Боже, джерело любови, добра і милосердя. Ти через свого слугу, святого мученика Валентина лікував різні хвороби, а також приводив до християнського життя у подружжі закоханих.Благаю Тебе, Господи, через Його заступництво дати і сьогодні ласку для тих, хто з вірою і любов’ю до Тебе прибігає. Амінь\".</strong></span></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1852:svjaty-velykomuchenyk-valentyn&catid=35:istorychni-postati-cat&Itemid=48',5518,NULL,1,1,26),
(1619,'P','2014-02-14',1,NULL,'Rss','Кошенята на продаж (14 лютого)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5515,200,10308083.jpg\" />Продавець в невеличкій крамниці повісив біля входу оголошення «Продаються кошенята».</p>\n<p>Цей напис, природньо, привернув увагу місцевих дітлахів і за лічені хвилини до крамниці зайшов хлопчик. Привітавшись із продавцем, він несміливо запитав про ціну кошенят.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1849:koshenjata-na-prodazh-14-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5517,NULL,1,1,26),
(1620,'P','2014-02-13',1,NULL,'Rss','«У серці людини. Утопія та зобов’язання»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5515,200,papa19francysk19_intervju.jpg\" />Папа Франциск 2005 року видав збірку думок «У серці людини. Утопія та  зобов’язання», присвячених темі побудови країни, нації, у світлі  непростої тогочасної ситуації аргентинського народу. Автор поділився  думками про будування майбутнього, призначеного для всіх. Ці думки не  втратили своєї актуальності та можуть бути застосовані до сьогоднішньої  України.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1848:2014-02-13-21-59-53&catid=32:gk-svit-cat&Itemid=58',5516,NULL,1,1,26),
(1621,'P','2014-02-12',1,NULL,'Rss','Притча про образу (13 лютого)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5514,200,65772-1920x1200.jpg\" />Якось учитель з учнем ішли путівцем. Бачать на зустріч їм прямує буркочучи чоловік, увесь розбурханий, мало не плаче.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Що сталося, друже мій? - запитує учитель.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Та мене щойно якийсь негідник образив останніми словами і до того ж пусто-дурно! Що я зробив йому?</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1847:prytcha-pro-obrazu-13-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5515,NULL,1,1,26),
(1622,'P','2014-02-12',1,NULL,'Rss','Перемога Божої правди, яка принесе мир','<p><strong><img style=\"margin: 10px; float: right;\" title=\"Broshchak\" src=\"http://poglyad.te.ua/wp-content/uploads/2014/02/Broshchak-300x204.jpg\" height=\"136\" width=\"200\" /></strong>Як  правильно молитися за перемогу Майдану проти диктатури діючої влади,  розповідав на лекції Школи християнського життя та євангелізації 8  лютого тернопільський магістр морального богослов’я, випускник  Люблінського католицького університету Андрій Брощак.</p>\n<p>Багато християн які не мають можливості перебувати у столиці на  Майдані кажуть: «Треба молитися!». Та не всі знають, як правильно це  робити, аби Бог почув наші прохання.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1846:peremoga-bozhoji-pravdy-jaka-prynese-myr&catid=27:gk-v-ukrajini-cat&Itemid=57',5514,NULL,1,1,26);
INSERT INTO `publications_publication` VALUES
(1623,'P','2014-02-12',1,NULL,'Rss','Голгофа Ольги Басараб','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5510,200,5dc4aa401b.jpg\" />Ольга Басараб народилася <a href=\"http://uk.wikipedia.org/wiki/1_%D0%B2%D0%B5%D1%80%D0%B5%D1%81%D0%BD%D1%8F\" title=\"1 вересня\">1 вересня</a> <a href=\"http://uk.wikipedia.org/wiki/1889\" title=\"1889\">1889</a> року в селі <a href=\"http://uk.wikipedia.org/wiki/%D0%9F%D1%96%D0%B4%D0%B3%D0%BE%D1%80%D0%BE%D0%B4%D0%B4%D1%8F\" title=\"Підгороддя\">Підгороддя</a> на <a href=\"http://uk.wikipedia.org/wiki/%D0%A0%D0%BE%D0%B3%D0%B0%D1%82%D0%B8%D0%BD%D1%81%D1%8C%D0%BA%D0%B8%D0%B9_%D1%80%D0%B0%D0%B9%D0%BE%D0%BD\" title=\"Рогатинський район\">Рогатинщині</a> в родині відомого громадського діяча о. Михайла Левицького.</p>\n<p>Навчалася Ольга у приватному пансіоні для дівчат у Вайсвассері (<a href=\"http://uk.wikipedia.org/wiki/%D0%A1%D1%96%D0%BB%D0%B5%D0%B7%D1%96%D1%8F\" title=\"Сілезія\">Сілезія</a>) та ліцеї <a href=\"http://uk.wikipedia.org/wiki/%D0%A3%D0%BA%D1%80%D0%B0%D1%97%D0%BD%D1%81%D1%8C%D0%BA%D0%B8%D0%B9_%D1%96%D0%BD%D1%81%D1%82%D0%B8%D1%82%D1%83%D1%82_%D0%B4%D0%BB%D1%8F_%D0%B4%D1%96%D0%B2%D1%87%D0%B0%D1%82_%D1%83_%D0%9F%D0%B5%D1%80%D0%B5%D0%BC%D0%B8%D1%88%D0%BB%D1%96\" title=\"Український інститут для дівчат у Перемишлі\">Українського інституту для дівчат у Перемишлі</a> (1902–1909), пізніше на однорічних курсах у Віденській торговій академії. З 1910&nbsp;р. працювала в страховому товаристві «Дністер», Земельному іпотечному <a href=\"http://uk.wikipedia.org/wiki/%D0%91%D0%B0%D0%BD%D0%BA\" title=\"Банк\">банку</a>.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=1845:golgofa-olgy-basarab&catid=25:parafija-cat&Itemid=27',5513,NULL,1,1,26),
(1624,'P','2014-02-11',1,NULL,'Rss','Розважання лютий 2014р.','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5510,200,400147.jpg\" />«Блаженні чисті серцем, бо вони побачать Бога» (Мт 5,8).</strong></p>\n<p>Проповідування Ісуса починається Нагірною проповіддю. Перед Тиверіадським озером, на пагорбах в околицях Капернаума, сидячи, як і вчителі того часу, Ісус проголошує натовпу людей блаженства. Не один раз у Старому Завіті звучить слово «блаженний», тобто звеличення того, хто сповняє у різні способи Слово Боже.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=1844:rozvazhannja-ljuty-2014r&catid=25:parafija-cat&Itemid=27',5512,NULL,1,1,26),
(1625,'P','2014-02-11',1,NULL,'Rss','Що в гаманці (12 лютого)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5510,200,8287.jpg\" />Кілька хлопців ображали християнина, який ішов через їхній квартал. А той відповідав підносячи за них молитви. Хтось запитав його.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чому ти відповідаєш на образи молитвою?</p>\n<p>Християнин відказав:</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1843:shcho-v-gamanci-12-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5511,NULL,1,1,26),
(1626,'P','2014-02-11',1,NULL,'Rss',' Розарій – «головна зброя» католиків','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5507,200,1002.jpg\" />- Хто Ти? – Спитала найстарша з трьох дітей Лусія.</strong></p>\n<p><strong>- Я – Цариця Святого Розарію, – відповіла прекрасна Пані.</strong></p>\n<p>Вона назвалася Царицею, і я в молитвах промовляю до Неї: Погоджуюся  бути Твоєю підданою, приймати Твої послання як накази. Приходячи до  людей, Марія повторює: «Моліться Розарій».</p>\n<p>Важко! І я вирішила написати Їй листа.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1842:2014-02-10-23-16-43&catid=28:tema-nomera-cat&Itemid=2',5510,NULL,1,1,26);
INSERT INTO `publications_publication` VALUES
(1627,'P','2014-02-11',1,NULL,'Rss','Посудина з оливою (11 лютого)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5507,200,DSCN8992.JPG\" />Прийшов до царя якось чоловік і запитав, чи той не знає, як спасти себе від прогрішення.</p>\n<p>Цар повелів йому наповнити посудину оливою по самі вінця і пронести через місто не розливши жодної краплини.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Якщо вильєш хоч краплину, то будеш без голови.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1841:posudyna-z-olyvoju-11-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5509,NULL,1,1,26),
(1628,'P','2014-02-11',1,NULL,'Rss','Спекуляції на темі антисемітизму - є провокацією','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5507,200,ArticleIma.jpg\" />Спекуляції на темі антисемітизму, які лунали особливо часто в бік  Майдану, є просто провокацією, організованою не знизу, а згідно з певним  планом. Такими роздумами про технології впливу на громадську свідомість  поділилась професор релігієзнавства Людмила Филипович.</p>\n<p>«Ця провокація – не ініціатива знизу, вона входила в певний план.  Недружня сторона Майдану, як легко помітити, постійно займається  нескінченними провокаціями. Українець, як це не прикро сказати, все ж  трохи лінивий. Для подібної спекуляції треба зібратись, запланувати це,  піти, вловити єврея, побити його – українець так не мислить. Так мислять  люди, які цьому навчені», – переконана релігієзнавець.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1840:spekuljaciji-na-temi-antysemityzmu-je-provokacijeju&catid=28:tema-nomera-cat&Itemid=2',5508,NULL,1,1,26),
(1629,'P','2014-02-10',1,NULL,'Rss','Притча про існування Бога (10 лютого)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5506,200,10-n.jpeg\" />Одного разу професор відомого університету поставив своїм студентам запитання.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чи є Бог сотворителем усього сущого.</p>\n<p>Один зі студентів хоробро відповів:</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1839:prytcha-pro-isnuvannja-boga-10-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5507,NULL,1,1,26),
(1630,'P','2014-02-10',1,NULL,'Rss',' Богоматір Сухого Дерева','<img style=\"margin: 10px; float: right;\" alt=\"Богоматір сухого дерева\" src=\"http://www.credo-ua.org/wp-content/uploads/2014/02/%D0%91%D0%BE%D0%B3%D0%BE%D0%BC%D0%B0%D1%82%D0%B5%D1%80%D1%8C-%D1%81%D1%83%D1%85%D0%BE%D0%B3%D0%BE-%D0%B4%D1%80%D0%B5%D0%B2%D0%B0.jpg\" height=\"125\" width=\"125\" />\n<p><strong>Цей образ був написаний 1642 року. Він представляє дуже  рідкісний сюжет: зображення Діви Марії з Немовлям, оточених гілками  сухого дерева.</strong></p>\n<p>Цей сюжет натхнений словами пророка Єзекиїля: «Всі польові дерева  взнають, що Я&nbsp;— Господь, що Я принижую дерево&nbsp; високе, що Я підвищую  дерево низеньке, що Я висушую дерево зелене, а дерево сухе сподіюю  квітучим» (Єз 17,&nbsp;24).</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1838:-bogomatir-suhogo-dereva&catid=35:istorychni-postati-cat&Itemid=48',5506,NULL,1,1,26),
(1631,'P','2014-02-09',1,NULL,'Rss',' Про послух владі, і чи кожна влада від Бога','<p><img style=\"margin: 10px; float: left;\" alt=\"о. Георгій Чистяков\" src=\"http://www.credo-ua.org/wp-content/uploads/2014/02/chistiakov3.jpg\" height=\"161\" width=\"200\" /></p>\n<p><strong>Коментар <a href=\"http://ru.wikipedia.org/wiki/%D0%A7%D0%B8%D1%81%D1%82%D1%8F%D0%BA%D0%BE%D0%B2,_%D0%93%D0%B5%D0%BE%D1%80%D0%B3%D0%B8%D0%B9_%D0%9F%D0%B5%D1%82%D1%80%D0%BE%D0%B2%D0%B8%D1%87\">о.&nbsp;Георгія Чистякова</a> до слів Апостола Павла «немає влади, щоб не була від Бога» (Рим 13,&nbsp;1).</strong></p>\n<p>Якщо ми з вами ось зараз переходимо до роздумів про те, що християнин  має бути активний,&nbsp;— а ми знаємо з минулого, які активні були  подвижники, які активні були праведники, яка активна була у  висловлюванні своїй поглядів на життя суспілства мати Марія Скобцова та  чимало інших людей її часу й попередніх часів,</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1837:-pro-posluh-vladi-i-chy-kozhna-vlada-vid-boga&catid=37:spilnota-cat&Itemid=49',5505,NULL,1,1,26);
INSERT INTO `publications_publication` VALUES
(1632,'P','2014-02-09',1,NULL,'Rss','«Бог не остання надія, а єдина»','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5501,200,658.jpg\" />Такі слова написані на одному з побожних малюнків, якими щедро  заповнений акаунт на Фейсбуку, нещодавно створений студитом о.Юстином  Бойком.</strong></p>\n<p>Ця інтернет-спільнота називається <a href=\"https://www.facebook.com/groups/248222872008736/\">«Молитовний Майдан України»</a>.  У&nbsp;поясненні до створення сторінки її автор написав: «У&nbsp;зв’язку з  останніми подіями, які сталися у Києві, а також по цілій Україні, кожен  свідомий громадянин шукає способу відповідної реакції.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1836:2014-02-08-22-11-41&catid=37:spilnota-cat&Itemid=49',5504,NULL,1,1,26),
(1633,'P','2014-02-08',1,NULL,'Rss','Запрошення на весілля (9 лютого)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5501,200,0_7c3fd_3ef98db0_L.jpg\" />Один юнак дуже гордився тим, що у нього дуже багато приятелів. Коли він вирішив одружитися, весілля взявся готувати його батько. Опріч іншого, батько також розсилав запрошення.</p>\n<p>Однак у день весілля жоден з приятелів нареченого так і не прийшов. Хлопець розсердився і кинувся до батька з претензіями.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1835:zaproshennja-na-vesillja-9-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5502,NULL,1,1,26),
(1634,'P','2014-02-08',0,NULL,'Note','Безперервна молитва i пiст за Укранiу','<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><span style=\"text-indent: 0em; line-height: 1.3em;\"><img style=\"float: right;\" src=\"image,5488,200,1555450_699267273439223_1091393233_n.jpg\" alt=\"\" width=\"200\" height=\"150\" /></span></span><span style=\"font-size: small;\"><span style=\"font-family: arial, helvetica, sans-serif;\"><span style=\"text-indent: 0em; line-height: 1.3em;\"><span style=\"font-family: arial, helvetica, sans-serif;\">- закликаємо усіх наших вірних та людей доброї волі,&nbsp;</span></span></span><span style=\"font-family: arial, helvetica, sans-serif;\"><strong style=\"text-indent: 0em; font-size: 0.625em; line-height: 1.3em;\">кожного дня о 21.00 год. єднатися у спільній молитві за Україну</strong><span style=\"text-indent: 0em; line-height: 1.3em;\">;</span></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- заохочуємо вірних також до&nbsp;<strong>приватних молитов за Україну</strong>;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- оголошуємо безперервний&nbsp;<strong>строгий піст в наших Єпархіях та Екзархатах</strong><br /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>','<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,5488,200,1555450_699267273439223_1091393233_n.jpg\" alt=\"\" width=\"200\" height=\"150\" />Практичні душпастирські вказівки&nbsp;</strong></span><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">про спільну молитву та піст за Україну</strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; доручаємо&nbsp;<strong>продовжувати у храмах, монастирях та семінаріях безперервну цілодобову молитву</strong>, відповідно до порядку, визначеного отцями-парохами та настоятелями;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; закликаємо усіх наших вірних та людей доброї волі,&nbsp;<strong>кожного дня о 21.00 год. єднатися у спільній молитві за Україну</strong>;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; заохочуємо вірних також до&nbsp;<strong>приватних молитов за Україну</strong>;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- &nbsp; &nbsp; оголошуємо безперервний&nbsp;<strong>строгий піст в наших Єпархіях та Екзархатах</strong>&nbsp;за таким порядком:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">o&nbsp;&nbsp; Понеділок &ndash; Київська архиєпархія, Донецько-Харківський Екзархат, Одесько-Кримський Екзархат;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">o&nbsp;&nbsp; Вівторок &ndash; Львівська архиєпархія, Стрийська єпархія;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">o&nbsp;&nbsp; Середа &ndash; Івано-Франківська архиєпархія, Коломийсько-Чернівецька єпархія;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">o&nbsp;&nbsp; Четвер &ndash; Тернопільська архиєпархія, Бучацька Єпархія, Самбірсько-Дрогобицька єпархія;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">o&nbsp;&nbsp; П&rsquo;ятниця &ndash; Сокальсько-Жовківська єпархія, Луцький Екзархат, Мукачівська єпархія;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; доручаємо після кожної Божественній Літургії промовляти нижче подану&nbsp;<strong>Молитву митрополита Андрея Шептицького за кращу долю українського народу</strong>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Молитва за кращу долю українського народу</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Всемогутній, безсмертний Боже, Отче Господа нашого Ісуса Христа! Ти страшний у Твоєму гніві, але безконечно добрий у Твоєму милосерді! Ти зсилаєш на наш грішний людський рід тяжкі допусти і тяжкі терпіння.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ти й на наш нарід зіслав такі терпіння, що їх не знали наші батьки. Ми мусимо гірко трудитися, щоб здобути собі насущний хліб. Віримо і надіємося, що тяжкі ті допусти зіслав Ти на людський рід як досвід, а не як кару.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Грішних твоїх синів Ти не відтручуєш, не відвертаєш твого пресвятого обличчя від їх долі, бо не хочеш смерти грішника, а його навернення та спасення.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Тому надіємося, що всі терпіння, що їх доводиться нам у житті зносити з милосердної Твоєї волі, допущені для нашого добра. Ми віримо й надіємося, що покаянням, щирою молитвою і щирим прийманням Найсвятіших Таїнств зможемо заслужити собі на те, що скоротиш час досвіду і терпінь, що милосердним оком споглянеш на твоїх дітей, що даси їм витривати з благодаттю у злиднях теперішнього життя і всемогутньою Твоєю волею положиш край нашим терпінням.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дорогоцінною кров&rsquo;ю твого Сина бажаємо надолужити Тобі за всі гріхи наших батьків і братів. Через руки наших священиків жертвуємо Тобі безкровну жертву пресвятої Євхаристії, що є повторенням і відновленням хресної жертви Твого Сина і Бога, Спасителя нашого Ісуса Христа.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Прийми цю жертву надолуження за всі гріхи людей, прийми наші благальні прохання про милосердя і твою святу благодать.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Умилосердися над усіма тими, що терплять. Даруй їм ласку терпеливо зносити гірку долю і через терпеливість заслужити на пільгу в терпіннях і перемогу в цьому смертному житті та на блаженство вічности. Амінь.</span></p>',NULL,5488,5,1,NULL,17);
INSERT INTO `publications_publication` VALUES
(1635,'P','2014-02-08',1,NULL,'Rss','Цінність веб-проектів УГКЦ','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5501,200,sheptytskyj_huzar.jpg\" />У ці непрості для нашої держави дні ми почуваємося виснаженими. Інколи хочемо заплющити очі, заткнути вуха, тобто відмежуватися від інформаційного простору, який оточує нас звідусіль. А може, саме час змінювати себе в собі? Може, все-таки варто вимкнути комп’ютер, телевізор чи радіоприймач і побути наодинці із собою?</p>\n<p>Настав час звернутися до українських автентичних духовних першоджерел. Нещодавно УГКЦ презентувала і водночас запросила долучитися до двох веб-проектів – «Любомир» та «365 днів з великим митрополитом Андреєм Шептицьким».</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1834:cinnist-veb-proektiv-ugkc&catid=27:gk-v-ukrajini-cat&Itemid=57',5503,NULL,1,1,26),
(1636,'P','2014-02-07',1,NULL,'Rss','Червоне море (8 лютого)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5499,200,index25.jpg\" />Християнин в електричці читає Біблію Поряд сидить безвірник. І раптом на обличчі вірного проступає подив.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Оце так! Та не вже?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Що ти там вичитав? – запитує атеїст.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1833:chervone-more-8-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5501,NULL,1,1,26),
(1637,'P','2014-02-07',1,NULL,'Rss','«Не можна допустити, щоб наші міста й села стали полем бою»','<div>\n<div>\n<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5499,200,index.jpg\" />Учасник Ініціативної групи “Першого грудня” Мирослав Попович — про владу і революцію, пошуки істини, правди і свободи.</p>\n</div>\n</div>\n<p>Ми зустрілися з Мирославом Володимировичем у його кабінеті в  Інституті філософії, який розташований між Михайлівською площею та  захопленим маніфестантами Українським домом. Безперечно, місце вплинуло  на нашу розмову.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1832:2014-02-07-19-18-16&catid=28:tema-nomera-cat&Itemid=2',5500,NULL,1,1,26),
(1638,'P','2014-02-06',1,NULL,'Rss','Небачений звір (7 лютого)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5488,200,images123.jpg\" />Учені ентузіасти дуже прагнули знайти якусь нову невідому тварину. Вони виїжджали в гори, шукали там сліди і збирали різноманітні відомості у місцевого населення.</p>\n<p>І від мешканців одного невеликого селища, розташованого у далекі глушині, вони почули. що в околиці блукає чудернацька істота. І справді, на стежках почали з’являтися дивні сліди, а вночі було чути, як хтось бродить на схилах гір.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1831:nebacheny-zvir-7-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5499,NULL,1,1,26),
(1639,'P','2014-02-06',1,NULL,'Rss','Ми стоїмо над прірвою кривавого хаосу','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5488,200,1_8.jpg\" />Ваше Преосвященство, як на Ваш погляд маємо дивитися на ситуацію, що складається зараз у нашій державі?</strong></p>\n<p>На теперішню ситуацію в Україні можна дивитися з двох перспектив: з суто людської і з Божої. Якщо дивитися на неї з людського погляду, то я би охарактеризував її як крайнє драматичну. Моє особисте враження, що з часів ІІ Світової війни в нас не було настільки драматичної ситуації.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1830:my-stojimo-nad-prirvoju-kryvavogo-haosu&catid=28:tema-nomera-cat&Itemid=2',5497,NULL,1,1,26);
INSERT INTO `publications_publication` VALUES
(1640,'P','2014-02-05',1,NULL,'Note','ПОЖЕРТВИ ПАРАФІЯН ЦЕРКВИ СВ. МИКОЛАЯ ЧУДОТВОРЦЯ НА АСКОЛЬДОВІЙ МОГИЛІ У СІЧНІ 2014 Р. Б.','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Милостиню роби згідно з тим, що маєш;Коли у тебе достатків повно, Роби з них, а коли мало маєш, то не бійся з того дати. Бо так готуєш собі добрий скарб на день потреби. Товит 4. 8-9.</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>','<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого. </span></p>\r\n<table style=\"width: 467px; height: 2278px;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">№ з/п</span></p>\r\n</td>\r\n<td width=\"312\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ж Е Р Т В О Д А В Ц І</span></p>\r\n</td>\r\n<td width=\"359\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пожертва на (для, до):</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1</span></p>\r\n</td>\r\n<td width=\"312\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Марія</span></p>\r\n</td>\r\n<td width=\"359\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Квіти і молитву до свят</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2</span></p>\r\n</td>\r\n<td width=\"312\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Надія</span></p>\r\n</td>\r\n<td width=\"359\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">3</span></p>\r\n</td>\r\n<td width=\"312\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Любов</span></p>\r\n</td>\r\n<td width=\"359\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Десятина</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">4</span></p>\r\n</td>\r\n<td width=\"312\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Родина Колбасовських</span></p>\r\n</td>\r\n<td width=\"359\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Десятина</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">5</span></p>\r\n</td>\r\n<td width=\"312\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Юлія</span></p>\r\n</td>\r\n<td width=\"359\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Десятина</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">6</span></p>\r\n</td>\r\n<td width=\"312\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ксенія</span></p>\r\n</td>\r\n<td width=\"359\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Потреби дітям під опікою &laquo;Карітас-Печерськ&raquo;</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">7</span></p>\r\n</td>\r\n<td width=\"312\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Тарас та Оксана Михалюнюк</span></p>\r\n</td>\r\n<td width=\"359\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Храм</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">8</span></p>\r\n</td>\r\n<td width=\"312\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Світлана Подоляк</span></p>\r\n</td>\r\n<td width=\"359\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Потреби церкви</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">9</span></p>\r\n</td>\r\n<td width=\"312\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Зоряна</span></p>\r\n</td>\r\n<td width=\"359\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Подяка Богу за дар материнства</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">10</span></p>\r\n</td>\r\n<td width=\"312\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Родина Дзьомбак</span></p>\r\n</td>\r\n<td width=\"359\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">11</span></p>\r\n</td>\r\n<td width=\"312\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Володимир та Романна Левицька</span></p>\r\n</td>\r\n<td width=\"359\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">12</span></p>\r\n</td>\r\n<td width=\"312\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Родина Яворських</span></p>\r\n</td>\r\n<td width=\"359\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">13</span></p>\r\n</td>\r\n<td width=\"312\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Юрій Лоза</span></p>\r\n</td>\r\n<td width=\"359\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дар на потреби церкви</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">14</span></p>\r\n</td>\r\n<td width=\"312\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Олег та Віра Кошіль</span></p>\r\n</td>\r\n<td width=\"359\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дар на пошиття фелону</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">15</span></p>\r\n</td>\r\n<td width=\"312\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Володимир та Володимира</span></p>\r\n</td>\r\n<td width=\"359\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">16</span></p>\r\n</td>\r\n<td width=\"312\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Георгій Георгійович Філіпчук</span></p>\r\n</td>\r\n<td width=\"359\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пам&rsquo;ятний знак Т.&nbsp;Шевченку</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">17</span></p>\r\n</td>\r\n<td width=\"312\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольд</span></p>\r\n</td>\r\n<td width=\"359\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пам&rsquo;ятний знак Т.&nbsp;Шевченку</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18</span></p>\r\n</td>\r\n<td width=\"312\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">о.&nbsp;Ігор та Антоніна</span></p>\r\n</td>\r\n<td width=\"359\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пам&rsquo;ятний знак Т.&nbsp;Шевченку</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">19</span></p>\r\n</td>\r\n<td width=\"312\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Марія</span></p>\r\n</td>\r\n<td width=\"359\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пам&rsquo;ятний знак до 200-річчя Т.&nbsp;Шевченку</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">20<