INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(842,'P','2009-08-27',1,NULL,'Note','В УНІАН презентували другий тур благодійних концертів «З вірою і любов’ю» ','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Як повідомив С.Архипчук, у концертах візьмуть участь відомі гурти, зокрема, &laquo;Піккардійська терція&raquo;, &laquo;Друга ріка&raquo;, &laquo;С.К.А.Й.&raquo;, &laquo;Гудімов&raquo;, а також виконавці: Тарас Петриненко, Тетяна Горобець, лідер гурту &laquo;Мандри&raquo; Фома. За його інформацією, гурт &laquo;Горгішелі&raquo; спеціально пише псалом, який представить на цих концертах. Ведучими концертів почергово будуть відома актриса, телеведуча Ольга Сумська та спортивний журналіст Ігор Мірошниченко.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">З ініціативи Української Греко-Католицької Церкви та під патронатом її Глави Блаженнішого Любомира <span style=\"text-decoration: underline;\"><span style=\"color: #0000ff;\">з 7 по 22 вересня у 12 містах України пройдуть благодійні концерти популярної музики &laquo;З вірою і любов&rsquo;ю&raquo;</span></span>. Про це на прес-конференції в УНІАН журналістам розповіли Блаженніший Любомир, Наталія Сумська, народна артистка України, лауреат Національної премії ім. Тараса Шевченка, Олег Собчук, лідер гурту &laquo;С.К.А.Й.&raquo; та Сергій Архипчук, режисер-постановник пісенного собору. </span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Сергій Архипчук нагадав, що академічні концерти, об&rsquo;єднані такою ж назвою, з великим успіхом пройшли в травні ц.р. Другий тур концертів охопить міста у 6 областях України: Волинській, Рівненській, Чернівецькій, Тернопільській, Івано-Франківській та Львівській.</span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Блаженніший Любомир переконаний, що ці концерти є &laquo;ліками на хвороби&raquo; сучасного суспільства, тому є намір охопити ними якнайбільше вікових груп населення та людей різних вірувань. Зокрема, якщо концерти суто класичної, академічної музики відвідували здебільшого люди старшого віку, то передбачається, що на концерти у вересні завітає &laquo;молодша публіка&raquo;, з огляду не тільки на вік, а й молодість духу. Планується організувати в майбутньому і дитячі концерти.</span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&laquo;Ми переконались, що ті концерти, які були у травні, що така музика в формі таких концертів має дуже благородний, заспокоюючий характер на людей. Люди сприймали це як бальзам на їх душі. І мені це здається надзвичайно важливим сьогодні особливо, коли ми живемо в такій ситуації &ndash; в період кризи економічної та інших проблем, що з тим зв&rsquo;язані, і напередодні політичного сезону, де створюється метушня, неспокій...&raquo;, &ndash; зазначив Глава УГКЦ.</span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">За його словами, за допомогою цих концертів є спроба представити людям певний &laquo;заспокоюючий бальзам для душі&raquo;. &laquo;Щоб в тій метушні вони могли ввійти в себе і відчути внутрішній спокій, внутрішнє задоволення, внутрішнє відчуття себе самих, яке повинне надати не тільки велике задоволення, а й момент духовного відпочинку і духовного підйому&raquo;, &ndash; це, за його словами, &laquo;основна думка&raquo; щодо того, чого хоче досягнути УГКЦ.</span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Як повідомив С.Архипчук, у концертах візьмуть участь відомі гурти, зокрема, &laquo;Піккардійська терція&raquo;, &laquo;Друга ріка&raquo;, &laquo;С.К.А.Й.&raquo;, &laquo;Гудімов&raquo;, а також виконавці: Тарас Петриненко, Тетяна Горобець, лідер гурту &laquo;Мандри&raquo; Фома. За його інформацією, гурт &laquo;Горгішелі&raquo; спеціально пише псалом, який представить на цих концертах. Ведучими концертів почергово будуть відома актриса, телеведуча Ольга Сумська та спортивний журналіст Ігор Мірошниченко.</span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Потрапити на концерт можна буде за запрошеннями, які розповсюджуватимуться передовсім у парафіях УГКЦ та молодіжних, громадських організаціях і спільнотах. Кожне таке запрошення передбачатиме певну суму внеску на підтримку церкви &ndash; від 20 до 50 грн.</span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Як зазначалось на прес-конференції, зібрані на весняних та нинішніх концертах кошти підуть на завершення будівництва Патріаршого собору Воскресіння Христового в Києві. Під час академічних концертів у травні було зібрано близько 800 тис. грн.</span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">За матеріалами </span></span><a href=\"http://religions.unian.net/ukr/\" target=\"_blank\"><span style=\"text-decoration: underline;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">religions.unian.net</span></span></span></a></p>',NULL,NULL,1761,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(843,'P','2008-11-01',1,NULL,'Note','Молитва \"Ангел Господній\" в празник Всіх Святих','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1 листопада, з нагоди празника Всіх Святих, розпочинаючи свою катехизу перед молитвою &laquo;Ангел Господній&raquo;, Вселенський Архиєрей сказав, що відвідуючи ботанічний сад нас вражає різноманітність квітів та рослин, саме тоді призадумуємося над тим, яку фантазію мав Сотворитель, творячи усю цю красу.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1 листопада, з нагоди празника Всіх Святих, розпочинаючи свою катехизу перед молитвою &laquo;Ангел Господній&raquo;, Вселенський Архиєрей сказав, що відвідуючи ботанічний сад нас вражає різноманітність квітів та рослин, саме тоді призадумуємося над тим, яку фантазію мав Сотворитель, творячи усю цю красу. Але подібно діється, коли поглянемо на святість: світ видається немов сад, де Дух Господній надихнув до святого життя велику кількість жінок та чоловіків, різного віку, соціального становища, мови, національності та культури. Кожен із них є відмінним від іншого, відрізняється своєю неповторною особистістю та духовною харизмою. Проте усі вони мають, немов, &laquo;знак&raquo; Христа, тобто відбиток Божої любові, любові Ісуса Христа, яку Він засвідчив, зокрема, на хресті. Усі вони перебувають у радості, беруть участь у святкуванні, яке не має кінця, проте, до цієї мети вони дійшли через чималі труднощі та випробування, за прикладом нашого Спасителя.<br /><br />Папа зазначив, що празник Всіх Святих був започаткований і відзначався ще у першому тисячолітті, коли християни молитовно вшановували усіх мучеників. Вже у 609 році у Римі, Папа Боніфатій ІV посвятив Пантеон, присвячуючи його Пресвятій Діві Марії та усім Мученикам. На думку Святішого Отця, мучеництво, в загальному, розуміємо як любов за Христа, яка нічого для себе не вимагає, як любов, що повністю жертвує себе у дарі Господеві та ближнім. Цю духовну ціль, до якої покликані усі, без винятку, християни, можна осягнути шляхом євангельських &laquo;блаженств&raquo;, дороговказів, які пропонує нам Святий Євангелист Матей. <img style=\"float: left;\" src=\"image,1582,200,foto_09.jpg\" alt=\"\" />Як наголосив Папа Венедикт ХVІ, це та сама дорога, якою йшов Ісус і якою, докладаючи усіх зусиль, крокували святі, усвідомлюючи свої людські обмеження. У своєму житті вони були убогі духом, засмучені гріхами, тихі, голодні та спраглі справедливості, милосердні, чисті серцем, миротворці та переслідувані за правду. Господь дозволив їм взяти участь у Його щасті: частково тут, на землі, та у повноті в небесному царстві. Тепер вони утішені, спадкоємці землі, насичені, прощені, споглядають Господа, дітьми Якого вони є. Підсумовуючи можна сказати, що &laquo;їхнє Царство Небесне&raquo; (див.&nbsp;Мт.&nbsp;5,3-10). <br /><br />А на закінчення Святіший Отець процитував слова однієї релігійної пісні, у якій оспівується палке прагнення християнина належати до громади святих. Далі Папа побажав, щоб у душах усіх вірних розгорілось таке прагнення, допомагаючи долати усі перешкоди, страх та труднощі, що траплятимуться на життєвому шляху; заохочуючи також довіритись материнській опіці Пресвятої Богородиці, яка супроводжуватиме усіх нас до небесної батьківщини.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,NULL,602,5,1,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(844,'P','2009-08-29',1,NULL,'Rss','Успіня Богородиці на Аскольдовій Могилі','<p>Свято Успіня Богородиці святкували на Аскольдовій Могилі. На при кінці Божественної літургії була освячена вода і принесені квіти для Богородиці. Цим святом завершується церковний рік.</p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=423:uspinja-bogorodyci-na-askoldovi-mogyli&catid=25:parafija-cat&Itemid=27',1769,NULL,1,26),
(845,'P','2009-09-07',1,NULL,'Note','Ще є можливість внести свою «цеголку» на будівництво Патріаршого собору Воскресіння Христового у Києві  ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Основною причиною відновлення акції, за словами Владики Богдана (Дзюрах), є часті звернення людей, які не змогли раніше взяти у ній участь, або хотіли б придбати ще одну &laquo;цеголку&raquo; для себе чи своїх ближніх.<br /></span></span></p>','<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"border: 1px solid black; margin: 5px; width: 241px; height: 170px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_cegolka10_01.jpg.jpg\" alt=\"\" />Від осені 2007 року до літа 2008 року тривав перший етап збору пожертв для будівництва Патріаршого собору Воскресіння Христового у м. Києві. Головний спосіб отримання необхідних коштів &ndash; розповсюдження спеціальних &laquo;цеголок&raquo; номіналом 10, 20, 50, 100, 500 та 1000 грн, придбавши які жертводавець робив свій посильний внесок у спільну велику справу. Упродовж того етапу від реалізації &laquo;цеголок&raquo; єпархії та екзархати УГКЦ в Україні перерахували на будівництво Патріаршого центру 3 млн 903 тис. 132 грн. Крім того, з Італії надійшло 43 тис. євро, а із США &ndash; 300 тис. 440 дол. Детальніше про підсумки цієї акції див. за адресою: <a href=\"http://kyivsobor.ugcc.org.ua/content/view/197/40/\" target=\"_blank\">kyivsobor.ugcc.org.ua/content/view/197/40/</a>.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Від 1 листопада 2008 року у всіх єпархіях і екзархатах в Україні відновлено розповсюдження &laquo;цеголок&raquo;. Як і раніше, їх можна придбати у кожній парафії УГКЦ України, а також у громадах УГКЦ в Італії, США, Польщі, Німеччині та Канаді.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Основною причиною відновлення акції, за словами Владики Богдана (Дзюрах), є часті звернення людей, які не змогли раніше взяти у ній участь, або хотіли б придбати ще одну &laquo;цеголку&raquo; для себе чи своїх ближніх. &laquo;Узгодивши це питання з нашими єпископами, ми вирішили продовжити акцію і сподіваємося, що економічна криза не спричинить кризи жертовності наших людей&raquo;, &ndash; сказав Владика Богдан. Станом на 1 вересня 2009 року через розповсюдження &laquo;цеголок&raquo; на будівництво Патріаршого собору було перераховано 625 тис. 771 грн <em>(детальніше див. у додатку)</em>.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Крім того, з 10 квітня до 10 липня благодійна акція зі збору коштів була <a href=\"http://kyivsobor.ugcc.org.ua/content/view/214/40/\" target=\"_blank\">проведена у мережі супермаркетів &laquo;Рукавичка&raquo;</a>. Голова збіркового відділу при Комітеті сприяння розвитку Патріаршого центру о. Василь Вільчик повідомив, що в такий спосіб було зібрано 12 тис. 984 грн. &laquo;Ми щиро вдячні п. Богдану Козаку, генеральному директорові ТзОВ ТВК &laquo;Львівхолод&raquo; (мережа магазинів &laquo;Рукавичка&raquo;), за розуміння потреби фінансування будівництва Патріаршого собору і за його внесок у цю богоугодну справу&raquo;, &ndash; сказав о. Василь Вільчик.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Будівництво триває, а тому збір коштів продовжується. У вересні розпочинається <a href=\"http://ugcc.org.ua/787.0.html\">другий тур благодійних концертів &laquo;З вірою і любов&lsquo;ю&raquo;</a>, зібрані кошти з якого підуть на завершення будівництва Патріаршого собору.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;Департамент інформації УГКЦ</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Додаток</em></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"font-size: x-small;\">Проміжні підсумки другого етапу акції &laquo;Моя &ldquo;цеголка&rdquo; на будівництво Патріаршого собору&raquo;</span></strong></span></span></p>\r\n<table style=\"border-collapse: collapse; text-align: justify;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td style=\"border: 1pt solid black; padding: 0cm 5.4pt; width: 240.85pt;\" width=\"321\" valign=\"top\">\r\n<p style=\"margin: 0pt;\" align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"font-size: x-small;\">Єпархія/екзархат</span></strong></span></span></p>\r\n</td>\r\n<td style=\"padding: 0cm 5.4pt; width: 237.7pt;\" width=\"317\" valign=\"top\">\r\n<p style=\"margin: 0pt;\" align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"font-size: x-small;\">Переказано на будівництво собору</span></strong></span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 0cm 5.4pt; width: 240.85pt;\" width=\"321\" valign=\"top\">\r\n<p style=\"margin: 0pt;\" align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Львівська архиєпархія</span></span></p>\r\n</td>\r\n<td style=\"padding: 0cm 5.4pt; width: 237.7pt;\" width=\"317\" valign=\"top\">\r\n<p style=\"margin: 0pt;\" align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">131&nbsp;881   грн</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 0cm 5.4pt; width: 240.85pt;\" width=\"321\" valign=\"top\">\r\n<p style=\"margin: 0pt;\" align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Стрийська єпархія</span></span></p>\r\n</td>\r\n<td style=\"padding: 0cm 5.4pt; width: 237.7pt;\" width=\"317\" valign=\"top\">\r\n<p style=\"margin: 0pt;\" align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">5&nbsp;410 грн</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 0cm 5.4pt; width: 240.85pt;\" width=\"321\" valign=\"top\">\r\n<p style=\"margin: 0pt;\" align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Бучацька єпархія</span></span></p>\r\n</td>\r\n<td style=\"padding: 0cm 5.4pt; width: 237.7pt;\" width=\"317\" valign=\"top\">\r\n<p style=\"margin: 0pt;\" align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18&nbsp;000   грн</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 0cm 5.4pt; width: 240.85pt;\" width=\"321\" valign=\"top\">\r\n<p style=\"margin: 0pt;\" align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Донецько-Харківський   екзархат</span></span></p>\r\n</td>\r\n<td style=\"padding: 0cm 5.4pt; width: 237.7pt;\" width=\"317\" valign=\"top\">\r\n<p style=\"margin: 0pt;\" align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">11&nbsp;000   грн</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 0cm 5.4pt; width: 240.85pt;\" width=\"321\" valign=\"top\">\r\n<p style=\"margin: 0pt;\" align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Луцький   екзархат</span></span></p>\r\n</td>\r\n<td style=\"padding: 0cm 5.4pt; width: 237.7pt;\" width=\"317\" valign=\"top\">\r\n<p style=\"margin: 0pt;\" align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">7&nbsp;460 грн</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 0cm 5.4pt; width: 240.85pt;\" width=\"321\" valign=\"top\">\r\n<p style=\"margin: 0pt;\" align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Коломийсько-Чернівецька єпархія</span></span></p>\r\n</td>\r\n<td style=\"padding: 0cm 5.4pt; width: 237.7pt;\" width=\"317\" valign=\"top\">\r\n<p style=\"margin: 0pt;\" align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">40&nbsp;020   грн</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 0cm 5.4pt; width: 240.85pt;\" width=\"321\" valign=\"top\">\r\n<p style=\"margin: 0pt;\" align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Тернопільсько-Зборівська єпархія</span></span></p>\r\n</td>\r\n<td style=\"padding: 0cm 5.4pt; width: 237.7pt;\" width=\"317\" valign=\"top\">\r\n<p style=\"margin: 0pt;\" align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">30&nbsp;000   грн</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 0cm 5.4pt; width: 240.85pt;\" width=\"321\" valign=\"top\">\r\n<p style=\"margin: 0pt;\" align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Київська архиєпархія</span></span></p>\r\n</td>\r\n<td style=\"padding: 0cm 5.4pt; width: 237.7pt;\" width=\"317\" valign=\"top\">\r\n<p style=\"margin: 0pt;\" align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">177&nbsp;480   грн</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 0cm 5.4pt; width: 240.85pt;\" width=\"321\" valign=\"top\">\r\n<p style=\"margin: 0pt;\" align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Івано-Франківська   єпархія</span></span></p>\r\n</td>\r\n<td style=\"padding: 0cm 5.4pt; width: 237.7pt;\" width=\"317\" valign=\"top\">\r\n<p style=\"margin: 0pt;\" align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">139&nbsp;720   грн</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 0cm 5.4pt; width: 240.85pt;\" width=\"321\" valign=\"top\">\r\n<p style=\"margin: 0pt;\" align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Самбірсько-Дрогобицька єпархія</span></span></p>\r\n</td>\r\n<td style=\"padding: 0cm 5.4pt; width: 237.7pt;\" width=\"317\" valign=\"top\">\r\n<p style=\"margin: 0pt;\" align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">64&nbsp;800   грн</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 0cm 5.4pt; width: 240.85pt;\" width=\"321\" valign=\"top\">\r\n<p style=\"margin: 0pt;\" align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"font-size: x-small;\">Всього</span></strong></span></span></p>\r\n</td>\r\n<td style=\"padding: 0cm 5.4pt; width: 237.7pt;\" width=\"317\" valign=\"top\">\r\n<p style=\"margin: 0pt;\" align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"font-size: x-small;\">625 771 грн</span></strong></span></span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>',NULL,NULL,1775,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(846,'P','2008-11-02',1,NULL,'Note','«Незгасима свічка» прибула на Івано-Франківщину','<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\r\n<h3><img style=\"float: left;\" src=\"image,1583,200,golodomor1.gif\" alt=\"\" />&nbsp; Яскрава свічка лине теплим жаром,</h3>\r\n</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\r\n<h3>Стоїть свідоцтвом смерті на Землі.</h3>\r\n</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\r\n<h3>І в темному вікні дзвенять примари,</h3>\r\n</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\r\n<h3>Мов мертві затонулі кораблі.</h3>\r\n</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\r\n<h3>Хіба це люди? Люди це чи звірі?</h3>\r\n</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\r\n<h3>Хто їх довів до цих страшних висот?</h3>\r\n</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\r\n<h3>Хто винен, що їх душі помарнілі</h3>\r\n</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\r\n<h3>Вже душі не людей, а півістот?</h3>\r\n</span></span></p>\r\n<p><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></p>\r\n<p>&nbsp;</p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Зроніть сльозу. Бо ми не мали сліз.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\r\n<p><strong>Заплачте разом, а не наодинці.</strong></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;Зроніть сльозу за тими, хто не зріс,</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;Що мали зватись гордо - УКРАЇНЦІ</strong></span></span></p>\r\n<img style=\"float: right;\" src=\"image,1583,200,pictureview1-pid-210.jpg\" alt=\"\" /> Сьогодні Івано-Франківська область прийняла естафету міжнародної акції &laquo;Незгасима свічка&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Як передає кореспондент УНІАН, опівдні на Вишківському перевалі Карпат офіційна делегація Івано-Франківщини прийняла від Закарпаття естафету акції. Потім вогонь &laquo;Незгасимої свічки&raquo; перевезли до Івано-Франківська, де на Вічевому майдані відбулися заходи в рамках цієї акції.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За участю молоді та громадськості міста пройшла акція &laquo;33 хвилини&raquo;, на якій молоді люди зачитували спогади очевидців Голодомору 1932-1933 років.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Священики відслужили панахиду за жертвами Голодомору. Учасники акції запалили свічки і лампади від &laquo;Незгасимої свічки&raquo; й поклали їх разом із вінками та квітами до пам\'ятників жертв комуністичних репресій у Меморіальному сквері.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Також відбулося траурне віче за участю представників облдержадміністрації, облради та Івано-Франківського міськвиконкому, на якому згадували причини і жертв Голодомору.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Після цих заходів &laquo;Незгасиму свічку&raquo; перевезли до храму Святого Воскресіння УГКЦ, де вона перебуватиме до завтра.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Завтра, З листопада, Прикарпаття передасть естафету акції Тернопільській області.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Довідка УНІАН. Міжнародна акція &bdquo;Незгасима свічка\" розпочалася 1 квітня ц. р. в Австралії і протягом року пройшла у 33 країнах світу, в тому числі у 6 країнах СНД - Білорусі, Казахстані, Молдові, Росії, Вірменії та Грузії.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В Україні акція \"Незгасима свічка\" стартувала 26 жовтня. Після проведення заходів в усіх обласних центрах 21 листопада &bdquo;Незгасиму свічку\" буде доставлено до Києва, де проводитимуться державні заходи з вшанування пам\'яті жертв Голодоморів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: <a class=\"td\" href=\"http://www.unian.net/ukr/news/news-282056.html\">http://www.unian.net/ukr/news/news-282056.html</a></span></span></p>',NULL,NULL,603,5,1,30);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(847,'P','2009-09-12',0,NULL,'Note','9 - 12 жовтня 2009 року Всенародна проща до собору Святої Софії в Римі  ','<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"border: 0px solid black; margin: 5px; width: 168px; height: 168px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_sofiya_prosha.jpg.jpg\" alt=\"\" />&laquo;Цей храм (Святої Софії - Божої Премудрості у Римі, &ndash; авт.) прикрашений за найкращими взорами українського іконопису. У цей храм, в день його посвячення 28 вересня 1969 року, Святіший Отець Павло VI приніс мощі святого Климента Папи, що їх у свій час привезли до Риму апостоли слов\'ян святі Кирило і Методій. Цей храм став духовним осередком для тисяч наших людей, яких доля вигнала з рідної землі, і які проживали в різних країнах вільної Європи та за океанами. Пам\'ять про торжественне посвячення, в якому взяло участь кілька тисяч вірних нашої Церкви, та про її будівничого Патріарха Йосифа (Сліпого) є нагодою, з якої користаємо сьогодні, щоб згадати тих, які своєю працею свідчили про живучість нашої Церкви у часі її жорстоких переслідувань&raquo;, &ndash; написав Блаженніший Любомир, Глава УГКЦ</span></span></p>','<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"border: 0px solid black; margin: 5px; width: 168px; height: 168px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_sofiya_prosha.jpg.jpg\" alt=\"\" />&laquo;Цей храм (Святої Софії - Божої Премудрості у Римі, &ndash; авт.) прикрашений за найкращими взорами українського іконопису. У цей храм, в день його посвячення 28 вересня 1969 року, Святіший Отець Павло VI приніс мощі святого Климента Папи, що їх у свій час привезли до Риму апостоли слов\'ян святі Кирило і Методій. Цей храм став духовним осередком для тисяч наших людей, яких доля вигнала з рідної землі, і які проживали в різних країнах вільної Європи та за океанами. Пам\'ять про торжественне посвячення, в якому взяло участь кілька тисяч вірних нашої Церкви, та про її будівничого Патріарха Йосифа (Сліпого) є нагодою, з якої користаємо сьогодні, щоб згадати тих, які своєю працею свідчили про живучість нашої Церкви у часі її жорстоких переслідувань&raquo;, &ndash; написав Блаженніший Любомир, Глава УГКЦ.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Проща, яка відбудеться з нагоди 40-ліття посвячення собору Святої Софії, буде присвячена також іншим ювілейним датам, які УГКЦ відзначатиме восени: 50-ліття посвячення нинішньої Папської колегії св. Покрови (тодішньої Малої семінарії); 65-ту річницю смерті Митрополита Андрея (Шептицького); 25-ту річницю смерті Патріарха Йосифа (Сліпого).</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;\"Премудрість збудувала собі дім\" (Пр. 9.1) &ndash; таке було гасло Патріарха Йосифа (Сліпого), який побудував собор Святої Софії, приміщення Українського католицького університету в Римі, повернув у власність УГКЦ Прочанський дім на площі Мадонна деі Монті в центральній частині Риму, заснував Товариство &laquo;Свята Софія&raquo;. Вшановуючи працю третьої хвилі еміграції, думаємо про сьогоднішню еміграцію. &laquo;Зібрати в розсіяні сущих&raquo; залишається завданням організаторів Всенародної прощі&raquo;, &ndash; пояснюють у своєму листі Владика Діонісій (Ляхович), Апостольський Візитатор для українців греко-католиків в Італії та Іспанії, та о. д-р. Іван Дацько, голова Всенародної прощі до собору Святої Софії в Римі 2009.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Детальну інформацію про прощу можна знайти на сайті: <a href=\"http://www.ucu.edu.ua/ukr/academics/ucu.rym/proshcha\" target=\"_blank\">www.ucu.edu.ua/ukr/academics/ucu.rym/proshcha</a>.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Зареєструвати прочанські групи, а також звертатися із запитаннями можна до таких осіб:</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>в Україні</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Оксана Рибак<br /> Тел.:8 050 93 77&nbsp;276<br /> Ел. пошта: <a href=\"mailto:proshcha09@gmail.com\">proshcha09@gmail.com</a></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>в Італії</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Марія Говгера <br /> Тел.: +39 06 474 40 68, факс: + 39 06 487 39 03<br /> Ел. пошта: <a href=\"mailto:vserodnepalomn2009@gmail.com\">vsenarodnepalomn2009@gmail.com</a></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\">&nbsp;</p>\r\n<blockquote style=\"margin-bottom: 0pt; margin-top: 0pt;\">\r\n<p style=\"margin: 0pt; text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"font-size: small;\">Звернення Блаженнішого Любомира, <br />Верховного Архиєпископа Києво-Галицького, <br />з нагоди Всенародної прощі до собору Святої Софії в Римі, <br />9-12 жовтня 2009 р.</span></strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Високопреосвященні та Преосвященні Владики! <br /> Всечесніші і Високопреподобні отці! Преподобні монахи і монахині! <br /> Дорогі в Христі брати і сестри! <br /> Мир Вам у Господі!</span></span></p>\r\n<p style=\"margin: 0pt; text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Премудрість збудувала собі дім (Пр. 9,1)</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорогі у Христі!</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Від ранніх століть християнства важливою частиною християнського життя були прощі. Побожний нарід бажав відвідувати Святу Землю - ті місцевості, які своєю присутністю освятив сам Воплочений Син Божий, Господь наш Ісус Христос. Свята Земля завжди була першою ціллю для паломників з усіх закутків земної кулі. Побожні люди не жалували труду і часу, щоб, побувавши на тих Господом освячених місцях, набрати наснаги для свого християнського життя. З бігом часу поставали й інші паломницькі осередки. Були це місця в різних країнах світу, де відбувалися чуда, де жили великі святці, де Божа благодать в особливий спосіб спливала через чудотворні ікони. Люди радо відвідували місця, які мали особливу релігійну вартість в житті їхнього народу чи їхньої спільноти. До таких місць належить собор Святої Софії - Божої Премудрості у місті Римі.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Рим від першого століття християнства був центром Вселенської Церкви. Там мученицькою смертю закінчили своє життя верховні апостоли Петро і Павло. Рим став осідком наступників святого апостола Петра, якому Ісус Христос доручив бути першим, щоб єднати християн та скріплювати їх у святій вірі, доручив очолювати Церкву, яка, за словами св. Кипріяна, предсідає в любові.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Земля, яка сьогодні окреслює Україну, вже від самих початків мала живий контакт із християнським Римом. Тут, на березі Чорного моря мучеництвом закінчив своє земне життя четвертий з ряду наступник святого апостола Петра - святий Климент Папа Римський. Тут також загинув ісповідник віри Папа Мартин І. Коли Київська Русь прийняла християнську віру за часів святого рівноапостольного князя Володимира Великого, контакти з Римом на різних рівнях помножилися. Доказом цього є чисельні відвідини та листування церковних достойників. Проте, під впливом Візантії, яка в XI столітті офіційно перервала сопричастя з Римською Церквою, послабилися також і зв\'язки Києва з Римом аж до тієї міри, що при кінці XVI століття єпископи Київської Митрополії відчули потребу ефективно об\'єднатися з Римським Апостольським Престолом і зробили це офіційним актом порозуміння. Через невідрадні політичні обставини та зазіхання сусідів не всі прийняли оту поновлену злуку, названу Берестейською унією. її нищили, переслідували та зневажали аж до наших часів; старалися знівечити наше сопричастя з Римським Апостольським Престолом, проте, поважна частина нашого народу залишилася вірною тому актові порозуміння.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Рим, його Церква під проводом свого єпископа - наступника святого апостола Петра завжди залишалася символом і знаком єдності Вселенської Церкви. Розуміння цього проявлялося також в тісних зв\'язках з Римом та його церковними установами. Тут був притулок для паломників з Києва. Рим часто відвідували Владики нашої Церкви, рівно ж багато наших богословів студіювали в різних папських університетах, тому тут при кінці XIX століття засновано семінарію, яка діє до сьогодні. Тут знайшли свій осідок центральні уряди монаших Чинів.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За старанням бл. п. Папи Івана XXIII у 1963 році до Риму прибув визволений з радянської неволі Митрополит Иосиф (Сліпий). Саме тут він постарався, щоб заіснували церковні та наукові осередки, існування яких на рідних землях було насильно перервано. Між тими його заходами найвеличавішим було спорудження собору Святої Софії - Божої Премудрості. Цей храм прикрашений за найкращими взорами українського іконопису. У цей храм, в день його посвячення 28 вересня 1969 року, Святіший Отець Павло VI приніс мощі святого Климента Папи, що їх у свій час привезли до Риму апостоли слов\'ян святі Кирило і Методій. Цей храм став духовним осередком для тисяч наших людей, яких доля вигнала з рідної землі, і які проживали в різних країнах вільної Європи та за океанами. Пам\'ять про торжественне посвячення, в якому взяло участь кілька тисяч вірних нашої Церкви, та про її будівничого Патріарха Йосифа (Сліпого) є нагодою, з якої користаємо сьогодні, щоб згадати тих, які своєю працею свідчили про живучість нашої Церкви у часі її жорстоких переслідувань.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Восени минає 40-ліття посвячення собору Святої Софії, 25-та річниця смерті його будівничого Блаженнішого Патріарха Йосифа, 65-та річниця смерті його видатного попередника Слуги Божого Митрополита Андрея (Шептицького) та 50-ліття посвячення приміщень малої семінарії, яку Апостольська Столиця збудувала для наших учнів, і з якої вийшло понад п\'ятсот випускників, а між ними майже сто священиків, що разом стали духовними і громадськими провідниками українських поселень в Західній Європі. Сьогодні там проживають і навчаються наші численні студенти з України.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Отож закликаємо вірних нашої Церкви - Владик, священнослужителів, членів монаших чинів та згромаджень, мирян з України та поселень, але головно тих, які проживають в різних країнах західної Європи, численно прибути до Риму, щоб духовно збагатитися та скористати з духовних благ, питомих для паломництва, і взяти участь у прощі, яка відбудеться 9-12 жовтня 2009 року.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Заохочуємо Високопреосвященних і Преосвященних Владик, священнослужителів, монашество та мирян скористатися з цієї нагоди духовного оновлення.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: right;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Благословення Господнє на Вас,</span></span></p>\r\n<p style=\"margin: 0pt; text-align: right;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">+ Любомир</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дано у Києві, при Патріаршому соборі Воскресіння Христового, 31 серпня 2009 року</span></span></p>\r\n</blockquote>',NULL,NULL,1779,5,1,47);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(848,'P','2009-09-09',1,NULL,'Note','Загальна аудієнція з Папою Венедиктом XVI ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Дорогі брати та сестри, приклад святого П&rsquo;єра Даміані спонукає також і нас до того, щоб завжди дивитись на хрест, як на найбільший акт любові Бога стосовно людини, який подарував нам спасіння&raquo;, &ndash; мовив Святіший Отець.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">9 вересня 2009 р., як і щосереди, Святіший Отець зустрівся із великою кількістю вірних, які у цей день зібрались у ватиканському залі Папи Павла VI, щоб почути повчальне слово Пастиря Вселенської Церкви, помолитись із ним Богородичну молитву &laquo;Ангел Господній&raquo; та отримати його Апостольське благословення. Протягом останніх загальних аудієнцій Папа Венедикт ХVІ заохочував вірних звернути увагу на приклад великих постатей, які мала Христова Церква. Цього разу Святіший Отець говорив про монаха з ХІ століття, а саме, про святого П&rsquo;єра, тобто Петра Даміані. <br /><br />За словами Вселенського Архиєрея, той святець любив самотність, і, водночас, був відважним церковним діячем. Святий П&rsquo;єр народився 1007 року в італійському місті Равенна, яке розташоване на відстані приблизно 300 км на північний-схід від Риму. В ранньому віці втратив батьків, тому його вихованням зайнялись старші сестра Розелінда та брат Даміано, який його усиновив. Саме з цієї причини згодом його почали називати П&rsquo;єр Даміані. Освіту здобував у сусідньому місті Фаенца, а потім у Пармі. Будучи добрим спеціалістом у сфері права, П&rsquo;єр відзначався також своїми здібностями до писання, його вважають одним із найбільших письменників свого часу, як також, знавцем творчості великих латинських класиків. Вразливість на красу спонукала П&rsquo;єра до поетичного споглядання світу та Бога, що привело до того, що у 1034 році він вирішив віддалитись від світу і вступити у монастир Фонте Авеллана, який лише кілька десятиліть тому заснував святий Ромуальд з Равенни, але швидко став дуже відомим через строгість життя, якій піддавались тамтешні монахи. Серед всіх таїнств християнства, монаха-ереміта П&rsquo;єра надзвичайно захоплювало таїнство хреста, він одного разу сказав: &laquo;Не любить Христа той, хто не любить хреста Христового&raquo;. Численні проповіді написав монах Даміані на тему хреста, у яких виявив своє розуміння цього таїнства.<br /><br /><br />&laquo;Дорогі брати та сестри, приклад святого П&rsquo;єра Даміані спонукає також і нас до того, щоб завжди дивитись на хрест, як на найбільший акт любові Бога стосовно людини, який подарував нам спасіння&raquo;, &ndash; мовив Святіший Отець. <br /><br />Далі Єпископ Риму зазначив, що протягом свого еремітського життя, святий П&rsquo;єр уклав монаші Правила, в яких сильно підкреслив строгість цього життя, наголошуючи на монастирській тиші, денній та нічній молитві, довгих постах, на братній любові до співбратів та послусі своєму настоятелю.<br /><br /><br />За словами Папи, вивченням і роздумуванням над Святим Письмом святий монах з Равенни відкрив містичні значення Божого слова, знаходячи у них поживу для свого духовного життя. Саме тому святий П&rsquo;єр називає монашу келію &laquo;розмовницею, де Бог розмовляє з людьми&raquo;. Для нього еремітське життя було вершиною християнського життя, оскільки монах є вільним від зв&rsquo;язків із світом і власним &laquo;я&raquo;, отримує &laquo;завдаток Святого Духа і його душа легко єднається з небесним Нареченим&raquo;. &laquo;Сьогодні це є важливим також і для нас, навіть якщо ми не є монахами: вміти створити тишу в нас, щоб слухати Божий голос, шукати, так би мовити, &laquo;розмовниці&raquo;, де Бог говорить з нами: розуміти Боже Слово в молитві і медитації є дорогою життя&raquo;, &ndash; наголосив Венедикт ХVІ. <br /><br />Після того Святіший Отець підкреслив, що П&rsquo;єр Даміані був людиною молитви, медитації, споглядання, а також і богословом. Як наголосив Вселенський Архиєрей, сопричастя з Христом творить єдність любові між християнами, додаючи, що над темою сопричастя святий П&rsquo;єр роздумував у своєму 28 листі, який можна назвати геніальним трактатом з екклезіології. П&rsquo;єр Даміані добре знав, що ідеал Святої Церкви, який він мав, не відповідав дійсності того часу. А тому не вагався говорити про зіпсованість монашества та духовенства, яким у цьому сприяла світська влада. Бажання виправити цю ситуацію, примусило П&rsquo;єра Даміані у 1057 році, з великим болем і сумом, залишити монастир і прийняти призначення на Кардинала-Єпископа Остії, входячи, таким чином, у гроно найближчих співробітників Папи у церковній реформі. Після 10-и років він отримав дозвіл повернутись до монастиря у Фонте Авеллана, однак, і надалі йому доводилось виконувати важливі церковні доручення, що вимагало від монаха час-до-часу залишати свій монастир і подорожувати. А повертаючись з Монтекассіно до Равенни, П&rsquo;єр несподівано захворів, тому був змушений зупинитись у місті Фаенца, в бенедектинському монастирі, де і помер вночі з 22 на 23 лютого 1072 року. <br /><br />На закінчення Папа зазначив, що є великою ласкою від Бога те, що у житті Церкви були такі великі і духовно багаті особистості, як П&rsquo;єр Даміані, який не зважаючи ні на що, залишився монахом до кінця, посвятившись також реформі Церкви. Своїм життям він дав нам свідчення того, що Бог завжди повинен бути для нас на першому місці.</span></span></p>',NULL,NULL,1786,5,1,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(849,'P','2008-11-02',1,NULL,'Rss','Д-р Тарас Шманько. \"Як поводитися у храмі\"','Д-р Тарас Шманько. \"Як поводитися у храмі\"\n<p align=\"justify\"><strong>Храм</strong></p>\n<p align=\"justify\"><img style=\"margin: 5px; float: left;\" src=\"http://charnetskyj.org.ua/img/b1-8.jpg\" height=\"364\" width=\"500\" /></p>\n<p align=\"justify\">Спільнотні богослуження відбуваються у спеціяльних приміщеннях - храмах, або по-іншому - церквах. Первісні християни збиралися на молитву в приватних будівлях, але швидко з\'явилися окремі приміщення, призначені лише для богослужень. Суттєві елементи християнських церков перейнято з юдейської традиції, однак упродовж століть облаштування храму зазнавало деяких змін, доки набуло звичного для нас вигляду.</p>\n<p align=\"justify\">\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=87:d-r-taras-shmanko-qjak-povodytysja-u-hramiq&catid=30:hrystyjanske-vchennia-cat&Itemid=37',604,NULL,1,26),
(850,'P','2008-11-02',1,NULL,'Note','Зустріч президента України з архиєпископом Мечиславом Мокшицьким','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Під час розмови архиєпископ Мокшицький зокрема подякував президентові за вітального листа у зв\'язку з призначенням митрополитом та запросив пана Ющенка на свій інгрес, тобто урочисте введення на митрополичий престол, до львівської римо-католицької базиліки Успіння Пресвятої Діви Марії.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1 листопада 2008 р. у Львові під час урочистостей з нагоди 90-ліття Західно-Української Народної Республіки відбулася зустріч президента України Віктора Ющенка з архиєпископом Мечиславом Мокшицьким, львівським митрополитом латинського обряду. Під час розмови архиєпископ Мокшицький зокрема подякував президентові за вітального листа у зв\'язку з призначенням митрополитом та запросив пана Ющенка на свій інгрес, тобто урочисте введення на митрополичий престол, до львівської римо-католицької базиліки Успіння Пресвятої Діви Марії. Президент України Віктор Ющенко передав щире привітання для кардинала Мар\'яна Яворського та ще раз поздоровив владику Мечислава Мокшицького, якого Святіший Отець Венедикт XVI підніс до гідності нового львівського римо-католицького митрополита.<br /><br />(для Радіо Ватикану,  Костянтин Чавага)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,NULL,605,5,1,42),
(851,'P','2008-11-03',1,NULL,'Rss','Вечір памя\'ті Андрея Шептицького','<p><strong>Перший день Суспільного тижня завершився Вечором пам\'яті митрополита Андрея Шептицького, який підготували члени організації \"Обнова-Київ\". </strong></p>\n<p>Вони зробили сучасну молодіжну інтепретацію життєвих поглядів митрополита Андрея. Презентація відбувалася у формі підготовки вистави про митрополита, в якій кожен із учасників мав своє бачення Шептицького.</p>\n<p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=88:vechir-pamjati-andreja-sheptyckogo&catid=27:gk-v-ukrajini-cat&Itemid=57',607,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(852,'P','2009-09-13',1,NULL,'Note','В УГКЦ провели семінар для викладачів місіології та євангелізації  ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: book antiqua,palatino;\">Спираючись на історичний досвід та еклезіологічну специфіку УГКЦ, о. д-р Мирон Бендик, ректор Дрогобицької духовної семінарії, представив присутнім бачення особливостей її місійного служіння. Доповідач переконливо продемонстрував, що УГКЦ, як Церква з тисячолітньою історією, покликана представити світові власні розв\'язки багатьох сучасних викликів та проблем.</span></span></p>','<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"border: 1px solid black; margin: 5px; width: 227px; height: 170px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_misiologia.jpg.jpg\" alt=\"\" />&laquo;Ми зібрались на цьому семінарі для того, щоб обговорити конкретні пропозиції, а також випрацювати наукову базу, яка б полегшила викладання предмету &laquo;Євангелізація і місіологія&raquo; в наших навчальних закладах, &ndash; зазначив Владика Ярослав (Приріз), виконуючий обов\'язки Голови Пасторально-місійного відділу Патріаршої курії УГКЦ. &ndash; Адже для того, щоб розбудити євангелізаційно-місійний дух в УГКЦ, слід якісно посилити академічну програму виховання, в першу чергу, духовенства, монашества і мирян&raquo;. На думку Владики Ярослава той факт, що понад 100 років тому вперше було поставлено єпископа поза межами нашої Церкви, а саме Преосвященного Владику Сотера (Ортинського) Єпархом Філадельфійським у США, окреслив нову місію УГКЦ &ndash; свідчити Христа &laquo;аж до краю землі&raquo;.</span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Семінар для викладачів місіології та євангелізації в семінаріях та монаших спільнотах УГКЦ проходив з 2 по 5 вересня у м. Трускавці у приміщенні Управління Самбірсько-Дрогобицької єпархії УГКЦ. Цей захід проводився з благословення Глави УГКЦ Блаженнішого Любомира та був організований Пасторально-місійним відділом Патріаршої курії УГКЦ як черговий етап, спрямований на інтенсифікацію місійної діяльності нашої Церкви.</span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Упродовж 4 днів представники 4 семінарій, 4 монаших спільнот і Українського католицького університету, завдяки насиченій програмі семінару, мали нагоду поглибити свої знання у галузі місіології та поділитись досвідом викладання цієї дисципліни у власних навчальних закладах.</span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Головним лектором був о. Жанфранческо Колцані, професор факультету місіології Папського урбаніанського університету, який представив основну проблематику та перспективи розвитку сучасної місіології. Зокрема, професор зосередив свою увагу на біблійних підставах місіології, проаналізував головні етапи розвитку місійної діяльності Церкви та представив її богословський контекст. Велику зацікавленість учасників семінару викликали його розповіді про сучасний стан місій у різних країнах світу.</span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Спираючись на історичний досвід та еклезіологічну специфіку УГКЦ, о. д-р Мирон Бендик, ректор Дрогобицької духовної семінарії, представив присутнім бачення особливостей її місійного служіння. Доповідач переконливо продемонстрував, що УГКЦ, як Церква з тисячолітньою історією, покликана представити світові власні розв\'язки багатьох сучасних викликів та проблем.</span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Представники Чину Найсвятішого Ізбавителя (ЧНІ) і Чину Святого Василія Великого (ЧСВВ) поділилися з присутніми досвідом євангелізаційної діяльності власних монаших спільнот серед вірних УГКЦ. У цих виступах безпосередньо було заторкнуто практичніх аспекти місійної діяльності.</span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Семінар для викладачів місіології та євангелізації став нагодою обговорити актуальний стан викладання цих дисциплін у навчальних закладах УГКЦ. Учасники відзначали, що для пробудження місійного духу в УГКЦ, слід якісно посилити академічну програму формації, в першу чергу, духовенства. У цьому контексті відбулося обговорення конкретних пропозицій, а також наголошено на необхідності випрацювання серйозної наукової бази, яка б полегшила викладання предмету &laquo;Особливості місіології УГКЦ&raquo;.</span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З огляду на те, яке зацікавлення викликав формат та зміст семінару, його учасники зійшлись на думці, що практику подібних зустрічей потрібно продовжити.</span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\">о. Олег Чупа,<br /> прес-секретар Самбірсько-Дрогобицької єпархії</span></p>',NULL,NULL,1798,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(853,'P','2009-09-24',1,NULL,'Note','Відійшов по вічну нагороду Владика Мирослав (Марусин)  ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">21 вересня 2009 року, у празник Різдва Пресвятої Богородиці за юліянським календарем, о 9:30 місцевого часу, у Римі, в українській Папській колегії святого Йосафата, на 86-му році, життя упокоївся у Бозі Високопреосвященніший Архиєпископ Мирослав Марусин, вислужений Секретар Конґреґації для Східних Церков.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3288,200,S.Giosafat.jpg\" alt=\"\" />21 вересня 2009 року, у празник Різдва Пресвятої Богородиці за юліянським календарем, о 9:30 місцевого часу, у Римі, в українській Папській колегії святого Йосафата, на 86-му році, життя упокоївся у Бозі Високопреосвященніший Архиєпископ Мирослав Марусин, вислужений Секретар Конґреґації для Східних Церков. Він відійшов до Господа у той сам день, що і 35 років раніше, 1974 року, Владика Іван Бучко, його святитель від нижчих до єпископських свячень, секретарем, співробітником і помічником якого він був протягом 25-ти років.<br /><br />Владика Мирослав Марусин народився 26 червня 1924 року у селі Княжому біля Золочева на Львівщині. Богословсько-філософські студії розпочав у Львівській Богословській Академії, де навчався від 1943 р. до 1944 р. Під час війни навчався в Падерборні, (Німеччина), а потім, від 1949 до 1951 р., у Папському Східному Інституті в Римі, здобувши ступінь доктора. Нижчі, дияконські та ієрейські свячення отримав з рук Архиєпископа Івана Бучка у 1948 р. в Німеччині. Тісно співпрацював з Владикою Бучком, Апостольським Візитатором українців греко-католиків у Західній Європі, невтомно йому допомагав, супроводжував у всіх відвідинах українських громад у світі, аж до свого призначення Апостольським Візитатором українців у Західній Європі. Єпископська хіротонія Кир Мирослава відбулась 28 липня 1974 року в Римі. Головним святителем був Владика Іван Бучко, а співсвятителями &ndash; Владики Платон Корниляк, Володимир Маланчук, Августин Горняк та Йоаким Сеґеді. <br />Від 1977 до 1982 року Владика Марусин був на відповідальному пості віце-президента Папської комісії у справах ревізії Кодексу Східного Канонічного Права. За призначенням Папи Івана Павла ІІ, від 1982 до 2001 року Преосвященний Мирослав Марусин, піднесений до гідності архиєпископа, був Секретарем Конґреґації для Східних Церков. Його архиєрейське служіння було віддане Церкві та народові, а, одночасно, і служінню чотирьом Вселенським Архиєреям: Павлові VI, Іванові Павлові I, Івану Павлові II та теперішньому Папі, Венедикту XVI.<br />Кир Мирослав Марусин був автором численних видань і наукових праць богословського, історичного, літургічного та біблійного змісту. Папська Марійська академія, Орден Лицарів Божого Гробу, Наукове товариство ім. Шевченка та інші установи удостоїли владику Мирослава Марусина почесними відзнаками. Віденський університет та Український вільний університет присвоїли йому звання почесного доктора.<br /><br />Завдяки проповідям Владики Мирослава Марусин та Богослуженням, що він відправляв і які транслювало Ватиканське Радіо, виросло та зберегло віру не одне покоління греко-католиків не тільки в Україні, але і на всьому радянському просторі. Свою останню проповідь для слухачів Ватиканського Радіо Владика Мирослав виголосив 28 серпня цього року, на празник Успіння Пресвятої Богородиці.<br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #153f6b; font-style: normal; font-variant: normal; font-weight: bold; line-height: normal; font-size-adjust: none; font-stretch: normal;\">24 вересня 2009 року похорон Владики Мирослава Марусина<br /></span>За повідомленням Прес-служби УГКЦ в Італії заупокійна Свята Літургія за світлої пам\'яті Кир Мирослава Марусина та Чин Архиєрейського Похорону будуть відправлені у четвер, 24 вересня, о 10-ій годині ранку в соборі Святої&nbsp; Софії в Римі.<br /><br />Чин погребення новопреставленого Архиєрея Мирослава буде звершено на міському кладовищі &bdquo;Пріма порта&rdquo; в Римі. Чин Похорону звершить Архиєпископ Циріл Васіль, ТІ, Секретар Конгрегації для Східних Церков, у співслужінні з уповноваженим від імені Глави УГКЦ, Блаженнішого Любомира Гузара,&nbsp; Владикою Діонісієм Ляховичем, ЧСВВ, Апостольським Візитатором для українців в Італії та Іспанії. На Чин Архиєрейського Похорону також прибуде Кардинал Леонардо Сандрі, Префект Конгрегації для Східних Церков.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У церкві св. Миколая Чудотворця на Аскольдовій Могилі було відслужено панахиду за упокій душі Владики Мирослава Марусина. Хай його душа отримає вічну нагороду за правдиве служіння Христу та Богу.<br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вічна йому пам&rsquo;ять! <br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пропонуємо Вашій увазі духовний заповіт Владики Мирослава Марусина, який відійшов до Господа у понеділок 21 вересня 2009 року:  <span style=\"text-decoration: underline;\"><a href=\"http://62.77.60.84/audio/ra/00178893.RM\"><img src=\"http://www.radiovaticana.org/ucr/img/listen.gif\" border=\"0\" alt=\"\" /></a> </span></span></span></p>',NULL,NULL,1805,5,1,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(854,'P','2009-10-10',0,NULL,'Note','«Спільнота «Діти Божого Милосердя» ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Парафіяльна служба милосердя церкви св. Миколая Чудотворця на Аскольдовій Могилі продовжує роботу над благодійними проектами. Закликаємо і вас бути небайдужими.&nbsp; Долучайтеся разом у Христі творити добро ближньому свому.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Завдання проекту: </strong>Долаючи дитячу недовіру, нервозність, апатію, відчуття непотрібності огорнути їх увагою і добротою; вкласти в серце дитини віру, любов до матері, Батьківщини, рідної мови; сприяти в навчанні і здобутті фаху; по можливості допомога працевлаштуванням.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Проект<strong>: &laquo;Спільнота &laquo;Діти Божого Милосердя&raquo; </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Виконавець</strong>: Парафія УГКЦ Св. Миколая на Аскольдовій могилі у м. Києві</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Мета проекту:</strong> Соціальна реабілітація бездоглядних дітей і їхнє залучення до здорового способу життя через навчання, працю і молитву</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Цільова група: 20 осіб</strong></span></span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">соціальні сироти      - 2</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">напівсироти      - 2</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">діти з      неповних сімей - 6</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">діти з      кризових родин, бездоглядні &ndash; 10</span></span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Завдання проекту: </strong>Долаючи дитячу недовіру, нервозність, апатію, відчуття непотрібності огорнути їх увагою і добротою; вкласти в серце дитини віру, любов до матері, Батьківщини, рідної мови; сприяти в навчанні і здобутті фаху; по можливості допомога працевлаштуванням.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Опис проекту:</strong> Діти, які займаються в державних інтернатах, мають щоденне харчування протягом навчальних днів, а бездоглядні діти, як правило, протягом тижня отримують обіди в благодійних організаціях (їдальнях). Наш проект дає можливість &nbsp;харчування потребуючих дітей у вихідні дні. Таким чином ми убезпечимо дітей від&nbsp; блукань у пошуку харчів та пригод, що могло би привести їх під небезпечний вплив вулиці.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Гарячі обіди в церковну хатину привозить повар. Діти збираються в п\'ятницю після обіду, а в суботу і неділю &ndash; на цілий день. Протягом всього часу перебування дітей при церкві разом з ними є вихователь, сестра-монахиня, вчителі, священик або волонтери з числа церковної молоді і парафіян. Заняття з дітьми починаються молитвою і обідом, потім навчання і відпочинок, спілкування, ігри. В неділю &ndash; участь у Службі Божій.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Соціальна допомога:</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Харчування. Закупівля продуктів, питної води. 20осіб х 20 грн х 12днів х10 місяців = 4&nbsp;800 грн</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Гуманітарний одяг (збірка в церкві, благодійні фонди)</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Медогляд &ndash; пошук доброчинців</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Стоматологічне лікування. 20осіб х 100 грн =&nbsp; 2&nbsp;000 грн</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Придбання учнівських проїздних квитків. 10осіб х 15 грн х 9 міс = 1&nbsp;350 грн</span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Навчання: Мета: </strong>Дати дітям уяву про деякі професії. Заохотити до праці через мистецтво. Виявити таланти</span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Катехизація</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Малювання, петриківський розпис. Витратні матеріали - 800 грн</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ліпка з глини. Витратні матеріали - 500 грн</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Різдвяна свічка &ndash; оздоблення. Організація акції розповсюдження свічки серед парафіян. Кошти використати на приготування святвечірнього столу для дітей. 800 грн</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Писанкарство &ndash; втілення таланту і дитячих фантазій. Організація аукціону &laquo;Подаруй ми писаночку&raquo;. Варіанти&nbsp; використання зібраних коштів обговорити з дітьми. 800 грн</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Залучення до церковного дитячого вертепу. Веснянки і гаївки. Участь дітей в концертах</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сувенірна відкритка. Реалізація за пожертву в церковній лавці. Витратні матеріали &ndash; збірка в церкві</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Проведення тренінгів професійною тренеркою з метою надання інформації та навчання дітей нести відповідальність за свої вчинки&nbsp; та т.ін. </span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Придбання церковної і дитячої літератури &ndash; внесок церкви</span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Спортивний відпочинок:</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Встановлення турніка. 2000 грн</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Придбання спортивного інвентаря:&nbsp; теніс, м&rsquo;ячі, шахи, настільні ігри</span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Праця.</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Прибирання території парку. Придбання інвентаря для прибирання. 500 грн</span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Проща. </strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Організувати за кошти церкви участь дітей в прощі в Зарваницю весною 2010 року (два тижні перед Великоднем). </span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Екскурсії.</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Організувати екскурсію в парк відпочинку Софіївка (або на страусину ферму). 1500 грн</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Забезпечити відвідування музею Тараса Шевченка, музею українського мистецтва</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В церковній хатині організувати перегляд фільмів про життя Христа, мтрп. Шептицького, Шевченка, героїв України</span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Персонал</strong>: Вихователь (керівник проекту), повар, писанкарка, художник, вчитель музики, волонтери&nbsp; &ndash; внесок організації</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Моніторинг </strong>Вихователь (керівник проекту) контролює присутність та поведінку дітей протягом всього часу перебування дітей при церкві. Успіхи та потреби дітей, відгуки вчителів, а також інтерв&rsquo;ю з дітьми періодично висвітлюватиметься на вебсторінці &nbsp;Аскольдової могили.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Для звітування перед благочинцями, а також для залучення коштів на наступний період буде змонтований 5-ти хвилинний відеоролик&nbsp; про життя в дитячій спільноті.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Бюджет проекту. 15 000 грн</strong></span></span></p>',NULL,NULL,1806,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(855,'P','2008-11-03',1,NULL,'Note','Чому ви нас так любите?','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Ви їдете до людей, везете дарунки і хочете з ними поспілкуватися. Поводьтеся так, щоб на завершення вашого там перебування в серцях, очах і устах тих людей, яких ви зустрінете, постало питання: Чому ви нас так любите? Думаю, що якщо ви відчуєте таку реакцію, то ваша подорож буде надзвичайно успішною&raquo;, - сказав у своєму напутньому слові Блаженніший Любомир.&nbsp;</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У неділю, 2 листопада, в рамках Суспільного тижня 2008 року, у Києві було проведено Акцію солідарності. Чотири групи вірних УГКЦ, які складалися з священиків, членів Федерації товариств &laquo;Обнова&raquo; з Києва, Чернівців, Івано-Франківська, Львова та Тернополя, членів мирянської організації &laquo;Матері в молитві&raquo;, парафіян київських парафій УГКЦ, хорових колективів паралельно відвідали дитячий будинок, інтернат для неповносправних осіб, інтернат для людей похилого віку та ув\'язнених у Лук\'янівському СІЗО.&nbsp;<br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У кожному з пунктів програма була однаковою: концерт, спілкування та роздавання подарунків. Частину дарунків було зібрано у парафіях УГКЦ, а частину купив оргкомітет. Найголовнішим серед пунктів програми, за словами організаторів, спілкування, яке має полягати у готовності вислухати. На завершення Акції солідарності учасники чотирьох груп повернулися до відправної точки: Патріаршого собору УГКЦ, де мали нагоду обмінятися враженнями від побаченого та обговорити можливі заходи на майбутнє задля виявлення постійної солідарності з потребуючими людьми.&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> &laquo;Ви їдете до людей, везете дарунки і хочете з ними поспілкуватися. Поводьтеся так, щоб на завершення вашого там перебування в серцях, очах і устах тих людей, яких ви зустрінете, постало питання: Чому ви нас так любите? Думаю, що якщо ви відчуєте таку реакцію, то ваша подорож буде надзвичайно успішною&raquo;, - сказав у своєму напутньому слові Блаженніший Любомир.&nbsp;<br /><br />Перед початком Акції солідарності у храмі Благовіщеня Пресвятої Богородиці Блаженніший Любомир відслужив Божественну Літургію та виголосив проповідь про потребу солідарності із потребуючими. &laquo;Ми хворіємо, старіємося, потрапляємо у різні обставини, у яких нам доводиться багато терпіти. І ми питаємо і за себе, і за інших: Чому це все? Чи не може Господь Бог це все направити?&raquo;, - сказав у проповіді Предстоятель УГКЦ. На думку проповідника, ми достеменно, остаточно не знаємо, чому Господь допускає терпіння: &laquo;Ми знаємо, що терпіння є наслідком гріха. Тому передусім маємо старатися не грішити. Але також можемо припустити (на прикладі євангельської історії про сліпородженого, - авт.), що Господь Бог допускає, щоб були серед нас потребуючі, щоб нас навчати бути співчутливими і надавати їм допомогу&raquo;.&nbsp;<br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,1586,200,RTEmagicC_s_t3.jpg.jpg\" alt=\"\" />1 - 5 листопада у Києві Українська Греко-Католицька Церква проводить Суспільний тиждень. Його тема: \"На шляху до солідарності українського суспільства\". Сьогодні, 3 листопада, в рамках Суспільного тижня відбудеться публічний діалог та дебати у прямому ефірі щодо прав і свобод у міжрелігійних та суспільних відносинах. До участі у дебатах, які розпочнуться о 19.30 на ТРК &laquo;Україна&raquo; у програмі &laquo;Шустер. Live&raquo;, запрошені Блаженніший Любомир, головний рабин Києва й України Яків Дов Блайх та голова Духовного управління мусульман України Ахмед Тамім.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>\r\n<p>&nbsp;</p>',NULL,NULL,606,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(856,'P','2009-10-10',0,NULL,'Note','«Зимовий відпочинок в Карпатах -2010»','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Парафіяльна служба милосердя церкви св. Миколая Чудотворця на Аскольдовій Могилі продовжує роботу над благодійними проектами. Закликаємо і вас бути небайдужими.&nbsp; Долучайтеся разом у Христі творити добро ближньому свому.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Опис проекту</strong>: Протягом року вихователі допомагають бездоглядним і потребуючим дітям долати труднощі в навчанні, в стосунках з батьками, однокласниками, вчителями. На катехитичних заняттях і заняттях гуртків, при ручній праці &nbsp;і в спілкуванні священик та вихователі є вчителями і порадниками, які з розрізнених дітей протягом трьох днів на тиждень&nbsp; впродовж року творять дружну спільноту і вміло готують до сприйняття і долання&nbsp; життєвих негараздів.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Проект<strong>: &laquo;</strong><strong>Зимовий відпочинок в Карпатах -2010</strong><strong>&raquo; </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Виконавець</strong>: Парафія УГКЦ Св. Миколая на Аскольдовій могилі у м. Києві</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Мета проекту:</strong><strong> </strong>зимове оздоровлення потребуючих дітей в Карпатах.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Цільова група: 18 осіб</strong></span></span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">соціальні сироти - 2</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">напівсироти - 2</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">діти з неповних сімей - 6</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">діти з кризових родин, бездоглядні &ndash; 4</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">діти зі статусом чорнобильця - 1</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">вихователі &ndash; 3 особи</span></span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Опис проекту</strong>: Протягом року вихователі допомагають бездоглядним і потребуючим дітям долати труднощі в навчанні, в стосунках з батьками, однокласниками, вчителями. На катехитичних заняттях і заняттях гуртків, при ручній праці &nbsp;і в спілкуванні священик та вихователі є вчителями і порадниками, які з розрізнених дітей протягом трьох днів на тиждень&nbsp; впродовж року творять дружну спільноту і вміло готують до сприйняття і долання&nbsp; життєвих негараздів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Активність дітей винагороджується відпочинком та участю в оздоровчо-катехитичному таборі в Карпатах протягом 4 днів (плюс переїзд) в кінці лютого 2010 року.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Діти діляться на дівочу та хлоп&rsquo;ячу групи. За кожну групу відповідає вихователь. Дітей супроводжує також священик. Переїзд з Києва до Славська Львівської області поїздом. Група дітей проживає в готелі. Харчування триразове. День відпочинку розпочинається руханкою, піднесенням прапору та виконанням гімну України. Участь у Службі Божій обов&rsquo;язкова. Після сніданку автомашиною відпочивальників підвозять до підйомника і дітям підбирають лижне спорядження. З дітьми постійно працює інструктор. Щоденний спуск на лижах.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Знайомство з дітьми з інших регіонів України.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В програмі відпочинку також ознайомлення з історією краю, розповіді про життя &nbsp;відомих людей, які прославили Україну.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Моніторинг</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong>Після повернення дітей з Карпат на вебсайті Аскольдової могили http://www.askoldova-mohyla.org подається стаття-звіт керівника групи, фотографії,&nbsp; відгуки дітей.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;Бюджет проекту:</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Проїздні квитки 240 грн х 18 осіб = 4&nbsp;320 грн</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Харчування, проживання 120 грн х 18 осіб = 2 160 грн</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Вартість підйомника 100 грн х 18 осіб = 1 800 грн</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Сухий пайок 500 грн</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Аптечка 200грн</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>ВСЬОГО: </strong><strong>8 980 ГРН</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вартість спорядження, оплата праці інструктора та щоденний доїзд до гори оплачується з пожертв інших доброчинців.</span></span></p>',NULL,NULL,1807,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(857,'P','2009-09-24',1,NULL,'Note','Про Аскольда і Діра','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На час правління Аскольда припадає проповідь св. братів Кирила та Методія. З їх життя виходить, що проповідували вони в Криму, де знайшли Євангелію та Псалтир, написаний &laquo;руськими&raquo; письменами, і християнина, який розмовляв руською мовою. На основі цих &laquo;письмен&raquo; св. Кирило уклав &laquo;глаголичне&raquo; письмо і переклав Євангелію на слов&rsquo;янську мову. Це мало бути 848 року.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У чужоземних джерелах збереглися уривчасті відомості про Українську державу в половині&nbsp; ІХ ст. На існування її вказує запис у Бертинській хроніці 839 року про прибуття візантійського посольства імператора Теофана до імператора Людовіка Побожного, яке просило, між іншим, дати дозвіл проїхати через володіння Людовіка &laquo;РУСАМ&raquo; (Ros). Вони прибули від руського князя до Царгороду, але, поки були там, &laquo;варварські й надзвичайно жорстокі племена&raquo; зайняли землі, через які вони мали повертатися додому, Людовік наказав з&rsquo;ясувати, хто були ті посли. І виявилося, що то були шведи<a href=\"#_ftn1\">[1]</a>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ця невиразна вказівка Бертинських аналів викликала багато гіпотез. Очевидно, володіння князя, від якого прийшли посли, лежали досить далеко від Чорного моря, і між ними та морем оселилися варвари; цими варварами могли бути угри або болгари, а князем &ndash; Київський князь. Можливо, що вислав він послами варягів-шведів, яких завжди було багато на службі в різних державах<a href=\"#_ftn2\">[2]</a>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З цього оповідання Бертинських аналів видно, що у 839 році в Європі ще не знали про молоду Українську державу, та не минуло й 30 років, як вона гучно повідомила про своє існування, примусивши затремтіти могутню Східню Римську імперію &ndash; Візантію. То був похід на Царгород. &laquo;Повість временних літ&raquo; оповідає, як у 866 році князі Аскольд і Дір, скориставшись з відсутності цісаря Михайла ІІІ, підступили з сльотою в 200 кораблів до Царгороду, зруйнували околиці міста, але самого міста не здобули. Завдяки чуду Влахернської ікони богородиці, шати якої опустили в море, знялася буря і знищила &laquo;безбожних Руси кораблі&raquo;<a href=\"#_ftn3\">[3]</a>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ця стисла згадка в &laquo;Повісти&raquo; доповнює цінні візантійські свідоцтва. Патріарх Фотій каже про цей напад у двох промовах: першу виголосив він у св. Софії Царгородській під час облоги міста, а другу &ndash; після зняття облоги. Їх доповнює &laquo;Окружне послання&raquo; патріарха Фотія 867 року, в якому дається характеристику Русів і повідомляється про їх охрищення<a href=\"#_ftn4\">[4]</a>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Цим не обмежуються візантійські джерела: в &laquo;житії патріарха Ігнатія&raquo;, написаному Микитою Пафлагонським, мабуть, біля 880 року, оповідається про напад Руси на Царгород, і &laquo;продовжувач Теофана&raquo;, теж оповідає про напад Руси; це оповідання цілком збігається зі свідоцтвом патріарха Фотія<a href=\"#_ftn5\">[5]</a>. Цей факт стверджує в усіх деталях хроніка Симона Логофета. Дуже важлива анонімова візантійська хроніка, яку опублікував Ф.Кюмон: в ній подається точна дата нападу Руси &ndash; 18 червня 860 року.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Таким чином, з цих різних джерел можна зробити висновок: у 860р. на Царгород напала сльота Руси. Безперечно, зорганізувати такий похід могла тільки держава, яка мала військо. Цікаві слова патріарха Фотія про те, що Руси підкорили сусідні народи її, &laquo;надмірно розгордившись, піднесли руку на Роменську імперію&raquo;.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дуже важливим є питання про походження цієї Руси і цієї великої фльоти, що могла прийти тільки від могутньої держави. Ряд учених ( Є. Голубинський, В. Васильєвський, В. Пархоменко ) припускали, що напад на Царгород, як і напад на Сурож та Амастриду, робила Созівська-Чорноморська Русь<a href=\"#_ftn6\">[6]</a>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Патріарх Фотій завдає удар цій гіпотезі: він точно зазначає, що Русь, яка напала на Царгород, була віддалена від нього великими просторами і не мала морських портів. З другого боку &ndash; свідоцтво патріарха Фотія, що Русь, перед походом 860-го року на Царгород, підкорила сусідів, дає підстави бачити&nbsp; в напасникові Київську Русь. Це мала бути могутня держава, яка спромоглася виставити аж 6&nbsp;000-8&nbsp;000 люду на 200 кораблях<a href=\"#_ftn7\">[7]</a>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Похід на Царгород 860 року пов&rsquo;язаний з дуже важливим питанням про охрищення Руси. Патріарх Фотій в &laquo;Окружному посланні&raquo; до патріархів Сходу року 867 оповідав, що руські князі, настрашені чудом у Царгороді, коли повернулися до Києва, то виявили бажання охриститися. Цісар Михайло ІІІ вислав єпископа та священників, і на Русі засновано Єпархію<a href=\"#_ftn8\">[8]</a>. Є низка свідоцтв про те, що охрищення відбулося не за цісаря Михайла ІІІ та патріарха Фотія, а за цісаря Василя І та патріарха Ігнатія<a href=\"#_ftn9\">[9]</a>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Про це свідчить онук Василя І, цісар Константин Порфірородний у своїй &nbsp;&laquo;Історії &nbsp;Василя І&raquo;.&nbsp; &nbsp;&nbsp;&nbsp;Про &nbsp;це &nbsp;&nbsp;оповідає&nbsp; &nbsp;й&nbsp;&nbsp; &nbsp;Никонівський&nbsp; &nbsp;&nbsp;літопис &nbsp;( щоправда, ХV ст., але в ньому є багато оповідань з давніх літописів, які не збереглися). Взагалі охрищенню Руси присвячено велику літературу й чимало суперечливих гіпотез. У всякому разі, питання про охрищення Київської Руси за князювання Аскольда можна визнати за доведене<a href=\"#_ftn10\">[10]</a>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На час правління Аскольда припадає проповідь св. братів Кирила та Методія. З їх життя виходить, що проповідували вони в Криму, де знайшли Євангелію та Псалтир, написаний &laquo;руськими&raquo; письменами, і християнина, який розмовляв руською мовою. На основі цих &laquo;письмен&raquo; св. Кирило уклав &laquo;глаголичне&raquo; письмо і переклав Євангелію на слов&rsquo;янську мову. Це мало бути 848 року<a href=\"#_ftn11\">[11]</a>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Коли Київський князь просив у Царгороду місіонерів, природно, цісар міг вислати св. Кирила. Є факти, які дають підстави ототожнити місіонера, що проповідував у Києві, із св. Кирилом: це- оповідання про чудо з Євангелією, яку кинули у вогонь,&nbsp; і вона не згоріла; внаслідок цього чуда багато людей охристилося.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Никонівський літопис відносить чудо до часів князювання Аскольда, а Кольбертинські анами свідчать, що сталося воно за св. Кирила<a href=\"#_ftn12\">[12]</a>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Народна традиція та літопис зберегли пам&rsquo;ять про Аскольда і Діра як християн. Літопис оповідає, що Олег наказав забити обох князів як узурпаторів. Аскольда поховано на Угорській горі, і над його могилою Ольма збудував церкву. Діра поховано в іншому місці &ndash; &laquo;за св. Ориною&raquo;. Сполучення двох імен - Аскольда та Діра &ndash; як суправителів, викликає великі сумніви. Треба гадати, що князювали вони не одночасно. Аль Масуді виразно писав про Діра, як про першого серед князів<a href=\"#_ftn13\">[13]</a>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">М.Грушевський вважав, що Дір князював після Аскольда, а може й після Олега<a href=\"#_ftn14\">[14]</a>, так само дивляться М.Таубе<a href=\"#_ftn15\">[15]</a> і О.І.Натаєвський<a href=\"#_ftn16\">[16]</a>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Митрополіт Іларіон вважає, що правили вони одночасно<a href=\"#_ftn17\">[17]</a>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Викликає багато суперечок сама Аскольдова постать. За літописом &ndash; Аскольд і Дір були боярами Новгородського князя Рюрика, які захопили в Києві владу<a href=\"#_ftn18\">[18]</a>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Питанням походження Аскольда займався М.Таубе і твердив, що Аскольд - швед, який прибув до Києва з дружиною через Західну Двіну і не мав нічого спільного з Рюриком<a href=\"#_ftn19\">[19]</a>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Але поруч із гіпотезами, які припускають варязьке походження Аскольда, є й інші: ще О.Шахматов вважав Аскольда за слов&rsquo;янина, навіть за Києвого сина<a href=\"#_ftn20\">[20]</a>. За ним пішов М.Приселков<a href=\"#_ftn21\">[21]</a>, а з пізніших дослідників &ndash; Б.Рибаков, який виводить ім&rsquo;я Аскольда від річки Оскіл, і пише &ndash; &laquo;Оскольд&raquo;<a href=\"#_ftn22\">[22]</a>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Константин Порфіродний згадує договір між Василем І і Руссю (Аскольдом) &ndash; (873-874)<a href=\"#_ftn23\">[23]</a>. На ті ж роки припадає створення першого Рафальштеттенського митного статуту, датованого часами короля Корльомана (880р.) або Людовіка Німецького (р.876), яким встановлювалося мито з краму, доводжуваного з Руси до Боварії<a href=\"#_ftn24\">[24]</a>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Усі ці факти: договори з Баварією та Візантією, похід на Царгород, масове охрищення &ndash; свідчать про існування в середині ІХ ст. могутньої державної організації. Слова патріарха Фотія про підкорення Руссю сусідніх народів цілком потверджуються вказівками Никонівського літопису: 865 року &laquo;воєважа Аскольд і Дір полочан&raquo;, а в 866 році, сказано в &laquo;історії Російській В.Татіщева: Аскольд і Дір &laquo;Ходи на кривичі и побіди їх&raquo;. Цікава вказівка Никонівського літопису з року 864, що &laquo; убієн бисть от болгар Оскольдов син&raquo;. Очевидно, були якісь воєнні&nbsp; дії в Болгарії<a href=\"#_ftn25\">[25]</a>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Так, з окремих розірваних фактів вимальовується образ Київської держави - Руси середини ІХ ст., що підкоряє сусідів, воює з Візантією, укладає з нею та іншими державами мирові і нарешті &ndash; приймає Християнство.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Усі ці факти дали російському історикові В.Ключевському право писати, що Руська держава була заснована діяльністю Аскольда, а за ним Олега. З Києва, а не з Новгорода, пішло об&rsquo;єднання слов&rsquo;ян&raquo;<a href=\"#_ftn26\">[26]</a>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Формування цієї держави довгий час не привертало уваги істориків, як пройшло не поміченим і для Заходу. Це був час, коли Європа була охоплена боротьбою германських племен за об&rsquo;єднання, за творенню державних одиниць. До того ж авари на довгий період створили заслону між германськими та слов&rsquo;янськими світами, і лише перемоги Карла Великого відкрили очам Західної Європи вже сформовані слов&rsquo;янські об&rsquo;єднання. Діяльність св.&nbsp; братів Кирила та Методія зв&rsquo;язала Захід із Сходом Християнством. На сході Європи точилася своя боротьба, формувалися об&rsquo;єднання, нав&rsquo;язувалися культурні та економічні зв&rsquo;язки з Азією, з Арабським халіфатом, Персією, Візантією; поширювався обмін культурними цінностями із старими, світового значення культурами Сходу й творилася нова блискуча культура, в якій у гармонійне ціле зливалися чужоземні впливи. На Сході Європи зароджувалася нова могутня потенційна сила &ndash; Україна &ndash;Русь<a href=\"#_ftn27\">[27]</a>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В організації першої Київської держави брали деяку участь нормани. VІІІ-ІХ століття були добою, коли нормани, під назвою яких об&rsquo;єднували населення Скандинавії &ndash; шведів, норвегів, а також данів, - на чолі зі своїми ватажками &ndash; вікінгами, почали вдиратися до різних земель та плюндрувати й грабувати їх. Деякі з них осідали на захоплених землях, інші верталися додому з пограбованим добром. Згодом окремі групи варягів стали найманим військом. У VІІІ ст. нормани, які наші літописи звали варягами, з&rsquo;явилися у верхів&rsquo;ях &nbsp;Волги і, підкоривши фінські племена - мерю, мурому, мещеру &ndash; опанували Волзький шлях. Близько 800-го року заснували варяги Стару Ладогу &ndash; Алдейгюбург, інша група їх оселилася тоді ж у гирлі Двіни і заснувала Зеебург, де пізніше постала Рига<a href=\"#_ftn28\">[28]</a>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На початку ІХ ст. нормани нападають на Західну Європу: руйнують Німеччину, Францію, Англію. На невеликих човнах піднімаються ріками і нищать Майни, Ворс, Ахен. Сеною вони піднялися до Парижу, 10 місяців тримали його в облозі. Допливли до Ісландії, Гренландії. Через Гібралтар пройшли до Середземного моря. Подекуди засновували свої держави: у Франції &ndash; Нормандію, на Середземному морі об&rsquo;єднали Сицилію та південну Італію й створили Норманське королівство<a href=\"#_ftn29\">[29]</a>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Близько до цих західноєвропейських подій оповідання наших літописів про постання держави в землі словенів. Під 859 роком оповідає літопис, що варяги прийшли із замор&rsquo;я, поклали данину на &laquo;чудь, и на словенях, на мери, и на всех ( весі. &ndash; Ред.), кривичах&raquo;. В цьому оповіданні відмежовано обшир володіння варягів. 862-го року, ці племена &laquo;изгната варяги за море&raquo;, але вони не спромоглися зорганізувати свою владу і закликали із замор&rsquo;я інших варягів. Прибули три брати: Рюрик, Синеус і Трувор.&nbsp; &nbsp;Рюрик сів у Новгороді, Синус &ndash; в Білоозері, а Тривор &ndash; в Ізборську. По смерті братів Рюрик став єдиновладним князем. У Рюрикові Н. Беляєв, а за ним П.Ковалевський бачать Рорика, конунга Фризії, примушеного в середині ІХ ст. покинути її<a href=\"#_ftn30\">[30]</a>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Літопис зазначає межі його володіння: Новгород &ndash; в землі словенів, Полоцьк &ndash; кривичів, Ростов &ndash; мері, Білоозеро &ndash; весі, Муром &ndash; муроми. Так на півночі в середині ІХ століття постала держава, яка охоплювала землі словенські ( словени, кривичі ) та фінські ( меря, весь, мурома ).</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У той час на півдні, в землі України &ndash; Руси, вже існувала велика держава з центром у Києві. Так у ІХ столітті творяться дві окремі, незалежні держави: напівфінська Новгородська та слов&rsquo;янська Київська.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Повість временних літ&raquo;, яка складалася в ХІІ ст., оповідає, що перед своєю смертю Рюрик передав правління родичеві Олегові і поручив йому сина Ігоря, який&nbsp; був ще малий. Року 882-го Олег із великим військом, в якому були варяги, чудь, словени, меря, весь і кривичі, пішов на південь, здобув Смоленськ, Любеч, Київ, підступно забив Аскольда та Діра і став правити Києвом.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За історичною літературою</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Підготував&nbsp; Михайло Якубовський</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">м. Київ , 17 вересня 2009 року.&nbsp;</span></span></p>\r\n<hr size=\"1\" />\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref1\">[1]</a> Наталія Полянська &ndash;Василенко. Історія України. Том 1. до середини XVII Століття, 2-е видання, м.Київ, &laquo;Либідь&raquo;, 1993, с.90.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref2\">[2]</a> К.Я. Грот. Моравія и Мадьяры с половины&nbsp; IX ст. до начала Х. СПБ, 1881, стор.232 &ndash; 233.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- М.Грушевський. Історія України &ndash; Руси, т.1, стор.394</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- М.Левченко. Очерки по истории Руссо &ndash; византийских отношений. А.Наук СССР, Інститут истории. М. 1956, стор. 55-56.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref3\">[3]</a> Повесть временных лет, вид. 1950р., стор.19.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref4\">[4]</a> О.І.Натаєвський. Кирило-Методіївське християнство в Русі-Україна. Рим, 1954, стор.9-11 &ndash; М.В.Левченко, Там же, стор.57</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref5\">[5]</a> М.В.Левченко. Там же, стор.58</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref6\">[6]</a> Н.Полонська-Василенко. Князь Київський Аскольд і перше хрещення&nbsp;&nbsp; Руси.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Наша&nbsp; культура&raquo;, Віннітет, 1952, ч.8, стор. 25.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">М.Міллер. Християнство в Україні. Русі до Володимира. &laquo;Визвольний шлях&raquo;, Леон, 1959, ч.7, стор.754.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref7\">[7]</a> М.Грушевський, Там же, т.1, стор.404</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref8\">[8]</a> В Никонівському літопису ХVI ст.. вміщено оповідання про охрищення Руси за Аскольда. &laquo;Пов.врем.літ&raquo;, 1950, ІІ, стор. 248-249.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Н Полонська- Василенко. Там же, стор. 27</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- О.І. Натаєвський. Там же, стор. 19-21, 25-26.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- О.І.Назарко, Св.Володимир Великий, володар і хреститель Руси-України; Рим, 1954,стор.29.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref9\">[9]</a> Питання про особи патріарха та імператора, за яких&nbsp; охрещена Русь, не розв&rsquo;язане. Імператор Михаїл ІІІ посідав престол від 842 до 867р.р.; 846 року посів патріярний престол Ігнатій. Року 857 патріарха Ігнатія заточено в монастир і на патріарха обрано видатного вченого Фотія. Року 867-го, 23 вересня, Мисаїл ІІІ був забитий з наказу Василя Македонця, що посів імператорський престол, як Василь І. Наступного дня усунено патріарха Фотія і поновлено патріарха Ігнатія. Таким чином відрядження місії на Русь в 867 році могло бути й за Михаїла ІІІ та Фотія, і за Василя І та Ігнатія.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Проф.І.Огієнко. Костянтин і Методій. Варшава, 1927, т.1, стор.26-41.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- С.Томашівський. Історія Церкви на Україні. Філадельфія, стор.49-50.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">-&nbsp; Миторополіт Іларіон. Поділ єдиної Христової Церкви і перші спроби поєднання її. Віннітет, 1953, стор.105-142;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Н.Полонська-Василенко. Там же, стор.30.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">-Повесть временніх лет&raquo;, М., 1950, т.ІІ&nbsp; стор. 248-249.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref10\">[10]</a> О.І.Нагаєвський. Там же, стор.46-47.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref11\">[11]</a> Проф..І.Огієнко. Костянтин і Методій; 1927, т.1.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp; - його ж, Українська церква, Прага, 1942, т.1, стор.99.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- М.Грушевський. Історія України-Руси; т.1, стор.405</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref12\">[12]</a> Є.Голубинський. История Церквы. М., 1902, т.1, стор.51-52;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Д-р І.Огієнко. Українська Церква, т.1, стор.27-29;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - О.І.Натаєвський. Там же, стор.52-54.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref13\">[13]</a> А.Я.Гаркави. Сказання мусульманських писателей&nbsp; о славянах и русских. СПТ, 1870.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref14\">[14]</a> М.Грушевський. там же, т.1, стор. 408-409.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref15\">[15]</a> М.de Taube/ Romeetla Russie, стор.133.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref16\">[16]</a> О.І.Натаєвський. Там же, стор.54.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref17\">[17]</a> Д-р І.Огієнко. Українська Церква, т.1, Прага, стор.184.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref18\">[18]</a> &laquo;Повесть временных лет&raquo;, М., 1950, т.1, стор.29</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref19\">[19]</a> M.de Taube. Там же, стор. 61-63</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref20\">[20]</a> А.А.Шахматов. Разыскания о русских летописах, стор.289</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref21\">[21]</a> М.Д.Приселков. История руського летописания, стор.40.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref22\">[22]</a> Б.А.Рыбаков. Очерки, стор.801</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref23\">[23]</a> M.de Taube. Там же, стор.93.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref24\">[24]</a> В.В.Василевский . Древняя торговля Киева с Регенсбургом. &laquo;Журн.Мих.Нар. Прав.&raquo;, 1888, т.258.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">M.de Taube. Там же, стор.52.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В.Пашуто. Очерки по истории Галицко-Волынской Руси.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ак.Наук СССР, 1950, стор.170.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref25\">[25]</a> Д.С.Лихачев. Повесть временніх лет, т.2, стор.246.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref26\">[26]</a> В.О.Ключевский. Курс русской истории, т.1, М., 1923, стор.175</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref27\">[27]</a> Д-р М.Андрусяк. Ґенеза східно-європейських народів.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp; - ANALECTA/ Otdinis S/Basilii Magni/ Romae, 1954, Сер.ЫЫ, Секц.ІІ, т.2, вип.1-2, стор.242.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref28\">[28]</a> T/ARNE/ La Suede et E&rsquo;Orient/ Upsala. 1914. П.П.Смирнов. Волзький шлях. К., 1929. А.Погодин. Варяги и Розсип. Белград, 1932.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref29\">[29]</a> P.Kovalevsky. Manuel d&rsquo;Histoire Russe, Paris, 1948. стор.37.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"#_ftnref30\">[30]</a> Н Бєляєв. Рорик Ютландський и Рюрик Новгородський, Прага, 1929.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">П.Ковалевський. Исторический путь России. Париж, 1949, стор.104</span></span></p>',NULL,NULL,1808,5,1,25);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(858,'P','2008-11-03',1,NULL,'Note','Глава УГКЦ наголосив, що сучасна Україна потребує християнської еліти','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На думку Патріарха Любомира, якщо є справжня еліта з обох сторін, тоді є виправдана надія на порозуміння. Тому нашим старанням має бути праця над тим, щоб у нас було \"достатньо людей на високому інтелектуальному і релігійному рівні розвитку\", - сказав Предстоятель УГКЦ.</span></span></p>','<p>&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Про те, що сьогодні в Україні бракує християнської еліти: людей, які мають не тільки високі професійні навики, не тільки широкий світогляд, але осіб, які мають високу християнську свідомість, Глава УГКЦ Патріарх Любомир (Гузар) сказав у виступі на Круглому столі під назвою \"Від 1939 до 2008: солідарність поколінь перед викликами сучасності\". Цей захід відбувся за участю молодіжної федерації УГКЦ \"Обнова\" і став початком Суспільного тижня 2008 року, який вперше після відновлення Незалежності 1 - 5 листопада проходить у Києві. Інформує <a href=\"http://www.risu.org.ua/\">РІСУ</a></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За повідомленням Департаменту інформації УГКЦ, під час свого виступу Глава УГКЦ говорив про християнську еліту не тільки зважаючи на політично-суспільне життя, але й з огляду на міжконфесійний діалог: \"Чому нам так складно знайти порозуміння між конфесіями в Україні? Бо ми не маємо християнської еліти. Якби було серед католиків, православних, протестантів у достатній кількості осіб, які були б справжньою християнською елітою, нам було б значно легше вести екуменічний діалог. Трудно провадити діалог з тими, хто до діалогу не дозрів\".</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На думку Патріарха Любомира, якщо є справжня еліта з обох сторін, тоді є виправдана надія на порозуміння. Тому нашим старанням має бути праця над тим, щоб у нас було \"достатньо людей на високому інтелектуальному і релігійному рівні розвитку\", - сказав Предстоятель УГКЦ.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.kreschatic.kiev.ua/news/1225708990.html</span></span></p>',NULL,NULL,608,5,1,27),
(859,'P','2008-11-02',1,NULL,'Note','Українсько-польські молитви на Личаківському цвинтарі у Львові','','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1 листопада 2008 р., в урочистість Всіх Святих, на Личаківському цвинтарі у Львові відбулися спільні молитви католиків двох обрядів на польських і українських військових похованнях. Ця традиція була започаткована двома кардиналами - Главою Української Греко-Католицької Церкви Блаженнішим Любомиром Гузаром і Його Еміненцією Мар\'яном Яворським, дотеперішнім львівським митрополитом Римо-Католицької Церкви в Україні. Цьогорічні молитви примирення очолювали львівський римо-католицький єпископ-помічник Леон Малий і отець мітрат Іван Дацько з Української Греко-Католицької Церкви. На гробах Личаківського кладовища було запалено сорок тисяч свічок.<br /><br />(для Радіо Ватикану, Костянтин Чавага)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/<br /></span></span></p>',NULL,NULL,614,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(860,'P','2009-09-24',1,NULL,'Note','«Assistance for children of imprisoned mothers» ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Executive</strong>: Curacy of Ukrainian Greek Catholic Church of St. Mykola on Askold grave in city of Kyiv</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Project aim</strong><strong>: </strong>Financial and moral assistance for children of imprisoned mothers who reside in orphanages or with tutors. &nbsp;Set up family relations between child and mother.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Target group: </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">46 children requested about communication or some certain assistance.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">4 children from family of many children who suffered from flood in fall 2008 in Carpathian region.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Project</strong>:<strong> &laquo;Assistance for children of imprisoned mothers&raquo; </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Executive</strong>: Curacy of Ukrainian Greek Catholic Church of St. Mykola on Askold grave in city of Kyiv</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Project aim</strong><strong>: </strong>Financial and moral assistance for children of imprisoned mothers who reside in orphanages or with tutors. &nbsp;Set up family relations between child and mother.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Target group: </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">46 children requested about communication or some certain assistance.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">4 children from family of many children who suffered from flood in fall 2008 in Carpathian region.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Project description</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">4 humanitarian packages, including food, means of individual sanitary, spiritual literature, clothes, shoes, greeting cars and letter from parishioner&rsquo;s children. An aim of such step is to create moral valuables, faith in good will, feeling of emotional experience and absolution. &nbsp;In order to receive every next parcel child should write a letter to his/her mother and answer us. &nbsp;Also 2 children with tutor&rsquo;s permit will have summer recreation at seaside.<strong> </strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">As the result of the action &ldquo;School briefcase&rdquo; announced on website and at the Church on Askold grave we gathered a lot of school exercise books, stationery and different school accessories. &nbsp;First humanitarian package anticipates assistance in arranging school briefcase.&nbsp; Means of individual sanitary will be purchased. &nbsp;In the letter a priest will bless a child for a happy new school year and will be sent in September.</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">A second parcel will contain food, means of individual sanitary, clothes and shoes (donation of parishioners and volunteers) and literature. &nbsp;In a package child will find St. Mykola&rsquo;s icon, history of his life and offer to give a gift to St. Mykola &ndash; to learn pray, to draw or to write smth for his/her mother and/or St. Mykola; to study difficult lesson, etc. &nbsp;A package will be sent during October-November.</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">A third humanitarian package will be sent with gifts from St. Mykola &ndash; sweets, toys and books.&nbsp; Food will be purchased.&nbsp; In a letter a child will find a Christmas greetings and greetings from parishioners&rsquo; children. &nbsp;A package will be sent during December-January.</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">At website and in the church action &ldquo;Easter basket&rdquo; will be announced with a purpose to create Easter basket for children and to teach parishioners&rsquo; children to share with poor children. We will buy means of individual sanitary.&nbsp; On catechism lessons parishioners&rsquo; children will write greetings and will put in children&rsquo;s basket. &nbsp;Gifts will be sent during April-May.</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">In May we will write an expenses report indicating quantity/quality indexes and our experience. &nbsp;We will select two children who will go for seaside recreation upon their tutor&rsquo;s permission in June 2010.</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">A humanitarian package will include. </span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Food: oil - 11 UAH, sugar -7 UAH, cans 2pcs &ndash; 20 UAH, biscuits &ndash; 6 UAH, condensed milk &ndash; 6 UAH, candies, tea/Easter cake &ndash; 20 UAH. Total 70 UAH.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Means of individual sanitary: tooth paste, tooth brush &ndash; 16 UAH, soap 4 pcs -12 UAH, detergent -12 UAH. Total 40 UAH.<strong> </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Project executives</strong>: 2 social employees</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Monitoring/Feedback</strong>. Receiving a humanitarian package is confirmed by user of the project by sending on our address the letter-notification which the receiver may found in our parcel.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Quarterly the recent news and developments of the project will be posted at website of Askold grave. &nbsp;Children&rsquo;s letters will be submitted in order to attract good will people who will be able to join for children&rsquo;s support.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Project budget</strong></span></span></p>\r\n<table style=\"text-align: justify;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>#</strong></span></span></p>\r\n</td>\r\n<td width=\"193\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Expense   item</strong></span></span></p>\r\n</td>\r\n<td width=\"180\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Calculation</strong></span></span></p>\r\n</td>\r\n<td width=\"84\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Grant,</strong></span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>UAH</strong></span></span></p>\r\n</td>\r\n<td width=\"71\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Grant,</strong></span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>$</strong></span></span></p>\r\n</td>\r\n<td width=\"97\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Organization   contribution</strong></span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1</span></span></p>\r\n</td>\r\n<td width=\"193\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Envelopes with stamps</span></span></p>\r\n</td>\r\n<td width=\"180\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">400 pcs х 2 UAH</span></span></p>\r\n</td>\r\n<td width=\"84\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">800</span></span></p>\r\n</td>\r\n<td width=\"71\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">100</span></span></p>\r\n</td>\r\n<td width=\"97\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">2</span></span></p>\r\n</td>\r\n<td width=\"193\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Humanitarian package: food</span></span></p>\r\n</td>\r\n<td width=\"180\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">50 persons х 70 UAH х 3 times</span></span></p>\r\n</td>\r\n<td width=\"84\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">10500</span></span></p>\r\n</td>\r\n<td width=\"71\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1300</span></span></p>\r\n</td>\r\n<td width=\"97\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">3</span></span></p>\r\n</td>\r\n<td width=\"193\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Humanitarian package: means</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">of individual sanitary</span></span></p>\r\n</td>\r\n<td width=\"180\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">50 persons х 40 UAH х 3 times</span></span></p>\r\n</td>\r\n<td width=\"84\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">6000</span></span></p>\r\n</td>\r\n<td width=\"71\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">750</span></span></p>\r\n</td>\r\n<td width=\"97\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">4</span></span></p>\r\n</td>\r\n<td width=\"193\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Post office expenses</span></span></p>\r\n</td>\r\n<td width=\"180\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">400 g/p х 15 UAH</span></span></p>\r\n</td>\r\n<td width=\"84\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">6 000</span></span></p>\r\n</td>\r\n<td width=\"71\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">750</span></span></p>\r\n</td>\r\n<td width=\"97\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">5</span></span></p>\r\n</td>\r\n<td width=\"193\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Clothes, shoes, spiritual and faction literature</span></span></p>\r\n</td>\r\n<td width=\"180\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n<td width=\"84\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n<td width=\"71\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n<td width=\"97\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">*</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">6</span></span></p>\r\n</td>\r\n<td width=\"193\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Employee&rsquo;s work</span></span></p>\r\n</td>\r\n<td width=\"180\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">2 persons</span></span></p>\r\n</td>\r\n<td width=\"84\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n<td width=\"71\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n<td width=\"97\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">*</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">7</span></span></p>\r\n</td>\r\n<td width=\"193\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Post office pack/box</span></span></p>\r\n</td>\r\n<td width=\"180\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">400 psc</span></span></p>\r\n</td>\r\n<td width=\"84\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n<td width=\"71\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n<td width=\"97\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">*</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>8</strong></span></span></p>\r\n</td>\r\n<td width=\"193\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Total</strong></span></span></p>\r\n</td>\r\n<td width=\"180\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n</td>\r\n<td width=\"84\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>23300</strong></span></span></p>\r\n</td>\r\n<td width=\"71\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>2   900</strong></span></span></p>\r\n</td>\r\n<td width=\"97\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>',NULL,NULL,1810,5,2,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(861,'P','2009-09-24',1,NULL,'Note','«Winds of hope» ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Executive</strong>: Curacy of Ukrainian Greek Catholic Church of St. Mykola on Askold grave in city of Kyiv</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Project aim:</strong><strong> </strong>Providing of spiritual guardianship and social assistance to disabled children, people of old age requiring any assistance, HIV positive persons with the aim of improvement of the life and health quality for them and their family members.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Target group: 25-30 persons</strong></span></span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">16 families with disabled children (on the basis of created group of mutual assistance for family members with disabled children)</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">10 persons of old age requiring any assistance</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">5 HIV positive families</span></span></li>\r\n</ul>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Project<strong>: &laquo;Winds of hope&raquo; </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Executive</strong>: Curacy of Ukrainian Greek Catholic Church of St. Mykola on Askold grave in city of Kyiv</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Project aim:</strong><strong> </strong>Providing of spiritual guardianship and social assistance to disabled children, people of old age requiring any assistance, HIV positive persons with the aim of improvement of the life and health quality for them and their family members.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Target group: 25-30 persons</strong></span></span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">16 families with disabled children (on the basis of created group of mutual assistance for family members with disabled children)</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">10 persons of old age requiring any assistance</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">5 HIV positive families</span></span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Project task:</strong></span></span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Establishing of trustful relations with mothers and their children who are sick with a cerebral palsy. &nbsp;Spiritual guardianship, catechism. &nbsp;Lessons to be arranged monthly at fund premises. &nbsp;Priest is intended to visit families for closer acquaintance subject to preliminary arrangement, to ascertain a problem and determining a possibility for providing assistance. &nbsp;Cultural guardianship for families.</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Priest will visit parishioners who are not able to go the church and take part in liturgies due to their health condition. (Place of their residence or in hospitals). Confession. &nbsp;Holy Communion. 5-6 persons monthly.</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Priest will visit lonely people of old age who suffer physically and spiritually and ask for spiritual knowledge and support. 5-6 persons monthly.</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Visiting HIV positive families. &nbsp;Spiritual guardianship. Support. &nbsp;Relations with 3 families are established already.</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Social employees/volunteers of the project will provide in-house services that do need it &ndash; upon request &ndash; sanitary assistance, cooking, cleaning the houses, good purchasing, etc.</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Providing car for transportation of ill children (who may move only on wheelchair) with parent&rsquo;s permission to visit church liturgies, to communicate with other children, to attend concerts.</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Repair of church car Peugeot of 1995: external repair &ndash; straightening and painting, saloon repair &ndash; renovation of seating places. </span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Food purchase (oil, sugar, grits, canned food, tea), goods of individual sanitary and cleaning means - $10 per person monthly.</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Purchasing medicines of first aid - $4 per person monthly; children and spiritual literature.</span></span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Project personnel: Priest, </strong>2 social employees/volunteers, driver</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Project description</strong>: &nbsp;Every Friday the priest (upon request with social employee) will visit wards (subject to preliminary agreement) in their houses or in places of residence on church car. &nbsp;The priest will visit every person monthly, or more often &ndash; if needed and will deliver humanitarian package with food, goods of individual sanitary, cleaning means, medicaments and literature. &nbsp;The services will be rendered only after clarification of every person&rsquo;s needs in particular. &nbsp;One of the main problems of all wards is loneliness, thus it is important to listen their life stories and be patient to their problems. &nbsp;Social employee will assist on house daily work. &nbsp;If necessary - communal services may be paid.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Concert&rsquo;s and excursion&rsquo;s visits will be organized of children, as well as visits to the church for main Christian holidays.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Project duration</strong> &ndash; 1 year</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Project budget:</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Food, goods of individual sanitary and cleaning means: $ 3 000</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Medicaments: $1 200</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Children&rsquo;s entertainment: $300</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Car/transportation expenses (repair, fuel, technical inspection): $800</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Payment for communal services: $300</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Children&rsquo;s spiritual literature: $200</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Telephone cards: $60</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Unforeseen expenses: $160</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Total: $ 7000</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Payment for employee&rsquo;s work and transportation: contribution of organisation</span></span></p>',NULL,NULL,1811,5,2,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(862,'P','2009-10-10',0,NULL,'Note','«КРИЛА НАДІЇ» ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Парафіяльна служба милосердя церкви св. Миколая Чудотворця на Аскольдовій Могилі продовжує роботу над благодійними проектами. Закликаємо і вас бути небайдужими.&nbsp; Долучайтеся разом у Христі творити добро ближньому свому.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Мета проекту:</strong><strong> </strong>Надання духовної опіки та соціальної допомоги дітям-інвалідам, людям похилого віку, що потребують сторонньої допомоги, ВІЛ-інфікованим з метою покращання якості їх життя та здоров&rsquo;я та життя членів їх родин.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Проект<strong>: &laquo;КРИЛА НАДІЇ&raquo; </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Виконавець</strong>: Парафія УГКЦ Св. Миколая на Аскольдовій могилі у м. Києві</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Мета проекту:</strong><strong> </strong>Надання духовної опіки та соціальної допомоги дітям-інвалідам, людям похилого віку, що потребують сторонньої допомоги, ВІЛ-інфікованим з метою покращання якості їх життя та здоров&rsquo;я та життя членів їх родин.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Цільова група: 25-30 осіб</strong></span></span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">16 сімей з &nbsp;дітьми інвалідами (на базі створеної групи взаємодопомоги для членів сімей з дітьми інвалідами)</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">10 осіб похилого віку, що потребують сторонньої допомоги </span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">5 ВІЛ-інфікованих родин</span></span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Завдання проекту:</strong></span></span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Налагодження довірливих стосунків з матерями та їхніми дітьми, хворими на ДЦП. Духовна опіка, катехизація. Заняття раз на місяць&nbsp; в приміщенні фонду. За домовленістю відвідування священиком родин для ближчого знайомства, вияснення проблем та визначення можливостей для надання допомоги. Духовна опіка родинами.</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Відвідування священиком парафіян, що за станом здоров\'я не можуть брати участь у богослужіннях. (За місцем проживання чи в лікувальних закладах). Сповідь. Святе Причастя. 5-6 осіб раз на місяць</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Відвідування священиком самотніх людей похилого віку, які терплять фізично і духовно, і просять&nbsp; про духовну науку і підтримку. 5-6 осіб раз на місяць</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Відвідування ВІЛ-інфікованих родин. Духовна опіка. Розрада. З двома родинами вже налагоджений зв\'язок.</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Соціальні працівники проекту надаватимуть потребуючим послуги на дому &nbsp;- при потребі - допомогу в дотриманні гігієни, приготування їжі, прибирання оселі, закупівля продуктів тощо. </span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Надання автомобіля для транспортування хворих дітей (колясочників) за дозволом батьків до церкви для участі дітей в богослужіннях, для спілкування дітей з парафіяльними дітьми, на парафіяльні концерти</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ремонт церковного автомобіля ПЕЖО 1995 р.в.: ремонт зовнішній &ndash; рихтовка та пофарбування,&nbsp; ремонт салону &ndash; оновлення сидінь. </span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Закупівля &nbsp;продуктів (олія,цукор, крупи, консерви,чай), засобів особистої гігієни та миючих засобів - $10 на особу на місяць</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Придбання ліків першої необхідності - $4 на особу на місяць; дитячої і духовної літератури</span></span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Персонал проекту</strong><strong>: </strong>Священик, 2 соціальні працівники, водій</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Опис проекту</strong>: &nbsp;Священик (при потребі з соціальним працівником) щоп&rsquo;ятниці на церковному автомобілі відвідуватиме підопічних (за домовленістю) за місцем проживання чи перебування. Кожну особу священик відвідає раз на місяць, чи частіше - при потребі і&nbsp; передасть гуманітарний пакет з продуктами, засобами особистої гігієни, миючими засобами, ліками, літературою. Послуги будуть надаватися після вияснення потреб кожної особи зокрема. Одна з головних проблем всіх підопічних &ndash; самотність, тому необхідно вислухати їхні історії життя, перенятися їхніми проблемами. Соціальний працівник допоможе виконати хатню роботу. При крайній необхідності будуть оплачені комунальні послуги.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Для дітей будуть організовані виїзди на концерт, на екскурсію, на храмове та великі християнські свята до церкви</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Тривалість проекту</strong> &ndash; 1 рік</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Бюджет проекту:</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Продукти харчування, засоби гігієни та миючі засоби:$ 3 000</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Товари медичного призначення: $1 200</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дозвілля дітей: $300</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Витрати на транспорт (ремонт, пальне, техогляд): $800</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Оплата комунальних послуг: $300</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дитяча і духовна література: $200</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Телефонні картки: $60</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Непередбачені витрати: $160</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>ВСЬОГО: </strong><strong>$ 7000</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Оплата праці персоналу та автотранспорт: внесок&nbsp; організації</span></span></p>',NULL,NULL,1812,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(863,'P','2009-10-10',0,NULL,'Note','«Допомога дітям ув’язнених матерів»','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Парафіяльна служба милосердя церкви св. Миколая Чудотворця на Аскольдовій Могилі продовжує роботу над благодійними проектами. Закликаємо і вас бути небайдужими.&nbsp; Долучайтеся разом у Христі творити добро ближньому свому.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Мета проекту</strong><strong>: </strong>Матеріальна і моральна підтримка потребуючих дітей ув&rsquo;язнених матерів, що проживають в інтернатах чи з опікунами. Налагодження родинних стосунків між дитиною та матір&rsquo;ю</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Проект:<strong> &laquo;</strong><strong>Допомога дітям ув&rsquo;язнених матерів</strong><strong>&raquo; </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Виконавець</strong>: Парафія УГКЦ Св. Миколая на Аскольдовій могилі у м. Києві</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Мета проекту</strong><strong>: </strong>Матеріальна і моральна підтримка потребуючих дітей ув&rsquo;язнених матерів, що проживають в інтернатах чи з опікунами. Налагодження родинних стосунків між дитиною та матір&rsquo;ю</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Цільова група: </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">46 потребуючих дітей, що в<strong> </strong>листах просять про спілкування чи про конкретну допомогу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">4 дітей з багатодітної родини, що потерпіли внаслідок повені&nbsp; на Прикарпатті восени 2008 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Опис проекту</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Потребуючим дітям будуть надіслані&nbsp; по 4 гуманітарних пакети - продукти, засоби гігієни, духовна і дитяча література; одяг, взуття, листи з поздоровленнями, повчальні листи та листи від дітей-парафіян - з метою формування моральних цінностей, віри в добро, почуття співпереживання та прощення. Для отримання кожної наступної посилки дитині необхідно написати листа мамі і відповісти&nbsp; нам. Також двоє дітей за дозволом опікунів будуть мати літнє оздоровлення на морі<strong>. <br /></strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Результатом оголошеної на сайті та безпосередньо в Церкві на Аскольдовій могилі Акції &laquo;Шкільний портфелик&raquo; було зібрано сотні зошитів, канцтовари та інше шкільне приладдя. Перший гуманітарний пакет передбачає допомогу у наповненні шкільного портфеля дитини.&nbsp; Закуповуються засоби гігієни. В листі священик поблагословить дитину на щасливий навчальний рік. Надсилатиметься у вересні.</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Друга посилка буде наповнена продуктами, засобами гігієни; одягом та взуттям (збірка парафіян та волонтерів), літературою. В пакеті буде образок св. Миколая, розповідь про його життя і пропозиція дитині зробити подарунок святому &ndash; вивчити молитву, намалювати чи написати щось для мами і святого Миколая; добре вивчити урок, який важко дається і т.п. Надсилатиметься протягом жовтня-листопада.</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Третій гуманітарний пакет буде надісланий з подарунками від св.. Миколая &ndash; солодощі, іграшки, книжки. Закуповуються продукти. В листі поздоровлення з Різдвяними святами і&nbsp; поздоровлення від парафіяльних дітей. Надсилатиметься протягом грудня-січня.</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На сайті і в церкві буде оголошена Акція &laquo;Великодній кошик&raquo; з метою частково наповнити Великодний кошик потребуючих дітей, а також навчити парафіяльних дітей ділитися з бідними. Також закупимо продукти і засоби гігієни. Парафіяльні діти на катехитичних заняттях напишуть привітання і вкладуть у кошик кожній дитині. Подарунки надсилатимуться протягом квітня- травня.</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У травні підведеться підсумок з написанням звіту про&nbsp; використання коштів і про кількісні і якісні показники, здобутий досвід. Буде визначено двох дітей, які за дозволом опікунів відпочиватимуть на морі з парафіяльними дітьми в&nbsp; оздоровчо-катехитичному таборі в червні 2010 року.</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вміст гуманітарних пакетів. </span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Продукти: олія - 11 грн, цукор -7 грн, консерви, 2шт &ndash; 20 грн, печиво &ndash; 6 грн, згущене молоко &ndash; 6 грн, цукерки, чай / паска &ndash; 20 грн. Разом 70 грн.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Засоби гігієни: зубна паста, щітка &ndash; 16 грн, мило, 4 шт -12 грн, пральний порошок -12 грн. Разом 40 грн.<strong> </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Виконавці проекту</strong>: 2 соціальних працівники<strong> <br /></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Моніторинг </strong>Зворотній зв\'язок. Отримання гуманітарного пакету підтверджується користувачем проекту надсиланням на нашу адресу поштової листівки, яка вкладається в посилку.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Раз на квартал перебіг діяльності проекту буде висвітлений на вебсторінці Аскольдової могили. Будуть подані листи дітей з проханнями (потребами) для залучення небайдужих людей доброї волі, які зможуть долучитися до підтримки дітей.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Бюджет проекту</strong></span></span></p>\r\n<table style=\"text-align: justify;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"38\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>№</strong></span></span></p>\r\n</td>\r\n<td width=\"170\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Стаття витрат</strong></span></span></p>\r\n</td>\r\n<td width=\"143\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Розрахунок</strong></span></span></p>\r\n</td>\r\n<td width=\"84\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Грант, грн</strong></span></span></p>\r\n</td>\r\n<td width=\"83\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Грант,$</strong></span></span></p>\r\n</td>\r\n<td width=\"140\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Внесок &nbsp;організації </strong></span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1</span></span></p>\r\n</td>\r\n<td width=\"170\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Конверти з марками</span></span></p>\r\n</td>\r\n<td width=\"143\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">400 шт х 2 грн</span></span></p>\r\n</td>\r\n<td width=\"84\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">800</span></span></p>\r\n</td>\r\n<td width=\"83\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">100</span></span></p>\r\n</td>\r\n<td width=\"140\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">2</span></span></p>\r\n</td>\r\n<td width=\"170\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Гум/пакет: продукти</span></span></p>\r\n</td>\r\n<td width=\"143\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">50 осіб х 70 грн</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">х 3 рази</span></span></p>\r\n</td>\r\n<td width=\"84\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">10500</span></span></p>\r\n</td>\r\n<td width=\"83\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1300</span></span></p>\r\n</td>\r\n<td width=\"140\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">3</span></span></p>\r\n</td>\r\n<td width=\"170\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Гум/пакет:   засоби гігієни</span></span></p>\r\n</td>\r\n<td width=\"143\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">50 осіб х 40 грн</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">х 3 рази</span></span></p>\r\n</td>\r\n<td width=\"84\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">6000</span></span></p>\r\n</td>\r\n<td width=\"83\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">750</span></span></p>\r\n</td>\r\n<td width=\"140\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">4</span></span></p>\r\n</td>\r\n<td width=\"170\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Поштові витрати</span></span></p>\r\n</td>\r\n<td width=\"143\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">400 г/п х 15 грн</span></span></p>\r\n</td>\r\n<td width=\"84\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">6 000</span></span></p>\r\n</td>\r\n<td width=\"83\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">750</span></span></p>\r\n</td>\r\n<td width=\"140\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">5</span></span></p>\r\n</td>\r\n<td width=\"170\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Одяг, взуття, література художня і духовна</span></span></p>\r\n</td>\r\n<td width=\"143\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n<td width=\"84\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n<td width=\"83\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n<td width=\"140\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">*</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">6</span></span></p>\r\n</td>\r\n<td width=\"170\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Праця волонтерів</span></span></p>\r\n</td>\r\n<td width=\"143\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">2 особи</span></span></p>\r\n</td>\r\n<td width=\"84\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n<td width=\"83\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n<td width=\"140\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">*</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">7</span></span></p>\r\n</td>\r\n<td width=\"170\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Поштовий ящик</span></span></p>\r\n</td>\r\n<td width=\"143\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">400 шт</span></span></p>\r\n</td>\r\n<td width=\"84\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n<td width=\"83\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n<td width=\"140\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">*</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>8</strong></span></span></p>\r\n</td>\r\n<td width=\"170\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Разом</strong></span></span></p>\r\n</td>\r\n<td width=\"143\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n</td>\r\n<td width=\"84\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>23300</strong></span></span></p>\r\n</td>\r\n<td width=\"83\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>2 900</strong></span></span></p>\r\n</td>\r\n<td width=\"140\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>',NULL,NULL,1813,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(864,'P','2008-11-04',1,NULL,'Note','Солідарність Церков та релігійних організацій у сфері медичної опіки ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Прагнучи до врегулювання засад взаємодії у справі гуманізації медичної опіки на основі поваги прав та гідності людини; традиційних духовних, морально-етичних та культурних цінностей, висловлено пропозицію підписати Угоду про співпрацю між МОЗ та ВРЦіРО і створити при Міністерстві Громадську раду з представників Церков та релігійних організацій України.</span></span></p>','<p>&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Під такою темою відбулася у понеділок, 3 листопада, міжконфесійна нарада. Ініціатива її проведення та підтримка в її організації&nbsp; надійшли від УГКЦ в контексті проведення Суспільного тижня.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Нарада стала ще одним кроком в налагодженні співпраці Міністерства охорони здоров\'я України та Церков і релігійних організацій України у сфері медичної опіки. В ході наради було обговорено питання процесу формалізації державно-конфесійних стосунків у сфері медичної опіки, створення робочої групи з питань розробки спільної стратегії щодо абортів, питання медичного капеланства (на прикладі госпісів) та питання імунопрофілактики населення. Прагнучи до врегулювання засад взаємодії у справі гуманізації медичної опіки на основі поваги прав та гідності людини; традиційних духовних, морально-етичних та культурних цінностей, висловлено пропозицію підписати Угоду про співпрацю між МОЗ та ВРЦіРО і створити при Міністерстві Громадську раду з представників Церков та релігійних організацій України.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Департамент інформації УГКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>\r\n<p>&nbsp;</p>',NULL,NULL,610,5,1,42),
(865,'P','2008-11-05',1,NULL,'Note','Архиєпископ Мечислав Мокшицький – Голова Конференції Римо-Католицьких Єпископів України','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Під час зустрічі римо-католицьких єпископів України, що від 3 до 5 жовтня проходить в селі Зарічани Житомирської області, обрано нового Голову Конференції Римо-Католицьких Єпископів України.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Під час зустрічі римо-католицьких єпископів України, що від 3 до 5 жовтня проходить в селі Зарічани Житомирської області, обрано нового Голову Конференції Римо-Католицьких Єпископів України. Ним став архиєпископ-митрополит Львівський латинського обряду Мечислав Мокшицький. Обрано також його заступника, яким став єпископ Леон Дубравський, Ординарій Кам\'янець-Подільської дієцезії. Секретарем Конференції переобрано владику Мар\'яна Бучека, єпископа-коад\'ютора Харківсько-Запорізької дієцезії.<br /><br />Пригадаємо, що протягом 16 років Конференцію єпископів РКЦ в Україні очолював кардинал Мар\'ян Яворський, вислужений архиєпископ-митрополит Львівський латинського обряду. 21 жовтня 2008&nbsp;р. Папа Венедикт&nbsp;XVI прийняв його зречення з уряду, подане у зв\'язку з досягненням відповідного віку.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/<br /></span></span></p>',NULL,NULL,617,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(866,'P','2009-09-29',1,NULL,'Note','Спіробітники Державної кримінально-виконавчої служби України здійснили прощу до Унева','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3300,200,Pilgr_DKVS.jpg\" alt=\"\" />На свято Воздвиження Чесного і Животворящого Господнього Хреста 26 - 27 вересня 2009 року Свято-Успенська Унівська лавра приймала у паломництві працівників пенітенціарної системи України. Перемишляни й Унів розташовані на Львівщині на перетині доріг поміж Львовом, Івано-Франківськом і Тернополем. Тому саме представники Кримінально-виконавчої служби від Управлінь Державного департаменту у Львівській, Івано-Франківській і Тернопільській областях взяли участь у цій прощі.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3300,200,Pilgr_DKVS.jpg\" alt=\"\" />На свято Воздвиження Чесного і Животворящого Господнього Хреста 26 - 27 вересня 2009 року Свято-Успенська Унівська лавра приймала у паломництві працівників пенітенціарної системи України. Перемишляни й Унів розташовані на Львівщині на перетині доріг поміж Львовом, Івано-Франківськом і Тернополем. Тому саме представники Кримінально-виконавчої служби від Управлінь Державного департаменту у Львівській, Івано-Франківській і Тернопільській областях взяли участь у цій прощі.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,3300,200,Univ_dkvs.JPG\" alt=\"\" />&laquo;Справедливість, віра, милосердя!&raquo; - це гасло було написано на хоругвах кожного з обласних управлінь ДКВС, віддзеркалюючи глибину християнського розуміння завдань цієї складної професії пенітенціарія. Проща розпочалася молебнем у церкві Св. Миколая у Перемишлянах &ndash; у храмі, де служив блаженний священомученик Омелян Ковч, в&rsquo;язень концтабору &laquo;Майданек&raquo;. На проповіді о. Богдан Манишин &ndash; нинішній настоятель храму, який приймав прочан &ndash; наголосив на важливості молитви в житті християн: &laquo;Оскільки паломництво є особливим різновидом молитви, то нею має бути освячена уся наша земна подорож, адже життя, сповнене спокус і хрестів, які неможливо збагнути й перенести без молитви.&raquo; Після освячення хоругв, паломники вирушили від Перемишлян до Унева. Серед прочан були також члени родин пенітенціарних службовців, в&rsquo;язничні душпастирі, студенти Львівської й Дрогобицької семінарій. Поруч із своїми співслужбовцями йшло керівництво Управлінь Держдепартаменту:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; генерал внутрішньої служби Василь Ільницький та його заступник соціально-виховної та психологічної роботи майор Мирослав Демків (Управління ДКВС України у Львівській області)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; підполковник внутрішньої служби Василь Бурак &ndash; заступник з питань соціально-виховної та психологічної роботи (Управління ДКВС України в Івано-Франківській області)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; підполковник внутрішньої служби Руслан Вовк (Управління ДКВС України у Львівській області).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3300,200,DKVS_UNIV.jpg\" alt=\"\" />Подорожуючих зустріли величні дзвони й ігумен монастиря високопреподобний отець Венедикт гостинно прийняв прочан. Святкова вечірня з литією, акафістове моління перед Унівською Чудотворною іконою Богородиці, панахида на чернечій горі наповнили духовну програму прощі. На завершення прочани переглянули фільм &laquo;Парох Майданека&raquo; про Блаженного священомученика Омеляна Ковча та фрагменти документального фільму про Першу Всеукраїнську прощу ДКВС України до Зарваниці у 2008 році.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,3300,200,Univ_dkvs.JPG\" alt=\"\" />27 вересня у Свято-Успенському храмі була відслужена святкова Утреня та Архиєрейська Божественна Літургія, яку очолив Владика Михаїл (Колтун), керівник Відділу у справах душпастирства силових структур України. На проповіді після Євангелія владика Михаїл сказав: &laquo;Нам слід черпати сили Божої для сповнення такої нелегкої служби, щоб нести її відповідально і гідно, ... силою Господнього Хреста долати гріх, що так утруднює наше життя... плекати людяне ставлення до покараних за злочини, адже добро опікає, очищує і освячує душі.&raquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На завершення прочани подякували Церкві за нагоду поглибити розуміння свого християнського покликання і духовну наснагу, якої так потребує кожний пенітенціарний працівник. Вони звернулися з проханням зарезервувати щороку свято Воздвиження Чесного і Животворящого Господнього Хреста для прощі в лавру співробітників Пенітенціарної системи України на що радо погодився ігумен монастиря &nbsp;високопреподобний отець Венедикт. &laquo;Бажаю вам миру у ваші серця, щоб ви та ваші ближні сповнені Божими Дарами повернулися додому&raquo; - сказав отець ігумен.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">диякон Костянтин Пантелей,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">референт УГКЦ з питань в&rsquo;язничного душпастирства</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Додаток</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Фрагменти з </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>КОМПЕНДІУМУ СОЦІАЛЬНОЇ ДОКТРИНИ ЦЕРКВИ</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>ПАПСЬКОЇ РАДИ СПРАВЕДЛИВІСТЬ І МИР</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">РОЗДІЛ ВОСЬМИЙ ПОЛІТИЧНА СПІЛЬНОТА</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">III. ПОЛІТИЧНА ВЛАДА</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">д. Каральна функція</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">402. Для захисту спільного блага легітимна державна влада має право й обов&rsquo;язок визначати покарання, відповідно до тяжкості злочину [Катехизм Католицької Церкви, 2266.]. Перед державою постає подвійне завдання: класти край діям, які порушують права людини і фундаментальні норми громадського співжиття, та відновлювати через каральну систему порядок, порушений кримінальною діяльністю. У правовій державі функцію визначати міру покарання покладено на суд: &laquo;Конституції сучасних держав, встановлюючи відповідні відносини між законодавчою, виконавчою і судовою владою, гарантують останній необхідну незалежність у сфері закону.&raquo; [Катехизм Католицької Церкви, 2266.]</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">403. Покарання &ndash; це не тільки захист громадського порядку і запорука безпеки людей, а й знаряддя виправлення злочинця &ndash; виправлення, яке набуває моральної цінності спокути, коли винуватець добровільно приймає покарання [Катехизм Католицької Церкви, 2266.]. Так досягається подвійна мета: з одного боку, заохочується реінтеграція засуджених у суспільство, а з іншого &ndash; утверджується справедливість, що примирює, справедливість, здатна відновити гармонію в соціальних відносинах, підірвану скоєним злочином. У зв&rsquo;язку з цим, діяльність, до якої покликані тюремні капелани, важлива не лише через свій специфічно релігійний характер, а й через притаманну їй функцію захисту людської гідності засуджених. На жаль, умови, в яких вони відбувають покарання, часто негативно позначаються на їхній гідності, а в&rsquo;язниці стають місцем нових злочинів. І все ж, виправні заклади створюють нагоду вкотре засвідчити християнську турботу про соціальну сферу: &laquo;Я&hellip; у тюрмі був, і ви прийшли до Мене&raquo; (Мт 25, 35,36).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">404. Діяльність інститутів, які визначають кримінальну відповідальність, завжди має особистий характер; вона повинна спрямовуватися на ретельний пошук правди і здійснюватись із цілковитою повагою до гідності і прав людини. Це означає, слід гарантувати дотримання прав що винного, що невинного. Необхідно керуватися юридичним принципом, згідно з яким не можна визначати покарання, якщо злочин ще не доведений. Під час розслідування необхідно суворо дотримуватися заборони застосовувати тортури, навіть у випадку тяжких злочинів: &laquo;Учень Христа відкидає будь-яке застосування таких методів, які нічим не можна виправдати і які жахливо принижують людську гідність як ката, так і жертви&raquo; [Іван Павло II, Звернення до Міжнародного комітету Червоного Хреста, Geneva (15 June 1982), 5: L\'Osservatore Romano, English edition, 26 July 1982, p. 3.]. Міжнародні юридичні документи із захисту прав людини справедливо вказують на заборону тортур як на принцип, який не можна порушувати за жодних обставин. Також неприпустимий &laquo;арешт, єдина мета якого &ndash; отримати важливу для судового процесу інформацію&raquo;&nbsp; [Іван Павло II, Звернення до Італійської Асоціації суддів (31 Березня 2000), 4: AAS 92 (2000), 633.]. Крім того, слід забезпечити, щоб &laquo;судові процеси проводилися швидко: їхня надмірна тривалість стає нестерпною для громадян і зрештою перетворюється на справжню несправедливість&raquo;&nbsp; [[Іван Павло II, Звернення до Італійської Асоціації суддів (31 Березня 2000), 4: AAS 92 (2000), 633.]. Здійснюючи слідство, слідчі мають бути особливо обережними, щоб не порушити принцип презумпції невинності і право звинуваченого на конфіденційність. Зважаючи на те, що і судді можуть помилятися, закон має гарантувати адекватне відшкодування жертвам судових помилок.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">405. Знак надії вбачає Церква в тому, що &laquo;громадська думка чинить все більший опір смертній карі &ndash; навіть якщо суспільство вдається до неї як до інструменту &ldquo;легітимного захисту&rdquo;. Цей опір постає з переконання, що сучасне суспільство спроможне успішно боротися зі злочинністю методами, які знешкоджують злочинця, але не позбавляють його остаточно можливості змінити своє життя&raquo; [Іван Павло II, Енцикліка Evangelium Vitae, 27: AAS 87 (1995), 432.]. Хоча традиційне вчення Церкви &ndash; за умови, що точно встановлено особу і відповідальність винного &ndash; не виключає смертну кару, &laquo;якщо це є єдиний можливий ефективний шлях захисту життя людей від незаконного агресора&raquo; [Катехизм Католицької Церкви, 2267.]. Однак, перевагу слід надавати безкровним засобам запобігання і покарання, адже &laquo;вони більш відповідні конкретним умовам спільного блага і гідності людської особи&raquo; [Катехизм Католицької Церкви, 2267.]. Збільшення кількості країн, які вдаються до заходів, спрямованих на законодавче скасування або тимчасове припинення смертної кари, свідчить про те, що випадки, коли злочинця конче потрібно стратити, трапляються &laquo;дуже рідко, а може, й ніколи&raquo; [Катехизм Католицької Церкви, 2267.]. Все більша відраза громадської думки до смертної кари і різні заходи щодо її скасування чи призупинення, &ndash; яскраве свідчення росту моральної свідомості.</span></span></p>',NULL,NULL,1817,5,1,30);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(867,'P','2008-11-04',1,NULL,'Note','Про Суспільний тиждень у програмі \"Шустер. Live\" ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\"Солідарність нам конечно потрібна, бо це є складова частина життя суспільства. Суспільство не може існувати, коли люди не звертають увагу одне на одного. Коли люди живуть для себе, тоді це велика трагедія. Щоб суспільство жило - потрібно солідарності. Це означає, що кожний має відчувати, що він потребує іншого і одночасно - що інші потребують мене, моєї допомоги\", - зокрема сказав Блаженніший Любомир.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">3 листопада у Києві в рамках Суспільного тижня: \"На шляху до солідарності українського суспільства\", який проводить Українська Греко-Католицька Церква, о 19.30 на ТРК \"Україна\" у програмі \"Шустер. Live\" відбувся публічний діалог та дебати у прямому ефірі щодо прав і свобод у міжрелігійних та суспільних відносинах. Участь у дебатах взяли представники трьох монотеїстичних релігій: Блаженніший Любомир, Глава УГКЦ, рабі Яків Дов Блайх, головний рабин Києва й України, та шейх Ахмед Тамім, голова Духовного управління мусульман України.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,1591,200,RTEmagicC_shuster1_01.jpg.jpg\" alt=\"\" />\"Солідарність нам конечно потрібна, бо це є складова частина життя суспільства. Суспільство не може існувати, коли люди не звертають увагу одне на одного. Коли люди живуть для себе, тоді це велика трагедія. Щоб суспільство жило - потрібно солідарності. Це означає, що кожний має відчувати, що він потребує іншого і одночасно - що інші потребують мене, моєї допомоги\", - зокрема сказав Блаженніший Любомир.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Крім головної теми учасники програми обговорили питання душевної рівноваги і спокою (не зважаючи на різні кризи), викладання у школах релігієзнавчих предметів, віротерпимості та екуменічного діалогу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Запрошуємо вас переглянути основні фрагменти цієї програми.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Департамент інформації УГКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>\r\n<p>&nbsp;</p>',NULL,NULL,611,5,1,42),
(868,'P','2008-11-05',1,NULL,'Rss','Триденна акція «За життя»','<p>З 16 червня з благословення Апостольського адміністратора Бучацької єпархії УГКЦ о. Дмитра Григорака, ЧСВВ було створено рух «За життя»., метою якого є пропаганда християнських цінностей у ставленні до людського життя від моменту його зачаття і до природої смерті. Головою християнського руху «За життя» став отець Ігор Довганюк.</p>\n<p>Першим кроком цієї організації було підготовка звернення до Президента України, Верховної Ради та Уряду про заборону абортів в Україні, яке підписали представники ведучих Церков Тенопільської області. У жовтні силами мирянського руху організовано триденну акцію „За життя\", у якій, крім членів цього руху взяли участь доктор медицини з Хорватії Антун Лісец та викладач морального богослов\'я Івано-Франківської Теологічної Академії о. Руслан П\'яста. Під час акції прочитано низку лекцій для різних верств населення м. Чорткова, Бучача та Тернополя: лікарів, вчителів і учнів, студентів, представників влади, організовано круглий стіл для представників місцевих мас-медіа. Кульмінацією цієї акції стло дійство на центральному майдані міста, де відбувся духовний концерт за участю місцевих та приїжджих аматорів сцени та відправлена панахида за  вбитих внаслідок абортів дітей, яку соборно разом з Апостольським адміністратором Бучацької єпархії УГКЦ о. Дмитром Григораком відслужили священики чортківського деканату.</p>\n<p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=89:trydenna-akcija-lza-zhyttjar&catid=29:propovidi-cat&Itemid=34',620,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(869,'P','2009-10-05',1,NULL,'Rss','Жіночий ансамбль «Аніма»','<p>Для мене пісня, наче поклик долі,</p>\n<p>Моє натхнення, дум стрімких політ.</p>\n<p>Я йду із нею по життєвім полі</p>\n<p>Й не зраджу їй до склону своїх літ.</p>\n<p>Кружляй же, завжди, пісне, на орбіті,</p>\n<p>Брини й мужай в різноголоссі нот,</p>\n<p>Про рідний край і нашу пісню в світі</p>\n<p>Не забувай, співучий мій народ!</p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=439:zhinochy-ansambl-lanimar&catid=25:parafija-cat&Itemid=27',1825,NULL,1,26),
(870,'P','2008-11-04',1,NULL,'Note','Незгасима свічка у Тернополі','<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Екуменічні молитви до 75-их роковин Голодомору в Україні Екуменічні молитви до 75-их роковин Голодомору в Україні.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Містами України проходить міжнародна естафета &laquo;Незгасима Свічка&raquo;, під час якої відбуваються екуменічні молитви. </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У рамках акції &laquo;Незгасима свічка&raquo; 31 жовтня у Львові біля пам\'ятника Тарасові Шевченку відправлено поминальну панахиду, під час якої разом молилися католики двох обрядів і православні. </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">21 листопада символічна &laquo;Незгасима свічка&raquo;буде доставлена до міста Києва, де на державному рівні відбудуться заходи зі вшанування пам&rsquo;яті жертв Голодомору в Україні.<br /><br />(для Радіо Ватикану, Костянтин Чавага)</span></span></p>','<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Екуменічні молитви до 75-их роковин Голодомору в Україні Екуменічні молитви до 75-их роковин Голодомору в Україні.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Містами України проходить міжнародна естафета &laquo;Незгасима Свічка&raquo;, під час якої відбуваються екуменічні молитви. </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br />У рамках акції &laquo;Незгасима свічка&raquo; 31 жовтня у Львові біля пам\'ятника Тарасові Шевченку відправлено поминальну панахиду, під час якої разом молилися католики двох обрядів і православні. </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">21 листопада символічна &laquo;Незгасима свічка&raquo;буде доставлена до міста Києва, де на державному рівні відбудуться заходи зі вшанування пам&rsquo;яті жертв Голодомору в Україні.<br /> <br /> (для Радіо Ватикану, Костянтин Чавага)</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">3 листопада у парку імені Тараса Шевченка, біля хреста, що поблизу пристані над Ставом відбулася міжконфесійна панахида та народне віче за тих, хто загинув у часи Голодомору 1932-1933 років. Опісля відбулася хода вулицями Тернополя до обласного драматичного театру, у фойє якого демонструвалася виставка світлин. А на сцені театру митці показали виставу \"Сльози Божої Матері\".</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Таким чином Тернопільщина долучилася до міжнародної акції \"Незгасима свічка\", яка відбувається в рамках вшанування загиблих під час Голодомору 1932-1933 років.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Символічна свічка подорожує країнами світу з 1 квітня 2008 року, вона вже була у 33 країнах, де є найбільша українська діаспора. В Україні акція стартувала 26 жовтня у Києві. Перед тим, як потрапити на терени Тернопільщини, свічка побувала на території Київської, Житомирської та Рівненської, Волинської, Івано-Франківської областей, а з Тернополя вона прямуватиме на Буковину.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Символічна свічка виготовлена з ініціативи Всеукраїнської організації \"Братство бджолярів України\". Вона є унікальним скульптурним твором, відлита з натурального воску, у вигляді снопу колосків, до якого тягнуться руки людей. Зовні, на бічну поверхню свічі, намотано у вигляді кільця кілька витків колючого дроту. Висота скульптури (без основи) - 82 см. Найбільший діаметр - 65 см. Маса (разом з основою) - 200 кг.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Акція завершиться 22 листопада у Києві - на відкритті Меморіалу жертв Голодомору 1932-1933 років. Після цього свічу буде передано до музею Голодомору, який відкриють у рамках Меморіалу біля стін Києво-Печерської Лаври.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Надія ШПОДАРУНОК,<br /> кореспондент газети &laquo;Божий сіяч&raquo;</span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>',NULL,NULL,613,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(871,'P','2008-11-05',1,NULL,'Note','Перший семінар Католицько-Мусульманського Форуму','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Католицько-Мусульманський Форум був заснований у березні 2008&nbsp;р., Папською Радою у справах міжрелігійного діалогу та представниками ісламу, як наслідок окремого діалогу, започаткованого листом, який 13 жовтня 2007&nbsp;р. до Папи Венедикта&nbsp;XVI і до інших Глав Церков та Церковних Спільнот, скерували 138 видатних особистостей ісламу.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /> У вівторок, 4 листопада 2008&nbsp;р., у Ватикані розпочався 1-й семінар, організований Католицько-Мусульманським Форумом, що триватиме три дні. Тема семінару - &laquo;Любов до Бога і любов до ближнього&raquo;, розглядається під двома аспектами. Першого дня учасники дискутували про &laquo;богословські та духовні фундаменти&raquo;, натомість другий день присвячений поглибленню тематики гідності людської особи та взаємної пошани. Про кожен аспект як католицька, так і мусульманська сторони, представляють свою доповідь, що є основою для подальшої дискусії.<br /><br />У четвер, 6 листопада, останній день семінару, передбачено аудієнцію зі Святішим Отцем Венедиктом&nbsp;XVI, а пополудні у Папському Григоріянському Університеті відбудеться прилюдна дискусія, під час якої буде представлено спільну Декларацію. У семінарі беруть участь по 29 представників від кожної сторони, між якими - експерти, радники та представники релігійної влади.<br /><br />Католицько-Мусульманський Форум був заснований у березні 2008&nbsp;р., Папською Радою у справах міжрелігійного діалогу та представниками ісламу, як наслідок окремого діалогу, започаткованого листом, який 13 жовтня 2007&nbsp;р. до Папи Венедикта&nbsp;XVI і до інших Глав Церков та Церковних Спільнот, скерували 138 видатних особистостей ісламу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/<br /></span></span></p>',NULL,NULL,615,5,1,43),
(872,'P','2008-11-05',1,NULL,'Rss','Про Сорок першу сесію Синоду Єпископів Києво-ГалицькогоВерховного Архиєпископства УГКЦ  ','<p>29-30 жовтня 2008 року у Львові-Брюховичах відбулася Сорок перша сесія Синоду Єпископів Києво-Галицького Верховного Архиєпископства УГКЦ. На сесійних засіданнях були присутні 17 членів Митрополичого Синоду та запрошений гість Преосвященний Владика Славомир (Мікловш), Єпарх Криживецький, що у Словенії, Хорватії та Боснії і Герцеговині.</p>\n<p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=90:pro-sorok-pershu-sesiju-synodu-jepyskopiv-kyjevo-galyckogoverhovnogo-arhyjepyskopstva-ugkc-&catid=27:gk-v-ukrajini-cat&Itemid=57',621,NULL,1,26),
(873,'P','2008-11-09',1,NULL,'Note','Фотовиставка про беатифікацію сестри Марти Вєцкої','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Урочистість беатифікації монахині Згромадження Сестер Милосердя св.&nbsp;Вінкентія де Поля сестри Марти Вєцкої, яку в травні цього року у Львові довершив Державний Секретар Ватикану кардинал Тарчізіо Бертоне, знайшла відображення в світлинах молодого львівського фотографа Данути Грещук. Її фотовиставка &laquo;Святі серед нас&raquo;.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Урочистість беатифікації монахині Згромадження Сестер Милосердя св.&nbsp;Вінкентія де Поля сестри Марти Вєцкої, яку в травні цього року у Львові довершив Державний Секретар Ватикану кардинал Тарчізіо Бертоне, знайшла відображення в світлинах молодого львівського фотографа Данути Грещук. Її фотовиставка &laquo;Святі серед нас&raquo;, співорганізатором якої стало Управління Львівської архидієцезії Римо-Католицької Церкви, 6 листопада відкрито у Львівському музеї історії релігії. У відкритті виставки взяли участь і виступили Секретар Конференції Римо-Католицьких Єпископів України владика Мар\'ян Бучек та представники греко-католицького і православного духовенства.<br /><br />Для Радіо Ватикану, Костянтин Чавага</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/<em><br /></em></span></span></p>',NULL,NULL,623,5,1,42);
