INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(988,'N','2010-03-28',1,NULL,'Rss','Вербна Неділя на Аскольдовій Могилі','<p align=\"right\"><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3500,200,5.JPG\" />«І ми, як діти, несучи знамена перемоги, Тобі</p>\n<p align=\"right\">Переможцеві смерти кличемо: Осанна на висотах,</p>\n<p align=\"right\">Благословен грядущий в ім’я Господнє»</p>\n<p align=\"right\">( Тропар Квітневої Неділі)</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=514:verbna-nedilja-na-askoldovi-mogyli&catid=25:parafija-cat&Itemid=27',2031,NULL,1,1,26),
(989,'P','2008-11-20',1,NULL,'Note','У Львові вшановують блаженну Йосафату Гордашевську','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">20 листопада - день Преподобної Матері Йосафати Гордашевської. Про її життя написано багато спогадів та книг. Воно було коротке, але дуже цікаве та захоплююче. Сестра Йосафата була тією свічкою, яка яскраво горіла для Бога і для людей.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">20 листопада - день Преподобної Матері Йосафати Гордашевської. Про її життя написано багато спогадів та книг. Воно було коротке, але дуже цікаве та захоплююче. Сестра Йосафата була тією свічкою, яка яскраво горіла для Бога і для людей. Вона повністю віддала іншим світло і тепло свого життя. Сестри Служебниці у своїх спільнотах в усьому світі урочисто відзначають свято співзасновниці свого Згромадження. <br /><br />Двері Провінційного дому Сестер Служебниць у м.&nbsp;Львові сьогодні відчинені для усіх бажаючих прийти та помолитися біля мощей Преподобної Йосафати. Діти, молодь та дорослі протягом дня чисельно приходять, щоб взяти участь у богослужіннях (вервицях, Молебнях, Акафістах) та просити у Преподобної Йосафати молитовного заступництва перед престолом Всевишнього. 20 листопада 2008 р. усі присутні мали нагоду переглянути відеофільм &laquo;Преподобна Йосафата Гордашевська - світло з України&raquo; та з уст сестер почути багато розповідей про блаженну.<br /><br />На святкування дня Преподобної Йосафати в Україну приїхала з Риму сестра Дженес Солюк, Головна Настоятелька Сестер Служебниць. Сьогодні, після Божественної Літургії в каплиці Українського Католицького Університету, сестра Дженес звернулася до студентів із словом про Преподобну Йосафату, а також побувала на посвяченні нової каплиці на честь блаженної. У Львівському інституті банківської справи організовано духовний вечір.<br /><br /><strong><em>Для Радіо Ватикану,</em></strong><strong><em> </em></strong><strong><em>Сестри Служебниці, Львів</em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/ </span></span><strong><em><br /></em></strong></p>',NULL,680,5,1,NULL,42),
(990,'P','2008-11-20',1,NULL,'Note','Наша церква відкрита для усіх','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,1663,200,CRW_2094.jpg\" alt=\"\" />Сьогодні до нашої церкви св. Миколая Чудотворця на Аскольдові Могилі завітали з екскурсією школярі однієї з київських шкіл. Кожна дитинка поставила свічечку перед образами і у думках звернулися до Бога так як уміє. На згадку про нашу церкву діти придбали собі, хрестики, вервечки, молитовнички. Можливо сьогоднішній день залишиться у їхній пам\'яті надовго.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,1663,200,CRW_2094.jpg\" alt=\"\" />Сьогодні до нашої церкви св. Миколая Чудотворця на Аскольдові Могилі завітали з екскурсією школярі однієї з київських шкіл. Діти правдиво зізнавалися, що не усі ходять до церкви, не знають як правильно хреститися та молитися і багато хто не знає про церкву взагалі. Прикро, що батьки не прививають дітям любові до Бога, не навчають їх християнським правдам. Але потішило те, що практично кожна дитинка поставила свічечку перед образами і у думках звернулися до Бога так як уміє. На згадку про нашу церкву діти придбали собі, хрестики, вервечки, молитовнички. Можливо сьогоднішній день залишиться у їхній пам\'яті надовго, а батьки дослухаються до розповідей своїх дітей і зможуть пояснити що таке християнство, його вчення, та й самі навідаються у храм Божий.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Матеріал та фото Любові Михайлюк</span></span></p>\r\n<p>&nbsp;</p>',NULL,683,5,1,NULL,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(991,'P','2010-04-09',0,NULL,'Note','Послання до жінок, що перебувають у місцях позбавлення волі','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Христос Воскрес!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорогі сестри, наша громада церкви св. Миколая Чудотворця на Аскольдовій Могилі, м. Київ, щиро вітає Вас зі святом воскресіння Христового, і бажає, щоб воскреслий Господь укріпив вашу віру, подав істинне покаяння і мир в серця, бажаємо Вам здоров&lsquo;я і швидкого повернення до дому.</span></span></p>','<p style=\"text-align: justify;\"><em><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Христос Воскрес!</span></span></em></p>\r\n<p style=\"text-align: justify;\"><em><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорогі сестри, наша громада церкви св. Миколая Чудотворця на Аскольдовій Могилі, м. Київ, щиро вітає Вас зі святом воскресіння Христового, і бажає, щоб воскреслий Господь укріпив вашу віру, подав істинне покаяння і мир в серця, бажаємо Вам здоров&lsquo;я і швидкого повернення до дому.</span></span></em></p>\r\n<p style=\"text-align: justify;\"><em><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Наше невелике служіння для жінки яка зробила помилку у своєму житті і зараз намагається прислужитись Вам. Ми отримуємо від Вас багато прохань, але знову наголошую - наша мета допомагати вашим дітям, які залишились без вашої опіки. Ми віримо, що це і ваше материнське бажання допомогти своїм дітям. Тому дуже просимо - у своїх листах постійно нагадуйте про своїх дітей, вказуйте їхні адреси. Для дітей ми намагатимемось завжди знайти допомогу.</span></span></em></p>\r\n<p style=\"text-align: justify;\"><em><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Просимо Вас долучатись до молитви, вивчати Біблію. Ми подаємо Вам уривок з Євангелія, щоб Ви прочитали про страшний суд який прийде для всіх.</span></span></em></p>\r\n<p style=\"text-align: justify;\"><em><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ми намагатимемось допомогти Вам необхідними ліками, при наявності рецептів з вашої санчастини. Ми старатимемось відповідати на всі ваші листи, та висилати посилки стає все важче.</span></span></em></p>\r\n<p style=\"text-align: justify;\"><em><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я дякую сестрам, які вишили ікони, намалювали малюнки і вислали нам. Надалі проситиму Вас - зголошуйтесь ті, хто може вишивати, малювати, писати статті до церковної газети. Чекаю ваших відповідей.</span></span></em></p>\r\n<p style=\"text-align: justify;\"><em><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З Богом, священик Ігор.</span></span></em></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Одкровення Івана. Гл.4</span></span></strong></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">ІІ. Перед днем Господнім</span></span></strong></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Небесна Літургія</span></span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Після цього я глянув, і от двері відчинені на небі, і голос перший, котрий я чув, як від сурми, що говорив зо мною, сказав: Зійди сюди, і покажу тобі, що має статися по цьому. 2. І зразу я був у дусі. І от престол стояв на небі, а на престолі &ndash; сидячий. 3. І цей сидячий був подібний з вигляду до каменя яс-писа і сардія, і райдуга навкруг престолу, подібна виглядом до смарагду. 4. Навкруг престолу &ndash; двадцять чотири престоли, а на престолах двадцять чотири старші сиділи, одягнені в одежі білі, і на головах їхніх вінці золоті. 5. Від престолу виходять блискавиці, і голоси, і громи. Сім світильників огненних палають перед престолом, які є сім духів Божих. 6. Перед престолом &ndash; наче море скляне, подібне до кришталю, а в середині престолу і навколо престола чотири істоти, повні очей напереді і ззаду. 7. Істота перша подібна до лева, друга істота подібна до тельця, третя істота мала обличчя, як у людини, а четверта істота подібна до орла летючого. 8. Чотири ці істоти&nbsp; мають кожна собі по шість крил; навколо і всередині повно очей, і невгаваючи ні вдень, ні вночі, промовляють: Свят, свят, свят Господь Бог Вседержитель, що був і є, і приходить.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">9. І коли складають ті істоти славу і честь, і подяку тому, хто сидить на престолі, що живе на віки вічні, 10. падають двадцять чотири старші перед тим, хто сидить на престолі, і поклоняються йому, що живе на віки вічні, і кладуть вінці свої перед престолом, промовляючи: 11. Достойний ти, Господи й Боже наш, прийняти славу, і честь, і силу, бо ти створив усе, і волею твоєю воно існує і створено.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Переклад, коментар о. д-р М. І. Любачівського</span></span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Початок пророчого видіння: Іван описує, що діється в небі. Він змальовує події з Божої точки зору. Небо, згідно з тодішнім уявленням, - це замкнений простір над землею (тристепенний образ світу). Ясновидець має бути свідком, він попадає в екстаз. Ясновидець бачить престол і блиск світла; Постаті Бога він не бачить. Двадцять чотири старші, тобто для кожного покоління Ізраїлю два свідки, які беруть участь у володінні Бога. 5. Блискавки, голоси й громи є знаками присутності Бога. Скляне море, для підкреслення безконечної відстані: досконалості Творця та недовершеності Його створіння, якому Отець Небесний дав право свободи вибору та здатність сказати: так Отче, хай станеться воля Твоя в мені. Тоді з безконечної відстані між Богом і створінням Триєдиний Бог діє в людині наповнюючи її святістю, готує її до вічного життя в благодаті та спогляданні&nbsp; Царства Небесного. Чотири живі істоти: чотири престольні ангели; вони повні очей, це означає, що немає нічого скритого перед Богом. Чотири істоти символізують чотири стихії: лев &ndash; вогонь; віл &ndash; землю; скорпіон із людським обличчям&nbsp; - воду; орел &ndash; повітря. Вони стали символами&nbsp; чотирьох євангелістів: Матвея, Марка, Луку та Івана. Небесна Богослужба: спасенні прославляють Бога, котрий приведе створіння до мети.</span></span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></strong></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Коментар о. д-р Януша Марії Анджиєвського</span></span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Щастя це бути завжди з Богом. В тюрмі чи на волі. Масонство добре попрацювало, щоб убити в нас Бога&hellip;Бачити Ісуса та свідчити про Нього це може допомогти нам та іншим.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo; Блаженні вбогі, бо ваше царство Боже. Блаженні голодні нині, бо насититеся. Блаженні, що плачете нині, бо будете сміятися. Блаженні будете, коли вас ненавидітимуть люди, коли вас вилучать, коли ганьбитимуть вас&nbsp; та коли викинуть&nbsp; як безчесне ваше ім&lsquo;я Сина чоловічого ради. Радійте того дня й веселіться, бо ваша нагорода велика на небі. Так само бо батьки їхні поводилися з пророками&raquo;. ( Єванг. Луки 6,21 )</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;Частіше займаймо свою уяву сценами, подіями, які описані у Святому Письмі. Так відкривається наше серце на Бога.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Наш час вигаданий сатаною, який хоче інформаційним маренням вивести людину з реального важкого світу в легкий світ самообману.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Якщо зараз є реальна любов, то і через 30 років буде реальне повернення до радості з цією людиною. Реальне добро це те, що дає нам надію на майбутнє. В Небесах вертаються до реального та доброго. Наші добрі справи, як дорогоцінне каміння. Добро це і відкрити двері будь кому, щоб увійшов перший. В любові не має дрібниць. Всі порухи тіла та душі мають бути наповнені любов&raquo;ю. Ми щезаємо із Землі, а добро залишається.</span></span></p>',NULL,2040,5,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(992,'P','2008-11-20',1,NULL,'Note','Угода про співпрацю між УГКЦ та командуванням ВМС ЗСУ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Одним із етапів втілення досягнутих домовленостей є проведення о. Миколою Квичем, відповідальним за капеланство в Одесько-Кримському екзархаті УГКЦ тематичних зустрічей із особовим складом ВМС та розвиток капелнських структур в Севастопольському Військово-морському інституті ім. П.С. Нахімова, чим займається Центр військового капеланства ЛА УГКЦ.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Як повідомлялось раніше 19 червня в штабі Військово-Морських Сил Збройних Сил України відбулося підписання угоди про співпрацю між Українською Греко-Католицькою Церквою та командуванням ВМС ЗСУ в ділянці забезпечення душпастирської опіки особового складу ВМС. Одним із етапів втілення досягнутих домовленостей є проведення о. Миколою Квичем, відповідальним за капеланство в Одесько-Кримському екзархаті УГКЦ тематичних зустрічей із особовим складом ВМС та розвиток капелнських структур в Севастопольському Військово-морському інституті ім. П.С. Нахімова, чим займається Центр військового капеланства ЛА УГКЦ.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">16 листопада представники цього Центру о. Степан Сус та бр. Андрій Зелінський, ТІ за підтримки командування інституту організували екскурсію для курсантів цього навчального закладу по визначних історичних та культурних місцях Південного берегу Криму. Під час поїздки мандрівники проїхали мальовничими берегами Чорного моря, оглянули храм Воскресіння Христового у м. Форосі, милувалися морським пейзажем із Ластівочного гнізда, побували у Левадійському палаці, пройшлися набережною славнозвісного українського курорту м. Ялти та завітали до місцевого Золотоустівського храму.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Учасники екскурсії інтелектуально збагатились історико-краєзнавчим матеріалом, запропонованим досвідченим гідом-екскурсоводом.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Бадьорий настрій та належну духовну атмосферу в курсантському товаристві підтримували військові капелани.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На зворотному шляху курсанти під гітару виконували добре їм відомі військові пісні. До Севастополя майбутні офіцери ВМС ЗС України повернулись у піднесеному настрої, духовно та культурно збагаченими, належно налаштованими на подальшу службу у лавах захисників Вітчизни.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Подібні екскурсії організовуються ЦВК не вперше. Вони є важливим елементом у системі духовно-патріотичного виховання у вищих військових начальних закладах де працюють представники Центру. Подібні заходи допомагають майбутнім офіцерам ЗСУ виховувати у собі почуття належної любові і поваги до рідної землі та поглиблювати свій духовно-культурний світогляд. Організація спільних поїздок неабияк сприяє зміцненню дружніх стосунків у курсантському колективі.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Інформацію надав Андрій Хомишин</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>\r\n<p>&nbsp;</p>',NULL,684,5,1,NULL,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(993,'P','2010-04-25',0,NULL,'Note','Простягнулася дорога з Бачева в широкий світ …','<p align=\"center\"><strong><em>&nbsp;&nbsp;&nbsp;&nbsp;</em></strong>&nbsp;о. Миколай-Василь Прийма народився 1 травня 1914 року у багатодітній родині Івана Цапа та Теодозії Гадуп&raquo;як в селі Бачів Перемишлянського району, Львівської області.</p>','<p align=\"center\"><strong><em>&nbsp;&nbsp;&nbsp; Простягнулася дорога з Бачева в широкий світ &hellip;</em></strong></p>\r\n<p>&nbsp;о. Миколай-Василь Прийма народився 1 травня 1914 року у багатодітній родині Івана Цапа та Теодозії Гадуп&raquo;як в селі Бачів Перемишлянського району, Львівської області.</p>\r\n<p>&nbsp;Про своє священиче покликання сказав наступне: &laquo;У моєму житті було кілька випадків, коли я знаходився на грані життя і смерті. Перший &ndash; коли мені було 9 років я помер, смерть засвідчили двоє лікарів. Оскільки в нас не було своєї хати, то похорон мав відбуватись у вуйка Івана. Вже все було приготовлено, паламар приніс Псалтир, Хрест. Мене переносили до вуйкової хати. Було слизько, падав дощ. На перелазі вуйко посковзнувся, та й кинув мене на лісковий кіл, що стирчав у штахетах. Я здригнувся і ожив. Але цілий тиждень був сліпий та страшно боліла голова.Другий раз я помер, коли &laquo;за німців&raquo; сидів у львівській тюрмі на вулиці Лоницького. Там я мав червінку і тиф. Вночі мене винесли до трупарні, щоб рано вивезти і викинути у великий рів за Львовом, де лежали тіла тисяч наших братів та сестер. Коли ж ранком зайшли поліцаї, а я вже ходив по трупарні.Третій випадок був у 1944 році , коли мене спіймали більшовики і так били, що я був чорний, як та ряса. Тоді сказали: &laquo;Вибирай: або копаєш собі яму, або ідеш у червону армію&hellip;&raquo; . Я вибрав червону армію і опинився на фронті, на передовій лінії вогню. Багато разів був продірявлений кулями мій одяг, шапка, але жодна куля мене не поранила. Тоді я зрозумів, що Бог мене береже. Я вже знав, що жовнір стріляє, а Бог кулі носить. У тих окопах, де мок по пояс у воді, я сказав: <strong>&laquo;Як вийду з того пекла, то піду вчитися на священника, а Тобі, Матінко Божа збудую церкву&hellip;&raquo; &nbsp;&nbsp;</strong></p>\r\n<p>Миколай-Василь подався в далеку чужину, шукаючи рятунку і захисту від руки окупанта. Там у чужині, відкривається тайна Божого покликання. Богусловську освіту здобуває в семінаріях Голландії і Німеччини. Сан диякона і священника отримує з рук українського єпископа-скитальця Кир Івана Бучка в 1950 році.</p>\r\n<p>Нововисвячений ієрей Василій отримує&nbsp; парохію без певного місця осідку, без конкретних числа вірних, без храму на теренах південно-західної Франції. Про труднощі і жертовність у перші часи священичого служіння о.Василій пише у вірші &laquo;Найкращий титул &ndash; &laquo;Отець&raquo;.</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &hellip;.Не раз без сотика в кишені ,</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; В чужому місті чи селі:</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Самітний Ти, немов в пустині,</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Годинами глядиш в святині -</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чи прийдуть земляки Твої.</p>\r\n<p>Богослужіння священник-скиталець проводив у римо-католицьких храмах або в домах своїх вірних.</p>\r\n<p>Скитаючись&nbsp; селами та містами тільки Боже Провидіння привело о. Василія в Люрд &ndash; центр міжнародних Марійських прощ. На початку о. Василій отримав дозвіл від місцевого єпископа на сталий притулок в головному кафедральному храмі Вервиці (Розер).Цей собор розташований над Гротою Божої Матері. При вході у храм зліва отець декорував престіл іконами Пречистої Богородиці, вишитими рушниками та обрусами і назвав його &ndash; &laquo;вівтар &ndash; України&raquo;. Тільки Пресвята Богородиця у Люрді могла об&rsquo;єднати нашу діаспору та підштовхнути на такий надзвичайний крок &ndash; побудову храму.</p>\r\n<p>Завдячуючи о. Василію в 1970 році у місті Тулузі відкривають пам&rsquo;ятник Тарасу Шевченку а, вулицю називають &laquo;Вулиця України&raquo; .</p>\r\n<p>Церква збудована у 1981 році, у візантійсько-українському стилі. Наша церква в Люрді унікальна, бо єдина не французька. Для о. Прийми будівництво церкви сповнило його бажання служити Матері Божій, як він собі постановив з вдячності після звільнення з концентраційного табору під час другої світової війни. У час коли на Україні діяв тоталітарний режим Радянського Союзу, у церкві Пресвятої Богородиці м. Тулузи була державна символіка&nbsp; &ndash;&nbsp; синьо-жовтий прапор і герб України &ndash; тризуб.</p>\r\n<p>У рідному селі Бачів о. Василій побудував церкву святого Івана Хрестителя та гроту Божої Матері, де з під стіп витікає природне джерело, обрамлене куском граніту, привезеним із Люрду, а саме із скелі, де 18 разів освятила його Матінка Божа.</p>\r\n<p>17 грудня 2004 року у далекій Франції серце о. Василія Прийми перестало битися . За його заповітом тіло перевезено на Україну в родинне село і 9 січня 2005 року в третій день Різдвяних свят передано землі.</p>\r\n<p>&nbsp;&nbsp; &laquo;&hellip;Пишу Вам, друзі і другині,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n<p>Любі сестри і брати:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n<p>Тіло моє в Україні &ndash;</p>\r\n<p>В церкві, в Бачівській святині &ndash;</p>\r\n<p>Після смерті зберегти.</p>\r\n<p>&nbsp;</p>\r\n<p>Хай спочину на горбочку,</p>\r\n<p>На білесенькій горі,</p>\r\n<p>У вишневому садочку,</p>\r\n<p>Під церквою в холодочку</p>\r\n<p>У родинному селі!</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n<p>У Бачів люди приїжджають<strong> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong>Зійди над Бачевом зорею</p>\r\n<p>Паломники з усіх сторін,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Пречиста Діво Пресвята,</p>\r\n<p>Пречисту Діву величають<strong>,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong>Світи, як сонце, над землею</p>\r\n<p>Співаючи Марійський гімн.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Провадь народи до Христа!</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Над Бачевом сонце сходить &ndash; </strong></p>\r\n<p><strong>З-поза Білої гори:</strong></p>\r\n<p><strong>Мати Божа там приходить,</strong></p>\r\n<p><strong>Людей до Христа приводить,</strong></p>\r\n<p><strong>Щоб щасливими були.</strong></p>\r\n<p><strong>У зеленому віночку</strong></p>\r\n<p><strong>Стоїть церква на горбочку &ndash;</strong></p>\r\n<p><strong>Хвала Бачева-села.</strong></p>\r\n<p><strong>Тут святий Іван Хреститель &ndash; </strong></p>\r\n<p><strong>Всіх мешканців покровитель &ndash; </strong></p>\r\n<p><strong>Захищає їх від зла.</strong><strong></strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p align=\"center\"><strong>Бачів має скелю з гроти массабієль</strong></p>\r\n<p>Бачів має скелю з гроти Массабієль &ndash;</p>\r\n<p>Там пісня лунає про марійський край:</p>\r\n<p>У стіп Діви Марії душа променіє &ndash;</p>\r\n<p>І на Україні Мати Божа діє.</p>\r\n<p>Сотні, тисячі народу&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n<p>Прийшли пити живу воду</p>\r\n<p>З бачівських чистих джерел,</p>\r\n<p>Які пречиста дівиця &ndash;</p>\r\n<p>Неба і землі Цариця &ndash;</p>\r\n<p>Як скарб, відкрила нам.</p>\r\n<p>Над Бачевом сонце гріє,</p>\r\n<p>Людська грота там зоріє</p>\r\n<p>Як джерело ласк й потіх:</p>\r\n<p>Дух Господній там витає,</p>\r\n<p>Божий люд благословляє &ndash;</p>\r\n<p>Хто скарби віри зберіг,</p>\r\n<p>Покинув смертельний гріх,</p>\r\n<p>Злого Духа переміг.</p>',NULL,2047,4,1,NULL,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(994,'P','2010-04-28',0,NULL,'Note','Звіт парафіяльної служби милосердя','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Парафіяльна служба завдяки парафіянам п. Ярославу і п. Інні надана матеріальна допомога дітям яких залишила мати (знаходяться у в&rsquo;язниці):</span></span></p>','<table style=\"width: 462px; height: 5050px;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td colspan=\"2\" width=\"618\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Парафіяльна служба завдяки парафіянам п. Ярославу і п. Інні надана матеріальна допомога дітям яких залишила мати (знаходяться у в&rsquo;язниці):</span></span></p>\r\n</td>\r\n<td width=\"91\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сума в грн.</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"3\" width=\"293\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1. Миронович Сергій, м. Донецьк, вул. Сохопина,1 кв.4</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дитині &ndash; Суботіної О.</span></span></p>\r\n</td>\r\n<td width=\"325\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Солодощі: цукор, печиво, халва,шоколад, арахіс</span></span></p>\r\n</td>\r\n<td width=\"91\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">46,81</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"325\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Гігієнічні товари: шампунь, мило, пральний порошок, зубна паста,&nbsp; зубна щітка</span></span></p>\r\n</td>\r\n<td width=\"91\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">42,35</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"325\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Разом на суму:</span></span></p>\r\n</td>\r\n<td width=\"91\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">89,16</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td rowspan=\"3\" width=\"293\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">2. Костюк Ганна, Житомирська обл. Бердичівський р-н., с.Осикова, вул.1-го травня,12</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дитині &ndash; Бойко Людмилі</span></span></p>\r\n</td>\r\n<td width=\"325\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Солодощі: цукор, печиво, халва,шоколад, арахіс.</span></span></p>\r\n</td>\r\n<td width=\"91\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">46,81</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"325\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Гігієнічні товари: шампунь, мило, пральний порошок, зубна паста,&nbsp; зубна щітка, крем для рук</span></span></p>\r\n</td>\r\n<td width=\"91\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">45,58</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"325\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Разом на суму:</span></span></p>\r\n</td>\r\n<td width=\"91\" valign=\"top\">\r\n<p><span st