INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1022,'P','2010-09-13',1,NULL,'Rss','Служба милосердя','<p>Парафіяльна служба милосердя з отцем Анатолієм та Орестою 03 вересня відвідали хворих та самотніх людей. Серед яких Олег, що хворіє на імунодефіцит, п. Світлану в якої хворі ноги, Богу подяка, що вже може ходити.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=576:sluzhba-myloserdja&catid=25:parafija-cat&Itemid=27',2138,NULL,1,1,26),
(1023,'P','2008-11-25',1,NULL,'Note','Папа Римський зустрівся з Католикосом Килікії Вірменської Апостольської Церкви','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Розпочався офіційний візит Католикоса Килікії Вірменської Апостольської Церкви до Папи та Церкви Риму. 24 листопада відбулася зустріч Його Святості Арама&nbsp;І з Святішим Отцем Венедиктом&nbsp;XVI.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Розпочався офіційний візит Католикоса Килікії Вірменської Апостольської Церкви до Папи та Церкви Риму. 24 листопада відбулася зустріч Його Святості Арама&nbsp;І з Святішим Отцем Венедиктом&nbsp;XVI.<br /><br /><img style=\"float: left;\" src=\"image,1681,200,foto_02.jpg\" alt=\"\" width=\"200\" height=\"136\" />Промовляючи до гостя, Папа пригадав свідчення віри, яке протягом століть дали численні вірменські святі та мученики. Говорячи про екуменічний діалог католиків з давніми християнськими Церквами Сходу, Венедикт&nbsp;XVI підкреслив важливий вклад у ньому вірменської Церкви, висловлюючи сподівання, що щоразу глибше пізнання апостольської традиції, яка нас єднає, допоможе давати спільне свідчення духовним та моральним цінностям, без яких неможливий справедливий та гуманний суспільний порядок. Нав\'язавши до того, що резиденція Католикоса Килікії розташована в Лівані, Папа запевнив свою щоденну молитву та турботу за мешканців країни та всього Близького Сходу, вболіваючи над конфліктами, що марнують усі зусилля, спрямовані на поєднання та мир. &laquo;Останнім часом нас засмутило збільшення насильства та переслідувань християн у різних частинах Близького Сходу та в інших місцевостях&raquo;, - сказав Венедикт&nbsp;XVI, зазначаючи, що лише тоді, коли зацікавлені країни можуть самі вирішувати свою долю, а етнічні та релігійні спільноти погодяться на взаємоприйняття та взаємопошану, можна збудувати мир на тривких фундаментах солідарності, справедливості і пошанування законних прав окремих осіб і народів.<br /><br />Католикос Арам&nbsp;І та єпископи і 50 мирян ВАЦ, які супроводжують Предстоятеля, молилися разом з Папою Венедиктом&nbsp;XVI у ватиканській каплиці &laquo;Redemptoris Mater&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,701,5,1,NULL,43),
(1024,'P','2008-11-26',1,NULL,'Note','Апостольська Столиця про потребу реорганізації міжнародної фінансової системи','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Напередодні всесвітньої економічної конференції, яка 29 листопада розпочнеться у столиці Катару місті Доха, Апостольська Столиця закликає до підписання загальносвітового договору про реорганізацію міжнародної фінансової системи.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Напередодні всесвітньої економічної конференції, яка 29 листопада розпочнеться у столиці Катару місті Доха, Апостольська Столиця закликає до підписання загальносвітового договору про реорганізацію міжнародної фінансової системи. У <em>Ноті</em> Папської Ради &laquo;Справедливість і мир&raquo;, затвердженій в Державному секретаріаті Ватикану, окреслено основні моральні питання, які стосуються фінансової системи та розвитку. Вихідною точкою <em>Ноти</em> є нова ситуація країн, що розвиваються, на яку впливають чинники, від них не залежні, а саме - подорожчання продовольства та енергоресурсів і фінансова криза.<br /><br />Важливе місце в документі займає питання необхідності реорганізації міжнародної фінансової системи, щоб убогі країни, які також потерпають від наслідків міжнародних криз, не були виключені з сфери прийняття рішень.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,703,5,1,NULL,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1025,'P','2010-09-14',1,NULL,'Rss','Храм УГКЦ в Одесі: дві сторони однієї медалі','<p>Як не парадоксально це звучить, але конфлікт із будівництвом церкви для українських греко-католиків в Одесі, а точніше фактична заборона його будівництва, свідчить про серйозні проблеми… в українському православ’ї.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=579:hram-ugkc-v-odesi-dvi-storony-odnijeji-medali&catid=27:gk-v-ukrajini-cat&Itemid=57',2141,NULL,1,1,26),
(1026,'P','2011-09-20',1,NULL,'Rss','Різдво Пресвятої Богородиці','<p>Коротка історія</p>\n<p align=\"justify\">Церква не має звичаю святкувати день народження для землі Святих Божих, але їхній день народження для неба - день смерти. Виїмок становлять двоє найбільших Святих у Церкві: Пречиста Діва Марія і св. Іван Христитель. Ми святкуємо не тільки їхнє небесне, але й їхнє земне різдво.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=581:rizdvo-presvjatoji-bogorodyci&catid=27:gk-v-ukrajini-cat&Itemid=57',2149,NULL,1,1,26),
(1027,'P','2008-11-26',1,NULL,'Note','Екологічна енергія у Ватикані','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">26 листопада у Ватикані, а приміщенні Папської Академії Наук, відбулося урочисте відкриття фотогальванічної системи (так званих &laquo;сонячних батарей&raquo;), розміщеної на покрівлі залу ім. Папи Павла&nbsp;VI, відомого місця проведення папських аудієнцій та інших зустрічей, зокрема, концертно-мистецького характеру.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">26 листопада у Ватикані, а приміщенні Папської Академії Наук, відбулося урочисте відкриття фотогальванічної системи (так званих &laquo;сонячних батарей&raquo;), розміщеної на покрівлі залу ім. Папи Павла&nbsp;VI, відомого місця проведення папських аудієнцій та інших зустрічей, зокрема, концертно-мистецького характеру.<br /><br />Зважаючи на заклики Папи Венедикта&nbsp;XVI про необхідність зберігати природне багатство і належне ставлення до нього, як до Божого створіння, Адміністрація Держави-Міста Ватикану розпочала здійснення різних ініціатив, спрямованих на захист навколишнього середовища. Зал Павла&nbsp;VI є першим прикладом таких рішень. Вибір цього місця пояснюється тим, що він збудований у сучасному стилі і його архітектурні властивості дозволяють на розміщення фотогальванічних панелей.<br /><br />Проект системи розробив проф.&nbsp;Лівіо ді Сантоллі з римського університету &laquo;Ля Сапієнца&raquo;, а його здійснення звершила німецька фірма SolarWorld AG, як подарунок для Святішого Отця Венедикта&nbsp;XVI.<br /><br />300 МВат/рік екологічної електроенергії, виробленої сонячним генератором, будуть використані для забезпечення потреб залу Павла&nbsp;VI і суміжних приміщень. Згідно з спеціальними підрахунками, для вироблення такої кількості електроенергії тепловим способом необхідні приблизно 80 тонн нафти, а це спричинює викиди у атмосферу понад 225 тонн вуглекислого газу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,704,5,1,NULL,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1028,'P','2008-11-26',1,NULL,'Rss','Святий священномученик Йосафат','<p style=\"text-align: justify;\"><img style=\"float: right;\" src=\"http://charnetskyj.org.ua/_pu/0/73900.jpg\" /><span style=\"font-size: 12pt; font-family: arial,helvetica,sans-serif;\"> \"Світильником світлим ти засіяв, священномучениче Йосафате, бо як пастир добрий віддав життя своє за вівці\"<em>(Тропар празника).</em></span></p>\n<div style=\"text-align: justify;\"></div>\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt; font-family: arial,helvetica,sans-serif;\">Празник святого священномученика Йосафата для вірних Української Католицької Церкви особливо дорогий, світлий і радісний, бо це празник нашого українського святого, празник нашого брата по крові й кості, празник великого апостола й мученика за святу віру й єдність Церков.</span></p>\n<div style=\"text-align: justify;\"></div>\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt; font-family: arial,helvetica,sans-serif;\">Та не тільки наша Церква, але й ціла Католицька Церква його почитає, його славить, до нього молиться, на його честь будує святині, а його мощі спочивають у серці християнсько-католицького світу, у базиліці святого апостола Петра в Римі. Святий Йосафат - це перший український святий, якого канонізувала Католицька Церква, йому і його мученицькій крові ми завдячуємо, що сьогодні визнаємо правдиву Христову віру та що ми члени правдивої Христової Церкви.</span></p>\n<div style=\"text-align: justify;\"></div>\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt; font-family: arial,helvetica,sans-serif;\">Не думаємо пригадувати тут історію його життя, канонізації чи культу, але радше хочемо звернути увагу на знаменні риси його особи й характеру, за які свята Церква його канонізувала та вписала до переліку святих мучеників. Особу святого Йосафата можна окреслити трьома словами: Він - святий, він - апостол, він - мученик.</span></p>\n<div style=\"text-align: justify;\"></div>\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt; font-family: arial,helvetica,sans-serif;\"> \n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=115:svjaty-svjashchennomuchenyk-osafat&catid=35:istorychni-postati-cat&Itemid=48',705,NULL,1,1,26),
(1029,'N','2010-09-23',1,NULL,'Note','Звіт наданої допомоги дітям','<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>З</strong><strong>віт наданої допомоги дітям, до початку 1 вересyя 2010 навчального шкільного року, дітям, які залишtні батьківської опіки (їх матері ув&rsquo;язнені). </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Болісно дивитися на страждання дітей, яких доля змусила терпіти. Економічна криза та багато інших негативних факторів, призвели до появи в нашому суспільстві безпритульних дітей.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ув&rsquo;язнені<strong> </strong>матері турбуються за своїх дітей, ось слова матері Алли, яка терпить покарання, просить допомоги в школу її донечці Діані.</span></span></p>','<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>З</strong><strong>віт наданої допомоги дітям, до початку 1 вересня 2010 навчального шкільного року, дітям, які залишені батьківської опіки (їх матері ув&rsquo;язнені). </strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Болісно дивитися на страждання дітей, яких доля змусила терпіти. Економічна криза та багато інших негативних факторів, призвели до появи в нашому суспільстві безпритульних дітей.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ув&rsquo;язнені<strong> </strong>матері турбуються за своїх дітей, ось слова матері Алли, яка терпить покарання, просить допомоги в школу її донечці Діані.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пише мати, Діани: &laquo;<em>моей</em><em> дочери</em><em> Дианочке 12 лет, воспитывает ее мама. Мамае 72 года, очень болеет, нужны деньги, чтобы собрать в сентябре Диану в школу, а денег нет. Дианочке нужны школьные принадлежности, сумка ранец, ручки, тетради, фломастеры, линейки, блокнот, школьная форма, а у матери просто денег нет, и на ноги обуви нет. Позвонила я домой,</em> <em>ребенок расстроенный. </em></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Отец Игорь, помогите собрать ребенка в школу, чтобы 1-го сентября она была счастлива. Господи, как мне жить с моей несостоятельностью? Помогите моей доченьке Дианочке.</em>&raquo;&nbsp;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Лист матері Світлани, яка відбуває покарання в Чернігівській виправній колонії:&nbsp; &laquo;<em>У </em><em>меня</em><em> дома остался</em><em> маленький сынок</em><em>, ему</em><em> 7 лет. Он живет</em><em> с моей</em><em> мамой. Маме очень тяжело, а сын мой Женя идет в первый класс. Вы знаете, сколько надо сейчас денег на школу. Извините, но я пишу Вам с надеждой, что Вы поможете. Для себя я ничего не прошу, прошу, помогите моему сыну. Я знаю, что Бог со мной и он услышит мои молитвы.</em>&raquo;.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Мати Алла А&hellip; в листі до нас, просить допомоги її дитині Андрію, в нього закінчується навчання в Університеті, має відбутися випускний вечір, а у нього немає одягу і взуття.&nbsp;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дякуючи Богу, парафіяльна служба церкви Святого Миколая на Аскольдовій могилі в особі настоятеля церкви<em> </em>отця Іго, а також завдяки доброчинцям п. Лесі з родини О.Тихого, п. Олександру з Тернополя, п. Євстахію &nbsp;з Києва, яки допомогли коштами, ва які помогли коштами, служба придбала необхідні канцтовари, а також солодощі для потребуючих дітей. </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Багато сил, часу і коштів потрібно для такої праці в допомозі, але протягнута рука часто рятує і підтримує дитячі зболені серця. &nbsp;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Волонтери від церкви Святого Миколая на Аскольдовій могилі п. Ганна, п. Марта, п. Людмила, п. Галина підготували допомогу дітям до школи.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ось, що входило в гуманітарні пакети: зошити в клітинку зошити в лінійку, ручки, альбоми для малювання, олівці кольорові, олівці звичайні, фарби акварельні, ручки кулькові, щоденники, а також ранці.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Одягом&nbsp; і взуттям допомогли прихожани церкви.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Також придбали гігієнічні товари: мило, зубні щітки, зубну пасту, шампунь. Солодощі дітям, цукерки та печиво.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Подяка матері Алли&hellip; за допомогу її дитині &ndash; синові, який навчався в Інтернаті і закінчував навчатись. &laquo;<em>У мене не було меж радості, мені хотілось кудись бігти &ndash; щось робити, я плакала і сміялась.</em></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Боже я не знаю, як Вам дякувати за Ваше розуміння, за допомогу моєму синові. Ранком на роботі всім жінкам розказувала, що є Бог і Він чує наші молитви і допомагає нашім дітям.</em>&raquo;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Лист дитини Андрія &ndash; &laquo;П<em>осилку від Вас я отримав, за яку Вам велике спасибі, Всі речі підійшли, а також дякую за солодощі. Костюм буде на випускний вечір. </em></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Завдяки Вам от. Ігор, я до випускного вечора вже готовий. Нехай Бог Вас береже за Вашу доброту.</em>&raquo;.</span></span></p>\r\n<ol>\r\n<li>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Виготовили і вислали дітям гуманітарні пакети на суму <span style=\"text-decoration: underline;\">703,27 грн.</span>&nbsp; </span></span></p>\r\n</li>\r\n<li>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В пакети вложили конверти з почтовими знаками на суму <span style=\"text-decoration: underline;\">20,40 грн.</span></span></span></p>\r\n</li>\r\n<li>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Заплачено за поштові коробки <span style=\"text-decoration: underline;\">102,00 грн.</span></span></span></p>\r\n</li>\r\n<li>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Заплачено пошті за пересилку посилок <span style=\"text-decoration: underline;\">235,08 грн.</span></span></span></p>\r\n</li>\r\n</ol>\r\n<p align=\"right\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"text-decoration: underline;\">Разом витрачено: 1&nbsp;060, 75 грн.&nbsp; </span></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Настоятель церкви св. Миколая на Аскольдовій могилі отець Ігор Онишкевич</span></span></p>',NULL,2151,4,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1030,'P','2010-09-24',1,NULL,'Note','Радійте зі мною, бо я знайшов овечку, що була загубилась.  (Лука 15, 6)','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Цей лист надіслала нам сестра Алла, яка зараз сидить у в&rsquo;язниці. Очевидно, що вона вона сидить і розуміє, що вона скоїла погані вчинки за які тепер усвідомлено страждає. Але вона навернулась і своїм прикладом показує людям, що з Богом все можливо.<br /></span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Цей лист надіслала нам сестра Алла, яка зараз сидить у в&rsquo;язниці. Очевидно, що вона вона сидить і розуміє, що вона скоїла погані вчинки за які тепер усвідомлено страждає. Але вона навернулась і своїм прикладом показує людям, що з Богом все можливо. Вона змінила себе і свої погляди на віру і тепер живе у Господі. Вона співпрацює з нами присилає для газети Оранти ці вірші і дописи. Вона потребує вас і вашої &nbsp;підтримки. Не будьте байдужими, проявіть своє милосердя. Вона потребує вашої уваги у будь-яких проявах, напишіть лист за адресою, який знаходиться у службі милосердя церкви св. Миколая на Аскольдовій могилі. Якщо маєте можливість допоможіть продуктами.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">***</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Не має кращого в цім світі,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">чим бути в Божій благодаті,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">і коли кожен час в служінні</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">єдиний Дух усіх єднає .</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я дякую Тобі , Господь,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">за всіх братів і всіх сестер,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">які дарують нам любов</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">і в них ми бачимо Тебе.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я дякую Тобі, Отець,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За всіх твоїх служителів,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">І за спасіння тих овець,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Що можуть чути вчителів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я дякую Тобі , наш Бог</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За розкриття Твоїх шляхів,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За світло і Твоє тепло,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Що Ти несеш від вчителів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я дякую Тобі , Господь,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За наших всіх служителів,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За кріп кість віри та любов,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Що Ти даруєш через них.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">И еще, излюбленные мои братья и сестры, мне очень хочется выслать сегодня свой жалом, который я назвала:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Завет с Богом</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>1 куплет: </em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ты не раз меня звал, призывал и спасал, знаю я.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Как ты ждал, мой Отец, этого светлого дня,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Как ты долго тянул свои руки ко мне и грустил,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Как страдал, пока сердце мое для себя не открыл.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">А сегодня ликует душа и кружат облака,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Мое сердце открыто, в него я впускаю Христа.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Обретаю я новую жизнь в Завете с Отцом,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">И теперь в моем сердце господствует только любовь.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>Припев: &nbsp;</em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;Я склоняю свои колени, я не ангел, я не гений, просто стать хочу похожей на Христа.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У Отца прошу прощения, принимая очищенье в покаянии от всякого греха.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>2 куплет: </em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Благодатью окутано тело и сердце с душой,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Не нужны мне сокровища мира в завете с тобой.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Нет ценны на все то, что Отец мой любя сотворил,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Для меня Он великую тайну свободы открыл.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Принимая крещенье водой, заключая завет,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я отцу обещал нести в мир, тепло лишь и свет.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Разорву я все нити греха и поду за Христом,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пуль не легкий пойду я, будет больно мне, ну а потом:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>Припев:&nbsp; &nbsp;</em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За смирение в обличении, за победу над искушением, я прийму благословеннее Отца.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я склоняю свои колени, &nbsp;я не ангел, я не гений, просто стать хочу похожей на Христа.<em>&nbsp;</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&hellip;На этом я пока закругляюсь, мои драгоценные сестры и братья во Христе. Я очень буду ждать от вас весточку. Очень хочу выслать для вашей Церкви подарок, сделанный своими руками, поэтому хочу попросить у вас большой конверт размером с форматного листа и почтовых марок на 9 грн. С этим у меня постоянная нехватка. Но Бог видит мои нужды и стремления, по этому я все отдаю в Его Всемогущие руки. Будьте благословенны! Мира, света, тепла и добра вам.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">С любовью ваша сестра Алла.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">02.09.2010г.&nbsp;&nbsp; &nbsp;</span></span></p>',NULL,2153,4,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1031,'P','2008-11-26',1,NULL,'Note','Пісню слави заспіваймо, щирим серцем нині враз, Йосафата звеличаймо в цей святий, великий час','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">25 листопада, як і щороку, у церкві св. Миколая Чудотворця на Аскольдовій могилі настоятель церкви отець Ігор та парафіяни на Божественній літургії молитвами зверталися до священномученика Йосафата.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">25 листопада, як і щороку, у церкві св. Миколая Чудотворця на Аскольдовій могилі настоятель церкви отець Ігор та парафіяни на Божественній літургії молитвами зверталися до священномученика Йосафата. Святий Йосафат - це перший український святий, якого канонізувала Католицька Церква, йому і його мученицькій крові ми завдячуємо, що сьогодні визнаємо правдиву Христову віру та що ми діти правдивої Христової Церкви.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пісню слави заспіваймо,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Щирим серцем нині враз,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Йосафата звеличаймо</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В цей святий, великий час.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Він за Церкву і за віру,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Як святий борець Христа,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Все, що мав, приніс в офіру:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Душу, тіло, кров, життя.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">I ми нині, браття, згідно,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Злучені в любві Христа,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Господа благаймо гідно,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Щоб пролита кров свята</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Посівом спасенним стала,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Віри, єдности Церков,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Щоб навіки всіх з\'єднала,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Під Христову хоругов!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Матеріал підготувала, Любов Михайлюк</span></span></p>',NULL,706,5,1,NULL,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1032,'P','2008-11-27',1,NULL,'Rss','Президентська відзнака для салезіян  ','<p>27 листопада 2008 року у Києві з ініціативи Міністерства України у справах сім`ї, молоді та спорту за підтримки Президента України Віктора Ющенка вперше в Україні відбувався Всеукраїнський форум усиновлювачів, на який було запрошено близько 470 дітей та батьків-усиновлювачів з усіх регіонів України. У форумі бере участь о.Михайло Чабан, Директор Родинного дому «Покрова» (м.Львів), яким опікується монаше Салезіянське Згромадження, місією якого є духовне та всебічне виховання дітей і молоді, особливо найбільш потребуючої та занедбаної.</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=116:prezydentska-vidznaka-dlja-salezijan-&catid=29:propovidi-cat&Itemid=34',707,NULL,1,1,26),
(1033,'P','2010-09-24',1,NULL,'Note','Пожертви парафіян церкви св. Миколая 2010 р. Б.','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">ПОЖЕРТВИ ПАРАФІЯН ЦЕРКВИ СВ. МИКОЛАЯ ЧУДОТВОРЦЯ НА АСКОЛЬДОВІЙ МОГИЛІ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">ПОЖЕРТВИ ПАРАФІЯН ЦЕРКВИ СВ. МИКОЛАЯ ЧУДОТВОРЦЯ НА АСКОЛЬДОВІЙ МОГИЛІ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У ЛИПНІ 2010 Р. Б.</span></span></p>\r\n<table style=\"text-align: justify; width: 411px; height: 600px;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"40\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1</span></span></p>\r\n</td>\r\n<td width=\"20