INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1035,'P','2009-02-26',1,NULL,'Note','','<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong> Молитва, ранішнього намірення</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Господи, дай мені зустріти з душевним  спокоєм усе, що принесе мені цей день. Дай мені цілковито віддатися на волю  твою святу. На всяк час дня цього в усьому настав  і підтримай мене. Які б я не дістав (дістала) вістки  сьогодні, навчи мене прийняти їх із спокійною душею і твердим переконанням, що  на все - твоя свята воля. У всіх ділах моїх і словах керуй  думками моїми і почуттями. У всіх непередбачених випадках не дай  мені забути, що все Ти послав. Навчи мене просто й мудро ставитися до  всіх людей, нікого не ображаючи, ні не засмучуючи. Господи, дай мені силу перенести втому  і всі події цього дня. Керуй моєю волею і навчи мене  молитися, вірити, надіятися, терпіти, прощати й любити. Амінь.</span></span></p>','',NULL,708,5,1,NULL,50),
(1036,'N','2008-11-28',0,NULL,'Note','111','','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,1689,200,IMG_9288_resize.JPG\" alt=\"\" /><img style=\"float: left;\" src=\"image,1689,200,180.jpg\" alt=\"\" />У суботу, 22 листопада, у калиновому гаю (Паркова дорога, м. Київ) о 17:00 годині відспівали панахиду&nbsp; за душі померлих від голодомору 32-33 років. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Молодь церкви св. Миколая Чудотворця з Аскольдової Могили та київські Пластуни засвітили 75 лампадок, щоб увіковічити пам\'ять загиблих українців у ці жахливі роки.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,1689,200,IMG_9343_resize.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,1689,200,IMG_9279_resize.JPG\" alt=\"\" />Ми маємо пам\'ятати свою історію, знати її і докладати усіх зусиль щоб не допустити такого безжалісного вороття.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Фото: Віталіни Басістої та Ярослава Павлюка<br /></span></span></p>',NULL,709,5,1,NULL,17),
(1037,'P','2008-11-28',1,NULL,'Rss','Вечір памя\'ті жертв голодомору 32-33 рр.','<p><img style=\"float: right;\" src=\"http://www.askoldova-mohyla.org/uk/image,1689,200,IMG_9288_resize.JPG\" />У суботу, 22 листопада, у калиновому гаю (Паркова дорога, м. Київ) о 17:00 годині відспівали панахиду  за душі померлих від голодомору 32-33 років.</p>\n<p> </p>\n<p>Молодь церкви св. Миколая Чудотворця з Аскольдової Могили та київські Пластуни засвітили 75 лампадок, щоб увіковічити пам\'ять загиблих українців у ці жахливі роки.</p>\n<p> </p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=117:vechir-pamjati-zhertv-golodomoru-32-33-rr&catid=25:parafija-cat&Itemid=27',710,NULL,1,1,26),
(1038,'P','2008-11-29',1,NULL,'Rss','В Одесі відбувся круглий стіл «Ліки солідарності»','<p><img style=\"float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_27-11-08.jpg.jpg\" />27 листопада в дитячому реабілітаційному центрі «Будинок з Ангелом» відбувся круглий стіл «Ліки солідарності» за участю громадських і релігійних організацій, людей, що займаються реабілітацією, з обмеженими фізичними і розумовими можливостями. Круглий стіл відбувся у рамках соціального тижня \"На шляху до солідарності українського суспільства\", що днями проходить в Одесі. У зустрічі взяли участь представники релігійних організацій м. Одеси, члени громадських організацій, а також заступник начальника Управління соціальнї праці Одеської міської ради Регіни Анатоліївни Миколаєвої. Учасники круглого столу говорили про проблеми людей з обмеженими можливостями, про питання подолання барьерності. Зокрема, пані Миколаєва проінформувала присутніх про дії міських властей, направлені на подолання барьерності в Одесі. Вона також звернулася до присутніх на зустрічі студентів одеських ВУЗів чуйніше відгукуватися на потреби людей із обмеженими можливостями.</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=118:v-odesi-vidbuvsja-krugly-stil-lliky-solidarnostir&catid=27:gk-v-ukrajini-cat&Itemid=57',713,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1039,'P','2010-09-29',0,NULL,'Note','Борис Ґудзяк: «Добре, коли християни допомагають владі освячуватись»','<div style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"http://glavred.info/images/b-00090948-a-00017203.jpg\" alt=\"\" /></span></span></div>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Отче, чи мав продовження інцидент з офіцером СБУ? Подібні візити, розмови тощо повторювались?</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Наразі видається, що крапкою був приїзд до нас Валерія Хорошковського, під час якого він познайомився з університетом. А інцидент, про який ви згадуєте, він пояснив певною некомпетентністю Служби &ndash; я не входив у діалектику, бо вважаю, що вже достатньо висловився з цього приводу. Ми сприйняли візит пана Хорошковського як вибачення.</span></span></p>','<div style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"http://glavred.info/images/b-00090948-a-00017203.jpg\" alt=\"\" /></span></span>\r\n<div><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Наталія Лебідь, журнал &laquo;Главред&raquo;, 24.09.10 // 09:28</span></span></div>\r\n<span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Отець Борис Ґудзяк, ректор Українського католицького університету, не так давно опинився в епіцентрі скандалу, який виявив нові (чи то пак добре забуті старі?) аспекти нашого існування.</strong></span></span></div>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Йдеться про візит до пана Ґудзяка співробітника СБУ, який говорив про можливі протести протии чинної влади і про всяк випадок закликав ректора притримати цю студентську &laquo;сверблячку&raquo;. Інакше, у коректній формі натякнув есбеушник, усім буде &laquo;боляче&raquo;. Скандал вийшов настільки гучним, що вибачатися перед Ґудзяком поїхав не абихто, а сам голова Служби Валерій Хорошковський. Зі згадки про ці епохальні відвідини &laquo;Главред&raquo; і починає розмову </em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Отче, чи мав продовження інцидент з офіцером СБУ? Подібні візити, розмови тощо повторювались?</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Наразі видається, що крапкою був приїзд до нас Валерія Хорошковського, під час якого він познайомився з університетом. А інцидент, про який ви згадуєте, він пояснив певною некомпетентністю Служби &ndash; я не входив у діалектику, бо вважаю, що вже достатньо висловився з цього приводу. Ми сприйняли візит пана Хорошковського як вибачення.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Але чому це сталося саме з Українським католицьким університетом? Чи до інших ректорів Львова працівники СБУ також навідувалися з &laquo;профілактичними розмовами&raquo;?</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я не маю доказів подібних розмов. Але припускаю, що такі були або могли бути. Констатую лише те, що трапилось із нами. Сподіваюся, що інші заклади також протестуватимуть, якщо до них застосують подібні методи.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>І все-таки: чому саме ви? У вас що, винятково активні студенти?</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я сказав би, що це передусім &ndash; вільні люди. Вони духовно активні, соціально активні. Наші студенти багато працюють із сиротами, з людьми з особливими потребами. І, звісно ж, серед студентства є люди, які переживають за розвиток України, за стан корупції в нашій державі. Свої переживання вони втілюють у живе слово &ndash; на сайтах, на майданах Львова та Києва...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Поговорімо про вас. Ви народились і виросли за кордоном, але повернулись в Україну 1992 року. Чому так? Якщо провести паралелі з долею Любомира Гузара, то Блаженнійший Любомир бодай зростав тут, у вас же &ndash; все інакше. Ваші перші дитячі враження не пов&rsquo;язані з Україною... </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я зростав, оточений двома культурами. Англійської мови не знав, доки не пішов у садочок. У нашій хаті готували страви української кухні, ми ходили до української церкви, а щосуботи &ndash; до школи україно&not;знавства. Українських компонентів було дуже багато в моєму житті &ndash; від материного молока. Ми з братом належали до &laquo;Пласту&raquo;, брали участь у змаганнях українського спортивного клубу. Таких, як ми, було дуже багато, але не всі, звісно, переїхали до України. У моєму житті головним було покликання &ndash; служити в Церкві, бути священиком. Це покликання було скероване патріархом Йосипом Сліпим, із яким я вперше познайомився ще в сім років, а потім ще двічі &ndash; будучи підлітком. Унаслідок цього я поїхав навчатися до семінарії в Рим (це була семінарія в екзилі &ndash; колегія Святої Софії при Українському католицькому університеті в Римі). Мене притягнула харизма патріарха Йосипа, він говорив, що треба готуватися до змін в Україні, що Радянський Союз не буде вічним. А ми були замолоді, щоб йому не вірити...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Якою ви побачили Україну після Європи й Америки?</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Побачив той контекст, що породжував найперші пісні мого життя. Побачив зразки тих церков, за якими будували в діаспорі. Словом, я побачив еталон. Було дуже багато рідного. І багато такого, що покоління моїх батьків не хотіло або не могло передати нам. Емігранти ідеалізували свою батьківщину. Хоча &laquo;якість&raquo; режиму вони добре знали. Про Голодомор у діаспорі говорили більше, ніж в Україні. Ми читали твори диси&not;дентів &ndash; Сверстюка, Світличного... Тобто, з одного боку, ми були заангажовані у драматичну історію України, де багато чого заборонено, а з іншого &ndash; усвідомлювали, що ми &ndash; ті, хто на свободі, &ndash; несемо певну відповідальність і маємо солідаризуватися з рештою України. Я не романтизую труднощі, але Україна і потреби її людей дуже вплинули на мій вибір. Працювати на них &ndash; для мене завдання вдячне.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Я запитувала про загальні враження від України. А що ви можете сказати про життя саме католицької громади на початку 1990 років? Чи це було те, що ви розраховували побачити?</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Крім того, що я був українцем і християнином, я також був вишколений як критично налаштований історик. Тобто багато чого досліджував з 1988-го до 1992 року. У цей період їздив туди й назад, знайомився з Церквою ще в підпіллі. І якщо до мого приїзду були дещо ідеалізовані уявлення, то потім вони просто конкретизувалися. Тому для мене не було ані шоку, ані розчарування, ані особливої екзальтації. Хочу сказати, що та дійсність, яку я застав, змусила мене ще більше шанувати людей, які зуміли залишитися собою, бути принциповими, цілісними за тодішніх радянських обставин.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Ви фактично започаткували Український католицький університет...</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Не зовсім так. Ідея як така була сформульована ще 1905 року митрополитом Шептицьким. Створення богословської академії наприкінці 1920-х було першим кроком до цього. Коли така спроба обірвалась, Йосип Сліпий, після 18 років ув&rsquo;язнення, 1963 року створив Український католицький університет у Римі. 1992 року я був серед тих, хто був покликаний працювати над проектом відновлення Богословської академії, що мала стати університетом. За цим проектом стояла майже столітня традиція, були певні концепти, за цим стояла церква, і вже були працівники. Це була ціла команда, і сьогодні в університеті працює 300 осіб, які є співтворцями, від яких я постійно вчуся, бо є доволі багато людей, які вміють робити щось краще за мене.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Але на сайті УКУ сказано, що його зас&not;новником є Фонд святого Климентія, головою якого обрано Любомира Гузара. Чому так?</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Тому що церква, за нашим законодавством, не може створювати університетів. Клуб шанувальників пива &ndash; може, а церква &ndash; ні. Це та спадщина, яка залишилася ще з радянських часів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>А ви гадаєте, що це неправильно, коли церква відділена від світських, державних справ?</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церква має бути відділена від держави, але вона не може бути відділена від суспільства.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Вона не відділена. Ви ж маєте паству...</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я вважаю, що в Україні є свобода совісті, але при цьому ми страждаємо від багатьох рецидивів радянського часу, коли держава тримає монополію на багато речей.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Але церква де-факто є частиною держави. І разом із нею тримає монополію. Зокрема, на доступ до тіла першої особи. Згадайте хоча б відеоряд, коли Президент України &ndash; байдуже, яке він носить прізвище, &ndash; йде перед виборами молитися буцімто за долю держави у супроводі священиків усіх конфесій. Ви можете собі уявити, щоб Ангела Меркель чи Ніколя Саркозі на такий глибоко інтимний акт, як молитва, скликали пресу? Оце і є &laquo;совок&raquo; у найгіршій своїй іпостасі. Церква, що публічно благословляє державного мужа і його чисті чи не дуже чисті помисли, стала елементом піару, але сама проти цього ані трохи не заперечує...</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я вважаю, що проблема є... Я запрошую ваших читачів зайти на наш сайт і ознайомитися з нашою позицією щодо виборів. Можу сказати тільки щодо своєї церкви: ми робимо все можливе, щоб постійно втілювати &laquo;політику невтручання&raquo;. Священикам заборонено агітувати за підтримку того чи іншого кандидата. Але, на жаль, не бракує й винятків. Можу говорити за свою церкву. В Українській Греко-Католицькій Церкві &ndash; 2500 священиків, десятки єпископів... За дотримання цього принципу дехто має десять балів, а дехто &ndash; два або три.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Політики заслуговують на ще нижчий бал. Але ось у чому також криється небезпека: кожен з очільників держави обирає собі ту чи іншу церкву для, так би мовити, президентського &laquo;пулу&raquo;. У Януковича у фаворитах &ndash; Лавра і Московський патріархат. Але і Ющенко свого часу не менш прозоро натякав, до кого горнеться. По-вашому, це сприяє єдності суспільства? &nbsp; &nbsp; &nbsp; </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Тут ми маємо ще один феномен. Президент Кучма встановив для своєї другої інавгурації протокол, який передбачає благословення в Соборі святої Софії за участі представників усіх конфесій. Так само було й з інавгурацією Віктора Ющенка. Під час інавгурації Віктора Януковича було порушено цей протокол. У церемонії останнього брав участь російський патріарх Кирил і представники лише однієї церкви. Те саме повторилось у День Незалежності. Чи таким чином Московський патріархат стає для нас державною церквою? На мою думку, все це спрямовано, так би мовити, у зов&not;сім некорисний напрямок.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>А що заважає священикам проігнорувати запрошення на інавгурацію? Не думаю, що це мало б вигляд більшою мірою не комільфо, ніж те, що ми бачимо зараз. Йдеться не просто про людину, яка зупинила вас на вулиці й попросила про благословення. Йдеться про підігрування небезпечній тенденції... </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я вважаю, що участь церков і релігійних організацій у суспільно значимих подіях, коли одну церкву не ставлять вище або нижче за іншу, є корисною і спирається на прецеденти. Навіть у секулярних державах, як-от в Америці, де є доволі радикальне відділення церкви від держави, все одно превалює турбота про інтереси того прошарку суспільства, для якого церковні традиції є вагомими.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>До Америки ми не доросли. Тим паче, там немає протистояння Московського і Київського патріархатів. Можливо, саме в секуляризації &ndash; наше спасіння. Інакше ситуація просто зайде у глухий кут...</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Однією фразою тут відповісти не вдасться. Я вважаю, що церква і держава мають бути розділені, але церква має право на участь у всіх значимих подіях, які відбуваються в суспільстві.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Яку саме участь? Невдовзі мине десять років із дня зникнення Георгія Гонгадзе, а відтак &ndash; і десять років від початку касетного скандалу, акції &laquo;Україна без Кучми&raquo; й арештів, що мали місце 9 березня 2001 року. Пригадується, як усі очільники різних церков виступили із засудженням дій тодішніх політв&rsquo;язнів, усі підтримали Кучму тощо. Як на мене, такої участі у суспільних справах краще не брати. А однією-єдиною персоною, яка не побоялася йти проти течії, був Любомир Гузар. Решта &laquo;прогнулась&raquo; і навіть не завважила своєї незрілості... </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ми зустрічаємося з вами після моєї бесіди зі студентами Києво-Могилянської академії на тему страху. Думаю, що в цілому в нашому суспільстві &ndash; і в церкві зокрема &ndash; є дуже багато страху. І це є причиною того, чому виникають різні неадекватні виступи, прояви та феномени.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>А твердження про те, що всяка влада від Бога, &ndash; належить до адекватних чи неадекватних (з точки зору сучасного світу) проявів?</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">І знову ви ставите надто об&rsquo;ємне запитання, для відповіді на яке треба дуже багато різних уточнень. Одне я вам скажу: яким би не було розуміння такого постулату, це не є мандат для християн потурати несправедливості, гріхові, неправді. &nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Тобто, якщо влада чинить гріх, обов&rsquo;язок християн &ndash; виступити проти цього?</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я думаю, що дуже добре, коли християни допомагають владі освячуватись.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>І шляхом революції зокрема? &nbsp; </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Не знаю, чи це найбільш сприятливий засіб допомагати людям освячуватись. Я хочу зараз закцентувати увагу на цілі, а питання методів залежить від різних обставин. &nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Ще до питання страху, але вже не лише того, що гніздиться в душах українців. Багато чого не боявся робити Папа Іоанн Павло ІІ. Не боявся вибачатися за злочини інквізиції, приміром... Чи бракує його зараз католицькій громаді всього світу?</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Іоанн Павло ІІ був великим Папою. Неможливо, щоб усі були однаковими. Було дуже важливим для України те, що Іоанн Павло ІІ був слов&rsquo;янином і 27 років говорив до нас українською мовою, що не завжди роблять навіть українські політики. Він розумів комуністичну систему і всі її гіркі плоди. Доводилося чути таку думку, що Папа-слов&rsquo;янин, який добре розумів комунізм і специфіку Східної Європи, можливо, гірше розумів Європу Західну. Папа Бенедикт у свій спосіб намагається осягнути західну культуру. Понтифікат Іоанна Павла ІІ тривав 27 років, а з теперішнім Папою ми ще не маємо цього часу. Ми побачимо, як історія його оцінить...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>П&rsquo;ять років &ndash; усе-таки час немалий. А наскільки зараз Ватикан цікавиться Україною?</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Зацікавлення спостерігається постійне, але я розумію, що є ностальгія за інтенсивністю того зацікавлення, яке було колись. Думаю, ми отримаємо більш ґрунтовну відповідь на це запитання, якщо візит Папи, про який ідеться в пресі, таки відбудеться 2012 року. &nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>У контексті сказаного: а чи й досі триває процес беатифікації Андрея Шептицького? Що чути з цього приводу?</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я не володію найсвіжішою інформацією. Знаю лише, що процес триває, він є складним, оскільки правління Андрея Шептицького було доволі тривалим і лишило по собі чималий архівний спадок. Беатифікація мученика, який жив кілька років і нічого не писав, відбувається значно швидше, ніж беатифікація архієрея, який жив 44 роки і писав дуже багато й на різні теми, і який до того ж правив церквою під вісьмома політичними режимами...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Що ви маєте на увазі під &laquo;писав дуже багато й на різні теми&raquo;? Відносини Шептицького з німецьким окупаційним режимом, які інколи по-різному трактують?</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">І це також.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Я думала, що на цьому спекулюють тільки в Україні, точніше, на теренах СНД. Тоді як &laquo;адвокатів диявола&raquo; міжнародного масштабу це не повинно цікавити...</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ні, це питання порушується і в Росії, і в Польщі. Не секрет, що в Польщі є багато противників беатифікації Шептицького, які працюють проти цього.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Я вважала, що остаточний вердикт виносять на рівні Риму та Ватикану...</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Так, але це рішення не приймають у вакуумі. Є моральне зобов&rsquo;язання комісії, що розглядає проблему беатифікації, &ndash; відповідати на всі запитання, навіть якщо вони є відверто провокаційними. Мушу зазначити, що процес Андрея Шептицького не є найдовшим. Деякі тривають століттями.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Прикро, що про нього взагалі мало хто знає. Тож до питання поширення інформації: я чула, що при Українському католицькому університеті відкрито школу журналістики...</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Буде відкрито восени. Це буде магістерська програма, яку склали для осіб, що мають уже і певну освіту, і, можливо, практику. Тож, як ви розумієте, вона не обмежена тільки релігійною журналістикою...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">http://glavred.info/archive/2010/09/24/092802-9.html</span></span></p>',NULL,2162,5,1,NULL,27);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1040,'P','2008-12-01',0,NULL,'Note','ОГОЛОШЕННЯ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1 грудня 2008 року - в приміщенні Національного академічного драматичного театру ім..Івана Франка гастролі Коломийського академічного обласного українського драматичного театру ім.І.Озаркевича з виставою &laquo;Гуцульський рік&raquo;(Г.Хоткевич). Парафіяни церкви св. Миколая Чудотворця на Аскольдовій могилі також відвідають виставу, бо цінують свою культуру, свою державу і своїми серцями віддані Богу.<br /></span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1 грудня 2008 року - в приміщенні Національного академічного драматичного театру ім..Івана Франка гастролі Коломийського академічного обласного українського драматичного театру ім.І.Озаркевича з виставою &laquo;Гуцульський рік&raquo;(Г.Хоткевич) <br /><br />Вистава &laquo;Гуцульський рік&raquo; - неповторна сценічна перлина з виразним гуцульським колоритом. В ній стверджується сила земного буття, мудрість і живучість традицій і вірувань народу, через які людина шукає злагоду з життям, з природою, з собою за Божим велінням гармонії і краси. Вистава увійшла до золотого фонду театральної скарбниці України і є сценічним першопрочитанням твору славетного драматурга. Вона була визнана кращою серед театральних доробків України, удостоєна участі в Единбурзькому театральному фестивалі (Шотландія). Одна з найстаріших вистав, її сценічна біографія складає близько 20 літ. <br /><br />Початок о 19 годині. Вартість квитка 10-70 грн,   <br /><br />Квитки можна придбати в касі театру, в храмах: на Аскольдовій могилі, Василія Великого (біля Львівської площі), Воскресіння Христового (на лівому березі) або за тел. 8-050-1400681 - Галина Миколаївна <br /><br />2 грудня 2008 року - в приміщенні Національної філармонії - ювілейний вечір народної артистки України Марії Стеф\'юк   <br /><br />. Початок о 19 годині. Запрошення за телефоном 234-33-68   <br /><br />18-19 грудня 2008 - свято Святого Миколая із групою &laquo;Дай Боже&raquo; в театрі &laquo;На липках&raquo;   <br /><br />З повагою,   <br /><br />Дерев\'янчук Геннадій Юхимович</span></span></p>',NULL,711,5,1,NULL,42),
(1041,'P','2008-11-29',1,NULL,'Rss','Вшанування мощей покровителя українського народу св. Йосафата Кунцевича ','<p><img style=\"float: right;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_25-11-08_01.jpg.jpg\" />25 листопада в день памяті святого священомученика Йосафата Кунцевича, у каплиці чоловічого монастиря «Воплоченого Слова» при парафії святих Кирила і Методія у Крихівцях-Долині, відбулося загальне (публічне) вшанування мощей святого, які адміністратор парафії о. Йосафат Бойко, ВС, привіз з Риму, де мощі перебувають у базиліці св. Петра.</p>\n<p>У каплиці знаходяться мощі першого ступеня 14 святих, які виставляються для загального вшанування у дні пам\'яті святих протягом літургійного року. До першого ступеня належать частинки з тіл святих (переважно з костей - \"ex ossibus\"). До другого ступеня мощей належать речі користовані святими (наприклад, вервиця чи біблія) і до третього - тканина, яка доторкалась тіла чи костей святого і яка переважно розповсюджується з різними іконками чи образками.</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=119:vshanuvannja-moshche-pokrovytelja-ukrajinskogo-narodu-sv-osafata-kuncevycha-&catid=27:gk-v-ukrajini-cat&Itemid=57',714,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1042,'P','2010-10-01',1,NULL,'Rss','Українське християнство крізь віки','<p>З вересня 2010 р. розпочалася організаційна підготовка до проведення VII конкурсу духовної пісні «Аскольдів глас» та 20-річчя заснування парафії церкви св. Миколая Чудотворця на Аскольдовій могилі. Захід традиційно відбувається у переддень свята св.&nbsp;Миколая Чудотворця, тобто 18 грудня.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=595:ukrajinske-hrystyjanstvo-kriz-viky&catid=25:parafija-cat&Itemid=27',2164,NULL,1,1,26),
(1043,'N','2010-10-09',1,NULL,'Note','Омеліан Ковч','<p><img src=\"image,3650,200,_MG_3647.JPG\" alt=\"\" width=\"162\" height=\"200\" /></p>','<p><img style=\"float: left;\" src=\"image,3650,200,_MG_3647.JPG\" alt=\"\" /></p>',NULL,2167,4,1,NULL,17),
(1044,'P','2010-10-11',1,NULL,'Note','Допомога матерям і покинутим дітям ','<p style=\"text-align: center;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Слава Ісусу Христу!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Молюся за Вас і прошу Божого милосердя, яке випрошуєте самі своєю вірою, надією, любов&rsquo;ю. Ви самі є співучасниками свого спасіння через покаяння.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Ми готові листуватись з Вами, та від тепер зворотного конверту підписувати не будемо. Ви самі визначатимете, чи спілкуватиметесь з нами.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">У нас немає такої можливості допомогти усім, ми можемо лише молитись за спасіння душ. Допомагатимемо ліками, допомагатимемо &laquo;покинутій дитині&raquo; через ваші материнські прохання. Кожна ваша співучасть: вишивання; малювання; дописи статей &ndash; будуть винагородженні. Прошу, будьте сильними у своїй вірі, Божа ласка не омине нас.</span></span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">З любов&rsquo;ю Ваш священик Ігор.</span></span></p>','<p style=\"text-align: center;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Слава Ісусу Христу!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Молюся за Вас і прошу Божого милосердя, яке випрошуєте самі своєю вірою, надією, любов&rsquo;ю. Ви самі є співучасниками свого спасіння через покаяння.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Ми готові листуватись з Вами, та від тепер зворотного конверту підписувати не будемо. Ви самі визначатимете, чи спілкуватиметесь з нами.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">У нас немає такої можливості допомогти усім, ми можемо лише молитись за спасіння душ. Допомагатимемо ліками, допомагатимемо &laquo;покинутій дитині&raquo; через ваші материнські прохання. Кожна ваша співучасть: вишивання; малювання; дописи статей &ndash; будуть винагородженні. Прошу, будьте сильними у своїй вірі, Божа ласка не омине нас.</span></span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">З любов&rsquo;ю Ваш священик Ігор.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">МОЛИТВА ЩОДЕННОГО ПОЖЕРТВУВАННЯ </span></span></strong><strong><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">ДЛЯ ЧЛЕНІВ АПОСТОЛЬСТВА МОЛИТВИ</span></span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">О, Божественне Серце Ісуса! У злуці з тим наміром, з яким Ти на землі віддавав славу Богові і тепер щоденно віддаєш у Пресвятій Тайні Євхаристії, жертвую Тобі через Непорочне Серце Пречистої Діви Марії усі свої молитви, справи, слова, думки й витривалість у терпінню нинішнього дня у винагороду за всі зневаги, образи і кривди, завдані Тобі.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Жертвую їх особливо за святішого Отця Папу Римського, за святу Церкву, за навернення грішників та в усіх намірах Апостольства молитви, призначених на цей місяць і на сьогоднішній день.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Пресвяті Серця Ісуса і Марії, спомагайте святу Церкву та Україну!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Святий Йосифе, Покровителю і Заступнику приятелів Ісусового Серця, моли Бога за нас!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Святий Архангеле Михаїле, святий Миколаю, святий Володимире, святий Йосафате, Заступники України, моліть Бога за нас!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">МІСЦЕВИЙ: Щоб люди в Україні під покровом Пресвятої Богородиці глибше усвідомили своє життєве покликання та вибір.</span></span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Великий князь Ярослав Мудрий, бажаючи забезпечити державу та народ, віддав землю й людей під Покров Пресвятої Богородиці. Вона, сказавши один раз ангелу:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&laquo;Ось я, слугиня Господня, нехай зі мною станеться за твоїм словом&raquo; (Як. 1, 38), ціле своє життя віддала у Господні руки, а разом з тим стала найдосконалішим&nbsp; інструментом Його Провидіння, через який Боже Слово увійшло в світ для спасіння людини. Її заступництво відкриває нам шлях до спасіння, тому Бог вчинив її царицею неба та землі. Кожний, хто вдається з молитвою до Богородиці та вдивляється пильно в її образ, навчаючись пізнавати й любити Божу волю, ніколи не осоромиться, віднайде правдиве життя та щастя. Сьогоднішнє суспільство великою мірою дезорієнтоване, людські цінності часто перевернуті &laquo;з ніг на голову&raquo;, особливо молоді люди бояться здійснювати життєвий вибір, йти назустріч поклику Божому, який кличе нас бути святими в будь-якому стані. Молімося разом, щоб Пречиста Діва Марія через свій Покров і заступництво випросила ласки, яка торкнулася б кожного серця, кожної людини в нашому народі, щоб ми могли більш усвідомлено та відповідально переживати своє покликання та сміливо,&nbsp; уповаючи на Боже Провидіння, здійснювати моральні вчинки в нашому житті.</span></span></p>',NULL,2172,4,1,NULL,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1045,'P','2010-10-13',1,NULL,'Rss','ФІНАНСОВИЙ ЗВІТ «Канікули з Богом в Карпатах»','<p>«Господь - мій пастир: нічого мені не бракуватиме.</p>\n<p>На буйних пасовиськах він дає мені лежати; веде мене на тихі води.</p>\n<p>Він відживляє мою душу, веде мене по стежках правих імени ради свого»</p>\n<p>(Пс. 23).</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=601:finansovy-zvit-lkanikuly-z-bogom-v-karpatahr&catid=25:parafija-cat&Itemid=27',2174,NULL,1,1,26),
(1046,'P','2008-11-29',1,NULL,'Note','В севастопольском кинотеатре провели церковную службу','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В Севастопольском кинотеатре Дружба, где до войны размещался католический храм, сегодня была проведена церковная служба.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" title=\"В севастопольском кинотеатре провели церковную службу\" src=\"http://k.img.com.ua/img/forall/a/6629/17.jpg\" alt=\"В севастопольском кинотеатре провели церковную службу\" width=\"290\" height=\"218\" />Как сообщается, &nbsp;литургию в честь священномученика Климента Римского,&nbsp;при полностью заполненном кинозале отслужили папский нунций -посол Ватикана, архиепископ Иван Юркович, правящий епископ Одесско-Симферопольской епархии владыка Бронислав Бернацкий, настоятель Римско-католической религиозной общины священномученика Климента Римского отец Юрий Зиминский вместе c крымским католическим духовенством.</span></span></p>\r\n<p>&nbsp;</p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В Севастопольском кинотеатре Дружба, где до войны размещался католический храм, сегодня была проведена церковная служба.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" title=\"В севастопольском кинотеатре провели церковную службу\" src=\"http://k.img.com.ua/img/forall/a/6629/17.jpg\" alt=\"В севастопольском кинотеатре провели церковную службу\" width=\"290\" height=\"218\" /> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Как сообщается, &nbsp;литургию в честь священномученика Климента Римского,&nbsp;при полностью заполненном кинозале отслужили папский нунций -посол Ватикана, архиепископ Иван Юркович, правящий епископ Одесско-Симферопольской епархии владыка Бронислав Бернацкий, настоятель Римско-католической религиозной общины священномученика Климента Римского отец Юрий Зиминский вместе c крымским католическим духовенством.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Как сообщила&nbsp; пресс-секретарь Римско-католической религиозной общины священномученика Климента Римского Татьяна Рихтун, служба была&nbsp;проведена по согласованию с Севастопольской горгосадминистрацией.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Также, как сообщают в общине около 10 лет назад в кинотеатре один раз уже была проведена церковная служба. Но, как отметила Рихтун, тогда&nbsp;служба проходила&nbsp;не при таком количестве верующих и не при таком представительстве католических священников.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Как стало известно, собор, выдержанный в стиле неоготики, функционировал до декабря 1936 года. В&nbsp;2011 году ему исполняется 100 лет.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В период Великой Отечественной войны здание костела было несколько разрушено. С 1960 года в здании функционирует кинотеатр. <br />Напомним, римо-католический приход в Севастополе насчитывает около 800 верующих.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Как сообщалось 8 июля, Ивано-Франковский, Львовский и Тернопольский <a href=\"http://korrespondent.net/ukraine/events/516463\" target=\"_blank\">облсоветы просят Папу Римского</a>&nbsp;предоставить Украинской греко-католической церкви статус Патриархата.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Как сообщалось 24 мая,&nbsp; государственный секретарь Ватикана кардинал Тарчицио Бертоне <a href=\"http://korrespondent.net/ukraine/events/471846\" target=\"_blank\">провел во Львове божественную литургию</a>, во время которой провозгласил беатификацию католической монахини Марты Вецкой.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>По материалам&nbsp;<a href=\"http://www.unian.net/\" target=\"_self\">УНИАН</a>, <a href=\"http://www.newsru.com/\" target=\"_self\">NEWSru.com</a></em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Эта же новость на &nbsp;<a href=\"http://korrespondent.net/ukraine/events/662917\" target=\"_blank\">Корреспондент.net</a></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://news.bigmir.net/ukraine/75291/<br /></span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>',NULL,712,5,1,NULL,30);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1047,'P','2008-11-29',1,NULL,'Note','Потрійний ювілей ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Таким висловом супроводжувалися урочистості в Івано-Франківській єпархії УГКЦ, які відбулися 27 листопада з нагоди 85-річчя життя, 60-річчя монашества та 50-річчя священства Преосвященного Владики Софрона (Мудрого), Єпископа-емерита Івано-Франківської єпархії, ректора Івано-Франківської теологічної академії.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_juvilej_sofron2.jpg.jpg\" alt=\"\" />Таким висловом супроводжувалися урочистості в Івано-Франківській єпархії УГКЦ, які відбулися 27 листопада з нагоди 85-річчя життя, 60-річчя монашества та 50-річчя священства Преосвященного Владики Софрона (Мудрого), Єпископа-емерита Івано-Франківської єпархії, ректора Івано-Франківської теологічної академії.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Святкування розпочалися Архиєрейською Божественною Літургією, яку очолив сам ювіляр в присутності Блаженнішого Любомира, Глави УГКЦ, та у співслужінні з владиками УГКЦ, членами Синоду Єпископів Києво-Галицького Верховного Архиєпископства, Митрополитом Львівським РКЦ в Україні Мечиславом Мокшицьким, а також численними священиками Івано-Франківської та сусідніх єпархій УГКЦ, - для Департаменту інформації повідомив о. Юрій Трухан, секретар Владики Володимира (Війтишина), Єпарха Івано-Франківського УГКЦ.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Основною темою привітальної промови Блаженнішого Любомира, яку він виголосив після прочитання Євангелії, було покликання. Проповідник спонукував учасників молитви задуматися, чи і як ми сповняємо своє життєве завдання? На прикладі ревного служіння Владики Софрона Богові і людям, який гідно використав свої таланти, примножив їх і продовжує далі, не зважаючи на роки життя, бути ревним працівником в божому винограднику, подавати приклад та натхнення для служіння, Глава УГКЦ заохочував слухачів до подібної реалізації, використання Богом даних нам талантів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_juvilej_sofron3.jpg.jpg\" alt=\"\" />На закінчення Літургії Владика Володимир (Війтишин) зачитав привітання від Папи Венедикта XVI, який уділив своє Апостольське благословення. Слова радості та найкращі побажання висловив Архиєпископ Мечислав Мокшицький, передавши також щирі вітання від Кардинала Мар\'яна Яворського, а також було зачитано привітання від Архиєпископа Івана Юрковича, Апостольського Нунція в Україні. Слова вітання для ювіляра надійшли також від Патрiарха Київського i всiєї Руси - України Філарета. Линули привітання і побажання для Владики Софрона і від представників міської та обласної влад, депутатів Верховної Ради України, місцевої інтелігенції, ректорів вищих навчальних закладів, викладачів, мирян та всіх людей, з якими Владика Софрон розпочинав працю на теренах Івано-Франківської єпархії.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У цей день з нагоди ювілеїв Владики Софрона в Івано-Франківському обласному музично-драматичному театрі ім. І. Я. Франка відбулася урочиста академія. Вечір привітань тісно переплітався з українською як духовною, так і сучасною піснею у виконанні церковних хорів: муніципального та семінарійного, а також хору Католицької школи ім. Василія Великого УГКЦ. Звучали пісні гуртів \"Скеля\", \"Дітей Світла\" і окремих відомих виконавців Прикарпаття.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На завершення Владика Софрон (Мудрий) виступив з подячним словом, в якому наголосив, що вся праця довгих років життя є присвячена на славу Божу, добро Церкви та Батьківщини України.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Департамент інформації УГКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span><br /></span></p>\r\n<p>&nbsp;</p>',NULL,715,5,1,NULL,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1048,'P','2008-11-27',1,NULL,'Note','Медіа-моніторинг церковних медіа став темою семінару для прес-секретарів єпархій УГКЦ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Семінар на тему \"Медіа-моніторинг - інструмент для відслідковування ефективності комунікацій\" 26 листопада 2008 року зібрав у Стрию прес-секретарів єпархій та церковних установ УГКЦ в Україні. Спеціалісти консалтингової компанії pro.mova представили технологію поетапного проведення медіа-моніторингу.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Семінар на тему \"Медіа-моніторинг - інструмент для відслідковування ефективності комунікацій\" 26 листопада 2008 року зібрав у Стрию прес-секретарів єпархій та церковних установ УГКЦ в Україні. Спеціалісти консалтингової компанії pro.mova представили технологію поетапного проведення медіа-моніторингу. Це було зроблено тому, що працівники церковних прес-служб найближчим часом долучаться до проведення аналізу медіа активності УГКЦ та вироблять комунікаційну стратегію Церкви на найближчі роки.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Семінар провели Вікторія Бринза, керівник аналітичного напрямку компанії pro.mova, та Олег Розвадовський, аналітик цієї ж компанії. Тематика тренінгу стосувалася визначення показника ефективності роботи церковних прес-служб, особливостей моніторингу глобальних та нових медіа і етапів проведення аналітичного моніторингу медіа. Учасників семінару привітав Владика Юліян (Ґбур), який очолює Митрополичу комісію соціальної комунікації УГКЦ.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Консалтингова компанія pro.mova є експертом у&nbsp;сфері зв\'язків з&nbsp;громадськістю (PR). Її &nbsp;послугами користуються лідери українського PR-ринку. Спеціалісти компанії&nbsp;розробляють навчальні продукти та рішення, якими пізніше користуються лідери українського та міжнародного PR ринку, забезпечують ефективні інтернет-комунікації та керують веб-проектами, а&nbsp;також консультують щодо медійних інновацій (Web 2.0: корпоративні блоги, соціальні мережі, подкасти). Очолює компанію, що має свої офіси у Києві та Львові, &nbsp;Євген Глібовицький.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">о. Павло Худ<br /> керівник Прес-служби Стрийської єпархії</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>\r\n<p>&nbsp;</p>',NULL,716,5,1,NULL,42),
(1049,'P','2008-11-29',1,NULL,'Note','Перша беатифікація на Кубі','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У суботу 29 листопада вперше на Кубі пройде церемонія беатифікації, тобто проголошення блаженним о. Хосе Олалло Валдеса, з Чину святого Івана Божого, членів якого називають також &laquo;боніфратами&raquo;.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У суботу 29 листопада вперше на Кубі пройде церемонія беатифікації, тобто проголошення блаженним о. Хосе Олалло Валдеса, з Чину святого Івана Божого, членів якого називають також &laquo;боніфратами&raquo;. Хоч це вже другий кубинський блаженний - першим був мученик о. Хосе Лопес Пітейра, що загинув під час переслідування католицької Церкви в Іспанії у тридцятих роках минулого сторіччя - але перший раз беатифікація буде проводитись саме на Кубі. Її очолить кардинал Жозе Сарайва Мартінс, вислужений префект Конгрегації у справах святих.<br /><br />Новий блаженний, якого почитають усі кубинці, не тільки віруючі, але й невіруючі, жив у ХІХ сторіччі і 54 роки свого життя посвятив доглядові за хворими та пораненими у лікарні, приносячи фізичне та духовне утішення, а це були важкі часи громадянської війни на Кубі та скасування усіх монаших згромаджень. І досі на Кубі його згадують як &laquo;апостола християнської любові&raquo; та &laquo;батька убогих&raquo;. Життя блаженного Олалло Валдеса є прикладом для усієї Церкви та великою заохотою для кубинців.</span></span></p>',NULL,717,5,1,NULL,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1050,'P','2010-10-01',1,NULL,'Note','«Євангеліє» про... «не пущать и запрещать»','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><strong>&laquo;Ворог українського народу&raquo; &ndash; УГКЦ?<br /><br /></strong>В листі до міської влади Одеси владика Агафангел аргументував, що &laquo;немає жодної необхідності&raquo; дозволяти греко-католикам мати свій власний храм і повчав чиновників, що &laquo;унія завжди була насильством над сумлінням українського та білоруського народів&raquo;.</span></span></p>','<div style=\"text-align: justify;\"><a href=\"../author/20913.html\"><span style=\"text-decoration: underline;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Іван Гвать</span></span></span></a></div>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Словаччина &ndash; Два тижні тому, напередодні чергової річниці страшного теракту у Всесвітньому торговельному центрі в Нью-Йорку, невідомий досі проповідник із американського штату Флоріда Террі Джонс тримав у напрузі практично весь світ. Він погрожував прилюдно спалити примірники священної книги мусульман &ndash; Коран, вважаючи, що ця релігія &ndash; від нечистого. А коли в світі полегшено зітхнули, що проповідник схаменувся і своєї обіцянки, яка призвела б до масових заворушень серед мусульман світу, не виконав, Радіо Свобода інформувало про вимогу єпископа Української православної церкви (МП) митрополита Одеського й Ізмаїльського Агафангела (Саввіна), щоб нашкодити Українській греко-католицькій церкві. </span></span></p>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Піар Террі Джонса вдався. Хоча б на кілька днів він став відомим на весь світ. Завдяки своїй ненависті до інаковіруючих. Йому навіть начебто пощастило довести правдивість (американської) приповідки: &laquo;Неможливо стати банкрутом, якщо торгуєш Богом&raquo;. <br /><br />Повернімося на &laquo;грішну&raquo; землю України. <br /><br />Митрополит Агафангел прилюдно спалювати &laquo;уніатські книги&raquo; напевно не збирається, але греко-католиків не терпить. Лише так можна зрозуміти його звернення до міської валди Одеси з проханням-вимогою заборонити будівництво храму для віруючих УГКЦ, яких у місті й в околиці біля 10 тисяч душ. Це громадяни України, платники податків, отже рівні в правах (і обов&acute;язках) із співгромадянами інших конфесій.<br /><br />В храмах греко-католиків не п&acute;ють, не сквернословлять, а моляться Богу.<br /><br />Хіба ж це не привід піти їм на зустріч?<br /><br />Виглядає, однак, що в УПЦ (МП) ще діють ієрархи з дивною наукою щодо інших християнських конфесій: &laquo;Не пущать и запрещать&raquo;.<br /></span></span><strong><br /><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Поганий приклад недавнього минулого </span></span></strong><br /><br /><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Колишній Львівсько-Тернопільський митрополит РПЦ Никодим (Руснак) 1989 року надіслав до місцевих органів КДБ донос на Українську греко-католицьку церкву, котра тоді почала відроджуватися. Мовляв, розберіться і вжийте необхідних заходів. Часи, однак, вже були не брежнєвські, текст листа-доносу передрукував журнал &laquo;Огонёк&raquo;. <br /><br />Никодима поспіхом перевели до Харкова. Оговтавшись &laquo;в связи с переходом на другую работу&raquo;, наповнивши порохівницю новим порохом, митрополит у червні 2001 року звернувся з листом до тодішнього керівника Харківської області, нині покійного Євгена Кушнарьова, з проханням не дозволити будівництво греко-католицького храму на території Харкова. <br /><br />Аргументація наводилась типово совєтська: зведення культової споруди єдиної в місті парафії Української греко-католицької церкви може призвести &laquo;до розпалювання міжрелігійної ворожнечі, а також принизить і образить православних&raquo; (УНІАН, 18.06. 2001).<br /><br />Виникає запитання: що зробив від себе митрополит Харківсько-Богодухівської єпархії УПЦ (МП) Никодим, щоб серед своїх вірників виплекати толерантне ставлення до співгромадян іншого віросповідання? <br /><br />Аргумент, що, мовляв, зведений для греко-католицьких християн храм у Харкові &laquo;принизить&raquo; православних віруючих є або проявом фундаменталізму (екзальтованої зарозумілості) владики, або наявністю мегакомплексу меншевартості. <br /><br />Якщо кожна гілка християнської релігії матиме у містах і селах України свій храм, то невже ж це образа для православних? Чому російські православні церкви у містах різних країн Західної Європи не викликають обурення тамтешніх католиків чи протестантів? Навряд чи такі запитання релігійні фанатики-фундаменталісти здатні сприймати на рівні здорового глузду.<br /><br />Подібних прикладів релігійної нетерпимості на пострадянський України 90-их років можна б навести чимало. <br /><br /><strong>Релігійна нетерпимість далі димить</strong><br /><br />Вона проявилася, зокрема, напередодні візиту папи Івана Павла ІІ до України влітку 2001 року. <br /><br />Дехто з віруючих &laquo;канонічої церкви&raquo; обзивав Понтифіка &laquo;антихристом&raquo;, &laquo;злодієм православних душ&raquo;. А один із ієрархів УПЦ (МП) на телевізійну камеру тоді публічно висловив бажання, аби Папі Римському не пощастило приземлитися в Києві, аби йому щось сталося ще поза межами повітряного простору України. <br /><br />Додав до цього свого шеляга і митрополит Одеський й Ізмаїльський Агафангел. Він звернувся тоді до своїх овечок словами: &laquo;С тревогой ожидает православная Одесчина приезд Римского понтифика&raquo; і закликав &laquo;во всех храмах ежедневно совершать акафистное пение Святителю Николаю, архиепископу Мир Ликийских, Чудотворцу об отражении нашествия латинян...&raquo; (&laquo;Независимая газета&raquo;-Религия, 14.06.2001). <br /><br />&laquo;Молитовний&raquo; заклик Агафангела був воланням із середньовіччя &ndash; часів міжрелігійних воєн з гаслом: кого володіння, того й релігія. А відгомін такого волання було чути й на порозі третього тисячоліття! <br /><br />Папа приїхав, помолився разом з греко- і римо-католиками України. &laquo;Нашествие латинян&raquo; скінчилося без &laquo;крадіжки&raquo; православних душ. До цього ніхто навіть і не додумався. <br /><br />Автор цих міркуван уже згадував про постать одеського владики з нагоди останнього візиту до України Московського патріарха Кирила, який кілька днів гостював в Агафангела (Радіо Свобода, 30.07.2010). Росіяни - Гундяєв і Саввін &ndash; однодумці, зокрема, якщо мова про реалізацію концепції &laquo;Русский мир&raquo; в Україні.<br /><br />Народний депутат України Ігор Гринів у своїх публіцистичних статтях наводить спогад колеги, народного депутата Ярослава Кендзьора, який свого часу зустрів нардепа і митрополита Агафангела в одній особі в ресторані Верховної ради України. Був Страсний четвер. Владика попивав коньяк і споживав вудженину. На зауваження Кендзьора про дотримання Великого посту, одеський святий отець цінічно відповів: &laquo;Для прохвоста нету поста!&raquo;. Дуже оригінальна самохарактеристика...<br /><br /><strong>&laquo;Ворог українського народу&raquo; &ndash; УГКЦ?</strong><br /><br />В листі до міської влади Одеси владика Агафангел аргументував, що &laquo;немає жодної необхідності&raquo; дозволяти греко-католикам мати свій власний храм і повчав чиновників, що &laquo;унія завжди була насильством над сумлінням українського та білоруського народів&raquo;.<br /><br />Не виключено, що декого з чиновників духовний отець, може, й переконав, зокрема тих, які знання про історію церкви в Україні почерпнули виключно з совєтських публікацій.<br /><br />Варто також нагадати, що реакція керівництва РПЦ (МП) у Києві на звернення митрополита Агафангела була незвичною. Мовляв, він висловив свою особисту думку, як керуючий єпархією і вона не відображає офіційну позицію церкви.<br /><br />Цікаво, відколи це в керівництві (Російської) УПЦ (МП) панує такий лібералізм? <br /><br />Мабуть, лише тоді, коли її єпископи з погордою, навіть із ненавистю висловлюються про інші конфесії, насамперед про Українську греко-католицьку церкву? Вона, як відомо, плекає серед своїх віруючих український патріотизм, богослужіння відбуваються українською мовою. <br /><br />Натомість УПЦ (МП) і далі є, на жаль, знаряддям русифікації України. <br />Щодо активності митрополита Агафангела, то вона, схоже, кормиться минулим і сучасним. Це традиційне московсько-російське недовір&acute;я до інших християнських конфесій, зокрема на постсовєтському просторі, який Російська церква вважає своєю &laquo;канонічною територією&raquo;. Згадаймо: під час окупації Галичини у Першій світовій війні брати-слов&acute;яни з Півночі подбали, насамперед, про обезголовлення греко-католицької церкви на Галичині. Митрополита Андрея Шептицького запроторили в тюрму вглиб Росії.<br /><br />Аналогічно поступили органи НКВС 1945-1946 роках, заарештувавши всіх єпископів УГКЦ. Російська православна церква потирала руки і, як могла, допомагала в цьому.<br /><br />Сучасне джерело сміливості для таких, як одеський владика, виступати проти співгромадян греко-католиків &ndash; публічні одкровення таких політиків, як міністр освіти й науки України Дмитро Табачник. Він теревенить (враження, що навіть цього бажає) про протистояння в Україні між &laquo;римсько-уніатсько-галіцийським&raquo; та &laquo;російсько-православним&raquo; етносами (і цивілізаціями). В цих розумуваннях стільки &laquo;правди&raquo; &laquo;також історика&raquo; Табачника, як, скажімо, у спробах протиставити &laquo;римсько-баварсько-католицький&raquo; етнос на півдні Німеччини &laquo;прусько-протестантському&raquo; етносу на півночі ФРН.<br /><br />Наспрвді, це підступна цілеспрямована антиукраїнська пропаганда, що відгонить сучасним маніхейством, замішаним на російських неоімперсько-націоналістичних дріжджах. Її мета &ndash; сприяти реалізації концепції московського патріарха Кирила про &laquo;Русский мир&raquo;, складовою якого є, крім росіян, також українці та білорусі, навіть і молдаван до цього причепили, мовляв &laquo;мы &ndash; единый народ&raquo;. Це спроба повернути Україну в стан малоросійства, звичайного придатка Росії. <br /><br />В такому контексті співгромадяни греко-католицького віросповідання в Україні є для табачників та агафангелів ворогами малоросійства, тому їм треба принаймні заборонити мати свої храми, щоб молитвою на рідній мови не &laquo;принижували&raquo; російське православ&acute;я. А далі побачимо... <br /><br />Так міркують деяки світила УПЦ(МП), насамперед, митрополит Одеський й Ізмаїльський.<br /><br />Врешті-решт, запитуєш себе: чи живим є серед православних владик Харкова, Одеси та в інших містах України таке поняття, як милосердя &ndash; наріжний камін євангельського вчення, яке проповідував Ісус Христос? Чи є взагалі вирозумлення до потреб співгромадян &ndash; християн іншого віросповідання? Чи далі панує наука: &laquo;не пущать и запрещать&raquo;?<br /><br />До речі, в Харкові храм для віруючих Української греко-католицької церкви, бодай з труднощами, проте будують. Міські чиновники збагнули, що краще сприяти міжконфесійному миру, ніж мати протистояння. Сподіваймося, що не забракне здорового глузду й чиновникам Одеси.<br /><br /><em>Думки, висловлені в рубриці &laquo;Точка зору&raquo;, передають погляди самих авторів і не конче відображають позицію Радіо Свобода.</em></span></span></div>\r\n<div style=\"text-align: justify;\"><a href=\"http://www.radiosvoboda.org/content/article/2167856.html\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">http://www.radiosvoboda.org/content/article/2167856.html</span></span></a></div>',NULL,2175,5,1,NULL,28);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1051,'P','2008-11-29',1,NULL,'Rss','Листи кардиналів Любомира Гузара та Мар’яна Яворського у зв’язку з початком служіння нового римо-католицького Митрополита в Україні','<p>Глава Української Греко-Католицької Церкви кардинал Любомир Гузар в листі до Митрополита-емерита Римо-Католицької Церкви в Україні кардинала Мар\'яна Яворського пояснив, що його відсутність на інгресі нового Архиєпископа-митрополита Мечислава Мокшицького до Львівської латинської катедри була зумовлена участю у відзначенні в місті Києві на державному рівні 75-ліття Голодомору в Україні. Разом з тим, Блаженніший Любомир зазначив, що він хотів би бути у Львові, щоб привітати нового Митрополита, але понад усе - щоби належно попрощатися з Його Еміненцією, незалежно від того, де кардинал Яворський буде в майбутньому перебувати.</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=120:lysty-kardynaliv-ljubomyra-guzara-ta-marjana-javorskogo-u-zvjazku-z-pochatkom-sluzhinnja-novogo-rymo-katolyckogo-mytropolyta-v-ukrajini&catid=27:gk-v-ukrajini-cat&Itemid=57',718,NULL,1,1,26),
(1052,'P','2008-11-29',1,NULL,'Rss','Зустріч католицьких єпископів двох обрядів в Україні','<p>На побажання Святішого Отця Венедикта XVI, яке Папа висловив під час візиту <em>ad limina</em> єпископів Римо-Католицької Церкви в Україні і Української Греко-Католицької Церкви, започатковано регулярні зустрічі католицьких єпископів двох обрядів. Перша така зустріч відбулася 28 листопада 2008 р. у Реколекційному Центрі Української Греко-Католицької Церкви в селищі Брюховичах біля Львова. Делегацію єпископів східного обряду очолював Верховний Архиєпископ Києво-Галицький кардинал Любомир Гузар, а делегацію єпископів латинського обряду - Митрополит Львівський Мечислав Мокшицький.</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=121:zustrich-katolyckyh-jepyskopiv-dvoh-obrjadiv-v-ukrajini&catid=27:gk-v-ukrajini-cat&Itemid=57',719,NULL,1,1,26),
(1053,'P','2008-11-29',1,NULL,'Note','Рим: доповідь про Голодомор в Україні','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У неділю 30 листопада релігійне товариство &laquo;Свята Софія&raquo; для українців католиків та Український католицький університет святого Климента Папи в Римі організовують зустріч, присвячену відзначенню 75-ої річниці Голодомору в Україні. З доповіддю на тему &laquo;Реакція на Голодомор 32-33 рр. в Україні у спогадах чужинців&raquo; виступить о. д-р Іван Музичка. Зустріч буде проведена у залі УКУ по вул. Боччеа о 12-ій годині, відразу після закінчення Святої Літургії.</span></span></p>','',NULL,720,5,1,NULL,43),
(1054,'P','2008-11-30',1,NULL,'Rss','ПРОГРАМА ХАРЧОВОЇ ДОПОМОГИ ПОТРЕБУЮЧИМ І ГОЛОДУЮЧИМ','<p>Допомога і місцева потреба</p>\n<p>У ці складні економічні часи Церква святого Миколая Чудотворця на Аскольдовій Могилі допомагає людям, котрі шукають допомоги в харчах, дехто вперше. Зараз більше, ніж коли-небудь, наші соціальні служби хочуть бути певні, що люди, вражені падінням економіки і зростанням цін на продукти, мають доступ до здорової їжі, щоб прогодувати себе і свої сім\'ї. Церква святого Миколая Чудотворця робить усе можливе, щоб гідно відповісти на зростання цих потреб і потребує допомоги громади для продовження своєї місії.</p>\n<p> </p>\n<p>Серед потребуючих - літні люди, бездомні, одинокі батьки і збіднілі сім\'ї. Дитяче недогодовування, зокрема, може мати загрозливі наслідки. Діти, народжені в сім\'ях, які не здатні їх прогодувати, на 25% частіше потрапляють до лікарень. Брак поживної їжі також може зашкодити розумовому і фізичному розвитку дитини, спричинивши багато по життєвих проблем. З вашою допомогою ми би могли допомагати людям у Києві!</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=122:programa-harchovoji-dopomogy-potrebujuchym-i-golodujuchym&catid=29:propovidi-cat&Itemid=34',723,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1055,'P','2010-10-15',1,NULL,'Note','ДАРИ НА ПОКРОВ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,3659,200,7.jpg\" alt=\"\" />Свято Покрова Пресвятої Богородиці, як і кожне церковне свято в Україні, обросло безліччю народних традицій і звичаїв, так що набуло навіть нового, вже не церковного звучання - стало святом українського війська -- козацтва та Української Повстанської Армії. Але, мабуть, мало хто знає, що на Покрова український віруючий народ дотримувався і, на щастя, дотримується досі ще однієї традиції - обдаровувати церкву щедрими і щирими дарами. Це про таких людей на кожній Літургії молимося: &laquo;жертводавців святого храму цього... - Господи, помилуй&raquo;.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,3659,200,7.jpg\" alt=\"\" />Свято Покрова Пресвятої Богородиці, як і кожне церковне свято в Україні,илуй&raquo;.</span> обросло безліччю народних традицій і звичаїв, так що набуло навіть нового, вже не церковного звучання - стало святом українського війська -- козацтва та Української Повстанської Армії. Але, мабуть, мало хто знає, що на Покрова український віруючий народ дотримувався і, на щастя, дотримується досі ще однієї традиції - обдаровувати церкву щедрими і щирими дарами. Це про таких людей на кожній Літургії молимося: &laquo;жертводавців святого храму цього... - Господи, пом</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3659,200,8.jpg\" alt=\"\" />Так, цього року день пам&rsquo;яті Пресвятої Богородиці, що простягла свій омофор на захист свого люду в Цареграді, парафіяни нашої церкви пп. Ігор та Ольга Давидови пожертвували розкішний образ Зарваницької Божої Матері, який для нашого храму вже віддавна став особливим оберегом і особливою святинею. Ікона незвичайна - вона вилита з бронзи; корони на головах Богородиці та Дитятка Ісуса позолочені, тло образу вкрите сріблом. Це не перший подарунок від подружжя Давидових, саме стараннями п. Ігоря в нашому храмі встановлені Царські врата та два підсвічники-ставники.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,3659,200,3.jpg\" alt=\"\" />Ще одним подарунком для громади &laquo;аскольдівців&raquo; став візит Владики Йосифа Міляна, єпископа-помічника Київської архиєпархії, який відслужив у храмі архиєрейську Божественну Літургію. Співслужили владиці ієреї о. Роман Сиротич, директор &laquo;Карітас-Київ&raquo;, о. Олекса Петрів, керівник справами Секретаріату УГКЦ, о. Костянтин Пантелей, референт у справах тюремного служіння УГКЦ, о. Богдан Чурило; священики нашого храму отець настоятель Ігор Онишкевич, о. Анатолій Тесля, о. Олексій Геник та отці диякони Олександр Соколовський і Михайло Продіус.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3659,200,5.jpg\" alt=\"\" />Владика Йосиф у своїй проповіді на євангельське читання свята закликав</span> замислитися над тим, що сучасна людина, часто забезпечена матеріальними і соціальними благами, залишається в суті речі полишеною, одинокою, порожньою. Це над нею, самотньою і покинутою, Богородиця простирає свій захисний покров, це її, самовпевнену, але слабку, прагне захистити. І парадоксальним і водночас знаменним здається те, що євангельське читання свята Покрова Пресвятої Богородиці розповідає нам історію інших людей, а саме Марії та Марти, котрі посперечалися в присутності Ісуса Христа, яку частку в служінні Йому варто їм обрати. І<img style=\"float: right;\" src=\"image,3659,200,9.jpg\" alt=\"\" /> лише одним-єдиним вигуком хтось із натовпу Ісусових слухачів згадав про ту, що дала Йому життя, навіть не назвавши її по імені: &laquo;Блаженні груди, що годували тебе...&raquo; Вітаючи присутнє на Літургії жіноцтво, що носить таке популярне в Україні ім&rsquo;я Марії, тієї, що так скромно, але так потужно присутня за кожним діянням, трудом і муками Свого Сина, владика Йосиф благословив в особі іменинниць із нашої парафії всіх українських матерів.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3659,200,11.jpg\" alt=\"\" />Владика Йосиф освятив новий образ Зарваницької Богородиці та благословив жертводавців подружжя Давидових. Відтак громада продовжила святкування за спільною трапезою.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,3659,200,13.jpg\" alt=\"\" />На завершення додамо, що початок святу Покрови Пресвятої Богородиці поклала відома подія, коли Богородиця захистила від нападу сарацинів християн, котрі перебували у безнадійному оточенні в одній із церков Константинополя. Подія ця відбулася понад тисячу років тому, в далекому Х столітті. Якось трепетно і зворушливо усвідомлювати, що всі ми святкували її пам&rsquo;ять у найдавнішому християнському храмі України-Руси, покровителем якого є цар Аскольд - сучасник тих легендарних часів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">отограф: Віталіна Онишкевич</span></span></p>',NULL,2176,4,1,NULL,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1056,'P','2008-12-01',0,NULL,'Note','ОГОЛОШЕННЯ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,1702,200,aids_ribbon.jpg\" alt=\"\" />1-го грудня у день Всесвітньої боротьби зі СНІДом у церкві св. Миколая Чудотворця на Аскольдовій Могилі о 17:30 відспівають молебен за спасіння постраждалих від ВІЛ, за їхнє оздоровлення, навернення до Бога. Будуть згадані усі хто допомагає ВІЛ інфікованим. Лампадками буде викладено знак співчуття ВІЛ інфікованим. Особливе співчуття дітям які безневинно постраждали від цієї страшної хвороби.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,1702,200,aids_ribbon.jpg\" alt=\"\" />1-го грудня у день Всесвітньої боротьби зі СНІДом у церкві св. Миколая Чудотворця на Аскольдовій Могилі о 17:30 відспівають молебен за спасіння постраждалих від ВІЛ, за їхнє оздоровлення, навернення до Бога. Будуть згадані усі хто допомагає ВІЛ інфікованим. Лампадками буде викладено знак співчуття ВІЛ інфікованим. Особливе співчуття дітям які безневинно постраждали від цієї страшної хвороби.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Зрозуміло, що рятунком для усіх є навернення до Божих заповідей, дотримання святості родинного життя, сімейного життя, чистоти до шлюбу і вірності в шлюбі. Виховання дітей, це єдиний шлях поєднання з Богом, щоб врятувати світ від цієї кари, яку самі люди на себе накликали.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дуже прикро, що сьогодні проблема СНІДу дуже гостро торкається наших родин, зокрема новонароджених дітей, безвинних дітей. Саме тому ми просимо щоб Господь був милостивий до всіх хто зіткнувся з цією проблемою.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Особлива подяка отцю Анатолію Теслі, який багато років допомагає родинам, які стикнулися з цією хворобою. Його молитва допомагає &nbsp;окремим людям поборювати цю недугу і навертатись до правдивого християнського життя. Отець Анатолій і надалі бажає розширити своє медичне капеланство, допомагати цим людям. Звертається з проханням до волонтерів допомагати йому у цій нелегкій праці, укріпляти словом Божим, молитвою тих хто страждає цією недугою.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Просимо усіх бажаючих допомогти отцю Анатолію у цьому служінні.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Матеріал Іллі, &laquo;Оранта&raquo;</span></span></p>\r\n<p>&nbsp;</p>',NULL,722,5,1,NULL,17),
(1057,'P','2008-11-30',1,NULL,'Note','Помер один із засновників УНА-УНСО','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">28 листопада у віці 67 років пішов з життя Петро Хмарук - член Проводу УНА-УНСО, якого вважали людиною-легендою, який провів у політув\'язненні понад 35 років.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">28 листопада у віці 67 років пішов з життя Петро Хмарук - член Проводу УНА-УНСО, якого вважали людиною-легендою, який провів у політув\'язненні понад 35 років. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Як повідомили у прес-службі УНА-УНСО, Хмарук стояв біля витоків організації. Він був організатором повоєнного підпільного руху в Україні. З 16 років - професійний революціонер, згодом - невловимий керівник антирадянського підпілля на Київщині, відомий публіцист. За його затримання КДБ тричі призначав винагороду.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В результаті переслідування режимом Хмарук провів в ув\'язненні понад 35 років, проте політичну діяльність не припинив. Він - ініціатор численних страйків ув\'язнених у радянських концтаборах.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Прощання з покійним відбудеться 1 грудня об 11:00 у церкві на Аскольдовій Могилі.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://ua.korrespondent.net/</span></span></p>',NULL,727,5,1,NULL,30);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1058,'P','2010-10-16',1,NULL,'Rss','3 тисячі дітей врятовано завдяки молитві ','<p>Американська  національна кампанія „40 Days for Life ” (40&nbsp; Днів за Життя) врятувала  щонайменше 3 тисячі дітей від смерті. Кампанія полягала на молитві та  показуванню жінкам альтернативи вбивства власної дитини.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=604:3-tysjachi-dite-vrjatovano-zavdjaky-molytvi-&catid=32:gk-svit-cat&Itemid=58',2177,NULL,1,1,26),
(1059,'P','2008-11-30',1,NULL,'Note','Проповідь Папи Венедикта XVI з нагоди початку Адвенту','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У своїй проповіді Венедикт&nbsp;XVI зауважив, що першим періодом літургічного року є Адвент, тобто час очікування на прихід Господа. Однак, тут йдеться не так про якийсь визначений період в майбутньому, але про духовний вимір, в якому вже тепер можемо прямувати назустріч Богові.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вчора, 29 листопада 2008&nbsp;р., ввечері, у ватиканський базиліці Святого Петра Вселенський Архиєрей Венедикт&nbsp;XVI відслужив урочисту Вечірню з нагоди початку Адвенту - періоду безпосереднього приготування до Різдва, яким у Римо-католицькій Церкві також розпочинається літургічний рік. <br /><br />У своїй проповіді Венедикт&nbsp;XVI зауважив, що першим періодом літургічного року є Адвент, тобто час очікування на прихід Господа. Однак, тут йдеться не так про якийсь визначений період в майбутньому, але про духовний вимір, в якому вже тепер можемо прямувати назустріч Богові. &laquo;У дійсності, саме це ми переживаємо в літургії: святкуючи літургічні періоди, ми уприсутнюємо таїнство&raquo;, - зазначив Папа. Цього разу - це таїнство приходу Бога, і ми прямуємо разом з ним до його остаточного здійснення в кінці часів, однак, уже тепер черпаємо з ласки спасіння. &laquo;Надія&raquo; - це слово, яке підсумовує цей особливий стан, коли ми чекаємо на щось, що має здійснитися, а, одночасно, вже можемо це передбачати і передчувати, вів далі Венедикт&nbsp;XVI, звернувши увагу на те, що Адвент є у найвищому ступені духовним періодом надії.<br /><br />&laquo;Господи, до Тебе взиваю, поспіши мені на допомогу&raquo;, - ці слова з Псалма, що проказуються під час Вечірні, є закликом, який підноситься в особистій небезпеці, але є також молитвою Церкви перед обличчям численних спокус, які її оточують, які загрожують її святості і цілісності. У цьому закликові можна почути також голос всіх праведних, які прагнуть протистояти злу, спокусі егоїстичного добробуту чи приємностей, які ображають людську гідність. Папа пригадав, що Ісус взяв на себе всі наші терпіння і спокуси, щоб дати нам благодать своєї перемоги.<br /><br />&laquo;Під час свого першого приходу через втілення, Божий Син хотів в усьому розділити разом з нами нашу людську природу. Очевидно, не брав участі в гріхові, але задля нашого спасіння переніс усі його наслідки&raquo;, - сказав Святіший Отець, зазначаючи, що заклик до надії, який приносить Адвент, від самого початку і виразно показує наше безумовне прагнення спасіння. &laquo;Можна сказати, що чекаємо на Спасителя, який, жодним чином, не є лише гарною оздобою вже спасенного світу, але єдиним шляхом визволення із смертельної небезпеки&raquo;, - підкреслив Венедикт&nbsp;XVI, додаючи, що усвідомлення цієї істини захищає нас від спокуси втікати від дійсності, але показує, що єдиною справжньою надією, яка не розчаровує, є пасхальна надія, якою прославляємо воплоченого, розп\'ятого і воскреслого Господа всесвіту.</span></span></p>',NULL,724,5,1,NULL,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1060,'P','2010-10-16',1,NULL,'Rss','Жебраю','<p>Жебраю</p>\n<p>відкрити очі зранку і згадати про Тебе</p>\n<p>не про дитину роботу хворобу кохання</p>\n<p>жебраю</p>\n<p>залишити каву й сніданок щоб не спізнитись до Церкви</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=606:zhebraju&catid=1:mystecka-storinka-cat&Itemid=50',2182,NULL,1,1,26),
(1061,'P','2010-10-17',1,NULL,'Note','Все в Божих руках','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Дорогі в Христі парафіяни!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Всі хто допомагає нашій церковній Службі Милосердя для потребуючих жінок.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">До Вашої уваги два листи. Які не буду коментувати, але ці дві жіночі долі знайшли нас і просять милосердя.&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Прочитайте їх листи. Вірю, що буде відклик і поради.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Дорогі в Христі парафіяни!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Всі хто допомагає нашій церковній Службі Милосердя для потребуючих жінок.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">До Вашої уваги два листи. Які не буду коментувати, але ці дві жіночі долі знайшли нас і просять милосердя.&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Прочитайте їх листи. Вірю, що буде відклик і поради.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Слава Господу нашему Иисусу Христу!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Мир Вашему дому!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Отец Игорь, сразу хочу принести извинения, что отрываю Вас от богоугодных дел. Пишу Вам письмо с мольбой о помощи. Очень, очень слёзно прошу Вас помогите мне. На свободе у меня осталась мама, ей 79 лет, она очень старенькая и сильно больная. Если у Вас есть такая возможность, помогите ей, чем сможете. У моей мамочки никого нет кроме меня, у неё сильно болят ноги, она плохо ходит, на улицу не выходит вообще, очень высокое давление, больное сердце. На её скудную пенсию невозможно купить медикаментов, её пенсии с трудом хватает оплатить коммунальные услуги. Если у Вас есть такая возможность, умоляю Вас помогите ей чем сможете. Вот её адрес:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Артеменко Мария Васильевна</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ул.Орловская, д.55, кв.1-а</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; г.Днепропетровск&nbsp;&nbsp;&nbsp;&nbsp; 49017</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я Вам буду благодарна от всего чистого сердца, буду за Вас молиться. Пусть Господь Бог благословляет Вас&nbsp; в Ваших добрых делах.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; С любовью о Господе</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Маргарита</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; P.S. Мне Господь Бог дал дар я пишу стихи и вот один из них:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Голубка, Горлица мне птица дорогая</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Неужто ты несешь</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Всех бед моих венец?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Прости меня, моя Родная,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Что не смогла наполнить счастьем</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Жизни твой ларец.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Прости, что дочь твоя споткнулась</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; На волосы добавив седины</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Прости её, она в себе замкнулась</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Она попала в лапы сатаны.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ты плачешь пересматривая снимки</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; На снимке гладишь юное лицо</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; И капают на жизнь твои слезинки</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; И ты не в силах ей сейчас помочь.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Моя родная, если бы ты знала,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Как я скучаю и тоскую за тобой</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Себя я презираю и ругаю</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Мне Божье наказанье твоя боль.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Отец Игорь, этот стих я посвящала своей мамочке. А ещё я умею красиво вышивать, меня этому в детстве научила моя мама, но для вышивания здесь ничего нет, ни полотна, ни ниток, ни иголок, ни пяльцев.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Пусть Господь Бог благословляет Вас в Ваших добрых делах.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; С любовью о Господе</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Маргарита</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Здравствуйте дорогой отец Игорь!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Пишет Вам Наталья и мой сынок Герман. Вы проигнорировали моё предыдущее письмо, но горе, что постигло моих детей и меня обязывает написать Вам. А в принципе мне то и писать некуда и некому. Я постараюсь вкратце описать свою ужасную беду, в чем сейчас я нуждаюсь. Только я прошу Вас ответьте мне сразу, пожалуйста! Так как у меня срок времени очень маленький &mdash; 3 месяца. А решать мою проблему надо начинать уже сейчас. Как Вы знаете из моих прежних писем, дома ждали меня двое старших детей: Настя (15 лет) и Влад (13 лет) и больная мама (70 лет). Так вот у мамы был инсульт. Она умерла. Я не могу описать что я чувствую. Но это очень страшное чувство. Всё перемешалось: боль, пустота, гнев на себя, страх, обида. Я не верю в это. Детей забрали на 3 месяца в приют. Потом если я не бъявлюсь &mdash; в интернат.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ну а теперь я объяснюсь, почему пишу Вам. У меня через 3 месяца открывается моя первая льгота &mdash; УДО. Я знаю со слов своего отрядного что могу его пройти. Только есть одно НО &mdash; это иск. Общая сумма 3 тыс. гривен. Но мне можно выплатить хотя бы часть иска. Эта часть 1 тыс. гривен. Я знаю, Вы мне, когда потерялось наше фото у Вас, писали что деньгами не помогаете. Но я очень Вас сильно, слёзно прошу войдите в мою ситуацию, пожалуйста! Помогите мне сколько сможете. Сделайте один раз исключение. У меня есть шанс уйти раньше. Тем более сейчас когда дети нуждаются во мне как никогда. В позапрошлом году они похоронили дедушку, в том году своего папу &mdash; моего мужа, в этом году бабушка умерла. Слишком много беды для ихних маленьких сердец. Я пишу Вам с мольбой в сердце что Вы поможете всё же. Иначе мне не пройти эту комиссию по УДО. И ждать придется что нам с Германом, что Насте с Владиком конца нашего срока. Дом опечатан, но где гарантия что его не разберут по кирпичикам. Сейчас такое творится. И не боятся же. Тут оступилась, поймали и гнев на себя имеешь, и просишь у Бога прощения, и думаешь простит ли? А это как посмотришь новости в страхе замираешь от нечеловеческой жестокости и тупости.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Уважаемый, дорогой отец Игорь и все служители храма! Я прошу у Господа и у всех вас, прошу как женщина и как мать. От себя и от имён своих троих деток. Помогите осуществиться нашей мечте &mdash; нашей встрече! На которую у меня большие намерения. Помогите нам дорогие люди, братья и сестры встретиться нам четверым, родным не через полтора года а через три месяца. Настенька с Владиком как раз будут три месяца в приюте, а потом их в интернат переведут. Я не хочу чтобы их в интернат отдавали, я хочу их забрать домой. Мамы уже не вернуть но есть шанс вернуть то есть забрать детей.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Дорогой отец Игорь, прочитайте моё письмо всем вслух. Я буду ждать что хоть кто нибудь но отзовётся на крик моей души. Поможет надеюсь хоть кто-нибудь, хоть какой-нибудь копеечкой. И я пройду УДО и встретятся наши четыре сердца. Я долго жду этой встречи. Прошу помогите. Времени осталось уже совсем мало &mdash; 3 месяца. Я очень хочу верить в это чудо. Это действительно будет чудо.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Пусть Вас хранит и благословляет Всемогущий наш Господь.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; С искренним уважением</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Наталья и мой малыш.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 30.09.2010 г.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; С нетерпением жду ответ.&nbsp;&nbsp;&nbsp;</span></span></p>',NULL,2184,4,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1062,'P','2010-10-22',1,NULL,'Note','Зустріч капеланів з керівництвом кримінально-виконавчої служби','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: right;\" src=\"image,3669,200,DSC_0231.JPG\" alt=\"\" width=\"200\" height=\"133\" />У вівторок, 19 жовтня 2010 року в центральному Управлінні Державного Департаменту з питань покарань відбулася зустріч керівництва з відповідальними в Церквах за&nbsp; в&rsquo;язничне служіння.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;В 2001 році головні Церкви в Україні створили Українську Міжконфесійну Християнську Місію &laquo;Духовна та благодійна опіка в місцях позбавлення волі&raquo;. Церкви &ndash; члени місії з того часу мають можливість&nbsp; координувати в&rsquo;язничне служіння та представляти свої інтереси перед Департаментом.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Перед засіданням в департаменті відбулася зустріч священнослужителів та представників департаменту і молитва в домініканському Інституті Релігійних Наук святого Томи Аквінсього, що є&nbsp; поблизу управління.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">У вівторок, 19 жовтня 2010 року в центральному Управлінні Державного Департаменту з питань покарань відбулася зустріч керівництва з відповідальними в Церквах за&nbsp; в&rsquo;язничне служіння. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">В 2001 році головні Церкви в Україні створили Українську Міжконфесійну Християнську Місію &laquo;Духовна та благодійна опіка в місцях позбавлення волі&raquo;. Церкви &ndash; члени місії з того часу мають можливість&nbsp; координувати в&rsquo;язничне служіння та представляти свої інтереси перед Департаментом.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Перед засіданням в департаменті відбулася зустріч священнослужителів та представників департаменту і молитва в домініканському Інституті Релігійних Наук святого Томи Аквінсього, що є&nbsp; поблизу управління.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Молитва звучала також підчас засідання. Метою зустрічі було представлення справ в&rsquo;язничного душпастирства новому керівництву Департаменту. Заступник Голови, Микола Ковальчук сказав, що Департамент високо цінує релігійне служіння в установах позбавлення волі. В&rsquo;язні, як особлива частина суспільства, потребують більше турботи. В рамках регулярних зустрічей і в робочій групі будуть обговорені подальші правові аспекти служіння з метою його удосконалення.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">З промови представника капеланів о. Григорія Драуса до керівництва Департаменту:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&laquo;Релігійне служіння від самого початку було суттєвою частиною в&rsquo;язничної системи, воно допомагає у досягненні дуже важливої мети покарання, а саме &nbsp;- переоцінки цінностей людини. Ми, в&rsquo;язничні капелани, віримо у те, що людина може відродитись в Ісусі Христі, наповнитись Його Духом і преобразитись на Божу природу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Наша зустріч є складовою частиною плану відновлення обличчя землі, а ми в свою чергу впевнені, що &nbsp;земля може відновитись завдяки Святому Духові Утішителеві.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Українська Міжконфесійна Християнська Місія \"Духовна та благодійна опіка в місцях позбавлення волі\", є частиною міжнародної Асоціації Тюремного Служіння, яка існує в майже ста країнах світу, і є, можна сказати, найбільшою профспілкою капеланів. Особливість цієї організації в Україні полягає у тому, що до неї входять не окремі особи, а Церкви, котрі проводять релігійне служіння в установах позбавлення волі. До Місії входять головні, серйозні Церкви, котрі дотримуються християнських принципів, взаємної поваги і бажання співпраці, не допускаючи у своє лоно деструктивні чи екстремальні культи. Місія співпрацює з в&rsquo;язничним Департаментом, прагнучи розвивати правову базу на користь служіння і усієї системи. Ми прагнемо підносити рівень підготовки служителів, навчаючи їх на курсах, які вже двічі проводились завдяки Білоцерківському Училищу, і які допомагають капеланам поглибити своє знання про специфіку місця, у якому вони проводять служіння.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">У церковному словнику &nbsp;є слово \"в&rsquo;язничний &nbsp;капелан\" (від латинського &laquo;капелла&raquo; - частина). Це особа, яка проводить релігійне служіння у місцях позбавлення волі. Частина суспільства, до якої належать особи, які позбавлені волі, потребує великої духовної підтримки і опіки. Ми бажаємо співпрацювати з Департаментом, бути для нього допомогою. Ми прислуховуємось вказівок та очікувань Департаменту.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Для подальшого розвитку співпраці і якості служіння, просимо про &nbsp;проведення регулярних зустрічей і створення комісії, до якої б входили три представника Місії, для вивчення аспектів релігійного служіння у місцях позбавлення волі.&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">На минулому тижні усі Церкви, які входять до складу Місії, молились про Боже благословення для усього персоналу кримінально - виконавчої служби, і надалі &nbsp;обіцяємо продовжувати цю молитву&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><strong><a href=\"index.php?option=com_true&amp;Itemid=238&amp;func=viewcategory&amp;catid=175\" target=\"_self\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">фоторепортаж</span></span></a></strong></p>\r\n<p><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><em><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">о.Григорій Драус</span></span></em></p>\r\n<p style=\"text-align: justify;\"><a href=\"http://www.catholic-media.org/ua/index.php?option=com_content&amp;task=view&amp;id=3056&amp;Itemid=1\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">http://www.catholic-media.org/ua/index.php?option=com_content&amp;task=view&amp;id=3056&amp;Itemid=</span></span>1</a></p>',NULL,2186,4,1,NULL,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1063,'P','2010-10-22',1,NULL,'Note','Катехитична школа','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: left;\" src=\"image,3671,200,1.jpg\" alt=\"\" width=\"200\" height=\"132\" />З початком Літургічного року в нашій церкві розпочалися заняття катехитичної школи. Вже традицією стало проведення однієї з перших катехизацій в родинному колі на природі. Не виключенням було і цьогорічне заняття. 18 вересня близько п&rsquo;ятдесяти дорослих та дітей вирушили до передмістя Києва &ndash; боярського лісу. Запланованій суботі передував теплий сонячний тиждень, а в зазначений час погода різко перемінилася на прохолодну дощову. Та це, звичайно, не злякало переважну більшість бажаючих розділити радість споживання духовної та іншої смачної поживи для душі і тіла.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: left;\" src=\"image,3671,200,1.jpg\" alt=\"\" width=\"200\" height=\"132\" />З початком Літургічного року в нашій церкві розпочалися заняття катехитичної школи. Вже традицією стало проведення однієї з перших катехизацій в родинному колі на природі. Не виключенням було і цьогорічне заняття. 18 вересня близько п&rsquo;ятдесяти дорослих та дітей вирушили до передмістя Києва &ndash; боярського лісу. Запланованій суботі передував теплий сонячний тиждень, а в зазначений час погода різко перемінилася на прохолодну дощову. Та це, звичайно, не злякало переважну більшість бажаючих розділити радість споживання духовної та іншої смачної поживи для душі і тіла.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: right;\" src=\"image,3671,200,3.jpg\" alt=\"\" width=\"132\" height=\"200\" />Отже, по приїзді на обране місце і всіх приготуваннях, зустріч розпочалася Літургією, яку провадив о. Ігор Онишкевич. Далі всі учасники розділилися на учнівські групки і взяли участь у, власне, занятті. Цього року працею катехитів курує с. Філомена Чворсюк, СНДМ. Сестричку з перших днів полюбили наші дітки і вже з задоволенням і трепетом сердечок слухають науки, які вона провадить. Оскільки особлива увага в катехизації, приділяється дітям, що готуються до першого причастя, з ними і розпочала займатися с. Філомена. Заняття на природі плавно перейшло в ігри, ну і як без пікнікового столу силами батьків? Кульмінаційною стравою був безперчно шашлик із курятини, свинини і риби, приготований і пожертвуваний сім&rsquo;єю Петра та Оксани Галуги.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: left;\" src=\"image,3671,200,7.jpg\" alt=\"\" width=\"200\" height=\"132\" />Такі зустрічі єднають&nbsp; сім&rsquo;ї, підсилюють відчуття церкви родинної, знайомлять парафіян ближче, щоб усі члени одного тіла жиле як одне ціле в мирі, злагоді і любові.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Перше виїзне заняття дало початок ще низці катехизацій за межами церковного подвір&rsquo;я.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: right;\" src=\"image,3671,200,10.jpg\" alt=\"\" width=\"200\" height=\"132\" />Так, наступна неділя вже радувала сонечком і першопричасники з батьками, на чолі з о. Ігорем вирушили до нової культурної візитівки Києва &ndash; скансену &laquo;</span></span><a href=\"http://mamajeva-sloboda.ua/\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Мамаєва слобода</span></span></a><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&raquo;. Сестричка Філомена та катехитка Світлана Заяць виклали чергову катехизацію для дітей, що розмістились прямо на широкій лаві під церквою Покрова Пресвятої Богородиці. Пізніше приємна дівчина-екскурсовод Одарка провела цікаву екскурсію закуточками музею, розповіла про побут, звичаї та легенди козацьких часів. І далі за вільною програмою всі змогли насолодитися та подивувати прекрасному місцю відродження українських традицій, відпочити за столом місцевої корчми.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: left;\" src=\"image,3671,200,6.jpg\" alt=\"\" width=\"200\" height=\"132\" />Як відомо, надзвичайно пишно і з великою пошаною українці святкують празник Покрови. В церкві на Аскольдовій могилі урочистості розпочалися ще у неділю 10 жовтня гарним концертом, підготованим молоддю та хором парафії під керівництвом Каті Чаплик. А вже в через тиждень в неділю, за традицією, що склалася в останні роки, парафіяни чисельно і організовано вирушили на святкову Службу Божу до церкви Покрова Пресвятої Богородиці у с. Пирогові, що під Києвом. Після Літургії всі присутні взяли участь у хресному ході навколо церкви та по тому милували очі та вуха прекрасним виступом пісенного колективу, побували на місцевому ярмарку народних умільців та частувалися смаколиками. А дітки? Авжеж &ndash; слухали чергові катехитичні науки в стінах дерев&rsquo;яної церкви.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: right;\" src=\"image,3671,200,11.jpg\" alt=\"\" width=\"200\" height=\"132\" />На носі перші шкільні канікули, і оскільки багато діток роз&rsquo;їжджаються на відпочинок, а також, в зв&rsquo;язку із грядущими виборами, катехитична школа також іде на короткі канікули. А вже з 7 листопада ласкаво запрошуємо всіх бажаючих до навчання. Школа провадиться окремо для кількох вікових груп:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">дошкільнята (4-7 років),</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">першопричасники (з 7 років і всі, хто ще не приступав до Тайни Сповіді),</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">післяпричасники,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">молодь,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">батьки.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: left;\" src=\"image,3671,200,9.jpg\" alt=\"\" width=\"200\" height=\"132\" />Заняття для дошкільнят проводитиме п. Світлана Заяць у хатинці, праворуч алеї, що веде від метро &laquo;Арсенальна&raquo; до церкви, щонеділі о 11:50.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Для першопричасників, після причасників та їх батьків &ndash; відповідно с. Філомена, п. Оксана Сенчук, п. Ірина Макар, п. Оксана Ясніцька та п. Марія Щербатих у фундації ім. О.Ольжича, по вул. І.Мазепи, 6, щонеділі о 10:50.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: right;\" src=\"image,3671,200,2.jpg\" alt=\"\" width=\"132\" height=\"200\" />Молодь гуртує Ірина Василик, щонеділі після дитячої Літургії у хатинці.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Зголошуватися на катехизацію можна до с. Філомени та Світлани Заяць, або відписати на ел. пошту: </span></span><a href=\"mailto:dity.askolda@gmail.com\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">dity.askolda@gmail.com</span></span></a><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"> &ndash; на ую адресу також можна присилати свої зауваження та побажання щодо катехитичної праці.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Світлана Заяць, катехит, для &laquo;Оранти&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Фото С.Заяць за посиланням: </span></span><a href=\"http://picasaweb.google.com/dity.askolda\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">http://picasaweb.google.com/dity.askolda</span></span></a></p>',NULL,2188,4,1,NULL,17);
