INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1100,'P','2008-12-04',1,NULL,'Note','Львівська міськрада визнала за УГКЦ статус репресованої церкви','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Дякуючи міській ініціативі, це перший крок, але він дуже важливий. Я сподіваюся, що цю ініціативу Львова підхоплять й інші міста, а згодом з\'явиться відповідний закон&raquo;, - каже керівник постуляційного центру УГКЦ о. Роман Тереховський.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">4 грудня 2008 року під час засідання сесії Львівської міської ради було прийнято рішення про визнання статусу &laquo;репресованої Церкви&raquo; за Українською греко-католицькою Церквою. Згідно з ухвалою, Львівська міська рада звернеться до Президента України Віктора Ющенка та Верховної Ради з проханням ініціювати на загальнодержавному рівні \"процес встановлення історичної справедливості щодо УГКЦ та визнання за нею статусу репресованої\".</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Дякуючи міській ініціативі, це перший крок, але він дуже важливий. Я сподіваюся, що цю ініціативу Львова підхоплять й інші міста, а згодом з\'явиться відповідний закон&raquo;, - каже керівник постуляційного центру УГКЦ о. Роман Тереховський. &laquo;Також ухвала передбачає підтримку та допомогу у реабілітації УГКЦ, а також надання їй усіх можливих пільг у межах чинного законодавства&raquo;, - повідомив заступник міського голови з гуманітарних питань Василь Косів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Прес-служба</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Львівської міської ради</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>\r\n<p>&nbsp;</p>',NULL,748,5,1,NULL,42),
(1101,'N','2008-12-21',0,NULL,'Note','Реколекції отця Олександра','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18-21 грудня 2008 р. у церкві св. Миколая Чудотворця на Аскольдовій Могилі відбудуться реколекції під проводом отця Олександра, ігумена отців студитів при Патріаршому Соборі у Києві за таким розкладом.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18-21 грудня 2008 р. у церкві св. Миколая Чудотворця на Аскольдовій Могилі відбудуться реколекції під проводом отця Олександра, ігумена отців студитів при Патріаршому Соборі у Києві за таким розкладом:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18 грудня&nbsp; о 18:00 - велика вечірня з Литією. 1 наука</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">19 грудня о 8:00 утреня, о 9:00 Божественна літургія, освячення води, молитва за оздоровлення. 2 наука</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; о 18:00 Божественна літургія. 3 наука.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">20 грудня&nbsp;&nbsp; о 8:00 утреня, о 9:00 Божественна літургія, панахида. 4 наука</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; о 17:00 (актовий зал педагогічного університету і м. Драгоманова) 5 наука, <strong>ІІ фестиваль-конкурс &laquo;Аскольдів глас&raquo;, святий Миколай приходить до дітей.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">21грудня&nbsp;&nbsp; &nbsp; о 10:00 Божественна літургія, проповідь, закінчення реколекцій.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\"Оранта\"</span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>',NULL,751,5,1,NULL,41);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1102,'P','2011-01-20',1,NULL,'Rss','Розп’яття Євгена Коновальця','<p>У цьому році виповнюється 120 років з дня народження відомого державного, військового і політичного діяча, Голови Проводу ОУН Євгена Коновальця. Як відомо, полковник Є.Коновалець трагічно загинув внаслідок терористичного акту, що його здійснив більшовицький агент Судоплатов, про що пізніше сам і зізнався.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=663:rozpjattja-jevgena-konovalcja&catid=37:spilnota-cat&Itemid=49',2297,NULL,1,1,26),
(1103,'N','2008-12-04',0,NULL,'Note','Апостолська Столиця підписала Конвенцію про заборону виготовлення, зберігання та поширення касетного озброєння','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">3 грудня 2008&nbsp;р., в столиці Норвегії м. Осло, відбулося підписання Конвенції щодо касетного озброєння, яка передбачає заборону виготовлення, зберігання та поширення значної частини існуючих на сьогодні так званих касетних бомб.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">3 грудня 2008&nbsp;р., в столиці Норвегії м. Осло, відбулося підписання Конвенції щодо касетного озброєння, яка передбачає заборону виготовлення, зберігання та поширення значної частини існуючих на сьогодні так званих касетних бомб. Передбачено надання допомоги для ліквідації наслідків застосування цього озброєння тим країнам, на території яких ще досі залишилися нерозірвані снаряди, а за підрахунками їх нараховується коло 33 мільйонів у понад 20 країнах світу, а також знищення боєприпасів і міжнародну співпрацю для допомоги жертвам. Договір підписали понад 100 країн, серед яких - Апостольська Столиця, однак на разі до нього ще не приєдналися ті країни, що мають найбільші запаси цієї зброї.<br /><br />Апостольська Столиця, разом з Австрією, Ірландією, Мексикою, Норвегією та Перу, була однією з перших, хто запропонував мораторій на застосування касетних бомб. В Осло Ватикан представляв архиєпископ Домінік Мамберті, Секретар Апостольської Столиці у справах стосунків з іншими Державами, який після підписання конвенції вручив акт про ратифікацію, підписаний Папою Венедиктом&nbsp;XVI, до якого додано окрему декларацію. У ній йдеться про те, що ратифікуючи Конвенцію щодо касетного озброєння, Апостольська Столиця заохочує усю міжнародну спільноту сприяти процесові роззброєння, слідкувати та виробленням зброї та дбати про те, щоб зміцнювалось міжнародне гуманітарне право, наново підтверджуючи гідність людської особи як найвищу цінність.<br /><br />Касетні бомби - це авіабомби і артилерійські снаряди, які складаються з виштовхуваного заряду і сотень вибухонебезпечних малих бойових елементів. Ці елементи розкидаються на великій території і не всі вибухають при ударі. Вони залишаються в бойовому стані впродовж тривалого часу, створюючи загрозу для мирного населення.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,749,5,1,NULL,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1104,'P','2008-12-01',1,NULL,'Note','Нехай земля буде пухом Петрові Хмаруку','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1 грудня на Аскольдовій Могилі тіло ново приставленого раба Петра Хмарука - члена проводу УНА-УНСО відспівали і провели в останню путь о. Ігор, о. Анатолій, о. Іларіон з української Автокефальної церкви, родина, близькі, побратими, соратники. Поховали Петра Хмарука на Лісному кладовищі м. Києва. Хай у Бозі спочине його душа.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,1732,200,IMG_0499.jpg\" alt=\"\" />1 грудня на Аскольдовій Могилі тіло ново приставленого раба Петра Хмарука - члена проводу УНА-УНСО відспівали і провели в останню путь о. Ігор, о. Анатолій, о. Іларіон з української Автокефальної церкви, родина, близькі, побратими, соратники. Поховали Петра Хмарука на Лісному кладовищі м. Києва. Хай у Бозі спочине його душа.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Біографія П.А.Хмарука (взята з передвиборчої листівки 1995 року): Народився у 1944 році. Організатор післявоєнного підпільного руху в Україні. З 16 років професійний революціонер, згодом - невловимий керівник антирадянського підпілля на Київщині (кінець 60-х рр.), відомий публіцист. За його затримання КДБ тричі призначав винагороду. В результаті переслідування режимом провів в ув\'язненні 29, 5 років (листівка - 1995 року, потім Петра Андрійовича було ще раз ув\'язнено - О.Б.), проте політичну діяльність не припинив. Ініціатор численних страйків ув\'язнених у радянських концтаборах. Член проводу УНА. Християнин.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Матеріала та фото: Олени Білозерської<br /></span></span></p>\r\n<p>&nbsp;</p>',NULL,752,5,1,NULL,17),
(1105,'P','2008-12-07',0,NULL,'Note','«Новомедія» закликає усіх християн доброї волі молитися за Українські ЗМІ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За інформацією Релігійно-Інформаційної Служби України, Асоціація &laquo;Новомедія&raquo; закликає всі християнські Церкви України об\'єднатися у неділю, 7 грудня, у спільній молитві за ЗМІ в Україні. Асоціація закликає молитися у цей день підчас богослужінь у храмах та поститись в намірах працівників українських засобів масової інформації, щоб вони сміливо &laquo;прославляли Ісуса Христа&raquo;; &laquo;причинялися до руйнування влади сатани та гріха&raquo;; &laquo;утверджували біблійні принципи та християнської етики у редакційній політиці мас-медіа&raquo;.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За інформацією Релігійно-Інформаційної Служби України, Асоціація &laquo;Новомедія&raquo; закликає всі християнські Церкви України об\'єднатися у неділю, 7 грудня, у спільній молитві за ЗМІ в Україні. Асоціація закликає молитися у цей день підчас богослужінь у храмах та поститись в намірах працівників українських засобів масової інформації, щоб вони сміливо &laquo;прославляли Ісуса Христа&raquo;; &laquo;причинялися до руйнування влади сатани та гріха&raquo;; &laquo;утверджували біблійні принципи та християнської етики у редакційній політиці мас-медіа&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Мас-медіа є основним інструментом впливу на громадську думку, а також головним засобом поширення інформації, утвердження ідей, поглядів та етики, - сказано у зверненні Асоціації до своїх членів і всіх християн. - У зв\'язку з цим ми запрошуємо Церкви, священнослужителів і всіх віруючих зайняти активну позицію з цього питання вже зараз. Християнство та засоби масової інформації в Україні мають стати єдиними у своїх цілях, поглядах і баченні&raquo;, - йдеться у Зверненні, яке підписав Президент &laquo;Новомедія&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"http://novomedia.org/modules/news/article.php?storyid=894\" target=\"_blank\">novomedia.org/modules/news/article.php</a></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>\r\n<p>&nbsp;</p>',NULL,753,5,1,NULL,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1106,'P','2008-12-05',1,NULL,'Note','Співчуття Президента Папської Ради сприяння єдності християн з приводу смерті Глави РПЦ','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Кардинал Вальтер Каспер, Президент Папської Ради в справах сприяння єдності християн, надіслав телеграму співчуття ієрархії та вірним Російської Православної Церкви з приводу смерті її Предстоятеля - Патріарха Алексія&nbsp;ІІ. Глава РПЦ, після тривалої недуги, відійшов до вічності 5 грудня 2008 р., вранці, у своїй резиденції в селищі Пєрєдєлкіно біля Москви.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Кардинал Вальтер Каспер, Президент Папської Ради в справах сприяння єдності християн, надіслав телеграму співчуття ієрархії та вірним Російської Православної Церкви з приводу смерті її Предстоятеля - Патріарха Алексія&nbsp;ІІ. Глава РПЦ, після тривалої недуги, відійшов до вічності 5 грудня 2008 р., вранці, у своїй резиденції в селищі Пєрєдєлкіно біля Москви.<br /><br />Як зазначає у своїй телеграмі кардинал Каспер, Його Святість Алексій&nbsp;ІІ очолював Російську Православну Церкву в періоді великих змін і під його проводом вона з оновленою внутрішньою життєздатністю вийшла з радянського періоду у сьогодення.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,754,5,1,NULL,43),
(1107,'P','2011-01-21',1,NULL,'Note','Свято Богоявлення Господнього','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: left;\" src=\"image,3783,200,IMG_5192.JPG\" alt=\"\" width=\"133\" height=\"200\" />19 січня припадає свято Богоявлення Господнього, яке в народі називають Водохреща, Йордан. Святкування в храмі Миколая, що на Аскольдовій могилі розпочалося, як зазвичай, з Утрені та урочистої архієрейської Літургії. Проповідь виголошував отець Олексій Генек. У своєму пастирському слові до вірних він торкнувся історії свята, яка сягає часів перших християн. Тоді Різдво та Богоявлення святкували в один день не розділяючи. Але з часом стала очевидною різниця міх цими святами. Адже у Різдві нашого Спасителя ми бачимо немовлям, тоді як на Богоявлення &ndash; дорослим чоловіком. &nbsp;&nbsp;В тайні хрещення, яку прийняв Господь з рук Івана христителя, Бог поділився великою тайною з людством &ndash; про існування Бога у Трьох особах. Зрозуміти цю тайну самотужки людині не підсилу. Тому відкриття Триєдиного Бога через сходження Святого Духа на Христа та голос Бога-Отця є виявом найвищого довіря Творця до людини.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Після Служби Божої процесія з отцями Ігорем, Анатолієм та Олексієм вийшли на велике водосвяття біля церкви. Після освячення води отець Ігор привітав всіх із Богоявленням Господнім та пригадав про велику благодать Божу, яка сходить на християн на місці молитви перших християн Русі-України.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Матеріал Оксани Шеремети, Світлини Ярослава Павлюка</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;<img style=\"float: left;\" src=\"image,3783,200,IMG_5136.JPG\" alt=\"\" width=\"200\" height=\"133\" /><img style=\"float: right;\" src=\"image,3783,200,IMG_5120.JPG\" alt=\"\" width=\"200\" height=\"133\" /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: left;\" src=\"image,3783,200,IMG_5173.JPG\" alt=\"\" width=\"133\" height=\"200\" /><img style=\"float: right;\" src=\"image,3783,200,IMG_5181.JPG\" alt=\"\" width=\"133\" height=\"200\" /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"