INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(2303,'P','2009-10-07',1,NULL,'Rss','Катехитичні вчення на природі','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3296,200,PA040128.JPG\" />У неділю 4 жовтня парафіяльна недільна школа церкви св. Миколая Чудотворця на Аскольдовій Могилі з групою дітей, які готуються до першої сповіді і торжественного причастя, разом з батьками вирушили на відпочинок і катехитичні заняття на природі. У мальовничому куточку Зазіммя на Десенці спільною Службою Божою, яку відслужив священик Ігор Онишкевич, розпочалися відпочинок і катехетичні заняття. Батьки теж мали свою зустріч яку проводила катехит Марія Щербатих. Навчання завершилось спільною трапезою, до приготування якої долучилась кожна родина, традиційною стравою відпочинку були шашлики.</p>\n<p>\n','','http://www.oranta.org/index.php?option=com_content&view=article&id=440:katehytychni-vchennja-na-pryrodi&catid=25:parafija-cat&Itemid=27',1827,NULL,1,1,26),
(2304,'P','2009-10-10',1,NULL,'Note','Церква під опікою депутатів','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Храм УГКЦ у Димитрово отримав допомогу від народних обранців<br /> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Донецьку область відвідали представники молодіжного руху України на чолі з заступником Міністра у справах сім\'ї, молоді та спорту&nbsp; Іваном Крульком. Візит відбувся в рамках святкування 20-ряччя Народного руху України. Також на урочистості прибули місцеві &laquo;рухівці&raquo; старшого покоління, представники партії з Дніпропетровська та Сумщини, а також духовенство.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Храм УГКЦ у Димитрово отримав допомогу від народних обранців<br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Донецьку область відвідали представники молодіжного руху України на чолі з заступником Міністра у справах сім\'ї, молоді та спорту&nbsp; Іваном Крульком. Візит відбувся в рамках святкування 20-ряччя Народного руху України. Також на урочистості прибули місцеві &laquo;рухівці&raquo; старшого покоління, представники партії з Дніпропетровська та Сумщини, а також духовенство.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Учасники заходу взяли участь у панахиді біля пам&rsquo;ятника полеглим у другій світовій війні, де пошанували пам&rsquo;ять В&rsquo;ячеслава Чорновола та всіх, хто віддав життя за волю України. Процесія у супроводі правоохоронців та українського реєстрового козацтва попрямувала до будинку культури, де й відбулася офіційна зустріч та святковий концерт.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Також всі учасники заходу відвідали будову церкви Петра і Павла у місті Димитрово. Священик храму от. Іван Василенко розповів гостям про всі досягнення церкви. Депутати пообіцяли всіляко допомагати церкві та парафіянам, а також одразу виділили близько 1 тис. гривень на пожертви.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Оксана Михайлова, ІА &laquo;Оранта&raquo;</span></span></p>',NULL,1828,5,1,NULL,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(2305,'P','2009-10-12',1,NULL,'Rss','Свято Покрови','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3303,200,006.jpg\" />11 жовтня в церкві Покрови св. Богородиці в музеї Пирогова, святкували 10 ювілей відновлення Богослужінь у дерев\'яній греко-католицькій церкві. Божественну Літургію відправив владика Богдан Дзюрах у спів служінні зі священиками  Київського Деканату. У своїй проповіді Владика Богдан розказав про історію празника покрови, коли Пресвята Богородиця захистила Царгород від знищення, Вона свої покровом, покрила молільників і захистила місто від нападу чужинців.</p>\n','','http://www.oranta.org/index.php?option=com_content&view=article&id=441:svjato-pokrovy&catid=25:parafija-cat&Itemid=27',1829,NULL,1,1,26),
(2306,'P','2009-10-13',1,NULL,'Rss','Пастирське послання до священиків, монахів, монахинь та усіх вірних Київської архиєпархії','<div style=\"text-align: center;\">Христос посеред нас!<br /></div>\n<p><br /> <strong>Вступ</strong><br /> Дорогі у Христі,<br /> слова християнського привіту «Христос посеред нас» дають усім нам зрозуміти, що усі ми є дітьми Того, хто з превеликої своєї любові задля нашого спасення зійшов із небес, жив поміж нас, страждав, помер та воскрес із мертвих. Ці основні істини віри ми сповідуємо щодня у Святій Літургії. Вони також дають нам зрозуміти, що ми є єдині у Христі, що ми є Його Церквою, якою Він набув чесною своєю кров\'ю. У ній усі ми, за словами апостола Павла, є немовби членами одного тіла, у якому кожний має свою єдину і неповторну місію.</p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=442:pastyrske-poslannja-do-svjashchenykiv-monahiv-monahyn-ta-usih-virnyh-kyjivskoji-arhyjeparhiji&catid=27:gk-v-ukrajini-cat&Itemid=57',1830,NULL,1,1,26),
(2307,'P','2009-10-14',1,NULL,'Note','Борис ГУДЗЯК: \"Не чекайте на авторитетів зверху! Робіть, підкочуйте рукави, починайте, відкривайте нові горизонти!\"','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"margin: 8px 8px 8px 0px; float: right;\" src=\"http://www.ukrslovo.org.ua/images/stories/gudzjak.jpg\" alt=\"alt\" width=\"120\" />Перші студенти Українського Католицького Університету вчилися у невеличкій будівлі колишнього дитячого садочка, який зимою часом не опалювався. Сьогодні УКУ у Львові є навчальним закладом європейського рівня, єдиним Католицьким університетом на пострадянському просторі. Ректорові, отцю Борису Гудзяку, який виріс у США в українські родині, вдалося за короткий час - абсолютно без державної підтримки - на пустому місці створити центр культурного, духовного і державотворчого розвитку. Людина, яка більшу частину життя прожила за кордоном, але переїхала до України і тут зуміла зробити все з нуля, так і радить українцям (особливо молодим) - втілювати власні проекти, не чекаючи на допомогу когось зверху. Чому ректор тепер так віддано захищає свій університет від політиків? Чому вважає, що український народ набагато випереджає у своєму розвитку власних лідерів? І які \"пригоди\" він влаштовує в Україні для іноземців? Про це та інше - у розмові з Борисом ГУДЗЯКОМ</span></span></p>',NULL,NULL,1832,5,1,NULL,30);
