INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(2312,'P','2009-10-15',1,NULL,'Note','Свято Покрови у лікарні № 11','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3320,200,1.jpg\" alt=\"\" width=\"200\" height=\"133\" />У свято Покрови отці греко-католицької церкви св. Миколая Чудотворця на Аскольдовій Могилі декан Київський Ігор Ошишкевич та Олексій Геник, капелан каплиці Покрови Пресвятої Богородиці у Київській клінічній лікарні №11 (район ДВЗ) відслужили молебен до Пресвятої Богородиці.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Хворі мали змогу відсвяткувати це величне свято поза власним домом. Київський декан передав головному лікарю п. Богдану пожерту 1000 грн. на придбання необхідних реактивів для лікарні.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Свято Покрови у лікарні для хворих і медперсоналу завершилось церковним співом, побажанням многолітня і миропомазанням.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Хай Бог дарує ласку і здоров&lsquo;я для всіх.</span></span></p>','<p><img style=\"float: left;\" src=\"image,3320,200,4.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,3320,200,2.jpg\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,3320,200,3.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,3320,200,5.jpg\" alt=\"\" width=\"200\" height=\"133\" /></p>',NULL,1837,5,1,NULL,49),
(2313,'P','2009-10-19',1,NULL,'Photos','Троянди Аскольда ','<p style=\"text-align: justify;\" dir=\"ltr\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,3321,200,13.jpg\" alt=\"\" width=\"165\" height=\"200\" />У вересні наша парафіянка п.Галина, що утримує власну оранжерею квіт, висадила на Аскольдовій Могилі 150 кущів троянд. Дві центральні клумби сьогодні зазеленіли саженцями троянд, усі 150 кущів прийнялись. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Клумби троянд оновлені пожертвою наших парафіян. Прошу всіх хто любить квіти, займаєтесь квітарством, допомагайте в засаджені квітами ще кількох клумб. Квіти створюють особливу красу і затишок на Аскольдовій Могилі. </span></span></p>\r\n<p style=\"text-align: justify;\" dir=\"ltr\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3321,200,1.jpg\" alt=\"\" />У вересні наша парафіянка п.Галина, що утримує власну оранжерею квіт, висадила на Аскольдовій Могилі 150 кущів троянд. Дві центральні клумби сьогодні зазеленіли саженцями троянд, усі 150 кущів прийнялись.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Клумби троянд оновлені пожертвою наших парафіян. Прошу всіх хто любить квіти, займаєтесь квітарством, допомагайте в засаджені квітами ще кількох клумб. Квіти створюють особливу красу і затишок на Аскольдовій Могилі. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Матеріал підготував Ілля, фото Віталіна \"Оранта\"</span></span></p>',NULL,1838,5,1,NULL,11);
