INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(2492,'P','2010-03-30',1,NULL,'Note','РОЗКЛАД на Страсний тиждень','<p align=\"center\"><strong>Страсний тиждень 2010 рік</strong></p>\r\n<p>&nbsp;</p>\r\n<p><strong>Великий понеділок</strong> &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;8:30 Літургія Передосвячених Дарів, Парастас</p>\r\n<p><strong>Великий вівторок</strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;18:00 Літургія Передосвячених Дарів, Парастас</p>\r\n<p><strong>Велика середа</strong></p>','<p align=\"center\"><strong>Страсний тиждень 2010 рік</strong></p>\r\n<p>&nbsp;</p>\r\n<p><strong>Великий понеділок</strong> &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;8:30 Літургія Передосвячених Дарів, Парастас</p>\r\n<p><strong>Великий вівторок</strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;18:00 Літургія Передосвячених Дарів, Парастас</p>\r\n<p><strong>Велика середа</strong></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;<strong>Великий понеділок о 17:00 розпочнеться Чин Соборування, </strong></p>\r\n<p><strong>В&nbsp; церкві буде постійна нагода до сповіді.</strong></p>\r\n<p>&nbsp;</p>\r\n<p><strong>&nbsp;</strong><strong>1 квітня. Великий четвер</strong>, Страсті спомин тайної вечері</p>\r\n<p>08:30 Вечірня з Літургією Василія Великого</p>\r\n<p>18:00 Страсна утреня</p>\r\n<p>&nbsp;</p>\r\n<p><strong>2 квітня. Велика п&rsquo;ятниця</strong>,</p>\r\n<p>08:15 Великі царські часи</p>\r\n<p>17:00 Вечірня з виставленням Плащаниці</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Єрусалимська утреня</p>\r\n<p>&nbsp;<strong><span style=\"text-decoration: underline;\">Церква буде відкрита цілу ніч, запрошуємо на молитву біля гробу Господнього</span></strong></p>\r\n<p>&nbsp;</p>\r\n<p><strong>3 квітня. Велика субота</strong>,</p>\r\n<p>08:15 Вечірня з Літургією Василя Великого</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n<p><strong>Освячена пасок &ndash; 18:00, 19:00, 20:00, 21:00, 22:00</strong></p>\r\n<p>23:30 Надгробне</p>\r\n<p><strong>4 квітня Пасха Воскресіння Христове</strong></p>\r\n<p>00:00 Пасхальна Утрення зі Службою Божою</p>\r\n<p>07:30 Божественна Літургія, освячення пасок</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Світлий тиждень</strong></p>\r\n<p>&nbsp;</p>\r\n<p>На кожний день</p>\r\n<p>&nbsp;</p>\r\n<p><strong>08:15 Воскресна Утрення,&nbsp; 09:00 Служба Божа</strong></p>\r\n<p><strong>18:00 Вечірня, Служба Божа</strong></p>',NULL,2020,4,1,NULL,38),
(2493,'P','2010-04-01',1,NULL,'Rss','Молодіжна Хресна Дорога','<p><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3503,200,1047.jpg\" />У п’ятницю&nbsp; 26 березня&nbsp; відбулась молодіжна хресна дорога в церкві Миколая Чудотворця підготована молодіжною спільнотою церкви.&nbsp; Цього року молодь церкви Миколая Чудотворця вдруге готувала Хресну Дорогу. Це вже стає хорошою традицією кожного року молоддю проводити хресну дорогу у часі Великого Посту.&nbsp; Підготовка таких заходів, як хресна дорога дає можливість глибше порозважати над стаціями, а потім поділитись своїми переживаннями.</p>\n','','http://www.oranta.org/index.php?option=com_content&view=article&id=516:molodizhna-hresna-doroga&catid=25:parafija-cat&Itemid=27',2021,NULL,1,1,26),
(2494,'P','2010-04-01',1,NULL,'Rss','Хресна Дорога ВІС','<p><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3503,200,535.jpg\" />В часі великого посту ми маємо можливість проходячи 14 стацій Хресної Дороги ще раз призадуматись, якою дорогу ціну&nbsp; заплатив за наше звільнення від гріха Ісус Христос.&nbsp; Кожна із спільнот, що готує Хресну дорогу намагається, як найглибше передати ті переживання які є характерними для неї. В часі цьогорічного великого посту молодь із церкви Миколая Чудотворця разом із молодіжною спільнотою Неопалима купина із церкви Благовіщеня Пресвятої Богородиці, до себе на хресну дорогу запросила спільнота ВІС.</p>\n','','http://www.oranta.org/index.php?option=com_content&view=article&id=515:hresna-doroga-vis&catid=25:parafija-cat&Itemid=27',2022,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(2495,'P','2010-04-01',1,NULL,'Rss','Молодіжні Великопісні реколекції.','<p><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3503,200,1287.jpg\" />З 27 по 28 березня відбулись щорічні молодіжні великопісні реколекції, організовані головою молодіжної комісії отцем Віталієм Марценюком. Ця дата реколекцій вибрана невипадково адже Вербна Неділя у церковному календарі також є і днем молоді. Реколекції почались у суботу 27 березня із літургії у храмі Василя Великого. В реколекціях прийняла участь молодь із всієї київської архієпархії, тому крім духовної віднови для кожного ці реколекції дали можливість &nbsp;познайомитись та обмінятись досвідом між спільнотами із різних парафій київської архієпархії.</p>\n','','http://www.oranta.org/index.php?option=com_content&view=article&id=517:molodizhni-velykopisni-rekolekciji&catid=25:parafija-cat&Itemid=27',2023,NULL,1,1,26),
(2496,'N','2010-05-11',1,NULL,'Rss','«Найкраща наука – це добрий приклад» - Руслана Дмитровська','<p style=\"text-align: left;\"><img style=\"margin: 10px; float: right;\" src=\"image,3535,200,10.JPG\" alt=\"\" /><strong>&laquo;Витай між нами, Христе, витай!<br />Щастя єдине в цей час гостини<br />В невиннім серці, серці дитини:<br />Ти наше сонце, життя і рай,<br />Витай між нами, Христе, витай!&raquo;</strong></p>','','http://oranta.org/index.php?option=com_content&view=article&id=526:lnakrashcha-nauka--ce-dobry-prykladr-ruslana-dmytrovska&catid=25:parafija-cat&Itemid=27',2053,NULL,1,1,26),
(2497,'P','2010-04-04',1,NULL,'Note','ВОСКРЕСІННЯ ГОСПОДНЕ! ','<p><img style=\"float: left;\" src=\"image,3507,200,_MG_9973.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3507,200,_MG_9994.JPG\" alt=\"\" /></p>','<p><img style=\"float: left;\" src=\"image,3507,200,_MG_9894.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3507,200,_MG_9915.JPG\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,3507,200,_MG_9973.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3507,200,_MG_9994.JPG\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,3507,200,_MG_9950.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3507,200,_MG_9953.JPG\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,3507,200,_MG_9878.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3507,200,_MG_9903.JPG\" alt=\"\" width=\"133\" height=\"200\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,3507,200,_MG_9830.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3507,200,_MG_9884.JPG\" alt=\"\" /></p>',NULL,2024,4,1,NULL,17),
(2498,'P','2010-04-06',1,NULL,'Note','Паска красна ув\'язненим жінкам від Аскольдової Могили','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3508,200,_MG_0061.JPG\" alt=\"\" /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Христос Воскрес!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорогі парафіяни, сьогодні ми всі разом зробили добру справу, відвідали жінок і дівчат у Лук\'янівському слідчому ізоляторі. Волонтери Карітас - Печерськ п. Надія, Віталіна принесли вістку про Воскресіння Христове. Завдяки пожертвами Оксани і Євгена, кожна жінка отримала смачну пасочку. Пожертвами Ярослава і Інни, було надано допомогу: шампунями, милом, пряниками, халвою, цукром, жіночою гігієною, фірма \"Фармак\" надала ліки. Для жінок проповідували священик Ігор і диякон Константин. Дорогі парафіяни просино Вас прочитати листи і звернутись до п. Марти, тим хто хоче допомогти.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3508,200,_MG_0061.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3508,200,_MG_0071.JPG\" alt=\"\" /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Христос Воскрес!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорогі парафіяни, сьогодні ми всі разом зробили добру справу, відвідали жінок і дівчат у Лук\'янівському слідчому ізоляторі. Волонтери Карітас - Печерськ п. Надія, Віталіна принесли вістку про Воскресіння Христове. Завдяки пожертвами Оксани і Євгена, кожна жінка отримала смачну пасочку. Пожертвами Ярослава і Інни, було надано допомогу: шампунями, милом, пряниками, халвою, цукром, жіночою гігієною, фірма \"Фармак\" надала ліки. Для жінок проповідували священик Ігор і диякон Константин. Дорогі парафіяни просино Вас прочитати листи і звернутись до п. Марти, тим хто хоче допомогти.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">ЛИСТ I</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Миру та Божої благодаті вельмишановний &nbsp;отець Ігор та всім тим хто Вас оточує.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дуже вдячна на відповідь на мого листа.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вибачте що турбую Вас своєю писаниною, але пишу те що в мене на душі. Хочу потішитись тим що мені снилось і те що я мрію зробити. Ось перший сон.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Начебто я їду в спальному приміщені тут в нас, і відчуваю що з мене тягніть душу. А саме. Хтось, кого я не бачу, телепатично каже: &laquo;Віддай душу, йди за мною, буде все&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я хочу крикнути від страху, але не можу. Я бачу жінок поряд зі мною, і їм говорю: &laquo;Що мені робити&raquo;. На що чую відповідь: &laquo;Біжи туди де багато людей&raquo;. Я повертаюсь і бачу того хто з мене тягнув душу. Перед моїми очима стояв високий чоловік, весь в чорному, сам дуже красивий. На відміну його краси я&nbsp; &nbsp;&nbsp;&nbsp;бачила єхидство в очах, посмішка і всі його негативи. Я почала бігти і шукати там де багато людей. Вибігла в довгий коридор, по якому з обох сторін були двері. Я відкриваю двері і бачу то вогонь, то воду, то яму,&nbsp; то стіну, то ще щось. Зосталась остання кімната и я відчиняю кімнату і дійсно бачу велику масу народу. Залетівши туди, я подивилась у вікно і знову побачила той образ. Я давай бігти в кінець кімнати і бачу стоїть Диякон і Батюшка. Я падаю навколішки і кричу криком спасіть. Хватаю його за рясу, він поклав на мене руку і я проснулась.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ось другий сон.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я стою біля собору, за воротами. Тут бачу їде велика свита православних (починаючи з менших чинів і закінчуючи великими). У мене дух перехватило від того сяйва їхнього вбрання. Відкриваються ворота і хтось говорить, що йде Митрополит всія України Блаженний Володимир. Побачив його /до цього я його не бачила, тільки що по телевізору/, я зустрілася з ним поглядом, низько вклонилась на що він зробив невеликий поклін і пішов далі. Я проснулась від якоїсь легкості.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За свої 40 років мені такого не снилось. Того страху який був в першому сні я й досі не відчуваю. Ось такі різні сни.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я часто, дуже часто, стала замислюватися, що мене чекатиме, коли прийде час покинути цей світ. Інколи замислююсь над тим щоб піти в послушниці. Можливо таким чином я зможу замолити&nbsp; свої гріхи і таким чином вимолити у Господа&nbsp; Бога &ndash; нашого спасителя, спасіння своєї душі та душі моїх рідних та близьких мені людей.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;Ви знаєте &nbsp;я така людина, що живу за такою притчею. Були два сусіда. Один багатий, другій бідний. Кожен день встаючи рано бідний прибирав сміття зі свого огорода.&nbsp; Але не своє сміття, а те що кидав багатій. Тривало це довго, доки в бідного не опинилась кількість грошей на які він накупляв всім , всіх солодощів, зложив в кошик та й відніс під двері багатія. Багатій проснувшись з ранку та й побачив це, та й питає у бідного: &laquo; Не бачив ти сусіде хто це приніс?&raquo; на що, бідний відповідає: &laquo;Чим багатий ви сусіде то тим і ділитесь, а чим багатий я то також ділюсь.&raquo;. За таким принципом я жила до цього, як потрапила сюди і живу тут. Але це тут чомусь думають, що такі люди, як я трішки ненормальні. Ось я трохи і поділилась з вами, що я за людина.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Отець Ігор, ви запитуєте за сина, яким він віком. Йому 17 років. Носить 41 розмір взуття і 48 розмір одежі, зріст 1м.70см. Ось дзвонила до дому і взнала таку новину. По якій причині він не хотів довчитись в К.ВЛ. і сказав що щоб ми не робили і якби ми і не просили він вчитись там не буде. Зробивши другій дзвінок я взнала що його перевели до школи. Я як мати повинна була написати чи подзвонили і дали прочухани, але як слід поміркувала і вирішила не втручатися. Син дорослий і повинен сам відповідати за свої вчинки. Знаю з його досвіду і тому не хочу нашкодити. Один раз він мені казав: &laquo;Мамо, та де я навчаюсь, діти дуже багатих, як тільки вони щось зроблять то за них є кому заступитись. А я мамо сам, у мене є тільки ти та старенька бабуся. Мені треба самому всього достигати. Там все вирішують гроші. За тестування я сам по своїм здібностям витягую, а вони мамо за гроші&raquo;. Ось можливо його це сильно заділо. Ми з моїм сином маємо дуже дружні стосунки. Немає жодних таємниць. Я йому довіряю і надіюсь на те що він зробить правильний вибір.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Коли він вчився в Боярці то там ходив до церковного хору. Йому подобалось. Одного разу приїжджав Митрополит Київський і всія України Блаженний Володимир і подарував книжечку церковний світ дитини. Я кожний день лягаючи спати її беру в руки і молюсь,не тільки по ній, а так само від себе.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Отець Ігор,дуже вдячна за Ваші молитви, та за Вашу турботу. В мене маленьке прохання. Якщо зможете надішліть мені Біблію крупним шрифтом, трохи погано бачу. Якщо зможете, то буду вдячна, колиб ви надіслали до 28 лютого мені щось, щоб я могла роздати стареньким людям. В мене в цей день 41 рік. Вибачте, але пишу без всякої брехні, а так&nbsp; як воно є.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дякую, за все щоб ви не зробили.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Да, нехай Вас&nbsp; охороняє Господь Бог &ndash; наш спаситель.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Недостойна раба Божа Лариса.&nbsp;&nbsp;&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">ЛИСТ II</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Мир вашему&nbsp; Дому!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Получила от вас письмо и с благодарностью отвечаю.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Хочу немного написать о себе, только не подумайте, что я жалуюсь или хочу обвинить кого либо &ndash; нет! Просто пишу, так как оно было, то есть немного облегчу душу! А в своей жизни виновата только сама. Вот я часто думаю &ndash; тяготы заключения, сам этот факт, этот кошмар &ndash; Господь наказал за мои тяжкие грехи или оградил меня от какой-то еще большей беды, горя!?!?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ну а с другой стороны, что страшнее и больнее чем остаться одна, без близких и родных моих людей, пережить своих детей?!? Ну ладно, начнем вкратце и по порядку.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Выросла и воспиталась я в добропорядочной семье. Родители удочерили меня совсем малюткой. Но я этого совершенно не ощущала, пока не погиб отец (на рыбалке утонул) и мне &laquo;добрые&raquo; люди не рассказали, так сказать &laquo;пожалели&raquo;! А мне тогда было 12 лет и сказать, что это было для меня очень жестокий и больной удар , значит сказать очень мягко! Даже после стольких лет, для меня очень свежи и тяжелы все душевные переживания и боли. Это я сейчас осознаю и понимаю, что этот психологический удар для 12-го ребенка был шокированный. И конечно последствия были не осознанны и спонтанны.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Не буду описывать все, но для матери я принесла немало слез и нервных срывов. Замуж вышла очень рано, в 18 лет родила дочь&nbsp; <span style=\"text-decoration: underline;\">Оленьку</span>, в 21 уже разошлась. В 30 лет опять вышла замуж в 32 родила сына <span style=\"text-decoration: underline;\">Рому</span>.&nbsp; Мама умерла (инсульт, полтора месяца лежала парализована), а <span style=\"text-decoration: underline;\">через пол года погибают (ДТП) дети Оле неполных 16 лет, а Ромочке</span> тогда почти 2 годика. Вот тогда все закружилось вокруг меня, а я как в стопоре была довольно долгое время. А рядом оказался не мужчина, не друг, а полное ничтожество. И вот я осталась как в одной сказке, старуха у разбитого корыта, та и того нет!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Никого и ничего нет, здоровья и того нет!!! Но я повторяю, что во всем том, что произошло после всех похорон, виновата только я сама. Просто на прочность, я оказалась ни что, просто тряпка. Сломалась как гнилая ветка. И ко всему прочему еще совершила тяжкий грех, забрала жизнь у другого человека, хотя это было все так спонтанно&nbsp; не осмысленно, но все же факт остается фактом. А самое интересное, я постоянно себя спрашиваю и в молитве прошу ответа у Господа, почему? Ну почему так, в октябре 2003 года у меня получился микро&ndash;инфаркт, а в январе 2004 года я лишаю жизни человека. Почему, ну почему? Почему у меня, микро&ndash;инфаркт, а не обширный, почему Господь не забрал меня?? А, я, забрала чужую жизнь???? Почему в том ДТП погибли мои дети, моя кровь, моя плоть, а я хоть с трудом, но жила, кстати после аварии у меня и остались последствия <span style=\"text-decoration: underline;\">проблемы с позвоночником / меж дисковая грыжа</span> и шипообразный пояс позвоночника/ с сердцем по народному у меня &laquo;Грудная жабра&raquo;, выкручивает суставы. Почему, почему рядом со мной погибают, а я&nbsp; жива???</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вот так вкратце я описала и немного выговорилась то, что на душе, может хоть немного будет легче! Ведь здесь у каждой свое горе, свои беды! И не каждому выскажешь свою боль. Спасибо Вам за Ваше внимание и понимание!!! Я Вам очень признательна, за то, что несмотря на свою занятость Вы уделяете мне внимания находя свободное время!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На этом буду заканчивать.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Что-то тяжело, совсем расклеилась. А 2 февраля мне полных <span style=\"text-decoration: underline;\">51 год !</span>!!!!&nbsp; &nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ангела Вам Хранителя с низким поклоном и с уважением р. Б Анна.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">30.01.10</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">P.S.: Отец Игорь, я хотела у вас спросить, сможете ли вы мне помочь с лекарствам? Если да, тогда я вышлю рецепты.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">р. Б. Анна.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">ЛИСТ III</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Слава Ісусу Христу!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Отець Ігор Онишкевич.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пише до Вас Ганна! Дуже Вам вдячна за відповідь на мій лист. А я і не брала до уваги на щот вашого невчасного листування. Я уповаю на Бога, бо я його люблю всім серцем і Господь бачить мої страждання, горе но він знае. Що я ще не та, якою маю бути, но я неперастаю молитися і просить у Господа прощення. Я знаю що мене спасе тільки Господь, він зімною.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Отець Ігор я Вас дуже прошу допоможіть мені десь в кінці лютого або на початку березня мене будуть відправляти на больничну у м. Львів, на операцію у мене рак груді, а їхати ні з чим, немає нічого мильного, гігієнічного &ndash; но що потрібно жінці. На перед вам&nbsp; дякую, прошу помоліться за мене, та дітей моїх.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дуже переживаю щоб усе було благополучно. Господь зі мною я знаю.&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я усе із Богом дньом і ночью.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Бувайте здорові</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Отець Ігор помоліться за нас.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">31.01.10 рік</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ганна.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Будь чесною у своїй поведінці.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорога гордості - це дорога впалих ангелів, а не дорога чесних людей. Чесна людина вміє достойно вести себе перед сильнішими і багатими цього світу, без гордості, але і без плазування та рабського понижування себе.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Чесна людина віддає Богові те, що Богові належить: послухай його законам і достойну участь у богослужіннях, а ближньому віддай те, що ближньому належить; не кривдить його, не обманює, небуває підлим, донощиком, а навпаки, задля віри в Бога старатися де тільки можна, допомагати й послужити ближньому. Чесна людина бажає служити Богові, хоч би і з пожертвуванням власного життя.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Чесна людина вміє у важких умовах задовольнитися і найменшим. Без нарікань і прокльонів переносить не одне лихо, а &laquo;хто терпен, той спанен&raquo;, як каже приповідка. Чесна людина завжди в своїх словах дотримується правди.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Будь завжди вірною Христові.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"text-decoration: underline;\">&laquo;Вірність Христові&raquo;!</span> В цих словах &ndash; програма цілого життя.&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"text-decoration: underline;\">Вірність Христові</span> - це всі християнські обов&rsquo;язки. Вірність Христові - це християнський світогляд, це дочасне щастя і вічне спасіння!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"text-decoration: underline;\">Бути вірним Христові</span> &ndash; це любити Бога і ближніх за для Бога, бо вони Божі діти.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"text-decoration: underline;\">Бути вірним Христові</span> &ndash; це давати Богові перше місце усьому.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"text-decoration: underline;\">Бути вірним Христові </span>&ndash; це високо цінити християнську молитву та до неї щодня в кожній важливій потребі життя вдаватися.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"text-decoration: underline;\">Бути вірним Христові </span>&ndash; це пам&rsquo;ятати про потреби ближніх і по змозі давати милостиню.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"text-decoration: underline;\">Бути вірним Христові </span>&ndash; це бути чистим, берегтись гріха, та за гріхи покутувати.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"text-decoration: underline;\">Бути вірним Христові </span>&ndash; це приймати причастя Христового Тіла і пити чашу його Крові.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"text-decoration: underline;\">Бути вірним Христові </span>&ndash; це любити Матір &ndash; Церкву, її знати і слухати, її коритися у справах віри й моралі, до неї голосно признаватися.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я прагну бути вірною Христові.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3508,200,IMG_0065.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3508,200,IMG_0062.JPG\" alt=\"\" /></span></span></p>',NULL,2025,4,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(2499,'N','2010-04-03',1,NULL,'Rss','Воскресіння Господа Бога і Спаса нашого Ісуса Христа','<img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/uploads/pics/sunday_of_the_resurrection.jpg\" width=\"203\" height=\"132\" /><img src=\"http://www.ugcc.org.ua/clear.gif\" width=\"1\" border=\"0\" height=\"6\" /><br /> \n<ul>\n</ul>\n<p>Коротка історія</p>\n<p style=\"text-align: justify;\">Слово \"пасха\" походить з єврейського \"песах\", що значить \"перехід\". Тут мова про перехід Господнього ангела, який одної ночі вбив усіх єгипетських первенців, коли фараон не хотів відпус­тити ізраїльський народ, а перейшов повз доми ізраїльтян, одвірки домів яких були помазані кров\'ю одно­річного ягняти. Слово \"пасха\" у жидів означало також ягня, якого заколювали на Пасху, а потім і сам день свята на згадку про визволення з Єгипту дістав назву Пасхи.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=287:voskresinnja-gospoda-boga-i-spasa-nashogo-isusa-hrysta&catid=28:tema-nomera-cat&Itemid=2',2026,NULL,1,1,26),
(2500,'N','2011-04-22',1,NULL,'Rss','Велика П\'ятниця (Свята Плащаниця)','<h3></h3>\n<h3><img style=\"margin: 10px; float: right;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_the_crucifiction.jpg.jpg\" height=\"137\" width=\"99\" /></h3>\n<p>Коротка історія</p>\n<p style=\"text-align: justify;\">Використання святої плащаниці в богослужбах Великої п\'ят­ниці й суботи так як ми її тепер почитаємо, відносно недавнє, бо має за собою заледве кількасот літ, але своїм походженням вона сягає самої смерті Ісуса Христа. Плащаниця це ніщо інше, як те простирало чи саван, у якому було завинуте мертве Тіло Христа Господа, коли Його покладено до гробу. Східна Церква у відправах Великої п\'ятниці й суботи не знала плащаниці майже півтори тисячі літ!</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=279:velyka-pjatnycja-svjata-plashchanycja&catid=28:tema-nomera-cat&Itemid=2',2027,NULL,1,1,26),
(2501,'N','2010-04-01',1,NULL,'Rss','Молодіжні Великопісні реколекції.','<p><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3503,200,1287.jpg\" />З 27 по 28 березня відбулись щорічні молодіжні великопісні реколекції, організовані головою молодіжної комісії отцем Віталієм Марценюком. Ця дата реколекцій вибрана невипадково адже Вербна Неділя у церковному календарі також є і днем молоді. Реколекції почались у суботу 27 березня із літургії у храмі Василя Великого. В реколекціях прийняла участь молодь із всієї київської архієпархії, тому крім духовної віднови для кожного ці реколекції дали можливість &nbsp;познайомитись та обмінятись досвідом між спільнотами із різних парафій київської архієпархії.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=517:molodizhni-velykopisni-rekolekciji&catid=25:parafija-cat&Itemid=27',2028,NULL,1,1,26),
(2502,'N','2010-04-01',1,NULL,'Rss','Хресна Дорога ВІС','<p><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3503,200,535.jpg\" />В часі великого посту ми маємо можливість проходячи 14 стацій Хресної Дороги ще раз призадуматись, якою дорогу ціну&nbsp; заплатив за наше звільнення від гріха Ісус Христос.&nbsp; Кожна із спільнот, що готує Хресну дорогу намагається, як найглибше передати ті переживання які є характерними для неї. В часі цьогорічного великого посту молодь із церкви Миколая Чудотворця разом із молодіжною спільнотою Неопалима купина із церкви Благовіщеня Пресвятої Богородиці, до себе на хресну дорогу запросила спільнота ВІС.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=515:hresna-doroga-vis&catid=25:parafija-cat&Itemid=27',2030,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(2503,'N','2010-03-28',1,NULL,'Rss','Вербна Неділя на Аскольдовій Могилі','<p align=\"right\"><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3500,200,5.JPG\" />«І ми, як діти, несучи знамена перемоги, Тобі</p>\n<p align=\"right\">Переможцеві смерти кличемо: Осанна на висотах,</p>\n<p align=\"right\">Благословен грядущий в ім’я Господнє»</p>\n<p align=\"right\">( Тропар Квітневої Неділі)</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=514:verbna-nedilja-na-askoldovi-mogyli&catid=25:parafija-cat&Itemid=27',2031,NULL,1,1,26),
(2504,'N','2010-03-27',1,NULL,'Rss','ПРОЩА-ПАЛОМНИЦТВО ДО ЗАЗДРОСТІ ТА ЗАРВАНИЦІ','<p><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"image,3496,200,DSCF0272.JPG\" alt=\"\" />Господь Бог, даючи нам день, дає разом з цим кожним днем і дорогу до себе, на якій ми робимо або півкроку, або крок, або ж долаємо цілу вічність на шляху до Нього. І однаково важливими для Отця нашого є малесенький крок і великий шлях, бо це порятунок душі. Таким і маленьким кроком, і великим шляхом стала Хресна дорога &laquo;На славу Господу і за спасіння України&raquo; з Києва до Заздрості й далі до Зарваниці для майже 100 паломників греко-католиків Києва та Кам&rsquo;янця-Подільського з отцем Ігорем Онишкевичем, яка відбулася 19-21 березня 2010 року Божого і була 14 за рахунком.</p>','','http://oranta.org/index.php?option=com_content&view=article&id=513:proshcha-palomnyctvo-do-zazdrosti-ta-zarvanyci&catid=25:parafija-cat&Itemid=27',2032,NULL,1,1,26),
(2505,'P','2010-03-25',1,NULL,'Rss','Святий Альберт Великий – приклад вченого, побожного і мудрого богослова','<p>Святий Альберт Великий, що жив у ХІІІ сторіччі, видатний німецький  богослов, філософ, науковець у сфері природничих наук, монах Ордену  домініканців, вчитель святого Томи Аквінського був в центрі уваги  катехизи Святішого Отця Венедикта ХVІ у середу, 24 березня 2010 року.  Загальна зустріч з десятками тисяч прочан пройшла на площі перед  базилікою святого апостола Петра у Ватикані.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=512:svjaty-albert-velyky--pryklad-vchenogo-pobozhnogo-i-mudrogo-bogoslova&catid=35:istorychni-postati-cat&Itemid=48',2033,NULL,1,1,26),
(2506,'P','2010-03-25',1,NULL,'Rss','Неділя Квітна. Вхід Господній у Єрусалим','<!--TYPO3SEARCH_begin-->\n<p><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3495,200,ad2a24ce7b.jpg\" width=\"86\" height=\"120\" />Коротка історія</p>\n<p align=\"justify\">Празник торжественного в\'їзду  Ісуса Христа до Єрусалима належить до найдавніших празників у Східній  Церкві. Свідчення про його святкування сягають III століття. Святкування  цього празника почалося в Єрусалимі і невдовзі стало празником усієї  Східної Церкви.\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=511:nedilja-kvitna-vhid-gospodni-u-jerusalym&catid=27:gk-v-ukrajini-cat&Itemid=57',2034,NULL,1,1,26),
(2507,'P','2010-03-22',1,NULL,'Rss','Послання Святішого Отця Бенедикта XVІ на XXV Всесвітній День Молоді','<p><strong><em>\"Учителю благий, що мені робити, щоб успадкувати життя  вічне?\"</em></strong> (Мр 10,17)<o:p></o:p></p>\n<p>Дорогі  друзі!<o:p></o:p></p>\n<p>Цього року відзначається двадцять п\'ята річниця встановлення  Всесвітнього Дня Молоді за бажанням Преподобного Івана Павла II, як  щорічна зустріч віруючої молоді всього світу. Це був пророчий почин, що  приніс багаті плоди, дав можливість новим поколінням християн  зустрічатися, слухати Слово Боже, відкривати красу Церкви і переживати  глибокий досвід віри, що привело багатьох до рішення повністю віддати  себе Христу.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=508:poslannja-svjatishogo-otcja-benedykta-xvi-na-xxv-vsesvitni-den-molodi&catid=32:gk-svit-cat&Itemid=58',2035,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(2508,'P','2010-08-01',0,NULL,'Note','Упокоївся єрм. Василій Вороновський, монах студит','<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3597,200,CRW_0122.jpg\" alt=\"\" />31 липня 2010 року Божого о год. 5:40 в м. Львові на 81 році життя, 51 році священства, 45&nbsp;році монашества переставився до Господа єрм. Василій Вороновський, монах студит.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дякуємо Господу за дар його життя і просимо у Нього, щоб Милосердний Бог зволив йому оглядати безнастанно його в Царстві Небесному!</span></span></p>','<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3597,200,CRW_0122.jpg\" alt=\"\" />31 липня 2010 року Божого о год. 5:40 в м. Львові на 81 році життя, 51 році священства, 45&nbsp;році монашества переставився до Господа єрм. Василій Вороновський, монах студит.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дякуємо Господу за дар його життя і просимо у Нього, щоб Милосердний Бог зволив йому оглядати безнастанно його в Царстві Небесному!</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\">&nbsp;</p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Додаток 1:</em></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Життєпис</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>єрм. Василія Вороновського, </strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>монаха студита</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,3597,200,CRW_0088.jpg\" alt=\"\" />6 травня 1929 року &ndash; народився в с. Гумниська, Буського району, в сім&rsquo;ї Івана і Ксенії. Сім&rsquo;я мала шестеро дітей, троє з яких померли в дитячому віці. Таким чином залишились інших троє - Михайло, Василь (майбутній священик та монах-студит) та Юрій (на даний час Єпарх Самбірсько-Дрогобицький УГКЦ).</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1935 рік &ndash; вступив до місцевої школи, де закінчив 6 класів. 7 клас закінчував в Буську.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1938 рік &ndash; Отці Редемптористи провадять місії в с.&nbsp;Гумниська. В часі місій Василь Вороновський вирішує стати священиком-місіонарем.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1942 рік &ndash; вступає до Малої духовної семінарії у Львові відразу на 3 курс.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1945 рік &ndash; після закриття Малої семінарії продовжує навчання у Буській середній школі.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3597,200,CRW_0049.jpg\" alt=\"\" />1948 рік &ndash; поступає до Львівського Державного Університету ім. Івана Франка на факультет німецької філології. Протягом часу навчання підтримує контакти з Греко-Католицьким духовенством, яке вимушено пішло у підпілля після Львівського псевдо-собору 1946 року.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1953-1970 роки - працює вчителем німецької та англійської мов, а також математики і фізики в селах Серники (1 рік), Гологори (5 років), Великосілки, Кизилів (11 років).</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">14 серпня 1959 року &ndash; отримує дияконські свячення з рук Преосвященного Владики Івана Слезюка.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">15 серпня 1959 року &ndash; Преосвященний Владика Іван Слезюк поставляє Василія Вороновського у священичий сан.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">16 серпня 1959 року -  Перша Служба Божа в церкві в с. Куличків, що біля Белзу.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1965 рік &ndash; вступає до підпільного монастиря Студитів, де Архимандритом був підпільний єпископ Ніканор Дейнега.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">27 вересня 1970 року &ndash; на празник Всемірного Воздвиження Чесного і Животворящого Хреста з рук Архимандрита Ніканора Дейнеги приймає Схиму з новим монашим ім&rsquo;ям Володимир.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,3597,200,CRW_0130.jpg\" alt=\"\" />Часи підпілля - єрм. Володимир Василій Вороновський відважно душпастирює як підпільний священик переважно по різних місцевостях Галичини, зокрема у селах Хлопчиці та Добряни.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">12 лютого 1970 року &ndash; перший арешт єрм. Володимира Василія Вороновського за те, що хрестив дітей на хуторі Фійна (біля Крехова).</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1976 рік &ndash; єрм. Володимир Василій Вороновський отримує дозвіл від Владики Василія Величковського на читання екзорцизму. Цей дозвіл пізніше підтвердив Митрополит Володимир Стернюк.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">12 лютого 1985 року &ndash; другий арешт за те, що висповідав людину по вул.&nbsp;Головацького,&nbsp;34 &nbsp;у Львові.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1 грудня 1989 року &ndash; третій арешт за публічну відправу в с. Хлопчиці.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3597,200,CRW_0123.jpg\" alt=\"\" />1992 - 1996 роки&nbsp; - викладач Церковного Уставу та духівник Львівської духовної семінарії Святого Духа.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Часи свободи -&nbsp; єрм. Володимир Василій Вороновський проповідує місії майже по всій території України, а також поза її межами (Польща, Італія). Невід&rsquo;ємною частиною усіх його місій є молитви за оздоровлення.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">6 травня 2004 року - отримав особливе благословення з нагоди 75-ліття від блаженної пам&rsquo;яті Папи Івана Павла ІІ.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">21 липня 2010 року - прийняв Велику Схиму з рук рідного брата Преосвященного Владики Юліана Вороновського з іменем Василій.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">31 липня 2010 року - Переставився до Господа о год. 5:40</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Упокой, Господи, душу усопшого раба Твого єрм. Василія!</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Додаток 2:</em></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>ПРОГРАМА ПОХОРОННИХ ВІДПРАВ<br /></strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">ЛЬВІВ</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Храм св. Архистратига Михаїла Студійського Уставу УГКЦ</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">(вул. В.Винниченка, 22)</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Субота, 31 липня 2010 р.Б.</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">19:00 &ndash; Парастас</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Неділя, 1 серпня 2010 р.Б.</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">7:00 &ndash; Св. Літургія з панахидою</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justi