INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(2589,'P','2010-08-02',0,NULL,'Note','«Країна Ангелят»','<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Хочу розповісти Вам, що вже вийшов наш перший відеодиск під назвою &laquo;Країна Ангелят&raquo;. Вийшов він разом з журналом &laquo;Ангелятко&raquo;, як додаток до журналу за липень 2010 року.</span></span><span style=\"font-size: small;\"></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На разі журнал &laquo;Ангелятко&raquo; випустив лише 700 дисків, які продавалися тільки по львівських газетних кіосках.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Слава Ісусу Христу!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Десь пів року тому, я писав Вам про наш відео проект &laquo;З любов\'ю до дітей&raquo;, і Ви навіть розмістили на Вашому сайті інформацію про нього http://askoldova-mohyla.org/uk/item,3425</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">А тепер хочу розповісти Вам, що вже вийшов наш перший відеодиск під назвою &laquo;Країна Ангелят&raquo;. Вийшов він разом з журналом &laquo;Ангелятко&raquo;, як додаток до журналу за липень 2010 року.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На разі журнал &laquo;Ангелятко&raquo; випустив лише 700 дисків, які продавалися тільки по львівських газетних кіосках.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Тепер у нас є новий власний сайт http://www.sonechko.pp.ua/ на ньому якраз і є деякі з мультиків, які є на диску.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Мета проекту - навчити дітей робити добро, думати по хорошому, бути добрими.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ціль - створення цікавих дитячих мультиків та фільмів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Можете про наш проект розмістити інформацію на сайті http://askoldova-mohyla.org і на сайтах Ваших друзів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З повагою, Олег.</span></span></p>',NULL,2115,5,1,NULL,24),
(2590,'P','2010-08-02',1,NULL,'Rss','Канікули з Богом','<p>«Канікули з Богом» - з таким закликом звернулась наша церква св.. Миколая Чудотворця на Аскольдовій Могилі до своєї громади. Цього року необхідно було знайти кошти для відпочинку дітей і на морі і у Карпатах. Усі добре знають, що наразі проводиться реставрація нашого храму: закінчили розпис крипти, церковного куполу, нещодавно замінили вікна, готуємось до розпису стін, тощо.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=564:kanikuly-z-bogom&catid=25:parafija-cat&Itemid=27',2116,NULL,1,1,26),
(2591,'P','2010-08-03',1,NULL,'Rss','Четвертий день дитячого табору «Канікули з Богом» у Карпатах','<p>Цей день залишиться в пам’яті вірян сумною подією – сьогодні прощалися з дорогим ієромонахом Василем Вороновським. Але віримо, що милий мільйонам сердець священик відійшов у світле місце, по нагороду за своє подвижницьке життя. Участь у поминальній Літургії прийняв також і наш парох о. Ігор Онишкевич.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=565:chetverty-den-dytjachogo-taboru-lkanikuly-z-bogomr-u-karpatah&catid=25:parafija-cat&Itemid=27',2117,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(2592,'P','2010-08-04',1,NULL,'Note','Світла пам‘ять великосхимника отця Василя Вороновського','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,3601,200,CRW_0155.jpg\" alt=\"\" />Сьогодні зі всіх куточків України спішили вірні, духовні діти щоб попрощатися з отцем Василем, провести&nbsp; його у світлу дорогу до Бога, у своїх благаннях і молитвах.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ми знаємо що не покидаєте нас, що й надалі будете за нас заступатися, просити прощення за наші гріхи перед Ісусом.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3601,200,CRW_0123.jpg\" alt=\"\" />Сьогодні зі всіх куточків України спішили вірні, духовні діти щоб попрощатися з отцем Василем, провести&nbsp; його у світлу дорогу до Бога, у своїх благаннях і молитвах.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ми знаємо що не покидаєте нас, що й надалі будете за нас заступатися, просити прощення за наші гріхи перед Ісусом.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Молимось, пам&lsquo;ятаємо про Вас отче і ми, парафіяни церкви св.. Миколая Чудотворця на Аскольдовій Могилі. Світла і вічна Вам пам&lsquo;ять.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,3601,200,CRW_0056.jpg\" alt=\"\" />Він служив своєму народу вірою і правдою, як навчили батьки Іван і Ксенія. Закінчив школу у Бузьку, навчався у Львівському університеті ім. Івана Франка, факультет німецької філології. 53-73 роки вчитель німецької мови в школі. Дитяча посвята служити Христу звершилася 15 серпня 1059 року владика Іван Слезюк руко положив в священичий сан. 1965 року отець Василь вступив у підпільний монастир студитів. Отець любив служити на Аскольдовій Могилі. Він провів тижневу місію у нашій церкві св.. Миколая, а родина Віктора Пинзеника встановила пам&lsquo;ятний месійний хрест на кручах дніпрових. <img style=\"float: left;\" src=\"image,3601,200,CRW_0098.jpg\" alt=\"\" />Це він втомлений іде з вірними встановленою хресною дорогою Аскольда і Руси-України і торжествено освячує її. Отець Вороновський заказав, щоб кожного четверга у день св.. Миколая і апостолів у нашій церкві освячувались вода і єлей та відчитувалась молитва оздоровлення, бо це день нашого тисячолітнього храму і тут будуть звершуватись чудо оздоровлення і тисячі людей прийдуть сюди на Аскольдову Могилу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,3601,200,CRW_0161.jpg\" alt=\"\" />Сьогодні 3 серпня у свято Успенській Унівській Лаврі прощались з отцем Василем святою Божественною літургією 5 владик, владика Юліан, рідний брат, владика Василь одеський, владика Тарасій стрийський, владика Ярослав, помічник дрогобицький, і владика Йосип київський очолив службу. Соборно служать понад 150 священиків і більше тисячі вірних зібрались у молитві.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пам&rsquo;ятаймо дорогі повчання отця Василя Вороновського: - &laquo;живім так, щоб колись по смерті змогли оглядати Бога в небі. Амінь&raquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Фото: Любові Михайлюк<br /></span></span></p>',NULL,2118,5,1,NULL,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(2593,'P','2010-08-04',1,NULL,'Rss','Свято Іллі у Карпатах','<p>Радуйся, Пророче Іллє. славний. Линуло над горами Карпатами у монастирі отців студитів в Яремче. Нічні чування наповнені молитвою і похід зі свічками об\'єднав сотні прочан у зоряній Карпатській ночі. Сам празник розпочався Архієрейською Божественною Літургією, яку відправили правлячий Архієрей Коломийсько-Чернівецької єпархії Микола Симкайло і високо преосвященний владика Ігор Возняк, Архієпископ Львівський, який виголосив проповідь.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=566:svjato-illi-u-karpatah&catid=25:parafija-cat&Itemid=27',2119,NULL,1,1,26),
