INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(2597,'P','2010-08-16',1,NULL,'Rss','Національна ідея митрополита','<p>Верховна Рада України не підтримала проект постанови, якою передбачалося на високому державному рівні відзначити 145-річчя від дня народження одного з найбільших церковних діячів України, глави Української Греко-Католицької Церкви, визначного громадського та політично-державного діяча митрополита Андрея Шептицького. Неординарність такого рішення вітчизняних парламентарів полягає у тому, що цього разу зроблено спробу «заблокувати» шлях до загальнодержавного визнання людині, чий авторитет і суспільна вага мають незрівнянно більший вплив на сьогодення і майбутнє України, аніж парламент нинішнього скликання.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=568:nacionalna-ideja-mytropolyta&catid=27:gk-v-ukrajini-cat&Itemid=57',2122,NULL,1,1,26),
(2598,'P','2010-08-19',1,NULL,'Rss','Яблука для Валерія Марченка','<p>У день великого свята Преображення Господнього зібралась наша громада церкви св.. Миколая Чудотворця на Аскольдовій Могилі на святкування. Святу Літургію відправляли настоятель Ігор, отець Василь Поточняк, отець Константан Пантелей і диякон Олександр Соколовський. Особливістю було те що на галереї церкви праворуч від входу, була вибудувана ціла композиція дарів наших садів і землі української.\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=570:jabluka-dlja-valerija-marchenka&catid=25:parafija-cat&Itemid=27',2123,NULL,1,1,26),
(2599,'P','2010-08-19',0,NULL,'Note','Трагічно загинула головний редактор інтернет-порталу «Релігія в Україні» Алла Дмитрук','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ця новина стала шокуючою для всіх, хто її знав. В розквіті сил пішла з життя глибока християнка, високопрофесійний журналіст та просто чарівна молода жінка.</span></span></p>','<div id=\"news-id-5559\" style=\"text-align: justify;\">\r\n<p><!--TBegin--><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a onclick=\"return hs.expand(this)\" href=\"http://www.religion.in.ua/uploads/posts/2010-08/1281694555_alla.jpg\"><img style=\"float: right;\" title=\"Трагічно загинула головний редактор інтернет-порталу &laquo;Релігія в Україні&raquo; Алла Дмитрук\" src=\"http://www.religion.in.ua/uploads/posts/2010-08/thumbs/1281694555_alla.jpg\" alt=\"Трагічно загинула головний редактор інтернет-порталу &laquo;Релігія в Україні&raquo; Алла Дмитрук\" /></a>Ця новина стала шокуючою для всіх, хто її знав. В розквіті сил пішла з життя глибока християнка, високопрофесійний журналіст та просто чарівна молода жінка.<br />Декілька людей одразу взялося написати некролог від імені редакції порталу &ldquo;Релігія в Україні&rdquo;. Зробити з надісланих текстів один - завдання вкрай складне. Тому ми публікуємо одразу декілька прощальних слів, не вказуючи імен авторів. Це знак того, що інформаційний ресурс &ldquo;Релігія в Україні&rdquo;, який редагувала Алла, став своїм для дуже багатьох людей: експертів, журналістів, тих, кому небайдужа доля Церкви в Україні, так що кожен з них може виступати від імені його редакції і є частиною редакції. Все це стало можливим завдяки Аллі.<br />Ми сподіваємось, що ресурс продовжить існувати - так, як його бачила Алла, виконуючи свою місію примирення та подолання розділень.<br />Зараз просимо всіх про молитви за рабу Божу Аллу. Нехай Господь подасть їй вічний спокій.<br />Найближчим часом буде повідомлено, коли і де в Києві відбудеться відспівування Алла. Після цього її буде поховано в неї на батьківщині - у Кривому Розі.</span></span><!--TEnd--></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">***<br />Алла Дмитрук була людина напрочуд сонячна, дивовижно енергійна і безкінечно порядна. Вона заряджала кожного з нас своєю надзвичайною працездатністю, непідробним інтересом до людей, подій і ріноманітних граней буття. Вона була віддана своїм близьким, друзям, своїй справі і своїй&nbsp; країні, прагнучи зробити все від неї залежне, щоби змінити її на краще.<br />Аллу любили всі, хто співпрацював з нею і хто просто її знав. З нею можна було сперечатися, не погоджуватися, наполягати на своєму, але не любити &ndash; не можна.<br />Очоливши інтернет-портал &laquo;Релігія в Україні&raquo;, за один лише рік Алла зробила його одним з провідних ресурсів релігійного сегменту українського веб-простору. Майданчиком для палких дискусій, джерелом серйозних академічних статей і яскравих розвідок, гарячих новин і сенсаційних інтерв&rsquo;ю він став завдяки її таланту, наполегливості й професійному чуттю.<br />Алла Дмитрук пішла від нас молодою і квітучою, виповненою величезних планів і розуміння, як саме зробити їх реальністю. З нами нами назавжди залишиться зроблене Аллою, пам&rsquo;ять про неї і її світлий образ.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">***<br />Життя &ndash; складне і незбагненне явище. Буває так, що людина стоїть на порозі власного розквіту, але зненацька все змінюється. Іноді &ndash; трагічно.<br />Алла була талановитим журналістом, який наполегливо працював над проектом &laquo;Релігія в Україні&raquo;. Цей проект, що відбувся виключно завдяки її праці, мав добре майбутнє. І ось на порозі цього майбутнього її не стало. Журналістський успіх було досягнуто, вона відбулась як професіонал, і тому особливо боляче відчувати цю втрату.<br />Алла була гарною людиною. Вона вміла весело і легко жити. Вона не могла проїхати пару зупинок на метро, тому що їй хотілося обов&rsquo;язково пройти вулицями, подолати шлях, на якому і віддаєш сили, і набуваєш сил. Таким самим було її відношення до роботи. Вона любила і вміла працювати. Любила віддавати всій час, дарувати своє спілкування. І надихатися на нові, ще не взяті висоти.<br />Алла була хорошою християнкою. Вона оцінювала все і вся виключно з позицій Євангелія. В спілкуванні із нею ти чекав весь час, що вона ось-ось скаже: а що в такому випадку сказав би Христос? Вона не вміла міряти звичайними земними мірками. Її мірило було простим і точним, тому що воно було євангельським, Христовим. Тому вона була принциповою і критичною. Але вона вміла попросити вибачення і вміла прощати. Вона вміла помиритися, запросивши на дружню вечерю, маскуючи власні християнські мотиви. Це була скромність великої душі. <br />Алла стала б хорошою дружиною для когось. Адже її робочий кабінет відразу перетворився на затишну домівку, в якій завжди був найкращий чай і прекрасні цукерки. Алла, при всьому професіоналізмі була жінкою, яка не може не творити затишку і надавати всьому навколо неповторної чарівності. <br />Алла стала б прекрасною матір&rsquo;ю. Діти тягнулися до неї, миттєво здружувалися, розповідали власні таємниці. Вони буквально не відходили від неї ні на крок, і вона мусила йти із ними до причастя, а матушки із Покровського називали її &laquo;мамою&raquo; і питали в неї імена малечі. <br />Легкість із якою вона йшла по життю не була легковажністю. Це була легкість душі, вільної від гріха. Легкість професіонала, який спирався на величезні знання набуті в Могилянці, хороший досвід і вміння працювати за будь-яких умов. Вона могла за вечір написати величезну статтю чи за ніч перевести із англійської розлогу доповідь. Вона вміла задати питання на інтерв&rsquo;ю так, як ніхто до цього не задавав &ndash; і поважні митрополити та єпископи, патріархи і кардинали говорили: &laquo;дівчино, приходь ще, з тобою легко розмовляти, з тобою чудово думається&raquo;. Її легкість була споріднена до легкості сонця. Це була легкість величного явища. <br />Її неможливо було зробити необ&rsquo;єктивною, її неможливо було &laquo;приватизувати&raquo; - так само як неможливо приватизувати сонце чи вплинути на нього. Вона просто не могла бути однією із багатьох в якійсь компанії. Вона була самодостатня. Вона була одна. Як сонце. <br />Є інші зірки, але вони є сонцями в інших місцях.<br />Для &laquo;Релігії в Україні&raquo; таким сонцем була виключно Алла.<br />Ми просимо молитов за душу Алли, за легкість її вічного життя. Вона потребує наших молитв.<br />І ми просимо всіх її пам&rsquo;ятати. Вона достойна нашої пам&rsquo;яті. <br />І ми просимо всіх простити її, якщо когось вона мимоволі обпекла власним теплом. Адже вона із тих людей, яким неможливо не пробачити. <br />Нехай ця легкість, із якою Алла йшла по життю, виявиться передвісником і передчуття легкості раю. Адже вона була готова до вічного ангельського життя. Але ми не були готові її втратити. І тому біль крає наше серце.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br />***<br />&laquo;И услышал я голос с неба, говорящий мне: напиши: отныне блаженны мертвые, умирающие в Господе; ей, говорит Дух, они успокоятся от трудов своих, и дела их идут вслед за ними&raquo;.<br />&nbsp;(Откр. 14:13)</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Воистину неподвластны рассудку планы Господни о каждом из нас, как и неизведанны время и путь, по которым нам суждено прийти к Нему.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сегодня мы получили печальное известие: внезапно ушла из жизни главный редактор ресурса &laquo;Религия в Украине&raquo; Алла Дмитрук, &ndash; первоклассный специалист, но прежде всего, замечательный добрый и открытый человек, с которым всегда было легко и интересно общаться. Для многих Алла стала примером человека непрестанно развивающегося и интересующегося, стремящегося к истине и умеющего заглянуть за рамки собственных предпочтений. Подтверждением этому являются многочисленные интервью, проведённые Аллой за время существования ресурса, с представителями различных конфессий и последователями различных идеологий. Воистину, смерть Аллы огромная утрата не только для нашего ресурса, но и многих, знающих её.<br />Любая смерть выходит за рамки доступного разуму, и всегда сложно разлучаться с дорогим человеком, нашим близким, кем-то кто стал привычным в нашей жизни, стал ей частью, с которым обсуждено множество книг, событий, идей, кулинарных рецептов (что особенно любила Алла), перепробовано множество блюд, и выпита бездна чая&hellip; Разум попросту отказывается верить в то, что этого человека вдруг не стало. И, безусловно, всякое слово звучит неуклюже, каким-то недоразумением перед лицом вечности.<br />Ни смерть, ни жизнь, ни Ангелы, ни Начала, ни Силы, ни настоящее, ни будущее, ни высота, ни глубина, ни другая какая тварь не может отлучить нас от любви Божией во Христе Иисусе, Господе нашем (Рим. 8:38-39). Помня эти слова, мы сейчас молимся об упокоении новопреставленной Аллы. И пусть Всемилостивый жизни податель в это время укрепит родных и близких ушедшей от нас.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">http://www.religion.in.ua/news/vazhlivo/page,1,2,5559-tragichno-zaginula-golovnij-redaktor-internet-portalu-religiya-v-ukrayini-alla-dmitruk.html</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Громада церкви св. Миколая Чудотворця на Аскольдовій Могилі висловлює своє співчуття рідним, друзям. Молитимось щоб Господь Бог прийняв хх світлу душу. Хій спочине у Бозі.</span></span></p>\r\n</div>',NULL,2124,5,1,NULL,30);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(2600,'P','2011-08-16',1,NULL,'Rss','Святе Преображення Господа Бога і Спаса нашого Ісуса Христа ','<p><img src=\"http://ugcc.org.ua/clear.gif\" border=\"0\" height=\"5\" width=\"1\" /><strong>Коротка історія</strong></p>\n<p align=\"justify\">Святкування цього празника сягає IV ст. В цьому часі св. Єлена, мати цісаря Костянтина Великого, збудувала храм на горі Тавор у честь Господнього Преображення. Від VI ст. цей празник урочисто святкується у Східній Церкві під назвою „Господнього Преображення\". В Західній Сирії у VIII столітті він називався „Празником Тавору\".</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=381:svjate-preobrazhennja-gospoda-boga-i-spasa-nashogo-isusa-hrysta-&catid=27:gk-v-ukrajini-cat&Itemid=57',2125,NULL,1,1,26),
(2601,'P','2010-08-21',1,NULL,'Rss','Милосердне милосердя','<p>Звернення Єпископа-помічника Київського Йосипа Міляна до усіх вірних християн греко-католицької церкви Київської архиєпархії у справі благодійності і благодійного фонду.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=571:myloserdne-myloserdja&catid=27:gk-v-ukrajini-cat&Itemid=57',2126,NULL,1,1,26),
(2602,'P','2010-08-25',1,NULL,'Note','«У нiй було щось згори...» ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span lang=\"RU\">У своїх спогадах чоловiк Наталiї Шухевич відверто зiзнається, чому їхня родина не мала дiтей та чому, попри тиск спецслужб, не розлучився з дружиною</span></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span lang=\"EN\">Навколо її смерті у засобах масової інформації Кабардино&ndash;Балкарської республіки вчинився чималий ажіотаж, якого я не очікував. Проста, але великої душі людина, вона як справжня християнка знаходила кожній людині виправдання, навіть відверто негативній особі. Я дивувався її високоморальності та людяності. Коли ми живемо, ходимо серед таких людей, ми їх рідко помічаємо. Мені незручно казати так, але вона певно була святою.</span></span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"http://umoloda.kiev.ua/img/content/i28/28805.gif\" alt=\"Наталiя та Муталіф прожили щасливих 63 роки. (Фото з архіву родини Аймурзаєвих.)\" /><span lang=\"RU\">\r\n<p>У своїх спогадах чоловiк Наталiї Шухевич відверто зiзнається, чому їхня родина не мала дiтей та чому, попри тиск спецслужб, не розлучився з дружиною</p>\r\n<p><strong>Наталія Шухевич, рідна сестра головнокомандувача УПА Романа Шухевича, прожила унікальне життя. Після радянських концтаборів вона вийшла заміж за репресованого балкарця Муталіфа Геграєва, з котрим прожила надзвичайно щасливі 63 роки, попри все шаленство навколишньої гебешної системи. Незважаючи на шлюб із мусульманином, не зреклася своїх поглядів ані релігійних, ані національних чи політичних. На превеликий жаль, покійна Наталія Шухевич за дисидентською звичкою не полишила жодних архівів та спогадів по братові. Сьогоднi виповнюється пiвроку з дня смертi Наталії Йосипівни, яку поховали на Личаківському цвинтарі у Львові. Ми публікуємо унікальні спогади її чоловіка Муталіфа Геграєва, передані &laquo;УМ&raquo; похресниками панi Наталi Еркеном та Ольгою Аймурзаєвими. Саме вони допровадили її останки з Кавказу аж до львівської церкви Святого Андрія.</strong></p>\r\n</span><span lang=\"EN\">\r\n<p>&nbsp;</p>\r\n<h4>Ажіотаж по смерті</h4>\r\n<p>Навколо її смерті у засобах масової інформації Кабардино&ndash;Балкарської республіки вчинився чималий ажіотаж, якого я не очікував. Проста, але великої душі людина, вона як справжня християнка знаходила кожній людині виправдання, навіть відверто негативній особі. Я дивувався її високоморальності та людяності. Коли ми живемо, ходимо серед таких людей, ми їх рідко помічаємо. Мені незручно казати так, але вона певно була святою.</p>\r\n<p>Вона все своє велике життя страждала через те, що була сестрою свого брата, великого політика, борця за визволення своєї Батьківщини, головнокомандувача Української повстанської армії. Вона не була простою людиною. Володіла якоюсь магічною силою. Близько неї людина робилася спокійною, врівноваженою, впевненішою в собі. Вона була своєрідним бар&rsquo;єром від неприємностей і загроз. Із Наталею ми прожили 63 роки &mdash; для мене вона була дружиною і матір&rsquo;ю, братом і сестрою.</p>\r\n<h4>Добровільна висилка</h4>\r\n<p>Я &mdash; колишній депортований офіцер. Після демобілізації з Радянської армії у 1946 роцi в МВС Кабардинської АРСР мені запропонували: депортувати етапом у місця нового місцепроживання балкарців або ж дати згоду на кабардинське підданство і залишитися в Кабардинській АРСР. Я погодився з другим варіантом. Дали мені роботу, житло, але за цей час, за три місяці, я встиг зв&rsquo;язатися з висланими родичами в Казахстані і тут же заявив: їду до них. Здав паспорт, отримав відповідні документи і поїхав сам у місце заслання мого народу.</p>\r\n<p>Наше знайомство з Наталею вiдбулося в 1947 роцi в Казахстані. Тоді нам обом було по 24 роки. До неї в мене було багато знайомих дівчат: і багаті, і гарні, але ні до кого з них душа так близько не лежала.</p>\r\n<h4>Не хотіли плодити дітей &mdash; вічних каторжників</h4>\r\n<p>Люди часто казали: &laquo;Ви ж така гарна пара, чому у вас немає нащадків?&raquo;. Цьому є вагоме пояснення. У комендатурі, куди я приїхав, мені дали на підпис анкету для прибулих військовослужбовців. Вона спиралась на постанову уряду, підписану прем&rsquo;єр&ndash;міністром СРСР Молотовим, й містила пункт, що стосується геноциду народу: спеціальний переселенець зобов&rsquo;язаний при народженні дитини реєструвати її через три доби, як нового спеціального переселенця! Найбільше мене вразив цей дикий законопроект, який ООН констатує як геноцидний акт. Я вирішив, нам не можна мати дітей &mdash; вічних каторжників! Багато сімей саме тому залишилися самотніми. Цей пункт постанови був відкритим викликом проти національної політики, проти тюркського світу, проти незгодних народів. Нема народу &mdash; нема проблеми.</p>\r\n<h4>Її не прописували по всій Україні</h4>\r\n<p>Ми з Наталією Йосипівною звільнилися від комендантського нагляду в 1956 роцi й приїхали до міста Нальчик, де нас так само зустріли &laquo;у штики&raquo;. Відмовили у прописці, і довелося шукати нам &laquo;щастя&raquo; у Львові, де ми наштовхнулися на ще гостріші &laquo;багнети&raquo;. Наталію Йосипівну як сестру Романа Шухевича не прописували по всій Україні. Мене насилу прописали і вмовляли розлучитися з нею. Умови для нас були створені нелюдські, постійно примушували залишити Львів. Ми ледве протрималися до 1960 року. Але потім змушені були поїхати назад до Нальчика. Місцеві органи КДБ не випускали нас із поля зору: постійні виклики, допити, котрі, повірте, сильно позначалися на стані здоров&rsquo;я і на психіці.</p>\r\n<h4>Менi б хотiлося померти в церквi</h4>\r\n<p>Наталія Йосипівна як справжня християнка зраділа, коли за допомогою священиків з Ірландії постала католицька парафія Святого Йосипа в Нальчику. Тоді вона ще не мала власного приміщення, парафіяни тулилися у приватних будинках, у порожніх санаторіях у Долинську (районі Нальчика). Пізніше було придбано приміщення, яке після ремонту й перепланування стало церквою.</p>\r\n<p>Наталія Йосипівна дуже любила її, відвідувала щонеділі та поверталася завжди радісно оновленою. Коли хвороба почала прогресувати, їй було важко ходити до церкви. Я боявся відпускати її саму. Тож іноді з церкви приходили по неї.</p>\r\n<p>При церкві є монастир. Дивовижний куточок, де бідні, хворі, нужденні люди знаходять турботу і затишок. Невеликий дворик і двоповерховий будинок, у дворі квіти, дерева фруктові, зелень навкруги. Гуртожитки &mdash; чоловічий і жіночий &mdash; так добре обставлені, що будь&ndash;який санаторій може позаздрити. Бідні люди тут проходять реабілітаційний догляд. Нагодовані, одягнені, приємно на них дивитися. Це результат постійної турботи черниць. Бували випадки, коли в республіканську лікарню приходили безпритульні, брудні люди, й адміністрація державної лікарні викликала цих самих черниць&hellip; Таке ставлення до свого ближнього можуть дати лише католицькі монашки.</p>\r\n<p>Мене вражає те, що цей католицький прихід єднає безліч народів: росіян, українців, поляків, німців, французів, ірландців, індійців, румунів, балкарців, кабардинців тощо. Людей об&rsquo;єднує одна iз заповідей Божих &mdash; &laquo;любов до ближнього свого&raquo;. Виходять із храму вони з радісними обличчями. Повторюю ще раз: моя дружина любила свій прихід і навіть говорила: &laquo;Мені б хотілося померти в церкві&raquo;.</p>\r\n<h4>Кожен із нас хотів першим піти з цього світу</h4>\r\n<p>Вона часто згадувала про смерть. Ми з нею сперечалися: кожен із нас хотів першим піти з цього світу. Вона була мобільнішою і бадьорішою за мене. Встигала робити по будинку майже все. Ми знали, що у неї небезпечна хвороба &mdash; склероз черевної аорти. Єдиний порятунок &mdash; операція, яка їй була протипоказаною. Так що ми обмежувалися лише рекомендаціями безпорадних лікарів. Кінець стався 21 лютого 2010 року, напередодні дня народження Наталії &mdash; 25 лютого. Ми хотіли поїхати на базар по продукти. Вранці вона встала бадьора і сказала: &laquo;Я так спала сьогодні, як ніколи&raquo;. І раптом каже, що в неї нога сильно заболіла. Вона її натерла якоюсь маззю. Наші процедури нічим не допомагали. Ми викликали &laquo;швидку&raquo;. Але медики теж не могли нічого вдiяти. Її повезли в республіканську лікарню, де буквально впродовж двох годин закінчила свій життєвий шлях багатостраждальна, легендарна жінка.</p>\r\n<h4>Як ти можеш мені таке пропонувати? Нікуди від тебе я не поїду</h4>\r\n<p>Вона постійно і невпинно молилася, щоб не побачити моєї смерті. І пішла першою. Для себе вона просила дві речі: нележачої смерті і щоб її поховали в родинному склепі у Львові.</p>\r\n<p>Вражаюче! Все вийшло як на замовлення. Вона просила цього у Небесного Отця. Як цьому не вірити!? Я все це бачив своїми очима і чув на власні вуха. Це не простий збіг.</p>\r\n<p>Вона була не проста смертна; в ній було щось згори. Життя разом упродовж 63 років, її присутність на кожному кроці мене підбадьорювали, давали силу, впевненість у собі. Її відсутність стала нестерпною. Не можу знайти собі місця. &laquo;Що це значить?&raquo; &mdash; запитував я себе. Відповіді не знаходив. Тепер, коли її немає, я ясно зрозумів, що вона володіла святістю від Бога.</p>\r\n<p>Колись давно у нас із нею була така розмова. Знаючи, що вона мучиться, доглядаючи мене хворого, я їй кажу: &laquo;Наталочко, я бачу, як тобі важко. Ти мучишся зі мною. Давай візьмемо провідника, і ти їдь до Львова до племінниці. Хоч відпочинеш місяць, а за цей час зі мною хто&ndash;небудь побуде&raquo;. Вона так розридалася, що я ледве її заспокоїв. Вона сказала: &laquo;Як ти можеш мені таке пропонувати? Якщо б ти жив ще 20 років у такому стані, я б повзала і доглядала за тобою. Нікуди від тебе я не поїду&raquo;.</p>\r\n<p>Антін БОРКОВСЬКИЙ<br />http://umoloda.kiev.ua/number/171/0/60874/</p>\r\n</span></span></span></p>',NULL,2127,5,1,NULL,27);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(2603,'P','2010-08-29',1,NULL,'Rss','Успіння Пресвятої Богородиці','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3609,200,1.JPG\" />28 серпня Церква святкує велике свято – Успіння Пресвятої Богородиці.</p>\n<p>Цього дня віяло прохолодою, хмари заступили усе небо, крапав невеликий дощ, пожовклі листочки поволі падали на землю, хоча ще не прийшов «листопад». &nbsp;Але не дивлячись на пахмурну погоду, багато вірних прийшло до Аскольдової могили, щоб помолитися та взяти участь у Святій Літургії. Погода навіювала нам певний смуток, адже у цей день увійшла у вічність Пресвята Богородиця. Однак Свята Церква та й усі ми християни повинні розглядати цю подію як свято радості, адже тіло Пречистої Богородиці по смерті Діва не зазнало зотління, але разом з душею було взято на небо.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=572:uspinnja-presvjatoji-bogorodyci&catid=25:parafija-cat&Itemid=27',2128,NULL,1,1,26),
(2604,'P','2010-09-04',1,NULL,'Note','Шкільний портфелик','<p><img style=\"float: left;\" src=\"image,3614,200,1.jpg\" alt=\"\" width=\"200\" height=\"133\" />Завершився проект Карітас - Печерськ допомоги потребуючим дітям. Завдяки скринці пожертв св. Миколая, також допомозі Галини і Олексія, Михаїла і&nbsp; Інни, Олега і Лідії було надано допомогу дітям для навчання в школі. Згідно своїх потреб вони отримали шкільний одяг, взутя, спортивні костюми. В неділю 5 вересня 2010 р. о 12:30 буду відправленна Божественна Літургія і уділенне Благословення для успішного навчання дітей.</p>','<p><img style=\"float: left;\" src=\"image,3614,200,1.jpg\" alt=\"\" width=\"200\" height=\"133\" />Завершився проект Карітас - Печерськ допомоги потребуючим дітям. Завдяки скринці пожертв св. Миколая, також допомозі Галини і Олексія, Михаїла і&nbsp; Інни, Олега і Лідії було надано допомогу дітям для навчання в школі. Згідно своїх потреб вони отримали шкільний одяг, взутя, спортивні костюми. В неділю 5 вересня 2010 р. о 12:30 буду відправленна Божественна Літургія і уділенне Благословення для успішного навчання дітей.</p>\r\n<p>Запрошуємо на цю Службу не тільки першокласників, але і вс<img style=\"float: right;\" src=\"image,3614,200,2.jpg\" alt=\"\" width=\"121\" height=\"200\" />іх учнів і&nbsp;студенство. Для дітей будуть роздані дарунки солодощами, ручками, зошитами, олівцями. По завершенню запрошуємо дітей на святкову гостину, яку приготують ресторан \"Бліни\" і ресторан \"Курені\", також пекарня добродія Ігоря пригостить дітей приниками власного виробництва, батьківська рада подбає про напої. Нагадую батькам, що в цей день відбудеться запис до церковної недільної школи. Чикаю на вас. З богом.</p>\r\n<p>Матеріал Іллі</p>\r\n<p>&nbsp;</p>\r\n<table style=\"width: 494px;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<colgroup span=\"1\"><col span=\"1\" width=\"43\"></col><col span=\"1\" width=\"156\"></col><col span=\"1\" width=\"103\"></col><col span=\"3\" width=\"64\"></col></colgroup>\r\n<tbody>\r\n<tr height=\"42\">\r\n<td width=\"43\" height=\"42\">&nbsp;</td>\r\n<td colspan=\"2\" width=\"259\">\"ШКІЛЬНИЙ ПОРТФЕЛИК\"</td>\r\n<td width=\"64\">&nbsp;</td>\r\n<td width=\"64\">&nbsp;</td>\r\n<td width=\"64\">&nbsp;</td>\r\n</tr>\r\n<tr height=\"25\">\r\n<td height=\"25\">&nbsp;</td>\r\n<td colspan=\"2\">Витрати на шкільне приладдя</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr height=\"25\">\r\n<td height=\"25\">&nbsp;</td>\r\n<td colspan=\"2\">30.08.2010 р.Б.</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr height=\"25\">\r\n<td height=\"25\">&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr height=\"25\">\r\n<td height=\"25\">№П/П</td>\r\n<td>ПІБ</td>\r\n<td>СУМА, ГРН</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr height=\"25\">\r\n<td height=\"25\">1</td>\r\n<td>Олег Ануріїв</td>\r\n<td>453</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr height=\"25\">\r\n<td height=\"25\">2</td>\r\n<td>Андрій Мосягін</td>\r\n<td>424</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr height=\"25\">\r\n<td height=\"25\">3</td>\r\n<td>Ніна Патрова</td>\r\n<td>442</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr height=\"25\">\r\n<td height=\"25\">4</td>\r\n<td>Степан Костров</td>\r\n<td>488</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr height=\"25\">\r\n<td height=\"25\">5</td>\r\n<td>Сергій Патров</td>\r\n<td>458</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr height=\"25\">\r\n<td height=\"25\">6</td>\r\n<td>Валентин Костюк</td>\r\n<td>436</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr height=\"25\">\r\n<td height=\"25\">7</td>\r\n<td>Валерія Тютіннін</td>\r\n<td>403</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<