td height=\"21\">&nbsp;</td>\r\n<td colspan=\"5\">&nbsp;Додатково пожертвованно 2000 грн, родиною Олега і Лідії.</td>\r\n</tr>\r\n<tr height=\"21\">\r\n<td height=\"21\">&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr height=\"21\">\r\n<td height=\"21\">&nbsp;</td>\r\n<td>Всього:</td>\r\n<td>6926, 31</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n<tr height=\"21\">\r\n<td height=\"21\">&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n</tbody>\r\n</table>',NULL,2131,4,1,NULL,19);
