INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(2609,'P','2010-09-09',0,NULL,'Note','','<div style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">МОЛИТВА ЗА ХВОРОГО</span></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ісусе Христе Боже наш! Коли хвора жінка з вірою доторкнулася до краю одежі Твоєї, вона стала здоровою. По вірі її Ти дав їй. З такою вірою і я вдаюся до Тебе, Лікарю всіх, допоможи рабу Твоєму (ім\'я) в його недузі. Дай йому спокій душевний і силу тілесну, поблагослови його сном спокійним, а з ним і здоров\'ям, і силою, і підійми його з ложа хвороби. Господи, ласка Твоя нехай буде над нами, бо ми надіємось на Тебе. Амінь.</span></span></div>','',NULL,2136,5,1,NULL,50),
(2610,'P','2010-09-15',1,NULL,'Note','ХРЕСНА ДОРОГА - ЗАПОВІТ ПАТРІЯРХА ЙОСИФА','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><img style=\"float: right;\" src=\"image,3620,200,4.jpg\" alt=\"\" />Всеукраїнська проща - паломництво на вшанування 26-тої річниці відходу до вічності світлої пам&rsquo;яті Блаженнішого патріарха Йосифа Сліпого.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В часі хресного ходу підчас стоянь читається відповідна частина Заповіту Патріарха Йосипа разом з молитвами Хресної дороги Господа нашого Ісуса Христа.&nbsp;В дорозі між стояннями прочани відмовляють молитви в загальному намірі, в особистих намірах та в намірах жертводавців, які долучилися до участі у прощі своїми пожертвами.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3620,200,1.jpg\" alt=\"\" />Київ - Заздрість - Зарваниця</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">10 - 12 вересня 2010 року Божого</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В часі хресного ходу підчас стоянь читається відповідна частина Заповіту Патріарха Йосипа разом з молитвами Хресної дороги Господа нашого Ісуса Христа.&nbsp;В дорозі між стояннями прочани відмовляють молитви в загальному намірі, в особистих намірах та в намірах жертводавців, які долучилися до участі у прощі своїми пожертвами.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,3620,200,7.jpg\" alt=\"\" />Загальний намір прощі-паломництва - &bdquo;За прощення і спасіння для всього Українського народу, за Боже Благословення для духовного Проводу Української Христової Церкви та об\'єднання всіх християн в Україні у єдиній помісній Українській Христовій Церкві під проводом Київського Патріарха, за Боже благословення для України, <img style=\"float: left;\" src=\"image,3620,200,22.jpg\" alt=\"\" />глави Держави - Президента України і всього державного проводу, за єдність влади і народу, за звільнення вад облуди ворожнечі і розбрату, за просвітлений в дусі миру і любові і пізнання Божої волі напередодні відповідального завдання для кожного українця &mdash; обрання на наступний термін нової благословенної Богом і справедливої місцевої влади\".</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3620,200,125.jpg\" alt=\"\" width=\"133\" height=\"200\" /><img style=\"float: right;\" src=\"image,3620,200,_MG_2465.JPG\" alt=\"\" width=\"133\" height=\"200\" /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3620,200,_MG_2523.JPG\" alt=\"\" width=\"200\" height=\"133\" /><img style=\"float: right;\" src=\"image,3620,200,4.jpg\" alt=\"\" width=\"200\" height=\"133\" /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3620,200,3.jpg\" alt=\"\" width=\"200\" height=\"104\" /><img style=\"float: right;\" src=\"image,3620,200,5.jpg\" alt=\"\" width=\"200\" height=\"113\" /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>',NULL,2137,4,1,NULL,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(2611,'P','2010-09-13',1,NULL,'Rss','Служба милосердя','<p>Парафіяльна служба милосердя з отцем Анатолієм та Орестою 03 вересня відвідали хворих та самотніх людей. Серед яких Олег, що хворіє на імунодефіцит, п. Світлану в якої хворі ноги, Богу подяка, що вже може ходити.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=576:sluzhba-myloserdja&catid=25:parafija-cat&Itemid=27',2138,NULL,1,1,26),
(2612,'P','2010-09-13',1,NULL,'Rss','Молодіжна проща до Вишгорода','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3620,200,125.jpg\" />Щороку у вересні місяці відбувається молодіжна проща до Вишгорода. Цього року проща збільшила свої масштаби, адже крім пішої прощі була також і велопроща. Велопроща&nbsp; брала свій початок у місті Ірпінь і 20 велопрочан мали подолати <st1:metricconverter productid=\"40 км\">40 км</st1:metricconverter>. Піша проща брала свій початок із міста Києва і всі прочани за задумом організаторів прощі мали зустрітись у&nbsp; місті Вишгороді.</p>\n<p>Проща до Вишгорода – це &nbsp;моя перша піша проща (у Вишгород також). З перших хвилин прощі &nbsp;відчувалася тепла, дружня, радісна атмосфера. Хоч &nbsp; дорога неблизька, та молитвою і піснею на вустах подолали ми її легко.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=577:molodizhna-proshcha-do-vyshgoroda&catid=25:parafija-cat&Itemid=27',2139,NULL,1,1,26),
(2613,'P','2010-09-13',1,NULL,'Rss','УГКЦ візьме участь у Всесвітньому Дню Творця ','<p>14 вересня в Українській Греко-Католицькій Церкві святкуватимуть Всесвітній Екологічний <em>День Творця</em>.&nbsp; В цей день в греко-католицький храмах молитимуться та служитимуть екологічні служби за збереження природи.</p>\n<p>Молебень за участю Глави УГКЦ, Патріарха Любомира (Гузара) відбудеться у храмі св. Миколая на Аскольдовій Могилі (м. Київ, вул. Паркова Дорога 1) 18:00.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=578:ugkc-vizme-uchast-u-vsesvitnomu-dnju-tvorcja-&catid=25:parafija-cat&Itemid=27',2140,NULL,1,1,26),
(2614,'P','2010-09-14',1,NULL,'Rss','Храм УГКЦ в Одесі: дві сторони однієї медалі','<p>Як не парадоксально це звучить, але конфлікт із будівництвом церкви для українських греко-католиків в Одесі, а точніше фактична заборона його будівництва, свідчить про серйозні проблеми… в українському православ’ї.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=579:hram-ugkc-v-odesi-dvi-storony-odnijeji-medali&catid=27:gk-v-ukrajini-cat&Itemid=57',2141,NULL,1,1,26),
