INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(2616,'P','2010-09-14',1,NULL,'Rss','«Ми за рівні права усіх Церков»: Блаженніший Любомир про Звернення Синоду Єпископів УГКЦ до Президента України  ','<p><img style=\"margin: 10px; float: right;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_huzar_press.jpg.jpg\" height=\"111\" width=\"82\" />«Тут не розходиться виключно про греко-католиків. Нам розходиться, щоб усі в Україні релігійні організації і Церкви мали забезпечене це саме право. Це підстава нашого мирного співжиття. Стільки років досі ми співжили мирно, спокійно», – так 13 вересня під час прес-конференції у Києві Глава УГКЦ Блаженніший Любомир прокоментував <a href=\"http://ugcc.org.ua/1411.0.html\">Синодальне звернення найвищих ієрархів Церкви до Президента України</a>.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=580:lmy-za-rivni-prava-usih-cerkovr-blazhennishy-ljubomyr-pro-zvernennja-synodu-jepyskopiv-ugkc-do-prezydenta-ukrajiny-&catid=27:gk-v-ukrajini-cat&Itemid=57',2143,NULL,1,1,26),
(2617,'P','2010-10-01',0,NULL,'Note','Екскурс у Мамаєву слободу','<p><img style=\"float: left;\" src=\"image,3648,200,DSC01587.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3648,200,DSC01571.JPG\" alt=\"\" /></p>','<p><img style=\"float: left;\" src=\"image,3648,200,DSC01587.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3648,200,DSC01571.JPG\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,3648,200,DSC01592.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3648,200,DSC01598.JPG\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,3648,200,DSC01602.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3648,200,DSC01619.JPG\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,3648,200,DSC01630.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3648,200,DSC01634.JPG\" alt=\"\" /></p>',NULL,2165,5,1,NULL,49),
(2618,'P','2010-09-14',1,NULL,'Rss','Звернення Блаженнішого Любомира, Верховного Архиєпископа Києво-Галицького, до вірних Української Греко-Католицької Церкви та людей доброї волі з нагоди дня Бога Творця (2010 р. Б.)','<p style=\"text-align: right;\"><em>«Яка їх, Твоїх діл, Господи сила! У премудрості все Ти створив, -<br />повна земля Твоїх створінь!»</em></p>\n<div style=\"text-align: right;\"></div>\n<p style=\"text-align: right;\"><em>Пс. 103, 24 </em></p>\n<p style=\"text-align: center;\"><em>Дорогі у Христі!</em></p>\n<p>Починаючи новий Церковний рік, ми знову задумуємось над питанням свого християнського покликання у створеному Богом світі. Це питання стає все більш актуальним в контексті важкого захворювання нашої планети, яке науковці називають екологічною кризою.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=582:zvernennja-blazhennishogo-ljubomyra-verhovnogo-arhyjepyskopa-kyjevo-galyckogo-do-virnyh-ukrajinskoji-greko-katolyckoji-cerkvy-ta-ljude-dobroji-voli-z-nagody-dnja-boga-tvorcja-2010-r-b&catid=27:gk-v-ukrajini-cat&Itemid=57',2144,NULL,1,1,26),
(2619,'P','2010-09-18',1,NULL,'Rss','Національна ідея митрополита','<p>Верховна Рада України не підтримала проект постанови, якою передбачалося на високому державному рівні відзначити 145-річчя від дня народження одного з найбільших церковних діячів України, глави Української Греко-Католицької Церкви, визначного громадського та політично-державного діяча митрополита Андрея Шептицького. Неординарність такого рішення вітчизняних парламентарів полягає у тому, що цього разу зроблено спробу «заблокувати» шлях до загальнодержавного визнання людині, чий авторитет і суспільна вага мають незрівнянно більший вплив на сьогодення і майбутнє України, аніж парламент нинішнього скликання.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=583:nacionalna-ideja-mytropolyta&catid=27:gk-v-ukrajini-cat&Itemid=57',2145,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(2620,'P','2010-09-18',1,NULL,'Rss','Любомир Гузар: Ну який мені брат Путін чи Медвєдєв? ','<p><strong><em>Невже хтось сподівався, що ми  візьмемо коси, сокири й підемо на вулицю?.. Ну що я можу сказати  Агафангелу? Тут не треба бути аналітиком, аби зрозуміти, що діється… </em></strong></p>\n<p><strong><em> </em></strong>Голова Української греко-католицької церкви Блаженіший Любомир Гузар відповів на запитання УНІАН.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=584:ljubomyr-guzar-nu-jaky-meni-brat-putin-chy-medvjedjev-&catid=27:gk-v-ukrajini-cat&Itemid=57',2146,NULL,1,1,26),
(2621,'P','2010-09-19',1,NULL,'Rss','Доктор Антун Лисець: «Щоб світ змінився, необхідно говорити йому правду про гріх» ','<p>Нещодавно портал «Православие.ру»  опублікував у скороченому вигляді інтерв\'ю з доктором медицини з Хорватії, католиком-мирянином, який присвятив себе боротьбі за людське життя й захист сімейних цінностей, Антуном Лисець. Доктор Лисець вже більше 25 років займається відвідуванням клінік, які роблять аборти, намагаючись переконати лікарів у необхідності захисту життя, що зароджується, а не його знищення.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=586:doktor-antun-lysec-lshchob-svit-zminyvsja-neobhidno-govoryty-omu-pravdu-pro-grihr-&catid=37:spilnota-cat&Itemid=49',2147,NULL,1,1,26),
(2622,'P','2010-09-19',1,NULL,'Rss','Блаженніший Любомир про ситуацію в Одесі: «Якщо влада прийме для нас неприхильне рішення, то хай би гідно його пояснила»  ','<p><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_huzar_dt_10.jpg.jpg\" width=\"114\" height=\"98\" />Таку думку висловив Глава УГКЦ Блаженніший Любомир <a href=\"http://www.ugcc.org.ua/1422.0.html\">в інтерв’ю УНІАН</a>, коментуючи ситуацію навколо скандалу, пов’язаного з побудовою в Одесі храму УГКЦ.</p>\n<p>За словами Блаженішого Любомира, представники УГКЦ за кілька останніх років «пройшли вже різні митарства».</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=585:blazhennishy-ljubomyr-pro-sytuaciju-v-odesi-ljakshcho-vlada-pryme-dlja-nas-nepryhylne-rishennja-to-ha-by-gidno-ogo-pojasnylar-&catid=27:gk-v-ukrajini-cat&Itemid=57',2148,NULL,1,1,26),
(2623,'P','2011-09-20',1,NULL,'Rss','Різдво Пресвятої Богородиці','<p>Коротка історія</p>\n<p align=\"justify\">Церква не має звичаю святкувати день народження для землі Святих Божих, але їхній день народження для неба - день смерти. Виїмок становлять двоє найбільших Святих у Церкві: Пречиста Діва Марія і св. Іван Христитель. Ми святкуємо не тільки їхнє небесне, але й їхнє земне різдво.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=581:rizdvo-presvjatoji-bogorodyci&catid=27:gk-v-ukrajini-cat&Itemid=57',2149,NULL,1,1,26),
(2624,'P','2010-09-20',0,NULL,'Note','Сьогодні народилась Київська семінарія','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: right;\" src=\"image,3633,200,1.jpg\" alt=\"\" width=\"200\" height=\"133\" />Архієрейською Божественною Літургією в неділю 19 вересня 2010 р.Б.&nbsp;&nbsp;розпочнеться навчання в Київській Трьохсвятительський семінарії, яка розмістилась в селі Княжичі.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Семінарія Київської Архиєпархії прийняла до себе перших 14 учнів із України. Їх Благословили Єпископи Кир. Йосиф, Київський помічник. Кир. Стефан, Донецький, Кир. Йосафат Луцький і Кир. Василь Одеський.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: left;\" src=\"image,3633,200,1.jpg\" alt=\"\" width=\"200\" height=\"133\" />Архієрейською Божественною Літургією в неділю 19 вересня 2010 р.Б.&nbsp;&nbsp;розпочнеться навчання в Київській Трьохсвятительський семінарії, яка розмістилась в селі Княжичі.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: right;\" src=\"image,3633,200,33.jpg\" alt=\"\" width=\"200\" height=\"133\" />Семінарія Київської Архиєпархії прийняла до себе перших 14 учнів із України. Їх Благословили Єпископи Кир. Йосиф, Київський помічник. Кир. Стефан, Донецький, Кир. Йосафат Луцький і Кир. Василь Одеський.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: left;\" src=\"image,3633,200,34.jpg\" alt=\"\" width=\"200\" height=\"133\" />Розпочнеться навчання Благословенням Блаженнішого Любомира.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Покликанні семінаристи отримали часослови, дар родини о. Мирослава Латинника пароха Ірпеня. Многая Літа співали Владиці Стефану з нагоди ювілею уродин.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: right;\" src=\"image,3633,200,4.jpg\" alt=\"\" width=\"200\" height=\"133\" />Також заліковою книжкою отримали&nbsp;студенти київської школи мирянського лідерства.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: left;\" src=\"image,3633,200,image.jpg\" alt=\"\" width=\"200\" height=\"133\" />Був представлений ректор&nbsp; семінарії о. Петро Жук і колектив.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Радіймо бо стали ми свідками великої події. Молімось за покликання.</span></span></p>',NULL,2150,4,1,NULL,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(2625,'N','2010-09-23',1,NULL,'Note','Звіт наданої допомоги дітям','<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>З</strong><strong>віт наданої допомоги дітям, до початку 1 вересyя 2010 навчального шкільного року, дітям, які залишtні батьківської опіки (їх матері ув&rsquo;язнені). </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Болісно дивитися на страждання дітей, яких доля змусила терпіти. Економічна криза та багато інших негативних факторів, призвели до появи в нашому суспільстві безпритульних дітей.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ув&rsquo;язнені<strong> </strong>матері турбуються за своїх дітей, ось слова матері Алли, яка терпить покарання, просить допомоги в школу її донечці Діані.</span></span></p>','<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>З</strong><strong>віт наданої допомоги дітям, до початку 1 вересня 2010 навчального шкільного року, дітям, які залишені батьківської опіки (їх матері ув&rsquo;язнені). </strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Болісно дивитися на страждання дітей, яких доля змусила терпіти. Економічна криза та багато інших негативних факторів, призвели до появи в нашому суспільстві безпритульних дітей.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ув&rsquo;язнені<strong> </strong>матері турбуються за своїх дітей, ось слова матері Алли, яка терпить покарання, просить допомоги в школу її донечці Діані.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пише мати, Діани: &laquo;<em>моей</em><em> дочери</em><em> Дианочке 12 лет, воспитывает ее мама. Мамае 72 года, очень болеет, нужны деньги, чтобы собрать в сентябре Диану в школу, а денег нет. Дианочке нужны школьные принадлежности, сумка ранец, ручки, тетради, фломастеры, линейки, блокнот, школьная форма, а у матери просто денег нет, и на ноги обуви нет. Позвонила я домой,</em> <em>ребенок расстроенный. </em></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Отец Игорь, помогите собрать ребенка в школу, чтобы 1-го сентября она была счастлива. Господи, как мне жить с моей несостоятельностью? Помогите моей доченьке Дианочке.</em>&raquo;&nbsp;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Лист матері Світлани, яка відбуває покарання в Чернігівській виправній колонії:&nbsp; &laquo;<em>У </em><em>меня</em><em> дома остался</em><em> маленький сынок</em><em>, ему</em><em> 7 лет. Он живет</em><em> с моей</em><em> мамой. Маме очень тяжело, а сын мой Женя идет в первый класс. Вы знаете, сколько надо сейчас денег на школу. Извините, но я пишу Вам с надеждой, что Вы поможете. Для себя я ничего не прошу, прошу, помогите моему сыну. Я знаю, что Бог со мной и он услышит мои молитвы.</em>&raquo;.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Мати Алла А&hellip; в листі до нас, просить допомоги її дитині Андрію, в нього закінчується навчання в Університеті, має відбутися випускний вечір, а у нього немає одягу і взуття.&nbsp;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дякуючи Богу, парафіяльна служба церкви Святого Миколая на Аскольдовій могилі в особі настоятеля церкви<em> </em>отця Іго, а також завдяки доброчинцям п. Лесі з родини О.Тихого, п. Олександру з Тернополя, п. Євстахію &nbsp;з Києва, яки допомогли коштами, ва які помогли коштами, служба придбала необхідні канцтовари, а також солодощі для потребуючих дітей. </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Багато сил, часу і коштів потрібно для такої праці в допомозі, але протягнута рука часто рятує і підтримує дитячі зболені серця. &nbsp;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Волонтери від церкви Святого Миколая на Аскольдовій могилі п. Ганна, п. Марта, п. Людмила, п. Галина підготували допомогу дітям до школи.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ось, що входило в гуманітарні пакети: зошити в клітинку зошити в лінійку, ручки, альбоми для малювання, олівці кольорові, олівці звичайні, фарби акварельні, ручки кулькові, щоденники, а також ранці.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Одягом&nbsp; і взуттям допомогли прихожани церкви.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Також придбали гігієнічні товари: мило, зубні щітки, зубну пасту, шампунь. Солодощі дітям, цукерки та печиво.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Подяка матері Алли&hellip; за допомогу її дитині &ndash; синові, який навчався в Інтернаті і закінчував навчатись. &laquo;<em>У мене не було меж радості, мені хотілось кудись бігти &ndash; щось робити, я плакала і сміялась.</em></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Боже я не знаю, як Вам дякувати за Ваше розуміння, за допомогу моєму синові. Ранком на роботі всім жінкам розказувала, що є Бог і Він чує наші молитви і допомагає нашім дітям.</em>&raquo;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Лист дитини Андрія &ndash; &laquo;П<em>осилку від Вас я отримав, за яку Вам велике спасибі, Всі речі підійшли, а також дякую за солодощі. Костюм буде на випускний вечір. </em></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Завдяки Вам от. Ігор, я до випускного вечора вже готовий. Нехай Бог Вас береже за Вашу доброту.</em>&raquo;.</span></span></p>\r\n<ol>\r\n<li>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Виготовили і вислали дітям гуманітарні пакети на суму <span style=\"text-decoration: underline;\">703,27 грн.</span>&nbsp; </span></span></p>\r\n</li>\r\n<li>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В пакети вложили конверти з почтовими знаками на суму <span style=\"text-decoration: underline;\">20,40 грн.</span></span></span></p>\r\n</li>\r\n<li>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Заплачено за поштові коробки <span style=\"text-decoration: underline;\">102,00 грн.</span></span></span></p>\r\n</li>\r\n<li>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Заплачено пошті за пересилку посилок <span style=\"text-decoration: underline;\">235,08 грн.</span></span></span></p>\r\n</li>\r\n</ol>\r\n<p align=\"right\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"text-decoration: underline;\">Разом витрачено: 1&nbsp;060, 75 грн.&nbsp; </span></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Настоятель церкви св. Миколая на Аскольдовій могилі отець Ігор Онишкевич</span></span></p>',NULL,2151,4,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(2626,'P','2010-09-23',1,NULL,'Note','ПОЖЕРТВИ ЗА ВЕРЕСЕНЬ 2010 Р.Б.','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесені Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</span></span></p>','<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">ПОЖЕРТВИ ЗА ВЕРЕСЕНЬ 2010 Р.Б.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесені Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</span></span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пожертва Володимира Северина на церкву &nbsp;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пожертва Марії на розпис церкви &nbsp;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пожертва сім&rsquo;ї Петра, Марії </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пожертва родини Криворуків на церкву</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пожертва родини Греськів на будівлю храму </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пожертва родини Курилона церкву</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пожертва родини Роїк на потреби церкви </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пожертва родини Турковських на церкву </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пожертва родини Турковських &laquo;просимо за молитву за нашу сім&rsquo;ю і всю родину&raquo; </span></span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp; <br /></span></span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Милостиню роби згідно з тим,</span></span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">що маєш;</span></span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Коли у тебе достатків повно,</span></span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">роби з них,</span></span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">а коли мало маєш,</span></span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">то не бійся й з того дати.</span></span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Бо так готуєш собі добрий скарб</span></span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">на день потреби.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;Товит 4. 8-9.</span></span></p>',NULL,2152,4,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(2627,'P','2010-09-24',1,NULL,'Note','Радійте зі мною, бо я знайшов овечку, що була загубилась.  (Лука 15, 6)','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Цей лист надіслала нам сестра Алла, яка зараз сидить у в&rsquo;язниці. Очевидно, що вона вона сидить і розуміє, що вона скоїла погані вчинки за які тепер усвідомлено страждає. Але вона навернулась і своїм прикладом показує людям, що з Богом все можливо.<br /></span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Цей лист надіслала нам сестра Алла, яка зараз сидить у в&rsquo;язниці. Очевидно, що вона вона сидить і розуміє, що вона скоїла погані вчинки за які тепер усвідомлено страждає. Але вона навернулась і своїм прикладом показує людям, що з Богом все можливо. Вона змінила себе і свої погляди на віру і тепер живе у Господі. Вона співпрацює з нами присилає для газети Оранти ці вірші і дописи. Вона потребує вас і вашої &nbsp;підтримки. Не будьте байдужими, проявіть своє милосердя. Вона потребує вашої уваги у будь-яких проявах, напишіть лист за адресою, який знаходиться у службі милосердя церкви св. Миколая на Аскольдовій могилі. Якщо маєте можливість допоможіть продуктами.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">***</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Не має кращого в цім світі,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">чим бути в Божій благодаті,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">і коли кожен час в служінні</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">єдиний Дух усіх єднає .</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я дякую Тобі , Господь,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">за всіх братів і всіх сестер,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">які дарують нам любов</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">і в них ми бачимо Тебе.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я дякую Тобі, Отець,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За всіх твоїх служителів,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">І за спасіння тих овець,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Що можуть чути вчителів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я дякую Тобі , наш Бог</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За розкриття Твоїх шляхів,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За світло і Твоє тепло,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Що Ти несеш від вчителів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я дякую Тобі , Господь,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За наших всіх служителів,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За кріп кість віри та любов,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Що Ти даруєш через них.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">И еще, излюбленные мои братья и сестры, мне очень хочется выслать сегодня свой жалом, который я назвала:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Завет с Богом</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>1 куплет: </em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ты не раз меня звал, призывал и спасал, знаю я.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Как ты ждал, мой Отец, этого светлого дня,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Как ты долго тянул свои руки ко мне и грустил,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Как страдал, пока сердце мое для себя не открыл.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">А сегодня ликует душа и кружат облака,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Мое сердце открыто, в него я впускаю Христа.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Обретаю я новую жизнь в Завете с Отцом,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">И теперь в моем сердце господствует только любовь.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>Припев: &nbsp;</em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;Я склоняю свои колени, я не ангел, я не гений, просто стать хочу похожей на Христа.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У Отца прошу прощения, принимая очищенье в покаянии от всякого греха.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>2 куплет: </em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Благодатью окутано тело и сердце с душой,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Не нужны мне сокровища мира в завете с тобой.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Нет ценны на все то, что Отец мой любя сотворил,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Для меня Он великую тайну свободы открыл.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Принимая крещенье водой, заключая завет,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я отцу обещал нести в мир, тепло лишь и свет.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Разорву я все нити греха и поду за Христом,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пуль не легкий пойду я, будет больно мне, ну а потом:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>Припев:&nbsp; &nbsp;</em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За смирение в обличении, за победу над искушением, я прийму благословеннее Отца.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я склоняю свои колени, &nbsp;я не ангел, я не гений, просто стать хочу похожей на Христа.<em>&nbsp;</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&hellip;На этом я пока закругляюсь, мои драгоценные сестры и братья во Христе. Я очень буду ждать от вас весточку. Очень хочу выслать для вашей Церкви подарок, сделанный своими руками, поэтому хочу попросить у вас большой конверт размером с форматного листа и почтовых марок на 9 грн. С этим у меня постоянная нехватка. Но Бог видит мои нужды и стремления, по этому я все отдаю в Его Всемогущие руки. Будьте благословенны! Мира, света, тепла и добра вам.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">С любовью ваша сестра Алла.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">02.09.2010г.&nbsp;&nbsp; &nbsp;</span></span></p>',NULL,2153,4,1,NULL,19);
