INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1266,'P','2008-12-21',1,NULL,'Note','Папа Венедикт XVI прийняв представників італійської Дитячої Католицької Акції','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Щороку перед Різдвом діти і підлітки італійської Дитячої Католицької Акції відвідують Святішого Отця, щоб від імені всіх своїх членів віком від 4-ьох до 14-ти років привітати його з святами. Ця зустріч з Венедиктом&nbsp;XVI відбулась 20 грудня. Коло 50-ки дітей з різних регіонів Італії принесли Папі свої подарунки та привітали з Різдвом.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Щороку перед Різдвом діти і підлітки італійської Дитячої Католицької Акції відвідують Святішого Отця, щоб від імені всіх своїх членів віком від 4-ьох до 14-ти років привітати його з святами. Ця зустріч з Венедиктом&nbsp;XVI відбулась 20 грудня. Коло 50-ки дітей з різних регіонів Італії принесли Папі свої подарунки та привітали з Різдвом. <br /><br />Венедикт&nbsp;XVI дуже радо прийняв юних членів Католицької Акції і, звертаючись до них, окремо наголосив, що Ісус є найдорожчим другом усіх та заохотив їх допомогти своїм друзям пізнати та полюбити Ісуса і Його Церкву, запрошуючи молитись за тих дітей у світі, які голодують, хворіють, які страждають задля воєн та нестатків, молитись також і за те, щоб Господь перемінив серця людей та допоміг людству будувати краще майбутнє для всіх дітей у світі. Папа також попросив дітей молитись і за нього, за нелегку місію, які Господь йому повірив та, побажавши щасливого Різдва, поблагословив усіх дітей.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,834,5,1,NULL,43),
(1267,'P','2008-12-21',1,NULL,'Rss','Депутата, що підняв український прапор над Київрадою у 90 році, привітали на храмовому празнику','<p>У церкві св. Миколая на Аскольдовій Могилі сьогодні під час храмового свята привітали чотирьох іменинників і серед них відомого поета-дисидента депутата Київради 90-го року, який голосував за підняття синьо-жовтого прапора над мерією, Миколу Горбаля.</p>\n<p>«Завжди приємно коли тебе поздоровляють, а тим паче у храмі, до якого ти ходиш на щонеділі службу Божу. Тим паче зараз, коли виходять книжки моїх поезій і люди вільно їх читають» сказав «Оранті» поет-іменинник. Нещодавно вийшла чергова книжка поезій Миколи Горбаля «Ув`язнені». За його словами він святкує іменини щоразу у церкві на Миколая, а день народження 30 вересня, хоч у паспорті в нього значиться, що він народився 6 травня.</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=153:deputata-shcho-pidnjav-ukrajinsky-prapor-nad-kyjivradoju-u-90-roci-pryvitaly-na-hramovomu-praznyku&catid=25:parafija-cat&Itemid=27',837,NULL,1,1,26),
(1268,'P','2008-12-21',1,NULL,'Rss','Співаймо разом з «Аскольдовим гласом»','Петро Жук: «Дуже рідкісна подія на рівні парафії»\n<p>Отець Петро Жук, член жюрі фестивалю «Аскольдів глас», голова катехитичної комісії Київської архиєпархії.</p>\n<p>Дуже приємні враження від дитячого співу. Дуже приємно, що парафія, тобто дуже маленька громада, зуміла зорганізувати такий фестиваль. Це є гарно і повчально для всіх інших. І отців і громад. Подібні фестивалі відбуваються, але більше на рівні всеукраїнському, на рівні регіонів, але ж щоби в церкві, на рівні парафії, то дуже рідкісне явище.</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=154:spivamo-razom-z-laskoldovym-glasomr&catid=25:parafija-cat&Itemid=27',838,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1269,'P','2011-08-27',1,NULL,'Note','ПОЖЕРТВИ ПАРАФІЯН ЦЕРКВИ СВ. МИКОЛАЯ ЧУДОТВОРЦЯ НА АСКОЛЬДОВІЙ МОГИЛІ  У ЛИПНІ 2011 Р. Б.','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</em></strong></span></span></p>\r\n<table style=\"text-align: justify; width: 475px; height: 1096px;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"260\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Ж Е Р Т В О Д А В Ц І</strong></span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Пожертва на (для):</strong></span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Юрій, Лідія, Дмитро та Софія</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церкву і молитву</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Любов</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церкву</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Родина Роїк</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церкву</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Парафіяни</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Прикрасу церкви</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Парафіяни</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дітей з онкологічної лікарні</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Надія</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церкву</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Олег Лужний</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Царські ворота для святилища крипти</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Школа &laquo;Золоче&raquo;</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церкву із освячення школи</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Жіночий камерний хор &laquo;Глорія&raquo; (Gloria) церкви Пресвятої Богородиці м.   Бережани</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вірні вислухали спів хору м. Бережани, об&lsquo;єдналися   разом молитвою до Всевишнього</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ігор та Наталя Бойко</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церкву</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сергій</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Розпис храму</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Світлана</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ув&lsquo;язнених</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Володимир та Світлана</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Десятина</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Микола та Ірина Гамкало</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церкву</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Галина</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дерев&lsquo;яний   вертеп</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Родина Пайкуш (Руслан і Любомир)</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Освячення і закінчення розпису</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Олег, Олександра та Роксолана</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Святкування та молитву</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Родина Курило</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церкву</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ткачук Леонід та Леся</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Святкування</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Оксана Білозір</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Святкування</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Родина Литвинчук (Петро та Лариса)</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Десятина</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Володимир</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церкву</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Олександр та Олена</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Розпис</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Юрій</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церкву</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"260\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n<td width=\"241\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>Милостиню роби згідно з тим,</em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>що маєш;</em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>Коли у тебе достатків повно,</em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>Роби з них,</em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>а коли мало маєш,</em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>то не бійся з того дати.</em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>Бо так готуєш собі добрий скарб</em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>На день потреби.</em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Товит 4. 8-9.</strong></span></span></p>',NULL,2558,5,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1270,'P','2011-08-31',1,NULL,'Note','«А жінці  навчати не дозволяю, ані  панувати над чоловіком,  але  бути  в мовчанні». 2 Тимофію.2:12. Біблія.','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З другої сторони листа ви будете мати  відповідь на свої особисті питання і прохання. Їх дадуть наші волонтери  які жертвують свій час на допомогу Вам. Переписуючись з Вами, дорогі  сестри, вже майже 20 років, не знайшов нікого із сестер із Києва, щоб  хотіли служити тим, що залишаються в цих стінах в покаянні. Мене  цікавить Ваша думка з цього приводу. Чому нема нікого серед Вас, щоб  помагати тим, що залишаються?</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Слава Ісусу Христу!Любі&nbsp; у&nbsp; Христі сестри!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Цей лист є загальний, як навчальний на основі прикладів богобоязних жінок із святої Біблії. Їхнім прикладом бажаємо наставити жінку на праведну і спасенну дорогу життя вічного, яка починається із віри у Воскресіння Христового і Пресвяту Трійцю</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Посилаючи нам свої дари, Господь сподівався, що кожен із нас даватиме щедрі&nbsp; духовні&nbsp; плоди, за святою книгою &bdquo;Левіт&rdquo;, главою 4 (пожертви&nbsp; у Святий Храм).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Головна тема цього&nbsp; церковного звернення &ndash; покаяння та цнотливий&nbsp; характер жінки, створеної із ребра Адамового. (Буття.1-2).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;А жінці&nbsp; навчати не дозволяю, ані&nbsp; панувати над чоловіком,&nbsp; але&nbsp; бути&nbsp; в мовчанні&raquo;. 2 Тимофію.2:12. Біблія.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Любі сестри! Деякі з Вас&nbsp; помилково відступили з Божої дороги любові. Гріх &ndash; то невидима&nbsp; сумна хвороба, і&nbsp; деяким&nbsp; жінкам&nbsp; потрібно&nbsp; мати біблейські знання&nbsp; про нове жіноче богоугодне заняття (вишивання&nbsp; блакитними нитками за св. книгою &bdquo;Числа&rdquo;,15:37-39,&nbsp; пошиття дорожних&nbsp;&nbsp; плащів колективом жінок, у св. книзі &bdquo;Дії святих апостолів&rdquo;, главі&nbsp; 9, за прикладом&nbsp; благодатної&nbsp; діви Тавіфи - Сарни).&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Свідчить св. Біблейська книга &bdquo;Числа&rdquo;, глава 15, вірші&nbsp; 1-4,&nbsp; 37-40.15:1. &bdquo;І сказав Господь Мойсеєві, говорячи: 2 Оголоси синам&nbsp; Ізраїлевим і скажи&nbsp; їм; коли ви&nbsp; ввійдете&nbsp; в&nbsp; землю&nbsp; вашого поселення, яку Я даю вам, 3 і&nbsp; будете&nbsp; приносити жертву&nbsp; Господу,&nbsp; все палення, або жертву, для якої заколюються&nbsp; воли&nbsp; і вівці, на виконання&nbsp; обітниці...&nbsp; щоб&nbsp; зробити приємні&nbsp; пахощі&nbsp; Господу. 4 І тоді той, хто&nbsp; приносить жертву&nbsp; свою&nbsp; Господу,&nbsp; повинен принести у приношення від хліба десяту&nbsp; частину ефи&nbsp; пшеничного&nbsp; борошна, змішаного з четвертою частиною&nbsp; гина&nbsp; єлею&rdquo;. (Числа.16:1-4.Біблія).&nbsp;&nbsp; 37&bdquo;І сказав Господь&nbsp; Мойсеєві, говорячи: 38 Оголоси&nbsp; синам Ізраїлевим і скажи їм, щоб вони робили собі китиці на краях одягу свого&nbsp; в роди їх, і&nbsp; в китиці на краях вставляли нитки з блакитної вовни&rdquo;. &bdquo;Числа&rdquo;.15:37-38.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Свята Лоіда &ndash; вихователь Тимофія</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Павло, з волі&nbsp; Божої &nbsp;&nbsp;апостол Ісуса Христа, за обітницею&nbsp; життя&nbsp; в Христі Ісусі, Тимофію, улюбленому синові&hellip;Пам&rsquo;ятаючи&nbsp; нелицемірну віру&nbsp; твою, як раніше була&nbsp; в&nbsp; бабусі&nbsp; твоєї Лоіди&nbsp; і в матері&nbsp; твоєї &nbsp;Евникії, впевнений, &nbsp;що&nbsp; вона&nbsp; і&nbsp; в&nbsp; тобі&raquo;.2 Тимофію. 1:5. Свідчить&nbsp; біблейський приклад&nbsp; про дар Божий через святого&nbsp; апостола Петра, якого послано до&nbsp; хворої&nbsp; благочинної діви Тавіфи (Сарни), яка&nbsp; трохи не померла&nbsp; від перевантаження&nbsp; у пошитті&nbsp; плащів. Любі&nbsp; сестри у Христі, щоб стати на правильний&nbsp; шлях істини, вам потрібно вивчити 2-е Послання до Тимофія, главу&nbsp; 1:5&nbsp; про&nbsp; подвиг старої&nbsp; благочестивої баби Лоіди&nbsp; у вихованні&nbsp;&nbsp; Тимофія, сина Евнікії.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З другої сторони листа ви будете мати відповідь на свої особисті питання і прохання. Їх дадуть наші волонтери які жертвують свій час на допомогу Вам. Переписуючись з Вами, дорогі сестри, вже майже 20 років, не знайшов нікого із сестер із Києва, щоб хотіли служити тим, що залишаються в цих стінах в покаянні. Мене цікавить Ваша думка з цього приводу. Чому нема нікого серед Вас, щоб помагати тим, що залишаються?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Наші умови залишаються незмінні і Ви їх знаєте,що допомогу надаємо тим хто має талант творення а саме малювання, вишивання, дар слова у поезії і оповідання. Також по можливості будемо допомагати ліками які замовленні рецептом санчастини. Ми будемо допомагати Вашим покинутим дітям до 14 років на Ваше прохання із вказаним точною адресою і описаною їхнім перебуванням і ця посилка буде ніби від Вашого імені.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З молитвою за Вас і Ваших дітей&nbsp; священик Ігор ОНИШКЕВИЧ, митрофорний&nbsp; протопресвітер &nbsp;і волонтери Церкви Святого Миколая Чудотворця&nbsp;&nbsp; на&nbsp; Аскольдовій Могилі. (Київ)з волонтерами і журналісткою рабою Божою Катериною</span></span></p>',NULL,2561,5,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1271,'P','2008-12-21',0,NULL,'Note','Молитва \"Ангел Господній\" з Папою Венедиктом XVI','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Це таїнство спасіння, окрім історичного, має ще й космічний вимір: Христос є сонцем благодаті, яке своїм промінням перемінює і запалює всесвіт, що в очікуванні&raquo;, - наголосив Папа Венедикт ХVІ, додаючи, що празник Різдва пов\'язаний із зимовим сонцестоянням, коли день, у північній півкулі, починає продовжуватися.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сьогодні, 21 грудня, перед проказуванням молитви &laquo;Ангел Господній&raquo; та уділенням Апостольського благословення усім тим, які прибулим на площу Святого Петра у Ватикані, Єпископ Риму звернувся до присутніх такими словами: &laquo;Дорогі брати та сестри, Євангелія цієї четвертої неділі Адвенту пропонує нам розповідь про Благовіщення (Лк.&nbsp;1,26-38), містерію, до якої щодня повертаємось, проказуючи &laquo;Ангел Господній&raquo;. Ця молитва дозволяє нам наново пережити вирішальний момент, у якому Бог постукав до серця Марії і, отримавши Її &laquo;так&raquo;, розпочав приймати тіло у Ній і від Неї&raquo;. Далі, пригадуючи зібраним про празник Різдва Христового, який невдовзі відзначатимуть християни, Святіший Отець звернув увагу присутніх на те, що кожен із нас є запрошеним зупинити свій погляд на великому таїнстві, яке зберігала Пресвята Богородиця продовж дев\'яти місяців у Своєму дівичому лоні, і призадуматись над воплоченням Божого Сина у людське тіло. Народження Бога у Вифлеємі є першою основною віссю спасіння, а другою є смерть і воскресіння Ісуса. Ці дві осі, за словами Вселенського Архиєрея, є нероздільними, вони виявляють один план Господній: спасти людство і його історію. <br /><br />&laquo;Це таїнство спасіння, окрім історичного, має ще й космічний вимір: Христос є сонцем благодаті, яке своїм промінням перемінює і запалює всесвіт, що в очікуванні&raquo;, - наголосив Папа Венедикт ХVІ, додаючи, що празник Різдва пов\'язаний із зимовим сонцестоянням, коли день, у північній півкулі, починає продовжуватися. У цьому контексті, Папа зазначив також і те, що площа Святого Петра є сонячним годинником: у ці дні тінь великого центрального обеліску є найдовшою протягом року. Це пригадує нам застосування астрономії в обрахуванні часу для молитви. Наприклад, молитва &laquo;Ангел Господній&raquo; проказується вранці, опівдні та ввечері. З нагоди того, що саме сьогодні, 21 грудня, о 12 годині, припало зимове сонцестояння, Папа привітав усіх тих, які в різний спосіб братимуть участь у заходах, присвячених всесвітньому року астрономії, що відзначатиметься наступного, 2009 року, і буде приурочений 4 сторіччю від перших телескопічних спостережень Галілео Галілея.<br /><br />Далі Святіший Отець заохотив вірних скерувати свій погляд до Пресвятої Богородиці та Її обручника Святого Йосифа, які очікують на народження Божого Сина, і навчитись від Них насолоджуватись радістю Різдва Христового.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,835,5,1,NULL,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1272,'P','2011-09-05',1,NULL,'Rss','Парафіяльний хор «Аскольд» у Коломиї','<p>&nbsp;З милості Божої, вже вкотре відбулась поїздка хору «Аскольд» до м. Коломиї. На празник Преображення Господнього, ми мали чудову нагоду разом зі священиками відслужити дві Божественні Літургії.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=784:parafijalny-hor-laskoldr-u-kolomyji&catid=25:parafija-cat&Itemid=27',2562,NULL,1,1,26),
(1273,'P','2011-09-14',1,NULL,'Note','Під покровом «футбольної» Богоматері','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Таємною  зброєю&raquo; поки що небоєздатної української  збірної на Євро&ndash;2012,  можливо, стануть знакова ікона та керамічні булави  від мешканців  прикарпатського Фитькова</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"http://umoloda.kiev.ua/img/content/i32/32423.gif\" alt=\"Рагулі: &laquo;Читачі &laquo;України молодої&raquo; першими бачать цей &laquo;Великий м&rsquo;яч&raquo;. \" width=\"165\" height=\"167\" /> <span lang=\"RU\"> </span></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Таємною зброєю&raquo; поки що небоєздатної української  збірної на Євро&ndash;2012, можливо, стануть знакова ікона та керамічні булави  від мешканців прикарпатського Фитькова</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Єврофутбольний креатив, що несподівано заструменів  із розташованого неподалік Надвірної невеликого підгірського села  Фитьків, де мешкає близько півтори тисячі осіб, у багатьох викликає  непоясниме здивування. Якщо ім&rsquo;я талановитого іконописця&ndash;самоука Василя  Стефурака, корінного, з діда&ndash;прадіда, фитьківчанина, відоме принаймні в  середовищі митців духовної &laquo;спеціалізації&raquo; (&laquo;УМ&raquo; розповідала про нього  чотири роки тому в матеріалі &laquo;Через горище &mdash; до Божественних вершин&raquo;),  то звідки ж тут узявся грузин Рагулі Власідзе? Тим часом його проекти зі  створення вболівальниками усіх країн&ndash;учасниць Євро&ndash;2012 килима розміром  у футбольне поле та стилізованих в українському стилі булав із кулями у  формі м&rsquo;яча для VIP&ndash;персон неодмінно стануть родзинками  довгоочікуваного свята європейського футболу.</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Через Москву &mdash; в Україну</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Рагулі &mdash; типовий грузин. Для нього, як і більшості співвітчизників,  освоєння орфоепії інших народів схоже на муки міфічного царя Тантала.  Неповторна суміш російських та українських слів, приправлена чистим  грузинським акцентом, у виконанні Власідзе породжує неймовірний суржик,  проте не створює жодних проблем для спілкування. Ідемо з Рагулі від  центру села до його старенького &laquo;скиту&raquo; на околиці, і кожного стрічного  фитьківчанина він вітає традиційним &laquo;Слава Ісусу Христу&raquo;. У відповідь &mdash;  доброзичливе &laquo;Навіки слава&raquo;.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Українці &mdash; я це зрозумів не відразу &mdash; дуже чуйний народ, але його на  історичних шляхах спіткало дуже багато страждань, &mdash; ділиться  спо&shy;стереженнями мій 55&ndash;річний співрозмовник. &mdash; Це видно по їхніх очах. У  них вони носять трагізм минулих часів. Мабуть, від цього &mdash; обережність і  сум, доброзичливість і недовіра. Та коли когось приймають у своє  середовище, ставляться, як до рідного. На собі відчув цю сердечність&raquo;.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Рагулі &mdash; професійний режисер. Закінчив Тбіліський театральний  інститут і встиг трохи попрацювати директором районного Будинку культури  в рідному Сурамі, де колись лікувалася Леся Українка. У горбачовські  часи навчався у Всесоюзному інституті кінематографії. Власне, з Москви  його перед розпадом СРСР і запросили до себе тернополяни. Відтоді мешкає  в Україні. Пише грузинською мовою. У перекладі на українську побачила  світ його збірка &laquo;Провінційне пекло&raquo;. Драми, що ввійшли до неї, за  оцінкою Богдана Бенюка, &laquo;вражають реалістичністю життя, що сплітається з  потойбіччям, проникаючи через серце у душу&raquo;.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Містичний простір Фитькова</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Старенька хата з невисокою стелею і дерев&rsquo;яними сволоками &mdash; теперішнє  Рагулеве пристанище. Тут він майже безвиїзно днює і ночує, хоча має  помешкання в Києві. Сюди потрапив випадково. &laquo;Це, &mdash; каже, &mdash; мабуть,  дарунок долі, бо Фитьків став для мене своєрідним містичним простором, у  якому вдалося створити чимало цікавих драматичних творів. Чому так,  залишається загадкою і для мене&raquo;.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Кілька років тому він узявся ретельно вивчати архівні матеріали про  національно&ndash;визвольну боротьбу українського народу в середині ХХ  століття, аби написати драму про головного провідника ОУН Степана  Бандеру. Тоді нагодився товариш Василь Бакота: &laquo;Що ти по готелях  поневіряєшся? У мене в селі є будинок, де раніше жила мама. Тепер він  вільний. Сподобається &mdash; живи і твори скільки завгодно&raquo;. Згодом свої  послуги запропонував молодий секретар місцевої сільради Дмитро Дмитрук &mdash;  також дав ключі від нинішньої оселі без будь&ndash;яких фінансових  зобов&rsquo;язань.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Усамітнення &mdash; добра нагода з головою занурюватися у творчий процес,  досягнути стану, коли зникає потреба контролювати час. &laquo;Іноді сплю  годину&ndash;півтори на добу і переживаю, що це &mdash; забагато, &mdash; зізнається  Рагулі. &mdash; Мої муки творчості бачить лише Матір Божа. Вона мене надихає і  захищає. Тому неймовірно зрадів, коли Василь Стефурак намалював її  ікону з футбольними атрибутами. Перед тим я дізнався, що у Фитькові  мешкає талановитий іконописець. Запросив його на горнятко кави.  Побачивши, що я вже встиг зробити до Євро&ndash;2012 і що планую, пан Василь  через тиждень приніс ікону&raquo;.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Експресивний Василь Стефурак створив образ Богородиці з Ісусом, котра  перстом правої руки вказує на стадіон, а лівою &mdash; на Сина Божого,  нагадуючи про духовні пріоритети. Як і слід було очікувати, &laquo;футбольне&raquo;  новаторство в іконографії неоднозначно сприйняло духовенство, хоча  ієрархи УГКЦ та Київського патріархату із розумінням поставилися до ідеї  фитьківських митців. &laquo;Єресі у цій картині не знайшов, тому прийшов  благословити задум двох ініціативних людей, &mdash; резюмував єпископ  Коломийсько&ndash;Чернівецький УГКЦ Миколай (Сімкайло). &mdash; Звичайно, для нас це  є щось нове і привертає увагу, але в цілому задум проекту є добрий&raquo;.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Булава як стимул</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Тепер ікона фитьківської Богородиці висить над ліжком Рагулі в  кімнаті&ndash;майстерні та надихає на нові єврофутбольні ініціативи. Нині  митець власноруч завершує роботу над циклом футбольних керамічних булав,  кожна з яких важить від п&rsquo;яти до семи кілограмів. Їх уже два десятки.  Серед тих, кому вони присвячені, &mdash; найвідоміші в Україні люди, і не  тільки пов&rsquo;язані з футболом. &laquo;Булави будуть урочисто вручені видатним  особистостям, але не зберігатимуться в їхніх приватних колекціях чи  коморах, &mdash; пояснює автор проекту. &mdash; Це &mdash; музейні експонати, і вони  повинні заохочувати кожного українця брати приклад зi знаменитостей,  котрі зробили щось значиме для цієї держави&raquo;.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Серед &laquo;домінантів&raquo; &mdash; Олег Блохін, Андрій Шевченко, брати Клички,  Альбіна Дерюгіна, Ліна Костенко, футбольні клуби донецький &laquo;Шахтар&raquo; та  київське &laquo;Динамо&raquo;. Особлива булава підготовлена для президента УЄФА  Мішеля Платіні. На ній кожен шип відповідає якомусь символові  української міфології, а на руків&rsquo;ї зображений козак&ndash;характерник з  атрибутами, що зміцнюють силу українського духу.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Булави Власідзе неповторні за задумом, проте однакові за технологією  виготовлення. Ущільнити час не вдалося &mdash; у глини свої закони, тому від  першого замісу до випалювання в печі та фінішного шліфування для кожної  булави знадобилося щонайменше два місяці. Скрупульозна, схожа на  ювелірну, робота вимагає терпіння і посидючості. Малорухомий спосіб  життя не минув безслідно: Рагулі незчувся, як набрав 12 наднормових  кілограмів ваги. Тепер зганятиме зайвий жирок &mdash; благо, що настає  динамічний етап реалізації проекту.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший пас із Ватикану?</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вивчаючи психологію європейських уболівальників, Рагулі зробив  висновок, що якраз через призму футболу вони найкраще можуть зрозуміти  національні традиції українського народу. Тому до свого проекту залучив  чимало талановитих майстрів народної творчості, передусім із Фитькова.  Приміром, Оксана Бакота вже понад два роки вишиває оригінальні  рушнички&ndash;шалики &mdash; народні сувеніри для гостей Євро&ndash;2012. На них &mdash;  символіка чемпіонату в обрамленні заворожуючих гуцульських та  бойківських візерунків.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Менш як за три місяці стануть відомі всі футбольні збірні, які  візьмуть участь у Євро&ndash;2012. Тоді за м&rsquo;яч &mdash; у прямому сенсі &mdash; візьметься  Рагулі з однодумцями. &laquo;У всіх цих країнах, &mdash; ділиться планами, &mdash; ми  хочемо провести фестивалі футболу під назвою &laquo;Великий м&rsquo;яч&raquo;. Його  спеціально виготовили в Харкові, й читачі &laquo;України молодої&raquo; першими  бачать цю унікальну шкіряну кулю. Об&rsquo;єднати вболівальників, між якими не  повинно бути ворожнечі, допоможе також величезний килим, розміром у  класичне футбольне поле, який піднімуть над стадіоном під час відкриття  європейської першості. Кожен бажаючий зможе зав&rsquo;язати на ньому свій  іменний вузлик. Усього їх буде 2 мільйони. Ми розробили програму для  інтернету, і кожен учасник, маючи свій персональний номер, знатиме всіх  близьких і далеких сусідів по килиму&raquo;.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перша гра &laquo;Великого м&rsquo;яча&raquo; запланована у Ватикані, на площі Святого  Петра &mdash; так драматург Рагулі Власідзе хвацько закрутив інтригу довкола  міжнародного футбольного фестивалю. Між іншим, чимало його ідей уже  підтримали урядовці, відповідальні за проведення в Україні Євро&ndash;2012.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Іван КРАЙНІЙ<br />http://umoloda.kiev.ua/number/171/0/69130/<br /></span></span></p>',NULL,2569,5,1,NULL,27);
