INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(1309,'P','2008-12-25',1,NULL,'Note','Різдвяне привітання Архипископа-Митрополита Львівського латинського обряду Мечислава Мокшицького','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Святкуючи разом з усіма, хто вірить в день Христа, коли Він - джерело Життя, виконуючи одвічний замисел Божої любові, з\'явився нам в людській натурі, я бажаю, щоби надія, яка пливе з цих радісних подій, пронизувала кожен момент життя і дії, засвідчуючи про Божу доброту до кожної людини!<br /></span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З нагоди Різдва Христового за григоріянським календарем, Голова Конференції Римо-Католицьких Єпископів України Архиєпископ-Митрополит Львівський Високопреосвященний Мечислав Мокшицький звернувся з привітанням до слухачів Української програми Ватиканського радіо.<br /><br />&laquo;Мої дорогі!<br />Святкуючи разом з усіма, хто вірить в день Христа, коли Він - джерело Життя, виконуючи одвічний замисел Божої любові, з\'явився нам в людській натурі, я бажаю, щоби надія, яка пливе з цих радісних подій, пронизувала кожен момент життя і дії, засвідчуючи про Божу доброту до кожної людини!<br />Нехай Різдво Христове і увесь 2009 Рік принесуть ласку відновлення в послуху за натхненням Святого Духу, щоби ми могли принести наше життя на славу Божу і &laquo;щоби наша радість була повною&raquo; (пор. Ів. 16:24).<br />Христос Раждається!&raquo;<br /><br />24 грудня, у Навечір\'я Різдва Христового, о 18 год. за київським часом, у Львівській митрополичій базиліці Успіння Пресвятої Діви Марії розпочнеться Вігілійна Служба Божа - Пастирка, яку очолить Архиєпископ-Митрополит Львівський Високопреосвященний Мечислав Мокшицький. Це Богослужіння буде транслювати Львівське державне телебачення. <br /><br />Для Радіо Ватикану, Костянтин Чавага</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span><strong><em><br /></em></strong></p>',NULL,853,5,1,NULL,42),
(1310,'P','2008-12-25',1,NULL,'Rss','Відкриття різдвяного вертепу на площі Святого Петра','<p><img src=\"http://www.oranta.org/images/stories/2008/11/foto_01.jpg\" style=\"float: left;\" /> На бажання Слуги Божого Папи Івана Павла ІІ, вже від 1982 року площу Святого Петра у Ватикані прикрашає різдвяний вертеп, за розмірами він сягає двоповерхового будинку. Традиція ватиканських вертепів збережена і теперішнім Папою Венедиктом ХVІ. Деякі статуї цьогорічного різдвяного дійства, що прикрашає ватиканську площу, походять із вертепу Святого Вінченцо Паллотті, що зберігається у базиліці Святого Андреа делле Валле і сягає 1842 року. Інші, натомість, були додані до згаданого вертепу продовж пізніших років. Згідно традиції, Різдво Христове супроводжується присутністю води та вогню, які символізують душевну чистоту і відновлення та перемогу світла над темрявою; ці два елементи присутні також і цього року у вертепі на площі Святого Петра.</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=159:vidkryttja-rizdvjanogo-vertepu-na-ploshchi-svjatogo-petra&catid=32:gk-svit-cat&Itemid=58',854,NULL,1,1,26);
