INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(2886,'P','2011-04-20',1,NULL,'Rss','У Страсний четвер Блаженніший Святослав уперше здійснить Чин омивання ніг ','<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/9d84f8fccc.jpg\" height=\"217\" width=\"150\" /></div>\n<p>У Страсний (Чистий) четвер, 21 квітня, Глава Української  Греко-Католицької Церкви Блаженніший Святослав (Шевчук), очолить Вечірню  з Літургією Василія Великого та вперше здійснить Чин омивання ніг  дванадцятьом священикам. Урочистості проходитимуть у Патріаршому соборі  Воскресіння Христового у Києві (<em>вул.</em><em> Микільсько-Слобідська, 5</em>). <strong>(Літургія розпочнеться о 7-00, Чин омивання ніг – 9-00).</strong></p>\n<p>«Першим жестом – який ми звершуємо у катедральних храмах під час чину  вмивання ніг саме у Великий четвер – Христос у видимий і зворушливий  спосіб упокорює себе перед власними учнями і вказує їм на те, що Він є  серед них як слуга. … Таким чином Спаситель навчає нас, що Його  священство не є запорукою людського владарювання чи привілеєм. <em>Бути священиком – означає бути для всіх слугою</em>,  бути тим, хто щодня обмиває людську нечистоту, тобто служить ближньому  за прикладом свого Господа», - наголошує Блаженніший Святослав у <a href=\"http://www.ugcc.org.ua/1765.0.html\">Посланні</a> до священнослужителів УГКЦ .</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=729:u-strasny-chetver-blazhennishy-svjatoslav-upershe-zdisnyt-chyn-omyvannja-nig-&catid=27:gk-v-ukrajini-cat&Itemid=57',2415,NULL,1,1,26),
(2887,'P','2011-04-20',1,NULL,'Rss','Символіка великоднього кошика','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://www.kyiv.ugcc.org.ua/img/upload/koshyk.jpg\" height=\"141\" width=\"187\" /></strong>Свято Христового Воскресіння наповнене радісними звичаями: писання писанок, ведення гаївок та освяченням продуктів великоднього кошика. Останнє має глибокий духовно-релігійний зміст та є складовою літургійного дійства. У кошику кожен продукт щось символізує та означає, вказує на глибокий зв\'язок між матеріальною і духовною реальністю, взаємовідносини творіння із Творцем.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=293:symvolika-velykodnogo-koshyka&catid=30:hrystyjanske-vchennia-cat&Itemid=37',2416,NULL,1,1,26),
(2888,'P','2011-04-20',1,NULL,'Rss','Історія Пасхи','<p>Зі Святого письма черпаємо відомості про старозавітнє святкування Пасхи. Слово «пасха» пішло від єврейського - «песах»,  що у перекладі означає - «перехід».</p>\n<p>Понад чотириста років ізраїльський народ страждав від єгипетської неволі. Господь промовив до Мойсея на горі Хорив, через палаючий кущ і поклав на нього місію вивести свій народ з неволі.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=292:istorija-pashy&catid=30:hrystyjanske-vchennia-cat&Itemid=37',2417,NULL,1,1,26),
(2889,'P','2011-04-20',1,NULL,'Rss','Молодіжна Хресна Дорога на схилах Дніпра','<p align=\"right\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3892,200,IMG_3655.JPG\" />«Вкажи мені , Боже, той шлях, ту Пречисту дорогу до Тебе, аби стати ближче до Тебе. Бо ніде так не є нам безпечно, як з Тобою, Господи!»<em> (із вступу до Хресної Дороги)</em></p>\n<p>Вже традицією стало, в часі Великого посту,&nbsp; молодіжною спільнотою церкви Миколая Чудотворця&nbsp; проводити Хресну Дорогу. Цього року окрім Хресної Дороги в храмі її провели і на схилах Дніпра.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=730:molodizhna-hresna-doroga-na-shylah-dnipra&catid=25:parafija-cat&Itemid=27',2418,NULL,1,1,26);
