INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(1836,'P','2009-03-12',1,NULL,'Rss','Пам‘яті Слави Стецько','<p><img src=\"http://www.oranta.org/images/stories/2008/10/image13711dsc_0018.jpg\" style=\"float: left; margin-right: 8px; border: #000 1px solid;\" />12 березня річниця пам‘яті Слави Стецько.</p>\n<p>Спочила Слава цього ж дня 2003 року. Відспівували її на Аскольдовій Могилі, бо вона себе вважала нашою парафіянкою. Кожен раз коли була в Києві приходила на недільну дитячу службу Божу. сідала на свою лавочку і діти оточували її. Вона дуже любила спілкуватись з дітьми. Особливу увагу Слава Стецько приділяла найменшим, брала їх на руки, навчала, забавляла.</p>\n<p> Саме Слава Стецько допомагала і добивалася відновлення нашої святині - Аскольдової Могили. З благословення Блаженнішого Любомира Гузара 1998 року 22 травня вона перша відкрила двері церкви і промовила слово до громади.</p>\n<p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=242:pamjati-slavy-stecko&catid=25:parafija-cat&Itemid=27',1230,NULL,1,26),
(1837,'P','2013-12-11',1,NULL,'Rss','Обмін (12 грудня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5336,200,1347891.jpg\" />П’ятирічна дівчинка не давала спокою батькові, наполегливо випитуючи, що він купить їй, коли поїде до міста. Нарешті батько втратив терпіння і з докором сказав:</p>\n<p>- Купи мені це, купи мені те! Ти думаєш тільки про те, що мають тобі дати твої батьки. А ти, що ти даєш нам?</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1680:obmin-12-grudnja&catid=1:mystecka-storinka-cat&Itemid=50',3854,NULL,1,26),
(1838,'P','2013-12-15',1,NULL,'Rss','Колір велосипеда (16 грудня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5345,200,mn1%2829%29.jpg\" />Одна дівчинка вирішила наново пофарбувати свого велосипеда.</p>\n<p>Обрала колір зелений, бо змалечку дуже його любила. Одначе старший брат сказав їй на те:</p>\n<p>- В житті не бачив зеленого велосипеда! Пофарбуй-но краще червоною фарбою. Буде класно!</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1684:kolir-velosypeda-16-grudnja&catid=1:mystecka-storinka-cat&Itemid=50',3863,NULL,1,26),
(1839,'P','2009-01-20',1,NULL,'Note','Школа-інтернат для неповносправних дітей у Потіївці','<p style=\"text-align: justify;\"><img style=\"float: left;\" src=\"http://www.oranta.org/images/stories/2008/10/d1.jpg\" alt=\"\" /><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Парафіяльна служба милосердя церкви св. Миколая Чудотворця на Аскольдовій Могилі, разом з друзями з Америки - паном Еріком та його командою. відвідали інтернат для неповносправних дітей у селі Потіївка, Житомирської області. У часі канікул у інтернаті залишилось 13-ро дітей повних сиріт, яким немає куди і до кого поїхати у ці величні свята. Наш візит для цих дітей став великою подією і святом у цей Богом даний час.</span></span></p>','<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Фото: Іллі <span lang=\"RU\">&laquo;Оранта&raquo;</span></span></span></p>\r\n<p class=\"MsoNormal\"><span lang=\"RU\"><img style=\"float: right;\" src=\"image,2205,200,IMG_6665.jpg\" alt=\"\" width=\"200\" height=\"150\" /><img style=\"float: left;\" src=\"image,2205,200,IMG_6646.jpg\" alt=\"\" /></span></p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\"><span lang=\"RU\"><img style=\"float: left;\" src=\"image,2205,200,IMG_6654.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,2205,200,IMG_6652.jpg\" alt=\"\" /></span></p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\"><span lang=\"RU\"><img style=\"float: left;\" src=\"image,2205,200,IMG_6656.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,2205,200,IMG_6658.jpg\" alt=\"\" /></span></p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<p class=\"MsoNormal\"><span lang=\"RU\"><img style=\"float: left;\" src=\"image,2205,200,IMG_6670.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,2205,200,IMG_6671.jpg\" alt=\"\" /><br /></span></p>',NULL,NULL,1231,5,1,49);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(1840,'P','2009-03-12',1,NULL,'Rss','«Рух подружніх пар» на Аскольдовій Могилі','<p><img src=\"http://www.oranta.org/images/stories/2008/10/001.jpg\" style=\"margin-right: 8px; float: left;\" /></p>\n<p>Сьогодні у підтримку «Руху подружніх пар» Блаженніший Любомир у церкві св. Миколая Чудотворця на Аскольдовій Могилі о 17 годині відправив Божественну літургію для подружжів. Служба пройшла у спів служінні священиків: отця Олексія Геник, керівника «Руху подружніх парк УГКЦ», декана, отця Ігоря Онишкевича, отця Богдана Чурила, і отця Олега, секретаря Блаженнішого.</p>\n<p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=243:lruh-podruzhnih-parr-na-askoldovi-mogyli&catid=25:parafija-cat&Itemid=27',1232,NULL,1,26),
(1841,'P','2009-01-30',1,NULL,'Note','Спільна Коляда','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,2207,200,DSC_0026.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,2207,200,DSC_0030.JPG\" alt=\"\" />На завершення Різдвяних свят наші парафіяни цьогоріч провели спільну коляду, разом смакували куттею і востаннє споглядали вертепівське дійство.</span></span></p>','<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Фото: Ігоря Андрійовича Савицького</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,2207,200,DSC_0037.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,2207,200,DSC_0053.JPG\" alt=\"\" /></span></span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,2207,200,DSC_0087.JPG\" alt=\"\" width=\"200\" height=\"133\" /><img style=\"float: right;\" src=\"image,2207,200,DSC_0144.JPG\" alt=\"\" /></span></span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,2207,200,DSC_0166.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,2207,200,DSC_0213.JPG\" alt=\"\" width=\"200\" height=\"133\" /></span></span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,2207,200,DSC_0018.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,2207,200,DSC_0210.JPG\" alt=\"\" /><br /></span></span></p>',NULL,NULL,1233,5,1,49),
(1842,'P','2013-12-16',1,NULL,'Rss','Спілкування (17 грудня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5345,200,19b5e2.jpg\" />У  найфешенебельнішому ресторані одного міста якась пані замовила собі  спаржеву юшку - фірмову страву цього ресторану. Вже за кілька хвилин  послужливий офіціянт поставив перед жінкою тарілку з юшкою, що парувала і  пахла дуже смачно. По тому вклонився й відійшов.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1685:spilkuvannja-17-grudnja&catid=1:mystecka-storinka-cat&Itemid=50',3867,NULL,1,26),
(1843,'P','2013-12-21',1,NULL,'Note','ХІІ фестиваль-конкурс Аскольдів Глас','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5358,200,546.jpg\" alt=\"\" width=\"136\" height=\"200\" />ХІІ ФЕСТИВАЛЬ-КОНКУРС ДУХОВНОЇ ПІСНІ&nbsp;<strong> &laquo;АСКОЛЬДІВ ГЛАС&raquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>21 грудня 2013 року &nbsp;о 16:30 Національний педагогічний університет ім. М. Драгоманова (м. Київ, вул. Пирогова, 9)</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Організатор фестивалю &ndash; УГКЦ св. Миколая Чудотворця на Аскольдовій Могилі</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Радісно, що з кожним роком концерти &laquo;Аскольдового гласу&raquo; все більше привертають увагу українського суспільства, інтелігенції, вірян різних церков та&nbsp; обдарованих виконавців.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,5358,200,546.jpg\" alt=\"\" width=\"136\" height=\"200\" /></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong><em>ХІІ Фестиваль-конкурс духовної пісні &laquo;Аскольдів глас&raquo;</em></strong></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Мистецький захід започаткований у 2007 році з ініціативи митрофорного протоієрея отця Ігоря Онишкевича та громади УГКЦ св.&nbsp;Миколая Чудотворця на Аскольдовій могилі, проводиться двічі на рік, має екуменічний характер. Метою конкурсу є популяризація найкращих зразків духовної, патріотичної, народно-обрядової музики, ознайомлення українського суспільства з історичними подіями минувшини, постаттю київського князя Аскольда, залучення до участі знакових співацьких колективів з усієї України та з-поза її меж.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Серед учасників конкурсу &laquo;Аскольдів глас&raquo; 2013 року &ndash; вокальні та вокально-інструментальні ансамблі із Черкащини, Вінничини, Львівщини, Івано-Франківщини, Київщини.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">В концертному виступі кожного колективу прозвучать два твори: перший, присвячений постаті київського князя Аскольда (в хрещенні названого Миколаєм) та другий із жанру духовної, патріотичної чи народно-обрядової музики. Виступи конкурсантів оцінює журі, до складу якого увійшли видатні діячі сьогодення. За майстерністю сценічного виконання, змістом обраних творів та музично-виконавським рівнем журі визначить переможців конкурсу, яких буде нагороджено грошовими преміями та&nbsp; подарунками.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Радісно, що з кожним роком концерти &laquo;Аскольдового гласу&raquo; все більше привертають увагу українського суспільства, інтелігенції, вірян різних церков та&nbsp; обдарованих виконавців.</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>Журі конкурсу:</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Заслужений діяч мистецтв України, Володар премії Президента України Гран-прі &laquo;Пісенний вернісаж 1998&raquo;, лауреат премії ім. Л.&nbsp;Костенко</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>Ірина Кириліна</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">(голова журі)</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Диригент студії&nbsp; при Національному заслуженому академічному українському народному хорі ім. Г. Верьовки, викладач</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>Володимир Федас</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Доктор філологічних наук, доцент кафедри української мови Національного університету \"Києво-Могилянська академія\"</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>Орися Демська</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Лауреат авторської пісні та співаної поезії, ведуча, правознавець</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>Наталя Жеміна</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Керівник Бюро УГКЦ для зв&rsquo;язків з органами державної влади,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">о.&nbsp;Роман (Небожук)</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Міс-Україна 2013 &ndash; <strong>Анна Заячківська</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong><em>Програма конкурсу</em></strong></span></p>\r\n<table style=\"width: 417px; height: 1967px;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"31\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">&nbsp;</span></p>\r\n</td>\r\n<td valign=\"top\" width=\"437\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong><em>Відкриття свята</em></strong></span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>&laquo;Михайлівські дзвони&raquo; - сл. Бориса Олійника, муз. Ірини Кириліної. </strong></span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong><em>Виконують заслужений діяч мистецтв України</em></strong><strong><em> Ірина Кириліна </em></strong><strong><em>та лауреат міжнародних конкурсів </em></strong><strong><em>Руслана Лоцман</em></strong><strong><em>.</em></strong></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"31\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">&nbsp;</span></p>\r\n</td>\r\n<td valign=\"top\" width=\"437\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong><em>Привітальне слово </em></strong><strong><em>реколектанта о.&nbsp;Віталія</em></strong></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"31\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">1.</span></p>\r\n</td>\r\n<td valign=\"top\" width=\"437\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>Дитячий фольклорний ансамбль &laquo;ВІНОЧОК&raquo; </strong></span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">(с.&nbsp;Сигнаївка, Черкаська обл.) Керівник &ndash; Тамара Лоцман</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">1. &laquo; Балада про Аскольда &raquo; - сл. і муз. Руслани Лоцман;</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">2. &laquo;В&rsquo;язанка колядок і щедрівок&raquo;.</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"31\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>2.</strong></span></p>\r\n</td>\r\n<td valign=\"top\" width=\"437\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>Гурт &laquo;ВЕСЕЛЯТА&raquo;</strong> (м.&nbsp;Івано-Франківськ)</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Керівник &ndash; Зорина Валіхновська</span></p>\r\n<pre><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">1. &laquo;Славний Аскольд&raquo; - сл. і муз. Зорини Валіхновської;</span></pre>\r\n<pre><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">2. &laquo;Маріямпільська Богородиця&raquo; - сл. Віри Багірової, муз. Зорини Валіхновської.</span></pre>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"31\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>3.</strong></span></p>\r\n</td>\r\n<td valign=\"top\" width=\"437\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">&nbsp;<strong>Вокальна група хору &laquo;СВІТАНОК&raquo;</strong> (м. Самбір)</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Керівник &ndash; Леся Мигалик</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">1. &laquo;Аскольде князю, дякуєм тобі&raquo; - сл. та муз. Олени Сенейко;</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">2. &laquo;Пісня наша дзвенить&raquo; - сл. і муз. Богдана&nbsp; Косопуда.</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"31\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>4.</strong></span></p>\r\n</td>\r\n<td valign=\"top\" width=\"437\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>Гурт &laquo;</strong><strong>Esthetic</strong><strong>voice</strong><strong>&raquo; (м. Київ)</strong></span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Керівник &ndash; Вадим Яценко</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">1. &laquo;Не давнина, а вічність&raquo; - сл.&nbsp;Олени &nbsp;Кулінської, муз.&nbsp;Світлани&nbsp;Острової, аранж. Вадима Яценка;</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">2. &laquo;Ой чий то кінь стоїть&raquo; - обр. укр. нар. пісні О.&nbsp;Токара.</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"31\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>5.</strong></span></p>\r\n</td>\r\n<td valign=\"top\" width=\"437\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>Фольклорний гурт &laquo;МОКОША&raquo; </strong>Вінницького училища культури і мистецтв ім. М.Д.&nbsp;Леонтовича (м.&nbsp;Вінниця)</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Керівник &ndash; Юлія Васюк</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">1. &laquo;До Аскольда&raquo; - вокальна епічна поема на вірші та музику</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Яни Шлябанської;</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">2. &laquo;Через поле широкеє&raquo; - кант в обр. Миколи Леонтовича.</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"31\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>6.</strong></span></p>\r\n</td>\r\n<td valign=\"top\" width=\"437\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>Молодіжний вокальний ансамбль &laquo;ЗАХІД&raquo; </strong>(м.&nbsp;Київ)</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Керівник &ndash; Ярина Чепіга</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>1.</strong> &laquo;Аскольдів Глас&raquo; - сл. Олени Сакалюк, муз. Роксолани Шпіци, аранж. Ярини Чепіги;</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">2. &laquo;Мати Милосердя&raquo; - сл. і муз. Марії Слєпченко, обр. Ганни Гаврилець.</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"31\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>7.</strong></span></p>\r\n</td>\r\n<td valign=\"top\" width=\"437\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>Вокальна група</strong> &ldquo;<strong>ЧОРНОБРИВЦІ&rdquo;</strong> Львівського мистецького центру &rdquo;Веселі черевички&rdquo; (м.&nbsp;Львів). Керівник &ndash; Василь Чучман</span></p>\r\n<p align=\"left\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">1. &laquo;У день святого Миколая&raquo; -&nbsp; сл. Руслани Лоцман, муз. Ірини Кириліної, аранж. Василя Чучмана;</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">2. &laquo;Тешуть теслі&raquo; - сл. Богдана-Ігоря Антонича, муз. Василя Жданкіна.</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"31\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>8.</strong></span></p>\r\n</td>\r\n<td valign=\"top\" width=\"437\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>Музична формація &laquo;</strong><strong>K</strong><strong>_</strong><strong>CORNFLOWER</strong><strong>&raquo; </strong>(м. Київ)</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">1. &laquo;Аскольдова звитяга&raquo; - сл. Христини Шонь, муз. Sam Watters;</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">2. &laquo;Шумить і гуде&raquo; - сл. народні, муз. <a title=\"Jesse Harris\" href=\"http://en.wikipedia.org/wiki/Jesse_Harris\">Jesse Harris</a>.</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"31\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>9. </strong></span></p>\r\n</td>\r\n<td valign=\"top\" width=\"437\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>Вокальний ансамбль &laquo;Кант&raquo;</strong> Львівського державного&nbsp; училища культури і мистецтв<strong>&nbsp; </strong>(м.&nbsp;Львів)</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Керівник &ndash; Мирон Дацко</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">1. &laquo;Як вабить спокоєм&raquo; - сл. Олени Кулінської, муз. Мирона Дацка;</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">2. &laquo;Коляда &ndash; колядка - сл. і муз. Лілії Кобільник.</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"31\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>10.</strong></span></p>\r\n</td>\r\n<td valign=\"top\" width=\"437\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>Вокальний ансамбль &laquo;ЖУРАВЛИНА&raquo;</strong> (м.&nbsp;Київ)</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Керівник - Ірина Петрівна Жданова</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">1.&laquo;Боже хрещення України&raquo; - сл. Тетяни Майданович, муз. Людмили Левченко-Бутуханової;</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">2. &laquo;Арія краси&raquo; - сл. Зої Дикої, муз. Ірини Кириліної.</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"31\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>11.</strong></span></p>\r\n</td>\r\n<td valign=\"top\" width=\"437\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>Вокальний ансамбль старовинної музики &laquo;АЛІТЕЯ&raquo; </strong>(м. Київ)</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Керівник - Жанна Гончаренко</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">1. &laquo;Дума про Аскольда&raquo; - сл. Василя Довжика, муз. Олени Лис;</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">2. Партесний концерт невідомого українського композитора</span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">XVII сторіччя &nbsp;&laquo;Тебе одіющагося світом&raquo;.</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"31\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></p>\r\n</td>\r\n<td valign=\"top\" width=\"437\">\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><em>Проведення благодійного аукціону на підтримку дітей-сиріт та напівсиріт Дитячого будинку с. Кропивна Золотінського р-ну Черкаської обл.</em></span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong><em>Виступ лауреата Міжнародних та Всеукраїнських конкурсів </em></strong><strong><em>Оксани Сливки.</em></strong></span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong><em>Нагородження учасників конкурсу.</em></strong></span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Ведучі свята &ndash; </em></strong><strong>Мирослава Козачок</strong> та<strong> В&rsquo;</strong><strong>ячеслав Матяш</strong><strong><em>&nbsp;</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Голова оргкомітету&nbsp;&nbsp; </em>Чаплик Катерина tel.: &nbsp;066&nbsp;447 80 99</span></p>',NULL,NULL,3875,5,1,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(1844,'P','2013-12-19',1,NULL,'Rss','Бюрократія (20 грудня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5356,200,3227512-lg.jpg\" />Суддя Верховного суду сидів собі якось на бе­резі річки, коли до нього підійшов подорожний і запитав:</p>\n<p>– Чи можна скористатися цим човном? Мені треба переплисти на другий берег ріки.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1688:bjurokratija-20-grudnja&catid=1:mystecka-storinka-cat&Itemid=50',3876,NULL,1,26),
(1845,'P','2009-03-14',1,NULL,'Note','Блаженніший Любомир відповів на запитання читачів \"Кореспондента\" ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Читачі журналу ставили свої запитання Предстоятелеві УГКЦ в рамках спільного проекту журналу \"Кореспондент\" і сайту \"Кореспондент.net\". Як повідомляє \"Кореспондент.net\", мета проекту - дати можливість читачам сайту поставити питання відомим політикам, громадським діячам, зіркам шоу-бізнесу і бізнесменам. Відтак з усього переліку запитань редакція вибрала найкращі, які й поставила Блаженнішому Любомирові.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\"Таке рішення (про перенесення осідку Глави УГКЦ зі Львова до Києва, - <em>авт.</em>) виглядало б нелогічним, якби всі греко-католики були лише на заході України. Я був у Донецьку і побачив, що і там є греко-католики, є і в Сумах, в Криму. Греко-католики були вигнані з тієї частини України, але вони туди повернулися\", - сказав Блаженніший Любомир, Глава УГКЦ, відповідаючи на запитання читачів українського щотижневого журналу \"Кореспондент\". </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Читачі журналу ставили свої запитання Предстоятелеві УГКЦ в рамках спільного проекту журналу \"Кореспондент\" і сайту \"Кореспондент.net\". Як повідомляє \"Кореспондент.net\", мета проекту - дати можливість читачам сайту поставити питання відомим політикам, громадським діячам, зіркам шоу-бізнесу і бізнесменам. Відтак з усього переліку запитань редакція вибрала найкращі, які й поставила Блаженнішому Любомирові.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Потрібно зазначити, що питання про перенесення осідку було поставлене декількома авторами. Також воно активно обговорювалося у коментарях на \"Кореспондент.net\". \"Ми не перенесли осідок Глави УГКЦ, а ми повернулися туди, де ми були колись\", - продовжив свою відповідь Блаженніший Любомир. За його словами, до початку XIX століття греко-католицький Київський митрополит був у Києві. А коли російська імперія практично повністю зайняла Україну, вона послідовно позбувалася греко-католиків на своїх землях. \"Але коли настав більшовицький режим, з різних причин греко-католики повернулися у східні, центральні та південні області України. І наш обов\'язок - їм послужити\", - пояснив Предстоятель УГКЦ. \"Київ - центр держави, і всі великі церковні організації мають тут свої головні представництва. Природно, що й УГКЦ головний свій осідок має у столиці. Греко-Католицька Церква - це не західноукраїнська, а всеукраїнська дійсність\", - наголосив Блаженніший Любомир.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На сторінках журналу можна також ознайомитися з відповідями Глави УГКЦ про імовірність об\'єднання традиційних християнських Церков, про політичну дійсність та про те, чи не мріяв він стати Папою Римським. Крім того є відповіді на запитання про ставлення УГКЦ до занять йогою та про теорію Дарвіна.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Департамент інформації УГКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>',NULL,NULL,1236,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(1846,'P','2013-12-24',1,NULL,'Rss','Дівчинка і вовк (24 грудня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5365,200,24-Opora1.jpg\" />Одного дня пополудні великий вовк чекав у темному лісі на дівчинку, що мала проходити з кошичком харчів для бабусі. Маленька дівчинка із кошичком харчів і справді надійшла.</p>\n<p>— Несеш цей кошичок для бабусі? — спитав вовк.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1725:divchynka-i-vovk-24-grudnja&catid=1:mystecka-storinka-cat&Itemid=50',3885,NULL,1,26),
(1847,'P','2013-12-25',1,NULL,'Rss','Мертвий чи живий? (26 грудня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5372,200,1483352.jpg\" />Онук великого вченого прийшов до свого дідуся. За плечима у руці хлопчина стискав пташку, яку зловив у саду.</p>\n<p>Хитро поглядаючи на дідуся, він спитав:</p>\n<p>– Горобчик, якого я маю, живий чи мертвий?</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1694:mertvy-chy-zhyvy-26-grudnja&catid=1:mystecka-storinka-cat&Itemid=50',3890,NULL,1,26),
(1848,'P','2014-01-19',0,NULL,'Note','РОЗКЛАД РІЗДВЯНИХ БОГОСЛУЖІНЬ у церві св. Миколая Чудотворця на Аскольдовій Могилі УГКЦ','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,5378,200,404948.jpg\" alt=\"\" width=\"200\" height=\"133\" /></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>19 січня 2014 р. Б. (НЕДІЛЯ)<br /></strong></span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Богоявлення Господнє</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">8.00 &ndash; Свята Літургія, Йорд. водосвяття</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">10.00 &ndash; Свята Літургія</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">12.30 &ndash; Свята Літургія для дітей</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У Новому 2014 Році Божому рожденне Дитятко Ісус, Бог і Спаситель наш, нехай благословить нашу Україну і всіх нас! Божих ласк у вірі, надії та любові!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>31 грудня 2013 р. Б. (</strong><strong>ВІВТОРОК</strong><strong>)</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">8.00 &ndash; Свята Літургія</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">22.00 &ndash; Подячна Свята Літургія, Новорічний молебень</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>6 січня 2014 р. Б. (ПОНЕДІЛОК)</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Надвечір&rsquo;я Різдва Господнього</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">8.00 &ndash; Царські Часи</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">9.00 &ndash; Вечірня з Літургією Василія Великого</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">17.00 &ndash; Велике Повечір&rsquo;я</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>7 січня 2014 р. Б.&nbsp; (ВІВТОРОК)</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Різдво Господа нашого Ісуса Христа</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">8.00 &ndash; Утреня</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">9.30 &ndash; Урочиста Свята Літургія</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">11.00 &ndash; початок Парафіяльної Коляди</span></p>