INSERT INTO `publications_publication` VALUES
(4621,'P','2009-01-05',1,NULL,'Rss','Традиції надвечір’я Христового Різдва','<p><img style=\"margin: 4px 3px; float: left;\" src=\"http://www.risu.org.ua/uimg/?f=10887.jpg&amp;s=-1&amp;b=yes&amp;bc=000000\" alt=\"Традиції надвечір\'я Христового Різдва\" height=\"180\" width=\"240\" />Свято Христового Різдва належить до найбільших християнських свят, які Церква відзначає особливо урочисто. Кульмінацією святкового приготування є надвечір\'я Різдва - день чування, молитви й посту. Святвечір має не тільки глибокі змістом церковні відправи, але передусім він багатий у нашому народі на cповнені символіки обряди і звичаї, деякі з яких сягають ще дохристиянських часів, розповідає <strong>Юлія КОЦАН</strong>.</p>\n<p align=\"right\">«Час нашого спасення вже настав.  <br /> Готуйся, вертепе, бо Діва надходить, щоб родити» <br /> (Стихира вечірні надвечір\'я)</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=174:tradyciji-nadvechirja-hrystovogo-rizdva&catid=28:tema-nomera-cat&Itemid=2',1911,NULL,1,1,26),
(4622,'P','2009-01-05',1,NULL,'Rss','У ніч з 6 на 7 січня українці святкують Різдво Христове','<p><img style=\"margin: 4px 3px; float: right;\" src=\"http://www.risu.org.ua/uimg/?f=10889.jpg&amp;s=-1&amp;b=yes&amp;bc=000000\" alt=\"У ніч з 6 на 7 січня більшість українців святкують Різдво Христове\" height=\"200\" width=\"200\" /><em>УКРАЇНА</em> - Різдво Христове належить до найбільших християнських, так званих дванадесятих свят, які Церква відзначає особливо урочисто. Це свято народження Ісуса Христа - незвичайної події, що від неї весь християнський світ веде своє літочислення. Усі українські Православні Церкви, Українська Греко-Католицька Церква, більшість українських протестантів, а в світі Єрусалимська, Російська і Сербська Православні Церкви святкують Різдво у ніч з 6 на 7 січня. Цей день є офіційним державним святом в Україні.</p>\n<p>Згідно з Євангелієм Ісус Христос народився в часи правління імператора Августа в місті Вифлеємі. Богородиця та Йосип Обручник з Назарету прибули до Вифлеєму для участі в перепису населення. Через відсутність місця в готелі родина була змушена переночувати в печері, що використовувалася як хлів для укриття худоби від непогоди. Під час народження Христа над печерою зійшла Вифлеємська зірка, яка вказала про цю подію трьом волхвам (мудрецям). Першими прийшли поклонитися Христу пастухи, яким радісну звістку сповістив янгол, потім волхви. Волхви піднесли Христу дарунки - золото, ладан і миро.</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=173:u-nich-z-6-na-7-sichnja-ukrajinci-svjatkujut-rizdvo-hrystove&catid=27:gk-v-ukrajini-cat&Itemid=57',1910,NULL,1,1,26),
(4623,'P','2009-01-05',1,NULL,'Note','Відзначення 100-го ювілею','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1 січня 2009 року в Івано-Франківську відзначили 100-ій Ювілей з дня народження провідника України - Степана Бандери. Спочатку урочистості відбулися в рідному селі Степана Бандери - Старому Угринові, поблизу міста Івано-Франківська, де він народився, зростав та виховувався в священичій родині Греко-Католицького священика.</span></span></p>','<p style=\"text-align: justify;\"><img style=\"float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_01-01-09.jpg.jpg\" alt=\"\" /><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1 січня 2009 року в Івано-Франківську відзначили 100-ій Ювілей з дня народження провідника України - Степана Бандери. Спочатку урочистості відбулися в рідному селі Степана Бандери - Старому Угринові, поблизу міста Івано-Франківська, де він народився, зростав та виховувався в священичій родині Греко-Католицького священика. Саме на цьому місті Преосвященний Владика Володимир (Війтишин), Єпарх Івано-Франківський, відслужив Панахиду, після якої святкування продовжились в м. Івано-Франківську, де було відкрито і освячено Меморіальний комплекс і пам\'ятник визначному політичному та громадському діячу, провіднику ОУН - Степану Бандері.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На відкритті Меморіального комплексу були присутні: Преосвященний Владика Володимир (Війтишин), Єпарх Івано-Франківський УГКЦ, Преосвященний Владика Миколай (Сімкайло), Єпарх Коломийсько-Чернівецький УГКЦ, Преосвященний Владика Софрон (Мудрий), ЧСВВ, Преосвященний Владика Іоасаф (Василиків), Архиєпископ Івано-Франківський і Галицький УПЦ КП, Преосвященний Владика Іоан (Бойчук), Правлячий Єпископ Коломийської єпархії УПЦ КП, а також духовенство Івано-Франківського та сусідніх деканатів УГКЦ, представник Президента України, а також представники влади міста і області, громадських та політичних організацій.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">о. Юрій Трухан, секретар</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Єпарха Івано-Франківського УГКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>\r\n<p>&nbsp;</p>',NULL,1909,5,1,NULL,42);
INSERT INTO `publications_publication` VALUES
(4624,'P','2009-01-05',1,NULL,'Rss','Привітання Блаженнішого Любомира на Різдво ГНІХ 2009 року','<p style=\"text-align: center;\">Високопреосвященним і Преосвященним Владикам,<br /> Всесвітлішим і Високопреподобним отцям,<br /> Преподобним ченцям і черницям,<br /> Дорогим у Христі сестрам і братам мирянам<br /> Мир і Боже благословення!</p>\n<p><img src=\"http://www.oranta.org/images/stories/2008/10/blazhennishyj_lubomyr_opt.jpg\" style=\"float: left;\" /> Дорогі у Христі, через кілька днів ми будемо святкувати Різдво Господа нашого Ісуса Христа, надзвичайно важливу подію для кожного і кожної з нас, для наших родин, для громад, для народу, для Церкви і для всього людства. Ця подія настільки важлива, що ми навіть ділимо історію людства на роки до народження Ісуса Христа та після Його народження. І робимо це не принагідно чи випадково, а на підставі глибокого усвідомлення, що світ до Його приходу на землю сильно відрізнявся від світу, який настав після Його приходу. Так само і з життям кожного і кожної з нас. Коли ми втілюємося в Ісуса Христа через святе таїнство Хрещення, пізнаємо Його і зустрінемося з Ним, наше життя набуває іншого, нового, кращого вигляду. Пізнати, зрозуміти, відчути ту близькість Бога до нас та можливість нашої близькості до Господа - це річ, яка цілковито змінює наше уявлення про людське життя, наше відчуття самих себе, власного життя і життя людства загалом.</p>\n<p>Вітаючи вас із святим празником, який от-от настане, сердечно бажаю кожному із вас, як і собі самому, щоб у ці святі дні ми могли наблизитися до Господа Бога, відкрити своє серце до Його любові, прийняти Його, зрозуміти, що Він перший полюбив нас і що зміст нашого життя - відповісти на Його любов своєю любов‘ю, любов\'ю до Нього та до всіх своїх ближніх.</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=171:pryvitannja-blazhennishogo-ljubomyra-na-rizdvo-gnih-2009-roku&catid=27:gk-v-ukrajini-cat&Itemid=57',1907,NULL,1,1,26),
(4625,'P','2009-01-05',1,NULL,'Rss','Різдво Господа Бога і Спаса нашого Ісуса Христа','<p><img src=\"http://www.oranta.org/images/stories/2008/10/5205ceff73.jpg\" style=\"float: right;\" /> <strong>7 січня 2009 року</strong></p>\n<p><strong> Різдво Господа Бога і Спаса нашого Ісуса Христа</strong></p>\n<ul>\n<li>Коротка історія свята</li>\n<li>Про ікону празника</li>\n<li>Тропар та кондак</li>\n<li>Празничний Апостол</li>\n<li>Євангеліє</li>\n<li>Проповідь</li>\n</ul>\n<p>Коротка історія</p>\n<p align=\"justify\">Протягом трьох перших сторіч християни не мали окремого празника Христового Різдва. У той час був тільки празник Богоявлення, яке святкували 6 січня. Цей празник поєднував у собі і Христове Різдво і Хрещення Ісуса в ріці Йордані. Грецьке слово \"Епіфанія\" чи \"Теофанія\", що значить \"З\'явління\" чи \"Богоявлення\", у перших віках християнства означало не тільки появу Ісуса Христа при його хрещенні, але і його появу на землі, тобто його народження.</p>\n<p align=\"justify\">Про давню злуку цих двох празників і їхнє святкування в один день свідчить тотожний уклад у нашому богослуженні. Схема богослужби цих празників одна й та сама. Обидва мають навечір\'я з постом, Літургію святого Василія Великого з вечірнею у навечір\'я, царські часи, велике повечер\'я зі співом \"З нами Бог\", на Літургії \"Єлици\" і т. п. Різниця в їхньому богослуженні полягає тільки у змісті богослужби.</p>\n<p align=\"justify\">\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=170:rizdvo-gospoda-boga-i-spasa-nashogo-isusa-hrysta&catid=28:tema-nomera-cat&Itemid=2',1906,NULL,1,1,26);
INSERT INTO `publications_publication` VALUES
(4626,'P','2009-01-05',1,NULL,'Rss','Поворот від магнетизму до духовних релігій','<p align=\"center\">Напередодні християнства </p>\n<p>Збігає друге тисячоліття від народження Ісуса - засновника світової релігії - християнства, яка стала основою західної цивілізації.  Християнство стало завершенням духовного повороту, здійсненого в першому тисячолітті до нашої ери - в період, який вчені називають  <em>осьовим часом</em>   Сватової історії людства. До цього часу панували магічні релігії,  Бога шукали поза людиною, а світогляд формувався міфами.  Світ  був  простий  i зрозумілий, всім заправляли численні боги, які вимагали послуху, проте,  умилостививши їх,  можна було скористатися  їхньою силою. Проблеми ж людини - як особистості,  як духовної істоти - не існувало.</p>\n<p>В осьовий  час  майже  одночасно  у всіх високорозвинених цивілізаціях з\'явилися вчення, які прагнули збагнути суть людини i суть Бога.  Авторів нових вчень - Лао-Цзи, Конфуція, Будду, Зороастра, Платона, іудейських пророків i, нарешті, Ісуса з  Назарету  нині називають Учителями людства.  Завдяки їм з\'явився новий тип людини  i  сформувався  той  світогляд,  який домінує й досі. Людина усвідомила сама себе, свої можливості i свої межі.  Перед нею відкрилась безодня безконечного світу  i безодня власної душі.</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=169:povorot-vid-magnetyzmu-do-duhovnyh-religi&catid=28:tema-nomera-cat&Itemid=2',1905,NULL,1,1,26),
(4627,'P','2009-01-05',1,NULL,'Note',' Передсвяття Різдва Христового','<h3 style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Святі мученики Критські</span></span></h3>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Під час переслідування християн римським Імператором Декієм погани ув\'язнили Теодула, Сатурніна, Євпора, Евніміяна, Зотика, Клеомена, Агатона, Василіда і Евариста Після жахливих тортур їх привели на суд до столиці Гортині.</span></span></p>\r\n<p>&nbsp;</p>','<h3 style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Передсвяття Різдва Христового. <br /> Свв. десятьох мучч., що в Криті.</span></span></h3>\r\n<h3 style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></h3>\r\n<h3 style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Святі мученики Критські</span></span></h3>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Під час переслідування християн римським Імператором Декієм погани ув\'язнили Теодула, Сатурніна, Євпора, Евніміяна, Зотика, Клеомена, Агатона, Василіда і Евариста Після жахливих тортур їх привели на суд до столиці Гортині.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Коли вони відмовилися принести жертву поганському божкові Юпітерові, староста наказав їх жорстоко мучити Вірні Христові борці свою муку терпіли з радістю, а поганам говорили: \"Ми готові швидше тисячу разів умерти, ніж стати Ідолопоклонниками\" У 250 році усі вони прославили Бога мученицькою смертю. Мощі святих мучеників пізніше перенесли до Рима.</span></span></p>\r\n<h3 style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Послання ап. Павла до Євреїв 8, 7-13</span></span></h3>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Браття, коли б перший завіт був бездоганний, то не було б місця для другого. Та Господь, докоряючи їм, говорить: Ось надходять дні, мовить, і я укладу новий завіт з домом Ізраїля і з домом Юди: не за завітом, що я уклав був з їхніми батьками в день, коли був взяв їх за руку, щоб вивести з землі Єгипетської. А що вони при моїм завіті не зосталися, говорить Господь, то я їх покину Ось той завіт, що я заключу з домом Ізраїля: По тих днях, каже Господь, я, поклавши мої закони їм на ум, напишу їх у них на серці і буду їм Богом, а вони будуть моїм народом. Ніхто не матиме потреби навчати свого ближнього, і ніхто свого брата, кажучи: Пізнай Господа. Всі бо вони, від найменшого до найбільшого, будуть Мене знати. Я бо буду милосердний супроти їхньої несправедливости, і їхніх гріхів не буду більше згадувати. А коли Він каже: Новий, тим робить перший старим. А те, що передавнене й застаріле, те близько заникнення.</span></span></p>\r\n<h3 style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Євангеліє від Марка 8, 11-21</span></span></h3>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В той час вийшли фарисеї і стали сперечатися з Ісусом і, спокушаючи Його, вимагали від Нього знаку з неба. Зітхнув Він важко з глибини серця і каже: Чого цей рід вимагає знаку? Істинно кажу вам: Не дасться цьому родові знаку! І, покинувши їх, знову сів у човен, і поплив на той бік. Учні ж забули взяти хліба - мали з собою лиш один хліб у човні. Він остерігав їх, кажучи: Глядіть бережіться закваски фарисейської та Іродової! І вони стали говорити між собою про те, що не мають хліба. Ісус, довідавшись про те, каже до них: Чого між собою говорите, що не маєте хліба? Невже ви іще не розумієте і не тямите? Невже у вас іще нечуле серце? Маючи очі - не бачите? Маючи вуха - не чуєте? Не пам\'ятаєте, як я ламав п\'ять хлібів на п\'ять тисяч? Скільки кошів, повних куснів, ви зібрали? - Дванадцять, кажуть Йому. А коли Я розламав сім хлібів на чотири тисячі, скільки кошів куснів ви зібрали? - Сім, кажуть Йому. І Він сказав їм: Іще не розумієте?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>',NULL,1904,5,1,NULL,42);
INSERT INTO `publications_publication` VALUES
(4628,'P','2009-01-04',1,NULL,'Note','Молитва \"Ангел Господній\" з Папою Венедиктом XVI у першу неділю 2009 року','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Святіший Отець наголосив на тому, що початок першої глави Євангелії від Івана пропонує нам синтез усієї християнської віри. За словами Вселенського Архиєрея, Святий Іван розпочинає згори, кажучи: &laquo;Споконвіку було Слово, і з Богом було Слово, і Слово було - Бог&raquo; (Ів.&nbsp;1,1). Та нечувана і незрозуміла для людини новина є у наступних словах: &laquo;І Слово стало тілом, і оселилося між нами&raquo; (Ів.&nbsp;1,14).</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ми зближаємося до закінчення Різдвяного Посту, а більша частина християнського світу уже відсвяткувала празник Різдва Христового і перебуває у святковій атмосфері. У цю другу неділю по Різдві, у римо-католицьких храмах під час Святих Літургій був прочитаний уривок з Євангелії від Святого Івана, про Боже Слово, Яке стало Тілом і оселилося між нами. Сьогодні, 4 січня, перед проказуванням із вірними молитви &laquo;Ангел Господній&raquo;, Папа Венедикт ХVІ запропонував зібраним на площі Святого Петра зупинитись над цим біблійним текстом, який заохочує кожного з нас ще раз призадуматись над незглибимою таємницею Різдва Божого Сина, щоб, таким чином, краще зрозуміти важливість нашого життя. <br /><br />Святіший Отець наголосив на тому, що початок першої глави Євангелії від Івана пропонує нам синтез усієї християнської віри. За словами Вселенського Архиєрея, Святий Іван розпочинає згори, кажучи: &laquo;Споконвіку було Слово, і з Богом було Слово, і Слово було - Бог&raquo; (Ів.&nbsp;1,1). Та нечувана і незрозуміла для людини новина є у наступних словах: &laquo;І Слово стало тілом, і оселилося між нами&raquo; (Ів.&nbsp;1,14). Продовжуючи, Іван Богослов не розповідає про абстрактні речі, від когось почуті, а про особистий життєвий досвід, бо ж він три роки, разом з іншими апостолами, був біля Спасителя, слухав Його повчань, був Його улюбленим учнем, який стояв під хрестом на Голготі, бачив смерть свого Учителя і був свідком Його перемоги над смертю. З усього цього досвіду Святий Апостол виносить особисте переконання: Ісус є втіленою Божою Надією, є Споконвічним Божим Словом, Яке стало людиною, приймаючи на себе смертне тіло.<br /><br />&laquo;Для правдивого ізраїльтянина, який знає Святі Писання, це не є абсурдом, а навпаки, - здійсненням усього старого Завіту: в Ісусі Христі досягає повноти таємниця Бога, Який промовляє до людей, немов до друзів, Який показується у Законі Мойсеєві, вченим і пророкам&raquo;, - наголосив Святіший Отець. Знаючи Ісуса, будучи із Ним, слухаючи Його проповіді і бачачи знаки, які Він чинив, учні зрозуміли, що саме у Ньому виповнюється Писання. Далі Папа процитував святого Уґо, який написав, що усі Божественні Писання - це одна Книга, Ісус Христос, оскільки вони усі про Нього говорять і в Ньому мають своє сповнення. &laquo;Кожен чоловік і кожна жінка потребує знайти глибокий сенс для власного існування. І для цього не вистарчає книжок, і навіть Святих Писань. Вифлеємська Дитина виявляє нам і показує справжнє &laquo;обличчя&raquo; доброго і вірного Бога, Який нас любить і не залишить нас навіть у смерті, - додав Папа Венедикт&nbsp;ХVІ, підсилюючи свою думку словами Святого Євангелиста, - Ніхто й ніколи Бога не бачив. Єдинородний Син, що в Отцевому лоні, - Той об\'явив&raquo;&nbsp;(Ів&nbsp;1,18).<br /><br />Закінчуючи свою недільну катехизу, Вселенський Архиєрей наголосив, що першою, Яка відкрила своє серце Слову, Яке стало Тілом, була Пречиста Діва Марія, покірна дівчина з Галилеї, ставши оселею Премудрості Божої. &laquo;Кожний із нас, як Апостол Іван, є запрошений &laquo;узяти Її (Марію) до себе&raquo; (Ів.&nbsp;19,27), щоб глибше пізнати Ісуса і досвідчити Його вірної і невичерпної любові. Це є моїм побажанням для кожного із вас, дорогі брати та сестри, на початку цього нового року&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,1901,5,1,NULL,43);
INSERT INTO `publications_publication` VALUES
(4629,'P','2009-01-03',1,NULL,'Note','Молитовні намірення Папи Венедикта XVI на січень 2009 року','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Від 14 до18 січня у столиці Мексики місті Мехіко проходитиме Всесвітня зустріч родин, а від 18 до 25 січня проводитиметься традиційний тиждень молитов за єдність християн. Тому Святіший Отець Венедикт&nbsp;XVI запропонував наступні молитовні намірення на перший місяць 2009 року.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Від 14 до18 січня у столиці Мексики місті Мехіко проходитиме Всесвітня зустріч родин, а від 18 до 25 січня проводитиметься традиційний тиждень молитов за єдність християн. Тому Святіший Отець Венедикт&nbsp;XVI запропонував наступні молитовні намірення на перший місяць 2009 року:<br /><br /><strong><span style=\"text-decoration: underline;\">Загальне:</span></strong> Щоб родина завжди була місцем виховання до любові, особистого зростання та передавання віри.<br /><br /><strong><span style=\"text-decoration: underline;\">Місійне:</span></strong> Щоб різні християнські конфесії, усвідомлюючи необхідність нової євангелізації у сучасному періоді глибоких перетворень, докладали зусиль, щоб звіщати Добру Новину і прямувати до повної єдності всіх християн, аби, таким чином, давати вірогідніше свідчення Євангелію.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,1900,5,1,NULL,43),
(4630,'P','2009-01-03',1,NULL,'Note','Відзначення представників духовенства УГКЦ під час заключної частини проекту \"Україна й українці - цвіт нації, гордість країни\"  ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У рамках міжнародного проекту &laquo;Україна й українці - цвіт нації, гордість країни&raquo; на урочистій церемонії лауреатам вручено нагрудний знак - орден &laquo;Патріот України&raquo;, диплом &laquo;Патріот України&raquo; за проявлений патріотизм та бездоганне служіння Україні, а також Книгу пошани, де розміщена інформація про лауреатів - видатних діячів нашої держави та діаспори.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">29 грудня 2008 року у Національній філармонії України міста Києва о 14.00 розпочалось урочисте відзначення визнаної еліти України, понад 160 осіб, серед яких також і представники української діаспори.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Завдяки опитуванню серед органів державного управління та ділових кіл України та української діаспори, були визначені представники ділової еліти, яким надана гідність носити звання &laquo;Патріота України&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Серед яких аграрії, підприємці, промисловці, транспортники, енергетики, будівельники, представники освіти і науки, діячі культури, працівники охорони здоров\'я.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У рамках міжнародного проекту &laquo;Україна й українці - цвіт нації, гордість країни&raquo; на урочистій церемонії лауреатам вручено нагрудний знак - орден &laquo;Патріот України&raquo;, диплом &laquo;Патріот України&raquo; за проявлений патріотизм та бездоганне служіння Україні, а також Книгу пошани, де розміщена інформація про лауреатів - видатних діячів нашої держави та діаспори.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">До цієї нагороди були представлені і відзначені також представники духовенства УГКЦ, серед яких:</span></span></p>\r\n<blockquote style=\"text-align: justify;\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Преосвященний Владика Гліб (Лончина), Куріальний єпископ УГКЦ, Апостольський Візитатор для українців греко-католиків Італії, Іспанії та Ірландії.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Преосвященний Владика Діонісій (Ляхович), Куріальний єпископ УГКЦ, Відповідальний за українців - емігрантів в Синоді УГКЦ.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Отець Олександр Сапунко, Пасторальний Координатор для українців греко-католиків в Італії.</span></span></p>\r\n</blockquote>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Нагороду вручив перший Президент незалежної України Леонід Кравчук.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Метою проведення акції є сприяння міжнародному визнанню позитивної ролі лідерів і ділових особистостей, формування та зміцнення позитивного ділового іміджу краян на Батьківщині та поза її межами, відродження народних традицій у всіх сферах діяльності, встановлення конструктивного діалогу між вітчизняними виробниками, науковцями, діячами науки, освіти і культури з представниками владних структур і громадскістю, активізація ділових відносин між Україною і українською діаспорою та створення гідного іміджу України у всьому світі.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Інформацію надала Ірина Ключковська,<br /> директор Інституту освіти, культури та звязків з діаспорою</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>',NULL,1899,5,1,NULL,42);
INSERT INTO `publications_publication` VALUES
(4631,'P','2009-01-03',1,NULL,'Note','Блаженніший Любомир вважає, що сучасну кризу можна використати на користь','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\"Сьогодні ми дещо затривожені, багато говориться про кризу і справді, можна передбачувати, що будуть для нас трудні часи, не все буде так укладатися, як ми хочемо, але воно, напевно, буде на нашу користь\", - сказав проповідник.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">31 грудня у храмі Благовіщення Пресвятої Богородиці при Патріаршому соборі Воскресіння Христового відбувся подячний молебень з нагоди завершення 2008 року. Молитву очолив Владика Богдан (Дзюрах), Єпископ-помічник Київської архиєпархії. На молебні був присутній Блаженніший Любомир, Верховний Архиєпископ Києво-Галицький, - для Департаменту інформації повідомив Тарас Семенюк.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Під час проповіді Блаженніший Любомир наголосив, що на кінець прожитого року слід подякувати Богу за прожиті моменти. \"Ми хочемо побачити, і так воно належить, все те, що сталося в нашому власному житті і довкруги нас, як дар від Господа Бога. Зрозуміти все те, що приємне, як дар від Господа Бога - це, можна сказати, самозрозуміле, але коли я скажу прийняти, як дар від Господа Бога те, що прикре, то, на перший погляд, воно якось так дивно звучить. Одначе, те що добре, так само, як те, що прикре, є дуже цінне для нас, бо воно від Господа Бога, нічого не стається принагідно. Усе що стається в нашому житті, довкруги нас і для нас, воно є від Господа Бога, воно є на нашу користь, воно є від Небесного Отця, який є і Всемогучий, і Всезнаючий, і дуже Добрий, який нічого не робить і нічого не допустить, щоби було нам шкідливе\", - сказав Предстоятель УГКЦ.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\"Сьогодні ми дещо затривожені, багато говориться про кризу і справді, можна передбачувати, що будуть для нас трудні часи, не все буде так укладатися, як ми хочемо, але воно, напевно, буде на нашу користь\", - сказав проповідник. Також Блаженніший Любомир закликав вірних в наступному році бути більш відкритими на Божу волю: \"Ми дивимось вперед, в наступний рік, очами святої віри як діти Доброго Батька. Дай Боже, щоби ми увійшли в цей новий рік з таким наставленням. Тоді, щоб не сталося, буде нам на добро\", - зазначив Глава УГКЦ.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Департамент інформації УГКЦ</span></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Проповідь Блаженнішого Любомира у храмі Благовіщення Пресвятої Богородиці при Патріаршому соборі УГКЦ на закінчення 2008 року</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Преосвященний Владико! Дорогі парафіяни!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сьогодні закінчується цивільний рік. Закінчується, здається, також отой період, коли в засобах масової інформації ми чули стільки різних програм, які підводили підсумок минулого року. З одної сторони - це здавалося б зайве, бо сонце завтра так само зійде, як і сьогодні, не буде якоїсь видимої різниці між сьогоднішнім, а завтрашнім днем. А однак, це добре, що ми і цілий людський рід, здається, звертає увагу на те, щоби зупинитись на хвилину, щонайменше, і застановитись над тим, що діється.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Що хотіли би ми сьогодні зробити? Ми хотіли б також застановитись, але, може, дещо в інший спосіб, ніж це роблять оті ЗМІ, і ті що там виступають - політики, політологи, журналісти чи інші люди. Бо хочемо подивитись на отой минулий рік іншими очима, очима віри і хочемо подивитись на нього також в такий спосіб, щоби витягнути для себе якнайбільшу можливу користь. Коли прислухуємося до тих різних аналіз минувшини - на телебаченні, чи в радіо, чи читаємо в газетах - деякі з них дуже поверхові, але деякі, справді, є дуже проникливі. Одначе, бракує якогось переконання, що вся ця аналіза, все це зусилля, інколи, як я згадав, дуже мудре, дуже проникливе, що воно справді має для нас практичне значення. Здається, що багато з того, що говориться, немов би далеке від нас, немов би ми в якийсь спосіб не задіяні в тому, що діється. А те, що ми направду хочемо, - бо це одинокий шлях, як може світ змінитися на краще, - щоб ота аналіза відносилася до нас особисто. Ми можемо говорити про цілий світ, і що нам з того, і яка світові користь з того, з тих багатьох слів? Коли ми застановимося над самими собою, коли глянемо на оцей рік, що минув в нашому власному житті, тоді інакше все укладається, бо в цьому нашому погляді, в цій аналізі є завдаток чогось кращого.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перш усього гляньмо в минуле, в минуле в нашому власному житті. Багато дечого сталося в житті кожного з нас за тих останніх 355, чи 356 днів. Сталося багато такого, що нам було дуже приємне, а з другої сторони, напевне, було немало того, що нам не було дуже приємне, а може навпаки - навіть дуже прикре. Як до цього ставитися? Звідки то все взялося? Воно ж не сталося так принагідно, без якоїсь цілі, без якогось змісту. Усе те, що сталося до останніх календарних днів, сталося з волі чи допусту Божого. Бачите, це є перша і дуже велика різниця між тою аналізою, яку хочемо робити ми, і тими аналізами, які чуємо у ЗМІ. Ми хочемо побачити, і так воно належить, все те, що сталося в нашому власному житті і довкруги нас, <strong>як дар від Господа Бога</strong>. Зрозуміти все те, що приємне, як дар від Господа Бога - це, можна сказати, самозрозуміле, але коли я скажу прийняти, як дар від Господа Бога те, що прикре, то, на перший погляд, воно якось так дивно звучить. Одначе, <strong>те що добре, так само, як те, що прикре, є дуже цінне для нас, бо воно від Господа Бога</strong>, нічого не стається принагідно. Усе що стається в нашому житті, довкруги нас і для нас, воно є від Господа Бога, воно є на нашу користь, воно є від Небесного Отця, який є і Всемогучий, і Всезнаючий, і дуже Добрий, який нічого не робить і нічого не допустить, щоби було нам шкідливе. Усе, навіть те, що дуже прикре: смерть якоїсь близької, дорогої нам особи, якась хвороба, може якась професійна невдача, що би воно не було, воно все остаточно є корисним для нас. Побачити це в нашому житті, це вже є велика річ, - не сортувати на добре і погане, на корисне і прикре, - але все приймати, як Божий дар.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Але тоді постає наступне питання, яке ми мусимо собі поставити, а що ми з цими дарами зробили? Як ми ці Божі дари прийняли? Бо, як я згадав, все, що від Господа Бога, то є на нашу користь, тільки не завжди воно нам виходить на користь, якщо ми не приймемо цього, як Божий дар. Як ми злегковажимо, знехтуємо, будемо нарікати на Господа Бога, бо не так все дається, як ми хотіли, тоді ці Божі дари змарновані. Отже, перш усього, перший відрух нашої душі, коли ми поглянули в наше минуле, особливо, в цей останній рік - подякувати Господу Богові за все, що він нам подарував, чи воно нам виглядало приємним, вигідним, чи прикрим, за все подякувати. І ми зійшлися тут, сьогодні, на цей подячний молебень, бо це повинно бути головною ниткою, головним мотивом цієї аналізи - подякувати Богу, це найважніше.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">А тоді подякувати Йому за все те, що ми зробили добре, що то значить? Коли ми прийняли і використали Його дари згідно Його святої волі, за це треба також Богові дуже сердечно подякувати. Якщо ми зуміли зрозуміти, що це все від Господа Бога і прийняти і використати на славу Божу, на користь ближніх і навіть на користь нашої власної душі - за все це треба Богові дуже подякувати, бо Він нас скріпив, Він дав нам очі святої віри побачити, зрозуміти те все, що з нами сталося.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Нажаль, і це ми мусимо собі признати, кожний з нас, нема тут винятків, не все, що Бог нам посилав, ми використали на добро. Нажаль, так стається в людському житті і потрібно перепросити Господа Бога за наш брак зрозуміння, за нашу надмірну любов самого себе, за нашу нехіть супроти наших ближніх, за наше відкинення, легковаження Божими дарами, перепросити Його за це, що ми не використали так, як треба використати дар Предоброго Небесного Отця.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У тих роздумуваннях, які чуємо в ЗМІ, відчувається один величезний брак: різні особи стараються прогнозувати, що то буде, як то буде. Думаю, що дехто з таких мудріших коментаторів може і дуже влучно дещо передбачити, але, на загал, більше уваги звертається на отого бика, який має бути символом наступного року, ніж на Господа Бога, який є Володарем всесвіту. А ми хочемо дивитися вперед, але дивитися разом з Господом Богом, то значить, молитися, просити в Господа Бога, щоби Він був для нас, для нашого зрозуміння, отим Батьком. Він ним є і завжди буде, але щоби ми це зрозуміли і щоби ми зрозуміли все, що буде в наступному році від Нього походити, будуть знову нові дари, щоби при Його Божій помочі ми ці дари використали. Чи ми сьогодні можемо передбачити що і як буде, чи ні, це є цілком другорядне, але одне є певне, що все, що буде, буде на нашу користь, для нашого добра, щоби ми це вміли використати, оцього нам потрібно. І коли ми будемо в такий спосіб дивитись в майбутнє, в цей наступний рік, то будемо направду щасливі.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сьогодні ми дещо затривожені, багато говориться про кризу і справді, можна передбачувати, що будуть трудні для нас часи, не все буде так укладатися, як ми хочемо, але воно, напевно, буде на нашу користь. Ми дивимось вперед, в наступний рік, очами святої віри як діти Доброго Батька. Дай Боже, щоби ми так увійшли в цей новий рік з таким наставленням. Тоді, щоб не сталося, буде нам добре.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ви знаєте, як то ми одне одного сьогодні, завтра вітаємо і бажаємо всього того, що, думаємо, є добре. Ми то робимо щиро, але як немічні люди. Бажаємо те, чого ми можемо дати, чого ми не можемо запевнити, але хоча є в нас добра воля. Побажаймо одне одному, щоби ми в цьому наступному році відчули себе дітьми Небесного Отця. Це буде справжнє побажання, це буде побажання не тільки на словах, які б вони не були щирі, і це буде побажання дуже конкретне, це буде побажання чогось, що ми можемо спокійно сказати, буде цілком певно здійснене. Тож побажаймо одне одному, щоби ми себе відчули в цьому наступному році і в кожному році нашого життя - дітьми Предоброго Батька.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Слава Ісусу Христу!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>31 грудня 2008 року</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>',NULL,1898,5,1,NULL,42);
INSERT INTO `publications_publication` VALUES
(4632,'P','2009-01-03',0,NULL,'Note','','<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong> Молитва св. Макарія до Пресвятої Тройці</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вставши зі сну, дякую Тобі, Боже, у  Святій Тройці єдиний, що у превеликій доброті і в безмежній терпеливості не  розгнівався на мене, грішного (грішну) і негідного (негідну), та не погубив мене  з моїми провинами, а в пребагатому своєму чоловіколюбстві підніс мене зі сну,  щоб я ранішньою молитвою прославляв (прославляла) твою силу. Просвіти, Владико, мої духовні очі,  відкрий мої уста, допоможи мені вправлятися в Твоєму законі і розуміти твої  заповіді. Допоможи мені виконувати твою святу волю і визнавати Тебе цілим серцем  та славити всесвяте ім\'я Твоє, Отця, і Сина, і Святого Духа, нині і повсякчас, і  на віки віків. Амінь.</span></span></p>','',NULL,1897,5,1,NULL,50),
(4633,'P','2009-01-02',1,NULL,'Note','Новий Рік та Різдво Христове на Аскольдовій Могилі','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,2161,200,DSC_0079.JPG\" alt=\"\" />Щороку парафіяни нашої церкви разом зі священиками зустрічають Новий Рік у молитві. Вночі відправляється Божественна літургія, а по тому і новорічні частування у церковній хатині. Цього року разом з нами зустрічав Новий Рік і владика Богдан Дзюрах. Молитва, передріздвяний настрій, колядки, смачна гостина - все що потрібно християнинові у новорічну ніч. У часі Різдва Христового парафіяни гуртуються до спільної куті. Святочний настрій огортає кожне серце і проникає у кожну душу. Народження спасителя несе кожному віру, надію та любов.<br /></span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Фото: Ігоря Андрійовича Савицького</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,2161,200,DSC_0035.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,2161,200,DSC_0037.JPG\" alt=\"\" /></span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,2161,200,DSC_0045.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,2161,200,DSC_0061.JPG\" alt=\"\" /></span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,2161,200,DSC_0085.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,2161,200,DSC_0088.JPG\" alt=\"\" /></span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,2161,200,DSC_0090.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,2161,200,DSC_0093.JPG\" alt=\"\" /><br /></span></span></p>',NULL,2161,5,1,NULL,49);
INSERT INTO `publications_publication` VALUES
(4634,'P','2009-01-02',1,NULL,'Note','В УГКЦ створено дві нові комісії: одна займатиметься кадровим забезпеченням, друга – покликанням і життям священиків','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Згідно з декретами Блаженнішого Любомира, в УГКЦ створено дві нові комісії: Комісію кадрового забезпечення Києво-Галицького Верховного Архиєпископства та Комісію священичого покликання, життя і служіння Києво-Галицького Верховного Архиєпископства. Про це йдеться у восьмому числі \"Вісника Києво-Галицького Верховного Архиєпископства\" за жовтень - грудень 2008 року.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Згідно з декретами Блаженнішого Любомира, в УГКЦ створено дві нові комісії: Комісію кадрового забезпечення Києво-Галицького Верховного Архиєпископства та Комісію священичого покликання, життя і служіння Києво-Галицького Верховного Архиєпископства. Про це йдеться у восьмому числі \"Вісника Києво-Галицького Верховного Архиєпископства\" за жовтень - грудень 2008 року. (Про комісії, які вже існують в УГКЦ, ви можете дізнатися за цим посиланням: <a href=\"http://www.ugcc.org.ua/39.0.html\">http://www.ugcc.org.ua/39.0.html</a>).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\"Комісія кадрового забезпечення буде займатися раціональним посиланням на вищі студії (спеціалізацію) студентів нашої Церкви. Її завданням є виробити кадрову політику Церкви і згідно з нею діяти. Члени цієї комісії мають стежити, щоб у кожній галузі богословської науки, у ділянці медіа, соціального служіння ми мали достатню кількість спеціалістів. Ця комісія діє на рівні Києво-Галицького Верховного Архиєпископства, тобто України\", - у коментарі для Департаменту інформації пояснив Блаженніший Любомир. За словами Предстоятеля УГКЦ, ця комісія, крім того, збиратиме базу даних про наявних спеціалістів. \"Дальша наша мета - за посередництвом Комісії вирішувати всі кадрові питання наших єпархій в Україні. Хочемо, щоб владика, маючи потребу у якомусь спеціалісті, міг легко це вирішити з допомогою Комісії кадрового забезпечення Києво-Галицького Верховного Архиєпископства\", - сказав Блаженніший Любомир. Головою Комісії, згідно з декретом Блаженнішого Любомира, призначено о. д-ра Бориса Ґудзяка.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Блаженніший Любомир також пояснив потребу створення Комісії священичого покликання, життя і служіння Києво-Галицького Верховного Архиєпископства, адже у Церкві вже діє Комісія священичого виховання в семінаріях УГКЦ. \"Священиче життя починається від покликання. Відтак майбутній священнослужитель виховується і навчається у семінарії, тоді приймає таїнство Священства і після цього виконує своє служіння. Програма пошуку покликань, особливості&nbsp; служіння священика тісно зв\'язані із конкретною країною, з її особливостями. Тому в кожній місцевості має бути створена своя комісія, яка б займалася цими аспектами життя священиків. Тому в Україні ми створили Комісію священичого покликання, життя і служіння\", - розповів Глава УГКЦ.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">При цьому Блаженніший Любомир пояснив, що спільним для всіх священнослужителів всієї Церкви є час навчання у семінарії. Тому зроблено відповідні заходи, щоб у всіх навчальних закладах світу, які є під опікою УГКЦ, чи в яких навчаються майбутні душпастирі Церкви, була однакова програма навчання і виховання. \"Це означає, що кожний священик УГКЦ, незважаючи на країну його навчання, буде мати ту саму підготовку, формацію. Це потрібно, бо ми обмінюємося священиками: з України їдуть до Північної Америки чи Аргентини, Італії чи Іспанії\", - сказав Глава УГКЦ. Головою Патріаршої (всецерковної. - Авт.) комісії священичого виховання в семінаріях УГКЦ є Владика Кен (Новаківський), Єпарх Нью-Вестмінтерський (Канада).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Департамент інформації УГКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>',NULL,1896,5,1,NULL,42);
INSERT INTO `publications_publication` VALUES
(4635,'P','2009-01-02',1,NULL,'Note','Перший випуск з Василіянського інституту філософсько-богословських студій у Брюховичах','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Для вісімнадцяти студентів сьомого курсу, що майже всі вже є дияконами, це була прощальна літургія, адже вони закінчили своє навчання. Більшість поки що залишаться виконувати своє служіння в Україні, а деякі невдовзі від\'їдуть на служіння або навчання закордон. Двоє співбратів, які навчальний шлях починали разом із теперішніми випускниками, отримали ієрейські свячення ще влітку та від\'їхали на навчання до Варшави.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">30 грудня 2008&nbsp;р., на закінчення зимової сесії, у каплиці Василіянського інституту філософсько-богословських студій у Брюховичах була відслужена подячна Архиєрейська Свята Літургія, яку очолив владика Софрон Мудрий,&nbsp;ЧСВВ, вислужений єпарх Івано-Франківський. Для вісімнадцяти студентів сьомого курсу, що майже всі вже є дияконами, це була прощальна літургія, адже вони закінчили своє навчання. Більшість поки що залишаться виконувати своє служіння в Україні, а деякі невдовзі від\'їдуть на служіння або навчання закордон. Двоє співбратів, які навчальний шлях починали разом із теперішніми випускниками, отримали ієрейські свячення ще влітку та від\'їхали на навчання до Варшави.<br /><br />Під час богослужіння випускників привітав владика Софрон, побажавши їм бути відважними, справжніми місіонерами Божого Слова на шляху до вічності. Коротким словом до вітань прилучився й о.&nbsp;Роберт Лисейко, ЧСВВ, Ректор інституту, вручивши кожному випускникові Компендіум Катехизму Католицької Церкви, щоби, за його словами, безпомилково навчали вірних Божих правд.<br /><br />Після скромного сніданку випускники-монахи мали зустріч з о.&nbsp;Протоігуменом Йоанікієм Чверенчуком, ЧСВВ. Він дав учорашнім студентам ряд практичних вказівок на подальше життя у Василіянському Чині Святого Йосафата. Пригадав, що їх попереду чекає духовний досвід, та те, що час їхньої людської формації не закінчився, але триватиме й надалі. О.&nbsp;Йоанікій зазначив, що раніше вони під час навчання здавали різноманітні іспити, проте тепер матимуть важливий іспит перед самим Богом.<br /><br />Для Радіо Ватикану, диякон Миколай МИКОСОВСЬКИЙ, ЧСВВ, Львів</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span><strong><em><br /></em></strong></p>',NULL,1895,5,1,NULL,42),
(4636,'P','2009-01-01',1,NULL,'Note','«Маріє, виблагай у Бога дару миру для Святої Землі і всього людства!»','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Скажімо Марії: супроводь нас, небесна Мати Спасителя, протягом усього року, що сьогодні розпочинається і виблагай у Бога дару миру для Святої Землі і всього людства. Пресвята Богородице, молися за нас. Амінь&raquo;, - цими словами Папа Венедикт&nbsp;XVI закінчив свою проповідь під час Святої Літургії, яка 1 січня 2009 року відправлялась у Ватикані, в базиліці святого Петра.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,1894,200,foto_08.jpg\" alt=\"\" />&laquo;У перший день року Боже Провидіння збирає нас на богослуження, яке кожного разу зворушує багатством і красою свого взаємозв\'язку: цивільний Новий рік зустрічається з вершиною різдвяного періоду - октави Різдва, - коли відзначається Божественне Материнство Марії і цей збіг знаходить щасливий підсумок в Всесвітньому Дні миру. У світлі Різдва з радістю звертаюсь до всіх з найкращими побажаннями для року, який щойно почався&raquo;, - такими словами розпочав свою першу у 2009 році проповідь Папа Венедикт&nbsp;XVI, під час відправи урочистої Божественної Літургії в базиліці святого апостола Петра у Ватикані. За традицією першого дня Нового року, коли католицька Церква латинського обряду святкує урочистість Пресвятої Богородиці Марії, Святіший Отець відправляє вранці Божественну Літургію, розпочинаючи Новий Рік Божим благословенням для всього світу.<br /><br />З Вселенським Архиєреєм співслужили кардинал Тарчізіо Бертоне, Державний Секретар Ватикану, кардинал Ренато Раффаеле Мартіно, Президент двох Папських Рад: &laquo;Справедливість і мир&raquo; і Ради Душпастирства мігрантів і подорожуючих, архиєпископ Фернандо Філоні, заступник Державного Секретаріату, архиєпископ Домінік Мамберті, Секретар Секретар у справах стосунків з державами і єпископ Джампаоло Крепальді, Секретар Папської Ради &laquo;Справедливість і мир&raquo;.<br /><br />Венедикт&nbsp;XVI звернувся з молитовним благанням до Пречистої Діви Марії, Матері Божого Сина, що став нашим братом, щоб Вона допомогла нам іти Його слідами, поборювати і перемагати бідність, будувати справжній мир, що є справедливим ділом. Окремо згадуючи теперішній збройний конфлікт у Святій Землі Святіший Отець молився: &laquo;Тобі ввіряємо глибоке бажання жити в мирі, яке випливає з серця переважної більшості ізраїльського та палестинського населення, ще раз підданого небезпеці масовим вибухом насильства в секторі Гази у відповідь на інше насильство. <br />Папа також згадав звернення предстоятелів християнських Церков Святої Землі, які звернулись із закликом припинити акти насильства, які несуть тільки руїну і розв\'язати проблеми мирним способом, а також призначили наступну неділю на день молитви за справедливість і мир. &laquo;Разом з ними, - сказав Папа, - і разом з їхніми дорогими вірними, особливо з невеликої, але ревної парафії в Газі, складімо у стіп Пречистої Діви Марії наші турботи за теперішнє і побоювання за майбутнє, але, одночасно, і впевнену надію, що з мудрим і далекоглядним вкладом усіх людей не буде неможливим вислухати одні одних, вийти одні одним назустріч і дати конкретні відповіді на розповсюджене прагнення жити в мирі, безпеці, в гідності. <br /><br />&laquo;Скажімо Марії: супроводь нас, небесна Мати Спасителя, протягом усього року, що сьогодні розпочинається і виблагай у Бога дару миру для Святої Землі і всього людства. Пресвята Богородице, молися за нас. Амінь&raquo;, - цими словами Папа Венедикт&nbsp;XVI закінчив свою проповідь під час Святої Літургії, яка 1 січня 2009 року відправлялась у Ватикані, в базиліці святого Петра.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,1894,5,1,NULL,43);
INSERT INTO `publications_publication` VALUES
(4637,'P','2009-01-01',1,NULL,'Note','Перший у 2009 році «Ангел Господній» з Вселенським Архиєреєм Венедиктом XVI (1 січня 2009 року)','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Святіший Отець наголосив, що в Ісусі Христі, Воплоченому Божому Слові, всім людям вказаний шлях спасіння, що, перш усього, є духовним відкупленням, але повністю охоплює все, що людське, включаючи також і суспільний та історичний вимір.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,1893,200,foto_04.jpg\" alt=\"\" />&laquo;У перший Нового року я з радістю звертаюсь до всіх вас, зібраних на площі святого Петра і до всіх, які сьогодні з нами завдяки радіо й телебаченню, з найщирішими побажаннями миру й усього найкращого&raquo;, - ці побажання прозвучали опівдні 1 січня 2009 року з уст Вселенського Архиєрея Венедикта&nbsp;XVI перед проказуванням молитви &laquo;Ангел Господній&raquo;, яка у перший день Нового року зібрала на площі святого Петра у Ватикані десяткми тисяч римлян та прочан. <br /><br />Святіший Отець наголосив, що в Ісусі Христі, Воплоченому Божому Слові, всім людям вказаний шлях спасіння, що, перш усього, є духовним відкупленням, але повністю охоплює все, що людське, включаючи також і суспільний та історичний вимір. <br /><img style=\"float: left;\" src=\"image,1893,200,foto_09.jpg\" alt=\"\" />Крім того Папа згадав і сьогоднішній 42-ий з черги Всесвітній День Миру, закликаючи до глобальної солідарності. Пригадаємо, що цьогорічне послання Венедикта&nbsp;XVI з цієї нагоди має назву: &laquo;&laquo;Долати убогість, будувати мир&raquo;. &laquo;Ісус Христос не організовував кампаній проти бідності, але звіщав убогим Євангеліє, щоб звільнити від моральної та матеріальної убогості. Невпинним ділом євангелізації та сприяння людині Церква робить це ж саме, - підкреслив Папа. &laquo;Благаймо Пречисту Діву Марію, Божу Матір, щоб допомогла всім людям спільно крокувати шляхом миру!&raquo; - звернувся із закликом Вселенський Архиєрей.<br />Папа також окремо привітав організаторів та учасників походу &laquo;Мир на всіх землях&raquo;, організованого католицькою спільнотою Святого Еґідія в Римі та в 70-ьох країнах світу, серед яких також і в Україні. <br /><br />Під час зустрічі з вірними на площі святого Петра у Ватикані Святіший Отець різними мовами привітав з Новим роком та уділив перше у 2009 році Апостольське благословення</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/<br /></span></span></p>',NULL,1893,5,1,NULL,43),
(4638,'P','2009-01-01',1,NULL,'Note','Проголошення вірним УГКЦ 2009 року Божого Роком християнського покликання  з особливим наголосом на священичому покликанні','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Головною духовною темою минулого 2008 року, згідно з постановою Синоду Єпископів Української Греко-Католицької Церкви 2007 року, було християнське покликання. Щиро сподіваємося, що упродовж року, який добігає до кінця, ми всі - чи то духовенство, чи миряни, чи старші, чи молодші - справді збагатилися, усвідомивши, що кожна людина, яка народжується на світ, крім дару життя і святої віри, одержує від свого Творця особливе життєве завдання - покликання.</span></span></p>','<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Всесвітлішому і високопреподобному духовенству,<br />преподобним ченцям і черницям, дорогим братам і сестрам у Христі<br />благословення і мир у Господі!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорогі у Христі!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Головною духовною темою минулого 2008 року, згідно з постановою Синоду Єпископів Української Греко-Католицької Церкви 2007 року, було християнське покликання. Щиро сподіваємося, що упродовж року, який добігає до кінця, ми всі - чи то духовенство, чи миряни, чи старші, чи молодші - справді збагатилися, усвідомивши, що кожна людина, яка народжується на світ, крім дару життя і святої віри, одержує від свого Творця особливе життєве завдання - покликання. Надіємося також, що кожен вірний намагався пильніше розпізнати власне покликання, плекати&nbsp; та дуже сумлінно виконувати його на Божу славу, на благо своїх ближніх та, найважливіше, для зрозуміння змісту свого життя. За це ми щиро молилися, співаючи під час Святої Літургії на сугубій ектенії: <em>&laquo;Ще молимось за всіх людей, щоб дане їм Господом покликання охоче приймали, за нього Богові щиро дякували, його сумлінно та витривало сповняли та Богові й людям ним радо служили&raquo;</em>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Синод Єпископів Української Греко-Католицької Церкви, який відбувся у вересні цього року, постановив продовжити на наступний 2009 рік тему християнського покликання з наголосом на священичому покликанні і, замість вищенаведеного прохання, співати у сугубій ектенії: <strong>&laquo;Ще молимось за наших священиків, дияконів, семінаристів та за їх святе покликання&raquo;</strong>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У цьому листі до вас, дорогі у Христі, хочу ширше пояснити цю головну тему 2009 року Божого. До наших священнослужителів та семінаристів буде видане ще одне, особливе, звернення.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Передусім хочу звернути вашу увагу на те, що у своїх молитвах і роздумах ми повинні продовжувати тему християнського покликання взагалі, навіть більше, старатися ще глибше, ще ґрунтовніше її зрозуміти, а понад усе здійснювати у щоденному житті. Упродовж 2008 року ви, напевно, слухали проповіді, читали книжки, брали активну участь у різних заходах на цю тему. Такі заходи, зокрема, організовували єпархіальні та між\'єпархіальні комісії, кожна у своїй особливій ділянці (сім\'я, молодь, миряни та ін.). Кожен з вас має й надалі запитувати себе, чи гідно і боговгодно виконує дане йому покликання та чи щиро старається допомогти дітям і молоді розпізнати та здійснювати їхнє покликання. Крім цього завдання, Синод Єпископів закликає нас усіх у 2009 році звернути особливу увагу на священичі покликання. Чому? Бо для розвитку і зростання нашої Церкви потрібні добрі духовні провідники, а такими насамперед є відповідно підготовлені та призначені до цього священнослужителі. Владики запрошують усіх членів Церкви застановитися над священним характером та важливістю священичого покликання і над тим, що можемо ми, велика церковна спільнота, зробити, щоб наші священики були взірцевими священнослужителями на Божу славу і на загальне добро всієї Церкви та рідного народу. Хочемо запропонувати вам кілька думок стосовно цього аспекту духовної теми наступного року.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Коли говоримо про покликання, а головно, коли нас закликають до молитов про священичі покликання, ми зазвичай думаємо, що головним і єдиним нашим завданням є просити Господа Бога, щоб кликав достатнє число кандидатів до священичого стану, або, по-людськи кажучи, щоб наповнив наші семінарії. Вступ до семінарії, безумовно, є дуже важливим елементом, бо від нього починається здійснення священичого покликання, однак він далеко не вичерпує всього змісту покликання до священичого стану. Насамперед треба сказати, що навчанню в семінарії передують інші стадії цього покликання. Добрий душпастир часто проповідує про покликання та пильно приглядається до членів своєї громади, намагаючись розпізнати принаймні його зародок у конкретних особах і, якщо таке помітить, то його дбайливо плекає. Добра парафія це - плідна земля, яка дає гарні плоди священичих покликань. Прикладом цього може слугувати одна з наших парафій у США, в штаті Пеннсильванія, яка в 20-30-х роках минулого століття дала 37 тривалих священичих і монаших покликань. Священики, монахи і монахині, вихідці цієї громади, одноголосно вважають це заслугою тодішнього отця-пароха. Ще більше пробудженню і зростанню священичого покликання сприяє добра християнська родина, яка є немовби колискою або теплицею нових духовних покликань. У сім\'ї віруючих людей до священства ставляться з пошаною. Навіть, якщо йдеться про негідного священика, то про це говорять з болем, а не зі злобою. А якщо в сім\'ї є хлопці, це покликання розглядають як одне з кращих можливих.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Не менше від праці отців-парохів і ревної молитви церковної громади до пробудження священичих покликань спричинюється приклад героїв віри. У зв\'язку з цим пригадуємо слова св. пам\'яті Папи Івана Павла ІІ під час Божественної Літургії у Львові 27 червня 2001 року Божого. Проголошуючи 27 членів нашої Церкви блаженними мучениками, Святіший Отець звернувся до присутніх на Літургії священиків з такими словами: &laquo;Якщо Господь благословить вашу країну численними покликаннями, якщо семінарії заповнені - і в цьому є джерело надії для вашої Церкви, - то все це, без сумніву, є одним із плодів їхньої жертви. Але це стає для вас великою відповідальністю&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Не слід сумніватися, що Господь Бог, як добрий Батько і Господар, кличе достатнє число робітників на своє жниво (пор. Лк. 10, 2). Якщо гідних кандидатів все ж таки не достатньо, то часто в цьому винні ми, люди, зокрема через занепад духовного життя в наших родинах, через брак відповідної проповіді та заохочення, а також через брак пошани до священичого стану. Годі сподіватися захоплення священичим покликання у сім\'ях, для яких найбільшою цінністю є гроші, або в яких для батьків найважливішою турботою є родинні зв\'язки. Тут треба згадати і про негативний вплив, який мають сучасні засоби масової інформації, що нерідко стають носіями антихристиянських ідей та поглядів. А вже зовсім нищівним є поганий життєвий приклад якогось священика, особливо місцевого душпастиря.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ці перешкоди є дуже великими, але вони не є нездоланними. Останнім часом ми не один раз чули критику на адресу священнослужителів через їхню негідну поведінку чи поверхневе виконання священичих обов\'язків. Запитаймо себе: чи ми молилися за виправлення таких священиків, людей, які потрапили у велику духовну кризу? Наскільки ми допомагали їм вийти з такого трагічного стану? Адже священнослужитель - це не самотній острівець серед широкого моря людства. Він особливо потребує підтримки з боку людей, які довкола нього, бо ж перебуває на видноті і кожен його крок є загальновідомим. Тут не слід також недооцінювати атак ворога Церкви, який діє за відомим принципом: <em>&laquo;Уражу пастиря і розпорошаться вівці стада&raquo;</em> (пор. Зах. 13, 7; Мт. 26, 31).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Серед засобів підтримки, яку ціла церковна спільнота має виявляти своїм священнослужителям, перше місце посідає молитва. Священик існує і діє з допомогою сили молитви не тільки власної, а й молитви громади, якій він служить. Тому Синод Єпископів закликає усіх членів Церкви молитися за священиків і дияконів - не тільки в тому намірі, щоб їх було достатнє число, а й насамперед за те, щоб той, хто вступив у цей святий стан, у ньому своє покликання згідно з Божою волею прожив і свято завершив.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Віруючі люди допомагають своєму священикові. Бо перед ним стоять великі вимоги: як перед особою, як перед священнослужителем, як перед адміністратором, як перед учителем, як перед громадянином, а якщо він одружений - як перед чоловіком і батьком. Священиче життя не встелена трояндами дорога, за винятком тих випадків, коли він хоче бути лжепророком і догоджувати примхам людей, замість того, щоб вести їх до Небесного Царства. Якщо ж він старається справді подобатися Богові, то його життя є радше тернистим шляхом. На цьому тлі ми краще зрозуміємо, ким хочемо бути: фарисеями, які насміхалися з Ісуса Христа, чи киринейцем, який допомагав Йому нести хрест.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорогі у Христі! Починаємо 2009 рік - Рік християнського покликанння з особливим наголосом на священичому покликанні. Думаймо про себе, про своє покликання. Застановімося і над покликанням до священичого стану, бо всі ми хочемо бачити наших священиків взірцевими Божими слугами, зміст життя яких - вести довірені їм душі до Небесного Отця. Разом з Ісусом Христом, Первосвящеником, молімося та підтримуймо наших священиків і дияконів.</span></span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Благословення Господнє на вас!</span></span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">+ ЛЮБОМИР</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Дано у Києві, при Патріаршому соборі Воскресіння Христового, 31 грудня 2008 року</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Молитва Слуги Божого Митрополита Андрея Шептицького<br />про добре українське духовенство,<br />яку поручаємо молитися усім вірним нашої Церкви</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Господи, Господи, поглянь з неба, подивись і відвідай виноградник, який насадила правиця Твоя.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Нехай всемогутня Твоя Рука буде завжди над цим народом, що його Ти полюбив. Дай йому, Предвічний Боже, у кожному поколінні, аж до кінця світу, єпископів і священиків святих, повних Твого Духа, - пастирів й учителів Твого Закону, що вміли б незмінно зберігати правду Твого святого Об\'явлення та з любов\'ю навчати й вести цей великий нарід.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Даруй українському духовенству ласку ніколи не боятися пожертвування, де тільки йде про Твою славу й добро цього народу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Розпали в серцях українських священиків духа ревності про спасіння душ. Відкрий перед їхніми очима премудрість Твого Об\'явлення і дай їм високе почуття святості того діла, до якого Ти їх покликав. Благослови їхню працю і їхні наміри. Хорони від усякого лиха. І єднай їх Твоєю благодаттю, щоб любов\'ю були одно, - як Ти, Отче, зі Сином і Син з Тобою. Амінь.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/<br /></span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>',NULL,1887,5,1,NULL,42);
INSERT INTO `publications_publication` VALUES
(4639,'P','2009-01-01',1,NULL,'Note','Блаженніший Любомир благословив учасників ХІІ Головного з’їзду Світського інституту катехиток, який відбувається у Бразилії  ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З 26 по 31 грудня 2008 року у м. Прудентополіс (Бразилія) відбувається ХІІ Головний з\'їзд Світського інституту катехиток Серця Ісусового. Як зазначають організатори, ціллю з\'їзду є бажання застановитися над життям, харизмою, духовністю та формацією інституту.</span></span></p>','<p style=\"text-align: justify;\"><img style=\"float: right;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_huzar_vid_sulymy_sm_01.jpg.jpg\" alt=\"\" /><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З 26 по 31 грудня 2008 року у м. Прудентополіс (Бразилія) відбувається ХІІ Головний з\'їзд Світського інституту катехиток Серця Ісусового. Як зазначають організатори, ціллю з\'їзду є бажання застановитися над життям, харизмою, духовністю та формацією інституту. Важливим завданням учасників зустрічі є окреслити напрямні подальшого служіння у Христовій Церкві. Крім того, через завершення каденції попереднього керівництва, на з\'їзді буде обрано нову Головну управу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\"Дата, яку ви вибрали для проведення свого з\'їзду мені виглядає справді символічною, бо кожний головний з\'їзд будь-якого релігійного згромадження - це дуже важлива хвилина, в якій у стіп Господа - володаря історії і цілого всесвіту - робиться перевірка пройденого шляху і намічається напрям подальшої життєвої подорожі\", - написав у своєму привітанні до учасників з\'їзду Блаженніший Любомир. Успіх цих важливих актів, на переконання Глави УГКЦ, у великій мірі залежить від того, як щиро, покірно та відверто станемо перед нашим Спасителем, подякуємо Йому за всі успіхи, перепросимо за всі недомагання і попросимо про щедрі благодаті. \"Дорогі Катехитки, того всього сердечно вам бажаю. Поручаю вас опіці Пресвятої Богородиці та заступництву усіх святих, яких видала наша Церква. Як завдаток Божих благодатей, разом із владиками нашої Церкви в Бразилії, уділяю вам архиєрейське благословення\", - написав Предстоятель УГКЦ.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Департамент інформації УГКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За маьеріалами: http://www.ugcc.org.ua/</span></span></p>\r\n<p>&nbsp;</p>',NULL,1886,5,1,NULL,43),
(4640,'P','2009-01-01',1,NULL,'Note','Богослуження та молитви у Ватикані з нагоди урочистості Пресвятої Богородиці Марії та початку Нового року','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З нагоди Урочистості Пресвятої Богородиці Марії, Папа Венедикт&nbsp;XVI, очолить вечірню в базиліці Святого Петра у Ватикані, яка, традиційно, завершується Адорацією Найсвятіших Тайн та подячною молитвою <em>Te Deum</em> (<em>Тебе, Бога, хвалимо</em>) з нагоди закінчення цивільного року.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сьогодні о 18.00 місцевого часу, з нагоди Урочистості Пресвятої Богородиці Марії, Папа Венедикт&nbsp;XVI, очолить вечірню в базиліці Святого Петра у Ватикані, яка, традиційно, завершується Адорацією Найсвятіших Тайн та подячною молитвою <em>Te Deum</em> (<em>Тебе, Бога, хвалимо</em>) з нагоди закінчення цивільного року. Після богослуження Венедикт&nbsp;XVI вийде на площу Святого Петра, де помолиться перед встановленим на ній вертепом. Завтра вранці Святіший Отець очолить Службу Божу у ватиканській базиліці, а опівдні, вперше у Новому році, помолиться разом з вірними молитву &laquo;Ангел Господній&raquo;.<br /><br />О 23.30 місцевого часу, кардинал Тарчізіо Бертоне, Державний Секретар Ватикану, розпочне на площі Святого Петра всенічне молитовне чування, організоване Рухом Родинної Любові, яке триватиме до 7 години ранку. Його мета - молитися за мир в серцях, в родинах і між народами. Рух Родинної Любові, що діє у Римській дієцезії, займається душпастирством та підтримкою молодих сімей, а приналежні катехити проводять передподружні курси для наречених, приготування до Святої Тайни Хрищення, катехизують дітей. Вже вшосте цей рух організовує молитовне чування напередодні Нового року, підтримуючи молитовні намірення Папи за мир у світі.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами:http://www.radiovaticana.org/</span></span></p>',NULL,1885,5,1,NULL,43);
INSERT INTO `publications_publication` VALUES
(4641,'P','2008-12-31',1,NULL,'Note','Від кого хрестився перший володар Русі – від Константинополя чи від Риму?','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Саме з християнським іменем князя Аскольда&nbsp; пов\'язана одна з найбільших загадок&nbsp; християнства на Русі.&nbsp; Річ в тім, що згідно з церковною традицією <em>ім\'я&nbsp; при хрещенні</em>&nbsp; надавалося&nbsp; на&nbsp; честь <em>хрещеного батька</em>.&nbsp; А хрещеним батьком князя Аскольда мусила бути, поза сумнівом, високопоставлена духовна або царствена особа: мова ж бо йшла про престиж держави.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">ЗАГАДКА&nbsp; ХРИСТИЯНСЬКОГО IМЕНI КНЯЗЯ АСКОЛЬДА</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Від кого хрестився перший володар Русі - від Константинополя чи від Риму?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Задовго до князя&nbsp; Володимира, якого Православна Церква вважає Першосвятителем Русі, християнську віру прийняв князь Аскольд, що разом з братом Дiром правив у Києві&nbsp; в&nbsp; 60-80&nbsp; рр. IХ ст.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Наскільки йому&nbsp; вдалося навернути до нової віри Русь, відомо мало. Літописи повідомляють тільки, що він за міськими мурами, де нині знаменита Аскольдова могила,&nbsp; спорудив церкву. I носила вона ім\'я святого Миколи. Ґрунтуючись на досліджені&nbsp; традиції&nbsp; тодішніх володарів називати храми на честь своїх патронів, видатний&nbsp; російський історик В.М. Татiщев зробив припущення, що при хрещенні князь Аскольд отримав ім\'я: Микола. З цим погоджуються нині більшість істориків.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Проте&nbsp; саме з християнським іменем князя Аскольда&nbsp; пов\'язана одна з найбільших загадок&nbsp; християнства на Русі.&nbsp; Річ в тім, що згідно з церковною традицією <em>ім\'я&nbsp; при хрещенні</em>&nbsp; надавалося&nbsp; на&nbsp; честь <em>хрещеного батька</em>.&nbsp; А хрещеним батьком князя Аскольда мусила бути, поза сумнівом, високопоставлена духовна або царствена особа: мова ж бо йшла про престиж держави. Так,&nbsp; зокрема,&nbsp; вчинив болгарський хан Борис під час&nbsp; свого хрещення у 863 році.&nbsp; Він виставив&nbsp; умову,&nbsp; щоб його хрещеним батьком вважався візантійський цар Михайло III, щоправда -&nbsp; заочно,&nbsp; бо під час церемонії той, звичайно, не зміг бути присутнім.&nbsp; I саме на його честь болгарського хана нарекли Михайлом.&nbsp; Подобним чином наш князь Володимир Святославович вибрав хрещеним батьком грецького царя Василія II, а отже i отримав&nbsp; його&nbsp; ім\'я.&nbsp; Згодом Володимир спорудив церкву Святого Василія - теж згідно з традицією.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">А хто ж&nbsp; міг бути&nbsp; хрещеним батьком князя Аскольда i на честь кого він отримав ім\'я:&nbsp; <em>Микола</em>?&nbsp; Адже ситуація політично була надзвичайно важлива:&nbsp; уперше володар Русі приймав християнство. Якщо припустити, що Аскольда хрестили греки - під час одного з походів на Царгород, як це традиційно&nbsp; вважається,&nbsp; то кого&nbsp; -&nbsp; з іменем&nbsp; Миколи! - київський князь погодився визнати своїм хрещеним батьком?&nbsp; Аналіз показує,&nbsp; що в&nbsp; той&nbsp; період&nbsp; у Візантії&nbsp; не&nbsp; було ні імператора,&nbsp; ні кого-небудь з його найзначніших родичів, ні патріарха, хто б носив ім\'я Микола.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Але ж був папа - Микола I!.. Випадковий збіг обставин? Навряд. Відомо,&nbsp; що&nbsp; саме цей римський першосвященик проводив активну&nbsp; політику&nbsp; у&nbsp; справі хрещення слов\'ян, благословив місіонерську діяльність Костянтина (Кирила) i Методія та їхні переклади Біблії на слов\'янську мову.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Доречно привернути увагу ще до однієї загадки, пов\'язаної з хрещенням Аскольда. Традиційно вважається,&nbsp; що хрещення Русі відбулося в часи князя Володимира - не так давно Православна Церква&nbsp; святкувала&nbsp; 1000-лiт цієї&nbsp; події.&nbsp; Але чому відлік християнської ери на Русі не ведеться від хрещення князя Аскольда? Здавалося б, для престижу руської церкви якраз вигідно підкреслити, що християнство в нас має значно давнішу історію - більше&nbsp; як&nbsp; на століття. Звичайно,&nbsp; про хрещення Аскольда в історії згадується, але чомусь - побіжно, а вся слава святителя Русі приписується князеві Володимирові.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Чимало&nbsp; істориків припускають,&nbsp; що тут ховається якась таємниця.&nbsp; Єдина гіпотеза, яка напрошується, це те, що Володимир прийняв хрещення від Костантинополя, а князь Аскольд - від Риму. Тільки хрещення від Римської Церкви, що аж ніяк не імпонує престижу Східної Церкви, може пояснити причину такої упередженості.&nbsp; Мабуть, не без впливу грецьких наставників, згадки про пріоритет&nbsp; Риму&nbsp; у&nbsp; справі&nbsp; хрещення Русі намагалися відтінити розповідями про Володимирове хрещення - але уже від&nbsp; Костантинополя.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Щоправда,&nbsp; дехто може заперечити, що Великий Розкол Церкви на&nbsp; Східну&nbsp; i&nbsp; Західну відбувся пізніше - через 65 років після Володимирового хрещення.&nbsp; Насправді - то&nbsp; був&nbsp; уже&nbsp; остаточний розпад єдиної Церкви,&nbsp; а перший розкол Церкви відбувся якраз в часи князя Аскольда,&nbsp; коли в Римі первосвящеником був&nbsp; Микола I,&nbsp; а&nbsp; константинопольську кафедру очолював патріарх Фотій - чи не найосвіченіший з вищих ієрархів Східної&nbsp; Церкви.&nbsp; Саме він уперше&nbsp; чітко сформулював догматичні розбіжності між Східною I Римською Церквами. I саме з того часу між обома Церквами існує глибоке&nbsp; протистояння&nbsp; i глуха боротьба за першість.&nbsp; До речі, змагалися Рим i Константинополь&nbsp; i&nbsp; за&nbsp; право&nbsp; першим&nbsp; хрестити слов\'ян.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Без сумніву,&nbsp; через це протистояння кожна з Церков замовчувала успіхи іншої - надто,&nbsp; якщо їхні інтереси перетинались. Цілком ймовірно, подібне трапилось i з хрещенням князя Аскольда, i взагалі - з хрещенням Русі. Але&nbsp; <em>де</em>, <em>коли</em> і <em>хто </em>саме хрестив першого володаря Русі?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Як здається, одним з ключів до розгадки цієї історії може служити саме християнське ім\'я Аскольда.&nbsp; А ще - цікава згадка в одному з літописів:&nbsp; \"Їде Аскольд на болгар,&nbsp; i був&nbsp; убитий його син\".&nbsp; В іншому літописі мовиться ще й про Діра i про те, що ходили брати не на болгар,&nbsp; а на греків. Протиріччя тут немає, бо&nbsp; під греками наші літописці мали на увазі християнські народи, які дотримувалися грецького&nbsp; обряду.&nbsp; Тобто,&nbsp; літописи повідомляють про&nbsp; похід&nbsp; київських&nbsp; князів на <em>хрещених болгар</em>.&nbsp; Виникає закономірне запитання: а чи не був похід безпосередньо пов\'язаний з драматичними подіями,&nbsp; які відбувалися в Болгарії після запровадження християнства?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Болгарський&nbsp; хан Борис давно прагнув хрестити свою&nbsp; державу,&nbsp; одначе цьому опиралась численна опозиція - з тих ста чистих болгарських родів,&nbsp; яких у 679 році привів на Балкани&nbsp; хан Аспарух,&nbsp; i&nbsp; які боялися розчинитися&nbsp; серед місцевих слов\'ян в наслідок прийняття спільної віри.&nbsp; Проте під тиском обставин - поразки у війні з греками та франками, голоду від страшенної засухи i 40-денного&nbsp; землетрусу - Болгарія нарешті&nbsp; 863&nbsp; року приймає&nbsp; хрещення від Костантинополя. Та&nbsp; вже наступного року опозиція підняла&nbsp; повстання.&nbsp; Хоча досвідченому i рішучому ханову Борисовi-Михайловi вдалося його швидко i нещадно придушити:&nbsp; 52&nbsp; старі болгарські роди&nbsp; було цілком винищено, але&nbsp; це не зломило язичників.&nbsp; I бунти спалахують практично постійно.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Поза сумнівом,&nbsp; антихристиянська&nbsp; опозиція&nbsp; зверталася за допомогою до інших язичницьких держав, в тому числі i до Русі, яка кілька років перед тим, 860 року, здобула&nbsp; собі на мiжнароднiй арені гучну славу успішним i зухвалим походом&nbsp; на&nbsp; Константинополь. Мабуть, саме про вiдповiдь руських князів на пропозицію війни проти християн i повідомляє&nbsp; наш&nbsp; літопис:&nbsp; \"Де Аскольд i Дір на болгар...\" Літописець вказує i дату походу: в літо 867-е.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Та, незважаючи на допомогу союзників, болгарські язичники знову зазнали невдачі.&nbsp; Більше того,&nbsp; у цій війні загинув&nbsp; син князя Аскольда. Хоча навряд чи руська дружина була розгромлена вщент; певнiш,&nbsp; болгарський князь Борис уклав мир з росами. I цілком ймовірно,&nbsp; що однією з умов мирного договору була вимога, щоб руський князь, а можливо, i його дружина, прийняли нову віру,&nbsp; якої вже дотримувалась Болгарія.&nbsp; Таким чином усувалась би загроза, що в майбутньому руси знову підтримають болгарських непокірних язичників.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Отже, хрещення Аскольда могло відбутися в&nbsp; Болгарії,&nbsp; під час&nbsp; невдалого&nbsp; походу&nbsp; 867&nbsp; року. Тепер&nbsp; напрошується це одна цікава гіпотеза, до якої підштовхує вибір християнського iменi для руського князя.&nbsp; Якщо взяти до уваги тодішню традицію, то логічно припустити, що хрещеним батьком міг бути папа римський Микола I.&nbsp; I насамперед&nbsp; тому, що в 867 році, в Болгарії, князя Аскольда міг хрестити тільки <em>римський священик</em>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Виявляється, що 866 р. хан Борис,&nbsp;&nbsp; незадоволений тим, що патріарх Фотій не погоджувався висвятити&nbsp; для&nbsp; Болгарії&nbsp; незалежного&nbsp; архієпископа,&nbsp; прогнав грецьких священикiв i звернувся за допомогою до Риму.&nbsp; Восени того&nbsp; ж року папа Микола I прислав до Болгарії мiсiю на чолі з єпископом Форомозою Портуенським, який заново хрестив болгар.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Якщо така&nbsp; гіпотеза&nbsp; про&nbsp; хрещення&nbsp; першого володаря Русі слушна,&nbsp; то найiмовiрнiше,&nbsp; що саме єпископ Формоза i здійснив необхідний обряд.&nbsp; При цьому, звичайно, Аскольд міг забажати, щоб його хрещеним батьком вважався папа римський -&nbsp; Микола&nbsp; I: гордий князь не погодився б, щоб&nbsp; статус його хрещеного батька був нижчим, ніж у болгарського володаря.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Після повернення до Києва князь Аскольд пробував запровадити християнство на Русі. Але особливо успіху не досяг. Більше того,&nbsp; невдоволені&nbsp; язичники&nbsp; прикликали&nbsp; з Новгорода князя Олега i&nbsp; 882 року вчинили кривавий&nbsp; державний&nbsp; переворот, убивши обох князів: Аскольда i Діра.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Наостанок варто зазначити,&nbsp; що хрещення, здається, прийняв&nbsp; тільки один з братів:&nbsp; Дір так i залишився язичником.&nbsp; На користь цього припущення свідчить факт, що брати були поховані в різних місцях. Аскольда - як християнина - поховали на мiсцi збудованої ним церкви;&nbsp; таким був i пізніше&nbsp; звичай&nbsp; в руських князів.&nbsp; Діра ж,&nbsp; який так i залишився язичником,&nbsp; поховали за старим руським законом,&nbsp; найiмовiрнiше - спалили, а потім урну з прахом закопали. Літописець Нестор повідомляє, що могила Дiрова - за святою Ориною, тобто, десь за церквою святої Ірини.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Можливо, подальші історичні дослідження проллють значно більше світла на події тих&nbsp; років і на відносини київських&nbsp; князів із Болгарською державою та з Римською Церквою.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Лесь Качковський</span></span></p>',NULL,1669,5,1,NULL,5);
INSERT INTO `publications_publication` VALUES
(4642,'P','2008-12-31',0,NULL,'Note','Запрошує різдвяна Зарваниця','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Чудотворне місце у Зарваниці, це воістину той Вифлеєм, де отримують духовну радість, спокій та умиротворення серця та душі паломників. Вертеп у Зарваниці можна відвідати до празника Стрітення Господнього.</span></span></p>','<p style=\"text-align: justify;\"><img style=\"float: right;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_24-12-08_02.jpg.jpg\" alt=\"\" /><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У різдвяний час Марійський духовний центр у Зарваниці на Тернопільщині приваблює безліч паломників. Адже узимку чудотворне місце набуває особливого колориту. Тим більше, що тут спеціально облаштовують вертепи: один у підземній церкві, де відчувається особлива атмосфера різдвяної містерії, другий на площі перед собором Зарваницької Матері Божої, де паломники, а особливо діти, тішаться тим, що Свята Родина перебуває серед живих персонажів вертепного дійства, подібно як це було описано у євангельських розповідях. Тут і овечки, кролики, фазани, голуби та інша живність.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Чудотворне місце у Зарваниці, це воістину той Вифлеєм, де отримують духовну радість, спокій та умиротворення серця та душі паломників. Вертеп у Зарваниці можна відвідати до празника Стрітення Господнього.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За додатковою інформацією можна звертатися до отця Володимира Фірмана (+380673511500) або отця Володимира Топоровського (+380672747621).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">прес-секретаріат</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Тернопільсько-Зборівської єпархії</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/<br /></span></span></p>\r\n<p>&nbsp;</p>',NULL,1831,5,1,NULL,42),
(4643,'P','2008-12-31',1,NULL,'Note','НА АСКОЛЬДОВІЙ МОГИЛІ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Справа, за станцією метро &laquo;Арсенальна&raquo;, стрімко спускається доріжка до Паркової алеї. Ще донедавна такий затишний, заліснений дніпровський схил в урочищі, яке згадується в літописах, нині сплюндрований. Тепер тут будівельний майданчик, де височіє отой висотний будинок-монстр, який викликав обурення киян. Начебто його мали &laquo;усікти&raquo;, але коли часто проходжу повз і дивлюся на нього, він не зменшується і, видається, поволеньки опоряджується. Скільки тоді зрубали дерев - тінистих, могутніх, кряжистих - широчезних в обхваті! Потім швиденько викорчували ці пні, аби не нагадували про розбій серед білого дня. І ніхто із кучмівської свити нині не покараний, хоча за кордоном таке варварство в заповідній зоні розцінюється як кримінал.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Справа, за станцією метро &laquo;Арсенальна&raquo;, стрімко спускається доріжка до Паркової алеї. Ще донедавна такий затишний, заліснений дніпровський схил в урочищі, яке згадується в літописах, нині сплюндрований. Тепер тут будівельний майданчик, де височіє отой висотний будинок-монстр, який викликав обурення киян. Начебто його мали &laquo;усікти&raquo;, але коли часто проходжу повз і дивлюся на нього, він не зменшується і, видається, поволеньки опоряджується. Скільки тоді зрубали дерев - тінистих, могутніх, кряжистих - широчезних в обхваті! Потім швиденько викорчували ці пні, аби не нагадували про розбій серед білого дня. І ніхто із кучмівської свити нині не покараний, хоча за кордоном таке варварство в заповідній зоні розцінюється як кримінал.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\r\n<script type=\"text/javascript\"></script>\r\n<script src=\"http://pagead2.googlesyndication.com/pagead/show_ads.js\" type=\"text/javascript\"></script>\r\n<script type=\"text/javascript\"><!--\r\nwindow.google_render_ad();\r\n// --></script>\r\n</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Якщо йти далі Парковою алеєю - зліва Зелений театр, а на горі церква Святого Миколая. Це місце віддавна облюбували художники: згадаймо хоча б акварель &laquo;Аскольдова могила&raquo; Тараса Шевченка (1846 р.), літографії та картини багатьох митців. Серед них Іван Їжакевич теж намалював картину &laquo;Могила Аскольда&raquo;. На ній - занесене снігом урочище; на передньому плані видніється цвинтар, будиночок священика. Оддалік міст над скутим кригою Дніпром. І в центрі - рожева церковця-ротонда з колонадою.<br />А який тут рай був улітку, можна уявити, прочитавши дожовтневий довідник М. Захарченка, інспектора класів Інституту шляхетних дівчат: &laquo;Довкола церкви, спускаючись уступами майже до Дніпра простелився величезний квітник із клумбами тінистих дерев, чарівних троянд та інших пахучих квітів. Серед буйних газонів виднілися надгробні пам\'ятники з білого мармуру. Вся Аскольдова могила обведена кам\'яною огорожею. Із боку Миколаївського узвозу тут збудовані в давньому стилі кам\'яні ворота і невеличкий кам\'яний будиночок для церковного причту&raquo;.<br />Унікальність цього цвинтаря створював не лише мальовничий ландшафт. У цьому урочищі відбулася зустріч київських князів Діра та Аскольда з варязьким вікінгом Олегом, який їх убив. Тіла князів, за свідченням літописів, &laquo;погребоша на горі, єже ся нині зовет Угорскоє&raquo;. За переказами, князь Аскольд охрестився в Цареграді у 860 році разом із частиною своєї дружини і дістав християнське ім\'я Микола. Через 50 років на цьому місці велика княгиня Ольга побудувала дерев\'яну церкву Святого Миколи, куди потім перенесли з Вишгорода її останки (пізніше князь Володимир перепоховав її в Десятинній церкві).<br />Близько 1036 року тут заснували Микільський жіночий монастир, де, як припускають історики, провела свої останні дні мати преподобного Феодосія Печерського. А в 1690 році за кошти гетьмана України Івана Мазепи було збудовано п\'ятибаневу кам\'яну церкву з хорами, дзвіницею - Військово-Микільський собор. Сюди перевели більшу частину братії сусіднього монастиря. На жаль, Мазепина церква до наших днів не збереглася, її зруйнували більшовики (на цьому місці стоїть колишній палац піонерів). Стара ж дерев\'яна церква на Аскольдовій могилі залишилася при цвинтарі, який із 1786 року став загальноміським.<br />У 1810 році головний архітектор Києва, відомий зодчий Андрій Меленський звів невелику кам\'яну церкву-ротонду на два яруси і престол у стилі ампір з усипальницею під нею. Храм славився прекрасним іконостасом із каррарського мармуру та золота, розписами за ескізами художника В. Васнецова, чудовою оранжереєю, за якою наглядав ієромонах Рафаїл, умілий садівник. Це він розпланував доріжки, дев\'ять терас для поховань, що спускалися каскадом, побудував сходи.<br />Цвинтар став одним із найупорядкованіших у Києві.<br />Тут були могили відомих у Києві письменників, політичних, громадських, духовних діячів. На Аскольдовій могилі упокоївся Василь Федорович Симиренко, багатий українець-промисловець, який протягом тривалого часу матеріально підтримував видання &laquo;Киевская старина&raquo;, &laquo;Рада&raquo;, &laquo;Громадська думка&raquo;, &laquo;Літературно-науковий вісник&raquo;. Його коштом вийшло кілька видань Шевченкового &laquo;Кобзаря&raquo;. Василь Федорович усе своє життя пунктуально виділяв десятину від своїх прибутків на розвиток української культури. Він матеріально підтримував і заохочував працю М. Драгоманова, М. Коцюбинського, М. Грушевського, фінансував хор М. Лисенка, не відмовляв у коштах багатьом діячам культури. &laquo;Кожен мусить робити те, до чого вдатний, - пояснював Василь Федорович. - Вони тямляться на громадських справах, то нехай їх і роблять, а я тямлю заробляти гроші, то і мушу їх заробляти для України&raquo;. <br />Крім почесного громадянина Києва Василя Симиренка, на Аскольдовій могилі поховано Єпіфанія, архімандрита Микільсько-Пустинно-Слупського монастиря, Г. Петрова, директора училищ Київської губернії, М. Юзефовича, поета, архівіста, який очолював Київську археологічну комісію, що видала &laquo;Літопис Граб\'янки&raquo;, П. Андрієвського, редактора київської газети &laquo;Зоря&raquo;, та багато інших достойних людей.<br />На цьому цвинтарі знайшли свій останній притулок також родичі відомих письменників: Е. Гулак-Артемовський, Д. Кантемир, М. Лєскова, А. Маркович, М. Подолинська, Ф. Рилєєв. Крім того, тут поховані учасники воєн - 1812 року, Кримської та російсько-турецької 1853-1856 років.<br />І ще багато років тихо минав би час, спокійно спливали б дні над цією чарівною місциною, де люди творили молитву Богові та думали про упокій душі. Та ось настав 1917 рік, і на цей некрополь чигала одна біда за одною. У березні 1918 року на цвинтарі з\'явилася свята могила - братське поховання Героїв Крут, які загинули в нерівному бою з червоною російською бандою Муравйова. Знаменитий хор Олександра Кошиця відспівав панахиду над 30-ма київськими студентами. Відгукнувся на цю подію й поет Павло Тичина: &laquo;На Аскольдовій могилі поховали їх&raquo;... Сьогодні про цих юнаків нагадує дерев\'яний хрест на символічній могилі біля церкви. Очевидно, вже давно настав час спорудити Героям Крут пам\'ятник.<br />У 1919 році за більшовицької влади колегія комунгоспу вирішила закрити цвинтар: мовляв, він переповнений, хоча місця було ще доволі. <br />Час від часу за клопотаннями родичів тут ще ховали киян. Але в 1934 році, мов смерч, змела, сплюндрувала бандитська влада це кладовище, зрештою, як і тисячі інших. Причину знайшли таку - цвинтар мав стати амфітеатром, звідки глядачі могли б спостерігати за святом на Дніпрі.<br />Зберігся документ того часу - наказ комісара освіти УРСР В. Затонського голові міськради Гінзбургу, де, зокрема, зазначалося: &laquo;Аскольдову могилу треба як кладовище ліквідувати, церкву закрити. Надгробні пам\'ятки використати як матеріал для будівель та оформлення парків тощо. Бажано, щоби мармур у великих кусках було використано як матеріал для скульптур, а то, приміром, не могли донедавна знайти шматка мармуру, щоби замовити погруддя вождів революції&raquo;.<br />Очевидець цього вандалізму заслужений архітектор, Герой України Олесь Силин розповідав мені, як стояли тоді на Аскольдовій могилі каменедробарки і трощили надгробки на друзки. Хоча довкола була така краса - гармонія вічного супокою в стародавньому урочищі над Дніпром, білосніжність мармурових скульптур, витончене мереживо чавунних ґрат. Стягували з постаментів &laquo;буржуев всех мастей&raquo;, а це були київська професура, духівництво, меценати, актори, письменники, лікарі, інженери, журналісти - відомі й шановані люди. Плити з їхніх могил ішли на бордюри тротуарів Хрещатика та колишньої вулиці Кірова.<br />Цвинтар облаштували під парк відпочинку, а церкву, знявши з неї баню з хрестом - під парковий павільйон. &laquo;Помилували&raquo; лише акторів М. Соловцова й М. Глєбову, льотчиків П. Нестерова й С. Крутеня, колекціонера старожитностей В. Тарновського, громадського діяча С. Подолинського, могили деяких родичів киян, які було перенесено на інші кладовища. Не стало 2000 поховань на Аскольдовій могилі. На жаль, і досі немає бодай якоїсь скромної таблички, яка б нагадувала, що на цих кручах існував один із найгарніших в Європі і найпрестижніших у Києві цвинтарів.<br />Під час Другої світової війни на Аскольдовій могилі німці ховали своїх солдатів та офіцерів. Їхні могили в 1944 році було знищено. А з 1945 року праворуч від церкви вже ховали радянських воїнів: звідси власне й починається Парк вічної слави.<br />Пам\'ять про некрополь із часом стала пригасати. Хіба що старожили пам\'ятали, якої дивовижної краси він був. Деяка надія на його відродження заяскріла після відкриття Музею історії Києва в 1979 році. Аскольдова могила стала його філією, парковим павільйоном. Тоді ротонду сяк-так відремонтували і в ній почали влаштовувати сезонні виставки. А в 1985 році працівники Музею історії Києва оголосили конкурс на кращий проект реставрації. Журі відібрало проект, за яким пропонували відновити церкву в тому вигляді, як її спорудив А. Меленський, збудувати одноповерховий будинок священика з брамою та дзвіницею. У підвалі церкви святого Миколая (а це єдина вціліла споруда Микільсько-Пустинно-Слупського монастиря) мала розміститися&nbsp; експозиція, яка б ознайомлювала відвідувачів із історією цього некрополя. Проект передбачав також відгородити невеличку частину території - ті тераси, де були поховані відомі діячі науки та культури, і поставити символічні знаки.<br />Але на відтворення і реставрацію цього музейного комплексу &laquo;Історична місцевість Аскольдова могила&raquo; не було коштів, проект завис у повітрі, а церква руйнувалася, облітав тиньк зі стін, скрізь розповзався грибок. І ось після тривалої тяганини, опору влади нарешті у ротонді почала проводити богослужіння греко-католицька конфесія, тоді єдина в Києві, що була безпритульною. Нарешті після зволікань міськдержадміністрація зробила в церкві ремонт, зібрали на це гроші також віруючі. <br />...Нині над цією оригінальною церковцею-ротондою, як і колись, височіє хрест. При вході до храму - меморіальна дошка із зображенням Папи Івана-Павла ІІ, який під час свого візиту до України відвідав Аскольдову могилу.<br />У Києві тепер збудовано і відновлено багато храмів. І однак з усіх кінців міста сюди приїжджають віруючі Української Греко-Католицької церкви. Часто стоїмо ми впритул або ж на сходах чи на подвір\'ї, бо приміщення церкви зовсім невеличке. Видно, якийсь магнетизм, особливу ауру випромінює це намолене місце, бо, побувавши тут, іншими очима дивишся на довколишній світ, на цю суєту суєт і розумієш: то все - земна марнота, вічною є тільки дорога до Всевишнього.<br /><br />Ольга МЕЛЬНИК</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://ukrgazeta.plus.org.ua/</span></span></p>',NULL,2112,5,1,NULL,12);
INSERT INTO `publications_publication` VALUES
(4644,'P','2008-12-30',1,NULL,'Rss',' Слово від Редакції','<p style=\"text-align: center;\"><strong><img src=\"http://www.oranta.org/images/stories/2008/11/christmasmanger_top1.jpg\" style=\"float: right;\" /> Христос рождається!</strong></p>\n<p style=\"text-align: center;\"><strong>Славімо його!</strong></p>\n<p>Дорогі парафіяни, читачі наших інтернет сторінок «Оранта» та «Аскольдова Могила» прийміть найщиріші вітання з Новим 2009 роком Божим, Різдвом Христовим і святим Богоявленням.</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=167:-slovo-vid-redakciji&catid=31:kolonka-redaktora-cat&Itemid=1',1884,NULL,1,1,26),
(4645,'P','2008-12-30',1,NULL,'Note','Конґо: брутальне вбивство в католицькому храмі','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">45 осіб брутально вбито в католицькому храмі на півночі країни. Трагедія сталась 26 грудня недалеко від Дорума, 40 кілометрів від кордону з Суданом та Уґандою. Жертвами стали, переважно, жінки, діти і похилі віком, яких у церкві порубано мачетами. Досі невідома точна кількість жертв. Військо звинувачує у злочині так звану Армію Опору Господа і твердить, що кількість вбитих перевищує 200 осіб.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">2008 рік не закінчується спокійно. Він позначений багатьма драматичними ситуаціями, як наприклад у Святій Землі, на Близькому Сході, в Африці, криваве переслідування християн в Індії. До цього додаються повідомлення про трагічну ситуацію в Демократичній Республіці Конґо. <br /><br />Принаймі 45 осіб брутально вбито в католицькому храмі на півночі країни. Трагедія сталась 26 грудня недалеко від Дорума, 40 кілометрів від кордону з Суданом та Уґандою. Жертвами стали, переважно, жінки, діти і похилі віком, яких у церкві порубано мачетами. Досі невідома точна кількість жертв. Військо звинувачує у злочині так звану Армію Опору Господа і твердить, що кількість вбитих перевищує 200 осіб.<br /><br />У п\'ятницю 26 грудня після молитви &laquo;Ангел Господній&raquo; Венедикт&nbsp;XVI згадав трагедію викрадених заручників в Латинській Америці, Африці та на Близькому Сході, зокрема, закликаючи молитись за двох католицьких монахинь, майже 2 місяці тому викрадених озброєними угрупуваннями на півночі Кенії.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,1882,5,1,NULL,43),
(4646,'P','2008-12-30',1,NULL,'Note','Різдвяні реколекції у Прилуцькій виховній колонії  ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Реколекції відбувалися у тамтешньому клубі, який нічим не різнився від звичайного, крім особливих глядачів. На початку отець трохи розповів хлопцям про нас і нашу програму. Духовні науки давав отець Олександр, досвідчений монах Студійського Уставу. Про піст, про Божу любов і народженого Ісуса, який нас ніколи не забуває, про виховання власної волі - всі ці слова запали в серце не лише засудженим.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">У Прилуцькій виховній колонії для неповнолітніх відбулися різдвяні реколекції. Їх організував диякон Костянтин Пантелей, разом із о. Олександром (Приліпом) та молоддю із товариства &laquo;Обнова-Київ&raquo;.</span></span></p>\r\n<p><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">До порогу...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Перш ніж переступити поріг колонії, всі ми здолали дорогу, і я не маю на увазі лише ці фізичні кілометри чи години, хочеться сказати шлях, який ми пройшли. Більшість із нас до колонії їхали вперше, а це значить - пережили щось, чого не переживали раніше і торкнулися того, з чим раніше не стикалися.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Пропозицію поїхати у цей заклад ми сприйняли легко, адже звикли бувати у різноманітних соціальних будинках. На автобусній станції ти бачиш гурт однодумців і почуваєшся, як звично. Привітання, усмішки, жарти, що зігрівають у цю холодну погоду і ось ти вже прямуєш у автобусі до Прилук.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">По дорозі ми докладно розпитуємо о.-д. Костянтина про українські виховні колонії, про злочини, які переважно скоюють неповнолітні, про їхній вік. Отець Костянтин опікується тюремним служінням в УГКЦ і про тюрми знає багато, він розповідає нюанси тамтешнього життя. Найбільше запам\'яталася фраза: &laquo;Вони так тішаться, коли до них приїжджають&raquo;. Подруга Іра згадує, як колись у Прилуцьку колонію їздив, уже покійний письменник Юрко Покальчук. Він навіть видавав разом із засудженими літературний журнал, допомагав влаштувати їхнє життя після виходу із колонії. Напередодні отець-диякон показував нам фільм про колонію, який робив письменник.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">В автобусі також обговорюємо цитати Святого Письма для різдвяного часу. Після духовних настанов отця Олександра, ми працюватимемо із підліткам у групах, будемо обмінюватись думками.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">За огорожею</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">І ось переступаємо поріг Прилуцької колонії. Вона мало нагадує в\'язницю із нашої уяви, швидше схожа на табір. Сьогодні тут перебуває 140 хлопців, середній вік яких 17 років, найстаршим - 22. Наші документи ретельно перевіряють і лише тепер справді відчуваємо поняття &laquo;за огорожею&raquo;. Вже коли ми наближаємось до хлопців, що стоять здалеку, несподівано кажу: &laquo;я не знаю, як це буде.... напевно я не готова&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;- &laquo;Про це треба було раніше подумати, - каже подруга, - не переймайся все буде гаразд&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Реколекції відбувалися у тамтешньому клубі, який нічим не різнився від звичайного, крім особливих глядачів. На початку отець трохи розповів хлопцям про нас і нашу програму. Духовні науки давав отець Олександр, досвідчений монах Студійського Уставу. Про піст, про Божу любов і народженого Ісуса, який нас ніколи не забуває, про виховання власної волі - всі ці слова запали в серце не лише засудженим.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Напередодні Різдва наше серце має відродитися знову, очистившись від злих помислів і наповнившись радістю народження Спасителя. Під час цього посту ми очікуємо на особливий вечір перед Різдвом, коли за столом збирається вся родина і у кожній шибці горять гірлянди. За столом ми перепрошуємо рідних за завдані шкоди, бажаємо щастя в новому році, і разом колядуємо. А зранку всією родиною йдемо в Церкву.... наше Різдво таке не схоже на тюремне. Різдво принесло зміни для людства і зміни у нашому житті, є точкою відліку, а отже супроводжується відвідними символами і дійствами. Для багатьох засуджених є лише точка відліку, від якої збігає наданий їм строк і інша точка - день коли мають покинути колонію.&nbsp; Передріздвяні духовні настанови мають звернути їхні серця у бік світлого, запалити те внутрішнє світло, яке замінить зовнішні ялинкові вогні. &laquo;Виховання міцної сили волі, здатності опиратися злу, відбудеться в одну мить. Це приходить з часом і є результатом наполегливої праці&raquo; - каже отець Олександр.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Досвід праці в групах не був для нас новим, але цього разу все було іншим. &laquo;Привіт, мене звати Таня і люблю займатися спортом. А ти?&raquo;&nbsp; ... так розпочалося обговорення в маленьких групах цитат із Святого Письма. Перша розмова не завжди легка і не відразу щира. І в той момент нас об\'єднував не спільний духовний досвід, а наша молодість. Ми всі маємо однаково сильні бажання і великі мрії, шукаємо надійну опору і глибоко в серці потребуємо Бога. А ще ми маємо спогади... тут не просто говорити про Бога, як люблячого батька, бо не у всіх малолітніх засуджених батько був люблячим. І не просто розпочати із фрази: &laquo;Згадаймо як у дитинстві...&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Напевне в цьому випадку найважливішим було просто спілкування. Ми ділилися власними переживаннями, важкими моментами та думками про Бога. І наші початкові хвилювання зникли... В якийсь момент раптом почули: &laquo;Пора уже закінчувати! - ні дайте нам ще п\'ять хвилин&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">На завершення хлопці висловили свої враження від праці в групах. Кожна групка мала іншу тему, інші роздуми, інші спогади. Та всі вони були тими маленькими кроками які наближали до різдва серця.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Тетяна Шпайхер</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>',NULL,1881,5,1,NULL,42);
INSERT INTO `publications_publication` VALUES
(4647,'N','2008-12-30',1,NULL,'Rss','«Отець Кирило Селецький – це великий святець; особа, якою я постійно захоплююсь»','<p><strong> Інтерв\'ю з с. Маркіяною (Василишин) про о. Кирила Селецького, беатифікаційний процес якого незабаром має розпочатися</strong></p>\r\n<p><em><img style=\"float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_seletskyj.jpg.jpg\" alt=\"\" />Історія України багата й постійно готує нам нові подарунки. В ній виринають забуті імена, щоб наповнити нашу сучасність новим змістом. Серед них в Українській Греко-Католицкій Церкві постало ім\'я отця Кирила Селецького (1835-1918) - душпастиря, письменника, просвітителя, якого з ласки Божої ми можемо невдовзі побачити серед сонму святих, як ще одного небесного заступника нашого народу. До вашої уваги інтерв\'ю із сестрою Згромадження Святого Йосифа Обручника Пречистої Діви Марії Маркіяною (Василишин) щодо особи о. Кирила Селецького та беатифікаційного процесу, який незабаром має розпочатися.</em></p>\r\n<p><strong>Чим заслужив отець Кирило Селецький любов та повагу українського чернецтва?</strong></p>\r\n<p>Отець Кирило Селецький був непересічною людиною в нашій Церкві. Він був відважним новатором; був не лише активним громадським діячем, душпастирем і народним письменником - але й став засновником двох жіночих чернечих Згромаджень: сестер-служебниць Непорочної Діви Марії та сестер св. Йосифа Обручника Пречистої Діви Марії, тобто був духовним батьком багатьох черниць. Заснування цих двох Згромаджень було надзвичайним поступом нашої Церкви, адже Українська Греко-Католицька Церква не мала жіночих монастирів, які б вели активне апостольське життя та служіння, допомагали парохам. Отець Кирило прагнув, щоб до монастиря вступали не тільки ті дівчата, які мали освіту (тоді вимагали принаймі шість класів, що унеможливлювало вступ до монастиря сільських дівчат) та посаг. За його переконанням, коли дівчина відчуває покликання, має відповісти на нього, незважаючи на те багата вона чи бідна. Вступаючи до Згромадження, дівчина має принести розум і серце, наповнене любов\'ю до Христа, а не гроші. Жертва її сил та відданості Згромадженню - це найбільше &laquo;віно&raquo; (посаг).</p>\r\n<p>&nbsp;</p>','','http://www.oranta.org/index.php?option=com_content&view=article&id=166:lotec-kyrylo-selecky--ce-velyky-svjatec-osoba-jakoju-ja-postino-zahopljujusr&catid=35:istorychni-postati-cat&Itemid=48',1880,NULL,1,1,26),
(4648,'P','2008-12-30',1,NULL,'Rss','«Отець Кирило Селецький – це великий святець; особа, якою я постійно захоплююсь»','<strong> Інтерв\'ю з с. Маркіяною (Василишин) про о. Кирила Селецького, беатифікаційний процес якого незабаром має розпочатися</strong>\n<p><em><img style=\"float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_seletskyj.jpg.jpg\" />Історія України багата й постійно готує нам нові подарунки. В ній виринають забуті імена, щоб наповнити нашу сучасність новим змістом. Серед них в Українській Греко-Католицкій Церкві постало ім\'я отця Кирила Селецького (1835-1918) - душпастиря, письменника, просвітителя, якого з ласки Божої ми можемо невдовзі побачити серед сонму святих, як ще одного небесного заступника нашого народу. До вашої уваги інтерв\'ю із сестрою Згромадження Святого Йосифа Обручника Пречистої Діви Марії Маркіяною (Василишин) щодо особи о. Кирила Селецького та беатифікаційного процесу, який незабаром має розпочатися.</em></p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=166:lotec-kyrylo-selecky-ce-velyky-svjatec-osoba-jakoju-ja-postino-zahopljujusr&catid=35:istorychni-postati-cat&Itemid=48',1883,NULL,1,1,26);
INSERT INTO `publications_publication` VALUES
(4649,'P','2008-12-29',1,NULL,'Note','Щомісячне послання Пресвятої Діви Марії, Меджугоріє (Боснія і Герцоговина) ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Дорогі діти! Ви бігаєте, працюєте, полагоджуєте справи - та все без благословення. Ви не молитеся! Сьогодні я закликаю вас зупинитися перед яслами і розважити про Ісуса (Котрого я також вам даю сьогодні) аби Він дав вам благословення і допоміг вам усвідомити, що без Нього у вас немає майбутнього. Тому, малі мої діточки, передайте своє життя в Ісусові руки, нехай Він провадить вас і захищає від усього злого. Дякую вам за те, що відповідаєте на мої прохання.&raquo;</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">дане візіонерці Марії 25 грудня 2008 року   <br /><br />&laquo;Дорогі діти! Ви бігаєте, працюєте, полагоджуєте справи - та все без   благословення. Ви не молитеся! Сьогодні я закликаю вас зупинитися перед   яслами і розважити про Ісуса (Котрого я також вам даю сьогодні) аби Він дав вам благословення і допоміг вам усвідомити, що без Нього у вас немає   майбутнього. Тому, малі мої діточки, передайте своє життя в Ісусові руки,   нехай Він провадить вас і захищає від усього злого. Дякую вам за те, що   відповідаєте на мої прохання.&raquo;   <br /><br />дане візіонерові Якову під час щорічного Різдвяного явлення   <br /><br />&laquo;Дорогі Діти! Сьогодні у такий особливий спосіб я закликаю вас молитися за   мир. Без Господа ви не можете мати миру, ані жити у мирі. Тому, малі   діточки, сьогодні у цей день милосердя відкрийте свої серця для Царя Миру, аби Він народився у вас і подарував вам свій мир; і ви будете носіями миру   у цьому світі, де його так бракує. Дякую, що відповідаєте на мої прохання.&raquo;</span></span></p>',NULL,1879,5,1,NULL,43),
(4650,'P','2008-12-29',1,NULL,'Note','Щорічна європейська зустріч молоді, організована екуменічною монашою спільнотою з Тезе (Taizé) у Франції','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">31-ша з черги Європейська зустріч молоді є одним із етапів &laquo;Паломництва довіри на землі&raquo;, яке спільнота з Тезе розпочала 1980 року і це духовне паломництво має на меті вести кожного до глибшої віри в Христа і до активнішої участі у церковному житті.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Брюссель, столиця Бельгії, приймає коло сорока тисяч молодих людей з усієї Європи та представників інших континентів світу, які з 29 грудня до 2 січня беруть участь у щорічній зустрічі європейської молоді, яку організовує екуменічна монаша спільнота з Тезе (Taiz&eacute;) у Франції. 31-ша з черги Європейська зустріч молоді є одним із етапів &laquo;Паломництва довіри на землі&raquo;, яке спільнота з Тезе розпочала 1980 року і це духовне паломництво має на меті вести кожного до глибшої віри в Христа і до активнішої участі у церковному житті.<br /><br />Ця екуменічна монаша спільнота була заснована 1940 року в селищі Тезе у Франції братом Роже Шутцом, що був протестантського віровизнання і який трагічно загинув 2005 року у віці 90 років. Спільнота тепер складається із приблизно ста братів понад 25 національностей, католиків та членів різних протестантських Церков.<br /><br />У Тезе щотижня відбуваються багатотисячні молодіжні зустрічі, а напередодні Нового Року, відзначення якого в усьому світі пов\'язане з масовими гуляннями, ось уже понад 30 років брати-монахи з Тезе організовують для юнаків і дівчат зовсім інакшу, молитовну зустріч. Кожного року вона проходить в іншій країні і збирає десятки тисяч молоді віком до тридцяти років. Такі молодіжні зустрічі проводяться також і на інших континентах світу. Наприклад, у листопаді цього року молодіжна зустріч проходила в столиці Кенії, місті Найробі. Вона пройшла під знаком примирення, зібравши коло семи тисяч учасників з різних африканських країн та різних етнічних груп Кенії.<br /><br />Ці зустрічі є екуменічними, адже молодь, яка на них збирається, належить до різних християнських Церков. Не бракує молодих людей також і інших релігій. Молоді християни різних віровизнань спільно моляться за мир, дискутують у молодіжних групах, які проводять брати із з Тезе, пізнають одні одних. Молодь проживає у родинах, школах, парафіяльних приміщеннях. Християнські громади столиці Бельгії та околиць доклали багато зусиль, щоб приготувати для кожного учасника теплий прийом. <br /><br />Понад 10 років у цих зустрічах бере участь також і греко-католицька та римо-католицька молодь України, а до них приєднуються також і православні. Учасники молодіжної паломницької подорожі до Брюсселю заздалегідь виїхали з різних єпархії та дієцезій України, щоб на 29 грудня прибути до Брюсселю. <br /><br />Цьогорічна зустріч у зустріч у Брюсселі проходить на тему: &laquo;Будувати Європу довір\'я&raquo;. Європейська молодь радо відповіла на заклик прийти до джерел Євангелія через молитву, тишу та роздумування. Кожен, вперше чи наново, може відкрити для себе сенс свого життя, знайти рушійну силу, яка допоможе йти шляхом довіри: довіри між людьми, довіри між християнами різних Церков, народами, довір\'я до Бога. Молоді люди ставляться до своєї участі в міжнародній європейській зустрічі не як до канікул, а як до зустрічі з Христом та ближніми. Завдяки спілкуванню та гостинності долаються бар\'єри та розбіжності, стає зрозумілішою дійсність, що все людство творить одну сім\'ю і всі є дітьми одного Бога.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,1877,5,1,NULL,43);
INSERT INTO `publications_publication` VALUES
(4651,'P','2008-12-29',1,NULL,'Note','Звернення Папи Венедикта XVI до учасників молодіжної екуменічної зустрічі в Брюсселі','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ви шукаєте джерел надії для себе самих і для всього світу, - пише Папа, - відкриваючи себе Христові в молитві і слуханні Його слова, а також ділячись з молодими людьми з усією Європи та інших континентів своїми прагненнями, переживаннями життя у Церкві, як місці сопричастя та дружби з усіма.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Святіший Отець Венедикт&nbsp;XVI звернувся до молодих учасників європейської екуменічної зустрічі, організованої екуменічною монашою спільнотою з Тезе (Taiz&eacute;) у Франції, яка розпочалась у Брюсселі 29 грудня 2008 року і триватиме до 2 січня 2009 року, із словами заохоти та благословення. У зверненні, яке в імені Папи надіслав Державний Секретар Ватикану кардинал Тарчізіо Бертоне, висловлюється особлива духовна близькість Вселенського Архиєрея. Святіший Отець пропонує молодим людям запитати самих себе: з якого джерела черпаємо життя, чим ми живемо? Ви шукаєте джерел надії для себе самих і для всього світу, - пише Папа, - відкриваючи себе Христові в молитві і слуханні Його слова, а також ділячись з молодими людьми з усією Європи та інших континентів своїми прагненнями, переживаннями життя у Церкві, як місці сопричастя та дружби з усіма.<br />&laquo;Папа поряд із вами під час вашого &laquo;паломництва довір\'я землею&raquo;, - читаємо далі у посланні Венедикта&nbsp;XVI, - яке багато років тому розпочав дорогий брат Роже. Папа довіряє вам відкрити нові шляхи передавання надії тим, хто поруч із вами, через особисту життєву участь там, де надто багато убогості, несправедливості і конфліктів. Бог потребує вашої віри, креативності, духа ініціативи. Для того, щоб ви змогли відповісти на Його заклик, Він дарує вам Свого Духа. Святий Дух відновить ваші сили, коли надійде втома чи знеохочення&raquo;.<br />Ввіряючи учасників молодіжної зустрічі заступництву Пречистої Діви Марії, Матері віруючих, Святіший Отець Венедикт&nbsp;XVI щиро уділяє їм сповнене любов\'ю Апостольське благословення, а також братам з Тезе, всім, хто організував це паломництво, пастирям та вірним, які їх приймають, а також і їхнім сім\'ям.<br />Окремі послання надіслали також глава Англіканської Церкви, архиєпископ Кентерберійський Роуен Вільямс, Генеральний Секретар Всесвітньої Лютеранської федерації Ішмаель Ноко, Генеральний Секретар ООН Пан Кі Мун, Президент Єврокомісії Мануел Баррозо та християнські Церкви Женеви, де минулого року проходила ця зустріч.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,1876,5,1,NULL,43),
(4652,'P','2008-12-29',1,NULL,'Note','Папа Венедикт XVI закликає до миру у Святій Землі','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Дорогі брати й сестри, Свята Земля, яка у різдвяному періоді є в центрі думок та почуттів вірних кожної частини світу, знову стурбована вибухом нечуваного насильства. Я глибоко засмучений з приводу померлих, поранених, матеріальної шкоди, страждань і сліз населення, що стало жертвою цього трагічного повторення атак та утисків...</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">28 грудня 2008&nbsp;р.,після проказування разом із вірними, що зібралися на площі Святого Петра у Ватикані, недільної молитви &laquo;Ангел Господній&raquo;, Папа Венедикт&nbsp;XVI звернувся із закликом щодо напруженої ситуації у Святій Землі, де нещодавно відновився збройний конфлікт між ізраїльським урядом та бойовиками &laquo;Хамасу&raquo;. Святіший Отець сказав:<br /><br />&laquo;Дорогі брати й сестри, Свята Земля, яка у різдвяному періоді є в центрі думок та почуттів вірних кожної частини світу, знову стурбована вибухом нечуваного насильства. Я глибоко засмучений з приводу померлих, поранених, матеріальної шкоди, страждань і сліз населення, що стало жертвою цього трагічного повторення атак та утисків. Земна батьківщина Ісуса не може далі бути свідком того великого кровопролиття, яке без кінця повторюється! Благаю припинити це насильство, яке повинно бути засуджене в кожному його прояві, і відновити перемир\'я у секторі Газа; прошу прояву гуманності і мудрості з боку всіх тих, які є відповідальними за цю ситуацію, прошу міжнародну спільноту не залишати невипробуваним нічого з того, що могло б допомогти ізраїльтянам та палестинцям вийти з цього замкненого кола і не приєднуватися до злобної логіки насильства, як я згадував два дні тому у посланні Urbi et Orbi, але надавати перевагу шляхові діалогу та переговорів. Довірмо Ісусові, Князеві Миру, нашу гарячу молитву в цих наміреннях, і звертаючись до Нього, пресвятої Марії та святого Йосифа, промовмо: \"Пресвята Родино з Назарету, що досвідчила страждань, подай світові мир\". Подай його сьогодні, передовсім, Святій Землі!&raquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,1875,5,1,NULL,43);
INSERT INTO `publications_publication` VALUES
(4653,'P','2008-12-29',1,NULL,'Note','Сестри Служебниці вшановують своїх попередниць  ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У неділю, 28 грудня 2008 року, Сестри Служебниці Непорочної Діви Марії мали нагоду пережити події, які для Церкви та Згромадження стали пам\'ятними й знаменними. Сестри спогадували своїх попередниць, котрі в часи переслідувань та політичних репресій зуміли зберегти віру та вірність своїм чернечим обітам; котрі своїм тихим та жертвенним служінням в різний спосіб наближали знаменну подію виходу УГКЦ з підпілля.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У неділю, 28 грудня 2008 року, Сестри Служебниці Непорочної Діви Марії мали нагоду пережити події, які для Церкви та Згромадження стали пам\'ятними й знаменними. Сестри спогадували своїх попередниць, котрі в часи переслідувань та політичних репресій зуміли зберегти віру та вірність своїм чернечим обітам; котрі своїм тихим та жертвенним служінням в різний спосіб наближали знаменну подію виходу УГКЦ з підпілля.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Свято розпочалося Архиєрейською Божественною Літургією, яку у каплиці Провінційного дому, що у м. Львові, відслужив Преосвященний Владика Гліб (Лончина). У проповіді Владика Гліб підкреслив, що важливо є пам\'ятати про минулі події історії та заохотив присутніх до вдячності Богові за вірність старших сестер, які зуміли зберегти свою віру та передати її молодим сестрам.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Гостями свята були п. Василь Косів, заступник Львівського міського голови з питань гуманітарної політики, п. Наталя Федорович, начальник управління соціального захисту, родичі, приятелі та добродії Сестер Служебниць, які в часи гонінь Церкви підтримували сестер.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Старші сестри - Порфирія Ґрень, Дарія Градюк та Мирона Склянка, як живі світичі історії, ділилися цікавими спогадами про минуле, про те, як Господь давав мудрість і силу виходити з трудних життєвих ситуацій.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пан Василь Косів висловив Сестрам Служебницям подяку від імені Львівського міського голови за їхню працю з дітьми, молоддю, хворими, за добрий приклад безкорисливого служіння, а також підкреслив, як багато можуть навчитися від сестер миряни.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пані Наталя Федорович заохотила сестер ще більше працювати з дітьми та молоддю, використовуючи ефективні сучасні методи, технічні засоби, зустрічі, щоб дійти до їхнього серця і привести їх до Христа, щоб передати їм пам\'ять минулого, виховуючи їх до добра та відповідальності.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Відтак усі присутні мали нагоду переглянути цікаву виставку про підпільне життя та діяльність Сестер Служебниць під час комуністичного режиму, яку, на основі архівних матеріалів, приготували наймолодші сестри - новички й кандидатки.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На завершення с. Надія Котур, Провінційна настоятелька, подякувала гостям та всім присутнім за спільну молитву, цінні свідчення та співпрацю.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Інформацію надали сс. Служебниці</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>',NULL,1874,5,1,NULL,42);
INSERT INTO `publications_publication` VALUES
(4654,'P','2008-12-29',1,NULL,'Note','За 17 років незалежності на Львівщині звели 377 культових споруд  ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Найбільш чисельною на Львівщині є Українська греко-католицька церква, яка налічує 1488 громад. Друге місце за кількістю громад займає Українська православна церква - Київський патріархат - 433 громади.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Найбільш чисельною на Львівщині є Українська греко-католицька церква, яка налічує 1488 громад. Друге місце за кількістю громад займає Українська православна церква - Київський патріархат - 433 громади. Найменш чисельними є громади: Церква євангельських християн в дусі апостолів в Україні, Новоапостольська церква, Релігійні організації пресвітеріанської церкви та інші, їхня кількість не перевищує 1. Такі дані подає відділ у справах релігій та національностей Львівської ОДА.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Українська греко-католицька церква також налічує найбільше священнослужителів - 951 особа. Друге місце за кількістю священнослужителів займають релігійні організації Свідків Єгови в Україні - 482 священнослужителі.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За даними звіту, всього на Львівщині зареєстровано 36 церков та релігійних організацій. Також станом на кінець поточного року у Львівській області налічується 1791 культова будівля. За період з 1991 по 2008 рік було збудовано 377 культових будівель, а в стані будівництва перебуває ще 221 об\'єкт.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"http://www.galinfo.com.ua/news/society/47485.html\" target=\"_blank\">http://www.galinfo.com.ua/news/society/47485.html</a></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>',NULL,1873,5,1,NULL,42),
(4655,'P','2008-12-29',1,NULL,'Note','«Різдвяна свічка» – запали вогник надії  ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\"Дана акція несе окрім того і дуже важливі виховні функції - з одного боку вона пропагує та заохочує до милосердя, співчуття, жертовності, а з другого боку вона згуртовує, єднає людей\", - розповів о. Андрій Нагірняк, референт з соціальних питань Патріархії УГКЦ.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З початком Різдвяного посту Міжнародний Благодійний Фонд \"Карітас України\" розпочинає щорічну всеукраїнську благодійну акцію &laquo;Різдвяна свічка&raquo;, зібрані кошти від якої йдуть на допомогу соціально-незахищеним дітям. З благословення Блаженнішого Любомира, акція охоплює всі єпархії і екзархати Української Греко-Католицької Церкви в Україні та деякі громади за кордоном.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\"Дана акція несе окрім того і дуже важливі виховні функції - з одного боку вона пропагує та заохочує до милосердя, співчуття, жертовності, а з другого боку вона згуртовує, єднає людей\", - розповів о. Андрій Нагірняк, референт з соціальних питань Патріархії УГКЦ.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Акція полягає у розповсюдженні, в першу чергу через парафії, спеціально виготовлених свічок. Щиро дякуємо усім, у кого милого року на столі горіла свічка \"Різдвяна допомога дітям\". Запрошуємо усіх і цього року приєднатись до цієї благодійної акції та подарувати потребуючим дітям світло надії, адже \"Коли береш - наповнюєш руку, коли даєш - наповнюєш серце!\"</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\"Ми можемо просто купити свічку на ринку, де за таку ж ціну вона може бути красивішою, якіснішою чи більшою. Але склавши пожертву на нашу свічку людина відчуває, що вона немов би запросила на Вечерю потребуючу дитинку. Різдвяна свічка, запалена у кожному домі на Свят Вечір є символом нашого єднання у спільній милосердній справі, спільній любові, спільному Карітасі. І людина, яка отримує в цей вечір подарунок чи допомогу від нас відчуває, що вона є у родині, що про неї хтось дбає, пам\'ятає і жертвує, готувавшись до Святої Вечері\", - ділиться своїм баченням суті цієї акції о. Ігор Козанкевич, директор БФ \"Карітас СДЄ\".</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За зібрані пожертви організації &laquo;Карітас&raquo; надають допомогу потребуючим дітям (сиротам, напівсиротам, дітям вулиці, дітям з неблагополучних та багатодітних родин), допомагають в їх лікуванні та оздоровленні, організовують літні табори. Усі ці заходи - це, передусім, можливість показати дітям, що Бог їх любить, що є хтось, хто ними цікавиться і про них піклується.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Інформацію надав свящ. Андрій Нагірняк</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/<br /></span></span></p>\r\n<p>&nbsp;</p>',NULL,1872,5,1,NULL,42);
INSERT INTO `publications_publication` VALUES
(4656,'P','2008-12-29',1,NULL,'Note','Карітас України став «Благодійником року — 2008»  ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Другий рік поспіль в Україні відзначають найдостойніших благодійників на конкурсі &laquo;Благодійник року&raquo;. Цього року перемогу у конкурсі здобув і Міжнародний благодійний фонд \"Карітас України\".</span></span></p>\r\n<p>&nbsp;</p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Другий рік поспіль в Україні відзначають найдостойніших благодійників на конкурсі &laquo;Благодійник року&raquo;. Цього року перемогу у конкурсі здобув і Міжнародний благодійний фонд \"Карітас України\".</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Нагороду президенту Карітасу України Андрію Васьковичу вручила дружина президента України Катерина Ющенко, яка є Головою Наглядової ради Міжнародного благодійного фонду &laquo;Україна 3000&raquo;, що виступив одним з організаторів конкурсу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Під час вручення нагороди пані Катерина відзначила: &laquo;Благодійність - це не обов\'язково вкладання коштів. В багатьох країнах світу люди роблять свою благородну справу, працюючи волонтерами. Займаючись благодійною роботою, людина виконує свій християнський обов\'язок і стає ближчою до тих сердець, яким вона допомагає. А це, в свою чергу, робить її більш терплячою до життєвих негараздів&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За словами організаторів конкурсу, перемогу Карітасу України зумовила, насамперед, багатостороння діяльність фонду. Адже організація втілює у життя проекти по домашній опіці, допомоги ВІЛ-позитивним особам та людям, які хворі на СНІД; допомагає узалежненим від алкоголю та наркотиків; займається з дітьми з неблагополучних та кризових сімей, а також дітьми вулиці. Окрім цього Карітас України завдяки розвиненій мережі консультаційних пунктів активно займається міграційними питаннями, надає допомогу людям, що постраждали внаслідок міграційних процесів, зокрема торгівлі людьми. І, врешті, Карітас України ніколи не залишається осторонь кризових ситуацій, у які зненацька потрапили люди, наприклад, внаслідок стихійних лих. Так, Карітас організовує збір коштів як в Україні, так і за кордоном і передає їх та іншу допомогу постраждалим. Наприклад, після повені у Західній Україні влітку нинішнього року, організація зібрала і передала допомогу потерпілим на суму понад 300 тисяч євро.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Саме тому усім людям, які працюють в Карітас України дуже приємно, що їх роботу помітили і відзначили. На цьому наголосив і президент Карітасу України Андрій Васькович. &laquo;Дуже приємно отримати цю нагороду, адже вона свідчить про те, що роботу сотень наших працівників та волонтерів помічають не лише ті, кому ми безпосередньо допомагаємо, а вона важлива загалом для суспільства&raquo;, - зазначив пан Васькович і додав: &laquo;Також для нас дуже важливо, що це відзнака саме від громадських організацій, які знають та розуміють благодійну діяльність&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сама церемонія нагородження була доволі камерною і пройшла у теплій невимушеній обстановці, що відповідало саме такій імпрезі, яка ставить у центр уваги людину. На цей захід завітали також відомі митці та спортсмени - співаки Олег Скрипка та Марічка Бурмака, актор Богдан Ступка, генеральний директор Міжнародного кінофестивалю &laquo;Молодість&raquo; Андрій Халпахчі, олімпійська чемпіонка Стела Захарова.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Організатори та учасники цього конкурсу, зокрема і Андрій Васькович, сподіваються, що незважаючи на світову фінансову та економічну кризу, благодійництво в Україні розвиватиметься. &laquo;Усі наші програми, які ми розглядаємо як моделі соціальної праці фінансуються, переважно, з-за кордону. Ми ж прагнемо до реалізації цих соціальних програм активніше залучати українських донорів, - розповів Андрій Васькович. - Тому ідеї поширення благодійництва в Україні, які пропагує цей конкурс є дуже важливими. Сподіваємося, що до підтримки соціальних програм і соціальних послуг, що їх надають неурядові організації, долучиться і держава. Надіюся на те, що Карітас України в наступному році матиме необхідні кошти для того, щоб і надалі реалізовувати нині існуючі програми, адже дуже не хочеться їх скорочувати, позаяк бачимо їх велику потребу для українського суспільства&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"http://www.caritas-ua.org/index.php?pg=424\" target=\"_blank\">http://www.caritas-ua.org/index.php?pg=424</a></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Довідка:</em><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Конкурс &laquo;Благодійник року&raquo; проводиться в Україні вдруге. Минулого року нагороду &laquo;Благодійник року&raquo; отримала і благодійна медична установа Шпиталь імені Митрополита Андрія Шептицького у Львові, відповідальність за розвиток якого з 2001 року несе Карітас України. </em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Організаторами конкурсу &laquo;Благодійник року-2008&raquo; виступили Міжнародний благодійний фонд &laquo;Україна 3000&raquo;, Всеукраїнський благодійний фонд &laquo;Дитячий світ&raquo;, Центр філантропії, Український Форум благодійників та ІАЦ &laquo;Громадський простір&raquo;. Мета конкурсу - винагородження та визнання представників української громадськості за проведення благодійної діяльності, спрямованої на вирішення актуальних проблем суспільства, а також популяризацію благодійництва в Україні. В конкурсі можуть брати участь фізичні та юридичні особи, які надали одно- чи багаторазову благодійну допомогу для розв\'язання суспільних проблем. До журі конкурсу входять експерти та громадські діячі, які мають високий авторитет в суспільстві. </em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Конкурс &laquo;Благодійник року - 2008&raquo; розпочався 3 липня 2008 року, його результати були оприлюднені 12 грудня, а нагородження переможців відбулося 22 грудня в Києві.</em></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>',NULL,1871,5,1,NULL,42);
INSERT INTO `publications_publication` VALUES
(4657,'P','2008-12-29',0,NULL,'Note','','<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Молитва до Пресвятої Тройці</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Справедливо є і годиться богоналежно  славити Тебе, Пресвяту Тройцю, нероздільно одну в своїй все безконечно  перевищуючій істоті, - Тебе, премилосердного нашого Бога. Тебе величають усі  земнородні, Тебе славословлять небесні духи. Єдине істотою божество, що живеш у  трьох особах, Тобі ми, всі вірні, приносимо богоналежний поклін. Отче Вседержителю, Сину і Слово Боже,  Всесвятий Душе, єдина сило і владо, єдине царство й величносте, помилуй і спаси  нас, що Тебе нашим Богом визнаємо, що до Тебе молимося, що Тобі завжди хочемо  служити і Тебе чимраз більше любити. Амінь.</span></span></p>','',NULL,1870,5,1,NULL,50),
(4658,'P','2008-12-28',1,NULL,'Note','Греко-католики освятили новий жіночий монастир на Печерську в Києві','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перед посвяченням молебень до св. Богородиці відправили от. Василій, ігумен монастиря св. Василіан, та декан київський, настоятель церкви св. Миколая на Аскольдовій могилі от. Ігор Онишкевич.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Монастир сестер-служебниць св. Софії збудований за новим проектом, він є окремим особняком на шість келій. Є приміщення для відправлення служб а також катехизації та інші. При з особняку є огороджений живописний двір з різноманітною рослинністю.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У неділю, 14 вересня, в день початку літургійного року Блаженіший патріарх Любомир Гузар у співслужінні з єпископом помічником, владикою Богданом Дзюрахом освятив новий жіночий монастир сестер-служебниць святої Софії на Печерську, за адресою вул. Раєвського, 9-а. Повідомляє &laquo;<a href=\"../\"> Оранта</a> &raquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перед посвяченням молебень до св. Богородиці відправили от. Василій, ігумен монастиря св. Василіан, та декан київський, настоятель церкви св. Миколая на Аскольдовій могилі от. Ігор Онишкевич.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Монастир сестер-служебниць св. Софії збудований за новим проектом, він є окремим особняком на шість келій. Є приміщення для відправлення служб а також катехизації та інші. При з особняку є огороджений живописний двір з різноманітною рослинністю.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Блаженіший Любомир Гузар у своєму слові попросив Божого благословення чернечому уряду, та побажав, щоби лампада віри запалена цього дня ніколи не згасала і світила також людям, зокрема киянам.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На урочистостях була присутня генеральна настоятельниця черниць УГКЦ, сестра Іванна, провінційна сестра Надія, що урядує сестрами служебницями в Україні, та інші.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Це дуже зручний монастир. Невеликий, але дуже зручний&raquo;, сказала провінційна сестра Надія, настоятелька сестер-служебницями в Україні.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\"Мені тут дуже подобається, але, на жаль, найближчим часом, я мушу переїздити до Львова. Сюди залюбки приходять кияни на катехитичні навчання&raquo;, сказала сестра Марта, яка вже деякий час прослужила у цьому монастирі.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.politarena.org.ua/</span></span></p>',NULL,2493,5,1,NULL,30);
INSERT INTO `publications_publication` VALUES
(4659,'P','2008-12-28',1,NULL,'Note','Богородична молитва \"Ангел Господній\" з Папою Венедиктом XVI','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На початку Папа пригадав, що для римо-католиків перша неділя після празника Різдва є святом Пресвятої Родини з Назарету. А різдвяний контекст є для цього дуже придатним, адже празник Народження Божого Сина є найбільшим родинним святом.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Для тих християн, які відзначають Різдвяні свята за григоріанським календарем, тобто новим календарним стилем, сьогодні неділя по Різдві Христовім. Отож, Святіший Отець Венедикт ХVІ свою традиційну недільну науку, що передує полудневій молитві &laquo;Ангел Господні&raquo; та Апостольському благословенню, виголосив у дусі народження Божого Дитятка. <br /><br />На початку Папа пригадав, що для римо-католиків перша неділя після празника Різдва є святом Пресвятої Родини з Назарету. А різдвяний контекст є для цього дуже придатним, адже празник Народження Божого Сина є найбільшим родинним святом. Про що пересвідчуємось з багатьох традицій та звичаїв, особливо тих, які у ці святкові дні збирають разом усю родину за святковим столом та щоб обмінятись побажаннями та дарунками.<br /><br />&laquo;Ісус забажав народитись у людській сім\'ї; мав Пречисту Діву Марію за матір, і Йосифа, який був Йому за батька; вони Його виростили і виховали з безмежною любов\'ю. Сім\'я Ісуса, насправді, заслуговує називатись &laquo;святою&raquo;, - зазначив Вселенський Архиєрей, додаючи, що з одного боку вона була схожою на усі інші сім\'ї і, як така, була взірцем подружньої любові, співпраці, жертвенності, довіри Божому Провидінню, працьовитості і солідарності, як також усіх інших сімейних цінностей. Але, у той самий час, Родина з Назарету є унікальною, відмінною від усіх інших через своє особливе покликання, пов\'язане з місією Ісуса Христа. <br /><br />&laquo;Щоб виявити красу і цінність сім\'ї, сьогодні в Мадриді зібрались тисячі осіб&raquo;, - наголосив Святіший Отець, і за допомогою телемосту, звернувся іспанською мовою до зібраних в іспанській столиці. Папа підкреслив, що сім\'я - це Божа благодать, яка дозволяє зрозуміти, що Господь є Любов\'ю. Любов\'ю, повністю безкорисливою, яка підтримує вірність без меж, навіть у найважчих життєвих обставинах. Ці характерні риси, в особливий спосіб, присутні у Пресвятій Родині, в якій Христос забажав прийти у світ і зростати у мудрості, під дбайливою опікою Марії і вірним доглядом Святого Йосифа. &laquo;Дорогі Родини, - промовив Папа до зібраних у Мадриді та до усіх тих, які чули його промову - не дозвольте, щоб слабшали любов, відкритість на життя і неперевершені вузи, які єднають вашу сім\'ю. Постійно просіть цього у Господа, спільно моліться, щоб на шляху до святості ваші намірення були освітлені вірою і підсилені Божою благодаттю&raquo;. <br /><br />Далі Єпископ Риму, запевняючи у своїй молитовній підтримці, закликав християнські родини засвідчити перед усім світом важливість сім\'ї для особи і суспільства. А на закінчення недільної катехизи Папа пригадав, що від 14 до 18 січня 2009 року у столиці Мексики відбудеться ІV Всесвітня Зустріч Родин, закликаючи до молитви за цю важливу церковну подію.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матерыалами: http://www.radiovaticana.org/</span></span></p>',NULL,1869,5,1,NULL,43);
INSERT INTO `publications_publication` VALUES
(4660,'P','2008-12-28',1,NULL,'Note','Спільна просфора та подячна Літургія на закінчення навчального семестру у Львівській духовній семінарії','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">26 грудня 2008 року у стінах Львівської духовної семінарії Святого Духа завершився останній день зимової сесії 2007-2008 навчального року.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">26 грудня 2008 року у стінах Львівської духовної семінарії Святого Духа завершився останній день зимової сесії 2007-2008 навчального року.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ввечері відбулася спільна урочиста вечеря, яка ознаменувала остаточне завершення осіннього семестру та початок зимових вакацій. На початку вечері отець-ректор Святослав Шевчук звершив обряд Спільної просфори і, роздаючи кожному студентові частичку благословенного хліба з медом, побажав веселих свят та доброго Нового року.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Під час вечері також відбувся виступ семінарійного октету \"Теофорос\" та хору, у виконанні яких прозвучали традиційні українські колядки.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На завершення вечері о. Святослав зазначив, що цією спільною вечерею ми дякуємо Богу за добрий та плідний семестр. Рівно ж окрему вдячність о. Святослав висловив отцям-префектам, які \"в особливий спосіб дбають про добро всієї семінарійної спільноти\".</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Наступного дня, 27 грудня, отець-ректор Святослав Шевчук у співслужінні з отцями-настоятелями семінарії очолив подячну Божественну Літургію на закінчення навчального семестру.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У своїй проповіді о. Святослав наголосив на важливості часу очікування у нашому житті, часу, який зі зближенням Різдва Христового стає інтенсивнішим, бо очікування пов\'язане з його звершенням. \"Чим більше людина шукає Бога, тим ближче Бог є до неї. Постава чекання є дуже притаманною рисою життя християнина між першим (Воплочення та Різдво Спасителя) і останнім приходом Христа у славі. Але між першим і останнім є ще один прихід Ісуса Христа, який здійснюється у тиші - прихід до того серця, яке Його очікує. Тому, повернувшись додому не гасіть світильника, не випускайте з рук ознак очікування, бо Христос надходить! Нехай той час, який ми тепер розпочинаємо буде часом ділення радості, часом у якому віднайдемо новонародженого Христа, який приходить у вигляді маленького дитяти\", - &nbsp;закликав проповідник.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На закінчення Літургії отець-ректор Святослав також привітав із 98-літнім ювілеєм о. Миколая Пристая та окропив всіх семінаристів освяченою водою.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Тарас Остафіїв,</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Тарас Гавришко,</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Медіа-центр Львівської духовної семінарії Святого Духа</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матерыалами: http://www.ugcc.org.ua/</span></span></p>\r\n<p>&nbsp;</p>',NULL,1868,5,1,NULL,42);
INSERT INTO `publications_publication` VALUES
(4661,'P','2008-12-28',1,NULL,'Note','У неділю перед Різдвом УГКЦ втретє відзначить День мігранта  ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">4 січня 2009 року УГКЦ втретє відзначає День мігранта. Важко переоцінити значення діаспори для збереження і розвитку УГКЦ у ХХ ст. На початку минулого століття Церква активно відгукнулася на потреби тодішньої української еміграції.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">4 січня 2009 року УГКЦ втретє відзначає День мігранта. Важко переоцінити значення діаспори для збереження і розвитку УГКЦ у ХХ ст. На початку минулого століття Церква активно відгукнулася на потреби тодішньої української еміграції. У кінці ХХ ст. діаспора віддала для Церкви найцінніше - цілу когорту видатних, високоосвічених, професійних людей. Зараз понад 2 млн. українських греко-католиків постійно проживає за межами України, що складає близько третини вірних нашої Церкви. На початку ХХІ ст. перед нами постає новий виклик - 4,5 млн. українців четвертої хвилі міграції потребують об\'єднаної турботи Церкви з обох боків океану.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З благословення Блаженнішого Любомира, Комісія УГКЦ у справах мігрантів запрошує усіх до загально-церковної молитви за мігрантів та членів їхніх родин. \"Відстань і час, які розділяють сім\'ї мігрантів, створюють значні виклики для витривалості у покликанні до подружжя, що мають суспільний і духовний виміри. Перший вимір відображається у статистиці про кількість розлучень, а також у зростаючій актуальності проблем соціального сирітства (на які вже давно звертають увагу освітяни, психологи та соціальні працівники). Другий вимір містить ключові відповіді на ці виклики. Якщо сім\'я буде будуватися на міцному фундаменті християнських цінностей та відкритості на пізнання Живого Бога, то незалежно де буде збудований цей дім, у ньому буде панувати злагода, а плодами будуть опіка та взаєморозуміння у подружжі, і батьків до дітей\", -у коментарі для Департаменту інформації сказав Григорій Селещук, голова комісії.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Постановою Сорок другої сесії Синоду Єпископів Києво-Галицького Верховного Архиєпископства Української Греко-Католицької Церкви (Львів-Брюховичі, 17-18 грудня&nbsp; 2008&nbsp; р.Б.) до Сугубної ектенії Літургії в День емігранта включено таке прохання: \"Ще молимось за братів і сестер наших, які через потреби виїхали за межі України та за їхні родини\".</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">До вашої уваги публікуємо переклад <a href=\"http://www.ugcc.org.ua/461.0.html\">Послання Святішого Отця Венедикта XVI з нагоди 95 Всесвітнього Дня мігранта та біженця</a>, здійсненний Комісією УГКЦ у справах мігрантів, а також <a href=\"http://www.ugcc.org.ua/462.0.html\">Звернення голови Комісії УГКЦ у справах мігрантів з нагоди відзначення в УГКЦ Дня мігранта</a>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Департамент інформації УГКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Послання Його Святості Венедикта XVI <br />з нагоди 95 Всесвітнього Дня мігранта та біженця</strong><br />( Тема: Св. Павло - емігрант та апостол народів)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Тема цьогорічного послання з нагоди Всесвітнього Дня емігранта та біженця - &bdquo;Святий Павло - &nbsp;емігрант та апостол народів\". Це пов\'язано із проголошеним мною Ювілейним роком з нагоди 2000-річчя від дня народження цього апостола. Справа Павла, емігранта за покликанням, - проповідування науки Божої, просвітництво та посередництво між різними культурами і Євангелією. Саме тому його життя і служіння справі Благовісті Христової залишається промовистим прикладом для наслідування для сучасних емігрантів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Савло народився в поселенні Тарс, на Сицилії, в сім\'ї єврейських емігрантів. Його було виховано у&nbsp; єврейській&nbsp; та еллінській мовах і культурах, однак під значним впливом здобутків суспільного життя римської цивілізації. Після зустрічі з Христом по дорозі до Дамаску (пор. Гал. 1, 13-16), не зраджуючи власній традиції та маючи пошану і вдячність до іудаїзму і Закону (пор. Рим. 9, 1-5; 10, 1; 2 Кор. 11, 22; Гал. 1, 13-14; Фил. 3, 3-6), він слухняно сприйняв волю Господа: &bdquo;Іди, бо я пошлю Тебе до поган далеко\" (Ді. 22, 21), - і, без вагань та роздумів, відважно і з натхненням посвятився новій місії. Його життя кардинально змінилося &nbsp;(пор. Фил. 3, 7-11): Ісус став для нього правдою життя і натхненням до апостольського служіння Євангелії. Із переслідувача християн він став справжнім апостолом Христа.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ведений святим Духом, Павло докладав усіх зусиль, щоб Євангеліє - \"сила Божа на спасіння кожному, хто вірує: перше іудеєві, а потім грекові\" (Рим. 1, 16), -&nbsp; проповідувалось усім, незалежно від національності чи культури. Під час своїх апостольських подорожей, незважаючи на часті протести, передусім йшов проповідувати&nbsp; Євангеліє у синагоги, до своїх одноплемінників у діаспорі (пор. Ді. 18, 4-6). І якщо вони його відкидали, звертався до поган, стаючи правдивим місіонарем емігрантів. Сам будучи емігрантом і мандрівним посланцем Ісуса Христа, він заохочував кожну людину до того, щоби та ставала у Божому Сині \"новим сотворінням\" (2 Кор. 5, 17).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Проповідуючи <em>керигму</em> Євангельської Благовісті, він досягнув самого Рима, збороздивши моря Близького Сходу і биті дороги Європи. Гнаний цією метою, без тіні жалю полишає він гостинну Антіохію, де проповідував Євангеліє перед поганами, що не визнавали юдаїзму, і де учнів Ісуса вперше було названо &bdquo;християнами\" (пор. Ді. 11, 20-26). Його життя і навчання повністю служили справі загального пізнання Христа і любові до Нього, справі розбудови у Ньому єдиного народу Божого.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Тепер, в епоху глобалізації, саме у цьому і полягає місія Церкви та кожного охрещеного. Ця місія, завдяки своєму душпастирському засягові та чутливості, обіймає всі сфери життя емігранта - приїжджого студентства, біженців, утікачів, переселенців, включно із жертвами сучасних форм рабського поневолення людини людиною, таких як, наприклад, торгівля людьми. Сьогодні необхідно проповідувати післанництво Відкуплення так само, як це робив Апостол Народів, беручи під увагу як різнорідні&nbsp; суспільні і культурні ситуації, так і особливі особисті проблеми кожного емігранта і подорожуючого, що є безпосередньо пов\'язаними із питомим для цієї категорії осіб стилем життя. Я хотів би, щоби кожна християнська спільнота виявляла таку ж апостольську ревність, як св. Павло, котрий усім проповідував любов Отця і Відкуплення у Христі Ісусі (Рим. 8, 15-16; Гал. 4, 6), щоб &bdquo; ще більше їх придбати\" (1 Кор. 9, 19), для слабких стаючи, як слабкий, для всіх - &bdquo;усім, щоб конче деяких спасти\" (1 Кор. 9, 22).&nbsp; Нехай його приклад буде для нас заохоченням, щоб і ми були солідарними з нашими братами і сестрами та щоб ми в кожному куточку землі й у різний спосіб дбали про мирне співіснування між різними народами, культурами і&nbsp; релігіями.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В чому полягала таємниця Апостола Народів? Характерна для нього місійна ревність і войовничий запал були наслідком того, що його самого &bdquo;Христос Ісус здобув\" (Фил. 3, 12). Павло був з\'єднаний з Ісусом настільки глибоко, що мав відчуття участі в Його житті через &bdquo;участь в Його&nbsp; муках\" (пор. Фил. 3, 10; Рим. 8, 17; 2 Кор. 4, 8-12; Кол. 1, 24). У цьому, властиво, і полягає таємниця та рушійна сила апостольського запалу св. Павла, який говорить: &bdquo;той, хто вибрав мене вже від утроби матері моєї і покликав своєю благодаттю, зволив об\'явити мені&nbsp; Сина свого, щоб я проповідував його між поганами\" (Гал. 1, 15-16; пор. Рим. 15, 15-16). Апостол почував себе співрозп\'ятим з Христом і стверджував: &bdquo;Живу вже не я, а живе Христос у мені\" (Гал. 2, 20). Жодні труднощі не були для нього перешкодою у веденні відважної євангелізаційної діяльності навіть у таких космополітичних містах як Рим і Коринт, мешканці яких у ті часи витворювали різнорідну й барвисту мозаїку від змішання численних національностей, мов, світів та культур.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Коли перечитуємо Діяння апостолів та послання, що їх Павло скеровує до різних народів, неначе знаходимо в них справжню модель справжньої Христової Церкви, що не є Церквою декількох втаємничених, але відкритою для всіх - Церквою, яка складається з різнобарвної многоти віруючих, але в котрій немає жодних відмінностей, пов\'язаних із культурою і расою кожного з її членів, адже кожен охрещений є живим членом того ж самого Тіла Христа. Під цим оглядом, по-справжньому виняткового значення набирає питання братерської солідарності, яка проявляється у щоденних проявах сопричастя, співучасті і радісної опіки й ьурботи про інших. Однак, як завжди повчає нас св. Павло, вияв цього виміру взаємної братерської любові є неможливим без&nbsp; готовності слухання і сприймання проповідуваного і впроваджуваного в життя Слова (пор. 1 Сол. 1, 6), яке заохочує всіх до наслідування Христа (пор. Еф. 5, 1-2) через наслідування життя самого Апостола (пор. 1 Кор. 11, 1). Адже чим більше спільнота є з\'єднана з Христом,&nbsp; тим більше вона піклується про ближнього, уникаючи будь-якого осуду, погорди і згіршення щодо його особи та живучи у взаємній любові (пор. Рим. 14, 1-3; 15, 7). Вірні, у їх наближені до Христа, відчувають себе у Ньому &bdquo;братами\", дітьми того самого Отця (Рим. 8, 14-16; Гал. 3, 26; 4,6). Цей скарб єдності і братерства проявляється в тому, що вони &bdquo;дбають про гостинність\" (пор. Рим. 12, 13), що є первородною дочкою agape (пор. 1 Тим. 3, 2; 5, 10; Тит 1, 8; Флм. 17).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Таким чином сповняється обітниця Господа: &bdquo;Я вас прийму. Я буду вам отцем, ви ж будете мені синами та дочками\"(2 Кор. 6, 17-18). І якщо ми це усвідомлюємо, то чи не повинні ми піклуватися про тих, хто знаходиться у важких умовах життя, а особливо про біженців та втікачів? І хіба можна не турбуватися про потреби людей насправді слабких і беззахисних, тих, які живуть&nbsp; в непевності і небезпеці, відкинутих на марґінеси суспільства, а нерідко навіть і зовсім позбавлених можливості будь-якого із ним спілкування? Саме їм необхідно приділити першочергову увагу, з огляду на загально відомий усім уступ одного із Павлових послань: &bdquo;Бог вибрав немудре світу, щоб засоромити мудрих...і незначне світу та погорджене Бог вибрав, і те чого не було, щоб знівечити те, що було, щоб жодне тіло не величалося перед Богом\"( 1 Кор. 1, 27-29).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорогі браття і сестри, нехай Всесвітній День мігранта та біженця, що його будемо святкувати 18 січня 2009 року, стане для всіх заохоченням до того, щоби жити у досконалій братній любові, не виділяючи й ані не дискримінуючи нікого, в переконанні, що нашим ближнім є кожен, хто нас потребує і кому ми можемо допомогти (пор. Deus caritas est, 15). Нехай наука і приклад св.Павла, великого і покірного Апостола і емігранта, просвітителя і благовісника численних народів і різнорідних культур, допоможе нам зрозуміти, що сповнення діл любові є вершиною і квінтесенцією цілого християнського життя. Заповідь любові - і ми є цілком свідомі цього - набирає життя, коли у єдності Святого Духа учні Христові беруть участь у Євхаристії, яка є найвищим виявом таїнства любові Христа. За прикладом&nbsp; Ісуса, який на Святій Вечері з\'єднав дар Євхаристії з новою заповіддю братньої любові, також і Його приятелі, наслідуючи Христа, що став слугою цілого людства, і зміцнювані Його благодаттю, повинні віддано служити один одному, дбаючи один про одного, у відповідності до поради святого Апостола: &bdquo;Носіте тягарі один одного, й таким чином виконаєте закон Христа\" (Гал. 6, 2). Адже тільки тоді зростає любов між&nbsp; віруючими, тільки тоді вона здатна поширитися і вилитися також і на всіх інших, тільки тоді вона стає здатною діткнути кожного (пор. 1 Сол. 3, 12).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;Дорогі браття і сестри, не переставаймо проповідувати цю &bdquo;Добру Новину\" і втілювати її у наше щоденне життя з натхненням, без страху і не жалкуючи сил.&nbsp; В почутті любові цілічно міститься усе послання Євангелія, а правдивих учнів Христа можна розпізнати саме по взаємній любові, по їх відкритості у стосунку до інших. Нехай цей дар випросить нам Апостол Павло, а особливо Марія - Мати, яка приймає і любить. Просячи Бога про опіку над усіма, які долучаються до справи допомоги емігрантам і загалом до справи душпастирського служіння посеред велелюдного світу міграції, обіцяю всім вам постійну пам\'ять у молитві і всім уділяю із серця Апостольське благословення.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Кастель Гандольфо, 24 серпня&nbsp; 2008 р.Б.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Венедикт XVI</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Переклад здійснено Комісією УГКЦ у справах мігрантів</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>\r\n<p align=\"center\"><em><strong>Звернення <br /> з нагоди відзначення в УГКЦ Дня мігранта</strong><br /> <em><strong>(4 січня 2009 р.Б.)</strong></em></em></p>\r\n<p><em>&nbsp;Уже втретє наша Церква відзначає День мігранта, який припадає на останню неділю напередодні Різдва. Саме у Різдвяний період особливо гостро відчувається туга за рідною домівкою. Адже Різдво - родинне свято, коли за столом збираються усією сім\'єю та згадують тих, хто десь далеко. </em></p>\r\n<p><em>Церква, як турботлива мати, хоче огорнути опікою усіх. А в особливий спосіб тих, хто не тішиться родинним теплом&nbsp; вдома, а зустрічає Різдво в чужих краях. Перед очима постає Пресвята родина - Марія, опікун Йосиф та новонароджене Дитятко Ісус - котрі теж були змушені втікати до Єгипту. Від самого народження Христос пізнав&nbsp; труднощі життя мігрантів. Та в усіх випробуваннях Пресвята родина була разом. </em></p>\r\n<p><em>Сучасний світ дає багато можливостей знайти своє місце в житті. Можливо, для когось воно буде за межами власної країни. Значення діаспори для збереження і розвитку нашої Церкви та держави важко переоцінити. Не можемо осуджувати тих, хто знайшов себе в іншій країні. Церква готова надати свою підтримку українським емігрантам за кордоном. Однак, як свідчить більш ніж сторічний досвід української діаспори, все залежить від самих емігрантів. Можливості Церкви стають дуже обмеженими без їхньої активної участі. </em></p>\r\n<p><em>Святе Письмо детально не описує життя Пресвятої родини у Єгипті.&nbsp; Виразним є тільки факт їхнього повернення до Батьківщини. Ми також із нетерпінням чекаємо повернення наших рідних додому. Досвід, який привозять мігранти, повертаючись в Україну, є дуже важливим. Треба з розумінням ставитися до тих проблем, з якими вони тут зіштовхуються. Адже за кілька років змінилася і Україна, і самі мігранти. Вони змушені наново звикати до умов життя, шукати можливості для працевлаштування, відновлювати родинне життя та спілкування з друзями. Наша допомога в цьому є дуже потрібною. </em></p>\r\n<p><em>У цей день, закликаємо усіх молитися за єдність та мир у родинах мігрантів, щоб віддаленість не стала причиною охолодження подружньої любові. Нічим неможливо замінити лагідне слово, теплий дотик та щиру увагу. Довгу розлуку найважче переносять діти. Тому мусимо дбати, щоб за першої нагоди сім\'я була знову разом. Підтримка сімейних зв\'язків є викликом і для родин мігрантів, і для Церкви та суспільства. </em></p>\r\n<p><em>Ми можемо збагатити світ добрим прикладом, якщо з приязню ставитимемося до іноземців, котрі приїжджають в Україну, пам\'ятаючи слова нашого Господа: \"Я був чужинцем, і Ви мене прийняли\" (Мт.&nbsp;25:35). Цей приклад може надихати людей в інших країнах також прихильно ставитися до українських мігрантів. На їхньому місці можуть виявитися наші рідні брати, сестри, батьки, дідусь або бабуся. Нехай у наших серцях завжди знайдеться тепло та щира молитва за них. </em></p>\r\n<p><em>З християнською любов\'ю, </em></p>\r\n<p align=\"right\"><em>Григорій Селещук,<br />голова Комісії УГКЦ у справах мігрантів</em></p>\r\n</em>За матерыалами: http://www.ugcc.org.ua/</span></span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>',NULL,1867,5,1,NULL,42);
INSERT INTO `publications_publication` VALUES
(4662,'P','2008-12-26',1,NULL,'Rss','Проповідь Святішого Отця Бенедикта XVI підчас нічної Меси в Урочистість Різдва Христового','<p><strong><img style=\"float: left;\" src=\"http://www.catholic-media.org/ua/images/foto_benedyctusXVI-02/foto_191-sw-mesa.jpg\" />Дорогі брати і сестри!</strong><br /> «Хто рівний Господу Богу нашому, що возсідає на престолі та споглядає вниз, на небо і на землю?! Саме такі слова лунають в одному з псалмів, коли ізраїльський народ прославляє, з одного боку велич Бога, а з іншого - його милосердну близькість до людей. Бог живе на висотах, а водночас, похиляється донизу... Бог є не осягнений, величний та незмірно перевищує нас.  Відстань здається безмежною - таке враження складається у людини , коли Творець всесвіту, Той, хто керує всім є десь дуже далеко, як це здається на початку.   Однак, згодом здійснюється вражаюче відкриття: «Той, кому немає рівних, Котрий возсідає на висотах», саме Він і споглядає донизу. Він похиляється, Він бачить кожного. Таке споглядання Бога вниз - це щось набагато більше, ніж звичайний погляд з висоти. Для Бога дивитися - означає діяти. Коли Бог дивиться на мене, коли Він мене бачить, Він також змінює мене та оточуючий мене світ. Тому слова псалму звучать: «Бог підіймає з пороху злиденного...» своїм спогляданням донизу Він підіймає мене, Він ласкаво бере мене за руку і допомагає здійматися моїй власній особистості із прірви до вершин.</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=165:propovid-svjatishogo-otcja-benedykta-xvi-pidchas-nichnoji-mesy-v-urochystist-rizdva-hrystovogo&catid=32:gk-svit-cat&Itemid=58',1865,NULL,1,1,26),
(4663,'P','2008-12-26',1,NULL,'Note','Держава та Церква спільно вироблятимуть єдину стратегію протидії абортам в Україні','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За останні 15 років, згідно з даними статистики, в Україні через аборти було вбито понад 8,5 млн. осіб: 1992 року - 1,2 млн., 2005 року - 242 тисячі. Це офіційна статистика, котра не враховує нелегальних абортів.&nbsp; </span></span></p>','<p style=\"text-align: justify;\"><img style=\"border: 0pt none; margin: 4px 3px; float: right;\" src=\"http://www.risu.org.ua/uimg/?f=10851.jpg&amp;s=-1&amp;b=yes&amp;bc=000000\" alt=\"Держава та Церква спільно вироблятимуть єдину стратегію протидії абортам в Україні\" width=\"200\" height=\"150\" /><em></em><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У Міністерстві охорони здоров\'я України 23 грудня відбулося засідання Міжвідомчої робочої групи з вироблення єдиної стратегії протидії абортам, внесення змін до нормативно-правових актів щодо питань штучного переривання вагітності. Про це повідомляє Інтернет-видання &laquo;Православіє в Україні&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За останні 15 років, згідно з даними статистики, в Україні через аборти було вбито понад 8,5 млн. осіб: 1992 року - 1,2 млн., 2005 року - 242 тисячі. Це офіційна статистика, котра не враховує нелегальних абортів. <br /><br />На сьогодні здійснення аборту за бажанням жінки законодавчо дозволено у 56 із 194 країн світу. Це 28 % країн з населенням близько 40 % від світового. <br /><br /> У Європі дві держави - Мальта і Ватикан - не дозволяють переривання вагітності за жодних умов; Ірландія, Андора, Сан-Марино і Монако дозволяють аборт тільки у випадку загрози життю вагітної жінки. В Польщі, Іспанії, Ліхтенштейні аборти дозволені з метою захисту не тільки життя, але й фізичного та психічного здоров\'я вагітної жінки, а також у випадку згвалтування, інцесту або аномального розвитку плоду. У Великобританії, Фінляндії, Ісландії та Люксембурзі аборт законодавчо дозволений також і за соціально-економічними показаннями. В інших країнах Європи, а також у США, Канаді, Австралії, жінки мають право на переривання вагітності за власним бажанням - обумовлюються тільки строки вагітності. <br /><br />Україні, як державі з глибинними християнськими традиціями, безумовно необхідно узгодити своє законодавство із християнськими заповідями, - повідомляє &laquo;Православіє в Україні&raquo;. - Але це не може відбутися миттєво. 20 серпня цього року Митрополит Володимир звернувся до Прем\'єр-міністра України Ю.В. Тимошенко з пропозицією впровадити проект &laquo;Обов\'язкове надання інформації в медичних закладах вагітним жінкам, які йдуть на аборт, стосовно процедур абортів та їх наслідків&raquo;. <br /><br />Сутність запропонованого проекту полягає в тому, що на видному місці біля кабінету гінеколога повинні знаходитись плакати, які розкривають процедуру аборту й можливі його наслідки для здоров\'я матері. Окремим питанням стоїть запровадження обов\'язкового передабортного консультування жінок. <br /><br />Учасники Робочої групи обговорили пропозиції щодо проекту &laquo;Обов\'язкове надання інформації в медичних закладах вагітним жінкам, які йдуть на аборт, стосовно процедур абортів та їх наслідків&raquo;. Під час роботи були утворені робочі підгрупи за напрямками. Наступне засідання вирішено провести в другій половині січня 2009 року.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">За матеріалами: http://www.risu.org.ua/</span><br /></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>',NULL,1864,5,1,NULL,30);
INSERT INTO `publications_publication` VALUES
(4664,'P','2008-12-26',1,NULL,'Note','Виставка \"Українське Різдво\"','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У Національному музеї Тараса Шевченка відкрито виставка \"Українське Різдво\". За повідомленням УКРІНФОРМу, це - барвиста, різноманітна, розкішна експозиція, півтисячі експонатів якої розміщені у шістьох просторих залах.<br /></span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У Національному музеї Тараса Шевченка відкрито виставка \"Українське Різдво\". За повідомленням УКРІНФОРМу, це - барвиста, різноманітна, розкішна експозиція, півтисячі експонатів якої розміщені у шістьох просторих залах. Як зазначила на відкритті директор музею Наталія Клименко, виставка створювалась спільно з Національним музеєм архітектури та побуту, який представив зі своєї колекції різдвяні ікони. Тут також живопис відомих художників і традиційні іграшки, і давні вертепи, різноманітні вироби, виготовлені відомими народними майстрами і дитячими гуртками. <br /><br /> \"Відвідини цієї виставки будуть однаковою мірою цікавими і для дітей, і для дорослих, тим більше, що під час неї аж до Водохреща проходитимуть майстер-класи з гончарного мистецтва, різьблення, виготовлення ляльок, вишивання\", - додала Наталія Клименко. <br /> \"Ця виставка в центрі Києві - один з доказів того, що наші народні корені, зокрема обряди святкування Різдва, ми не просто зберегли, а й примножили\", - сказав заступник міністра культури і туризму Микола Яковина. <br /><br /> За словами керівника мистецької студії для глухонімих дітей і сиріт ім.О.Саєнка, заслуженого діяча мистецтв України Ніни Саєнко, \"експозиція ще раз переконує в тому, якою незбагненною, творчою є душа українського народу\".</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.risu.org.ua/</span></span></p>',NULL,1863,5,1,NULL,30),
(4665,'N','2008-12-26',1,NULL,'Note','','<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Бог не запитає</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1. Бог не запитає тебе, яким автомобілем їздив, але скільки людей ти          підвіз...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">2. Бог не запитає тебе, яку площу мав твій дім, а скільки подорожніх          ти в ньому прийняв...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">3. Бог не запитає тебе, як ти одягався, а запитає, скількох людей ти          зодягнув...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">4. Бог не запитає тебе скільки грошей ти заробляв, але яким способом...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">5. Бог не запитає тебе яку посаду ти займав, але чи ти працював чесно          і сумлінно...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">6. Бог не запитає тебе, скільки ти мав приятелів, але для кого з них          був приятелем ти...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">7. Бог не запитає тебе, в якому оточенні ти жив, але як ти ставився до          своїх сусідів...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8. Бог не запитає тебе, скільки разів ти говорив правду, але запитає          скільки разів ти збрехав.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">9. Бог не запитає тебе, чому ти так довго зволікав з покаянням і виправленням.          Він з любов\'ю запровадить тебе до твого небесного житла.</span></span></p>\r\n<p>&nbsp;</p>','',NULL,1862,5,1,NULL,50);
INSERT INTO `publications_publication` VALUES
(4666,'P','2008-12-26',1,NULL,'Rss','Поезія Зоряни Живки','<p>РIЗДВЯНЕ ВІНШУВАННЯ <br /> <br /> Різдвяна зоря зазирає до хати.<br /> Хай буде на радість ця хата багата!<br /> Даруй нам, Ісусе, любові у серце,<br /> Нехай вона рясно на ближніх поллється!<br /> <br /> Зміцни нашу віру, Спасителю в яслах,<br /> Щоб світло Твоє в наших душах не згасло!<br /> Очисти від злого, навчи пильнувати,<br /> У кожному дневі Тебе шанувати!</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=164:poezija-zorjany-zhyvky&catid=1:mystecka-storinka-cat&Itemid=50',1860,NULL,1,1,26),
(4667,'P','2008-12-26',1,NULL,'Note','Молитва \"Ангел Господній\" з Папою Венедиктом XVI у другий день Різдвяних свят','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сьогодні, 26 січня, вірні Римо-Католицької Церкви урочисто відзначають спогад про мученицьку смерть диякона Степана, який став першим, хто віддав своє життя за Ісуса Христа. З нагоди цього другого дня Різдвяних свят Вселенський Архиєрей звернувся із словом повчання до вірних, які численно зібрались на ватиканській площі Святого Петра.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сьогодні, 26 січня, вірні Римо-Католицької Церкви урочисто відзначають спогад про мученицьку смерть диякона Степана, який став першим, хто віддав своє життя за Ісуса Христа. З нагоди цього другого дня Різдвяних свят Вселенський Архиєрей звернувся із словом повчання до вірних, які численно зібрались на ватиканській площі Святого Петра.<br /><br />Відразу на початку своєї промови, говорячи про первомученика Степана, одного із семи перших дияконів Христової Церкви у Єрусалимі, який був мужем повним віри і Святого Духа, Папа Венедикт ХVІ звернув увагу на одну особливість у біблійній розповіді про смерть цього відважного праведника, про яку вартувало б згадати, особливо, у році присвяченому Святому Апостолові Павлу. Папа процитував 58 стих із 7-ї глави книги Діянь Апостолів: &laquo;Свідки ж поклали свою одежу у ногах хлопця, що звався Савло&raquo;. Це перша згадка про вірного батьківській традиції Савла, що спершу був ревним переслідувачем християн, гонителем Церкви Христової. За словами Святішого Отця, коли роздумаємо над подальшим розвитком життєвого шляху Савла, то можемо сказати, що свідчення віри святого Степана було вирішальним у процесі його навернення. В короткому часі, після каменування Степана, Савло, й надалі сумлінно захищаючи передану йому славними попередниками духовну спадщину, вирушив до Дамаску, щоб арештувати послідовників нової релігії, тобто християн. Але у дорозі йому з\'явився воскреслий Спаситель, змінюючи докорінно життя молодого Савла. Упавши із коня і почувши голос, що кликав його по-імені, юнак озвався: &laquo;Хто ти, Господи?&raquo; А у відповідь почув слова: &laquo;Я&nbsp;-&nbsp;Ісус, що його ти переслідуєш&raquo; (Діяння Апп.&nbsp;9,5).<br /><br />Далі Папа Венедикт ХVІ підкреслив, що Савло, будучи свідком каменування Степана, бачив як помирав той відважних свідок Ісуса, бачив, що його смерть була дуже подібною до смерті його Учителя з Назарету, Який молився за своїх мучителів і просив в Отця для них прощення. По дорозі до міста Дамаску Савло зрозумів, що переслідуючи Церкву, він переслідує Ісуса Христа, Який помер і справді воскрес, Який живе у Своїй Церкві, Який живе також і в святому Степанові, що віддав своє життя за Христа, щоб із Ним з\'єднатись і жити навіки. Таким чином, Павло змінив своє життя і став вірним учнем Того, Якого переслідував. Від того моменту Ісус стає для нього справедливістю, святістю, спасінням, стає усім. А згодом, він сам послідує прикладу святого Степана, проливаючи у Римі свою кров за Ісуса, за Його Євангелію. <br /><br />&laquo;Дорогі брати і сестри, у святому Степані бачимо здійснення перших плодів спасіння, яке Різдво Христове принесло людству: перемогу життя над смертю, любові над ненавистю, світла правди над темрявою брехні&raquo;, - наголосив Вселенський Архиєрей. А далі проказав із вірними молитву &laquo;Ангел Господній&raquo; та уділив усім присутнім своє Апостольське благословення.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,1859,5,1,NULL,43);
INSERT INTO `publications_publication` VALUES
(4668,'P','2008-12-26',1,NULL,'Note','Пастирський лист Римо-Католицького єпископату України з нагоди свята Пресвятої Родини','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Митрополит Львівський Мечислав Мокшицький наголошує на важливості та актуальності енцикліки Папи Павла&nbsp;VІ про людське життя, яка побачила світ 40 років тому: &laquo;Повчання цього документу є особливо важливе для родин, бо воно основане на моральних засадах, встановлених Господом Богом, які стосуються подружньої пари у найбільш шляхетній, делікатній і святій сфері - передавання людського життя&raquo;.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У сорокову річницю проголошення енцикліки Святішого Отця Павла VI &bdquo;Humanae vitae\", від імені Конференції Римо-Католицьких Єпископів України, Архиєпископ-Митрополит Львівський Мечислав Мокшицький звернувся до духовенства і вірних з Пастирським Посланням, яке в усіх латинських парафіях України, разом з обрядом визнання віри і відновленням подружніх обітниць, оголосять у неділю по Різдві, 28&nbsp;грудня, коли відзначатиметься свято Пресвятої Родини.<br /><br />У пастирському посланні, виданому напередодні Різдвяних свят, говориться: &laquo;Ісус Христос - правдивий Бог і правдива Людина - бажав прийти у світ в людській родині, щоб не лише з міццю підкреслити важливість подружжя, цього особливого і нерозривного зв\'язку між чоловіком і жінкою, який Творець установив на початку, але піднести його до гідності таїнства і, таким чином, освятити кожне подружжя, кожну людську родину&raquo;.<br /><br />Далі Митрополит Львівський Мечислав Мокшицький наголошує на важливості та актуальності енцикліки Папи Павла&nbsp;VІ про людське життя, яка побачила світ 40 років тому: &laquo;Повчання цього документу є особливо важливе для родин, бо воно основане на моральних засадах, встановлених Господом Богом, які стосуються подружньої пари у найбільш шляхетній, делікатній і святій сфері - передавання людського життя&raquo;. Пояснюючи енцикліку &bdquo;Humanae vitae\", Іван Павло ІІ підкреслив, що з точки зору моральних засад цього документу центральне значення має фрагмент: &bdquo;Церква [...] навчає, що кожен подружній акт повинен бути відкритим на передачу життя\" (HV, 11). &bdquo;Це вчення, неодноразово викладене Учительським Урядом Церкви, базується на встановленому Богом нерозривному зв\'язку між об\'єднавчим та прокреативним значеннями, притаманним подружньому акту. Цей зв\'язок людина не має права самовільно розривати.\" (HV, 12). <br /><br />&laquo;У &bdquo;Humanae vitae\" Святіший Отець Павло VI передусім пригадує і навчає, що Творець у своєму задумі тільки подружнім парам дає право на любовне поєднання. А це тому, що тільки справжня, тобто вірна, виключна, жертовна і взаємна подружня любов може гарантувати відповідальність за нове людське життя. Бо кожне подружнє спілкування зберігає зміст взаємної та справжньої любові, так як і його призначення до найвищої відповідальності, - батьківства. Завдяки цим двом функціям подружнього акту, чоловік та дружина є співпрацівниками Бога-Творця у передаванні людського життя. З волі Бога, вони через подружній акт торкаються двох найважливіших взаємопов\'язаних суттєвих аспектів: любові і життя&raquo;, - наголошується у посланні, продовжуючи, що &laquo;правильно переживати своє подружнє поєднання здатні лише ті, які щиро і глибоко вірять, що це сам Господь Бог, а не вони самі чи сліпий випадок, керує їхнім життям. (...) Такі подружжя, завдяки вірі у Божу присутність в їхньому житті, вміють панувати над собою. Це стає для них можливим тоді, коли ведуть глибоке духовне життя&raquo;.<br /><br />У посланні зокрема наголошується на пересторозі папської енцикліки перед вживанням протизаплідних засобів, використання яких є виявом егоїзму та дбання лише задовольнити тілесні пристрасті. &laquo;Той, хто здатен на безвідповідальні сексуальні дії у подружжі, швидко стає жертвою спокуси подружньої невірності (HV, 17). Святіший Отець Павло&nbsp;VІ у своїй енцикліці каже: &bdquo;Слід висловити тривогу через те, що чоловіки, які призвичаїлися до протизаплідних практик, можуть втратити пошану до жінки і, легковажачи її тілесним благом і психічною рівновагою, сприймати її як звичайне знаряддя задоволення своєї пожадливості, а не як супутницю, яку слід поважати і любити\" (HV,&nbsp;17).<br /><br />А на закінчення конференція римо-католицьких єпископів звертається із закликом до усіх подружніх пар, а також до тих, які приготовляються до подружнього життя, щоб пам\'ятали, &laquo;що Господь і Його закон мають бути їх остаточною нормою&raquo;.<br /><br />Єпископи заохотили усіх вірних, цілими родинами, взати участь у відзначенні свята Пресвятої Родини, щоб отримати благословення на новий 2009 рік, відновити свої хресні та подружні обітниці.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,1858,5,1,NULL,42);
INSERT INTO `publications_publication` VALUES
(4669,'P','2008-12-26',1,NULL,'Note','Римо-католики Львова святкують Різдво Христове','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Різдвяні Богослужіння у Львівській латинській митрополичій базиліці Успіння Пресвятої Діви Марії очолював Архиєпископ-Митрополит Львівський Мечислав Мокшицький.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Різдвяні Богослужіння у Львівській латинській митрополичій базиліці Успіння Пресвятої Діви Марії очолював Архиєпископ-Митрополит Львівський Мечислав Мокшицький. Під час Пастирки, яку транслювало Львівське державне телебачення, Митрополит Мокшицький виголосив проповідь, в якій, зокрема, закликав вірних проявити турботу про тих своїх ближніх, які нині болісно переживають драму Христового Різдва, опинившись в матеріальній та духовній скруті. У святковій Службі Божій в львівській латинській катедрі взяли участь також представники Української Греко-Католицької Церкви на чолі з львівським архиєпископом Ігорем Возьняком, ЧНІ, і ректором Українського Католицького Університету отцем Борисом Ґудзяком. Разом із латинниками молилися отці-василіяни, сестри служебниці, монахині інших Згромаджень, численні миряни східного обряду. Після завершення Літургії римо-католиків привітали з Різдвом Христовим Владика Ігор Возьняк, голова Львівської обласної ради пан Мирослав Сеник і міський голова Львова пан Андрій Садовий. Митрополит Мечислав Мокшицький очолив також Нічне Різдвяне Богослужіння у львівській римо-католицькій парафії Матері Божої Неустанної Помочі, де завершується будівництво нового храму. <br /><br />Для Радіо Ватикану, Костянтин Чавага</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,1857,5,1,NULL,42),
(4670,'P','2008-12-26',1,NULL,'Rss','Слово від Редакції','<p><img src=\"http://www.oranta.org/images/stories/dsc_00232.jpg\" style=\"float: right;\" /> Дорогі в Христі брати і сестри, ми представляємо Вам нашу нову програму «Різдвяна допомога дітям».</p>\n<p>Парафіяльна служба милосердя, стараннями своїх волонтерів-доброчинців продовжує допомагати потребуючим дітям і виконувати ті проекти які раніше були засвідчені.</p>\n<p>Особливістю «Різдвяної допомоги дітям» є те, що ми продовжуємо вести проект допомога дітям, матері яких перебувають в ув\'язнені. На запит парафіяльної служби, з департаменту виконання покарань ми отримали листа, де подаються уточненні списки дітей, які залишилися без материнської опіки. З огляду на таку розширену інформацію цього разу волонтери Карітас запропонували нову програму допомоги цим дітям. У листі є вказані точні адреси установ де перебувають матері, тому пані Зіновія запропонувала написати кожній матері лист і отримати від неї підтвердження точної адреси дитини. У листі ми наголошуємо: - «наша християнська громада від вашого імені хоче поздоровити вашу дитину зі святами і передати дарунок, але для цього потрібно щоб ви нам відповіли і вказали точну адресу перебування дитини». Пані Зіновія приготувала 150 таких листів і відіслала їх адресатам. У кожному листі є конверт з вказаною зворотною адресою і вкладений листок для письма. У такий спосіб ми хочемо залучити маму до активності допомоги дитині і самій отримати надію на життя і спасіння.</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=162:slovo-vid-redakciji&catid=31:kolonka-redaktora-cat&Itemid=1',1841,NULL,1,1,26);
INSERT INTO `publications_publication` VALUES
(4671,'P','2008-12-26',1,NULL,'Rss','Щорічний проект  мистецького центру «Майстерня пісні»','<p><img src=\"http://www.oranta.org/images/stories/2008/11/afisha1.jpg\" style=\"float: right;\" /> Запрошуємо!</p>\n<p>Щорічний проект  мистецького центру «Майстерня пісні»</p>\n<p>По Рождеству</p>\n<p>Образ Свята в голосах, звуках, кольорах</p>\n<p> </p>\n<p> «По Рождеству» збирає нас на своєрідний підсумок, післязвуччя  традиційних зимових свят</p>\n<p>Різдва - Нового року - Василя - Водохреща.</p>\n<p>Цього року дарунок для нас усіх - співпраця трьох :</p>\n<p>проект «Раса» -  давні коляди і щедрівки:    Наталя Половинка, Олена Костюк, Уляна Горбачевська</p>\n<p>зірка сучасної авангардної музики</p>\n<p>Клаус Кугель, Німеччина     - ударні, перкусії</p>\n<p>та креативна група Національного музею у Львові ім. А. Шептицького :</p>\n<p>Остап Лозинський, Олександр Бриндіков - мультимедійний проект «Споглядаючи ікону».</p>\n<p>                                       Рай упразнився - Христос народився!</p>\n<p> \n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=163:shchorichny-proekt-mysteckogo-centru-lmasternja-pisnir&catid=37:spilnota-cat&Itemid=49',1842,NULL,1,1,26),
(4672,'P','2008-12-26',1,NULL,'Note','Ющенко поручил подготовится к почтению памяти павших под Крутами','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ющенко поручил правительству разработать и в 2-недельный срок утвердить план мероприятий на 2009 год, предусмотрев там проведение в учебных заведениях военно-исторических чтений, образовательно-воспитательных мероприятий, возложение цветов к памятникам, связанных с битвой под Крутами, создание экспозиций, в том числе из архивных материалов.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Президент Виктор Ющенко поручил Кабинету Министров подготовить план мероприятий по почтению памяти павших в битве под Крутами.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Об этом говорится в указе №1188/2008 от 22 декабря.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ющенко поручил правительству разработать и в 2-недельный срок утвердить план мероприятий на 2009 год, предусмотрев там проведение в учебных заведениях военно-исторических чтений, образовательно-воспитательных мероприятий, возложение цветов к памятникам, связанных с битвой под Крутами, создание экспозиций, в том числе из архивных материалов.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Кроме того, Кабмин должен развивать музей \"Памяти героев Крут\" и создавать условия для его посещения, в первую очередь учениками и студентами.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В то же время Ющенко поручил Совету министров Крыма, областным, Киевской и Севастопольской городским государственным администрациям утвердить свои планы мероприятий.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">29 января 1918 года 250 киевских юнкеров, студентов и гимназистов из Студенческого куреня, а также 100 гайдамаков и вольных казаков и 12 конных казаков под командованием сотника Лощенко заняли оборону железнодорожной станции Круты, чтобы предотвратить наступление на Киев большевистских войск Советской России под командованием Муравьева.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">30 января 1918 года 6-тысячный отряд красной гвардии с матросами Балтийского флота во главе обошел линию украинской обороны.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Небольшой части гайдамаков и казаков удалось спастись, отступив на Киев, большинство погибло, а попавшие в плен гимназисты, юнкера и студенты были расстреляны.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">19 марта 1918 года тела части погибших были торжественно перезахоронены в братской Аскольдовой могиле в Киеве.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://h.ua/</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">УКАЗ ПРЕЗИДЕНТА УКРАЇНИ № 1188/2008</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Про вшанування подвигу Героїв Крут</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З метою вшанування подвигу юних захисників України у бою під Крутами у січні 1918 року та належного відзначення у 2009 році цієї історичної події <strong>постановляю:</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1. Кабінету Міністрів України:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1) розробити та затвердити у двотижневий строк план заходів із вшанування у 2009 році подвигу Героїв Крут, передбачивши, зокрема:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">проведення урочистих заходів із вшанування подвигу Героїв Крут, покладення квітів до пам\'ятників і пам\'ятних знаків, пов\'язаних із цією подією;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">проведення у загальноосвітніх, вищих, у тому числі військових, навчальних закладах тематичних воєнно-історичних читань, інших освітньо-виховних заходів;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">створення експозицій, у тому числі архівних матеріалів, присвячених подвигу Героїв Крут та пов\'язаним із цим подіям;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">2) вжити заходів, спрямованих на розвиток Меморіального комплексу &laquo;Пам\'яті Героїв Крут&raquo;, зокрема, його музейної експозиції, а також на створення належних умов для відвідування цього Комплексу насамперед учнівською та студентською молоддю.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">2. Раді міністрів Автономної Республіки Крим, обласним, Київській та Севастопольській міським державним адміністраціям затвердити плани заходів з відзначення 91-ї річниці подвигу Героїв Крут та забезпечити їх виконання, вжити додаткових заходів щодо впорядкування пам\'ятників та місць поховання осіб, які загинули у боротьбі за свободу і незалежність України.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">3. Державному комітету телебачення та радіомовлення України забезпечити висвітлення заходів із вшанування подвигу Героїв Крут, організувати цикли тематичних радіо- та телепередач, виступи у засобах масової інформації вчених, політичних та громадських діячів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Президент України Віктор ЮЩЕНКО</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">22 грудня 2008 року</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.president.gov.ua/</span></span></p>',NULL,1845,5,1,NULL,30);
INSERT INTO `publications_publication` VALUES
(4673,'P','2008-12-26',1,NULL,'Note','Родина-мать подорожала','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сейчас - 194 утвержденных символа столицы. В январе 2009 г. этот список может пополниться еще 22 объектами. Значит, появится больше возможностей для наполнения горбюджета - за счет предпринимателей, использующих в названиях своих заведений или продукции символику столицы.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сейчас - 194 утвержденных символа столицы. В январе 2009 г. этот список может пополниться еще 22 объектами. Значит, появится больше возможностей для наполнения горбюджета - за счет предпринимателей, использующих в названиях своих заведений или продукции символику столицы.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><img src=\"http://www.aif.ua/UserFiles/Image/2008/AIF/52/52_14.jpg\" border=\"1\" alt=\"\" hspace=\"2\" vspace=\"2\" align=\"left\" />Проня Прокоповна за деньги<br /><br /></strong>СРЕДИ уже &laquo;узаконенных&raquo; символов - Аскольдова могила, Киево-Печерская лавра, Гидропарк, река Де-сенка, Андреевская церковь, Золотые ворота, Крещатик, Оболонь, Лысая гора, Тель-бин, Мышеловка, Боричев Ток, Владимирский рынок. Есть в нем и само название города - &laquo;Киев&raquo; и производные от него. Юридические лица, использующие в предпринимательской деятельнос-<br />ти эти официальные символы столицы&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (названия или&nbsp;&nbsp; изображения), платят&nbsp;&nbsp;&nbsp; в&nbsp;&nbsp;&nbsp; бюджет 0,1%&nbsp;&nbsp; от&nbsp; стоимости продукции.&nbsp;&nbsp; А&nbsp; частные предприниматели - 5 необлагаемых &laquo;минимумов&raquo; (85 грн).<br />На сессии Киевсовета, запланированной на 17 января 2009 г., этот список может вырасти.&nbsp;&nbsp; Сим-<br />волами&nbsp;&nbsp; города могут стать также скульптуры Прони Прокоповны вместе с Голохвастовым, Архистратига Михаила (хоть мэру столицы Л. Черновецкому изваяние этого святого не нравится), Ярослава Мудрого у Золотых ворот (прозванного в народе &laquo;мужиком с тортом&raquo; из-за того, что князь держит в руках макет Софии Киевской, похожий на кондитерское изделие).<br /><br /><strong>Пробки - на пьедестал<br /></strong>&laquo;ХОРОШО,&nbsp; что популярные памятники и местности&nbsp; становятся&nbsp; официальными&nbsp;&nbsp;&nbsp; символами города,&nbsp;&nbsp; -&nbsp; считает директор&nbsp; аналитико-исследовательского&nbsp; центра &laquo;Институт&nbsp; города&raquo; <strong>Александр Сергиенко. - Ведь предприниматели, зарабатывающие на достоянии всех киевлян, должны наполнять городскую казну&raquo;. Кстати, в более выигрышном положении в этом плане - бизнесмены из пригорода, которые бесплатно могут использовать любые столичные символы и &laquo;наживаться&raquo; на них, ведь решения Киевсовета распространено только на предпринимателей-киевлян.</strong><br />Кстати, в результате соц-опроса киевлян, касающегося символики столицы, проведенного одной из компаний в начале уходящего 2008 г., оказалось, что 17% горожан символом города считают Киево-Печерскую лавру, 15% - Андреевский спуск, 14% - Майдан Независимости, 12% - ул. Крещатик, 9% - скульптуру Родины-матери, а 8% - транспортные пробки. Так что не исключено, что в обозримом будущем транспортные заторы станут еще одним символом столицы.<strong><br />Галина ГИРАК</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.aif.ua/<br /></span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>',NULL,1843,5,1,NULL,27);
INSERT INTO `publications_publication` VALUES
(4674,'P','2008-12-26',1,NULL,'Note','Владика Гліб (Лончина) про Візантійський духовний центр у Зальцбурґу','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Діяльність центру, безумовно, сприятиме глибшому порозумінню, а також і приязні між Сходом і Заходом\", - так для Департаменту інформації Владика Гліб (Лончина), Куріальний єпископ, прокоментував важливість відкриття у Зальцбурґу (Австрія) Візантійського духовного центру.<br /></span></span></p>','<p style=\"text-align: justify;\"><img style=\"float: right;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_ikonostas-u-Salzburgu.jpg.jpg\" alt=\"\" /><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\"Візантійський духовний центр у Зальцбурґу дає можливість іншим християнам глибше запізнатися з нашою духовністю, обрядами, звичаями. Його завдання - дати Церквам Заходу, а особливо людям Зальцбурґу, можливість брати активну участь і поділяти багатство візантійської духовності. Діяльність центру, безумовно, сприятиме глибшому порозумінню, а також і приязні між Сходом і Заходом\", - так для Департаменту інформації Владика Гліб (Лончина), Куріальний єпископ, прокоментував важливість відкриття у Зальцбурґу (Австрія) Візантійського духовного центру. Центр було відкрито і посвячено 22 листопада 2008 року за участі Владики Гліба.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Центр постав при українській католицькій спільноті, яка збирається на богослужіння у церкві Святого Марка в Зальцбурґу. Його основником є римо-католицький єпископ Владика Альойс Котґасер, Архиєпископ Зальцбурґу, при сприянні Кардинала Крістофа Шьонборна, Ординарія для католиків візантійського обряду в Австрії. Директором центру призначено о. Николая Горникевича, пароха української католицької спільноти у Зальцбурґу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За словами Владики Гліба, основними напрямками діяльності Візантійського духовного центру є передусім богослужіння: часи, вечірні, акафісти, Літургії, які служать німецькою мовою. Крім того, час від часу будуть проводитися лекції, доповіді, семінари, які допоможуть краще пізнати візантійську культуру і духовність.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Веб-сторінку Української Греко-Католицької Церкви у Західній Австрії знайдете за адресою: <a href=\"http://www.ukrainische-kirche.at/\" target=\"_blank\">www.ukrainische-kirche.at</a>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Департамент інформації УГКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>\r\n<p>&nbsp;</p>',NULL,1840,5,1,NULL,43),
(4675,'P','2008-12-25',1,NULL,'Note','Фестиваль-конкурс \"Аскольдів глас\"','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,1951,200,DSC_0263.JPG\" alt=\"\" />Фестиваль-конкурс \"Аскольдів глас\", що надав слухачам щасливу нагоду поринути в глибини літургійного та народно-обрядового поетичного слова та співу, якими впродовж віків українці дивували світ, відкрив нові імена і таланти, якими так багатий наш український народ.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Фото: Ігора Андрійовича Савицького&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,1951,200,1.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,1951,200,2.jpg\" alt=\"\" /></span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,1951,200,3.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,1951,200,4.jpg\" alt=\"\" /></span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,1951,200,6.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,1951,200,5.jpg\" alt=\"\" /></span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,1951,200,8.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,1951,200,7.jpg\" alt=\"\" /></span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,1951,200,9.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,1951,200,10.jpg\" alt=\"\" /><br /></span></span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,1951,200,11.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,1951,200,12.jpg\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,1951,200,14.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,1951,200,13.jpg\" alt=\"\" /></p>',NULL,1951,5,1,NULL,49);
INSERT INTO `publications_publication` VALUES
(4676,'P','2008-12-25',0,NULL,'Note','Аскольдова могила (комплекс) (IX - XX ст.)','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,1839,200,askoldova_mogila_1.jpg\" alt=\"\" />Аскольдова могила - це історична місцевість, розташована на крутих схилах над Дніпром недалеко від Липок і Берестового. Тут розташований парк \"Аскольдова могила\", археологічний культурний шар села Угорського, церква св. Миколая, декілька пам\'ятників та Зелений театр.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За літописами в цій місцевості у 882 відбулося вбивство київських князів Аскольда і Діра воєводою новгородського князя Рюрика Олегом, який після того захопив владу в Києві. Невдовзі Аскольда ніби-то поховали тут же.<br /> Ця легенда має деякі розходження з науковими даними. Наприклад, відомо, що князь Дір був попередником Аскольда і помер задовго до нього.</span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>','<p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\" bgcolor=\"#ffffff\">\r\n<tbody>\r\n<tr valign=\"top\">\r\n<td height=\"10\" valign=\"top\"><br /></td>\r\n<td colspan=\"2\" align=\"left\">Київ м., Печерський р-н, Аскольдова Могила іст. місц. , Паркова дорога</td>\r\n<td><br /></td>\r\n</tr>\r\n<tr valign=\"top\">\r\n<td height=\"10\" valign=\"top\"><br /></td>\r\n<td width=\"10%\">Статус:</td>\r\n<td width=\"80%\" align=\"left\">Пам\'ятка історії національного значення, Пам\'ятка природи місцевого значення, Пам\'ятка археології національного значення</td>\r\n<td width=\"9%\"><br /></td>\r\n</tr>\r\n<tr valign=\"top\">\r\n<td width=\"1%\" height=\"10\" valign=\"top\"><br /></td>\r\n<td width=\"10%\" align=\"left\">Тип:</td>\r\n<td width=\"80%\" align=\"left\">Садово-паркові, Поховальні</td>\r\n<td width=\"9%\"><br /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</p>\r\n<p align=\"justify\">Аскольдова могила - це історична місцевість, розташована на крутих схилах над Дніпром недалеко від Липок і Берестового. Тут розташований парк \"Аскольдова могила\", археологічний культурний шар села Угорського, церква св. Миколая, декілька пам\'ятників та Зелений театр.</p>\r\n<p align=\"justify\">За літописами в цій місцевості у 882 відбулося вбивство київських князів Аскольда і Діра воєводою новгородського князя Рюрика Олегом, який після того захопив владу в Києві. Невдовзі Аскольда ніби-то поховали тут же.<br /> Ця легенда має деякі розходження з науковими даними. Наприклад, відомо, що князь Дір був попередником Аскольда і помер задовго до нього.</p>\r\n<p align=\"justify\">Ще за часів Київської Русі на Аскольдовій могилі була збудована дерев\'яна церква. Вона неодноразово перебудовувалася, знищувалася і відновлювалася. Остання дерев\'яна трибанна Миколаївська церква була збудована у XVII столітті.</p>\r\n<p align=\"justify\">З 1786 цвинтар, що існував навколо церкви, стає міським.</p>\r\n<p align=\"justify\">У 1809-1810 на місці старої дерев\'яної церкви була збудована мурована церква-ротонда св. Миколи (арх. Меленський О.).</p>\r\n<p align=\"justify\">У 1845 цвинтар був закритий для загального користування. Після цього тут здійснювали поховання лише найбільш заможних мешканців Києва.<br /> Однак після 1871 кладовище знову починає активно використовуватися.</p>\r\n<p>&nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/1862--timm_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/1862--timm_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> <br />Аскольдова могила. Малюнок Тімма В., 1862.</p>\r\n<p align=\"justify\">У 1866 навколо Миколаївської церкви збудували огорожу з дзвіницею при вході.</p>\r\n<p>&nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/189%21%7E%7E---001_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/189%21%7E%7E---001_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> <br />Аскольдова могила: церква св. Миколи, цвинтар і огорожа з дзвіницею. Малюнок к. ХІХ ст.</p>\r\n<p align=\"justify\">У 1892-1894 церква св. Миколи була відремонтована.</p>\r\n<p align=\"justify\">В 1901 була збудована нова дзвіниця.</p>\r\n<p>&nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/190%21%7E%7E--001_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/190%21%7E%7E--001_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> <br />Аскольдова могила: Миколаївська церква, кладовище і огорожа з новою дзвіницею. Фото п. ХХ ст.</p>\r\n<p>&nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/190%21%7E%7E--002_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/190%21%7E%7E--002_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> &nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/190%21%7E%7E--003_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/190%21%7E%7E--003_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> <br />Усипальниця баронів Штейнгель (арх. Городецький В.) та могила-пам\'ятник Яценку О. Фото п. ХХ ст.</p>\r\n<p align=\"justify\">У 1919 кладовище було закрите.</p>\r\n<p align=\"justify\">У 1932 металеві частини надгробків і огорожі були вилучені на металобрухт.</p>\r\n<p align=\"justify\">У 1935 кладовище офіційно закрито і ліквідовано разом з переважною більшістю надгробків та склепів (ряд могил перенесено на інші кладовища).<br /> Над церквою св. Миколи у 1936 була надбудована колонада, і вона набула вигляду паркового павільйону.</p>\r\n<p align=\"justify\">Під час окупації Києва німецькими військами на Аскольдовій могилі було влаштовано кладовище німецьких солдат і офіцерів.</p>\r\n<p>&nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/1943--001_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/1943--001_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> <br />Німецьке кладовище на Аскольдовій могилі. Фото 1943.</p>\r\n<p align=\"justify\">Після звільнення Києва, у 1944 це кладовище ліквідували.</p>\r\n<p align=\"justify\">Після 1945 Аскольдова могила стала одним з військових кладовищ для загиблих учасників визволення Києва від фашистських загарбників.<br /> У 1957 прах загиблих радянських солдатів та офіцерів було перенесено до парку Вічної Слави.</p>\r\n<p>&nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/198%21%7E%7E--001_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/198%21%7E%7E--001_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> <br />Церква св. Миколи, перебудована в парковий павільйон. Фото до реставрації.</p>\r\n<p align=\"justify\">У 1992 церква була передана УГКЦ.<br /> Під час реставрації у 1997-1998 церкві попернули її первісний вигляд.</p>\r\n<p align=\"justify\">Нижче - фото Аскольдової могили 2005.</p>\r\n<p>&nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--337_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--337_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> &nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--362_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--362_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> <br />Парк та Церква св. Миколи на Аскольдовій могилі.</p>\r\n<p>&nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--353_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--353_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> <br />Каплиця Воздвиження Чесного Хреста.</p>\r\n<p>&nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--359_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--359_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> &nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--355_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--355_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> <br />Безіменні могили в парку та пеньок колись врослого в огорожу дерева.</p>\r\n<p>&nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--339_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--339_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> <br />В літописі є згадка про угрів, які ніби-то спинялися в цій місцевості табором у 898 р. Аскольдову могилу також називали Угорським урочищем, за однією версією - саме через цей угорський табір. Однак є і інша версія: від давньослов\'янського &laquo;утор&raquo; - крутий землистий берег.<br /> Як би там не було, над Парковою алеєю встановили пам\'ятний знак з угорською символікою. Фото 2005.</p>\r\n<p>&nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--343_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--343_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> <br />Пам\'ятник студентам, які загибли під Крутами в бою з більшовиками у 1918.</p>\r\n<p>&nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--327_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--327_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> &nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--330_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--330_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> &nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--332_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--332_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> <br />На іншому боці Паркової дороги у 2000 на честь 2000-річчя Різдва Христового був споруджений храм св. Андрія Первозванного, встановлений пам\'ятний знак та пам\'ятник святому. Фото 2005.</p>\r\n<p>&nbsp;</p>',NULL,1839,5,1,NULL,15);
INSERT INTO `publications_publication` VALUES
(4677,'P','2008-12-25',1,NULL,'Rss','Різдво у Ватикані з Папою Венедиктом XVI','<br />\n<p><img src=\"http://www.oranta.org/images/stories/2008/11/foto_055.jpg\" style=\"float: right;\" /> 25 грудня більшість християн світу урочисто святкують празник Різдва Христового. Також і ті, які відзначатимуть його за старим стилем, тобто 7 січня, єднаються з братами і сестрами у радісній прославі Господа нашого Ісуса Христа, що прийнявши людське тіло, народився у Вифлиємі.<br /> <br /> Вчора опівночі ватиканська базиліка святого апостола Павла була по вінця заповнена вірними, які з\'їхались з усього світу, щоб разом із Папою Венедиктом XVI відсвяткувати містерію Христового Різдва - таїнства Божого Світла, що просвічує темряву, Слова, що стало Тілом, Хліба спа сіння, що зійшов з небес.<br /> <br /> Сьогодні опівдні за римським часом з центральної лоджії ватиканської базиліки святого апостола Петра Вселенський Архиєрей Венедикт XVI виголосив різдвяне звернення до вірних та всіх людей доброї волі й привітав народи світу 64-ма мовами, серед яких також і українською.</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=161:rizdvo-u-vatykani-z-papoju-venedyktom-xvi&catid=32:gk-svit-cat&Itemid=58',1838,NULL,1,1,26),
(4678,'P','2008-12-25',1,NULL,'Rss','Українська колядка у Римі','<p><img src=\"http://www.oranta.org/images/stories/2008/11/foto_07.jpg\" style=\"float: left;\" /> Сьогодні українські колядки та різдвяні літургічні співи лунають також і у різних частинах Риму. Адже за григоріянським календарем свята в Римі відзначають Сестри Служебниці Непорочної Діви Марії, Сестри Василіянки з Генерального Дому, Отці Василіяни, Українські Папські Колегії. Згадані спільноти вчора збиралися за свят-вечірніми столами, а сьогодні у каплицях відправлено різдвяні богослуження. У церкві Христа Царя, що при Генеральному Домі Отців Василіян, у різдвяній Божественній Літургії взяли участь численні миряни, яких доля закинула на чужину. Користаючи з тієї нагоди, що святкові дні в Італії є вихідними, натомість, 7 січня вже розпочинаються робочі будні, багато вірних у ці дні намагаються взяти участь у богослуженнях.</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=160:ukrajinska-koljadka-u-rymi&catid=32:gk-svit-cat&Itemid=58',1835,NULL,1,1,26),
(4679,'P','2008-12-25',1,NULL,'Rss','Відкриття різдвяного вертепу на площі Святого Петра','<p><img src=\"http://www.oranta.org/images/stories/2008/11/foto_01.jpg\" style=\"float: left;\" /> На бажання Слуги Божого Папи Івана Павла ІІ, вже від 1982 року площу Святого Петра у Ватикані прикрашає різдвяний вертеп, за розмірами він сягає двоповерхового будинку. Традиція ватиканських вертепів збережена і теперішнім Папою Венедиктом ХVІ. Деякі статуї цьогорічного різдвяного дійства, що прикрашає ватиканську площу, походять із вертепу Святого Вінченцо Паллотті, що зберігається у базиліці Святого Андреа делле Валле і сягає 1842 року. Інші, натомість, були додані до згаданого вертепу продовж пізніших років. Згідно традиції, Різдво Христове супроводжується присутністю води та вогню, які символізують душевну чистоту і відновлення та перемогу світла над темрявою; ці два елементи присутні також і цього року у вертепі на площі Святого Петра.</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=159:vidkryttja-rizdvjanogo-vertepu-na-ploshchi-svjatogo-petra&catid=32:gk-svit-cat&Itemid=58',1834,NULL,1,1,26);
INSERT INTO `publications_publication` VALUES
(4680,'P','2008-12-25',1,NULL,'Note','Різдвяне привітання Архипископа-Митрополита Львівського латинського обряду Мечислава Мокшицького','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Святкуючи разом з усіма, хто вірить в день Христа, коли Він - джерело Життя, виконуючи одвічний замисел Божої любові, з\'явився нам в людській натурі, я бажаю, щоби надія, яка пливе з цих радісних подій, пронизувала кожен момент життя і дії, засвідчуючи про Божу доброту до кожної людини!<br /></span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З нагоди Різдва Христового за григоріянським календарем, Голова Конференції Римо-Католицьких Єпископів України Архиєпископ-Митрополит Львівський Високопреосвященний Мечислав Мокшицький звернувся з привітанням до слухачів Української програми Ватиканського радіо.<br /><br />&laquo;Мої дорогі!<br />Святкуючи разом з усіма, хто вірить в день Христа, коли Він - джерело Життя, виконуючи одвічний замисел Божої любові, з\'явився нам в людській натурі, я бажаю, щоби надія, яка пливе з цих радісних подій, пронизувала кожен момент життя і дії, засвідчуючи про Божу доброту до кожної людини!<br />Нехай Різдво Христове і увесь 2009 Рік принесуть ласку відновлення в послуху за натхненням Святого Духу, щоби ми могли принести наше життя на славу Божу і &laquo;щоби наша радість була повною&raquo; (пор. Ів. 16:24).<br />Христос Раждається!&raquo;<br /><br />24 грудня, у Навечір\'я Різдва Христового, о 18 год. за київським часом, у Львівській митрополичій базиліці Успіння Пресвятої Діви Марії розпочнеться Вігілійна Служба Божа - Пастирка, яку очолить Архиєпископ-Митрополит Львівський Високопреосвященний Мечислав Мокшицький. Це Богослужіння буде транслювати Львівське державне телебачення. <br /><br />Для Радіо Ватикану, Костянтин Чавага</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span><strong><em><br /></em></strong></p>',NULL,1833,5,1,NULL,42),
(4681,'P','2008-12-25',1,NULL,'Note','Різдвяні богослуження у Вифлиємі','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Святкування Різдва Господа Нашого Ісуса Христа розпочинаються і у місті, де Він народився. Сьогодні до Вифлиєму прибув латинський єрусалимський Патріарх Блаженніший Фуад Туаль.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Святкування Різдва Господа Нашого Ісуса Христа розпочинаються і у місті, де Він народився. Сьогодні до Вифлиєму прибув латинський єрусалимський Патріарх Блаженніший Фуад Туаль. Після привітання на площі Різдва, він відслужить урочисту Вечірню, а вночі очолить нічну різдвяну Службу Божу в храмі святої Катерини, що при базиліці Христового Різдва, яка побудована на тому місці, де був вертеп у якому народився Ісус. В богослуженні візьмуть участь місцеві вірні та паломники, що прибули до Святої Землі. Передбачається присутність Глави Палестинської Автономії Абу Мазена, представників міської влади та дипломатичного корпусу.<br /><br />Завтра блаженніший Фуад, латинський Патріарх Єрусалиму, у вифлиємській церкві Святої Катерини відслужить ранкову різдвяну Службу Божу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,1832,5,1,NULL,43);
INSERT INTO `publications_publication` VALUES
(4682,'P','2008-12-25',1,NULL,'Note','Християнська відповідальність за природу','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Напередодні Різдва, у промові до Римської Курії, 23 грудня 2008 року, Святіший Отець Венедикт XVI в особливий спосіб звернув увагу на питання християнської відповідальності за природу, основою якої є наша віра у Бога Творця.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Напередодні Різдва, у промові до Римської Курії, 23 грудня 2008 року, Святіший Отець Венедикт XVI в особливий спосіб звернув увагу на питання християнської відповідальності за природу, основою якої є наша віра у Бога Творця. Зокрема папа наголосив, що Земля - це не просто власність, яку можемо використовувати згідно своїх інтересів та побажань, але натомість ми покликані бути вірними &laquo;доглядачами створіння&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Понтифік закликав Церкву послужити для спасіння природи. Він сказав, що Церква сьогодні не може обмежитись на духовну проповідь. Згідно папи Венедикта XVI &laquo;Церква несе відповідальність за все створіння і зобов\'язана цю відповідальність унаочнити в дії&raquo;. При цьому святіший отець наголосив, що &laquo;Церква мусить не просто оберігати землю, воду та повітря як Божі дари, які належать всім&raquo;, але вона мусить насамперед піклуватися про формування здорової &laquo;екології людини&raquo;, захищаючи її від самознищення.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Інформаційна служба Екологічного центру</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Івано-Франківської єпархії УГКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>',NULL,1830,5,1,NULL,42),
(4683,'P','2008-12-24',1,NULL,'Note','Пам‘ятаймо і поминаймо синів наших','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">23 грудня року Божого у церкві св. Миколая Чудотворця на Аскольдовій могилі, на проханням родини вшанували пам\'ять Івана Корпана з Долини. Також попросили молитися за душі Дмитра Данилишина та Василя Біласа, уродженців Трускавця.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">23 грудня року Божого у церкві св. Миколая Чудотворця на Аскольдовій могилі, на проханням родини вшанували пам\'ять Івана Корпана з Долини. Також попросили молитися за душі Дмитра Данилишина та Василя Біласа, уродженців Трускавця. <img style=\"float: left;\" src=\"image,1829,200,251206.jpg\" alt=\"\" width=\"150\" height=\"96\" />1932 року їх було страчено у Бригідках і поховано на Янівському цвинтарі у Львові. Ці молоді хлопці віддали своє життя за волю України.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Декан Київський Ігор Онишкевич пригадав їхню історію, подякував родині за поминання спочилих душ. Декан наголосив: - хоч і геройський вчинок віруючих хлопців не може бути схвалений християнством, оскільки вони чинили насилля, навіть в ім\'я незалежності України, але ми не маємо права про них забувати, ми повинні молитися у поминанні і пам\'ятати їх, як наших синів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Матеріал Іллі, &laquo;Оранта&raquo;</span></span></p>\r\n<p>&nbsp;</p>',NULL,1829,5,1,NULL,17);
INSERT INTO `publications_publication` VALUES
(4684,'P','2008-12-24',1,NULL,'Note','Do good without expecting reward ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Father-superior of the Greek-Catholic church is father Grygoriy and through&nbsp;him the help is&nbsp;distributed. Four families at the moment: those of Roman Myronyak, Borys Myronyak, Lesya Nykolaychuk and Oksana Uhors\'ka have sent us thank you letters.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">St.Nicolas parish on Askoldova Mohyla helps victims of this summer floods, among them inhabitants of the village Voskresinci&nbsp;near Kolomya in Ivano-Frankivs\'k region.&nbsp;The parishioners collect food product, clothing, donate everything they can afford. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Father-superior of the Greek-Catholic church is father Grygoriy and through&nbsp;him the help is&nbsp;distributed. Four families at the moment: those of Roman Myronyak, Borys Myronyak, Lesya Nykolaychuk and Oksana Uhors\'ka have sent us thank you letters. <br />&nbsp;   <br />Though our help is modest as compared with the disaster it is genuine and with all of our hearts. We have sent packages containing food products, household &nbsp;articles, and medicine in October, November, and December addressed for father Grygoriy Simkovych who then passes the help to the victims. The total cost of the goods is 2131 Hryvna. This help was collected by Kyiv parishes and send by volunteers of the Caritas-Petchersk organization. We also have sent the invitations for children from these families to come for Christmas holidays to Kyiv. <br />&nbsp;   <br />Let\'s help each other, love each other and God will live in our hearts.</span></span></p>',NULL,1982,5,2,NULL,19),
(4685,'P','2008-12-24',1,NULL,'Rss','Святий Миколай завітав до Штутгарту','<p>Українці з давніх часів вважали Миколая своїм святим.</p>\n<p><img style=\"float: left;\" src=\"http://www.askoldova-mohyla.org/uk/image,1827,200,MSTBen100_2081.jpg\" />Святий Миколай - образ, що асоціюється з високими людськими моральними цінностями, глибокою вірою у справедливість існування на землі. Не гроші, а любов, порядність, доброчесність та благородство керують світом. І один раз на рік, у чарівну грудневу ніч усі ми маємо можливість бути доброчинцями задля власної втіхи і Божої ласки. Дякуючи доброму імені Святого Миколая кожен з нас може зробити щасливими діток, обділених долею у батьківській ласці. Та й до власних дітей цього дня ми маємо можливість прийти з казкою і відзначити за слухняність та гарне навчання.</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=158:svjaty-mykola-zavitav-do-shtutgartu&catid=32:gk-svit-cat&Itemid=58',1828,NULL,1,1,26),
(4686,'N','2008-12-24',0,NULL,'Note','1','','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Українці з давніх часів вважали Миколая своїм святим.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,1827,200,MSTBen100_2081.jpg\" alt=\"\" />Святий Миколай - образ, що асоціюється з високими людськими моральними цінностями, глибокою вірою у справедливість існування на землі. Не гроші, а любов, порядність, доброчесність та благородство керують світом. І один раз на рік, у чарівну грудневу ніч усі ми маємо можливість бути доброчинцями задля власної втіхи і Божої ласки. Дякуючи доброму імені Святого Миколая кожен з нас може зробити щасливими діток, обділених долею у батьківській ласці. Та й до власних дітей цього дня ми маємо можливість прийти з казкою і відзначити за слухняність та гарне навчання.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,1827,200,MikSt_100_2063.jpg\" alt=\"\" />До Парафії Святого Василія Великого з центром у Штутгарті цього року завітав Святий Миколай. У неділю 21 грудня на Святу Літургію до Української церкви в Штутгарті з\'їхалися родини з дітьми з усього Вюртембергу. Парох Роман Врущак благословив усіх дітей.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Після закінчення урочистої Літургії вірні заспівали українську пісню &laquo;Ой, хто, хто, Миколая любить&raquo; і до церкви увійшов Святий Миколай з великим мішком. Гості з України, діти з львівської родини Щур приготували для Чудотворця невелику програму з українських пісень та віршів. Тож Миколай першими подарував двом Софійкам та Насті Щур мішечки з подарунками. Усі дітки отримали від нього українські образочки та цукерки.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,1827,200,MStZUS_100_2086.jpg\" alt=\"\" />Особливо зворушливо було бачити на святі родину Деніс. Ця родина має власного сина Бена. А ще усиновила двох українських дітей Андрійка та Богданку з сиротинця у Житомирі. Батьки роблять все для того, щоб українські діти не забували у Німеччині свою церкву, мову та культуру.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Святого Миколая майстерно зіграв режисер Андрій Критенко. Роздавати подарунки йому допомагав дяк Торстен Галл.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">А ще в Українській церкві того дня з\'явилася новорічна ялинка, що засяяла електричними гірляндами.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дмитро Мірецький,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"mailto:miretsky@ukr.net\">miretsky@ukr.net</a></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Штутгарт, Німеччина</span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>',NULL,1827,5,1,NULL,43);
INSERT INTO `publications_publication` VALUES
(4687,'P','2008-12-23',0,NULL,'Note','Історія','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Стародавня Микільська <a title=\"Церква\" href=\"http://uk.wikipedia.org/wiki/%D0%A6%D0%B5%D1%80%D0%BA%D0%B2%D0%B0\">церква</a> була споруджена не пізніше <a title=\"10 століття\" href=\"http://uk.wikipedia.org/wiki/10_%D1%81%D1%82%D0%BE%D0%BB%D1%96%D1%82%D1%82%D1%8F\">Х століття</a> якимось Ольмою поряд зі своїм двором, над могилою <a title=\"Князь\" href=\"http://uk.wikipedia.org/wiki/%D0%9A%D0%BD%D1%8F%D0%B7%D1%8C\">князя</a> <a title=\"Аскольд\" href=\"http://uk.wikipedia.org/wiki/%D0%90%D1%81%D0%BA%D0%BE%D0%BB%D1%8C%D0%B4\">Аскольда</a> в <a title=\"Угорське урочище\" href=\"http://uk.wikipedia.org/wiki/%D0%A3%D0%B3%D0%BE%D1%80%D1%81%D1%8C%D0%BA%D0%B5_%D1%83%D1%80%D0%BE%D1%87%D0%B8%D1%89%D0%B5\">урочищі Угорському</a>, яке з <a title=\"18 століття\" href=\"http://uk.wikipedia.org/wiki/18_%D1%81%D1%82%D0%BE%D0%BB%D1%96%D1%82%D1%82%D1%8F\">XVIІІ столітті</a> зветься Аскольдовою могилою. Назву церква отримала за ім\'ям, яке прийняв Аскольд при хрещенні у <a title=\"867\" href=\"http://uk.wikipedia.org/wiki/867\">867</a> році&nbsp;- Микола. Певний час поширеною була думка про те, що до будування церкви причетна <a title=\"Ольга (княгиня)\" href=\"http://uk.wikipedia.org/wiki/%D0%9E%D0%BB%D1%8C%D0%B3%D0%B0_%28%D0%BA%D0%BD%D1%8F%D0%B3%D0%B8%D0%BD%D1%8F%29\">княгиня Ольга</a>, а, отже, церква є найдавнішою на <a title=\"Київська Русь\" href=\"http://uk.wikipedia.org/wiki/%D0%9A%D0%B8%D1%97%D0%B2%D1%81%D1%8C%D0%BA%D0%B0_%D0%A0%D1%83%D1%81%D1%8C\">Русі</a>. Виникла ця думка, скоріше за все, через помилку переписувача, який, свого часу, замість &laquo;Олма&raquo; написав &laquo;Олга&raquo;. Втім, деякі дослідники та автори дотримуються прямо протилежної версії. Найсміливіші обстоюють фантастичне твердження, що засновник <a title=\"Православ\'я\" href=\"http://uk.wikipedia.org/wiki/%D0%9F%D1%80%D0%B0%D0%B2%D0%BE%D1%81%D0%BB%D0%B0%D0%B2%27%D1%8F\">православного</a> храму Ольма&nbsp;- це язичник Олмош (Альмош), ватажок <a title=\"Мадяри\" href=\"http://uk.wikipedia.org/wiki/%D0%9C%D0%B0%D0%B4%D1%8F%D1%80%D0%B8\">угрів</a>, які проходили повз <a title=\"Київ\" href=\"http://uk.wikipedia.org/wiki/%D0%9A%D0%B8%D1%97%D0%B2\">Київ</a> наприкінці <a title=\"9 століття\" href=\"http://uk.wikipedia.org/wiki/9_%D1%81%D1%82%D0%BE%D0%BB%D1%96%D1%82%D1%82%D1%8F\">ІХ століття</a>.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Стародавня історія</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Стародавня Микільська <a title=\"Церква\" href=\"http://uk.wikipedia.org/wiki/%D0%A6%D0%B5%D1%80%D0%BA%D0%B2%D0%B0\">церква</a> була споруджена не пізніше <a title=\"10 століття\" href=\"http://uk.wikipedia.org/wiki/10_%D1%81%D1%82%D0%BE%D0%BB%D1%96%D1%82%D1%82%D1%8F\">Х століття</a> якимось Ольмою поряд зі своїм двором, над могилою <a title=\"Князь\" href=\"http://uk.wikipedia.org/wiki/%D0%9A%D0%BD%D1%8F%D0%B7%D1%8C\">князя</a> <a title=\"Аскольд\" href=\"http://uk.wikipedia.org/wiki/%D0%90%D1%81%D0%BA%D0%BE%D0%BB%D1%8C%D0%B4\">Аскольда</a> в <a title=\"Угорське урочище\" href=\"http://uk.wikipedia.org/wiki/%D0%A3%D0%B3%D0%BE%D1%80%D1%81%D1%8C%D0%BA%D0%B5_%D1%83%D1%80%D0%BE%D1%87%D0%B8%D1%89%D0%B5\">урочищі Угорському</a>, яке з <a title=\"18 століття\" href=\"http://uk.wikipedia.org/wiki/18_%D1%81%D1%82%D0%BE%D0%BB%D1%96%D1%82%D1%82%D1%8F\">XVIІІ столітті</a> зветься Аскольдовою могилою. Назву церква отримала за ім\'ям, яке прийняв Аскольд при хрещенні у <a title=\"867\" href=\"http://uk.wikipedia.org/wiki/867\">867</a> році&nbsp;- Микола. Певний час поширеною була думка про те, що до будування церкви причетна <a title=\"Ольга (княгиня)\" href=\"http://uk.wikipedia.org/wiki/%D0%9E%D0%BB%D1%8C%D0%B3%D0%B0_%28%D0%BA%D0%BD%D1%8F%D0%B3%D0%B8%D0%BD%D1%8F%29\">княгиня Ольга</a>, а, отже, церква є найдавнішою на <a title=\"Київська Русь\" href=\"http://uk.wikipedia.org/wiki/%D0%9A%D0%B8%D1%97%D0%B2%D1%81%D1%8C%D0%BA%D0%B0_%D0%A0%D1%83%D1%81%D1%8C\">Русі</a>. Виникла ця думка, скоріше за все, через помилку переписувача, який, свого часу, замість &laquo;Олма&raquo; написав &laquo;Олга&raquo;. Втім, деякі дослідники та автори дотримуються прямо протилежної версії. Найсміливіші обстоюють фантастичне твердження, що засновник <a title=\"Православ\'я\" href=\"http://uk.wikipedia.org/wiki/%D0%9F%D1%80%D0%B0%D0%B2%D0%BE%D1%81%D0%BB%D0%B0%D0%B2%27%D1%8F\">православного</a> храму Ольма&nbsp;- це язичник Олмош (Альмош), ватажок <a title=\"Мадяри\" href=\"http://uk.wikipedia.org/wiki/%D0%9C%D0%B0%D0%B4%D1%8F%D1%80%D0%B8\">угрів</a>, які проходили повз <a title=\"Київ\" href=\"http://uk.wikipedia.org/wiki/%D0%9A%D0%B8%D1%97%D0%B2\">Київ</a> наприкінці <a title=\"9 століття\" href=\"http://uk.wikipedia.org/wiki/9_%D1%81%D1%82%D0%BE%D0%BB%D1%96%D1%82%D1%82%D1%8F\">ІХ століття</a>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У <a title=\"971\" href=\"http://uk.wikipedia.org/wiki/971\">971</a> році церкву Святого Миколая було зруйновано сином Ольги&nbsp;- <a title=\"Святослав Ігоревич\" href=\"http://uk.wikipedia.org/wiki/%D0%A1%D0%B2%D1%8F%D1%82%D0%BE%D1%81%D0%BB%D0%B0%D0%B2_%D0%86%D0%B3%D0%BE%D1%80%D0%B5%D0%B2%D0%B8%D1%87\">Святославом</a>, правління якого пов\'язане із жорстокою <a title=\"Язичництво\" href=\"http://uk.wikipedia.org/wiki/%D0%AF%D0%B7%D0%B8%D1%87%D0%BD%D0%B8%D1%86%D1%82%D0%B2%D0%BE\">язичницькою</a> реакцією, переслідуванням <a title=\"Християнство\" href=\"http://uk.wikipedia.org/wiki/%D0%A5%D1%80%D0%B8%D1%81%D1%82%D0%B8%D1%8F%D0%BD%D1%81%D1%82%D0%B2%D0%BE\">християн</a> і нищенням церков. Лише у <a title=\"990\" href=\"http://uk.wikipedia.org/wiki/990\">990</a> році князь <a title=\"Володимир Великий\" href=\"http://uk.wikipedia.org/wiki/%D0%92%D0%BE%D0%BB%D0%BE%D0%B4%D0%B8%D0%BC%D0%B8%D1%80_%D0%92%D0%B5%D0%BB%D0%B8%D0%BA%D0%B8%D0%B9\">Володимир</a> знову спорудив тут <a title=\"Дерево\" href=\"http://uk.wikipedia.org/wiki/%D0%94%D0%B5%D1%80%D0%B5%D0%B2%D0%BE\">дерев\'яний</a> храм. <a title=\"1036\" href=\"http://uk.wikipedia.org/wiki/1036\">1036</a> року при церкві було засновано жіночий <a title=\"Монастир\" href=\"http://uk.wikipedia.org/wiki/%D0%9C%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D0%B8%D1%80\">монастир</a>, у якому доживала свого віку мати одного із засновників <a title=\"Києво-Печерська лавра\" href=\"http://uk.wikipedia.org/wiki/%D0%9A%D0%B8%D1%94%D0%B2%D0%BE-%D0%9F%D0%B5%D1%87%D0%B5%D1%80%D1%81%D1%8C%D0%BA%D0%B0_%D0%BB%D0%B0%D0%B2%D1%80%D0%B0\">Києво-Печерської обителі</a> <a title=\"Феодосій Печерський\" href=\"http://uk.wikipedia.org/wiki/%D0%A4%D0%B5%D0%BE%D0%B4%D0%BE%D1%81%D1%96%D0%B9_%D0%9F%D0%B5%D1%87%D0%B5%D1%80%D1%81%D1%8C%D0%BA%D0%B8%D0%B9\">преподобного Феодосія</a>. <sup><a href=\"http://uk.wikipedia.org/wiki/%D0%A6%D0%B5%D1%80%D0%BA%D0%B2%D0%B0_%D0%A1%D0%B2%D1%8F%D1%82%D0%BE%D0%B3%D0%BE_%D0%9C%D0%B8%D0%BA%D0%BE%D0%BB%D0%B0%D1%8F_%D0%BD%D0%B0_%D0%90%D1%81%D0%BA%D0%BE%D0%BB%D1%8C%D0%B4%D0%BE%D0%B2%D1%96%D0%B9_%D0%BC%D0%BE%D0%B3%D0%B8%D0%BB%D1%96#cite_note-0\">[1]</a></sup> Він містився чи то на самій Аскольдовій могилі, чи то на узвишші над нею.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Існує переказ, пов\'язаний із подією, яка сталася <a title=\"1113\" href=\"http://uk.wikipedia.org/wiki/1113\">1113</a> року в житті великого князя Київського <a title=\"Мстислав I Володимирович (Великий)\" href=\"http://uk.wikipedia.org/wiki/%D0%9C%D1%81%D1%82%D0%B8%D1%81%D0%BB%D0%B0%D0%B2_I_%D0%92%D0%BE%D0%BB%D0%BE%D0%B4%D0%B8%D0%BC%D0%B8%D1%80%D0%BE%D0%B2%D0%B8%D1%87_%28%D0%92%D0%B5%D0%BB%D0%B8%D0%BA%D0%B8%D0%B9%29\">Мстислава Володимировича</a>, сина <a title=\"Володимир Мономах\" href=\"http://uk.wikipedia.org/wiki/%D0%92%D0%BE%D0%BB%D0%BE%D0%B4%D0%B8%D0%BC%D0%B8%D1%80_%D0%9C%D0%BE%D0%BD%D0%BE%D0%BC%D0%B0%D1%85\">Мономаха</a>. Повертаючись пізньої пори із полювання, він заблукав у дрімучому <a title=\"Ліс\" href=\"http://uk.wikipedia.org/wiki/%D0%9B%D1%96%D1%81\">лісі</a>, який покривав собою місцевість сучасного <a title=\"Печерськ\" href=\"http://uk.wikipedia.org/wiki/%D0%9F%D0%B5%D1%87%D0%B5%D1%80%D1%81%D1%8C%D0%BA\">Печерська</a>. Шукаючи дорогу з нього, князь несподівано побачив яскраве світло, що його випромінював образ <a title=\"Святий Миколай\" href=\"http://uk.wikipedia.org/wiki/%D0%A1%D0%B2%D1%8F%D1%82%D0%B8%D0%B9_%D0%9C%D0%B8%D0%BA%D0%BE%D0%BB%D0%B0%D0%B9\">Cвятого Миколая</a>, який містився на високому пеньку, якраз біля тієї самої дороги. На згадку про таке чудесне явлення він <a title=\"1115\" href=\"http://uk.wikipedia.org/wiki/1115\">1115</a> року заснував при Миколаївській церкві на Аскольдовій могилі (чи перетворив із зазначеного вище жіночого) чоловічий монастир (чи відновив після зруйнування його <a title=\"1096\" href=\"http://uk.wikipedia.org/wiki/1096\">1096</a> року <a title=\"Половці\" href=\"http://uk.wikipedia.org/wiki/%D0%9F%D0%BE%D0%BB%D0%BE%D0%B2%D1%86%D1%96\">половцями</a>). Через якийсь час на місці пригоди, що сталася із Мстиславом, з\'явилася стовпоподібна <a title=\"Каплиця (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%9A%D0%B0%D0%BF%D0%BB%D0%B8%D1%86%D1%8F&amp;action=edit&amp;redlink=1\">каплиця</a> (слуп) із образом Cвятого Миколая, яка вказувала напрямок руху до обителі на <a title=\"Аскольдова могила\" href=\"http://uk.wikipedia.org/wiki/%D0%90%D1%81%D0%BA%D0%BE%D0%BB%D1%8C%D0%B4%D0%BE%D0%B2%D0%B0_%D0%BC%D0%BE%D0%B3%D0%B8%D0%BB%D0%B0\">Аскольдовій могилі</a>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">До самого <a title=\"Микільський Пустинський монастир (Київ) (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%9C%D0%B8%D0%BA%D1%96%D0%BB%D1%8C%D1%81%D1%8C%D0%BA%D0%B8%D0%B9_%D0%9F%D1%83%D1%81%D1%82%D0%B8%D0%BD%D1%81%D1%8C%D0%BA%D0%B8%D0%B9_%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D0%B8%D1%80_%28%D0%9A%D0%B8%D1%97%D0%B2%29&amp;action=edit&amp;redlink=1\">Микільського монастиря</a>, оточеного густим пустинним лісом, додалися назви Пустинний, Слупський або Стовповий. <a title=\"1147\" href=\"http://uk.wikipedia.org/wiki/1147\">1147</a> року князь <a title=\"Андрій Боголюбський\" href=\"http://uk.wikipedia.org/wiki/%D0%90%D0%BD%D0%B4%D1%80%D1%96%D0%B9_%D0%91%D0%BE%D0%B3%D0%BE%D0%BB%D1%8E%D0%B1%D1%81%D1%8C%D0%BA%D0%B8%D0%B9\">Андрій Боголюбський</a> приписав його до Києво-Печерської обителі. Відновлений після руйнації, заподіяної <a title=\"1240\" href=\"http://uk.wikipedia.org/wiki/1240\">1240</a> року татаро-монголами <a title=\"Хан\" href=\"http://uk.wikipedia.org/wiki/%D0%A5%D0%B0%D0%BD\">хана</a> <a title=\"Батий\" href=\"http://uk.wikipedia.org/wiki/%D0%91%D0%B0%D1%82%D0%B8%D0%B9\">Батия</a>, монастир у <a title=\"15 століття\" href=\"http://uk.wikipedia.org/wiki/15_%D1%81%D1%82%D0%BE%D0%BB%D1%96%D1%82%D1%82%D1%8F\">XV столітті</a> знову грабувався <a title=\"Кримські татари\" href=\"http://uk.wikipedia.org/wiki/%D0%9A%D1%80%D0%B8%D0%BC%D1%81%D1%8C%D0%BA%D1%96_%D1%82%D0%B0%D1%82%D0%B0%D1%80%D0%B8\">татарами</a>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Найдавніше зображення церкви Святого Миколая&nbsp;- дерев\'яної, з трьома банями&nbsp;- було знайдене на <a title=\"План Києва 1638 року (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%9F%D0%BB%D0%B0%D0%BD_%D0%9A%D0%B8%D1%94%D0%B2%D0%B0_1638_%D1%80%D0%BE%D0%BA%D1%83&amp;action=edit&amp;redlink=1\">плані Кальнофойського</a>. Наприкінці <a title=\"XVI століття\" href=\"http://uk.wikipedia.org/wiki/XVI_%D1%81%D1%82%D0%BE%D0%BB%D1%96%D1%82%D1%82%D1%8F\">XVI століття</a><a title=\"Берестейська унія\" href=\"http://uk.wikipedia.org/wiki/%D0%91%D0%B5%D1%80%D0%B5%D1%81%D1%82%D0%B5%D0%B9%D1%81%D1%8C%D0%BA%D0%B0_%D1%83%D0%BD%D1%96%D1%8F\">уніатською</a>. За <a title=\"Митрополит\" href=\"http://uk.wikipedia.org/wiki/%D0%9C%D0%B8%D1%82%D1%80%D0%BE%D0%BF%D0%BE%D0%BB%D0%B8%D1%82\">митрополита</a> Київського <a title=\"Петро Могила\" href=\"http://uk.wikipedia.org/wiki/%D0%9F%D0%B5%D1%82%D1%80%D0%BE_%D0%9C%D0%BE%D0%B3%D0%B8%D0%BB%D0%B0\">Петра Могили</a><a title=\"Гетьман\" href=\"http://uk.wikipedia.org/wiki/%D0%93%D0%B5%D1%82%D1%8C%D0%BC%D0%B0%D0%BD\">гетьманів</a> цілий ряд універсалів і грамот на володіння маєтностями. Один із них, а саме <a title=\"Іван Мазепа\" href=\"http://uk.wikipedia.org/wiki/%D0%86%D0%B2%D0%B0%D0%BD_%D0%9C%D0%B0%D0%B7%D0%B5%D0%BF%D0%B0\">Іван Мазепа</a> упродовж <a title=\"1690\" href=\"http://uk.wikipedia.org/wiki/1690\">1690</a>-<a title=\"1696\" href=\"http://uk.wikipedia.org/wiki/1696\">1696</a><a title=\"Гора\" href=\"http://uk.wikipedia.org/wiki/%D0%93%D0%BE%D1%80%D0%B0\">горі</a> над Миколаївською церквою за проектом <a title=\"Архітектор\" href=\"http://uk.wikipedia.org/wiki/%D0%90%D1%80%D1%85%D1%96%D1%82%D0%B5%D0%BA%D1%82%D0%BE%D1%80\">архітектора</a> <a title=\"Старцев Йосиф\" href=\"http://uk.wikipedia.org/wiki/%D0%A1%D1%82%D0%B0%D1%80%D1%86%D0%B5%D0%B2_%D0%99%D0%BE%D1%81%D0%B8%D1%84\">Йосипа Старцева</a> великий мурований 5-банний <a title=\"Військово-Микільський собор (Київ) (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%92%D1%96%D0%B9%D1%81%D1%8C%D0%BA%D0%BE%D0%B2%D0%BE-%D0%9C%D0%B8%D0%BA%D1%96%D0%BB%D1%8C%D1%81%D1%8C%D0%BA%D0%B8%D0%B9_%D1%81%D0%BE%D0%B1%D0%BE%D1%80_%28%D0%9A%D0%B8%D1%97%D0%B2%29&amp;action=edit&amp;redlink=1\">Військово-Микільський собор</a>, який став соборним храмом Микільського монастиря. У <a title=\"Келія (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%9A%D0%B5%D0%BB%D1%96%D1%8F&amp;action=edit&amp;redlink=1\">келії</a> довкола нього переселилася більша частина братії із території на Аскольдовій могилі, а під горою лишився монастирський <a title=\"Цвинтар\" href=\"http://uk.wikipedia.org/wiki/%D0%A6%D0%B2%D0%B8%D0%BD%D1%82%D0%B0%D1%80\">цвинтар</a> з дерев\'яною Миколаївською церквою, котра з головної перетворилася на приписну цвинтарну.</span> обитель була певний час  вона отримала від українських  років власним коштом спорудив на </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Новий храм</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У <a title=\"1809\" href=\"http://uk.wikipedia.org/wiki/1809\">1809</a> році <a title=\"Вороніж\" href=\"http://uk.wikipedia.org/wiki/%D0%92%D0%BE%D1%80%D0%BE%D0%BD%D1%96%D0%B6\">воронезький</a> купець Самійло Мещеряков побажав увічнити память своєї <a title=\"Дружина (жінка)\" href=\"http://uk.wikipedia.org/wiki/%D0%94%D1%80%D1%83%D0%B6%D0%B8%D0%BD%D0%B0_%28%D0%B6%D1%96%D0%BD%D0%BA%D0%B0%29\">дружини</a>, яка померла у Києві і була похована на <a title=\"Аскольдова могила\" href=\"http://uk.wikipedia.org/wiki/%D0%90%D1%81%D0%BA%D0%BE%D0%BB%D1%8C%D0%B4%D0%BE%D0%B2%D0%B0_%D0%BC%D0%BE%D0%B3%D0%B8%D0%BB%D0%B0\">Аскольдовій могилі</a>. З цією метою він замовив <a title=\"Меленський Андрій Іванович (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%9C%D0%B5%D0%BB%D0%B5%D0%BD%D1%81%D1%8C%D0%BA%D0%B8%D0%B9_%D0%90%D0%BD%D0%B4%D1%80%D1%96%D0%B9_%D0%86%D0%B2%D0%B0%D0%BD%D0%BE%D0%B2%D0%B8%D1%87&amp;action=edit&amp;redlink=1\">Андрієві Меленському</a> проект мурованої церкви, яка й була збудована того ж року (під керівництвом Василя Сєрікова) за 8 тисяч <a title=\"Рубль\" href=\"http://uk.wikipedia.org/wiki/%D0%A0%D1%83%D0%B1%D0%BB%D1%8C\">рублів</a> і освячена <a title=\"1 вересня\" href=\"http://uk.wikipedia.org/wiki/1_%D0%B2%D0%B5%D1%80%D0%B5%D1%81%D0%BD%D1%8F\">1 вересня</a> <a title=\"1810\" href=\"http://uk.wikipedia.org/wiki/1810\">1810</a> року. Церква була двоповерхова, виконана у стилі <a title=\"Ампір\" href=\"http://uk.wikipedia.org/wiki/%D0%90%D0%BC%D0%BF%D1%96%D1%80\">ампір</a> та отримала форму ротонди, оточеної колонадою тосканського ордеру. Її вінчала велика напівсферична баня з <a title=\"Ліхтар (надбудова)\" href=\"http://uk.wikipedia.org/wiki/%D0%9B%D1%96%D1%85%D1%82%D0%B0%D1%80_%28%D0%BD%D0%B0%D0%B4%D0%B1%D1%83%D0%B4%D0%BE%D0%B2%D0%B0%29\">ліхтарем</a> та маленькою дзвіницею (у <a title=\"1882\" href=\"http://uk.wikipedia.org/wiki/1882\">1882</a> баню храму було дещо спрощено). <a title=\"Тарас Шевченко\" href=\"http://uk.wikipedia.org/wiki/%D0%A2%D0%B0%D1%80%D0%B0%D1%81_%D0%A8%D0%B5%D0%B2%D1%87%D0%B5%D0%BD%D0%BA%D0%BE\">Тарас Шевченко</a><a title=\"Акварель\" href=\"http://uk.wikipedia.org/wiki/%D0%90%D0%BA%D0%B2%D0%B0%D1%80%D0%B5%D0%BB%D1%8C\">акварелей</a>. Всередині церкви, у напівпідвальній крипті було передбачено 77 місць для поховань. У <a title=\"1816\" href=\"http://uk.wikipedia.org/wiki/1816\">1816</a> році церкву розписано іконописцем Білецьким, а у <a title=\"1840\" href=\"http://uk.wikipedia.org/wiki/1840\">1840</a> році цвинтар закрили.</span> зобразив її на одній з </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У <a title=\"1847\" href=\"http://uk.wikipedia.org/wiki/1847\">1847</a> році церкву разом із цвинтарем планувалося знести, оскільки поруч прокладався шлях до <a title=\"Миколаївський ланцюговий міст (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%9C%D0%B8%D0%BA%D0%BE%D0%BB%D0%B0%D1%97%D0%B2%D1%81%D1%8C%D0%BA%D0%B8%D0%B9_%D0%BB%D0%B0%D0%BD%D1%86%D1%8E%D0%B3%D0%BE%D0%B2%D0%B8%D0%B9_%D0%BC%D1%96%D1%81%D1%82&amp;action=edit&amp;redlink=1\">Ланцюгового мосту</a>, а крім того, вона була в аварійному стані внаслідок зсувів схилу. Однак <a title=\"10 вересня\" href=\"http://uk.wikipedia.org/wiki/10_%D0%B2%D0%B5%D1%80%D0%B5%D1%81%D0%BD%D1%8F\">10 вересня</a> того ж року храм на Аскольдовій могилі відвідав <a title=\"Микола I (російський імператор)\" href=\"http://uk.wikipedia.org/wiki/%D0%9C%D0%B8%D0%BA%D0%BE%D0%BB%D0%B0_I_%28%D1%80%D0%BE%D1%81%D1%96%D0%B9%D1%81%D1%8C%D0%BA%D0%B8%D0%B9_%D1%96%D0%BC%D0%BF%D0%B5%D1%80%D0%B0%D1%82%D0%BE%D1%80%29\">імператор Микола І</a>, котрий заборонив знесення заявивши:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>&laquo;Анітрохи падінням не загрожує; треба трохи полагодити і церква має існувати&raquo;.</em> <sup><a href=\"http://uk.wikipedia.org/wiki/%D0%A6%D0%B5%D1%80%D0%BA%D0%B2%D0%B0_%D0%A1%D0%B2%D1%8F%D1%82%D0%BE%D0%B3%D0%BE_%D0%9C%D0%B8%D0%BA%D0%BE%D0%BB%D0%B0%D1%8F_%D0%BD%D0%B0_%D0%90%D1%81%D0%BA%D0%BE%D0%BB%D1%8C%D0%B4%D0%BE%D0%B2%D1%96%D0%B9_%D0%BC%D0%BE%D0%B3%D0%B8%D0%BB%D1%96#cite_note-1\">[2]</a></sup></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дошку з цією історичною фразою повісили згодом у відремонтованому храмі. Іншу дошку, з довжелезною (і наполовину вигаданою) історією Аскольдової могили, згодом почепили на <a title=\"Фасад\" href=\"http://uk.wikipedia.org/wiki/%D0%A4%D0%B0%D1%81%D0%B0%D0%B4\">фасаді</a> церкви у <a title=\"1866\" href=\"http://uk.wikipedia.org/wiki/1866\">1866</a> році, під час відзначення 1000-ліття хрещення <a title=\"Аскольд\" href=\"http://uk.wikipedia.org/wiki/%D0%90%D1%81%D0%BA%D0%BE%D0%BB%D1%8C%D0%B4\">Аскольда</a>. Тоді ж країною поширилася <a title=\"Легенда\" href=\"http://uk.wikipedia.org/wiki/%D0%9B%D0%B5%D0%B3%D0%B5%D0%BD%D0%B4%D0%B0\">легенда</a> про те, що тут знаходиться також і його поховання. На запитання про це богомольців військові писарі показували на місце за вівтарем храму, де існувало поховання невідомої особи, а пастирські служки уточнювали: &laquo;Не тут, а під церквою у склепі&raquo;. <sup><a href=\"http://uk.wikipedia.org/wiki/%D0%A6%D0%B5%D1%80%D0%BA%D0%B2%D0%B0_%D0%A1%D0%B2%D1%8F%D1%82%D0%BE%D0%B3%D0%BE_%D0%9C%D0%B8%D0%BA%D0%BE%D0%BB%D0%B0%D1%8F_%D0%BD%D0%B0_%D0%90%D1%81%D0%BA%D0%BE%D0%BB%D1%8C%D0%B4%D0%BE%D0%B2%D1%96%D0%B9_%D0%BC%D0%BE%D0%B3%D0%B8%D0%BB%D1%96#cite_note-2\">[3]</a></sup></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У <a title=\"1856\" href=\"http://uk.wikipedia.org/wiki/1856\">1856</a> році церкву знову розписали, а у <a title=\"1861\" href=\"http://uk.wikipedia.org/wiki/1861\">1861</a> році, коштом монастиря, відновлений цвинтар оточили кам\'яною огорожею з брамою, над воротами якої влаштували дзвіницю. Також було споруджено кам\'яний будинок для священика.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На закритому з <a title=\"1845\" href=\"http://uk.wikipedia.org/wiki/1845\">1845</a> року для загального користування цвинтарі, починаючи з другої половини <a title=\"XIX століття\" href=\"http://uk.wikipedia.org/wiki/XIX_%D1%81%D1%82%D0%BE%D0%BB%D1%96%D1%82%D1%82%D1%8F\">XIX століття</a>, почали здійснювати поховання найбільш заможних жителів <a title=\"Київ\" href=\"http://uk.wikipedia.org/wiki/%D0%9A%D0%B8%D1%97%D0%B2\">Києва</a>. Зрозумівши, що від цього можна мати неабиякий зиск, монастирське начальство стало практикувати широкий розпродаж місць на ньому, а на початку <a title=\"1880\" href=\"http://uk.wikipedia.org/wiki/1880\">1880</a>-х років запровадило навіть посаду наглядача, першим із яких виявився ієромонах отець Рафаїл. Він багато зробив для того, аби зберегти й утвердити звичай прибирати могили й утримувати їх у належному стані, перетворюючи це похмуре місце на привабливий куточок міста.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У <a title=\"1882\" href=\"http://uk.wikipedia.org/wiki/1882\">1882</a> році церкву Святого Миколая було відреставровано за проектом <a title=\"Ніколаєв Володимир Миколайович\" href=\"http://uk.wikipedia.org/wiki/%D0%9D%D1%96%D0%BA%D0%BE%D0%BB%D0%B0%D1%94%D0%B2_%D0%92%D0%BE%D0%BB%D0%BE%D0%B4%D0%B8%D0%BC%D0%B8%D1%80_%D0%9C%D0%B8%D0%BA%D0%BE%D0%BB%D0%B0%D0%B9%D0%BE%D0%B2%D0%B8%D1%87\">Володимира Ніколаєва</a>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Після своєї смерті, яка сталася у поважному віці, отець Рафаїл також знайшов тут для себе вічний спокій. Його місце заступив у <a title=\"1892\" href=\"http://uk.wikipedia.org/wiki/1892\">1892</a> році ієромонах того самого монастиря отець Веніамін. Найперше він звернув увагу на церкву, яка своїм зовнішнім виглядом і внутрішнім оздобленням не відповідала станові кладовища з його чистотою, прекрасними надгробними пам\'ятниками і склепами. Не маючи в кишені жодної зайвої копійки, наглядач звернувся за допомогою до приватних осіб, які мали тут могили своїх близьких, і несподівано для себе отримав широку підтримку: у <a title=\"1893\" href=\"http://uk.wikipedia.org/wiki/1893\">1893</a> році у церкві провели капітальний ремонт. Усередині з\'явилися привезений із <a title=\"Італія\" href=\"http://uk.wikipedia.org/wiki/%D0%86%D1%82%D0%B0%D0%BB%D1%96%D1%8F\">Італії</a> біло-<a title=\"Мармур\" href=\"http://uk.wikipedia.org/wiki/%D0%9C%D0%B0%D1%80%D0%BC%D1%83%D1%80\">мармуровий</a><a title=\"Васнецов Віктор Михайлович\" href=\"http://uk.wikipedia.org/wiki/%D0%92%D0%B0%D1%81%D0%BD%D0%B5%D1%86%D0%BE%D0%B2_%D0%92%D1%96%D0%BA%D1%82%D0%BE%D1%80_%D0%9C%D0%B8%D1%85%D0%B0%D0%B9%D0%BB%D0%BE%D0%B2%D0%B8%D1%87\">Віктора Васнецова</a>, і дубовий паркет. Освячення оновленого храму здійснив <a title=\"7 липня\" href=\"http://uk.wikipedia.org/wiki/7_%D0%BB%D0%B8%D0%BF%D0%BD%D1%8F\">7 липня</a><a title=\"1894\" href=\"http://uk.wikipedia.org/wiki/1894\">1894</a> року митрополит Київський Іоанікій. Також, у <a title=\"1901\" href=\"http://uk.wikipedia.org/wiki/1901\">1901</a> році, коштом Марії Поторжинської було збудовано нову, більшу дзвіницю.</span> іконостас, новий настінний розпис, виконаний за ескізами живописця </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Новітні часи</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Після встановлення <a title=\"УРСР\" href=\"http://uk.wikipedia.org/wiki/%D0%A3%D0%A0%D0%A1%D0%A0\">радянського режиму</a> церкву закрили, але у <a title=\"1921\" href=\"http://uk.wikipedia.org/wiki/1921\">1921</a>&nbsp;- <a title=\"1934\" href=\"http://uk.wikipedia.org/wiki/1934\">34</a><a title=\"УАПЦ\" href=\"http://uk.wikipedia.org/wiki/%D0%A3%D0%90%D0%9F%D0%A6\">УАПЦ</a>. У <a title=\"1934\" href=\"http://uk.wikipedia.org/wiki/1934\">1934</a> році церкву закрито вже остаточно, а кладовище знищене з метою влаштування розважального парку. У <a title=\"1936\" href=\"http://uk.wikipedia.org/wiki/1936\">1936</a> році церкву перебудували під <a title=\"Ресторан\" href=\"http://uk.wikipedia.org/wiki/%D0%A0%D0%B5%D1%81%D1%82%D0%BE%D1%80%D0%B0%D0%BD\">ресторан</a>, а у <a title=\"1938\" href=\"http://uk.wikipedia.org/wiki/1938\">1938</a> році архітектор <a title=\"Юрченко Петро Григорович\" href=\"http://uk.wikipedia.org/wiki/%D0%AE%D1%80%D1%87%D0%B5%D0%BD%D0%BA%D0%BE_%D0%9F%D0%B5%D1%82%D1%80%D0%BE_%D0%93%D1%80%D0%B8%D0%B3%D0%BE%D1%80%D0%BE%D0%B2%D0%B8%D1%87\">Петро Юрченко</a> досить дотепно переробив колишній храм на парковий павільйон, надбудувавши замість бані наскрізну іонійську колонаду. <sup><a href=\"http://uk.wikipedia.org/wiki/%D0%A6%D0%B5%D1%80%D0%BA%D0%B2%D0%B0_%D0%A1%D0%B2%D1%8F%D1%82%D0%BE%D0%B3%D0%BE_%D0%9C%D0%B8%D0%BA%D0%BE%D0%BB%D0%B0%D1%8F_%D0%BD%D0%B0_%D0%90%D1%81%D0%BA%D0%BE%D0%BB%D1%8C%D0%B4%D0%BE%D0%B2%D1%96%D0%B9_%D0%BC%D0%BE%D0%B3%D0%B8%D0%BB%D1%96#cite_note-3\">[4]</a></sup> Тоді ж було знесено і дзвіницю початку ХХ ст., будинок настоятеля і паркан 60-х рр. ХІХ ст.</span> роках вона ще діяла, як парафіяльна </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У такому вигляді церква проіснувала до останньої реставрації <a title=\"1997\" href=\"http://uk.wikipedia.org/wiki/1997\">1997</a>&nbsp;- <a title=\"1998\" href=\"http://uk.wikipedia.org/wiki/1998\">98</a> років (архітектор Володимир Хромченков), коли було відновлено первісний вигляд споруди. <a title=\"26 квітня\" href=\"http://uk.wikipedia.org/wiki/26_%D0%BA%D0%B2%D1%96%D1%82%D0%BD%D1%8F\">26 квітня</a> <a title=\"1992\" href=\"http://uk.wikipedia.org/wiki/1992\">1992</a> році храм передано релігійній громаді <a title=\"Українська греко-католицька церква\" href=\"http://uk.wikipedia.org/wiki/%D0%A3%D0%BA%D1%80%D0%B0%D1%97%D0%BD%D1%81%D1%8C%D0%BA%D0%B0_%D0%B3%D1%80%D0%B5%D0%BA%D0%BE-%D0%BA%D0%B0%D1%82%D0%BE%D0%BB%D0%B8%D1%86%D1%8C%D0%BA%D0%B0_%D1%86%D0%B5%D1%80%D0%BA%D0%B2%D0%B0\">Української греко-католицької церкви</a>. У напівпідвалі нині також влаштовано церкву в ім\'я Святого Сильвестра Папи Римського. Освячення відновленої Миколаївської церкви здійснив <a title=\"22 травня\" href=\"http://uk.wikipedia.org/wiki/22_%D1%82%D1%80%D0%B0%D0%B2%D0%BD%D1%8F\">22 травня</a> <a title=\"1998\" href=\"http://uk.wikipedia.org/wiki/1998\">1998</a> року тоді ще Преосвященний Екзарх Києво-Вишгородський УГКЦ <a title=\"Гузар Любомир\" href=\"http://uk.wikipedia.org/wiki/%D0%93%D1%83%D0%B7%D0%B0%D1%80_%D0%9B%D1%8E%D0%B1%D0%BE%D0%BC%D0%B8%D1%80\">Любомир Гузар</a>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церква Святого Миколая на Аскольдовій могилі прославилась також тим, що це був перший храм, який відвідав <a title=\"Папа Римський\" href=\"http://uk.wikipedia.org/wiki/%D0%9F%D0%B0%D0%BF%D0%B0_%D0%A0%D0%B8%D0%BC%D1%81%D1%8C%D0%BA%D0%B8%D0%B9\">Папа Римський</a> <a title=\"Іван Павло ІІ\" href=\"http://uk.wikipedia.org/wiki/%D0%86%D0%B2%D0%B0%D0%BD_%D0%9F%D0%B0%D0%B2%D0%BB%D0%BE_%D0%86%D0%86\">Іван Павло ІІ</a> під час свого апостольського візиту в <a title=\"Україна\" href=\"http://uk.wikipedia.org/wiki/%D0%A3%D0%BA%D1%80%D0%B0%D1%97%D0%BD%D0%B0\">Україну</a> влітку <a title=\"2001\" href=\"http://uk.wikipedia.org/wiki/2001\">2001</a> року. <sup><a href=\"http://uk.wikipedia.org/wiki/%D0%A6%D0%B5%D1%80%D0%BA%D0%B2%D0%B0_%D0%A1%D0%B2%D1%8F%D1%82%D0%BE%D0%B3%D0%BE_%D0%9C%D0%B8%D0%BA%D0%BE%D0%BB%D0%B0%D1%8F_%D0%BD%D0%B0_%D0%90%D1%81%D0%BA%D0%BE%D0%BB%D1%8C%D0%B4%D0%BE%D0%B2%D1%96%D0%B9_%D0%BC%D0%BE%D0%B3%D0%B8%D0%BB%D1%96#cite_note-4\">[5]</a></sup></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Під пам\'ятною дошкою, встановленою на стіні церкви на честь візиту Папи, міститься напис:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>&laquo;У цьомі храмі Св. Миколая Чудотворця на Аскольдовій могилі <a title=\"23 червня\" href=\"http://uk.wikipedia.org/wiki/23_%D1%87%D0%B5%D1%80%D0%B2%D0%BD%D1%8F\">23 червня</a> <a title=\"2001\" href=\"http://uk.wikipedia.org/wiki/2001\">2001</a></em><em>&nbsp;</em><em>р. Божого Святійший Отець Іван Павло ІІ в молитві перед Чудотворною іконою Зарваницької Богородиці віддав під Її покровительство свою Апостольську подорож Україною&raquo;</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Аско́льдова моги́ла</strong> - одна з найвідоміших історичних місцевостей та урочище у <a title=\"Київ\" href=\"http://uk.wikipedia.org/wiki/%D0%9A%D0%B8%D1%97%D0%B2\">Києві</a>, де колись також містився престижний некрополь, знищений за <a title=\"СРСР\" href=\"http://uk.wikipedia.org/wiki/%D0%A1%D0%A0%D0%A1%D0%A0\">радянських часів</a>. У наш час - парк, пам\'ятка садово-паркового мистецтва. Розташована між Дніпровським узвозом, вулицею Січневого Повстання і Маріїнським парком, охоплюючи частину Дніпровських схилів.</span></span></p>\r\n<p><strong><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Історія</span></span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Аскольдова могила виникла на місці <a title=\"Угорське урочище\" href=\"http://uk.wikipedia.org/wiki/%D0%A3%D0%B3%D0%BE%D1%80%D1%81%D1%8C%D0%BA%D0%B5_%D1%83%D1%80%D0%BE%D1%87%D0%B8%D1%89%D0%B5\">урочища Угорського</a>. Цю місцевість здавна пов\'язували з легендою про <a title=\"Вбивство Аскольда і Діра\" href=\"http://uk.wikipedia.org/wiki/%D0%92%D0%B1%D0%B8%D0%B2%D1%81%D1%82%D0%B2%D0%BE_%D0%90%D1%81%D0%BA%D0%BE%D0%BB%D1%8C%D0%B4%D0%B0_%D1%96_%D0%94%D1%96%D1%80%D0%B0\">вбивство Аскольда і Діра</a>: за літописом, <a title=\"Аскольд\" href=\"http://uk.wikipedia.org/wiki/%D0%90%D1%81%D0%BA%D0%BE%D0%BB%D1%8C%D0%B4\">Аскольда</a><a title=\"Ольга\" href=\"http://uk.wikipedia.org/wiki/%D0%9E%D0%BB%D1%8C%D0%B3%D0%B0\">Ольга</a><a title=\"Церква Святого Миколая на Аскольдовій могилі\" href=\"http://uk.wikipedia.org/wiki/%D0%A6%D0%B5%D1%80%D0%BA%D0%B2%D0%B0_%D0%A1%D0%B2%D1%8F%D1%82%D0%BE%D0%B3%D0%BE_%D0%9C%D0%B8%D0%BA%D0%BE%D0%BB%D0%B0%D1%8F_%D0%BD%D0%B0_%D0%90%D1%81%D0%BA%D0%BE%D0%BB%D1%8C%D0%B4%D0%BE%D0%B2%D1%96%D0%B9_%D0%BC%D0%BE%D0%B3%D0%B8%D0%BB%D1%96\">церкву Св. Миколи</a> (Микола - це ім\'я, яке прийняв Аскольд при хрещенні у <a title=\"867\" href=\"http://uk.wikipedia.org/wiki/867\">867</a> році). У <a title=\"971\" href=\"http://uk.wikipedia.org/wiki/971\">971</a> році церкву було зруйновано сином Ольги - <a title=\"Святослав\" href=\"http://uk.wikipedia.org/wiki/%D0%A1%D0%B2%D1%8F%D1%82%D0%BE%D1%81%D0%BB%D0%B0%D0%B2\">Святославом</a>, правління якого пов\'язане із жорстокою <a title=\"Язичництво\" href=\"http://uk.wikipedia.org/wiki/%D0%AF%D0%B7%D0%B8%D1%87%D0%BD%D0%B8%D1%86%D1%82%D0%B2%D0%BE\">язичницькою</a> реакцією, переслідуванням <a title=\"Християнство\" href=\"http://uk.wikipedia.org/wiki/%D0%A5%D1%80%D0%B8%D1%81%D1%82%D0%B8%D1%8F%D0%BD%D1%81%D1%82%D0%B2%D0%BE\">християн</a> і нищенням церков. Лише у <a title=\"990\" href=\"http://uk.wikipedia.org/wiki/990\">990</a> році князь <a title=\"Володимир Великий\" href=\"http://uk.wikipedia.org/wiki/%D0%92%D0%BE%D0%BB%D0%BE%D0%B4%D0%B8%D0%BC%D0%B8%D1%80_%D0%92%D0%B5%D0%BB%D0%B8%D0%BA%D0%B8%D0%B9\">Володимир</a> відновив раніше зруйнований храм. У <a title=\"1036\" href=\"http://uk.wikipedia.org/wiki/1036\">1036</a> році при церкві було засновано жіночий монастир</span> поховано саме на цьому місці. Згідно з переказами ще княгиня  збудувала тут у Х ст. дерев\'яну </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Не пізніше XV ст. на Аскольдовій могилі виник <a title=\"Микільський Пустинський монастир (Київ) (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%9C%D0%B8%D0%BA%D1%96%D0%BB%D1%8C%D1%81%D1%8C%D0%BA%D0%B8%D0%B9_%D0%9F%D1%83%D1%81%D1%82%D0%B8%D0%BD%D1%81%D1%8C%D0%BA%D0%B8%D0%B9_%D0%BC%D0%BE%D0%BD%D0%B0%D1%81%D1%82%D0%B8%D1%80_%28%D0%9A%D0%B8%D1%97%D0%B2%29&amp;action=edit&amp;redlink=1\">Микільський Пустинський монастир</a>. У XVII ст. серед київських просвітників поширилася думка, що монастирська церква - це і є стародавня церква Св. Миколи, що згадується у літописі як поставлена над могилою князя Аскольда. Звідси дослідникам ХІХ ст. вже неважко було зробити висновок, що літописне урочище Угорське - це місцевість навколо Пустинського монастиря. Тоді й розповсюдилася назва \"Аскольдова могила\", популяризації якої сприяли однойменні роман <a title=\"Загоскін Михайло (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%97%D0%B0%D0%B3%D0%BE%D1%81%D0%BA%D1%96%D0%BD_%D0%9C%D0%B8%D1%85%D0%B0%D0%B9%D0%BB%D0%BE&amp;action=edit&amp;redlink=1\">Михайла Загоскіна</a> та опера <a title=\"Верстовський Олексій (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%92%D0%B5%D1%80%D1%81%D1%82%D0%BE%D0%B2%D1%81%D1%8C%D0%BA%D0%B8%D0%B9_%D0%9E%D0%BB%D0%B5%D0%BA%D1%81%D1%96%D0%B9&amp;action=edit&amp;redlink=1\">Олексія Верстовського</a>.</span></span></p>\r\n<p><strong><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Некрополь</span></span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Некрополь на Аскольдовій могилі складався від кінця XVIII до початку ХХ ст. У <a title=\"1715\" href=\"http://uk.wikipedia.org/wiki/1715\">1715</a> році територія навколо старої Микільської церкви стала цвинтарем - спочатку суто монастирським, а у <a title=\"1786\" href=\"http://uk.wikipedia.org/wiki/1786\">1786</a> році - загальноміським. У <a title=\"1810\" href=\"http://uk.wikipedia.org/wiki/1810\">1810</a> році тут було збудовано нову муровану церкву (зведену за проектом архітектора <a title=\"Меленський Андрій Іванович (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%9C%D0%B5%D0%BB%D0%B5%D0%BD%D1%81%D1%8C%D0%BA%D0%B8%D0%B9_%D0%90%D0%BD%D0%B4%D1%80%D1%96%D0%B9_%D0%86%D0%B2%D0%B0%D0%BD%D0%BE%D0%B2%D0%B8%D1%87&amp;action=edit&amp;redlink=1\">Андрія Меленського</a>), яку в середині ХІХ ст. планувалося знести разом з цвинтарем, але втручання <a title=\"Микола I (російський імператор)\" href=\"http://uk.wikipedia.org/wiki/%D0%9C%D0%B8%D0%BA%D0%BE%D0%BB%D0%B0_I_%28%D1%80%D0%BE%D1%81%D1%96%D0%B9%D1%81%D1%8C%D0%BA%D0%B8%D0%B9_%D1%96%D0%BC%D0%BF%D0%B5%D1%80%D0%B0%D1%82%D0%BE%D1%80%29\">імператора Миколи І</a> врятувало й те, й інше. Щоправда, поховання тут припинилися, але з початку <a title=\"1870\" href=\"http://uk.wikipedia.org/wiki/1870\">1870</a>-х рр. відновилися. Наприкінці ХІХ ст. через постійні зсуви схилу цвинтар опинився у аварійному стані, і з того часу, впродовж 20 років, тут велися дренажні та інженерні роботи.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Цвинтар на Аскольдовій могилі був значно меншим за <a title=\"Байковий цвинтар\" href=\"http://uk.wikipedia.org/wiki/%D0%91%D0%B0%D0%B9%D0%BA%D0%BE%D0%B2%D0%B8%D0%B9_%D1%86%D0%B2%D0%B8%D0%BD%D1%82%D0%B0%D1%80\">Байковий</a>, але не поступався йому за елітарністю некрополя. Тривалий час тут ховали відомих і заможних киян, були споруджені численні художні надгробки і склепи. На схилах <a title=\"Дніпро\" href=\"http://uk.wikipedia.org/wiki/%D0%94%D0%BD%D1%96%D0%BF%D1%80%D0%BE\">Дніпра</a> знаходили вічний притулок філософи і лікарі, письменники та актори, інженери й композитори. Тут були поховані як відомі українофіли <a title=\"Лашкевич Олександр Степанович (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%9B%D0%B0%D1%88%D0%BA%D0%B5%D0%B2%D0%B8%D1%87_%D0%9E%D0%BB%D0%B5%D0%BA%D1%81%D0%B0%D0%BD%D0%B4%D1%80_%D0%A1%D1%82%D0%B5%D0%BF%D0%B0%D0%BD%D0%BE%D0%B2%D0%B8%D1%87&amp;action=edit&amp;redlink=1\">Олександр Лашкевич</a> та <a title=\"Тарновський Василь Васильович (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%A2%D0%B0%D1%80%D0%BD%D0%BE%D0%B2%D1%81%D1%8C%D0%BA%D0%B8%D0%B9_%D0%92%D0%B0%D1%81%D0%B8%D0%BB%D1%8C_%D0%92%D0%B0%D1%81%D0%B8%D0%BB%D1%8C%D0%BE%D0%B2%D0%B8%D1%87&amp;action=edit&amp;redlink=1\">Василь Тарновський</a>, так і не менш відомі москвофіли <a title=\"Юзефович Михайло Володимирович\" href=\"http://uk.wikipedia.org/wiki/%D0%AE%D0%B7%D0%B5%D1%84%D0%BE%D0%B2%D0%B8%D1%87_%D0%9C%D0%B8%D1%85%D0%B0%D0%B9%D0%BB%D0%BE_%D0%92%D0%BE%D0%BB%D0%BE%D0%B4%D0%B8%D0%BC%D0%B8%D1%80%D0%BE%D0%B2%D0%B8%D1%87\">Михайло Юзефович</a> та <a title=\"Дрентельн Олександр Романович (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%94%D1%80%D0%B5%D0%BD%D1%82%D0%B5%D0%BB%D1%8C%D0%BD_%D0%9E%D0%BB%D0%B5%D0%BA%D1%81%D0%B0%D0%BD%D0%B4%D1%80_%D0%A0%D0%BE%D0%BC%D0%B0%D0%BD%D0%BE%D0%B2%D0%B8%D1%87&amp;action=edit&amp;redlink=1\">Олександр Дрентельн</a>. Багато ховали тут військових - від ветеранів війни 1812 року з <a title=\"Наполеон\" href=\"http://uk.wikipedia.org/wiki/%D0%9D%D0%B0%D0%BF%D0%BE%D0%BB%D0%B5%D0%BE%D0%BD\">Наполеоном</a> і учасників <a title=\"Кримська війна\" href=\"http://uk.wikipedia.org/wiki/%D0%9A%D1%80%D0%B8%D0%BC%D1%81%D1%8C%D0%BA%D0%B0_%D0%B2%D1%96%D0%B9%D0%BD%D0%B0\">Кримської війни</a> до героїв <a title=\"Перша світова війна\" href=\"http://uk.wikipedia.org/wiki/%D0%9F%D0%B5%D1%80%D1%88%D0%B0_%D1%81%D0%B2%D1%96%D1%82%D0%BE%D0%B2%D0%B0_%D0%B2%D1%96%D0%B9%D0%BD%D0%B0\">Першої світової</a> - льотчика <a title=\"Нестеров Петро Миколайович\" href=\"http://uk.wikipedia.org/wiki/%D0%9D%D0%B5%D1%81%D1%82%D0%B5%D1%80%D0%BE%D0%B2_%D0%9F%D0%B5%D1%82%D1%80%D0%BE_%D0%9C%D0%B8%D0%BA%D0%BE%D0%BB%D0%B0%D0%B9%D0%BE%D0%B2%D0%B8%D1%87\">Петра Нестерова</a> та гусарів Київського полку. На Аскольдовій могилі були також поховані лікар <a title=\"Мерінг Франц (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%9C%D0%B5%D1%80%D1%96%D0%BD%D0%B3_%D0%A4%D1%80%D0%B0%D0%BD%D1%86&amp;action=edit&amp;redlink=1\">Франц Мерінг</a>, антрепренер <a title=\"Сєтов Йосип Якович (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%A1%D1%94%D1%82%D0%BE%D0%B2_%D0%99%D0%BE%D1%81%D0%B8%D0%BF_%D0%AF%D0%BA%D0%BE%D0%B2%D0%B8%D1%87&amp;action=edit&amp;redlink=1\">Йосип Сєтов</a>, акторське подружжя <a title=\"Соловцов Микола Миколайович (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%A1%D0%BE%D0%BB%D0%BE%D0%B2%D1%86%D0%BE%D0%B2_%D0%9C%D0%B8%D0%BA%D0%BE%D0%BB%D0%B0_%D0%9C%D0%B8%D0%BA%D0%BE%D0%BB%D0%B0%D0%B9%D0%BE%D0%B2%D0%B8%D1%87&amp;action=edit&amp;redlink=1\">Миколи Соловцова</a> та <a title=\"Глєбова Марія Михайлівна (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%93%D0%BB%D1%94%D0%B1%D0%BE%D0%B2%D0%B0_%D0%9C%D0%B0%D1%80%D1%96%D1%8F_%D0%9C%D0%B8%D1%85%D0%B0%D0%B9%D0%BB%D1%96%D0%B2%D0%BD%D0%B0&amp;action=edit&amp;redlink=1\">Марії Глєбової</a>, богослов <a title=\"Певницький Василь Федорович (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%9F%D0%B5%D0%B2%D0%BD%D0%B8%D1%86%D1%8C%D0%BA%D0%B8%D0%B9_%D0%92%D0%B0%D1%81%D0%B8%D0%BB%D1%8C_%D0%A4%D0%B5%D0%B4%D0%BE%D1%80%D0%BE%D0%B2%D0%B8%D1%87&amp;action=edit&amp;redlink=1\">Василь Певницький</a>, багатій-меценат <a title=\"Симиренко Василь Федорович\" href=\"http://uk.wikipedia.org/wiki/%D0%A1%D0%B8%D0%BC%D0%B8%D1%80%D0%B5%D0%BD%D0%BA%D0%BE_%D0%92%D0%B0%D1%81%D0%B8%D0%BB%D1%8C_%D0%A4%D0%B5%D0%B4%D0%BE%D1%80%D0%BE%D0%B2%D0%B8%D1%87\">Василь Симиренко</a> і ще близько двох тисяч відомих та видатних осіб.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За доби визвольних змагань на кладовищі виникло чимало братських поховань. На Аскольдовій могилі у лютому <a title=\"1918\" href=\"http://uk.wikipedia.org/wiki/1918\">1918</a> року поховали <a title=\"Червона армія\" href=\"http://uk.wikipedia.org/wiki/%D0%A7%D0%B5%D1%80%D0%B2%D0%BE%D0%BD%D0%B0_%D0%B0%D1%80%D0%BC%D1%96%D1%8F\">червоногвардійців</a>, котрі здобували місто; у березні 1918 року - українських юнаків, що <a title=\"Битва під Крутами\" href=\"http://uk.wikipedia.org/wiki/%D0%91%D0%B8%D1%82%D0%B2%D0%B0_%D0%BF%D1%96%D0%B4_%D0%9A%D1%80%D1%83%D1%82%D0%B0%D0%BC%D0%B8\">загинули під Крутами</a>, обороняючи <a title=\"Київ\" href=\"http://uk.wikipedia.org/wiki/%D0%9A%D0%B8%D1%97%D0%B2\">Київ</a> від <a title=\"Більшовики\" href=\"http://uk.wikipedia.org/wiki/%D0%91%D1%96%D0%BB%D1%8C%D1%88%D0%BE%D0%B2%D0%B8%D0%BA%D0%B8\">більшовицьких</a> військ; пізніше - <a title=\"Петлюра Симон Васильович\" href=\"http://uk.wikipedia.org/wiki/%D0%9F%D0%B5%D1%82%D0%BB%D1%8E%D1%80%D0%B0_%D0%A1%D0%B8%D0%BC%D0%BE%D0%BD_%D0%92%D0%B0%D1%81%D0%B8%D0%BB%D1%8C%D0%BE%D0%B2%D0%B8%D1%87\">петлюрівських</a> козаків.</span></span></p>\r\n<p><strong><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Міський парк</span></span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a title=\"31 травня\" href=\"http://uk.wikipedia.org/wiki/31_%D1%82%D1%80%D0%B0%D0%B2%D0%BD%D1%8F\">31 травня</a> <a title=\"1919\" href=\"http://uk.wikipedia.org/wiki/1919\">1919</a> року радянські органи оголосили про закриття цвинтару. У <a title=\"1934\" href=\"http://uk.wikipedia.org/wiki/1934\">1934</a> році почалася ліквідація та перетворення некрополя на парк \"Аскольдова могила\". У зв\'язку з цим відбулося варварське руйнування численних склепів на надгробків, які були утилізовані (півтора десятки статуй передали як натуру для студентів <a title=\"Національна академія образотворчого мистецтва і архітектури\" href=\"http://uk.wikipedia.org/wiki/%D0%9D%D0%B0%D1%86%D1%96%D0%BE%D0%BD%D0%B0%D0%BB%D1%8C%D0%BD%D0%B0_%D0%B0%D0%BA%D0%B0%D0%B4%D0%B5%D0%BC%D1%96%D1%8F_%D0%BE%D0%B1%D1%80%D0%B0%D0%B7%D0%BE%D1%82%D0%B2%D0%BE%D1%80%D1%87%D0%BE%D0%B3%D0%BE_%D0%BC%D0%B8%D1%81%D1%82%D0%B5%D1%86%D1%82%D0%B2%D0%B0_%D1%96_%D0%B0%D1%80%D1%85%D1%96%D1%82%D0%B5%D0%BA%D1%82%D1%83%D1%80%D0%B8\">Художнього інституту</a>). Лише одиничні поховання, перенесені до інших некрополів, збереглися досі, як-от могила льотчика Петра Нестерова на <a title=\"Лук\'янівський цвинтар\" href=\"http://uk.wikipedia.org/wiki/%D0%9B%D1%83%D0%BA%27%D1%8F%D0%BD%D1%96%D0%B2%D1%81%D1%8C%D0%BA%D0%B8%D0%B9_%D1%86%D0%B2%D0%B8%D0%BD%D1%82%D0%B0%D1%80\">Лук\'янівському кладовищі</a>, подружжя акторів Миколи Соловйова та Марії Глєбової на <a title=\"Байкове кладовище\" href=\"http://uk.wikipedia.org/wiki/%D0%91%D0%B0%D0%B9%D0%BA%D0%BE%D0%B2%D0%B5_%D0%BA%D0%BB%D0%B0%D0%B4%D0%BE%D0%B2%D0%B8%D1%89%D0%B5\">Байковому кладовищі</a>, мецената Василя Тарновського на <a title=\"Звіринецьке кладовище\" href=\"http://uk.wikipedia.org/wiki/%D0%97%D0%B2%D1%96%D1%80%D0%B8%D0%BD%D0%B5%D1%86%D1%8C%D0%BA%D0%B5_%D0%BA%D0%BB%D0%B0%D0%B4%D0%BE%D0%B2%D0%B8%D1%89%D0%B5\">Звіринецькому кладовищі</a>. Що ж до <a title=\"Церква Святого Миколая на Аскольдовій могилі\" href=\"http://uk.wikipedia.org/wiki/%D0%A6%D0%B5%D1%80%D0%BA%D0%B2%D0%B0_%D0%A1%D0%B2%D1%8F%D1%82%D0%BE%D0%B3%D0%BE_%D0%9C%D0%B8%D0%BA%D0%BE%D0%BB%D0%B0%D1%8F_%D0%BD%D0%B0_%D0%90%D1%81%D0%BA%D0%BE%D0%BB%D1%8C%D0%B4%D0%BE%D0%B2%D1%96%D0%B9_%D0%BC%D0%BE%D0%B3%D0%B8%D0%BB%D1%96\">Миколаївської церкви</a>, то у <a title=\"1936\" href=\"http://uk.wikipedia.org/wiki/1936\">1936</a> році її перетворили на ресторан, а у <a title=\"1938\" href=\"http://uk.wikipedia.org/wiki/1938\">1938</a> за проектом архітектора <a title=\"Юрченко Петро Григорович\" href=\"http://uk.wikipedia.org/wiki/%D0%AE%D1%80%D1%87%D0%B5%D0%BD%D0%BA%D0%BE_%D0%9F%D0%B5%D1%82%D1%80%D0%BE_%D0%93%D1%80%D0%B8%D0%B3%D0%BE%D1%80%D0%BE%D0%B2%D0%B8%D1%87\">Петра Юрченка</a> досить дотепно перебудували на парковий павільйон: замість пласкої <a title=\"Ампір\" href=\"http://uk.wikipedia.org/wiki/%D0%90%D0%BC%D0%BF%D1%96%D1%80\">ампірової</a> бані споруджено відкриту іонічну колонаду без даху.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В останній раз Аскольдова могила використовувалася як цвинтар за <a title=\"Райхскомісаріат Україна\" href=\"http://uk.wikipedia.org/wiki/%D0%A0%D0%B0%D0%B9%D1%85%D1%81%D0%BA%D0%BE%D0%BC%D1%96%D1%81%D0%B0%D1%80%D1%96%D0%B0%D1%82_%D0%A3%D0%BA%D1%80%D0%B0%D1%97%D0%BD%D0%B0\">нацистської окупації</a>. Тут було організовано цвинтар німецьких офіцерів та солдатів, яких ховали чіткими рядами під однаковими деревяними хрестами. На пагорбі біля церкви-ротонди стояла велика скульптура - голова німецького солдата у шоломі. Ці поховання були знищені невдовзі після відступу німецьких військ. Натомість було влаштовано невелику алею могил радянських воїнів, котрі відзначилися при <a title=\"Оборона Києва 1941 (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%9E%D0%B1%D0%BE%D1%80%D0%BE%D0%BD%D0%B0_%D0%9A%D0%B8%D1%94%D0%B2%D0%B0_1941&amp;action=edit&amp;redlink=1\">обороні</a> та <a title=\"Визволення Києва від німецьких військ (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%92%D0%B8%D0%B7%D0%B2%D0%BE%D0%BB%D0%B5%D0%BD%D0%BD%D1%8F_%D0%9A%D0%B8%D1%94%D0%B2%D0%B0_%D0%B2%D1%96%D0%B4_%D0%BD%D1%96%D0%BC%D0%B5%D1%86%D1%8C%D0%BA%D0%B8%D1%85_%D0%B2%D1%96%D0%B9%D1%81%D1%8C%D0%BA&amp;action=edit&amp;redlink=1\">визволенні Києва</a>. Пізніше їх перенесли до парку Вічної Слави.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На сьогоднішній день Аскольдова могила - частина мальовничого парку на правому березі Дніпра та одне з улюблених місць прогулянок киян. Миколаївську церкву на початку <a title=\"1990\" href=\"http://uk.wikipedia.org/wiki/1990\">1990</a>-х рр. віддали релігійній громаді <a title=\"Українська греко-католицька церква\" href=\"http://uk.wikipedia.org/wiki/%D0%A3%D0%BA%D1%80%D0%B0%D1%97%D0%BD%D1%81%D1%8C%D0%BA%D0%B0_%D0%B3%D1%80%D0%B5%D0%BA%D0%BE-%D0%BA%D0%B0%D1%82%D0%BE%D0%BB%D0%B8%D1%86%D1%8C%D0%BA%D0%B0_%D1%86%D0%B5%D1%80%D0%BA%D0%B2%D0%B0\">греко-католицької церкви</a>, нині це діючий храм, який у <a title=\"2001\" href=\"http://uk.wikipedia.org/wiki/2001\">2001</a> році відвідав <a title=\"Іван Павло ІІ\" href=\"http://uk.wikipedia.org/wiki/%D0%86%D0%B2%D0%B0%D0%BD_%D0%9F%D0%B0%D0%B2%D0%BB%D0%BE_%D0%86%D0%86\">Папа Іван Павло ІІ</a>. Також у 1990-х рр. на Аскольдовій могилі встановлено дерев\'яний хрест як символічний надгробок <a title=\"Битва під Крутами\" href=\"http://uk.wikipedia.org/wiki/%D0%91%D0%B8%D1%82%D0%B2%D0%B0_%D0%BF%D1%96%D0%B4_%D0%9A%D1%80%D1%83%D1%82%D0%B0%D0%BC%D0%B8\">героїв Крут</a>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Уго́рське уро́чище</strong> - літописне урочище у <a title=\"Київ\" href=\"http://uk.wikipedia.org/wiki/%D0%9A%D0%B8%D1%97%D0%B2\">Києві</a>, що нині зветься <a title=\"Аскольдова могила\" href=\"http://uk.wikipedia.org/wiki/%D0%90%D1%81%D0%BA%D0%BE%D0%BB%D1%8C%D0%B4%D0%BE%D0%B2%D0%B0_%D0%BC%D0%BE%D0%B3%D0%B8%D0%BB%D0%B0\">Аскольдовою могилою</a>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Назва, за літописною версією, походить від табору давніх <a title=\"Угорці\" href=\"http://uk.wikipedia.org/wiki/%D0%A3%D0%B3%D0%BE%D1%80%D1%86%D1%96\">угорців</a> (\"угрів\"), які проходили повз Київ під час переселення з Причорномор\'я на <a title=\"Дунай\" href=\"http://uk.wikipedia.org/wiki/%D0%94%D1%83%D0%BD%D0%B0%D0%B9\">Дунай</a>. Ще до того Угорське урочище стало місцем <a title=\"Вбивство Аскольда і Діра\" href=\"http://uk.wikipedia.org/wiki/%D0%92%D0%B1%D0%B8%D0%B2%D1%81%D1%82%D0%B2%D0%BE_%D0%90%D1%81%D0%BA%D0%BE%D0%BB%D1%8C%D0%B4%D0%B0_%D1%96_%D0%94%D1%96%D1%80%D0%B0\">вбивства Аскольда і Діра</a>. <a title=\"Аскольд\" href=\"http://uk.wikipedia.org/wiki/%D0%90%D1%81%D0%BA%D0%BE%D0%BB%D1%8C%D0%B4\">Аскольд</a> був тут і похований; над його могилою пізніше встановлено <a title=\"Церква Святого Миколая на Аскольдовій могилі\" href=\"http://uk.wikipedia.org/wiki/%D0%A6%D0%B5%D1%80%D0%BA%D0%B2%D0%B0_%D0%A1%D0%B2%D1%8F%D1%82%D0%BE%D0%B3%D0%BE_%D0%9C%D0%B8%D0%BA%D0%BE%D0%BB%D0%B0%D1%8F_%D0%BD%D0%B0_%D0%90%D1%81%D0%BA%D0%BE%D0%BB%D1%8C%D0%B4%D0%BE%D0%B2%D1%96%D0%B9_%D0%BC%D0%BE%D0%B3%D0%B8%D0%BB%D1%96\">церкву Св. Миколая</a>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В урочищі (можливо ще з часів Аскольда) існував княжий двір, який з <a title=\"1151\" href=\"http://uk.wikipedia.org/wiki/1151\">1151</a> року був офіційною резиденцією <a title=\"Ізяслав Мстиславич\" href=\"http://uk.wikipedia.org/wiki/%D0%86%D0%B7%D1%8F%D1%81%D0%BB%D0%B0%D0%B2_%D0%9C%D1%81%D1%82%D0%B8%D1%81%D0%BB%D0%B0%D0%B2%D0%B8%D1%87\">Ізяслава Мстиславича</a>. Знайдений тут скарб <a title=\"Дирхам\" href=\"http://uk.wikipedia.org/wiki/%D0%94%D0%B8%D1%80%D1%85%D0%B0%D0%BC\">дирхемів</a> свідчить про заселеність місцевості у VIII - на поч. X ст. Є навіть припущення, що княжий двір, власне, і був заснований Аскольдом (що пояснює чому його вбито саме тут).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://uk.wikipedia.org/</span></span></p>',NULL,1991,5,1,NULL,12);
INSERT INTO `publications_publication` VALUES
(4688,'P','2008-12-23',1,NULL,'Rss','Публікуємо постанови Сорок другої сесії Митрополичого Синоду ','<p>17-18 грудня 2008 року у Львові-Брюховичах відбулася Сорок друга сесія Синоду Єпископів Києво-Галицького Верховного Архиєпископства Української Греко-Католицької Церкви. На сесійних засіданнях були присутні 17 членів Митрополичого Синоду. Сьогодні ми публікуємо постанови цієї сесії Синоду.</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=157:publikujemo-postanovy-sorok-drugoji-sesiji-mytropolychogo-synodu-&catid=27:gk-v-ukrajini-cat&Itemid=57',1826,NULL,1,1,26),
(4689,'P','2008-12-23',1,NULL,'Note','Передріздвяна зустріч Папи з членами Римської Курії','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Щороку, перед Різдвом Христовим, члени Колегії Кардиналів, Римської Курії, різних відомств Ватикану та представники Папи в різних країнах світу збираються разом, щоб привітати Святішого Отця із різдвяними святами та Новим роком. Ця зустріч є також нагодою проаналізувати внутрішній стан Церкви та підбити підсумки року, що минає.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Щороку, перед Різдвом Христовим, члени Колегії Кардиналів, Римської Курії, різних відомств Ватикану та представники Папи в різних країнах світу збираються разом, щоб привітати Святішого Отця із різдвяними святами та Новим роком. Ця зустріч є також нагодою проаналізувати внутрішній стан Церкви та підбити підсумки року, що минає.<br /><br />Така зустріч відбулася у Ватикані в понеділок, 22 грудня 2008&nbsp;р. Від імені зібраних співробітників Папи, до Венедикта&nbsp;XVI звернувся кардинал Анжело Содано, Декан Колегії Кардиналів. Висловивши вдячність Папі за його служіння, він пригадав, що святий Павло у посланні до Коринтян писав: &laquo;Усе чиню задля Євангелія&raquo;. Кардинал Содано зауважив, що ці слова є підсумком служіння Венедикта&nbsp;XVI. &laquo;З глибокою уважністю ми слухати повчання, які Ви до нас скеровували у своїх документах, проповідях, промовах, виголошених у Ватикані і у інших країнах, які Ви відвідували&raquo;, - сказав він, звертаючись до Папи, а далі перелічив найважливіші моменти діяльності Вселенського Архиєрея.<br /><br />Промовляючи до своїх найближчих співробітників, Святіший Отець до кожного скерував свої щирі побажання разом з вдячністю за допомогу, яку вони надають у виконанні нелегкого служіння, зазначаючи, що завданням Церкви та Наступника святого Петра, як і його співробітників, є дбати про те, аби для всіх у світі ставала більш видимою Божа благодать.<br /><br />Венедикт&nbsp;XVI зазначив, що рік, який добігає до завершення, був багатим на спомини недавніх важливих подій в історії Церкви, а це - 50-та річниця від відходу до вічності Папи Пія&nbsp;XII і обрання Івана&nbsp;ХХІІІ; 40 років від дня публікації енцикліки &laquo;Humanae vitae&raquo; та 30 років від дня смерті її автора Папи Павла&nbsp;VI, і, відповідно, початку понтифікату Папи Івана Павла&nbsp;ІІ. Важливим моментом, який скеровує наш погляд на майбутнє, Венедикт&nbsp;XVI назвав урочисте відкриття Року святого Павла, що відбулося 28 червня у базиліці святого Павла поза мурами, у присутності Вселенського Патріарха Вартоломея&nbsp;І та представників інших Церков та Церковних Спільнот.<br /><br />Великим святом віри Святіший Отець назвав Всесвітній день молоді, що у австралійському місті Сіднеї зібрав навколо Папи понад 200000 юнаків та дівчат з усіх кінців світу. Апостольські подорожі до США та Франції стали нагодою того, що Церква показала себе світові і у світі як духовна сила, що вказує на дороги життя, і через свідчення віри приносить людству світло. Не можна не згадати Синод Єпископів, під час якого Архиєреї з усього світу об\'єдналися навколо Божого Слова. Аналізуючи різні аспекти цих подій, Венедикт&nbsp;XVI особливу увагу присвятив значенню Всесвітніх днів молоді, та пригадав роль Святого Духа у житті світу та Церкви, що було темою цьогорічної зустрічі молоді у Сіднеї.<br /><br />Далі Святіший Отець зазначив, що органічною частиною свята є радість. І якщо свято можна організувати, то радість можна лише запропонувати як дар. Нам вона подарована у достатній кількості. Її нам дарує Святий Дух, Який є радістю. Радість є даром, у якому зібрані всі інші дари, вона є виразом щастя, буття у гармонії з самим собою, що є можливим лише тоді, коли перебуваємо у гармонії з Богом і сотвореним світом. Природною властивістю радості є поширюватися, потреба ділитися нею і передавати її далі. &laquo;Місійний дух Церкви є нічим іншим, як поштовхом передавати далі ту радість, яка нам була дарована. Нехай же вона буде завжди живою в нас, а, отже, променіє над світом з його випробуваннями: таким є моє побажання наприкінці цього року&raquo;, - сказав Венедикт&nbsp;XVI, побажавши своїм співробітникам у новому році радості, що походить від Бога.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,1825,5,1,NULL,43);
INSERT INTO `publications_publication` VALUES
(4690,'P','2008-12-23',1,NULL,'Note','Вшанування пам\'яті підпільного греко-католицького єпископа Якова Тимчука, ЧСВВ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У Бучацькій єпархії УГКЦ, 20 грудня 2008 року, відбулося урочисте відзначення 20-их роковин від дня смерті владики Якова Тимчука, ЧСВВ (1919-1988). У всіх храмах єпархії відслужено поминальні Святі Літургії та панахиди.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У Бучацькій єпархії УГКЦ, 20 грудня 2008 року, відбулося урочисте відзначення 20-их роковин від дня смерті владики Якова Тимчука, ЧСВВ (1919-1988). У всіх храмах єпархії відслужено поминальні Святі Літургії та панахиди. У катедральному соборі свв.&nbsp;апп.&nbsp;Петра й Павла у м.&nbsp;Чорткові Служу Божу очолив о.&nbsp;Дмитро Григорак,&nbsp;ЧСВВ Апостольський Адміністратор Бучацької єпархії. Опісля виголосив промову о.&nbsp;Віталій Дуткевич, ЧСВВ, який розповів про блаженної пам\'яті владику Якова, наголосивши на тому, що він упродовж багатьох років засівав Боже зерно у людських душах. &laquo;Найкращою пам\'яттю про нього буде те, що ми підемо його слідами&raquo;, - сказав о.&nbsp;Віталій, висловивши сподівання, що єпископа Тимчука колись проголосять блаженним і святим.<br /><br />Після молитви у катедральному соборі, учасники заходу від\'їхали у м.&nbsp;Заліщики, до могили Преосвященного Якова, де він спочиває поруч зі своїм братом о.&nbsp;Єронімом Тимчуком,&nbsp;ЧСВВ. На цвинтарі відслужено панахиду. У проповіді о.&nbsp;Дмитро Григорак,&nbsp;ЧСВВ, Апостольський Адміністратор, мовив про ісповідників віри, їхнє праведне життя, геройську дорогу. Про те, що боротьба зі злом ніколи не припиняється - кожен із нас має сказати своє слово у боротьбі із ним. &laquo;Бог перемагає через нас і в нас&raquo;, - виокремив о.&nbsp;Дмитро, звертаючи увагу на те, що естафету боротьби ісповідники передали нам.<br /><br />О.&nbsp;Дам\'ян Кастран,&nbsp;ЧСВВ,&nbsp;ігумен Гошівського василіянського монастиря, зосередився на тому, що добрих учинків немає без любові. Пригадав, що покійні брати були його професорами. &laquo;Ви сьогодні свідчите їхню пам\'ять віри та любові, і ваша присутність є тим свідченням&raquo;, - сказав о. Дам\'ян. Спогадами поділився і о.&nbsp;Володимир Палчинський,&nbsp;ЧСВВ, ігумен монастиря Царя-Христа в м.&nbsp;Івано-Франківську. Мер м.&nbsp;Заліщики п.&nbsp;Володимир Бенев\'ят висловив усім подяку та згадав, що заліщани дуже шанують своїх духовних наставників.<br /><br />Опісля, у приміщенні Єпархіяльного Управління Бучацької Єпархії в м.&nbsp;Чорткові, відбулася святкова конференція, яку вступним словом розпочав о. Дмитро Григорак, ЧСВВ. Звучали доповіді, пісні, свідчення, спогади про богоугодні діла, про постать владики Якова Тимчука, важкий для УГКЦ час підпілля тощо. Доповідь на тему &laquo;Діяльність священиків в умовах підпілля на теренах Бучацької Єпархії&raquo; виголосив о.&nbsp;Іван Сеньків.<br /><br />Для Радіо Ватикану, диякон Миколай МИКОСОВСЬКИЙ, ЧСВВ, Брюховичі</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,1824,5,1,NULL,42);
INSERT INTO `publications_publication` VALUES
(4691,'P','2008-12-23',1,NULL,'Note','Знову за старе?','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Нещодавно у Варшаві побачила світ книжка римо-католицького священика Тадеуша Ісаковича-Залеського &laquo;Przemilczane ludob&oacute;jstwo na Kresach&raquo; (Мовчанка про народовбивство на кресах), сама назва якої є дуже промовистою. Зрозуміло, що зміст книжки відповідає назві і йдеться в ній про &laquo;злочини українського народу&raquo;, які той начебто здійснив і продовжує здійснювати проти поляків. Під час одного із телеефірів президент України Віктор Ющенко як приклад добросусідських, а то й дружніх взаємин між двома народами назвав взаємини між українцями і поляками. Не зважаючи на складу історію, зокрема збройне протистояння на Волині у 1943 році та спецоперацію &laquo;Вісла&raquo;, українцям і полякам, на його думку, вдалося знайти &laquo;історичне порозуміння&raquo;, яке дозволяє вибудовувати справжні партнерські стосунки між двома народами і двома державами.&nbsp;</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Нещодавно у Варшаві побачила світ книжка римо-католицького священика Тадеуша Ісаковича-Залеського &laquo;Przemilczane ludob&oacute;jstwo na Kresach&raquo; (Мовчанка про народовбивство на кресах), сама назва якої є дуже промовистою. Зрозуміло, що зміст книжки відповідає назві і йдеться в ній про &laquo;злочини українського народу&raquo;, які той начебто здійснив і продовжує здійснювати проти поляків. Під час одного із телеефірів президент України Віктор Ющенко як приклад добросусідських, а то й дружніх взаємин між двома народами назвав взаємини між українцями і поляками. Не зважаючи на складу історію, зокрема збройне протистояння на Волині у 1943 році та спецоперацію &laquo;Вісла&raquo;, українцям і полякам, на його думку, вдалося знайти &laquo;історичне порозуміння&raquo;, яке дозволяє вибудовувати справжні партнерські стосунки між двома народами і двома державами.<br /><br />Очевидно, що нині взаємини між Україною і Польщею виглядають набагато гармонічнішими, аніж, скажімо, між Україною та Росією чи Румунією. Останні, зокрема, не приховують територіальних претензій до Української держави.<br /><br />Однак вважати, що між українським і польським народами сьогодні не має протиріч, а дружні взаємини президента Ющенка і президента Польщі Качинського віддзеркалюють атмосферу злагоди й безпроблемності, є передчасним і таким, що не відповідає дійсності.<br /><br />Ще не вщухла істерія, пов\'язана із намаганнями окремих польських політиків і державних діячів відзначити 65-річницю так званої Волинської трагедії з метою засудження ОУН і УПА, як на щит піднято нові проблеми, що можуть мати негативні наслідки для добросусідських взаємин, що існують між Україною і Польщею.<br /><br />Нещодавно у Варшаві побачила світ книжка римо-католицького священика Тадеуша Ісакович&not;а-Залеського &laquo;Przemilczane ludob&oacute;jstwo na Kresach&raquo; (Мовчанка про народовбивство на кресах), сама назва якої є дуже промовистою. Зрозуміло, що зміст книжки відповідає назві і йдеться в ній про &laquo;злочини українського народу&raquo;, які той начебто здійснив і продовжує здійснювати проти поляків. Крім старих, нафталінних звинувачень у &laquo;різанині&raquo; під час Другої світової війни додалися нові: виявляється поляків убивали лише тому, що вони - поляки, а вина за це лежить на... Українській Греко-Католицькій Церкві митрополит якої Андрей Шептицький буцімто закликав українців вступати до лав СС. Дісталося польській владі та українському президентові: офіційна Варшава обвинувачується у небажанні засудити &laquo;народовбивство&raquo;, а президент Ющенко у тому, що &laquo;вручає медалі бандерівцям&raquo;.<br /><br />На книжку польського ксьондза можна було і не звертати особливої уваги. Врешті-решт, сучасна Польща - це плюралістична країна, де кожен має право висловлювати свої думки, в тому числі щодо українсько-польських стосунків. Проте існують деякі &laquo;але&raquo;. <br />Згадана книжка не просто лежить у бібліотеках та на полицях книжкових магазинів, а дуже активно пропагується майже усіма впливовими польськими ЗМІ.<br /><br />Можливо, вперше за багато останніх років до антиукраїнської вакханалії долучилася католицька церква у Польщі. Без сумніву, книжка Ісакович&not;а-Залеського не лише не сприяє міжконфесійній злагоді польських католиків і українських греко-католиків, а вбиває клин між ними і церквами. Наслідки цього можуть бути нищівними як для одних та і других. На цьому важливо наголосити, оскільки автор засліплений ненавистю до всього українського, як правило, не добирає слів. Висловлювання на зразок &laquo;вирвали серце&raquo;, &laquo;розпороли живіт&raquo;, &laquo;дерли шкіру&raquo;, &laquo;вирізали язики&raquo;; &laquo;різня&raquo;, &laquo;масакра&raquo; вживаються ним свідомо й покликані викликати ненависть поляків до українців. До речі, Римо-Католицька Церква жодним чином не відреагувала на писанину її вірного.<br /><br />Врешті-решт, публіцистика польського ксьондза не обмежується лише інтерпретаціями історичного минулого й добиранням, а точніше не добиранням відповідних епітетів. Мета книжки - поставити під сумнів існування незалежної України у її нинішніх кордонах. Цей автор недвозначно заявляє, що Львів, Тернопіль і Луцьк мають належати Польщі. Адже, за його твердженням, все що за Бугом - польська земля.<br /><br />Правда, тут необхідно відзначити, що автор не оригінальний. Чимало маргінальних польських громадських організацій і партій, громадських та політичних діячів час від часу піднімають на люди ідею великої Польщі &laquo;від моря до моря&raquo;. Але правдою є і те, що ця безглузда ідея дедалі менше захоплює пересічних поляків, які вже давно змирилися з існуванням Української держави у тих кордонах, які чинні сьогодні. Натомість книжка покликана реанімувати шовіністичні інстинкти, не дати їм можливості канути у лету.<br /><br />У зв\'язку з цим, виникає логічне запитання: як реагувати на все це. Не думаю, що &laquo;історичні аргументи&raquo; української сторони напоумлять польського українофоба. Зрештою, наші аргументи давно відомі й не раз озвучувалися під час дискусії стосовно подій на Волині у 1943 році, операції &laquo;Вісла&raquo;, польських поховань на Личаківському цвинтарі у Львові тощо. Єдине про що хотілося б сказати, то це про абсурдність звинувачень митрополита УГКЦ Андрея Шептицького, який у на початку війни, коли нацистський режим набирав сили, і майже всі &laquo;моральні авторитети&raquo; Європи воліли відмовчуватися, відважився виступити із посланням &laquo;Не убий!&raquo;, де є такі слова: &laquo;Дивним способом обманюють себе і людей, що політичне вбивство не вважають гріхом, наче би політика звільняє чоловіка від обов\'язку Божого закону та оправдувала злочин противний людській природі. Так не є... За потоптання Божого закону може наступити й наступає Божа кара - найбільше зло і нещастя для людства&raquo;. Коментарі, як кажуть, зайві.<br /><br />Поки що важко сказати, яким чином бурхлива діяльність польського письменника в ризах може відбитися на офіційних стосунках Києва і Варшави. Хотілося б вірити, що ніяк. Але інцидент із згаданої книжкою та її масовим поширенням у Польщі має спонукати українську владу до переосмислення думки, що з минулим українського і польського народів усе гаразд. Мовляв, сторінку перегорнуто й можна будувати спільне європейське майбутнє. Так думати, означає втратити почуття реальності. Процес &laquo;примирення&raquo; насправді лише розпочався, а не завершився. За старе знову взялися поляки. Відповідь - за українцями.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Богдан Червак</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://ukr.pik.org.ua/</span></span></p>',NULL,1823,5,1,NULL,27);
INSERT INTO `publications_publication` VALUES
(4692,'P','2008-12-22',0,NULL,'Note','','<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong> Молитва в день іменин</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З цілого серця дякую Тобі, Господи, що Ти вчинив мене гідним прийняти святу тайну  хрещення і стати членом Твоєї Церкви. Поможи мені, Господи, щоб я назавжди був  (була) вірною й чесною дитиною Твоєю і мужнім борцем за справи Христові і за  славу його святого імени між людьми. Нині, у день мого небесного заступника,  перед тобою, Боже, я відновляю мій хресний обіт і, відрікаючися диявола та його  слуг, обіцяю вірити в Христа і вірно йому служити. Вірую в усе, що Ти, Господи, нам  об\'явив, і постановляю жити і вмерти так, як це належить вірному (вірній) святої  Церкви. Святий (ім\'я), мій небесний  опікуне, заступися за мене перед престолом Всевишнього і випроси ласку, щоб я  міг Тебе наслідувати, а по смерті разом з тобою оглядати славу божу в небі на  віки вічні. Амінь.</span></span></p>','',NULL,1822,5,1,NULL,50),
(4693,'P','2008-12-22',1,NULL,'Rss','Зачаття Пресвятої Богородиці святою Анною','<table align=\"right\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"306\">\n<tbody>\n<tr>\n<td rowspan=\"2\" valign=\"top\"><img src=\"http://www.ugcc.org.ua/clear.gif\" height=\"1\" width=\"5\" /></td>\n<td><img src=\"http://www.ugcc.org.ua/clear.gif\" height=\"1\" width=\"300\" /></td>\n</tr>\n<tr>\n<td valign=\"top\"><img style=\"float: right;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/bfe42f030b.jpg\" height=\"354\" width=\"300\" /></td>\n</tr>\n</tbody>\n</table>\n<h3>22 грудня 2008 року</h3>\n<br /> \n<ul>\n<li>Коротка історія свята</li>\n<li>Про ікону празника</li>\n<li>Тропар та кондак</li>\n<li>Празничний Апостол</li>\n<li>Євангеліє</li>\n<li>Проповідь</li>\n</ul>\n<p> </p>\n<h3>Коротка історія</h3>\n<p align=\"justify\">Давній і глибокий культ Пресвятої Богородиці, що є знаменною віхою Східної Церкви, виявляється у великій кількості Богородичних празників у нашому церковному календарі. Ті празники розказують нам найважливіші моменти з життя Божої Матері від її чудесного зачаття аж до її славного успення.</p>\n<p align=\"justify\">Святе Євангеліє не багато говорить про життя Пречистої Діви Марії, тож нічого дивного, що більшість Богородичних празників не основується на подіях святого Євангелія, а радше на християнській традиції та на оповіданнях апокрифічних книг перших віків. Сюди належить також і празник Зачаття, який наша Церква святкує 22 (9) грудня. Цей празник має окрему історію на Сході та на Заході.</p>\n<p align=\"justify\">\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=156:zachattja-presvjatoji-bogorodyci-svjatoju-annoju&catid=35:istorychni-postati-cat&Itemid=48',1821,NULL,1,1,26),
(4694,'N','2008-12-21',0,NULL,'Note','1','','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,1819,200,4.JPG\" alt=\"\" />20 грудня 2008 р. у приміщенні актової зали НПУ ім.&nbsp;М.Драгоманова відбувся <em>ІІ фестиваль-конкурс духовної пісні \"Аскольдів глас\"</em>. Розпочався вечір реколекційною наукою о. Олександра про роль і значення духовного співу на Божественній Літургії. Опісля гостей і учасників фестивалю привітала заслужена артистка України, парафіянка церкви св. Миколая Чудотворця на Аскольдовій могилі - Леся Горова. Після цього, власне, і стартував конкурс.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Участь у заході взяли церковні хори \"Аскольд\", \"Дзвони Подолу\", \"Хор храму Благовіщення при Патріаршому соборі\", фольклорний гурт \"Дивень\", жіночий ансамбль \"Anima\", вокально-інструментальний ансамбль \"Золоті струни\", сольні виконавці - Вікторія Михайлюк, Марія Чабан, Галина Книгиницька та Даниїла Костик. Загалом, десять учасників представили свою програму на розсуд журі та публіки. На святі пісні та співочої майстерності у виконанні конкурсантів звучали твори духовної тематики та українські старовинні і сучасні різдвяні колядки.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,1819,200,2.jpg\" alt=\"\" />Для оцінювання майстерності, змістовності та артистичності виконання творів було запрошено журі у складі: Михайло Хай - професор, кандидат мистецтвознавства, Валентина Кузик - музикознавець, доктор філософії мистецтва, лауреат Премії ім. М.В.Лисенка, член Національної спілки композиторів України, Василь Федоришин - заслужений діяч мистецтв України, професор, о.&nbsp;Петро Жук - голова Катехитичної комісії Київської архиєпархії, Жанна Боднарук - заслужена <em>артистка</em> України, Світлана Острова - композитор, органістка, диригент-хоровик, старший викладач Національної музичної академії ім. П.Чайковського, член спілки композиторів України.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Після закінчення конкурсної програми до дітей завітав святий Миколай. Разом із небесним почетом ангелят та чортиками чудотворець розпитував про добрі справи, що їх зробили діти за цей рік, дарував подарунки.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Після вистави було оголошено результати конкурсу. За рішенням журі, у трьох номінаціях - \"хор\", \"ансамбль\", \"соліст\" визначено по три призових місця. Так, у номінації \"<span style=\"text-decoration: underline;\">хор</span>\" дипломом І ступеню відзначено хор <strong>\"Дзвони Подолу\"</strong><strong>\"Аскольд\"</strong> (ІІ ступінь) та <strong>\"Хор храму Благовіщення при Патріаршому соборі\"</strong> (ІІІ ступінь).</span> (керівник - Тарас Миронюк), грамоти отримали хор </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,1819,200,1.jpg\" alt=\"\" />Номінацію \"<span style=\"text-decoration: underline;\">ансамбль</span>\" вдруге очолив ансамбль бандуристів <strong>\"Золоті струни\" </strong>(керівник - Наталія Слободянюк). Колектив утворився<strong> </strong>у 2001 році при Київському Палаці дітей та юнацтва.<strong> </strong>В ньому займаються більше 30 дівчаток та хлопчиків віком від 7 до 21 року. Грамотами ІІ та ІІІ ступенів нагороджено жіночий ансамбль <strong>\"</strong><strong>Anima</strong><strong>\"</strong> (утворений при УГКЦ св. Миколая Чудотворця на Аскольдовій могилі) та фольклорний гурт <strong>\"Дивень\"</strong> (ІМФЕ ім. М.Рильського).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Заслужена артистка України Жанна Боднарук виголосила оцінки у номінації \"<span style=\"text-decoration: underline;\">соліст</span>\". Першу нагороду та грошову премію присуджено юній виконавиці <strong>Марії Чабан</strong> із м. Івано-Франківська. Грамоту ІІ ступеню отримала <strong>Галина Книгиницька</strong>, грамоту ІІІ ступеню - <strong>Вікторія Михайлюк</strong>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,1819,200,P1010009.JPG\" alt=\"\" width=\"200\" height=\"150\" />Таким чином, фестиваль-конкурс \"Аскольдів глас\", що надав слухачам щасливу нагоду поринути в глибини літургійного та народно-обрядового поетичного слова та співу, якими впродовж віків українці дивували світ, відкрив нові імена і таланти, якими так багатий наш український народ.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">По завершенню фестивалю усім чемним діточкам святий Миколай роздав солодкі дарунки, хвалив за християнську вихованість і любов до Бога.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Катерина Чаплик, &laquo;Оранта&raquo;</span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>',NULL,1819,5,1,NULL,42);
INSERT INTO `publications_publication` VALUES
(4695,'P','2008-12-21',1,NULL,'Rss','Співаймо разом з «Аскольдовим гласом»','Петро Жук: «Дуже рідкісна подія на рівні парафії»\n<p>Отець Петро Жук, член жюрі фестивалю «Аскольдів глас», голова катехитичної комісії Київської архиєпархії.</p>\n<p>Дуже приємні враження від дитячого співу. Дуже приємно, що парафія, тобто дуже маленька громада, зуміла зорганізувати такий фестиваль. Це є гарно і повчально для всіх інших. І отців і громад. Подібні фестивалі відбуваються, але більше на рівні всеукраїнському, на рівні регіонів, але ж щоби в церкві, на рівні парафії, то дуже рідкісне явище.</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=154:spivamo-razom-z-laskoldovym-glasomr&catid=25:parafija-cat&Itemid=27',1818,NULL,1,1,26),
(4696,'P','2008-12-21',1,NULL,'Rss','«Аскольдів глас» 2008','<p><img style=\"float: right;\" src=\"http://www.askoldova-mohyla.org/uk/image,1819,200,4.JPG\" />20 грудня 2008 р. у приміщенні актової зали НПУ ім. М.Драгоманова відбувся <em>ІІ фестиваль-конкурс духовної пісні \"Аскольдів глас\"</em>. Розпочався вечір реколекційною наукою о. Олександра про роль і значення духовного співу на Божественній Літургії. Опісля гостей і учасників фестивалю привітала заслужена артистка України, парафіянка церкви св. Миколая Чудотворця на Аскольдовій могилі - Леся Горова. Після цього, власне, і стартував конкурс.</p>\n<p>Участь у заході взяли церковні хори \"Аскольд\", \"Дзвони Подолу\", \"Хор храму Благовіщення при Патріаршому соборі\", фольклорний гурт \"Дивень\", жіночий ансамбль \"Anima\", вокально-інструментальний ансамбль \"Золоті струни\", сольні виконавці - Вікторія Михайлюк, Марія Чабан, Галина Книгиницька та Даниїла Костик. Загалом, десять учасників представили свою програму на розсуд журі та публіки. На святі пісні та співочої майстерності у виконанні конкурсантів звучали твори духовної тематики та українські старовинні і сучасні різдвяні колядки.</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=155:laskoldiv-glasr-2008&catid=25:parafija-cat&Itemid=27',1820,NULL,1,1,26),
(4697,'P','2008-12-21',1,NULL,'Rss','Депутата, що підняв український прапор над Київрадою у 90 році, привітали на храмовому празнику','<p>У церкві св. Миколая на Аскольдовій Могилі сьогодні під час храмового свята привітали чотирьох іменинників і серед них відомого поета-дисидента депутата Київради 90-го року, який голосував за підняття синьо-жовтого прапора над мерією, Миколу Горбаля.</p>\n<p>«Завжди приємно коли тебе поздоровляють, а тим паче у храмі, до якого ти ходиш на щонеділі службу Божу. Тим паче зараз, коли виходять книжки моїх поезій і люди вільно їх читають» сказав «Оранті» поет-іменинник. Нещодавно вийшла чергова книжка поезій Миколи Горбаля «Ув`язнені». За його словами він святкує іменини щоразу у церкві на Миколая, а день народження 30 вересня, хоч у паспорті в нього значиться, що він народився 6 травня.</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=153:deputata-shcho-pidnjav-ukrajinsky-prapor-nad-kyjivradoju-u-90-roci-pryvitaly-na-hramovomu-praznyku&catid=25:parafija-cat&Itemid=27',1817,NULL,1,1,26);
INSERT INTO `publications_publication` VALUES
(4698,'P','2008-12-21',1,NULL,'Note','Папські побажання для нововисвячених священиків Згромадження Легіонерів Христа','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Урочистість в папській базиліці мала міжнародний та місійний характер, адже новопресвітери походять з різних країн світу. Відповідно, храм був переповнений паломниками з усіх куточків планети.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">21 грудня 2008 р., після проказування молитви &laquo;Ангел Господній&raquo;, вітаючи різними мовами паломників, зібраних на площі Святого Петра, Венедикт&nbsp;XVI окремо привітав 50 нововисвячених священиків зі Згромадження Легіонерів Христа, яким у суботу, 20 грудня 2008&nbsp;р., уділив ієрейські свячення кардинал Анжело Содано, Декан Колегії Кардиналів. Папа, зокрема, сказав: &laquo;Найдорожчі, Христова любов, яка надихала святого Павла у його місії, нехай надихає ваше служіння. Від щирого серця благословляю вас і ваших близьких&raquo;. <br /><br />Свячення відбулися у папській базиліці Святого Павла поза мурами в рамках відзначення Року святого Павла, адже Апостол Народів є особливим покровителем Згромадження Легіонерів Христа. Варто додати, що 34 з нововисвячених священиків свої дияконські свячення отримали 29 червня&nbsp;ц.р., саме в день відкриття Року святого Павла.<br /><br />Урочистість в папській базиліці мала міжнародний та місійний характер, адже новопресвітери походять з різних країн світу. Відповідно, храм був переповнений паломниками з усіх куточків планети. Справа від престолу, збудованого над місцем поховання святого Павла, було встановлено образ Матері Божої з Ґвадалупе, покровительки Згромадження. Богородиця з Ґвадалупе є також покровителькою Мексики, звідки походять 23 новоєреї.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,1816,5,1,NULL,43),
(4699,'P','2008-12-21',0,NULL,'Note','Молитва \"Ангел Господній\" з Папою Венедиктом XVI','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Це таїнство спасіння, окрім історичного, має ще й космічний вимір: Христос є сонцем благодаті, яке своїм промінням перемінює і запалює всесвіт, що в очікуванні&raquo;, - наголосив Папа Венедикт ХVІ, додаючи, що празник Різдва пов\'язаний із зимовим сонцестоянням, коли день, у північній півкулі, починає продовжуватися.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сьогодні, 21 грудня, перед проказуванням молитви &laquo;Ангел Господній&raquo; та уділенням Апостольського благословення усім тим, які прибулим на площу Святого Петра у Ватикані, Єпископ Риму звернувся до присутніх такими словами: &laquo;Дорогі брати та сестри, Євангелія цієї четвертої неділі Адвенту пропонує нам розповідь про Благовіщення (Лк.&nbsp;1,26-38), містерію, до якої щодня повертаємось, проказуючи &laquo;Ангел Господній&raquo;. Ця молитва дозволяє нам наново пережити вирішальний момент, у якому Бог постукав до серця Марії і, отримавши Її &laquo;так&raquo;, розпочав приймати тіло у Ній і від Неї&raquo;. Далі, пригадуючи зібраним про празник Різдва Христового, який невдовзі відзначатимуть християни, Святіший Отець звернув увагу присутніх на те, що кожен із нас є запрошеним зупинити свій погляд на великому таїнстві, яке зберігала Пресвята Богородиця продовж дев\'яти місяців у Своєму дівичому лоні, і призадуматись над воплоченням Божого Сина у людське тіло. Народження Бога у Вифлеємі є першою основною віссю спасіння, а другою є смерть і воскресіння Ісуса. Ці дві осі, за словами Вселенського Архиєрея, є нероздільними, вони виявляють один план Господній: спасти людство і його історію. <br /><br />&laquo;Це таїнство спасіння, окрім історичного, має ще й космічний вимір: Христос є сонцем благодаті, яке своїм промінням перемінює і запалює всесвіт, що в очікуванні&raquo;, - наголосив Папа Венедикт ХVІ, додаючи, що празник Різдва пов\'язаний із зимовим сонцестоянням, коли день, у північній півкулі, починає продовжуватися. У цьому контексті, Папа зазначив також і те, що площа Святого Петра є сонячним годинником: у ці дні тінь великого центрального обеліску є найдовшою протягом року. Це пригадує нам застосування астрономії в обрахуванні часу для молитви. Наприклад, молитва &laquo;Ангел Господній&raquo; проказується вранці, опівдні та ввечері. З нагоди того, що саме сьогодні, 21 грудня, о 12 годині, припало зимове сонцестояння, Папа привітав усіх тих, які в різний спосіб братимуть участь у заходах, присвячених всесвітньому року астрономії, що відзначатиметься наступного, 2009 року, і буде приурочений 4 сторіччю від перших телескопічних спостережень Галілео Галілея.<br /><br />Далі Святіший Отець заохотив вірних скерувати свій погляд до Пресвятої Богородиці та Її обручника Святого Йосифа, які очікують на народження Божого Сина, і навчитись від Них насолоджуватись радістю Різдва Христового.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,1815,5,1,NULL,43);
INSERT INTO `publications_publication` VALUES
(4700,'P','2008-12-21',1,NULL,'Note','Папа Венедикт XVI прийняв представників італійської Дитячої Католицької Акції','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Щороку перед Різдвом діти і підлітки італійської Дитячої Католицької Акції відвідують Святішого Отця, щоб від імені всіх своїх членів віком від 4-ьох до 14-ти років привітати його з святами. Ця зустріч з Венедиктом&nbsp;XVI відбулась 20 грудня. Коло 50-ки дітей з різних регіонів Італії принесли Папі свої подарунки та привітали з Різдвом.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Щороку перед Різдвом діти і підлітки італійської Дитячої Католицької Акції відвідують Святішого Отця, щоб від імені всіх своїх членів віком від 4-ьох до 14-ти років привітати його з святами. Ця зустріч з Венедиктом&nbsp;XVI відбулась 20 грудня. Коло 50-ки дітей з різних регіонів Італії принесли Папі свої подарунки та привітали з Різдвом. <br /><br />Венедикт&nbsp;XVI дуже радо прийняв юних членів Католицької Акції і, звертаючись до них, окремо наголосив, що Ісус є найдорожчим другом усіх та заохотив їх допомогти своїм друзям пізнати та полюбити Ісуса і Його Церкву, запрошуючи молитись за тих дітей у світі, які голодують, хворіють, які страждають задля воєн та нестатків, молитись також і за те, щоб Господь перемінив серця людей та допоміг людству будувати краще майбутнє для всіх дітей у світі. Папа також попросив дітей молитись і за нього, за нелегку місію, які Господь йому повірив та, побажавши щасливого Різдва, поблагословив усіх дітей.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,1814,5,1,NULL,43),
(4701,'P','2008-12-21',0,NULL,'Note','«Вифлеємський вогонь миру» - ОГОЛОШЕННЯ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Молодіжна організація &laquo;Пласт - Національна скаутська організація України&raquo; впродовж багатьох років сприяє всебічному вихованню української молоді та дітей. Однією з традиційних акцій цієї організації є &laquo;Вифлеємський вогонь миру&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сценарій передачі \"Вифлеємського вогню миру\" у Києві. Місце - Софійська площа. Дата - неділя, 21 грудня 2008 року.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Молодіжна організація &laquo;Пласт - Національна скаутська організація України&raquo; впродовж багатьох років сприяє всебічному вихованню української молоді та дітей. Однією з традиційних акцій цієї організації є &laquo;Вифлеємський вогонь миру&raquo; <br /><br />Ця акція, котра набула значного поширення серед молоді європейських країн. Вогонь, котрий завжди горить у печері, де народився Спаситель, поширюється молодими людьми, минаючи кордони, долаючи тисячі кілометрів, символізуючи єдність світу навколо ідеї Добра, Братерства, Справедливості та Єднання. <br /><br />В Україні акція відбувається під патронатом Голови наглядової ради Міжнародного благодійного фонду &laquo;Україна 3000&raquo; п. Катерини Ющенко. її проведення в цьому році вже благословили Глави та Предтстоятелі більшості традиційних церков (УАПЦ, УПЦ КП, УГКЦ, РКЦ). <br /><br />Цього року акція &laquo;Вифлеємський вогонь миру&raquo; в Україні буде проведена уже вдесяте. Протягом її проведення планується взяти участь української делегації у старті акції у Відні, провести прес-конференцію, поширити Вогонь у 300 міст і сіл України у всіх областях, піднятися з ним на Говерлу, видати спеціальний випуск журналу &laquo;Пластовий шлях&raquo;, книжку з підсумками 10-ліття акції в Україні та ще багато іншого. До Києва Вогонь потрапить завдяки велопробігу від україно-польського кордону, що завершиться 21 грудня великим урочистим святом на Софійській площі. У ньому візьмуть представники всіх областей України, які поширюватимуть Вогонь далі. Планується також участь молоді з Росії, Молдови, Грузії та Вірменії. <br /><br /> Запрошуємо Вас долучитися до проведення акції &laquo;Вифлеємський вогонь миру&raquo;, відвідати акцію передачі Вифлиємського Вогню у Києві у неділю, 21 грудня 2008 р. на Софійському майдані у Києві. Офіційний початок о 14.00. <br /><br />Сценарій передачі \"Вифлеємського вогню миру\" у Києві. Місце - Софійська площа. Дата - неділя, 21 грудня 2008 року. Ведуча - Соломія Кейван, координатор міжнародної акції &laquo;Віфлеємський вогонь миру&raquo; в Україні <br /><br />14.00 - 14.10 - офіційний початок, вступне слово ведучої.   <br /><br />14.10 - 14.15 - поява велосипедистів, які під спів хору завозять Вифлеємський вогонь до сцени.   <br /><br />14.15 - 14.25 - передача вогню від велосипедистів до Соломії Кейван   <br /><br />14.25 - 14.40 - вогонь передається священику, молебень, відспів &laquo;Отче Наш&raquo;   <br /><br />14.40 - 15.00 - привітання представників усіх церков   <br /><br />15.00 - 15.10 - привітання Катерини Ющенко   <br /><br />15.10 - 15.15 - привітання Вячеслава Стебницького   <br /><br />15.15 - 15.30 - оголошення імен представників регіонів, що повезуть по Україні Вогонь.   <br /><br />15.30 - 16.00 - запалення всіх лампадок і свічок, передача Вогню усім присутнім.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: <a href=\"http://chs.org.ua/\" target=\"_blank\">http://chs.org.ua</a>/</span></span></p>',NULL,1808,5,1,NULL,42);
INSERT INTO `publications_publication` VALUES
(4702,'N','2008-12-21',0,NULL,'Note','Реколекції отця Олександра','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18-21 грудня 2008 р. у церкві св. Миколая Чудотворця на Аскольдовій Могилі відбудуться реколекції під проводом отця Олександра, ігумена отців студитів при Патріаршому Соборі у Києві за таким розкладом.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18-21 грудня 2008 р. у церкві св. Миколая Чудотворця на Аскольдовій Могилі відбудуться реколекції під проводом отця Олександра, ігумена отців студитів при Патріаршому Соборі у Києві за таким розкладом:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18 грудня&nbsp; о 18:00 - велика вечірня з Литією. 1 наука</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">19 грудня о 8:00 утреня, о 9:00 Божественна літургія, освячення води, молитва за оздоровлення. 2 наука</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; о 18:00 Божественна літургія. 3 наука.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">20 грудня&nbsp;&nbsp; о 8:00 утреня, о 9:00 Божественна літургія, панахида. 4 наука</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; о 17:00 (актовий зал педагогічного університету і м. Драгоманова) 5 наука, <strong>ІІ фестиваль-конкурс &laquo;Аскольдів глас&raquo;, святий Миколай приходить до дітей.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">21грудня&nbsp;&nbsp; &nbsp; о 10:00 Божественна літургія, проповідь, закінчення реколекцій.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\"Оранта\"</span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>',NULL,1731,5,1,NULL,41),
(4703,'P','2008-12-20',0,NULL,'Note','Повість врем\'яних літ Роки 6616 [1108] — 6625 [1117].','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В літо 6616 [1108]. Було закладено церкву святого Михайла, Золотоверху, князем Святополком місяця липня в 11-й [день]. І закінчили трапезницю Печерського монастиря за ігумена Фектиста, а була закладена за повелінням Гліба, котрий і кошти на неї дав.</span></span></p>','<p><a href=\"http://izbornyk.org.ua/pvlyar/yar11.htm\">Роки 6616 [1108] &mdash; 6625 [1117].</a></p>\r\n<p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6616. Заложена бысть церкви святаго Михаила, Золотоверхая, Святополкомъ княземъ, м<small>Ђ</small>сяца июля въ 11; и тряпезницю кончаша Печерьскаго манастыря, при Фектист<small>Ђ</small> игумен<small>Ђ</small>, юже заложи повеленьемь Гл<small>Ђ</small>бовомъ, иже ю и стяжа.</p>\r\n<p>В се же л<small>Ђ</small>то вода бысть велика во Дн<small>Ђ</small>пр<small>Ђ</small>, и въ Десн<small>Ђ</small> и въ Припет<small>Ђ</small>.</p>\r\n<p>Того же л<small>Ђ</small>та вложи Богъ въ сердце аньхимандриту игумену Печерьскому, нача понужати Феоктистъ Святополка князя вписати Федосья в синаникъ, Богу тако изволшю; Святополкъ же радъ бысть, вооб<small>Ђ</small>щася створити се, и се в<small>Ђ</small>ды житье его; и нача Святополкъ узв<small>Ђ</small>щати житье Федосьево; и вел<small>Ђ</small> и вписати в с<small>Ђ</small>наникъ, еже створи митрополитъ, вписа его в с<small>Ђ</small>наникъ; повел<small>Ђ</small> же митрополитъ по вс<small>Ђ</small>мъ епискупьямъ вписати Федосья въ с<small>Ђ</small>наникъ; вси же епископи с радостію вписаша, и поминають его во вс<small>Ђ</small>хъ сбор<small>Ђ</small>хъ.</p>\r\n<p>В се же л<small>Ђ</small>то преставися Катерина, Всеволожа дщерь, м<small>Ђ</small>сяца июля въ 24.</p>\r\n<p>В се же л<small>Ђ</small>то кончаша верхъ святая Богородица Влах<small>Ђ</small>рны на Клов<small>Ђ</small>, заложен<small>Ђ</small>й Стефаномъ епископомъ, бывшу ему пр<small>Ђ</small>же игуменомъ Печерьскаго манастыря.</p>\r\n<p>В л<small>Ђ</small>то 6617. Преставися Евъпраксии, Всеволожа дщи, м<small>Ђ</small>сяца июля въ 9 день, и положено бысть т<small>Ђ</small>ло ея в Печерьскомъ манастыри у дверий, яже къ угу; и вчиниша надъ нею божницю, ид<small>Ђ</small>же лежить т<small>Ђ</small>ло ея.</p>\r\n<p>В то же л<small>Ђ</small>то, м<small>Ђ</small>сяца декабря въ 2 день, Дмитръ Иворовичь взя вежи Половецкие у Дона; 1000 в<small>Ђ</small>жь взя послани Володимеромъ княземъ.</p>\r\n<p>В л<small>Ђ</small>то 6618. Идоша весн<small>Ђ</small> на Половц<small>Ђ</small> Святополкъ, и Володимеръ, Давыдъ; и дошедше Воиня, воротишася.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6616 [1108]. Було закладено церкву святого Михайла, Золотоверху, князем Святополком місяця липня в 11-й [день]. І закінчили трапезницю Печерського монастиря за ігумена Фектиста, а була закладена за повелінням Гліба, котрий і кошти на неї дав.</p>\r\n<p>У це ж літо вода була велика у Дніпрі, і в Десні, і в Припеті.</p>\r\n<p>Того ж літа вложив Бог у серце архімандриту ігуменові Печерському, почав понужати Феоктист князя Святополка вписати Федосія в синодик, як волю Божу. Святополк був радий, пообіцяв зробити це і знати житіє його. І почав Святополк вивчати житіє Федосія і звелів вписати його в синодик, що і зробив митрополит, вписавши його в синодик. І повелів митрополит у всіх єпископіях вписати Федосія в синодик. Усі ж єпископи із радістю вписали і поминають його на всіх соборах.</p>\r\n<p>У це ж літо померла Катерина, дочка Всеволода, місяця липня в 24-й [день].</p>\r\n<p>У це ж літо закінчили верх [церкви] святої Богородиці Влахірни на Клові, закладеної Стефаном єпископом, який перед тим був ігуменом Печерського монастиря.</p>\r\n<p>В літо 6617 [1109]. Померла Євпраксія, дочка Всеволода, місяця липня в 9-й день, ї поклали тіло її в Печерському монастирі біля дверей, що далі на південь. І зробили над нею божницю, де лежить тіло її.</p>\r\n<p>Того ж літа місяця грудня в 2-й день Дмитр Іворович узяв вежі половецькі біля Дону. Тисячу веж узяли послані князем Володимиром.</p>\r\n<p>В літо 6618 [1110]. Ходили весною на половців Святополк, Володимир і Давид. І, дійшовши до Воїня, повернулися.</p>\r\n<p>Того ж літа прийшли половці до Воїня, повернулися назад.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Того же л<small>Ђ</small>та пришедше Половци Воини, воротишася.</p>\r\n<p>Того же л<small>Ђ</small>та пришедше Половци, воеваша около Переяславля по селомъ.</p>\r\n<p>Того же л<small>Ђ</small>та взяша Половьци, ідучи назад, много селъ.</p>\r\n<p>В то же л<small>Ђ</small>то бысть знаменье в Печерьскомъ манастыри, февраля въ 11 день: явися столпъ огненъ отъ земля до небесе, а молнья осв<small>Ђ</small>тиша всю землю, и на небеси погрем<small>Ђ</small> в часъ 1 нощи; весь миръ вид<small>Ђ</small>. Сесь же столпъ ста на тряпезници камян<small>Ђ</small>й, яко не видити хреста бяше, и стоя мало, ступи на церковь и ста надъ гробомъ Федосьевомъ и потомъ надъ верхъ съступи, аки ко въстоку лицемъ, и потомъ невидимо бысть. Се же бяше не огнь, ни столпъ, но видъ ангельскый: ангелъ бо сице являеться, ово столпомъ огненомъ, ово же пламеномь; якоже рче Давидъ: творя ангелы своя духы и слугы своя огнь пламянъ; и слеми суть повеленьемь Божьимъ, аможе хощеть Владыка всихъ Творець, ангеломъ и челов<small>Ђ</small>комъ. Ангелъ бо приходить, кд<small>Ђ</small> благая м<small>Ђ</small>ста и молитвении домове, и ту показають н<small>Ђ</small>что мало видинья своего, ово бо огнемь, ово столпомъ, ово инакымъ вид<small>Ђ</small>ньемъ, яко мощно зр<small>Ђ</small>ти имь; не мощно бо зр<small>Ђ</small>ти челов<small>Ђ</small>комъ естества ангельскаго вид<small>Ђ</small>ти, аще и Моиси великій не возможе вид<small>Ђ</small>ти ангельскаго естьства, водяшеть бо я во дне столпъ облаченъ, а в нощи столпъ огненъ; то се не столпъ водяше ихъ, но ангелъ Божий идяще пред нимь в нощи и во дне. Тако и се явленье, которое показываше, емуже быти хотяше, еже бо и бысть: на второе л<small>Ђ</small>то не сий ли ангелъ вожь на иноплеменникы супостатъ бысть, якоже рече: ангелъ предъ тобою предъидеть; и пакы: ангелъ твой буди с тобою?</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Того ж літа приходили половці, воювали по селах навколо Переяслава.</p>\r\n<p>Того ж літа, ідучи назад, взяли половці багато сіл.</p>\r\n<p>У те ж літо було знамення в Печерському монастирі лютого в 11-й день: з\'явився вогненний стовп від землі до неба, а блискавка освітила всю землю, і на небесах прогреміло о першій годині ночі, весь мир бачив. Цей же стовп став на трапезниці кам\'яній, що й хреста не було видно, і, трохи постоявши, зійшов на церкву і став над гробом Федосія і потім зійшов на верх [церкви], немовби до сходу лицем, і потім не стало видно. Це ж був не огонь, і не стовп, але вид ангельський, бо так ангел являється: то огненним стовпом, то полум\'ям. Як сказав Давид: &laquo;Повертаючи ангелів своїх у духи і слуги свої, в огонь полум\'яний, посилає їх повелінням Божим Владика і Творець всіх, куди хоче,- ангелам і людям. Ангел-бо приходить туди, де благі місця і молитовні доми, і тут показують мало - небагато видіння свого або вогнем, або [вогненним] стовпом, або іншим видінням, щоб можна було побачити їм, бо людям не можна бачити натуру ангельську, як і Мойсей великий не зміг побачити ангельської натури, бо водив його вдень стовп захмарений, а вночі стовп огненний, то це не стовп водив їх, але ангел Божий, ідучи перед ним і вночі, і вдень. Так і це знамення показувало, що мало бути і що було: на друге літо чи не цей ангел був вождем на чужинців і супостатів, як сказано: &laquo;Ангел тобі передує&raquo;, і ще: &laquo;Ангел твій буде з тобою&raquo;.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&nbsp;</p>\r\n<p><em>[Заключне слово у Лаврентіївському списку 1377 року]</em></p>\r\n<p>&nbsp;</p>\r\n<p>Игуменъ Силивестръ святаго Михаила написахъ книги си Л<small>Ђ</small>тописець, над<small>Ђ</small>яся отъ Бога милость прияти, при князи Володимер<small>Ђ</small>, княжащу ему Кыев<small>Ђ</small>, а мн<small>Ђ</small> в то время игуменящю у святого Михаіла въ 6624, индикта 9 л<small>Ђ</small>та; а иже чтеть книги сия, то буди ми в молитвахъ.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&nbsp;</p>\r\n<p><em>[Заключне слово у Лаврентіївському списку 1377 року]</em></p>\r\n<p>&nbsp;</p>\r\n<p>Я, Сильвестр, ігумен монастиря святого Михайла, написав книги ці, сподіваючись від Бога милість мати, при князі Володимирі, коли він княжив у Києві, а я в той час ігуменом був у святого Михайла літа 1116 [6624], індикта 9 літа. А хто читає книги ці - помолися за мене.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Якоже пророкъ Давидъ глаголеть: яко ангеломъ своимъ запов<small>Ђ</small>сть о тебе схранить тя. Якоже пишеть премудрый Епифаний: къ коей же твари ангелъ приставленъ: ангелъ облакомъ и мъгламъ, и сн<small>Ђ</small>гу и граду и мразу, ангелъ гласомъ и громомъ, ангелъ зимы и зноеви, и осени и весны и л<small>Ђ</small>та, всему духу твари его на земли, и тайныя бездны, и суть скровены подъ землею, и преисподьнии тьмы, и сущи во безднахъ, безны бывшия древле верху земля, отъ неяже тмы, вечеръ и нощь, и св<small>Ђ</small>тъ и день, ко всимъ тваремъ ангели приставлени; такоже ангелъ приставленъ къ которой убо земли, да соблюдають куюжьдо землю, аще суть и погани; аще Божий гн<small>Ђ</small>въ будеть на кую убо землю, повел<small>Ђ</small>вая ангелу тому на кую убо землю бранью ити, то оной земл<small>Ђ</small> ангелъ не вопротивится [по] вел<small>Ђ</small>нью Божью. Яко и се бяше, и на ны кавелъ Богъ, гр<small>Ђ</small>хъ ради нашихъ, иноплеменникы поганыя, и поб<small>Ђ</small>жахуть ны повел<small>Ђ</small>ньемъ Божьимъ: они бо бяху водими аньеломъ, по повел<small>Ђ</small>нью Божью. Аще ли кто речеть, яко аньела н<small>Ђ</small>сть у поганыхъ, да слышить, яко Олександру Макидоньскому ополчившю на Дарья, и пошедшю ему и побидившю землю всю отъ въстокъ и до западъ, и поби землю Егупетьскую, и поби Арама, и приде в островы морьскыя; и взрати лице свое взыти въ Ерусалимъ, побидити Жиды, занеже бяху мирни со Дарьемь, и поиде со всими вои его, и ста на товарищи и почи; и присп<small>Ђ</small> ночь, и лежа на ложи своемь посред<small>Ђ</small> шатра, отверзъ очи свои, види мужа стояща над нимь, и м<small>Ђ</small>чь нагъ в руц<small>Ђ</small> его, и обличіе меча его яко молоніи, и запряже мечемь своимъ на главу цареву; и ужасеся царь велми и рече: не бий мене; и рече ему ангелъ: посла мя Богъ уимати цар<small>Ђ</small> великии предъ тобою и люди многи, азъ же хожю предъ тобою, помагая ти; а нын<small>Ђ</small> в<small>Ђ</small>дай, яко умьреши, понеже помыслилъ еси взити въ Ерусалимъ, зло створити ер<small>Ђ</small>емъ Божьимъ и к людемъ его. И рече царь: молю тя, о Господи, отпусті нын<small>Ђ</small> гр<small>Ђ</small>хъ раба твоего; аче не любо ти, а ворочюся дому моему. И рече ангелъ: не бойся; иди путемъ твоимъ къ Иерусалиму, и узриши ту въ Ерусалими мужа въ [о] бличенье мое, и борзо пади на лици своемь и поклонися мужу тому, и все еже речеть к тоб<small>Ђ</small> створи, не пр<small>Ђ</small>ступи р<small>Ђ</small>чь ему; в онь же день преступиши р<small>Ђ</small>чь его, и умреши. И въставъ царь, иде въ Ерусалимъ, и пришедъ въспроси ер<small>Ђ</small>евъ: иду ли на Дарья? и показаша ему книги Данила пророка, и рекоша ему: ты еси козелъ, а онъ овенъ, и потолчеши и возмеши царство его. Се убо не ангелъ ли вожаше Олексаньдра? не поганъ ли поб<small>Ђ</small>жаше, и вси Елини кумирослужебници? Тако и сі погании попущени гр<small>Ђ</small>хъ ради нашихъ. Се же в<small>Ђ</small>домо буди, яко въ хрестьянехъ [не] единъ ангелъ, но елико крестишася, паче же къ благов<small>Ђ</small>рнымъ княземъ нашимъ: но противу Божью повеленью не могуть противитися, но молять Бога прил<small>Ђ</small>жно за хрестьяньскыя люди. Якоже и бысть: молитвами святыя Богородицы и святыхъ ангелъ умилосердися Богъ, и посла ангелы в помощь Русьскимъ княземъ на поганыя; якоже рече к Моис<small>Ђ</small>еви: се ангелъ мой пр<small>Ђ</small>дъидеть предъ лицемъ твоимъ; якоже рекохомъ пр<small>Ђ</small>же. Зьнаменье се бысть м<small>Ђ</small>сяца февраля въ 11 день, исходяще сему л<small>Ђ</small>ту 18.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Як пророк Давид говорить: &laquo;Як ангелам своїм заповість про тебе,- збереже тебе&raquo;. Як пише премудрий Епіфаній: &laquo;До кожного творіння ангела приставлено: акгел - до хмар і туманів, і снігу, і граду, і морозу, ангел - до голосу і грому, ангел - до зими, і спеки, і осені, і весни, і літа, до всякого живого творіння божого на землі, і до таємничої безодні, і до того, що сховано під землею, і до підземної тьми, і до сущих у безоднях, і до безодні, що до створення світу була над землею, від неї ж тьма, вечір, і ніч, і світло, і день,- до всіх творінь ангели приставлені&raquo;. Також ангела приставлено до будь-якої землі, щоб оберігали кожну землю, хоч би вона була і поганська. Якщо Божий гнів упаде на яку-небудь землю, тому ангелу Бог наказує іти війною на ту землю, а тієї землі ангел не противиться повелінню Божому. Як і це було, і на нас навів Бог, через гріхи наші, чужеплемінців поганих, і перемагали нас повелінням Божим, бо водив їх ангел за повелінням Божим. А коли хто скаже, що ангела нема у поганих, то нехай послухає, як Олександр Македонський ополчився на Дарія, і пішов на нього, і завоював землю всю від сходу і до заходу, і побив землю Єгипетську, і побив Арама, і прийшов на острови морські, і повернув лице своє до Єрусалима, щоб взяти його і перемогти жидів, оскільки були у мирі із Дарієм, і пішов зі всім військом своїм, і став табором на спочинок. І приспіла ніч, і лежав він на ложі своєму посеред намету, одкрив очі свої і побачив мужа, який стояв над ним із оголеним мечем у руці, і лезо меча його як блискавка; і замірився мечем своїм на голову цареву; і вжахнувся вельми цар і сказав: &laquo;Не бий мене&raquo;. І рече йому ангел: &laquo;Послав мене Бог упокорювати царів великих перед тобою і численний люд, я ж ходжу поперед тебе, допомагаючи тобі. А нині відай, що помреш, оскільки намислив ти ввійти в Єрусалим, зло вчинити ієреям Божим і людям його&raquo;. І рече цар: &laquo;Молю тебе, о Господи, одпусти нині гріх раба твого, і, якщо не любо тобі, я повернуся до дому свого&raquo;. І рече ангел: &laquo;Не бійся, іди шляхом своїм в Ієрусалим, і побачиш там, в Єрусалимі, мужа в подобі моїй, і відразу пади ниць і поклонися мужу тому, і все, що скаже тобі, виконай, не порушуй сказаного ним. У який день порушиш сказане ним, у той день і помреш&raquo;. І встав цар, пішов в Єрусалим, і, коли прийшов, запитав євреїв: &laquo;Чи йти мені на Дарія?&raquo; І показали йому книги Данила пророка і сказали йому: &laquo;Ти - козел, а він - баран. І розіб\'єш і візьмеш царство його&raquo;. Так от, чи не ангел водив Олександра? Чи не погани тікали і всі елліни кумирослужителі? Так і цих поган напущено на нас через гріхи наші. Отже побачимо, що у християн [не] один ангел, але стільки, скільки є хрещених, а тим паче у благовірних князів наших, та Божому повелінню вони не можуть противитися, але молять Бога старанно за християнський люд. Так і було: молитвами святої Богородиці і святих ангелів умилосердився Бог і послав ангелів на допомогу руським князям проти поганих. Як сказав [Бог] Мойсею: &laquo;Це ангел мій попереду йтиме перед лицем твоїм&raquo;. Як сказано раніше, знамення це було місяця лютого в 11-й день, наприкінці цього літа 6618-го.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6619. Вложи Богъ Володимеру въ сердце, и нача глаголяти брату своему Святополку, понужая его на поганыя, на весну. Святополкъ же пов<small>Ђ</small>да дружини своей р<small>Ђ</small>чь Володімерю: они же рекоша: &laquo;не веремя нын<small>Ђ</small> погубити смерды отъ рольи&raquo;. И посла Святополкъ к Володимерю, глаголя: &laquo;да быхови ся сняла и о томъ подумалі быхомъ съ дружиною&raquo;. Послании же приидоша къ Володимеру и пов<small>Ђ</small>даша всю р<small>Ђ</small>чь Святополчю; и прииде Володимеръ, и ср<small>Ђ</small>тостася на Долобьск<small>Ђ</small>, и с<small>Ђ</small>доша въ единомъ шатр<small>Ђ</small>, Святополкъ съ своею дружиною, а Володимеръ съ своею.</p>\r\n<p>И бывшу молчанью, и рече Володимеръ: &laquo;брате! ты еси стар<small>Ђ</small>й; почни глаголати, како быхъмъ промыслили о Русьской земли&raquo;. И рече Володимеръ: &laquo;како я хочю молвити, а на мя хотять молвити твоя дружина и моя, рекуще: хощеть погубити смерды и ролью смердомъ? но се дивно ми брате; оже смердовъ жалуете и ихъ коний, а сего не помышляюще, оже на весну начнеть смердъ тотъ орати лошадью тою, и при<small>Ђ</small>хавъ Половчинъ ударить смерда стр<small>Ђ</small>лою и поиметь лошадь ту, и жену его и д<small>Ђ</small>ти его, и гумно его зажжеть; то о с<small>Ђ</small>мь чему не мыслите?&raquo; И рекоша вся дружина: &laquo;право воистину тако есть&raquo;. И рече Святополкъ: &laquo;се язъ, брате, готовъ есмь с тобою&raquo;; и посласта ко Давыдови Святославичю, велячи ему съ собою. И въста Володимеръ и Святополкъ, и ц<small>Ђ</small>ловастася, и поидоста на Половц<small>Ђ</small>, Святополкъ съ сыномъ, Ярославъ и Володимеръ съ сынми, и Давыдъ со сыномъ, и поидоша возложивше надежю на Бога и на пречистую Матерь его и на святыя ангелы его; и поидоша въ 2 нед<small>Ђ</small>лю поста, а в пятокъ быша на Сул<small>Ђ</small>; в суботу поидоша и быша на Хорол<small>Ђ</small>, и ту и сани пометаша; а в нед<small>Ђ</small>лю поидоша, в ню же хресть ц<small>Ђ</small>лують, и приидоша на Пслъ и оттуди сташа на р<small>Ђ</small>ц<small>Ђ</small> Голт<small>Ђ</small>, ту пождаша и вои, и оттудо идоша Върьскла, ту же завътра, въ среду, хрестъ ц<small>Ђ</small>ловаша и възложиша всю свою надежю на хрестъ, со многими слезами; и оттуд<small>Ђ</small> преидоша многи р<small>Ђ</small>ки, въ 6 нед<small>Ђ</small>лю поста, и поидоша к Донови во вторникъ. И оболочишася во брон<small>Ђ</small>, и полки изрядиша, и поидоша ко граду Шаруканю; и князь Володимеръ пристави попы своя, <small>Ђ</small>дучи предъ полкомъ, п<small>Ђ</small>ти тропари и коньдакы хреста честнаго и канунъ святой Богородици. По<small>Ђ</small>хаша ко граду, вечеру сущю, и в нед<small>Ђ</small>лю выидоша из города, и поклонишася княземъ Рускымъ, и вынесоша рыбы и вино; и перележаша нощь ту. И завътра, въ среду, поидоша къ Сугрову, и пришедше зажьгоша и, а в четвергъ поидоша с Дона, а в пятницю, завътра, м<small>Ђ</small>сяца марта въ 24 день, собрашася Половци, изрядиша Половци полки своя и поидоша к боеви. Князи же наши възложише надежю свою на Бога, и рекоша: &laquo;убо смерть намъ зд<small>Ђ</small>, да станемъ кр<small>Ђ</small>пко!&raquo; и ц<small>Ђ</small>ловаша другъ друга, възведше очи свои на небо, призываху Бога вышняго. И бывшю же соступу и брани кр<small>Ђ</small>пц<small>Ђ</small>, Богъ вышний возр<small>Ђ</small> на иноплеменникы со гн<small>Ђ</small>вомъ, падаху предъ хрестьяны; и тако поб<small>Ђ</small>жени быша иноплеменьници, и падоша мнози врази наши супостати предъ Рускыми князи и вои на потоце Дег<small>Ђ</small>я, и поможе Богъ Рускымъ княземъ. И въздаша хвалу Богу въ тъ день, и заутра, субот<small>Ђ</small> наставш<small>Ђ</small>, празноваша Лазарево въскресенье [и] Благов<small>Ђ</small>щенья день, и похваливше Бога, проводиша суботу, и в нед<small>Ђ</small>лю пріидоша. Наставшю же понед<small>Ђ</small>лнику страстныя нед<small>Ђ</small>ли, паки иноплеменницы собраша полки, своя многое множество, и выступиша яко борове велиции, и тмами тмы и оступиша полкы Рускыи. И посла Господь Богъ ангела в помощь Русьскымъ княземъ; и поидоша Половецьстии полъци и полъц<small>Ђ</small> Русьст<small>Ђ</small>и, и сразишася первое с полкомъ, и тр<small>Ђ</small>сну аки громъ сразившимася челома, и брань бысть люта межи ими, и падаху обои. И поступи Володимеръ с полки своими, и Давыдъ, и возр<small>Ђ</small>вше Половци вдаша плещи свои на б<small>Ђ</small>гъ; и падаху Половци предъ полкомъ Володимеровомъ, невидимо бьеми ангеломъ, яко се видяху мнози челов<small>Ђ</small>ци, и главы летяху невидимо стинаемы на землю. И побиша я в понед<small>Ђ</small>лникъ страстный, м<small>Ђ</small>сяца марта въ 27 день, избьени быша иноплеменниц<small>Ђ</small> многое множество на р<small>Ђ</small>ц<small>Ђ</small> Салниц<small>Ђ</small>, и спасе Богъ люди своя.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6619 [1111].</p>\r\n<p>І вселив Володимиру Бог у серце [бажання],</p>\r\n<p>І почав говорити братові своєму Святополку,</p>\r\n<p>І понуджував його на поганих йти весною.</p>\r\n<p>І повідав Святополк дружині своїй Володимира мову,</p>\r\n<p>І дружина йому відказала: &laquo;Не верем\'я нині смердів губити від ріллі&raquo;.</p>\r\n<p>І послав Святополк до Володимира сказати: &laquo;Треба зібратись,</p>\r\n<p>І про те з дружиною подумати&raquo;.</p>\r\n<p>&nbsp;</p>\r\n<p>Посланії ж прийшли до Володимира</p>\r\n<p>І всю мову Святополка розповіли.</p>\r\n<p>І прийшов Володимир,</p>\r\n<p>І зустрілись вони на Долобську,</p>\r\n<p>І сиділи в одному наметі -</p>\r\n<p>Святополк із своєю дружиною,</p>\r\n<p>І Володимир - зі своєю,</p>\r\n<p>І було між ними мовчання.</p>\r\n<p>І рече Володимир: &laquo;Брате! Ти є старший, почни розмову,</p>\r\n<p>Як би нам промислити про Руськую землю&raquo;.</p>\r\n<p>І рече Володимир:</p>\r\n<p>&laquo;Таке я хочу сказати, а проти мене хоче мовити твоя і моя дружина</p>\r\n<p>І скажуть: &laquo;Хоче смердів погубити і ріллю смердам&raquo;.</p>\r\n<p>Але це дивно мені, брате, що смердів жалуєте і їх коней,</p>\r\n<p>А про те не думаєте, що весною,</p>\r\n<p>Як почне смерд орати конем тим,</p>\r\n<p>Над\'їде половчин, заб\'є смерда стрілою,</p>\r\n<p>І забере коня того,</p>\r\n<p>І жону його,</p>\r\n<p>І дітей його,</p>\r\n<p>І гумно його запалить. То чому про це не думаєте?&raquo;</p>\r\n<p>І загомоніла вся дружина: &laquo;Так воно і є насправді&raquo;.</p>\r\n<p>І сказав Святополк: &laquo;Тепер я, брате, іти з тобою готовий&raquo;</p>\r\n<p>І послали до Давида Святославича,</p>\r\n<p>І веліли йому спільно на поганих.</p>\r\n<p>І встали Володимир і Святополк,</p>\r\n<p>І поцілувалися.</p>\r\n<p>І пішли на половців: Святополк із сином,</p>\r\n<p>І Ярослав та Володимир із синами,</p>\r\n<p>І Давид із сином,</p>\r\n<p>І пішли, поклавши надію на Бога,</p>\r\n<p>І на його Матір пречисту,</p>\r\n<p>І на святих ангелів його.</p>\r\n<p>І вийшли в похід вони в другу неділю посту,</p>\r\n<p>А у п\'ятницю були на Сулі,</p>\r\n<p>У суботу пішли і були на Хоролі,</p>\r\n<p>І тут вони сани покинули,</p>\r\n<p>А в неділю, коли хрест цілують, пішли</p>\r\n<p>І прийшли на Псьол,</p>\r\n<p>І звідти стали на ріці Голті,</p>\r\n<p>І тут воїв почекали</p>\r\n<p>І звідти пішли на Ворсклу,</p>\r\n<p>І там, на другий день, в середу, хрест цілували,</p>\r\n<p>І возложили всю свою надію на хрест із сльозами великими,</p>\r\n<p>І звідти ріки перейшли численні,</p>\r\n<p>І вийшли до Дону в вівторок на шостому тижні великого посту.</p>\r\n<p>І одяглись у броню,</p>\r\n<p>І полки урядили,</p>\r\n<p>І пішли на город Шарукань.</p>\r\n<p>І попереду війська їхав князь Володимир</p>\r\n<p>І наказав попам співати тропарі,</p>\r\n<p>І кондаки хреста чесного,</p>\r\n<p>І канон святої Богородиці.</p>\r\n<p>І під\'їхали до города, як був вже вечір,</p>\r\n<p>І в неділю вийшли із города</p>\r\n<p>І поклонилися князям руським,</p>\r\n<p>І винесли вони і риби, і вина,</p>\r\n<p>І переночувало ніч там [руське військо],</p>\r\n<p>І пішли другого дня у середу до Сугрова,</p>\r\n<p>І коли прийшли, запалили його,</p>\r\n<p>А в четвер пішли із Дону.</p>\r\n<p>&nbsp;</p>\r\n<p>А у п\'ятницю ізранку, березня у день двадцять четвертий,</p>\r\n<p>Половці зібралися, половці полки свої урядили</p>\r\n<p>І пішли на бій.</p>\r\n<p>І поклали князі наші надію свою на Бога,</p>\r\n<p>І сказали: &laquo;Може, й смерть нам тут, так станем кріпко&raquo;.</p>\r\n<p>І один одного цілували.</p>\r\n<p>І возводили у небо свої очі,</p>\r\n<p>І закликали Бога вишнього.</p>\r\n<p>І тоді зійшлись,</p>\r\n<p>І була брань кріпка,</p>\r\n<p>І Бог вишній на чужеплемінців з гнівом глянув -</p>\r\n<p>І падали перед християнами.</p>\r\n<p>І розгромили так чужеплемінців,</p>\r\n<p>І полягли численні враги-супостати наші</p>\r\n<p>Від руських князів і воїв на потоці Дегія,</p>\r\n<p>І поміг Бог руським князям.</p>\r\n<p>І хвалу воздали в той день Богові</p>\r\n<p>І на ранок, як надійшла субота, Лазареве воскресіння святкували І Благовіщення день.</p>\r\n<p>І, похваливши Бога, провели суботу,</p>\r\n<p>І неділі діждались.</p>\r\n<p>І, коли надійшов понеділок страсного тижня,</p>\r\n<p>Знову чужеплемінці полків своїх безліч зібрали,</p>\r\n<p>І на русів як бори неозорі виступили,</p>\r\n<p>І тисячами тисяч обступили вони полки руські.</p>\r\n<p>І послав Господь Бог ангела у поміч князям руським,</p>\r\n<p>І пішли полки половецькі</p>\r\n<p>І полки руські,</p>\r\n<p>І зіткнулися перші ряди полків,</p>\r\n<p>І наче грім ударив - вдарились чолами,</p>\r\n<p>І брань люта була між ними,</p>\r\n<p>І гинули одні й другі.</p>\r\n<p>І став натискати Володимир з полками своїми</p>\r\n<p>І Давид,</p>\r\n<p>І коли половці силу узріли, взяли ноги на плечі,</p>\r\n<p>І падали половці перед полком Володимировим,</p>\r\n<p>І бив їх невидимий ангел,</p>\r\n<p>І це бачили різні люди,</p>\r\n<p>І летіли голови на землю - стиналися невидимо,</p>\r\n<p>І розгромили їх у страсний понеділок,</p>\r\n<p>Березня місяця в день двадцять сьомий,</p>\r\n<p>І на Салниці-ріці чужеплемінців силу-силенну перебили,</p>\r\n<p>І спасіння Бог дав для людей своїх.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Святополкъ же, и Володимеръ, и Давыдъ прославиша Бога, давшаго имъ поб<small>Ђ</small>ду таку на поганыя, и взяша полона много, и скоты и кони и овц<small>Ђ</small>, и колодниковъ много изоимаша рукама. И въпросиша колодникъ, глаголюще: &laquo;како васъ толика сила и многое множество, не могосте ся противити, но воскор<small>Ђ</small> поб<small>Ђ</small>госте?&raquo; Си же отв<small>Ђ</small>щеваху, глаголюще: &laquo;како можемъ бітися с вами? а друзии <small>Ђ</small>здяху верху васъ въ оружьи св<small>Ђ</small>тл<small>Ђ</small> и страшни, иже помагаху вамъ?&raquo; Токмо се суть ангели, отъ Бога послани помогать хрестьяномъ. Се бо ангелъ вложи въ сердце Володимеру Манамаху пустити братью свою на иноплеменникы, Русьскии князи; се бо, якоже рекохомъ, видинье видиша в Печерьскомъ манастыри, еже стояше столпъ огненъ на тряпезници, таже преступ<small>Ђ</small> на церковь и оттуда к Городцю; ту бо бяше Володимеръ в Радосыни, и тогда се ангелъ вложи Володимеру въ сердце, нача понужати, якоже рекохомъ. Т<small>Ђ</small>мже достойно похваляти ангелы, якоже Іоанъ Златоустець рече: ибо ти Творцю безначално поють, милостиву ему быти и тиху челов<small>Ђ</small>комъ. Ангелы бо, глаголю, наша поборникы на противныя силы воюющимъ, имъ же есть архангелъ Михаилъ, ибо со дьяволомъ т<small>Ђ</small>ла ради Моисиева противяся, на князь же Перьский свободы ради людьския противяся, повеленьемь Божьимъ, всю тварь разд<small>Ђ</small>лити языкомъ старишины наставляюще; симъ же н<small>Ђ</small>коего Перьсямъ прозр<small>Ђ</small>ти оправда, Михаила же сущимъ обр<small>Ђ</small>заномъ людемъ схранити повел<small>Ђ</small>, съставити же пред<small>Ђ</small>лы ихъ прогн<small>Ђ</small>ваньемь непрогн<small>Ђ</small>ваныя ярости, но отъ блаженаго н<small>Ђ</small>коего неизреченьнаго слова; сему же работати Июд<small>Ђ</small>емь Персямъ нудящю, сему же на свободу изъвлекущю, и прил<small>Ђ</small>жно к Богу молитву приносящимъ, глаголюще: Господи Вседержителю! докол<small>Ђ</small> не помилуеши Иерусолима и градъ Июдовыхь? ихже презр<small>Ђ</small> с<small>Ђ</small>мьдесятное. л<small>Ђ</small>то, егоже видиньемь и Данилъ летящее лице его яко видъ молъиный рещи, очи его яко св<small>Ђ</small>щи, и мышьци его и голени яко видъ м<small>Ђ</small>ди блещащеся, и гласъ слова его яко гласъ многаго народа. Отъ нихъ есть осла отвращая, и Валама отъ нечистого волъшьвл<small>Ђ</small>нья празднотворий. Отъ нихъ же и м<small>Ђ</small>чь извл<small>Ђ</small>къ противу, Ісусу Наугину помощи ему на противныя образомъ повел<small>Ђ</small>вая. Отъ нихъ есть 100 и 80 тысящь Суриськиихъ единою нощью поразихъ, и сопъ варварьскоыхъ см<small>Ђ</small>си смертью. Отъ нихъ же есть, иже пророка Амбакума \'Въздухомъ принесъ скоченіемь до пророка Данила, поср<small>Ђ</small>д<small>Ђ</small> же левъ пр<small>Ђ</small>пита к. Таковии же убо и тации на враги изящьствують. Такоже есть и богол<small>Ђ</small>пный Рафаилъ, отъ единыя рыбы ур<small>Ђ</small>за утрьникы, б<small>Ђ</small>снующуюея отроковицю изц<small>Ђ</small>ли, и сл<small>Ђ</small>па старца сълънъця вид<small>Ђ</small>ти створи ему. Убо не великихъ ли честий достойни суть нашю жизнь храняще? Не токмо бо хранители языкомъ повелени быша аньгели, якоже р<small>Ђ</small>чено бысть: &laquo;егда разд<small>Ђ</small>ляше Вышний языкы, ихъ же расия сыны Адамовы, постави пред<small>Ђ</small>лы языкомъ по числу ангелъ Божиихъ&raquo;; но и в<small>Ђ</small>рнымъ челов<small>Ђ</small>комъ комуждо достася ангелъ. Ибо отроковица Роди кзглаголавши апостолъ предъ дверьми стоящю Петру, Иродова лица изб<small>Ђ</small>гь, глаголаху не имущи в<small>Ђ</small>ры: &laquo;и ангелъ его есть&raquo;. Свидительствуеть же и самъ Господъ, глаголя: &laquo;видите и не радите единого отъ малыхъ сихъ: глаголю бо вамъ, яко ангели ихъ видять лице выну Отца моего, сущю на небес<small>Ђ</small>хъ&raquo;. Еще же у коейждо церкви хранітеля ангелы пристави Христосъ, якоже открываеть Іоану, глаголя: &laquo;рци ангелу, сущему въ церькви Измуреньст<small>Ђ</small>: видихъ твою нищету и скорбь, нъ богатъ еси&raquo;. Добро изв<small>Ђ</small>стьно убо есть любящимъ насъ ангеломъ, яко насъ ради къ Владыц<small>Ђ</small> молящимся: ибо &laquo;служебнии дуси суть&raquo;, якоже и апостолъ глаголеть: &laquo;въ служенье слеми хотящимъ ради . насл<small>Ђ</small>дити спасенье&raquo;. Ихъже и поборникы и сборникы, якоже и ныня слышалъ еси Данила, како вводи архангела Михаила Персемь, в часъ прогн<small>Ђ</small>ванья, нашея ради свободы. Се бо людемъ работати Персямъ нужаше, якоже речено бысть, се же раздришити пленьныя тщашеся. И одол<small>Ђ</small>еть Михаилъ противнику, ибо Ефратъ Жидове прешедше, отъ него паки селенье прияша, и градъ и церковь създаша.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Святополк же, і Володимир, і Давид прославили Бога за таку перемогу над поганими. І взяли полон великий, і худобу, і коні, і вівці, і колодників чимало зловили руками. І запитали бранців, кажучи: &laquo;Як ви такою силою і такою безліччю не могли нам противитись, але невдовзі побігли?&raquo; Вони ж відповідали, кажучи: &laquo;Як можемо битися з вами, як другі їздили зверху вас в оружжі світлому і страшному і помагали вам?&raquo; Адже це були ангели, від Бога послані у поміч християнам. Адже це ангел вложив у серце Володимиру Мономаху [бажання] пустити братів своїх, русьських князів, на чужоплемінців. Це ж бо, як уже повідали, знамення бачили в Печерському монастирі, де стояв стовп вогняний на трапезниці, а потім переступив на церкву і звідти на Городець, бо там був Володимир у Радосині. І тодї-от ангел і вложив Володимиру в серце [бажаний], і [він] почав понуджати, як уже розказали. От тому й достойно похваляти ангелів, як сказав Іоанн Златоуст: &laquo;Бо ті Творцеві вічно співають, щоб милостивий і тихий був для людей. Ангели-бо, кажу, наші поборники, коли воюємо з ворожими силами, а [воєвода] над ними - архангел Михаїл, бо з дияволом за тіло Мойсеєве боровся, проти князя перського за свободу людську змагався, повелінням Божим всі створіння поділив, народам старійшин настановив, тим же персам якогось [старійшину] доглянути дозволив, Михайла ж обрізаним людям сховати повелів, межі скласти [звелів] їхнього прогнівання не гріховною лютістю, але якимось блаженним, несказанним словом. Причому цей працювати іудеїв на персів примушував, а той намагався звільнити їх і старанно молився Богу, кажучи: &laquo;Господи Вседержителю! Коли ж ти помилуєш Ієрусалим і городи іудові, які ти залишив без догляду [от уже] сімдесяте літо?&raquo; Його ж бачив і Даниїл, як він летів із лицем, розповідав, подібним до блискавки, очі його - як свічки, і м\'язи його і стегна - як мідь блищали, і голос слова його - як голос численного народу. Він ослицю збочив з дороги і Валаама нечистого волхвування пустотворчі відвернув. Він і меч добув проти Ісуса Навіна, наказуючи в цей спосіб допомогти йому проти ворогів. Ангели сто вісімдесят тисяч сірійців за одну ніч перебили і сон варварів перетворили в смерть. Ангели пророка Аввакума рухом повітря перенесли до пророка Данила, щоб між левами годував його. Такії от вони і такий у боротьбі з ворогом хист мають. Такий і богоподібний Рафаїл: із одної риби вирізав жир - і біснувату дівицю зцілив, і сліпому старцю дав змогу сонце бачити. Великої ж бо честі гідні ті, що наше життя оберігають. Бо не тільки охоронцями народів зобов\'язані бути ангели, як уже сказано було: &laquo;Коли Всевишній розділяв народи і розсіював синів Адамових, визначив кількість народів за числом ангелів Божих&raquo;. Але і кожній віруючій людині дістався ангел. Бо коли дівиця Роді говорила апостолам, перед дверима стояв [апостол] Петро, котрий уникнув Ірода, її мові не вірили [і говорили]: &laquo;Вона - ангел його&raquo;. Свідчить же і сам Господь, кажучи: &laquo;Дивіться і не зневажайте жодного із малих цих: кажу-бо вам, що ангели їхні не бачать лице Отця мого, який на небесах&raquo;. А ще ж і в кожній церкві ангела-хранителя поставив Христос, що і відкрив Іоанну, кажучи: &laquo;Скажи ангелу, який у церкві Ізмуренській: &laquo;Бачив твою бідність і скорботу, але ти багатий&raquo;. Бо про добро знають ангели, що люблять нас, що за нас перед Владикою моляться: бо вони - &laquo;службові духи&raquo;, про що і апостол говорить: &laquo;На служіння послані тим, хто хоче заслужити спасіння&raquo;, їм вони і оборонці, і помічники, як то і нині чув про Данила, як він вводить архангела Михаїла до персів, у годину гніву [Божого] ради нашої свободи. Бо той примушував людей працювати на персів, як уже розказано було, а цей намагався полонених звільнити. І перемагає Михаїл ворога, бо коли жиди перейшли Єфрат, від нього знову осідлість прийняли і городи і церкви побудували.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Такоже и великий Епифаний в<small>Ђ</small>ща: коемуждо языку ангелъ приставленъ; и списанье бо къ Данилу глагола: ангелъ [властеля Персомъ] и властеля Елиномъ и Михаила властеля Июд<small>Ђ</small>емъ; глаголеть же: и постави уставы по числу ангелъ. И се пакы якоже Иполитъ глаголеть, толкуеть Данила: в л<small>Ђ</small>то третьее Кура царя, азъ Данилъ плакахъся три нед<small>Ђ</small>ли, перваго же м<small>Ђ</small>сяца смирихся, моля Бога дний 20 и І, прося отъ него откровенья тайны; и услышавъ отець пусти слово твое, кажа хотящее быти имъ; и бысть на велиц<small>Ђ</small> л<small>Ђ</small>по бяшеть ту ся явити, ид<small>Ђ</small>же хотяше и гр<small>Ђ</small>хи отпущати; и возведъ очи свои, вид<small>Ђ</small>хъ, и се мужь од<small>Ђ</small>нъ в багоръ. Первый рече вид<small>Ђ</small>ньемь, аки Гаврилъ ангелъ летя; сд<small>Ђ</small> же не тако, но видъ самого Господа, видъ же несвершена челов<small>Ђ</small>ка, но образомъ челов<small>Ђ</small>комъ являющеся, якоже глаголеть: &laquo;и се мужь од<small>Ђ</small>нъ въ пъстро&raquo;, и лядвия его припоясани златомъ чистомъ, и т<small>Ђ</small>ло его аки Оарсисъ, и лице ему аки молнья, и очи ему яко св<small>Ђ</small>щи огнен<small>Ђ</small>и, и мышци ему [и] плещи подобни м<small>Ђ</small>ди чист<small>Ђ</small>, и гласъ его аки народа многа&raquo;. И падохъ на земли, и се я мя аки рука речи челов<small>Ђ</small>ку, и еще въстави мя на кол<small>Ђ</small>ну, и рече ко мн<small>Ђ</small>: не бойся, Даниле! в<small>Ђ</small>си, что ради приидохъ к тоб<small>Ђ</small>? брань хочю створити съ княземь Перьскымъ; Но пов<small>Ђ</small>даю ти псанье в писаньи истинномъ, и н<small>Ђ</small>сть никогоже прящася о семь со мною, разв<small>Ђ</small> Михаила князя вашего. Того бо оставихъ ту: отъ него же бо дне устремися молити предъ Богомъ твоимъ, услыша молитву твою, и лущень есмь азъ брань створити со княземъ Перескымъ; св<small>Ђ</small>тъ н<small>Ђ</small>который бысть не отпусти люди, да скоро убо будеть молитва твоя свершена, противихся ему и оставихъ ту Михаила князя вашего. Кто есть Михаилъ, разв<small>Ђ</small> аньгела пр<small>Ђ</small>данаго людемь, яко и к Моисиеви глаголеть: &laquo;не имамъ с вами ити на путь, занеже суть людье жестокою выею&raquo;; но ангелъ мой идеть с вами? Якоже и се, с Божьею помощью, молитвами святыя Богородица и святыхъ ангелъ, възвратишася Русьстии князи въсвояси, съ славою великою, къ своімъ людемъ; и ко всимъ странамъ далнимъ, рекуще къ Грекомъ, и Угромъ, и Ляхомъ, и Чехомъ, дондеже и до Рима проиде, на славу Богу, всегда и ныня и присно во в<small>Ђ</small>ки, аминь.</p>\r\n<p>Того же л<small>Ђ</small>та преставися княгыни Всеволожая, м<small>Ђ</small>сяца октября въ 7 день, и положена бысть у святаго Андр<small>Ђ</small>я в манастыри.</p>\r\n<p>Того же л<small>Ђ</small>та преставися Іоанъ, епископъ Черниговьский, м<small>Ђ</small>сяца ноября въ 23.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Так само і великий Єпіфаній віщав: &laquo;Кожному народові ангела приставлено&raquo;. І в писанії до Данила говорить: &laquo;Ангел [володаря персам] і володаря еллінам назначив і Михаїла - володаря іудеям. Бо ж говорять: &laquo;І поставив устави за числом ангелів&raquo;. І от знову ж Іполит говорить, тлумачачи Данила: &laquo;На третє літо царя Кіра я, Данило, плакав три тижні, на кінець першого ж місяця вгамувався, молячи Бога днів 21, прохаючи його відкрити тайну. І почув Отець мовлене тобою слово, сказав, що судилося їм і що збулося на великій ріці, і що добре було б являтися Богові там, де хотів гріхи відпустити. І возвів очі свої і побачив мужа, одягнутого в багряницю. Перший, хто побачив би, скаже, що то Гавриїл-ангел летить. Тут же не так, але вид самого Господа, вид же не справжньої людини, але образ людський з\'явлений, як сказано: &laquo;І от муж, одягнений в усе строкате, і бедра його перев\'язані золотом чистим, і тіло його - як топаз, і лице у нього - ніби блискавка, і очі в нього - як свічі огненнії, і м\'язи в нього і плечі подібні міді чистій, і голос у нього - здається, говорить безліч людей&raquo;. І упав я на землю, і от взяла мене немовби рука людська, і поставила мене на коліна, і говорить до мене: &laquo;Не бійся, Даниле! Знаєш, чого я прийшов до тебе? Брань хочу створити з князем перським. Але повідаю тобі, що написано в Писанії істинному, і нема нікого, хто б спорив зі мною про це, окрім Михаїла, князя вашого. Його ж бо залишив тут, і від того дня, як кинувся я молитися Богові твоєму, почув він молитву твою, і відпущений я брань створити з князем перським. Дехто радив не відпускати людей, хай якнайшвидше буде здійснена молитва твоя, противився йому, і залишив я тут Михайла, князя вашого&raquo;. Хто такий Михаїл, як не ангел приданий людям? Про це й до Мойсея [Бог] промовляє: &laquo;Не піду з вами в дорогу, оскільки люди з твердою шиєю, але ангел мій піде з вами&raquo;. Так от і зараз із божою поміччю, молитвами святої Богородиці і святих ангелів, повернулися Руськії князі додому із славою великою, що рознеслася серед своїх людей і серед далеких країн, промовляючи до Греків, і Угрів, і Ляхів, і Чехів, навіть і до Риму дійшла, на славу Богу, завжди, і нині, і во віки віків, амінь.</p>\r\n<p>Того ж літа померла княгиня Всеволодова місяця жовтня в сьомий день, і поклали її біля [церкви] святого Андрія в монастирі.</p>\r\n<p>Того ж літа помер Іоанн, єпископ Чернігівський, місяця листопада в 23-й [день].</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6620, индикта. Ярославъ ходи на Ятвяз<small>Ђ</small>, сынъ Святополчь; и поб<small>Ђ</small>ди я; и пришедъ с войны, посла Новугороду и поя Мьстиславлю дщерь соб<small>Ђ</small> женъ, Володимерю внуку, м<small>Ђ</small>сяца маия въ 12, а приведена бысть іюня въ 29.</p>\r\n<p>Того же л<small>Ђ</small>та, ведоша Володимерьну Офимью въ Угры за короля.</p>\r\n<p>Того же л<small>Ђ</small>та преставися Давыдъ Игоревичь, м<small>Ђ</small>сяца маия въ 25, и положено бысть т<small>Ђ</small>ло его, въ 29, въ церькви святыя Богородица Влах<small>Ђ</small>рн<small>Ђ</small> на Клов<small>Ђ</small>.</p>\r\n<p>Томъ же л<small>Ђ</small>т<small>Ђ</small> преставися Янка, дщи Всеволожа, сестра Володим<small>Ђ</small>ра, м<small>Ђ</small>сяца ноября въ 3 день, положена бысть у церкви святаго Андр<small>Ђ</small>я, юже б<small>Ђ</small> создалъ отець ея; ту бо ся б<small>Ђ</small> и постьригла у церкви тоя, д<small>Ђ</small>вою сущи.</p>\r\n<p>Исходящю же сему л<small>Ђ</small>ту, и поставиша Феоктіста епископомъ Черн<small>Ђ</small>гову, игумена Печерьскаго, м<small>Ђ</small>сяца генваря въ 12 день, а посажень на стол<small>Ђ</small> въ 19; и радъ б<small>Ђ</small> князь Давыдъ и княгини, 6<small>Ђ</small> бо <small>Ђ</small>й отець духовный, и бояре и вси людье радовахуся, б<small>Ђ</small> бо пред німъ епископъ боленъ и не моги служити и лежа в бол<small>Ђ</small>сти л<small>Ђ</small>тъ 25, т<small>Ђ</small>мьже князь и людье жадаху епискупл<small>Ђ</small> служб<small>Ђ</small>, и радовахуся славяще Бога. Сему же тако бывьшю и братьи сущи безъ игумена, совокупившимся братьи вс<small>Ђ</small>й, и нарекоша у соб<small>Ђ</small> игумена Прохора попина, и възв<small>Ђ</small>стиша митрополиту и князю Святополку о немь, и повел<small>Ђ</small> князь митрополиту поставити с радостію, и поставленъ бысть нед<small>Ђ</small>л<small>Ђ</small> маслено<small>Ђ</small> в четвертъ, м<small>Ђ</small>сяца февраля въ 9 день; и тако внидоша в постъ братья и со игуменомъ.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6620 [1112], індикта [5] Ярослав, син Святополків, ходив на ятвягів і переміг їх. І, коли повернувся з війни, послав місяця травня в 12-й день у Новгород і взяв дочку Мстиславову, Володимирову внуку, собі за жінку, а була приведена [в Київ] червня в 29-й [день].</p>\r\n<p>Того ж літа видали Володимирову Офимію в Угри за короля.</p>\r\n<p>Того ж літа помер Давид Ігоревич місяця травня в 25-й [день]. І поклали тіло його в 29-й [день] у церкві святої Богородиці Влахірни на Клові.</p>\r\n<p>Того ж літа померла Янка, дочка Всеволодова, сестра Володимира [Мономаха], місяця листопада в 3-й день. Похоронили її в церкві святого Андрія, яку збудував її батько, там вона була і постриглася в черниці у церкві тій. будучи дівою.</p>\r\n<p>На початку ж цього ж літа поставили Феоктиста, ігумена Печерського, єпископом Чернігівським місяця січня в 12-й день, а посадили на [єпископському] столі 19-го. І радий був князь Давид і княгиня, бо був він її духовним отцем. І бояри, і всі люди раділи, бо був перед ним єпископ хворий, і не міг відправляти службу божу, і лежав хворий 25 літ, а тому князь і люди жадали єпископської служби і тепер раділи, славлячи Бога.</p>\r\n<p>Так само було і з братією, що жила без ігумена, і зібралася тоді вся братія, і вибрала собі за ігумена Прохора попа, і повідомили про це митрополита і князя Святополка. І звелів князь митрополитові поставити його з радістю. І поставили його ігуменом на масляному тижні в четвер, місяця лютого в 9-й день. І так вступила братія в піст із ігуменом.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6621. Бысть знаменье в солнци въ I часъ дне, бысть видити вс<small>Ђ</small>мъ людемъ, остася солнца мало аки м<small>Ђ</small>сяца доловъ рогома, м<small>Ђ</small>сяца марта въ 19 день, а луны въ 9. Се же бывають знаменья не на добро, бывають знаменья въ солнци и в лун<small>Ђ</small> или зв<small>Ђ</small>здами не по всей земл<small>Ђ</small>, но в которой любо земл<small>Ђ</small> аще будеть знаменье, то та земля и видить, а ина земля не видить: тако се древле, во дни Антиоховы, быша знаменья въ Ерусалим<small>Ђ</small>, ключися являтися на въздуси на конихъ рыщуще во оружьи, и оружьемь двизанье; то се бяше въ Иерусалим<small>Ђ</small> токмо, а по инымъ землямъ не бяше сего. Якожь бысть знаменье въ солнц<small>Ђ</small>, проявляше Святополчю смерть; посемь бо присп<small>Ђ</small> праздникъ Пасхы, и празьноваша, и по празниц<small>Ђ</small> разболися князь; а преставися благов<small>Ђ</small>рный князь Михаилъ, зовемый Святополкъ, м<small>Ђ</small>сяца априля въ 16 день, за Вышегородомь, и привезоша и в лодьи Киеву, и спрятавше т<small>Ђ</small>ло его и възложиша на сан<small>Ђ</small>, и плакашеся по немь бояре и дружина его вся, п<small>Ђ</small>вше над нимь обычныя п<small>Ђ</small>сни, и положиша въ церкви святаго Михаила, юже б<small>Ђ</small>.самъ создалъ; княгини же его много раздили богатьство монастыремъ и попомъ и убогымъ, яко дивитися вс<small>Ђ</small>мъ челов<small>Ђ</small>комъ, яко такоя милости никтоже можеть створити. Наутрия же, въ семы на 10 день, св<small>Ђ</small>тъ створиша Кияне, послаша к Володимеру, глаголюще: &laquo;поиди, княже, на столъ отенъ и д<small>Ђ</small>денъ&raquo;. Се слышавъ Володимеръ, плакася велми, и не поиде жаляси по брат<small>Ђ</small>. Кияни же разъграбиша дворъ Путятинъ тысячьского, идоша на Жиды и разъграбиша я; и послашася паки Кияне к Володимеру, глаголюще: &laquo;поиди, княже, Киеву; аще ли не поидеши, то в<small>Ђ</small>си, яко много зло уздвигнеться, то ти не Путятинъ дворъ, ни соцькихъ, но и Жиды грабити, и паки ти поидуть на ятровь твою и на бояры и на манастыр<small>Ђ</small>, и будеши отв<small>Ђ</small>тъ им<small>Ђ</small>лъ, княже, оже ти манастыр<small>Ђ</small> разъграбять&raquo;. Се же слышавъ Володимеръ, поиде в Киевъ.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6621 [1113]. Було знамення на сонці о 1-й годині дня. Бачили всі люди: сонця залишилося дуже мало, і було як місяць вниз рогами, місяця березня в 19-й день, а Місяця в 9-й. Такі знамення бувають не на добро, бувають знамення на сонці і місяці чи на зірках не по всій землі, але коли в якій-небудь землі і буде знамення, то та земля і бачить, а інша земля не бачить. Так от і в давнину, в дні Антіохові, було знамення в Єрусалимі: почали там з\'являтися в повітрі кінні вершники озброєні і мерехтіння зброї. То це було тільки в Ієрусалимі, а в інших землях не було цього. Як сталося знамення на сонці, то воно предвіщало Святополкову смерть. Після цього знамення надійшло свято Паски, і відсвяткували його, а після свята розхворівся князь. А помер благовірний князь Михайло, якого звали Святополком, місяця квітня в 16-й день за Вишгородом. І привезли його в лодії у Київ, і прибравши тіло його, і возложили на сани. І оплакували його бояри і дружина його вся, співали над ним обрядові пісні, і похоронили його у церкві святого Михайла, яку сам і збудував. Княгиня ж його великі багатства роздала монастирям, і попам, і вбогим, що дивувалися всі люди, бо такої щедрої милості ніхто не може зробить.</p>\r\n<p>Ранком же в 17-день кияни зібрали раду і послали до Володимира [Мономаха] з проханням: &laquo;Іди, князю, на стіл батьківський і дідівський&raquo;. Почувши це, Володимир дуже плакав і не пішов [у Київ], горюючи за братом. Кияни ж розграбували двір Путяти, тисяцького, напали на жидів і розграбили їх. І послали кияни знову до Володимира, вмоляючи: &laquo;Іди, княже, в Київ. А коли не прийдеш, то знай, що багато зла станеться, і не тільки Путятин двір, чи соцьких, чи жидів пограбують, а ще й на ятрівку твою нападуть, і на бояр, і на монастирі, і будеш ти відповідати, княже, якщо розграбують і монастирі&raquo;. Почув це Володимир і пішов у Київ.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&nbsp;</p>\r\n<p><em>Начало княженья Володим<small>Ђ</small>ря, сына Всеволожа.</em></p>\r\n<p>&nbsp;</p>\r\n<p>Володимеръ Мономахъ с<small>Ђ</small>де Киев<small>Ђ</small>, в нед<small>Ђ</small>лю, уср<small>Ђ</small>тоша же и митрополитъ Никифоръ съ епископы и со всими Кияне, с честью великою, с<small>Ђ</small>д<small>Ђ</small> на стол<small>Ђ</small> отца своего и д<small>Ђ</small>дъ своихъ, и вси людье ради быша, и мятежь влеже. Слышавъ же Половц<small>Ђ</small> смерть Святополчю, и съвокупившеся, и придоша къ Выры; Володимеръ же, совокупивъ сыны свои и сыновц<small>Ђ</small>, иде къ Выру и совокупися съ Олгомъ, Половц<small>Ђ</small> же б<small>Ђ</small>жаша.</p>\r\n<p>Того же л<small>Ђ</small>та посади сына своего Святослава в Переяславл<small>Ђ</small>, а Вячьслава у Смоленьск<small>Ђ</small>.</p>\r\n<p>В се же л<small>Ђ</small>то преставися игуменья Лазорева манастыря, свята житьемь, м<small>Ђ</small>сяца семтября въ 4 надесятъ день, живши л<small>Ђ</small>тъ шестьдесятъ в чернечеств<small>Ђ</small>, а отъ роженья девяносто л<small>Ђ</small>тъ и два.</p>\r\n<p>В се же л<small>Ђ</small>то поя Володимеръ за сына своего Романа Володаревну, м<small>Ђ</small>сяца семтября въ І надесятъ день.</p>\r\n<p>В се же л<small>Ђ</small>то Мьстиславъ заложи церковь камяну святаго Николы, на княж<small>Ђ</small> двор<small>Ђ</small>, у Торговища Нов<small>Ђ</small>город<small>Ђ</small>.</p>\r\n<p>Того же л<small>Ђ</small>та посади сына своего Ярополка в Переяславл<small>Ђ</small>.</p>\r\n<p>Томъ же л<small>Ђ</small>т<small>Ђ</small> поставиша епископа Данила Гургеву, а Б<small>Ђ</small>логороду Никиту.</p>\r\n<p>Въ л<small>Ђ</small>то 6622. Преставися Святославъ, сынъ Володимеръ, м<small>Ђ</small>сяца марта 16 день, и положенъ бысть во Переяславл<small>Ђ</small> у церкви святаго Михаила; ту бо отець ему далъ столъ выведы и из Смоленьска.</p>\r\n<p>В се же л<small>Ђ</small>то Мьстиславъ заложи Новъгородъ болий перваго.</p>\r\n<p>В се же л<small>Ђ</small>то заложена бысть Ладога камениемъ на присп<small>Ђ</small>, Павломъ посадникомъ, при княз<small>Ђ</small> Мьстислав<small>Ђ</small>. Пришедшю ми в Ладогу, пов<small>Ђ</small>даша ми Ладожане, сд<small>Ђ</small> яко есть егда будеть туча велика, и находять д<small>Ђ</small>ти наши глазкы стекляныи, и малыи и великии, провертаны, а другыя подл<small>Ђ</small> Волховъ беруть, еже выполоскываеть вода, отъ нихъже взяхъ боле ста; суть же различни. Сему же ми ся дивлящю, рекоша ми: се не дивно; и суть и еще мужи старии ходили за Югру и за Самоядь, яко видивше сами на полунощныхъ странахъ, спаде туча, и в той тучи спаде в<small>Ђ</small>верица млада, акы топерво рожена, и възрастъши и расходится по земли, и пакы бываеть друга туча, и спадаютъ оленци мали в н<small>Ђ</small>й, и възрастають и расходятся по земли. Сему же ми есть послухъ посадникъ Павелъ Ладожкый и вси Ладожане. Аще ли кто сему в<small>Ђ</small>ры не иметь, да почтеть фронографа; въ царство Право, дожгьцю бывшю и тучи велици, и пшеница с водою многою см<small>Ђ</small>шена спаде, юже събравше насыпаша сус<small>Ђ</small>кы велия. Такоже при Аврильян<small>Ђ</small> крохти сребреныя спадоша, а въ Африк<small>Ђ</small>и трие камени спадоша превелици. И бысть по потоп<small>Ђ</small> и по разд<small>Ђ</small>леньи языкъ, поча царьствовати п<small>Ђ</small>рвое Местромъ отъ рода Хамова, по немь Еремия, по немь Феоста, иже и Саварога нарекоша Егуптяне. Царствующю сему Феост<small>Ђ</small> въ Егупт<small>Ђ</small>, въ время царства его, спадоша кл<small>Ђ</small>щ<small>Ђ</small> съ небес<small>Ђ</small>, нача ковати оружье, пр<small>Ђ</small>же бо того палицами и камениемь бьяхуся. Тъ же Феоста законъ устави женамъ за единъ мужь посагати и ходити говеющи, а иже прелюбы д<small>Ђ</small>ющи казнити повел<small>Ђ</small>ваше, сего ради прозваше и богъ Сварогъ, преже бо сего жены блудяху, к нему же хотяше, и бяху акы скотъ блудяще; аще родяшеть д<small>Ђ</small>тищь, который <small>Ђ</small>й любъ бываше дашеть; се твое д<small>Ђ</small>тя, онъ же створяше празнество приимаше. Феость же сий законъ расыпа, и въстави единому мужю едину жену им<small>Ђ</small>ти, и жен<small>Ђ</small> за одинъ мужь посагати, аще ли кто переступить, да ввергуть и в пещь огнену. Сего ради и прозваша и Сварогомъ и блажиша и Егуптяне. И по семъ царствова сынъ его, именемъ Солнце, егоже наричють Дажьбогъ, семъ тысящь и 400 и сеъдесять дни, и яко быти л<small>Ђ</small>тома двемадесятьмати по лун<small>Ђ</small>, видяху бо Егуптяне инии чисти, ови по лун<small>Ђ</small> чтяху, а друзии... деньми л<small>Ђ</small>ть чтяху, двою бо надесять м<small>Ђ</small>сяцю число потомъ ув<small>Ђ</small>даша, отнележе начаша челов<small>Ђ</small>ци дань давати царямъ. Солнце царь, сынъ Свароговъ, еже есть Дажьбогъ, б<small>Ђ</small> бо мужь силенъ, слышавше н<small>Ђ</small> отъ кого жену н<small>Ђ</small>кую отъ Егуптянинъ богату и всажену сущю, и н<small>Ђ</small>коему въсхот<small>Ђ</small>вшю блудити с нею, искаше ся яти ю хотя, и не хотя отца своего закона расыпати Сварожа, поемъ со собою мужь н<small>Ђ</small>колко своихъ, разум<small>Ђ</small>въ годину, егда прелюбы д<small>Ђ</small>еть нощью припаде на ню, не удоси мужа с нею, а ону обр<small>Ђ</small>те лежащю съ ин<small>Ђ</small>мъ, с нимъже хотяше, емъ же ю и мучи и пусти ю водити по земли у коризн<small>Ђ</small>, а того любод<small>Ђ</small>йца ус<small>Ђ</small>кну; и бысть чисто житье по всей земли Егупетьской и хвалити начаша. Но мы не предложимъ слова, но рц<small>Ђ</small>мъ съ Давыдомъ: вся елико въсхот<small>Ђ</small> и створи Господь на небеси и на земли, в мори въ всихъ безнахъ, възводяй облакы отъ посл<small>Ђ</small>днихъ земли. Се бо и бысть посл<small>Ђ</small>дняя земля, о нейже сказахомъ первое.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&nbsp;</p>\r\n<p><em>Початок княжіння Володимира, сина Всеволода.</em></p>\r\n<p>&nbsp;</p>\r\n<p>Володимир Мономах сів у Києві в неділю. Зустрічав його митрополит Никифор із єпископами і зі всіма киянами із великими почестями. Сів на столі отця свого і дідів своїх, і всі люди були раді, і смута вляглася. Почули ж половці про смерть Святополка, і зібралися, і прийшли до Вири. Володимир же зібрав синів своїх і синовців, і пішов до Вири, і з\'єднався з Олегом. Половці ж втекли.</p>\r\n<p>Того ж літа посадив [Володимир Мономах] сина свого Святослава в Переяславі, а Вячеслава - у Смоленську.</p>\r\n<p>У це ж літо померла ігуменя Лазаревого монастиря, свята життям, місяця вересня в 14-й день, проживши шістдесят років у чернецтві, а від народження - дев\'яносто два.</p>\r\n<p>У це ж літо взяв Володимир за сина свого Романа Володарівну, місяця вересня в 11-й день.</p>\r\n<p>У це ж літо Мстислав заклав церкву кам\'яну святого Миколи на княжому дворі біля Торговища в Новігороді.</p>\r\n<p>Того ж літа посадив сина свого Ярополка в Переяславі.</p>\r\n<p>Того ж літа поставили єпископа Данила - Гургеву, а в Білогороду - Никиту.</p>\r\n<p>В літо 6622 [1114]. Помер Святослав, син Володимирів, місяця березня в 16-й день, і поклали [його] в Переяславі в церкві святого Михайла, бо там батько дав йому стіл, вивівши його із Смоленська.</p>\r\n<p>У це ж літо Мстислав заклав Новгород більший першого.</p>\r\n<p>У це ж літо була закладена Ладога камінням на призьбі Павлом-посадником при князі Мстиславі. Коли я прийшов у Ладогу, мені розповіли ладожани, що &laquo;тут, коли буває хмара велика, знаходять наші діти очка склянії, і малі, і великі, прокручені. А другі біля Волхова збирають, коли виполоскує вода. Від них узяв більше ста, усі різні. Коли ж я дивувався з цього, мені сказали: &laquo;Це не дивно. Є ще старі мужі, які ходили за Югру і за Самоядь і самі бачили в північних краях: спаде хмара, а з тої хмари спадають вивірки молоді, наче щойно народилися, і виростають, і розходяться по Землі. І знову ж, буває друга хмара, і з неї спадають маленькі оленята - і виростають, і розходяться по землі&raquo;. Цьому ж у мене є оповідач - посадник Павло Ладожський і всі ладожани. А коли хто не йме віри цьому, то нехай прочитає хронограф: &laquo;За царювання Право під час дощу із великої хмари спадала пшениця із рясною водою змішана, яку, зібравши, зсипали в великі сусіки. Також при Авреліані крихти срібні спадали, а в Африкії три камені превеликі упали&raquo;. А після потопу і розділення народів почав царювати найперше Местром із роду Хамового, після нього Єремія, після нього - Феоста, якого єгиптяни називають Саварогом. &laquo;За царя Феоста в Єгипті, у врем\'я царювання його, упали кліщі з небес - і почали [єгиптяни] кувати оружжя, а до того билися палицями і камінням. Той же Феоста закон встановив для жінок, щоб виходили заміж за одного чоловіка і говіти ходили, а тих, хто перелюби чинить, тих карати велів. Через це прозивали його бог Сварог&raquo;. &laquo;Раніше-бо до цього жінки блудили, йому віддатись хотіли, і жили у блуді, як скот. А коли жінка народжувала дитину, то віддавала тому, хто їй любий був: &laquo;Це твоє дитя&raquo;. Він же, приймаючи, влаштовував святкування. Феост же цей звичай знищив і постановив одному чоловікові одну жінку мати, і жінці на одного чоловіка посягати, а коли хто порушить цей закон, то нехай його ввергнуть у піч огненну&raquo;. &laquo;Через це і прозвали його Сварогом і шанували його єгуптяни. І після нього царював син його, на ймення Сонце, якого називають Дажбог. 7470 днів, що складає літ двадцять за місячним календарем, бо, як видно, єгуптяни по-іншому не лічили: одні за місяцем лічили, а другі .[...] днями літа лічили; число дванадцять місяців потім узнали, відколи люди почали данину давати царям. Сонце цар, син Сварогів, тобто Дажбог, був мужем сильним. Почув від когось про якусь єгуптянку, багату і знатну, і якогось чоловіка, що забажав ввійти в блуд із нею, шукав її, бажаючи схопити її, і, не бажаючи батька свого, Сварога, закон порушити, узяв із собою декілька мужів своїх, знаючи годину, коли вона перелюб чинить, вночі напав на неї, коли мужа не було вдома, і застав її у ліжку з іншим, якого вона хотіла. Схопив же її, і мучив її, і пустив її ходити по землі в ганьбі, а того любодійця розсік. І настало непорочне життя по всій землі Єгупетській, і почали восхваляти [його]&raquo;. Але ми не будемо продовжувать оповідь, а скажемо разом з Давидом: &laquo;Все, що забажав, і створив Господь на небесах і на землі, у морі і в усіх безоднях, підіймаючи хмари від кінців землі&raquo;. Це ж бо й була кінцева земля, про неї розповіли ми напочатку.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6623, индикта 8, съвъкупишася братья Русции князи, Володимеръ зовемый Монамахъ, сынъ Всеволожь, и Давыдъ Святославлиць, и Олегъ братъ его, и сдумаша перенести мощи Бориса и Гл<small>Ђ</small>ба: бяху бо создали церковь има камяну, на похвалу и честь телесема ею и на положение. Первое же освятиша церковь камяную, мая въ 1 день, в суботу; наутрия же въ 2 день перенесоша святая. И бысть сборъ великъ, сшедшюся народу съ всихъ странъ, митрополитъ Микифоръ съ всими епископы, съ Фектистомъ Черниговскымъ, с Лазаремъ Переяславьскымъ, епископомъ Никитою Б<small>Ђ</small>логородьскымъ и с Даниломъ Гурьговьскымъ, и съ игумены, с Прохоромъ Печерьскымъ, и съ Селивестромъ святаго Михаила, и Сава святаго Спаса, и Григорий святаго Андр<small>Ђ</small>я, Петръ Кловьскый, и прочии игумени, и освятиша церковь каменую; и отп<small>Ђ</small>вшимъ имъ об<small>Ђ</small>дьнюю, об<small>Ђ</small>даша у Ольга и пиша, и бысть учрежение велико, и накррмиша убогыя и странъныя по 3 дни. И яко бысть утро, митрополитъ, епископи, игумени оболокошася у святительскыя ризы и св<small>Ђ</small>ща въжгегъ, с кад<small>Ђ</small>лы благовоньными, и придоша к ракама святою, и взяша раку Борисову, и въставиша и на возила, и поволокоша ужи князи и бояре, черньцемъ упр<small>Ђ</small>дъ идущимъ съ св<small>Ђ</small>щами, попомъ по нимъ идущимъ, таже игумени, таже епископи предъ ракою, а княземъ за ракою идущимъ, межи воромъ; и не б<small>Ђ</small> лз<small>Ђ</small> вести отъ множества народа, поламляху воръ, а инии и покрили бяху градъ и забрала, яко страшно бяше видити народа множество; и повел<small>Ђ</small> Володимеръ р<small>Ђ</small>жючи паволокы, орници, б<small>Ђ</small>ль, розмета народу, овъ же сребреникы метати людемъ силно налегшимъ, а быша легко внесли въ церковь, едва возмогоша [вьвезти въ церковь] и поставити раку сред<small>Ђ</small> церкви, и идоша по Гл<small>Ђ</small>ба. Т<small>Ђ</small>мже образомъ и сего привезоша и поставиша у брата. Распри же бывши межи Володимеромъ и Давыдомъ и Ольгомъ, Володимеру бо хотящю я поставити сред<small>Ђ</small> церкви и теремъ серебренъ поставити надъ нима, а Давыдъ и Олегъ хотяшета поставити я в комару, &laquo;идеже отець мой, [рече], назнаменалъ&raquo; на правой сторон<small>Ђ</small>, идеже бяста устроен<small>Ђ</small> комар<small>Ђ</small> има. И рече митрополитъ и епископи: &laquo;верзите жребии, да кд<small>Ђ</small> изволита мученика, ту же я поставимъ&raquo;, и вгодно се бысть. И положи Володимеръ свой жребии, а Давыдъ и Олегъ свой жребии на святой трапез<small>Ђ</small>, и вынеся жребии Давыдовъ и Олговъ, и поставиша я в комару ту на десн<small>Ђ</small>й стран<small>Ђ</small>, кде ныне лежита. Принесена же бысть святая мученика, маия въ вторый день, из деревяной церкви в каменую Вышегород<small>Ђ</small>. Иже еста похвала княземъ нашимъ и заступника земли Русц<small>Ђ</small>й, иже славу св<small>Ђ</small>та сего попраста, а Христа узлюбиста, по стопамъ его изволиста шествовати, овчате Христов<small>Ђ</small> добрии, яже влекома на заколение не противистася, ни отб<small>Ђ</small>жаста нужныя смерти; т<small>Ђ</small>же и съ Христомъ въцаристася у в<small>Ђ</small>чную радость, и даръ иц<small>Ђ</small>ленія приемша отъ Спаса нашего Ісуса Христа неоскудно подаваста недужнымъ, с в<small>Ђ</small>рою приходящимъ въ святый храмъ ею, поборника отечьству своему. Князи же, и бояре, и вси людие, празноваша по три дни и похвалиша Бога и святою мученику, и тако раздидошася кождо въсвояси. Володимеръ же окова рац<small>Ђ</small> сребромъ и златомъ, и украси гроба ею, такоже и комар<small>Ђ</small> покова сребромъ и златомъ; имже покланяются людие, просяще прощения гр<small>Ђ</small>хомъ.</p>\r\n<p>Въ се же л<small>Ђ</small>то бысть знамение: погибе солнце и бысть яко м<small>Ђ</small>сяць, егоже глаголють нев<small>Ђ</small>гласи: сн<small>Ђ</small>даемо солнце.</p>\r\n<p>В се же л<small>Ђ</small>то преставися Олетъ Святославличь месяца августа въ 1 день, а во вторый погребенъ бысть у святаго Спаса, у гроба отца своего Святослава.</p>\r\n<p>Того же л<small>Ђ</small>та устрои мостъ чересъ Дн<small>Ђ</small>пръ Володимира</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6623 [1115], індикта 8, зібралися брати, князі руські, Володимир, званий Мономахом, син Всеволодів, і Давид Святославич, і Олег, брат його, і надумали перенести мощі Бориса і Гліба, бо спорудили для них церкву кам\'яну на похвалу і честь і на покладення їхніх тіл. Спершу ж освятили церкву кам\'яну в перший день травня, в суботу; ранком же другого дня перенесли святих. І було зібрання велике народу, зійшлися люди з усіх кінців - митрополит Никифор з усіма єпископами: з Феоктистом Чернігівським, з Лазарем Переяславським, єпископом Никитою Білогородським і з Данилом Гургівським, і з ігуменами: з Прохором Печерським, і з Селівестром святого Михайла, і Сава святого Спаса, і Григорій святого Андрія, Петро Кловський і інші ігумени. І освятили церкву кам\'яну. І коли відспівали вони обідню, всі обідали і пили в Олега, і було велике частування, і годували вбогих і подорожніх протягом трьох днів. А коли настав ранок [2-го травня], митрополит, єпископи, ігумени облачились у святительські ризи, запалили свічки і кадила благовонні, і прийшли до рак святих, і взяли раку Борисову, і поставили її на возила, князі і бояри взялися за гужі і поволокли. Попереду ішли ченці із свічками, за ними попи, а також ігумени, єпископи перед ракою, а князі йшли за ракою поміж воринням, і не можна було везти через силу-силенну народу, поламали вориння, а інші вкрили городські стіни і помости, аж страшно було дивитися на таку силу народу. І звелів Володимир нарізати паволоки, шерстяні матерії, шкурки білок і розкидати поміж народ, а де люди особливо сильно напирали, метали срібні монети,- і легко внесли раку Бориса в церкву, і ледве змогли поставити раку посеред церкви, і пішли за Глібом. У такий же спосіб і цього привезли і поставили біля брата. І сталася незгода між Володимиром та Давидом і Олегом. Володимир-бо хотів поставити їх посеред церкви і терем срібний звести над ними, а Давид і Олег хотіли поставити їх під склепінням, &laquo;де батько мій назначив&raquo;, з правого боку, де і були зроблені склепіння для них. І сказав митрополит і єпископи: &laquo;Кидайте жереби, і де вкажуть мученики, там і поставимо їх&raquo;. І погодилися на це. І поклав Володимир свій жереб, а Давид і Олег свій жереб на святій трапезі. І вийняли жереб Давидів і Олегів. І поставили їх у той склеп, що з правого боку, де й зараз лежать. Перенесено ж було святих мучеників із дерев\'яної церкви у кам\'яну другого травня в Вишгороді. І це - похвала князям нашим і заступникам землі Руської, бо славу світу цього зневажили, а Христа возлюбили і стопами його зволили шествувать. Овечата Христові добрії, вони не противились, коли вели їх, щоб заколоти, не рятувалися від насильницької смерті, а тому і з Христом воцарилися у вічную радість і дар зцілення прийняли від Спаса нашого Ісуса Христа, щедро подаючи це зцілення недужим, що з вірою приходять у святий храм поборників своєї вітчизни їхнього імені.</p>\r\n<p>Князі ж, і бояри, і всі люди святкували три дні, і похвалили Бога і святих мучеників, і так розійшлися по своїх домівках. Володимир же окував раки сріблом і золотом і оздобив гроби їхні, а також і склепіння окував сріблом і золотом. І поклоняються їм люди, просять відпущення гріхів.</p>\r\n<p>У це ж літо було знамення: згинуло сонце і стало як місяць, про нього невігласи кажуть: з\'їдене сонце.</p>\r\n<p>У це ж літо помер Олег Святославич місяця серпня у перший день, а на другий поховали його у [церкві] святого Спаса, біля гробу отця їхнього Святослава.</p>\r\n<p>Того ж літа Володимир збудував міст через Дніпро.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Въ л<small>Ђ</small>то 6624. Приходи Володимеръ на Гл<small>Ђ</small>ба: Гл<small>Ђ</small>бъ бо бяше воевалъ Дреговичи и Случескъ пожегъ, и не каяшеться о семъ, ни покаряшеться, но бол<small>Ђ</small> противу Володимеру глаголаше, укаряя и Володимеръ же, над<small>Ђ</small>яся на Бога и на правду, проиде къ Смоленьску, съ сынъми своими и с Давыдомъ Святославичемъ и Олговичи, и взя Вячеславъ Ръшю и Копысу, а Давыдъ съ Ярополкомъ и взя Дрьютескъ на щитъ, а Володимеръ самъ поиде къ Смоленьску; и затворися Гл<small>Ђ</small>бъ въ градь. Володимеръ же нача ставити истьбу у товара своего, противу граду. Гл<small>Ђ</small>бови же, узрившю, ужасеся сердцемъ, и нача ся молити Гл<small>Ђ</small>бъ Володимеру, шля оть себе послы. Володимеръ же съжалиси т<small>Ђ</small>мъ, оже проливашеться кровь въ дни постъныя великого поста, и вдасть ему миръ; Гл<small>Ђ</small>бъ же, вышедъ из города съ д<small>Ђ</small>тми и съ дружиною, поклонися Володимеру, и молвиша р<small>Ђ</small>чи о мир<small>Ђ</small>, и об<small>Ђ</small>щася Гл<small>Ђ</small>бъ по всему послушати Володимера. Володимеръ же умиривъ Гл<small>Ђ</small>ба и наказавъ его о всемъ, вдасть ему Менескъ, а самъ възвратися Киеву. Ярополкъ же сруби городъ Желъди Дрьючаномъ, ихже б<small>Ђ</small> полонилъ.</p>\r\n<p>В се же л<small>Ђ</small>то Мъстиславъ Володимеричь ходи на Чюдь с Новгородци и со Пьсковичи, и взя городъ ихъ именемъ Медв<small>Ђ</small>жа Глава, и погостъ бещисла взяша, и възвратишася въсвояси съ многомъ полономъ.</p>\r\n<p>В се же л<small>Ђ</small>то иде Леонъ царевичь, зять Володимерь, на куръ Олексия царя, и вдася городовъ ему Дунайскыхъ н<small>Ђ</small>колко; и в Дельстр<small>Ђ</small> город<small>Ђ</small> лестию убиста и два Сорочинина, посланая царемъ, м<small>Ђ</small>сяца августа въ 15 день.</p>\r\n<p>В се же л<small>Ђ</small>то князь великий Володимеръ посла Ивана Войтишича, и посажа посадники по Дунаю.</p>\r\n<p>В се же л<small>Ђ</small>то посла Володимеръ сына своего Ярополка, и Давыдъ сына своего Всеволода, на Донъ, и взяша три грады: Сугровъ, Шаруканъ, Балинъ. Тогда же Ярополкъ приведе соб<small>Ђ</small> жену, красну велми, Ясьскаго князя дщерь, полонивъ.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6624 [1116]. Ходив Володимир на Гліба, бо Гліб повоював дреговичів, і Случеськ спалив, і не каявся в тому, і покірності не виявляв, а ще більше говорив проти Володимира, докоряв йому. Володимир же, надіючись на Бога і на правду, пішов до Смоленська з синами своїми і з Давидом Святославичем та Олеговичами, і взяв Вячеслав Ршу і Копису, а Давид із Ярополком взяли Дрютеск на щит, а Володимир сам пішов до Смоленська. І зачинився Гліб у городі. Володимир же почав ставити істьбу в своєму обозі напроти города. Коли Гліб все це побачив, серце його охопив жах. І почав Гліб молити Володимира, посилаючи від себе послів. Володимир же пожалів, що проливається кров у дні великого посту, і дав йому мир. Гліб же вийшов із города з дітьми і з дружиною, поклонився Володимиру і домовився з ним про мир. І пообіцяв Гліб у всьому слухати Володимира. Володимир же омирив Гліба і дав йому про все настанови, дав йому Мінськ, а сам повернувся в Київ. Ярополк же поставив город Желді дрючанам, яких він узяв був у полон.</p>\r\n<p>У це ж літо Мстислав Володимирич ходив на чудь з новгородцями і псковичами, і взяв город їхній, що називається Медвежа Голова, і погостів безліч узяв, і повернувся додому з великим полоном.</p>\r\n<p>У це ж літо ходив Леон царевич, зять Володимирів, на господаря Олексія царя, і декілька городів Дунайських йому здалися. І в Дельстрі-городі його підступно вбили два сарацини, послані царем, місяця серпня в 15-й день.</p>\r\n<p>У це ж літо князь великий Володимир послав Івана Войтишича, і він посаджав посадників [у городах] по Дунаю.</p>\r\n<p>У це ж літо послав Володимир сина свого Ярополка і Давид сина свого Всеволода на Дон. І взяли [вони] три гради: Сугров, Шарукан, Балин. Тоді ж Ярополк привів собі жону, вельми красну дочку Яського князя, полонянку.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Томъ же л<small>Ђ</small>т<small>Ђ</small> и Предъславна, черница, Святославна преставися.</p>\r\n<p>Томъ же л<small>Ђ</small>т<small>Ђ</small> ходи Вячеславъ на Дунай, съ Фомою Ратиборичемъ, и пришедъ къ Дьрьстру и не въсп<small>Ђ</small>вше ничтоже, воротишась.</p>\r\n<p>В се же л<small>Ђ</small>то бишася с Половци и с Торкы и съ Печен<small>Ђ</small>гы у Дона, и с<small>Ђ</small>кошася два дни и дв<small>Ђ</small> нощи, и придоша в Русь къ Володимеру Торци и Печен<small>Ђ</small>зи.</p>\r\n<p>В се же л<small>Ђ</small>то преставися Романъ Всеславичь.</p>\r\n<p>В се же л<small>Ђ</small>то преставися Мьстиславъ, внукъ Игоревъ.</p>\r\n<p>Томъ же л<small>Ђ</small>т<small>Ђ</small> Володимерь отда дщерь свою Огафью за Всеволодка.</p>\r\n<p>В л<small>Ђ</small>то 6625. Приведе Володимеръ Мьстислава из Новагорода и дасть ему отець Б<small>Ђ</small>лъгородъ, а Нов<small>Ђ</small>город<small>Ђ</small> с<small>Ђ</small>де Мьстиславичь, сынъ его, внукъ Володимеровъ.</p>\r\n<p>В се же л<small>Ђ</small>то иде Володимеръ на Ярослава к Володимерю, и Давыдъ Ольговичь, и Володарь, и Василко, и оступиша и у город<small>Ђ</small> Володимери, и стояша дний шестьдесять, и створи миръ съ Ярославомъ, Ярославу покорившюся и вдарившю челомъ передъ стрыемъ своимъ Володимеромъ; и наказавъ его Володимеръ о всемъ, веля ему къ соб<small>Ђ</small> приходити, &laquo;когда тя позову&raquo;, и тако в мир<small>Ђ</small> разидошася кождо въсвояси. Тогда же придоша Половци къ Болгаромъ, и высла имъ князь БолъТарьскый пити съ отравою, и пивъ Аспакъ и прочии князи вси помроша.</p>\r\n<p>Семъ же л<small>Ђ</small>т<small>Ђ</small> преставися Лазоръ, епископъ Переяславьскый, семтября въ 6.</p>\r\n<p>Томъ же л<small>Ђ</small>т<small>Ђ</small> придоша Б<small>Ђ</small>лов<small>Ђ</small>жьци в Русь.</p>\r\n<p>В се ж л<small>Ђ</small>то поя Володимеръ за Андр<small>Ђ</small>я внуку Тугърртъканову.</p>\r\n<p>В се же л<small>Ђ</small>то потрясеся земля, семтября въ двадесять шестый.</p>\r\n<p>Того же л<small>Ђ</small>та выведе Гл<small>Ђ</small>ба изъ М<small>Ђ</small>ньска Володимеръ, и церковь заложи на Альт<small>Ђ</small> мученику. Володимеръ же посла сына Романа во Володимерь княжить.</p>\r\n<p>Того же л<small>Ђ</small>та умре куръ Олексий, и взя царство сынъ его Иванъ.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Того ж літа і Предславна Святославна, черниця, померла.</p>\r\n<p>Того ж літа ходив Вячеслав на Дунай із Фомою Ратиборичем, і прийшли до Дністра, і, не встигши нічого, повернулися.</p>\r\n<p>У це ж літо бився [Володимир Мономах] з половцями, і з торками, і з печенігами на Дону. І рубалися два дні і дві ночі, і прийшли на Русь до Володимира торки і печеніги.</p>\r\n<p>У це ж літо помер Роман Всеславич.</p>\r\n<p>У це ж літо помер Мстислав, внук Ігорів.</p>\r\n<p>Того ж літа Володимир віддав дочку свою Огафію за Всеволодка.</p>\r\n<p>В літо 6625 [1117]. Привів Володимир Мстислава із Новагорода і дав йому отець Білгород, а в Новігороді сів Мстиславич, син його, внук Володимирів.</p>\r\n<p>У це ж літо ходив Володимир на Ярослава у Володимир, і Давид Ольгович, і Володар, і Василько. І обступили його у городі Володимирі, і стояли днів шістдесят, і створили мир із Ярославом. Ярослав же покорився і вдарив чолом перед стриєм своїм Володимиром. Володимир дав йому про все наказ і велів приходити до нього, &laquo;коли тебе покличу&raquo;. І так мирно розійшлися по домівках.</p>\r\n<p>Тоді ж приходили половці до болгар. І вислав їм князь болгарський пити з отрутою, і, випивши, Аепак і інші князі половецькі всі померли.</p>\r\n<p>Цього ж літа помер Лазар, єпископ переяславський, вересня в 6-й [день].</p>\r\n<p>Того ж літа прийшли біловежці в Русь.</p>\r\n<p>У це ж літо взяв Володимир за Андрія внучку Тугортканову.</p>\r\n<p>У це ж літо трусилася земля вересня в 26-й [день].</p>\r\n<p>Того ж літа Володимир вивів Гліба із Мінська і церкву заклав на Альті мученикові. Володимир же послав сина Романа княжити у Володимир.</p>\r\n<p>Того ж літа помер господар Олексій і взяв царство син його Іван.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>',NULL,1856,5,1,NULL,25);
INSERT INTO `publications_publication` VALUES
(4704,'P','2008-12-20',0,NULL,'Note','Повість врем\'яних літ Роки 6604 [1096] — 6615 [1107].','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Олег обіцяв іти до брата свого Давида в Смоленськ і прийти з братом своїм до Києва і договір виробить. І не захотів Олег це зробити. Прийшов він до Смоленська, і зібрав воїв, і пішов у Муром.</span></span></p>','<p><a href=\"http://izbornyk.org.ua/pvlyar/yar10.htm\">Роки 6604 [1096] &mdash; 6615 [1107].</a></p>\r\n<p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Ольгов<small>Ђ</small> об<small>Ђ</small>щавшюся ити къ брату своему Давыдови Смоленьску, и приити с братомъ своимъ Киеву, и обрядъ положити, и не восхот<small>Ђ</small> сего Олегъ сътворити; онъ пришедъ къ Смоленьску и поемъ воя, и поиде Мурому, у Муром<small>Ђ</small> тогда сущю Изяславу; [бысть же в<small>Ђ</small>сть Изяславу], яко Олегъ идеть к Мурому, посла Изяславъ по вои Ростову, і Суждалю, и по Б<small>Ђ</small>лозерц<small>Ђ</small>, и собра вои много. И посла Олегъ послы своя къ Изяславу, глаголи: &laquo;иди у волость отца своего Ростову, а то есть волость отца моего; да хочю ту с<small>Ђ</small>дя порядъ положити съ отцемь твоимъ, се бо мя выгналъ из города отца моего; или ты ми зд<small>Ђ</small> не хощеши хл<small>Ђ</small>ба своего же вдати?&raquo; И не послуша Изяславъ словесъ сихъ, над<small>Ђ</small>яся на множество вой. Олегъ же над<small>Ђ</small>яся на правду свою, яко правъ б<small>Ђ</small> в с<small>Ђ</small>мъ, и поиде к городу с вои; Изяславъ же исполчися передъ городомъ на пол<small>Ђ</small>. Олегъ же поиде противу ему полкомь, и сняшася обо<small>Ђ</small>, и бысть брань люта: и убиша Ізяслава, сына Всеволожа, [Володимеря внука] м<small>Ђ</small>сяца септября въ 6 день; прочии же вои поб<small>Ђ</small>гоша, ови чресъ л<small>Ђ</small>съ, друзии же в городъ, [Олегъ же вниде въ градъ] и прияша и горожане; Изяслава же вземьше, и положиша в манастыри святаго Спаса, и оттуда перенесоша и Новугороду, и положиша [и] у свято<small>Ђ</small> Софьи, на л<small>Ђ</small>вой сторон<small>Ђ</small>. Олегъ же по приятьи града, изоима Ростовц<small>Ђ</small>, и Б<small>Ђ</small>лозерци, и Суждальц<small>Ђ</small>, искова, и устремися на Суждаль: и пришедъ Суждалю, и Суждальци дашася ему: Олегъ же омиривъ городъ, овы изоима, другыя расточи, им<small>Ђ</small>нье ихъ взя. И прииде к Ростову: и Ростовци вдашася ему. И перея всю землю Муромьскую и Ростовьскую и посажа посадники по городомъ, и дани поча брати. И посла к нему Мьстиславъ посолъ свой из Новагорода, глаголя: &laquo;иди опять Мурому, а в чюжей волост<small>Ђ</small> не с<small>Ђ</small>ди; и азъ пошлю молиться съ дружиною своею къ отцю моему, и смирю тя с нимь; аще и брата моего убилъ еси то есть не дивно, в ратехъ бо цари и мужи погыбають&raquo;. Олегъ же не восхот<small>Ђ</small> сего послушати, но паче мышляше и Новъгородьци прияти; и посла Олегъ брата своего Ярослава, въ сторож<small>Ђ</small>, а самъ стояше на поли у Ростова. Мьстиславъ же сдумавъ с Новгородьци, и послаша передъ собою сторож<small>Ђ</small> Добрыну Рагуиловича; Добрыня же первое изоима данникы. Ув<small>Ђ</small>да же Ярославъ се, яко изоима даньници, стояшеть бо тогда Ярославъ на Медв<small>Ђ</small>дици у сторожи, б<small>Ђ</small>жа то<small>Ђ</small> нощи, и приб<small>Ђ</small>же ко Олгови и пов<small>Ђ</small>да ему, яко идеть Мьстиславъ. Прииде же в<small>Ђ</small>сть к Олгови, яко сторожеве его изоиман<small>Ђ</small>, поиде к Ростову, Мьстиславъ же поиде на Волгу, пов<small>Ђ</small>даша ему, яко Олегъ узвратилъся есть к Ростову, а Мьстиславъ поиде по немь. Олегъ же прииде к Суждалю, и слышавъ, яко идеть по немъ Мьстиславъ, Олегъ же повел<small>Ђ</small> зажещи городъ Суждаль, токмо остася дворъ манастырескъ Печерьскаго манастыря и церкви, яже тамо есть святаго Дмитрея, юже б<small>Ђ</small> далъ Ефр<small>Ђ</small>мъ і съ селы. Олегъ же поб<small>Ђ</small>же к Мурому, а Мьстиславъ поиде к Суждалю, и с<small>Ђ</small>дя ту посылаше к Ольгови, мира прося, глаголя: &laquo;яко мни [й] азъ есмь тебе, шлися ко отцю моему, а дружину вороти, юже еси заялъ; а язъ тебе во всемъ послушаю&raquo;. Олегъ же посла к нему, хотя мира лестью; Мьстиславъ же емь в<small>Ђ</small>ры льсти, и распусти дружину по селомъ; и наста Федорова нед<small>Ђ</small>ля І поста, и присп<small>Ђ</small> Федорова субота, Мьстиславу с<small>Ђ</small>дящю на об<small>Ђ</small>д<small>Ђ</small>, и прииде ему в<small>Ђ</small>сть, яко Олегъ на Клязьм<small>Ђ</small>, близь бо б<small>Ђ</small> пришелъ без в<small>Ђ</small>сти; Мьстиславъ бо емъ ему в<small>Ђ</small>ру, не постави сторожовъ; но Богъ в<small>Ђ</small>сть избавити челов<small>Ђ</small>кы благочестивыя своя ото льсти. Олегъ же установися на Клязьм<small>Ђ</small>, мня, яко убояся его Мьстиславъ и поб<small>Ђ</small>гнеть; къ Мьстиславу собрася дружина въ тъ день и въ другий, Новгородц<small>Ђ</small>, и Ростовц<small>Ђ</small>, і Б<small>Ђ</small>лозерьци, Мьстиславъ же ста предъ городомъ, исполъчивъ дружину; не поступи ни Олегъ къ Мьстиславу, ни Мьстиславъ на Олга, и стояста противу ерб<small>Ђ</small> дний 4. И прииде Мьстиславу в<small>Ђ</small>сть, яко &laquo;послалъ ти отець Вячьслава брата с Половьци&raquo;; и прииде Вячьславъ у четвертокъ по Федоровой нед<small>Ђ</small>л<small>Ђ</small> поста. А в пятокъ завътра поиде Олегъ, исполчився, к городу, а Мьстиславъ поиде противу ему с Новгородци. И въда Мьстиславъ стягъ Володимерь Половчину, именемь Куману, удавъ ему п<small>Ђ</small>шьц<small>Ђ</small>, поставивъ и на правомъ крил<small>Ђ</small>, и напя стягъ Володимерь; и узри Олегъ стягъ Володимерь, и убояся, и ужась нападе на нь и на вои его; и поидоша к боези противу соб<small>Ђ</small>, и поиде Олегъ противу [Мьстиславу, а Ярославъ поиде противу] Вячьславу. Мьстиславъ же перешедъ пежарь с Новгородц<small>Ђ</small>, и ступишася на Колачьц<small>Ђ</small>, и бысть брань кр<small>Ђ</small>пка, и нача одоляти Мьстиславъ; и видивъ Олегъ, яко поиде стягъ Володимерь, и нача заходити в тылъ его, и убояся поб<small>Ђ</small>же Олегъ, и одол<small>Ђ</small> Мьстиславъ. Олегъ же приб<small>Ђ</small>жа Мурому, и затвори Ярослава Муром<small>Ђ</small>, и самъ иде к Рязаню; Мьстиславъ же прииде Мурому, и створи миръ с Муромьци, поя люди своя Ростовц<small>Ђ</small> же и Суждальци, и поиде к Рязаню по Ольз<small>Ђ</small>. Олегъ же выб<small>Ђ</small>же из Рязаня, а Мьстиславъ створи миръ с Рязаньци; и поя люди своя, яже б<small>Ђ</small> заточилъ Олегъ. И посла ко Олгови, глаголя: &laquo;не б<small>Ђ</small>гай никаможе, но послися ко братьи своей с молбою, не лишать тебе Русьской земл.и; а изъ послю къ отцю молит [и] ся о тоб<small>Ђ</small>&raquo;. Олегъ же об<small>Ђ</small>щася тако створити. Мьстиславъ же узворотися въспять къ Суждалю, и оттуда приде Новугороду в городъ свой, молитвами преподобнаго епископа Никиты. Се же бысть исходящю лъту 6604, индикта 4, наполы.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Олег обіцяв іти до брата свого Давида в Смоленськ і прийти з братом своїм до Києва і договір виробить. І не захотів Олег це зробити. Прийшов він до Смоленська, і зібрав воїв, і пішов у Муром. У Муромі був тоді Ізяслав. [Прийшла звістка до Ізяслава], що Олег іде на Муром. Послав Ізяслав за воями в Ростов, і Суздаль, і за білозерцями, і зібрав воїв багато. І послав Олег своїх послів до Ізяслава, кажучи: &laquo;Іди у волость батька свого - в Ростов, а це волость мого батька. І хочу тут сісти і мир заключить з батьком твоїм. Це ж бо він вигнав мене із города мого батька. Чи і ти мені тут не хочеш мого ж таки хліба дати?&raquo; І не послухав Ізяслав словес цих, надіючись на численне військо. Олег же надіявся на правду свою, що був у цьому прав, і пішов з військом на город. Ізяслав же ізполчився перед городом на полі. Олег же пішов з військом супроти нього, і зійшлися обоє, і була битва люта. І убили Ізяслава, сина Володимира, внука Всеволода, місяця вересня в шостий день, рештки ж воїв його розбіглися, одні через ліс, а другі в город. [Олег же ввійшов у город], і прийняли його горожани. Ізяслава ж взяли і поклали в монастирі святого Спаса, і звідти перенесли його в Новгород і поклали його в святій Софії, з лівого боку. Олег же після взяття города похапав ростовців, і білозерців, і суздальців - і закував. І кинувся на Суздаль. І коли прийшов до Суздалю, суздальці здалися йому. Олег же омирив город: одних похапав, других вигнав, а майно їхнє забрав. І прийшов у Ростов. І ростовці здалися йому. І зайняв він всю землю Муромську і Ростовську, і посадив посадників у городах, і дань почав брати. І послав до нього Мстислав посла свого з Новгорода, кажучи: &laquo;Іди знову в Муром, а в чужій волості не сідай. І я з дружиною своєю пошлю вмолити батька мого, і примирю тебе з ним. Якщо і брата мого ти убив, то це не дивно, бо в битвах царі і мужі гинуть&raquo;. Олег же не захотів його послухати, а ще замислив і новгородців підкорити. І послав Олег брата свого Ярослава в сторожу, а сам став на полі біля Ростова. Мстислав же порадився з новгородцями, і послали вони попереду в сторожу Добриню Рагуїловича. Добриня ж спершу переловив [Олегових] даньників. Довідався Ярослав, що похапали даньників, а стояв тоді Ярослав у сторожі на Медведиці, і тої ж ночі утік. І прибіг до Олега і повідав йому, що йде Мстислав. Прийшла звістка Олегові, що сторожа його захоплена, і він пішов у Ростов. Мстислав же пішов на Волгу, коли повідомили йому, що Олег повернувся в Ростов. І Мстислав пішов за ним. Олег же прийшов у Суздаль, і, почувши, що Мстислав іде за ним, повелів Олег запалити город Суздаль, тільки залишився двір монастирський Печерського монастиря і церква тамошня святого Дмитрія, яку Єфрем дав [монастиреві] разом із селами. Олег же побіг до Мурома, а Мстислав прийшов у Суздаль і, сидячи тут, послав до Олега просити миру, кажучи: &laquo;Я молодший від тебе, звертайся до батька мого, а дружину, яку захопив, поверни, і я тебе у всьому послухаю&raquo;. Олег же послав до нього, прикидаючись, що хоче миру. Мстислав же повірив в обман і розпустив дружину по селах. І настав Федорів тиждень першого [великого] посту, і прийшла Федорова субота. 1 коли Мстислав сидів на обіді, прийшла йому вість, що Олег на Клязьмі і підійшов так близько потаємно, бо Мстислав повірив йому і не виставив сторожу. Але Бог знає, як захистити людей благочестивих своїх від обману. Олег же зупинився на Клязьмі, думаючи, що злякається його Мстислав і втече. До Мстислава зійшлася дружина в той день і на другий: новгородці, і ростовці, і білозерці. Мстислав же став перед городом і ізполчив дружину, і не рушив ні Олег на Мстислава, ні Мстислав на Олега. І стояли супроти один одного чотири дні. І прийшла Мстиславові звістка, що &laquo;послав тобі батько [на допомогу] брата Вячеслава з половцями&raquo;. І прийшов Вячеслав у четвер після Федорової неділі посту. А на другий день у п\'ятницю рушив Олег, приготувавшись до бою, на город, а Мстислав пішов назустріч йому з новгородцями. І вручив Мстислав стяг Володимирів половчину на ймення Куману, віддавши йому піхотинців, і поставив його на правому крилі, і розгорнув стяг Володимирів. І побачив Олег стяг Володимирів, і злякався, і жах напав на нього і на воїв його. І рушили в бій один проти одного, і пішов Олег проти Мстислава, а Ярослав пішов на Вячеслава. Мстислав же перейшов пожарище з новгородцями, і зійшлися на Колачці, і була битва жорстока, і почав перемагати Мстислав. І побачив Олег, як ішов стяг Володимирів і став заходити йому в тил, і, злякавшись, побіг Олег, і здолав його Мстислав. Олег же прибіг у Муром і зачинив Ярослава в Муромі, а сам пішов у Рязань. Мстислав же прийшов до Мурома і заключив мир із муромцями, взяв людей своїх - ростовців і суздальців - і пішов на Рязань за Олегом. Олег же вибіг із Рязані, а Мстислав уклав мир із рязанцями, і взяв людей своїх,що їх був ув\'язнив Олег. ! послав до Олега, кажучи: &laquo;Не тікай нікуди, але пошли до братів своїх з благанням не позбавляти тебе Руської землі, а я пошлю до батька молитися за тебе&raquo;. Олег же обіцяв так зробити.</p>\r\n<p>Мстислав же повернувся назад у Суздаль, а звідти пішов у Новгород, у свій город, на прохання преподобного єпископа Никити. Се ж було наприкінці літа 6604 [І096], індикта 4, на половині.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6605. Приидоша Святополкъ, и Володимеръ, и Давыдъ Игоревичь, и Василко Ростиславичь, і Давыдъ Святославичь, и брать его Олегъ, и сняшася Любци на строенье мира, и глаголаше к соб<small>Ђ</small>, рекуще: &laquo;почто губимъ Рускую землю, сами на ся котору имуще? а Половци землю нашю несуть роздно, и ради суть, оже межи нами рать до нын<small>Ђ</small>; отсел<small>Ђ</small> им<small>Ђ</small>мься по едино сердце и съблюд<small>Ђ</small>мь Рускую землю, кождо держить очьчину свою: Святополку Киевъ Изяславль, Володимеръ Всеволожь, Давыдъ и Олегъ, Ярославъ Святославли; имьже раздаялъ Всеволодъ городы, Давыдози Володимеръ, Ростиславичема Перемышль Володареви, Теребовлъ и Василкови&raquo;. И на томъ ц<small>Ђ</small>ловаша хрестъ; &laquo;да аще отсел<small>Ђ</small> кто на кого вьстанеть, то на того будемь вси и честьный крестъ&raquo;; и рекоша вси: &laquo;да будеть на нь хрестъ честный и вся земля Руская&raquo;, и ц<small>Ђ</small>ловавшеся и поидоша усвояси. И прииде Святополкъ Кыеву съ Давыдомъ, и рад<small>Ђ</small> быша людье вси; токмо дьяволъ печаленъ бяше о любви с<small>Ђ</small>й, и вл<small>Ђ</small>з<small>Ђ</small> сотона у сердьце н<small>Ђ</small>которымъ мужемъ и начаша глаголати къ Давыдови Игоревичю, рекуще сице: &laquo;яко Володимеръ сложилъся есть с Василкомъ на Святополка и на тя&raquo;. Давыдъ же имъ в<small>Ђ</small>ры лживымъ словесемъ, нача молвити на Василка, глаголя сице: &laquo;кто есть убилъ брата твоего Ярополка? а нын<small>Ђ</small> мыслить на тя и на мя, и сложилъся есть с Володимеромъ; да промышляй си о своей голов<small>Ђ</small>&raquo;. Святополкъ же смятеся умомъ, реки: &laquo;и еда се право будеть, или лжа&raquo;, не вид<small>Ђ</small>; і рече Святополкъ Давыдови: &laquo;да еще право молвиши, да Богъ ти будь послухъ, аще ли завистью молвиши, да Богъ будеть за т<small>Ђ</small>мъ&raquo;. Святополкъ же съжалиси по брат<small>Ђ</small> своемь и о соб<small>Ђ</small>, нача помышляти, еда се право будеть? и я в<small>Ђ</small>ру Давыдови, и перельсти Давыдъ Святополка, і начаста думати о Василц<small>Ђ</small>; а Василко сего не в<small>Ђ</small>даше и Володимеръ. И нача Давыдъ глаголати: &laquo;аще не имеве Василка, то ни тоб<small>Ђ</small> княженья у Киев<small>Ђ</small>, ни мн<small>Ђ</small> Володимери&raquo;; и послуша сего Святополкъ. И приде Василко въ 4 ноября, и перевезеся на Выдобичь, іде поклонитися къ святому Михаилу в манастырь, и ужина ту, а товары своя постави на Рудици; вечеру же бывшю прииде в товаръ свой. Наутрия же бывшю, присла Святополкъ, река: &laquo;не ходи отъ именинъ моихъ&raquo;. Василко же ртопр<small>Ђ</small>ся, река: &laquo;не могу ждати; еда будеть рать дома&raquo;. И присла к нему Давыдъ: &laquo;не ходи, брате, и не ослушайся брата стар<small>Ђ</small>йшаго, поидев<small>Ђ</small> оба&raquo;; и не въсхот<small>Ђ</small> Василко створити тако, ни послушаеть ею. І рече Давыдъ къ Святополку: &laquo;видиши ли, не помнить тебе, ходя в руку твоею; аще ли отъидеть въ свою волость, самъ узриши, аще ти не заиметь городовъ твоихъ Турова і Пиньска, и прочихъ городовъ твоихъ, да помянеши мя; но призвавъ и ныня, ими, и дай его мн<small>Ђ</small>&raquo;.</p>\r\n<p>И послуша его Святополкъ, и посла по Василка, глаголя: &laquo;да аще не хощеши ждати до имянинъ моихъ, и прииди нын<small>Ђ</small>, да ц<small>Ђ</small>луеши мя, и пос<small>Ђ</small>димы вси с Давыдомъ&raquo;. Василко же об<small>Ђ</small>щася приити, не в<small>Ђ</small>дый лесть, юже коваше на нь Давыдъ. Василко же вс<small>Ђ</small>дъ на конь по<small>Ђ</small>ха, і въср<small>Ђ</small>те и отрокъ его и пов<small>Ђ</small>да ему, глаголя: &laquo;не ходи, княже, хотять тя яти&raquo;; и не послуша сего, помышляя: &laquo;како мя хотять яти? оногды ц<small>Ђ</small>ловали хресть, рекуще: аще кто на кого будеть, хрестъ [на того да] и мы вси&raquo;; и помысливъ си, перехрестися, река: &laquo;воля Господня да будеть&raquo;. И при<small>Ђ</small>ха в мал<small>Ђ</small> дружин<small>Ђ</small> на княжь дворъ; і вылезе противу ему Святополкъ и идоша въ гридницу, и прииде Давыдъ, і с<small>Ђ</small>доша. І нача Святополкъ глаголати: &laquo;остани на святокъ&raquo;. И рече Василко: &laquo;не могу, брате, остати; уже есмь повел<small>Ђ</small>лъ товаромъ поити переди&raquo;. Давыдъ же с<small>Ђ</small>дяше аки н<small>Ђ</small>мъ, и рече Святополкъ: &laquo;завьтрокай, брате&raquo;; и об<small>Ђ</small>щася Василко завътрокати. И рече Святополкъ: &laquo;посидита вы зд<small>Ђ</small>, а язъ л<small>Ђ</small>зу, наряжю&raquo;; и л<small>Ђ</small>зе вонъ, а Давыдъ с Василкомъ с<small>Ђ</small>доста. И нача Василко глаголати ко Давыдови, и не 6<small>Ђ</small> в Давыд<small>Ђ</small> гласа, и ни послушанья; б<small>Ђ</small> бо ужаслъся, и лесть им<small>Ђ</small>я въ сердц<small>Ђ</small>.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6605 [1097]. І зійшлися Святополк, і Володимир,</p>\r\n<p>І Давид Ігоревим, і Василько Ростиславич,</p>\r\n<p>І Давид Святославич, і брат його Олег,</p>\r\n<p>І зібралися в Любці, щоб установити мир,</p>\r\n<p>І говорили поміж собою, кажучи: &laquo;Нащо губимо Руську землю</p>\r\n<p>І між собою чвари діємо?</p>\r\n<p>А половці землю нашу розносять порізно</p>\r\n<p>І радіють, що між нами рать донині.</p>\r\n<p>Віднині злиймося в єдине серце</p>\r\n<p>І збережімо Руську землю.</p>\r\n<p>І хай кожен тримає отчину свою:</p>\r\n<p>Святополку - Київ Ізяславів, Володимир - Всеволоду,</p>\r\n<p>Давид. Олег і Ярослав - Святославові,</p>\r\n<p>Їм же Всеволод роздав города: Давидові - Володимир</p>\r\n<p>І Ростиславичам: Перемишль - Володареві; Теребовлю - Василькові&raquo;.</p>\r\n<p>І на тому цілували хрест:</p>\r\n<p>&laquo;Хай тільки віднині хтось на іншого встане,</p>\r\n<p>Проти нього будемо всі</p>\r\n<p>І чесний хрест&raquo;.</p>\r\n<p>І сказали всі: &laquo;Хай буде проти того хрест чесний</p>\r\n<p>І вся земля Руська&raquo;.</p>\r\n<p>І, поцілувавшися, розійшлися по домівках.</p>\r\n<p>І прийшли Святополк із Давидом у Київ,</p>\r\n<p>І раді були всі люди,</p>\r\n<p>І тільки диявол засмучений був доброю згодою цією,</p>\r\n<p>І проник сатана у серце деяких мужів,</p>\r\n<p>І почали вони підбивати Давида Ігоревича такими словами:</p>\r\n<p>&laquo;Володимир змовився з Васильком проти Святополка</p>\r\n<p>І проти тебе&raquo;.</p>\r\n<p>І повірив Давид брехливим словесам,</p>\r\n<p>І почав наговорювати на Василька, кажучи таке:</p>\r\n<p>&laquo;Хто убив брата твого Ярополка?</p>\r\n<p>А нині замишляє на тебе і на мене</p>\r\n<p>І змовився з Володимиром.</p>\r\n<p>Так подумай про свою голову&raquo;.</p>\r\n<p>Святополк же знітився умом і сказав:</p>\r\n<p>&laquo;І чи це правда, чи брехня - не знаю&raquo;.</p>\r\n<p>І ще сказав Святополк Давидові:</p>\r\n<p>&laquo;Якщо правду говориш, хай Бог тебе послухає,</p>\r\n<p>А коли із заздрощів говориш, хай Бог буде за тим&raquo;.</p>\r\n<p>Святополк же пожалів брата свого і себе,</p>\r\n<p>І почав задумуватися, чи це правда буде?</p>\r\n<p>І повірив Давидові,</p>\r\n<p>І обдурив Давид Святополка.</p>\r\n<p>І почали вони думати про Василька.</p>\r\n<p>А Василько і Володимир цього не знали.</p>\r\n<p>І почав Давид говорити: &laquo;Якщо не схопимо Василька,</p>\r\n<p>То не князювати ні тобі у Києві, ні мені у Володимирі&raquo;.</p>\r\n<p>І послухав його Святополк.</p>\r\n<p>І прийшов Василько четвертого листопада,</p>\r\n<p>І переправився на Видобичі,</p>\r\n<p>І пішов поклонитися святому Михайлові в монастир.</p>\r\n<p>І вечеряв тут, а обоз свій залишив на Рудиці,</p>\r\n<p>І коли настав вечір, повернувся до свого обозу.</p>\r\n<p>І коли настав ранок, прислав до нього Святополк, кажучи:</p>\r\n<p>&laquo;Не йди з іменин моїх&raquo;.</p>\r\n<p>Василько ж відпирався, кажучи:</p>\r\n<p>&laquo;Не можу ждати, а що, як дома війна&raquo;.</p>\r\n<p>І прислав до нього Давид:</p>\r\n<p>&laquo;Не йди, брате, не роби непослуху братові старшому,</p>\r\n<p>Прийдемо обидва&raquo;.</p>\r\n<p>І не захотів Василько зробити так, не послухав їх.</p>\r\n<p>І сказав Давид Святополкові:</p>\r\n<p>&laquo;Чи бачиш, не пам\'ятає тебе, ходячи під рукою твоєю.</p>\r\n<p>А коли повернеться в свою волость, сам побачиш,</p>\r\n<p>Що він захопить города твої Туров і Пінськ</p>\r\n<p>І інші города твої, згадаєш тоді мене.</p>\r\n<p>Але заклич його нині, схопи і дай його мені&raquo;.</p>\r\n<p>І послухав його Святополк,</p>\r\n<p>І послав за Васильком, говорячи:</p>\r\n<p>&laquo;Якщо не хочеш чекати до моїх іменин,</p>\r\n<p>То приходь зараз, поцілуєш мене</p>\r\n<p>І посидимо всі разом із Давидом&raquo;.</p>\r\n<p>Василько ж пообіцяв прийти, не знаючи про підступність,</p>\r\n<p>Яку кував на нього Давид.</p>\r\n<p>Василько ж сів на коня, і поїхав,</p>\r\n<p>І зустрів він отрока свого,</p>\r\n<p>І той розповів йому, кажучи:</p>\r\n<p>&laquo;Не ходи, князю, хочуть тебе схопить&raquo;.</p>\r\n<p>І не послухав його, думаючи: &laquo;Як це мене хочуть схопити?</p>\r\n<p>Недавно цілували хрест, кажучи,</p>\r\n<p>Якщо хто проти кого піде, [проти того] хрест буде і ми всі&raquo;.</p>\r\n<p>І розмисливши так, перехрестився і сказав:</p>\r\n<p>&laquo;Хай буде воля Господня&raquo;.</p>\r\n<p>І приїхав на княжий двір із малою дружиною,</p>\r\n<p>І вийшов назустріч йому Святополк,</p>\r\n<p>І пішли вони в гридницю,</p>\r\n<p>І прийшов Давид, і сиділи вони.</p>\r\n<p>І почав Святополк говорити: &laquo;Залишайся на свято&raquo;.</p>\r\n<p>І сказав Василько: &laquo;Не можу, брате, залишитися,</p>\r\n<p>Я вже й обозу звелів рушати вперед&raquo;.</p>\r\n<p>Давид же сидів, як німий.</p>\r\n<p>І сказав Святополк: &laquo;Поснідай, брате&raquo;.</p>\r\n<p>І погодився Василько поснідати.</p>\r\n<p>І сказав Святополк: &laquo;Посидьте ви тут, а я піду розпоряджусь&raquo;.</p>\r\n<p>І Вийшов геть, а Давид із Васильком сиділи.</p>\r\n<p>І почав Василько говорити до Давида,</p>\r\n<p>А Давид ні говорив, ні слухав, бо був охоплений жахом</p>\r\n<p>І мав у серці підступ.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>И пос<small>Ђ</small>д<small>Ђ</small>въ мало Давыдъ, рече; &laquo;гд<small>Ђ</small> есть братъ?&raquo; Они же рекоша ему: &laquo;стоить на с<small>Ђ</small>нехъ&raquo;. І въставъ Давыдъ, рече: &laquo;ать иду по нь, а ты ту, брате, пос<small>Ђ</small>ди&raquo;. И въста Давыдъ л<small>Ђ</small>зе вонъ. И яко выступи Давыдъ, і запроша Василка въ 5 ноября, и оковавъше въ дво<small>Ђ</small> оковы, и приставиша к нему сторож<small>Ђ</small> на ночь. Наутрия же Святополкъ созва бояре и Кияне, и пов<small>Ђ</small>да имъ, еже б<small>Ђ</small> ему пов<small>Ђ</small>далъ Давыдъ: &laquo;яко брата ти убилъ, и на тя св<small>Ђ</small>щалъ с Володимеромъ, хочеть тя убити и градъ твой заяти&raquo;. И рекоша бояре и людье: &laquo;тоб<small>Ђ</small>, княже, головы свое<small>Ђ</small> достоить блюсти; да аще есть молвилъ право Давыдъ, да прииметь Василко казнь; аще ли не право глаголалъ Давыдъ, да прииметь месть отъ Бога и отв<small>Ђ</small>щаеть предъ Богомъ&raquo;. И ув<small>Ђ</small>д<small>Ђ</small>ша игумени, и начаша молитися о Васильц<small>Ђ</small> къ Святополку; и рече имъ Святополкъ: &laquo;ото Давыдъ&raquo;. Давыдъ же се във<small>Ђ</small>давъ, нача поостривати на осл<small>Ђ</small>пленье: &laquo;аще ли сего не створити, и его пустиши, тъ ни тоб<small>Ђ</small> княжити, ни мн<small>Ђ</small>&raquo;. Святополкъ же хотяше пустити и, но Давыдъ не хотяше, блюдася его. І на ту нощь ведоша и Звенигороду, иже есть городъ малъ у Киева, яко десяти веръсты въдале, и привезъше и на кол<small>Ђ</small>хъ; окована суща, и съсадиша и с колъ, и въведоша в ыстобъку малу. И с<small>Ђ</small>дящю ему, узр<small>Ђ</small> Василко Торчина остряща ножь, и вразум<small>Ђ</small>, яко хотять и ослипити, и възпи къ Богу плачемъ великомъ и стонаньемъ великомъ. И се вл<small>Ђ</small>зоша послании Святополкомъ и Давыдомъ, Сновидъ Изечевічь, конюхъ Святополчь, и Дмитръ, конюхъ Давыдовъ, почаста простирати коверь, и простерта яста Василка и хотяща я повр<small>Ђ</small>щи и; и боряшеться с нима кр<small>Ђ</small>пко; и не можета его повр<small>Ђ</small>щи; и се вл<small>Ђ</small>зъше друзии повергоша и, и связаша и, и снемьше доску с печи, и възложиша на персии ему; и с<small>Ђ</small>доста обаполы Сновидъ Изечевичь и Дмитръ, и не можаста его удержати; и приступиста ина два, и сняста другую дъску с печи, и с<small>Ђ</small>доста, и удавиша и рамяно, яко персемъ троскотати. И приступи Торчинъ, именемь Береньди, овчюхъ Святополчь, держа ножь, хотя ув<small>Ђ</small>рьт<small>Ђ</small>ти ножь в око и гр<small>Ђ</small>ши ока и перер<small>Ђ</small>за ему лице, и бяше знати рану ту на лици ему; посемь же уверт<small>Ђ</small> ему ножь в з<small>Ђ</small>ницю, изя з<small>Ђ</small>ницю, посемь у другое око уверт<small>Ђ</small> ножь, изя другу зиницю, и томъ час<small>Ђ</small> бысть яко мертвъ. И вземьше и на ковр<small>Ђ</small>, узложиша и на кола яко мертва, и повезоша и Володимерю.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>І посидівши трохи, запитав Давид: &laquo;Де брат?&raquo;</p>\r\n<p>Йому ж відповіли: &laquo;Стоїть у сінях&raquo;.</p>\r\n<p>І, підвівшись, Давид сказав:</p>\r\n<p>&laquo;Я піду за ним, а ти тут, брате, побудь&raquo;.</p>\r\n<p>І встав Давид і вийшов геть.</p>\r\n<p>І як тільки вийшов Давид, замкнули Василька 5 листопада,</p>\r\n<p>І закували в подвійні окови,</p>\r\n<p>І приставили до нього сторожу на ніч.</p>\r\n<p>Ранком же Святополк скликав бояр і киян</p>\r\n<p>І повідав їм, що йому розповів Давид: [Василько] &laquo;брата твого убив</p>\r\n<p>І проти тебе змовився з Володимиром, хоче вбити тебе</p>\r\n<p>! город твій захопити&raquo;.</p>\r\n<p>І сказали бояре і люди:</p>\r\n<p>&laquo;Тобі, князю, належить берегти голову свою.</p>\r\n<p>І якщо правду сказав Давид, хай прийме Василько кару.</p>\r\n<p>А коли неправду говорив Давид, хай прийме помсту від Бога</p>\r\n<p>І відповідає перед Богом&raquo;.</p>\r\n<p>І довідалися ігумени</p>\r\n<p>І почали просити Святополка за Василька.</p>\r\n<p>І сказав їм Святополк: &laquo;Ото Давид&raquo;.</p>\r\n<p>Давид же узнав про це і почав підбивати на кару осліпленням:</p>\r\n<p>&laquo;Якщо цього не зробиш і його відпустиш,</p>\r\n<p>То ні тобі не княжити, ні мені&raquo;.</p>\r\n<p>Святополк же хотів відпустити Василька,</p>\r\n<p>А Давид не хотів, пильнував його.</p>\r\n<p>І в ту ж ніч відвезли Василька в Звенигород&raquo;</p>\r\n<p>Невеличкий городок біля Києва, віддалений на десять верст.</p>\r\n<p>І привезли його на колі, закованого,</p>\r\n<p>І висадили з кола,</p>\r\n<p>І завели в ізтобку малу.</p>\r\n<p>І сидячи там, побачив Василько торчина, який гострив ніж,</p>\r\n<p>І зрозумів, що його хочуть осліпити.</p>\r\n<p>І заволав до Бога плачем великим</p>\r\n<p>І стогнанням великим.</p>\r\n<p>І тут ввійшли посланії Святополком</p>\r\n<p>І Давидом - Сновид Ізечевич, конюх Святополків,</p>\r\n<p>І Дмитр, конюх Давидів,</p>\r\n<p>І почали розстилати ковер,</p>\r\n<p>І, розіславши, схопили Василька,</p>\r\n<p>І хотіли вони повалити його.</p>\r\n<p>І боронився від них завзято,</p>\r\n<p>І не могли його повалить.</p>\r\n<p>І тут ввійшли другі, і повалили його, і зв\'язали його,</p>\r\n<p>І, знявши дошку з печі, поклали на груди йому,</p>\r\n<p>І сіли з двох кінців Сновид Ізечевич і Дмитр,</p>\r\n<p>І не могли утримати його,</p>\r\n<p>І підійшли двоє інших,</p>\r\n<p>І зняли другу дошку з печі, і сіли,</p>\r\n<p>І придавили йому рамена, аж груди затріщали.</p>\r\n<p>І підійшов торчин, на ймення Беренді, овчух Святополків,</p>\r\n<p>І, тримаючи ніж, хотів вкрутити ніж в око,</p>\r\n<p>І промахнувся,</p>\r\n<p>І перерізав йому лице,</p>\r\n<p>І зараз видно рану ту на його лиці,</p>\r\n<p>І після цього вдарив ножем в зіницю,</p>\r\n<p>І вийняв зіницю,</p>\r\n<p>І потім у друге око вкрутив ніж,</p>\r\n<p>І вийняв другу зіницю.</p>\r\n<p>І був Василько в той час як мертвий.</p>\r\n<p>І взяли йото на коврі,</p>\r\n<p>І поклали його на коло, як мертвого,</p>\r\n<p>І повезли його в Володимир.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>И въспроси воды, они же даша ему, и испи воды, и въступи душа, и поминуся, и пощюпа сорочкы и рече: &laquo;чему есте сняли с мене? да быхъ в с<small>Ђ</small>й сорочици смерть приялъ и сталъ предъ Богомъ въ кровав<small>Ђ</small> сорочиц<small>Ђ</small>&raquo;. Он<small>Ђ</small>мъ же об<small>Ђ</small>давшимъ, поидоша с нимь въскор<small>Ђ</small> на кол<small>Ђ</small>хъ, а по грудну пути, б<small>Ђ</small> бо тогда м<small>Ђ</small>сяць груденъ, рекше ноябрь; и приидоша с нимъ Володимерю въ 6 день. Прииде же и Давыдъ по немъ, яко зв<small>Ђ</small>рь уловилъ; и посадиша и у двор<small>Ђ</small> Вак<small>Ђ</small>ев<small>Ђ</small>, і пріставиша 30 мужь стр<small>Ђ</small>щи, а 2 отрока княжа, Улана и Колчю. Вълодимеръ же слышавъ, яко ятъ есть Василко и осл<small>Ђ</small>пленъ, ужасеся, и въсплакася вельми і рече: &laquo;сего не было есть у Русьской земли ни. при д<small>Ђ</small>дехъ нашихъ, ни при отціхъ нашихъ, сякого зла&raquo;. И ту абье посла ко Давыду и к Ольгови Святъславичема, глаголя: &laquo;поид<small>Ђ</small>та к Городцю, да поправимъ сего зла, еже ся сотвори у Русьской земли и в насъ братьи, оже уверже в ны ножь; да аще сего не поправим.ъ, больше зло въстанеть в насъ, и начнеть братъ брата заколати, и погыбнеть земля Русьская, и врази наши Половци пришедъше возмуть землю Русьскую&raquo;. Се слышавъ . Давыдъ и Олегъ, печална быста вельми и начаста плакатися, рекуща: &laquo;яко сего не было в родъ нашемь&raquo;; и ту абье собравьша воя, и приидоста к Володимеру. Володимеру сущю с вои стоящю у бору, Володимеръ же, и Давыдъ, и Олегъ послаша муж<small>Ђ</small> свои къ Святополку, глаголюще: &laquo;что се створилъ еси в Русьской земл<small>Ђ</small>, уверьглъ еси ножь в ны? чему еси ослипилъ брата своего? аще быти вина какая была на нь, обличилъ бы пред нами, и упр<small>Ђ</small>въ бы и створилъ ему; а ныне кая вина до него, оже ему се створилъ еси&raquo;. И рече Святополкъ: &laquo;пов<small>Ђ</small>далъ ми Давыдъ Игоревичь, яко Василко брата ти убилъ Ярополка, и тебе хощеть убити и заяти волость твою, Туровъ, и Пинескъ, и Берести, и Погорину, и шелъ рот<small>Ђ</small> с Володимеромъ, яко с<small>Ђ</small>сти Володимеру в Киев<small>Ђ</small>, а Василкови Володимери; а неволя ми главы своея блюсти, и не язъ его сл<small>Ђ</small>пилъ, но Давыдъ, і велъ и к соб<small>Ђ</small>&raquo;. И р<small>Ђ</small>ша мужи Володимери, и Давыдови, и Олгови:</p>\r\n<p>&laquo;изв<small>Ђ</small>та о семъ не м<small>Ђ</small>ита, яко Давыдъ есть сл<small>Ђ</small>пилъ и: не въ Давыдов<small>Ђ</small> град<small>Ђ</small> ятъ есть, ни осл<small>Ђ</small>пленъ, но въ твоемъ город<small>Ђ</small> ятъ и осл<small>Ђ</small>пленъ&raquo;; и се имъ глаголющимъ разидошася раздно. Наутрия же хотя Володимеру, и Давыдови, и Ольгови чересъ Дн<small>Ђ</small>пръ на Святополка, Святополкъ же хотяше поб<small>Ђ</small>гнути ис Кыева, и не даше ему Кияне поб<small>Ђ</small>гнути, но послаша Всеволохаою княгину и митрополита Николу къ Володимеру, глаголюща: &laquo;молимся, княже, тоб<small>Ђ</small> и братома твоима, не моз<small>Ђ</small>те погубити Русьской зе.мл<small>Ђ</small>; аще бо возмете рать межю собою, погаки имуть радоватися и возмють землю нашю, юже б<small>Ђ</small>ша стяжали ваши д<small>Ђ</small>ди, і отци ваши, трудомъ великимъ и хороборьствомъ побаряюще по Русьской земли, а ины земли приискаху; а вы хощете погубити Русьскую землю&raquo;. Всеволожая княгини и митрополитъ приидоста к Володимеру, и молистася ему, и пов<small>Ђ</small>даста молбу Кыянъ, яко створити миръ, и блюсти земли Руской, и брань им<small>Ђ</small>ти с погаными. И се слышавъ Володимеръ, расплакася [и рече]: &laquo;по истин<small>Ђ</small> отци нашк и д<small>Ђ</small>ди наши соблюдоша Русьскую землю, а мы ю хощемъ погубити&raquo;; и преклонися на мрлбу, чтяшеть бо ю яко матерь, отца ради сврего, бъ бо любимъ отцю своему повелику, в живот<small>Ђ</small> и по смерти, и не ослушася его ни в чемь же; и послуша яко матере и митрополита, такоже чтя санъ святительскый, не пр<small>Ђ</small>слуша молбы его. Володимеръ же такъ есть любьзнивъ: любовь им<small>Ђ</small>я к митрополитомъ і къ епискупомъ, паче же черноризецький чинъ любя, и приходящая к нему напиташе и напояше, акы мати д<small>Ђ</small>ти своя; аще кого видить или шюмна, или в коемь зазор<small>Ђ</small>, и не осужаше, но все на любовь прикладаше и ут<small>Ђ</small>шаше. Но мы на прежереченое узврат<small>Ђ</small>мься. Княгини же бывши у Володимера, и прииде Кыеву и пов<small>Ђ</small>да всю р<small>Ђ</small>чь Святополку и Кияномъ, яко миръ будеть. И начаша межи собою мужи слати, и умиришася на семь, яко р<small>Ђ</small>ша Святополку: &laquo;яко се Давыдова есть сольстилъ; то иди ты, Святополче, на Давыда, любо ими и, любо прожени&raquo;. Святополкъ же емься по се, и ц<small>Ђ</small>ловаше хрестъ межи собою, миръ створше.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>І коли привезли, зупинились із ним,</p>\r\n<p>Переїхавши міст Воздвиженський, на. торговищі.</p>\r\n<p>І зняли з нього сорочку криваву,</p>\r\n<p>І дали попаді випрати. Попадя ж відіпрала,</p>\r\n<p>І наділа на нього, коли ті обідали,</p>\r\n<p>І почала оплакувати його попадя, як покійника.</p>\r\n<p>І збудив його плач і спитав: &laquo;Де це я?&raquo;</p>\r\n<p>Вони ж відповіли йому: &laquo;Біля Звиждення, в граді&raquo;.</p>\r\n<p>І попросив води, вони ж дали йому.</p>\r\n<p>І напився води,</p>\r\n<p>І вернулась душа,</p>\r\n<p>І опам\'ятався,</p>\r\n<p>І пощупав сорочку,</p>\r\n<p>І сказав: &laquo;Нащо знімали її з мене?</p>\r\n<p>Хай би я в цій сорочиці смерть прийняв</p>\r\n<p>І став перед Богом в кривавій сорочці&raquo;.</p>\r\n<p>І коли ж оті пообідали,</p>\r\n<p>Негайно ж поїхали з ним на колі по грудкастій дорозі,</p>\r\n<p>Бо був тоді місяць грудень, себто ноябрь.</p>\r\n<p>І прибули з ним у Володимир на шостий день.</p>\r\n<p>Прибув і Давид слідом за ним, наче звіра зловив,</p>\r\n<p>І посадив його у дворі Вакієві,</p>\r\n<p>І приставили тридцять мужів стерегти його,</p>\r\n<p>І двох отроків княжих, Улана і Колчу.</p>\r\n<p>Володимир же [Мономах] як почув, що схопили Василька</p>\r\n<p>І осліпили,- вжахнувся,</p>\r\n<p>І залився сльозами,</p>\r\n<p>І сказав: &laquo;Такого зла не було ще у Руській землі</p>\r\n<p>Ні за дідів наших, ні за батьків наших&raquo;.</p>\r\n<p>І відразу ж послав до Давида</p>\r\n<p>І до Олега Святославича сказати:</p>\r\n<p>&laquo;Приходьте в Городець, щоб виправити зло,</p>\r\n<p>Яке трапилося в Руській землі</p>\r\n<p>І в нас, братії. Уже загнано у нас ніж.</p>\r\n<p>І якщо цього не поправимо,</p>\r\n<p>То ще більше зло постане серед нас,</p>\r\n<p>І почне брат брата колоти,</p>\r\n<p>І загине земля Руськая,</p>\r\n<p>І вороги наші, половці, прийдуть</p>\r\n<p>І візьмуть землю Руськую&raquo;.</p>\r\n<p>Коли почули про це Давид і Олег, опечалилися вельми</p>\r\n<p>І заплакали, промовляючи: &laquo;Такого ще не було в роді нашому&raquo;.</p>\r\n<p>І тут же негайно зібрали воїв,</p>\r\n<p>І прийшли до Володимира.</p>\r\n<p>Володимир тоді стояв із воями у бору.</p>\r\n<p>Володимир же, і Давид, і Олег послали мужів своїх до Святополка,</p>\r\n<p>І питають: &laquo;Що це ти наробив у Руській землі?&raquo;</p>\r\n<p>Ти ввігнав ніж у нас, нащо ти осліпив брата свого?</p>\r\n<p>Якби було якесь звинувачення проти нього,</p>\r\n<p>То мав би викрити його перед нами,</p>\r\n<p>І, довівши провину, зробив би так.</p>\r\n<p>А нині яка вина його, що ти вчинив із ним так&raquo;.</p>\r\n<p>І сказав Святополк: &laquo;Розповів мені Давид Ігоревим,</p>\r\n<p>Що Василько брата твого, Ярополка, убив,</p>\r\n<p>І тебе хоче вбити,</p>\r\n<p>І захопити волость твою - Туров, і Пінеськ, і Берестя, і Погорину,-</p>\r\n<p>І що поклявся [Василько] з Володимиром,</p>\r\n<p>Що сяде Володимир у Києві,</p>\r\n<p>А Василько - у Володимирі.</p>\r\n<p>А я зобов\'язаний голову свою берегти.</p>\r\n<p>І не я осліпив, а Давид, він і забрав його до себе&raquo;.</p>\r\n<p>І сказали мужі Володимирові, і Давидові, і Олегові:</p>\r\n<p>&laquo;Марно оправдуєшся, що Давид осліпив його:</p>\r\n<p>Не в Давидовому городі його схопили і осліпили,</p>\r\n<p>А в твоєму городі взяли і осліпили&raquo;.</p>\r\n<p>І коли це сказали, розійшлися порізно.</p>\r\n<p>&nbsp;</p>\r\n<p>На другий день Володимир, Давид і Олег мали намір перейти через Дніпро і виступити проти Святополка. Святополк же хотів втекти з Києва, і не дали йому кияни втекти, але послали княгиню Всеволодову і митрополита Николу до Володимира сказати: &laquo;Благаємо, княже, тебе і братів твоїх, не погубіть Руської землі. Бо як почнете війну Між собою, погані возрадуються і захоплять землю кашу, яку збирали ваші діди і батьки ваші трудами великими і хоробрістю, обороняючи Руську землю, а інші землі прилучаючи до неї, а ви хочете погубити Руську землю&raquo;. Всеволодова княгиня з митрополитом прийшли до Володимира, і благали його, і передали просьбу киян, щоб укласти мир і берегти землю Руську і брань мати з поганими. І коли почув це Володимир, заплакав [і сказав]: &laquo;Воістину батьки наші і діди наші зберегли Руськую землю, а ми її хочемо погубити&raquo;. І здався на благання, бо шанував її як матір, ради батька свого, бо любив його батько любов\'ю великою і за життя, і після смерті, і він не ослухавсь його ні в чому, а тому і матір послухав, і митрополита також шанував за сан святительський, а тому й не зневажив прохань його. Володимир же був сповнений любові: любив митрополитів, і єпископів, а найбільше чорноризький чин любив, і тих, що приходили до нього, годував і напував, як мати дітей своїх. Якщо кого бачив або шумним, або в соромі якому, то не осуджував, а все любов\'ю міряв і втішав. Але повернемося до попередньої розповіді.</p>\r\n<p>Княгиня ж побувала у Володимира, і прийшла в Київ, і передала все, сказане Святополкові і киянам, і що мир буде. І почали один до одного мужів посилати і зійшлися на тому, що сказали Святополкові: &laquo;Якщо це Давидів заколот, то ти іди, Святополче, на Давида. Або схопи його, або прожени&raquo;. Святополк же погодився на це, і поклялися на хресті між собою, мир уклали.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Василкови же сущю в Володимери, на пр<small>Ђ</small>жер<small>Ђ</small>ченомь м<small>Ђ</small>ст<small>Ђ</small>, яко приближися постъ великый, и мн<small>Ђ</small> ту сущю в Володим<small>Ђ</small>р<small>Ђ</small>, в едину нощь присла по мя князь Давыдъ. И приидохъ к нему, и с<small>Ђ</small>дяху дружина около его, и посади мя, и рече ми: &laquo;се молвилъ Василко сы ночи ко Вланови и къ Колчи, реклъ тако Василко: се слышу, оже идеть Володимеръ и Святополкъ на Давыда; да же бы мене Давыдъ послуталъ, да быхъ послалъ мужа своего к Володимеру воротися, в<small>Ђ</small>де бо ся с нимъ что молвивъ, не поидеть; да се, Василю, шлю тя, <small>Ђ</small>ди к Василкови со сима отрокома, и молви ему тако: оже хощеши [к Володимеру] послати мужа своего, и воротится Володимеръ, то вдамъ ти который любо городъ, любо Всеволожь, любо Шеполь, любо Перемиль&raquo;. Азъ же идохъ к Василкови, и пов<small>Ђ</small>дахъ ему всю р<small>Ђ</small>чь Давыдову. Онъ же рче: &laquo;сего есть не мол.вилъ; но над<small>Ђ</small>яся на Богъ, послю к Володимеру, да быша не прольяли крови мене д<small>Ђ</small>ля; но сему ми дивно, даеть ми градъ свой, а мой Теребовль, моя волость&raquo;; пождавши и нын<small>Ђ</small>, якоже и бысть; въскор<small>Ђ</small> бо прия вдасть свою. Мн<small>Ђ</small> же рече: &laquo;иди къ Давыдови и рци ему: пришли ми Кулм<small>Ђ</small>я; азъ его пошьлю к Володимеру&raquo;. И не послуша его Давыдъ, и посла мя река пакы: &laquo;н<small>Ђ</small>ту Кулъм<small>Ђ</small>я&raquo;. I рече ми Василко: &laquo;пос<small>Ђ</small>ди мало&raquo;, і повел<small>Ђ</small> слузи своему ити вонъ, и с<small>Ђ</small>де со мною, и нача глаголати: &laquo;се азъ слышю, оже мя хочеть Давыдъ давати Ляхомъ; то ся мало насытилъ крове моея, і се хощеть больше ся насытити, иже мя вдасть имъ: азъ бо Ляхомъ много зла створихъ, и еще есмь хот<small>Ђ</small>лъ створити и мьстити Русьскую землю; аще мя вдасть Ляхомь, не боюся смерти, но се пов<small>Ђ</small>даю ти: по истин<small>Ђ</small> яко наведе на мя Богъ за мое узвышенье, яко приде ми в<small>Ђ</small>сть, яко идуть ко мн<small>Ђ</small> Береньдичи, и Печен<small>Ђ</small>зи, и Торци, и се рекохъ въ ум<small>Ђ</small> своемь: оже мі будуть Берендичи и Торци и Печен<small>Ђ</small>зи, і реку брату своему Володареви и Давыдови: даита дружину свою моложьщюю, а сама пиита и веселитася; и помыслихъ на землю Лядьскую: наступлю на зиму і на л<small>Ђ</small>то и возму землю Лядьскую и мьщю землю Русьскую; и посемь хот<small>Ђ</small>лъ есмь переяти Болгары Дунайскыя, і посадити я у себе; посемь хотяхъ проситися у Святополка и у Володимера на Половц<small>Ђ</small>, и поиду, р<small>Ђ</small>хъ, на Половц<small>Ђ</small>, да любо нал<small>Ђ</small>зу соб<small>Ђ</small> славу, любо главу свою сложю за Русьскую землю; а<em> </em>иное помышленье въ сердци моемъ не было ни на Святополка, ни на Давыда, и се кленуся Богомъ и его приществиемь, яко не помыслилъ есмь зла братьи моей ни в чемь же, но за мое узнесенье, иже поидоша Береньдичи ко мн<small>Ђ</small>, и веселяся сердце мое, и възвеселися умъ мой, и низложи мя Богъ и см<small>Ђ</small>ри мя&raquo;. Посемь же приходящю Великому дни, поиде Давыдъ, прияти хотя власть Василкову; і въср<small>Ђ</small>те и Володарь, брать Василкозъ, у Бужьиска, и не см<small>Ђ</small> Давыдъ стати противу Володареви, и затворися въ Бужьск<small>Ђ</small>, и оступи градъ Бужескъ Володарь. И нача Володарь молвити: &laquo;почто зло створивъ не каешися сего? да уже поминися, колко еси зла створилъ&raquo;. Давыдъ же на Святополка нача изв<small>Ђ</small>тъ творити, глаголя: &laquo;ци я се створилъ, ци ли у моемъ город<small>Ђ</small>? язъ и самъ боялъся, аще быша и мене не яли и створили тоже; неволя ми было пристати св<small>Ђ</small>ту ихъ, ходящю в рукахъ ихъ&raquo;. И рече Володарь: &laquo;Богъ свидитель тому, а нын<small>Ђ</small> пусти брата моего, и створю с тобою миръ&raquo;. И радъ бывъ Давыдъ, посла по Василка, и приведы и, и уда и Володареви, и створися миръ, и разидостася. И с<small>Ђ</small>де Василко в. Теребовли, Давыдъ приде Володимерю. Ставши весн<small>Ђ</small>, и прииде Володарь и Василко на Давыда, и приидоста ко Всеволожю, а Давыдъ затворися у Володимер<small>Ђ</small>. Он<small>Ђ</small>ма же ставшима около Всеволожа, и взяста копьемъ городъ и зажьгоста огнемь; и выб<small>Ђ</small>гоша людье [отъ] огня; и повел<small>Ђ</small>. Василко вся ис<small>Ђ</small>щи, и створи Василко мьщенье на людьехъ неповиньныхъ, и пролья кровь неповиньну. Посемь же приидоста Володимерю, и Давыдъ затворися в город<small>Ђ</small>, си же обьступиста градъ. И посласта к Володимерцемь, глаголюща: &laquo;в<small>Ђ</small> не приидохов<small>Ђ</small> на городъ вашь, ни на васъ, но на вороги своя, на Туряка, и на Лазоря, и на Василя, ти бо суть намолвили Давыда, и т<small>Ђ</small>хъ есть послушалъ Давыдъ и створилъ все зло; аще хощете за сихъ битися, да се мы готовы; аще ли, то выдайте враги наша&raquo;.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Коли ж Василько був у Володимирі на раніше визначеному місці, коли наближався великий піст, а я був тоді у Володимирі, одної ночі прислав за мною князь Давид. І я прийшов до нього, і сиділа дружина біля нього, і посадив мене, і сказав мені: &laquo;Ось Василько говорив цієї ночі із Вланом і Колчею, і так сказав Василько: &laquo;Оце чую, що йдуть Володимир і Святополк на Давида. Якби мене Давид послухав, щоб я послав мужів своїх до Володимира з проханням повернутися назад, бо я знаю, що сказати йому, і він не піде&raquo;. Так оце, Василю, шлю тебе, іди до Василька із цими отроками, і скажи йому так: &laquo;Якщо хочеш [до Володимира] послати мужа свого і Володимир повернеться назад, то дам тобі будь-який город: або Всеволож, або Шеполь, або Перемиль&raquo;. Я ж пішов до Василька і передав йому все, що Давид сказав. Він же прорік: &laquo;Того я не говорив, але надіюся на Бога, пошлю до Володимира, хай не проливають крові ради мене. Але мене ось що дивує: дає мені город свій, а мій Теребовль,- моя волость&raquo;. Пройшов час, і це збулося, бо невдовзі прийняв волость свою. Мені ж сказав: &laquo;Іди до Давида і скажи йому: хай пришле мені Кулмія, я його пошлю до Володимира&raquo;, І не послухав його Давид, і послав мене знову сказати: &laquo;Нема Кулмія&raquo;. І сказав мені Василько: &laquo;Посидь трохи&raquo;. І звелів слузі своєму вийти геть, і сів зі мною, і почав говорити: &laquo;От я чую, що хоче Давид віддати мене ляхам. То ще мало наситився кров\'ю моєю, і от хоче більше насититися, якщо мене віддасть їм: бо я ляхам багато зла сотворив, і ще більше хотів натворити, і мстити за Руську землю. Якщо мене віддасть ляхам, не боюся смерті, але ось що повідаю тобі: воістину, все це наслав на мене Бог за мої надмірні гордощі. Як стало мені відомо, що ідуть до мене берендичі, і торки, і печеніги, і я сказав сам собі: якщо в мене будуть берендичі, і торки, і печеніги, тоді скажу братові своєму Володарю і Давидові: дайте мені дружину свою молодшу, а сами пийте і веселіться. А сам замисел мав проти землі Лядської: нападу взимку, а влітку і заволодію землею Лядською і відомщу за землю Руську. А після цього хотів захопити болгарів дунайських і посадити їх у себе. Потім хотів проситися у Святополка і у Володимира йти на половців. І піду, сказав, на половців, і або славу знайду собі, або голову свою покладу за Руськую землю. А інших помислів у серці моєму не було ні супроти Святополка, ні проти Давида. І от клянуся Богом і його пришестям, що не бажав я зла братам моїм ні в чому, але за моє самовеличання, що пішли берендичі до мене, і веселилося серце моє, і возвеселився розум мій,- і повалив мене Бог і упокорив мене&raquo;. Після цього, коли наближався Великдень, пішов Давид, щоб захопити волость Василькову. І зустрів його Володар, брат Васильків, біля Бужеська. І не посмів Давид стати на бій з Володарем і зачинився у Бужеську. І обступив Володар город Бужеськ. І почав Володар говорити: &laquo;Йому зло зробив і не каєшся? Опам\'ятайся вже, скільки зла натворив&raquo;. Давид же на Святополка став наговорювати, кажучи: &laquo;Хіба це я натворив чи це сталося в моєму городі? Я і сам боявся, щоб і мене не схопили і не зробили того ж. Поневолі я мусив пристати на раду їхню, бо сам був у руках їхніх&raquo;. І сказав Володар: &laquo;Бог свідок тому, а нині пусти брата мого і я створю мир із тобою&raquo;. І зрадів Давид, послав за Васильком і, коли привели, віддав його Володареві, і створили мир, і розійшлися. І сів Василько в Теребовлі, Давид повернувся у Володимир. Коли настала весна, пішли Володар і Василько на Давида і прийшли до Всеволожа, а Давид зачинився в Володимирі. Вони ж зупинилися біля Всеволожа, і взяли город коп\'єм, і підпалили вогнем, і вибігали люди із вогню, і наказав Василько всіх їх порубати, і вчинив Василько помсту на людях неповинних, і пролив кров неповинну. Після цього прийшли до Володимира, і Давид зачинився в городі, а вони обступили город. І послали до володимирців, кажучи: &laquo;Знайте, що ми прийшли не на город ваш і не на вас, а на ворогів своїх, на Туряка, і на Лазаря, і на Василя, бо вони намовили Давида, і їх послухав Давид, і вчинив усе зло. Якщо хочете за них битися, то ми готові, а якщо ні, то видайте нам ворогів наших&raquo;.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Гражани же слышавше се, и созвониша в<small>Ђ</small>че, и рекоша Давыдови людье на в<small>Ђ</small>че: &laquo;выдай мужи сия, мы не бьемъся за сихъ, а за тя можемъ ся бити, а за сихъ не бьемъся; аще ли, то отворимъ ворота городу, а самъ промышляй о соб<small>Ђ</small>&raquo;; и неволя бысть выдати я. И рече Давыдъ: &laquo;н<small>Ђ</small>ту ихъ сд<small>Ђ</small>&raquo;, б<small>Ђ</small> бо я послалъ до Лучька; он<small>Ђ</small>мь же пошедшимъ Лучьску, Турягъ б<small>Ђ</small>жалъ Кыеву, а Лазорь и Василь воротистася Турийську. И слышаша людье, яко в Турийск<small>Ђ</small> суть, и кликоша людье на Давыда, рекуще: &laquo;выдай, кого ти хотять; аще ли, то предамыся&raquo;. Давыдъ же пославъ приведе Василья и Лазаря, и вдасть я; и створися миръ в нед<small>Ђ</small>лю, а завътра в понед<small>Ђ</small>лникъ, по зорямъ, пов<small>Ђ</small>сиша Лазоря и Василя и растр<small>Ђ</small>ляша стр<small>Ђ</small>лами Василковичи, и идоша отъ града. Се второе мьщенье створи, егоже бяше не л<small>Ђ</small>по створити, дабы отместникъ Богъ былъ, и възложити было на Бога отмыценье свое; якоже рече Пророкъ: и въздамъ месть врагомъ, и ненавидящимъ мене въздамъ, яко кровь сыновъ своихъ мьщаеть и мьстить; и въздасть месть врагомъ, и ненавидящимъ его въздасть. Симь же отъ града отшедшимъ, и сею снемьше погребоша. Святополку же об<small>Ђ</small>щавшюся се створити, прогнати Давида, поиде к Берестью к Ляхомъ; и се слышавъ Давыдъ, иде в Ляхы к Володиславу, ища помощи. Ляхове же об<small>Ђ</small>щашася се створити, и взяша у него 50 гривенъ злата, рекуще ему: &laquo;поиди с нами Берестью, яже се вабить ны Святополкъ на снемъ; и ту умиримъ тя съ Святополкомъ&raquo;. И послушавъ ихъ Давыдъ, иде Берестью с Володиславомъ. И ста Святополкъ въ град<small>Ђ</small>, а Ляхов<small>Ђ</small> на Буз<small>Ђ</small>, и сносися Святополкъ р<small>Ђ</small>чью с Ляхы, и дасть имъ великия дары на Давыда; Володиславъ рече [Давыдови]: &laquo;не послушаеть мене Святополкъ; да иди опять вьспять. И прииде Давыдъ Володимерю, а Святояолкъ св<small>Ђ</small>тъ створи с. Ляхы, и поиде къ Пиньску, посла по во<small>Ђ</small>; и прииде Дорогобужю, и дожда ту вой своихъ, поиде на Давыда къ граду, Давыдъ затворися въ град<small>Ђ</small>, чая помочи в Ляховъ на Святополк[а]; б<small>Ђ</small>ша бо рекли ему: &laquo;яко на тя приидуть Русьскии князи, то мы ти будемъ помощници&raquo;; солгаша, а емлюще злато у Давыда. Святополкъ же оступи городъ, а Давыдъ въ град<small>Ђ</small>, и стоя Святополкъ около города 7 нед<small>Ђ</small>ль; и поча Давыдъ молитися: &laquo;пусти мя из города&raquo;; Святополкъ же об<small>Ђ</small>щася ему, и ц<small>Ђ</small>ловаша хрестъ межи собою, и изиде Давыдъ из порода, и прииде в Червенъ, а Святополкъ вииде в городъ в великую суботу, Давыдъ же б<small>Ђ</small>жа в Ляхы. Святополкъ же прогнавъ Давыда, нача думати на Володаря и на Василка, глаголя: &laquo;яко се есть волость отца моего и брата&raquo;; и поиде на ня. И се слышавъ Володаръ и Василко, поидоста противу, вземше хрестъ, [егоже ц<small>Ђ</small>ловалъ к нима на семъ, &laquo;яко на Давыда&raquo;] приишелъ есмь, а с вама хощю им<small>Ђ</small>ти миръ и любовь&raquo;; и преступи Святополкъ [крестъ], над<small>Ђ</small>яся на множество вой. И съступишася на поли на Рожни, исполчившимъся имъ обоимъ, Василко же узвыси хрестъ, глаголя: &laquo;яко сего еси ц<small>Ђ</small>ловалъ, се яко взялъ еси зракъ у мене очью моею, а се нын<small>Ђ</small> отъяти хощеши душю мою; і межі буди нами хрестъ сий честный&raquo;; и поидоша обои противу соб<small>Ђ</small> к боеви, и съступишася полци, и мнози челов<small>Ђ</small>ци благов<small>Ђ</small>рнии вид<small>Ђ</small>ша крестъ надъ Василковыми вои, узвышьшийся вельми. Брани же велиц<small>Ђ</small> бывши и многымъ падающимъ отъ обою полку, вид<small>Ђ</small> Святополкъ, яко люта брань, и поб<small>Ђ</small>же къ Володимерю; и Володарь же и Василко поб<small>Ђ</small>дивша, стаста ту рекуща: &laquo;довол<small>Ђ</small>еть нама на межи своей стати&raquo;, и не идоста никаможе. Святополкъ же приб<small>Ђ</small>же Володимерю, и с нимь сына его два, и Святоша сынъ Давыдовъ Святъславичь, и прочая дружина. Святополкъ же посади сына своего Володимери Мьстислава, иже б<small>Ђ</small> отъ наложниц<small>Ђ</small> ему, а Ярослава посла Угры, вабя Угры на Володаря, а самъ иде Кыеву. Ярославъ, сынъ Святополчь, прииде съ Угры, и король Коломанъ и 2 пискупа, и сташа около Перемышля по Вягру, а Володарь затворися въ град<small>Ђ</small>. Давыдъ же въ тъ чинъ пришедъ из Ляховъ, и посади жену свою у Володаря, а самъ иде в Полозц<small>Ђ</small>; и уср<small>Ђ</small>те и Бонякъ, и воротися Давыдъ, и поидоста на Угры. Идущіма же има, и сташа ночьл<small>Ђ</small>гу, и яко бысть полунощи, и въставъ Бонякъ отъ<small>Ђ</small>ха отъ рати, и поча выти волъчьски, и отвыся ему волкъ, и начаша мнози волци выти; Бонякъ же при<small>Ђ</small>ха пов<small>Ђ</small>да Давыдови, яко &laquo;поб<small>Ђ</small>да ны есть на Угры&raquo;. И завтра Бонякъ исполчивъ вои свои, Давыдово 100, а Бонякъ у 300 ст<small>Ђ</small>хъ; и разд<small>Ђ</small>ли на 3 полкы, и поиде ко Угромъ. И пусти на воропъ Алтунопу въ 50, а Давыда постави подъ стягомъ, а самъ разд<small>Ђ</small>лися на два полка, по 50 на сторону. Угре же исполчишася на заступы, б<small>Ђ</small> бо Угоръ числомъ 100 тысящь. Алтунопа же пригна къ первому заступу, і стр<small>Ђ</small>ливше, поб<small>Ђ</small>гну предъ Угры, Угре же погнаху по нихъ, мьняху Боняка б<small>Ђ</small>жаща, а Бонякъ гнаше с<small>Ђ</small>ка у плещи, Алтунопа взвратився успять, и не допустяху Угоръ опять, и тако множицею избиваше я; Бонякъ же разд<small>Ђ</small>лися на 3 полкы, и сбиша Угры в мячь, яко соколъ галиц<small>Ђ</small> збиваеть. И поб<small>Ђ</small>гоша Угре, и мнози истопоша у Вягру, друзии же въ Сану; и б<small>Ђ</small>жаще возл<small>Ђ</small> Санъ у гору, и спихаху другъ друга, и гна по нихъ два дни, с<small>Ђ</small>кущи я; ту же убиша бискупа ихъ Купана, и отъ боляръ многи; якоже глаголаху, погыбло убьено 40 тысящь. Ярославъ же б<small>Ђ</small>же на Ляхы, к прииде Берестью, а Давыдъ заемъ Сут<small>Ђ</small>йску и Червенъ, и прииде внезапу и зая Володимерц<small>Ђ</small>, а Мьстиславъ затворися у град<small>Ђ</small> съ засадою, уже бяше у него Берестьяне, и Пиняни, Вышегородци; и ста Давыдъ, оступивъ городъ, и часто приступаше. Единою подступиша къ граду подъ вежами, он<small>Ђ</small>мь же бьющимся съ града, и стр<small>Ђ</small>ляющимъ межи собою, идяху стр<small>Ђ</small>лы акы дожчь; Мьстиславу же хотящю стрилити, внезапу вдаренъ бысть подъ пазуху стр<small>Ђ</small>лою, на заборол<small>Ђ</small>хъ, скважнею, и сведоша и, и на ту нощь умре, и таиша его 3 дни, и в четвертый день пов<small>Ђ</small>даша и на в<small>Ђ</small>чи. И рекоша людье: &laquo;се князь убьенъ; да аще ся вдамы, и Святополкъ погубить ны&raquo;; и послаша къ Святополку, глаголюще: &laquo;се сынъ твой убьенъ, а мы изнемогаемъ голодомъ; аще не придеши, хотять ся людье предати, не могуще глада терп<small>Ђ</small>ти&raquo;. Святополкъ же посла Путяту, своего воеводу; Путята же пришедъ с вои к Луцьку къ Святоши, сыну Давидову, и ту бяху мужи Давыдови у Святош<small>Ђ</small>, заходилъ бо б<small>Ђ</small> Святоша рот<small>Ђ</small>; &laquo;аще поидеть на тя Святополкъ, пов<small>Ђ</small>мъ ти&raquo;; и не сътвори сего Святоша, но изоима муж<small>Ђ</small> Давыдовы, а самъ поиде на Давыда. И прииде Святоша и Путята августа въ 5 день, Давыдови облежащю градъ в полудне, а Давыдови спящю, и нападоша на н<small>Ђ</small> и начаша с<small>Ђ</small>щи; и горожане скочишася съ града, и почаша с<small>Ђ</small>щ<small>Ђ</small> вои Давыдовы, и поб<small>Ђ</small>же Давыдъ и Мьстиславъ, сыновець его. Святоша и Путята переяста городъ, и посадника Святополча Василья посадиста; и прииде Святоша Лучьску, а Путята Киеву.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Горожани ж, це почувши, задзвонили на віче, і сказали Давидові люди на вічі: &laquo;Видай мужів цих, ми не будемо битися за них, а за тебе можемо битися, а за них не б\'ємося. А якщо ні, то відчинимо городу ворота, і будеш сам турбуватися про себе&raquo;. І неволили видати їх. І сказав Давид: &laquo;Немає їх тут&raquo;. Бо послав був до Луцька. Із тих, що пішли до Луцька, Туряк утік у Київ, а Лазар і Василь повернулися в Турийськ. І почули люди, що вони в Турийську, і загукали люди на Давида, вимагаючи: &laquo;Видай, кого від тебе хотять, а якщо ні - здамося!&raquo; Давид же послав привести Василя і Лазаря і видав їх. І заключили мир у неділю, а на другий день у понеділок на світанку повісили Лазаря і Василя і розстріляли стрілами Васильковичі і пішли від города. Так удруге помстився, хоча й недобре було це робити. Треба було, щоб месником був Бог, і на нього возложити помсту свою, як каже пророк: &laquo;І воздам помсту ворогам і тим, що ненавидять мене, воздам, бо за кров синів своїх Бог помщається і мстить. І воздасть помсту ворогам і тим, хто ненавидить його, воздасть&raquo;. І коли ж ті відійшли, повішених зняли і похоронили.</p>\r\n<p>Святополк же, пообіцявши прогнати Давида, пішов у Берестя до ляхів. І коли почув про це Давид, пішов у Ляхи до Володислава шукати допомоги. Ляхи ж обіцяли допомогти, і взяли в нього п\'ятдесят гривен золота, і сказали йому: &laquo;Іди з нами до Берестя, туди кличе нас на раду Святополк, і там помиримо тебе із Святополком&raquo;. І послухав їх Давид і пішов до Берестя із Володиславом. І зупинився Святополк у городі, а ляхи на Бузі. І зносився Святополк із ляхами річкою, і дав їм дари великі за Давида. Володислав сказав Давидові: &laquo;Не слухає мене Святополк, так що вертайся назад&raquo;. І прийшов Давид у Володимир, а Святополк змовився із ляхами і пішов у Пінськ, пославши за воями. І прийшов у Дорогобуж, і дочекався тут воїв своїх, і пішов на Давида до города. Давид зачинився у городі, сподіваючись на допомогу ляхів проти Святополка, бо вони сказали йому: &laquo;Якщо на тебе прийдуть руськії князі, то ми будемо помічниками [тобі]&raquo;. Збрехали, а золото в Давида взяли. Святополк же оточив город, а Давид був у городі. І стояв Святополк біля города сім тижнів. І почав Давид проситися: &laquo;Пусти мене з города&raquo;. Святополк же пообіцяв йому, і поцілувавши хрест один другому, і вийшов Давид із города, і прийшов у Червен, а Святополк ввійшов у город у великодню суботу. Давид же втік у Ляхи.</p>\r\n<p>&nbsp;</p>\r\n<p>Святополк же прогнав Давида</p>\r\n<p>І став замишляти на Володаря</p>\r\n<p>І на Василька, кажучи: &laquo;То волость батька мого і брата&raquo;.</p>\r\n<p>І пішов на них,</p>\r\n<p>І довідалися про це Володар і Василько,</p>\r\n<p>І виступили назустріч,</p>\r\n<p>І взяли хрест, який цілував їм на тому:</p>\r\n<p>&laquo;На Давида я прийшов, а з вами хочу мати мир і любов&raquo;.</p>\r\n<p>І переступив Святополк хрест, надіючись на численних воїв.</p>\r\n<p>І зійшлися у полі на Рожні, ісполчились обидва.</p>\r\n<p>Василько ж возвисив хрест і сказав:</p>\r\n<p>&laquo;Ти його цілував і взяв у мене зір очей моїх,</p>\r\n<p>А це нині хочеш відняти і душу мою.</p>\r\n<p>Хай буде між нами хрест цей чесний!&raquo;</p>\r\n<p>І пішли один на одного в бій,</p>\r\n<p>І зійшлися полки,</p>\r\n<p>І багато людей благовірних бачили хрест,</p>\r\n<p>Піднятий дуже високо над Васильковими воями.</p>\r\n<p>Битви були жорстокі,</p>\r\n<p>І багато впало в обох полках,</p>\r\n<p>І побачив Святополк, яка лютує битва,</p>\r\n<p>І побіг у Володимир.</p>\r\n<p>І перемогли Володар і Василько, зупинилися там і сказали:</p>\r\n<p>&laquo;Належить нам на межі своїй стати&raquo;.</p>\r\n<p>І не пішли нікуди.</p>\r\n<p>Святополк же прибіг у Володимир,</p>\r\n<p>І з ним синів його два,</p>\r\n<p>І Святоша, син Давида Святославича,</p>\r\n<p>І рештки дружини.</p>\r\n<p>Святополк же посадив у Володимирі сина свого Мстислава,</p>\r\n<p>Народженого йому від наложниці,</p>\r\n<p>А Ярослава послав в Угри, підбити угріз іти на Володаря,</p>\r\n<p>А сам пішов у Київ.</p>\r\n<p>Ярослав, син Святополків, прийшов із уграми</p>\r\n<p>І королем Коломаном і двома єпископами.</p>\r\n<p>І стали вони біля Перемишля по Вагру,</p>\r\n<p>А Володар закрився в городі.</p>\r\n<p>Давид же в той час повернувся із Ляхів,</p>\r\n<p>І посадив жону свою у Володаря, а сам пішов у Половці.</p>\r\n<p>І зустрів його Боняк,</p>\r\n<p>І повернувся Давид,</p>\r\n<p>І пішли на угрів.</p>\r\n<p>І пішли вони,</p>\r\n<p>І стали на ночліг,</p>\r\n<p>І коли була північ</p>\r\n<p>І піднявся Боняк,</p>\r\n<p>І від\'їхав від раті,</p>\r\n<p>І став вити по-вовчому,</p>\r\n<p>І одвив йому вовк,</p>\r\n<p>І стало безліч вовків вити.</p>\r\n<p>Боняк же приїхав і повідав Давидові:</p>\r\n<p>&laquo;Наша буде перемога над уграми&raquo;.</p>\r\n<p>І ранком Боняк ісполчив воїв своїх -</p>\r\n<p>І Давидових 100 і у Боняка 300 стягів -</p>\r\n<p>І розділив їх на три полки,</p>\r\n<p>І рушив на угрів.</p>\r\n<p>І пустив наперед Алтунопу із 50-ма стягами,</p>\r\n<p>І Давида поставив під стягом,</p>\r\n<p>А сам розділився на два війська, по 50 стягів у кожному.</p>\r\n<p>Угри ж ісполчились на заступи,</p>\r\n<p>І було угрів числом сто тисяч.</p>\r\n<p>Алтунопа же пригнав до першого заступу,</p>\r\n<p>І, стріливши, помчав від угрів.</p>\r\n<p>Угри погналися за ними, думаючи, що то тікає Боняк,</p>\r\n<p>А Боняк гнався [сам], рубаючи їх по плечах.</p>\r\n<p>Алтунопа повернувся проти,</p>\r\n<p>І не допустили угрів назад,</p>\r\n<p>І так безліч перебили їх.</p>\r\n<p>Боняк же розділив військо на три полки</p>\r\n<p>І збив угрів у м\'яч, як сокіл галиці збиває.</p>\r\n<p>І побігли угри,</p>\r\n<p>І багато їх потопилось у Вагрі, а інші ж у Сяні,</p>\r\n<p>І тікали понад Сяном у гори,</p>\r\n<p>І спихали один другого,</p>\r\n<p>І гналися за ними два дні, рубаючи їх.</p>\r\n<p>Тут же убили біскупа їхнього Купана,</p>\r\n<p>І багатьох бояр,</p>\r\n<p>І, як говорять, загинуло смертю сорок тисяч.</p>\r\n<p>Ярослав же утік у Ляхи</p>\r\n<p>І прийшов у Берестя, а Давид захопив Сутійськ і Червен,</p>\r\n<p>І, прийшовши раптово, захопив Володимирці,</p>\r\n<p>А Мстислав закрився у граді з засадою,</p>\r\n<p>І вже були у нього берестяни, і піняни, вишегородці.</p>\r\n<p>І став Давид, оточивши город,</p>\r\n<p>І часто водив на приступ.</p>\r\n<p>Одного разу підійшли до граду під вежами,</p>\r\n<p>І коли били з города</p>\r\n<p>І стріляли одні в одних, то стріли ішли, як дощ.</p>\r\n<p>І коли Мстислав хотів стрілити, стоячи на заборолах,</p>\r\n<p>І несподівано через скважину вражений був під груди стрілою.</p>\r\n<p>І звели його зі стіни,</p>\r\n<p>І тієї ж ночі він помер.</p>\r\n<p>І тримали це в тайні три дні,</p>\r\n<p>А на четвертий день повідомили на вічі.</p>\r\n<p>І сказали люди: &laquo;От князя убито,</p>\r\n<p>А коли ще й ми здамося, то Святополк переб\'є нас&raquo;.</p>\r\n<p>І послали до Святополка сказати:</p>\r\n<p>&laquo;Син твій убитий, а ми знемагаємо від голоду.</p>\r\n<p>Якщо не прийдеш, люди будуть змушені здатися,</p>\r\n<p>Не можуть витерпіти голод&raquo;.</p>\r\n<p>Святополк же послав Путяту, свого воєводу.</p>\r\n<p>Путята ж прийшов із воями у Луцьк до Святоші, сина Давида.</p>\r\n<p>І були тут, у Святоші, мужі Давидові,</p>\r\n<p>Бо поклявся [Давидові] Святоша:</p>\r\n<p>&laquo;Якщо піде на тебе Святополк, повідомлю тобі&raquo;.</p>\r\n<p>І не зробив цього Святоша,</p>\r\n<p>Але схопив мужів Давидових, а сам пішов на Давида,</p>\r\n<p>І прийшли Святоша і Путята у п\'ятий день серпня,</p>\r\n<p>До обложеного Давидом граду у полуднє,</p>\r\n<p>А Давид спав.</p>\r\n<p>І напали на них, і почали сікти.</p>\r\n<p>І горожани вискочили з града,</p>\r\n<p>І почали сікти воїв Давидових,</p>\r\n<p>І побіг Давид і Мстислав, племінник його.</p>\r\n<p>Святоша і Путята зайшли у город,</p>\r\n<p>І посадником Святополкового Василя посадили.</p>\r\n<p>І прийшов Святоша у Луцьк, а Путята повернувся в Київ.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Давыдъ же поб<small>Ђ</small>же Половц<small>Ђ</small>, и уср<small>Ђ</small>те и Бонякъ Половц<small>Ђ</small>; и поиде Давыдъ и Бонякъ на Святошю у городу, и створиша миръ. И изиде Святоша из города, и прииде къ отцю своему Черн<small>Ђ</small>гову; а Давыдъ прия Луческъ, и оттуду прииде к Володимерю, посадникъ же Василь выб<small>Ђ</small>же из города, а Давыдъ перея Володимерь и с<small>Ђ</small>де в немь. А на другое л<small>Ђ</small>то снемь створиша князи Святополкъ, Володимеръ, Давыдъ и Олегъ, привабиша Давыда Игоревича, и не даша ему Володимеря, но даша ему Дорогобужь, у н<small>Ђ</small>мъ же и въмр<small>Ђ</small>; а Святополкъ перея Володимерь, и посади сына своего Ярослава.</p>\r\n<p>В л<small>Ђ</small>то 6606. Прииде Володимеръ, и Давыдъ, и Олегъ на Святополка, и сташа у Городца, и створиша миръ.</p>\r\n<p>В се же л<small>Ђ</small>то заложи Володимеръ церковь камяну свято<small>Ђ</small> Богородиц<small>Ђ</small>, Переяславли, на княж<small>Ђ</small> двор<small>Ђ</small>.</p>\r\n<p>Того же л<small>Ђ</small>та заложи Володимеръ Мономахъ городъ на Въстри.</p>\r\n<p>В л<small>Ђ</small>то 6607. Иде Святополкъ на Давыда [къ Володимерю и прогна Давыда] в Ляхы.</p>\r\n<p>В се же л<small>Ђ</small>то бысть знаменье надъ Володимеремь, м<small>Ђ</small>сяца априля: два круга, а в нею аки солнце, и до шестаго часа, а ночь аки 3 стязи св<small>Ђ</small>тл<small>Ђ</small>, оли до зорь.</p>\r\n<p>У се же л<small>Ђ</small>то побьени Угре, у Перемышля.</p>\r\n<p>В се же л<small>Ђ</small>то убьенъ бысть Мьстиславъ, сынъ Святополчь, у Володимери, м<small>Ђ</small>сяца июня 12 день.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Давид же побіг у Половці,</p>\r\n<p>І зустрів його Боняк половецький.</p>\r\n<p>І пішли Давид і Боняк на Святошу до города [Луцька],</p>\r\n<p>І уклали мир.</p>\r\n<p>І вийшов Святоша із города,</p>\r\n<p>І прийшов до батька свого в Чернігів,</p>\r\n<p>А Давид узяв Луцьк</p>\r\n<p>І звідти перейшов у Володимир.</p>\r\n<p>Посадник же Василь утік із города,</p>\r\n<p>А Давид перебрав Володимир і сів у ньому.</p>\r\n<p>А на друге літо зібрали князі раду:</p>\r\n<p>Святополк, Володимир, Давид і Олег,</p>\r\n<p>І привабили Давида Ігоревича</p>\r\n<p>І не дали йому Володимир, але дали йому Дорогобуж.</p>\r\n<p>У ньому ж і помер.</p>\r\n<p>А Святополк перебрав [у свої руки] Володимир</p>\r\n<p>І посадив [у ньому] сина свого Ярослава.</p>\r\n<p>&nbsp;</p>\r\n<p>В літо 6606 [1098]. Пішли Володимир, і Давид, і Олег на Святополка, і стали біля Городця, і уклали мир.</p>\r\n<p>У це ж літо заклав Володимир церкву кам\'яну святої Богородиці у Переяславі на княжому дворі.</p>\r\n<p>Того ж літа заснував Володимир Мономах город на Вострі.</p>\r\n<p>В літо 6607 [1099]. Ходив Святополк на Давида [у Володимир і прогнав Давида] в Ляхи.</p>\r\n<p>У це ж літо було знамення над Володимиром у місяці квітні: два кола, а в них неначе сонце, і так до шостої години, а на ніч, як три стяги світлі і аж до зорь.</p>\r\n<p>У це ж літо побили угрів під Перемишлем.</p>\r\n<p>У це ж літо був убитий Мстислав, син Святополків, у Володимирі місяця червня [у] дванадцятий день.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>[Въ л<small>Ђ</small>то 6608]. Вниде Мьстиславъ отъ Давыда на море, м<small>Ђ</small>сяца июня въ 10.</p>\r\n<p>Того же л<small>Ђ</small>та братья створиша миръ межи собою, Святополкъ, и Володимеръ, Давыдъ, и Олегъ, въ Ув<small>Ђ</small>тичихъ, м<small>Ђ</small>сяца августа въ 14 день. Того же м<small>Ђ</small>сяца въ 30, в томъ же м<small>Ђ</small>ст<small>Ђ</small>, братья вся сняшася, Святополкъ, Володимеръ, Олегъ, прииде к нимъ Давыдъ Игоревичь, и рече имъ: &laquo;начто мя есте привабили? осе есмь; кому до мене обида?&raquo; И отв<small>Ђ</small>ща к нему Володимеръ: &laquo;ты еси прислалъ к намъ, р<small>Ђ</small>ка: хощю, братье, приити къ вамъ и пожаловати своее обиды; да се еси пришелъ и с<small>Ђ</small>диши съ своею братьею на единомъ ковр<small>Ђ</small>: и чему не жалуеши, до кого ти обида?&raquo; и не отв<small>Ђ</small>ща ему ничтоже Давыдъ. И сташа уся братья на конихъ; и ста Святополкъ съ своею дружиною, а Давыдъ и Олегъ съ своею дружиною, раздно, кром<small>Ђ</small> себе, а Давыдъ Игоревичь с<small>Ђ</small>дяше опрочь, и не припустяху его к соб<small>Ђ</small>, и особ<small>Ђ</small> думаху о Давыд<small>Ђ</small>. И сдумавше послаша къ Давыдови мужи свои, Святополкъ Путяту, Володимеръ [Орогостя и] Ратибора, Давыдъ [и] Олегъ Торчина; послании же придоша къ Давыдови и рекоша ему: &laquo;се ти мовлять братья: не хощемъ ти вдати стола Володимерьскаго, зане увергъ еси ножь в ны, егоже не было в Русьской земли; но мы тебе не имемъ, ни иного зла створимъ, но се ти даемъ, шедъ сяди в Божескомъ, <em>в </em>Остроз<small>Ђ</small>; Дубенъ и Черторыескъ, то ти даеть Святополкъ, а се ти даеть Володимеръ 200 гривенъ, Давыдъ и Олегъ 200 гривенъ&raquo;. И тогда посла послы своя к Володареви и к Василкови: &laquo;поими брата своего Василка к соб<small>Ђ</small>, и буди вамъ Перемышль; да чего вамъ любо, да с<small>Ђ</small>д<small>Ђ</small>та, аще ли, да пусти Василка с<small>Ђ</small>мо, ат<small>Ђ</small> и кормимъ зд<small>Ђ</small>; а холопы наши и смерды выдаита&raquo;; и не послуша сего Володарь и Василко. А Давыдъ с<small>Ђ</small>дяше у Вожьскомъ, и посемь вда Святополкъ Давыдови Дорогобужь, в н<small>Ђ</small>мьже и вомре; а Володимеръ вдасть сынови своему Ярославу.</p>\r\n<p>В л<small>Ђ</small>то 6609. Преставися Всеславъ, Полотскый князь, м<small>Ђ</small>сяца априля въ 14 день, у 9 день, въ среду.</p>\r\n<p>В то же л<small>Ђ</small>то затворися Ярославъ Ярополчичь у Берестьи, и изиде на нь Святополкъ, и заступи и в город<small>Ђ</small>, и емь и, окова и приведе и до Киева; и молися о немь митрополитъ и игумени, умолиша Святополка, и узаводиша и у раку святою Бориса и Гл<small>Ђ</small>ба, и сняша с него оковы и пустиша и.</p>\r\n<p>В т<small>Ђ</small>мъ же л<small>Ђ</small>т<small>Ђ</small> съвокупишася братья: Святополкъ, и Володимеръ, Давыдъ, Олегъ, Ярославъ съ братьею, на Золотьчи; и пріслаша Половци послы ото вс<small>Ђ</small>хъ князь къ всей ратьи, глаголюще и просяще мира; и р<small>Ђ</small>ша имъ князи Русьсции: &laquo;аще хощете мира, да совокупимся у Сакова&raquo;; і послаша Половци, и сняшася в Сакова, и створиша миръ с Половци, и пояша таль межи собою, м<small>Ђ</small>сяца семьтября въ 15 день.</p>\r\n<p>В се же л<small>Ђ</small>то Володимеръ заложи церковь у Смоленьск<small>Ђ</small> свято<small>Ђ</small> Богородиц<small>Ђ</small> камяну, епискупью.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6608 [1100]. Пішов Мстислав від Давида на море місяця червня в десятий [день].</p>\r\n<p>Того ж літа брати уклали мир між собою - Святополк, і Володимир, Давид, і Олег - в Увітичах, місяця серпня в чотирнадцятий день. Того ж місяця в 30-й день на тому ж місці брати всі зібралися - Святополк, Володимир, Олег,- і прийшов до них Давид Ігоревич і сказав їм: &laquo;Нащо ви мене привабили? От я. У кого на мене обида?&raquo; І відповів йому Володимир: &laquo;Ти сам прислав до нас, кажучи: &laquo;Хочу, браття, прийти до вас і поскаржитися за свої обиди&raquo;. Так от ти прийшов і сидиш зі своїми братами на одному коврі, так чому не скаржишся, до кого твоя обида?&raquo; І не відповів йому Давид нічого. І стали всі браття на коней. І став Святополк зі своєю дружиною, а Давид і Олег зі своєю дружиною нарізно один від другого. А Давид Ігоревич сидів осторонь, і не підпустили його до себе, але окремо радились про Давида. І, порадившись, послали до Давида мужів своїх: Святополк - Путяту, Володимир - [Орогостя і] Ратибора, Давид [і] Олег - Торчина. Посланії ж прийшли до Давида і сказали йому: &laquo;От що говорять тобі браття: &laquo;Не хочемо тобі дати стіл Володимирський, тому що ти ввігнав ніж у нас, чого не було в Русьській землі. Але ми тебе хапати не будемо і якогось іншого зла не зробимо. Але от що тобі дамо: іди і сідай у Божеському, в Острозі. Дубен і Чорторийськ дає тобі Святополк, а Володимир ось дає тобі двісті гривен, Давид і- Олег - двісті гривен&raquo;. І тоді послали послів своїх до Володаря і Василька: &laquo;Візьми брата свого Василька до себе, і буде вам Перемишль. Якщо це вам любо, то сидіть, а якщо ні, то відпусти Василька сюди, до нас, і годуватимемо тут. А холопів наших і смердів видайте&raquo;. І не послухали цього Володар, і Василько. А Давид сидів у Божському, і після цього віддав Святополк Давидові Дорогобуж, у ньому ж він і помер. А Володимир віддав синові своєму Ярославу.</p>\r\n<p>В літо 6609 [1101]. Помер Всеслав, полоцький князь, місяця квітня в чотирнадцятий день, о 9 [годині] дня, в середу.</p>\r\n<p>У те ж літо закрився Ярослав Ярополчич у Бересті, і пішов на нього Святополк, і застав його в городі, і схопив його, окував і привів до Києва. І просили за нього митрополит і ігумени, і вблагали Святополка, і взяли клятву з нього біля раки святих Бориса і Гліба, і зняли з нього окови, і відпустили його.</p>\r\n<p>В тім же літі зібралися браття: Святополк, і Володимир, Давид, Олег, Ярослав із браттями на Золотчі. І прислали половці послів від усіх князів до всіх братів сказати, що просять миру. І відповіли їм князі Русьськії: &laquo;Якщо хочете миру, то зберемося біля Сакова&raquo;. І послали половці, і зібралися біля Сакова, і створили мир з половцями, і взяли заложників один у другого місяця вересня у п\'ятнадцятий день.</p>\r\n<p>У це ж літо Володимир заклав у Смоленську церкву святої Богородиці, кам\'яну, єпископську.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6610. Выб<small>Ђ</small>же Ярославъ Ярополчичь ис Кыева, м<small>Ђ</small>сяца октября въ 1 день. Того же м<small>Ђ</small>сяца на исход<small>Ђ</small>, перьльсти Ярославъ Святополчичь Ярослава Ярополчича, ятъ и на Нури, и приведе къ отцю Святополку, и оковаша и.</p>\r\n<p>Того <em>же </em>л<small>Ђ</small>та, м<small>Ђ</small>сяца октября у 20, приде Мьстиславъ, сынъ Володимерь, с Новгородци, б<small>Ђ</small> бо Святополкъ с Володимеромъ рядъ им<small>Ђ</small>лъ, яко Новугороду быти Святополчю и посадити сынъ свой [в Нов<small>Ђ</small>город<small>Ђ</small>, а въ Володімери сына своего посадити] Володимеру; и прииде Мьстиславъ Кыеву, и с<small>Ђ</small>доша в ыстобц<small>Ђ</small>, и рекоша мужи Володимери: &laquo;се присла Володимеръ сына своего, да се с<small>Ђ</small>дять Новгородц<small>Ђ</small>; да поемьше сына твоего идуть Новугороду, а Мьстиславъ да идеть Володимерю&raquo;. И рекоша Новгородци Святополку: &laquo;се мы, княже, прислани к тоб<small>Ђ</small>, и рекли намъ тако: не хощемъ Святополка, ни сына его; аще ли дв<small>Ђ</small> голов<small>Ђ</small> им<small>Ђ</small>ть сынъ твой, то посли и; сего ны далъ Всеволодъ, ускормили есмы соб<small>Ђ</small> князя, а ты еси шелъ отъ насъ&raquo;. Святополкъ же многу им<small>Ђ</small> прю с ними, он<small>Ђ</small>мь же не восхот<small>Ђ</small>вшимъ, поемьше Мьстислава поидоша Новугороду.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6610 [1102]. Утік Ярослав Ярополчич із Києва місяця жовтня у перший день. Наприкінці того ж місяця обманув Ярослав Святополчич Ярослава Ярополчича, схопив його на Нурі і привів до батька Святополка, і окували його. Того ж літа, місяця жовтня у двадцятий день прийшов Мстислав, син Володимирів, із новгородцями, бо Святополк із Володимиром угоду мали, що Новгород буде Святополків і що він посадить сина свого [в Новігороді, а у Володимирі посадить свого сина] Володимир. І прийшов Мстислав у Київ, і посідали в істобці, і сказали мужі Володимирови: &laquo;Це прислав Володимир сина свого, а це сидять новгородці. Хай забирають сина твого і йдуть у Новгород, а Мстислав хай іде в Володимир&raquo;. І сказали новгородці Святополку: &laquo;Це ми, княже, прислані до тебе, і сказали нам так: не хочемо ні Святополка, ні сина його. Якщо ж дві голови має син твій, то посилай його. Цього ж [Мстислава] нам дав Всеволод, і викормили ми самі собі князя, а ти пішов від нас&raquo;. Святополк же довго сперечався з ними, але вони не захотіли, взяли Мстислава і пішли в Новгород.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В то же л<small>Ђ</small>то бысть знаменье на небеси, м<small>Ђ</small>сяца генваря 29, по 3 дни: аки пожарная зоря отъ въстока и уга и запада и с<small>Ђ</small>вера, и бысть тако св<small>Ђ</small>тъ всю нощь, акы отъ луны полны св<small>Ђ</small>тящеся.</p>\r\n<p>В то же л<small>Ђ</small>то бысть знаменье у лун<small>Ђ</small>, м<small>Ђ</small>сяца февраля въ 5 день. Того же м<small>Ђ</small>сяца въ 7 день бысть знаменье въ солнц<small>Ђ</small>: огородилося бяше солнце въ 3 дуги, и быша другыя дугы хрепты к соб<small>Ђ</small>. И сия видяще знаменья, благов<small>Ђ</small>рн<small>Ђ</small>и челов<small>Ђ</small>ци съ въздыханьемь моляхуся Богу съ слезами, да бы Богъ обратилъ знаменья си на добро: знаменья бо бывають ово же на добро, ово же на зло; яко и си знаменья быша на добро. На пр<small>Ђ</small>идущее л<small>Ђ</small>то вложи Богъ мысль добру в Русьскии князи, умыслиша дерзнути на Половц<small>Ђ</small>, поити в землю ихъ, еже и бысть; якоже скажемъ въ пришедшее л<small>Ђ</small>то.</p>\r\n<p>В се же л<small>Ђ</small>то преставися Володиславъ, Лядьский князь.</p>\r\n<p>В се же л<small>Ђ</small>то преставися Ярославъ Ярополчичь, м<small>Ђ</small>сяца августа въ 11 день.</p>\r\n<p>В се же л<small>Ђ</small>то ведена бысть дщи Святополча Сбыслава в Ляхы за Болеслава, м<small>Ђ</small>сяца ноября въ 16 день.</p>\r\n<p>В то же л<small>Ђ</small>то родися у Володимера сынъ Андр<small>Ђ</small>й,</p>\r\n<p>В л<small>Ђ</small>то 6611. Вложи Богъ ву серьдце Русьскимъ княземъ мысль благу, Святополку [и] Володимеру, и снястася думати на Долобьск<small>Ђ</small>; и с<small>Ђ</small>де Святополкъ съ своею дружиною, а Володимеръ съ своею дружиною, а въ единомъ шатр<small>Ђ</small>. И поча думати, и начаша глаголати дружина Святополча: &laquo;не веремя весн<small>Ђ</small> воевати, хочемь погубити смерды, и ролью имъ&raquo;. И рече Володимеръ: &laquo;дивно ми, дружино, оже лошади кто жалуеть, еюже ореть кто; а сего чему не расмотрите, оже начнеть смердъ орати, и Половчинъ пра<small>Ђ</small>ха ударить смерда стр<small>Ђ</small>лою, а кобылу его поиметь, а в село въ<small>Ђ</small>хавъ поиметь жену его и д<small>Ђ</small>ти, и все им<small>Ђ</small>нье его возметь? то лошади его жалуешь, а самого чему не жалуешь?&raquo; И не могоша противу ему отв<small>Ђ</small>щати дружина Святополча, и рече Святополкъ: &laquo;брате, се азъ готовъ уже&raquo;, и въста Святополкъ; и рече ему Володимеръ: &laquo;то ти, брате, велике добро створиши Русьской земли&raquo;. И посласта къ Давыдови и к Олгови, глаголюща: &laquo;поидита на Половщи, а любо будемь живи, любо мертви&raquo;; Давыдъ же послуша ею, а Олегъ не послуша сего, вину река: &laquo;не здоровлю&raquo;. Володимеръ же ц<small>Ђ</small>ловавъ брата своего, поиде Переяславлю, а Святополкъ по немь, и Давыдъ Святъславичь, и Давыдъ Всеславичь, и Мьстиславъ Игоревъ унукъ, Вячьславъ Ярополчичь, Ярополкъ Володимеричь; и поидоша на конихъ и в лодьяхъ, и приидоша ниже порогъ, и сташа въ протолчехъ и в Хортичимъ остров<small>Ђ</small>; і вс<small>Ђ</small>доша на кон<small>Ђ</small>, и п<small>Ђ</small>шьци из лод<small>Ђ</small>й выс<small>Ђ</small>давше идоша в поле 4 дни, и придоша на Сутинъ. Половци же слышавше, яко идуть Русь, и собрашася бес числа и начаша думати; и рече Русоба: &laquo;просимъ мира в Руси, яко кр<small>Ђ</small>пко ся имуть бити с нами, мы бо много зла створихомъ Руской земли&raquo;.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>У те ж літо було знамення на небесах, місяця січня 29-го протягом трьох днів: неначе заграви пожежі із сходу, і півдня, і заходу, і півночі; і було так світло всю ніч, як від повного місяця світиться.</p>\r\n<p>У те ж літо на місяці було знамення місяця лютого в 5-й день. Того ж місяця на сьомий день було знамення на сонці: огородилося було сонце трьома дугами, хребтами одна до другої. І коли дивилися на це знамення благовірні люди, із зітханнями і в сльозах молилися Богу, щоб Бог повернув ці знамення на добро: адже знамення бувають одні на добро, другі ж на зло, то щоб і ці знамення були на добро. Наступного літа вселив Бог мисль добру Руським князям: задумали дерзнуть проти половців, піти в землю їхню, що і було, про що й скажемо під наступним літом.</p>\r\n<p>Цього ж літа помер Володислав, лядський князь.</p>\r\n<p>Цього ж літа помер Ярослав Ярополчич місяця серпня в 11-й день.</p>\r\n<p>Цього ж літа відвели дочку Святополкову Збиславу в Ляхи за Болеслава місяця листопада в 16-й день.</p>\r\n<p>У те ж літо народився у Володимира син Андрій.</p>\r\n<p>&nbsp;</p>\r\n<p>В літо 6611 [1103].</p>\r\n<p>І поклав Бог у серце Русьським князям думку добру,</p>\r\n<p>І Святополку, і Володимиру.</p>\r\n<p>І зійшлися на раду вони у Долобську,</p>\r\n<p>І сів Святополк із своєю дружиною,</p>\r\n<p>І Володимир із своєю дружиною</p>\r\n<p>І в одному шатрі.</p>\r\n<p>І почали вони думати.</p>\r\n<p>І почала казати дружина Святополкова:</p>\r\n<p>&laquo;Не час весною воювати: хіба щоб смердів погубити</p>\r\n<p>І ріллю їхню&raquo;.</p>\r\n<p>І сказав Володимир: &laquo;Дивуюсь, дружино,</p>\r\n<p>Що коней шкодуєте, якими орють,</p>\r\n<p>А про те чомусь не говорите, що як смерд розпочне орати,</p>\r\n<p>А половчин над\'їде, вдарить смерда стрілою,</p>\r\n<p>А кобилу його захопить,</p>\r\n<p>А в оселю заскочить - зловить жінку його і діти,</p>\r\n<p>І майно його усе візьме.</p>\r\n<p>То його коня для нас так жалко,</p>\r\n<p>А самого смерда не жалуєш?&raquo;</p>\r\n<p>І не могла заперечить йому дружина Святополкова,</p>\r\n<p>І сказав Святополк: &laquo;Брате, от я і готовий вже&raquo;,</p>\r\n<p>І встав Святополк.</p>\r\n<p>І сказав йому Володимир:</p>\r\n<p>&laquo;То ти, брате, велике добро землі Руській створиш&raquo;.</p>\r\n<p>І послали вони до Давида,</p>\r\n<p>І до Олега [послали] сказати:</p>\r\n<p>&laquo;Ідіть на половців: або будемо живі, або мертві&raquo;.</p>\r\n<p>І послухався їх Давид, а Олег не послухав того,</p>\r\n<p>І причину назвав: &laquo;нездоровий&raquo;,</p>\r\n<p>І цілував брата свого Володимир</p>\r\n<p>І пішов [у свій] Переяслав, а за ним Святополк,</p>\r\n<p>І Давид Святославич,</p>\r\n<p>І Давид Всеславич,</p>\r\n<p>І Мстислав, онук Ігорів, Вячеслав Ярополчич,</p>\r\n<p>І Ярополк Володимирич.</p>\r\n<p>І пішли на конях і в лодіях,</p>\r\n<p>І спустилися нижче порогів,</p>\r\n<p>І стали в Протолчах і на острові Хортичім,</p>\r\n<p>І посідали на коней.</p>\r\n<p>І піхотинці, із лодій зійшовши,</p>\r\n<p>Ішли полем чотири дні,</p>\r\n<p>І прийшли на Сутин.</p>\r\n<p>І зачули половці, що Русь іде,</p>\r\n<p>І зібралися незліченою силою,</p>\r\n<p>І стали думати.</p>\r\n<p>І сказав Русоба: &laquo;Будем миру у Русі прохати,</p>\r\n<p>Бо запекло будуть руси з нами битись,</p>\r\n<p>Бо чимало зла землі Руській вчинили&raquo;.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>И р<small>Ђ</small>ша уншии Урусоб<small>Ђ</small>: &laquo;аще ся ты боиши Руси, но мы ся не боимъ; сихъ бо избивше, и поидемь в землю ихъ н приимемъ вся грады ихъ, и кто избавить ихъ отъ насъ?&raquo; Рустии же князи и вои моляху Бога, и об<small>Ђ</small>ты въздаяху Богу и пречистей его Матери, ово кутьею, овъ же милостынею къ убогымъ, ови же монастыремъ требованья. И сице молящимся, поидоша Половьци, и посла передъ собою въ сторож<small>Ђ</small> Алтунопу, иже словяше мужьствомъ; такоже и Русьстии князи послаша сторож<small>Ђ</small> свои, и въстерегоша Алтунопу, и объступиша Алтунопу, и въбиша и и сущая с нимъ; ни единъ же избы отъ нихъ, но вся избиша. И поидоша полци Половецьстии аки борове, и не б<small>Ђ</small> перезрити ихъ; и Русь поидоша противу имъ. И великий Богъ вложи в жалость велику у Половц<small>Ђ</small>, и страхъ нападе на ня и трепетъ отъ лица Русьскыхъ вой, и др<small>Ђ</small>маху сам<small>Ђ</small>, и конемъ ихъ не бяше сп<small>Ђ</small>ха у ногахъ; Русь же с весельемь на конихъ и п<small>Ђ</small>ши потекоша к нимъ. Половци же видивше устремленье Руское на ся, не доступивше поб<small>Ђ</small>гоша передъ Рускыми князи; наши же погнаша, с<small>Ђ</small>куще я, въ 4 м<small>Ђ</small>сяца априля. И великое спасенье створи Богъ въ тъ день благов<small>Ђ</small>рнымъ княземъ Русьскымъ и всимъ хрестьяномъ, а на врагы наш<small>Ђ</small> дасть поб<small>Ђ</small>ду велику.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>І відповіли молодші Урусобі:</p>\r\n<p>&laquo;Якщо ти Русі боїшся, то ми не боїмося,</p>\r\n<p>Цих переб\'ємо,</p>\r\n<p>І підем в землю їхню,</p>\r\n<p>І захопимо всі городи ми їхні,</p>\r\n<p>І ніхто від нас не збавить їх&raquo;.</p>\r\n<p>Руськії ж князі і вої помолились Богу,</p>\r\n<p>І обітниці давали Богу,</p>\r\n<p>І пречистій його Матері: одні - кутею,</p>\r\n<p>Інші - милостинею убогим, другі - монастирям на потребу.</p>\r\n<p>І коли вони отак молились, половці прийшли</p>\r\n<p>І послали перед себе у сторожу Алтунопу,</p>\r\n<p>Що уже був мужністю прославивсь.</p>\r\n<p>І руські князі свою сторожу теж послали,</p>\r\n<p>І підстерегли, і обступили Алтунопу, його вбили і всіх сущих з ним,</p>\r\n<p>І не одного його вбили, а всіх побили,</p>\r\n<p>І рушили полки половецькі, як бори,</p>\r\n<p>І не можна було їх окинути оком,</p>\r\n<p>І Русь рушила супроти них.</p>\r\n<p>І великий Бог вселив жалість велику у половців,</p>\r\n<p>І напав на них страх і трепіт від вигляду руських воїв,</p>\r\n<p>І самі вони ніби в дрімку запали,</p>\r\n<p>І не стало у ногах у коней їхніх спіху, а Русь весело</p>\r\n<p>І кіньми і пішо на них потекла.</p>\r\n<p>І коли половці узріли стрім русів проти себе</p>\r\n<p>І, не доступившись [полків руських], побігли перед руськими князями,</p>\r\n<p>І погналися за ними наші,</p>\r\n<p>І посікли їх у день четвертий квітня.</p>\r\n<p>І велике Бог в той день послав спасіння</p>\r\n<p>І князям всім руським благовірним,</p>\r\n<p>І усім благочестивим християнам,</p>\r\n<p>І над ворогами нашими дав перемогу велику.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>И убиша ту в полку князий 20: Урусобу, Кочия, Яросланопу, Китанопу, Кунама, Асупа, Курътыка, Ченегрепа, Сурьбарь и прочая князя ихъ; а Вельдуза яша. Посемь же с<small>Ђ</small>доша братья, поб<small>Ђ</small>дивше враги своя; и приведоша Белдузя къ Святополку, и нача Белдузь даяти на соб<small>Ђ</small> злато и сребро, и кон<small>Ђ</small> и скотъ; Святополкъ же посла Володимеру. И пришедшу ему, упроси его Володимеръ: &laquo;то веде, яла вы рота? многажды бо ходивше рот<small>Ђ</small>, воевасте Русьскую землю; то чему ты не училъ сыновъ своихъ и роду своего не переступати рот<small>Ђ</small>, но проливаете кровь хрестьянску? да се буди кровь твоя на глав<small>Ђ</small> твоей&raquo;; и повели убити и, тако рас<small>Ђ</small>коша и на удъ. И посемь сняшася братья вся, і рече Володимеръ: &laquo;се день, иже створи Господь, възрадуемься и възвеселимься в онь; яко Богъ избавилъ ны есть отъ врагъ нашихъ, и покори враги наша, и скруши главы зм<small>Ђ</small>евыя, и далъ есть Господь брашно ихъ намъ&raquo;; взяша бо тогда скота и овц<small>Ђ</small> и кони и вельблуды, и веж<small>Ђ</small> с добыткомъ и съ челядью, и заяша Печен<small>Ђ</small>ги и Торъки с вежами. И приидоша в Русь с полономъ великымъ, и съ славою и с поб<small>Ђ</small>дою великою усвояси.</p>\r\n<p>Того же л<small>Ђ</small>та приидоша прузии, августа въ 1 день.</p>\r\n<p>Томъ же л<small>Ђ</small>т<small>Ђ</small>, того же м<small>Ђ</small>сяца въ 18 день, иде Святополкъ и сруби Гурьговъ, егоже б<small>Ђ</small>ша пожьгл<small>Ђ</small> Половци.</p>\r\n<p>Того же л<small>Ђ</small>та бися Ярославъ с Моръдвою, м<small>Ђ</small>сяца марта въ 4 день, и поб<small>Ђ</small>женъ бысть Ярославъ.</p>\r\n<p>В л<small>Ђ</small>то 6612. Ведена дщи Володарева за царевича за Олексинича, Царюграду, м<small>Ђ</small>сяца июля въ 20.</p>\r\n<p>В томъ же л<small>Ђ</small>т<small>Ђ</small> ведена Передъслава, дщи Святополча, во Угры, за королевича, м<small>Ђ</small>сяца августа въ 21.</p>\r\n<p>Того же л<small>Ђ</small>та прииде митрополитъ Никифоръ в Русь, м<small>Ђ</small>сяца декабря въ 6 день.</p>\r\n<p>В томъ же л<small>Ђ</small>т<small>Ђ</small> преставися Вячьславъ Ярополчичь, въ 13 день. Никифоръ митрополитъ посаженъ на стол<small>Ђ</small>.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>І убили у тім поході двадцять князів [половецьких]: Урусобу, Кочія, Яросланопу, Китанопу, Кунама, Асупа, Куртика, Ченегрепа, Сурбара і інших князів їхніх, а Белдузя взяли [у полон]. Після цього ж посідали браття, перемігши ворогів своїх. І привели Белдузя до Святополка, і почав Белдузь давати за себе золото, і срібло, і коні, і худобу. Святополк же послав за Володимиром. І коли прийшов, запитав у Белдузя Володимир: &laquo;Чи ти знаєш, що здолала вас клятва? Безліч раз ви клялися і все ж воювали Руськую землю? То чому ж ти не вчив синів своїх і роду свого не переступати клятву, але проливаєте кров християнську? Хай же буде кров твоя на голові твоїй&raquo;. І повелів убити його, так і розсікли його на шматки.</p>\r\n<p>І після цього зібралися браття всі, і сказав Володимир: &laquo;Це день, що його створив Господь, возрадуємося і возвеселимося в цей день, тому що Бог звільнив нас від ворогів наших, і покорив ворогів наших, і сокрушив голови змієві, і віддав Господь їхнє багатство нам&raquo;. Взяли-бо тоді худобу, і вівці, і коні, і верблюди, і вежі із добрами, і з челяддю, і захопили печенігів і торкіз із вежами. І повернулись на Русь з полоном великим, і з славою, і з перемогою великою додому.</p>\r\n<p>Того ж літа прийшла саран.ча серпня в 1-й день.</p>\r\n<p>Того ж літа і того ж місяця в 18-й день пішов Святополк і відбудував Гургів, який спалили були половці.</p>\r\n<p>Того ж літа бився Ярослав із мордвою місяця березня в 4-й день, і переможений був Ярослав.</p>\r\n<p>В літо 6612 [1104]. Відвели дочку Володареву в Цареград за царевича Олексинича, місяця липня в 20-й [день].</p>\r\n<p>У тому ж літі відвели Передславу, дочку Святополка, в Угри за королевича місяця серпня в 21-й [день].</p>\r\n<p>Того ж літа прийшов у Русь митрополит Никифор місяця грудня в 6-й день.</p>\r\n<p>У тому ж літі помер Вячеслав Ярополчич, на 13-й день. Никифор митрополит посажений на столі.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Сего же л<small>Ђ</small>та исходяща, посла Святополкъ Путяту на Менескъ, а Володимеръ посла сына своего Ярополка, а Олегъ самъ иде на Гл<small>Ђ</small>ба, поемъше Давыда Всеславича; и не усп<small>Ђ</small>ша ничтоже, и възвратишася опять. И родися у Святополка сынъ, и нарекоша имя ему Брячиславъ.</p>\r\n<p>В се же л<small>Ђ</small>то бысть знаменье: стояще солнце въ круз<small>Ђ</small>, а посред<small>Ђ</small> круга хрестъ, а посред<small>Ђ</small> креста солнце, а вн<small>Ђ</small> круга оба полы 2 солнца, надъ солнцемъ же кром<small>Ђ</small> круга дуга, рогома на с<small>Ђ</small>веръ; тако же знаменье в лун<small>Ђ</small> т<small>Ђ</small>мь же образомъ, м<small>Ђ</small>сяца февраля въ 4 и 5 и 6 день, въ дне по три дни, а в ночи и в лун<small>Ђ</small> по три ночи.</p>\r\n<p>В л<small>Ђ</small>то 6613. Увалися верхъ святаго Андр<small>Ђ</small>я.</p>\r\n<p>В се же л<small>Ђ</small>то постави митрополитъ Анфилохыя епископа Володимерю, августа въ 27.</p>\r\n<p>Томъ же л<small>Ђ</small>т<small>Ђ</small> постави Лазоря Переяславлю, м<small>Ђ</small>сяца ноября 12. Томъ же л<small>Ђ</small>т<small>Ђ</small> постави Мину Полотьск<small>Ђ</small>, м<small>Ђ</small>сяца декабря в 13 день.</p>\r\n<p>Томъ же л<small>Ђ</small>т<small>Ђ</small> явися зв<small>Ђ</small>зда с хвостомъ, на запад<small>Ђ</small>, и стоя м<small>Ђ</small>сяць.</p>\r\n<p>Того же л<small>Ђ</small>та пришедъ Бонякъ зим<small>Ђ</small> на Заруб<small>Ђ</small> и поб<small>Ђ</small>ди Торкы и Береньд<small>Ђ</small><small>Ђ</small>.</p>\r\n<p>В л<small>Ђ</small>то 6614. Повоеваша Половци около Зар<small>Ђ</small>чьска и посла по нихъ Святополкъ Яня Вышатича, и брата его Путяту, Иванка Захарьича, и Козарина, и угонивьше Половц<small>Ђ</small> до Дуная, полонъ отъяша, а Половц<small>Ђ</small> исъс<small>Ђ</small>коша.</p>\r\n<p>В се же л<small>Ђ</small>то преставися Янь, старець добрый, живъ л<small>Ђ</small>тъ 90, въ старост<small>Ђ</small> мастит<small>Ђ</small>; живъ по закону Божию, не хужии первыхъ праведникъ, у него же азъ слышахь многа словеса, яже вписахъ в л<small>Ђ</small>тописиць; б<small>Ђ</small> бо мужь благъ, и кротокъ, и см<small>Ђ</small>ренъ, отгребаяся отъ всякоя вещи, его же и гробъ есть в Печерьскомь монастыр<small>Ђ</small>, у притвор<small>Ђ</small>, ид<small>Ђ</small>же лежить т<small>Ђ</small>ло его положено м<small>Ђ</small>сяца июня въ 24.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>У кінці цього ж літа послав Святополк Путяту на Менеськ, а Володимир послав сина свого Ярополка, а Олег сам пішов на Гліба [мінського], узявши Давида Всеславича. І нічого не добилися, і повернулися назад. І родився у Святополка син, і нарекли ім\'я йому Брячислав.</p>\r\n<p>У це ж літо було знамення: стояло сонце в крузі, а посеред круга хрест, а посеред хреста сонце, а поза кругом з обох боків два сонця, над сонцем же, окрім круга,- дуга, рогами на північ. Таке ж знамення було і на місяці, і таким же чином місяця лютого в четвертий, п\'ятий і шостий день, вдень [на сонці], а вночі на місяці по три дні і три ночі підряд.</p>\r\n<p>В літо 6613 [1105]. Завалився верх [церкви] святого Андрія.</p>\r\n<p>У це ж літо поставив митрополит єпископом у Володимир Анфілохія серпня в 27-й [день].</p>\r\n<p>Того ж літа поставив [він єпископом] Лазаря у Переяслав місяця листопада в 12-й [день].</p>\r\n<p>У тому ж літі поставив Мину [єпископом] у Полоцьк місяця грудня в 13-й день.</p>\r\n<p>Того ж літа з\'явилася на заході звізда з хвостом і стояла місяць.</p>\r\n<p>Того ж літа взимку прийшов Боняк на Заруб і переміг торків і берендеїв.</p>\r\n<p>В літо 6614 [1106]. Повоювали половці побіля Зарічеська і послав на них Святополк Яня Вишатича і брата його Путяту, Іванка Захарича і Козарина. І гнали половців до Дунаю, полон відібрали, а половців порубали.</p>\r\n<p>У це ж літо помер Янь, старець добрий, жив літ дев\'яносто, в старості маститий: жив по закону Божому, не гірший перших праведників, від нього ж я чув багато словес, які і вписав у літописець. Був же він муж добрий, і покірливий, і смиренний, уникав всіляких тяжб, його ж і гріб є в Печерському монастирі, у притворі, де лежить тіло його, покладене місяця червня в 24-й [день].</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В то же л<small>Ђ</small>то пострижеся Еупракси, Всеволожа дщи, м<small>Ђ</small>сяца декабря въ 6 день.</p>\r\n<p>Того же л<small>Ђ</small>та помраченье бысть въ солнци, августа. [Тогда пострижеся князь Святоша, сынъ Давыдовъ, внукъ Святославль Никола, м<small>Ђ</small>сяца февраля въ 17].</p>\r\n<p>[В томъ же л<small>Ђ</small>т<small>Ђ</small> поб<small>Ђ</small>діша Зим<small>Ђ</small>гола Всеславича и всю братію и дружину убиша ихъ 9 тысячь.</p>\r\n<p>В л<small>Ђ</small>то 6615 кругъ луны 4, а солнечного] 8 л<small>Ђ</small>то.</p>\r\n<p>В се же время преставися Володимеря княгини, м<small>Ђ</small>сяца маия въ 7 день. Того же м<small>Ђ</small>сяца воева Бонякъ и зая кон<small>Ђ</small> у Переяславл<small>Ђ</small>.</p>\r\n<p>Томъ же л<small>Ђ</small>т<small>Ђ</small> прииде Бонякъ, и Шарукань старый, и ини князи мнози, и сташа около Лубна; Святополкъ же, и Володимеръ, и Олегъ, Святославъ, Мьстиславъ, Вячьславь, Ярополкъ, идоша на Половц<small>Ђ</small> къ Лубьну, въ 6 часъ дне бродишася черес Сулу, и кликоша на н<small>Ђ</small>. Половци же вжасошася, отъ страха не възмогоша и стяга поставити, но поб<small>Ђ</small>гоша хватаючи конии, [а друзіи п<small>Ђ</small>ши поб<small>Ђ</small>гоша, наши же начаша с<small>Ђ</small>щи я, а другыа руками имати и гнаша я до Хорола. Убиша же Тааза, Бонякова брата, а Сугра яша и братію его, а Шурукань одва утече, отб<small>Ђ</small>гоша же товара своего, и взяша Рускіи вои, м<small>Ђ</small>сяца августа въ 12 день, и възвратишася въсвоаси съ поб<small>Ђ</small>дою в<small>Ђ</small>ликою. Святополкъ же пріиде] завътреню въ Печерьскый манастырь, на Успенье святыя Богородица, и братья ц<small>Ђ</small>ловаша и [съ] радостью великою, яко врази наши поб<small>Ђ</small>жены быша, молитвами святыя Богородица и великого Федосья отца нашего; и тако бо обычай имяше Святополкъ: коли идяше на войну, или инамо, оли поклонився въ гроба Федосьева и молитву вземъ у игумена сущаго, тоже идяше на путь свой.</p>\r\n<p>В то же л<small>Ђ</small>то преставися княгини, Святополча мати, м<small>Ђ</small>сяца генваря въ 4 день.</p>\r\n<p>Томъ же л<small>Ђ</small>т<small>Ђ</small>, того же м<small>Ђ</small>сяца, иде Володимеръ, и Давыдъ, и Олегъ к Аяп<small>Ђ</small> и другому Аеп<small>Ђ</small>, и створиша миръ; и поя Володимеръ за Гергия Епиопину дщерь! Яс<small>Ђ</small>ну внуку, а Олегъ поя [за сына] Акаспиду дщерь, Яневу внуку, м<small>Ђ</small>сяца генваря во вторы на 10 день.</p>\r\n<p>М<small>Ђ</small>сяца... въ 15 день трясеся земля передъ лазорями.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Того ж літа постриглася [в черниці] Євпраксія, Всеволодова дочка, місяця грудня в 6-й день.</p>\r\n<p>Того ж літа було затемнення сонця, у серпні.</p>\r\n<p>Тоді ж постригся [в ченці] князь Святоша, син Давидів, внук Святославів, [під іменем] Никола місяця лютого в 17-й [день].</p>\r\n<p>[У тому ж літі перемогла зимигола Всеславича і всю братію, і дружину їхню убила - дев\'ять тисяч.</p>\r\n<p>В літо 6615 [1107] місячний круг 4, а сонячного] 8-ме літо. У це ж врем\'я померла княгиня [жона] Володимира, місяця травня в 7-й день. Того ж місяця воював Боняк і захопив коні в Переяславі.</p>\r\n<p>Того ж літа прийшов Боняк, і Шарукань старий, і інших князів багато, і стали біля Лубна; Святополк же, і Володимир, і Олег, Святослав, Мстислав, Вячеслав, Ярополк пішли на половців до Лубна і о шостій годині дня перейшли вбрід Сулу і гукнули на них. Половці ж вжахнулися, від страху не могли і стяги поставити, а побігли, хватаючи коні, [а другі пішки побігли, а наші почали сікти їх, а інших руками ловили і гнали їх до Хорола. Убили Тавза, Бонякового брата, а Сугра і братів його взяли у полон, а Шарукан ледве утік. Позбулися половці й обозу свого, який і захопили руські вої місяця серпня в 12-й день і повернулися додому з перемогою великою. Святополк же прийшов] на заутреню в Печерський монастир на Успіння святої Богородиці, і братія цілувала його з радістю великою, оскільки вороги наші були переможені молитвами святої Богородиці і великого Федосія, отця нашого. Такий-бо обичай мав Святополк: коли ішов на війну чи ще кудись, то поклонявся біля гробу Федосія і молитву брав у тодішнього ігумена, і після цього ішов дорогою своєю.</p>\r\n<p>У те ж літо померла княгиня, мати Святополка, місяця січня в 4-й день.</p>\r\n<p>Того ж літа і в той же місяць ходив Володимир, і Давид, і Олег до Аепи і до другого Аепи і створили мир. І взяв Володимир за Юрія дочку Аепину, Ясіну внуку. А Олег взяв [за сина] дочку Акаспида, Яневу внуку місяця січня, у вівторок, на 10-й день.</p>\r\n<p>Місяця... в 15-й день тряслася земля перед світанком.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>',NULL,1855,5,1,NULL,25);
INSERT INTO `publications_publication` VALUES
(4705,'P','2008-12-20',0,NULL,'Note','Повість врем\'яних літ Повчання Володимира Мономаха','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я, недостойний, дідом своїм Ярославом благословенним, славним, батьком возлюбленим і матір\'ю своєю з Мономахів - названий при хрещенні Василем, руським іменем Володимир [у благочесті наставлений, дітям моїм у доброчесності домогтись успіхів бажаючи, се пишу поучення вам, улюблені,] і ради людей християнських, яких стільки уберіг з милості своєї і батьківської молитви від всіляких бід! Сидячи на санях, подумав я в душі своїй і похвалив Бога, що до сих днів мене допровадив.</span></span></p>','<p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p><em>Поученье [За Лаврентіївским літописом 1377 р.]</em></p>\r\n<p>&nbsp;</p>\r\n<p>Азъ худый д<small>Ђ</small>домъ своимъ Ярославомъ, благословленымъ, славнымъ, наречный въ крещении Василий, русьскымь именемь Володимиръ, отцемь възлюбленымь и матерью своею Мьномахы [во благочестии наказанъ, чаадомъ моимъ преспеяти въ доброд<small>Ђ</small>теляхъ желая, се пишу поученье вамъ взлюбленнии] и хрестьяных людий д<small>Ђ</small>ля, колико бо сблюдъ по милости своей и по отни молитв<small>Ђ</small> от вс<small>Ђ</small>х б<small>Ђ</small>дъ! С<small>Ђ</small>дя на санех, помыслих в души своей и похвалих бога, иже мя сихъ дневъ гр<small>Ђ</small>шнаго допровади. Да д<small>Ђ</small>ти мои, или инъ кто, слышавъ сю грамотицю, не посм<small>Ђ</small>йтеся, но ему же люба д<small>Ђ</small>тий моихъ, а приметь е в сердце свое, и не л<small>Ђ</small>нитися начнеть такоже и тружатися.</p>\r\n<p>Первое, бога д<small>Ђ</small>ля и душа своея, страх им<small>Ђ</small>йте божий в сердци своемь и милостыню творя неоскудну, то бо есть начатокъ всякому добру. Аще ли кому не люба грамотиця си, а не поохритаються, но тако се рекуть: на далечи пуги, да на санех с<small>Ђ</small>дя, безл<small>Ђ</small>пицю [е] си молвилъ.</p>\r\n<p>Уср<small>Ђ</small>тоша бо мя слы от братья моея на Волз<small>Ђ</small>, р<small>Ђ</small>ша: &laquo;Потъснися к нам, да выженемъ Ростиславича и волость ихъ отъимем; еже ли не поидеши с нами, то мы соб<small>Ђ</small> будем, а ты соб<small>Ђ</small>&raquo;. И р<small>Ђ</small>хъ: &laquo;Аще вы ся и гн<small>Ђ</small>ваете, не могу вы я ити, ни креста переступити&raquo;</p>\r\n<p>И отрядивъ я, вземъ Псалтырю, в печали разгнухъ я, и то ми ся выня: &laquo;Вскую печалуеши, душе? Вскую смущаеши мя?&raquo; и прочая. И потомь собрах словца си любая, и складохъ по ряду, и написах: Аще вы посл<small>Ђ</small>дняя не люба, а передняя приимайте.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p><em>Повчання [Володимира Мономаха за Лаврентіївським списком]</em></p>\r\n<p>&nbsp;</p>\r\n<p><em>Повчання. </em>Я, недостойний, дідом своїм Ярославом благословенним, славним, батьком возлюбленим і матір\'ю своєю з Мономахів - названий при хрещенні Василем, руським іменем Володимир [у благочесті наставлений, дітям моїм у доброчесності домогтись успіхів бажаючи, се пишу поучення вам, улюблені,] і ради людей християнських, яких стільки уберіг з милості своєї і батьківської молитви від всіляких бід! Сидячи на санях, подумав я в душі своїй і похвалив Бога, що до сих днів мене допровадив.</p>\r\n<p>Хай діти мої, чи інший хто, слухаючи сю грамотицю, не посміються, а кому із дітей моїх вона буде люба - хай прийме її в серце своє і, не лінуючись, почне, як і я, трудитися.</p>\r\n<p>Перш за все, бога ради і душі своєї, майте страх божий в серці своєму і милостиню подавайте щедру,- бо то початок всякого добра. Якщо ж комусь не люба грамотиця ся, хай не насміхається, а так про неї скаже: на далекій путі та на санях сидячи, небилиць наговорив.</p>\r\n<p>Зустріли були мене на Волзі посли від братів моїх і сказали: &laquo;Приєднуйся до нас, і виженемо Ростиславичів, і волость їхню відберемо. Якщо ж не підеш з нами, то ми собі будемо, а ти собі&raquo;. І я сказав: &laquo;Хоч ви й гніваєтесь, але не можу я ні з вами піти, ні клятву порушити&raquo;.</p>\r\n<p>І відправивши їх, узяв Псалтир, розгорнув у печалі, і таке мені випало: &laquo;Пощо сумуєш, душе? Пощо бентежиш мене?&raquo; і далі. А потім вибрав слова ці чудові, розклав їх у ряд, і написав: &laquo;Якщо вам останні не любі, то беріть передніх&raquo;.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&laquo;Вскую печална еси, душе моя? Вскую смущаеши мя? Уповаи на бога, яко испов<small>Ђ</small>мся ему&raquo;. &laquo;Не ревнуй лукавнующимъ, ни завиди творящимъ безаконье, зане лукавнующии потребятся, терпящии же господа,- ти обладають землею. И еще мало,- и не будеть гр<small>Ђ</small>шника; взыщеть м<small>Ђ</small>ста своего, и не обрящеть. Кротции же насл<small>Ђ</small>дять землю, насладяться на множьств<small>Ђ</small> мира. Назираеть гр<small>Ђ</small>шный праведнаго, и поскрегчеть на нь зубы своими; господь же посм<small>Ђ</small>ется ему и прозрить, яко придеть день его. Оружья извлекоша гр<small>Ђ</small>шьници, напряже лукъ свой истр<small>Ђ</small>ляти нища и убога, заплати правыя сердцемь. Оружье ихъ внидеть в сердця ихъ, и луци ихъ скрушатся. Луче есть праведнику малое, паче богатства гр<small>Ђ</small>шных многа. Яко мышца гр<small>Ђ</small>шных скрушится, утвержаеть же праведныя господь. Яко се гр<small>Ђ</small>шници погыбнуть; праведныя же милуя и даеть. Яко благословящии его насл<small>Ђ</small>дять землю, кленущии же его потребятся. От господа стопы челов<small>Ђ</small>ку исправятся. Егда ся падеть, и не разбьеться, яко господь подъемлеть руку его. Унъ б<small>Ђ</small>х, и сстар<small>Ђ</small>хся, и не вид<small>Ђ</small>хъ. праведника оставлена, ни с<small>Ђ</small>мени его просяща хл<small>Ђ</small>ба. Весь день милуеть и в заимъ даеть праведный, и племя его благословлено будеть. Уклонися от зла, створи добро, взищи мира и пожени, и живи в в<small>Ђ</small>кы в<small>Ђ</small>ка&raquo;.</p>\r\n<p>&laquo;Внегда стати челов<small>Ђ</small>комъ, убо живы пожерли ны быша; внегда прогн<small>Ђ</small>ватися ярости его на ны, убо вода бы ны потопила&raquo;.</p>\r\n<p>&laquo;Помилуй мя, боже, яко попра мя челов<small>Ђ</small>къ, весь день боряся, стужи ми. Попраша мя врази мои, яко мнози борющиися со мною с выше&raquo;. &laquo;Возвеселится праведник, и егда видить месть; руц<small>Ђ</small> свои умыеть в крови гр<small>Ђ</small>шника. И рече убо челов<small>Ђ</small>къ: аще есть плодъ праведника, и есть убо Богъ судяй земли&raquo;. &laquo;Измий мя от врагъ моихъ, боже, и от встающих на мя отъими мя. Избави мя от творящих безаконье, и от мужа крови спаси мя; яко се уловиша душю мою&raquo;.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&laquo;Пощо сумуєш ти, душе моя? Пощо бентежиш мене? Я надіюсь на Бога, бо перед ним сповідаюся&raquo;. &laquo;Не змагайся з лукавими, не завидуй творящим беззаконня, бо лукаві будуть знищені, а боготерпці - ті будуть володіти землею. І ще трохи - і не буде грішника, шукатиме місця свого - і не знайде.</p>\r\n<p>Покірнії ж успадкують землю і насолоджуватимуться багатоликим світом. Наглядає грішний за праведним і скрегоче на нього зубами своїми. Господь же посміється з нього і провидить, коли прийде день його [останній]. Оружжя добули грішники, напинають лук свій, щоб підстрелити убогого і нужденного, вразить справедливих серцем. Оружжя їхнє вразить власне серце, а луки їхні зламаються. Лучче малість праведних, аніж великі багатства грішних. Бо сила грішних зламається, а праведних зміцнює Господь. Отож, грішники згинуть, а праведних же він милує і дає їм. Отож, ті, що благословляють його, успадкують землю, а ті, що проклинають його, будуть випущені. Господь стопи чоловіка направляє. І навіть коли впаде - не розіб\'ється, бо Господь підтримує руку його. Молодим був і постарів, а не бачив залишеним праведника, ні дітей його, щоб хліба просили. Щодень подає милостиню і в борг дає праведний, і нащадки його благословенні будуть. Ухиляйся від зла, твори добро, шукай миру і проганяй [зло],- і живи во віки віків&raquo;.</p>\r\n<p>&laquo;Коли б повстали люди, то живими б пожерли нас; коли б розгнівалась на нас лють його, то вода б нас потопила&raquo;.</p>\r\n<p>&laquo;Помилуй мене, Боже, бо зневажив мене чоловік, щодень борючись, гнітить мене. Зневажили мене вороги мої, бо багато бореться проти мене з неба&raquo;. &laquo;Возвеселиться праведник і, коли побачить відомщення, руки свої омиє в крові грішника. Адже сказав чоловік: &laquo;Якщо є нагорода праведнику, то є й Бог, що творить суд на землі&raquo;. &laquo;Візьми мене із рук ворогів моїх, Боже, і від тих, що повстають на мене, відніми мене. Увільни мене від творящих беззаконня і обрятуй мене від кровожадних, які полонили душу мою&raquo;.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&laquo;И яко гн<small>Ђ</small>въ въ ярости его, и животь в воли его; вечеръ водворится плачь, а заутра радость&raquo;. &laquo;Яко лучьши милость твоя, паче живота моего, и устн<small>Ђ</small> мои похвалита тя. Тако благословлю тя в живот<small>Ђ</small> моемь, и о имени твоемь възд<small>Ђ</small>ю руц<small>Ђ</small> мои&raquo;. &laquo;Покры мя от соньма лукавого и от множьства д<small>Ђ</small>лающих неправду&raquo;. &laquo;Възвеселитеся вси праведнии сердцемь. Благословлю господа на всяко время, воину хвала его&raquo;, и прочая.</p>\r\n<p>Якоже бо Василий учаше, собрав ту уноша, душа чисты, нескверньни, т<small>Ђ</small>леси худу, кротку бес<small>Ђ</small>ду и в м<small>Ђ</small>ру слово господне: &laquo;Яди [и] питью бесъ плища велика быти, при старых молчати, премудрыхъ слушати, стар<small>Ђ</small>йшимъ покарятися, с точными и меншиими любовь им<small>Ђ</small>ти; без луки бес<small>Ђ</small>дующе, а много разум<small>Ђ</small>ти; не свер<small>Ђ</small>повати словомь, ни хулити бес<small>Ђ</small>дою, не обило см<small>Ђ</small>ятися, срамлятися стар<small>Ђ</small>йших, к женам нел<small>Ђ</small>пымъ не бес<small>Ђ</small>довати, долу очи им<small>Ђ</small>ти, а душю гор<small>Ђ</small>, преб<small>Ђ</small>гати; не стр<small>Ђ</small>кати учить легкых власти, ни в кую же им<small>Ђ</small>ти, еже от вс<small>Ђ</small>х честь. Аще ли кто васъ можеть ин<small>Ђ</small>мь усл<small>Ђ</small>ти, от бога мьзды да чаеть и в<small>Ђ</small>чных благъ насладится&raquo;. &laquo;О Владычице Богородице! Отъими от убогаго сердца моего гордость и буесть, да не възношюся суетою мира сего&raquo;; в пустошн<small>Ђ</small>мь семь житьи.</p>\r\n<p>Научися, в<small>Ђ</small>рный челов<small>Ђ</small>че, быти благочестию д<small>Ђ</small>латель, научися, по евангельскому словеси, &laquo;очима управленье, языку удержанье, уму см<small>Ђ</small>ренье, т<small>Ђ</small>лу порабощенье, гн<small>Ђ</small>ву погубленье, помыслъ чистъ им<small>Ђ</small>ти, понужаяся на добрая д<small>Ђ</small>ла, господа ради; лишаемъ - не мьсти, ненавидимъ - люби, гонимъ - терпи, хулимъ - моли, умертви гр<small>Ђ</small>хъ&raquo;. &laquo;Избавите обидима, судите сирот<small>Ђ</small>, оправдайте вдовицю. Прид<small>Ђ</small>те, да сожжемъся, глаголеть господь. Аще будуть гр<small>Ђ</small>си ваши яко оброщени, яко сн<small>Ђ</small>гъ об<small>Ђ</small>лю я&raquo;, и прочее.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&laquo;І, як гнів у люті його, так життя і воля його: ввечері вселяється плач, а ранком [з\'являється] радість&raquo;.</p>\r\n<p>&laquo;Бо лучче милість твоя, аніж життя моє, і вуста мої хай восхвалять тебе. Так благословлю тебе за життя моє і в честь твою здійму руки мої&raquo;.</p>\r\n<p>&laquo;Заховай мене від сонмища лукавих і від зборища творящих неправди&raquo;. &laquo;Возвеселіться, всі праведні серцем. Благословляю Господа повсякчас, безкінечна хвала йому&raquo; і таке інше.</p>\r\n<p>От так і Василій навчав, зібравши біля себе юних, мати душу чисту й незаплямовану, тіло худокосте, розмову лагідну і дотримуватись слова Господнього: &laquo;їжа і питво має бути без шуму великого, при старших мовчать, премудрих слухать, старшим коритися, з рівними і меншими в любові бути,- розмовляти без лукавства, а розуміти багато; не лютитися словом, не хулити мовою, не багато сміятися, соромитись старших, з жінками безпутними не розмовляти, очі долу опускати, а душу д\'горі, уникаючи їх. Не ухилятися наставляти падких до влади, ні в що ставити, що від усіх честь. Коли ж хтось із вас може іншим корисне зробити, від Бога на віддяку і насолодження вічними благами - хай надіється&raquo;. &laquo;О Владичице Богородице! Відведи від убогого серця мого гордість і буйство, щоб не величався марнотами світу цього у нашім пустотнім житті&raquo;.</p>\r\n<p>Навчися, праведний чоловіче, поступати благочестиво, навчися, за євангельським словом, &laquo;очима управлять, язик стримувать, ум упокорювать, тіло поневолювать, гнів погублять, помисли чисті мати, спонукаючись на добрі діла ради Господа; позбавлений - не мсти, зненавиджений - люби, гнаний - терпи, підданий хулі - моли, умертви гріх&raquo;.</p>\r\n<p>&laquo;Заступайтесь за обиженого, давайте розраду сироті, оправдайте вдовицю. Приходьте, щоб об\'єднатися,- говорить Господь.- Якщо гріхи ваші будуть як багряниця, обілю я їх, як сніг&raquo; і таке інше.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&laquo;Восияеть весна постная и цв<small>Ђ</small>тъ покаянья, очистимъ собе, братья, от всякоя крови плотьскыя и душевныя. Св<small>Ђ</small>тодавцю вопьюще рц<small>Ђ</small>мъ: Слава тоб<small>Ђ</small>, челов<small>Ђ</small>колюбче!&raquo;.</p>\r\n<p>Поистин<small>Ђ</small>, д<small>Ђ</small>ти моя, разум<small>Ђ</small>йте, како ти есть челов<small>Ђ</small>колюбець богъ мклостивъ и премилостивъ. Мы челов<small>Ђ</small>ци, гр<small>Ђ</small>шни суще и смертни, то оже ны зло створить, то хощемъ и пожрети и кровь его прольяти вскор<small>Ђ</small>; а господь нашь, влад<small>Ђ</small>я и животомъ и смертью, согр<small>Ђ</small>шенья наша выше главы нашея терпить, и пакы и до живота нашего. Яко отець, чадо свое любя, бья, и пакы привлачить е к соб<small>Ђ</small>, такоже и господь нашь показал ны есть на врагы поб<small>Ђ</small>ду, 3-ми д<small>Ђ</small>лы добрыми избыти его и поб<small>Ђ</small>дити его: покаяньемъ, слезами и милостынею. Да то вы, д<small>Ђ</small>ти мои, не тяжька запов<small>Ђ</small>дь Божья, оже т<small>Ђ</small>ми д<small>Ђ</small>лы 3-ми избыти гр<small>Ђ</small>ховъ своихъ и царствия не лишитися.</p>\r\n<p>А Бога д<small>Ђ</small>ля не л<small>Ђ</small>нитеся, молю вы ся, не забывайте 3-х д<small>Ђ</small>лъ т<small>Ђ</small>хъ: не бо суть тяжка; ни одиночьство, ни чернечьство, ни голодъ, яко инии добрии терпять, но малым д<small>Ђ</small>ломъ улучити милость божью.</p>\r\n<p>&laquo;Что есть челов<small>Ђ</small>къ, яко помниши и?&raquo;. &laquo;Велий еси, Господи, и чюдна д<small>Ђ</small>ла твоя, никакже разумъ челов<small>Ђ</small>ческъ не можетв испов<small>Ђ</small>дати чюдес твоихъ;- и пакы речемъ: велий еси, Господи, и чюдна д<small>Ђ</small>ла твоя, и благословено и хвално имя твое в в<small>Ђ</small>кы по всей земли&raquo;. Иже кто не похвалить, ни прославляеть силы твоея и твоих великыхъ чюдес<small>Ђ</small> и добротъ, устроеныхъ на семь св<small>Ђ</small>т<small>Ђ</small>: како небо устроено, како ли солнце, како ли луна, како ли зв<small>Ђ</small>зды, и тма и св<small>Ђ</small>т, и земля на водах положена, Господи, твоимъ промыслом! Зв<small>Ђ</small>рье рознол\'ичнии, и птица и рыбы украшено твоимъ промыслом, Господи! И сему чюду дивуемъся, како от персти создавъ челов<small>Ђ</small>ка, како образи розноличнии въ челов<small>Ђ</small>чьскыхъ лицих,-аще и весь миръ совокупить, не вси &gt;въ один образ, но кый же своимъ лиць образомъ, по божии мудрости. И сему ся подивуемы, како птица небесныя изъ ирья идуть, и перв<small>Ђ</small>е, [въ] наши руц<small>Ђ</small>, и не ставятся на одиной земли, но и сильныя и худыя идуть по вс<small>Ђ</small>мъ землямъ, Божиимь повелъньемь, да наполнятся л<small>Ђ</small>си и поля. Все же то далъ Богъ на~угодье челов<small>Ђ</small>комъ, на сн<small>Ђ</small>дь, на веселье. Велика, Господи, милость твоя на нас, яже та угодья створилъ еси челов<small>Ђ</small>ка д<small>Ђ</small>ля гр<small>Ђ</small>шна. И ты же птиц<small>Ђ</small> небесныя умудрены тобою, Господи; егда повелиши, то вспоють, и челов<small>Ђ</small>кы веселять тобе; и егда же не повелиши имъ, языкъ же им<small>Ђ</small>юще онем<small>Ђ</small>ють. &laquo;А благословенъ еси, Господи, и хваленъ з<small>Ђ</small>ло!&raquo; всяка чюдеса и ты доброты створивъ и зд<small>Ђ</small>лавъ, &laquo;Да иже не хвалить тебе, Господи, и не в<small>Ђ</small>руеть вс<small>Ђ</small>м сердцемь и всею душею во имя Отца и Сына и Святаго Духа, да будеть проклятъ&raquo;.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&laquo;Засіяє весна посту і квіт покаяння; очистимо себе, браття, від всілякої крові, тілесної і душевної. Звертаючись до світодавця, говоримо: &laquo;Слава тобі, чоловіколюбче!&raquo;</p>\r\n<p>Справді, діти мої, зрозумійте, який до тебе чоловіколюбивий Бог милостивий і премилостивий. Ми, люди, грішні і смертні, і якщо нам хтось зло зробить, то хочемо зтерти його і кров його пролити негайно; а Господь наш, володіючи і життям і смертю, безмірні гріхи наші терпить і аж до кінця життя нашого. Як люблячий батько б\'є дитя своє і знову пригортає до себе, так і Господь наш показав нам, як ворогів перемагать, як трьома добрими ділами позбуватися від них і перемагати їх: покаянням, сльозами і милостинею. І це вам, діти мої, не тяжка заповідь Божа, як тими ділами трьома позбутися гріхів своїх і царства [небесного] не лишитися. І, Бога ради, не лінуйтеся, умоляю вас, не забувайте тих трьох діл: не тяжкі вони, ні усамітнення, ні чернецтво, ні голод, що інші добродійні терплять, адже й малими ділами [можна] заслужить милість Божу.</p>\r\n<p>&laquo;Що таке чоловік, як подумаєш про нього?&raquo; &laquo;Великий ти, Господи, і дивні діла твої, і ніяк не може розум людський розповісти про чудеса твої&raquo;,- і знову кажемо: &laquo;Великий ти, Господи, і дивні діла твої, і благословение і славне ім\'я твоє во віки по всій землі&raquo;.</p>\r\n<p>І хто не воздасть хвалу, не прославлятиме сили твоєї і твоїх великих чудес і доброт, содіяних на цьому світі: як небо влаштовано, а чи сонце, чи місяць, чи зорі, і тьма, і світло, і земля на водах покладена, твоїм, Господи, промислом! Звірі різні, і птиці, і риби украшені твоїм, Господи, промислом! І цьому диву дивуємось, як із персті створив чоловіка, які лиця різноликі у людської подоби, що коли і всіх людей зібрать, не всі вони на одне лице, але кожний має своє лице за Божою мудрістю.</p>\r\n<p>І цьому дивуватимемось, як птиці небесні із вирію ідуть і, перш за все, в наші руки, і не поселяються в одному місці, але і сильні і слабенькі розселяються, за Божим повелінням, по всій землі, щоб наповнились ліси і поля. І все ж те дав Бог на догоду чоловікові, для їжі, для розваги. Велика, Господи, милість твоя до нас, що ті блага створив ти ради чоловіка грішного. І ті ж птиці небесні умудрені тобою, Господи, коли повелиш, то заспівають і чоловіка тобі розважать, а коли не повелиш їм, то й ті, що голос мають, оніміють. &laquo;І благословенний ти, Господи, і восхвалений сильно&raquo;. &laquo;Всілякі чудеса і блага створив і зробив. ! хто не хвалить тебе, Господи, і не вірує всім серцем і всією душею во ім\'я Отця і Сина і Святого Духа,- хай буде проклятий&raquo;.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Си словца прочитаюче, д<small>Ђ</small>ти моя, божествная, похвалите Бога, давшего нам милость свою: а се от худаго моего безумья наказанье. Послушайте мене: еще не всего приімете, то половину.</p>\r\n<p>Аще вы богъ умякчить сердце, и слезы своя испустите о гр<small>Ђ</small>с<small>Ђ</small>х своих, рекуще: якоже блудницю и разбойника и мытаря помиловалъ еси, тако и нас гр<small>Ђ</small>шных помилуй! И в церкви то д<small>Ђ</small>йте и ложася. Не гр<small>Ђ</small>шите ни ідину же ночь, аще можете, поклонитися до земли; а ли вы ся начнеть не мочи, а трижды. А того не забывайте, не л<small>Ђ</small>нитеся, т<small>Ђ</small>мъ бо ночнымъ поклономъ и п<small>Ђ</small>ньемъ челов<small>Ђ</small>къ поб<small>Ђ</small>жаеть дьявола, и что въ день согр<small>Ђ</small>шить, а т<small>Ђ</small>мь челов<small>Ђ</small>къ избываеть. Аще и на кони <small>Ђ</small>здяче не будеть ни с кым орудья, аще ин<small>Ђ</small>х молитвъ не ум<small>Ђ</small>ете молвити, а &laquo;Господи помилуй&raquo; зов<small>Ђ</small>те бес престани, втайн<small>Ђ</small>: та бо есть молитва вс<small>Ђ</small>х л<small>Ђ</small>пши, нежели мыслити безл<small>Ђ</small>пицю <small>Ђ</small>здя.</p>\r\n<p>Всего же паче убогых не забывайте, но елико могуще по сил<small>Ђ</small> кормите, и придайте сирот<small>Ђ</small>, и вдовицю справдите сами, а не вдавайте силным погубити челов<small>Ђ</small>ка. Ни права, ни крива не убивайте, ни повел<small>Ђ</small>вайте убити его. Аще будеть повиненъ смерти, а душа не погубляйте никакоя же хрестьяны. Р<small>Ђ</small>чь молвяче, и лихо и добро, не кленитеся богомь, ни хреститеся, н<small>Ђ</small>ту бо ти нужа никоеяже. Аще ли вы будете крестъ ц<small>Ђ</small>лозати к братьи или г кому, а ли управивъше сердце свое, на немже можете устояти, тоже ц<small>Ђ</small>луйте, и ц<small>Ђ</small>ловавше блюд<small>Ђ</small>те, да не, приступки, погубите душ<small>Ђ</small> свое<small>Ђ</small>. Епископы, и попы и игумены... с любовью взимайте от них благословленье, и не устраняйтеся от нихъ, и по сил<small>Ђ</small> любите и набдите, да приимете отъ них молитву от бога. Паче всего гордости не им<small>Ђ</small>йте в сердци и въ ум<small>Ђ</small>, но рц<small>Ђ</small>м: смертни есмы, днесь живи, а заутра в гробъ; се все, что ны еси вдалъ, не наше, но твое, поручилъ ны еси на мало дний. И в земли не хороните, то ны есть великъ грехъ. Старыя чти яко отца, а молодыя яко братью. В дому своемь не л<small>Ђ</small>нитеся, но все видите; не зрите на тивуна, ни на отрока, да не посм<small>Ђ</small>ются приходящии к вамъ ни дому вашему, ни об<small>Ђ</small>ду вашему. На войну вышедъ, не л<small>Ђ</small>нитеся, не зрите на воеводы; ни питью, ни <small>Ђ</small>денью не лагодите, ни спанью; и сторож<small>Ђ</small> сами наряживайте, и ночь, отвсюду нарядивше около вои тоже лязите, а рано встан<small>Ђ</small>те; а оружья не снимайте с себе вборз<small>Ђ</small>, не розглядавше л<small>Ђ</small>нощами, внезапу бо челов<small>Ђ</small>къ погыбаеть. Лж<small>Ђ</small> блюдися и пьяньства и блуда, в томъ бо душа погыбаеть и т<small>Ђ</small>ло. Куда же ходяще путемъ по своимъ землямъ, не дайте пакости д<small>Ђ</small>яти отрокомъ, ни своимъ, ни чюжимъ, ни в сел<small>Ђ</small>хъ, ни в жит<small>Ђ</small>хъ, да не кляти вас начнуть. Куда же поидете, идеже станете, напойте, накормите унеина; и боле же чтите гость, откуду же к вамъ придеть, или простъ, или добръ, или солъ, аще не можете даромъ, брашномъ и питьемь: ти бо мимоходячи прославять челов<small>Ђ</small>ка по вс<small>Ђ</small>м землямъ, любо добрымъ, любо злымъ. Болнаго прис<small>Ђ</small>тите; надъ мертвеця ид<small>Ђ</small>те, яко вси мертвени есмы. И челов<small>Ђ</small>ка не мин<small>Ђ</small>те, не прив<small>Ђ</small>чавше, добро слово ему дадите. Жену свою любите, но не дайте имъ надъ собою власти. Се же вы конець всему: страхъ божий им<small>Ђ</small>йте выше всего.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Оці слівця божественні прочитавши, діти мої, похваліте Бога, дающого нам милість свою; а се від слабкого мого розуму напучення. Вислухайте мене, якщо не все приймете, то половину.</p>\r\n<p>Якщо вам Бог пом\'якшить серце, і сльози свої зроните за гріхи свої, кажучи: &laquo;Як блудницю, і розбійника, і митаря помилував ти, так і нас, грішних, помилуй!&raquo; І в церкві це робіть і [спать] лягаючи. Не грішіть ані єдину ніч, а якщо можете, поклоніться до землі, а коли вам нездужатиметься, то тричі. А того не забувайте, не лініться, бо тим нічним поклоном і молитвою чоловік перемагає диявола, і що вдень согрішить, то тим чоловік [вночі] спасається [від гріха]. А якщо і на кокі їдучи не будете ні з ким справ мати, і коли інших молитов не вмієте промовлять, то &laquo;Господи помилуй&raquo; повторяйте безперестанку про себе: бо та молитва найліпша,- аніж розмишлять, їздячи, про нісенітницю.</p>\r\n<p>А найголовніше - убогих не забувайте, а скільки можете по змозі годуйте і подайте [милостиню] сироті, і вдовицю оправдуйте самі, а не давайте сильним погубити людину. Ні правого, ні винуватого не вбивайте і не веліть убивати його. Якщо навіть заслуговуватиме смерті, і то не погубляйте жодного християнина. Слово мовлячи, і про погане, і про добре, не кляніться Богом і не хрестіться, бо в тому нема тобі ніякої потреби. Якщо ж ви будете клятву давати братам чи будь-кому, то провірте серце своє і, на чому можете устоять, у тому і кляніться, а, поклявшися, дотримуйтесь, аби, порушивши [клятву], не погубили душі своєї. Єпископи, і попи, і ігумени з любов\'ю приймайте від них благословення, і не стороніться їх, і, в міру сил своїх, любіть і печіться про них, щоб прийняти їхню молитву від Бога. А понад усе - гордині не майте ні в серці, ні в розумі, але скажемо: &laquo;Смертні ми, сьогодні живі, а завтра - в гріб; це все, що ти нам дав, не наше, а твоє, доручене нам тобою на мало днів. І в землі не ховайте [скарбів], то нам великий гріх. Старих шануй, як отця, а молодих - як братів. У домі своєму не лінуйтеся, а все помічайте. Не покладайтесь на тівуна, ні на отрока, щоб не насміхалися гості ваші ні з дому вашого, ні з обіду вашого. На війну вийшовши, не лінуйтеся, не покладайтеся на воєвод; ні питву, ні їді не попускайте, ні спанню; і сторожів самі споряжайте, і вночі, розставивши скрізь [сторожу] , біля воїв самі лягайте, а рано вставайте; а оружжя не спішіть із себе знімати, не роздивившись, бо з-за лінощів раптово чоловік гине. Лжі остерігайтеся, і п\'янства, і блуду, бо в них гине душа і тіло. Куди б ви не йшли і якими шляхами по своїх землях, не давайте отрокам чинити пакості, ні своїм, ні чужим, ні в селах, ні в житлах, щоб не почали вас проклинать. Куди не підете і де не станете, напійте і нагодуйте страждущого. А найбільше шануйте гостя, звідки б він до вас не прийшов, чи простий чоловік, чи знатний, чи посол. Якщо не можете [пошанувати його] дарами, то їжею і питвом: бо вони мимоходом прославлять чоловіка по всіх краях або добрим, або злим. Хворого відвідайте, покійного проведіть, бо всі ми смертні. І чоловіка не минайте, не привітавши, доброго слова не сказавши. Жінку свою любіть, але не давайте їм над собою влади. Це ж і кінець всьому: страх Божий майте над усе.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Аще забивайте всего, а часто прочитайте: и мне будеть бе-сорома, и вамъ будеть добро.</p>\r\n<p>Его же ум<small>Ђ</small>ючи, того не забывайте доброго, а его - же не ум<small>Ђ</small>ючи, а тому ся учите, якоже бо отець мой, дома с<small>Ђ</small>дя, изум<small>Ђ</small>яше 5 языкъ, в томъ бо честь есть от ин<small>Ђ</small>хъ земль. Л<small>Ђ</small>ность бо всему мати: еже ум<small>Ђ</small>еть, то забудеть, а егоже не ум<small>Ђ</small>еть, а тому ся не учить. Добр<small>Ђ</small> же творяще, не мозите ся л<small>Ђ</small>нити ни на что же доброе, первое к церкви: да не застанеть васъ солнце на постели; тако бо отець мой д<small>Ђ</small>яшеть блаженый и вси добрии мужи свершении. Заутренюю отдавше богови хвалу, и потомъ солнцю въсхсдящю, и узр<small>Ђ</small>вше солнце, и прославити Бога с радостью и рече: &laquo;Просв<small>Ђ</small>ти очи мои, Христе Боже, иже далъ ми еси св<small>Ђ</small>тъ твой красный! И еще: Господи, приложи ми л<small>Ђ</small>то къ л<small>Ђ</small>ту, да прокъ, гр<small>Ђ</small>ховъ своих покаявъся, оправдивъ животъ&raquo;, тако похвалю Бога! И с<small>Ђ</small>дше думати с дружиною, или люди оправливати, или на ловъ <small>Ђ</small>хати, или по<small>Ђ</small>здити, или лечи спати: спанье есть от Бога присужено полудне. О тъ чина бо почиваеть и зв<small>Ђ</small>рь, и птици и челов<small>Ђ</small>ци.</p>\r\n<p>А се вы пов<small>Ђ</small>даю, д<small>Ђ</small>ти моя, трудъ свой, оже ся есмь тружалъ, пути д<small>Ђ</small>я и ловы с 13 л<small>Ђ</small>тъ. Первое к Ростову идохъ, сквоз<small>Ђ</small> Вятич<small>Ђ</small>, посла мя отець, а самъ иде Курьску; и пакы 2-е к Смолиньску со Ставкомь с Скордятичемъ, той пакы и отъиде к Берестию со Изяславомь, а мене посла Смолиньску, то и-Смолиньска идохъ Володимерю. Тое же зимы той посласта Берестию брата на головні, иде бяху ляхове пожгли, той ту блюдъ городъ тихъ. Та идохъ Переяславлю отцю, а по Велиц<small>Ђ</small> дни ис Переяславля та Володимерю - на Сутейску мира творить с ляхы. Оттуда пакы на л<small>Ђ</small>то Володимерю опять.</p>\r\n<p>Та посла мя Святославъ в Ляхы; ходивъ за Глоговы до Чешьскаго л<small>Ђ</small>са, ходивъ в. земли ихъ 4 м<small>Ђ</small>сяци. И в то же л<small>Ђ</small>то и д<small>Ђ</small>тя ся роди стар<small>Ђ</small>йшее новгородьское. Та оттуда Турову, а на весну та Переяславлю, таже Турову.</p>\r\n<p>И Святославъ умре, и язъ пакы Смолиньску, а и-Смолиньска той же зим<small>Ђ</small> та к Новугороду; на весну Гл<small>Ђ</small>бови в помочь. А на л<small>Ђ</small>то со отцемь подъ Полтескъ, а на другую зиму с Святополкомъ подъ Полтескъ,- ожьгьше Полтескъ; онъ иде Новугороду, а я с половци на Одрьскъ, воюя, та Чернигову. И пакы, и-Смолиньска къ отцю придох Чернигову. И Олегъ приде, из Володимеря выведенъ, и возвахъ и к соб<small>Ђ</small> на об<small>Ђ</small>дъ со отцемь в Чернигов<small>Ђ</small>, на Красн<small>Ђ</small>мь двор<small>Ђ</small>, и вдахъ отцю 300 гривен золота. И пакы и-Смолиньска же пришедъ, и проидох сквоз<small>Ђ</small> половечьскыи вои, бьяся, до Переяславля, и отца нал<small>Ђ</small>зохъ с полку пришедше. Той пакы ходихомъ, том же л<small>Ђ</small>т<small>Ђ</small>, со отцемь и со Изяславомь битъся Чернигову с Борисомъ, и поб<small>Ђ</small>дихомъ Бориса и Олга. И пакы идохом Переяславлю, и стахомъ во Обров<small>Ђ</small>.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Якшо почнете забувати це, то частіше перечитуйте: і мені не буде соромно, і вам буде добре.</p>\r\n<p>Не забувайте того доброго, що вмієте, а чого не вмієте, тому навчайтеся, як батько мій, дома сидячи, вивчив п\'ять мов, у тому честь мав від інших країн. Лінь-бо - мати всьому: що уміє, те забуде, а чого не вміє, того не навчається. Добро діючи, не лінуйтеся ні на що добре, найперше - до церкви: хай не застане вас сонце в постелі. Так і отець мій блаженний робив, і всі знатні мужі славетні. На заутрені воздати Богові хвалу і потім, коли сонце сходить і побачивши сонце, належить прославити Бога із радістю і сказати: &laquo;Просвіти очі мої, Христе Боже, що дав ти мені світ свій красний!&raquo; І ще: &laquo;Господи, додай мені літо до літа, щоб у подальшому, в гріхах своїх покаявшись, оправдав життя своє&raquo;. Так я похваляю Бога, сідаючи думать з дружиною, чи людям суд творити, чи на лови їдучи, чи дань збираючи, чи спать лягаючи: спання Богом визначено на полуднє. По цьому визначенню відпочивають і звірі, і птиці, і люди.</p>\r\n<p>А це вам повідаю, діти мої, про труд свій, який я сповняв, шляхи верстаючи і на ловах із тринадцяти років. Перший раз ішов я до Ростова через [землю] витячів. Послав мене отець, а сам пішов на Курськ. І знову другий раз ходив у Смоленськ із Ставком Скордятичем, який потім пішов на Берестя з Ізяславом, а мене послав у Смоленськ, а із Смоленська пішов я у Володимир. Тої ж зими послали мене брати у Берестя на пожарище, де ляхи були попалили, і там  я  тримав город у тиші. Потім пішов у Переяслав до батька, а після Великодня із Переяслава у Володимир - у Сутейську мир заключить із ляхами. Звідти знову на літо - у Володимир.</p>\r\n<p>Тоді послав мене Святослав у Ляхи. Ходив за Глогів до Чешського лісу, ходив у землі їхній чотири місяці. І в те ж літо і дитя перше народив, новгородське. А звідти [пішов] до Турова, а на весну в Переяслав, потім до Турова.</p>\r\n<p>Як умер Святослав, я знову - в Смоленськ, а із Смоленська тієї ж зими - у Новгород; на весну - Глібові на поміч. А літом із батьком під Полтеськ, а другої зими із Святополком під Полтеськ,- випалили Полтеськ. Він пішов у Новгород, а я з половцями на .Одреск, воюючи, і в Чернігів, І знову із Смоленська прийшов до отця у Чернігів. І Олег прийшов [туди] із Володимира виведений, і запросив його із батьком до себе на обід у Чернігові на Краснім дворі, і подарував отцю триста гривен золота. І знову, коли із Смоленська повертався, ішов, б\'ючись із половецькими воями, до Переяслава і застав там отця, який з походу повернувся. Тоді знову ходили того ж літа із батьком і з Ізяславом до Чернігова битися з Борисом. І перемогли Бориса і Олега. І знову пішли у Переяслав, і стали в Оброві.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>И Всеславъ Смолнескъ ожьже, и азъ вс<small>Ђ</small>дъ с черниговци о двою коню, и не застахом въ Смолиньск<small>Ђ</small>. Т<small>Ђ</small>мже путем по Всеслав<small>Ђ</small> пожегъ землю и повоевавъ до Лукамля и до Логожьска, та на Дрьютьскъ воюя, та Чернигову.</p>\r\n<p>А на ту зиму повоеваша половци Стародубъ весь, и азъ шедъ с черниговци и с половци, на Десн<small>Ђ</small> изьимахом князи Асадука и Саука, и дружину ихъ избиша. И на заутре<small>Ђ</small> за Новымъ Городом разгнахомъ силны вои Белкатгина, а се мечи и полонъ весь отяхом.</p>\r\n<p>А въ вятичи ходихом по дв<small>Ђ</small> зим<small>Ђ</small> на Ходоту и на сына его,, и ко Корьдну, ходихъ 1-ю зиму. И пакы по Изяславичихъ за Микулинъ, и не постигохомъ ихъ. И на ту весну къ  Ярополку совкуплятъся на Броды.</p>\r\n<p>Томже л<small>Ђ</small>т<small>Ђ</small> гонихом по Половьцихъ за Хоролъ, иже Горошинъ взяша.</p>\r\n<p>И на ту осень идохомъ с черниговци и с Половци, с Чит<small>Ђ</small>евичи, к М<small>Ђ</small>ньску: изъ<small>Ђ</small>хахом городъ, и не оставихомъ у него ни челядина, ни скотины.</p>\r\n<p>На ту зиму идохомъ къ Ярополку совокуплятися на Броды, и любовь велику створихомъ.</p>\r\n<p>И на весну посади мя отець в Переяславли передъ братьею, и ходихомъ за Супой. И <small>Ђ</small>дучи к Прилуку городу, и ср<small>Ђ</small>тоша ны внезапу половечьскы<small>Ђ</small> князи, 8 тысячь, и хот<small>Ђ</small>хом с ними ради битися, но оружье бяхомъ услали напередъ на повоз<small>Ђ</small>хъ, и внидохом в городъ; только Семцю яша одиного живого, ти смердъ н<small>Ђ</small>колико, а наши он<small>Ђ</small>хъ боле избиша и изьимаша, и не см<small>Ђ</small>ша ни коня пояти в руц<small>Ђ</small>, и б<small>Ђ</small>жаша на Сулу тое ночи. И заутра, на Госпожинъ день, идохомъ к Б<small>Ђ</small>л<small>Ђ</small> Вежи, и Богъ ны поможе и святая Богородица: избихом 900 Половець, и два князя яша, Багубарсова брата, Асиня и Сакзя, а два мужа толко утекоста.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>І Всеслав Смоленськ попалив, і я з чернігівцями, сівши о двох конях, погнав, і не застав у Смоленську. Тим же шляхом, женучись за Всеславом, попалив землю і повоював до Лукомля і до Логожська, потім на Дрюцьк, воюючи, потім у Чернігів.</p>\r\n<p>А в ту зиму повоювали половці Стародуб весь, і я ходив із чернігівцями і з половцями, на Десні захопили [в полон] князів Асадука і Саука, а дружину їхню перебили. І наступного ранку за Новим Городом розігнали сильне військо Белкатчіна, а семечів і полон весь відбили.</p>\r\n<p>А у В\'ятичі ходив дві зими на Ходоту і на сина його, і до Кордну ходив першої зими. І ще за Ізяславичами [гналися] за Микулин, і не настигли їх. І тієї весни - до Ярополка у Броди на раду. Того ж літа ходили в погоню за Хорол ріку за половцями, які захопили були Горошин.</p>\r\n<p>І тієї осені ходили з чернігівцями і в половцями-читієвичами до Мінська: захопили город і не залишили в ньому ні челядника, ні скотини.</p>\r\n<p>Тієї зими ходили до Ярополка на раду у Броди і дружбу велику заключили.</p>\r\n<p>І весною посадив мене отець у Переяславі попереду братів, і ходили за Супій. І, йдучи в город Прилуку, несподівано нас зустріли половецькі князі із вісьмома тисячами, і хотіли б ради з ними битися, але оружжя відіслали були вперед на повозах, і ввійшли в город. Тільки семця одного взяли живим та декілька смердів, а наші їхніх більше перебили і зловили; і половці, не сміючи навіть коня взяти в руки, повтікали тієї ж ночі на Сулу. А наступного дня, на Госпожин день, ходили до Білої Вежі, і Бог нам поміг і свята Богородиця: перебили дев\'ятсот половців і двох князів взяли - Багубарсового брата Асиня і Сакзя, і тільки два мужі їхні втекли.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>И потомь на Святославль гонихом по Половцих, и потомь на Торческый городъ, и потомь на Гюргевъ по половцих. И пакы на той же сторон<small>Ђ</small> у Красна половци поб<small>Ђ</small>дихом; и потомь с Ростиславомъ же у Варина веж<small>Ђ</small> взяхом. И потомь ходивъ Володимерю, паки Ярополка посадих, и Ярополкъ умре.</p>\r\n<p>И пакы по отни смерти и при Святополц<small>Ђ</small>, на Стугн<small>Ђ</small> бившеся съ половци до вечера, бихом - у Хал<small>Ђ</small>па, и потомь миръ створихом с Тугорканомъ и со ин<small>Ђ</small>ми князи половечьскими; и у Гл<small>Ђ</small>бовы чади пояхом дружину свою всю.</p>\r\n<p>И потомь Олегъ на мя приде с Половчьскою землею к Чернигову, и бишася дружина моя и нимь 8 дний о малу греблю, и не вдадуче виити имъ въ острогъ; съжаливъси хрестьяных душь и селъ горящих и манастырь, и р<small>Ђ</small>хъ: &laquo;Не хвалитися поганым!&raquo;. И вдахъ брату отца его м<small>Ђ</small>сто, а самъ идох на отця своего м<small>Ђ</small>сто Переяславлю. И выидохом на святаго Бориса день ис Чернигова, и <small>Ђ</small>хахом сквоз<small>Ђ</small> полкы половьчски<small>Ђ</small>, не въ 100 дружин<small>Ђ</small>, и с д<small>Ђ</small>тми и с женами. И облизахутся на нас акы волци стояще, и от перевоза и з гор<small>Ђ</small>, богъ и святый Борисъ не да имъ мене в користь,- неврежени доидохом Переяславлю.</p>\r\n<p>И с<small>Ђ</small>д<small>Ђ</small>хъ в Переяславли 3 л<small>Ђ</small>та и 3 зимы, и с дружиною своею, и многы б<small>Ђ</small>ды прияхом от рати и от голода. И идохом на вои ихъ за Римовъ, и богъ ны поможе - избихом я, а другия поимахом.</p>\r\n<p>И пакы Итлареву чадь избиша, и вежи ихъ взяхом, шедше за Голтавомь.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>А потім на Святославль гналися за половцями, а потім на город Торчеськ, і потім на Гюргів за половцями. І знову на тому боці [Дніпра], біля Красна, половців перемогли. І потім із Ростиславом же у Варина вежі взяли. І потім ходив у Володимир, знову Ярополка посадив, і Ярополк помер.</p>\r\n<p>І знову після батькової смерті і при Святополку на Стугні билися з половцями до вечора, билися біля Халіпа, і потім мир заключили із Тугорканом і з іншими князями половецькими, і у Глібових людей відібрали дружину всю свою.</p>\r\n<p>І потім Олег на мене прийшов із усією Половецькою землею до Чернігова, і билася дружина моя з ним вісім днів за малу греблю, і не дали ввійти їм в острог. Жаліючи душі християнські, і села палаючі, і монастирі, я сказав: &laquo;Не хвалитися поганим!&raquo; І віддав братові стіл батька його, а сам пішов на стіл батька свого в Переяслав: і вийшли ми в день святого Бориса із Чернігова, і їхали крізь полки половецькі, і не сто [воїв було в] дружині, а разом із дітьми і з жонами. І облизувалися на нас [половці], як вовки, стоячи і біля перевозу і на горах, та Бог і святий Борис не дав їм мене на поживу, неушкоджені дійшли ми до Переяслава.</p>\r\n<p>І сидів я у Переяславі три літа і три зими з дружиною своєю, і чимало бід прийняв від війни і від голоду. І ходили на воїв половецьких за Римів, і Бог нам поміг - перебили їх, а других в полон забрали.</p>\r\n<p>І знову ж, їтларевих чад перебили, і вежі їхні взяли, пішовши за Голтав.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>И Стародубу идохом на Олга, зане я бяше приложилъ к половцем. И на богъ идохом, с Святополком на Боняка за Рось.</p>\r\n<p>И Смолиньску идохом, с Давыдомь смирившеся. Паки, идохом другое с Ворониц<small>Ђ</small>.</p>\r\n<p>Тогда же и торци придоша ко мн<small>Ђ</small>, и с половець Чит<small>Ђ</small>евичи, идохом противу имъ на Сулу.</p>\r\n<p>И потомь паки идохом к Ростову на зиму, и по 3 зимы ходихом Смолинску. И-Смолиньска идох Ростову.</p>\r\n<p>И пакы, с Святополком гонихом по Боняц<small>Ђ</small>&raquo; но ли оли убиша, и не постигохом ихъ. И потомь по Боняц<small>Ђ</small> же гонихом за Рось, и не постигохом его.</p>\r\n<p>И на зиму Смолинску идохъ, и-Смоленска по Велиц<small>Ђ</small> дни выидох; и Гюргева мати умре.</p>\r\n<p>Переяславлю пришедъ на л<small>Ђ</small>то, собрах братью.</p>\r\n<p>И Бонякъ приде со вс<small>Ђ</small>ми половци къ Кснятиню, идохом за не ис Переяславля за Сулу, и богъ ны поможе, и полъкы ихъ поб<small>Ђ</small>дихом, к ннязи изьмахом л<small>Ђ</small>пшии, и по Рожеств<small>Ђ</small> створихом миръ съ Аепою, и поимъ у него дчерь, идохом Смоленьску. И потомь идох Ростову.</p>\r\n<p>Пришед из Ростова, паки идох на половци на Урубу с Святополком, и богъ ны поможе.</p>\r\n<p>И потомь паки на Боняка к Лубьну, и богъ ны поможе.</p>\r\n<p>И потомь ходихом к Воиню с Святополком; и потомь пакы на Донъ идохом с Святополком и с Давыдомъ, и богъ ны поможе.</p>\r\n<p>И к Выреви бяху пришли Аепа и Бонякъ, хот<small>Ђ</small>ша взяти и, ко Ромну идох со Олгомь и з<em> </em>д<small>Ђ</small>тми на нь, и они очутивше б<small>Ђ</small>жаша.</p>\r\n<p>И потомь к М<small>Ђ</small>ньску ходихом на Гл<small>Ђ</small>ба, оже ны бяше люди заялъ, и богъ ны поможе, и створихом свое мышленое.</p>\r\n<p>И потомь ходихом къ Володимерю на Ярославця, не терпляче злобъ его.</p>\r\n<p>А И-Щернигова до Кыева нестишьды <small>Ђ</small>здих ко отцю, днемъ есмъ пере<small>Ђ</small>здилъ до вечерни. А вс<small>Ђ</small>х путий 80 и 3 великих, а прока не испомню менших. И мировъ есмъ створилъ с половечьскыми князи безъ одиного 20, и при отци и кром<small>Ђ</small> отца, а дая скота много и многы порты сво<small>Ђ</small>.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>І в Стародуб ходили на Олега, тому що накладав був із половцями. І на Буг ходили, із Святополком на Боняка [ходив] за Рось.</p>\r\n<p>І в Смоленськ ходили, із Давидом замирилися. І знову, вдруге, ходили із Ворониці.</p>\r\n<p>Тоді ж і торки прийшли до мене із половцями-читієвичами, ходили ми їм назустріч на Сулу.</p>\r\n<p>І потім знову ходили на зиму в Ростов, і три зими ходили в Смоленськ. Із Смоленська пішов я в Ростов.</p>\r\n<p>І знову з Святополком ганялися за Боняком, але не убили, і не наздогнали його.</p>\r\n<p>І на зиму пішов у Смоленськ, із Смоленська вийшов після Великодня; і Гюргева [Юріева] мати померла.</p>\r\n<p>У Переяслав прийшов на літо, зібрав братів.</p>\r\n<p>І Боняк прийшов зі всіма половцями до Кснятика, пішли за ним із Переяслава за Сулу, і Бог нам поміг, і полки їхні перемогли, і князів полонили ліпших, і після Різдва уклали мир із Аепою, і, взявши у нього дочку, пішли в Смоленськ. І потім я пішов у Ростов.</p>\r\n<p>Повернувшись із Ростова, знову пішли на половців, на Урубу, із Святополком, і Бог нам поміг.</p>\r\n<p>І потім знову на Боняка до Лубен, і Бог нам поміг.</p>\r\n<p>І потім ходили до Воїня із Святополком; і потім знову пішли на Дон із Святополком із Давидом, і Бог нам поміг.</p>\r\n<p>І до Виру прийшли було Аепа і Боняк, хотіли взяти його; до Ромна пішли ми з Олегом і з дітьми на них, а вони, прочувши, втекли.</p>\r\n<p>! потім на Мінськ ходили на Гліба, який захопив наших людей, і Бог нам поміг, і зробили те, що задумали.</p>\r\n<p>І потім ходили до Володимира на Ярославця, не стерпівши злочинів його.</p>\r\n<p>А із Чернігова до Києва зо сто разів їздив до батька, за день приїжджав до вечерні. А всіх переїздів було вісімдесят три лише великих, а решту малих і не згадаю. І мирів укладав із половецькими князями без одного двадцять, і з батьком, і без батька, і дарував скотини багато і багато одягу свого.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>И пустилъ есмъ половечскых князь л<small>Ђ</small>пших изъ оковъ толико: Шаруканя 2 брата, Багубарсовы 3, Осеня брать<small>Ђ</small> 4, а вс<small>Ђ</small>х л<small>Ђ</small>пшихъ князий ин<small>Ђ</small>хъ 100. А самы князи богъ живы в руц<small>Ђ</small> дава: Коксусь с сыномь, Акланъ, Бурчевичь, Таревьскый князь Азгулуй, ин<small>Ђ</small>хъ кметий молодых 15, то т<small>Ђ</small>хъ живы ведь, ис<small>Ђ</small>къ, вметахь в ту р<small>Ђ</small>чку въ Салню. По чередам избьено не съ 200 в то время л<small>Ђ</small>пших.</p>\r\n<p>А се тружахъся лозы д<small>Ђ</small>я: понеже с<small>Ђ</small>дох в Чернигов<small>Ђ</small>, а и-Щернигова вышед, и до сего л<small>Ђ</small>та по сту уганивал и имь даром всею силою кром<small>Ђ</small> иного лова, кром<small>Ђ</small> Турова, иже со отцемь ловилъ есмъ всякъ зв<small>Ђ</small>рь.</p>\r\n<p>А се в Чернигов<small>Ђ</small> д<small>Ђ</small>ялъ есмъ: конь диких своима руками связалъ есмь въ пушах 10 и 20 живых конь, а кром<small>Ђ</small> того же по ровни <small>Ђ</small>здя ималъ есмъ своима рукама т<small>Ђ</small> же кони дики<small>Ђ</small>. Тура мя 2 метала на роз<small>Ђ</small>х и с конемъ, оленя мя одинъ болъ, а 2 лоси, одинъ ногами топталъ, а другой рогома болъ, вепрь ми на бедр<small>Ђ</small> мечь оттялъ, медв<small>Ђ</small>дь ми у кол<small>Ђ</small>на подъклада укусилъ, лютый зв<small>Ђ</small>рь скочилъ ко мн<small>Ђ</small> на бедры и конь со мною поверже. И богъ неврежена мя съблюде. И с коня много падах, голову си розбих дважды, и руц<small>Ђ</small> и ноз<small>Ђ</small> свои вередих, въ уности своей вередих, не блюда живота своего, ни щадя головы своея.</p>\r\n<p>Еже было творити отроку моему, то сам есмь створилъ, д<small>Ђ</small>ла на войн<small>Ђ</small> и на лов<small>Ђ</small>хъ, ночь и день, на зною и на зим<small>Ђ</small>, не дая соб<small>Ђ</small> упокоя. На посадники не зря, ни на биричи, сам творилъ, что было надоб<small>Ђ</small>, весь нарядъ, и в дому своемь то, я творйлъ есмь. И в ловчих ловчий нарядъ сам есмь держалъ, и в конюс<small>Ђ</small>х, и о сокол<small>Ђ</small>хъ и о ястреб<small>Ђ</small>х.</p>\r\n<p>Тоже и худаго смерда и убогы<small>Ђ</small> вдовиц<small>Ђ</small> не далъ есмъ силным обид<small>Ђ</small>ти, и церковнаго наряда и службы сам есмъ призиралъ.</p>\r\n<p>Да не зазрите ми, д<small>Ђ</small>ти мои, ни инъ кто, прочетъ, не хвалю бо ся ни дерзости своея, но хвалю бога и прославьляю милость его, иже мя гр<small>Ђ</small>шнаго и худаго селико л<small>Ђ</small>т сблюд от т<small>Ђ</small>хъ часъ смертныхъ, и не л<small>Ђ</small>нива мя былъ створилъ, худаго, на вся д<small>Ђ</small>ла челов<small>Ђ</small>чьская потребна. Да сю грамотицю прочитаючи, потъсн<small>Ђ</small>теся на вся д<small>Ђ</small>ла добрая, славяще бога с святыми его. Смерти бо ся, д<small>Ђ</small>ти, не боячи ни рати, ни от зв<small>Ђ</small>ри, но мужьское д<small>Ђ</small>ло творите, како вы богъ подасть. Оже бо язъ от рати, и от зв<small>Ђ</small>ри и от воды, от коня спадаяся, то никто же вас не можеть вредитися и убити, понеже не будет от бога повел<small>Ђ</small>нр. А иже от бога будеть смерть, то ни отець, ни мати, ни братья не могуть отьяти, но аче добро есть блюсти, божие блюденье л<small>Ђ</small>пл<small>Ђ</small><small>Ђ</small> есть челов<small>Ђ</small>чьскаго.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>І звільнив ліпших половецьких князів із оков стільки: двох братів Шаруканевих, Багубарсавих три, чотирьох братів Овчини, а всіх інших ліпших князів - сто. А самих князів Бог живими в руки давав: Коксус із сином, Аклан, Бурчевич, таревський князь Азгулуй, інших кметів молодих п\'ятнадцять, цих я живими привів, порубав і пометав у ту річку Славлий. А по черзі перебив біля 200 у те врем\'я ліпших.</p>\r\n<p>А от так трудився на ловах, доки сидів у Чернігові, і коли із Чернігова вийшов, і до цього літа по сто [звірів] заганяв і брав без великих зусиль, окрім інших ловів поза Туровом, де я з батьком ловив всякого звіра.</p>\r\n<p>А от що я в Чернігові робив: коней диких своїми руками у пущах в\'язав, по десять і двадцать живих коней, окрім того, по Росі їздячи, ловив своїми руками тих же диких коней. Два тури піднімали мене на рогах разом із конем, олень мене один бив і два лосі, один ногами топтав, а другий рогами бив, вепр мені на бедрі меч відняв, ведмідь мені біля коліна пітник укусив, лютий звір стрибнув мені на бедра і коня разом зі мною звалив. І Бог неушкодженим мене зберіг. І з коня багато падав, голову собі двічі розбивав, і руки і ноги собі вередив, в юності своїй вередив, не жалів життя свого, не щадив голови своєї.</p>\r\n<p>Те, що треба було робити отроку моєму, сам робив, на війні і на ловах, вночі і вдень, у спеку і в мороз, не даючи собі спокою. На посадників, ні на биричів не оглядався, сам робив, що було потрібно, весь наряд, і в домі своєму робив так само. І для ловчих ловчий наряд сам давав, що стосувалося і конюхів, і соколів, і яструбів.</p>\r\n<p>А ще і бідного смерда, і вбогу удовицю не давав сильним обідити, і церковний порядок і службу сам пильнував.</p>\r\n<p>Тож не осуджуйте мене, діти мої чи хто інший, коли прочитає це: бо не хвалю себе, ні відваги своєї, а хвалю Бога і прославляю милість його, що мене, грішного і мізерного, стільки літ оберігав від того смертного часу, і не лінивим створив мене, бідного, а на всі діла людські потрібним. І цю грамотицю прочитаючи, подвигніться на всі добрі діла, славлячи Бога і святих його. Смерті-бо, діти, не бійтеся ні ратньої, ні від звіра, але мужське діло вершіть, як вам Бог подасть. Бо коли я ні від раті, ні від звіра і від води, і з коня падаючи [не загинув], то і з вас ніхто не зможе постраждати і загинути, якщо не буде на те волі Божої. А коли від Бога буде смерть, то ні отець, ні мати, ні брати не зможуть відняти [у неї], але коли й про добро дбати, то Божа охорона ліпша людської.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>О многострастный и печалны азъ! Много борешися сердцемь, и одол<small>Ђ</small>вши, душе, сердцю моему, зане, тл<small>Ђ</small>ньн<small>Ђ</small> сущи, помышляю, како стати пред страшным судьею, каянья и см<small>Ђ</small>ренья не приимшим межю собою.</p>\r\n<p>Молвить бо иже: &laquo;Бога люблю, а брата своего не люблю, ложь есть&raquo;. И пакы: &laquo;Аще не отпустите прегр<small>Ђ</small>шений брату, ни вам отпустить отець вашь небесный&raquo;. Пророкъ глаголеть: &laquo;Не ревнуй лукавнующим, ни завиди творящимъ безаконье&raquo;. &laquo;Что есть добро и красно, но еже жити братья вкуп<small>Ђ</small>! Но все дьяволе наученье! то бо были рати при умных д<small>Ђ</small>д<small>Ђ</small>х наших, при добрых и при блаженыхъ отцихъ наших. Дьяволъ бо не хочет добра роду челов<small>Ђ</small>чскому, сваживаеть кы. Да се ти написах, зане принуди мя сынъ мой, его же еси хрстилъ, иже то с<small>Ђ</small>дить близь тобе, прислалх ко мн<small>Ђ</small> мужь свой и грамоту, река: &laquo;Ладимъся и см<small>Ђ</small>римся, а братцю моему судъ пришелъ. А в<small>Ђ</small> ему не будев<small>Ђ</small> местника, но възложив<small>Ђ</small> на бога, а стануть си пред богомъ; а Русьскы земли не погубим. И азъ вид<small>Ђ</small>х см<small>Ђ</small>ренье сына своего, схалихси, и бога устрашихся, рекох: онъ въ уности своей и в безумьи сице см<small>Ђ</small>ряеться - на бога укладаеть; азъ челов<small>Ђ</small>къ гр<small>Ђ</small>шенъ есмь паче вс<small>Ђ</small>х челов<small>Ђ</small>къ.</p>\r\n<p>Послушах сына своего, написах ти грамоту: аще ю. приимеши с добромь, ли с поруганьемь, свое же узрю на твоемь писаньи. Сими бо словесы варих тя переди, его же почаяхъ от тебе, см<small>Ђ</small>реньем и покаяньем, хотя от бога ветхыхъ своихъ гр<small>Ђ</small>ховъ оставления. Господь бо нашь не челов<small>Ђ</small>къ есть, но богъ всей вселен<small>Ђ</small>, иже хощеть, в мегновеньи ока вся створити хощеть, то сам претерп<small>Ђ</small> хуленье, и оплеванье, и ударенье, и на смерть вдася, животом влад<small>Ђ</small>я и смертью. А мы что есмы, челов<small>Ђ</small>ци гр<small>Ђ</small>шни и лиси? - днесь живи, а утро мертви, днесь в слав<small>Ђ</small> и въ чти, а заутра в гроб<small>Ђ</small> и бес памяти, ини собранье наше разд<small>Ђ</small>лять.</p>\r\n<p>Зри, брать, отца наю: что взяста, или чим има порты? но токмо оже еста створила души своей. Но да сими словесы, пославше бяше переди, брат, ко мн<small>Ђ</small> варити мене. Егда же убиша д<small>Ђ</small>тя мое и твое пред тобою, и бяше теб<small>Ђ</small>, узр<small>Ђ</small>вше кровь его и т<small>Ђ</small>ло пред тобою, и бяше теб<small>Ђ</small>, узр<small>Ђ</small>вше кровь его и т<small>Ђ</small>ло увянувшю, яко цв<small>Ђ</small>ту нову процветшю, яко же агньцю заколену, и рещи бяше, стояще над ним, вникнущи въ помыслы души своей: &laquo;Увы мн<small>Ђ</small>! что створих? И пождавъ его безумья, св<small>Ђ</small>та сего мечетнаго кривости ради нал<small>Ђ</small>зох гр<small>Ђ</small>х соб<small>Ђ</small>, отцю и матери слезы&raquo;.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&laquo;О многостраждальний і печальний я! Багато, душе, борешся з серцем, і перемагаєш серце моє, оскільки, тлінням будучи, роздумую, як по\'стати перед страшним суддею, не покаявшися і не примирившися між собою. Бо коли хто каже: &laquo;Бога люблю, а брата свого не люблю, то лож&raquo;. І йде: &laquo;Коли не відпустите гріхів братових, то і вам не відпустить Отець ваш небесний&raquo;. Пророк каже: &laquo;Не змагайся з лукавими, не заздри творящим беззаконня&raquo;. &laquo;Що добріше і прекрасніше, ніж жити братам у згоді!&raquo; А все від диявольського наущення, бо ж були війни і за розумних дідів наших, і за добрих і блаженних отців наших. Диявол-бо не хоче добра родові людському, сварить нас. Так оце я тобі написав, оскільки приневолив мене син мій, якого ти, [Боже], хрестив, і який ото сидить поблизу тебе. Прислав він до мене мужа свого із грамотою, кажучи: &laquo;Поладимося і помиримося, а братові моєму суд [Божий] прийшов. А ми з тобою не будемо месниками, а покладемо те на Бога, коли вони самі стануть перед Богом, а Руської землі не будемо губить&raquo;. І я побачив смиренність сина свого, пом\'якшав і, Бога устрашившись, сказав: &laquo;Він по молодості своїй і нерозумності такий смиренний, на Бога уповає, я ж чоловік грішний більше всіх людей&raquo;.</p>\r\n<p>Послухав я сина свого, написав тобі грамоту: &laquo;Чи приймеш її з добром, чи з хулою, побачу із твоєї відповіді. Адже цими словами я попередив тебе, чого надіюсь від тебе, смиренністю і покаянням, бажаючи в Бога старих гріхів своїх відпущення. Господь бо наш - не чоловік, але Бог всієї вселенної, і як захоче, то в одну мить створить все, що хоче, та сам перетерпів і хулу, і оплюзання, і биття, і на смерть віддав себе, над життям маючи владу і смертю. А що ми, люди грішні і злі? - сьогодні живі, а завтра мертві, сьогодні в славі і в пошані,<sub>;</sub> а завтра у гробу і в забутті. Інші надбане нами розділять.</p>\r\n<p>Подивись, брате, на батьків наших: що взяли [з собою] чи на що їм [там] одяги? Але тільки [і взяли з собою те], що зробили вони душі своїй. Тобі першому, брате, належало послати до мене з цими словами. Коли ж убили дитя моє і твоє у тебе на очах, годилося б тобі, побачивши кров його і тіло, зів\'яле подібно квітці, яка щойно розквітла, подібно ягняті заколеному, сказати, стоячи над ним, вникнувши в помисли душі своєї: &laquo;Горе мені! Що я зробив? Я, скориставшись його нерозумністю, ради неправди світу цього марнотного, взяв гріх на себе, а батькові й матері його дав сльози&raquo;.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>И рещи бяше Давыдскы: &laquo;Азъ знаю, гр<small>Ђ</small>х мой предо мною есть воину&raquo;. Не крове д<small>Ђ</small>ля пролитья,- помазаникъ божий Давыдъ, прелюбод<small>Ђ</small>янье створивъ посыпа главу свою и плакася горко; во тъ час, отда ему согр<small>Ђ</small>шенья его богъ. А к богу бяше покаятися, а ко мн<small>Ђ</small>, бяше грамоту ут<small>Ђ</small>шеную, а сноху мою послати ко мне, зане н<small>Ђ</small>сть в ней ни зла, ни добра, да бых обуимъ оплакалъ мужа ея и оны сватбы ею, въ п<small>Ђ</small>сний м<small>Ђ</small>сто: не вид<small>Ђ</small>хъ бо ею перв<small>Ђ</small>е радости, ни в<small>Ђ</small>нчанья ею, за гр<small>Ђ</small>хы своя! А бога д<small>Ђ</small>ля пусту ю ко мн<small>Ђ</small> вборз<small>Ђ</small> с первым сломь, да с нею кончавъ слезы, посажю на м<small>Ђ</small>ст<small>Ђ</small>, и сядеть акы горлица на сус<small>Ђ</small> древ<small>Ђ</small> жел<small>Ђ</small>ючи, а язъ ут<small>Ђ</small>шюся о боз<small>Ђ</small>.</p>\r\n<p>Т<small>Ђ</small>м бо путем шли д<small>Ђ</small>ди и отци наши: судъ от бога ему пришелъ, а не от тебе. Аще бы тогда свою волю створилъ, и Муромъ нал<small>Ђ</small>злъ, а Ростова бы не заималъ, а послалъ ко мн<small>Ђ</small>, отсюда ся быхом уладили. Но сам разум<small>Ђ</small>й, мн<small>Ђ</small> ли бы послати к теб<small>Ђ</small> достойно, ци ли тоб<small>Ђ</small> ко мн<small>Ђ</small>? Да же еси вел<small>Ђ</small>лъ д<small>Ђ</small>тяти: &laquo;Слися къ отцю&raquo;, десятью я есмъ послалъ.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>І сказати належало б [тобі], як сказав Давид: &laquo;Я знаю, гріх мій стоїть переді мною завжди&raquo;. Не ради пролитої крові помазаник божий Давид, перелюб вчинивши, посипав голову свою [попелом] і плакав гірко. У той же час відпустив йому Бог гріхи його. А перед Богом треба було б [тобі] покаятися, а до мене написати листа утішливого, а сноху мою послати до мене, бо нема в ній ні зла, ні добра, хай би я обійняв її і оплакав мужа її і свайбу їхню, замість пісень, бо не бачив їхньої першої радості, ні вінчання їхнього через гріхи свої! Так, Бога ради, відпусти її до мене якнайшвидше з першим послом, хай я з нею закінчу сльози, посажу її на місці і сяде вона як горлиця на сухому дереві, жаліючи, а я утішуся в Бозі.</p>\r\n<p>Тим-бо шляхом ішли діди і батьки наші: суд від Бога йому прийшов, а не від тебе. Якби ти тоді свою волю вволив і Муром добув, а Ростова б не займав і послав до мене, то ми тут би і домовилися. Але сам розсуди, чи мені посилати до тебе належало, а чи тобі до мене? Якби ти звелів дитяті: &laquo;Порадься з батьком&raquo;, я б десять раз послав.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Дивно ли, оже мужь умерлъ в полку ти? Л<small>Ђ</small>пше суть измерли и роди наши. Да не выискывати было чюжего,- ни мене в соромъ, ни в печаль ввести. Научиша бо и паропци, да быша соб<small>Ђ</small> нал<small>Ђ</small>зли, но оному нал<small>Ђ</small>зоша зло. Да еже нечнеши каятися богу, и мн<small>Ђ</small> добро сердце створити, пославъ солъ свой, или пископа, и грамоту напиши с правдою, той волость възмешь с добромъ, и наю сердце обратиши к соб<small>Ђ</small>, и л<small>Ђ</small>пше будемъ яко и преже; н<small>Ђ</small>смъ ти ворожбить, ни местьникъ. Не хот<small>Ђ</small>хъ бо крови твоея вид<small>Ђ</small>ти у Стародуба: но не дай ми богъ крови от руку твоею вид<small>Ђ</small>ти, ни от повел<small>Ђ</small>нья твоего, ни котораго же брата. Аще ли лжю, а богъ мя в<small>Ђ</small>даеть и крест честный. Оли то буду гр<small>Ђ</small>х створилъ, оже на тя шедъ к Чернигову, поганых д<small>Ђ</small>ля, а того ся каю; да то языком братьи пожаловахъ, и пакы е пов<small>Ђ</small>дах, зане челов<small>Ђ</small>къ есмъ.</p>\r\n<p>Аще ти добро, да с т<small>Ђ</small>мь... али ти лихо е, да то ти с<small>Ђ</small>дить сынъ твой хрестьный с малым братомъ своимъ, хл<small>Ђ</small>бъ <small>Ђ</small>дучи д<small>Ђ</small>день, а ты с<small>Ђ</small>диши в своемъ - и о се ся ради; али хочеши тою убити, а то ти еста, понеже не хочю я лиха, но добра хочю братьи и Русьск<small>Ђ</small>й земли. А его же то и хощеши насильем, тако в<small>Ђ</small> даяла и у Стародуба и милосердуюча по теб<small>Ђ</small>, очину твою. Али богъ послух тому, с братом твоимъ рядилися есв<small>Ђ</small>, а не поможеть радятися бес тебе. И не створила есв<small>Ђ</small> лиха ничтоже, ни рекла есв<small>Ђ</small>: сли к брату, дондеже уладимся. Оже ли кто вас не хочеть добра, ни мира хрестьяном, а не буди ему от бога мира узр<small>Ђ</small>ти на оном св<small>Ђ</small>т<small>Ђ</small> души его!</p>\r\n<p>Не по нужи ти молвлю, ни б<small>Ђ</small>да ми которая по боз<small>Ђ</small>, сам услышишь; нодуша ми своя лутши всего св<small>Ђ</small>та сего.</p>\r\n<p>На страшн<small>Ђ</small>й при бе - суперник обличаюся, и прочее.</p>\r\n<p>&laquo;Премудрости наставниче и смыслу давче, несмысленым казателю и нищим заступниче! Утверди в разум<small>Ђ</small> мое сердце, владыко! Ты дажь ми слово, отче, се бо устнама моима не възбрани въпити ти: милостиве, помилуй падшаго!&raquo;. &laquo;Упованье мое богъ, приб<small>Ђ</small>жище мое Христосъ, покровъ мой святый духъ&raquo;. Надеже и покрове мой, не презри мене, благая! Тебе бо имуще, помощницю в печали и в бол<small>Ђ</small>зни и от злых вс<small>Ђ</small>х, и тебе славлю, преп<small>Ђ</small>тая! И разум<small>Ђ</small>йте и видите, яко азъ есмь богъ, испытаяй сердця и св<small>Ђ</small>дый мысли, обличаяй д<small>Ђ</small>ла, опаляяй гр<small>Ђ</small>хы, судяй сирот<small>Ђ</small>, и убогу и нищю&raquo;. &laquo;Всклонися, душе моя, и д<small>Ђ</small>ла своя помысли, яже зд<small>Ђ</small>я, пред очи свои принеси, и капля испусти слезъ своих, и пов<small>Ђ</small>жь яв<small>Ђ</small> д<small>Ђ</small>янья и вся мысли Христу, и очистися&raquo;. Андр<small>Ђ</small>а честный, отче треблаженый, пастуше Критьскый! Не престай моляся за ны чтущая тя, да избудем вси гн<small>Ђ</small>ва, и печали, и тля, и гр<small>Ђ</small>ха и б<small>Ђ</small>д же, чтуще память твою в<small>Ђ</small>рно. Град свой схрани, д<small>Ђ</small>вице, мати чистая, иже о теб<small>Ђ</small> в<small>Ђ</small>рно царствуеть, да тобою кр<small>Ђ</small>питься и тоб<small>Ђ</small> ся над<small>Ђ</small>еть, поб<small>Ђ</small>жать вся брани, испром<small>Ђ</small>тает противныя и творить послушанье. &laquo;О преп<small>Ђ</small>тая мати, рожьшия вс<small>Ђ</small>х святыхъ пресвятаго слова! Приимши нынешнее послушанье, от всякия напасти заступи и грядущия мукы к теб<small>Ђ</small> вопьющих. Молим ти ся, раби твои, и прекланяем си кол<small>Ђ</small>ни сердця нашего: приклони ухо твое, чистая, и спаси ны в скорбех погружающаяся присно, и сблюди от всякого пл<small>Ђ</small>ненья вражья твой град, богородице! Пощади, боже, насл<small>Ђ</small>дья твоего, прегр<small>Ђ</small>шенья наша вся презри, нын<small>Ђ</small> нас им<small>Ђ</small>я молящих тя, на земли рожьшюю тя бе - с<small>Ђ</small>мене, земную милость, изволивъ обратитися, Христе, в челов<small>Ђ</small>чьство&raquo;. Пощади мя, спасе, рожься и схрань рожьшюю тя нетл<small>Ђ</small>нну по рожеств<small>Ђ</small>, и егда сядеши судити д<small>Ђ</small>ла моя, яко безгр<small>Ђ</small>шенъ и милостивъ, яко богъ и челов<small>Ђ</small>колюбець. Д<small>Ђ</small>во пречистая, неискусна браку, богообрадованая, в<small>Ђ</small>рным направленье! Спаси мя погыбшаго, к Сыну си вопьющи: Помилуй мя, господи, помилуй; егда хощеши судити, не осуди мя въ огнь, ни обличи мене яростью си; молит тя д<small>Ђ</small>ва чистая, рожшая ти, Христе, и множство ангелъ и мученикъ зборъ.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Чи дивно, що муж поліг у поході? Так гинули ліпші із предків наших. Хай би не вишукував був чужого і мене у ганьбу і в печаль не вводив. Підговорили бо його парубки, хай би собі що-небудь добули [Самі], але йому здобули зло. І якщо почнеш каятися перед Богом, і до мене щиросердним будеш, пославши посла свого чи єпископа, і листа напишеш із запевненням [миру], то й волость візьмеш добром, і наше серце прихилиш до себе, і ліпше будемо, ніж раніше: я тобі не ворог і не месник. Не хотіз-бо крові твоєї вйдіти у Стародубі, але не дай мені. Бог кров від руки твоєї видіти, чи від повеління твого, чи котрогось із братів. Якщо ж я брешу, хай Бог мене судить і хрест чесний. Чи в тому гріх сотворив, що на тебе ішов у Чернігів, ради поганих, у тому каюся, про те я братам висловив жаль і знову повідав, бо я - людина.</p>\r\n<p>Якщо тобі добре, хай з тим, якщо тобі лихо, то от у тебе сидить син твій хрещений із малим братом своїм, Хліб їдять дідівський, а ти сидиш на своєму [хлібі],- на цьому і домовляйся. Якщо ж хочеш їх убить, то вони у тебе, оскільки не хочу я лиха, але добра хочу братії 5 Руській землі. А те, що хочеш взяти насильно, давали тоді у Стародубі як вотчину твою, милосердуючи до тебе. Сам Бог свідок тому, що ми з братом твоїм радилися! на випадок, якщо він не зможе домовитися без тебе. І ми не зробили ніякого лиха, навіть не сказали: &laquo;Домовляйся з братом, доки не домовитесь&raquo;. Якщо котрийсь із вас не хоче добра, ні миру християнам, то не дасть йому Бог узріти миру душі його на тому світі!</p>\r\n<p>Не з потреби говорю тобі чи біди моєї, посланої Богом, сам почуєш. Але душа мені своя лучча всього світа цього. На страшному суді без оскаржувача сам на себе виказуватиму і інше.</p>\r\n<p>&laquo;Премудрості наставниче і розуму давче, нерозумних учителю і нужденних заступнику! Утверди в розумі серце моє, Владико! Ти дай мені дар слова, Отче, вустам моїм не перешкоджай волать до тебе: &laquo;Милостивий, помилуй падшого!&raquo; &laquo;Надія моя - Бог, пристанище - Христос, оборона - Святий Дух! Надіє і обороно моя, не зневаж мене, блаженна. Ти у мене помічниця в печалі, і в недугах, і від усіх бід, і тебе славлю, оспівана! ї розумійте і видьте, що я Бог, випробовую серця і відаю помисли, викриваю діла, спалюю гріхами, даю суд сироті, і бідному, і злиденному&raquo;. &laquo;Схилися, душе моя, і про діла свої подумай, які содіяла, очима своїми оглянь їх, і краплю зрони сліз своїх, і повідай відкрито всі діла свої і всі помисли Христу, і очистись&raquo;. Андрію чесний, отче преблаженний, пастуше Крітський! Не лишай молитися за нас, шануючих тебе, хай позбудемося всі, шануючі пам\'ять твою вірно, гніву, і печалі, і тління, і гріха, і бід. Город свій схорони, Діво, Матір пречистая, який тобою чесно царствує, хай тобою кріпиться і на тебе надіється, перемагає у всіх війнах, відмітає ворогів і тримає їх у послусі. &laquo;О Пречистая Матір, що народила найсвятіше із святих - Слово! Прийнявши нинішній послух, від всілякої напасті і грядущої муки захисти [нас], до тебе волаючих. Молимося тобі, раби твої, і схиляємо коліна серця нашого: схили вухо твоє, чистая, і спаси нас, в скорботах занурених вічно, і сохрани від всякого ворожого полону твій град, Богородице! Пощади, Боже, спадщину твою, прогрішіння наші всі зневаж, маючи нині нас, воздающих молитви тобі і тій, що родила тебе на землі без сімені, земну милість, призволившу обернути, Христе, в чоловіка&raquo;. Пощади мене, Спасе, народився і зберіг ту, що народила тебе, нетлінною після народження твого, коли сядеш судити діла мої, як безгрішний і милостивий, як Бог і чоловіколюбець.</p>\r\n<p>Діво пречистая, неспокушена шлюбом, богообрадувана, віруючим настанова! Спаси мене, пропащого, до Сина твого волаючого: &laquo;Помилуй мене, Господи, помилуй! Якщо хочеш судити, не осуди мене на вогонь, не плямуй мене гнівом своїм,- молить тебе Діва чистая, рождениця твоя, Христе, і безліч ангелів і мучеників сонм&raquo;.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>О Христ<small>Ђ</small> Исус<small>Ђ</small> господ<small>Ђ</small> нашемъ, ему же подобаеть честь и слава, отцю и сыну и святому духу, всегда и нын<small>Ђ</small>, присно, в<small>Ђ</small>къ.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><em>[Продовження Л<small>Ђ</small>тописи по Ипатскому списку]</em></p>\r\n<p>&nbsp;</p>\r\n<p>Се ще хощю сказати, яже слышахъ преже сихъ 4 л<small>Ђ</small>тъ, яже сказа ми Гурята Роговичь Новгородець, глаголя сице: яко послахъ отрока своего в Печеру, люди, иже суть дань дающе Новугороду; і пришедшю отроку моему к нимъ, и оттуд<small>Ђ</small> иде въ Угру. Угра же суть людье языкъ н<small>Ђ</small>мъ, и със<small>Ђ</small>дятся съ Само<small>Ђ</small>дью на полунощныхъ сторонахъ. Угра же рекоша отроку моему: &laquo;дивно находимъ мы чюдо ново, егоже н<small>Ђ</small>смы слыхали преже сихъ л<small>Ђ</small>тъ; се же нын<small>Ђ</small> третьее л<small>Ђ</small>то поча быти: суть горы заидуче в луку моря, имьже высота акы до небеси, и в горахъ тыхъ кличь великъ и говоръ, и с<small>Ђ</small>куть гору, хотяще прос<small>Ђ</small>чися; и есть в гор<small>Ђ</small> той прос<small>Ђ</small>чено оконце мало, и туда молвять, не разум<small>Ђ</small>ти языку ихъ, но кажють жел<small>Ђ</small>зо и помавають рукою, просяще жел<small>Ђ</small>за; и аще кто дасть имъ жел<small>Ђ</small>зо, или ножь, или сокиру, и они дають скорою противу. Есть же путь до горъ тыхъ проходимъ пропастьми, сн<small>Ђ</small>гомъ и л<small>Ђ</small>сомъ; т<small>Ђ</small>мь не доходимъ ихъ всегда; есть же и подаль на полунощьи&raquo;. Мн<small>Ђ</small> же рекшю къ Гурят<small>Ђ</small>: &laquo;се суть людье закл<small>Ђ</small>нлен<small>Ђ</small> Олексанъдромъ Макидоньскомъ царемъ&raquo;. Якоже сказа о нихъ Мефедий Патарийскъ, глаголя: Олександръ, царь Макидоньский, възыде на въсточныя страны до моря, нар<small>Ђ</small>цаемое Солнце м<small>Ђ</small>сто, и від<small>Ђ</small> челов<small>Ђ</small>кы нечистыя, отъ племени Афетова; ихъ же нечистоту вид<small>Ђ</small>въ: ядяху скверну всяку, комары, мухы, коткы, зм<small>Ђ</small>я, мертвеца не погребати, но ядяху и женьски<small>Ђ</small> изъврагы и скоты вся нечистыя. То вид<small>Ђ</small>въ Олександръ убояся, еда како умножаться осквернять землю, [загна ихъ] на полунощныя страны у горы высокыя; и Богу повел<small>Ђ</small>вшю, соступишася о нихъ горы полунощьныя, токмо не ступишася о нихъ горы 12 локътю; и створиша врата м<small>Ђ</small>деная, и помазаша суньклитомь, и аще хотять взяти, и не возмогуть, ни огнемъ могуть ижьжещи; в<small>Ђ</small>шь бо суньклитова сица есть: ни огнь можетъ ижьжещи его, ни жел<small>Ђ</small>зо его пріиметь; у посл<small>Ђ</small>дняя же дни по сихъ осми кол<small>Ђ</small>нъ, иже изидуть отъ пустыня Етривьския, изидуть си скверныи языци, яже суть в горахъ полунощныхъ, по повел<small>Ђ</small>нью Божью. Но мы на прежер<small>Ђ</small>ченое уворотимься, якоже б<small>Ђ</small>хомъ глаголали перв<small>Ђ</small>е.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Во ім\'я Христа Ісуса, Господа нашого, йому ж належить честь і слава, Отцю і Сину і Святому Духу, завжди і нині, і во віки віків.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><em>Продовження Іпатського літопису</em></p>\r\n<p>&nbsp;</p>\r\n<p>Тут я хочу розповісти, що чув за чотири роки до цього. Оповідав мені Гурята Рогович, новгородець, розказуючи таке: &laquo;Послав я отрока свого до Печери, людей, які дань дають Новгороду. І прийшов отрок мій до них, а звідти пішов до угрів. Угри - це люди, що говорять німою мовою, і сусідять вони з самоїддю в північних краях. Угри ж сказали отроку моєму: &laquo;Дивне чудо нове ми знайшли, про нього не чули до цих літ. Се нині третє літо продовжується: є гори, що заходять в затоку морську, а висотою аж до небес, і в горах тих [чутно] крик великий і гомін, і рубають гору, хотячи прорубатися, і є в горі тій прорубане віконце мале, і звідти говорять, і не зрозуміти мови їхньої, але показують на залізо і роблять знаки рукою, просячи заліза.</p>\r\n<p>І коли хто дасть їм залізо, чи ніж, чи сокиру,- і вони в обмін дають хутра. Дорога ж до гір тих непрохідна через прірви, сніги і ліси, тому й не доходимо до них ніколи. Іде дорога та і далі на північ&raquo;. Я ж сказав Гуряті: &laquo;Це люди, заклепані царем Олександром Македонським, як розповідає, про них Мефодій Патарійський, кажучи: &laquo;Олександр, цар Македонський прийшов у східні країни до моря, яке називають Сонячним місцем. І побачив [там] людей нечистих із племені Афетового. І їхню нечистоту бачив: їли [вони] погань всіляку: комарів, мух, котів, змій; і померлих не хоронили, а пожирали, і жіночі викидиші, і скотів усіх нечистих. Побачив усе те Олександр і злякався, щоб, коли розмножаться, не споганили землю. [І загнав їх] в опівнічні краї у гори високі. І за божим повелінням зімкнулися за ними гори північні, тільки не зімкнулися за ними гори на 12 ліктів. І звелися ворота мідні і покрилися сунклітом. І коли хтось захоче їх взяти, то не зможе. Ні вогнем не можна їх спалити: річ бо сунклітова така: ні вогонь не може її спалити, ні залізо її не бере. У останні ж дні після цих восьми колін, що вийдуть із пустині Єтривської, вийдуть ось ці скверні народи, що знаходяться в горах північних з Божого повеління&raquo;.</p>\r\n<p>Але ми до попереднього повернемося, про що раніше говорили.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>',NULL,1854,5,1,NULL,25);
INSERT INTO `publications_publication` VALUES
(4706,'P','2008-12-20',0,NULL,'Note','Повість врем\'яних літ Роки 6584 [1076] — 6604 [1096]','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В літо 6584 [1076]. Ходив Володимир, син Всеволода, і Олег Святославич допомагати ляхам проти чехів.</span></span></p>','<p><a href=\"http://izbornyk.org.ua/pvlyar/yar08.htm\">Роки 6584 [1076] &mdash; 6604 [1096]</a></p>\r\n<p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6584. Ходи Володимеръ, сынъ Всеволожь и Олегъ Святъславль Ляхомь в помочь на Чехы.</p>\r\n<p>В се же л<small>Ђ</small>то преставися Святославъ, сынъ Ярославль, м<small>Ђ</small>сяца декабря въ 27, отъ р<small>Ђ</small>занья желве, и положенъ бысть у Спаса; и с<small>Ђ</small>де по немь Усеволодъ на стол<small>Ђ</small>, м<small>Ђ</small>сяца генваря въ 1 день.</p>\r\n<p>В се же л<small>Ђ</small>то родися у Володимера сынъ Мьстиславъ, внукъ Всеволожь.</p>\r\n<p>В л<small>Ђ</small>то 6585. Поиде Изяславъ с Ляхы, Всеволодъ же поиде противу ему. И бывшу Всеволоду, с<small>Ђ</small>де Борисъ в Чернигов<small>Ђ</small> м<small>Ђ</small>сяца мая 4 день, бысть княженья его дний 8, и б<small>Ђ</small>жа Тмутороканю к Романов<small>Ђ</small>. Всеволодъ же взыиде противу брату Изяславу на Волынь, и створи миръ, и пришедъ Изяславъ с<small>Ђ</small>де в Киев<small>Ђ</small>, м<small>Ђ</small>сяца іюля 15 день; Олегъ же, Святославль сынъ, б<small>Ђ</small> во Всеволода в Черн<small>Ђ</small>гов<small>Ђ</small>.</p>\r\n<p>В л<small>Ђ</small>то 6586. Б<small>Ђ</small>жа Олегъ, сынъ Святославль, Тмутороканю отъ Всеволода, м<small>Ђ</small>сяца априля въ 10 день.</p>\r\n<p>В се же л<small>Ђ</small>то убьенъ бысть Гл<small>Ђ</small>бъ, Святославль сынъ, в Заволочьи. Б<small>Ђ</small> же Гл<small>Ђ</small>бъ милостивъ на вбогия и страньнолюбивъ, тщанье им<small>Ђ</small>я къ церквамъ, теплъ на в<small>Ђ</small>ру и кротокъ, взоромъ красенъ; его же т<small>Ђ</small>ло положено бысть в Чернигов<small>Ђ</small> за Спасомь, м<small>Ђ</small>сяца іюля 23 день. С<small>Ђ</small>дящу в него м<small>Ђ</small>сто Святополку в Нов<small>Ђ</small>город<small>Ђ</small>, сыну Изяславлю, Ярополку с<small>Ђ</small>дящу у Смоленьска, приведе Олегъ и Борисъ поганыя на Рускую землю, и поидоста на Всеволода с Половц<small>Ђ</small>. Всеволодъ же изоиде противу има на Съжици, и побидиша Половц<small>Ђ</small> Русь, и мнози убьени быша ту: убьенъ бысть ту Иванъ Жирославичь и Тукы, Чюдинь братъ, Пор<small>Ђ</small>й, и ини мнози, м<small>Ђ</small>сяца августа 25 день. Олегъ же і Борисъ придоста Чернигову, мьняще одол<small>Ђ</small>вше, а земли Руской много зла створившимъ, прольяше кровь хрестьянску, еяже кровь взыщеть Богъ отъ руку ея, отв<small>Ђ</small>тъ дати за погиблыя душа хрестьяньск<small>Ђ</small>. Всеволодъ же приде къ брату своему Изяславу Кыеву, и ц<small>Ђ</small>ловавшеся с<small>Ђ</small>доста; Всеволодъ же испов<small>Ђ</small>да ему все бывшее. И рече ему Изяславъ: &laquo;брате! не тужи; видиши бо, колко ся мн<small>Ђ</small> сключи зла? первое бо, не выгнаша ли мене и им<small>Ђ</small>нье мое разграбиша? и паки кую вину створилъ есмь, не изгнаста ли вы мене брата своя? и не блудилъ ли по чюжимъ землямъ, им<small>Ђ</small>нья лишенъ быхъ? не створи зла ничтоже; і нын<small>Ђ</small>, братъ, не тужив<small>Ђ</small>; аще будеть нама причастье в Руской земл<small>Ђ</small>, то об<small>Ђ</small>ма, аще лишена будев<small>Ђ</small>, то об<small>Ђ</small>ма, язъ сложу главу свою за тя&raquo;. И се рекъ, ут<small>Ђ</small>ш<small>Ђ</small> Всеволода, и повел<small>Ђ</small> збирати воя отъ мала до велика; и поиде Изяславъ сь Ярополкомъ, сыномъ своимъ, и Всеволодъ с Володимеромъ, сыномъ своимъ. И поидоша к Чернигову, и Черниговц<small>Ђ</small> затворишася у град<small>Ђ</small>; Олегъ же и Борисъ бяшета в Чернигов<small>Ђ</small>. Черн<small>Ђ</small>говьцемь же не отворящимся, приступиша ко граду; Володимеръ же приступи къ вратомъ въсточнымъ, отъ стръженей отъя врата, і взяша градъ околний, і пожгоша огнемъ, людемъ выб<small>Ђ</small>гшимъ во днешний городъ. Изяславъ же и Всеволодъ слышаста, яко иде Олегъ и Борисъ противу, Изяславъ же и Всеволодъ урянився, поидоста отъ града противу Олгов<small>Ђ</small>. И рече Олегъ к Борисов<small>Ђ</small>: &laquo;не ходив<small>Ђ</small> противу, не можев<small>Ђ</small> стати противу четыремь княземь; но пошлив<small>Ђ</small> с молбою къ строема своіма&raquo;. И рече ему Борисъ: &laquo;ты зри готова, язъ имъ противенъ всимъ&raquo;; и похвалився велми, не в<small>Ђ</small>ды, яко Богъ гордымъ противится, см<small>Ђ</small>ренымъ же благодать даеть, и да не похвалится силны силою своею. И поидоста протіву, и бывшимъ имъ на м<small>Ђ</small>сьт<small>Ђ</small> на Нежатини нив<small>Ђ</small>, и совокупившимъся обоимъ, бысть с<small>Ђ</small>ча зла: и п<small>Ђ</small>рвое убиша Бориса, сына Вячьславля, похвалившася велми; Изяславу стоящю в п<small>Ђ</small>шьсц<small>Ђ</small>хъ, унезапу при<small>Ђ</small>хавъ одинъ, удари копьемь за плеча; и тако убьенъ бысть Изяславъ, сынъ Ярославль. Предолжен<small>Ђ</small> же бывш<small>Ђ</small> с<small>Ђ</small>ч<small>Ђ</small>, поб<small>Ђ</small>же Олегъ в мал<small>Ђ</small> дружині, едва втече, и б<small>Ђ</small>жа Тмютороканю. Убьенъ бысть князь Изяславъ м<small>Ђ</small>сяца октября въ 3 день, вземше же т<small>Ђ</small>ло его, привезоша в лодьи и поставиша противу Городцю; и изидоша противу ему всь городъ Киевъ, и възложиша на сан<small>Ђ</small> повезоша и с п<small>Ђ</small>сньми попове и черноризици, и понесоша въ градъ, и не б<small>Ђ</small> лз<small>Ђ</small> слышати п<small>Ђ</small>нья въ плач<small>Ђ</small> велиц<small>Ђ</small> и вопл<small>Ђ</small>: плакася по немь весь городъ Киевъ. Ярополкъ же идяще по немь, плачася съ дружиною своею: &laquo;отче, отче мой! что еси бес печали пожіль на св<small>Ђ</small>т<small>Ђ</small> семь, многи напасти приемь отъ люд<small>Ђ</small>й и отъ братья своея? се же погибе не отъ брата, но за брата своего положи главу свою&raquo;. И прянесъше положиша т<small>Ђ</small>ло его въ церкв<small>Ђ</small> святыя Богородица, уложиша и в раку камяну и моромряну. Б<small>Ђ</small> же Изяславъ мужь взоромъ красенъ, т<small>Ђ</small>ломъ великь, незлобивъ нравомъ, кривды ненавидя, любя правду; клюкъ же в немь не б<small>Ђ</small>, ни льсти, но простъ умомъ, не воздая зла за зло. Колко ему створиша Киян<small>Ђ</small>! самого выгнаша, а домъ его разграбиша, и не възда противу тому зла; аще ли кто д<small>Ђ</small>еть: &laquo;Киян<small>Ђ</small> ис<small>Ђ</small>клъ, котор<small>Ђ</small>и же высадили Всеслава ис поруба&raquo;, то сь того не створ<small>Ђ</small>, ни сынъ его. Паки же брата своя выгнаста и, и ходи по чужей земл<small>Ђ</small>, блудя; и с<small>Ђ</small>дящю ему паки на своемь стол<small>Ђ</small>, Всеволоду пришедшю поб<small>Ђ</small>жену к нему, не рече ему: &laquo;колко подъяхъ отъ ваю зла за зло?&raquo; но ут<small>Ђ</small>ши и рекъ ему: &laquo;елма же ты, брате мой, показа ко мн<small>Ђ</small> любовь, уведе мя на столъ мой, нарекъ мя стар<small>Ђ</small>йши себе, се язъ не помяну злобы перво<small>Ђ</small>; ты мн<small>Ђ</small> еси братъ, а я тоб<small>Ђ</small>, и положю главу свою за тя&raquo;, еже и бысть; не рече бо ему: &laquo;колко зло створиста мн<small>Ђ</small>, и се нын<small>Ђ</small> тоб<small>Ђ</small> ся приключи&raquo;, не рече, &laquo;сего кром<small>Ђ</small> мене&raquo;, но на ся перея печаль братню, показа любовь велику, свершая апостола, глаголюща: ут<small>Ђ</small>шайте печальныя. По истин<small>Ђ</small>, аще что створилъ есть на св<small>Ђ</small>т<small>Ђ</small> семь етеро согр<small>Ђ</small>шенье, отдасться ему, зане положи главу свою за брата своего, ни желая болшая части, ни им<small>Ђ</small>н<small>Ђ</small>я хотя болшаго, но за братню обиду. О сяковыхъ бо и Господъ рече: да кто положить душю свою за други своя [сей велій наречеся въ царствіи небеснемь] Соломонъ же рече: братье, в б<small>Ђ</small>дахъ пособиви бывайте, любовь бо есть выше всего. Якоже Іоаннъ глаголеть: Богъ [любы есть], и пребывая у любви, у Бози пребываеть, и Богъ в немь пребываеть; о семь свершаеться любы, да достоянье имамъ в день судный, да якоже онъ есть, и мы есмы в мир<small>Ђ</small> семь; боязни н<small>Ђ</small>сть в любви, но свершена любы вонъ изм<small>Ђ</small>щеть боязнь, яко боязнь мученье имать, боя же ся н<small>Ђ</small>сть свершенъ у любви; аще кто речеть: Бога люблю, а<em> </em>брата своего ненавидя, ложь есть; не любяй брата своего, егоже видить, Бога, егоже не видить, како можеть любити? сию запов<small>Ђ</small>дь имать отъ него, да любяй Бога, любить і брата своего. У любви бо все свершаеться: любви бо ради гр<small>Ђ</small>си расыпаються, любви бо ради и Господъ сниде на землю и распятся за ны гр<small>Ђ</small>шныя, и вземь гр<small>Ђ</small>хи наша, пригвозд<small>Ђ</small> на крест<small>Ђ</small>, давъ намъ крестъ свой на помочь и на прогнанье б<small>Ђ</small>сомъ; любви ради и мученици прольяша кровь свою; люби еже ради и сий князь пролья кровь свою за брата своего, свершая запов<small>Ђ</small>дь Господню.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6584 [1076]. Ходив Володимир, син Всеволода, і Олег Святославич допомагати ляхам проти чехів.</p>\r\n<p>Цього ж літа помер Святослав, син Ярослава, 27 грудня від розрізання пухлини, і похоронили його у [церкві святого] Спаса [в Чернігові], а після нього сів на стіл Усеволод місяця січня в перший день.</p>\r\n<p>Цього ж літа народився у Володимира син Мстислав, внук Всеволода.</p>\r\n<p>В літо 6585 [1077]. Пішов Ізяслав із ляхами, Всеволод же вийшов проти нього. За Всеволода сів Борис [син Вячеслава] у Чернігові місяця травня в четвертий день, і прокняжив вісім днів, і втік у Тмуторокань до Романа. Всеволод же вийшов назустріч братові Ізяславу на Волинь і уклав мир. І прийшов Ізяслав, і сів у Києві місяця липня в п\'ятнадцятий день. Олег, син Святослава, був у Всеволода в Чернігові.</p>\r\n<p>В літо 6586 [1078]. Утік Олег, син Святослава, від Всеволода у Тмуторокань 10 квітня.</p>\r\n<p>Цього ж літа був убитий Гліб, Святославів син, у Заволоччі. Був же Гліб милостивий до вбогих і страннолюбець, дбав про церкви, теплий вірою був, поштивий, лицем гожий. Тіло його було поховано в Чернігові за церквою Спаса місяця липня в двадцять третій день. Коли замість нього у Новгороді сидів Святополк, син Ізяславів, а Ярополк сидів у Смоленську, привели Олег і Борис поганих на Руську землю і пішли проти Всеволода [разом] з половцями. Всеволод виступив проти них на Сожицю, і перемогла половців Русь. І багато вбито було тут: убитий був тут Іван Жирославич, і Туки, Чудинів брат, Порій і багато інших місяця серпня у 25 день. Олег же і Борис прийшли у Чернігів, думаючи, що перемогли, а землі Руській багато зла причинили, пролили кров християнську, за яку Бог спитає у них, і відповідь дадуть вони за погублені душі християнські.</p>\r\n<p>Всеволод же прийшов до брата свого Ізяслава в Київ. Вони поцілувалися і сіли. Всеволод же розповів йому про все, що трапилося. І рече йому Ізяслав: &laquo;Брате! Не тужи. Видиш бо, скільки мені скоїлося зла? Найперше, чи не вигнали були мене і чи не розграбували майно моє? І за яку провину мою вигнали ви мене, брата свого? І чи не блукав я по чужих землях, добра позбавлений, не вчинивши ніякого зла? І нині, брате, не будемо тужити; якщо буде нам причастя в Руській землі, то обом, якщо позбавлені будемо, то обидва. Я покладу голову свою за тебе&raquo;. І, так кажучи, утішив Всеволода, і повелів збирати воїв від малого до великого. І пішов Ізяслав із сином своїм Ярополком і Всеволод із сином своїм Володимиром [у похід]. І прийшли до Чернігова, а чернігівці закрилися в городі. Олег же і Борис [не] були там у Чернігові. Чернігівці ж воріт не відчинили, і приступили до города. Володимир же пішов на приступ східних воріт від Строжені, відбивши ворота, захопив їх і взяв спільний город і спалив вогнем, люди повтікали у внутрішній город. Ізяслав же і Всеволод почули, що Олег і Борис ідуть проти них, відступили і пішли від города проти Олега. І рече Олег Борисові: &laquo;Не підемо проти них, не зможемо вистояти супроти чотирьох князів, а краще пошлемо до стриїв своїх прохати миру&raquo;. І рече йому Борис: &laquo;Ти бачиш мене готовим, я проти них усіх стану&raquo;. І надто похвалився, не відаючи, що Бог гордим противиться, а смиренним благодать дає, тож хай не похваляється сильний силою своєю. І пішли вони супроти, і зійшлися у полі на Нежатиній ниві, і зіткнулися обидва війська. І була січа люта. Першого убили Бориса, сина Вячеславового, який вельми похвалявся. Коли Ізяслав стояв, спішившись, несподівано прискочив один і вдарив списом з-за плеча. І так був убитий Ізяслав, син Ярославів. Січа продовжувалася, і побіг Олег з малою дружиною, і ледве втік, і біг до Тмуторокані.</p>\r\n<p>Убитий був князь Ізяслав місяця жовтня у третій день. Тіло його взяли в лодію і привезли й поставили напроти Городця. І вийшов назустріч йому весь город Київ, і возложили на сани тіло його, і повезли із піснеспівами попів і чорноризців, і понесли його в город. І не можна було почути співів за плачем великим і криками. Оплакував його весь город Київ. Ярополк же йшов за ним, плачучи з дружиною своєю: &laquo;Отче мій! Отче! Скільки прожив ти без печалі на цьому світі! Багато напастей витерпів від людей і від братів своїх! І ось загинув не від брата, а за брата свого положив голову свою&raquo;. І, принісши, поклали тіло його в церкві святої Богородиці, й положили його у кам\'яну раку і мармурову. Був же Ізяслав муж на вигляд красний, вродливий, тілом великий, норовом незлобливий, кривду ненавидів, правду любив, хитрощів не було в нього, ні лестощів, був розумом простий, не воздавав злом за зло. А скільки [зла] зробили йому кияни! Самого вигнали, а дім його розграбували,- не воздав злом за зло, навіть коли хто скаже: &laquo;Киян порубав, котрі висадили Всеслава із порубу&raquo;. То він того не робив, ні син його. Знову ж, свої брати його прогнали, і ходив він по чужій землі, блукаючи. І коли знову сидів на своєму столі, а Всеволод після поразки прийшов до нього, не сказав йому: &laquo;Скільки від вас натерпівся?&raquo; А втішив і сказав йому: &laquo;Оскільки ж ти, брате мій, виявив до мене любов, посадив мене на стіл мій, назвав мене старшим за себе, то я не спом\'яну тобі злоби давньої. Ти мені брат, а я тобі, і я положу голову свою за тебе&raquo;, що і сталося. Адже не сказав йому: &laquo;Скільки зла причинив мені, і се нині тобі приключилося&raquo;. Не сказав: &laquo;Це мене не стосується&raquo;. Але на себе взяв журбу братню, виявив любов велику, йдучи за словами апостола: &laquo;Втішайте печальних&raquo;. Воістину, якщо і зробив на цьому світі якесь грішне діло, відпуститься йому, тому що поклав голову свою за брата свого, не ради більшої частки чи багатства більшого, але за братню кривду. Про таких-от і Господь рече: &laquo;Хто покладе душу свою за друзів своїх, [той наречеться великим у царстві небеснім]&raquo;. Соломон же рече: &laquo;Браття, у бідах пособляйте, бо любов над усе&raquo;. Так же й Іоанн говорить: &laquo;Бог - [це любов], пробуваючий в любові, пробував у Бозі, а Бог у ньому пробував&raquo;. Так вершиться любов, як надбання на день судний. Хай який він - і ми такі будемо в світі цьому&raquo;. Любов страху не має. Справжня любов проганяє страх геть, бо страх - то муки. &laquo;Хто відчуває страх, той недосконалий в любові&raquo;. &laquo;Якщо хто рече: &laquo;Я Бога люблю&raquo;, та ненавидить брата свого, той неправдомовець. Не любить брата свого, якого бачить, як може любити Бога, якого не бачить?&raquo; &laquo;Цю заповідь ми одержали від нього, щоб той, хто любить Бога, любив і брата свого&raquo;. Адже все в любові відбувається. Ради любові і гріхи розсипаються, ради любові-бо й Господь зійшов на землю і дав розіп\'яти себе за нас, грішних, і взяв гріхи наші, прибив себе на хресті, дав нам хрест свій у поміч, щоб проганяти бісів. Любові ради і мученики пролили кров свою. Любові ради і цей князь пролив кров свою за брата свого, здійснюючи заповідь Господню.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&nbsp;</p>\r\n<p><em>Начало княженья Всеволожа в Киеві.</em></p>\r\n<p>&nbsp;</p>\r\n<p>Всеволодъ же с<small>Ђ</small>де Киев<small>Ђ</small> на стол<small>Ђ</small> отца своего и брата своего, переемь всю власть Рускую; и посади сына своего Володимера в Чернигов<small>Ђ</small>, а Ярополка Володимер<small>Ђ</small>, придавъ ему Туровъ.</p>\r\n<p>В л<small>Ђ</small>то 6587. Приде Романь с Половц<small>Ђ</small> к Воиню, Всеволодъ же ставъ у Переяславля и створи миръ с Половц<small>Ђ</small>; и възвратися Романъ въспять, і бывшу ему, убиша и Половц<small>Ђ</small> Романъ, м<small>Ђ</small>сяца августа 1 день; и суть кости его и до сего л<small>Ђ</small>та тамо лежаче, сына Святославля, і внука Ярославля; а Олга емше Козар<small>Ђ</small> поточиша [за] море Царюгороду. Всеволодъ же посади посадника Ратибора Тмутороканю.</p>\r\n<p>В л<small>Ђ</small>то 6588. Заратишася Торці Переяславлестии на Русь, Всезолодъ же посла на н<small>Ђ</small> сына своего Володимера, Володимеръ же шедъ побивъ Тороки.</p>\r\n<p>В л<small>Ђ</small>то 6589. Б<small>Ђ</small>жа Игоревичь Давыдъ с Володаремъ Ростиславичемъ, м<small>Ђ</small>сяца мая въ 18 день; и придоста Тмутороканю, і яста Ратибора, и с<small>Ђ</small>доста Тмутороканю.</p>\r\n<p>В л<small>Ђ</small>то 6590. Ос<small>Ђ</small>нь умре Полов<small>Ђ</small>цкий князь.</p>\r\n<p>В л<small>Ђ</small>то 6591. Приде Олегъ изъ Грекъ Тмутороканю, и я Давыда и Володаря Ростиславичь, и с<small>Ђ</small>де Тмуторокани; и ис<small>Ђ</small>че Козары, иже б<small>Ђ</small>ша св<small>Ђ</small>тници на убьенье брата его и на самого, а Давыда и Володаря пусти.</p>\r\n<p>В л<small>Ђ</small>то 6592. Приходи Ярополкъ ко Всеволоду на Великъ день.</p>\r\n<p>В се же время вб<small>Ђ</small>госта Ростиславича два отъ Ярополка; [и пришедше прогнаста Ярополка] и посла Всеволодъ сына своего Володимера, и выгна Ростиславича, и посади Ярополка Володимер<small>Ђ</small>.</p>\r\n<p>В се же л<small>Ђ</small>то Давыдъ зая Гр<small>Ђ</small>кы во Олешьи, и зая в нихъ все им<small>Ђ</small>нье; Всеволодъ же пославъ приведе и, и вдасть ему Дорогобужь.</p>\r\n<p>В л<small>Ђ</small>то 6593. Ярополку хотящю ити на Всеволода, послушавшю ему злыхъ св<small>Ђ</small>тникъ; се ув<small>Ђ</small>давъ Всеволодъ, посла противу ему сына своего Володимера, а Ярополкъ же оставивъ матерь свою и дружину свою в Лучьск<small>Ђ</small>, а самъ б<small>Ђ</small>жа в Ляхи. Володимеру же прішедшу к Лучску, дашася Лучан<small>Ђ</small>; Володимеру же посадившю Давыда въ Володимер<small>Ђ</small>, у Ярополка м<small>Ђ</small>сто, а матерь Ярополчю, и жену его, и дружину его приведе Киеву, им<small>Ђ</small>нье его вземь.</p>\r\n<p>В л<small>Ђ</small>то 6594. Всеволодъ заложи церковь святаго Андр<small>Ђ</small>я, при Иван<small>Ђ</small> преподобномь митрополит<small>Ђ</small>; створи у церкви тоя манастырь, в немже пострижеся дщи его д<small>Ђ</small>вою, именемь Янька, [она же Янка] совокупивши черноризици многи, пребываше с ними по манастырьскому чину.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&nbsp;</p>\r\n<p><em>Початок княжіння Всеволода в Києві.</em></p>\r\n<p>&nbsp;</p>\r\n<p>Всеволод же сидів у Києві на столі батька свого і брата свого, перейнявши всю владу руську. І посадив сина свого Всеволода в Чернігові, а Ярополка - у Володимирі, придав йому і Туров.</p>\r\n<p>В літо 6587 [1079]. Прийшов Роман із половцями до Воїия, Всеволод же став біля Переяслава і уклав мир з половцями. 1 повернувся Роман назад до половців, і вбили його половці у перший день місяця серпня. І до наших днів кості його, сина Святославового і внука Ярославового, лежать там. А Олега схопили козари, полонили і відправили за море до Цареграда. Всеволод же посадив у Тмуторокані посадником Ратибора.</p>\r\n<p>В літо 6588 [1080]. Послали рать на Русь торки переяславські. Всеволод же послав проти них сина свого Володимира. Володимир же пішов і побив торків.</p>\r\n<p>В літо 6589 [1081]. Втекли Давид Ігоревич з Володарем Ростиславичем місяця травня у вісімнадцятий день, і прийшли у Тмуторокань, і захопили Ратибора, і сіли в Тмуторокані.</p>\r\n<p>В літо 6590 [1082]. Восени помер половецький князь.</p>\r\n<p>В літо 6591 [1083]. Повернувся Олег із Греків до Тмуторокані, і захопив Давида і Володаря Ростиславича, і сів у Тмуторокані, і перебив козар, що були причетні до вбивства брата його і на нього [замишляли], а Давида і Володаря відпустив.</p>\r\n<p>В літо 6592 [1084]. Приходив Ярополк до Всеволода на Великдень.</p>\r\n<p>У цей же час втекли обидва Ростиславичі від Ярополка, і, повернувшись, прогнали Ярополка. І послав Всеволод сина свого Володимира, і [той] прогнав Ростиславичів і посадив у Володимирі Ярополка.</p>\r\n<p>У це ж літо Давид захопив у Олеш\'ї греків і забрав у них все майно, Всеволод же послав привести його і віддав йому Дорогобуж.</p>\r\n<p>В літо 6593 [1085]. Ярополк хотів іти на Всеволода, піддавшись намовлянням злих радників. Довідався про це Всеволод і послав проти нього сина свого Володимира, Ярополк же залишив матір і дружину свою в Луцьку, а сам утік у Ляхи. Коли ж Володимир прийшов до Луцька, лучани здалися. Володимир же посадив у Володимирі Давида замість Ярополка, а матір Ярополкову, і жону його, і дружину його привів у Київ і майно його забрав.</p>\r\n<p>В літо 6594 [1086]. Всеволод заклав церкву святого Андрія за митрополита Івана Преподобного і заснував біля тої церкви монастир. У ньому постриглася дівою дочка його на ймення Янька, [она же Янка]. Оця Янка зібрала багато чорноризиць і жила з ними згідно монастирського чину.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6595. Приде Ярополкъ из Ляховъ, и створи миръ с Володимеромъ, и иде Володимеръ опять Черн<small>Ђ</small>гову; а Ярополкъ с<small>Ђ</small>де Володимер<small>Ђ</small>. I перес<small>Ђ</small>дивъ мало дн<small>Ђ</small>й, иде Звенигороду; и не дошедшу ему города, прободенъ бысть отъ проклятаго Нерядьца, отъ дьяволя наученья и отъ злыхъ челов<small>Ђ</small>къ. Князю же Ярополку лежащу на санкахъ, а онъ с коня саблею прободе я, м<small>Ђ</small>сяца ноября въ 22; тогда въздвигнувся Ярополкъ, выторгну исъ себе саблю, и рече великимъ гласомъ: &laquo;охъ, то тъ мя вороже погуби&raquo;! И б<small>Ђ</small>жа Нерядець проклятый до Перемышля к Рюрикови, а Ярополка взяша отроци на конь передъ ся, Радко, і Воикина и инии отроци, несоша к Володимерю, а оттуду Киеву.</p>\r\n<p>И изниде противу ему благов<small>Ђ</small>рный князь Всеволодъ со сынома своима, Володимеремъ и Ростиславомъ, и вси бояр<small>Ђ</small>, і блаженый митрополитъ Иванъ, и чернорисци, прозвутер<small>Ђ</small>, і вси Кияне великъ плачь створиша над нимъ, со псалъмы и п<small>Ђ</small>снми проводнша и до манастыря святаго Дмитрия, съпрятавше т<small>Ђ</small>ло его, съ честью положиша і в рац<small>Ђ</small> у церкви святаго апостола Петра, юже б<small>Ђ</small> самъ началъ здати, м<small>Ђ</small>сяця декабря въ 5 день. Многы б<small>Ђ</small>ды приемь, без вины изгонимъ отъ братья своея, обидимъ, и разграбленъ, наконець и смерть горку прия: но в<small>Ђ</small>чн<small>Ђ</small>й жизни и покою сподобися. Такъ бо бяше блаженый князь Ярополкъ кротокъ, смиренъ, братолюбивъ и нищелюбець, десятину дая отъ всихъ скотъ своихъ свят<small>Ђ</small>й Богородиця, и отъ жита на вся л<small>Ђ</small>та, и моляше Бога всегда, глаголя: &laquo;Господи Боже мой, Ісусе Христе приими молитву мою, и дай же ми смерть таку, якоже вдалъ еси брату моему Борису и Гл<small>Ђ</small>бови, отъ чюжюю руку, да омыю гр<small>Ђ</small>хи вся своею кровью, избуду суетнаго св<small>Ђ</small>та и сити вражии&raquo; егоже прошенья не лиши его благий Богъ, усприя благая она, ихже ни око не види, ни вхо слыша, ни на сердце челов<small>Ђ</small>ку не взиде, яже уготова Богъ любящимъ его.</p>\r\n<p>В се же л<small>Ђ</small>то ходи Всеволодъ къ Перемышлю.</p>\r\n<p>В лъто 6596. Священа бысть церкви святаго Михаила манастыря Всеволожа, митрополитомь Иоанномь и епископомь Лукою, Исаемь, игуменьство тогда держащу того манастыря Лазореви.</p>\r\n<p>Томъ же л<small>Ђ</small>т<small>Ђ</small> иде Святополкъ из Новагорода Турову на княженье.</p>\r\n<p>У се же л<small>Ђ</small>т<small>Ђ</small> умре Никонъ, Печерьский игуменъ.</p>\r\n<p>В се же л<small>Ђ</small>то взяша Болгаре Муромъ.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6595 [1087]. Вернувся Ярополк із Ляхів і уклав мир із Володимиром. І пішов Володимир знову в Чернігів. Ярополк же сів у Володимирі. І, перечекавши якийсь час, пішов у Звенигород. І, не дійшовши до города, був проколотий проклятим Нерядцем за намовою диявола і злих людей. Князь Ярополк лежав на санках, а Нерядець з коня проткнув його шаблею 22 листопада. Тоді піднявся Ярополк, висмикнув із себе шаблю і голосно закричав: &laquo;Ох, то ти мене, вороже, згубив!&raquo; І утік Нерядець триклятий у Перемишль до Рюрика, а Ярополка взяли отроки на коня поперед себе. Радко, Войкиня та інші отроки понесли [його] до Володимира, а звідти до Києва.</p>\r\n<p>І вийшов назустріч йому благовірний князь Всеволод із синами своїми, Володимиром і Ростиславом, і всі бояри, і блаженний митрополит Іван, і чорноризці, просвітери. І всі кияни великий плач здійняли над ним, із псалмами і піснеспівами провели його до монастиря святого Дмитрія і поклали його з честю в раку у церкві святого апостола Петра, яку він сам почав зводити місяця грудня у п\'ятий день. Великі лиха пережив, безвинного виганяли брати його, обижали і грабували, насамкінець і смерть гірку прийняв, але вічне життя і спокій заслужив. Такий був блаженний князь Ярополк -лагідний, покірний, братолюбивий і до бідних прихильний, десятину від всіх добр своїх віддавав святій Богородиці і від жита на всі літа і завжди молив Бога, промовляючи: &laquo;Господи, Боже мій, Ісусе Христе! Прийми молитву мою і дай же мені смерть таку, яку ти дав братам моїм, Борису і Глібові: від чужої руки, хай омию я гріхи всі своєю кров\'ю, позбудуся суєтного світу цього і сіті ворожої&raquo;. Його прохання не лишив милосердний Бог: він такі отримав блага, яких ні око не бачить, ні вухо не чує, ні серце людське не передчуває - їх приготував Бог для тих, хто любить його.</p>\r\n<p>У це ж літо ходив Всеволод у Перемишль.</p>\r\n<p>В літо 6596 [1088]. Була освячена церква святого Михайла у Всеволодовому монастирі митрополитом Іоанном і єпископами Лукою, Ісаєм та ігуменом того монастиря Лазарем.</p>\r\n<p>Того ж літа пішов із Новгорода княжити в Турові Святополк.</p>\r\n<p>У це ж літо помер Никін, ігумен Печерський.</p>\r\n<p>В це ж літо болгари взяли Муром.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6597. Священа бысть церкви Печерьская святыя Богородица манастыря Федосьева, Иваномъ митрополитомъ, и Лукою Б<small>Ђ</small>логородскимъ епископомь, и епискупомъ Ростовьскимъ Исаиемь, и Иваномъ Черниговьскымь епискупомъ, и Антоньемь Гурьговьскимь игуменомь, при благов<small>Ђ</small>рномь князи Всеволод<small>Ђ</small> державному Руския земля, и чадома его, Володимера и Ростислава, воеводьство держащю Киевьской тясяши Яневи, игуменьство держащу Ивану.</p>\r\n<p>В се же л<small>Ђ</small>то преставися Іоанъ митрополитъ. Бысть же Іоанъ, си мужь хитръ книгамъ и ученью, милостивъ убогимъ и вдовицамъ, ласкавъ же всякому, к богату и къ убогу, смиренъ же умомъ и кротокъ, и молчаливъ, р<small>Ђ</small>чистъ же, книгами святыми ут<small>Ђ</small>шая печальныя, и сяковаго не бысть така преже в Руси, ни по н<small>Ђ</small>мь не будеть такий. В се же л<small>Ђ</small>то иде Янъка въ Греки, дщ<small>Ђ</small> Всеволожа, нар<small>Ђ</small>ченая пр<small>Ђ</small>же.</p>\r\n<p>В л<small>Ђ</small>то 6598. Приведе Янка митрополита и оскопьчину, егоже видивше людье вси рекоша: &laquo;се мертвець пришелъ&raquo;, отъ года бо до года пребывъ, умре; б<small>Ђ</small> же се мужь не книженъ и умомъ простъ и простор<small>Ђ</small>къ.</p>\r\n<p>В се же л<small>Ђ</small>то священа бысть церкви святаго Михаила Переяславлеския, Ефр<small>Ђ</small>момъ тоя церкви митрополитомъ, иже ю есть создалъ велику сущю, и пристрою в н<small>Ђ</small>й велику створи, и украсивь ю всякою красотою, церковьными съсуды. Сий бо Ефр<small>Ђ</small>мъ в си л<small>Ђ</small>та много зданье въздвиже въ церкви святаго Михаила, заложи же церковь на воротехъ святаго Федора и святаго Андр<small>Ђ</small>я, у церкве у воротъ, и городъ каменъ и строенна баня камяна, сего же не бысть в Руси; и въкраси городъ Переяславьскый зданьи церковними и прочими зданьи.</p>\r\n<p>В л<small>Ђ</small>то 6599. Игуменъ и черноризци св<small>Ђ</small>тъ створше, р<small>Ђ</small>ша: &laquo;не добро есть лежати отцю нашему Федосьеви кром<small>Ђ</small> манастыря церкви своея, понеже тъ есть основалъ церковь и черноризци совокупилъ&raquo;, и св<small>Ђ</small>тъ створше повел<small>Ђ</small>ша устроити м<small>Ђ</small>сто, идеже положити мощи его. И присп<small>Ђ</small>вшу празднику Усп<small>Ђ</small>нья Богородиц<small>Ђ</small> треми деньми, и повел<small>Ђ</small> игуменъ рушити, гд<small>Ђ</small> лежать мощ<small>Ђ</small> отца нашего Федосья, егоже повел<small>Ђ</small>нью быхъ азъ гр<small>Ђ</small>шный первое самовидець; се же и скажю, не слухомъ бо слышавъ, по самъ о соб<small>Ђ</small> началникъ. Пришедшю бо игумену ко мн<small>Ђ</small> и рекшю мн<small>Ђ</small>: &laquo;поидев<small>Ђ</small> в пещеру к Федосьеви&raquo;, азъ .же пришедъ съ игуменомъ, не в<small>Ђ</small>дущю никому же, разъглядавша, куда копати, и назнаменавша м<small>Ђ</small>сто кд<small>Ђ</small> копати, кром<small>Ђ</small> устья. Рече же ко мн<small>Ђ</small> игуменъ; &laquo;не мози пов<small>Ђ</small>дати никому же отъ братьи, да не ув<small>Ђ</small>сть никтоже; но поими, его же хощеши, да ти поможеть&raquo;. Азъ же пристроихъ семь дни рогалия, имже копати. И въ вторникъ, вечоръ в суморокъ, пояхъ съ собою 2 брата, не в<small>Ђ</small>дущю никому же, приидохъ в пещеру и отп<small>Ђ</small>хъ псалмы, почахъ копати; и вътрудився вдахъ другому брату, и копахомъ до полуночья; и трудихомся, не могуще ся докопати, начахъ тужити, еда како на страну копаемъ. Азъ же вземь рукалью, начахъ рамяно копати, другу моему опочивающю предъ пещерою, я рече ми: &laquo;удариша в било&raquo; и азъ тотъ часъ протяхъ на мощи Федосьевы, і оному глаголющю ко мн<small>Ђ</small> &laquo;удариша в било&raquo;; мн<small>Ђ</small> же рекшю: &laquo;прокопахъ уже&raquo;, Егда же прокопахъ, объдержашетъ мя ужасть, начахъ звати Господи помилуй! Въ тъ же часъ с<small>Ђ</small>дяста два брата в манастыри, егда игуменъ утаився, н<small>Ђ</small> с кимъ принесеть его отай, зряста к пещер<small>Ђ</small>; и егда удариша в било, видиста три столпы аки дугы зарни, и стоявше и приидоша надъ верхъ церкве, идЪже положенъ бысть Федосий. В се же время вид<small>Ђ</small> Стефанъ, іже бысть в него м<small>Ђ</small>сто ігуменъ, в се же время бысть епископъ, видивъ въ своемъ манасътыри чресъ поле зарю велику надъ пещерою; мн<small>Ђ</small>въ яко несуть Федосья, б<small>Ђ</small> бо ему изв<small>Ђ</small>щено переже днемь един<small>Ђ</small>мь, и съжаливси, яко без него приносять и, и вс<small>Ђ</small>дъ на конь вборз<small>Ђ</small> по<small>Ђ</small>ха, поемъ съ собою Климянта, егоже постави игумена по соб<small>Ђ</small>, идяста в соб<small>Ђ</small>, видяста зарю велику; и яко приидоста близъ, вид<small>Ђ</small>ста св<small>Ђ</small>щи многы надъ пещерою, и приидоста к пещер<small>Ђ</small>, и не видиста ничтоже, і приидоста дну в пещеру, намъ с<small>Ђ</small>дящимъ у мощ<small>Ђ</small>й его Егда бо прокопахъ, послахъ ко игумену: &laquo;прииди да и возмсмъ&raquo;, игуменъ же приде съ дв<small>Ђ</small>ма братома; и прокопахъ велми, вл<small>Ђ</small>зохъ, и видихомъ мощ<small>Ђ</small> его лежащ<small>Ђ</small>, но състав<small>Ђ</small> не распалися быша, і власи главнии притяскли бяху; възлозъше на варанитью, вынесоша предъ пещеру. На другий же день совокупишася епископи, Ефримъ Переяславьскый, Стефанъ Володимерьскый, Иванъ Черниговьскый, Муринъ Гургеньскый, ігумени вси отъ всихъ манастыревъ с чернорисци приидоша, і людье благов<small>Ђ</small>рнии, взяша мощи Федосьевы съ св<small>Ђ</small>щами и съ темьяны, и принесоша і положиша и у своей ему церкв<small>Ђ</small>, у притвор<small>Ђ</small> на десн<small>Ђ</small>й стран<small>Ђ</small>, м<small>Ђ</small>сяца августа въ 14, у четвергъ, в часъ 1 дне, индикта 14 л<small>Ђ</small>та; і празноваша св<small>Ђ</small>тло въ тъ день. Се же пов<small>Ђ</small>мь мало н<small>Ђ</small>что, еже ся збысть прор<small>Ђ</small>ченье Федосьево: игуменьство бо Федосьеви держащю у живот<small>Ђ</small> своемь и правящю стадо, порученое ему Богомъ, черноризци, не токмо же си едини, но и мирьскими печалися душами, како быша спаслися, паче же о сынехъ своихъ душевныхъ, ут<small>Ђ</small>шая и наказая приходящая к кему, другойчи в домы ихъ приходя і благословленье имъ подавая. Единою бо ему пришедшю в домъ Яновъ къ Янов<small>Ђ</small> и к жен<small>Ђ</small> его Марьи, Федосий бо б<small>Ђ</small> любя я, занеже живяста по заповнд<small>Ђ</small> Господн<small>Ђ</small> и в любви живяста; единою же ему пришедшю к нима, и учашеть я о милостын<small>Ђ</small> ко убогимъ и о царьств<small>Ђ</small> небесн<small>Ђ</small>мь, еже пріяти праведникомь, а гр<small>Ђ</small>шьнымъ муку, і о смертнемь час<small>Ђ</small>. И се ему глаголющю і о положсньи т<small>Ђ</small>ла у гроб<small>Ђ</small> има, и рече има Яневая: &laquo;кто в<small>Ђ</small>сть, гд<small>Ђ</small> мя положать?&raquo; рече же ей Федосий: &laquo;по истин<small>Ђ</small> идежа азъ лягу, ту и ты положена будеши&raquo;. Се же събысться. Игумену бо преставшюся преже 18 л<small>Ђ</small>тъ, се сбысться: в се бо л<small>Ђ</small>то преставися Яновая, іменемъ Марья, м<small>Ђ</small>сяца августа въ 16 день, и пришедъше чернорисци, п<small>Ђ</small>вше обычная п<small>Ђ</small>сни, и принесше и положиша ю у церкв<small>Ђ</small> свытыя Богородица, противу гробу Федосьеву, на л<small>Ђ</small>вой сторон<small>Ђ</small>, Федосий бо положенъ бысть 14 день, а си въ 16 день. Се сбысться прор<small>Ђ</small>ченье блаженаго отца нашего Федосья, добраго пастуха, іже пасяше словесныя овца нелицем<small>Ђ</small>рно, с кротостыо и с расмотрениемь, блюда ихъ, и бдя за не, и моляся за порученое ему стадо, і за люди хрьстьянския, і за землю Рускую, іже по отшествии его моляся за люди в<small>Ђ</small>рныя і за своя ученики, иже взирающе на раку твою, поминають ученье твое і въздержанье твое, і прославляють Бога. Азъ гр<small>Ђ</small>шный твой рабъ и ученикъ недоум<small>Ђ</small>ю, чимъ похва лити тя, добраго твоего житья і въздержанья. Но се реку мало н<small>Ђ</small>что: радуйся, отче нашь и наставниче Федосий, мирьскыя плища отринувъ, молчанье възлюбивъ, Богу послужилъ еси у мнишьскомъ житьи, всяко соб<small>Ђ</small> принес<small>Ђ</small>нье принеслъ еси божественое, пос<small>Ђ</small>щеньемь преузвысився, плотьскыхъ сластий възненавидивъ, і мирьскую красоту і желанье в<small>Ђ</small>ка сего отринувъ, усл<small>Ђ</small>дуя стопамъ, высокомысленымъ Отцемь ревнуя, молчаньемъ взвышаяся, смиреньемь украшаяся. Радуйся, укр<small>Ђ</small>плеся надежею, і в<small>Ђ</small>чныхъ благъ приемъ, умертвивъ плотьскую похоть, источникъ безаконью и мятежь, преподобне, і б<small>Ђ</small>совскихъ козн<small>Ђ</small>й изб<small>Ђ</small>гъ и отъ ситі его, с праведными, отче, почилъ еси, усприемъ противу трудомъ своимъ изм<small>Ђ</small>здье, Отцемь насл<small>Ђ</small>дникъ бывъ, посл<small>Ђ</small>довавъ ученыо ихъ і праву ихъ, въздержанью ихъ, і правило ихъ правя. Паче же ревноваше великому Федосью житьемь и нравомь и въздержаньемь, ревнуя и посл<small>Ђ</small>дьствуя обычаю его, и пр<small>Ђ</small>ходя отъ д<small>Ђ</small>ла в д<small>Ђ</small>ло уншее, обычныя молбы Богу уздая, і воню, благоуханья прінося кад<small>Ђ</small>ло молитвеное, темьянъ благоуханьный; поб<small>Ђ</small>дивъ мирьскую похоть и миродерьжца князя в<small>Ђ</small>ка сего, супротивника поправъ дьявола і его козни, поб<small>Ђ</small>дьникъ явися, противнымъ его стр<small>Ђ</small>ламъ и гордымъ помысломъ ставъ супротивно, укрипився оружьемь крестьнымъ и в<small>Ђ</small>рою непобедимою, і Божьею помощью. І помолися за мя, честный отче, ізбавлену быти отъ сити неприязненъ, і отъ противнаго врага съблюди мя твоїми молитвам.</p>\r\n<p>В се же л<small>Ђ</small>то бысть знаменье въ солнци: погибе, мало ся его оста, акы м<small>Ђ</small>сяць бысть, в часъ 2 дне, м<small>Ђ</small>сяца мая въ 21.</p>\r\n<p>В се же л<small>Ђ</small>то бысть Всеволоду ловы д<small>Ђ</small>юща зв<small>Ђ</small>риныя за Вышегородомъ, заметавшимъ тенета і людемь кликнувшимъ, спаде привеликъ зм<small>Ђ</small>й с небес<small>Ђ</small>; і ужасошася вси людье.</p>\r\n<p>В се же время земля стукну, мноз<small>Ђ</small> слышаша.</p>\r\n<p>В се же л<small>Ђ</small>то волъхвъ явися у Ростов<small>Ђ</small> і погибе.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6597 [1089]. Освячена була церква Печерська святої Богородиці Федосієвого монастиря Іваном митрополитом, білогородським єпископом Лукою і єпископом ростовським Ісаєм, і чернігівським єпископом Іваном і гургівським ігуменом Антонієм за благовірного князя Всеволода, володаря Руських земель, і дітей його, Володимира і Ростислава, за воєводи Яня, який держав Київську тисячу, та ігумена Івана.</p>\r\n<p>У це ж літо помер Іоанн митрополит. Був Іоанн цей муж умудрений книгами і навчанням, милостивий до убогих і вдів, ласкавий до всякого, до багатого і до вбогого, смиренний розумом, і лагідний, і мовчазний, але ж і красномовний; святими книгами втішав засмучених. І не було досі такого на Русі, і після нього не буде такого. У це ж літо пішла в Греки дочка Всеволода Янка, названа раніше.</p>\r\n<p>В літо 6598 [1090]. Привела Янка митрополита, скопця, люди, хто бачив його, всі говорили: &laquo;От, мертвяк прийшов&raquo;. І, пробувши рік, помер. Був сей муж і не начитаний, і простакуватий, і просторікуватий.</p>\r\n<p>У це ж літо була освячена переяславська церква святого Михайла митрополитом тієї церкви Єфремом, він же її і збудував великою, і прибудови до неї великі поробив, і оздобив її всілякими прикрасами, церковними сосудами. Цей Єфрем у ті літа багато будов воздвиг навколо церкви святого Михайла: заклав же церкву святого Федора на городських воротях, і церкву святого Андрія біля церковних воріт, і город кам\'яний і поставив бані кам\'яні, чого досі не було на Русі. І прикрасив город Переяславський будинками церковними і іншими будинками.</p>\r\n<p>В літо 6599 [1091]. Ігумен [Печерського монастиря] із чорноризцями раду радили і сказали: &laquo;Недобре лежати отцю нашому Федосієві поза монастирем і церквою своєю, тому що він заснував церкву і чорноризців з\'єднав&raquo;. І, порадившись, звеліли приготувати місце, де покласти мощі його. І через три дні, коли надійшло свято Успіння Богородиці, звелів ігумен копати, де лежать мощі отця нашого Федосія, його повелінню я, грішний, перший самовидець. От про це і скажу, бо не слухом чував, але сам собі началь. ник. Бо прийшов до мене ігумен і сказав мені: &laquo;Підемо в печеру до Федосія&raquo;. Я ж прийшов із ігуменом, так, щоб ніхто не знав, роздивилися, куди копати, і назначили місце, де копать, поза виходом. Сказав же мені ігумен: &laquo;Не смій розповідати нікому ж із братії, щоб ніхто не знав, але візьми, кого хочеш, хай тобі допоможе&raquo;. Я ж того дня приготував рогалія, щоб копати. І у вівторок ввечері, як смеркло, взяв із собою двох із братії, щоб ніхто не знав, прийшов у печеру і, відспівавши псалми, почав копати. І, натрудившись, дав другому брату, і копали до півночі. І трудилися і не могли докопатися. І я затужив, думаючи, що копаємо вбік. Я ж узяв рогалію і почав запопадливо копати, аж доки друг мій, що відпочивав перед печерою, не сказав мені: &laquo;Ударили в било&raquo;. І я в той же час досяг мощів Федосія. І тому, що сказав мені: &laquo;Ударили в било&raquo;, я сказав: &laquo;Я вже докопався&raquo;. Коли ж докопався, охопив мене жах. і я почав благати: &laquo;Господи, помилуй!&raquo;</p>\r\n<p>У той саме час, коли ігумен ще не сказав, з ким він буде переносити його тайно, сиділи в монастирі два брати і дивилися в бік печери. І коли ударили в било, вони бачили, як три стовпи, неначе сяючі дуги, пересувалися і стали над церквою, де покладений був Федосій. У той же час Стефан, який був замість Федосія ігуменом, у це ж врем\'я був єпископом, бачив у своєму монастирі за полем зорю велику над печерою. І подумав, що несуть Федосія, бо напередодні його повідомили про це, і пожалів, що без нього переносять його, і сів на коня, і швидко поїхав, взявши з собою Климента, якого потім настановив ігуменом замість себе. І, їдучи, бачили обидва зорю велику. І коли наблизилися, побачили безліч свічок над печерою. А коли прийшли до печери, то не побачили нічого, і ввійшли в глиб печери, де ми сиділи біля мощів його. Бо, коли докопався, послав до ігумена: &laquo;Приходь, візьмемо його&raquo;. Ігумен же прийшов із двома братами, і я розкопав ширше, влізли і побачили, що лежать мощі його, але суглоби не порозпадалися, і волосся на голові злиплося. І, возложивши на варанитю, винесли перед печеру. Наступного дня зібралися єпископи - Єфрим Переяславський, Стефан Володимирський, Іван Чернігівський, Мурин Гургенський, всі ігумени зі всіх монастирів із чорноризцями прийшли, і люди благовірні взяли мощі Федосієві із тем\'янрм і свічками, і принесли і поклали його у рідній йому церкві, у притворі з правого боку, у четвер 14 серпня в годину дня, індикта 14 літа. І празнували той день світло.</p>\r\n<p>Тепер розповім дещо про те, як збулося передбачення Федосієве. Ще за життя Федосія, коли він був ігуменом і порядкував стадом чорноризців, дорученим йому від Бога, не тільки ними одними, але й мирськими опікувався душами, як би їм спастися, а найбільше про синів своїх духовних, утішав і напучував тих, що приходили до нього, а до других сам приходив до них додому і благословення їм давав. Одного разу він прийшов у дім Янів до Яня і його жінки Марії, бо Федосій любив їх за те, що жили згідно заповіді Господній і в любові жили. Одного разу зайшов до них і повчав їх про милостивість до бідних і про царство небесне, яке дістанеться праведникам, а грішникам - муки, і про смертний час. І коли він говорив їм, як мають класти тіло у гріб, сказала йому [жінка] Янева: &laquo;Хто знає, де мене покладуть?&raquo; Їй же Федосій сказав: &laquo;Воістину, де я ляжу, там і ти похована будеш&raquo;. Це і збулося. Ігумен же помер раніше від неї за вісімнадцять років. Це збулося того ж літа, місяця в шістнадцятий день серпня померла [жінка] Яньова на ймення Марія. І прийшли чорноризці, відспівали належні піснеспіви, і принесли і поклали її в церкві святої Богородиці, напроти гробу Федосієвого з лівого боку. Федосія похоронили чотирнадцятого, а її - шістнадцятого. Так збулося пророцтво блаженного отця нашого Федосія, доброго пастуха, що випасав розумних овець нелицемірно, з покірливістю і з увагою дбаючи про них і турбуючись за них, і молився за доручене йому стадо, і за люд християнський, і за землю Руську, за яких ти і на тому світі молишся, за людей праведних, і за учнів своїх, що, дивлячись на раку твою, згадують учення твоє і повстримність твою, і прославляють Бога. Я, грішний твій раб і учень, недомислюю, як похвалити тебе, добре твоє життя і повстримність. Але скажу коротко: &laquo;Радуйся, отче наш і наставнику Федосію! Мирську плещу кинув, мовчання возлюбив, Богу послужив єси у чернечому житті, всілякі принесення божі собі приносив, постуванням превознісся, плотські насолоди возненавидів, і красу земну і бажання світу цього відкинув, йдучи слідами високомисельних отців, змагаючись з ними, мовчанням звеличився, смиренням прикрасився. Радій, кріпися надією і вічні блага приймай, бо, умертвивши плотську похіть,- джерело беззаконня і смути,- ти, преподобний, і бісівських підступів, і сітей уник, ти з праведними, отче, упокоївся і дістав за труди свої нагороду, бо спадкоємцем став отців [церкви], наслідував учення їхнє і право їхнє, повстримність їхню, і правило їхнє справляв&raquo;.</p>\r\n<p>Особливо ж наслідували великого Федосія життям і норовом, і повстримністю дбаючи і наслідуючи звичаю його, і, переходячи з одного діла до іншого, і належні молитви воздаючи Богу, замість пахощів приносячи кадило молитвенне, тем\'ян духмяний, перемігши мирську похіть і світодержця, князя віку цього, супротивника здолавши диявола і підступи його, переможцем з\'явився, ворожим його стрілам і гордим помислам супроти ставши, зміцнився оружжям хресним і вірою непереможною, і Божою поміччю. І помолися за мене, чесний отче, і вирятуй від сіті ворожої, і від ворога супротивного вбережи мене своїми молитвами.</p>\r\n<p>У це ж літо було знамення на сонці: зникло, мало його залишилося, стало як місяць о другій годині дня місяця травня в двадцять перший день.</p>\r\n<p>У це ж літо, коли Всеволод полював на звірів за Вишгородом, і було вже закинуто тенета, і люди загукали, як упав превеликий змій з небес, і вжахнулися всі люди.</p>\r\n<p>У те ж врем\'я земля так стукнула, що багато хто чув.</p>\r\n<p>У це ж літо волхв об\'явився у Ростові і загинув.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6600. Предивно бысть чюдо у Полотьск<small>Ђ</small>: у мечьт<small>Ђ</small> и в нощи бывши тутенъ, стонаше полунощи, яко челов<small>Ђ</small>ци рыщуть б<small>Ђ</small>си по улици; аще кто выл<small>Ђ</small>зяше ис хоромины, хотя вид<small>Ђ</small>ти, і абье уязвенъ бяше невидимо отъ б<small>Ђ</small>совъ, и с того умираху, и не см<small>Ђ</small>яху излазити іс хоромъ; пос<small>Ђ</small>мь же начаша во дне являтися на кон<small>Ђ</small>хъ, і не б<small>Ђ</small> ихъ видити сам<small>Ђ</small>хъ, но кони ихъ видити копыта; и тако уязьвляху люди Полотьскыя і его область, Т<small>Ђ</small>мъ и челов<small>Ђ</small>ци глаголяху: яко навье быоть Полочаны; се же знаменье поча быти отъ Дрьюцька.</p>\r\n<p>В си же времена бысть знаменье у небес<small>Ђ</small>: яко кругъ бысть посред<small>Ђ</small> неба привеликъ.</p>\r\n<p>У се же л<small>Ђ</small>то ведро бяше, яко ізгаряше земьля, і мнози борове ізгоряхуся сам<small>Ђ</small> і болота; многа знаменья бываху по земл<small>Ђ</small>, и рать велика бяше отъ Половець отвсюду, і взяша 3 городы, Пос<small>Ђ</small>ченъ, Прилукъ, і многа села повоеваша.</p>\r\n<p>В се же л<small>Ђ</small>то воеваша Половц<small>Ђ</small> Ляхи, с Васильемь Ростиславичемь.</p>\r\n<p>У се же л<small>Ђ</small>то умре Рюрикъ, сынъ Ростиславль.</p>\r\n<p>У си же времена мнози челов<small>Ђ</small>ци умираху различными недуги, якоже глаголаху продающимъ хрестъ, отъ Филипова дни до мясопущь 7 тысящь; се же бысть за гр<small>Ђ</small>хы наш<small>Ђ</small>, яко умножишася гр<small>Ђ</small>си паши неправди; се же наведе на ны Богъ, веля намъ имити покоянье і въстагнутися отъ гр<small>Ђ</small>ха і зависти, отъ прочихъ злыхъ д<small>Ђ</small>лъ неприязненыхъ.</p>\r\n<p>В л<small>Ђ</small>то 6601, индикта 1 л<small>Ђ</small>то. Преставися князь Всеволодъ, сынъ Ярославль, внукъ Володимеръ, м<small>Ђ</small>сяца априля 13 день, а погребенъ бысть 14 день; нед<small>Ђ</small>ли сущи тогда страстьн<small>Ђ</small>й і дни сущу тогда четвергу великому, в онъже положенъ бысть у гроб<small>Ђ</small> у велиц<small>Ђ</small>й церкви святыя Софья. Сий благов<small>Ђ</small>рный князь Всеволодъ б<small>Ђ</small> измлада любя правду, и набдя убогия, і воздая честь епископомь и прозвутеромъ, излиха же любляше чернорисц<small>Ђ</small>, и подаваше требованье имъ; б<small>Ђ</small> же и самъ уздержася отъ пьяньства и похот<small>Ђ</small>, т<small>Ђ</small>мь любимъ б<small>Ђ</small><em> </em>отцемь своимъ, яко глаголати отцю его к нему: &laquo;сыну мой! благо теб<small>Ђ</small>, яко слышу о тоб<small>Ђ</small> кротость, і радуюся, яко ты покоиши старость мою; аще аще ти Богъ подасть прияти власть стола моего, по братьи своей, с правдою, а с не насильемь, то егда Богъ отведеть тя отъ житья твоего, то ту ляжеши, идеже азъ, у гроба моего, понеже люблю тя паче братья твоея&raquo;. Се же сбысться отца его, еже глаголалъ б<small>Ђ</small>: сему же приемьшю посл<small>Ђ</small> же всея братья столъ отца своего, і по смерти брата своего, с<small>Ђ</small>де Киев<small>Ђ</small> княжа, и быша ему печал<small>Ђ</small> болш<small>Ђ</small>, паче неже с<small>Ђ</small>дящю ему у Переяславл<small>Ђ</small>; с<small>Ђ</small>дшю бо ему Киев<small>Ђ</small>, печаль бысть ему о сыновницихъ своихъ, яко начаша ему стужати, хотяще власті, і овъ сея, овъ же друго<small>Ђ</small>; се же смиривая ихъ, раздаваше волост<small>Ђ</small> имъ; у сихъ же печали въсташа и недузи ему, і присп<small>Ђ</small>ваше к нему старость; і нача любиті смыслъ уныхъ, и св<small>Ђ</small>тъ творяше с ними; си же начаша і заводити и негодовати дружины своея первыя, і людемь не хот<small>Ђ</small>ти княж<small>Ђ</small>е правд<small>Ђ</small>, і начаша тивун<small>Ђ</small> его грабити, людии продаяти, сему нев<small>Ђ</small>дущю у бол<small>Ђ</small>зн<small>Ђ</small>хь своихъ. И разбол<small>Ђ</small>вшюся ему велмі, посла [по] сына своего Володимера Черн<small>Ђ</small>гову. И пришедшу Володимеру, видивъ отца больна суща, плакася велми; и прис<small>Ђ</small>дящю Володимеру і Ростиславу, сыну меншому его, и пришедшу же часу, пр<small>Ђ</small>ставіся тихо і кротко, и приложися к отцемь своимъ, княживъ л<small>Ђ</small>тъ у Киев<small>Ђ</small> 15; а в Переяславл<small>Ђ</small> л<small>Ђ</small>то, а Чернигов<small>Ђ</small> л<small>Ђ</small>то. Володимеръ же плакався с Ростиславомъ, братомъ своімъ, спрятаста т<small>Ђ</small>ло его; собрашася епископи, игумени с чернорисці, и попове, и бояре, и простии людье, вземше т<small>Ђ</small>ло его со обычними п<small>Ђ</small>сми и положиша у святой Софьи, якоже рекохомъ преже. Володимеръ же нача размышляти, река: &laquo;аще язъ сяду на стол<small>Ђ</small> отца своего, то имамъ рать со Святополкомъ узяти, яко то есть столъ отца моего переже былъ&raquo;. И тако размысливъ, посла по Святополка Турову, а самъ иде Черн<small>Ђ</small>гову, а Ростнславь Переяславлю. И минувши Велику дни, и прішедше праздн<small>Ђ</small>й нед<small>Ђ</small>ли, день антипасхы, м<small>Ђ</small>сяца апріля 24.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>У літо 6600 [1092]. Предивне чудо було в Полоцьку: тут вночі з\'явилися привиди, стогнали опівночі, як люди, шниряли біси вулицями. Коли хто із хоромів виходив, щоб побачити, відразу ж був бісами непомітно уражений і від того помирав, і ніхто не насмілювався виходити з хоромів. Після цього почали [біси] вдень з\'являтися на конях, і не було видно їх самих, але видно було копита їхніх коней. І так уражали людей в Полоцьку і його області. Тому і люди говорили: &laquo;Мертві б\'ють половчан&raquo;. Почалося це знамення від Друцька.</p>\r\n<p>У ці времена було знамення в небесах: наче круг був посеред неба величезний.</p>\r\n<p>У це ж літо засуха була, неначе вигоріла земля, і багато лісів самі загорялися, і болота. Багато знаменій було на землі, і нашестя велике було половців звідусюди, і взяли три городи: Посічень, [Переволоку], Прилуки, і багато сіл звоювали.</p>\r\n<p>У це ж літо воювали половці ляхів з Василем Ростиславичем.</p>\r\n<p>У це ж літо помер Рюрик, син Ростиславів.</p>\r\n<p>У ці ж времена багато людей вимерло від різних хвороб; як говорили\'продавці хрестів, від Пилипового дня до м\'ясопусту [продали] сім тисяч [хрестів]. Було ж це за гріхи наші, оскільки умножалися гріхи наші і неправди, це ж наслав на нас Бог, наказуючи нам покаятися і утримуватися від гріха і заздрощів, від -інших злих вчинків неприязних.</p>\r\n<p>В літо 6601 [1093], індикта в перше літо. Помер князь Всеволод, син Ярославів, внук Володимирів, місяця квітня в 13-й день, а похоронили 14-го. Був тоді страсний тиждень, і день був тоді - великий четвер. У цей день поклали його в гріб у великій церкві святої Софії. Цей благовірний князь Всеволод змалечку любив правду, і дбав про убогих, і виявляв повагу до єпископів і пресвітерів, особливо ж любив чорноризців і обдаровував їм усім потрібним. Він і сам стримувався від п\'янства і похоті, за що любив його і батько, який говорив до нього: &laquo;Сину мій! Благо тобі, що чую про твою покірливість, і радію, що ти заспокоюєш старість мою. Якщо Бог дасть тобі прийняти владу столу мого, після братів твоїх, по праву, а не насильством, то коли Бог забере тебе з цього світу, то тут ляжеш, де я, біля гробу мого, тому, що люблю тебе більше братів твоїх&raquo;. 1 збулися слова батька його, що казав був: одержав він стіл батька свого після всіх братів, після смерті брата свого сів княжити у Києві, багато горя зазнав, більше, ніж коли княжив у Переяславі. Коли він княжив у Києві, багато гризоти завдавали племінники його, які стали досаджати йому, домагаючись волості, один цієї, а той другої. І він, щоб примирити їх, роздавав волості їм. Серед цих гризот постали і недуги його, і приспіла до нього й старість. І почав він любити розум юних і радитися з ними, от і почали вони викликати в нього незадоволення своєю старою дружиною, і люди не могли добитися суду княжого, і почали тівуни грабувати його, людей продавати, а він, через хвороби свої, не знав цього. Коли ж сильно розхворівся, послав він за сином своїм Володимиром у Чернігів. І коли прийшов Володимир і побачив \'батька хворого, плакав дуже. І в присутності Володимира і Ростислава, сина свого молодшого, коли прийшов час, відійшов тихо і покірно і приєднався до предків своїх, живши в Києві літ п\'ятнадцять, і в Переяславі літо, і в Чернігові літо. Володимир же, оплакавши його з Ростиславом, братом своїм, прибрали тіло його. Зібралися єпископи, ігумени з чорноризцями, і попи, і бояри, і простії люди і, взявши тіло його, з належними піснеспівами поклали у святій Софії, як уже сказали раніше.</p>\r\n<p>Володимир же почав розмишлять, кажучи: &laquo;Коли я сяду на стіл батька свого, то доведеться воювати із Святополком, оскільки стіл [київський] раніше належав його батькові&raquo;. І, так розмисливши, послав до Турова за Святополком, а сам пішов у Чернігів, а Ростислав у Переяслав. І коли минув Великдень і святковий тиждень, в день антипасхи місяця квітня 24.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><strong><em>Начала Святополча Кіев<small>Ђ</small> княженья.</em></strong></p>\r\n<p>&nbsp;</p>\r\n<p><em>Начало княженья Святополча у Киев<small>Ђ</small>.</em></p>\r\n<p>&nbsp;</p>\r\n<p>Приде Святополкъ Киеву; изидоша противу ему Кияне с поклономъ, і прияша с радостью, і с<small>Ђ</small>де на стол<small>Ђ</small> отца своего и стрыя своего.</p>\r\n<p>В се же время поидоша Половц<small>Ђ</small> на Рускую землю; слышавше, яко умерлъ есть Всеволодъ, послаша послы къ Святополку о мир<small>Ђ</small>. Святополкъ же не здума с болшею дружиною отнею и стрыя своего, но св<small>Ђ</small>тъ створи с пришедшими с нимь, ізоімавъ послы всажа въ погребъ; слышавше же се Половц<small>Ђ</small>, почаша воевати; и прнидоша Половц<small>Ђ</small> мнози, і оступиша Торъчьский градъ. Святополкъ же слышавъ Половц<small>Ђ</small>, посла прося мира, і пустиша по земл<small>Ђ</small> воююще. Святополкъ же нача сбирати во<small>Ђ</small>, хотя на н<small>Ђ</small>. І р<small>Ђ</small>ша ему мужи смыслен<small>Ђ</small>и: &laquo;не кушайся противу имъ, яко мало имаши вои&raquo;. Онъ же рече имъ: &laquo;им<small>Ђ</small>ю отрокъ своихъ 8 сотъ, иже могуть противу имъ стати&raquo;. Начаша же друзии несмыслен<small>Ђ</small>и молвити: &laquo;поіди, княже&raquo;; смысленыи же глаголаху: &laquo;аще бы пристроилъ 8 тысящь, не лихо ти есть, наша земля оскудила есть отъ ратий и продажь; но пошлися къ брату своему Володимеру, дабы ти помоглъ&raquo;. Святополкъ же послуша ихъ, і посла к Володимеру, дабы помоглъ ему; Володимеръ же собра вои свои, и посла по Ростислава, брата своего, до Переяславля, веля ему помогати Святополку. Володимеру же пришедшю Киеву, и совокупися у святаго Михаила, и взяста межи собою распр<small>Ђ</small> и которы; и уладившася, ц<small>Ђ</small>ловаста крестъ межи собою. А Половцемъ воюющимъ по земл<small>Ђ</small>, и р<small>Ђ</small>ша ему муж<small>Ђ</small> смыслен<small>Ђ</small>и: &laquo;почто вы распрю имата межи собою? а погании губять землю Рускую; посл<small>Ђ</small>д<small>Ђ</small> ся смирита, а нын<small>Ђ</small> поидита противу имъ, любо с миромъ, любо ратью&raquo;. Володимеръ же хотяше мира, Святополкъ же хотя ратью; и поиде Святополкъ, и Володимеръ, и Ростиславь къ Тр<small>Ђ</small>полю. И приидоша ко Стугн<small>Ђ</small>. Святополкъ же, и Володимеръ, и Ростиславъ съзваша дружину свою на св<small>Ђ</small>тъ хотяче поступити чересъ р<small>Ђ</small>ку; и начаша думати. И глаголаше Володимеръ; &laquo;яко сд<small>Ђ</small> стояще чересъ р<small>Ђ</small>ку, угроз<small>Ђ</small> сей, створимъ миръ с ними&raquo;; и присташа св<small>Ђ</small>ту сему смыслен<small>Ђ</small>и мужи, Янь и прочіи. Кияне же не восхот<small>Ђ</small>ша св<small>Ђ</small>та сего, но рекоша: &laquo;хощемъ ся бити; поступимъ на ону сторону р<small>Ђ</small>к<small>Ђ</small>&raquo;; излюбиша св<small>Ђ</small>т[ъ] ось, и преидоша Стугну р<small>Ђ</small>ку; б<small>Ђ</small> бо тогда наводнилася велми. Святополкъ же, и Володимеръ, и Ростиславъ исполчившеся, поидоша; на десной сторон<small>Ђ</small> Святополкъ, а на шюе<small>Ђ</small> Володимеръ, а посеред<small>Ђ</small> Ростиславъ; і минувше Треполь, проидоша валъ. И се Половц<small>Ђ</small> идяху противу, и стр<small>Ђ</small>лци противу пред ними; нашимъ же ставшимъ межи валома, і поставиша стяги своя, и изидоша стрилци из валу; и Половци пришедше к валови, поставиша стяги своя, налягоша первое на Святополка, и възломиша полкъ его, Святополкъ же стояше кр<small>Ђ</small>пко, и поб<small>Ђ</small>гоша людье, не терпяще ратныхъ противленья; і посл<small>Ђ</small>же поб<small>Ђ</small>же Святополкъ. И налегоша на Володимера, і бысть брань люта; и поб<small>Ђ</small>же и Володимеръ с Ростиславомъ и вои его. И приб<small>Ђ</small>гоша к р<small>Ђ</small>ц<small>Ђ</small> Стугн<small>Ђ</small>, и въ бродъ Володимеръ с Ростиславомъ, и нача утапати Ростиславъ передъ очима Володимеровыма; и хот<small>Ђ</small> подхватити брата своего, и мало не втону самъ; и тако утопе Ростиславъ, сынъ Всеволожь. Володимеръ же пребредъ р<small>Ђ</small>ку с маломь дружины, мнози же падоша отъ полка его, и бояр<small>Ђ</small> его туто падоша; і пришедъ на ону сторону Дн<small>Ђ</small>пра, плакася по брат<small>Ђ</small> своемъ и по дружини своей; иде Черн<small>Ђ</small>гову печаленъ велми. Святополкъ же уб<small>Ђ</small>же въ Треполь и затворися ту, і б<small>Ђ</small> до вечора, и в ту нощь приде Кыеву. Половци же видивше се, пустиша по земл<small>Ђ</small> воююче, а друзии узъвратишася к Торочьскому. Си же ся злоба соключи въ день святаго Възнесенья Господа нашего Ісуса Христа, м<small>Ђ</small>сяца маия 26. Ростислава же искавше обр<small>Ђ</small>тоша и в р<small>Ђ</small>ц<small>Ђ</small>; и вземше и принесоша и Кыеву, і плакася по немь мати его, і всі людье плакаша по немь повелику, уности его ради; и собрашася епископы и попов<small>Ђ</small> і чернорисц<small>Ђ</small>, и п<small>Ђ</small>сни обычныя п<small>Ђ</small>вше, и положиша [и] въ церкви свято<small>Ђ</small> Софь<small>Ђ</small>, у отца своего.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><strong><em>Початок Святополкового в Києві князювання</em></strong></p>\r\n<p>&nbsp;</p>\r\n<p><em>Початок княження Святополка в Києві</em></p>\r\n<p>&nbsp;</p>\r\n<p>Прийшов Святополк у Київ. Вийшли йому назустріч кияни з поклоном і прийняли з радістю. І сів на столі батька свого і стрия свого.</p>\r\n<p>У це ж врем\'я пішли половці на Руську землю, почувши, що помер Всеволод, послали вони послів до Святополка, пропонуючи заключити мир. Святополк же, не порадившися з більшою дружиною батька і стрия свого, але порадився із тими, хто прийшов із ним, схопив послів і посадив у погріб. Почувши про це, половці почали воювати. І прийшла половців безліч, і обступили вони город Торчеськ. Святополк же, як почув про половців, послав просити миру, а вони пустились [по Руській] землі воювати. Святополк же почав збирати воїв, щоб іти на половців. І сказали йому мужі розсудливі: &laquo;Не спокушайся іти проти них, як мало воїв маєш&raquo;. Він же рече їм: &laquo;Маю отроків у себе вісімсот, які можуть супроти них стати&raquo;. А другі нерозсудливі почали говорити: &laquo;Іди, княже&raquo;. Розумні ж говорили: &laquo;\'Коли б ти зібрав вісім тисяч, не було б біди. Наша земля обідніла від воєн і поборів. Але звернися до брата свого Володимира, аби допоміг тобі&raquo;. Святополк же послухав їх і послав до Володимира, щоб той допоміг йому. Володимир же зібрав воїв своїх і послав по Ростислава, брата свого, в Переяслав, звелівши йому помагати Святополку. Володимир же прийшов у Київ і зустрівся [із Святополком] біля святого Михайла, і затіяли між собою сварки і незгоду, а домовившись, цілували хрест між собою. А половці продовжували воювать [Руську] землю, і сказали йому мужі розумні: &laquo;Чого ви сваритеся між собою? А погані гублять землю Руську. Потім помиритеся, а нині підіть проти них або з миром, або війною&raquo;. Володимир бажав миру. Святополк же хотів воювати. І пішли Святополк, і Володимир, і Ростислав до Трипілля. І прийшли до Стугни. Святополк же, і Володимир, і Ростислав скликали дружину свою на раду, маючи намір перейти через ріку. І почали думать. І сказав Володимир: &laquo;Доки стоїмо тут під прикриттям ріки такою угрозою, заключимо мир з ними&raquo;. І пристали на цю раду розумні мужі, Янь і інші. Кияни ж не прийняли цієї поради, а сказали: &laquo;Хочемо битися, перейдемо на той бік річки&raquo;. І полюбилась рада, і перейшли Стугну ріку, а була вона тоді вельми повновода. Святополк же, і Володимир, і Ростислав, приготувавши дружини до бою, пішли. З правого боку ішов Святополк, з лівого - Володимир, а посередині - Ростислав. І, поминувши Трипілля, пройшли вал. І от половці пішли назустріч із стрільцями попереду. Наші ж стали поміж валами, поставили стяги свої і рушили стрільці із валу. І половці, підійшовши до валу, поставили стяги свої і налягли найперше на Святополка і зламали полк його. Святополк же стояв твердо, але люди його побігли, не витримавши натиску половецьких військ, і останнім побіг Святополк. І налягли на Володимира, і була битва люта. І побіг і Володимир із Ростиславом і вої його. І прибігли до ріки Стугни і [кинулися] вбрід Володимир із Ростиславом, і став тонути Ростислав на очах у Володимира. І хотів Володимир підхопити брата свого, і трохи не втонув і сам. І так утонув Ростислав, син Всеволодів. Володимир же перебрів ріку із рештками дружини, бо більшість полку його полягла, і бояри його тут полягли. І перебрався на цей бік Дніпра, оплакав брата свого і дружину свою, пішов вельми печальний у Чернігів. Святополк же утік у Треполь і закрився там, і був до вечора, і в ту ж ніч прийшов до Києва. Половці ж, коли побачили це, кинулися по [Руській] землі воювати, а другії повернулися в Торчеськ.</p>\r\n<p>Приключилася ця біда в день святого Вознесіння Господа нашого Ісуса Христа, місяця травня 26. Ростислава ж шукали і знайшли його в ріці і, взявши, принесли його до Києва, і плакала за ним мати його, і всі люди плакали дуже за ним через юність його. І зібралися єпископи, і попове, і чорноризці, і належні піснеспіви відспівали, і поклали його в церкві святої Софії біля батька свого.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Половцемь же ос<small>Ђ</small>дяще Торочьский, противящемъ же ся Торокомъ и кр<small>Ђ</small>пко борющимъ ізъ города, убиваху многы отъ противныхъ, Половци же начаша нал<small>Ђ</small>гати, отоймати воду, ізнемогати начаша у город<small>Ђ</small> людье жажою водною [и] гладомъ. I прислаша Торци къ Святополку, глаголюще: &laquo;аще не пришлеши брашна, предатися имамы&raquo; Святополкъ же пославъ, и не б<small>Ђ</small>. лз<small>Ђ</small> украстися в городъ, множества [ради] ратныхъ. И стояше около города нед<small>Ђ</small>лъ 9, и раздилишася на двое: едини сташа у града, рать борющю, а друзии поидоша Кыеву, и пустиша на воронь межи Кыевъ и Вышегородъ. Святополкъ же выиде на Желяню, и поидоша обои противу соб<small>Ђ</small>, и ступишася, и укр<small>Ђ</small>пися брань, поб<small>Ђ</small>гоша наши предъ иноплеменн<small>Ђ</small>кы, и падаху язвен<small>Ђ</small>и, предъ врагы нашими, и мнози погибоша и быша мертви, паче неже во Треполя. Святополкъ же приде Кыеву самъ трет<small>Ђ</small>й, а Половц<small>Ђ</small> узъвратишаяся к Торочкому; і быша же си злая м<small>Ђ</small>сяца июля 23. Наутрея же, въ 24, у святою мученику Бориса и Гл<small>Ђ</small>ба, бысть плачь великъ у город<small>Ђ</small>, гр<small>Ђ</small>хъ ради нашихъ великихъ, за умноженье безаконий нашихъ. Се на ны Богъ пусти поганыя, не милуя ихъ, но иасъ казня, да быхомъ ся востягнули отъ злыхь д<small>Ђ</small>лъ; симь казнить ны нахоженьемь поганыхъ, се бо есть бо батъгъ Божий, да н<small>Ђ</small>коли смірившеся успомянемся отъ злаго пути. Сего ради во праздникы Богъ намъ наводить с<small>Ђ</small>тованье: якоже ся створи в се л<small>Ђ</small>то первое зло на Вознесенье, у Треполя, 2 у на празникъ Бориса и Гл<small>Ђ</small>ба, иже есть праздникъ новой Рускый. Сего ради Пророкъ глаголаше: и пр<small>Ђ</small>ложю праздникы ваша у плачь и п<small>Ђ</small>сни ваша в рыданье Створи бо ся плачь великъ у земл<small>Ђ</small> нашей, и опуст<small>Ђ</small>ша села наша и город<small>Ђ</small> наши, і быхомъ б<small>Ђ</small>гающе и предъ враги нашими. Якоже и Пророкъ глаголаше: падете предъ враги вашими, и поженуть вы ненавидящеи васъ, и поб<small>Ђ</small>гнете никомуже не женющю по васъ: і скрушу руганье гордтліья вашего, и будеть во тщету кр<small>Ђ</small>пость ваша, и биеть вы приходяй м<small>Ђ</small>чь, и будеть земля ваша пуста, двор<small>Ђ</small> ваши пуст<small>Ђ</small> будуть, яко вы злі есте и лукав<small>Ђ</small>, и азъ поиду к вамъ яростью лукавою, глаголеть Господь Богъ святый Израилевъ. Ибо лукавии сынове Измаилове пожигаху села і гумьна, і мьноги церкви запалиша огнемь, да не чюдиться никтоже о с<small>Ђ</small>мь: идеже множество гр<small>Ђ</small>ховъ, ту всякого вид<small>Ђ</small>нья показанье. Сего ради вселеньная продасться, сего ради гн<small>Ђ</small>въ простреся, сего ради земля мучена бысть: ови ведутся полонен<small>Ђ</small>, а друзии пос<small>Ђ</small>каеми бывають, друзии на месть даеми бывають, і горкую приемлюще смерть, друзии трепещуть зряще убиваемыхъ, другии гладомъ умориваеми и водною жажею; едино пр<small>Ђ</small>щенье, едина казнь, многов<small>Ђ</small>щныя имуще раны, и различныя печали и страшныя мукы, ови вязани и пятами пьхаеми, і на мороз<small>Ђ</small> держими і вкаряеми; и се пристранн<small>Ђ</small> и страшн<small>Ђ</small>и, яко на хр<small>Ђ</small>стьяньсц<small>Ђ</small> род<small>Ђ</small> страхъ н колибанье и б<small>Ђ</small>да упространися. Право и достойно, тако да накажемся, і тако соб<small>Ђ</small> в<small>Ђ</small>ру имемъ, кажеми есмы: подобаше бо намъ в руц<small>Ђ</small> преданымъ быти языку страньну и безаконьну и лукавнейшю, паче всея земля. Рц<small>Ђ</small>мь велегласно: праведенъ еси, Господи, и праведн<small>Ђ</small> суди твои. Рц<small>Ђ</small>мь по оному разбойнику: мы достойная яже сд<small>Ђ</small>яхомъ и прияхомъ. Рц<small>Ђ</small>мь съ Иевомъ; якоже Господеви любо, тако и бысть; буді имя Господне благословено у в<small>Ђ</small>кы. Да нахоженіемъ поганыхъ мучими, владыку познаемъ, егоже мы прогн<small>Ђ</small>вахомъ: прославлени бывше [не прославихомъ, почтени бывше] не почтохомъ, освятивьшеся, не разум<small>Ђ</small>хомъ, куплени бывше, не поработахомъ, породившеся, не яко отца постыдихомся. Согр<small>Ђ</small>шихомъ і казнимы есмы, якоже согр<small>Ђ</small>шихомъ, тако и стражемъ: і град<small>Ђ</small> вси опуст<small>Ђ</small>ша, і переид<small>Ђ</small>мъ поля, ід<small>Ђ</small>же пасома быша стада кон<small>Ђ</small>, овц<small>Ђ</small> і волове; се все тще нын<small>Ђ</small> видимъ, нивы порожьше стоять зв<small>Ђ</small>ремъ жилища быша. Но обаче над<small>Ђ</small>емься на милость Божью: казн<small>Ђ</small>ть бо ны добр<small>Ђ</small> благый Владыка, и [не] по безаконью нашему створи намъ і [не] по гр<small>Ђ</small>хомъ нашимъ въздалъ есть намъ; тако подобаеть благому Владыц<small>Ђ</small> казати ны по множеству гр<small>Ђ</small>ховъ. Тако Господь створи намъ: падшая уставить, Адамле преступленье прости, баню нетл<small>Ђ</small>нья дарова, свою кровь за ны излья. Якоже ны від<small>Ђ</small> неправо пр<small>Ђ</small>бывающа, нанесе намъ сущюю рать и скорбь, да хотяще и не хотяще и всяко в будущий в<small>Ђ</small>къ обрящемь милость і лготу отъ мукы, не мьстить бо Господь двойч<small>Ђ</small> о томь. О неизреченьному челов<small>Ђ</small>колюбью! якоже видити ны неволею к соб<small>Ђ</small> обрящающася: тмами любовь яже к намъ! понеже хотяще уклонитися отъ заповид<small>Ђ</small> его, се уже не хотяще терпимъ с нужею, и понеже неволею, се уже волею. Кд<small>Ђ</small> бо б<small>Ђ</small> тогда умиленье в насъ? нын<small>Ђ</small> же вся полна суть слезъ. Кд<small>Ђ</small> б<small>Ђ</small> в насъ уздыханье? нын<small>Ђ</small> же плачь по всимъ улицамъ умножися, ізбьеныхъ ради, иже избиша безаконн<small>Ђ</small>и. Половц<small>Ђ</small> воеваша много и възвратишася к Торъческому, изнемогоша людье въ град<small>Ђ</small> отъ глада, и предашася ратнымъ; Половц<small>Ђ</small> же, приемьше градъ, запалиша огнемь, и люди разділиша и ведоша я у веж<small>Ђ</small> к сердоболямъ своимъ и сродникомъ своимъ. Мучими зимою, и оц<small>Ђ</small>пляемЪ у алъчб<small>Ђ</small> и в жаж<small>Ђ</small>, і в б<small>Ђ</small>д<small>Ђ</small> поблед<small>Ђ</small>вше лицн, и почернивше телесы; незнаемою страною языкомъ іспаленомъ, нази ходяще и бос<small>Ђ</small>, ногы имуще избодены терньемь. Съ слезами отв<small>Ђ</small>щеваху другъ другу, глаголюще: &laquo;азъ б<small>Ђ</small>хъ сего города&raquo;, а другии: &laquo;азъ сего села&raquo;; і тако съвъспрошахуся со слезами, родъ свой пов<small>Ђ</small>дающе, очи възводяще на небеса к Вышнему, в<small>Ђ</small>дущему тайная. Да никто же дерзнеть рещи: яко ненавидіми Богомь есмы! Кого бо Богъ тако любить, якоже насъ узлюбилъ есть? кого бо тако почтилъ есть, якоже насъ прославилъ есть і възнеслъ есть? никого же. Імъже паче ярость свою уздвиже на ны, якоже паче всихъ [почтени бывше, гор<small>Ђ</small>е вс<small>Ђ</small>хъ съд<small>Ђ</small>яхомъ гр<small>Ђ</small>хы, якоже паче вс<small>Ђ</small>хъ] просв<small>Ђ</small>щени бывше, і Владычию волю в<small>Ђ</small>дуще і презр<small>Ђ</small>вше, въ л<small>Ђ</small>поту паче ин<small>Ђ</small>хъ казнимы есмы. Се бо азъ гр<small>Ђ</small>шный многа и часто Бога прогн<small>Ђ</small>вахъ, і часто согр<small>Ђ</small>шая бываю по вся дни.</p>\r\n<p>В се же л<small>Ђ</small>то преставися Ростиславъ, сынъ Мьстиславль, внукъ Изяславль, м<small>Ђ</small>сяца октября въ I; а погребенъ бысть м<small>Ђ</small>сяца ноября въ 16, въ церкви святыя Богородица Десятиньныя.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Коли ж половці обсіли Торчеськ, торці чинили опір і завзято боролися з города, вбиваючи багато ворогів. Половці ж почали налягати, відвели воду, і люди в городі стали знемагати від спраги й голоду. І прислали торці до Святополка сказати: &laquo;Якщо не пришлеш їди, змушені будемо здатися&raquo;. Святополк же послав, але не було змоги прокрастися в город, бо навколо було безліч ратних. І стояли біля города дев\'ять тижнів, і розділилися надвоє: одні стали біля города, з військом б\'ючись, а другі пішли до Києва і вчинили набіг між Києвом і Вишгородом. Святополк же вийшов на Желаню, і пішли обидва війська одне проти другого, і зійшлися, і розгорілася битва. І побігли наші від чужинців, і падали, зранені ворогами нашими, і багато загинуло, і впали мертві, більше, ніж біля Треполя. Святополк же прийшов у Київ сам-третій, а половці повернулися до Торчеська. І сталася ця біда місяця липня 23. На ранок же 24-го, в день святих мучеників Бориса і Гліба, стояв плач великий у городі через гріхи наші великі, через побільшення беззаконня нашого. Це на нас Бог наслав поганих, не їх милуючи, а нас караючи, щоб ми відсахнулися від злих справ, так карає нас нашестям поганих, бо це батіг Божий, щоб ми, опам\'ятавшись, утримувалися від злого шляху. Цього ради в празники Бог нам наводить тужбу, так як перше зло в це літо вчинив на [свято] Вознесіння, біля Трипілля, а друге - на свято Бориса і Гліба, а це нове свято руське. Цього ради пророк сказав: &laquo;Оберну свята ваші у плач, а пісні ваші - в ридання&raquo;. Стався-бо плач великий на нашій землі, і опустіли села наші і городи наші, і тікали ми від ворогів наших [у святкові дні]. Про це і пророк говорить: &laquo;Впадете перед ворогами вашими, і поженуть вас ті, що ненавидять вас, і побіжите, ніким не гнані&raquo;; &laquo;і сокрушу пиху вашої сили, і стане в ніщо міць ваша, і битиме вас захожий меч, і опустіє земля ваша, і опустіють двори ваші, бо ви злі і лукаві, і я прийду до вас із лютістю лукавою&raquo;. Так говорить Господь Бог святий Ізраїлів. Бо лукавії сини Ізмаїлови палили села і токи, і багато церков спалили вогнем, і нехай ніхто не дивується цьому: &laquo;Де безліч гріхів, там і видовища всілякого покарання&raquo;. Через це і вселенна була зраджена, через це і гнів послався, через це і народ на муки відданий: одних у полон ведуть, а других убивають, а інших на кару віддають, і вони гірку смерть приймають, другії - тремтять, бачачи тих, що вбивають, інших <em>же </em>голодом і спрагою морять. Одна загроза, одна кара, що різноманітні має рани і різні печалі, і страшні муки тих, кого зв\'язують і п\'ятами б\'ють, на морозі тримають <em>ї </em>мордують. І це тим дивніше і страшно, що на християнський рід страх, і сумніви, і нещастя поширились. Справедливо і заслужено, хай будемо покарані, і у те віритимемо, коли будемо покарані: випадає нам &laquo;потрапити в руки ворогу чужоземному, найбеззаконнішому і найпідступнішому з-посеред усіх народів&raquo;. Речемо велемовно: &laquo;Праведний ти, Господи, і справедливі судді твої&raquo;. Речемо за прикладом того розбійника: &laquo;Ми заслужене за діла свої отримали&raquo;. Говоримо разом з їовом: &laquo;Як Господу любо, так і трапилося. Хай буде ім\'я Господнє благословенне в віках&raquo;. Через нашестя поганих і завдані ними муки пізнаємо Владику, якого ми прогнівили: ми були прославлені - і не прославили його, пошановані були - і не пошанували його, просвітились - і не зрозуміли, куплені були - і не попрацювали, народилися - і засоромилися, наче він і не батько. Согрішили - і покарані, як согрішили, так і страждаємо: і городи всі опустіли, і перейдемо поля, де паслися стада - коні, вівці і воли,- і нічого цього нині не бачимо, ниви стоять порослі і стали пристанищем для звірів. Але, одначе, сподіваємося на милість Божу: карає-бо нас тяжко добрий Владика. І не за нашим беззаконням зробив нам, а за гріхи наші воздав нам. Так подобає доброму Владиці карати нас за численні гріхи.</p>\r\n<p>Так Господь зробив нам: упалих підняв, Адамів злочин простив, маківку нетлінності подарував, свою кров за нас пролив. Оскільки бачить нас неправедно живущих, наслав на нас цю війну і скорботу, щоб і ті, що хочуть, і не хочуть, і всякий в будучині знайшли милість і полегшу від муки, не помщається-бо Господь подвійно за те саме. О несказанне людинолюбство, оскільки бачив нас, що невільно до нього зверталися: безмежна його любов до нас! Адже хотіли ми ухилитися від заповідей його, а от тепер уже хоча й не хочемо, а терпіти змушені, і хоча й приневолені, терпимо уже з власної волі. Хіба була тоді у нас розчуленість? А нині всі сповнені сліз. Хіба була у нас жалісливість? А нині плач на всіх вулицях стоїть за вбитими, яких згубили беззаконники.</p>\r\n<p>Половці розбійничали довго і повернулися до Торчеська. Обезсиліли люди в городі з голоду і здалися нападникам. Половці ж, захопивши город, запалили вогнем, а людей розділили і повели їх у вежі до сердоболів своїх і родичів своїх. Змучені зимою, сковані, голодні і спраглі, і з поблідлими в нещасті лицями, і почорнілі тілом, у чужій землі, із запаленими язиками, йшли, голі і босі, маючи зранені терням ноги. Із сльозами відповідали одне одному, промовляючи: &laquo;Я був з цього города&raquo;, а другий: &laquo;Я з цього села&raquo;. І так, перемовляючись, із сльозами, розповідали про рід свій, зводили очі на небеса до Всевишнього, знаючого тайне. Хай ніхто не посміє сказати, що Бог ненавидить нас! Бо кого ще так любить Бог, як нас возлюбив? Кого ще так пошанував, як нас: прославив і возніс? Нікого! Через те найбільше гнів свій підніс до нас, що більше за інших пошановані були, а найгірші гріхи содіяли, що більше за інших освічені були і волю Владики знали, а зневажили її красу. І за це більше за інших, покарані. Ось і я, грішний, дуже і часто гніваю Бога, і часто буває, що грішу повсякдень.</p>\r\n<p>У це ж літо помер Ростислав, син Мстиславів, внук Ізяславів, місяця жовтня з перший [день]. А похоронили його місяця листопада в шістнадцятий [день], у Десятинній церкві святої Богородиці.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6602. Створи миръ с Половц<small>Ђ</small> Святополкъ, і поя жену, дщерь Тугортоканю, князя Половецьскаго</p>\r\n<p>Того же л<small>Ђ</small>та Олегъ приде с Половц<small>Ђ</small> іс Тмутороканя, и прииде к Чернигову, Володимерь же затворися в город<small>Ђ</small>, Олегъ же прииде ко граду и пожьже около града, і манастыри пожьже; Володимеръ же створи миръ со Олгомъ, и иде из города на столъ отень до Переяславля, а Олегъ вниде в городъ отца своего. Половьц<small>Ђ</small> же начаша воевати около Чернигова, Ольгов<small>Ђ</small> не возбраняющю, 6<small>Ђ</small> бо самъ повел<small>Ђ</small>лъ имъ воевати. Се уже третье наведе Олегъ поганыя на Рускую землю; его же гр<small>Ђ</small>ха дабы и Богъ простилъ, понеже много хрестьянъ ізъгублено бысть, а другее полонено бысть и расточено по землямъ.</p>\r\n<p>В се же л<small>Ђ</small>то приідоша прузи на землю Рускую, м<small>Ђ</small>сяца августа въ 16, і поядоша всяку траву и многа жита; і не б<small>Ђ</small> сего слышано во днехъ первыхъ в земл<small>Ђ</small> Руской, якоже видиста очи наши за гр<small>Ђ</small>хи наша.</p>\r\n<p>В се же л<small>Ђ</small>то преставися епископъ Володимерьск<small>Ђ</small>й Стефанъ, м<small>Ђ</small>сяца априля въ 27, въ 6 часъ нощи, бывшу преже ігумену Печерьскаго манастыря.</p>\r\n<p>В л<small>Ђ</small>то 6603. Идоша Половц<small>Ђ</small> на Гр<small>Ђ</small>кы съ Девьгеневичемь, і воеваша на Грекы; а царь я Девьгеневича і осл<small>Ђ</small>п<small>Ђ</small>.</p>\r\n<p>В се же л<small>Ђ</small>то приидоша Половц<small>Ђ</small>, Ітларь, Китанъ, к Володимеру на миръ; і приде Итларь у городъ Переяславль, а Китанъ ста межи валома с вои, и вда Володимерь сына своего Святослава Китанови у таль, а Итларъ бысть въ град<small>Ђ</small> с лучшею дружиною.</p>\r\n<p>В се же время пришелъ Славятя іс Киева отъ Святополка к Володимеру, на н<small>Ђ</small>кое орудье; и начаша думати дружина Ратиборова чадь съ княземь Володимеромъ о погублен<small>Ђ</small> Итларевы чади. Володимеру же не хотящю сего створити, глаголющю ему: &laquo;како могу се азъ створити, рот<small>Ђ</small> с ними ходивъ?&raquo; Отв<small>Ђ</small>щавше же дружина, рекоша Володимеру: &laquo;княже, н<small>Ђ</small>сть ти в томъ гр<small>Ђ</small>ха; привелъ ти <small>Ђ</small> Богъ в руц<small>Ђ</small> твои, чему он<small>Ђ</small> к тоб<small>Ђ</small> всегда рот<small>Ђ</small> ходяще, губять землю Рускую и кровь хрестьяньску проливають беспрестани&raquo;. И послуша ихъ Володимерь; в ту нощь посла Володимерь Славяту с н<small>Ђ</small>колкимъ дружиною и с Торыкы, межи вала; выкрадше первое Святослава, и потомъ убиша Китана и дружину его всю избиша. Вечеру сущю тогда суботному, а Итлареви в ту ноідь лежащю на синици у Ратибора і не в<small>Ђ</small>душу ему, что ся надъ Китаномъ створи в ту ношъ; наутр<small>Ђ</small>я же в нед<small>Ђ</small>лю, в завьтрен<small>Ђ</small> сущи години, пристрои же Ратиборъ отроки в оружьи, и ізъбу престави имъ затопити. І присла Володімеръ отрока своего Бандюка по Итлареву чадь, і рече Бяидукъ ко Итлареви: &laquo;зоветь вы князь Володимеръ, реклъ тако: обувшеся в тепл<small>Ђ</small> истъб<small>Ђ</small> і завътрокавше в Ратибора, приид<small>Ђ</small>те же ко мн<small>Ђ</small>&raquo; И рече Итларь: &laquo;тако буди&raquo;. И яко вл<small>Ђ</small>зоша во истьбу, и запроша я; и вои зл<small>Ђ</small>зъше на изьдбу, и прокопаша истьбу, и тако Ельб<small>Ђ</small>хъ Ратиборечь вземъ лукъ свой и наложивъ стр<small>Ђ</small>лу, вдари Итларя подъ ссрдце, и дружину его всю простр<small>Ђ</small>ляша; и тако зл<small>Ђ</small> испроверже животъ свой Итларь со дружиною своею, в нед<small>Ђ</small>лю сыропустную, въ 1 часъ дни. Святополкъ же и Володимерь посласта ко Олгови, веляча ему ити съ собою на Половьц<small>Ђ</small>; Олегъ же об<small>Ђ</small>щася ити съ нима, и пошедъ, не иде <em>с </em>нима в путь единъ. Святополкъ же и Володимеръ идоста на веж<small>Ђ</small>, [и взяста вежи] и полониша скоты, и кони, и вельблюды, и челядь, и приведоста в землю свою; и начаста гн<small>Ђ</small>ватися на Олга, яко не шедшю ему на поганыя с нима И посла Святополкъ и Володимер<small>Ђ</small> ко Олгови, глаголюща сице: &laquo;се ты не шелъ еси с нами на поганы<small>Ђ</small>, иже погубиша землю Русьскую; а се у тебе есть Ітларевичь, любо [его] убий, любо дай нама, то есть ворогъ мама и Русьской земл<small>Ђ</small>&raquo; Олегъ же сего не послуша и бысть межи ими ненависть.</p>\r\n<p>В се же л<small>Ђ</small>то приидоша Половц<small>Ђ</small> ко Гурьгову, и стояша около его л<small>Ђ</small>то все, мало не възяша его, Святополкъ же умири я; Половц<small>Ђ</small> же приидоша за Рось, Гюргевци же выб<small>Ђ</small>гоша и приидоша къ Кыеву. Святополкъ же повел<small>Ђ</small> рубити городъ на Вытечевьскомъ холъм<small>Ђ</small>, имя свое нарекъ Святополчь градъ, и повел<small>Ђ</small> епискому Мюриму со Гурговц<small>Ђ</small> с<small>Ђ</small>ст<small>Ђ</small> ту, и Засаковцемъ, и прочимъ о[тъ] нихъ; а Гюргевъ зажгоша Половц<small>Ђ</small> тощь.</p>\r\n<p>Сего же л<small>Ђ</small>та исходяча, иде Давыдъ Святославичь из Новагорода къ Смоленьску; Новгородц<small>Ђ</small> же идоша Ростову по Мьстислава Володимерича, и поемъше и приведоша и Новугороду, а Давыдови рекоша: &laquo;не ходи к намъ&raquo;. Пошедъ Давыдъ, узворотися и с<small>Ђ</small>де у Смоленьск<small>Ђ</small> опять, а Мьстислазъ с<small>Ђ</small>де у Нов<small>Ђ</small>город<small>Ђ</small>.</p>\r\n<p>В се же время прииде Изяславъ, сынъ Володимерь, іс Курьска к Мурому; и прияша и Муромьц<small>Ђ</small>, и я посадника Олгова.</p>\r\n<p>В се же л<small>Ђ</small>то приидоша пруз<small>Ђ</small>, м<small>Ђ</small>сяца августа 28, и покрыша землю, и б<small>Ђ</small> вид<small>Ђ</small>ти страшно, идяху к полуношнымъ страиахъ, ядуще траву или проса.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6602 [1094]. Уклав мир Святополк із половцями і взяв жінку, дочку Тугортокана, князя половецького.</p>\r\n<p>Того ж літа Олег прийшов із Тмуторокані з половцями. І прийшов до Чернігова. Володимир же закрився в городі. Олег же прийшов до города і попалив побіля города, і монастирі попалив. Володимир же уклав мир з Олегом і пішов із города на стіл отчий у Переяслав, а Олег ввійшов у город отця свого. Половці ж почали воювати біля Чернігова. Олег не забороняв їм, бо сам повелів їм воювати. Це вже втретє навів Олег поганих на Руську землю, хай Бог простить йому гріх його, бо багато християн вигублено було, а других у полон взято і розсіяно по [чужих] землях.</p>\r\n<p>У це ж літо прийшла саранча на землю Руську місяця серпня в 16-й [день] і поїла всяку траву і багато жита. І того не чули від днів перших землі Руської, Що бачили очі наші, за гріхи наші.</p>\r\n<p>У це ж літо місяця квітня в 27-й [день] о шостій годині ночі помер єпископ Володимирський Стефан, який раніше був ігуменом Печерського монастиря.</p>\r\n<p>В літо 6603 [1095]. Ходили половці на Греки із Девгеневичем. І воювали на Греки, а цар [грецький] захопив Девгеневича і осліпив.</p>\r\n<p>У це ж літо прийшли половці, Ітлар і Китан, до Володимира миритися. І прийшов Ітлар у город Переяслав, а Китан став між валами з військом. І дав Володимир сина свого Святослава у заложники Китанові, а Ітлар перебував у городі з луччою дружиною.</p>\r\n<p>У це ж врем\'я прийшов Слав\'ята із Києва від Святополка до Володимира у якійсь справі. І почала думати дружина Ратиборових чад з князем Володимиром, як винищити Ітларових чад. Володимир не хотів цього робити, кажучи: &laquo;Як я можу це зробити, коли поклявся йому?&raquo; У відповідь дружина сказала Володимирові: &laquo;Княже, не буде тобі з тому гріха. Привів їх сам Бог у руки твої. Чому вони завжди, давши клятву тобі, гублять землю Руськую і кров християнську проливають безперестанно&raquo;.</p>\r\n<p>І послухав їх Володимир, і в ту ж ніч послав Володимир Слав\'яту з невеликою дружиною і з тррками між вали. Найперше викрали Святослава, а потім убили Китана і всю його дружину перебили. Був тоді суботній вечір, а Ітлар у ту ніч спав у Ратибора на сіннику і не знав сам, що зробили з Китаном у ту ніч. Ранком у неділю, в години заутрені, приготував Ратибор отроків в оружжі і наказав їм натопити хату. І прислав Володимир отрока свого Бандюка за їтларевими чадами. І рече Бандюк Ітларові: &laquo;Кличе вас князь Володимир, сказав так: &laquo;Взуєтеся в теплій хаті, поснідаєте в Ратибора і приходьте до мене&raquo;. І рече Ітлар: &laquo;Так буде&raquo;. І як тільки ввійшли в хату, і заперли їх. І вилізли вої на хату, і проробили діру, і тоді Ельбіх Ратиборич узяв лук свій, і наклав стрілу, ударив Ітлара під серце, і дружину його всю перестріляли. І так безславно закінчив життя своє Ітлар із дружиною своєю в сиропусну неділю о 1 годині дня. Святополк же і Володимир послали до Олега, наказуючи йому іти з ними на половців. Олег же пообіцяв іти з ними і, виступивши, не пішов із ними одним шляхом. Святополк же і Володимир пішли на вежі, і взяли їх, і захопили худобу, і коні, і верблюди, і челядь, і привели у землю свою. І почали гніватися на Олега, що не пішов із ними на поганих. І послали Святополк з Володимиром до Олега, кажучи таке: &laquo;От ти не пішов з нами на поганих, що розорили землю Руську, а ще й маєш у себе Ітларевича. Або вбий його, або дай нам, то ворог наш і Руської землі&raquo;. Олег же їх не послухав, і була між ними вражда.</p>\r\n<p>У це ж літо прийшли половці до Гургова і простояли біля нього все літо, і ледве не захопили його. Святополк же замирив їх. Половці ж прийшли за Рось, а гургівці вибігли і прийшли в Київ. Святополк же повелів закладати город на Витичівському горбі, і назвав його своїм іменем - Святополків град, і повелів єпископу Мюриму із гургівцями сісти тут, і засаківцям і іншим із других городів. А Гургів опустілий запалили половці.</p>\r\n<p>Наприкінці цього ж літа пішов Давид Святославич із Новгорода до Смоленська. Новгородці ж пішли в Ростов за Мстиславом Володимиричем і, взявши його, привели в Новгород, а Давидові сказали: &laquo;Не ходи до нас&raquo;. І пішов Давид, повернувся і сів у Смоленську знову, а Мстислав сів у Новгороді.</p>\r\n<p>У це ж врем\'я прийшов Ізяслав, син Володимирів, із Курська до Мурома. І прийняли його муромці, і захопив він посадника Олегового.</p>\r\n<p>У це ж літо прийшла саранча, місяця серпня 28, і покрила землю, і було дивитися страшно. Ішла вона в північні землі, пожираючи траву і просо.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6604. Святополкъ и Володимеръ посласта к Олгови, глаголюща сице: &laquo;поиди Кыеву, ать рядъ учинимъ о Руской земьл<small>Ђ</small> предъ епископы, игумены и предъ мужи отець нашихъ, и передъ горожаны, дабы оборонили землю Русьскую отъ поганых&raquo;. Олегъ же усприемъ смыслъ буй и словеса величава, рече сице: &laquo;н<small>Ђ</small>сть л<small>Ђ</small>по судити [мене] епископомъ и черньцемъ, или смердомъ&raquo;; и не восхот<small>Ђ</small> ити къ братома своима, послушавъ злыхъ св<small>Ђ</small>тникъ. Святополкъ же и Володимеръ рекоста к нему: &laquo;да се ты ни на поганыя идеши с нама, ни на думу, тоть и ты зло мыслиши на наю и помотати хощеши поганымъ; а Богъ промежи нама будеть&raquo;. Святополкъ же и Володимеръ идоста на Олга къ Чернигову; і выб<small>Ђ</small>же Олегъ іс Черн<small>Ђ</small>гова, м<small>Ђ</small>сяца маия въ 3 день, в суботу. Святополкъ же и Володимеръ гнаста по н<small>Ђ</small>мь, Олегъ же вб<small>Ђ</small>же въ Стародубъ и затзорися ту; Святополкъ же и Володимеръ оступиста [и] у город<small>Ђ</small>, и бьяхуся из города кр<small>Ђ</small>пко, а си приступаху къ граду, и уязвен<small>Ђ</small> бываху мноз<small>Ђ</small> отъ обоиихъ; и бысть межи ими брань люта, и стояша около града дни 30 и 3, и изнемагаху люди в город<small>Ђ</small>. И выиде Олегъ из города, хотя мира, и вдаста ему миръ, рекуще сице: &laquo;нди къ брату своему Давыдови, и приидита къ Кыеву на столъ отець нашихъ и д<small>Ђ</small>дъ нашихъ, яко то есть стар<small>Ђ</small>й в земл<small>Ђ</small> наш<small>Ђ</small>й Кыевъ, и ту достоить намъ снятися и порядъ положити&raquo;. Олегъ же об<small>Ђ</small>іцася створити, и на семь ц<small>Ђ</small>ловаша хрестъ.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6604 [1096]. Святополк і Володимир послали до Олега, кажучи таке: &laquo;Приходь у Київ, та укладемо договір про Руську землю у присутності єпископів, ігуменів, і з мужами батьків наших, і з горожанами, щоб боронити землю Руську від поганих&raquo;. Олег же, сповнившись розумом зухвалим і словами зарозумілими, сказав таке: &laquo;Не добре мене судити ні єпископам, ні ченцям, ні смердам&raquo;. І не захотів іти до \"братів своїх, послухавши злих порадників. Святополк же і Володимир сказали про нього: &laquo;Так от ти ні на поганих не ходиш із нами, ні на думу до нас, то і ти недобре проти нас замишляєш і помагати хочеш поганим. Хай Бог розсудить нас&raquo;. Святополк же і Володимир пішли на Олега в Чернігів. І вибіг Олег із Чернігова місяця травня в третій день у суботу. Святополк же і Володимир гналися за ним. Олег же вбіг у Стародуб і закрився там. Святополк же і Володимир облягли його в городі, і билися із города завзято, а ці йшли приступом на город, і було багато вражених з обох боків. І була між ними січа запекла, і стояли навколо города тридцять три дні, і знемагали люди в городі. І вийшов Олег із города, благаючи миру. І дали йому мир, сказавши таке: &laquo;Іди до брата свого Давида і приходьте в Київ, на стіл батьків наших і дідів наших, бо то найстарший у землі нашій, Київ, і в ньому належить нам зібратися і порядок покласти&raquo;. Олег же обіцяв так зробити і на цьому цілував хрест.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В се же время прииде Бонякъ с Половьц<small>Ђ</small> къ Кыеву, у нед<small>Ђ</small>лю, отъ вечера, и повоеваша околъ Кыева, и пожьже на Берестовомъ дворъ княжь.</p>\r\n<p>В се же веремя воева Куря с Половц<small>Ђ</small> Переяславля, и Устье пожьже, м<small>Ђ</small>сяца маия 24. Олегъ же выде исо Стародуба вонъ и прииде къ Смоленьску, и не прияша его Смолняне, и иде к Рязаню; а Святополкъ и Володимеръ идоста усвояси.</p>\r\n<p>Того же м<small>Ђ</small>сяца приде Тугорканъ, тесть Святополчь, къ Переяславлю, м<small>Ђ</small>сяца маия въ 31, и ста около города, а Переяславц<small>Ђ</small> затворишася в город<small>Ђ</small>. Святополкъ же и Володимеръ поидоста на нь, по с<small>Ђ</small>й сторон<small>Ђ</small> Дн<small>Ђ</small>пра, и приидоста к Зарубу, и туто перебродистася, и не почютиша ихъ Половц<small>Ђ</small>, Богу схраншю ихъ, исполчившася поидоста к городу; гражан<small>Ђ</small> узр<small>Ђ</small>вше, ради быша, и изидоша к нима, а Половин стояху на оной сторон<small>Ђ</small> Труб<small>Ђ</small>жа; исполчивъвшеся. Святополкъ же и Володимеръ убредша у Труб<small>Ђ</small>шь к Половцемъ, и нача Володимеръ хот<small>Ђ</small>ти порядити дружины, они же не послушаша, но удариша в кон<small>Ђ</small> къ противнымъ; и се видивше Половци, устремишася на б<small>Ђ</small>гъ, и наши погнаша у сл<small>Ђ</small>дъ ратныхъ, с<small>Ђ</small>куще противныя. Сд<small>Ђ</small>явъ т<small>Ђ</small> день Господь спасенье велико, м<small>Ђ</small>сяца иуля въ 19 день поб<small>Ђ</small>жени быша иноплеменьниц<small>Ђ</small>, и князь ихъ Тугортъканъ убьенъ бысть, и сынъ его, и инии князи мнози ту падоша; наутрея же нал<small>Ђ</small>зоша Тугоркана мертва, и взя и Святополкъ [акы] цьстя своего и аки врага, и привезъше Киеву и погребоша и на Берестовом<small>Ђ</small> на могыл<small>Ђ</small>, межи путемъ грядущимъ на Берестовое; а другымъ идушимъ в монастырь. Въ 20 того же м<small>Ђ</small>сяца, въ день пятокъ, въ часъ 1 дне, прииде второе Бонякъ безбожный, шолудивый, отай, хыщникъ, Кыеву внезапу, и мало в городъ не вогнаша Половци, и зажгоша по п<small>Ђ</small>ску около города, и увратишася на монастыр<small>Ђ</small>, і пожгоша манастырь Стефанечь, деревн<small>Ђ</small>, и Германечь. І приидоша на манастырь Печерьскый, намъ сущимъ по к<small>Ђ</small>льямъ почивающимъ по заутрени, и кликоша около макастыря, и поставиша 2 стяга предъ вороты манастырьскыми, намъ же б<small>Ђ</small>жащимъ за домъ монастыря, а другымъ уб<small>Ђ</small>гшимъ на полат<small>Ђ</small>; безбожніи же сынове Измаилеви выс<small>Ђ</small>коша врата манастырю и устремишася по к<small>Ђ</small>льямъ, выс<small>Ђ</small>кающе двери, изношаху еже аще обр<small>Ђ</small>таху у к<small>Ђ</small>льи; и посемь всжгоша домъ святыя Владычиц<small>Ђ</small> Богородиц<small>Ђ</small>, и приидоша къ церкви, и зажгоша двер<small>Ђ</small> яже ко угу устроенни<small>Ђ</small>, и въ вторыя, иже к с<small>Ђ</small>веру; і вл<small>Ђ</small>зъше у притворъ у гроба Федосьева, и вземьше иконы, зажигашу двери и укаряху Бога и законъ нашь. Богъ же терпяше, и еще бо не скончалися бяху гр<small>Ђ</small>си ихъ и безаконье ихъ; т<small>Ђ</small>мже и глаголаху: &laquo;гд<small>Ђ</small> есть Богъ ихъ? да поможеть имъ и избавить я отъ насъ&raquo;, и ина словеса хулная глаголаху на святыя иконы, насмихающеся, не в<small>Ђ</small>дуще, яко Богъ казнить рабы своя напастьми и ратьми, да явиться яко злато искушено у горьнил<small>Ђ</small>: хрестьяномъ бо многими скорбьми і печальми [подобает] внити въ царство небесное, а симъ поганымъ ругателемъ на семь св<small>Ђ</small>т<small>Ђ</small> пріемшімъ веселье и пространество, а на ономъ св<small>Ђ</small>т<small>Ђ</small> приимуть муку съ дьяволомъ и огнь в<small>Ђ</small>чный. Тогда же зажгоша и дворъ красный, егоже поставилъ благов<small>Ђ</small>рный князь Всеволодъ на хол му, иже есть иадъ Выдобычь: то все окани<small>Ђ</small>и Половц<small>Ђ</small> запалиша огнемъ. Т<small>Ђ</small>мьже и мы, посл<small>Ђ</small>д<small>Ђ</small>ствуюіде Пророку, глаголемъ: Боже мой! положи я, яко коло, аки огнь предъ лицемь в<small>Ђ</small>тру, иже попалить дубравы, тако поженеши я бурею твоею, исполниши и лица ихъ досаженья; се бо оскверниша и пожгоша святый домъ твой, манастырь Матере твоея, и трупье рабъ твоихъ; убиша бо отъ братья нашея н<small>Ђ</small>колько оружьемь безбожьнии сынове Измаилеви, пущенни на казнь хрестьяномъ. Аще ли бо си суть отъ пустыня Етривьския, межи въстокомъ и с<small>Ђ</small>веромъ; ищьло жь есть ихъ кол<small>Ђ</small>нъ 4: Торкмене, и Печен<small>Ђ</small>зи, Торци, Половьц<small>Ђ</small>. Мефедий же свидительствуеть о нихъ, яко 8 кол<small>Ђ</small>нъ проб<small>Ђ</small>гл<small>Ђ</small> суть, егда ис<small>Ђ</small>че я Гедеонъ, осмь ихъ б<small>Ђ</small>жа в пустыню, а 4 ис<small>Ђ</small>че. Друзии же глаголють: сыны Амоновы. Н<small>Ђ</small>сть тако: сынове бо Моавли Хвалисе, а сынове Амонові Болгаре, а Срацини отъ Измаила и творяться Сарини, и прозвана имя соб<small>Ђ</small> Саракине, рекше: Сарини есмы, Т<small>Ђ</small>мьже Хвалисе и Болгаре суть отъ дщерью Лотову, иже зачаста отъ отца своего, т<small>Ђ</small>мже нечисто есть племя ихъ; а Измаило роди 12 сына, отъ нихъ же суть Торъкмени, Печен<small>Ђ</small>зи, и Торци, и Половци, иже исходять отъ пустын<small>Ђ</small>; и по сихъ 8 кол<small>Ђ</small>нъ къ кончин<small>Ђ</small> в<small>Ђ</small>ка наидуть, заклепани в гор<small>Ђ</small> Олександромъ Макидоньскомь, нечистыя челов<small>Ђ</small>кы.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>У це ж врем\'я прийшов Боняк із половцями до Києва, в неділю ввечері, і повоював околицю Києва, і спалив двір княжий на Берестовому.</p>\r\n<p>У це ж врем\'я Куря з половцями воював Переяслав, і Устя спалив місяця травня 24. Олег же вийшов із Стародуба геть і прийшов у Смоленськ. І не прийняли його смоленці, і пішов він у Рязань. А Святополк і Володимир пішли додому.</p>\r\n<p>У тому ж місяці прийшов Тугоркан, тесть Святополків, до Переяслава, місяця травня в 31 день, і став біля города, а переяславці закрилися в городі. Святополк же і Володимир пішли на нього по цьому [правому] боці Дніпра, і прийшли до Заруба, і тут перейшли вбрід, і не помітили їх половці, бо Бог оберігав. І, приготувавшись до бою, пішли на город. Горожани, побачивши, були раді і вийшли до них, а половці стояли на тому боці Трубежа, зготувавшись до бою. Святополк же і Володимир пішли вбрід через Трубіж на половців. 1 спочатку Володимир хотів вишикувати дружини, вони ж не послухали, а ударили в коней на ворога. І коли це побачили половці, кинулися тікати, і наші погналися вслід за ратними, рубаючи ворогів. Содіяв Господь у той день спасіння велике: місяця липня в дев\'ятнадцятий день переможені були чужоплемінці, і князя їхнього Тугортокана було вбито, і сина його, і інших князів багато тут полягло. Ранком знайшли Тугоркана мертвого. І взяв його Святополк як тестя свого і як ворога, і привіз до Києва і похоронив його на могилі в Берестовому, між дорогами, що розбігаються одна на Берестове, а друга - в монастир. А 20-го того ж місяця, в п\'ятницю, о першій годині дня, прийшов удруге до Києва Боняк безбожний, шолудивий, потайки, як хижак, несподівано, і трохи в город не ввігналися половці. І запалили пониззя біля города, і повернули на монастирі, і спалили Стефанів монастир, села і Германів. І прийшли до монастиря Печерського, коли ми в келіях після заутрені відпочивали, і вигукували побіля монастиря, і поставили два стяги перед воротами монастирськими, а ми втікали поза монастир, а інші втікали на дзвіниці. Безбожні ж сини Ізмаїлови вирубали ворота монастирські і кинулися по келіях, вирубуючи двері, виносили все, що знаходили в келіях, а після цього запалили дім святої Владичиці Богородиці, і прийшли в церкву, і запалили південні двері, і другі, що з півночі, і влізли у притвор, де гроб Федосіїв, і, взявши ікони, підпалили двері, і оскверняли Бога і закон наш. Бог же терпів, ще-бо не настав кінець гріхам їхнім і беззаконню їхньому, а тому і говорили: &laquo;Де їхній Бог? Хай допоможе їм і захистить їх від нас&raquo;. І іншими словами хулу говорили на святі ікони, насміхаючися і не знаючи, як Бог карає рабів своїх напастями і війнами, щоб ставали вони як золото, в горнилі випробувань: адже християнам через безліч скорбот і печалей належить ввійти в царство небесне, а цим поганим хулителям, які на цьому світі прийняли веселість і привілля, на тому світі дістанеться мука від диявола і огонь вічний. Тоді ж підпалили і двір Красний, що його поставив благовірний князь Всеволод на горі, що над Видубичами,- все те окаянні половці спалили вогнем. Ось тому і ми, ідучи за пророком, просимо: &laquo;Боже мій! Оберни їх в колесо, як вогонь проти вітру, що випалює діброви, так пожени їх бурею твоєю, понівеч лиця їхні досадою&raquo;. Це ж бо вони осквернили і спалили святий дім твій, монастир Матері твоєї, і прах рабів твоїх. Убили декілька братів наших оружжям безбожні сини Ізмаїлови, наслані на кару християнам.</p>\r\n<p>Вийшли ж вони із пустині Єтрівської між сходом і північчю. І вийшло їх чотири коліна: торкмени і печеніги, торки, половці. Мефодій же свідчить про них, що вісім колін пробігли, коли вирубав їх Гедеон, вісім їх бігло в пустиню, а чотири він вирубав. Другі ж говорять: сини Амонови. Але це не так: сини ж бо Моава - це хваліси, а сини Амонови - болгари, а сарацини від Ізмаїла, і видають себе за саринів, і назвали себе сарацинами, кажучи: &laquo;Сарини ми&raquo;. Отже, хваліси і болгари .походять від дочок Лотових, що зачали їх від батька свого, а тому й нечисте плем\'я їхнє. А Ізмаїл народив дванадцять синів, від яких пішли торкмени, печеніги, і торки, і половці, що вийшли з пустині. І після цих восьми колін на кінець віку вийдуть заклепані в горі Олександром Македонським нечисті люди.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&nbsp;</p>',NULL,1853,5,1,NULL,25);
INSERT INTO `publications_publication` VALUES
(4707,'P','2008-12-20',0,NULL,'Note','Повість врем\'яних літ Року 6577 [1069] — 6583 [1075].','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В літо 6577 [1069]. Пішов Ізяслав з Болеславом на Всеслава. Всеслав же пішов проти. І прийшов Всеслав до Білогорода, і, дочекавшись ночі, тайком утік від киян із Білогорода до Полоцька.</span></span></p>','<p><a href=\"http://izbornyk.org.ua/pvlyar/yar07.htm\">Року 6577 [1069] &mdash; 6583 [1075].</a></p>\r\n<p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6577. Поиде Изяславъ с Болеславомъ на Вьсеслава, Всеславъ же поиде противу; и приде къ Б<small>Ђ</small>лугороду Всеславъ, бывшю нощи, утаися Кыанъ б<small>Ђ</small>жа из Б<small>Ђ</small>лагорода кь Полотьску. Заутра же видивше людье б<small>Ђ</small>жавша князя, и вьзвратишася Кыеву и створиша в<small>Ђ</small>че, послдшася кь Святославу и кь Всеволоду, глаголюще: &laquo;мы же зло створили есмы, князя своего прогнавше, а се ведеть на ны землю Лядьскую; а поиде вь градъ отца своего; аще ли не хощета, то намъ неволя; зажегши городъ свой, и ступити вь Гр<small>Ђ</small>цискую землю&raquo;. И Рече имъ Святославъ: &laquo;в<small>Ђ</small> послев<small>Ђ</small> кь брату своему, да аще поидеть на вы с Ляхы погубить васъ, то в<small>Ђ</small> противу ему ратью не дадив<small>Ђ</small> погубити града отца своего; аще ли хощемъ с миромъ, то в мал<small>Ђ</small> придеть дружин<small>Ђ</small>&raquo;; и ут<small>Ђ</small>шиста Кыяны. Святославъ же и Всеволодъ посласта кь Изяславу, глаголюще: &laquo;Всеславь ти б<small>Ђ</small>жалъ, а не води Ляховъ Кыеву, противнаго ти н<small>Ђ</small>туть; аще ли хощеши гн<small>Ђ</small>вомъ ити и погубити градъ, то в<small>Ђ</small>си, яко намъ жаль отня стола&raquo;. То слышавъ Изяславъ, остави Ляхы, иде с Болеславомъ, мало Ляховъ поемь; посла же предъ собою сына своего Мьстислава Кыеву. И пришедъ Мьстиславъ исьс<small>Ђ</small>че Кыаны, иже бяху выс<small>Ђ</small>кли Всеслава, числомъ 70 чади, а другыя исьсл<small>Ђ</small>пиша, другыя без вины погубивъ, не испытавъ. Изяславу же идущю кь граду, и изиидоша людье противу с поклономъ, и прияша князь свои Кыяне; и с<small>Ђ</small>де Изяславъ на стол<small>Ђ</small> своемь, м<small>Ђ</small>сяца мая вь 2 день, и распуща Ляхы на покормъ; и изьбиваху Ляхы отай; возвратися Болеславъ въ землю свою. Изяславъ же изгна торгь на гору, и прогна Всеслава ис Полотьска, и посади сына своего Мьстислава вь Полотьск<small>Ђ</small>, иже вьскор<small>Ђ</small> умре ту; и посади в него м<small>Ђ</small>сто брата его Святополка, а Всеславу же б<small>Ђ</small>жавшю.</p>\r\n<p>В л<small>Ђ</small>то 6578. Родися у Всеволода сынъ, и нар<small>Ђ</small>коша именемь Ростиславъ.</p>\r\n<p>Того же л<small>Ђ</small>та заложена бысть церквы святаго Михаила, в манастыр<small>Ђ</small> Вьсеволожи на Выдобычи.</p>\r\n<p>В л<small>Ђ</small>то 6579 Воеваша Половци у Растовца [и] у Неятина.</p>\r\n<p>Того же л<small>Ђ</small>та выгна Всеславъ Святополка ис Полотьска.</p>\r\n<p>Того же л<small>Ђ</small>та поб<small>Ђ</small>ди Ярополкъ Всеслава у Голотичьска.</p>\r\n<p>В та же времена приде волъхвь, прилыценъ б<small>Ђ</small>сомъ; пришедъ бо Кыеву, глаголаше: &laquo;явили ми ся есть 5 богъ, глаголюще сице: пов<small>Ђ</small>дай людемь, яко на пять л<small>Ђ</small>ть Дн<small>Ђ</small>пру потещи вьспять, а землямь переступати на ина м<small>Ђ</small>ста, яко стати Гр<small>Ђ</small>чкой земли на Руской земли, а Руской на Гр<small>Ђ</small>чкой, и прочимъ землямъ изм<small>Ђ</small>нитися&raquo;; егоже невегласни послушахуть, а в<small>Ђ</small>рнии насм<small>Ђ</small>хахуся, глаголюще ему. &laquo;б<small>Ђ</small>съ тобою играеть на пагубу тоб<small>Ђ</small>&raquo;. Еже и бысть ему. вь едину бо нощь бысть без в<small>Ђ</small>сти. Б<small>Ђ</small>сы бо потокше и, на зло вьводятъ и, посемъ же насм<small>Ђ</small>хающися, вринуша и в пропасть смертьную, научивше и глаголати; яко се скажемь б<small>Ђ</small>совьское наущение и д<small>Ђ</small>йство. Бывши бо единою скудости вь Ростовьст<small>Ђ</small>й области, и вьстаста два волъхва отъ Ярославьля, глаголюща: &laquo;яко в<small>Ђ</small> св<small>Ђ</small>мы кто обилье держить&raquo;; и поидоста по Волз<small>Ђ</small>; и кд<small>Ђ</small> придучи в погость, ту же начаста лучьшия жены, глаголюща, яко си жито держать, а сии медъ, а сии рыбы, а сии скору. И привожаху к нима сестры своя, и матери, и жены своя; она же вь мьчт<small>Ђ</small> прор<small>Ђ</small>завше за плечемь, вынимаста любо жито, любо рыбы, или в<small>Ђ</small>верицю, и убиваша многы жены, им<small>Ђ</small>ния ихъ имаша соб<small>Ђ</small>. И приидоста на Б<small>Ђ</small>лоозеро; и б<small>Ђ</small> о нею людий иа<small>Ђ</small>хъ 300</p>\r\n<p>В то же время приключися прити отъ Святослава дань емлющю Яневи, сыну Вышатину; и пов<small>Ђ</small>даша ему Б<small>Ђ</small>лоозерьци, яко два кудесника избила многы жены по Волъз<small>Ђ</small> и по Шьксн<small>Ђ</small>, и пришла есть с<small>Ђ</small>мо. Янь же испытавъ, чья еста смерда, и ув<small>Ђ</small>д<small>Ђ</small>въ, яко своего ему князя, пославь же къ нимъ иже около ею суть, и рече имъ: &laquo;выдайте волъхва та с<small>Ђ</small>мо, яко смерда еста моего князя&raquo;; они же сего не послушаша. Янь же поиде самъ безъ оружья, и р<small>Ђ</small>ша ему отроки его: &laquo;не ходи безъ оружья, осоромять тя&raquo;; онъ же повел<small>Ђ</small> взяти оружье отрокомъ, и бяста 12 отрока с нимъ, и поиде к нимь кь л<small>Ђ</small>су. Они же сташа сплчившеся противу. Яневи же идущю с топорцемь, выступиша отъ нихъ трие мужии, придоша кь Яневи, рекуще ему: &laquo;видя идеши на смерть, не ходи&raquo;; оному же повел<small>Ђ</small> бити я, кь прочимъ же поиде. Они же сунушася на ня, единъ гр<small>Ђ</small>ши Яня топоромъ, Янь же оборотя топоромъ и удари тыльемь, и повел<small>Ђ</small> отрокомъ с<small>Ђ</small>щи я; они же б<small>Ђ</small>жаша в л<small>Ђ</small>съ; убиша же ту попа Янева. Янь же вшедъ в городъ к Б<small>Ђ</small>лоозерчемь, и рече имъ: &laquo;аще не имете волъхву сею, и не иду отъ васъ за л<small>Ђ</small>то&raquo;. Б<small>Ђ</small>лоозерьци же шедше и яша и, и приведоша я к нему, и рече има: &laquo;что ради погубисте толико челов<small>Ђ</small>къ?&raquo; Онима же рекшима: &laquo;яко си держать гобину, да аще истр<small>Ђ</small>бив<small>Ђ</small> избьев<small>Ђ</small> всихъ, и будеть обилье; аще ли хощеши, то предъ тобою выемлев<small>Ђ</small> жито, или рыбу, или ино&raquo;. Янъ же рече: &laquo;по истин<small>Ђ</small> лжете: створилъ бо есть Богъ челов<small>Ђ</small>ка отъ земля, и съставленъ костьми и жилами отъ крови, и н<small>Ђ</small>сть в немъ ничтоже, токмо Богъ единъ в<small>Ђ</small>сть&raquo;. Она же рекоста: в<small>Ђ</small>. два в<small>Ђ</small>даев<small>Ђ</small>, како есть створенъ челов<small>Ђ</small>къ&raquo;. Онъ же рече: &laquo;како?&raquo; Она же рекоста: &laquo;мывся Богъ в мовьници и вьспотився, отерься в<small>Ђ</small>хтемь, и св<small>Ђ</small>рже с небеси на землю; и распр<small>Ђ</small>ся Сотона сь Богомъ, кому в немь створити челов<small>Ђ</small>ка? и створи дьяволъ челов<small>Ђ</small>ка, а Богъ душю в онь вложи; т<small>Ђ</small>мже аще умреть челов<small>Ђ</small>кь, в землю идеть, а душа кь Богу&raquo;. Рече же има Янь: &laquo;по истин<small>Ђ</small> прельстилъ есть васъ дьяволъ; которому Богу в<small>Ђ</small>руета?&raquo; Она же рекоста: &laquo;Антихръсту&raquo;. Онъ же рече има: &laquo;то гд<small>Ђ</small> есть?&raquo; Она же рекоста: &laquo;с<small>Ђ</small>дить вь бездн<small>Ђ</small>&raquo;. И рече има Янь: &laquo;то кий есть Богъ, с<small>Ђ</small>дя вь бездн<small>Ђ</small>? то есть б<small>Ђ</small>съ, а Богъ есть с<small>Ђ</small>дя на небес<small>Ђ</small>хъ и на престолъ, славимъ отъ ангелъ, иже предъстоять ему со страхомъ, не могуще на нь зр<small>Ђ</small>ти; а сий бо отъ ангелъ св<small>Ђ</small>рженъ бысть, егоже вы глаголете Антихръста, за величание его и св<small>Ђ</small>рженъ бысть с небеси, и есть в бездн<small>Ђ</small>, якоже вы глаголета, ждя, егда придеть с небес<small>Ђ</small>, и сего емь Антихръста, свяжеть узами и посадить во огни в<small>Ђ</small>чнемь со слугами его, и иже к нему в<small>Ђ</small>руеть, а вама же зде мука прияти отъ мене, а по смерти тамо&raquo;. Он<small>Ђ</small>ма же рекшима &laquo;нама бози пов<small>Ђ</small>дають: не можеши нама створити ничтоже&raquo; Онъ же рече има: &laquo;лжють вама бози ваши&raquo;. Она же рекоста. &laquo;нама предстати предъ Святославомъ, а ты намъ не можеши створити ничтоже&raquo; Янь же повел<small>Ђ</small> бити я, и поторъгати брад<small>Ђ</small> ею. Сима же битыма и брад<small>Ђ</small> поторган<small>Ђ</small> проск<small>Ђ</small>помъ, рече има Янь: &laquo;что вамъ боз<small>Ђ</small> молвять?&raquo; Он<small>Ђ</small>ма же рекьшима: &laquo;стати намъ предъ Святославомъ&raquo;. И повел<small>Ђ</small> Янь вложити има рубля въ уста, и привязати ко упругамъ, и пустити я предъ собою в лодьяхъ, а самъ по нихъ иде. И сташа на устьи Шекьсны, и рече има Янь: &laquo;што вамъ молвять бози ваши?&raquo; Она же рекоста сице: &laquo;намъ бози молвять, не быти нама живымъ отъ тебе&raquo;. И рече има Янь: &laquo;то вамъ право молвять боз<small>Ђ</small> ваши&raquo;. Она же рекоста. &laquo;аще насъ пустиши, много ти добра будеть; аще насъ погубиши. многу печаль приимеши и зло&raquo;. Онъ же рече има: &laquo;аще васъ отпущю, то зло ми будеть отъ Бога; аще ли васъ погублю, то мьзда ми будеть отъ Бога&raquo;. И рече Янь к подвозникомь: &laquo;ци кому васъ родинъ убьенъ отъ сею?&raquo; Они же р<small>Ђ</small>ша: &laquo;мн<small>Ђ</small> мати, а другому сестра, иному родичь&raquo;. Онъ же рече имъ: &laquo;мьстите своихъ&raquo;. Они же поимше я, избиша и и пов<small>Ђ</small>сиша я на др<small>Ђ</small>в<small>Ђ</small>: отм<small>Ђ</small>стье приимша отъ Бога по правд<small>Ђ</small>. Яневи же идущю домовь, вь другую нощъ медв<small>Ђ</small>дь вл<small>Ђ</small>зъ, угрызъ я и сн<small>Ђ</small>де кудеснику; и тако погибнуста научениемь дьяволимъ, ин<small>Ђ</small>мь в<small>Ђ</small>дуща и гадающа, а своея пагубы не в<small>Ђ</small>дуща. Аще быста в<small>Ђ</small>дала, то не бы пришла на м<small>Ђ</small>сто се, ид<small>Ђ</small>же ятома быти, аще ли ята быста, то почто глаголаста: &laquo;яко не умрети нама&raquo; а оному мыслящю убити я? Но се есть б<small>Ђ</small>совьское учеше; б<small>Ђ</small>си бо не в<small>Ђ</small>дають мысли челов<small>Ђ</small>ческыя, но влагають помыслъ вь челов<small>Ђ</small>ка, а тайны не в<small>Ђ</small>дуща, Богъ же единъ в<small>Ђ</small>сть помышления челов<small>Ђ</small>цьска, б<small>Ђ</small>си бо не в<small>Ђ</small>дають ничегоже: суть бо немощни и худи взоромъ. Яко се скажемь о взор<small>Ђ</small> ихъ и о омраченьи ихъ. В си бо времена и в с<small>Ђ</small> л<small>Ђ</small>та, приключися н<small>Ђ</small>коему Новгородцю прити в Чюдь и приде кудесьнику, хотя волъхования отъ него; онъ же, по обычаю своему, нача призывати б<small>Ђ</small>сы вь храмину свою. Новгородцю же с<small>Ђ</small>дящю на пороз<small>Ђ</small> тоя храмины вь сторон<small>Ђ</small>, кудесникъ лежаще оц<small>Ђ</small>пъ, и шибе имъ б<small>Ђ</small>съ; кудесникъ же вьставъ, рече Новгородцю: &laquo;бозн наши не см<small>Ђ</small>ють внити, н<small>Ђ</small>что имаши на соб<small>Ђ</small>, егоже бояться&raquo;. Онъ же помяну кресть на соб<small>Ђ</small>, и отъшедъ пов<small>Ђ</small>си кром<small>Ђ</small> храмины тоя; онъ же нача изнова призывати б<small>Ђ</small>сы, б<small>Ђ</small>си же мечтавше имъ, пов<small>Ђ</small>даша, что ради пришелъ есть; посемъ же нача присити его: &laquo;что ради бояться его, егоже носимъ на соб<small>Ђ</small> крестъ?&raquo; Онъ же рече: &laquo;то есть знамение небесного Бога, егоже наши бози бояться&raquo;. Онъ же рече: &laquo;бози наши живуть вь безднахъ; суть же образомъ черни, крилати, хвостъ имущи, вьсходять же и подъ небо, слушающе вашихъ богъ; ваши бози на небес<small>Ђ</small> суть; аще кто умреть отъ вашихъ людий, то возносимъ есть на небо; аще ли отъ нашихъ умираеть, то носимъ есть к нашимъ богомъ вь бездну&raquo;. Якоже гр<small>Ђ</small>шници вь ад<small>Ђ</small> суть, ждуще мукы в<small>Ђ</small>чныя, а прав<small>Ђ</small>дници вь небеснемь царствии вь жилищи вьдворяються съ ангелы. Сица ти есть б<small>Ђ</small>совьская сила, и л<small>Ђ</small>пота, и немощь; т<small>Ђ</small>мьже и прельщають челов<small>Ђ</small>кы, велящи имъ глаголати вид<small>Ђ</small>нья, являющеся имъ несвершеною в<small>Ђ</small>рою, являющеся вь сн<small>Ђ</small>, ин<small>Ђ</small>мь вь м<small>Ђ</small>чт<small>Ђ</small>, и тако волъхвують научениемъ дьяволимъ. Паче же женами б<small>Ђ</small>совьская волъхвования бывають; искон<small>Ђ</small> бо б<small>Ђ</small>съ жену прельсти, жена же мужа; таковый родъ много волъхвуеть жены чарод<small>Ђ</small>йствомь, и отравою, ин<small>Ђ</small>мы б<small>Ђ</small>совськими козньми. Но и мужи прельщени бывають отъ б<small>Ђ</small>совъ нев<small>Ђ</small>рнии; яко и се вь первый родъ при апостол<small>Ђ</small>хъ бо бысть Симонъ волъхвъ, иже вълъшествомъ творяше, повел<small>Ђ</small> псомь челов<small>Ђ</small>чскы глаголати, и самъ прем<small>Ђ</small>няшеться, ово старъ, ово молодъ, ово ли иного пременяше въ иного образъ, в мечтаньи сице творяшеть И Анній и Амврий волъшвеньемь чюдеса творяшеть противу Моис<small>Ђ</small>еви но въскор<small>Ђ</small> не возмогоста. но и Кунонъ творяшеть мьчтаниемь б<small>Ђ</small>совьскымъ, яко и по водамъ ходити и ина м<small>Ђ</small>чтания творяше, б<small>Ђ</small>сомъ льстимь, на пагубу соб<small>Ђ</small> и ин<small>Ђ</small>мъ Сице бысть волъхвъ вьсталъ при Гл<small>Ђ</small>б<small>Ђ</small> в Нов<small>Ђ</small>город<small>Ђ</small> глаголашеть бо людемь и творяшеть бо ся аки Богомъ, и многы прельсті, мало не весь городъ глаголаше бо, &laquo;яко все ведаю&raquo; хуля в<small>Ђ</small>ру крестьяньскую глаголашеть бо &laquo;яко прейду по Волъ хову предъ всими&raquo;. И бысть мятежь в город<small>Ђ</small>, и вси яша ему в<small>Ђ</small>ру и хотя поб<small>Ђ</small>дити епископа, епископъ же вземь крестъ и оболкъся в ризы, ста рекъ: иже хощеть в<small>Ђ</small>ру яти волъхву, да за нь идеть, аще ли в<small>Ђ</small>руеть кто кресту, да идеть [к нему]&raquo;. И разд<small>Ђ</small>лишася на двое князь бо Гл<small>Ђ</small>бъ и дружина его сташа у епископа, а людье вси идоша за волъхва и бысть мятежь великъ вельми: Гл<small>Ђ</small>бъ же возма топоръ подъ скутъ и приде к волъхву и рече ему: &laquo;то веси ли, что утр<small>Ђ</small> хощеть быти что ли до вечера?&raquo; Онъ же рече: &laquo;все в<small>Ђ</small>даю&raquo;. И рече Гл<small>Ђ</small>бъ: &laquo;то в<small>Ђ</small>си ли, что [ти] хощеть днесь быти?&raquo; Онъ же рече: &laquo;чюдеса велика створю&raquo;. Гл<small>Ђ</small>бъ же выня топоръ, и ростя и и паде мертвъ, и людие разийдошася, онъ же погибе т<small>Ђ</small>ломъ и душею, предавься дьяволу.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6577 [1069]. Пішов Ізяслав з Болеславом на Всеслава. Всеслав же пішов проти. І прийшов Всеслав до Білогорода, і, дочекавшись ночі, тайком утік від киян із Білогорода до Полоцька. Побачили ранком люди, що князь утік, і повернулися в Київ, і зібрали віче, і послали до Святослава і до Всеволода, кажучи: &laquo;Сотворили ми зло, князя свого прогнавши, а тепер він веде на нас землю Лядську. Так ідіть в город батька свого, а коли не захочете, то будемо приневолені город свій спалити і податися в Грецьку землю&raquo;. І сказав їм Святослав: &laquo;Ми пошлемо послів до брата свого, якщо він піде на вас із ляхами, то винищить вас, то ми підемо проти нього війною і не дамо погубити город батька свого, а коли наміри його мирні, то він прийде з малою дружиною&raquo;. І заспокоїлися кияни. Святослав же і Всеволод послали до Ізяслава, кажучи: &laquo;Всеслав від тебе, утік, не веди ляхів до Києва, ворогів там у тебе нема. А коли хочеш у гніві йти і погубити город, то знай, як нам жаль отнього столу&raquo;. Почув те Ізяслав, залишив ляхів і пішов з Болеславом, узяв з собою небагато поляків, а поперед себе послав до Києва сина свого Мстислава. І коли прийшов до Києва, Мстислав перебив киян, які звільнили Всеслава, числом сімдесят людей, а інших осліпив, других безвинних умертвив, навіть не розібравшись. Коли ж Ізяслав підійшов до города, вийшли назустріч йому люди з поклоном, і прийняли кияни князя свого, і сів Ізяслав на столі своєму другого травня. І розпустив ляхів на прокорм, а їх убивали потай, і повернувся Болеслав у землю свою. Ізяслав же перевів торг на гору, і прогнав Всеслава із Полоцька, і посадив там сина свого Мстислава, він же незабаром тут і помер. І посадив замість нього брата його Святополка. А Всеслав же утік.</p>\r\n<p>В літо 6578 [1070]. У Всеволода народився син. І дали йому ім\'я Ростислав.</p>\r\n<p>Того ж літа було закладено церкву святого Михайла у Всеволодовому монастирі на Видубичах.</p>\r\n<p>В літо 6579 [1071]. Воювали половці у Растовця і у Неятина.</p>\r\n<p>Того ж літа Всеслав вигнав Святополка із Полоцька. Того ж літа Ярополк переміг Всеслава біля Голотичська.</p>\r\n<p>У тому ж часі з\'явився волхв, зваблений бісом При йшов до Києва і каже: &laquo;Мені являлися п\'ять богів і ска зали таке: розкажи людям, що через п\'ять років Дніпро потече назад, а землі поміняються місцями- стане Грецька земля на Руській землі, а Руська на Грецькій, та й інші \'Землі поміняються&raquo;. Невігласи слухали його, а віруючі насміхалися, кажучи йому: &laquo;Біс грається тобою на твою погибіль&raquo;. Так і сталося з ним: одної ночі він пропав безвісти. Адже біси підбивають і на зло людей уводять, а потім ще насміхаються, ввергаючи їх у прірву смертную, навчають їх і [що] говорити, як ми оце і розкажемо [про таке] бісівське намовляння і дійство.</p>\r\n<p>Одного разу під час недороду в Ростовській області з\'явилися два волхви із Ярославля, кажучи: &laquo;Ми знаємо, хто запаси ховає&raquo;. І пішли вони по [березі] Волги, і в яку погость прийдуть, так і починають називати кращих жон, кажучи: оці жито держать, а ці - мед, а ці - рибу, а ці - хутра. І приводили до них сестер своїх, і матерів, і жон своїх. Вони ж, у насланні [бісівському], прорізали запліччя, виймали звідти або жито, або рибу, чи вивірицю і убивали багатьох жінок, а майно їхнє забирали собі. І прийшли вони на Білоозеро, і було з ними інших людей триста чоловік.</p>\r\n<p>У той же час трапилося Яневі, Вишатиному синові, прийти туди від Святослава данину збирати. І розповіли йому білоозерці, що два кудесники перебили багато жінок по Волзі і Шексні і прийшли сюди. Янь же розпитав, чиї вони смерди, ці люди, і довідався, що його князя, послав до них, бо були поблизу, і сказав їм: &laquo;Видайте волхва сюди, як смерда мого князя&raquo;. Вони ж його не послухали. Янь же пішов сам без оружжя, і сказали йому отроки його: &laquo;Не йди без оружжя, осоромлять тебе&raquo;. Він же звелів взяти отрокам оружжя, і було з ним дванадцять отроків, і пішов до них у ліс. Вони ж ополчилися проти. Коли Янь рушив на них з топірцем, наперед від них виступило три мужі, підійшли до Яня і сказали йому: &laquo;Бачиш, що йдеш на смерть, не ходи&raquo;. Одному наказавши бити їх, сам пішов до інших. Вони ж кинулись на Яня, один замахнувся топором. Янь же перехопив топір, і ударив обухом, і повелів отрокам сікти їх. Вони ж кинулись тікати в ліс і тут же убили Яневого попа. Янь же ввійшов у город до білоозерців і сказав їм: &laquo;Коли не зловите цих волхвів, і за літо не піду від вас&raquo;. Білоозерці ж пішли, і зловили їх, і привели їх до нього. І спитав їх: &laquo;Навіщо згубили стільки людей?&raquo; Ті ж відказали: &laquo;Вони тримають запаси, і коли винищимо, перебивши, всіх, і буде достаток. Якщо хочеш, то при тобі виймемо жито, чи рибу, чи щось інше&raquo;. Янь же сказав: &laquo;Суща брехня, Бог сотворив чоловіка із землі, і складений він із кісток і кров\'яних жил, і нема в ньому нічого більше, і більше тільки Бог знає&raquo;. Вони ж відказали: &laquo;Ми два знаємо, як створений чоловік&raquo;. Він же спитав: &laquo;Як?&raquo; Вони ж відповіли: &laquo;Мився Бог у мивниці, розпотівся, обтерся віхтем і скинув його з неба на землю. І засперечався Сатана з Богом, кому з нього сотворити чоловіка? І сотворив диявол чоловіка, а Бог душу в нього вклав. От тому, коли вмирає чоловік, тіло в землю йде, а душа до Бога&raquo;. Сказав же їм Янь: &laquo;Воістину, спокусив вас диявол. Якому Богові віруєте?&raquo; Вони ж сказали: &laquo;Антихристові!&raquo; Він же сказав їм: &laquo;То де він?&raquo; Вони ж відповіли: &laquo;Сидить у бездні&raquo;. І сказав їм Янь: &laquo;То який то Бог, коли сидить у бездні? То біс, а Бог сидить на небесах і на престолі, а його славлять ангели, які стоять перед ним у страсі, не сміють на нього глянути. А цей [Сатана] і був скинутий від ангелів, його ви називаєте Антихристом, за гординю його і скинутий був з небес і тепер у бездні, як ви кажете, в чеканні, коли Бог прийде з небес і візьме цього Антихриста, зв\'яже путами і посадить разом зі слугами його і тими, що в нього вірують, в огонь вічний. А вам [належить] прийняти муки від мене тут, а після смерті - там&raquo;. Вони ж сказали: &laquo;Нам боги говорять, що ти з нами не можеш зробити нічого!&raquo; Він же сказав: &laquo;Брешуть вам боги ваші&raquo;. Вони ж сказали: &laquo;Нам належить предстати перед Святославом, а ти сам не можеш зробити нічого&raquo;. Янь же повелів бити їх і посмикати їм бороди. Коли їх били і розщепом видирали бороди, запитував їх Янь: &laquo;Що вам боги говорять?&raquo; Вони ж відповідали: &laquo;Стати нам перед Святославом&raquo;. І наказав Янь вкласти їм рубля в уста, прив\'язати їх до ребра лодії і пустити поперед себе, а сам за ними йшов. І коли стали в гирлі Шексни, запитав їх Янь: &laquo;Що вам говорять боги ваші?&raquo; Вони ж відповіли так: &laquo;Нам боги говорять, що не бути нам живими від тебе&raquo;. І сказав їм Янь: &laquo;То вам правду мовлять боги ваші&raquo;. Вони ж сказали: &laquo;Якщо нас пустиш, багато тобі добра буде, а коли нас погубиш, велику печаль приймеш і зло&raquo;. Він же сказав їм: &laquo;Якщо вас отпущу, то погано буде мені від Бога, а коли вас погублю, то нагорода мені буде від Бога&raquo;. І спитав Янь підвізників: &laquo;Чи є в кого із вас хтось із рідні ними убитий?&raquo; Вони ж кажуть: &laquo;Мені мати, а іншому - сестра, а ще іншому родич&raquo;. Він же каже їм: &laquo;Мстіте за своїх&raquo; Вони ж схопили їх, умертвили і повісили на дереві: помству прийняли від Бога по правді Коли Янь пішов додому, на другу ніч медвідь виліз [на дерево], розтерзав їх і пожер кудесників. І так загинули вони, навчені дияволом,- іншим знали і передбачали погибіль, а своєї погибелі не передбачили. А коли б знали, то не прийшли б на це місце, де мали бути схоплені, а коли були схоплені, то нащо говорили: &laquo;Не померти нам&raquo;, коли Янь уже намислив убити їх? Але це і є бісівське учення, бо біси не знають думок людських, а вкладають помисл у чоловіка, а тайни не знають. Бог же один знає помисли людські, біси ж не знають нічогісінько, бо вони немощні і худі на вигляд. От якраз і розкажемо про вигляд їхній і оману їхню.</p>\r\n<p>У ті ж часи і в ті роки трапилося якомусь новгородцю прийти в Чудь, і прийшов він до кудесника, і просить поволхвувати йому. Той же, за звичаєм своїм, почав скликати бісів у храмину свою. Новгородець же сидів на порозі тієї храмини збоку, кудесник же лежав у оціпенінні: його пройняв біс. Кудесник же встав і сказав новгородцю: &laquo;Боги наші не сміють ввійти, бо щось маєш на собі, чого вони бояться&raquo;. Він же згадав, що має на собі хрест, і відійшов, і повісив хрест поза храминою тією. Волхв знову почав викликати бісів, біси ж, підкидаючи ним, повідали чого ради прийшов [новгородець}. Потім [почав] просити його: &laquo;Чого ради [біси] бояться того, чий хрест на собі носимо?&raquo; Він же сказав: &laquo;То знамення небесного Бога, а його наші боги бояться&raquo;. Він же рече: &laquo;Боги наші живуть у безднах, з виду чорні, крилаті, хвоста мають, забираються і під небо слухати ваших богів. Ваші боги на небі, а коли хто помирає з ваших людей, то його возносять на небо, а коли ж хто із наших помре, його несуть до наших богів у бездну&raquo;. [Так воно і є]: грішники в аді, ждуть муки вічної, а праведники в жилищі царства небесного поселяються з ангелами. От така і є бісівська сила, і краса, і неміч, тим же і зваблюють людей, бо велять їм розповідати видіння, які з\'являються не твердим у вірі, ві сні, іншим у мріях, і так волхвують наукою диявольською. Особливо ж через жінок бісівські волхвування бувають, бо із правіку біс жінку спокусив, жінка ж мужа, через те у теперішньому роді [людському] багато волхвують жінок чарами, і отрутою, і іншими бісівськими підступами. Але і чоловіки невірнії бувають зваблені бісами, як то було в першому роді при апостолах, коли був волхв Симон, що волхвування творив: наказував псам по-людському говорити і сам перекидався то в старого, то в молодого то іншого переміняв у інший образ, в навожд&eacute;нні таке творив. І Анній і Амврій волхвуванням чуда творили проти Мойсея, але небавом уже нічого не могли. Так і Кунон напускає ману бісівську, ніби по водах ходять, і інші навождення творив, зваблений бісом на згубу собі й іншим</p>\r\n<p>Такий волхв явився у Новгороді за Гліба. Він розмовляв з людьми, видаючи себе за бога, і багатьох звабив, мало не весь город, бо говорив: &laquo;Я все знаю&raquo;. Хулив віру християнську, бо говорив: &laquo;Я Волхов перейду у всіх на очах&raquo; ї була смута в городі, і всі повірили йому, і хотіли перемогти єпископа. Єпископ же взяв хрест, і одягнувся в ризу, і став говорити: &laquo;Якщо хто хоче вірити волхву, хай за ним іде, а хто в хреста вірує, хай [до хреста] іде&raquo;. І розділилися надвоє: князь Гліб із дружиною своєю став біля єпископа, а люди всі пішли за волхвом. І була смута дуже велика. Гліб же взяв топір-під плащ і прийшов до волхва і спитав його: &laquo;Чи ти знаєш, що збудеться ранком а чи до вечора?&raquo; Він же відповів. &laquo;Все знаю&raquo; І питає Гліб: &laquo;А чи знаєш, що [з тобою] буде сьогодні?&raquo; Він же відповідає: &laquo;Чудеса великі сотворю&raquo;. Гліб же вийняв топір і розрубав його, і волхв упав мертвий. І люди розійшлися. Він же загинув тілом і душею, віддався дияволу.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6580. Принесоша святая страстотерпца Бориса и Гл<small>Ђ</small>ба. Совокупившеся Ярославличи, Изяславъ, Святъславъ и Всеволодъ, митрополитъ же б<small>Ђ</small> тогда Георгий, епископъ Петръ Переяславьскый, Михаилъ Юрьевьскый, Федосий же игуменъ Печерьскый, Софроний же святаго Михаила игуменъ, Герьманъ святаго Спаса игуменъ и Никола игуменъ Переяславьский, и прочии игуменъ, и вси створивше праздникъ св<small>Ђ</small>телъ, и пре ложиша я в новую церковь, юже сд<small>Ђ</small>ла Изяславъ, яже стоить и нын<small>Ђ</small> Вземше бо первое Бориса в деревяний рак<small>Ђ</small>, Изяславъ, и Святославъ, и Всеволодъ, вземше на плещи своя и понесоша, и предъидущимъ черноризьцемь, св<small>Ђ</small>ща держаще в рукахъ, и по нихъ дьякони с кандилы, и посемь прозвутери, и по нихъ епископи с митрополитомъ, и по нихъ раку несуще, идяхуть. И принесьше и в новую церковь, отверзоша раку, исполнися церкви благоухания вон<small>Ђ</small> благы; видивше се, прославиша Бога. И митрополита ужасъ обииде, бяше бо нетвердо в<small>Ђ</small>руя к нима; и падъ ниць, прося прощения. И ц<small>Ђ</small>ловавше мощи его, вложиша и В раку камену. Посемъ же вземше Гл<small>Ђ</small>ба в рац<small>Ђ</small> камени, и вьставиша и на сани, и емше за вужа везоша и; яко быша вь дв<small>Ђ</small>рехъ, сташа рака, не поидущи. И повел<small>Ђ</small>ша народу звати: &laquo;Господи помилуй&raquo;, и повезоша, и положиша я м<small>Ђ</small>сяца мая вь 20. И отп<small>Ђ</small>вще литургию, об<small>Ђ</small>даша братья си вся на купь, когождо с бояры своими, и с любовью великою. Б<small>Ђ</small> бо тогда держа Вышегородъ Чюдинъ, а церковь Лазорь. И посемь разиидошася вьсвояси.</p>\r\n<p>В л<small>Ђ</small>то 6581. Въздвиже дьяволъ котору вь братьи сей Ярославличихъ. И бывши распре межи ими, быста сь себе Святославъ со Всеволодомъ на Изяслава; и изииде Изяславъ ись Кыева, Святославъ же и Всеволодъ внидоста в Кыевъ, м<small>Ђ</small>сяца марта вь 22, и с<small>Ђ</small>доста на стол<small>Ђ</small> на Берестовомъ, преступивша запов<small>Ђ</small>дь отню. Святославъ же б<small>Ђ</small> начало выгнанию братню, желая болшая власти; Всеволода бо прельсти, и глаголя: &laquo;яко Изяславъ вьстаеть сь Всеславомъ, мысля на наю; да аще его не варив<small>Ђ</small>, имать насъ прогнати;&raquo; и тако взострі Всеволода на Изяслава. Изяславъ же иде в Ляхы со им<small>Ђ</small>ниемь и сь женою, уповая богатьствомъ многымь, глаголя: &laquo;яко симь нал<small>Ђ</small>зу воя;&raquo; еже взяша у него Ляхове, показаша ему путь отъ себе. А Святославъ с<small>Ђ</small>де в Кыев<small>Ђ</small>, прогнавъ брата своего, преступивъ запов<small>Ђ</small>дь отьню, паче же и Божию. Великъ бо есть гр<small>Ђ</small>хъ преступати запов<small>Ђ</small>дь отца своего: ибо исперва преступиша сынови Хамов<small>Ђ</small> на землю Сифову, по 400 л<small>Ђ</small>тъ отмыцение прияша отъ Бога; отъ племени бо Сифова суть Евр<small>Ђ</small>и, иже избіша Хананейско племя, вьсприяша свой жребий и свою землю. И пакы преступи запов<small>Ђ</small>дь Исавъ отца своего, и прия убийство; не доброе есть преступати пред<small>Ђ</small>ла чюжаго.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6580 [1072]. Перенесли [останки] святих страстотерпців Бориса і Гліба. Зібралися Ярославичі - Ізяслав, Святослав і Всеволод, тодішній митрополит Георгій, єпископ Петро Переяславський, Михайло Юр\'ївський, Федосій же ігумен Печерський, Софроній - [монастиря] святого Михайла ігумен, Герман - [монастиря] святого Спаса ігумен, і Никола - ігумен Переяславський, і інші ігу мени, і всі, хто створив цей світлий празник, і перенесли їх у нову церкву, збудовану Ізяславом, яка стоїть і нині. Спочатку взяли останки Бориса в дерев\'яній раці. Ізяслав, Святослав і Всеволод взяли на плечі свої і понесли, а попереду йшли чорноризці, тримаючи в руках свічі, а за ними диякони з кадилами, а потім пресвітери, і за ними єпископи з митрополитами, а за ними ішли ті, що несли раку. І принесли її в нову церкву, і відкрили раку, і церква наповнилась благоуханням приємних запахів, і всі, хто бачив це, прославили Бога. І митрополита жах обійняв, бо не дуже вірував у [святість покійних], і впав ниць, і просив прощення. І коли поцілували мощі Борисові, поклали їх у раку кам\'яну. Після цього взяли [останки] Гліба у раці кам\'яній, поставили її на сани і, взявшись за гужі, повезли її. І коли були уже в дверях, зупинилась рака і не пройшла. І повеліли народу кликати: &laquo;Господи, помилуй!&raquo; І повезли його, і перехоронили його 20 травня. І коли відспівали літургію, обідали брати всі разом, кожний із боярами своїми, і з любов\'ю великою. Управляв тоді Вишгородом Чудин, а церквою - Лазар. І після цього розійшлися по домівках.</p>\r\n<p>В літо 6581 [1073]. Розпалив диявол усобиці між братами цими Ярославичами. І були сварки між ними, були заодно Святослав із Всеволодом проти Ізяслава. І пішов Ізяслав із Києва, Святослав же і Всеволод увійшли в Київ 22 березня і сіли на столі в Берестовому, порушивши заповідь отчу. Святослав же поклав початок вигнання брата, бажаючи більшої влади, адже Всеволода він звабив, намовляючи: &laquo;Ізяслав змовився із Всеславом, помишляючи на нас, і, якщо його не випередимо, прожене нас&raquo;. І так згострив Всеволода на Ізяслава. Ізяслав же пішов у Ляхи із жінкою і з багатством великим, покладаючись на яке, говорив: &laquo;Цим я знайду воїв&raquo;. Все це у нього ляхи відібрали і показали дорогу від себе. А Святослав сів у Києві, прогнавши брата свого і порушивши заповідь отчу, а найбільше - Божу. Адже великий гріх порушувати заповідь батька свого: бо найперші спокусились сини Хамові на землю Сифову, а через чотириста років прийняли помсту від Бога, бо від Сифового племені суть євреї, що перебили хананейське плем\'я і повернули свій жереб і свою землю. І ще порушив заповідь батька свого Ісав і був убитий. Недобре переходити чужі межі.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Того же л<small>Ђ</small>та основана бысть церковь Печерьская Святославомъ княземь, сыномъ Ярославлимь, игуменомъ Федосьемь, епископомъ Михаиломъ, митрополиту Георгиеви тогда сущю вь Гр<small>Ђ</small>ц<small>Ђ</small>хъ, а Святославу в Кыев<small>Ђ</small> с<small>Ђ</small>дящю.</p>\r\n<p>В л<small>Ђ</small>то 6582. Федоси[й] игуменъ Печерьскый преставися. Скажемь бо о усьпеніи его мало. Федосий бо обычай имяше, приходящю бо постьному времени, в нед<small>Ђ</small>лю Масленую вечеръ бо, по обычаю, целовавъ братью и поучивъ ихъ, како проводити постьное время вь молитвахъ нощьныхъ и дневныхъ, и блюстися отъ помыслъ скверныхъ, и отъ б<small>Ђ</small>совьскаго нас<small>Ђ</small>янья. &laquo;Б<small>Ђ</small>си бо, рече, вс<small>Ђ</small>вають черноризьцемъ похот<small>Ђ</small>ния лукава, вжагающа ему помыслы, т<small>Ђ</small>мьже врежаеми бывають имъ молитвы; да приходящая таковыя мысли вьзбраняти и знамениемъ крестнымь, глаголюще сице: Господи Ісусе Христе Боже нашь, помилуй насъ, аминъ; и к симь вьздержание им<small>Ђ</small>ти отъ многаго брашна; вь <small>Ђ</small>деньи бо мьноз<small>Ђ</small> и вь питьи безмерн<small>Ђ</small> вьзрастають помысли лукавии, помысломъ же вьзьрастьшимь стваряеться гр<small>Ђ</small>хъ. Т<small>Ђ</small>мже, рече, &laquo;противитися б<small>Ђ</small>совьскому д<small>Ђ</small>йству и пронырьству ихъ, и блюстися отъ л<small>Ђ</small>ности и отъ многаго сна, и бодру быти на п<small>Ђ</small>ние церковьное, и на предания отецьская, и на почитания книжная; паче же им<small>Ђ</small>ти во уст<small>Ђ</small>хъ псаломъ Давидовъ подобаеть черноризьцемь; симь прогонити б<small>Ђ</small>совьское уныние; паче же всего им<small>Ђ</small>ти любовь в себе к м<small>Ђ</small>ншимь и кь стар<small>Ђ</small>йшимъ покорение и послушание, кь стар<small>Ђ</small>йшимь же и кь м<small>Ђ</small>ншимь любовь и наказание, образъ бывати собою вьздержаниемь и бд<small>Ђ</small>ньемь, и хожениемь, смирениемь; и тако наказывати и м<small>Ђ</small>ньшая, ут<small>Ђ</small>шивати я, и тако проводити постъ&raquo;. Глаголашеть бо сице: &laquo;яко Богъ далъ ест,ь намъ сию 40 дний на очищение души; се бо есть десятина отъ л<small>Ђ</small>та даема Богу: дний бо есть отъ года до года 300 и 60 и 5 дний, и отъ сихъ дний десятый день вьздаяти Богу десятину, еже есть постъ си четыр<small>Ђ</small>десядный, в ня же дни очистившеся душа, празнуеть св<small>Ђ</small>тло Вьскремение Господне, веселящеся о Боз<small>Ђ</small>. Постъное бо время очищаеть убо умъ челов<small>Ђ</small>ку, Пощение бо исперва проображено бысть: Адаму не вкусити отъ древа единого; пости бо ся Моис<small>Ђ</small>й дний 40, сподобь бо ся прияти законъ на гор<small>Ђ</small> Синайст<small>Ђ</small>й, н в<small>Ђ</small>д<small>Ђ</small>вь славу Божию; постомъ Самуила мати роди; постивьшеся Ниневгитян<small>Ђ</small> гн<small>Ђ</small>ва Божия избыша, постнвся Данилъ вид<small>Ђ</small>нья сподобися великаго постився Илья акы на небо взятъ бысть, и в пищю породную, постившеся трие отроцн угасиша силу огненую; постився Господь дний 40, намъ показа постное время; постомъ апостоли искорениша б<small>Ђ</small>совьское учение; постомъ явишася отци наши акы св<small>Ђ</small>тила в мир<small>Ђ</small>, и сияють и по смерти, показавше труды великыя и вьздьрьжания, яко се и великий Антоний, и Евьфимий, и Сава и прочии отци, ихъже и мы поревнуемь, братье&raquo;. Сице поучивъ братью и ц<small>Ђ</small>ловавъ вся по имени, и тако изиидяше из манастыря, возмя малъ коврижекъ; и вшедъ в пещеру и затворяше двери пещеры, и засыпаше пьрьстью, и не глаголаше никомуже; аще ли будяше нужное орудье, то оконцемь мало б<small>Ђ</small>с<small>Ђ</small>доваше, в суботу или в нед<small>Ђ</small>лю; а по иныи дни пребываше в пост<small>Ђ</small> и вь молитв<small>Ђ</small>, и вьздержашеся кр<small>Ђ</small>пко. И прихожаше в манастырь в пятокъ наканун<small>Ђ</small> Лазоревъ; в сий бо день кончаеться пость 40, начинаеться отъ перваго понед<small>Ђ</small>лника наставшии Федоров<small>Ђ</small> нед<small>Ђ</small>л<small>Ђ</small>, кончаеть же ся в пятокъ Лазоревъ; а страстная нед<small>Ђ</small>ля уставлена есть поститися страсти ради Господня. Федосьеви же пришедшю по обычаю, ц<small>Ђ</small>лова братью и празнова сь ними нед<small>Ђ</small>лю Цв<small>Ђ</small>тную, и дошедъ великаго дни Въськрешения Господня, по обычаю празновавъ св<small>Ђ</small>тло, впаде в болезнь. И разбол<small>Ђ</small>вшюся ему дний 5, посемь бывшу вечеру, и повел<small>Ђ</small> изьнести ся на дворъ, братья же вземше и на санехъ, и поставиша и прямо церкви; онъ же повел<small>Ђ</small> братью собрати всю, братья же удариша в било, и собравшеся вси. Онъ же рече имъ: &laquo;братье моя, и отци мои, и чада моя! се азъ отхожю отъ васъ, якоже яви ми Господь в постьное время, в пещер<small>Ђ</small> ми сущю, изыити отъ св<small>Ђ</small>та сего; вы же кого хощете игуменомъ поставити себ<small>Ђ</small>, да и азъ благословение подалъ быхъ ему?&raquo; Они же рекоша ему: &laquo;ты еси отець намъ вс<small>Ђ</small>мъ, да егоже изволиши самъ, то намъ будеть отець игуменъ, и послушаемь его, яко и тобе&raquo;. Отець же нашь Федосий рече: &laquo;шедше кром<small>Ђ</small> мене наречете, егоже хощете, кром<small>Ђ</small> двою брату, Николы, Игната; вь прочихъ кого хощете, отъ стар<small>Ђ</small>йшихъ даже и до меншихъ&raquo;. Они же послушавъше его, отступивше мало кь церкви, сдумавьше и послаша два брата, глаголюще сице: &laquo;егоже изволить Богъ и твоя честная молитва, егоже тоб<small>Ђ</small> любо, того нарци&raquo;. Федосий же рече имъ: &laquo;да аще отъ мене хощете игумена прияти, то азъ створю вамъ, но не по своему изволению, но по Божию строенью&raquo;; и нарече имъ Якова прозвутера. Братьи же не любо бысть, глаголюще: &laquo;яко не зд<small>Ђ</small> есть постригълъся&raquo;; б<small>Ђ</small> бо Ияковъ пришелъ сь Летьца с братомъ своимъ Павломъ. И начаша братья просити Стефа [на] деместьвяника, суща тогда ученика Федосьева, глаголюще: &laquo;яко сесь есть вьздраслъ подъ рукою твоею, и о теб<small>Ђ</small> послужилъ есть; сего нын<small>Ђ</small> вдай&raquo;. Рече же имъ Федосий: &laquo;се азъ, по Божию повелению, нареклъ б<small>Ђ</small>хъ вамъ Якова; се же вы своею волею створити хощете&raquo;. И послушавъ ихъ, и предасть имъ Стефана, да будеть имъ игуменъ; и благослови Стефана, и речи ему: &laquo;чадо! се предаю ти манастырь, блюди с опасеньемь его, якоже устроихъ и вь службахъ, то держи; преданья манастырьская и устава не изм<small>Ђ</small>няй, но твори вся по закону, по чину манастырьскому&raquo;. И посемь вземше и братья, и несоша и в к<small>Ђ</small>лью и положиша и на одр<small>Ђ</small>. И шестому дни наставило, и болну сущю велми, приде к нему Святославъ сь сыномъ своимъ Гл<small>Ђ</small>бомъ; и с<small>Ђ</small>дящима има у него, рече ему Федосий: &laquo;се отхожю св<small>Ђ</small>та сего, и се предаю ти манастырь на сблюдение, егда будеть что смятение в немь; се поручаю Стефану игуменьство, не давай его въ обиду&raquo;. И князь ц<small>Ђ</small>ловавъ его, и об<small>Ђ</small>щася пещися манастыремь, и отъиде отъ него. Семому же дни пришедшю, изнемогающю Федосьеви, и призва Стефана и братью, и нача имъ глаголати сице: &laquo;аще по моемь отшествии св<small>Ђ</small>та сего, аще буду Богу угодилъ, и приялъ мя будеть Богъ, то по моемь отшествии манастырь сь начнеть строити и прибивати в немя: то в<small>Ђ</small>жьте, яко приялъ мя есть Богъ; аще ли по моемь живот<small>Ђ</small> оскудевати начнеть манастырь, а черноризьци потребами манастырьскыми, то в<small>Ђ</small>дуще будете, яко не угодилъ буду Богу&raquo; И се ему глаголющю, плакахуся братья, глаголюще: &laquo;отче! моли за ны Господа; в<small>Ђ</small>мы бо, яко Богъ труда твоего не презр<small>Ђ</small>&raquo;. И пр<small>Ђ</small>с<small>Ђ</small>д<small>Ђ</small>ша братья у него ту нощь всю; и наставшю дни осмому, вь вторую суботу по пасц<small>Ђ</small>, вь 2 часъ дни, и предасть душю в руц<small>Ђ</small> Божии, м<small>Ђ</small>сяца мая вь 3 день, индикта вь 11 л<small>Ђ</small>то; и плакашася по немь братья. Б<small>Ђ</small> же Федосий запов<small>Ђ</small>далъ братьи положити ся в пещер<small>Ђ</small>, ид<small>Ђ</small>же показа труды многы, и рекъ сице: &laquo;в нощи похраните т<small>Ђ</small>ло мое&raquo;, якоже и створиша. Вечеру бо присп<small>Ђ</small>вшю, вся братья вземше т<small>Ђ</small>ло его, и положиша и в пещер<small>Ђ</small>, проводивьше сь п<small>Ђ</small>сми, и сь св<small>Ђ</small>щами, честьно, на хвалу Господу нашему Ісусу Христу. Стефану же предержащю манастыръ, и блаженное стадо, яже б<small>Ђ</small> совокупилъ Федосий, таки черноризьци аки св<small>Ђ</small>тила в Руськой земли сияху: ово бо бяху постьници, овии же на бд<small>Ђ</small>ние, овии же на кланяние коленьное, овии же на пощение чересъ день и чересъ два дни, овии же ядяху хл<small>Ђ</small>бъ с водою, инии же зелье варено, а друзии в любви пребывающе, м<small>Ђ</small>ншии покоряющеся стар<small>Ђ</small>йшимъ, не см<small>Ђ</small>юще пред ними глаголати, но все с покорениемь и с послушаниемь великомъ; и такоже и стар<small>Ђ</small>йшии имяху любовь к меншимъ, наказаху и, ут<small>Ђ</small>шающе, аки чада вьзлюбленая. Аще который братъ впадеть в кое любо согр<small>Ђ</small>шение, и ут<small>Ђ</small>шаху и, и епитемью единого брата разд<small>Ђ</small>ляху 3-е, или 4 за великую любовь: такова бо бяше любовь в братьи той, и вьдержание велико. Аще братъ етеръ вънъ идяше изъ манастыря, и вся братья имяху о томъ печаль велику, и посылають по нь, приводяху брата къ манастырю; и шедше вси покланяхуся игумену, и умолять игумена, и приимаху в манастырь брата с радостію. Таци бо б<small>Ђ</small>ша любовници и вьзд<small>Ђ</small>рьжници, отъ нихъ же нам<small>Ђ</small>ню н<small>Ђ</small>колико мужь чюдныхъ. Первый Д<small>Ђ</small>мьянъ прозвутерь: бяше постьникъ и въздерьжьникъ, яко разв<small>Ђ</small>е хл<small>Ђ</small>ба и воды ясти ему до смерті своей. Аще бо коли кто принесяше д<small>Ђ</small>тищь боленъ, кацимъ любо недугом<small>Ђ</small> одерьжимъ, приношаху в манастырь, или св<small>Ђ</small>ршенъ челов<small>Ђ</small>къ, кацимъ любо недугомъ [одръжимъ], прихожаше в манастырь къ блаженому Федосьеви: и повел<small>Ђ</small>ваше сему Демьяну молитву творити надъ болящимъ, и абье створяше молитву и масломъ святымъ помазаже, и абье исц<small>Ђ</small>л<small>Ђ</small>ваху приходящии к нему. Единою же ему разбол<small>Ђ</small>вшюся, кон<small>Ђ</small>ць прияти лежащю ему в болести, и приде к нему ангелъ вь образ<small>Ђ</small> Федосьев<small>Ђ</small>, даруя ему царство небесное за труды его; посемь <em>же </em>приде Федосий з братьею, и с<small>Ђ</small>доша у него, оному же изнемогающю, вьзр<small>Ђ</small>въ на игумена и рече: &laquo;не забывай, игумене еже ми еси ночесь об<small>Ђ</small>щалъ&raquo;; и разум<small>Ђ</small> Федосий великий, яко вид<small>Ђ</small>ние вид<small>Ђ</small>, и рече ему: &laquo;брате Д<small>Ђ</small>мьяне! еже ти есмь об<small>Ђ</small>щаль, то ти буди&raquo;. Онь же см<small>Ђ</small>живъ очи, и предасть духъ в руц<small>Ђ</small> Божии; игуменъ же и братья похорониша т<small>Ђ</small>ло его. Такоже и другый братъ, именемъ Ерем<small>Ђ</small>й, иже помняше крещсние зсмли Руськой. Сему даръ данъ отъ Бога: пропов<small>Ђ</small>даше [и] провид<small>Ђ</small> будущая, и аще кого видяше в помышлении, обличаше втайн<small>Ђ</small> и наказаше блюстися отъ дьявола; аще который братъ мысляше изыити из манастыря, узряше и, пришедъ к нему, и обличаше мысль его и ут<small>Ђ</small>шаше брата; и аще кому речаше, любо добро, любо зло, сбывашеться старцево слово. Б<small>Ђ</small> же и другий братъ, именемъ Матф<small>Ђ</small>й: той б<small>Ђ</small> прозорливъ. Единою ему стоящю вь церькви на м<small>Ђ</small>ст<small>Ђ</small> своемь, и вьзведе очи свои, и позр<small>Ђ</small> во братьи, иже стоять поюще по об<small>Ђ</small>има сторонама, и вид<small>Ђ</small> обьходяще б<small>Ђ</small>са, вь образ<small>Ђ</small> Ляха, в луд<small>Ђ</small>, носяща вь припол<small>Ђ</small> цв<small>Ђ</small>токъ, еже глаголеться л<small>Ђ</small>покъ. И обьходя подл<small>Ђ</small> братью, взимая из лона цв<small>Ђ</small>токъ, и в<small>Ђ</small>ржаше на кого любо: аще прилпяше кьму цв<small>Ђ</small>токъ поющихъ отъ братья, и тъ мало стоявъ и раслабевъ умомъ, вину створивъ каку любо, исходяще изь церкви и шедъ в к<small>Ђ</small>лью, и спаше, и не възвратяшеся вь церковь до отп<small>Ђ</small>тья; аще ли верже на другаго, и не прилпяше к нему цв<small>Ђ</small>токъ, стояше бо кр<small>Ђ</small>пко вь п<small>Ђ</small>ньи, дондеже отпояху утренюю, и тогда идяше в к<small>Ђ</small>лью свою; и се видя стар<small>Ђ</small>ць, пов<small>Ђ</small>да братьи своей. И пакы же сий стар<small>Ђ</small>ць виде се: по обычаю бо сему старцю отстоявшю утренюю, братьи отп<small>Ђ</small>вши заутренюю предъ зорями, идоша по к<small>Ђ</small>льямъ своимь, сий же стар<small>Ђ</small>ць исхожаше ись церкви. Идущю же ему единою, и с<small>Ђ</small>де почивая подъ биломъ, б<small>Ђ</small> бо к<small>Ђ</small>лья его подале церкви, и види се, акы толпа поиде отъ врать; и вьзведе очи свои, вид<small>Ђ</small> единаго сидяща на свиньи, а другыя текущи около его. И рече имь стар<small>Ђ</small>ць: &laquo;камо идете?&raquo; и рече б<small>Ђ</small>съ с<small>Ђ</small>дя на свиньи: &laquo;по Михаля по Толбоковича&raquo;. Стар<small>Ђ</small>ць знаменася крестнымъ знаменьемь, и приде в к<small>Ђ</small>лью; и бысть св<small>Ђ</small>тъ, и разум<small>Ђ</small> стар<small>Ђ</small>ць, и рече к<small>Ђ</small>л<small>Ђ</small>йнику: &laquo;иди, вьспроси, есть ли Михаль в кельи?&raquo; и р<small>Ђ</small>ша ему: &laquo;яко вьскочилъ есть чресъ столпъе по заутр<small>Ђ</small>ни&raquo;. И пов<small>Ђ</small>да, стар<small>Ђ</small>ць вид<small>Ђ</small>ние се игумену и всей братьи. При семь бо старьци Федосий преставилъся, и бысть Стефанъ игуменъ, и по Стефани Никонъ, и сему старцю и еще сущю. Единою ему стоящю на заутрении, възведе очи свои, хотя видити игумена Никона: и вид<small>Ђ</small> осла стояща на игумени м<small>Ђ</small>ст<small>Ђ</small>, и разум<small>Ђ</small>, яко не вьсталъ есть игуменъ. Такоже ина многа вид<small>Ђ</small>ния провид<small>Ђ</small>ше стар<small>Ђ</small>ць сьй, почи въ старости добр<small>Ђ</small> в манастыр<small>Ђ</small> семь. Яко се бысть другый черноризець, именемь Исакий: яко еще сущю в мирьскомъ житьи, богату сущю ему, б<small>Ђ</small> бо купець родомъ Торопчанинъ, и помысли быти мнихомъ, и раздая им<small>Ђ</small>ние свое тр<small>Ђ</small>бующимъ и по манастыремь, иде кь великому Антонию в пещеру, моляшеся ему, дабы створилъ черноризьцемь; и приятъ и Антоний, и возложи на нь порты черн<small>Ђ</small>цьки<small>Ђ</small>, и нарече имя ему Исакий, б<small>Ђ</small> бо имя ему мирьское Чернь. Сий же Исакий вьсприя житье крепко: обл<small>Ђ</small>чеся въ власяницю, и повел<small>Ђ</small> купити соб<small>Ђ</small> козелъ, и одерти м<small>Ђ</small>шькомь козелъ и возьвлече и на власяницю, и осъше около его кожа сыра; и затворися в пещер<small>Ђ</small>, вь единой улици, вь к<small>Ђ</small>льиц<small>Ђ</small> мал<small>Ђ</small>, яко 4 лакотъ, и ту моляше Бога беспрестани день и нощь со слезами; 6<small>Ђ</small> же ядение его проскура одина, и таже чресъ день, и воды в м<small>Ђ</small>ру пьяше. Приношаше же ему великий Антоний, и подаваше оконьцемь ему, яко ся вм<small>Ђ</small>стяше рука: и тако приимаше пищю. И того створи л<small>Ђ</small>тъ 7: на св<small>Ђ</small>тъ не вылазя, ни на ребрехъ лежа, но с<small>Ђ</small>дя мало приимаше сна. И единою, по обычаю, наставшю вечерю, и поча кланятися, поя псалмы, оли до полунощи; и яко струдяшеться, с<small>Ђ</small>дяше же на с<small>Ђ</small>дал<small>Ђ</small> своемь. И единою же ему с<small>Ђ</small>дящю, по обычаю, и св<small>Ђ</small>щю угасившю, и внезапу св<small>Ђ</small>тъ восия, яко солнце вь пещер<small>Ђ</small>, яко зракомъ внимая челов<small>Ђ</small>ку; и поидоста дв<small>Ђ</small> уноши к нему прекрасьна, и блистася лице има яко и солнце, и глаголаста к нему: &laquo;Исакье! в<small>Ђ</small> есв<small>Ђ</small> ангела, а се идеть к тоб<small>Ђ</small> Христосъ съ ангелы&raquo;. И вьставъ Исакий вид<small>Ђ</small> толпу, и лица ихъ паче солнца, и еднъ посред<small>Ђ</small> ихъ, и сьяху отъ лица его паче всихъ; и глаголаста ему: &laquo;Исакье! то ти Христосъ; падъ поклонися ему&raquo;. Онь же не разум<small>Ђ</small> б<small>Ђ</small>совьскаго д<small>Ђ</small>йства, ни памяти перекреститися; выступя поклонися, акы Христу, б<small>Ђ</small>совьскому д<small>Ђ</small>йству. Б<small>Ђ</small>си же кликнуша и рекоша: &laquo;нашь еси уже, Исакье&raquo;; и вьведоша и в к<small>Ђ</small>льицю, и посадиша и, и начаша садитися около его, [и бысть] полна келья [и] улица Печерьская. И рече единъ отъ б<small>Ђ</small>совъ, глаголемый Христосъ: &laquo;возмите соп<small>Ђ</small>ли, и бубны, и гусли, и ударяйте ать ны Исакье сьпляшеть&raquo;. И удариша в соп<small>Ђ</small>ли, и вь гусли, и вь бубни, и начаша имъ играти; и утомивше и, оставиша иле жива сущи, и отъидоша, поругавшеся ему. Заутра же бывши св<small>Ђ</small>ту, и прісп<small>Ђ</small>вшю вкушению хл<small>Ђ</small>ба, и приде Антоний кь [о] концю по обычаю, и глагола: &laquo;благослови, отче Исакье!&raquo; и не бысть гласа, ни послушания; и многажды глаголя Аньтоний, и не бысть отв<small>Ђ</small>та; и глагола Антоний: &laquo;се уже яко преставилъся есть&raquo;. И посла в манастырь по Федосья и по братью. И откопаше, гд<small>Ђ</small> б<small>Ђ</small> загражено устье, и пришедше и взяша и, мняще и мертваго, и вынесыие положиша и предъ пещерою; и узр<small>Ђ</small>ша яко живъ есть, и рече игуменъ Федосий: &laquo;яко се имать отъ б<small>Ђ</small>совьскаго д<small>Ђ</small>йства&raquo;; и положиша и на одр<small>Ђ</small>; и служаще около его Антоний.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Того ж літа [1073] була закладена церква Печерська князем Святославом, сином Ярослава, ігуменом Федосієм, єпископом Михайлом. Митрополит Георгій був тоді в Греках, а в Києві сидів Святослав.</p>\r\n<p>В літо 6582 [1074] Федосій, ігумен Печерський, помер Розкажемо коротко про його успіння. Федосій звичай мав. З настанням посту у Масляну неділю ввечері, за звичаєм, вітати братію і повчати її, як проводити піст у молитвах нічних і денних, як берегтися від помислів гріховних і від бісівського навіювання. &laquo;Адже біси,- говорив,- навіюють чорноризцям похотіння лукаві, розпалюючи їхні помисли і тим, буває, шкодять їхнім молитвам. Треба оборонятися, коли приходять такі думки, хресним знаменням, промовляючи таке: &laquo;Господи, Ісусе Христе, Боже наш, помилуй нас, амінь&raquo;. А до цього ще треба стримувати себе від надмірної їди, бо в надмірній їді і в питті безмірнім виростають помисли лукавії, а від зрослих помислів твориться гріх&raquo;. А через те, говорив він, &laquo;опирайтеся бісівському діянню і пронозливості їхній, і бережіться лінощів і довгого сну, і будьте бадьорі на співання церковне, і на заповіти батьківські, і на читання книжне. А більш усього подобає чорноризцям мати на вустах псалми Давидові, ними проганяти бісівську безнадію. А над усе берегти в собі любов до менших, а до старших - покору і послух, а [старшим] до менших - любов і настанову, бути зразком здержливості, і невсипущості, і трудолюбивості, і покірливості і цьому навчати менших і втішати їх і так проводити піст&raquo;. І говорив таке: &laquo;Бог дав нам оці сорок днів для очищення душі, а це тільки десятина року, давана Богу, днів-бо від року до року триста і шістдесять і п\'ять, і від цих днів віддавать десятий день як десятину Богу - це і є сорокаденний піст, і в ці дні, очистившись, душа празнує світле Воскресіння Господнє, радіючи Богу. Бо врем\'я посту очищає ум чоловіку. Постування-бо від правіку мало свій прообраз: Адам не вкушав [плодів] від дерева єдиного, постився Мойсей сорок днів, після чого сподобився прийняти закон на горі Сінайській і побачив славу Божу. У піст народила Самуїла мати, постом ніневгитяни гніву Божого позбулися, під час посту Даниїл сподобився видіння великого, постився Ілля аж доки на небо був узятий для їжі райської; постуючи, три отроки погасили силу огненну; постився Господь сорок днів, показавши нам врем\'я посту, постом апостоли викоренили бісівське вчення, з посту постали наші отці [церкави] як світильники світу і сіяють і після смерті, з\'явивши зразки трудів великих і повстримності, як ото і великий Антоній, і Євфимій, і Сава, і інші отці, їх же і ми ревно наслідуємо, братія&raquo;. От так повчивши братію і попрощавшись із усіма по імені, вийшов із монастиря, взявши кілька коврижок, і ввійшов у печеру, і закрив двері печери, 1 засипав їх землею, і не розмовляв ні з ким. Коли була до нього необхідна справа, то розмовляв через маленьке віконце у суботу чи неділю, а в останні дні перебував у пості і в молитві, дотримуючись строгої повстримності. І прийшов у монастир у п\'ятницю напередодні Лазаревого дня, бо в цей день закінчується сорокаденний піст, що починається від першого понеділка Федорової неділі, закінчується у Лазареву п\'ятницю. А у страсну неділю встановлено поститися ради страждань Господніх. Коли ж Федосій, як звичайно, прийшов, вітав братію і святкував із ними Цвітну [вербну] неділю, і дочекався великого дня Воскресіння Господнього, і, як завжди, відсвяткував радісно - і захворів, ї прохворів п\'ять днів, після цього ввечері звелів винести його на подвір\'я, братія ж взяла його на сани і поставила навпроти церкви. Він же повелів скликати всю братію, братія ж ударила в било, і зібралися всі. Федосій же сказав їм: &laquo;Братія моя, і отці мої, і діти мої! Оце я відходжу від вас, бо з\'явив мені Господь під час посту в печері моїй відійти з цього світу. Ви ж кого хочете поставте собі за ігумена, я б дав йому благословення своє?&raquo; Вони ж сказали йому: &laquo;Ти отець усім нам, і кого побажаеш\'сам, той і буде нам отець ігумен, і слухатимемо його, як і тебе&raquo;. Отець же наш Федосій рече: &laquo;Ідіть і домовтеся без мене, кого хочете, окрім двох із братії - Николи і Гната, із інших, кого хочете, від найстарших і до наймолодших&raquo;. Вони ж послухалися його, відійшли трохи від церкви, порадились і послали двох братів сказати так: &laquo;Кого хоче Бог і твоя чесна молитва, хто тобі любий, того назви&raquo;. Федосій же рече їм: &laquo;Тож коли від мене бажаєте прийняти ігумена, то я дам вам, але не за своїм бажанням, а за Божим урядженням&raquo;. І назвав їм Якова пресвітера. Братії це не любо було, говорили &laquo;не тут був пострижений&raquo;, бо Яків прийшов із Летьця разом із братом своїм Павлом. І почала братія просити Стефана деместв\'яникового, тодішнього учня Федосієвого, кажучи: &laquo;Цей возріс під рукою твоєю, і в тебе послужив, цього нині дай&raquo;. Федосій же їм рече: &laquo;Я вам з Божого повеління назначив був Якова, а ви по своїй волі хочете зробить&raquo;. Одначе послухав їх і дав їм Стефана, щоб був їхнім ігуменом, і благословив його, і сказав йому: &laquo;Дитя моє! От передаю тобі монастир, бережно його охороняй і, як я влаштував його в службах, так і тримай. Заповітів монастирських і уставу не міняй, а роби все по закону і чину монастирському&raquo;. І після цього взяли його браття, і віднесли в келію, і поклали його на одрі. І коли настав шостий день і було йому дуже погано, прийшов до нього Святослав із сином своїм Глібом. І коли вони сіли біля нього, сказав йому Федосій: &laquo;От і відходжу з цього світу і оце доручаю тобі монастир на догляд, коли трапиться якесь сум\'яття у ньому. Ігуменство поручаю Стефану, не давай його в обиду&raquo;. І князь попрощався з ним і пообіцяв пектися про монастир, і пішов від нього. На сьомий день знемагаючий Федосій покликав Стефана і братію і став їм говорити таке: &laquo;Якщо після мого відходу з цього світу буду угодний Богові і прийме мене Бог, то після мого відходу монастир почне розбудовуватися і багатіти: ото знайте, що прийняв мене Бог. Якщо ж після моєї смерті почне монастир занепадати, а чорноризці не старатися потребами монастирськими, то будете знать, що не вгодив я Богові&raquo;. І коли він це говорив, плакала братія, кажучи: &laquo;Отче! Моли за нас Господа, бо знаємо, що Бог труда твого не зневажить&raquo;. І просиділа братія біля нього всю ту ніч, а наступного, восьмого дня, у другу суботу після паски, о другій годині дня віддав він душу в руки Божі місяця травня у третій день, індикта в одинадцяте літо. І плакала по ньому братія. Федосій же заповів братії покласти його в печері, де з\'явив численні труди, і казав таке: &laquo;Уночі похороніть тіло моє&raquo;. Так вони і зробили. Коли наступив вечір, вся братія взяла тіло його і поклала в печері, провівши з піснеспівами і зі свічами, чесно, на хвалу Господу нашому, Ісусу Христу.</p>\r\n<p>Коли ж Стефан управляв монастирем і блаженним стадом, що його зібрав Федосій, таки чорноризці подібно світильникам в Руській Землі сіяли: одні-бо були постники, другі ж у старанні, інші - у колінопоклонінні, ще інші постилися через день і через два дні, другі ж їли хліб з водою, інші - варені овочі, а всі в любові пребували: молодші корилися старшим, не сміли перед ними говорити, а все з покірністю і з послухом великим. А також і старшії мали любов до молодших, навчали їх, утішали, як дітей возлюблених. А коли хто-небудь із братів впадав у яке-небудь прогрішіння, його втішали і єпітимію, накладену на одного брата, розділяли троє і четверо в знак великої любові; така була любов у тій братії і воздержаніє велике. Якщо один із братії раптово залишав монастир, вся братія була тим вельми опечалена, посилали за ним, приводили брата в монастир і йшли всі з поклоном до ігумена, і вмоляли його, і приймали [знову] брата у монастир із радістю. От такі були дружні і стримані, із них назову кілька мужів дивовижних.</p>\r\n<p>Перший - Дем\'ян Пресвітер: був постник і воздержник, що, окрім хліба і води, нічого більше не їв до смерті своєї. Якщо ж коли хтось приносив хворе дитя, якимось недугом пойняте, в монастир чи дорослий чоловік, яким-небудь недугом пойнятий, приходив у монастир до блаженного Федосія,- тоді наказував він цьому Дем\'янові творити молитву над хворим. І як тільки помолиться і єлеєм святим помаже,- негайно ізцілялися ті, що приходили до нього. Коли ж одного разу Дем\'ян розхворівся і лежав у болісті, кінця чекаючи, прийшов до нього ангел в образі Федосія, даруючи йому царство небесне за труди його. Потім уже прийшов Федосій з братією і сиділи у нього, а він, знемагаючи, подивився на ігумена і рече: &laquo;Не забувай, ігумене, що ти мені вночі обіцяв&raquo;. І зрозумів Федосій великий, яке видіння бачив, і рече йому: &laquo;Брате Дем\'яне! Що я тобі обіцяв, те тобі буде&raquo;. Він же закрив очі і віддав дух в руки Божії. Ігумен же і братія похоронили тіло його.</p>\r\n<p>Також і другий брат був, на ймения Єремій, що пам\'ятав хрещення землі Руської. Йому був даний дар від Бога: провіщати і передбачати майбутнє, і коли бачив когось в задумі, виказував [його задуми] віч-на-віч і наказував берегтися диявола. Коли якийсь брат намислював піти із монастиря і [Єремій] бачив його, то приходив до нього і виявляв [задум його] і втішав брата. І якщо кому говорив, чи добре, чи погане, збувалося старцеве слово.</p>\r\n<p>Був і другий брат, ім\'ям Матвій: цей був прозірливий. Одного разу, коли він стояв у церкві на місці своєму і підняв очі свої, обвів ними братію, що стояла, співаючи, з обох боків, і побачив, що братію обходить біс у образі ляха, в плащі, носив у приполі квітку, що називається ліпок. І обходячи побіля братію, біс виймав із-за пазухи квітку і кидав на кого-небудь. Якщо квітка прилипала до когонебудь із братів, що співали, той, трохи постоявши із розслабленим розумом, шукав яку-небудь причину, виходив із церкви, ішов у келію, і спав, і не вертався в церкву до кінця служби. Коли ж кидав на другого, і квітка до нього не прилипала, той стояв міцно у співі, доки відспівають утреню, і тоді йшов у келію свою. І це бачив старець, повідав братії своїй. Іще цей старець бачив таке: як завжди, він відстояв заутреню, братія, відспівавши заутреню до світанку, пішла по своїх келіях, а цей старець [останнім] виходив із церкви. Ішов він один і сів спочити під билом, бо його келія була далеченько від церкви, і от бачить, ніби натовп іде від воріт. Він підвів очі свої і побачив одного [чоловіка] верхи на свині, а другі пропливали поруч із ним. І рече їм старець: &laquo;Куди йдете?&raquo; І рече біс, сидячи на свині: &laquo;По Михаля по Толбоковича&raquo;. Старець осінився хресним знаменням і пішов у келію. І коли розвиднилось, і зрозумів старець [в чому справа], і рече келейникові: &laquo;Піди, запитай, чи в келії Михаль?&raquo; І, сказали йому: &laquo;Щойно, після заутрені, перескочив через огорожу&raquo;. І розповів старець про це видіння ігуменові і всій братії. При цьому старці Федосій помер, і був Стефан ігуменом, а після Стефана Никін, а цей старець все ще жив. Стояв він якось на заутрені, піднімає очі свої, щоб побачити ігумена Никона, і бачить: стоїть осел на ігуменовому місці, і зрозумів, що ігумен ще не став на своє місце. Також багато інших багато видінь провидів цей старець і спочив у глибокій старості в цьому монастирі.</p>\r\n<p>А був ще інший чорноризець, іменем Ісакій. Коли ще був у світському житті, багатий був, купець, походив із Торопця. І намислив стати монахом, і роздав майно своє тим, хто його потребував, і монастирям, і пішов до великого Антонія в печеру з молитовною просьбою, аби постриг його в чорноризці. І прийняв його Антоній, і одягнув його в одежу чернецьку, і дав ім\'я йому Ісакій, а світське ім\'я мав Чернь. Цей Ісакій прийняв життя суворе, облачився у власяницю і повелів купити йому козла, обідрав шкуру мішком і натягнув її на власяницю, і висохла на ньому шкура сира. І зачинився в печері, в одному із проходів, у келії маленькій на чотири лікті, і там молився Богу безперестанно вдень і вночі зі сльозами. З\'їдав він по одній проскурі через день і воду в міру пив. Приносив же йому великий Антоній і подавав через віконце, в якому могла вміститися лише рука; і так приймав [від Антонія] їжу. І жив він так сім років, не вилазячи на світло, навіть на ребра не лягав, а лише сидів, дуже мало спав. І одного разу, за звичаєм, з настанням вечора він почав кланятися, співаючи псалми майже до півночі. І коли натрудився, сидів на сидінні своєму. І одного разу, коли він, як звичайно, сидів і загасив свічу, йому несподівано світ возсіяв, наче сонце в печері, осліплюючи зір людський. І підійшли до нього двоє юнаків прекрасних із сяючими, як сонце, лицями, і сказали йому: &laquo;Ісакію, ми - ангели, а там йде до тебе Христос із ангелами&raquo;. І встав Ісакій, побачивши натовп і лиця їхні, кращі за сонце, а лице одного з них сяяло більше за всіх. І сказали йому: &laquo;Ісакію! Оце тобі Христос, впади, поклонися йому&raquo;. Він же не зрозумів, що то бісівське дійство, і забув перехреститися, встав і поклонився, як Христові, бісівському дійству. Біси ж загорлали і рекли: &laquo;Ти уже наш, Ісакію!&raquo; І ввели його в келійку, і посадили його, і почали самі розсідатися побіля нього, і була повна келія, і весь прохід печерний. І рече один із бісів, якого називали Христос: &laquo;Візьміть сопілки, і бубни, і гуслі, і грайте, хай нам Ісакій затанцює&raquo;. І заграли біси на сопілках, на гуслях, на бубнах, і стали грати Ісакію. І, стомивши його, залишили ледве живого і відійшли, насміявшися над ним.</p>\r\n<p>На другий день, тільки-но розвиднилося і настав час вкушати хліб, прийшов до віконця Антоній, як завжди, і сказав: &laquo;Благослови, отче Ісакію!&raquo; І не було ні слуху, ні духу. І многократно кликав Антоній, і не було відповіді. І сказав Антоній: &laquo;От він уже й помер&raquo;. І послав у монастир за Федосієм і братією. І відкопали вони засипаний вхід, ввійшли і взяли Ісакія, вважаючи, що він мертвий, винесли і поклали його перед печерою. І побачили, що він живий. І сказав ігумен Федосій: &laquo;Це трапилося через бісівські дії&raquo;. І поклали його на одрі, і став доглядати його Антоній.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В то же время приключися Изяславу прити из Ляховъ, и нача гн<small>Ђ</small>ватися Изяславъ на Антония изо Всеслава; и приславъ Святославъ, нощью поя Антония к Чернигову. Антоний же пришедъ кь Чернигову, и вьзлюби Болъдину гору, и ископавъ пещеру, и ту вселися; и есть манастырь свято<small>Ђ</small> Богородиц<small>Ђ</small>, на Болдинахъ горахъ, и до сихъ дний. Федосий же увида, яко Антоний шелъ къ Чернигову, и шедъ с братьею вьзя Исакья, и принесе кь соб<small>Ђ</small> в к<small>Ђ</small>лыо и служаше около его; б<small>Ђ</small> бо раслабленъ т<small>Ђ</small>ломъ и умомъ, яко немощи ему обратитися на другую страну, ни вьстати, и ни с<small>Ђ</small>дити, но лежа на едвдой стран<small>Ђ</small>, и подъ ся поливаше многажды, и червье кыняхуся под;ъ бедру ему с мочения. Федосий же самъ своима рукама омываше и сьтряпашеть и; за 2 л<small>Ђ</small>тъ створи се около его. Се же бысть чюдно и дивно: яко за дв<small>Ђ</small> л<small>Ђ</small>тъ лежа сий, ни хл<small>Ђ</small>ба вкуси ни воды, ни отъ какаго брашна, ни отъ овоща, ни языкомъ проглагола, но н<small>Ђ</small>мъ и глухъ лежа за 2 л<small>Ђ</small>тъ. Феодосий моляшеть Бога за нь, и молитву творяшеть над ним нощь и день, дондеже на 3-ее л<small>Ђ</small>то проглаголавъ, и слыша, и на ногы нача вьставати акы младен<small>Ђ</small>ць, и нача ходити. И не брежаше кь церкви ити, но нужею привлечахуть его кь церкви и тако помалу научиша и; и посемь научися и на тряпезницю ходити, и посажеше и кром* братья, и положаху пред нимь хл<small>Ђ</small>бъ, и не взимаше его, олны вложити будяше в руц<small>Ђ</small> ему. Федосий же рече: &laquo;положите хл<small>Ђ</small>бъ пред нимь и не вкладайте в руц<small>Ђ</small> ему, ать самъ ясть&raquo;, и небреже за нед<small>Ђ</small>лю ясти, и помалу оглядавься кушавше хл<small>Ђ</small>ба; и тако научися <small>Ђ</small>сті; и тако избави и Феодосий отъ козни дьявола и отъ прел<small>Ђ</small>сти. Исакий же вьсприя дерьзновение и вьздержание жестоко. Федосью же преставившюся, и Стефану в него м<small>Ђ</small>сто бывшю, Исакий же рече: &laquo;се уже прельстилъ мя еси, дьяволе, с<small>Ђ</small>дяща на единомъ м<small>Ђ</small>ст<small>Ђ</small> а уже не имамъ затворитися в пещер<small>Ђ</small>, но имамъ тя поб<small>Ђ</small>дити ходя в манастыр<small>Ђ</small>&raquo;. И облечеся в власяницю, и на власяницю свиту вотоляну, и нача уродьство творити; и помагати нача поваромъ и варити на братью. И на заутренюю ходя преже всихъ, и стояще кр<small>Ђ</small>пко и недвижно. Егда же присп<small>Ђ</small>яше зима и мрази лютии, и сьтояше вь прабошняхъ вь черевьихъ и вь протоптаныхъ, яко прим<small>Ђ</small>рьзняше нози его кь камени, и не двигняше ногами, дондеже отпояху заутренюю. И по завьтрении идяше в поварницю, и приготоваше огнь, и воду, и дрова, и приходяху прочии повари отъ братья. Единъ же поваръ такоже 6<small>Ђ</small> именемь Исакий, и рече посмихаяся Исакьи: &laquo;оно с<small>Ђ</small>дить вранъ черьный, иди, ими его&raquo;, онъ же поклонився ему до земли, и шедъ я врана и принесе ему предо всими повары, и ужасошася, и пов<small>Ђ</small>даша игумену и братви, и начаша и братья чтити. Онъ же, не хотя славы челов<small>Ђ</small>ческыя, и нача уродьствовати, и пакостити нача ово игумену, ово братьи, ово мирьскымь челов<small>Ђ</small>комъ, друзии же дары ему даяху; и поча по миру ходити, такоже уродомъ ся творя. И вселися в пещеру, в нейже преже былъ, уже бо б<small>Ђ</small> Аньтоний преставилъся; и совокупи соб<small>Ђ</small> уныхъ, и вьскладаше порты черн<small>Ђ</small>цькыя, да ово отъ игумена Никона раны приимаше, ово ли отъ родитель д<small>Ђ</small>тьскыхъ, сесь же то все терпяше, и подъимаше раны и наготу, и студень, день и нощь Вь едину бо нощь вьжегъ пещь во истопц<small>Ђ</small> пещеры: и яко разгор<small>Ђ</small>ся пещь, б<small>Ђ</small> бо утла, и нача пылати пламень утлизнами, оному же н<small>Ђ</small>чимь заложити, и вьступле на пламень ногама босыма, дондеже изгор<small>Ђ</small> пещь, и слезе. Ина много пов<small>Ђ</small>даху о немь, а другому и самовидци быхомъ. И тако взя поб<small>Ђ</small>ду на б<small>Ђ</small>совьскыя силы, яко и мухъ ни во что же имяше устрашения ихъ, и мечтания ихъ, глаголашеть бо к нимъ: &laquo;аще бо мя б<small>Ђ</small>сте первое прельстил<small>Ђ</small>, понеже не в<small>Ђ</small>дахъ козний вашихъ и лукавьства; нын<small>Ђ</small> же имамъ Господа Ісуса Христа Бога нашего и молитву отца нашего Федосья, над<small>Ђ</small>юся на Христа, имамъ поб<small>Ђ</small>дити васъ&raquo; И многажды б<small>Ђ</small>си пакости д<small>Ђ</small>аху, и глаголаху ему &laquo;и нашь еси, поклонилъся еси нашему старійшини и намъ&raquo;, онъ же глагола имъ: &laquo;вашь стар<small>Ђ</small>йшина есть Антихрьсть, а вы б<small>Ђ</small>си есте&raquo;, и перекр<small>Ђ</small>стися, и тако ищезняху. Овогда же ли пакы в нощи прихожаху к нему, и страхъ ему творяще ово вь мечт<small>Ђ</small>, яко се многъ народъ с мотыками и с лыскари, глаголюще: &laquo;раскопаемы, пещеру сию и сего зде загребемь&raquo;; инии же глаголаху &laquo;б<small>Ђ</small>жи, Исакье, хотять тя загрести&raquo;, онъ же глаголаше к нимъ: аще бысте человец<small>Ђ</small> был<small>Ђ</small>, то вь день бысте ходили, а вы есте тма, во тм<small>Ђ</small> ходите&raquo;, и знаменася крестнымь знамениемь, они же ищезняху. А другойчи страшахуть и во образ<small>Ђ</small> медв<small>Ђ</small>жьи, овогда же лютомь зв<small>Ђ</small>ремь, овогда же воломь, ово ли змия ползаху к нему, ово ли жабы, и мыши и всякъ гадъ.</p>\r\n<p>И не возмогоша ему ничьтоже створити, и рекоша ему: &laquo;Исакий! поб<small>Ђ</small>дилъ ны еси&raquo;. Онъ же рече: &laquo;якоже и вы перв<small>Ђ</small>е мене поб<small>Ђ</small>дили есте, вь образ<small>Ђ</small> Ісусъ Христов<small>Ђ</small> и вь ангельскомъ, недостойн<small>Ђ</small> суще того вид<small>Ђ</small>ния; то первое являстеся вь образ<small>Ђ</small> зв<small>Ђ</small>риномъ и скотьемь, змиями, и гадомь, аци же и сами бысте, скв<small>Ђ</small>рни, зли вь вид<small>Ђ</small>ньи&raquo;; и абье погыбоша б<small>Ђ</small>си отъ него, и оттол<small>Ђ</small> не бысть ему накости отъ б<small>Ђ</small>совъ. Якоже самъ пов<small>Ђ</small>даше: &laquo;яко се бысть ми за три л<small>Ђ</small>та брань си&raquo;. И потомь нача кр<small>Ђ</small>пл<small>Ђ</small>е жити и вьздержание им<small>Ђ</small>ти, пощенье и бд<small>Ђ</small>ние; и тако живущю ему, сконча житье свое. И разбол<small>Ђ</small>ся в пещер<small>Ђ</small>, и несоша и болна в манастырь, и до осмаго дни скончася о Господ<small>Ђ</small>; игуменъ же Иванъ и братья спрятавше т<small>Ђ</small>ло его, и погребоша и. Таци же б<small>Ђ</small>ша чернорисци Федосьева манастыря, иже сияють и по смерти яко св<small>Ђ</small>тила, и молять Бога за зд<small>Ђ</small> сущюю братью, и за приносящия в манастырь, и за мирьскую братью, вь немже и нын<small>Ђ</small> доброд<small>Ђ</small>тельно житье живуть, и обьще вкуп<small>Ђ</small>, вь п<small>Ђ</small>ньихъ и вь молитвахъ и в послушаньихъ, на славу Богу всемогущому, и Федосьевами молитвами сблюдаеми, ему же слава вь в<small>Ђ</small>кы, аминъ.</p>\r\n<p>В л<small>Ђ</small>то 6583. Почата бысть церкви Печерьская надъ основаньемь Стефаномъ игуменомъ: изъ основанья бо Федосий поча, а на основаньи Стефанъ поча; и кончана бысть на третье л<small>Ђ</small>то, м<small>Ђ</small>сяца іюля въ 1 день.</p>\r\n<p>В се же л<small>Ђ</small>то придоша послы из Немець къ Святославу; Святославъ же величашеся, показа имъ богатьство свое; они же видивьше бещисленое множество злата, и сребра, и паволокъ, р<small>Ђ</small>ша: &laquo;се ни во что же есть, се бо лежить мертво; сегосуть см<small>Ђ</small>тье лучьше, мужи бо доищуть и болша сего&raquo;. Сице ся похвали Езекий, царь Іюд<small>Ђ</small>йский, к посломъ царя Асурийска, егоже вся взята быша у Вавилоня; тако и по сего смерти все им<small>Ђ</small>нье расъсыпашася разднь.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>У той час приключилося Ізяславу повернутися із Ляхів, і почав гніватися Ізяслав на Антонія через Всеслава. І прислав Святослав, і забрав уночі Антонія до Чернігова. Коли Антоній прибув до Чернігова, то возлюбив Болдину гору, і викопав печеру, і поселився там. І стоїть монастир святої Богородиці на Болдиних горах і до наших днів. Федосій же довідався, що Антоній пішов у Чернігів, і пішов із братією, і взяв Ісакія, і переніс до себе в келію, і доглядав його, бо був він настільки немощний тілом і розумом, що не міг ні перекинуться з боку на бік, ні встати, ні сісти, а лежав на одному боці і справляв нужду під себе, так що черви кишіли під ребрами в нього від нечистот. Федосій же сам своїми руками обмивав і перевдягав його і робив це біля нього протягом двох літ. То було дивне чудо, що за два літа лежання він ані хліба не вкусив, ані води, ані будьякої їди, ні овочів, ані язиком не поворухнув, а німий і глухий лежав два літа. Федосій молив бога за нього, і молився над ним ніч і день, аж доки на третє літо заговорив, і почув, і на ноги почав зводитися, як маля, і почав ходити. Але не хотів іти до церкви, а силою приводили його до церкви, і так потроху привчили його, а потім навчився і в трапезную ходить, а саджали його окремо від братії, і клали перед ним хліб, і не брав його, хіба коли вкладали йому в руки. Федосій же сказав: &laquo;Покладіть хліб перед ним і не вкладайте в руки йому, хай сам їсть&raquo;. І не хотів цілий тиждень їсти, і потроху, оглядаючись, відкушував хліба, і так навчився їсти, і так Федосій звільнив його від підступів і спокуси диявола. Ісакій же [знову] взявся завзято до жорстокого воздержанія. Коли ж помер Федосій і на його місце став Стефан, Ісакій сказав: &laquo;От тобі вже вдалося обдурити мене, дияволе, коли я сидів на одному місці, а тепер я вже не зачинюся в печері, але маю перемогти тебе, ходячи в монастирі&raquo;. І одягнув власяницю, а на власяницю - свиту вовняну, і почав юродствувати, і почав помагати поварам і варити на братію. І на заутреню приходив раніше за всіх, і стояв твердо і нерушно. Коли ж надходила зима і морози люті, стояв у шкарбанах дірявих і протоптаних, що й ноги його примерзали до каменя, а він ані ногою не поворухне, доки не відспівають заутреню. А після заутрені ішов у поварницю і приготовляв огонь, і воду, і дрова, доки прийдуть інші повари від братії. Один із поварів також був на ім\'я Ісакій і сказав якось, посміхаючись, Ісакію: &laquo;Оно сидить ворон чорний, піди, візьми його&raquo;. Він же поклонився йому до землі, і пішов, узяв ворона і приніс йому на очах у всіх поварів, і ті прийшли в жах, і розповіли ігуменові й братії. І почала братія шанувати його. Він же, не бажаючи слави людської, почав юродствувать, і почав капості робить то ігуменові, то братії, то мирянам, а деякі дари йому давали. І почав він по світі ходить, продовжуючи юродствувати. І поселився він у печеру, де раніше жив,- Антоній на той час уже помер,- і збирав до себе юних, і покладав на них облачення чернецькі, за що від ігумена Никона мав побої або й від батьків тих дітей. Він же все те терпів, приймав побої і переносив і наготу, і холод удень і вночі. Одної ночі розпалив він піч у хатинці біля печери, і розгорілася піч, бо була ветха, і почав вогонь пробиватися щілинами, а йому нічим було закласти, так він ступив на вогонь ногами босими і зліз, аж як перегоріла піч. І багато іншого розказують про нього, а дечому я сам був очевидцем. І так, одержавши перемогу над бісівськими силами, як над мухами, ні за що мав погрози їхні і ману, і говорив до них: &laquo;Хоч ви мене спочатку і обдурили, оскільки не знав каверз ваших і лукавства, то нині маю з собою Господа Ісуса Христа Бога нашого і молитву отця нашого Федосія; надіюся на Христа і маю перемогти вас&raquo;. І багато разів біси капості йому робили і говорили до нього: &laquo;Ти наш, ти поклонився нашому старійшині і нам&raquo;. Він же відповідав їм: &laquo;Ваш старійшина - Антихрист, а ви - біси&raquo;, і хрестився, і вони щезали. Інколи ж знову вночі приходили до нього і страху йому нагонили видіннями, неначе багато народу з мотиками і кирками говорять: &laquo;Розкопаємо печеру оцю і оцього тут загребемо&raquo;. Він же відповідав їм: &laquo;Коли б ви були люди, то ви б ходили вдень, а ви - тьма і у тьмі ходите&raquo;,- і осіняв себе хресним знаменням, і вони щезали. Другого разу настрашували його в медвежому образі, інколи лютим звірем, інколи ж волом, чи то змії повзуть до нього, чи то жаби і миші, і всілякі гади. І не могли йому нічого зробить, і сказали йому: &laquo;Ісакій! Ти переміг нас!&raquo; Він же сказав: &laquo;Ви мене спочатку перемогли в образі Ісуса Христа і в ангельському образі, недостойні того видіння, то знову являється в образі звіриному і скотському, зміями і гадами, якими насправді і є, скверні, злі на вигляд&raquo;. І враз щезали біси від нього, і відтоді не було йому пакості від бісів. Якось і сам оповідав: &laquo;Що це за війна була у мене з ними три роки&raquo;. І потім почав впевненіше жити і дотримувався воздержання, посту і старання. І так живучи, закінчив дні свої. Розхворівся в печері, і перенесли його, хворого, в монастир, і на восьмий день помер у Бозі. Ігумен же Іван і братія прибрали тіло його і похоронили.</p>\r\n<p>Такі-от були чорноризці Федосієвого монастиря, що сяють і після смерті подібно світилам, і молять Бога за сущу на цьому світі братію, і за мирську братію, і за приносящих на монастир, в якому і нині живуть добродійним життям всі разом у співах, і в молитвах, і в послусі на славу Богові всемогущому і Федосієвими молитвами оберігаються, йому ж і слава в віках, амінь.</p>\r\n<p>В літо 6583 [1075]. Почав будувати ігумен Стефан церкву Печерську на основі, яку заклав Федосій, а на тій основі Стефан почав. І закінчили будувати на третій рік місяця липня в перший день.</p>\r\n<p>Цього ж літа приходили посли із Німець до Святослава. Святослав же величався, показав їм багатство своє. Вони ж, як побачили незчисленну кількість золота, і срібла, і паволок, сказали: &laquo;Це ніщо, бо воно лежить мертве, сміття. Краще за це - мужі, які можуть добути і більше цього&raquo;. Так похвалився Єзекій, цар Іудейський, перед послами царя Ассірійського, у якого все було взято у Вавілон, так і після смерті Єзекія все майно його розсипалося в прах.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>',NULL,1852,5,1,NULL,25);
INSERT INTO `publications_publication` VALUES
(4708,'P','2008-12-20',0,NULL,'Note','Повість врем\'яних літ Роки 6545 [1037] — 6576 [1068].','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В літо 6545 [1037] заклав Ярослав город великий Київ, а біля нього Золоті ворота. Заклав і церкву митрополичу на честь святої Софії, премудрості божої, і потім на Золотих воротах кам\'яну церкву святої Богородиці Благовіщення.</span></span></p>','<p><a href=\"http://izbornyk.org.ua/pvlyar/yar06.htm\">Роки 6545 [1037] &mdash; 6576 [1068].</a></p>\r\n<p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Въ л<small>Ђ</small>то 6545. Заложи Ярославъ городъ великый Кыевъ, у негоже града врата суть златая, заложи же церковь святыя Софья, премудрость Божию, митрополью, и посемь церьковь на Златыхъ врат<small>Ђ</small>хъ камену святыя Богородица Благов<small>Ђ</small>щение; сий же премудрый князь Ярославъ того д<small>Ђ</small>ля створи Благов<small>Ђ</small>щение на врат<small>Ђ</small>хъ, дать всегда радость граду тому святымь Благов<small>Ђ</small>щениемь Господнимь и молитвою святыя Богородица и архангела Гаврила; посемь святаго Георгия манастырь, и святыя Орины. И при семь нача в<small>Ђ</small>ра крестьянская плодитися в Руси и раширятися, и чернорисци поча множитися, и манастыреве почаху быти. И б<small>Ђ</small> Ярославъ любя церковныя уставы, и попы любяше повелику, излиха же б<small>Ђ</small> любаче черноризьци, и книгамъ прилежа, почитая часто в день и вь нощи; и собра писц<small>Ђ</small> многы, и пр<small>Ђ</small>кладаше отъ Гр<small>Ђ</small>кь на Словеньскый языкъ и писмя, и списаша многы книги, и сниська, имиже поучаються в<small>Ђ</small>рнии людье, и наслажаються учения божественаго гласа. Якоже бо н<small>Ђ</small>кто землю разореть, другый же нас<small>Ђ</small>еть, инии же пожинають и ядять пищу бескудну, да и се; отець бо сего Володимиръ землю разора и умягчи,.рекше крещениемъ просв<small>Ђ</small>тивъ; сий же Ярославъ, сынъ Володимерь, нас<small>Ђ</small>я книжными словесы сердца в<small>Ђ</small>рныхъ людий, а мы пожинаемь, учение приемлюще книжное. Велика бо полза бываеть челов<small>Ђ</small>ку отъ учения книжнаго; и книгами бо кажеми и учими, есми пути покаянию, и мудрость бо обр<small>Ђ</small>таемь и вздержание отъ словесъ книжныхъ; се бо суть рекы напаяющи вселеную всю, се суть исходяща мудрости; книгамъ бо есть неищетная глубина, сими бо есми в печали ут<small>Ђ</small>шаемы есмы, си суть узда вьздеръжанию. Мудрость бо велика есть, яко бо и Соломонъ хваляше ю глаголаше: азъ премудрость вселихъ; св<small>Ђ</small>тъ, и разумъ и смыслъ; азъ призвахъ страхъ Господень; мой св<small>Ђ</small>тъ, моя мудрость, мое утв<small>Ђ</small>ржение; мною цари царствують и силнии пишють правду, мною вельможи величаються, мучители удержать землю; азъ любящая люблю, ищющимъ мене обрящють. Аще поищеши вь книшахъ мудрости и прилежно, то обрящеши великую ползу души своей; иже и часто кто чтеть книгы, то бес<small>Ђ</small>дуеть с Богомъ или святыми мужьями; почитая пророчьскыя бес<small>Ђ</small>ды, еуангельская учения и апостольская, и житья святыхъ отець, и вьсприемлеть душа ползу велику. Ярославъ же сь, якоже рекохомъ, любимъ б<small>Ђ</small> книгамъ, и многы списавь положи вь церкви святой Софьи, юже созда самъ, и украси ю иконами многоц<small>Ђ</small>ньными, и златомъ, и сребромъ, и сосуды церковьными, въ нейже обычныя п<small>Ђ</small>сни Богу вьздають в годы обычныя. И ины церкви ставяше по градомъ и по м<small>Ђ</small>стомъ, поставляя попы и дая им<small>Ђ</small>ния своего урокъ, и вели имъ учити людий и приходити часто кь церквамъ; попови бо часто достоить учити людий, понеже тому есть поручено Богомъ; и умножишася прозвутери, и людье хрестьяньст<small>Ђ</small>и. И радовашеся Ярославъ видя многи церкви и люди христіаны з<small>Ђ</small>ло; а врагъ с<small>Ђ</small>товаше, поб<small>Ђ</small>жаемь новыми людьми, крестьяными.</p>\r\n<p>Въ л<small>Ђ</small>то 6546. Иде Ярославъ на Ятвягы.</p>\r\n<p>Въ л<small>Ђ</small>то 6547. Священа бысть церкви святыя Богородица, юже созда Володимеръ, отець Ярославль, митрополитомъ Феопеньтомъ.</p>\r\n<p>В л<small>Ђ</small>то 6548. Ярославъ иде на Литву.</p>\r\n<p>В л<small>Ђ</small>то 6549. Иде Ярославъ на Мазовшаны вь лодьяхъ.</p>\r\n<p>В л<small>Ђ</small>то 6550. Иде Володимиръ, сынъ Ярославль, на Ямь, поб<small>Ђ</small>дивъ я; и помроша кони у Володимерь вой, и яко еще дышющимь конемь, сдираху хъзы с нихъ: толикъ б<small>Ђ</small> моръ в кон<small>Ђ</small>хъ.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6545 [1037] заклав Ярослав город великий Київ, а біля нього Золоті ворота. Заклав і церкву митрополичу на честь святої Софії, премудрості божої, і потім на Золотих воротах кам\'яну церкву святої Богородиці Благовіщення. Цей же премудрий князь Ярослав для того поставив церкву Благовіщення на Золотих воротах, щоб завжди приносити тому городу радість святим Благовіщенням Господнім і молитвою святої Богородиці і архангела Гавриїла, Після цього [заснував] монастир святого Георгія і святої Орини. І при цьому почала віра християнська множитися на Русі і поширюватися. І чорноризці почали множитися, і з\'явилися монастирі. І любив Ярослав церковні статути, і попів любив дуже, особливо ж любив чорноризців. І до книг був прихильний, читав часто і вдень, і вночі. І зібрав писців много, і переклали вони із грецької на словенську мову і письмо. І написали вони багато книг, і славу цим здобули, за їхніми книгами повчаються вірнії люди і насолоджуються, навчаючись божественного слова. Так, неначе один хтось виоре землю, а другий посіє, а інші - пожинають і споживають страву неоскудну, так і це. Адже батько його Володимир землю розорав і спушив, тобто хрещенням просвітив. Цей же Ярослав, син Володимирів, засіяв книжними словами серця вірних людей, а ми пожинаємо, приймаючи науку книжну. Велика-бо користь буває чоловікові від науки книжної, бо книги вказують нам і навчають нас, як іти шляхом покаяння, і мудрість, і стриманість здобуваємо із слів книжних. Книги подібні рікам, що тамують спрагу цілого світу, це джерела мудрості. Книги - бездонна глибина, ми ними в печалі втішаємось, вони - узда для тіла і душі. Мудрість - велика, її і премудрий Соломон похваляє словами: &laquo;Я, премудрість, вселила світло, і розум, і смисл. Я приклала страх господній. Моє світло, моя мудрість, моє утвердження. Мною царі царюють і сильні правду пишуть, мною вельможні величаються, мучителі землею управляють. Я люблячих мене - люблю, а хто шукає мене - знайде&raquo;.</p>\r\n<p>А коли старанно пошукаєш у книгах мудрості, то матимеш велику користь для душі своєї. Бо той, хто часто читає книги, той з Богом бесідує або зі святими мужами. Читаючи пророчі бесіди, євангельські та апостольські повчання і житія святих отців, одержує душа користь велику.</p>\r\n<p>Ярослав же цей, як уже сказали, мав любов до книг і багато переписав їх і зібрав у церкві святої Софії, яку сам створив і прикрасив її іконами многоцінними, і золотом, і сріблом, і сосудами церковними. В ній же належні пісні Богу співають у належний час. І інші церкви ставив він у городах і місцях, посилав до них попів, і давав їм із багатств своїх урок, і наказував їм навчати людей і часто ходити до церкви. Попові-бо належить учити людей, оскільки це доручено Богом. І побільшало пресвітерів і люду християнського. І радувався Ярослав, споглядаючи багато церков і людей християнських, а ворог ремствував, переможений новими людьми християнськими.</p>\r\n<p>В літо 6546 [1038] Ярослав ходив на ятвягів.</p>\r\n<p>В літо 6547 [1039] митрополит Феопент освятив церкву святої Богородиці, яку Збудував Володимир, батько Ярославів.</p>\r\n<p>В літо 6548 [1040] Ярослав ходив на литовців.</p>\r\n<p>В літо 6549 [1041] Ярослав ходив у лодіях на мазовшан.</p>\r\n<p>В літо 6550 [1042] Володимир, син Ярославів, ходив на ям і переміг їх. І попадали коні у воїв Володимира. І коли коні ще дихали, із них уже здирали шкури - такий був мор на коней!</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6551. Посла Ярославъ Володимира, сына своего, на Гр<small>Ђ</small>кы, и да ему воя многы, а воеводьство поручи Вышат<small>Ђ</small>, отцю Яневу. И поиде Володимирь на Царьградъ в лодьяхъ, и придоша в Дунай, и отъ Дуная поидоша кь Царюграду; и бысть буря велика, и разби корабл<small>Ђ</small> Руси, и княжь корабль разби в<small>Ђ</small>тръ, и взяша князя в корабли Ивань Творимирича, воеводы Ярославля; прочии вои Володим<small>Ђ</small>р<small>Ђ</small> вивержении быша [на] брегъ, числомъ 6000, и хотяче поити в Русь, и не иде с ними никтоже отъ дружины княжа И рече Вышата: &laquo;азъ пойду с ними&raquo;, и выс<small>Ђ</small>де ис корабля к нимъ, рекъ: &laquo;аще живъ буду, то с ними; аще либо погибну, [то] с дружиною&raquo;. И поидоша хотяче в Русь. И бысть в<small>Ђ</small>сть Гр<small>Ђ</small>комъ, яко избило море Русь, и пославъ царь, именемь Мономахъ, по Руси олядіи 14; Володимеръ же видивъ, яко идуть по нихъ, вьспятився изби олядии Гр<small>Ђ</small>чьския, и возвратися в Русь, сьс<small>Ђ</small>давшися в корабл<small>Ђ</small> сво<small>Ђ</small>. Вышату же яша сь изверженьши на брегъ, и приведоша я Царюграду, и сл<small>Ђ</small>пиша Руси много; по 3-хъ же л<small>Ђ</small>т<small>Ђ</small>хъ миру бывшю, и пущенъ бысть Вышата вь Русь къ Ярославу. В сии же вемена вьдасть Ярославъ сестру свою за Казимира, и вьдасть Казимиръ за в<small>Ђ</small>но людий 8 сотъ, еже б<small>Ђ</small> полонилъ Болеславъ, поб<small>Ђ</small>дивъ Ярослава. В л<small>Ђ</small>то 6552. Выгребена быста 2 князя, Ярополкъ и Олегъ, сына Святославля, и крестиша кости ею положи я вь церкви святыя Богородица, в Володим<small>Ђ</small>ри.</p>\r\n<p>Того же л<small>Ђ</small>та умре Брячьславъ, сынъ Изяславль, внукъ Володимиръ, отецъ Всеславль, и Всеславъ, сынъ его, с<small>Ђ</small>де на стол<small>Ђ</small> его; егоже роди мати отъ волъхвования, матери бо родивши ему, и бысть ему язва на глав<small>Ђ</small> его, рекоша же волъсви матери его: &laquo;се язьвено на глав<small>Ђ</small> его, навяжи на нь, да носить е до живота своего&raquo;, еже носилъ Всеславъ и до смертного дни на соб<small>Ђ</small>; сего ради немилостивъ есть на кровопролитье.</p>\r\n<p>В л<small>Ђ</small>то 6553. Заложи Володимиръ святую Софью в Нов<small>Ђ</small>город<small>Ђ</small>.</p>\r\n<p>В л<small>Ђ</small>то 6554. В се л<small>Ђ</small>то бысть тишина велика.</p>\r\n<p>В л<small>Ђ</small>то 6555. Ярославъ иде на Мазовшаны, и поб<small>Ђ</small>ди я, и князя ихъ уби Моислава, и покори я Казимеру.</p>\r\n<p>В л<small>Ђ</small>то 6556. В л<small>Ђ</small>то 6557.</p>\r\n<p>В л<small>Ђ</small>то 6558. Преставися жена Ярославля княгини, февраля въ 10.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6551 [1043].</p>\r\n<p>Послав Ярослав сина свого Володимира на греків,</p>\r\n<p>І дав воїв йому багато,</p>\r\n<p>І доручив воєводство Вишаті, отцю Яневому,</p>\r\n<p>І пішов Володимир на Цареград у лодіях,</p>\r\n<p>І прийшов на Дунай,</p>\r\n<p>І від Дунаю пішов на Цареград.</p>\r\n<p>І була буря велика,</p>\r\n<p>І розбила кораблі русі,</p>\r\n<p>І князів корабель розбив вітер,</p>\r\n<p>І взяв князя в корабель [свій] Іван Творимирич, воєвода Ярославів,</p>\r\n<p>Інші вої Володимира, числом шість тисяч, на берег були викинуті,</p>\r\n<p>І, коли захотіли в Русь повернутися,</p>\r\n<p>І не пішов із ними із дружини князя ніхто.</p>\r\n<p>І сказав Вишата: &laquo;Я піду з ними&raquo;.</p>\r\n<p>І висів із корабля до них</p>\r\n<p>І сказав: &laquo;Якщо живий буду, то з ними,</p>\r\n<p>І якщо загину, то з дружиною&raquo;.</p>\r\n<p>І пішли, прагнучи в Русь.</p>\r\n<p>І дійшла звістка до греків, що море русів погромило,</p>\r\n<p>І послав цар на ймення Мономах за русами лодій чотирнадцять.</p>\r\n<p>І побачив Володимир, що йдуть по них,</p>\r\n<p>І, повернувшись, погромив кораблі грецькії,</p>\r\n<p>І сіли в кораблі свої,</p>\r\n<p>І повернулися в Русь.</p>\r\n<p>&nbsp;</p>\r\n<p>А Вишату ж полонили разом з викинутими на берег,</p>\r\n<p>І привели їх до Цареграда,</p>\r\n<p>І багато русі осліпили.</p>\r\n<p>Через три літа мир настав.</p>\r\n<p>І відпустили Вишату в Русь до Ярослава</p>\r\n<p>&nbsp;</p>\r\n<p>У цьому часі видав Ярослав сестру свою за Казимира І дав Казимир як віно вісімсот людей, яких був полонив ще Болеслав, коли переміг Ярослава.</p>\r\n<p>В літо 6552 [1044]. Відкопано було двох князів - Ярополка і Олега, синів Святослава, і охрещено їхні кості, і покладено їх в церкві святої Богородиці у Володимирі.</p>\r\n<p>Того ж року помер Брячислав, син Ізяславів, внук Володимирів, батько Всеславів. І Всеслав, син Брячислава, сів на батьківський стіл [у Полоцьку], а народила його мати через волхвування, бо коли мати народила його, у нього на голові була виразка. І сказали волхви матері його: &laquo;Цю виразку на голові його пов\'яжи пов\'язкою, і хай носить її до кінця життя свого&raquo;. Її носив Всеслав на собі до смертного дня свого. І через це був немилостивий до кровопролиття.</p>\r\n<p>В літо 6553 [1045]. Володимир заклав церкву святої Софії у Новігороді.</p>\r\n<p>В літо 6554 [1046]. В це літо була тиша велика.</p>\r\n<p>В літо 6555 [1047] Ярослав ходив на мазовшан, і переміг їх, і убив князя їхнього Моїслава, і підкорив їх Казимирові.</p>\r\n<p>В літо 6556 [1048]. В літо 6557 [1049].</p>\r\n<p>В літо 6558 [1050]. Померла княгиня, жона Ярослава, 10 лютого.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6559. Постави Ярославъ Лариона митрополитомъ Руси свят<small>Ђ</small>й Софьи, собравъ епископы. И се да скажемъ, чего ради прозвася Печерскый манастырь: боголюбивому князю Ярославу любяще Берестовое, и церковь сущую святыхъ апостолъ, и попы многы набдящю, и в нихже б<small>Ђ</small> прозвутерь, именемъ Ларионъ, мужь благъ, и книженъ и постникъ; и хожаше с Берестового на Дьн<small>Ђ</small>пръ на холмъ, кде нын<small>Ђ</small> ветхый манастырь Печерьскый, и ту молитвы творяше; б<small>Ђ</small> бо л<small>Ђ</small>съ ту великъ. Иськопа ту печеръку малу, 2 саженю, и приходя с Берестового, отпеваше часы и моляшеся ту Богу втайн<small>Ђ</small>. Посемъ же возложи Богъ князю въ сердце, постави его митрополитомъ свят<small>Ђ</small>й Софьи; а си печерка тако оста. И не по мноз<small>Ђ</small>хъ дн<small>Ђ</small>хъ б<small>Ђ</small> н<small>Ђ</small>кий челов<small>Ђ</small>къ, именемь мирьскимь отъ града Любча; и вьзложи сему Богъ вь страну ити, онъ же устремися вь Святую Гору ити, и вид<small>Ђ</small> манастыря сущая ту, и вьзлюби чернецьскый образъ, и приде вь единъ манастырь отъ сущихъ ту манастыревъ, и моли игумена того, дабы на нь възложилъ образъ мнискый. И онъ же послушавъ его, постриже его, и нарче имя ему Аньтоний, и наказавъ его и научивъ его черн<small>Ђ</small>цкому образу, и рече ему: &laquo;да иди опять вь Русь, и буди благословение отъ Святыя Горы, и мнози отъ теб<small>Ђ</small> чернорисци будуть&raquo;; и благослови его, отпусти, рекъ ему: &laquo;иди сь миромъ&raquo;. Антоний же приде кь Кыеву, и мышляше кд<small>Ђ</small> жити, и походи по манастыремь, и не возлюби, Богу не хотящу, и поча ходити по дебремь и по горамъ, ища, кде бы ему Богъ показалъ, и приде на холмъ, идеже б<small>Ђ</small> Ларионъ печеру ископалъ, и вьзлюби мьстьце се, и вселися в онь, и нача молитися Богу, со слезами глаголя: &laquo;Господи! утверди мя в м<small>Ђ</small>стьц<small>Ђ</small> семь, и да будеть на м<small>Ђ</small>стьци семь благословение Святыя Горы и моего игумена, иже мя постриглъ&raquo;. И поча жити ту, моля Бога, яды хл<small>Ђ</small>бъ сухий, и того чересъ день, и воды в м<small>Ђ</small>ру вкушая, и копая печеру, и не дадя соб<small>Ђ</small> покоя ни день ни нощь, вь труд<small>Ђ</small>хъ пребывая, вь бьд<small>Ђ</small>ни и вь молитвахъ. Посемъ же уведавше добр<small>Ђ</small>и челов<small>Ђ</small>ц<small>Ђ</small>, и приходяху к нему, приносяще ему на потребу; и прослу же и великий Антоний, и приходяще к нему просяху отъ него благословения. Посемъ же преставлешюся великому кьнязю Ярославу, и прия власть его сынъ Изяславъ, и с<small>Ђ</small>де Кыев<small>Ђ</small>, Антоний же прославленъ бысть в Руской земли. Изяславъ же ув<small>Ђ</small>давъ житие его, и приде с дружиною своею, прося у него благословения и молитвъ. И ув<small>Ђ</small>данъ бысть всими великий Антоний и честимъ, и начаша приходити к нему братья, и нача приимати и постригати я; и собрашася братья к нему яко числомъ 12. Иськопаша печеру велику, и церковь, и келья, яже суть и до сего дни в печер<small>Ђ</small> подъ ветхымъ манастыремь. Совокуплени же братьи, рече имъ Антоний: &laquo;се Богъ васъ съвокупи, братье, отъ благословения есте Святыя Горы, иже постриже мене игуменъ Святыя Горы, а я васъ постригалъ, да буди на васъ благословение первое отъ Бога, а второе отъ Святыя Горы&raquo; И се рекъ имъ, рече имъ: &laquo;живете особ<small>Ђ</small>, поставлю вы игумена, а самъ хощю вь ину гору с<small>Ђ</small>сти одинъ, якоже и преже бяхъ обыклъ, уединивься&raquo;. И постави имъ игумена, именемъ Варламъ, а самъ иде в гору, ископа печеру, яже есть подъ новымъ манастыремь, в нейже и сконча животъ свой, живъ вь доброд<small>Ђ</small>тели, и не выходя ис печеры л<small>Ђ</small>тъ 40 николиже никаможе, в ней же лежать мощи его и до сего дни. Братья же [и] игуменъ живяху в печер<small>Ђ</small>, и умножившимся братьи и не могущимъ имъ вм<small>Ђ</small>ститися в печеру, и помыслиша поставити вн<small>Ђ</small> печеры манастырь; и приде игуменъ и братья ко Аньтонию, и рекоша ему: &laquo;отче! умножилося братьи, а не можемся вм<small>Ђ</small>ститися в печер<small>Ђ</small>: да бы Богъ повел<small>Ђ</small>лъ и твоя молитва, да быхомъ поставил<small>Ђ</small> церквицю малу вн<small>Ђ</small> печеры&raquo;. И повел<small>Ђ</small> имъ Аньтонии. Они же поклонишася ему, и поставиша церьквицю малу надъ печерою во имя святыя Богородица Успение И нача Богъ умножити чернориз<small>Ђ</small>ць, молитвами святыя Богородица, и св<small>Ђ</small>тъ створиша братья съ игуменомъ поставити манастырь, и р<small>Ђ</small>ша братья кь игумену къ Антонию &laquo;отче! братья умножаеться, а хот<small>Ђ</small>л<small>Ђ</small> быхомъ поставити манастырь&raquo;. Антоний же радъ бывъ, рче:</p>\r\n<p>&laquo;благословенъ Богъ о всемь, и молитва святыя Богородица и сущихъ отець иже въ Свят<small>Ђ</small>й- Гор<small>Ђ</small> да будеть с вами&raquo;. И се рекъ, посла единого отъ братій къ Изяславу князю, река тако: &laquo;княже мой! се Богъ умножаеть братью, а м<small>Ђ</small>сце мало; да бы ны вдалъ гору ту, яже есть надъ печерою&raquo;. Изяславъ же се слышавъ радъ бывъ, и мужи свои посла, и дасть имъ гору ту. Игуменъ же и братья заложи церковь велику, и манастырь оградиша столпъемь, и к<small>Ђ</small>лья постави многы, и церковь св<small>Ђ</small>ршиша и украсиша ю иконами. И оттол<small>Ђ</small> начаша звати манастырь Печерскый; имже б<small>Ђ</small>ша жили черньцы преже в печер<small>Ђ</small>, и отъ того прозвася Печерскый манастырь; есть же Печерьскы манастырь отъ благословения Святыя Горы пошелъ. Манастыреви же свершену, игуменьство же держащю Варламу, Изяславъ же постави манастырь святаго Дмитр<small>Ђ</small>я, и выведе Варлама на игуменьство кь святому Дмитрею, хотя створити вышни сего манастыря, над<small>Ђ</small>яся бога[т]ств<small>Ђ</small>. Мнозии бо манастыри отъ царь и отъ бояръ и отъ богатства поставлени, но не суть таци, кации же суть поставлени слезами, и пощениемь, и молитвою, и бд<small>Ђ</small>ниемъ: Антоний бо не им<small>Ђ</small> злата, ни сребра, но пощениемъ и слезами, якоже глаголахъ.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6559 [1051] Ярослав настановив Ларіона митрополитом Русі, зібравши єпископів у святій Софії.</p>\r\n<p>І тут хай буде розказано, чому Печерський монастир так названо.</p>\r\n<p>&nbsp;</p>\r\n<p>Боголюбивий князь Ярослав любив [село] Берестове,</p>\r\n<p>І церкву сущу святих апостолів,</p>\r\n<p>І попів зібрав у ній багато,</p>\r\n<p>І був серед них презвітер на ймення Ларіон,</p>\r\n<p>І був муж добрий він,</p>\r\n<p>І книжник, і пісник,</p>\r\n<p>І ходив із Берестового на кручу Дніпровську,</p>\r\n<p>Де нині старий монастир Печерський,</p>\r\n<p>І тут молитви творив.</p>\r\n<p>І був тут ліс великий.</p>\r\n<p>І викопав там печерку малу на два сажні,</p>\r\n<p>І приходив туди з Берестового, відспівував години</p>\r\n<p>І богу молився таємно.</p>\r\n<p>І через це вселив Бог князеві у серце намір</p>\r\n<p>Поставити його митрополитом у святій Софії,</p>\r\n<p>А печерка ота так і залишилась.</p>\r\n<p>І днів небагато минуло,</p>\r\n<p>І був чоловік один світського звання із града Любеча,</p>\r\n<p>І вселив йому Бог бажання мандрувати,</p>\r\n<p>І вирушив він на Святу Гору,</p>\r\n<p>І побачив тамтешні монастирі,</p>\r\n<p>І возлюбив чернецький образ.</p>\r\n<p>І прийшов в один із тамошніх монастирів,</p>\r\n<p>І благав ігумена того, щоб на нього поклав образ мниський.</p>\r\n<p>І він послухав його, і постриг,</p>\r\n<p>І дав йому ім\'я Антоній,</p>\r\n<p>І дав йому настанови,</p>\r\n<p>І навчив його життю чернецькому,</p>\r\n<p>І сказав йому: &laquo;Повертайся знову на Русь,</p>\r\n<p>І хай буде тобі благословення від Святої Гори,</p>\r\n<p>І багато від тебе чорноризців буде&raquo;</p>\r\n<p>І відпустив його з благословенням,</p>\r\n<p>І сказав йому: &laquo;Йди з миром&raquo;.</p>\r\n<p>&nbsp;</p>\r\n<p>І коли Антоній прийшов у Київ,</p>\r\n<p>І роздумувати став, де йому жити,</p>\r\n<p>І обійшов монастирі всі</p>\r\n<p>І жодного не вподобав, бо того Бог не захотів.</p>\r\n<p>І став ходити він по дебрях і по горах,</p>\r\n<p>І шукав місце, яке б йому Бог показав,</p>\r\n<p>І прийшов на кручу, де Ларіон печеру викопав,</p>\r\n<p>І возлюбив те місце,</p>\r\n<p>І поселився в ній,</p>\r\n<p>І став Богу молитися,</p>\r\n<p>Із сльозами промовляючи: &laquo;Господи! Утверди мене на цьому місці,</p>\r\n<p>І хай буде на цьому місці благословення Святої Гори</p>\r\n<p>І мого ігумена, що постриг мене&raquo;.</p>\r\n<p>І почав жити тут, молячи Бога, їсти хліб сухий,</p>\r\n<p>І то через день,</p>\r\n<p>І в міру води споживати,</p>\r\n<p>І печеру копати.</p>\r\n<p>І не давав собі спокою ні вдень, ні вночі,</p>\r\n<p>У трудах пребуваючи, у бдінні і в молитвах.</p>\r\n<p>&nbsp;</p>\r\n<p>Згодом же дізнались добрі люди,</p>\r\n<p>І приходили до нього,</p>\r\n<p>І приносили йому на потребу.</p>\r\n<p>І пішла слава про великого Антонія,</p>\r\n<p>І приходили до нього брати благословення.</p>\r\n<p>Після смерті великого князя Ярослава</p>\r\n<p>Прийняв владу його син Ізяслав,</p>\r\n<p>І сів у Києві.</p>\r\n<p>Антоній же був славний у Руській землі.</p>\r\n<p>І довідався Ізяслав про життя його,</p>\r\n<p>І прийшов до нього з дружиною своєю,</p>\r\n<p>І просив у нього благословення</p>\r\n<p>І молитися за них.</p>\r\n<p>І був знаний всіма великий Антоній</p>\r\n<p>І честь мав.</p>\r\n<p>І почала приходити до нього братія,</p>\r\n<p>І він приймав і постригав [у ченці] їх.</p>\r\n<p>І так зібралося до нього братів дванадцять.</p>\r\n<p>І викопали печеру велику,</p>\r\n<p>І церкву,</p>\r\n<p>І келії.</p>\r\n<p>І до цього дня є вони в печері під старим монастирем.</p>\r\n<p>І сказав Антоній до братії, що зібралась:</p>\r\n<p>&laquo;Се Бог вас поєднав, браття, І ви тут від благословення Святої Гори,</p>\r\n<p>Ігумен Святої Гори постриг мене, а я вас постригав,</p>\r\n<p>І хай буде на вас перше благословення від Бога,</p>\r\n<p>А друге від Святої Гори.</p>\r\n<p>І це сказавши, мовив їм:</p>\r\n<p>&laquo;Живіте осібно, настановлю я вам ігумена,</p>\r\n<p>А сам хочу на іншій горі поселитись один,</p>\r\n<p>Щоб, як звик уже, жити,, усамітнившись&raquo;.</p>\r\n<p>І поставив їм ігумена на ім\'я Варлам,</p>\r\n<p>А сам пішов на гору, викопав печеру,</p>\r\n<p>Яка тепер під новим монастирем,</p>\r\n<p>У ній же і закінчив життя своє,</p>\r\n<p>І жив у доброчесності,</p>\r\n<p>І не виходив з печери ніколи й нікуди сорок літ.</p>\r\n<p>І в ній же лежать мощі його і до сьогодні.</p>\r\n<p>А братія з ігуменом жили в печері,</p>\r\n<p>І примножилося братії,</p>\r\n<p>І не могли вже вміститись в печері,</p>\r\n<p>І намислили збудувати поза нею монастир.</p>\r\n<p>І прийшов ігумен з братією до Антонія</p>\r\n<p>І сказав йому: &laquo;Отче! Примножилось братії,</p>\r\n<p>І не можемо вміститися в печері.</p>\r\n<p>І коли б звелів Бог і молитва твоя,</p>\r\n<p>Щоб поставили ми церковцю малу поза печерою&raquo;,</p>\r\n<p>І звелів їм Антоній.</p>\r\n<p>І вони поклонилися йому</p>\r\n<p>І поставили церквицю малу над печерою</p>\r\n<p>В ім\'я святої Богородиці Успенія.</p>\r\n<p>І став Бог молитвами святої Богородиці примножувати чорноризців,</p>\r\n<p>І раду учинила братія з ігуменом поставити монастир,</p>\r\n<p>І звернулась братія з ігуменом до Антонія;</p>\r\n<p>&laquo;Отче! Примножується братія,</p>\r\n<p>І хотіли б збудувати монастир&raquo;.</p>\r\n<p>Антоній радий був і сказав:</p>\r\n<p>&laquo;Благословенний у всьому Бог&raquo;</p>\r\n<p>І молитва святої Богородиці,</p>\r\n<p>І сущих у Святій Горі отців молитва -</p>\r\n<p>Хай буде з вами&raquo;.</p>\r\n<p>І, сказавши це, послав одного із братії до князя Ізяслава так сказати:</p>\r\n<p>&laquo;Княже мій! Се Бог примножує братію, а місця мало.</p>\r\n<p>Чи не дав би нам ту гору, що над печерою?&raquo;</p>\r\n<p>Ізяслав же почув се і радий був,</p>\r\n<p>І мужів своїх послав,</p>\r\n<p>І дав їм гору ту.</p>\r\n<p>Ігумен же з братією заклали церкву велику.</p>\r\n<p>І монастир стовпами обнесли,</p>\r\n<p>І келій набудували багато,</p>\r\n<p>І церкву звели,</p>\r\n<p>І оздобили її іконами.</p>\r\n<p>І відтоді почали звати монастир Печерський.</p>\r\n<p>І оскільки жили ченці раніш в печері,</p>\r\n<p>І від того прозвався Печерський монастир.</p>\r\n<p>І постав же Печерський монастир від благословення Святої Гори.</p>\r\n<p>І коли монастир постав, ігуменство тримав Варлам,</p>\r\n<p>Ізяслав збудував монастир святого Димитрія,</p>\r\n<p>І вивів Варлама на ігуменство до святого Димитрія,</p>\r\n<p>І хотів поставити цей монастир вище Печерського,</p>\r\n<p>Бо надіявся на [своє] багатство.</p>\r\n<p>Багато монастирів від царів, від бояр</p>\r\n<p>І від їхніх багатств поставлено,</p>\r\n<p>Та не такі вони, як ті, що сльозами поставлені,</p>\r\n<p>І постом, і молитвою, і старанням,</p>\r\n<p>Бо Антоній ні золота, ні срібла не мав,</p>\r\n<p>А постом і сльозами [будував],</p>\r\n<p>Як я вже говорив.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Варламу же шедшю святому Дмитр<small>Ђ</small>ю, и св<small>Ђ</small>тъ створше братья, идоша кь старцю Аньтонию, и рекоша: &laquo;постави намъ игумена&raquo;. Онъ же рче имъ: &laquo;кого хощете?&raquo; Они же р<small>Ђ</small>ша ему: &laquo;кого хощеть Богъ и ты&raquo;. И рече: &laquo;кто болий есть в васъ, акь есть Федосий, послушливъ, и кротокъ, и смиреный; да сьи будеть игуменъ ваадъ&raquo;. Братья же ради бывше, и поклонишася старцю, и поставиша Федосья игуменомъ братьи, сущей числомъ 20. Федосьеви же приимшю манастырь, и поча им<small>Ђ</small>ти воздержание велико, пощение и молитвы сь слезами, и совокупляти нача многы черьнорисци, и совокупи братьи числомъ 100. И нача иськати правила чернечьскаго, и обр<small>Ђ</small>теся тогда Михаилъ, черн<small>Ђ</small>ць манастыря Студискаго, иже б<small>Ђ</small> пришелъ изъ Гр<small>Ђ</small>кь с митрополитомъ Георгиемъ, и нача у него искати устава черьнець Студийскыхъ и обр<small>Ђ</small>ть у него, и списа, и устави въ манастыри своемъ, како п<small>Ђ</small>ти п<small>Ђ</small>ния манастырьская, и поклонъ како держати, и чтения почитати, и стояние въ церкви, и весь рядъ церковьный, на тряпез<small>Ђ</small> с<small>Ђ</small>дание, и что ясти въ кыя дни, все съ въставлениемъ. Федосий все то приобр<small>Ђ</small>тъ, и предасть манастырю своему. Отъ негоже манастыря прияша вси манастыр<small>Ђ</small> уставъ по всемъ манастыремь; т<small>Ђ</small>мже почтенъ есть манастырь Печерьскый стар<small>Ђ</small>й всихъ и честью боле всихъ. Федосьеви же живущю в манастыр<small>Ђ</small>, и правящю добродътельное житье и черн<small>Ђ</small>цьское правило, и приимающе всякого приходящего к нему, к немуже и азъ придохъ, худый и недостойны рабъ, приять мя л<small>Ђ</small>тъ ми сущю 17 отъ рожения моего. Се же написахъ и положихъ, и в кое л<small>Ђ</small>то почалъ быти манастырь, что ради зовется Печерьскый манастырь; а о Федосьев<small>Ђ</small> житьи паки скажемь.</p>\r\n<p>В л<small>Ђ</small>то 6560. Преставися Володим<small>Ђ</small>рь, сынъ Ярославль, стар<small>Ђ</small>йший в Нов<small>Ђ</small>город<small>Ђ</small>, и положенъ бысть вь свят<small>Ђ</small>й Софьи, юже б<small>Ђ</small> самъ создалъ.</p>\r\n<p>В л<small>Ђ</small>то 6561. У Всеволода родися синъ Володимиръ отъ цариц<small>Ђ</small> Гр<small>Ђ</small>чькое.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>І коли Варлам пішов до святого Дмитрія, тоді брати раду створили,</p>\r\n<p>І пішли до старця Антонія, і сказали:</p>\r\n<p>&laquo;Постав над нами ігумена&raquo;.</p>\r\n<p>Він же спитав їх: &laquo;Кого хочете?&raquo;</p>\r\n<p>Вони ж сказали йому: &laquo;Кого хоче Бог і ти&raquo;.</p>\r\n<p>І сказав [Ант.оній]: &laquo;Хто більше із вас, як не Федосій,</p>\r\n<p>І найслухняніший, і покірливий, і смиренний.</p>\r\n<p>Хай він буде вашим ігуменом&raquo;.</p>\r\n<p>Браття ж, раді бувши, поклонилися старцю,</p>\r\n<p>І поставили Федосія ігуменом над братією,</p>\r\n<p>А було їх числом двадцять.</p>\r\n<p>І коли Федосій прийняв монастир,</p>\r\n<p>Він почав тримати помірність велику,</p>\r\n<p>І постування, і слізні молитви,</p>\r\n<p>І збирати почав багато чорноризців,</p>\r\n<p>І зібрав братії числом сто.</p>\r\n<p>І почав шукати правила чернецького,</p>\r\n<p>І знайшовся тоді Михайло чернець</p>\r\n<p>Із монастиря Студитського,</p>\r\n<p>А прийшов він із Греції з митрополитом Георгієм,</p>\r\n<p>І став у нього шукати уставу ченців Студитських,</p>\r\n<p>І знайшов у нього, і переписав,</p>\r\n<p>І увів в монастирі своєму,</p>\r\n<p>Як співати співи монастирські,</p>\r\n<p>І як поклонятися, й читання шанувати,</p>\r\n<p>І стояння в церкві, і лад весь церковний запровадив,</p>\r\n<p>Як на трапезі сідати,</p>\r\n<p>І у дні які що їсти, все за уставом,</p>\r\n<p>І усе оте Федосій пізнав</p>\r\n<p>І в монастирі своєму запровадив.</p>\r\n<p>&nbsp;</p>\r\n<p>Від його монастиря всі перейняли статут і ввели його по всіх монастирях. Тому й шанується монастир Печерський як найстаріший і найславніший із всіх. Коли Федосій жив у монастирі і вів добродійне життя, дотримуючись чернецьких правил і приймаючи кожного, хто до нього приходив, і я прийшов до нього, бідний і недостойний раб, і прийняв він мене, коли було мені сімнадцять років від народження мого. Це я написав і встановив, в яке літо почав бути монастир, що тепер називається Печерський монастир. А про Федосієве життя ще розповімо.</p>\r\n<p>В літо 6560 [1052]. У Новгороді помер Володимир, старший син Ярослава, і поклали його в святій Софії, яку сам збудував.</p>\r\n<p>В літо 6561 [1053]. У Всеволода народився син Володимир від цариці грецької.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6562. Преставися князь Руский Ярославь. И еще живу сущю ему, наряди сыны своя, рекы имъ: &laquo;се азъ отхожю св<small>Ђ</small>та сего, а вы, сынов<small>Ђ</small> мои, им<small>Ђ</small>йте межи собою любовь, понеже вы есте братья единого отца и единой матерь; да аще будете в любви межи собою, и Богъ будеть в васъ, и покорить вы противныя под вы, и будете мирно живуще; аще ли будете ненавистьно живуще, вь распряхъ которающеся, то и сами погибнете, и землю отець своихъ и д<small>Ђ</small>дъ погубите, иже нал<small>Ђ</small>зоша трудомъ великомъ; но послушайте братъ брата, пребывайте мирно; се же поручаю в себе м<small>Ђ</small>сто столъ свой стар<small>Ђ</small>йшому сынови своему, брату вашему Изяславу, Кыевъ, сего послушайте, якоже послушасте мене, да ть вы будеть вь мене м<small>Ђ</small>сто; а Святославу Черниговъ, а Всеволоду Переяславль, а Вячеславу Смолнескь&raquo;. И тако разд<small>Ђ</small>ли городы, запов<small>Ђ</small>давъ имъ не преступати пред<small>Ђ</small>ла братня, ни сгонити; рекь Изяславу: &laquo;аще кто хощеть обидити своего брата, но ты помогай егоже обидять&raquo;; и тако наряди сыны своя пребывати в любви. Самому же болну сущю и пришедшю ему к Вышегороду, разбол<small>Ђ</small>ся велми, Изяславу тогда в Туров<small>Ђ</small> князящю, а Святославу вь Володимир<small>Ђ</small>, а Всеволодъ тогда у отца, 6<small>Ђ</small> бо любимъ отцемъ паче всея братья, егоже имяше у себе. Ярославу же присп<small>Ђ</small> кон<small>Ђ</small>ць житья, и предасть душю свою м<small>Ђ</small>сяца февраля въ 20, в суботу в нед<small>Ђ</small>ли поста вь святаго Федора день. Всеволодъ же спрята т<small>Ђ</small>ло отца своего, вьзложивъ на сани и повезоша Кыеву, попов<small>Ђ</small> по обычаю п<small>Ђ</small>сни п<small>Ђ</small>вше, и плакашеся по немь людье; и принесъше положиша и в рац<small>Ђ</small> мороморян<small>Ђ</small>, вь церкви свят<small>Ђ</small>й Софья, и плакася по немь Всеволодъ и людье вси. Жить же вс<small>Ђ</small>хъ л<small>Ђ</small>тъ Ярослав<small>Ђ</small> 70 и 6.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6562 [1054]. Помер князь руський Ярослав. І коли ще живий був, зібрав синів своїх і сказав їм: &laquo;От я і відходжу із цього світу, а ви, сини мої, живіть між собою в любові, бо всі ви брати від одного батька і одної матері. І якщо будете жити в любові між собою, і Бог буде з вами, і підгорне під вас ворогів ваших, і будете мирно жити. Та коли будете в ненависті жити, в сварках і міжусобицях, то й самі загинете, і землю батьків і дідів своїх погубите, яку здобули трудом великим. Але слухайте брат брата, живіть мирно. Тепер же заповідаю стіл свій у Києві старшому синові моєму, братові вашому Ізяславу, його слухайте, як слухали мене, хай він замінить вам мене. А Святославу - Чернігів, а Всеволоду - Переяслав, а Вячеславу- Смоленськ&raquo;. І так розділив [між ними] города, заповідав їм не переходити братніх меж і не зганяти [зі стола]. Говорив Ізяславу: &laquo;Коли хтось захоче обидити брата свого, то ти помагай тому, кого обидять&raquo;. І так наставляв синів Своїх жити в любові. Сам \"він уже був тоді хворий і, коли приїхав у Вишгород, розхворівся дуже. Ізяслав тоді в Турові княжив, а Святослав у Володимирі, а Всеволод тоді з батьком був, бо любив його батько більше всіх братів і тримав у себе. Надійшов кінець життя Ярославу, і віддав він душу свою місяця лютого в двадцятий день, у суботу, першого тижня посту, у день святого Федора. Всеволод же прибрав тіло батька свого, возложив на сани і повіз до Києва. Попи за звичаєм піснеспіви співали, і плакали по ньому люди. І принесли і поклали його в мармурову раку у церкві святої Софії, і плакали за ним Всеволод і люди всі. Прожив же Ярослав всіх літ сімдесят і шість.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&nbsp;</p>\r\n<p><em>Начало княжения Изяславля вь Киев<small>Ђ</small>.</em></p>\r\n<p>&nbsp;</p>\r\n<p>В л<small>Ђ</small>то 6563. Пришедъ Изяславъ с<small>Ђ</small>де в Кыев<small>Ђ</small>, а Святославъ в Чернигов<small>Ђ</small>, Всеволодъ же в Переяславл<small>Ђ</small>, Игорь в Володимер<small>Ђ</small>, Вячеславъ вь Смоленьсц<small>Ђ</small>.</p>\r\n<p>В то же л<small>Ђ</small>то иде Всеволодъ на Торкы зим<small>Ђ</small> къ Воіню, и поб<small>Ђ</small>ди Торкы.</p>\r\n<p>Того же л<small>Ђ</small>та приходи Блушь с Половци, и створи Вссволодъ миръ с ними, и возвратишася [Половци] вьсвояси.</p>\r\n<p>В л<small>Ђ</small>то 6564.</p>\r\n<p>В л<small>Ђ</small>то 6565. Преставися Вячеславъ; сынъ Ярославль, Смоленьский, и посадиша Игоря вь Смольньсц<small>Ђ</small>, изъ Володимеря выведше</p>\r\n<p>В л<small>Ђ</small>то 6566. Поб<small>Ђ</small>ди Изяславъ Голядь.</p>\r\n<p>В л<small>Ђ</small>то 6567 Изяславъ и Святославъ и Всеволодъ высадиша стрыя своего ис поруба, с<small>Ђ</small>д<small>Ђ</small>вша 20 и 4 л<small>Ђ</small>тъ, и водивше и ко кресту и бысть черньцемь.</p>\r\n<p>В л<small>Ђ</small>то 6568. Преставився Игорь, сынъ Ярославъ.</p>\r\n<p>Того же л<small>Ђ</small>та Изяславъ, и Святославъ, и Всеволодъ, и Все славъ, совокупивше воя бещислены, и поидоша на конихъ и в лодьяхъ, бещисленое множьство, на Торкы. И се слышавше Торци, убоявьшеся, проб<small>Ђ</small>гоша и до сего дни, и помроша б<small>Ђ</small>гающе, Божиимъ гн<small>Ђ</small>вомъ гоними, овии отъ зимы, друзии же гладомъ, инии же моромъ [и] судомъ Божиимъ; и такъ Богъ избави крестьяны отъ поганыхъ.</p>\r\n<p>В л<small>Ђ</small>то 6569. Придоша Половци первое на Руськую землю воевати, Всеволодъ же изыиде противу имъ, м<small>Ђ</small>сяца февраля вь 2 день; и бившимъся имъ, поб<small>Ђ</small>диша Всеволода, и воевавше отъидоша. Се бысть первое зло на Руськую землю отъ поганыхъ безбожныхъ врагъ; бысть же князь ихъ Сокалъ.</p>\r\n<p>В л<small>Ђ</small>то 6570.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&nbsp;</p>\r\n<p><em>Початок княження Ізяслава в Києві.</em></p>\r\n<p>&nbsp;</p>\r\n<p>В літо 6563 [1055]. Прийшов Ізяслав і сів у Києві, а Святослав у Чернігові, Всеволод у Переяславі, Ігор у Володимирі, Вячеслав у Смоленську</p>\r\n<p>Того ж року взимку ходив Всеволод на торків до Воїню і переміг торків.</p>\r\n<p>Того ж літа приходив Блуш з половцями, і Всеволод уклав мир із ними, і половці пішли геть.</p>\r\n<p>В літо 6564 [1056].</p>\r\n<p>В літо 6565 [1057]. Помер Вячеслав смоленський, син Ярослава. І посадив Ігоря в Смоленську, а з Володимира вивів.</p>\r\n<p>В літо 6566 [1058] Ізяслав переміг голядь.</p>\r\n<p>В літо 6567 [1059] Ізяслав, Святослав і Всеволод звільнили стрия свого [Судислава] з поруба, де він просидів двадцять чотири роки, і взяли з нього клятву на хресті, і він став ченцем.</p>\r\n<p>В літо 6568 [1060]. Помер Ігор, син Ярослава.</p>\r\n<p>Того ж літа Ізяслав, Святослав, Всеволод і Всеслав зібрали воїв без числа і пішли на конях і в лодіях на торків. І коли прочули про це торки, то так перестрашились, що як побігли, так і до сьогодні бігають, божим гнівом гнані, і перемерли в бігах - одні від холоду, другі з голоду інші від мору і суду божого. Так Бог позбавив християн від поганих.</p>\r\n<p>В літо 6569 [1061] вперше прийшли половці на Руську землю воювати. Всеволод же вийшов проти них місяця лютого у другий день. І билися вони, перемогли Всеволода і після бою відійшли. Це було перше зло на Руську землю від поганих безбожних ворогів. Був же князь у них Сокал.</p>\r\n<p>В літо 6570 [1062].</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6571. Преставися Судиславъ, братъ Ярославль, и погребоша и во церкви святаго Георгия.</p>\r\n<p>Того же л<small>Ђ</small>та в Нов<small>Ђ</small>город<small>Ђ</small> иде Волхово вьспять дний 5; се же знамение не [на] добро бысть: на 4-е л<small>Ђ</small>то погор<small>Ђ</small> весь городъ.</p>\r\n<p>В л<small>Ђ</small>то 6572. Б<small>Ђ</small>жа Ростиславъ къ Тмутороканю, сынъ Володимиръ, внукъ Ярославль, и с нимъ б<small>Ђ</small>жа Порей и Вышата, сынъ Остромирь воеводы Новгородьского; и пришедъ выгна Гл<small>Ђ</small>ба изъ Тмуторокана, а самъ с<small>Ђ</small>де в него м<small>Ђ</small>сто.</p>\r\n<p>В л<small>Ђ</small>то 6573. Иде Святославъ на Ростислава кь Тмутороканю, Ростиславъ же отступи прочь из града, не убоявься его, но не хотя противу стрыеви своему оружья взяти. Святославъ же пришедъ къ Тмутороканю, посади сына своего пакы Гл<small>Ђ</small>ба, и вьзвратися вьсвояси; пришедъ пакы опять Ростиславъ, и выгна Гл<small>Ђ</small>ба, и приде Гл<small>Ђ</small>бъ къ отцю своему; Ростиславъ же пришедъ с<small>Ђ</small>де въ Тмуторокан<small>Ђ</small>.</p>\r\n<p>В то же л<small>Ђ</small>то Всеславъ с<small>Ђ</small>де рать почалъ.</p>\r\n<p>В та же времена бысть знамение на запад<small>Ђ</small>: звезда превелика, луч<small>Ђ</small> имуще аки кровав<small>Ђ</small>, всходящи с вечера по заход<small>Ђ</small> солънечнемь, и бысть за 7 дний; се же проявляющи не на добро. Посемъ же быша усобиц<small>Ђ</small> многы и нашестви поганыхъ на Руськую землю, си бо зв<small>Ђ</small>зда акы кровава, проявъляющи крови пролитье. В та же времена бысть д<small>Ђ</small>тище вьвержено вь С<small>Ђ</small>томле; сего же д<small>Ђ</small>тища выволокоша рыболов<small>Ђ</small> в невод<small>Ђ</small>, егоже позоровахомъ и до вечера, и пакы выв<small>Ђ</small>ргоша и вь воду, бяше бо на лиц<small>Ђ</small> его сице срамнии удове, а иного н<small>Ђ</small>льз<small>Ђ</small> казати срама ради. Пред симже временемь солнце пр<small>Ђ</small>менися, не бысть св<small>Ђ</small>тло, но акы м<small>Ђ</small>сяць бысть; егоже невегласии глаголють сн<small>Ђ</small>даему сущю. Се же бывають сия знамения не на добро, мы бо по сему розум<small>Ђ</small>хомъ. Якоже древле, при Антиос<small>Ђ</small>, вь Ерусалим<small>Ђ</small>, ключися внезапу по всему граду за 40 дний являтися на въздус<small>Ђ</small> на конихъ рищищимъ, вь оружьи, златыя одежа имущи, и полкы обо[амо] являющемъ, и оружью движащюся; се же являше нахожение Антиохово, нашествие рати на Ерусалимъ. Посемъ же при Нерон<small>Ђ</small> цар<small>Ђ</small> в томъ же Ерусалим<small>Ђ</small> въсия зв<small>Ђ</small>зда, вь образъ копийный надъ городомъ; се же проявляше нахожение рати отъ Римлянъ И пакы сице бысть при Устиян<small>Ђ</small> цар<small>Ђ</small>, зв<small>Ђ</small>зда вьсия на запад<small>Ђ</small>, испущающи луча, юже прозываху блисталницю, и бысть сияющи за 20 дний, посемъ же бысть зв<small>Ђ</small>здамъ течение с вечера до утрия, яко мн<small>Ђ</small>ти всимъ, яко падають зв<small>Ђ</small>зды, и пакы солнце без лучь сияше; се же проявляше крамолы, недузи, челов<small>Ђ</small>комъ умертвие бяше. Паки же при Маврикии цари бысть се: жена д<small>Ђ</small>тище роди безъ о.чью, безъ руку, вь чресла б<small>Ђ</small> ему рыбьий хвостъ прирослъ, и песъ родися шестоногъ. Въ Африкии же 2 д<small>Ђ</small>тища родистася, единъ о 4 ногахъ, а другий о двою главу Посемъ же бысть при Костянтин<small>Ђ</small> иконоборци, сына Леонова, течение зв<small>Ђ</small>здьное бысть на не0ес<small>Ђ</small>хъ, оттергаху бо ся на землю, и яко видящимъ мн<small>Ђ</small>ти кончину; тогда же вьздухъ вьзлияся повелику. В Сурии же бысть трусъ велий, земл<small>Ђ</small> рас<small>Ђ</small>дшися три поприщь, изииде дивно изъ земли мьска, челов<small>Ђ</small>цскымъ гласомъ глаголющи, проповъдающи наитье языка, еже и бысть: наидоша бо Срацини на Палестиньскую землю. Знаменья бо вь небеси, или вь зв<small>Ђ</small>здахъ, или вь солнци, или птицами, или етеромъ чимъ, не благо бываеть; но знамения сица на зло бывають, или проявление рати, или гладу, или на смерть проявьляеть.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6571 [1063] помер Судислав, брат Ярослава, і поховали його в церкві святого Георгія.</p>\r\n<p>Того ж літа в Новгороді води Волхова ішли назад днів п\'ять. Це знамення не на добро було: на четверте літо погорів весь город.</p>\r\n<p>В літо 6572 [1064] утік Ростислав, син Володимира, внук Ярослава до Тмуторокані, а з ним утік Порей і Вишата, син Остромира, воєводи Новгородського. І, прийшовши, вигнав Гліба із Тмуторокані, а сам сів на його місце.</p>\r\n<p>В літо 6573 [1065] ходив Святослав на Ростислава до Тмуторокані. Ростислав же відступив геть із города, не тому, що злякався його, а тому, що не захотів проти стрия свого підняти оружжя. Святослав же прийшов до Тмуторокані, знову посадив сина свого Гліба і повернувся назад. Прийшов знову Ростислав і вигнав Гліба, і прийшов Гліб до отця свого, Ростислав же, повернувшись, сів у Тмуторокані.</p>\r\n<p>Того ж літа Всеслав міжусобну війну розпочав.</p>\r\n<p>У ті ж времена було знамення на заході: зірка превелика, неначе з кривавими променями, сходила звечора після заходу сонця, і так було сім днів. Се віщувало не на добро. Після цього було усобиць багато і нашестя поганих на Руську землю, бо ця зірка була неначе кривава і віщувала кровопролиття. У ті ж времена було дитя викинуто в [ріку] Сітомлю, це дитя витягли риболови в неводі, його роздивлялися до вечора і знову вкинули його в воду, бо на його лиці були сороміцькі частини тіла, а про інше через сором і говорити не можна. Перед цим же временем сонце перемінилося, було не світле, а ніби місяць; про нього невігласи говорили, що можна з\'їсти. Так ото подібні знамення бувають не на добро. Ми це з того розуміємо, що ще в давнину, при Антіосі, в Єрусалимі трапилося: раптом по всьому городу протягом сорока днів стали з\'являтися в повітрі вершники на конях, при оружжі, в золотих одежах, і з\'являлися полками, розмахуючи оружжям. Це віщувало напад Антіоха, нашестя військ на Єрусалим. А потім ще за царя Нерона, у тому ж Єрусалимі, возсіяла списоподібна звізда над городом. Це віщувало нашестя римського війська. А ще таке було при Юстиніані царі: звізда возсіяла на заході, випускаючи промені, за що прозвали її &laquo;блистальницею&raquo;, і сяяла двадцять днів, а після цього була звізд течія з вечора й до ранку, так що всі думали, що то зорепад; і знову сонце без променів сіяло. Се ж предвіщало крамоли, недуги, смерть людям. Знову ж за царя Маврикія було таке: жінка дитя народила без очей, без рук, а до живота йому риб\'ячий хвіст приріс. І пес народився шестиногий. У Африці ж двоє дітей народилося: один четвероногий, а другий двохголовий. А потім же за царя Костянтина іконоборця, Леонового сина, була звізд течія на небесах, зорі одторгалися й падали на землю, і хто бачив, той чекав кінця. Тоді ж повітря рясно стікало на землю. У Сірії ж був трус сильний, земля розверзлася на три поприща, і із землі чудом вийшов мул, людською мовою говорив і предвіщав нашестя чужинців, що й сталося: напали сарацини на Палестинську землю. Знамення бо в небесах, чи в зірках, чи в сонці, чи у птицях, чи в чомусь іншому не на добро бувають. Такі знамення на зло бувають, віщуючи війну, чи голод, а чи смерть.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6574. Ростиславу с<small>Ђ</small>дящу вь Тмуторокани, и емлющи дань у Касогъ и в ыныхъ странахъ. Сего же убоявъ же ся Гр<small>Ђ</small>ци, послаша с лестью Котпана; оному же пришедшю къ Ростиславу и ув<small>Ђ</small>рившюся ему, и чьтяше и Ростиславъ. Единою же пьющу Ростиславу с дружиною своею, рече Котопанъ: &laquo;княже! хощю на тя пити&raquo;; оному же рекшу: &laquo;пий&raquo;. Онъ же испивъ половину чаши, а половину вдасть князю пити; дотиснувься палцемъ в чашю б<small>Ђ</small> бо им<small>Ђ</small>я подъ ногьтемь растворение смертьное, и дасть князю урекъ смерть до осми дний. Оному же испившю, Котопанъ же пришедъ Кьрсуню пов<small>Ђ</small>да, яко в сий день умреть Ростиславъ, якоже и бысть; сего же Котопана побиша камениемь людье Корсуньстии. Б<small>Ђ</small> же Ростиславъ мужь добръ на рать, вьзрастомъ же л<small>Ђ</small>пъ и красенъ лицемь, милостивъ убогимъ; умре же м<small>Ђ</small>сяца февраля въ третий день, и тако положенъ бысть вь церкви святыя Богородица.</p>\r\n<p>В л<small>Ђ</small>то 6575. Заратися Всеславъ, сынъ Брячьславль Полотьский, и зая Новъгородъ; Ярославличи же трие, Изяславъ, Святославъ, Всеволодъ, совокупивше воя, идоша на Всеслава зим<small>Ђ</small> сущи велиц<small>Ђ</small>. И придоша къ М<small>Ђ</small>ньску, и М<small>Ђ</small>нян<small>Ђ</small> затворишася вь град<small>Ђ</small>; си же братья взяша М<small>Ђ</small>нескъ, исьс<small>Ђ</small>коша мужи, а жены и д<small>Ђ</small>ти взяша на щиты, и поидоша кь Немиз<small>Ђ</small>; и Всеславъ поиде противу. И совокупившеся обои на Немиз<small>Ђ</small>, м<small>Ђ</small>сяца марта вь 3 день, и бяше сн<small>Ђ</small>гъ великъ, и поидоша противу соб<small>Ђ</small>; и бысть с<small>Ђ</small>ча зла, падоша мноз<small>Ђ</small>, и одол<small>Ђ</small> Изяславъ, Святоставъ, Всеволодъ, а Всеславъ б<small>Ђ</small>жа. Посемъ же, м<small>Ђ</small>сяца іюня вь 10 день, Изяславъ, Святославъ и Всеволодъ целовавше крестъ честный кь Всеславу, рекше: &laquo;приди к нама, а не створимъ ти зла&raquo;; онъ же над<small>Ђ</small>яся ц<small>Ђ</small>ловании креста, пере<small>Ђ</small>ха в лодьи чрес<small>Ђ</small> Дн<small>Ђ</small>пръ. Изяславу же в шатеръ предъидущю, и тако яша Всеслава на Рши, у Смоленьска, преступивше крестъ. Изяславъ же приведе Всеслава Кыеву, и вьсадиша и в порубъ съ дв<small>Ђ</small>имя сынънома.</p>\r\n<p>В л<small>Ђ</small>то 6576. Придоша иноплеменьници на Рускую землю, Половци мноз<small>Ђ</small>, Изяславъ же, и Святославъ и Всеволодъ изиидоша противу имъ на Льто; и бывши нощи, поидоша противу соб<small>Ђ</small>, гр<small>Ђ</small>хъ ради нашихъ попусти Богъ на ны поганыя, и поб<small>Ђ</small>диша Половци. Наводить Богъ по гн<small>Ђ</small>ву своему иноплеменьники на землю, и тако скрушенымъ имъ вьспомянуться к Богу; усобная же рать бываеть отъ сважения дьяволя. Богъ бо не хощеть зла вь челов<small>Ђ</small>ц<small>Ђ</small>хъ, но блага; а дьяволъ радуеться злому убийству кровопролитью, вьздвигая свары, зависти, братоненавид<small>Ђ</small>ния, клеветы. Земли же согр<small>Ђ</small>шивши котор<small>Ђ</small>й любо, то казнить Богъ смертью, или гладомъ, наведениемь поганых, или вредомъ, или гус<small>Ђ</small>ницею, или ин<small>Ђ</small>ми казньми, аще ли покаавшеся будемъ в немже ны Богъ велить быти.</p>\r\n<p>Глаголеть бо намъ Пророкомъ: обратитеся ко мн<small>Ђ</small> всимъ серд. цемъ вашимъ, постомъ и плачемь. Да аще сице творимъ, всихъ гр<small>Ђ</small>хъ прощени будемь, но мы на злое взвращаемься, аки свинья в кал<small>Ђ</small> гръховьнемь присно валяющеся и тако пребываемь. Т<small>Ђ</small>мже и Пророкомъ намъ глаголеть разумъхъ, рече, яко жестокъ еси, и шия желъзна выя твоя, того ради удержахъ отъ васъ дождь, предълъ единъ одождихъ, а другаго не одождихъ, исьше, и поразихъ вы зноемь и различными казньми; то и тако не обратитеся ко мн<small>Ђ</small>. Сего ради винограды ваша, и смоквие вае нивы и добравы ваша истьрохъ, глаголеть Господь а злобъ вашихъ не могохъ истерти, послахъ на вы различныя бол<small>Ђ</small>зни и смерти тяжькы, и на скот<small>Ђ</small> ихъ казнъ свою послахъ то и тако не обратистеся ко мнъ, но р<small>Ђ</small>сте, мужаимъся. Докол<small>Ђ</small> не насы тистеся злобъ вашихъ? Вы бо уклонистеся отъ пути моего глаго леть Господъ, съблазнисте многы сего ради свидитель скоро на противныя на прелюбод<small>Ђ</small>йца<sub>;</sub> и на кленущаяся именемъ моимъ во лжю и на лищающая мьзды наимника и насиль ствующе сирот<small>Ђ</small> и вдовици, и на укланяющая судъ криво. Почто не въздръжастеся вь гр<small>Ђ</small>с<small>Ђ</small>хъ вашихъ, но уклонисте законы моя и не схранисте ихъ? И обратитеся ко мн<small>Ђ</small>, и обращюся кь вамъ, глаголеть Господь, и азъ отвергу вамъ хляби небесныя, и възвра щю отъ васъ гн<small>Ђ</small>въ свой, дондеже все обилуеть вамъ, и не могуть изнемощи виногради ваши и нивы Но вы отяжасте на мя словеса ваша, глаголюще суетнъ работая Богу т<small>Ђ</small>мже усты чтуть мя а сердце ваше далече отстоитіь отъ мене, глаголеть Господъ Того ради ихже просимъ и не улучимъ будуть бо, рече, егда призоветь мя и азъ не послушаю васъ, взищете мене злии, и не обрящете; не вьсхот<small>Ђ</small>ша бо ходи [ти] по путемь моимъ, да того ради затворяеться небо, ово ли зл<small>Ђ</small> отв<small>Ђ</small>рзаеться, градъ в дождя м<small>Ђ</small>сто пущая, ово ли сланою плоды узнабляя и земьлю зноемъ томя нашихъ ради гр<small>Ђ</small>хъ Аще ли ся покаемъ о злобахъ своихъ, то аки чадомъ своимъ подасть намъ вся прошения, и одождить намъ Дождь ранъ и позденъ, и наполняться гумна ваша пшеници, и прольються точила виньная и маслиньная, и вьздамъ вамъ за л<small>Ђ</small>та, яже пояша прузи и хрустове, и гусиница; сила моя великая, юже послахъ на вы, глаголеть Господъ Вседержитель. И си слышаще вьстягнемся отъ зла на добро, вьзищете суда, избавите обидимаго; на покаяние придемь, не вьздающе зла на зло, и ни клеветы за клевету, но любовию прил<small>Ђ</small>пимся Господ<small>Ђ</small> Боз<small>Ђ</small> нашемъ, постомъ и рыданиемь, слезами омывающе вся прегр<small>Ђ</small>шения, не словомъ, нар<small>Ђ</small>чающеся крестьани, а поганьски живуще. Се бо не поганьски [ли] живемь, аще вьсрящемь в<small>Ђ</small>рующе? Аще бо кто усрящеть чернорисца, то вьзвращаеться, или единець, или свинью; то не поганьскии ли есть се? Се бо по дьяволю научению кобъ сию держать, друзии же к чіханию в<small>Ђ</small>руеть, же бываеть на здравье голов<small>Ђ</small>. Но сими дьяволъ льстить, и другыми нравы всякыми льстьми, превабляеть ны отъ Бога, трубами, скомрахы, и гусльми, и русальями. Видимъ бо игрища утолочена, и людий множьство на нихъ, яко упихати начнуть другъ друга, позоры д<small>Ђ</small>юще отъ б<small>Ђ</small>са замышленаго д<small>Ђ</small>ла; а церкви стоять, егда же бываеть годъ молитвы, мало ихъ обр<small>Ђ</small>таеться вь церкви. Да сего ради казни приемлемь отъ Бога всякыя, нахожение ратныхъ, по Божью повелению приемлемь казнь гр<small>Ђ</small>хъ ради нашихъ.</p>\r\n<p>И мы же на предлежащее возвратимся.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6574 [1066] Ростислав сидів у Тмуторокані і збирав дань у касогів і в інших народів. Греки так устрашилися цього, що послали підступного котопана. Коли він прийшов до Ростислава, то ввійшов до нього в довір\'я, і шанував його Ростислав.</p>\r\n<p>Одного разу, коли Ростислав пирував із дружиною своєю, котопан сказав: &laquo;Княже! Хочу випить за твоє здоров\'я. Тому князь сказав: &laquo;Пий&raquo;. Він же випив половину чаші, а половину дав випити князеві, доторкнувшись пальцем у чашу, бо мав під нігтем розчинну отруту смертельну і дав князеві, прирікши на смерть через сім днів. Князь випив, а котопан прийшов у Корсунь і звістив, що в цей день помре Ростислав, так і сталося. Цього ж котопана люд корсунський побив камінням. А був Ростислав прекрасний воїн, віку хорошого, лицем гожий, до убогих милостивий. Помер місяця лютого третього дня і похоронили його в церкві святої Богородиці.</p>\r\n<p>В літо 6575 [1067] почав міжусобну війну Всеслав, син Брячеслава, полоцький і зайняв Новгород. Троє Ярославичів - Ізяслав, Святослав і Всеволод,- об\'єднавши воїв, пішли на Всеслава суворої зими. І прийшли до Мінська, а мінчани закрилися в городі. Тоді брати взяли Мінськ, перебили мужів, а жон і дітей взяли на щити і пішли до [річки] Неміги. І Всеслав вийшов проти них. І зіткнулися обидва [війська] на Немізі третього березня, і пішли одне проти другого. І були сніги глибокі, і була січа люта, і багато загинуло, і здолали Ізяслав, Святослав і Всеволод, а Всеслав утік.</p>\r\n<p>Після цього десятого червня Ізяслав, Святослав і Всеволод на чесному хресті поклялися Всеславу і сказали: &laquo;Приходь до нас, не зробимо тобі зла&raquo;. Він же, повіривши у клятву на хресті, переїхав у лодії через Дніпро. Коли ж Ізяслав, йдучи попереду, вів до свого шатра Всеслава, того схопили на Рші біля Смоленська, порушивши клятву на хресті дану. Ізяслав же привів Всеслава до Києва і посадив його у поруб із двома синами.</p>\r\n<p>В літо 6576 [1068] прийшли іноплемінці на Руську землю - сила-силенна половців. Ізяслав, Святослав і Всеволод вийшли проти них на Альту, і коли настала ніч, пішли вони один на одного. За гріхи наші наслав Бог на нас поганих, і перемогли половці. У гніві своєму наводив Бог чужинців на землю [нашу], і тільки після поразки від них згадали про Бога. На міжусобні ж війни спокушає диявол. Адже Бог не бажає зла людям, а добра. А диявол радіє зі злого вбивства і кровопролиття, збуджує чвари і заздрощі, братоненависництво, наклепи. А коли ж у гріх впадає якась країна, то карає бог смертю, або голодом, або нашестям поганих, або засухою, або гусінню, або іншими карами, щоб ми покаялися, і в каятті нам Бог велить жити.</p>\r\n<p>Передають-бо нам пророки [слова Бога]: &laquo;Верніться до мене всім серцем вашим, постом і плачем&raquo;. І коли так чинити будемо, простяться нам всі гріхи. Але ми у злі крутимось, подібно свиням у калі гріховному вічно барложимося і так живемо. Через тих же пророків [Бог] нам говорить: &laquo;Я знав,- каже,- що впертий ти є, твоя ж шия - то м\'язи залізні, і через те не допустив до вас дощу, одне місце оросив, а друге не оросив, висушив і покарав вас спекою і різними карами, і то ви не вернулися до мене. Через те виногради ваші, і смоківниці ваші, ниви і діброви ваші знищив я,- говорить Господь.- А злоби вашої не зміг витравити. Наслав на вас різні хвороби і смерті тяжкі, і на скотину вашу кару свою послав, але й тоді ви не вернулися до мене, а сказали: &laquo;Мужаємось&raquo;. Допоки не насититеся злобами вашими? Адже ви ухилилися зі шляху мого,- говорить Господь,- і спокусили багатьох&raquo;, а тому: &laquo;Свідчить скоро буду проти ворогів, на перелюбців, і на тих, що, брешучи, клянуться іменем моїм, і на тих, що не платять за працю наймитам, обижають сиріт і вдів, і суд схиляють на несправедливість. Чому не утримуєтесь від гріхів ваших, але перекручуєте закони мої і не дотримує теся їх? Зверніться до мене, і я звернуся до вас,- говорить Господь,- і я розкрию вам безодні небеснії, і від верну від вас гнів свій, доки не матимете всього в достатку, і не зможуть виснажитися виноградники і ниви ваші. Але ви обтяжуєте мене словами своїми, говорячи: &laquo;Суєтно служить Богу&raquo;. А тому: &laquo;Вуста шанують мене, а серце ваше далеко від мене&raquo;,- говорить Господь. Через те що просимо - не одержуємо. &laquo;А буде,- говорить,- що покличете мене - і я не послухаю вас, шукатимете мене в біді - і не знайдете, бо не побажали іти шляхом моїм&raquo; а через те і закривається небо або зло розверзається, град замість дощу пускає або морозом плоди поб\'є і землю спекою виснажить через гріхи наші. А коли покаємося в злодійствах своїх, то, як чадам своїм, &laquo;дасть на спасіння і зішле вам дощу, дощу раннього й пізнього, і наповняться токи пшеницею, і переливатимуться чавильні вином і оливою, і надолужу Я вам за ті роки, що пожерла сарана, і коник, і гусінь. Моє військо велике, що я посилав проти вас&raquo; - говорить Господь Вседержитель. І, це чуючи, ухиляймося від зла до добра, вимагаймо справедливого суду, заступаймось за покривдженого; покаймося і не будемо воздавати злом за зло і наклепом за наклеп, а любов\'ю пригорнемося до Господа Бога нашого, постом і плачем, сльозами омиваючи всі прогрішіння наші, щоб не на словах називалися християнами, живучи по-поганськи. Хіба не як погани живемо, коли віримо в зустріч? Бо коли хто зустріне чорноризця або пустинника чи свиню, то вертається назад. Хіба це не по-поганськи? Адже се за диявольським підмовленням віримо в прикмети, а інші у чхання вірять, що воно на здоров\'я голові. Але цими і іншими способами диявол спокушає, всілякими хитрощами зваблює нас від Бога, трубами, скоморохами, і гуслями, і русаліями. Адже бачимо ігрища утоптані і людей безліч на них. А як почнуть давити один одного, видовища робить - бісом задумані діла,- а церкви стоять, навіть коли настає час молитви, мало їх знаходиться в церкві. От через це і кари різні приймаємо від Бога, військові нашестя, за Божим повелінням кари приймаємо через гріхи наші.</p>\r\n<p>Але повернемося до попереднього [повіствування].</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Изяславу же со Всеволодомъ Кыеву пришедшю, а Святославу Чернигову, и людье Кыевьстии приб<small>Ђ</small>гоша Кыеву, и створивше в<small>Ђ</small>че на торговищи, и р<small>Ђ</small>ша пославшеся ко князю: &laquo;се Половци росулися по земли; да вдай, княже, оружья и кони, и еще бьемься с ними&raquo;. Изяславъ же сего не послуша. И начаша людье говорити на воеводу на Коснячь, и идоша с в<small>Ђ</small>ча на гору, и придоша на дворъ Коснячьковъ, и не обр<small>Ђ</small>тоша его, у двора сташа Брячьславля и р<small>Ђ</small>ша: &laquo;поидемь, высадимь дружину ис погреба&raquo;. И разд<small>Ђ</small>лишася на двое: и половина ихъ иде кь погребу, а половина иде по мосту; сии же идоша на княжь дворъ. Изяславу с<small>Ђ</small>дящу на сен<small>Ђ</small>хъ с дружиною своею, и начаша пр<small>Ђ</small>тися сь княземь. Стояще дол<small>Ђ</small>, а кьнязю изо оконца зрящю, и дружин<small>Ђ</small> стоящи у князя, рече Тукы, Чюдиновъ братъ, Изяславу: &laquo;видиши, княже, людье вьзвыли; посли, ать блюдуть Всеслава&raquo;. И се ему глаголющю, и другая половина людий приде отъ погреба, створивше погребъ, и р<small>Ђ</small>ша дружина князю: &laquo;се зло есть; посли ко Всеславу, ать призвавше ко оконьцю, и проньзнути и мечемь&raquo;; и не послуша сего князь. Людье же кликнуша и идоша к порубу Всеславлю; Изяславъ же се видивъ, со Всеволодомь поб.<small>Ђ</small>госта с двора, людье же выс<small>Ђ</small>коша Всеслава ис поруба, въ 15 день сентября, и поставиша и сред<small>Ђ</small> двора княжа, и дворъ княжь разъграбиша, бещисленое множьство злата и сребра, и кунами и скорою. Изяславъ же б<small>Ђ</small>жа в Ляхы. Посемъ же Половцемъ воюющимъ по земли Руст<small>Ђ</small>й, а Святославу же сущю в Чернигов<small>Ђ</small>, а Половцемь вою[ю] щимъ около Чернигова, Святославъ же собравъ дружину, и н<small>Ђ</small>колико ихъ изыиде на ня ко Сновьску. Но узр<small>Ђ</small>ша Половци идуща въя, и пристрояшася противу; и видивъ Святославъ множьство ихъ, и рече дружин<small>Ђ</small> своей: &laquo;потягнемъ, уже намъ нельз<small>Ђ</small> камо ся д<small>Ђ</small>ти&raquo;, и удариша вь кон<small>Ђ</small>; и одол<small>Ђ</small> Святославъ вь тр<small>Ђ</small>хъ тысящахъ, а Полов<small>Ђ</small>ць 12 тысящь, и тако изби я, и друзии потопоша вь Снъви, а князя ихъ руками яша, вь 1 день ноября, и вызвратися с поб<small>Ђ</small>дою вь градъ свой Черниговъ Святославъ. Всеславъ же с<small>Ђ</small>де в Кыев<small>Ђ</small>. Се же Богъ яви крестьную силу, понеже Изяславъ ц<small>Ђ</small>ловавъ крестъ, и я и; т<small>Ђ</small>мже наведе Богъ поганыя, сего же яв<small>Ђ</small> избави крестъ честьный; вь день бо Вьздвижения Всеславъ и въздохнувъ рече: &laquo;о кресте честный! понеже к тоб<small>Ђ</small> в<small>Ђ</small>ровахъ, избави мя отъ рова сего&raquo;. Богъ же показа силу крестьную на показание земли Руст<small>Ђ</small>й, да не преступають честнаго креста, ц<small>Ђ</small>ловавше его; аще ли кто преступить, то и сд<small>Ђ</small> приимуть казнь, и на преидущемь в<small>Ђ</small>ц<small>Ђ</small> казнь в<small>Ђ</small>чную. Понеже велика есть сила крестьная: крестомъ бо поб<small>Ђ</small>жени бывають силы б<small>Ђ</small>совьскыя, крестомъ бо Господь княземъ пособить в бранехъ, крестомъ огражени в<small>Ђ</small>рнии челов<small>Ђ</small>ци поб<small>Ђ</small>жають супостаты противныя, крестомъ бо вьскор<small>Ђ</small> избавляеть отъ напасти, призывающимъ его с в<small>Ђ</small>рою. Ничтоже ся б<small>Ђ</small>си бояться, токмо креста; аще бо бываеть отъ б<small>Ђ</small>совъ м<small>Ђ</small>чтание, знаменавъше лице крестомь прогоними бывають. Всеславъ с<small>Ђ</small>де вь Кыев<small>Ђ</small> м<small>Ђ</small>сяць 7.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Коли Ізяслав із Всеволодом прийшли до Києва, а Святослав до Чернігова, то люд київський збігся в Києві, і влаштували віче на торговищі, і сказали, звернувшись до князя: &laquo;От половці розсипалися по землі, так видай, княже, оружжя і коні, і ми ще поб\'ємося з ними&raquo;. Ізяслав же до цього не прислухавсь. І стали люди намовлять на воєводу Коснячка, і, повертаючись із віча на гору, вони прийшли на двір Коснячків, і не знайшли його, і зупинились біля двору Брячислава і сказали: &laquo;Підемо і звільнимо з темниці дружину&raquo;. І розділилися на дві групи: і пішла половина їх до темниці, а половина пішла по мосту, і оці прийшли на княжий двір. Ізяслав у цей час на сінях раду радив із дружиною своєю, і ті, що зупинились внизу, стали спорити з князем. А князь дивився із віконця, і дружина стояла біля князя, і сказав Тука, Чудинів брат, Ізяславу: &laquo;Бачиш, княже, люди галасують, пошли, щоб краще стерегли Всеслава&raquo;. І коли говорив йому, прийшла і друга половина людей від темниці, відчинивши погріб. І сказала дружина князеві: &laquo;Це біда. Пошли до Всеслава, хай прикличуть його до віконця і заколять його мечем&raquo;. І не пристав на це князь. Люди ж загукали і пішли до темниці Всеславової. Івяслав все це бачив і разом із Всеволодом утік із двору. Люди ж вирубали Всеслава з поруба п\'ятнадцятого вересня і привели його посеред двору княжого, а двір княжий розграбували - незліченну кількість золота і срібла, і в монетах, і у злитках. Ізяслав же втік у Ляхи.</p>\r\n<p>Після цього, коли половці чинили розбій по землі Руській, а Святослав був у Чернігові, половці почали розбійничать побіля Чернігова, Святослав зібрав дружину, і частина її виступила проти них біля Сновська. Але коли половці побачили, що йдуть вої, то приготувались до бою. І Святослав побачив, що половців безліч, і сказав дружині своїй: &laquo;Вдаримо, бо нікуди уже нам дітися&raquo;. І пришпорили коней, і здолав Святослав, маючи три тисячі, дванадцять тисяч половців, і так розбив їх, що деякі потопилися в Снові, а князів їхніх власними руками в полон узяв першого листопада, і повернувся Святослав з перемогою у град свій Чернігів.</p>\r\n<p>Всеслав же сидів у Києві. Це Бог з\'явив силу хреста, оскільки Ізяслав цілував хрест Всеславу і захопив його. Через те навів Бог поганих, Всеслава ж явно визволив хрест чесний, бо в день Воздвиження Всеслав зітхнув і сказав: &laquo;О хрест чесний! Оскільки я в тебе вірив, ти і визволив мене із цього рову&raquo;. Бог же показав силу хресну на науку землі Руській, щоб не порушували клятву дану на хресті, цілуючи його. І коли хто порушить, то і тут прийме кару, і на тому світі матиме кару вічну. Бо велика сила хрестова: хрестом-бо перемагають сили бісівські, хрестом-бо Господь князям у битвах пособляє, хрестом хранимі віруючі люди супостатів-ворогів перемагають, хрестом від напасті швидко звільняються ті, хто з вірою до нього звертається. Нічого біси не бояться, тільки хреста. А коли в людей з\'являються бісівські видіння, осінивши лице хрестом, проганяють їх. Всеслав сидів у Києві сім місяців.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>',NULL,1851,5,1,NULL,25);
INSERT INTO `publications_publication` VALUES
(4709,'P','2008-12-20',0,NULL,'Note','Повість врем\'яних літ Роки 6522 [1014] — 6544 [1036].','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В літо 6522 [1014]. Коли Ярослав був у Новгороді, згідно уроку давав він з року в рік дві тисячі гривен Києву, а тисячу гривен роздавав [дружині] в Новгороді. І так давали всі посадники новгородськії.</span></span></p>','<p><a href=\"http://izbornyk.org.ua/pvlyar/yar05.htm\">Роки 6522 [1014] &mdash; 6544 [1036].</a></p>\r\n<p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Въ л<small>Ђ</small>то 6522. Ярославу сущу въ Нов<small>Ђ</small>город<small>Ђ</small>, и урокомъ дающю 2000 гривенъ отъ года до года Кыеву, а тысячю Нов<small>Ђ</small>город<small>Ђ</small> гривенъ раздаваху: и тако даху вси посадниц<small>Ђ</small> Новыородстии, а Ярославъ поча сего не даяти Кыеву отцю своему. И рче Володимиръ: &laquo;теребите путь и мосты мостите&raquo;, хотяще бо ити на Ярослава, на сына своего, но разбол<small>Ђ</small>ся.</p>\r\n<p>В л<small>Ђ</small>то 6523. Хотящю ити Володим<small>Ђ</small>ру на Ярослава, Ярославъ же посла за море, и приведе Варягы, бояся отца своего; но Богъ не дасть дьяволу радости. Володимеру же разбол<small>Ђ</small>вшюся, в се же время бяше у него Борисъ; а Печен<small>Ђ</small>гомъ идущимъ на Русь и посла противу имъ Бориса, а самъ боляше велми, в ней же болести и скончася м<small>Ђ</small>сяца юля въ 15 день. Умре же Володимиръ, князь великый, на Берестовъмь, и потаиша и, б<small>Ђ</small> бо Святополкъ в Кыев<small>Ђ</small>. И нощью же межи кл<small>Ђ</small>тми проимавъше помостъ, и ковьр<small>Ђ</small> опрятавши, и ужи св<small>Ђ</small>сиша и на землю, и възложивъша и на сани, и везоша, и поставиша вь свят<small>Ђ</small>[й] Богородиці в церкви, юже б<small>Ђ</small> самъ создалъ. Се же увид<small>Ђ</small>вше людье, и снидошася бещисла и плакашася по немь, бояре акы заступника земли ихъ, убозии акы заступника и кормителя; и вложиша и вь гроб<small>Ђ</small> мраморяни, спрятавше т<small>Ђ</small>ло его с плачемь великимъ, блаженаго князя. Се есть новы Костянтинъ великаго Рима, иже крестивъся самъ и люди своа: и тако сий створи подобьно ему. Аще бо б<small>Ђ</small> преже в поганьств<small>Ђ</small> и на скв<small>Ђ</small>рную похоть желая, но посл<small>Ђ</small>ди прил<small>Ђ</small>жа к покаянью, якоже в<small>Ђ</small>щаще Апостолъ: идеже умножися гр<small>Ђ</small>хъ, ту изобильствуеть благодать. Аще бо пр<small>Ђ</small>же в нев<small>Ђ</small>жьств<small>Ђ</small> етера быша сгр<small>Ђ</small>шения, посл<small>Ђ</small>ди же расыпашася покаяньемь и милостынями. Якоже глаголеть: в чемъ тя застану, в томъ ти и сужю, якоже Пророкъ глаголеть: живъ азъ Адана Господь, якоже не хощю смерти гр<small>Ђ</small>шника, [но] якоже обратитися ему отъ пути своего и живу быти; обращениемь обратися отъ пути своего злаго. Мнози бо праведнии творяще и по правд<small>Ђ</small> живуще, и кь смерти совращаються правого пути и погыбають; а друзии развращено пребывають, и кь смерти вьспомянуться, и покаяньемь добрымъ очистять гр<small>Ђ</small>хи. Якоже Пророкъ глаголеть: праведный не возможе спастися въ день гр<small>Ђ</small>ха его. Егда рекуть прав<small>Ђ</small>дному: живъ будеши, сьи же уповаеть правдою своею, ти сотворить безаконье, вся правда его не въспомянеться в неправд<small>Ђ</small> его, юже створи, и в ней умреть. И егда рекуть нечестивому: смертию умреши, ти обратиться отъ пути своего, и створить судъ и правду, и заимъ судъ лъжю отдаеть, и вьсхищение възвратить, вси гр<small>Ђ</small>си его, яже сгр<small>Ђ</small>шилъ есть, не помянеться, яко судъ и правду створилъ есть, и живъ будеть в нихъ; комужьдо васъ сужю по пути его, доме Израилевъ! Сый же умеръ во испов<small>Ђ</small>дании добрьмь, покааньемъ расыпа гр<small>Ђ</small>хы своя, милостынями, иже есть паче всего добр<small>Ђ</small>й. Милости бо хощю а не жертв<small>Ђ</small>. Милостыня бо есть всего луче и вышьше, възводящи до самаго небеси предъ Богъ. Якоже ангелъ Корнильеви рече: молитвы твоя и милостыня твоя взийдоша в память предъ Богомъ. Дивно есть се, колико добра створи Руской земли, крестивь ю. Мы же, крестьяни суще, не вьздаемь почестья противу оного възданью. Аще бы онъ не крестилъ насъ, то и нын<small>Ђ</small> быхомъ был<small>Ђ</small> въ прельсти дьявол<small>Ђ</small>, якоже и прародители наши погибнуша. Да аще быхомъ и мы потщание и молбы приносити к Богу за нь, въ день преставления его, видя бы Богъ тщание наше къ нему, прославилъ бы и: намъ бо достоить Бога молити за нь, понеже т<small>Ђ</small>мь Бога познахомъ. Нъ дай ти Господь по сердцю твоему, и вся прошения твоя исполни, егоже желаше царства небеснаго; даждь ти Господь в<small>Ђ</small>н<small>Ђ</small>ць с прав<small>Ђ</small>дными, в пищи райстий весельи ликъствованье сь Аврамомъ и с прочими патр<small>Ђ</small>архы; якоже Соломонъ рче: умершю прав<small>Ђ</small>дному, не погибнеть упованье. Сь бо в память держать Рустии людье, поминающе святое крещение, и прославляюще Бога вь молитвахъ и вь пъсн<small>Ђ</small>хъ и въ псалмихъ, поюще Господеви новии людье, просв<small>Ђ</small>щени Духомъ Святымь, чающе надежда великаго Бога Спаса нашего Ісуса Христа, вьздати комуждо противу трудомъ неизреченьную радость, юже буди улучити всимъ крестьяномъ.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6522 [1014]. Коли Ярослав був у Новгороді, згідно уроку давав він з року в рік дві тисячі гривен Києву, а тисячу гривен роздавав [дружині] в Новгороді. І так давали всі посадники новгородськії. А Ярослав почав цього не давати Києву, батькові своєму. І сказав Володимир: &laquo;Розчищайте дорогу і мости мостіть&raquo;,- бо хотів іти на Ярослава, сина свого, та розхворівся.</p>\r\n<p>Року 6523 [1015]. Хотів іти Володимир на Ярослава, Ярослав же послав за море і привів варягів, боячись батька свого. Але Бог не дав дияволу радості. Володимир розхворівся, в той час у нього був [син його] Борис, а печеніги пішли походом на Русь. Володимир послав супроти них Бориса, а сам розхворівся ще дужче. Від цієї хворості і помер в липні місяці, у п\'ятнадцятий день. Помер же Володимир, великий князь, на Берестові, і смерть його приховали, бо був у Києві Святополк. І вночі розібрали поміст між клітями, і в килим загорнули, і рідні спустили на землю його, і на сани возложили його, і повезли, і поставили його в [церкві] святої Богородиці, яку він сам і збудував. Та люди довідалися про це, зійшлися без ліку і плакали за ним: бояри як за заступником землі їхньої, а бідні як за своїм заступником і кормильцем. І поклали його в гріб мармуровий, і поховали тіло його, блаженного князя, з плачем великим.</p>\r\n<p>Це новий Костянтин великого Рима, що охрестився сам і людей своїх [охрестив]. Так і Володимир зробив подібно йому. Якщо раніше був у поганстві і хтивих насолод бажав, то потім великих успіхів зажив у покаянні за заповідями апостольськими: &laquo;Де умножався гріх, там ряснітиме благодать&raquo;. Бо коли раніше у невігластві деколи було согрішав, то пізніше розсипався покаяннями і милостинями, Як сказано: &laquo;У чому тебе застану, в тому тебе і судитиму&raquo;. Як говорить пророк: &laquo;Живий я, Аданай Господь, бо не хочу смерті грішника&raquo;. Але як зійти йому із шляху свого і живим залишитися? Хрещенням повернешся із шляху свого гріховного. Бо багато праведників, що по правді живуть і роблять, перед смертю збиваються правдивого шляху і гинуть. А інші в розпутстві живуть, а перед смертю одумуються і покаянням добрим від гріхів очищаються. Як каже пророк: &laquo;Праведний не зможе врятуватися в день согрішіння&raquo;. Коли кажуть праведному: &laquo;Живий будеш&raquo;,- то він повірить у правдивість свою і зробить тобі беззаконня, і вся правда його загине в неправді його, яку вчинив, і в ній помре. І коли кажуть нечестивому: &laquo;Смертю помреш&raquo;,- то він відійде з шляху свого, і вчинить суд і правду, і те, що брав у борг і обманом, суд простить, і захоплене поверне, всі гріхи його, що грішив, не пом\'януться, оскільки суд і правду вчинив сам і жити буде в них. Кожному з вас присуджу дорогу його, доме Ізраїлів! Володимир же помер у сповіданні доброму, покаянням і милостинями розвіяв гріхи свої, що понад усе добре. &laquo;Милості бо хочу, а не жертв&raquo;. Милостиня бо всього найлучче і найвище, бо возводить до небес перед самого Бога. Як то ангел Корнилові сказав: &laquo;Молитви твої і милостині твої зійдуть у пам\'ять Богові&raquo;. Подиву гідно, скільки добра зробив він Руській землі, хрестивши її. Ми ж, ставши християнами, не воздаємо йому почестей, рівних його заслугам. Бо коли б не охрестив нас, то і нині були б в омані диявольській, в якій і прародичі наші загинули. І коли б ми мали старання і молитву за нього приносили Богу в день смерті його, Бог би Побачив нашу ревність до нього і прославив би його: адже нам належить молити за нього Бога, оскільки завдяки йому пізнали ми Бога. Нехай же Господь воздасть тобі за бажанням твоїм, і всі прохання твої здійснить, зокрема про царство небесне, якого бажав. Дасть тобі Господь вінець з праведними, змогу насолоджуватись райськими яствами і веселитися з Авраамом та іншими патріархами, як сказав Соломон: &laquo;Померлому праведнику не вмирає надія&raquo;. Ось чому й бережуть пам\'ять [про Володимира] руськії люди, згадуючи святе хрещення, і прославляють Бога в молитвах; і в піснях, і в псалмах співають Господеві новії люди, просвіщенні Духом Святим, сподіваючись надії великого Бога, Спаса нашого Ісуса Христа, який воздасть кожному за труди його невимовну радість, яку належить одержати всім християнам.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Святополкъ же с<small>Ђ</small>де в Киев<small>Ђ</small> по отци своемь, и созва Кыяны, и нача им<small>Ђ</small>ние имь даяти; а они приимаху, и не б<small>Ђ</small> сердце ихъ с нимъ, яко братья ихъ быша с Борисомъ. Борису же возвратившюся с войны, не обр<small>Ђ</small>тшю Печен<small>Ђ</small>гъ, в<small>Ђ</small>сть приде ему: &laquo;яко отець ти умерлъ&raquo;. И плакася по отци велми, любимь бо б<small>Ђ</small> отцемь паче всихъ; и ста на Алт<small>Ђ</small> пришедъ. Ръша ему дружина отня: &laquo;се дружина у тебе отня и вои: поиди, сяди в Кыев<small>Ђ</small> на стол<small>Ђ</small> отн<small>Ђ</small>&raquo;. Онъ же рче: &laquo;не буди то мн<small>Ђ</small> възняти рукы на брата на стар<small>Ђ</small>йшаго; аще отець ми умре, то сей ми будеть вь отца м<small>Ђ</small>сто&raquo;. И се слышавше вои, и разиидошася отъ него, Борисъ же стояше съ отрокы своими. Святополкь же исполнися, Каиновъ смыслъ приимъ, посылая к Борису, глаголя: &laquo;яко с тобою хощю любовь им<small>Ђ</small>ти, и к отню ти придамъ&raquo;, льстя под нимъ, како бы погубити. Святополкъ же приде нощью к Вышегороду, и отай призва Путщю и Вышегородьскыя боярьц<small>Ђ</small>, и рче имъ: &laquo;прияете ли мн<small>Ђ</small> всимъ сердцемь?&raquo; И рче Путьша: &laquo;можетъ головы сво<small>Ђ</small> съ Вышегородци положити&raquo;. Онъ же рче имъ: &laquo;не пов<small>Ђ</small>дите никомуже, шедше убийте брата моего Бориса&raquo;. Они же вьскор<small>Ђ</small> об<small>Ђ</small>щашася ему створити се. О сяковыхъ бо Соломонъ рче: скоры суть бес правды прольяти кровь; сбирають соб<small>Ђ</small> злая, ти бо об<small>Ђ</small>щаються крови; сихъ путье суть скончевающе безаконие, нечестиемь бо свою душю емлють. Послании же придоша [на Алту] нощью, и подъступиша ближе, и слышаша блаженаго Бориса поюща заутренюю; пов<small>Ђ</small>даша бо ему: &laquo;яко хотять тя погубити&raquo;. И вьставъ нача п<small>Ђ</small>ти, глаголя: Господи! что ся умножишася стужающии ми? вьстають на мя мнози. Пакы: яко стр<small>Ђ</small>лы твоя уньзоша во мн<small>Ђ</small>, яко азъ на раны готовъ и бол<small>Ђ</small>зни моя предо мною есть. И пакы глаголаше: Господи! услыши молитву мою, и не вниди в судъ с рабомъ твоимь, яко не оправдится предъ тобою всякъ живы, яко погна врагъ дущю мою. И кончавь ексапсалмы, и видивь, яко послании суть погубить его, и нача п<small>Ђ</small>ти псалътырь, глаголя: яко [обидоша мя] унци тучни, и сборъ злобныхъ ос<small>Ђ</small>де мя. Господи Боже мой, на тя уповахъ, и спаси мя и отъ всихъ гонящихъ избави мя. Посемъ же нача канунъ п<small>Ђ</small>ти; тако вь заутрьню помолися, зря на икону, глаголя на образъ Владычень: &laquo;Господи Ісусе Хриете, иже симь образомъ явися на земли спасения ради нашего, изволивый своею волею пригвоздити руци свои на крест<small>Ђ</small>, и приемь страсть гр<small>Ђ</small>хъ ради нашихъ, тако и мене сподоби прияти страсть; се же не отъ противныхъ приимаю, но отъ брата своего, и не створи ему, Господи, в семь гр<small>Ђ</small>ха&raquo;. И помолившюся ему, и вьзлеже на одр<small>Ђ</small> своемь. И се нападоша на нь, акы зв<small>Ђ</small>рье дивии около шатра, и насунуша и копьи, и прободоша Бориса, и слугу его, падша на немь, прободоша с нимь; б<small>Ђ</small> бо сьи любимъ Борисомъ. Бяше бо отрокъ съ родомъ Угринъ, именемъ Георгий, егоже любляше повелику Борисъ, б<small>Ђ</small> бо възложи на нь гривьну злату, в нейже престояше ему; избиша же отрокы многы Борисовы. Георгиеви же не могуще сняти вборз<small>Ђ</small> гривны сь ш<small>Ђ</small>и, и ус<small>Ђ</small>кънуша главу его, и тако сняша гривну ту, а главу отв<small>Ђ</small>ргъше прочь; т<small>Ђ</small>мже не обр<small>Ђ</small>тоша посл<small>Ђ</small>же т<small>Ђ</small>ла его вь трупьи. Бориса же убивше оканьнии, ув<small>Ђ</small>р<small>Ђ</small>вше и в шатеръ, и вьзложиша и на кола, повезоша и, еще дышющу ему. И увидивьше се оканьный Святополкъ, и яко еще ему дышющу, и посла два Варяга приконьчевати его. Он<small>Ђ</small>ма же пришедшима и видившима, яко еще ему живу сущю, и единъ ею извлекъ мечь и проньзе ю кь сердцю&raquo;. И тако скончася блаженый Борисъ, приимь в<small>Ђ</small>н<small>Ђ</small>ць отъ Христа Бога с прав<small>Ђ</small>дными, причтеся сь пророкы и съ апостолы, и с лики мученикы въдворяяся, Авраму на лон<small>Ђ</small> почивая, видя неизречьньную радость, вьсп<small>Ђ</small>вая съ ангелы и веселяся с ликы святыхъ. И положиша т<small>Ђ</small>ло его, принесоша и отай. Вышегороду, вь церкви святаго Василия. Оканьнии же убийци придоша кь Святополку, аки хвалу имуще, безаконьници. Суть же имена симъ законопреступникомъ: Путьша, Талець, Еловичь, Ляшько; отець же ихъ Сотона. Сици бо слугы б<small>Ђ</small>си бывають; б<small>Ђ</small>си бо на зло посылаеми бывають, а ангели на благое. Ангелъ бо челов<small>Ђ</small>ку зла не створяеть, но благое мислить ему всегда, пакы же крестьяномъ помогають, и заступають отъ супротивнаго врага; а б<small>Ђ</small>си на злое всегда ловять, завидяще ему, понеже видять челов<small>Ђ</small>ка Богомъ почьщена, и завидяще ему, и на зло слеми скори суть. Рче бо: кто идеть прелестить Ахава? И рче б<small>Ђ</small>съ: се азъ иду. Золъ челов<small>Ђ</small>къ тщиться на злое, не хужь есть б<small>Ђ</small>са; б<small>Ђ</small>си бо бояться Бога, а золъ челов<small>Ђ</small>къ ни Бога ся боить, ни челов<small>Ђ</small>къ стыдиться; б<small>Ђ</small>си бо креста Господня бояться, а золъ челов<small>Ђ</small>къ ни креста боиться. Т<small>Ђ</small>мъже и Давидъ глаголаше: аще воистину убо право глаголете, право судите, сынове челов<small>Ђ</small>чьстии; ибо вь сердци д<small>Ђ</small>лаете безаконие на земли, неправду рукы ваша спл<small>Ђ</small>тають; учюжени быша гр<small>Ђ</small>шници отъ ложеснъ, заблудиша отъ чрева, глаголюща лжю; ярость ихъ по образу змиину.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Святополк же сів у Києві після батька свого, і скликав киян, і став давати їм різні добра. І вони приймали, але серця їхні не були з ним, а з братом його, їхні серця були з Борисом. Коли Борис повертався з походу, не знайшовши печенігів, вість прийшла до нього: &laquo;Батько у тебе помер&raquo;. І гірко плакав за батьком, бо любив його батько більше за всіх. І зупинився, дійшовши до Альти. Сказала йому батьківська дружина: &laquo;Дружина ця у тебе і вої - батькові: піди і сядь у Києві на батьківському столі&raquo;. Він же сказав: &laquo;Не підніму руки на брата свого старшого. Якщо батько в мене помер, то цей мені буде замість батька&raquo;. І коли вої почули це, то розійшлися від нього. Борис же стояв зі своїми отроками. Святополк же намислив Каїнову справу і послав сказати Борисові: &laquo;Хочу з тобою любов мати, і до того, що батько дав, ще додам&raquo;. А сам брехав йому, думаючи, як його згубити. Святополк же вночі прийшов до Вишгорода і таємно покликав Путшу і вишгородських.бояр і запитав їх: &laquo;Чи віддані мені всім серцем?&raquo; І сказав Путша: &laquo;Можемо голови свої з вишгородцями покласти [за тебе]&raquo;. Він же сказав їм: &laquo;Не кажучи нікому й нічого, підіть і вбийте брата мого Бориса...&raquo; Вони ж пообіцяли йому негайно це виконать. Про таких Соломон сказав: &laquo;Швидкі підступно кров пролити. Збирають у злі собі подібних, а ті беруть обіцянку кров пролити. Такий шлях тих, що чинять беззаконня, бо безчестям душу свою виймають&raquo;. Послані ж прийшли на Альту вночі, і коли підійшли ближче, то почули, що блаженний Борис співає заутреню, бо сповістили йому: &laquo;Хочуть убити тебе&raquo;. І, вставши, почав співати, примовляючи: &laquo;Господи! За що умножаєш ворогів мені? Багато стає проти мене&raquo;. І ще: &laquo;Неначе стріли твої ввігналися в мене, та я до ран готовий і скорбота моя переді мною&raquo;. І ще говорив: &laquo;Господи! Вислухай молитву мою і не вступай в суд із рабом твоїм, бо ніхто з живих не оправдається перед тобою, бо переслідує ворог душу мою&raquo;. І закінчив ексапсалми, і побачив, що прислані прийшли убити його, і почав співати псалтир, промовляючи: &laquo;Наче оточили мене тельці жирнії, і скопище злих обступило мене. Господи Боже мій, на тебе уповаю і спаси мене і від всіх насильників заступи мене&raquo;. Потім почав співати канон. Так помолився в заутреню і сказав, дивлячись на ікону, на образ Владики: &laquo;Господи Ісусе Христе, що в цьому образі явився на землі спасіння ради нашого, власною волею дав прибити руки свої на хресті і прийняв страждання за гріхи наші. Так і мене сподоби прийняти страждання. Не від ворогів їх приймаю, а від брата свого, і не став йому, Господи, це за гріх&raquo;. І помолившись йому, возліг на постіль свою. І тут напали на нього як звірі дикі біля шатра, і просунули в нього списи, і прохромили Бориса, і слугу його, що прикрив тілом, прохромили з ним. Любив його Борис. Був той отрок родом угрин, на ймення Георгій. Дуже любив його Борис, бо возложив на нього гривну золоту, в якій він і служив йому. Перебили вони багато отроків Борисових. У Георгія ж не могли швидко зняти із шиї гривну, і відсікли голову його, і так зняли гривну ту, а голову відкинули геть. Тому опісля не знайшли тіла його серед трупів. Коли вбили Бориса, закрутили його окаянні в шатро, і поклали на повіз, і повезли його, а він ще дихав. І побачив це окаянний Святополк, що Борис ще дихає, і послав двох варягів прикінчити його. Коли вони прийшли і побачили, що він ще живий, то один із них видобув меч і ввігнав його в серце. І так помер блаженний Борис, прийнявши вінець від Христа Бога з праведниками, порівнявшись з пророками і апостолами, поселившись із сонмом мучеників починати на лоні Аврамовому, споглядати несказанну радість, співати з ангелами і веселитися зі святими. І поклали тіло його, яке тайно принесли до Вишгорода, в церкві святого Василія. А ті окаянні вбивці прийшли до Святополка, неначе похвалу заслужили, беззаконники. Імена ж цих законопереступників Путша, Талець, Єлович, Ляшко, а батько їхній - Сатана. Бо такі слугами бісівськими бувають, адже біси на зле посилаються, а ангели - на добре. Ангели-бо чоловікові зла не роблять, а добра йому завжди бажають, а особливо християнам помагають і захищають від супротивного ворога, а біси на зле завжди ловлять, заздрять чоловікові, оскільки бачать його Богом пошанованим і заздрять йому, а коли їх на зле посилають - швидкі на виконання. Питає ж: &laquo;Хто піде спокусить Ахава?&raquo; І біс відповідає: &laquo;Це я піду&raquo;. Злий чоловік старається на зле не гірше біса. Біси хоч Бога бояться, а злий чоловік ні Бога не боїться, ні людей не стидається. Біси хоч хреста господнього бояться, а злий чоловік і хреста не боїться. Про таких і Давид говорить: &laquo;Чи справді справедливо говорите, чи по правді судите, сини людськії, бо творите в серці беззаконня на землі, бо неправду руки ваші сплітають. Почавши від матернього лона, відступили беззаконники: блукають брехуни, як тільки зродила їх мати. Лють їхня подібна отруті зміїній&raquo;.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Святополкъ же оканьный помысли в себ<small>Ђ</small>, рекъ: уже убихъ Бориса; а еще како бы убити Гл<small>Ђ</small>ба?&raquo; И приимъ смысль Каиновъ, с лестью посла кь Гл<small>Ђ</small>бу, глаголя сице: &laquo;пойти вборьз<small>Ђ</small>, отець тя зоветь, не здоровить бо велми&raquo;. Гл<small>Ђ</small>бъ же вс<small>Ђ</small>дъ на конь, поиде с маломъ дружины, б<small>Ђ</small> бо послушливъ отцю. И пришедшю ему на Волгу, на пол<small>Ђ</small> потъчеся конь вь рв<small>Ђ</small>, и наломи ему ногу мало, и приде ко Смоленьску, и поиде отъ Смоленьска, яко зр<small>Ђ</small>има, и ста на Смядин<small>Ђ</small> в корабли. Въ се время пришла б<small>Ђ</small> в<small>Ђ</small>сть отъ Передьславы кь Ярославу о отни смерти, и посла Ярославъ кь Гл<small>Ђ</small>бу, глаголя: &laquo;не ходи; отець ти умерлъ, а брать ти убитъ отъ Святополка&raquo;. И се слышавъ Гл<small>Ђ</small>бъ, вьспи велми сь слезами, и плачася по отци, паче же и по брат<small>Ђ</small>, и нача молитися со слезами, глаголя: &laquo;увы мн<small>Ђ</small>, Господи! луче бы мн<small>Ђ</small> умрети с братомь, нежели жити вь св<small>Ђ</small>т<small>Ђ</small> семь прелестнемъ; аще бо быхъ вид<small>Ђ</small>лъ, брате, лице твое ангелское, умерлъ быхъ с тобою. Нын<small>Ђ</small> же, что ради остахъ азъ единъ? кде суть словеса твоя, яже глаголаше ко мн<small>Ђ</small>, брате мой любимый? нын<small>Ђ</small> уже не услышю тихаго твоего наказания. Да аще еси получилъ деръзновение у Бога, молися о мн<small>Ђ</small>, да и азъ быхъ ту же приялъ смерть; луче бы ми с тобою умрети, нежели вь св<small>Ђ</small>т<small>Ђ</small> семь прельстнемъ жити&raquo;. И сице ему молящюся сь слезами, и внезапу придоша послании отъ Святополка на погубленье Гл<small>Ђ</small>ба, и ту абье послании яша корабль Гл<small>Ђ</small>бовъ, и обнажиша оружья. И отроци Гл<small>Ђ</small>бовы уныша. Оканьный же Горяс<small>Ђ</small>ръ повел<small>Ђ</small> вборз<small>Ђ</small> зар<small>Ђ</small>зати Гл<small>Ђ</small>ба; поваръ же Гл<small>Ђ</small>бовъ, именемь Торчинъ, выньзь ножь зар<small>Ђ</small>за Гл<small>Ђ</small>ба. Аки ягня непорочно на жерътву Богови, вь воню благоухания, жерьтва словесная, и прія в<small>Ђ</small>н<small>Ђ</small>ць, вшедъ въ небесныя обители, и узр<small>Ђ</small> желаемаго брата своего, и радовашеся с нимь неизреченьною радостью, юже улучиста братолюбьемь своимь. Се коль добро и коль красно, еже жити братома вкуп<small>Ђ</small>! А оканни же възвратишася вьспять, якоже рче Давидъ: възвратишася гр<small>Ђ</small>шници въ адъ. И пакы: оружье изьвл<small>Ђ</small>коша гр<small>Ђ</small>шници, и напрягоша лукы своя истр<small>Ђ</small>ляти нища и убога, заклати правыя сердцемь; и оружье ихъ вниде вь сердца ихъ, и луци ихъ скрушаться; яко гр<small>Ђ</small>шници погибнуть, изъщезающе яко дымъ погибьнуть. Он<small>Ђ</small>мъ же пришедшимъ, пов<small>Ђ</small>дающимъ Святополку: &laquo;яко створихомъ повел<small>Ђ</small>ное тобою&raquo;. Онъ же се слыша, и вьзвеселися сердце его болма, и не в<small>Ђ</small>ды Давида глаголюща: что ся хвалиши о злоб<small>Ђ</small>, сильн<small>Ђ</small>? и безаконье умысли языкъ твой, яко бритва изострена створилъ есть лесть; вьзлюбилъ еси злобу паче благостыня, неправду неже глаголати правду; возлюбилъ еси вся глаголы потопныя, языкъ льстивъ; сего ради Богъ раздрушить тя до конца, и вьстерьгнеть тя отъ села твоего, и корень твой отъ земля живущихъ; якоже и Соломонъ рече: азъ вашей погибели посм<small>Ђ</small>юся, порадую же ся внегда грядеть на вы пагуба; т<small>Ђ</small>мъже сн<small>Ђ</small>дять своего пути плоды, и своея нечести насытяться Гл<small>Ђ</small>бу же убьену и пов<small>Ђ</small>ржену бывшю на брез<small>Ђ</small> межю дв<small>Ђ</small>и ма кладома, посемъ же вьземше и везоша и, и положиша брата своего Бориса у церкви святаго Василья. Совокуплен т<small>Ђ</small>лома, паче же и душама, у Владыкы всихъ Царя пребывающа, в радости бескон<small>Ђ</small>чн<small>Ђ</small>й, и вь св<small>Ђ</small>т<small>Ђ</small> неизреченьнемь, подающа иц<small>Ђ</small>ления дары Руськой земл<small>Ђ</small>, ин<small>Ђ</small>мь приходящимъ правою в<small>Ђ</small>рою даета иц<small>Ђ</small>ление: хромымъ ходити, сл<small>Ђ</small>пымъ прозр<small>Ђ</small>ние, болящимъ ц<small>Ђ</small>лбы, окованымъ раздр<small>Ђ</small>шение, печалнымъ ут<small>Ђ</small>ху, напастьнымъ избавление; и еста заступника Руськой земли, и св<small>Ђ</small>т<small>Ђ</small>лника сияюща выну и молящися выну ко Владыци о своихъ людехъ. Т<small>Ђ</small>мже и мы долъжни есме хвалити достойно страстотерпца Христова, молящеся прил<small>Ђ</small>жно к нима, рекуще: радуйтася, страстотерпца Христова, заступьника Руськой земли, еже иц<small>Ђ</small>ление подаета приходящимъ к вамъ в<small>Ђ</small>рою и любовью. Радуйтася, небесьная жителя, вь плоти ангела быста, единомыслено служителя, вьрьста единообразна, святымь единодушьна; т<small>Ђ</small>мъ стражющимь всимъ исц<small>Ђ</small>ление подаета. Радуйтеся, Борисе и Гл<small>Ђ</small>бе богомудрая, яко потока точита отъ кладязя водъ живоносныя; исц<small>Ђ</small>ления ист<small>Ђ</small>кають в<small>Ђ</small>рнымъ людемъ на иц<small>Ђ</small>ление. Радуйтася, луча св<small>Ђ</small>тозарная, и явистася яко св<small>Ђ</small>тил<small>Ђ</small> озаряюща всю землю Рускую, всегда тму отгоняща, являющася в<small>Ђ</small>рою неуклоньною. Радуйтася, недр<small>Ђ</small>маньная ока, стяжавша душа на св<small>Ђ</small>ршение, Божиихъ святыхъ запов<small>Ђ</small>дий приимша вь сердци своемь, блаженая. Радуйтася, брата<em> </em>вкуп<small>Ђ</small>, в м<small>Ђ</small>ст<small>Ђ</small>хъ златозарныхъ, в сел<small>Ђ</small>хъ небесныхъ, и вь слав<small>Ђ</small> неувядающей, еяже по достоянию сподобистася. Радуйтеся, Божьими св<small>Ђ</small>тлостьми яв<small>Ђ</small> облистаеми, всего мира обьходита, б<small>Ђ</small>сы отгоняюща, недугъ иц<small>Ђ</small>ляюща, св<small>Ђ</small>телъника предобрая и заступника теплая, суща сь Богомъ, блажеными лучами ражьждизаеми выну, добля страстьника, душа просв<small>Ђ</small>щающа в<small>Ђ</small>рнымъ людемь: вьзвысила бо есть ваю св<small>Ђ</small>тоносная небеса любы, т<small>Ђ</small>мь красныхъ всихъ насл<small>Ђ</small>одоваста вь небеснемь житьи, славу, и райскую пищю, св<small>Ђ</small>тъ разумный, и красная радости. Радуйтася, яко напаяющиа сердца, горести и болезнемь отгоняща, страсти злыя иц<small>Ђ</small>ляюща, каплями кровными святыми очервивьша багряницю, преславная; ту же красно носяща, съ Христомь царствуете всегда, молящеся за новыя люди христьянскыя и сродьникы своя, земля Руская благословися ваю кровью; и мощьми положениемь въ церкви, духомъ божествен<small>Ђ</small> просв<small>Ђ</small>щаете, в нейже с мученикы яко мученика за люди своя молита. Радуеться церквы, св<small>Ђ</small>тозарное солнце стажавши, вьсходъ всегда просв<small>Ђ</small>щаеть вь страданьи, вь славу ваю, мученикомъ. Радуйтася, св<small>Ђ</small>тл<small>Ђ</small>и зв<small>Ђ</small>зд<small>Ђ</small>, заутра вьсходящи. Ново христолюбивая заступника наша страстотерпца! покорита поганыя подъ ноз<small>Ђ</small> княземь нашимъ, молящася ко Владыц<small>Ђ</small> и Богу нашему и мирьно пребывати вь совокупьлении и вь зравьи, избавляюща отъ усобныя рати и отъ пронырства дьявола. Сподобита же и насъ, поющихъ и почитающихъ ваю честное торжество, вь вся в<small>Ђ</small>кы до скончания.</p>\r\n<p>Святополкъ же оканьный злый уби Святослава, пославь кь гор<small>Ђ</small> Угорьской, б<small>Ђ</small>жащу ему вь Угры; и нача помышляти: &laquo;яко избыо всю братью свою, и приму власть Рускую единъ&raquo;. Помысли высокоумьемь своимь, а не в<small>Ђ</small>ды, яко даеть [Богъ] власть, емуже хощеть; поставляеть царя и князя Вышьний, [емуже хощеть] дасть. Аще бо кая земля управиться предъ Богомъ, поставляеть царя и князя прав<small>Ђ</small>дна, любяща судъ и правду, и властемъ устраяеть судью правяща судъ. Аще бо князи правдиви бывають на земли, то много отъдаються согр<small>Ђ</small>шения; аще ли зли и лукави бывають, то болшее зло наводить Богъ на землю ту, понеже глава есть земли то; тако бо Исая рече: согр<small>Ђ</small>шиша отъ главы и до ногу, [еже] есть отъ царя и до простыхъ людий. Лют<small>Ђ</small> бо граду тому, в немже князь унъ, любя вино пити со гусльми и сь младыми св<small>Ђ</small>тникы. Сяковыя Богъ даеть за гр<small>Ђ</small>хы, а старыя мудрыя отъемлеть, якоже Исая глаголеть: отъиметь Господь отъ Ерусалима кр<small>Ђ</small>пость и кр<small>Ђ</small>пкаго исполина, и челов<small>Ђ</small>ка храбра, и судью, и пророка, и смирена старца, и дивна св<small>Ђ</small>тника, и мудра хытр<small>Ђ</small>ца, разумьна послушника; и поставьлю уношю князя имъ, и ругателя имъ обладающа ими.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Святополк же окаянний став думати про себе, міркуючи так: &laquo;Оце вже убив Бориса, а як би ще убити й Гліба?&raquo; І надумав каїнове діло, послав до Гліба таку брехню сказати: &laquo;Приїжджай якнайшвидше, батько тебе кличе, дуже хворий він&raquo;. Гліб же сів на коня і пішов із малою дружиною, бо був слухняний у батька. І коли він прийшов на Волгу, то в полі спіткнувся кінь його об рів і трохи пошкодив йому ногу. І прийшов до Смоленська, і пішов від Смоленська, і недалеко від нього на Смядині став у кораблі. У цей час прийшла звістка від Передслави до Ярослава про батькову смерть. І послав Ярослав сказати Глібові: &laquo;Не йди, батько твій помер, а брата убив Святополк&raquo;. І коли почув це Гліб, то дуже голосив у сльозах і плакав за батьком, а ще більше за братом, і почав молитися в сльозах, примовляючи: &laquo;О горе мені, Господи! Лучче б мені померти з братом, аніж жити в цьому світі підступному. Якби був побачив, брате, лице твоє ангельське, помер би з тобою. А тепер чого ради я залишився один? Де слова твої, що говорив мені, брате мій любимий? Тепер уже не почую тихого твого напучення, І якщо ти маєш можливість просить у Бога, то помолися за мене, щоб і я ту ж смерть прийняв. Лучче б мені з тобою померти, аніж у світі цьому підступному жити&raquo;. І коли він молився в сльозах, несподівано з\'явилися послані Святополком на вбивство Гліба. І тут несподівано послані захопили Глібів корабель і оголили оружжя. І отроки Глібові занепали духом. Окаянний же Горясир звелів негайно зарізати Гліба, а кухар Глібів, на ймення Торчин, ввігнав ніж і зарізав Гліба, як непорочне ягня в жертву Богові. Замість духмяного фіміаму - жертва людська. І прийняв вінець [царства небесного], і ввійшов у небесні обителі, і побачив жаданого брата свого, і радів із ким несказанною радістю, якої досяг братолюбством своїм. Як добре і як красно, що житимуть брати разом! А окаянні ж повернулися назад, як рече Давид: &laquo;Повернуться грішники в пекло&raquo;. І ще: &laquo;Оружжя вийняли безбожники і напнули луки свої стріляти в нужденних і вбогих, вбивати щирих серцем. І оружжя ввійде в серця їхні, і луки їхні потрощаться, і загинуть грішники, і щезнуть, як щезає дим&raquo;.</p>\r\n<p>Вони ж, окаянні, повернулися і звістили Святополкові: &laquo;Зробили, що ти повелів&raquo;. Він же, коли почув це, розвеселився серцем ще більше, не відаючи, що Давид сказав: &laquo;Чого хвалишся злодійством, сильний? І беззаконня замишляє язик твій, що гострий як бритва, вчинив обман&raquo;. Возлюбив ти злобу більше, ніж благодіяння, говорити неправду більше, ніж правду, розлюбив ти язиком брехливим всі слова згубні: через це Бог знищить тебе вщент, і відторгне тебе від села твого, і корінь твій від землі живущих, про що і Соломон рече: &laquo;Я з вашої погибелі посміюся і радітиму, як найде згуба на вас. За це їстимуть плоди вчинків своїх і переситяться нечистями своїми&raquo;.</p>\r\n<p>Гліб же був убитий і кинутий на березі між двома колодами. Після цього взяли його, і повезли, і поклали поруч брата Бориса у церкві святого Василія. І поєдналися вони тілами, а ще більше - душами, перебуваючи у Владики Царя всіх у радощах безкінечних і в світі несказанному, подаючи дари цілющі Руській землі, іншим прихожим праведною вірою даючи зцілення: кривим - ходити, сліпим - прозріння, хворим - одужання, закутим - звільнення, засмученим - утіху, занапащеним - визволення. Заступники вони Руської землі і світильники, вічно сіяючі і в молитвах про своїх людей до Владики. Ось тому і ми маємо гідно восхвалять страстотерпців Христових, молячись старанно до них, промовлючи: &laquo;Радуйтеся, страстотерпці Христові, заступники Руської землі, що зцілення подаєте тим, хто приходить до вас, вірою і любов\'ю. Радуйтеся, небеснії жителі, в плоті ангелів перебуваючі, однодумні служителі Богу, пара одностайна, святим єдинодушна,- тому і подаєте зцілення всім страждущим. Радуйтеся, Борисе і Глібе богомудрі! Ви виливаєтесь як потоки із джерела цілющих вод живодайних, що витікають для зцілення вірних людей. Радуйтеся, промені світосяйні, і з\'являйтеся як світила, що осявають всю землю Руськую, завжди тьму відганяючи і з\'являючи віру неухильну. Радуйтеся, очі недріманні! Душі свої ви схилили на звершення божих святих заповідей і прийняли їх в серця свої, о блаженні! Радуйтеся, брати, разом перебуваючи в місцях злотосяйних, у селах небесних і в славі нев\'янучій, якої гідно заслужили. Радуйтеся, божественним світлом для всіх осіянні! Ви весь світ обійшли, бісів відганяючи, від хвороб ізціляючи, світильники предобрі і заступники теплі, біля Бога сущі, блаженним промінням вічно осяяні, мужні страстотерпці, просвітники душ вірним людям. Звеличила вас світлоносна небесна любов, через неї успадкували ви всю красу небесного життя, славу і райські яства, світ розуму і красну радість. Радуйтеся, що напуваєте серця, горе і хвороби відганяєте, злі страждання ізціляєте; краплями крові святої окровавили багряниці преславнії, красно носячи їх, з Христом завжди царствуєте, молячись за нових людей християнських - співвітчизників своїх. Земля Руська благословилася кров\'ю вашою і мощами, покладеними в церкві; духом божественним її просвіщаєте, у ній же з мучениками, як мученики, за людей своїх молитесь. Радується церква, святосяйне сонце здобувши, завжди схід просвіщає в стражданні на вашу славу, мученики. Радуйтеся, світлії зорі, що ранком сходите! Нові христолюбиві заступники, наші страстотерпці! Покоріть поганих під ноги князям нашим, воздаючи молитви Владиці Богові нашому, щоб жили вони в мирі, в єдності і в здоров\'ї, убережіть їх від міжусобних воєн і від підступів диявола. Сподобайте ж і нас, що хвалу вам співають і вшановують ваше славне торжество, в усі віки і до кінця світу.</p>\r\n<p>Святополк же окаянний і злий убив Святослава, пославши до гір Угорських, коли той тікав в Угри, і став думать: &laquo;Коли переб\'ю всіх братів своїх, буду один володіть Руською землею&raquo;. Так розмишляв він у зарозумілості своїй, а не знав, що Бог дає владу кому хоче, настановляє царя і князя Всевишній тому, кому хоче дати. Коли якусь землю уподобає Бог, то настановляє їй царя і князя праведного, що любить суд і правду, і право заводить через суддю, який чинить суд. Коли ж правдиві князі на землі, то багато гріхів прощається землі тій, а коли злі і лукаві бувають, то ще більше зло наводить Бог на землю ту, оскільки є главою землі тієї. Так і Ісайя говорить: &laquo;Согрішили від голови до ніг&raquo;, тобто від царя і до простих людей. &laquo;Горе бо тому граду, в якому князь юний&raquo; любить пити вино під гуслі <em>А </em>молодими радниками. Таких Бог дає за гріхи, а старих і мудрих відбирає, як сказав Ісайя: &laquo;Відбере Господь від Ієрусалима міць, і міцного богатиря, і хороброго чоловіка, і суддю, і пророка, і смиренного старця, і дивного радника, і мудрого хитруна, і проречистого словом. І поставлю юного князя їм, і хулителя поставлю володіть ними&raquo;.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Святополкъ же оканьны нача княжити в Кыев<small>Ђ</small>. И созвавъ люди, нача даяти ов<small>Ђ</small>мь корьзна, а другимъ кунами, и раздая множьство. Ярославу же не в<small>Ђ</small>дущю отни смерти, Варязи бяху мнози у Ярослава, и насилье творяху. Новгородьцемь. И вьставше на нь Новгородьци, избиша Варягы вь двор<small>Ђ</small>хъ Пронихъ, и разгн<small>Ђ</small>вася Ярославъ, и шедъ на Рокъмъ, и с<small>Ђ</small>д<small>Ђ</small> вь двор<small>Ђ</small>; и пославъ к Новьгородьцемь, и рече: &laquo;уже мн<small>Ђ</small> сихъ не кресити&raquo;. И позва къ соб<small>Ђ</small> нарочитая мужа, иже бяху исьс<small>Ђ</small>кли Варяги; и облъсти ихъ и ис<small>Ђ</small>че [1000]. В ту же нощь приде ему в<small>Ђ</small>сть ис Кыева отъ сестры его Передьславы: &laquo;отець ти умерлъ, а Святополкъ с<small>Ђ</small>дить в Киев<small>Ђ</small>, пославъ уби Бориса и Гл<small>Ђ</small>ба, а ты блюдися сего повелику&raquo;. И се слышавъ Ярославъ, печаленъ бысть по отци, и по брату, и о дружин<small>Ђ</small>; заутра же собравъ избытокъ Новгородцевь, и рече Ярославъ: &laquo;о любимая дружино, юже избихъ вчера, а нын<small>Ђ</small> быша надоб<small>Ђ</small>&raquo;. И утре слезъ, и рече имъ на в<small>Ђ</small>ч<small>Ђ</small>: &laquo;отець мой умерлъ, а Святополкъ с<small>Ђ</small>дить в Кыев<small>Ђ</small>, избивая братью свою&raquo;. И р<small>Ђ</small>ша Новгородьц<small>Ђ</small>: &laquo;аще, княже, братья наша ис<small>Ђ</small>чен<small>Ђ</small> суть, можемъ по тоб<small>Ђ</small> бороти&raquo;. И собра Ярославъ Варягъ тысящю, а прочихъ вой 40 тысящь, и поиде на Святополка, нарекъ Бога, рекъ: &laquo;не азъ почахъ избивать братью, но онъ; да будеть Богъ отместьникъ крови братьи моея, зане без вины пролья кровь Борисову и Гл<small>Ђ</small>бову праведною: еда и мн<small>Ђ</small> си же створить? но суди ми, Господи, по правд<small>Ђ</small>, да скончаеться злоба гр<small>Ђ</small>шнаго&raquo;. И поиде на Святополка; слышавъ же се Святополкъ идуща Ярослава, и пристрои бещисла вои, Руси и Печен<small>Ђ</small>гъ, и изиде противу Любчю, обонъ полъ Дн<small>Ђ</small>пра, и Ярославъ об сюду.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Святополк же окаянний почав княжити в Києві. Скликав людей і став роздавати кому одяг, а другим грішми, і роздав безліч. Ярослав же не знав про смерть батька. Варягів багато було в Ярослава, і насильство чинили новгородцям. І повстали на них новгородці, і перебили варягів на дворах Проніх. І розгнівався Ярослав, і пішов у Ракомо, і сидів у дворі. І послав до новгородців, і сказав: &laquo;Уже мені цих не воскресити&raquo;. І покликав до себе кращих мужів, які посікли варягів, і, обдуривши їх, посік [100]. Тої ж ночі прийшла йому звістка із Києва від сестри його Передслави: &laquo;Батько помер твій, а Святополк сидить у Києві, послав убити Бориса і Гліба, а ти бережися його дуже&raquo;. ї коли це почув Ярослав, туга охопила його за батьком, і за братом, і за дружиною. Другого дня зібрав решток новгородців і сказав Ярослав: &laquo;О люба дружино, яку я вчора побив, а нині мені потрібна&raquo;. І втер сльози, і сказав їм на вічі: &laquo;Батько мій помер, а Святополк сидить в Києві, убиває братів своїх&raquo;. І сказали новгородці: &laquo;Хоча, княже, і посічені браття наші, можемо за тебе боротися&raquo;. І зібрав Ярослав тисячу варягів, а других воїв сорок тисяч, і пішов на Святополка, покликавши Бога [в свідки своєї правди], сказав: &laquo;Не я почав убивати братів, а він, хай буде Бог месником за кров братів моїх, тому що без вини пролив Борисову і Глібову праведну кров, мабуть, і мені те саме зробить? Розсуди мене, Господи, по правді, і хай скінчаться злочини грішника&raquo;. І пішов на Святополка. Почув про це Святополк, що Ярослав іде, і зібрав воїв силу-силенну - русів і печенігів,- і вийшов супроти нього до Любеча на той берег Дніпра, а Ярослав був на цьому.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6524. Приде Ярославъ на Святополка, и сташа противу оба полъ Дн<small>Ђ</small>пра, и не см<small>Ђ</small>яху ни си на они наити, и ни т<small>Ђ</small>и на сихъ, и стояла за 3 м<small>Ђ</small>сяц<small>Ђ</small> противу соб<small>Ђ</small>. И воевода нача Святополчь, яздя вьзл<small>Ђ</small> б<small>Ђ</small>рег, укаряти Новгородци, глаголя: &laquo;что приидосте с хромьцемь симь, а вы плотници суще? а приставимъ вы хоромъ рубить нашихъ&raquo;.</p>\r\n<p>Се слышавше Новгородця, и р<small>Ђ</small>ша Ярославу: &laquo;яко заутра перевеземься на нихъ; аще кто не пойдеть с нами, то сами потнемъ&raquo;. Б<small>Ђ</small> бо уже вь замрозъ; и стояша Святополкъ межи дв<small>Ђ</small>има озерома вьсю нощь упивься с дружиною своею; Ярославъ же заутра исполчивъ дружину, противу св<small>Ђ</small>ту перевезеся. И выс<small>Ђ</small>дше на брегъ, отринуша лодью отъ берега, и поидоша противу соб<small>Ђ</small>, и сьвькупившеся на м<small>Ђ</small>ст<small>Ђ</small>; и бысть с<small>Ђ</small>ча зла, не б<small>Ђ</small> лз<small>Ђ</small> озеромъ помогати Печен<small>Ђ</small>гомъ, и притиснуша Святополчь вои кь озеру, и вьступиша на ледъ, и одол<small>Ђ</small>вати нача Ярославъ; видивъ же Святополкъ поб<small>Ђ</small>же. И одол<small>Ђ</small> Ярославъ, Святополкъ же б<small>Ђ</small>жа в Ляхы. Ярославъ же с<small>Ђ</small>де въ Кыев<small>Ђ</small> на стол<small>Ђ</small> отни; б<small>Ђ</small> же тогда Ярославъ л<small>Ђ</small>тъ 28.</p>\r\n<p>В л<small>Ђ</small>то 6525. Ярославъ ввоиде в Кыевъ, и погор<small>Ђ</small>ша церкви.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6524 [1016] прийшов Ярослав на Святополка.</p>\r\n<p>І стали з обох боків Дніпра,</p>\r\n<p>І не зважувались напасти ні ці на тих, ні ті на цих.</p>\r\n<p>І стояли так три місяці один проти другого.</p>\r\n<p>І почав воєвода Святополків роз\'їжджати вздовж берега,</p>\r\n<p>І докоряти новгородцям словами:</p>\r\n<p>&laquo;Чого прийшли з отим кульгавим, ви ж теслі?</p>\r\n<p>От і заставимо вас хороми нам зводить&raquo;.</p>\r\n<p>І почули це новгородці і сказали Ярославу:</p>\r\n<p>&laquo;Завтра ми переправимося на них,</p>\r\n<p>Якщо хто не піде з нами, то самі вдаримо&raquo;.</p>\r\n<p>А були вже заморозки,</p>\r\n<p>І стояв Святополк між двома озерами</p>\r\n<p>І всю ніч пив із своєю дружиною.</p>\r\n<p>Ярослав же на ранок приготував дружину до бою</p>\r\n<p>І засвіт переправився.</p>\r\n<p>І, висадившись на берег, відштовхнули човни від берега,</p>\r\n<p>І пішли супроти них і зійшлися на полі.</p>\r\n<p>І була січа люта,</p>\r\n<p>І не могли печеніги через озеро прийти на поміч,</p>\r\n<p>І притиснули воїв Святополкових до озера,</p>\r\n<p>І зійшли вони на лід,</p>\r\n<p>І став перемагати Ярослав.</p>\r\n<p>Побачив це Святополк і втік.</p>\r\n<p>І переміг Ярослав.</p>\r\n<p>Святополк же втік до Ляхів.</p>\r\n<p>Ярослав же сів у Києві на батьківський стіл,</p>\r\n<p>І було ж тоді Ярославові літ 28.</p>\r\n<p>&nbsp;</p>\r\n<p>В літо 6525 [1017] Ярослав ввійшов у Київ, і погоріли церкви.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6526. Поиде Болеславъ сь Святополкомъ на Ярослава с Ляхы; Ярославъ же множество совокупи Руси, Варягы, Словены, поиде противу Болеславу и Святополку, и приде Велыню, и сташа оба полъ р<small>Ђ</small>кы Буга. И б<small>Ђ</small> у Ярослава корьмилець и воевода Будый; и нача Буды укаряти Болеслава, глаголя: &laquo;да что ти пропоремь трескою чрево твое толъстое&raquo;. Б<small>Ђ</small> бо великъ и тяжекъ Болеславъ, яко ни на кони не моги с<small>Ђ</small>д<small>Ђ</small>ти, но бяше смысленъ; и рече Болеславъ: &laquo;аще вы сего укора не жаль, азъ единъ погибну&raquo;. И вьс<small>Ђ</small>дъ на конь, вьбреде в р<small>Ђ</small>ку, а по немъ вои его. Ярославъ же не утягну исполчитися, и поб<small>Ђ</small>ди Болеславъ Ярослава; Ярославъ же уб<small>Ђ</small>жавь с четырми челов<small>Ђ</small>кы к Новугороду, Болеславъ же вниде в Кыевъ сь Святополкомъ. И рече Болеславъ: &laquo;разведете дружину мою по городомъ на кормъ&raquo;, и бысть тако. Ярославу же приб<small>Ђ</small>гшу къ Новугороду, и хотяше б<small>Ђ</small>жати за море, и посадникъ Костянтинъ, сынъ Добрынъ, с Новгородци рас<small>Ђ</small>коша лодья Ярославл<small>Ђ</small>, рекуще: &laquo;можемься еще битись по тоб<small>Ђ</small> с Болеславомъ и сь Святополкомъ&raquo;. И начаша скотъ брати отъ мужа по четыре куны, а отъ старост<small>Ђ</small> по 10 гривенъ, а отъ бояръ по осмидесять гривенъ; приведоша Варягы, и вьдаша имъ скотъ, и совькупи Ярославъ воя многи. Болеславъ же б<small>Ђ</small> вь Кыев<small>Ђ</small> с<small>Ђ</small>дя, безумный же Святополкъ рече: &laquo;елико же Ляховъ по городомъ, избивайте я&raquo;. Избиша Ляхы. Болеславъ же б<small>Ђ</small>жа ис Кыева, воизма им<small>Ђ</small>ние и бояры Ярославл<small>Ђ</small> и сестр<small>Ђ</small> его, и Настаса пристави десятиньнаго кь им<small>Ђ</small>ни, б<small>Ђ</small> бо ся ему вьв<small>Ђ</small>рилъ л<small>Ђ</small>стью, и людий множьство веде съ [со]бою, и грады Черв<small>Ђ</small>ньскыя зая соб<small>Ђ</small>; и приде вь свою землю. Святополкъ же нача княжити в Кыев<small>Ђ</small>, и поиде Ярославъ на Святополка, и поб<small>Ђ</small>ди Ярославъ Святополка, и б<small>Ђ</small>жа Святополкъ вь Печен<small>Ђ</small>гы.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6526 [1018] прийшов Болеслав з ляхами і зі Святополком на Ярослава.</p>\r\n<p>Ярослав же зібрав багато русі, варягів, словенів</p>\r\n<p>І пішов Супроти Болеслава і Святополка,</p>\r\n<p>І прийшов до Велиня,</p>\r\n<p>І стали вони на протилежних берегах Бугу.</p>\r\n<p>І був у Ярослава кормилець і воєвода Будий.</p>\r\n<p>І почав Будий збиткуватись над Болеславом словами:</p>\r\n<p>&laquo;Ось почекай, пропоремо кілякою черево твоє товсте&raquo;.</p>\r\n<p>Бо був Болеслав великий і важкий,</p>\r\n<p>Що й на коні сидіти не міг,</p>\r\n<p>Зате був розумний.</p>\r\n<p>І сказав Болеслав [дружині своїй]:</p>\r\n<p>&laquo;Якщо вас не діймає сором за збиткування такі, то я сам загину&raquo;.</p>\r\n<p>І сів на коня, кинувся в ріку, а за ним вої його.</p>\r\n<p>Ярослав же не встиг приготуватися до бою,</p>\r\n<p>І переміг Болеслав Ярослава.</p>\r\n<p>Ярослав же утік із чотирма чоловіками до Новгорода.</p>\r\n<p>Болеслав же ввійшов у Київ із Святополком.</p>\r\n<p>І сказав Болеслав:</p>\r\n<p>&laquo;Розведіть дружину мою по городах на прокорм&raquo;,</p>\r\n<p>І було так зроблено.</p>\r\n<p>Ярослав же прибіг до Новгорода</p>\r\n<p>І хотів тікати за море.</p>\r\n<p>І посадник Костянтин, син Добрині,</p>\r\n<p>Із новгородцями порубали лодії Ярославові</p>\r\n<p>І сказали: &laquo;Можемо ще битися за тебе з Болеславом і Святополком&raquo;.</p>\r\n<p>І почали збирати гроші по чотири куни з мужа,</p>\r\n<p>І з старост по десять гривен,</p>\r\n<p>І з бояр по вісімнадцять гривен.</p>\r\n<p>І привели варягів і заплатили їм гроші.</p>\r\n<p>І зібрав Ярослав воїв много.</p>\r\n<p>&nbsp;</p>\r\n<p>Болеслав же сидів у Києві,</p>\r\n<p>А окаянний Святополк сказав:</p>\r\n<p>&laquo;Скільки є ляхів по городах - убивайте їх&raquo;.</p>\r\n<p>І перебили ляхів.</p>\r\n<p>Болеслав же втік із Києва, захопивши великі багатства,</p>\r\n<p>І бояр Ярославових, і сестер його,</p>\r\n<p>І Настаса з десятинної церкви,</p>\r\n<p>Який обманом втерся до нього в довір\'я,</p>\r\n<p>Приставив [до награбованого] майна.</p>\r\n<p>І людей багато вивів із собою,</p>\r\n<p>І гради Червенські захопив собі,</p>\r\n<p>І повернувся в свою землю.</p>\r\n<p>Святополк же почав княжити в Києві.</p>\r\n<p>І пішов Ярослав на Святополка,</p>\r\n<p>І переміг Ярослав Святополка,</p>\r\n<p>І втік Святополк до печенігів.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6527. Приде Святополкъ с Печен<small>Ђ</small>гы в сил<small>Ђ</small> тяжьц<small>Ђ</small>, и Ярославъ собра множьство вои, и взииде противу ему на Алъто. Ярославъ же ста на м<small>Ђ</small>ст<small>Ђ</small>, идеже убиша Бориса, и вьзд<small>Ђ</small>въ руц<small>Ђ</small> на небо, и рече: &laquo;кровь брата моего вопиеть къ тоб<small>Ђ</small>, Владыко, мьсти отъ крови прав<small>Ђ</small>днаго сего, якоже мьстилъ еси оть крови Авелевы, положивъ на Каин<small>Ђ</small> стенанье и трясение; тако положи на семь&raquo;. И помолився рекъ: брата мои! аще есте отсюду т<small>Ђ</small>ломъ отошла, то молитвою своею помозита ми на противнаго сего убийцю гордаго&raquo;. И се ему рекшю, и поидоша противу соб<small>Ђ</small>, и покрыша поле Лядьское обои отъ множьства вой. Б<small>Ђ</small> же пятокъ тогда; всходящю солнцю, и совокупишася обои, и бысть с<small>Ђ</small>ча зла, аки же не была в Руси, и за рукы емлюще с<small>Ђ</small>чахуся; и соступишася трижды, яко по удольемь кровь течаще; и къ вечеру одол<small>Ђ</small> Ярославъ, а Святополкъ б<small>Ђ</small>жа. Б<small>Ђ</small>жащю же ему, и нападе на нь б<small>Ђ</small>съ, и раслаб<small>Ђ</small>ша кости его, и не можаше с<small>Ђ</small>д<small>Ђ</small>ти на кони, и ношахуть и вь носилахъ; и принесоша и кь Берестью, б<small>Ђ</small>гающе с нимь, онъ же глаголаше: &laquo;поб<small>Ђ</small>гнете со мною, женуть по насъ&raquo;. Отроци же его посылаху противу: &laquo;егда кто поженеть по немь?&raquo; и не б<small>Ђ</small> никогоже вьсл<small>Ђ</small>дъ женущаго с нимь; онъ же в немощи лежа, и вьсхапився глаголаше: &laquo;о, се женуть, оно женуть, поб<small>Ђ</small>гнете&raquo;! И не можаше стерп<small>Ђ</small>ти на единомъ м<small>Ђ</small>ст<small>Ђ</small>, и проб<small>Ђ</small>же Лядьскую землю, гонимъ гн<small>Ђ</small>вомъ Божиимъ, и проб<small>Ђ</small>же пусты ню межи Чяхы и Ляхы, и ту испроверже животъ свой зл<small>Ђ</small>. Егоже и по правд<small>Ђ</small>, яко неправ<small>Ђ</small>дна, суду пришедшу, по отшествии сего св<small>Ђ</small>та прияша муку сего оканьнаго Святополка: показываше яв<small>Ђ</small> посланая пагубная рана, вь смерть немилостивно вьгна, и по смерти в<small>Ђ</small>чно мучнмъ есть и связанъ. Есть же могила его в пустыни той и до сихъ дний, исходить же отъ ней смрадъ золъ. Се же Богъ показа на показание княземь Рускымъ, да аще сице же створять, се слышавше, ту же казнь приимуть; но больши сея, понеже се в<small>Ђ</small>дуще бывшее, створыти какое же зло братоубийство. 7 бо мьстий прия Каинъ, убивъ Ав<small>Ђ</small>ля, Ламехъ 70; понеже бо Каинъ не в<small>Ђ</small>ды мьщьния прияти отъ Бога, а Ламехъ в<small>Ђ</small>ды казнь, бывшюю на прародителю его, створи убийство Рече бо Ламехъ своима женама; мужа убихъ вь вредъ мн<small>Ђ</small>, и упошю вь язву мн<small>Ђ</small>; т<small>Ђ</small>мже, рече, 70 мьстий на мн<small>Ђ</small>, понеже, рече, в<small>Ђ</small>дая створихъ. Се Ламехъ уби 2 брата Енохова, и поя соб<small>Ђ</small> жен<small>Ђ</small> его; сьи же ; Святополкъ новы Ламехъ, иже ся родилъ отъ прелюбод<small>Ђ</small>анья, иже изби братью свою, сыны Гедеоновы, тако и сь бысть. Ярославъ же пришедъ с<small>Ђ</small>де в Кыев<small>Ђ</small>, утеръ пота с дружиною своею, показавъ поб<small>Ђ</small>ду и трудъ великъ.</p>\r\n<p>Въ л<small>Ђ</small>то 6528. Родися у Ярослава сынъ, и нарече имя ему Володимиръ.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6527 [1019].</p>\r\n<p>Прийшов Святополк з печенігами силою грізною,</p>\r\n<p>І Ярослав зібрав багато воїв,</p>\r\n<p>І вийшов супроти нього на Альту.</p>\r\n<p>Ярослав же став на тому місці, де був убитий Борис,</p>\r\n<p>І здійняв руки в небо, і сказав:</p>\r\n<p>&laquo;Кров брата мого вопіє до тебе, Владико!</p>\r\n<p>Відомсти за кров цього праведника,</p>\r\n<p>Як помстився за Авелеву кров,</p>\r\n<p>Осудивши Каїна на стогнання і трусіння, так і цьому зроби&raquo;.</p>\r\n<p>І, коли помолився, сказав:</p>\r\n<p>&laquo;Брати мої! Хоч тілом і відійшли ви звідси,</p>\r\n<p>То молитвою своєю поможіть мені проти ворога цього -</p>\r\n<p>Убивці зарозумілого&raquo;.</p>\r\n<p>І після того, як сказав це йому, пішли один проти одного.</p>\r\n<p>І поле Лядське покрили обидва війська множством воїв.</p>\r\n<p>І була тоді п\'ятниця,</p>\r\n<p>І сходило сонце,</p>\r\n<p>І зійшлися обидва війська,</p>\r\n<p>І була січа люта, якої ще не було на Русі,</p>\r\n<p>І за руки хапали один другого, і рубалися,</p>\r\n<p>І сходилися тричі, аж кров долинами текла.</p>\r\n<p>І надвечір переміг Ярослав, а Святополк утік.</p>\r\n<p>І коли він тікав, вселився в нього біс,-</p>\r\n<p>І розслабли кості його,</p>\r\n<p>І не міг він сидіть на коні, і несли його на носилках</p>\r\n<p>І принесли його до Берестя, ті, що тікали з ним</p>\r\n<p>Він же говорив: &laquo;Біжіть бігом зі мною, женуться за нами&raquo;.</p>\r\n<p>Отроки його посилали назад подивитись.</p>\r\n<p>&laquo;Чи не гониться хто за ним?&raquo;</p>\r\n<p>І не було нікого, хто б гнався вслід йому.</p>\r\n<p>Він же лежав немощний і, підхоплюючись, говорив.</p>\r\n<p>&laquo;Ой, гоняться, оно женуться, біжіть!&raquo;</p>\r\n<p>І не міг витримати на одному місці,</p>\r\n<p>І перебіг Лядську землю, гнаний гнівом божим,</p>\r\n<p>І перебіг у пустинь між Чешською і Лядською землею,</p>\r\n<p>І тут закінчив своє зле життя.</p>\r\n<p>Праведний суд настиг його неправедного,</p>\r\n<p>І по відході з цього світу прийняв Святополк муки окаянного:</p>\r\n<p>Показана вочевидь послана йому Богом смертельна рана,</p>\r\n<p>Яка жорстоко кинула його смерті,</p>\r\n<p>І після смерті терпить він, зв\'язаний, вічні муки</p>\r\n<p>Є і могила його в тій пустині і до наших днів,</p>\r\n<p>І виходить із неї сморід нестерпний.</p>\r\n<p>Се Бог з\'явив на показ князям Руським,</p>\r\n<p>Хай, коли таке вчинять, знають про це.</p>\r\n<p>І вони, коли зроблять подібне зле братовбивство, таку ж приймуть кару, але більшу цієї, оскільки вже знають, що було. Сім бо помст прийняв Каїн, убивши Авеля, Ламех сімдесят, оскільки Каїн не знав, що прийме помсту від Бога, а Ламех знав кару, яку поніс його прародич, вчи нивши вбивство. Сказав-бо Ламех своїм жонам: &laquo;Мужа убив на шкоду собі, і юнака убив, чим наніс сам собі рану Ось тому,- сказав,- сімдесят помст належить мені, оскільки,- сказав,- знаючи зробив&raquo;. Це Ламех убив двох братів Єнохових і взяв собі їхніх жінок. Цей же Святополк - новий Ламех, що народився від перелюбу, що повбивав братів своїх, синів Гедеонових, такий і цей був.</p>\r\n<p>Ярослав же повернувся і сів у Києві, утер піт з дружиною своєю та показав перемогу і труд великий.</p>\r\n<p>В літо 6528 [1020] народився в Ярослава син, і дав ім\'я йому - Володимир.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6529. Приде Брячиславъ, сынъ Изяславль, внукъ Володимеръ, на Новъгородъ, и зая Новъгородъ, поимъ множество Новгородець им<small>Ђ</small>ние ихъ, поиде Полотьску опять; и пришедшю ему к Судмири р<small>Ђ</small>ц<small>Ђ</small>, Ярославъ и выиде ис Кыева 7 день постиже и ту, и поб<small>Ђ</small>ди Ярославъ Брячислава и Новьгородц<small>Ђ</small>, и вороти и ту к Новугороду, а Брячиславъ б<small>Ђ</small>жа къ Полотьску.</p>\r\n<p>Въ л<small>Ђ</small>то 6530. Приде Ярославъ кь Берестью. Вь си же Бремена Мьстиславу сущю въ Тмуторокани, и поиде на Касогы. Слышавъ же се князь Касожький Редедя, изыиде противу ему, и ставшима об<small>Ђ</small>има полкома противу соб<small>Ђ</small>, и рече Редедя кь Мьстиславу: &laquo;что ради губив<small>Ђ</small> дружину межи собою, но снидев<small>Ђ</small>ся бороться; да аще одол<small>Ђ</small>ешь ты, и возьмеши им<small>Ђ</small>ние мое, и жену мою, и землю мою; аще ли азъ одол<small>Ђ</small>ю, то возму твое все&raquo;. И рече Мьстиславъ: &laquo;тако буди&raquo;. И сь<small>Ђ</small>хаста, и рече Редедя кь Мьстиславу: &laquo;не оружьемь ся бьев<small>Ђ</small>, но борьбою&raquo;. И ястася бороти кр<small>Ђ</small>пко, и на долз<small>Ђ</small> борющимся има, и нача изнемогати Мьстиславъ, б<small>Ђ</small> бо великъ Редедя; и рече Мьстиславъ: &laquo;о пресвятая Богородице! помози ми; аще бо одол<small>Ђ</small>ю сему, сьзижю церковь вь имя твое&raquo;. И се рекъ, удари имъ о землю, и вынемь ножь, удари и вь гортань ложемъ, и ту бысть зар<small>Ђ</small>занъ Редедя; и вьшедъ в землю его, и взя все им<small>Ђ</small>ние его, и жену его, и д<small>Ђ</small>ти его, и дань възложи на Касогы. И пришедьшю к Тьмутороканю, и заложи церковь святыя Богородица, и созда ю, яже стоить и до сего дни вь Тмуторокан<small>Ђ</small>.</p>\r\n<p>В л<small>Ђ</small>то 6531. Поиде Мьстиславъ на Ярослава, с Козары и сь Касогы.</p>\r\n<p>В л<small>Ђ</small>то 6532. Ярославу сущю въ Нов<small>Ђ</small>город<small>Ђ</small>, приде Мьстиславъ ис Тьмуторокана Кыеву; и не прияша его Кыян<small>Ђ</small>; онъ же с<small>Ђ</small>дь на стол<small>Ђ</small> Чернигов<small>Ђ</small>, Ярославу сущу въ Нов<small>Ђ</small>город<small>Ђ</small> тогда.</p>\r\n<p>В се же л<small>Ђ</small>то вьсташа вьлъсви в Суждалцихъ, избиваху старую чадь, по дьяволю наученью и б<small>Ђ</small>сованию, глаголюще, яко си держать гобино. И мятежь великъ и голодъ въ всей стран<small>Ђ</small> той [бысть]; идоша по Волз<small>Ђ</small> вси людье вь Болъгары, и прив[е]зоша жито, и тако ожиша. Слышавъ же Ярославъ вълъхвы ты, и приде къ Суждалю; изьима волъхвы, расточи, а другия показни, рекъ сице: &laquo;Богъ наводить по гр<small>Ђ</small>хомъ на кою землю гладомъ, или моромъ, или ведромъ, или иною казнью, а челов<small>Ђ</small>кь н<small>Ђ</small> в<small>Ђ</small>сть ничтоже&raquo;. И вьзвративъся Ярославъ, и поиде к Новугороду, и посла Ярославъ за море по Варяги, и приде Акунъ с Варягы, и б<small>Ђ</small> Акунъ сл<small>Ђ</small>пъ, и луда у него златомъ истыкана.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6529 [1021] приходив Брячислав, син Ізяслава, внук Володимира, на Новгород, і взяв Новгород, захопив багатьох новгородців і майно їхнє, і пішов знову в Полоцьк. І коли дійшов він до Судмирі-ріки, Ярослав вийшов із Києва і на сьомий день догнав його тут. І переміг Ярослав Брячислава, і новгородців повернув у Новгород, а Брячислав утік у Полоцьк.</p>\r\n<p>&nbsp;</p>\r\n<p>В літо 6530 [1022] прийшов Ярослав до Берестя.</p>\r\n<p>І в цей час Мстислав з Тмуторокані [вийшов]</p>\r\n<p>І пішов на касогів.</p>\r\n<p>І почув про це Редедя, князь касожський,</p>\r\n<p>І вийшов супроти нього,</p>\r\n<p>І стали обидва війська один проти одного,</p>\r\n<p>І рече Редедя Мстиславові: &laquo;Чого ради губити свої дружини?</p>\r\n<p>Але зійдемося і самі поборемось.</p>\r\n<p>І коли здолаєш ти, візьмеш майно моє,</p>\r\n<p>І жону мою, і дітей моїх,</p>\r\n<p>І землю мою.</p>\r\n<p>А коли я здолаю, то візьму все твоє&raquo;.</p>\r\n<p>І сказав Мстислав: &laquo;Буде так!&raquo;</p>\r\n<p>І, з\'їхавшись, сказав Редедя до Мстислава:</p>\r\n<p>&laquo;Не оружжям будемо битися, а боротьбою&raquo;.</p>\r\n<p>І зчепилися боротися кріпко,</p>\r\n<p>І боролися довго,</p>\r\n<p>І почав знемагати Мстислав, бо був Редедя здоровий.</p>\r\n<p>І рече Мстислав: &laquo;О пресвята Богородице, поможи мені!</p>\r\n<p>І коли здолаю цього, зведу церкву в їм я твоє&raquo;.</p>\r\n<p>І, це сказавши, ударив ним об землю, вихопив ніж,</p>\r\n<p>І ударив його ножем у гортань,</p>\r\n<p>І зарізав тут Редедю.</p>\r\n<p>І пішов Мстислав у землю його,</p>\r\n<p>І забрав все майно його,</p>\r\n<p>І жону його, і дітей його,</p>\r\n<p>І дань наклав на касогів.</p>\r\n<p>І прийшов у Тмуторокань,</p>\r\n<p>І заклав церкву святої Богородиці,</p>\r\n<p>І збудував її,</p>\r\n<p>І стоїть вона і до сьогодні в Тмуторокані.</p>\r\n<p>&nbsp;</p>\r\n<p>В літо 6531 [1023] пішов Мстислав на Ярослава з козарами і з касогами.</p>\r\n<p>В літо 6532 [1024], коли Ярослав був у Новгороді, прийшов Мстислав із Тмуторокані до Києва. І не прийняли його кияни, він же сів на стіл у Чернігові, Ярослав був у Новгороді тоді.</p>\r\n<p>Цього ж літа повстали волхви у Суздальцях, перебили стару челядь, по диявольському напученню і бісівському підступу, говорили, що ці тримають запаси. І смута велика і голод були по всій тій землі. Ішли всі люди по Волзі в Болгарську землю, і привозили жита, і так вижили. Почув же Ярослав про тих волхвів і прийшов у Суздаль, половив волхвів, одних повиганяв, а других покарав, і сказав таке: &laquo;За гріхи Бог насилає на якусь землю голод, чи мор, чи засуху, чи інші кари, а чоловік нічого не знає про те&raquo;.</p>\r\n<p>&nbsp;</p>\r\n<p>І повернувся Ярослав,</p>\r\n<p>І прийшов у Новгород,</p>\r\n<p>І послав за море по варягів.</p>\r\n<p>І прийшов Акун із варягами,</p>\r\n<p>І був Акун той сліпучий і плащ у нього золотом тканий.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>И приде ко Ярославу; и Ярославъ сь Акуномъ поиде на Мьстислава, Мьстиславъ же слышавъ изииде противу имя кь Листьвну. Мьстиславъ же с вечера исполчи дружину, и постави. С<small>Ђ</small>веръ вь чело противу Варягомъ; а самъ ста с дружиною своею по крилома. И бывъши нощи, бысть тма, и громове и молънья и дождь. И рече Мьстиславъ дружин<small>Ђ</small> своей: &laquo;поидемь на н<small>Ђ</small>&raquo;. И поиде Мьстиславъ и Ярославъ противу, и съступишася въ чело Варяз<small>Ђ</small> сь С<small>Ђ</small>веромъ, и трудишася Варязи с<small>Ђ</small>куще С<small>Ђ</small>веръ, и по семь наступи Мьстиславъ с дружиною своею, и нача с<small>Ђ</small>чи Варягы, и бысть с<small>Ђ</small>ча силна; яко посв<small>Ђ</small>тяше мъльнъя, и блисташася оружья, и б<small>Ђ</small> гроза велика, и с<small>Ђ</small>ча силна и страшна. Вид<small>Ђ</small>въ же Ярославъ, яко поб<small>Ђ</small>жаемъ есть, и поб<small>Ђ</small>же сь Якуномъ, княземъ Варяжькимъ, и Акунъ ту отб<small>Ђ</small>же луды златое; а Ярославъ же приде к Новугороду, и Якунъ иде за море. Мьстиславъ же, о св<small>Ђ</small>тъ заутра, и вид<small>Ђ</small> лежачи ис<small>Ђ</small>чены отъ своихъ С<small>Ђ</small>в<small>Ђ</small>ръ и Варягы Ярославли, и рече: &laquo;кто сему не радъ? се лежить С<small>Ђ</small>верянинъ, а се Варягъ, а своя дружина ц<small>Ђ</small>ла&raquo;. И посла Мьстиславъ по Ярослав<small>Ђ</small> глаголя: &laquo;с<small>Ђ</small>ди ты на стол<small>Ђ</small> своемь Кыев<small>Ђ</small>, понеже ты еси стар<small>Ђ</small>ши братъ, а мн<small>Ђ</small> буди сторона&raquo;; и не см<small>Ђ</small>яше Ярославъ в Кыевъ ити, донележе смиристася. И с<small>Ђ</small>дяше [Мьстиславъ въ Чернигов<small>Ђ</small>, а] Ярославъ в Нов<small>Ђ</small>город<small>Ђ</small>, и бяху с<small>Ђ</small>дяще в Кыев<small>Ђ</small> мужи Ярославл<small>Ђ</small>.</p>\r\n<p>В томъ же л<small>Ђ</small>т<small>Ђ</small> родися у Ярослава другый сынъ, и нарече имя ему Изяславъ.</p>\r\n<p>В л<small>Ђ</small>то 6533</p>\r\n<p>В л<small>Ђ</small>то 6534. Ярославъ сьвокупи воя многы, и приде Кыеву, и створи миръ с оратомъ своимъ Мьстиславомъ у Городьца. И разд<small>Ђ</small>листа и по Дн<small>Ђ</small>пръ Рускую землю: Ярославъ прия су страну, а Мьстиславъ ону; и начаста жити мирно и въ братолюбьи, и преста усобица и мятежъ, и бысть тишина велика в земли Руской.</p>\r\n<p>В л<small>Ђ</small>то 6535. Родися третий сынъ Ярославу, и нарече имя ему Святославъ.</p>\r\n<p>В л<small>Ђ</small>то 6536.</p>\r\n<p>В л<small>Ђ</small>то 6537. Мирно л<small>Ђ</small>то.</p>\r\n<p>В л<small>Ђ</small>то 6538 Ярославъ взя Белзъ И родися Ярославу четвертый сынъ, и нарече имя ему Всеволодъ. Сего л<small>Ђ</small>та иде Ярославъ на Чюдь и поб<small>Ђ</small>ди я, и постави городъ Юрьевъ</p>\r\n<p>В се же время умершю Болеславу великому в Лях<small>Ђ</small>хъ, и бысть мятежь великъ в Лядьской земли; и вьставше людье избиша епископы, и попы, и бояры своя, и бысть мятежь въ нихъ.</p>\r\n<p>В л<small>Ђ</small>то 6539. Ярославъ и Мьстиславъ собраста воя многы, и идоста на Ляхы и заяста грады Червенъскыя опять, и повоеваста Лядьскую землю, и многы Ляхы приведоста, и разд<small>Ђ</small>листа я; и посади Ярославъ своя по Рси, и суть и до сего дни.</p>\r\n<p>В л<small>Ђ</small>то 6540. Ярославъ поча ставити городы по Рсі.</p>\r\n<p>В л<small>Ђ</small>то 6541. Мьстиславичь Еустафий умре.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>І прийшов він до Ярослава.</p>\r\n<p>І пішов Ярослав із Акуном на Мстислава.</p>\r\n<p>Мстислав же довідався про це</p>\r\n<p>І виступив назустріч їм до Листвина.</p>\r\n<p>Мстислав же звечора зготував до бою дружину</p>\r\n<p>І поставив сівер на чолі проти варягів,</p>\r\n<p>А сам став з дружиною своєю на правому і лівому крилі.</p>\r\n<p>І була ніч, була темінь,</p>\r\n<p>І грім, і блискавка, і дощ.</p>\r\n<p>І сказав Мстислав дружині своїй:</p>\r\n<p>&laquo;Вдаримо на них&raquo;.</p>\r\n<p>І пішли Мстислав і Ярослав супроти,</p>\r\n<p>І зіткнулися чільні варяги з сіверою,</p>\r\n<p>І труждалися варяги, рубаючи сівер,</p>\r\n<p>А після цього вдарив Мстислав з дружиною своєю,</p>\r\n<p>І почав рубати варягів.</p>\r\n<p>І була січа люта.</p>\r\n<p>І коли спалахували блискавки - виблискувала зброя,</p>\r\n<p>І була велика гроза,</p>\r\n<p>І січа сильна і страшна.</p>\r\n<p>Побачив Ярослав, що терпить поразку,</p>\r\n<p>І втік із Акуном, князем варязьким,</p>\r\n<p>І Акун тут загубив плащ свій золотий.</p>\r\n<p>Ярослав же прийшов до Новгорода,</p>\r\n<p>А Акун пішов за море.</p>\r\n<p>&nbsp;</p>\r\n<p>Мстислав же засвіт рано побачив порубаних</p>\r\n<p>І полеглих своїх сівер і варягів Ярославових,</p>\r\n<p>І сказав: &laquo;Хто цьому не порадіє?</p>\r\n<p>Це лежить сіверянин, а це варяг. А своя дружина ціла&raquo;.</p>\r\n<p>І послав Мстислав за Ярославом, кажучи:</p>\r\n<p>&laquo;Ти сиди на своєму столі в Києві, оскільки ти старший брат,</p>\r\n<p>А мені буде [ліва] сторона [Дніпра]&raquo;.</p>\r\n<p>І не посмів Ярослав у Київ іти, доки не помирилися</p>\r\n<p>І сидів Мстислав у Чернігові,</p>\r\n<p>А Ярослав у Новгороді,</p>\r\n<p>І сиділи в Києві мужі Ярославови.</p>\r\n<p>Того ж літа народився в Ярослава другий син, і дав ім\'я йому Ізяслав.</p>\r\n<p>В літо 6533 [1025].</p>\r\n<p>В літо 6534 [1026] Ярослав зібрав воїв много, і прийшов до Києва, і уклав мир із братом своїм Мстиславом біля Городця. І розділили по Дніпру Руську землю: Ярослав взяв цю [праву] сторону, а Мстислав ту [ліву]. І стали жити мирно і в братолюбстві, і припинилися усобиці, і смути, і настала тиша велика в землі Руській.</p>\r\n<p>В літо 6535 [1027] народився третій син у Ярослава, і назвав він його Святославом.</p>\r\n<p>В літо 6536 [1028].</p>\r\n<p>В літо 6537 [1029]. Мирне літо.</p>\r\n<p>В літо 6538 [1030] Ярослав узяв Белз. І народився в Ярослава четвертий син, і назвав він його Всеволодом Цього ж літа ходив Ярослав на чудь, і переміг їх, і поставив город Юр\'їв.</p>\r\n<p>У це ж врем\'я помер Болеслав Великий у ляхів, і була смута велика в Лядській землі: і повстали люди, і перебили єпископів, і попів, і бояр своїх. І була смута в них.</p>\r\n<p>В літо 6539 [1031] Ярослав і Мстислав зібрали воїв багато, і пішли на ляхів, і знову зайняли городи Червоноруські, і звоювали Лядську землю, і багато ляхів привели, і поділили їх між собою.</p>\r\n<p>І посадив Ярослав своїх по Рсі, і живуть вони там і по цей день.</p>\r\n<p>В літо 6540 [1032] Ярослав почав ставити города по Рсі.</p>\r\n<p>В літо 6541 [1033] Мстиславич Євстафій помер.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6542. Мьетиславъ изыиде на ловы, и разбол<small>Ђ</small>ся и умре; и положиша и въ церкви святаго Спаса, юже создалъ самъ; б<small>Ђ</small> бо вьздано ея при немъ вьзвыше яко и на кон<small>Ђ</small> стоячи рукою досячи. Б<small>Ђ</small> же Мьстиславъ дебелъ т<small>Ђ</small>ломъ, чермьномъ лицемъ великома очима, храбръ на рати, и милостивъ, и любяше дружину повелику, а им<small>Ђ</small>ния не щадяше, ни питья, ни ядения не браняше. Посемь же прия власть его Ярославъ, и бысть единовластець Руской земли. Иде Ярославъ к Новугороду, посади сына своего Володимира в Нов<small>Ђ</small>город<small>Ђ</small>, епископа постави Жидяту; и в то время родися Ярославу сынъ и нарекоша имя ему Вячеславъ. И Ярославу же сущу в Нов<small>Ђ</small>город<small>Ђ</small>, и приде ему в<small>Ђ</small>сть, яко Печен<small>Ђ</small>з<small>Ђ</small> обьемъ стоять Кыевъ. Ярославъ же собравъ воя многы, Варягы и Словены, и прииде Кыеву и вьниде вь градъ свой. И б<small>Ђ</small> же Печен<small>Ђ</small>гъ бещисла. Ярославъ же выступи из града, исполчи дружину и постави Варягы посред<small>Ђ</small>, а на прав<small>Ђ</small>й стран<small>Ђ</small> Кыяны, а на л<small>Ђ</small>вемъ крил<small>Ђ</small> Новгородц<small>Ђ</small>. И сташа предъ городомъ, а Печен<small>Ђ</small>з<small>Ђ</small> приступати начаша, и соступишася на м<small>Ђ</small>ст<small>Ђ</small>, ид<small>Ђ</small>же есть нын<small>Ђ</small> святая Софья, митрополья Руская; б<small>Ђ</small> бо тогда поле вн<small>Ђ</small> града. И б<small>Ђ</small> с<small>Ђ</small>ча зла, и одна одол<small>Ђ</small>въ к вечеру Ярославь. И поб<small>Ђ</small>гоша Печен<small>Ђ</small>з<small>Ђ</small> раздно, и не в<small>Ђ</small>дахуся камо б<small>Ђ</small>жаче, и овии б<small>Ђ</small>гающе тоняху в Ситомли, ин<small>Ђ</small>и же во ин<small>Ђ</small>хъ р<small>Ђ</small>кахъ, и тако погибоша, а прокъ ихъ проб<small>Ђ</small>гоша и до сего дни.</p>\r\n<p>В то же л<small>Ђ</small>то всади Ярославъ Судислава вь порубь, брата своего, Плесков<small>Ђ</small>; оклеветаны к нему.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6542 [1034]. Мстислав вийшов на лови, захворів і помер. І похоронили його в церкві святого Спаса, яку сам і збудував. При ньому було зведено її в висоту на стільки, що й стоячи на коні не можна було досягти рукою. Був же Мстислав дебелий тілом, червонощокий, з великими очима, хоробрий у битві і милостивий, найбільше любив дружину, а багатства не жалів [для неї], ні в питті, ні в їді не спиняв. Після цього прийняв владу його Ярослав і став одновладцем Руської землі.</p>\r\n<p>&nbsp;</p>\r\n<p>Пішов Ярослав у Новгород</p>\r\n<p>І посадив сина свого Володимира в Новгороді,</p>\r\n<p>А єпископом поставив Жидяту.</p>\r\n<p>І в той час народився в Ярослава син,</p>\r\n<p>І назвали його Вячеславом.</p>\r\n<p>І коли перебував Ярослав у Новгороді,</p>\r\n<p>Прийшла йому звістка, що печеніги взяли в облогу Київ.</p>\r\n<p>Ярослав же зібрав багато воїв - варягів і словен -</p>\r\n<p>І прийшов до Києва і ввійшов у город свій.</p>\r\n<p>І було печенігів безліч.</p>\r\n<p>Ярослав же виступив із города, зготував дружину до битви</p>\r\n<p>І поставив варягів посередині,</p>\r\n<p>А з правого краю - киян, а на лівому крилі - новгородців.</p>\r\n<p>І стали перед городом, а печеніги почали наступати,</p>\r\n<p>І зійшлися на місці,</p>\r\n<p>Де нині собор святої Софії - митрополія Руська,-</p>\r\n<p>А тоді було там поле за городом.</p>\r\n<p>І була січа люта,</p>\r\n<p>І ледве здолав надвечір Ярослав.</p>\r\n<p>І побігли печеніги врізнобіч,</p>\r\n<p>І не знали куди біжать.</p>\r\n<p>І одні втікачі втонули у Ситомлі, інші ж - у інших ріках,</p>\r\n<p>І так загинули, а решта їх бігає невідомо де і до сьогодні.</p>\r\n<p>Того ж літа посадив Ярослав Судислава, брата свого, у поруб у Плескові, за наклепом у злому замислі проти нього.</p>\r\n<p>[В літо 6543 (1035). В літо 6544 (1036)]</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&nbsp;</p>',NULL,1850,5,1,NULL,25);
INSERT INTO `publications_publication` VALUES
(4710,'P','2008-12-20',0,NULL,'Note','Повість врем\'яних літ Роки 6495 [987] — 6521 [1013].','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В літо 6495 [987]. Скликав Володимир своїх бояр і старійшин городських і сказав їм: &laquo;Оце приходили до мене болгари, кажучи: &laquo;Прийми закон наш&raquo;. Потім прийшли німці, і ті хвалили закон свій.</span></span></p>','<p><a href=\"http://izbornyk.org.ua/pvlyar/yar04.htm\">Роки 6495 [987] &mdash; 6521 [1013].</a></p>\r\n<p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6495. Съзва Вълодімерь бояры своя и старци градскыа, и рече имъ: &laquo;се приходиша къ мн<small>Ђ</small> Болгаре, рекуще: пріими законъ нашь; посемъ же пріидоша Н<small>Ђ</small>мци, и тые хваляху законъ свой; по сихъ приходиша Жидове. Сихъ же посл<small>Ђ</small>ди приходиша и Греци, хуляще вс<small>Ђ</small> законы, свой же хваляще, а много глаголаша сказующе отъ начала миру; суть же хитро сказующе, яко и другый св<small>Ђ</small>тъ пов<small>Ђ</small>дають быти, и чюдно слышати ихъ ...[...] ... зсмыслены числомъ 10, и р<small>Ђ</small>ша имъ: &laquo;идете первое в Болгары, испытайте в<small>Ђ</small>ру ихъ и службу&raquo;. Они же идоша, и пришедше видиша скв<small>Ђ</small>рная д<small>Ђ</small>ла ихъ и кланяние вь ропати; и придоша в землю свою. И рече имъ Володимерь: &laquo;идите пакы въ Н<small>Ђ</small>мц<small>Ђ</small> и сглядайте такоже, и оттуду идете въ Грекы&raquo;. Они же придоша въ Н<small>Ђ</small>мц<small>Ђ</small>, и сглядавше церковь и службу ихъ, и придоша к Царюграду, и внидоша къ царю; царь же испыта, коея ради вины придоша, они же испов<small>Ђ</small>даша ему вся бывшая. Си слышавъ царь, и радъ бысть, и честь велику створи имъ въ тъ день. Наутр<small>Ђ</small>я же посла къ патреарху, глаголя сице: &laquo;придоша Русь, пытающе в<small>Ђ</small>ры нашея; да пристрой церковь и клиросъ, и самъ причинися въ святительскія ризы, да видятъ славу Бога нашего&raquo;. И си слышавъ патреархъ, и повел<small>Ђ</small> созвати клиросъ всь, и по обычаю створи празникъ, и кадила вьжгоша, и п<small>Ђ</small>ния ликы составиша. И иде и царь с ними во церковь, и поставиша я на пространьн<small>Ђ</small> м<small>Ђ</small>ст<small>Ђ</small>, показующе красоту церковьную, и п<small>Ђ</small>нья и службу архиер<small>Ђ</small>йскыи, предстоянья дьяконъ, сказающе имъ служение Бога своего; они же въ изум<small>Ђ</small>ньи бывше, и удивившеся, похвалиша службу ихъ. И призвавша я царя Василей и Костянтинъ и реста имъ: &laquo;идите в землю вашу&raquo;, и отпусти и с дары великы и с честью. Они же придоша въ землю свою, и созва князь бояры своя и старца. Рече Володимеръ: &laquo;се придоша послании нами мужи, да слышимъ отъ нихъ бывшее&raquo;; и рече имъ: &laquo;скажите предъ дружиною&raquo;. Они же р<small>Ђ</small>ша: &laquo;яко ходихомъ первое в Болгары и смотрихомъ, како ся кланяють въ храмин<small>Ђ</small>, рекше в ропат<small>Ђ</small>, стояще безъ пояса; и поклонився, сядеть и глядить с<small>Ђ</small>мо и овамо, акы б<small>Ђ</small>шенъ, и н<small>Ђ</small>сть веселия у нихъ, но печаль и смрадъ великъ, и н<small>Ђ</small>сть добръ законъ ихъ. И придохомъ въ Н<small>Ђ</small>мц<small>Ђ</small>, и видихомъ службу творяща, а красоты не видихомъ никоеяже. И придохомъ же въ Гр<small>Ђ</small>кы, и ведоша ны ид<small>Ђ</small>же служать Богу своему, и не св<small>Ђ</small>мы, на небеси ли есмь был<small>Ђ</small>, или на земл<small>Ђ</small>: н<small>Ђ</small>сть бо на земли такого вида, или красоты такоя, недоум<small>Ђ</small>емь бо сказати; токмо то в<small>Ђ</small>мы, яко отъинудь Богъ съ челов<small>Ђ</small>кы пребываеть, и есть служба ихъ паче всихъ странъ. Мы убо не можемь забыти красоты тоя; всякъ бо челов<small>Ђ</small>къ, аще преже вкусить сладка, посл<small>Ђ</small>ди же не можеть, горести прияти, тако и мы не имамъ сде жити&raquo;. Отв<small>Ђ</small>щавъша же бояр<small>Ђ</small> и р<small>Ђ</small>ша: &laquo;аще лихъ бы законъ Гр<small>Ђ</small>чкый, то не бы баба твоя Олга прияла крещения, яже б<small>Ђ</small> мудр<small>Ђ</small>йши всихъ челов<small>Ђ</small>къ&raquo;. Отв<small>Ђ</small>щавъ же Володимеръ рече: &laquo;то кде крещение приимемь&raquo;? Они же р<small>Ђ</small>ша: &laquo;кд<small>Ђ</small> ти любо&raquo;.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6495 [987]. Скликав Володимир своїх бояр і старійшин городських і сказав їм: &laquo;Оце приходили до мене болгари, кажучи: &laquo;Прийми закон наш&raquo;. Потім прийшли німці, і ті хвалили закон свій. Після цих приходили жиди. Наостанку приходили і греки, хулили всі закони, свій же вихваляли. І багато чого говорили, розповідали від початку світу, мудро розповідали і про потойбічний світ, який начебто має бути, і любо слухати їх...&raquo; [Вибрали мужів славних і] розумних числом десять і сказав їм: &laquo;Ідіть найперше до болгар, випробуйте їхню віру і службу&raquo;. Вони ж пішли і, прийшовши, побачили паскудні діла їхні і поклоніння в мечеті - і повернулися в землю свою. І сказав їм Володимир: &laquo;Підіть ще до німців і подивіться також, а звідти ідіть у Греки&raquo;. Вони ж прийшли до німців і споглядали церкву їхню і службу в ній. І прийшли до Цареграда, і ввійшли до царя. Цар же запитав: &laquo;Ради чого прийшли?&raquo; Вони ж розповіли йому про все, що було.</p>\r\n<p>Послухав їхню розповідь цар і був радий, і честь велику виявив їм у той день. На ранок же послав до патріарха, кажучи таке: &laquo;Прийшли руси вивчати віру нашу. Так приготуй церкву і клірос і сам одягнися в святительські ризи, хай побачать славу Бога нашого&raquo;. І почув це патріарх, і звелів зібрати весь клір, і, як звичайно, відправив святкову службу, і кадила розпалили, і організували співи і хори. І пішов і цар з ними до церкви. І поставили їх на почесному місці, показуючи красу церковну, і співи, і службу архієрейську, поставу дияконів, і розповіли їм про служіння Богові своєму. Вони ж були в захопленні, і дивувалися, і хвалили службу їхню. І закликали їх царі Василій і Костянтин і сказали їм: &laquo;Ідіть в землю вашу&raquo;. І відпустили їх з дарами великими і честю. Вони ж повернулися в землю свою. І скликав князь своїх бояр і старійшин. Рече Володимир: &laquo;Оце повернулися послані нами мужі, послухаємо про все, що було з ними&raquo;. І сказав їм: &laquo;Говоріть перед дружиною&raquo;. Вони ж сказали: &laquo;Найперше ходили до болгар і дивилися, як вони моляться в храмі, точніше сказати, в мечеті, стоячи там без пояса. А поклониться - сяде і дивиться сюди і туди, як божевільний. І нема в них радості, а [тільки] печаль і сморід великий. І недобра віра їхня.</p>\r\n<p>І прийшли до німців, і бачили як відправляють службу божу, а краси не бачили ніякої. І прийшли ми в Греки, і водили нас туди, де служать Богові своєму, і не знали, чи ми на небесах були, чи на землі: нема-бо на землі такого видовища чи краси такої, щоб зуміли порівнять із нею. Знаємо тільки, що там Бог із людьми пробуває і служба їхня лучча, аніж в усіх інших народів. Не можемо забути ми краси тієї, бо всякий чоловік, коли спочатку покуштує солодощів, потім не захоче гіркого прийняти. Так і ми не маємо уже так жити&raquo;. Бояри порадились і сказали: &laquo;Якби поганий був закон грецький, то не прийняла б хрещення баба твоя Ольга, а була вона наймудріша із всіх людей&raquo;. Порадившись, Володимир спитав: &laquo;То де приймемо хрещення?&raquo; Вони ж відповіли: &laquo;Де тобі любо&raquo;.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>И минувшу л<small>Ђ</small>ту. В л<small>Ђ</small>то 6496, иде Володимеръ с вои на Корсунь, градъ Гр<small>Ђ</small>чкый, и затворишася Корсуняни въ град<small>Ђ</small>. И ста Володим<small>Ђ</small>ръ объ онъ полъ града в лимени, въдал<small>Ђ</small> града стр<small>Ђ</small>лища единого, и боряхуся кр<small>Ђ</small>пко горожан<small>Ђ</small> с ними, Володимеръ обьстоя градъ. И изнемогаху людие въ град<small>Ђ</small>, и рече Володимеръ к гражаномъ: &laquo;аще ся не вдасте, имамъ стояти за 3 л<small>Ђ</small>тъ&raquo;. Володимеръ же изряди воя своя, и повед<small>Ђ</small> присну сыпати к граду. Симъ же спущимъ, Корсуняне подкопавше ст<small>Ђ</small>ну градьскую, крадяху сыпленую персть, и ношаху к соби в градъ, сыплюще посред<small>Ђ</small> града; вои же присыпаху боле, и Володимеръ стояше. И се мужь, именемъ Анастасъ, Корсунянинъ, стр<small>Ђ</small>ли, написавъ на стр<small>Ђ</small>л<small>Ђ</small>: &laquo;кладязи, яже суть за тобою отъ вьстока, изъ того вода идеть по труб<small>Ђ</small>; копавше преймете воду&raquo;. Володимеръ же се слышавъ, възр<small>Ђ</small>въ на небо и рече: &laquo;аще ся сбудеть се, имамъ креститися&raquo;. И ту абье повел<small>Ђ</small> копати прекы трубамъ, и переяша воду; и людье изнемогаху жажею водною, и предашася. И вниде Володимеръ въ градъ и дружина его, и посла Володимиръ къ цареви Василию и Костянтину, глаголя сице: &laquo;се градъ ваю славный взяхъ; слышю же се, яко сестру имаете двою, да аще ею не вдасте за мя, то створю граду вашему, якоже и сему створихъ&raquo;. И се слышавша царя, быста печална, посласта в<small>Ђ</small>сть, сице глаголюще: &laquo;не дост[оит]ь крестьянамъ за поганыя посягати и даяти; аще ли ся крестиши, приимеши се, и получиши царство небесное, и с нами единов<small>Ђ</small>рникъ будеши; аще ли сего не хощеши створити, не можев<small>Ђ</small> дати сестры своей за тя&raquo;. И се слышавъ Володим<small>Ђ</small>ръ, и рече посланымъ отъ царю: &laquo;глаголете царема тако: яко азъ крещюся; яко испытахъ преже сихъ дний законъ вашь, и есть ми любъ, и в<small>Ђ</small>ра ваша и служение, иже ми испов<small>Ђ</small>даша послании нами мужи&raquo;. И се слышавша царя, и ради быста, и умолиста сестру свою именемъ Анну, и посласта къ Володимеру, глаголющи: &laquo;крестися, тогда послев<small>Ђ</small> сестру свою к тоб<small>Ђ</small>&raquo;. И рече Володимиръ: &laquo;да пришедше [съ] сестрою вашею, крестять мя&raquo;. И послушаста царя, и посласта сестру свою, и сановникы н<small>Ђ</small>кыя, и прозвутеры; она же не хотяше ити, яко въ поганыя, и рече имъ: &laquo;луче бы ми сд<small>Ђ</small> умрети&raquo;. И речеста ей брата: &laquo;егда како обратить Богъ Рускую землю въ покаяние, и Гр<small>Ђ</small>чкую землю избавиши отъ лютыя рати; видиши ли, колико зло створиша Русь Гр<small>Ђ</small>комъ? нын<small>Ђ</small> же, аще не идеши, тоже имуть творити намъ&raquo;; и одва принудиста. Она же вс<small>Ђ</small>дши в кубару, ц<small>Ђ</small>ловавши ужикы своа с плачемь, поиде чресъ море; яко приде къ Корсуню, изл<small>Ђ</small>зоша Корсуняни съ поклономъ, и введоша ю въ градъ, и посадиша ю в полат<small>Ђ</small>. По Божью же строенью, въ се время разбол<small>Ђ</small>лся Володимиръ очима, и не видяше ничтоже, и тужаше велми, и не домышляше, что сотворити; и посла къ нему цариця, рекуще: &laquo;аще хощеши болезни сея избыти, то вьскор<small>Ђ</small> крестися; аще ли ни, то не имаеши избыти сего&raquo;. И си слышавъ Володимеръ, [рече:]: &laquo;аще се истина будеть, по ист<small>Ђ</small>н<small>Ђ</small> великъ Богъ крестьянескъ&raquo;. И повел<small>Ђ</small> крестити ся. И епископъ же Корсуньскый с попы царицины, огласивъ и, и крести Володим<small>Ђ</small>ра, и яко возложи руку на нь, абье прозр<small>Ђ</small>. Видивъ же се Володимеръ напрасное исц<small>Ђ</small>ление, и прослави Бога, рекъ: &laquo;топервое увид<small>Ђ</small>хъ Бога истиньнаго&raquo;. Си же увидивше дружина его, мнози крестишася. Крести же ся въ церкви святое Софьи, и есть церкви та стояще в Корсуни град<small>Ђ</small>, на м<small>Ђ</small>ст<small>Ђ</small> посред<small>Ђ</small> града, идеже торгъ д<small>Ђ</small>ють Корсунян<small>Ђ</small>; полата Володим<small>Ђ</small>ря воскрай церкви стоить и до сего дни, и царицина полата за олътаремь. По крещении же приведе царицю на обручение. Се же не св<small>Ђ</small>дуще право, глаголють, яко крестился есть в Кыев<small>Ђ</small>; инии же р<small>Ђ</small>ша: въ Василев<small>Ђ</small>, друзии же р<small>Ђ</small>ша, инако сказающе. И крещену же Володимеру въ Корсуни, предаша ему в<small>Ђ</small>ру крестьяньскую, рекуще сице: &laquo;да не прельстять тебе н<small>Ђ</small>ции отъ еретикъ, но в<small>Ђ</small>руй, сице глаголя:</p>\r\n<p>&laquo;В<small>Ђ</small>рую въ единого Бога Отца, Вседержителя, Творца небу и земл<small>Ђ</small>, и до конца в<small>Ђ</small>ру сию. И пакьи в<small>Ђ</small>рую въ единого Бога Отца нерожена, и въ единого Сына рожена, и въ единъ Святый Духъ исходящъ. Три собьства свершена, мьсльна, разд<small>Ђ</small>ляема числомъ и собьствомъ, и не божествомъ разд<small>Ђ</small>лна, и совокупляеться неразд<small>Ђ</small>лна. Отець бо Богъ Отець, присно сый пребываеть въ отечьств<small>Ђ</small>, нероженъ, безначаленъ, начало [и] вина всимъ единемь рожениемь сы Сыну и Духови; отъ негоже ражаеться Сынъ преже всіхъ в<small>Ђ</small>къ, исходить же Духъ Святый и безъвременьна и безъ л<small>Ђ</small>та; вкуп<small>Ђ</small> Отець, вкуп<small>Ђ</small> Сынъ, вкуп<small>Ђ</small> Духъ Святый есть. Сынъ подобосущенъ и безначаленъ Отцю, рожениемь точию равнествуя Отцю и Духу. Духъ есть пресвятый, Отцю и Сыну подобосущенъ и присносущенъ. Отцю бо отечьство, Сыну же сыновьство, Святому Духу исхожение. Ни Отець бо въ Сынъ или Духъ преступаетъ, ни Сынъ въ Отца и Духа, ни Духъ въ Сынъ или Отець, недвижна бо свойствья. Не трие бози, [но] единъ Богъ, понеже едино божество въ трехъ лицихъ. Хот<small>Ђ</small>ньемъ же Отца же и Духа свою пасти тварь, отечьскыхъ ядръ, иже не отступи, сшедъ и въ д<small>Ђ</small>вичьское ложе пресвятое, акы Божье с<small>Ђ</small>мя вшедъ, и плоть сьдушьвну, и словесну же и умну, не преже бывшю приимъ, изийде Богъ воплощенъ, родивыся неизр<small>Ђ</small>ченьн<small>Ђ</small>, и д<small>Ђ</small>вство матери схрани нетл<small>Ђ</small>ньно. Не смятение, ни разм<small>Ђ</small>шение и ни изм<small>Ђ</small>нения пострадавъ, но пребывъ, еже б<small>Ђ</small>, приемы рабий зракъ истиною, а не мечтаниемъ, всячьскы, разв<small>Ђ</small> гр<small>Ђ</small>ха, намъ подобенъ бывъ.</p>\r\n<p>Волею родися, волею взалка, волею вжада, волею трудися, волею устрашися, волею умре истиною, а не мечтаниемъ; вся св<small>Ђ</small>ршена, не оклеветанный страсти челов<small>Ђ</small>чества. Распятъ же ся, смерти вкуси безъгр<small>Ђ</small>шный, и въскресъ въ своей плоти, и не в<small>Ђ</small>д<small>Ђ</small>вши истл<small>Ђ</small>ния, на небеса взыиде и седе одесную Отца, и придеть же пакы съ славою судити живымъ и мертвымъ; якоже взийде сь своею плотью, тако и снидеть. Къ симъ едино крещение испов<small>Ђ</small>даю водою и Духомъ, приступаю къ пречистымъ тайнамъ, в<small>Ђ</small>рую въ истину т<small>Ђ</small>ло и кровь, и приемлю церковьная предания, и кланяюся честнымъ иконамъ, кланяюся древу честному и кресту и всякому кресту, и святымъ мощемь и святымъ сьсудомъ&raquo;.</p>\r\n<p>&laquo;В<small>Ђ</small>ру[й] же Семи Сборъ святыхъ отець: иже есть первый в Никии 300 и 18, иже проклята Арья, и пропов<small>Ђ</small>даша в<small>Ђ</small>ру непорочну и праву; вторый же Сборъ в Костянтин<small>Ђ</small>град<small>Ђ</small> святыхъ отець 100 и 50, иже прокляша Македонья духоборца, и пропов<small>Ђ</small>даша Троицю единосущную; 3-й же Сборъ въ Ефес<small>Ђ</small> святыхъ [отець] 100 на Несторья, егоже прокленше, пропов<small>Ђ</small>даша святую Богородицю; 4-й Сборъ в Халкидон<small>Ђ</small> святыхъ отець 600 и 30 на Евтуха и Диоскора, еюже прокленше святии отци,. изъгласивше свершена Бога и св<small>Ђ</small>ршена челов<small>Ђ</small>ка Господа нашего Ісуса Христа; 5-й Сборъ въ Цар<small>Ђ</small>град<small>Ђ</small> святыхъ отець 100 и 60 и 5 на Ерегенова предания, и на Евагрия, ихже прокляша святии отци; 6-й Сборъ въ Цар<small>Ђ</small>град<small>Ђ</small> святыхъ отець 100 и 70 на Сергиа и Кура, ихже прокляша святии отци; 7-й Сборъ въ Никеи святыхъ отець 300 и 50, прокляша, иже не покланяються иконамъ. Не приимай же отъ Латын<small>Ђ</small> учения, ихже учение развращено: вл<small>Ђ</small>зъше бо вь церковь, не покланяються иконамъ, но стоя поклониться, и поклонився напишеть кресть на земли и ц<small>Ђ</small>луеть, и вьстанеть простъ ногами на немъ; да легъ ц<small>Ђ</small>луеть, а вьставъ попираеть. Сего бо. апостоли не предаша; предали суть апостоли крестъ поставленъ ц<small>Ђ</small>ловати, иконы предаша. Лука бо еуангелистъ первое написанъ, посла вь миръ, якоже глаголеть Васил<small>Ђ</small>й; яко на первый образъ икона приходить. Пакы же землю глаголеть матерью. Да аще имъ есть земля мати, то отець имъ есть небо, искони створи Богъ небо и такоже землю; тако глаголеть: Отче нашь, иже еси на небеси. Аще ли по сихъ разуму земля есть мати, почто плюете на матерь свою, да семо ю лобызаете, а семо ю скв<small>Ђ</small>рните? Сего же Римлян<small>Ђ</small> не творяху, но исправляху на всихъ Сбор<small>Ђ</small>хъ, сходяще отъ Рима и отъ всихъ престолъ. На первомъ сбор<small>Ђ</small>, иже на Арья, иже в Никеи, отъ Рима преже Селивестръ посла епископы и прозвутеры; а отъ Александрия Афана [сі] я, отъ Царяграда Митрофанъ посла епископы отъ себ<small>Ђ</small>; и тако исправяху в<small>Ђ</small>ру. На вторемъ же Сбор<small>Ђ</small> отъ Рима Дамасъ, а отъ Александр<small>Ђ</small>а Тимоф<small>Ђ</small>й, отъ Антиохия Мелетий, Курилъ Ерусалимскый, Григор<small>Ђ</small>й Богословець. На третьемъ же Сбор<small>Ђ</small> Келестинъ Римьский, Курилъ Александрийский. На 4 же Сбор<small>Ђ</small> Леонтий Римьскый, Анатал<small>Ђ</small>й Царяграда, Увеналий Ерусалимскый. На пятомъ Сбор<small>Ђ</small> Римьский Вилигий, Евьтухий Царяграда, Аполинарий Александрийскый, Домнинь Антиохийскый. На шестомъ Сбор<small>Ђ</small> отъ Рима Агафонь, Георгий Царяграда, Феофанъ Антиохийскый, отъ Александрия Петръ мнихъ. На 7-мь Сбор<small>Ђ</small> Андрианъ отъ Рима, Тарасий Царяграда, Политьянь Александр<small>Ђ</small>йскый, Феодоръ Антиохийскый, Илья Ерусалимскый. Сии вси съ своими епископы и сходящеся и правяху в<small>Ђ</small>ру. По семемъ же сбор<small>Ђ</small> Петръ Гугнивый сь иними шедъ в Римъ и престолъ въсхитивъ, развративъ в<small>Ђ</small>ру, отв<small>Ђ</small>рзъся престола Ерусалимьскаго, и Александр<small>Ђ</small>скаго, и Царяграда, и Антиохийскаго. И возмутиша Италию всю, с<small>Ђ</small>юще учение свое раздно; т<small>Ђ</small>мъже держать не в одино съглашение в<small>Ђ</small>ру, но раздно: овии бо попов<small>Ђ</small> одиною женою оженився служать, и друзии до семи женъ поимающе служать. Ина же многа раздно держать, ихъже блюдися учения: пращають же гр<small>Ђ</small>хы на дару, еже есть зл<small>Ђ</small>е всего. Богъ да хранить, княже, отъ сего.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>І коли проминув рік, в літо 6496 [988] пішов Володимир із воями на Корсунь, город грецький, і закрилися корсунці в городі. І став Володимир поблизу, півколом від лиману на віддалі польоту стріли від города, і самовіддано боролися з ними горожани, і Володимир взяв город в облогу. І знемагали люди в городі, і рече Володимир горожанам: &laquo;Якщо не здастеся, то буду стояти і три роки&raquo;.</p>\r\n<p>Володимир же спорядив воїв своїх і повелів насип зводити до городських стін. У тих, що насипали, корсунці, підкопавши стіну городську, викрадали насипану землю, і виносили до себе в город, і зсипали посеред города. Вої ж присипали ще більше, і Володимир стояв. І от один корсун\'ський муж іменем Анастас пустив стрілу і написав на ній: &laquo;За тобою зі сходу колодязі, від них вода по трубі тече в город. Перекопай і перейми воду&raquo;. Володимир, як почув це, підняв очі в небо і сказав: &laquo;Якщо це збудеться - охрещуся&raquo;. І негайно повелів копати там поперечний рів, і перейняв воду. І люди знесилились від спраги і здалися. І ввійшов Володимир у город, і дружина його, і послав Володимир до царів Василія й Костянтина, кажучи так: &laquo;Оце город узяв ваш славний. Прочував, що маєте сестру дівицю, і якщо не віддасте за мене, то зроблю столиці вашій те, що цьому городу зробив&raquo;. І, почувши це, царі засумували, і послали відповідь, таке кажучи: &laquo;Не личить християнок за поганих віддавати. А коли охрестишся, то візьмеш її, і доступиш царства небесного, і з нами єдиновірником будеш. А коли цього не захочеш зробити, то не зможемо сестру свою віддать за тебе&raquo;. І вислухав це Володимир і сказав посланцям від царів: &laquo;Скажіть царям так: &laquo;Я охрещусь, оскільки ще до днів цих уподобав закон ваш, і любий він мені, і віра ваша, і богослужіння, про яке розповідали мені посланії нами мужі&raquo;. І почувши це, царі раді були; і вблагали сестру свою на ймення Анна, і послали до Володимира, кажучи: &laquo;Охрестись, тоді пошлемо сестру свою до тебе&raquo;. І рече Володимир: &laquo;Хай ті, що прийдуть з сестрою вашою, охрестять мене&raquo;. І послухали царі, і послали сестру свою, і сановників деяких, і священиків. Вона ж не хотіла іти, як до поганих, і говорила їм: &laquo;Лучче б мені тут умерти&raquo;. І сказали їй брати: &laquo;Може, якось наверне Бог землю Руську до покаяння, і Грецьку землю позбавиш від лютої війни, хіба не бачиш, скільки зла натворила Русь грекам? Тепер же, якщо не підеш, зроблять і нам те саме, [що в Корсуні]&raquo;. І ледве примусили. І сіла вона в кубару, і з плачем попрощалась із рідними своїми, й попливла через море. І коли прибула в Корсунь, то вийшли на берег корсунці з поклоном, і ввели її в город, і посадили її в палаті. Із божого допусту в це врем\'я розхворівся очима Володимир, і не бачив нічого, і затужив сильно, і не знав, що робити. І послала до нього цариця, кажучи: &laquo;Коли хочеш хвороби цієї позбутись, то охрестися негайно, а коли ні, то не збудешся цього&raquo;. І як почув це, сказав Володимир: &laquo;Якщо справді так буде, то воістину великий Бог християнський&raquo;. І повелів охрестити себе. І єпископ же корсунський із попами царициними благословив його і охрестив Володимира. І коли возложив руку на нього, Володимир у ту ж мить прозрів. Коли Володимир відчув це нагле прозріння, то прославив Бога і сказав: &laquo;Тепер тільки узнав Бога істинного&raquo;. Побачила це дружина його, і багато охрестилося. Хрестилися ж в церкві святої Софії, і стоїть та церква в Корсуні посеред города, де торгують корсунці. Палата Володимира окрай церкви стоїть і до сьогодні, а царицина палата - за вівтарем.</p>\r\n<p>Після охрещення Володимира привели царицю на обручення. Ті, що не знають цього, говорять, ніби Володимир хрестився у Києві, а другі - що в Василеві, а ще інші по-іншому говорять. І охрещеному в Корсуні Володимиру, після прийняття ним християнства, сказали таке: &laquo;Нехай не спокусять тебе ніякі єретики, але віруй, так промовляючи:</p>\r\n<p>&laquo;Вірую в єдиного бога Отця, Вседержителя, Творця неба і землі&raquo; - і до кінця цей символ віри. І ще: &laquo;Вірую в єдиного бога Отця ненародженого, і в єдиного Сина народженого, і в єдиний Святий Дух ісходящий&raquo;. У трьох лицях втілений, лише розумом поділений на три числа і лиця, а не божеського сутністю поділений, бо поєднується нероздільно. Отець бо, Бог отець, прісно сущий, в батьківстві перебуває, ненароджений, безначальний, початок і першопричина всьому, єдинонародженим Синові і Духові. Від нього ж народжується Син, раніше всіх часів ісходить же Дух Святий - і безвременно, і безсрочно. Разом Отець, разом Син, разом Дух Святий. Син - подобосущий і безначальний Отцю, народженням тільки відмінний від Отця і Духа. Дух же - пресвятий, Отцю і Синові подобосущий і прісносущий, Отцю бо - отецтво, Синові - синівство, Святому Духові - ісхоженіє. Ні Отець в Сина чи Духа не переходить, ні Син - в Отця і Духа, ні Дух - в Сина чи Отця: нерухомі-бо їх властивості. Не три боги, але один Бог, оскільки єдине божество у трьох лицях. Бажанням же Отця і Духа своє врятувати творіння батьківського сім\'я, що не відійшло, а зійшло і в дівоче лоно пресвятеє ввійшло, як боже сім\'я, і плоть одушевлена, і словесна, і розумна, якої раніше не було, і явився Бог втілений, народившись несказанно і зберігши непорушною непорочність матері. Не переніс ні сум\'яття, ні змішання, ані змін, але залишився, як був, прийняв вид рабський насправді, а не в уяві, у всьому, окрім гріха, був нам подібний.</p>\r\n<p>З власної волі народився, з власної волі проголодав, з власної волі відчув спрагу, з власної волі трудився, з власної волі відчув страх, з власної волі помер - помер насправді, а не в уяві, всі справжні, непідробні страждання людські пережив. Був розп\'ятий, смерть спізнав безгрішний, і воскрес в своїй плоті, і не зазнавши тління зійшов на небеса, і сів праворуч Отця, і прийде ж знову в славі судити живих і мертвих, як вознісся у своїй плоті, так і зійде. До цього єдине хрещення сповідую водою і Духом, приступаю до пречистих тайн, вірую воістину в тіло і кров, і приймаю церковні оповіді, і поклоняюся пречесним іконам, поклоняюся пречесному древу, і всякому хресту, і святим мощам, і святим сосудам&raquo;.</p>\r\n<p>&laquo;Віруй і в сім соборів святих отців, із них перший в Нікії - триста вісімнадцять [отців], що прокляв Арія і проповідував віру непорочну і праву. Другий собор в Костянтиніграді - святих отців сто п\'ятдесят, що прокляв духоборця Македонія і проповідував єдиносущну Трійцю. Третій собор - в Ефесі - сто святих отців супроти Несторія, проклявши якого, проповідували святу Богородицю. Четвертий собор в Халкідоні - шістсот тридцять святих отців супроти Євтуха і Діоскора, їх же прокляли святії отці і проголосили правдивим Богом і справжньою людиною Господа нашого Ісуса Христа. П\'ятий собор в Цареграді - святих отців сто шістдесят п\'ять супроти учення Орігена і супроти Євагрія, їх же прокляли святії отці. Шостий собор в Цареграді - святих отців сто сімдесят супроти Сергія і Кура, їх же прокляли святії отці. Сьомий собор в Нікеї - святих отців триста п\'ятдесят прокляли тих, хто не поклоняється святим іконам&raquo;.</p>\r\n<p>&laquo;Не приймай учення від латинян, їхнє учення спотворене: ввійшовши в церкву, не поклоняються іконам, а стоячи кланяються, а поклонившись, малюють хрест на землі і цілують, а вставши, стають ногами на нього. Так що лежачи - цілують, а стоячи - зневажають. Цього апостоли не навчали, апостоли учили цілувати поставлений хрест і шанувать ікони. Євангеліст Лука перший написав [ікону] і послав у світ [на поклоніння]. Василій говорить, що [шанування] ікони на першообраз її переходить. Знову ж землю називає матір\'ю. Хай же, коли земля їм - мати, то отець їм - небо, спервовіку сотворив Бог небо, а також землю. Так говорять: &laquo;Отче наш, іже єси на небеси&raquo;. А коли, на їхню думку, земля - мати, то нащо плюєте на матір свою? Тут же її лобизаєте, а тут її оскверняєте? Цього римляни не робили, але постановляли на всіх соборах, збираючись із Рима і із всіх єпархій. На перший собор в Нікеї проти Арія із Рима [папа] Селівестр послав єпископів і пресвітерів, із Александрії [патріарх] Афанасій, із Цареграда [патріарх] Митрофан послав від себе єпископів,- і так виправляли віру. На другому соборі із Рима [папа] Дамас, а із Александрії [патріарх] Тимофій, із Антіохії [патріарх] Мелетій. Кирило Єрусалимський, Григорій Богословець. На третьому ж соборі - [папа] римський Келестин, [патріарх] александрійський Кирило. На четвертому ж соборі - [папа] римський Леонтій, [патріарх] константинопольський Анатолій, [патріарх] єрусалимський Ювеналій. На п\'ятому соборі - [папа] римський Вілігій, [патріарх] константинопольський Євтухій, [патріарх] александрійський Аполлінарій, [патріарх] антіохійський Домнін. На шостому соборі від Рима [папа] Агафон, [патріарх] Георгій із Цареграда, [патріарх] антіохійський Феофан, від Александрії чернець Петро. На сьомому соборі від Рима [папа] Андріан [патріарх] Тарасій від Цареграда, [патріарх] Політіан александрійський, [патріарх] Феодор антіохійський, [патріарх] Ілля єрусалимський. Всі вони сходилися зі своїми єпископами і укріпляли віру. Після сьомого собору Петро Гугнивий разом із іншими ввійшов у Рим і захопив престол і віру сплюндрував, відкинувшись від престолу єрусалимського, александрійського, константинопольського і антіохійського. І збурив усю Італію, сіяв учення своє вороже і тим самим розкололи єдину віру: одні попи служать, маючи лише одну жону, а другі служать, маючи до семи жінок. Інші багато в чому по-різному чинять, їхнього учення належить оберігатися. Прощають вони і гріхи за дари, що найгірше всього. Хай береже тебе, княже, від цього Бог&raquo;.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Володимеръ же поимъ царицю и Настаса, и попы Корсуньскыя, мощи святаго Климента и Фива, ученика его, и поима сьсуды церковныя, иконы на благословенье себ<small>Ђ</small>. Постави же церковь святаго Іоанна Предтечю въ Корсун<small>Ђ</small> на гор<small>Ђ</small>, иже ссыпаще сред<small>Ђ</small> града, крадущи приспу, и яже и церкви стоить и до сего дни. Взяша же идя м<small>Ђ</small>дян<small>Ђ</small> 2 капищи, и 4 кон<small>Ђ</small> м<small>Ђ</small>дяны, иже и нын<small>Ђ</small> стоять за святою Богородицею; яко иже не в<small>Ђ</small>дуще мнятся мраморяны суща. Вдасть же за в<small>Ђ</small>но Корсунь Гр<small>Ђ</small>комъ цариц<small>Ђ</small> д<small>Ђ</small>ля, а самъ прииде Кыеву. И яко приде, повел<small>Ђ</small> кумиры испроврещи, овыи с<small>Ђ</small>щи, а другыя огньви предати; Перуна же повел<small>Ђ</small> привязати кь коневи хвосту и влещи с горы по Боричеву на ручай, и 12 мужа пристави бити жезлиемь. Се же не яко древу чюющу, но на поругание б<small>Ђ</small>су, иже прильщаше симъ образомъ челов<small>Ђ</small>кы, да возм<small>Ђ</small>стье приметь отъ челов<small>Ђ</small>къ. Велий еси, Господи, чюдная д<small>Ђ</small>ла твоя! вчера чьстимь отъ челов<small>Ђ</small>къ, а днесь поругаемь. И вл<small>Ђ</small>кому же ему по Ручаеви къ Дн<small>Ђ</small>пру, плакахуся его нев<small>Ђ</small>рнии людье, еще бо не бяху прияли крещения; и привлекше и, вринуша и въ Дн<small>Ђ</small>пръ. И пристави Володимеръ рекъ: &laquo;аще кде пристанеть вы, то отр<small>Ђ</small>вайте его отъ берега, доньдеже порогы пройдеть; тогда охабитеся его&raquo;. Они же пов<small>Ђ</small>леное створиша. Яко пустиша и пройде сквоз<small>Ђ</small> порогы, изв<small>Ђ</small>рже и в<small>Ђ</small>тръ на р<small>Ђ</small>нь, иже и до сего дни словетъ Перуня р<small>Ђ</small>нь. Посемъ же Володимиръ посла послы своя по всему граду, глаголя: &laquo;аще не обрящеться кто заутра на р<small>Ђ</small>ц<small>Ђ</small>, богать ли, убогъ, или нищь, или работенъ, противникъ мн<small>Ђ</small> да будеть&raquo;. И се слышавше людье, съ радостью идяху, радующеся, и глаголаху: &laquo;аще бы се не добро было, не бы сего князь и бояри прияли&raquo;.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Володимир же взяв царицю, і Настаса, і попів корсунських, і мощі святого Климента, і Фіва, учня його, і взяв сосуди церковні, і ікони на благословення собі. Поставив [Володимир] церкву святого Іоанна Предтечі в Корсуні на горі, яку насипали посеред города, коли викрадали землю із насипу. І та церква стоїть і до наших днів. Ідучи [з Корсуні] взяв він два мідні капища і четверо мідних коней, що і нині стоять за [церквою] святої Богородиці, невігласи думають, що вони мармурові. Володимир віддав Корсунь грекам як віно за царицю, а сам повернувся в Київ. І коли прийшов, повелів звергнути всіх кумирів: одних порубать, а других попалити. Перуна ж повеліа прив\'язати коневі до хвоста і волочити з гори Боричевим узвозом на Ручай, а дванадцять мужів приставив бити його жезлами. І робили це не тому, що дерево може відчувати, а для наруги над бісами, що спокушали цим образом людей,- хай прийме відплату від людей. Великий єси, Господи, і дивні діла твої! Вчора був шанований людьми, а сьогодні зневажений. І коли тягли Перуна Ручаєм до Дніпра, оплакували його невірнії люди, бо ще не прийняли хрещення. І, притягнувши, вкинули його в Дніпро. І приставив Володимир [до нього людей], кажучи: &laquo;Якщо де пристане, то відпихайте його від берега аж доки не пройде пороги, тоді залишите його&raquo;. Вони ж виконали, як було звелено. І коли пустили Перуна і пройшов він через пороги, викинув його вітер на рінь, що й до сьогодні називається Перунова рінь. Після цього Володимир послав своїх посланців по всьому городу сказати: &laquo;Якщо завтра хтонебудь не буде на річці: чи багатий, чи вбогий, чи жебрак, а чи раб,- той буде ворог мій&raquo;. І почувши це, люди йшли з радістю, раділи і говорили: &laquo;Якби це не було добре, не прийняли б цього князь і бояри&raquo;.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Наутр<small>Ђ</small>я же изииде Володим<small>Ђ</small>ръ съ попы царицины и Корсуньскыми на Дн<small>Ђ</small>пръ, и снидеся бещисла людии: и вл<small>Ђ</small>зоша въ воду, и стояху ови до ше<small>Ђ</small>, а друзии до персий, млад<small>Ђ</small>и же отъ берега, друзии же младенци держаще, св<small>Ђ</small>ршении же бродяху, попов<small>Ђ</small> же стояще молитвы творяху. И бяше видити радость велика на небеси и на земля, толико душъ спасаемыхъ; а дьяволъ стеняше, глаголя: увы мн<small>Ђ</small>, яко отсюду прогонимь есмь! зд<small>Ђ</small> бо мн<small>Ђ</small>хъ жилище им<small>Ђ</small>ти, яко сд<small>Ђ</small> не суть учения апостольская, ни суть в<small>Ђ</small>дуще Бога, но веселяхуся о служб<small>Ђ</small> ихъ, еже служаху мн<small>Ђ</small>; иже се поб<small>Ђ</small>жаемь есмь отъ нев<small>Ђ</small>гласа сего, а не отъ апостолъ и мученикъ, и ни имамъ уже царствовать во странахъ сихъ. Крестившимъ же ся людемъ, идоша когождо в домы своя. Володим<small>Ђ</small>ръ же радъ бывъ, яко позна Бога самъ и людие его, и возр<small>Ђ</small>въ на небо и рче: &laquo;Боже великый, створивый небо и землю! призри на новыя люди своя, вдай же имъ, Гослоди, ув<small>Ђ</small>дити тебе, истеньнаго Бога, якоже увидиша страны крестьяньскыя; и утверди у нихъ в<small>Ђ</small>ру правую и несъвратну; мн<small>Ђ</small> помози, Господи, на супротивнаго врага, да над<small>Ђ</small>юся на тя и на твою державу, поб<small>Ђ</small>жаю козни его&raquo;. И се рекъ, повел<small>Ђ</small> рубити церькви и поставляти по м<small>Ђ</small>стомъ, идеже стояше кумиры; и постави церковь святаго Василья на холм<small>Ђ</small>, ид<small>Ђ</small>же стояша кумири Перунъ и прочии, идеже требы творяху князь и людье; и на [ча] ставити по градомъ церкви и попы, и людие на крещение приводити по всемъ град [омъ] и селомъ. И пославъ, нача поимати у нарочитой чади д<small>Ђ</small>ти и даяти на учение книжное а матери же чадъ своихъ плакахуся по нихъ, и еще бо ся бяху не утв<small>Ђ</small>рдил<small>Ђ</small> в<small>Ђ</small>рою, но акы по мерьтв<small>Ђ</small>ц<small>Ђ</small> плакахуся. Симь же раздаянымъ на учение книжное, и сбысться пророчьство на Руской земл<small>Ђ</small>, глаголющее: вь оны дни услышать глусии словеса книжная, яснъ будеть языкъ гугнивыхъ. Си бо не б<small>Ђ</small>ша пр<small>Ђ</small>ди слышали словеса книжная, но по Божью строенью, и по милости своей помилова Богъ, якоже рче Пророкъ: помилую, егоже хощю. Помилова бо ны пакы банею бытия и обновлениемь Духа, и по изволению Божию, а не по нашимъ д<small>Ђ</small>ломъ. Благословенъ Господь Ісусъ Христосъ, иже вьзлюби новыя люди, Рускую землю, и просв<small>Ђ</small>ти ю крещениемь святымь. Т<small>Ђ</small>мже и мы припадаемь к нему, глаголюще: Господи Ісусе Христе! что ти воздамы о всихъ. яже ты въздасть намъ, гр<small>Ђ</small>шнымъ сущимъ? недоум<small>Ђ</small>емь противу даромъ твоимъ въздати, велий бо еси, чюдна д<small>Ђ</small>ла твоя, и величью твоему н<small>Ђ</small>сть конца, и роды и родъ въсхвалимъ д<small>Ђ</small>ла твоя, рекуще съ Давидомъ: придете, възрадуемься Господеви, и воскликнемь Богу и Спасу нашему, варимъ лице его испов<small>Ђ</small>даниемь; испов<small>Ђ</small>дающеся ему, яко благъ, яко въ в<small>Ђ</small>кы милость его, яко избавилъ еси отъ врагъ нашихъ, рекше отъ идолъслужитель. И пакы рчемъ съ Давидомъ: воспойте Господеви п<small>Ђ</small>снь нову, воспойте Господеви вся земля, въспойте Господеви, благословите имя его, благов<small>Ђ</small>стите день отъ дни спасение его, възв<small>Ђ</small>стите вь языц<small>Ђ</small>хъ славу его, и во вс<small>Ђ</small>хъ людехъ чюдеса его; яко велий Господь хваленъ з<small>Ђ</small>ло, и величью его н<small>Ђ</small>сть конца. Колика ти радость! не единъ, ни два спасаеться. Рече бо Господь: яко радость бываеть на небеси о един<small>Ђ</small>мъ гр<small>Ђ</small>шниц<small>Ђ</small> кающемся; се же не единъ, ни два, но бещисленое множьство къ Богу приступаша, святымь крещениемь просв<small>Ђ</small>щени. Якоже пророкъ рече: въскроплю на вы воду чисту, очиститеся отъ идолъ вашихъ и гр<small>Ђ</small>хъ вашихъ. И пакы другый пророкъ рче: кто яко Богъ отъемля гр<small>Ђ</small>хы и преступая неправду? яко хотяй милостивь есть; тъ обратить и ущедрить ны, погрузи гр<small>Ђ</small>хы наша въ глубин<small>Ђ</small>. Ибо Павелъ глаголеть: братья! елико насъ креститься въ Христа Ісуса, и въ смерть его крестихомся, погребохомся убо съ нимъ крещениемъ вь смерть; да якоже въста Христосъ отъ мертвыхъ съ славою Отчею, такоже и мы въ обновлении житья пойдемь. И пакы: ветхая мимоидоша, и се быша нова; нын<small>Ђ</small> приближися намъ спасение, нощь успе, а день приближися, имъже прив<small>Ђ</small>дение обр<small>Ђ</small>тохомъ в<small>Ђ</small>рою князя нашего Володимера въ благость сию, имъже восхвалимся и стоимъ; нын<small>Ђ</small> же свободивъшеся отъ гр<small>Ђ</small>ха, поработившеся Господеви, имате плодъ вашь вь священие.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Ранком [Наступного дня] зійшов Володимир із царициними і корсунськими попами до Дніпра, і зійшлось людей без ліку: і ввійшли всі в воду, і стояли у ній по шию, а другі - до грудей, малі біля берега, інші тримали дітей, на руках, дорослі бродили, а попи ж стояли і творили молитву. І було видно радість велику на небі і на землі: стільки душ врятованих! А диявол стогнав, причитаючи: &laquo;О горе мені! Прогнаний я звідси! Тут думав мати житло, бо не було тут учення апостольського, не знали тут Бога, і радів я із служіння тих, що служили мені. А тепер я переможений невігласом, <em>а </em>не апостолами і мучениками, і вже більше не буду царствувати в цих землях&raquo;.</p>\r\n<p>Охрещені ж люди розійшлися по домівках своїх. Володимир був радий, що пізнав Бога сам і люди його, подивився на небо і сказав: &laquo;Боже великий, що створив небо і землю! Зглянься на нових людей своїх, дай же їм, Господи, пізнати тебе, істинного Бога, як пізнали тебе країни християнські, і утверди в них віру правильну і неухильну. Поможи мені, Господи, проти супротивного ворога, надіюся на тебе і на твою силу в перемозі над його підступами&raquo;. І, сказавши це, повелів будувати церкви і ставити їх на місцях, де стояли ідоли. І поставив церкву святого Василія на горі, де стояли ідоли - Перун і інші і де приносили їм жертви князь і люди. І почав ставити церкви і попів по інших городах, і людей приводити на хрещення по всіх городах і селах. І почав [Володимир] посилати забирать дітей у нарочитих мужів своїх і віддавати їх на навчання книжне. А матері плакали за чадами своїми, як за померлими, бо ще не були кріпкі у вірі. Через оцих, відданих на навчання книжне, і збулося пророцтво на Руській землі, в якому сказано: &laquo;У ті дні глухі почують слова книжні і ясною буде мова гундосих&raquo;. Ці-бо не чули раніше мови книжної, але з божого допусту і з милості своєї помилував їх Бог, як сказав пророк: &laquo;Помилую, кого захочу&raquo;. Помилував-бо нас святим хрещенням і оновленням Духа, і за милістю божою, а не за нашими ділами. Благословен Господь Ісус Христос, що возлюбив новоохрещених, Руську землю, і просвітив її святим хрещенням. От тому і ми припадаємо до нього, кажучи: &laquo;Господи Ісусе Христе! Чим воздамо тобі за все, що ти воздав нам грішним? Не знаємо, чим воздати за дари твої, великий бо єси, дивні діла твої, і величі твоїй немає меж&raquo;. Із роду в рід будемо восхваляти діла твої, говорячи разом із Давидом: &laquo;Прийдіть, возрадуємося Господові і воскликнемо Богу і Спасителю нашому, предстанемо перед лицем його у славослов\'ї&raquo;, славте його, бо він благий, бо вічна милість його, бо звільнив нас від ворогів наших, тобто ідолослужителів. І ще скажемо з Давидом: &laquo;Заспівайте Господу нову пісню! Заспівайте Господу всім народом, заспівайте Господу, благословіть ім\'я його, благовістіть з дня на день спасіння його, возвістіть в народах славу його, і серед усіх людей чуда його, бо великий Господь і всеславний, і величі його нема кінця&raquo;. Яка радість! Не один і не два-бо зазнають спасіння. Сказав Господь: &laquo;Радість на небі буває і від одного грішника, що покаявся&raquo;. А тут не один, не два, а незліченна кількість до Бога приступила, святим хрещенням просвітилася. Як сказав пророк: &laquo;Окроплю вас водою чистою, і очиститесь від ідолопоклонства вашого і гріхів ваших&raquo;. І ще другий пророк сказав: &laquo;Хто, як не Бог, прощає гріхи і не ставить в провину неправду? І бажаючи цього - милостивий. Той наверне [до себе], і ущедрить нас, і ввергне у бездну гріхи наші&raquo;. І [апостол] Павло говорить: &laquo;Браття! Скільки нас охрестилося в Ісуса Христа, і в смерть його хрестимся, і хрещенням погребені-бо з ним у смерть, нехай, як Христос воскрес із мертвих славою Отця, так і ми підемо в оновленому житті&raquo;. І ще: &laquo;Старе минуло, тепер все нове&raquo;. &laquo;Нині наблизилося до нас спасіння, ніч минула, а день настав&raquo;. Завдяки князеві нашому Володимиру придбали ми доступ через віру до цієї благодаті, завдяки йому здобудемо славу і стоїмо. Нині ж, звільнившись від гріха і ставши рабами Господа, одержали плід свій у святості.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Т<small>Ђ</small>мъже долъжни есми рабътати Господеви, радующеся ему; рече бо Давидъ: работайте Господеви съ страхомъ, и радуйтеся ему с трепетомъ. Мы же вопиемь къ Владыц<small>Ђ</small> Богу нашему, глаголюще: благословенъ Господь, иже не дасть насъ в ловитьву зубомъ имъ; с<small>Ђ</small>ть скрущися, и мы избавлени быхомъ отъ прелести дьяволя. И погыбе память его съ шюмомъ, и Господь вь в<small>Ђ</small>кы пребываеть, хвалимъ отъ Рускихъ сыновъ, поеиь въ Троици; а демони проклинаемы отъ благов<small>Ђ</small>рныхъ мужь и отъ гов<small>Ђ</small>йньныхъ женъ, иже прияли суть крещение покаяние въ отпущение гр<small>Ђ</small>ховъ, нови людье крестьянстии, избрани Богомъ. Володимиръ же просв<small>Ђ</small>щенъ самъ, и сынови его, и земля его. Б<small>Ђ</small> бо у него сыновь 12: Вышеславъ, Изяславъ, Святополкъ, и Ярославъ, Всеволодъ, Святославъ, Мьстиславъ, Борисъ и Гл<small>Ђ</small>бъ, Станиславъ, Позвиздъ, Судиславъ. И посади Вышеслава въ Нов<small>Ђ</small>город<small>Ђ</small>, а Изяслава въ Полотьсц<small>Ђ</small>, а Святополка въ Туров<small>Ђ</small>, Ярослава въ Ростов<small>Ђ</small>; и умершю же стар<small>Ђ</small>йшому Вышеславу въ Нов<small>Ђ</small>город<small>Ђ</small>, и посади Ярослава въ Нов<small>Ђ</small>город<small>Ђ</small>, а Бориса въ Ростов<small>Ђ</small>, а Гл<small>Ђ</small>ба въ Муром<small>Ђ</small>, Святослава въ Деревехъ, Всеволода въ Володимер<small>Ђ</small>, Мьстислава въ Тмуторокан<small>Ђ</small>. И рече Володимеръ: &laquo;се не добро есть мало городовъ около Кыева&raquo;. И нача ставити городи по Десн<small>Ђ</small>, и по Устрьи, по Трубешеви, и по Сул<small>Ђ</small>, и по Стугн<small>Ђ</small>; и нача нарубати мужи лутши отъ Словенъ, и отъ Кривичъ, и отъ Чюдий, и отъ Вятичь, и отъ сихъ насели и грады; б<small>Ђ</small> бо рать отъ Печен<small>Ђ</small>гъ, и б<small>Ђ</small> воюяся с ними и одоляя имъ.</p>\r\n<p>В л<small>Ђ</small>то 6497.</p>\r\n<p>В л<small>Ђ</small>то 6498.</p>\r\n<p>В л<small>Ђ</small>то 6499. Пос<small>Ђ</small>мъ же Володимиру живущю в закон<small>Ђ</small> крестьяньст<small>Ђ</small>мъ, и помысли создати каменую церковь святыя Богородица, и пославъ приведе мастеры отъ Грькъ. Заченшю здати, яко сконча зижа, украси ю иконами, и поручивъ ю Настасу Корсунянину, и попы Корсуньския пристави служити въ ней, вда ту все, еже б<small>Ђ</small> взялъ въ Корсуни, иконы, и ссуды церковныя, и кресты.</p>\r\n<p>В л<small>Ђ</small>то 6500. Володимеръ заложи градъ Б<small>Ђ</small>лъ и наруби въ нъ отъ ин<small>Ђ</small>хъ градъ, и много людий сведе в онь; и б<small>Ђ</small> бо любя городъ ось.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>А тому й повинні служити Господу і радуватися йому, як сказав Давид: &laquo;Служіть Господу із страхом і радуйтеся йому з трепетом&raquo;. Ми ж вопієм до владики Бога нашого словами: &laquo;Благословен Господь, що не дав нас у здобич зубам їхнім. Сіть порвалася, і ми звільнені від обману диявола. І загине слава його з шумом, а Господь пребуде в віках і прославлятиметься руськими синами, оспіваний в Трійці, а демони проклинатимуться благовірними мужами і вірними жонами, котрі прийняли хрещення і покаяння у відпущенні гріхів. Нові люди, вибрані богом,- християни!&raquo;</p>\r\n<p>Володимир просвітився сам, і сини його, і земля його.</p>\r\n<p>Було ж у нього дванадцять синів: Вишеслав, Ізяслав, Святополк, Ярослав, Всеволод, Святослав, Мстислав, Борис, Гліб, Станіслав, Позвізд, Судислав. І посадив Вишеслава в Новгороді, а Ізяслава в Полоцьку, а Святополка в Турові, Ярослава в Ростові, а коли помер старший Вишеслав у Новгороді, посадив у ньому Ярослава, а Бориса - в Ростові, а Гліба - в Муромі. Святослава - в Деревах, Всеволода - у Володимирі, Мстислава - у Тмуторокані. І сказав Володимир: &laquo;Це погано, що мало городів навколо Києва&raquo;. І почав ставити городи по Десні, і по Острі, і по Трубежу, і по Сулі, і по Стугні. І став набирати луччих мужів від словен, від кривичів, від чуді і від в\'ятичів,- і ними заселяв городи, бо була війна з печенігами. І, воюючи з ними, перемагав їх.</p>\r\n<p>В літо 6497 [989].</p>\r\n<p>В літо 6498 [990].</p>\r\n<p>В літо 6499 [991]. Після цього жив Володимир у вірі християнській. І задумав збудувати кам\'яну церкву святої Богородиці, і послав привести грецьких майстрів. І розпочав будувати, а коли скінчив будову, украсив її іконами і доручив її Настасу Корсунцю, і поставив попів корсунських служити в ній, віддавши до неї все, що взяв був у Корсуні: ікони, сосуди церковні і хрести.</p>\r\n<p>В літо 6500 [992]. Володимир заснував Білгород, і набрав до нього людей із інших городів, і багато людей поселив у ньому, бо любив цей город.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6501. Иде Володимиръ на Хорваты. Пришедшю же ему съ войны Хорватьской, и се Печенез<small>Ђ</small> придоша по оной сторон<small>Ђ</small> отъ Сулы; Володимеръ же [поиде] противу имъ, и уср<small>Ђ</small>т<small>Ђ</small> я на Трубеши над броду, кд<small>Ђ</small> нын<small>Ђ</small> Переяславль. И ста Володимеръ на сей стран<small>Ђ</small>, а Печен<small>Ђ</small>з<small>Ђ</small> на оной, и не см<small>Ђ</small>яху си на ону сторону, и они на сю сторону. И при<small>Ђ</small>ха князь Печен<small>Ђ</small>скый к р<small>Ђ</small>ц<small>Ђ</small>, и возва Володимира, и рече ему: &laquo;пусти ты свой мужь, а я свой, да ся борета; да аще твой мужь ударитъ моимъ, да не воюемься за три л<small>Ђ</small>та, и разийдошася раздно; аще ли нашь мужь ударить вашимь, да воюемь за три л<small>Ђ</small>та&raquo;. Володимеръ же пришедъ в товары, посла по товаромъ бирича, глаголя: &laquo;н<small>Ђ</small>туть ли такаго мужа, иже бы ся ялъ с Печен<small>Ђ</small>жяниномъ братися&raquo;? и не обр<small>Ђ</small>теся никд<small>Ђ</small>же. И заутра при<small>Ђ</small>хаша Печен<small>Ђ</small>з<small>Ђ</small>, а свой мужь приведоша, а нашихъ не бысть. И поча тужити Володим<small>Ђ</small>ръ, посылая по всимъ воемъ своимъ, и приде единъ мужь старъ к нему, и рече ему: &laquo;княже, есть у мене единъ сынъ дома менший, а сь четырма есмь вышелъ, а онъ дома: отъ д<small>Ђ</small>тьства си своего н<small>Ђ</small>сть кто имъ ударилъ: единою бо ми сварящю, оному же мнущю кожу, и разгн<small>Ђ</small>вася на мя, преторже черевии руками&raquo;. Князь же се слышавъ и радъ бысть, и посла по нь борзо, и приведоша и ко князю, и князъ пов<small>Ђ</small>да ему вся; сьи же рече: &laquo;княже! не в<small>Ђ</small>мь, могу ли с него, да искусите мя: н<small>Ђ</small>туть ли вола велика и силна&raquo;? И нал<small>Ђ</small>зоша волъ силенъ, и повел<small>Ђ</small> раздражати вола, и возложи [на] нь жел<small>Ђ</small>зо горяче, и пустиша вола, и поб<small>Ђ</small>же волъ мимо нь, и похвати вола рукою за бокъ, и выня кожю с мясы, елико ему рука я; и рече ему Володимеръ: &laquo;можесь ся с нимь бороти&raquo;. И назавтр<small>Ђ</small>е придоша Печен<small>Ђ</small>з<small>Ђ</small>, и почаша звати: &laquo;н<small>Ђ</small>сть ли мужа? се нашь досп<small>Ђ</small>лъ&raquo;. Володим<small>Ђ</small>ръ же повел<small>Ђ</small> той ночи обл<small>Ђ</small>щися въ оружье; и выпустиша Печен<small>Ђ</small>з<small>Ђ</small> мужь свой, и б<small>Ђ</small> превеликъ з<small>Ђ</small>ло, и страшенъ; и выступи мужь Володим<small>Ђ</small>рь, и възр<small>Ђ</small>въ Печен<small>Ђ</small>жинъ и посм<small>Ђ</small>яся, б<small>Ђ</small> бо средний т<small>Ђ</small>ломъ. И разм<small>Ђ</small>ривше межи об<small>Ђ</small>има полкома, и пустиша я къ соб<small>Ђ</small>, и ястася кр<small>Ђ</small>пко, и удави Печен<small>Ђ</small>жинина в руку до смерти, и удари имь о землю; и вьскликоша Русь, а печен<small>Ђ</small>з<small>Ђ</small> поб<small>Ђ</small>гоша, а Русь погнаша по нихъ, с<small>Ђ</small>куще <small>Ђ</small>, и прогнаша ихъ.</p>\r\n<p>Володим<small>Ђ</small>ръ же радъ бывъ, и заложи городъ на броду томъ и нарче и Переяславль, зане перея славу отрокъ. Володимиръ же великымь мужемь створи его и отца его. Володимиръ же възвратися вь Киевь с поб<small>Ђ</small>дою и славою великою.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6501 [993] ходив Володимир на хорватів.</p>\r\n<p>І тільки вернувся він із війни хорватської,</p>\r\n<p>І тут же прийшли по тім боці від Сули печеніги,</p>\r\n<p>І пішов проти них Володимир,</p>\r\n<p>І зустрів їх на Трубежі біля броду, де тепер Переяслав.</p>\r\n<p>І став Володимир по цьому боці, а печеніги - на тому</p>\r\n<p>І не зважувались наші на той бік перейти,</p>\r\n<p>І печеніги - на цей бік.</p>\r\n<p>І над\'їхав до ріки князь печенізький,</p>\r\n<p>І викликав Володимира.</p>\r\n<p>І рече йому: &laquo;Ти пусти свого мужа, а я свого - хай поборються,</p>\r\n<p>І коли твій муж ударить [об землю] моїм,</p>\r\n<p>То три, роки воювать не будем</p>\r\n<p>І розійдемося в різні боки.</p>\r\n<p>А коли наш муж ударить [об землю] вашим,</p>\r\n<p>Будем воювати вас три роки&raquo;.</p>\r\n<p>І прийшов у стан свій Володимир</p>\r\n<p>І по стану бирича послав спитати:</p>\r\n<p>&laquo;Чи нема у нас такого мужа, щоб до герцю став із печенігом?&raquo;</p>\r\n<p>І ніде такого не знайшлося.</p>\r\n<p>І над\'їхали ранком печеніги,</p>\r\n<p>І привели свого мужа, а наших не було.</p>\r\n<p>І засумував Володимир, до своїх всіх воїв посилав він.</p>\r\n<p>І прийшов до нього муж один, старий вже,</p>\r\n<p>І сказав йому: &laquo;Дома, княже, є у мене син найменший,</p>\r\n<p>Із чотирма я вийшов, а він дома,</p>\r\n<p>І з дитячих літ ніхто його не здужав.</p>\r\n<p>І один раз насварився був на нього, коли шкуру м\'яв,</p>\r\n<p>І, розгнівавшись на мене, він руками шкуру перервав&raquo;.</p>\r\n<p>І коли почув це князь, був дуже радий,</p>\r\n<p>І послав за ним і дуже швидко,</p>\r\n<p>І до князя привели його,</p>\r\n<p>І усе повідав йому князь.</p>\r\n<p>І сказав він: &laquo;Княже! Я не знаю, чи зумію його подолати,</p>\r\n<p>І випробуйте мене: чи нема вола, щоб був великий і сильний?&raquo;</p>\r\n<p>І знайшовся віл великий й сильний,</p>\r\n<p>І звелів його розлютувати.</p>\r\n<p>І розпеченим залізом роздражнили</p>\r\n<p>І тоді вола пустили.</p>\r\n<p>І, коли віл пробігав повз нього,</p>\r\n<p>Ухопив його за бік рукою</p>\r\n<p>Й вирвав шкуру з м\'ясом, скільки вхопив в руку.</p>\r\n<p>І рече йому [князь] Володимир: &laquo;Можеш з ним боротись&raquo;.</p>\r\n<p>І прийшли на ранок печеніги</p>\r\n<p>Й стали кликать: &laquo;Де ваш муж? От наш уже готовий!&raquo;</p>\r\n<p>Й повелів князь всім в ту ніч одіть оружжя.</p>\r\n<p>І випустили печеніги свого мужа,</p>\r\n<p>І страшний він був і дуже превеликий.</p>\r\n<p>І виступив муж Володимирів,</p>\r\n<p>І коли побачив печеніг, то розсміявся: бо середнього був зросту.</p>\r\n<p>І розміряли от місце між військами,</p>\r\n<p>І пустили їх один проти другого,</p>\r\n<p>І схопились вони кріпко,</p>\r\n<p>І здушив печеніга р.уками до смерті</p>\r\n<p>І об землю ним ударив.</p>\r\n<p>І вигукнула русь, а печеніги побігли,</p>\r\n<p>І русь гналася за ними,</p>\r\n<p>І сікла їх, і геть прогнала.</p>\r\n<p>І був дуже радий Володимир.</p>\r\n<p>І заклав на броді тім він город</p>\r\n<p>І ім\'я йому дав Переяслав,</p>\r\n<p>Бо &laquo;переяв славу&raquo; [князів] отрок.</p>\r\n<p>І зробив його великим мужем Володимир, :</p>\r\n<p>І отця його.</p>\r\n<p>І повернувся в Київ Володимир з перемогою</p>\r\n<p>І славою великою.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6502.</p>\r\n<p>В л<small>Ђ</small>то 6503.</p>\r\n<p>В л<small>Ђ</small>то 6504. Володимиръ же видивъ церковь св<small>Ђ</small>ршену, и вшедъ в ню помолися Богу, глаголя: &laquo;Господи Боже! призри с небеси и вижь, пос<small>Ђ</small>ти винограда своего, и св<small>Ђ</small>рши, яже насади десница твоя, люди сия новыя, имъже обратилъ еси сердца в разумъ, познати тебе истиньнаго Бога; и призри на церковь сию, юже создахъ недостойный рабъ твой, во имя рожьшая ти матери и Приснод<small>Ђ</small>выя Марья Богородица; и аще помолиться кто въ церкви сей, то услыши молитву его, и отпусти вся гр<small>Ђ</small>хы его, молитвы ради пречистыя Богородица&raquo;. И помолившюся ему, и рекъ сице: &laquo;се даю церкви сей свят<small>Ђ</small>й Богородиц<small>Ђ</small> отъ им<small>Ђ</small>ния своего и отъ моихъ градъ десятую часть&raquo;. И положи написавъ клятьву вь церкви сей, рекь: &laquo;аще сего посудить кто, да будеть проклятъ&raquo;. И вдасть десятину Анастасу Корсунянину, и створи же празникъ великъ в той день бояромъ и старцемъ градьскымъ, и убогимъ им<small>Ђ</small>ние много. По сихъ же придоша Печен<small>Ђ</small>з<small>Ђ</small> к Василеву, и Володим<small>Ђ</small>ръ с малою дружиною изыйде противу имъ, и съступившимся имъ, не могъ Володим<small>Ђ</small>ръ ст<small>Ђ</small>рь п<small>Ђ</small>ти противу, подб<small>Ђ</small>гь ста подъ мостомъ, и одва укрыся отъ противныхъ; и тогда об<small>Ђ</small>щася Володим<small>Ђ</small>ръ поставити церковь вь Василев<small>Ђ</small> святое Преображение, б<small>Ђ</small> бо празникъ Преображению Господню въ день, егда си бысть с<small>Ђ</small>ча. Избывъ же Володим<small>Ђ</small>ръ сего, постави церковь, и творяше празникъ, варя 300 переваръ меду, и зваше бояры своя, и посадникы, и стар<small>Ђ</small>йшины по всимъ градомъ, и люди многы, и раздаваше 300 гривенъ убогымъ. И празнова князь Володимеръ ту дний 8, и възвращашеться Кыеву на Успение святыя Богородица, и ту пакы празникъ св<small>Ђ</small>тель творяше, съзываше бещисленое множьство народа. Видяще же люди крестьяны суща, радовашеся душею и т<small>Ђ</small>ломъ, и тако по вся л<small>Ђ</small>та творяше. Б<small>Ђ</small> бо любя книжная словеса, слыша бо единою еуангелие чтомо: блажении милостивии, яко т<small>Ђ</small>и помиловании будуть; и пакы: продайте им<small>Ђ</small>ния ваша и дайте нищимъ; и пакы: не искрывайте соб<small>Ђ</small> сокровища на земли, идеже тля тлить и татье подъкоповають, но скрывайте соб<small>Ђ</small> скровище на небеси, идеже ни тля тлить, ни татье крадуть; и Давида глаголюща: благъ мужь милуя и дая; Соломона слыша глаголюща: дая нищимъ, Богу взаемь даеть. Си слышавъ, повел<small>Ђ</small> нищю всяку и убогу приходити на дворъ на княжь и взимати всяку потребу, питье и яденье, и отъ скотьниць купами. Устроя же се, рекъ: &laquo;яко немощни и болнии не могуть доити двора моего&raquo;, повеле устроити кола, и вьскладываше хл<small>Ђ</small>бы, мяса, рыбы, и овощь разноличный, и медъ въ бочкахъ, а вь другыхъ квасы возити по градомъ, въпрашающе: &laquo;кд<small>Ђ</small> болни и нищии, не могы ходити&raquo;? и т<small>Ђ</small>мь раздаваху на потребу. И се же творя людемь своимь по вся нед<small>Ђ</small>ля, устави по вся дни на двор<small>Ђ</small> вь гридници пиръ творити и приходити бояромь, и грыдьмъ, и соцькимъ, и десятникомъ, и нарочитымъ мужемь, и при княз<small>Ђ</small> и безъ князя; и бываше на об<small>Ђ</small>д<small>Ђ</small> томь множьство рих мясъ, и отъ скота, и отъ зв<small>Ђ</small>рины, и бяше же изобилью всего. Егда же подопьяхуться и начаху роптати на князя, глаголюще: &laquo;зло есть нашимъ головамъ! да намъ ясти древяными лжицами, а не серебряными&raquo;. И се слышавъ Володимиръ, повел<small>Ђ</small> исковати лжици сребряны ясти дружин<small>Ђ</small>, рекъ сице: &laquo;яко сребромъ и златомъ не имамъ нал<small>Ђ</small>сти дружины, а дружиною нал<small>Ђ</small>зу сребро и злато, яко д<small>Ђ</small>дъ мой и отець мой доискася дружиною злата и сребра&raquo;. Б<small>Ђ</small> бо любяше Володимиръ дружину, и с ними дума о строеньи землинемь, и о устав<small>Ђ</small> земленемь, и о рат<small>Ђ</small>хъ; и 6<small>Ђ</small> живя с князи околными его миромъ, с Болеславомъ Лядьскымъ, и съ Стефаномъ Угорьскымъ, и съ Ондроникомъ Чьшьскимъ, и б<small>Ђ</small> миръ межи има и любы. И живяще Володимиръ въ страс<small>Ђ</small> Божии.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6502 [994].</p>\r\n<p>В літо 6503 [995].</p>\r\n<p>В літо 6504 [996]. Володимир побачив, що церкву збудовано, і ввійшов до неї помолитися богу, промовляючи: &laquo;Господи Боже! Поглянь з неба, і дивись, і відвідай сад свій, і збережи, що насадила правиця твоя,- людей цих нових, серця яких ти навернув до розуму, дав пізнати себе, істинного Бога. І доглянь церкву цю, що збудував недостойний раб твій во ім\'я матері, яка народила тебе,- пріснодіви Марії Богородиці. І коли помолиться хто у цій церкві, то почуй молитву його і відпусти всі гріхи його, ради молитви пречистої Богородиці&raquo;. І помолився він і сказав так: &laquo;Оце даю церкві оцій святої Богородиці від себе і від моїх городів десяту частину&raquo;. І, постановивши, клятву написав в церкві цій, де сказано: &laquo;Коли це відмінить хтось, нехай буде проклятий!&raquo; ї дав десятину Анастасові Корсунцю. І влаштував у той день свято велике для бояр і старійшин городських, і вбогим роздав великі багатства. Згодом прийшли печеніги до Василева. І Володимир із малЬю дружиною вийшов проти них. І зійшовшись з ними, не міг вистояти Володимир, утік і став під мостом і ледве сховався від ворога. І тоді Володимир дав обіцянку поставити у Василеві церкву святого Преображенія, бо в той день, як була січа, був празник Преображенія господнього. Уникнувши небезпеки, Володимир поставив церкву і влаштував свято, наваривши триста перевар меду. І покликав своїх бояр, і посадників, і старійшин з усіх городів, і багато людей, і роздав убогим триста гривен. І празникував тут князь Володимир вісім днів, і повернувся до Києва в день Успіння святої Богородиці, і тут знову влаштував світлий празник, скликавши безчисленну кількість народу. Бачив, що люди його вже християни, і радувався душею і тілом, і так робив щороку. І ще любив слово книжне, прочитавши одного разу в Євангелії: &laquo;Блаженні милостиві, бо вони помилувані будуть&raquo;. І ще: &laquo;Продайте маєтності ваші і роздайте убогим&raquo;. І ще: &laquo;Не зберігайте для себе скарбів на землі, де міль точить і вори підкопують, але зберігайте собі скарби на небі, де ні міль не точить, ні злодії не крадуть&raquo;. І Давид говорить: &laquo;Благословенний муж милує і допомагає&raquo;. Чув він і слова Соломона: &laquo;Хто дає убогим - богові позичає&raquo;. Почув це і звелів жебракам всяким і убогим приходити на княжий двір і брати, що потрібно,- питво і їжу, і з казни - кунами. Влаштувавши це, казав: &laquo;Немощні і хворі не можуть дійти до двору мого&raquo;. І велів запрягати вола і, наклавши хліба, м\'яса, риби, і різних овочів, і мед в бочках, а в других - квас, розвозити по городу, питаючи: &laquo;Де хворі і жебраки, що не можуть ходить?&raquo; І роздавав їм все необхідне. І робив це для людей своїх щонеділі. І встановив в усі дні на своєму дворі у гридниці влаштовувати пири, щоб приходили туди бояри, і гриді, і соцькі, і десяцькі, і нарочиті мужі,- і коли є князь, і коли нема. І було на обідах тих багато м\'яса - і гов\'ядини, і дичини, і було всього в достатку. Одного разу підпили і почали нарікати на князя, кажучи: &laquo;Горе головам нашим! Дав нам їсти дерев\'яними ложками,\' а не срібними&raquo;. Почув це Володимир і звелів викувати срібні ложки, щоб їла дружина його. І сказав так: &laquo;Сріблом і золотом не знайду дружини, а з дружиною здобуду і срібло, і золото, як дід мій і батько мій з дружиною дошукувалися золота і срібла&raquo;. Бо любив Володимир дружину і радився з нею про розбудову держави, і про закони державні, і про війни. І жив із навколишніми князями в мирі - з Болеславом Лядським, і з Стефаном Угорським, і з Ондроником Чешським. І був мир між ними і любов. І жив Володимир у страсі божому.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>И умножишася разбоев<small>Ђ</small>, и рече епископъ Володимиру: &laquo;се умножишася разбойници; почто не казниши&raquo;? Онъ же рече: &laquo;боюся гр<small>Ђ</small>ха&raquo;. Они же р<small>Ђ</small>ша ему: &laquo;ты поставленъ еси отъ Бога на казнь злымъ, а на милование добрымъ; достоять ти казнити разбойника, нъ съ испытаниемь&raquo;. Володимеръ же отв<small>Ђ</small>ргъ виры, и нача казнити разбойникы, и р<small>Ђ</small>ша епископы и старци: &laquo;рать многа; а еже вира, то на конихъ и на оружьи буди&raquo;. И рече Володимиръ: &laquo;да тако буди&raquo;. И живяше Володимиръ по строенью д<small>Ђ</small>дню и отню.</p>\r\n<p>В л<small>Ђ</small>то 6505. Володимиру шедшю к Новугороду по в<small>Ђ</small>рхние во<small>Ђ</small> на Печен<small>Ђ</small>гы, б<small>Ђ</small> бо рать велика беспересту [па]. В то же время ув<small>Ђ</small>даша Печен<small>Ђ</small>з<small>Ђ</small>, яко князя н<small>Ђ</small>ту, придоша и сташа около Б<small>Ђ</small>лограда. И не дадяхуть вылести из града, б<small>Ђ</small> бо голодъ великъ вь град<small>Ђ</small>, и не лз<small>Ђ</small> Володимиру помочи, и не б<small>Ђ</small> лз<small>Ђ</small> поити ему, и еще бо ся бахуть не собрали к нему вои, Печен<small>Ђ</small>гъ же б<small>Ђ</small> множьство много. И удолжишася остояче вь град<small>Ђ</small> люди, и б<small>Ђ</small> гладъ великъ, и створиша в<small>Ђ</small>че вь град<small>Ђ</small>, и р<small>Ђ</small>ша: &laquo;се хочемь помрети отъ глада, и отъ князя помочи н<small>Ђ</small>тъ; да луче ли ны умрети? вдадимся Печен<small>Ђ</small>гомъ, да кого ли оживять, кого ли умертвять; уже помираемь отъ глада&raquo;. И тако св<small>Ђ</small>тъ створиша, и б<small>Ђ</small> же одинъ стар<small>Ђ</small>цъ не былъ в в<small>Ђ</small>чи томь, вьпрошаше: &laquo;что ради створиша в<small>Ђ</small>че людье&raquo;? и пов<small>Ђ</small>даша ему, яко утро хотять ся людье передати Печен<small>Ђ</small>гомъ.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>І почастішали розбої, і сказав єпископ Володимиру: &laquo;От побільшало розбійників, чому не караєш?&raquo; Він же відповів: &laquo;Боюся гріха&raquo;. Вони ж сказали йому: &laquo;Ти поставлений Богом на кару злим і на милість добрим. Тобі належить карати розбійника, але після встановлення вини&raquo;. Володимир же відмінив віри і почав карати розбійників. І сказали єпископи і старійшини: &laquo;Воєн маємо багато, а коли б у нас була віра, то пішла б вона на коні і на оружжя&raquo;. І сказав Володимир: &laquo;Хай так і буде&raquo;. І жив Володимир як було за діда і батька.</p>\r\n<p>&nbsp;</p>\r\n<p>В літо 6505 [997] пішов Володимир до Новгорода</p>\r\n<p>Зібрати верхніх воїв супроти печенігів,</p>\r\n<p>Бо війна була із ними безперервна.</p>\r\n<p>І тоді прочули печеніги, що князя немає,</p>\r\n<p>І прийшли, і Білоград взяли в облогу,</p>\r\n<p>І не давали з города вийти.</p>\r\n<p>І у городі був голод сильний.</p>\r\n<p>І не міг Володимир помогти,</p>\r\n<p>І не можна було піти йому,</p>\r\n<p>Іще-бо не зібрав доволі воїв,</p>\r\n<p>А печенігів сила-силенна була.</p>\r\n<p>І затяглася осада людей у городі,</p>\r\n<p>І був сильний голод.</p>\r\n<p>І зібрали віче в граді,</p>\r\n<p>І сказали: &laquo;Можемо ось з голоду померти,</p>\r\n<p>І від князя помочі нема.</p>\r\n<p>І хіба вже лучче нам померти?</p>\r\n<p>А здамося, то когось погублять,</p>\r\n<p>А когось же й жити полишають,</p>\r\n<p>А так з голоду усі ми повмираєм&raquo;.</p>\r\n<p>І на раду ту усі пристали.</p>\r\n<p>І знайшовся чоловік старенький,</p>\r\n<p>А на вічі тім не був присутній,</p>\r\n<p>І спитав: &laquo;Пощо віче люди збирали?&raquo;</p>\r\n<p>І повідали йому, що ранком хочуть люди здатись печенігам.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Се же слышавь, посла по стар<small>Ђ</small>йшины градьскыя и рче имъ: &laquo;слышахъ, яко хощете передатися Печен<small>Ђ</small>гомъ&raquo;. Они же р<small>Ђ</small>ша: &laquo;не ст<small>Ђ</small>рпять людье голода&raquo;. И рче имъ: &laquo;послушайте мене, не передадитеся за три дни, и азъ что вы велю, и сотворите&raquo;. И они же ради и об<small>Ђ</small>щашася послушати, и рче имъ: &laquo;сберете по горсти овса, или пшениц<small>Ђ</small>, или отробъ&raquo;; они же шедше ради снискаху. И повел<small>Ђ</small> же имъ створити ц<small>Ђ</small>жь, в немъже варять кисель, и повел<small>Ђ</small> копати кладезь, и вьставити тамо кадь, и налья ц<small>Ђ</small>жа кадь, и повел<small>Ђ</small> копати другий кладезь, и вьставити тамо другую кадь. Повел<small>Ђ</small> имъ искати меду, они же шедше взяша лукно меду, б<small>Ђ</small> бо погребено вь княжи медуши; и повел<small>Ђ</small> росытити воду велми, и вьльяти вь кадь и вь друз<small>Ђ</small>мь кладяз<small>Ђ</small> тако. Наутр<small>Ђ</small>я же посла по Печен<small>Ђ</small>гы; горожани же рекоша, шедше Печен<small>Ђ</small>гомъ: &laquo;поимете къ соб<small>Ђ</small> тали наша, а васъ до 10 мужь идете вь градъ, и видите, что ся д<small>Ђ</small>еть въ град<small>Ђ</small> нашемь&raquo;. Печен<small>Ђ</small>зи же ради бывше, мняще, яко хотять ся передати, а сами избраша лучшии мужи вь град<small>Ђ</small>, и послаша я вь градъ, да розъглядають, что ся д<small>Ђ</small>еть вь град<small>Ђ</small> у нихъ. И придоша вь градъ, и рекоша людие: &laquo;почто губите себе? коли можете перестояти насъ? аще стоите 10 л<small>Ђ</small>тъ, что можете створити намъ? им<small>Ђ</small>емь бо кормьлю отъ земля; аще ли не в<small>Ђ</small>руете, да видите своими очима&raquo;. И приведоша я кь кладязю, ид<small>Ђ</small>же ц<small>Ђ</small>жь и почерпоша в<small>Ђ</small>дромъ льяху в ладкы, и взряху пред ними, яко вариша пред ними кисель; и поемьша и приведоша кь другому кладязю, и почерпоша сыты, и почаша ясти первое сами, потомъ же и Печен<small>Ђ</small>з<small>Ђ</small>. И удивишася, рекоша: &laquo;не имуть сему в<small>Ђ</small>ры наши князи, аще не ядять сами&raquo;. И людье нальяша корчагу ц<small>Ђ</small>жа и сыты отъ кладязя, и вдаша Печен<small>Ђ</small>гомъ; они же пришедше пов<small>Ђ</small>даша вся бывшая. И вариша кисель и яша князи Печен<small>Ђ</small>жьстии, и подивишася и поемше талия своя, а он<small>Ђ</small>хъ пустивше, и вьсташа отъ града и вь своя идоша.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>І почувши це, послав по грродських старійшин</p>\r\n<p>І сказав їм: &laquo;Чув, що хочете здаватись печенігам?&raquo;</p>\r\n<p>І відповіли вони: &laquo;Люд голоду не стерпить&raquo;.</p>\r\n<p>І рече їм: &laquo;Ви послухайте мене,</p>\r\n<p>І ще три дні не здавайтесь,</p>\r\n<p>І що вам звелю, те і зробіте&raquo;.</p>\r\n<p>І вони зраділи, обіцяли слухатись.</p>\r\n<p>І рече їм: &laquo;Зберете вівса по жмені, чи пшениці, а чи висівок&raquo;.</p>\r\n<p>І вони охоче пішли і зібрали.</p>\r\n<p>І звелів їм ціж зробити, з нього кисіль варять,</p>\r\n<p>І колодязь повелів копати,</p>\r\n<p>І кадіб поставити у ньому</p>\r\n<p>І налити ціжу в кадіб.</p>\r\n<p>І звелів копать другий колодязь.</p>\r\n<p>І у ньому вставить другий кадіб,</p>\r\n<p>І звелів їм меду пошукати.</p>\r\n<p>І пішли вони й принесли лукно меду,</p>\r\n<p>У медуші сховане князівській.</p>\r\n<p>І звелів зробить щонайсолодшу ситу</p>\r\n<p>І налити в кадіб.</p>\r\n<p>І в другім колодязі так само.</p>\r\n<p>І послав за печенігами він ранком.</p>\r\n<p>І прийшли до печенігів і сказали горожани:</p>\r\n<p>&laquo;Ви візьміть заложників до себе наших,</p>\r\n<p>А від вас хай десять мужів підуть в город</p>\r\n<p>І подивляться, що діється в нашому городі!&raquo;</p>\r\n<p>І раділи дуже печеніги, думали, що хочуть здатись,</p>\r\n<p>І вибрали десять луччих мужів у граді</p>\r\n<p>І послали їх у город роздивитись,</p>\r\n<p>Що там робиться у городі у їхнім?</p>\r\n<p>І прийшли печеніги у город, і сказали їм люди:</p>\r\n<p>&laquo;Пощо губите себе? Хіба вам перестояти нас?</p>\r\n<p>І хоч би ви десять літ стояли, що нам можете зробити?</p>\r\n<p>Їжі нам земля дає доволі,</p>\r\n<p>А коли не вірите, на власні очі гляньте!&raquo;</p>\r\n<p>І привели їх до колодязя, де ціж був,</p>\r\n<p>І черпали відром, і лили в ладки,</p>\r\n<p>І зварили перед ними.</p>\r\n<p>І, попоївши, привели до другого колодязя,</p>\r\n<p>І, сити зачерпнувши, стали їсти</p>\r\n<p>Спершу сами, а потім й печеніги.</p>\r\n<p>І. здивувалися вони, і так сказали:</p>\r\n<p>&laquo;Не йнятимуть віри князі наші, якщо самі не попробують&raquo;.</p>\r\n<p>І налили їм люди корягу ціжу і сити з колодязя</p>\r\n<p>І дали печенігам.</p>\r\n<p>І коли вони вернулись, то розповіли як було,</p>\r\n<p>І зварили кисіль, і їли його князі печенізькі,</p>\r\n<p>І дивувалися.</p>\r\n<p>І забрали своїх заложників, а білгородських відпустили,</p>\r\n<p>І знялись і геть пішли в свій город.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&nbsp;</p>\r\n<p>Въ л<small>Ђ</small>то 6506.</p>\r\n<p>Въ л<small>Ђ</small>то 6507.</p>\r\n<p>Въ л<small>Ђ</small>то 6508. Преставися Малъфридъ.</p>\r\n<p>Въ се же л<small>Ђ</small>то преставився и Рогън<small>Ђ</small>дь, мати Ярославля.</p>\r\n<p>Въ л<small>Ђ</small>то 6509. Преставився Изяславъ, отець Брячьславль, сынъ Володим<small>Ђ</small>ръ.</p>\r\n<p>Въ л<small>Ђ</small>то 6510.</p>\r\n<p>Въ л<small>Ђ</small>то 6511. Преставися Всеславъ, сынъ Изяславль, внукъ Володим<small>Ђ</small>ръ.</p>\r\n<p>Въ л<small>Ђ</small>то 6512.</p>\r\n<p>Въ л<small>Ђ</small>то 6513.</p>\r\n<p>Въ л<small>Ђ</small>то 6514.</p>\r\n<p>Въ л<small>Ђ</small>то 6515. Принесени святии вь святую Богородицю.</p>\r\n<p>В л<small>Ђ</small>то 6516.</p>\r\n<p>Въ л<small>Ђ</small>то 6517.</p>\r\n<p>Въ л<small>Ђ</small>то 6518.</p>\r\n<p>Въ л<small>Ђ</small>то 6519. Преставися царици Володимеряа Анна.</p>\r\n<p>Въ л<small>Ђ</small>то 6520.</p>\r\n<p>Въ л<small>Ђ</small>то 6521.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6506 [998].</p>\r\n<p>В літо 6507 [999].</p>\r\n<p>В літо 6508 [1000] померла Малфрід. У це ж літо померла Рогнідь, мати Ярославова.</p>\r\n<p>В літо 6509 [1001] помер Ізяслав, батько Брячислава, син Володимира.</p>\r\n<p>В літо 6510 [1002].</p>\r\n<p>В літо 6511 [1003] помер Всеслав, син Ізяслава, внук Володимирів.</p>\r\n<p>В літо 6512 [1004].</p>\r\n<p>В літо 6513 [1005].</p>\r\n<p>В літо 6514 [1006].</p>\r\n<p>В літо 6515 [1007] принесено святих у церкву святої Богородиці.</p>\r\n<p>В літо 6516 [1008].</p>\r\n<p>В літо 6517 [1009].</p>\r\n<p>В літо 6518 [1010].</p>\r\n<p>В літо 6519 [1011] померла Володимирова цариця Анна.</p>\r\n<p>В літо 6520 [1012].</p>\r\n<p>В літо 6521 [1013].</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&nbsp;</p>',NULL,1849,5,1,NULL,25);
INSERT INTO `publications_publication` VALUES
(4711,'P','2008-12-20',0,NULL,'Note','Повість врем\'яних літ Роки 6478 [970] — 6494 [986].','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В літо 6478 [970]. Святослав посадив Ярополка в Києві, а Олега в Деревах. У цей же час прийшли люди новгородськії просити собі князя: &laquo;Якщо не підете до нас, то самі знайдемо собі князя&raquo;. І рече до них Святослав: &laquo;Аби хто йшов до вас?&raquo;</span></span></p>','<p><a href=\"http://izbornyk.org.ua/pvlyar/yar03.htm\">Роки 6478 [970] - 6494 [986].</a></p>\r\n<p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6478. Святославъ посади Ярополка в Кыев<small>Ђ</small>. а Олга въ Дерев<small>Ђ</small>хъ. В се же время придоша людие Новъгородьстии, просяще князя себ<small>Ђ</small>: &laquo;аще не пойдете к намъ, то нал<small>Ђ</small>земъ князя себ<small>Ђ</small>&raquo;. И рече к нимъ Святославъ: &laquo;абы кто к вамъ шелъ&raquo;. И отъпр<small>Ђ</small>ся Ярополкъ и Олгъ; и рече Добрыня: &laquo;просите Володимиря&raquo;. Володимиръ бо б<small>Ђ</small> отъ Малуши, милостьниц<small>Ђ</small> Ольжины; сестра же б<small>Ђ</small> Добрыня, отецъ же има Малъко Любчанинъ, и б<small>Ђ</small> Добрыня уй Володимиру. И р<small>Ђ</small>ша Новгородци Святославу: &laquo;въдай ны Володимира&raquo;. И пояша Новгородьци Володиміра себ<small>Ђ</small>, и иде Володимиръ съ Добрынею уемъ своимъ к Новугороду, а Святославъ къ Переяславцю.</p>\r\n<p>В л<small>Ђ</small>то 6479. Прииде Святославъ Переяславцю, изатворишася Болгаре в город<small>Ђ</small>. И изъл<small>Ђ</small>зоша Болгаре на с<small>Ђ</small>чу противу Святославу, и бысть с<small>Ђ</small>ча велика, и одол<small>Ђ</small>ваху Болгаре. И рече Святославъ воемъ своимъ: &laquo;уже намъ зд<small>Ђ</small> пасти; потягнемъ мужьскы, братье и дружино!&raquo; И к вечеру одол<small>Ђ</small> Святославъ, и взя городъ копьемъ, рькя: &laquo;се городъ мой&raquo;. И посла къ Гр<small>Ђ</small>комъ, глаголя: &laquo;хощю на вы ити, и взяти городъ вашь, яко и сий&raquo;. И ркоша Гр<small>Ђ</small>ци: &laquo;мы недужи противу вамъ стати; но возми на насъ дань, и на дружину свою, и пов<small>Ђ</small>жьте ны колько васъ, да вдамы по числу на головы&raquo;. Се же ркоша Гр<small>Ђ</small>ци, л<small>Ђ</small>стяче подъ Русью: суть бо Гр<small>Ђ</small>ци мудри и до сего дни. И рече имъ Святославъ: &laquo;есть насъ 20 тысящь&raquo;, и прирче 10 тысящь; б<small>Ђ</small> бо Руси 10 тысящь толко. И пристроиша Гр<small>Ђ</small>ци 100 тысящь на Святослава, и не даша дани. И поиде Святославъ на Гр<small>Ђ</small>кы, и изидоша противу Руси. Вид<small>Ђ</small>въ же Русь и убояшася з<small>Ђ</small>ло множьства вои. И рече Святославъ: &laquo;уже намъ н<small>Ђ</small>камо ся д<small>Ђ</small>ти, и волею и неволею стати противу: да не посрамимъ земли Руские, но ляжемы костью ту, и мертвы[й] бо сорома не имаеть; аще ли поб<small>Ђ</small>гнемъ, то срамъ намъ; и не имамъ уб<small>Ђ</small>гнути, но станемъ кр<small>Ђ</small>пко, азъ же предъ вами пойду. Аще моя глава ляжеть, тоже промыслите о себ<small>Ђ</small>&raquo;. И ркоша вои: &laquo;идеже глава твоя ляжеть, ту и главы наша сложимъ&raquo;. И исполчишася Русь, и Гр<small>Ђ</small>ци противу, и сразистася полка, и оступиша Гр<small>Ђ</small>ци Русь, и бысть с<small>Ђ</small>ча велика, и одол<small>Ђ</small> Святославъ, и Гр<small>Ђ</small>ци поб<small>Ђ</small>гоша; и поиде Святославъ воюя къ городу, и другия городы разбивая, иже стоять пусты и до днешьнего дни. И съзва царь з полату бояры своя, и рече имъ: &laquo;что створимъ, не можемъ стати противу ему!&raquo;. И ркоша ему бояре: &laquo;посли к нему дары, искусимъ и, любезнивъ ли есть злату или паволокамъ&raquo;? Послаша к нему злато, и паволокы, и мужа мудры, и рькоша ему: &laquo;глядай взора его, лица его, и смысла его&raquo;. Онъ же вземъ дары, приде къ Святославу, и яко придоша Гр<small>Ђ</small>ци с поклономъ, рече: &laquo;въведете я с<small>Ђ</small>мо&raquo;. И придоша и поклонишася ему, и положиша предъ нимъ злато и паволокы; и рече Святославъ, прочь зря: &laquo;похороните&raquo;. Отроци же Святославли вземше похорониша. Посли же царевы възвратишася къ цареви, и съзва царь бояры, и ркоша же послании: &laquo;яко придохомъ к нему и въдахомъ дары, и не позр<small>Ђ</small> на ня, и повел<small>Ђ</small> схоронити&raquo;. И рече единъ: &laquo;искуси и единою и еще, посли ему оружье&raquo;. Они же послушаша его, и послаша ему мечь и ино оружие. [И принесоша ему мечь].</p>\r\n<p>Онъ же прииимъ, нача любити и хвалити и ц<small>Ђ</small>ловати царя. И придоша опять къ царю, и пов<small>Ђ</small>даша вся бывшая. И ркоша бояре: &laquo;лютъ с<small>Ђ</small>й мужь хощеть быти, яко им<small>Ђ</small>ния небрежеть, а оружье емлеть; имися по дань&raquo;. И посла царь, глаголя сіце: &laquo;не ходи къ городу, но възми дань, и еже хощеши&raquo;: за маломъ бо 6<small>Ђ</small> не щелъ Царяграда. И вдаша ему дань; имашетъ же и за убьеныя, глаголя: &laquo;яко родъ его възметь&raquo; Възя же и дары многы, и възвратися в Переяслав<small>Ђ</small>ць с похвалою великою. Вид<small>Ђ</small>въ же мало дружины своея, рече в себе: &laquo;егда како прел<small>Ђ</small>стивше изъбьють дружи ну мою и мене, б<small>Ђ</small>ша бо мьнози погыбли на полку&raquo;; и рече: &laquo;пойду в Русь и приведу боле дружины&raquo;. И посла послы къ цареви в Дерест<small>Ђ</small>ръ, бь бо ту царь, река сице: &laquo;хощю им<small>Ђ</small>ті миръ с тобою твердъ и любовь&raquo;. Се же слышавъ царь, радъ бысть, и посла дары къ нему болша п<small>Ђ</small>рвыхъ. Святославъ же прия дары, и поча думати съ дружиною своею, рекя сице: &laquo;аще не створимъ мира съ царемъ, а ув<small>Ђ</small>сть царь, яко мало насъ есть, и пришедше оступять ны в город<small>Ђ</small>; а Руская земля далече есть, а Печен<small>Ђ</small>зи с нами ратии, а кто ны поможеть? Но створимъ миръ съ царемъ, се бо ны ся по дань ялъ, и то буди доволно намъ. Аще ли начнеть не управляти дани, то изнова изъ Руси, съвокупивше воя множайша первыхъ, и придемъ к Царюграду&raquo;.</p>\r\n<p>И люба бысть р<small>Ђ</small>чь сі дружин<small>Ђ</small>, и послаша л<small>Ђ</small>пьшии мужи къ цареви; и придоша в Дерьстеръ, и пов<small>Ђ</small>даша цареви. Царь же наутр<small>Ђ</small>я призва я, и рече царь: &laquo;да глаголють посли Русции&raquo;. Они же ркоша: &laquo;тако глаголетъ князь нашь: хочю им<small>Ђ</small>ти любовь съ царемъ Гр<small>Ђ</small>цькымъ св<small>Ђ</small>ршену прочая вся л<small>Ђ</small>та&raquo;. Царь же радъ бывъ, повел<small>Ђ</small> письцю писати на харотью вься р<small>Ђ</small>чи Святославли; и начаша глаголати посли вся р<small>Ђ</small>чи, и нача писець писати, глаголя сице:</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6478 [970]. Святослав посадив Ярополка в Києві, а Олега в Деревах. У цей же час прийшли люди новгородськії просити собі князя: &laquo;Якщо не підете до нас, то самі знайдемо собі князя&raquo;. І рече до них Святослав: &laquo;Аби хто йшов до вас?&raquo; І відмовилися Ярополк і Олег. І говорить Добриня: &laquo;Просіть Володимира&raquo;. Володимир-бо був від Малуші - Ольжиної любимиці, сестри Добрині, а батьком їм був Малко Любчанин, і був Добриня дядьком Володимира.</p>\r\n<p>&nbsp;</p>\r\n<p>І сказали новгородці Святославу: &laquo;Дай нам Володимира&raquo;.</p>\r\n<p>І взяли новгородці собі Володимира.</p>\r\n<p>І пішов Володимир з Добринею, дядьком своїм, у Новгород,</p>\r\n<p>А Святослав до Переяславця.</p>\r\n<p>&nbsp;</p>\r\n<p>В літо 6479 [971]. Прийшов Святослав у Переяславець,</p>\r\n<p>І закрилися болгари в городі.</p>\r\n<p>І вийшли болгари на січу проти Святослава,</p>\r\n<p>І була січа велика,</p>\r\n<p>І стали перемагать болгари.</p>\r\n<p>І рече Святослав воям своїм: &laquo;Невже нам тут полягти?</p>\r\n<p>Вдаримо ж мужньо, браття і дружино!&raquo;</p>\r\n<p>І до вечора одолів Святослав,</p>\r\n<p>І взяв город приступом, сказавши: &laquo;Це город мій!&raquo;</p>\r\n<p>І послав до греків зі словами: &laquo;Хочу на ви іти</p>\r\n<p>І взяти город ваш, як і цей&raquo;.</p>\r\n<p>І сказали греки: &laquo;Ми не дужі супроти вас стати,</p>\r\n<p>Так візьми із нас дань і на дружину свою.</p>\r\n<p>І скажіть нам, скільки вас, щоб сплатили ми на кожного&raquo;.</p>\r\n<p>Це ж говорили греки, щоб обдурити русь,</p>\r\n<p>Є ж бо греки мудрі й до сьогодні.</p>\r\n<p>І рече їм Святослав: &laquo;Є нас двадцять тисяч&raquo;,</p>\r\n<p>І прибавив десять тисяч, русів-бо було лиш десять тисяч.</p>\r\n<p>І виставили греки сто тисяч проти Святослава,</p>\r\n<p>І не дали дані.</p>\r\n<p>І пішов Святослав на греків,</p>\r\n<p>І вийшли вони проти русі.</p>\r\n<p>І коли русь побачила греків</p>\r\n<p>І дуже злякалась безлічі воїв,</p>\r\n<p>І рече Святослав: &laquo;Уже нам діватися нікуди,</p>\r\n<p>І волею і неволею станемо супроти:</p>\r\n<p>Так не посоромимо землі Руської, а ляжемо тут кістьми,</p>\r\n<p>Бо мертвий сорому не має.</p>\r\n<p>Коли ж побіжимо, то сором нам.</p>\r\n<p>Тож не маємо втікати, а станем кріпко,</p>\r\n<p>Я ж попереду вас піду.</p>\r\n<p>А коли моя голова поляже, то про свої думайте самі&raquo;.</p>\r\n<p>І сказали вої: &laquo;Де твоя голова поляже, там і наші положим&raquo;.</p>\r\n<p>І ополчилася русь, а греки супроти,</p>\r\n<p>І вдарилися два війська, і обступили греки русь,</p>\r\n<p>І була січа велика, і одолів Святослав, а греки побігли.</p>\r\n<p>І пішов Святослав на столицю, воюючи</p>\r\n<p>І розбиваючи другі городи, що стоять пусті і донині.</p>\r\n<p>І зібрав цар в палату бояр своїх і рече їм:</p>\r\n<p>&laquo;Що будем робити? Не можемо встояти проти нього!&raquo;</p>\r\n<p>І порадили йому бояри: &laquo;Пошли йому дари,</p>\r\n<p>І вивідаємо, чи любить він золото і паволоки</p>\r\n<p>І послали йому золото і паволоки з мужем мудрим,</p>\r\n<p>І сказали йому: &laquo;Дивись на погляд його, і лице, і мислі його&raquo;.</p>\r\n<p>І взяв він дари і прийшов до Святослава,</p>\r\n<p>І як прийшли греки з поклоном, сказав: &laquo;Введіть їх сюди&raquo;,</p>\r\n<p>І прийшли, і поклонилися йому,</p>\r\n<p>І поклали перед ним золото і паволоки,</p>\r\n<p>І рече Святослав, убік дивлячись: &laquo;Сховайте&raquo;.</p>\r\n<p>І отроки Святослава взяли і сховали,</p>\r\n<p>Посли ж грецькі повернулися до царя.</p>\r\n<p>І зібрав цар бояр,</p>\r\n<p>І розповіли послані: &laquo;Як прийшли до нього</p>\r\n<p>І піднесли дари, він і не глянув на них,</p>\r\n<p>А наказав сховати&raquo;.</p>\r\n<p>І сказав один: &laquo;Випробуй його ще один раз: пошли йому зброю&raquo;.</p>\r\n<p>Вони ж послухали його і послали йому меч та іншу зброю.</p>\r\n<p>І принесли Святославу меч.</p>\r\n<p>Він же прийняв, почав любуватися, і хвалити,</p>\r\n<p>І вітати царя.</p>\r\n<p>І знову прийшли до царя,</p>\r\n<p>І розповіли йому все, як було.</p>\r\n<p>І сказали бояри: &laquo;Лютий буде цей муж, якщо багатство зневажа,</p>\r\n<p>А зброю бере,- плати йому дань&raquo;.</p>\r\n<p>І послав цар, щоб сказати таке: &laquo;Не йди на город,</p>\r\n<p>А візьми дань і що хочеш&raquo;.</p>\r\n<p>І мало не дійшов був до Цареграда.</p>\r\n<p>І дали йому дань, узяв же і на убитих, кажучи:</p>\r\n<p>&laquo;Візьме [за убитого] рід його&raquo;.</p>\r\n<p>Взяв же і дари численні</p>\r\n<p>І повернувся в Переяславець із славою великою.</p>\r\n<p>Та побачив же, що мала у нього дружина, і сказав собі:</p>\r\n<p>&laquo;Коли-небудь якоюсь хитрістю переб\'ють дружину мою і мене,</p>\r\n<p>Бо багато загинуло в поході&raquo;. І сказав:</p>\r\n<p>&laquo;Піду на Русь і приведу більшу дружину&raquo;.</p>\r\n<p>І послав послів до царя в Дерестер, бо там тоді цар був,</p>\r\n<p>І сказав так: &laquo;Хочу мати з тобою мир міцний і любов&raquo;.</p>\r\n<p>Це почувши, цар радий був</p>\r\n<p>І послав до нього дари більші перших.</p>\r\n<p>Святослав же прийняв дари,</p>\r\n<p>І почав думати з дружиною своєю, і сказав так:</p>\r\n<p>&laquo;Якщо не заключимо миру з царем і узнає цар, як мало нас,</p>\r\n<p>То прийде і обступить нас в городі,</p>\r\n<p>А Руська земля далеко, і печеніги з нами воюють,</p>\r\n<p>І хто нам поможе?</p>\r\n<p>Але заключимо мир з царем, він обіцяє дань платити,</p>\r\n<p>І того нам буде доволі.</p>\r\n<p>А коли почне не дотримуватись дані,</p>\r\n<p>То знову із Русі, зібравши воїв більш ніж перше,</p>\r\n<p>І прийдемо до Цареграда&raquo;.</p>\r\n<p>І сподобались ці речі дружині,</p>\r\n<p>І найліпших мужів до царя послали,</p>\r\n<p>І у Дерстер прийшли, і цареві повідали.</p>\r\n<p>Цар же ранком послів закликав,</p>\r\n<p>І сказав цар: &laquo;Нехай промовляють посли руськії&raquo;.</p>\r\n<p>Вони ж сказали: &laquo;Так говорить князь наш:</p>\r\n<p>Хочу мати любов з царем грецьким,</p>\r\n<p>На всі прийдешні літа звершену&raquo;.</p>\r\n<p>Цар же радий був і писцеві повелів</p>\r\n<p>На хартію всі слова Святослава записати.</p>\r\n<p>І почали посли говорити всі слова,</p>\r\n<p>І писець почав писати, проказуючи таке:</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&laquo;Равно другаго св<small>Ђ</small>щания, бывшего при Святослав<small>Ђ</small>, велиц<small>Ђ</small>мъ князи Руст<small>Ђ</small>мъ, и при Св<small>Ђ</small>нгельд<small>Ђ</small>, писано при Феофил<small>Ђ</small> сенкел<small>Ђ</small> и ко Иоану, нар<small>Ђ</small>цаемому Цимьскому, царю Гр<small>Ђ</small>цькому, в Дерьстр<small>Ђ</small>, м<small>Ђ</small>сяця иулия, индикта 14. Азъ Святдславъ, князь Рускый, якоже кляхся, и утв<small>Ђ</small>ржаю на св<small>Ђ</small>щании семъ роту свою. И хочю им<small>Ђ</small>ти миръ и св<small>Ђ</small>ршену любовь съ всякымъ и великымъ царемь Гр<small>Ђ</small>цькимъ, и съ Васильемъ и съ Костянтиномъ, и съ богодухновенными цари, и съ всими людми вашими, иже суть подо мною Русь, бояре и прочии, до конца в<small>Ђ</small>ка. Яко николиже помышляю на страну вашю, ни сбираю людий, ни языка иного приведу на страну вашю и елико есть подъ властью[ю] Гр<small>Ђ</small>цькою, ни на власть Коръсуньскую, и елико есть городовъ их<small>Ђ</small>, ни на страну Болъгарьску. Да аще инъ кто помыслить на страну вашю, да азъ буду противенъ ему и бьюся с нимъ. Якоже и кляхся азъ к царемъ Гр<small>Ђ</small>ць.скымъ, и со мною бояре и Русь вся, да хранимъ правая св<small>Ђ</small>щания. Аще ли отъ т<small>Ђ</small>хъ сам<small>Ђ</small>хъ и преждереченыхъ не хранимъ, азъ же и со мною и подо мною, да им<small>Ђ</small>емъ клятву отъ Бога, в неже в<small>Ђ</small>руемъ, в Перуна и въ Волоса, бога скотья, да будемъ золот<small>Ђ</small> якоже золото се, и своимъ оружьемь да исс<small>Ђ</small>чени будемъ, да умремъ. Се же им<small>Ђ</small>ете во истину, якоже створихъ нын<small>Ђ</small> к вамъ, и написахъ на харотьи сей и своїми печатьми запечатахомъ&raquo;.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&laquo;Згідно другого договору, укладеного при Святославі, великому князеві Руському, і при Свінгельді, писаного при Феофілі сінкелі за Іоанна, званого Цимським, царя грецького, в Дерстрі, місяця липня, індикта 14, [в літо 6479].</p>\r\n<p>Я, Святослав, князь руський, як і клявся, так і скріпляю договором цим клятву свою. І хочу мати мир і справжню любов з кожним із великих, царів грецьких, і з Василем, і з Костянтином, і з богоданними царями, і з усіма людьми вашими, як і вся мені підвладна Русь, бояри і інші,- до кінця віку. Я ніколи не буду посягати на землю вашу, ані збирать [проти неї] людей, ані іншого народу не приведу на землю вашу чи на інші землі, що під владою грецькою, ані на володіння Корсунські з усіма їхніми городами, ані на землю Болгарську. А якщо хтось інший посягне на землю вашу, то я буду його ворогом і битимуся з ним. Як і клявся я царям грецьким, а зі мною бояри і Русь вся, будемо Дотримуватися справедливого договору. А коли порушимо сказане тут і раніше, я і всі, хто зі мною і піді мною, хай будемо прокляті Богом, в якого віруємо, Перуном і Волосом, богом скота, і хай будемо жовті, як золото, і нехай своїм оружжям посічені будемо, і нехай умремо. Майте за правду це, що обіцяю вам нині і записав на хартії цій і своїми печатками скріпив&raquo;.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Створивъ же миръ Святославъ съ Гр<small>Ђ</small>кы, и поиде в лодьяхъ къ порогомъ, и рече ему воевода отень и Св<small>Ђ</small>нгелдъ: &laquo;поиди, княже, около на конехъ, стоять бо Печен<small>Ђ</small>зи в пороз<small>Ђ</small>хъ&raquo;. И не послушаше его, и поиде въ лодьяхъ. Послаша же Переяславци къ Печен<small>Ђ</small>гомъ, глаголя: &laquo;идеть Святославъ в Русь, въземъ им<small>Ђ</small>нье много у Гр<small>Ђ</small>къ и полонъ бещисленъ, а с маломъ дружины&raquo;. Слышавше же Печен<small>Ђ</small>зи се, заступиша порогы, и приде Святославъ къ порогомъ, и не б<small>Ђ</small> лз<small>Ђ</small> проити пороговъ; и ста зимовать въ Б<small>Ђ</small>лобережьи; не б<small>Ђ</small> в нихъ брашна, и бысть гладь велікъ, яко по полугривн<small>Ђ</small> голова коняча; и зимова Святославъ. Весн<small>Ђ</small> же присп<small>Ђ</small>въши, поиде Святославъ в порогы.</p>\r\n<p>В л<small>Ђ</small>то 6480. И приде Святославъ в порогы, и нападе на ня Куря, князь Печен<small>Ђ</small>жьскый, и убиша Святослава. И взяша голову его и во лб<small>Ђ</small> его зд<small>Ђ</small>лаша чащю, оковавше лобъ его золотомъ, и пьяху в немъ. Св<small>Ђ</small>нгельдъ же приде къ Киеву къ Ярополку. И бысть всехъ л<small>Ђ</small>ть княжения Святославля л<small>Ђ</small>тъ 28.</p>\r\n<p>В л<small>Ђ</small>то 6481. И нача княжити Ярополкъ.</p>\r\n<p>В л<small>Ђ</small>то 6482.</p>\r\n<p>В л<small>Ђ</small>то 6483. Ловы д<small>Ђ</small>ющю Св<small>Ђ</small>ньгельдичю, именемъ Лотъ, и [с] шедъ бо изъ Киева гна по зв<small>Ђ</small>ри в л<small>Ђ</small>с<small>Ђ</small>; узр<small>Ђ</small> и Олегъ и рече: &laquo;кто се есть&raquo;? И ркоша ему: &laquo;Св<small>Ђ</small>нгелдичь&raquo;, и за<small>Ђ</small>хавь уби и, б<small>Ђ</small> бо ловы д<small>Ђ</small>я Олегъ. И о томъ бысть межи има ненависть Ярополку на Ольга, и молвяше всегда Ярополку Св<small>Ђ</small>нгелдъ: &laquo;поиди на брата своего и приимеши власть единъ его&raquo;, хотя отмьстити сыну своему.</p>\r\n<p>В л<small>Ђ</small>то 6484.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Святослав уклав мир з греками</p>\r\n<p>І пішов у лодіях до порогів,</p>\r\n<p>І сказав йому воєвода батьків, Свингельд:</p>\r\n<p>&laquo;Обійди, князю, обіч на конях, бо стоять печеніги в порогах&raquo;.</p>\r\n<p>І не послухав його, і пішов на лодіях.</p>\r\n<p>А переяславці до печенігів послали сказати:</p>\r\n<p>&laquo;Вертає Святослав на Русь з частиною дружини,</p>\r\n<p>Взявши у греків багатства великі і полон без ліку&raquo;.</p>\r\n<p>Як почули це печеніги - зайняли пороги.</p>\r\n<p>І прийшов Святослав до порогів, і не зміг пройти їх.</p>\r\n<p>І залишився зимувати у Білобережжі.</p>\r\n<p>І не було в них що їсти,</p>\r\n<p>І був голод великий, що по півгривни за кінську голову [платили].</p>\r\n<p>І так зимував Святослав.</p>\r\n<p>А як весна прийшла, пішов Святослав до порогів.</p>\r\n<p>&nbsp;</p>\r\n<p>В літо 6480 [972]. І прийшов Святослав до порогів,</p>\r\n<p>І напав на нього Куря, князь печенізький,</p>\r\n<p>І убив Святослава.</p>\r\n<p>І взяли голову його,</p>\r\n<p>І з черепа його зробили чашу,</p>\r\n<p>І, золотом окувавши, пили з неї.</p>\r\n<p>Свингельд же прийшов у Київ до Ярополка.</p>\r\n<p>І було всіх літ княження Святослава двадцять вісім.</p>\r\n<p>&nbsp;</p>\r\n<p>В літо 6481 [973]. Почав княжити Ярополк.</p>\r\n<p>В літо 6482 [974].</p>\r\n<p>В літо 6483 [975]. Полюючи, Свингельдич, на ймення Лот, вийшов із Києва на лови і гнав звіра у лісі. Побачив його [князь] Олег і питає: &laquo;Хто це?&raquo; І відповіли йому: &laquo;Свингельдич&raquo;. І, заїхавши, убив його [Олег], бо сам був на ловах. І з-за цього сталася ворожнеча між Ярополком і Олегом, а Свингельд, бажаючи помститися за сина, завжди говорив Ярополку:</p>\r\n<p>&laquo;Піди на брата свого і один будеш володіти і його волостю&raquo;.</p>\r\n<p>В літо 6484 [976].</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6485. Поиде Ярополкъ на Олга, брата своего, на Деревьскую землю, и изыде противу ему Олегъ, и ополчистася; и сразившимася полкома, и поб<small>Ђ</small>ди Ярополкъ Олга. И поб<small>Ђ</small>гъшю же Олгови с вои своими в городъ, р<small>Ђ</small>комый Вручий, и бяше мость чрезъ гроблю к воротомъ городнымъ и т<small>Ђ</small>снячіся другъ друга спехнуша Олга с моста въ дебрь, и падаху людье мнози с моста, и удавиша и кони и челов<small>Ђ</small>ци. И вшедъ Ярополкъ в городъ Олговъ, прия волость его и посла искати брата своего; и искавше его не обр<small>Ђ</small>тоша. И рече одинъ Древлянинъ: &laquo;азъ вид<small>Ђ</small>хъ вчера, яко съпехънуша и с моста&raquo;. И посла Ярополкъ искати [его] и волочиша трупье изъ гробли отъ утра и до полудня, и нал<small>Ђ</small>зоша исподи Олга подъ трупьемъ, и внесъше, положиша на на ковр<small>Ђ</small>. И приди Ярополкъ надъ онь, и плакася, и рече Свеньгелду: &laquo;вижь, иже ты сего хотяше&raquo;. И погребоша Ольга на м<small>Ђ</small>ст<small>Ђ</small> у города Вручего, и есть могила его [у] Въручего и до сего дни. И прія волость его Ярополкъ. И у Ярополка жена Гр<small>Ђ</small>кини б<small>Ђ</small>, и бяше была черницею, юже б<small>Ђ</small> привелъ отець его Святославъ, и въда ю за Ярополка, красы д<small>Ђ</small>ля лица ея. Слышавъ же се Володимиръ в Нов<small>Ђ</small>город<small>Ђ</small>, яко Ярополкъ уби Олга, убоявся б<small>Ђ</small>жа за моря: а Ярополкъ посади посадникъ свой въ Нов<small>Ђ</small>город<small>Ђ</small>, и б<small>Ђ</small> волод<small>Ђ</small>я единъ в Руси.</p>\r\n<p>В л<small>Ђ</small>то 6486.</p>\r\n<p>В л<small>Ђ</small>то 6487.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6485 [977].</p>\r\n<p>Пішов Ярополк на брата свого Олега у Деревську землю,</p>\r\n<p>І виступив против нього Олег і ополчився.</p>\r\n<p>І була битва між військами,</p>\r\n<p>І переміг Ярополк Олега.</p>\r\n<p>І побіг Олег із воями своїми в город Вручий,</p>\r\n<p>І був Міст через греблю до воріт городських,</p>\r\n<p>І в тісняві, спихаючи один другого, зіпхнули Олега із мосту в рів.</p>\r\n<p>І падало багато людей з мосту,</p>\r\n<p>І душилися і коні, і люди.</p>\r\n<p>І ввійшов Ярополк у город Олегів,</p>\r\n<p>І захопив волость його,</p>\r\n<p>І послав шукати брата свого.</p>\r\n<p>І шукали його і не знайшли.</p>\r\n<p>І сказав один древлянин:</p>\r\n<p>&laquo;Я бачив учора, як із мосту його зіпхнули&raquo;.</p>\r\n<p>І послав Ярополк шукати його.</p>\r\n<p>І витягали трупи із рову від ранку і до обіду,</p>\r\n<p>І знайшли Олега насподі під трупами,</p>\r\n<p>І, внісши, поклали його на килимі.</p>\r\n<p>І прийшов Ярополк і плакав над ним,</p>\r\n<p>І сказав Свингельду: &laquo;Дивись, ти цього хотів&raquo;.</p>\r\n<p>І похоронили Олега у полі біля города Вручого,</p>\r\n<p>І є могила його біля Вручого і до сьогодні.</p>\r\n<p>І успадкував волость його Ярополк.</p>\r\n<p>А у Ярополка жона грекиня була,</p>\r\n<p>А перед тим була вона черницею,</p>\r\n<p>І привів її був колись батько його Святослав,</p>\r\n<p>І віддав її за Ярополка, бо була надзвичайно вродлива.</p>\r\n<p>Володимир, почувши в Новгороді, що Ярополк убив Олега, дуже злякався і втік за море, а Ярополк посадив посадника свого в Новгороді і став один володіти Руссю.</p>\r\n<p>В літо 6486 [978].</p>\r\n<p>В літо 6587 [979].</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6488. Приде Володимиръ с Варягы къ Новугороду, и рече посадникомъ Ярополъчимъ: &laquo;идете къ брату моему и речете ему: Володимиръ идеть на тя, пристраивайся противу бит[и]ся&raquo;. И с<small>Ђ</small>де в Нов<small>Ђ</small>город<small>Ђ</small>, и посла к Роговолоду, князю Полотьску, глаголя: &laquo;хощю пояти дщерь твою жен<small>Ђ</small>&raquo;. Онъ же рече дъщери своей: &laquo;хощеши ли за Володимира&raquo;? Она же рече: &laquo;не хощю розути Володимира, но Ярополка хочю&raquo;. Б<small>Ђ</small> бо Рогъволодъ перешелъ изъ заморья, имяше волость свою Полотьск<small>Ђ</small>, а Туръ Туров<small>Ђ</small>, отъ негоже и Туровци прозвашася. И приидоша отроци Володимири, и пов<small>Ђ</small> [да] ша ему всю р<small>Ђ</small>чь Рогн<small>Ђ</small>дину, дщери Рогъволож<small>Ђ</small>, князя Полотьского. Володимиръ же събра вои многы, Варягы и Слов<small>Ђ</small>ны, и Чюдь и Кривичи, и поиде на Рогъволода. В се же время хотяху весті Рогън<small>Ђ</small>дь за Ярополка: и приіде Володимиръ на Полотескъ, и уби Рогъволода и сына его два, а дщерь его Рогън<small>Ђ</small>дь поя жен<small>Ђ</small>, и поиде на Ярополъка. И приде Володимиръ къ Киеву съ вои многыми, и не може Ярополкъ стати противу Володимиру, и затворися Ярополкъ въ Киев<small>Ђ</small> съ людьми своими и с воеводою Блудомъ; и стояще Володимиръ обрывся на Дорогожичі, межи Дорогожичемъ и Капичемъ, и есть ровъ и до сего дне. Володимиръ же посла къ Блуду, воевод<small>Ђ</small> Ярополчю, с л<small>Ђ</small>стью глаголя: &laquo;поприяй ми; аще убью брата своего, им<small>Ђ</small>ти тя начну въ отца м<small>Ђ</small>сто своего. и многу честь возмеши отъ мене; не я бо почалъ братью бити, но онъ; азъ же того убояхъся и придохъ на нь&raquo;. И рече Блудъ къ посланнымъ Володимиро [вы] мъ: &laquo;ти въ приязнь азъ буду&raquo;. О злая л<small>Ђ</small>сть челов<small>Ђ</small>чьская! Якоже Давидъ глаголеть: ядый хл<small>Ђ</small>бь мой възвеличилъ есть на мя лесть. Сь убо лукавоваше на князя л<small>Ђ</small>стыо. А пакы: языки своими лыдаху; суди имъ, Боже, да отпадуть отъ мыслий своихъ по множьству нечестья изърини я, яко прогн<small>Ђ</small>ваша тя, Господи. И пакы то же рече Давидъ: мужи крови льстиви не припловятъ дний своихъ. Се есть св<small>Ђ</small>тъ золъ, еже св<small>Ђ</small>щеваютъ на кровопролитье; то суть неистовии, иже приимъше отъ князя или отъ господина своего честь и дары, ти мыслять о глав<small>Ђ</small> князя своего на погубление, горьше суть таковыи б<small>Ђ</small>совъ: якоже и Блудъ предасть князя своего, пріимъ отъ него чести многы, сь бо бысть повиненъ крови той. Се бо Блудъ затворивъся съ Ярополкомъ, слаше къ Володимиру часто, веля ему приступати къ городу бранью, самъ мысля убить Ярополка; гражаны же н<small>Ђ</small>лз<small>Ђ</small> убити его. Блудъ же не възмогъ, како бы и погубити, замысли л<small>Ђ</small>стью, веля ему не изълазить на брань изъ града; и рече же Блудъ Ярополку: &laquo;Киян<small>Ђ</small> слются къ Володимирю, глаголюще: приступай къ городу бранью, яко предамы ти Ярополка; поб<small>Ђ</small>гни изъ града&raquo;. И послуша его Ярополкъ, и б<small>Ђ</small>жа изъ града, и пришедъ затворися въ град<small>Ђ</small> Род<small>Ђ</small>н<small>Ђ</small> на устьи Ръси, а Володимиръ вниде в Киевъ и ос<small>Ђ</small>дяху Ярополка в Родн<small>Ђ</small>, и б<small>Ђ</small> гладъ великъ в немъ; и есть притча и до сего дне: б<small>Ђ</small>да аки в Родн<small>Ђ</small>. И рече Блудъ Ярополку: видиши ли, колко вои у брата твоего? намъ ихъ не перебороти; и твори мръ съ братомъ своимъ&raquo;; льстя подъ нимъ се рече. И рече Ярополкъ: &laquo;тако буди&raquo;. И посла Блудъ къ Володимеру, глаголя: &laquo;яко събыся мысль твоя, яко приведу Ярополка к теб<small>Ђ</small>, и пристрой убити и&raquo;. Володимиръ же то слышавъ, въшедъ въ дворъ теремьный отень, о немъже преже сказахомъ, с<small>Ђ</small>де ту с вои и съ дружиною своею. И рече Блудъ Ярополку: &laquo;поиди къ брату своему и рьци ему: что ми ни вдаси, то язъ прииму&raquo;.</p>\r\n<p>Поиде же Ярополкъ, и рече ему Варяжько: &laquo;не ходи, княже, убьють тя; поб<small>Ђ</small>гни в Печен<small>Ђ</small>гы и приведеши воя&raquo;; и не послуша его. И приде Ярополкъ къ Володимиру, и яко пол<small>Ђ</small>зе въ дв<small>Ђ</small>ри, подъяста и два Варяга мечема подъ пазус<small>Ђ</small>; Блудъ же затвори дв<small>Ђ</small>ри и не дасть, по немъ виити своимъ. И тако убьенъ бысть Ярополкъ. Варяжько же вид<small>Ђ</small>въ, яко убьенъ бысть Ярополкъ, б<small>Ђ</small>жа съ двора в Печен<small>Ђ</small>гы и мьного воева с Печен<small>Ђ</small>гы на Володимира, и одва приваби и заходивъ к нему рот<small>Ђ</small>. Володимиръ же зал<small>Ђ</small>же жену братьню Гр<small>Ђ</small>киню, и б<small>Ђ</small> не праздна, отъ нея же роди Святополка. Отъ гр<small>Ђ</small>ховнаго бо корене злый плодъ бываеть, понеже была б<small>Ђ</small> мати его черницею, а второе, Володимиръ залеже ю не по браку; прелюбод<small>Ђ</small>йчищь бысть убо, т<small>Ђ</small>мьже и отець его не любяше, б<small>Ђ</small> бо отъ двою отцю, отъ Ярополка и отъ Володимира. Посемъ р<small>Ђ</small>ша Варязи Володимиру: &laquo;се градъ нашъ, и мы прияхомъ и, да хощемъ имати откупъ на нихъ по 2 гривн<small>Ђ</small> отъ челов<small>Ђ</small>ка&raquo;. И рече имъ Володимиръ: &laquo;пожьдете, да же вы куны сберуть, за м<small>Ђ</small>сяць&raquo;. И жьдаша за м<small>Ђ</small>сяць, и не дасть имъ, и р<small>Ђ</small>ша Варязи: &laquo;съльстилъ еси намъ, да покажи ны путь въ Гр<small>Ђ</small>кы&raquo;; онъ же рече: &laquo;идете&raquo;.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6488 [980]. Повернувся Володимир у Новгород з варягами</p>\r\n<p>І сказав посадникам Ярополка:</p>\r\n<p>&laquo;Ідіть до брата мого,</p>\r\n<p>І скажіть йому: &laquo;Володимир іде на тебе, готуйся з ним битися&raquo;,-</p>\r\n<p>І сів у Новгороді.</p>\r\n<p>І послав до Рогволода, князя Полоцького, кажучи:</p>\r\n<p>&laquo;Хочу дочку твою за жону взяти&raquo;.</p>\r\n<p>Він же спитав у дочки своєї: &laquo;Чи хочеш за Володимира?&raquo;</p>\r\n<p>Вона ж рече: &laquo;Не хочу роззути Володимира, за Ярополка хочу&raquo;.</p>\r\n<p>Колись-бо Рогволод прийшов із-за моря</p>\r\n<p>І волость свою мав у Полоцьку,</p>\r\n<p>А Тур - у Турові, від нього ж і туровці прозвалися,</p>\r\n<p>І прийшли отроки Володимирові,</p>\r\n<p>І переповіли йому всю мову Рогніди, дочки Рогволода, князя Полоцького,</p>\r\n<p>І зібрав Володимир воїв багато: варяги, і словіни,</p>\r\n<p>І чудь, і кривичі -</p>\r\n<p>І пішов на Рогволода.</p>\r\n<p>А в цей час хотіли вести Рогніду за Ярополка.</p>\r\n<p>І напав Володимир на Полоцьк,</p>\r\n<p>І убив Рогволода і синів його двох,</p>\r\n<p>А дочку його Рогніду за жону взяв,</p>\r\n<p>І пішов на Ярополка.</p>\r\n<p>І прийшов Володимир на Київ із військом великим,</p>\r\n<p>І не зміг Ярополк йому опір вчинити,</p>\r\n<p>І сховався Ярополк у Києві</p>\r\n<p>Із людьми своїми і з воєводою Блудом.</p>\r\n<p>І стояв Володимир, окопавшись, на Дорогожичі,</p>\r\n<p>І є рів той і до сьогодні - між Дорогожичем і Капичем.</p>\r\n<p>І послав Володимир до Блуда, воєводи Ярополка,</p>\r\n<p>Із улесливістю кажучи: &laquo;Посприяй мені,</p>\r\n<p>І, коли уб\'ю брата свого, за батька рідного тебе матиму,</p>\r\n<p>І велику честь від мене матимеш,</p>\r\n<p>І не я розпочав братовбивство, а він,</p>\r\n<p>І я, боячись того, і прийшов на нього&raquo;.</p>\r\n<p>І рече Блуд через посланих Володимиром:</p>\r\n<p>&laquo;Буду тобі я другом&raquo;.</p>\r\n<p>О зла підступність людськая! Як Давид говорить:</p>\r\n<p>&laquo;Жерущий хліб мій обманув мене&raquo;.</p>\r\n<p>А Цей обманом задумав підступність проти свого князя.</p>\r\n<p>І ще: &laquo;Язиком своїм обманювали - суди їх, Боже,</p>\r\n<p>І хай відмовляться від замислів своїх,</p>\r\n<p>І за безліччю безчестя їхнього - отвергни їх,</p>\r\n<p>Бої вони прогнівили тебе, Господи&raquo;.</p>\r\n<p>І ще про те сказав Давид:</p>\r\n<p>&laquo;Мужі крові підступної не проживуть і половини днів своїх&raquo;.</p>\r\n<p>Це; світ зла, що віщує кровопролиття,</p>\r\n<p>То безумці, що приймають від князя чи пана свого честь і дари,</p>\r\n<p>А помишляють погубити життя князя свого,</p>\r\n<p>Такії гірше нечистої сили.</p>\r\n<p>Так-от і Блуд зрадив князя свого, хоч мав від нього багато честі,</p>\r\n<p>А став повинний у крові його.</p>\r\n<p>Цей Блуд зачинився з Ярополком,</p>\r\n<p>А сам часто посилав до Володимира,</p>\r\n<p>Закликаючи його город приступом брати,</p>\r\n<p>А сам замишляв у цей час убить Ярополка,</p>\r\n<p>Боячись, що через горожан убити його не зможе.</p>\r\n<p>І таки Блуд не зміг погубити його,</p>\r\n<p>І замислив підступність,</p>\r\n<p>І радив йому не виходить на битву із города.</p>\r\n<p>І сказав же Блуд Ярополку:</p>\r\n<p>&laquo;Кияни посилають до Володимира і говорять:</p>\r\n<p>&laquo;Бери город приступом, і ми видамо тобі Ярополка&raquo;,-</p>\r\n<p>Тікай із города&raquo;.</p>\r\n<p>І послухав його Ярополк, і утік із города,</p>\r\n<p>І, прийшовши, закрився в городі Родині в гирлі Росі,</p>\r\n<p>А Володимир ввійшов у Київ і обложив Ярополка у Родні.</p>\r\n<p>І був голод великий у Родні,</p>\r\n<p>І до сьогодні є прит.ча: &laquo;Біда, як у Родні&raquo;.</p>\r\n<p>І сказав Блуд Ярополкові:</p>\r\n<p>&laquo;Чи бачиш, скільки воїв у брата твого? Нам їх не перебороти,</p>\r\n<p>Заключай мир із братом своїм!&raquo;</p>\r\n<p>І, кажучи це, брехав йому.</p>\r\n<p>І сказав Ярополк: &laquo;Хай буде так!&raquo;</p>\r\n<p>І послав Блуд до Володимира, кажучи:</p>\r\n<p>&laquo;Так от, збудеться твій намір, як приведу до тебе Ярополка,</p>\r\n<p>І приготуйся убити його&raquo;.</p>\r\n<p>Володимир же, те почувши, вийшов у двір батьківського терема,</p>\r\n<p>Про нього раніш ми сказали,</p>\r\n<p>І засів тут із воями і з дружиною своєю.</p>\r\n<p>І говорить Блуд Ярополку:</p>\r\n<p>&laquo;Йди до брата свого і скажи йому:</p>\r\n<p>&laquo;Що не даси мені, те я прийму&raquo;.</p>\r\n<p>І пішов Ярополк, а Варяжко йому говорить:</p>\r\n<p>&laquo;Не йди, княже, уб\'ють тебе, втечи до печенігів і приведеш воїв&raquo;.</p>\r\n<p>І не послухав його.</p>\r\n<p>І прийшов Ярополк до Володимира,</p>\r\n<p>І як тільки ввійшов у двері,-</p>\r\n<p>І два варяги підняли його мечами під пазуху,</p>\r\n<p>Блуд же зачинив двері і не дав за ним ввійти своїм.</p>\r\n<p>І так був убитий Ярополк.</p>\r\n<p>Варяжко ж бачив, як був убитий Ярополк,</p>\r\n<p>І втік із двору до печенігів,</p>\r\n<p>І довго воював з печенігами проти Володимира,</p>\r\n<p>І ледве привабив [Володимир на свій бік Варяжка],</p>\r\n<p>І дав йому клятвену обіцянку.</p>\r\n<p>Володимир же заліг із жінкою брата - гречанкою,</p>\r\n<p>І була вона вагітна,</p>\r\n<p>І народився від неї Святополк.</p>\r\n<p>А від гріховного кореня поганий плід буває,</p>\r\n<p>Оскільки була його мати черницею,</p>\r\n<p>А по-друге, Володимир заліг із нею не в шлюбі, як прелюбодійник,</p>\r\n<p>А тому і батько не любив Святополка,</p>\r\n<p>Бо був неначе від двох батьків - від Ярополка і від Володимира.</p>\r\n<p>Через якийсь час варяги сказали Володимиру:</p>\r\n<p>&laquo;Цей город наш, ми його захопили</p>\r\n<p>І хочемо взяти відкуп з горожан: по дві гривні з кожного&raquo;.</p>\r\n<p>І рече їм Володимир: &laquo;Пождіть, хай же за місяць зберуть вам куни&raquo;.</p>\r\n<p>І ждали місяць, і не дав їм.</p>\r\n<p>І сказали варяги: &laquo;Збрехав ти нам,</p>\r\n<p>То покажи нам шлях у греки&raquo;. Він же сказав: &laquo;Ідіть!&raquo;</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Изъбра отъ нихъ мужа добры и смыслены и храбъры, и раздая имъ грады; прочии же идоша Царюграду. И посла предъ ними послы, глаголя сице цареві: &laquo;се идуть к теб<small>Ђ</small> Варязи, не мози ихъ д<small>Ђ</small>ржати в город<small>Ђ</small>, или то створять ти въ град<small>Ђ</small>, яко зд<small>Ђ</small>, но расточи я раздно, а семо не пущай ни единого&raquo;. И нача княжити Володимиръ въ Киев<small>Ђ</small> одинъ, и постави кумиры на холъму вн<small>Ђ</small> двора теремнаго: Перуна деревяна, а голова его серебряна, а усъ золотъ, и Хоръса, и Дажьбога, и Стрибога, и С<small>Ђ</small>марьгла, и Мокошь. И жряху имъ, наричуще богы, и привожаху сыны своя, и жряху б<small>Ђ</small>сомъ, и оскв<small>Ђ</small>рняху землю требами своимі, и оскв<small>Ђ</small>рнися требами земля Руская и холмъ тъ. Но преблагый Богъ не хотяй смерти гр<small>Ђ</small>шникомъ; на томъ холм<small>Ђ</small> нын<small>Ђ</small> церкы есть святаго Василья, якоже посл<small>Ђ</small>д<small>Ђ</small> скажемъ. Мы же на преднее възвратимся. Володимиръ же посади Добрыню уя своего в Нов<small>Ђ</small>город<small>Ђ</small>; и пришедъ Добрыня Новугороду, постави Перуна кумиръ надъ р<small>Ђ</small>кою Волховомъ, и жряхуть ему людье Новгородьстии акы Богу. Б<small>Ђ</small> же Володимиръ поб<small>Ђ</small>женъ похотью женьскою, быша ему водимыя: Рогън<small>Ђ</small>дь, юже посади на Лыбеди, идеже есть нын<small>Ђ</small> селце Передславино, отъ неяже роди 4 сыны: Изеслава, Мьстислава, Ярослава, Всеволода, и 2 дщери; отъ Гр<small>Ђ</small>кини Святополка; отъ Чехыни Вышеслава, а отъ другия Святослава [и Мьстислава]; отъ Болъгарыни Бориса и Гл<small>Ђ</small>ба; и наложьниць у него 300 въ Вышегород<small>Ђ</small>, 300 в Б<small>Ђ</small>л<small>Ђ</small>город<small>Ђ</small>, а 200 на Берестов<small>Ђ</small>мъ в сельци, еже зовуть и нын<small>Ђ</small> Берестовее. И б<small>Ђ</small> несыть блуда, и приводи к себ<small>Ђ</small> мужьскыя жены и д<small>Ђ</small>вици растля я; б<small>Ђ</small> бо женолюбець, яко и Соломонъ, б<small>Ђ</small> бо [женъ] у Соломона, рече, 700, а наложниць 300. Мудръ же б<small>Ђ</small>, а након<small>Ђ</small>ць погибе; сь же б<small>Ђ</small> нев<small>Ђ</small>гласъ након<small>Ђ</small>ць обр<small>Ђ</small>те спасение. Велий бо Господь, и велья кр<small>Ђ</small>пость его, и разуму его н<small>Ђ</small>сть числа! зло бо есть женьская прел<small>Ђ</small>сть, якоже рече Соломонъ, покаявся о женахъ: не внимати зл<small>Ђ</small> жен<small>Ђ</small>; медъ бо каплеть отъ устъ ея, жены любод<small>Ђ</small>йца, во время наслажаетъ твой гортань, посл<small>Ђ</small>д<small>Ђ</small>же горьч<small>Ђ</small>е желчи обрящеши; прил<small>Ђ</small>пляющаяся ей смертью въ адъ; на пути бо животъныя не находить, блудна бо теченья ея, и неблагоразумна. Се же рече Соломонъ о прелюбод<small>Ђ</small>ицахъ; о добрыхъ же женахъ рече: дражьши есть каменья многоц<small>Ђ</small>ньнаго; радуется о ней мужь ея, д<small>Ђ</small>еть бо мужеви своему благо все житье; обр<small>Ђ</small>тши волну и ленъ, створить благопотребная рукама своима; бысть яко корабль, куплю д<small>Ђ</small>юще, издалеча събираеть себ<small>Ђ</small> богатьство; и въстаеть из нощи, и даеть брашно дому и д<small>Ђ</small>ло рабынямъ; вид<small>Ђ</small>вши тяжание куповаше, отъ д<small>Ђ</small>лъ руку своею насадить тяжание; препоясавши кр<small>Ђ</small>пько чресла своя, и утв<small>Ђ</small>рьди мышьци свои на д<small>Ђ</small>ло, и вкуси, яко добро д<small>Ђ</small>лати, и не угасаеть св<small>Ђ</small>тилникъ ея всю нощь; руц<small>Ђ</small> свои простираеть на полезная, локти же свои утв<small>Ђ</small>ржаеть на веретено; руц<small>Ђ</small> свои отв<small>Ђ</small>рзаеть убогимъ, плодъ же простре нищимъ; не печеться о дому своемъ мужь ея, егда кд<small>Ђ</small> будеть; сугуба од<small>Ђ</small>нья створить мужю своему, очерьвлена и багъряна себ<small>Ђ</small> од<small>Ђ</small>нья; възоренъ бываеть въ врат<small>Ђ</small>хъ мужь ея, внегда аще сядеть на соньмищи съ старци и съ жители земля; опоны створи и оддасть в куплю; уста же своя отв<small>Ђ</small>рзе смыслено, и въ чинъ молвить языкомъ своимъ; въ кр<small>Ђ</small>пость и в л<small>Ђ</small>поту облечеся; милостыня ея въздвигоша, чада ея обогат<small>Ђ</small>ша, и мужь ея похвали ю; жена бо разумлива благословена есть, боязнь же Господню да хвалить; да дите ей отъ плода устъну ея, да хвалять въ вратехъ мужа ея.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>І вибрав із них мужів добрих, і розумних, і хоробрих,</p>\r\n<p>І роздав їм городи, а інші ж пішли до Цареграда.</p>\r\n<p>І послав перед них послів сказати таке цареві:</p>\r\n<p>&laquo;Се ідуть до тебе варяги, не маєш тримати їх в городі,</p>\r\n<p>А то накоять тобі в городі, як тут,</p>\r\n<p>І розсели їх порізно, а сюди не пускай жодного&raquo;.</p>\r\n<p>І почав княжити Володимир у Києві один,</p>\r\n<p>І поставив кумирів на горі за теремним двором:</p>\r\n<p>Перуна дерев\'яного, а голова його срібна, а вус золотий,</p>\r\n<p>І Хорса, і Дажбога, і Стрибога, і Сімаргла, і Мокош.</p>\r\n<p>І жертви їм приносили, називаючи богами,</p>\r\n<p>І приводили до них синів своїх,</p>\r\n<p>І жертви приносили бісам,</p>\r\n<p>І оскверняли землю требами своїми.</p>\r\n<p>І осквернялася требами земля Руська</p>\r\n<p>І гора та.</p>\r\n<p>&nbsp;</p>\r\n<p>Але преблагий Бог не хоче смерті грішників. На тій горі нині стоїть церква святого Василія, як про це пізніше скажемо. Ми ж повернемося до попереднього.</p>\r\n<p>Володимир же посадив Добриню, дядю свого, в Новгороді. І коли Добриня прийшов у Новгород, поставив над річкою Волховом Перуна ку\'мир, і приносили йому жертви люди новгородські, як Богу.</p>\r\n<p>Володимир же був переможений пожадливістю до жінок. Були до нього приведені: Рогніда, що жила на Либеді, де нині село Передславино, вона народила йому чотири сина: Ізяслава, Мстислава, Ярослава, Всеволода - і дві дочки. Від гречанки мав Святополка, від чешки - Вишеслава, а від другої [чешки] - Святослава і Мстислава. Від болгарки - Бориса і Гліба. І наложниць було у нього триста в Вишгороді, триста в Білігороді і двісті на Берестовім в невеликому селі, яке і нині називають Берестове. І був ненаситним у блуді, і приводив до себе заміжніх жінок і дівиць розбещував, бо був жонолюбець, як і Соломон, бо було у Соломона, кажуть, сімсот жон, а наложниць - триста. Мудрий же був, а врешті-решт загинув. Цей же був невіглас, а в кінці кінців придбав собі вічне спасіння. &laquo;Великий-бо Господь, і велика міць його, і розум його безмежний!..&raquo; Жіночі зваби - зло, як сказав, покаявшись, Соломон про жінок: &laquo;Не звертай уваги на злу жінку; крапають солодощ губи блудниці, лише короткий час усолоджує вона гортань твою, а після гіркоту жовчі відчуєш. Той, хто входить до неї, не вернеться, після смерті його стежка в ад. По стежці життя не йде вона, бо блудлива течія життя її і нерозважлива&raquo;. Це сказав Соломон про блудниць, про чеснотних же жінок говорить: &laquo;Дорожчі за каміння коштовне. Радіє із неї муж її, бо робить мужу своєму благо все життя. Придбає вовну і льон, створить усе необхідне руками своїми. Подібна кораблеві торговому, здалеку збирає собі багатство. І встає з ночі, і годує сім\'ю свою, і дає роботу рабиням. Побачить поле - купує, руками власними засіє поле. Кріпко стан свій перев\'язавши, силу рук віддасть праці. І відчуває, що добро творить. І не згасає всю ніч її світильник. Руки свої прикладає до корисного, лікті ж свої віддає веретену, долоні рук розкриває для вбогих, подасть плід жебракові. Не печеться про дім свій муж її, де б він не був. Винятковий одяг пошиє мужу своєму, а червлений і багряний одяг - для себе. Помітний буває на воротях муж її, а особливо коли сяде на зібранні із старійшинами і жителями землі. Покривала зробить і віддасть на продаж. Вуста ж свої розкриває розумно і до ладу мовить язиком своїм. В силу і красу одяглась вона. Милість підносить її, діти її збагачують, і муж хвалить її. Розумна жона - благословенна, і хай похваляє страх божий. Дайте їй від плоду вуст її, і хай прославляють на воротях мужа її&raquo;.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6489. Иде Володимиръ к Ляхомъ и зая грады ихъ, Перемышль, Червенъ и ины городы, иже суть и до сего дне подъ Русью. Семъ же л<small>Ђ</small>т<small>Ђ</small> и Вятичи поб<small>Ђ</small>ди, и възложи на ня дань отъ плуга, якоже отець его ималъ.</p>\r\n<p>В л<small>Ђ</small>то 6490. Заратишася Вятичи, и иде на ня Володимиръ, и поб<small>Ђ</small>ди я въторое.</p>\r\n<p>В л<small>Ђ</small>то 6491. Иде Володімиръ на Ятвягы, и взя землю ихъ. И приде къ Киеву, и творяше требу кумиромъ с людми своими; и ркоша старии и бояре: &laquo;мечемъ жребий на отрока и д<small>Ђ</small>вицю; на него же падеть, того зар<small>Ђ</small>жемы богомъ&raquo;. И бяше Варягъ одинъ, б<small>Ђ</small> дворъ его, идеже б<small>Ђ</small> церкви святыя Богородица, юже създа Володимиръ; б<small>Ђ</small> же Варягътъ пришелъ отъ Гр<small>Ђ</small>къ, и д<small>Ђ</small>ржаше в<small>Ђ</small>ру втайн<small>Ђ</small> крестьяньскую; и бъ у него сынъ красенъ лицемъ и душею, и на сего паде жребій, по зависти дьяволи. Не т<small>Ђ</small>рпяше бо дьяволъ, власть им<small>Ђ</small>я надъ всими; сьй бяше ему акы т<small>Ђ</small>рнъ въ сердци, и тщашеся потребити оканный и наусти люди. И р<small>Ђ</small>ша пришедъша послании к нему: &laquo;яко паде жребий на сынъ твой, изволиша бо и бози себ<small>Ђ</small>; да створимъ требу богомъ&raquo;. И рече Варягъ: не суть бози, но древо; днесь есть, а утро изъгнило есть; не ядять бо, ни пьють, ни молвять, но суть д<small>Ђ</small>лани руками въ древ<small>Ђ</small>, сокирою и ножемъ; а Богъ единъ есть, емуже служать Гр<small>Ђ</small>ци и кланяются, иже створилъ небо и землю, и челов<small>Ђ</small>ка, и зъв<small>Ђ</small>зды, и солнце, и луну, и далъ есть жити на земли; а си бози что сдьлаша? сами дьлани суть; не дамъ сына своего б<small>Ђ</small>сомъ&raquo;. Они же шедъше пов<small>Ђ</small>даша людемъ; они же вземъше оружье, поидоша на нь и разъяша дворъ около его; онъ же стояще на с<small>Ђ</small>нехъ съ сыномъ своимъ. Р<small>Ђ</small>ша ему: &laquo;дай сына своего, дамы и богомъ&raquo;. Онъ же рече: &laquo;аще суть бози, то единого себе послють бога, да поимуть сына моего; а вы чему перетеребуете имъ&raquo;? И кликнуша, и пос<small>Ђ</small>коша с<small>Ђ</small>ни подъ ними, и тако побиша я, и не св<small>Ђ</small>сть никтоже, кде положиша я. Бяху, бо челов<small>Ђ</small>ци тогда невегласи [и] погани; и дьяволъ радовашеся сему, не в<small>Ђ</small>ды, яко близъ погибель хотяше быти ему. Тако бо и пре [дъ] тъщашеся погубити родъ хьрестьянскый, но прогонимъ бяше крестомъ честнымъ во иныхъ странахъ. Зд<small>Ђ</small> же мняшеся оканьный, яко зд<small>Ђ</small> ми есть жілище; зд<small>Ђ</small> бо не суть учіли апостоли, ни пророци прорекъли. Не в<small>Ђ</small>дый Пророка глаголюща: и нареку не люди моя; о апостол<small>Ђ</small>хъ же рече: во всю землю изидоша в<small>Ђ</small>щания &laquo;хъ, и в кон<small>Ђ</small>ць вселеныя глаголи ихъ. Аще бо и т<small>Ђ</small>ломъ апостоли суть зд<small>Ђ</small> не были, но учения ихъ яко трубы гласять по вселенной въ ц<small>Ђ</small>рьквахъ. Ихъже ученьемъ поб<small>Ђ</small>жаемъ противнаго врага, попирающе подъ ноз<small>Ђ</small>; якоже попраста и сия отьченика, и приимъша в<small>Ђ</small>н<small>Ђ</small>ць небесный съ святыми мученикы и съ праведными.</p>\r\n<p>В л<small>Ђ</small>то 6492. Иде Володимиръ на Радимичи. И б<small>Ђ</small> у него воевода Волчий Хвостъ, и посла предъ собою Володимиръ Волчия Хвоста; и ср<small>Ђ</small>те Радимичи на р<small>Ђ</small>ц<small>Ђ</small> Пищан<small>Ђ</small> [и] поб<small>Ђ</small>ди Волчий Хвостъ Радимичи; т<small>Ђ</small>мъ и Русь корятся Радимичемъ, глаголюще: П<small>Ђ</small>щаньци Волъчья Хвоста б<small>Ђ</small>гають. Быша же Радимичи отъ рода Ляховъ, и пришедше ту ся вселиша, и платять дань в Руси, и повозъ везуть и до сего дне.</p>\r\n<p>В л<small>Ђ</small>то 6493. Иде Володимиръ на Болъгары съ Добрынею уемъ своимъ в лодьяхъ, а Торкы берегомъ приведе на конехъ, и тако поб<small>Ђ</small>ди Болгары. И рече Добърыня Володимиру: &laquo;съглядахъ колодникъ, и суть вси в сапоз<small>Ђ</small>хъ; симъ дани намъ не платити, пойдев<small>Ђ</small> искать лапотникъ&raquo;. И сътвори миръ Володимиръ с Болгары, и рот<small>Ђ</small> заходиша межи собою, и р<small>Ђ</small>ша Болгаре: &laquo;толи не буди мира межи нами, оли же камень начнеть плавати, а хмель грязнути&raquo;. И приде Владимиръ къ Киеву.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6489 [981]. Пішов Володимир на ляхів і захопив города їхні Перемишль, Червен й інші города, які і до сьогодні під Руссю. Цього ж року і в\'ятичів переміг, і наклав на них дань від плуга, як і отець його брав.</p>\r\n<p>В літо 6490 [982]. Повстали дружно в\'ятичі, і пішов на них Володимир, і переміг їх вдруге.</p>\r\n<p>В літо 6491 [983]. Пішов Володимир на ятвягів і взяв землю їхню. І повернувся до Києва, і приніс жертву кумирам з людьми своїми. І сказали старійшини і бояри: &laquo;Мечем жереб на отрока і дівицю, на кого впаде, того і заріжемо в жертву богам&raquo;. І був варяг один, був двір його, де зараз церква святої Богородиці, яку збудував Володимир. Варяг той прийшов колись від греків і таємно сповідував віру християнську. І був у нього син - красень на вроду і душею, і на нього, із зависті диявола, випав жереб. Не терпів його диявол, бо мав владу над усіма, а цей був йому як тернина в серці, і тужився згубити його окаянний, і науськав людей. І прийшли посланці до нього і сказали: &laquo;Як випав жереб на твого сина, то вибрали його собі боги, щоб ми принесли його в жертву богам&raquo;. І рече варяг: &laquo;Не боги то, а дерево. Сьогодні є, а завтра згнило. Не їдять-бо, не п\'ють, не говорять, а зроблені руками з дерева, сокирою і ножем. А Бог один, йому служать і поклоняються греки, він сотворив небо, і землю, і чоловіка, і зорі, і сонце, і місяць, і дав життя на землі, а ці боги що зробили? Самі зроблені. Не дам сина свого бісам&raquo;. Посланці пішли і розповіли людям. Вони ж взялися за оружжя, пішли на нього і рознесли його двір, а він стояв на сінях із сином своїм. Сказали йому: &laquo;Дай сина свого, дамо його богам&raquo;. Він же відповів: &laquo;Якщо вони боги, то хай пошлють одного собі бога і візьмуть сина мого. <em>А </em>ви чому їм жертви приносите?&raquo; І гукнули, і порубали сіни під ними, і так їх убили. І не знає ніхто, де їх поклали. Бо були тоді люди невігласами, поганами, І диявол радів цьому, не знаючи, як близько уже його погибель. Так він намагався згубити рід християнський, але прогнаний був хрестом чесним в інших землях.</p>\r\n<p>Тепер же думав окаянний, що тут собі знайде пристанище, бо тут ні апостоли не навчали, ні пророки не пророкували. Не відав, що пророк сказав: &laquo;І назову не моїх людей моїми людьми&raquo;, а про апостолів же сказано: &laquo;По всій землі розійшлися повчання їхні, і до кінця вселенної - слова їхні&raquo;. А якщо і самі апостоли тут не були, то учення їхнє, як звуки труби у вселенній, лунає в церквах. Їхнім ученням перемагаємо богопротивного ворога, кидаючи його під ноги. Так же перемогли його ції два мученики і прийняли вінець небесний нарівні із святими мучениками і праведними.</p>\r\n<p>В літо 6492 [984]. Пішов Володимир на радимичів. І був у нього воєвода Вовчий Хвіст. І послав Володимир поперед себе Вовчого Хвоста. І зустрів він радимичів на ріці Піщаній, і переміг радимичів Вовчий Хвіст. А тому і дражнить русь радимичів, приказуючи: &laquo;Піщанці від вовчого хвоста тікають&raquo;. Були ж радимичі ляського роду, і, прийшовши, поселились тут, і платять данину Русі, і повоз возять і до сьогодні.</p>\r\n<p>В літо 6493 [985]. Ходив Володимир на болгар в лодіях із дядьком своїм Добринею, а торків провів берегом на конях. І так переміг болгар. І сказав Добриня Володимиру: &laquo;Оглядав колодників, а еони всі в чоботях. Ці данину нам не платитимуть, підемо шукати лапотників&raquo;. І уклав Володимир мир з болгарами, і поклялися один другому, і сказали болгари: &laquo;Тоді миру між нами не буде, коли камінь почне плавати, а хміль тонути&raquo;. І повернувся Володимир до Києва.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6494. Приидоша Болгаре в<small>Ђ</small>ры Бохъмичи, глаголюще: &laquo;яко ты князь еси мудръ и смысленъ, и не в<small>Ђ</small>си закона; да в<small>Ђ</small>руй въ законъ нашь и поклонися Бохъмиту&raquo;. Рече Володимиръ: &laquo;кака есть в<small>Ђ</small>ра ваша?&raquo; Они же р<small>Ђ</small>ша: &laquo;в<small>Ђ</small>руемъ Богу; а Бохъмитъ ны учить, глаголя: обр<small>Ђ</small>зати уды тайныя, а свинины не <small>Ђ</small>сти, а вина не пити, и по смерти съ женами похоть творити блудную; дасть Бохъмитъ комуждо по семидесятъ женъ красъныхъ, и избереть едину красну и вс<small>Ђ</small>хъ красоту възложить на едину, и та будеть ему жена; зд<small>Ђ</small> же, рече, достоить блудъ творити всякыи; на семъ же св<small>Ђ</small>т<small>Ђ</small> аще будеть кто убогъ, то и тамо; аще ли богатъ есть зд<small>Ђ</small>, то и тамо&raquo;; и ина многа лесть, еяже нелз<small>Ђ</small> писати срама ради. Володимиръ же слушаше ихъ, бь бо самъ любяше жены и блужение многое, и послушаше сладъко; но се б<small>Ђ</small> ему не любо, обр<small>Ђ</small>зание удовъ, и о яденьи свиныхъ мясъ, а о питьи отинюдь; рекъ: &laquo;Руси веселье питье, не можемъ без того быти&raquo;. Посемъ же придоша Н<small>Ђ</small>мци отъ Рима, глаголюще: &laquo;яко придохомъ послани отъ папежа&raquo;; и ркоша ему: &laquo;реклъ ти папежь: земля твоя яко земля наша, а в<small>Ђ</small>ра ваша не акы в<small>Ђ</small>ра наша; в<small>Ђ</small>ра бо наша св<small>Ђ</small>тъ есть, кланяемъся Богу, иже створи небо и землю, и зв<small>Ђ</small>зды, и м<small>Ђ</small>сяць, и всяко дыхание, а бози ваши древо суть&raquo;. Володимиръ же рече: &laquo;какая есть запов<small>Ђ</small>дь ваша?&raquo; Они же р<small>Ђ</small>ша: &laquo;пощение по сил<small>Ђ</small>; аще кто пьеть или <small>Ђ</small>сть, все въ славу Божию, рече учитель нашь апостолъ Павелъ&raquo;. Рече же Володимиръ Н<small>Ђ</small>мцемъ: &laquo;идете опять, яко отци наши сего не прияли суть&raquo;. Се слышавше Жидове Козарстии приидоша, ркуще: &laquo;слышахомъ, яко приходиша Болъгаре и Хрестьяни, учаще тя кождо ихъ в<small>Ђ</small>р<small>Ђ</small> своей; Хрестьяни бо в<small>Ђ</small>рують, его же мы распяхомъ; а мы в<small>Ђ</small>руемъ едину Богу Аврамову, Исакову, Ияковлю&raquo;. И рече Володимиръ: &laquo;что есть законъ вашь?&raquo; Они же р<small>Ђ</small>ша: &laquo;обр<small>Ђ</small>затися, и свинины не ясти, ни заячины, суботу хранити&raquo;. Онъ же рече: &laquo;то кде есть земля ваша&raquo;? Они же р<small>Ђ</small>ша: &laquo;въ Иерусалим<small>Ђ</small>&raquo;. Онъ же рече: &laquo;то тамо ли есть нын<small>Ђ</small>?&raquo; Они же р<small>Ђ</small>ша: &laquo;разгн<small>Ђ</small>валъся Богъ на отци наши, и расточи ны по странамъ гр<small>Ђ</small>хъ ради нашихъ, и предана бысть земля наша Хрестьяномъ&raquo;. Володимиръ же рече: &laquo;то како вы ин<small>Ђ</small>хъ учите, а сами отв<small>Ђ</small>ржени Бога? Аще бы Богъ любилъ васъ, то не бысте расточени по чюжимъ землямъ; еда и намъ тоже мыслите зло пріяти&raquo;? Посемъ же прислаша Гр<small>Ђ</small>ци къ Володимиру философа, глаголюще сице: &laquo;слы шахомъ, яко приходили суть Болгаре, уча ще тя приняти в<small>Ђ</small>ру свою, ихъже в<small>Ђ</small>ра оскв<small>Ђ</small>рняеть небо и землю, иже суть проклят<small>Ђ</small> паче ве<small>Ђ</small>хъ человъкъ, уподобльшеся Содому и Гомору, на няже пусти Богъ кам<small>Ђ</small>нье горущее, и потопи я и погрязоша; яко и сихъ ожидаеть день погибели ихъ, егда придеть Богъ судити на землю и погубити вься творящая безаконье и скв<small>Ђ</small>рны д<small>Ђ</small>ющая; си бо омывають оходы своя, поливавшеся водою, и въ ротъ вливають, и по брад<small>Ђ</small> мажются, наричюще Бохмита; такоже и жены ихъ творять ту же скв<small>Ђ</small>риу и ино же пуще, отъ совокупления мужьска вкушають&raquo;. Си слышавъ Володимиръ, плюну на землю, рекъ: &laquo;не чисто есть дъло&raquo;. Рече же философь: &laquo;слышахомъ же и се, яко приходиша отъ Рима Н<small>Ђ</small>мци учить васъ к в<small>Ђ</small>р<small>Ђ</small> своей, ихъже въра с нами мало же развращена, служать бо опр<small>Ђ</small>снокы, рекше оплатъкы, ихъже Богъ не преда, но повел<small>Ђ</small> хл<small>Ђ</small>бомъ служити, и преда апостоломъ, приимъ хл<small>Ђ</small>бъ и рекъ: &laquo;се есть т<small>Ђ</small>ло мое, ломимое за вы; такоже и чашю приимъ, рече: се есть кровь моя новаго зав<small>Ђ</small>та. Си же того не творять, и суть не исправил<small>Ђ</small> в<small>Ђ</small>ры&raquo;. Рече же Володимиръ: &laquo;придоша къ мн<small>Ђ</small> Жидове, глаголюще, яко Н<small>Ђ</small>мьци и Гр<small>Ђ</small>ци в<small>Ђ</small>ру[ю]ть, егоже мы распяхомъ&raquo;. Философъ же рече: &laquo;воистину в того в<small>Ђ</small>руемъ, т<small>Ђ</small>хъ бо пророци прорекоша, яко Богу родитися, а другии распяту быти [и погребену быти] и третьи день въскреснути, и на небеса възити; они же ты пророкы избиваху, а другия претираху. Егда же събысться проречение ихъ, сниде на землю, и распятье приять, и въскресе, и на небеса възиде, а сихъ же ожидаше покаянья за 40 л<small>Ђ</small>тъ и за 6 л<small>Ђ</small>тъ, не покаяшася, и посла на ня Римляны, грады ихъ разъбиша, а сам<small>Ђ</small>хъ расточиша по странамъ, и работають въ странахъ&raquo;. Рече же Володимиръ: &laquo;что ради сниде Богъ на землю, и страсть таку приять?&raquo; Отв<small>Ђ</small>щавъ же рече философъ: &laquo;аще хощеши, княже, послушати [да скажу ти] изъ начала, что ради сниде Богъ на землю&raquo;. Володимиръ же рече: &laquo;послушаю радъ&raquo;. И нача философъ глаголати сице:</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6494 [986]. Прийшли болгари магометанської віри, кажучи: &laquo;Ти князь мудрий і розумний, а закону не знаєш. Віруй у наш закон і поклонися Бохмиту&raquo;. Володимир запитав: &laquo;Яка ж ваша віра?&raquo; Вони ж відповіли. &laquo;Віруємо богу, а Бохмит нас навчає, наказуючи: робити обрізання, а свинини не їсти і вина не пити, а з жінками і після смерті похотіння блудні творити можна: дасть Бохмит кожному по сімдесят жон красних, і вибере одну найвродливішу, і красу всіх їй віддасть, і вона буде йому жона. Тут же, говорить, належить безперешкодно віддаватися всякому блуду. Якщо хтось на цьому світі бідний, то і на тому. Якщо хто багатий тут, то і там&raquo;. І іншої багато брехні говорили, про яку і писати соромно.</p>\r\n<p>Володимир же слухав їх, бо і сам любив жінок і грішив з ними багато, і вислухав з насолодою. Але ось це було йому не любо: обрізання, заборона їсти свинину, а особливо заборона випивати. Сказав: &laquo;Для Русі пиття - веселість, не можемо без того бути&raquo;.</p>\r\n<p>Після цього прийшли німці від Риму, кажучи: &laquo;Ми прийшли як посланці від папи&raquo;. І сказали Володимиру: &laquo;Говорив тобі папа: земля твоя - як і земля наша, а віра ваша не така, як віра наша. Бо наша віра - світло, кланяємося Богу, що сотворив небо, і землю, і зорі, і місяць, і все, що дихає, а ваші боги - дерево&raquo;. Володимир же рече: &laquo;А яка заповідь ваша?&raquo; Вони ж відповіли: &laquo;Піст, по можливості, а коли хто п\'є чи їсть,- все на славу божу,- говорить учитель наш апостол Павло&raquo;. Сказав Володимир німцям: &laquo;Ідіть, звідки прийшли, бо й батьки наші не прийняли цього&raquo;.</p>\r\n<p>Це почули козарські жиди і, прийшовши до Володимира, сказали: &laquo;Чули ми, що приходили до тебе болгари і християни і кожний навчав тебе своєї віри. Християни вірують в того, кого ми розп\'яли, а ми віруємо в єдиного бога Аврама, Ісака, Якова&raquo;. І рече Володимир: &laquo;Який закон ваш?&raquo; Вони ж відповіли: &laquo;Обрізатися, і свинини і заячини не їсти, суботу берегти&raquo;. Він же запитав: &laquo;А де земля ваша?&raquo; Вони ж відповіли: &laquo;В Ієрусалимі&raquo;. Він же перепитав: &laquo;То чи там вона і нині?&raquo; Вони ж відповіли: &laquo;Розгнівався бог на батьків наших і розсіяв нас за гріхи наші по світу, а землю нашу віддав християнам&raquo;. Володимир же рече: &laquo;То як ви Інших учите, а самі отвергнуті богом? Коли б бог любив вас, то не були б розсіяні по чужих землях. Чи і нам того зла хочете?&raquo;</p>\r\n<p>Після цього до Володимира прислали греки філософа, який сказав таке: &laquo;Чули ми, що приходили болгари і схиляли тебе прийняти віру свою, їхня ж віра оскверняє небо і землю, і прокляті вони гірше всіх людей, і уподобились Содому і Гоморрі, на яких напустив Бог палаюче каміння і затопив їх, і потонули. Так і на цих чекає день погибелі їхньої, коли прийде Бог на землю судити і погубить всіх, що беззаконня творять і скверни діють. Ці ж бо підмиваються, поливаючись водою, і в рот ту воду вливають, і по бороді нею мажуть, поминаючи Бохмита. Подібно і жони їхні роблять те ж паскудство і ще більше, вкушаючи те, що від злягання з мужами буває&raquo;. Отож вислухавши, плюнув Володимир на землю і сказав: &laquo;Нечисте діло&raquo;. Філософ же рече: &laquo;Чули ж ми і те, як приходили від Риму німці навчати вас своєї віри. Їхня ж віра від нашої дещо відмінна, служать-бо на опрісноках, тобто на облатках, яких Бог не заповідав, бо повелів на хлібі служити і апостолам заповідав, взявши хліб, казав: &laquo;Це тіло моє, яке ламають за вас&raquo;. Також і чашу взявши, говорив: &laquo;Це кров моя нового завіту&raquo;. Ці ж того не роблять, а значить неправильно вірують. Каже Володимир: &laquo;Приходили до мене жиди і говорили, що німці і греки вірують в того, кого вони розп\'яли&raquo;. Філософ же пояснив: &laquo;Воістину віруємо в того, їхні ж пророки предрікали, що народиться Бог, а другі, що розп\'ятий і похований буде, а на третій день воскресне і на небеса вознесеться. Вони ж тих пророків побили, а других катували. Коли ж збулися пророцтва їхні, коли зійшов на землю і був розп\'ятий, і вознісся на небеса, чекав Бог на покаяння їхнє сорок і шість років - і не покаялися. І послав тоді на них римлян. Римляни городи їхні порозбивали, а їх самих розсіяли по інших землях, і труждаються вони в тих землях&raquo;. Володимир запитав: &laquo;Чого ради зійшов Бог на землю і страждання такі прийняв?&raquo; Відповідаючи, сказав філософ: &laquo;Якщо хочеш, князю, послухати, то розповім тобі все від початку, чого ради зійшов Бог на землю&raquo;. Володимир сказав: &laquo;Радий послухати&raquo;. І почав філософ розповідати так:</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&laquo;Въ начала исп<small>Ђ</small>рва створі Богъ небо и землю въ 1 день. Въ вторый день створи твердь, иже есть посред<small>Ђ</small> водъ; сего же дни разд<small>Ђ</small>лишася воды, полъ ихъ възиде на тв<small>Ђ</small>рдь, а полъ ихъ подъ твердь. Въ 3 день сътвори море, р<small>Ђ</small>кы, источники и с<small>Ђ</small>мена. Въ 4 солнце, и луну и зв<small>Ђ</small>зды, и украси Богъ небо. Вид<small>Ђ</small>въ же п<small>Ђ</small>рвый отъ ангелъ, стар<small>Ђ</small>йшина чину ангельску, помысли в себе, рекъ: сниду на землю, и прииму землю, и поставлю престолъ свой на облац<small>Ђ</small>хъ с<small>Ђ</small>верьскыхъ, и буду подобенъ Богу. И ту абье сверже и съ небеси,. а по немъ спадоша, иже б<small>Ђ</small>ша подъ нимъ чинъ десятый. Б<small>Ђ</small> же имя противнику Сотанаилъ, в не же м<small>Ђ</small>сто постави стар<small>Ђ</small>йшину Михаила. Сотана же, гр<small>Ђ</small>шивъ помысла своего и отъпадъ славы п<small>Ђ</small>рвыя, наречеся противникъ Богу. Посемъ же въ 5 день створи Богъ киты, и гады, и рыбы, и птицы пернатыя, и зв<small>Ђ</small>ри, и скоты, и гады земныя. Въ 6 день створи же Богъ челов<small>Ђ</small>ка. Въ 7 день почти Богъ отъ д<small>Ђ</small>лъ своихъ, еже есть субота. И насади Богъ рай на въстоци въ Едем<small>Ђ</small>, и въведе Богъ ту челов<small>Ђ</small>ка, егоже созда, и запов<small>Ђ</small>да ему отъ древа всякого ясти, отъ древа же единого не ясти, иже есть разум<small>Ђ</small>ти злу и добру. И б<small>Ђ</small> Адамъ в раи, и видяше Бога и славяше, егда ангели славяху Бога, и онъ с ними. И възложи Богъ на Адама сонъ, и успе Адамъ, и взятъ Богъ едино ребро у Адама, и створі ему жену, и приведе ю къ Адаму, и рече Адамъ: &laquo;се кость отъ кости моея, и плоть отъ плоти моея, си наречеться жена&raquo;. И нарече Адамъ имена вс<small>Ђ</small>мъ скотомъ, и птицамъ, и зв<small>Ђ</small>ремъ, и гадомъ; и сам<small>Ђ</small>ма ангелъ пов<small>Ђ</small>да имени. И покори Богъ Адаму зв<small>Ђ</small>ри и скоты, и обладаше всими, и послушаху его. Вид<small>Ђ</small>въ же дьяволъ, яко почсти Богъ челов<small>Ђ</small>ка, позавид<small>Ђ</small>въ ему, преобразися въ змию, и прииде къ Евз<small>Ђ</small>, и рече ей: &laquo;почто не яста отъ древа, сущаго посред<small>Ђ</small> рая&raquo;? И рече жена къ змии: &laquo;рече Богъ не имата ясти, оли да умрета смертью&raquo;. И рече змия къ жен<small>Ђ</small>: &laquo;смертью не умрета; в<small>Ђ</small>даше бо Богъ, яко въ нь же день яста отъ него, отв<small>Ђ</small>рзостася очи ваю, и будета яко Богъ, разум<small>Ђ</small>вающа добро и зло&raquo;. И вид<small>Ђ</small> жена, яко добро древо въ ядь, и вземьши жена сн<small>Ђ</small>сть, и въдасть мужю своему, и яста, и отв<small>Ђ</small>рзостася очи има, и разум<small>Ђ</small>ста яко нага есть, и сшиста листвиемъ смоковьнымь препоясание. И рече Богъ: &laquo;проклята земля въ д<small>Ђ</small>лехъ твоихъ, в печали яси вся дни живота твоего&raquo;. И рече Господь Богъ: &laquo;егда како прострета руку, и возмета отъ древа животнаго, и живета в в<small>Ђ</small>къ&raquo;. Изъгна Господь Богъ Адама из рая. И с<small>Ђ</small>де прямо раю, плачася и д<small>Ђ</small>лая землю, и порадовася Сатана о проклятьи земля: се на ны первое паденіе, горкый отв<small>Ђ</small>тъ, отъпаденія ангелъскаго житья. И роди Адамъ Каина и Ав<small>Ђ</small>ля; и б<small>Ђ</small> Каинъ ратай, а Ав<small>Ђ</small>ль пастухъ. Принесе Каинъ отъ плодъ земныхъ къ Богу, и не прия Богъ даровъ его; а Авель принесе отъ агн<small>Ђ</small>ць п<small>Ђ</small>рв<small>Ђ</small>н<small>Ђ</small>ць, и прия Богъ дары Ав<small>Ђ</small>левы. Сотона же въл<small>Ђ</small>зе въ Каина, и постр<small>Ђ</small>каше Каина на убийство Авелево; и рече Каинъ къ Ав<small>Ђ</small>лю: &laquo;изидев<small>Ђ</small> на поле&raquo;. И яко изидоша, въста Каинъ и хотяше убити и, [и] не ум<small>Ђ</small>яше убити; и рече ему Сотона: &laquo;возми камень и удари и&raquo;. И уби Ав<small>Ђ</small>ля. И рече Богъ Каину: &laquo;кде есть братъ твой?&raquo; Онъ же рече: &laquo;егда азъ стражь есмь брату моему&raquo;? И рече Бог: &laquo;кровь брата твоего въпиетъ къ мн<small>Ђ</small>, буди стоня и трясыся до живота своего&raquo;. Адамъ же и Евга плачющася бяста, и дьяволъ радовашеся, рекъ: &laquo;сего же Богъ почсти, азъ створихъ ему отпасти отъ Бога, и се нын<small>Ђ</small> плачь ему нал<small>Ђ</small>зохъ&raquo;. И плакастася по Ав<small>Ђ</small>л<small>Ђ</small> л<small>Ђ</small>тъ 30, и не съгни т<small>Ђ</small>ло его, и не ум<small>Ђ</small>ста погрести его; и повел<small>Ђ</small>ньемъ Божиимъ птенца два прилет<small>Ђ</small>ста, единъ ею умре, и единъ же ископа яму, вложи ум<small>Ђ</small>ршаго, и погребе. Вид<small>Ђ</small>вша же се Адамъ и Евга, ископаста ему яму, и вложиста Ав<small>Ђ</small>ля, и погребоста и с плачемъ. Бысть же Адамъ л<small>Ђ</small>тъ 230, роди Сифа и 2 дщери, и поя едину Каинъ, а другую Сифъ, и отъ того челов<small>Ђ</small>ци расплодишася по земли, и не познаша створшаго я, исполнишася блуда, и всякого скаредія, и убийства, и зависти, и живяху скотьскы челов<small>Ђ</small>ци. И б<small>Ђ</small> Ной единъ прав<small>Ђ</small>денъ въ род<small>Ђ</small> семъ, и роди 3 сыны, Сима, Хама, Афета. И рече Богъ: &laquo;не имать пребывать духъ мой въ челов<small>Ђ</small>цехъ&raquo;, и рече: &laquo;да потреблю челов<small>Ђ</small>ка, егоже створихъ, отъ челов<small>Ђ</small>ка до скота&raquo;. И рече Богъ Ноеви: &laquo;створи ковчегъ в долготу лакоть 300, а в широту 50, а въ выше 30 лакотъ&raquo;, во Егупт<small>Ђ</small> бо локтемъ саженъ зовуть. Д<small>Ђ</small>лаему же ковчегу за 100 л<small>Ђ</small>тъ, и пов<small>Ђ</small>даше Ной, яко быти потому, посм<small>Ђ</small>хахуся ему людье. И егда сд<small>Ђ</small>ла ковчегъ, рече Господь Богъ Ноеві: &laquo;вл<small>Ђ</small>зи ты, и жена твоя, и сынове твои, и снохы твоя, и въведи я к себ<small>Ђ</small> по двоему отъ вс<small>Ђ</small>хъ гадъ&raquo;. И въведе Ной, якоже запов<small>Ђ</small>да ему Богъ. И наведе Богъ потопъ на землю, и потопе всяка плоть, и ковчегъ плаваше на вод<small>Ђ</small>; егда же посяче вода, изл<small>Ђ</small>зе Ной, и сынове его, и жена его; и отъ сихъ расплодися земля. И быша челов<small>Ђ</small>ци мнози и единогласни, р<small>Ђ</small>ша другъ другу: &laquo;зиждемъ столпъ до небесе&raquo;. И начаша здати, и б<small>Ђ</small> стар<small>Ђ</small>йшина имъ Невродъ, и рече Богъ: &laquo;умножишася челов<small>Ђ</small>ци, и помыслы ихъ суетни&raquo;; и съниде Богъ, и размЪси языкы на 70 и два языка Адамовъ же языкъ бысть неотъятъ у Авера; то бо едінъ не приложися къ безумью ихъ, р<small>Ђ</small>къ сице: &laquo;аще бы челов<small>Ђ</small>комъ Богъ реклъ на небо столпъ д<small>Ђ</small>лати, то повел<small>Ђ</small>лъ бы самь Богъ словомъ, якоже створи небеса, и землю, и море, и вся видимая и невидимая&raquo;. Того ради сего языкъ не прем<small>Ђ</small>нися; отъ сего суть Евр<small>Ђ</small>и На 70 и единъ языкъ разд<small>Ђ</small>лишася, и разидошася по странамъ, кождо свой нравъ прияша, и по дьяволю научению ови рощениемъ и кладязямъ жряху, и р<small>Ђ</small>камъ, и не познаша Бога. Отъ Адама же до потопа л<small>Ђ</small>тъ 2242, а отъ потопа до разъд<small>Ђ</small>ленья языкъ л<small>Ђ</small>тъ 529. Посемъ же дьяволъ в болша прелщения въверже челов<small>Ђ</small>кы, и начаша кумиры творить, ови древяныа и м<small>Ђ</small>дяныя, а друзии мороморяны, златы и сребряны; и кланяхуться имъ, и привожаху сыны своя и дыцери своя, и закалаху предъ ними, и б<small>Ђ</small> вся земля оскв<small>Ђ</small>рнена. И началникъ же бяше кумиротворению Серухъ, творяше бо кумиры въ имена мертвыхъ челов<small>Ђ</small>къ, бывшимъ ов<small>Ђ</small>мъ царемъ, другымъ храбрымъ, и волъхвомъ, и женамъ прелюбод<small>Ђ</small>йцамъ. Се же Серухъ роди Фару, Фара же роди 3 сыны, Аврама, и Нахора, и Арана. Фара же творяше кумиры, навыкъ у отца своего. Аврамъ же пришедъ въ умъ, възр<small>Ђ</small>въ на небо, и рече: &laquo;воистину то есть Богъ, иже створилъ небо и землю, а отець мой прельщаеть челов<small>Ђ</small>кы&raquo;. И рече Аврамъ: &laquo;искущю богъ отца своего&raquo;; и рече: &laquo;отче! прельщаеши челов<small>Ђ</small>кы, творя кумиры древяны; то есть Богъ, иже створилъ небо и землю&raquo;. И приимъ Аврамъ огнъ, зажьже идолы въ храмины. Вид<small>Ђ</small>въ же се Аранъ, братъ Аврамовъ, р<small>Ђ</small>внуя по идол<small>Ђ</small>хъ, хот<small>Ђ</small> умьчати идолъ, самъ згор<small>Ђ</small> ту Аранъ, и умре предъ отцемъ; пред<small>Ђ</small> с<small>Ђ</small>мъ бо не умиралъ сынъ предъ отцемъ, но отець предъ сыномъ, и отъ-сего начаша умирати сынове предъ отцемъ. И възлюби Богъ Аврама, и рече Богъ Авраму: &laquo;изиди изъ дому отца твоего и поиди в землю, в ню же ти покажю, и створю тя въ языкъ великъ, и благословять тя кол<small>Ђ</small>на земная&raquo;. И створи Аврамъ, якоже запов<small>Ђ</small>да ему Богъ. И поя Аврамъ Лота, сыновца своего, и б<small>Ђ</small> бо ему Лотъ шюринъ и сыновець, б<small>Ђ</small> бо Аврамъ поялъ братьню дщерь Ароню Сарру, и приде в землю Ханан<small>Ђ</small>йску къ дубу высоку, и рече Богъ къ Авраму: &laquo;с<small>Ђ</small>мени твоему дамъ землю сию&raquo;. И поклонися Аврамъ Богу; Аврамъ же бяше л<small>Ђ</small>ть 70, егда изиде отъ Хараона. Б<small>Ђ</small> же Сарра неплоды, болящі неплотьскымъ; рече Сарра Авраму: &laquo;вл<small>Ђ</small>зи убо къ раб<small>Ђ</small> моей&raquo;. И поемши Сарра Агарь, и вдасть и мужеви своему, и вл<small>Ђ</small>зъ Аврамъ къ Агари, и зача Агарь и роди сына, и прозва [и] Аврамъ Измаиломъ, а Аврамъ же б<small>Ђ</small> л<small>Ђ</small>тъ 86, егда родися Измаилъ. Посемъ же заченши Сарра, роди сына и нарече имя ему Исакъ; и пов<small>Ђ</small>л<small>Ђ</small> Богъ Авраму обр<small>Ђ</small>зати отроча, и обр<small>Ђ</small>за Аврамъ въ 8 день. И възълюби Богъ Аврама и племя его, и нарече я въ люди себ<small>Ђ</small>, и отлучи я отъ языкъ, нарекъ люди своя. Сему же Исаку възмогущю, Авраму же живущю л<small>Ђ</small>тъ 175, и умре и погребенъ бысть. Исаку же бывшю л<small>Ђ</small>тъ 60, роди два сына, Исава и Якова; Исавъ же бысть дукавъ, а Яковъ правдивъ. Сий же Яковъ работа у уя своего изъ дщери его изь меньшее 7 л<small>Ђ</small>тъ, и не дасть ему ея Лаванъ, уй его, рекъ: &laquo;стар<small>Ђ</small>йшюю поими&raquo;; и вдасть ему Лию стар<small>Ђ</small>йшюю, и изъ другое рекъ ему другую работай 7 л<small>Ђ</small>тъ. Онъ же работа другую 7 л<small>Ђ</small>тъ из Рахіли, и поя себ<small>Ђ</small> 2 сестреници, отъ нею же роди 8 сыновъ: Рувима, Семеона, Левгию, Июду, Исахара, и Заулона, Иосифа и Веньамина, от робу двою: Дана, Нефталима, Гада, Асира, и отъ сихъ расплодишася Жидове. Ияковъ же сниде въ Егупетъ, сы л<small>Ђ</small>тъ 130, с родомъ своимъ числомъ 65 душь; поживе же въ Егупт<small>Ђ</small> л<small>Ђ</small>ть 17 и успе, и поработиша племя его за 400 л<small>Ђ</small>тъ. По сихъ же л<small>Ђ</small>т<small>Ђ</small>хъ възмогоша людье Жидовьстии умножишася, и насиляхуть имъ Егуптяне работою. В си же Бремена родися Моис<small>Ђ</small>й в Жидехъ, и р<small>Ђ</small>ша волъстви Егупетьстии царю, яко родилъся есть дьтищъ въ Жидохъ, иже хощеть погубити Егупетъ. Ту абье повел<small>Ђ</small> царь ражающаяся д<small>Ђ</small>ти жидовьскыя им<small>Ђ</small>тати в р<small>Ђ</small>ку. Мати же Моис<small>Ђ</small>ова убоявшися сего погубления, вземъши младен<small>Ђ</small>ць, вложи въ крабьицю, и несъши постави в луз<small>Ђ</small>. В се же время сниде дщи Фараонова Фермуфи купаться, и від<small>Ђ</small> отроча плачющеся, и възя е и пощади е, и нарече имя ему Моисий, и въскорми е. И бысть отроча красно, и бысть 4-хъ л<small>Ђ</small>тъ, и приведе и дщи Фараоня къ отцю своему Фараону. Вид<small>Ђ</small>въ же Моис<small>Ђ</small>я Фараонъ, нача любити Фараонъ отроча; Моисій же хапаяся за шию цареву, срони в<small>Ђ</small>нець съ главы царевы, и попра и. Вид<small>Ђ</small>въ же волхвъ, рече цареви &laquo;о царю! погуби отроча се; аще ли не погубиши, имаеть погубити всь Егупетъ&raquo;, и не послуша его царь, но паче повел<small>Ђ</small> не погубити д<small>Ђ</small>тий жидовьскыхъ. Моисееви же вьзмогъшю, и бысть великъ в дому Фараони; и бысть царь инъ, възавид<small>Ђ</small>ша ему бояре. Моис<small>Ђ</small>й же уби Егупьтянина, б<small>Ђ</small>жа изъ Егупта; и приде в землю Мадиамьску, и ходя по пустыни, научися отъ ангела Гавриила о бытьи всего мира, и о п<small>Ђ</small>рв<small>Ђ</small>мъ челов<small>Ђ</small>ци, и яже суть была по немъ, и по потоп<small>Ђ</small>, и о см<small>Ђ</small>шении языкъ, аще кто колико л<small>Ђ</small>тъ бяше былъ, и зв<small>Ђ</small>зное хожение и число, земльную м<small>Ђ</small>ру и всяку мудрость. Посемъ же явися ему Богъ, в купин<small>Ђ</small> огньмь, и рече ему: &laquo;вид<small>Ђ</small>хъ б<small>Ђ</small>ду людий моихъ въ Егупт<small>Ђ</small>, и низъл<small>Ђ</small>зохъ изяти я отъ руку Егуретьску, изъвести я отъ земля тоя; ты же йди къ Фараону царю Егупетъску, и речеши ему: пусти Израиля, да три дни положать требу Господу Богу; аще не послушаеть тебе царь Егупетъскый, побью и всими чюдесы моими&raquo;. И пришедъшю Моис<small>Ђ</small>ови, и не послуша его Фараонъ, и пусти Богъ 10 казней на Фараона: І р<small>Ђ</small>кы въ кровь, 2 жабы, 3 мьшице, 4 песья мухы, 5 смерть на скотъ, 6 прыщьеве горьци, 7 градъ, 8 прузи, 9 тма три дни 10 моръ въ челов<small>Ђ</small>ц<small>Ђ</small>хъ. Сего ради 10 казний бысть на нихъ, яко 10 м<small>Ђ</small>сяць топиша д<small>Ђ</small>ти жидовьскы. Егда же бысть моръ въ Егупте, и рече Фараонъ Моис<small>Ђ</small>ови [и] брату его Аарону: &laquo;отьидета въскор<small>Ђ</small>&raquo;. Моис<small>Ђ</small>й же събравъ люди Жидовьскыя, поиде отъ земля Егупетъскыя, и ведяще я Господь путемъ по пустыни къ Чермьному морю, и предъидяше предъ ними нощью столпъ огньнъ, а во дни облаченъ. Слышавь же Фараонъ, яко б<small>Ђ</small>жать людье, погна по нихъ и притисну я къ морю. Вид<small>Ђ</small>въше же людье Жидовстиі, възопиша на Моис<small>Ђ</small>я, ркуще: &laquo;почто изве[де]ны на смерть&raquo;? И възопи Моис<small>Ђ</small>й къ Богу, и рече Господь: &laquo;что вопиеши къ мн<small>Ђ</small>? удари жезломъ в море&raquo;. И створи Моис<small>Ђ</small>й тако, и раступися вода на двое, и внидоша сынове Израилеви в море. Вид<small>Ђ</small>въ же Фараонъ, гна по нихъ; сынове же Израилеви проидоша по суху; яко изл<small>Ђ</small>зоша на берегъ, и съступися море о Фараон<small>Ђ</small> и о воихъ его. И възлюби Богъ Израиля, и идоша отъ моря три дни по пустыни, и придоша въ Мерру; и б<small>Ђ</small> ту вода горка, и възропташа людье на Бога, и показа имъ [Господь] древо, и вложи е Моис<small>Ђ</small>й въ воду, и осладишася воды. Посемъ же пакы възропташа людье на Моис<small>Ђ</small>я и на Арона, ркущи: &laquo;луче ны бяше въ Егупт<small>Ђ</small>, еже ядохомъ мяса, и тукъ, и хл<small>Ђ</small>бъ до сытости&raquo;. И рече Господь Богъ Моис<small>Ђ</small>ови: &laquo;слышахъ хулнаніе сыновъ Израилевыхъ&raquo;; и дасть има манну ясти. Посемъ же дасть имъ законъ на гор<small>Ђ</small> Синайстий. И Моис<small>Ђ</small>ови въшедъшю на гору къ Богу, они же съльявше тельчью главу, поклонишася, аки Богу, ихъже Моис<small>Ђ</small>и исъс<small>Ђ</small>че 3000 числомъ. Посемъ же пакы възропташа на Моис<small>Ђ</small>я и на Арона, еже не б<small>Ђ</small> воды, и рече Господь Моисеови: &laquo;удари жезломъ, в камень&raquo;.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&laquo;Напочатку, у перший день, Бог створив небо і землю. На другий день створив твердь посеред води. У цей же день розділив води: половина їх зійшла на твердь, а половина - під твердь. На третій день сотворив море, ріки, джерела і сімена. На четвертий - сонце, і місяць, і зорі,- і прикрасив бог небо. Побачив це перший серед ангелів - старійшина чину ангельського, подумав і сказав: &laquo;Зійду на землю, і заволодію нею, і поставлю престол свій на хмарах північних, і буду подібний богу&raquo;. І відразу ж був скинутий з небес, а за ним впали ті, що були під його началом - чин десятий. Ім\'я було ворогу Сотанаїл. А на його місце поставив Бог старійшину Михаїла. Сатана ж, согрішивши в помислах своїх і втративши первісну славу, назвався ворогом Богу. Після цього на п\'ятий день створив Бог китів, і гадів, і риб, і птиць пернатих, і звірів, і худобу, і гадів земних. На шостий день створив Бог чоловіка. На сьомий день відпочивав Бог від трудів своїх, була це субота. І насадив Бог рай на сході в Едемі, і ввів Бог туди чоловіка, якого створив, і заповідав йому від всякого дерева їсти плоди, від одного ж дерева, що дає пізнання зла і добра,- не їсти. І був Адам в раю, і бачив і славив Бога. Коли ангели славили Бога,- і він з ними. І навів Бог на Адама сон, і заснув Адам, і взяв Бог одно ребро в Адама, і створив йому жону, і привів її до Адама. І сказав Адам: &laquo;Це кість від кості моєї і плоть від плоті моєї, отож буде називатися жоною&raquo;. І назвав Адам імена всім скотам, і птицям, і звірам, і гадам, і самим ангелам повідав імена. І підкорив бог Адаму звірів і худобу, і володів всіма, і слухали його. Побачив диявол, як пошанував Бог чоловіка, позаздрив йому; перекинувся в змію, прийшов до Євзі і рече їй: &laquo;Чому не їсте з дерева, що посеред раю?&raquo; І каже жона змії: &laquo;Бог сказав: не їжте, а то помрете смертю&raquo;. І рече змія жоні: &laquo;Смертю не помрете, бо відає Бог, що в той день, коли ви з\'їсте з цього дерева, розкриються очі ваші і будете як Бог, розуміючи добро і зло&raquo;. І побачила жона, що дерево їстівне, і, взявши, з\'їла, і дала мужу своєму, і їли обоє. І розкрилися очі їм, і зрозуміли, що голі, і пошили із листя фігового дерева підв\'язки. І сказав Бог: &laquo;Проклята земля за діла твої, в скорботі їстимеш всі дні життя твого&raquo;. І ще сказав Господь Бог: &laquo;Коли простягнете руку і візьмете від дерева життя,- будете жить вічно&raquo;. Вигнав Господь Бог Адама з раю. І поселився він напроти раю, нарікаючи і обробляючи землю. І радувався Сатана, що проклято землю: це їхнє перше падіння і гірка розплата, позбавлення ангельського життя. І народив Адам Каїна і Авеля, і був Каїн хліборобом, а Авель - пастухом. Приніс Каїн від плодів земних жертву Богу - і не прийняв Бог дарів його. А Авель приніс від ягниці первістка. І прийняв Бог дари Авеля. Сатана ж уліз в Каїна і став підбивать його на вбивство Авеля. І сказав Каїн Авелю: &laquo;Підемо в поле&raquo;. І коли прийшли, піднявся Каїн, і хотів убити його, і не вмів убити. І сказав йому Сатана: &laquo;Візьми камінь і вдар його&raquo;. І убив Авеля. І рече Бог Каїну: &laquo;Де брат твій?&raquo; Він же відповів: &laquo;Чи я сторож брата свого?&raquo; І сказав Бог: &laquo;Кров брата твого голосить до мене, будеш стогнать і трястися до кінця життя свого&raquo;. Адам же і Євга в плачі билися, а диявол радів, кажучи: &laquo;Цього Бог пошанував, я зробив йому, аби відпав від Бога. І це нині плач йому наслав&raquo;. І оплакували Авеля тридцять років, і не згнило тіло його, і не вміли поховати його. І повелінням божим прилетіло двоє пташенят, одне з них вмерло, а друге викопало яму, поклало померлого і похоронило. Побачили це Адам і Євга, викопали Авелю яму, і поклали його, і похоронили його з плачем. Коли було Адамові двісті тридцять років, народив він Сифа і двох дочок. І взяв одну Каїн, а другу - Сиф. І від них люди розплодилися на землі, і забули того, хто створив їх, сповнилися блуду, і всілякої скнарості, і вбивств, і заздрощів, і жили люди по-скотськи. І був Ной один праведний в роді людському. І народив трьох синів: Сима, Хама і Афета. І сказав Бог: &laquo;Не буде дух мій в людях&raquo;, і сказав: &laquo;Вигублю людей, і все - від людини і до скотини, яких я створив&raquo;. І сказав Бог Ноєві: &laquo;Зроби ковчег довжиною триста ліктів, а в ширину п\'ятдесят, і в висоту тридцять ліктів&raquo;; у Єгипті-бо ліктем сажень називають. Зробив Ной ковчег за сто років і коли повідав, що буде потоп,- посміялися з нього люди. І коли зробив ковчег, сказав Господь Бог Ноєві: &laquo;Ввійди ти, і жона твоя, і сини твої, і невістки твої; і введи їх до себе, по двоє від всіх гадів&raquo;. І ввів Ной, як то заповідав йому Бог. І навів Бог потоп на землю, і потонуло все живе, а ковчег плавав на воді. Коли ж спала вода, вийшов Ной, і сини його, і жона його. І від них заселилася земля. І було людей багато, і всі були одномовні, і сказали один другому: &laquo;Збудуємо стовп до небес&raquo;. І почали будувати, і був у них старійшина - Неврод. І сказав Бог: &laquo;Розмножилися люди, і стали помисли їхні марнотні&raquo;. І зійшов Бог і розділив мову їхню на сімдесят і дві мови. Адамова ж мова була залишена у Авера, який один тільки не пристав до їхньої безглуздої справи, кажучи таке: &laquo;Якби Бог сказав людям до неба стовп робити, то звелів би сам своїм словом - так само як створив небеса, і землю, і море, і все видиме і невидиме&raquo;. Через те його мова не змінилася, від нього пішли євреї. На сімдесят і одну мову розділилися люди і розійшлися по країнах, кожний свій звичай прийняв, і за диявольським напученням жертви приносили гаям, колодязям і рікам,- і не пізнали Бога. Від Адама і до потопу літ дві тисячі двісті сорок два, а від потопу і до поділу мов - літ п\'ятсот двадцять дев\'ять. Після цього ж диявол у ще більші спокуси ввергнув людей, і почали вони кумирів творить: одні дерев\'яні і мідні, а другі - мармурові, золоті і срібні. І кланялися їм, і приводили до них своїх синів і дочок, і заколювали перед ними, і була осквернена вся земля. І започаткував ідолопоклонство Серух, створював ідолів на честь померлих людей, колишнім царям, другі - хоробрим, і волхвам, і жонам-блудницям. Оцей Серух народив Фару, а Фара народив трьох синів: Аврама, Нахора і Арана. Фара ж робив ідолів, навчившись у батька свого. Аврам же, добравши розуму, подивився на небо і сказав: &laquo;Воістину то Бог, що створив небо і землю, а отець мій спокушає людей&raquo;. І сказав Аврам: &laquo;Випробую богів батька мого&raquo;. І рече: &laquo;Батьку! Обдурюєш людей, створюючи дерев\'яних кумирів. То Бог, що створив небо і землю&raquo;. І взяв Аврам огонь, підпалив ідолів у храмі. Побачив; же це Аран, брат Аврамів, і, маючи ревність до ідолів, хотів винести їх, але сам згорів тут і помер раніше за батька. До цього не вмирав син раніше батька, але батько поперед сина. І з тих пір почали помирати сини раніше батька. І возлюбив Бог Аврама і сказав йому: &laquo;Вийди з дому батька свого і піди в землю, яку я тобі покажу, і народом великим тебе я створю, і благословляться в тобі всі племена землі!&raquo; І зробив Аврам, як те заповідав йому Бог. І взяв Аврам Лота, свого небожа. І був йому Лот шурином і небожем, бо Аврам взяв був за жону собі дочку брата Арана - Сару. І прийшов [Аврам] в землю Хананійську до дуба високого. І сказав Бог Аврамові: &laquo;Нащадкам твоїм дам землю цю&raquo;. І поклонився Аврам Богу. Аврам же май сімдесят літ, коли вийшов із Хараона. Сара ж була неплідна, хворість безплідності мала. І сказала Сара Авраму: &laquo;Ввійди до раби моєї&raquo;. І взяла Сара Агар і дала її мужеві своєму. І увійшов Аврам до Агари - і вона зачала, і народила сина. І назвав його Аврам Ізмаїлом. Аврамові ж було літ вісімдесят шість, коли народився Ізмаїл. Після цього зачала Сара, народила сина і дала ім\'я йому Ісак. І звелів Бог Авраму звершить обрізання отрочаті, і обрізав Аврам [Ісака] на восьмий день. І возлюбив Бог Аврама і плем\'я його, і назвав його своїм народом, а йоли назвав своїм народом, то відокремив його від других народів. І возмужав Ісак. Аврам же жив сто сімдесят п\'ять літ, і помер, і був похоронений. Коли Ісаку було 60 років, народив двох синів - Ісава і Якова. Ісав був лукавий, а Яків - правдивий. Оцей Яків сім літ працював у дядька свого із-за дочки його меншої, і не віддав її за нього дядько його Лаван, а сказав: &laquo;Візьми старшу&raquo;. І віддав Якову старшу Лію, а за другу сказав йому працювать ще сім літ. Він же заради другої, Рахілі, працював сім літ - і взяв двох сестер, від них же народив вісім синів: Рувима, Семеона, Левгію, Іуду, Ісахара і Заулона, Иосифа і Веніаміна. І від двох рабинь - Дана, Нефталіма, Гада, Асира - і від цих розплодились жиди. Яків же, коли йому було сто тридцять років, відправився з родом своїм, в кількості шістдесят п\'ять душ, в Єгипет. Пожив у Єгипті сімнадцять літ і помер, а плем\'я його перебувало в рабстві чотириста років. По цих літах спромігся люд жидівський зміцніти і розмножитися, а єгиптяни гнобили їх роботою. У ці часи народився у жидів Мойсей. І повідали волхви єгипетські цареві, що у жидів народилось дитя, яке хоче погубить Єгипет. Негайно повелів цар всіх жидівських дітей, що народжуються, кидати в ріку. Мойсеєва ж мати злякалася цього винищення, взяла дитя, поклала в корзинку і, віднісши, поставила в лузі. У те врем\'я ішла дочка фараона Фермуфі купатись і побачила отроча, яке плакало, і взяла його, і пощадила його, і дала ім\'я йому - Мойсей, і вигодувала його. І було дитя красне, і, як сповнилось йому чотири роки, привела його дочка фараона до батька свого. Коли фараон побачив Мойсея, то сподобав дитя. Мойсей же, хапаючись за шию, скинув царський вінець з голови і наступив на нього. Волхв, який бачив це, сказав цареві: &laquo;О царю! Убий це отроча. А коли ти його не погубиш, має погубити весь Єгипет&raquo;. І не послухав його цар, а ще й звелів не вбивати більше дітей жидівських. Мойсей змужнів і став впливовим у домі фараона. І коли прийшов інший цар, стали заздрити Мойсеєві бояри. Мойсей же, убивши єгиптянина, втік із Єгипту. І прийшов у землю Мадіамську, і, блукаючи пустинею, довідався від ангела Гавриїла про буття всього світу, і про першого чоловіка, і про те, що було після нього, і після потопу, і про змішання мов, і хто скільки років жив, і про рух зірок і кількість їх, і про земельну міру, і всіляку премудрість. Після цього з\'явився йому Бог у палаючій купині і сказав йому: &laquo;Бачив біду людей моїх у Єгипті і зійшов звільнити їх з-під руки єгипетської, вивести їх із землі тієї. Ти ж іди до фараона, царя єгипетського, і скажи йому: &laquo;Пусти Ізраїль, хай три дні приносять жертву Господу Богу. Якщо ж не послухає тебе цар єгипетський, поб\'ю його всіма чудесами моїми&raquo;. І прийшов Мойсей, і не послухав його фараон, і напустив Бог десять покарань на фараона: 1) криваві ріки, 2) жаби, 3) миші, 4) песі мухи, 5) мор худоби, 6) гнійники, 7) град, 8) саранчу, 9) триденну тьму, 10) мор на людей. А тому десять покарань впало на них, що десять місяців топили дітей жидівських. Коли ж почався мор в Єгипті, сказав фараон Мойсеєві і братові його Аарону: &laquo;Відійдіть якнайшвидше!&raquo; Мойсей же зібрав люд жидівський і пішов із землі Єгипетської. І вів їх Господь через пустині до Червоного моря, а поперед них ішов вогненний стовп - вночі, а вдень - хмарний. Почув фараон, що тікають люди, і погнався за ними, і притиснув їх до моря. Побачив це люд жидівський і заремствував на Мойсея, кажучи: &laquo;Нащо повів нас на смерть?&raquo; І возремствував Мойсей до Бога, і сказав Господь: &laquo;Чого гукаєш до мене? Удар жезлом по морю&raquo;. І зробив Мойсей так - і розступилася вода надвоє, і ввійшли сини Ізраїля у море. Побачив це фараон і погнався за ними. Сини ж Ізраїля перейшли море по сухому, і, як тільки вийшли на берег,- зійшлося море над фараоном і воїнами його. І возлюбив Бог [синів] Ізраїля, і йшли вони від моря три дні пустинею, і прийшли в Мерру. І була тут вода гірка, і заремствували люди на Бога, і показав їм Господь дерево, і поклав його Мойсей в воду, і посолодшала вода. Після цього знову ремствували люди на Мойсея і на Аарона, кажучи: &laquo;Лучче нам було у Єгипті, де ми їли м\'ясо, цибулю і хліб досхочу&raquo;. І сказав Господь Бог Мойсеєві: &laquo;Чув нарікання синів Ізраїлевих&raquo;,- і дав їм манну їсти. Після цього дав їм закон на горі Сінайській. І коли Мойсей вийшов на гору до Бога, вони ж відлили голову тельця і поклонилися їй, як Богу. І знищив Мойсей [за це] три тисячі людей. А потім знову нарікали на Мойсея і на Аарона, що не було води. І сказав Господь Мойсеєві: &laquo;Удар жезлом в камінь&raquo;.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Рекъ: &laquo;исъ сего камени егда не испустив<small>Ђ</small> воды&raquo;? И разгн<small>Ђ</small>вася Господь на Моис<small>Ђ</small>я, яко не възвеличи Господа, и не вниде в землю об<small>Ђ</small>тованую сего ради, роптанья он<small>Ђ</small>хъ ради; но възведе и на гору Вамьску и показа ему землю об<small>Ђ</small>тованую, и умре Моис<small>Ђ</small>й ту на гор<small>Ђ</small>. И прия власть Ісусъ Навгинъ; сий приде въ землю об<small>Ђ</small>тованую, и изби Хананейско племя, и всели в нихъ м<small>Ђ</small>сто сыны Израилевы. Умерьшю же Ісусу, бысть судья въ него м<small>Ђ</small>сто Июда; ин<small>Ђ</small>хъ судий бысть 14, при нихъже забывше Бога, изъведъшаго я изъ Егупта, начаша служити б<small>Ђ</small>сомъ. И разгн<small>Ђ</small>вася Богъ, предаяшеть я иноплеменьникомъ на расхыщение, и егда ся начнуть каяти, помиловашеть ихъ; и пакы укланяхуся на б<small>Ђ</small>сослужение. По сихъ же служаше Илий жрець, и посемъ Самуилъ пророкъ. Р<small>Ђ</small>ша людье Самуилу: &laquo;постави намъ царя&raquo;. И разъгн<small>Ђ</small>вася Богъ на Израиля, и постави надъ ними царя Саула. Таче Саулъ не изволи ходити въ зав<small>Ђ</small>т<small>Ђ</small> Господни, избра Господь Давида, постави и царя надъ Израилемъ, и угоди Давидъ Богу. Сему Давиду кляся Богъ, яко отъ племени его родитися Богу. И п<small>Ђ</small>рвое начаша пророчьствовати о воплощении Божии, рекъ: изъ щрева преже деньница родихъ тя. Се же пророчьствовавъ 40 л<small>Ђ</small>тъ, и умре. И по немъ царствова, пророчьствова сынъ его Соломонъ, иже възгради церковь Богови и нарече ю Святая Святыхъ; и бысть мудръ, но наконецъ поползеся; царствовавъ л<small>Ђ</small>тъ 40 и умре. По Соломон<small>Ђ</small> же царьствовавъ сынъ Ровоамъ; при семъ разд<small>Ђ</small>лися царство на двое, Жидовьское въ Ерусалим<small>Ђ</small> одино, а другое въ Сурии. Въ Самар<small>Ђ</small>и же царствова Еровамъ, холопъ Соломонь, иже створи дв<small>Ђ</small> крав<small>Ђ</small> злат<small>Ђ</small>, и постави едину вь Вефили на холм<small>Ђ</small>, а другую въ Ендан<small>Ђ</small>, и рекъ: &laquo;се бога твоя, Израилю&raquo;; и кланяхуся людье, а Бога забыша. Таче и въ Иерусалим<small>Ђ</small> [начаша] забывати Бога [и] начаша кланятися Валу, рекъше ратьну богу, еже есть Ер<small>Ђ</small>й, и забыша Бога отець своихъ. И нача Богъ посылати к нимъ пророкы; пророцы же начаша обличати о безаконьи ихъ и о служеньи кумиръ, они же начаша пророкы избивати, обличаеми отъ нихъ. И разгн<small>Ђ</small>вася Богъ на Израиля велми, и рече: &laquo;отрину [я] отъ себе и призову ины люди, иже мене послушаютъ; и аще съгр<small>Ђ</small>шать, не помяну съгр<small>Ђ</small>шения ихъ&raquo;. И нача посылати пророкы, глаголя: &laquo;прор<small>Ђ</small>чайте о отв<small>Ђ</small>ржении жидовьств<small>Ђ</small> и о призваньи странъ&raquo;. П<small>Ђ</small>рвое же начаша пророчьствовати Осий, глаголя: престану царство дому Израилева, и скрушю лукъ Израилевъ, и не приложю пакы помиловати дому Израилева, но отм<small>Ђ</small>таю отв<small>Ђ</small>ргуся ихъ, глаголеть Господь, и будуть блудяще въ языцехъ. Иерем<small>Ђ</small>я же рече: аще станеть Самуилъ и Моис<small>Ђ</small>й, не помилую ихъ. И паки той же Ерем<small>Ђ</small>я рече: тако глаголеть Господь: се кляхся именемъ моимъ великымъ, аще будеть отсел<small>Ђ</small> кд<small>Ђ</small> имя мое именуемо въ уст<small>Ђ</small>хъ Июд<small>Ђ</small>йскыхъ. Иезек<small>Ђ</small>ль же рече: тако глаголеть Господь Аданаи: расъс<small>Ђ</small>ю вы вся останки твоя въ вся в<small>Ђ</small>тры, зане святая моя оскв<small>Ђ</small>рнависте вьсими негодован[м]и твоими; азъ же тя отрину и не имамъ тя помиловати пакы. Малахия же рече: тако глаголеть Господь: уже н<small>Ђ</small>сть ми хот<small>Ђ</small>нья въ васъ, понеже отъ въстока и до запада имя мое прославися въ языц<small>Ђ</small>хъ, и на всякомъ м<small>Ђ</small>ст<small>Ђ</small> приносится кадило имени моего и жертва чиста, зане велье имя мое во языц<small>Ђ</small>хъ; сего ради дамъ васъ на поносъ и на пришествие въ вся языки. Исая великый рече: тако глаголеть Господь: простру руку свою на тя, истьлю тя и рас<small>Ђ</small>ю тя, и не приведу тя. И пакы и тъ же рече: възненавид<small>Ђ</small>хъ праздникы ваша и начатки м<small>Ђ</small>сяць вашихъ [и суботъ вашихъ] не приемлю. Амосъ же пророкъ рече: слышите слово Господне: азъ приемлю на вы плачь; домъ Израилевъ падеся и не приложи въстати. Малахия же рече: тако глаголеть Господь: послю на вы клятву и прокл<small>Ђ</small>ну благословение ваше, и разорю, и не будеть въ васъ. И много пророчьствоваша о отв<small>Ђ</small>ржении ихъ. Симъ же пророкомъ повел<small>Ђ</small> Богъ пророчьствовати о призваньи ин<small>Ђ</small>хъ странъ в нихъ м<small>Ђ</small>сто. Нача звати Исая, тако глаголя: яко законъ отъ мене изидеть, и судъ мой св<small>Ђ</small>тъ странамъ; приближается скоро правда моя, изидеть, и на мышцю мою страны уповаютъ.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Спитав [Мойсей]: &laquo;А що, коли із цього каменя не потече вода?&raquo; І розгнівався Господь на Мойсея, що не возвеличив Господа і не ввійде у землю обітовану через сумніви ці і через нарікання. Але вивів його на гору Вамську і показав йому землю обітовану, і помер Мойсей тут на горі. І прийняв владу Ісус Навгін. Цей прийщов у землю обітовану, і вибив хананейське плем\'я, і поселив замість них синів Ізраїлевих. Коли ж помер Ісус, замість нього став суддя Іуда, а інших суддів було чотирнадцять. При них же забули Бога, який вивів їх із Єгипту, і почали служити бісам. І розгнівався Бог, і віддав їх іноплемінникам на розграбування. І коли почали каятися,- помилував їх. А вони знову схилялися до бісослужіння. Після цих служив Ілій-жрець,- а після нього Самуїл-пророк. Сказав люд Самуїлу: &laquo;Постав нам царя&raquo;. І розгнівався Бог на [синів] Ізраїля і поставив над ними царя Саула. Одначе Саул не побажав підкоритися законові господньому, і вибрав Господь Давида, і поставив його царем над Ізраїлем. І угодив Давид Богу. Цьому Давидові обіцяв Бог, що із його племені народиться Бог. І перший почав пророкувати про втілення Боже, кажучи: &laquo;Із чрева раніше зірки вранішньої народив тебе&raquo;. От це пророкував він сорок літ і помер. Після нього царював і пророкував син його Соломон, який воздвиг церкву Богові і назвав її &laquo;святая святих&raquo;. І був мудрий, але насамкінець согрішив. Царював сорок літ і помер. Після Соломона ж царював син його Ровоам. При ньому розділилося царство надвоє: одне жидівське в Ієрусалимі, а друге - в Сурії. У Самарії ж царював Єровам, холоп Соломонів, який створив дві корови золоті, і поставив одну в Вефілі на горі, а другу в Єндані, і сказав: &laquo;Це боги твої, Ізраїлю&raquo;. І поклонялися люди, а Бога забули. Так само і в Ієрусалимі почали забувати Бога і почали поклонятися Валу, так називали бога війни,- це той же Ерій. І забули Бога предків своїх. І почав Бог посилати до них пророків, а пророки ж почали викривати їхнє беззаконня і служіння кумирам. Вони ж вчиняли побиття пророків за їхні викриття. І розгнівався вельми Бог на [синів] Ізраїля, і сказав: &laquo;Одвергну вас від себе і прикличу інший люд, що слухатиме мене, а якщо і согрішать - не пом\'яну гріхів їхніх&raquo;. І почав посилати пророків, кажучи: &laquo;Пророкуйте про отвергнення жидів і про покликання [нових] народів&raquo;. Найпершим став пророкувати Осія, кажучи: &laquo;Покладу кінець царству дома Ізраїлевого і сокрушу лук Ізраїлів, але, відмітаючи, отвергнуся їх, - говорить Господь.- І будуть тинятися між народами&raquo;. Ієремія ж сказав: &laquo;Коли встануть Самуїл і Мойсей,- не помилую їх&raquo;. І знову той же Ієремія говорить: &laquo;Так говорить Господь: &laquo;Оце поклявся іменем моїм великим, що не буде ім\'я моє вимовлятися устами іудейськими&raquo;. Ієзекіль же сказав: &laquo;Так говорить Господь Адонаї: &laquo;Розсію вас і всі рештки ваші - на всі вітри, за те, що осквернили святилище моє всіма огудами своїми; я ж відкинусь від тебе і не помилую тебе знову&raquo;. Малахія ж сказав: &laquo;Так говорить Господь: &laquo;Уже нема мого благовоління до вас, оскільки від сходу і до заходу ім\'я моє прославиться в народах, і на всякому місці будуть курити фіміам моєму імені, і приносити жертву чисту, тому що славне ім\'я моє між народами. Через це віддам вас на хулу і на розпорошення серед всіх народів&raquo;. Ісая великий сказав: &laquo;Так говорить Господь: &laquo;Підійму руку свою на тебе, згною і розсію тебе і більше не зберу тебе&raquo;. І ще той же пророк сказав: &laquo;Возненавидів празники ваші, і новомісячні ваші, і субот ваших не приємлю&raquo;. Амос же пророк сказав: &laquo;Слухайте слово Господнє: &laquo;Я здійму плач за вами. Дім Ізраїлів упав і не зможе більше встати&raquo;. Малахія ж сказав: &laquo;Так говорить Господь:.&laquo;Пошлю на вас прокляття, і прокляну благословення ваше, і зруйную, і не буде з вами&raquo;. І багато пророкували про отвергнення їх Цим же пророкам повелів Бог пророкувати про прикликання інших народів замість них. І став волати Ісая, так кажучи: &laquo;Закон від мене постане, і суд мій - світло народам. Скоро настане правда моя, зійде, і на силу мою народи уповають&raquo;.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Иерем<small>Ђ</small>я же рече: тако глаголеть Господь: и положю дому Июдову зав<small>Ђ</small>тъ новъ, дая законы в разум<small>Ђ</small>нья ихъ, и на сердца ихъ напишю, и буду имъ въ Богъ, и ти будуть мьн<small>Ђ</small> въ люди. Исая же рече: ветхая мимо идоша, а новая възв<small>Ђ</small>щаю, и прежъ възв<small>Ђ</small>щения явлено бысть вамъ; пойте Господеви п<small>Ђ</small>снь нову; работающимъ ми призоветь имя ново, еже благословится имя [по] всей земли; домъ мой домъ молитв<small>Ђ</small> прозовется по вс<small>Ђ</small>мъ языкомъ. Той же Исая глаголеть: открыеть Господь мышьцю свою святую предъ вс<small>Ђ</small>мі языкы, узрять вси конци земля спасение Бога нашего. Давидъ же: хвалите Господа вси языци, похвалите его вьси людье. Тако -Богу възлюбившю новыя люди, рекъ имъ, сънити к нимъ самъ, и явитися челов<small>Ђ</small>екомъ плотью, и пострадати за Адамово преступление. И начаша пророчьствовати о воплощении Божии; и п<small>Ђ</small>рвое Давидъ, глаголя: рече Господь Господеви моему: сяди одесную мене, дондеже положю врагы твоя подъ ножье ногама твоима. И пакы: рече Господь къ мн<small>Ђ</small>: сынъ мой еси ты, азъ днесь родихъ тя. Исая же рече: не солъ, ни в<small>Ђ</small>стникъ, но самъ Господь пришедъ спасеть ны. И пакы: яко д<small>Ђ</small>тищь родися намъ, емуже бысть начало на рам<small>Ђ</small> его, и прозовется имя его Велика Св<small>Ђ</small>та Ангелъ, и велика власть его и миру его н<small>Ђ</small>сть конца. И пакы: се въ утроб<small>Ђ</small> Д<small>Ђ</small>вая зачать, и родить сынъ, и прозовуть имя ему Еммануилъ. Мих<small>Ђ</small>я же рече: ты Виолеоме, доме Ефрантовъ, еда не моглъ еси быти в тысящахъ Июдовахъ? ис тебе бо ми изидетъ стар<small>Ђ</small>йшина быти князь ихъ въ Израили, исходъ его отъ дний в<small>Ђ</small>ка; сего ради дасться до времени ражающая, родить и прочии отъ братья его обратятся на сыны Израилевы. Иеремия же рече: се Богъ нашъ, и не въм<small>Ђ</small>нится инъ к нему: изъобр<small>Ђ</small>те вьсякъ путь художьства, яко дасть Иякову отроку своему; по сихъ же на земли явися и съ челов<small>Ђ</small>кы поживе. И пакы: челов<small>Ђ</small>къ есть; и кто ув<small>Ђ</small>сть, яко Богъ есть? яко челов<small>Ђ</small>къ же умираеть. Захарья же рече: не послушаша сына моего, и не услышю ихъ, глаголеть Господь. Иос<small>Ђ</small>й рече: тако глаголеть Господь: плоть моя отъ нихъ. Проркоша же и о страсти его ркуще; якоже рече Исая: о лют<small>Ђ</small> души ихъ, понеже св<small>Ђ</small>тъ золъ св<small>Ђ</small>щаша, ркуще, свяжемъ праведника. И пакы той же рече: тако глаголеть Господь: азъ не супротивлюся, ни противлюся, ни глаголю противу; плещи мои дахъ на раны, и ланит<small>Ђ</small> мои на заушение, и лица своего не отвратихъ отъ стыд<small>Ђ</small>нья. Еремия же рече: приидите, въложимъ древо въ хл<small>Ђ</small>бъ его, изътребимъ отъ земля животъ его. Моис<small>Ђ</small>й же рече о распятьи его: узрите жизнь вашю висящю предъ очима вашима. И Давидъ рече: въскую шаташася языци. Исая же рече: яко овьча на заколенье веденъ бысть. Ездра же рече: благословенъ Богъ, распростеръ руц<small>Ђ</small> свои, и спаслъ Иерусалима. И о въскресении же его рькоша. Давидъ: въстани, Боже, суди земли, яко ты насл<small>Ђ</small>диши въ вс<small>Ђ</small>хъ странахъ. И пакы: да въскреснеть Богъ, и разидутся врази его. И пакы: въскресни, Господи Боже мой, да възнесеться рука твоя. Исая же рече: сходящии въ страну и с<small>Ђ</small>нь смертьную, св<small>Ђ</small>ть восияеть на вы. Захарья же рече: и ты въ крови зав<small>Ђ</small>та твоего испустилъ еси ужикы своя отъ рова, не имущи воды. И ино много пророчьствова о немъ же, и сбытся все&raquo;. Рече же Володимиръ: &laquo;то въ кое время събыться се, и было ли се есть? егда ли топ<small>Ђ</small>рво хощеть быти се?&raquo; И философь же отв<small>Ђ</small>щавъ рече ему: &laquo;яко уже преже сьбысться все, егда Богъ въплотися. Якоже преже ркохъ, ЖидоМъ пророкы избивающимъ, царемъ ихъ законы преступающимъ, предасть я на расхыщение, въ пленъ и ведени быша въ Асурию, гр<small>Ђ</small>хъ ради ихъ, и работаша тамо л<small>Ђ</small>тъ 70; и по семъ възвратишася на землю свою не бъ у нихъ царя, но архиер<small>Ђ</small>и обладаху ими до Ирода иноплеменьника, иже облада ими. В сего же власть, в л<small>Ђ</small>то 5000 и 500, посланъ бысть Гаврилъ въ Назарефъ къ д<small>Ђ</small>вици Марии отъ кол<small>Ђ</small>на Давидова, рещи ей: Радуйся, обрадованная, Господь с тобою. И отъ слова сего зачатъ Слово Божие во утроб<small>Ђ</small>, и породи Сына, и нарече имя ему Ісусъ. И се волъстві приидоша отъ въстока, глаголюще: кд<small>Ђ</small> есть рожийся царь</p>\r\n<p>Жи [до] вескъ? вид<small>Ђ</small>хомъ зв<small>Ђ</small>зду его на въстоци, приидохомъ поклонится ему. Слышавъ же се Иродъ царь, смятеся и всь Иерусалимъ с нимъ; и призвавъ книжникы и старци людьскыя, въпрошаше: кде Христосъ ражается? Они же р<small>Ђ</small>ша ему: въ Вифлеом<small>Ђ</small> Июд<small>Ђ</small>йст<small>Ђ</small>мь. Иродъ же се слышавъ, посла, рекъ: изб<small>Ђ</small>йте младенца, сущая во Вефлиом<small>Ђ</small>, до дву л<small>Ђ</small>ту. О.ни же шедше, избиша младениць 14000. Марья же убоявшися, скры отроча; Иосифъ же съ Мариею, поимъ отроча, и б<small>Ђ</small>жа въ Егупетъ и бысть ту до умертвия Иродова. Въ Егупт<small>Ђ</small> же явися ангелъ Иосифу, глаголя: въстани, поими отроча и матерь его, и иди в землю Израилеву. Пришедъшю же ему, вселися въ Назарефъ. И възрастъшю же ему и бывшю ему л<small>Ђ</small>тъ 30, нача чюдеса творити и пропов<small>Ђ</small>дати царство небесное, изъбра 12, и яже ученикы себ<small>Ђ</small> нарече. И нача чюдеса творити велика, мертвыя въскрешати, прокаженыя очищати, хромыя ходити, сл<small>Ђ</small>пымъ прозр<small>Ђ</small>нье творити, и ина многа чюдеса велика, якоже б<small>Ђ</small>ша пророци прорекли о немъ, глаголюще: тъ недугы наша иц<small>Ђ</small>ли, и болезни подъя. И крестися въ Иердан<small>Ђ</small> отъ Ивана, показая новымъ людемъ обновление. Крестившю же ся ему, и се отв<small>Ђ</small>рзошася небеса, и Духъ сходящь зракомъ голубиномъ на нь, и гласъ глаголя: се есть Сынъ мой възлюбленый, о немъ же благоизволихъ. И посылаше ученикы своя пропов<small>Ђ</small>дати царство небесное, и покаяние въ оставленье гр<small>Ђ</small>ховъ, и хотя исполнити пророчьство, и нача пропов<small>Ђ</small>дати, яко подобаеть сыну челов<small>Ђ</small>чьскому пострадати, и распяту быти, и въ третий день въскреснути. И учащю ему въ церкви, архиереи исполнишася зависти, и книжници искаху убити и, и емъше, и ведоша и къ иг<small>Ђ</small>мону Пилату. Пилатъ же испытавъ, яко безъ вины предаша и, хот<small>Ђ</small> пустити и; они же р<small>Ђ</small>ша ему: аще того пустиши, не имаеши быти другъ кесареви, Пилатъ же повел<small>Ђ</small>, да и распнутъ. Они же поемъше Ісуса, ведоша и на м<small>Ђ</small>сто Краньево, и ту и распяша; и бысть тма по всей земли отъ шестаго часа до 9-го, и при 9-мь час<small>Ђ</small> испусти духъ Ісусъ, и церковная запона раздрася на двое, и мертвии въстаяху мьнози, имъже повел<small>Ђ</small> въ раи быти. И снемъше же и съ креста, положиша и въ гроб<small>Ђ</small> и печатьми запечаташа .гробъ, людье же Жидовьстии и стражи приставиша, ркуще: еда украдуть и нощью ученици его. Онъ же въ 3 день въскресе и явися ученикомъ; и въскресъ изъ мертвыхъ, рекъ имъ: идете въ вся языкы, и научите вся языкы, крестяще во имя Отца и Сына и Святаго Духа. И пребысть съ ними 40 дний, являяся имъ по въскресении. И егда исполнися 40 [дній], повел<small>Ђ</small> имъ ити на гору Елеоньскую, и ту явися имъ, и благословивъ я, рече имъ: сядете въ град<small>Ђ</small> Иерусалим<small>Ђ</small>, дондеже послю об<small>Ђ</small>тование Отца моего. И егда възношашеся на небо, ученикы поклонишася ему, и възъратишася въ Иерусалимъ, и бяху выну въ церкви. И егда скончася дний 50, сниде Духъ Святый на Апостолы; и приимъше об<small>Ђ</small>тование Святого Духа, разидошася по вьселен<small>Ђ</small>й, учаще и крестяще водою&raquo;.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Ієремія ж сказав: &laquo;Так говорить Господь: &laquo;І покладу дому Іуди завіт новий, даючи закони для їхнього розуміння, і в серцях їхніх напишу, і буду їм Богом, а вони будуть моїм народом&raquo;. Ісая ж сказав: &laquo;Старе мимо пройшло, а нове возвіщу, і до возвіщення воно буде явлено вам. Співайте Господу пісню нову&raquo;. &laquo;Рабам моїм буде дано нове ім\'я, що благословиться по всій землі. Дім мій назвуть домом молитви у всіх народів&raquo;. Той же Ісая говорить: &laquo;Відкриє Господь силу свою святую перед всіма народами, узрять у всіх кінцях землі спасіння Бога нашого&raquo;. Давид же [говорить]: &laquo;Славте Господа всі народи, прославляйте його всі люди&raquo;. Так возлюбив Бог нових людей і сказав їм, що зійде до них сам, з\'явиться в людській плоті, і стражданням спокутує Адамів гріх. І почали пророкувати про втілення боже, і найперший Давид, кажучи: &laquo;Сказав Господь Господу моєму: &laquo;Сядь праворуч мене, доки покладу ворогів твоїх у підніжжя ніг твоїх&raquo;. І ще: &laquo;Сказав мені Господь: &laquo;Ти син мій, я сьогодні народив тебе&raquo;. Ісая ж сказав: &laquo;Ні посол, ні вісник, але сам Господь прийде і спасе нас&raquo;. І ще: &laquo;Як дитя народиться нам, то йому ж нести владарювання на раменах своїх, і наречуть ім\'я його - Великого Світу Ангел. Велика влада його, і світу його нема кінця&raquo;. І ще: &laquo;Це Діва в утробі [своїй] зачне, і народить сина, і назвуть його ім\'ям Емануїл&raquo;. Міхей же рече: &laquo;Ти, Віфлеєме, доме Єфранта, хіба ти не міг бути серед тисяч Іудиних? Із тебе ж бо постане старійшина, який стане князем їхнім в Ізраїлі, і поява його від днів предковічних. Через це настановляє їх до часу, допоки не народить тих, які родять. І тоді інші брати його стануть синами Ізраїлю&raquo;. Ієремія ж каже: &laquo;Це Бог наш, і ніхто інший не зрівняється з ним: він знайшов всі шляхи премудрості, яку дарував отроку своєму Якову. Після цього він з\'явився на землі і жив серед людей&raquo;. І ще: &laquo;Людина він, і хто узнає, що він - Бог? Адже і помирає як людина&raquo;. Захарія ж каже: &laquo;Не послухали сина мого, а я не почую їх&raquo;,- говорить Господь. І Осія сказав: &laquo;Так говорить Господь: &laquo;Плоть моя від них&raquo;. Напророкували ж і страждання його, кажучи, як сказав Ісая: &laquo;О горе душі їхній! Бо злу раду дали вони, кажучи: зв\'яжемо праведника&raquo;. І ще той же пророк сказав: &laquo;Так говорить Господь: &laquo;Я ні опираюся, ні противлюся, ані заперечую. Плечі свої віддав на рани, а щоки мої для ляпаса, і лиця свого не відвернув від посоромлення і ганьблення&raquo;. Ієремія ж сказав: &laquo;Приходьте, покладемо дерево в хліб його і відторгнемо від землі життя його&raquo;. Мойсей же сказав про розп\'яття його: &laquo;Побачите життя своє, що висітиме перед очима вашими&raquo;. І Давид сказав: &laquo;Пощо метушаться народи?&raquo; Ісая ж сказав: &laquo;Його вели, як вівцю на заріз&raquo;. Єздра ж сказав: &laquo;Благословенний Бог, який розпростер руки свої і врятував Ієрусалим&raquo;.</p>\r\n<p>І про воскресіння ж його сказав Давид: &laquo;Востань, Боже, суди землю, бо ти успадковуєш всі народи&raquo;. І ще: &laquo;Хай воскресне Бог і розбіжаться вороги його&raquo;. І ще: &laquo;Воскресни, Господи Боже мій, хай вознесеться рука твоя&raquo;. Ісая ж сказав: &laquo;Зійшлі в країну тіні смертної, світло возсіяє на вас!&raquo; Захарія ж сказав: &laquo;І ти в крові заповіту свого відпустив в\'язнів своїх із рову, в якому нема води&raquo;.</p>\r\n<p>І багато ще було пророцтв про нього - і збулися всі&raquo;.</p>\r\n<p>Володимир же запитав: &laquo;То в яке врем\'я збулося це і чи все це було? Чи тільки тепер збудеться?&raquo; І філософ, відповідаючи, сказав йому: &laquo;Усе це вже раніше збулося, коли Бог втілився. Як я уже сказав, коли жиди пророків били, а їхні царі закони зневажали, віддані вони були на розграбування, в полон, виведені були в Асурію за гріхи свої і були в рабстві там сімдесят років, а потім повернулися на землю свою, і не було у них царя, а архієреї володіли ними до іноплемінника Ірода, що панував над ними. В час владарювання Ірода, в літо 5500-те, послано було [архангела] Гавриїла в Назарет до діви Марії, із коліна Давидового, сказати їй: &laquo;Радуйся, обрадувана, Господь з тобою!&raquo; І від слів цих зачала вона в утробі своїй слово боже, і народила сина, і дала ім\'я йому Ісус. І тут волхви прийшли зі сходу, питаючи: &laquo;Де тут народився цар жидівський? Бачили зірку його на сході і прийшли поклонитися йому&raquo;. Почув це цар Ірод, стурбувався, і весь Ієрусалим з ним. І закликав книжнкків і старійшин народу, питаючи: &laquo;Де народився Христос?&raquo; Вони ж відповіли йому: &laquo;У Віфліємі іудейському&raquo;. Ірод же, це почувши, послав з наказом: &laquo;Перебийте всіх Віфліємських дітей до двох років&raquo;. Вони ж пішли і перебили чотирнадцять тисяч дітей. А Марія, злякавшись, сховала дитя. Йосиф же з Марією взяли дитя і втекли в Єгипет і пробули там до смерті Ірода. У Єгипті ж Йосифу явився ангел і сказав. &laquo;Встань, візьми дитя і матір його і йди в землю Ізраїльську&raquo;. Повернувшись, поселилися в Назареті. І виріс Ісус, і коли було йому тридцять літ, почав він творити чуда і проповідувати царство небесне. Вибрав він дванадцять і назвав їх своїми учнями. І став творити чуда великі: мертвих воскрешати, від прокази очищати, кривих ізціляти, сліпих прозрівати і інших багато чудес великих, які пророки передбачили були, кажучи: &laquo;Ти недуги наші зцілив, і хвороби наші на себе взяв&raquo;. І охрещений був в Іордані Іваном [Хрестителем], показуючи новим людям оновлення. Коли його хрестили, отверзлися небеса, і Дух зійшов в подобі голубиній на нього, і голос сказав: &laquo;Це син мій возлюблений, до нього моє благовоління&raquo;. І послав він учнів своїх проповідувати царство небесне і покаяння на позбуття гріхів. І хотів здійснить пророцтво, і почав проповідувати, як подобає синові людському постраждати, і бути розп\'ятим, і на третій день воскреснути. І коли навчав у церкві, архієреї і книжники переповнились заздрістю, і книжники шукали привід убити його, і схопили, і повели до правителя Пілата. Пілат же дізнався, що привели його без вини, і хотів відпустити його. Вони ж сказали йому: &laquo;Якщо відпустиш цього, не будеш другом кесареві [Римському]&raquo;. Пілат же звелів, щоб його розп\'яли. Вони ж узяли Ісуса, повели його на Краньєве поле і тут розп\'яли його. І настала тьма по всій землі від шостої години до дев\'ятої. І о дев\'ятій годині віддав дух Ісус, і церковна запона роздерлася надвоє, встало багато мертвих, яким він звелів ввійти до раю. І зняли його з хреста, і положили його в гріб, і печатками запечатали гріб жиди, і сторожу приставили, кажучи: &laquo;Аби не вкрали вночі учні його&raquo;. Він же воскрес на третій день і з\'явився учням. І, воскресши із мертвих, сказав їм: &laquo;Ідіть до всіх народів і навчіть всі народи, охрещуючи їх во ім\'я Отця і Сина і Святого Духа&raquo;. І пробув з ними сорок днів, приходячи до них після воскресіння. І коли пройшло сорок днів, звелів їм іти на гору Елеонську. І тут явився їм, і благословив їх, і сказав: &laquo;Живіть в граді Ієрусалимі, доки не пошлю вам обітницю Отця мого&raquo;. І коли возносився на небо, учні поклонилися йому, і повернулися в Іерусалим, і завжди були в церкві. І коли минуло п\'ятдесят днів, зійшов Дух Святий на апостолів, і, прийнявши обітницю Святого Духа, розійшлись по вселенній, навчаючи і охрещуючи водою&raquo;.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Рече же Володимиръ къ философу: &laquo;что ради отъ жены родися, и на дьрев<small>Ђ</small> распятъся, и водою крестися?&raquo; Философъ же рече ему:</p>\r\n<p>&laquo;Сего ради, понеже исп<small>Ђ</small>рва родъ челов<small>Ђ</small>чьскый женою съгр<small>Ђ</small>ши, дьяволъ прельсти Евгою Адама и отпаде рая, такоже и Богъ отм<small>Ђ</small>стье створи дьяволу: женою п<small>Ђ</small>рв<small>Ђ</small>е поб<small>Ђ</small>женье бысть дьяволу, женою бо п<small>Ђ</small>рьв<small>Ђ</small>е испаденіе бысть Адаму изъ раа, отъ жены же пакы въплътися Богъ и повел<small>Ђ</small> в рай ити в<small>Ђ</small>рнымъ. А еже на древе распяту быти, сего ради, яко отъ древа вкушь, и испаде породы; Богъ же на древ<small>Ђ</small> страсть пріать, да древомъ діяволь поб<small>Ђ</small>женъ будеть, и отъ древа праведнаго пріимуть праведніи. А еже водою обновленіе; понеже преумножившимся гр<small>Ђ</small>хомъ в челов<small>Ђ</small>цехъ, и наведе Богъ потопъ на землю, и потопи челов<small>Ђ</small>кы водою. Сего ради рече Богъ: понеже погубихъ водою челов<small>Ђ</small>кы гр<small>Ђ</small>ха ради ихъ, нын<small>Ђ</small> же пакы водою очищу гр<small>Ђ</small>хы челов<small>Ђ</small>комъ, обновленіемъ водою. Ибо Жидовьскый родъ въ мори очистишася отъ Египетскаго злаго нрава, понеже вода изначала бысть пръвое; рече бо: и Духъ Божій ношашеся връху воды; иже бо и нын<small>Ђ</small> крестятся водою и духомъ. Преображеніе бысть пръвое водою, якоже Гедеонъ прообрази. Егда же пріиде к нему ангелъ, веляше ему ити на Мадіама; и искушаше и рече къ Богу, положивь руно на гумн<small>Ђ</small>, рекь: и аще будеть по всей земли роса, а на рун<small>Ђ</small> суша; и бысть тако. Се же прообрази, яко иностраны б<small>Ђ</small>ша преже суша, а Жидове руно, а на Жид<small>Ђ</small>хъ суша. И-пророци же пропов<small>Ђ</small>даша, яко водою обновленіе будеть. Апостоломъ же учащимъ по вселенней в<small>Ђ</small>ровати Богу, ихъже ученіе и мы Греци пріахомъ; и вся вселенная в<small>Ђ</small>руетъ ученію ихъ. Нарекль же есть Богъ единъ день, в онже хощетъ судити, пришедый, живымъ и мертвымъ, и въздати комуждо по д<small>Ђ</small>ломъ ихъ: праведному царство небесное, и красоту неизречекную, веселіе без конца, и не умирати в в<small>Ђ</small>кы, а гр<small>Ђ</small>шникомъ мука огненна, и червъ неусыпаемый, и Муц<small>Ђ</small> не будеть конца. Сице же будуть мученіа, иже не в<small>Ђ</small>руютъ Господу нашему Ісусу Христу, мучими будутъ въ огни, иже ся не креститъ&raquo;.</p>\r\n<p>И се рекь, показа ему запону, на нейже б<small>Ђ</small> написано судище Господне; показываше же ему одесную праведныа въ веселіи предъидущу в рай, а ошуюю гр<small>Ђ</small>шныа идущихъ въ муку. Вълодимеръ же въздохнувь рече: &laquo;добро симъ одесную, горе же симъ ошуюю&raquo;. Онъ же рече: &laquo;аще хощеши одесную стати, то крестися&raquo;. Вълодимеръ же положи на сердци своемъ, рекъ: &laquo;пожду еще мало&raquo;, хотя испытати о вс<small>Ђ</small>хъ в<small>Ђ</small>рахъ. Вълодимеръ же сему дары многы въдавь, отпусти съ честію великою.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Володимир же запитав філософа: &laquo;Чого ради від жінки народився, і на дереві був розп\'ятий, і водою охрестився?&raquo; Філософ же відповів йому:</p>\r\n<p>&laquo;Ось чому! Оскільки від початку рід людський согрішив жінкою: диявол спокусив Адама Євгою, і той позбувся раю. Так і бог помстився дияволу: через жінку спочатку переміг диявол, бо через жінку спочатку Адам вигнаний був із раю, через жінку і втілився Бог і наказав ввійти вірним до раю. А на дереві був розіп\'ятий через те, що від дерева вкусив Адам і з-за цього був вигнаний з раю. Бог же на дереві прийняв страждання, щоб деревом був переможений диявол, і від дерева життя матимуть спасіння праведні. А що водою сталося оновлення, то тому, що на людей, які умножили гріхи свої, наслав Бог на землю потоп і потопив людей водою. Через це сказав Бог: &laquo;Оскільки водою погубив я людей за гріхи їхні, то нині знову водою очищу людей від гріхів - водою оновлення&raquo;. Адже і жидівський рід в морі очистився від єгипетського злого норову, бо вода була створена першою, сказано бо: &laquo;І Дух Божий носився поверх води&raquo;, а тому і нині хрестяться водою і духом. І перше перетворення було зроблено водою. Його вчинив Гедеон. Коли прийшов до нього ангел і звелів йому іти до Медіама, [він же], випробовуючи, сказав Богові, поклавши руно на току: &laquo;Коли буде по всій землі роса, руно буде сухим&raquo;. І було так. Це ж був образ того, що всі інші країни були раніше сухими, а жиди - руно. І на жидах - суша. І пророки проповідували, що через воду прийде оновлення. Коли апостоли по всьому світові навчали вірити в Бога, їхнє учення і ми, греки, прийняли. І вся вселенна вірує в їхнє учення. Призначив же Бог один день, в який, зійшовши з небес, буде судити живих і мертвих і кожному воздасть за ділами його: праведному - царство небесне і красу несказанну, безкінечну веселість і безсмертя в віках, а грішникам - мука огненна, нескінченна гризота і муки без кінця. Такі будуть муки для тих, що не вірують Господу нашому Ісусу Христу, мучитимуться в огні, якщо не охрестяться&raquo;.</p>\r\n<p>І коли сказав це, [філософ] показав [Володимиру] запону, на якій було намальовано судилище господнє; показав же йому праведників справа, які в радощах прямують у рай, а зліва - грішників, що йдуть на муки. Володимир, зітхнувши, сказав: &laquo;Добре цим, справа, горе ж цим, зліва&raquo;. Він же сказав: &laquo;Коли хочеш одесную стать, то охрестися&raquo;. Володимир же поклав у серце своє [бажання] і сказав: &laquo;Почекаю ще трохи&raquo;. Хотів розвідати про всі віри. Володимир дав філософу дари великі і відпустив з честю великою.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&nbsp;</p>',NULL,1848,5,1,NULL,25);
INSERT INTO `publications_publication` VALUES
(4712,'P','2008-12-20',0,NULL,'Note','Повість врем\'яних літ Роки 6421 [913] — 6477 [969].','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В літо 6421 [913]. Почав княжити Ігор після Олега. У це ж врем\'я почав царювати Костянтин, Леонів син, Романів зять. І деревляни розпочали війну проти Ігоря після смерті Олега.</span></span></p>','<p><a href=\"http://izbornyk.org.ua/pvlyar/yar02.htm\">Роки 6421 [913] &mdash; 6477 [969].</a></p>\r\n<p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6421. Поча княжити Игорь по Ольз<small>Ђ</small>.</p>\r\n<p>В се же время поча царствовати Костянтинъ, сынъ Леонтовъ, зять Романовъ. И Деревлян<small>Ђ</small> заратишася отъ Игоря по Олгов<small>Ђ</small> см<small>Ђ</small>рти.</p>\r\n<p>В л<small>Ђ</small>то 6422. Иде Игорь на Древляны, и поб<small>Ђ</small>дивъ възложи на ня дань большю Ольговы.</p>\r\n<p>В то же л<small>Ђ</small>то приде Семеонъ Болгарьскый на Царьградъ, и створивъ миръ, иде въсвояси.</p>\r\n<p>В л<small>Ђ</small>то 6423. Приидоша Печен<small>Ђ</small>зи п<small>Ђ</small>рвое на Рускую землю и створивше миръ съ Игоремъ, идоша къ Дунаю.</p>\r\n<p>В си же времена приде Семеонъ пл<small>Ђ</small>няя Фракию; Гр<small>Ђ</small>ци же послаша по Печен<small>Ђ</small>гы. Печен<small>Ђ</small>гомъ же пришедъшимъ и хотящимъ на Семеона, расваришася Гр<small>Ђ</small>цкыя воеводы; вид<small>Ђ</small>въше Печен<small>Ђ</small>зи, яко сами на ся рать имуть, отъидоша въсвояси, а Болгаре съ Гр<small>Ђ</small>кы съступишася, и пос<small>Ђ</small>чени быша Гр<small>Ђ</small>ци. Семеонъ прия градъ Оньдьр<small>Ђ</small>янь, иже первое Орестовъ городъ нарицашеся, сына Агамемнонъ, иже п<small>Ђ</small>рвое въ трехъ р<small>Ђ</small>кахъ купався, недуга избы, ту сего града въ свое имя нарече. Посл<small>Ђ</small>д<small>Ђ</small> же Андр<small>Ђ</small>янъ кесарь обновивъ и въ свое имя нарече Андр<small>Ђ</small>янъ, мы же зовемъ Ондр<small>Ђ</small>янемъ градомъ.</p>\r\n<p>В л<small>Ђ</small>то 6424.</p>\r\n<p>В л<small>Ђ</small>то 6425.</p>\r\n<p>В л<small>Ђ</small>то 6426.</p>\r\n<p>В л<small>Ђ</small>то 6427.</p>\r\n<p>В л<small>Ђ</small>то 6428. Поставленъ Романъ царемъ въ Гр<small>Ђ</small>ц<small>Ђ</small>хъ. Игорь же воеваше на Печен<small>Ђ</small>гы.</p>\r\n<p>В л<small>Ђ</small>то 6429.</p>\r\n<p>В л<small>Ђ</small>то 6430.</p>\r\n<p>В л<small>Ђ</small>то 6431.</p>\r\n<p>В л<small>Ђ</small>то 6432.</p>\r\n<p>В л<small>Ђ</small>то 6433.</p>\r\n<p>В л<small>Ђ</small>то 6434.</p>\r\n<p>В л<small>Ђ</small>то 6435.</p>\r\n<p>В л<small>Ђ</small>то 6436.</p>\r\n<p>В л<small>Ђ</small>то 6437. Прииде Семеонъ на Царьградъ, и попл<small>Ђ</small>ни Фракию и Македонью, и приде къ Царюграду в сил<small>Ђ</small> в<small>Ђ</small>лиц<small>Ђ</small> и в горъдости, и створи миръ с Романомъ царемъ, и възвратися въсвояси.</p>\r\n<p>В л<small>Ђ</small>то 6438.</p>\r\n<p>В л<small>Ђ</small>то 6439.</p>\r\n<p>В л<small>Ђ</small>то 6440.</p>\r\n<p>В л<small>Ђ</small>то 6441.</p>\r\n<p>В л<small>Ђ</small>то 6442, П<small>Ђ</small>рвое придоша Угри на Царьградъ, и пленяху всю Фракию, Романъ же створи миръ со Угры.</p>\r\n<p>В л<small>Ђ</small>то 6443.</p>\r\n<p>В л<small>Ђ</small>то 6444.</p>\r\n<p>В л<small>Ђ</small>то 6445.</p>\r\n<p>В л<small>Ђ</small>то 6446.</p>\r\n<p>В л<small>Ђ</small>то 6447.</p>\r\n<p>В л<small>Ђ</small>то 6448.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6421 [913]. Почав княжити Ігор після Олега. У це ж врем\'я почав царювати Костянтин, Леонів син, Романів зять. І деревляни розпочали війну проти Ігоря після смерті Олега.</p>\r\n<p>В літо 6422 [914]. Ходив Ігор на древлян і переміг, і наклав на них данину більшу Олегової. Того ж літа приходив Семеон Болгарський до Цареграда, заключив мирний договір і повернувся додому.</p>\r\n<p>В літо 6423 [915]. Вперше прийшли на Руську землю печеніги і, заключивши мир з Ігорем, пішли до Дунаю.</p>\r\n<p>У ті часи приходив Семеон і полонив Фракію. Греки ж послали за печенігами. Коли печеніги прийшли і вже хотіли виступити проти Семеона, як розсварилися грецькі воєводи. Печеніги побачили, що греки самі між собою воюють, пішли додому. А болгари зійшлися з греками, і порубані були греки. Семеон же захопив город Ондріян, який раніше називався городом Ореста, Агамемнонового сина, який колись тут у трьох ріках купався, позбувся хвороби і тут цей город назвав своїм іменем. Пізніше ж цар Андріян город обновив і назвав своїм іменем - Андріян, ми ж називаємо Ондріяновим градом.</p>\r\n<p>В літо 6424 [916].</p>\r\n<p>В літо 6425 [917].</p>\r\n<p>В літо 6426 [918].</p>\r\n<p>В літо 6427 [919].</p>\r\n<p>В літо 6428 [920]. Поставлено Романа царем Грецької землі. Ігор же воював проти печенігів.</p>\r\n<p>В літо 6429 [921].</p>\r\n<p>В літо 6430 [922].</p>\r\n<p>В літо 6431 [923].</p>\r\n<p>В літо 6432 [924].</p>\r\n<p>В літо 6433 [925].</p>\r\n<p>В літо 6434 [926].</p>\r\n<p>В літо 6435 [927].</p>\r\n<p>В літо 6436 [928].</p>\r\n<p>В літо 6437 [929]. Прийшов Семеон до Цареграда, і захопив Фракію і Македонію, і прийшов до Цареграда з силою великою і з гордістю, і заключив мир з царем Романом, і повернувся додому.</p>\r\n<p>В літо 6438 [930].</p>\r\n<p>В літо 6439 [931].</p>\r\n<p>В літо 6440 [932].</p>\r\n<p>В літо 6441 [933].</p>\r\n<p>В літо 6442 [934]. Вперше прийшли до Цареграда угри і полонили всю Фракію. Роман же заключив мир з уграми.</p>\r\n<p>В літо 6443 [935].</p>\r\n<p>В літо 6444 [936].</p>\r\n<p>В літо 6445 [937].</p>\r\n<p>В літо 6446 [938].</p>\r\n<p>В літо 6447 [939].</p>\r\n<p>В літо 6448 [940].</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6449. Иде Игорь на Гр<small>Ђ</small>кы; и яко послаша Болгаре в<small>Ђ</small>сть къ царю, яко идуть Русь на Царьградъ ск<small>Ђ</small>дий 10 тысящь. Иже и поидоша и приплуша, и почата воевати Вифиньскыя страны, и пл<small>Ђ</small>новаху по Понту до Ираклия и до Фофлагоньскы земля, и всю страну Никомидийскую пополониша, и Судъ всь пожьгоша. Ихъже емъше, ов<small>Ђ</small>хъ растинаху, и другія же, сторожи поставьляюще, стр<small>Ђ</small>лами растр<small>Ђ</small>ляху; и изъламляху, опакы руц<small>Ђ</small> связавше, и гвозды жел<small>Ђ</small>зны посред<small>Ђ</small> головъ вбивахуть има Много же и святыхъ церквий огьневи предаша, и им<small>Ђ</small>нье не мало обою сторону взяша, [монастыря же и села все огневи предаша]. Потомь же пришедшемъ воемъ отъ въстока, Панфиръ деместникъ съ четырмидесятъ тысящь, Фока же патрикий съ Македоны, Феодоръ же стратилатъ съ Фракы, и с ними же и сановницы боярьстии обидоша Русь около. И св<small>Ђ</small>щаша Русь, и изидоша противу вооружившися на Гр<small>Ђ</small>кы, и брани межю има бывши зл<small>Ђ</small>, одва одол<small>Ђ</small>ша Гр<small>Ђ</small>ци. Русь же възвратишася къ дружин<small>Ђ</small> своей к вечеру, и на ночь вл<small>Ђ</small>зъше въ лодья отб<small>Ђ</small>гоша. Феофанъ же уср<small>Ђ</small>те я въ оляд<small>Ђ</small>хъ съ огнемь, и нача пущати огнь трубами на лодья Рускыя, и бысть від<small>Ђ</small>ти страшно чюдо: Русь же видяще пламень, вм<small>Ђ</small>тахуся въ воду морьскую, хотяще убр<small>Ђ</small>сти, ......... отяще и тако прочии възвратишася въсвояси. Т<small>Ђ</small>мь же пришедъшимъ в землю свою, пов<small>Ђ</small>даху кождо своимъ о бывшемъ и о лядьн<small>Ђ</small>мъ огни: &laquo;якоже молонья&raquo;, рече, &laquo;иже на небесихъ, Гр<small>Ђ</small>ци имуть в себе, в сию пущающе жьжаху насъ; и сего ради не одол<small>Ђ</small>хомъ имъ&raquo;. Игорь же пришедъ и нача съвокупити вои многы, и посла по Варягы за море, вабя и на Гр<small>Ђ</small>кы, хотя поити на ня.</p>\r\n<p>В л<small>Ђ</small>то 6450. Семеонъ иде на Хорваты, и поб<small>Ђ</small>женъ бысть Хорваты, и умре, оставивъ Петра, сына своего, княжить. В се же л<small>Ђ</small>то родися Святославъ у Игоря.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6449 [941].</p>\r\n<p>Ігор ходив у похід на греків.</p>\r\n<p>І послали вість царю болгари:</p>\r\n<p>&laquo;Іде Русь на Цареград, скідій десять тисяч&raquo;.</p>\r\n<p>І як прийшли вони, і пристали [до берега],</p>\r\n<p>І стали воювати країни Віфінські,</p>\r\n<p>І полонили їх по Понту аж до Іраклія</p>\r\n<p>І до землі Фофлагонської.</p>\r\n<p>І всю країну Нікомидійську пополонили,</p>\r\n<p>І Суд весь спалили.</p>\r\n<p>І кого брали, тих розтинали,</p>\r\n<p>Інших як мішені розставляли</p>\r\n<p>І стрілами розстрілювали</p>\r\n<p>І [хребти] ламали, назад руки зв\'язавши,</p>\r\n<p>І цвяхи залізні посеред голів їм забивали.</p>\r\n<p>І святих церков багато вогневі віддали,</p>\r\n<p>І з обох боків великі добра взяли</p>\r\n<p>Монастирі і села попалили.</p>\r\n<p>Потім прийшли вої зі сходу,</p>\r\n<p>Із сорокатисячним військом Панфир-деместник,</p>\r\n<p>Із македонянами Фока-патрикій,</p>\r\n<p>Із фракійцями Федір-стратилат,</p>\r\n<p>Із ними ще й бояри сановні,-</p>\r\n<p>І обступили русь.</p>\r\n<p>І раду радила русь,</p>\r\n<p>І вийшли оружно проти греків,</p>\r\n<p>І битва між ними була жорстока,</p>\r\n<p>І ледь-ледь подолали греки.</p>\r\n<p>Надвечір повернулися руси до дружини своєї</p>\r\n<p>І вночі, посідавши в лодії, відбігли.</p>\r\n<p>Феофан же зустрів їх у лодіях вогнем,</p>\r\n<p>І почав пускати вогонь трубами на лодії руськії,</p>\r\n<p>І було видно страшне чудо:</p>\r\n<p>І русь же, бачачи полум\'я, стрибала у воду морськую,</p>\r\n<p>І хотіла врятуватися,</p>\r\n<p>І тільки деякі додому повернулись.</p>\r\n<p>Ті ж, що в землю свою повернулись,-</p>\r\n<p>Кожен своїм оповідали про те, що сталось,</p>\r\n<p>І про вогонь із лодій:</p>\r\n<p>&laquo;Неначе блискавку небесну,- казали,-</p>\r\n<p>Мають греки у себе</p>\r\n<p>І, її пускаючи, нас попалили,</p>\r\n<p>І через це не здолали ми їх&raquo;.</p>\r\n<p>І коли повернувся Ігор,</p>\r\n<p>Почав знову множство воїв гуртувати І послав по варягів за море, ваблячи іти на греків, Бо сам знову хотів іти на них.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6451. Пакы приидоша на Царьградъ, и миръ створивше с Романомъ, възвратишася въсвояси.</p>\r\n<p>В л<small>Ђ</small>то 6452. Игорь совокупи воя многы, Варягы, и Русь, и Поляны, и Слов<small>Ђ</small>ны, и Кривичи, и Печен<small>Ђ</small>гы ная, и тали в нихъ поемъ, поиде на Гр<small>Ђ</small>кы в лодьяхъ и на конехъ, хотя мстити себе. Се слышавше Курсунци, послаша къ Роману, глаголюще: &laquo;се идуть Русь, покърыли суть море корабли&raquo;. Такоже и Болгаре послаша в<small>Ђ</small>сть, глаголюще: &laquo;идуть Русь, и Печен<small>Ђ</small>гы наяли суть к соб<small>Ђ</small>&raquo;. Се слышавъ царь, посла къ Игореви лутьшии бояры, моля и глаголя: &laquo;не ходи, но возьми дань, юже ималь Олегъ, и придамъ еще къ той дани&raquo; Такоже и Печен<small>Ђ</small>гомъ послаша паволокы и золото много. Игорь же дошедъ Дуная, съзва дружину и нача думати, и пов<small>Ђ</small>да имъ р<small>Ђ</small>чь цареву. Ркоша же дружина Игорева: &laquo;да аще сице глаголеть царь, то что хощемъ боле того, не бившися имати злато, и серебро, и паволокы. Еда кто в<small>Ђ</small>сть, кто одол<small>Ђ</small>еть, мы ли, они ли? или с моремъ кто св<small>Ђ</small>тенъ? Се бо и не по земли ходимъ, но по глубин<small>Ђ</small> морьстий, и обьча смерть вс<small>Ђ</small>мъ&raquo;. И послуша ихъ Игорь, и повел<small>Ђ</small> Печен<small>Ђ</small>гомъ воевати Болгарьскую землю, а самъ вз<small>Ђ</small>мъ у Гр<small>Ђ</small>къ злато и паволокы на вся воя, възвратися въспять, и приде къ Киеву въсвояси.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6450 [942]. Семеон ходив на хорватів, і переможений був хорватами, і помер, а залишив князювати Петра, сина свого. Цього ж року народився у Ігоря син Святослав.</p>\r\n<p>В літо 6451 [943]. Знову ходили на Цареград, заключили мир з Романом і повернулися додому.</p>\r\n<p>В літо 6452 [944]. Ігор зібрав багато воїв: варяги, і русь, і поляни, і словіни, і кривичі, і печенігів найняв, і взяв від них заложників,- пішов проти греків у лодіях і на конях, прагнучи помститися за себе. Почули це корсунці і послали до Романа, повідомляючи: &laquo;Тут іде русь, море покрила кораблями&raquo;. Також і болгари послали вість, попереджаючи: &laquo;Іде русь, і печенігів найняли до себе&raquo;. Почув це цар і послав до Ігоря луччих бояр, молячи і прохаючи: &laquo;Не йди, а візьми данину, яку брав Олег, і додам ще до тої дані&raquo;. Також і печенігам послав паволоки і багато золота. Коли Ігор підійшов до Дунаю, скликав дружину, і став раду радити, і переповів їм мову цареву. Ігорева ж дружина сказала: &laquo;Коли так говорить цар, то чого нам потрібно більше, без битви взяти золото, і срібло, і паволоки? Хіба відомо, хто одоліє, чи ми, чи вони? Чи з морем хто в спілці? Адже не по землі ходимо, а глибинами морськими, і спільна смерть для всіх&raquo;. І послухався їх Ігор, і звелів печенігам воювати Болгарську землю, а сам узяв у греків золото і паволоки на всіх воїв і повернувся назад, і прийшов додому в Київ.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6453. Присла Романъ и Костянтинъ и Стефанъ послы къ Игореви построити мира п<small>Ђ</small>рваго; Игорь же глаголавъ съ ним [и] о мир<small>Ђ</small>. Посла Игорь мужи свои къ Роману, Романъ же събра бояры и сановникы. И приведоша Рускыя послы, и повел<small>Ђ</small>ша глаголати и писати обоихъ р<small>Ђ</small>чи на харотью, равно другаго св<small>Ђ</small>щания, бывшаго при цари Роман<small>Ђ</small>, и Костянтині, Стефан<small>Ђ</small>, христолюбивыхъ кладыкъ:</p>\r\n<p>&laquo;Мы отъ рода Рускаго послы и гостье, Иворъ, посолъ Игоревъ великаго князя Рускаго, и обьчни посли: Вуефастъ Святославль, сына Игорева, Искусеви Олгы княгыня, Слуды Игоревъ, нетий Игоревъ Ул<small>Ђ</small>бъ Володиславль, Каницаръ Предславинъ, Шигобернъ, Съфандръ жены Ул<small>Ђ</small>бовы, Прастенъ, Турдуви, Либиарь, Фастов [ъ], Гримъ, Сфирковъ, Праст<small>Ђ</small>нъ, Якунъ, нетий Игоревъ, Кары, Тудков, Каршевъ, Тудоровъ, Егриерлисковъ, Войстовъ, Иковъ, Истръ, Яминдовъ, Ятьвягъ, Гунаревъ, Шибьридъ, Алдань, Колъклековъ, Стеггиетоновъ, Сфирка, Алвадъ, Гудовъ, Фудри, Тулбовъ, Муторъ, Утинъ, куп<small>Ђ</small>цъ Адунь, Адолбъ, Ангивладъ, Ул<small>Ђ</small>бъ, Фрутанъ, Гомолъ, Куци, Емигъ, Турьбридъ, Фурьст<small>Ђ</small>нъ, Бруны, Роалъдъ, Гунастръ, Фраст<small>Ђ</small>нъ, Ингелдъ, Турбернъ, и другий Турбернъ, Ул<small>Ђ</small>бь, Турбенъ, Моны, Руалдъ, Св<small>Ђ</small>нь, Стиръ, Алданъ, Тилий, Апубкарь, Св<small>Ђ</small>нь, Вузел<small>Ђ</small>въ, Исинько, Биричь, послании отъ Игоря, великаго князя Рускаго, и отъ всея княжья, и отъ вс<small>Ђ</small>хъ людий Руское земли. И отъ т<small>Ђ</small>хъ запов<small>Ђ</small>дано объновити ветхый миръ и отъ ненавидящаго добра, вьраждолюбца дьявола, разорити отъ многъ л<small>Ђ</small>тъ, утв<small>Ђ</small>рдити любовь межю Гр<small>Ђ</small>кы и Русью. И великый нашь князь Игорь, и бояре его и людие вси Рустии послаша ны къ Роману и Стефану и Костянтину, великымъ царемъ Гр<small>Ђ</small>цкымъ, створити любовь [съ] самими цари, и съ вс<small>Ђ</small>мъ боярьствомъ, и съ всими людми Гр<small>Ђ</small>цкими, на вся л<small>Ђ</small>та, дондеже солнце сияеть и всь миръ стоить. Иже помыслить отъ страны Рускыя раздрушити таковую любовь, и еліко ихъ священие прияли суть, да приимуть м<small>Ђ</small>сть отъ Бога Всед<small>Ђ</small>ржителя, осужение и на погибель и в сий в<small>Ђ</small>къ и в будущий; а елико ихъ не крещено есть, ди не имуть помощи отъ Бога, ни отъ Перуна, да не ущитятся щиты своими, и да пос<small>Ђ</small>чени будуть мечи своими, и отъ стр<small>Ђ</small>лъ и отъ иного оружья своего, и да будуть раби и в сий в<small>Ђ</small>къ, и будущий. Великий князь Рускый и бояр<small>Ђ</small> его да посилають на то въ Гр<small>Ђ</small>кы къ великымъ царемъ Гр<small>Ђ</small>цкымъ корабля, елико хотять, съ послы своими и гостьми, якоже имъ уставлено есть. Ношаху послы печати златы, а гостие серебряны. Нын<small>Ђ</small> же ув<small>Ђ</small>далъ есть князь нашь посилати грамоту къ царству вашему: иже поеылаеми бывають отъ нихъ послы и гостье, да приносять грамоту, пишюще сице: яко послахъ корабль селико; и отъ т<small>Ђ</small>хъ да ув<small>Ђ</small>мы и мы, оже с миромъ приходять. Аще ли безъ грамоты приидуть и предани будуть намъ, д<small>Ђ</small>ржимъ и хранимъ, дондеже възв<small>Ђ</small>стимъ князю вашему. Аще ли руку не дадять и противятся, да убьени будуть, и да не изыщеться смерть ихъ отъ князя вашего. Аще ли уб<small>Ђ</small>жавше приидуть въ Русь, и мы напишемъ къ князю вашему, и яко имъ любо, тако створять. И аще придуть Русь без купля, да не взимають м<small>Ђ</small>сячины. И да запретить князъ словомъ своимъ, и приходящи Руси сде, да не творять бещинья в сел<small>Ђ</small>хъ, ни въ стран<small>Ђ</small> нашей. И приходящимъ имъ, да витають у святаго Мамы; да послеть царство ваше да испишеть имена ихъ, и тогда възмуть м<small>Ђ</small>сячьное свое і посли сл<small>Ђ</small>бное свое, а гостье м<small>Ђ</small>сячное свое, п<small>Ђ</small>рвое отъ града Киева, и пакы ис Чернигова и ис Переяславля и прочии городи. И да входять в городъ одиными вороты съ царевомъ мужемъ безъ оружья 50 мужь, и да творять куплю якоже имъ надоб<small>Ђ</small>, и пакы да исходять; и мужь царьства вашего да хранить я, да аще кто отъ Руси или отъ Гр<small>Ђ</small>къ створить криво, да оправляетъ. Входя же Русь в тородъ, да не творять пакости и не им<small>Ђ</small>ють власти купити паволокъ лише по пятидесятъ золотникъ; и отъ т<small>Ђ</small>хъ паволокъ аще кто купить, да показаеть цареву мужеви, и тъя запечатаеть и дасть имъ. И отходящи Руси отсюду взимають отъ насъ еже надоби брашно на путь, и еже надоб<small>Ђ</small> лодьямъ, якоже установлено есть п<small>Ђ</small>рвое, и да възвращаются съ спасениемъ въ свою сторону, и да не имуть волости зимовати у святаго Мамы. И аще ускочить челядинъ отъ Руси, понеже приидуть въ страну царства нашего, и отъ святаго Мамы, и аще будеть и обрящеться, да поимуть и; аще ли не обрящется, да на роту идуть наши крестьяная Русь, а не хрестьянин по закону своему: ти тогда взимають отъ насъ ц<small>Ђ</small>ну свою, якоже уставлено есть преже, 2 паволоц<small>Ђ</small> за челядинъ. Аще ли кто отъ людий царства вашего, или отъ рода вашего, или отъ ин<small>Ђ</small>хъ городъ ускочить челядинъ нашъ къ вамъ, и принесеть что, да взвратять е опять; и еже что принеслъ будеть ц<small>Ђ</small>ло все, да возметь отъ него золотника два им<small>Ђ</small>чнаго. Аще ли покусится кто взяти отъ Руси и отъ людий царства вашего, иже то створить, покажненъ будеть вельми; аще ли и взялъ будеть, да заплатить сугубо. Аще ли створить тоже Гр<small>Ђ</small>чинъ Русину, да прииметь ту же казнь, якоже приялъ есть онъ. Аще ли ключится украсти Русину отъ Гр<small>Ђ</small>къ что, или Гр<small>Ђ</small>чину отъ Руси, достойно есть да възвра [ти] ть е не точью едино, но и ц<small>Ђ</small>ну его; аще украденое обрящется продаемо, да вдасть ц<small>Ђ</small>ну его сугубу, и тъ покажненъ будеть по закону Гр<small>Ђ</small>цкому, и по уставу Гр<small>Ђ</small>цкому, и по закону Рускому. И елико християнъ отъ власті нашея пленена приведуть Русь, ту аще будеть уноша, или д<small>Ђ</small>вица добра, да въдадять золотникъ 10 и поимуть и; аще ли есть средов<small>Ђ</small>чъ, да вдасть золотникъ 8, и поиметь и; аще ли будеть старъ, или д<small>Ђ</small>тичь, да вдасть золотникъ 5. Аще ли обьрящутся Русь работающе у Гр<small>Ђ</small>къ, аще суть полоници, да искупають а Русь по 10 золотникъ; аще ли купилъ и будеть Гр<small>Ђ</small>чинъ, подъ крестомъ, достоить ему да възметь ц<small>Ђ</small>ну, елико же далъ будеть на немъ. О Курсуньсций сторон<small>Ђ</small>, колко же есть городъ на той часті, да не имудь власти князи Рускыи да воюеть на т<small>Ђ</small>хъ сторонахъ, а та страна не покоряется вамъ; и тогда аще просить вои отъ насъ князь Рускый, дамы ему елико ему будеть треб<small>Ђ</small>, и да воюеть. И о томъ, аще обрящють Русь кувару Гр<small>Ђ</small>чьску вывержену на н<small>Ђ</small>коемъ любо м<small>Ђ</small>ст<small>Ђ</small>, да не преобидять ея; аще ли отъ нея възметь кто что, или челов<small>Ђ</small>ка поработить, или убьеть, да будеть повиненъ закону Рускому и Гр<small>Ђ</small>цкому. И аще обрящють Русь Корсуняны рыбы ловяща въ устьи Дн<small>Ђ</small>пра, да не творять имъ зла никакого же. И да не им<small>Ђ</small>ють Русь власти зимовати въ устьи Днепра, Б<small>Ђ</small>лобережа, ни у святаго Елеуф<small>Ђ</small>рья; но егда придеть осень, да идуть в домы своя в Русь. А о сихъ, иже то приходять Черьнии Болгаре, и воюють въ стран<small>Ђ</small> Корсуньстий, и велимъ князю Рускому да ихъ не пущаеть и пакостять сторон<small>Ђ</small> его. Или аще ключится проказа н<small>Ђ</small>кака отъ Гр<small>Ђ</small>къ, сущихъ подъ властью царства нашего, да не имате власти казнити я, но повел<small>Ђ</small>ньемь царства нашего, да прииметь, якоже будеть створилъ. И аще убьеть крестьянінъ Русина [или Русинъ Христіанина] да держимъ будеть створивши убійство отъ ближнихъ убьенаго, да убьють и. Аще ли ускочить створивы убо[й] и уб<small>Ђ</small>жить, и аще будеть имовитъ, да возмуть им<small>Ђ</small>нье его ближнии убьенаго; аще ли есть неимовитъ створивый убийство и ускочить, да ищють его, дондеже обрящется; [аще ли обрящется] да убьенъ будетъ. Или аще ударить мечемъ, или копьемъ, или кац<small>Ђ</small>мъ инымъ съсудомъ Русинъ Гр<small>Ђ</small>чина, или Гр<small>Ђ</small>чинъ Русина, да того д<small>Ђ</small>ля гр<small>Ђ</small>ха заплатить серебра литръ 5, по закону Рускому; аще ли есть неимовитъ, да како можеть въ толко же и проданъ будеть, яко да и порты, в нихъже ходить, и то с него сняти, а опрочи да на роту ходить по своей в<small>Ђ</small>р<small>Ђ</small>, яко не им<small>Ђ</small>я ничтоже, ти тако пущенъ будеть. Аще ли хот<small>Ђ</small>ти начнеть наше царьство отъ васъ вои на противящася намъ, да пишють к великому князю вашему, и пошлеть к намъ, елико хощемъ; и оттол<small>Ђ</small> ув<small>Ђ</small>дять иныя страны, каку любовь им<small>Ђ</small>ю [ть] Гр<small>Ђ</small>ци с Русью. Мы же св<small>Ђ</small>щание все положимъ на двою харатью, и едина харотья есть у царства нашего, на нейже есть крестъ и имена наша написана, а на другой посли ваши и гостье ваши. А отходяче с посломъ царства нашего, да попроводять к великому князю Игореви Рускому и к людемъ его, и ти приимающе харотью, на роту идуть хранити истину, якоже мы св<small>Ђ</small>щахомъ и написахомъ на харотью сию, на нейже суть написана имена наша. Мы же, елико насъ крестилися есмы, кляхомся церковью святаго Ильи въ зборн<small>Ђ</small>й церкви, и предълежащи [мъ] честнымъ крестомъ, и харотьею сею, хранити же все, еже есть написано на ней, и не преступати отъ того ничтоже; а оже переступіть се отъ страны нашея, или князь, или инъ кто, или крещенъ, или некр<small>Ђ</small>щенъ, да не имать отъ Бога помощи, и да будуть рабі в сий в<small>Ђ</small>къ и въ будущий, и да заколенъ будеть своимъ оружьемъ. А некрещении Русь да полатають щиты своя и мечи своя нагы, и обручи свои и прочая оружья, и да кл<small>Ђ</small>нуться о всемъ, и яже суть написана на харотьи сей и хранити отъ Игоря и отъ вс<small>Ђ</small>хъ бояръ и отъ вс<small>Ђ</small>хъ людий и отъ страны Рускыя, въ прочая л<small>Ђ</small>та и всегда. Аще ли же кто отъ князь и отъ людий Рускыхъ, или крестьянъ, или некрещеный, переступить все еже написано на харотьи сей, и будеть достоинъ своимъ оружьемъ умрети, и да будеть проклятъ отъ Бога и отъ Перуна, и яко преступи свою клятъву. Да обаче будеть добр<small>Ђ</small>, Игорь великый князь да хранить любовь вьсю правую да не раздрушится, дондеже солнце сияеть и всь миръ стоить, въ нын<small>Ђ</small>шняя в<small>Ђ</small>кы и в будущая&raquo;.</p>\r\n<p>Послании же посли Игоремъ придоша къ Игореви съ послы Гр<small>Ђ</small>цкими, и пов<small>Ђ</small>даша вся р<small>Ђ</small>чи царя Романа. Игорь же призва послы Гр<small>Ђ</small>цкыя, рече: &laquo;молвите, что вы казалъ царь?&raquo; И ркоша посли цареви: &laquo;се посла ны царь, радъ есть миру, и хочеть миръ им<small>Ђ</small>ти съ княземъ Рускымъ и любовь; и твои посли водили суть царя нашего рот<small>Ђ</small>, и насъ послаша рот<small>Ђ</small> водить тебе и мужь твоихъ&raquo;. И об<small>Ђ</small>щася Игорь сице створить. И наутр<small>Ђ</small>я призва Игорь посли, и приде на холъмы, кде стояше Перунъ, и покладоша оружья своя, и щиты, и золото, и ходи Игорь рот<small>Ђ</small> и мужи его, и елико поганыя Руси; а христьяную Русь водиша въ цервовь святаго Ильи, яже есть надъ ручьемъ, кон<small>Ђ</small>ць Пасыньц<small>Ђ</small> бес<small>Ђ</small>ды и Козаре: се бо б<small>Ђ</small><em> </em>сборная церкви, мнози бо б<small>Ђ</small>ша Варязи христьяни. Игорь же утв<small>Ђ</small>рдивъ миръ съ Гр<small>Ђ</small>кы, отпусти послы, одаривъ скорою и челядью и воскомъ, и отпусти я; посли же придоша къ цареви, и пов<small>Ђ</small>даша вся р<small>Ђ</small>чи Игоревы и любовь яже къ Гр<small>Ђ</small>комъ. Игорь же нача княжити въ Киев<small>Ђ</small>, и миръ им<small>Ђ</small>я къ вс<small>Ђ</small>мъ странамъ. И присп<small>Ђ</small> осень, и нача мыслить на Деревляны, хотя примыслити большюю дань.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6453 [945]. Прислали Роман, і Костянтин, і Стефан послів до Ігоря, щоб відновити попередній мир. Ігор же говорив з ними про мир. Послав Ігор своїх мужів до Романа. Зібрав Роман своїх бояр і сановників. І привели руських послів, і звеліли їм говорити і писати на хартію сказане обома [сторонами] відповідно попередньому мирному договору, укладеному за царів Романа, і Костянтина, і Стефана, христолюбивих владик:</p>\r\n<p>&laquo;Ми, від роду руського посли і купці, Івор, посол великого князя Руського Ігоря, і іншії посли: Вуефаст від Святослава, сина Ігоревого, Іскусеві від княгині Ольги; Слуди від Ігоря, небіж Ігорів Уліб від Володиславля, Каницар від Предслави; Шигоберн, Сфандр від Улібової жони, Прастен, Турдуві, Лібіар, Фастів, Грім, Сфірків, Прастін, Якун, небіж Ігорів, Кари, Тудків, Каршев, Тудорів, Егріерлісків, Войстів, Іків, Істр, Яміндів, Ятвяг, Гунарів, Шібрід, Алдан, Колклеків, Стеггієтонів, Сфірка, Алвад, Гудів, Фудрі, Тулбів, Мутор, Утін, купець Адун, Адолб, Ангівлад, Уліб, Фрутан, Гомол, Куці, Еміг, Турбрід, Фурстін, Бруни, Роальд, Гунастр, Фрастін, Інгельд, Турберн, і другий Турберн, Уліб, Турбен, Мони, Руальд, Свін, Стир, Алдан, Тілій, Апубкар, Свій, Вузелів, Ісинько, Бирич,- посланії від Ігоря, великого князя Руського, і від усіх князів, і від усіх людей Руської землі. І від них заповідано поновити старий мир, підступом ненависника добра і враждолюбця - диявола - порушений уже багато років, і зміцнити любов поміж греками і руссю.</p>\r\n<p>І великий наш князь Ігор, і бояре його, і всі люди руськії, що послали нас до Романа, і Стефана, і Костянтина, великих царів грецьких, укласти договір про дружбу з самими царями, і зі всім боярством, і зі всіма людьми грецькими на всі літа, допоки сонце сяє і весь світ стоїть. І хто із країни Руської намислить порушити цю дружбу, а прийняли хрещення, то хай впаде на них помста Бога Вседержителя, і осуджені будуть на погибіль і в цей вік і в майбутній. А коли не хрещені, то хай не мають допомоги ні від Бога, ні від Перуна, і хай їх не захистять власні щити, і хай посічені будуть мечами своїми, і загинуть від стріл та іншого оружжя свого, і хай будуть рабами і в цьому житті, і потойбічному.</p>\r\n<p>Великий князь Руський і бояри його хай посилають на те в Грецьку землю до великих царів грецьких кораблі, скільки хотять, з послами своїми і купцями, як то для них установлено. [Досі] носили посли печатки золоті, а купці - срібні. Нині ж звелів князь ваш посилати грамоти до нас, царів, що послані від них посли і купці хай приносять грамоту, в якій буде написано про це: скільки послано кораблів, і аби ми з того упевнилися, що ідуть вони з миром.</p>\r\n<p>А коли без грамоти прийдуть і будуть взяті нами, то будемо тримати і охороняти, доки не повідомимо князеві вашому. А коли в руки не даватимуться і опір чинити стануть,- то будуть убиті, і хай смерть їхня не буде відомщена князем вашим. А коли втечуть і повернуться на Русь, то ми напишемо князеві вашому, і хай робить, що хоче. І коли прийде русь не для торгівлі, хай не стягають місячини. І хай заборонить князь словом своїм, щоб прибула сюди русь не творила безчинства по селах, ані в країні нашій. І нехай прибулі проживають у святого Мами, і коли пошле царство ваше - хай напише їхні імена, і тоді хай беруть місячину свою: посли свою посольську, а купці свою місячину, найперші - від града Києва, потім із Чернігова, і із Переяслава й інших городів. І нехай входять у город одними воротами [в супроводі] царевого мужа без зброї по п\'ятдесят мужів, і хай торгують скільки потрібно, і виходять назад. І муж царства вашого хай охороняє їх.</p>\r\n<p>А коли хто із Русі чи із Грецької землі поступить неправо, хай судиться. Коли русь входить в город, хай не чинить пакості, і не має права купляти паволоки дорожче, ніж по п\'ятдесят золотників. А коли хто купить такої паволоки, нехай покаже царевому мужеві, і той поставить печатку і дозволить. Відбуваюча звідси русь нехай бере від нас, що потрібно: їжу в дорогу і що потрібно для лодій, як було встановлено раніше. І нехай повертаються в цілковитій безпеці в свою країну, а зимувати у святого Мами не мають права.</p>\r\n<p>А коли втече руський челядин, то нехай прийдуть [за ним] в землі царства нашого. І коли його відшукають у святого Мами, хай забирають, а коли не знайдеться, то нехай клянуться нам руські християне, а не християне - згідно свого закону. І нехай тоді беруть із нас свою ціну, як було встановлено раніше,- дві паволоки за челядина.</p>\r\n<p>А коли хто із людей царства вашого, чи із роду вашого, чи із інших городів ваших втече до нас [на Русь] і принесе що-небудь, хай те буде повернуто назад. І коли принесене буде все ціле, то нехай візьмуть із нього два золотники за поїмку.</p>\r\n<p>А коли ж хто-небудь із русі чи із людей вашого царства спокуситься на кражу і те вчинить, буде суворо покараний. І коли вже візьме, хай заплатить подвійно.</p>\r\n<p>А коли подібне зробить гречин русину, нехай прийме ту ж кару, яку поніс він.</p>\r\n<p>А коли трапиться украсти що-небудь русину у грека чи грекові у русина, то належить повернути не тільки вкрадене, а й ціну його. А коли вкрадене виявиться проданим, то нехай поверне його подвійну ціну і буде покараний по закону руському.</p>\r\n<p>І скільки б полонених християн, наших підданих, не привела русь, буде то юнак чи красна дівиця, хай платять греки десять золотників і забирають їх; а коли середнього віку, хай платять вісім золотників - і забирають їх; а коли буде старий чи дитина - нехай дають за них п\'ять золотників.</p>\r\n<p>А коли знайдуться руси, що яко полонені перебувають в рабстві у греків, нехай викупають і руси по десять золотників. А коли, їх купив гречин, то йому належить поклястися на хресті,- і хай візьме ціну, яку заплатив за раба.</p>\r\n<p>Про Корсунську землю. Скільки б не було там городів, князі руськії не мають влади в них і права воювати в тих краях. І та земля не підкоряється вам. А коли князь руський попросить у нас воїв, дамо, скільки йому буде треба,- і нехай воює.</p>\r\n<p>І про те: коли русь знайде грецький корабель, викинутий у будь-якому місці, хай не роблять йому шкоди. А коли хто візьме із нього що-небудь, чи чоловіка поверне в рабство, чи уб\'є, то має підлягати суду згідно закону руського і грецького.</p>\r\n<p>І коли руси застануть корсунців у гирлі Дніпра за ловлею риби, нехай не чинять їм ніякого зла.</p>\r\n<p>І хай русь не має права зимувати в гирлі Дніпра, в Білобережжі, ані біля святого Єлеуфір\'я; і з настанням осені хай ідуть по домівках своїх на Русі.</p>\r\n<p>І про сих: якщо прийдуть чорні болгари і стануть воювати в Корсунській землі, то велимо князеві руському, аби їх не пускав, бо нашкодять і його землі.</p>\r\n<p>Якщо ж коли буде вчинено якесь злодійство [кимось] із греків, сущих під владою царською нашою, то не маєте права карати їх, але з нашого царського повеління хай буде покараний за содіяне.</p>\r\n<p>Якщо ж християнин уб\'є русина [чи русин християнина], хай затримають убивцю родичі вбитого і уб\'ють його.</p>\r\n<p>Якщо ж убивця сховається і втече і коли буде багатий, нехай візьмуть майно його родичі вбитого. А коли убивця неімущий і сховається, хай шукають його, допоки знайдеться, [а коли знайдеться] - нехай буде вбитий.</p>\r\n<p>Якщо ж ударять мечем чи списом, чи кацією, чи якимось іншим предметом русин грека, чи грек русина, хай за те беззаконня винуватець заплатить п\'ять літрів срібла, згідно закону Руського. А якщо винуватець неімущий, то нехай, що тільки можна, те й буде продано, навіть і штани, в яких ходить, і ті з нього зняти; а про решту нехай поклянеться своєю вірою, що не має більше нічого, і тільки після цього буде відпущений.</p>\r\n<p>Якщо ж наше царство захоче від вас взяти воїв проти ворогів наших, то напишемо про те великому князеві вашому, а він пошле, скільки хочемо: і з цього довідаються інші країни, яка існує дружба між Грецькою землею і Руссю.</p>\r\n<p>Ми ж договір цей поклали на двох хартіях, і одна хартія зберігається у нас, царів, на ній поставлено хрест і імена наші написано, а на другій - імена послів і купців ваших.</p>\r\n<p>А коли виїдуть посли наші царські, то нехай супроводять їх до великого князя руського Ігоря і до людей його, а ті приймаючи хартію, поклянуться нехай дотримуватися чесно, про що домовилися і записали в цю хартію, на якій написано імена наші.</p>\r\n<p>Ми ж, оскільки прийняли хрещення від вас, поклялися церквою святого Іллі в соборній церкві і предпокладеним правдивим хрестом і хартією цією, дотримуватися всього, що в ній написано, і не порушувати із того нічого. А коли хто-небудь із нашої землі порушить це - чи князь, чи інший хто, хрещений чи не хрещений,- хай не матиме він божої помочі і буде рабом у цьому і в потойбічному житті, і хай буде заколений власною зброєю.</p>\r\n<p>А нехрещена русь хай покладе свої щити, і оголені мечі свої, і обручі свої, і інше оружжя і нехай клянуться про все, що написано на цій хартії, і не відступатимуть від того ні Ігор, ні бояри його, ні всі люди землі Руської і в майбутні роки, і завжди.</p>\r\n<p>Якщо ж хтось із князів чи із людей руських, чи християнин чи нехрещений, порушить все ж написане на хартії цій,- заслужить смерті від своєї зброї і нехай буде проклятий від Бога і від Перуна як клятвопорушник.</p>\r\n<p>І нехай все-таки великий князь Ігор належним чином береже цю правдиву дружбу, і нехай вона залишиться непорушною, допоки сонце сяє і весь світ стоїть, і нині, і во віки віків&raquo;.</p>\r\n<p>&nbsp;</p>\r\n<p>Послані ж Ігорем посли прийшли до нього з послами грецькими</p>\r\n<p>І повідали про все, що цар Роман казав їм.</p>\r\n<p>Ігор же закликав послів грецьких</p>\r\n<p>І рече [їм]: &laquo;Розкажіть, що цар вам наказав?&raquo;</p>\r\n<p>І відповіли посли цареви: &laquo;Се послав нас цар, він радий миру,</p>\r\n<p>І хоче мир і любов із князем руським мати,</p>\r\n<p>І посли твої від царя нашого приймали клятву,</p>\r\n<p>І послав нас цар прийнять від тебе клятву</p>\r\n<p>І мужів твоїх&raquo;.</p>\r\n<p>І пообіцяв їм Ігор це зробити,</p>\r\n<p>І послів назавтра запросив [князь] Ігор,</p>\r\n<p>І зійшов він на горби, де стояв Перун,</p>\r\n<p>І поклали оружжя своє, і щити, і злото,</p>\r\n<p>І поклявся Ігор, і його бояри,</p>\r\n<p>І всі руси, що були погани.</p>\r\n<p>&nbsp;</p>\r\n<p>А християн-русинів водили [на клятву] в церкву святого</p>\r\n<p>Іллі, що стоїть над потоком в кінці Бесіди Пасинців і Козарського, тут-бо була соборна церква, бо багато варягів були християни.</p>\r\n<p>&nbsp;</p>\r\n<p>Ігор же із греками мир ствердив, відпустив послів</p>\r\n<p>І одарував їх хутром, челяддю і воском.</p>\r\n<p>І відпустив їх.</p>\r\n<p>І прийшли посли до свого царя,</p>\r\n<p>І розповіли про все, що Ігор говорив їм,</p>\r\n<p>І про його любов до греків.</p>\r\n<p>І почав у Києві князь Ігор княжити,</p>\r\n<p>І мир мав із усіма країнами.</p>\r\n<p>І коли підійшла осінь,</p>\r\n<p>Ігор на древлян похід задумав,</p>\r\n<p>Забажав з них дань ще більшу взяти.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6453. Ркоша дружина Игореви: &laquo;отроци Св<small>Ђ</small>нделжи изоод<small>Ђ</small>л<small>Ђ</small>ся суть оружьемь и порты, а мы нази; и поиди, княже, с нами в дань, да и ты добудешь и мы&raquo;. И послуша ихъ Игорь, иде в Дерева в дань, и примысляше къ п<small>Ђ</small>рвой дани, и насиляще имъ, и мужи его; и возмя дань, и поиде въ свой городъ. Идущю же ему въспять, размысли рече дружин<small>Ђ</small> своей: &laquo;идите вы с данью домови, а язъ възвращюся и похожю еще&raquo;. И пусти дружину свою домови, с маломъ же дружины възвратися, желая болшая им<small>Ђ</small>нья. Слышавше же Древляне, яко опять идеть, съдумавше Древляне съ княземъ своимъ Маломъ и ркоша: &laquo;аще ся въвадить волкъ въ овц<small>Ђ</small>, то относить по единой все стадо, аще не убьютъ его; тако и сий, аще не убьемъ его, то вси ны погубить&raquo;; и послаша к нему глаголюще: &laquo;почто идеши опять? поималъ еси вьсю дань&raquo;. И не послуша ихъ Игорь, и шедше из города Искорост<small>Ђ</small>ня противу Древляне, и убиша Игоря и дружину его: б<small>Ђ</small> бо ихъ мало. И погребенъ бысть Игорь; и есть могила его у Искоростиня города в Дерев<small>Ђ</small>хъ и до сего дни. Ольга же бяше в Киев<small>Ђ</small> съ сыномъ своимъ д<small>Ђ</small>тьскомъ Святославомъ, и кормилець б<small>Ђ</small> его Асмудъ и воевода б<small>Ђ</small> Свинделдъ, тоже отець Мьстишинъ. Ркоша же Деревлян<small>Ђ</small>: &laquo;се князя Рускаго убихомъ; поимемъ княгиню его Олгу за князь свой Малъ, и Святослава, и створимъ ему якоже хощемъ&raquo;. И послаша Деревляне лучьшии мужи свои, числомъ 20, в лодьи къ Ольз<small>Ђ</small>, и приста подъ Боричевомъ въ лодьи Б<small>Ђ</small> бо тогда вода текущи возл<small>Ђ</small> горы Кьевьскыя и на Подол<small>Ђ</small> не с<small>Ђ</small>дяхуть людье, но на гор<small>Ђ</small>; городъ же бяше Киевъ, идеже есть нын<small>Ђ</small> дворъ Гордятинъ и Никифоровъ, а дворъ кьняжь бяше в город<small>Ђ</small>, идеже есть нын<small>Ђ</small> дворъ Воротиславль и Чюдинь, а перев<small>Ђ</small>сище б<small>Ђ</small> вн<small>Ђ</small> города, [и б<small>Ђ</small> вн<small>Ђ</small> города] дворъ теремный и другый, идеже есть дворъ демесниковъ, за святою Богородицею надъ горою, б<small>Ђ</small> бо ту теремъ каменъ. И пов<small>Ђ</small>даша Олз<small>Ђ</small>, яко Деревляни придоша, и възва Ольга к соб<small>Ђ</small> и рече имъ: &laquo;добр<small>Ђ</small> гостье приидоша&raquo;. И ркоша Древляне: &laquo;придохомъ княгини&raquo;. И рече имъ Ольга: &laquo;да глаголите, что ради приидосте с<small>Ђ</small>мо?&raquo; И ркоша Деревляни: &laquo;посла ны Деревьская земля, ркущи сице: мужа твоего убихомъ, бяшеть бо мужь твой яко волкъ въсхыщая и грабя, а наши князи добри суть, иже роспасли суть Деревьскую землю; да иди за нашь князь за Малъ&raquo;; б<small>Ђ</small> бо ему имя Малъ, князю Деревьскому. Рече же имъ Олга: &laquo;люба ми есть р<small>Ђ</small>чь ваша, уже мн<small>Ђ</small> своего князя не кр<small>Ђ</small>сити; но хощю вы почтити на утьр<small>Ђ</small>я предъ людми своими, а нын<small>Ђ</small> идите в лодью свою, и лязьте в лодьи величающеся; азь утро пошлю по вы, вы же речете: не <small>Ђ</small>демь ни на конехъ, ни пеши идемъ, но понесете ны в лодьи; и вьзънесутъ вы в лодьи&raquo;. И отпусти я в людью. Ольга же повел<small>Ђ</small> ископати яму велику и глубоку, на двор<small>Ђ</small> теремьскомъ, вн<small>Ђ</small> города. И заутра Ольга, с<small>Ђ</small>дящи в терем<small>Ђ</small>, посла по гости, и приидоша -к нимъ глаголюще:</p>\r\n<p>&laquo;зоветь вы Ольга на честь велику&raquo;. Они же ркоша: &laquo;не <small>Ђ</small>демъ ни на конехъ, ни на воз<small>Ђ</small>хъ, ни п<small>Ђ</small>шь идемъ, но понесите ны в лодьи&raquo;. Ркоша же Кияне: &laquo;намъ неволя; князь наш убитъ, а княгини наша хощеть за вашь князь&raquo;, и понесоша я в лодьи. Они же сидяху в перегр<small>Ђ</small>б<small>Ђ</small>хъ и въ великихъ сустогахъ гордящеся; и принесоша я на дворъ къ Ольз<small>Ђ</small>, и несъше я и вринуша въ яму и съ лодьею. И приникши Олга, и рече имъ: &laquo;добьра ли вы честь&raquo;? Они же ркоша: &laquo;пуще ны Игоревы см<small>Ђ</small>рти&raquo;. И повел<small>Ђ</small> засыпати я живы, и посыпаша я. И пославши Олга к Деревляномъ, рече: &laquo;да аще мя право просите, то пришлите къ мн<small>Ђ</small> мужи нарочиты, да въ велице чести пойду за вашь князь, еда не пустять мене людие Киевьсции&raquo;. Се слышавше Древляне, изъбраша лучьшая мужи, иже д<small>Ђ</small>ржатъ Деревьскую землю, и послаша по ню. Деревляномъ же прішедъшимъ, повел<small>Ђ</small> Олга мовницю створити, ркущи сице: &laquo;измывшеся придета къ мн<small>Ђ</small>&raquo;. Они же пережьгоша мовницю, и вл<small>Ђ</small>зоша Древляне, и начаша мытися; и запроша мовницю о нихъ, и повел<small>Ђ</small> зажечи я отъ дв<small>Ђ</small>рий, и ту изгор<small>Ђ</small>ша вси. И посла къ Деревляномъ, ркущи сице: &laquo;се уже иду к вамъ, да пристройте меды мьногы у города, идеже убисте мужа моего, да поплачюся надъ гробомъ его, и створю трызну мужю моему&raquo;. Они же слышавше, свезоша меды многы з<small>Ђ</small>ло. Олга же поемши мало дружин<small>Ђ</small>, и легько идущи, приде къ гробу его и плакася по мужи своемъ; и повелъ людемъ съсути могилу велику, и яко съспоша повел<small>Ђ</small> трызну творити. По семъ с<small>Ђ</small>доша Деревлян<small>Ђ</small> пити, и повел<small>Ђ</small> Олга отрокомъ своимъ служити передъ ними; и ркоша Деревляне къ Олз<small>Ђ</small>: &laquo;кд<small>Ђ</small> суть друз<small>Ђ</small> наши, ихъже послахомъ по тя&raquo;? Она же рече: &laquo;идуть по мн<small>Ђ</small> съ дружиною мужа моего&raquo;. И яко упишася Древляне, повел<small>Ђ</small> отрокомъ своимъ пити на ня, а сама отъиде прочь, и потомъ повел<small>Ђ</small> отрокомъ с<small>Ђ</small>чи я. И исъс<small>Ђ</small>коша ихъ 5000; а Ольга възвратися къ Киеву и пристрои воя на прокъ ихъ.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6453 [945] Ігореві мовила дружина:</p>\r\n<p>&laquo;Отроки Свиндельжі оружжям і в порти ізоділись,</p>\r\n<p>А ми голі. І піди, князю, з нами по дань,</p>\r\n<p>І ти добудеш, і ми&raquo;.</p>\r\n<p>І послухав Ігор їх - пішов по дань в Дерева</p>\r\n<p>І до першої нову ще дань примислив,</p>\r\n<p>І насилля їм з мужами своїми чинив.</p>\r\n<p>І зібрав він дань, і в город свій пішов.</p>\r\n<p>І коли назад вертався, то розмислив</p>\r\n<p>І рече своїй дружині: &laquo;Ви ідіть додому з данню,</p>\r\n<p>Я ж вернуся й походжу іще&raquo;.</p>\r\n<p>І дружину відпустив додому,</p>\r\n<p>Сам з дружиною малою повернувся,</p>\r\n<p>Бо бажав ще більшого багатства.</p>\r\n<p>І почули древляни, що йде [Ігор] знову,</p>\r\n<p>І тримали раду з князем своїм Малом,</p>\r\n<p>І сказали: &laquo;Якщо вовк увадиться по вівці,</p>\r\n<p>По одній все виносить він стадо, коли не уб\'ють його,</p>\r\n<p>Так і цей: як не вб\'ємо його, то нас усіх погубить&raquo;</p>\r\n<p>І послали Ігорю сказати: &laquo;Пощо знову йдеш?</p>\r\n<p>Всю дань забрав же&raquo;.</p>\r\n<p>І не послухав їх Ігор.</p>\r\n<p>Із города Іскоростеня вийшли проти нього древляни</p>\r\n<p>І убили Ігоря і дружину його, бо було їх мало,</p>\r\n<p>І поховали вони Ігоря в Деревах,</p>\r\n<p>І донині є його могила біля Іскоростеня.</p>\r\n<p>Ольга ж була в Києві із сином своїм, дитям Святославом,</p>\r\n<p>І кормилець був його Асмуд,</p>\r\n<p>І був Свиндельд-воєвода, він же батько Мстишин.</p>\r\n<p>Кажуть деревляни: &laquo;Це убили руського ми князя, А жону його, княгиню Ольгу, одружимо з князем своїм Малом,</p>\r\n<p>І Святослава [візьмемо]; і йому те зробимо, що схочем&raquo;</p>\r\n<p>&nbsp;</p>\r\n<p>І послали деревляни в лодії двадцять мужів щонайлуччих до Ольги І пристали під Боричевим в лодії. Бо текла тоді вода біля гори Київської і на Подолі не сиділи люди, але на горі. Город же був Київ там, де нині двір Гордятин й Никифорів, а двір княжий був у городі, де нині двір Воротиславлів і Чудинів, а перевісище було за городом, і був за городом двір теремний і другий, де сьогодні двір демесника - за святою Богородицею над горою, був там терем кам\'яний.</p>\r\n<p>&nbsp;</p>\r\n<p>І сказали Ользі, що прийшли деревляни,</p>\r\n<p>І покликала Ольга їх до себе</p>\r\n<p>І сказала: &laquo;Добрі прийшли гості&raquo;.</p>\r\n<p>І відповіли деревляни: &laquo;Ми прийшли, княгине&raquo;.</p>\r\n<p>І рече їм Ольга: &laquo;Так скажіте, чого ради ви прийшли сюди?&raquo;</p>\r\n<p>І деревляни кажуть їй: &laquo;Нас земля послала Деревська.</p>\r\n<p>Щоб таке сказати: мужа твого вбили,</p>\r\n<p>Бо твій муж як вовк нас обкрадав і грабив,</p>\r\n<p>А наші князі добрі, бо збагатили Деревську землю.</p>\r\n<p>То іди за князя нашого, за Мала&raquo;,</p>\r\n<p>Бо було ім\'я йому - Мал, князю Деревському&raquo;.</p>\r\n<p>І каже їм Ольга: &laquo;Люба мені мова ваша,</p>\r\n<p>Уже мені свого князя не воскресити, але вас хочу почтити</p>\r\n<p>Завтра ранком перед моїм людом.</p>\r\n<p>А нині ж в лодію свою ідіте</p>\r\n<p>І у ній сідайте, величаючись;</p>\r\n<p>А я ранком пошлю за вами, ви ж кажіть:</p>\r\n<p>&laquo;Не поїдемо на конях і пішо не підем,</p>\r\n<p>Але в лодії нас понесіте&raquo;.</p>\r\n<p>І вознесуть вас у лодії&raquo;.</p>\r\n<p>І до лодії їх відпустила.</p>\r\n<p>І велику і глибоку викопати яму</p>\r\n<p>Ізвеліла Ольга на теремному дворі за городом.</p>\r\n<p>І як ранок настав, Ольга, у теремі сидячи, за гостями послала.</p>\r\n<p>І прийшли до них [посланці] й кажуть:</p>\r\n<p>&laquo;Кличе вас Ольга на честь велику&raquo;.</p>\r\n<p>Вони ж кажуть: &laquo;Не поїдем ні на конях, ні возами,</p>\r\n<p>І пішо не підемо - несіть нас в лодії&raquo;.</p>\r\n<p>І кияни кажуть: &laquo;Нам неволя, князь наш вбитий,</p>\r\n<p>А княгиня наша хоче за вашого князя&raquo;.</p>\r\n<p>І понесли їх у лодії. Вони ж сиділи в перегребах,</p>\r\n<p>І з великими сустогами й гордились.</p>\r\n<p>І принесли їх у двір до Ольги,</p>\r\n<p>І як несли, так і вергли з лодією в яму.</p>\r\n<p>І, до них схилившись, рече Ольга:</p>\r\n<p>&laquo;Чи добра вам честь?&raquo;</p>\r\n<p>І вони відповіли: &laquo;Гірше нам, ніж Ігорева смерть&raquo;.</p>\r\n<p>І звеліла засипать їх живими,</p>\r\n<p>І засипали їх.</p>\r\n<p>І послала Ольга до деревлян сказати:</p>\r\n<p>&laquo;І якщо справді мене просите,</p>\r\n<p>То пришліть до мене мужів нарочитих, Щоб з великою честю за вашого князя піти,</p>\r\n<p>Бо інакше не пустять мене люди київськії&raquo;.</p>\r\n<p>Як почули це деревляни, найлуччих мужів відібрали,</p>\r\n<p>Що держать Деревськую землю,</p>\r\n<p>І послали за нею.</p>\r\n<p>І коли прийшли [у Київ] деревляни, Ольга мовницю велить їм зготувати</p>\r\n<p>І рече таке: &laquo;Вимиєтесь - прийдете до мене&raquo;,</p>\r\n<p>Їм мовницю добре натопили</p>\r\n<p>І, забравшись в неї, деревляни стали митись</p>\r\n<p>І заперли мовницю за ними,</p>\r\n<p>І палить її від дверей звеліла [Ольга],</p>\r\n<p>І там всі вони згоріли.</p>\r\n<p>І послала до деревлян так сказати:</p>\r\n<p>&laquo;Це до вас уже іду я,</p>\r\n<p>І медів багато приготуйте біля города, де мужа мого вбили,</p>\r\n<p>І поплачу над гробом його,</p>\r\n<p>І створю тризну по мужі своєму&raquo;.</p>\r\n<p>Вони ж, почувши про це, назвозили медів дуже багато.</p>\r\n<p>Ольга ж взяла малу дружину</p>\r\n<p>І, легко ідучи, прийшла до гробу Ігоря.</p>\r\n<p>І оплакала мужа свого,</p>\r\n<p>І звеліла людям своїм насипати високу могилу,</p>\r\n<p>І, коли насипали, повеліла тризну сотворити.</p>\r\n<p>І як сіли деревляни пити,</p>\r\n<p>Ольга отрокам своїм звеліла, щоб прислужували їм.</p>\r\n<p>І спитали деревляни Ольгу: &laquo;Де є друзі наші,</p>\r\n<p>Яких ми по тебе посилали?&raquo;</p>\r\n<p>Вона ж рече: &laquo;Йдуть за мною з дружиною мужа мого&raquo;.</p>\r\n<p>І коли впилися деревляни,</p>\r\n<p>Отрокам княгиня пити за них повеліла,</p>\r\n<p>А сама відійшла геть</p>\r\n<p>І потім отрокам наказала їх посікти.</p>\r\n<p>І посікли їх п\'ять тисяч, а Ольга повернулась в Київ</p>\r\n<p>І зібрала військо проти них.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&nbsp;</p>\r\n<p><em>Начало княженья Святославьля.</em></p>\r\n<p>&nbsp;</p>\r\n<p>В л<small>Ђ</small>то 6454. Ольга съ сыномъ Святославомъ събра вои многы и храбры, и иде на Деревьскую землю. И изыдоша Древляне противу; и снемъшемася об<small>Ђ</small>ма полкома на купь, суну копьемъ Святославъ на Деревляны, и копье лет<small>Ђ</small> въсквози уши коневи, и ударі в ногы коневи: б<small>Ђ</small> бо в<small>Ђ</small>льми д<small>Ђ</small>тескъ. И рече Свенгелдъ и Асмудъ: &laquo;князь уже почалъ; потягнемъ, дружино, по князи&raquo;. И поб<small>Ђ</small>диша Деревьляны; Деревлян<small>Ђ</small> же поб<small>Ђ</small>гоша и затворишася в город<small>Ђ</small>хъ своихъ. Ольга же устр<small>Ђ</small>мися съ сыномъ своимъ на Искорост<small>Ђ</small>нь городъ, яко т<small>Ђ</small> бяху убил<small>Ђ</small> мужа ея, и ста около города съ сыномъ, а Деревляне затворишася в город<small>Ђ</small> и боряху кр<small>Ђ</small>пько из города, в<small>Ђ</small>даху бо, яко сами убил<small>Ђ</small> князя и на что ся предати. И стоя Ольга л<small>Ђ</small>то ц<small>Ђ</small>ло, и не можаше взяти города, и умысли сице: посла къ городу ркущи: &laquo;чего хощете дос<small>Ђ</small>д<small>Ђ</small>ти? а вси ваши городи передашася мн<small>Ђ</small>, и ялися по дань, и д<small>Ђ</small>лаютъ нивы своя и землю свою, а вы хощете голодомъ измерети, не имучися по дань&raquo;. Деревляне же рькоша: &laquo;ради быхомъ ся яли по дань, но хощеши мьщати мужа своего&raquo;. Рече же имъ Ольга: &laquo;яко азъ уже мьстила есмь мужа своего, когда придоша къ Киеву, и второе, и третьее, еже когда творяхуть трызъну мужю моему; а уже не хощю отмщения творить, но хощю дань имати по малу, и смирившися с вами, пойду опять&raquo;. Ркоша же Древляне: &laquo;что хощеши у насъ? ради даемъ и медомъ и скорою&raquo;. Она же рече имъ: &laquo;нын<small>Ђ</small> у васъ н<small>Ђ</small>ту меду, ни скоры, но мала у васъ прошю: дайте ми отъ двора по три голуби и по три воробьи; азъ бо не хощю тяжькы дани възложити на васъ, якоже мужь мой, но сего у васъ прошю мала, изнемогли бо ся есте въ осад<small>Ђ</small>; да вдайте ми се малое&raquo;. Деревляне же ради быша, събраша же отъ двора по три голуби и по три воробьи, и послаша къ Ольз<small>Ђ</small> с поклономъ. Ольга же рече &laquo;се уже ся есте покорил<small>Ђ</small> мн<small>Ђ</small> и моему д<small>Ђ</small>тяти, а идете в городъ, а язъ заутра отступлю отъ города и пойду в городъ свой&raquo;. Деревьляне же ради. быша, вънидоша в городъ, и пов<small>Ђ</small>даша людемъ; и обрадовашася людье в город<small>Ђ</small>. Ольга же раздая воемъ комуждо по голуби, а дьругимъ по воробьеви, и повел<small>Ђ</small> къемуждо голубеви и воробьеви привязати ч<small>Ђ</small>рь, и об<small>Ђ</small>рьтываючи въ плткы малы, нитъкою пов<small>Ђ</small>рьзаючи къ вс<small>Ђ</small>мъ голубемъ и воробьемъ; и повел<small>Ђ</small> Ольга, яко см<small>Ђ</small>рчеся, пустити голубі и воробии воемъ своимъ. Голуби же и воробьеве полет<small>Ђ</small>ша въ гн<small>Ђ</small>зда своя, ови в голубникы своя, воробьеве же подъ остр<small>Ђ</small>хы; и тако загарахуться голубници, и отъ нихъ кл<small>Ђ</small>ти и одрины, и не б<small>Ђ</small> двора, идеже не горяше, и не б<small>Ђ</small>льз<small>Ђ</small> гасити, вси бо двор<small>Ђ</small> взгор<small>Ђ</small>шася. И поб<small>Ђ</small>гоша людье из города, и повел<small>Ђ</small> Олга воемъ своимъ имати я; и яко взя городъ и пожьже и, стар<small>Ђ</small>йшины же города ижьже, и прочая люди ов<small>Ђ</small>хъ изби, а другия работ<small>Ђ</small> преда мужемъ своимъ, а прокъ остави ихъ платити дань, и възложи на ня дань тяжьку, и дв<small>Ђ</small> часті идета Киеву, а третьяя Вышегороду къ Ольз<small>Ђ</small>; б<small>Ђ</small> бо Вышегородъ Ольжинъ городъ. И иде Олга по Деревьской земли съ сыномъ своимъ и дружиною своею, уставляющи уставы и урокы; и суть становища ея и ловища ея. И приде в городъ свой Киевъ съ сыномъ своимъ Святославомъ, и пребывши л<small>Ђ</small>то едино</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>&nbsp;</p>\r\n<p><em>Початок князювання Святослава.</em></p>\r\n<p>&nbsp;</p>\r\n<p>В літо 6454 [946].</p>\r\n<p>Ольга з [своїм] сином Святославом</p>\r\n<p>Військо велике й хоробре зібрала</p>\r\n<p>І пішла на Деревську землю.</p>\r\n<p>І супроти вийшли древляни.</p>\r\n<p>І зійшлися обидва війська докупи,</p>\r\n<p>І метнув списа Святослав на деревлян&raquo;</p>\r\n<p>І пролетів спис поміж вуха коневі,</p>\r\n<p>І ударив йому у ноги,</p>\r\n<p>Бо був [Святослав] ще дитиною.</p>\r\n<p>І сказали Свингельд і Асмуд: &laquo;Князь уже почав!</p>\r\n<p>Ударимо і ми, дружино, за князем!&raquo;</p>\r\n<p>І перемогли деревлян,</p>\r\n<p>І древляни побігли,</p>\r\n<p>І зачинилися в городах своїх.</p>\r\n<p>Ольга ж кинулася з сином своїм</p>\r\n<p>На город Іскоростінь, бо ті були мужа її вбили.</p>\r\n<p>І стала побіля города з сином своїм,</p>\r\n<p>А деревляни в городі зачинилися</p>\r\n<p>І кріпко із города боронилися,</p>\r\n<p>Знали-бо, що самі убили князя</p>\r\n<p>І немає на що їм надіятися.</p>\r\n<p>І стояла Ольга ціле літо,</p>\r\n<p>І не змогла Іскоростінь взяти.</p>\r\n<p>І намислила так: послала в город сказати:</p>\r\n<p>&laquo;Ви чого хочете досидіти?</p>\r\n<p>Ваші городи вже всі мені здалися,</p>\r\n<p>І згодились на дань,</p>\r\n<p>І ниви і землю свою обробляють,</p>\r\n<p>А ви хочете з голоду померти,</p>\r\n<p>Не хочучи данину платити&raquo;.</p>\r\n<p>Деревляни ж їй відповідали: &laquo;Раді б ми відбутись даниною,</p>\r\n<p>Але ж ти за мужа хочеш метатись&raquo;.</p>\r\n<p>Ольга ж їм сказала: &laquo;Я уже за мужа відомстила,</p>\r\n<p>Коли в Києві ви перший раз і вдруге побували,</p>\r\n<p>Третій раз я помсту вам вчинила,</p>\r\n<p>Як по мужу тризну творили.</p>\r\n<p>Більше вже помщатися не хочу,</p>\r\n<p>Але хочу тільки взяти із вас дань помірну</p>\r\n<p>Й, замирившись з вами, геть піду&raquo;.</p>\r\n<p>Запитали деревляни: &laquo;Що од нас ти хочеш?</p>\r\n<p>Раді дать тобі меди і хутра&raquo;.</p>\r\n<p>А вона на це їм відказала:</p>\r\n<p>&laquo;Нині в вас немає меду ані хутра,</p>\r\n<p>Але хочу з вас я небагато:</p>\r\n<p>По три голуби і горобці мені від двору дайте,</p>\r\n<p>Бо не хочу дань тяжку на вас я накладати,</p>\r\n<p>Як мій муж. А тому й прошу у вас я небагато,</p>\r\n<p>Бо в осаді ви вже знемоглися,</p>\r\n<p>Тож мені оце малеє дайте&raquo;.</p>\r\n<p>Деревляни ж були раді,</p>\r\n<p>По три голуби і горобці з двору зібрали</p>\r\n<p>І з поклоном Ользі послали.</p>\r\n<p>Ольга ж їм сказала:</p>\r\n<p>&laquo;От уже ви мені і моєму дитяті скорились,</p>\r\n<p>Ідіть у свій город, а я завтра відступлю від нього</p>\r\n<p>І у свій город піду&raquo;.</p>\r\n<p>Деревляни ж були дуже раді,</p>\r\n<p>Ввійшли в город і звістили людям,</p>\r\n<p>І всі люди в городі зраділи.</p>\r\n<p>Ольга ж кожному із воїв по голубу роздавала,</p>\r\n<p>А іншим - по горобцю.</p>\r\n<p>І звеліла кожному голубові і горобцю чір прив\'язати</p>\r\n<p>І в шматки тканини огортати,</p>\r\n<p>І ниткою голубам і горобцям в\'язати.</p>\r\n<p>І як смеркло, Ольга своїм воям повеліла</p>\r\n<p>Голубів і горобців пустити.</p>\r\n<p>Голуби ж і горобці в свої гнізда полетіли:</p>\r\n<p>Одні в голубники свої, горобці ж - під стріхи.</p>\r\n<p>І так загорілись голубники,</p>\r\n<p>І від них спалахнули кліті і одрини,</p>\r\n<p>І не було двору, де б не горіло,</p>\r\n<p>І не можна було гасити, бо усі двори загорілися.</p>\r\n<p>І побігли люди із города,</p>\r\n<p>І звеліла Ольга воям своїм хапати їх.</p>\r\n<p>І коли взяла город - спалила його,</p>\r\n<p>І старійшин же города спалила,</p>\r\n<p>І інших людей тих перебила,</p>\r\n<p>А інших в рабство мужам своїм віддала,</p>\r\n<p>А решту, щоб данину платили, залишила,</p>\r\n<p>І поклала на них дань тяжку.</p>\r\n<p>І дві частини [її] ішли Києву,</p>\r\n<p>А третя - Вишегороду, для Ольги,</p>\r\n<p>Був-бо Вишегород Ольжин город.</p>\r\n<p>І пішла Ольга по Деревській землі з сином своїм</p>\r\n<p>І з дружиною своєю, визначаючи устави і уроки,</p>\r\n<p>І донині є місця її становищ і ловищ її.</p>\r\n<p>І прийшла в город свій Київ з сином своїм Святославом,</p>\r\n<p>І пробула [в ньому] одне літо.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6455 иде Олга к Новугороду и устави по Мьст<small>Ђ</small> погосты и дань, и по Луз<small>Ђ</small> погосты и дань и оброкы; и ловища ея суть по всей земли, и знамения и м<small>Ђ</small>ста и погосты, [и сани ея стоять въ Плесков<small>Ђ</small> и до сего дне], и по Дн<small>Ђ</small>пру перев<small>Ђ</small>сища и по Десн<small>Ђ</small>, и есть село ея Ольжичи и до сего дни. Изрядивши, възвратися къ сыну своему в Киевъ и пребываше с нимъ въ любви.</p>\r\n<p>В л<small>Ђ</small>то 6456.</p>\r\n<p>В л<small>Ђ</small>то 6457.</p>\r\n<p>В л<small>Ђ</small>то 6458.</p>\r\n<p>В л<small>Ђ</small>то 6459.</p>\r\n<p>В л<small>Ђ</small>то 6460.</p>\r\n<p>В л<small>Ђ</small>то 6461.</p>\r\n<p>В л<small>Ђ</small>то 6462.</p>\r\n<p>В л<small>Ђ</small>то 6463. Иде Олга въ Гр<small>Ђ</small>кы, и приде к Царюграду. И б<small>Ђ</small> тогда Костянтинъ сынъ Леонтовъ, и вид<small>Ђ</small>въ ю добру сущю лицемъ и смыслену велми, и удивися царь разуму ея, бес<small>Ђ</small>дова к ней и рекъ ей: &laquo;подобна еси царствовати в город<small>Ђ</small> семъ с нами&raquo;. Она же разум<small>Ђ</small>вши, и рече къ царю: &laquo;азъ погана есмь, да аще меня хощеши крестити, то кр<small>Ђ</small>сти мя самъ; аще ли, то не крешуся&raquo;. И крести ю царь с патриархомъ. Просв<small>Ђ</small>щена же бывши радовашеся душею и т<small>Ђ</small>ломъ; и поучи ю патриархъ о в<small>Ђ</small>р<small>Ђ</small>, и рече ей: &laquo;благословена ты еси в Руськыхъ князехъ, яко възлюби св<small>Ђ</small>тъ, а тму остави; благословити тя имуть сынове Рустии и въ посл<small>Ђ</small>дний родъ внукъ твоихъ&raquo;. И запов<small>Ђ</small>да ей о церковнемъ устав<small>Ђ</small>, и о молитв<small>Ђ</small> и пост<small>Ђ</small>, и о милостыни, и о възд<small>Ђ</small>ржании т<small>Ђ</small>ла чиста; она же, поклонивши главу, стояше аки губа напаяема, внимающи ученью, и поклонившися патриарху, глаголаше: &laquo;молитвами твоими, владыко, да съхранена буду отъ с<small>Ђ</small>ти неприязнены&raquo;. Б<small>Ђ</small> же имя ей наречено въ крещении Олена, якоже и древняя царица, мати велікого Костянтина. И благослови ю патриархъ, и отпусти ю. И по крещении призва ю царь и рече ей: &laquo;хощю тя поняти жен<small>Ђ</small>&raquo;. Она же рече: &laquo;како мя хощеши поняти, а кр<small>Ђ</small>стивъ мя самъ и нарекъ мя дщерь? а въ крестьян<small>Ђ</small>хъ того н<small>Ђ</small>сть закона, а ты самъ в<small>Ђ</small>си&raquo;. И рече царь: &laquo;переклюка мя, Олга&raquo;; и вдасть ей дары многы, золото и серебро, паволокы, съсуды разноличныя, и отпусти ю, нарекъ ю дщерь себ<small>Ђ</small>. Она же хотячи домови, приде къ патриарху, благословения просящи на домъ, и рече ему: &laquo;людье мои погани и сынъ мой, да бы мя Богъ съблюлъ отъ вьсякого зла&raquo;. И рече патриархъ: &laquo;чадо в<small>Ђ</small>рное! въ Христа кр<small>Ђ</small>стилася еси и въ Христа облечеся, и Христосъ съхранить тя, якоже съхрани Еноха в п<small>Ђ</small>рвыя роды, потомъ Ноя в ковчез<small>Ђ</small>, Аврама отъ Авимелеха, Лота отъ Содомлянъ, Моисея отъ Фараона, Давида отъ Саула, три отрокы отъ пещи, Данила отъ зв<small>Ђ</small>рий, тако и тебе избавить отъ неприязни и с<small>Ђ</small>тий его&raquo;. И благослови ю патриархъ, и иде с миромъ в землю свою, и приде къ Киеву. Се же бысть, яко и при Соломони, приде царица Ефиопьская, слышати хотящи мудрость Соломоню, многу мудрость вид<small>Ђ</small>ти и зънамения: тако и си блащная Олга искаше добрые мудрости Божия, но она челов<small>Ђ</small>цьскыя, а си Божия. Ищющи бо премудрости обрящутъ. Премудрость на исходящихъ поеться, на путехъ же д<small>Ђ</small>рзновение водить, на краихъ же ст<small>Ђ</small>нъ забралныхъ пропов<small>Ђ</small>дается, въ врат<small>Ђ</small>хъ же градныхъ д<small>Ђ</small>рзающи глаголеть; елико бо л<small>Ђ</small>тъ незлобивии д<small>Ђ</small>ржатся по пьравду. И си бо отъ възраста блаженая Олена искаше мудростью, что есть луче всего, въ св<small>Ђ</small>т<small>Ђ</small> семъ, и нал<small>Ђ</small>зе бисеръ многоц<small>Ђ</small>ньный, еже есть Христосъ. Рече бо Соломонъ: желанье благов<small>Ђ</small>рныхъ наслажаеть душю; и приложиши сердце свое в разумъ, азъ бо любящая мя люблю, а ищющии мене обрящють мя. Но Господь рече: приходящаго къ мн<small>Ђ</small> не иждену вонъ. Си же Ольга приде къ Киеву, и якоже рькохомъ, и присла к ней царь Грецкый, глаголя: &laquo;яко много дарихъ тя; ты же глаголала ми, яко аще възращюся в Русь, многы дары послю ти, челядь и воскъ и скору, и воя многы в помощь&raquo;. Отв<small>Ђ</small>щавши же Олга рече къ посломъ: &laquo;аще ты&raquo;, рци, &laquo;такоже постоиши у мене в Почайн<small>Ђ</small>, якоже азъ в Суду, то тогда ти вдамъ&raquo;. И отпусти послы си рекши. Живяше же Олга съ сыном своимъ Святославомъ, и учашеть его мати креститися, и не брежаше того, ни въ уши внимаше; но аще кто хотяше волею креститися, не браняху, но ругахуся тому. Нев<small>Ђ</small>рнымъ бо в<small>Ђ</small>ра крестьянская уродьство есть; не смыслиша бо, ни разум<small>Ђ</small>ша въ тм<small>Ђ</small> ходящии, и не вид<small>Ђ</small>ша славы Господня, одебел<small>Ђ</small>ша бо сердца ихъ, и ушима бо тяшько слышаша, очима вид<small>Ђ</small>ти. Рече бо Соломонъ: д<small>Ђ</small>латель нечестивыхъ далече отъ разума; понеже звахъ вы, и не послушасте, и прострохъ словеса, и не разум<small>Ђ</small>сте, но отм<small>Ђ</small>тасте моя с [ъ] в<small>Ђ</small>ты и моихъ же обличений не внимасте; възненавид<small>Ђ</small>ша бо премудрость, а страха Господня не изволиша, ни хотяху моихъ внимати с [ъ] в<small>Ђ</small>тъ, подражаху же моя обличенія. Якоже Олга часто глаголаше: &laquo;азъ, сыну, Бога познахъ и радуюся; аще и ты познаеши Бога, то радоватися начнеши&raquo;. Онъ же не внимаше того, глаголя: &laquo;како азъ хощю инъ законъ одинъ приняти? а дружина моя сему см<small>Ђ</small>ятись начнуть&raquo;. Она же рече ему: &laquo;аще ты крестишися, вси имуть тоже творить&raquo;. Онъ же не послуша матери, и творяше норовы поганьскыя, не в<small>Ђ</small>дый, аще кто матери не слушаеть, в б<small>Ђ</small>ду впадае; якоже рече: аще кто отца или матерь не слушаеть, смертью да умреть. Се же тому гн<small>Ђ</small>вашеся на матерь. Соломонъ бо рече: кажа злыя, пріемлеть себе досажение; обличая нечестивого, поречеть себ<small>Ђ</small>; обличе^ния бо нечестивымъ мозолье имъ суть; не обличай злыхъ, да не възненавидять тебе. Но обаче любяше Олга сына своего Святослава, ркущи: &laquo;воля Божия да будеть, аще Богъ въсхощеть помиловати роду моего и земли Рускые, да възло[жи]ть имъ на сердце обратитися къ Богу, якоже и мн<small>Ђ</small> Богъ дарова&raquo;. И се рекши моляшеся за сына и за люди по вся дни и нощи, кормячи сына своего до мужьства его и до възъраста его.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6455 [947] пішла Ольга до Новугорода</p>\r\n<p>І встановила по Мсті погости і дань,</p>\r\n<p>І по Лузі погости, і дань, і оброки.</p>\r\n<p>І ловища її є по всій землі,</p>\r\n<p>І знамення, і місця, і погости</p>\r\n<p>[І сани її стоять в Плескові і донині],</p>\r\n<p>І по Дніпру перевісища, і по Десні,</p>\r\n<p>І є село її Ольжичі і до сьогодні.</p>\r\n<p>І, давши лад, повернулась до сина свого в Київ,</p>\r\n<p>І там жила з ним у згоді.</p>\r\n<p>&nbsp;</p>\r\n<p>В літо 6456 [948].</p>\r\n<p>В літо 6457 [949].</p>\r\n<p>В літо 6458 [950].</p>\r\n<p>В літо 6459 [951].</p>\r\n<p>В літо 6460 [952].</p>\r\n<p>В літо 6461 [953].</p>\r\n<p>В літо 6462 [954].</p>\r\n<p>&nbsp;</p>\r\n<p>В літо 6463 [955]. Пішла Ольга в Греки,</p>\r\n<p>І прийшла до Цареграда.</p>\r\n<p>І був тоді царем Костянтин, син Леонів,</p>\r\n<p>І видів її доброю, і вродливою, і розумною дуже,</p>\r\n<p>І здивувався цар розуму її,</p>\r\n<p>І, бесідуючи з нею, сказав їй:</p>\r\n<p>&laquo;Достойна ти царювать в городі цьому з нами&raquo;.</p>\r\n<p>І зрозуміла вона і рече царю: &laquo;Поганка я,</p>\r\n<p>І якщо хочеш мене охрестити, то хрести сам,</p>\r\n<p>А якщо ні, то не охрещуся&raquo;.</p>\r\n<p>І охрестив її цар з патріархом.</p>\r\n<p>І, ояснена бувши, радувалася душею і тілом,</p>\r\n<p>І наставляв її в вірі патріарх, і сказав їй:</p>\r\n<p>&laquo;Благословенна ти серед Руських князів,</p>\r\n<p>Бо возлюбила світло, а тьму залишила.</p>\r\n<p>Благословлятимуть тебе сини русьтії</p>\r\n<p>І в найдальшому роду твоїх внуків&raquo;,</p>\r\n<p>І дав їй заповіді про церковний устав,</p>\r\n<p>І про молитву, і піст, і про милостиню,</p>\r\n<p>І про дотримання тілесної чистоти,</p>\r\n<p>І стояла вона, схиливши голову,</p>\r\n<p>І як губка, вбирала, у повчання вслухалась.</p>\r\n<p>І, вклонившись патріархові, говорила:</p>\r\n<p>&laquo;Молитвами твоїми, владико,</p>\r\n<p>Хай убережена буду від сіті ворожої&raquo;.</p>\r\n<p>І було дано в хрещенні їй ім\'я Олена,</p>\r\n<p>Як і стародавній цариці, матері великого Костянтина.</p>\r\n<p>І благословив її патріарх, і відпустив.</p>\r\n<p>І після хрещення прикликав її цар</p>\r\n<p>І рече їй: &laquo;Хочу тебе в жони взяти!&raquo;</p>\r\n<p>І рече вона: &laquo;Як-то мене хочеш взяти:</p>\r\n<p>І хрестив мене сам, і дочкою назвав?</p>\r\n<p>А в християн немає такого закону,</p>\r\n<p>І ти сам [це] знаєш&raquo;.</p>\r\n<p>І рече цар: &laquo;Переклюкала мене, Ольго&raquo;.</p>\r\n<p>І дав їй дари численні: зрлото, і срібло,</p>\r\n<p>І паволоки, і сосуди різні,</p>\r\n<p>І відпустив її, назвавши дочкою своєю.</p>\r\n<p>Вона ж бо, хочучи додому, прийшла до патріарха,</p>\r\n<p>І просила благословити на дорогу додому,</p>\r\n<p>І сказала йому: &laquo;Люди мої - погани, і син мій,</p>\r\n<p>Тож хай збереже мене Бог від усілякого зла&raquo;.</p>\r\n<p>І рече патріарх: &laquo;Чадо вірнеє! У Христа ти хрестилась</p>\r\n<p>І в Христа одягнешся,</p>\r\n<p>І Христос збереже тебе, як Еноха зберіг у перші часи,</p>\r\n<p>І потім Ноя в ковчезі, Аврама від Авімелеха,</p>\r\n<p>І Лота від содомлян, Мойсея від фараона</p>\r\n<p>І Давида від Саула, трьох отроків від печі,</p>\r\n<p>І Данила від звірів,- так і тебе збавить від ворога</p>\r\n<p>І від сітей його&raquo;.</p>\r\n<p>І благословив її патріарх.</p>\r\n<p>І пішла вона з миром у землю свою,</p>\r\n<p>І прийшла в Київ.</p>\r\n<p>І було це, як і при Соломоні:</p>\r\n<p>Прийшла цариця Ефіопська, бажаючи почути мудрість Соломонову,</p>\r\n<p>І велику мудрість видіти і знамення.</p>\r\n<p>Так і ця блаженна Ольга шукала доброї мудрості божої,</p>\r\n<p>Але цариця ефіопська - людської, а Ольга - божої.</p>\r\n<p>&laquo;Ті, що шукають премудрість,- знайдуть&raquo;.</p>\r\n<p>&laquo;Премудрість на початках [дороги] співається,</p>\r\n<p>На шляхах дерзновення водить,</p>\r\n<p>На краю стін із заборолами проповідується,</p>\r\n<p>При воротях градних сміливо говорить;</p>\r\n<p>Скільки-бо років незлобивії тримаються правди?&raquo;</p>\r\n<p>І ця-бо від зрілості блаженна Ольга шукала мудрістю,</p>\r\n<p>Що є найліпше всього на світі цьому,</p>\r\n<p>І бісер знайшла многоцінний, яким є Христос.</p>\r\n<p>Рече-бо Соломон: &laquo;Бажання благовірних насолоджує душу,</p>\r\n<p>І прихилиш серце своє до розуму,</p>\r\n<p>Я-бо тих, що люблять мене, люблю,</p>\r\n<p>А ті, що шукають мене, знайдуть мене&raquo;.</p>\r\n<p>Бо рече Господь: &laquo;Того, хто приходить до мене, не прожену геть&raquo;.</p>\r\n<p>Сія ж Ольга прийшла до Києва, як було сказано,</p>\r\n<p>І прислав до неї цар грецький [послів] зі словами:</p>\r\n<p>&laquo;Я багато обдарував тебе. Ти ж говорила мені:</p>\r\n<p>&laquo;Коли я повернуся на Русь, багаті дари пошлю тобі:</p>\r\n<p>І челядь, і віск, і хутра,</p>\r\n<p>І воїв численних у поміч&raquo;.</p>\r\n<p>І у відповідь послам сказала Ольга:</p>\r\n<p>&laquo;Коли ти,- каже,- також попостоїш у мене в Почайні,</p>\r\n<p>Скільки я в Суду, то тоді дам тобі&raquo;.</p>\r\n<p>І послів, це сказавши, відпустила.</p>\r\n<p>Жила ж Ольга з сином своїм Святославом,</p>\r\n<p>І навчала його мати хреститися,</p>\r\n<p>А він не зважав на те, ані дослухався того,</p>\r\n<p>Але коли хто з власної волі хотів охреститися,</p>\r\n<p>То не забороняв, але осуджував того.</p>\r\n<p>Для невірних-бо віра християнська - то потворність,</p>\r\n<p>Бо не знають і не розуміють ті, хто в тьмі ходить,</p>\r\n<p>І не відають слави господньої,</p>\r\n<p>Бо одебеліли серця їхні,</p>\r\n<p>І тяжко вухами чують, а очима бачать.</p>\r\n<p>Рече-бо Соломон: &laquo;Діяч нечестивих далекий від розуму&raquo;,</p>\r\n<p>Бо кликала вас, і не послухали ви,</p>\r\n<p>І вам поклав слова - і не зрозуміли,</p>\r\n<p>Але ради мої ви відкинули,</p>\r\n<p>І звинувачень моїх не приймаєте,</p>\r\n<p>І зненавиділи-бо премудрість,</p>\r\n<p>І не захотіли страху господнього,</p>\r\n<p>І не хотіли приймати поради моєї,</p>\r\n<p>І погорджували всіма моїми звинуваченнями!&raquo;</p>\r\n<p>Так і Ольга часто говорила:</p>\r\n<p>&laquo;Я, сину, пізнала Бога і радуюся,</p>\r\n<p>А коли і ти пізнаєш Бога, то почнеш радуватися&raquo;.</p>\r\n<p>Він же не приймав того, кажучи:</p>\r\n<p>&laquo;Як я можу один іншу віру прийняти?</p>\r\n<p>Таж дружина моя з цього буде сміятись&raquo;.</p>\r\n<p>Вона ж рече йому: &laquo;Охрестишся ти -</p>\r\n<p>І всі інші те саме зроблять&raquo;.</p>\r\n<p>Він же матері не послухав і далі справляв звичаї поганські,</p>\r\n<p>Не відаючи, коли хто матері не слухає, в біду впадає,</p>\r\n<p>Як сказано: &laquo;Коли хто батька чи матір не слухає,</p>\r\n<p>Хай умре&raquo;.</p>\r\n<p>Це ж тому Святослав гнівався на матір.</p>\r\n<p>Соломон же сказав:</p>\r\n<p>&laquo;Хто картає насмішника, той собі ганьбу бере,</p>\r\n<p>Хто ж безбожникові виговорює, сором собі набуває&raquo;.</p>\r\n<p>&laquo;Картання безбожників - як мозоль для них&raquo;.</p>\r\n<p>&laquo;Не дорікай пересмішникові, щоб те\'бе не зненавидів він&raquo;.</p>\r\n<p>Одначе Ольга любила сина свого Святослава, кажучи:</p>\r\n<p>&laquo;Хай буде воля Божа. Коли Бог захоче помилувати рід мій</p>\r\n<p>І землі Руськії, хай вкладе їм у серце бажання Бога,</p>\r\n<p>Як то і мені Бог дарував&raquo;.</p>\r\n<p>І, це кажучи, молилася за сина</p>\r\n<p>І за людей всі дні і ночі,</p>\r\n<p>І наставляла сина свого до змужніння його</p>\r\n<p>І до його повноліття.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6464.</p>\r\n<p>В л<small>Ђ</small>то 6465.</p>\r\n<p>В л<small>Ђ</small>то 6466.</p>\r\n<p>В л<small>Ђ</small>то 6467.</p>\r\n<p>В л<small>Ђ</small>то 6468.</p>\r\n<p>В л<small>Ђ</small>то 6469.</p>\r\n<p>В л<small>Ђ</small>то 6470.</p>\r\n<p>В л<small>Ђ</small>то 6471.</p>\r\n<p>В л<small>Ђ</small>то 6472. Князю Святославу възрастьшю и възмужавшю, нача воя съвокупляти многы и храбры, б<small>Ђ</small> бо и самъ хоробръ и легокъ, ходя акы пардусъ, воины многы творяше. Возъ бо по себ<small>Ђ</small> не возяше, ни котла, ни мясъ варя, но потонку изр<small>Ђ</small>завъ конину, или зв<small>Ђ</small>рину, или говядину, на угълехь испекъ, ядяше; ни шатра имяше, но подъкладъ постилаше, а с<small>Ђ</small>дло въ головахъ; такоже и прочии вои его вси бяху. И посылаше къ странамъ глаголя: &laquo;хочю на вы ити&raquo;. И иде на Оку р<small>Ђ</small>ку и на Волгу, и нал<small>Ђ</small>зе [на] Вятичи, и рече имъ: &laquo;кому дань даете&raquo;? Они же ркоша: &laquo;Козаромъ по щелягу отъ рала даемъ&raquo;.</p>\r\n<p>В л<small>Ђ</small>то 6473. Иде Святославъ на Козары. Слышавше же Козаре, изыдоша протіву съ княземъ своимъ каганомъ, и съ [с]тупишася бити; и бывши брани межи ими, одол<small>Ђ</small> Святославъ Козаромъ и городъ ихъ Б<small>Ђ</small>лувежю взя. И Ясы поб<small>Ђ</small>ди и Касогы, и приде къ Киеву.</p>\r\n<p>В л<small>Ђ</small>то 6474. Поб<small>Ђ</small>ди Вятичь Святославъ, и дань на нихъ възложи.</p>\r\n<p>В л<small>Ђ</small>то 6475. Иде Святославъ на Дунай на Болъгары. И бившимъся, одол<small>Ђ</small> Святославъ Болгаромъ, и взя городовъ 80 по Дунаю; и с<small>Ђ</small>де княжя ту въ Переяславци, емля дань на Гр<small>Ђ</small>ц<small>Ђ</small>хъ.</p>\r\n<p>В л<small>Ђ</small>то 6476. Придоша Печенизи п<small>Ђ</small>рвое на Рускую землю, а Святославъ бяше в Переяславци; и затворися Ольга съ внукы своими, Ярополкомъ и Олгомъ и Володимеромъ, в город<small>Ђ</small> Киев<small>Ђ</small>. И оступиша Печенизи городъ в сил<small>Ђ</small> тяжьц<small>Ђ</small>, бещисленое множьство около города, и не б<small>Ђ</small> лз<small>Ђ</small> выл<small>Ђ</small>сти изъ града, ни в<small>Ђ</small>сти послаті; и изнемогаху людье гладомъ и водою. И събравшеся людье оноя страны Дн<small>Ђ</small>пьра, въ лодьяхъ, и об [ъ] ону страну стояху, и не б<small>Ђ</small> лз<small>Ђ</small> внити в Киевъ ни единому же ихъ, ни изъ города къ он<small>Ђ</small>мъ. И въстужиша людье в город<small>Ђ</small> и ркоша: &laquo;н<small>Ђ</small>сть ли кого, иже бы на ону страну моглъ дойти: аще не приступите утро подъ городъ, предатися имамъ Печен<small>Ђ</small>гомъ&raquo;? И рече одинъ отрокъ: &laquo;азъ могу преити&raquo;. Горожани же ради бывше, ркоша отроку: &laquo;аще можеши како ити, иди&raquo;. Онъ же изыде изъ града съ уздою, и хожаше сквозь Печен<small>Ђ</small>гы, глаголя: &laquo;не вид<small>Ђ</small> ли коня никтоже&raquo;? б<small>Ђ</small> бо ум<small>Ђ</small>я Печен<small>Ђ</small>скы, и мняхуть и своихъ. И яко приближися к р<small>Ђ</small>ц<small>Ђ</small>, св<small>Ђ</small>ргъ порты съ себе, сунуся въ Дн<small>Ђ</small>пръ и побр<small>Ђ</small>де; и вид<small>Ђ</small>вше Печен<small>Ђ</small>зи, устр<small>Ђ</small>мишася на нь стр<small>Ђ</small>ляюще его, и не могоша ему ничтоже створити. Они де вид<small>Ђ</small>вше съ оноя страны, при<small>Ђ</small>хавше в лодьи противу ему, взяша и въ лодью и привезоша и къ дружин<small>Ђ</small>; и рече имъ: &laquo;аще не подъступите заутра рано подъ городъ, предатися имуть людье Печен<small>Ђ</small>гомъ&raquo;. Рече же имъ воевода ихъ, именемъ Притичь: &laquo;подъступимъ заутра в людьяхъ и попадъше княгиню и княжичи умьчимъ на сю страну и люди; аще ли сего не створимъ, погубити ны имать Святославъ&raquo;. И яко бысть заутра, вс<small>Ђ</small>доша в лодья, противу св<small>Ђ</small>ту въструбиша велми трубами, и людье въ град<small>Ђ</small> кликоша. Печениз<small>Ђ</small> же мн<small>Ђ</small>ша князи пришедша, поб<small>Ђ</small>гоша розно отъ града; изыде Олга съ внукы и съ люд ми к лодьямъ. И вид<small>Ђ</small>въ же князь Печен<small>Ђ</small>жьскый, възвратися единъ къ воевод<small>Ђ</small> Притичю, и рече: &laquo;кто се приде&raquo;? И рече ему: &laquo;людье оноя страны&raquo;. И рече князь Печен<small>Ђ</small>жьскый: &laquo;а ты князь ли еси&raquo;? Онъ же рече: &laquo;азъ есмъ мужь его, и пришелъ есмь въ сторожехъ, а по мн<small>Ђ</small> идеть вои бещисленое множьство&raquo;. Се же рече, грозя имъ. И рече князь Печен<small>Ђ</small>жьскый Притичу: &laquo;буди ми другъ&raquo;. Онъ же рече: &laquo;тако буді&raquo;. И подаста руку межю собою, и вдасть Печен<small>Ђ</small>жьскый князь Притичу конь, саблю, стр<small>Ђ</small>лы; онъ же дасть ему брони, щитъ, мечь. И отступиша Печен<small>Ђ</small>з<small>Ђ</small> отъ города, и не бяше лз<small>Ђ</small> коня напоити, на Лыбеди Печен<small>Ђ</small>гы. И послаша Кияне къ Святославу, глаголюще: &laquo;ты, княже, чюжей земли иідешь и блюдешь, а своея ся лишивъ; мал<small>Ђ</small> бо насъ не възяша Печен<small>Ђ</small>зи, и матерь твою и д<small>Ђ</small>тий твоихъ. Аще не придеши, ни оборониши насъ, да пакы [насъ] възмуть, аще ти жаль отьчины своея, и матерь стары суща, и д<small>Ђ</small>ти[й] своихъ&raquo;. То слышавъ Святославъ, вборз<small>Ђ</small> въс<small>Ђ</small>дъ на кони съ дружиною своею, и приде къ Киеву, и ц<small>Ђ</small>лова матерь свою, и д<small>Ђ</small>ти своя, съжалиси о бывшемъ отъ Печен<small>Ђ</small>гъ; и събра воя и прогна Печен<small>Ђ</small>гы в поле, и бысть мирно.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6464 [956].</p>\r\n<p>В літо 6465 [957].</p>\r\n<p>В літо 6466 [958].</p>\r\n<p>В літо 6467 [959].</p>\r\n<p>В літо 6468 [960].</p>\r\n<p>В літо 6469 [961].</p>\r\n<p>В літо 6470 [962].</p>\r\n<p>В літо 6471 [963].</p>\r\n<p>&nbsp;</p>\r\n<p>В літо 6472 [964]. Коли князь Святослав виріс і змужнів,</p>\r\n<p>І почав воїв збирати багато й хоробрих,</p>\r\n<p>І сам-бо був хоробрий і легкий, ходячи як барс,</p>\r\n<p>І воєн багато провів.</p>\r\n<p>І возів за собою не возив, ні котлів, ні м\'яса не варив,</p>\r\n<p>А тоненько наріз&aacute;в конини, чи звірини, чи гов\'ядини</p>\r\n<p>І, на жару запікаючи, їв.</p>\r\n<p>І шатра не мав, а пітник стелив,</p>\r\n<p>А сідло в головах.</p>\r\n<p>І всі вої його такими були.</p>\r\n<p>І посилав у інші землі, кажучи: &laquo;Хочу на ви іти&raquo;.</p>\r\n<p>І пішов на Оку-ріку, і на Волгу.</p>\r\n<p>І натрапив на в\'ятичів, і питає їх:</p>\r\n<p>&laquo;Кому дань даєте?&raquo;</p>\r\n<p>Вони ж відповіли: &laquo;Козарам - по шелягу від рала даємо&raquo;.</p>\r\n<p>&nbsp;</p>\r\n<p>В літо 6473 [965]. Пішов Святослав на козар.</p>\r\n<p>Прочувши про те, козари виступили супроти з князем своїм, каганом,</p>\r\n<p>І зійшлися у битві.</p>\r\n<p>І була битва між ними, переміг Святослав козарів,</p>\r\n<p>І город їхній Білу Вежу взяв.</p>\r\n<p>І ясів переміг, і касогів,</p>\r\n<p>І до Києва повернувся.</p>\r\n<p>&nbsp;</p>\r\n<p>В літо 6474 [966]. Святослав переміг в\'ятичів і дань на них наклав.</p>\r\n<p>&nbsp;</p>\r\n<p>В літо 6475 [967]. Іде Святослав на Дунай воювати болгар.</p>\r\n<p>І в битві переміг Святослав болгарів,</p>\r\n<p>І взяв городів вісімдесят по Дунаю.</p>\r\n<p>І сів княжити тут в Переяславці, беручи дань з греків.</p>\r\n<p>В літо 6476 [968]. Вперше прийшли на Руську землю печеніги,</p>\r\n<p>А Святослав був у Переяславці,</p>\r\n<p>І закрилася Ольга із внуками своїми -</p>\r\n<p>Ярополком, і Олегом, і Володимиром - у городі Києві.</p>\r\n<p>І обступили печеніги город силою великою,</p>\r\n<p>І стали кількістю незчисленною побіля города,</p>\r\n<p>І не можна було ні вилізти з города, ні вість послати.</p>\r\n<p>І знемагали люди від голоду і без води.</p>\r\n<p>І зібралися люди протилежного боку Дніпра,</p>\r\n<p>І стояли в лодіях на тому березі,</p>\r\n<p>І не можна було жодному з них проникнути в Київ,</p>\r\n<p>Ані з города до них.</p>\r\n<p>І затужили люди в городі і питали:</p>\r\n<p>&laquo;Чи не знайдеться хто, щоб на той бік міг дійти</p>\r\n<p>[І передати]: &laquo;Коли не підійдете ранком до города</p>\r\n<p>Змушені будемо здатися печенігам&raquo;?&raquo;</p>\r\n<p>І рече один отрок: &laquo;Я зможу перейти&raquo;.</p>\r\n<p>Горожани ж були раді і сказали хлопцю:</p>\r\n<p>&laquo;Якщо можеш якось іти, йди&raquo;.</p>\r\n<p>Він же вийшов із города з вуздечкою в руках</p>\r\n<p>І, ходячи поміж печенігами, питав:</p>\r\n<p>&laquo;Чи ніхто з вас не бачив коня?&raquo; -</p>\r\n<p>І його приймали за свого, бо умів по-печенізьки.</p>\r\n<p>І коли наблизився до ріки, скинув одяг із себе,</p>\r\n<p>І кинувся в Дніпро, і пішов убрід.</p>\r\n<p>І як побачили печеніги, то кинулись за ним,</p>\r\n<p>І стріляли в нього,</p>\r\n<p>І не могли йому нічого зробити.</p>\r\n<p>І побачили на протилежному боці,</p>\r\n<p>І приїхали в лодії йому назустріч,</p>\r\n<p>І взяли його в лодію, і привезли до дружини.</p>\r\n<p>І сказав їм: &laquo;Якщо не підійдете під город завтра рано,</p>\r\n<p>То здадуться люди печенігам&raquo;.</p>\r\n<p>І рече їм воєвода їхній, на імення Претич:</p>\r\n<p>&laquo;Завтра рано в лодіях підійдем</p>\r\n<p>І, тайком підкравшись, візьмемо княгиню і княжичів</p>\r\n<p>І умчимо із людьми на цей берег;</p>\r\n<p>А коли цього не зробимо,</p>\r\n<p>Святослав, нас усіх погубить&raquo;.</p>\r\n<p>І як настав ранок, посідали в лодії</p>\r\n<p>І на світанку в труби сильно затрубили.</p>\r\n<p>І люди в городі закричали.</p>\r\n<p>І здалося печенігам, що князь вернувся,</p>\r\n<p>І побігли урозтіч від города.</p>\r\n<p>І зійшла Ольга з онуками і людьми до лодій.</p>\r\n<p>І побачив це князь печенізький,</p>\r\n<p>І повернувся один до воєводи Претича,</p>\r\n<p>І рече: &laquo;Хто це прийшов?&raquo;</p>\r\n<p>І відповів йому [воєвода]: &laquo;Люди з того боку&raquo;.</p>\r\n<p>І рече князь печенізький: &laquo;А чи не князь ти?&raquo;</p>\r\n<p>Він же відповів: &laquo;Я муж його</p>\r\n<p>І прийшов із сторожею, а за мною іде воїв безліч&raquo;.</p>\r\n<p>Це сказав, погрожуючи печенігам.</p>\r\n<p>І сказав князь печенізький Претичу: &laquo;Будь мені другом&raquo;.</p>\r\n<p>Він же відповів: &laquo;Нехай так буде!&raquo;</p>\r\n<p>І подали руки один одному,</p>\r\n<p>І дав печенізький князь Претичу коня, шаблю і стріли,</p>\r\n<p>А він дав йому кольчугу, щит і меч.</p>\r\n<p>І відступили печеніги від города,</p>\r\n<p>І не можна було коня напоїти на Либеді через печенігів.</p>\r\n<p>І послали кияни до Святослава, кажучи:</p>\r\n<p>&laquo;Ти, княже, чужу землю шукаєш і глядиш,</p>\r\n<p>А свою залишив. А нас мало не забрали печеніги,</p>\r\n<p>І матір твою, і дітей твоїх.</p>\r\n<p>І якщо не прийдеш і не захистиш нас, то таки візьмуть.</p>\r\n<p>І хіба тобі не жаль своєї отчини,</p>\r\n<p>І матері старої, і дітей своїх?&raquo;</p>\r\n<p>Як почув те Святослав, швидко сів на коні з дружиною своєю,</p>\r\n<p>І прийшов до Києва,</p>\r\n<p>І цілував матір свою і дітей своїх,</p>\r\n<p>І співчував про те, що пережили від печенігів.</p>\r\n<p>І зібрав воїв і прогнав печенігів у поле,</p>\r\n<p>І настав мир.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6477. Рече Святославъ къ матери своей и къ бояромъ своимъ: &laquo;нелюбо ми есть в Киев<small>Ђ</small> жити, хощю жити в Переяславци в Дунаи, яко то есть среда земли моей, яко ту вся благая сходяться: отъ Гр<small>Ђ</small>къ паволокы, золото, вино, и овощи разноличьнии, и ис Чеховъ, и изъ Угоръ серебро и комони, изъ Руси же скора и воскъ, и медъ и челядъ&raquo;. И рече ему мати: &laquo;видиши ли мя болну сущю, камо хощеши отъ мене?&raquo; б<small>Ђ</small> бо разбол<small>Ђ</small>лася уже; рече же ему: &laquo;погребъ мя иди аможе хощеши&raquo;. И по трехъ днехъ умре Олга, и плакася по ней сынъ ея, и внуци ея, и людье вси плачемъ великимъ, и несъше погребоша ю на м<small>Ђ</small>ст<small>Ђ</small>. И б<small>Ђ</small> запов<small>Ђ</small>дала Олга не творити трызны надъ собою, б<small>Ђ</small> бо имущи прозвутера и тъ похорони, блажену Олгу. Си бысть предътекущия христьянской земли аки д<small>Ђ</small>ньница предъ солнцемъ и аки заря предъ св<small>Ђ</small>томъ, си бо сияше аки луна в нощи, тако си в нев<small>Ђ</small>рныхъ челов<small>Ђ</small>ц<small>Ђ</small>хъ св<small>Ђ</small>тяшеся аки бисеръ въ кал<small>Ђ</small>; калн<small>Ђ</small> бо б<small>Ђ</small>ша гр<small>Ђ</small>хомъ, не омовени святымъ крещениемъ. Си бо омыся святою куп<small>Ђ</small>лью, съвлечеся гр<small>Ђ</small>ховныя одежда ветхаго челов<small>Ђ</small>ка Адама, и въ новый Адамъ обл<small>Ђ</small>чеся, еже есть Христосъ. Мы же речемъ къ ней: радуйся, руское познаніе къ Богу; начатокъ примирению быхомъ. Си п<small>Ђ</small>рвое вниде въ царство небесное отъ Руси, сию бо хвалять Рустии сынове, акы началницю: ибо по смерти моляшеся къ Богу за Русь, праведнихъ бо душа не умирають; якоже рече С[о]ломонъ: похваляему прав<small>Ђ</small>дному възвеселятся людье, бесмертье бо есть память его, яко отъ Бога познавается и отъ челов<small>Ђ</small>къ. Се бо вси челов<small>Ђ</small>ци прославляють, видяще лежащю в т<small>Ђ</small>л<small>Ђ</small> за многа л<small>Ђ</small>та; рече бо Пророкъ: прославляюща мя прославлю. О сяковых бо Давидъ глаголаше: в память в<small>Ђ</small>чную будеть прав<small>Ђ</small>дникъ, отъ слуха зла не убоится; готово сер [д] це его уповати на Господа, утв<small>Ђ</small>рдися сердце его и не подвижится. Соломонъ бо рече: &laquo;праведници въ в<small>Ђ</small>кы живуть, и отъ Господа мьзда имъ есть и строение отъ Вышняго; сего ради примуть царствие красот<small>Ђ</small> и в<small>Ђ</small>н<small>Ђ</small>ць доброты отъ рукы Господня, яко десницею защитить я и мышьцею покрыеть я. Защитилъ бо есть силою блаженую Олгу отъ противника и супостата дьявола.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6477 [969]. Сказав Святослав матері своїй</p>\r\n<p>І боярам своїм: &laquo;Не любо мені в Києві жити,</p>\r\n<p>Хочу жити в Переяславці на Дунаї,</p>\r\n<p>Бо то середина землі моєї, бо туди всі блага стікають:</p>\r\n<p>Від греків - паволоки, золото, вино і овочі різні,</p>\r\n<p>А від чехів і угрів - срібло і коні,</p>\r\n<p>Із Русі ж - хутра, і віск, і мед, і челядь&raquo;,</p>\r\n<p>І сказала йому мати: &laquo;Чи бачиш мене хвору</p>\r\n<p>І куди хочеш від мене?&raquo; - бо була розхворілася вже.</p>\r\n<p>І рече йому: &laquo;Похорониш мене - і йди куди хочеш&raquo;.</p>\r\n<p>І через три дні померла Ольга,</p>\r\n<p>І плакав за нею син її, і внуки її,</p>\r\n<p>І люди всі - плачем великим,</p>\r\n<p>І понесли і поховали її у полі.</p>\r\n<p>І заповіла Ольга не творити тризни над собою,</p>\r\n<p>Бо мала свого пресвітера,</p>\r\n<p>І той похоронив блаженну Ольгу.</p>\r\n<p>Отже, була [Ольга] предтечею християнської землі,</p>\r\n<p>Як вранішня зірка перед сходом сонця</p>\r\n<p>І як зоря передсвітом,</p>\r\n<p>Отож сіяла вона, як місяць вночі,</p>\r\n<p>Так і серед людей невірних сіяла,</p>\r\n<p>Як бісер у калі.</p>\r\n<p>Кальні-бо були гріхами, не омиті святим хрещенням.</p>\r\n<p>А вона ж омилась святою купіллю,</p>\r\n<p>І скинула одяг гріховний ветхого чоловіка Адама,</p>\r\n<p>І в нового Адама одяглася, що є Христос.</p>\r\n<p>І ми звертаємось до неї: &laquo;Радуйся, руське пізнання Бога,</p>\r\n<p>Початок нашого з ним примирення&raquo;.</p>\r\n<p>А вона першою із Русі ввійшла в царство небесне,</p>\r\n<p>Її й восхваляють як начинательку сини руськії,</p>\r\n<p>Бо й після смерті молиться Богу за Русь,</p>\r\n<p>Бо душі праведних не умирають, як сказав Соломон:</p>\r\n<p>&laquo;Радіють люди, похвалу воздаючи праведнику&raquo;,</p>\r\n<p>Бо безсмертя його - пам\'ять,</p>\r\n<p>Оскільки визнається він і Богом, і людьми.</p>\r\n<p>Це [Ольгу] всі люди прославляють,</p>\r\n<p>Бо бачать тіло її багато років непідвладне тлінню,</p>\r\n<p>Рече-бо Пророк: &laquo;Тих, що мене прославляють,- прославлю&raquo;.</p>\r\n<p>Про таких і Давид говорить: &laquo;У пам\'яті вічній буде праведник</p>\r\n<p>І злих чуток не злякається, його серце живе надією на Господа,</p>\r\n<p>Зміцниться серце його і буде несхитним&raquo;.</p>\r\n<p>Соломон же рече: &laquo;Праведники живуть вовіки,</p>\r\n<p>І від Господа їм нагорода, і турбота Всевишнього.</p>\r\n<p>Цього ради й приймуть царство краси</p>\r\n<p>І вінець доброти із руки Господа, який захистить їх правицею</p>\r\n<p>І покриє їх м\'язами&raquo;.</p>\r\n<p>Захистив-бо силою блаженную Ольгу від противника</p>\r\n<p>І супостата-диявола.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>',NULL,1847,5,1,NULL,25);
INSERT INTO `publications_publication` VALUES
(4713,'P','2008-12-20',0,NULL,'Note','Повість врем\'яних літ До 6420 [912] року.','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Літописець Руський, з богом, починаємо. Отче, благослови!</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Повість врем\'яних літ чорноризця Федосієвого монастиря Печерського, звідки пішла Руська земля, і хто в ній найперший почав княжити, [і з чого Руська земля] такою стала.</span></span></p>\r\n<p>&nbsp;</p>','<p><a href=\"http://izbornyk.org.ua/pvlyar/yar01.htm\">До 6420 [912] року.</a></p>\r\n<p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p><strong><em>Л<small>Ђ</small>тописець Руский съ Богомъ починаемь. Отче благослови.</em></strong></p>\r\n<p><em><br /></em></p>\r\n<p><em>Пов<small>Ђ</small>сть временныхъ л<small>Ђ</small>тъ черноризца Федосьева манастыря Печерьскаго, откуду есть пошла Руская земля и хто в ней почалъ п<small>Ђ</small>рв<small>Ђ</small>е княжити [и откуду Руская земля] стала есть.</em></p>\r\n<p>&nbsp;</p>\r\n<p>Се начнемь пов<small>Ђ</small>сть сию.</p>\r\n<p>По потоп<small>Ђ</small> бо 3-е сынове Ноеви розд<small>Ђ</small>лиша земьлю: Симъ, Хамъ, Афетъ. Яся въстокъ Симови: Перьсида, Ватрь, доже и до Иньдикия в долготу и в широту и до Нирокурия, якоже рещи отъ въстока доже й до полуд[е]нья, и Сурия, и Мидия, и Ефратъ р<small>Ђ</small>ку, и Вавилонъ, Кордуна, Асуриан<small>Ђ</small>, Месопоттамия, Аравия стар<small>Ђ</small>йшая, Елумаисъ, Индиа, Аравиа силная, Кулии, Комагины, Финикия вся.</p>\r\n<p>Хамови же яс [я] полуденья часть: Егупетъ, Ефиопья, прилежащия къ Индомъ, другая же Ефиопья, из неяже исходить р<small>Ђ</small>ка Ефиопьская Чермьна, текущая на въстокъ, Фива, Луви прилежащи доже до Куриния, Мармариа, Сурите, Ливуи другая, Нумидия, Масурия, Мавритания противу сущи Гадир<small>Ђ</small>. Сущимъ же къ встокомъ имать Киликию, Памфилию, Пасидию, Мосию, Лукаонию, Фругию, Камалию, Ликию, Карию, Лудию, Масию другую, Троаду, Солиду, Вифунию, старую Фругию. И островы пакы имать: Сардинию, Крітъ, Купръ, і р<small>Ђ</small>ку Гиону, зовемую Нилу.</p>\r\n<p>Афетови же яся полунощная страна и западная: Мидия, Олъвания, Армения малая и великая, Каподокия, Фефлагони, Галатия, Кольхысъ, Воспории,.Меоти, Дереви, Сармати, Тавриани, Скуфия, Фраци, Македония, Далматия, Молоси, Фесалия, Локрия, Пеления яже и Полопонисъ наречется, Аркадия, Ипириноя, Илурикъ, Словене, Лухития, Аньдріакия, Аньдриатиньска пучина; имать же и островы: Вританию, Сикелию, Евию, Родона, Хиона, Л<small>Ђ</small>звона, Куфирана, Закуньфа, Кефалиния, Ифакину, Керкуру, и часть Асійскія страны, и нарицаюмую Онию, и р<small>Ђ</small>ку Тигру, текущую межи Миди и Вавилономъ; до Понетьского моря на полунощныя страны: Дунай, Днестръ, и Кавькасийскыя горы, рекше Угорьскыя, и оттуда рекше доже и до Днепра; и прочая р<small>Ђ</small>кы: Десна, Припеть, Двина, Волховъ, Волга, иже идеть на въстокъ въ часть Симову. Въ Афетові же части с<small>Ђ</small>дить Русь, Чудь и вси языц<small>Ђ</small>: Меря, Мурома, Всь, Мордва, Заволочьская Чюдь, Пермь, Печера, Ямь, Югра, Литва, Зимигола, Корсь, С<small>Ђ</small>тьгола, Либь. Ляхове же и Пруси и Чюдь прис<small>Ђ</small>дять к морю Варяскому. По сему же морю с<small>Ђ</small>дять Варязи с<small>Ђ</small>мо къ вьстоку до пред<small>Ђ</small>ла Симова, по тому же морю с<small>Ђ</small>дять къ западу до земли Агаляньски и до Волошьскые. Афетово же кол<small>Ђ</small>но и то: Варязи, Свеи, Урмане, Гот<small>Ђ</small>, Русь, Аглян<small>Ђ</small>, Галичан<small>Ђ</small>, Волохове, Римлян<small>Ђ</small>, Н<small>Ђ</small>мц<small>Ђ</small>, Корлязи, Венедици, Фрягов<small>Ђ</small> и прочии прис<small>Ђ</small>дять отъ запада къ полуденью и със<small>Ђ</small>дятся съ племенемъ Хамовымъ.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p><strong><em>Літописець Руський, з богом, починаємо. Отче, благослови!</em></strong></p>\r\n<p><em><br /></em></p>\r\n<p><em>Повість врем\'яних літ чорноризця Федосієвого монастиря Печерського, звідки пішла Руська земля, і хто в ній найперший почав княжити, [і з чого Руська земля] такою стала.</em></p>\r\n<p>&nbsp;</p>\r\n<p>Се почнемо повість оцю.</p>\r\n<p>Після потопу-бо троє синів Ноєвих, Сим, Хам і Афет, розділили [між собою] землю. Симові дістався схід: Персида, Ватр, аж до Індикія в довжину і до Нирокурія в ширину, так би мовити, від сходу і аж до полудення, і Сурія, і Мідія, і ріка Єфрат, і Вавілон, Кордуна, Асуріани, Месопотамія, Аравія старійша, Єлумаїс, Індія, Аравія сильна, Кулії, Комагини, вся Фінікія.</p>\r\n<p>Хамові ж дісталася полуднева частина: Єгипет, Єфіопія, що прилягає до Інду, друга Єфіопія, звідки бере початок єфіопська ріка Чермна, що тече на схід, Фіва, Луві, прилягає аж до Курінія; Мармарія, Суріте, Лівуї, Лівуї друга, Нумідія, Масурія, Мавританія, що напроти Гадирі. Із тих, що на сході, одержав Кілікію, Памфілію, Пісідію, Мосію, Лукаонію, Фругію, Камалію, Лікію, Карію, Лудію, Масію другу, Троаду, Соліду, Віфунію, стару Фругію. І острови одержав: Сардінію, Кріт, Купр і ріку Гіону, по-іншому - Ніл.</p>\r\n<p>Афетові ж дісталися північні і західні краї: Мідія, Олванія, Арменія мала і велика, Каподокія, Фефлагоні, Галатія, Кольхис, Воспорії, Меоти, Дереви, Сармати, Тавриани, Скуфія, Фраці, Македонія, Далматія, Молоси, Фесалія, Локрія; Пеленія, або ж Полопоніс, Аркадія, Іпіріноя, Ілурік, Словене, Лухитія, Андріакія, Андріатинське море; отримав також і острови: Британію, Сікелію, Євію, Родону, Хіону, Лізвону, Куфирану; Закунфу, Кефалинію, Іфакіну, Керкуру, і частину Азійського краю, що називають Онією, і ріку Тигр, що тече між Міді і Вавілоном; [і ті, що течуть] до Понтського моря в північних краях: Дунай, Дністр, і Кавкасійські гори, звані Угорськими, і звідти, кажуть, аж до Дніпра; та інші ріки: Десна, Припеть, Двіна, Волхов, Волга, що плине на схід у володіння Симові. В Афетовій же частині сидять русь, чудь і всі народи: меря, мурома, весь, мордва, заволочська чудь, перм, печера, ям, югра, литва, зимигола, корс, сітьгола, ліб. Ляхи ж, і прусси, і чудь присидять біля моря Варязького. Понад цим же морем сидять варяги - в напрямку на схід, до меж Симових, і на захід понад тим же морем сидять до землі агалянської і волоської. Афетове ж коліно і це: варяги, свеї, урмани, готи, русь, агляни, галичани, волохи, римляни, німці, корляги, венедиці, фрягове та інші присидять від заходу до полудення і сусідять з Хамовим племенем.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Симъ же и Хамъ и Афетъ, разд<small>Ђ</small>ливше землю, и жребий метавше не переступати никомуже въ жребий братень, и живяху кождо въ своей части И бысть языкъ единъ, и умножившимся челов<small>Ђ</small>комъ на земли, и помыслиша создати столпъ до небеси, въ дни Нектана и Фалека, и събравшеся на м<small>Ђ</small>ст<small>Ђ</small> Сенарь пол<small>Ђ</small> здати столпъ до небесе и город<small>Ђ</small> около его Вавилонъ, и здаша столпъ за 40 л<small>Ђ</small>тъ, и не св<small>Ђ</small>ршенъ бысть. И спиде Господь Богъ вид<small>Ђ</small>ть городъ и столпа, и рече Господь: се родъ единъ и языкъ единъ. И см<small>Ђ</small>си Богъ языки и разд<small>Ђ</small>ли на 70 и ра два языка, и расс<small>Ђ</small>я по всей земли. По разм<small>Ђ</small>шеньи же языкъ, Богъ в<small>Ђ</small>тромъ великомъ раздруши столпъ, и есть останокъ его межи Асура и Вавилона; и есть въ высоту и въ ширину локотъ 5323 локотъ; въ л<small>Ђ</small>та многа хранимъ останокъ. По раздрушеніи же столпа и по разд<small>Ђ</small>лении языкъ, прияша сынове Симовы въсточныя страны, а Хамовы же сынове полуденныя страны; Афетови же сынове западъ прияша и полунощьныя страны. Оть сихъ же 70 и дву языку бысть языкъ Словенескъ отъ племени же Афетова, нарицаем<small>Ђ</small>и Норци, иже суть Словен<small>Ђ</small>.</p>\r\n<p>По мноз<small>Ђ</small>хъ же времен<small>Ђ</small>хъ с<small>Ђ</small>л<small>Ђ</small> суть Словени по Дунаеви, кде есть нын<small>Ђ</small> Угорьская земля и Болгарьская. Отъ т<small>Ђ</small>хъ Словенъ розидошася по земьли и прозвашася имены своими, кде с<small>Ђ</small>дше на которомъ м<small>Ђ</small>ст<small>Ђ</small>. Яко пришедше с<small>Ђ</small>доша на р<small>Ђ</small>ц<small>Ђ</small> именемъ Морав<small>Ђ</small>, и прозвашася Морава, а друзии Чес<small>Ђ</small> нарекошася. А се ти же Слов<small>Ђ</small>не: Хорвати Б<small>Ђ</small>лии, Серпь, и Хутан<small>Ђ</small>. Волохомъ бо нашедшимъ на Словены на Дунайскые и с<small>Ђ</small>дшимъ в нихъ и насиляющимъ имъ, Слов<small>Ђ</small>не же ови пришедше и с<small>Ђ</small>доша на Висл<small>Ђ</small>, и прозвашася Ляхов<small>Ђ</small>, а отъ т<small>Ђ</small>хъ Ляховъ прозвашася Поляне, Ляхов<small>Ђ</small> друзии Лютич<small>Ђ</small>, инии Мазовшане, а инии Поморяне. Такоже и т<small>Ђ</small> же Слов<small>Ђ</small>не пришедше с<small>Ђ</small>доша по Днепру и наркошася Поляне, а друзии Деревляне, зане с<small>Ђ</small>доша в л<small>Ђ</small>с<small>Ђ</small>хъ; а друзии с<small>Ђ</small>доша межи Прип<small>Ђ</small>тью и Двиною и наркошася Дреговичи; и инии с<small>Ђ</small>доша на Двин<small>Ђ</small>, и наркошася Полочане, р<small>Ђ</small>чькы ради, яже втечеть въ Двину, именемь Полота, отъ сея прозвашася Полочан<small>Ђ</small>. Слов<small>Ђ</small>не же с<small>Ђ</small>доша около озера Илмера, и прозвашася своимъ именемъ и сд<small>Ђ</small>лаша городъ и нарекоша и Новъгородъ; а друзии же с<small>Ђ</small>доша на Десн<small>Ђ</small> и по Семи и по Сул<small>Ђ</small> и наркошася С<small>Ђ</small>вера. И тако разидеся Словенескъ языкъ, т<small>Ђ</small>мьже и прозвашася Словеньская грамота.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Сим же, Хам і Афет розділили землю,</p>\r\n<p>І жереб метали не переступати нікому жереб братній,</p>\r\n<p>І жив кожний у своїй частині.</p>\r\n<p>І був народ єдиний,</p>\r\n<p>І умножився людьми на землі,</p>\r\n<p>І намислили вони в дні Нектана і Фалека спорудити стовп до небес,</p>\r\n<p>І зібралися в місцевості Сенар зводити на полі стовп до неба,</p>\r\n<p>І город біля нього Вавілон,</p>\r\n<p>І будували стовп сорок літ,</p>\r\n<p>І нез акінчили його.</p>\r\n<p>І зійшов Господь Бог подивитися город і стовп,</p>\r\n<p>І сказав Господь: &laquo;Се рід один</p>\r\n<p>І мова одна&raquo;.</p>\r\n<p>І змішав Бог мови,</p>\r\n<p>І розділив на сімдесят і на дві мови,</p>\r\n<p>І розсіяв по всій землі.</p>\r\n<p>&nbsp;</p>\r\n<p>Після змішання мов Бог вітром великим зруйнував стовп,</p>\r\n<p>І є останок його на межі Асура і Вавілона.</p>\r\n<p>І є він у висоту і в ширину 5323 лікті,</p>\r\n<p>І вже багато літ бережуть останок.</p>\r\n<p>&nbsp;</p>\r\n<p>Після зруйнування стовпа і поділу мов взяли сини Симові східні країни, а Хамові сини - полудневі країни, Афетові ж сини взяли захід і опівнічні країни. Від цих же сімдесяти і двох народів і був народ слов\'янський племені Афетового, названий норцями, що суть словени.</p>\r\n<p>По довгих временах сіли словени по Дунаю, де нині Угорська земля і Болгарська. Від тих словен розійшлися по землі і прозвалися іменами своїми, де хто сів, на якому місці. Хто прийшов і сів на ріці Мораві, то й прозвалися морава, а інші нареклися чехами. А се ті ж словени: хорвати білії, серп і хутани. Коли на дунайських словен найшли волохи, і сіли на їхніх землях, і чинили над ними насильство, словени ж ті прийшли і сіли на Віслі і стали називатися ляхами, а від тих ляхів пішли інші назви - поляни, ляхи другії - лютичі, інші - мазовшани, ще інші - поморяни. Також і ті словени, що прийшли і сіли по Дніпру, нареклися поляни, а другії - деревляни, оскільки сиділи в лісах; а інші сиділи межи Прип\'яттю і Двіною і називалися дреговичі; ще інші жили на Двіні і називалися полочани від назви річки Полота, що впадає в Двіну. Від неї і прозвалися - полочани.</p>\r\n<p>Словіни ж сиділи побіля озера Ілмера, називалися своїм іменем, і збудували город, і нарекли його Новгород; а другії ж сиділи по Десні, по Семі, і по Сулі і називалися сівера.</p>\r\n<p>І так розійшовся словенський народ, його іменем і грамота названа словенською</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Поляномъ же живущимъ особь по горамъ симъ, и б<small>Ђ</small> путь из Варягъ въ Грекы; и изъ Грекъ по Днепру, и в<small>Ђ</small>рхъ Днепра волокъ до Ловоти, и по Ловоти виити въ Илмерь озеро великое, из него же озера потечеть Волховъ и втечеть въ озеро великое Нево, и того озера внидетъ устье в море Варяское; и по тому морю внити доже и до Рима, а отъ Рима прити по тому же морю къ Царюграду, и отъ Царяграда прити в Понтъ море, в неже втечеть Дн<small>Ђ</small>пръ р<small>Ђ</small>ка. Дн<small>Ђ</small>прь бо течеть изъ Воковьского л<small>Ђ</small>са, и потечеть на полудни; а Двина изъ того же л<small>Ђ</small>са потечеть, и идеть на полуночье и внидеть в море Варяское; ис того же л<small>Ђ</small>са потечеть Волга на въстокъ, и вътечеть седьмьюдесятъ жерелъ в море Хвалійское. Т<small>Ђ</small>мьже из Русі можеть ити по Волз<small>Ђ</small> в Болгары и въ Хвалисы, и на въстокъ доити въ жеребий Симовъ; а по Двин<small>Ђ</small> въ Варягы, а изъ Варягъ и до Рима, отъ Рима же и до племени Хамова. А Днепръ втечеть въ Понтеское море треми жерелы, иже море словеть Руское, по нему же училъ святый апостолъ Андр<small>Ђ</small>й, братъ Петровъ, якоже ркоша. Андр<small>Ђ</small>юучащю в Синопии, пришедшю ему в Корсунь, увид<small>Ђ</small>, яко ис Коръсуня близъ устье Дьн<small>Ђ</small>пръское, и въсхот<small>Ђ</small> поити в Римъ, и приде въ устье Днепръское; и оттол<small>Ђ</small> поиде по Дн<small>Ђ</small>пру гор<small>Ђ</small>, и по приключаю Божію приде и ста подъ горами на берез<small>Ђ</small>. И заутра въставъ, рече к сущимъ с нимъ ученикомъ: &laquo;видите горы сия? яко на сихъ горахъ въсияеть благодать Божия, имать и городъ великъ быти, и церкви мьногы имать Богъ въздвигнути&raquo;. И въшедъ на горы сия, и благослови я, и постави крестъ, и помолився Богу, и сл<small>Ђ</small>зе съ горы сея, а идеже посл<small>Ђ</small>же бысть Киевъ, и поиде по Дн<small>Ђ</small>пру гор<small>Ђ</small>. И приде въ Словены, идеже нын<small>Ђ</small> Новъгородъ. И вид<small>Ђ</small>въ люди ту сущая, какъ ихъ обычай, и како ся мыють и хвощются, и удивися имъ. И иде въ Варягы, и приде въ Римъ, испов<small>Ђ</small>да, елико научи и елико вид<small>Ђ</small>, и рече имъ: &laquo;дивно вид<small>Ђ</small>хъ землю Словеньску; идущю ми с<small>Ђ</small>мо, вид<small>Ђ</small>хъ бан<small>Ђ</small> древяны, и пережьгуть я велми, и съвлекутся и будутъ нази, и обольются мытелью, и возмуть в<small>Ђ</small>никы и начнуть хвостатись, и того соб<small>Ђ</small> добьють, одва выл<small>Ђ</small>зуть ле живы, и обольются водою студеною, и тако оживутъ. И тако творять по вся дни не мучими ни кым [ъ] же, но сами ся мучать, и творять не мытву себ<small>Ђ</small>, но мученье&raquo;. И се слышавше, дивляхуся. Андр<small>Ђ</small>й же бывъ в Рим<small>Ђ</small>, приде въ Синопию. Поляномъ же живущимъ особ<small>Ђ</small> и влад<small>Ђ</small>ющимъ роды своими, яже и до сея братья бяху Поляне, и живяху кождо съ родомъ своимъ на своихъ м<small>Ђ</small>стехъ, волод<small>Ђ</small>юще кождо родомъ своимъ. И быша 3 брата, единому имя Кий, а другому Щекъ, а третьему Хоривъ, и сестра их Лыб<small>Ђ</small>дь. И с<small>Ђ</small>дяше Кий на гор<small>Ђ</small>, кд<small>Ђ</small> нын<small>Ђ</small> увозъ Боричевъ, а Щекъ с<small>Ђ</small>дяше на гор<small>Ђ</small>, кд<small>Ђ</small> нын<small>Ђ</small> зовется Щековица, а Хоривъ на третьей гор<small>Ђ</small>, отнюдуже прозвася Хоривіца; створиша городокъ, во имя брата ихъ стар<small>Ђ</small>йшаго, и наркоша и Киевъ. И бяше около города л<small>Ђ</small>съ и боръ великъ, и бяху ловяще зв<small>Ђ</small>рье. Бяхуть бо мудр<small>Ђ</small> и смыслени, и нарицахуся Поляне, отъ нихъ же суть Поляне Кияне и до сего дни.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Поляни жили осібно по горах сих,</p>\r\n<p>І був шлях із варягів у греки;</p>\r\n<p>І од греків по Дніпру,</p>\r\n<p>І з верхів\'їв Дніпра волоком до Ловоті,</p>\r\n<p>І по Ловоті входили в велике Ілмер-озеро.</p>\r\n<p>Із цього озера витікає Волхов</p>\r\n<p>І впадає в озеро велике Нево,</p>\r\n<p>Із того озера впадає гирло в море Варязьке,</p>\r\n<p>І по тому морю можна дійти і до Риму,</p>\r\n<p>І від Риму прийти морем аж до Царгорода,</p>\r\n<p>І від Царгорода прийти в Понт-море, в нього впадає Дніпр-ріка.</p>\r\n<p>Із Воковського лісу витікає Дніпро</p>\r\n<p>І тече на південь;</p>\r\n<p>І Двіна із того ж лісу витікає</p>\r\n<p>І тече на північ,</p>\r\n<p>І впадає в море Варязьке;</p>\r\n<p>Із того ж лісу витікає Волга</p>\r\n<p>І тече на схід,</p>\r\n<p>І впадає сімдесятьма рукавами в море Хвалійське.</p>\r\n<p>Із Русі тим шляхом можна дійти Волгою до болгар,</p>\r\n<p>І в Хваліси,</p>\r\n<p>І на схід дійти у жереб Симів;</p>\r\n<p>І Двіною до варягів</p>\r\n<p>І з варягів аж до Риму,</p>\r\n<p>А від Риму - і до племені Хамового.</p>\r\n<p>А Дніпро впадає в Понтське море трьома рукавами,</p>\r\n<p>І море те називають Руським.</p>\r\n<p>Понад морем Руським учив святий апостол Андрій, Брат Петрів, як його називають.</p>\r\n<p>&nbsp;</p>\r\n<p>Андрій учив у Синопії,</p>\r\n<p>І коли прийшов у Корсунь,</p>\r\n<p>І довідався, що поблизу Корсуні гирло Дніпрське,</p>\r\n<p>І захотів піти в Рим.</p>\r\n<p>І прийшов до гирла Дніпрського</p>\r\n<p>І звідти пішов по Дніпру горі,</p>\r\n<p>І за призначенням божим прийшов</p>\r\n<p>І став під горами на березі.</p>\r\n<p>І, вставши зранку, рече учням своїм, що були з ним:</p>\r\n<p>&laquo;Бачите гори оці? Яка на горах цих возсія благодать божа,</p>\r\n<p>І має бути город великий,</p>\r\n<p>І багато церков бог воздвигне&raquo;.</p>\r\n<p>І зійшов на гори ці,</p>\r\n<p>І благословив їх,</p>\r\n<p>І хрест поставив,</p>\r\n<p>І помолився Богу,</p>\r\n<p>І зійшов з гори тої, де опісля постав Київ,</p>\r\n<p>І пішов по Дніпру горі.</p>\r\n<p>І прийшов до словен, де нині Новгород.</p>\r\n<p>І побачив людей там сущих, який в них обичай,</p>\r\n<p>І як вони [в лазнях] миються,</p>\r\n<p>І хвощаться,</p>\r\n<p>І дивувався їм,</p>\r\n<p>І пішов до варягів,</p>\r\n<p>І прийшов до Риму.</p>\r\n<p>І розповів, чому навчав і що бачив.</p>\r\n<p>І рече їм:</p>\r\n<p>&laquo;Дивну бачив землю словенську,</p>\r\n<p>Проходячи якою, бачив бані дерев\'яні,</p>\r\n<p>І перегрівають їх вельми,</p>\r\n<p>І роздягаються догола</p>\r\n<p>І обливаються мителлю,</p>\r\n<p>І беруть віники</p>\r\n<p>І починають хвоськатись,</p>\r\n<p>І до того себе б\'ють, що ледве живі вилазять,</p>\r\n<p>І обіллються студеною водою</p>\r\n<p>І знову оживають.</p>\r\n<p>І так роблять завжди, ніхто їх не мучить,</p>\r\n<p>А мучать самі себе,</p>\r\n<p>І творять не миття собі, а мучення&raquo;.</p>\r\n<p>І хто слухав це, дивувалися.</p>\r\n<p>Андрій же, побувши в Римі, пішов у Синопію.</p>\r\n<p>Поляни ж жили осібно і володіли родами своїми,</p>\r\n<p>І до цих братів, отже, були поляни,</p>\r\n<p>І жили кожен з родом своїм, на своїх місцях,</p>\r\n<p>І володів кожен родом своїм.</p>\r\n<p>І були три брати. Одному ім\'я Кий,</p>\r\n<p>А другому - Щек,</p>\r\n<p>А третьому - Хорив,</p>\r\n<p>І сестра їхня - Либідь.</p>\r\n<p>І сидів Кий на горі, де нині узвіз Боричів,</p>\r\n<p>А Щек сидів на горі, що й нині зоветься Щекавиця,</p>\r\n<p>А Хорив - на третій горі, від нього ж прозвалася Хоривиця.</p>\r\n<p>І в ім\'я брата свого старшого заклали городок</p>\r\n<p>І назвали його Київ.</p>\r\n<p>І був коло города ліс</p>\r\n<p>І бір великий,</p>\r\n<p>І були [вони] мисливці на звірів.</p>\r\n<p>І тямущі та мудрі були,</p>\r\n<p>І називалися поляни,</p>\r\n<p>І від них же поляни-кияни і до сьогодні.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Инии же, не в<small>Ђ</small>дуще, ркоша, яко Кий есть перевозникъ бысть; у Киева бо перевозъ бяше тогда съ оноя страны Днепра, т<small>Ђ</small>мь глаголаху: на перевозъ на Киевъ. Аще бо быль перевозникъ Кый, то не бы ходилъ къ Царюграду; но сий Кий княжаше в роду своем; и приходившю ему къ царю не св<small>Ђ</small>мы, но токмо о семъ в<small>Ђ</small>мы, якоже сказають, яко велику честь приялъ есть от царя, которого не в<small>Ђ</small>мъ и при которомъ приходи цари. Идущю же ему опять, приде къ Дунаеви, и възлюби м<small>Ђ</small>сто и сруби городокъ малъ, и хотяше с<small>Ђ</small>сти с родомъ своимъ, и не даша ему близъ живущии; еже и до нын<small>Ђ</small> нар<small>Ђ</small>чють Дунайци городіще Киев<small>Ђ</small>ць. Киеви же прішедшю въ свой городъ Киевъ, ту и сконча животъ свой, и брата его Щекъ и Хоривъ и сестра ихъ Лыб<small>Ђ</small>дь ту скончашася.</p>\r\n<p>И по сей братьи почаша д<small>Ђ</small>ржати родъ ихъ княжение въ Поляхъ; а въ Деревляхъ свое, а Дрьговичи свое, а Слов<small>Ђ</small>не свое въ Новъгород<small>Ђ</small>, а другие на Полот<small>Ђ</small>, иже и Полочан<small>Ђ</small>. Отъ сихъ же и Кривичи, иже с<small>Ђ</small>дять на верхъ Волгы, и на в<small>Ђ</small>рхъ Двины, и на в<small>Ђ</small>рхъ Дн<small>Ђ</small>пра, ихъ же и городъ есть Смол<small>Ђ</small>нескъ: туда бо с<small>Ђ</small>дять Кривичи. Таже с<small>Ђ</small>веро отъ нихъ, на Б<small>Ђ</small>л<small>Ђ</small>озер<small>Ђ</small>, с<small>Ђ</small>дять В<small>Ђ</small>сь, а на Ростов<small>Ђ</small> озер<small>Ђ</small> Меря, а на Клещин<small>Ђ</small> озер<small>Ђ</small> с<small>Ђ</small>дять М<small>Ђ</small>ря же: а по Оц<small>Ђ</small> р<small>Ђ</small>ц<small>Ђ</small>, кде втечеть въ Волгу, языкъ свой Мурома, и Черемиси свой языкъ, и Мордва свой языкъ. Се бо токмо Слов<small>Ђ</small>нескъ языкъ въ Русі: Поляне, Деревляне, Новъгородьци, Полочане, Дьрьговичи, С<small>Ђ</small>веро, Бужане, зань с<small>Ђ</small>дять по Бугу, посл<small>Ђ</small> же Волыняне. І се суть инии языц<small>Ђ</small>, иже дань дають Руси: Чюдь, Весь, Меря, Мурома, Черемись, Мордва, П<small>Ђ</small>рмь, Печера, Ямь, Литва, Зим<small>Ђ</small>гола, Корсь, Норома, Либь; си суть свой языкъ имуще, отъ кол<small>Ђ</small>на Афетова, иже живуть на странахъ полунощныхъ.</p>\r\n<p>Словеньску же языку, якоже ркохомъ, живущю на Дунаи, придоша отъ Скуфъ, рекше отъ Козаръ, рекомии Болгаре, и с<small>Ђ</small>доша по Дунаеви, нас<small>Ђ</small>лниц<small>Ђ</small> Словеномъ б<small>Ђ</small>ша. А по семъ придоша Угре Б<small>Ђ</small>лии, на насл<small>Ђ</small>диша землю Слов<small>Ђ</small>ньскую, прогнаше Волохы, иже б<small>Ђ</small>ша преже приял<small>Ђ</small> землю Словеньску; си бо Угри ночаша быти при Ираклии цари, иже ходиша на Хоздроя царя П<small>Ђ</small>рьскаго. В си же времена бысть и Обре, иже воеваша на царя Ираклия и мало его не яша; си же Обри воеваша на Слов<small>Ђ</small>ны и примучиша Дул<small>Ђ</small>бы, сущая Слов<small>Ђ</small>ны, и насилье творяху женамъ Дул<small>Ђ</small>бьскымъ: аще по<small>Ђ</small>хати бяше Обрину, не дадяше въпрячи коня, ни волу, но веляше въпрячи 3, или 4, ли 5 женъ в тел<small>Ђ</small>гу и повести Обрина; а тако мучаху Дул<small>Ђ</small>бы. Бяху бо Обри т<small>Ђ</small>ломъ велиц<small>Ђ</small>, а умомъ горди, и потреби я Богъ, и помроша вси, и не оста ни единъ Обринъ; и есть причта в Руси и до сего дни: погибоша аки Обри; ихъже н<small>Ђ</small>сть ни племени, ни насл<small>Ђ</small>дка. По сихъ бо придоша Печениз<small>Ђ</small>; и пакы идоша Угри Чернии мимо Киевъ, посл<small>Ђ</small> же при Ольз<small>Ђ</small>.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Інші, не знаючи, кажуть, що Кий був перевізником; бо біля Києва був перевіз тоді з того боку Дніпра, тому й говорили: &laquo;на перевіз на Київ&raquo;, а коли б Кий був перевізником, то не ходив би до Цареграда. Але цей Кий княжив у роду своєму; і ходив він до царя, якого - не знаю, але тільки знаю те, як переказують, що велику честь мав від царя, якого - не знаю і при якому приходив царі.</p>\r\n<p>Ідучи назад, прийшов [Кий] до Дунаю, і вподобав місце, і поставив городок малий, і хотів сісти з родом своїм, і не дали йому ті, що жили поблизу, ще й донині називають дунайці городище Києвець. Кий же прийшов у свій город Київ, тут скінчив життя своє, і брат його Щек, і Хорив, і сестра їхня Либідь - тут померли.</p>\r\n<p>І після тих братів почав їхній рід держати княження в Полях, а в Деревлях - своє, а дреговичі - своє, <em>а </em>словіни - своє в Новігороді, а інші на Полоті, себто полочани. Від цих же і кривичі, що сидять у верхів\'ях Волги, і в верхів\'ях Двіни, і в верхів\'ях Дніпра, і їхній город - Смоленськ, там-бо сидять кривичі. Так само сівери біля них, на Білоозері сидить весь, а на Ростові-озері - меря, на Клещині-озері також сидить меря, а по Оці-ріці, де впадає в Волгу, свою мову має мурома, і черемиси - свою мову, і мордва свою мову. Це-бо тільки в Русі словінська мова: поляни, деревляни, новгородці, полочани, дреговичі, сіверо, бужани, бо сидять по Бузі,- потім волиняни. А це інші народи, що дань дають Русі: чудь, весь, меря, мурома, черемись, мордва, пермь, печера, ям, литва, зимігола, корсь, норома, либ,- ці свою мову мають, всі вони - з коліна Афетового, що живуть в країнах опівнічних.</p>\r\n<p>До словенського народу, що жив, як уже казав, на Дунаї, прийшли від скуф, сказати б від козар, так звані болгари і, осівши по Дунаєві, стали чинити насилля над словенами. А після них прийшли угри білі, і захопили землю словінську, і прогнали волохів, які ще раніше взяли словенську землю. Ці угри з\'явилися за царя Іраклія, який ходив у похід на пірського царя Хоздроя. У ці часи були і обри, що воювали проти царя Іраклія і ледве не полонили його; ці ж обри воювали проти словін, і пригнітили дулібів, теж словінів, і насилля чинили над жінками дулібськими: якщо треба поїхати обрину, то не давав впрягти ні коня, ні вола, а велів впрягти три, або чотири, або й п\'ять жінок у телігу і везти обрина; і так мучили дулібів. Були обри тілом великі, а умом горді, і винищив їх бог, і померли всі, і не залишилося жодного обрина; і є на Русі донині прислів\'я: &laquo;погибоша аки обри&raquo;, і нема ні племені їхнього, ні наслідника. Після них прийшли печеніги, а потім ішли повз Київ угри чорні, але то вже пізніше, за Олега.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Поляномъ живущимъ особ<small>Ђ</small>, якоже ркохомъ, сущии отъ рода Слов<small>Ђ</small>ньска, и наркошася Поляне, а Деревляне отъ Словенъ же, и нарекошася Древляне; Радимичи бо и Вятичи отъ Ляховъ. Бяста бо два брата в Ляс<small>Ђ</small>хъ, Радимъ, а другый Вятокъ, и пришедша с<small>Ђ</small>доста, Радимъ на Съжю, и прозвашася Радимичи, а Вятко с<small>Ђ</small>де своимъ родомъ по Оц<small>Ђ</small>, отъ него прозвашася Вятичи. И живяху в мир<small>Ђ</small> Поляне, и Древляне, и Северо, и Радимичи, и Вятичи, и Хорвати. Дул<small>Ђ</small>би же живяху по Бугу, кде нын<small>Ђ</small> Волыняне, а Улутичи, Тиверци с<small>Ђ</small>дяху по Бугу и по Дн<small>Ђ</small>пру, и прис<small>Ђ</small>дяху къ Дунаеви; и б<small>Ђ</small> множ[с]тво ихъ, с<small>Ђ</small>дяху бо по Бугу и по Днепру оли до моря, и суть городи ихъ и до сего дне, да то ся зовяху отъ Гр<small>Ђ</small>къ Великая Скуфь.</p>\r\n<p>Им<small>Ђ</small>яхуть бо обычая своя, и законы отець своихъ и предания кождо своя норовъ. Поляне бо своихъ отецъ обычаи имяху тихъ и кротокъ, и стыд<small>Ђ</small>нье къ снохамъ своимъ и къ сестрамъ, и къ матеремъ своимъ, и снохы къ свекровамъ своимъ и къ д<small>Ђ</small>веремъ велико стыд<small>Ђ</small>нье имуще; и брачныи обычаи [и] м<small>Ђ</small>аху: не хожаше женихъ по нев<small>Ђ</small>сту, но привожаху вечеръ, а заутра приношаху что на ней вдадуче. А Деревляни живяху зв<small>Ђ</small>рьскымъ образомъ, жівуще скотьскы: и убиваху другъ друга, ядуще все нечисто, и браченья в нихъ не быша, но умыкаху у воды д<small>Ђ</small>вица. А Радимичи, и Вятичи, и Северо одинъ обычай имяху: живяху в л<small>Ђ</small>с<small>Ђ</small>, якоже всякый зв<small>Ђ</small>рь, ядуще все нечисто, и срамословье в нихъ предъ отци и предъ снохами; и бьраци не бываху в нихъ, но игрища межю селы. И схожахуся на игрища, на плясанья и на вся б<small>Ђ</small>совьскыя п<small>Ђ</small>сни, и ту умыкаху жены соб<small>Ђ</small>, с нею же кто св<small>Ђ</small>щевашеся; имяхуть же по дв<small>Ђ</small> и по три жены. И аще кто умряше, творяху трызну надъ нимъ, и по семъ творяху кладу велику, и възложатъ на кладу мертв<small>Ђ</small>ца, и съжигаху, и по семъ събравше кости, вложаху въ ссудъ малъ и поставляху на столп<small>Ђ</small> на путехъ, иже творять Вятичи и нын<small>Ђ</small>. Си же обычай творяху и Кривічи и прочии погании, не в<small>Ђ</small>дуще закона Божиа, но творяху сами себ<small>Ђ</small> законъ.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Поляни ж, як говорилось, жили осібно, належали до словінського роду і назвалися поляни. А деревляни також словени і назвалися древлянами. Радимичі ж і вятичі - від ляхів. Було у ляхів два брати - Радим, а другий Вяток. І прийшли і сіли: Радим на Сожі, і від нього прозвалися радимичі, а Вятко сів зі своїм родом по Оці, від нього і прозвалися в\'ятичі. І жили в мирі поляни, і древляни, і сіверо, і радимичі, і в\'ятичі, і хорвати. Дуліби жили по Бузі, де нині волиняни, а улутичі, тіверці сиділи по Бузі і по Дніпру і присидять до Дунаю, і було без ліку їх, сиділибо по Бузі і по Дніпру аж до моря, і є города їхні і до сьогодні. Край той греки називають Велика Скуф.</p>\r\n<p>Всі мають звичаї свої, і батьківські закони, і перекази, кожен норов свій. Поляни своїх батьків обичаї мають, тихі й покірливі, і стидливість до снох своїх, і до сестер, і до матерів своїх. І снохи до свекрів і діверів своїх велику стидливість виявляють. І брачні обичаї мають: не ходить жених по невісту, а приводять її ввечері, а наступного дня приносять, що за нею дадуть. А деревляни жили позвіриному, жили по-скотськи: і убивали один другого, їли все нечисте, і брачення у них не бувало, а умикали дівиць біля води. А радимичі, і в\'ятичі, і сіверо один обичай мали: жили в лісі, як всякий звір, їли все нечисте, і соромослів\'я в них при батьках і при снохах. І брачення у них не бувало, а ігрища між селами. І сходилися на ігрища, на танцювання, і на всі бісівські пісні, і тут умикали собі жінок, хто з якою зговориться; мали ж по дві і по три жони. А коли хто помирав, творили над ним тризну, опісля робили вогнище велике, і клали на вогнище померлого [в колоді] - і спалювали. Потім збирали кості, вкладали в невеликий сосуд і виставляли на стовпі на путях, як то роблять в\'ятичі і зараз.</p>\r\n<p>Такого ж звичаю дотримувались і кривичі та інші поганії, що не відали закону божого, а самі творили для себе закон.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Глаголеть Георгий в л<small>Ђ</small>тописьц<small>Ђ</small>: ибо комуждо языку ов<small>Ђ</small>мь законъ исписанъ есть, другымъ же обычая; зане безаконнымъ отечьствиемъ мнится. Отъ нихъ же п<small>Ђ</small>рьвое Сирии, живущии на кон<small>Ђ</small>цъ земля, законъ имуть отець своихъ и обычая: не любод<small>Ђ</small>яти, ни пр<small>Ђ</small>любод<small>Ђ</small>яти, ни красти, ни клеветати, ли убити, ли зло д<small>Ђ</small>яти всема отъинудь. Законъ же Иуктиріянъ, глаголемии Върахман<small>Ђ</small> и островичи, иже отъ прад<small>Ђ</small>дъ показаньемь и благочестьемь, мясъ не ядуще, ни вина пьюще, ни блуда І творяще, ни какоя же злобы творяще, страха ради многа. Ибо яв<small>Ђ</small> таче прилежащимъ к нимъ Индомъ, убиствод<small>Ђ</small>йца, скв<small>Ђ</small>рнотворящии, гн<small>Ђ</small>вливи паче естьства; вънутрен<small>Ђ</small>йшии же стран<small>Ђ</small> ихъ челов<small>Ђ</small>кы ядуще, и страньствующихъ убиваху, паче же ядять яко иси. Етеръ же законъ Халд<small>Ђ</small>емъ и Вавилоняномъ: матери поимати и съ братними чады блудъ д<small>Ђ</small>яти, и убивати; всяко бестудьное д<small>Ђ</small>яние яко д<small>Ђ</small>телье мнятся д<small>Ђ</small>юще, любо аще и далече страны своея будуть. Инъ же законъ Гилиомъ: жены у нихъ орють, и хоромы зижють, и мужьскыя д<small>Ђ</small>ла творять, но и любы творить елико хощеть, не възд<small>Ђ</small>ржаемы отъ мужий своихъ отинудь, ни зазрять, в нихъ же суть и хоробрыя жены, ловити зв<small>Ђ</small>р<small>Ђ</small> кр<small>Ђ</small>пькы; влад<small>Ђ</small>ють жены мужьми своими и въдобьляють ими. Въ Вритании же многи мужи съ единою женою спять, такоже и многыя жены съ единымъ мужемъ похотьствують, и безаконьная законъ отецъ творять независтьно, и невъзд<small>Ђ</small>ржанно. Амазоняни же мужа не имуть, акы скотъ бесловесный, но единою л<small>Ђ</small>томъ к вешнимъ днемъ оз<small>Ђ</small>мьствени будуть, и съчитаються съ окр<small>Ђ</small>стными мужи, яко н<small>Ђ</small>которое имъ торжество и велико празднество время т<small>Ђ</small> [мъ] мнять, отъ нихъ заченшимъ в чрев<small>Ђ</small>, и пакы разб<small>Ђ</small>гнутся отсюду вси. Въ время же хотящимъ родити, аще родится отроча, погубять и, аще ли д<small>Ђ</small>вическъ полъ, то въздоять и прил<small>Ђ</small>жьно и въспитають. Якоже се и нын<small>Ђ</small> при насъ Половци законъ д<small>Ђ</small>ржать отець своихъ, кровь проливати, а хвалящеся о семъ, и ядуще мертвечину и всю нечистоту, хомакы и сусолы, и поимають мачехы своя и ятрови, и ины обычая отець своихъ. Но мы же християне, елико земль, иже в<small>Ђ</small>рують въ святую Троицю, и въ едино крещение, и въ едину в<small>Ђ</small>ру, законъ имамъ одинъ, елико в Христа кр<small>Ђ</small>стихомъся, и въ Христа облекохомся.</p>\r\n<p>По сихъ же л<small>Ђ</small>техъ, по смерти братья сея, быша обидими Деревляны и ин<small>Ђ</small>ми околными, и наидоша я Козаре, с<small>Ђ</small>дящая в л<small>Ђ</small>с<small>Ђ</small>схъ на горахъ, и ркоша Козар<small>Ђ</small>: &laquo;платите намъ дань&raquo;. Задумавше же Поляне и вдаша отъ дыма мечь, и несоша Козар<small>Ђ</small> къ князю своему и къ стар<small>Ђ</small>йшинамъ своимъ, и р<small>Ђ</small>ша имъ: &laquo;се нал<small>Ђ</small>зохомъ дань нову&raquo;. Они же р<small>Ђ</small>ша имъ: &laquo;откуду&raquo;? Они же р<small>Ђ</small>ша имъ: &laquo;в л<small>Ђ</small>с<small>Ђ</small> на горахъ, надъ р<small>Ђ</small>кою Дн<small>Ђ</small>прьскою&raquo;. Оні же ркоша: &laquo;что суть вдал<small>Ђ</small>&raquo;? Они же показаша мечь. И р<small>Ђ</small>ша старц<small>Ђ</small> Козарстии: &laquo;не добра дань, княже! мы доискахомся оружьемь одиноя страны, р<small>Ђ</small>кше саблями, а сихъ оружье обоюдуостро, рекше мечи; си имуть имати и на насъ дань и на ин<small>Ђ</small>хъ странахъ&raquo;. Се же събысться все; не отъ своея воля ркоша, но отъ Божия извол<small>Ђ</small>нья.</p>\r\n<p>Яко и при Фараон<small>Ђ</small>, цари Егупетьстемь, егда приведоша Моис<small>Ђ</small>я предъ Фараона, и ркоша старци Фараони: сий хощеть смирить область Егупетську, якоже и бысть. Погыбоша Егуптян<small>Ђ</small> отъ Моис<small>Ђ</small>я, а п<small>Ђ</small>рв<small>Ђ</small>е бьша работающе имъ, тако и си п<small>Ђ</small>рв<small>Ђ</small>е влад<small>Ђ</small>ша, а посл<small>Ђ</small>д<small>Ђ</small> сам<small>Ђ</small>ми влад<small>Ђ</small>ють. Якоже и бысть: волод<small>Ђ</small>ють бо Козары Русьтии князи и до днешняго дне.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Говорить Георгій [Амартол] у літописці: &laquo;Бо кожному народу є спільний писаний закон, а другому - обичаї, що вважається беззаконним батьківським заповітом. Серед таких найперші сірійці, що живуть край світу; закон прийняли, батьками даний, і обичаї: не любодіяти, не чинити перелюб, не красти, не обмовляти, чи убивати, чи, взагалі, творити будь-яке зло. Закон же у іуктиріан, яких ще називають врахманами і островитами, від прадідів за приклад і благочестя взятий, під страхом великим не їсти м\'яса, не пити вина, блуду не творити і ніякої злоби. А у сусідніх з ними індусів діється так: убивають, скверну творять, гнівливі понад міру, у глибині їхньої країни людей їдять, і подорожніх убивають, а найбільше їдять, як пси. Якийсь закон у халдеїв і вавілонян: з матерями одружуються, і з дітьми братів блуд чинять, і убивають; будь-яке соромітне діло вважають за доброчесне і творять його навіть перебуваючи далеко від своєї країни. Інший закон у гіліом: жінки у них орють, і хороми зводять, і мужські справи вершать, але і коханню віддаються досхочу, не стримувані ніскільки чоловіками своїми, і не соромляться їх, жінки в них хоробрі, здатні ловити звірів; владарюють жінки над своїми чоловіками і правлять ними. У Вританії ж багато чоловіків сплять із однією жінкою, а також буває, що кілька жінок з одним чоловіком насолоджуються, і беззаконний закон батьків творять без осуду і спину. Амазонки ж чоловіків не мають, подібно скотам безсловесним, але один раз у рік, весняного дня виходять із своєї землі і злучаються з чоловіками навколишніх місць і вважають той час урочистим і великим святом. Коли ж відчують в утробі зачаття, розбігаються звідти всі. Коли приходить час родити і народжується отроча, гублять його, а якщо дівчинка, то вигодують її старанно і виховають&raquo;.</p>\r\n<p>Так само і зараз, при нас, половці тримаються батьківських законів; кров проливають, ще й хваляться цим, їдять мертвеччину і всіляку нечистоту, хом\'яків і сусолів, і одружуються із своїми мачухами та ятрівками, і інші обичаї батьків своїх [сповняють]. Але ж ми християни, скільки земель, що вірують у святу Трійцю, і в єдине хрещення, і в єдину віру, закон маємо один, оскільки у Христа хрестимося, і в Христа одяглися.</p>\r\n<p>&nbsp;</p>\r\n<p>По цих же роках, після смерті братів отих,</p>\r\n<p>Зазнали утисків [поляни] від деревлян</p>\r\n<p>І інших навколишніх [народів].</p>\r\n<p>І найшли на них козари, де в лісах на горах сиділи,</p>\r\n<p>І рекли козари: &laquo;Платіте нам дань&raquo;.</p>\r\n<p>І порадилися поляни,</p>\r\n<p>І дали від диму меч,</p>\r\n<p>І понесли козари своєму князю</p>\r\n<p>І до старійшин своїх</p>\r\n<p>І вістять їм: &laquo;Це добули нову дань&raquo;.</p>\r\n<p>Вони ж кажуть їм: &laquo;Звідки?&raquo;</p>\r\n<p>Вони ж відповіли їм: &laquo;Із лісу на горах,</p>\r\n<p>Що над рікою Дніпрською&raquo;.</p>\r\n<p>Вони ж питають: &laquo;І що дали?&raquo;</p>\r\n<p>Вони ж показали меч.</p>\r\n<p>І сказали старці козарськії:</p>\r\n<p>&laquo;Недобра дань, княже!</p>\r\n<p>Ми добули їх зброєю однобічною, кажемо - шаблею,</p>\r\n<p>А в них оружжя на два боки гостре, зветься мечі.</p>\r\n<p>Ці будуть і з нас данину брати,</p>\r\n<p>І з інших країн&raquo;.</p>\r\n<p>І збулося все це, бо не з власної волі говорили, а з божого повеління.</p>\r\n<p>&nbsp;</p>\r\n<p>Так було і за фараона, царя єгупетського, коли привели Мойсія перед фараона, і сказали старці фараонови: &laquo;Цей хоче смирити землю Єгупетську&raquo;, так і сталося. Загинули єгуптяни від Мойсія, а ті перш робили на них, так і ці спершу владарювали, а потім ними самими володіють. Так і є: володіють козарами руськії князі і до сьогоднішнього дня.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6360, индикта 15, наченшю Михаилу царьствовати, начася прозывати Руская земля. О семъ бо ув<small>Ђ</small>дахомъ, яко при семъ цари приходиша Русь на Царьградъ, якоже пишеть в л<small>Ђ</small>тописании Гр<small>Ђ</small>цкомъ; т<small>Ђ</small>мьже и отсел<small>Ђ</small> почнемъ и числа положимъ: яко отъ Адама до потопа л<small>Ђ</small>тъ 2242; а отъ потопа до Аврама л<small>Ђ</small>тъ 1082; отъ Аврама до исхожения Моис<small>Ђ</small>ева л<small>Ђ</small>тъ 430; отъ исхожения Моис<small>Ђ</small>ева до Давида л<small>Ђ</small>тъ 601; отъ Давида и отъ начала царства Соломоня до пл<small>Ђ</small>нения Иерусалимова л<small>Ђ</small>тъ 448; отъ пл<small>Ђ</small>нения до Александра л<small>Ђ</small>тъ 318; отъ Александра до Христова Рождества л<small>Ђ</small>тъ 333; отъ Христова Рожьства до Костянтина л<small>Ђ</small>тъ 318; отъ Костянтина же до Михаила сего л<small>Ђ</small>тъ 542. Отъ п<small>Ђ</small>рьваго л<small>Ђ</small>та Михаила сего до п<small>Ђ</small>рваго л<small>Ђ</small>та Олгова, Рускаго князя, л<small>Ђ</small>тъ 29; отъ п<small>Ђ</small>рваго л<small>Ђ</small>та Олгова, понел<small>Ђ</small>же с<small>Ђ</small>де в Киев<small>Ђ</small>, до п<small>Ђ</small>рваго л<small>Ђ</small>та Игорева л<small>Ђ</small>тъ 31; отъ п<small>Ђ</small>рваго л<small>Ђ</small>та Игорева до п<small>Ђ</small>рваго л<small>Ђ</small>та Святославля л<small>Ђ</small>тъ 33; отъ п<small>Ђ</small>рваго л<small>Ђ</small>та Святославля до п<small>Ђ</small>рваго л<small>Ђ</small>та Ярополча л<small>Ђ</small>тъ 28; Ярополкъ княжи л<small>Ђ</small>тъ 8; Володимеръ княжи л<small>Ђ</small>тъ 37. Ярославъ княжи л<small>Ђ</small>тъ 40. Т<small>Ђ</small>мьже отъ смерти Святославля до смерти Ярославли л<small>Ђ</small>тъ 85; отъ смерти Ярославли до смерти Ярополчи л<small>Ђ</small>тъ 60. Но мы на предлежащее възъвратимся и скажемъ, что ся уд<small>Ђ</small>яло в л<small>Ђ</small>та си; якоже преже почали бяхомъ п<small>Ђ</small>рвое л<small>Ђ</small>то Михаила, и по ряду положимъ числа.</p>\r\n<p>В л<small>Ђ</small>то 6361-е.</p>\r\n<p>В л<small>Ђ</small>то 6362-е.</p>\r\n<p>В л<small>Ђ</small>то 6363-е.</p>\r\n<p>В л<small>Ђ</small>то 6364-е.</p>\r\n<p>В л<small>Ђ</small>то 6365-е.</p>\r\n<p>В л<small>Ђ</small>то 6366-е. Михаилъ царь изыде с вои берегомъ и моремъ на Болъгары; Болгар [ы] же увид<small>Ђ</small>вьши, не могоша противу, креститися просиша покорятися Гр<small>Ђ</small>комъ. Царь же крести князя ихъ в бояры вся, и миръ сътвори съ Болгары.</p>\r\n<p>Въ л<small>Ђ</small>то 6367. Имаху дань Варязи приходяще изъ заморья на Чюди и на Слов<small>Ђ</small>нехъ, и на М<small>Ђ</small>ряхъ и на Вс<small>Ђ</small>хъ [и на] Кривичахъ; а Козаре имахуть на Полянехъ, и на С<small>Ђ</small>верехъ, и на Вятичихъ, имаху по б<small>Ђ</small>л<small>Ђ</small> и в<small>Ђ</small>вериц<small>Ђ</small> тако отъ дыма.</p>\r\n<p>В л<small>Ђ</small>то 6368.</p>\r\n<p>В л<small>Ђ</small>то 6369.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6360 [852], індикта 15, коли почав царювати Михайло, почала прозиватися Руська земля. Про це довідалися ми з грецького літопису, в якому сказано, що при цьому царі Русь приходила на Цареград; так от від цієї дати почнемо і відлік зробимо: як від Адама до потопу літ 2242; а від потопу до Аврама літ 1082, від Аврама до відходу Мойсієва літ 430, од відходу Мойсієва до Давида літ 601, від Давида і від початку царювання Соломона до полону Ієрусалима літ 448, од полону до Александра [Македонського] літ 318, од Александра до різдва Христового літ 333, од різдва Христового до Костянтина літ 318, од Костянтина ж до Михайла цього літ 542. Од першого літа Михайла цього до першого літа Ольгова, руського князя, літ 29; од першого літа Ольгова, з того часу, як сів у Києві, до першого літа Ігоревого літ 31; од першого літа Ігоревого до першого літа Святославового літ 33, од першого літа Святославового до першого літа Ярополкового літ 28; Ярополк княжив 8 літ; Володимир княжив літ 37. Ярослав княжив літ 40. Таким чином, од смерті Святослава до смерті Ярослава літ 85; од смерті Ярослава до смерті Ярополка літ 60. Та ми повернемося до початку і скажемо, що діялося в літа ці, як уже раніше почали були з першого літа Михайла, і вряд покладемо числа.</p>\r\n<p>В літо 6361 [853].</p>\r\n<p>В літо 6362 [854].</p>\r\n<p>В літо 6363 [855].</p>\r\n<p>В літо 6364 [856].</p>\r\n<p>В літо 6365 [857].</p>\r\n<p>В літо 6366 [858]. Цар Михайло пішов з воями берегом і морем на болгар; болгари ж побачили, що не зможуть чинити опір, попросили похрестити [їх і обіцяли] покорятися грекам. Цар же похрестив князів їхніх і всіх бояр і мир уклав з болгарами.</p>\r\n<p>В літо 6367 [859]. Із замор\'я прийшли варяги і брали данину на Чуді, і на Словінах, і на Мері, і на Весі, [і на] Кривичах; а козари брали на Полянах, і на Сіверах, і на В\'ятичах, брали по білі і вівериці від диму.</p>\r\n<p>В літо 6368 [860].</p>\r\n<p>В літо 6369 [861].</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6370. И изгнаша Варягы за море, и не даша имъ дани, и почата сами в соб<small>Ђ</small> волод<small>Ђ</small>ти; и не б<small>Ђ</small> в нихъ правды, и въста родъ на родъ, и быша усобиц<small>Ђ</small> в нихъ, и воевати сами на ся почаша. И ркоша: &laquo;поищемъ сами в соб<small>Ђ</small> князя, иже бы волод<small>Ђ</small>лъ нами и рядилъ по ряду, по праву&raquo;. Идоша за море к Варягомъ к Руси,- сіце бо звахуть ты Варягы Русь, яко се друзии зовутся Свее, друзии же Урмани, Аньгляне, инии Готе,- тако и си. Ркоша Русь Чюдь, Словен<small>Ђ</small>, Кривичи и Всь: &laquo;земля наша велика и обилна, а наряда въ ней н<small>Ђ</small>тъ; да поидете княжить и волод<small>Ђ</small>ть нами&raquo;. И изъбрашася трие брата с роды своими, и пояша по соб<small>Ђ</small> всю Русь, и придоша къ Слов<small>Ђ</small>номъ п<small>Ђ</small>рв<small>Ђ</small>е и срубиша городъ Ладогу, и с<small>Ђ</small>де стар<small>Ђ</small>йший в Ладоз<small>Ђ</small> Рюрикъ, а другий Синеусъ на Б<small>Ђ</small>л<small>Ђ</small>озер<small>Ђ</small>, а трет<small>Ђ</small>й Труворъ въ Изборьсц<small>Ђ</small>. И отъ т<small>Ђ</small>хъ Варягъ прозвася Руская земля. По дъвою же л<small>Ђ</small>ту умре Синеусъ и братъ его Труворъ, и прия Рюрикъ власть всю одинъ; и пришедъ къ Ильмерю и сруби городъ надъ Волховомъ, и прозваша и Новъгородъ, и с<small>Ђ</small>де ту княжа и раздая мужемъ своимъ волости, и городы рубати, овому Полътескъ, овому Ростовъ, другому Б<small>Ђ</small>лоозеро. И по т<small>Ђ</small>мь городомъ суть находниц<small>Ђ</small> Варязи; п<small>Ђ</small>рвии населницы в Нов<small>Ђ</small>город<small>Ђ</small> Словен<small>Ђ</small>, и в Полотьск<small>Ђ</small> Кривичи, Ростов<small>Ђ</small> Меряне, на Б<small>Ђ</small>л<small>Ђ</small>озер<small>Ђ</small> Весь, Муром<small>Ђ</small> Мурома, и т<small>Ђ</small>ми вс<small>Ђ</small>ми обладаше Рюрикъ. И бяста у него два мужа, не племени его, Асколдъ и Дирдъ, по боярина, и та испросистася къ Царюграду с родомъ своимъ. поидоста по Дънепру, идучи мимо, и узр<small>Ђ</small>ста на гор<small>Ђ</small> городокъ и въспрошаста, ркуще: &laquo;чий се городъ&raquo;? Они же ркоша: &laquo;была суть три братья, Кий, Щекъ, Хоривъ, иже сд<small>Ђ</small>лаша городъ сий, и изъгыбоша, а мы с<small>Ђ</small>димъ роды ихъ и платимы дань Козаромъ. Асколдъ же и Диръ остаста въ город<small>Ђ</small> семъ, и многы Варягы съвокуписта; и начаста влад<small>Ђ</small>ти Польскою землею. Рюрику же княжящю в Нов<small>Ђ</small>город<small>Ђ</small>.</p>\r\n<p>Л<small>Ђ</small>то 6371.</p>\r\n<p>Л<small>Ђ</small>то 6372.</p>\r\n<p>Л<small>Ђ</small>то 6373.</p>\r\n<p>Л<small>Ђ</small>то 6374. Иде Асколдъ и Диръ на Гр<small>Ђ</small>кы, и и приде въ 14 л<small>Ђ</small>то Михаила царя. Царю же отшедъшю на Агаряны, и дошедшю ему Черное р<small>Ђ</small>кы, в<small>Ђ</small>сть епархъ посла ему, яко Русь идетъ на Царьградъ; и воротися царь. Си же внутрь Суда вшедъше, много убийство християномъ створиша, и въ двою сту кораблий Царьградъ оступиша. Царь же одва в городъ вниде, и с патриарьхомъ Фотиемъ къ сущий церкви святии Богородици Вълахерн<small>Ђ</small>хъ всю нощь молитву створиша, такоже божественую ризу святыя Богородица с п<small>Ђ</small>сьн<small>Ђ</small>ми изнесъше, в р<small>Ђ</small>ку омочиша. Тишин<small>Ђ</small> сущи, и морю укротившюся, абье буря с в<small>Ђ</small>тромъ въста, и волнамъ великымъ въставшимъ засобь, и безъбожныхъ Руси корабля смяте; и къ берегу прив<small>Ђ</small>рже, и изби я, яко малу ихъ отъ таковыя б<small>Ђ</small>ды избыти и въсвояси възвратишася.</p>\r\n<p>В л<small>Ђ</small>то 6375.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6370-го [862].</p>\r\n<p>І прогнали варягів за море,</p>\r\n<p>І не дали їм дані,</p>\r\n<p>І почали самі в себе володіти;</p>\r\n<p>І не було в них правди,</p>\r\n<p>І повстав рід на рід,</p>\r\n<p>І були усобиці в них,</p>\r\n<p>І самі між собою воювати почали.</p>\r\n<p>І сказали: &laquo;Пошукаємо самі в себе князя,</p>\r\n<p>І щоб володів нами,</p>\r\n<p>І суди по ряду, по праву&raquo;.</p>\r\n<p>І пішли за море до варягів, до русі,</p>\r\n<p>І звалися-бо ті варяги русь.</p>\r\n<p>Інші так само зовуться свеї,</p>\r\n<p>Інші - урмани, аньгляне,</p>\r\n<p>Інші - готи,- от так і ці [прозивалися русь].</p>\r\n<p>І сказали русь, чудь, словени, кривичі і весь:</p>\r\n<p>&laquo;Земля наша велика і багата,</p>\r\n<p>А наряду нема в ній;</p>\r\n<p>Ідіть княжити і володіти нами&raquo;.</p>\r\n<p>І зібралися три брати з родами своїми,</p>\r\n<p>І взяли з собою всю русь,</p>\r\n<p>І прийшли найперше до словінів,</p>\r\n<p>І поставили город Ладогу,</p>\r\n<p>І сів найстарший, Рюрик, в Ладозі,</p>\r\n<p>А другий, Синеус,- на Біліозері,</p>\r\n<p>А третій, Трувор,- в Ізборську.</p>\r\n<p>І од тих варягів прозвалась земля Руською.</p>\r\n<p>І як минуло два роки, помер Синеус</p>\r\n<p>І брат його Трувор,</p>\r\n<p>І прийняв Рюрик всю владу один;</p>\r\n<p>І прийшов до Ільмера,</p>\r\n<p>І поставив город над Волховом,</p>\r\n<p>І назвав його Новгород,</p>\r\n<p>І сів тут княжити,</p>\r\n<p>І роздавав мужам своїм волості,</p>\r\n<p>І городи ставив, тому - Полтеск, тому Ростов, другому - Білоозеро.</p>\r\n<p>І в тих городах варяги були нахідники,</p>\r\n<p>А перші посельці в Новігороді - словени.</p>\r\n<p>А в Полоцьку - кривичі, в Ростові - меряни, на Біліозері - весь, на Муромі - мурома,</p>\r\n<p>І всіма ними володів Рюрик.</p>\r\n<p>І було у нього двоє мужів, не його племені,</p>\r\n<p>Але бояри - Аскольд і Дір.</p>\r\n<p>І відпросилися вони до Цареграда з родом своїм.</p>\r\n<p>І пішли по Дніпру,</p>\r\n<p>І, йдучи мимо, уздріли на горі городок</p>\r\n<p>І запитали, кажучи: &laquo;Чий се город?&raquo;</p>\r\n<p>І відповіли вони: &laquo;Було три брати, Кий, Щек, Хорив,</p>\r\n<p>І поставили вони город цей,</p>\r\n<p>І згинули. А ми роду їхнього, сидимо тут</p>\r\n<p>І платимо данину козарам&raquo;.</p>\r\n<p>І осталися Аскольд і Дір в городі цьому,</p>\r\n<p>І багато варягів зібрали</p>\r\n<p>І почали володіти польською землею.</p>\r\n<p>А Рюрик княжив у Новігороді.</p>\r\n<p>&nbsp;</p>\r\n<p>Літо 6371 [863].</p>\r\n<p>Літо 6372 [864].</p>\r\n<p>Літо 6373 [865].</p>\r\n<p>&nbsp;</p>\r\n<p>Літо 6374 [866].</p>\r\n<p>Ходили Аскольд і Дір на греків</p>\r\n<p>І прийшли в 14-те літо Михайла царя,</p>\r\n<p>Цар же ходив на агарян</p>\r\n<p>І дійшов уже був до Чорної ріки,</p>\r\n<p>І послав єпарх йому вість,</p>\r\n<p>Що йде Русь на Цареград</p>\r\n<p>І повернувся цар.</p>\r\n<p>І ввійшли ці в середину Суда,</p>\r\n<p>І багато християн перебили</p>\r\n<p>І двомастами кораблів Цареград обступили.</p>\r\n<p>Цар же ледве у город ввійшов</p>\r\n<p>І з патріархом Фотієм в тамтешній церкві святої Богородиці Влахерніх</p>\r\n<p>Усю ніч молитву творили.</p>\r\n<p>А ще божественну ризу святої Богородиці</p>\r\n<p>Із піснями винесли</p>\r\n<p>І в ріці омочили.</p>\r\n<p>Тиша була, і море спокійне.</p>\r\n<p>Раптом буря із вітром знялася,</p>\r\n<p>І знову й знову вали великі котила,</p>\r\n<p>І безбожних русів кораблі розметала,</p>\r\n<p>І до берега прибила,</p>\r\n<p>І потрощила, що мало їх такої біди уникло</p>\r\n<p>І додому повернулося.</p>\r\n<p>&nbsp;</p>\r\n<p>В літо 6375 [867].</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6376. Поча царствовати Василий.</p>\r\n<p>В л<small>Ђ</small>то 6377. Кр<small>Ђ</small>щена бысть вся земля Болгарьская.</p>\r\n<p>В л<small>Ђ</small>то 6378.</p>\r\n<p>В л<small>Ђ</small>то 6379.</p>\r\n<p>В л<small>Ђ</small>то 6380.</p>\r\n<p>В л<small>Ђ</small>то 6381.</p>\r\n<p>В л<small>Ђ</small>то 6382.</p>\r\n<p>В л<small>Ђ</small>то 6383.</p>\r\n<p>В л<small>Ђ</small>то 6384.</p>\r\n<p>В л<small>Ђ</small>то 6385.</p>\r\n<p>В л<small>Ђ</small>то 6386.</p>\r\n<p>В л<small>Ђ</small>то 6387. Ум<small>Ђ</small>ршю же Рюрикови, предасть княжение свое Олгови, отъ рода ему суща, въдавъ ему на руц<small>Ђ</small> сына своего Игоря; бяще бо молодъ велми.</p>\r\n<p>В л<small>Ђ</small>то 6388.</p>\r\n<p>В л<small>Ђ</small>то 6389.</p>\r\n<p>В л<small>Ђ</small>то 6390. Поиде Олгъ, премъ вои свои многы, Варягы, Чюдь, Слов<small>Ђ</small>ны, М<small>Ђ</small>рю, Весь, Кривичи, [и приде къ Смоленску и с Кривичи] и прия городъ Смольнескъ, и посади в немъ мужь свой. Оттуда поиде внизъ, и прищедъ взя Любечь, и посади мужь свой. И придоста къ горамъ Киевьскымъ, и увид<small>Ђ</small> Олгъ, яко Осколдъ и Диръ княжита, и похорони вои въ лодьяхъ, а другыя назади остави, а самъ приде нося Игоря молода. И приступль подъ Угорьское, похоронивъ вои свои, и посла къ Асколду и Диру, глаголя: &laquo;яко гостье есмы, идемъ въ Гр<small>Ђ</small>кы отъ Олга и отъ Игоря княжичича; да приедета к роду своему к намъ&raquo;. Асколдъ же и Диръ придоста; и выскакаша вси из лод<small>Ђ</small>й, и рече Олгъ къ Асколдови и Дирови: &laquo;вы ни князя, ни роду княжя, но азъ есмь роду княжа&raquo;, и вынесоша Игоря, &laquo;а сь сынъ Рюриковъ&raquo;. И убиша Асколда и Дира, и несоша на гору, еже ся нын<small>Ђ</small> зоветь Угорьское, [идеже нын<small>Ђ</small>] Олминъ дворъ; на той могил<small>Ђ</small> поставилъ Олма церковь святаго Николы; а Дирова могила за святою Ориною. И с<small>Ђ</small>де Олегъ княжа в Кыев<small>Ђ</small>, и рече Олегъ: &laquo;се буди мати городомъ Рускымъ&raquo;. И б<small>Ђ</small>ша у него Слов<small>Ђ</small>ни и Варязи, и прочии прозвашася Русью. Се же Олегъ нача городы ставить; и устави дани Слов<small>Ђ</small>номъ, и Кривичемъ, и Мерямъ; и устави Варягомъ дань даяти отъ Новагорода 300 гривенъ на л<small>Ђ</small>то, мира д<small>Ђ</small>ля, еже до смерти Ярославли даяше Варягомъ.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6376 [868]. Почав царювати Василій.</p>\r\n<p>В літо 6377 [869]. Охрещена була вся земля Болгарська</p>\r\n<p>В літо 6378 [870].</p>\r\n<p>В літо 6379 [871].</p>\r\n<p>В літо 6380 [872].</p>\r\n<p>В літо 6381 [873].</p>\r\n<p>В літо 6382 [874]</p>\r\n<p>В літо 6383 [875].</p>\r\n<p>В літо 6384 [876].</p>\r\n<p>В літо 6385 [877].</p>\r\n<p>В літо 6386 [878].</p>\r\n<p>В літо 6387 [879]. Помер Рюрик і передав княжіння своє Олегові, із свого роду, і віддав йому на руки сина свого Ігоря, бо вельми малий був.</p>\r\n<p>В літо 6388 [880]. В літо 6389 [881].</p>\r\n<p>В літо 6390 [882].</p>\r\n<p>І пішов Олег, взявши воїв своїх багато -</p>\r\n<p>Варягів, чудь, словін, мерю, весь, кривичів,</p>\r\n<p>І прийшов до Смоленська з кривичами,</p>\r\n<p>І взяв город Смоленськ,</p>\r\n<p>І посадив у ньому мужа свого.</p>\r\n<p>І звідти пішов вниз,</p>\r\n<p>І, прийшовши, взяв Любеч,</p>\r\n<p>І посадив у ньому мужа свого.</p>\r\n<p>І прийшов до гір Київських,</p>\r\n<p>І побачив Олег, що Аскольд і Дір княжать,</p>\r\n<p>І заховав воїв у лодіях,</p>\r\n<p>А інших позаду залишив,</p>\r\n<p>А сам пішов, несучи Ігоря малого,</p>\r\n<p>І підійшов під Угорське</p>\r\n<p>[І тут] заховав воїв своїх,</p>\r\n<p>І послав до Аскольда і Діра, кажучи:</p>\r\n<p>&laquo;Купці ми, ідемо до греків від Ольга</p>\r\n<p>І від Ігоря-княжича;</p>\r\n<p>Приходьте до роду свого, до нас&raquo;.</p>\r\n<p>Аскольд же і Дір прийшли.</p>\r\n<p>І вискочили всі із лодій,</p>\r\n<p>І рече Ольг до Аскольда і Діра:</p>\r\n<p>&laquo;Не князі ви і не княжого роду,</p>\r\n<p>Але я княжого роду&raquo;.</p>\r\n<p>І виніс Ігоря: &laquo;А це син Рюриків&raquo;.</p>\r\n<p>І убили Аскольда і Діра,</p>\r\n<p>І віднесли на гору, що й нині зветься Угорське,</p>\r\n<p>І де тепер Ольмин двір.</p>\r\n<p>І на тій могилі поставив Ольма церкву святого Миколи,</p>\r\n<p>А Дірова могила за святою Ориною.</p>\r\n<p>І сів Олег, княжучи в Києві,</p>\r\n<p>І рече Олег: &laquo;Се буде мати городам руським&raquo;.</p>\r\n<p>І були у нього словіни і варяги.</p>\r\n<p>І інші, що прозвалися руссю.</p>\r\n<p>Це ж Олег почав городи ставить, і встановив данину словінам, і кривичам, і мерям, і встановив варягам давати данину од Новагорода триста гривен на літо ради миру, яку і давали варягам аж до смерті Ярослава.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6391. Поча Олегъ воевати на Древляны, и примучивъ я, поча на нихъ дань имать по черьн<small>Ђ</small> кун<small>Ђ</small>.</p>\r\n<p>В л<small>Ђ</small>то 6392. Іде Олегъ на С<small>Ђ</small>вяры, и поб<small>Ђ</small>ди С<small>Ђ</small>веры, и възложи на нихъ дань легъку, и не дасть имъ Козаромъ дани даяти, рекъ: &laquo;азъ имъ противенъ, а вамъ нечему&raquo;.</p>\r\n<p>В л<small>Ђ</small>то 6393. Посла Олегъ к Радимичемъ, ркя: &laquo;кому дань даете?&raquo; они же р<small>Ђ</small>ша: &laquo;Козаромъ&raquo;. И рече имъ Олегъ: &laquo;не давайте Козаромъ, но мн<small>Ђ</small> давайте&raquo;, и вдаша Олгови по щелягу, якоже и Козаромъ даяху. И б<small>Ђ</small> обладая Олегъ Деревляны, Полянми, Радимичи, а со Уличи и Тиверьци им<small>Ђ</small>яше рать.</p>\r\n<p>В л<small>Ђ</small>то 6394.</p>\r\n<p>В л<small>Ђ</small>то 6395. Леонъ царствова, сынъ Васильевъ, иже и Левъ прозвася, и братъ его Александръ, иже царьствоваша л<small>Ђ</small>тъ 26.</p>\r\n<p>В л<small>Ђ</small>то 6396.</p>\r\n<p>В л<small>Ђ</small>то 6397.</p>\r\n<p>В л<small>Ђ</small>то 6398.</p>\r\n<p>В л<small>Ђ</small>то 6399</p>\r\n<p>В л<small>Ђ</small>то 6400.</p>\r\n<p>В л<small>Ђ</small>то 6401.</p>\r\n<p>В л<small>Ђ</small>то 6402.</p>\r\n<p>В л<small>Ђ</small>то 6403.</p>\r\n<p>В л<small>Ђ</small>то 6404.</p>\r\n<p>В л<small>Ђ</small>то 6405.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6391 [883]. Почав Олег воювать проти древлян, і підкорив їх, і почав з них данину брати по чорній куні.</p>\r\n<p>В літо 6392 [884]. Ходив Олег проти сівери, і переміг сіверів, і наклав на них данину легку, і не дозволив їм дані давати козарам, і сказав: &laquo;Я ворог їм, і вам нема чого [данину їм платити]&raquo;.</p>\r\n<p>В літо 6393 [885]. Послав Олег до радимичів, питаючи: &laquo;Кому дань даєте?&raquo; Вони ж відповіли: &laquo;Козарам&raquo;. І рече їм Олег: &laquo;Не давайте козарам, а мені давайте&raquo;. І дали Ольгові по шелягу, як і козарам давали. І володів Олег деревлянами, полянами, радимичами, а з уличами і тіверцями воював.</p>\r\n<p>В літо 6394 [886].</p>\r\n<p>В літо 6395 [887]. Царював син Василів - Леон, якого називали Левом, і брат його Александр, що царював літ двадцять шість.</p>\r\n<p>В літо 6396 [888].</p>\r\n<p>В літо 6397 [889].</p>\r\n<p>В літо 6398 [890].</p>\r\n<p>В літо 6399 [891].</p>\r\n<p>В літо 6400 [892].</p>\r\n<p>В літо 6401 [893].</p>\r\n<p>В літо 6402 [894].</p>\r\n<p>В літо 6403 [895].</p>\r\n<p>В літо 6404 [896].</p>\r\n<p>В літо 6405 [897].</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6406. Ідоша Угре мимо Киевъ горою, еже ся зоветь нын<small>Ђ</small> Угорьское, и пришедше къ Дн<small>Ђ</small>пру, сташа вежами; б<small>Ђ</small>ша бо ходяще яко и Половци. И пришедше отъ въстока и устремишася чересъ горы великыя, иже прозвашася горы Угорьскыя, и почата воевати на живущая ту. С<small>Ђ</small>дяху бо ту преже Словене, и Волохове переяша землю Словенскую; посемъ же Угре прогнаша Волохы, и насл<small>Ђ</small>диша землю ту, и с<small>Ђ</small>доша съ Словеньми, покоривше я подъ ся, и оттол<small>Ђ</small> прозвася земля Угорьска. И начаша воевати Угре на Гр<small>Ђ</small>кы, и пополониша землю Фрачьскую и Македоньску доже и до Селуня, и начаша воевати на Мораву и на Чехы: б<small>Ђ</small> бо единъ языкъ Слов<small>Ђ</small>н<small>Ђ</small>скъ.</p>\r\n<p>Слов<small>Ђ</small>н<small>Ђ</small> же с<small>Ђ</small>дяху по Дунаю, ихъ же прияша Угре, и Морава, и Чеси, и Ляхов<small>Ђ</small>, и Поляне, яже нын<small>Ђ</small> зовемая Русь. Симъ п<small>Ђ</small>рв<small>Ђ</small> положены книга Морав<small>Ђ</small>, яже и прозвася грамота Словеньская, яже грамота е[сть] в Руси и в Болгарехъ Дунайскыхъ.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6406 [898].</p>\r\n<p>Ішли угри мимо Києва горою, що зоветься нині Угорське,</p>\r\n<p>І прийшли до Дніпра,</p>\r\n<p>І стали вежами, бо ходили так, як і половці,</p>\r\n<p>І прийшли зі сходу,</p>\r\n<p>І кинулись через гори великі, що прозвалися гори Угорськії,</p>\r\n<p>І почали воювати проти живущих там.</p>\r\n<p>Сиділи-бо тут раніше словени,</p>\r\n<p>І волохи захопили землю словенську,</p>\r\n<p>А потім угри прогнали волохів,</p>\r\n<p>І посіли землю ту,</p>\r\n<p>І сиділи із словенами, підкоривши їх собі,</p>\r\n<p>І відтоді прозвалася земля Угорська,</p>\r\n<p>І почали воювати угри з греками,</p>\r\n<p>І захопили землю Фрачську</p>\r\n<p>І Македонську - аж до Селуня,</p>\r\n<p>І почали війну проти морави і чехів:</p>\r\n<p>Був-бо один народ словінський -</p>\r\n<p>Словіни ж сиділи по Дунаю, підкорені уграми,</p>\r\n<p>І морава, і чехи, і ляхове,</p>\r\n<p>І поляни, яких нині називають русь.</p>\r\n<p>&nbsp;</p>\r\n<p>З них мораві найперше було покладено книги, від того і грамота прозвалася словенською, від того грамота є в Русі, і в Болгарах Дунайських.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Слов<small>Ђ</small>номъ бо живущимъ крещенымъ и княземъ ихъ, Ростиславъ, и Святополкъ, и Коцелъ послаша къ царю Михаилу, глаголюще: &laquo;земля наша крещена, и н<small>Ђ</small>сть в насъ учитель, иже бы насъ училъ и казалъ, и протолковалъ святыя книгы; не разум<small>Ђ</small>емъ бо ни Гр<small>Ђ</small>чьскому языку, ни Латиньскому; оны бо ны инако учать, а друзии инако; т<small>Ђ</small>мьже не разум<small>Ђ</small>емъ книжнаго разума, ни силы ихъ; а послете ны учителя, иже могуть ны сказати книжная словеса и разумъ ихъ&raquo;. Се слышавъ Михаилъ царь, съзъва философы вся, и сказа имъ р<small>Ђ</small>чи вси Словеньскыхъ князь. И ркоша философы: &laquo;есть мужь в Селуни, именемъ Левъ, и суть у него сынове разумиви языку Словеньску, и хытра два сына у него и философа&raquo;. Се слышавъ царь, посла по ня в Селунь къ Лвови, глаголи: &laquo;пошли к намъ сына своя Мефедья и Костянтина&raquo;. Се слышавъ Левъ, въскор<small>Ђ</small> посла я, и придоста къ цареви, и рече има царь: &laquo;се прислалася ко мн<small>Ђ</small> Словеньская земля, просяще учителя себ<small>Ђ</small>, иже бы моглъ истолковати святыя книгы; сего бо желаютъ&raquo;. И умолена быста царемъ, и послаша я въ Слов<small>Ђ</small>ньскую землю къ Ростиславу, и Святополку, и Коцьлови. Сима же пришедъшима, начаста съставляти писмена азъбуковная Словеньскы, и преложиста Апостолъ и Еуангелие, и ради быша Слов<small>Ђ</small>н<small>Ђ</small>, яко слышаша величья Божия своимъ языкомъ. По семъ же переложиста Псалтырь и Октаикъ, и прочая книгы. Н<small>Ђ</small>ции же начаша хулити Словеньскыя книгы, глаголюще: &laquo;яко не достоить ни которому же языку им<small>Ђ</small>ти абуковъ своихъ, разъв<small>Ђ</small> Евреи, и Гр<small>Ђ</small>къ, и Латины, по Пилатову писанию, еже на крест<small>Ђ</small> Господни написа&raquo;. Се же услышавъ папежь Римьскый, похули т<small>Ђ</small>хъ, иже ропьщють на книгы Слов<small>Ђ</small>ньскыя, рькя: &laquo;да ся исполнить книжное слово, яко въсхвалять Бога вьси язици; другое же: вси възглаголють языки различными величья Божия, яко же дасть имъ Святый Духъ отв<small>Ђ</small>щевати; да аще кто хулить Словеньскую грамоту, да будуть отлучени отъ церкве, дондеже исправятся; ти бо суть волци, а не овц<small>Ђ</small>, яже достоить отъ плодъ позвати я, и хранитися ихъ. Вы же, чада Божия, послушайте учения и не отринете наказания церковнаго, якоже вы наказалъ Мефедий учитель вашь&raquo;. Костяньтинъ же възвратися въспять, и иде учить Болгарьска языка, а Мефедий оста въ Морав<small>Ђ</small>. По семь же Коцелъ князь постави Мефедия епископа въ Пании, на м<small>Ђ</small>ст<small>Ђ</small> святаго апостола Андроника, единого отъ 70 ученика святаго апостола Павла. Мефедий же посади 2 попа борзописца велми, и преложи вся кьнигы исполнь отъ Гр<small>Ђ</small>цька языка въ Слов<small>Ђ</small>н<small>Ђ</small>скъ шестью м<small>Ђ</small>сяць, наченъ отъ марта, м<small>Ђ</small>сяца до двудесяту и 6 дний октября м<small>Ђ</small>сяца. Окончавъ же, достойную хвалу и славу Богу въздасть, дающему таку благодать епископу Мефедью, настолнику Андроникову Т<small>Ђ</small>мь же Слов<small>Ђ</small>ньску языку есть учитель Андроникъ апостолъ: Моравы бо доходилъ и апостолъ Павелъ и училъ ту; ту бо е [сть] Илурикъ, егоже доходилъ апостолъ Павелъ, ту бо бяша Слов<small>Ђ</small>ни п<small>Ђ</small>рв<small>Ђ</small>е. Т<small>Ђ</small>мьже Слов<small>Ђ</small>ньску языку учитель есть Павелъ, отъ негоже языка и мы есме Русь: т<small>Ђ</small>мже и намъ Руси учитель есть Павелъ апостолъ, понеже училъ есть языкъ Слов<small>Ђ</small>нескъ, и поставилъ есть епископа и нам<small>Ђ</small>стника по себ<small>Ђ</small> Андроника Слов<small>Ђ</small>ньску языку А Слов<small>Ђ</small>нескъ языкъ и Рускый одинъ; отъ Варягъ бо прозвашася Русью, а п<small>Ђ</small>рв<small>Ђ</small>е б<small>Ђ</small>ша Слов<small>Ђ</small>не; аще и Поляне звахуся, но Слов<small>Ђ</small>ньская р<small>Ђ</small>чь б<small>Ђ</small>. Полянми же прозвашася, занеже в пол<small>Ђ</small> с<small>Ђ</small>дяху; языкъ Слов<small>Ђ</small>ньскый б<small>Ђ</small> имъ един<small>Ђ</small>.</p>\r\n<p>В л<small>Ђ</small>то 6407.</p>\r\n<p>В л<small>Ђ</small>то 6408.</p>\r\n<p>В л<small>Ђ</small>то 6409.</p>\r\n<p>В л<small>Ђ</small>то 6410. Леонъ царь ная Угры на Болгары. Угре же нашедше, всю землю Болгарьскую пл<small>Ђ</small>новаху; Семенъ же ув<small>Ђ</small>давъ, на Угры възвратися, Угри противу поидоша и поб<small>Ђ</small>диша Болгары, яко одъва Семеонъ въ Деръстеръ уб<small>Ђ</small>жа.</p>\r\n<p>В л<small>Ђ</small>то 6411. Ігореви възрастъшю, и хожаше по Олз<small>Ђ</small> и слушаше его; и приведоша ему жену отъ Плескова именемь Ольгу.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Словіни-бо жили охрещені, і князі їхні, Ростислав, і Святополк, і Коцел, послали до царя Михайла, кажучи: &laquo;Земля наша хрещена, і нема в нас учителя, щоб нас навчав, і наставляв, і протлумачив святі книги; бо не розуміємо ні грецької мови, ні латинської, одні-бо нас поодному вчать, а другі по-іншому, через те не розуміємо книжного розуму, ні їхньої сили. І пошліть нам учителя, що може нам розповісти про книжні слова і їхній розум&raquo;.</p>\r\n<p>Це почувши, Михайло-цар скликав усіх філософів і переказав їм слова словенських князів. І сказали філософи: &laquo;Є муж у Селуні на ймення Лев, і є у нього сини, що знають мову словенську, і мудрі два сини у нього і філософи&raquo;. Почув це цар, послав за ними в Селунь до Лева, кажучи: &laquo;Пошли до нас синів своїх Мефодія і Костянтина&raquo;. Почув це Лев і відразу послав їх. І прийшли до царя, і рече їм цар: &laquo;Це звернулася до мене словенська земля, просячи учителя собі, який би міг витлумачити святі книги, цьогобо бажають&raquo;. І упросив їх цар, і послав їх у словінську землю до Ростислава, і Святополка, і Коцела. Оці прибулі почали складати письмена азбуковні словенські і переклали &laquo;Апостол&raquo; і &laquo;Євангеліє&raquo;, і раді були словіни, що почули велич божу своєю мовою. Після цього переклали &laquo;Псалтир&raquo;, і &laquo;Октоїх&raquo;, і інші книги. Ниції почали хулити словенські книги, кажучи: &laquo;Недостойно жодному народу мати азбуку свою, окрім євреїв, і греків, і латинян, згідно Пілатового писання, що на хресті господньому написав&raquo;. Почув про це папа римський і осудив тих, хто нарікав на книги словінські, кажучи: &laquo;Хай збудеться книжне слово, що восхвалять Бога всі народи&raquo;. І друге: &laquo;Всі різними мовами восхвалять велич божу, оскільки святий Дух дав їм говорити. Якщо ж хтось хулить словенську грамоту, хай буде відлучений від церкви, доки не виправиться; бо то <em>є </em>вовки, а не вівці, їх належить за плодами пізнавати і берегтися їх. Ви ж, чада божії, послухайте учення і не отвергнете повчання церковного, як то вам заповів Мефодій, учитель ваш&raquo;. Костянтин же повернувся назад і став навчати болгарської мови, а Мефодій залишився в Мораві. Потім же князь Коцел настановив Мефодія єпископом в Панії, на місце святого апостола Андроника, одного із сімдесяти учнів святого апостола Павла. Мефодій же посадив двох попів, хороших скорописців, і переклав повністю всі книги із грецької мови на словінську за шість місяців: почав у березні місяці і закінчив 26 дня жовтня місяця. По закінченню ж воздав достойну хвалу і славу Богові, що дав таку благодать єпископові Мефодію, настольнику Андроника. От тому учителем словінського народу є апостол Андроник. До Морави ж приходив і апостол Павло і навчав там; там-бо е їлурик, до нього приходив апостол Павло, там-бо були словіни найперші. От тому учителем словінського народу є [апостол] Павло. Від того народу - і ми, русь; от тому і нам, русі, є учителем апостол Павло, оскільки навчав народ словінський і настановив після себе словінському народові єпископа і намісника Андроника. А словінський народ і руський - один. Від варягів-бо прозвалися руссю, а раніше були словіни; хоч і полянами називалися, але мова була словінська. Полянами прозвалися, оскільки в полі сиділи, а були одного народу словінського.</p>\r\n<p>В літо 6407 [899].</p>\r\n<p>В літо 6408 [900].</p>\r\n<p>В літо 6409 [901].</p>\r\n<p>В літо 6410 [902]. Цар Леон найняв угрів проти болгар. Угри ж, напавши, всю землю болгарську полонили. Семен же, довідавшись, повернув на угрів, угри пішли супроти і перемогли болгар, так що Семеон ледве втік у Дерстер.</p>\r\n<p>В літо 6411 [903]. Ігор виріс і ходив після Олега [збирати данину], і слухали його; і привели йому жону із Плескова на ймення Ольга.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6412.</p>\r\n<p>В л<small>Ђ</small>то 6413.</p>\r\n<p>В л<small>Ђ</small>то 6414.</p>\r\n<p>В л<small>Ђ</small>то 6415. Іде Олегъ на Гр<small>Ђ</small>кы, Игоря остави въ Кыев<small>Ђ</small>; поя же множьство Варягъ, Слов<small>Ђ</small>нъ, и Чюди, и Кривичи, и Мерю, и Поляны, и С<small>Ђ</small>веру, и Деревляны, и Радимичи, и Хорваты, и Дул<small>Ђ</small>бы, и Тиверци, яже суть толковины: си вси звахуться Великая Скуфь. И сь с<small>Ђ</small>ми всеми поиде Олегъ на кон<small>Ђ</small>хъ и в корабл<small>Ђ</small>хъ, и б<small>Ђ</small> числомъ кораблий 2000. И приде къ Царюграду, и Гр<small>Ђ</small>ци замкоша Судъ, а городъ затвориша. И выл<small>Ђ</small>зе Олегъ на берегъ, и повел<small>Ђ</small> воемъ изъволочити корабля на берегъ, и повоева около города, и много убийство створи Гр<small>Ђ</small>комъ, и полаты многы разбиша, а црькви пожьгоша; а ихъже имяху полоняникы, ов<small>Ђ</small>хъ пос<small>Ђ</small>каху, другыя же мучаху, иныя же растр<small>Ђ</small>ляху, а другая въ море вметаша, и ина многа зла творяху Русь Гр<small>Ђ</small>комъ, елико же ратнии творять. И повел<small>Ђ</small> Олегъ воемъ своимъ колеса изъд<small>Ђ</small>лати и въставити корабля на колеса; и бывшю покосну в<small>Ђ</small>тру, успяша пр<small>Ђ</small> с поля, и идяше къ городу. Вид<small>Ђ</small>вше же Гр<small>Ђ</small>ц<small>Ђ</small>, убояшася и ркоша, выславше ко Ольгови: &laquo;не погубляй городъ, имемься по дань, якоже хощеши&raquo;. И стави Олегъ вои, и вынесоша ему брашна и вино, и не прия его; 6<small>Ђ</small> бо устроено съ стравою.</p>\r\n<p>И убояшася Гр<small>Ђ</small>ц<small>Ђ</small> и ркоша: &laquo;н<small>Ђ</small>сть се Олегъ, но святый Дмитрий, посланъ на ны отъ Бога&raquo;. И запов<small>Ђ</small>да Олегъ дань даяти на 2000 кораблий, по 12 гривн<small>Ђ</small> на челов<small>Ђ</small>ка, а в корабли по 40 мужь; и яшася Гр<small>Ђ</small>ци по се, и почаша Гр<small>Ђ</small>ци мира просити, дабы не воевалъ Гр<small>Ђ</small>цькой земли. Олегъ же, мало отступивъ отъ города, нача миръ творити съ царем а Гр<small>Ђ</small>цькыма, съ Леономъ и съ Александромъ, посла к нима в городъ Карла, Фарлофа, Велмуда, Рулава и Ст<small>Ђ</small>мида, глаголя: &laquo;имете ми ся по дань&raquo;. И ркоша Гр<small>Ђ</small>ц<small>Ђ</small>: &laquo;чего хочете, и дамы ти&raquo;. И запов<small>Ђ</small>да Олегъ дати воемъ на 2000 кораблий, по дв<small>Ђ</small>натьцать гривн<small>Ђ</small> на ключь, и потомъ даяти углады на Рускіе городы: п<small>Ђ</small>рвое на Кіевъ, таже и на Черниговъ, и на Переяславъль, и на Полътескъ, и на Ростовъ, и на Любечь, и на прочая городы; по т<small>Ђ</small>мь бо городомъ с<small>Ђ</small>дяху князья подъ Ольгомъ суще: &laquo;да приходять Русь, хл<small>Ђ</small>бное емлють, елико хотять; а иже придуть гостье, да емлють м<small>Ђ</small>сячину на 6 м<small>Ђ</small>сяць, и хл<small>Ђ</small>бъ, и вино, и мяса, и рыбы, и овощемъ, и да творять имъ мовь, елико хотять; и пойду[ть] же Русь домови, да емлють у царя вашего на путь брашно, и якоря, и ужища, и пр<small>Ђ</small> и елико надоб<small>Ђ</small>&raquo;. И яшася Гр<small>Ђ</small>ци, и ркоша царя и боярьство все:</p>\r\n<p>&laquo;аще приидуть Русь бес купли, да не взимають м<small>Ђ</small>сячины; да запр<small>Ђ</small>тить князь людемъ своимъ, приходящимъ Руси зд<small>Ђ</small>, да не творять пакости в сел<small>Ђ</small>хъ и въ стран<small>Ђ</small> нашей; приходящии Русь да витають у святаго Мамы, и послеть царство наше, да испишють имена ихъ, и тогда возмутъ м<small>Ђ</small>сячное свое, п<small>Ђ</small>рвое отъ города Киева, и пакы ис Чернигова, и Переяславля, и прочии городи; и да входять в городъ одіными вороты, съ царевымъ мужемъ, безъ оружья, мужь 50, и да творять куплю, якоже имъ надоб<small>Ђ</small>, не платяче мыта ни в чемьже&raquo;. Царь же Леонъ съ Олександромъ миръ створиста съ Ольгомъ, имъшеся по дань и рот<small>Ђ</small> заходивше межи собою, целовавше сами крестъ, а Ольга водиша и мужий его на роту; по Рускому закону кляшася оружьемь своимъ, и Перуномъ богомъ своимъ, и Волосомъ скотьимъ богомъ, и утвердиша миръ. И рече Олегъ: &laquo;ишийте пре паволочити Руси, а Слов<small>Ђ</small>номъ кропиинныя&raquo;, и бысть тако; и пов<small>Ђ</small>сиша щиты своя въ врат<small>Ђ</small>хъ, показающе поб<small>Ђ</small>ду, и поиде отъ Царяграда. И въспяша Русь пре паволочиты<small>Ђ</small>, а Слов<small>Ђ</small>не кропиинныя, и раздра я в<small>Ђ</small>тръ; и ркоша Словен<small>Ђ</small>: &laquo;имемъся своимъ толъстинамъ, не даны суть Слов<small>Ђ</small>номъ пре кропииныя&raquo;. И приде Олегъ къ Киеву, неся золото, и паволокы, и овощи, и вина, и всяко узорочье и прозваша Ольга в<small>Ђ</small>щий: бяху бо людие погани и нев<small>Ђ</small>голось.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6412 [904].</p>\r\n<p>В літо 6413 [905].</p>\r\n<p>В літо 6414 [9061</p>\r\n<p>&nbsp;</p>\r\n<p>В літо 6415 [907].</p>\r\n<p>І пішов Олег на греків,</p>\r\n<p>Ігоря лишивши в Києві,</p>\r\n<p>І взяв безліч варягів,</p>\r\n<p>І словін,</p>\r\n<p>І чуді, і кривичів, і мері, і полян,</p>\r\n<p>І сіверів, і деревлян, і радимичів, і хорватів,</p>\r\n<p>І дулібів, і тіверців, що є толковини в князя,-</p>\r\n<p>Ці всі звуться &laquo;Великая Скуф&raquo;.</p>\r\n<p>І із цими усіма пішов Олег на конях</p>\r\n<p>І в кораблях,</p>\r\n<p>І було кораблів числом дві тисячі.</p>\r\n<p>І прийшов до Цареграда,</p>\r\n<p>І греки замкнули Суд, а город зачинили.</p>\r\n<p>І зійшов Олег на берег,</p>\r\n<p>І звелів воям ізволочити кораблі на берег,</p>\r\n<p>І звоював околиці города,</p>\r\n<p>І багато греків перебив,</p>\r\n<p>І численні палати розбив,</p>\r\n<p>А церкви попалив,</p>\r\n<p>А кого в полон захопили -</p>\r\n<p>Одних порубали, других же замучили,</p>\r\n<p>Інших же розстріляли, а ще інших в море пометали.</p>\r\n<p>І різного зла багато Русь грекам наробила,</p>\r\n<p>Як то завжди ратнії роблять.</p>\r\n<p>І звелів Олег воям своїм колеса поробити</p>\r\n<p>І поставити кораблі на колеса.</p>\r\n<p>І, маючи попутний вітер, напнули паруси</p>\r\n<p>І пішли по полю на город.</p>\r\n<p>Побачили ж це греки і злякалися,</p>\r\n<p>І рекли, пославши до Олега:</p>\r\n<p>&laquo;Не погубляй город, зберемо данину, яку захочеш&raquo;.</p>\r\n<p>І зупинив Олег воїв,</p>\r\n<p>І винесли йому їжу і вино,</p>\r\n<p>І не прийняв його, бо було заправлене отрутою.</p>\r\n<p>І злякалися греки і сказали.</p>\r\n<p>&laquo;Не Олег це, а святий Дмитрій,</p>\r\n<p>І посланий проти нас богом&raquo;.</p>\r\n<p>І звелів Олег данину давати на дві тисячі кораблів</p>\r\n<p>І по дванадцять гривен на чоловіка,</p>\r\n<p>А в кораблі по сорок мужів,</p>\r\n<p>І погодились греки на це,</p>\r\n<p>І почали миру просити, аби не воював Грецької землі.</p>\r\n<p>І відійшов Олег недалеко від города,</p>\r\n<p>І почав мир творити з царями грецькими</p>\r\n<p>З Леоном і з Олександром.</p>\r\n<p>І послав до них в город Карла, Фарлофа, Вельмуда, Рулава</p>\r\n<p>І Стімида зі словами: &laquo;Платіть мені данину&raquo;.</p>\r\n<p>І сказали греки: &laquo;Чого хочеш, те дамо тобі&raquo;.</p>\r\n<p>І звелів Олег дати воям на дві тисячі кораблів</p>\r\n<p>По дванадцять гривен на весло</p>\r\n<p>І потім давати углади на руські города:</p>\r\n<p>Найперше на Київ, також на Чернігів, і на Переяслав,</p>\r\n<p>І на Полтеськ, і на Ростов,</p>\r\n<p>І на Любеч, і на інші города.</p>\r\n<p>По тих городах сидять князі, Олегу підвладні:</p>\r\n<p>&laquo;Хай приходять руси, хлібне беруть, скільки хочуть;</p>\r\n<p>А коли прийдуть гості, хай беруть місячне на шість місяців,</p>\r\n<p>І хліб, і вино, і м\'яса, і риби, і овочів,</p>\r\n<p>І хай влаштовують їм миття [в лазні], скільки захочуть.</p>\r\n<p>І коли руси повертатимуться додому,</p>\r\n<p>Хай беруть у цісаря вашого на дорогу хліб,</p>\r\n<p>І якорі, і ужища, і паруси,-</p>\r\n<p>І скільки потрібно&raquo;.</p>\r\n<p>І згодилися греки,</p>\r\n<p>І сказали царі і боярство все:</p>\r\n<p>&laquo;А коли прийдуть руси без товарів,</p>\r\n<p>Хай не збирають місячне.</p>\r\n<p>Хай заборонить князь людям своїм,</p>\r\n<p>Русі, що приходить сюди, чинити шкоду на селах</p>\r\n<p>І в країні нашій.</p>\r\n<p>Хай русь, що прибуває, вітають біля святого Мами,</p>\r\n<p>І, коли пришле [до них своїх людей] царство наше,</p>\r\n<p>Хай перепишуть імена їхні,</p>\r\n<p>І тоді візьмуть місячне своє,</p>\r\n<p>Першими ті, що від города Києва,</p>\r\n<p>Потім ті, що від Чернігова, і Переяслава,</p>\r\n<p>І інших городів.</p>\r\n<p>І хай входять у город одними ворітьми,</p>\r\n<p>Із царевим мужем, без оружжя, по п\'ятдесят мужів.</p>\r\n<p>І хай торгують, скільки їм потрібно,</p>\r\n<p>Не сплачуючи ніякого мита&raquo;.</p>\r\n<p>&nbsp;</p>\r\n<p>Царі Леон і Олександр уклали мир з Олегом,</p>\r\n<p>І зобов\'язалися данину платити,</p>\r\n<p>І клятву взаємну давали: самі хрест цілували,</p>\r\n<p>А Олега і мужів його водили на роту за руським законом:</p>\r\n<p>Клялися оружжям своїм, і богом своїм Перуном,</p>\r\n<p>І Волосом, богом скоту.</p>\r\n<p>І так утвердили мир.</p>\r\n<p>І рече Олег: &laquo;Пошийте русам паруси паволочитії,</p>\r\n<p>А словінам шовковії!&raquo;</p>\r\n<p>І було так.</p>\r\n<p>І повісили щити свої на воротях на знак перемоги.</p>\r\n<p>І пішли од Цареграда.</p>\r\n<p>І напнули руси паруси паволочитії,</p>\r\n<p>А словіни шовковії.</p>\r\n<p>І роздер їх вітер,</p>\r\n<p>І сказали словени: &laquo;Напнемо свої полотнянії,</p>\r\n<p>Раз не далися словінам паруси шовковії&raquo;.</p>\r\n<p>І повернувся Олег до Києва, несучи золото і паволоки,</p>\r\n<p>І овочі, і вина, і всіляке узороччя.</p>\r\n<p>І прозвали Олега - віщий, бо люди були ще поганами</p>\r\n<p>І невігласами.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В л<small>Ђ</small>то 6416.</p>\r\n<p>В л<small>Ђ</small>то 6417.</p>\r\n<p>В л<small>Ђ</small>то 6418.</p>\r\n<p>В л<small>Ђ</small>то 6419. Явися зв<small>Ђ</small>зда велика на запад<small>Ђ</small>, коп<small>Ђ</small>йнымъ образомъ.</p>\r\n<p>В л<small>Ђ</small>то 6420. Посла Олегъ мужи свои построити мира и положити ряды межи Гр<small>Ђ</small>кы и Русью; и посла глаголя равко другаго св<small>Ђ</small>щания, бывшаго при т<small>Ђ</small>хъ же царихъ Лва и Александра.</p>\r\n<p>&laquo;Мы отъ рода Рускаго, Карлы, Инегелдъ, Фарлофь, Веремудъ, Рулавъ, Гуды, Руалдъ, Карнъ, Фрелавъ, Рюаръ, Актеву, Труанъ, Лидульфостъ, Стемиръ, иже послани отъ Олга, великаго князя Рускаго, и отъ вс<small>Ђ</small>хъ, иже суть подъ рукою его, ев<small>Ђ</small>тълыхъ бояръ, к вамъ, Львови и Александру и Костянтину, великымъ о Боз<small>Ђ</small> самод<small>Ђ</small>ржьцемъ, царемь Гр<small>Ђ</small>цкымъ, на уд<small>Ђ</small>ржание и на изв<small>Ђ</small>щение отъ многыхъ л<small>Ђ</small>тъ межю Християны и Русью бывшюю любовь похот<small>Ђ</small>ньемъ нашихъ князь и по повел<small>Ђ</small>нию, и отъ вс<small>Ђ</small>хъ, иже суть подъ рукою его сущихъ Руси. Наша св<small>Ђ</small>тлость боле ин<small>Ђ</small>хъ хотящихъ же о Боз<small>Ђ</small> уд<small>Ђ</small>ржати и изв<small>Ђ</small>стити такую любовь, бывшюю межю Християны и Русью, многажды право судихомъ, но точью простословесенъ, и писаниемь и клятвою твердою, кл<small>Ђ</small>ншеся оружьемь своимъ, такую любовь изв<small>Ђ</small>стити и утв<small>Ђ</small>рдити по в<small>Ђ</small>р<small>Ђ</small> и по закону нашему. Суть, яко понеже мы ся имали о Божии в<small>Ђ</small>р<small>Ђ</small> и любви, главы таковыя: По п<small>Ђ</small>рвому слову да умиримся с вами, Гр<small>Ђ</small>кы, да любимъ другъ друга отъ всея душа и извол<small>Ђ</small>нья, и не вдадимъ, елико наше изволение быти отъ сущихъ подъ рукою нашихъ князь св<small>Ђ</small>тлыхъ, никакому же съблазну или вин<small>Ђ</small>; но потщимся, елико по сил<small>Ђ</small>, на схранение прочихъ и вьсегда л<small>Ђ</small>тъ с вами, Гр<small>Ђ</small>кы, испов<small>Ђ</small>даниемь и написаниемъ съ клятвою изв<small>Ђ</small>щаемую любовь непревратну и непостыжну. Такоже и вы, Гр<small>Ђ</small>ци, да храните таку же любовъ къ княземъ же св<small>Ђ</small>тлымъ нашимъ Рускымъ и къ вс<small>Ђ</small>мъ, иже суть подъ рукою св<small>Ђ</small>тлаго князя нашего, несъблазнену и непреложну всегда. и въ вся л<small>Ђ</small>та. А о головахъ, иже ся ключють проказа, урядимся сице: Да елико яв<small>Ђ</small> будеть показании явлеными, да им<small>Ђ</small>ють в<small>Ђ</small>рное о тац<small>Ђ</small>хъ явлении; а ему же начнуть не яти в<small>Ђ</small>ры, да не кленется часть та, Иже ищеть неятью в<small>Ђ</small>ры; да егда кл<small>Ђ</small>нется по в<small>Ђ</small>р<small>Ђ</small> своей, будеть казнь, якоже явится съгр<small>Ђ</small>шение о семъ, Аще кто убиеть, Крестьянина Русинъ, или Христьянинъ Русина, да умреть, идеже а ще створить убийство. Аще ли уб<small>Ђ</small>жить створивши убийство, аще есть имовить, да часть его, сир-<small>Ђ</small>чь иже его будеть по закону, да возметь ближний убьенаго; а иже на убившаго, да им<small>Ђ</small>еть толц<small>Ђ</small>мь же прибудеть по закону; аще ли есть неимовитъ створивый убойство и уб<small>Ђ</small>жавъ, да д<small>Ђ</small>ржится тяжи, доньдеже обрящется, яко да умреть. Аще ли ударить мечемь или бьеть кац<small>Ђ</small>мъ любо съсудомъ, за то ударение или убьение да вдасть литръ 5 сребра по закону Рускому; аще ли будеть неимовить тако створивый, да вдасть елико можеть и да соиметь съ себе и ты самыя порты своя, в нихъже ходить, а опроч<small>Ђ</small> да рот<small>Ђ</small> ходить своею в<small>Ђ</small>рою, яко никакоже иному помощи ему, да пребываеть тяжа оттол<small>Ђ</small> не взискаема о семь. Аще украдеть Русинъ что любо у Кр<small>Ђ</small>стьянина, или пакы Христьянинъ у Русина, и ятъ будеть в томъ час<small>Ђ</small> тать, егда татьбу сътворить, отъ погубившаго что любо, аще приготовится, татьбу творяй и убиень будетъ: да не възыщется смерть его ни отъ Христьянъ, ни от Руси, но паче убо да възметь свое, иже будеть погубилъ. И аще въдасть руц<small>Ђ</small> украдый, да ятъ будеть т<small>Ђ</small>мь же, у негоже будеть украдено, и связанъ будеть, и отдасть то, еже см<small>Ђ</small> створить, и створить трижды о семь. Аще ли кто, или Русинъ Хр<small>Ђ</small>стьяну, или Хр<small>Ђ</small>стьянъ Русину, мучения образомъ искусъ творити и насилье яв<small>Ђ</small>, или възметь что либо дружинне, да въспятить троичь. Аще выв<small>Ђ</small>ржена лодья будеть в<small>Ђ</small>тромъ великомъ на землю чюжю, и обрящються тамо иже отъ насъ Руси, да аще кто идеть снабьд<small>Ђ</small>ти лодью с рухломъ своимъ, и отсылати пакы на землю Крестьяньску, да проводимъ ю сквоз<small>Ђ</small> всяко страшно м<small>Ђ</small>сто, дондеже придеть в бестрашно м<small>Ђ</small>сто; аще и таковая лодья, и отъ буря, или боронения земнаго боронима, не можеть възборонитися въ своя си м<small>Ђ</small>ста, спотружаемъся гр<small>Ђ</small>бцем бо тоя лодья, мы Русь, и допровадимъ с куплею ихъ нездорову, та аще ключится близъ земли Гр<small>Ђ</small>цькы; аще ли ключится такоже проказа лодья Руст<small>Ђ</small>й, да проводимъ ю въ Рускую земьлю, и да продають рухло тоя лодья, и аще что можеть предати отъ лодья, воволочимъ имъ, мы Русь, да егда ходимъ въ Гр<small>Ђ</small>кы или съ куплею, или в солбу, къ цареви [в] ашему, да пустимъ я съ честью проданое рухло лодья ихъ; аще ли ключится кому отъ тоя лодья в ней убьену быти, или бьену быти отъ насъ Русі, или взяти что любо, да повиньни будуть то створшии прежде реченную епит<small>Ђ</small>мьею. Отъ т<small>Ђ</small>хъ аще полоняникъ обою страну д<small>Ђ</small>ржимъ есть, или отъ Руси, или отъ Гр<small>Ђ</small>къ, проданъ въ ину страну; оже обрящеться или Русинъ или Гр<small>Ђ</small>чинъ, да не купять и възвратять и скупленое лице въ свою страну, и възмуть ц<small>Ђ</small>ну его купящии, или мниться въ куплю на дань челядиная ц<small>Ђ</small>на; такоже аще отъ рати ятъ будеть, да отъ т<small>Ђ</small>хъ Гр<small>Ђ</small>къ такоже да възвратится въ свою страну, и отдана будеть ц<small>Ђ</small>на его, якоже речено есть, якоже есть купля. Егда же требуеть на войну ити, егда же потребу творите, и си хотять почестити царя вашего, да аще въ кое время елико ихъ придеть и хотять оставити у царя вашего, своею волею да будуть. О Руси о полон<small>Ђ</small>ньи. Многажды отъ коея убо страны пришедшимъ в Русь и продаемомъ въ Кристьяны, и еще же и отъ Християнъ полонныхъ мьногажды отъ коея любо страны приходящимъ в Русь: се продаеми бывають по 20 золота и да придуть въ Гр<small>Ђ</small>кы. О томъ аще украденъ будеть челядинъ Рускый, или въскочить, или по нужи проданъ будеть, и жаловати начнуть Русь, да покажеться таковое отъ челядина, да имуть и въ Русь; но и гостье погубіша челядинъ, и жалують, да ищють и, обр<small>Ђ</small>таемое да имуть е; аще ли кто искушения сего не дасть створити, м<small>Ђ</small>стникъ да погубить правду свою. О работающихъ въ Гр<small>Ђ</small>ц<small>Ђ</small>хъ Руси у Христьяньского царя: Аще кто умреть, не урядивъ своего им<small>Ђ</small>нья, ци и своихъ не имать, да възвратить им<small>Ђ</small>нье къ малымъ ближикамъ в Русь; аще ли створить обрящение, таковый възметь уряженое его, кому будеть писалъ насл<small>Ђ</small>дити им<small>Ђ</small>нье, да насл<small>Ђ</small>дить е о [ть] взимающихъ куплю Руси отъ различныхъ ходящихъ въ Гр<small>Ђ</small>кы и удолжающихъ. Аще злод<small>Ђ</small>й възвратится в Русь, да жалують Русь Христьяньскому царству, и ятъ будеть таковый и възвращенъ будеть не хотяй в Русь. Си же вся да творять Русь Гр<small>Ђ</small>камъ, идеже аще ключится таково. На утвержение же и неподвижение быти межи вами Христьяны и Русью, бывший миръ сътворихомъ Ивановомъ написаниемъ на двою харотью, царя вашего и своею рукою, предлежащимъ честнымъ крестомъ и святою единосущною Троицею, единаго истиньнаго Бога нашего, изв<small>Ђ</small>сти и дасть нашимъ словомъ; мы же кляхомся къ царю вашему, иже отъ Бога суще яко Божие здание по закону, и по покону языка нашего, не переступати ни намъ ни иному отъ страны нашея отъ уставленыхъ глаголовъ мира и любве. И таково написание дахомъ царства вашего на утв<small>Ђ</small>ржение обоему пребывати, таковому св<small>Ђ</small>щанию, на утв<small>Ђ</small>ржение и изв<small>Ђ</small>щение межи вами бывающаго мира, м<small>Ђ</small>сяца себтября въ 2, а в нед<small>Ђ</small>лю 15, в л<small>Ђ</small>то создания миру 6420&raquo;.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>В літо 6416 [908].</p>\r\n<p>В літо 6417 [909].</p>\r\n<p>В літо 6418 [910].</p>\r\n<p>В літо 6419 [911]. Велика списоподібна звізда була видна на заході.</p>\r\n<p>В літо 6420 [912]. Послав Олег мужів своїх встановити мир і заключити договір між греками і руссю. І послав запропонувати основу другого договору [копію попереднього] , що був за тих же царів - Лева і Александра.</p>\r\n<p>&laquo;Ми від роду руського - Карли, Інегельд, Фарлоф, Веремуд, Рулав, Гуди, Руальд, Карн, Фрелав, Рюар, Актеву, Труан, Лідульфост, Стемир - послані від Олега, великого князя руського, і від усіх світлих бояр, які під його рукою, до вас - Лева, і Александра, і Костянтина, великих у бозі самодержців, царів грецьких,- на зміцнення і засвідчення багатолітньої любові між християнами і руссю за бажанням і з наказу наших князів і всіх, що під його рукою живуть на Русі. Наша світлість [князь] більше за інших бажає милістю божою зміцнити і скріпити таку любов, що була між християнами і руссю, многократно по праву судив, не тільки простослівно, а й на письмі, і клятвою твердою, бо клялися оружжям своїм,- таку любов [хоче князь Олег] утвердити і скріпити згідно віри і закону нашого.</p>\r\n<p>Статті [договору], якими себе зобов\'язали по божій вірі і дружбі, суть такі:</p>\r\n<p>Насамперед хай настане мир з вами, греки, і будемо любить один другого від усієї душі і з доброї волі, і не дамо, оскільки це в нашій волі, вчинити сущим світлим князям під рукою нашою, жодного обману чи злочину, але будемо старатися, скільки сили нашої, зберігати з вами, греки, в наступні роки і завжди, непорушну і щиру любов, розказану, і на письмі записану, і клятвою засвідчену. Так само і ви, греки, зберігайте таку ж непорушну і щиру любов до світлих князів наших руських і до всіх сущих під рукою світлого князя нашого завжди і на вічні часи. А про статті, що дотикаються можливого вчинення злочинів, домовимось так: як тільки будуть явно засвідчені провини, хай вони вважаються злочинами, що безперечно скоєні, а коли почнуть не йняти віри, хай не клянеться та сторона, що домагається не йняти віри, а нехай клянеться в тому, у що вірить, і хай буде кара [такою], яким виявиться вчинений гріх. Про це:</p>\r\n<p>А коли хто уб\'є,- християнина русин чи християнин русина,- хай помре на місці вбивства.</p>\r\n<p>А коли убивця втече, а виявиться багатим, хай частину багатства, яка належить йому по закону, візьме ближній забитого, але й жона вбивці хай має стільки, скільки належить по закону.</p>\r\n<p>А коли убивця є неімущим і втік, хай перебуває під судом, доки знайдеться, а тоді помре.</p>\r\n<p>А якщо ударить мечем, чи битиме кацією або сосудом, хай за ті побої чи й убивство дає п\'ять літрів срібла, згідно закону Руського.</p>\r\n<p>А коли винуватець буде неімущим, хай сплачує скільки зможе і хай зніме з себе і ті штани свої, в яких ходить, а про решту хай клянеться своєю вірою, що ніхто інший йому не допоможе, і хай суд після цього не стягує з нього решти за це.</p>\r\n<p>А коли вкраде що-небудь русин у християнина чи, навпаки, християнин у русина, і злодій буде спійманий потерпілим у той час, коли крадіжку чинив або готувався її вчинити, і буде вбитий, то хай не буде покарано смертю його ні християнами, ні руссю, але потерпілий хай візьме своє, що втратив.</p>\r\n<p>А коли ж злодій добровільно віддасться в руки і буде взятий тим, у кого було вкрадено, то хай буде зв\'язаний і віддасть те, що посмів украсти, потрійно за це. А коли хтось, чи русин християнину, чи християнин русину, вчинить побої і спокуситься на грабунок і виявить [при цьому] насильство чи візьме що-небудь дружинне,- хай поверне потрійно.</p>\r\n<p>А коли вітром великим викине на землю чужу лодію, і там знайдеться хто-небудь із нас, русі, і коли хто піде забезпечити лодію зі своїм товаром і відіслати знову в землю християнську, ми проведемо її через будь-яке небезпечне місце, доки не прийде в місце безпечне.</p>\r\n<p>А коли така лодія бурею чи іншими земними стихіями затримується і не може безперешкодно повернутися в рідні краї, ми, русь, допоможемо гребцями тій лодії і відпровадимо з їхніми покупками,- хай ідуть здорові.</p>\r\n<p>А коли подібне приключиться поблизу Грецької землі, коли трапиться така біда лодії руській, то проведемо її в Руську землю, і хай продають товари з тієї лодії, і коли що можна продати із лодії, то щоб ми, руси, [могли без перешкоди] винести [товари на грецький берег].</p>\r\n<p>А коли [ми, русь] прийдемо до греків чи для торгівлі, чи з посольством до вашого царя, то [ми, греки] з честю пропустимо для продажу товари з руських лодій.</p>\r\n<p>А коли трапиться, що хто-небудь із нас, русі, із тої лодії буде вбитий, чи побитий, чи взято що-небудь, то винуватці вчиненого мають понести вищезазначені покарання.</p>\r\n<p>Про тих, що з полоні обома країнами утримуються, або із Русі, чи із Греції продані в іншу країну: коли відшукається чи русин, чи грек, нехай викуплять і повернуть викупленого в його країну і візьмуть ціну його ті, що викупили, або хай буде запропонована ціна, належна за челядника.</p>\r\n<p>Також коли від раті взятий буде, то від тих греків також хай повернеться в свою країну, і віддана буде ціна його, як уже сказано чи яка прийнята в торгівлі [людьми].</p>\r\n<p>А коли потрібно буде на війну іти, коли ж потребу оголосите, і ці [руські вої] захочуть вшанувати царя вашого, то в який би час і скільки б їх не прийшло, і захочуть залишитися у царя вашого,- хай буде їхня воля.</p>\r\n<p>Про Русь і про полонених: полонені із будь-якої країни і в будь-якій кількості, взяті на Русі, продаються у християнські землі, так само і полонені християни, приведені на Русь із будь-якої країни і в будь-якій кількості: за кожного має бути заплачено по двадцять золотих,- і хай повертаються в Грецьку землю.</p>\r\n<p>Про те, якщо буде украдено руського челядина, або він втече, або буде силоміць проданим, і Русь почне оскаржувати, і доведе це про свого челядина,- хай забирають - і на Русь. Але і купці, коли втратять челядина і оскаржать, хай шукають його, а коли знайдуть, хай заберуть його. А коли хто не дозволить провести таке слідство, той втратить правду свою.</p>\r\n<p>Про русів, що працюють у Грецькій землі в християнського царя: коли хто помре, не влаштувавши свої майнові справи чи своїх там не матиме, хай повернуть майно на Русь дітям його. А коли зробить заповіт, то відказане хай візьме той, кому померлий заповів успадкувати майно, хай успадкує його через тих, хто займається на Русі торгівлею, з цією метою ходить у Грецьку землю і бере позичку. А коли злочинець [не] повернеться на Русь, хай Русь скаржиться християнському царству, і такий буде спійманий і повернений на Русь примусово. Це ж саме хай робить і Русь Грецькій землі, якщо таке приключиться.</p>\r\n<p>На утвердження ж і непорушність, яка має бути між вами, християнами, і руссю, уклали ми цей мирний договір Івановим написанням на двох хартіях - царя вашого і своєю рукою,- предпокладений чесним хрестом і святою єдиносущною Трійцею єдиного істинного Бога нашого, засвідчений і даний нашим словом [послам]. Ми ж клянемося цареві вашому, від бога сущого, як божому створінню, згідно закону і звичаю народу нашого, ні нами, ні будь-ким іншим із країни нашої не переступати глаголів миру договірних і любові.</p>\r\n<p>І таке написання дали царству вашому на утвердження і засвідчення вам існуючого між нами миру.</p>\r\n<p>Місяця себтября в 2-й день, а в неділлю 15, в літо від сотворіння світу 6420 [912]&raquo;.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Царь же Леонъ послы Рускыя почстивъ дарми, золотомъ, и паволоками, и фофудьями, и пристави къ нимъ мужи свои показати имъ церковьную красоту, и полаты златыя, и в нихъ сущая богатьства, злато много и паволокы и кам<small>Ђ</small>нье драгое, и страсти Господни, в<small>Ђ</small>н<small>Ђ</small>ць, и гвоздье, и хламиду багряную, и мощи святыхъ, учаще я к в<small>Ђ</small>р<small>Ђ</small> своей и показающе имъ истинную в<small>Ђ</small>ру; и тако отпусти я въ свою землю съ честью великою. Послании же Ольгомъ посли придоша къ Олгови и пов<small>Ђ</small>даша вся р<small>Ђ</small>чи обою царю, како створиша миръ, и урядъ положиша межю Гр<small>Ђ</small>цькою землею и Рускою, и клятвы не переступати ни Гр<small>Ђ</small>цемь ни Руси. И живяше Олегъ миръ им<small>Ђ</small>я къ вс<small>Ђ</small>мъ странамъ, княжа въ Киев<small>Ђ</small>. И присп<small>Ђ</small> осень, и помяну Олегъ конь свой, иже б<small>Ђ</small> поставилъ кормити, не вс<small>Ђ</small>дати на нь. Б<small>Ђ</small> бо преже въпрошалъ волъхвовъ кудесникъ: &laquo;отъ чего ми есть умьрети?&raquo; И рече ему одинъ кудесникъ: &laquo;княже! конь, егоже любиши и <small>Ђ</small>здиши на немъ, отъ того ти умрети&raquo;. Олегъ же приимь въум<small>Ђ</small>, си рече: &laquo;николи же всяду на конь, ни вижю его боле того&raquo;; и пов<small>Ђ</small>л<small>Ђ</small> кормити и и не водити его к нему, и пребывъ н<small>Ђ</small>колко л<small>Ђ</small>тъ . не д<small>Ђ</small>я его, дондеже и на Гр<small>Ђ</small>кы иде. И пришедшю ему къ Киеву, и пребысть 4 л<small>Ђ</small>та, на 5 л<small>Ђ</small>то помяну конь свой, отъ него же бяху рекъли волъстви умрети Ольгови, и призва стар<small>Ђ</small>йшину конюхомъ, рекя: &laquo;где есть конь мой, его же б<small>Ђ</small>хъ поставилъ кормити и блюсти его?&raquo; Онъ же рече: &laquo;умерлъ есть&raquo;. Олегъ же посм<small>Ђ</small>яся и укори кудесника, рекя: &laquo;то ть не право молвять волъсві, но все то лъжа есть; конь умерлъ, а я живъ&raquo;. И пов<small>Ђ</small>л<small>Ђ</small> ос<small>Ђ</small>длати конь: &laquo;да ть вижю кости его&raquo;. И при<small>Ђ</small>ха на м<small>Ђ</small>сто, идеже бяху лежаще кости его голы и лобъ голъ; и сл<small>Ђ</small>зъ с коня, посм<small>Ђ</small>яся рекя: &laquo;отъ сего ли лъба смерть мн<small>Ђ</small> взяти?&raquo; и въступи ногою на лобъ; и выникнучи зм<small>Ђ</small>я, и уклюну и в ногу, и с того разбол<small>Ђ</small>вся умьре. И плакашася по немъ вси людие плачемъ великомъ, и несоша и, и погребоша и на гор<small>Ђ</small>, иже глаголеться Щековица; есть же могила его до сего дни, словеть могила Олгова. И бысть вс<small>Ђ</small>хъ л<small>Ђ</small>тъ его княжения 33, Се же дивно есть, яко отъ волъхвования сбывается чарод<small>Ђ</small>ствомъ. Якоже бысть во царство Деменьтьяново, н<small>Ђ</small>кый волъхвъ, именемъ Аполоній Тіянинъ, знаемъ бяше, шествуя и творя всюду в городехъ и в сел<small>Ђ</small>хъ б<small>Ђ</small>совьская чудеса творя, отъ Рима бо пришедъ въ Узантию, умоленъ бысть отъ живущихъ ту створити сия, отгна множьство змий и скоропия изъ града, яко не вьрежатися челов<small>Ђ</small>комъ отъ нихъ; ярость коньскую обуздавъ, егда схожахуся бояр<small>Ђ</small>. Такоже и въ Антиохию пришедъ, и умоленъ бывъ отъ нихъ, [томимомъ бо Антиохомъ отъ скорпий и отъ комаровъ], створи скорпий м<small>Ђ</small>дянъ, и погребе и в земли, и малъ столпъ мраморянъ постави надъ нимъ, и повел<small>Ђ</small> трость д<small>Ђ</small>ржати челов<small>Ђ</small>комъ и ходити по городу, звати, тростемъ трясомомъ: &laquo;бес комара граду&raquo;; тако изъщезоша изъ града комари и скорпия. Испросиша же и пакы отъ лежащими на град<small>Ђ</small> трусъ, въздохну, списа на дщиц<small>Ђ</small> сия: &laquo;увы тебе, оканьный городе, яко потрясешися много, од<small>Ђ</small>ржимъ будеши огнемъ; ополчать же тя и при берез<small>Ђ</small> Сиорити&raquo;! О немь же и великий Анастасий Божия города рече: &laquo;Аполонию же доже и до нын<small>Ђ</small> на н<small>Ђ</small>ц<small>Ђ</small>хъ м<small>Ђ</small>стехъ сбываются створенаа, стоящая, окована отвращение четвероногъ, птица могущи вредити челов<small>Ђ</small>кы, другыя же на възд<small>Ђ</small>ржание струямъ р<small>Ђ</small>чнымъ незд<small>Ђ</small>ржаньно текущимъ, но ина н<small>Ђ</small>кая на тл<small>Ђ</small>нье и вредъ челов<small>Ђ</small>комъ суща, на поб<small>Ђ</small>жение стоять. И не точью бо за живота его така и таковая створиша б<small>Ђ</small>сов<small>Ђ</small> его ради, но и по смерти его пребывающа у гроба его знаменья творяху во имя его, а на прелещение оканнымъ челов<small>Ђ</small>комъ, болма крадомымъ на таковая отъ дьявола&raquo; Кто убо что речеть о творящихъ волшевныхъ д<small>Ђ</small>лъ? яко то таковый горазнъ бысть волшебнымъ прелщениемъ, яко выну зазряше в<small>Ђ</small>дый Аполоний, яко неистовьстве на ся философьскую хитрость имуща подобашеть бо ему, рещи, якоже и азъ, словомъ точью твориті ихъже хотяше, а не св<small>Ђ</small>ршениемъ творити повел<small>Ђ</small>ваемая отъ него. Таже вся ослаблениемъ Божиимъ и творениемъ б<small>Ђ</small>совьскымъ бываеть, таковыми вещьми искушатися нашея православныя в<small>Ђ</small>ры, аще тверда есть [и] искрь пребывающи Господеви, ни не влекома врагомъ мечетныхъ ради чюдесъ и сотонинъ д<small>Ђ</small>лъ, творимомъ отъ рабъ и слугъ злоб<small>Ђ</small>. И еще именемъ Господнимъ пророчьствоваша н<small>Ђ</small>ции, яко Валамъ, и Саулъ, и Каняфа, и б<small>Ђ</small>съ пакы изгнаша, яко Июда и сынове Скевави. Убо и на недостойнии благодать [д<small>Ђ</small>й] ствуеть многажды, да етеры зд<small>Ђ</small>тельствуеть. Ибо Валаамъ чюжь б<small>Ђ</small> обоихъ, житья и в<small>Ђ</small>ры; по обаче св<small>Ђ</small>д<small>Ђ</small>тельство [ва] в немъ благодать ин<small>Ђ</small>хъ ради смотрения. И Фараонъ таковый б<small>Ђ</small>, но и тому предбудущая показа. И Навходъносоръ законопреступный, но и сему пакы по мноз<small>Ђ</small>хъ сущихъ посред<small>Ђ</small> же града откры; т<small>Ђ</small>мь являя, яко мнози прекостьни имуще умъ, предъ образомъ Христовымъ знаменають иною кознью на прел<small>Ђ</small>сть челов<small>Ђ</small>комъ не разум<small>Ђ</small>ющимъ добраго, якоже бысть Симонъ волъхвъ, и Менердъ, [и] ини таковыхъ ради, поистин<small>Ђ</small>, рече, не чюдесы прельщати.</p>\r\n</td>\r\n<td width=\"50%\" valign=\"top\">\r\n<p>Цар же Леон вшанував послів руських дарами - золотом, і паволоками, і дорогим одягом князівським, і приставив до них мужів своїх показати їм церковну красу, і палати золоті, і сущі в них багатства: багато золота, і паволоки, і каміння дорогоцінного, і страсті господні - вінець, і цвяхи, і хламиду багряну, і мощі святих, навертаючи їх до віри своєї і показуючи їм істинну віру. І так відпустив їх в свою землю з почестями великими.</p>\r\n<p>Послані ж Олегом посли повернулися до нього і переповіли йому слова обох царів, як мир уклали і договір заключили між Грецькою землею і Руською, і [зобов\'язалися] клятву не порушувати - ні грекам, ні русі.</p>\r\n<p>&nbsp;</p>\r\n<p>І жив Олег, княжачи в Києві,</p>\r\n<p>І мир мав з усіма країнами.</p>\r\n<p>І прийшла осінь,</p>\r\n<p>І спом\'янув Олег коня свого,</p>\r\n<p>Якого був наказав годувати,</p>\r\n<p>І не сідати на нього,</p>\r\n<p>Бо колись запитав був волхвів-кудесників:</p>\r\n<p>&laquo;Від чого я маю умерти?&raquo;</p>\r\n<p>І рече йому один кудесник:</p>\r\n<p>&laquo;Княже! Кінь, його ти любиш</p>\r\n<p>І на ньому їздиш,- смерть твоя від нього&raquo;.</p>\r\n<p>І узяв Олег це в ум,</p>\r\n<p>І сказав: &laquo;Я ніколи більше на коня не сяду,</p>\r\n<p>І його ніколи більше не побачу&raquo;.</p>\r\n<p>І звелів годувати його</p>\r\n<p>І до нього коня не водити.</p>\r\n<p>І прожив так скількись літ, коня не бачив,</p>\r\n<p>Аж доки не пішов на греків.</p>\r\n<p>А коли у Київ повернувся,</p>\r\n<p>Відтоді пройшло чотири літа,</p>\r\n<p>Спом\'янув коня свого на п\'яте літо,</p>\r\n<p>Від нього предвіщали смерть Олегу волхви.</p>\r\n<p>І покликав він конюхів старійшину</p>\r\n<p>І спитав: &laquo;Де кінь мій,</p>\r\n<p>Якого я поставив годувати і глядіти?&raquo;</p>\r\n<p>Він же відповів: &laquo;Умер кінь&raquo;.</p>\r\n<p>Посміхнувсь Олег і докорив кудесника словами:</p>\r\n<p>&laquo;То не правду мовлять волхви,</p>\r\n<p>Все брехня те: кінь умер - живий я&raquo;.</p>\r\n<p>І звелів коня він осідлати:</p>\r\n<p>&laquo;Я його кістки побачить хочу&raquo;.</p>\r\n<p>І приїхав на те місце, де лежали голі кості і лоб голий,</p>\r\n<p>І з коня зліз, посміявшись, каже:</p>\r\n<p>&laquo;Чи від цього лоба смерть мені прийняти?&raquo;</p>\r\n<p>І наступив ногою на лоб,</p>\r\n<p>І виповзла змія, і вжалила його в ногу,</p>\r\n<p>І від того розхворівся і помер.</p>\r\n<p>І плакали за ним всі люди плачем великим,</p>\r\n<p>І понесли його,</p>\r\n<p>І поховали його на горі, яку називають Щекавиця.</p>\r\n<p>І є могила його до сьогодні, славна могила Олегова.</p>\r\n<p>І було всіх літ його князювання тридцять і три.</p>\r\n<p>&nbsp;</p>\r\n<p>Це ж дивно, як від волхвування збувається чародійство. Так було за царювання Дементіана. Якийсь волхв, на ймення Аполлоній Тиянин, був відомий тим, що ходив по городах і селах і творив скрізь бісівські чуда. Із Риму прийшов до Узантії, жителі якої упросили його сотворити таке: він вигнав із города безліч змій і скорпіонів, аби люди не вражалися ними, і буйність кінську упокорив на зібранні бояр. Також коли прийшов у Антіохію, був упроханий ними, бо потерпали від скорпіонів і комарів. Він зробив скорпіона мідного, і закопав його в землю, і невеликий мармуровий стовп поставив над ним, і звелів людям взяти палиці і ходити по городу і вигукувать, палицями вимахуючи: &laquo;Бути городу без комарів!&raquo; І так щезли із города комарі і скорпіони. А ще просили його вирятувати від землетрусів, що загрожували городу. Зітхнувши, написав він на дощечці таке: &laquo;Гай-гай, окаянний городе. Як попотрясе тебе [земля],- охоплений будеш вогнем, і ополчать тебе і на березі Сіоріті!&raquo; Про нього ж і великий Анастасій божого города рече: &laquo;Чуда Аполлонія і донині в деяких місцях збуваються, зокрема ті, що звершені, аби відганяти четвероногих і птиць, які могли б зашкодити людям. Інші ж - на упокорення річних потоків нестримної течії, та деякі інші, на перемогу поставлені проти спроможних принести людям погибіль і шкоду. І не тільки за його життя такі і подібні чуда створили біси, а й після смерті біля гробу його витворяли біси різні чуда його іменем, спокушаючи окаянних людей, зловлених у сіті диявола&raquo;. Так хто і що рече про творящих чарівні справи? Як отой був майстер на знахарське спокушання, і ніколи не зважав на те знаменитий Аполлоній, як у шаленстві вдавався до любомудрих хитрощів, а йому б личило сказати: &laquo;Тож і я творю словом тільки те, що хотів&raquo;. І не чинити дій, на які він здатний. Бо все те з допусту божого і підступністю бісівською діється. Тими речами випробовується наша православна віра, що міцна є, і біля Господа перебуває, і не піддатна шкідливій чортівщині і сатанинській підступності, яких вживають раби і слуги злоби. А деякі ще й від імені Господа пророчать, як Валаам, і Саул, і Каіяфа: і бісів навіть виганяють, як Іуда і сини Скеваві. Бо й на недостойних благодать діє многократно як багато хто свідчить. Адже Валаам цурався обох - життя і віри, тим паче благодать, що сходила на нього, була дана для переконання інших. І фараон був такий, але і йому майбутнє було розкрито. І Навуходоносор був законопорушником, та і йому навіть долю майбутніх поколінь посеред города [Бог] відкрив, засвідчивши тим, як ті, що мають ворожий розум, перед образом Христовим силою чужої волі пророкують, спокушаючи людей, які не розуміють доброго. Такими були Симон волхв, і Менерд, і інші, їм подібні, про яких істинно сказано: &laquo;Не чудами спокушати...&raquo;</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&nbsp;</p>',NULL,1846,5,1,NULL,25);
INSERT INTO `publications_publication` VALUES
(4714,'P','2008-12-20',1,NULL,'Rss','Чи зрубана ялинка є справді символом Різдва?','<p><img width=\"127\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_19.12.2008.jpg.jpg\" height=\"170\" style=\"float: left;\" />\"Вічнозелені сосни, ялини та ялиці безперервно поглинають вуглекислий газ, затримують пил, сажу, різноманітні токсичні, канцерогенні та радіоактивні речовини, виробляють кисень та очищують повітря. Своїм корінням вони укріпляють ґрунти, вразливі до ерозії, поглинають та втримують надлишок води. Крім того, ці дерева є необхідними компонентами екологічних систем, а для багатьох істот - рідною домівкою. Відповідно, їх знищення спричиняє не тільки руйнування природи, але також цілу низку згубних наслідків для життя і здоров\'я людини та суспільного блага, що в дуже трагічний спосіб підтвердили цьогорічні повені в Західній Україні\", - наголошують автори звернення Бюро Києво-Галицького Верховного Архиєпископства УГКЦ із питань екології з приводу всесвітньої природозахисної акції \"Живе різдвяне дерево\" (текст документу див. у додатку).</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=151:chy-zrubana-jalynka-je-spravdi-symvolom-rizdva&catid=27:gk-v-ukrajini-cat&Itemid=57',1812,NULL,1,1,26),
(4715,'P','2008-12-20',1,NULL,'Rss','Чи зрубана ялинка є справді символом Різдва?','<p><img width=\"127\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_19.12.2008.jpg.jpg\" height=\"170\" style=\"float: left;\" />\"Вічнозелені сосни, ялини та ялиці безперервно поглинають вуглекислий газ, затримують пил, сажу, різноманітні токсичні, канцерогенні та радіоактивні речовини, виробляють кисень та очищують повітря. Своїм корінням вони укріпляють ґрунти, вразливі до ерозії, поглинають та втримують надлишок води. Крім того, ці дерева є необхідними компонентами екологічних систем, а для багатьох істот - рідною домівкою. Відповідно, їх знищення спричиняє не тільки руйнування природи, але також цілу низку згубних наслідків для життя і здоров\'я людини та суспільного блага, що в дуже трагічний спосіб підтвердили цьогорічні повені в Західній Україні\", - наголошують автори звернення Бюро Києво-Галицького Верховного Архиєпископства УГКЦ із питань екології з приводу всесвітньої природозахисної акції \"Живе різдвяне дерево\" (текст документу див. у додатку).</p>\n<p>\n',NULL,'http://www.oranta.org/index.php?option=com_content&view=article&id=152:chy-zrubana-jalynka-je-spravdi-symvolom-rizdva&catid=27:gk-v-ukrajini-cat&Itemid=57',1813,NULL,1,1,26),
(4716,'N','2008-12-18',0,NULL,'Note','11','','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,1810,200,sviatiy_mikolay.gif\" alt=\"\" />Велике вшанування, яким св. Миколай втішається довгі сторіччя між християнами обох обрядів, є засвідченням його надзвичайної святості й небесної слави. Св. Бонавентура так писав про нього: \"Миколай, вибраний вже в лоні своєї матері, святий з дитинства, був славою юнаків, честю старців, похвалою священиків, сонячним сяйвом єпископів. Слава про його чуда розійшлася по всій землі, Миколая величають усі люди. Щодня помножуються чуда за його посередництвом. Св. Миколая прославляють на морі, величають на суші, призивають у небезпеках. Коли наближається гроза і штормить море, тоді зі сльозами кличуть на допомогу св. Миколая; коли приходить нещастя, св. Миколай є нашим заступником. І не тільки до християн, але й до поган дійшла слава його імені, він у них в такій великій шані, що вони громадами сходяться величати та прославляти його ім\'я. Між багатьма великими чудами, що їх він творив, найбільшим чудом був він сам\".</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">А ще раніше, в VII сторіччі, над гробом св. Миколая похвальне слово проголосив св. Андрій Критський: \"Не стільки освічуе церкву світильник, поставлений на золотому високому свічнику, скільки ти, св. Миколаю, поставлений Христом - правдивим Світлом - на святительському престолі, просвічував у світі свою паству, вів її до небесного Світлаі наче з висоти освічував чистим світлом духовного пізнання\".</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Св. Миколай у IV столітті був єпископом у місті Мирі (Мала Азія), в давній Лікійській провінції. Відомі різні оповідання про св. Миколая, передання й леґенди. З давніх оповідань про св. Миколая відомо, що він народився в місті Патарі, де його стрий був єпископом. Побожні батьки дбайливо виховували свого сина в християнському дусі, а стрий дбав про його освіту. Після передчасної смерті батьків Миколай вирішив передати успадкований по батьках маєток на діла християнської любові. Саме тоді один Патарський громадянин, утративши всі свої гроші, хотів пустити своїх дочок на злу дорогу. Щоб допомогти цій родині, Миколай підкинув потайки до їхньої хати мішечок золота. Так він зробив три рази. Це зберегло чесне ім\'я родини.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Як душпастир св. Миколай ревно працював над спасінням своїх вірників, а також з любов\'ю помагав їм у всіх їхніх потребах, часто навіть чудесним способом. Так, наприклад, він з\'явився у сні Римському імператору Константину І, Великому, та наказав йому відпустити на волю трьох старшин, які несправедливо були засуджені на смерть.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Згідно з усіма переданнями св. Миколай помер у Мирі, де над його гробом поставили церкву. На його честь відзначали окреме свято. Один його життєписець написав у X столітті про св. Миколая так: \"Захід, як і Схід, вихваляе його та прославляе. Люди будують на його честь церкви. Всюди е його образи, на його честь проголошують проповіді та святкують свята. Всі християни вшановують його пам\'ять та просять у нього опіки. Його ласки не підвладні часові, тривають з роду в рід, спливають на всю землю: Про його чуда знають: скити, індуси, варвари, африканці, так само, як італійці\".</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Коли місто захопили мусульмани, італійцям вдалося 1087 року перевезти мощі св. Миколая з Мири до міста Бар. Це причинило вшанування Святого на католицькому Заході. В середньовіччі лише в одній Англії було чотириста церков св. Миколая. Після Матері Божої образи св. Миколая малювали більше, ніж будь-якого іншого Святого. Як свого особливого небесного заступника закликають св. Миколая на допомогу моряки на Сході, а на Заході діти чекають від нього дарунки за добру поведінку. Просять допомоги в св. Миколая також в\'язні й полонені. Всі ці прохання появилися на основі добрих справ і чудес великого чудотворця - св. Миколая.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Український народ теж здавна вшановує його. Багато в нашій державі церков св. Миколая. Немає іконостасу, на якому б не було його образу. В церковних богослужіннях часто згадують св. Миколая, а кожний четвер посвячений у нашому богослужінні його особливому вшануванню.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,1810,200,image002.jpg\" alt=\"\" />З любові до Бога в св. Миколая зародилось велике милосердя до ближніх. Багатство, яке він успадкував від своїх батьків, Святий не вважав своєю власністю, а добром, що належить усім бідним і потребуючим. Св. Миколай не чекав, щоб людина, яка щось потребує прийшла до нього і просила допомоги, а сам шукав бідних, щоб їм допомагати. А робив це переважно так, що ті навіть не знали, хто є їхнім доброчинцем. Св. Миколай чинив згідно з Святим Євангелієм: \"Нехай не нає ліва рука, що дає права\" та слідкував, щоб сповнилися Христові слова: \"Не показуйте своєї праведності перед людьми, щоб вони вас бачили і хвалили за те; нехай знає про ваші добрі діла лише Отець Небесний\".</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Коли в Мирах помер єпископ Іван, навколишні єпископи зібралися вибирати нового і просили Бога вказати їм найдостойнішого чоловіка на це звання. Архієпископ мав видіння у сні, щоб чоловіка, який перший прийде вранці до церкви на молитву, вибрати і висвятити на єпископа, бо це праведна людина, що горить любов\'ю до Бога і ближнього.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Першим, хто прийшов до церкви на молитву, був св. Миколай. Тоді найстарший єпископ сказав йому, що він, за Божою волею, має прийняти свячення на єпископа. І хоч св. Миколай ніколи навіть не думав про таку високу честь, все ж не міг йти проти виразної Божої волі. Тоді він розповів архієпископу, що і він мав дивне видіння: Ісус Христос передав йому святе Євангеліє, а Божа Мати - єпископський омофор. І ось він встав рано, щоб піти до церкви і просити в Бога ласки і просвітлення, як розуміти цей сон, а тут йому повідомили, що він має бути єпископом.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Св. Миколай, будучи владикою, не тільки не змінив своєї милосердної поведінки щодо людей, а навпаки, його добродійність ще більше засяяла. Тоді сповнилися слова Христові: \"Так нехай світить перед людьми ваше світло, щоб вони, бачивши ваші добрі вчинки, прославляли вашого Отця, що на небі\" (Мт 5:16). Сам св. Миколай жив дуже скромно. Одяг його був простий, він їв тільки один раз на день, ввечері; весь час присвячував молитві й ділам християнського милосердя. Кожного року 1 вересня скликував на Собор духовенство, радився з ним про потреби народу, наказуючи дбати про бідних, виконувати бажання Христа: \"Нехай не буде бідних між вами...\" А Бог помагав св. Миколаю в його добродійності, не раз виразними чудами. Коли в Мирі настав голод, св. Миколай ревно просив у Бога помочі. На далекій Сицилії одному торговцеві збіжжям являється в сні св. Миколай і дає йому добрий грошовий завдаток та замовляє корабель збіжжя для голодуючих у Мирі. Пробудившись, купець, на своє велике здивування, знайшов у своєму кулаку завдаток на збіжжя, що його остаточно переконало: це був не звичайний сон, а Боже об\'явлення. Купець відразу наповнив корабель збіжжям, приплив до Мири, де Миколай купив це збіжжя для голодуючих.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Св. Миколай був дуже ревний у Божих справах, за свободу Церкви, за чистоту християнської віри, за поширення Царства Божого на землі. Якийсь час довелося йому страждати за Христа у в\'язниці. Св. Миколай вийшов на волю, коли новий правитель Константан Великий переміг Лікинія, невинно ув\'язнених християн визволив з тюрми, а Христовій Церкві дав свободу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За давнім переданням св. Миколай на І Соборі в Нікеї (325 р.) виступив проти єретика Арія, який заперечував у Христі Божу природу. Арій навчав, що Христос не був Богочоловіком, а тільки людиною, хоч і великим пророком і посланцем Божим. Коли ж на Соборі Арій вперто хулив Христа, то Святий Миколай вдарив Арія в лице. Отці Собору не похвалили Святого за це і навіть наклали на нього церковну покуту.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Бог прославив св. Миколая великими чудами як за життя, так і після смерті. Він увійшов в історію Церкви як Великий Чудотворець. Таким згадується св. Миколай в богослужбових піснях, акафістах, стихирах. Слава про св. чудотворця була така велика, що навіть погани просили його заступництва в своїх потребах. В Україні у великому вшануванні були чудотворні ікони св. Миколая. Окремої згадки заслуговує ікона св. Миколая Мокрого (Морського), опікуна вояків-моряків. З мощей св. Миколая, що спочивають в катедральному храмі італійського міста Бар, дотепер випливає цілюще миро, і діються при них чуда. Тому слушно співає наша св. Церква: \"Радуйся, св. Миколаю, великий Чудотворче...\"</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Культ Святого Миколая</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,1810,200,4debba5ca1fd.jpg\" alt=\"\" />Святицй Миколай брав участь у Першому Вселенському Соборі, 325 року у Нікеї. На Нікейському Соборі був прийнятий &laquo;Символ віри&raquo; - молитва &laquo;Вірую ... &raquo;. Св. Миколай був одним із співавторів цієї молитви.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Культ Св. Миколая почав набувати розмаху у V ст., коли Цісар Юстиніан І (527-565) збудував на його честь церкву в Константинополі. Цісар Мануїл Комнен (1143-1181) державним законом, затвердив святкувати празник св. Миколая 6 грудня (19 грудня). 3 Візантії його культ поширився по цілому світі. Найстарший життєпис про св. Миколая походить з ІХ-го століття. На заході Папа Римський Миколай І (858-867) - коло 860 року збудував у Римі церкву Св. Миколая Чудотворця, таким чином культ Святого Миколая поширився на всю християнську Європу. На сьогоднішній день у Європі є понад 3500 храмів збудованих на честь Св. Миколая.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На українські землі, культ Св. Миколая, прийшов разом з християнством - Хрещення Русі (988 р.). В другій половині ХІ століття в Києві було споруджено перший храм Св. Миколая Чудотворця на Аскольдовій горі. У давнину - Св. Миколай, для українського народу, був заступником від небезпеки зі степів (Монголо-татарської навали). Пісні про Св. Миколая належать до найдавніших зразків української поезії, серед них такі пісні, як &laquo;Ой хто, хто Миколая любить&raquo;. В соборі св. Софії в Києві знаходиться ікона св. Миколая Чудотворця (Мокрого) з 1090 року.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Духовне Застереження!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З Особистістю Св. Миколая пов\'язана давня традиція: дарувати дітям у надвечір\'я Св. Миколая подарунки. Шкода тільки, що цей гарний і повний глибокого значення звичай перетворився на карикатуру Св. Миколая у вигляді відомого Санта Клауса. У теперішньому світі панує вже не закон безкорисного милосердя, а дух бізнесу, реклами, заробітку навіть на святих речах, тому новочасні погани використали і саму особу історичного св. Миколая для прагматичних цілей. Християни повинні знати і пояснювати своїм дітям, ким був св. Миколай, звідки походить звичай дарувати дітям подарунки в його день, а всі історії про Санта Клауса - це лише людські вигадки, неповажні пародії на святі речі. Тож любімо і молімося до Святого Миколая знаючи, що він насправді Чудотворець.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://promin-lubovi.narod.ru/</span></span></p>',NULL,1810,5,1,NULL,43);
