INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3648,'P','2013-04-27',1,NULL,'Rss','Лиш небо, земля і молитва','<p>З Києва до Тернополя, а далі до родового села кардинала Йосипа Сліпого Заздрість просити благословення і відпущення гріхів приїхали 150 людей з різних куточків України – Києва, Кам’янець-Подільського, Одеси, Львова, Тернополя. Серед них – і літні люди, і багато молоді, діти і підлітки. Чимало з них відвідують прощу усі дев’ять років, відколи було встановлено Хресну дорогу від села Заздрість до Маріїнського духовного центру у Зарваниці, що на Тернопільщині.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1150:lysh-nebo-zemlja-i-molytva&catid=25:parafija-cat&Itemid=27',3202,NULL,1,1,26),
