INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2303,'P','2009-07-11',1,NULL,'Rss',' Католицький Народний Катехизм','<p><strong>Віра</strong></p>\n<p><strong>Ісус Христос</strong></p>\n<p><strong>Спасіння</strong></p>\n<p>Від трагічних наслідків первородного гріха нас звільнив Ісус            Христос, наш Спаситель.</p>\n<p>Упавши у гріх, людина не була в силі повернути собі втрачені святість і праведність            і пов\'язані з ними блага. Людина, фізично вмерши, не може воскреснути сама по            собі; людина, яка вмре духовно, теж сама не може повернути душі життя. \"Людина,            не зумівши без Божої ласки вберегти одер­жану від Бога праведність, не може            без неї повернути втрачене\" (св. Авг.). Людина після свого гріхопадіння            була подібна до хворого, який, хоч може рухати руками і ногами, без чужої допомоги            не здатний встати з постелі (св. Тома з Акв.). Христос для людства, враженого            хитрістю чорта і позбавленого надприродних дарів, був, як милосердний самарянин            для іудея, який побував у руках розбійників. Називаємо Його Спасителем, Спасом,            бо приніс лік хворому людству і, врятував його від вічної смерті (Займер).</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=378:-katolycky-narodny-katehyzm&catid=30:hrystyjanske-vchennia-cat&Itemid=37',1659,NULL,1,26),
(2304,'P','2009-07-09',1,NULL,'Note','Папа Венедикт XVI зреформував діяльність комісії, відповідальної за діалог з прихильниками лефевристського руху','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Через повідомлення, опубліковане Ватиканським прес-центром, кардинал Левада зазначає, що своїм рішенням Святіший Отець прагне виявити особливу і батьківську турботу щодо Братства святого Пія&nbsp;Х, з метою допомогти подолати труднощі, які стоять на перешкоді осягнення повної єдності з Католицькою Церквою.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Апостольським листом <em>Motu proprio &laquo;Ecclesiae unitatem&raquo;</em>, від 8 липня 2009&nbsp;р., Папа Венедикт&nbsp;XVI зреформував діяльність Папської Комісії &laquo;Ecclesia Dei&raquo; - структури, завданням якої є провадити діалог з спільнотами та особами, що, належавши до лефевристського руху, прагнуть повернутися до повної єдності з Папою. Зважаючи на те, що проблеми, пов\'язані з діяльністю Братства святого Пія&nbsp;Х, мають доктринальний характер, Вселенський Архиєрей підпорядкував згадану комісію Конгрегації Віровчення, призначивши її Президентом Префекта згаданої Конгрегації кардинала Вільяма Леваду.<br /><br />Через повідомлення, опубліковане Ватиканським прес-центром, кардинал Левада зазначає, що своїм рішенням Святіший Отець прагне виявити особливу і батьківську турботу щодо Братства святого Пія&nbsp;Х, з метою допомогти подолати труднощі, які стоять на перешкоді осягнення повної єдності з Католицькою Церквою.<br /><br /><em>Motu proprio &laquo;Ecclesiae unitatem&raquo;</em> пригадує, що зняття екскомуніки з чотирьох єпископів-лефевристів у січні цього року мало на меті звільнити цих осіб з тягаря найбільшого церковного покарання, однак, залишаються актуальними доктринальні питання, і поки вони не будуть врегульовані, Братство святого Пія&nbsp;Х не може втішатися канонічним статусом у Католицькій Церкві, а його священнослужителі не можуть виконувати в ній жодних обов\'язків.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,NULL,1654,5,1,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2305,'P','2009-07-11',1,NULL,'Rss','Святих славних і всехвальних апп. Петра і Павла','<table align=\"right\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"306\">\n<tbody>\n<tr>\n<td rowspan=\"2\" valign=\"top\"><img src=\"http://www.ugcc.org.ua/clear.gif\" height=\"1\" width=\"5\" /></td>\n<td><img src=\"http://www.ugcc.org.ua/clear.gif\" height=\"1\" width=\"300\" /></td>\n</tr>\n<tr>\n<td valign=\"top\"><img src=\"http://www.ugcc.org.ua/uploads/pics/peter_paul.jpg\" align=\"top\" border=\"0\" height=\"407\" width=\"300\" /><br /><img src=\"http://www.ugcc.org.ua/clear.gif\" border=\"0\" height=\"5\" width=\"1\" /><br /><center>Апостоли Петро і Павло</center><br /></td>\n</tr>\n</tbody>\n</table>\n<br />\n<p><strong>Коротка історія</strong></p>\n<p align=\"justify\">Святим Апостолам завдячуємо скарб Христової віри. Вони передали нам Христову науку, від них маємо св. Євангелію і апостольські листи, вони поклали тривку основу для Христової Церкви. Наша Східна Церква з великою урочистістю святкує 12 липня (29 червня) празник, що в наших богослужбових книгах має таку назву: „Святих Славних і Всехвальних Первоверховних Апостолів Петра й Павла\". Обидва ці Апостоли визначні своїм характером, своєю ревною апостольською працею та своїм великим культом у Церкві.</p>\n<p align=\"justify\">З огляду на їх превелике значення для Церкви, їхній культ починається від їхньої смерті. Їхні гроби в Римі були всім християнам добре знані й втішалися великою пошаною. Св. Єронім (+ 420) каже: „Коли я ще як юнак учився у Римі, то зі своїми ровесниками звичайно в неділю ходив я на гроби Апостолів і Мучеників\". Їхній культ стає загальний у 4-му столітті у Східній і Західній Церквах. Костянтин Великий (+ 337) збудував у Царгороді величний храм на честь 12 Апостолів, де він і похований. Найдавніші церковні календарі вже мають їхній празник.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=379:svjatyh-slavnyh-i-vsehvalnyh-app-petra-i-pavla&catid=35:istorychni-postati-cat&Itemid=48',1660,NULL,1,26),
(2306,'P','2009-07-11',1,NULL,'Note','Генеральні секретарі Єпископських Конференцій Європи обговорять питання життя і місії священиків','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Серед головних тем, які будуть обговорені, є питання життя і місії священиків. &laquo;Сподіваємося, що зможемо поділитися своїм досвідом священичої формації, адже у нашій Церкві цей рік є проголошений Роком християнського покликання з особливим наголосом на священичому покликанні.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_sekretari_konferencij_evropy_01.jpg.jpg\" alt=\"\" />9 липня 2009 року у Львові розпочався 37-ий З\'їзд Генеральних секретарів Єпископських Конференцій Європи, який триватиме до 13 липня. Учасниками зустрічі є близько тридцяти п\'яти осіб з понад тридцяти країн Європи. Нашу країну представляють Владика Богдан (Дзюрах), Секретар Синоду Єпископів УГКЦ, та Владика Мар\'ян Бучек, Секретар Конференції Римо-Католицьких Єпископів України.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У перший день для учасників з\'їзду було проведено презентацію УГКЦ в історичному, адміністративному та статистичному вимірах, яку підготували працівники УКУ, а також було представлено історію і сучасність міста Львова. У п\'ятницю і суботу відбуватимуться робочі засідання, які складатимуться із семи сесій. На першій із словами привітання до учасників зустрічі звернуться Блаженніший Любомир, Глава УГКЦ, Архиєпископ Іван Юркович, Апостольський Нунцій в Україні та Архиєпископ Мечислав Мокшицький, Митрополит Львівський РКЦ в Україні.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У п\'ятницю о 18.00 учасники з\'їзду відслужать Літургію у Катедральному соборі Успіня Пресвятої Богородиці РКЦ в Україні, а у неділю о 10.00 - в Архикатедральному соборі Святого Юра УГКЦ. Крім того у неділю вони відвідають Філософсько-богословський факультет УКУ та Львівську духовну семінарію, а також матимуть зустріч з мером Львова Андрієм Садовим.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_sekretari_konferencij_evropy2.jpg.jpg\" alt=\"\" />Як зазначив у коментарі для Департаменту інформації УГКЦ Владика Богдан, Генеральні секретарі Єпископських Конференцій Європи зустрічаються раз на рік у різних країнах для обговорення тих чи інших нагальних проблем, які стоять перед Католицькою Церквою у Європі. &laquo;Зустріч в Україні має два завдання: провести цьогорічне робоче засідання, де обговорити актуальні питання життя Церкви, а також краще пізнати Українську Греко-Католицьку та Римо-Католицьку Церкви, їхні відносини з іншими Церквами, присутніми в Україні та державною владою&raquo;, - сказав Секретар Синоду Єпископів УГКЦ.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Серед головних тем, які будуть обговорені, є питання життя і місії священиків. &laquo;Сподіваємося, що зможемо поділитися своїм досвідом священичої формації, адже у нашій Церкві цей рік є проголошений Роком християнського покликання з особливим наголосом на священичому покликанні. Цю тему ми також активно обговорюємо і розпрацьовуємо на засіданнях нашого Синоду Єпископів. Рівно ж будемо раді зачерпнути поради наших гостей у справі навчання та виховання майбутніх священиків&raquo;, - сказав Владика Богдан. При цьому він зазначив, що під час засідань планується підняти питання душпастирської опіки вірних УГКЦ, які перебувають на заробітках у різних європейських країнах. &laquo;Хочемо представити духовні потреби наших емігрантів і говорити про можливість служіння для них у тому числі й одружених священиків&raquo;, - розповів Владика Богдан (Дзюрах).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Департамент інформації УГКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/<br /></span></span></p>',NULL,NULL,1661,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2307,'P','2009-07-11',1,NULL,'Note','Генеральний секретар Ради Єпископських Конференцій Європи впевнений, що міграція дає нагоду для взаємного збагачення різних народів','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Хочу з вдячністю згадати єпископів тих країн, які уже нам надали всебічне сприяння для налагодження душпастирської опіки наших вірних&raquo;, - сказав Владика Богдан (Дзюрах), Секретар Синоду Єпископів УГКЦ. При цьому він розповів, що під час з\'їзду ділився з його учасниками труднощами, які виникають у цьому питанні. &laquo;Хочемо щоб більше священиків поїхали у країни Західної Європи і там якнайкраще послужили нашим вірним у складних обставинах їхнього життя...<br /></span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;У структурах Ради Єпископських Конференцій Європи є комісія, яка займається питаннями міграції. Вона тепер готує міжнародний конгрес, який відбудеться у 2010 році. Будемо розглядати міграцію не тільки як трудність, але як шанс. Адже це явище дає нагоду для взаємного збагачення різних народів&raquo;, - у коментарі для ЗМІ сказав о. Дуарте да Кунья, Генеральний секретар Ради Єпископських Конференцій Європи, під час свого перебування у Львові. Отець Дуарте прибув до Львова для участі у роботі 37-го З\'їзду Генеральних секретарів Єпископських Конференцій Європи, який відбувається 9 - 13 липня 2009 року.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Я був вражений працьовитістю, чемністю української сім\'ї, яку я знаю у Португалії. Але найбільше враження було від свідчення їхньої віри. Правда, нажаль, багато сімей є розділені кордонами. Ми повинні сказати усьому Європейському Союзові про цю проблему і старатися, щоб ця важка дійсність була тягарем не тільки для цих людей чи їхніх країн, але для усіх членів співдружності. Всі уряди повинні співпрацювати для того, щоб сім\'ї були об\'єднані&raquo;, - наголосив о. Дуарте.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У свою чергу Владика Мар\'ян Бучек, Секретар Конференції Римо-Католицьких Єпископів України, зазначив, що тема міграції є актуальної на зустрічах генеральних секретарів вже декілька останніх років. Важливим, на думку Владики Мар\'яна, є забезпечити належну душпастирську опіку для заробітчан. &laquo;Зазвичай бачимо, що якщо заробітчани перебувають під опікою доброго душпастиря, то їхні сім\'ї не розпадаються. Подруги мають більше духовних сил, щоб протистояти цій загрозі&raquo;, - сказав Секретар Конференції Римо-Католицьких Єпископів України.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Хочу з вдячністю згадати єпископів тих країн, які уже нам надали всебічне сприяння для налагодження душпастирської опіки наших вірних&raquo;, - сказав Владика Богдан (Дзюрах), Секретар Синоду Єпископів УГКЦ. При цьому він розповів, що під час з\'їзду ділився з його учасниками труднощами, які виникають у цьому питанні. &laquo;Хочемо щоб більше священиків поїхали у країни Західної Європи і там якнайкраще послужили нашим вірним у складних обставинах їхнього життя. Для цього потрібно, щоб на душпастирське служіння могли виїжджати також і одружені священики&raquo;, - сказав Владика Богдан.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пригадуємо, що під час Сорок п\'ятої сесії Синоду Єпископів Києво-Галицького Верховного Архиєпископства УГКЦ, яка відбулася 30 червня - 1 липня 2009 року було написано <a href=\"http://www.ugcc.org.ua/744.0.html?&amp;L=0\">Звернення щодо ситуації українських трудових емігрантів</a>, адресоване насамперед Президенту, Голові ВР України, Прем\'єр-міністру, а також уповноваженим керівникам парламентських фракцій. У документі зокрема йдеться про те, що новітні трудові мігранти з України на сьогодні абсолютно незахищені в країнах свого перебування, не відчувають безпеки. Закликаючи владу до цілеспрямованих активних дій, члени Синоду наголосили, що &laquo;злободенним завданням є термінове вироблення чіткої міграційної політики України, а також відповідних програм на загальнонаціональному та регіональному рівнях&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Департамент інформації УГКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/<br /></span></span></p>',NULL,NULL,1662,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2308,'P','2009-07-11',1,NULL,'Note','Папа Венедикт XVI зустрівся з Президентом США Бараком Обамою','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Як йдеться у комюніке Ватиканського прес-центру, під час щирої розмови Венедикт&nbsp;XVI та Барак Обама, насамперед, зупинилися над питаннями, що перебувають у сфері зацікавлення усіх і становлять великий виклик щодо майбутнього кожної країни та справжнього поступу народів, а саме - захист та підтримка життя та право на спротив сумління. Також йшла мова про проблеми міграції, звертаючи особливу увагу на аспект об\'єднання сімей.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">10 липня 2009&nbsp;р. у Ватикані відбулася зустріч Папи Венедикта&nbsp;XVI з Президентом США Бараком Обамою. Перед тим, Президент зустрівся з Державним Секретарем Апостольської Столиці кардиналом Тарчізіо Бертоне.<br /><br />Як йдеться у комюніке Ватиканського прес-центру, під час щирої розмови Венедикт&nbsp;XVI та Барак Обама, насамперед, зупинилися над питаннями, що перебувають у сфері зацікавлення усіх і становлять великий виклик щодо майбутнього кожної країни та справжнього поступу народів, а саме - захист та підтримка життя та право на спротив сумління. Також йшла мова про проблеми міграції, звертаючи особливу увагу на аспект об\'єднання сімей.<br /><br />Під час зустрічі також заторкнуто теми міжнародної політики, у світлі результатів саміту країн &laquo;Великої вісімки&raquo;, а також перспективи миру на Близькому Сході, в чому позиції сторін співпадають. Іншими аргументами були діалог між культурами та релігіями, фінансово-економічна криза на глобальному рівні і її моральні наслідки, продовольча безпека, надання допомоги для розвитку, передовсім, у Африці та Латинській Америці, проблеми, пов\'язані з наркобізнесом. Врешті, було підкреслено важливість виховання до толерантності.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/<br /></span></span></p>',NULL,NULL,1663,5,1,43),
(2309,'P','2009-07-12',1,NULL,'Rss','Понад Дніпром знайдено печеру, викопану на честь засновника Києво-Печерської лаври ','<p><img style=\"margin: 10px; float: right;\" src=\"http://www.askoldova-mohyla.org/uk/image,3148,200,190px-St_Anthony_of_Kiev.jpg\" />У середу група археологів віднайшла вхід до печери св. Антонія, яка знаходиться в місті Любечі. За словами керівника завідуючого відділом наукових досліджень печер та пам\'яток археології Національного архітектурно-історичного заповідника «Чернігів стародавній», у 1692 році невідомий монах в Любечі в пам\'ять про Антонія Печерського викопав печеру над Дніпром, що була зруйнована у 1920 році. До моменту приїзду археологів на цьому місці височів пагорб, який заріс травою.\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=380:ponad-dniprom-znadeno-pecheru-vykopanu-na-chest-zasnovnyka-kyjevo-pecherskoji-lavry-&catid=35:istorychni-postati-cat&Itemid=48',1666,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2310,'P','2009-07-12',1,NULL,'Note','Духовні вправи','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"text-decoration: underline;\">Для хлопців та дівчат</span></strong><strong><span style=\"text-decoration: underline;\"> </span></strong><strong><span style=\"text-decoration: underline;\">від 17 років</span></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>&bdquo;Під Духовними Вправами розуміємо усі <strong>способи іспиту совісті, розважання, споглядання (контемпляції), усної чи розумової молитви</strong> та інших духовних молитов... Тому що так як прогулянка чи біг є вправами для тіла, усі способи підготовки душі, щоб вона могла <strong>позбутися усіх невпорядкованих прив&acute;язаностей</strong>, і, позбувшись, могла <strong>шукати і наслідувати Божу волю у своєму житті</strong>, називаються Духовними Вправами\"</em> (св.Ігнатій Лойола)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>&laquo;Яким би не було твоє заняття, май час зайнятися собою&raquo;</em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;(Тома Кемпійський)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Якщо ви <strong>загубилися на життєвому шляху</strong> і не знаєте, куди йти;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">якщо ви <strong>на порозі важливих рішень</strong>;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">якщо ви <strong>думаєте зробити у житті якийсь важливий крок</strong>;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">якщо ви <strong>не впевнені, чи добре йдете вже обраним шляхом</strong>;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">якщо ваша <strong>душа не знаходить спокою</strong> через море пристрастей, що її атакують;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">якщо ви просто <strong>хочете зупинитися і роздумати у молитві</strong> над своїм життям;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">якщо ви <strong>хочете знайти Божу</strong> волю для себе і слідувати за нею, бо тільки у Ньому ми знайдемо себе і тільки Він знає, для чого ми створені;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">то це <strong>час для вас</strong>!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"text-decoration: underline;\">Для хлопців та дівчат</span></strong><strong><span style=\"text-decoration: underline;\"> </span></strong><strong><span style=\"text-decoration: underline;\">від 17 років</span></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>&bdquo;Під Духовними Вправами розуміємо усі <strong>способи іспиту совісті, розважання, споглядання (контемпляції), усної чи розумової молитви</strong> та інших духовних молитов... Тому що так як прогулянка чи біг є вправами для тіла, усі способи підготовки душі, щоб вона могла <strong>позбутися усіх невпорядкованих прив&acute;язаностей</strong>, і, позбувшись, могла <strong>шукати і наслідувати Божу волю у своєму житті</strong>, називаються Духовними Вправами\"</em> (св.Ігнатій Лойола)</span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"text-decoration: underline;\">За адресою: </span></strong></span></span></p>\r\n<table style=\"text-align: justify;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"416\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">вул. Вербова, 12, Крихівці, </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">м. Івано-Франківськ</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">тел. (0342) 77-45-94, 8-098-9488025</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">(о. Йосафат Бойко, ВС)</span></span></p>\r\n</td>\r\n<td width=\"431\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">вул. Гонти, 15, Крихівці</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">м. Івано-Франківськ</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">тел. (0342) 77-41-42, 8-096-7463422</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">(с. Марія Гошівська)</span></span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>',NULL,NULL,1667,5,1,40);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2311,'P','2009-07-13',1,NULL,'Note','Молитва та уважність на дорогах. Побажання Святішого Отця на період відпусток та вакацій','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Молитва не знає віддалей і розставань: де б ми не були, вона робить нас єдиними серцем і душею. Говорячи про від\'їзди, - додав Папа, - користаю з нагоди, щоб ще раз наголосити на обов\'язкові всіх бути уважними за кермом і дотримуватись правил дорожнього руху.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">12 липня 2009, промовляючи до вірних після проказування молитви &laquo;Ангел Господній&raquo; на площі Святого Петра у Ватикані, Святіший Отець сказав: &laquo;Завтра, за Божим благоволінням, я від\'їду на короткий період відпочинку у горах. Поїду до Валле-д\'Аоста у Ле Комб, місцевості, яка стала відомою завдяки перебуванню там мого дорогого попередника Івана Павла&nbsp;ІІ, яку і я також дуже люблю. Прощаючись з площею Святого Петра і з містом Римом, заохочую всіх супроводжувати мене молитвою. Молитва не знає віддалей і розставань: де б ми не були, вона робить нас єдиними серцем і душею. Говорячи про від\'їзди, - додав Папа, - користаю з нагоди, щоб ще раз наголосити на обов\'язкові всіх бути уважними за кермом і дотримуватись правил дорожнього руху. Добра відпустка починаються саме від цього!&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/<br /></span></span></p>',NULL,NULL,1668,5,1,43),
(2312,'P','2009-07-16',1,NULL,'Rss','Святе Преображення Господа Бога і Спаса нашого Ісуса Христа ','<img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/256b6e094d.jpg\" width=\"300\" height=\"393\" /><img src=\"http://ugcc.org.ua/clear.gif\" width=\"1\" border=\"0\" height=\"5\" /><br /><strong>Коротка історія</strong>\n<p align=\"justify\">Святкування цього празника сягає IV ст. В цьому часі св. Єлена, мати цісаря Костянтина Великого, збудувала храм на горі Тавор у честь Господнього Преображення. Від VI ст. цей празник урочисто святкується у Східній Церкві під назвою „Господнього Преображення\". В Західній Сирії у VIII столітті він називався „Празником Тавору\".</p>\n<p align=\"justify\">Первісно празник Преображення святковано в місяці лютому. Одначе тому, що цей радісний празник звичайно випадав у часі Великого Посту, а це було незгідне з духом посту й покути, то його перенесено на день 6 серпня. Чому якраз на цей день? Історик Євсевій і св. Іван Дамаскин є тієї думки, що Господнє Преображення відбулося 40 днів перед Христовою смертю. Тож св. Церква йдучи за цією думкою, перенесла цей празник з місяця лютого на 6 серпня тому, бо 40 днів пізніше, це є 14 вересня, випадає празник Воздвиження Чесного Хреста - пам\'ять Христових мук і смерті.\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=381:svjate-preobrazhennja-gospoda-boga-i-spasa-nashogo-isusa-hrysta-&catid=27:gk-v-ukrajini-cat&Itemid=57',1684,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2313,'P','2009-07-18',1,NULL,'Rss','Італія проти застосування аборту як засобу демографічного контролю','<p>Останніми днями італійський парламент виступив із пропозицією, щоб державна влада Італії офіційно звернулась до Організації Об\'єднаний Націй, пропонуючи заборонити практику аборту як засобу контролю над народжуваністю і щоб забезпечити кожній жінці право не бути примушеною чи зобов\'язаною робити аборт. Цю ініціативу членів італійського парламенту прокоментував кардинал Ренато Мартіно, Президент Папської Ради \"Справедливість та Мир\", наголошуючи на тому, що цей жест є великим кроком вперед у поширенні і забезпеченні основних людських прав.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=382:italija-proty-zastosuvannja-abortu-jak-zasobu-demografichnogo-kontrolju&catid=29:propovidi-cat&Itemid=34',1685,NULL,1,26),
(2314,'P','2009-07-13',1,NULL,'Note','Рекордна кількість паломників на батьківщині святого Павла','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Серед позитивних наслідків Року святого Павла, який Вселенська Церква відзначала від 28 червня 2008&nbsp;р. до 28 червня 2009&nbsp;р. з нагоди 2000-ліття народження Апостола Народів, можна назвати рекордну кількість паломників, які відвідали батьківщину святого Павла - місто Тарс (Туреччина). Там побували понад 400 груп із 30 країн світу.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Серед позитивних наслідків Року святого Павла, який Вселенська Церква відзначала від 28 червня 2008&nbsp;р. до 28 червня 2009&nbsp;р. з нагоди 2000-ліття народження Апостола Народів, можна назвати рекордну кількість паломників, які відвідали батьківщину святого Павла - місто Тарс (Туреччина). Там побували понад 400 груп із 30 країн світу. З цієї нагоди турецька влада надала спеціальний дозвіл служити Святі Літургії та проводити богослужіння у місцевому християнському храмі святого Павла. Справа в тому, що турецька влада у 1943 році перетворила цю культову споруду на музей, і навіть ті, хто хотіли відправити богослуження чи взяти у ньому участь, були змушені платити за вхідний квиток.<br /><br />Тепер християни Туреччини сподіваються на розширення цього дозволу, який зніме обмеження з можливості молитися у храмі. &laquo;Сподіваюся, що церква у Тарсі в швидкому часі з музею перетвориться у центр духовних паломництв&raquo;, - зазначив єпископ Луїджі Падовезе, Апостольський вікарій Анатолії, в інтерв\'ю для доброчинної організації &laquo;Допомога Церкві, яка страждає&raquo;. Він зазначив, що турецькі мусульмани мали нагоду спостерегти християн не як туристів, але як молитовних паломників, і побожність людей, які відвідували місця, пов\'язанні із життям святого Павла, дуже вразила турецький народ. &laquo;Уже зрозуміло, що у Тарсі відбуватиметься почитання святого Павла і християни не вважатимуть місце, де він народився, просто музеєм&raquo;, - зазначив єпископ Падовезе.<br /><br />Труднощі, що стосуються служіння у храмі святого Павла у Тарсі є частиною непростих проблем, які мусить долати християнська меншина у Туреччині. Християни становлять менш ніж 1% населення країни і часто зазнають дискримінації та утисків.<br />За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,NULL,1669,5,1,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2315,'P','2009-07-18',1,NULL,'Note','Марійський візит кардинала Джованні Лайоло до України','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Високій гість із Ватикану передав присутній у храмі благословення Святішого Отця Венедикта&nbsp;XVI. Разом із кардиналом Лайоло співслужили Архиєпископ-митрополит Львівський Мечислав Мокшицький, Апостольський Нунцій в Україні архиєпископ Іван Юрковіч і єпископ-помічник Леон Малий.<br /></span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Марійським назвав свій візит до України Президент Папської Комісії у справах Ватиканської держави кардинал Джованні Лайоло. Він прибув до Львова 17 липня. Уранці 18 липня кардинал Лайоло відправив Святу Літургію у латинській митрополичій базиліці Успіння Пресвятої Діви Марії. У своїй проповіді Його Еміненція Джованні Лайоло зазначив, що під час нинішніх відвідин України йому випала честь молитися перед чудотворними образами Зарваницької Богородиці, Бердичівської Божої Матері та Матері Божої Ласкавої, що у львівській латинській катедрі. Високій гість із Ватикану передав присутній у храмі благословення Святішого Отця Венедикта&nbsp;XVI. Разом із кардиналом Лайоло співслужили Архиєпископ-митрополит Львівський Мечислав Мокшицький, Апостольський Нунцій в Україні архиєпископ Іван Юрковіч і єпископ-помічник Леон Малий. Кардинал Лайоло відвідав резиденцію львівських митрополитів латинського обряду, де ознайомився зі станом ремонту цього будинку - пам\'ятки архітектури. Зі Львова кардинал Джованні Лайоло відбув до Зарваниці, де відбувається Всеукраїнська проща до Зарваницької Богородиці. <br /><br />Для Радіо Ватикану, Костянтин ЧавагаЗ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">а матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,NULL,1686,5,1,43),
(2316,'P','2009-07-13',1,NULL,'Note','Однією з головних тем 37-го з’їзду Генеральних секретарів Єпископських Конференцій Європи була енцикліка «Caritas in Veritate»  ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Важливість соціального служіння Церкви відзначив і Секретар Синоду Єпископів УГКЦ Владика Богдан (Дзюрах). &laquo;Соціальна тема для нас є дуже важливою, особливо з огляду на те, що після років комуністичного переслідування, коли Церкві забороняли проводити харитативну діяльність, на сьогодні Церква починає заповняти ті ділянки, які раніше собі привласнила держава.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_g_s.jpg.jpg\" alt=\"\" />Однією з головних тем 37-го з\'їзду Генеральних секретарів Єпископських Конференцій Європи, який відбувався 9-12 липня 2009 року у реколекційному будинку в Брюховичах під Львовом, була третя енцикліка Папи Римського Венедикта XVI &laquo;Caritas in Veritate&raquo; (Милосердя в істині). Цей документ Святішого Отця був підписаний 29 червня 2009 року, а опублікований 7 липня латинською, італійською, французькою, англійською, німецькою, іспанською і португальською мовами.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Як розповів під час зустрічі із релігійними журналістами о. д-р Ганс Лангендорфер, Секретар Німецької Єпископської Конференції, у третій енцикліці Понтифік звернувся до соціальної тематики, піднятої в 1967 році у знаменитій енцикліці його попередника Папи Павла VI &laquo;Populorum Progressio&raquo; (Прогрес народів). У &laquo;Caritas in Veritate&raquo;, на думку о. Ганса, Папа Венедикт XVI поставив три головні акценти: 1) у центрі цього документу не стоїть бізнес, гроші, а людина. Як про це зазначив о. Дуарте да Кунья, Генеральний секретар Ради Єпископських Конференцій Європи: &laquo;Головна думка енцикліки полягає в тому, що Папа ставить в центр турботи Церкви людину. Церква повинна дивитися на людину в її повноті, в її розвитку і сприяти цьому розвитку на всіх рівнях: інтелектуальному, духовному, економічному, політичному та соціальному. Це - завдання Церкви - захищати людину, дивитися на людину&raquo;, - зауважив він; 2) люди, громадяни певної країни, мають право брати участь у житті своєї держави. &laquo;Ситуацію, коли люди, які бідні чи слабші, не можуть мати впливу чи голосу в тій чи іншій країні, слід змінювати&raquo;, - сказав о. Лангендорф; 3) різні країни мають різний рівень розвитку. Потрібно звертати увагу на бідні країни і їм допомагати. &laquo;Йдеться про поліцентризм. Різні країни не мають однакової економіки, тому ми повинні відкрити ринки, щоб кожна держава думала не тільки про Європейський Союз, чи якісь конкретні могутні держави, а насамперед про держави, які найбільше терплять, не концентруючись на конкретному регіоні чи структурі&raquo;, - сказав Секретар Німецької Єпископської Конференції.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Важливість соціального служіння Церкви відзначив і Секретар Синоду Єпископів УГКЦ Владика Богдан (Дзюрах). &laquo;Соціальна тема для нас є дуже важливою, особливо з огляду на те, що після років комуністичного переслідування, коли Церкві забороняли проводити харитативну діяльність, на сьогодні Церква починає заповняти ті ділянки, які раніше собі привласнила держава. Церква може дати щось більше, аніж тільки хліб, вона може дати любов, Божу ласку, відчуття близькості до іншої людини, яка є в потребі. Саме тому ми вбачаємо, що для нас ця нова енцикліка буде великою допомогою в різних ділянках соціального служіння&raquo;, - відзначив Владика Богдан.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Довідка</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Папа Римський Венедикт XVI свою першу енцикліку &laquo;Deus caritas est&raquo; (Бог є любов) підписав 25 грудня 2005 року. Вона була оприлюднена рівно через місяць і була своєрідним богословським роздумом про суть божественної любові як основи церковної місії милосердя і людинолюбства.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Друга енцикліка Понтифіка - &laquo;Spe salvi&raquo; (Врятовані надією) була опублікована 30 листопада 2007 року і розкривала християнське розуміння надії в сьогоднішньому світі.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Департамент інформації УГКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://ugcc.org.ua/</span></span></p>',NULL,NULL,1670,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2317,'P','2009-07-13',1,NULL,'Note','Генеральний секретар Ради Єпископських Конференцій Європи впевнений, що міграція дає нагоду для взаємного збагачення різних народів','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Я був вражений працьовитістю, чемністю української сім\'ї, яку я знаю у Португалії. Але найбільше враження було від свідчення їхньої віри. Правда, нажаль, багато сімей є розділені кордонами. Ми повинні сказати усьому Європейському Союзові про цю проблему і старатися, щоб ця важка дійсність була тягарем не тільки для цих людей чи їхніх країн, але для усіх членів співдружності. Всі уряди повинні співпрацювати для того, щоб сім\'ї були об\'єднані&raquo;, - наголосив о. Дуарте.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_rev_Duarte.jpg.jpg\" alt=\"\" />&laquo;У структурах Ради Єпископських Конференцій Європи є комісія, яка займається питаннями міграції. Вона тепер готує міжнародний конгрес, який відбудеться у 2010 році. Будемо розглядати міграцію не тільки як трудність, але як шанс. Адже це явище дає нагоду для взаємного збагачення різних народів&raquo;, - у коментарі для ЗМІ сказав о. Дуарте да Кунья, Генеральний секретар Ради Єпископських Конференцій Європи, під час свого перебування у Львові. Отець Дуарте прибув до Львова для участі у роботі 37-го З\'їзду Генеральних секретарів Єпископських Конференцій Європи, який відбувається 9 - 13 липня 2009 року.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Я був вражений працьовитістю, чемністю української сім\'ї, яку я знаю у Португалії. Але найбільше враження було від свідчення їхньої віри. Правда, нажаль, багато сімей є розділені кордонами. Ми повинні сказати усьому Європейському Союзові про цю проблему і старатися, щоб ця важка дійсність була тягарем не тільки для цих людей чи їхніх країн, але для усіх членів співдружності. Всі уряди повинні співпрацювати для того, щоб сім\'ї були об\'єднані&raquo;, - наголосив о. Дуарте.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У свою чергу Владика Мар\'ян Бучек, Секретар Конференції Римо-Католицьких Єпископів України, зазначив, що тема міграції є актуальною на зустрічах генеральних секретарів вже декілька останніх років. Важливим, на думку Владики Мар\'яна, є забезпечити належну душпастирську опіку для заробітчан. &laquo;Зазвичай бачимо, що якщо заробітчани перебувають під опікою доброго душпастиря, то їхні сім\'ї не розпадаються. Подруги мають більше духовних сил, щоб протистояти цій загрозі&raquo;, - сказав Секретар Конференції Римо-Католицьких Єпископів України.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Хочу з вдячністю згадати єпископів тих країн, які уже нам надали всебічне сприяння для налагодження душпастирської опіки наших вірних&raquo;, - сказав Владика Богдан (Дзюрах), Секретар Синоду Єпископів УГКЦ. При цьому він розповів, що під час з\'їзду ділився з його учасниками труднощами, які виникають у цьому питанні. &laquo;Хочемо щоб більше священиків поїхали у країни Західної Європи і там якнайкраще послужили нашим вірним у складних обставинах їхнього життя. Для цього потрібно, щоб на душпастирське служіння могли виїжджати також і одружені священики&raquo;, - сказав Владика Богдан.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пригадуємо, що під час Сорок п\'ятої сесії Синоду Єпископів Києво-Галицького Верховного Архиєпископства УГКЦ, яка відбулася 30 червня - 1 липня 2009 року було написано <a href=\"http://ugcc.org.ua/744.0.html?&amp;L=0\">Звернення щодо ситуації українських трудових емігрантів</a>, адресоване насамперед Президенту, Голові ВР України, Прем\'єр-міністру, а також уповноваженим керівникам парламентських фракцій. У документі зокрема йдеться про те, що новітні трудові мігранти з України на сьогодні абсолютно незахищені в країнах свого перебування, не відчувають безпеки. Закликаючи владу до цілеспрямованих активних дій, члени Синоду наголосили, що &laquo;злободенним завданням є термінове вироблення чіткої міграційної політики України, а також відповідних програм на загальнонаціональному та регіональному рівнях&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Департамент інформації УГКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://ugcc.org.ua/<em><br /></em></span></span></p>',NULL,NULL,1671,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2318,'P','2009-07-13',1,NULL,'Note',' Сучасні методи навчання у катехизації ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Найкраще вчимося на основі власного досвіду. Катехит чи вчитель використовуючи активні методи навчання, дає можливість учням самим відкривати важливі правди, моральні рішення у життєво важливих ситуаціях. Те, що учні навчаться у такий спосіб, стане частиною їх особистого досвіду і, швидше за все, відобразиться у їх подальшому житті.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пошук нових методів передавання Доброї новини є важливим завданням, яке постає перед кожним катехитом і вчителем християнської етики. Іван Павло ІІ в Апостольському поученні про катехизу в наш час Catechesi tradendae, п. 51 пише: &laquo;Різний вік та інтелектуальний розвиток християн, їх ступінь церковної й духовної зрілості та багато інших особистих обставин вимагають, щоб катехиза використовувала різні методи для досягнення її особливої мети - виховання у вірі&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">3-5 липня 2009 року у реколекційному домі сестер Чину св. Василія Великого у с. Береги на Львівщині відбувся науково-практичний семінар &laquo;Написання сценарію уроку із застосуванням активних методів&raquo;. Провів семінар мгр. Збігнєв Барцінскі, голова правління Товариства педагогів \"НАТАН\" (Республіка Польща). Учасники семінару (25 осіб) - катехити, вчителі християнської етики, працівники і викладачі Українського католицького університету - збагатили свої практичні навики і знання у новітніх способах проведення уроку із застосуванням активних методів. Врезультаті семінару постали сценарії катехиз літургійної тематики, які повинні допомогти учням повніше брати участь у Божественній Літургії. Учасники семінару взаємно збагачувалися, ділячись власними досвідом і розробками у цій сфері.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Найкраще вчимося на основі власного досвіду. Катехит чи вчитель використовуючи активні методи навчання, дає можливість учням самим відкривати важливі правди, моральні рішення у життєво важливих ситуаціях. Те, що учні навчаться у такий спосіб, стане частиною їх особистого досвіду і, швидше за все, відобразиться у їх подальшому житті.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Організував семінар Катехитично-педагогічний інститут УКУ, який вже більше десяти років розпрацьовує методичні матеріали і формує нові покоління катехитів і вчителів християнської етики.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Андрій Козіброда,<br /> працівник Катехитично-педагогічного інституту УКУ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://ugcc.org.ua/</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>',NULL,NULL,1672,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2319,'P','2009-07-13',1,NULL,'Note','Нелегальні іммігранти в Італії матимуть можливість легалізуватися','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Після недавнього (02.07.2009) прийняття в Італії закону щодо безпеки, який посилює заходи&nbsp; боротьби з нелегальною імміграцією уряд країни планує провести селективну легалізацію іноземних працівників-нелегалів, що працюють в італійських сім\'ях домогосподарками або по догляду за хворими чи пристарілими особами. Даний законопроект входитиме до антикризового пакету і є результатом спільної роботи Міністра внутрішніх справ Роберто Мароні і Міністра праці Мауріціо Сакконі.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Після недавнього (02.07.2009) прийняття в Італії закону щодо безпеки, який посилює заходи&nbsp; боротьби з нелегальною імміграцією уряд країни планує провести селективну легалізацію іноземних працівників-нелегалів, що працюють в італійських сім\'ях домогосподарками або по догляду за хворими чи пристарілими особами. Даний законопроект входитиме до антикризового пакету і є результатом спільної роботи Міністра внутрішніх справ Роберто Мароні і Міністра праці Мауріціо Сакконі.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Цей закон планується обговорити в Парламенті 20 липня. Оскільки проведення масових амністій нелегальних іммігрантів у країнах членах є заборонено Європейським Союзом, ця легалізація називатиметься &laquo;<em>вибіркове врегулювання робочих відносин домашніх працівників</em>&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З джерел у Парламенті стали відомі основні моменти майбутнього нововведення. Роботодавець (громадянин Італії, ЄС, або ж іншої держави, що має дозвіл на постійне перебування) зможе легалізувати одну домогосподарку і двох працівників по догляду за хворими. Необхідними умовами є: одноразова сплата у розмірі 500 євро за кожного робітника, початок робочих відносин не пізніше квітня 2009 року та наявність робочих відносин на момент подачі запиту.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Запити на легалізацію роботодавці зможуть подати впродовж одного місяця, з 1 по 30 вересня 2009 року, через інтернет до єдиного офісу з імміграції при Префектурах.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У вересні, на час дії легалізації, уряд оголосить мораторій на видворення з країни нелегалів, за винятком кримінальних злочинців, а також на адміністративне та кримінальне переслідування роботодавців, які залучають до роботи нелегалів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Легалізація стосуватиметься виключно домашніх працівників, оскільки на думку Міністра Сакконі: &laquo;її поширення на інші професії, італійський ринок праці, в умовах кризи, просто не витримає&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На думку ініціаторів цього закону, він дозволить врятувати від кримінального переслідування нелегальних працівників та їх роботодавців, а також суттєво поповнить державну казну, оскільки окрім одноразової сплати суми в 500 євро, легалізовані робітники зразу ж сплачуватимуть відрахування до пенсійного фонду та на інші соціальні витрати. Враховуючи, що за оцінками експертів, кількість зацікавлених у легалізації робітників становитиме близько 500.000, то вигода для бюджету Італії очевидна.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дана легалізація матиме величезне значення для українських нелегалів, оскільки як відомо, більшість наших співвітчизників працюють в Італії в сім\'ях домогосподарками та по догляду за хворими.&nbsp; За приблизними підрахунками, в Італії перебуває нелегально близько 200.000 громадян України.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Активними прихильниками та ініціаторами даного закону є Католицька Церква Італії, депутати-католики, що входять до більшості та опозиція.<br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Олесь Городецький, <br />Голова Християнського Товариства Українців в Італії</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://ugcc.org.ua/</span></span></p>',NULL,NULL,1673,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2320,'P','2009-07-15',1,NULL,'Note','Загальні аудієнції з Папою Венедиктом XVI у першому півріччі 2009 року','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Від початку 2009 року Єпископ Риму провів 22 загальні аудієнції, під час яких, 4 лютого, закінчив свій четвертий цикл катехиз, присвячений святому апостолові Павлу. А від середи 11 лютого Папа Венедикт ХVІ розпочав п\'ятий цикл повчань про великих авторів західної та східних Церков.<br /></span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Щосереди протягом року на площі Святого Петра, або у ватиканському залі Павла VІ проходять загальні аудієнції зі Святішим Отцем, які мають неабиякий відгомін у світовій пресі. Однак сьогодні, 15 липня 2009 р., Папа Венедикт ХVІ не зустрівся із вірними, оскільки розпочався його літній відпочинок на півночі Італії. <br /><br />Від початку 2009 року Єпископ Риму провів 22 загальні аудієнції, під час яких, 4 лютого, закінчив свій четвертий цикл катехиз, присвячений святому апостолові Павлу. А від середи 11 лютого Папа Венедикт ХVІ розпочав п\'ятий цикл повчань про великих авторів західної та східних Церков. Десять зустрічей було присвячено духовній спадщині таких великих церковних постатей, як святі Кирило і Методій, святий Іван Дамаскин, святий Боніфатій та інші. <br /><br />Протягом року Папа кілька разів був змушений перервати тяглість катехитичних повчань, оскільки відвідував африканські країни Камерун та Анголу, а потім відбув подорож до Святої Землі. <br /><br />Під час зустрічей у середи Святіший Отець також проповідував про Рік Священства, Пасхальне Таїснтво, Восьмидення Пасхи і про празник Петра і Павла. Остання загальна аудієнція Вселенського Архиєрея перед виїздом на літній відпочинок була присвячена його третій енцикліці &laquo;Любов у істині&raquo;, яка була опублікована7 липня. <br /><br />Варто зазначити, що аудієнції з Папою у середи є досить новим звичаєм. Папа Пій ХІІ був першим Вселенським Архиєреєм, який почав стало проводити зустрічі із вірними на площі перед ватиканською базилікою, або в інших придатних для цього залах. Згідно із архівними даними, перша катехиза того роду датується 26 квітням 1939 року. Майже усі повчання папи Пія ХІІ під час таких зустрічей були приурочені подружжю, подружній любові та родині.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,NULL,1674,5,1,43),
(2321,'P','2009-07-18',1,NULL,'Rss',' ХРЕЩЕННЯ РУСІ-УКРАЇНИ','<p style=\"text-align: right;\"><em>Всесвітлішим, всечеснішим </em></p>\n<div style=\"text-align: right;\"></div>\n<p style=\"text-align: right;\"><em>та високопреподобним отцям-душпастирям</em></p>\n<div style=\"text-align: right;\"></div>\n<p style=\"text-align: right;\"><em>Київської архиєпархії УГКЦ</em></p>\n<p style=\"text-align: center;\"><strong>Слава Ісусу Христу!</strong></p>\n<p>Цього року, в неділю, 26 липня, наша Церква буде згадувати та урочисто святкувати 1021 річницю Хрещення Русі-України, дату, знаменну для всіх нас - спадкоємців Володимирового Хрещення.</p>\n<p>З цієї нагоди поручаю всім парохам та адміністраторам Київської архиєпархії:\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=383:-hreshchennja-rusi-ukrajiny&catid=27:gk-v-ukrajini-cat&Itemid=57',1687,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2322,'P','2009-07-15',1,NULL,'Note','Визнання за УГКЦ статусу репресованої Церкви дасть їй можливість краще відповісти на сучасні потреби','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&laquo;Ми мусимо працювати. Мусимо стукати в ті двері - в надії, що держава це зрозуміє. Ще в часи Президента Кучми він назвав УГКЦ невід\'ємною часткою духовного життя і повноправним суб\'єктом української історії, з якої ця Церква колись &laquo;була брутально виштовхнута&raquo;. Це певне моральне визнання провини держави перед Церквою&raquo;</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Щодо того, чого ми очікуємо від цього рішення (визнання за УГКЦ статусу репресованої Церкви, - авт.), то слід констатувати, що внаслідок жахливих репресій Церква потерпіла не тільки морально, духовно, коли були знищені структури Церкви, священство, репресовані вірні та духовенство. Але також було втрачено багато матеріальних надбань, які не потрібні нам самі по собі, бо Церква не є якоюсь банковою системою, але потрібні для того, щоб ми могли краще реалізувати наше євангелізаційне завдання. Тому, якщо буде визнано офіційно статус репресованої Церкви, сподіваємося також, що буде визнане право на певну реституцію. Уже форми її та динаміку слід визначати у співпраці з відповідними комісіями з боку держави, але що цей процес повинен відбутися - немає сумніву - це є елемент історичної справедливості&raquo;, - наголосив Владика Богдан (Дзюрах), Секретар Синоду Єпископів УГКЦ, у коментарі для <a href=\"http://www.risu.org.ua/\" target=\"_blank\">РІСУ</a>, яке він дав під час проведення 37-го з\'їзду Генеральних секретарів Єпископських Конференцій Європи.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Владика Богдан відзначив, що на сьогодні вкрай необхідним є пізнання правди про історію УГКЦ. Він сказав, що поки що про офіційне визнання такого статусу за УГКЦ ще рано говорити, та воно повинно прийти &laquo;в міру осягнення нашим суспільством більшої зрілості та свободи&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&laquo;Ми мусимо працювати. Мусимо стукати в ті двері - в надії, що держава це зрозуміє. Ще в часи Президента Кучми він назвав УГКЦ невід\'ємною часткою духовного життя і повноправним суб\'єктом української історії, з якої ця Церква колись &laquo;була брутально виштовхнута&raquo;. Це певне моральне визнання провини держави перед Церквою&raquo;, - сказав щодо цієї теми в інтерв\'ю для веб-порталу <a href=\"http://religion.in.ua/main/806-kardinal-lyubomir-guzar-pro-zminu-statusu-ugkc.html\" target=\"_blank\">&laquo;Релігія в Україні&raquo;</a> Блаженніший Любомир. При цьому Глава УГКЦ пояснив, що Церкві було б легше жити й працювати, якби вона мала необхідні майнові засоби. &laquo;Тоді б ми мали засоби для кращого будівництва та розвитку УКУ, могли би організовувати будинки для літніх людей, для сиріт, для тих, хто лишився самотнім після розлучення тощо&raquo;, - пояснив Предстоятель УГКЦ. При цьому він додав, що Церква не потребує багатств власне для себе. &laquo;Багато грошей для Церкви - це свого роду прокляття. Тому треба бути надзвичайно обережним у фінансових питаннях. Надмір грошей псує Церкву. Тому ми потребуємо стільки, скільки необхідно&raquo;, - сказав Блаженніший Любомир.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">19 червня 2009 року Тернопільська обласна рада визнала за УГКЦ статус репресованої Церкви. Попередньо цей статус за УГКЦ визнали Львівська (4 грудня 2008 року) та Івано-Франківська (27 лютого 2009 року) міські ради. Обласні ради зробили це 23 грудня 2008 року та 23 березня 2009 року відповідно. 4 червня 2009 року визнала за УГКЦ статус репресованої Церкви Тернопільська міська рада. Згідно з їх ухвалами вони також звернулися до Президента України та Верховної Ради з проханням ініціювати на загальнодержавному рівні &laquo;процес встановлення історичної справедливості щодо УГКЦ та визнання за нею статусу репресованої&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Департамент інформації УГКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://ugcc.org.ua/</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>',NULL,NULL,1677,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2323,'P','2009-07-15',1,NULL,'Note','Початок беатифікаційного процесу о. Кирила Селецького  ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">11 липня 2009 року у Сокалі на Львівщині, у Катедральному храмі святих апостолів Петра і Павла, відбулося урочисте відкриття беатифікаційного процесу Слуги Божого отця Кирила Селецького. Архиєрейську Святу Літургію очолив Правлячий Архиєрей Сокальсько-Жовківської єпархії Владика Михаїл (Колтун), у співслужінні з Високопреосвященним Владикою Ігорем (Возьняком), Архиєпископом Львівським.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">11 липня 2009 року у Сокалі на Львівщині, у Катедральному храмі святих апостолів Петра і Павла, відбулося урочисте відкриття беатифікаційного процесу Слуги Божого отця Кирила Селецького. Архиєрейську Святу Літургію очолив Правлячий Архиєрей Сокальсько-Жовківської єпархії Владика Михаїл (Колтун), у співслужінні з Високопреосвященним Владикою Ігорем (Возьняком), Архиєпископом Львівським.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Після Божественної Архиєрейської Літургії була розпочата I-ша сесія трибуналу. На початку сесії канцлер Сокальсько-Жовківської єпархії о. Роман Синицький пояснив усім присутнім суть і значення зібрання, а також зачитав документ Конгрегації у справах святих про дозвіл на початок процесу та Декрет скликання трибуналу. Опісля Єпископ Сокальсько-Жовківський Михаїл (Колтун) оголосив початок беатифікаційного процесу Слуги Божого о.&nbsp;Кирила Селецького, а також вручив декрети обраним членам трибуналу. Після вручення декретів Владика Михаїл, с. Маркіана Василишин, Постулятор даної справи, та обрані члени трибуналу склали присягу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Після закінчення заходів у м.&nbsp;Сокалі відбувся молебень до Пресвятої Богородиці у селі Жужеляни, де було посвячено таблицю на могилі отця Кирила.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: <a href=\"http://www.radiovaticana.org/ucr/Articolo.asp?c=302147\" target=\"_blank\">www.radiovaticana.org/ucr/Articolo.asp</a></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>',NULL,NULL,1678,5,1,42),
(2324,'N','2009-07-18',0,NULL,'Note','','','<p><strong>Донецькі у Львові їли повітря Європи</strong></p>\r\n<p><strong>Парафіянка з Донбасу під час літніх канікул пройшла школу лідерства в Українському католицькому університеті</strong></p>\r\n<p>Про таку поїздку можна було тільки мріяти. Львів - це мрія. Бажаю кожному поринути в цю затишну українську атмосферу! Зустріла нас сестра Луїза Цюпа, директор &nbsp;катехитичного педагогічного інституту, заступник голови патріаршої катехитичної комісії УГКЦ. В самій школі у нас відбулися тренінги з соціальної педагогіки та богослов\'я. Тут я познайомилася з активними, веселими, духовно багатими людьми, які не лише навчали мене, але і вчилися у мене.</p>\r\n<p><img style=\"float: left;\" src=\"image,3171,200,x_b055aecf.jpg\" alt=\"\" />У моєму щоденнику в ті дні, з\'явився запис, який мені самі тяжко було б підробити:</p>\r\n<p>&laquo;Навкруги тиша...Щось манить мене туди, де багато люду, музик, ресторанів. Це мене лякає бо насправді я волію огортатися тишею. Дивуюсь своїй хоробрості: як можна було наважитись на такий крок?&raquo;...</p>\r\n<p>Іду, всі дивляться на мене, мов на якусь дівчину з іншої &nbsp;планети. Страх розвіяла подруга, яка йшла поруч зі мною майже все життя. Ноги дуже болять, а очі не вірять такій красі. Так і хочеться \"їсти\" те свіже повітря, гуляти зеленими парками, бачити щасливі усмішки дітей - це просто ЩАСТЯ!!! Щастя - це є Львів. Тяжко &nbsp;передати словами емоції, які зараз на душі, але якщо хоч на хвильку &nbsp;потрапити до \"маленької Європи\", то відчуєш себе найщасливішою людиною в &nbsp;світі!&raquo;</p>\r\n<p><strong>Христина Чуприна, парафіянка церкви св. Миколая у м. Димитрові, що поблизу Донецька спеціально для &laquo;Оранти&raquo;</strong></p>',NULL,NULL,1688,5,1,5);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2325,'P','2009-07-15',1,NULL,'Note','Віце-прем\'єр Іван Васюник відзначив роль Церков у моральному вихованні молоді','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Віце-прем\'єр-міністр заявив: &laquo;Я прошу вас спільної відповідальності, допомоги. Пліч-о-пліч з Урядом разом ми маємо шанс поза цими політичними баталіями навести лад і таким чином допомогти у належному вихованні нашого молодого покоління&raquo;.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Спільна позиція Уряду та українських Церков щодо збереження моральних принципів у суспільстві дає можливість належно виховувати молоде покоління. Таку думку висловив Віце-прем\'єр-міністр України Іван&nbsp;Васюник, виступаючи 13 липня 2009 року <a href=\"http://www.irs.in.ua/index.php?option=com_content&amp;view=article&amp;id=365&amp;catid=34&amp;Itemid=61\" target=\"_blank\">на зустрічі</a> Прем\'єр-міністра з представниками Всеукраїнської Ради Церков і релігійних організацій. Про це повідомляє <a href=\"http://www.irs.in.ua/\" target=\"_blank\">Інститут релігійної свободи</a>.&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Віце-прем\'єр-міністр заявив: &laquo;Я прошу вас спільної відповідальності, допомоги. Пліч-о-пліч з Урядом разом ми маємо шанс поза цими політичними баталіями навести лад і таким чином допомогти у належному вихованні нашого молодого покоління&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Я думаю, що якщо ми будемо разом працювати, то в невдовзі з українських телеекранів, з ефірів зникне пропагування культу насилля, ксенофобії і не толерантного відношення до інших національностей, представників інших віросповідань. Я думаю, що таким чином ми можемо зупинитися за крок. Слава Богу, Україну до цього часу оминали такі кричущі факти, від яких здригається чи-то Німеччина, чи-то інші цивілізовані країни. Ви знаєте про події в школах. Це все результат того, що діти бачать з телевізорів&raquo;, - сказав Іван&nbsp;Васюник.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Він зауважив, що протягом останніх п\'яти років були, на його переконання, здійснені свідомі кроки для того, щоб така важлива функція держави, як захист суспільної моралі, не виконувалася. Однак, тепер урядова Національна експертна Комісія з питань захисту суспільної моралі працює.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Також Віце-прем\'єр подякував Церквам за велику роботу в умовах фінансової та економічної кризи, і за те, що вони допомагають підтримувати соціальний оптимізм народу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За словами Віце-прем\'єра, позиція Уряду загалом, і Прем\'єр-міністра зокрема, підтверджує, що питання, які висловило духовенство, в процесі діалогу можна буде реалізувати.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Слід відзначити, що під час зустрічі з Прем\'єр-міністром члени Всеукраїнської Ради Церков і релігійних організацій засвідчили свою підтримку діяльності Національної експертної комісії України з питань захисту суспільної моралі, з якою у грудні 2008 року вони <a href=\"http://www.irs.in.ua/index.php?option=com_content&amp;view=article&amp;id=88&amp;catid=34&amp;Itemid=61\" target=\"_blank\">підписали Меморандум</a> про співробітництво. При цьому глава УПЦ Київського патріархату Патріарх Філарет закликав главу Уряду дати доручення Міністерству закордонних справ України не підтримувати на міждержавному рівні жодних документів стосовно легалізації одностатевих шлюбів чи підтримки одностатевих відносин.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами:<a href=\"http://www.irs.in.ua/\" target=\"_blank\">www.irs.in.ua</a></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>',NULL,NULL,1680,5,1,30);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2326,'P','2009-07-16',1,NULL,'Note','Віктор Ющенко візьме участь у Всеукраїнській прощі до чудотворної ікони Богородиці Зарваницької','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img src=\"http://www.oda.te.gov.ua/sites/oda.te.gov.ua/images/news/8881.jpg\" border=\"1\" alt=\"\" hspace=\"5\" width=\"180\" height=\"135\" align=\"left\" />У неділю, 19 липня, Президент України візьме участь у Всеукраїнській прощі до Зарваниці (Тернопільська область) - найбільшого паломницького центру Української Греко-католицької церкви. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Уже за доброю традицією 18-19 липня в Зарваниці відбудеться всеукраїнська проща. З кожним роком статус прощі та кількість прочан, які беруть у ній участь, невпинно зростає. За прогнозами експертів, у ці вихідні у Зарваниці очікується 100 тисяч прочан з різних куточків України.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"border: 1px solid black; float: left; margin-left: 5px; margin-right: 5px;\" src=\"http://www.oda.te.gov.ua/sites/oda.te.gov.ua/images/news/8881.jpg\" alt=\"\" width=\"180\" height=\"135\" />У неділю, 19 липня, Президент України візьме участь у Всеукраїнській прощі до Зарваниці (Тернопільська область) - найбільшого паломницького центру Української Греко-католицької церкви. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Уже за доброю традицією 18-19 липня в Зарваниці відбудеться всеукраїнська проща. З кожним роком статус прощі та кількість прочан, які беруть у ній участь, невпинно зростає. За прогнозами експертів, у ці вихідні у Зарваниці очікується 100 тисяч прочан з різних куточків України.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;Вперше в історії на прощу завітає найвища посадова особа Української держави - Президент.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Цікаво, що сам Віктор Ющенко&nbsp; в Зарваниці буде не вперше. До цього він уже двічі був у святому місці - в 2000 році, коли перебував на чолі уряду, та 2003 році - як лідер опозиції. Саме тоді до майбутнього Президента зверталися жителі Теребовлянщини, зокрема населених пунктів Зарваницько-Золотниківського куща, про допомогу в газифікації сіл. Ющенко пообіцяв докласти всіх зусиль, щоб газифікувати не лише села Теребовлянщини, а й Зарваницьку святиню. Своє слово Віктор Андрійович дотримав. Впродовж шести років років&nbsp; голубий вогник запалав більш як в 35 селах Теребовлянського району, а також газифіковано Марійський Духовний Центр в Зарваниці. Зокрема, при активній підтримці особисто Віктора Ющенка та сприянні народного депутата від Теребовлянщини Ігоря Тарасюка, впродовж 2003-2008 років газифіковано 16 населених пунктів Зарваницько-Золотниківського куща, прокладено:&nbsp; 57,379 км. підвідного (до населених пунктів) газопроводу;&nbsp;&nbsp; 140,479 км. внутрішнього (в межах населених пунктів) газопроводу, газифіковано більше 2000 житлових помешкань.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Газифіковано такі села: Семиківці, Підруда, Маловоди, Раківці, Соснів, Надрічне, Соколів, Панталиха, Золотники, Бурканів, Гайворонка, Вишнівчик, Зарваниця, Сапова, Котузів, Гвардійськ.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На цьогорічній прощі, присвяченій покликанню до священичого стану, крім паломників з різних міст і сіл України, будуть багато віруючих з ближнього та дальнього зарубіжжя, представники вищого духовенства, керівники області.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Вище духовенство Церкви буде представлено Патріархом УГКЦ Любомиром кардиналом Гузаром, єпископами Степаном Меньком, Михаїлом Колтуном, Василем Медвітом, Василем Семенюком. Також до прощі приєднається кардинал Римо-Католицької церкви, дипломат Святого Престолу, голова Папської комісії у справах міста-держави Ватикан та Губернаторства Ватикан Джованні Лайоло. За матеріалами: http://www.oda.te.gov.ua/</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>',NULL,NULL,1682,5,1,30);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2327,'P','2009-07-16',1,NULL,'Note','Діалог Наступника святого Петра зі світом','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Зустрічаючись з політичними лідерами, Венедикт&nbsp;XVI мав нагоду не лише отримати новини, але також заслухати точки зору, опінії та пропозиції, які підтримують цей постійний діалог Наступника святого Петра зі світом, діалог, іншим сильним вираженням якого є Апостольські подорожі та зустрічі з Єпископськими Конференціями в рамках візитів &laquo;ad Limina&raquo;.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Протягом перших семи місяців 2009 року Папа Венедикт&nbsp;XVI прийняв на аудієнціях 23-х політиків, зокрема, Президентів та Глав Урядів, а також керівників міжнародних організацій. Крім королівської сім\'ї Принца Уельського, Генерального Секретаря Арабської Ліги та Спікера Конгресу&nbsp;США, Вселенський Архиєрей прийняв 12 найвищих урядових осіб Європи, 5 з Америки, 2 з Азії та 1 з Австралії. Пригадаємо, що 1 червня 2009&nbsp;р. відбувся офіційний візит до Апостольської Столиці Президента України Віктора Ющенка, який також був прийнятий Папою.<br /><br />У 2008 році Венедикт&nbsp;XVI зустрівся з 19-ма лідерами чи політичними представниками найвищого рівня (3 з Африки, 4 з Азії, 1 латиноамериканський, 7 європейських, 3 з Океанії та 1 із Північної Америки), а також з Генеральним Директором Міжнародної Організації Праці та Генеральним Секретарем ООН.<br /><br />Такі зустрічі є свідченням турботи Папи про долю народів. Зустрічаючись з політичними лідерами, Венедикт&nbsp;XVI мав нагоду не лише отримати новини, але також заслухати точки зору, опінії та пропозиції, які підтримують цей постійний діалог Наступника святого Петра зі світом, діалог, іншим сильним вираженням якого є Апостольські подорожі та зустрічі з Єпископськими Конференціями в рамках візитів &laquo;ad Limina&raquo;. Варто згадати також вручення новими послами вірчих грамот, яких у 2009 році було 15.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,NULL,1683,5,1,43),
(2328,'P','2009-07-18',1,NULL,'Rss','Бог єднає Україну','<p>Парафіянка з Донбасу під час літніх канікул пройшла школу лідерства в Українському католицькому університеті</p>\n<p>Про таку поїздку можна було тільки мріяти. Львів - це мрія. Бажаю кожному поринути в цю затишну українську атмосферу! Зустріла нас сестра Луїза Цюпа, директор  катехитичного педагогічного інституту, заступник голови патріаршої катехитичної комісії УГКЦ. В самій школі у нас відбулися тренінги з соціальної педагогіки та богослов\'я. Тут я познайомилася з активними, веселими, духовно багатими людьми, які не лише навчали мене, але і вчилися у мене.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=384:bog-jednaje-ukrajinu&catid=37:spilnota-cat&Itemid=49',1689,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2329,'P','2009-07-20',1,NULL,'Rss','Греко-католики київської архиєпархії відзначатимуть річницю Хрещення Володимирового ','<p>У неділю, 19 липня, за тиждень перед святкуванням Хрещення, на всіх літургіях у храмах Київської архиєпархії УГКЦ священики нагадали вірним про цю значущу подію для України та закликали всіх по можливості приходити до церкви у вишиванках та традиційних українських убраннях.\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=386:greko-katolyky-kyjivskoji-arhyjeparhiji-vidznachatymut-richnycju-hreshchennja-volodymyrovogo-&catid=27:gk-v-ukrajini-cat&Itemid=57',1691,NULL,1,26),
(2330,'P','2009-07-20',1,NULL,'Rss','Відбулася Всеукраїнська проща до Зарваниці','<p><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_19-07-09-3.jpg.jpg\" /></p>\n<p>Десятки тисяч прочан з усієї України 18-19 липня 2009 р. зібралися у Марійському духовному центрі Зарваниці, що на Тернопільщині. Вшанувати чудотворну ікону Матері Божої Зарваницької приїхали Глава УГКЦ блаженніший Любомир Гузар, Голова Папської комісії у справах Держави-Міста Ватикану та Губернаторства Держави-Міста Ватикану кардинал Джованні Лайоло, Митрополит Львівський РКЦ Мечислав Мокшицький, Папський нунцій в Україні архиєпископ Іван Юрковіч, владики УГКЦ.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=387:vidbulasja-vseukrajinska-proshcha-do-zarvanyci&catid=27:gk-v-ukrajini-cat&Itemid=57',1692,NULL,1,26),
(2331,'N','2009-07-20',0,NULL,'Note','','','<p>&nbsp;</p>\r\n<p><img src=\"http://ugcc.org.ua/uploads/RTEmagicC_19-07-09-3.jpg.jpg\" alt=\"\" />Десятки тисяч прочан з усієї України 18-19 липня 2009&nbsp;р. зібралися у Марійському духовному центрі Зарваниці, що на Тернопільщині. Вшанувати чудотворну ікону Матері Божої Зарваницької приїхали Глава УГКЦ блаженніший Любомир Гузар, Голова Папської комісії у справах Держави-Міста Ватикану та Губернаторства Держави-Міста Ватикану кардинал Джованні Лайоло, Митрополит Львівський РКЦ Мечислав Мокшицький, Папський нунцій в Україні архиєпископ Іван Юрковіч, владики УГКЦ.</p>\r\n<p><img src=\"image,3176,200,21874.jpg\" alt=\"\" width=\"200\" height=\"160\" />Після молитви біля ікони Зарваницької Матері Божої владика Василь Семенюк, Єпарх Тернопільсько-Зборівський УГКЦ, подарував високому гостеві з Ватикану її копію та квіти. \"Ця ікона займе особливе місце у моїй домівці, - зазначив кардинал Лайоло, - і зробить мене ближчим до вас. Квіти ж я покладу до стіп Пресвятої Богородиці, щоб Вона завжди опікувалася вами. Будьте певні, що Богородиця хоче бути завжди з вами і дбати не тільки про духовне, а, як дбайлива Мати, також і про матеріальне. Найбільшою цінністю, яку Вона хоче вкласти у ваше серце, є Ісус Христос. Це те, що зробить вас багатими і щедрими.<br /><br />Особливим моментом цьогорічної прощі стало посвячення купелі святої Анни. Впродовж тільки першого дня прощі у купелі побували тисячі паломників - черга охочих біля сходів стояла постійно.</p>\r\n<p><img src=\"http://ugcc.org.ua/uploads/RTEmagicC_19-07-09-2.jpg.jpg\" alt=\"\" />Вечірню Святу Літургію 18 липня служили одразу чотири владики-ювіляти: Степан Меньок,&nbsp;ЧНІ, Екзарх Донецько-Харківський, Михаїл Колтун,&nbsp;ЧНІ, Єпарх Сокальсько-Жовківський, Василь Медвіт,&nbsp;ЧСВВ, єпископ-помічник Донецько-Харківського Екзархату, та Василь Семенюк, яким цьогоріч виповнюється 60 років. \"Маємо перед собою чотирьох мужів, яких Бог обдарував не тільки довголіттям, а й дав силу Духа, щоб вони ці роки використали на добро Церкви. Ми можемо їх обдарувати чимось. Хочемо владикам подарувати молитву, щоб могли роки, які їм дасть Бог, використати якнайкраще\", - привітав архиєреїв блаженніший Любомир. <br /><br />Як стемніло, відбулася процесія зі свічками від парафіяльної церкви до чудотворного місця, потім відбувся духовний концерт на Співочому полі. <br /><br />У неділю, 19 липня, на прощу до Зарваниці прибув Президент України Віктор Ющенко з родиною. \"Дякую цій землі і всій Західній Україні за те, що береже наш національний і соборний дух\", - зазначив він у своєму виступі. Марійському духовному центру Президент подарував кивот-дарохранильницю та Євхаристійний набір, а від владики Василя Семенюка отримав копію чудотворної ікони Зарваницької Матері Божої.<br /><br />У своїй недільній проповіді Глава УГКЦ Блаженніший Любомир нагадав, що проща проходить під знаком священичого покликання і наголосив на тому, що потребуємо добрих священиків, через яких зможе освятитися Божий люд.<br /><br />За матеріалами: http://www.radiovaticana.org/</p>\r\n<p>&nbsp;</p>',NULL,NULL,1693,5,1,5);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2332,'P','2009-07-20',1,NULL,'Rss','Тема міграції в новій енцикліці Папи Венедикта XVI «Caritas in Veritate»','<p><a href=\"http://3.bp.blogspot.com/_WPjZST8SC40/SD3LDY9qqDI/AAAAAAAASME/xkzl2zvqZ74/s400/destauranimams121007.jpg\" target=\"_blank\">http://3.bp.blogspot.com/_WPjZST8SC40/SD3LDY9qqDI/AAAAAAAASME/xkzl2zvqZ74/s400/destauranimams121007.jpg</a><img style=\"margin: 10px; float: right;\" complete=\"true\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_caritas-in-veritate1.jpg.jpg\" />«Caritas in Veritate» (з лат. «Любов у істині») є третьою за чергою енциклікою Папи Венедикта XVI. Цього разу Святіший Отець присвячує свої роздуми соціальним проблемам та темі цілісного розвитку людини. Як відомо, нова енцикліка була підписана до друку Папою Венедиктом XVI 29 червня 2009 року, а 7 липня, напередодні самміту «Великої вісімки», що проходив у Аквілі (Італія), «Caritas in Veritate» була представлена та опублікована французькою, англійською, італійською, польською, португальською, іспанською та німецькою мовами.</p>\n<p>У п\'ятому розділі під заголовком «Співробітництво людської родини»<strong> </strong>енцикліка «Caritas in Veritate» звертає увагу світової громадськості зокрема на проблеми міграції.</p>\n<p>Як відомо, феномен міграції як з огляду української численної еміграції в країни Західної Європи та Америки, так і з огляду іноземної імміграції в Україну є неабияк актуальний і українському суспільству, який нажаль поки що не знаходить широкого відголосу ні серед політиків, ні в українських ЗМІ.\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=388:tema-migraciji-v-novi-encyklici-papy-venedykta-xvi-lcaritas-in-veritater&catid=32:gk-svit-cat&Itemid=58',1694,NULL,1,26),
(2333,'P','2009-07-23',1,NULL,'Rss','Король Майкл співав до Бога','<p>Ми чули, що саме  король Майкл співав до Бога</p>\n<p>Мир  прийде!!! Коли людина  ні про що не замислюється,  їй живеться добре. Вона  думає  тільки про  свої повсякденні  проблеми, домашні  клопоти, роботу. Це  не є  дуже добре...  Я не згодна з таким  правом на існування.  Якщо ти людина, то   повинна думати не тільки  про себе, але й про інших.\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=389:korol-makl-spivav-do-boga&catid=37:spilnota-cat&Itemid=49',1697,NULL,1,26),
(2334,'P','2009-07-27',1,NULL,'Rss','Святкові літургії з приводу Хрещення Володимирового на найдавнішому місці молитви','<p>У неділю у церкві св. Миколая на Аскольдовій Могилі відбулися три літургії з нагоди чергової річниці Хрещення Володимирового. Під час богослужінь вірні почули «звернення Верховного Архієпископа Української греко-католицької церкви, а також взяли участь у чині освячення води.</p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=392:svjatkovi-liturgiji-z-pryvodu-hreshchennja-volodymyrovogo-na-nadavnishomu-misci-molytvy&catid=25:parafija-cat&Itemid=27',1703,NULL,1,26),
(2335,'P','2009-07-26',1,NULL,'Rss','Діти парафіян церкви св. Миколая на Аскольдовій Могилі вирушили на карпатський курорт','<p>5 вихователів та 25 дітей сьогодні відпочивають та набираються сил в оздоровчому центрі «Заріччя» монастиря Андрія Первозваного. Сюди парафіяльну громаду запросив отець Йосафат, настоятель релігійної громади святого Йосафата в Івано-Франківську, із яким попередньо церква уклала угоду.</p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=393:dity-parafijan-cerkvy-sv-mykolaja-na-askoldovi-mogyli-vyrushyly-na-karpatsky-kurort&catid=25:parafija-cat&Itemid=27',1704,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2336,'P','2009-07-20',1,NULL,'Note','У ВР України зареєстрований законопроект про надання УГКЦ статусу репресованої  ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Відповідний проект постанови N4845 у Верховній Раді зареєстрували 17 липня депутати Іван Денькович, Дмитро Шлемко і Василь Деревляний (БЮТ).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Раніше депутати Івано-Франківської, Львівської, Тернопільської областей зверталися з проханнями до Президента Віктора Ющенка ініціювати встановлення історичної справедливості щодо УГКЦ на загальнонаціональному рівні.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Відповідний проект постанови N4845 у Верховній Раді зареєстрували 17 липня депутати Іван Денькович, Дмитро Шлемко і Василь Деревляний (БЮТ).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Раніше депутати Івано-Франківської, Львівської, Тернопільської областей зверталися з проханнями до Президента Віктора Ющенка ініціювати встановлення історичної справедливості щодо УГКЦ на загальнонаціональному рівні.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">УГКЦ - найбільша (близько 5,5 млн. віруючих) Католицька Церква східного обряду. Ця Церква була ліквідована сталінським режимом і насильницьки \"возз\'єднана\" з Російською Православною Цeрквою після Другої світової війни. Незважаючи на офіційну заборону і жорстокі переслідування, УГКЦ зберегла свої єрархічні структури у підпіллі та діаспорі, а у гpудні 1989 року домоглася своєї офіційної легалізації.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Навесні 1991 р. Мирослав-Іван кардинал Любачівський, тодішній Глава УГКЦ, повернувся з еміграції до своєї резиденції у Львовi. Сьогодні Главою Української Греко-Католицької Церкви є Верховний Архиєпископ Любомир кардинал Гузар. З 21 серпня 2005 року резиденція Глави УГКЦ знаходиться у Києві. Маючи( за даними Держкомнацрелігій станом на 1 січня 2009 року) 3566 громад, УГКЦ сьогодні посідає одне з провідних місць за кількістю релігійних громад в Україні.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Єпархії УГКЦ існують у Польщі, Німеччині, Франції, Великобританії, США, Канаді, у північній Америці, в Буенос-Айресі (Арґентина), Куритибі (Бразилія) і Мельбурні (Австралія). Українські греко-католики мають також вікаріат у Румунії, громади у Литві (Вільнюс), Латвії (Рига, Даугавпілс та Резекне), Естонії (Таллінн), Молдові (Кишинів), Австрії, Параґваї, Венесуелі, Португалії, Італії, Іспанії, Греції, Швейцарії, Бельгії, Казахстані та Росії.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: <a href=\"http://gska2.rada.gov.ua/pls/zweb_n/webproc4_1?id=&amp;pf3511=35875\" target=\"_blank\">www.risu.org.ua/ukr/news/article;30184/ gska2.rada.gov.ua/pls/zweb_n/webproc4_1</a></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>',NULL,NULL,1695,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2337,'P','2009-07-23',1,NULL,'Note','Під патронатом Блаженнішого Любомира в Україні буде проведено концерти популярної музики','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Коли ми почали дискутувати над цим проектом, я зрозумів, що можемо дати людям далеко більше ніж просто видовище... Дехто може вважати, що успіх цих концертів залежить від того, як глибоко сягнемо у наші кишені. Але правдою є те, що він залежить від того, як широко відкриємо вуха нашого серця для сприйняття краси, для сприйняття духовних вартостей, які криються в красі...щоби ми з них вийшли кращими людьми&raquo;, - зазначив Блаженніший Любомир (Гузар).</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_kupoly_2009_02.jpg.jpg\" alt=\"\" /><a href=\"http://ugcc.org.ua/623.0.html\">Як і попередній</a>, цей тур також благодійний. (Під час концертів Музика об\'єднує, підтримує, надихає. З 7 до 22 вересня Українська Греко-Католицька Церква запрошує на концерти &laquo;З вірою і любов\'ю&raquo;. Як відомо, академічні концерти, об\'єднані такою ж назвою, з великим успіхом пройшли у травні. Це була культурницька місія. Тур став посланням УГКЦ сучасникам. Наші часи характеризуються кризою надії, повсякчасним ревізуванням вічних цінностей. Ми піддаємося різним інформаційним навіюванням, що спонукають нас до метушливого існування. Саме звернення до музики, гармонії, академічної культури було одним із дороговказів донедавна катакомбної Церкви - куди йти, у чому шукати розраду і, зрештою, насолоду.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Коли ми почали дискутувати над цим проектом, я зрозумів, що можемо дати людям далеко більше ніж просто видовище... Дехто може вважати, що успіх цих концертів залежить від того, як глибоко сягнемо у наші кишені. Але правдою є те, що він залежить від того, як широко відкриємо вуха нашого серця для сприйняття краси, для сприйняття духовних вартостей, які криються в красі...щоби ми з них вийшли кращими людьми&raquo;, - зазначив Блаженніший Любомир (Гузар). Цей концертний тур став містком між українською і європейською музичною культурами, між українською культурою - класичною і сучасною. Ба більше, спротивом наступу масової культури у найгірших її зразках. А головне, це було спонукання до консолідації українців різних конфесій.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Природно, що така ініціатива потребувала невідкладного продовження. Саме ним і буде організація другого туру &laquo;З вірою і любов\'ю&raquo;, що охопить 13 міст і містечок України. Маршрут нашого творчого десанту пролягатиме через шість областей: Волинську, Рівненську, Чернівецьку, Тернопільську, Івано-Франківську і Львівську.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У концертах популярної музики візьмуть участь: Тарас Петриненко і Тетяна Горобець, вокальна формація &laquo;Піккардійська терція&raquo;, лідер &laquo;Мандрів&raquo; Фома, гурти &laquo;С.К.А.Й&raquo;, &laquo;Гудімов&raquo;, &laquo;Горгішелі&raquo;, &laquo;Транс-формер&raquo;, Наталя і Дарина (Мамай) Сумські, Ігор Мірошниченко. Режисер-постановник - Сергій Архипчук. Вітальне слово виголосить Глава УГКЦ Блаженніший Любомир.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На концертах академічної музики було зібрано біля 800 тис. гривень. І цього разу виручені кошти підуть на завершення будівництва Патріаршого собору Воскресіння Христового у Києві. Отож на підтримку української духовності й УГКЦ до кола видатних діячів української культури приєднуються популярні вітчизняні музиканти, і, звісно, численні співучасники-глядачі. Зібравшись у вірі і любові, підтримаймо один одного!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Департамент інформації УГКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://ugcc.org.ua/<br /></span></span></p>',NULL,NULL,1696,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2338,'P','2009-07-23',1,NULL,'Note','\"Послання надії\" Конференції Європейських Церков','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Оскільки ми є християнами, насмілюємося надіятися&raquo; - з таким посланням до мешканців Європи звертаються делегати різних Церков Європи, зібрані на ХІІІ&nbsp;Загальній асамблеї Конференції Європейських Церков, яка від 15 до 21&nbsp;липня 2009&nbsp;р. відбулася у Франції в місті Ліоні. У зустрічі взяли участь представники різних Церков Європи - православні, протестанти, англіканці та старо-католики.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Оскільки ми є християнами, насмілюємося надіятися&raquo; - з таким посланням до мешканців Європи звертаються делегати різних Церков Європи, зібрані на ХІІІ&nbsp;Загальній асамблеї Конференції Європейських Церков, яка від 15 до 21&nbsp;липня 2009&nbsp;р. відбулася у Франції в місті Ліоні. У зустрічі взяли участь представники різних Церков Європи - православні, протестанти, англіканці та старо-католики.<br /><br />&laquo;Надія - читаємо у заключному документі зустрічі - дає нам радість, мир, сміливість, відвагу та свободу. Звільняє нас від страху, відкриває наші серця і зміцнює наше свідчення Воскреслого Господа. Ми, християни, покликані до єдиної надії у Христі, Який є джерелом любові, прощення і примирення&raquo;. У спільному посланні європейські Церкви висловлюють стурбованість тим, що в той час, коли з запалом докладаються зусилля для побудови об\'єднаної та примиреної Європи, між народами, культурами та релігіями зводяться нові мури поділів: між громадянами та іммігрантами, між багатими та бідними, між працюючими та безробітними, між тими, кого права шанують і тими, кого утискають. У документі також згадано проблеми, пов\'язані з кліматичними змінами та економічною кризою.<br /><br />&laquo;Не зважаючи на все, ми твердо переконані в тому, що як християни, ми маємо особливу надію, якою можемо ділитися в ситуаціях, які здаються безнадійними&raquo;. Надія є там, де тривають змагання за правду та справедливість, там, де протистоїться будь-якій формі насильства та расизму, де захищається людську гідність. Надія є там, де наголошується на вимогах безкорисливої солідарності між окремими людьми та між народами, де є щира пошана до створеного світу. &laquo;Викликом, який Генеральна Асамблея ставить перед усіма Церквами-членами, - пишуть учасники ХІІІ&nbsp;Асамблеї Конференції Європейських Церков, - є відважне звіщення надії. Надії, яка не виражається порожніми деклараціями, але конкретними вчинками і живою вірою. Стверджуємо, що Церкви повинні працювати на користь справедливості і говорити правду можновладцям. Це означає руйнувати мури між людьми, культурами і релігіями, щоб навчитися розрізняти образ Бога в обличчі ближнього. Це означає шанувати, а не лише толерувати інших людей. Однак, понад усе, це означає відшукати нові способи виражати нашу солідарність з бідними, далекими і близькими до нас&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',NULL,NULL,1700,5,1,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2339,'P','2009-07-25',1,NULL,'Rss','Наші щирі вітання нашому настоятелю і наставнику','<p>Шановний Отче Ігорю!</p>\n<p><img src=\"http://askoldova-mohyla.org/uk/image,3188,200,IMG_0352.jpg\" style=\"float: right; margin: 10px;\" width=\"200\" height=\"150\" />Вітаємо Вас з 50-ти річним ювілеєм.</p>\n<p>Ми розділяємо з Вами всю радість святкувань і шкодуємо, що нас немає поряд з вами.</p>\n<p>Ці п\'ятдесят років Ви прожили в невтомній праці, з повною самовіддачею, великими здобутками.</p>\n<p>Глибока віра, працьовитість і жертовність - це ті головні риси, якими Господь наділив Вас, і які є головною рушійною силою у Вашому житті.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=394:nashi-shchyri-vitannja-nashomu-nastojatelju-i-nastavnyku&catid=25:parafija-cat&Itemid=27',1706,NULL,1,26),
(2340,'P','2009-07-29',1,NULL,'Note','Зростають лави нащадків Володимира','<pre style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Звичайно, це насамперед православні церкви в Україні, це греко-католицька<br />церква, але навіть протестантські церкви, вони теж вважають себе часткою<br />Володимирового хрещення, як це не парадоксально звучить. І російська<br />зарубіжна церква теж приєдналася зараз до Московського патріархату і вважає <br />себе наступницею Володимирового хрещення&raquo;, сказав отець Кирило, голова<br />синодальної комісії зі зовнішніх зв\'язків УПЦ.</span></span></pre>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>','<pre style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У Лаврі вже відзвітували про результати візиту московського гостя</span></span></pre>\r\n<pre style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Відбулася прес-конференція з участю голів синодальних комісій УПЦ зі ЗМІ<br />отця Олександра Коваленка та зі зовнішніх зв\'язків отця архімандрита Кирила<br />Говоруна, які оцінили візит Московського патріарха до України як успішний.<br />За словами представників Митрополита київського Володимира Сабодана,<br />Московський патріарх довів, що це був візит не політичний, а пастирський.<br />За їхніми словами Предстоятель руської православної Церкви підтримує рух<br />місцевих священників до відходу від так званого &laquo;політичного православ\'я&raquo;</span></span></pre>\r\n<pre style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Кореспондент інформаційної агенції &laquo;Оранта&raquo; поцікавився в архімандрита<br />Кирила Говоруна, які релігійні юрисдикції належать нині до Володимирового<br />хрещення. </span></span></pre>\r\n<pre style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Звичайно, це насамперед православні церкви в Україні, це греко-католицька<br />церква, але навіть протестантські церкви, вони теж вважають себе часткою<br />Володимирового хрещення, як це не парадоксально звучить. І російська<br />зарубіжна церква теж приєдналася зараз до Московського патріархату і вважає <br />себе наступницею Володимирового хрещення&raquo;, сказав отець Кирило, голова<br />синодальної комісії зі зовнішніх зв\'язків УПЦ.<strong><br /></strong>Матеріал: Оксани Михайлової, інформаційна агенція &laquo;Оранта&raquo;</span></span></pre>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>',NULL,NULL,1707,5,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2341,'P','2009-08-26',0,NULL,'Note','Ясна гора чекає прочан на коронацію чудотворної ікони Матері Божої Гошівської','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><strong>28&nbsp;серпня 2009&nbsp;р</strong>., у празник Успення Пресвятої Богородиці, на Ясній Горі у с.Гошові (Долинський район Івано-Франківської області) відбудеться <strong>коронація Гошівської чудотворної ікони Матері Божої</strong>, перед якою впродовж століть моляться тисячі паломників Ясної Гори</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><strong><img style=\"float: right;\" src=\"image,3191,200,image002.gif\" alt=\"\" width=\"107\" height=\"200\" />28&nbsp;серпня 2009&nbsp;р</strong>., у празник Успення Пресвятої Богородиці, на Ясній Горі у с.Гошові (Долинський район Івано-Франківської області) відбудеться <strong>коронація Гошівської чудотворної ікони Матері Божої</strong>, перед якою впродовж століть моляться тисячі паломників Ясної Гори.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Як було повідомлено раніше, 1 липня 2009 р., <strong>Святіший Отець Венедикт&nbsp;XVI</strong> <strong>поблагословив</strong> <strong>золоті корони</strong> для Гошівської чудотворної ікони Матері Божої під час загальної аудієнції на площі Святого Петра у Ватикані. Вселенському Архиєрею була подарована копія Гошівської чудотворної ікони Матері Божої, яку для Папи привіз ігумен василіянського монастиря на Ясній Горі у Гошові. о.&nbsp;Дам\'ян Кастран ЧСВВ.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><em><img style=\"float: left;\" src=\"image,3191,200,image003.jpg\" alt=\"\" width=\"200\" height=\"150\" />Довідково:</em><em> У мальовничій місцевості на Прикарпатті, у селі Гошеві, що у Долинському районі Івано-Франківської області, Пресвята Богородиця протягом багатьох років єднає в молитві численних паломників з різних куточків України та світу. Вірні, і не лише греко-католики, спішать із поклоном до стіп Марії, щоб приклонитися перед Її чудотворною іконою на Ясній Горі, благаючи заопікуватись нами, Її дітьми, та випрошуючи благодатей. </em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><em>11 січня 2009 р. копія Чудотворної ікони Гошівської Матері Божої поставлена на почитання у храмі св. Миколая Чудотворця на Аскольдовій Могилі.</em>&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><strong>&nbsp;</strong><strong>За додатковою інформацією </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">просимо звертатися до отців Василіян</span></span></strong></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></strong><strong><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">на Ясній Горі, с. Гошів:</span></span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Монастир Преображення</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Господа нашого Ісуса Христа</span></span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Ігумен: о. Дам&rsquo;ян (Кастран)</span></span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">тел. (03-477) 30-532; 72-56-33</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">с. Гошів, Долинський р-н <br />77520 Івано-Франківська обл.</span></span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">у Києві:</span></span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Монастир св. Василія Великого</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><strong>Настоятель: о. Василь (Тучапець) <br /></strong>тел./факс: (044) 486-44-08, <br />тел.: (044) 486-14-69</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">вул. Вознесенський узвіз, 7 <br />04053 м. Київ</span></span></p>',NULL,NULL,1708,5,1,47);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2342,'P','2009-08-05',1,NULL,'Rss','Початок Успенського посту','<p>14 серпня 2009 року</p>\n<p>Коротка історія</p>\n<p>Християни перших віків до великих празників завжди приготовлялися постом і молитвою. З цієї священної практики з часом розвинулися коротші чи довші пости. На першому місці стоїть тут Великий Піст перед світлим празником Господньої Пасхи. Перед празником Христового Різдва ввійшов у звичай піст Пилипівки. З особливого культу до святих верховних апостолів Петра й Павла зродився піст Петрівки. А вкінці, прийшов наймолодший з чотирьох річних постів, піст Успенський. Ним ми приготовляємо себе до найбільшого празника Пресвятої Богородиці, її святого Успення.\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=397:pochatok-uspenskogo-postu&catid=30:hrystyjanske-vchennia-cat&Itemid=37',1709,NULL,1,26),
(2343,'P','2009-08-06',1,NULL,'Rss','«Маємо пам’ятати, що все йде поволі»','<p>Глава УГКЦ Блаженніший Любомир (Гузар) - це людина, яка наче знає майбутнє, дивиться в нього і з огляду на свою візію творить сьогодення. Блаженніший завжди відверто говорить про процеси, які відбуваються в Україні та світі, це надзвичайно розумна, мудра та водночас проста у поведінці та слові людина.</p>\n<p>Про погляд на важливість ролі священнослужителів у час духовної, перш за все, а не фінансової, кризи, аналіз політичної ситуації у країні, майбутній візит Патріарха всія Русі Кирила - в ексклюзивному інтерв\'ю ZAXID.NET із Главою УГКЦ Блаженнішим Любомиром (Гузаром).</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=398:lmajemo-pamjataty-shcho-vse-de-povolir&catid=27:gk-v-ukrajini-cat&Itemid=57',1710,NULL,1,26),
(2344,'P','2009-08-04',1,NULL,'Rss','СБУ збирається розсекретити нові докази репресій в СРСР проти УГКЦ ','<p>«Хтось міг би сказати, що знайдення документу (йдеться про лист Хрущова до Сталіна, про який нещодавно повідомила Австрійська католицька інформагенція, - <em>авт.</em>), який доводить причетність керівництва СРСР до знищення Української Греко-Католицької Церкви не є жодною сенсацією, а його публікація - взагалі зайва річ, адже усім і так було відомо, хто виніс смертний вирок УГКЦ, в яких кабінетах зароджувалися плани і координувалися дії, скеровані на її цілковите знищення. Справді, упродовж усього повоєнного часу, а в Україні - за роки незалежності, з\'явилося чимало книг і публікацій, які доводять однозначно: за проектом знищення УГКЦ стоїть сталінський режим і його вірні слуги», - сказав Владика Богдан (Дзюрах), Секретар Синоду Єпископів УГКЦ, коментуючи інформацію про лист Микити Хрущова до Йосипа Сталіна, в якому тодішній керівник компартії УРСР звітується про виконану роботу у справі ліквідації УГКЦ (текст документу можна прочитати <a href=\"http://www.oranta.org/fileadmin/user_upload/pdf/Khruschov_to_Stalin.pdf\">за цим посиланням</a>).</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=399:sbu-zbyrajetsja-rozsekretyty-novi-dokazy-represi-v-srsr-proty-ugkc-&catid=27:gk-v-ukrajini-cat&Itemid=57',1712,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2345,'N','2009-08-07',1,NULL,'Rss','Інтерв’ю з парохом греко – католицької церкви святого Миколая, що на Аскольдовій Могилі, отцем Ігорем Онишкевичем щодо літнього відпочинку дітей парохії у Карпатах','<p align=\"center\"><strong>Отче Ігорю, відомо, що парафіяльна рада, будуючи плани подорожі, старається щороку змінювати місце відпочинку. Чому Ви вирішили поїхати саме у село Заріччя Івано-Франківської області, що розташувалось неподалік славного Яремче?</strong><strong>&nbsp;</strong></p>\r\n<p>Ми щороку маємо такі подорожі та, дійсно, &nbsp;змінювати місце відпочинку. Це дуже корисно для дітей, адже вони, зростаючи з парафією, мають можливість бачити різні місця, монастирі, церкви. Ми довго шукали місце відпочинку: мали зв\'язки з приватними садибами, але Бог&nbsp; благословив нас на відпочинок у Заріччі.</p>','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=400:intervju-z-parohom-greko--katolyckoji-cerkvy-svjatogo-mykolaja-shcho-na-askoldovi-mogyli-otcem-igorem-onyshkevychem-shchodo-litnogo-vidpochynku-dite-parohiji-u-karpatah&catid=25:parafi',1713,NULL,1,26),
(2346,'P','2009-08-07',1,NULL,'Note','Виставка мистецьких робіт ув’язнених відкриється у Києві на Свято Преображення ','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Виставка, яка проходитиме під патронатом Блаженнішого Любомира, буде нагодою для завершення національного відбору робіт на Міжнародний конкурс образотворчого мистецтва ув\'язнених та засуджених у Берліні. Її тема &laquo;Очима інших&raquo;. У Свято Преображення 50 кращих творів з України буде відібрано мистецьким журі для участі у вересневому фіналі 2009 року.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Українська Греко-Католицька Церква, за сприянням <img style=\"float: right;\" src=\"image,3194,200,RTEmagicC_transfiguration_gif.gif\" alt=\"\" width=\"149\" height=\"200\" />Державної кримінально-виконавчої служби України, проводить виставку &laquo;Преображення Господньою любов&rsquo;ю&raquo;. Мета виставки &ndash; донести до суспільства особливе послання від наших співгромадян, що опинилися у місцях позбавлення волі, а також познайомити широкий загал із в&rsquo;язничним душпастирським служінням Церкви. Експозиція включатиме близько 300 кращих творів, переданих з пенітенціарних установ усіх областей України. </span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Виставка, яка проходитиме під патронатом Блаженнішого Любомира, буде нагодою для завершення національного відбору робіт на Міжнародний конкурс образотворчого мистецтва ув\'язнених та засуджених у Берліні. Її тема &laquo;Очима інших&raquo;. У Свято Преображення 50 кращих творів з України буде відібрано мистецьким журі для участі у вересневому фіналі 2009 року.</span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Відкриття експозиції &ndash; 19 серпня 2009 року о 10.00 у храмі Св. Василія Великого Української Греко-Католицької Церкви. Адреса: Вознесенський узвіз, 7 (Львівська площа).</span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Акредитація журналістів:</span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">тел./факс 279-19-20<br />моб. тел. +380984229221<br />e-mail: </span></span><a href=\"mailto:panteley@ukr.net\"><span style=\"text-decoration: underline;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">panteley@ukr.net</span></span></span></a></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Відповідальний - диякон Костянтин Пантелей,<br />референт УГКЦ з питань душпастирства у пенітенціарній системі України<br /><br /></span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Результати нещодавнього Віденського міжнародного конкурсу робіт ув&rsquo;язнених і засуджених &laquo;Знаки життя&raquo; (червень 2009), де було оголошено імена трьох українських фіналістів можна побачити за цим посиланням: &nbsp;</span></span><a href=\"http://www.iccppc.org/prisoners_art/2009_list_of_prize_winners.htm\" target=\"_blank\"><span style=\"text-decoration: underline;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">www.iccppc.org/prisoners_art/2009_list_of_prize_winners.htm</span></span></span></a><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">, а їхні твори: </span></span><a href=\"http://www.iccppc.org/ICCPPC_photo_library/2009_art_gallery/index.html\" target=\"_blank\"><span style=\"text-decoration: underline;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">www.iccppc.org/ICCPPC_photo_library/2009_art_gallery/index.html</span></span></span></a><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">.</span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">За матеріалами: Департаменту інформації УГКЦ</span></span></p>',NULL,NULL,1711,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2347,'P','2009-08-03',1,NULL,'Note','На Печерських пагорбах просили за навернення грішників, що палити хрест у меморіальному комплексі жертвам Голодомору','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Акт вандалізму відбувся під час візиту московського патріарха Кирила до України.&nbsp; &laquo;Парафіяни кажуть, що в такий спосіб тривають спроби мордувати жертви Голодомору. Протягом неділі після кожної літургії ми тричі молилися за навернення грішників&raquo;, - заявив&nbsp; священик церкви св. Миколая отець Анатолій.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: left;\" src=\"image,3199,200,28871.jpg\" alt=\"\" />Цієї неділі тричі молилися парафіяни церкви святого Миколая на Аскольдовій могилі за навернення грішників, що припалили розп`яття Подячного хреста у Калиновому гаю, який є частиною меморіального комплексу пам`яті жертв Голодомору.&nbsp; Повідомляє ІА&laquo;</span></span><a href=\"http://www.oranta.org/\"><span style=\"text-decoration: underline;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Оранта</span></span></span></a><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&raquo; </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Акт вандалізму відбувся під час візиту московського патріарха Кирила до України.&nbsp; &laquo;Парафіяни кажуть, що в такий спосіб тривають спроби мордувати жертви Голодомору. Протягом неділі після кожної літургії ми тричі молилися за навернення грішників&raquo;, - заявив&nbsp; священик церкви св. Миколая отець Анатолій.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&laquo;Спроби спалити того чи іншого хреста історичного комплексу Аскольдова могила здійснюються достатньо регулярно, але з того нічого не виходить. Жоден ще не згорів. Хрести, які перебувають під захистом серйознішим, ніж пожежники чи правоохоронні структури&raquo;, пояснив агенції автор пам`ятника Подячний хрест львівський архітектор Віталій Соболевський.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"FONT-FAMILY: arial,helvetica,sans-serif\"><span style=\"FONT-SIZE: small\">За матеріалами: <a href=\"http://kreschatic.kiev.ua/\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">http://kreschatic.kiev.ua/</span></span></a></span></span></p>',NULL,NULL,1716,5,1,27),
(2348,'N','2009-08-09',0,NULL,'Note','','','<p style=\"text-align: justify;\"><strong><img style=\"float: right;\" src=\"image,3201,200,087.jpg\" alt=\"\" width=\"200\" height=\"150\" />Отче Ігорю, відомо, що парафіяльна рада, будуючи плани подорожі, старається щороку змінювати місце відпочинку. Чому Ви вирішили поїхати саме у село Заріччя Івано-Франківської області, що розташувалось неподалік славного Яремче?</strong><strong>&nbsp;</strong></p>\r\n<p style=\"text-align: justify;\">Ми щороку маємо такі подорожі та, дійсно, &nbsp;змінювати місце відпочинку. Це дуже корисно для дітей, адже вони, зростаючи з парафією, мають можливість бачити різні місця, монастирі, церкви. Ми довго шукали місце відпочинку: мали зв\'язки з приватними садибами, але Бог&nbsp; благословив нас на відпочинок у Заріччі.</p>',NULL,NULL,1718,5,1,5);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2349,'P','2009-08-05',1,NULL,'Note','Українські переможці 2009 року на мистецькому конкурсі «Знаки життя» у Відні','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Імена трьох українців, були названі вперше поміж переможцями на 2-му Міжнародному конкурсі образотворчого мистецтва засуджених і ув\'язнених, що завершився цього тижня у Відні, Австрія. 1349 твори (28 з них &ndash; з України), авторів, що перебувають у місцях позбавлення волі, надійшли за посередництвом в&rsquo;язничного католицького душпастирства 44 країн світу з усіх континентів. Авторів 70 праць, обраних міжнародним журі, буде незабаром відмічено преміями.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: left;\" src=\"image,3200,200,winning_poster.jpg\" alt=\"\" width=\"127\" height=\"195\" />Імена трьох українців, були названі вперше поміж переможцями на 2-му Міжнародному конкурсі образотворчого мистецтва засуджених і ув\'язнених, що завершився цього тижня у Відні, Австрія. 1349 твори (28 з них &ndash; з України), авторів, що перебувають у місцях позбавлення волі, надійшли за посередництвом в&rsquo;язничного католицького душпастирства 44 країн світу з усіх континентів. Авторів 70 праць, обраних міжнародним журі, буде незабаром відмічено преміями.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: right;\" src=\"image,3200,200,Viden_1.jpg\" alt=\"\" width=\"149\" height=\"200\" />Роман Ралько &ndash; автор твору, що належить до десяти найкращих &ndash; виконав образ Христа складною технікою набивки шовковою ниткою. Степан Моцьо вирізьбив образ Богородиці &laquo;Неупиваєма Чаша&raquo;&ndash; другий фіналіст конкурсу.&nbsp; Акварельний пейзаж, що показує дорогу до храму, під назвою &laquo;На околиці села&raquo;, виконаний Віталієм Мальцевим, вихованцем Прилуцької колонії для неповнолітніх, отримав спеціальне визнання журі. Про це 29 липня 2009 року під час прес-конференції в УНІАН &laquo;Участь громадськості та представників релігійних конфесій у процесі виправлення і ресоціалізації засуджених, проведення культурно-масових та духовно-просвітницьких заходів&raquo; повідомив диякон Костянтин Пантелей.&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: left;\" src=\"image,3200,200,Viden_2.jpg\" alt=\"\" width=\"158\" height=\"200\" />&laquo;Пенітенціарне душпастирство Української Греко-Католицької Церкви, у співпраці з Міжнародною Католицькою Комісією В&rsquo;язничної Душпастирсської Опіки (ICCPPC) за сприянням соціально-виховної та соціально-психологічної служби Державного департаменту України з питань виконання покарань та Міністерства культури України, вперше прилучилася до організації Міжнародного конкурсу образотворчого мистецтва засуджених і ув\'язнених, започаткованого ICCPPC&raquo; - сказав диякон.&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: right;\" src=\"image,3200,200,Viden_3.jpg\" alt=\"\" width=\"156\" height=\"200\" />Ініціатором цієї справи, за благословенням Святішого отця Бенедикта XVI, від 2005 року став доктор Крістіан Кун &ndash; відомий правозахисник та нинішній президент ІССРРС. Понад двісті робіт Віденського конкурсу увійдуть до спеціального каталогу що готує Комісія. Протягом кількох наступних років праці фіналістів конкурсу будуть експонуватися у багатьох країнах світу, також в Україні. 2010 року виставка буде представлена у Бразилії з нагоди відкриття 12 &ndash;го Конгресу ООН з питань запобігання злочинності та кримінального правосуддя.&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">19 серпня у греко-католицькому храмі Св. Василія Великого у Києві буде відкрито Виставку робіт засуджених &laquo;Переображення Господньою Любов&rsquo;ю&raquo;. Мистецтво &ndash; є своєрідним способом важливого повідомлення, що в&rsquo;язні бажають висловити модерному світу.&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Докладніше див.: </span></span><a href=\"http://www.unian.net/press/?lang=1&amp;url=press_news&amp;id=106095\"><span style=\"text-decoration: underline;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">http://www.unian.net/press/?lang=1&amp;url=press_news&amp;id=106095</span></span></span></a></p>',NULL,NULL,1717,5,1,30);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2350,'P','2009-08-07',1,NULL,'Rss','Інтерв’ю з парохом греко – католицької церкви святого Миколая, що на Аскольдовій Могилі, отцем Ігорем Онишкевичем щодо літнього відпочинку дітей парохії у Карпатах','<p><strong><img width=\"200\" src=\"http://askoldova-mohyla.org/uk/image,3201,200,087.jpg\" height=\"150\" style=\"float: right; margin: 10px;\" />Отче Ігорю, відомо, що парафіяльна рада, будуючи плани подорожі, старається щороку змінювати місце відпочинку. Чому Ви вирішили поїхати саме у село Заріччя Івано-Франківської області, що розташувалось неподалік славного Яремче? </strong></p>\n<p>Ми щороку маємо такі подорожі та, дійсно,  змінювати місце відпочинку. Це дуже корисно для дітей, адже вони, зростаючи з парафією, мають можливість бачити різні місця, монастирі, церкви. Ми довго шукали місце відпочинку: мали зв\'язки з приватними садибами, але Бог  благословив нас на відпочинок у Заріччі.\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=400:intervju-z-parohom-greko-katolyckoji-cerkvy-svjatogo-mykolaja-shcho-na-askoldovi-mogyli-otcem-igorem-onyshkevychem-shchodo-litnogo-vidpochynku-dite-parohiji-u-karpatah&catid=25:parafij',1719,NULL,1,26),
(2351,'P','2009-08-09',1,NULL,'Rss','Цікаві миті життя дітей парафії святого Миколая у літньому таборі відпочинку в українських Карпатах','<p><strong><em>26 липня</em></strong></p>\n<p> </p>\n<p><img src=\"http://askoldova-mohyla.org/uk/image,3204,200,083.jpg\" style=\"float: right; margin: 10px;\" />Цього дня Cлужбу Божу служив  отець Мирослав Ханенко, якого досі ми не знали, але Богослужіння нам сподобалось. Перший день не був дуже насичений, адже ми тільки освоювалися. І виникло бажання пограти у різні ігри, але спортивного інвентаря ще не було, адже отець Ігор повинен був прибути мікроавтобусом наступного дня разом зі спортивним знаряддям.</p>\n<p>Цього дня нас розподілили по кімнатах і куренях. Були три курені. Перший курінь відправився на катехизацію до вихователя  - пані Орести Цембрівської,  другий - до Ганни Шастак, а третій - пішов вправлятись мистецтвом співу до пані Надії Кучеревої. Цього дня ми готували наш концерт для отця Ігорю, адже у нього ювілей - 50 років від дня народження. \n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=401:cikavi-myti-zhyttja-dite-parafiji-svjatogo-mykolaja-u-litnomu-tabori-vidpochynku-v-ukrajinskyh-karpatah&catid=25:parafija-cat&Itemid=27',1720,NULL,1,26),
(2352,'N','2009-08-09',0,NULL,'Note','','','<p><img style=\"float: left;\" src=\"image,3204,200,09089.jpg\" alt=\"\" width=\"200\" height=\"150\" />На другий день нашого відпочинку повинен приїхати отець Ігор. Зранку ми пішли оглядати річку: місця де можна&nbsp; купатися. На природі провели останню репетицію: дехто готував побажання отцю Ігорю, співали &laquo; Многая літа&raquo;, розказували вірші. Повернувшись до дому, побачили отця Ігоря, який щирою усмішкою зустрічав нас. Розпочався невеликий концерт. Отець-парох був приємно здивований, він роздав кожній дитині декілька цукерок. Всі були задоволені.&nbsp;</p>\r\n<p>З нами була сестра Анастасія, але на другий день вона змушена була покидати табір та їхати у запланованих справах. Ми провели її, побажали щасливої дороги. Після тихого часу, який тривав дві&nbsp; години, пішли на річку. Вода була холодна, аж перехоплювало подих, тим не менше, всі діти попробували на дотик воду.&nbsp;</p>\r\n<p>Після вечері - вервечка. Потім сон.&nbsp;</p>\r\n<p><img style=\"float: right;\" src=\"image,3204,200,09085.jpg\" alt=\"\" />Сьогодні від\'їжджаємо. Було сумно. Після сніданку, діти почали складати речі. Сьогодні останній раз ми пішли на річку. Вода була холодна. &nbsp;&nbsp;&nbsp; Олег Качур та Андрій Масягін згуртували дітей і запалили останню ватру біля річки. Ми прийшли до дому. О четвертій годині приїхав автобус. Коли ми прибули до Івано - Франківська, для нас була запланована екскурсія, яку провів пан Ярослав. Було дуже цікаво, адже ми мали змогу прогулюватися містом, і водночас слухати цікаві факти. Діти дізналися чому місто Станіслав перейменували в Івано -&nbsp; Франківськ.&nbsp;</p>',NULL,NULL,1722,5,1,13);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2353,'P','2009-08-09',1,NULL,'Rss','Любов, як вітер – її не видно, але ти її відчуваєш…','<p>З 26 липня по 6 серпня 25 дітей та 5 вихователів з парафії святого Миколая, що на Аскольдовій могилі, відпочивали у селі Заріччя Івано - Франківської області.  Під час цього відпочинку діти побували на двох екскурсіях: по Карпатському краю та по місту Івано - Франківську. Діти мали змогу ходити у гори та купатися в гірській річці. </p>\n<p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=402:ljubov-jak-viter--jiji-ne-vydno-ale-ty-jiji-vidchuvajesh&catid=25:parafija-cat&Itemid=27',1723,NULL,1,26),
(2354,'P','2009-08-08',1,NULL,'Rss','Католицький Народний Катехизм','<p><strong>Віра</strong></p>\n<p><strong>ОБІТНИЦЯ СПАСИТЕЛЯ</strong></p>\n<p>Бог пробачив гріхопадіння людині, але не пробачив ангелам, бо гріх людей не був таким важким, як гріх ангелів. Люди не мали такого розуму, як ангели, і не згрішили з власної волі, а їх спокусив диявол. Врешті, люди частково визнали свою вину і шкодували, що завинили. Людство становить одну родину бо походять від одних прародичів. Вина одного (Адама) зашкодила всім, тому спасіння одного (Ісуса Христа) повинно було допомогти і дійсно допомогло всім. Бог не хотів через провину одного зробити нещасним увесь людський рід. \n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=403:katolycky-narodny-katehyzm&catid=30:hrystyjanske-vchennia-cat&Itemid=37',1724,NULL,1,26),
(2355,'P','2009-08-12',1,NULL,'Note','Всесвітній відгук на енцикліку Папи Венедикта ХVІ “Caritas in veritate”','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Протягом місяця в ЗМІ з&rsquo;явилось понад 4300 аналітичних статей та коментарів до енцикліки &ldquo;Caritas in veritate&rdquo; англійською, французькою, іспанською, італійською та португальською мовами. Загальна кількість коментарів значно більша, адже не враховані статті слов&rsquo;янськими мовами, ані місцевими мовами країн Азії та Африки, де енцикліка викликала великий резонанс.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Проминув місяць від оприлюднення Вселенським Архиєреєм Венедиктом ХVІ енцикліки &ldquo;Caritas in veritate&rdquo;, яка викликала широкий відгук на світовому рівні. Незаважаючи на те, що повний текст енцикліки основними європейськими мовами доступний в мережі Інтернет на офіційному сайті Ватикану, її друковане видання стало бестселером у багатьох країнах, очоливши список книг, які користуються найбільшим попитом. <br /><br />Протягом місяця в ЗМІ з&rsquo;явилось понад 4300 аналітичних статей та коментарів до енцикліки &ldquo;Caritas in veritate&rdquo; англійською, французькою, іспанською, італійською та португальською мовами. Загальна кількість коментарів значно більша, адже не враховані статті слов&rsquo;янськими мовами, ані місцевими мовами країн Азії та Африки, де енцикліка викликала великий резонанс.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">За матеріалами: </span></span><a href=\"http://www.radiovaticana.org/\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">http://www.radiovaticana.org/</span></span></a></p>',NULL,NULL,1727,5,1,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2356,'P','2009-08-13',1,NULL,'Note','Уряд України спростив візовий режим для католицьких священиків та монахів з-за кордону','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">На прохання Голови Конференції Римо-Католицьких Єпископів України архиєпископа Львівського Мечислава Мокрицького, який 29 липня ініціював офіційну зустріч з прем&rsquo;єр-міністром України панею Юлією Тимошенко, Кабінет Міністрів України прийняв рішення, на підставі якого духовні особи з-за кордону, що отримали багаторазову візу для служіння в Україні, зможуть тепер залишатися в країні не 180 днів, а протягом усього року.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">На прохання Голови Конференції Римо-Католицьких Єпископів України архиєпископа Львівського Мечислава Мокрицького, який 29 липня ініціював офіційну зустріч з прем&rsquo;єр-міністром України панею Юлією Тимошенко, Кабінет Міністрів України прийняв рішення, на підставі якого духовні особи з-за кордону, що отримали багаторазову візу для служіння в Україні, зможуть тепер залишатися в країні не 180 днів, а протягом усього року. Передбачено також видачу віз терміном до трьох років за погодженням із Державним комітетом у справах національностей та релігій. Про це повідомила прес-служба Державного комітету у справах національностей та релігій України. <br /><br />(для Радіо Ватикану, Костянтин Чавага)<br />За матеріалами: </span></span><a href=\"http://www.radiovaticana.org/\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">http://www.radiovaticana.org/</span></span></span></a></p>',NULL,NULL,1729,5,1,42),
(2357,'N','2009-08-13',0,NULL,'Note','','','<p>Парафіяльна служба милосердя &laquo;Карітас-Печерськ&raquo; церкви св. Миколая на Аскольдовій могилі відправила 76 посилок допомоги потребуючим. А саме: дітям, яких залишила мама, що відбуває покарання у тюрмі, потерпілим від повені родинам у с. Воскресенці Коломийського району Івано-франківської області.</p>\r\n<p><img style=\"float: left;\" src=\"image,3213,200,posylky.jpg\" alt=\"\" width=\"200\" height=\"149\" />&laquo;Також ми не забули про матерів, що відбувають покарання у в`язниці. Цю працю провадять волонтери пані Марта Бліхар, Марьяна Черешнівська та інші. Запрошуємо волонтерів для цієї місії милосердного служіння і молитви за потребуючих. Особливо потрібен юрист, який би міг допомогти матерям, що ув`язнені, щоб відновити їхні права на залишених дітей&raquo;, прокоментував подію для ІА &laquo;Оранта&raquo; настоятель церкви от. Ігор Онишкевич.</p>\r\n<p>ІА &laquo;Оранта&raquo;</p>\r\n<p>&nbsp;</p>\r\n<p>На фото: настоятель церкви св. Миколая на Аскольдовій могилі Ігор Онишкевич та волонтер проекту Марта Бліхар відправляють оформляють посилки у поштовому відділенні на Печерську</p>',NULL,NULL,1730,5,1,5),
(2358,'P','2009-08-13',1,NULL,'Rss','«Карітас-Печерськ» відправив 76 посилок допомоги потребуючим','<p><img src=\"http://www.oranta.org/images/stories/posylky.jpg\" style=\"margin: 10px; float: left; border: #000000 1px solid;\" />Парафіяльна служба милосердя «Карітас-Печерськ» церкви св. Миколая на Аскольдовій могилі відправила 76 посилок допомоги потребуючим. А саме: дітям, яких залишила мама, що відбуває покарання у тюрмі, потерпілим від повені родинам у с. Воскресенці Коломийського району Івано-франківської області.</p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=405:lkaritas-pecherskr-vidpravyv-76-posylok-dopomogy-potrebujuchym&catid=25:parafija-cat&Itemid=27',1731,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2359,'P','2009-08-09',1,NULL,'Rss','Любов, як вітер – її не видно, але ти її відчуваєш…','<p>З 26 липня по 6 серпня 25 дітей та 5 вихователів з парафії святого Миколая, що на Аскольдовій могилі, відпочивали у селі Заріччя Івано - Франківської області.  Під час цього відпочинку діти побували на двох екскурсіях: по Карпатському краю та по місту Івано - Франківську. Діти мали змогу ходити у гори та купатися в гірській річці. </p>\n<p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=402:ljubov-jak-viter-jiji-ne-vydno-ale-ty-jiji-vidchuvajesh&catid=25:parafija-cat&Itemid=27',1733,NULL,1,26),
(2360,'P','2009-08-17',0,NULL,'Note','Євангелізація','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Графік проведення євангелізації при парохіях</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">21 серпня - Аскольдова Могила</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">8:30 - Літургія</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Вулична євангелізація</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">16:00 - Праця з парафіяльною молоддю, дітьми та старшими - проповідь, молитва, ігри</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">18:00 - Літургія, опісля - євангелізаційна молитва</span></span></p>','<p><img src=\"image,3219,500,vazhlyvo!!!-1.jpg\" alt=\"\" width=\"372\" height=\"500\" /><img src=\"image,3219,500,vazhlyvo2.jpg\" alt=\"\" width=\"445\" height=\"500\" /></p>',NULL,NULL,1736,5,1,41),
(2361,'P','2009-08-17',1,NULL,'Rss','Святе Преображення Господа Бога і Спаса нашого Ісуса Христа ','<a id=\"c1344\"></a>\n<p><strong>Коротка історія</strong></p>\n<p align=\"justify\"><img src=\"http://www.oranta.org/images/stories/256b6e094d.jpg\" style=\"margin: 10px; float: right; border: #000000 1px solid;\" />Святкування цього празника сягає IV ст. В цьому часі св. Єлена, мати цісаря Костянтина Великого, збудувала храм на горі Тавор у честь Господнього Преображення. Від VI ст. цей празник урочисто святкується у Східній Церкві під назвою „Господнього Преображення\". В Західній Сирії у VIII столітті він називався „Празником Тавору\".</p>\n<p align=\"justify\">Первісно празник Преображення святковано в місяці лютому. Одначе тому, що цей радісний празник звичайно випадав у часі Великого Посту, а це було незгідне з духом посту й покути, то його перенесено на день 6 серпня. Чому якраз на цей день? Історик Євсевій і св. Іван Дамаскин є тієї думки, що Господнє Преображення відбулося 40 днів перед Христовою смертю. Тож св. Церква йдучи за цією думкою, перенесла цей празник з місяця лютого на 6 серпня тому, бо 40 днів пізніше, це є 14 вересня, випадає празник Воздвиження Чесного Хреста - пам\'ять Христових мук і смерті.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=407:svjate-preobrazhennja-gospoda-boga-i-spasa-nashogo-isusa-hrysta-&catid=30:hrystyjanske-vchennia-cat&Itemid=37',1738,NULL,1,26),
(2362,'P','2009-08-18',1,NULL,'Rss','Глава УГКЦ візьме участь у відкритті виставки образотворчого та декоративно-прикладного мистецтва в’язнів','<p>19 серпня 2009 року о 10.00 у виставковому залі храму Св. Василія Великого Української Греко-Католицької Церкви <em>(Київ, Вознесенський узвіз, 7)</em>, відбудеться відкриття експозиції образотворчого та декоративно-прикладного мистецтва засуджених, які відбувають покарання в установах позбавлення волі.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=408:glava-ugkc-vizme-uchast-u-vidkrytti-vystavky-obrazotvorchogo-ta-dekoratyvno-prykladnogo-mystectva-vjazniv&catid=27:gk-v-ukrajini-cat&Itemid=57',1739,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2363,'P','2009-08-16',1,NULL,'Note','Пречиста Діва Марія – це Сонце, яке зійшло над темрявою історії ','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&laquo;У Божому плані сотворення, &ndash; зазначив він, &ndash; людина повинна була мати чистоту та красу Непорочно Зачатої. Цей задум був порушений, але не знищений гріхом, і був оновлений і наново представлений людині, щоб вона його вільно прийняла у вірі через Втілення Божого Сина, звіщене та здійснене у Пресвятій Діві Марії&raquo;.</span></span></p>','<p style=\"text-align: justify; font: 12px/18px Verdana, Arial, Helvetica, sans-serif; color: #153f6b;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Вже стало традицією, що в празник Внебовзяття Пречистої Діви Марії, один з найбільш святкованих Богородичних празників у Церкві латинського обряду, парафіяльний храм містечка Кастель Ґандольфо, посвячений на честь святого Томи з Вілланови, приймає Вселенського Архиєрея. Перебуваючи у серпні у своїй літній резиденції саме в цій місцевості, Святіший Отець відправляє там святкове Євхаристійне богослуження. Не було винятком і 15 серпня 2009 року.<br /><br />У проповіді під час Служби Божої Папа Венедикт&nbsp;XVI пригадав життєвий шлях Пресвятої Богородиці, починаючи від Її Непорочного Зачаття. &laquo;У Божому плані сотворення, &ndash; зазначив він, &ndash; людина повинна була мати чистоту та красу Непорочно Зачатої. Цей задум був порушений, але не знищений гріхом, і був оновлений і наново представлений людині, щоб вона його вільно прийняла у вірі через Втілення Божого Сина, звіщене та здійснене у Пресвятій Діві Марії&raquo;.<br /><br />Коментуючи євангельський уривок, який переказує нам пісню подяки Пресвятої Богородиці &laquo;Величай, душе моя, Господа&raquo;, Папа пригадав, як відразу після Благовіщення Пречиста Діва Марія поспішила до своєї родички Єлисавети, зазначаючи, що для Неї іти за своїм покликанням означало вийти на новий шлях, поза своїм домом, віддаючись лише Божому проводові, який діяв у Ній через Святого Духа, здійснивши втілення Слова. <br /><br />&laquo;Святий Амброзій, &ndash; зазначив Венедикт&nbsp;XVI, &ndash; коментуючи поспіх Пресвятої Діви Марії, стверджує: &ldquo;Божа благодать не вносить сповільнення&rdquo;. Життя Пресвятої Богородиці кероване Кимось Іншим, (тобто Богом). Вона мовила: &ldquo;Ось я слугиня Господня, нехай зі мною станеться за твоїм словом&rdquo;. Отже, Її життя формується Святим Духом, воно позначене подіями і зустрічами, як згадана з Єлисаветою, але, передовсім, надзвичайно особливими стосунками з Її Сином Ісусом. Це шлях, на якому Марія, зберігаючи і роздумуючи у серці події Свого життя, дедалі глибше розрізняла в них таємничий план Бога Отця для спасіння світу&raquo;. Йдучи за Ісусом, починаючи від Вифлиєму, під час Його прихованого життя і прилюдної діяльності, аж до підніжжя хреста, Пресвята Богородиця постійно, навіть у найтемніших хвилинах чи в стражданнях, духовно підіймалась до Бога, повністю приймаючи Його задумлюбові, віддаючись у Його руки, через що Вона є для Церкви прикладом віри. &laquo;Внебовзяття, &ndash; підкреслив Венедикт&nbsp;XVI, &ndash; пригадує нам, що життя Пресвятої Марії, як і кожного християнина, є шляхом наслідування Христа&raquo;, який обов&rsquo;язково проходить через хрест, але має ясно окреслену мету &ndash; остаточну перемогу над гріхом і смертю та повну спільність з Богом. І саме у Внебовзятій Пречистій Діві можемо споглядати звершення Її віри, Її шляху, який Вона вказує для Церкви і для кожного з нас.<br />Коментуючи читання з книги Одкровення, Папа звернув увагу на те, що життя людини на землі є шляхом, який постійно проходить через боротьбу між добром і злом. &laquo;Таким є стан історії людства: вона є немов подорож часто бурхливим морем; Пречиста Діва Марія є зорею, яка веде нас до Свого Сина Ісуса, Сонця, що зійшло над темрявою історії і дарує нам надію, якої потребуємо: надію на те, що ми можемо перемогти, що Бог переміг і що через Хрищення ми увійшли у цю перемогу&raquo;, &ndash; сказав Святіший Отець, а свою проповідь закінчив молитвою святого Бернарда, прохаючи у Пресвятої Богородиці, щоб Вона, завдяки отриманим благодатям, допомагала усім нам стати учасниками блаженства і вічної слави у небі.<br />За матеріалами: </span></span><a href=\"http://www.radiovaticana.org/\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">http://www.radiovaticana.org/</span></span></a></p>',NULL,NULL,1735,5,1,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2364,'P','2009-09-12',1,NULL,'Note','Меджугорський день молитви у Зарваниці','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">2 &ndash; 3 жовтня 2009 р. б уде проходити Меджугорський день молитви у Зарваниці. На днях молитви буде присутня сестра Тереза Гажіова зі спільноти &ldquo; Opus Maria &rdquo;. Координатором заходу в Україні є отець Петро Кобаль тел. 8097 248 53 20. </span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: left;\" src=\"image,3220,200,zarvanica-plakat-4.jpg\" alt=\"\" width=\"141\" height=\"200\" />2 &ndash; 3 жовтня 2009 р. б уде проходити Меджугорський день молитви у Зарваниці. На днях молитви буде присутня сестра Тереза Гажіова зі спільноти &ldquo; Opus Maria &rdquo;.Координатором заходу в Україні є отець Петро Кобаль тел. 8097 248 53 20. З усіх організаційних питань прохання звертатися за телефонами:</span></span><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Київ &ndash; Олег Шептицький тел. 8050 545 85 80</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Ужгород &ndash; Мирослав Дуда тел. 8050 502 64 14</span></span><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Тернопіль &ndash; Степан Хміль тел. 8095 846 90 93</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Бажаючим взяти участь прохання зорганізуватися на парафіях і старшим груп просимо заздалегідь зробити заявку, повідомити про очікувану кількість паломників, щоб по можливості домовитися про ночівлю та харчування. Індивідуальним учасникам прохання теж про себе повідомляти.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Інформацію про Меджугор&rsquo; є можна отримати на сайті</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&lt;http://www.medjugorje.ru/&gt; www.medjugorje.ru</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">За численними побажаннями формується група на відпочинок в часі бархатного сезону в Чорногорію з відвіданням визначних християнських святинь. З 25 вересня по 6 жовтня. Орієнтовна вартість 250 у.о. євро</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Паломництва на Святу Землю всього за 998 у.о. $ увесь рік</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Найближчі дати вильотів: 15 вересня 2009 р., 25 жовтня 2009 р., 29 листопада &nbsp;2009 р., 13 грудня 2009 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Приймаються групові заявки, запрошуємо до співпраці релігійні громади, паломницькі центри, та регіональних представників.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Нагадуємо, що хто із вас хотів би відвідати Рим на римо-католицьке Різдво прохання зголоситися? У нас є можливість замовити Запрошення на Святкову Службу Божу в Соборі св. Петра і Павла яку буде проводити Папа Бенедиктом XVI. Пожертва на участь в паломництві буде складати приблизно 400 євро.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">З графіком усіх паломництв та розписаними програмами ви можете ознайомитися на сайті &lt;http://www.yednist.ucoz.com/&gt; www. yednist.ucoz.com або ми вам надішлемо. Деталі за тел. 8050 545 85 80</span></span></p>',NULL,NULL,1737,5,1,47);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2365,'P','2009-08-17',1,NULL,'Note','Закон мігрантів: Щоб в Україну повернулися мільйони працелюбних громадян, слід готувати до цього їхніх родичів, сусідів і владу','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Понад рік в Україні діє проект допомоги заробітчанам, що повертаються додому. Створена мережа з 30 громадських організацій, що покриває 16 областей України. Це і юридична консультація, і психологічний супровід у подоланні конфліктних ситуацій у сім&rsquo;ях. Провідна організація мережі &mdash; міжнародний благодійний фонд &laquo;Карітас України&raquo;. Серед співорганізаторів &mdash; Комісія Української греко&ndash;католицької церкви у справах мігрантів. Це наймолодша комісія УГКЦ, створена в жовтні 2007 року, утім, дієва і творча. ЇЇ центральний офіс &mdash; у Львові, наразі формуються місцеві осередки. Під опікою комісії є українські трудові емігранти та їхні родини (незважаючи на конфесійну належність), а також біженці, що знаходять притулок у нашій країні.</span></span></p>',NULL,NULL,NULL,1740,5,1,27),
(2366,'N','2009-08-21',0,NULL,'Note','','','<p><img style=\"float: right;\" src=\"image,3225,200,3.jpg\" alt=\"\" />Свято Преображення Господа нашого Ісуса Христа, зібрало багато парафіян і гостей міста Києва на Утреню і Божественну Літургію. Парафіяни принесли в кошиках яблука і багато дарів саду і землі нашої, як подяку Господу за гарний урожай.</p>\r\n<p><img style=\"float: left;\" src=\"image,3225,200,1.jpg\" alt=\"\" />Усі ці дари освятили священики храму. Особливою потіхою було частування всіх парафіян херсонськими динями і кавунами. Збирався також кошик яблук для Валерія Марченка. Це вже за традицією і по розповідях мами Валерія яка колись давно не могла передати своєму сину спасівське яблуко, коли він перебував у тюрмі. Діти, якими опікується парафіяльна Служба Милосердя ( Карітас-Печерськ), особливо смакували виноградом і грушами а пані Ореста розповідала їм про свято.</p>\r\n<p>Освяченою водою священики окропили первоплоди і всіх парафіян. Щоб були здорові на Многая і Благая Літа!</p>\r\n<p>Матеріал: Іллі, фото: Віталіни Онишкевич &laquo;Оранта&raquo;</p>',NULL,NULL,1742,5,1,5),
(2367,'P','2009-08-24',1,NULL,'Rss','Привітання Папи Венедикта XVI з нагоди Дня Незалежності України, надіслане на ім’я Президента України Віктора Ющенка','<p>Його Висоповажності <br />Панові Віктору Ющенку<br />Президентові України</p>\n<p>Національне свято України дарує мені нагоду звернутися до Вашої Високоповажності з побажаннями миру та щастя, які я виражаю Вам та усім Вашим співвітчизникам.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=410:pryvitannja-papy-venedykta-xvi-z-nagody-dnja-nezalezhnosti-ukrajiny-nadislane-na-imja-prezydenta-ukrajiny-viktora-jushchenka&catid=32:gk-svit-cat&Itemid=58',1743,NULL,1,26),
(2368,'P','2009-08-23',1,NULL,'Rss','Президент України з нагоди Дня Незалежності нагородив представників УГКЦ ','<p>Президент України Віктор Ющенко підписав указ № 622/2009 «Про відзначення державними нагородами України релігійних діячів». Цим документом «за вагомий особистий внесок у розвиток духовності в Україні, багаторічну плідну церковну діяльність та з нагоди 18-ї річниці Незалежності України» зокрема було нагороджено декілька представників духовенства Української Греко-Католицької Церкви.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=411:prezydent-ukrajiny-z-nagody-dnja-nezalezhnosti-nagorodyv-predstavnykiv-ugkc-&catid=27:gk-v-ukrajini-cat&Itemid=57',1745,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2369,'P','2009-08-23',1,NULL,'Note','Іти за Ісусом вимагає відречень','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&laquo;Бажаєте і ви відійти?&raquo; &ndash; запитав Христос дванадцятьох Апостолів. &laquo;Цей занепокоюючий виклик, &ndash; мовив Венедикт&nbsp;XVI, &ndash; відгукується у наших серцях і очікує від кожного особистої відповіді&raquo;. Ісусові не вистачає поверхневої приналежності, Він очікує, що все наше життя проходитиме згідно з Його словом та Його бажанням.</span></span></p>','<p style=\"text-align: justify; color: #153f6b;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Слідування за Євангелієм &laquo;включає труднощі та відречення тому, що часто потрібно іти проти течії&raquo;, &ndash; сказав Папа Венедикт&nbsp;XVI, промовляючи до кількох тисяч паломників, які опівдні у неділю 23 серпня 2009&nbsp;р. зібралися на внутрішньому подвір&rsquo;ї літньої резиденції Вселенських Архиєреїв у Кастель Ґандольфо, щоб помолитися молитву &laquo;Ангел Господній&raquo;.<br /><br />У своєму повчанні Святіший Отець коментував євангельський уривок, який на цей день пропонує літургія Римо-Католицької Церкви і який розповідає про те, що після того, як Ісус об&rsquo;явив Себе &laquo;хлібом життя&raquo;, споживаючи який людина увійде у вічне життя, багато слухачів говорили між собою: &laquo;Хто може це слухати?&raquo; &ndash; і відійшли. &laquo;Також і сьогодні, &ndash; зазначив Папа, &ndash; чимало людей залишаються згіршеними парадоксом християнської віри. Ісусове навчання їм здається &ldquo;твердим&rdquo;, надто важким для прийняття і впровадження у життя&raquo;. Є такі, що відкидають та залишають Христа, інші намагаються пристосувати Боже слово до модних течій, позбавляючи його істинного сенсу.<br /><br />&laquo;Бажаєте і ви відійти?&raquo; &ndash; запитав Христос дванадцятьох Апостолів. &laquo;Цей занепокоюючий виклик, &ndash; мовив Венедикт&nbsp;XVI, &ndash; відгукується у наших серцях і очікує від кожного особистої відповіді&raquo;. Ісусові не вистачає поверхневої приналежності, Він очікує, що все наше життя проходитиме згідно з Його словом та Його бажанням. Одночасно, слідування за Христом наповняє серця радістю і дає повний сенс нашому існуванню. Апостол Петро, а з ним &ndash; уся Церква, відповіли: &laquo;Господи, куди підемо? У Тебе &ndash; слова життя вічного і ми увірували та пізнали, що Ти &ndash; Святий Божий&raquo;. &laquo;Дорогі брати й сестри, &ndash; сказав на завершення Папа, &ndash; також і ми можемо і бажаємо повторити у цю хвилину відповідь святого Петра, очевидно, усвідомлюючи нашу людську слабкість, наші проблеми і труднощі, але довіряючи силі Святого Духа, яка виражається і виявляється у спільності з Ісусом&raquo;. Венедикт&nbsp;XVI зазначив, що коли з довір&rsquo;ям відкриємо серця Христові, зможемо, за прикладом Пречистої Діви Марії, досвідчити, що єдиним щастям на землі є любити Бога і знати, що Він нас любить.</span></span></p>\r\n<p style=\"text-align: justify; font: 12px/18px Verdana, Arial, Helvetica, sans-serif; color: #153f6b;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">За матеріалами: </span></span><a href=\"http://www.radiovaticana.org/\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">http://www.radiovaticana.org/</span></span></a></p>',NULL,NULL,1744,5,1,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2370,'P','2009-08-24',1,NULL,'Rss','Слово від Редакції','<p><img src=\"http://www.oranta.org/images/stories/2008/10/image13711dsc_0018.jpg\" style=\"margin: 10px; float: left; border: #000000 1px solid;\" />Слава Ісусу Христу!</p>\n<p>Дорогі в Христі, парафіяни, читачі нашої газети «Оранта».</p>\n<p>Час Святого Преображення звершився.</p>\n<p>Пам\'ятаймо слова Господа «Це Син мій улюблений, що Його Я вподобав. Його слухайтеся» (Матей 17.5)</p>\n<p>Ці слова почули небагато вірних, які 19 серпня 1991 року прийшли молитися у Іонійський скит, Свято-Троїцького Храму у ботанічному саду, який ми освятили молитвою і вірою , але змушені були покинути через напад і насилля наших братів з Лаври.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=413:slovo-vid-redakciji&catid=31:kolonka-redaktora-cat&Itemid=1',1746,NULL,1,26),
(2371,'P','2009-08-21',1,NULL,'Rss','Католицький Народний Катехизм','<p><strong>Віра</strong></p>\n<p><strong>ПРОРОЦТВА ПРО ПРИХІД МЕСІЇ</strong></p>\n<p>Месія народиться у Вифлеємі.</p>\n<p>Пророк Міхея (біля 700 р. перед X р.) каже: \"Ти ж, Вифлеєме-Єфрато <sup>1</sup>), занадто малий єси, щоб бути між тисячами Юди. З тебе вийде мені той, хто має бути Володарем в Ізраїлі; його походження із давніх-давен, з днів споконвічних\".</p>\n<p>Месія прийде до зруйнування святині.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=415:katolycky-narodny-katehyzm&catid=30:hrystyjanske-vchennia-cat&Itemid=37',1748,NULL,1,26),
(2372,'P','2009-08-21',1,NULL,'Rss','Пригоди італійського священика на Донбасі','<p>У міста Димитрово Донецької області два дні перебував священик із Флоренції Володимир Волошин, якого запросив о. Іван Василенко, парох греко-католицької церкви святих верховних апостолів Петра і Павла.</p>\n<p>«Протягом багатьох днів я буду осмислювати побачене. Взагалі, дуже боявся їхати на Донеччину, бо чутки ходили не дуже добрі, а виявилося навпаки», повідомив священик з Італії ІА «Оранта»</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=417:prygody-italiskogo-svjashchenyka-na-donbasi&catid=27:gk-v-ukrajini-cat&Itemid=57',1750,NULL,1,26),
(2373,'P','2009-08-24',1,NULL,'Rss','Пам\'ятай свій Рід','<p>Дорогі парафіяни, <img height=\"144\" width=\"200\" src=\"http://www.oranta.org/image,3235,200,6.jpg\" style=\"margin: 10px; float: right;\" />прошу Вас пам\'ятати історію свої родинних місць, де народились наші діди і прадіди, звідкіля починається ваш рід, батьківщину звідки Господь покликав нас до життя. Збирайте і поповнюйте своє родинне древо. Особливо, прошу дітей, щоб допитувались бабів і дідів, хто ми і чийого роду. Нотуйте все у щоденники, бо ці крупиці - це велика цінність і пам\'ять наша. Також прошу парафіян згадуйте своїх священиків, які відали своє життя за віру, не зрадили Христа і свою паству. Згадуйте тих, що у далеких Сибірах гинули, але не зраджували Христову віру і нашу Церкву.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=418:pamjata-svi-rid-&catid=28:tema-nomera-cat&Itemid=2',1751,NULL,1,26),
(2374,'N','2009-08-25',0,NULL,'Note','','','<p>Дорогі парафіяни, <img style=\"float: right;\" src=\"image,3235,200,6.jpg\" alt=\"\" width=\"200\" height=\"144\" />прошу Вас пам\'ятати історію свої родинних місць, де народились наші діди і прадіди, звідкіля починається ваш рід, батьківщину звідки Господь покликав нас до життя. Збирайте і поповнюйте своє родинне древо. Особливо, прошу дітей, щоб допитувались бабів і дідів, хто ми і чийого роду. Нотуйте все у щоденники, бо ці крупиці - це велика цінність і пам\'ять наша. Також прошу парафіян згадуйте своїх священиків, які відали своє життя за віру, не зрадили Христа і свою паству. Згадуйте тих, що у далеких Сибірах гинули, але не зраджували Христову віру і нашу Церкву.</p>\r\n<p>На поминальних Божественних Літургіях ми маємо чути імена тих, хто вірою і правдою служив Богові і Україні. Сьогодні подаю до вашої уваги шиматизм Угнівського деканату де згадується моє рідне село Журавці, саме там могили моїх предків, Церква Воздвиження Чесного Христа де мій тато Адам приймав з рук Блаженного Йосафата Кациловського, перше святе причастя. Дякую диякону Олександру Соколовському який відшукав ці матеріали в архівах і подав нам до користування.&nbsp;</p>\r\n<p>Прошу Вас, дорогі парафіяни, подавайте матеріали нашої пам\'яті, свідчення про односельців, священиків які вірою служили Богові і Україні.&nbsp;</p>\r\n<p>Ваш священик, Ігор Онишкевич</p>',NULL,NULL,1752,5,1,5);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2375,'P','2009-08-26',1,NULL,'Rss','Звернення Блаженнішого Любомира у день празника Успіння Пресвятої Богородиці','<blockquote>\n<p style=\"text-align: right;\"><em>Київ, 17 серпня 2009 року Божого</em><strong></strong></p>\n<p style=\"text-align: center;\"><strong>Звернення Блаженнішого Любомира, Верховного Архиєпископа Києво-Галицького, <br />у день празника Успіння Пресвятої Богородиці <br />до духовенства та мирян Української Греко-Католицької Церкви, <br />які з нагоди цього празника здійснюють прощі до великих відпустових місць, <br />Богородичних святинь і чудотворних ікон</strong></p>\n<p style=\"text-align: center;\"> Дорогі у Христі!</p>\n<p>Звертаюся з проханням до Преосвященних Владик і достойних проповідників, щоб вони, перш ніж почнуть свою празничну проповідь, прочитали це моє невелике слово до вас.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=419:zvernennja-blazhennishogo-ljubomyra-u-den-praznyka-uspinnja-presvjatoji-bogorodyci&catid=27:gk-v-ukrajini-cat&Itemid=57',1753,NULL,1,26),
(2376,'P','2009-08-26',1,NULL,'Note','Пісенний собор. Прес-конференція в УНІАН про концерти популярної музики','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><strong>Учасники прес-конференції:</strong> Блаженніший Любомир (Гузар), Наталія Сумська, народна артистка України, лауреат Національної премії ім. Тараса Шевченка, Олег Собчук, лідер гурту \"С.К.А.Й.\" та Сергій Архипчук, режисер</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">27 серпня 2009 року о 9.30 в інформаційному агентстві УНІАН відбудеться прес-конференція, під час якої журналістам буде повідомлено про <span style=\"text-decoration: underline;\"><span style=\"color: #0000ff;\">благодійні концерти популярної музики &laquo;З вірою і любов&rsquo;ю&raquo;</span></span>. Як відомо, <span style=\"text-decoration: underline;\"><span style=\"color: #0000ff;\">академічні концерти</span></span>, об&rsquo;єднані такою ж назвою, з великим успіхом пройшли у травні. Цей тур, який триватиме з 7 до 22 вересня, пройде у 13 містах і містечках Волинської, Рівненської, Чернівецької, Тернопільської, Івано-Франківської і Львівської областей України. Концерти відбудуться під патронатом Блаженнішого Любомира (Гузара), Глави Української Греко-Католицької Церкви. </span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">У концертах популярної музики візьмуть участь: <strong>Тарас Петриненко і Тетяна Горобець</strong>, вокальна формація <strong>&laquo;Піккардійська терція&raquo;</strong>, лідер &laquo;Мандрів&raquo; <strong>Фома</strong>, гурти <strong>&laquo;Друга ріка&raquo;</strong>, <strong>&laquo;С.К.А.Й&raquo;</strong>, <strong>&laquo;Гудімов&raquo;</strong>, <strong>&laquo;Горгішелі&raquo;</strong>, <strong>&laquo;Транс-формер&raquo;</strong>, <strong>Наталя і Дарина (Мамай) Сумські</strong>, <strong>Ігор Мірошниченко</strong>. Режисер-постановник &ndash; <strong>Сергій Архипчук</strong>.</span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Як і попередній, цей тур також благодійний. (Під час концертів академічної музики було зібрано біля 800 тис. гривень). І цього разу виручені кошти підуть на завершення будівництва Патріаршого собору Воскресіння Христового у Києві. Отож на підтримку української духовності й УГКЦ до кола видатних діячів української культури приєднуються популярні вітчизняні музиканти, і, звісно, численні співучасники-глядачі.</span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><strong>Учасники прес-конференції:</strong> Блаженніший Любомир (Гузар), Наталія Сумська, народна артистка України, лауреат Національної премії ім. Тараса Шевченка, Олег Собчук, лідер гурту \"С.К.А.Й.\" та Сергій Архипчук, режисер.</span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Департамент інформації УГКЦ</span></span></p>',NULL,NULL,1754,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2377,'P','2009-08-26',1,NULL,'Rss','Глава УГКЦ у День Незалежності молився за те, щоб українці «жили по правді, діяли справедливо, любили свій народ» ','<p>У День святкування 18-ї річниці незалежності нашої держави у Софійському соборі Києва відбулася молитва за Україну. Традиція починати святкування Дня Незалежності зі спільної Молитви за Україну та український народ започаткована Віктором Ющенком декілька років тому. Від Української Греко-Католицької Церкви учасником молитви був Блаженніший Любомир, Верховний Архиєпископ Києво-Галицький.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=420:glava-ugkc-u-den-nezalezhnosti-molyvsja-za-te-shchob-ukrajinci-lzhyly-po-pravdi-dijaly-spravedlyvo-ljubyly-svi-narodr-&catid=27:gk-v-ukrajini-cat&Itemid=57',1755,NULL,1,26),
(2378,'P','2009-08-26',1,NULL,'Note','В Херсоне снесли греко-католический храм','<p style=\"TEXT-ALIGN: justify; FONT-FAMILY: \"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">- Я испытал настоящий шок, когда мне сообщили об уничтожении храма. Не верится, что такое может происходить сегодня, на восемнадцатом году Независимости. Последний раз церкви в Украине разрушали еще при Сталине. &ndash; так прокомментировал Одесско-Крымский экзарх УГКЦ владыка Василий Ивасюк.</span></span></p>','<div><a href=\"http://kp.ua/daily/author/\"><span style=\"text-decoration: underline;\"><span style=\"color: #0000ff;\">\r\n<div style=\"TEXT-ALIGN: justify; FONT-FAMILY: \"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Строители разрушили двухэтажное здание посреди бела дня, не пояснив ничего церковникам.</span></span></div>\r\n<span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Снежана ПАВЛОВА</span></span></span></span></a></div>\r\n<p><span id=\"font_size\"></span>\r\n<p>&nbsp;</p>\r\n<p style=\"TEXT-ALIGN: justify\">&nbsp;</p>\r\n</p>\r\n<p style=\"FONT-FAMILY: \" align=\"justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: right;\" src=\"http://kp.ua/upimg/5a3e8f353c90697f8fa8aa939d18ebfb3dd46ab0/163079.jpg\" alt=\"\" />Двухэтажный храм находился в центре Херсона, был в совместном пользовании Украинской Греко-Католической Церкви и Немецкой Евангелическо-Лютеранской Церкви в Украине.</span></span></p>\r\n<p style=\"FONT-FAMILY: \"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"FONT-FAMILY: \" align=\"justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">- Представители строительной компании &laquo;Парнас&raquo;, не имея никаких разрешительных документов, с усмешкой на устах варварски разрушили христианскую святыню, а на месте храма вырыли глубокий котлован. - сообщил ужасающую новость&nbsp; декан Херсонского деканата УГКЦ, отец Степан Макар.&nbsp;</span></span></p>\r\n<p style=\"FONT-FAMILY: \" align=\"justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Имя заказчика в церкви пока не знают, но уверены, что причина такого акта вандализма в том, что земля находится в центре города&nbsp; и она очень прибыльна для бизнеса.&nbsp;&nbsp;&nbsp;</span></span></p>\r\n<p style=\"FONT-FAMILY: \" align=\"justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">- Я испытал настоящий шок, когда мне сообщили об уничтожении храма. Не верится, что такое может происходить сегодня, на восемнадцатом году Независимости. Последний раз церкви в Украине разрушали еще при Сталине. &ndash; так прокомментировал Одесско-Крымский экзарх УГКЦ владыка Василий Ивасюк.&nbsp;&nbsp;&nbsp;</span></span></p>\r\n<p style=\"FONT-FAMILY: \" align=\"justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Церковники собираются встретиться с представителями власти, подать иск в суд о прекращении любых действий до выяснения обстоятельств и отстаивать храм в дальнейшем согласно документам. Заявление в прокуратуру уже подали.&nbsp;</span></span></p>\r\n<p align=\"justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">За матеріалами: </span></span><a style=\"FONT-FAMILY: \" href=\"http://kp.ua/daily/250809/191842/\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">http://kp.ua/daily/250809/191842/</span></span></a></p>',NULL,NULL,1758,5,1,27);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2379,'P','2009-08-26',1,NULL,'Note','Кто из религиозных деятелей Украины самый влиятельный','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Значительной заслугой кардинала Любомира Гузара &laquo;Корреспондент&raquo; считает то, что Украинская греко-католическая церковь за время его председательства перестала восприниматься, как религиозная организация исключительно западных областей Украины. &laquo;Он привлекает новых верующих своим моральным авторитетом и медленно, но неуклонно продвигает свое влияние в восточные регионы&raquo; - отмечает издание. Сам же глава УГКЦ &laquo;может служить образцом скромности в быту, полностью отвечающем его монашескому обету&raquo;, убеждены составители рейтинга.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Патриарх Филарет (Денисенко) &ndash; самый влиятельный человек в Украине среди религиозных деятелей. Так определил авторитетный журнал &bdquo;Корреспондент&rdquo;. Предстоятель Украинской православной церкви Киевского патриархата занял 30 место в рейтинге &bdquo;Топ 100 самых влиятельных людей Украины&rdquo;. В сотню влиятельных людей также попали митрополит Владимир (Сабодан) (32 место) и кардинал Любомир Гузар (54 место).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Журнал отмечает, что несмотря на то, что в Украине больше приходов УПЦ Московского патриархата (которую возглавляет митрополит Владимир), однако многочисленные опросы общественного мнения показывают, что большинство украинцев предпочитают ходить в храмы УПЦ Киевского патриархата.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&bdquo;Статус главы официально второй, а неофициально первой церкви Украины делает Филарета духовным авторитетом для большого числа верующих и влиятельным общественным деятелем. А его последовательная и принципиальная позиция в борьбе за единую и независимую Украинскую православную церковь &ndash; достойным доверия еще и глазах нерелигиозных, но патриотически настроенных соотечественников&raquo;, - утверждает &bdquo;Корреспондент&rdquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">По словам авторов рейтинга, Патриарха Филарета &bdquo;поддерживают многие сторонники независимости в самой УПЦ (МП), что еще больше крепит позиции Филарета и вселяет уверенность в победе его идеи&rdquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">В то же время, митрополит Владимир, по мнению журналистов издания, остается &laquo;главой влиятельной церкви&raquo;, то есть тем, кто принимает важные решения, а также &laquo;пастырем и духовным авторитетом для многих украинцев, особенно в восточных и южных областях страны&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Значительной заслугой кардинала Любомира Гузара &laquo;Корреспондент&raquo; считает то, что Украинская греко-католическая церковь за время его председательства перестала восприниматься, как религиозная организация исключительно западных областей Украины. &laquo;Он привлекает новых верующих своим моральным авторитетом и медленно, но неуклонно продвигает свое влияние в восточные регионы&raquo; - отмечает издание. Сам же глава УГКЦ &laquo;может служить образцом скромности в быту, полностью отвечающем его монашескому обету&raquo;, убеждены составители рейтинга.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">За матеріалами: </span></span><a href=\"http://unian.net/rus/news/news-332759.html\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">http://unian.net/rus/news/news-332759.html</span></span></a></p>',NULL,NULL,1759,5,1,30);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2380,'P','2009-08-26',1,NULL,'Rss','Успіння Пресвятої Богородиці','<p align=\"justify\"><strong>Коротка історія</strong></p>\n<p align=\"justify\">Наш церковний рік не тільки багатий на різні Богородичні празники, але він також ними починається і завершується. Його відкриває празник Різдва Пресвятої Богородиці, а завершує її славне Успіння, що в наших літургійних книгах має таку величну назву: „Успіння Пресвятої славної Владичиці нашої Богородиці і Приснодіви Марії\".</p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=412:uspinnja-presvjatoji-bogorodyci&catid=30:hrystyjanske-vchennia-cat&Itemid=37',1760,NULL,1,26),
(2381,'P','2009-08-31',1,NULL,'Note','«Криза, у якій ми опинилися, показала велику недугу у суспільстві»: Глава УГКЦ ','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&laquo;Ми можемо вийти із цієї хвороби, на мою думку, у той спосіб, що кожний із нас &ndash; чи молодий, чи старий &ndash; старатиметься зберегти себе, як людину&raquo;, &ndash; продовжив Блаженніший Любомир. При цій нагоді він пригадав про концерти, які проводитиме УГКЦ, головним завданням яких є те, щоб молодь відчула себе людиною.</span></span></p>','<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"border: 1px solid black; margin: 5px; width: 170px; height: 227px; float: right;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_huzar_unian_02.jpg.jpg\" alt=\"\" />Блаженніший Любомир, Глава Української Греко-Католицької Церкви, наголосив на тому, що сучасна криза показала велику недугу у суспільстві. &laquo;Криза, у якій ми опинилися, показала велику недугу у суспільстві. Це криза ума і серця. І це є гірше, як криза економічна. Одним словом, суспільство захворіло&raquo;, &ndash; відзначив Предстоятель УГКЦ <a href=\"http://ugcc.org.ua/news_single.0.html?&amp;tx_ttnews%5Btt_news%5D=1482&amp;tx_ttnews%5BbackPid%5D=2&amp;cHash=aa67e70be2\">27 серпня 2009 року під час прес-конференції в УНІАН</a>.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Ми можемо вийти із цієї хвороби, на мою думку, у той спосіб, що кожний із нас &ndash; чи молодий, чи старий &ndash; старатиметься зберегти себе, як людину&raquo;, &ndash; продовжив Блаженніший Любомир. При цій нагоді він пригадав про концерти, які проводитиме УГКЦ, головним завданням яких є те, щоб молодь відчула себе людиною. &laquo;Не предметом, але підметом, який має власну гідність та покликання зробити щось для себе і для інших&raquo;, &ndash; сказав Предстоятель УГКЦ.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Мені здається, що ми не можемо чекати на те, що хтось із зовні розв&lsquo;яже нам проблеми. Безумовно, поміч із зовні є дуже корисна: від батьків, вчителів, духовенства, від суспільств взагалі. Але остаточно розв&lsquo;язка повинна прийти із нутра, адже зустріч людини із такими не легко проминаючими вартостями, якою є мистецька краса, спонукає застановитися, очиститися, стати більше собою&raquo;, &ndash; підсумовує Блаженніший Любомир.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Другий важливий чинник виходу із кризи, на думку Предстоятеля УГКЦ, це почуття відповідальності перед собою і перед другими.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Департамент інформації УГКЦ</span></span></p>',NULL,NULL,1762,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2382,'P','2009-08-31',1,NULL,'Note','Блаженніший Любомир (Гузар) посів 54 сходинку рейтингу „Топ 100 найбільш впливових людей України” ','<p style=\"text-align: justify;\"><span><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Блаженніший Любомир (Гузар) посів 54 сходинку рейтингу &bdquo;Топ 100 найбільш впливових людей України&rdquo;. Згадуючи Предстоятеля УГКЦ Блаженнішого Любомира, &laquo;Корреспондент&raquo; зазначає, що його паства в Україні щороку збільшується, як мінімум на 1%, а у 2008 році греко-католики вийшли на 3 місце після православних і протестантів. &laquo;УГКЦ вже не сприймається як релігійна організація виключно західних областей України, і відкриття патріаршого собору в Києві стало остаточною крапкою в утвердженні впливовості цієї церкви в державі&raquo;. </span></span></span></p>','<p style=\"text-align: justify; margin: 0px;\"><span><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Блаженніший Любомир (Гузар) посів 54 сходинку рейтингу &bdquo;Топ 100 найбільш впливових людей України&rdquo;. Згадуючи Предстоятеля УГКЦ Блаженнішого Любомира, &laquo;Корреспондент&raquo; зазначає, що його паства в Україні щороку збільшується, як мінімум на 1%, а у 2008 році греко-католики вийшли на 3 місце після православних і протестантів. &laquo;УГКЦ вже не сприймається як релігійна організація виключно західних областей України, і відкриття патріаршого собору в Києві стало остаточною крапкою в утвердженні впливовості цієї церкви в державі&raquo;. </span></span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-size: 10pt;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Автори рейтингу вбачають тут &laquo;значну заслугу&raquo; її голови, який &laquo;приваблює нових віруючих своїм моральним авторитетом і поволі, але неухильно просуває свій вплив у східні регіони&raquo;. Главу УГКЦ названо освіченим, розсудливим і глибоко віруючим, і це приваблює тих, для кого важлива духовна складова релігії. За висновками &laquo;Корреспонденту&raquo;, Блаженніший Любомир намагається дистанціюватися від матеріальних багатств і політики. Все це і збільшує його вплив не тільки серед вірних УГКЦ, але й освічених і патріотично налаштованих українців. </span></span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify; margin: 0px;\"><span style=\"font-size: 10pt;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Корреспондент</span></span></span></p>',NULL,NULL,1763,5,1,30),
(2383,'P','2009-08-31',1,NULL,'Note','Молитовні намірення Папи на вересень 2009','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><strong><span style=\"text-decoration: underline;\"></span></strong><strong><span style=\"text-decoration: underline;\">Місійне</span></strong>: Щоб християни в Лаосі, Камбоджі і М&rsquo;янмі, які часто зазнають великих труднощів, не знеохотились звіщати Євангеліє своїм братам, покладаючись на силу Святого Духа.</span></span></span></p>','<p style=\"text-align: justify; color: #153f6b;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">У вересні цього року Святіший Отець Венедикт ХVІ запрошує молитись у наступних наміреннях:<br /><br /><strong><span style=\"text-decoration: underline;\">Загальне</span></strong>: Щоб Боже Слово було більше пізнане, сприйняте і пережите, як джерело свободи й радості.<br /><br /><strong><span style=\"text-decoration: underline;\">Місійне</span></strong>: Щоб християни в Лаосі, Камбоджі і М&rsquo;янмі, які часто зазнають великих труднощів, не знеохотились звіщати Євангеліє своїм братам, покладаючись на силу Святого Духа.<br /><br />Понад 50 мільйонів членів Апостольства Молитви, започаткованого 1844 року у Франції і яке тепер поширене на всіх континентах світу, щодня підносять до Господа молитовні благання в наміреннях Вселенського Архиєрея. Апостольство Молитви поширене не тільки в римо-католицькій Церкві, але також і в багатьох парафіях УГКЦ у світі.</span></span></p>',NULL,NULL,1766,5,1,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2384,'P','2009-08-31',1,NULL,'Note','Катерина Ющенко відвідала дитячий будинок сімейного типу, який провадять Сестри Служебниці ','<p style=\"text-align: justify;\"><span style=\"font-family: \'Arial\', \'sans-serif\';\"><span style=\"font-size: small;\">Кульмінацією зустрічі була спільна молитва разом з Гостею в домашній каплиці Дитячого будинку, а на прощання п. Катерина подарувала господиням дому ікону св. Миколая і з піднесенням та надією сказала: &laquo;Хай цей дім стане духовним джерелом, завдяки якому може змінитися ціле місто&raquo;.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">28 серпня, на свято Успіння Богородиці дитячий будинок сімейного типу у м. Золочеві, який провадять Сестри Служебниці Непорочної Діви Марії, відвідала Перша леді України &ndash; Катерина Ющенко. &laquo;Вітаємо Вас, хай Господь Бог Вас благословить&hellip;&raquo; - співали діти, вітаючи дружину президента.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Пані Катерина радо прийняла запрошення с. Надії Котур, Провінційної Настоятельки СНДМ, відвідати Дитячий будинок сімейного типу, в якому на даний час Сестри виховують 7 дітей, позбавлених батьківського піклування.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Зустрічали п. Катерину Ющенко сестри та діти, а також чисельні представники місцевої влади та багато людей, які вийшли на вулиці м. Золочева, щоб привітати Високоповажну Гостю.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Атмосфера зустрічі була надзвичайно теплою і щирою. Діти подарували п. Катерині власноруч виготовлене&nbsp; &laquo;серце&raquo; зі своїми іменами, як символ любові від родини до родини і заспівали вітальну пісню. Дружина Президента, в свою чергу, знайомилася з кожною дитиною та її історією життя, з побутом дітей та дому, в якому вони проживають, вислуховувала всі турботи та потреби сімейного дому, якими ділилися сестри.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Кожна дитина отримала подарунки з рук п. Катерини, яка пообіцяла допомогти сестрам у вирішенні нагальних проблем, що стосуються лікування дітей та створення необхідних умов на території будинку.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Кульмінацією зустрічі була спільна молитва разом з Гостею в домашній каплиці Дитячого будинку, а на прощання п. Катерина подарувала господиням дому ікону св. Миколая і з піднесенням та надією сказала: &laquo;Хай цей дім стане духовним джерелом, завдяки якому може змінитися ціле місто&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Прес-служба Сестер Служебниць</span></span></p>',NULL,NULL,1764,5,1,30);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2385,'P','2009-08-31',1,NULL,'Rss','Примирення, як тест на щирість кандидатів в президенти','<p>1-ше вересня 1939 року ввійшло в історію як початок Другої світової війни. Для жителів Західної України з цього дня починається нова історична доба, яка й по сьогодні не закінчилась. Для корінних жителів встановлення нової влади супроводжувалось репресіями, терором, втратою приватних земельних наділів, виселенням з рідних домівок та багато іншого. З 1944 року почалось повторне встановлення радянської влади, що супроводжувалось масовим збройним спротивом при активній підтримці всього місцевого населення.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=421:prymyrennja-jak-test-na-shchyrist-kandydativ-v-prezydenty&catid=37:spilnota-cat&Itemid=49',1767,NULL,1,26),
(2386,'P','2009-08-30',1,NULL,'Note','Молитва \"Ангел Господній\" з Папою Венедиктом XVI в останню неділю літа','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&laquo;Коли подружжя великодушно посвячується вихованню дітей, провадячи їх і скеровуючи до відкриття Божого задуму любові, то приготовляє той родючий духовний ґрунт, де зроджуються і дозрівають покликання до священства і до богопосвяченого життя&raquo;, &ndash; наголосив Єпископ Риму, додавши, що з цього бачимо наскільки пов&rsquo;язані міцно між собою подружжя і дівицтво.</span></span></p>','<p style=\"text-align: justify; color: #153f6b;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Сьогодні, в останню неділю місяця серпня, Святіший Отець Венедикт ХVІ зустрівся із численними паломниками, що прибули у Кастель Ґандольфо, де розташована літня резиденція Вселенських Архиєреїв, щоб зустрітись із Папою, послухати його недільне повчання, проказати разом із ним молитву &laquo;Ангел Господній&raquo; та отримати його Апостольське благословення. <br /><br />Цього разу Святіший Отець пригадав вірним, що 27 серпня Римо-Католицька Церква пригадувала на Літургії святу Моніку, матір великого святого Західної Церкви, святого Августина. Папа наголосив, що її вважають зразком і покровителькою усіх християнських матерів, зазначивши, що про неї багато довідуємося із автобіографічної книжки її сина, яка має назву &laquo;Сповідь&raquo;. З неї дізнаємось, що святий Августим прийняв вчення про Спасителя із материнським молоком і був вихований святою Монікою у християнському дусі, а закладені нею християнські принципи були у його серці навіть у часах його духовного і морального занепаду. Моніка ніколи не переставала молитись за навернення свого сина і її молитва увінчалась успіхом, адже по багатьох роках вона таки побачила його повернення до віри і прийняття ним Святої Тайни Хрищення. Господь вислухав благальні молитви цієї святої матері. Папа пригадав, що одного разу місцевий єпископ сказав до Моніки: &laquo;Неможливо, щоб син стількох сліз був втрачений&raquo;. Святий Августин не лише навернувся, але й вирішив стати монахом, тому, повернувшись до Африки, заснував монашу спільноту. Як підкреслив Вселенський Архиєрей, свята Моніка була для свого сина &laquo;більше, ніж матір, вона була джерелом його християнства&raquo;. Бачачи навернення свого сина, що було її найбільшим бажання продовж багатьох років, і його цілковиту посвяту на служіння Господеві, свята Моніка радісно перейшла по вічну нагороду 27 серпня 387 року, маючи 56 років. Святий Августин часто повторював, що його мати &laquo;породила його двічі&raquo;. <br /><br />Далі Папа Венедикт ХVІ зазначив, що історія християнства сповнена незліченними прикладами святих батьків і справжніх християнських родин, які супроводжували життєвий шлях великодушних священиків та пастирів Церкви, вказуючи, зокрема, на Василія Велекого, Григорія Назіянського, Луіджі Бельтраме Кваттроккі та Марію Корсіні. Двоє останні жили на переломі ХІХ-ХХ століть і були проголошені блаженними Папою Іваном Павлом ІІ у жовтні 2001 року. Ця подія була приурочена двадцятої річниці папського документу &laquo;Familiaris consortio&raquo;, який не лише висвітлює цінності подружжя та завдання сім&rsquo;ї, але й заохочує подружню пару до особливих старань на шляху до святості, черпаючи силу у святій Тайні Подружжя. <br /><br />&laquo;Коли подружжя великодушно посвячується вихованню дітей, провадячи їх і скеровуючи до відкриття Божого задуму любові, то приготовляє той родючий духовний ґрунт, де зроджуються і дозрівають покликання до священства і до богопосвяченого життя&raquo;, &ndash; наголосив Єпископ Риму, додавши, що з цього бачимо наскільки пов&rsquo;язані міцно між собою подружжя і дівицтво. <br /><br />А на закінчення Папа заохотив вірних, особливо у цей рік Священства, молитись за те, щоб через заступництво святого пароха з Арсу, Жана Марії Віяннея, християнські родини ставали маленькими церквами, у яких усі покликання та усі харизми, даровані Святим Духом, могли бути прийняті і належно доцінені. <br /><br />А після проказування Богородичної молитви та уділення благословення, Папа знову звернувся до вірних, пригадуючи, що у вівторок, 1 вересня, в Італії відзначатиметься День захисту довколишнього середовища, заохочуючи, зокрема, і ндустріальні держави до відповідальної співпраці за майбутнє нашої планети, аби найбідніші народи не платили більшу ціну за кліматичні зміни.</span></span></p>',NULL,NULL,1765,5,1,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2387,'P','2009-08-31',1,NULL,'Rss','Найбільше багатство – мудрість!','<p><strong>30 серпня  - святковий день на Аскольдовій Могилі. Сьогодні зібралося тут багато парафіян громади святого Миколая Чудотворця, адже скоро дітям потрібно іти до школи, і вони хочуть отримати благословення від Бога, священиків на добру науку. </strong></p>\n<p><strong>Спершу священики УГКЦ Роман Небожук, Олекса Петрів і настоятель церкви святого Миколая отець Ігор Онишкевич відслужили Божественну Літургію.</strong></p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=422:nabilshe-bagatstvo--mudrist&catid=25:parafija-cat&Itemid=27',1768,NULL,1,26),
(2388,'P','2009-09-01',1,NULL,'Rss','Ясна гора приймає кожного паломника','<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3191,200,image003.jpg\" width=\"200\" height=\"150\" /></em></p>\n<p>Монастир, що у Гошеві на Ясній горі приймав паломників з усієї України. 28 серпня 2009 р. у свято Успення Божої Матері тут відбулася знаменна подія - коронування чудотворної ікони Божої Матері Гошівської, біля якої упродовж століть відбувалися сотні чудес зцілення, навернення, оздоровлення. Дванадцять паломників з числа парафіян церкви Св. Миколая на Аскольдовій могилі, що у м. Києві, також побували на цьому величному святі духовності та віри. Дорога прочан до Святині та була оплачена добродіями Сергієм та  Русланою. Дана пожертва стала офірою на молитву за здоров\'я та спокій родини, за  мир та процвітання України.</p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=424:jasna-gora-prymaje-kozhnogo-palomnyka&catid=25:parafija-cat&Itemid=27',1770,NULL,1,26),
(2389,'N','2009-09-01',0,NULL,'Note','','','<p>&nbsp;</p>\r\n<p>Свято Успіня Богородиці святкували на Аскольдовій Могилі. На при кінці Божественної літургії була освячена вода і принесені квіти для Богородиці. Цим святом завершується церковний рік.</p>\r\n<p>Наші паломники з Ясної гори, що брали участь коронації Гошівської Богородиці, повідомляють про велику кількість молільників, священиків. Вітаємо отця Ігумена Дем\'яна зі святом і його Днем народження. Многая літа!</p>\r\n<p>Матеріал, Іллі, &laquo;Оранта&raquo;</p>',NULL,NULL,1771,5,1,5),
(2390,'P','2009-09-02',1,NULL,'Rss','Причини проти контрацепції і стерилізації','<p>Святіший Отець Пій ХІ у Енцикліці «Casti Conubii» («Чисте подружжя») проти контрацепції і стерилізації написав: (56) «Оскільки деякі, відкрито відступаючи від незмінного і переданого від початків християнського вчення, вважають, що у наші дні потрібно урочисто проголосити інше вчення щодо цього питання, то Католицька Церква, якій сам Бог доручив обов\'язок навчати і захищати чесність та чистоту звичаїв, міцно стоячи посеред моральної руїни, яка її оточує, з метою збереження чистоти шлюбного зв\'язку від цієї огидної плями, піднімає свій голос на знак Божого післаництва та Нашими устами знову проголошує: кожне подружнє єднання, у якому статевий акт зумисно позбавлений своєї природної здатності продовження роду, порушує Божий та природний закон, і хто собі таке дозволяє, чинить тяжкий гріх. \n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=425:prychyny-proty-kontracepciji-i-sterylizaciji&catid=37:spilnota-cat&Itemid=49',1772,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2391,'P','2009-09-03',1,NULL,'Rss','Життя св. Апостола Юди-Тадея','<p>Святий Юда-Тадей був одним із дванадцяти апостолів Ісуса Христа. <img src=\"http://charnetskyj.org.ua/img/Tadej-_5.jpg\" style=\"float: right; margin: 10px;\" /> Батько його - Алфей (Клеоп), брат святого Йосифа, Обручника Пречистої Діви Марії, а мати - Марія Клеопова, близька родичка Матері Божої. В Євангелії читаємо, що Марія Клеопова безстрашно стояла під хрестом разом з Матір\'ю Страждальною і Марією Магдалиною. Вона була дуже побожною жінкою, її чоловік Клеоп був одним із тих, які сміливо розповідали про Христове Воскресіння. Саме йому разом з іншими вірними учнями з\'явився Ісус Христос по дорозі в Емаус. Набожність батьків, а також близьке споріднення із святою Родиною, а вслід за тим добрий приклад з її життя послужили причиною того, що святий Тадей відзначався надзвичайними чеснотами. Коли прийшов час апостольської діяльності Ісуса Христа святий Юда-Тадей став його ревним учнем. Будучи його близьким родичем, ніколи цим не чванився і називав себе слугою Христовим.\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=426:zhyttja-sv-apostola-judy-tadeja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',1773,NULL,1,26),
(2392,'P','2009-09-07',1,NULL,'Rss','25-та річниця смерті Блаженнішого Патріарха Йосифа Сліпого  ','<p><a target=\"_blank\" href=\"http://62.77.60.84/audio/ra/00176851.RM\">http://62.77.60.84/audio/ra/00176851.RM</a></p>\n<p>(звукозапис: з проповіді Блаженнішого Йосифа Сліпого під часпосвячення собору Святої Софії у Римі, 27 вересня 1969 року)</p>\n<p><img src=\"http://ugcc.org.ua/uploads/RTEmagicC_Patr_Josyf.jpg.jpg\" complete=\"true\" style=\"float: left; margin: 10px;\" />7 вересня 2009 року виповнюється 25 років від переходу у вічність цього геройського ісповідника віри, одного з найвідважніших борців за права Христової Церкви та українського народу, світлого символу незламного борця за Божу правду й людську гідність.</p>\n<p>Протягом останніх десятиріч в історії нашої батьківщини та Церкви відбулись визначні події - незалежність України та легалізація УГКЦ дозволили перевезення тлінних останків Блаженнішого Йосифа Сліпого з собору Святої Софії у Римі до Львова, що відбулось 1992 року, і тепер вони спочивають у крипті собору святого Юра.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=427:25-ta-richnycja-smerti-blazhennishogo-patriarha-osyfa-slipogo-&catid=35:istorychni-postati-cat&Itemid=48',1774,NULL,1,26),
(2393,'P','2009-09-07',1,NULL,'Rss','Усікновення чесної голови св. Івана Христителя','<p><img style=\"margin: 10px; float: right;\" src=\"http://ugcc.org.ua/uploads/pics/beheading_John_Baptist.jpg\" height=\"481\" width=\"300\" /><img src=\"http://ugcc.org.ua/clear.gif\" border=\"0\" height=\"5\" width=\"1\" /></p>\n<p>11 вересня 2009 року</p>\n<p><strong>Тропар та кондак</strong></p>\n<p align=\"justify\"><em>Тропар, глас 2: </em>Пам\'ять праведного з похвалами, і тобі вистачає свідчення Господнє, предтече, бо ти показався воістину і від пророків чесніший, бо у струях сподобився хрестити Проповіданого. Тому за істину пострадав ти, радуючися, благовістив ти і тим, що в аді, Бога, явленого в тілі, що взяв гріх світу і подає нам велику милість.</p>\n<p align=\"justify\">Слава: <em>Кондак, глас 5: </em>Предтечі славне усікновення було якось божественним задумом, щоб і тим, що в аді, проповів Спасове пришестя. Нехай, отже, ридає Іродія, що випросила беззаконне вбивство, бо возлюбила не закон божий, ні живий вік, але - лукавий і дочасний.</p>\n<p align=\"justify\">І нині: <em>Богородичний, глас </em>5: Радуйся, двері Господні непроходимі, радуйся, стіно і покрове тих, що прибігають до тебе, радуйся, пристане, бурями не навіщувана і подружжям незаймана, що родила плоттю Творця твого і Бога. Молитись не переставай за тих, що оспівують і поклоня­ються різдву твоєму.\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=428:usiknovennja-chesnoji-golovy-sv-ivana-hrystytelja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',1777,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2394,'P','2009-09-07',1,NULL,'Rss','Завершується збір документів у справі проголошення блаженним отця Андрія Бандери  ','<p>Постуляційний центр беатифікації та канонізації святих УГКЦ завершує збір документів у справі проголошення блаженним отця Андрія Бандери, батька провідника ОУН Степана Бандери.</p>\n<p>Серед документів, зібраних для беатифікаційного процесу, сотні свідчень очевидців про життя о. Андрія, його діяль­ність і смерть.</p>\n<p>За різних режимів у Галичині священик проповідував слово Боже і зберігав україн­ський дух, культуру. До дня свого арешту отець Андрій не покинув своїх парафіян. У вирі соціальних трагедій душпастир сам виховав сімох ді­тей.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=429:zavershujetsja-zbir-dokumentiv-u-spravi-progoloshennja-blazhennym-otcja-andrija-bandery-&catid=35:istorychni-postati-cat&Itemid=48',1778,NULL,1,26),
(2395,'P','2009-09-09',1,NULL,'Rss','УГКЦ: шлях від катакомб до патріархату','<p>У Верховній Раді України зареєстровано проект постанови про надання Українській Греко-Католицькій Церкві статусу репресованої. Якщо народні обранці підтримають дану ініціативу, то  таким чином буде відновлено історичну справедливість щодо однієї з найбільших християнських церков, без якої не можливо уявити релігійне, духовне та культурне життя України.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=430:ugkc-shljah-vid-katakomb-do-patriarhatu&catid=27:gk-v-ukrajini-cat&Itemid=57',1783,NULL,1,26),
(2396,'P','2009-09-09',1,NULL,'Note','Президент України нагородив о. мітрата Стефана Батруха  ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8 вересня 2009 року Президент України Віктор Ющенко під час зустрічі з українською громадою Польщі нагородив орденом &laquo;За заслуги&raquo; ІІІ ступеня о. мітрата Стефана Батруха, настоятеля парафії Різдва Пресвятої Богородиці УГКЦ в м. Люблін, &laquo;за значний особистий внесок у рятуванні української спадщини та будування добросусідських відносин&raquo; (указ № 623/2009).</span></span></p>','<blockquote style=\"margin-bottom: 0pt; margin-top: 0pt; text-align: justify;\">\r\n<p style=\"margin: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"border: 1px solid black; margin: 5px; width: 227px; height: 170px; float: right;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_batrux.jpg.jpg\" alt=\"\" />8 вересня 2009 року Президент України Віктор Ющенко під час зустрічі з українською громадою Польщі нагородив орденом &laquo;За заслуги&raquo; ІІІ ступеня о. мітрата Стефана Батруха, настоятеля парафії Різдва Пресвятої Богородиці УГКЦ в м. Люблін, &laquo;за значний особистий внесок у рятуванні української спадщини та будування добросусідських відносин&raquo; (указ № 623/2009).</span></span></p>\r\n<p style=\"margin: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Президент України та його дружина, в рамках робочого візиту до Польщі, зустрілися з представниками української громади та вірними УГКЦ спочатку в Архикатедральному соборі Перемисько-Варшавської митрополії, а відтак в Українській школі ім. Маркіяна Шашкевича в м. Перемишль.</span></span></p>\r\n<p style=\"margin: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt;\" align=\"right\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Для Департаменту інформації повідомив Михайло Лесів</span></span></p>\r\n</blockquote>',NULL,NULL,1785,5,1,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2397,'P','2009-09-08',1,NULL,'Note','Співпраця Церкви і держави задля утвердження в Україні високих моральних цінностей  ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Духовні провідники погодились з позицією Блаженнішого Любомира, Глави УГКЦ, озвучену представником УГКЦ, про те, що телебачення та радіо не повинно допускати лихослів&rsquo;я в ефірі &ndash; нецензурної лексики, яка за останні часи стала широко використовуватись у молодіжних розважальних програмах, гумористичних проектах та іншій телепродукції.</span></span></p>','<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Голова Національної експертної комісії України з питань захисту суспільної моралі Василь Костицький представив главам та представникам конфесій звіт про діяльність очолюваного ним відомства. Про активізацію діяльності Національної комісії та її досягнення у сфері утвердження моральних засад суспільства за останній період Василь Костицькій розповів під час засідання Секретаріату Всеукраїнської Ради Церков і релігійних організацій 3 вересня 2009 року. Про це повідомляє <a href=\"http://www.irs.in.ua/\" target=\"_blank\">Інститут релігійної свободи, м.Київ</a>.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У своїй доповіді голова Національної комісії повідомив про заходи, вжиті за наслідками виявлення фактів незаконного поширення матеріалів еротичного та порнографічного характеру, публікацій ксенофобського, антисемітського змісту та таких, що ображають національну гідність українців та представників інших націй, а також релігійні почуття віруючих. Особливу увагу він приділив загрозливій тенденції вільного поширення продукції, яка пропагує жорстокість та насильство, має еротичний чи порнографічний характер,&nbsp; у різних медійних засобах, Інтернеті та у мобільному контенті, відкритих для доступу дітей.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Під час обговорення даної проблематики представники Церков і релігійних організацій висловили свій категоричний протест проти публікації розпусних та образливих матеріалів. У якості прикладів були згадані публікації у щоденній газеті &laquo;Блік&raquo;, які були визнані Національною комісією такими, що порушують законодавство про захист суспільної моралі. На думку релігійних діячів, у виданні не тільки ображаються християнські цінності та почуття віруючих людей, але й нав&rsquo;язується розпусний спосіб життя читачам, серед яких можуть бути підлітки та діти. </span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Учасники засідання звернули увагу на важливу у цьому сенсі відкриту Заяву Всеукраїнської Ради Церков і релігійних організацій від 2 червня 2006 року щодо необхідності поважати релігійні почуття, символи, традиції та дотримуватись високих моральних стандартів у журналістській діяльності.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Духовні провідники погодились з позицією Блаженнішого Любомира, Глави УГКЦ, озвучену представником УГКЦ, про те, що телебачення та радіо не повинно допускати лихослів&rsquo;я в ефірі &ndash; нецензурної лексики, яка за останні часи стала широко використовуватись у молодіжних розважальних програмах, гумористичних проектах та іншій телепродукції.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У ході зустрічі Василь Костицький також поінформував про реалізацію положень <a href=\"http://www.irs.in.ua/index.php?option=com_content&amp;view=article&amp;id=88&amp;catid=34&amp;Itemid=61\" target=\"_blank\">Меморандуму про співробітництво</a> між Всеукраїнською Радою Церков і релігійних організацій та Національною комісією, підписаного у грудні 2008 року. Він запропонував низку проблемних питання, задля вирішення яких Національна комісія та релігійна спільнота може об&rsquo;єднати свої зусилля та поглибити співпрацю.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На завершення, учасники засідання запевнили керівництво Національної комісії, що&nbsp;Церкви та релігійні організації і надалі будуть послідовно підтримувати всі заходи, спрямовані на формування в Україні суспільства, яке складається з духовно і морально здорових та гармонійно розвинених особистостей.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em><a href=\"http://www.irs.in.ua/\" target=\"_blank\">http://www.irs.in.ua</a> </em></span></span></p>',NULL,NULL,1780,5,1,30);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2398,'P','2009-09-08',1,NULL,'Note','Під патронатом глави УГКЦ стартують благодійні концерти','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Тур став посланням УГКЦ сучасникам. Наші часи характеризуються кризою надії, повсякчасним ревізуванням вічних цінностей. Ми піддаємося різним інформаційним навіюванням, що спонукають нас до метушливого існування. Дехто може вважати, що успіх цих концертів залежить від того, як глибоко сягнемо у наші кишені. Але правдою є те, що він залежить від того, як широко відкриємо вуха нашого серця для сприйняття краси, для сприйняття духовних вартостей, які криються в красі&hellip; Аби ми з них вийшли кращими людьми&raquo;, &ndash; зазначив Блаженніший Любомир.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\r\n<p>Сьогодні в Луцьку за ініціативи Української греко-католицької церкви та під патронатом її глави Блаженнішого Любомира (ГУЗАРА) стартують благодійні концерти популярної музики &ldquo;З вірою і любов&rsquo;ю&rdquo;. Про це УНІАН повідомили в прес-службі УГКЦ</p>\r\n<p>Концерти триватимуть 7-22 вересня в 12-ти містах України.</p>\r\n<p>Як відомо, академічні концерти, об&rsquo;єднані такою ж назвою, з великим успіхом пройшли в травні. За словами глави УГКЦ, це була культурницька місія.</p>\r\n<p>&laquo;Тур став посланням УГКЦ сучасникам. Наші часи характеризуються кризою надії, повсякчасним ревізуванням вічних цінностей. Ми піддаємося різним інформаційним навіюванням, що спонукають нас до метушливого існування. Дехто може вважати, що успіх цих концертів залежить від того, як глибоко сягнемо у наші кишені. Але правдою є те, що він залежить від того, як широко відкриємо вуха нашого серця для сприйняття краси, для сприйняття духовних вартостей, які криються в красі&hellip; Аби ми з них вийшли кращими людьми&raquo;, &ndash; зазначив Блаженніший Любомир.</p>\r\n<p>Глава УГКЦ також додав, що травневий концертний тур став містком між українською і європейською музичною культурами, між українською культурою &ndash; класичною і сучасною. Ба більше, спротивом наступу масової культури в найгірших її зразках. А головне &ndash; це було спонукання до консолідації українців різних конфесій.</p>\r\n<p>Блаженніший сподівається, що в повсякденній метушні люди зможуть увійти в себе і відчути внутрішній спокій, внутрішнє задоволення, внутрішнє відчуття себе самих, яке повинне надати не тільки велике задоволення, а й момент духовного відпочинку і духовного підйому. Це, за його словами, &ldquo;основна думка&rdquo; щодо того, чого хоче досягнути УГКЦ.</p>\r\n<p>У концертах візьмуть участь відомі гурти, зокрема, &ldquo;Піккардійська терція&rdquo;, &ldquo;Друга ріка&rdquo;, &ldquo;СКАЙ&rdquo;, &ldquo;Гудімов&rdquo;, а також виконавці: Тарас ПЕТРИНЕНКО, Тетяна ГОРОБЕЦЬ, лідер гурту &ldquo;Мандри&rdquo; ФОМА, Ігор МІРОШНИЧЕНКО. Гурт &ldquo;Горгішелі&rdquo; спеціально пише псалом, який представить на цих концертах. Ведучою концертів буде відома актриса і телеведуча Наталка СУМСЬКА, яка також читатиме на концертах вірші.</p>\r\n<p>Потрапити на концерт можна буде за запрошеннями, які розповсюджуватимуться передовсім у парафіях УГКЦ та молодіжних, громадських організаціях і спільнотах. Кожне таке запрошення передбачатиме певну суму внеску на підтримку церкви &ndash; від 20 до 50 грн.</p>\r\n<p>Як зазначають організатори, зібрані на весняних та нинішніх концертах кошти підуть на завершення будівництва Патріаршого собору Воскресіння Христового в Києві. Під час академічних концертів у травні було зібрано близько 800 тис. грн.</p>\r\n<p>Крім Луцька, концерти вібуватимуться у таких містах: 8 вересня &ndash; Рівне; 9 вересня &ndash; Чортків (Тернопільщина), 12 вересня &ndash; Тернопіль, 13 вересня &ndash; Івано&ndash;Франківськ, 14 вересня &ndash; Чернівці, 15 вересня &ndash; Коломия (Івано&ndash;Франківщина), 16 вересня &ndash; Долина (Івано&ndash;Франківщина), 17 вересня &ndash; Стрий (Львівщина), 20 вересня &ndash; Брошнів (Івано&ndash;Франківщина), 21 вересня &ndash; Жовква (Львівщина), 22 вересня &ndash; Львів.</p>\r\n</span></span></p>\r\n<div style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">постiйна адреса статтi: <br /><a href=\"http://www.unian.net/ukr/news/news-334885.html\">http://www.unian.net/ukr/news/news-334885.html</a></span></span></div>',NULL,NULL,1781,5,1,30);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2399,'P','2009-09-12',1,NULL,'Note','Формаційні реколекції  о.Євгена Станішевського','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">26-27 вересня 2009 року </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">в Храмі Святого&nbsp;Василія&nbsp;Великого</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">в рамках БІБЛІЙНОГО АПОСТОЛЯТУ відбудуться</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">формаційні&nbsp;реколекції</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">о.Євгена&nbsp;Станішевського</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">для&nbsp;катехитів&nbsp;, координаторів і провідників Біблійних та</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">євангелізаційних&nbsp;служінь&nbsp;Київської&nbsp;Архиєпархії</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">та активних, небайдужих християн:</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">26-27 вересня 2009 року</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">в Храмі Святого&nbsp;Василія&nbsp;Великого</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">в рамках БІБЛІЙНОГО АПОСТОЛЯТУ відбудуться</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">формаційні&nbsp;реколекції</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">о.Євгена&nbsp;Станішевського</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">для&nbsp;катехитів&nbsp;, координаторів і провідників Біблійних та</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">євангелізаційних&nbsp;служінь&nbsp;Київської&nbsp;Архиєпархії</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">та активних, небайдужих християн:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Кошт участі у&nbsp;реколекціях&nbsp;&ndash; орієнтовно 70-80грн.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">(остаточна сума буде розрахована після попередньої реєстрації</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">учасників).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ця сума включає:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">участь у&nbsp;реколекціях&nbsp;, отримання матеріалів&nbsp;реколекцій&nbsp;,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">2 обіди продовж днів проведення&nbsp;реколекцій&nbsp;,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">фуршетний&nbsp;стіл з кавою, чаєм та печивом впродовж усіх&nbsp;реколекцій&nbsp;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">УВАГА!!!&nbsp;&nbsp; Реєстрацію продовжено до 14.09.09!!!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Контакти:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Лілії Гнатюк &hellip;&hellip;&hellip;&hellip;&hellip;&hellip;.... 8-050-330-79-34</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Володимиру&nbsp;Тимощуку&nbsp;&nbsp;&hellip;.&nbsp; 8-050-311-86-24</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вже записано 40 чоловік!!!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>',NULL,NULL,1782,5,1,41);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2400,'P','2009-09-08',1,NULL,'Note','У день нового церковного року увагу суспільства звертатимуть на екологію  ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">14 вересня 2009 року, у перший день нового церковного року, о 11.00, у Києві (вул. Урицького, 35) розпочнеться круглий стіл на тему &laquo;Екологія життя&raquo;, присвячений екологічному святкуванню Дня Творця.<br /></span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"border: 0px solid black; margin: 5px; width: 227px; height: 170px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_ekolohiya.jpg.jpg\" alt=\"\" />14 вересня 2009 року, у перший день нового церковного року, о 11.00, у Києві (вул. Урицького, 35) розпочнеться круглий стіл на тему &laquo;Екологія життя&raquo;, присвячений екологічному святкуванню Дня Творця. Організатори заходу &ndash; Міністерство охорони навколишнього природного середовища України та Українська Греко-Католицька Церква у партнерстві з Державною екологічною академією післядипломної освіти та управління.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З 1989 року з ініціативи Вселенського Константинопольського Патріарха Димитрія І у церквах православної, католицької та протестантської традицій розпочалися екологічні Дні молитви за сотворіння, щоб у день початку нового церковного року (1/14 вересня) звернути особливу увагу на охорону дарованого Богом довкілля. З того ж таки 1989 року в Європі розпочинає свою діяльність Європейська мережа екологічної співпраці християн (European Christian Environmental Network).</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Спадщина Чорнобиля, жертвами якого стали мільйони наших співгромадян, безрозсудне нищення зелених легень України &ndash; лісів, виснаження внаслідок екстенсивного способу господарювання національного скарбу &ndash; родючих чорноземів, нагромадження промислових відходів та сміття, забруднення повітря та водоймищ, що досягли критичної межі, еколого-правовий нігілізм &ndash; все це тільки деякі окремі складові глибокої екологічної кризи, яку переживає сьогодні Україна&raquo;, &ndash; наголошують організатори круглого столу. Вони інформують, що сьогодні 80% українців живуть у небезпечних зонах, тобто там, де забруднення повітря та води досягає критичного та небезпечного рівня для здоров\'я та життя; екологічний фактор відіграє вагому роль у зниженні рівня імунітету та розвитку різноманітних захворювань. У рамках круглого столу запропоновано обговорити таке питання: екологія навколишнього середовища, екологія людини та об\'єднання зусиль Церкви та Держави у цій сфері.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Під час круглого столу відбудеться презентація книги &laquo;Екологічний дороговказ. Маленькі поради для великих змін&raquo;, виданої Бюро УГКЦ із питань екології. Автор &ndash; Володимир Шеремета, керівник Бюро.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У заході візьмуть участь представники Міністерства охорони навколишнього природного середовища, Міністерства з надзвичайних ситуацій, Державної екологічної академії післядипломної освіти та управління, Української Греко-Католицької Церкви, традиційних християнських Церков та релігійних організацій України. &nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На завершення круглого столу будуть подані рекомендації учасників для дальшого інформування та можливого прийняття відповідних рішень на рівні відповідних міністерств та відомств.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Департамент інформації УГКЦ</span></span></p>',NULL,NULL,1784,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2401,'P','2009-09-09',1,NULL,'Note','Ющенко назвав передачу Народного дому в Перемишлі українській громаді жестом сильної волі','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Звертаючись до присутньої в храмі громади президент назвав передачу Народного дому в Перемишлі українській громаді жестом сильної волі, який тоталітарний режим ніколи б не зробив. Президент також подякував віруючим української громади за збереження пам&rsquo;яті полеглих героїв 20-40-х років минулого століття. Віктор Ющенко також наголосив, що знаючи правду про минуле, треба бути достойним сьогодення.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Президент України Віктор Ющенко 8 вересня відвідав Український народний дім у Перемишлі. Українських журналістів всередину не пустили, - інформує кореспондент ZAXID.NET з місця події.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перед тим Президент відвідав кафедральний собор святого Івана Хрестителя в Перемишлі, де передав ікону пратулинських мучеників митрополиту Перемишльсько-Варшавської митрополії УГКЦ Івану Мартинюку.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Звертаючись до присутньої в храмі громади президент назвав передачу Народного дому в Перемишлі українській громаді жестом сильної волі, який тоталітарний режим ніколи б не зробив. Президент також подякував віруючим української громади за збереження пам&rsquo;яті полеглих героїв 20-40-х років минулого століття. Віктор Ющенко також наголосив, що знаючи правду про минуле, треба бути достойним сьогодення.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Водночас член Об&rsquo;єднання українців Польщі, заступник голови Перемишльського відділення об&rsquo;єднання Анна Дрозд у коментарі журналістам зазначила, що українська громада отримала цю будівлю завдяки величезним зусиллям. Вона додала, що всі юридичні аспекти стосовно передачі будівлі будуть оформлені - коли не буде перешкод з боку міської влади та з огляду на майбутні президентські вибори у Польщі - протягом трьох місяців. \"Найпізніше, коли ми очікуємо повної передачі, - це початок наступного року. Українська громада орендує цю будівлю з 1956 року. Українська громада в Перемишлі налічує близько 3 тисяч осіб, загалом в місті живе 62 тис.осіб. Хоча я переконана, що 80% мешканців міста мають українське коріння. Будинок зараз є у віданні Міністерства внутрішніх справ і адміністрації Польщі. З боку держави всі необхідні документи є, справа за адміністрацією міста\", - зазначила Анна Дрозд.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вона також додала, що на відновлення будинку, який, за її словами, є у жахливому стані, потрібно близько 3 млн євро: \"Це, звичайно, початкові роботи. Ми розраховуємо на кошти з грантів ЄС. Хочемо тут зробити справжній осередок українства, зокрема, ресторан української кухні\", - зазначила Анна Дрозд.</span></span></p>\r\n<div style=\"text-align: justify;\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Довiдка ZAXID.NET</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Український народний дім було збудовано 1904 року з доброчинних пожертв і завдяки старанням адвоката Теофіля Кормоша. Після І Світової війни в цьому приміщенні розташовувалося театральне товариство \"Українська бесіда\", пізніше - товариство ім. Лесі Українки, товариство наукових викладів імені Петра Могили, головний осередок \"Просвіти\" і банк \"Міщанська каса\".</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1956 року українська громада орендує приміщення в цьому будинку. Зараз в будинку розташовані осередок Об&rsquo;єднання українців Польщі та Товариство \"Український народний дім\".</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</div>',NULL,NULL,1787,5,1,30);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2402,'P','2009-09-09',1,NULL,'Note','20 лет назад греко-католики вышли из подполья','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span id=\"font_size\">- УГКЦ &ndash; это наиболее репрессированная церковь. Но даже в те страшные времена не было большего сообщества, которое объединяло бы такое количество мужчин, женщин, детей, образованных колхозников. Это та общность, которая сказала системе: &laquo;Нет!&raquo;,- заявил Борис Гудзяк.</span></span></span></p>','<div style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"http://kp.ua/daily/author/\">Юлия ЛАСТИВКА</a></span></span></div>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span id=\"font_size\">\r\n<p align=\"justify\">В связи с этим событием в нашем городе состоится много интересных&nbsp; и познавательных мероприятий, которые, конечно же, не обойдут своим вниманием львовяне. Об этом на пресс-конференции сообщил журналистам Орест Фредына. В частности, участники празднования увидят документальный фильм &laquo;Наследие Андрея Шептицкого&raquo;, побывают на концерте популярной музыки, в котором примут участие &laquo;Пиккардийская терция&raquo;, &laquo;Друга ріка&raquo;, &laquo;Гудимов&raquo;, &laquo;Горгишели&raquo;, Тарас Петриненко и другие.</p>\r\n<p align=\"justify\">- УГКЦ &ndash; это наиболее репрессированная церковь. Но даже в те страшные времена не было большего сообщества, которое объединяло бы такое количество мужчин, женщин, детей, образованных колхозников. Это та общность, которая сказала системе: &laquo;Нет!&raquo;,- заявил Борис Гудзяк.</p>\r\n<p align=\"justify\">По словам Ореста Фредыны, кульминацией празднования станет Архиерейская Божественная Литургия, которая состоится 20 сентября, вблизи храма Св. Архистратига Михаила (ул. Винниченко,22). В общем молебне примут участие иерархи УГКЦ, духовенство и верующие разных епархий. Перед началом Богослужения во всех храмах Львовской Архиепархии УГКЦ&nbsp; все горожане услышат радостный перезвон. После Литургии состоится поход паствы улицами города к собору Св. Юра.</p>\r\n<p align=\"justify\">Орест Фредына попросил желающих&nbsp; прийти на богослужение в вышиванках, которые символизируют непокоренность духа украинского народа.</p>\r\n<p align=\"justify\">http://kp.ua/daily/</p>\r\n</span></span></span></p>',NULL,NULL,1788,5,1,27),
(2403,'P','2009-09-07',1,NULL,'Rss','Шкільний портфелик у Потіївці','<p>Парафіяни церкви св. Миколая, що на Аскольдовій Могилі разом з парафіяльними дітьми відвідали школу інтернат для неповносправних дітей, у селі Потіївка, Житомирської області. Діти парафіяльної служби милосердя разом з Орестою Цимбрівської приготували сиротам подарунки - портфелики, зошити, олівці, щоденники, ручки, лінійки, альбоми, фломастери та фарби для малювання. До цієї благодійної поїздки приєднався і Віктор Морозов, він влаштував для сиріт святковий концерт.</p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=431:shkilny-portfelyk-u-potijivci&catid=29:propovidi-cat&Itemid=34',1789,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2404,'P','2009-09-10',1,NULL,'Note','На міжнародній конференції в Бозе дискутують про «Духовну боротьбу в православній традиції»  ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Передусім учасники конференції зупинилися на вченні Святого Писання про духовну боротьбу, на співвідношенні між таїнством Святого причастя й духовною боротьбою, між очищенням душі (серця) і боротьбою з дияволом.<br /></span></span></p>','<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"border-style: solid; border-width: thin; margin: 5px; width: 227px; height: 170px; float: right;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_boze1_01.jpg.jpg\" alt=\"\" />9 - 12 вересня 2009 року в стародавньому монастирі Бозе на півночі Італії відбувається щорічна Міжнародна екуменічна конференція православної духовності. Організована монастирем конференція зібрала найбільш авторитетних фахівців у сфері богослов\'я й світської науки, ієрархів та вважається однією з найбільш ефективних &laquo;комунікативних площадок&raquo; для просування діалогу між східною й західною християнськими традиціями. Головне завдання конференції &ndash; аналіз основних принципів православного догматичного світогляду.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У конференції беруть участь представники різних християнських Церков: Католицької, Православних і Протестантських, а також світські вчені, вчені-богослови східної й західної традицій. Від Української Греко-Католицької Церкви у конференції бере участь д. д-р Ігор Шабан, голова Комісії УГКЦ для сприяння єдності між християнами.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"border: 1px solid black; margin: 5px; width: 227px; height: 170px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_boze2.jpg.jpg\" alt=\"\" />Конференція 2009 року продовжує розпочатий в 1993 році діалог між різними християнськими традиціями, богословами й світськими вченими. Темою цьогорічної конференції є &laquo;Духовна боротьба в православній традиції&raquo;.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Передусім учасники конференції зупинилися на вченні Святого Писання про духовну боротьбу, на співвідношенні між таїнством Святого причастя й духовною боротьбою, між очищенням душі (серця) і боротьбою з дияволом. Також учасники конференції мають намір звернутися до свідчень святих ХХ століття, які являють собою зразки відкритості на шляху любові до ближнього&raquo;, &ndash; з Бозе повідомив д. Ігор Шабан.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На завершення учасники конференції розглянуть питання своєї духовної боротьби заради єдності Церков у сучасному світі.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Більш докладно з матеріалами конференцій можна ознайомитися на офіційному сайті монастиря Бозе: <a href=\"http://www.monasterodibose.it/index.php/content/blogcategory/252/527/lang,en/\" target=\"_blank\">www.monasterodibose.it/index.php/content/blogcategory/252/527/lang,en/</a>.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Департамент інформації УГКЦ</span></span></p>',NULL,NULL,1790,5,1,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2405,'P','2009-09-10',1,NULL,'Rss','Звернення Блаженнішого Любомира','<blockquote>\n<div style=\"text-align: center;\"></div>\n<p style=\"text-align: center;\"><strong>Звернення<br /> Блаженнішого Любомира,<br /> Верховного Архиєпископа Києво-Галицького,<br /> до вірних Української Греко-Католицької Церкви <br /> та людей доброї волі з нагоди дня Бога-Творця</strong></p>\n<p style=\"text-align: right;\"><em>Узяв Господь Бог чоловіка й осадив його в Едемському саді<br /> порати його й доглядати його.</em><br />Бут. 2, 15</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=432:zvernennja-blazhennishogo-ljubomyra&catid=27:gk-v-ukrajini-cat&Itemid=57',1791,NULL,1,26),
(2406,'N','2009-09-13',0,NULL,'Note','','','<p>У церкві святого Миколая, що на Аскольдовій Могилі, також святкували цей празник. Божественну Літургію для дітей провели парох греко - католицької церкви святого Миколая отець Ігор Онишкевич та священик Роман Небожук. Атмосфера в церкві була надзвичайно піднесена, адже проповідь надихала парафіян на сприйняття цієї події з глибоким християнським змістом.</p>\r\n<p>Отець Ігор Онишкевич наголосив:</p>\r\n<p><img src=\"image,3276,200,111.jpg\" alt=\"\" width=\"160\" height=\"200\" />&laquo;Кожна наука має свій знак. Ось, наприклад, &nbsp;медицина символізує палиця на якій є змій. Чому саме так? Якось одному мудрецю розказали про тяжко хворого хлопчика. Його просили, щоб він навідався до дому того хлопця і допоміг йому. Мудрець вирішив допомогти юнаку. Він взяв свою палицю і пішов стежиною до дому хлопця. Але по дорозі на його палицю залізла змія. Він хотів вбити її, але побачив, що в неї в роті є якесь зілля. Він вийняв його і це зілля врятувало хворого юнака. Тому символом медицини є палиця зі змією.</p>\r\n<p>Символом нашого князя Володимира Великого був тризуб. На ньому він хотів знищити всіх своїх ворогів. Але після того, як Володимир повірив у Бога, він змінив свій знак, поставивши в центрі тризуба &nbsp;Хрест! Цим він хотів захистити свій народ від ворогів.</p>\r\n<p>Ми знаємо, що символом віри в Бога Ісуса Христа є Хрест. Ми повинні пам\'ятати про це та усім серцем сприймати цей символічний християнський знак.&raquo;</p>\r\n<p>Після Богослужіння відбулося миропомазування. Також отець Ігор зробив декілька оголошень про поїздку в Батурин та пригадав вірним &nbsp;розклад Богослужінь.</p>\r\n<p>Батьківський комітет того дня провів чергові збори, на яких вирішувались питання, пов\'язані з катехизацією дітей та відпочинком на природі.</p>\r\n<p>На малозабезпечених дітей чекав гарячий обід, про який подбали пані Ореста Цибрівська та Марія Бортнічук. До речі, ці обіди проводяться не тільки щонеділі, але й у інші дні тижня. Ця милосердна акція відбувається у рамках проекту &laquo;Діти Божого Милосердя&raquo;, завдання якого є огорнути малозабезпечених дітей увагою і добротою; вкласти в серце дитини віру, любов до матері, Батьківщини, рідної мови; сприяти в навчанні і здобутті фаху.</p>\r\n<p>Пообідавши, діти відпочивали біля церковної хатини. Відбулися ігри з настільного тенісу, бадмінтону.</p>\r\n<p>Матеріал підготував Олег Качур, &laquo;Оранта&raquo;</p>',NULL,NULL,1793,5,1,5);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2407,'P','2009-09-12',1,NULL,'Note','Комісія Всеукраїнської Ради Церков з питань соціального служіння','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Серед досягнень створеного Міжконфесійного ресурсного центру з питань ВІЛ/СНІД було відзначено організацію регулярних тренінгів для священнослужителів та активних вірних різних конфесій з питань протидії цій епідемії в Україні та методології надання допомоги інфікованим особам. Центр, за сприяння Комісії ВРЦіРО, ..</span></span></p>','<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">9 вересня 2009 року у Києві відбулося засідання Комісії з питань соціального служіння Всеукраїнської Ради Церков і релігійних організацій (ВРЦіРО). У ході зустрічі представники конфесій проаналізували підсумки роботи Комісії та заслухали звіт її виконавчого секретаря, священика Андрія Нагірняка про діяльність Комісії за період 2007 &ndash; 2009 рр. За цей час Комісія розробила Концепцію та Стратегію участі Церков та релігійних організацій України у протидії епідемії ВІЛ/СНІД, які були затверджені на засіданні ВРЦіРО у грудні 2007 року. Про це повідомляє <a href=\"http://www.irs.in.ua/\" target=\"_blank\">Інститут релігійної свободи, м.Київ</a>.&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Як зазначалось у звіті, за ініціативи Комісії ВРЦіРО з питань соціального служіння створено Міжконфесійний ресурсний центр з питань ВІЛ/СНІД, організаційну та фінансову діяльність якого координує Всеукраїнський благодійний фонд &laquo;Віра. Надія. Любов&raquo;. Комісія також затвердила кандидатуру священика Андрія Нагірняка на посаді Координатора міжконфесійної програми щодо ВІЛ/СНІДу, створила та підтримує діяльність спеціальної веб-сторінки.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Серед досягнень створеного Міжконфесійного ресурсного центру з питань ВІЛ/СНІД було відзначено організацію регулярних тренінгів для священнослужителів та активних вірних різних конфесій з питань протидії цій епідемії в Україні та методології надання допомоги інфікованим особам. Центр, за сприяння Комісії ВРЦіРО, провів також внутрішньоконфесійні тренінги для студентів духовних навчальних закладів та священнослужителів, а також декілька конференцій та нарад за участі представників Всеукраїнської Ради Церков і релігійних організацій, Міністерства охорони здоров\'я України, програм ООН в Україні та Росії, Міжнародного Альянсу з ВІЛ/СНІДу в Україні, Всеукраїнської мережі людей що живуть з ВІЛ, міжконфесійних товариств Білорусі та Росії.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За останні роки за ініціативи Міжконфесійного ресурсного центру з питань ВІЛ/СНІД було проведено 5 міжконфесійних регіональних зустрічей &ndash; у&nbsp; Донецьку, Севастополі,&nbsp; Запоріжжі, Дніпропетровську та Одесі. До кінця 2009 року планується проведення ще двох подібних зустрічей &ndash; у Полтаві та&nbsp; Харкові.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Заслухавши звіт, члени Комісії ВРЦіРО з питань соціального служіння визнали роботу Комісії та її виконавчого секретаря задовільною та переобрали представника Української Греко-Католицької Церкви, священика Андрія Нагірняка на цю посаду ще на два роки.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"http://www.irs.in.ua/\" target=\"_blank\">www.irs.in.ua</a></span></span></p>',NULL,NULL,1792,5,1,42);
