INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2508,'P','2010-01-29',1,NULL,'Rss','Велика Коляда','<p><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://www.askoldova-mohyla.org/uk/image,3419,200,IMG_4765_resize.JPG\" />У неділю 24 січня у церкві Василя Великого відбулась Велика Коляда, яку організував голова молодіжної комісії о.Віталій Марценюк. Ця коляда уже проводиться вдруге і має на меті зібрати всі молодіжні спільноти київської архієпархії для того, щоб разом відсвяткувати Різдво Ісуса Христа.&nbsp; Варто відмітити, що крім молодіжних спільнот Києва, у святкуванні взяла участь молодь із Фастова, Житомира. Розпочав коляду вітальним словом Владика Йосиф Мілян.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=479:velyka-koljada&catid=27:gk-v-ukrajini-cat&Itemid=57',1951,NULL,1,26),
(2509,'P','2010-01-27',1,NULL,'Rss','НОВИЙ РОМАН ПРО АВЕЛЯ ТА КАЇНА','<p>«Керівництво&nbsp; до дії одержала&nbsp; у Святому Письмі, у «Біблії», де часто наголошується на важких часах , які називаються&nbsp; «останніми днями» перед Господнім Судом: «Знай же&nbsp; ти це, що останніми днями настануть тяжкі часи. Будуть бо люди тоді самолюбні, грошолюбні, зарозумілі, горді, богозневажники, батькам неслухняні, невдячні… жорстокі, ненависники добра. Люди лихі та дурисвіти матимуть успіх у злому, зводячи&nbsp; й зведені бувши». 2-е послання святого апостола Павла до Тимофія. 3. 1-3. 13. «Біблія». (З духовного інтерв’ю&nbsp; «На полі бою добра і зла» Катерини Пасічної&nbsp;&nbsp; з журналісткою Іриною Лобовик. Газета «Я.Ти.Ми», 11 вересня 1997 р.)&nbsp; Новий роман&nbsp;&nbsp; \"Йди, Каїне, в державу&nbsp; Нод\" створено на Оболоні.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=480:novy-roman-pro-avelja-ta-kajina&catid=35:istorychni-postati-cat&Itemid=48',1952,NULL,1,26),
(2510,'P','2010-02-06',1,NULL,'Rss','Вчимося петриківському розпису на Аскольдовій Могилі','<p align=\"center\">Петриківський розпис -&nbsp; очима Андрія Масягіна – учня творчої майстерні Ганни Мастак</p>\n<p align=\"right\">Петриківський розпис не є достатньо відомий у світі. Але він кожного дня набуває популярності в Україні. Дізнаймося більше про цей чудовий стиль малювання. Відродимо петриківський розпис.</p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=481:vchymosja-petrykivskomu-rozpysu-na-askoldovi-mogyli&catid=25:parafija-cat&Itemid=27',1953,NULL,1,26),
(2511,'P','2010-02-06',1,NULL,'Rss','«Бог завжди з нами» - Оксана Ясніцька','<p align=\"right\"><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://www.askoldova-mohyla.org/uk/image,3428,200,4.JPG\" />«Як добре у родині життя своє прожити,</p>\n<p align=\"right\">Як добре у родині добро й біду ділити.</p>\n<p align=\"right\">І як радіє Мама, і як радіє Тато,</p>\n<p align=\"right\">Коли в своїй родині так діточок багато.</p>\n<p align=\"right\">Ми є родина, ми є родина,</p>\n<p align=\"right\">Ми є родина у Христі.»</p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=482:lbog-zavzhdy-z-namyr-oksana-jasnicka&catid=25:parafija-cat&Itemid=27',1954,NULL,1,26),
(2512,'P','2010-02-09',1,NULL,'Rss','Празник трьох Святителів','<p><img style=\"margin: 10px; float: right;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/097172721a.jpg\" width=\"132\" height=\"156\" />12 лютого 2010 року Празник трьох Святителів</p>\n<p>Коротка історія</p>\n<p align=\"justify\">Празник Трьох Святителів належить до найновіших празників грецької Церкви. Поштовхом до встановлення цього празника була велика суперечка у другій половині XI ст. про те, хто з них трьох є більш цінний для Церкви. Одні вище ставили святого Василія Великого, другі святого Григорія Богослова, а ще инші святого Йоана Золотоустого. Прихильники святого Василія звались василіяни, святого Григорія — григоріяни, а святого Йоана — йоаніти. Цей спір розв\'язали самі Святителі.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=484:praznyk-troh-svjatyteliv&catid=35:istorychni-postati-cat&Itemid=48',1956,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2513,'P','2010-02-09',1,NULL,'Rss','Духовна віднова для  молоді','<p><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3438,200,IMG_5259.JPG\" />Швидкий ритм життя в Києві часто не дає можливості зупинитись на хвильку перепочити відновити свої сили, та крім фізичного відпочинку нам ще потрібно дбати і про духовний зріст. У сестер служебниць Непорочної Діви Марії є хороша практика, на початку кожного місяця проводити духовну віднову. Цю практику вирішила перейняти і молодь із спільноти церкви св. Миколая Чудотворця і &nbsp;6 лютого відбулась у домі Сестер-Служебниць НДМ духовна віднова для молоді.\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=485:duhovna-vidnova-dlja-molodi&catid=25:parafija-cat&Itemid=27',1957,NULL,1,26),
(2514,'P','2010-02-10',1,NULL,'Rss','Зимовий відпочинок дітей Карітас-Печерськ у Карпатах','<p align=\"center\"><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3440,200,DSCN2959.JPG\" />Інтерв’ю з парохом греко – католицької церкви святого Миколая, що на Аскольдовій Могилі, отцем Ігорем Онишкевичем щодо зимового відпочинку дітей Карітас - Печерськ у Карпатах.</p>\n<p>- Отче Ігорю, скажіть, будь ласка, яка головна мета зимової поїздки в Карпати? З якою думкою Ви відправлялися в подорож?</p>\n<p>- Наша основна мета – закріплення катехитичного навчання дітей групи Карітас у недільній школі.\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=486:zymovy-vidpochynok-dite-karitas-pechersk-u-karpatah&catid=25:parafija-cat&Itemid=27',1958,NULL,1,26),
(2515,'P','2010-02-10',1,NULL,'Note','Діти в Карпатах!','<p><img style=\"float: left;\" src=\"image,3442,200,DSCF8293.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3442,200,DSCF8269.JPG\" alt=\"\" /></p>','<p><img style=\"float: left;\" src=\"image,3442,200,DSCF8293.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3442,200,DSCF8269.JPG\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,3442,200,DSCF8368.JPG\" alt=\"\" width=\"200\" height=\"150\" /><img style=\"float: right;\" src=\"image,3442,200,DSCF8356.JPG\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,3442,200,DSCN2926.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3442,200,DSCN2901.JPG\" alt=\"\" width=\"200\" height=\"150\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,3442,200,FEBRARY278.jpg\" alt=\"\" width=\"200\" height=\"150\" /><img style=\"float: right;\" src=\"image,3442,200,FEBRARY032.jpg\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,3442,200,DSCN3018.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3442,200,FEBRARY168.jpg\" alt=\"\" /></p>',NULL,NULL,1959,4,1,17),
(2516,'P','2010-02-10',1,NULL,'Rss','Засніжені гори Карпати зустріли дітей парафії церкви святого Миколая!','<p><strong>Щоденник відпочинку!</strong></p>\n<p><strong><span style=\"text-decoration: underline;\"><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3442,200,DSCF8368.JPG\" />4  лютого:</span></strong> <strong>Відпочинок з любов’ю для дітей!</strong></p>\n<p>О 18:00 діти з групи Карітас – Печерськ, що гуртуються у парафії святого Миколая, уже чекають на пероні біля вокзалу, щоб вирушити у довгоочікувану зимову поїздку в Карпати. Усі знають один одного, спілкуються, радіють, що скоро зможуть побачити і насолодитися катанням на засніжених горах України. Поїзд уже вирушає у Чернівці, тому діти моляться Богу, щоб гарно провести ці дорогоцінні дні відпочинку.</p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=487:zasnizheni-gory-karpaty-zustrily-dite-parafiji-cerkvy-svjatogo-mykolaja&catid=25:parafija-cat&Itemid=27',1960,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2517,'P','2010-02-14',1,NULL,'Rss','Послання Святішого Отця Бенедикта XVI на Великий Піст 2010 року ','<p>Дорогі брати й сестри!</p>\n<p>Щороку, з нагоди Великого Посту, Церква заохочує нас до щирого перегляду нашого життя у світлі євангельських повчань. Цього року я хочу поділитися з вами деякими роздумуваннями над розлогою темою справедливості, виходячи від ствердження святого Павла: <em>Божа справедливість появилася через віру в Христа</em> (пор. Рм&nbsp;3,21-22).</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=488:poslannja-svjatishogo-otcja-benedykta-xvi-na-velyky-pist-2010-roku-&catid=32:gk-svit-cat&Itemid=58',1963,NULL,1,26),
(2518,'P','2010-02-14',1,NULL,'Rss','РІЗДВО РАЗОМ','<p>Попри всі стихійні лиха та епідемію грипу ми родиною вирішили зустріти Різдво не вдома, на Харківщині, а на Західній Україні. Відчувши, що це є Божою волею, я зателефонувала на Стрийщину до пані Марії. Без будь-яких вагань, легко і радісно, ця берегиня великої родини (має двоє дітей і четверо онуків, а на п\'ятого чекають) запросила нас до себе не зважаючи ні на стихійні лиха, ні на епідемію. З пані Марією Николин ми познайомились в Києві на прощі. Зі сходу і заходу України їдуть прочани, віруючі різних конфесій, на освячену віками прощу до святої Софії.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=489:rizdvo-razom&catid=37:spilnota-cat&Itemid=49',1964,NULL,1,26),
(2519,'P','2010-02-14',1,NULL,'Rss','Стрітення Господа Бога і Спаса нашого Ісуса Христа','<img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/716d86efb4.jpg\" width=\"116\" height=\"145\" />\n<p>15 лютого 2010 року</p>\n<p>Коротка історія</p>\n<p align=\"justify\">Празник Стрітення засновується на події з життя нашого Спасителя, яку записав святий євангелист Лука (2, 22-40). Закон Мойсея приписував, щоб кожна жінка після народження дитини 40 днів не сміла входити до храму, бо в той час вона вважалася нечистою. Ці 40 днів називалися днями очищення.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=490:stritennja-gospoda-boga-i-spasa-nashogo-isusa-hrysta&catid=27:gk-v-ukrajini-cat&Itemid=57',1965,NULL,1,26),
(2520,'P','2010-02-15',1,NULL,'Rss','Празник Господнього Стрітення на Аскольдовій Могилі','<p><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3446,200,DSCN3025.JPG\" />«Радуйся благодатна, Богородице, бо з Тебе засяяло Сонце правди Христос Бог наш, який просвічує тих, що в темряві» (Тропар празника)</p>\n<p>«Різдвяний круг свят завершується празником&nbsp; Господнього Стрітення, що його святкуємо 15 лютого, сорокового дня по Христовому Різдві. Цей празник своїм змістом тісно зв’язаний з Христовим Різдвом. Бо як у Різдві, так і тут Христос при своїм&nbsp; пожертвувані у святині об’являє нам своє божество…</p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=492:praznyk-gospodnogo-stritennja-na-askoldovi-mogyli&catid=25:parafija-cat&Itemid=27',1966,NULL,1,26),
(2521,'P','2010-02-17',1,NULL,'Rss','Проповідь Святого Йоана Віаннея про покаяння ','<p align=\"right\">Тож покайтеся і наверніться, щоб ваші гріхи були стерті&nbsp; (Ді 3,19)</p>\n<m:defjc m:val=\"centerGroup\"> <m:smallfrac m:val=\"off\"> <m:dispdef> <m:lmargin m:val=\"0\"> <m:rmargin m:val=\"0\"> <m:defjc m:val=\"centerGroup\"> <m:wrapindent m:val=\"1440\"> <m:intlim m:val=\"subSup\"> <m:narylim m:val=\"undOvr\"> </m:narylim></m:intlim> </m:wrapindent>\n<p>Апостол Петро звинувачує євреїв у боговбивстві й невдячності стосовно до Ісуса Христа. Ті, що були присутні при цьому, починають плакати й кажуть: «Що маємо робити, щоб ми могли отримати прощення?» Святий Петро втішає їх і вказує, що єдина їхня надія й спасіння є в Ісусі Христі, Який помер за гріхи всіх людей і воскрес. Тож покайтеся і наверніться, щоб ваші гріхи буди стерті» (Дії 3,19).</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=494:propovid-svjatogo-oana-vianneja-pro-pokajannja-&catid=35:istorychni-postati-cat&Itemid=48',1968,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2522,'P','2010-02-16',1,NULL,'Note','РОЗКЛАД','<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3450,200,1.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,3450,200,3.jpg\" alt=\"\" /></span></span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Великопісні дні віднови на Аскольдовій могилі :<strong> <br /></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&laquo;Слово Боже живе і діяльне (Євр.4,12)&raquo;</strong></span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Великопісні дні віднови на Аскольдовій могилі :</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&laquo;Слово Боже живе і діяльне (Євр.4,12)&raquo;</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"text-decoration: underline;\"><img style=\"vertical-align: baseline;\" src=\"image,3450,200,2.jpg\" alt=\"\" width=\"200\" height=\"140\" /></span></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"text-decoration: underline;\">26 лютого</span></strong> п&rsquo;ятниця о 17:00 Вечірня, Хресна дорога.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"text-decoration: underline;\">18:00 Перша наука:</span></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Чи&nbsp; Бог сьогодні говорить до мене ? </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"text-decoration: underline;\">&nbsp;27 лютого</span></strong> субота о 08:00 Утреня, Поминальна Служба Божа, Панахида</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"text-decoration: underline;\">10:00 Друга наука:</span></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Чому прийшов Ісус ? (Мк 1,35-44)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">17:00 Вечірня</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"text-decoration: underline;\">18:00 Третя наука:</span></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Як бути щасливим з Божої точки зору (Лк 6,17-23).</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"text-decoration: underline;\">&nbsp;28 лютого</span></strong> неділя о 08:15, 10:00, 12:30 Служба Божа</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"text-decoration: underline;\">Проповіді:</span></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;Небачене, але можливе завдяки Ісусові (Мк2,1-12). </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3450,200,3.jpg\" alt=\"\" width=\"200\" height=\"123\" /> Реколекції проведе о. Юрій Щурко, керівник духовно &ndash; пасторально&nbsp; відділу УКУ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>',NULL,NULL,1967,4,1,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2523,'P','2010-02-20',1,NULL,'Note','Розклад Богослужень','<p style=\"text-align: left;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Розклад Богослужень на другий тиждень Великого посту</span></span></strong><span style=\"font-size: small;\"></span></p>\r\n<p style=\"text-align: left;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">22 (09) понеділок. 2-ий тиждень Великого Посту. Святого мученика Никифора.</span></span></p>\r\n<p style=\"text-align: left;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8-30 6-тий Час Літургія Святого Івана Золотоустого. </span>Панахида</span></p>\r\n<p style=\"text-align: left;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18-00&nbsp;&nbsp; Літургія Святого Івана Золотоустого Панахида</span></span></p>','<p><strong><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Розклад Богослужень на другий тиждень Великого посту</span></span></strong></p>\r\n<p><strong><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">22 (09) понеділок</span></span></strong></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">2-ий тиждень Великого Посту.Святого мученика Никифора.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8-30 6-тий Час Літургія Святого Івана Золотоустого. </span>Панахида</span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18-00&nbsp;&nbsp; Літургія Святого Івана Золотоустого. Панахида<br /></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>23 (10) вівторок</strong> Святого мученика Харалампія.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8-00 6-тий Час Літургія Святого Івана Золотоустого. </span>Панахида</span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18-00&nbsp;&nbsp; Літургія Святого Івана Золотоустого Панахида</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>24 (11) середа</strong> Святого священомученика Власія, єпископа Севастійського.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8-30 6-тий Час &nbsp;Вечірня з Літургією Напередосвячених Дарів.Прастас-сорокоусти.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18-00 Вечірня з Літургією Напередосвячених Дарів Акафіст Страстям Господнім<br /><br /></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>25 (12) четвер </strong>Святого отця нашого Мелетія, архиєпископа Антіохійського.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8-30 6-тий Час Літургія Святого Івана Золотоустого. </span>Панахида</span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18-00&nbsp;&nbsp; Літургія Святого Івана Золотоустого Панахида</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>26 (13) п&rsquo;ятниця</strong> Преподобного отця нашого Мартинія</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8-30 Вечірня з Літургією Напередосвячених Дарів Парастас-Сорокоусти</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">17-00 Повечір&rsquo;я Хресна дорога Реколекції 1ша наука </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /><strong>27 (14) субота </strong>Субота Заупокійна. Преподобного отця нашого Авсентія.<br />І переставлення преподобного отця нашого Константина філософа, у монахах Кирила, учителя слов&rsquo;янського. І преподобного Марона, пустинника, чудотворця.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;8-00На Утреня</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">9-00 Літургія Святого Івана Золотоустого.Панахида</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">10-30&nbsp; Реколекції 2га наука</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На Літургії: Все тільки Кирилові;&nbsp; Апостол і Євангеліє спочатку дня, а потім Кирилові. Служба Суботи Заупокійної пропускається.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">17-00 Літургія Святого Івана Золотоустого&nbsp; Реколекції 3тя наука</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>28 (15) неділя (гл.5&nbsp; Єв.5) </strong>2-га Великого Посту. Святого апостола Онисима. <br /></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8-15 10-00 12-30 Літургія Святого Василія Великого.<br />Проповіді реколентанта на кожній Літургії</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Телефон для довідок: (044) 570 32 90, (050) 4473157</span></span></p>',NULL,NULL,1969,4,1,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2524,'P','2010-02-23',0,NULL,'Note','«Я, йду, Христе»','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Запрошуємо прихожан греко-католицької громади&nbsp; відвідати Національний центр імені&nbsp; Леся Курбаса (м.Київ,Володимирська,23В) <strong>1</strong><strong> березня о 19:00</strong>. Підтримаймо театр зі Львова і виставу <strong>&laquo;</strong><strong>Я, йду, Христе</strong><strong>&raquo;.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вистава створена за книгами Григора Меріям -Лужницького &laquo;Дванадцять листів о. Андрія Шептицького до матері&raquo; і мемуарами &laquo;Молодість та покликання о. Романа Шептицького&raquo;, написані матір&rdquo;ю митрополита&nbsp;Софією&nbsp;Шептицькою&nbsp;з роду Фредрів.</span></span></p>','<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Запрошуємо усіх&nbsp; відвідати Національний центр імені&nbsp; Леся Курбаса (м.Київ,Володимирська,23В) <strong>1</strong><strong> березня о 19:00</strong>. Підтримаймо театр зі Львова і виставу <strong>&laquo;</strong><strong>Я, йду, Христе</strong><strong>&raquo;.</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Виставу благословив Блаженніший Любомир Кардинал Гузар Верховний Архієпископ Києво-Галицький УГКЦ. </strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вистава створена за книгами Григора Меріям -Лужницького &laquo;Дванадцять листів о. Андрія Шептицького до матері&raquo; і мемуарами &laquo;Молодість та покликання о. Романа Шептицького&raquo;, написані матір&rdquo;ю митрополита&nbsp;Софією&nbsp;Шептицькою&nbsp;з роду Фредрів.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В її основі вистави покладено матеріал із книги Григора Лужницького&nbsp;&ldquo;Дванадцять листів отця Андрея Шептицького до матері&rdquo; (художній твір на основі автентичних листів) та мемуари &ldquo;Молодість і покликання Романа Шептицького&rdquo;, написані матір&rsquo;ю митрополита Софією Шептицькою з роду Фредрів.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Режисер&nbsp;-&nbsp; <strong>Ірина Волицька</strong> -заслуж. діяч мистецтв України</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Софія ( мати Андрія Шептицького)&nbsp;- заслужена артистка України, лауреат премії ім. Котляревського - &nbsp; <strong>Лідія Данильчук</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Андрій&nbsp;Шептицький <strong>-&nbsp;Володимир Губанов</strong> - актор львівського театру &bdquo;Воскресіння&rdquo;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Тривалість &ndash; 1год. 20 хв.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Всім бажаючим завжди радий творчий колектив театру \"У кошику\", всі, хто не в змозі оплатити квитки приходьте, вам завжди ми знайдемо місце.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дивовижна гра професійних акторів,&nbsp; щоб не пройшла у пустій залі.</span></span></p>',NULL,NULL,1971,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2525,'P','2010-02-23',1,NULL,'Rss','Ділитись світлом нашої віри… Родина Галуга','<p align=\"right\"><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3453,200,22.JPG\" />«Діти – як квіти: поливай, то ростимуть».</p>\n<p>Петро Галуга виховує разом з дружиною Оксаною двох діточок Богданка і Олексійка. У Києві родина живе з 2001 року. А на Аскольдовій Могилі ми маємо змогу бачити їх завжди у гарному настрої та у чудових вишитих українських сорочках.</p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=496:dilytys-svitlom-nashoji-viry-rodyna-galuga&catid=25:parafija-cat&Itemid=27',1972,NULL,1,26),
(2526,'N','2010-02-23',0,NULL,'Note','40 днів пам‘яті спочилого раба Божого Івана Рибалка.','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Поминальна служба буде відслужена у церкві св.. Миколая Чудотворця на Аскольдовій Могилі у п&rsquo;ятницю. 26 лютого. о 10 годині. </span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Поминальна служба буде відслужена у церкві св.. Миколая Чудотворця на Аскольдовій Могилі у п&rsquo;ятницю. 26 лютого. о 10 годині. Рідні, друзі. усі. хто пам&rsquo;ятав Івана Рибалка матимуть можливість помолитися за упокій його душі.</span></span></p>',NULL,NULL,1973,5,1,17),
(2527,'P','2010-02-23',1,NULL,'Rss','Дитина дарунок Божий!','<p>* Спіраль убиває дітей! При її вживанні зачаття відбувається, але дитина найчастіше вмирає в перші дні, тому що спіраль не дозволяє їй імплантуватися (закріпитися) в матці. Якщо відбувається затримка менструації, то це значить, що дитина все таки вижила. Треба зберегти їй життя. Спіраль перетворює здорову жінку в хвору (запалення і нагноєння в матці, яйцеводах, черевній порожнині, кровотечі, болі, недокрів\'я...).</p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=497:dytyna-darunok-bozhy&catid=37:spilnota-cat&Itemid=49',1974,NULL,1,26),
(2528,'P','2010-02-28',1,NULL,'Rss','«Усе знесла й перемогла його любові сила. Того великого вогню і смерть не погасила.»','<p align=\"center\"><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3460,200,DSCN1110.JPG\" />Інтерв’ю з пані Русланою Дмитровською - головним організатором Шевченківського свята, що планується провести 14 березня у церкві святого Миколая, що на Аскольдовій Могилі.</p>\n<p align=\"right\">Незабаром визначна дата - 9 березня – день народження Тараса Григоровича Шевченка. Вже багато поколінь українці згадують про цей день і пошановують великого поета – творця української національної ідеї. Він боровся за волю українського народу, пишучи справедливі, і водночас чутливі вірші, які закарбувалися у пам’яті нашого народу.</p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=498:luse-znesla--peremogla-ogo-ljubovi-syla-togo-velykogo-vognju-i-smert-ne-pogasylar&catid=25:parafija-cat&Itemid=27',1978,NULL,1,26),
(2529,'N','2010-03-01',0,NULL,'Photos','Піст - весна для душі!','<p>Наші парафіяни за прикладом Євангельських товаришів, що через стелю опустили розслабленого до ніг Ісуса, який оздоровив його поспішили сьогодні в перший день весни&nbsp;зробити добре діло. Ірина і Володимир допомогли коштами придбати ліки для тяжкохворої&nbsp; Ольги з Чернігівської установи. Вона пише нам, що має дуже низький&nbsp; гемоглобін, лікарі говорять, що без ліків не зможе вижити. Найперши просить пігулки тітанол. Вона дуже хоче повернутись до своїх дітей, яких залишилось троє дітей під опікою рідних. Також просить вислати вервицю для молитви.</p>','<p>Наші парафіяни за прикладом Євангельських товаришів, що через стелю опустили розслабленого до ніг Ісуса, який оздоровив його поспішили сьогодні в перший день весни&nbsp;зробити добре діло. Ірина і Володимир допомогли коштами придбати ліки для тяжкохворої&nbsp; Ольги з Чернігівської установи. Вона пише нам, що має дуже низький&nbsp; гемоглобін, лікарі говорять, що без ліків не зможе вижити. Найперши просить пігулки тітанол. Вона дуже хоче повернутись до своїх дітей, яких залишилось троє дітей під опікою рідних. Також просить вислати вервицю для молитви.</p>\r\n<p>Волонтери Карітасу за кошти придбали ліки і вислали посилку загальною вартістю 170 грн.. Також сьогодні для лікарні №11, терапевтичне віділення було передано гумунітарну допомогу памперси для дорослих і рідке мило 24 кг..</p>\r\n<p>Все це стало можливим завдяки старанням волонтерів Карітас - Печерськ п. Антоніни, п. Зоряни, п. Михайла.</p>\r\n<p>Ділами милосрдя оздоровляється наша душа, радіє весні, очікує Паску.</p>\r\n<p>Матеріал Ільї</p>',NULL,NULL,1979,4,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2530,'P','2010-02-27',1,NULL,'Note','Слово Твоє «світильник»! – реколекції','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,3460,200,CRW_6535.jpg\" alt=\"\" />26 лютого 2010 р. Божого у церкві св. Миколая Чудотворця на Аскольдовій Могилі розпочались ре колекції про Слово Боже. До чисельної громади проповідував отець Юрій, викладач УКУ. У своїй науці він наголосив &ndash; Бог присутній у Євхаристії і своєму Слові. Ми не можемо сприймати Слово поверхнево, ми маємо слухати, чути і розуміти Слово Боже. Адже Його Слово &ndash; це дія.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3460,200,CRW_6531.jpg\" alt=\"\" />26 лютого 2010 р. Божого у церкві св. Миколая Чудотворця на Аскольдовій Могилі розпочались ре колекції про Слово Боже. До чисельної громади проповідував отець Юрій, викладач УКУ. У своїй науці він наголосив &ndash; Бог присутній у Євхаристії і своєму Слові. Ми не можемо сприймати Слово поверхнево, ми маємо слухати, чути і розуміти Слово Боже. Адже Його Слово &ndash; це дія.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Що є Боже Слово? Слово Боже стоятиме повіки. Послав своє Слово і вилікував, Слово Твоє &laquo;світильник&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ми чуємо Слово Боже і непереймаемось ним, не віримо йому. Тому ходимо сумні і нещасні.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,3460,200,CRW_6535.jpg\" alt=\"\" />З Божим Словом все повстало. Бог вимагає від нас все! Тобто, наше серце, нашу повноту. Коли ми довіримось Богу все наповниться радістю.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Отець Юрій подасть три науки, які будуть наповняти нас і наше серце Словом Божим.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Запрошуємо Вас прийти в суботу о 10:30 і 18:00&nbsp; щоби укріпити свою віру.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Матеріал Іллі.</span></span></p>',NULL,NULL,1977,5,1,17),
(2531,'P','2010-03-02',1,NULL,'Rss','УГКЦ вперше відзначила День пам‘яті абортованих дітей  ','<p><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_za_jyttya_04.jpg.jpg\" /></p>\n<p>27 лютого в Українській Греко-Католицькій Церкві відзначали День пам‘яті абортованих дітей. Цей захід проходив у рамках Програми протидії абортам в Україні, затвердженої на <a href=\"http://www.ugcc.org.ua/860.0.html\" class=\"internal-link\">Сорок шостій сесії Синоду Єпископів Києво-Галицького Верховного Архиєпископства УГКЦ</a>, що проходив 13-14 вересня 2009 року.&nbsp;</p>\n<p>«Наша Церква постійно плекає різні ініціативи, зокрема, у питанні захисту життя від моменту зачаття. Яскравим прикладом плоду діяльності у цьому напрямку є прийняття Синодом «Програми протидії абортам в Україні». Це подія, яка свідчить про входження у новий етап виконання місії служіння життю», ‒ розповідає п. Степан Дмитришин, голова Комісії УГКЦ у справах душпастирства охорони здоров’я.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=499:ugkc-vpershe-vidznachyla-den-pamjati-abortovanyh-dite-&catid=27:gk-v-ukrajini-cat&Itemid=57',1980,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2532,'P','2010-03-03',1,NULL,'Note','«Київські лекції УКУ 2009-2010»','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">4 та 5 березня до Києва з двома лекціями на дві різні теми завітає проф. Джефрі Вілз - засновник Української католицької освітньої фундації в Канаді, п\'ять років пропрацював віце-ректором УКУ, засник понад десятка найрізноманітніших благодійних проектів в Україні.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорогі Друзі!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Український Католицький Університет та Комітет Приятелів УКУ в Києві має приємність запросити Вас на уже шосту по рахунку лекцію в рамках проекту &laquo;Київські лекції УКУ 2009-2010&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">4 та 5 березня до Києва з двома лекціями на дві різні теми завітає проф. Джефрі Вілз - засновник Української католицької освітньої фундації в Канаді, п\'ять років пропрацював віце-ректором УКУ, засник понад десятка найрізноманітніших благодійних проектів в Україні.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">4 березня &nbsp;о 19.40 в аудиторії No.207* Інституту Релігійних Наук Св. Томи Аквінського. &lt;http://www.it.dominic.ua/uk/2008-12-17-22-41-20&gt;(Вул. Якіра, 13.М. Лукянівська) відбудеться лекція на тему: \"What is the State of Fundraising in Ukraine Today?\" Лекція відбуватиметься англійською мовою з синхронним перекладом</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">5 березня о.19.15 уже традиційно в конференц-залі церкви Святого Василія Великого (Вул. Вознесенський узвіз, 7, просимо заходити з бокового входу, що знаходиться з правої сторони церкви) відбудеться ще одна лекція під назвою: \"Good news:* *Greek-Catholic Projects for Orphans\" Лекція відбуватиметься англійською мовою з синхронним перекладом</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Щиро запрошуємо всіх зацікавлених та просимо допомогти у поширені цієї інформації. Афіші дивіться в додатку, з усіма питаннями та побажаннями не вагайтеся звертатися до Київського представництва УКУ за тел. 044-425-39-37 або 097-758-12-55.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Статтю про попередні лекції, котрі в Києві читав *о.др. Ігор Бойко 15-16 лютого читайте тут &lt;http://ucu.edu.ua/ukr/community/article;4570%20&gt;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Хай щастить!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Галя Шостак. Керівник проектів Київського представництва Українського Католицького Університету.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">вул. Хорива, 33/20, Київ, 04070, Україна, тел./факс: 38 (044) 425-39-37, моб.: 38 097 758 12 55, веб: www.ucu.edu.ua; www.supporting.ucu.edu.ua</span></span></p>',NULL,NULL,1983,5,1,41);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2533,'N','2010-03-03',1,NULL,'Note','Лист надії!','<p>Получила ваше письмо, большое вам спасибо. А то что нет финансовой помощи, то на это все значит воля Божия. Мне остается только молиться и уповать на Бога. А так же за все благодарить Бога. Ведь нам людям кажется, что с нами произошла плохая ситуация и мы ее не понимаем, но Бог оборачивает эту ситуацию нам во благо. Нам не обязательно своим человеческим разумом понимать, что делает Бог. Поэтому я подчиняюсь полностью воле Бога.</p>','<p>Слава Исусу Христу!!!</p>\r\n<p>День добрый,&nbsp; отец Игорь!</p>\r\n<p>Мир и благодать вам от Господа Исусу Христа!</p>\r\n<p>&nbsp;</p>\r\n<p>Получила ваше письмо, большое вам спасибо. А то что нет финансовой помощи, то на это все значит воля Божия. Мне остается только молиться и уповать на Бога. А так же за все благодарить Бога. Ведь нам людям кажется, что с нами произошла плохая ситуация и мы ее не понимаем, но Бог оборачивает эту ситуацию нам во благо. Нам не обязательно своим человеческим разумом понимать, что делает Бог. Поэтому я подчиняюсь полностью воле Бога. Уверовала я здесь в колонии, хотя пересматривая свою жизнь, понимаю, что Бог стучался мне в сердце уже очень давно, только я не хотела открывать для него сердце. Погрязла в своих грехах: блуде, наркотиках итд., итп., не хотела ничего видеть, и никого слушать, в своей гордости думала, что я сама все знаю&hellip; Но я рада что сейчас нахожусь здесь, в колонии, Бог сохранил мою жизнь, ведь я могла бы уже гореть в аду. Мало того, что я живу, у меня еще есть дочь. Слава Господу!!!</p>\r\n<p>&nbsp;</p>\r\n<p>Я Бога благодарю, что он нашел меня в этой колонии, омыл, очистил и ведет свои путем. Я никогда не думала, что смогу измениться и поменять свои взгляды, а тем более иметь свободу от наркотиков. Вся слава и величание за это нашему Господу!!! И сейчас душа моя поет и славит Бога.</p>\r\n<p>&nbsp;</p>\r\n<p>Моей Серафиме было 5 февраля, 1 год. Я постоянно молюсь за нее, ведь молитва матери сильна.</p>\r\n<p>&nbsp;</p>\r\n<p>Я также прошу вас молиться за моего сына Диму, ему 21 год, что бы Господь привел его к покаянию, а так же примирил его со мной. Хотя и внешне у нас отношения хорошие, но глубоко в сердце он имеет на меня обиду. А самое главное, чтобы Господь уберег его от греха, из-за тяжелого финансового положения, он рылся в интернете и нашел людей занимающихся продажей органов и надумал продать свою почку, но Слава Богу у него срывались встречи с этими людьми. Он отчаялся, потому что работы найти не может, в связи с кризисом, везде сплошной обман, 2 фирмы его обманули, после сделанной работы ему не заплатили. Так что прошу вас очень молитесь за него, об устройстве жизни.</p>\r\n<p>18.02.10 С любовью Елена и Серафима.</p>\r\n<p>&nbsp;</p>\r\n<p>P.S. Будьте Благословенны и простите, если я что-то не так написала.</p>\r\n<p>&nbsp;</p>\r\n<p>Дорогі парафіяни!<br />Це лист п. Олени з вязниці. Ви бачите її навернення і її потреби. В часі посту, хто забажає надати допомогу матері Олені і її дочці Серафимі, то зверніться до мене. Ми готуємо для них Пасхальний кошик радості, а саме: для дитини підгузники, харчування, їжу, одяг. Для мами: продукти харчування, одяг, гігієна.</p>\r\n<p>Хто бажає написати лист і поспілкуватись с п. Оленою перешліть на наш церковний имейл.</p>\r\n<p>Ваш священик Ігор!!!!</p>',NULL,NULL,1984,4,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2534,'P','2011-04-08',0,NULL,'Note','','<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Молитва до св. Тадея</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">О святий Юдо-Тадею, що був гідний належати до родини Спасителя, Пресвятої Діви Марії і Святого Йосифа, прибігаю до Тебе через Найсвятіше Серце Ісуса, хочу приносити Богу славу за ласки, Тобі уділені. Впавши на коліна, прошу Тебе покірно через Найсвятіше Серце Ісуса, споглянь на мене милосердно, не погорди моєю смиренною просьбою, щоб довір&rsquo;я не було захитане. Тобі Бог уділив ласки, що приходиш людям з допомогою в безнадійних обставинах. О прийди Ти мені з поміччю, щоби я міг (могла) милосердя Боже звеличати вовік-віків. Амінь.<br />Ціле своє життя буду тобі вдячний (<em>вдячна</em>) і залишуся ревнішним (<em>ревнішою</em>) Твоїм (<em>Твоєю</em>) почитателем (<em>почитателькою</em>).<br />Святий Тадею, особливий Опікуне в безнадійному положенні, молися за нами. Амінь.</span></span></p>','',NULL,NULL,1987,5,1,50),
(2535,'P','2010-03-08',0,NULL,'Note','Великопісні реколекції для молоді','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #ff0000;\"><strong><span lang=\"UK\">27-28 березня 2010 р.Б.</span></strong></span><span lang=\"UK\">, в Києві, при храмі св. Василія Великого на Львівській площі (<span style=\"color: #ff0000;\"><strong>Вознесенський узвіз 7</strong></span>), відбудуться великопісні реколекції для молоді. Тема реколекцій - &laquo;Розважання про молитву св. Єфрема Сиріна&raquo;. Провадить &ndash; о. Василь Поточняк.</span></span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>27-28 березня 2010 р.Б.</strong>, в Києві, при храмі св. Василія Великого на Львівській площі (<strong>Вознесенський узвіз 7</strong>), відбудуться великопісні реколекції для молоді. Тема реколекцій - &laquo;Розважання про молитву св. Єфрема Сиріна&raquo;. Провадить &ndash; о. Василь Поточняк. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">А в Квітну неділю буде урочисто святкуватись архиєпархіяльний день молоді. В рамках святкування молодь візьме участь у Божественній Літургії, зустрінеться з владикою Йосифом Міляном та огляне виставу &laquo;Хресна дорога&raquo; у виконанні театральної студії &laquo;Воскресіння&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Розклад реколекцій</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Субота, 27 березня 2010 р.Б.<br /> </strong>(<em>Монастир ЧСВВ, Львівська площа, вул. Вознесенський узвіз 7</em>)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>10.00</strong> &ndash; Реєстрація учасників<br /><strong>11.00</strong> &ndash; Божественна Літургія<br /><strong>12.00</strong> &ndash; Кава-чай<br /><strong>12.30</strong> &ndash; І Наука<br /><strong>13.30</strong> &ndash; Обід<br /><strong>14.30</strong> &ndash; ІІ Наука<br /><strong>15.45</strong> &ndash; Праця в групах<br /><strong>17.00</strong> &ndash; Кава-чай<br /><strong>17.15</strong> &ndash; Ділення<br /><strong>18.00</strong> &ndash; Вечірня<br /><strong>19.00</strong> &ndash; Вечеря<br /><strong>20.00</strong> &ndash; Нічні чування<br /><strong>22.00</strong> &ndash; Виїзд до &laquo;Карітасу&raquo; (для некиян)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Неділя, 28 березня 2010 р.Б.</strong><br /> (<em>Патріарший Собор Воскресіння Христового // Виставковий центр - Метро &laquo;Лівобережна&raquo;, вул. Микільсько-Слобідська 5</em>)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>9.00</strong> &ndash; Ранкова молитва<br /><strong>10.00</strong> &ndash; Виїзд з &laquo;Карітасу&raquo; (для некиян)<br /><strong>11.00</strong> &ndash; Архиєрейська Божественна Літургія<br /><strong>13.00</strong> &ndash; Зустріч молоді з вл. Йосифом Міляном (Виставковий центр)<br /><strong>14.00</strong> &ndash; Театральне дійство (театр &laquo;Воскресіння&raquo; м. Львів)<br /><strong>14.30</strong> &ndash; Обід, роз&rsquo;їзд</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em><strong>Прес-служба Київської архиєпархії</strong></em></span></span></p>',NULL,NULL,1988,5,1,41);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2536,'P','2010-03-08',0,NULL,'Note','Розважання','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-weight: bold;\">&hellip;Побачивши ж народ&hellip;</span> Також сьогодні приходить до наших Церков багато народу. Перш за все в неділю. Під час тижня, лише деякі знайдуть час, щоб зайти за Ісусом. Ніби Йому хочемо сказати, що в щоденних ситуаціях Його не потребуємо. Він бажає щоденно крокувати з нами. Дозвольмо Йому це. </span></span></p>','<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-weight: bold;\">14 березня, неділя (4-та Посту) &ndash; Мт 4,25-5,12</span></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-weight: bold;\"> Препмуч. Євдокії</span></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-weight: bold;\">&nbsp;</span></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-weight: bold;\">А йшла за ним велика сила людей з Галилеї, з Десятимістя, з Єрусалиму, з Юдеї та з Зайордання. Побачивши ж народ, зійшов на гору. І коли він сів, підійшли до нього його учні; а він, відкривши уста, почав навчати їх: </span></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-weight: bold;\"> &ldquo;Блаженні вбогі духом, бо їхнє Царство Небесне.</span></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-weight: bold;\"> Блаженні тихі, бо вони успадкують землю.</span></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-weight: bold;\"> Блаженні засмучені, бо будуть утішені.</span></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-weight: bold;\"> Блаженні голодні та спраглі справедливости, бо вони наситяться.</span></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-weight: bold;\"> Блаженні милосердні, бо вони зазнають милосердя. </span></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-weight: bold;\"> Блаженні чисті серцем, бо вони побачать Бога.</span></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-weight: bold;\"> Блаженні миротворці, бо вони синами Божими назвуться.</span></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-weight: bold;\">&nbsp;</span></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-weight: bold;\"> Блаженні переслідувані за правду, бо їхнє Царство Небесне.</span></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-weight: bold;\"> Блаженні ви, коли вас будуть зневажати, гонити та виговорювати всяке лихо на вас, обмовляючи мене ради.</span></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-weight: bold;\"> Радійте й веселіться, бо нагорода ваша велика на небі; так бо переслідували пророків, які були перед вами&rdquo;.</span></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-weight: bold;\">&nbsp;</span></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-weight: bold;\"><br />&hellip;Побачивши ж народ&hellip;</span> Також сьогодні приходить до наших Церков багато народу. Перш за все в неділю. Під час тижня, лише деякі знайдуть час, щоб зайти за Ісусом. Ніби Йому хочемо сказати, що в щоденних ситуаціях Його не потребуємо. Він бажає щоденно крокувати з нами. Дозвольмо Йому це. <br /><span style=\"font-weight: bold;\">&hellip;зійшов на гору</span>&hellip;Кожен з нас мав би у своєму житті знайти &laquo;гору&raquo;, місце, з котрого є близько до Бога. Може це бути куточок кімнати в хаті, в гуртожитку, за столом на роботі&hellip; Найважливіше є повернути своє серце в напрямку до Бога та сказати Йому коротко, своїми словами, який Він для нас важливий, що ми би хотіли Його любити, що бажаємо належати Йому. <br /><span style=\"font-weight: bold;\">&hellip;&ldquo;Блаженні вбогі духом, бо їхнє Царство Небесне</span>&hellip;Блаженства теоретично знає майже кожен з нас. Це норма, котра від часів Ісуса не змінилася. Бути вбогим духом, плачучим, тихим&hellip;, це тепер не є модним. Надавати Богу перше місце у своєму серці дуже важко, бо пропозицій навколо нас дуже багато. Страждати з любові до Бога, коли навколишній світ радіє, це вже понад наші сили. Бути справедливим там, де крадуть, не дають зарплату, чи це можливо? В теперішньому світі приймають інші цінності. Але наперекір всім нашим &laquo;неможливо&raquo;, блаженства є точною нормою для отримання неба. <br /><br /><span style=\"font-weight: bold;\">Господи, вже сьогодні хочу сповнювати Твої слова. </span></span></span></p>',NULL,NULL,1989,5,1,40);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2537,'P','2010-03-09',1,NULL,'Rss','Глава УГКЦ закликав вірних молитися за державу та її владу не тільки перед виборами, а й упродовж цілого року  ','<p><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_vid_malka.jpg.jpg\" width=\"94\" height=\"126\" />Днями вийшло Звернення Блаженнішого Любомира до вірних Української Греко-Католицької Церкви та всіх людей доброї волі із закликом молитися за державу упродовж цілого року. На початку звернення Предстоятель УГКЦ наголошує на тому, що «упродовж багатьох років наша Церква та інші Церкви і релігійні організації у передвиборчий час закликають своїх вірних молитися за народ, за державу, за Україну... Тому створюється враження, що треба молитися в тому намірі тільки тоді, коли виникають великі потреби».</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=500:glava-ugkc-zaklykav-virnyh-molytysja-za-derzhavu-ta-jiji-vladu-ne-tilky-pered-vyboramy-a-uprodovzh-cilogo-roku-&catid=27:gk-v-ukrajini-cat&Itemid=57',1991,NULL,1,26),
(2538,'P','2010-03-10',1,NULL,'Rss','Тарасова ніч на Аскольдовій Могилі','<p><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3473,200,333.jpg\" width=\"99\" height=\"151\" />Традиційно з року в рік на Аскольдовій Могилі 9 березня була відслужена панахида пам‘яті і прослави Тараса Шевченка. Тарасова ніч з 9 на 10 березня була морозна, зоряна і вкрита снігом. У нашій церковній хаті напалена піч, зібрались вірні, друзі, свідомі українці щоб молитись і читати «Кобзар».</p>\n<p>1840 році вийшла авторська збірка Тараса Шевченка «Кобзар». Він її сам вичитав, сам видав, сам розставив крапки та коми. Палітурка була синьою а сторінки жовтими. І починався «Кобзар» з вірша «Думи мої, думи мої…».</p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=501:tarasova-nich-na-askoldovi-mogyli&catid=25:parafija-cat&Itemid=27',1992,NULL,1,26),
(2539,'P','2010-03-11',1,NULL,'Rss','Документи однозначно свідчать, що Львівський «собор» 1946 року був псевдо церковним зібранням','<p>Сьогодні минає 64 роки від того дня, як у Львові закінчився псевдособор 1946 року, згідно з рішенням якого в СРСР була ліквідована Українська Греко-Католицька Церква.</p>\n<p>Як повідомив директор Інституту історії Церкви Українського католицького університету о. Андрій Михалейко, переконливим свідчення того, що собор 1946 року проводився&nbsp; спільними зусиллями парткерівництва, державних структур та органів держбезпеки у співпраці з Руською Православною Церквою, є документи, віднайдені дослідниками історії Церкви у фондах Державного галузевого архіву СБУ та Центрального державного архіву громадських організацій України.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=502:dokumenty-odnoznachno-svidchat-shcho-lvivsky-lsoborr-1946-roku-buv-psevdo-cerkovnym-zibrannjam&catid=27:gk-v-ukrajini-cat&Itemid=57',1993,NULL,1,26),
(2540,'P','2010-03-14',1,NULL,'Rss','«Обніміться ж, брати мої, молю вас, благаю!» - Тарас Шевченко','<p align=\"right\"><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3479,200,CRW_6644.jpg\" />«Думи мої, думи мої, Лихо мені з вами!</p>\n<p align=\"right\">Нащо стали на папері сумними рядами?..</p>\n<p align=\"right\">Чом вас вітер не розвіяв в степу, як пилину?</p>\n<p align=\"right\">Чом вас лихо не приспало, як свою дитину?..»</p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=504:lobnimitsja-zh-braty-moji-molju-vas-blagajur-taras-shevchenko&catid=25:parafija-cat&Itemid=27',1997,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2541,'P','2010-03-13',0,NULL,'Note','Розклад Богослужінь на 5-тий тиждень Великого посту','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">29 березня о 17-00 чин соборування вірних на оздоровлення і спасіння. Розпочнеться літургією Напередосвячених Дарів. Участь 7-мох священиків, нагода до сповіді. Єлопомазання. Просимо наших вірних готуватися постом, молитвою, милосердям і таїнством покаяння.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>понеділок</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8-30 6-тий Час Літургія Святого Івана Золотоустого. Панахида</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18-00 Літургія Святого Івана Золотоустого. Панахида</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>вівторок</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8-00 6-тий Час Літургія Святого Івана Золотоустого. Акафіст страстям Господнім</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18-00 Літургія Святого Івана Золотоустого Панахида</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>середа</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8-30 6-тий Час &nbsp;Вечірня з Літургією Напередосвячених Дарів. Прастас-сорокоусти.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18-00 Вечірня з Літургією Напередосвячених Дарів. Хресна дорога.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>четвер</strong>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8-30 6-тий Час Літургія Святого Івана Золотоустого. Панахида</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18-00 Літургія Святого Івана Золотоустого Панахида</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>п&rsquo;ятниця</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8-30 Вечірня з Літургією Напередосвячених Дарів Парастас-Сорокоусти</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">17-30 хресна дорога від станції метро &laquo;Дніпро&raquo; до церкви по кручах Дніпрових (вийти з метро &laquo;Дніпро&raquo; пройти метрів 400 в бік Подолу. Зліва перша стаця хресної дороги)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18-00 Вечірня з Літургією Напередосвячених Дарів. Хресна дорога.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>субота </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8-15 Утреня</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">9-00 Літургія Святого Івана Золотоустого. Парастас-сорокоусти</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18-00 Літургія Святого Івана Золотоустого. Вечірня.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>неділя</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8-15 10-00 12-30 Літургія Святого Василія Великого.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">29 березня о 17-00 чин соборування вірних на оздоровлення і спасіння. Розпочнеться літургією Напередосвячених Дарів. Участь 7-мох священиків, нагода до сповіді. Єлопомазання. Просимо наших вірних готуватися постом, молитвою, милосердям і таїнством покаяння.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Телефон для довідок: (044) 570 32 90, (050) 4473157</span></span></p>',NULL,NULL,1995,5,1,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2542,'P','2010-03-14',1,NULL,'Rss','«Ефективність у благодійності»','<p>Інтерв’ю про благодійність і Церкву</p>\n<p><em><img style=\"margin: 10px; float: right;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_nebozhuk_foto.jpg.jpg\" width=\"119\" height=\"99\" /></em>Які існують показники ефективної благодійної діяльності, чи може церква бути рушієм доброчинності та волонтерства? Наскільки важливі цінності у здійснені допомоги іншим? На всі ці питання спробував відповісти керівник Бюро Української Греко-Католицької Церкви для зв’язків з органами державної влади о. Роман Небожук.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=505:lefektyvnist-u-blagodinostir&catid=27:gk-v-ukrajini-cat&Itemid=57',1999,NULL,1,26),
(2543,'P','2009-03-17',0,NULL,'Note','Літні канікули 2008 року Божого. Крим. Феодосія ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Парафія церкви св. Миколая Чудотворця на Аскольдовій Могилі (Київ) щороку проводить оздоровлення бездоглядних та парафіяльних дітей на Чорному морі. Невеличкий відеозвіт літнього табору 2008 року Божого.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> <object width=\"425\" height=\"350\" data=\"http://www.youtube.com/v/Lu-ycKHjN0Q\" type=\"application/x-shockwave-flash\"><param name=\"src\" value=\"http://www.youtube.com/v/Lu-ycKHjN0Q\" /></object> <br /></span></span></p>',NULL,NULL,2002,5,1,48),
(2544,'P','2010-03-17',1,NULL,'Rss','Caritas in Veritate - про розвиток людини та суспільних взаємин','<p>Розглядаючи у шостому розділі своєї соціальної енцикліки «Любов у  істині», який називається «Розвиток народів та техніка» взаємозв’язок  між розвитком та технологічним прогресом, Папа Венедикт&nbsp;XVI вказує на  дедалі зростаючу всюдиприсутність засобів суспільної комунікації.  Святіший Отець зазначає, що зважаючи на фундаментальну важливість ЗМІ у  зміні способу сприймання та пізнання дійсності та самої ж людини, стає  необхідним уважна призадума над їхнім впливом, докладаючи зусилля для  того, щоб вони ставали <em>нагодою для гуманізації</em>.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=506:caritas-in-veritate-pro-rozvytok-ljudyny-ta-suspilnyh-vzajemyn&catid=32:gk-svit-cat&Itemid=58',2003,NULL,1,26),
(2545,'P','2009-06-18',0,NULL,'Note','15 - річний ювілей Згромадження Cестер Cлужебниць Непорочної Діви Марії у Києві','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">31 травня 2009 року Божого у греко-католицькій церкві святого Миколая, що на  Аскольдовій могилі, відбулося святкування 15-річного ювілею служіння  Сестер Служебниць Непорочної Діви Марії у Києві.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Урочистості розпочались Святою Літургією, у якій взяв участь високий  гість - Апостольський Нунцій в Україні Його Високопреосвященство  Архієпископ Іван Юркович. У часі Богослужіння проповідь виголосив парох  греко-католицької церкви святого Миколая отець Ігор Онишкевич.</span></span></p>','<p>\r\n<object width=\"425\" height=\"350\" data=\"http://www.youtube.com/v/t9fF4KnsKFA\" type=\"application/x-shockwave-flash\">\r\n<param name=\"src\" value=\"http://www.youtube.com/v/t9fF4KnsKFA\" />\r\n</object>\r\n</p>',NULL,NULL,2005,5,1,48);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2546,'P','2010-03-19',1,NULL,'Rss','Великодній день','<p><strong><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3480,200,picture.php.jpg\" width=\"77\" height=\"115\" />Марія Морозенко</strong> - поетеса, член Національної спілки  письменників України. Автор збірок «Зоряні перевесла» (1999), «Елегія  любові» (2006), історичної поеми «Княгиня Ольга» (2004), поем \"Марія  Магдалина\"(моновиставу Лариси Недін «Поза зоною досяжності» представлено  цього року) та \"Князь Святослав\" (2006), дитячих казок «Про країну  ласунів», «Де найкраще в світі жити?», навчальних серій «Хто це?», «Що  це?», серії пізнавальних загадок, літературного переказу казки  «Дванадцять місяців» та багатьох інших творів.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=507:velykodni-den&catid=1:mystecka-storinka-cat&Itemid=50',2007,NULL,1,26),
(2547,'P','2010-03-19',0,NULL,'Note','Станьте учасниками веб-конференції Блаженнішого Любомира','<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><img style=\"border: 1px solid black; margin: 5px; float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_web_huzar.jpg.jpg\" alt=\"\" width=\"101\" height=\"82\" />26 березня 2010 року з 11.00 до 13.00</strong> відбудеться відкрита для ЗМІ веб-конференція Блаженнішого Любомира. Тема веб-конференції: <strong>&laquo;Міграція. Завдання Церкви у  служінні мігрантам&raquo;</strong>.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Блаженніший Любомир запрошує українських  мігрантів з усього світу та усіх зацікавлених осіб до on-line  спілкування.<br /></span></span></p>','<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><img style=\"border: 1px solid black; margin: 5px; width: 142px; height: 112px; float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_web_huzar.jpg.jpg\" alt=\"\" />26 березня 2010 року з 11.00 до 13.00</strong> відбудеться відкрита для ЗМІ веб-конференція Блаженнішого Любомира,  Глави УГКЦ. Тема веб-конференції: <strong>&laquo;Міграція. Завдання Церкви у  служінні мігрантам&raquo;</strong>.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Блаженніший Любомир запрошує українських  мігрантів з усього світу та усіх зацікавлених осіб до on-line  спілкування. Після проведення веб-конференції <strong>на міграційному  порталі &laquo;Навігатор&raquo;</strong> (<a href=\"http://www.navihator.net/\" target=\"_blank\">www.navihator.net</a>)  розпочнеться акція зі збору пропозицій &laquo;Церква разом з мігрантами&raquo;.  Мета акції &ndash; підготовка питань та рекомендацій для цьогорічного  Патріаршого Синоду щодо душпастирської опіки та соціального служіння  мігрантам та їхнім родинам за кордоном та в Україні. Питання міграції &ndash;  це головна тема Синоду Єпископів УГКЦ 2010 року.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Поставити свої запитання ви можете вже зараз,  скориставшись спеціальною формою за цим посиланням: <a href=\"../?ask=headline&amp;id=12\">web.ugcc.org.ua/?ask=headline&amp;id=12</a></span></span></p>',NULL,NULL,2006,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2548,'P','2009-03-19',0,NULL,'Note','Парафіяльна служба милосердя церкви св. Миколая Чудотворця на Аскольдовій Могилі','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Парафіяльна служба милосердя церкви св. Миколая Чудотворця на Аскольдовій Могилі продовжує роботу над благодійними проектами. Закликаємо і вас бути небайдужими.&nbsp; Долучайтеся разом у Христі творити добро ближньому свому.</span></span></p>','<p>\r\n<object width=\"425\" height=\"350\" data=\"http://www.youtube.com/v/m_gHflZQCj8\" type=\"application/x-shockwave-flash\">\r\n<param name=\"src\" value=\"http://www.youtube.com/v/m_gHflZQCj8\" />\r\n</object>\r\n</p>',NULL,NULL,2008,5,1,48),
(2549,'P','2010-03-22',1,NULL,'Rss','Послання Святішого Отця Бенедикта XVІ на XXV Всесвітній День Молоді','<p><strong><em>\"Учителю благий, що мені робити, щоб успадкувати життя  вічне?\"</em></strong> (Мр 10,17)<o:p></o:p></p>\n<p>Дорогі  друзі!<o:p></o:p></p>\n<p>Цього року відзначається двадцять п\'ята річниця встановлення  Всесвітнього Дня Молоді за бажанням Преподобного Івана Павла II, як  щорічна зустріч віруючої молоді всього світу. Це був пророчий почин, що  приніс багаті плоди, дав можливість новим поколінням християн  зустрічатися, слухати Слово Боже, відкривати красу Церкви і переживати  глибокий досвід віри, що привело багатьох до рішення повністю віддати  себе Христу.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=508:poslannja-svjatishogo-otcja-benedykta-xvi-na-xxv-vsesvitni-den-molodi&catid=32:gk-svit-cat&Itemid=58',2009,NULL,1,26),
(2550,'P','2010-03-23',0,NULL,'Note','Стежками Ісуса Христа','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Паломницький центр Матері Божої  Неустанної Помочі запрошує вас долучитися до наших прочанських поїздок  християнськими святинями Європи та Ізраїлю. Крім відвідування храмів та  святинь, передбачені також екскурсії  видатними історичними місцями. Всі поїздки відбуваються у супроводі  священика УГКЦ.</span></span></p>','<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong style=\"color: #003300;\">Слава  Ісусу Христу!</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong style=\"color: #003300;\">Пропонуємо  вам ознайомитись з програмами найближчих поїздок до святих місць Європи  та Ізраїлю, які організовує наш паломницький центр.</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong style=\"color: #003300;\">Більше інформації  ви можете отримати на сайті <em><a href=\"http://transfer.lviv.ua/\" target=\"_blank\">transfer.lviv.ua</a></em>,</strong><span style=\"color: #003300;\"> </span><strong style=\"color: #003300;\">або зателефонувавши  нам  за тел.: (063) 783-20-96</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong style=\"color: #003300;\">&nbsp;</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong style=\"color: #003300;\"> </strong><br /><br /></span></span></p>\r\n<table style=\"border-collapse: collapse; text-align: justify;\" border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td style=\"text-align: left; vertical-align: top; letter-spacing: 0pt; word-spacing: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"margin: 0pt; padding: 0pt; border: 0pt none;\" alt=\"\" /><br /></span></span></td>\r\n<td style=\"text-align: left; vertical-align: top; padding-left: 10px; letter-spacing: 0pt; word-spacing: 0pt;\" align=\"left\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em><strong>17-24 квітня 2010 р. Б.</strong></em><br /> </strong><em><span style=\"color: #ed7700;\"><span style=\"color: #ed7700;\">Стежками Ісуса Христа</span></span></em><br /><strong>паломницький  тур до Ізраїлю</strong><br /><br />виліт з  Києва (аеропорт \"Бориспіль)<br />групу супроводжує священик<br /><br /></span></span>\r\n<div>\r\n<div><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Вартість </strong><strong>$</strong><strong>1050</strong><strong>&nbsp; </strong><a href=\"http://www.transfer.lviv.ua/?a=0-32\" target=\"_blank\"><em>програма  туру...</em></a></span></span></div>\r\n</div>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /><br /></span></span></p>\r\n<table style=\"border-collapse: collapse; text-align: justify;\" border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td style=\"text-align: left; vertical-align: top; letter-spacing: 0pt; word-spacing: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"margin: 0pt; padding: 0pt; border: 0pt none;\" alt=\"\" /></span></span></td>\r\n<td style=\"text-align: left; vertical-align: top; padding-left: 10px; letter-spacing: 0pt; word-spacing: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em><strong>1-12 травня 2010 р. Б.</strong></em><br /><em><span style=\"color: #ed7700;\">Всі дороги  ведуть до Риму (+ Сан Джовані Ротондо + Барі + Тортоно)</span></em></span><span style=\"font-family: arial,helvetica,sans-serif;\"><br />Тур   святинями Італії з відвідуванням  поховання о. Піо в Сан Джовані Ротондо та відвідування мощей св. Миколая  в Барі. <br />Також в програмі туру заїзд в м. Тортоно, де жив і  похований Дон Луїджі Оріоне, основоположник ордену Оріоністів.<br /><br /><strong>Львів         - Краків - Ченстохова - Відень - Венеція - Сан Джовані Ротондо - Барі -  Рим (Ватикан) - Тортоно - Будапешт - Егер - Львів</strong><br /><strong><br />Вартість:      395 Є  + 100 грн.&nbsp; </strong><a href=\"http://www.transfer.lviv.ua/?a=0-26\" target=\"_blank\"><em>програма  туру...</em></a><br /></span></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /><br /></span></span></p>\r\n<table style=\"border-collapse: collapse; text-align: justify;\" border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td style=\"text-align: left; vertical-align: top; letter-spacing: 0pt; word-spacing: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"margin: 0pt; padding: 0pt; border: 0pt none;\" alt=\"\" /><br /></span></span></td>\r\n<td style=\"text-align: left; vertical-align: top; padding-left: 10px; letter-spacing: 0pt; word-spacing: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em><strong>10-16 червня 2010 р. Б. /  19-25 серпня 2010  р. Б. / 23-29 вересня 2010  р. Б.</strong></em><br /><em><span style=\"color: #ed7700;\">Всі дороги ведуть  до Риму </span><span style=\"color: #ed7700;\">(економ)</span></em><br /><br /> паломницько-екскурсійний  тур до Риму за унікальною ціною<br /><strong><br />Львів -  Будапешт - Венеція - Рим (Ватикан) - Будапешт - Егер - Львів<br /><br /></strong></span></span>\r\n<div style=\"text-align: center;\">\r\n<div style=\"text-align: center;\">\r\n<div>\r\n<div style=\"text-align: left;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> <strong style=\"text-decoration: blink;\"> <span style=\"color: #ff0000;\"><strong><em><span style=\"color: #ff0000;\"><span style=\"color: #ff0000;\">унікальна ціна 270 Євро</span> </span></em></strong></span><span style=\"color: #ff0000;\"><strong><em><span style=\"color: #ff0000;\">+ 100 грн. &nbsp;</span></em></strong></span> </strong><a href=\"http://www.transfer.lviv.ua/?a=0-27\" target=\"_blank\"><em>програма  туру...</em></a></span></span></div>\r\n</div>\r\n</div>\r\n</div>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>',NULL,NULL,2011,5,1,47);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2551,'P','2010-03-23',1,NULL,'Note','Хресна дорога - \"НА СЛАВУ ГОСПОДНЮ І ЗА СПАСІННЯ УКРАЇНИ\"','<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,3495,200,IMG_8765.JPG\" alt=\"\" width=\"104\" height=\"79\" /></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Традиційно біля 100 паломників з Києва вирушили в&nbsp;прощу - паломництво до Заздрості і Зарваниці. <strong>Київ - Заздрість - Зарваниця</strong> 19-21 березня 2010 р.Б. Приєднались до нас 35 вірних з Камянця-Подільського, 10 вірних з Хмельницька, Стрия, Львова, Франківська і місцеві жителі.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3495,200,IMG_8793.JPG\" alt=\"\" /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Традиційно біля 100 паломників з Києва вирушили в&nbsp;прощу - паломництво до Заздрості і Зарваниці. <strong>Київ - Заздрість - Зарваниця</strong> 19-21 березня 2010 р.Б. Приєднались до нас 35 вірних з Камянця-Подільського, 10 вірних з Хмельницька, Стрия, Львова, Франківська і місцеві жителі.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,3495,200,IMG_8765.JPG\" alt=\"\" /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Прийми небесний Отче цю нашу маленьку жертву - хресну дорогу, яку ми з Твоєї ласки хочемо здійснити To6i на славу i за спасіня України. Щиро прагнемо здійснити цю жертву досконало i достойно. Тому просимо в Тройці Єдиного Бога Отця i Сина i Святого Духа, - перш ніж ми розпочнемо цю хресну ходу, очисти наші серця i наші душі, розбуди у нас щирий жаль каяття за всі наші гріхи, щире бажання в чаci цієї xpecнoї дороги терпіти i покутувати за себе, за своїх рідних i близьких нашому серцю людей i за весь наш український народ.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3495,200,IMG_8770.JPG\" alt=\"\" /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пречиста Діво Mapiє, молися за нас i доверши нашу недосконалу жертву i молитву, щоб Господь Бог за молитвами i заслугами Святих i праведників нашого народу, нашого Патріарха Йосифа, блаженних новітніх мучеників нашої Святої Церкви, Bcix святих українського народу i вcix святих зглянувся<sup> &nbsp;</sup>над нами i помилував нас.</span></span></p>',NULL,NULL,2012,5,1,17),
(2552,'P','2010-03-25',1,NULL,'Rss','Святий Альберт Великий – приклад вченого, побожного і мудрого богослова','<p>Святий Альберт Великий, що жив у ХІІІ сторіччі, видатний німецький  богослов, філософ, науковець у сфері природничих наук, монах Ордену  домініканців, вчитель святого Томи Аквінського був в центрі уваги  катехизи Святішого Отця Венедикта ХVІ у середу, 24 березня 2010 року.  Загальна зустріч з десятками тисяч прочан пройшла на площі перед  базилікою святого апостола Петра у Ватикані.</p>\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=512:svjaty-albert-velyky--pryklad-vchenogo-pobozhnogo-i-mudrogo-bogoslova&catid=35:istorychni-postati-cat&Itemid=48',2013,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2553,'P','2010-03-25',1,NULL,'Rss','Неділя Квітна. Вхід Господній у Єрусалим','<!--TYPO3SEARCH_begin-->\n<p><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3495,200,ad2a24ce7b.jpg\" width=\"86\" height=\"120\" />Коротка історія</p>\n<p align=\"justify\">Празник торжественного в\'їзду  Ісуса Христа до Єрусалима належить до найдавніших празників у Східній  Церкві. Свідчення про його святкування сягають III століття. Святкування  цього празника почалося в Єрусалимі і невдовзі стало празником усієї  Східної Церкви.\n',NULL,1,'http://www.oranta.org/index.php?option=com_content&view=article&id=511:nedilja-kvitna-vhid-gospodni-u-jerusalym&catid=27:gk-v-ukrajini-cat&Itemid=57',2014,NULL,1,26),
(2554,'P','2009-06-26',0,NULL,'Note','День матері та перше святе причастя на Аскольдові Могилі 2009 року Божого','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">10 травня у греко-католицькій церкві святого Миколая, що на Аскольдовій Могилі, було подвійне свято День Матері та перша сповідь і святе причастя для парафіяльних дітей.</span></span></p>','<p>\r\n<object width=\"425\" height=\"350\" data=\"http://www.youtube.com/v/YZsChGA4Br4\" type=\"application/x-shockwave-flash\">\r\n<param name=\"src\" value=\"http://www.youtube.com/v/YZsChGA4Br4\" />\r\n</object>\r\n</p>',NULL,NULL,2015,5,1,48),
(2555,'P','2010-03-27',1,NULL,'Rss','ПРОЩА-ПАЛОМНИЦТВО ДО ЗАЗДРОСТІ ТА ЗАРВАНИЦІ','<p><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3496,200,DSCF0272.JPG\" />Господь Бог, даючи нам день, дає разом з цим кожним днем і дорогу до себе, на якій ми робимо або півкроку, або крок, або ж долаємо цілу вічність на шляху до Нього. І однаково важливими для Отця нашого є малесенький крок і великий шлях, бо це порятунок душі. Таким і маленьким кроком, і великим шляхом стала Хресна дорога «На славу Господу і за спасіння України» з Києва до Заздрості й далі до Зарваниці для майже 100 паломників греко-католиків Києва та Кам’янця-Подільського з отцем Ігорем Онишкевичем, яка відбулася 19-21 березня 2010 року Божого і була 14 за рахунком.</p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=513:proshcha-palomnyctvo-do-zazdrosti-ta-zarvanyci&catid=25:parafija-cat&Itemid=27',2016,NULL,1,26),
(2556,'P','2010-04-01',1,NULL,'Rss','Молодіжна Хресна Дорога','<p><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3503,200,1047.jpg\" />У п’ятницю&nbsp; 26 березня&nbsp; відбулась молодіжна хресна дорога в церкві Миколая Чудотворця підготована молодіжною спільнотою церкви.&nbsp; Цього року молодь церкви Миколая Чудотворця вдруге готувала Хресну Дорогу. Це вже стає хорошою традицією кожного року молоддю проводити хресну дорогу у часі Великого Посту.&nbsp; Підготовка таких заходів, як хресна дорога дає можливість глибше порозважати над стаціями, а потім поділитись своїми переживаннями.</p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=516:molodizhna-hresna-doroga&catid=25:parafija-cat&Itemid=27',2021,NULL,1,26),
(2557,'P','2010-04-01',1,NULL,'Rss','Хресна Дорога ВІС','<p><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3503,200,535.jpg\" />В часі великого посту ми маємо можливість проходячи 14 стацій Хресної Дороги ще раз призадуматись, якою дорогу ціну&nbsp; заплатив за наше звільнення від гріха Ісус Христос.&nbsp; Кожна із спільнот, що готує Хресну дорогу намагається, як найглибше передати ті переживання які є характерними для неї. В часі цьогорічного великого посту молодь із церкви Миколая Чудотворця разом із молодіжною спільнотою Неопалима купина із церкви Благовіщеня Пресвятої Богородиці, до себе на хресну дорогу запросила спільнота ВІС.</p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=515:hresna-doroga-vis&catid=25:parafija-cat&Itemid=27',2022,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2558,'P','2010-03-28',1,NULL,'Note','Квітна неділя у світлинах','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3500,200,CRW_6795.jpg\" alt=\"\" />Після Богослужіння відбулося урочисте освячення вербних гілочок. Церковний хор співом прославляв Ісуса Христа. Хто не мав &laquo;шутки&raquo; міг взяти її біля церкви. Отець Ігор Онишкевич попіклувався, щоб парафіянам роздавали ці символи єрусалимської зустрічі Ісуса Христа. Люди легенько били один одного, промовляючи: &laquo;Не я б&rsquo;ю, лоза б&rsquo;є, за тиждень Великдень&raquo;. Так справді за тиждень Великдень. Цього дня люди чекають ще з більшим нетерпінням.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Фото: Любові Михайлюк</span></span></p>\r\n<p style=\"text-align: justify;\"><img style=\"float: left;\" src=\"image,3500,200,CRW_6794.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,3500,200,CRW_6797.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img style=\"float: left;\" src=\"image,3500,200,CRW_6798.jpg\" alt=\"\" width=\"200\" height=\"133\" /><img style=\"float: right;\" src=\"image,3500,200,CRW_6800.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img style=\"float: left;\" src=\"image,3500,200,CRW_6795.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,3500,200,CRW_6802.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img style=\"float: left;\" src=\"image,3500,200,CRW_6807.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,3500,200,CRW_6806.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img style=\"float: left;\" src=\"image,3500,200,CRW_6803.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,3500,200,CRW_6808.jpg\" alt=\"\" /></p>',NULL,NULL,2017,5,1,49),
(2559,'P','2010-04-01',1,NULL,'Rss','Молодіжні Великопісні реколекції.','<p><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3503,200,1287.jpg\" />З 27 по 28 березня відбулись щорічні молодіжні великопісні реколекції, організовані головою молодіжної комісії отцем Віталієм Марценюком. Ця дата реколекцій вибрана невипадково адже Вербна Неділя у церковному календарі також є і днем молоді. Реколекції почались у суботу 27 березня із літургії у храмі Василя Великого. В реколекціях прийняла участь молодь із всієї київської архієпархії, тому крім духовної віднови для кожного ці реколекції дали можливість &nbsp;познайомитись та обмінятись досвідом між спільнотами із різних парафій київської архієпархії.</p>\n','',1,'http://www.oranta.org/index.php?option=com_content&view=article&id=517:molodizhni-velykopisni-rekolekciji&catid=25:parafija-cat&Itemid=27',2023,NULL,1,26);
