INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2585,'P','2010-05-10',1,NULL,'Rss','Тепле серце матері…','<pre><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3533,200,14.jpg\" />Можна вибрать друга і по духу брата,</strong></pre>\n<pre><strong>Лиш не можна, сину, матір вибирати.</strong></pre>\n<pre><strong>Можна все на світі вибирати, сину,</strong></pre>\n<pre><strong>Вибрати не можна тільки Батьківщину.</strong></pre>\n<pre><em><strong>В. Симоненко<br /></strong></em></pre>\n<p>Мама… Це найдорожче слово кожної людини. Мама – це перше слово кожного малюка. Мама найрідніша для нас людина. Мама завжди допоможе, порадить, а у важку хвилину пригорне до серця.</p>\n<p>Тому вшанування матері – є найголовнішим завданням людини. І такий день, як День матері дає нам можливість висловити любов і пошану нашим матерям.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=525:teple-serce-materi&catid=25:parafija-cat&Itemid=27',2052,NULL,1,26),
(2586,'P','2010-05-11',1,NULL,'Rss','Молодіжна велопроща','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3535,200,IMG_1143.JPG\" />Для кожного першотравневі свята асоціюються із відпочинком і можливістю кудись поїхати, адже саме свято давно &nbsp;втратило своє першопочаткове значення. Для мене випала хороша нагода взяти участь у молодіжній прощі із Ялти&nbsp; до Судака,&nbsp; що проходила із 1 по 3 травня і організована братом Августином із згромадження Васильян. У цій велопрощі взяли участь 37 учасників із різних регіонів України. За ці три дні молодь мала подолати <st1:metricconverter productid=\"120 км\">120 км</st1:metricconverter> кримських доріг, які характерні стрімкими спусками та підйомами. Згідно розкладу всі збирались у місті Ялті біля римо-католицького костелу, на всіх хто приїздили на місце збору уже чекав обід. Після обіду молодь помолилась&nbsp; молебень до Богородиці за щасливу прощу. Вирушаючи в прощу ми просимо у певному наміренні, крім того що кожен мав, особисте намірення було також із загальне намірення, за навернення грішників та за кращу долю України.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=527:molodizhna-veloproshcha&catid=25:parafija-cat&Itemid=27',2054,NULL,1,26),
(2587,'P','2010-04-11',1,NULL,'Rss','ХРЕСНА ДОРОГА молодіжної спільноти церкви святого Миколая Чудотворця на Аскольдовій Mогилі, 2010 р. Б.','<p>Вступна пісня</p>\n<p><strong><span style=\"text-decoration: underline;\">1. Настала ніч</span></strong></p>\n<p><strong>1.</strong> Настала ніч, затихло всюди,</p>\n<p>Заснув святий Єрусалим.</p>\n<p>Схиливши голову на груди,</p>\n<p>Христос сидів і учні з ним. /2 р.</p>\n<p><strong>&nbsp;</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=528:hresna-doroga-molodizhnoji-spilnoty-cerkvy-svjatogo-mykolaja-chudotvorcja-na-askoldovi-mogyli-2010-r-b&catid=30:hrystyjanske-vchennia-cat&Itemid=37',2056,NULL,1,26),
(2588,'P','2010-05-11',1,NULL,'Rss','Молодь парафії','<p align=\"center\"><strong><span style=\"text-decoration: underline;\">Сторінки життя молодіжної спільноти</span></strong><strong> </strong></p>\n<p align=\"center\">Церкви св. Миколая Чудотворця з Аскольдової Могили, м. Київ</p>\n<p align=\"center\">(вересень 2009 р. Б. – червень 2010 р. Б.)</p>\n<p align=\"center\"> </p>\n<p>Духовний провідник:<strong> Отець Олексій Геник, </strong></p>\n<p>Катехит організації:<strong> Ірина Василик, сестра Анастасія</strong></p>\n<p>Лідер організації:<strong> Ярослав Павлюк</strong></p>\n<p><span style=\"text-decoration: underline;\"> </span></p>\n<p>Наше гасло: <strong>СЛАВА БОГУ, ЧЕСТЬ МАРІЇ, НАМ МИР!</strong></p>\n<p><strong> </strong></p>\n<p><strong><span style=\"text-decoration: underline;\"></span></strong></p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=529:molod-parafiji&catid=25:parafija-cat&Itemid=27',2057,NULL,1,26);
