(2645,'P','2010-08-11',0,NULL,'Note','«Канікули з Богом» у Карпатах','<p><img style=\"float: left;\" src=\"image,3603,200,DSC_4754.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3603,200,DSC_0589.JPG\" alt=\"\" /></p>','<p><img style=\"float: left;\" src=\"image,3603,200,DSC_4754.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3603,200,DSC_0589.JPG\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,3603,200,DSC_0007.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3603,200,DSC_0043.JPG\" alt=\"\" width=\"200\" height=\"132\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,3603,200,DSC_0572.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3603,200,DSC_0105.JPG\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,3603,200,DSC_0118.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3603,200,DSC_0122.JPG\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,3603,200,DSC_0162.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3603,200,DSC_0143.JPG\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,3603,200,DSC_0143.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3603,200,DSC_4415.JPG\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>