INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2891,'P','2011-05-31',1,NULL,'Rss','Звернення Синоду Єпископів Києво-Галицького Верховного Архиєпископства УГКЦ до духовенства і вірних УГКЦ та всіх людей доброї волі про збереження суспільного миру','<p style=\"text-align: right;\" class=\"rteright\">«Блаженні миротворці,<br /> бо вони синами Божими назвуться» (Мт. 5, 9).</p>\n<p style=\"text-align: center;\" class=\"rtejustify\">Дорогі у Христі!</p>\n<p class=\"rtejustify\">У цей пасхальний час, коли воскреслий Христос приходить до своїх учнів  перед своїм вознесінням, ми особливо переживаємо ту істину нашої віри,  що день святої Пасхи є для нас найбільшим і найважливішим Днем перемоги.  Саме цього дня Ісус Христос власною смертю переміг нашу смерть, а своїм  славним воскресінням дарував нам вічне життя. У день Пасхи зазнав  поразки неприятель людського роду – диявол, а у Христі перемогли ми всі.  Ісус Христос приходить до апостолів крізь замкнені двері, щоб передати  їм радість перемоги. Перші слова воскреслого Спасителя до апостолів «Мир  вам!» найповніше передають цю всепереможну радість, засвідчену  апостолом Іваном: «І зраділи учні, побачивши Господа» (Ів. 20, 20).</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=750:zvernennja-synodu-jepyskopiv-kyjevo-galyckogo-verhovnogo-arhyjepyskopstva-ugkc-do-duhovenstva-i-virnyh-ugkc-ta-vsih-ljude-dobroji-voli-pro-zberezhennja-suspilnogo-myru&catid=27:gk-v-ukraji',2469,NULL,1,26),
(2892,'P','2011-06-01',1,NULL,'Rss','Вознесіння Господа Бога і Спаса нашого Ісуса Христа','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3942,200,0928428a75.jpg\" />28 травня 2009 року </strong></p>\n<p><strong>Коротка історія</strong></p>\n<p align=\"justify\">Празник Господнього Вознесіння завжди припадає у четвер 40-го дня по Христовому Воскресінні. Це один з великих Господських празників і має 9-денне попразденство. Він звеличує подію Христового Вознесіння на небо та підкреслює її значення для Христа і для нас. Св. Іван Золотоустий у своїй проповіді на Вознесіння каже: „Сьогодні людський рід совершенно примирений з Богом. Зникла давня боротьба й ворожнеча. Ми, що були недостойні жити й на землі, - вознесені на небо. Сьогодні стаємо наслідниками небесного царства, ми, що не вартуємо й земного, виходимо на небо й успадковуємо престол Царя і Господа. А людська природа, перед якою херувим боронив рай, піднесена тепер понад усякого херувима\".</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=334:voznesinnja-gospoda-boga-i-spasa-nashogo-isusa-hrysta&catid=27:gk-v-ukrajini-cat&Itemid=57',2470,NULL,1,26),
(2893,'P','2011-06-04',1,NULL,'Rss','Глава УГКЦ заявив «Корреспонденту», що вірить у створення єдиної української Церкви ','<div class=\"news-single-img\"><img style=\"margin: 10px; float: right;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/4600ab1bf3.jpg\" height=\"165\" width=\"115\" /></div>\n<p>Глава Української Греко-Католицької Церкви Блаженніший  Святослав (Шевчук) в інтерв\'ю журналу «Кореспондент» заявив, що вірить у  можливість створення єдиної помісної Української Церкви.</p>\n<p>Видання пише, що Блаженніший Любомир говорив про можливість  сопричастя УГКЦ з усіма українськими православними. У зв\'язку з цим  журналісти Анна Мороз та Ірина Соломко, які проводили інтерв’ю,  поцікавилися, чи є Блаженніший Святослав прихильником об\'єднання.</p>\n<p>«Він (Блаженніший Любомир, − <em>ред.</em>) не раз висловлювався за  те, що єдина помісна Церква − реальність. Передусім тому, що вона вже  існувала в такому вигляді, але в силу певних причин потім була розділена  на кілька конфесій. Оскільки є спільний фундамент, є і шанси, що ця  єдність відновиться. Але до цього нам потрібно йти», − заявив  Предстоятель УГКЦ.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=753:glava-ugkc-zajavyv-lkorrespondentur-shcho-viryt-u-stvorennja-jedynoji-ukrajinskoji-cerkvy-&catid=27:gk-v-ukrajini-cat&Itemid=57',2473,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2894,'P','2011-06-04',1,NULL,'Rss','Блаженніший Любомир закликав українців подбати про майбутнє без корупції ','<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/2c62b5c356.gif\" height=\"201\" width=\"150\" /></div>\n<p>«Закон проти корупції може стати ефективним тільки тоді, коли  члени нашого суспільства змалечку будуть виховані в дусі чесної  поведінки». Про це Глава УГКЦ в 2001-2011 роках Блаженніший Любомир  написав в опублікованій виданням «Українська правда» статті <a href=\"http://www.pravda.com.ua/columns/2011/06/2/6264424/\" target=\"_blank\">«Як побороти корупцію»</a>.</p>\n<p>Аналізуючи значення Закону № 7487 від 7 квітня 2011 року «Про засади  запобігання і протидії корупції в Україні», Блаженніший Любомир  зазначає, що це дуже важлива подія в житті нашого народу, щонайменше під  двома оглядами: «<em>По-перше</em>, устами своїх представників у  Верховній Раді наш народ заявив, що вважає корупцію, чи хабарництво,  великим лихом, якого треба позбутися. <em>По-друге</em>, на підставі  закону від 17 квітня 2010 року творяться похідні закони, згідно з якими  можна карати порушників у цій ділянці, бо корупція − це злочин,  недотримання урядових повноважень задля власної користі», − зазначається  у статті.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=752:blazhennishy-ljubomyr-zaklykav-ukrajinciv-podbaty-pro-mabutnje-bez-korupciji-&catid=27:gk-v-ukrajini-cat&Itemid=57',2474,NULL,1,26),
(2895,'P','2011-06-05',1,NULL,'Rss','Дітвора з Аскольдової Могили пізнає козацькі шляхи…','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3958,200,4.jpg\" />Четвертого червня діти з Аскольдової могили поїхали в славне місце – козацьку фортецю Батурин. Поїздка була організована парохом церкви святого Миколая отцем Ігорем Онишкевичем. Чисельна група з тридцяти чотирьох осіб поїхала пізнавати козацькі шляхи.</p>\n<p>До Батурина потрібно було їхати близько трьох годин. Але за цей час в автобусі діти разом з батьками мали змогу молитися, декламувати Шевченка, спілкуватися.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=754:ditvora-z-askoldovoji-mogyly-piznaje-kozacki-shljahy&catid=25:parafija-cat&Itemid=27',2476,NULL,1,26),
(2896,'P','2011-06-07',1,NULL,'Rss','ВІЗІЇ ДЛЯ ЦЕРКВИ І НАЦІЇ','<p>У чималій творчій спадщині теоретика консерватизму, видатного політичного діяча початку минулого сторіччя В. Липинського релігійно-церковна тематика посідає чільне місце. Відомий мислитель був переконаний, що релігія, інститут Церкви мають стати наріжним каменем духовного життя України як самостійної держави. Свої міркування щодо місця і ролі релігії та Церкви в суспільстві В. Липинський оформив у невеликій книжці «Релігія і церква в історії України», яка вперше побачила світ у 1925 році.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=755:viziji-dlja-cerkvy-i-naciji&catid=37:spilnota-cat&Itemid=49',2477,NULL,1,26),
(2897,'P','2011-06-08',1,NULL,'Note','Пепрша сповідь та урочисте святе причасття 2011 року Божого','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З року в рік у нашій святій церковці стає дедалі більше першопричасників.День першої сповіді та урочистого причасття запам&rsquo;ятається кожному. Особливо першопричасникам, адже вони увійшли у &laquo;відповідальне&raquo; життя, відчули, що бо з Богом стали зрілішими, самостійнішим та впевненішим на життєвій ниві.</span></span></p>','<p>\r\n<object width=\"425\" height=\"350\" data=\"http://www.youtube.com/v/5sijz2MRnyc\" type=\"application/x-shockwave-flash\">\r\n<param name=\"src\" value=\"http://www.youtube.com/v/5sijz2MRnyc\" />\r\n</object>\r\n</p>',NULL,NULL,2478,5,1,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2898,'P','2011-06-04',1,NULL,'Note','Глава УГКЦ: У мене немає власної машини. Постараюся не уподібнюватися українській владі','<div style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> <img id=\"current_img\" style=\"float: left;\" title=\"Глава УГКЦ: У меня нет собственной машины. Постараюсь не уподобляться украинским властям\" src=\"http://k.img.com.ua/img/forall/a/12246/78.jpg\" alt=\"Глава УГКЦ: У меня нет собственной машины. Постараюсь не уподобляться украинским властям\" width=\"142\" height=\"99\" /> <span id=\"0097f\">Він зустрів Корреспондент в крихітному кабінеті без вікон, із стін якого дивилися Папа римський <a href=\"http://files.korrespondent.net/persons/benedict\">Бенедикт XVI</a>, Богородиця і \"око\" ввімкненого кондиціонера. До приходу журналістів компанію йому складав ноутбук. <br /><br />\"Я  збираюся зробити церкву відкритою, більш доступною і зрозумілою.  Потрібно звернутися до кожної людини сучасною і зрозумілою їй мовою.  Донести вічні істини, той давній скарб київського християнства таким  чином, щоб людина прийняла його і жила з ним. Тоді церква виконає свою  місію\" , - заявив він. </span></span></span></div>','<div style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> <img id=\"current_img\" style=\"float: left;\" title=\"Глава УГКЦ: У меня нет собственной машины. Постараюсь не уподобляться украинским властям\" src=\"http://k.img.com.ua/img/forall/a/12246/78.jpg\" alt=\"Глава УГКЦ: У меня нет собственной машины. Постараюсь не уподобляться украинским властям\" width=\"300\" height=\"210\" /></span></span><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Наймолодший серед лідерів  українських церков, голова українських греко-католиків, 41-річний  Святослав (Шевчук) в інтерв\'ю Корреспонденту заявив, що має намір  дотримуватися аскетизму.</span></span><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Архієпископ призначив журналістам зустріч  на столичних Липках, але навіть у цьому районі, де, як пише  Корреспондент, Bentley аж ніяк не розкіш, а в гаманці середнього  перехожого дзвенять мільйони, Святослав примудряється дотримуватися  звичного йому аскетизму. <br /><br />Він зустрів Корреспондент в крихітному кабінеті без вікон, із стін якого дивилися Папа римський <a href=\"http://files.korrespondent.net/persons/benedict\">Бенедикт XVI</a>, Богородиця і \"око\" ввімкненого кондиціонера. До приходу журналістів компанію йому складав ноутбук. <br /><br />\"Я  збираюся зробити церкву відкритою, більш доступною і зрозумілою.  Потрібно звернутися до кожної людини сучасною і зрозумілою їй мовою.  Донести вічні істини, той давній скарб київського християнства таким  чином, щоб людина прийняла його і жила з ним. Тоді церква виконає свою  місію\" , - заявив він. <br /><br />Він також зауважив, що поки не дотримав  слова про те, що заведе сторінку на Facebook. \"У мене просто немає часу,  я до кінця ще не налагодив своє життя в новому статусі, а соціальні  мережі забирають багато часу. Використовувати людину, яка писала б  замість мене, не хочу. Я хочу бути чесним з тими, хто хоче зі мною  спілкуватися\", - зазначив Святослав. <br /><br />\"Ми живемо в умовах  інформаційного суспільства. Якщо чогось немає в цьому просторі, про  нього відразу всі забувають. Ми шукаємо найефективніші способи  інформування кожного нашого парафіянина і розробляємо стратегію  інформаційної присутності. Якщо Вас цікавить, наскільки збільшилася  кількість читачів, які споживають нашу інформацію, то вона зросла.  Всього в світі близько 7 млн ​​греко-католиків, з них 5 млн - в  Україні\", - заявив він, відповідаючи на питання про успішність УГКЦ як  великого гуманітарного проекту. <br /><br />Корреспондент пише, що коли  Святослав очолив церкву, то шокував колег з інших церков тим, що носить  недорогий годинник і їздить демократичним автомобілем. Видання  поцікавилося у глави УГКЦ, чи не збирається він виправити ситуацію. <br /><br />\"Мені  не хочеться нічого міняти. Є стара латинська приказка: honores mutant  mores - \"почесті міняють звички\". Я б хотів, щоб у моєму випадку вона не  спрацювала. У мене немає власної машини, поки я користуюся автомобілем  Блаженнішого Гузара або Патріаршої курії. Постараюся не уподібнюватися  українській владі. Подивимося, чи вийде це в мене\", - заявив він. <br /><br /><em>Повністю інтерв\'ю з новим главою українських греко-католиків, читайте в № 21 видання Корреспондент від 3 червня 2011 року. </em></span></span></p>\r\n<span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span id=\"0097f\">\r\n<p><em><a rel=\"nofollow\" href=\"http://korrespondent.net/\" target=\"_blank\"><strong>Корреспондент.net</strong></a></em></p>\r\n</span></span></span></div>',NULL,NULL,2475,5,1,30);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2899,'P','2011-06-07',1,NULL,'Rss','Зіслання Святого Духа. П\'ятдесятниця','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3961,200,b35bf8168f.jpg\" />Коротка історія</strong></p>\n<p align=\"justify\">Світлий празник Пасхи завершується не менше світлим і великим празником Святої П\'ятдесятниці. По Христовому Різдві і Воскресінні цей празник належить до найбільших празників нашого церковного року. Зіслання Святого Духа це - наче вінець і печать на ділі спасіння людського роду, що його доконав Божий Син. В дні св. П\'ятдесятниці сам Святий Дух помазує апостолів на проповідників Христового Благовістя. В дні Зіслання Святого Духа родиться і починає діяти Христова Церква. Святий Дух її провадить, просвічує, освячує і зберігає на дорозі правди.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=338:zislannja-svjatogo-duha-pjatdesjatnycja&catid=27:gk-v-ukrajini-cat&Itemid=57',2479,NULL,1,26),
(2900,'P','2011-06-08',1,NULL,'Rss','УГКЦ вшановує пам’ять о. Маркіяна Шашкевича, який національно і духовно відроджував Україну ','<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/86c7edfa70.jpg\" height=\"175\" width=\"150\" /></div>\n<p>З нагоди двохсотріччя з дня народження о. Маркіяна Шашкевича <em>(6 листопада 1811 року)</em> та з приводу його річниці смерті <em>(7 червня 1843 року)</em> Блаженніший Святослав звернувся з <a href=\"http://www.ugcc.org.ua/1826.0.html\">Посланням</a> до українського духовенства та мирян. Глава УГКЦ закликав бачити у  постаті цього священика приклад любові до свого народу, який захищав  його права і свободи та підносив культуру&nbsp;й духовність.</p>\n<p>Незважаючи на коротке життя о. Маркіяна (32 роки), його внесок у  формування українського національного духу та національне пробудження  нашого народу є неоціненним. «Великий поет, визначний лінгвіст, а  передусім невтомний, ревний і святий священик, котрого Господь послав  нашому народові в часи великого духовно-культурного занепаду, щоб  пробудити його, показати йому його гідність і велич, скерувати його на  шлях національного та духовного відродження», – таким бачить його особу  Глава УГКЦ.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=756:ugkc-vshanovuje-pamjat-o-markijana-shashkevycha-jaky-nacionalno-i-duhovno-vidrodzhuvav-ukrajinu-&catid=27:gk-v-ukrajini-cat&Itemid=57',2480,NULL,1,26),
(2901,'P','2011-06-09',1,NULL,'Rss','Пенітенціарне душпастирство УГКЦ на порозі впровадження капеланства набуває професійних рис','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3963,200,Lviv_2011_1.jpg\" />6 - 8 червня 2011року у Реколекційному центрі Львівської Архиєпрахії відбувався триденний семінар єпархіальних капеланів Пенітенціарного душпастирства Української Греко-Католицької Церкви. Він проходив, з благословення преосвященнішого владики Михаїла (Колтуна), єпископа Сокальсько-Жовквівського, керівника Департаменту УГКЦ у справах душпастирства силових структур України.&nbsp; Крім капеланів були присутні також катехити та волонтери цього особливого і дуже цінного служіння Церкви. Семінар «Душпастирська&nbsp; практика в’язничного служіння» загострив увагу капеланів на застосуванні у пастирських ситуаціях психолого-педагогічних знань, канонічного права, морального богослов’я, міжнародного і вітчизняного права у кримінально-виконавчій сфері.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=757:penitenciarne-dushpastyrstvo-ugkc-na-porozi-vprovadzhennja-kapelanstva-nabuvaje-profesinyh-rys&catid=27:gk-v-ukrajini-cat&Itemid=57',2481,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2902,'P','2011-06-09',0,NULL,'Note','Святкування 10-річчя візиту блаженного Папи Івана Павла ІІ в Україну розпочнеться виставкою ','<div style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"http://www.ugcc.org.ua/index.php?eID=tx_cms_showpic&amp;file=uploads%2Fpics%2Flogo_Papa.jpg&amp;md5=1b1baa85f2a668bb93ad44030fd02d8a842a10f8&amp;parameters[0]=YTo0OntzOjU6IndpZHRoIjtzOjQ6IjgwMG0iO3M6NjoiaGVpZ2h0IjtzOjM6IjYw&amp;parameters[1]=MCI7czo3OiJib2R5VGFnIjtzOjQyOiI8Ym9keSBiZ0NvbG9yPSIjZmZmZmZmIiBz&amp;parameters[2]=dHlsZT0ibWFyZ2luOjA7Ij4iO3M6NDoid3JhcCI7czozNzoiPGEgaHJlZj0iamF2&amp;parameters[3]=YXNjcmlwdDpjbG9zZSgpOyI%2BIHwgPC9hPiI7fQ%3D%3D\" target=\"thePicture\"><img style=\"border: 0pt none; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/73a04c7580.jpg\" alt=\"\" width=\"150\" height=\"149\" /></a></span></span></div>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Святкування 10-річчя історичного візиту Блаженного Папи Івана  Павла ІІ в Україну розпочнеться виставкою &laquo;Папа, Україна, я&raquo;. Відкриття  виставки відбудеться у вівторок, 14 червня, о 18-00, в Українському домі  (вул. Хрещатик, 2).</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Організатори заходу пропонують відвідувачам оглянути близько сотні  експонатів &ndash; ексклюзивні світлини, книги, мистецькі твори, присвячені  Папі, твори самого Івана Павла ІІ, його особисті речі. Як зазначає  голова оргкомітету святкувань Владика Йосиф (Мілян), виставка допоможе  піднести дух українців, ще раз представить Україні Великого Папу.</span></span></p>','<div style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"http://www.ugcc.org.ua/index.php?eID=tx_cms_showpic&amp;file=uploads%2Fpics%2Flogo_Papa.jpg&amp;md5=1b1baa85f2a668bb93ad44030fd02d8a842a10f8&amp;parameters[0]=YTo0OntzOjU6IndpZHRoIjtzOjQ6IjgwMG0iO3M6NjoiaGVpZ2h0IjtzOjM6IjYw&amp;parameters[1]=MCI7czo3OiJib2R5VGFnIjtzOjQyOiI8Ym9keSBiZ0NvbG9yPSIjZmZmZmZmIiBz&amp;parameters[2]=dHlsZT0ibWFyZ2luOjA7Ij4iO3M6NDoid3JhcCI7czozNzoiPGEgaHJlZj0iamF2&amp;parameters[3]=YXNjcmlwdDpjbG9zZSgpOyI%2BIHwgPC9hPiI7fQ%3D%3D\" target=\"thePicture\"><img style=\"border: 0pt none; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/73a04c7580.jpg\" alt=\"\" width=\"150\" height=\"149\" /></a></span></span></div>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Святкування 10-річчя історичного візиту Блаженного Папи Івана  Павла ІІ в Україну розпочнеться виставкою &laquo;Папа, Україна, я&raquo;. Відкриття  виставки відбудеться у вівторок, 14 червня, о 18-00, в Українському домі  (вул. Хрещатик, 2).</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Організатори заходу пропонують відвідувачам оглянути близько сотні  експонатів &ndash; ексклюзивні світлини, книги, мистецькі твори, присвячені  Папі, твори самого Івана Павла ІІ, його особисті речі. Як зазначає  голова оргкомітету святкувань Владика Йосиф (Мілян), виставка допоможе  піднести дух українців, ще раз представить Україні Великого Папу.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&bdquo;Робимо виставку, щоби відродити високий дух народу, який панував&nbsp;у  час історичного візиту в Україну блаженного Папи 10 років тому.&nbsp;Рівно ж  хочемо звернути увагу народу на глибокі напутні слова Івана Павла ІІ:  &laquo;Народ гине, якщо занепадає його дух, народ зростає, якщо його дух  щоразу підноситься: такий народ ніяка зовнішня сила не може знищити&rdquo;, &ndash;  зазначає єпископ.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Експозиція триватиме до 24 червня. Крім виставки, у програмі заходів  21-26 червня відбудеться подіумна дискусія &laquo;Візит Папи Івана Павла ІІ в  Україну: уроки для держави та суспільства&raquo; за участю Блаженнішого  Любомира Гузара, Генерального секретаря Синоду Єпископів Католицької  Церкви Архиєпикопа Миколи Етеровича, віце-ректора Українського  католицького університету Мирослава Мариновича (Український дім), опера  &bdquo;Мойсей&rdquo; (в Національній опері), вулична вистава &bdquo;Йов&rdquo; (на Контрактовій  площі) та багато іншого.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Прес-служба Київської архиєпархії</span></span></p>',NULL,NULL,2482,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2903,'P','2011-06-09',1,NULL,'Note','Через поріг тисячоліття з Іваном Павлом ІІ (44)','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Слова про любов і діла милосердя, пов&rsquo;язані з людськими стражданнями,  дозволяють нам ще раз відкрити в основі людських страждань спасенне  страждання Христа. Ісус сказав: &laquo;Ви мені це робили&raquo;. Він сам є Тим, хто  зазнає любові, Він є Тим, хто отримує допомогу, коли її надається  людині, яка страждає. Він сам присутній у людині, що терпить, оскільки  Його спасенне страждання відкрите раз і назавжди для кожного людського  страждання.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"text-decoration: underline;\"><a href=\"http://212.77.9.15/audio/ra/00264960.RM\"><img src=\"http://www.radiovaticana.org/ucr/img/listen.gif\" border=\"0\" alt=\"\" /></a> </span> Блаженний Іван Павло ІІ часто говорив і навчав про значення терпіння  та болю в людському житті, зокрема, в Апостольському Листі &laquo;Salvifici  Dolores&raquo; (Спасенного болю), що був оприлюднений 11 лютого 1984 року.  Блаженний Іван Павло ІІ, серед іншого, пише:<br /><br />&laquo;Слова про любов і  діла милосердя, пов&rsquo;язані з людськими стражданнями, дозволяють нам ще  раз відкрити в основі людських страждань спасенне страждання Христа.  Ісус сказав: &laquo;Ви мені це робили&raquo;. Він сам є Тим, хто зазнає любові, Він є  Тим, хто отримує допомогу, коли її надається людині, яка страждає. Він  сам присутній у людині, що терпить, оскільки Його спасенне страждання  відкрите раз і назавжди для кожного людського страждання. І всі ті, що  страждають, раз і назавжди покликані стати учасниками &laquo;Христових мук&raquo;,  як і всі покликані &laquo;доповнити&raquo; своїми стражданнями &laquo;те, чого ще бракує  скорботам Христовим&raquo; (Кол 1,24). Христос навчив людину одночасно творити  добро через страждання і творити добро тим, хто страждає. У цьому  подвійному аспекті Він повністю розкрив значення страждання&raquo;, &ndash; написав  блаженний Іван Павло ІІ.<br />http://www.radiovaticana.org/</span></span></p>',NULL,NULL,2483,5,1,44),
(2904,'P','2011-06-13',1,NULL,'Rss','«В Україні відбувається ідеологічна зачистка молодіжних організацій і рухів», - Блаженніший Святослав на зустрічі із головами молодіжних комісій ','<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/45f6d00026.jpg\" height=\"104\" width=\"150\" /></div>\n<p><strong>Наша Церква опрацьовує стратегію до 2020 року, -  наголосив на зустрічі голів молодіжних комісій Блаженніший Святослав,  яка відбулася 10 червня 2011 р. у залі засідань Львівської архиєпархії.</strong></p>\n<p><strong> </strong></p>\n<p><strong>Завдання Синоду</strong></p>\n<p>На початку зустрічі Глава Церкви повідомив, що попередній Синод  визначив групу єпископів, які створили групу №1. Ця група думала над  тим, якою ми хочемо бачити нашу Церкву. У цю групу, серед інших, також  був включений і Блаженніший Святослав і вона мала випрацювати стратегію  розвитку до 2020 р.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=758:lv-ukrajini-vidbuvajetsja-ideologichna-zachystka-molodizhnyh-organizaci-i-ruhivr-blazhennishy-svjatoslav-na-zustrichi-iz-golovamy-molodizhnyh-komisi-&catid=37:spilnota-cat&Itemid=49',2489,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2905,'P','2011-06-14',1,NULL,'Note','Зіслання Святого Духа ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">ТРІЙЦЯ (П\'ятидесятниця) (грецьк. Pentekoste), одне з найбільших  християнських свят, яке святкується на 50-й день від Великодня в пам\'ять  зішестя Святого Духа на апостолів і присвячене прославлянню Святої  Трійці. У Православній Церкві &mdash; велике двунадесяте свято. На Заході  свято П\'ятидесятниці не носить назви Трійці, хоча тісно пов\'язується з  її шануванням. З XIV ст. святом Трійці в Католицькій Церкві стала  називатися неділя по П\'ятидесятниці.</span></span></p>','<p>\r\n<object width=\"425\" height=\"350\" data=\"http://www.youtube.com/v/bot98PlZqNc\" type=\"application/x-shockwave-flash\">\r\n<param name=\"src\" value=\"http://www.youtube.com/v/bot98PlZqNc\" />\r\n</object>\r\n</p>',NULL,NULL,2490,5,1,48),
(2906,'P','2011-06-15',1,NULL,'Rss','«Катарсис в стиле prêt-a-porte»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3974,200,273005.jpg\" />14 червня 2011 року Божого у вівторок після Неділі Зіслання Св. Духа настоятель храму Св. Миколая Чудотворця на Аскольдовій Могилі о. Ігор Онишкевич разом з волонтерами пенітенціарного душпастирства Орестою Цибрівською та Юлею Щербій відвідали жінок Київської слідчої установи, що на Лук’янівці. Сприянням Карітас-Київ (директор о. Роман Сиротич) та доброчинців громади Св. Миколая для жінок були передані Біблії, предмети особистої гігієни та солодощі. Окрім того, на вшановування пам\'яті 150-ліття перевезення останків Тараса Шевченка до Канева, громада посприяла, щоб на кожну камеру було передано по примірнику останнього перевидання Кобзаря.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=759:lkatarsys-v-style-pret-a-porter&catid=25:parafija-cat&Itemid=27',2492,NULL,1,26),
(2907,'P','2011-06-15',1,NULL,'Rss','Блаженніший Святослав: «Для вас я єпископ, але разом з вами я - християнин»','<div class=\"news-picture\"><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/2576.jpg\" height=\"182\" width=\"249\" /></div>\n<p>Імена двох поважних релігійних особистостей звучали під час розмови із Верховним Архиєпископом <strong>Святославом Шевчуком</strong>, новим лідером <strong>Української Греко-Католицької Церкви</strong>. Один помер ще до того, як Архиєпископ народився. Інший – тоді, коли він закоренився у релігійній кар’єрі.</p>\n<p class=\"rtejustify\">Дух Митрополита Андрея Шептицького - благочестивого, кмітливого і  сміливого лідера Української Греко-Католицької Церкви під час ІІ  світової війни, і папи Івана Павла ІІ обіцяють бути провідними силами на  початку подорожі Блаженнішого Святослава, котрий очолює глобальну  церкву, що налічує понад п’ять мільйонів вірних.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=760:blazhennishy-svjatoslav-ldlja-vas-ja-jepyskop-ale-razom-z-vamy-ja-hrystyjanynr&catid=27:gk-v-ukrajini-cat&Itemid=57',2493,NULL,1,26),
(2908,'P','2011-06-16',1,NULL,'Rss','Доля Аскольдового Хреста','<p>«І був розп‘ятий за нас за Понтія Пилата, і страждав, і був похований, і воскрес на третій день згідно з писанням. І вознісся на Небо і сидить праворуч Отця.»</p>\n<p style=\"text-align: right;\">«Символ віри»</p>\n<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3977,200,CRW_0621.JPG\" />Був палений, був плюндрований, був спилений і вознесений на Київських кручах – така доля Хреста з І-ї стаці «Хресної дороги» - «Ісус засуджений на смерть», возведеного на честь пам‘яті князя Аскольда-Миколая, мученика за віру Христову. 14 червня 2011 року Божого спиляний Хрест (11 червня 2011 року Божого) стараннями парафіянина церкви св. Миколая Чудотворця на Аскольдовій Могилі&nbsp; Михайла Теофіловича, будівельника, був повернутий на своє величне місце. Низький уклін, щира дяка, Божої ласки Вам, пане Михайле, і Вашій родині.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=761:dolja-askoldovogo-hresta&catid=25:parafija-cat&Itemid=27',2495,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2909,'P','2011-06-16',1,NULL,'Note','Літній відпочинок дітей','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3979,200,4.JPG\" alt=\"\" />16 червня 2011 року Божого, діти парафії св. Миколая Чудотворця вирушили в  оздоровчо-катехитичний табір в с. Нирків, Заліщанський р-н.  Тернопільської обл..</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p><span style=\"font-size: small;\"> </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Цей табір проводить молодіжна комісія Бучацької єпархії. На оздоровлення наших діток запросив її голова  о. Ігор Середа.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Очікуйте  подальшого інфорування про відпочинок.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В молитві просимо Божого Благословення. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>','<p style=\"text-align: center;\"><strong><img style=\"float: left;\" src=\"image,3979,200,4.JPG\" alt=\"\" width=\"200\" height=\"133\" /></strong></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">16  червня 2011 року Божого, діти парафії св. Миколая Чудотворця вирушили в   оздоровчо-катехитичний табір в с. Нирків, Заліщанський р-н.   Тернопільської обл..</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p><span style=\"font-size: small;\"> </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Цей табір проводить молодіжна комісія Бучацької єпархії. На оздоровлення наших діток запросив її голова  о. Ігор Середа.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Очікуйте  подальшого інфорування про відпочинок.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В молитві просимо Божого Благословення. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3979,200,3.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3979,200,2.JPG\" alt=\"\" /></span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3979,200,6.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3979,200,1.JPG\" alt=\"\" /><br /></span></span></p>',NULL,NULL,2496,4,1,17),
(2910,'P','2011-06-18',1,NULL,'Note','«Усе ,що зробили одному з найменших братів моїх – це ви Мені зробили»','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,3980,200,CRW_0649.JPG\" alt=\"\" />Парафіяльна служба милосердя церкви св. Миколая Чудотворця продовжує своє служіння&nbsp; онко хворим дітям та їхнім родинам. Молебнем до Пресвятої Богородиці розраджували серця та душі діток у лікарні охматдит отець Василь Поточняк та парафіяни нашої церкви. У хвилини розпачу, переживань за дітей, батьки як ніколи відчувають потребу у Божій ласці, заступництві Пресвятої Матері. Лише у щирій молитві відчуваємо силу Божої любові, його благодать і зцілення душі. Лише з Богом і у Бозі ми перебуваємо у спокої та рівновазі.</span></span></p>','<p><img style=\"float: left;\" src=\"image,3980,200,CRW_0635.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3980,200,CRW_0660.JPG\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,3980,200,CRW_0644.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3980,200,CRW_0642.JPG\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,3980,200,CRW_0658.JPG\" alt=\"\" width=\"200\" height=\"133\" /><img style=\"float: right;\" src=\"image,3980,200,CRW_0649.JPG\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,3980,200,CRW_0626.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3980,200,CRW_0655.JPG\" alt=\"\" /></p>',NULL,NULL,2497,5,1,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2911,'P','2011-06-19',1,NULL,'Rss','Петровий (Апостольський) піст','<table style=\"width: 306px;\" align=\"right\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\n<tbody>\n<tr>\n<td rowspan=\"2\" valign=\"top\"><img src=\"http://www.ugcc.org.ua/clear.gif\" height=\"1\" width=\"5\" /></td>\n<td><img src=\"http://www.ugcc.org.ua/clear.gif\" height=\"1\" width=\"300\" /></td>\n</tr>\n<tr>\n<td valign=\"top\"><img src=\"http://www.ugcc.org.ua/uploads/pics/Start-of-Apostles-Fast.jpg\" align=\"top\" border=\"0\" height=\"440\" width=\"300\" /><br /><img src=\"http://www.ugcc.org.ua/clear.gif\" border=\"0\" height=\"5\" width=\"1\" /><br /><center>12 Апостолів</center><br /></td>\n</tr>\n</tbody>\n</table>\n<p><strong>Коротка історія</strong></p>\n<p align=\"justify\">Святі Апостоли молитвою і постом приготовлялися на зіслання Святого Духа. Вони багато молилися й постили перед тим, як почали проповідувати Євангелію. Після молитви й посту вони висвячували нових пресвітерів на апостольську працю, як це читаємо в Діяннях апостольських: „Вони настановили їм по церквах пресвітерів, а після молитви й посту, передали їх Господеві, в якого ті увірували\" (14, 23).</p>\n<p align=\"justify\">Тож і св. Церква з давен-давна молитвою і постом приготовляє своїх вірних до зустрічі великого празника Святих Верховних Апостолів Петра й Павла, що їх пам\'ять святкуємо 12 липня, а наступного дня ще й Собор 12-ти Апостолів. Цей піст наступає по св. П\'ятдесятниці, тому в давнину деколи звали його постом П\'ятдесятниці. Він знаний також, як піст св. Апостолів, піст Апостольський, піст Петрів і піст літній. Наш народ зве його Петрівкою.</p>\n<p align=\"justify\">Піст св. Апостолів дуже давній, бо сягає перших віків християнської віри. З 4 століття маємо про нього свідоцтва св. Атанасія Великого і св. Амвросія Медіолянського, а з 5 ст. св. Льва Великого і Теодорета Кирського.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=342:15-chervnja-2009-roku-pochatok-petrovogo-apostolskogo-postu&catid=27:gk-v-ukrajini-cat&Itemid=57',2498,NULL,1,26),
(2912,'P','2011-06-19',1,NULL,'Rss','ЗАКЛИК Блаженнішого Святослава до молитви у День пам’яті 22 червня 2011 року Божого','<p>Дорогі у Христі!</p>\n<p class=\"rtejustify\">22 червня цього року виповнюється 70 років від того часу, коли на  теренах нашої Батьківщини розпочалося смертельне зіткнення двох  людиноненависницьких ідеологій – фашизму та комунізму. Спершу союзники,  які разом влаштовували переможні паради на крові невинних жертв, того  дня смертельно зударилися між собою. Ця дата ознаменувалася жахливим  кровопролиттям, спричиненим як однією, так і другою стороною. Тому,  безперечно, вона є для нас днем смутку над українською землею, що  просякла кров’ю невинно убієнних, і цього дня особливо звучать Божі  слова: «Що ти вчинив? Ось голос крові брата твого кличе до мене з землі»  (Бут. 4, 10).</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=762:zaklyk-blazhennishogo-svjatoslava-do-molytvy-u-den-pamjati-22-chervnja-2011-roku-bozhogo&catid=27:gk-v-ukrajini-cat&Itemid=57',2499,NULL,1,26),
(2913,'P','2011-06-19',1,NULL,'Rss','Звернення Синоду УГКЦ з нагоди шістдесятиріччя священичих свячень Святішого Отця Венедикта XVI','<div class=\"news-picture\"><img style=\"margin: 10px; float: right;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/17_06_11.jpg\" height=\"182\" width=\"249\" /></div>\n<p><strong>Всесвітліші та всечесніші отці!</strong></p>\n<p class=\"rtejustify\"><strong>Преподобні ченці та черниці!</strong></p>\n<p class=\"rtejustify\"><strong>Дорогі у Христі брати і сестри!</strong></p>\n<p class=\"rtejustify\">&nbsp;29 червня 2011 року Божого Святіший Отець  Венедикт XVI святкуватиме 60-річчя своїх священичих свячень. За Божим  Провидінням цей Вселенський Архиєрей став Христовим священиком саме в  той день, коли Церква святкує празник Святих верховних апостолів Петра і  Павла (за григоріанським календарем). Цей ювілей спонукає нас до  вдячної молитви за дар Христового священства, що його прийняв 60 років  тому Глава Католицької Церкви, а водночас дає нам гарну нагоду  застановитися над важливістю його служіння у&nbsp; Вселенській Церкві.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=763:zvernennja-synodu-ugkc-z-nagody-shistdesjatyrichchja-svjashchenychyh-svjachen-svjatishogo-otcja-venedykta-xvi&catid=32:gk-svit-cat&Itemid=58',2500,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2914,'P','2011-06-21',1,NULL,'Note','До Києва привезуть мощі Івана Павла II','<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right; margin: 5px;\" title=\"Moshchy.gif\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_42950_Moshchy.gif\" border=\"0\" alt=\"Moshchy.gif\" width=\"184\" height=\"184\" />Привезуть  волосся і головний убір понтифіка. Ці мощі вважаються святими так само,  як і кров Івана Павла II, яка є в храмах у кількох європейських  країнах.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">23 червня о 18.00 в соборі Св. Олександра у Києві відбудеться  урочиста служба. Крім того, римо-католики проведуть 40-годинну месу  подяки за приїзд Папи до Києва.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">26 червня мощі понтифіка пронесуть вулицями Києва під час урочистої хресної ходи.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Поклонитися мощам усі охочі можуть в соборі Св. Олександра за адресою вул. Костьольна, 17, з 23 по 26 червня.<span id=\"selection-end\" style=\"position: absolute; overflow: hidden; width: 1px; height: 1px;\"><br />&lt;a href=\"http://risu.org.ua\"&gt;Джерело публікації: risu.org.ua&lt;/a&gt;</span></span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right; margin: 5px;\" title=\"Moshchy.gif\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_42950_Moshchy.gif\" border=\"0\" alt=\"Moshchy.gif\" width=\"184\" height=\"184\" />Привезуть  волосся і головний убір понтифіка. Ці мощі вважаються святими так само,  як і кров Івана Павла II, яка є в храмах у кількох європейських  країнах.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">23 червня о 18.00 в соборі Св. Олександра у Києві відбудеться  урочиста служба. Крім того, римо-католики проведуть 40-годинну месу  подяки за приїзд Папи до Києва.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">26 червня мощі понтифіка пронесуть вулицями Києва під час урочистої хресної ходи.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Поклонитися мощам усі охочі можуть в соборі Св. Олександра за адресою вул. Костьольна, 17, з 23 по 26 червня.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За повідомленням Католицького часопису&nbsp; <a href=\"http://www.credo-ua.org/\" target=\"_blank\">Credo</a>,  мощі Івана Павла ІІ вже побували у Вінниці. Реліквію привезли у  Вінницький санктуарій Божого Милосердя 17 червня. Реліквіарій, у якому  знаходиться піуска Папи, медаль, викарбувана з нагоди його візиту до  України, та волосся блаженного, привіз до Вінниці о. Павло Вишковський  ОМІ, директор Католицького Медіа-Центру. Священик з реліквіями  блаженного Папи-слов&rsquo;янина вже побував у 30 парафіях і планує відвідати  ще 10.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">http://risu.org.ua/</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span id=\"selection-end\" style=\"position: absolute; overflow: hidden; width: 1px; height: 1px;\"><br />&lt;a href=\"http://risu.org.ua\"&gt;Джерело публікації: risu.org.ua&lt;/a&gt;</span></span></span></p>',NULL,NULL,2501,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2915,'P','2011-06-23',1,NULL,'Note','Мощі Блаженного Папи Івана Павла ІІ у Києві','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3987,200,CRW_0786.JPG\" alt=\"\" />23 червня, у 10-ту річницю пастирського візиту Папи Івана Павла ІІ в Україну, мощі Блаженного привезли до конкатедрального собору св. Олександра в Києві. У реліквіарію зберігається І-ша категорія мощей: волосся з голови Папи Івана Павла ІІ, піуска, яку носив Папа та медаль, викарбувана з нагоди його візиту. Сьогодні, як і 10 років тому, Балаженний Папа Іван Павло ІІ поблагословив Україну літнім дощем. Це своєрідне очищення наших сердець, наповнення наших душ і навернення до вчення Христа.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Фото Любові Михайлюк</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3987,200,CRW_0742.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3987,200,CRW_0734.JPG\" alt=\"\" /></span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3987,200,CRW_0747.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3987,200,CRW_0752.JPG\" alt=\"\" /></span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p styl