INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2952,'P','2011-09-17',0,NULL,'Note','«НАМАЛЮЄМО СВІТ РАЗОМ!» - акція присвячена Дню Бога Творця','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,4051,200,456654.jpg\" alt=\"\" />У неділю <strong>18 вересня о 14:00 на  Аскольдовій Могилі</strong> у Києві проходитиме відкрита мистецька акція&nbsp; <strong>&laquo;Намалюємо  світ разом&raquo;, присвячена екологічному дню Бога Творця.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Головна мета акції &ndash; <strong>усвідомлення  відповідальності кожної людини за створений Богом світ</strong>. Усі бажаючі,  малі та дорослі, відтворюватимуть своє бачення світу природи у  спільному малюнку, або ж створюватимуть свої особисті &laquo;картини&raquo; &ndash;  організатори приготували для цього усе необхідне.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,4051,200,456654.jpg\" alt=\"\" />У неділю <strong>18 вересня о 14:00 на Аскольдовій Могилі</strong> у Києві проходитиме відкрита мистецька акція&nbsp; <strong>&laquo;Намалюємо світ разом&raquo;, присвячена екологічному дню Бога Творця.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Особливий гість акції &ndash; відомий сучасний українсько-вірменський художник&nbsp; БОРИС ЄГІАЗАРЯН.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Головна мета акції &ndash; <strong>усвідомлення відповідальності кожної людини за створений Богом світ</strong>. Усі бажаючі, малі та дорослі, відтворюватимуть своє бачення світу природи у спільному малюнку, або ж створюватимуть свої особисті &laquo;картини&raquo; &ndash; організатори приготували для цього усе необхідне.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Блаженніший Любомир (Гузар),</strong> Архиєпископ-емерит виголошуватиме проповідь у намірі збереження створеного Богом світу під час Божественної Літургії, що передуватиме акції</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Акція &laquo;Намалюємо світ разом&raquo; ініційована та проводиться Українською Греко-Католицькою Церквою за підтримки Патріаршого товариства святого рівноапостольного Великого київського князя Володимира.</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"text-decoration: underline;\">Розклад заходів на неділю, 18 вересня, у церкві св.. Миколая Чудотворця на Аскольдовій Могилі (вул. Паркова дорога 1, Київ):</span></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>12:30&ndash;13:45&nbsp;&nbsp; Божественна літургія для дітей за участю Блаженнішого Любомира (Гузара), Архиєпископа-емерита УГКЦ</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>14:00&ndash;16:00&nbsp; &nbsp;&nbsp;&nbsp; Відкрита акція спільного малюнку &laquo;Намалюємо світ разом!&raquo; <br /></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Додаткова інформація:&nbsp;&nbsp; <a href=\"http://www.ugcc.org.ua/\">http://www.ugcc.org.ua</a></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <a href=\"..//\">http://askoldova-mohyla.org</a></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"text-decoration: underline;\"><img style=\"float: left;\" src=\"image,4051,200,tolerant.jpg\" alt=\"\" />Довідково:</span> День Творця, або День Створіння був започаткований у 1989 році Константинопольським Патріархом Димитрієм І. Відтоді у цей день дедалі більше християн в усьому світі об&rsquo;єднують свої зусилля на благо довкілля,&nbsp; дякують Богу за створений світ і моляться про збереження і спасіння усього створіння, яке &laquo;стогне і страждає в тяжких муках&raquo; (Рим. 8, 22).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">2011 рік проголошений Організацією Об&rsquo;єднаних Націй&nbsp; міжнародним роком лісів.&nbsp; З огляду на це, Всесвітня Рада Церков закликає до молитов за збереження лісу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>&copy; УГКЦ<br /></em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>В оголошенні використано текст Володимира Яцульчака</em></span></span></p>',NULL,NULL,2568,5,1,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2953,'P','2011-09-14',1,NULL,'Rss','Знайди 10 відмінностей, або: яка ціна нашої уважної свідомості?','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4053,200,golodomor.jpg\" />Пригадую в дитинстві нашу увагу, зосередженість, пам‘ять тренували грою «знайди 10 відмінностей»: у двох, наче б то схожих малюнках необхідно було знайти відмінності. Подобалась вона мені тоді, сьогодні не дуже… в подіях 5-ти денної давнини відмінностей далеко не 10 а декілька сотень. Останніми днями ЗМІ просто кишать інформацією про те, що на смітнику знайдені меморіальні «хрести-таблички» жертвам голодомору з калинового гаю на схилах Дніпра. Кишать інформацією постфактум і не більше. Жодних свідків події, жодних відповідей від влади чи інших міських служб. Ніде, нічого, ані чик-чи-рик. Запити нардепів, звернення, все наче вода у суху землю. Складається враження, що все так і було, радше нічого не було взагалі.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=789:znady-10-vidminnoste-abo-jaka-cina-nashoji-uvazhnoji-svidomosti&catid=37:spilnota-cat&Itemid=49',2571,NULL,1,26),
(2954,'P','2011-09-14',1,NULL,'Note','Знайти себе у прощі... (світлини)','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,4055,200,6.JPG\" alt=\"\" />Слава Ісусу христу!</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я, Червінський Володимир, перший раз прийняв участь в прощі до Зарваниці. Раніше я проходив різноманітні психологічні тренінги для персонального розвитку та росту, але з впевнінестю можу сказати що проща вище всяких тренінгів. У пішій ході забуваєш про суєту яка панує в повсякденному житті, в цей момент відчуваєш як сходить Святий дух, який дає відповіді на поставленні раніше питання перед собою. Всіх хто хотів піти і засумнівався, всім зневіреним дуже рекомендую відложити свої справи і піти з прочанами. Всім читачам бажаю Божої ласки та благодатті!!!</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Фото Володимира Чернівського</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,4055,200,5.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,4055,200,7.JPG\" alt=\"\" /></span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,4055,200,2.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,4055,200,1.JPG\" alt=\"\" /></span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,4055,200,8.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,4055,200,6.JPG\" alt=\"\" /><br /></span></span></p>',NULL,NULL,2572,5,1,49);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2955,'P','2011-09-15',1,NULL,'Rss','Погляд...','<p style=\"text-align: right;\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4052,200,DSCN9063.JPG\" />Туди, звідкіль вертаються птахи,</p>\n<p style=\"text-align: right;\">Ми дивимось задумливо і тихо,</p>\n<p style=\"text-align: right;\">На жаль, багато ще не знаєм ми</p>\n<p style=\"text-align: right;\">у круговерті днів, словесних віхол.</p>\n<p style=\"text-align: right;\">Не ті слова зроняємо із вуст,</p>\n<p style=\"text-align: right;\">І погляд наш - докірливий, холодний,</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=790:pogljad&catid=1:mystecka-storinka-cat&Itemid=50',2573,NULL,1,26),
(2956,'P','2011-09-23',1,NULL,'Note','Подяка','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Слава Ісусу Христу!</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Думаю, що ніколи не пізно подякувати людям, котрі зробили багато доброго.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Хочу висловити щиру вдячність усім, хто організовував катехитичний табір цього літа для дітей у Рудному. Багатьох я не знаю, а тих, про кого знаю, хотіла би розповісти.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Слава Ісусу Христу!</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Думаю, що ніколи не пізно подякувати людям, котрі зробили багато доброго.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Хочу висловити щиру вдячність усім, хто організовував катехитичний табір цього літа для дітей у Рудному. Багатьох я не знаю, а тих, про кого знаю, хотіла би розповісти.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Приємною несподіванкою для мене стало те, що о. Ігор Онишкевич повідомив: дітям нашої парафії вона буде вполовину дешевшою, хоч коштувала й так недорого, бо що таке нині 150 грн.? Тож хоч тут фінансові проблеми не стали перешкодою.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">О. Роман, о. Ярослав та інші, чиїх імен я навіть не знаю, опікувалися нашими дітьми у Рудному, щоб і відпочили вони, і Слово Боже пізнали.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Моя донька не відзначається спокійною вдачею, тож з нею буває дуже нелегко. У перший же вечір вона впала і вдарилася  це те, що я дізналась наступного дня, та напевне там було не все так просто, бо о. Роман серед ночі возив її у травмпункт, їй зробили знімок і сказали, що все добре, просто є сильне забиття, і приписали мазь, яку отець відразу ж купив. Я всього і не знаю, але впевнена, нелегко було їм з моєю дитиною, і дуже вдячна всім.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Слава Ісусу Христу!</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Світлана</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>',NULL,NULL,2576,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2957,'P','2011-10-05',1,NULL,'Rss','Про Стратегію розвитку УГКЦ на період до 2020 року: «Жива парафія – місце зустрічі з живим Христом» ','<div class=\"news-single-img\"><img style=\"margin: 10px; float: right;\" src=\"http://ugcc.org.ua/typo3temp/pics/2aeaf73e40.jpg\" height=\"104\" width=\"150\" /></div>\n<p class=\"Default\">«Проголошення Доброї Новини всім людям, тобто  євангелізація, − це місія, яку доручив Церкві Господь Бог. Українська  Греко-Католицька Церква, як невід’ємна частина Вселенської Церкви, також  бере участь у цій місії», − сказав у своєму вступному слові під час  прес-конференції в УНІАН 5 жовтня 2011 року Блаженніший Святослав  (Шевчук), Глава УГКЦ, розповідаючи про <em>Стратегію розвитку УГКЦ на період до 2020 року</em>.</p>\n<p class=\"Default\">За словами Предстоятеля<strong> </strong>УГКЦ, для  виконання вищевказаної місії Церкви, єпископи вирішили якнайчіткіше  визначити стратегію діяльності Церкви на наступні роки. Щоб полегшити  втілення цього проекту та кращого відстеження його перебігу, було  визначено період десять років: до 2020 року (включно).</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=793:pro-strategiju-rozvytku-ugkc-na-period-do-2020-roku-lzhyva-parafija--misce-zustrichi-z-zhyvym-hrystomr-&catid=27:gk-v-ukrajini-cat&Itemid=57',2578,NULL,1,26),
(2958,'P','2011-10-05',1,NULL,'Rss','Єпископи УГКЦ написали Президенту України: про українську мову, еміграцію, правосуддя та відносини між Церквами і державою ','<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/74a302dc06.jpg\" height=\"104\" width=\"150\" /></div>\n<p>«Викликає нашу стурбованість те, що на порозі майбутніх  парламентських виборів в Україні деякими політиками та політичними  угрупованнями знову ставиться розмінною монетою питання української  мови. Нас хвилюють рівно ж повідомлення про закриття україномовних шкіл в  деяких регіонах нашої Батьківщини. Ми переконані, що ця вкрай  недалекоглядна і безвідповідальна політика не служить утвердженню  української державності», − з такими словами звернулися єпископи  Української Греко-Католицької Церкви у листі до Президента України  Віктора Януковича. Цей лист вони написали у Бразилії, під час проведення  цьогорічного засідання Синоду Єпископів УГКЦ. Про це 5 жовтня, під час  прес-конференції в УНІАНі, повідомив Блаженніший Святослав (Шевчук),  Глава УГКЦ.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=792:jepyskopy-ugkc-napysaly-prezydentu-ukrajiny-pro-ukrajinsku-movu-emigraciju-pravosuddja-ta-vidnosyny-mizh-cerkvamy-i-derzhavoju-&catid=27:gk-v-ukrajini-cat&Itemid=57',2579,NULL,1,26),
(2959,'P','2011-10-05',1,NULL,'Rss','«ШКІЛЬНИЙ ПОРТФЕЛИК» ДІТЯМ БОЖОГО МИЛОСЕРДЯ вересень 2011 р.Б.','<p align=\"right\"><strong>«Ні про що не журіться, але в усьому появляйте Богові ваші прохання молитвою і благанням з подякою» (Фил.4 ,6)</strong></p>\n<p>Завдяки пожертві від організації&nbsp; “Ліс” пані Ніни Харчук було надано допомогу дітям Божого Милосердя для придбання шкільного приладдя. Кожна дитина, згідно своїх потреб, отримала необхідне для навчання. А в неділю 4 вересня 2011 р. о 12:30 відправлена Божественна Літургія і уділено Благословення для успішного навчання дітей, студентам, вчителям.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=794:lshkilny-portfelykr-ditjam-bozhogo-myloserdja-veresen-2011-rb&catid=25:parafija-cat&Itemid=27',2580,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2960,'P','2011-10-07',1,NULL,'Rss','ЗВІТ №18  про надання матеріальної допомоги “Шкільний портфелик” дітям, покинутим матерями, які відбувають покарання в місцях позбавлення волі (01.09.2011 р.)','<p>Коштом пожертвувань п.Ніни Харчук (орг-ція ЛІС) зібрані та відправлені посилки з матеріальною допомогою з нагоди початку нового навчального року дітям, матері яких відбувають покарання у місцях позбавлення волі.</p>\n<p>Допомога надавалась зошитами, щоденниками, канцелярськими приладдями, продуктами, солодощами, гігієнічними засобами, речами особистого вжитку, що надійшли від жертводавців. Кожна дитина отримала в подарунок молитовничок.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=795:zvit-18-pro-nadannja-materialnoji-dopomogy-shkilny-portfelyk-ditjam-pokynutym-materjamy-jaki-vidbuvajut-pokarannja-v-miscjah-pozbavlennja-voli-01092011-r&catid=25:parafija-cat&Itemid=27',2581,NULL,1,26),
(2961,'P','2011-10-07',1,NULL,'Rss','Трагічна історія українського кобзарства (хроніка)','<p>&nbsp;Вироком Спеціальної колегії Київського обласного суду від 14 жовтня 1935 р. була засуджена група кобзарів, які в 1920-х та на початку 1930-х років подорожували по теренам Київської та Дніпропетровської області, виконуючи народні пісні. Мали вони бібліотеку, де було понад сімсот книжок. Тут пропонується коротка інформація про цих кобзарів.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=796:tragichna-istorija-ukrajinskogo-kobzarstva&catid=37:spilnota-cat&Itemid=49',2583,NULL,1,26),
(2962,'P','2011-10-16',1,NULL,'Rss','Негативну громадську думку про католиків постійно підживлюють різні політичні сили, біляцерковні структури, ЗМІ, – Людмила Филипович','<div class=\"news-picture\"><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/golovna.jpg\" height=\"182\" width=\"249\" /></div>\n<br />\n<p class=\"rtejustify\">Сучасний стан Церкви в Україні, ЇЇ взаємини з владою, а також  непоодинокі випадки утисків греко-католицьких громад, зокрема нещодавній  інцидент в Донецьку, коли вірним УГКЦ відмовилися продовжити оренду,  чим залишили їх без культової споруди, для<a href=\"http://catholicnews.org.ua/\"> «Католицького Оглядача»</a> прокоментувала п.<strong> Людмила Филипович</strong>, релігієзнавець, д-р філос. наук, проф., зав. відділенням релігійних процесів Інституту філософії НАНУ.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=797:negatyvnu-gromadsku-dumku-pro-katolykiv-postino-pidzhyvljujut-rizni-politychni-syly-biljacerkovni-struktury-zmi-ljudmyla-fylypovych&catid=37:spilnota-cat&Itemid=49',2586,NULL,1,26),
(2963,'P','2011-10-18',1,NULL,'Rss','Перемога нашого колективу','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4069,200,DSC_3368.JPG\" />15–16 жовтня у м. Вишгород пройшов ІІІ фестиваль-конкурс «Вишгородська Покрова». Організаторами конкурсу виступили Вишгородська міська рада, вишгородський міський хор «Коралі», громада собору Вишгородської Богородиці УГКЦ та міський центр творчості «Джерело».</p>\n<p>Церкву св. Миколая Чудотворця на Аскольдовій Могилі на конкурсі представляв жіночий ансамбль «Аніма» (керівник Надія Кучерява), який другий рік поспіль долучається до цього гарного пісенного свята.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=798:peremoga-nashogo-kolektyvu&catid=25:parafija-cat&Itemid=27',2587,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2964,'P','2011-10-18',1,NULL,'Rss','«Єдина Помісна Церква в Україні може відбутися скоріше, аніж ми думаємо», - Блаженніший Святослав','<div class=\"nimages\"><img style=\"margin: 10px; float: left;\" src=\"http://galinfo.com.ua/gallery/intxt/c/d/cd.jpg\" /></div>\n<p>Блаженніший Патріарх Святослав (Шевчук) – один з наймолодших  очільників Української греко-католицької церкви. Із його обранням Главою  УГКЦ, повязують нове дихання Церкви та посилення її впливу на суспільні  процеси. Молодість та енергія Блаженнішого дозволяє активніше  впроваджувати ідеї й принципи християнського буття не лише в Україні,  але й за її межами.</p>\n<p>Обрання Святослава (Шевчука) Патріархом було неочікуваним, однак  прогнозованим. Вірні УГКЦ позитивно та з надією сприйняли молодого Главу  Церкви.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=799:ljedyna-pomisna-cerkva-v-ukrajini-mozhe-vidbutysja-skorishe-anizh-my-dumajemor-blazhennishy-svjatoslav&catid=27:gk-v-ukrajini-cat&Itemid=57',2588,NULL,1,26),
(2965,'P','2011-10-25',1,NULL,'Rss','Дорога до Риму','<p><strong> </strong></p>\n<p><img ilo-full-src=\"http://askoldova-mohyla.org/uk/image,4073,200,1353.jpg\" style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4073,200,1353.jpg\" />З 13 по 21 жовтня громада греко-католиків з київських церков на чолі з парохом Церкви Св. Миколая, що на Аскольдовій Могилі о.Ігорем Онишкевичем, перебувала на прощі до Риму. Серед 45 прочан основну більшість становили мешканці Києва, а також Львова,&nbsp; Тернополя та Житомира.</p>\n<p>У самому Римі ми фактично перебували всього півтора дні, а решта часу – це була дорога до нього. Ми відбули святі літургії з місцевими греко-католицькими священниками та о. Ігорем в містах Європи:&nbsp; Брно (Чехія), Відні (Австрія), Римі (Італія) і Празі (Чехія). Протягом всієї дороги, а це 5 ночей та декілька днів уривками в автобусі, ми мали щодня Утреню, Вечірню, Літургію годин, Божественну Літургію, і молитву на вервичці&nbsp; за участю о. Ігоря і наших парафіянок Дмитровської Руслани (учасниці церковного хору Церкви Св. Миколая, що на Аскольдовій могилі ) та Ірини Журило (з тієї ж парафії), яка допомагала нам молитися на вервичці. Вервички о. Ігор всім подарував перед поїздкою. А також ми&nbsp; мали з собою&nbsp;&nbsp; значки із зображенням Папи Римського Івана Павла II біля Церкви на Аскольдовій могилі під час його туру Україною у 2001році. Парохам, з якими ми проводили Святі Літургії у Європі, також було подаровано ці відзнаки.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=800:doroga-do-rymu&catid=25:parafija-cat&Itemid=27',2591,NULL,1,26),
(2966,'P','2011-10-28',1,NULL,'Rss','Проблеми підлітків. Тиск і відкинення однолітків','<p align=\"justify\">В книзі Дж. Мак-Дауелла і Б.Хостетлера «Як допомогти молоді» описано, як  матір випадково прочитала щоденник своєї дочки-підлітка, намагаючись  зрозуміти дивну поведінку своєї дитини останнім часом. В щоденнику було  описано сексуальний досвід. Коли матір спробувала поговорити з донькою,  та спочатку неймовірно обурилась, потім «почала плакати, але між  риданнями вона розповіла історію відкинення і жорстокості, зневажливі  погляди однокласників і безсердечні жарти, коли її впихали  в шкафчик і  копали в дверку, як учні обливали її їжею і напоями, коли вона сиділа  одиноко в шкільній їдальні...</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=801:problemy-pidlitkiv-tysk-i-vidkynennja-odnolitkiv&catid=37:spilnota-cat&Itemid=49',2593,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2967,'P','2011-10-24',1,NULL,'Note','Реколекції для подружніх пар','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><img style=\"float: right;\" src=\"image,4072,200,para.jpg\" alt=\"\" width=\"97\" height=\"147\" />9</strong><strong>-</strong><strong>11</strong><strong> </strong><strong>грудня 201</strong><strong>1</strong><strong> року</strong> в монастирі Місії Облати Непорочної Марії&nbsp; в&nbsp; Обухові відбудуться <strong>реколекції для подружніх пар.</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Початок реколекцій о <strong>17:00 у п&rsquo;ятницю</strong>, завершення &ndash; о <strong>15:00 в неділю</strong>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Обов&rsquo;язковий попередній запис</strong> на реколекції можна здійснити за телефонами:</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Київський   осередок руху &laquo;Подружні   зустрічі&raquo; УГКЦ</span></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Слава Ісусу Христу!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><img style=\"float: right;\" src=\"image,4072,200,para.jpg\" alt=\"\" />9</strong><strong>-</strong><strong>11</strong><strong> </strong><strong>грудня 201</strong><strong>1</strong><strong> року</strong> в монастирі Місії Облати Непорочної Марії&nbsp; в&nbsp; Обухові відбудуться <strong>реколекції для подружніх пар.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"> </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Під час реколекцій кожне подружжя може відновити свіжість взаємних почуттів, поглибити взаємну любов чи наново відкрити її, відновити пережиття змісту тайни подружжя. Участь у реколекціях дає можливість чоловікові і дружині краще зрозуміти один одного, навчитися долати конфлікти.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"> </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На реколекції запрошуються подружні пари (обов&rsquo;язкова присутність чоловіка і дружини). Сімейний стаж значення не має.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Початок реколекцій о <strong>17:00 у п&rsquo;ятницю</strong>, завершення &ndash; о <strong>15:00 в неділю</strong>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Обов&rsquo;язковий попередній запис</strong> на реколекції можна здійснити за телефонами:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">050&nbsp;443 1421 (Марта), 050&nbsp;315 0547 (Андрій).</span></span></p>',NULL,NULL,2589,5,1,41);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2968,'P','2011-10-26',1,NULL,'Note','Блаженніший Любомир (Гузар) − учасник програми «Глибинне буріння»','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У суботу, 22 жовтня, о 12.30 на Першому  національному телеканалі (УТ-1) Блаженніший Любомир (Гузар) буде  учасником програми &laquo;Глибинне буріння&raquo; з Андрієм Пальчевським. Програма  була записана напередодні у заміській резиденції Глави УГКЦ у Києві.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> \r\n<object width=\"425\" height=\"350\" data=\"http://www.youtube.com/v/k43HyAC5MWI&amp;feature\" type=\"application/x-shockwave-flash\">\r\n<param name=\"src\" value=\"http://www.youtube.com/v/k43HyAC5MWI&amp;feature\" />\r\n</object>\r\n</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У суботу, 22 жовтня, о 12.30 на Першому  національному телеканалі (УТ-1) Блаженніший Любомир (Гузар) буде  учасником програми &laquo;Глибинне буріння&raquo; з Андрієм Пальчевським. Програма  була записана напередодні у заміській резиденції Глави УГКЦ у Києві.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Під час ефіру співрозмовники обговорили  широке коло питань, розпочинаючи від віри в Бога, існування зла у світі,  щастя, до питань справедливості, патріотизму та єдності українського  суспільства.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Якщо б в Україні сьогодні кожний з нас  застановився і сказав, що від цього моменту він буде старатися робити  тільки те, що корисне, що добре для мене, для ближніх, для мого народу &ndash;  ми б були найщасливішою державою, найщасливішим народом на земній кулі.  Нас ніхто не зробить щасливішими, а лише кожний з нас зокрема і всі ми  разом&raquo;, &minus; сказав Блаженніший Любомир, відповідаючи на запитання про те,  як допомогти громадянам нашої держави почуватися більш щасливими.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Повтор програми &laquo;Глибинне буріння&raquo; за участю  Блаженнішого Любомира можна подивитися у середу, 26 жовтня, о 20.20.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Джерело: <a href=\"http://www.ugcc.org.ua/\" target=\"_blank\">Департамент інформації УГКЦ</a></span></span></p>',NULL,NULL,2592,5,1,42),
(2969,'P','2011-10-30',1,NULL,'Rss','Шляхами історії','<p>22 жовтня, в суботу, група парафіян церкви Святого Миколая на Аскольдовій Могилі, у Києві, здійснила поїздку до трьох історичних місць на Черкащині, де творилися одні з найвизначніших сторінок української історії. Ми відвідали Чигирин – гетьманську столицю часів гетьманування Богдана Хмельницького. Оглянули музей та відновлену резиденцію Богдана Хмельницького. Побачене приємно вражає своєю красою. За допомогою екскурсовода дізналися про умови життя і звичаї того часу.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=803:shljahamy-istoriji&catid=25:parafija-cat&Itemid=27',2596,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2970,'P','2011-11-01',1,NULL,'Note','ЗВІТ №21  про надання матеріальної допомоги дітям, які виховуються в Будинках матері та дитини при виправних колоніях м.Чернігова та м.Одеси (04.10.2011 р.)','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Коштом пожертвувань добродіїв: п.Анжели та п.Віталія зібрані та  відправлені посилки з матеріальною допомогою дітям, які виховуються в  Будинку матері та дитини при Чернігівській та Чорноморській (м.Одеса)  виправних колоніях, в яких відбувають покарання їхні матері.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомога надавалась дитячим одягом, памперсами та гігієнічними засобами, придбаними жертводавцями.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Робота виконана силами волонтерів: п.Марти, п.Василя, п.Михайла та п.Анни.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Коротка інформація про надану допомогу:</span></span></p>','<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Коштом пожертвувань добродіїв: п.Анжели та п.Віталія зібрані та відправлені посилки з матеріальною допомогою дітям, які виховуються в Будинку матері та дитини при Чернігівській та Чорноморській (м.Одеса) виправних колоніях, в яких відбувають покарання їхні матері.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Допомога надавалась дитячим одягом, памперсами та гігієнічними засобами, придбаними жертводавцями.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Робота виконана силами волонтерів: п.Марти, п.Василя, п.Михайла та п.Анни.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Коротка інформація про надану допомогу:</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>1.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Допомога дітям Будинку матері та дитини при Чернігівській ВК на суму 2183 грн. 80 коп.</strong></span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>2.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Допомога дітям Будинку матері та дитини при Чорноморській ВК (м.Одеса) на суму 2070 грн. 80 коп.</strong></span></span></li>\r\n</ol>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; В підсумку надано матеріальну допомогу поштовим відправленням від 04.10.2011 р. на суму <strong>4 254 грн. 60 коп.</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Крім того, вартість:</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">поштових коробок &ndash; <strong>36 грн.</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">пересилання &mdash; <strong>120 грн. 12 коп.</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума витрат від&nbsp; 04.10.2011 р. &mdash; 4 410 грн. 72 коп.</strong></span></span></p>',NULL,NULL,2598,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2971,'P','2011-11-10',1,NULL,'Note','Господь рясно благословляє тих, хто Йому служить.','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Є в нашій церкві важливе служіння, це &ndash; служіння милосердя, яке вже на протязі багатьох років опікується ув&rsquo;язненими жінками та їх маленькими діточками. Волонтери нашої церкви по можливості приймають безпосередню участь в житті цих жінок та їх діточок.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>Шановні парафіяни та гості нашої церкви!</em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Приємно бачити, якою активною є наша церква та ми з вами. Це підтверджується нашими ділами, оскільки розвиваються та діють різні церковні та соціальні служіння у церкві на Аскольдовій могилі; більшість наших парафіян беруть активну участь у певному служінні чи допомозі потребуючим, зо що щира їм подяка!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Господь рясно благословляє тих, хто Йому служить, адже віра без діл є мертвою, тож наша справжня віра проявляється й також через благочинні справи.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Є в нашій церкві важливе служіння, це &ndash; служіння милосердя, яке вже на протязі багатьох років опікується ув&rsquo;язненими жінками та їх маленькими діточками. Волонтери нашої церкви по можливості приймають безпосередню участь в житті цих жінок та їх діточок.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Можливо, є серед нас люди, які ще не задіяні ні в якому служінні. Я звертаюсь саме до вас, шановні брати та сестри.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Наше служіння милосердя є дуже цікавим і приємним. Ми потребуємо вашої допомоги та підтримки. А особливо ув&rsquo;язнені люди чекають вашої уваги, адже багатьом з них немає до кого звернутися навіть за порадою. Ми намагаємося підтримати жінок, які опинилися за ґратами через листування, відвідини в&rsquo;язниць, духовну та матеріальну допомогу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Волонтери нашого служіння отримують великі благословення від Господа, адже Сам Господь сказав у Євангелії: <strong><em>&laquo;Бо я голодував, і ви дали мені їсти&hellip; хворий, і ви навідались до мене; у тюрмі був, і ви прийшли до мене&hellip; Усе, що ви зробили одному з моїх братів найменших &ndash; ви мені зробили&raquo; - &nbsp;</em></strong><em>(Євангеліє від Матвія 25 розділ, вірші 35-40).</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">І сьогодні наш Господь Ісус Христос є не тільки зображеним на образах, Він також перебуває і у в&rsquo;язниці у серцях наших ближніх. Можливо, саме там Він на Вас чекає, саме на вашу допомогу та підтримку. Адже, допомагаючи людям, що позбавлені волі, ми допомагаємо Христу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Отож, <strong><em>запрошую Вас приєднатися</em></strong> до цього благословенного служіння нашому Господу та ближнім!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Будемо раді бачити вас серед наших волонтерів, познайомитися з вами ближче, спілкуватися та разом виконувати цю важливу працю. Адже ніхто з нас не знає, чого завтра будемо потребувати ми самі та звідки і яку допомогу отримаємо. Тож, не вагайтеся допомогти комусь сьогодні, поки ще є час і нагода.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За благословенням пароха церкви святого Миколая, що на Аскольдовій Могилі прот. Ігоря Онишкевича відповідальною за дану волонтерську службу призначається п.Вікторія Леонова.</span></span></p>',NULL,NULL,2601,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2972,'P','2011-11-14',1,NULL,'Rss','Служіння у рамках Суспільного тижня ','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4084,200,DSC05426.JPG\" />11 листопада 2011 р. в рамках служіння Суспільного тижня проголошеного церквою, церковна служба християнського милосердя « Карітас-Печерськ» парафії св. Миколая Чудотворця на Аскольдовій Могилі відвідали Чернігівську виправну жіночу колонію. У служінні милосердя взяли участь: о. Ігор Онишкевич з парафіянами-волонтерами Ірина Журило і артисти. Біля виправного закладу нас зустріли с.Оксана Пелех та с. Христина Пецольд, які бувають тут досить часто: приносять передачі, виконують деякі прохання жінок, що перебувають в колонії, а ще, неподалік Чернігова, в селищі Любичі разом з священником редемптористом о. Віталієм Котиком працюють над створенням реабіталіційного центру для жінок, що звільняються. Місцева влада для цих цілей віддала старе приміщення швейного училища, де зараз коштом жертводавців провадиться ремонт.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=805:sluzhinnja-u-ramkah-suspilnogo-tyzhnja-&catid=27:gk-v-ukrajini-cat&Itemid=57',2602,NULL,1,26),
(2973,'P','2011-11-14',0,NULL,'Note','ЗВІТ №22 про розподілення благодійних пожертвувань парафіян церкви св.Миколая на Аскольдовій Могилі ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Служба  милосердя при церкві св.Миколая на Аскольдовій Могилі, зібравши  пожертвувані парафіянами: одяг чоловічий, жіночий та дитячий, взуття,  ігри, іграшки дитячі та інші речі особистого вжитку, розподілила їх  таким чином:</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Служба милосердя при церкві св.Миколая на Аскольдовій Могилі, зібравши пожертвувані парафіянами: одяг чоловічий, жіночий та дитячий, взуття, ігри, іграшки дитячі та інші речі особистого вжитку, розподілила їх таким чином:</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&mdash; уділила потребуючим мирянам;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&mdash;&nbsp; відправила гуманітарний вантаж Благодійному фонду &ldquo;Сузір\'я&rdquo; в с.Іванкові Бориспільського р-ну Київської обл. &ndash; 20 мішків 11.10.2011 р.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Робота виконана силами волонтерів: п.Михайла, п.Василя, п.Олександра, п.Надії, п.Анни.</span></span></p>',NULL,NULL,2604,5,1,19),
(2974,'P','2011-12-15',0,NULL,'Note','Різдвяний подарунок покинутій дитині','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,4092,200,maket_2_rab.jpg\" alt=\"\" />Парафіяльна служба милосердя церкви св.. Миколая Чудотворця на Аскольдовій Могилі запрошує долучитися до церковного проекту &laquo;Різдвяний подарунок покинутій дитині&raquo;.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Простягніть руку допомоги. Пам&rsquo;ятаймо дітей, матері котрих знаходяться у місцях позбавлення волі, ці діти потребують тепла та уваги, нашої підтримки, нашої помочі.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,4092,200,maket_2_rab.jpg\" alt=\"\" />Парафіяльна служба милосердя церкви св.. Миколая Чудотворця на Аскольдовій Могилі запрошує долучитися до церковного проекту &laquo;Різдвяний подарунок покинутій дитині&raquo;.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Простягніть руку допомоги. Пам&rsquo;ятаймо дітей, матері котрих знаходяться у місцях позбавлення волі, ці діти потребують тепла та уваги, нашої підтримки, нашої помочі.</span></span></p>',NULL,NULL,2609,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2975,'P','2011-11-24',1,NULL,'Note','ПОЖЕРТВИ ПАРАФІЯН ЦЕРКВИ СВ. МИКОЛАЯ ЧУДОТВОРЦЯ НА АСКОЛЬДОВІЙ МОГИЛІ У ЖОВТНІ 2011 Р. Б.','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</em></strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<table style=\"width: 475px; text-align: justify;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"57\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>№ з/п</strong></span></span></p>\r\n</td>\r\n<td width=\"418\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Ж Е Р Т В О Д А В Ц І</strong></span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"57\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1</span></span></p>\r\n</td>\r\n<td width=\"418\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Марія Стельмах</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"57\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">2</span></span></p>\r\n</td>\r\n<td width=\"418\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Юлія (син Іван)</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"57\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">3</span></span></p>\r\n</td>\r\n<td width=\"418\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Люба</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"57\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">4</span></span></p>\r\n</td>\r\n<td width=\"418\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Родина Ільків</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"57\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">5</span></span></p>\r\n</td>\r\n<td width=\"418\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Олег Лужний</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"57\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">6</span></span></p>\r\n</td>\r\n<td width=\"418\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Мирослав, Лариса, Еліна, Каріна та Іван</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"57\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">7</span></span></p>\r\n</td>\r\n<td width=\"418\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Володимир та Світлана</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"57\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8</span></span></p>\r\n</td>\r\n<td width=\"418\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Марина</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"57\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">9</span></span></p>\r\n</td>\r\n<td width=\"418\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ірина</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"57\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">10</span></span></p>\r\n</td>\r\n<td width=\"418\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Юрій Палош</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"57\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">11</span></span></p>\r\n</td>\r\n<td width=\"418\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Родина Цікало (Іван, Людмила та Янна)</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"57\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">12</span></span></p>\r\n</td>\r\n<td width=\"418\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">(через Ірину Гузенко)</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"57\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">13</span></span></p>\r\n</td>\r\n<td width=\"418\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Едуард та сина Аліка</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"57\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">14</span></span></p>\r\n</td>\r\n<td width=\"418\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Радіона, Ольга та Романа</span></span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>Милостиню роби згідно з тим, що маєш; коли у тебе достатків повно, Роби з них,&nbsp; а коли мало маєш, то не бійся з того дати. Бо так готуєш собі добрий скарб на день потреби.</em></strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Товит 4. 8-9.</strong></span></span></p>',NULL,NULL,2610,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2976,'P','2011-11-24',1,NULL,'Rss','Пам’яті Йосафати Гордашевської','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4092,200,IMG_2794.JPG\" />20 листопада на Аскольдовій Могилі вірні парафіяни просили заступництва у св. Йосафати Гордашевської.</p>\n<p>Народжена у Львові, Михайлина (сестра Йосафата) Гордашевська стала першою черницею згромадження Сестер Служебниць Непорочної Діви Марії. Під її керівництвом семеро перших послушниць розпочали свою духовну формацію. Саме сестра Йосафата започаткувала служіння в галузі освіти й охорони здоров\'я, також відповідала та курувала відкриття нових місій.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=806:pamjati-osafaty-gordashevskoji&catid=25:parafija-cat&Itemid=27',2611,NULL,1,26),
(2977,'P','2011-11-24',1,NULL,'Rss','Зарваницька Богородиця в Золотоноші','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4092,200,PB060803.JPG\" />За Благословенням Владики Йосифа Міляна помічника Київської Архиєпархії,у неділю 30 жовтня 2011 р Б. На Аскольдовій Могилі парафії Святого Миколая було передано копію Чудотворної Ікони Зарваницької матері Божої, до парафії Святого Духа, м. Золотоноша, щоб Зарваницька Матір Божа була покровителькою та Заступницею м. Золотоноша та всіх парафін.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=807:zarvanycka-bogorodycja-v-zolotonoshi&catid=25:parafija-cat&Itemid=27',2612,NULL,1,26),
(2978,'P','2011-11-26',1,NULL,'Rss','Пам\'яті жертв голодомору','<p style=\"text-align: right;\" class=\"uiStreamMessage\" data-ft=\"{&quot;type&quot;:1}\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4092,200,305153_197723566975775_100002143980527_445030_557574148_n.jpg\" /><span style=\"font-size: 12pt;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Минає час, пливе рікою,<br /> Кудись звертає за бугор.<br /> Та пам`ять ця завжди зі мною<br /> Про той страшний голодомор.<br /> Забути все дитячі очі<br /> На фотокартках не дають,<br /> Серденько крають проти ночі<br /> І тінями в думках снують.<br /> Ніяк не можу зрозуміти,<br /> Збагнути те не можу сам:<br /> Що завинили бідні діти<br /> «Червоним» нелюдам – катам?!</span></span></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=808:pamjati-zhurtv-golodomoru&catid=1:mystecka-storinka-cat&Itemid=50',2614,NULL,1,26),
(2979,'P','2011-12-02',1,NULL,'Rss','Звернення традиційних Українських Церков до вірних та до всіх людей доброї волі з нагоди двадцятиліття референдуму на підтвердження Акту проголошення незалежності України','<p style=\"text-align: right;\"><em>Сказав Господь:<br /> «Шукайте ж спершу  Царства Божого і правди Його, <br /> а все це додасться вам» (М</em><em>т.</em><em> 6, 33)</em></p>\n<p style=\"text-align: center;\">Дорогі у Христі брати і сестри!</p>\n<p>Двадцять років тому наш народ підтвердив свій вибір – жити у  самостійній, незалежній державі. Перед нами відкрилися великі  можливості, а разом з тим з’явилася і велика надія, що нарешті, після  століть поневолення і розділень, наша держава духовно й матеріально  розквітне.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=810:zvernennja-tradycinyh-ukrajinskyh-cerkov-do-virnyh-ta-do-vsih-ljude-dobroji-voli-z-nagody-dvadcjatylittja-referendumu-na-pidtverdzhennja-aktu-progoloshennja-nezalezhnosti-ukrajiny&catid=27',2616,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2980,'P','2011-12-05',1,NULL,'Rss','\"Жінка має поєднувати в собі дві протилежності\"- глава УГКЦ Святослав Шевчук','<p><strong>– Роблю все, що від мене залежить, </strong>але вище голови  не стрибну. Блаженнійший увесь час у роз\'їздах,&nbsp;– виправдовується  телефоном отець Ігор Яців, прес­секретар глави Української  греко­католицької церкви 41­річного Святослава Шевчука. Майже півроку  намагаюся домовитись про інтерв\'ю з владикою. У понеділок, 31 жовтня  таки дочекалася. Біля станції столичного метро \"Лісова\", поблизу  заправки нас зустрічає Ігор Яців. Дверцята його чорного Chevrolet з боку водія побиті. Напередодні ввечері отець потрапив у аварію.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=811:qzhinka-maje-pojednuvaty-v-sobi-dvi-protylezhnostiq-glava-ugkc-svjatoslav-shevchuk&catid=27:gk-v-ukrajini-cat&Itemid=57',2617,NULL,1,26),
(2981,'P','2011-12-05',1,NULL,'Note','2012 рік УГКЦ в Україні відзначатиме як Рік Святих Таїнств','<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left; margin: 5px;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_rik_sv_tajn.jpg.jpg\" alt=\"\" width=\"99\" height=\"146\" />У  контексті підготовки до святкування 1025-річчя Хрещення  Русі-України,&nbsp;2012 рік УГКЦ в Україні відзначатиме як Рік Святих  Таїнств. Водночас, Синод Єпископів УГКЦ проголосив в усіх єпархіях  Європи, Азії, Америки та Австралії наступний рік &ndash; Роком християнського  покликання з особливим наголосом на покликання мирян.</span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Відзначення Року Святих Таїнств  розпочнеться з Різдвяного посту 2011 роздумами над народженням Божого  Сина. Особливу увагу до таїнств Хрещення і Миропомазання приділять від  Різдвяних свят до Стрітення. У Великий піст увага буде зосереджена на  таїнстві Покаяння, а тиждень до та після Великодня &ndash; таїнстві  Євхаристії.</span></span></p>','<div style=\"text-align: justify;\">\r\n<div id=\"social_block\">\r\n<div><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><a title=\"Send to Facebook\" href=\"http://www.ugcc.org.ua/2057.0.html\">&nbsp;</a> <a title=\"Tweet This\" href=\"http://www.ugcc.org.ua/2057.0.html\">&nbsp;</a> </span></span></div>\r\n</div>\r\n<span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"> <img style=\"float: left; margin: 5px;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_rik_sv_tajn.jpg.jpg\" alt=\"\" width=\"99\" height=\"146\" />У  контексті підготовки до святкування 1025-річчя Хрещення  Русі-України,&nbsp;2012 рік УГКЦ в Україні відзначатиме як Рік Святих  Таїнств. Водночас, Синод Єпископів УГКЦ проголосив в усіх єпархіях  Європи, Азії, Америки та Австралії наступний рік &ndash; Роком християнського  покликання з особливим наголосом на покликання мирян.</span></span></div>\r\n<p><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Відзначення Року Святих Таїнств  розпочнеться з Різдвяного посту 2011 роздумами над народженням Божого  Сина. Особливу увагу до таїнств Хрещення і Миропомазання приділять від  Різдвяних свят до Стрітення. У Великий піст увага буде зосереджена на  таїнстві Покаяння, а тиждень до та після Великодня &ndash; таїнстві  Євхаристії.</span></span></p>\r\n<p><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Протягом п&rsquo;ятдесяти днів від празника  Зіслання Святого Духа головну увагу в роздумах буде зосереджено на  таїнствах Священства і Подружжя. Про таїнство Єлеопомазання  роздумуватимуть у період посту напередодні свята апостолів Петра і  Павла. </span></span></p>\r\n<p><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Про важливість посвячень та  благословень у Церкві роздумуватимуть в період Успенського посту (від 14  до 27 серпня), а час після Успіння Пресвятої Богородиці стане нагодою  для роздумів про християнське значення смерті. З початком церковного  року (14 вересня) учасники роздумів зможуть зосередитися на таїнствах,  як джерелі парафіяльного життя.</span></span></p>\r\n<p><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Відповідно до програми, заплановано  заходи на загальноцерковному та єпархіальному рівнях. Передбачено різні  заходи, зокрема реколекції, катехитичні науки, з&rsquo;їзди, Євхаристійні  тижні, прощі, семінари та християнські табори. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\">http://www.ugcc.org.ua/2057.0.html</span></p>',NULL,NULL,2618,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(2982,'P','2011-12-06',1,NULL,'Rss','Глава УКГЦ Святослав Шевчук: Нас вважають опозицією. Ч.1','<div class=\"img\"><img style=\"margin: 10px; float: right;\" src=\"http://i.obozrevatel.ua/8/1223978/433214.jpg\" alt=\"Глава УКГЦ Святослав Шевчук: Нас вважають опозицією. Ч.1\" height=\"163\" width=\"245\" /></div>\n<p>Ввечері 1 грудня ми разом із керівником прес-департаменту  Української Греко-католицької церкви отцем Ігорем Яцівим їдемо у Княжичі  – місцину під Києвом, де розташована резиденція глави УКГЦ. Дорога  знайома – раніше нею прямували на зустріч до Любомира Гузара, котрий  цього року з власної волі склав обов’язки глави церкви (випадок  безпрецедентний!), щоб поступитися місцем молодшому і міцнішому  здоров’ям – Святославу Шевчуку.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=812:glava-ukgc-svjatoslav-shevchuk-nas-vvazhajut-opozycijeju-ch1&catid=27:gk-v-ukrajini-cat&Itemid=57',2619,NULL,1,26),
(2983,'P','2011-12-17',1,NULL,'Rss','«У Йосифа Сліпого була далекоглядність Андрея Шептицького»','<div class=\"news-picture\"><img style=\"margin: 10px; float: right;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/230-9-1.jpg\" height=\"182\" width=\"249\" /></div>\n<p><strong>Особистий секретар Глави УГКЦ о. Іван Дацько — про Патріарха і про сучасну релігійну ситуацію</strong></p>\n<p>Про кардинала Йосифа Сліпого, главу Української греко-католицької  церкви (1944 — 1984), говорив увесь світ ще до того, як у Радянському  Союзі почули термін «горбачовська перебудова». Про його звільнення  клопотав американський президент Джон Кеннеді, а стараннями Папи Івана  XXIII йому було дозволено виїхати у вільний світ — до Риму. В жовтні  1963-го він виступив із промовою на ІІ Ватиканському Соборі, де,  всупереч традиції, його вітали оплесками. Після 18 років ГУЛАГу він ще  чверть століття керував Церквою. Попрощатися з ним прийшов Папа Іван  Павло ІІ, і знову, всупереч традиції, поцілував його руку. Згідно із  заповітом, 7 вересня 1992-го тіло Патріарха Йосифа перепоховане у крипті  Львівського кафедрального собору св. Юра.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=813:lu-osyfa-slipogo-bula-dalekogljadnist-andreja-sheptyckogor&catid=35:istorychni-postati-cat&Itemid=48',2621,NULL,1,26),
(2984,'P','2011-12-22',1,NULL,'Rss','Результати VIІІ фестивалю-конкурсу духовної пісні «Аскольдів глас»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4107,200,CRW_3090.JPG\" /></p>\n<p>17 грудня 2011 р. у затишній залі НПУ ім. М.Драгоманова відбувся VIІІ фестиваль-конкурс духовної пісні «Аскольдів глас». Пісенне свято розпочалося із привітального слова ректора Київської Трьохсвятительської духовної семінарії УГКЦ – о. Петра Жука.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=814:rezultaty-viii-festyvalju-konkursu-duhovnoji-pisni-laskoldiv-glasr&catid=25:parafija-cat&Itemid=27',2625,NULL,1,26),
(2985,'P','2011-12-24',1,NULL,'Rss','РІЗДВЯНЕ ПОСЛАННЯ БЛАЖЕННІШОГО СВЯТОСЛАВА','<p style=\"text-align: center;\" class=\"rteright\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4106,200,CRW_3549.JPG\" />Високопреосвященним і Преосвященним Архиєпископам та Митрополитам,<br /> боголюбивим єпископам, всечесному духовенству, преподобному монашеству,<br /> возлюбленим братам і сестрам, в Україні та на поселеннях у світі сущим</p>\n<p style=\"text-align: right;\"><em><strong>Христос народжується – славте!<br /> Христос із небес – зустрічайте!<br /> Христос на землі – бадьортеся!<br /> </strong>Канон Утрені Різдва Христового</em></p>\n<p style=\"text-align: center;\"><strong>Дорогі у Христі! </strong></p>\n<p style=\"text-align: center;\"><strong>&nbsp;</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=815:rizdvjane-poslannja-blazhennishogo-svjatoslava&catid=27:gk-v-ukrajini-cat&Itemid=57',2627,NULL,1,26);
