INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3005,'P','2012-01-30',1,NULL,'Rss','Відпочинок на лижах або подорож у Мигово','<p><img style=\"margin: 10px; float: left;\" src=\"http://oranta.org/image,4139,200,DSCN4499.JPG\" /><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4139,200,DSCN4499.JPG\" />26 січня.</p>\n<p>Цей день з нетерпінням чекало 14 дітей з церкви святого Миколая, що на Аскольдовій Могилі. Адже сьогодні з Київського вокзалу вони разом з трьома вихователями та священиком Анатолієм Теслею відправляється потяг Київ-Чернівці до&nbsp; туристичного комплексу Мигово. Весела метушня в поїзді так втомила дітей, що вони поринули у сон.<br /><br /></p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=827:vidpochynok-na-lyzhah-abo-podorozh-u-mygovo&catid=25:parafija-cat&Itemid=27',2657,NULL,1,26),
(3006,'P','2012-01-31',1,NULL,'Rss','І по той бік паркану є життя','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4140,200,CRW_4667.JPG\" />Парафіяльна служба милосердя «Карітас-Печерськ» церкви св. Миколая Чудотворця на Аскольдовій Могилі продовжує свою опіку над жінками, які перебувають у закладах позбавлення волі. Цього разу ми провідали жінок в уманській виправної колонії №129. Нас було п’ятеро: отець Ігор Онишкевич, настоятель храму, поет і співак Зеновій Филипчук, волонтери пані Ганна Мушинська, що опікується разом з пані Маркою Бліхар жінками в неволі, Любов Михайлюк, та Орина Маслова.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=828:i-po-to-bik-parkanu-je-zhyttja&catid=25:parafija-cat&Itemid=27',2658,NULL,1,26),
(3007,'P','2012-02-01',1,NULL,'Note','Відпочинок у Мигово - фоторепортаж','<p><img style=\"float: left;\" src=\"image,4142,200,4.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,4142,200,5.JPG\" alt=\"\" /></p>','<p>Фото Ігоря Качура</p>\r\n<p><img style=\"float: left;\" src=\"image,4142,200,4.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,4142,200,5.JPG\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,4142,200,7.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,4142,200,8.JPG\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,4142,200,DSCN4456.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,4142,200,DSCN4464.JPG\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,4142,200,DSCN4467.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,4142,200,DSCN4470.JPG\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,4142,200,DSCN4486.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,4142,200,DSCN4490.JPG\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,4142,200,DSCN4499.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,4142,200,DSCN4536.JPG\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbs