INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3008,'P','2012-02-06',1,NULL,'Rss','Велика родина: одна мама і 216 дітей!','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4144,200,DSC02796.JPG\" />Волонтери та парафіяльна служба милосердя 25 січня відвідали Борзнянську загальноосвітню школу-інтернат в Чернігівській області. Хоч і свята закінчились, але св.. Миколай ще досі роздає свої подарунки. Цього разу ми зібрали трохи менше подарунків, ніж завжди. Але і на цьому велике дякую усім, хто приніс речі, іграшки, хто допоміг матеріально, закупити різдвяні сніговички з цукерками, два комп’ютери та телевізор. Діти були дуже щасливі та вдячні. Обличчя їх світилися неймовірно!</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=829:velyka-rodyna-odna-mama-i-216-dite&catid=29:propovidi-cat&Itemid=34',2662,NULL,1,26),
(3009,'P','2012-02-10',1,NULL,'Note','Запрошуємо паломників на Аскольдову Могилу','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,4146,200,404948.jpg\" alt=\"\" />Громада УГКЦ св. Миколая Чудотворця на Аскольдовій Могилі запрошує паломників на святу Літургію кожного дня о 8:30 та 18:00. (http://askoldova-mohyla.org/uk/item,4073) Аскольдова Могила є найдавнішою памяткоюю і джерелом віри Христової від Хрещення 866 року Божого і мученицького свідчення за віру Христову 882 року Божого Аскольда-Миколая царя і мученика за віру Христову. (http://askoldova-mohyla.org/uk/item,4118)</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,4146,200,404948.jpg\" alt=\"\" />Громада УГКЦ св. Миколая Чудотворця на Аскольдовій Могилі запрошує паломників на святу Літургію кожного дня о 8:30 та 18:00. (http://askoldova-mohyla.org/uk/item,4073) Аскольдова Могила є найдавнішою памяткоюю і джерелом віри Христової від Хрещення 866 року Божого і мученицького свідчення за віру Христову 882 року Божого Аскольда-Миколая царя і мученика за віру Христову. (http://askoldova-mohyla.org/uk/item,4118) <br />У цій церковці 2001 року у часі свого пастирськоговізиту на Україну, сьогодні вже бажений, Папа Іван Павло ІІ стоячи на колінах складав свої молитви до Чудотворної ікони-копії Зарваницької Богородиці (http://askoldova-mohyla.org/uk/item,3967)<br />Запрошуємо молодь, усіх паломників вклонитись і помолитись другій святині над Дніпром - Героям Крут, що є пам&rsquo;ятною могилою всіх невідомих братів і сестер, що полягли за волю України. Могила Героям Крут була відновлена, вшанована козацьким Хрестом, і освячена Блаженнійшим Святославом 29 січня 2012 р. (http://askoldova-mohyla.org/uk/item,4138)<br />Наша церковця відкрита продовж дня. Усі прочани мають можливість тут помолитись, відпочити і підкріпитись гарячим чаєм та кавою, які приготує парафіяльний Карітас.<br />Прошу зголошуватись за телефоном: 050 447 31 57, 067 406 89 33 адміністратор парафії ієрей Ігор.</span></span></p>',NULL,NULL,2663,5,1,17),
(3010,'P','2012-02-12',1,NULL,'Rss','З нагоди Дня особливої пастирської уваги УГКЦ до в’язничного служіння в Неділю про Блудного Сина церква молиться за в’язнів.','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4146,200,51.jpg\" />Бог шукає кожну людину незалежно від обставин, в яких вона опинилася. Божа справедливість засуджує зло, але людину не відкидає. Милосердний Отець завжди чекає на повернення Блудного Сина. Тому в’язниця потребує вісників Його милосердя. Бог воплотився, щоб Світло сягнуло навіть найтемніших закутків людського буття.</p>\n<p>Кожне суспільство доручає законній державній владі реагувати на злочинність за допомогою правоохоронних служб та системи правосуддя. З огляду кримінальні переступи ми, ради відносного комфорту та безпеки, приймаємо необхідність існування покарання ув\'язненням.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=830:z-nagody-dnja-osoblyvoji-pastyrskoji-uvagy-ugkc-do-vjaznychnogo-sluzhinnja-v-nedilju-pro-bludnogo-syna-cerkva-molytsja-za-vjazniv&catid=28:tema-nomera-cat&Itemid=2',2664,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3011,'P','2012-02-15',1,NULL,'Rss','Стрітення Господа Бога і Спаса нашого Ісуса Христа','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4148,200,stritenya.jpg\" />\"Радуйся благодатна, Богородице Діво, бо з Тебе засяяло Сонце правди Христос Бог наш, що просвічує тих, що в темряві\"</p>\n<p>Тропар празника</p>\n<p>Різдвяне коло свят завершується празником Господнього Стрі­тення,  який святкуємо 2 лютого, на сороковий день після Христо­вого Різдва. Цей  празник своїм змістом тісно пов\'язаний з Христо­вим Різдвом, бо як у  Різдві, так і тут Христос при своїм пожерт­вуванні у святині об\'являє  нам Свою божественність. Від зустрічі Божої Дитини і Його Пресвятої  Матері з праведним Симеоном і сам празник у Східній Церкві називається  Стрітення. Візьмемо до уваги три питання: основу празника, його  встановлення та дух богослуження.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=831:stritennja-gospoda-boga-i-spasa-nashogo-isusa-hrysta&catid=27:gk-v-ukrajini-cat&Itemid=57',2667,NULL,1,26),
(3012,'P','2012-02-15',1,NULL,'Rss','Куди літом поїдуть відпочивати діти з Аскольдової Могили?','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4149,200,888.JPG\" />Кожного року настоятель греко-католицької церкви святого Миколая, що на Аскольдовій Могилі, отець Ігор Онишкевич організовує літні&nbsp; відпочинки для дітей парафії у Карпатах та на морі. Також відбуваються зимові подорожі у Карпати, де діти залюбки катаються на лижах. Ці проекти здійснюються з 1995 року. Важко повірити, але вже17 років поспіль отець Ігор Онишкевич з Божою допомогою знаходить жертводавців та організовує ці незабутні поїздки. Зауважмо, для багатьох дітей саме наша парафія відкрила море.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=832:kudy-litom-pojidut-vidpochyvaty-dity-z-askoldovoji-mogyly&catid=25:parafija-cat&Itemid=27',2668,NULL,1,26),
(3013,'P','2012-02-16',1,NULL,'Rss','ПАСТИРСЬКЕ ПОСЛАННЯ Синоду Єпископів Української Греко-Католицької Церкви до вірних з нагоди 120-літнього ювілею Патріарха Йосифа (Сліпого)','<div class=\"news-picture\"><img style=\"margin: 10px; float: right;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size_vertical/news/pictures/rtemagicc_slipyj_b.jpg.jpg\" height=\"182\" width=\"133\" /></div>\n<p><strong>ПАСТИРСЬКЕ ПОСЛАННЯ</strong></p>\n<p class=\"rtecenter\">Синоду Єпископів Української Греко-Католицької Церкви до вірних<br /> з нагоди 120-літнього ювілею Патріарха Йосифа (Сліпого)</p>\n<p>Дорогі у Христі!</p>\n<p>Кожен ювілей наших слуг Божих та ісповідників віри – це не лише добра  нагода соборно згадати їхні імена і всім Божим народом нашої Церкви  помолитися за упокій їхніх душ та просити Бога про їхню прославу на  землі: ми робимо це постійно. Відзначення ювілеїв – це також і духовний  привілей нового спілкування зі світочами нашої церковної історії, що  здійснюється через читання їхнього слова та осмислення їхнього чину. Це  робить нас причасниками їхньої мудрості та вдячними спадкоємцями їхнього  досвіду.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=833:pastyrske-poslannja-synodu-jepyskopiv-ukrajinskoji-greko-katolyckoji-cerkvy-do-virnyh-z-nagody-120-litnogo-juvileju-patriarha-osyfa-slipogo&catid=27:gk-v-ukrajini-cat&Itemid=57',2670,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3014,'P','2012-02-16',0,NULL,'Note','','<p style=\"text-align: center;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Молитва св. Єфрема Сирійського</span></span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Господи і Владико життя мого, духа млявости і недбайливости, владолюбства і пустослів&rsquo;я віджени від мене.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Духа ж доброчесности і смиренномудрія, терпіння й любови даруй мені, недостойному рабові твоєму.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Так, Господи Царю, дай мені зріти мої прогрішення і не осуджувати брата мого. Бо ти благословен єси на віки віків, амінь&raquo;.</span></span></p>','',NULL,NULL,2671,5,1,50),
(3015,'P','2012-02-22',1,NULL,'Note','Паломництво до СТРАДЧУ 24-25 березня 2012 року Божого','<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a title=\"Увеличить\" rel=\"lytebox[vacation]\" href=\"http://pilgrimage.in.ua/img/_news/ps240312.jpg\"><img style=\"margin-left: 5px; margin-right: 5px; border: 0pt none; float: left;\" src=\"http://pilgrimage.in.ua/img/_news/ps240312.jpg\" alt=\"\" height=\"150\" /></a><strong>23.03.2012 (п&rsquo;ятниця)</strong> <br /> Від&rsquo;їзд з залізничного вокзалу Києва до Львова* </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>24.03.2012 (субота)</strong> <br /> 08.00 Приїзд до Львову* <br />09.30 Приїзд до  Страдчу <br />10.00. Св. Літургія (Сповідь, Причастя) у храмі Успіння Пресвятої Богородиці <br />12.30 Відвідини могил бл.муч. Миколи Конрада і Володимира Прийми<br /></span></span></p>','<h2 style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>ПРОГРАМА ПАЛОМНИЦТВА</strong></span></span></h2>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a title=\"Увеличить\" rel=\"lytebox[vacation]\" href=\"http://pilgrimage.in.ua/img/_news/ps240312.jpg\"><img style=\"float: right; margin-left: 5px; margin-right: 5px; border: 0pt none;\" src=\"http://pilgrimage.in.ua/img/_news/ps240312.jpg\" alt=\"\" height=\"150\" /></a><strong>23.03.2012 (п&rsquo;ятниця)</strong> <br /> Від&rsquo;їзд з залізничного вокзалу Києва до Львова* </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>24.03.2012 (субота)</strong> <br /> 08.00 Приїзд до Львову* <br />09.30 Приїзд до  Страдчу <br />10.00. Св. Літургія (Сповідь, Причастя) у храмі Успіння Пресвятої Богородиці <br />12.30 Відвідини могил бл.муч. Миколи Конрада і Володимира Прийми <br />13.30. Обід <br />15.00 Хресна дорога <br />18.00 Виїзд до Львова <br />19.00 Вільний час <br />21.00 Від&rsquo;їзд до Києва *</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong> 25.03.2012 (неділя)</strong> <br />9.30. Прибуття до Києва*</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">*  Час від&rsquo;їзду і приїзду може змінитись <br />** Для студентів &ndash; студенський квиток</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Пожертва:  400 грн </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>До пожертви  включено: </strong><br />-	транспортне обслуговування  (потяг+автобус) <br />-	страхування  <br />-	обід  <br />-	екскурсійне обслуговування  <br />-	духовний та інформаційний супровід</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Необхідно взяти з собою: </strong><br />-	посуд на св.воду <br />-	ліки (по потребі)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Замовити: </strong><br />+38 044 279 19 20 <br />+38 067 174 54 24 <br />+38 095 622 79 48</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"mailto:proshi@pilgrimage.in.ua\">proshi@pilgrimage.in.ua</a></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">http://pilgrimage.in.ua/index.php?option=com_content&amp;amp;view=article&amp;amp;id=731:palom-stradch240312-&amp;amp;catid=41:nppcugcc&amp;amp;Itemid=89</span></span></p>',NULL,NULL,2676,5,1,47);
