INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3017,'P','2012-02-24',1,NULL,'Rss','Початок Великого посту','<p>Уже апостоли й перша християнська громада почали заміняти день    жидівської Пасхи днем поминання Христових мук і смерті. Роковини    Христової смерті були для них дуже сумним днем. Тож, щоб його належно    відсвяткувати, вони зберігали піст у цей день. І так первісна    християнська Пасха почала існувати у вигляді посту. Це була хресна    Пасха. За свідченням святого Іринея (к. 125 — к. 203), про яке говорить    історик Церкви Євсевій (к. 260 — к. 340), перший зародок нинішнього    40-денного посту в той час обмежу­вався одним-двома днями посту, який не    вважався передпасхальним, а таки самою Пасхою.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=835:pochatok-velykogo-postu&catid=27:gk-v-ukrajini-cat&Itemid=57',2679,NULL,1,26),
(3018,'P','2012-02-24',0,NULL,'Note','','<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Молитва покаяння</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong></strong>Владико Христе Боже, що страстями Своїми  пристрастi мої зцiлив i ранами Своїми мої рани вилiкував! Подай менi, що  перед Тобою багато нагрiшив, сльози розчулення; дай змогу моєму тiлу  насолоджуватися пахощами Животворчого Тiла Твого, а душу мою усолоди  Твоєю Чесн&oacute;ю Кров&rsquo;ю вiд тiєї гiркоти, котрою мене напував супротивник;  пiднеси мiй розум до Тебе, бо прилип до землi, i виведи мене з ями  погибелi; бо не маю покаяння, не маю душевного жалю за грiхи, не маю  сльози втiшної, що приводить дiтей Твоїх до їх наслiддя. Затьмарив я  розум життєвими пристрастями i, як хворий, не можу споглянути на Тебе у  хворобi, не можу зiгрiтися сльозами любови до тебе. Але, Владико Господи  Iсусе Христе, Скарбе добра, подай менi всецiле покаяння i серце,  любов&rsquo;ю наповнене, щоб шукало Тебе, подай менi благодать Твою й вiднови в  менi риси Твого образу. Я покинув Тебе, але Ти не покидай мене; вийди  на пошук мене, виведи мене на пасовища Твої та прилучи мене до овець  &oacute;браної Твоєї отари, насити мене з ними поживою Божественних Твоїх Таїн,  молитвами Пречистої Твоєї Матерi i всiх святих Твоїх. Амiнь</span></span></p>','',NULL,NULL,2680,5,1,50),
(3019,'P','2012-02-27',1,NULL,'Note','ПОРАДНЯ У СПРАВАХ МІГРАНТІВ ТА ЇХНІХ РОДИН!','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сслужіння &laquo;<strong>ПОРА</strong><strong>ДНЯ</strong><strong> </strong><strong>У СПРАВАХ МІГРАНТІВ</strong><strong> </strong><strong>ТА ЇХНІХ РОДИН!&raquo;</strong> здійснюється у храмі Св. Миколая Чудотворця на Аскольдовій Могилі. Консультації та підтримку проводить Руслана Дмитровська у приватному режимі за телефоном +380979312124 а також кожної неділі опісля дитячої Святої Літургії о 14:00 годині.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сслужіння &laquo;<strong>ПОРА</strong><strong>ДНЯ</strong><strong> </strong><strong>У СПРАВАХ МІГРАНТІВ</strong><strong> </strong><strong>ТА ЇХНІХ РОДИН!&raquo;</strong> здійснюється у храмі Св. Миколая Чудотворця на Аскольдовій Могилі. Консультації та підтримку проводить Руслана Дмитровська у приватному режимі за телефоном +380979312124 а також кожної неділі опісля дитячої Святої Літургії о 14:00 годині.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Також за консультацією у справах мігрантів та їхніх родин можна звертатися до настоятеля храму о. Ігора Онишкевича +38-050-447-31-57, +38-044-570-32-90, e-mail: <a href=\"mailto:oranta-gazeta@ukr.net\">oranta-gazeta@ukr.net</a></span></span></p>',NULL,NULL,2682,5,1,40);
