INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3819,'P','2013-06-18',1,NULL,'Rss','Рішення класу (19 червня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4834,200,compassion.jpg\" />- Якщо не дозволиш мені це зробити, я не зможу піти до школи! Мені буде соромно. Це дуже важливо, матусю!</p>\n<p>Елена вибухнула плачем. Це її найефективніша зброя.</p>\n<p>- Ох! Роби що хочеш…- пробуркотіла мати, кидаючи ложечку до мийки. – Будеш виглядати, як потвора. Тим гірше для тебе!</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1263:rishennja-klasu-19-chervnja&catid=1:mystecka-storinka-cat&Itemid=50',3352,NULL,1,1,26),
(3820,'P','2013-06-18',1,NULL,'Rss','Будьте відважними та терпеливими свідками','<p><img style=\"margin: 10px; float: left;\" src=\"http://media01.radiovaticana.va/imm/1_0_702605.JPG\" height=\"117\" width=\"177\" />«Я  не соромлюся Євангелія... Як охрищені, ви вже не під законом, а під  благодаттю». Ці слова святого Павла стали поштовхом для Папи Франциска,  щоб під час катехизи, виголошеної експромтом, поділитися з учасниками  церковного Конгресу Римської дієцезії своїми думками про життя Божою  благодаттю та про його зв’язок з ділом євангелізації. Своє слово  Святіший Отець виголосив під час відкриття триденного конгресу, що  відбулося 17 червня 2013 р. ввечері, в залі Павла VI у Ватикані.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1264:budte-vidvazhnymy-ta-terpelyvymy-svidkamy&catid=32:gk-svit-cat&Itemid=58',3353,NULL,1,1,26),
(3821,'P','2013-06-19',1,NULL,'Rss','Місія ЮНЕСКО розкритикувала Україну за ігнорування рекомендації щодо буферних зон «Софії Київської» та «Києво-Печерської лаври»','<a href=\"http://vk.com/share.php?url=http%3A%2F%2Frisu.org.ua%2Fua%2Findex%2Fall_news%2Fculture%2Finheritance%2F52685%2F\"> </a>\n<p><img style=\"margin: 10px; float: left;\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_52685_%DE%CD%C5%D1%CA%CE.jpg\" alt=\"ЮНЕСКО.jpg\" title=\"ЮНЕСКО.jpg\" height=\"126\" width=\"189\" />Спостережна  місія ЮНЕСКО заявила, що Україна систематично не виконує приписи з  охорони буферних зон заповідників \"Софія Київська\" і \"Києво-Печерська  лавра\". Про це йдеться у звіті місії ЮНЕСКО, що відвідала Київ у квітні.</p>\n<p>До кінця року Україні рекомендували прийняти новий закон про  культурну спадщину, в якому описувалися б поняття буферних зон, а також  об\'єднати буферні зони заповідників і ввести в них мораторій на нові  забудови. У Київраді сумніваються, що рекомендації будуть виконані.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1265:2013-06-19-08-34-38&catid=37:spilnota-cat&Itemid=49',3354,NULL,1,1,26),
(3822,'P','2013-06-19',1,NULL,'Rss','У Києві пройшла презентація Всеукраїнської виставки-конкурсу образотворчого мистецтва в\'язнів «Переображення Господньою Любов’ю».','<div><img style=\"margin: 10px; float: right;\" src=\"http://3.bp.blogspot.com/-Ug9mMRjzkHc/UcCJ6cebMWI/AAAAAAAAENg/DT2L1YlgJzQ/s400/3039.jpg\" height=\"127\" width=\"191\" /></div>\n<p><strong>18 червня 2013 року </strong>в  актовій залі Центрального будинку художника відбулася презентація  виставки-конкурсу образотворчого мистецтва в\'язнів «Переображення  Господньою Любов\'ю». В рамках заходу було представлено <a href=\"http://prison-pastoral.blogspot.com/2013/06/blog-post_18.html\" target=\"_blank\">етапи підготовки конкурсу</a> та соціальні аспекти цієї мистецької події.&nbsp;Захід відбувся за сприяння  Національної спілки художників України для широкого інформування про  майбутню виставку-конкурс та про важливість процесу реінтеграції наших  співгромадян, що опинилися у місцях позбавлення волі. Виставка-конкурс  відкриється у Києві у Музеї Івана Гончара у серпні 2013 року. \n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1266:2013-06-19-08-55-58&catid=27:gk-v-ukrajini-cat&Itemid=57',3355,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3823,'P','2013-06-19',1,NULL,'Rss','Любити ворогів – це молитися за них','<p><img style=\"margin: 10px; float: left;\" alt=\"Папа Франциск\" src=\"http://www.credo-ua.org/wp-content/uploads/2013/06/1_0_702680.jpg\" height=\"131\" width=\"198\" />Любити ворогів нелегко, але цього від нас вимагає Ісус. Папа Франциск&nbsp; підкреслив, що для того, аби  могти простити ворогам, фундаментальною справою є молитися за них.</p>\n<p>Святіший Отець поставив питання про те, як можливо любити ворогів, у  загальнолюдському контексті. Як можна любити тих, які приймають рішення  про бомбардування, вбиваючи багатьох людей? Як можна любити тих, «які  задля любові до грошей» перешкоджають доступові людей похилого віку до  ліків, залишаючи їх помирати? Чи тих, які шукаючи виключно особистих  інтересів, чинять багато зла? Але цього від нас вимагає Ісус.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1267:ljubyty-vorogiv-ce-molytysja-za-nyh&catid=32:gk-svit-cat&Itemid=58',3356,NULL,1,1,26),
(3824,'P','2013-06-20',1,NULL,'Rss','Все гаразд (20 червня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4839,200,x_3a3ecbbb.jpg\" />Одного дня у шпиталь до відділу інтенсивної терапії прийняли пацієнта на  ім\'я Петро. Це був високий сильний чоловік який мав рак кістки.</p>\n<p>Хоча  він дуже страждав, однак рідко скаржився. Дружина оточувала його  великою любов\'ю, усіляко старалася аби він мав якнайліпший догляд.  Кілька разів його уже піддавали хіміотерапії і його енергія, його  життєва сила вичерпалася.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1268:vse-garazd-20-chervnja&catid=1:mystecka-storinka-cat&Itemid=50',3357,NULL,1,1,26),
(3825,'P','2013-06-20',1,NULL,'Rss',' Що таке відпуст (індульгенція)?','<p><img style=\"margin: 10px; float: left;\" alt=\"відпуст\" src=\"http://www.credo-ua.org/wp-content/uploads/2013/06/prochenie.jpg\" height=\"156\" width=\"208\" /></p>\n<p><strong>Церква вважає гріх смертельно небезпечним духовним  захворюванням, яке можна порівняти з такими фізичними хворобами, як рак,  СНІД, або з наркотичною залежністю.</strong></p>\n<p>Особливість цього духовного захворювання полягає в тому, що сама людина  свідомо і добровільно вводить його в себе - як і в разі використання  наркотиків. Спочатку людина відчуває задоволення, але потім незрима сила  послідовно руйнує людський організм і, зрештою, - знищує його...</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1269:-shcho-take-vidpust-indulgencija&catid=30:hrystyjanske-vchennia-cat&Itemid=37',3358,NULL,1,1,26),
(3826,'P','2013-06-22',0,NULL,'Note','Молитовні чування у церкві святого Миколая Чудотворця, що Аскольдовій Могилі','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4844,200,zalyshys.jpg\" alt=\"\" width=\"200\" height=\"148\" /></span><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Час: <strong>22 травня з 18:00 до 23:00.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Місце: <strong>Аскольдова Могил</strong>а, вул. Паркова дорога, 1</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Молитва до Святого Духа. Наука.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Молитовні чування.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Вервиця до Святого Духа.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4844,200,zalyshys.jpg\" alt=\"\" width=\"200\" height=\"148\" />Час: <strong>22 травня з 18:00 до 23:00.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Місце: <strong>Аскольдова Могил</strong>а, вул. Паркова дорога, 1</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Програма</strong>:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18:00 - Божественна Літургія.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18:40 - Молебень.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">19:00 - Велика Вечірня.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">19:40 - Перерва на каву і чай.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">20:00 - Молитва до Святого Духа. Наука.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">20:30 - Молитовні чування.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">21:30 - Вервиця до Святого Духа.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">22:00 - Утреня.</span></p>',NULL,3361,5,1,NULL,40);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3827,'P','2013-06-14',0,NULL,'Note','','<div style=\"text-align: center;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: x-small;\"><strong>Молитва на Зіслання Святого Духа</strong></span></div>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: x-small;\">Коли Ти, Христе Спасителю, вознісся на небо, Ти обіцяв Своїм Апостолам й Ученикам зіслати з неба Утішителя Святого Духа, що мав їх навчити всякої правди. </span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: x-small;\">Ти сповнив, Христе, ту обітницю. Ти зіслав з неба Святого Духа на святих Своїх Учеників й Апостолів і ми в величний день Зіслання Святого Духа обходимо ту світлу й велику пам\'ятку цієї великої і святочної хвилини. Вона для нас не чужа й не далека, бо кожний з нас стається також учасником цього небесного Дару. </span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: x-small;\">Ми приймаємо Твого Святого Духа у св. Тайні Миропомазання, ми приймаємо Його в кожному побільшенні освячуючої благодаті. </span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: x-small;\">Тоді Ти зіслав Свого Святого Духа видимим способом, у вигляді вогнених язиків, але невидимим способом зсилаєш Його на нас усіх при кожному доброму ділі, при кожній молитві, а передовсім тоді, коли з освячуючою благодаттю, з любов\'ю дітей звертаємося до Отця Небесного й Дух Твій Святий проливається в наші серця любов\'ю до Бога й любов\'ю до ближніх. </span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: x-small;\">Через те наші тіла й наші душі є храмом Святого Твого Духа. </span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: x-small;\">Твій Дух з нами перебуває й наставляє нас на правильні дороги християнського життя. </span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: x-small;\">Віднови в наших душах той храм Твого Святого Духа. </span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: x-small;\">Віднови в наших душах те перебування Його Божою любов\'ю. </span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: x-small;\">Хочемо бути Твоїми вірними слугами, хочемо сповнити Твої заповіді, хочемо до смерти витривати у святій Вірі й любові до Бога й ближніх. </span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: x-small;\">Як у щоденних молитвах, так і сьогодні про це просимо: &bdquo;Серце чисте створи в мені, Боже, й Дух правий обнови в утробі моїй. Не відкинь мене від лиця Твого й Духа Твого Святого не відійми від мене. Віддай мені радість спасення Свого й Духом владичним утверди мене&rdquo; (Пс. 50) </span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: x-small;\">З тим самим проханням звертаємося й до Святого Духа кожної днини в році, а особливим способом в Його велике і світле свято. </span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: x-small;\">Небесного Царя, Утішителя, Духа Правди взиваємо щодня: прийди і вселися в нас й очисти нас від усякої скверни і спаси, Святий Душе, наші душі, відкуплені Христовою кров\'ю. </span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: x-small;\">Амінь.</span></p>','',NULL,3362,5,1,NULL,50);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3828,'I','2013-06-20',1,NULL,'Rss','Ріка (21 червня)','<div class=\"itemIntroText\">\r\n<p><img style=\"margin: 10px; float: right;\" src=\"image,4844,200,4891899730_d451ae4491_z.jpg\" alt=\"\" />Троє людей опинилися якось на березі річки, яка несла свої спінені бурхливі води. Ці люди мусили перебратися на інший берег. Для них це було дуже важливо.</p>\r\n</div>\r\n<p>Перший чоловік, хитрий купець і великий комбінатор, який умів керувати справами і людьми, став навколішки і звернувся до Бога: &laquo;Господи, зроби так, щоб я відважився кинутися у ці небезпечні води і переправився через ріку. На тому боці мене чекають важливі справи. Я подвою свої прибутки, проте мушу поспішити...&raquo; Підвівся і, по хвилі вагання, кинувся до води. Але ріка забрала його зі собою.</p>','','http://oranta.org/index.php?option=com_content&view=article&id=1270:rika-21-chnrvnja&catid=25:parafija-cat&Itemid=27',3363,NULL,1,1,26),
(3829,'P','2013-06-21',1,NULL,'Note','ЗВІТ №12 про «Пасхальний кошик» дітям, матері яких відбувають покарання в місцях позбавлення волі (16.05.2013 р.)','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4847,200,DSCN4901.JPG\" alt=\"\" width=\"200\" height=\"150\" />&ldquo;... Помоліться за мою донечку Анастасію і мою матір р.Божью Марію... <strong>допоможіть моїй дитині весняними одіждами, взуттям. В Насті немає курточки, туфельок... Може є можливість допомогти карандашами, зошитами, підручниками для початкових занять.</strong> Буду дуже вдячна всім прихожанам церкви за любу допомогу для моєї сім&rsquo;ї. Буду молитися за Вас&rdquo;</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4847,200,DSCN4901.JPG\" alt=\"\" width=\"200\" height=\"150\" />Завершили наповнення &laquo;Пасхального кошика&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Всього відправлено 5 посилок з &laquo;Пасхальним кошиком&raquo;, з них 2 посилки в Будинки матері і дитини при Чернігівській та Чорноморській (м.Одеса) виправних колоніях, 2 посилки в родини засуджених матерів, &nbsp;а також одна посилка дітям (дівчаткам-двійнятам) в родину недавно звільненої з ув&rsquo;язнення матері, яка щиро намірена провадити своє подальше життя з Господом (в чому наглядно переконалися згодом, відвідавши родину &ndash; <em>див.новини сайту</em>).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Посилки доставлені до поштового відділення настоятелем церкви св.Миколая на Аскольдовій могилі о.Ігорем Онишкевичем.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Посилки отримали: <br /></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>1.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Будинок матері і дитини &nbsp;при Чернігівській виправній колонії ЧВК-44, м.Чернігів</strong></span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>2.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Будинок матері і дитини при Чорноморській виправній колонії ЧВК-74, м.Одеса</strong></span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>3.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Діти (Анастасія</strong><strong>&ndash;12р., Сніжана-7р.) ув\'язненої Світлани П. (Чернігівська ВК)</strong> <strong>у Вінницьку обл.</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;&hellip;<strong>В мене є 2 донечки (Анастасія-2001 р.н., Сніжана-2006 р.н.), вони на даний час проживають з моєю сестрою...</strong> <strong>допоможіть будь-ласка моїм дітям чим можите</strong>. Буду щиро вдячна Вам&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Дякую за Вашу турботу</strong>... <strong>Відсилаю Вам перелік за ту посилку, що мої діти отримали на Святого Миколая. Дякую Вам за все!&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 18.01.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали дітям Світлани П. у Вінницьку обл.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">23.02.2012 р. на суму 25 грн.10 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">14.06.2012 р. на суму 70 грн.24 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">03.09.2012 р. на суму 131 грн.77 коп. &ndash; &laquo;Шкільний портфелик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">15.12.2012 р. &ndash; &laquo;Подарунок від св.Миколая&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 227 грн.11 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,4847,200,DSCN4910.jpg\" alt=\"\" width=\"150\" height=\"200\" />4.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Діти (Марія-6р., Дарія-6р.) Наталії Н., звільненої з Чернігівської ВК) в Чернігівську обл.</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Благодаря Вашим и маминым молитвам Господь помиловал меня</strong>... У нас в Чернигове я знаю есть Греко-католицкая церковь я хочу её найти и посещать её... <strong>моя душа и жизнь дальнейшая в руках Господа и ради детей есть смысл жизни</strong>. Поэтому<strong> я не хочу оступатся, я выбор сделала быть с Господом</strong>... я очень рада что Вы всё же поверили мне и поддерживали меня и моих родных... по Божьей милости я вырвалась из этого ада. <strong>Пишите мне, я очень нуждаюсь в духовном наставлении.</strong> Ведь если я уже дома, это не говорит о том что всё нормально&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 18.01.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали дітям Наталії Н. в Чернігівську обл.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">20.04.2012 р. на суму 105 грн.49 коп. &laquo;Пасхальний кошик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">07.09.2012 р. на суму 126 грн.58 коп. &laquo;Шкільний портфелик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">15.12.2012 р. &ndash; &laquo;Подарунок від св.Миколая&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 232 грн.07 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,4847,200,DSCN4911.jpg\" alt=\"\" width=\"200\" height=\"160\" />5.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Дитина (Анастасія &ndash; 4,5р.) ув\'язненої Вікторії В. (Чернігівська ВК) в м.Чернігів</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Помоліться за мою донечку Анастасію і мою матір р.Божью Марію... <strong>допоможіть моїй дитині весняними одіждами, взуттям. В Насті немає курточки, туфельок... Може є можливість допомогти карандашами, зошитами, підручниками для початкових занять.</strong> Буду дуже вдячна всім прихожанам церкви за любу допомогу для моєї сім&rsquo;ї. Буду молитися за Вас&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 07.09.2011 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали дитині Вікторії В. в м.Чернігів:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">12.12.2011 р. &ndash; &laquo;Подарунок від св.Миколая&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">26.04.2012 р. на суму 74 грн.95 коп. &ndash; &laquo;Пасхальний кошик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">28.12.2012 р. &ndash; &laquo;Подарунок від св.Миколая&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги - 74 грн.95 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Додаткові витрати від&nbsp; 16.05.2013 р. &mdash; 11 грн. 10 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">вартість поштових конвертів, вкладених у посилки &mdash; <strong>12 грн.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">пересилання &mdash;<strong> 125 грн.90 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума витрат від&nbsp; </strong><strong>16.05.2013</strong><strong> р. &mdash; </strong><strong>149</strong><strong> грн. </strong><strong>00</strong><strong> коп. </strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна вага поштових відправлень від </strong><strong>16.05</strong><strong>.2013 р. &ndash;29,8 кг.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума витрат на проект &laquo;Пасхальний кошик&raquo; - 1 586 грн.43 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>в т.ч. пожертви &ndash; 700.00 грн.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна вага посилок &ndash; 161,32 кг.</strong></span></p>',NULL,3364,5,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3830,'P','2013-06-21',0,NULL,'Note','ЗВІТ №13 про розподілення гуманітарної допомоги від БФ «Тріумф серця», а також благодійних пожертвувань парафіян церкви св. Миколая на Аскольдовій могилі (17.05.2013 р.)','<ul>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">одяг чоловічий, жіночий, дитячий &ndash; 14 коробів+3 мішки</span></li>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">взуття &ndash; 1 мішок</span></li>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">іграшки дитячі &ndash; 1 мішок</span></li>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">ігри дитячі &ndash; 2 коробки</span></li>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">книжечки дитячі</span></li>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">прокладки антисептичні для хворих &ndash; 1 упаковка</span></li>\r\n</ul>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Служба милосердя при церкві св.Миколая на Аскольдовій могилі, зібравши пожертвувані парафіянами: одяг чоловічий, жіночий та дитячий, взуття, ігри, іграшки дитячі та інші речі особистого вжитку, а також гуманітарну допомогу від благодійного фонду &laquo;Тріумф серця&raquo;, розподілила їх таким чином:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&mdash; уділила потребуючим мирянам;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&mdash; відправила гуманітарний вантаж Благодійному фонду &ldquo;Сузір\'я&rdquo; в с.Іванкові Бориспільського р-ну Київської обл.:</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">одяг чоловічий, жіночий, дитячий &ndash; 14 коробів+3 мішки</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">взуття &ndash; 1 мішок</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">іграшки дитячі &ndash; 1 мішок</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">ігри дитячі &ndash; 2 коробки</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">книжечки дитячі</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">прокладки антисептичні для хворих &ndash; 1 упаковка</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Робота виконана &nbsp;силами волонтерів: п.Мирослави, п.Віри, п.Світлани, п.Анни.</span></p>',NULL,3365,5,1,NULL,19),
(3831,'P','2013-06-20',1,NULL,'Rss','Ріка (21 червня)','<div class=\"itemIntroText\">\n<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4844,200,4891899730_d451ae4491_z.jpg\" />Троє людей опинилися якось на березі річки, яка несла свої  спінені бурхливі води. Ці люди мусили перебратися на інший берег. Для  них це було дуже важливо.</p>\n</div>\n<p>Перший чоловік, хитрий купець і великий комбінатор, який умів  керувати справами і людьми, став навколішки і звернувся до Бога:  «Господи, зроби так, щоб я відважився кинутися у ці небезпечні води і  переправився через ріку. На тому боці мене чекають важливі справи. Я  подвою свої прибутки, проте мушу поспішити...» Підвівся і, по хвилі  вагання, кинувся до води. Але ріка забрала його зі собою.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1270:rika-21-chnrvnja&catid=1:mystecka-storinka-cat&Itemid=50',3366,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3832,'P','2013-06-21',1,NULL,'Rss','«Головне моє послання можна було б висловити словами воскреслого Ісуса Христа: “Мир вам!”»','<p class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/73e33c9db3.jpg\" height=\"161\" width=\"111\" /></p>\n<p>З 26 по 30 червня Глава Української Греко-Католицької Церкви  Блаженніший Святослав (Шевчук) перебуватиме з офіційним візитом у  Республіці Польща (РП).</p>\n<p>Зокрема, 27 червня у Варшаві Предстоятель УГКЦ візьме участь у  Панахиді з нагоди 70-х роковин Волинської трагедії, на якій також будуть  архиєпископ Юзеф Ковальчик, Примас Польщі, кардинал Казімєж Нич,  Митрополит Варшавський, і Президент Польщі Броніслав Коморовський. Після  молитви Глава УГКЦ матиме особисту зустріч із Президентом Польщі.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1280:2013-06-21-11-54-35&catid=27:gk-v-ukrajini-cat&Itemid=57',3367,NULL,1,1,26),
(3833,'P','2013-06-21',1,NULL,'Rss','Має прибути Бог! (22 червня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4848,200,18.01-500.jpg\" />Якось один чоловік довідався, що його збирається відвідати Бог.</p>\n<p>“Мене? – занепокоївся чоловік. – У моєму домі?”</p>\n<p>Він став гарячково бігати по всіх кімнатах, піднявся по сходах,  вийшов аж на дах. Знову спустився і швидко увійшов до пивниці. Тепер,  коли до нього мав прийти Бог, він подивився на свій будинок иншими  очима.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1271:maje-prybuty-bog-22-chervnja&catid=1:mystecka-storinka-cat&Itemid=50',3368,NULL,1,1,26),
(3834,'P','2013-06-22',1,NULL,'Rss','День Святої Трійці. П’ятидесятниця','<p class=\"news-picture\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4851,200,000bw3x7.jpg\" /><strong>Перемогти страх, відмовитися від власних схем і переконань, щоб  відкритися до горизонтів Бога. І сказати «ні» відособленості,  винятковості, паралельним шляхам, які несуть поділ, – сказав Папа  Франциск у проповіді під час Літургії на Урочистість П’ятидесятниці. Після читання Євангелія Папа сказав:&nbsp;</strong></p>\n<p>Дорогі брати і сестри, в цей день ми споглядаємо і знову переживаємо у літургії вилив Святого  Духа, посланого Воскреслим Христом на Свою Церкву; подія благодаті, що  наповнила єрусалимську світлицю, щоб поширитися по всьому світу.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1213:den-svjatoji-trici-pjatydesjatnycja&catid=32:gk-svit-cat&Itemid=58',3369,NULL,1,1,26),
(3835,'P','2013-06-22',1,NULL,'Rss','Велика країна малих людей','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4851,200,ArticleImages_43849_01.jpg\" />Україна – це велика країна! Таке гасло в останні роки часто звучить у наших ЗМІ.</p>\n<p>Україна багата своєю природою: безкраїми полями, горами, морями й   озерами, дорогоцінним чорноземом, природними мінералами, - старовинною   самобутньою історією, прекрасними архітектурними пам’ятками, визначними   науковцями та творцями в різних ділянках культури, суспільними діячами,   державними мужами і полководцями.</p>\n<p> </p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1281:velyka-krajina-malyh-ljude&catid=37:spilnota-cat&Itemid=49',3370,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3836,'P','2013-06-22',1,NULL,'Rss','12 років тому - ПАСТОРСЬКИЙ ВІЗИТ ПАПИ ІВАНА ПАВЛА ІІ НА АСКОЛЬДОВУ МОГИЛУ','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4851,200,pope1.jpg\" />У 2000 році в часі святкування Світового дня молоді у Римі мені пощастило побувати на аудієнції в Папи Римського Івана Павла ІІ у літній папській резиденції в Кастель-Гандольфо. Це була зустріч із польською мо­лоддю, із месою в латинському обряді, з привітальним словом Папи і короткою можливістю зробити фото на пам’ять. Коли черга дійшла до нашої маленької груп­ки, Папа відразу розпізнав нашу мову і у відповідь теж звернувся українською, — це було надзвичайно.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1282:12-rokiv-tomu-pastorsky-vizyt-papy-ivana-pavla-ii-na-askoldovu-mogylu&catid=25:parafija-cat&Itemid=27',3371,NULL,1,1,26),
(3837,'P','2013-06-22',1,NULL,'Rss','«ВКЛОНЯЮСЯ ЧИСТІЙ ХРИСТОВІЙ ВІРІ ЦІЄЇ ЗЕМЛІ» — Папа Іван Павло ІІ','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4851,200,pope4.jpg\" />У 2001 р. відбувся апостольський візит Папи Римсько­го Івана Павла ІІ на багатостраждальну українську землю. Зустрічі зі Святішим Отцем на Аскольдовій могилі передувало кілька подій із життя нашої пара­фії, про які варто згадати.</p>\n<p>Усе почалося 1997 р. з нашої прочанської подорожі до Ченстохови на зустріч із Папою Іваном Павлом ІІ. Це була не просто проща, а справжня хресна дорога. Безліч разів ламався в дорозі автобус, на кордоні нас зустріли довжелезні черги, ретельна перевірка доку­ментів, потім було тривале блукання польськими ав­тобанами…</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1283:2013-06-22-20-02-51&catid=25:parafija-cat&Itemid=27',3372,NULL,1,1,26),
(3838,'P','2013-06-22',1,NULL,'Rss','Празник Святої П’ятдесятниці','<p align=\"right\"><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4851,200,Pentecost.png\" />\"Ми виділи світло істинне, ми прийняли Духа небесного, ми знайшли віру істинну, нероздільній Тройці поклоняємося, Вона бо спасла нас\" (Стиховна вечірні П\'ятдесятниці).</em></p>\n<p>Світлий празник Пасхи завершується не менш світлим і вели­ким празником Святої П\'ятдесятниці. Після Христового Різдва і Воскресення цей празник належить до найбільших празників на­шого церковного року. Зіслання Святого Духа — це наче вінець і печать на справі спасення людського роду, що здійснив Божий Син.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1284:praznyk-svjatoji-pjatdesjatnyci&catid=27:gk-v-ukrajini-cat&Itemid=57',3373,NULL,1,1,26),
(3839,'P','2013-06-22',1,NULL,'Rss','Закон (23 червня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4848,200,6775.jpg\" />На парохію постукав брудний і бідно вдягнений хлопець. Був це  постачальник наркотиків, якого розшукувала поліція і банда жорстоких  конкурентів. Він не знав, де сховатися.</p>\n<p>Столітній священик, який  відчинив двері, був заклопотаний цим візитом.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1272:zakon-23-chervnja&catid=1:mystecka-storinka-cat&Itemid=50',3374,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3840,'P','2013-06-23',1,NULL,'Rss','Де мій поцілунок? (24 червня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4848,200,208475_362778407171811_2057632751_n.jpg\" />Жила колись одна дівчинка на ім’я Цецилія її батьки дуже багато  працювали. Це була гарна, щаслива сім’я. Лише одного бракувало, але це  Цецилія завважила щойно у певний момент. Одного разу, коли Цецилія мала 9  років, вона вперше ночувала в домі своєї подруги Аделі. Коли прийшла  пора на сон, мама Аделі відгорнула ковдру і кожну з них поцілувала  надобраніч.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1273:de-mi-pocilunok-24-chervnja&catid=1:mystecka-storinka-cat&Itemid=50',3375,NULL,1,1,26),
(3841,'P','2013-06-24',0,NULL,'Note','Сто днів понтифікату Папи Франциска','<p>&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Українські священики діляться враженнями про колишнього аргентинського кардинала, який брав участь у відправах священика з України.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><a href=\"http://ugcc.tv/ua/media/55154.html\">http://ugcc.tv/ua/media/55154.html</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p>&nbsp;</p>','<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Українські священики діляться враженнями про колишнього аргентинського кардинала, який брав участь у відправах священика з України.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Папа Римський Франциск перетнув символічну часову межу &ndash; 100 днів понтифікату. Аргентинський кардинал Бергольйо став першим неєвропейським понтифіком за останні 1300 років. Разом з іменем святого Франциска Папа перейняв і його аскетичний спосіб життя. Відмовився від запропонованих апартаментів, замінив золотий хрест на залізний, часом самостійно готує собі їжу та їздить у відкритому авто. На Страсному тижні Папа відвідав неповнолітніх ув&rsquo;язнених, яким обмив і поцілував ноги.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Журналісти ТВі поспілкувалися з представниками духовенства в Україні про нового Папу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><a href=\"http://ugcc.tv/ua/media/55154.html\">http://ugcc.tv/ua/media/55154.html</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p>&nbsp;</p>',NULL,3376,5,1,NULL,48),
(3842,'P','2013-06-24',1,NULL,'Rss',' Блаженніший Святослав: «Просимо сьогодні Духа Святого, щоб він усунув з-посеред нас кам’яні серця і дав здатність розуміти ближнього» ','<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/5dcc384c8e.jpg\" height=\"102\" width=\"150\" /></div>\n<p>«Сьогодні відчуваємо себе людьми Божими, Божим народом. Бачимо,  що християнство – це не якась \"одна з релігій світу\", де людина шукає  Бога. Християнство – життя у Святому Дусі. Євангеліє Христове не є ще  однією теорією чи моральною доктриною, яку нас запрошують вивчити й  пізнати. Євангеліє – проголошення істини, що я і ви можемо одержати нове  серце. Дух Святий може зійти на кожного, хто Його прикликає і входить у  членство Церкви Христової».</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1285:2013-06-24-15-27-49&catid=27:gk-v-ukrajini-cat&Itemid=57',3377,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3843,'P','2013-06-24',1,NULL,'Rss','100 днів понтифікату, знакові заяви та українсько-польська католицька солідарність','<p><img style=\"margin: 10px; float: right;\" alt=\"pope-852-rtx10t1p\" src=\"http://popefrancis.org.ua/wp-content/uploads/2013/06/pope-852-rtx10t1p-300x168.jpg\" height=\"133\" width=\"236\" />Зазвичай,  коли якась особа вступає на нову посаду, перші 100 днів її праці є  перехідними. В цей час визначається її особистість і стиль праці. У  випадку Папи Франциска, світові не довелось чекати довго, щоб пізнати  його стиль. Всього за кілька місяців його відкритість оживила Церкву.  Агенція <a href=\"http://www.romereports.com/\" target=\"_blank\">Rome reports</a> визначила ключові точки, які, на їх думку, є визначальними в перші дні понтифікату Святішого Отця Франциска.\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1287:100-dniv-pontyfikatu-znakovi-zajavy-ta-ukrajinsko-polska-katolycka-solidarnist&catid=32:gk-svit-cat&Itemid=58',3378,NULL,1,1,26),
(3844,'P','2013-06-24',1,NULL,'Rss','22 червня напередодні свята Зіслання Святого Духа відбулись молитовні чування','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4807,200,IMG_8057.JPG\" />Сучасний темп життя виснажує фізичні та духовні сили людини, ми потребуємо постійного відновлення, отримання Дарів Святого Духа. Церква встановлює свята, щоб пригадати християнам події описані в Євангелії, для того щоб їх глибоко пережити, як це зробили апостоли у свій час, необхідне приготування. Молитовні чування, що відбулись в суботу мали мету допомогти духовно пережити радісну подію Зіслання Святого Духа, що святкувалась наступного дня.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=1288:22-chervnja-naperedodni-svjata-zislannja-svjatogo-duha-vidbulys-molytovni-chuvannja&catid=25:parafija-cat&Itemid=27',3379,NULL,1,1,26),
(3845,'P','2013-06-24',1,NULL,'Rss','Зелені свята для Папи на Аскольдовій','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4862,200,IMG_8178.JPG\" />На 23 червня цього року для нашої парафії припало два свята водночас: зелені свята – зіслання Святого Духа, що відзначається на п’ятидесятий день після Пасхи і 12-та річниця пастирського візиту Папи Івана Павла ІІ в Україну. Саме цього дня, 12 років тому, нашу парафію відвідав, тепер уже Блаженний, Папа Іван Павло ІІ. Саме наша церква св. Миколая Чудотворця на Аскольдовій Могилі стала першою християнською святинею, куди ступила нога Понтифіка, першою в Україні де склав свої молитви Іван Павло ІІ і віддав свій візит під покров Пресвятої Богородиці перед чудотворною іконою Зарваницької Божої Матері.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=1289:zeleni-svjata-dlja-papy-na-askoldovi&catid=25:parafija-cat&Itemid=27',3380,NULL,1,1,26),
(3846,'P','2013-06-24',1,NULL,'Rss',' Папа скасовує титули і привілеї','<p><img style=\"margin: 10px; float: left;\" alt=\"Папа\" src=\"http://www.credo-ua.org/wp-content/uploads/2013/06/RNS-482x322.jpg\" height=\"124\" width=\"186\" /></p>\n<p>Папа Франциск вирішив скасувати традицію надання почесного  титулу Джентельмена Його Святості за заслуги перед Ватиканом. Цей звичай  Папа вважає архаїчним, непотрібним і шкідливим, – написала в суботу  газета «Corriere della Sera».</p>\n<p>Відмова від титулів і пов\'язаного з ними церемоніалу та парадних фраків  – це наступний крок Папи Франциска, який прагне простоти, скромності та  не хоче, щоби Ватикан і його найближче оточення трактували як двір.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1290:-papa-skasovuje-tytuly-i-pryvileji&catid=32:gk-svit-cat&Itemid=58',3381,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3847,'P','2013-06-24',1,NULL,'Rss','Клуб дев’яноста дев’яти (25 червня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4848,200,7495_10151910057887355_173454080_n.jpg\" />Жив колись дуже сумний король, який мав слугу – чоловіка напрочуд щасливого. У нього зажди був щасливий усміх на устах.</p>\n<p>- Слуго, - спитав якось король, - у чому таємниця твоєї радості?</p>\n<p>- Нема в мене жодної таємниці. Просто не маю причини бути сумним. Я щасливий, тому що можу тобі служити. Мешкаю з дружиною у будинку, який мені призначили. Маю їжу і одяг, а час від часу і кілька монет на пиво.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1274:klub-devjanosta-devjaty-25-chervnja&catid=1:mystecka-storinka-cat&Itemid=50',3382,NULL,1,1,26),
(3848,'P','2013-06-25',1,NULL,'Rss','Пустельники (26 червня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4848,200,NB3DzVyUdQM.jpg\" />Поблизу джерела мешкало багато пустельників. Кожен із них збудував собі свою хатину і провадив усі дні у глибокій тиші, у роздумах і молитвах. Кожен у зосереджені взивав до Божої присутності.</p>\n<p>Бог хотів їх навідати, проте Йому не вдалося знайти дороги. Посеред пустелі бачив лише окремі точки, на відстані одна від одної.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1275:pustelnyky-26-chervnja&catid=1:mystecka-storinka-cat&Itemid=50',3383,NULL,1,1,26),
(3849,'P','2013-06-25',1,NULL,'Rss','Бути християнином – це не випадковість, а поклик любові','<p><img style=\"margin: 10px; float: left;\" src=\"http://media01.radiovaticana.va/imm/1_0_704616.JPG\" height=\"129\" width=\"194\" /></p>\n<p>Бути  християнами – це поклик любові, поклик стати Божими дітьми, наголосив Святіший Отець. «Коли я  читаю цей уривок, – сказав Єпископ Риму, – думаю про Близький Схід та  дуже прошу у Господа, щоб дав усім нам мудрість, мудрість не сваритись  за мир – я звідси, а ти – звідти. Авраам, пояснив далі Папа, далі йшов  вперед, він залишив свою землю, щоб іти, не знаючи куди, але туди, де  йому вкаже Господь. Отже, далі йшов вперед, бо вірив слову Господа Бога,  Який «запросив його вийти із своєї землі». І цей чоловік, якому було,  можливо, 90 років, дивиться на землю, яку йому вказує Господь, і вірить.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1296:buty-hrystyjanynom--ce-ne-vypadkovist-a-poklyk-ljubovi&catid=32:gk-svit-cat&Itemid=58',3384,NULL,1,1,26),
(3850,'P','2013-06-25',1,NULL,'Rss','Бути християнином – це не випадковість, а поклик любові','<p><img style=\"margin: 10px; float: left;\" src=\"http://media01.radiovaticana.va/imm/1_0_704616.JPG\" height=\"129\" width=\"194\" /></p>\n<p>Бути  християнами – це поклик любові, поклик стати Божими дітьми, наголосив Святіший Отець. «Коли я  читаю цей уривок, – сказав Єпископ Риму, – думаю про Близький Схід та  дуже прошу у Господа, щоб дав усім нам мудрість, мудрість не сваритись  за мир – я звідси, а ти – звідти. Авраам, пояснив далі Папа, далі йшов  вперед, він залишив свою землю, щоб іти, не знаючи куди, але туди, де  йому вкаже Господь. Отже, далі йшов вперед, бо вірив слову Господа Бога,  Який «запросив його вийти із своєї землі». І цей чоловік, якому було,  можливо, 90 років, дивиться на землю, яку йому вказує Господь, і вірить.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1296:buty-hrystyjanynom-ce-ne-vypadkovist-a-poklyk-ljubovi&catid=32:gk-svit-cat&Itemid=58',3385,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3851,'P','2013-06-26',1,NULL,'Note','Блаженний Володимир Прийма офіційно став покровителем мирян УГКЦ','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4869,200,00032.mts_.staticheskie001.jpg\" alt=\"\" width=\"200\" height=\"112\" />&laquo;Беручи до уваги норми кодексу Канонів Східних Церков, цим декретом, згідно із постановою Синоду єпископів УГКЦ, у рік, коли наша Церква звертає увагу на покликання мирян, торжественно проголошуємо блаженного мученика Володимира Прийму покровителем мирян&raquo;, &mdash; йдеться у документі першоєрарха.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>','<p style=\"text-align: justify;\"><img style=\"float: right;\" src=\"image,4869,200,00032.mts_.staticheskie001.jpg\" alt=\"\" width=\"200\" height=\"112\" /><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Блаженного Володимира Прийму 26 червня офіційно проголошено покровителем мирян-греко-католиків. Відповідну заяву від імені Патріарха УГКЦ Святослава (Шевчука) оприлюднив секретар Синоду єпископів УГКЦ єпископ Богдан (Дзюрах) під час всеукраїнської прощі до відпустового центру у с. Страдч, що на Львівщині.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">&laquo;Беручи до уваги норми кодексу Канонів Східних Церков, цим декретом, згідно із постановою Синоду єпископів УГКЦ, у рік, коли наша Церква звертає увагу на покликання мирян, торжественно проголошуємо блаженного мученика Володимира Прийму покровителем мирян&raquo;, &mdash; йдеться у документі першоєрарха.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Як вже інформувала <a href=\"http://risu.org.ua/ua/index/all_news/culture/religious_ceremonies/52694/\" target=\"_blank\">РІСУ</a>, цьогорічне паломництво до Страдча присвячено пам&rsquo;яті <a href=\"http://risu.org.ua/ua/index/exclusive/events_people/37438/\" target=\"_blank\">блаженних о. Миколи Конрада та дяка Володимира Прийми</a> і вперше пройшло на всеукраїнському рівні. Участь у ньому взяло близько 30 тисяч вірян із трьох західноукраїнських областей, греко-католицькі монахи, священики, єпископи, митрополити та Апостольський нунцій в Україні Архиєпископ Томас Галіксон, який передав усім присутнім вітання від Папи Франциска. Очолив всеукраїнську прощу Патріарх УГКЦ Святослав (Шевчук).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Звертаючись до священнослужителів та мирян, Глава УГКЦ Святослав, наголосив, що подвиг Володимира Прийми є дуже повчальним. За його словами, блаженний Володимир реально сприйняв євангельські слова і не побоявся бути переслідуваним за правду.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">&laquo;Коли всі тремтіли від страху перед новими вбивцями, які прийшли на зміну іншим, він (Володимир Прийма, - ред.) побачив священика, який з Таїнствами йшов до хворої жінки. Він побачив блаженства Євангелії в дії і пішов услід за парохом, не лякаючись, смерті&raquo;, &mdash; зазначив Патріарх УГКЦ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><a href=\"http://risu.org.ua/ua/index/exclusive/events_people/37438/\" target=\"_blank\"><img style=\"float: left;\" src=\"image,4869,200,ArticleImages_52766_130626_01stradch.jpg\" alt=\"\" width=\"200\" height=\"133\" />Довідка:</a> Володимир Прийма народився 17 липня 1906 року в с. Страдч (нині Яворівський район Львівської області). Після закінчення дяківської бурси при Митрополитові Андреєві Шептицькому служив дяком і дириґентом у сільській церкві. 26 червня 1941 року Володимир разом із місцевим парохом о. Миколою Конрадом поверталися додому, після сповіді хворої жінки. Їх зупинили солдати НКВС і жорстоко закатували у лісі. Понівечені тіла Христових мучеників поховали на парафіяльному цвинтарі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">У 2001 році Папа Іван Павло ІІ під час свого візиту в Україну о. Миколу Конрада і дяка Володимира Прийму проголосив блаженними мучениками Христової Церкви.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Синод єпископів УГКЦ, який відбувався у Вінніпезі (Канада) у вересні 2012 року, проголосив блаженного Володимира Прийму покровителем мирян. У червні 2013 року цього статусу блаженному надали офіційно в урочистій обстановці.</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong class=\"post-title\">Всеукраїнська проща до Страдчу. Проповідь Глави УГКЦ. Зустріч з журналістами. (відео)</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Проповідь Блаженнішого Святослава Шевчука:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong class=\"post-title\"><iframe src=\"http://www.youtube.com/embed/tyyXxBi5oZg\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Зустріч з журналістами:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong class=\"post-title\"><iframe src=\"http://www.youtube.com/embed/1Yg7z3Y-rrQ#at=75\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe></strong><strong class=\"post-title\"><br /></strong></span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>',NULL,3386,5,1,NULL,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3852,'P','2013-06-26',1,NULL,'Rss','Про що нам нагадує слово «храм»?','<p><img style=\"margin: 10px; float: left;\" alt=\"1_0_704903\" src=\"http://popefrancis.org.ua/wp-content/uploads/2013/06/1_0_704903.jpg\" height=\"131\" width=\"197\" />Дорогі брати і сестри,</p>\n<p>Сьогодні я хочу коротко звернутись до ще однієї картини, яка  допомагає нам проілюструвати містерію Церкви: &nbsp;храм (пор. Lumen Gentium,  6).</p>\n<p>Про що нам нагадує слово «храм»? Воно нам нагадує про будівлю,  споруду. В основному, воно багатьом нагадує історію Ізраїльського  Народу, про яку розповідається в Старому Завіті. В Єрусалимі величний  Храм Соломона був місцем зустрічі з Богом у молитві.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1297:2013-06-26-17-20-17&catid=32:gk-svit-cat&Itemid=58',3387,NULL,1,1,26),
(3853,'P','2013-06-26',1,NULL,'Rss','«Уніатизм» і православно-католицький діалог','<strong> </strong>\n<p align=\"left\"><img style=\"margin: 10px; float: right;\" src=\"http://theology.in.ua/php_uploads/images/articles/ArticleImages_52765_Losten.jpg\" alt=\"Losten.jpg\" title=\"Losten.jpg\" height=\"174\" width=\"116\" />Розбіжності  з питання «уніатизму» віддавна є дражливим моментом у  православно-католицькому діалозі. Доки Східні Католицькі Церкви у  країнах колишнього соціалістичного табору не вийшли з підпілля (а  ста­лося це 1988 р. і найближчого по ньому часу), видавалося, що ці  розбіж­ності можна делікатно оминати: існувало мовчазне припущення, що  Східні Католицькі Церкви в діаспорі поступово зникнуть, а на Близькому  Сході пропадуть під мусульманським та сіоністським тиском. У  Московського і Бухарестського патріархатів побутувало уявлення, що Рим  якось погодився із злученням Української Католицької Церкви з  Московським патріархатом, а Румунської Католицької Церкви - з  Бухарестським.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1298:2013-06-26-18-41-12&catid=28:tema-nomera-cat&Itemid=2',3388,NULL,1,1,26),
(3854,'P','2013-06-30',1,NULL,'Rss','Золоті роки (30 червня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4847,200,78890.jpg\" />Вони побрались п’ятдесят років тому. Одного дня сиділи на залізничній станції в очікуванні потяга. На лавці сиділо двійко закоханих.</p>\n<p>Старенькі мовчки спостерігали за молодою парою. Хлопець ніжно обіймав дівчину і пристрасно цілував її.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1279:zoloti-roky-30-chervnja&catid=1:mystecka-storinka-cat&Itemid=50',3400,NULL,1,1,26),
(3855,'P','2013-06-26',1,NULL,'Rss','«Страдч - гора блаженств нашого українського народу…»','<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/2b62079f52.jpg\" height=\"101\" width=\"150\" /></div>\n<p>Близько 20 тисяч вірних взяло участь у першій всеукраїнській  прощі до Страдчу, що на Львівщині, присвяченій пам’яті страдецьких  блаженних мучеників – о. Миколая Кондрата і дяка Володимира Прийми, Дню  мирянина та Року віри.</p>\n<p>На початку паломництва студенти Львівської духовної семінарії Святого  Духа та Чортківської дяківсько-катехитичної академії імені  священномученика Григорія Хомишина співали молебень та співом  супроводжували пішу ходу до Страдецької гори.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1299:2013-06-26-19-12-48&catid=27:gk-v-ukrajini-cat&Itemid=57',3389,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3856,'P','2013-06-26',1,NULL,'Rss','Все (27 червня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4274,200,1005041_527565343976666_673119966_n.jpg\" />Того самого дня, кілька місяців після народження, малий кульгавий Леонардо (якого звали Лео) і Томазо опинилися у дитячому будинку. Працівниці ставились до них дуже добре, а діти з початкової школи, до якої ходили – не надто. Діти часто виявляли жорстокість у стосунку до несміливого Лео, але Томазо вмів заступитися, оскільки був хлопцем сильним і розумним. Він був найліпшим учнем у класі і найспритнішим хлопцем на подвір’ї. Томазо допомагав бідному Лео і завжди залишався поруч із ним. Заспокоював його, коли той боявся, чекав на нього, коли той гуляв, бавився з ним, старався, аби друг не переймався своїм каліцтвом, веселив його, розповідаючи різні потішні історії.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1276:vse-27-chervnja&catid=1:mystecka-storinka-cat&Itemid=50',3390,NULL,1,1,26),
(3857,'P','2013-06-27',1,NULL,'Rss',' Блаженні новомученики УГКЦ ','<p><img style=\"margin: 10px; float: left;\" src=\"http://4.bp.blogspot.com/-_YI6zu4bRBc/T89gNC996lI/AAAAAAAAADM/lDRW_2bS_Gg/s200/Martire.jpg\" height=\"200\" width=\"133\" /></p>\n<div>\n<p>У історії  деякі люди мають привілей пережити свою історичну епоху. Мученики перших  віків християнства завдяки свідченню віри є одним з прикладів  історичної пам’яті християн. Люди, які покликані свідчити віру в Христа перед переслідувачами стають на шлях праведного Авеля. «Вірою  Авель приніс Богу кращу, ніж Каїн, жертву; вона засвідчила про нього,  що він праведний, коли сам Бог дав свідоцтво на користь його дарів; нею  він, хоч і мертвий, далі промовляє». Євр. 11, 4. Віра в Господа Бога стала каменем спотикання для людей, які «проміняли славу нетлінного Бога на подобу, що зображує тлінну людину».  Рим. 1, 23. Від жертви Авеля до сьогодні віра в Господа Бога проходить  випробування. Церква шанує як мучеників свідків віри, які перед людьми  які відкидали Бога, через пролиття крові власним життям засвідчили  вірність Христу.</p>\n</div>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1310:-blazhenni-novomuchenyky-ugkc-&catid=35:istorychni-postati-cat&Itemid=48',3391,NULL,1,1,26),
(3858,'P','2013-06-27',0,NULL,'Note','Беатифікація новомучеників УГКЦ Іваном Павлом ІІ','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4875,200,bl_papa.jpg\" alt=\"\" width=\"200\" height=\"138\" />Відео урочистого проголошення блаженними 28 мучеників УГКЦ, які загинули за віру у XX ст. Обряд беатифікації здійснив Святіший Отець Іван Павло ІІ, 27 червня 2001 р., у Львові, під час Візиту до України. </span><br /><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Свідками події стали близько 1,5 млн. людей, які молилися Літургію у Візантійському обряді, разом з Папою Римським, на львівському іподромі.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4875,200,bl_papa.jpg\" alt=\"\" width=\"200\" height=\"138\" />Відео урочистого проголошення блаженними 28 мучеників УГКЦ, які загинули за віру у XX ст. Обряд беатифікації здійснив Святіший Отець Іван Павло ІІ, 27 червня 2001 р., у Львові, під час Візиту до України. </span><br /><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Свідками події стали близько 1,5 млн. людей, які молилися Літургію у Візантійському обряді, разом з Папою Римським, на львівському іподромі.</span><br /><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Детальніше про новомучеників <a class=\"yt-uix-redirect-link\" title=\"http://missiopc.blogspot.com/p/blog-page_9437.html\" dir=\"ltr\" href=\"http://www.youtube.com/redirect?q=http%3A%2F%2Fmissiopc.blogspot.com%2Fp%2Fblog-page_9437.html&amp;session_token=nGYuQR0Ml_N82X5PrQr_Es-Vx018MTM3MjQzNzUzNkAxMzcyMzUxMTM2\" rel=\"nofollow\" target=\"_blank\" data-redirect-href-updated=\"true\">http://missiopc.blogspot.com/p/blog-p...</a></span><br /><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Відео 2001 р., з архіву Департаменту інформації УГКЦ. Опубліковано 26 червня 2013 р., у 12-ту річницю Візиту Святішого Отця Івана Павла ІІ в Україну. </span><br /><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сайт Папського візиту до України: <a class=\"yt-uix-redirect-link\" title=\"http://www.papalvisit.org.ua/\" dir=\"ltr\" href=\"http://www.youtube.com/redirect?q=http%3A%2F%2Fwww.papalvisit.org.ua%2F&amp;session_token=nGYuQR0Ml_N82X5PrQr_Es-Vx018MTM3MjQzNzUzNkAxMzcyMzUxMTM2\" rel=\"nofollow\" target=\"_blank\" data-redirect-href-updated=\"true\">http://www.papalvisit.org.ua/</a></span><br /><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Більше відео з приїзду Блаженного Івана Павла ІІ на Медіаресурсі УГКЦ <a class=\"yt-uix-redirect-link\" title=\"http://ugcc.tv\" dir=\"ltr\" href=\"http://www.youtube.com/redirect?q=http%3A%2F%2Fugcc.tv%2F&amp;session_token=nGYuQR0Ml_N82X5PrQr_Es-Vx018MTM3MjQzNzUzNkAxMzcyMzUxMTM2\" rel=\"nofollow\" target=\"_blank\" data-redirect-href-updated=\"true\">http://ugcc.tv</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/DT2OTaYpBh0#at=10\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>',NULL,3392,5,1,NULL,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3859,'P','2013-06-28',1,NULL,'Rss','Алхімік-роззява (28 червня)','<p>&nbsp;<img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4848,200,klasyfikacija-koshtovnyh-kameniv-03.jpg\" />Жив колись алхімік, який усе своє життя присвятив пошукам філософського каменя, що має силу перетворювати залізні предмети у золото. «Я досліджу усі каміння на землі, один за другим. І так напевно знайду філософський камінь», - думав він.</p>\n<p>Спершу це видавалося простим. Алхімік обв’язався залізним ланцюгом і торкався до нього кожним знайденим каменем. Він усе мандрував, а коли помітив якийсь камінь – брав його і торкався ланцюга. Той жест став усім його життям.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1277:alhimik-rozzjava-28-chervnja&catid=1:mystecka-storinka-cat&Itemid=50',3393,NULL,1,1,26),
(3860,'P','2013-10-08',1,NULL,'Note','Аскольдів глас - збірник 2011','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вслухайся в голос неба,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вітру дзвінкі акорди</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Й підемо в нашу церкву</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">До Миколая-Аскольда.</span></p>','<p><strong>Поезія Т.Чаплик</strong></p>\r\n<p><strong>Із заходу на схід із півночі на південь</strong></p>\r\n<p>Сучасні спадкоємці прадавньої країни.</p>\r\n<p>Порозуміння й злагоди збудуємо мости,</p>\r\n<p>Щоби Аскольдовій Русі-землі цвісти.</p>\r\n<p>&nbsp;</p>\r\n<p><strong><em>Приспів:</em></strong></p>\r\n<p>Єднаймося у вірі Аскольдові нащадки,</p>\r\n<p>Щоб заповіти предків берегти.</p>\r\n<p>Щоб Батьківщину нашу у світі прославляти</p>\r\n<p>Єднаймося у силі діти і батьки.</p>\r\n<p>&nbsp;</p>\r\n<p>Свідомими вкраїнцями треба нам зростати</p>\r\n<p>І жити треба так, як князь Аскольд учив,</p>\r\n<p>Сміливо й невідступно свій край обороняти,</p>\r\n<p>Щоб люди прославляли і Бог благословив.</p>\r\n<p><strong><em>Приспів:</em></strong></p>\r\n<p>&nbsp;</p>\r\n<p>В єднанні наша сила, а у дружбі &ndash; слава</p>\r\n<p>Візьмемося за руки сестри і брати,</p>\r\n<p>Поставимо на ноги великую державу,</p>\r\n<p>Працюймо неустанно до здійснення мети.</p>\r\n<p><strong><em>Приспів:</em></strong></p>\r\n<p><strong><em>&nbsp;</em></strong></p>\r\n<p><span style=\"text-indent: 0em; font-size: 0.625em; line-height: 1.3em;\">&nbsp;</span></p>\r\n<p class=\"aCxSpFirst\"><strong>До князя Аскольда</strong><span style=\"text-indent: 0em; font-size: 0.625em; line-height: 1.3em;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\">Аскольде-князю, з висоти</p>\r\n<p class=\"aCxSpMiddle\">Поглянь на нашу Україну.</p>\r\n<p class=\"aCxSpMiddle\">Земляче рідний, нам прости,</p>\r\n<p class=\"aCxSpMiddle\">Що ненька наша на колінах.</p>\r\n<p class=\"aCxSpMiddle\">Що ми її не підвели,</p>\r\n<p class=\"aCxSpMiddle\">І підвести не маєм сили,</p>\r\n<p class=\"aCxSpMiddle\">Що славу й честь її святу</p>\r\n<p class=\"aCxSpMiddle\">Зберегти ми не зуміли.</p>\r\n<p class=\"aCxSpMiddle\"><span style=\"text-indent: 0em; font-size: 0.625em; line-height: 1.3em;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\">Навчи нас, князю, перш за все</p>\r\n<p class=\"aCxSpMiddle\">Служити рідному народу</p>\r\n<p class=\"aCxSpMiddle\">І як мету в життя нести</p>\r\n<p class=\"aCxSpMiddle\">Його добробут і свободу.</p>\r\n<p class=\"aCxSpMiddle\">Молись до Господа за нас,</p>\r\n<p class=\"aCxSpMiddle\">Щоб він, Всевишній, дав нам ласку,</p>\r\n<p class=\"aCxSpMiddle\">У рідній нашій Батьківщині</p>\r\n<p class=\"aCxSpLast\">Омріяну створити казку.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: large;\"><strong style=\"text-indent: 0em; font-size: 0.625em; line-height: 1.3em;\">Ти був патріотом, Аскольде</strong></span><span style=\"text-indent: 0em; font-size: 0.625em; line-height: 1.3em;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\">Ти був патріотом, Аскольде,</p>\r\n<p class=\"aCxSpMiddle\">Колись на Русі князював,</p>\r\n<p class=\"aCxSpMiddle\">Хрестив наш народ і навіки</p>\r\n<p class=\"aCxSpMiddle\">В історію ім&rsquo;я вписав.</p>\r\n<p class=\"aCxSpMiddle\">&nbsp;</p>\r\n<p class=\"aCxSpMiddle\">За землю святу Українську</p>\r\n<p class=\"aCxSpMiddle\">Не раз і не два воював,</p>\r\n<p class=\"aCxSpMiddle\">Щоб кат і нападник ворожий</p>\r\n<p class=\"aCxSpMiddle\">Ногою сюди не ступав.</p>\r\n<p class=\"aCxSpMiddle\">&nbsp;</p>\r\n<p class=\"aCxSpMiddle\">Багато спливло з того часу,</p>\r\n<p class=\"aCxSpMiddle\">Затерлись деталі в віках.</p>\r\n<p class=\"aCxSpMiddle\">Та ім&rsquo;я твоє незабутнє</p>\r\n<p class=\"aCxSpMiddle\">Лунає у нас на устах.</p>\r\n<p class=\"aCxSpMiddle\">&nbsp;</p>\r\n<p class=\"aCxSpMiddle\">Окрасо Вкраїни і гордість,</p>\r\n<p class=\"aCxSpMiddle\">Про тебе складаєм пісні,</p>\r\n<p class=\"aCxSpMiddle\">Ми знаєм, що ти їх почуєш</p>\r\n<p class=\"aCxSpLast\">Десь там у святій вишині.</p>\r\n<p class=\"aCxSpLast\">&nbsp;</p>\r\n<p style=\"text-align: left;\" align=\"center\"><strong>Аскольдів храм</strong></p>\r\n<p class=\"ListParagraphCxSpFirst\" style=\"text-align: left;\" align=\"right\"><strong>&nbsp;сл. і муз. Ю.Ремезенко</strong></p>\r\n<p class=\"ListParagraphCxSpMiddle\">&nbsp;</p>\r\n<ol>\r\n<li>&nbsp; Пагорби Дніпра (2 р.)</li>\r\n</ol>\r\n<p class=\"ListParagraphCxSpMiddle\">Видно здалека (2 р.)</p>\r\n<p class=\"ListParagraphCxSpMiddle\">Тишу і любов (2 р.)</p>\r\n<p class=\"ListParagraphCxSpMiddle\">Зберігають ці святі місця.</p>\r\n<p class=\"ListParagraphCxSpMiddle\">&nbsp;</p>\r\n<ol>\r\n<li>&nbsp; В кожного свій шлях (2 р.)</li>\r\n</ol>\r\n<p class=\"ListParagraphCxSpMiddle\">Але в далині (2 р.)</p>\r\n<p class=\"ListParagraphCxSpMiddle\">Згадуємо ми (2 р.)</p>\r\n<p class=\"ListParagraphCxSpMiddle\">Із дитинства істини прості.</p>\r\n<p class=\"ListParagraphCxSpMiddle\">&nbsp;</p>\r\n<p class=\"ListParagraphCxSpMiddle\"><strong>Приспів:</strong></p>\r\n<p class=\"ListParagraphCxSpMiddle\">Стежечкою, стежечкою</p>\r\n<p class=\"ListParagraphCxSpMiddle\">Я до храму йду</p>\r\n<p class=\"ListParagraphCxSpMiddle\">Розквітає біля храму</p>\r\n<p class=\"ListParagraphCxSpMiddle\">Кожная душа</p>\r\n<p class=\"ListParagraphCxSpLast\">Благодаттю Божою сповнена.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>',NULL,3661,5,1,NULL,54);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3861,'P','2013-06-29',1,NULL,'Rss','Греко- і римо-католики закликали українців і поляків до примирення','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4876,200,shevchuk_mixalik.jpg\" />Представники Української греко-католицької церкви  та Римо-католицької церкви Польщі підписали у Варшаві спільну декларацію  з нагоди 70-річчя Волинської трагедії.</p>\n<p>Про це повідомляє <a href=\"http://www.ugcc.org.ua/news_single.0.html?&amp;tx_ttnews[tt_news]=11590&amp;cHash=65e537bfd4ebe0fa05949fd0ccce99b6\">офіційний сайт</a> УГКЦ.</p>\n<p>У документі підкреслюється, що насильство та етнічні чистки за жодних  обставин не мають права бути методом вирішення конфліктів і не можуть  бути виправдані ні політичним, ні економічним, ні релігійним інтересом.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1311:greko-i-rymo-katolyky-zaklykaly-ukrajinciv-i-poljakiv-do-prymyrennja&catid=28:tema-nomera-cat&Itemid=2',3394,NULL,1,1,26),
(3862,'P','2013-06-29',1,NULL,'Note','Блаженніший Святослав та Президент Польщі спільно молилися за жертв Волинської трагедії','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4878,200,87d70f8f-83c7-4316-aa58-22953781c874.file.jpg\" alt=\"\" width=\"200\" height=\"109\" />До присутніх звернулися: Блаженніший Святослав, Глава УГКЦ, владика Іван Мартиняк, Митрополит Перемишльсько-Варшавський, архиєпископ Юзеф Міхалік, Примас Польщі, та Президент Польщі Броніслав Коморовський.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У молитві також взяли участь: архиєпископ Юзеф Міхалік, Примас Польщі, кардинал Казімєж Нич, Митрополит Варшавський, і Президент Польщі Броніслав Коморовський.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong class=\"main\"><img style=\"float: right;\" src=\"image,4878,200,87d70f8f-83c7-4316-aa58-22953781c874.file.jpg\" alt=\"\" width=\"200\" height=\"109\" />27 червня у Варшаві, у греко-католицькому храмі Успіння Пресвятої Діви Марії, Блаженніший Святослав Шевчук, Предстоятель УГКЦ, очолив панахиду з нагоди 70-х роковин Волинської трагедії. </strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У молитві також взяли участь: архиєпископ Юзеф Міхалік, Примас Польщі, кардинал Казімєж Нич, Митрополит Варшавський, і Президент Польщі Броніслав Коморовський.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">До присутніх звернулися: Блаженніший Святослав, Глава УГКЦ, владика Іван Мартиняк, Митрополит Перемишльсько-Варшавський, архиєпископ Юзеф Міхалік, Примас Польщі, та Президент Польщі Броніслав Коморовський.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/cVV5nCiXCQ8\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>',NULL,3395,5,1,NULL,43),
(3863,'P','2013-06-28',1,NULL,'Note','Справжній християнин – це людина радості, яка опирає свою віру на скелі, якою є Христос','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4879,200,1_0_705352.JPG\" alt=\"\" width=\"200\" height=\"133\" />Папа звернув увагу на дві крайності у християнському житті: часто християни є або суворими і сумними, або радісними, але без розуміння ідеї християнської радості. А також, аналізуючи історію християнства, можна сказати, що існували два типи християн: &laquo;християни слів&raquo;, тобто ті, які лише говорять &laquo;Господи Господи&raquo;, і &laquo;християни вчинків&raquo;, які своєю поведінкою дають свідчення своїх християнських переконань.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4879,200,1_0_705352.JPG\" alt=\"\" width=\"200\" height=\"133\" />Є люди, які надягають маску християн і грішать, або живуть дуже поверховним життям чи відзначаються надмірною негнучкістю, забуваючи, таким чином, про те, що справжній християнин &ndash; це людина радості, яка опирає свою віру на скелі Христа. Це була одна із головних думок, над якими Святіший Отець Франциск запропонував призадуматись присутнім на Святій Месі у каплиці ватиканської резиденції &laquo;Дім Святої Марти&raquo; у четвер, 26 червня 2013 року. Того дня з Папою співслужив кардинал Раймунду Дамасену Ассіс, Архиєпископ Апаресіди у Бразилії, а також інші єпископи та священики. В Євхаристійному богослуженні взяли участь представники Медичної Служби Апостольської Столиці, на чолі з їхнім директором та особистим лікарем Вселенського Архиєрея, доктором Патріціо Поліскою. </span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У своїй проповіді Папа звернув увагу на дві крайності у християнському житті: часто християни є або суворими і сумними, або радісними, але без розуміння ідеї християнської радості. А також, аналізуючи історію християнства, можна сказати, що існували два типи християн: &laquo;християни слів&raquo;, тобто ті, які лише говорять &laquo;Господи Господи&raquo;, і &laquo;християни вчинків&raquo;, які своєю поведінкою дають свідчення своїх християнських переконань. </span><br /><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Єпископ Риму, коментуючи біблійний уривок з Євангелії від Матея, який був прочитаний того дня під час Святої Меси, наголосив, що завжди існувала спокуса жити наше християнство поза скалою, якою є сам Христос, тобто будувати своє життя на чомусь іншому, а не на міцній основі, на Господі Ісусі. Лише тоді, коли будуємо своє життя на надійній скелі, на Ісусі, то навіть, як випаде сильний дощ, розлиються потоки і подують сильні вітри, той дім встоїться. На жаль, сказав Папа, в минулому, а також й у наші дні, є чимало християн лише на словах, які впадають у спокусу будувати своє християнське життя без Христа. </span><br /><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Придивляючись ближче тим &laquo;християнам слів&raquo;, які лише на словах є учнями Ісуса, Єпископ Риму вказав на деякі їхні характеристики. Він наголосив, що є два зразки таких християн. Перші &ndash; це ті, які замість любити скелю, тобто Ісуса Христа, люблять лише гарні слова, а тому залишаються лише на поверхні християнського життя, як слід не занурюючись у нього. Вони є гностиками наших днів. Інший зразок &ndash; це суворі і сумні християни, які, як пожартував Святіший Отець, завжди дивляться у підлогу. Вони вважають, що бути християнином, вимагає жити у постійній жалобі. Вселенський Архиєрей наголосив, що у наш час є досить багато таких християн, але вони не є справжніми християнами, вони лише надягають маску християн. &laquo;Вони не знають, Ким є Господь, не знають, що це є скеля, не є вільними християнами. І, говорячи дещо простіше, вони не мають радості&raquo;. </span><br /><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Той перший зразок християн, про яких ми говорили, можна охарактеризувати певною веселістю, але вона є лише поверховною. Натомість другий зразок відзначається &laquo;безперервним похоронним чуванням&raquo;, не усвідомлюючи, чим є християнська радість і не вміючи втішатись життям, яке нам подарував Господь, оскільки не вміють говорити із Спасителем. Вони не відчувають себе на Христі, як на міцній скелі, і не лише не є радісними, але й не є вільними. Як бачимо, одні є в&rsquo;язнями поверховності, а другі є в&rsquo;язнями суворості. В житті таких людей Святий Дух, Який вчиняє особу вільною, не знаходить місця. А на закінчення Папа підкреслив, що Господь запрошує нас будувати наше християнське життя на Ньому, на скелі, на тому, що дає нам свободу і радість.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Текст із сторінки <a href=\"http://uk.radiovaticana.va/news/2013/06/27/%D0%BF%D0%B0%D0%BF%D0%B0_%D1%84%D1%80%D0%B0%D0%BD%D1%86%D0%B8%D1%81%D0%BA:_%D1%81%D0%BF%D1%80%D0%B0%D0%B2%D0%B6%D0%BD%D1%96%D0%B9_%D1%85%D1%80%D0%B8%D1%81%D1%82%D0%B8%D1%8F%D0%BD%D0%B8%D0%BD_%E2%80%93_%D1%86%D0%B5_%D0%BB%D1%8E%D0%B4%D0%B8%D0%BD%D0%B0_%D1%80%D0%B0%D0%B4%D0%BE%D1%81%D1%82%D1%96,_%D1%8F%D0%BA%D0%B0_%D0%BE%D0%BF%D0%B8%D1%80%D0%B0/ucr-705352\">http://uk.radiovaticana.va/news/2013/06/27/</a></span></p>',NULL,3396,5,1,NULL,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3864,'P','2013-06-29',1,NULL,'Rss','Розквітлі зорі... на землі ','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4878,200,images.jpg\" />Недарма хтось сказав, що зорі квітнуть на землі. Проте так, як вони здивували мене цього вечора, навіть годі описати.</p>\n<p>Жадібно впиваюся рідним повітрям. Воно п`янке під цю пору, наче міцний трунок. До щему в душі проникає в мене, зрошує своєю прохолодою, торкається спомином.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1312:rozkvitli-zori-na-zemli-&catid=1:mystecka-storinka-cat&Itemid=50',3397,NULL,1,1,26),
(3865,'P','2013-06-29',1,NULL,'Rss','Без виправдання (29 червня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4847,200,zalyshys.jpg\" />Якоїсь неділі на брамі церкви вивісили таке оголошення: «Аби всі могли прийти до церкви, у найближчий тиждень ми організуємо «особливу неділю без виправдань». У захристії розмістимо ліжка для всіх тих, котрі твердять, що неділя є єдиним днем тижня, коли вони можуть виспатися.</p>\n<p>Буде приготовано спеціальний сектор м’яких фотелів для тих, котрі вважають, що церковні лави незручні. Тим, у кого очі надто стомлені після ночі, проведеної перед телевізором, запропонуємо відповідні краплі для очей.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1278:bez-vypravdannja-29-chervnja&catid=1:mystecka-storinka-cat&Itemid=50',3398,NULL,1,1,26),
(3866,'P','2013-06-29',1,NULL,'Note','Митрополит Андрей Шептицький','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4882,200,l_242d8216.jpg\" alt=\"\" width=\"200\" height=\"150\" />Він на свої очі бачив Андрея Шептицького. Коли став священиком то мав особливо нагоду слухати його настанови та пророчі слова про переслідування Церкви. Він, Максим Германюк (1911-1996), митрополит з Канади, належав до отців редемптористів. В інтервю розповідає про зустріч з Андреєм Шептицьким у вересні 1938 р., після свого священичого рукоположення.</span></p>','<p>&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4882,200,l_242d8216.jpg\" alt=\"\" width=\"200\" height=\"150\" /></span><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Він на свої очі бачив Андрея Шептицького. Коли став священиком то мав особливо нагоду слухати його настанови та пророчі слова про переслідування Церкви. Він, Максим Германюк (1911-1996), митрополит з Канади, належав до отців редемптористів. В інтервю розповідає про зустріч з Андреєм Шептицьким у вересні 1938 р., після свого священичого рукоположення. </span><br /><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Відеозапис здійснено у липні &nbsp;1994 р., в Прилбичах на Львівщині, у родинному селі Шептицьких. Розмовляла Оксана Гайова, Студія \"Собор\".</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Митрополит Андрей Шептицький</strong></span></p>\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/bVFCPQhRSoU\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>\r\n<p><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></p>\r\n<p>&nbsp;</p>\r\n<p class=\"yt\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Митрополит Андрей Шептицький -- опікун і меценат</span></strong></p>\r\n<p class=\"yt\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/qvchHZ7uQCQ\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></strong></p>\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p>&nbsp;</p>',NULL,3399,5,1,NULL,48);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3867,'P','2013-07-01',1,NULL,'Rss',' Глава УГКЦ очолив святкування 1025-ліття Хрещення України-Русі в Гурово-Ілавецькому (Польща)','<p><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/38af9dd1da.jpg\" height=\"100\" width=\"150\" /></p>\n<p>29 червня за участю кількох тисяч вірян із Польщі та з-за  кордону в Гурово-Ілавецькому розпочалося святкування 1025-ліття Хрещення  України-Русі. Божественну Архиєрейську Літургію очолив Блаженніший  Святослав (Шевчук), Глава Української Греко-Католицької Церкви.  Необхідно відзначити, що крім єпископату Греко-Католицької Церкви, в ній  взяв участь архиєпископ Селестіно Мільоре, Апостольський нунцій в  Польщі.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1313:-glava-ugkc-ocholyv-svjatkuvannja-1025-littja-hreshchennja-ukrajiny-rusi-v-gurovo-ilaveckomu-polshcha&catid=28:tema-nomera-cat&Itemid=2',3403,NULL,1,1,26),
(3868,'P','2013-07-01',1,NULL,'Rss','«Заохочую вас бути в контакті один з одним…», - Блаженніший Любомир','<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/6034aff6e7.jpg\" height=\"106\" width=\"150\" /></div>\n<p>«Сьогодні існує багато сучасних засобів комунікації, які значно  полегшують спілкування. Гадаю, потрібно їх використовувати. Адже, коли  ми думаємо разом і обмінюємося власними думками та досвідами, ми стаємо  сильнішими і, відповідно, можемо більше зробити для Церкви і свого  народу. Заохочую вас через ці сучасні засоби комунікації бути в контакті  один з одним: в контакті думок та діл», - закликав молодіжних лідерів  Блаженніший Любомир (Гузар), Архиєпископ-емерит УГКЦ, під час Форуму  молодіжних лідерів УГКЦ, що відбувся 28-30 червня у Львівській духовній  семінарії Святого Духа УГКЦ.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1314:lzaohochuju-vas-buty-v-kontakti-odyn-z-odnymr-blazhennishy-ljubomyr&catid=37:spilnota-cat&Itemid=49',3404,NULL,1,1,26),
(3869,'P','2013-07-01',1,NULL,'Note','Визнання віри Хорхе Бергольйо (Папи Франциска)','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вірю в Марію, мою матір, Котра мене любить і ніколи не залишить самого. І очікую щоденних несподіванок, які будуть проявом любові, сили, зради і ігріха: це буде супроводжувати мене аж до дня остаточної зустрічі з тим чудовим обличчям, яке я не знаю як виглядає, від якого ще втікаю, але яке прагну пізнати і полюбити. Амінь.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em><img style=\"float: left;\" src=\"image,4888,200,163777416-203x300.jpg\" alt=\"\" width=\"135\" height=\"200\" />Визнання віри, яке Хорхе Маріо Бергольйо написав за день до прийняття священичих свячень (1969 року).</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хочу вірити в Бога Отця, Котрий любить мене як свою дитину, і в Ісуса, Господа, Котрий вдихнув у моє життя свого духа, щоб я усміхався і міг дійти до Царства вічного життя.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вірю в історію мого життя, над яким осіннього дня, 21 вересня, спочив сповнений любові погляд Бога, попровадив мене на місце зустрічі і запросив йти за Ним.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вірю в моє терпіння, яке не приносить плоди через егоїзм, в якому скривається.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вірю в слабкість моєї душі, яка прагне брати і нічого не давати&hellip; не давати.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вірю в доброту інших людей, і що мушу любити їх без страху, і що ніколи не можу зрадити їх, шукаючи власної безпеки.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вірю в богопосвячене життя.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вірю, що можу палко любити.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вірю у щоденне умертвіння, всепалення, від яких втікаю, але які усміхаються до мене і просять, щоб я їх прийняв.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вірю в терпеливість Бога, яка обнімає і є такою приємною, як літня ніч.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вірю, що тато є в небі разом з Господом.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вірю, що там також є отець Дуарте і заступається за моє священство.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вірю в Марію, мою матір, Котра мене любить і ніколи не залишить самого. І очікую щоденних несподіванок, які будуть проявом любові, сили, зради і ігріха: це буде супроводжувати мене аж до дня остаточної зустрічі з тим чудовим обличчям, яке я не знаю як виглядає, від якого ще втікаю, але яке прагну пізнати і полюбити. Амінь.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">За матеріалами <a href=\"http://deon.pl/\" target=\"_blank\">deon.pl</a></span></p>',NULL,3405,5,1,NULL,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3870,'P','2013-07-02',1,NULL,'Rss','Розп\'яття (3 липня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4865,200,2615229.jpg\" />У старій церкві дуже високо  висіло величезне срібне розп’яття. Дві особливості вирізняли його. Перша  - це терновий вінець на голові Ісуса: був із золота, викладений  коштовними рубінами.</p>\n<p>Друга особливість - права рука Ісуса: не була прибита до хреста, а дещо відхилена вперед.</p>\n<p>Одна історія пояснює причину цього.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1293:rozpjattja-3-lypnja&catid=1:mystecka-storinka-cat&Itemid=50',3406,NULL,1,1,26),
(3871,'P','2013-07-03',1,NULL,'Rss','Слухай (4 липня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4865,200,10477758_15b11bb0.jpg\" />Одного юнака, якого приятель запросив на своє весілля, здивувало, як багато осіб вітало молодят.</p>\n<p>Зауважив, що гості й родичі молодих вітали одне одного механічно, навіть не слухаючи сказане.\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1294:sluha-4-lypnja&catid=1:mystecka-storinka-cat&Itemid=50',3407,NULL,1,1,26),
(3872,'P','2013-07-04',1,NULL,'Note','Звіт №7 про НАДХОДЖЕННЯ  ЛИСТІВ 11.05.2013 р. — 31 лист','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Получила Вашого листа, за якого дуже вдячна... У нас дозволяють звонить 2 рази на тиждень. <strong>З сином спілкуюсь. Здоровья в нього пока нормально (</strong><em>ВІЛ-інфікований</em><strong>). Дуже він цікавиться Біблейними історіями.</strong> Мама даже йому купила діск з мультфільмом &laquo;Літаючий будинок&raquo;. <strong>Читає дитячю &laquo;Біблію&raquo;.</strong> У мене теж все нормально <strong>готуюся до ісповіді 4.05. Я хожу до церкви до отця Віталія. Греко -католицької</strong>&rdquo;</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Листи від ув\'язнених </span></em></strong><strong><em><span style=\"text-decoration: underline;\">&nbsp;(з них вперше звернулись 5 осіб)</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Чернігівська виправна колонія ЧВК-44 (м.Чернігів) &mdash; </strong><strong>22</strong><strong> лист</strong><strong>и</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Збаразька виправна колонія ЗВК-63 (Тернопільська обл.) &ndash; 1 лист</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Селезнівська виправна колонія СВК-143 (Луганська обл.) </strong><strong>&mdash;</strong><strong> 5 листів</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Орджонікідзевський виправний центр ОВЦ-79 (Дніпропетр. обл.) </strong><strong>&mdash;</strong><strong> 2 листи</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Листи звільнених з ув</span></em></strong><strong><em><span style=\"text-decoration: underline;\">&rsquo;</span></em></strong><strong><em><span style=\"text-decoration: underline;\">язнення &ndash; 1 лист</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Я звільняюсь 18 квітня, їду на Полтаву рибцентр з синочком (<em>2,5 р.)</em></strong><em>.</em>.. <strong>хочу просити допомогти нам коштами на первий час</strong>, як тільки приїдемо то я напишу Вам листа. Мені дуже приємно, що Ви так хвилюїтесь за нас з сином бо знаючи яке <strong>зараз важке життя люди жорстокі і з ненавістю</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Наталія П. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Чувство одиночества часто загоняет человека в тупик</strong>. А тут Вы с теплом и заботой. Слава БОГУ! У меня нет деток, я жизнь прос...а свою а теперь пытаюсь найти выход и нагнать упущенное. Я верю что БОГ есть и поможет мне. Помолитесь усиленной молитвой за моих сестер за наш мир с ними Елена и Татьяна. Спасибо Вам огромное. <strong>Я на коленях молюсь о Вас, я так сильно благодарна Вам за то что Вы есть</strong>. Храни Вас БОГ&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Євгенія К. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Моїй донечці 6 років, у вересні іде до школи, </strong>живе разом з бабусею. Моя Настя дуже росла так якщо вещи то на 7-8 років, а розмір ноги 30-31... <strong>Дякую Вам за Вашу турботу за диточок</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Юлія Є. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Сама я сирота, ростила тётя... <strong>Я после операции, пока не работаю, помогите по возможности, мыльным и продуктами питания</strong>, пожалуста&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Наталія К. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Дома у меня осталась старенькая мама, ей 75 лет</strong>. Живет она одна... Недавно ей стало сильно плохо, соседи вызвали &laquo;скорую&raquo; и уже в больнице <strong>ей поставили диагноз &ndash; сахарный диабет... денег на лечение у неё нет</strong>... Слезно прошу помогите моей матери. <strong>Вышлите ей лекарства &laquo;Диаформин&raquo; и &laquo;Комбиприл&raquo;.</strong> Уповаю только на Божью милость и Ваше милосердие&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Тетяна С. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Мои семеро деток которые проживают в Виннице, очень благодарны Вам, за помощь</strong>. Самый маленький мой сыночек Ванюшка пишет, что <strong>перед праздниками получают посылку от отца Игоря, </strong>и пишет мне (я молюсь за отца Игоря, и прошу у Бога ему здоровья, я знаю что здоровье самое главное потому что мамочка ты у меня больная, а кроме тебя мамка у нас никого нет!) И я молюсь, хожу по суботам в церковь, приходит о.Виталий, с.Оксана. Прошу у Господа нашего, чтобы дал мне здоровья, сил, терпенья, выдержать эти муки, и выйти живой к детям, <strong>мне только 48 лет, а так хочется ещё жить, быть вместе с детьми... Молитесь за меня и моих деток. Я нахожусь в местах лишения свободы уже без двух месяцев 5 лет, ещё 1 год 9 мес.</strong><strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Галина Б. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Получила от Вас лекарства, за которые очень благодарю. Спасибо Господу за Вас,</strong> что располагает Ваши сердца по отношению к нам &ndash; находящимся в колонии. Мне очень хочется пожелать Вам, отец Игорь, и Вашей Церкви, чтобы Господь обильно благословил Ваши семьи всеми Своими благословениями, чтобы в сердцах жила любовь, был мир Божий и радость... духовных, душевных и материальных благословений Вам!&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Людмила С. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... я каждое утро пью святую воду и ем кусочек просфорки, и читаю молитву &laquo;во оставление грехов, в просвищение ума, и в укрепление телесных и душевных сил&raquo;, обезательно утреннюю молитву, вечером мы ходим на вечернюю молитву нас собираеться сестёр 10-12. Благодарим за всё Господа! Уважаемый Игорь, <strong>я здесь заканчиваю школу на &laquo;Волонтёра&raquo; я хотела у Вас спросить, по освобождению я могу быть при церкви, служить Господу, я хочу ездить по колониям и свидетельствовать людям, кем я была, и как Господь изменил меня, и мою жизнь. </strong>Дорогая сестра, <strong>я даже не знаю с чего начать на свободе, в какую церковь пойти</strong>, я полностью положилась на Боженьку! Знаете с.Вера мне кажется если уже сделал шаг вперёд к Господу, то назад уже непойдёшь, а будеш всё больше и больше наполняться Святым Духом, и больше познавать Слово Божье... <strong>Вы очень хорошие люди, и я Вас очень прошу пишите будем с Вами общаться на духовную тему.</strong> Знаете как я всегда говорю: <strong>Спасибо, Иисус, что Твоя любовь стала примером для меня, помоги мне так любить людей, как Ты их полюбил!</strong> <strong>Аминь. Благодарю Вас за красивые стихи! И очень Вас хотела попросить положить просфорку, у меня заканчиваеться , и молитвы в течение дня. Я уже семь месяцев как исцелина от курева</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ольга Ч. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Лекарства ваши получила, большое спасибо</strong>. Ножки уже Слава Богу! Периодически конечно воспалаяются, но это пройдёт. Ведь <strong>я молюсь Богу каждый день, вернее несколько раз на день благодарю Его за исцеление</strong>... <strong>Я пошла учится в библейскую школу</strong>, хоть там и не дают никакого атестата, но я хочу изучить чуть-чуть Библию, подумать, позапамьятать. <strong>Мне присылают уроки которые я прочитываю а потом отвечаю на домашнее задание</strong>. Пока у меня только всего один урок пройден, жду оценки... <strong>Хочу попросить Вас</strong>, если у Вас конечно будет такая возможность <strong>выслать мне на Пасху какую нибудь маленькую посылочку. Чем Боженька благословит</strong>. А там если не получится и на том спасибо, что письма мне пишете, поддержуете меня&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ольга Т. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Я получила Ваше лекарство за которое большое Вам спасиба</strong>. Пусть Господь Бог наш одарит своей любовью.Здоровья Вам крепкого и всего самого найлучшего&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Аліна Б. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Долго не отписывала Вам очень болела. <strong>У меня хронический астматический бронхит... чем не лечи не помогает. Единственное что мне помогает то это козий жыр</strong>, но его очень тяжело здесь достать... Отец Игорь и сестрички <strong>хочу поздравить с днём Святого Воскресенья Пасхи. Хочу попросить у Вас вышлите пожалуйста пару пасочек и крашенок</strong>... Я благодарна нашему Великому Спасителю! Господу Богу Иисусу Христу за все Его милости, и за то что Вы есть&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Валентина Г. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Спасибо вам за ваше понимание и моральную поддержку!..</strong> для меня огромное счастье, что вот так всё обернулось. Обратилась к вам с просьбой помочь моей дочечке, а преобрела взаимопонимание в общении с вами. Я благодарна Господу за всё, что Он для меня уготовил и надеюсь на Его дальнейшую милость&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Тетяна М. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>У меня есть сестра Наташа, младшая. Она сечас находится на Луганске (обл.) в лагере в ПГТ Селезневка</strong>. Очень болеет, трудно привыкает к терапии, <strong>ІІ ст. клиническая СПИДа</strong>. <strong>У сестры день рождение 23-го апреля, будет 35 лет, а я не могу ей даже выслать открытку.</strong> Прошу Вас от всего сердца поздравить мою Наташу и этим поддержать ее морально и духовно. <strong>Все мы находящие в этих местах, потерялись, заблудились и получили по жызни &laquo;обрезание&raquo;. Калеки в вере, в любви, в духе</strong>... <strong>Помогите словом</strong>. Может Бог востановить любого. Я верю, что сестра моя примет Исуса Хреста и покается. Аминь&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Євгенія Б. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Вы пишете за Серафиму... она с мамой. Я буду благодарна за помощь им. Мне так же нужна ваша молитвенная поддержка. <strong>У меня в мае открывается повторное УДО (условно-досрочное освобождение), молитесь, чтобы меня отпустили</strong>. Как говорит Писание: Жребий кидается человеком, а выбор от Господа. У меня <strong>в мае будет 5 лет как я сижу, с Богом я 4 года и Бог так много сделал в моем сердце, столько мусора от туда выбросил. А самое главное, у меня пропала ненависть к людям. Я научилась радоваться чужим радостям, сострадать, просто служить людям во имя Господа</strong>. И мне от этого легко и хорошо на сердце. Меня очень радуют эти перемены. И вся заслуга в этом Господа. Бог Великий и Чудный!!! Вот только жаль, что не все это понимают, что у многих закрыты глаза. А как же хочется эту радость донести всем.<strong> Я настолько доверилась Богу во всем, как ребенок доверяет своим родителям, не сколько не сомневаясь в словах родителей, так и я верю и уповаю на Бога, а уповающий на Бога не постыдится и я знаю, что все Божьи обетования исполнятся в моей жизни</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Олена Г. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>хочу поблагодарить Вас за лекарства, которые выслали мне. Благодарю Господа и Вас за эту помощь</strong>... А <strong>краски были бы для меня лучшим подарком</strong> и не только на Паску, но и на День рождения (30 апреля). За посылку сыну пишу, что с Нового года бабуля лежала в больнице, а сейчас они в России у родственников. Сыну 14 лет, Александр. Бабушка решила, что-бы он последние школьные годы проучился в России, его там пропишут, потом поступит в институт и может там и останется. Ведь <strong>мне ещё сидеть 4 года,</strong> бабушке 83 года. Я очень скучаю, но понимаю, что так будет лучше На всё воля Господа. <strong>Сейчас пост, но мяса мы и так не видим, а вот мир в душе я храню. Стараюсь ни с кем не ссориться, не злиться, мыслить только о хорошем, излучать добро. Вот такой мой пост в этих стенах</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Яна Г. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Низкий Вам поклон и огромное спасибо за наших деток</strong>. Я как-то вопрос решу с вещами. <strong>Очень огромная проблема с обувью. Пожалуйста помогите приобрести кроссовки 39-40 р</strong>. Поздравление с светлым днем Пасхи&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Лілія С. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>хочу поблагодарить Вас за лекарства, которые Вы мне выслали, большое Вам спасибо</strong>. Спасибо Господу Богу за то, что посылает таких людей как Вы, что помогаете нам&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Олена Ю. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Нам не разрешают греть воду, да ещё и кипятильником?! Это наказуемо! Мы можем кипятильником только нагреть себе чая, кофе, запарить гречку, мивину, картошку пюре, всё, что продаётся в пакетиках (быстрого приготовления). А объём работы моей, тоесть уборки, мытья полов и туалета, площадь не маленькая, воды надо много. Зачем мне нарушения, <strong>я сильно, очень сильно хочу домой, к маме и своей доченьке Наташе (<em>інвалід дитинства</em>). Они меня ждут! А я, в свою очередь, добросовестно тружусь и стараюсь к осени пройти УДО</strong>. Вот вчера получила я от мамы письмо, я так наплакалась. <strong>Пишет мне мама, что наверное меня она не дождётся, сильно болеет, совсем слабое сердце, передвигаться по дому тяжело. А так хочет со мной встретиться! Им тяжело без меня, а мне &ndash; без них</strong>... В селе сейчас, весной, столько работы, особенно огород посадить надо, а я нахожусь здесь! Кто им там поможет, ума не приложу? Дорогой о.Игорь, прошу Вас помогите, <strong>мне надо крема для рук &laquo;Чистая линия&raquo; &laquo;Лаванда узколистая&raquo;. Руки у меня стали шершавыми, пучки и на изгибах трещины образовались от моющих средств. Такую я терплю боль, что врагу не пожелаешь.</strong> И ещё, о.Игорь за все года, а я отсидела 10 лет, очень хочу пасхи. Вышлите мне, пожалуйста... <strong>Моей маме, Евдокии Федосеевной, 25 апреля исполнится 85 лет. Помолитесь за мою маму, Наташу, о здравии, меня &ndash; Анну</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Анна Г. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>В заключении я нахожусь с 2004 г. За эти 8,5 лет я не видела ни дочь, ни внука, которому уже 6 лет. Материальной возможности нет ни у меня, ни у моей дочери. Освобождаюсь я аж в 2019 году. Отец Игорь, очень Вас прошу, помогите нам встретиться</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Світлана Т. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Спасибо, что не забываете обомне и моих сыновьях.</strong> Старший Илюша увлекается футболом. <strong>Я Вас просила помолится</strong> о нём, у него была травма коленного сустава. <strong>Слава Богу! Что Ваши и мои молитвы услышал Господь. У Илюши срослась нога.</strong> И он снова может посещать секцию. <strong>Адамчик живёт с моей свекровью. Кроме него у свекрови ещё живут 5 детей малолетних. </strong>Моего мужа сестры детки. Адамчик среди них самый маленький. <strong>Очень любит рисовать и разукрашивать книги. Просит у меня машину...</strong> Я знаю что перед пасхой Вы будете отправлять деткам посылочки. <strong>Хотела вас попросить выслать моему сыночку Адамчику шампунь, мыло и машынку</strong>. Низкий Вам поклон за то, что низабываете о нас и помогаете&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Інна Г. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Получила Вашого листа, за якого дуже вдячна... У нас дозволяють звонить 2 рази на тиждень. <strong>З сином спілкуюсь. Здоровья в нього пока нормально (</strong><em>ВІЛ-інфікований</em><strong>). Дуже він цікавиться Біблейними історіями.</strong> Мама даже йому купила діск з мультфільмом &laquo;Літаючий будинок&raquo;. <strong>Читає дитячю &laquo;Біблію&raquo;.</strong> У мене теж все нормально <strong>готуюся до ісповіді 4.05. Я хожу до церкви до отця віталія. Греко -католицької</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Світлана В. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Искренне Вам благодарна за лекарства и мыло</strong>... при возможности вышлите пожалуйста что-то для печени Лиф-52; расторопша; алахол и обезбаливающие спалмалгон; ибупрофен мазь фаниган&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Галина Ч. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>мне 37 лет, нахожусь в неволе 9-й год, общий срок &ndash; 15 лет</strong>, родственных связей нету, помощи нету. <strong>Очень мне не хватает моральной поддержки</strong>, хочу общаться на духовную тему, буду очень рада переписке с вами. У меня к вам огромная просьба, <strong>помогите мне пожалуйста по-возможности</strong>, буду вам очень благодарна<strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Олена М-М. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Мне вещей ненадо... <strong>помогите по-возможности мыльное</strong>: шампунь, мыло туал., порошок <strong>и продуктов что Бог даст</strong>... пачечку майонеза, мне очень сильно хочется, может <strong>бублики или сухарики,</strong> мивины в пачках... <strong>кипятильник (05) потому что нечем даже воды греть... если будет возможность хоть гр 200 сала</strong>... Болела, грипп был, но вроде бы прошло...<strong> ещё 1 г. впереди</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Віра С. (Селезнівська ВК, Луганська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo; Получила от Вас весточку и очень рада была за такие лесные слова в мой адрес... Читала на всю комнату и женщины находящиеся рядом, наверное тоже были рады за меня или делали вид создавая эту радость. Люди особенно в этих местах лживы лукавы, лицемерны. Мало таких кто отличается порядочностью, доброжелательностью. Вы знаете я почемуто всегда считала что меня с моими качествами души, надо занести в красную книгу как редчайшие экземпляры нашей матушки землицы которые вымирают. Во мне живы многие жизненые принципы, рождёные при Сталине, воспитываемые на Ленинизме и т.д. Только вот <strong>с 35 лет всё у меня пошло кувырком</strong>. И почему? Зачем?.. <strong>Я думаю это Господь таким вот образом сохраняет меня, лишний раз напоминая мне, что я личность и не имею право так беспутно проводить жизнь, теряя себя и те дни, года отведёные мне на свете этом. И только любовью Божьей я всё ещё здесь</strong>... У меня есть квартира, но в ней ничего нет сильно долго нахожусь здесь... Я люблю жизнь и поэтому даже в моём положении могу радоваться солнцу, нарцисам, которые у нас в зоне начали распускаться... Я работаю хотя шить уже трудно остиехандроз, да и глаза не видя 1,75 очки бы мне... <strong>Очень хочется счастья, тепла, блага, поздно не поднятся уже. Вот теперь хоть Вы пишете, а то и писать некому. Одна я...</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Терновник</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ну наконец то ты пришла,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Весна! Веснуха вся в веснушках</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Такая кроткая вошла</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ты знаешь как тебя ждала</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Весна! Кричали птицы в клетке</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Начало жизни на земле</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Весна несёт с обой надежду</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">О вольном счастье, бытие</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Там далеко там за запреткой</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Где всё в весну не так как здесь</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Там женщину лелеют словно ветку</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Расцвевшую в кусту.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Не в том терновнике колючем</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А в бузине, в любви, в белу</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">От запаха благоухает ветка</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ведь на свободе не в плену.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">И всё же женщина как птица,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Что в клетке пятую весну</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В терновнике поёт о птице</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Которая поёт о ней в саду.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">И пусть так будет больно за утрату</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Тех дней прошедших не вернуть.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Воздастся Богом &ndash; как наградой</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Поющей не в терновнике</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">в саду</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Это мой стих. Написан он 1 марта 2008 года. С Пасхой Вас&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Галина Т. (Селезнівська ВК, Луганська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Огромное спасибо за ваше письмо. Получать письма в таких местах, большой праздник... Работы пока у нас нет, в прошлом месяце зарплата была аж 16 гривен. А надо и помыться и постираться... Может Господь смилуется и даст нам работу. Каждый день молюсь и прошу у Него Благословения... Верочка, я очень рада что у меня есть такая сестра во Христе, которая всегда найдёт тёплое, сердечное слово, поддержит. Спасибо что вы есть и да хранит вас Господь&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ольга К. (Селезнівська ВК, Луганська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>двое моих детей (совершеннолетняя дочь Карина и маленький сынишка Богдан, которому исполнилось 5 лет) живут с моей мамой ей скоро будет 80 лет</strong>... У дочери сейчас обострение (она в 14 лет переболела туберкулёзом), а сейчас весеннее истощение организма, и поэтому сильно болеет, а денег на питание и лекарство не хватает. <strong>Если у Вас есть такая возможность, перечислите на её имя деньги</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Є(вгенія) Т. (Селезнівська ВК, Луганська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>даст Бог в этом году буду дома</strong>... Очень устала хочу домой ну на всё Божья воля... <strong>Проблема с ногами и каждые два, три месяца надо пить альтрон</strong> и компликс и уколы, а всё дорого. Мне <strong>врач на Чернигове сказал если я не буду пить таблетки и принимать уколы то могу вскоре перейти на костыли</strong>. Не дай Бог. Если будет возможность у Вас <strong>вышлите мне пожайлуста л</strong><strong>і</strong><strong>к</strong><strong>і</strong>. Даст Бог это будет последнее лечение у меня здесь&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Світлана Ш. (Орджонікідзевський ВЦ, Дніпропетровська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... получила от Вас письмо, огромное спасибо... <strong>У меня УДО в октябре даст Бог пойду домой... есть ли возможность у Вас помочь мне с ликарством... и зубную пасту, шампунь, тетрадь нет листиков писать и не за что купить.</strong> Я бы Вам не писала если могла взять здесь. Пусть хранит Вас Господь всегда и везде и на всех ваших путях!&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Світлана Ш. (Орджонікідзевський ВЦ, Дніпропетровська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Я искренне благодарна Вам</strong> за ваше письмо, особенно <strong>за стихи, которые затронули мою зачерствевшую душу. </strong>Я как на исповеди расскажу Вам, что многое пережила в своей жизни. И от всей этой черноты я захлопнулась как устрица. Мне хочеться чего-то доброго, тёплого и простого человеческого общения. <strong>Я буду рада если в Вашем лице я найду моральную и духовную поддержку. Это как раз то, чего нам находящимся в заключении не хватает</strong>.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Мне уже 36 лет и радость в моей жизни одна мама и дочка, ради которых я живу&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; .&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Світлана Г. (Селезнівська ВК, Луганська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>я освободилас смалинким сыном... нам нужны срочно денги</strong> очен срочно. А у меня таких нету денег <strong>нам надо полторы штуки гр</strong>. если можно забирите нам пожалуста...<strong>иму надо зделат опирацыю</strong>... у нас никово нету. Помогите нам пожалуста. В начале в мае будит опирация и срочно нам потрибуются денги&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Наталія П. (звільнена з Чернігівської ВК)</em></span></p>',NULL,3408,5,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3873,'P','2013-07-06',1,NULL,'Rss',' Lumen Fidei – перша енцикліка Папи Франциска ','<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/aea724377c.jpg\" height=\"92\" width=\"150\" /></div>\n<p><em>Lumen Fidei</em> («<em>Світло віри</em>») – так називається  перша енцикліка Папи Франциска, підписана 29 червня 2013 року, яка  вийшла в п’ятницю, 5 липня. Звернена до єпископів, пресвітерів,  дияконів, богопосвячених осіб та мирян і поділена на чотири розділи,  вона, як зазначає сам Папа Франциск у 7 пункті, була майже закінчена  його попередником, і додає: «За це я йому дуже вдячний й у Христовому  братерстві приймаю цю цінну працю, роблячи свій вклад<em> </em>».</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1318:-lumen-fidei--persha-encyklika-papy-francyska-&catid=32:gk-svit-cat&Itemid=58',3415,NULL,1,1,26),
(3874,'P','2013-07-03',1,NULL,'Rss','Мирослав Маринович: \"Політичні лідери повинні захищати життя і чинити мир, а не розпалювати війну\"','<p><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_marynovych.jpg.jpg\" height=\"151\" width=\"112\" />Панахида  за упокій душ польських та українських жертв Волинської трагедії  1943-го, яку 27 червня очолив Блаженніший Патріарх УГКЦ&nbsp;<strong>Святослав Шевчук</strong> у варшавському храмі отців-василіян у присутності президента Польщі&nbsp;<strong>Броніслава Коморовського</strong> та інших високопосадовців цієї держави, відродила одну історичну  особливість, пов\'язану із греко-католицькими ієрархами. У минулому і  митрополит&nbsp;<strong>Адрей Шептицький</strong>, і патріарх&nbsp;<strong>Йосип Сліпий</strong> виступали на міжнародному рівні не лише як первоієрархи, а й як етнархи  - особи, які, крім Церкви, представляють іще й увесь народ. Цього разу  роль такого етнарха відіграв&nbsp;<strong>Патріарх Святослав</strong>.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1315:myroslav-marynovych-qpolitychni-lidery-povynni-zahyshchaty-zhyttja-i-chynyty-myr-a-ne-rozpaljuvaty-vinuq&catid=37:spilnota-cat&Itemid=49',3409,NULL,1,1,26),
(3875,'P','2013-07-04',1,NULL,'Note','Ліквідація Греко-Католицької Церкви в Україні в повоєнні роки','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4893,200,image.php.jpg\" alt=\"\" width=\"200\" height=\"137\" />Історіографія насильної ліквідації УГКЦ та життя у підпіллі у чотирьох частинах програми \"Машина часу\". </span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Серед трагічних сторінок української історії ХХ століття доля УГКЦ, її священників та вірних посідає особливе місце.</span></p>','<p><iframe src=\"http://www.youtube.com/embed/0JuNn59KgCA\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe></p>\r\n<p>&nbsp;</p>\r\n<p><iframe src=\"http://www.youtube.com/embed/MLlSENZ45ec\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe></p>\r\n<p>&nbsp;</p>\r\n<p><iframe src=\"http://www.youtube.com/embed/tIY20UYLITU\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe></p>\r\n<p>&nbsp;</p>\r\n<p><iframe src=\"http://www.youtube.com/embed/Dv0yYO9zvrI\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe></p>',NULL,3410,5,1,NULL,48),
(3876,'P','2013-07-04',1,NULL,'Rss','Блаженних Івана Павла ІІ та Івана ХХІІІ канонізують на початку грудня','<a href=\"http://vk.com/share.php?url=http%3A%2F%2Frisu.org.ua%2Fua%2Findex%2Fall_news%2Fworld_news%2F52836%2F\"> </a>\n<p><img style=\"margin: 10px; float: left;\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_52836_Ioan23.jpg\" alt=\"Іван ХХІІІ\" title=\"Іван ХХІІІ\" height=\"118\" width=\"158\" />Комісія  кардиналів та єпископів з Конгрегації у справах канонізації святих 2  липня розглянула справу ймовірного чудесного зцілення за заступництвом <a href=\"http://risu.org.ua/ua/index/all_news/catholics/vatikan/42124/\" target=\"_blank\">блаженного</a> Папи Івана Павла ІІ. Як інформує ватиканіст Андреа Торніеллі, ця ж  комісія має висловити своє рішення і у справі чуда за заступництвом  блаженного Папи Івана ХХІІІ. Також цілком можливо, що цих двох пап  римських канонізують під час однієї урочистості на початку грудня.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1316:blazhennyh-ivana-pavla-ii-ta-ivana-hhiii-kanonizujut-na-pochatku-grudnja&catid=27:gk-v-ukrajini-cat&Itemid=57',3411,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3877,'P','2013-07-04',1,NULL,'Rss','Спізнений месія (5 липня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4865,200,390884.jpg\" />В одній єврейській спільноті,  яка дуже старанно виконувала всі приписи закону, було оголошено, що в  урочисту суботню ніч прийде Месія. Мав розпочати Свою місію саме у їхній  спільноті.</p>\n<p>Зібралися всі.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1295:spizneny-mesija-5-lypnja&catid=1:mystecka-storinka-cat&Itemid=50',3412,NULL,1,1,26),
(3878,'P','2013-07-05',1,NULL,'Rss','«Через добрі справи лежить дорога до поглиблення віри», – о. Андрій Нагірняк','<p class=\"NoSpacing\"><em><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_o_nagirnyak.jpg.jpg\" height=\"117\" width=\"164\" />Отець Андрій Нагірняк, керівник Відділу соціальних питань Патріаршої курії, відповів на запитання Департаменту інформації УГКЦ.</em></p>\n<p class=\"NoSpacing\"><strong>– Отче Андрію, якось ви сказали, що соціальне служіння – це те, що може об’єднати різні конфесії України. Чому?</strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1317:lcherez-dobri-spravy-lezhyt-doroga-do-poglyblennja-viryr--o-andri-nagirnjak&catid=27:gk-v-ukrajini-cat&Itemid=57',3413,NULL,1,1,26),
(3879,'P','2013-07-05',1,NULL,'Rss','Чому кричимо (6 липня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4872,200,923010.jpg\" />Одного разу вчитель спитав своїх учнів:</p>\n<p>- Чому розгнівані люди кричать?</p>\n<p>- Кричать, бо втрачають терпіння, - відповів один з учнів.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1300:chomu-krychymo-6-lypnja&catid=1:mystecka-storinka-cat&Itemid=50',3414,NULL,1,1,26),
(3880,'P','2013-10-08',1,NULL,'Note','Аскольдів глас - збірник 2012','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В історію записана,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В молитвах заколисана.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Тарас колись намалював,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Як храм цей Київ прикрашав.</span></p>','<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-size: small;\"><strong>Цар Русі &ndash; Аскольд</strong></span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. Зої Ружин, муз. Олени Личкіної</strong></span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Зійшла на князя благодать...</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І князь Аскольд прийняв хрещення.</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Настав час вірі возсіять!</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Послав Господь благословення...</span></p>\r\n<p class=\"Style4\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"Style4\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І подвиг, й мученицький шлях...</span></p>\r\n<p class=\"Style4\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Безсмертному Аскольду - слава!</span></p>\r\n<p class=\"Style4\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З Христовим вченням у віках &ndash;</span></p>\r\n<p class=\"Style4\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Під Богом і Народ! Й Держава!</span></p>\r\n<p class=\"Style5\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"Style5\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Приспів:</em></span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Князь Аскольд нам сяє,</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Бога прославляє!</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Першим охрестився,</span></p>\r\n<p class=\"Style3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Світочем явився!</span></p>\r\n<p class=\"Style3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Цар Русі &ndash; Аскольд!</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Воскресла пам&rsquo;ять крізь віки,</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Молитви слово розквітає...</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Радіють сонце і зірки!</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нам Цар - Аскольд благо віщає!</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Княгиня Ольга возвела</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На честь Царя - Аскольда храми.</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Княгині Ользі честь, хвала!</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Під України небесами!</span></p>\r\n<p class=\"Style5\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"Style5\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Приспів:</em></span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Князь Аскольд нам сяє,</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Бога прославляє!</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Першим охрестився,</span></p>\r\n<p class=\"Style3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Світочем явився!</span></p>\r\n<p class=\"Style3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Цар Русі &ndash; Аскольд!</span></p>\r\n<p class=\"Style3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"Style3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\"><em>До князя Аскольда</em></strong></p>\r\n<p class=\"aCxSpFirst\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. Тетяни Озкан, муз. Катерини Кучерявої</strong></span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">1. Аскольде-князю, з висоти</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Поглянь на нашу Україну.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Земляче рідний, нам прости,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Що ненька наша на колінах.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Що ми її не підвели,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І підвести не маєм сили,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Що славу й честь її святу</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Зберегти ми не зуміли.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2. Навчи нас, князю, перш за все</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Служити рідному народу</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І як мету в життя нести</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Його добробут і свободу.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Молись до Господа за нас,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Щоб він, Всевишній, дав нам ласку,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У рідній нашій Батьківщині</span></p>\r\n<p class=\"aCxSpLast\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Омріяну створити казку.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>&nbsp;</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>&nbsp;</em></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\"><em>Нащадки Аскольда</em></strong></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. і муз. Олени Сенейко</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">1. А на нашій Україні був славетний княжий рід,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Князь Аскольд їх прадід сивий, той що Русь хранив від бід,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Від Аскольдової слави свої корені несем</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Я про нього заспіваю, а на серці тихий щем:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приспів:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На Аскольдовій могилі ми згадаємо князів,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти гордись, моя Вкраїно, ти гордись, що мала їх,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На Аскольдовій могилі ми згадаємо тебе,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти &nbsp;дивись, Аскольде, сивий із небес.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2. А сини Аскольда славні у походах і піснях,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Переможно підіймали золотої волі стяг</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Я до тебе щиро серцем і душею пригорнусь,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мій Аскольде і моя Вкраїно-Русь.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приспів:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>&nbsp;</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>&nbsp;</em></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\"><em>Аскольдовий дзвін</em></strong></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. і муз. Ольги Лозинської </strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">1. Осяяна, омріяна,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Легендами оспівана</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольд колись Христа приймав,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;На вірність Богу присягав.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Опалена, зруйнована &ndash;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Поміж хрестів захована.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Княгиня Ольга зберегла</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І знову людям віддала.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церква Миколая &ndash;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Храм, що я кохаю.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Зустрічі, надії,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Віра в наші мрії!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;2. В історію записана,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В молитвах заколисана.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Тарас колись намалював,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Як храм цей Київ прикрашав.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Збудована, оновлена</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Молитвами вшанована.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Тебе зустріне там отець, -</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Гріхам покладемо кінець.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span><em style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Приспів:</em></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\"><em>Пісня про Аскольда</em></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. Христини Шонь, муз. Марка Дайніна</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Ніби лицар крізь віки,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">О Аскольде, проходиш &nbsp;ти.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший мученик християн Русі,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Що освятив землі ці.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">О Аскольде, князю всіх киян,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">що добробут їхній пильнував.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти збережений в пам&rsquo;яті своїх синів</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Як той, хто спокій зберегти зумів.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти хрещення прийняв святе,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">хоч землі ці поганським богам</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">поклонялись щодень.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Та Аскольде ти &ndash; правду людям відкрив.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Підступно вбитий із рук поган</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти святу кров за віру пролляв</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Наслідувати ми будем тебе</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І про Христа розкажемо людям.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\"><em>Аскольд і Дір</em></strong></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Сл. Олександра Олеся, муз. Віктора Соломіна</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Славна в Рюрика дружина,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Всім далась вона взнаки,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А Аскольд і Дір &mdash; найперші,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Найславніші вояки.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нудно їм сидіти дома</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Без походів, без боїв,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І вони прохають князя,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Щоб на волю їх пустив.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Князь подумав і дозволив.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Попрощалися вони</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І з такими ж вояками</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Посідали на човни.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І по Волхову угору</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В невідомий край летять.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мов орлів широкі крила,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Їхні весла лопотять.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приплили до краю Волхов,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Далі витягли човни</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І в лісах шляхів шукають</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">До чужої сторони.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Бачать &mdash; річка. Довго нею</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Геть на полудень плили,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аж угледіли на горах</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Місто, вежі і вали.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приплили, на берег, вийшли</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І питають у полян,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хто живе у цьому місті,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хто тепер у ньому пан?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Тут колись жили та вмерли</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Кий, Хорив та Щек, брати.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А тепер ми під хозаром,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Животій та дань плати&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Не журіться!&raquo; &mdash; Дір промовив.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;За мечі!&raquo; &mdash; Аскольд гукнув.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Кожний птахом стрепенувся,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Кожний гнівом спалахнув.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Налякалися хозари</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І в степи біжатъ гуртом.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І дружина здобуває</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пишний Київ над Дніпром.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Стали вільними поляни,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вихваляють вояків,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А Аскольда разом з Діром</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вибирають на князів.</span></p>',NULL,3662,5,1,NULL,54);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3881,'P','2013-07-06',1,NULL,'Rss','Блаженніший Святослав: «Ми засудили чотири ідеології, які вчать ненавидіти людей іншої національності»','<p><em><img style=\"margin: 10px; float: right;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_shevchuk_mixalik.jpg.jpg\" height=\"130\" width=\"192\" /></em>Як ми вже повідомляли, днями у Варшаві між Українською Греко-Католицькою Церквою та Римо-Католицькою Церквою Польщі<strong> </strong><a href=\"http://www.ugcc.org.ua/news_single.0.html?&amp;tx_ttnews%5btt_news%5d=11590&amp;cHash=65e537bfd4ebe0fa05949fd0ccce99b6\">було підписано спільну декларацію</a> примирення і прощення, яка стала знаковою подією в житті українського й польського народів. Про візит до Польщі, <a href=\"http://www.ugcc.org.ua/2768.0.html\">Декларацію примирення і прощення</a>,  про святкування 1025-річчя Хрещення України-Русі й значення віри в  житті сучасної людини розповість читачам «Галичини» в ексклюзивному  інтерв’ю Глава УГКЦ Блаженніший Святослав.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1319:blazhennishy-svjatoslav-lmy-zasudyly-chotyry-ideologiji-jaki-vchat-nenavydity-ljude-inshoji-nacionalnostir&catid=27:gk-v-ukrajini-cat&Itemid=57',3416,NULL,1,1,26),
(3882,'P','2013-07-06',1,NULL,'Rss','Небо за бали (7 липня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4872,200,divine_mercy.jpg\" />Одна добра християнка опинилась перед дверима до Неба. Була стривожена.  Святий Петро ласкаво прийняв її, але мовив: «Щоби ввійти до Раю, треба  набрати сто балів».</p>\n<p>Жінка почала перелічувати: «Я була вірною  дружиною ціле життя. Намагалась виховати по-християнськи своїх дітей. Не  вповні вдалося це мені, але я зробила все, що змогла. Упродовж 22 років  була катехиткою. Працювала на користь місії, допомагала благодійній  організації. Завжди намагалась добре ставитись до всіх, з ким мала  справу - особливо до священика і моїх сусідів...».</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1301:nebo-za-baly-7-lypnja&catid=1:mystecka-storinka-cat&Itemid=50',3417,NULL,1,1,26),
(3883,'P','2013-07-07',1,NULL,'Rss','Запитання (8 липня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4872,200,love-lamp.jpg\" />Знаний філософ щодня намагався збагнути сенс життя. Присвятив  розгадуванню цієї загад­ки найкращі літа свого життя. Радився зі  славет­ними мудрецями людства, та не знайшов жодної задовільної  відповіді.</p>\n<p>Одного вечора відпочивав у своєму саду. Узяв на руки п\'ятирічну донечку, яка весело гралася, і спитав її:</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1302:zapytannja-8-lypnja&catid=1:mystecka-storinka-cat&Itemid=50',3418,NULL,1,1,26),
(3884,'P','2013-07-08',1,NULL,'Rss','«Приклад Івана Павла ІІ вчить, що криву історії можна зігнути»','<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/d418ae54a5.jpg\" height=\"100\" width=\"150\" /></div>\n<p>Надзвичайні досягнення блаженного Папи Івана Павла ІІ  показують, що такого поняття, як «історична неминучість» не існує.  Ситуація не мусить бути такою, якою вона є, - криву історії можна  зігнути в більш людському напрямку. Таку думку висловив автор  бестселерів про Папу, всесвітньовідомий філософ Джордж Вайґель на  міжнародному симпозіумі «Церква у ХХІ столітті: виклики служіння у  глобалізованому світі», що відбувся 5 липня в Українському католицькому  університеті. Про це повідомляє прес-служба УКУ.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1320:lpryklad-ivana-pavla-ii-vchyt-shcho-kryvu-istoriji-mozhna-zignutyr&catid=32:gk-svit-cat&Itemid=58',3419,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3885,'P','2013-07-07',1,NULL,'Rss','Празники св. Івана Хрестителя','<p class=\"text\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4902,200,johnthebaptist.jpg\" />«Істинно кажу вам, що між народженими від жінок не було більшого від Івана Хрестителя» (Мат. 11, 11)</p>\n<p class=\"text\">Між  різними Святими, що їх почитає Східна Церква особливим культом  славиться св. Іван Хреститель, Господній Предтеча. Він єдиний з усіх  Святих після Божої Матері, що має свій празник Зачаття і празник Різдва.  Про його великий культ у Східній Церкві найкраще свідчать аж шість  празників у його честь на протязі церковного року: Празник Зачаття,  Різдва, Усікновення, Перше і Друге Знайдення Голови, Третє Знайдення  Голови і Собор після празника Богоявлення. — Тож погляньмо на його особу  й на його культ у Східній Церкві.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1321:praznyky-sv-ivana-hrestytelja&catid=35:istorychni-postati-cat&Itemid=48',3420,NULL,1,1,26),
(3886,'P','2013-07-12',1,NULL,'Rss','Фініш (13 липня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4873,200,Vorota.jpg\" />Один чоловік по смерті добувся до Бога. Господь прийняв його, радо усміхаючись, та водночас уважно дивився поза нього.</p>\n<p>Усмішка Бога стала сумною. Запитав чоловіка:</p>\n<p>«Але … ти прийшов сам ? ..»</p>\n<p><strong>Чоловік, його кінь і його пес ішли дорогою, коли в них влучила блискавка. Не знали, що ступили на останню стежину свого життя.</strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1307:finish-13-lypnja&catid=1:mystecka-storinka-cat&Itemid=50',3427,NULL,1,1,26),
(3887,'P','2013-07-08',1,NULL,'Rss','Каплички, якими вони бувають та чим відрізняються від церков і храмів','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4900,200,tserkov-chasovnya-boimov-chasovnya-boimov-lvov-ukraina.jpg\" />Кожний із нас неодноразово бачив ці маленькі церкви  без вівтаря, чув слова – капелла та капличка і напевне задумувався над  тим чому люди будують їх, ці невеликі та схожі на церкви будівлі, у чому  відмінність капличок від церков, а головне чи справді вони є такими ж  енергетично сильними місцями як храми та церкви освячені патріархатом,  мощами святих та чудодійними іконами?В цій статті ми  намагатимемось дати Вам відповіді на ці запитання. Сподіваємось, що наша  розповідь про каплички сподобається і буде корисною для Вас.</p>\n<p>Отже  – Капличка. Згідно енциклопедії – це невелика християнська будівля без  спеціального приміщення для вівтаря. А ось церква та сам народ здатні  розповісти нам про ці будівлі значно більше.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1322:kaplychky-jakymy-vony-buvajut-ta-chym-vidriznjajutsja-vid-cerkov-i-hramiv&catid=30:hrystyjanske-vchennia-cat&Itemid=37',3421,NULL,1,1,26),
(3888,'P','2013-07-08',1,NULL,'Rss','Дуже просто (9 липня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4872,200,son.jpg\" />Один чоловік не міг спати, бо діймав його якийсь жах. Щойно лягав,  здавалося йому, що якесь страховисько ворушиться під ліжком. Просиджував  ніч, прислухаючись, паралізований страхом.Лікар спробував промовити до  його здорового глузду. Приписав сильне снодійне. Проте жах не полишав  чоловіка.</p>\n<p>«Нині вночі якесь чудовисько, спрагле крові, ховалося під моїм ліжком!»</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1303:duzhe-prosto-9-lypnja&catid=1:mystecka-storinka-cat&Itemid=50',3422,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3889,'P','2013-07-09',1,NULL,'Rss','Цариця (10 липня)','<p class=\"uiStreamMessage userContentWrapper\" data-ft=\"{&quot;type&quot;:1,&quot;tn&quot;:&quot;K&quot;}\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4873,200,DSCN9421.JPG\" />Коли  помер цар, молодий принц, дещо непокоячись, готувався зайняти його  місце. Мудрий старий вихователь сказав юнакові: «Тобі потрібна допомога.  Перш ніж вступити на престол, обери собі майбутню дружину, але вважай:  це має бути дівчина, котрій можеш повністю довіряти. Запроси всіх  дівчат, які прагнуть стати царицею. Я поясню тобі, як знайти гідну».</p>\n<p class=\"uiStreamMessage userContentWrapper\" data-ft=\"{&quot;type&quot;:1,&quot;tn&quot;:&quot;K&quot;}\">Наймолодша з посудомийок на царській кухні була таємно закохана у принца. Вирішила теж зголоситися.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1304:carycja-10-lypnja&catid=1:mystecka-storinka-cat&Itemid=50',3423,NULL,1,1,26),
(3890,'P','2013-07-10',1,NULL,'Rss','Непотрібний шнурочок (11 липня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4873,200,319452.jpg\" />Був собі колись бавовняний шнурочок, який почувався непотрібним. «Я  надто слабкий, щоб бути шнуром, – скаржився. – Я недостатньо гарний, щоб  мене використати для створення гобелена чи навіть для гаптування. Ах!  Якби я був золотою ниткою…, оздобив би одяг архієпископа. Я невдаха!  Нікому я не потрібен. Нікому не подобаюсь, навіть самому собі!»</p>\n<p>Зіщулившись на кріслі, слухав сумну музику і постійно почувався самотнім.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1305:nepotribny-shnurochok-11-lypnja&catid=1:mystecka-storinka-cat&Itemid=50',3424,NULL,1,1,26),
(3891,'P','2013-07-11',1,NULL,'Rss','Дзеркало (12 липня)','<div class=\"itemIntroText\">\n<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4873,200,14799.jpg\" />На мальовничому екзотичному гамірному  базарі в одному місті на Сході заможна американська туристка побачила  дивовижне дзеркало у срібній оправі.</p>\n<p>Однак його ціна була високою, надто високою. Коли вони торгувалися,  продавець сказав, що дзеркало має чарівну силу - у цілому світі такого  немає.</p>\n</div>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1306:dzerkalo-12-lypnja&catid=1:mystecka-storinka-cat&Itemid=50',3425,NULL,1,1,26),
(3892,'P','2013-07-11',1,NULL,'Rss','  Празник св. Апостолів Петра і Павла ','<p class=\"text\"><img style=\"margin: 10px; float: left;\" src=\"http://www.credo-ua.org/wp-content/uploads/2012/06/29.06_%D0%BF%D0%B5%D1%82%D1%80%D0%B0%D0%BF%D0%B0%D0%B2%D0%BB%D0%B01.jpg\" title=\"Святі Апостоли Петро і Павло\" height=\"116\" width=\"194\" />Мабуть, неможливо говорити про день свята Петра й Павла без екуменічних алюзій.</p>\n<p class=\"text\">Чим є більшість проблем, розколів, труднощів у християнстві? Сварками,  обумовленими історико-культурними та особистісними моментами. До цього  додаються доктринальні проблеми, відмова у покорі й «застигання» таких  становищ, подібне до контрактур на переламах. Хірурги знають: коли  зламані кості неправильно зростаються, їх потрібно знову ламати, щоби  скласти правильно… Отож і процес розламів та примирень у Церкві зовсім  не є солодким.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1323:-praznyk-sv-apostoliv-petra-i-pavla-&catid=35:istorychni-postati-cat&Itemid=48',3426,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3893,'P','2013-07-13',1,NULL,'Rss','Історія світу (14 липня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4873,200,1330350606.jpg\" />Жила колись Мишка, яка мріяла стати царицею звірів. Наказала зробити  для неї трон із хліба та корону з шоколадної обгортки, а тоді оголосила:  «Я – цариця звірів! Чотириногі, двоногі, крилаті і повзучі – всі маєте  мене слухати, поважати і приносити подарунки у день народження». Звістка  швидко розійшлася, бо звірі полюбляють пліткувати. Першими прилетіли  Бджоли, які хутко вклонилися цариці, принісши трохи меду, і незадоволено  гуділи: «Маємо так багато роботи, ще цього нам бракувало! Однак ми  виконали наказ, і нас зауважили!..»</p>\n<p>Приходили також инші звірі, яким нічого було робити, і вшановували царицю.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1308:istorija-svitu-14-lypnja&catid=1:mystecka-storinka-cat&Itemid=50',3428,NULL,1,1,26),
(3894,'P','2013-07-13',1,NULL,'Rss','Суспільне питання в енцикліці Папи Франциска Lumen Fidei ','<p><img style=\"margin: 10px; float: left;\" src=\"http://media01.radiovaticana.va/imm/1_0_710158.JPG\" />«Якщо  усунемо віру в Бога з наших міст, між нами ослабне довіра, нас  об’єднуватиме лише страх, а стабільність буде під загрозою», – читаємо в  55-му пункті першої енцикліки Папи Франциска <em>Lumen Fidei</em> (Світло  віри). За його словами, віра, прийнята та поглиблена в сім’ї, через  досвід любові Небесного Отця, здатна просвітлювати всі суспільні  стосунки, відкриваючи нам шлях до взаємного братерства.</p>\n<p>Святіший  Отець зазначає, що центром біблійної віри є Божа любов і Його дбання про  кожну людину, і коли цей критерій притемнюється, людина втрачає своє  місце у Всесвіті, губиться серед природи, відмовляючись від моральної  відповідальності або претендуючи на роль абсолютного судді, приписуючи  собі право необмежених маніпуляцій.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1324:suspilne-pytannja-v-encyklici-papy-francyska-lumen-fidei-&catid=32:gk-svit-cat&Itemid=58',3429,NULL,1,1,26),
(3895,'P','2013-07-14',1,NULL,'Rss',' У Зарваниці Блаженніший Святослав застеріг молодь перед вченням гендерної філософії ','<p><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/2663a53f23.jpg\" height=\"100\" width=\"150\" />13 липня Блаженніший Святослав, Глава і Отець УГКЦ, на  Всеукраїнській і молодіжній прощі у Зарваниці зустрівся із молоддю.  Перед багатотисячною аудиторією Предстоятель УГКЦ презентував аудіо-диск  «100 відповідей на запитання молоді», у якому навчав про основи  духовного життя, суть подружжя, подружню любов, зміст людського тіла та  людської статевості. Першоієрарх окремо говорив про небезпеки гендерної  філософії та ідеології.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1325:-u-zarvanyci-blazhennishy-svjatoslav-zasterig-molod-pered-vchennjam-gendernoji-filosofiji-&catid=27:gk-v-ukrajini-cat&Itemid=57',3430,NULL,1,1,26),
(3896,'P','2013-07-14',1,NULL,'Rss',' Сумна казка (15 липня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4873,200,300.jpg\" />Перший Посланець, який зійшов на землю, перебував на ній недовго. Щойно  люди почули початок його Послання, почали кричати і , кидати каміння,  примусили його піти.</p>\n<p>Проте потім замислилися й відчули страх. Щоби задобрити Небо, звели колону на ринку.</p>\n<p>Небо поставилося з розумінням і замість покарати послало на землю  другого Посланця. Його Послання було палке, але жорстоке, безжальне.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1309:-sumna-kazka-15-lypnja&catid=1:mystecka-storinka-cat&Itemid=50',3431,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3897,'P','2013-07-15',1,NULL,'Note',' Проща до Зарваниці завершилася: десятки тисяч вірних ішли пішки та молилися під дощем ','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4915,200,pz_08.JPG\" alt=\"\" width=\"200\" height=\"133\" />Блаженнійший Святослав привітав усіх гостей та прочан, які, незважаючи на сильний дощ, молилися на Літургії, уділив архипастирське благословення, подякував організаторам за наполегливу працю, священикам, які з вірними ішли пішки та сповідали під час прощі, представникам влади з допомогу в організації паломництва.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,4915,200,14-07-13-2.jpg\" alt=\"\" width=\"176\" height=\"200\" />&laquo;Якщо Бог є світлом, то світлом життя є віра в Бога&raquo; - п</strong>ро це сказав Глава УГКЦ у ніч проти 14 липня після багатотисячного походу вірних зі свічками до монумента Матері Божої у місці її об\'явлення. &laquo;За допомогою віри ми єднаємося із живим та вічним Богом, ми ним просвічуємося, стаємо його світлом, носіями Божественного проміння&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Першоієрарх закликав свідчити свою віру вчинками, а відтак піднести вгору свічники: &laquo;Світімо нашою вірою, не блукаймо в темряві, просвічуймо сьогодні наші родини, нашу Батьківщину - усіх тих людей, які є поруч&raquo;. Опісля попросив кожного придивитися до своєї свічки та пригадати тих людей, від яких одержали віру в Бога. &laquo;Пригляньмося добре до наших світильників, бо сьогодні є наша відповідальність, щоб, боронь Боже, в наших руках, у нашому житті це світло віри не загасло. Пригляньмося добре до того світла, яке повинні носити в собі&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Оскільки цього року проща до Зарваниці присвячена Року віри та збіглася із 1025-річчям Хрещення України-Русі, Блаженніший говорив про важливість події Хрещення, як часу просвічення нашого народу. &laquo;Будьте віруючими, будьте християнами в тому часі, в якому Господь Бог передав нам той найбільший скарб нашого народу. Бо саме християнська віра була цією внутрішньою життєвою силою нашого народу понад 1000 років&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Глава УГКЦ закликав передати світло своєї віри дітям, друзям та ближнім, &laquo;щоб ця естафета мала продовження&raquo;. &laquo;Саме нас Господь посилає, щоби просвітили їхню темряву...&nbsp;щоб &ldquo;народ, який сидів у темряві&rdquo;, побачив світло саме через нашу Церкву&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Похід зі свічками розпочався біля парафіяльного храму Зарваниці, де перебуває ікона Матері Божої Зарваницької. Після Літургії, яку очолив владика Дмитро (Григорак), єпарх Бучацький, вірні центральною вулицею села несли свічки та в супроводі оркестру співали пісню &laquo;Із Києва міста&raquo;. Відтак на чудотворному місці вдарили дзвони, а прочани наблизилися до монумента, де Верховний Архиєпископ спільно з єпископами та священиками розпочав молебень до Матері Божої.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/MXy38GxDNRo\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4915,200,pz_08.JPG\" alt=\"\" width=\"200\" height=\"133\" />14 липня Архиєрейською Божественною Літургією, яку очолив Глава УГКЦ Блаженніший Святослав (Шевчук), та освяченням пам\'ятника Івану-Павлу ІІ, Папі Римському, завершилася Всеукраїнська та молодіжна проща. У Богослужінні взяли участь вірні з різних куточків України та з-за кордону, 10 архиєреїв УГКЦ, близько 300 священиків та представники влади.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Значна частина вірних ішла до Зарваниці пішки. Найбільша група, близько 1500 осіб, традиційно здійснила пішу прощу від Архикатедрального собору Тернополя. Найбільше часу у дорозі провели прочани з Хмельницького вікаріату: до Зарваниці вони йшли майже тиждень. Ішли здебільшого молоді люди, інші - приїхали на автобусах, автомобілях та велосипедах.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Блаженнійший Святослав привітав усіх гостей та прочан, які, незважаючи на сильний дощ, молилися на Літургії, уділив архипастирське благословення, подякував організаторам за наполегливу працю, священикам, які з вірними ішли пішки та сповідали під час прощі, представникам влади з допомогу в організації паломництва.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Цьогоріч проща була приурочена 1025-ій річниці Хрещення Русі-України та Року віри, яким в УГКЦ проголосили 2013 рік. Саме ці тематики заторкнув Блаженніший Святослав у своїй проповіді.<br /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Після Літургії відбулася Панахида за жертв українсько-польського протистояння на Волині 1943 року та освячення пам\'ятника Івану-Павлу ІІ, Папі Римському, який споруджений поблизу купелі св. Анни.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Високопреосвященний архиєпископ і митрополит Тернопілсько-Зборівський Василій (Семенюк), як намісник Марійського духовного центру у Зарваниці, подякува Главі УГКЦ за візит та від подарував ікону Матері Божої Зарваницької.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/Eurx30tCF5Q#at=830\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><a href=\"http://www.tze.org.ua/reportazhi/1050-%D0%BF%D1%80%D0%BE%D1%89%D0%B0-%D0%B4%D0%BE-%D0%B7%D0%B0%D1%80%D0%B2%D0%B0%D0%BD%D0%B8%D1%86%D1%96-%D0%B7%D0%B0%D0%B2%D0%B5%D1%80%D1%88%D0%B8%D0%BB%D0%B0%D1%81%D1%8F-%D0%B4%D0%B5%D1%81%D1%8F%D1%82%D0%BA%D0%B8-%D1%82%D0%B8%D1%81%D1%8F%D1%87-%D0%B2%D1%96%D1%80%D0%BD%D0%B8%D1%85-%D1%96%D1%88%D0%BB%D0%B8-%D0%BF%D1%96%D1%88%D0%BA%D0%B8-%D1%82%D0%B0-%D0%BC%D0%BE%D0%BB%D0%B8%D0%BB%D0%B8%D1%81%D1%8F-%D0%BF%D1%96%D0%B4-%D0%B4%D0%BE%D1%89%D0%B5%D0%BC.html\" target=\"_blank\">фоторепортаж</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><a href=\"http://www.tze.org.ua/reportazhi/1050-%D0%BF%D1%80%D0%BE%D1%89%D0%B0-%D0%B4%D0%BE-%D0%B7%D0%B0%D1%80%D0%B2%D0%B0%D0%BD%D0%B8%D1%86%D1%96-%D0%B7%D0%B0%D0%B2%D0%B5%D1%80%D1%88%D0%B8%D0%BB%D0%B0%D1%81%D1%8F-%D0%B4%D0%B5%D1%81%D1%8F%D1%82%D0%BA%D0%B8-%D1%82%D0%B8%D1%81%D1%8F%D1%87-%D0%B2%D1%96%D1%80%D0%BD%D0%B8%D1%85-%D1%96%D1%88%D0%BB%D0%B8-%D0%BF%D1%96%D1%88%D0%BA%D0%B8-%D1%82%D0%B0-%D0%BC%D0%BE%D0%BB%D0%B8%D0%BB%D0%B8%D1%81%D1%8F-%D0%BF%D1%96%D0%B4-%D0%B4%D0%BE%D1%89%D0%B5%D0%BC.html\" target=\"_blank\">Прес-служба архиєпархії</a></span></p>',NULL,3432,5,1,NULL,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3898,'P','2013-07-15',1,NULL,'Note','День пам’яті київського князя Аскольда','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4916,200,2-askold.jpg\" alt=\"\" width=\"198\" height=\"190\" />У &laquo;круглому столі&raquo;, присвяченому Дню пам&rsquo;яті київського князя Аскольда, державного діяча, що сприяв поширенню християнства в Русі-Україні, візьмуть участь науковці, церковні та громадські діячі. Зокрема, планується участь представників від Інституту історії України НАН України, Національного університету ім. Т.Г.Шевченка, Національного педагогічного університету ім. М.Драгоманова, Ужгородської української богословської академії ім. свв. Кирила і Мефодія УПЦ, Київської православної богословської академії УПЦ-КП, Товариства па&rsquo;мяті князя Оскольда тощо.</span></p>','<div class=\"entry\">\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4916,200,2-askold.jpg\" alt=\"\" width=\"198\" height=\"190\" />Київська &ldquo;Просвіта&rdquo; спільно з &laquo;Товариством па&rsquo;мяті князя Оскольда&raquo; започатковує проект, присвячений Дню пам&rsquo;яті київського князя Аскольда. Перше засідання відбудеться у День його пам&rsquo;яті 15 липня о 15:00 в Національній парламентській бібліотеці України (вул. Набережно-Хрещатицька, 1).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У &laquo;круглому столі&raquo;, присвяченому Дню пам&rsquo;яті київського князя Аскольда, державного діяча, що сприяв поширенню християнства в Русі-Україні, візьмуть участь науковці, церковні та громадські діячі. Зокрема, планується участь представників від Інституту історії України НАН України, Національного університету ім. Т.Г.Шевченка, Національного педагогічного університету ім. М.Драгоманова, Ужгородської української богословської академії ім. свв. Кирила і Мефодія УПЦ, Київської православної богословської академії УПЦ-КП, Товариства па&rsquo;мяті князя Оскольда тощо.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Наразі ця тема особливо актуальна в перед час офіційного святкування 1025-річниці Хрещення Руси-Україні, оскільки Володимирове Хрещення сталося можливим в тому числі і завдяки діяльності його попередника &ndash; київського князя-християнина Аскольда.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Серед організаторів круглого столу Національна парламентська бібліотека України, Київська &laquo;Просвіта&raquo;, Ужгородська українська богословська академія ім. свв. Кирила і Мефодія УПЦ та Товариство па&rsquo;мяті князя Оскольда.</span></p>\r\n</div>',NULL,3433,5,1,NULL,5),
(3899,'P','2013-07-15',1,NULL,'Rss','Свято Ризопокладання у Влахерні та пам’ять про перше Оскольдове Хрещення Русі','<p>&nbsp;<img style=\"margin: 10px; float: left;\" src=\"http://www.religion.in.ua/uploads/posts/2011-07/thumbs/1310923068_.0.jpg\" alt=\"Свято Ризопокладання у Влахерні та пам’ять про перше Оскольдове Хрещення Русі\" title=\"Свято Ризопокладання у Влахерні та пам’ять про перше Оскольдове Хрещення Русі\" height=\"131\" width=\"198\" />15 липня – свято Ризопокладання у Влахерні та пам\'ять про перше Оскольдове Хрещення Русі</p>\n<p>Ведучи мову про початок християнізації Русі, неможливо не зупинитися   більш детально на Чуді з Чесною Ризою пресвятої Богородиці у Влахерні   під час облоги Константинополя у 860 році русичами на чолі з князем   Оскольдом.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1327:svjato-ryzopokladannja-u-vlaherni-ta-pamjat-pro-pershe-oskoldove-hreshchennja-rusi&catid=28:tema-nomera-cat&Itemid=2',3434,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3900,'P','2013-07-15',1,NULL,'Rss','Усмішка (16 липня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4917,200,4ScbbTChdgE.jpg\" />Жила колись усмішка, яка мандрувала світом. Це була щира, радісна,  приязна усмішка. Була щаслива так, як може бути щаслива усмішка, і час  від часу наспівувала.</p>\n<p>Одного дня прийшла у містечко, у якому рух був особливо напружений,  мешканці також не знали спокою. Усмішка чемно стояла біля переходу,  чекаючи на зелене світло, коли раптом дві машини зіткнулися. Різко  загальмували біля краю хідника.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1328:usmishka-16-lypnja&catid=1:mystecka-storinka-cat&Itemid=50',3435,NULL,1,1,26),
(3901,'P','2013-07-16',0,NULL,'Note','Звіт №8 про НАДХОДЖЕННЯ  ЛИСТІВ 30.05.2013 р. — 36 листів','<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4919,200,398733.jpg\" alt=\"\" width=\"200\" height=\"91\" /></span></strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... </span><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Знаходжусь в місцях позбавлення волі вже п&rsquo;ятий рік. На волі ростуть двоє діток. Моя старша донька йде в цьому році в перший клас їй шість років. Я вас прошу будь ласка допоможіть моїй свекрусі зібрати мою доньку до школи. Можливо: зошити, альбоми для малювання, що зможите&rdquo;</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Листи від ув\'язнених </span></em></strong><strong><em><span style=\"text-decoration: underline;\">&nbsp;(з них вперше звернулись 5 осіб)</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Чернігівська виправна колонія ЧВК-44 (м.Чернігів) &mdash; 13 лист</strong><strong>ів</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Кача</strong><strong>нівська виправна колонія </strong><strong>К</strong><strong>ВК-</strong><strong>5</strong><strong>4 (м.</strong><strong>Харк</strong><strong>ів) &mdash; </strong><strong>4</strong><strong> лист</strong><strong>и</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Збаразька виправна колонія ЗВК-63 (Тернопільська обл.) &ndash; 1 лист</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Селезнівська виправна колонія СВК-143 (Луганська обл.) </strong><strong>&mdash;</strong><strong> 10 листів</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Орджонікідзевський виправний центр ОВЦ-79 (Дніпропетр. обл.) </strong><strong>&mdash;</strong><strong> 1 лист</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">&nbsp;</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Подяка за посилки для дітей &ndash;6 листів</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">&nbsp;</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Листи звільнених з ув</span></em></strong><strong><em><span style=\"text-decoration: underline;\">&rsquo;</span></em></strong><strong><em><span style=\"text-decoration: underline;\">язнення &ndash; 1 лист</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Знаходжусь в місцях позбавлення волі вже п</strong><strong>&rsquo;</strong><strong>ятий рік. На волі ростуть двоє діток. Моя старша донька йде в цьому році в перший клас їй шість років.</strong> Я вас прошу будь ласка <strong>допоможіть моїй свекрусі зібрати мою доньку до школи. Можливо: зошити, альбоми для малювання, що зможите</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Олена П. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Спасиба большое опекуну что зделала поездку моей дочери ко мне. Я очень ей благодарна за свою дочь. <strong>Спасиба Вам большое за посылочку, которою Вы выслали моей дочери </strong>на день рождения. <strong>Очень меня просила когда я буду освобождатся что-бы, её звозит к Вам в церковь</strong>. Я люблю и горжусь что у меня такая дочь. Вот она написала Вам тоже небольшое письмо</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;<em>Добрий день Св&rsquo;ятий о.Ігор <strong>дякую вам за ваші гостинці які Ви мені прислали на день народження Я зараз перебуваю з мамою на побачені Бог почув мої молитви щоб я побачила маму і це збулось</strong>. Св&rsquo;ятий о.Ігор дякую що ви допомагаєте таким дітям як я. <strong>Я вічна буда вдячна вам</strong></em>&raquo;&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Оксана Б. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Невозможно на бумаге описать</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Всё, что хочется мне Вам, родные, пожелать...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Это чудо, что мне Бог вас подарил,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Щедро вами Он меня благословил.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Каждый день благодарю Его за вас,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; И как никогда, молюсь сейчас,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; И прошу благословений у Отца,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чтобы всем вам дал в сердца!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Мир, спокойствие с избытком,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Веры крепкой и любви открытой,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Новых достижений и побед,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Духа бодрости на целый век.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ярким светом ослепляя тьму,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чтобы каждый день для вас был лучшим,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чтоб никто не смог ваш мир нарушить,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Милосердия для вас прошу я,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Благодати Божьей каждый миг,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; И ещё Отца молю я,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чтобы каждый многого достиг!!!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Приближается Воскресение Христово, с этим Великим Праздником всем своим сердцем и всей душой я спешу вас поздравить дорогие и благословенные отец Игорь, и все, все братья и сёстры в Господе! Пусть с приближением этого Чудесного Дня прибавится у вас и ваших близких и родных сил, здоровья, новых побед и достижений... И в каждом дне вашей жизни пусть сопровождают вас Божья любовь и милость!.. Благодарю Господа за вас и всякое духовное общение; за всех, кто молится о нас, кто заботиться и не даёт человеку чувствовать себя одиноким! Слава Господу за всех, кто работает на Божьей ниве!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Я высылаю для вас Стих-пожелание. А также, прошу Вас по возможности помочь лекарствами, которые в выписанном рецепте высылаю и в инструкции. Понимая, что для каждого, это &ndash; благословение &ndash; благословлять нуждающихся, я молю Бога, чтобы у вас была возможность благословлять других и самим жить в достатке и избытке...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Немного о себе. Занимаюсь сейчас вышивкой картины, которую буду потом отсылать на конкурс, который состоится 14 августа в г.Киеве. Тема: &laquo;Господне Преображение&raquo;</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">... Завжди у Тебе є для мене час,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Завжди мене чекаєш кожну мить,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Завжди: учора, зараз, завтра, кожен раз</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мене благаєш Ти: &laquo;Дитино, зупинись&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Твоя любов завжди все покриває,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вона ніколи не перестає,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вона свого ніколи не шукає,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вона себе до краплі віддає.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти дав мені усе, що я просила,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Но все це мені щастя не дало,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сьогодні, Батько, моє серце зрозуміло &ndash;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Просити треба було тільки ту любов...&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Алла Б. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>освобождаюсь 28.03.14 г.</strong> <strong>У меня случилась беда, моя дочь попала в аварию, и только благодаря Господу осталась жива!</strong> Её прооперировали, нашлись добрые люди, помогли деньгами. <strong>Она одна, воспитывает дочь. Внучку звать Ангелина (6 лет).</strong> У меня нет возможности помочь материально. Ежедневно прошу Господа Бога, Отца Небесного, чтоб не оставлял их, чтоб оберегал от бед. А по воскресеньям хожу в церковь, я верю, что Он слышит меня, и благодарю Его за Любовь и Тепло... Господи! Вы не представляете, что творится в душе. <strong>Прошу, пред Вами приклоняясь помочь собрать ребёнка в школу, может какими-то продуктами питания, одеждой.</strong><strong>..</strong><strong> пожалуйста по возможности, чем сможете, помогите!</strong><strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Олена Ю. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Я поздравляю Вас с Великим праздником Пасхой!</strong> И желаю Вам всех Божьих благословений!.. <strong>Я благодарю нашего Господа за Вас и за таких людей, как все вы, за то, что Вы не забываете тех, кто в узах и проявляете сочувствие и сострадание</strong>... Я всю свою оставшуюся жизнь хочу посвятить служению Господу. А пока <strong>здесь в колонии я рассказываю окружающим о Христе. Господь меня сильно изменил, я не курю, не ругаюсь матом, хожу на служения и на вечернюю молитву</strong>. Работаю я сейчас на матрасах. Зарплата маленькая 20 гр., но зато не такая тяжёлая, как грузчик. Благодарю Бога за всё, что имею... <strong>Наша колония, как маленький городок.</strong> Мы по улице ходим в столовую, на работу и проверки у нас на улице. <strong>Вот только всё окружено каменным забором и колючей проволокой. </strong>Сейчас я пишу письмо в молитвенной комнате Греко-Католической церкви. У нас в колонии много деревьев, клумбы с цветами. Я живу в 3<sup>х</sup>этажном здании типа барака. В 22<sup>00</sup> отбой, а в 6<sup>00</sup> утра подъём, потом зарядка, завтрак и на работу. <strong>Нахожусь я здесь 3 года и 2 месяца, а осталось 2 года и 4 мес. </strong>Я думаю, что Господь меня не оставит, ведь <strong>мне то и освобождаться некуда, все умерли, мама, сын, а отчим женился, но я верю, что Бог всё устроит</strong>. Ведь уповающий на Него не постыдится&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ірина С. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Если есть у Вас возможность можете на лицевой счет положить 200 гр, тут есть магазин чтобы купить Диане конфет, и соков, если нет такой возможности, то вышлите посылкой сладостей</strong>. Если Вы будете высылать посылку, и в посылку будете ложить вещи, то сильно плохие вещи не ложите нам их в ДМР не отдают жалко просто что они будут валяться на складе&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Світлана Л. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Спасибо Вам большое за вашу помощь</strong> (<em>вислали ліки</em>), ваше внимание, что Вы не оставляете без внимания просьбы нуждающих... Пускай Господь Бог хранит Вас&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Наталія К. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Болею</strong>... я уже тысячу раз пожалела, что не сделала осенью операцию во Львове, как мне <strong>предлагали (удалить желчный)...</strong> <strong>Жуткие боли каждый день, хожу на сан.часть пить таблетки обезбаливающие по 3-4-5 раз в день. </strong>Ходила к начальнику сан.части, чтобы вывезли в Чернигов прооперировали, но она говорит, только будет приступ (т.е.если я потеряю сознание), а так у них нет денег... <strong>Молю Бога, чтоб хоть не умереть здесь, чтобы увидеть мамочку и детей. Надеюсь, что Бог поможет.</strong> Дома тоже проблемы. <strong>Старший сын шёл с футбола, на него напали, хотели отнять мобильный телефон, он не отдал, его избили, сломали челюсть. Делали 3 операции </strong>в нашей Днепропетровской областной больнице. <strong>Мама заняла большие деньги на операции и лечение</strong>, как отдавать не знаем. <strong>У меня в июне УДО. Начальник отряда написала на меня рапорт, т.е.нарушение, чтобы не отпускать меня.</strong> В общем о.Игорь у меня в данный момент очень чёрная и тяжёлая полоса. И когда всё это кончится &ndash; я не знаю. Я понимаю с одной стороны, что без воли Божией ничего не бывает, а сдругой стороны... Вы меня извените, что нагрузила на вас свои проблемы. <strong>Если можно, вышлите пожалуйста детям посылку им очень не легко</strong>. Да благословит вас Господь за ваше доброе сердце. Молюсь о вас всегда&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Валерія О. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>сижу год, срок 4 года</strong>. Родом с г.Ильичевска, обл.Одесская. <strong>18.05.87 г. рождения.</strong> Мне 26 лет. <strong>На свободе одна мама, живет на пенсию. У меня есть маленький ребенок, который находится с мамой</strong>. Зовут доченьку в честь святой девы Марии, Машенька. Я очень люблю свою Марию. Очень корю себя что нахожусь не рядом. Я росла в нормальной семье, но мне не хватало материнской заботы, любви... Только сейчас я начала задумываться, что надо что-то менять в своей жизни... Работаю на швейной фабрике, заработка не хватает даже на самое необходимое... <strong>Мне хотелось бы сделать какие-то подарки для дочечьки, если у Вас есть возможность то хотелось бы 3-ох колесный велосипед, ей уже 2 года 1 месяц, она родилась 10 марта 2011 г</strong><strong>.</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ольга С. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Получила помощь от вас &ndash; огромное спасибо за благославление. Хочу поделиться с вами всеми радостной вестью &ndash; 6 мая я родила сына</strong>. Слава Господу Богу нашему, всё прошло благополучно. Планировала назвать сына Давидом, но, т.к. 6 мая день Георгия, я решила так и назвать своего сынулю. Мама тоже одобрила это имя. Слава Богу, <strong>после долгих месяцев молчания мама решила откликнуться на мои просьбы простить меня</strong> и мы сней спокойно поговорили по телефону. В общем, <strong>мир, любовь, спокойствие и понимание наконец-то победили боль, злость и разочарования. Ещё раз огромное вам спасибо за всё что вы делали и делаете для меня и моей семьи</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ірина С. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Христос Воскрес! Христос Воскрес!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Всі радіймо щиро Нині,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Бог дав радість Всій Родині,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Прийшла радість із Небес</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Христос Воскрес!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Дорогий шановний о. Ігор! Вітаю Вас із святом вербною неділею,вітаю Вас з Великим Святом, Великим Днем Воскресінням Христовим.</strong> Ісус, син Божий пережив такі страшні муки, переніс страшенні страждання заради нас людей, щоб ми нарешті усвідомили свої вчинки, поступки, щоб зупинилися, покаялись і більше ніколи не грішили. Такою дорогою ціною, своїм життям, заплатив син Божий заради нашого спасіння, заради спасіння душам нашим. І щоб ми прийшли до Нього, втихомирили свою гординю, прийшли з низько опущеною головою і просили, молячи навколішках у Нього прощення за свої гріхи, бо знаємо, що немає такої провини, яку би не простив нам Господь Бог наш, тільки покаятись щиро від усього серця! Він, єдинородний син Батька Свого, переживши такі людські знущання, помер і Воскрес! Хвала Йому і Слава Йому! Радіймо люди, бо Він Спаситель душам нашим! <strong>Христос Воскрес! Веселих Вам свят!&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ірина В. (Качанівська ВК, м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Шановний отец Ігор мені потрібно тетрациклінова глазна мазь</strong> і каплі тіотриазолін <strong>в мене болять і сльозяться очі а в нас в колонії нема таких ліків</strong>. Я дуже прошу <strong>якщо можна то вишліть мені бо я неможу вязати.</strong> Я буду дуже вдячна&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Тетяна С. (Качанівська ВК, м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... вишлить пожалуйста по возможности капронові колготи, летний шарф, пасту, мило, щетку, ращоску, ручку, шампунь и по возможности какието туфли летние... у меня размер ноги 36... О Ігор я вот что думала <strong>после моего освобождения Вы меня с моей доцей возмите к себе в церков я хочу и после освобождения быть с Богом, служить Богу и узнавать Бога дальше и чтоб моя доця тоже знала о Боге и росла в кругу верующих людей... </strong>Я уже для себя решила быть всегда с Богом и служить только Ему, и помогать деткам которые в интернатах в детдомах&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Олена К. (Качанівська ВК, м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>дуже вдячн</strong><strong>і Вам всім хто з вами рядом наш низький уклін за вашого дорогого листа, це велика радість для нас</strong>. Все робим, щоб було добре, щоб вийти раніше додому бо <strong>сильно важко тут...</strong> Моя бабуля Юзепа ходить на телевізор, а я читаю книги, Біблію, молитви переписую хороші за нашого Господа... Моя бабуля Юзепа впала, стало плохо закрутилась голова, синяки набила аж чорні плохо неможе... Да <strong>дякуємо Господу Богу, що Він нас некинув, а дав Вас, що Ви за нас піклуєтесь...</strong> Ми ходим у хустках, а бабуля боліє.... Вітаємо Вас всіх з Великоднем бажаємо довгіх років і ніколи нехворіти... може зможете пасти для ручок бо я багато пишу собі із газет, журналів, з книг<strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Юзепа К. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... я с 9 апреля <strong>вышла на работу в огород бесплатно, что б заработать поощрение, чтоб раньше выйти с этих стен</strong>. У нас только воскресенье выходной день, работаем с семи утра до пяти вечера, тягаем и по два ведра воды, и камни и землю очень устаём... Но я очень рада, что меня взяли на работу, хоть и бесплатно. <strong>Во время работы можешь хоть немножко уходить в себя и забывать, где находишься</strong>. Сейчас уже вся зона в тюльпанах и нарциссах. Насадили много других цветов, рассады, цветут черешни, сливы, груши, вишни. Вообщем всё бы хорошо, живы-здоровы, <strong>кормят нас нормально. </strong>Но тут в бригаде одна третья часть люди цыганской национальности, очень лукавые люди, которые сидят за мошенничество, за продажу наркотиков. Начались у нас конфликты, цыгане хотят взять верх... Спасибо, что Господь даёт смирения, терпения, чтоб не унывать и не падать духом... я вообще всегда благодарю Господа, что я ещё жива, без лекарств (ВІЛ-інфікована), <strong>нам даже самые элементарные гипопротекторы как силибор не дают</strong>... На днях думаю начну читать сначала Библию по порядку, начиная с Бытия. Это я буду уже третий раз в своей жизни читать Библию. Хоть <strong>мне уже скоро будет 43 года,</strong> до этого срока я Её никогда не читала. Спасибо Богу что Он руками людей Святым Духом написал такую книжку, на все случаи жизни... Извените меня, что написала письмо такое, обсуждение своих ближних Господь не приемлет, но я их сразу же и прощаю ближних своих, потому что и Господь прощает нас грешников за наши грехи&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Яна К. (Селезнівська ВК, Луганська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Уважаемый Отец Игорь, прошу не откажите в благом деле, <strong>не для себя прошу, для матери</strong>. Она старенькая пенсионерка, <strong>болеет сахарным диабетом и</strong> коленные суставы не дают ей покоя, очень трудно и больно ходить (<strong>остеохондроз</strong>). Пенсии на лекарства не хватает... В больнице ей предлагают более дешевые, которые ей не подходят... сахар в крови плохо падает. А ещё <strong>с моей мамой остался мой сын Дениска 15 лет. Им очень трудно,</strong> помочь с этих мест не могу... Помогите, <strong>если есть такая возможность дайте адрес матери в газету, может люди добрые помогут ей на лекарства, или медикаментами, либо кто знает народные средства </strong>кто болеет такими же болезнями. <strong>Верю есть ещё люди добрые.</strong> Верю что и Вы Отец Игорь не останетесь равнодушным к людям просящим помощи у Вас. Вот адрес мамы:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Колинько Тамара Викторовна</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">ул.Караваева 3/20&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; г.Днепропетровск&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Индекс 49064&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Анжела К. (Селезнівська ВК, Луганська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Сейчас весна, много работы по уборке территории, на посадку рассады овощей, ремонты, побелка... <strong>хочу выразить свою огромную благодарность</strong> за Вашу померную помощь, <strong>за то, что Вы не забыли меня грешную</strong>. Спасиба за стихи я их часто перечитываю. Вера я Вас очень прошу <strong>не оставляйте меня без своего внимания</strong> <strong>пишите мне. Ведь я совсем одна без родных и близких мне людей. А с Вами я понемного обретаю душевное спокойствие</strong> и Вы помогаете мне преодолевать все житейские трудности. А их здесь хватает&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Тетяна Т. (Селезнівська ВК, Луганська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Хотела попросить у Вас выслать мне чтоб можно было связать носочки, рукавички, пряжи на это, или вещи, которые можно было бы распустить и сделать пряжу.</strong> Буду очень надеяться и ждать от Вас помощи&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ірина К. (Селезнівська ВК, Луганська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Я </strong><strong>писала Вам что собираюсь поехать на аперацию, но пока отлаживаю, по тому что просила Вас о помощи и вы мне писали что нужно немного подождать</strong>. Я смогу побыть ещё недели три. А потом прийдётца ехать. И <strong>чтобы не случилось, я верю в то, что вы за меня молитесь</strong><strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Марина Д. (Селезнівська ВК, Луганська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Я получила Вашу по</strong><strong>сылку, сказать правду, росплакалась. Я очень Вам благодарна и всем людям которые протянули руку помощи</strong>... <strong>31.01 мне зделали апирацию, всё прошло успешно &ndash; Слава Богу</strong>. После операции всё отправили в онко лабораторию, жду анализы. <strong>Чувствую себя неплохо, думаю что с Вашими и своими молитвами мы всё приодолеим.</strong> Отец Игорь <strong>огромное спасибо за Библию, </strong>я хоть и маленький, но хороший ученик. Хочу Вам росказать небольшой секрет. Ведь <strong>у меня мой прадедушка священик, он был епископ, служил в Черниговской и Киевской области</strong>, потом они с матушкой уехали в Кривой Рог, он служил там в православной церкви. Мне было 7 лет. Прадед умер в 93 года. Помню его похорон, очень много было священиков и служителей... У нас есть отец Григорий православная церковь, он мне сказал ежай на операцию, и нечиго небойся мы здесь будем молиться т.е.на земле, а прадед с Богом с облоков. Вы знаете, он мне придал множество сил, я знала что со мной Бог и люди которым я небезразлична. <strong>Очень хочеться увидеть Вас, поблагодарить воочую, сейчас такая благодать в душе, и знаю что я не сама, у меня возрождается новая семья</strong>. Дорогой отец Игорь я Вас попрошу, <strong>неоставляйте меня, пишите мне письма</strong>. На следующей неделе нач.санчасти напишет рецепт, с Божей помощью <strong>если у Вас будут средства помогите лекарствами</strong><strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Людмила Н. (Селезнівська ВК, Луганська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Начальник санчасти прочитал мою медицинскую карту, выписку с Львовского МОБа, и вот какая проблема, <strong>у меня гипатит &laquo;С&raquo;</strong> роспад печени. Я <strong>посылаю Вам рецепт, если в Ваших силах, купите лекарства</strong>, <strong>осталось сидеть 1 год</strong>, но окупировали кучи болячек, и самое основное печень. <strong>Я слёзно вас прошу помогите, в 36 лет я поняла что жить хочеться ещё больше</strong>. Мне настолько неудобно перед вами, но обратиться больше неккому, буду пытаться написать письмо &laquo;Спасите наши души&raquo;, но пока нет конверта. Ходила в церковь, помолилась, просила у Господа здоровья родным, прощения грехов, после молитвы роскаялась, я будто-бы родилась на свет, настолько легче стало. <strong>Если сможете вышлите ладанку на грудь, с Матерью и Иисусом</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Людмила Н. (Селезнівська ВК, Луганська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Сама я с Донецка. Вот уже год и восемь как нахожусь в местах лишения свободы, а срок четыре года.</strong> В последнее время я не нахожу себе места. Произошло чего я не ожидала. Ещё на тюрьме я встретила человека которого всю свою жизнь искала и у нас завязались очень серьёзные отношения... <strong>Приехав на лагерь я узнала о том, что я вич инфецирована и что болезнь моя у меня уже лет пять</strong>. Вот теперь голова идёт кругом, от того как сказать человеку который мне небезразличен и которому небезразлична и я. Но сказать я знаю всё равно нужно. О таких вещах молчать нельзя. А сейчас <strong>я молюсь ко святому целителю и великомученику Пантелеймону и прошу его о своём исцелении. И очень хочу попросить и вас помолиться ему тоже о моём исцелении. </strong>Пожалуйста... <strong>В моей жизни никого уже не осталось мама с папою умерли, сестра умерла</strong>. Остался у меня один брат, но что-то в последние пол года нидаёт о себе ни каких известий... если есть такая возможность, <strong>помогите пожалуйста с мылом, порошком, шампунью, зубною пастою, прокладками,туалетною бумагою, станками</strong>. А если есть возможность выслать <strong>чая, сахара, мёда, сухого молока и какие либо жиры</strong> буду очень вам признательна и <strong>кипятильник на 0,5</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Олена Ф. (Селезнівська ВК, Луганська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>мне </strong><strong>31 год. Срок у меня очень большой 11 лет</strong>. Не хочу Вас обманывать и лукавить, <strong>к Господу я ещё не пришла, тоесть не приняла покаяния</strong>. <strong>Библию читаю, хожу на собрание когда к нам приезжают, а вот покаяться ну никак.</strong> А почему, я сама незнаю. Вот <strong>я бы хотела переписываться с верующим человеком, чтобы он меня просвещал в духовном мире. </strong>Потому что<strong> здесь в этих стенах без Бога очень тяжело</strong>... хочу обратиться к Вам за помощью. Здесь самое главное это предметы личной гигиены, что-нибудь из продуктов, конверты &ndash; так как я <strong>буду писать Касационную жалобу</strong>, что-нибудь из обуви 37-38 р. и кипятильник. И ещё если можно <strong>вышлите пожалуйста Библию</strong>, у меня есть, но она библиотекарьская, а хочется иметь свою&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Марина Д. (Селезнівська ВК, Луганська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>получила от вас посылку за которую очинь спасиба. Пусть вас Бог благословыть за вашу любовь и ласку и заботу за людей которие нуждаюца вашой помощи.</strong> Немного прывыкла здесь но сиравно на лагыри краче там хочь намалинькие расходы мы отоварувалысь а здесь этого нет, счас я в город на работу выёжаю работа немного грязнувата зато на свежом воздухе здесь вязать научилась здесь мастирська и мы тоже ходим на работу и нас учат вязать. Спасибо за молитвы мне очинь понравились. <strong>У меня в октябре начинаюца льготы </strong>может и я вже домой пойду... <strong>в миня з очкамы проблема стёкла выпадают </strong>потому что они треснули, бо я на работу нашу. Но вы уже прислали посылку и мне стыдно у вас просит я собиралась вам письмо написать но вы попередылы мене. Потому что з конвертами проблема. <strong>Размер два з половиною или &ndash; 3</strong>... Постравляю з прошетшымы празникамы, и спаской Христовой&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Людмила В. (Орджонікідзевський ВЦ, Дніпропетровська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;<strong>Большое Вам Человеческое спасибо за то, что есть такие Люди как В</strong><strong>ы</strong><strong>! Да хранит Вас Бог!</strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; С ув.семья О. &rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Валентина О. (м.Запоріжжя, Владислав-12р., Вероніка-4р., діти Вікторії Г., Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Получено всё по списку. Спасибо</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Тамара К. (м.Запоріжжя, Настя-13р., Олег-4р., діти Ірини С., Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Мир Вашему дому! Ув.Игорь Онишкевич и Церковна громада.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Получили Вашу посылку за которую большое спасибо. Дети очень обрадывались</strong> и передали Вам большое спасибо. А Дианка сказала что пускай этих людей Бог охраняет и спасибо за всё, что вы делаете.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">С ув.семья К.&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Вячеслав К. (Київ. обл., Олександра-6р., Діана-8р., діти Інни К., Чернігів. ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Шановні добродії!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Христос Воскрес!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Отримала Вашу посилку, від усього серця дякую вам.</strong> Хай вам у всьому щастить і Бог допомагає Вам у всіх всіх добрих справах. Низкий Вам уклін.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З повагою Світлана Л.&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Світлана Л. (Дніпропетровська обл., Євген-11р., син Алли Б., Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;<strong>Спасибо вам огромное о.Ігор Онишкевич!</strong><strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Леонід С. (м.Херсон, школа-інтернат.,13р., син Наталії Н., Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;<strong>хочу </strong><strong>воспользоваться случаем и поздравить Вас отец Игорь и Ваших помощников со Светлым Воскресением Христовым! Христос Воскрес! Очень Вам благодарна за Вашу заботу, внимание и помощь, огромное Вам человеческое спасибо, низкий Вам поклон</strong>. Вещи все очень хорошие, а главное какраз на Вику, ей 29 марта исполнилось 11 лет и в этом году она переходит в 6 класс. Спасибо за школьные пренадлежности и сладости, да вообще за всё спасибо, <strong>нашей радости нет придела ведь у нас денег нет даже на хлеб.</strong> 31 декабря умерла наша бабушка и мы остались одни, <strong>на Вику получаю 364 гривны, группу не продлили (нужно платить 1500 гр., а у меня их нету), на работу устроится не могу </strong>(с моей астиой и гипертонией) никто не берёт да и противопоказаний очень много, ещё и аллергия. Вобщем проблем куча, а здесь такой подарок, <strong>сам Господь усмотрел в Вашем лице протянуть нам руку помощи</strong>... Очень бы хотелось побывать у Вас и повидаться с Вами, надеюсь когда-нибудь Господь и это усмотрит. А пока мы радуемся Вашим подаркам, благодарим Бога за Его любовь и милость, за все Его благословение, и благодарим от всего сердца. <strong>Спасибо, что не забываете нас и не бросаете в тяжкую минуту. Пусть Господь благословит Вас и воздаёт за Вашу доброту и заботу</strong><strong>&rdquo;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Тетяна С. (звільнена з Дніпродзержинської ВК)</em></span></p>',NULL,3436,5,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3902,'P','2013-08-01',0,NULL,'Note','Восьма десятиденна Міжнародна піша Проща родин мігрантів із Самбора до Зарваниці','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4920,200,190.jpg\" alt=\"\" width=\"200\" height=\"119\" />Десять днів сотні-тисячі людей йдуть з молитвою за возз\'єднання українських родин, за успішну українську державу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сім попередніх Прощ провадив отець Василь Поточняк, який багато років свого служіння віддав заробітчанам. У нинішньому часі через найприкріші, які тільки можуть бути обставини, духовне &laquo;дитя&raquo; отця Василя буде без самого отця Василя.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4920,200,190.jpg\" alt=\"\" width=\"200\" height=\"119\" />Десять днів сотні-тисячі людей йдуть з молитвою за возз\'єднання українських родин, за успішну українську державу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сім попередніх Прощ провадив отець Василь Поточняк, який багато років свого служіння віддав заробітчанам. У нинішньому часі через найприкріші, які тільки можуть бути обставини, духовне &laquo;дитя&raquo; отця Василя буде без самого отця Василя.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На замовлення Пасторально-місійного відділу Української Греко-Католицької Церкви відзнято фільм про шосту Прощу \"Благослови, Господи, путь, що по ній піду\"</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Цьогоріч із 2 до 11 серпня 2013 року Пасторально-місійний відділ Української Греко-Католицької Церкви увосьме організовує десятиденну Міжнародну пішу Прощу мігрантів і їх родин від Самбора до Зарваниці загальною відстанню 250 км.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Україна як держава заробітчанам майже не допомагає. Україна, за Конституцією правова, демократична і соціальна держава &laquo;розписалася&raquo; у своїй непрофпридатності як тільки її громадянин задумався про заробітки. Прикро вголос визнавати, але інші держави набагато більше допомагають нашим співвітчизникам, бо Україна, яка неспроможна була своїм громадянам зарадити вдома, не дуже прагне опікуватися ними і надалі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">За такої ситуації про людей мусить дбати Церква, бо, як і колись, і нині людей рятує Віра. Віра в Бога на чужині &ndash; то велике щастя! У такому вже є певна традиція в українців. Ще наші прадіди, із першої хвилі, приїжджали до Гамерики з порожніми кишенями, але з вірою в Бога. Вони будували церкви направду швидше, аніж свої власні хати. Нинішні наші українці талановиті не менше. І враження на чужині їх переповнюють, може, ще більше. Навіть Шевченка переспівують: Не називаю її раєм... тої Італії!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У нинішньому часі на офіційному церковному рівні Синодом УГКЦ прийнято окреме Звернення щодо ситуації українських трудових емігрантів. Засідаючи у Новояворівську 30 червня &ndash; 1 липня 2009 року, церковні ієрархи мусили визнати, що незважаючи на масштабність феномену трудової еміграції з України він не отримав належної уваги з боку політичних кіл, а значить, і державних структур. &laquo;Попри декларативну зацікавленість, і то дуже рідкісну, фак&shy;тично жодна із політичних сил не запропонувала цілісного підходу до вирішення проблем та конкретних дієвих кроків його реалізації&raquo;, &mdash; прямо говориться там.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">УГКЦ не випадково опікується мігрантами. Саме її вірні в тяжкий період історії Церкви і врятували саму Церкву. Бо не було би їх на теренах світових, куди радянська влада аж ніяк не могла дотягнутися, &ndash; не було би і єпископів таких, які мали силу волі заявити всьому світові про митрополита Йосифа Сліпого на Другому Ватиканському Соборі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сама Церква постановила визначити першу неділю після Різдва (Пам&rsquo;ять святих і праведних Йосифа Обручника, Давида царя і Якова, по плоті брата Господнього) Днем мігранта в Українській Греко-Католицькій Церкві.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Законодавчим&raquo; органом Церкви утворено Пасторально-місійний відділ задля представлення Главі УГКЦ потреби вірних у тих країнах, де немає відповідних церковних структур. До таких країн віднесено, певно, півсвіту. Ще раніше у 2006 році Синод єпископів визначив стратегію розвитку нашої Церкви, яка сформульована як &laquo;Святість об&rsquo;єднано&shy;го Божого люду&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Як пояснює Блаженніший Святослав, &laquo;святість об&rsquo;єднаного Божого люду є стратегією УГКЦ&raquo;, оскільки сьогодні наша Церква направду є поширена по цілому світі&raquo;. У греко-католицьких семінаріях запроваджено викладання загальної місіології та місіології Української Греко-Католицької Церкви. А семінаристи-старшокурсники мають зустрічі із практикуючими місіонерами. У Церкві готують до душпастирської діяльності за кордоном тих отців і монахів, а також і мирян, які зголошуються присвятити себе такому служінню.</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Виконавчим секретарем Пасторально-місійного відділу УГКЦ призначено священика Василя Поточняка. Взагалі-то, о.Василь заробітчанам віддав 15 років свого жертовного священичого служіння. Мав особисту місію від Блаженнішого Любомира відшукувати українців в Італії, засновувати українські церковні громади в різних італійських містах, налагоджувати та розвивати церковно-громадське життя серед українців Італії.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Діяльність Пасторально-місійного відділу зосереджена не тільки за кордоном, але також в Україні. Для українських заробітчан, котрі повернулися додому, в Україні створюються громадські організації, проводяться щомісячні зустрічі родин мігрантів, упродовж шести років відбуваються 10-денні піші прощі із Самбора до Марійського відпустового центру в Зарваниці. Бо Церква є насамперед Церквою, а тому первісним залишається проповідування Слова Божого. В тому числі і через молитву. Як каже Блаженніший Любомир, &laquo;молитва має велику силу. Молитися не за таку чи таку розв&rsquo;язку. Не мета молитви &mdash; радити Богові, як воно буде краще. Не думаю, що Бог потребує радників. У своїй молитві ми мусимо поручити Богові свою ситуацію, &mdash; він бачить вихід із нашого глухого кута краще за нас. Він уміє розв&rsquo;язувати будь-що. Це надзвичайно важливо &mdash; свідомо залишити вибір Богові!&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церква щодня молиться за тих, хто виїхав з України в пошуках &laquo;хліба насущного&raquo;. Складовою системної роботи УГКЦ щодо душпастирської опіки мігрантів є Прощі у намірах сімей заробітчан та їх родин, які зорганізовує від мо&shy;менту створення Пасторально-місійний відділ Церкви.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Проща &mdash; це насамперед одна із форм прославлення Бога живого! Це своя дорога кожного до Бога, наша дорога Його слідами, де ми Спасителю співчуваємо, каємося щиро, постійно перебуваємо в контакті з Богом! Це не тільки пізнавальна екскурсія й розважальний похід! Насамперед, Проща &ndash; то є духовна праця християнина! Тому Проща як особлива форма душпастирської опіки і &laquo;прижилася&raquo; в заробітчан.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Проща &laquo;Самбір-Зарваниця&raquo; &mdash; це не разова акція в рік для її паломників, а скоріше всього ціль життя. Багато є в Ук&shy;раїні благословенних місць, а заробітчани собі облюбували й вибрали Самбір і Зарваницю. Між іншим, нині Блаженний Папа Іван Павло ІІ, відвідуючи десять років тому Україну, свою апостольську подорож до нашої країни поручив Зарваницькій Богоматері, і попросив у неї заступництва для &laquo;усіх християн, усіх чоловіків і жінок доброї волі, які живуть у цій великій Країні&raquo;!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церква таку Прощу організовує вже увосьме. А 24 липня 2005 року, як пригадують самі мігранти, то були їхні перші кроки в Україні. Тоді в Дрогобичі вони зорганізували першу свою зустріч з дітьми, на яку з&rsquo;їхалися з усієї України. Далі була перша піша Проща від Самбора до Зарваниці. &laquo;Це були не просто піші десять днів, це було десять днів родинно&shy;сті!&raquo;, &mdash; пригадує пані Оксана. Організаторами тої Прощі була Церковна громада УГКЦ м. Болоньї (Італія) та Карітас Самбірсько-Дрогобицької Єпархії УГКЦ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">7 липня 2006 року о 7 годині ранку в храмі Різдва Пресвятої Богородиці м.Самбора розпочалася Свята Літургія за па&shy;ломників Самбір-Зарваниця та родини заробітчан. Літургію відслужили о.Василь Поточняк &mdash; духовний опікун українців греко-католиків м.Болоньї (Італія), о.Ігор Козанкевич &ndash; духовний опікун &laquo;Карітасу&raquo; Самбірсько-Дрогоби&shy;цької єпархії, о.Богдан Добрянський &ndash; декан Храму Різдва Пресвятої Богородиці м.Самбора. З Самбора о 9.30 вийшло 24 паломники, а до Зарваниці прийшло 65 прочан. У 2007 році проща із Самбора до Зарваниці мала 260 км. Із Самбора вирушили 82 паломники до Зарваниці прийшло 115 паломників. У 2008 році &mdash; 250 км із Самбора вирушило 90 паломників, до Зарваниці прийшло 125.</span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вихід з Самбора від храму Різдва Пресвятої Богородиці 2 серпня о 9.45 (поч. Літургії о 7.00) за маршрутом:</span></strong></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2.08-п&rsquo;ятниця (&asymp; 32 км): САМБІР, Луки (обід), Градівка (нічліг); </span></strong><br /><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> 3.08-субота (&asymp; 31 км): Великий Любінь (обід), Оброшино(нічліг);</span></strong><br /><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> 4.08-неділя (&asymp; 28 км): Оброшино (вихід о 7,00 зранку) &rarr;Львів, ц. св.. Юра (Св. Літургія о 11,00) (обід), Виннички (нічліг);</span></strong><br /><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> 5.08-понеділок (&asymp; 27 км): Гаї, Городиславичі (обід), Селиське (нічліг);</span></strong><br /><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> 6.08-вівторок (&asymp; 23 км): Коросне (обід), Унів (нічліг);</span></strong><br /><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> 7.08-середа (&asymp; 34 км): Липівці, Пленихів, Чемеринці (обід), Дунаїв, Рекшин (нічліг);</span></strong><br /><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> 8.08-четвер (&asymp; 33 км): Жуків (обід), Криве (нічліг);</span></strong><br /><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> 9.08-п&rsquo;ятниця (&asymp; 34 км): Новосілка (обід), Котузів, Зарваниця (нічліг);</span></strong><br /><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> 09-11.08- п&rsquo;ятниця, субота і неділя: ЗАРВАНИЦЯ</span></strong></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Орієнтовна щоденна програма паломництва: 7.30-8.30 &ndash; св. Літургія; 8.45 &ndash; сніданок біля церкви; 9.30 &ndash; вихід; 13.30 &ndash; молитва в храмі, обід, зустріч з вірними; 16.30 &ndash; вихід; 20.00 &ndash; молитва в храмі, вечеря, зустрічі з вірними, чування.</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З собою треба мати: молитовник, вервицю або чотки, плащ-накидку від дощу, спальний мішок або покривало, карімат, ліхтарик, змінне взуття та одяг, ложку, миску, горнятко, необхідні медикаменти, гроші на перебування в Зарваниці, повернення додому та незаплановані витрати.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Рекомендуємо йти в зручному (випробуваному) взутті. Дорогою заходитимемо до всіх храмів. Паломництво супроводжуватиме транспорт, де можна буде отримати першу медичну допомогу, здати багаж тощо. Нічліги будуть по родинах та школах. Харчування організовують парафіяни. Перебування в Зарваниці та повернення додому за власні кошти. Організаційний внесок для учнів та студентів 30 грн., для дорослих 75 грн.</span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Додаткова інформація на офіційному сайті <a href=\"http://palomnyk.at.ua\" target=\"_blank\">http://palomnyk.at.ua</a> та за тел.: о. Ігор Козанкевич &ndash; (03244) 2-24-58; п. Людмила &ndash; 097-761-87-30</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">P.S. Отцеві Ігореві можна телефонувати на мобільний 0673808098 (правда, з 1 серпня, бо його наразі нема в Україні)</span></p>',NULL,3437,5,1,NULL,47);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3903,'P','2013-08-04',0,NULL,'Note','Вечір пам’яті Лесі Українки','<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4921,200,99197_html_ed32b91.jpg\" alt=\"\" width=\"200\" height=\"134\" />4 серпня 2013 року Божого у церкві св. Миколая Мирлікійського на Аскольдовій Могилі, вул. Паркова Дорога 1, м. Київ, Україна (<a href=\"item,1532\">http://askoldova-mohyla.org/uk/item,1532</a>) о 17:00 проходитиме Вечір пам&rsquo;яті (сто років, з дня народження для неба) відомої української поетеси, письменниці, перекладача, культурного діяча Лесі Українки (1913-2013)</span></strong></p>','<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4921,200,99197_html_ed32b91.jpg\" alt=\"\" width=\"200\" height=\"134\" />4 серпня 2013 року Божого у церкві св. Миколая Мирлікійського на Аскольдовій Могилі, вул. Паркова Дорога 1, м. Київ, Україна (<a href=\"item,1532\">http://askoldova-mohyla.org/uk/item,1532</a>) о 17:00 проходитиме Вечір пам&rsquo;яті (сто років, з дня народження для неба) відомої української поетеси, письменниці, перекладача, культурного діяча Лесі Українки (1913-2013)</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вечір пам&rsquo;яті відбуватиметься за підтримки Київського музею Лесі Українки.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У заході братимуть участь викладачі, випускники та студенти НМАУ імені П.І.Чайковського</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У програмі мистецького заходу:</span></p>\r\n<ol style=\"text-align: justify;\" start=\"1\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В. Зубицький \"Рапсодія\". Виконує Станіслав Зубицький (флейта)</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">М. Лисенко, вірші Лесі Українки \"Смутнові провесни\". П. Гайдамака, вірші Лесі Українки \"Горить моє серце\", \"Стояла я і слухала весну\" Виконує Марина Дзюба (сопрано)</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Р.Шуман \"Арабески\" Виконує Наталія Мальцева (фортепіано)</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ф.Крейслер \"Прелюдія і алегро\" в стилі Г.Пуньяні, М.Скорик \"Мелодія\". Виконують Ніна Сіваченко (скрипка) та Тетяна Юшко (фортепіано)</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вечір провадитиме заступник директора Державного музею ім. Л.Українки Ірина Щукіна.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомога у перевезенні музичних інструментів А.Ю.Гінсар.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Запрошуємо київську спільноту долучитися до мистецько-просвітницького заходу і відкрити для себе Лесю (Лари́су Ко́сач-Кві́тку) по новому.</span></p>',NULL,3438,5,1,NULL,17),
(3904,'P','2013-07-16',1,NULL,'Rss','Проповідь архиєпископа Мечислава Мокшицького на 70 роковинах Волинської трагедії ','<p><img style=\"margin: 10px; float: left;\" alt=\"Мечислав Мокшицький\" src=\"http://www.credo-ua.org/wp-content/uploads/2011/10/mokpg.jpg\" height=\"182\" width=\"132\" /></p>\n<p><strong>Вступ</strong></p>\n<p>У грудні 1943 року був убитий о. Людвік Вродарчик, облат. Незважаючи на  перестороги, він не став ховатися, не втік, не залишив парафії в  Окопах, Луцької дієцезії, де він був настоятелем. Адже допомагав він  усім – полякам, українцям, євреям. Нічого злого не вчинив і не мав чого  таїти, але не хотів залишати Пресвятого Таїнства самого у своєму храмі.  Забрали його під час молитви й жорстоко катували. У місці, де його було  вбито, на хатній стіні в Карпилівці, залишилася чітка пляма крові, яку  неможливо змити чи зафарбувати. Світлина з того місця була передана на  вступний беатифікаційний процес, що розпочався за ініціативи єпископів  Римсько-Католицької Церкви в Україні.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1336:propovid-arhyjepyskopa-mechyslava-mokshyckogo-na-70-rokovynah-volynskoji-tragediji-&catid=27:gk-v-ukrajini-cat&Itemid=57',3439,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3905,'P','2013-07-16',1,NULL,'Rss','Якщо б я знав раніше (17 липня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4917,200,270221208.jpg\" />Одного дня Бог, зайшов до Раю і, на Своє велике здивування, побачив, що  там ж всі. Жодну душу не відіслано до пекла. Трохи Його це занепокоїло.  Адже має обов’язок пильнувати справедливість. Навіщо ж тоді пекло, якщо  його не використовувати?  Отож мовив до Архангела Гаврила: «Поклич усіх  перед Мій престол і прочитай десять заповідей». Покликали всіх. Гавриїл прочитав першу заповідь. Тоді Бог сказав: «Усі,  хто згрішив проти цієї заповіді, негайно мають піти до пекла». Кілька  людей вийшли з натовпу й засмучено пішли до пекла.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1329:jakshcho-b-ja-znav-ranishe-17-lypnja&catid=1:mystecka-storinka-cat&Itemid=50',3440,NULL,1,1,26),
(3906,'P','2013-07-17',1,NULL,'Rss','Дурний вухналь (18 липня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4917,200,128.jpg\" height=\"155\" width=\"159\" />Одному купцеві надзвичайно поталанило на торгах: він продав увесь свій  товар, а до порожніх саков поклав декілька злитків золота і срібла.</p>\n<p>Він, чоловік обачний, хотів якнайшвидше вернутися додому, тож не гаючись вирушив у дорогу.</p>\n<p>Міцно прив\'язав до сідла сакви, пришпорив коня і поскакав. Близько  полудня купець зробив зупинку в невеликому містечку. Стаєнний слуга,  який порав коня, зауважив небезпечну річ:\"Пане, в підкові на правій передній нозі твого коня нема одного вухналя!\"</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1330:durny-vuhnal-18-lypnja&catid=1:mystecka-storinka-cat&Itemid=50',3441,NULL,1,1,26),
(3907,'P','2013-07-17',1,NULL,'Note','Положення фестивалю-конкурсу духовної пісні «Аскольдів глас»','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мистецький захід носить екуменічний характер, покликаний популяризувати кращі зразки духовної, патріотичної, народно-обрядової музики, ознайомити широкий загал із історичними подіями минувшини, постаттю київського князя Аскольда (в хрещенні названого Миколаєм), залучити до участі співацькі колективи різних складів з усієї України та з-за її меж.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&rdquo;Аскольдів глас&rdquo;</strong> &ndash; фестиваль-конкурс духовної пісні, започаткований у 2007 р. за ініціативи о.‑митрата Ігоря Онишкевича та громади УГКЦ св.&nbsp;Миколая Чудотворця на Аскольдовій могилі. Конкурс проводиться в грудні напередодні свята св.&nbsp;Миколая, архиєпископа Мир Лікійських, чудотворця. Мистецький захід носить екуменічний характер, покликаний популяризувати кращі зразки духовної, патріотичної, народно-обрядової музики, ознайомити широкий загал із історичними подіями минувшини, постаттю київського князя Аскольда (в хрещенні названого Миколаєм), залучити до участі співацькі колективи різних складів з усієї України та з-за її меж.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><span style=\"text-decoration: underline;\">Учасники:</span></strong> до участі в <strong>X</strong><strong>ІІ фестивалі-конкурсі духовної пісні &rdquo;Аскольдів глас&rdquo; </strong>запрошуються аматорські вокальні та вокально-інструментальні ансамблі. Кількість учасників 5&ndash;9 осіб. Вік учасників 12&ndash;36 років.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><span style=\"text-decoration: underline;\">Термін проведення конкурсу:</span></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>X</strong><strong>ІІ фестиваль-конкурс духовної пісні &rdquo;Аскольдів глас&rdquo; </strong>пройде 21 грудня 2013 р. о 16:30&nbsp;у приміщенні Національного педагогічного університету ім.&nbsp;М.&nbsp;Драгоманова, за адресою вул. Пирогова, 9 (станція м. Університет).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><span style=\"text-decoration: underline;\">Умови конкурсу</span></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Концертний виступ колективу повинен включати обов&rsquo;язковий твір, присвячений постаті київського князя Аскольда, та другий твір із жанру духовної, патріотичної або народно-обрядової музики. Можливе виконання сучасної авторської музики християнського змісту під фонограму мінус. Максимальна тривалість виступу &ndash; 12&nbsp;хв.</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><span style=\"text-decoration: underline;\">&nbsp;</span></strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><span style=\"text-decoration: underline;\">Склад журі:</span></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Для оцінювання у фестивалі-конкурсі запрошується компетентне журі, до складу якого входять спеціалісти із музичною, філологічною та богословською освітою, представники ЗМІ, загальною кількістю 5 осіб.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><span style=\"text-decoration: underline;\">Критерії оцінювання:</span></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">- Зміст обраних творів, насамперед обов&rsquo;язкового твору про князя Аскольда;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">- &nbsp;Музично-виконавський рівень;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">- &nbsp;Довершеність поетичного компоненту пісні;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">- &nbsp;Сценічність виконання.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На<strong> ХІІ конкурсі</strong> <strong>&rdquo;Аскольдів глас&rdquo; </strong>журі визначить трьох переможців, виступи яких буде визнано найкращими.</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Переможці конкурсу нагороджуються грошовими преміями у розмірі:</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Диплом І ступеню &ndash; 6000 грн.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Диплом ІІ ступеню &ndash; 4000 грн.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Диплом ІІІ ступеню &ndash; 2000 грн.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Володарі грамот отримують подарунки&nbsp; та спеціальні призи від членів журі.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><span style=\"text-decoration: underline;\">Для участі в конкурсі до оргкомітету слід надіслати:</span></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">- &nbsp;Анкету з програмою виступу (прийом заявок з 01 липня до 01 листопада 2013 р.);</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">- &nbsp;Фото ансамблю, відео- або аудіозапис (<em>обов&rsquo;</em><em>язково</em>).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Оргкомітет фестивалю:</strong> о.-митр. Ігор&nbsp;Онишкевич, К.Чаплик, В.Федоришин, А.Чаплик, А.Гетьман, В.Слободян, Н.Кучерява, С.&nbsp;Заяць, Н.Пуряєва, М.Козачок, В.Матяш, І.Василик, Л.Михайлюк, І.Савицький, А.Павлюк, С.Карпа, Т.Чернета,.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Відповідальні</strong> за проведення конкурсу:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Катерина Чаплик &ndash; 066&nbsp;447 80 99; 067&nbsp;197 80 13</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">e&ndash;mail: <a href=\"mailto:Askoldiv_glas2011@ukr.net\">Askoldiv_glas2011@ukr.net</a> або <a href=\"mailto:Chaplyk_Kateryna@ukr.net\">Chaplyk_Kateryna@ukr.net</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Інформацію про історію і результати попередніх конкурсів можна отримати на сайті <a href=\"http://www.askoldova-mohyla.org\">www.askoldova-mohyla.org</a> рубрика &laquo;Аскольдів глас&raquo;.</span></p>',NULL,3442,5,1,NULL,53);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3908,'P','2013-07-18',1,NULL,'Rss','Навіщо біжиш? (19 липня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4917,200,1364120128.jpg\" />Дивлячись у вікно, що виходило на ринок, учитель помітив одного свого  учня. Це був Гайкель - він поспішав і виглядав дуже заклопотаним.  Учитель гукнув його і запросив до себе.</p>\n<p>\"Гайкелю, ти бачив, яке було вранці сьогодні небо?\"</p>\n<p>\"Ні, учителю\".</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1331:navishcho-bizhysh-19-lypnja&catid=1:mystecka-storinka-cat&Itemid=50',3443,NULL,1,1,26),
(3909,'P','2013-07-17',1,NULL,'Rss','Шлях до спасіння – Зарваниця зустрічає молодь','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4926,200,77.JPG\" />Зарваниця – те місце де люди відчувають духовне об’єднання людини з Богом. Майже кожного місяця відбуваються величні всеукраїнські прощі до Зарваниці, які укріплюють духовний стан людини. Кожного року Зарваниця зустрічає тисячі людей з різних областей України. Традиційно люди приїжджають до Зарваниці автобусом, але не менше людей ідуть на прощу пішки: хто з Тернополя, хто з Хмельницька, хто з Рівного, хто з Франківська, хто з сіл, що розташовані рядом зі Зарваницею.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1338:shljah-do-spasinnja--zarvanycja-zustrichaje-molod&catid=28:tema-nomera-cat&Itemid=2',3444,NULL,1,1,26),
(3910,'P','2013-07-18',1,NULL,'Rss','Блаженніший Любомир (Гузар) візьме участь в Освітньому форумі під час Всецерковної прощі в Києві','<a href=\"http://vk.com/share.php?url=http%3A%2F%2Frisu.org.ua%2Fua%2Findex%2Fall_news%2Fcommunity%2Fchurch_and_school%2F53003%2F\"> </a>\n<p><img style=\"margin: 10px; float: left;\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_53003_Husar_konf.jpg\" alt=\"Husar_konf.jpg\" title=\"Husar_konf.jpg\" height=\"192\" width=\"134\" />У  виставковому центрі біля Патріаршого собору Воскресіння Христвого УГКЦ,  що в&nbsp; Києві, 17 серпня 2013 року відбудеться Освітній форум на тему:  «Християнські цінності в освіті. Виклики і перспективи». Захід  розпочнеться о 10.30 в рамках Всецерковної прощі до Патріаршого собору  Воскресіння Христового, що проходитиме 16-19 серпня.</p>\n<p>Почесним гостем дискусії під час форуму буде Блаженніший Любомир  (Гузар), Архиєпископ-емерит УГКЦ. Ініціатор проведення - Комісія УГКЦ у  справах освіти та виховання у співпраці із Комісією УГКЦ у справах  мирян, Громадською радою з питань співпраці з Церквами та релігійними  організаціями при Міністерстві освіти і науки, молоді та спорту України,  Катехитично-педагогічним інститутом УКУ.</p>\n<p> </p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1339:blazhennishy-ljubomyr-guzar-vizme-uchast-v-osvitnomu-forumi-pid-chas-vsecerkovnoji-proshchi-v-kyjevi&catid=27:gk-v-ukrajini-cat&Itemid=57',3445,NULL,1,1,26),
(3911,'P','2013-07-19',1,NULL,'Rss','Прогрес (20 липня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4917,200,155-990x662_zpse8215acd.jpg\" />У непролазних лісах Амазонії в Південній Америці один білий чоловік  шукав нафту. Він дуже поспішав. Перші два дні тубільці, яких він найняв  носильниками, робили все, аби якнайліпше впоратися зі своїми дуже  важкими обов\'язками.</p>\n<p>Але на третій день вони стали перед білим  чоловіком безмовно й непорушно, з відсутніми виразами облич. Тубільці не  в змозі були працювати далі - це було ясно як день.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1332:progres-20-lypnja&catid=1:mystecka-storinka-cat&Itemid=50',3446,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3912,'P','2013-07-20',1,NULL,'Rss','Молитва (21 липня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4917,200,dutuna-kylbaba.jpg\" />«Ісусе милий, Дитятко Боже, дякую Тобі, що Ти дав мені братика, хоча Ти знаєш, що я просив пса. Твій Мартин».</p>\n<p><strong>Андрійко мав лиш одну велику мрію: велосипед. Яскраво-жовтого кольору, з різними технічними новинками – він вразив Андрійка з першого погляду. Хлопець побачив його в місті на виставці і вже не міг забути: щоночі бачив його уві сні, бачив п’ючи молоко і читаючи підручник з історії.</strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1333:molytva-21-lypnja&catid=1:mystecka-storinka-cat&Itemid=50',3447,NULL,1,1,26),
(3913,'P','2013-07-21',1,NULL,'Rss','Сказати, чому і коли я радію з того, що є греко-католиком?','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4929,200,IMG_0733-20.jpg\" />Я є священиком Української Греко-Католицької Церкви. Приналежність до неї сповняє мене особливою гордістю і радістю в особливі моменти мого життя.</p>\n<p>Я радий тоді, коли йду пішки до Гошева в оточенні 20 тисяч молоді, які усміхаючись, прощею долають більше тридцяти кілометрів за кілька годин.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1341:skazaty-chomu-i-koly-ja-radiju-z-togo-shcho-je-greko-katolykom&catid=28:tema-nomera-cat&Itemid=2',3448,NULL,1,1,26),
(3914,'P','2013-07-20',1,NULL,'Rss','Енцикліка Папи Франциска СВІТЛО ВІРИ (LUMEN FIDEI)','<p><img style=\"margin: 10px; float: left;\" alt=\"LUMEN FIDEI\" src=\"http://www.credo-ua.org/wp-content/uploads/2013/07/lumen2.jpg\" height=\"166\" width=\"121\" /></p>\n<p><strong>ЕНЦИКЛІКА</strong> <strong>LUMEN FIDEI – СВІТЛО ВІРИ</strong> <strong>СВЯТІШОГО ОТЦЯ</strong> <strong>ФРАНЦИСКА</strong></p>\n<p>ЄПИСКОПАМ, СВЯЩЕНИКАМ, ДИЯКОНАМ, БОГОПОСВЯЧЕНИМ ОСОБАМ І ВСІМ ВІРНИМ МИРЯНАМ ПРО ВІРУ</p>\n<p>1. Світло віри: саме так Традиція Церкви назвала великий дар,  принесений Ісусом Христом, котрий в Євангелії від Івана говорить про  себе: “Я – світло, на світ прийшов, щоб кожен, хто в мене вірує, не  перебував у темряві. ” (Ів.12,46). Також і Св.Павло висловлюється такими  словами: “Бо Бог… освітлив серця наші, щоб у них сяяло знання Божої  слави, що на обличчі Ісуса Христа.” (ІІ Кор. 4,6)</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1342:encyklika-papy-francyska-svitlo-viry-lumen-fidei&catid=32:gk-svit-cat&Itemid=58',3449,NULL,1,1,26),
(3915,'P','2013-07-19',1,NULL,'Rss',' Блаженні тихі, бо вони успадкують землю','<p><img style=\"margin: 10px; float: left;\" src=\"http://www.credo-ua.org/wp-content/uploads/2011/07/efe94602a2d9.gif\" title=\"Ісус\" height=\"153\" width=\"202\" /></p>\n<p>&nbsp;<strong>Він не буде ні змагатися, ні кричати і голосу його ніхто не буде  чути на майданах. Він очеретини надломаної не доломить і гнота тліючого  не загасить, аж поки не доведе право до перемоги. На його ім\'я будуть  надіятися погани. (Мт 12, 19-20) </strong></p>\n<p>Мені, як священику, частенько доводиться чути нарікання різних людей  такого типу: «чому Бог його не покарає», «скільки вже можна терпіти», та  прокльони типу «Бог напевно його покарає».</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=1343:-blazhenni-tyhi-bo-vony-uspadkujut-zemlju&catid=30:hrystyjanske-vchennia-cat&Itemid=37',3450,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(3916,'P','2013-07-21',1,NULL,'Note','ПОЖЕРТВИ ПАРАФІЯН ЦЕРКВИ СВ. МИКОЛАЯ ЧУДОТВОРЦЯ НА АСКОЛЬДОВІЙ МОГИЛІ У ЧЕРВНІ 2013 Р. Б.','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Милостиню роби згідно з тим, що маєш;Коли у тебе достатків повно, Роби з них, а коли мало маєш, то не бійся з того дати. Бо так готуєш собі добрий скарб На день потреби.&nbsp;&nbsp; Товит 4. 8-9.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</em></strong></span></p>\r\n<table style=\"width: 469px; height: 1882px;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>№ з/п</strong></span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Ж Е Р Т В О Д А В Ц І</strong></span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Пожертва на (для, до):</strong></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1</span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Олег та Олександра Бачун</span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Оздоровлення дітей</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2</span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Василь та Ілона</span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">3</span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Василь</span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Лікування Володимира&nbsp; Тимощука</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">4</span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Юрій, Наталія, Вікторія та Олександра</span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дітям-сиротам</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">5</span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольд</span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дзвіницю</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">6</span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ігор та Любов</span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Потреби церкви</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">7</span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Василь та Оксана</span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Весільна пожертва</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">8</span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Андрій та Оксана</span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Весільна пожертва</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">9</span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Роман Лялька</span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Потреби церкви</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">10</span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Юрій та Зоряна Гребініченки</span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дітей-сиріт і потреби церкви</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">11</span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Олександр та Зоя</span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дзвіницю</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">12</span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Анатолій</span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Лікування Володимира&nbsp; Тимощука</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">13</span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Антоніна</span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">14</span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Марія Шпак</span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дітям-сиротам на відпочинок</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">15</span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Олег та Марія</span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Храм</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">16</span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Родина Курилів</span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дзвіницю</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">17</span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ярослав та Марія</span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18</span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольд</span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Потребуючим дітям</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">19</span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Леонід та Леся, сини Ігор та Богдан</span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дзвіницю</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">20</span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольд</span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дзвіницю</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">21</span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Родина Роїк</span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Потреби церкви</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">22</span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Родина Курило</span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">23</span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ігор, Наталя, Оксана, Антон та Анна</span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\">\r\n<p align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">24</span></p>\r\n</td>\r\n<td width=\"350\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Родина Артиш</span></p>\r\n</td>\r\n<td width=\"321\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Милостиню роби згідно з тим, що маєш;Коли у тебе достатків повно, Роби з них, а коли мало маєш, то не бійся з того дати. Бо так готуєш собі добрий скарб На день потреби.</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Товит 4. 8-9.</strong></span></p>',NULL,3451,5,1,NULL,19);
