INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3055,'P','2012-05-01',1,NULL,'Note','ЗВІТ №8 про візит Великоднього благословення до ув’язнених жінок Лукянівського СІЗО м.Києва  (17.04.2012 р.)','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>17-го квітня</strong>, у третій день свята Пасхи Господньої, ув&rsquo;язнених жінок Лук&rsquo;янівського СІЗО м.Києва з Великодним благословенням відвідали настоятель церкви св.Миколая на Аскольдовій могилі о.Ігор Онишкевич та о.Костянтин Пантелей, якого Господь сподобив нести службу тюремного капеланства.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>17-го квітня</strong>, у третій день свята Пасхи Господньої, ув&rsquo;язнених жінок Лук&rsquo;янівського СІЗО м.Києва з Великодним благословенням відвідали настоятель церкви св.Миколая на Аскольдовій могилі о.Ігор Онишкевич та о.Костянтин Пантелей, якого Господь сподобив нести службу тюремного капеланства.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Двері тюремних камер відчинялися співом &laquo;Христос Воскрес&raquo; у натхненному виконанні Катерини Ч. Чесні отці несли у кожну камеру Боже благословення, окроплювали свяченою водою тимчасову обитель ув&rsquo;язнених, здійснювали Таїнство миропомазання, дарували ув&rsquo;язненим ікону Хреста-Спасителя, хрестики та образочки зі святими ликами.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Матеріальну частину візиту взяли на себе волонтери Ганна, Орина, Анна. Вони дарували ув&rsquo;язненим свячені пасочки та яєчка, пожертвувані парафіянами, печиво, закуплене благодійною службою церкви на пожертвування парафіян, та цукерки &ndash; дарунок доброчинця п.Олександра Л.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Зі сльозами зворушення слухали &nbsp;ув&rsquo;язнені напутнє слово, звернене до них, дякували, що за них не забули в дні такого Великого, Світлого Свята, просили навідуватись частіше, поспіхом записували на аркушиках паперу імена своїх рідних та близьких, які потребують молитви, щоб вручити священикам.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Найменшим мешканцям в&rsquo;язничного дому дістались дві упаковки памперсів дитячих та іграшок, пожертвуваних доброчинцями.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Загалом на пожертви доброчинців придбано для візиту:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Хрестики та іконки в церковній крамниці на суму 319 грн.50 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Печиво на суму 326 грн.30 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Всього витрачено 645 грн.80 коп.</strong></span></span></p>',NULL,NULL,2734,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3056,'P','2012-05-03',1,NULL,'Note','Благодійний обід для потребуючих дітей у ресторані «Дніпро»','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #000000;\">\"Думаю,   що такі ініціативи є дуже важливими та потрібними у сьогоднішньому   суспільстві, оскільки є прикладом соціальної відповідальності та заохоти   та подібних добрих справ\", - підсумовує о. Роман Сиротич, директор БФ   \"Карітас Київ\".</span></span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #000000;\">21  квітня 2012 року у центральному ресторані готелю &laquo;Дніпро&raquo; відбувся  благодійний обід для дітей з кількох дитячих будинків сімейного  типу,-повідомляє о. Роман Сиротич, директор БФ \"Карітас Київ\". До заходу  цьогоріч долучився також і Благодійний Фонд \"Карітас Київ\".</span></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #000000;\"><img style=\"float: right;\" src=\"image,4218,200,P1040457.JPG\" alt=\"\" />\"Період Великодніх свят &nbsp;-  це чудова нагода для добрих справ, і завжди велика приємність  зустрічати таких щирих і безпосередніх гостей, як діти, тому не дивно,  що атмосфера свята була наповнена радістю, дитячим сміхом та хорошим  святковим настроєм\", - говорить представник організатора Холодницька  Аня.</span></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #000000;\">Такий  благодійний захід в перший тиждень Великодня став доброю традицією у  готелі &ndash; на маленьких гостей чекала розважальна програма, смачний  частунок від майстерних кухарів ресторану &laquo;Дніпро&raquo; та подарунки від  готелю спільно з </span><span lang=\"RU\"><a href=\"https://r.mail.yandex.net/url/_nBIUFlp7MQ2yb01st-TBA,1335955818/www.caritas-kiev.org.ua\" target=\"_blank\"><span style=\"color: #000000;\"><span lang=\"UK\">Благодійним фондом &laquo;Карітас-Київ&raquo;</span></span></a></span><span style=\"color: #000000;\">.</span></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: #000000;\">\"Думаю,  що такі ініціативи є дуже важливими та потрібними у сьогоднішньому  суспільстві, оскільки є прикладом соціальної відповідальності та заохоти  та подібних добрих справ\", - підсумовує о. Роман Сиротич, директор БФ  \"Карітас Київ\".</span></span></span></p>',NULL,NULL,2735,5,1,30),
(3057,'P','2012-05-09',1,NULL,'Rss','«Крила Надії»','<p align=\"right\">Є і між дарами різниця, але Дух той самий; &nbsp;різниця теж і між служінням, але Господь той самий. Різниця є і між ділами, але Бог той самий, який усе в усіх робить. &nbsp;(I Кор. 12, 4-6)</p>\n<p><strong>Дорогі в Христі, </strong>«Крила Надії» продовжує своє служіння, душпастирська праця та домашня опіка з 1998 року разом з о. Анатолієм та волонтерами, кожну першу п’ятницю місяця відвідують людей похилого віку, які терплять фізично і духовно, просять&nbsp; про духовну науку та підтримку, вони потребують сторонньої допомоги.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=862:lkryla-nadijir&catid=25:parafija-cat&Itemid=27',2738,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3058,'P','2012-05-10',1,NULL,'Rss','Список розстріляних у Сандармосі українців і вихідців з України','<div>\n<p><strong>Сандармох – 75 років розстрілу</strong></p>\n</div>\n<p>В рамках спеціального проекту, приуроченого 75-м роковинам розстрілу  «соловецького етапу» в урочищі Сандармох у Карелії, Радіо Свобода  публікує <a href=\"http://www.radiosvoboda.org/content/article/24477308.html\" target=\"_blank\">списки українських жертв «Сандармоху»</a> і просить всіх небайдужих долучитися до&nbsp;<a href=\"http://www.radiosvoboda.org/sandarmoh.html\" target=\"_blank\">цього проекту</a>.&nbsp;Ідея позначити 2012 рік як рік пам’яті жертв «Сандармоху» належить Оксані Забужко. Вона висловила цю думку&nbsp;<a href=\"http://www.radiosvoboda.org/content/article/24441132.html\" target=\"_blank\">в ефірі «Вашої свободи»</a></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=863:spysok-rozstriljanyh-u-sandarmosi-ukrajinciv-i-vyhidciv-z-ukrajiny&catid=37:spilnota-cat&Itemid=49',2739,NULL,1,26),
(3059,'P','2012-05-15',0,NULL,'Note','ІХ фестивалю-конкурсу духовної пісні «Аскольдів глас»','<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,4220,200,lofo_glas.jpg\" alt=\"\" />Христос Воскрес!</span></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорогі парафіяни та гості стольного граду Києва!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Запрошуємо Вас бути глядачами <strong>ІХ фестивалю-конкурсу духовної пісні </strong><strong>&laquo;Аскольдів глас&raquo;</strong>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Концерт відбудеться <strong>20 травня 2012 р. Б. об 12:00</strong> (після Божественної Літургії о 10.00).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Цього року фестиваль присвячений 20-ти річному ювілею від початку моління та заснування парафії на Аскольдовій Могилі, а також 1130 річниці з дня вбивства царя і князя, мученика за віру христову Аскольда-Миколая.</span></span></p>','<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Христос Воскрес!</span></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорогі парафіяни та гості стольного граду Києва!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Запрошуємо Вас бути глядачами <strong>ІХ фестивалю-конкурсу духовної пісні </strong><strong>&laquo;Аскольдів глас&raquo;</strong>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,4220,200,lofo_glas.jpg\" alt=\"\" />Концерт відбудеться <strong>20 травня 2012 р. Б. об 12:00</strong> (після Божественної Літургії о 10.00).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Цього року фестиваль присвячений 20-ти річному ювілею від початку моління та заснування парафії на Аскольдовій Могилі, а також 1130 річниці з дня вбивства царя і князя, мученика за віру христову Аскольда-Миколая.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У фестивалі беруть участь молоді обдаровані співаки-солісти з Київщини, Львівщини, Криму.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Співайте Богові нашому, співайте!&raquo; <br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Оргкомітет: довідки за тел.: Катерина 066&nbsp;447 80 99</span></span></p>',NULL,NULL,2737,5,1,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3060,'P','2012-07-16',0,NULL,'Note','','<p style=\"text-align: center;\"><strong><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Молитва-звернення до Бога про зцілення за допомогою Святого Шарбеля</span></span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Господь, нескінченно Святий, Ти надихнув святого самітника-монаха на те, щоб жити і померти як Ісус, дав йому силу для позбавлення людей хвороб. Ми благаємо Тебе, Господи, послати нам милість любити і служити Тобі,як Шарбель. Всемогутній Господь, що дозволив своєму святому творити багаточисленні дива і надавати милості, змилуйся над нами ... (імя), щоб ми теж відчули прояв його особливої милості. Допоможи нам через святого Шарбеля віднайти здоровя. Нехай буде так.&nbsp;Отче наш, слава Тобі.<br />Амінь </span></span></p>','',NULL,NULL,2742,5,1,50),
(3061,'P','2012-05-20',1,NULL,'Note','Музичне привітання з нагоди Дня Матері','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,4227,200,7654567.jpg\" alt=\"\" width=\"200\" height=\"146\" />Окрім  жіночого вокального ансамблю &ldquo;Аніма&rdquo; у пісенному святі взяли участь  родина Процев&rsquo;ят, читець Анастасія Козяк, дует сопілкарів &ldquo;Дударик&rdquo; у  складі Володимира та Андрія Джосан, дитячий ансамбль, а також співачки  Олена Захарова, що пройняла слухачів до сліз зворушення піснею &ldquo;Посіяла  людям&rdquo; та Маріанна Гавриш. </span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,4227,200,7654567.jpg\" alt=\"\" />13 травня 2012 р. у церкві св. Миколая Чудотворця на Аскольдовій могилі пройшов концерт, присвячений Дню Матері. Хоч на дворі було зимно, але щира пісня, єднала серця слухачів і виступаючих.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Окрім жіночого вокального ансамблю &ldquo;Аніма&rdquo; у пісенному святі взяли участь родина Процев&rsquo;ят, читець Анастасія Козяк, дует сопілкарів &ldquo;Дударик&rdquo; у складі Володимира та Андрія Джосан, дитячий ансамбль, а також співачки Олена Захарова, що пройняла слухачів до сліз зворушення піснею &ldquo;Посіяла людям&rdquo; та Маріанна Гавриш. Окрім вокальних номер, пролунала в&rsquo;язанка українських народних мелодій у виконанні ансамблю &ldquo;Золоті ворота&rdquo; &ndash; Маріанна Грабовська (скрипка) та Андрій Халаш (баян).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Прикметно, що у всі часи українці трималися травневого святкування Дня Матері на відміну від вкоріненого у радянські часи 8 березня.</span></span></p>',NULL,NULL,2744,5,1,17),
(3062,'P','2012-05-26',1,NULL,'Rss','ІХ всеукраїнський фестиваль-конкурс духовної пісні «Аскольдів глас»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4228,200,CRW_6391.JPG\" />20 травня 2012 р.&nbsp;Б. в церкві св.&nbsp;Миколая Чудотворця на Аскольдовій могилі пройшов ІХ всеукраїнський фестиваль-конкурс духовної пісні <strong>«Аскольдів глас»</strong>. Фестиваль був одним із заходів до 20-річного ювілею від початку моління на Аскольдовій могилі, а також 1130 річниці з дня народження святого Аскольда-Миколая для неба.</p>\n<p><strong>У</strong> фестивалі-конкурсі взяло участь вісім співаків-солістів, родом із Львівщини, Івано-Франківщини, Тернопільщини, Рівненщини, Київщини, Луганщини та Криму.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=864:ih-vseukrajinsky-festyval-konkurs-duhovnoji-pisni-laskoldiv-glasr&catid=25:parafija-cat&Itemid=27',2746,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3063,'P','2012-06-02',1,NULL,'Rss','Сила заповітного слова…','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4230,200,zapovit.jpeg\" />Заповіт. Заповітне слово… Воно має особливу енергетичну силу, силу промовляння вустами роду, до якого ти належиш. Заповітне слово неможливо прирівняти до жодного іншого, оскільки воно містить в собі не лише інформаційну, комунікативну складову, але й виступає могутнім засобом духовного єднання людини зі своїми предками&nbsp; та нащадками. У заповіті сконцентровано дороговказ&nbsp; заповідача на майбутнє, окреслено шляхи та життєві орієнтири для інших людей.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=865:syla-zapovitnogo-slova&catid=28:tema-nomera-cat&Itemid=2',2748,NULL,1,26),
(3064,'P','2012-05-23',0,NULL,'Note','Центр академічного душпастирства Київської Архиєпархії УГКЦ Запрошує Вас взяти участь у МАРІЙСЬКОМУ ПАЛОМНИЦТВІ','<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,4228,200,Logo_centre_2.jpg\" alt=\"\" width=\"137\" height=\"137\" />Ми пропонуємо вам:</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Супровід духівника та молитовна&nbsp; програма,</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Літургія в греко-католицьких. церквах Парижу, Люрду, Мюнхена</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Молитва перед чудотворними іконами в Зарваниці та Ченстохові,</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Можливість купання у чудотворній купелі в Люрді</span></span></p>','<p style=\"text-align: justify;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"text-decoration: underline;\"><img style=\"float: left;\" src=\"image,4228,200,Logo_centre_2.jpg\" alt=\"\" />Маршрут&nbsp; подорожі:</span></span></span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>0</strong><strong>3</strong><strong> &ndash; 1</strong><strong>5</strong><strong> СЕРПНЯ&nbsp; 2012 р.Б.</strong></span></span></p>\r\n<h6 style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Київ - Зарваниця &ndash; Ченстохова (Польща) - Краків (Польща) &ndash;Париж (Франція) &ndash; Люрд (Франція, підніжжя Піренеїв) &ndash; Ніца (Франція, Лазурний берег) - Королівство Монако (Лазурний берег) - Мюнхен (Німеччина, Баварія) - Київ<br /><br /></span></span></h6>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ми пропонуємо вам:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Супровід духівника та молитовна&nbsp; програма,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Літургія в греко-католицьких. церквах Парижу, Люрду, Мюнхена</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Молитва перед чудотворними іконами в Зарваниці та Ченстохові,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Можливість купання у чудотворній купелі в Люрді</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,4228,200,765.jpeg\" alt=\"\" />Проїзд автобусом єврокласу, оформлення документів,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">2 дні (+ночівля та сніданок) в Кракові, Празі, Мюнхені</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1&nbsp; повний&nbsp; день + автобусна екскурсія по Парижу </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">3 повні дні: 2 ночівлі, сніданки, обіди та вечері у Люрді</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Екскурсія на фабрику французьких парфумів у Ніці </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Відпочинок на Лазурному березі в Королівстві Монако</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Зголоситися якнайшвидше!</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Контакти: </strong>097&nbsp;148-13-71, <a href=\"mailto:malchyn_olenka@yahoo.com\">malchyn_olenka@yahoo.com</a>, Оленка Мальчин </span></span></p>',NULL,NULL,2745,5,1,47);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3065,'P','2012-06-02',1,NULL,'Note','ЗВІТ №13 про розподілення гуманітарної допомоги від благодійного фонду «Олива», а також благодійних пожертвувань парафіян церкви св.Миколая на Аскольдовій могилі (14.05.2012 р.)','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Служба милосердя при церкві св.Миколая на Аскольдовій могилі, зібравши пожертвувані парафіянами: одяг чоловічий, жіночий та дитячий, взуття, ігри, іграшки дитячі та інші речі особистого вжитку, а також гуманітарну допомогу від благодійного фонду &laquo;Олива&raquo;, розподілила їх таким чином:</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Служба милосердя при церкві св.Миколая на Аскольдовій могилі, зібравши пожертвувані парафіянами: одяг чоловічий, жіночий та дитячий, взуття, ігри, іграшки дитячі та інші речі особистого вжитку, а також гуманітарну допомогу від благодійного фонду &laquo;Олива&raquo;, розподілила їх таким чином:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&mdash; уділила потребуючим мирянам;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&mdash;&nbsp; відправила гуманітарний вантаж Благодійному фонду &ldquo;Сузір\'я&rdquo; в с.Іванкові Бориспільського р-ну Київської обл. 14.05.2012 р.:</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">одяг чоловічий, жіночий, дитячий &ndash; 35 коробів+3 мішки</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">взуття &ndash; 1 короб</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">ігри та іграшки дитячі &ndash; 2 мішки</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Робота виконана силами волонтерів:&nbsp; п.Олександра, п.Тетяни, п.Анатолія, п.Анни.</span></p>',NULL,NULL,2751,5,1,19),
(3066,'P','2012-06-02',1,NULL,'Rss','Зіслання Святого Духа. П\'ятидесятниця','<img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/47122728a0.jpg\" height=\"255\" width=\"182\" />&nbsp;\n<p>Коротка історія</p>\n<p>Світлий празник Пасхи завершується не менше світлим і  великим   празником Святої П’ятдесятниці. По Христовому Різдві і  Воскресінні цей   празник належить до найбільших празників нашого  церковного року.   Зіслання Святого Духа це - наче вінець і печать на ділі  спасіння   людського роду, що його доконав Божий Син. В дні св.  П’ятдесятниці сам   Святий Дух помазує апостолів на проповідників  Христового Благовістя. В   дні Зіслання Святого Духа родиться і починає  діяти Христова Церква.   Святий Дух її провадить, просвічує, освячує і  зберігає на дорозі   правди.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=866:zislannja-svjatogo-duha-pjatydesjatnycja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',2752,NULL,1,26),
(3067,'P','2012-06-02',1,NULL,'Rss','Не біймося «русского міра», будуймо український світ, – Глава УГКЦ Святослав (Шевчук)','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4234,200,IMG_4902_resize.JPG\" /></strong>Українці повинні  будувати свій український світ, незалежно від того,  де вони перебувають,  вважає глава Української греко-католицької  церкви, Верховний  архієпископ Святослав Шевчук. Радіо Свобода  зустрілося з Блаженнішим  Святославом у польському Вроцлаві, де  відбулося засідання Постійного Синоду УГКЦ – органу, який допомагає  Верховному архієпископу управляти Церквою в Україні та за її межами.  Греко-католицькі єпископи розмовляли  про стратегію розвитку церкви,  зокрема про духовну опіку над  українськими емігрантами. Глава УГКЦ  поділився з Радіо Свобода своїми  міркуваннями щодо еміграції та гарячих  мовних дискусій в Україні.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=867:ne-bimosja-lrusskogo-mirar-budumo-ukrajinsky-svit--glava-ugkc-svjatoslav-shevchuk&catid=27:gk-v-ukrajini-cat&Itemid=57',2753,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3068,'P','2012-05-02',1,NULL,'Note','Заповіт Симона Петлюри','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Як будете в церкві на 12 євангеліях, слухатимете Слово Боже про Страсті Христові,</span> <span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Як будуть носити Святу Плащаницю біля Церкви, </span><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Як співатимуть радісне &laquo;Христос Воскрес&raquo;, </span><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Згадайте про мучеників України, </span><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Згадайте про всіх, кого розстріляли більшовики,..</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>До свята Героїв</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>(Великоднє звернення до українського народу)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Як будете в церкві на 12 євангеліях, слухатимете Слово Боже про Страсті Христові,</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Як будуть носити Святу Плащаницю біля Церкви,</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Як співатимуть радісне &laquo;Христос Воскрес&raquo;,</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Згадайте про мучеників України,</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Згадайте про всіх, кого розстріляли більшовики,</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Згадайте і про тих, хто зі зброєю в руках боронив</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Нашу рідну землю від московських катів</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> І своїх іуд-запроданців, -</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Згадайте і помоліться за них!</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Не за горами кари час!</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Воскресне наша Україна, скине з себе</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Московсько-большевицьке ярмо.</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Не забувайте про мучеників і праведників наших.</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Не забувайте про невинну кров</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> І готуйтесь до помсти над ворогами,</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Що не дають жити нам</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> По-людському!</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <em>Симон ПЕТЛЮРА, 1926р.</em></span></p>',NULL,NULL,2755,5,1,13),
(3069,'P','2012-05-03',1,NULL,'Note','Заповіт Андрія Мельника','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Будіть віру в&nbsp;прийдешнє, в&nbsp;Українську Самостійну державу, в&nbsp;якій не буде місця ні для визиску людини людиною, ні для національного гноблення, ні для затиску вільної думки, в&nbsp;якій український нарід буде свобідним господарем на своїй землі і&nbsp;по своїй волі&raquo;.</span></p>\r\n<p>&nbsp;</p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Будіть віру в&nbsp;прийдешнє, в&nbsp;Українську Самостійну державу, в&nbsp;якій не буде місця ні для визиску людини людиною, ні для національного гноблення, ні для затиску вільної думки, в&nbsp;якій український нарід буде свобідним господарем на своїй землі і&nbsp;по своїй волі&raquo;.</span></p>',NULL,NULL,2760,5,1,13);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3070,'P','2012-05-02',1,NULL,'Note','Олена Теліга','<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вітрами й сонцем Бог мій шлях намітив,&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Та там, де треба,&mdash; я тверда й сувора.&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> О, краю мій, моїх ясних привітів&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Не діставав від мене жодний ворог.</span></p>','<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сучасникам</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">(моральний заповіт<em>сучасному та майбутнім поколінням</em>)</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Не треба слів! Хай буде тільки діло!&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Його роби &mdash; спокійний і суворий,&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Не плутай душу у горіння тіла,&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Сховай свій біль. Зломи раптовий порив&raquo;.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Але для мене &mdash; у святім союзі:&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Душа і тіло, щастя з гострим болем.&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Мій біль бринить, зате коли сміюся,&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> То сміх мій рветься джерелом на волю!</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Не лічу слів. Даю без міри ніжність.&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> А може в цьому й є моя сміливість:&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Палити серце в хуртовині сніжній,&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Купати душу у холодній зливі.</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Вітрами й сонцем Бог мій шлях намітив,&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Та там, де треба,&mdash; я тверда й сувора.&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> О, краю мій, моїх ясних привітів&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Не діставав від мене жодний ворог.</span></p>',NULL,NULL,2756,5,1,13),
(3071,'P','2012-06-05',1,NULL,'Rss','Cходження Святого Духа','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4236,200,3.jpg\" />«Ми бачили світло істини,&nbsp; ми прийняли Духа Небесного, ми знайшли віру істинну, Нероздільній Тройці поклоняємося, Вона cпасла нас» ( Стиховня вечірні 50-ці)</p>\n<p>Світлий празник Пасхи завершується не менш світлим і великим празником Святої П’ятидесятниці. З богослужби П’ятидесятинці прийшла до наших щоденних молитов нам добре знана молитва до св. Духа:&nbsp; « Царю небесний…», яка благословляє кожне наше Богослужіння.</p>\n<p>День Св. П’ятидесятниці святкує саму подію сходження святого Духа на апостолів, а понеділок призначений на віддання особливої чести св. Духові, як третій Божі особі . Про це Синаксар понеділка так каже: «В цей&nbsp; день, в понеділок 50-ці, празнуємо того все святого&nbsp; й животворного і всесильного Духа, єдиного з Тройці Бога, єдино гідного, єдиносущного і єдино славного Отцю і Синові… Сам так розповідає про це велике свято отець Юліан Катрій у своїй книзі «Пізнай свій обряд».</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=868:chodzhennja-svjatogo-duha&catid=25:parafija-cat&Itemid=27',2761,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3072,'P','2012-05-02',1,NULL,'Note','Духовний заповіт Миколи Міхновського','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Не бери собі дружини з чужинців, бо твої діти будуть тобі ворогами, не приятелюй з ворогами нашого народу, бо ти додаєш їм сили і відваги, не накладай укупі з гнобителями нашими, бо зрадником будеш. </span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Кредо борця за волю і славу України формулює Міхновський у 10 заповідях УНП, котрі наводимо нижче:&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> 1. Одна, Єдина, Неподільна, Самостійна, Вільна, Демократична Україна, Республіка робочих людей - це ідеал української людини, за здійснення якого ти повинен боротися, не шкодуючи свого життя.&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> 2. Усі люди твої брати. Але москалі, ляхи, мадяри, румуни та жиди - це вороги нашого народу, як довго вони панують над нами і визискують нас.&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> 3. Україна для українців! Отже вигонь звідусіль з України чужинців-гнобителів.&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> 4. Усюди й завжди вживай української мови. Хай ні дружина твоя, ні діти твої не поганять твоєї господи мовою чужинців-гнобителів.&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> 5. Шануй діячів рідного краю, ненавидь його ворогів, зневажай перевертнів-відступників і добре буде цілому твоєму народові й тобі.&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> 6. Не вбивай Україну своєю байдужістю до всенародних інтересів.&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> 7. Не зробися ренегатом-відступником.&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> 8. Не обкрадай власного народу, працюючи на ворогів України.&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> 9. Допомагай своєму землякові поперед усіх, держись купи.&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> 10. Не бери собі дружини з чужинців, бо твої діти будуть тобі ворогами, не приятелюй з ворогами нашого народу, бо ти додаєш їм сили і відваги, не накладай укупі з гнобителями нашими, бо зрадником будеш.&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Слід зазначити, що всіх цих заповідей сам автор дотримувався до трагічної смерті. Сторіччя проминає з часу оприлюднення цього тексту, але актуальним він є і нині. Суттєво не змінився і склад поневолювачів, влучно визначених М.Міхновським. Не відійшли в минуле і зрада, відступництво, ренегатство, страх і байдужість. Цей нестерпний стан мусимо змінити, адже і сьогодні, як і сто років тому - &ldquo;Головна причина нещасть нашої нації - брак націоналізму серед ширшого загалу її&rdquo;.&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Напевно варто зупинитися на суті і сенсі гасла, висунутого М.Міхновським ще понад сторіччя тому, гасла, котре і досьогодні є об`єктом всіляких нападок, перекручень та інсинуацій, а саме гасла &ldquo;Україна для українців!&rdquo;.&nbsp;</span></p>',NULL,NULL,2757,5,1,13);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3073,'P','2012-05-02',1,NULL,'Note','Заповіт княгині Ольги','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Цей Завіт слід виконати на зорі, в той день, коли життя я на землі зачав і свято це було велике. Якщо дочка моя, одягши той вінець, в молитві за народ просити стане, я дам закон, що суть життя є, якщо же ні &ndash; загибель всім. Написане це на трьох срібних листах у рік 6454 від створення миру Інд. 5 чому я Григорій чорноризець і багато людей свідки є.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дочка моя, благословенна будь, прабатьків здійснила волю ти, в єдиний народ Роди зібравши. Щоб зберегти Рід твій, у якім кров моя, цей Заповіт даю, що говорити буду я, ніхто не сміє змінити інакше прокляну в вік. Дочка моя, Роду твоєму, доки не вийде строк, потрібне як би вмерти, розчинитися серед народу, хоронителем вірним, оберігатися будете. Якщо хто задумає заподіяти вам зло, загинуть не тільки тілом, тому, що мало, а духом і душею, і плодитися будуть як сміття, і змию, тому, що без потреби будуть, і до себе не допущу. У таємниці зберегти слід дочку твою, благословенна буде хай вона, вклоняться їй сини і дочки матері твоєї, шануватися вона буде, хто благословляє її, благословенні будуть, що проклинає &ndash; прокляті, послужать їй народи і вклоняться їй племена, бо через неї, народиться та, що буде матір&lsquo;ю тобі, і прийде в мир, коли настане днів кінець, і зможе дати вона тому народу силу, бо є вона початок і коней всьому, життя що має на землі. Багато дам пророк я для народу цього, але користі буде мало в тому. І будуть гинути Роди, що не виконують мого закону, і урятувати вже ніхто не зможе їх. Цей народ прийме велику скорботу і строку їй тисяча років і буде голод, війни, а найбільше мерзенність духу, і будуть, наче пси на розтерзанні, терзати цей народ ті лживі князі, у божевіллі своєму, переробляючи історію, несуть загибель для народу і собі. І будуть прокляті, хто сам назвавшись князем, живе не в князівським законі. Вона прийде щоб виконати заповідь мою, що обіцяв я праотцям твоїм, вінець первородства срібний одягти їй повинно, щоб в століттях Роди з&lsquo;єднати, якщо однієї з нею крові ми, шкоди це не заподіє їй, і дам їй суд вершити над народом тим, &nbsp;як вирішить вона, я виконаю все, адже це є закон. Не слід опікуватися невір&lsquo;ям народу, зневага до твого Роду, сповістить у кому є закон, а в кому вже немає його. Дочка моя, коли вона в невіданні з хоронителем своїм, виконає перший той закон, ти заспокой її і навчи всього, що повинна знати, я прийду, і буду говорити з нею. І кожний, хто в смиренності великій, коли дочка моя на голову вінець одягне коліно схилить, урятує він себе і духом він моїм відзначен буде, інші же згинуть. Цей Завіт слід виконати на зорі, в той день, коли життя я на землі зачав і свято це було велике. Якщо дочка моя, одягши той вінець, в молитві за народ просити стане, я дам закон, що суть життя є, якщо же ні &ndash; загибель всім. Написане це на трьох срібних листах у рік 6454 від створення миру Інд. 5 чому я Григорій чорноризець і багато людей свідки є.</span></p>',NULL,NULL,2758,5,1,13);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3074,'N','2012-06-11',1,NULL,'Rss','Радомишль – осідок Унії','<p>До 250-ї річниці введення на престол Пилипа Володкевича, Митрополита Київського, Галицького та Всієї Русі 8 червня 2012 року була проведена Всеукраїнська науково-історична конференція на тему: &laquo;Радомишль в історії Української церкви 1682-1840 роки&raquo;. Науковці, професори, доктори історичних наук приїхали з різних куточків України до міста Радомишль (Житомирська обл..) У своїх дослідженнях вони представили учасникам конференції культурно-історичну спадщину цього міста: розкрили його таємниці, діткнулися болючих питань Української церкви&hellip;</p>','',1,'http://oranta.org/index.php?option=com_content&view=article&id=869:radomyshl--osidok-uniji&catid=27:gk-v-ukrajini-cat&Itemid=57',2762,NULL,1,26),
(3075,'N','2012-06-11',1,NULL,'Rss','Радомишль – осідок Унії','<p><img style=\"margin: 10px; float: right;\" src=\"image,4245,200,CRW_9922.JPG\" alt=\"\" />До 250-ї річниці введення на престол Пилипа Володкевича, Митрополита Київського, Галицького та Всієї Русі 8 червня 2012 року була проведена Всеукраїнська науково-історична конференція на тему: &laquo;Радомишль в історії Української церкви 1682-1840 роки&raquo;. Науковці, професори, доктори історичних наук приїхали з різних куточків України до міста Радомишль (Житомирська обл..) У своїх дослідженнях вони представили учасникам конференції культурно-історичну спадщину цього міста: розкрили його таємниці, діткнулися болючих питань Української церкви&hellip;</p>\r\n<p>Захід розпочався панахидою-пам&lsquo;яті митрополита Пилипа Володкевича, та митрополитів Лева Шептицького і Ясона Смогоржевського, які були тут поховані. Владика Йосиф (Мілян) зазначив: &ndash; &nbsp;&laquo;наша молитва в Радомишлі закладе нові підвалини розвитку та розквіту Української Греко Католицької Церкви&raquo;. Владика наголосив: &laquo;Автентичну історію пишуть і трактують люди. Тому ми тут, щоб поставити певні акценти, говорити про історичні факти, заслуги, невдачі, відкривати істинну історію. Радомишль свого часу став осідком митрополитів, тому це місто повинно посісти чільне місце в історії Української Церкви&raquo;.</p>','',1,'http://oranta.org/index.php?option=com_content&view=article&id=869:radomyshl-osidok-uniji&catid=27:gk-v-ukrajini-cat&Itemid=57',2763,NULL,1,26),
(3076,'P','2012-06-11',1,NULL,'Rss','Радомишль – осідок Унії','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4245,200,CRW_9922.JPG\" />До 250-ї річниці введення на престол Пилипа Володкевича, Митрополита Київського, Галицького та Всієї Русі 8 червня 2012 року була проведена Всеукраїнська науково-історична конференція на тему: «Радомишль в історії Української церкви 1682-1840 роки». Науковці, професори, доктори історичних наук приїхали з різних куточків України до міста Радомишль (Житомирська обл..) У своїх дослідженнях вони представили учасникам конференції культурно-історичну спадщину цього міста: розкрили його таємниці, діткнулися болючих питань Української церкви…</p>\n<p>Захід розпочався панахидою-пам‘яті митрополита Пилипа Володкевича, та митрополитів Лева Шептицького і Ясона Смогоржевського, які були тут поховані. Владика Йосиф (Мілян) зазначив: – &nbsp;«наша молитва в Радомишлі закладе нові підвалини розвитку та розквіту Української Греко Католицької Церкви». Владика наголосив: «Автентичну історію пишуть і трактують люди. Тому ми тут, щоб поставити певні акценти, говорити про історичні факти, заслуги, невдачі, відкривати істинну історію. Радомишль свого часу став осідком митрополитів, тому це місто повинно посісти чільне місце в історії Української Церкви».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=869:radomyshl-osidok-uniji&catid=28:tema-nomera-cat&Itemid=2',2764,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3077,'P','2012-06-25',1,NULL,'Note','Увесь  бо закон  міститься у цій  одній  заповіді: «Люби ближнього  твого, як  себе  самого!» ( Гал. 5,14)','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4249,200,P1000281.JPG\" alt=\"\" width=\"150\" height=\"200\" />Парафіяльна служба милосердя, висловлює щиру подяку Михайлу Петровичу, який 21 травня 2012 року, з Божою допомогою, привіз продукти харчування для кухні-Карітас.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У п&rsquo;ятницю, суботу та неділю, у церковній хатині кухарі п. Марія та п. Ніна готують смачні обіди для дітей Божого Милосердя, студентів, а також тим, хто потребує. Продовж дня гарячі обіди отримує близько 30 людей, тому ця допомога є дуже необхідна.<br /></span></p>\r\n<p>&nbsp;</p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4249,200,P1000281.JPG\" alt=\"\" width=\"150\" height=\"200\" />Парафіяльна служба милосердя, висловлює щиру подяку Михайлу Петровичу, який 21 травня 2012 року, з Божою допомогою, привіз продукти харчування для кухні-Карітас.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У п&rsquo;ятницю, суботу та неділю, у церковній хатині кухарі п. Марія та п. Ніна готують смачні обіди для дітей Божого Милосердя, студентів, а також тим, хто потребує. Продовж дня гарячі обіди отримує близько 30 людей, тому ця допомога є дуже необхідна. Всі діти радіють солодощам, і пан Михайло, котрий також був дитиною, знає про це, тому привіз макаронні вироби, борошно, гречану та рисову крупу, буряк, цибулю, картоплю, яйця, моркву, майонез, томатну пасту, маргарин, олію, масло, а також яблука, банани, апельсини, чай, цукор, сушку, солодощі, печиво. Усі продукти закуплені в ТОВ &laquo;Бімаркет&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нехай Господь оберне обличчя своє до Вас і дасть Вам мир!</span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Настоятель церкви святого Миколая Мирлікійського Чудотворця на Оскольдовій могилі - митрофорний протопресвітер о. Ігор Ілля Онишкевич.</span></p>',NULL,NULL,2766,5,1,19),
(3078,'P','2012-07-06',1,NULL,'Rss','Літній відпочинок у Карпатах','<div align=\"right\">\n<p><em>Ви – світло світу. Не можна сховати місто, що лежить на верху гори.</em></p>\n<p><em>Так нехай світить перед людьми ваше світло, щоб вони бачивши ваші добрі вчинки, прославляли вашого Отця, що на небі.</em></p>\n</div>\n<div align=\"right\">\n<p><em>Мт</em><em>. 5, 14, 16</em></p>\n</div>\n<div>\n<p>З Божим благословенням отець Ігор Онишкевич, настоятель храму святого Миколая на Аскольдовій могилі та отець Олег Лучка з села Вербівка, Ружинського району Житомирської області церкви Різдва Пресвятої Богородиці, 30&nbsp;червня&nbsp;2012&nbsp;року Божого відправили дітей у християнський табір до села Заріччя…</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=871:litni-vidpochynok-u-karpatah&catid=25:parafija-cat&Itemid=27',2767,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3079,'P','2012-07-17',1,NULL,'Rss','Карпатськими схилами або як ми відпочивали у горах','<p style=\"text-align: justify;\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4225,200,DSCN6149.jpg\" />Літня пора приносить нам завжди щасливі емоції, адже чи не кожна дитина відпочиває в різних куточках нашої країни: чи то в Карпатах, чи то на морі. Дітям з церкви святого Миколая, що на Аскольдовій могилі, також пощастило. Разом зі своїми друзями 30 червня 2012 року вони вирушили на відпочинок до церковного табору, що розташувався&nbsp; у Карпатах, в селі Заріччя Надвірнянського району Івано-Франківської області. Варто згадати, що тут свій відпочинок два роки тому проводила також група дітей з Аскольдової могили. Як і минулий раз, так і цьогоріч діти абсолютно задоволені відпочинком. Цього року з нами поїхали також і декілька сімей нашої парафії. А виховним процесом та катехизацією опікувались: пані Ореста Цибрівська, Світлана Заєць, Мирослава Мазур та пан Дмитро Засульський.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=872:karpatskymy-shylamy-abo-jak-my-vidpochyvaly-u-gorah&catid=25:parafija-cat&Itemid=27',2768,NULL,1,26),
(3080,'P','2012-07-17',1,NULL,'Rss','Покладення чесної ризи Пресвятої Богородицi у Влахернi','<p align=\"justify\"><img src=\"http://askoldova-mohyla.org/uk/image,4225,200,400.jpg\" style=\"margin: 10px; float: right;\" />Покладення  чесної ризи Пресвятої Богородицi у Влахернi (V). У роки правління  візантійського імператора Льва Великого, Македонянина (457-474), брати  Гальбій і Кандид, наближені царя, вирушили з Константинополя до  Палестини на поклоніння святим місцям. У невеликому селищі поблизу  Назарету вони зупинилися на нічліг в однієї старої єврейки. У її будинку  паломники звернули увагу на запалені свічки і дим фіміаму. На питання,  що за святиня знаходиться в будинку, благочестива жінка довго не хотіла  відповідати, але після невідступних прохань повідала, що зберігає дорогу  святиню - Ризу Богородиці, від якої відбуваються багато чудес та  зцілень. Пресвята Діва перед Успінням подарувала одну зі Своїх риз  благочестивій дівиці-єврейці з цього роду, заповівши їй передати її  перед смертю також дівчині. Так, від покоління до покоління, Риза  Богоматері зберігалася в цій родині.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=873:pokladennja-chesnoji-ryzy-presvjatoji-bogorodyci-u-vlaherni&catid=27:gk-v-ukrajini-cat&Itemid=57',2769,NULL,1,26),
(3081,'P','2012-07-17',1,NULL,'Rss','Бліц-інтерв’ю з настоятелем храму святого Миколая на Аскольдовій могилі, отцем Ігорем Онишкевичем та священиком Василем Чудійовичем з приводу освячення каплички над Дніпром на честь Аскольда-Миколая','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,4252,200,17c.jpg\" style=\"margin: 10px; float: left;\" />Сьогодні було освячено капличку, яку спорудила греко-католицька громада святого Миколая, що на Аскольдовій могилі. Запитуємо нашого пароха отця Ігоря: &nbsp;хто відгукнувся на цю ідею та за які кошти вдалося спорудити капличку?</strong></p>\n<p>Доволі значну фінансову допомогу у будівництві нашої церковної каплички надали дві родини: Михайла та Ольги та Олега й Лідії. Їхніми стараннями і, звичайно, допомогою наших парафіян вдалося спорудити цю сакральну споруду. Кожен по копійці вкладав у цю справу. Тому зараз, завдяки зібраними коштами, над Дніпром височіє ця капличка.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=874:blic-intervju-z-nastojatelem-hramu-svjatogo-mykolaja-na-askoldovi-mogyli-otcem-igorem-onyshkevychem-ta-svjashchenykom-vasylem-chudiovychem-z-pryvodu-osvjachennja-kaplychky-nad-dniprom-na-c',2770,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3082,'P','2012-07-17',0,NULL,'Note','Пожертва Аскольдової могили на лікування о.Андрія Нагірняка','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4254,200,image001.jpg\" alt=\"\" width=\"128\" height=\"178\" />Слава Ісусу Христу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">За Благословенням Владики Йосифа 8 липня парафіяни церкви Св.Миколая на Аскольдовій могилі зібрали та переказали 4000 грн. пожертви на лікування в Брюсселі від онкології о.Андрія Нагірняка.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Щиро дякую усім добродіям та жертводавцям за цю збірку на трьох Службах Божих!</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава Ісусу Христу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4254,200,image001.jpg\" alt=\"\" width=\"128\" height=\"178\" />За Благословенням Владики Йосифа 8 липня парафіяни церкви Св.Миколая на Аскольдовій могилі зібрали та переказали 4000 грн. пожертви на лікування в Брюсселі від онкології о.Андрія Нагірняка.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Щиро дякую усім добродіям та жертводавцям за цю збірку на трьох Службах Божих!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Якщо ви бажаєте долучитись до цієї доброї справи та допомогти молодому отцеві УГКЦ, батькові чотирьох дітей, то прошу вас залишати ваші пожертви у нашому храмі з призначенням &laquo;<em>На зцілення о.Андрія Нагірняка</em>&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">о.Ігор&nbsp;</span></p>',NULL,NULL,2771,5,1,19),
