INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3258,'P','2013-03-11',1,NULL,'Rss','ЗВЕРНЕННЯ Блаженнішого Святослава','<p style=\"text-align: center;\"><strong>Синоду Єпископів Києво-Галицького Верховного Архиєпископства Української Греко-Католицької Церкви до вірних та всіх людей доброї волі з приводу 70-х роковин Волинської трагедії</strong></p>\n<p style=\"text-align: center;\"><em> </em></p>\n<p style=\"text-align: center;\"><em>Всечесному духовенству, преподобному монашеству, мирянам Української Греко-Католицької Церкви та всім людям доброї волі</em></p>\n<p style=\"text-align: center;\"><strong> </strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1030:zvernennja-blazhennishogo-svjatoslava&catid=27:gk-v-ukrajini-cat&Itemid=57',3011,NULL,1,26),
(3259,'P','2013-03-11',1,NULL,'Rss','Що таке \"сиропуст\"?','<p>Свята Церква, помалу готуючи нас до посту, у М\'ясопусну неділю наказала не їсти м\'ясні страви. У часі сиропусної або сироїдної седмиці Церква дозволяє споживати тільки молочні стра­ви. Та в Сиропусну неділю треба й від цих страв відмовлятися. Звідси й назва \"сиро-пуст\", тобто відпущення сира. Цей тиждень називався в народі сирний або масляний, а Сиропусна неділя мала назву пущення, тобто утримання від усіх молочних продуктів. У цю неділю в нас справляли \"запусти\", тобто останні передпісні забави.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1031:shcho-take-qsyropustq&catid=27:gk-v-ukrajini-cat&Itemid=57',3012,NULL,1,26),
(3260,'P','2013-03-11',1,NULL,'Rss',' Один з багатьох днів (12 березня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,1458,200,1.jpg\" />Поглянь, мамо! – вигукнула семирічна Марта.</p>\n<p>- Зараз, зараз! – автоматично вимовила знервована жінка за кермом, маючи на голові купу справ, що іще очікували на неї вдома.</p>\n<p>Потім була вечеря, перегляд телепрограм, купання, телефонні розмови, аж прийшов час коли треба було вкладатись спати.</p>\n<p>- Марто, вже час іти в ліжечко!</p>\n<p>Дівчинка побігла східцями вгору. Мама вже падала від утоми, та цілуючи  її, уже ж знайшла в собі сили, щоб помолитися з донечкою.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1032:-odyn-z-bagatoh-dniv-12-bereznja&catid=1:mystecka-storinka-cat&Itemid=50',3013,NULL,1,26),
(3261,'P','2013-03-11',1,NULL,'Rss','ПАМ\'ЯТЬ ПРО ГОЛОВУ ОУН М.ПЛАВ’ЮКА','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4497,200,Mykola_Plaviuk.jpg\" />Щиро дякую усім, хто з нагоди річниці смерті Голови ОУН Миколи Плав’юка прийшов вшанувати його пам\'ять. Передусім вдячний настоятелю Церкви Святого Миколая Чудотворця на Аскольдовій Могилі отцю Ігорю, який відправив зворушливу панахиду за покійним і завершив її виконанням державного гімну України. Як відомо, М. Плав’юк був довголітнім парафіянином цієї Церкви, допомагав її становленню. Сьогодні ж Церква гідно вшанувала пам\'ять свого вірного парафіянина.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1034:pamjat-pro-golovu-oun-mpavjuka&catid=37:spilnota-cat&Itemid=49',3015,NULL,1,26),
(3262,'P','2014-04-12',1,NULL,'Rss','Поучення тим, хто приступає до таїнства Покаяння','<p><img src=\"http://askoldova-mohyla.org/uk/image,4393,200,028_4001.jpg\" style=\"margin: 10px; float: left;\" /></p>\n<p align=\"center\"><strong>ПОУЧЕННЯ ТИМ, ХТО ПРИСТУПАЄ ДО СВЯТИХ ТАЇНСТВ ПОКАЯННЯ І ЄВХАРИСТІ</strong><strong>Ї</strong></p>\n<p>Святе таїнство Сповіді (Покаяння) є особливою зустріччю заблуканої  людини, якою є кожен із нас, з милосердним Богом, нашим люблячим  Батьком. Для того, щоб глибоко пережити цю зустріч, потрібно належним  чином приготуватися до неї, виконуючи наступні умови:</p>\n<p>1)&nbsp; зробити іспит совісті;</p>\n<p>2)&nbsp; збудити жаль за гріхи;</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1039:pouchennja-tym-hto-prystupaje-do-tajinstva-pokajannja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',3019,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3263,'P','2013-03-12',1,NULL,'Note','«Карітас Київ» розпочав збір коштів для придбання будинку для сім’ї із 4 дітьми ','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4499,200,DSC04901.JPG\" alt=\"\" width=\"144\" height=\"109\" />Четверо маленьких діток зможуть жити у власному будиночку разом із батьками! А не в лісі у наметі &ndash; як раніше. Аби викупити хату потрібно знайти 32700 грн &ndash; захмарні гроші для багатодітної родини, але не великі гроші для всіх людей доброї волі, які зможуть пожертвувати для втілення цього доброго діла&raquo;, - підсумовує о. Роман Сиротич, директор БФ &laquo;Карітас Київ&raquo;.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4499,200,DSC04901.JPG\" alt=\"\" width=\"200\" height=\"150\" />Восени 2012 року &laquo;Карітас Київ&raquo; (<a href=\"http://caritas-kiev.org.ua/\" target=\"_blank\">www.caritas-kiev.org.ua</a>) допоміг родині, яка жила просто в лісі з чотирма дітьми, які проживали чимало часу у наметі. Четверо дітей (віком 5, 4 і 2 роки, а наймолодшій 2 місяці) і двоє молодих батьків жили просто неба, не маючи засобів на існування, їжі, теплого одягу і засобів гігієни. Коли нічна температура почала наближатись до 0 С&deg;, житлом для молодої сім&rsquo;ї продовжував служити туристичний намет. Але завдяки зусиллям працівників Карітасу, о. Роману Сиротичу і о. Юрію Голоднюку сьогодні ці діти отримали тепле житло, вони нагодовані і перебувають у безпеці.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">31-річний батько молодої родини Олександр народився у бідній багатодітній родині в селі на Херсонщині. Його 27-річна дружина Тамара народилася у Києві, своїх батьків не знає, виховувалася у інтернаті. Познайомилася пара у Києві, куди Олександр приїхав на заробітки. Жили у Черкаській області у старенькій хатині, яка згодом просто зруйнувалася. Відтак подружжя опинилося на вулиці без даху над головою і на цей час у них вже було 4 дітей. Певний час сім&rsquo;я мешкала в орендованій кімнаті у Миколаївській області, але коли не змогли оплачувати оренди, їх виселили.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Далі почалося вуличне життя, намагання усіма можливими способами вийти із складної ситуації. Завдяки зусиллям працівників Карітасу у вересні 2012 року було орендовано будинок у с. Мехедівка Драбівського району Черкаської області, де Саша, Тамара і дітки проживають до сьогоднішнього дня.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хатка у доброму стані, має дві кімнати, кухню, коридор, веранду. Є котел та газ, сарай і погріб, деякі меблі. Ділянка огороджена парканом. Карітас завдяки жертводавцям надав їм речі першої необхідності &ndash; їжу, одяг, іграшки, памперси, засоби гігієни, медикаменти. Двоє дітей влаштовані у садочок, ведеться пошук роботи для батька сім&acute;ї.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Єдина проблема &ndash; оренда будинку скоро завершиться. Після переговорів із власницею вдалося домовитися про придбання дому. Звертаємося до людей доброї волі по допомогу &ndash; якщо кожен з нас вкладе невеличку суму для майбутнього 4 неповнолітніх дітей і молодого подружжя, які пережили стільки поневірянь, ми спільно звершимо грандіозну дуже добру справу! Четверо маленьких діток зможуть жити у власному будиночку разом із батьками! А не в лісі у наметі &ndash; як раніше. Аби викупити хату потрібно знайти 32700 грн &ndash; захмарні гроші для багатодітної родини, але не великі гроші для всіх людей доброї волі, які зможуть пожертвувати для втілення цього доброго діла&raquo;, - підсумовує о. Роман Сиротич, директор БФ &laquo;Карітас Київ&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Гроші можна пожертвувати у зручний для кожного спосіб, сконтактувавшись із директором (о. Роман 0971945911): переслати на рахунок Карітасу (Благодійний фонд &laquo;Карітас Київ&raquo;, ЄДРПОУ 21477781, Відділення № 8 у м. Києві ЦФ ПАТ &laquo;Кредобанк&raquo;,&nbsp; МФО 325365, р/р 2600910299143), або по безготівковому розрахунку на <a href=\"https://ubb.org.ua/uk/project/593/#tabs-1\">https://ubb.org.ua/uk/project/593/#tabs-1</a>.</span></p>',NULL,NULL,3016,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3264,'P','2013-03-13',1,NULL,'Rss','Глава УГКЦ: Понтифікат Папи Бенедикта ХVІ підніс нашу Церкву на якісно новий рівень','<p><strong><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4503,200,p3093130.jpg\" />Ваше Блаженство, після відставки Святішого Отця Бенедикта  ХVІ багато йшлося про його понтифікат, однак в Україні мало відомо про  взаємовідносини теперішнього Папи-емерита і нашої Церкви. Яким, на вашу  думку, є значення понтифікату Бенедикта ХVІ для УГКЦ?</em></strong></p>\n<p>Впевнений, щоб глибоко і докладно дати відповідь на це питання,  потрібен час, щоби всі ті моменти спілкування і опіки над нашою Церквою з  боку Бенедикта ХVІ глибоко зрозуміти і пов’язати. Докладніше можу  говорити про кілька таких моментів, які я сам особисто досвідчив і  пережив.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=1040:glava-ugkc-pontyfikat-papy-benedykta-hvi-pidnis-nashu-cerkvu-na-jakisno-novy-riven&catid=27:gk-v-ukrajini-cat&Itemid=57',3027,NULL,1,26),
(3265,'P','2013-03-13',1,NULL,'Rss','Осел і сопілка (14 березня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4500,200,87655.jpg\" />У травні на луці лежала загублена кимось Сопілка. Вже давно ніхто на ній  не грав. Аж одного дня поблизу проходив Осел, він узяв та й подув у  Сопілка щосили. Ніколи у своєму житті Осел не чув такого чарівного  звуку. І не лише він,  у своєму ослячому житті, але й Сопілка – у  своєму.</p>\n<p>Ні Осел, ні Сопілка не могли зрозуміти, що сталося (обоє не були  розумними, але вірили в розумність), вони відсахнулися одне від одного ,  засоромившись найпрекраснішої події в їхньому житті.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1035:osel-i-sopilka-14-bereznja&catid=1:mystecka-storinka-cat&Itemid=50',3028,NULL,1,26),
(3266,'P','2013-03-13',1,NULL,'Rss','Маємо Папу','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4511,200,1798_120579191462672_669654678_n.png\" />Брати і сестри, добрий вечір!</p>\n<p>Як вам відомо, обов’язком Конклаву було надати Риму єпископа. Виглядає  на те, що мої брати-кардинали дійшли майже до краю землі, щоб знайти  його… але ось ми тут. Дякую вам за вітання, що надійшли від єпархіальної  спільноти Риму.</p>\n<p>Перш за все хочу вас просити про молитву за нашого Папу-емерита  Бенедикта ХVІ, щоб Господь його благословив, а Мати Божа оберігала.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1041:majemo-papu&catid=32:gk-svit-cat&Itemid=58',3029,NULL,1,26),
(3267,'P','2013-03-14',1,NULL,'Rss','Ангел охоронець','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4514,200,1298722671.jpg\" />Дивна сила молитви.Він стояв під стіною, зіпершись на неї крилом, і тихо  плакав. Гарячі сльози стікали по прекрасному лику і капали на землю.  Якби він був видимим, то усі бачили б, що з тих сліз виростають тоненькі  паростки квітів, але від того, що сльози такі гарячі й пекучі, аж до  болю, квіти в’януть. А він усе стояв і ждав, ждав, коли юнак, душа  котрого була ввірена йому Богом, вийде з тієї будівлі, куди не можна  було йому ввійти.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1042:angel-ohoronec&catid=30:hrystyjanske-vchennia-cat&Itemid=37',3032,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3268,'P','2013-03-15',1,NULL,'Rss','Слушна думка (16 березня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4500,200,66560.jpg\" />Один мандрівник, ідучи селом, побачив під деревом людину з мотузкою в  руці. Чоловік обв’язав себе на поясі одним кінцем, а другий – намагався  закинути на міцну грубу гіляку.</p>\n<p>- Чоловіче, ти що надумав робити? – запитав мандрівник.</p>\n<p>- Хочу повіситись, - відповів той.</p>\n<p>- Отак? – здивувався перехожий. – Та ж мотузку зав’язують на шиї.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1037:slushna-dumka-16-bereznja&catid=1:mystecka-storinka-cat&Itemid=50',3033,NULL,1,26),
(3269,'P','2013-03-13',0,NULL,'Note','Глава УГКЦ: Новообраний Папа дуже добре знає про нашу Церкву і духовність','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4513,200,1363202996.jpg\" alt=\"\" width=\"200\" height=\"102\" />Новообраний Папа Франциск І є вихованцем нашого священика Степана Чміля, який тепер похований у базиліці св. Софії в Римі. Нинішній Папа, а тоді студент Салезіянської школи, щодня раненько, поки ще всі хлопці спали, прислуговував о. Чмілю під час Служби Божої. Він добре знає наш обряд і навіть пам&rsquo;ятає нашу Літургію.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4513,200,ArticleImages_51592_378997_560334880658243_747233064_n.jpg\" alt=\"\" />Глава Української Греко-Католицької Церкви Патріарх Святослав (Шевчук) розповів кореспондентці РІСУ про новообраного Римського Архиєрея Франциска І. За його словами, Понтифік добре знає ситуацію УГКЦ, сам був вихованцем о. Степана Чміля і навіть студентом прислуговував тому під час Літургії.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Будучи єпископом УГКЦ в Аргентині, Патріарх Святослав мав нагоду краще познайомитися з Кардиналом Берґольо, який був його ординарієм, і вважає, що з цим Папою УГКЦ чекає гарне майбутнє. Він описує Франциска І як скромну людину та водночас глибокого інтелектуала.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Новообраний Папа Франциск І є вихованцем нашого священика Степана Чміля, який тепер похований у базиліці св. Софії в Римі. Нинішній Папа, а тоді студент Салезіянської школи, щодня раненько, поки ще всі хлопці спали, прислуговував о. Чмілю під час Служби Божої. Він добре знає наш обряд і навіть пам&rsquo;ятає нашу Літургію. Востаннє я його відвідував, коли прощався з Аргентиною, від&rsquo;їжджаючи до України, і просив, щоб він дав свої свідчення для беатифікаційного процесу о. Степана Чміля, на що він радо погодився. Отож, Святіший Отець дуже добре знає і про нашу Церкву, і про нашу Літургію та обряд, про нашу духовість.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4513,200,1363202996.jpg\" alt=\"\" width=\"200\" height=\"102\" />Крім того, Папа Франциск І ще як Архиєпископ Буенос-Айреса був ординарієм для східних католиків, зокрема, тих, які не мали своєї єрархії. А наша єпархія в Аргентині є суфраганна Архиєпископства Буенос-Айреса. Таким чином Кардинал Берґольо завжди опікувався нашою Церквою в Аргентині, і я, як молодий єпископ, робив свої перші кроки в цьому служінні під його оком та з його допомогою. Тому я гадаю, ми справді будемо мати в його особі того Святішого Отця, який дбатиме про нашу Церкву. І я сподіваюся, що з тим Папою нас чекають дуже гарні події в майбутньому.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Якщо говорити власне про особу нового Святішого Отця, то він - надзвичайно скромна людина. Наприклад, у Буенос-Айресі він ніколи не використовував своєї машини, завжди їздив громадським транспортом, завше дуже скромно одягався. І вирізнявся тим, що найбільше хотів дбати про вбогих; він був той, хто відвідував людей у найбідніших кварталах і взагалі є особою душпастирського складу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Як єзуїт Папа Франциск І є надзвичайно глибоким інтелектуалом. І я пригадую, що його проповіді зазвичай були вельми короткі, часом навіть складалися буквально з п&rsquo;яти-шести речень, але в них він так багато казав, що після того його короткого слова завжди тривала мовчанка на майже п&rsquo;ять-сім хвилин, для того, щоб над цим словом можна було глибоко роздумувати&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">http://risu.org.ua/ua/index/all_news/catholics/ugcc/51592/</span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>',NULL,NULL,3030,5,1,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3270,'P','2013-03-16',1,NULL,'Rss','Що таке піст і як правильно постити?','<p align=\"left\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4514,200,mol.jpg\" />Цими днями         Свята Церква вводить нас у благословенний час Великого посту. Християнська         традиція використовує різні назви для визначення цього періоду: Великий         піст, Чотиридесятниця, Великодній піст, Строгий піст та інші. Це є особливий         час для покаяння, молитви, боротьби з гріховними звичками. Хто як проведе         цей піст, так і зможе пережити і відчути торжество і велич Святого Христового         Воскресіння.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1043:shcho-take-pist-i-jak-pravylno-postyty&catid=28:tema-nomera-cat&Itemid=2',3034,NULL,1,26),
(3271,'P','2013-03-16',1,NULL,'Rss','Бажання (17 березня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4500,200,rozpjattia.jpg\" />Жив на світі дуже бідний чоловік. Він твердо вірив у св. Миколая і палко  до нього молився. Врешті-решт святий вирішив зустрітися з тим чоловіком  і одного дня зійшов з неба.</p>\n<p>Прибувши в дім бідняка і побачивши його злидні, св.. Миколай вирішив йому допомогти.</p>\n<p>Він доторкнувся пальцем каменя біля порогу, і той одразу ж перетворився на золото.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1038:bazhannja-17-bereznja&catid=1:mystecka-storinka-cat&Itemid=50',3035,NULL,1,26),
(3272,'P','2013-03-17',1,NULL,'Rss','Неділя прощення','<p><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4518,200,372.jpg\" />«Неділя прощення»</em><em> – </em><em>таку назву має</em><em> </em>Неділя  перед початком Великого Посту. В цей день Церква запрошує вірних  &nbsp;простити один одному кривди та образи. Заохочує перепросити&nbsp; своїх  ближніх, щоб з чистим сумлінням стати на шлях Великого Посту, який  провадить до Воскресіння.<em>&nbsp;</em></p>\n<p><em>«Коли ви прощатимете людям їхні провини, то й Отець ваш небесний  простить вам. А коли ви не будете прощати людям, то й Отець ваш небесний  не простить вам провин ваших» (Мт. 6, 14-15)</em><em> </em></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1044:nedilja-proshchennja&catid=31:kolonka-redaktora-cat&Itemid=1',3036,NULL,1,26),
(3273,'P','2013-03-17',1,NULL,'Rss','Прощання понтифіка, майбутнє УПЦ (МП) і політика на святі Хрещення — в центрі уваги ЗМІ','<p>Підготувала Марина ДОВЖЕНКО, експерт Центру суспільного моніторингу</p>\n<p style=\"text-align: center;\"><strong>Головні релігійні події лютого 2013 року очима світських ЗМІ</strong></p>\n<p><em>У ході роботи над моніторингом було  проаналізовано контент 25 ЗМІ: інформаційних агенцій УНІАН, Українські  новини, РБК-Україна; сайтів телевізійних каналів — «Подробности», ТСН, 5  канал, TVi; «Радіо Свобода»; всеукраїнських та регіональних видань  «Українська правда», «Коментарі», «День», «Україна молода», «Дзеркало  тижня», «Український тиждень», «Коммерсант», «Сегодня», «Главком»,  «Фокус», портал Liga.net, «Корреспондент», «Голос», «Левый берег»,  «Бі-бі-сі Україна», Zaxid.net та ZIK.</em></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1045:proshchannja-pontyfika-mabutnje-upc-mp-i-polityka-na-svjati-hreshchennja-v-centri-uvagy-zmi&catid=27:gk-v-ukrajini-cat&Itemid=57',3040,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3274,'P','2013-03-17',1,NULL,'Note','Чин всепрощення у Сиропусну неділю','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4520,200,4868931.gif\" alt=\"\" width=\"200\" height=\"135\" />Зі щирого серця признаємось, що згрішили супроти ближніх, свідомо чи несвідомо образили, скривдили і прогнівили та з цілого серця просимо прощення.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Благословіть, чесний отче, і простіть нам, грішним, провини наші добровільні і недобровільні, свідомі і несвідомі, які ми вчинили словом, ділом, чи думкою, і всіма почуттями нашими.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4520,200,4868931.gif\" alt=\"\" width=\"200\" height=\"135\" />Священик після Літургії або вечірні у Сиропусну неділю стає на амвоні лицем до вірних і мовить: Священик: Пригадаймо собі, брати і сестри, всіх тих, які нас образили, скривдили, або провинились супроти нас. Люди: Зі щирого серця прощаємо всім тим, які нас образили, скривдили, або провинилися супроти нас. Священик: Пригадаймо собі, брати і сестри, всіх тих, яких ми свідомо чи несвідомо образили, скривдили, або прогнівили. Люди: Зі щирого серця признаємось, що згрішили супроти ближніх, свідомо чи несвідомо образили, скривдили і прогнівили та з цілого серця просимо прощення. Священик кланяється до землі вірним та навколішках мовить: Священик: Благословіть, брати і сестри, і простіть мені, грішному, чим згрішив я в цей день та по всі дні життя мого словом, ділом, думкою і всіма почуттями моїми. Люди: Бог нехай простить і помилує вас, чесний отче, і ми вам прощаємо. Священик: Простіть мені, брати і сестри, якщо я через забуття чи недбайливість не сповнив усіх своїх обов\'язків супроти вас. Люди: Бог нехай простить і помилує вас, чесний отче, і ми вам прощаємо. Священик: Простіть, брати і сестри, мені та всім моїм співбратам у священстві за все, чим ми прогрішились проти вас. Священик встає з колін. Люди кланяються до землі священикові та навколішки мовлять: Люди: Благословіть, чесний отче, і простіть нам, грішним, чим згрішили ми в цей день та по всі дні життя нашого словом, ділом, думкою і всіма почуттями нашими. Священик: Благодаттю Своєю Бог нехай простить всі гріхи ваші і помилує вас, і я вам прощаю. Люди: Благословіть, чесний отче, і простіть нам, грішним, якщо ми через забуття чи недбайливість не сповняли своїх обов\'язків супроти вас. Священик: Благодаттю Своєю Бог нехай простить всі гріхи ваші і помилує вас, і я вам прощаю. Люди: Благословіть, чесний отче, і простіть нам, грішним, провини наші добровільні і недобровільні, свідомі і несвідомі, які ми вчинили словом, ділом, чи думкою, і всіма почуттями нашими. Священик: Благодаттю Своєю Бог нехай простить всі гріхи ваші і помилує вас, і я вам прощаю.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/NTNWpHBM-2I\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><a href=\"http://www.facebook.com/photo.php?fbid=553763611322924&amp;set=a.208111469221475.55122.198406070192015&amp;type=1&amp;theater\">http://www.facebook.com/photo.php?fbid=553763611322924&amp;set=a.208111469221475.55122.198406070192015&amp;type=1&amp;theater</a><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><br /><br /></span></p>',NULL,NULL,3037,5,1,48);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3275,'P','2013-03-17',1,NULL,'Note',' Глава УГКЦ запросив Папу Франциска в Україну ','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4521,200,papa_shevchuk.jpg\" alt=\"\" width=\"200\" height=\"130\" />Блаженніший Святослав (Шевчук), Предстоятель Української Греко-Католицької Церкви, надіслав вітального листа на ім&rsquo;я Святішого Отця Франциска з нагоди обрання Вселенським Архиєреєм і запросив його відвідати Україну з нагоди 1025-річчя Хрещення України. &laquo;Це було б великим благословенням для нас та всієї України розраховувати на Вашу присутність під час такої визначної події&raquo;.</span></p>','<div class=\"news-single-img\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;<img style=\"float: left;\" src=\"image,4521,200,papa_shevchuk.jpg\" alt=\"\" width=\"200\" height=\"130\" /></span><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Блаженніший Святослав (Шевчук), Предстоятель Української Греко-Католицької Церкви, надіслав вітального листа на ім&rsquo;я Святішого Отця Франциска з нагоди обрання Вселенським Архиєреєм і запросив його відвідати Україну з нагоди 1025-річчя Хрещення України ( <a href=\"http://ugcc.tv/ua/media/54993.html\" target=\"_blank\">слухати</a>). &laquo;Це було б великим благословенням для нас та всієї України розраховувати на Вашу присутність під час такої визначної події&raquo;, - ідеться у привітальному листі.</span></div>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Глава Церкви привітав новообраного Папу Франциска від свого імені та від всієї Української Греко-Католицької Церкви іспанською мовою. &laquo;З глибини наших сердець радіємо, що&nbsp;Вас вибрали Папою. Приєднуємося до вітань нашими молитвами, які сьогодні звучать із вуст нашої пастви, що знаходиться на всіх континентах&raquo;, - написав Блаженніший у листі до Папи Франциска.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Глава УГКЦ відзначає, що Святіший Отець добре знайомий із УГКЦ,&nbsp;яка пройшла мученицький шлях. Уперше Папа зустрівся з УГКЦ, навчаючись у салезіанській школі та&nbsp;прислуговуючи отцю Степану Чмілю у святилищі під час Літургій. Пізніше, будучи Архиєпископом Буенос-Айреса, він мав нагоду її пізнавати, опікуючись Аргентинською єпархією Покрови Пресвятої Богородиці УГКЦ: її єпископами, священиками, ченцями і черницями та всіма вірними.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Особисто висловлюю Богові свою щиру вдячність, оскільки саме в Буенос-Айресі я, молодий та щойно призначений єпископ, робив свої перші кроки, перебуваючи там під Вашою батьківською опікою. І сьогодні впевнений, що зможу розраховувати на підтримку Вашої Святості, як і Аргентинська єпархія Покрови Пресвятої Богородиці&raquo;, - зазначив Глава УГКЦ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Відтак Блаженніший Святослав наголосив, що Українська Греко-Католицька Церква розвивається, зростає та засвідчує свою віру. &laquo;Хочу запевнити Вас у нашій єдності, сопричасті та синівській вірності Вам, як Вселенському Архиєрею, вірності, яку наша Церква скріпила пролитою кров&rsquo;ю своїх мучеників протягом своєї історії та особливо під час жахливих років панування комуністичної влади заради&nbsp;єдності із Вселенською Церквою&raquo;, - наголосив Блаженніший Святослав.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На завершення Предстоятель УГКЦ попросив у Святішого Отця апостольського благословення для УГКЦ та побажав йому, щоб він &laquo;служив Богові та Вселенській Церкві з мудрістю і духовною силою на многії літа&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Департамент інформації УГКЦ</span></p>',NULL,NULL,3038,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3276,'P','2013-03-17',1,NULL,'Note','НАУКОВО-ПРАКТИЧНИЙ СЕМІНАР ДЛЯ КАТЕХИТІВ КИЇВСЬКОЇ АРХИЄПАРХІЇ З НАГОДИ РОКУ ВІРИ','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4522,200,IMG_0467.JPG\" alt=\"\" width=\"200\" height=\"150\" />Метою семінару було удосконалення педагогічної майстерності катехитів через представлення новітніх активних методів катехизації (ігрових, дидактичних, театралізованих тощо).</span></p>\r\n<p style=\"text-align: justify;\"><em><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Методи катехизації, яких ми навчилися, чудові та доступні. Ми, учасники семінару, мали змогу побувати не лише в ролях катехитів, але й зіграти звукорежисерів, фокусників та лікарів, що роздають вітаміни віри. Дякую!</span></em></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>с. Бернадета (м. Житомир)</strong></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4522,200,IMG_0549.JPG\" alt=\"\" width=\"200\" height=\"150\" />2-3 березня 2013 р.Б. Катехитична комісія Київської Архиєпархії з нагоди Року віри організувала проведення навчально-практичного семінару для катехитів &laquo;Віра твоя спасла тебе: активні методи праці з дітьми та молоддю&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На семінарі були присутні катехити з парохій Києва, Вінниці, Чернігова, Житомира та інших деканальних катехитичних осередків.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Метою семінару було удосконалення педагогічної майстерності катехитів через представлення новітніх активних методів катехизації (ігрових, дидактичних, театралізованих тощо).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4522,200,IMG_0504.JPG\" alt=\"\" width=\"200\" height=\"150\" />Дводенний семінар проводила Наталія Сиротич, катехит, дитячий педагог-психолог.</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;Відгуки катехитів про семінар</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Мали нагоду бути разом, вчитись і знаходити спільну думку, навчатись новим методам катехизації, співвідносячи здобуті знання із власним досвідом. </em></span></p>\r\n<p style=\"text-align: right;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>с. Євстахія Романчук, ЧСВВ (м. Житомир)</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Семінарі був цікавим, веселим, пізнавальним, щирим, душевним. Вдалося освоїти багато нових методів праці з дітьми, підлітками та молоддю.</em></span></p>\r\n<p style=\"text-align: right;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Наталія Борис (м. Козятин Вінницької обл.)</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em><img style=\"float: left;\" src=\"image,4522,200,IMG_0467.JPG\" alt=\"\" width=\"200\" height=\"150\" />Усі методи катехизації, яких ми навчилися, чудові та доступні. Ми, учасники семінару, мали змогу побувати не лише в ролях катехитів, але й зіграти звукорежисерів, фокусників та лікарів, що роздають вітаміни віри. Дякую!</em></span></p>\r\n<p style=\"text-align: right;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>с. Бернадета (м. Житомир)</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>За час семінару отримали стільки корисної інформації і зарядилися такою великою кількістю енергії, що хотілося одразу бігти у катехитичний клас до дітей і застосовувати усі знання на практиці. Цього року їду катехитом на дитячий літній табір, і тепер не можу дочекатися дня поїздки, щоб застосувати на практиці усі знання та навички, здобуті на семінарі.</em></span></p>\r\n<p style=\"text-align: right;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Галина Притиск (м. Київ)</strong></span></p>',NULL,NULL,3039,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3277,'P','2013-03-17',1,NULL,'Note',' HABEMUS PAPAM! HABEMUS PAPAM FRANCISCUM!','<h3 class=\"news-title\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4524,200,izbranie-papy-francyska-14-19-990x660.jpg\" alt=\"\" width=\"200\" height=\"133\" />Трансляція Інавгураційної Святої Меси Папи Римського Франциска на Першому Національному.</span></h3>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Інавгураційна Свята Меса Папи Римського Франциска на площі</strong>&nbsp;<strong>Святого Петра у Римі.</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;<strong>19 березня з 12:30 в ефірі Першого Національного.</strong></span></p>','<h3 class=\"news-title\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4524,200,izbranie-papy-francyska-14-19-990x660.jpg\" alt=\"\" width=\"200\" height=\"133\" />Трансляція Інавгураційної Святої Меси Папи Римського Франциска на Першому Національному.</span></h3>\r\n<div>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;На нас чекає&nbsp;велична світова подія &ndash;&nbsp;<strong>Інавгураційна Свята Меса Папи Римського Франциска на площі</strong>&nbsp;<strong>Святого Петра у Римі.</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;<strong>19 березня з 12:30 в ефірі Першого Національного &ndash; майже три години спільної молитви українських вірян з християнами світу у центрі Європи.</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Коментують інтронізацію головний редактор Першого Національного телеканалу&nbsp;Христина Стебельська&nbsp;та о.&nbsp;Сергій Панченко.</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Помолимося разом за Папу, зі служінням якого Україна пов&rsquo;язує добрі надії!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp; Надія Петруньок, Літредактор головної редакції НТКУ</span></p>\r\n</div>',NULL,NULL,3041,5,1,43),
(3278,'P','2013-03-17',1,NULL,'Rss','Товариство Ісуса - єзуїти','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4523,200,000d5q5e.jpg\" />Формула Інституту Товариства Ісуса, затверджена Папою Юлієм ІІІ 21 липня 1550 р.</strong></p>\n<p>«Хто прагне служити як Божий воїн під стягом хреста у нашому  Товаристві, яке бажаємо визначати іменем Ісуса, а також служити одному  лишень Господові та Церкві, Його нареченій, під проводом Римського  Понтифіка, Христового намісника на землі, склавши урочистий обіт  чистоти, повинен пам\'ятати те, про що йтиметься далі.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1046:tovarystvo-isusa-jezujity&catid=35:istorychni-postati-cat&Itemid=48',3042,NULL,1,26),
(3279,'P','2013-03-17',1,NULL,'Rss',' Великий покаянний канон Андрея Критського','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4525,200,38617.jpg\" />Святий Андрій Критський (7 ст.)  народився в Дамаску у сім’ї благочестивих християн. До семирічного віку  хлопчик був німим. Дар мови він отримав після причастя Святих Христових  Таїн. Після цього юнак допитливо вивчав Святе Письмо та богословські  науки. У віці чотирнадцяти років він прийняв постриг в обителі  преподобного Сави Освяченого у Єгипті.</p>\n<p>Святий Андрій вів суворе та цнотливе життя. Його покірність та  стриманість, чесноти та розум викликали подив.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1047:-11-bereznja-2012-velyky-pokajanny-kanon-andreja-krytskogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37',3043,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3280,'P','2013-03-17',1,NULL,'Note','Бог ніколи не втомлюється прощати. На молитві «Ангел Господній» з новообраним Папою Франциском','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4527,200,534x300_217830.jpg\" alt=\"\" width=\"200\" height=\"112\" />&laquo;О, браття і сестри, Боже обличчя &ndash; це обличчя милосердного батька, який завжди терпеливий. Чи ви задумувалися колись про Божу терпеливість, про терпеливість, яку Бог має до кожного з нас?&raquo;, &ndash; запитав Папа Франциск, наголошуючи, що Бог завжди терпеливий щодо нас і чекає, коли ми з розкаянням повернемося до Нього. Невтомно чекає. &laquo;Дрібка милосердя вчиняє світ менше холодним та справедливішим&raquo;</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4527,200,534x300_217830.jpg\" alt=\"\" width=\"200\" height=\"112\" />Не втомлюймося просити в Бога прощення. Цей заклик кілька разів прозвучав цієї неділі на площі Святого Петра, як також заохочення не осуджувати ближніх. </span><br /><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дві неділі поспіль вікно робочого кабінету Святішого Отця, яке виходить на площу Святого Петра, було замкненим. Але 17 березня 2013 р., опівдні, у вікні знову з&rsquo;явилася постать в білій сутані, яку з великим ентузіазмом привітав кількасот тисячний збір вірних, що заповнив площу Святого Петра, довколишні вулиці та площі, як також широку вулицю Кончіліаціоне, що веде до площі Святого Петра.</span><br /><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Папа Франциск вперше проказав з вірними Богородичну молитву &laquo;Ангел Господній&raquo;. Але перед тим звернувся з повчанням, в якому прокоментував римо-католицьке літургійне читання цієї неділі, в якому розповідається про те, як Ісус простив жінці-грішниці. Ця тема також актуальна в житті тих християн східної традиції, які 18 березня розпочинають Великий Піст.</span><br /><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Брати й сестри, доброго дня! Після першої зустрічі в середу, сьогодні знову маю змогу звернутися до всіх з привітанням! І я радий, що можу це зробити в неділю, в Господній день! Це гарно та важливо для нас, християн: зустрічатися в неділю, вітати одні-одних, спілкуватися, як ото сьогодні, на цій площі. Площі, яка завдяки засобам комунікації, має розміри світу&raquo;, &ndash; цими словами Святіший Отець розпочав своє слово. Коментуючи згадану євангельську розповідь, він зазначив, що нас, насамперед, вражає поведінка Ісуса, в Якого немає слів засуду, але &ndash; слова любові та заклик до навернення.</span><br /><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;О, браття і сестри, Боже обличчя &ndash; це обличчя милосердного батька, який завжди терпеливий. Чи ви задумувалися колись про Божу терпеливість, про терпеливість, яку Бог має до кожного з нас?&raquo;, &ndash; запитав Папа Франциск, наголошуючи, що Бог завжди терпеливий щодо нас і чекає, коли ми з розкаянням повернемося до Нього. Невтомно чекає. &laquo;Дрібка милосердя вчиняє світ менше холодним та справедливішим&raquo;, &ndash; вів далі Святіший Отець, цитуючи книжку кардинала Каспера, й додаючи, що ми потребуємо зрозуміти це Боже милосердя.</span><br /><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Далі Папа поділився спогадами про те, як одна бабця в Буенос-Айресі під час сповіді, з абсолютною впевненістю сказала йому, тоді молодому єпископові, що Бог все прощає. &laquo;Але звідки пані про це знає&raquo;, &ndash; запитав її єпископ, на що вона відповіла: &laquo;Якщо б Господь не прощав усе, то світ не існував би&raquo;. Це, за словами Святішого Отця, мудрість, яка походить від Святого Духа. &laquo;Не забуваймо ніколи цього слова: Бог ніколи не втомлюється прощати, ніколи!&raquo;, &ndash; закликав Папа Франциск, додаючи: &laquo;І ми також вчімося бути милосердними до всіх. Призиваймо заступництво Пресвятої Богородиці, Яка тримала в Своїх обіймах Боже Милосердя, Яке стало тілом&raquo;.</span></p>',NULL,NULL,3044,5,1,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3281,'P','2013-03-18',1,NULL,'Note','Канон прп. Андрея Критського','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4528,200,an_krit_big.jpg\" alt=\"\" width=\"200\" height=\"125\" />Один за одним розкриваються мої гріхи, що глибоко пов&rsquo;язані з незавершеною трагедією відносин людини з Богом; історія першого гріхопадіння &ndash; це моя особиста історія:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Я вчинив злочин первозданного Адама; я знаю, що я відкинутий від Бога і вічного Його Царства й насолоди через мої гріхи&hellip;</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4528,200,an_krit_big.jpg\" alt=\"\" width=\"200\" height=\"125\" />Канон починається із глибоко особистого крику:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Звідки почну оплакувати окаянного мого життя діяння? Або який покладу початок, Христе, нинішньому риданню?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Один за одним розкриваються мої гріхи, що глибоко пов&rsquo;язані з незавершеною трагедією відносин людини з Богом; історія першого гріхопадіння &ndash; це моя особиста історія:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Я вчинив злочин первозданного Адама; я знаю, що я відкинутий від Бога і вічного Його Царства й насолоди через мої гріхи&hellip;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Я втратив всі божественні дари:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Я опоганив одяг моєї плоті, опоганив те, що було, Спасе, за Твоїм образом і подобою. Я затьмарив душевну красу насолодами пристрастей. Нині я роздер першу мою одіж, яку мені на початку виткав Творець, і тому я лежу нагий&hellip;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/nJBBFrU1J0A\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/cdD-z9pW1OY\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/t_zvBQf1x5s\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/-XbQk1nzoGQ\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/BvUtv7kERQM\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/kugRWMfkbDc\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/obJHb9Ajn-0\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/rZlHWimulcI\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/qMHh9YtZGu8\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>\r\n<p><a href=\"http://www.youtube.com/playlist?list=PL104EE6E8C855BDAE\">http://www.youtube.com/playlist?list=PL104EE6E8C855BDAE</a></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>',NULL,NULL,3045,5,1,48);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3282,'P','2013-03-18',1,NULL,'Rss','Зустріч (18 березня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4528,200,0000t8t4.jpg\" />Семирічний Максиміліян, сердитий, з руками в кишенях, вийшов з уроку релігії. Поводячи плечима, зневажливо пробурчав: «Тільки про одне говорять: Бог, Бог, Бог … Чому не розкажуть про щось цікаве? Про футбол чи рок-групи?»</p>\n<p><strong>«Почуй мене, Господи!</strong></p>\n<strong> </strong>\n<p><strong>Мені розповідали, що Тебе нема, а я, дурень повірив у це.</strong></p>\n<strong> </strong>\n<p><strong>&nbsp;</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1048:zustrich-18-bereznja&catid=1:mystecka-storinka-cat&Itemid=50',3046,NULL,1,26),
(3283,'P','2013-03-18',1,NULL,'Rss','Богослужіння Великого посту','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4529,200,pist.jpg\" />Чотиридесятниця</strong></p>\n<p>Великий піст –&nbsp;зовсім особливий&nbsp; час&nbsp;церковного року, насичений&nbsp;не  частими богослужіннями, більшість яких відбуваються тільки в цей період  церковного року. Народжений з практики підготовки катехуменів до  Хрещення, Великий піст став часом відродження й відновлення всіх  християн, що дотримуються візантійського церковного обряду.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1051:bogosluzhinnja-velykogo-postu&catid=30:hrystyjanske-vchennia-cat&Itemid=37',3047,NULL,1,26),
(3284,'P','2013-03-18',1,NULL,'Rss','Герб і девіз Папи Франциска','<p><img style=\"margin: 10px; float: right;\" src=\"http://media01.radiovaticana.va/imm/1_0_674601.JPG\" /></p>\n<p>Папа  Франциск вирішив залишити свій попередній єпископський девіз «Miserando  atque eligendo» та основні елементи герба, які він був вибрав з моменту  своїх єпископських свячень, що відзначається лінійною простотою.</p>\n<p>Голубий  щит увінчаний символами папської гідності, тими самими, які прийняв  його попередник Венедикт ХVІ – мітра між схрещеними золотим та срібним  ключами, пов’язаними червоним шнуром з китицями. У верхній частині герба  знаходиться емблема Товариства Ісусового, Ордену Єзуїтів, з якого  походить Папа: яскраве сяюче сонце, а посередині три латинські літери,  IHS, що є монограмою Христа. Над центральною буквою вони увінчані  хрестом, а під ними – три чорні цвяхи, символ Христових страждань.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1052:gerb-i-deviz-papy-francyska&catid=32:gk-svit-cat&Itemid=58',3050,NULL,1,26),
(3285,'P','2013-03-18',1,NULL,'Note','Відкрився сайт, присвячений Міжнародній всецерковній прощі до Патріаршого собору УГКЦ','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Розпочав свою роботу сайт&nbsp;<a href=\"http://proshcha.ugcc.org.ua/\" rel=\"nofollow\" target=\"_blank\">proshcha.ugcc.org.ua</a>, присвячений Міжнародній всецерковній прощі до Патріаршого собору в Києві, яка відбудеться 16-18 серпня 2013 року.</span></p>','<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Розпочав свою роботу сайт&nbsp;<a href=\"http://proshcha.ugcc.org.ua/\" rel=\"nofollow\" target=\"_blank\">proshcha.ugcc.org.ua</a>, присвячений Міжнародній всецерковній прощі до Патріаршого собору в Києві, яка відбудеться 16-18 серпня 2013 року.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сайт функціонує для зручної реєстрації паломників із України та з-за кордону.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На&nbsp;сайті&nbsp;можна ознайомитися з релігійними турами Україною, які пропонує Патріарший паломницький центр УГКЦ, переглянути програму паломництва до Патріаршого собору та, передовсім, зареєструватися на прощу, щоб спланувати свою поїздку до Києва. Про це повідомляє <a href=\"http://www.ugcc.org.ua/\" rel=\"nofollow\" target=\"_blank\">Департамент інформації УГКЦ</a>.</span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>',NULL,NULL,3051,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3286,'P','2013-03-18',1,NULL,'Rss','Послання Папи-емерита Бенедикта XVI на Великий Піст','<p><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4534,200,ceneri2010e.jpg\" />Віра в любов пробуджує любов: «Ми пізнали й увірували в ту любов, яку Бог до нас має» (1 Ів. 4,16)</em></p>\n<p>Дорогі брати і сестри,</p>\n<p>Відзначення Великого Посту в контексті Року Віри надає нам цінну нагоду  роздумати над відношенням між вірою і любов’ю: між вірою в Бога – Бога  Ісуса Христа – і любов’ю, яка є плодом Святого Духа і яка веде нас  шляхом посвячення себе Богові та іншим людям.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1053:poslannja-papy-emeryta-benedykta-xvi-na-velyky-pist&catid=28:tema-nomera-cat&Itemid=2',3052,NULL,1,26),
(3287,'P','2013-03-18',1,NULL,'Rss','«Чи був для Вас несподіваним вибір Конклаву?»','<p><em><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4534,200,svyatoslav_u_klobuku.jpg\" />«Чи був для Вас несподіваним вибір Конклаву?»</strong></em> – з цього запитання розпочався брифінг Блаженнішого Святослава Шевчука,  Глави УГКЦ, приурочений обранню Папи Франциска. Зустріч з журналістами  відбулася ввечері, 14 березня 2013 р. Блаженніший Святослав розповів:</p>\n<p>– <em>Коли я почув проголошення імені нового Папи, був дуже зворушений.  Справді, Господь Бог дав мені можливість знати його особисто.</em></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1054:2013-03-18-20-44-19&catid=27:gk-v-ukrajini-cat&Itemid=57',3053,NULL,1,26),
(3288,'P','2013-03-18',1,NULL,'Rss','70 років із часу українсько-польського конфлікту на Волині','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_sheptyckyj.jpg.jpg\" width=\"218\" height=\"142\" /></strong>Цього  року виповнюється 70 років із часу українсько-польського конфлікту на  Волині. У Польщі та Україні по-різному інтерпретують події 1943-1944  років, під час яких потерпіло мирне населення. Намагання УГКЦ і РКЦ у справі Волинської трагедії  зробити спільну заяву до вірних зазнало невдачі.<strong> </strong></p>\n<p>«УГКЦ і РКЦ намагалися зробити спільну заяву, але не вдалося виробити  \"єдиної позиції\" у тексті до вірних обох Церков», –&nbsp;пояснив Митрополит  Львівський Мєчислав Мокшицький. Архиєпископ зауважив, що в Україні мало  говорять про події на Волині.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1055:70-rokiv-iz-chasu-ukrajinsko-polskogo-konfliktu-na-volyni&catid=27:gk-v-ukrajini-cat&Itemid=57',3054,NULL,1,26),
(3289,'P','2013-03-19',1,NULL,'Rss','Цап-відбувайло (19 березня)','<div class=\"itemIntroText\">\n<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4528,200,1_1220.JPG\" />Десятеро селян ішли дорогою на свої  поля. Несподівано налетів ураган. Він несамовито шарпав дерева, земля  здригалася і глухо стугоніла.</p>\n</div>\n<p>Утікаючи від грому і граду, люди заховалися в руїнах старого замку.</p>\n<p>Світло блискавиць, спалахуючи то тут, то там, творило страхітливий  танець. Наростав шум вітру, наближався гуркіт грому. Селяни добряче  налякалися, і поміж них прошелестів шумок, що, мовляв, серед них мусить  бути грішник, який стягнув на всіх це шаленство природи.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1049:cap-vidbuvalo-19-bereznja&catid=1:mystecka-storinka-cat&Itemid=50',3055,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3290,'P','2013-03-19',1,NULL,'Rss',' Не бійтеся бути добрими і ніжними, – проповідь Папи під час інавгураційної Меси','<p><img style=\"margin: 10px; float: left;\" alt=\"Папа Франциск\" src=\"http://www.credo-ua.org/wp-content/uploads/2013/03/%D0%BF%D0%B0%D0%BF%D0%B0-%D0%A4%D1%80%D0%B0%D0%BD%D1%86%D0%B8%D1%81%D0%BA.jpg\" height=\"142\" width=\"211\" /></p>\n<p><strong>Інавгураційна Меса Папи Франциска, яка триває на площі Святого  Петра у Ватикані, зібрала десятки тисяч вірних, 130 офіційних делегацій,  серед яких 30 президентів країн. Вперше майже за тисячу років в  інавгурації бере участь Патріарх Константинополя.</strong></p>\n<p>Свою проповідь Папа Франциск виголосив для усіх людей доброї волі, а не  лише для християн. Святіший Отець говорив про покликання зберігати  красу світу, яке стосується усіх людей.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1057:-ne-bitesja-buty-dobrymy-i-nizhnymy-propovid-papy-pid-chas-inavguracinoji-mesy&catid=32:gk-svit-cat&Itemid=58',3056,NULL,1,26),
(3291,'P','2013-03-19',1,NULL,'Rss','Триденні реколекційні науки(14-16 березня) на тему: «Вірую, Господи, допоможи моєму невірству» (Мк. 9, 24)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4540,200,0101.jpg\" />Час Великого Посту, є можливістю наблизитись до Бога, відкрити наші серця для його світла, для цього в першу чергу необхідне взаємне порозуміння і прощення ближнього, тому напередодні церква святкує Неділю Всепрощення. За традицією, якої дотримувались чернечі згромадження у монастирях, ми просимо один у одного вибачення своїх провин у наших спільнотах.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1058:2013-03-19-13-52-41&catid=25:parafija-cat&Itemid=27',3058,NULL,1,26),
(3292,'P','2013-03-19',1,NULL,'Rss','Святі Отці Церкви про Покаяння','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4541,200,media-219146-2.jpg\" />Преподобний Єфрем Сирійський</strong>: «Оскарження себе  самого»: «Не хочу підкорятися, але люблю, щоби мені підкорялися. Не  хочу, щоб мене ображали, та ображаю інших. Не люблю, коли мене  звинувачують, але часто звинувачую інших. Не хочу слухати критики, проте  люблю критикувати. Не хочу прощати, але шукаю прощення. Не прагну  розуміти інших, однак домагаюся, щоби мене розуміли. Не хочу виконувати  наказів, та люблю їх давати. Мудрий я на те, щоби давати поради, але не  на те, щоб їх виконувати. Вказую, що треба робити, а роблю те, що не  треба».\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1059:svjati-otci-cerkvy-pro-pokajannja&catid=28:tema-nomera-cat&Itemid=2',3059,NULL,1,26),
(3293,'P','2013-03-19',1,NULL,'Rss','Правила посту в Українській Греко-Католицькій Церкві ','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4541,200,rosary2.jpg\" />Піст в практиці Церкви існує від самих початків. Але з бігом часу кожна  із конфесій встановила свою практику посту. Слід однак пам’ятати, що  основна мета посту – духовна, тобто, насамперед, потрібно постити від  гріха, злих схильностей. У дні посту необхідно стримуватись від лихих  діл, думок, слів; зберігати погідність духу, внутрішній мир та радість.  Стримування від гріха набагато важливіше ніж стримування від їжі, хоча  це також необхідне та допомагає поборювати гріх, вправлятись у духовному  зрості, духовному житті. Нам же потрібно пам’ятати та виконувати і одне  і друге.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1060:pravyla-postu-v-ukrajinski-greko-katolycki-cerkvi-&catid=30:hrystyjanske-vchennia-cat&Itemid=37',3060,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3294,'P','2013-03-19',1,NULL,'Note',' «Можу запевнити вас, що це зовсім не піар», – Блаженніший Святослав про поведінку Папи Франциска ','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Вміння бути собою та надзвичайна простота &ndash; риси, які найбільше характеризують нового Папу&raquo;, &ndash; сказав в інтерв&rsquo;ю Блаженніший Святослав. Саме &laquo;простота та вміння бути собою&raquo;, на переконання Глави УГКЦ, будуть шляхом нової євангелізації в часи понтифікату Папи Франциска.</span></p>','<div class=\"news-single-img\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Таке переконання висловив Блаженніший Святослав (Шевчук) американській інформаційній агенції CNS.&laquo;Папа показує себе таким, яким він є насправді як людина, пастир, священик, єпископ та батько&raquo;, &ndash; зазначив Предстоятель УГКЦ.</span></div>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Вміння бути собою та надзвичайна простота &ndash; риси, які найбільше характеризують нового Папу&raquo;, &ndash; сказав в інтерв&rsquo;ю Блаженніший Святослав. Саме &laquo;простота та вміння бути собою&raquo;, на переконання Глави УГКЦ, будуть шляхом нової євангелізації в часи понтифікату Папи Франциска.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Коментуючи жест новообраного Єпископа Рима, коли він вийшов до людей і вклонився перед ними, просячи їхнього благословення, Блаженніший Святослав сказав: &laquo;Це дуже давній літургійний жест, який до сьогодні зберігається у візантійській традиції: перед тим як кандидата висвячують на священика або єпископа, спільнота вірних мусить проголосити своє визнання \"axios\" (гідний)&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Предстоятель УГКЦ був багато разів на Богослужіннях, які очолював кардинал Бергольо: &laquo;Його проповіді завжди були короткими, але повчальними&raquo;, &ndash; зазначив він.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Відповідаючи на запитання журналіста про ставлення нового Папи до інших конфесій, Верховний Архиєпископ зазначив, що Святіший Отець Франциск, без сумніву, буде &laquo;екуменічним Папою&raquo;: &laquo;Це випливає з його здатності будувати лише справжні, автентичні відносини з іншими людьми&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У ставленні Папи до сучасних моральних проблем, вважає Глава УГКЦ, &laquo;він буде захищати людську гідність, гідність сім&rsquo;ї та людської статевості, однак робитиме це, передусім, як пастир і батько. Досі кардинал Бергольо ніколи не виступав у ролі судді, він ніколи не захищав абстрактні правила, а турбувався про гідність людини&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Блаженніший Святослав переконаний, що Папа Франциск продовжуватиме дивувати нас і надалі та своєю простотою і автентичністю запропонує секуляризованому світові альтернативу нової євангелізації.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">За матеріалами <a href=\"http://www.catholicnews.com/\" target=\"_blank\">www.catholicnews.com</a></span></p>',NULL,NULL,3061,5,1,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3295,'P','2013-03-19',1,NULL,'Rss','Оберігати Божі дари, щоб запалити світло надії!','<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/63f70967fe.jpg\" height=\"96\" width=\"150\" /></div>\n<p>«Дорогі брати й сестри! Дякую Господу Богу за те, що можу  відслужити цю Святу Месу з нагоди початку Петрового служіння в день  пам’яті святого Йосифа, Обручника Пречистої Діви Марії та Небесного  покровителя Вселенської Церкви: це дуже багатий за змістом збіг. Крім  того, сьогодні іменини мого шанованого попередника: ми єднаємося з ним  молитвою, сповненою любові та вдячності», – цими словами Папа Франциск  розпочав свою проповідь під час інавгураційної Служби Божої, яку він  очолив на площі Святого Петра у вівторок, 19 березня 2013 року.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1061:oberigaty-bozhi-dary-shchob-zapalyty-svitlo-nadiji&catid=32:gk-svit-cat&Itemid=58',3062,NULL,1,26),
(3296,'P','2013-03-19',0,NULL,'Note','Інавгурація Папи Римського Франциска (відео)','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У Ватикані зійшов на престол Папа Франциск. Свідками інавгурації двохсот шітдесят шостого Понтифіка - першого єзуїта та представника Америки на Святому престолі - стали десятки тисяч вірян, що зібралися на площі Святого Петра.<br /></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У Ватикані зійшов на престол Папа Франциск. Свідками інавгурації двохсот шітдесят шостого Понтифіка - першого єзуїта та представника Америки на Святому престолі - стали десятки тисяч вірян, що зібралися на площі Святого Петра.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/Dr_B4naS-so\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/aH-imER8raE\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/uC_NSAjLfak\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>',NULL,NULL,3063,5,1,48),
(3297,'P','2013-03-19',1,NULL,'Rss','Челядник (20 березня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4528,200,IMG_1008.jpg\" />В епоху кам’яних замків і войовничих лицарів в обладунках, один відважний юнак вирішив стати ковалем. Він прийшов до кузні учнем і дуже швидко навчився таємниць ремесла. Умів користуватися різним інструментами, кував залізо на кувадлі, надимав ковальський міх. Хлопець став вправним майстром: він навчився виковувати гострі шаблі й легкі шоломи, що витримували будь-який удар, канделябри з тисячами рельєфів і міцні ґрати.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1050:cheljadnyk-20-bereznja&catid=1:mystecka-storinka-cat&Itemid=50',3064,NULL,1,26),
(3298,'P','2013-03-20',1,NULL,'Rss','«Ви є лист Христовий,  якого пізнають і читають усі люди»','<p data-ft=\"{&quot;type&quot;:1,&quot;tn&quot;:&quot;K&quot;}\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4546,200,doubts_about_bible.jpg\" />Звернімо  увагу на нас, а краще на те, як Бог давиться на мене і на тебе. «Ви є  лист Христовий, якого пізнають і читають усі люди» (II Кор. З, 2).</p>\n<p data-ft=\"{&quot;type&quot;:1,&quot;tn&quot;:&quot;K&quot;}\">Ці прості і глибокі слова звернено до нас з Вами. По нас можна  прочитати, в кого віримо, як живемо і чи вартує іти тими дорогами, якими  йдемо. Через кожного з нас Ісус Христос посилає свій лист світові -  там, де живемо: на своїй вулиці, у будинку, під\'їзді, на своїй парохії,  на роботі, в школі, в хорі, під час відпочинку чи святкування Дня  народження і навіть як ми десь вирушаємо в дорогу, - ми є «листом»  Христовим, якого відчитують люди.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1066:2013-03-20-21-19-35&catid=31:kolonka-redaktora-cat&Itemid=1',3065,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3299,'P','2013-03-20',1,NULL,'Rss','Хто більше значить (21 березня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4536,200,1034025-580x314.jpg\" />Був собі добрий і побожний брамин, який жив на пожертви вірних. Одного дня він подумав: «Переодягнутися в біснуватого і пуду просити милостиню».</p>\n<p>Опоясався ганчір’ям, як це роблять парії, найбідніші люди Індії.</p>\n<p>Того дня ніхто не вклонився йому на вулиці, ані не дав жодної милостині.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1056:hto-bilshe-znachyt&catid=1:mystecka-storinka-cat&Itemid=50',3066,NULL,1,26),
(3300,'P','2013-04-21',0,NULL,'Note','Чиніть діла милосердя і винагорода за вами буде','<p style=\"text-align: justify;\"><img style=\"float: left;\" src=\"image,4552,200,147.jpg\" alt=\"\" width=\"118\" height=\"103\" /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Парафіяльна служба милосердя &laquo;Карітас-Печерський&raquo; закликає кожного з Вас до спільної щирої молитви, посту душі, прояву милосердного милосердя.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;А як ти чиниш милостиню, хай не знатиме ліва рука твоя, що робить правиця твоя, щоб таємна була твоя милостиня, а Отець твій, що бачить таємне, віддасть тобі явно&raquo;. Матея Гл.6 (3-4)</span></p>\r\n<p>&nbsp;</p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4552,200,147.jpg\" alt=\"\" width=\"200\" height=\"200\" />&laquo;Стережіться виставляти свою милостиню перед людьми, щоб бачили вас; а як ні, то не матимете нагороди від Отця вашого, що на небі. Отож, коли чиниш ти милостиню, не сурми перед себе, як то роблять оті лицеміри по синагогах та вулицях, щоб хвалили їх люди. Поправді кажу вам: вони мають уже нагороду свою!&raquo;. Матея Гл.6 (1-2)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Парафіяльна служба милосердя &laquo;Карітас-Печерський&raquo; закликає кожного з Вас до спільної щирої молитви, посту душі, прояву милосердного милосердя.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">- складемо десятину на добру справу</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">- посадимо дерево, виноград, троянди на Аскольдовій горі</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">- зберемо подарунки дітям, матері яких перебувають у місцях позбавлення волі</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">- зберемо Великодній кошик для жінок в ув&rsquo;язненні (засоби гігієни, гостинці, тощо)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">- зберемо Пасхальні гостинці в школи-інтернат</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">- приготуємо Великодню паску для жінок, що перебувають у Лук&rsquo;янівському СІЗО</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4552,200,0_24b4b_27c6b772_XL.jpg\" alt=\"\" width=\"200\" height=\"133\" />- відвідаємо і допоможемо хворим, яких провідує о. Анатолій</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">- відвідаємо і допоможемо хворим діткам, яких провідує о. Олексій з молоддю</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;А як ти чиниш милостиню, хай не знатиме ліва рука твоя, що робить правиця твоя, щоб таємна була твоя милостиня, а Отець твій, що бачить таємне, віддасть тобі явно&raquo;. Матея Гл.6 (3-4)</span></p>',NULL,NULL,3069,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3301,'P','2013-03-21',1,NULL,'Note','Молодіжні чування','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4553,200,chuvannia_molodi.jpg\" alt=\"\" width=\"141\" height=\"200\" />\"Христе страждаючий змилуйся над нами\"</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> у програмі:</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> - велика вечірня</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> - вервиця</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> - хресна дорога зі свічками</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> - розважання над уривками зі святого письма</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> - сповідь</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> - Свята Літургія</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> - Ісусова молитва</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> - страсні пісні</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Присвяти ніч Богові, який за Тебе з любові життя своє віддав</span></p>','<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4553,200,chuvannia_molodi.jpg\" alt=\"\" width=\"141\" height=\"200\" />\"Христе страждаючий змилуйся над нами\"</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> у програмі:</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> - велика вечірня</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> - вервиця</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> - хресна дорога зі свічками</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> - розважання над уривками зі святого письма</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> - сповідь</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> - Свята Літургія</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> - Ісусова молитва</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> - страсні пісні</span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Присвяти ніч Богові, який за Тебе з любові життя своє віддав</span></p>\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">22 берзня 2013</span></p>\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">початок о 20:00<br /></span></p>\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">закінчення о 06:00<br /></span></p>',NULL,NULL,3070,5,1,41),
(3302,'P','2013-03-21',1,NULL,'Rss','Запрошення на банкет','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4552,200,Peisakh-2.jpg\" />Від раннього ранку виглядаю Тебе. Хочу запросити Тебе в подорож. Сьогодні&nbsp; хочу мандрувати з Тобою дорогою, котрої не знаєш, йти шляхом тих, хто в пошуку. Бажаю бо вивести Тебе із тісноти твоєї, що гніздиться у серці твоєму, у зачерствілих думках, в уявленнях про Мене, у вірі в Мене, у довір`ї Мені. Я з Тобою коли Ти мене виглядаєш, шукаєш, запитуєш, прагнеш, молишся, відкриваєш, промовляєш.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1073:zaproshennja-na-banket&catid=30:hrystyjanske-vchennia-cat&Itemid=37',3071,NULL,1,26),
(3303,'P','2013-03-22',1,NULL,'Rss','Гвинтик (22 березня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4546,200,Gendarme.jpg\" />У корпусі величезного корабля був вкручений гвинтик. Маленький і непримітний, він нічим не відрізнявся від багатьох інших, таких же пересічних гвинтиків, що з’єднували дві величезні сталеві плити.</p>\n<p>Під час довгої подорожі через Індійський океан маленький гвинтик усвідомив, що йому обридло це сіре і непомітне існування (за всі роки не знайшлося нікого, хто принаймі подякував би йому за працю). Гвинтик заявив: «Все, з мене досить! йду геть! »</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1062:gvyntyk-22-bereznja&catid=1:mystecka-storinka-cat&Itemid=50',3072,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3304,'P','2013-03-22',1,NULL,'Note','«Закликаю вас під час Великого посту звернути особливу увагу на потреби людей», - Блаженніший Святослав ','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4559,200,176660.jpg\" alt=\"\" width=\"200\" height=\"129\" />&laquo;Хтось може подумати, що Господь засуджує багатство і прославляє бідність. Убогі не менше за багатих потребують Бога, щоб спастися. Можливо, вони лише менше схильні покладатися на матеріальний добробут для осягнення свого щастя. Багатство вимагає відповідальності за те, щоб правильно розпоряджатися ним на благо інших, щоб служити їм, ділитися скарбами, які дарував Господь Бог&raquo;.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4559,200,176660.jpg\" alt=\"\" width=\"200\" height=\"129\" />&laquo;Закликаю вас під час Великого посту звернути особливу увагу на потреби людей у нашій країні, де існує провалля між багатими й бідними, де вечеря вдвох у престижному ресторані дорівнює місячному бюджетові сім&rsquo;ї пенсіонерів, а автомобіль преміум-класу &ndash; вартості квартири для кількох безпритульних. Адже милостиня є необхідною умовою справжнього посту&raquo;, - пише у <a href=\"http://ugcc.org.ua/2695.0.html\">Пастирському посланні</a> до вірних на Великий піст Блаженніший Святослав (Шевчук), Глава УГКЦ, зазначаючи, що до діл милосердя і милостині покликані всі християни, незалежно від своїх статків.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На його переконання, багаті люди в бідних країнах несуть особливу відповідальність і унікальну нагоду &ndash; малою пожертвою зробити велике діло, допомогти іншому змінити своє життя. &laquo;Варто пам&rsquo;ятати, що благодійність &ndash; це не лише грошові пожертви, а й присвячення іншим свого часу, уваги та ділення знаннями. Таким чином ми наближаємося до самого Творця&raquo;, - пише Блаженніший Святослав. Церква вчить, що багатство &ndash; це Божий дар, який є засобом для служіння, і воно може стати знаряддям на шляху до спасіння, якщо не є метою, а засобом. Багатство&nbsp; має цінність тоді, коли воно допомагає його власникові зростати духовно. Та водночас володіння багатством може бути небезпечним, як, зрештою, кожне рішення людини.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На думку Предстоятеля УГКЦ, матеріальні блага є відносними і другорядними, проте, щоб спатися не обов&rsquo;язково бути бідним: &laquo;Хтось може подумати, що Господь засуджує багатство і прославляє бідність. Убогі не менше за багатих потребують Бога, щоб спастися. Можливо, вони лише менше схильні покладатися на матеріальний добробут для осягнення свого щастя&raquo;. За словами Верховного Архиєпископа УГКЦ, земний достаток є покликанням до відповідальності за ближнього: &laquo;Багатство вимагає відповідальності за те, щоб правильно розпоряджатися ним на благо інших, щоб служити їм, ділитися скарбами, які дарував Господь Бог&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Департамент інформації УГКЦ</span></p>',NULL,NULL,3076,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3305,'P','2013-03-23',1,NULL,'Rss','Останнє місце (23 березня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4546,200,42079775_119474588450719500.jpg\" />Пекло було вже майже заповнене, однак перед його брамою очікувало ще багато грішників. Дияволу не залишилося жодного іншого виходу, як заблокувати двері. Він вийшов до кандидатів на вхід і промовив до них:</p>\n<p>«Залишилося тільки одне місце, і, ясна річ, його можна зайняти тільки той з вас, хто був найбільшим грішником».</p>\n<p>Потім запитав:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1063:ostannje-misce-23-bereznja&catid=1:mystecka-storinka-cat&Itemid=50',3077,NULL,1,26),
(3306,'P','2013-03-24',1,NULL,'Rss','Спокуса (24 березня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4546,200,2.jpg\" />Один пустельник був такий святий, що одною ногою вже, як мовлять, стояв у небі.</p>\n<p>Його печера була вибита в стіні високої, покритої зеленню гори, і в ній майже нічого не було. Пустельник збирав лісові ягоди, трави, а інколи до недільного обіду, - трохи грибів.</p>\n<p>«Як же його спокусити?» - сушив собі голову Диявол.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1064:spokusa-24-bereznja&catid=1:mystecka-storinka-cat&Itemid=50',3078,NULL,1,26),
(3307,'P','2013-03-25',1,NULL,'Rss','Достатньо (25 березня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4546,200,0_df0_1e67d815_L.jpg\" />В одному краї сталася велика посуха. Спершу пожовкла, а згодом і висохла трава. Зів’яли й поникли кущі та дерева. Не було жодної краплі дощу з неба, ані жодної рослинки із землі.</p>\n<p>Малі й великі звірі гинули від спраги. Тільки найсильніші спромоглися на рішучий крок і залишали цю пустелю смерти.</p>\n<p>А посуха і далі лютувала. Навіть старі й могутні дерева з глибоким корінням втратили листя. Повисихали джерела, русла струмків і річок.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1065:dostatno-25-bereznja&catid=1:mystecka-storinka-cat&Itemid=50',3079,NULL,1,26),
(3308,'P','2013-03-25',1,NULL,'Rss','ДИВНІ РЕАКЦІЇ МОСКОВСЬКОГО І НЕ ЗОВСІМ МОСКОВСЬКОГО ПРАВОСЛАВ’Я НА ОБРАННЯ НОВОГО ПАПИ','<p>Обрання кардинала Хорхе МаріоБергольйо єпископом Риму останніми тижнями  активно коментували представники найрізноманітніших організацій,  товариств, лобі, груп і конфесій. Коментарі представляли щонайширшу  палітру поглядів, почуттів, очікувань і страхів: від безмежного  захоплення до безмежного засудження, від чемних протокольних фраз до  серйозних спроб аналізу ситуації.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1074:dyvni-reakciji-moskovskogo-i-ne-zovsim-moskovskogo-pravoslavja-na-obrannja-novogo-papy&catid=32:gk-svit-cat&Itemid=58',3080,NULL,1,26),
(3309,'P','2013-03-26',1,NULL,'Rss','Заповіді 104-річного Андрія Ворона','<p><strong><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4565,200,3_1_0.jpg\" />«Десятиліттями я живу в одному місці. Роблю одну роботу. Веду бесіди з одними людьми. Сідаю за той же стіл у той же час і їм майже однакову їжу. Лягаю в один час в одне ліжко...Минають літа, але ніщо не міняється — ні довкілля, ні я. Бо не міняється моя душа. І це добре. Дивлюся на образи діда, батька, на своїх онуків і правнуків - і здається, що я на цьому світі був завжди. І буду вічно...» </em></strong></p>\n<p><strong><em> </em></strong></p>\n<p align=\"right\"><strong><em>Андрій Ворон</em></strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1075:zapovidi-104-richnogo-andrija-vorona&catid=37:spilnota-cat&Itemid=49',3083,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3310,'P','2013-03-26',1,NULL,'Rss','\"Панна Бідність\" та сучасна церква','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4567,200,p_54575_1_gallerybig.jpg\" />Вислів \"панна Бідність\", за легендою, належить католицькому святому  Франциску Ассизському. Адже вийшовши із доволі небідної родини, той жив  милостинею та проповідував смиренність і каяття. Але й не гребував, до  речі, важкою селянською працею, коли допомоги в ній потребували віряни.</p>\n<p>Тому самому, що не мав ані взуття, ані змінного одягу – ходив у рубищі, підпоясаний шматком мотузки.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1076:qpanna-bidnistq-ta-suchasna-cerkva&catid=27:gk-v-ukrajini-cat&Itemid=57',3085,NULL,1,26),
(3311,'P','2013-03-26',1,NULL,'Rss','Є проблема: від понеділка до суботи жити тим, чим люди наповнюються в церкві в неділю','<p><em><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4567,200,16FEDE43-BC3B-45FC-98FD-6ABCCA478AEA_w640_r1_s.jpg\" /></strong>Нещодавно  Архиєпископ Любомир (Гузар) відзначив 80-літній ювілей. З цієї нагоди  (неважливо — напередодні дати чи після) хотілося з ним говорити про живу  віру і любов — те, на що він найбільше звертав нашу увагу впродовж  десяти років свого патріаршого служіння. Звертає і тепер.</em></p>\n<p><em>Зрештою, владика володіє харизмою любові, у чому багато хто сам переконався. А, як відомо, любов і жива віра притягують. </em></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1077:ljubomyr-guzar-qje-problema-vid-ponedilka-do-suboty-zhyty-tym-chym-ljudy-napovnjujutsja-v-cerkvi-v-nediljuq&catid=31:kolonka-redaktora-cat&Itemid=1',3086,NULL,1,26),
(3312,'P','2013-03-26',1,NULL,'Rss','Троянда (27 березня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4548,200,normal_00014_11052008.jpg\" />Німецький поет Рільке якийсь час жив у Парижі. Щодня дорогою до університету він разом зі своєю приятелькою француженкою переходив дуже людну вулицю.</p>\n<p>На розі цієї вулиці сиділа вже старша жінка і просила милостиню у перехожих - завжди на тому самому місці, нерухомо, як статуя, з простягнутою рукою й опущеними до землі очима.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1068:trojanda-27-bereznja&catid=1:mystecka-storinka-cat&Itemid=50',3087,NULL,1,26),
(3313,'P','2013-03-27',1,NULL,'Rss','Де сходяться небо із землею (28 березня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4548,200,1VRxNWW9snQ.jpg\" />У старовинній книзі з бібліотеки монастиря два монахи прочитали, що на краю світу є таке місце, де сходяться небо з землею. І ось вони вирішили відшукати його, пообіцявши, що не повернуться доти, доки не знайдуть.</p>\n<p>Мандруючи по світу, вони наражалися на безліч небезпек, витримували спеку і холод, терпіли страшні злигодні, які часто супроводжують паломництво. Долали тисячі перешкод, які спроможні були відвернути людину від її мети. Та все-таки монахи витримали.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1069:de-shodjatsja-nebo-iz-zemleju-28-bereznja&catid=1:mystecka-storinka-cat&Itemid=50',3093,NULL,1,26),
(3314,'P','2013-03-28',1,NULL,'Rss','Не сперечаймося про те, хто більше завинив','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4577,200,Dopomoga.jpg\" />Про минуле, зокрема, оте жорстоке й довговічне, можна міркувати по-різному: тямкувати про власні кривди, леліяти мрії про помсту, плекати біль і культивувати ненависть до переслідувачів. Можна також – а це далебі важче – переступити крейдове коло взаємної недовіри й ворожості, щоб створити інше майбутнє в ім’я порозуміння й примирення.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1078:ne-sperechamosja-pro-te-hto-bilshe-zavynyv&catid=37:spilnota-cat&Itemid=49',3096,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3315,'P','2013-03-27',1,NULL,'Note','Потрібна інвалідна коляска 2-х річній дівчинці!','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4571,200,dsc_0997.jpg\" alt=\"\" width=\"74\" height=\"101\" />&nbsp; &nbsp; Їй всього 2 роки, але вона не може ходити через цілий перелік діагнозів: синдром рухових порушень, гіпотрофія ІІ ступеня, дефіцитна анемія І ступеня.</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp; &nbsp; Дівчинка проживає в багатодітній малозабезпеченій сім&rsquo;ї. Батьки не в змозі купити дівчинці інвалідну коляску. Через це її старші сестричка Сніжана і братик Дмитрик (9 і 7 років) носять її на руках. Ще у Олі є молодший братик Назар, якому 5 місяців. </span></p>','<p style=\"text-align: justify;\"><img style=\"float: right;\" src=\"image,4571,200,dsc_0997.jpg\" alt=\"\" width=\"147\" height=\"200\" /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp; &nbsp; Їй всього 2 роки, але вона не може ходити через цілий перелік діагнозів: синдром рухових порушень, гіпотрофія ІІ ступеня, дефіцитна анемія І ступеня.</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp; &nbsp; Дівчинка проживає в багатодітній малозабезпеченій сім&rsquo;ї. Батьки не в змозі купити дівчинці інвалідну коляску. Через це її старші сестричка Сніжана і братик Дмитрик (9 і 7 років) носять її на руках. Ще у Олі є молодший братик Назар, якому 5 місяців.&nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Багатодітна сім&rsquo;я ледве зводить кінці з кінцями, а бідна дівчинка весь час лежить чи сидить на дивані і, скоріше за все, вже ніколи не зможе ходити.</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> &nbsp;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp; Друзі, давайте не залишимось байдужими і разом придбаємо для Олі інвалідний візок,</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> щоб вона мала змогу самостійно пересуватись.</span><br /><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <strong>&nbsp; &nbsp;Сума до збору &ndash; 3 980</strong> <strong>гривень.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp; Контактна особа: Михалевич Олена (050) 416-06-13 </span><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Інвалідний коляску плануємо купити ось таку</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> <a href=\"http://med-magazin.com.ua/item_N1023.htm\" rel=\"nofollow\">http://med-magazin.com.ua/item_N1023.htm</a></span></p>',NULL,NULL,3088,5,1,19),
(3316,'P','2013-03-28',1,NULL,'Rss','Струни челлі','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4577,200,92700188.jpg\" />Я сьогодні ніяк не змогла настроїти інструменту. Всі мої намагання хоч якось зарадити справі, довготривалі старання підтягнути струни не увінчалися успіхом. І то вже не лише один кілочок, а й другий був таким неслухняним: щоразу, коли його підтягувала так і вислизав з-під пальців. Я була безпорадною і просто безсилою що-небудь змінити. Струни видавали такий спотворений звук, наче жалілися мені чи то на мене. Та все ще все мало бути попереду.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1079:struny-chelli&catid=1:mystecka-storinka-cat&Itemid=50',3099,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3317,'P','2013-03-27',1,NULL,'Note','Зустрічайте величне свято Великодня разом із Першим Національним!','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На Першому Національному телеканалі України розпочинаються Великодні трансляції з Рима. Українці зможуть долучитися до молитви Вселенської Церкви разом з християнами всього світу.</span></p>','<p class=\"xfmc3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Упродовж 20 років найбільші християнські свята &ndash; в ефірі державного телеканалу України. &nbsp;</span></p>\r\n<p class=\"xfmc3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Майже півтора мільярда католиків, зокрема й в Україні, очікують на Святу Месу під проводом <strong>Папи Римського Франциска</strong>.</span></p>\r\n<p class=\"xfmc3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На Першому Національному телеканалі України розпочинаються Великодні трансляції з Рима. Українці зможуть долучитися до молитви Вселенської Церкви разом з християнами всього світу.</span></p>\r\n<p class=\"xfmc3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>29 березня &ndash; Велика П&rsquo;ятниця</strong></span></p>\r\n<p class=\"xfmc3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><span style=\"text-decoration: underline;\">22:15 &ndash; 00:00</span></strong> &ndash; трансляція <strong>Хресної Дороги в Колізеї </strong>за участю Святішого Отця Франциска. Долучіться до Хресної ходи та спізнайте розважання кожної Стації!</span></p>\r\n<p class=\"xfmc3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>31 березня &ndash; Неділя Воскресіння Господнього </strong></span></p>\r\n<p class=\"xfmc3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><span style=\"text-decoration: underline;\">11</span></strong><strong><span style=\"text-decoration: underline;\">:15 &ndash; 13:00</span></strong> &ndash; транслюватиметься <strong>Свята Великодня Меса</strong>.</span></p>\r\n<p class=\"xfmc3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><span style=\"text-decoration: underline;\">13:00 &ndash; 13:40</span></strong> &ndash; Апостольське Благословення Святішого Отця для Рима і цілого світу<strong>&laquo;</strong><strong>Urbi et Orbi</strong><strong>&raquo;</strong>. &nbsp;</span></p>\r\n<p class=\"xfmc3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Коментують:</span></p>\r\n<p class=\"xfmc3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Головний редактор Першого Національного телеканалу <strong><em>Христина Стебельська</em></strong> та докторант Григоріанського університету о. <strong><em>Олександр Дасік</em></strong>.&nbsp;&nbsp;</span></p>\r\n<p class=\"xfmc3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Просимо завчасно оголосити про трансляції під час храмових богослужінь та розмістити інформацію на Вашому сайті.</em></span></p>\r\n<p class=\"xfmc3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Україна &ndash; під Благословенням Святішого Отця!</span></p>',NULL,NULL,3090,5,1,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3318,'P','2013-03-30',1,NULL,'Note','Світлої пам’яті брата Ігоря Богуша','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">29 березня парафіян разом із отцем Ігорем Онишкевичем провадили спільну молитву на спомин Брата Ігоря Богуша. Три роки тому брат Ігор відійшов у вічність по заслужену нагороду. &laquo;Добре мати друзів і на небі, й на землі. Варто, щоб першим серед них завжди був Христос&raquo;. (О. Рафаїл Бухінгер. Любов як Бог).</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">29 березня парафіян разом із отцем Ігорем Онишкевичем провадили спільну молитву на спомин Брата Ігоря Богуша.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Три роки тому брат Ігор відійшов у вічність по заслужену нагороду. &laquo;Добре мати друзів і на небі, й на землі. Варто, щоб першим серед них завжди був Христос&raquo;. (О. Рафаїл Бухінгер. Любов як Бог). Брат володів особливим даром, духовним шляхом - віддане служіння ближньому. Брат Ігор був чуйним і переживав за спасіння хворих, намагався кожному донести слово Боже, показати Його бездоганну любов.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Рідні, друзі щоразу згадують його вдачу чистої віри і довіри, розуміння і поваги до загубленого, його постійної готовності допомогти, навернути, віддати усього себе.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Світла і вічна пам\'ять брату Ігорю.</span></p>',NULL,NULL,3106,5,1,17),
(3319,'P','2013-03-27',1,NULL,'Note',' У другу суботу Великого посту в УГКЦ молитимуться за абортованих дітей ','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4575,200,28624_600.jpg\" alt=\"\" width=\"141\" height=\"86\" />Організатори акції сподіваються, що багато людей, які не знали правди про аборти, усвідомивши цінність людського життя, зможуть стати на захист життя зачатих дітей, яким загрожує знищення - молитися і працювати, щоб таке явище, як аборт не мало місця в українському суспільстві.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4575,200,28624_600.jpg\" alt=\"\" width=\"200\" height=\"122\" />30 березня в Українській Греко-Католицькій Церкві відзначатимуть День пам&rsquo;яті абортованих дітей. Ще в 2009 році Синод Єпископів УГКЦ затвердив документ &laquo;Програма протидії абортам в Україні Української Греко-Католицької Церкви&raquo;, у якому пропонує запровадити практику відзначати День &nbsp;пам&rsquo;яті абортованих у другу суботу Великого посту, а також День подяки за дар життя 7 квітня, на свято Благовіщення.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З 30 березня до 7 квітня 2013 року на Львівщині проходитиме акція &laquo;Тиждень за життя&raquo;. Цього року ініціативу ЛОГО Товариство &bdquo;Рух за життя&rdquo; підтримало духовенство УГКЦ, Асоціація лікарів-католиків, спільноти УМХ, ТУСК &bdquo;Обнова&rdquo;, інші громадські та релігійні організації. Подібні акції проходитимуть також в інших областях України.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4575,200,or2zj4mz.gif\" alt=\"\" width=\"200\" height=\"138\" />Протягом цих днів усі небайдужі матимуть нагоду долучитися до молитви за дітей, навмисно вбитих у лоні матері, за всіх, зранених через аборти, випрошуючи в Бога благодать зцілення, прощення та примирення.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Організатори акції сподіваються, що багато людей, які не знали правди про аборти, усвідомивши цінність людського життя, зможуть стати на захист життя зачатих дітей, яким загрожує знищення - молитися і працювати, щоб таке явище, як аборт не мало місця в українському суспільстві.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">За матеріалами РІСУ</span></p>',NULL,NULL,3092,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3320,'P','2013-03-31',1,NULL,'Rss','Те що... (31 березня)','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4548,200,00237905.jpg\" />Те, що нам казали, коли ми були дітьми:</strong></p>\n<p>Не біжи; йди поволі; швидко; їж усе; мий руки; чисти зуби; мовчи; говори; перепроси; привітайся; ходи сюди; відчепись від мене; йди бавитися; не перешкоджай; не біжи; вважай, бо впадеш; тим гірше для тебе; ти не вмієш; ти замалий; я сам зроблю; ти вже великий; йди спати; вставай, вже пізно; я маю роботу; бався сам; одягнися; не стій на сонці; йди на сонце; не розмовляй з повним ротом...</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1072:te-shcho-31-bereznja&catid=1:mystecka-storinka-cat&Itemid=50',3107,NULL,1,26),
