INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3323,'P','2013-04-06',1,NULL,'Note','У Львові представлять диск, у якому містяться унікальні стародавні напіви богородичних догматів восьми гласів','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4581,200,78684904.jpg\" alt=\"\" width=\"57\" height=\"82\" />Цей диск є першим у серії &laquo;Традиція українського церковного співу&raquo; в рамках проекту Благодійного фонду імені Митрополита Андрея (Шептицького). Така серія дисків, що випускатимуться і далі,&nbsp; має на меті ознайомити широке коло людей зі скарбами нашої молитовної та духовно-мистецької спадщини та сприяти відновленню традиції літургійного співу.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4581,200,78684904.jpg\" alt=\"\" width=\"141\" height=\"200\" />6 квітня 2013 року, напередодні свята Благовіщення Пресвятої Богородиці, у храмі Святих Верховних апостолів Петра і Павла УГКЦ у Львові (вул. Театральна, 11) відбудеться презентація диска \"Передсущий явився\", у якому містяться напіви богородичних догматів восьми гласів у виконанні учасників хору священиків Львівської митрополії УГКЦ &laquo;Глас&raquo; - о. Руслана Греха та д-на Назарія Яруніва. Духовний вечір розпочнеться о 19.00 Великою вечірнею з полієлеєм, яку очолить владика Венедикт (Алексійчук), Єпископ-помічник Львівської архиєпархії УГКЦ. Після Вечірні учасники крилосного хору &laquo;Глас&raquo; виконають кілька унікальних стародавніх напівів із диска \"Передсущий явився\".</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Додамо, що мелодії церковних піснеспівів, записані на диску &laquo;Передсущий явився&raquo;, належать до духовної спадщини української церковної монодії. Цей диск є першим у серії &laquo;Традиція українського церковного співу&raquo; в рамках проекту Благодійного фонду імені Митрополита Андрея (Шептицького). Така серія дисків, що випускатимуться і далі,&nbsp; має на меті ознайомити широке коло людей зі скарбами нашої молитовної та духовно-мистецької спадщини та сприяти відновленню традиції літургійного співу. Метою діяльності Благодійного фонду імені Митрополита Андрея Шептицького, за сприяння якого був випущений диск, є збереження та поширення спадщини Митрополита Андрея для розвитку Церкви, а також творення і утвердження громадянського суспільства в Україні.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Контактна особа: Анастасія Войтюк, +38(066)6261357</span></p>',NULL,NULL,3098,5,1,42),
(3324,'P','2013-03-31',1,NULL,'Rss','Реєстрація групи Догнала бумерангом вдарить по державі','<p style=\"text-align: justify;\"><em><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_29-03-13-6.jpg.jpg\" height=\"149\" width=\"129\" />Священик УГКЦ о. Орест Вільчинський ще з 2004 року уважно слідкує за діяльністю</em><em> </em><em><a href=\"http://risu.org.ua/ua/index/resourses/directory/%7E%D0%94/48515/\" target=\"_blank\">релігійного угрупування Антоніна Догнала</a>,  відомим ще як «підгорецькі» отці. Багатьох людей, які потрапили до  цього угрупування, йому вдалося врятувати особисто. За це догналіти його  піддали анафемі.</em><em> </em></p>\n<p style=\"text-align: justify;\"><em>Щоправда, священика це не зупинило. Навпаки. Він з подвійною  енергією почав викривати «підгорецьких» отців та допомагати людям, які  мають з ними клопоти.</em></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1082:rejestracija-grupy-dognala-bumerangom-vdaryt-po-derzhavi&catid=27:gk-v-ukrajini-cat&Itemid=57',3108,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3325,'P','2013-03-28',0,NULL,'Note','«Найбільший атеїст, який тільки може бути в Україні, не зробив би цього одноосібно», - владика Йосиф Мілян про події на Черкащині ','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4583,200,0330.jpg\" alt=\"\" width=\"200\" height=\"150\" />&laquo;Аналізуючи акти вандалізму, що відбулися в липні минулого року в Золотоноші, і те, що сталося цієї ночі, можна сказати, що зло активізується тоді, коли у світі і в Церкві відчувається благодать Святого Духа. Мені шкода тих людей, які піддаються злу і чинять такі злочини. Не менше шкода тих спільнот і організацій, які за цим стоять. Переконаний, що найбільший атеїст, який тільки може бути в Україні, не зробив би цього одноосібно&raquo;, - сказав (<a href=\"http://ugcc.org.ua/fileadmin/user_upload/audio/vl_Yosyf_pro_Zolotonoshu.WMA\" target=\"_blank\">слухати</a>) владика Йосиф Мілян</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4583,200,0330.jpg\" alt=\"\" width=\"200\" height=\"150\" />&laquo;Аналізуючи акти вандалізму, що відбулися в липні минулого року в Золотоноші, і те, що сталося цієї ночі, можна сказати, що зло активізується тоді, коли у світі і в Церкві відчувається благодать Святого Духа. Мені шкода тих людей, які піддаються злу і чинять такі злочини. Не менше шкода тих спільнот і організацій, які за цим стоять. Переконаний, що найбільший атеїст, який тільки може бути в Україні, не зробив би цього одноосібно&raquo;, - сказав (<a href=\"http://ugcc.org.ua/fileadmin/user_upload/audio/vl_Yosyf_pro_Zolotonoshu.WMA\" target=\"_blank\">слухати</a>) владика Йосиф Мілян, Єпископ-помічник Київської архиєпархії УГКЦ, коментуючи <a href=\"http://catholicnews.org.ua/chergovii-akt-vandalizmu-v-zolotonoshi-na-cherkashchini#point\">події, що сталися в ніч з 26 на 27 березня в м. Золотоноші, що на Черкащині</a>. Цієї ночі вандали вже вдруге понівечили фігуру Пресвятої Богородиці, цього разу відбили голову.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4583,200,0877.jpg\" alt=\"\" width=\"150\" height=\"200\" />Як сказав єпископ, сьогодні УГКЦ інтенсивно готується до відзначення 1025-річчя Хрещення Русі-України і освячення Патріаршого собору УГКЦ Воскресіння Христового в Києві, що відбудеться в рамках Всенародної прощі до цього собору 18 серпня 2013 року. На його думку, саме тоді, коли Божа благодать спочиває на Церкві, їй намагається протидіяти зло.&nbsp; &laquo;Я не вірю в те, що хтось один, посилаючись на власне сумління, може робити такі вандальні кроки, що полягають у нищенні скульптури Богородиці, - висловив переконання владика Йосиф і додав: - Ми пережили тоталітарну систему комуністичного режиму, який декларував свої переконання. Та навіть у той час у Радянському Союзі атеїсти не діяли так відкрито і нахабно... Я не вірю, що це акція однієї людини. Будемо молитися, щоб Бог їм простив&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нагадаємо, що&nbsp; <a href=\"http://catholicnews.org.ua/u-zolotonoshi-zlovmisniki-ponivechili-khrest-ta-figuru-bogor#point\">перший напад на греко-католицьку громаду Золотоноші стався</a> у ніч із 7 на 8 липня 2012 року. Тоді зловмисники вирвали з фасаду каплиці, в якій молиться місцева греко-католицька громада, хрест і викинули його на вулицю, а також намагалися вирвати фігурку Божої Матері, котра стоїть на подвір&rsquo;ї перед входом до каплиці.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><a href=\"http://ugcc.org.ua\" target=\"_blank\">Департамент інформації УГКЦ</a></span></p>',NULL,NULL,3100,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3326,'P','2013-04-01',1,NULL,'Rss','Бавитися з Богом (1 квітня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4581,200,30020.jpg\" />Якось один чоловік затримався біля гурту хлопчаків, які бавилися на  подвір’ї між будинками. Він почав підстрибувати, танцювати та виробляти  всілякі блазнівські штучки, щоб забавити їх.</p>\n<p>Мати одного з них побачила це зі свого вікна. Через якийсь час вона вийшла на подвір’я, підійшла до свого сина і сказала:</p>\n<p> </p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1080:bavytysja-z-bogom-1-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50',3109,NULL,1,26),
(3327,'P','2013-04-03',1,NULL,'Rss','Недільний обід (3 квітня)','<p style=\"text-align: justify;\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4581,200,6731094_w640_h640_1.jpg\" />Мама, як звичайно, покликала з кухні:</p>\n<p style=\"text-align: justify;\">– Прошу до столу. Все готове!</p>\n<p style=\"text-align: justify;\">Її  чоловік, який читав, і два сини, що дивилися телевізор, швидко  відгукнулися і з шумом сіли до столу. Прийшла господиня, але замість  пахучих страв, кинула на стіл жменьку сіна.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1084:nedilny-obid-3-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50',3111,NULL,1,26),
(3328,'P','2013-04-03',1,NULL,'Rss',' Блаженніший Любомир (Гузар): «Глибока і щира молитва блаженного Папи Івана Павла II пояснює його великий вплив на світ» ','<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/ca5ccd2377.jpg\" height=\"153\" width=\"150\" /></div>\n<p>«Блаженний Папа Іван Павло II відчував потребу відвідувати  світ. Це не було його особистим способом діяння, а своєрідним релігійним  завданням. Такі мандрівки світом були його способом проповідування  Ісуса Христа. Підкреслюю, Папа був мужем молитви і людиною віри. Мені  здається, коли згадую його людські характеристики, його особисту  історію, що цей елемент глибокої та щирої молитовності пояснює його  великий вплив на світ», – про це сказав Блаженніший Любомир (Гузар),  Архиєпископ-емерит УГКЦ, в інтерв’ю для «Історичної правди з Вахтангом  Кіпіані» телеканалу ZIK.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1085:2013-04-03-06-26-40&catid=32:gk-svit-cat&Itemid=58',3112,NULL,1,26),
(3329,'P','2013-04-01',1,NULL,'Rss',' Великоднє послання Urbi et Orbi 2013 року Папи Франциска ','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4581,200,161.jpg\" />Дорогі брати й сестри з Риму та всього світу, благодатного вам Великодня!</p>\n<p>Яка велика радість для мене змогти донести вам цю звістку: Христос  Воскрес! Я хотів би, щоб вона досягнула кожну домівку, кожну сім’ю,  особливо там, де є найбільше страждань, лікарні, тюрми...</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1086:-velykodnje-poslannja-urbi-et-orbi-2013-roku-papy-francyska-&catid=32:gk-svit-cat&Itemid=58',3113,NULL,1,26),
(3330,'P','2013-04-03',1,NULL,'Rss','Духовна домашня опіка','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4597,200,IMG_1619.JPG\" />Вже 15 років поспіль отець Анатолій Тесля провадить духовний провід для тяжкохворих парафіян. Щомісяця відвідує хворих на дому, у будинку престарілих на масиві \"Чайка\". Серед опікуваних відома режисерка кіностудії Довженко Світлана Світлична (будинок престарілих), поетеса Світлана Космачівська (на дому).</p>\n<p>Такий вид служіння можна поділити на підкатегорії:</p>\n<p>духовна опіка над парафіянами, які не можуть ходити до храму і просять причастя на домуопіка немічних і хворих, які потребують священика.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1087:duhovna-domashnjai-opika&catid=25:parafija-cat&Itemid=27',3115,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3331,'P','2013-04-03',1,NULL,'Note','Сміливість Івана Павла ІІ трощила диктатури, - Папа Франциск про свого знаменитого попередника','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4581,200,264987974633.jpg\" alt=\"\" width=\"200\" height=\"132\" />&laquo;Іван Павло ІІ був послідовним свідком Господа, &ndash; сказав тоді кардинал, &ndash; і він ставився до свого народу &laquo;з послідовністю чоловіка Божого&raquo;. Щоранку він &laquo;проводив багато часу на поклонінні Пресвятим Дарам&raquo;, даючи &laquo;формувати себе Божій силі&raquo;. У часи, коли ми більше потрібуємо свідків, аніж учителів, &ndash; сказав архиєпископ Буенос-Айреса, &ndash; Іван Павло ІІ до останньої миті життя був саме таким: &laquo;Вірним свідком&raquo;.<br /></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4581,200,264987974633.jpg\" alt=\"\" width=\"200\" height=\"132\" />Вісім років тому, 2 квітня, відійшов у вічність блаженний Іван Павло ІІ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Папа Войтила завжди цінував і поважав Хорхе Маріо Берґольйо, якого 1992 року поставив єпископом і 2001-го підніс до кардинальської гідності, повідомляє <a href=\"http://www.credo-ua.org/2013/04/80465\" rel=\"nofollow\" target=\"_blank\">Сredo</a>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Будучи кардиналом Буенос-Айреса, нинішній Папа Франциск виголосив дві проповіді, присвячені Іванові Павлові ІІ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Іван Павло ІІ не мав страху &laquo;бо прожив своє життя, споглядаючи Воскреслого Господа&raquo;, &ndash; сказав кардинал Берґольйо у день 1 травня 2011 року в кафедральному соборі Буенос-Айреса. У Римі то був день беатифікації Слуги Божого Кароля Войтили, але і в Аргентині цей день був святом для Церкви.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Іван Павло ІІ, підкреслив тоді майбутній Папа Франциск, &ndash; не боявся, і саме це &laquo;трощило диктатури&raquo;. &laquo;Сміливість і рішучість, що їх нам дає Воскресіння Христа, упевненість у тому, що ми прощені милістю Господа&raquo;, - сказав кардинал, - &laquo;позбавляють нас страху&raquo;. Нехай і нині лунають у нашому серці слова Господа Ісуса і блаженного Йоана Павла ІІ: &laquo;Не бійтеся!&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">4 квітня 2005 року через два дні після смерті Папи Войтили, архиєпископ Буенос-Айреса звершив св.Месу в пам&rsquo;ять про покійного Папу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Іван Павло ІІ був послідовним свідком Господа, &ndash; сказав тоді кардинал, &ndash; і він ставився до свого народу &laquo;з послідовністю чоловіка Божого&raquo;. Щоранку він &laquo;проводив багато часу на поклонінні Пресвятим Дарам&raquo;, даючи &laquo;формувати себе Божій силі&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Послідовність &ndash; підкреслив майбутній Папа Франциск, &ndash; &laquo;не можна купити, їй неможливо навчитися&raquo;. Послідовність &laquo;виховується в серці через споглядання&raquo;. Можна з повним правом сказати, &ndash; додав кардинал Берґольйо, &ndash; що Іван Павло ІІ був людиною послідовною, бо в усьому &laquo;підкорявся Божій волі&raquo;. У часи, коли ми більше потрібуємо свідків, аніж учителів, &ndash; сказав архиєпископ Буенос-Айреса, &ndash; Іван Павло ІІ до останньої миті життя був саме таким: &laquo;Вірним свідком&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">http://risu.org.ua/ua/index/all_news/catholics/vatikan/51836/</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/mNzZmmTAtcA\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>',NULL,NULL,3114,5,1,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3332,'P','2013-04-07',1,NULL,'Note','Великодні реколекці «ПОКАЯННЯ»','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Покаяння &ndash; це таїнство, в якому християнин, що відверто визнає свої гріхи перед Богом і виявляє тверду волю покаятися, отримує відпущення гріхів видимо від священика, а невидимо &ndash; від Самого Господа нашого Ісуса Христа&raquo;</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">5-7 квітня 2013 р.Б. у храмі св.. Миколая Мирлікійського на Аскольдовій Могилі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ре колекції провадить отець Онуфрій Репецький ЧСВВ</span></p>\r\n<p style=\"text-align: justify;\"><em><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&laquo;Покаяння &ndash; це таїнство, в якому християнин, що відверто визнає свої гріхи перед Богом і виявляє тверду волю покаятися, отримує відпущення гріхів видимо від священика, а невидимо &ndash; від Самого Господа нашого Ісуса Христа&raquo;</strong></span></em></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Розклад:</span></strong></p>\r\n<p style=\"text-align: justify;\"><em><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">5 квітня (п&rsquo;ятниця)</span></strong></em></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>18:00</strong> Служба Божа. <strong>Перша наука</strong></span></p>\r\n<p style=\"text-align: justify;\"><em><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">6 квітня (субота)</span></strong></em></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08:00 Утреня <strong>09:00</strong> Служба Божа. <strong>Друга наука</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>18:00 </strong>Служба Божа. <strong>Третя наука</strong></span></p>\r\n<p style=\"text-align: justify;\"><em><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">7 квітня (неділя)</span></strong></em></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>08:15</strong> Служба Божа. <strong>Четверта наука</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>10:00 </strong>Служба Божа. <strong>П&rsquo;ята наука</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>12:00 </strong>Служба Божа. <strong>Шоста наука</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>',NULL,NULL,3116,5,1,41),
(3333,'P','2013-04-04',1,NULL,'Rss','Плоди (5 квітня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4599,200,7563.jpeg\" />Один чоловік, який шукав мудрості, постановив зійти на гору, де раз  на два роки об\'являв себе Господь Бог.</p>\n<p>Першого року він споживав те, що  давала йому земля. На другий рік уже не мав чим харчуватися, тож вирішив  повернутися до міста.</p>\n<p>\"Бог несправедливий! - крикнув він у небо. - Не зважив на те, що я  жив тут цілий рік, аби почути Його голос. А тепер я зголоднів і мушу йти  звідси, хоч так і не почув Його мови.\"</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1089:plody-5-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50',3120,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3334,'P','2013-04-04',1,NULL,'Rss','Служіння Богові','<p><img style=\"margin: 5px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4602,200,73015114_Hristos.jpg\" />Служіння Богові полягає в смиренні.</p>\n<p>Проте в сучасному світі люди не бажають чути про смирення чи послух. Для них це те саме, що визнати себе слабшими за когось, а слабаком не хоче бути ніхто. На жаль, смирення асоціюється лише з негативними явищами: з поразкою, програшем чи відступом. Наприклад, сумирними вважаються, злочинці затримані поліцією.</p>\n<p>Таке розуміння смирення не випадкове. Зважаючи на те, що цілий світ живе за принципом «виживають найсильніші», головним завданням стає перемога над суперниками. Люди з усіх сил намагаються перемогти, перевершити і довести свою перевагу над іншими.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1090:sluzhinnja-bogovi&catid=30:hrystyjanske-vchennia-cat&Itemid=37',3121,NULL,1,26),
(3335,'P','2013-04-05',1,NULL,'Rss','Гуси (6 квітня)','<p align=\"left\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4599,200,images.jpg\" />Гуси на подвір\'ї чітко дотримувалися усталеного  тижневого розпорядку. Того дня вони купалися у ставку, ретельно вичищали  своє пір\'я, обливалися, полоскали собі горлянки, чистили дзьоби, а  потім, крокуючи перевальцем, збиралися скраю подвір\'я, у затінку старої  плакучої верби.</p>\n<p align=\"left\">Тоді-то мудрий, всіма шанований гусак, спадкоємець блискучої традиції,  що йде від Звитяжної Гусячої Спільноти,</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1091:gusy-6-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50',3122,NULL,1,26),
(3336,'P','2013-04-06',1,NULL,'Rss','День батька (7 квітня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4599,200,b1qNVPx8Sv0.jpg\" />На собі вона мала гарну сукенку жовтогарячого кольору, волосся було  стягнуте червоно-золотавою стрічкою - готувалася йти до школи. Був День  батька, отже, всі діти мали прийти до школи в супроводі своїх татусів.  Вона єдина йшла з мамою.</p>\n<p>Мама переконувала її залишитися вдома, мовляв, у  школі, найімовірніше, не зрозуміють їхнього вчинку. Та дівчинка  прагнула будь-що-будь розповісти всім про свого татуся, який сильно  відрізнявся від інших.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1092:den-batka-7-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50',3126,NULL,1,26),
(3337,'P','2013-04-07',1,NULL,'Rss','Благовіщення Пресвятої Богородиці','<p><img src=\"http://askoldova-mohyla.org/uk/image,4609,200,01.jpg\" style=\"margin: 10px; float: left;\" />Благовіщення, Благовіщення Пресвятої Богородиці – велике двонадесяте свято, третє за значенням народно-релігійне свято після <a href=\"http://www.sviato.in.ua/velykden%27_rel.php\">Великодня</a> і <a href=\"http://www.sviato.in.ua/rizdvo_rel.php\">Різдва Христового</a>. Православні святкують Благовіщення 7&nbsp;квітня, католики – 25&nbsp;березня.</p>\n<p><strong>Походження свята</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1093:blagovishchennja-presvjatoji-bogorodyci&catid=27:gk-v-ukrajini-cat&Itemid=57',3127,NULL,1,26),
(3338,'P','2013-04-07',1,NULL,'Rss','Паркування (8 квітня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4610,200,46531687979.jpeg\" />Він був справді блискучим менеджером. Робочий день його був  організований бездоганно – сприяли цьому комп’ютер, мобільний телефон і  годинник. Цінував чи не кожну секунду свого часу!</p>\n<p>Одного дня він їхав до великої правничої фірми, де мав зустрітися з  поважними людьми. Але розкішний хмародер, у якому розмістився офіс тієї  фірми, був стояв у самісінькому середмісті, й менеджер якоїсь миті  збагнув, що, виїжджаючи, не врахував шаленого автомобільного руху в  центрі міста, ані того, що неодмінно матиме труднощі з паркуванням своєї  машини.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1094:parkuvannja-8-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50',3128,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3339,'P','2013-04-08',1,NULL,'Rss','Мама і син з Христом','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4612,200,DSCN9899.JPG\" />Ніна Марчненко любила приходити молитись на Аскольдову Могилу і часто &nbsp;недільну Божественну Літургію молилась в Патріаршому Соборі Воскресіння Христового в Києві. Тим вона свідчила, що є християнкою не розділеною конфесіями.</p>\n<p>Її розповіді про свого сина і продовження його життя у ній у мамі, робили її святою. Мужні і світлі очі світились любов’ю і прощенням.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1097:mama-i-syn-z-hrystom&catid=31:kolonka-redaktora-cat&Itemid=1',3131,NULL,1,26),
(3340,'P','2013-04-08',1,NULL,'Note','«Покаяння – це омивання свого серця»','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,4612,200,106408_medium.jpg\" alt=\"\" width=\"170\" height=\"106\" />&laquo;Господи, Ти &ndash; дихання моє, Ти &ndash; сила моя, Ти &ndash; любов моя, Ти &ndash; покора моя, Ти &ndash; свобода моя, Ти &ndash; радість моя. Ти &ndash; дихання мого дихання, Ти &ndash; серце мого серця, Ти &ndash; душа моєї душі, Ти &ndash; мудрість моєї мудрості. Ти &ndash; натхнення мого серця, Ти &ndash; небо мого серця, Ти &ndash; багатство мого серця, Ти &ndash; царство Боже мого серця.&raquo;.</strong></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4612,200,0127.jpg\" alt=\"\" width=\"200\" height=\"150\" />Передвеликодні ре колекції, які провадив отець Онуфрій Репецький, ЧСВВ на парафії св.. Миколая Чудотворця на Аскольдовій з 5 по 7 квітня, посіяли здорове зерно довіри до Христа, скріпили віру у Христа у багатьох серцях. Головною темою реколекційних наук була тема &laquo;ПОКАЯННЯ&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">За загальним сприйняттям: <strong>&laquo;Покаяння &ndash; це таїнство, в якому християнин, що відверто визнає свої гріхи перед Богом і виявляє тверду волю покаятися, отримує відпущення гріхів видимо від священика, а невидимо &ndash; від Самого Господа нашого Ісуса Христа&raquo;.</strong> Отець Онуфрій це твердження розширює до простоти людського сприйняття:<strong> &laquo;Покаяння &ndash; це омивання свого серця, очищення відносин з Тими, проти кого Ти щось маєш. Покаяння &ndash; це плач над вчинками. Якими Ти ображаєш Бога всередині себе. Покаяння &ndash; це неупереджений суд, який ми виносимо собі перед Богом. Покаяння &ndash; це внутрішня переоцінка самого себе. Покаяння &ndash; це відречення від старої людини. Покаяння &ndash; це повернення до безгрішного життя&hellip;&raquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Духовна віднова, якою щиро ділився отець перед світлим празником воскресіння Христового, відкрила нове бачення живої молитви, живого спілкування з Богом:<strong> &laquo;Господи, Ти &ndash; дихання моє, Ти &ndash; сила моя, Ти &ndash; любов моя, Ти &ndash; покора моя, Ти &ndash; свобода моя, Ти &ndash; радість моя. Ти &ndash; дихання мого дихання, Ти &ndash; серце мого серця, Ти &ndash; душа моєї душі, Ти &ndash; мудрість моєї мудрості. Ти &ndash; натхнення мого серця, Ти &ndash; небо мого серця, Ти &ndash; багатство мого серця, Ти &ndash; царство Боже мого серця.&raquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Слідкуйте за дописами, згодом, Ви зможете побачити реколекції отця Онуфрія, що проходили у церкві св.. Миколая Мирлікійського на Аскольдовій Могилі, у відео форматі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Підготувала Любов Михайлюк, &laquo;Оранта&raquo;.</span></p>',NULL,NULL,3129,5,1,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3341,'P','2013-04-09',1,NULL,'Rss','«Служаниця» свого сина (оновлено)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4612,200,900587608979607.jpg\" />Ніна Марченко (Смужаниця) все своє життя слідувала прикладу Матінки Божої</p>\n<p>4 квітня у Спілці письменників України відбулася презентація книги про життя Ніни Марченко (Смужаниці, 1929-2011) «Матінка Ніна» – матері відомого дисидента-шістдесятника Валерія Марченка. Серед упорядників книги – прихожанка Церкви Святого Миколая на Аскольдовій Могилі Наталія Пуряєва та Андрій Горбаль, небіж Ніни Марченко.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1096:2013-04-08-10-12-59&catid=37:spilnota-cat&Itemid=49',3132,NULL,1,26),
(3342,'P','2013-04-08',1,NULL,'Rss','В Україні стартує соціальний проект поколінь «Встигни сказати головне»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4614,200,golovne.jpg\" />Міжнародний громадський рух «Живи» ініціює в Україні новий соціальний  проект поколінь «Встигни сказати головне», який стартує 20 квітня під  девізом «Щоб звернути серця батьків до дітей».</p>\n<p>Метою проекту є об\'єднати молоде покоління країни навколо уваги і  дієвого співчуття по відношенню до старшого покоління, сприяти розвитку  почуття вдячності і поваги дітей до батьків, чути покоління, щоб  встигнути сказати головне!</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1098:2013-04-08-11-59-37&catid=37:spilnota-cat&Itemid=49',3134,NULL,1,26),
(3343,'P','2013-04-08',1,NULL,'Rss','Просвітницьке паломництво дітей «Божого милосердя» та дітей катехитичної школи парафії','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4388,200,733995_563584453674173_1196504355_n.jpg\" />На заклик парафіяльної служби милосердя «Карітас-Печерський» до спільної щирої молитви, посту душі, прояву милосердного милосердя у часі великоднього посту відгукнувся колектив компанії «Еконія», єдиного в Україні інноваційного виробника питної води для діток <a href=\"http://www.econia.com.ua/uk/\">http://www.econia.com.ua/uk/</a>.</p>\n<p>Президент компанії Ірина Варагаш добре розуміє, що основна мета милосердного милосердя полягає не тільки у матеріально-фінансовій помочі, головним і дуже важливим аспектом у цьому служінні є поміч духовна, просвітницька, пізнавальна.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1099:2013-04-08-15-48-20&catid=25:parafija-cat&Itemid=27',3135,NULL,1,26),
(3344,'P','2013-04-08',1,NULL,'Rss','Ціна (9 квітня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4610,200,IMG_0126.jpg\" />Один  шанований людьми священик, що служив на маленькій парафії, завжди вельми  старанно готувався до недільної проповіді. Якось у храмі — то був  Великодній ранок — він зійшов на казальницю. А в руці тримав іржаву  металеву клітку, яку помістив так, щоб усі вірні могли її бачити.</p>\n<p>Парафіяни  були здивовані. Священик тим часом говорив:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1095:cina-9-kvitnjaja&catid=1:mystecka-storinka-cat&Itemid=50',3136,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3345,'P','2013-04-09',1,NULL,'Rss',' Звернення Бюро УГКЦ з питань екології з приводу Всеукраїнської акції «Посади дерево» ','<p><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/derevo2.jpg\" height=\"182\" width=\"249\" />У межах проведення Великопосної ініціативи «Екологічне навернення для  порятунку створіння», Бюро УГКЦ з питань екології закликає долучитися 20  квітня до Всеукраїнської акції «Посади дерево», метою якої є вияв  конкретних діл милосердя стосовно своїх ближніх і всього створеного  Богом світу.</p>\n<p>Бог покликав нас «порати і доглядати» (Бут. 2, 15) своє створіння, бути  його добрими садівниками, примножувати отримані дари природи. Кожне  дерево Божого саду має дуже важливе значення: \n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1103:2013-04-09-12-39-14&catid=37:spilnota-cat&Itemid=49',3139,NULL,1,26),
(3346,'P','2013-04-14',1,NULL,'Note','Розклад Богослужінь на 4-тий тиждень Великого посту','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>14.04.2013 неділя 4-та Великого посту</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-15 10-00 12-30 Літургія Святого Василія Великого</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>08.04.2013 понеділок</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00Часи.Літургія св. Йоана Золотоустого.&nbsp;Панахида Молитви оздоровлення і благословення мощами&nbsp;&nbsp; св. Шарбеля</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 Літургія св. Йоана Золотоустого. Акафіст Страстям Господнім</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>09.04.2013 вівторок</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Часи, Літургія св.Йоана Золотоустого.&nbsp; Панахида</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 .Літургія св.Йоана Золотоустого.&nbsp;Панахида</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>10.04.2013 середа</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Вечірня з Літургією Напередосвячених Дарів.&nbsp;Прастас-сорокоусти.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00&nbsp;Вечірня з Літургією Напередосвячених Дарів.&nbsp;Прастас-сорокоусти. Хресна дорога</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>11.04.2013 четвер</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Часи, Літургія св. Йоана Золотоустого&nbsp; Панахида</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Освячення води і єлею,молитви оздоровлення</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 Літургія св. Йоана Золотоустого Акафіст Страстям Господнім</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>12.04.2013 п\'ятниця</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Вечірня з Літургією Напередосвячених Дарів Парастас-сорокоусти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">17:30 Хресна Дорога від Дніпра</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 Вечірня з Літургією Напередосвячених Дарів, Хресна дорога</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>13.04.2013 Субота Заупокійна.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Утрення</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">09-00 Літургія Святого Івана Золотоустого.&nbsp;Парастас-сорокоусти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 Літургія Святого Івана Золотоустого Вечірня</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>14.04.2013 неділя 4-та Великого посту</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-15 10-00 12-30 Літургія Святого Василія Великого</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">телефон для довідок:&nbsp;<a href=\"tel:%28044%29%20570%2032%2090\" target=\"_blank\"><strong>(044) 570 32 90</strong></a>,&nbsp;<a href=\"tel:%28050%29%204473157\" target=\"_blank\"><strong>(050) 447 31 57</strong></a></span></p>',NULL,NULL,3138,5,1,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3347,'P','2013-04-09',1,NULL,'Rss','Папа закликав католиків вийти з окопів','<p><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/papa_v_laterani.jpg\" height=\"182\" width=\"249\" />У середу, минулого тижня, Святіший Отець Франциск закликав католиків  перестати боятися і вийти з затвору церковних стін, куди їх загнав  секуляризм з його псевдотолерантною політкоректністю: «<a href=\"http://catholicnews.org.ua/promova-papi-frantsiska-na-zagalnii-audiyentsiyi-3042013r-po#point\">Спробуймо  відчувати радість від того, що ми є християнами! Ми віримо у  Воскреслого, Котрий подолав зло і смерть! Маймо також сміливість «вийти  назовні», щоб нести цю радість і світло в усі куточки нашого життя!  Воскресіння Христа є нашою найбільшою впевненістю, це – наш найцінніший  скарб!</a></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1104:papa-zaklykav-katolykiv-vyty-z-okopiv&catid=32:gk-svit-cat&Itemid=58',3140,NULL,1,26),
(3348,'P','2013-04-09',1,NULL,'Rss','Знак запитання (10квітня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4621,200,vera.jpg\" />Жив собі на світі знак запитання. Був дуже привабливим і, як усі, властиво, знаки запитання, вельми інтелігентним.</p>\n<p>Проте від певний час мандрував по країні сумний та пригнічений, сповнений гіркоти й розчарування.</p>\n<p>Скидалося  на те, що він став нікому не потрібний. Усі дедалі частіше зверталися  до його запеклого ворога, знаку оклику. Тільки й робили, що викрикували:  \"Вперед! Зупинися! Рушай! Геть з дороги!\"</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1100:znak-zapytannja-10kvitnja&catid=1:mystecka-storinka-cat&Itemid=50',3141,NULL,1,26),
(3349,'P','2013-04-09',1,NULL,'Rss','Піст — це не дієтичний ритуал, це звільнення від гріха','<p><img style=\"margin: 10px; float: right;\" src=\"http://www.rivnepost.rv.ua/artimages/2013-26-p19_batiushka.jpg\" alt=\"Блаженніший Святослав (Шевчук)\" height=\"148\" width=\"136\" /></p>\n<p class=\"text10\"><strong>—  Перебуваючи на Рівненщині, ви зустрічалися  не тільки з релігійними  громадами, а й зі студентами та міським головою  Рівного. Які ваші  враження від таких зустрічей?</strong></p>\n<p class=\"text10\">—  Рівненщина — це земля глибоко побожних, релігійних  людей. Незважаючи на  те, до якої конфесії вони сьогодні належать, вони  спраглі до  досконалості у християнському житті. Окрім цього, це є  історична земля  освіти та культури. Де ще ми можемо побачити в світі  спільноту, яка має  таке глибоке культурне, європейське коріння, як на  Рівненщині?</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1105:blazhennishy-svjatoslav-shevchuk-qpist-ce-ne-dijetychny-rytual-ce-zvilnennja-vid-grihaq&catid=27:gk-v-ukrajini-cat&Itemid=57',3142,NULL,1,26),
(3350,'P','2013-04-10',1,NULL,'Rss','Яблука (11 квітня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4621,200,x_68fd889f.jpg\" />Декілька молодих менеджерів, що тиждень навчалися на курсах  підвищення кваліфікації, бігли коридорами аеропорту. Це було ввечері у  п’ятницю, усі хотіли летіти на вихідні додому.</p>\n<p>Чоловіки спізнювалися. Рейс літака відкладали вже кілька разів.</p>\n<p>Бігли коридорами летовища, стискаючи в долонях паспорти, квитки та ручки валіз.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1101:jabluka-11-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50',3144,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3351,'P','2013-04-11',1,NULL,'Note','Нагадай мені, Боже….','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4628,200,jesusoncrossoo.jpg\" alt=\"\" width=\"236\" height=\"178\" />Нагадай мені, Боже... приниженням про відкинутих, щоб я не возносився(-лась) надто високо над іншими і не забував(-ла)&nbsp; про забутих.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нагадай мені, Боже... гріхом про грішних людей, щоб я їх не осуджував(-ла), а співчував(-ла) і молився(-лася) за їхнє навернення.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нагадай мені, Боже... терпінням про страждаючих, щоб я не забув(-ла) співчувати і плакати.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4628,200,jesusoncrossoo.jpg\" alt=\"\" width=\"200\" height=\"150\" />Нагадай мені, Боже... хворобою, що існують хворі, щоб я вмів(-ла) їх розуміти і не забув(-ла) про них.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нагадай мені, Боже... терпінням про страждаючих, щоб я не забув(-ла) співчувати і плакати.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нагадай мені, Боже... приниженням про відкинутих, щоб я не возносився(-лась) надто високо над іншими і не забував(-ла)&nbsp; про забутих.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нагадай мені, Боже... образою про скривдженних, щоб я не забув(-ла)&nbsp; біль душі, коли виникне бажання когось образити.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нагадай мені Боже... голодом про бідних і знедолених, щоб я не забув(-ла)&nbsp; дивитись в очі потребуючим і підтримати їх чим зможу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нагадай мені, Боже... красою цього світу, щоб я пригадав(-ла) собі, що я є сотворений(-а) на Твій образ і подобу, і що покликаний(-а) продовжувати Твоє діло творення.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нагадай мені, Боже... гріхом про грішних людей, щоб я їх не осуджував(-ла), а співчував(-ла) і молився(-лася) за їхнє навернення.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нагадай мені, Боже... радістю, що існує радість, і щоб не забув(-ла), що життю потрібно радіти.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нагадай мені, Боже... самотністю, щоб я пам&rsquo;ятав(-ла)&nbsp; про осуджених несправедливо, щоб я відчув(-ла)&nbsp; на собі одинокість і покинутість і, щоб я не забув(-ла)&nbsp; душевний біль одинокої людини.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нагадай мені, Боже&hellip;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Не забудь про мене, Боже...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти знаєш, що я часто можу захопитись тим, що мене віддалює від реальності життя.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти знаєш, як я потім блукаю, щоб повернутись до щоденності.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Тому нагадуй про те, що є довкола, що біля нас, що буває і в нас самих.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Коли &ndash; це все спіткає мене, то пригадай мені цю молитву, щоб я пригадав(-ла)&nbsp; собі, що Ти мене пам&rsquo;ятаєш...</span></p>',NULL,NULL,3145,5,1,41);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3352,'P','2013-04-11',1,NULL,'Rss','Про співжиття Церкви і Держави','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4627,200,phpqmOaVrp4Cs.jpg\" />Мені часто ставлять запитання під час   різного роду зустрічей про відносини церкви і держави. Думаю, що це   зацікавлення має багато підстав.</p>\n<p>Зрозуміло, що в такій державі, як наша, де є   багато церков і релігійних організацій, їх стосунок до держави, а тим   більше до чинної державної влади цікавить свідомих, передусім віруючих   громадян. Тому хочу назвати кілька причин (які мені відомі), чому люди   ставлять таке запитання.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1111:pro-spivzhyttja-cerkvy-i-derzhavy&catid=27:gk-v-ukrajini-cat&Itemid=57',3146,NULL,1,26),
(3353,'P','2013-04-11',1,NULL,'Note','ЗВІТ №3 про розподілення гуманітарної допомоги від благодійного фонду «Олива», а також благодійних пожертвувань парафіян церкви св.Миколая на Аскольдовій могилі (31.01.2013 р.)','<ul>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">одяг чоловічий, жіночий, дитячий &ndash; 17 коробів+6 мішків</span></li>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">взуття &ndash; 3 мішки</span></li>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">ігри та іграшки дитячі &ndash; 2 мішки</span></li>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">духовна та художня література &ndash; 1 коробка</span></li>\r\n</ul>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Служба милосердя при церкві св.Миколая на Аскольдовій могилі, зібравши пожертвувані парафіянами: одяг чоловічий, жіночий та дитячий, взуття, ігри, іграшки дитячі та інші речі особистого вжитку, а також гуманітарну допомогу від благодійного фонду &laquo;Олива&raquo;, в т.ч. антисептичні прокладки для хворих, розподілила їх таким чином:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&mdash; уділила потребуючим мирянам;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; відправила гуманітарний вантаж Благодійному фонду &ldquo;Сузір\'я&rdquo; в с.Іванкові Бориспільського р-ну Київської обл.:</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">одяг чоловічий, жіночий, дитячий &ndash; 17 коробів+6 мішків</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">взуття &ndash; 3 мішки</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">ігри та іграшки дитячі &ndash; 2 мішки</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">духовна та художня література &ndash; 1 коробка</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Робота виконана силами волонтерів: п.Мирослави, п.Світлани, п.Михайла, п.Анни.</span></p>',NULL,NULL,3147,5,1,19),
(3354,'P','2013-04-11',1,NULL,'Note','ЗВІТ №5 про розподілення гуманітарної допомоги від БФ «Тріумф серця», а також благодійних пожертвувань парафіян церкви св.Миколая на Аскольдовій могилі (22.02.2013 р.)','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&mdash; уділила потребуючим мирянам;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&mdash; відправила гуманітарний вантаж Благодійному фонду &ldquo;Сузір\'я&rdquo; в с.Іванкові Бориспільського р-ну Київської обл.:</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Служба милосердя при церкві св.Миколая на Аскольдовій могилі, зібравши пожертвувані парафіянами: меблі, одяг чоловічий, жіночий та дитячий, взуття, ігри, іграшки дитячі та інші речі особистого вжитку, а також гуманітарну допомогу від благодійного фонду &laquo;Тріумф серця&raquo;, розподілила їх таким чином:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&mdash; уділила потребуючим мирянам;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&mdash; відправила гуманітарний вантаж Благодійному фонду &ldquo;Сузір\'я&rdquo; в с.Іванкові Бориспільського р-ну Київської обл.:</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">одяг чоловічий, жіночий, дитячий &ndash; 13 мішків</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">взуття &ndash; 1 мішок</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">іграшки дитячі &ndash; 2 мішки</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">ігри дитячі &ndash; 1 коробка</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">шафи для одягу &ndash; 2 шт.</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Робота виконана &nbsp;силами волонтерів: п.Мирослави, п.Світлани, п.Михайла, п.Анни.</span></p>',NULL,NULL,3149,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3355,'P','2013-04-12',1,NULL,'Note',' 11 квітня 1945 року радянська влада арештувала Митрополита Йосифа (Сліпого)','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Фільм про історію УГКЦ та переслідування, яких зазнали її члени. У фільмі також йдеться про псевдособор 1946 року та Греко-Католицьку Церкву у підпіллі.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong class=\"main\">\"Збудована вірою\" - документальний фільм, який розповідає про трагічний період переслідування Церкви.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Фільм про історію УГКЦ та переслідування, яких зазнали її члени. У фільмі також йдеться про псевдособор 1946 року та Греко-Католицьку Церкву у підпіллі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><a href=\"http://ugcc.tv/ua/media/55051.html\">http://ugcc.tv/ua/media/55051.html</a></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>',NULL,NULL,3153,5,1,48),
(3356,'P','2013-04-12',1,NULL,'Rss','Чудо (13 квітня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4628,200,1331807683_main-7625-a384cdce6e175f9787255a9770534b3c.jpg\" />Ось правдива історія однієї дівчинки, яка вірила і знала, що любов  здатна творити дива. Її братикові загрожувала смерть від пухлини в  мозку. Батьки хлопчика зробили все, щоб його врятувати, однак заможними  не були і, витративши всі свої заощадження, відвернути лиха таки не  змогли.</p>\n<p>Якось увечері батько, із важким серцем, сказав заплаканій  матері:</p>\n<p>- Знаєш, люба, - нам, певно, не вдасться його врятувати. Тут може  зарадити тільки чудо!</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1108:chudo-13-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50',3154,NULL,1,26),
(3357,'P','2013-04-13',1,NULL,'Rss','Після нападу грабіжника (14 квітня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4628,200,57641.jpg\" />Увечері професор Метью Генрі вертався з університету  додому, коли біля самісінького будинку хтось зненацька приставив йому до  чола пістолет.</p>\n<p>Грабіжник у масці велів йому віддати портфель і теку. Професор  послухався, й розбійник миттєво зник у темряві.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1109:pislja-napadu-grabizhnyka-14-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50',3155,NULL,1,26),
(3358,'P','2013-04-14',1,NULL,'Rss','А коли цвіли  проліски...','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4623,200,221532016.jpg\" />Вітер уривчасто завивав тужливу пісню. Дерева похитували йому розпатланими кронами в унісон. У верховітті зависло те незнане: і доки? Доки ще вередуватиме холоднеча, невже не набридло наввипередки гнатися із весною, такою несміливою і мало що не одчайдушною.</p>\n<p>Але де там!.. Той волоцюга-вітер, і де тільки йому духу вистачає, набрав повні щоки повітря і як дмухне, що аж дух перехопило! .. і нема чому дивуватися. Він не може спокійно всидіти на лавочці і белемкати ногами.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1107:a-koly-cvily-prolisky&catid=1:mystecka-storinka-cat&Itemid=50',3156,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3359,'P','2013-04-14',1,NULL,'Rss','Польовий цвіркун і монета (15 квітня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4628,200,1333681046_13.jpg\" />Один мудрий індус мав приятеля. Приятель жив у Мілані. Вони  познайомилися в Індії, коли італієць приїздив туди зі своєю сім\'єю.  Індус провадив екскурсії для італійців, відкриваючи їм найхарактерніші  закутки своєї вітчизни.</p>\n<p>Міланець заприязнився з індусом і на знак вдячності запросив того до  свого міста. Індус довго не міг відважитися на таку далеку подорож, але  врешті-решт дав себе переконати.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1110:polovy-cvirkun-i-moneta-15-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50',3157,NULL,1,26),
(3360,'P','2013-04-15',1,NULL,'Rss','ПРАВДИВИЙ ХРИСТИЯНИН ЗАВЖДИ Є АПОСТОЛОМ','<p><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/12_apostles2.jpg\" height=\"182\" width=\"249\" />Під сам кінець минулого тижня, 13 квітня, світ облетіла новина – <a href=\"http://catholicnews.org.ua/papa-priznachiv-komisiyu-yaka-dopomagatime-reformuvati-kuriy#point\">Папа створив Комісію з питань реформи Римської Курії</a>.&nbsp;  Черговий інформаційний привід. Стрічки новин знову сповнилися  заголовками про Ватикан, Церкву і Папу: «Папа іменував кардиналів, які  реформуватимуть Церкву», «Папа перебудовує Церкву», «Кардинали  управлятимуть Церквою разом з Папою»…… У тому каламутному потоці зовсім  втратився правдивий зміст новини – йдеться про реформу Римської Курії і  не більше.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1113:pravdyvy-hrystyjanyn-zavzhdy-je-apostolom&catid=32:gk-svit-cat&Itemid=58',3158,NULL,1,26),
(3361,'P','2013-04-15',1,NULL,'Rss','Про покликання Папи Франциска: Чому вирішив стати священиком?','<p><img width=\"300\" height=\"181\" src=\"http://popefrancis.org.ua/wp-content/uploads/2013/04/Pope_Francis_younger_0_0-300x181.jpg\" alt=\"Pope_Francis_younger_0_0\" style=\"margin: 10px; float: right;\" /></p>\n<p><em>Як  сталось так, що хімік-лаборант, шанувальник футболу і танго став  священиком і таким чином розпочав шлях, який в результаті привів його на  Престіл Св. Петра? Його життя на завжди змінив один день…</em></p>\n<p>Хорхе Маріо було 17 років і разом з друзями він збирався на  прогулянку. Це було 21 вересня 1953 року. В південній частині півкулі  цей період – початок весни і традиційний «день прогулянок».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1114:pro-poklykannja-papy-francyska-chomu-vyrishyv-staty-svjashchenykom&catid=35:istorychni-postati-cat&Itemid=48',3160,NULL,1,26),
(3362,'P','2013-04-16',1,NULL,'Rss','Жертва (17 квітня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4644,200,b8bIAVQQW8Q.jpg\" />В  одній африканській церкві, під час збору пожертв, служки ходили поміж  лавами з великим кошиком з лози, схожим на ті, що служать для збору  маніоку.</p>\n<p>Хлопчина,  який сидів в останньому ряді, не зводив зосередженого погляду з кошика,  що переходив по рядах. Він тяжко зітхнув, бо не мав нічого, щоб  пожертвувати Господеві.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1117:zhertva-17-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50',3162,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3363,'P','2013-04-16',1,NULL,'Rss','Блаженний отець Лука Пассі - свідок любові Христа до сиріт','<p><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size_vertical/news/pictures/1_0_683329.jpg\" height=\"249\" width=\"182\" />У суботу, 13 квітня, кардинал Анджело Амато, Префект Конгрегації у  справах святих, в базиліці святого Марка у Венеції, проголосив блаженним  отця Луку Пассі, який був ревним свідком любові Христа до найубогіших  та найбідніших дітей. У 1838 році він започаткував спільноту мирянок,  яка називається «Pia Opera di Santa Dorotea» (Милосердне Діло святої  Доротеї). Метою цієї спільноти була опіка, навчання та виховання бідних,  часто залишених своїми батьками, дівчат.</p>\n<p>Отець Лука народився в шляхетній венеціанській сім’ї, яка мешкала у м.  Берґамо (північ Італії). В 1807 році розпочинає навчання у семінарії.  Протягом того часу він тісно співпрацює з Марійською спільнотою, яка  діяла при парафії містечка Кальчінате (північ Італії).</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1118:blazhenny-otec-luka-passi-svidok-ljubovi-hrysta-do-syrit&catid=35:istorychni-postati-cat&Itemid=48',3163,NULL,1,26),
