INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3365,'P','2013-04-15',1,NULL,'Rss','Професор і перевізник (16 квітень)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4644,200,Odna_doroga.jpg\" />Один відомий професор університету, якого мали представити до Нобелівської премії, вибрався на озеро.</p>\n<p>Він  попросив перевізника, аби той повозив його по озеру на своєму човні.  Добродій погодився. Коли відплили вже доволі далеко від берега, професор  узявся допитуватись.</p>\n<p>- Чи ти знаєш історію?</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1116:profesor-i-pereviznyk-16-kviten&catid=1:mystecka-storinka-cat&Itemid=50',3164,NULL,1,26),
(3366,'P','2013-04-15',1,NULL,'Rss','Науковці з усієї України у стінах УКУ обговорили шляхи подолання історичних конфліктів','<a href=\"http://vkontakte.ru/share.php?url=http%3A%2F%2Frisu.org.ua%2Fua%2Findex%2Fall_news%2Fcommunity%2Fconferences%2F51979%2F\"> </a>\n<p><img style=\"margin: 10px; float: left;\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_51979_%D3%CA%D3_2.jpg\" alt=\"УКУ_2.jpg\" title=\"УКУ_2.jpg\" height=\"121\" width=\"181\" />Науковці  з різних куточків України під час ХVI Всеукраїнської конференції  «Болісні травми минулого: історична правда та імперативи сумління»  шукали шляхи розв’язання історичних конфліктів.</p>\n<p>Конференція відбулась в Українському католицькому університеті 12-13 квітня.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1115:naukovci-z-usijeji-ukrajiny-u-stinah-uku-obgovoryly-shljahy-podolannja-istorychnyh-konfliktiv&catid=27:gk-v-ukrajini-cat&Itemid=57',3165,NULL,1,26),
(3367,'P','2013-04-17',1,NULL,'Rss','Гостина у замку (18 квітня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4645,200,vodna-stuhiia.jpg\" />Село, що розташувалося біля замку, розбудив голос каштелянового посланця, який зачитував на площі оголошення свого пана.</p>\n<p>\"Наш улюблений господар запрошує усіх своїх добрих і вірних підданих  взяти участь у гостині, яку він влаштовує з нагоди своїх уродин. На  кожного чекатиме приємна несподіванка.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1119:gostyna-u-zamku-18-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50',3166,NULL,1,26),
(3368,'P','2013-04-17',1,NULL,'Rss',' Блаженніший Святослав: «Зневага парламентаризму в Україні викликає велике занепокоєння в суспільстві» ','<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/09c2fa9cce.jpg\" height=\"96\" width=\"150\" /></div>\n<p>17 квітня відбулася зустріч Всеукраїнської ради Церков і  релігійних організацій (ВРЦіРО) з Головою Верховної Ради Володимиром  Рибаком та депутатами ВР із різних депутатських фракцій.</p>\n<p>Ця зустріч відбулася з ініціативи міжфракційного депутатського  об’єднання «За сприяння свободі сумління». На цій зустрічі від імені  ВРЦіРО головував Блаженніший Святослав (Шевчук).</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1120:2013-04-17-20-53-22&catid=27:gk-v-ukrajini-cat&Itemid=57',3167,NULL,1,26),
(3369,'P','2013-04-18',1,NULL,'Rss','«Ватикан: погляд зблизька»','<p align=\"center\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4645,200,1357223320_nunciy.jpg\" />Що таке Ватикан (Святий Престол), Римська Курія, Колегія Кардиналів, хто такий Апостольський Нунцій</p>\n<p align=\"center\">Я отримав лист від брата Войцеха від 15 лютого, із запрошенням представити вам виступ під назвою: «Ватикан: погляд зблизька»; передбачається, що я маю обговорити терміни «Ватикан» та «Святий Престол», описати Римську Курію та Колегію Кардиналів для вас, а також розповісти про себе, чи радше, про мою особливу посаду, посаду Апостольського Нунція; дати вам основну інформацію, якщо хочете.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1122:2013-04-17-21-08-19&catid=30:hrystyjanske-vchennia-cat&Itemid=37',3168,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3370,'P','2013-04-17',1,NULL,'Rss','Християнофобія - всесвітня загроза','<p><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/136496_massimo_introvigne_7.jpg\" height=\"120\" width=\"164\" />Про<strong> християнофобію</strong> і необхідність оборони визнавців <strong>Христа</strong> відомий соціолог і релігієзнавець Масімо Інтровіньє розмовляв з  російським агентством «Інтерфакс-Релігія». Інтровіньє був експертом ОБСЄ  у справах аналізу і протистояння расизму, антисемітизму та  дискримінації християн і визнавців інших релігій.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1121:hrystyjanofobija-vsesvitnja-zagroza&catid=32:gk-svit-cat&Itemid=58',3169,NULL,1,26),
(3371,'P','2013-04-17',1,NULL,'Rss','Покоління підпілля УГКЦ передало молоді досвід збереження віри','<a href=\"http://vkontakte.ru/share.php?url=http%3A%2F%2Frisu.org.ua%2Fua%2Findex%2Fexclusive%2Freportage%2F52017%2F\"> </a>\n<p><img style=\"margin: 10px; float: right;\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_52017_hhh.jpg\" height=\"142\" width=\"212\" />В  Українському Католицькому Університеті миряни та священнослужителі  УГКЦ, які під час правління комуністичного режиму перебували у підпіллі,  зустрілися із молоддю, аби поділилися своїми спогадами про минуле.</p>\n<p>Зустріч під назвою «Мужність свідчити віру в сучасному світі» 17  квітня провели Фонд Святого Володимира, Інститут Історії Церкви УКУ та  Інститут лідерства та управління УКУ у межах проекту «ДІАЛОГ ПОКОЛІНЬ».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1123:pokolinnja-pidpillja-ugkc-peredalo-molodi-dosvid-zberezhennja-viry&catid=27:gk-v-ukrajini-cat&Itemid=57',3171,NULL,1,26),
(3372,'P','2013-04-18',1,NULL,'Rss','Антирелігійна кампанія радянської влади у 1958-1964 рр. і римо-католицька Церква в Україні','<p><img style=\"margin: 10px; float: right;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size_vertical/news/pictures/450px-lwow-katedralacinska-wnetrze1.jpg\" height=\"152\" width=\"110\" /><em><strong>Від редакції:</strong> Утиски і переслідування католиків у  СССР завдали серйозної рани історичній пам’яті нашого народу. Пропонуємо  увазі наших читачів статтю українського науковця&nbsp; Володимира Байдича  опубліковану в 2011 році в «Наукові записки» Інституту політичних і  етнонаціональних досліджень ім. І.Ф.Кураса НАНУ 2 (52)про долу  Римо-Католицької Церкви в Україні в часі антирелігійних гонінь Микити  Хрущова 1958 – 1964 рр. Орфографія, стиль і мова автора збережені.</em></p>\n<em></em> \n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1124:antyreligina-kampanija-radjanskoji-vlady-u-1958-1964-rr-i-rymo-katolycka-cerkva-v-ukrajini&catid=27:gk-v-ukrajini-cat&Itemid=57',3172,NULL,1,26),
(3373,'P','2013-04-18',1,NULL,'Rss','Звіт для орла (19 квітня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4655,200,x_129c5954.jpg\" />Орел,  король над усіма птахами, віддавна чув про незвичайний талант солов\'я.  Як добрий володар, він прагнув переконатися, правду кажуть чи ні. Аби  пересвідчитись, послав на перевірку двох своїх урядників: павича і  жайворонка. Вони мали оцінити красу і спів соловейка.</p>\n<p>Посланці виконали свою місію і повернулися до орла.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1125:zvit-dlja-orla-19-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50',3173,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3374,'P','2013-04-18',1,NULL,'Rss','Духовний світ блаженної мучениці  с. Тарсикії Мацьків, СНДМ','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4655,200,Josafata.jpg\" />Особливі риси: <em>духовний світ, духовні якості, чесноти і особистісні риси, покликання, віра, християнська любов, мучеництво. </em></p>\n<p><em> </em></p>\n<p><em>У статті представлено особливості становлення особистості Ольги Мацьків та риси&nbsp; с.Тарсикії на початку її богопосвяченого життя. До&nbsp; агіографічного портрету належать її&nbsp; духовні пориви та натхнення. Особливим духовним даром є її&nbsp; монаше покликання&nbsp; та покликання до мучеництва. </em></p>\n<p>Поняття «духовний світ особи» дуже часто в українській наукові літературі, а зокрема філософській, хоч і належить до поняття «духовність», нерідко носить значення змісту суспільної свідомості, що показує матеріалістичну парадигму у рамках якої мислить автор.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1127:duhovny-svit-blazhennoji-muchenyci-s-tarsykiji-mackiv-sndm&catid=35:istorychni-postati-cat&Itemid=48',3174,NULL,1,26),
(3375,'P','2013-04-19',1,NULL,'Rss','ПОСЛАННЯ Блаженнішого Святослава, Верховного Архиєпископа Києво-Галицького, до молод','<p><img style=\"margin: 10px; float: right;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/11-09-19_patriarh_v_ucu_vp-51.jpg\" height=\"182\" width=\"249\" /><em>Ніхто твоїм молодим віком хай не гордує, але будь зразком для вірних у слові, поведінці, любові, вірі й чистоті (1 Тим. 4, 12).</em></p>\n<p>Улюблена в Христі українська молоде!</p>\n<p>Традиційно у Квітну неділю, у день Торжественного входу Господа до  Єрусалима, думаю про вас, молюся за вас та хочу звернутися до вас зі  своїм словом. У сьогоднішньому святі, події тріумфу Христа в Єрусалимі,  місці Його приниження і прославлення, – є особливий заклик до Божого  люду.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1129:poslannja-blazhennishogo-svjatoslava-verhovnogo-arhyjepyskopa-kyjevo-galyckogo-do-molod&catid=27:gk-v-ukrajini-cat&Itemid=57',3176,NULL,1,26),
(3376,'P','2013-04-19',1,NULL,'Rss','Човен (20 квітня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4655,200,verbna_nedila.jpg\" />Одного вечора два туристи, що зупинилися в кемпінґу на  березі озера, вирішили переплисти човном на другий берег, аби посидіти в  тамтешній кнайпі.</p>\n<p>Засиділися вони до пізньої ночі і встигли спорожнити цілу батарею  пляшок.</p>\n<p>Нарешті, дещо похитуючись, туристи вийшли із кнайпи, проте їм таки  вдалося зайняти місця у човні, аби рушити у зворотній бік.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1126:choven-20-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50',3177,NULL,1,26),
(3377,'P','2013-04-19',1,NULL,'Rss','Папа Франциск: Лише євангелізуючи, Церква стає Матір’ю','<p><img style=\"margin: 10px; float: left;\" src=\"http://media01.radiovaticana.va/imm/1_0_684371.JPG\" />Щодня  у Службі Божій, яку Папа Франциск очолює в каплиці ватиканської  резиденції «Святої Марти», де він мешкає, беруть участь працівники  різних ватиканських інституцій. Наприклад у середу, 17 квітня, у  ранковій Євхаристійній молитві з Папою взяли участь працівники Інституту  Релігійних Справ, так званого, ватиканського банку. Звертаючись із  коротким словом до присутніх, Святіший Отець запропонував роздумати над  прочитаним того дня біблійним уривком з книги Діянь Апостолів, у якому  Богом натхнений автор описує життя першої громади християн, розповідаючи  про те, як послідовники Ісуса жили у мирі та любові та про перше  переслідування вірних, яке розпочалось після каменування диякона  Стефана. На думку Папи Франциска – це частково є стилем життя Церкви:  між миром любові і переслідуваннями.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1130:papa-francysk-lyshe-jevangelizujuchy-cerkva-staje-matirju&catid=28:tema-nomera-cat&Itemid=2',3178,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3378,'P','2013-04-20',1,NULL,'Rss','Поблизько вогню (21 квітня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4660,200,922822_10151563659147241_739970808_n.jpg\" />Якось один чоловік підійшов до Ісуса і мовив:</p>\n<p>— Учителю, усі ми знаємо, що походиш від Бога і навчаєш нас іти  шляхом істини. Але мушу Тобі сказати, що Твої послідовники, ті, яких  називаєш апостолами, або Твоєю спільнотою, мені анітрохи не подобаються. Я побачив, що вони зовсім не відрізняються від звичайних людей.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1131:poblyzko-vognju-21-kvitnja&catid=25:parafija-cat&Itemid=27',3179,NULL,1,26),
(3379,'P','2013-04-20',1,NULL,'Rss','Папа Франциск: Подолати спокусу фальшивого «здорового глузду»','<p><img style=\"margin: 10px; float: left;\" src=\"http://media01.radiovaticana.va/imm/1_0_684851.JPG\" height=\"147\" width=\"221\" />Літеплі  християни – це ті, які хотіли би будувати Церкву згідно зі своїми  мірками, але така Церква не є Ісусовою Церквою. На це звернув увагу Папа  Франциск, проповідуючи під час Святої Меси в каплиці резиденції «Дім  Святої Марти», яку він відслужив 20 квітня 2013 р. вранці.</p>\n<p>У  Діяннях Апостолів читаємо, що після переслідування в первісній  християнській громаді настав період миру, під час якого вона  зміцнювалася, та ходила і зростала в богобоязні та утішенні Святого  Духа.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1132:2013-04-20-20-10-05&catid=27:gk-v-ukrajini-cat&Itemid=57',3180,NULL,1,26),
(3380,'P','2013-04-21',1,NULL,'Rss','Клоун (22 квітня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4662,200,lvivskij-kloun-stvorjuje-kavo_699_s1.jpg\" />До кабінету відомого психіатра якось увійшов чоловік, на перший погляд  цілком урівноважений, поважний зі себе й елегантний. Та за кілька хвилин  розмови лікар переконався, що відвідувач перебуває у полоні глибокої  депресії і безпросвітного гнітючого смутку.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1133:kloun-22-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50',3181,NULL,1,26),
(3381,'P','2014-04-14',1,NULL,'Rss','Чин Соборування (Єлеопомазання)','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,4666,200,sunday-of-the-paralytic.jpg\" style=\"margin: 10px; float: right;\" />ЄЛЕОПОМАЗАННЯ</strong></p>\n<p>\"Страждає хто між вами? Хай молиться. Радіє хто? Хай псалми співає. Нездужає хто між вами? Хай прикличе пресвітерів церковних і хай вони моляться над ним, помазавши його єлеєм в ім\'я Господнє. Молитва віри спасе недужого, і Господь його підійме; і як він учинив гріхи, вони йому простяться.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=273:chyn-soboruvannja-jeleopomazannja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',3184,NULL,1,26),
(3382,'P','2013-04-22',1,NULL,'Rss','Великий вождь (23 квітня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4666,200,images.jpg\" />У  давні часи, ще коли всі ходили босі, був собі великий індіянський  вождь, який мав дуже чутливі стопи і черстве серце. Він страшенно  страждав, коли йому доводилося ходити по гострих каміннях, якими була  вкрита земля, що належала його племені.</p>\n<p>І після довгих роздумів вождь  прийняв важливе рішення: він наказав своїм воїнам убити всіх бізонів і  їх м\'якою шкірою вистелити терени володінь племені.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1134:velyky-vozhd-23-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50',3185,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3383,'P','2013-04-22',1,NULL,'Rss',' «Півтори тисячі годинників для незрячих – це прояв любові до ближніх», - о. Роман Сиротич, директор БФ «Карітас-Київ» ','<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/d4f55e5857.jpg\" height=\"112\" width=\"150\" /></div>\n<p class=\"xfmc0\">«З нагоди Року дияконії (соціального служіння) в УГКЦ у час Великого посту Благодійний фонд <a href=\"http://www.caritas-kiev.org.ua/\" target=\"_blank\">«Карітас-Київ»</a> завдяки жертводавцям провів масштабну акцію допомоги незрячим і літнім  людям в Україні - \"Годинники для незрячих\". Закуплено та роздано майже  1500 годинників і будильників. У планах ще така ж кількість, а, можливо,  і більша», - повідомляє о. Роман Сиротич, директор Благодійного фонду  «Карітас-Київ».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1135:2013-04-22-21-02-16&catid=29:propovidi-cat&Itemid=34',3186,NULL,1,26),
(3384,'P','2013-04-28',1,NULL,'Note','Розклад Богослужінь на 6-тий тиждень Великого посту','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>28.04.2013 неділя Квітна 6-та Великого посту</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-15 10-00 12-30 Літургія Святого Івана Золотоустого</span></p>','<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">22.04.2013 понеділок</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00Часи.Літургія св. Йоана Золотоустого&nbsp;Панахида Молитви оздоровлення і благословення мощами&nbsp;&nbsp; св. Шарбеля</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 Літургія св. Йоана Золотоустого Акафіст Страстям Господнім</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">23.04.2013 вівторок</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Часи, Літургія св. Йоана Золотоустого&nbsp; Панахида</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 .Літургія св. Йоана Золотоустого&nbsp;Панахида</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">24.04.2013 середа</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Вечірня з Літургією Напередосвячених Дарів.&nbsp;Прастас-сорокоусти.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00&nbsp;Вечірня з Літургією Напередосвячених Дарів, Хресна дорога</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">25.04.2013 четвер</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Часи, Літургія св. Йоана Золотоустого&nbsp; Панахида акафіст св. Миколаю Чудотв.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Освячення води і єлею, молитви оздоровлення</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 Літургія св. Йоана Золотоустого Акафіст Страстям Господнім</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">26.04.2013 п\'ятниця</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Вечірня з Літургією Напередосвячених Дарів Парастас-сорокоусти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">17:30 Хресна Дорога від Дніпра</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 Вечірня з Літургією Напередосвячених Дарів, Хресна дорога</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">27.04.2013 Субота Заупокійна.Лазерева</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Утрення</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">09-00 Літургія Святого Івана Золотоустого.&nbsp;Парастас-сорокоусти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">11-00 Акафіст Богородиці в крипті св.Сильвестра Папи</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 Літургія Святого Івана Золотоустого Вечірня</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">28.04.2013 неділя Квітна 6-та Великого посту</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-15 10-00 12-30 Літургія Святого Івана Золотоустого</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">телефон для довідок:&nbsp;<a href=\"tel:%28044%29%20570%2032%2090\" target=\"_blank\"><strong>(044) 570 32 90</strong></a>,&nbsp;<a href=\"tel:%28050%29%204473157\" target=\"_blank\"><strong>(050) 447 31 57</strong></a></span></p>',