INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3386,'P','2013-04-23',1,NULL,'Rss','Любомир ГУЗАР: «Тому ми не повинні втікати від політики, а поводитися в ній як християни»','<p> </p>\n<p><img style=\"margin: 10px; float: left;\" alt=\"гузар\" src=\"http://www.acp.org.ua/wp-content/uploads/2013/04/%D0%B3%D1%83%D0%B7%D0%B0%D1%80-200x300.jpg\" height=\"173\" width=\"116\" />Середина  першого дня Саміту наповнена топовими спікерами. Серед них –  Блаженніший Любомир (Гузар), глава Української Греко-Католицької Церкви  (2001-2011).</p>\n<p>«Мені не подобається, що в ЗМІ часто пишуть Європа й Україна, –  розпочав він. – Ми були Європою від самих початків. Про це свідчить  історія. Наприклад, київського князя Ярослава Мудрого називали «свекром  Європи». Маючи, якщо не помиляюся, дванадцятеро дітей, він одружував їх з  синами і доньками королів, князів всього європейського континенту.  Ярославові діти почували себе деінде, як вдома, оскільки були  європейцями.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1136:2013-04-22-21-05-44&catid=37:spilnota-cat&Itemid=49',3187,NULL,1,26),
(3387,'P','2013-04-22',1,NULL,'Rss','Молодь відроджує пам’ять про митрополита Григорія Яхимовича','<p><img width=\"98\" height=\"134\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size_vertical/news/pictures/hryhoriy_yahymovych.jpg\" style=\"margin: 10px; float: right;\" /></p>\n<div>\n<p>28 квітня відбудеться презентація книги про Григорія Яхимовича –  митрополита Української Греко-Католицької Церкви, одного з провідних  діячів українського національного відродження середини XIX століття, -  повідомляє Ярослав Жукровський, засновник Фундації імені Григорія  Яхимовича.</p>\n</div>\n<div>\n<p>Презентація відбудеться під час урочистості з нагоди 221-ї річниці від  дня народження та 150-ти річчя від дня смерті Григорія Яхимовича у його  рідному селі Підбірці, що на Львівщині.</p>\n</div>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1137:molod-vidrodzhuje-pamjat-pro-mytropolyta-grygorija-jahymovycha&catid=35:istorychni-postati-cat&Itemid=48',3188,NULL,1,26),
(3388,'P','2013-04-23',1,NULL,'Rss','Сорокова річниця довічних чернечих обітів Папи Франциска в Товаристві Ісуса','<p><img style=\"margin: 10px; float: right;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size_vertical/news/pictures/1_0_685271.jpg\" height=\"122\" width=\"88\" />У понеділок, 22 квітня 2013 р., виповнилося 40 років відтоді, як отець  Хорхе Берґольйо, теперішній Папа Франциск, склав урочисту чернечу  професію (довічні обіти) в Товаристві Ісуса, тобто, Ордені Отців  Єзуїтів.Як пояснив о. Федеріко Ломбарді, ТІ, Директор Ватиканського  прес-центру, який є співбратом у чернецтві Святішого Отця, дата 22  квітня не є випадковою.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1138:sorokova-richnycja-dovichnyh-chernechyh-obitiv-papy-francyska-v-tovarystvi-isusa&catid=32:gk-svit-cat&Itemid=58',3189,NULL,1,26),
(3389,'P','2013-04-24',1,NULL,'Rss','Пригода з їжаками (24 квітня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4665,200,578443_470694852973531_1569590393_n.jpg\" />Якось улітку сімейство їжаків поселилося у лісі. Погода стояла чудова,  було гаряче, і їжаки цілий день гралися у затінку дерев або пустували на  полях поблизу лісу, бавилися у хованки поміж квітів, ловили мушок, аби  підживитися, а коли приходила ніч, засинали на купках моху поблизько  нірок. Та ось одного дня вони побачили, що листя з дерев уже опадає.  Наближалася осінь! Листки падали і падали. Їжаки бавилися, ганяючись за  ними.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1139:prygoda-z-jizhakamy-24-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50',3190,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3390,'P','2013-04-24',1,NULL,'Rss',' «У сьогоднішньому світі важко бути молодим, а ще важче бути молодим християнином», - Блаженніший Святослав ','<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/1a90187012.jpg\" height=\"100\" width=\"150\" /></div>\n<p>«У сьогоднішньому світі важко бути молодим, а ще важче бути  молодим християнином. Переслідування завжди були притаманні життю  Церкви, але в сучасному західному світі вони стали іншими, більш  витонченими, прихованими, а тому – не менше загрозливими та руйнівними.  Часто молода людина соромиться сказати про своє християнство, боячись  бути осміяною».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1140:2013-04-24-05-54-14&catid=37:spilnota-cat&Itemid=49',3191,NULL,1,26),
(3391,'P','2013-04-24',1,NULL,'Rss','Миша (25 квітня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4666,200,thumbnail.jpg\" />Якось одна чемна і добре вихована мишка, що мала симпатичний домашній  вигляд, відчайдушно тікаючи від кота, опинилася у пивниці багатої вілли.  Там було зовсім темно і мишка впала до якоїсь дивної калюжі. Це був  добрий коньяк, що витік через попсутий отвір дубової бочки. Мишка  несміливо лизнула дивну рідину. Смак їй сподобався. Це було щось міцне  та запашне і гріло горло, мов вогонь.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1141:mysha-25-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50',3192,NULL,1,26),
(3392,'P','2013-04-25',1,NULL,'Rss','Молодіжна Хресна дорога у дусі Тезе, на схилах Дніпра','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4675,200,IMG_4035.JPG\" />У вівторок 23 квітня 2013 року парафіяни з різних храмів Києва взяли участь у молодіжній Хресній дорозі, яка була організована молоддю парафії Миколая Чудотворця, що на Аскольдовій могилі. Близько сорока людей, серед яких були члени товариства «Обнова» та їхній духівник отець Володимир Мальчин, отець Олексій Геник з парафії Святого Миколая Чудотворця, отець Михайло Томич, брати-семінаристи Київської Трьохсвятительської духовної семінарії, що в Княжичах та парафіяни з різних церков Києва йшли від самого Дніпра і аж до церкви Святого Миколая Чудотворця, що на Аскольдовій могилі.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1142:molodizhna-hresna-doroga-u-dusi-teze-na-shylah-dnipra&catid=25:parafija-cat&Itemid=27',3193,NULL,1,26),
(3393,'P','2013-04-24',1,NULL,'Rss','Думка про страшний суд нехай не буде причиною страху, але спонукою ставати кращими','<p><img style=\"margin: 10px; float: left;\" src=\"http://media01.radiovaticana.va/imm/1_0_685963.JPG\" /> «Людська історія розпочалася зі створенням  чоловіка та жінки на Божі образ і подобу, а завершиться остаточним  Христовим судом», – сказав Святіший Отець на початку катехизи,  пропонуючи цього разу замислитися над словами молитви <em>Вірую,</em> що Ісус «знову прийде в славі, щоб судити живих і мертвих».</p>\n<p>За  словами Папи, віра в другий Христовий прихід іноді не є дуже ясною та  переконливою в серці християн, однак, Ісус під час земного життя часто  говорив про дійсність цього приходу.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1143:dumka-pro-strashny-sud-neha-ne-bude-prychynoju-strahu-ale-sponukoju-stavaty-krashchymy&catid=32:gk-svit-cat&Itemid=58',3194,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3394,'P','2013-04-25',1,NULL,'Rss','Під пічкою (26 квітня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4676,200,vyrastili-detskoe-serdce.jpg\" />Молодим людям, які приходили до нього вперше, раббі  Бунам розповідав історію вчителя Езекії, сина Єкеля, що з Кракова.</p>\n<p>По багатьох роках тяжкої праці (що, проте, зовсім не порушило його віри в  Бога), йому приснився сон, ніби він мусить податися до Праги, аби там,  під мостом, що провадить до королівського палацу, шукати скарб.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1144:pid-pichkoju-26-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50',3195,NULL,1,26),
(3395,'P','2013-04-26',1,NULL,'Rss','Мовчання (27 квітня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4678,200,images.jpg\" />Один чоловік прийшов до монаха пустельника. Він спитав його: - Чого вчить тебе це життя у мовчанні? Монах, що саме набирав води з криниці, відповів своєму гостеві: - Подивись на дно цієї криниці. Що ти бачиш? Чоловік зазирнув до колодязя. - Нічого не бачу, - відповів. Якийсь час монах стояв непорушно, а тоді знову звернувся до гостя: - Подивись тепер! Що бачиш у колодязі?</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1145:movchannja-27-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50',3196,NULL,1,26),
(3396,'N','2013-04-27',1,NULL,'Note','Проща до зарваниці у світлинах','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Кожен хрест на цій ділянці має свою історію, розповідає митрофорний протопресвітер Церкви Миколая Чудотворця на Аскольдовій Могилі о. Ігор Онишкевич. Адже вірні церкви споруджували цю хресну дорогу своїми силами, за своєю ініціативою у буремний 2004 році, коли Україна переживала політичне напруження, а духовні отці шукали духовного зцілення народу. &laquo;Ця хресна дорога має пряме відношення до нашої парафії. Це ніби парафія створила собі своєю вірою і молитвою, заслугами &ndash; давністю місця &ndash; церкви святого Миколая, що стоїть на Аскольдовій могилі. Ми вимолили, створили цю дорогу, через наших людей.</span></p>','',NULL,NULL,3197,5,1,49),
(3397,'P','2013-04-26',1,NULL,'Rss',' «Молюся, щоб Святий Дух пробуджував у серцях священиків справжній місійний дух», - Блаженніший Святослав (Шевчук) ','<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/89c9c041a6.jpg\" height=\"104\" width=\"150\" /></div>\n<p>«Священик повинен бути праведником, що, за словами ап. Павла,  живе з віри... &nbsp;Він повинен навчати віри, проголошуючи Слово Боже...  Душпастир повинен навчати віри насамперед свідченням власного життя», -  підкреслює Блаженніший Святослав (Шевчук), Глава УГКЦ, у <a href=\"http://ugcc.org.ua/2705.0.html\">Посланні до священнослужителів УГКЦ на Страсний четвер.</a></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1148:2013-04-27-05-53-08&catid=27:gk-v-ukrajini-cat&Itemid=57',3199,NULL,1,26),
(3398,'P','2013-04-27',1,NULL,'Rss','Про фарисеїв, Понтія Пилата та Страсті Христові','<p>Страсті Христові й містерію  Воскресіння кожен переживає по-своєму, залежно від глибини віри,  духовного й життєвого досвіду. Поза тим, вони є невичерпним джерелом  творчого натхнення, оригінальних інтерпретацій дійових осіб і подій, що  передували хресній смерті і воскресінню Господа.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1149:pro-farysejiv-pontija-pylata-ta-strasti-hrystovi&catid=30:hrystyjanske-vchennia-cat&Itemid=37',3200,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3399,'P','2013-04-27',1,NULL,'Rss','Лиш небо, земля і молитва','<p>З Києва до Тернополя, а далі до родового села кардинала Йосипа Сліпого Заздрість просити благословення і відпущення гріхів приїхали 150 людей з різних куточків України – Києва, Кам’янець-Подільського, Одеси, Львова, Тернополя. Серед них – і літні люди, і багато молоді, діти і підлітки. Чимало з них відвідують прощу усі дев’ять років, відколи було встановлено Хресну дорогу від села Заздрість до Маріїнського духовного центру у Зарваниці, що на Тернопільщині.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1150:lysh-nebo-zemlja-i-molytva&catid=25:parafija-cat&Itemid=27',3202,NULL,1,26),
(3400,'P','2013-04-29',1,NULL,'Rss','Дурна черепаха (30 квітня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4685,200,1808.jpg\" />Якось в одній далекій долині почалася повінь. Дощ падав так сильно і  безперервно, що невдовзі часі всі поля і ниви були залиті водою. З  кожним днем вода підносилася все вище і вище.</p>\n<p>Раптом почувся чийсь плач. Плакала найповільніша і найдурніша черепаха.</p>\n<p>— Чому ти плачеш? — заґеґала гуска, що пролітала над нею.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1152:durna-cherepaha-30-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50',3204,NULL,1,26),
(3401,'P','2013-04-30',1,NULL,'Rss','«Сьогодні український народ можна порівняти з учнями Ісуса Христа»','<p class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/ec60b3c84a.jpg\" height=\"118\" width=\"80\" />«Наш народ і суспільство сьогодні можна порівняти з учнями  Ісуса Христа, що знову переживають втрату надії, розчарування та  безсилля, споглядаючи на свого зневаженого та розп’ятого Вчителя. Ми  сподівалися на відродження вільної держави, а натомість так часто  зазнаємо гіркоти розчарувань та досвідчуємо крах власних ілюзій». Про це  пише Глава Української Греко-Католицької Церкви Блаженніший Святослав  (Шевчук) у <a href=\"http://www.ugcc.org.ua/2722.0.html\">Великодньому посланні</a> до духовенства та всіх вірних УГКЦ.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1172:2013-04-29-21-14-25&catid=27:gk-v-ukrajini-cat&Itemid=57',3205,NULL,1,26),
(3402,'P','2013-04-29',1,NULL,'Rss','Папа Франциск: Сповідь – це не тортура, а прослава Бога, Який прощає','<p><img src=\"http://uk.radiovaticana.va/global_images/x.gif\" border=\"0\" height=\"3\" width=\"470\" /></p>\n<p><img style=\"margin: 10px; float: left;\" src=\"http://media01.radiovaticana.va/imm/1_0_687352.JPG\" height=\"147\" width=\"99\" />Соромитися  своїх гріхів – це чеснота смиренної людини, що приготовляє до прийняття  Божого прощення. Коментуючи  уривок з Першого послання святого Івана, в якому говориться, що «Бог є  світлом і жодної темряви немає в Ньому», Святіший Отець підкреслив, що  ми, натомість, в своєму житті маємо «сутінки», моменти, хвилини, «коли  все, також і у власному сумлінні, є темрявою», але це ще не означає  «ходити в темряві». «Ходити в темряві, – пояснив він, – означає бути  задоволеним самим собою: бути переконаним в тому, що мені не потребно  жодного спасіння». І з такої дороги не є легко звернути назад.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1171:papa-francysk-spovid-ce-ne-tortura-a-proslava-boga-jaky-proshchaje&catid=30:hrystyjanske-vchennia-cat&Itemid=37',3206,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3403,'P','2013-05-04',1,NULL,'Note',' Великодні Богослужіння на Першому Національному телеканалі','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Великодні Богослужіння</strong> на Першому Національному телеканалі розпочнуться у суботу, <strong>4 травня</strong>, о <strong>22:55</strong>, і триватимуть усю ніч.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Показ Хресної ходита ВоскресноїУтренівідбудеться <strong style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">з кафедральних соборів України</strong></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший Національний телеканал вітає українців із прийдешнім Святом Воскресіння Христового!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У цьому світлому часі щиро запрошуємо долучитися до <strong>прямих трансляцій </strong><strong>знаменних Великодніх богослужінь із кафедральних соборів України</strong><strong>.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Великодні Богослужіння</strong> на Першому Національному телеканалі розпочнуться у суботу, <strong>4 травня</strong>, о <strong>22:55</strong>, і триватимуть усю ніч.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Показ Хресної ходита ВоскресноїУтренівідбудеться <strong>з кафедральних соборів України</strong>:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Свято-Володимирський кафедральний собор у Херсонесі;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Свято-Володимирський кафедральний собор у Києві;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Храм Святого Василія Великого;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Свято-Андріївський собор.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Повтор Великодніх Богослужінь відбудеться у <strong>неділю, </strong><strong>5 </strong><strong>трав</strong><strong>ня</strong><strong>, з 8:00.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Коментують:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Головний редактор Першого Національного телеканалу&nbsp;<strong><em>Христина Стебельська</em></strong>, доктор богослов&rsquo;я <strong><em>Дмитро Степовик</em></strong> і духовенство традиційних українських Церков з часу Володимирового хрещення: <strong><em>о. Ігор Яців</em></strong>, <strong><em>о. Георгій Коваленко </em></strong>та <strong><em>о.</em></strong> <strong><em>Віталій Дан</em></strong><strong><em>ь</em></strong><strong><em>чак</em></strong>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Запрошуємо до єднання у молитві в часі Воскресіння Христового!</span></p>\r\n<p>&nbsp;</p>',NULL,NULL,3207,5,1,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3404,'P','2013-04-30',1,NULL,'Rss','Любовний лист (1 травня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4685,200,7fa0176de928def418b2ea14d36.jpg\" />Одна княжна на свої уродини отримала від нареченого важку, округлої  форми посилку. Дуже заінтригована, що там таке, вона швидко відкрила її і  знайшла… гарматний снаряд.</p>\n<p>Розчарована князівна кинула його на землю.</p>\n<p>При падінні зовнішня оболонка снаряда розламалася, а в ній виявився  менший снаряд зі срібла. Князівна взяла його в руки і, оглядаючи,  випадково сильно натиснула пальцями. Срібна оболонка прорвалася, а  всередині виявилась золота коробка.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1154:ljubovny-lyst-1travnja&catid=1:mystecka-storinka-cat&Itemid=50',3208,NULL,1,26),
(3405,'P','2013-05-02',1,NULL,'Rss','Великий четвер. Спомин Тайної вечері','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4683,200,tajna_v.jpg\" />У цей день ми пригадуємо подію, коли Ісус Христос умив ноги своїм апостолам,          Його останню вечерю з учнями та встановлення таїнства священства. Усе          це відбулося напередодні Пасхи, тому що Спаситель знав про свої останні          дні земного життя й про завершення місії спасіння людського роду через          смерть на хресті. І тут варто пригадати, що Син Божий багато разів намагався          застерегти свого учня Юду від зради, вживаючи часом дуже гострих слів,          як наприклад: \"Та один же з вас - диявол!» (Йо 6, 70).</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1169:velyky-chetver-spomyn-tanoji-vecheri&catid=30:hrystyjanske-vchennia-cat&Itemid=37',3210,NULL,1,26),
(3406,'P','2013-05-02',1,NULL,'Rss','Коло радости (3 травня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4685,200,35300292.jpg\" />Одного дня селянин, який жив неподалік від обителі, підійшов до  монастирської брами й енергійно застукав у неї своєю мозолистою рукою.  Коли воротар відчинив, чоловік, усміхаючись, показав йому великий кетяг  соковитого винограду:</p>\n<p>— Брате-воротарю, — сказав селянин, — знаєте, кому хочу подарувати цей чудовий виноград?</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1156:kolo-radosty-3-travnja&catid=1:mystecka-storinka-cat&Itemid=50',3211,NULL,1,26),
(3407,'P','2013-05-03',1,NULL,'Rss','Страсна п’ятниця ','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4683,200,456474.jpg\" />Головним християнським обрядом у страсну п\'ятницю є винесення  плащаниці. Плащаниця символізує полотно, в яке був загорнутий Ісус  Христос після зняття з Хреста. Після Воскресіння на полотні залишився  відбиток Його Образу, який утворився, як дослідили учені, шляхом  великого випромінювання.</p>\n<p><strong>Це єдиний день в році, коли не правиться Служба Божа</strong>,  є лише Вечірня з винесенням Плащаниці. Її виносять у другій половині  дня. Іде тричі обхід довкола церкви з Плащаницею, яку несуть за чотири  кінці священики або старші парафіяни.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1168:strasna-pjatnycja-&catid=30:hrystyjanske-vchennia-cat&Itemid=37',3212,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3408,'P','2013-05-03',1,NULL,'Rss','Правдивий сліпець (4 травня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4685,200,987554.jpg\" />Стародавня перська казка розповідає про чоловіка, що жив однією думкою,  котра його ніколи не покидала, — мати золото, якнайбільше золота.</p>\n<p>Це  прагнення з’їдало йому мозок, з’їдало і серце. В нього не було жодної  іншої думки, жодного іншого бажання. Тільки золото і золото.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1157:pravdyvy-slipec-4-travnja&catid=1:mystecka-storinka-cat&Itemid=50',3213,NULL,1,26),
(3409,'P','2013-05-03',1,NULL,'Rss','Великодня субота','<p class=\"стиль36\"><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4685,200,3491452781_9f266f228b.jpg\" />Розмаїття  великодніх яєць</strong></p>\n<p class=\"стиль36\"><strong>Крашанка - </strong>яйце  пофарбоване в один колір (червоний, жовтий синій, зелений, золотавий...)</p>\n<p class=\"стиль36\"><strong>Крапанка </strong>- на поверхню яйця  нанести крапочки воску (покапати), а  після занурити його у фарбу. Коли  поверхня яйця висохне, знову покапати воском  і занурити яйце у темнішу  фарбу.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1165:velykden-nabilshe-svjato-na-zemli&catid=37:spilnota-cat&Itemid=49',3214,NULL,1,26),
(3410,'P','2013-05-04',1,NULL,'Rss',' Велика Субота ','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4683,200,delegatiiblagodatnijiogoni2012.jpg\" />Досить часто ми десь “губимо” Велику Суботу. Тільки закінчується  Страсна П\'ятниця, відразу починаємо думати про святкування Воскресіння  як в храмі, так і потім вдома з родиною.</p>\n<p>Я теж для себе особисто відкрила тайну Великої Суботи зовсім недавно.  Спонукали мене до цього мої життєві переживання якоїсь темряви,  страждання, самотності.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1170:-velyka-subota-&catid=30:hrystyjanske-vchennia-cat&Itemid=37',3215,NULL,1,26),
(3411,'P','2013-05-04',1,NULL,'Rss',' Великоднє послання Папи Франциска ','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4685,200,207638595.jpg\" />Дорогі брати й сестри з усього світу, благодатного вам Великодня!<br />Яка велика радість для мене змогти донести вам цю звістку: Христос  Воскрес! Я хотів би, щоб вона досягнула кожну домівку, кожну сім’ю,  особливо там, де є найбільше страждань, лікарні, тюрми...</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1153:-velykodnje-poslannja-papy-francyska-&catid=28:tema-nomera-cat&Itemid=2',3216,NULL,1,26),
(3412,'P','2013-05-05',1,NULL,'Rss','НАРОДНІ ЗВИЧАЇ СВЯТКУВАННЯ ВЕЛИКОДНЯ','<p> </p>\n<p align=\"left\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4686,200,IMG_0293-2.jpg\" />В українського народу побутують чудові звичаї                    та традиції, які передаються з покоління до покоління.</p>\n<p> </p>\n<p align=\"left\">Упродовж пасхальних днів періодично дзвонять церковні                    дзвони:</p>\n<p> </p>\n<p align=\"left\">Дзвони дзвонять величаво, всіх до церкви звуть...<br /> Йдуть у Божий Храм дорослі й діточок ведуть.</p>\n<p> </p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1166:narodni-zvychaji-svjatkuvannja-velykodnja&catid=37:spilnota-cat&Itemid=49',3218,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3413,'P','2013-05-05',1,NULL,'Rss',' Легенди про Великдень','<p><img style=\"margin: 10px; float: right;\" src=\"http://dna.com.ua/uploads/posts/2013-04/1365749753_legendi-velikden-1.jpg\" alt=\"Легенди про Великдень\" title=\"Легенди про Великдень\" height=\"102\" width=\"136\" /></p>\n<div>\n<p>У Великодню суботу роблять крашанки, або, як кажуть на Київщині, «галунять яйця», а в Карпатах — «сливчать сливки».За народнім віруванням, крашанки готуються в суботу тому, що яйця,  пофарбовані в п\'ятницю, швидко псуються, а, бувши зробленими в суботу,  вони зберігаються протягом усіх свят.  \n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1167:-legendy-pro-velykden&catid=37:spilnota-cat&Itemid=49',3219,NULL,1,26),
(3414,'P','2013-05-05',1,NULL,'Rss','Цікаві факти про Великдень','<p><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/uskrsnje-jaje.gif\" height=\"132\" width=\"181\" />Христове Воскресіння, як історичний факт, сталося на перший по суботі  день, тобто у неділю. Тому християни почали святкувати цей день –  неділю.</p>\n<p class=\"rtejustify\">Для євреїв часів Ісуса число 8 (вісім) символізувало настання нових  месіанських часів. Тому часто неділю у старохристиянських текстах  називали і восьмим днем тижня, тобто месіанським.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1164:cikavi-fakty-pro-velykden&catid=28:tema-nomera-cat&Itemid=2',3221,NULL,1,26),
(3415,'P','2013-05-06',1,NULL,'Rss','Справа дрозда (7 травня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4685,200,images.jpg\" />Мені було десять років, коли я побачив, як дрозд жадібно дзьобав ягоди  бузини. Через якийсь час він здійнявся, полетів на мур нашого сусіда і  почав ретельно чистити собі дзьоб об камінь.</p>\n<p>Маленьке зернятко бузини, майже невидиме, впало йому з дзьоба і закотилося під мур.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1160:sprava-drozda-7-travnja&catid=1:mystecka-storinka-cat&Itemid=50',3222,NULL,1,26),
(3416,'P','2013-05-08',1,NULL,'Note','Великодні провідини Лук’янівської в’язниці','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4708,200,100_5748.jpg\" alt=\"\" width=\"200\" height=\"133\" />Дорогі парафіяни це ваша заслуга і ваша жертва-десятина на Божу справу, прошу вас на день П&rsquo;ятидесятниці в Зелені свята теж зі мною прийти до ув&rsquo;язнених жінок, принести їм гостинці і утвердити віру в Святого Духа, прошу підтримки добровольців у листуванні з жінками у підтримці їхніх покинутих дітей у служінні для ближнього.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Христос Воскрес з любов&rsquo;ю!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4708,200,100_5748.jpg\" alt=\"\" width=\"200\" height=\"133\" />У світлий вівторок Христового Воскресіння добровольці Карітас-Печерськ служби милосердя парафії св. Миколая на Аскольдовій могилі стараннями священика Костянтина Пантелея відвідали жінок у Лук&rsquo;янівській в&rsquo;язниці. Парафія готувалась до відвідин ув&rsquo;язнених жінок, готувались своєю вірою воскреслого Христа, який прийшов до убогих, хворих, ув&rsquo;язнених, прийшов до всіх, щоб сповістити визволення. Дари для жінок були освячена пасха і крашанка, яблука, солодощі і звичайно слово Боже - молитовники. Добровольцями були миряни, що трудяться у проекті тюремного служіння, який розпочався ще в 1992 році, коли парафією був заснований Карітас-Київ.У цей святковий день в свій час для потребуючих виділили сестри: Віра Гусєва, Ореста Цимблівська, Ганна Мушинська разом із священиками: о. Костянтином і о. Ігорем.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нас зустрічали усміхнені жінки, які старались гарно виглядати і старались похрестосатись, тобто привітатись поцілунком Пасхальної радості. Вони раділи молитві, освяченій воді, спільно молились Богородице Діво і просили у Господа звільнення і навернення. Цей час проведений з жінками був корисний і повчальний для всіх. Не забули ми привітати також і вихователів установи і вдячні їм за їхню щирість і сприяння.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дорогі парафіяни це ваша заслуга і ваша жертва-десятина на Божу справу, прошу вас на день П&rsquo;ятидесятниці в Зелені свята теж зі мною прийти до ув&rsquo;язнених жінок, принести їм гостинці і утвердити віру в Святого Духа, прошу підтримки добровольців у листуванні з жінками у підтримці їхніх покинутих дітей у служінні для ближнього.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ваш&nbsp;священник&nbsp;Ігор</span></p>',NULL,NULL,3225,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3417,'P','2013-05-09',1,NULL,'Rss','Великодні традиції Першого Національного','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4708,200,htmlimage.jpeg\" />Ще з часів дитинства пригадується світлий Великодній час. Родина збирається у бабусі, щоб поєднатися у молитві, поділитися свяченою паскою, похристосуватися писанками та обмінятися останніми новинами. І все це – під незмінний вже двадцять років показ Великодніх богослужінь на Першому Національному телеканалі. Відбувається Великодня трансляція з кафедральних соборів України в режимі реального часу, і в цьому також – традиція.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1173:velykodni-tradyciji-pershogo-nacionalnogo&catid=37:spilnota-cat&Itemid=49',3226,NULL,1,26),
(3418,'P','2013-05-09',1,NULL,'Rss','Яйце (10 травня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4685,200,319525-1920x1200.jpg\" />Бідна жінка знайшла одне яйце. Дуже зрадівши, вона покликала свого чоловіка і дітей та сказала:</p>\n<p>— Наші турботи і бідування скінчилися! Дивіться! Я знайшла оце яйце. Ми  його не їстимемо, а занесемо до сусіда, щоб його квочка висиділа нам  курчатко, яке стане куркою. Очевидно, і курку не будемо їсти, а  розведемо багато курей, щоб мати багато яєць.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1163:jace-10-travnja&catid=1:mystecka-storinka-cat&Itemid=50',3227,NULL,1,26),
(3419,'P','2013-05-10',1,NULL,'Note',' «Цивілізаційний вибір України зробив ще князь Володимир – це європейський вибір», - Блаженніший Святослав','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Свого часу князь Володимир, коли вирішив стати християнином, коли запропонував християнську віру киянам &ndash; зробив цивілізаційний вибір у бік християнської Європи. Це був європейський вибір рівноапостольного Володимира. І першим плодом цього вибору було те, що князь Ярослав Мудрий став тестем Європи. Таким чином не тільки князі, а й культура Київської Русі почала розвиватися як європейська християнська культура&raquo;.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Свого часу князь Володимир, коли вирішив стати християнином, коли запропонував християнську віру киянам &ndash; зробив цивілізаційний вибір у бік християнської Європи. Це був європейський вибір рівноапостольного Володимира. І першим плодом цього вибору було те, що князь Ярослав Мудрий став тестем Європи. Таким чином не тільки князі, а й культура Київської Русі почала розвиватися як європейська християнська культура&raquo;. Про це сказав (<a href=\"http://www.ugcc.tv/ua/media/55088.html\" target=\"_blank\">дивитися</a>) Глава Української Греко-Католицької Церкви Блаженніший Святослав (Шевчук) у великодньому інтерв&rsquo;ю 5-му каналу, відповідаючи на запитання, як Греко-Католицька Церква визначає цивілізаційний напрямок руху України: на Схід &ndash; у православно-екумейний під назвою &laquo;Митний союз&raquo; &ndash; чи на європейський протестантсько-католицький Захід.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Предстоятель Церкви висловив спостереження: &laquo;Якраз цього року, можливо, за таким дивним збігом обставин, хоча я думаю, що це Божа воля, ми святкуватимемо 1025-річчя Хрещення Русі-України й саме на цей рік припадає період історичного вибору щодо нового входження України в європейську сім&rsquo;ю народів&raquo;. Він вважає, що коли сьогодні хтось думає, куди йти &ndash; це &laquo;штучний, спокусливий голос&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Повторімо сьогодні в нашій країні той самий вибір у бік Європи, який зробив був князь Володимир. Коли хтось хоче використовувати православну Церкву як засіб для якоїсь геополітичної боротьби, тоді він завжди забуватиме коріння. Гадаю, що всі Церкви Київської традиції повинні відкрити для себе, що їхньою матірною Церквою є Церква Константинополя. І таким чином ми побачимо, в який бік князь Володимир скерував вектор християнського пошуку й звідки світло Євангелія прийшло на наші землі&raquo;, - наголошує Блаженніший Святослав.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Департамент інформації УГКЦ</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/IwCs9Vz56Zk\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>',NULL,NULL,3228,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3420,'P','2013-05-10',1,NULL,'Rss',' «Бути матір\'ю священика - це привілей і благородне покликання…» - Блаженніший Святослав ','<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/73eb41ab41.jpg\" height=\"181\" width=\"125\" /></div>\n<p>«Зі смиренною вдячністю схиляю голову перед матір\'ю кожного  священика нашої Церкви. Висловлюю вдячність матері священика передусім  за її жертви, за живу віру, – за віру, пронизану любов’ю, яку вона з  любов\'ю прищепила своєму синові», - пише у <a href=\"http://ugcc.org.ua/2732.0.html\">Листі до дорогих матерів священиків, які служать в УГКЦ</a>, Блаженніший Святослав (Шевчук), Глава УГКЦ.</p>\n<p>У цьому листі автор зазначає, що найурочистіший і найбільш натхненний  момент, що його може пережити вірний нашої Церкви, – це мати привілей  бути на свяченнях молодого юнака та отримати з його рук перше священиче  благословення на завершення чину рукоположення.&nbsp;</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1174:2013-05-10-18-19-46&catid=28:tema-nomera-cat&Itemid=2',3229,NULL,1,26),
(3421,'P','2013-05-11',1,NULL,'Rss','Координатор руху «За життя»: «Законодавча заборона абортів разюче зменшує кількість убитих дітей»','<p><a class=\"jcepopup\" title=\"Координатор руху &laquo;За життя&raquo;: &laquo;Законодавча заборона абортів разюче зменшує кількість убитих дітей&raquo;\" href=\"http://4vlada.com/uploads/1366787719-random1557-24_04_13.jpg\" target=\"_blank\"> <img style=\"margin: 10px; float: left;\" src=\"http://4vlada.com/uploads/1366787719-random1557-24_04_13.jpg\" alt=\"\" width=\"187\" height=\"82\" /><em>Координатор руху \"За життя!\" на Рівненщині&nbsp;Антоніна Торбіч переконана, що&nbsp;материнство &ndash; це неймовірна можливість реалізації для жінки. Фото з сімейного архіву</em></a></p>','',1,'http://oranta.org/index.php?option=com_content&view=article&id=1196:2013-05-11-10-10-56&catid=37:spilnota-cat&Itemid=49',3231,NULL,1,26),
(3422,'P','2013-05-11',1,NULL,'Rss','Неділя Біла','<img style=\"margin: 10px; float: left;\" alt=\"Томина неділя\" src=\"http://ecumenicalcalendar.org.ua/system/avatars/72/thumb/_.jpg?1267985590\" height=\"134\" width=\"220\" />\n<p>Наступна неділя після Христового Воскресення має різні назви.  Неділя  Томина — від події у святому Євангелії, де говориться про зустріч  апостола Томи з воскреслим Христом.</p>\n<p>Неділя Антипасхи, що з грецької означає \"замість Пасхи\", бо для  перших християн вона була наче повторенням празника Пасхи. У Щоденнику  паломництва Сильвії Аквітанської (IV ст. ) читаємо, що восьмого дня  після Христового Воскресення в Єрусалимі були окремі торжества та, що  того дня читали святе Євангеліє про навернення апостола Томи.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1199:nedilja-bila&catid=28:tema-nomera-cat&Itemid=2',3232,NULL,1,26),
(3423,'P','2013-05-11',1,NULL,'Rss','Коли Бог творив Маму (12 травня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,591.jpg\" />Одного  разу добрий Бог вирішив створити... маму. Шість днів та ночей Він  роздумував і експериментував. Та ось з\'явився ангел і каже:</p>\n<p>- Ти стільки часу тратиш на неї!</p>\n<p>-  Так... Але чи ти читав вимоги замовлення? Вона повинна складатися зі  180 рухомих частин, які можна було б при потребі замінити, її поцілунок  має лікувати все - від зламаної ноги до розчарування в коханні, також  вона мусить мати шість пар рук.</p>\n<p> </p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1176:koly-bog-tvoryv-mamu-12-travnja&catid=1:mystecka-storinka-cat&Itemid=50',3233,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3424,'P','2013-05-12',1,NULL,'Rss','Що було б із нашою цивілізацією, якби Христос не воскрес?','<p class=\"nimages\"><img style=\"margin: 10px; float: left;\" src=\"http://dyvensvit.org/media/gallery/intxt/j/a/jakby8hrystos8ne8voskres.jpg\" /> <strong class=\"main\">Християни вірять, що Христос, прийшовши в наш уражений  гріхом світ, не міг не померти, а померши, не міг не воскреснути. Для  того Він - Бог - і став людиною. Уявити, що Він не воскресав - це все  одно, що уявити, ніби Він і не народжувався.</strong><br />Уявити таке можливо лише в рамках фантастики. І, до речі, деякі  фантасти справді зверталися до цієї теми. Для більшості з них як людей  нецерковних відсутність факту Воскресіння Христового означає, передусім,  відсутність Церкви.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1197:shcho-bulo-b-iz-nashoju-cyvilizacijeju-jakby-hrystos-ne-voskres&catid=30:hrystyjanske-vchennia-cat&Itemid=37',3234,NULL,1,26),
(3425,'P','2013-05-13',1,NULL,'Rss','Греко-католики - відкриті для всіх! - брат Сергій Синовіцький','<p><img style=\"margin: 10px; float: left;\" src=\"http://dyvensvit.org/media/gallery/intxt/k/t/ktds10_synovic1010kyj.jpg\" /> Греко-католицька  громада завжди була і продовжує бути  активною в суспільному житті.  Брат Сергій Синовіцький навчається на  третьому курсі Київської  Трьохсвятительської Духовної семінарії,  випускники якої стають  священиками... Про греко-католицьку громаду,  важливість служіння,  доброї справи та щирого наміру ми поговорили з  братом Сергієм<strong>.</strong></p>\n<p><strong>- Сергію, що Вас покликало вчитися на священика?</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1198:greko-katolyky-vidkryti-dlja-vsih-brat-sergi-synovicky&catid=27:gk-v-ukrajini-cat&Itemid=57',3236,NULL,1,26),
(3426,'P','2013-05-15',1,NULL,'Rss','Вибір  (15 травня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,DJfUEFGQR10.jpg\" />Один чоловік завжди відчував себе пригніченим через різні життєві  труднощі. І якось він поділився цим із відомим духовним наставником.</p>\n<p>- Не можу більше! Життя стало просто нестерпне!</p>\n<p>- Учитель узяв у пригорщу попелу і вкинув до склянки, у якій перед тим була кришталево чиста питна вода, і промовив:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1179:vybir-15-travnja&catid=1:mystecka-storinka-cat&Itemid=50',3238,NULL,1,26),
(3427,'P','2013-05-15',1,NULL,'Rss','«Від Бенедикта до Франциска» – книга, яка додає оптимізму','<p><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size_vertical/news/pictures/da-benedetto-a-francesco.jpg\" height=\"133\" width=\"97\" />Минуло вже два місяці з часу обрання на кафедру апостола Петра  Святішого Отця Папу Франциска; і цей короткий відрізок часу можна по  праву назвати плідним та незвичайним. З цієї нагоди книжкові полиці  поповнила нова книга отця Антонія Спадаро, директора всесвітньовідомого  католицького журналу «Католицька Цивілізація» (<em>La</em> <em>Civilt</em><em>à </em><em>Cattolica</em>), під назвою «Від Венедикта до Франциска».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1201:2013-05-15-06-29-57&catid=32:gk-svit-cat&Itemid=58',3239,NULL,1,26),
(3428,'P','2013-05-14',1,NULL,'Rss',' «Наша Церква є більмом в оці для всіх тих, хто не прагне справжньої єдності…» - Глава УГКЦ для КАІ ','<p class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/1896e77d7a.jpg\" height=\"142\" width=\"98\" />«Примирення між нашими Церквами допоможе порозумітися  російському і українському народам. Як це часто буває між сусідами, у  нас є багато взаємних історичних проблем, але ми не можемо будувати  майбутнього без християнського спілкування. Процес примирення також  допоможе подолати українофобію в Росії та зупинить процес русифікації  України», - <a href=\"http://ekai.pl/wydarzenia/temat_dnia/x66767/kierujemy-do-braci-z-polski-prosbe-o-wybaczenie/?page=1\" target=\"_blank\">про це сказав Блаженніший Святослав (Шевчук), Глава УГКЦ, в інтерв’ю для КАІ (Католицької інформаційної агенції)</a>.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1200:2013-05-15-06-21-55&catid=27:gk-v-ukrajini-cat&Itemid=57',3240,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3429,'P','2013-05-15',1,NULL,'Rss','Папа Франциск: Хто не здатний любити, той завжди самотній','<p>Якщо хочемо по-справжньому наслідувати Ісуса, ми повинні прожити життя,  як дар, призначений для того, щоб його дарувати іншим, «а не як скарб,  який треба зберегти». Про це говорив Папа Франциск під час проповіді, пропонуючи призадуматися над  протиставленням дороги любові та шляху егоїзму.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1202:papa-francysk-hto-ne-zdatny-ljubyty-to-zavzhdy-samotni&catid=32:gk-svit-cat&Itemid=58',3241,NULL,1,26),
(3430,'P','2013-05-18',1,NULL,'Note','Подаруй землі квітку - народна толока на Аскольдовій Могилі','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4725,200,toloka.jpg\" alt=\"\" width=\"200\" height=\"200\" />Аскольдова могила запрошує на народну толоку <strong>\"Подаруй Землі квітку\"</strong>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Збір <strong>18 травня 2013 р.Б. об 11:00</strong> біля церкви св. Миколая Чудотворця на Аскольдові Могилі. </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мета народної толоки <strong>\"Подаруй землі квітку\"</strong> - уквітчати клумби, посадити молоді дерева, що скріплюватимуть грун дніпровських схилів, прибрати прицерковну територію, прославляти Бога зберігаючи все живе створене Всевишнім.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4725,200,toloka.jpg\" alt=\"\" width=\"200\" height=\"200\" />Аскольдова могила запрошує на народну толоку <strong>\"Подаруй Землі квітку\"</strong>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Збір <strong>18 травня 2013 р.Б. об 11:00</strong> біля церкви св. Миколая Чудотворця на Аскольдові Могилі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мета народної толоки <strong>\"Подаруй землі квітку\"</strong> - уквітчати клумби, посадити молоді дерева, що скріплюватимуть грун дніпровських схилів, прибрати прицерковну територію, прославляти Бога зберігаючи все живе створене Всевишнім.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Запрошуємо долучитись до творення прекрасного дітей \"Карітас-Печерський\" та молодь парафії.</span></p>',NULL,NULL,3242,5,1,17),
(3431,'P','2013-05-15',1,NULL,'Rss','Наш Владика','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3524,200,CRW_7040.jpg\" />У Томину неділю поминання спочилих. Цей день є пам\'ятним для  греко-католиків. За благословенням владики Софрона Дмитерка (світла і  вічна йому пам\'ять) стало традицією, на місцевому цвинтарі села  Чапаївка, у цю неділю, на місці першого поховання поминати Блаженного  Йосафата Коциловського єпископа Перемиcького.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1203:nash-vladyka&catid=25:parafija-cat&Itemid=27',3243,NULL,1,26),
(3432,'P','2013-05-16',1,NULL,'Rss','Спокуса (16 травня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,iir_m.jpg\" />Спекотливої літньої днини робітникові, що працював на багатого  господаря, було звелено скопати сад. Наймит неохоче взявся до роботи.  При цьому він нарікав на Адама, що через нього люди отак використовують  працю інших людей.</p>\n<p>Ці ремствування і прокльони дійшли до вух господаря.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1180:spokusa-16-travnja&catid=1:mystecka-storinka-cat&Itemid=50',3244,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3433,'P','2013-05-16',1,NULL,'Rss','Чи насправді ми бажаємо \"єдиноспасаємого\" примирення?','<p><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_prymyrennya.jpg.jpg\" height=\"147\" width=\"202\" />На  цей рік припадає 70-та річниця Волинської трагедії, коли загинуло  багато тисяч невинних людей, поляків за походженням. У зв\'язку з цією  датою з боку церковних, політичних і громадських осередків лунають різні  звернення та заяви.</p>\n<p>Пригадуються різні трагічні моменти у стосунках українського та  польського народів, як також акти примирення між українцями та поляками  на різних рівнях.&nbsp;Настало певне напруження в польсько-українських  відносинах, яке породжує неспокій та тривогу.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1205:chy-naspravdi-my-bazhajemo-qjedynospasajemogoq-prymyrennja&catid=27:gk-v-ukrajini-cat&Itemid=57',3245,NULL,1,26),
(3434,'P','2013-05-17',1,NULL,'Rss','Про захід сонця (17 травня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,83c31c2bda8c8165378f09fd78cc.jpeg\" />Дуже давно один місіонер разом зі своїм провідником-індіянином пробився  через Скелясті Гори. Щовечора, коли чубок сонця ховався за видноколом, молодий індіянин  відходив від місіонера, повертався у той бік і починав ритмічно  переступати з ноги на ногу. При цьому він упівголоса наспівував якусь  тужливу пісню.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1181:pro-zahid-soncja-17-travnja&catid=1:mystecka-storinka-cat&Itemid=50',3247,NULL,1,26),
(3435,'P','2013-05-18',1,NULL,'Rss','Молоко для Господа Бога (18 травня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,ua.The-love-of-God-must-have-the-first-place....jpg\" />Якось Мойсей, блукаючи по пустелі, зустрів пастуха. Мойсей провів із ним  цілий день, допоміг подоїти овець. Коли вже стемніло, пророк зауважив,  що пастух відлив трохи молока до посудини і поставив її на скелі,  недалеко від того місця, де вони ото перебували.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1182:moloko-dlja-gospoda-boga-18-travnja&catid=1:mystecka-storinka-cat&Itemid=50',3248,NULL,1,26),
(3436,'P','2013-05-19',1,NULL,'Rss','Сигнал (19 травня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,TSvVeoIE9I4.jpg\" />Після страшної бурі на морі вцілілий бідолаха, учепився за рештки свого  човна, добрався до маленького безлюдного острівка. Острівець той майже  суцільно вкрили непривітні гострі скелі. Бідака став гаряче благати  Бога, аби його порятував. Кожного дня він вдивлявся, чи десь-там, з-за  горизонту, не з’явиться допомога, проте ніхто з нею не поспішав.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1183:sygnal-19-travnja&catid=1:mystecka-storinka-cat&Itemid=50',3249,NULL,1,26),
(3437,'P','2013-05-19',1,NULL,'Rss','Неділя мироносиць','<p><img style=\"margin: 10px; float: left;\" src=\"http://i280.photobucket.com/albums/kk188/rozdum/myronosyci_2012_rozdum.jpg\" height=\"129\" width=\"164\" /><em>Вернувшись від гробу, про все те сповістили Одинадцятьох та всіх інших. Лк.24.9</em></p>\n<p>Дорогі браття і сестри у Христі, у неділю Мироносиць, читаючи  розповідь про вірних жінок-учениць Христа, згадуємо про вірних і  побожних жінок, послідовниць Ісуса Христа. Вони є прикладом живої віри в  живого воскреслого Спасителя-Месії. Повірили у Воскресіння Спасителя,  не побачивши Його тіла, перебороли страх нерозуміння і сповістили учням  цю радісну новину.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1206:nedilja-myronosyc&catid=30:hrystyjanske-vchennia-cat&Itemid=37',3250,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3438,'P','2013-05-17',1,NULL,'Note','Гроші повинні служити, а не керувати','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Святіший Отець: Гроші повинні служити, а не керувати! Папа любить усіх, багатих і бідних, але в Христове ім&rsquo;я Папа зобов&rsquo;язаний нагадувати багатому, щоб допомагати бідному, його поважати та йому сприяти. Папа закликає до безкорисливої солідарності та спрямування етики на користь людині в фінансовій та економічній сфері.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нові посли з чотирьох континентів світу &ndash; Киргизької Республіки в Азії, Антигуа і Барбуди, центральноамериканської країни у східній частині Карибського моря, Великого Герцогства Люксембургу в Європі та Ботсвани, держави в Південній Африці &ndash; 16 травня 2013 року вручили вірчі грамоти Святішому Отцеві Франциску. </span><br /><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Чотири країни з різних континентів та з відмінними внутрішніми й церковними обставинами, стоять перед обличчям ідентичних викликів, таких як економічна криза, соціальні напруження, утвердження нової невидимої фінансової тиранії.</span><br /><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Звертаючись до послів, Папа Франциск наголосив, що тиранія грошей та корупції випливає з відкидання етики, відмови від Бога. Сучасна людина применшена до однієї вимоги &ndash; споживацтва. В той час, коли бачимо прогрес в галузі медицини, освіти та комунікацій, більша частина людства і надалі живе у щоденній нестабільності. Згасає радість життя, страх і відчай охоплюють серця багатьох людей, навіть у так званих &laquo;багатих країнах&raquo;, зростає неморальність та насилля, дедалі очевиднішою стає бідність</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">. </span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Однією з причин таких ситуацій, на думку Святішого Отця, є неправильне ставлення до грошей, згода на те, щоб гроші панували над нами і над нашим суспільством. Папа підкреслив, що причиною фінансової кризи є глибока антропологічна криза, яка заперечує першість людини в суспільстві, в економіці. Людина не тільки зведена до споживацтва, але й сама вважається немов товаром, який можна використати і викинути. Святіший Отець вказав, що утверджується нова невидима тиранія фінансових спекуляцій, яка відкидає етику, адже вона посилається на Бога, Який перебуває поза межами ринкових категорій. </span><br /><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Побажанням Папи є здійснення етичної фінансової реформи, яка би, в свою чергу, привела до економічної реформи, що була би корисною для всіх. Однак, це вимагає мужньої зміни у наставленні політичних діячів, наголосив Святіший Отець і додав: &laquo;Гроші повинні служити, а не керувати! Папа любить усіх, багатих і бідних, але в Христове ім&rsquo;я Папа зобов&rsquo;язаний нагадувати багатому, щоб допомагати бідному, його поважати та йому сприяти. Папа закликає до безкорисливої солідарності та спрямування етики на користь людині в фінансовій та економічній сфері.</span><br /><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Святіший Отець пригадав слова святого Івана Золотоустого, що &laquo;не ділитись з бідними власним добром означає їх обкрадати і позбавляти їх життя. Блага, якими володіємо, не є нашими, але їхніми&raquo;. </span><br /><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церква заохочує державних керівників по-справжньому служити загальному добру своїх народів та формувати нове політичне й економічне мислення, яке сприятиме здоровому співіснуванню економічної та суспільної сфери.</span></p>\r\n<div style=\"text-align: justify;\"><br /> <br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Текст із сторінки <a href=\"http://uk.radiovaticana.va/Articolo.asp?c=692764\">http://uk.radiovaticana.va/Articolo.asp?c=692764</a></span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">сайту Радіо Ватикану</span></div>',NULL,NULL,3246,5,1,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3439,'P','2013-05-19',1,NULL,'Rss','ЗАКЛИК БЛАЖЕННІШОГО СВЯТОСЛАВА ДО МОЛИТВИ ЗА УКРАЇНСЬКІ РОДИНИ','<p>Уже третій рік поспіль Українська Греко-Католицька Церква з ініціативи Комісії у справах родини долучилася до проведення в Україні Тижня подружжя – міжнародного руху, який спрямований на поширення в суспільстві сімейних цінностей. Святкування в нашій Церкві розпочалося з Дня матері – 12 травня, а <a href=\"http://uk.wikipedia.org/wiki/15_%D1%82%D1%80%D0%B0%D0%B2%D0%BD%D1%8F\" title=\"15 травня\">15 травня</a> ми відзначали Міжнародний день сім\'ї. Заохочуємо душпастирів, прородинні організації та сім’ї і надалі докладати всіх зусиль задля підтримки традиційних родинних цінностей, базованих на Божому Об’явленні та науці Христової Церкви.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1207:zaklyk-blazhennishogo-svjatoslava-do-molytvy-za-ukrajinski-rodyny&catid=27:gk-v-ukrajini-cat&Itemid=57',3251,NULL,1,26),
(3440,'P','2013-05-19',1,NULL,'Rss',' Папа Франциск: церковність є фундаментальною характеристикою кожного християнина','<p class=\"news-title\"><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/1_0_693557.jpg\" height=\"127\" width=\"174\" />Новизна, гармонія та місія – три слова, пов’язані з дією Святого Духа,  стали темою проповіді, яку Папа Франциск виголосив під час урочистого  Євхаристійного богослуження з нагоди Урочистості Зіслання Святого Духа. У  неділю, 19 травня 2013 р., що за новим стилем була неділею  П’ятидесятниці, понад 250 тисяч членів різних мирянських рухів, спільнот  та організацій, як також звичайних паломників, знову заповнили площу  Святого Петра у Ватикані та вулицю Кончіліаціоне.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1208:-papa-francysk-cerkovnist-je-fundamentalnoju-harakterystykoju-kozhnogo-hrystyjanyna&catid=32:gk-svit-cat&Itemid=58',3252,NULL,1,26),
(3441,'P','2013-05-19',1,NULL,'Rss','Урочисте причастя на Аскольдовій','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4734,200,182828_138275583031727_1784860436_n.jpg\" />11-12 травня&nbsp; 2013 року відбулося свято у храмі св. Миколая Доброго на Аскольдовій моглі. До Першої св. Сповіді та Урочистого св. Причастя приступило 22 осіб. Решта дітей (4 осіб) у зв’язкуз поважними причинами святкуватимуть &nbsp;влітку під час літнього табору. На цій події були присутні батьки, родини,знайомі та з’їхалось багато гостей з різних куточків світу. Святкову св. Літургію, відслужив о. Ігор Онишкевич, настоятель храму, та о. Константин Пантелей. Рядочок дітей, вдягнених в українське вбрання, немов намистинки розсипались по церковному подвір’ю, тримаючи в руках прикрашені свічки, що символізують світло Христове, яке освітлює життя.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1209:urochyste-prychastja-na-askoldovi&catid=25:parafija-cat&Itemid=27',3253,NULL,1,26),
(3442,'P','2013-05-20',1,NULL,'Rss','Клубок вовни (20 травня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,images.jpg\" />Королівський двір гучно обходив святкування з нагоди прибуття короля до  столиці. У багато прикрашеній залі король приймав дари від своїх  підданих. Усі подарунки були дуже цінні: коштовна зброя, срібляні  келихи, тканини, гаптовані золотом.</p>\n<p>Коли вервиця жертводавців уже майже підходила до кінця, з’явилася  столітня селянка. Вона йшла, важко ступаючи у своїх дерев’яних  взувачках. Жінка накульгувала і спиралася на палицю. У повній тиші вона  вийняла з кошика дарунок, що був старанно згорнутий у полотно.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1184:klubok-vovny-20-travnja&catid=1:mystecka-storinka-cat&Itemid=50',3254,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3443,'P','2013-05-20',1,NULL,'Rss','А Кароль Войтила давав списувати на іспитах?','<p><img style=\"margin: 10px; float: left;\" alt=\"Кароль Войтила\" src=\"http://www.credo-ua.org/wp-content/uploads/2013/05/Wojtyla-sciagac.jpg\" height=\"170\" width=\"144\" /></p>\n<p><strong>Як годиться найкращому учневі в класі, Кароль Войтила перший  написав іспит на атестат зрілості, але віддав його останнім, аби не  засмучувати однокласників, – каже Євгеній Мруз, останній живий співучень  папи Йоана Павла ІІ.</strong></p>\n<p>18 травня – день чергового спомину дня народження блаженного Папи  Войтили (18.05.1920), 13 травня був день спомину замаху на нього на  площі св.Петра, а 14 травня минуло 75 років відтоді, як Кароль Войтила  склав на «відмінно» іспит на атестат зрілості у державній гімназії  ім.Мартіна Вадовіти у Вадовіцах. На шкільному заключному балу Льолек  пройшов кілька кругів танцю, але потім хутко зник, навіть не ковтнувши  вина.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=1210:a-karol-votyla-davav-spysuvaty-na-ispytah&catid=35:istorychni-postati-cat&Itemid=48',3255,NULL,1,26),
(3444,'P','2013-05-20',1,NULL,'Rss',' Духовна криза починається з браку любові у сім’ї, – Блаженніший Любомир Гузар','<p class=\"news-submitted\"><img style=\"margin: 10px; float: right;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/film_01.staticheskie001.jpg\" height=\"136\" width=\"187\" /><strong><em>Ваше Блаженство, наше суспільство перебуває в глибокій  кризі, багато хто говорить, що це криза духовна, і вбачає в ній  відображення кризи української родини, сім’ї як родинного осередку, і,  перш за все, як домашньої церкви, яка, з поміж іншого, перестала бути  осередком спільної молитви, перестала бути місцем, де підростаюче  покоління перебирає естафету віри від своїх батьків. Чи, на Вашу думку,  це відповідає дійсності?</em></strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1211:-duhovna-kryza-pochynajetsja-z-braku-ljubovi-u-simji--blazhennishy-ljubomyr-guzar&catid=37:spilnota-cat&Itemid=49',3256,NULL,1,26),
(3445,'P','2013-05-20',1,NULL,'Rss',' Або ми євангелізуємо суспільство, або завтра нас переслідуватимуть','<p class=\"news-picture\"><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/6a8d71abab9f3810320f6a7067002b1e.jpg\" height=\"121\" width=\"167\" />Минулий тиждень на європейському континенті ознаменував собою новий  етап у проведенні нової ліберальної культурної революції – гендерної.  Найстаріша демократія континенту – Франція, практично перетворилася на  ліберальну диктатуру: її <a href=\"http://catholicnews.org.ua/prezident-frantsiyi-pidpisav-zakon-pro-odnostatevi-shlyubi#point\">президент  Франсуа Олланд таки підписав закон, який зрівнює в правах  гомосексуальні пари з нормальними сім’ями, і це попри мільйонні протести  французів</a>.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1212:-abo-my-jevangelizujemo-suspilstvo-abo-zavtra-nas-peresliduvatymut&catid=32:gk-svit-cat&Itemid=58',3257,NULL,1,26),
(3446,'P','2013-05-20',1,NULL,'Rss','Самотність – самота','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4740,200,images.jpg\" />Вона прийшла і обійняла мене за плече. Сиділа мовчки і, здавалось, навіть не дихала або ж дихала моїм диханням, так що cпершу зовсім не помічала її. Вона чекала, щоби вкотре бути потрібною. Вміла чекати. Тихо, самотньо, довго. Найчастіше вона з`являлася пізньою вечоровою порою, разом зі мною коротала безсонну ніч.&nbsp; Не говорила нічого, слухала. Переживала мою тривогу, неспокій, сум`яття, розчарування. Виявляла потроху мій біль. О так, тоді він краявся надвоє. Він вже не був таким нестерпним і пекучим. Не дошкуляв, не тупцював на місці. Відлягав помалу.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1214:samotnist--samota&catid=1:mystecka-storinka-cat&Itemid=50',3258,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3447,'P','2013-05-21',1,NULL,'Rss','Дві голови  (21 травня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,img0.jpg\" />Над берегом одного з озер на півночі Індії жив дивний птах, який мав дві  голови. Одна була з правого боту тулуба, а інша – з лівого. Один тулуб з  двома головами.</p>\n<p>Якось птах кружляв у пошуках поживи. Очі правої голови зауважили соти з диким медом. Птах одразу кинувся згори до них.</p>\n<p>Голова, котра з лівого боку, просила: \"Дай мені трохи\".</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1185:dvi-golovy-21-travnja&catid=1:mystecka-storinka-cat&Itemid=50',3259,NULL,1,26),
(3448,'P','2013-05-21',1,NULL,'Note','Концерт хору Аскольд у неділю жінок мироносиць','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Чому ми святкуємо день жон-мироносиць? Не тільки пахуче миро несли вони до гробу Спасителя. Вони несли свою велику віру, непохитну вірність, співчуття, милосердя і любов. Не зникли у світі ні вірність, ні любов. Вони - як і турбота, самовідданість, милосердя, доброта, - завжди пов\'язані з образом жінки.</span></p>','<p><iframe src=\"http://www.youtube.com/embed/TQw5FcIIuNk\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe></p>\r\n<p><iframe src=\"http://www.youtube.com/embed/nTHc9mPZfCE\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe></p>\r\n<p><iframe src=\"http://www.youtube.com/embed/jTF2Vq8pmZk\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe></p>\r\n<p><iframe src=\"http://www.youtube.com/embed/wChtLNiTq_A\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe></p>\r\n<p><iframe src=\"http://www.youtube.com/embed/U2-XOEMXhdM\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe></p>',NULL,NULL,3261,5,1,48),
(3449,'P','2013-05-22',1,NULL,'Rss','Перенесення мощів св. Миколая Чудотворця з Мир до міста Барі','<p><img style=\"margin: 10px; float: left;\" alt=\"Перенесення мощів св. Миколая Чудотворця з Мир до міста Барі\" src=\"http://ecumenicalcalendar.org.ua/system/avatars/145/thumb/54-mykola.JPG?1268258866\" height=\"146\" width=\"109\" /></p>\n<p>В XI столітті грецька імперія переживала важкий час. Турки  спустошували її володіння у Малій Азії, розоряли міста і села, вбивали  їх жителів, і супроводжували свої розбійні напади зневагою святих  храмів, мощів, ікон і книг. Мусульмани робили замах знищити мощі  святителя Миколая, глибоко шанованого всім християнським світом.</p>\n<p>Осквернення святинь обурювало не тільки східних, але і західних  християн. Особливо побоювалися за мощі святителя Миколая християни в  Італії, серед яких було багато греків.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1216:perenesennja-moshchiv-sv-mykolaja-chudotvorcja-z-myr-do-mista-bari&catid=30:hrystyjanske-vchennia-cat&Itemid=37',3263,NULL,1,26),
(3450,'P','2013-05-22',1,NULL,'Rss','Любомир Гузар: Мусимо вирішити чи хочемо почати з прощення чи ж з визначення того, хто більше згрішив','<p class=\"news-picture\"><img style=\"margin: 10px; float: right;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size_vertical/news/pictures/huzar.jpg\" height=\"187\" width=\"136\" />Мусимо вирішити, чи хочемо ми почати з прощення або ж з  визначення того, хто більше згрішив. В такому випадку знайдемо багато  злих речей з обох сторін. Єдиним виходом однак є воля простити не лише з  однієї сторони, – вважає кардинал Любомир Гузар. На думку Верховного  Архиєпископа-емерита Києво-Галицького УГКЦ, це добре, що дійшло до  суперечок навколо відзначення 70-ої річниці Волинської Трагедії. «Для  мене це – Боже благословення, що дійшло до розпалення емоцій. Теперішня  ситуація спонукає нас до того, що ми мусимо щось зробити. Українці і  поляки повинні задуматись: Що далі? Чи маємо надалі воювати? В історії  ми вже наробили достатньо дурниць. Чи маємо так жити і надалі? Я вважаю,  що в цьому немає сенсу», – говорив кардинал Гузар в інтерв’ю <a target=\"_blank\" href=\"http://ekai.pl\">польській агенції КАІ</a>.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1217:ljubomyr-guzar-musymo-vyrishyty-chy-hochemo-pochaty-z-proshchennja-chy-zh-z-vyznachennja-togo-hto-bilshe-zgrishyv&catid=37:spilnota-cat&Itemid=49',3264,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(3451,'P','2013-05-22',1,NULL,'Rss','Що любить Папа – про хобі Франциска','<p><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/c834502922.jpg\" height=\"152\" width=\"209\" /><strong>Він ухиляється від комп’ютера. Коли його питають про інтернет, то  він жартує, що почне ним користуватись вже на пенсії. Ще будучи  кардиналом, користувався електричною друкарською машинкою. Усі листи, що  надходили електронною поштою, йому роздруковували і передавали  секретарі.</strong></p>\n<p>Більшість давніх сусідів, якщо пам’ятають малого Хорхе, то не з  футбольного поля. Його запам’ятали з книжками, які він постійно з собою  носив.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1218:shcho-ljubyt-papa-pro-hobi-francyska&catid=32:gk-svit-cat&Itemid=58',3265,NULL,1,26),
(3452,'P','2013-05-22',1,NULL,'Rss','Перепоховання українського Пророка','<p align=\"justify\" lang=\"uk-UA\"><img style=\"margin: 10px; float: right;\" src=\"http://volyniany.org.ua/images/stories/reportazh/shevchenko/kanev1.jpg\" height=\"162\" width=\"254\" /><strong>22 травня </strong>–  важлива дата для кожного українця. У цей день прах Тараса Григоровича  Шевченка, згідно з його заповітом, було перепоховано в Україні.</p>\n<p align=\"justify\" lang=\"uk-UA\">Тяжкою була для Тараса Григоровича зима 1861 року. Перебуваючи далеко вiд батькiвщини, в Санк<strong>-</strong>Петербурзі,  він все частіше хворів, усе бiльше вiдчував свою самотнiсть. 9 березня,  в день його народження, до нього завiтало кiлька знайомих.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1219:perepohovannja-ukrajinskogo-proroka&catid=37:spilnota-cat&Itemid=49',3266,NULL,1,26),
(3453,'P','2013-05-23',1,NULL,'Rss','Поступ (23 травня)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,82a156167aa18068c4592ca79a097f7e.jpg\" />Коли моя бабуся вирушила навідати свою маму, вона відводила на це три  дні. Один день ішов, щоб добратися туди на бричці, запряженій кіньми, ще  один – аби розповісти і послухати останні новини, посидівши трохи в  кухні, а трохи у саду, і третій день – на дорогу назад.</p>\n<p>Коли моя мати їздила до своєї мами, то їй потрібно було два дні. Вона  добиралася потягом і, якщо їй щастило з пересадками, приїжджала того ж  дня від вечір.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1187:postup-23-travnja&catid=1:mystecka-storinka-cat&Itemid=50',3267,NULL,1,26),
(3454,'P','2013-05-23',1,NULL,'Rss','Другий сорт, або навіщо українцям Папа і копти','<p><img style=\"margin: 10px; float: left;\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_52390_Schotkina_risu2.jpg\" alt=\"Катерина Щоткіна\" title=\"Катерина Щоткіна\" height=\"138\" width=\"184\" />Мабуть, ніде журналіст не відчуває своєї непотрібності так гостро, як у нашій релігійній темі.</p>\n<p>«А чому, власне, світська преса має  писати про зречення Папи Римського? Це стосується католиків – нехай  католики про це й пишуть». Приблизно так був сформульований пост в  обговоренні <a href=\"http://risu.org.ua/ua/index/expert_thought/analytic/51976/\" target=\"_blank\">огляду Марини Довженко</a>.  Не знаю, як там автор статті, але я спершу розгубилася, прочитавши це –  своєчасне, панове, — питання. Ну як це – чому? Як – чому? А про що ж  іще писати? Це ж подія. Не побоюся цього слова – сенсація.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1220:drugy-sort-abo-navishcho-ukrajincjam-papa-i-kopty&catid=37:spilnota-cat&Itemid=49',3269,NULL,1,26);
