INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5113,'P','2014-09-10',1,NULL,'Note','ПОЖЕРТВИ ПАРАФІЯН ЦЕРКВИ СВ. МИКОЛАЯ ЧУДОТВОРЦЯ НА АСКОЛЬДОВІЙ МОГИЛІ У СЕРПНІ 2014 Р. Б.','<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</span></p>','<p><strong><em>Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</em></strong></p>\r\n<table style=\"width: 491px;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><strong>№ з/п</strong></p>\r\n</td>\r\n<td width=\"189\">\r\n<p align=\"center\"><strong>Ж Е Р Т В О Д А В Ц І</strong></p>\r\n</td>\r\n<td width=\"265\">\r\n<p align=\"center\"><strong>Пожертва на (для, до):</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">1</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Тарас, Олена-Катерина та Тарас</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Дзвін та подяка Богу за те, що уціліли в аварії</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">2</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Аскольд</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Дзвіницю</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">3</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Іван та Марія</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Церкву</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">4</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Парафіяни</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Потреби поранених воїнів</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">5</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Марія Шпак</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Дітям на відпочинок</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">6</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Родина Греськів</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Потреби храму</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">7</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Олег</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Церкву</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">8</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Лідія</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Церкву</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">9</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Ігор та Наталя</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Церкву</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">10</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Людмила</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Десятина, подяка за працю</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">11</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Василь та Анна</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Будову, пісок</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">12</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Василь Бойчук</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Щебень</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">13</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Світлана</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Шкільний портфелик</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">14</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Родина Курило</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Церкву</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">15</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Володимир та Ірина</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Церкву</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">16</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Марія</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Храм, подяка Богу</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">17</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Андрій (СТО)</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Шкільний портфелик дітям, що потребують</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em><strong>Милостиню роби згідно з тим, що маєш; Коли у тебе достатків повно, Роби з них, а коли мало маєш, то не бійся з того дати. Бо так готуєш собі добрий скарб На день потреби.</strong></em></p>\r\n<p><strong>Товит 4. 8-9.</strong></p>',NULL,4656,5,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5114,'P','2014-09-11',1,NULL,'Rss','Усікновення голови св. Івана Хрестителя','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6136,200,image004.jpg\" />Після того, як&nbsp;<strong>Святий Іван Предтеча</strong> охрестив Господа, цар Ірод &nbsp;Антипа, правитель Галілеї, посадив його у темницю. Про його мученицьку кончину оповідають Євангелія від Матфея&nbsp;<em>(Мф. 14, 1-12)</em> і Марка (<em>Мк. 6, 14-29)</em><o:p></o:p></p>\n<p>У царя була законна дружина, дочка аравійського царя Арефи. Ірод залишив її і жив разом з Іродіадою, дружиною свого брата. Пророк Іван неодноразово викривав його, але цар не посмів заподіяти йому зла, так як шанував Івана Хрестителя як пророка і боявся народного гніву.<o:p></o:p></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2410:usiknovennja-golovy-sv-ivana-hrestytelja&catid=27:gk-v-ukrajini-cat&Itemid=57',4657,NULL,1,1,26),
(5115,'P','2014-09-12',1,NULL,'Rss','Лише маючи милосердне серце, людина може насправді наслідувати Ісуса Христа','<p><img src=\"http://askoldova-mohyla.org/uk/image,6140,200,1_0_824627.jpg\" style=\"margin: 10px; float: left;\" />Лише маючи милосердне серце, людина може насправді наслідувати Ісуса Христа. На основі цієї думки Папа Франциск побудував свою ранкову проповідь. Проповідник також наголосив на тому, що життя християнина не є життям егоїстичним, зацикленим виключно на своїй власній особі та особистих проблемах, але є даром для інших. Лише живучи своє життя як дар, зможемо любити наших ворогів, до чого закликає нас Господь Ісус.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2411:lyshe-majuchy-myloserdne-serce-ljudyna-mozhe-naspravdi-nasliduvaty-isusa-hrysta&catid=32:gk-svit-cat&Itemid=58',4658,NULL,1,1,26),
(5116,'P','2014-09-12',1,NULL,'Rss','Це наша і ваша боротьба за свободу!','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6140,200,z_tv.jpg\" style=\"margin: 10px; float: right;\" />Якщо сьогодні Європа віддасть Україну в руки Росії, то невдовзі і сама загине – остерігає Блаженніший Святослав, Глава Української Греко-Католицької Церкви. Основна помилка Європи – на його думку – полягає в тому, що Європейський Союз декларує відданість певним цінностям, однак не може їх боронити.</strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2412:ce-nasha-i-vasha-borotba-za-svobodu&catid=28:tema-nomera-cat&Itemid=2',4659,NULL,1,1,26),
(5117,'P','2014-09-12',1,NULL,'Rss','Положення чесного пояса Пресвятої Богородиці','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6143,200,1378817914.jpg\" />Згідно передання, Пресвята Богородиця Марія сама виплела свій пояс із верблюжої шерсті та передала його Апостолу Томі, коли возносилася на Небеса. Цим потішила його, бо ж він запізнився на її Успіння.</p>\n<p>Оскільки мощей Пресвятої Богородиці немає, бо вона взята до Неба всеціло, то пояс як і її риза – залишилися нам на згадку про Неї та як доказ того, що Вона жила на землі. Пояс переховували інші учні після мученицької смерті святого апостола Томи, а тоді поклали його у Церкві Влахернській у Константинополі.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2414:polozhennja-chesnogo-pojasa-presvjatoji-bogorodyci&catid=27:gk-v-ukrajini-cat&Itemid=57',4661,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5118,'P','2014-09-12',1,NULL,'Rss','Навчитися слухати (13 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6143,200,4564.jpg\" />Один старенький цілий день сидів у храмі навпроти ікони. Якось церковний прислужник запитав старого:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Що каже вам Бог?<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Він нічого не каже, Він лише слухає.\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2415:navchytysja-sluhaty-13-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4662,NULL,1,1,26),
(5119,'P','2014-09-13',1,NULL,'Rss','День Бога Творця','<p><img src=\"http://askoldova-mohyla.org/uk/image,6143,200,68277_900.jpg\" style=\"margin: 10px; float: right;\" />У неділю, 14 вересня, Українська Греко-Католицька Церква, долучаючись до міжнародної екуменічно-екологічної ініціативи Константинопольського Патріархату, відзначає День Бога Творця як особливе свято, яке покликане, через прославу нашого Творця і подяку за отримані від нього дари та доброчинства, спричинитися до утвердження нашої віри та життя згідно з його заповідями (за Божою подобою) у різних вимірах: особистому, сімейному, суспільному, а зокрема, у ставленні до Його створіння – природи.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2416:zvernennja-bjuro-ugkc-z-pytan-ekologiji-do-dnja-boga-tvorcja-2014-r-b&catid=27:gk-v-ukrajini-cat&Itemid=57',4663,NULL,1,1,26),
(5120,'P','2014-09-13',1,NULL,'Rss','Про любов без знеболювання','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6146,200,images.jpg\" style=\"margin: 10px; float: left;\" />Прагнення бути з іншою людиною і будувати з нею глибокий зв’язок не може базуватися на страхові перед самотністю, перед стражданням. Мотивація має бути принципово інакша: я мрію про те, щоби дати комусь почуття безпеки, щоби створити дім. Розмова з о.&nbsp;Мирославом Малінським, студентським душпастирем і ректором храму св.&nbsp;Матвія у Вроцлаві (Польща).</strong>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2417:pro-ljubov-bez-zneboljuvannja&catid=37:spilnota-cat&Itemid=49',4664,NULL,1,1,26),
(5121,'P','2014-09-13',1,NULL,'Rss','«Майдан і церква»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6146,200,0000033265-majdan-zachistka.jpg\" />“Майдану би не було, якби не віра. Віра здатна спонукати людей до дій, на які не здатні ті, хто цієї віри не має”, - такими словами розпочала 11 вересня презентацію&nbsp;<strong><a href=\"http://risu.org.ua/ua/index/studios/announcements_of_publications/57602/\" target=\"_blank\">книги “Майдан і Церква. Хроніка подій та експертна оцінка”</a></strong> у Дзеркальному залі палацу Потоцьких професор-релігієзнавець Людмила Филипович.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2418:2014-09-13-10-37-50&catid=37:spilnota-cat&Itemid=49',4665,NULL,1,1,26),
(5122,'P','2014-09-13',1,NULL,'Rss','Дорогі подарунки (14 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6148,200,valentines-day.jpg\" />Якось прийшов в одне селище старий мудрий чоловік і оселився там. Він любив дітей і проводив з ними чимало часу. Але ще він любив дарувати їм подарунки, але тільки дуже ламкі речі.</p>\n<p>Хай як старалися діти, їхні нові іграшки часто ламалися. Діти засмучувалися і гірко плакали. Минав час і мудрець знову дарував їм іграшки, але ще тендітніші. Врешті батькам урвався терпець і вони прийшли до нього:</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2419:dorogi-podarunky-14-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4666,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5123,'P','2014-09-15',1,NULL,'Rss','Забута перемога (16 вересня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6153,200,origina.JPG\" style=\"margin: 10px; float: left;\" />Отаман вів своє військо у вилазку довгою кам’янистою дорогою. Воїни, що втрачали сили на важкому шляху і боялися ворогів почали досадувати і нарікати, але отаман суворо змушував іти вперед без відпочинку і затримок.<o:p></o:p></p>\n<p>Уранці вони зненацька напали на ворога і перемогли. І після битви запитав отаман у воїнів:<o:p></o:p></p>\n<p><!--[if !supportLists]--></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2425:zabuta-peremoga-16-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4673,NULL,1,1,26),
(5124,'P','2014-09-14',1,NULL,'Rss','Літургійний рік як знак живої Церкви','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6149,200,41980966.jpg\" />Вересень є не лише звичайним, календарним початком нової пори року, а й важливою подією у житті нашої Церкви. Саме 1 (14) вересня Церква святкує початок, «найменування», нового церковного року, який розпочинає своєрідне коло літургійних богослужінь.</p>\n<p>Установлення дати, якою розпочинається літургійний рік на 1 вересня (за новим стилем) чи на 14 – за старим, має давню історію, початки якої сягають ще часів Римської імперії. Якщо поглянути у церковний календар, то в день початку церковного року зустрічаємо слово «індикт», що з латинської мови означає «проголошення» або «податок».</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2420:liturginy-rik-jak-znak-zhyvoji-cerkvy&catid=27:gk-v-ukrajini-cat&Itemid=57',4667,NULL,1,1,26),
(5125,'N','2014-09-15',1,NULL,'Rss','«І сказав Господь: Що ти зробив? Голос крови брата твого взиває до Мене з землі» (Бут. 4, 10).»','<p><img style=\"margin: 10px; float: right;\" src=\"image,6149,200,14039778.jpg\" alt=\"\" />Новий тиждень розпочався для України початком виборчих перегонів, які на певний час змусять суспільство забути про криваві жертви на Сході, про мордерства, вбивства, тортури, викрадення, про несправедливість. Але забуваючи про них, суспільство їх не позбудеться.</p>\r\n<p>Нормальна людська психіка прагне сховатися, не пам&rsquo;ятати про усе це насильство, жорстокість. Але труни героїв та герої-каліки будуть і далі стукати до серця, до совісті.</p>','','http://oranta.org/index.php?option=com_content&view=article&id=2421:2014-09-15-21-51-35&catid=28:tema-nomera-cat&Itemid=2',4668,NULL,1,1,26),
(5126,'P','2014-09-15',1,NULL,'Rss','«І сказав Господь: Що ти зробив? Голос крови брата твого взиває до Мене з землі» (Бут. 4, 10).»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6149,200,14039778.jpg\" />Новий тиждень розпочався для України початком виборчих перегонів, які на певний час змусять суспільство забути про криваві жертви на Сході, про мордерства, вбивства, тортури, викрадення, про несправедливість. Але забуваючи про них, суспільство їх не позбудеться.</p>\n<p>Нормальна людська психіка прагне сховатися, не пам’ятати про усе це насильство, жорстокість. Але труни героїв та герої-каліки будуть і далі стукати до серця, до совісті.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2422:2014-09-15-21-51-38&catid=28:tema-nomera-cat&Itemid=2',4669,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5127,'P','2014-09-16',1,NULL,'Rss','Відвідини хворих','<p>“Бо Я… слабував…”</p>\n<p>Спільнота храму святого Миколая Чудотворця на Аскольдовій могилі «Епіфанія» на чолі із священиком Анатолієм Теслею в черговий раз відвідала людей похилого віку.</p>\n<p>Приємно розпочати день, знаючи, що на тебе чекає робота чи навчання, чи якесь служіння, побачити знайомі обличчя людей. У книзі «Мозаїка милосердя» о. Арнальдо Панґрацці зазначає - &nbsp;відвідини є першим і найосновнішим елементом для встановлення особистого зв’язку з хворим. Саме тоді душпастир має нагоду стати свідком Бога та речником християнської спільноти для того, хто страждає.\n','','http://oranta.org/index.php?option=com_content&view=article&id=2423:vidvidyny-hvoryh&catid=25:parafija-cat&Itemid=27',4670,NULL,1,1,26),
(5128,'P','2014-09-14',1,NULL,'Rss','Благословення (15 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6153,200,9132.JPG\" />Один хлопчик, який згодом став ученим, у школі мав славу телепня.<o:p></o:p></p>\n<p>Одного разу школу відвідав поважний гість. Учитель показуючи на того хлопчика сказав:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Це найдурніший учень у школі.<o:p></o:p></p>\n<p>Перш ніж покинути школу, відвідувач підійшов до дитини і промовив дружнє благословення такого змісту:</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2424:blagoslovennja-15-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4671,NULL,1,1,26),
(5129,'P','2014-09-16',1,NULL,'Rss','Неприйнятий дар (17 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6153,200,obgyn-gift.jpg\" />Старий майстер бойових мистецтв вирішив більше ніколи не битися. Але одного разу його все таки викликав на поєдинок юний воїн, який вважав, що він куди майстерніший і сильніший.<o:p></o:p></p>\n<p>Однак старий майстер і ніяк не реагував на нього. Тоді воїн взявся ображати його і його предків, щоб спровокувати майстра, але і це не допомогло.<o:p></o:p></p>\n<p>В решті решт, юний воїн опустив руки і пішов.<o:p></o:p></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2426:neprynjaty-dar-17-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4672,NULL,1,1,26),
(5130,'P','2014-09-17',1,NULL,'Rss','Синодальне послання до вірних Української Греко-Католицької Церкви та всіх людей доброї волі про суспільно-політичну ситуацію в Україні  напередодні виборів до Верховної Ради','<p align=\"right\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6153,200,2-1.jpg\" />…<em>Не лякайся й не падай духом, бо Господь, Бог твій, з тобою, куди б ти не подався&nbsp;</em><span style=\"font-size: 0.875em;\">(Книга Ісуса Навина 1, 9).</span></p>\n<p align=\"center\"><em><strong>Дорогі в Христі!</strong></em></p>\n<p>Уже близько року весь наш народ перебуває в паломництві до справжньої свободи. Пройшовши визвольний досвід Революції гідності, ми спільно переносимо трагічний військовий конфлікт, анексію Криму й агресію з боку сусідньої держави на Cході України, переживаємо складну економічну кризу, постійно стоїмо перед викликами в політичній сфері. Як ми можемо мати певність, що Бог є з нами?</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2427:synodalne-poslannja-do-virnyh-ukrajinskoji-greko-katolyckoji-cerkvy-ta-vsih-ljude-dobroji-voli-pro-suspilno-politychnu-sytuaciju-v-ukrajini-naperedodni-vyboriv-do-verhovnoji-rady&catid=27:gk-v-ukrajini-cat&Itemid=57',4675,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5131,'P','2014-09-18',1,NULL,'Rss','Церква є «вселенська» і «апостольська»','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6157,200,kst_z_papoju1.jpg\" />Загальна аудієнція з Папою Франциском 17 вересня 2014р.: Вселенська і апостольська Церква (повний текст)</strong></p>\n<p>Дорогі брати і сестри,</p>\n<p>Коли визнаємо нашу віру, то говоримо, що Церква є «вселенська» і «апостольська». Яке значення мають ці дві характерні риси Церкви? Яку вони мають вартість для християнських спільнот і для кожного з нас?</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2429:2014-09-18-05-23-15&catid=32:gk-svit-cat&Itemid=58',4676,NULL,1,1,26),
(5132,'P','2014-09-18',1,NULL,'Rss','Потреба молитви, але не тільки','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6157,200,543753.jpg\" style=\"margin: 10px; float: left;\" />За останній вже майже рік наш нарід молився.</strong><strong> </strong></p>\n<p>Віруючі люди радо і з переконання, переймали заклики своїх Церков чи релігійних організацій і молилися. Не перестали молитись і сьогодні.</p>\n<p>Церкви та релігійні організації не перестали молитись, закликати до молитви і навіть до покаянних практик, таких, як піст. Вони при різних нагодах дальше моляться і закликають до щирої молитви.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2430:potreba-molytvy-ale-ne-tilky&catid=28:tema-nomera-cat&Itemid=2',4677,NULL,1,1,26),
(5133,'P','2014-09-18',1,NULL,'Rss','«Кожен має оголосити воєнний стан у своїй душі»','<p><strong><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6160,200,sverstyuk.jpg\" />Українським інтелектуалам дуже складно боротися з російською пропагандою, бо вона ґрунтується не на інтелекті, а на брехні. Таку думку висловив відомий філософ, письменник, дисидент, учасник Ініціативної групи «Першого грудня» Євген Сверстюк під час своєї лекції «Один в полі воїн: де проходить український інтелектуальний фронт» у Національному музеї «Тюрма на Лонцького» в рамках Форуму видавців у Львові.</em></strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2431:2014-09-18-10-17-44&catid=37:spilnota-cat&Itemid=49',4678,NULL,1,1,26),
(5134,'P','2014-09-27',0,NULL,'Note','«ВІЗЬМУ МОЛИТВУ – МАМИН ОБЕРІГ»','<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><span style=\"font-family: arial, helvetica, sans-serif;\"><span style=\"text-indent: 0em; line-height: 1.3em;\"><img style=\"float: left;\" src=\"image,6162,200,10403226.jpg\" alt=\"\" width=\"200\" height=\"150\" />Ласкаво просимо на&nbsp;літературно-мистецьку зустріч&nbsp;</span><span style=\"text-indent: 0em; line-height: 1.3em;\">із циклу &laquo;</span><em style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Молюся віршами сьогодні</em><span style=\"text-indent: 0em; line-height: 1.3em;\">&raquo;&nbsp;</span><span style=\"text-indent: 0em; line-height: 1.3em;\">з поетесою, істориком, громадським діячем&nbsp;</span><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Іриною Василик &nbsp;</strong>28.09.2014 о 14:30</span></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>&laquo;ВІЗЬМУ МОЛИТВУ &ndash; МАМИН ОБЕРІГ&raquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Місце проведення заходу:&nbsp;</span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Церква св. Миколая на Аскольдовій могилі.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Вхід вільний.</span></p>','<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6162,200,10403226.jpg\" alt=\"\" width=\"200\" height=\"150\" />Ласкаво просимо на&nbsp;літературно-мистецьку зустріч&nbsp;із циклу &laquo;<em>Молюся віршами сьогодні</em>&raquo;&nbsp;з поетесою, істориком, громадським діячем&nbsp;<strong>Іриною Василик &nbsp;</strong>28.09.2014 о 14:30</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>&laquo;ВІЗЬМУ МОЛИТВУ &ndash; МАМИН ОБЕРІГ&raquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">У програмі також беруть участь:</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- Жіночий вокальний ансамбль &laquo;АНІМА&raquo; УГКЦ св. Миколая Чудотворця на Аскольдовій могилі;</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><span style=\"line-height: 1.3em;\">- Родинний гурт &laquo;ДИВОГРАЙ&raquo;;</span></span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><span style=\"line-height: 1.3em;\">- Лауреат міжнародних та всеукраїнських конкурсів РУСЛАНА ДМИТРОВСЬКА;</span></span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><span style=\"text-indent: 0em; line-height: 1.3em;\">- Піаніст, володарка Гран-Прі, І премії Міжнародного конкурсу піаністів (Італія), викладач НМА України ім. П.Чайковського НАТАЛЯ МАЛЬЦЕВА.</span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Місце проведення заходу:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Церква св. Миколая Чудотворця на Аскольдовій могилі (Паркова дорога, 1).</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Вхід вільний</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><a href=\"https://www.facebook.com/events/1462029497410937/\">https://www.facebook.com/events/1462029497410937/</a></span></p>',NULL,4679,5,1,NULL,16);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5135,'P','2014-09-18',1,NULL,'Rss','Істинна цінність (19 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6162,200,h0MXG1nu9Bs.jpg\" />Одного разу шкільний учитель прийшов до вельми шанованої учительки і звинуватив її у тому, що її метод навчання цілком не логічний, що це просто якась безглузда балаканина і таке інше.</p>\n<p>Учителька дістала зі своєї торбини самоцвіт. Вона запропонувала учителеві піти в крамничку де продають вироби зі срібла і батарейки для годинників. І виторгувати за нього хоча б 10 тисяч карбованців.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2432:istynna-cinnist-19-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4680,NULL,1,1,26),
(5136,'P','2014-09-29',1,NULL,'Rss','Що посієш (29 вересня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6188,200,020_siyyach.jpg\" style=\"margin: 10px; float: left;\" /></p>\n<p>Селянин ревно засівав своє поле. До нього підійшов хлопчик і сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я, коли виросту, ніколи не буду сіяти зерна.</p>\n<p>Чоловік поглянув на нього і нічого не відповів.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Це ж невдячна справа, - говорив далі хлопчик.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2456:shcho-posijesh-29-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4706,NULL,1,1,26),
(5137,'P','2014-09-19',1,NULL,'Rss','Лише з миром можна боронити свою землю, мову та Церкву','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6162,200,zapisati_203.jpg\" />Розмова з отцем Михайлом Гарватом, священиком храму Різдва Пресвятої Богородиці у Львові</p>\n<p>Близько тисячі загиблих, більше ніж три тисячі поранених військових — такі жертви боротьби за незалежність України. Напевно, кожної української сім’ї торкнулася війна: є знайомі, родичі, які мобілізовані, поранені, а то й полонені чи загиблі. Нині складний та важкий час для усіх. Як його пережити, як уникнути тривоги? Всі запитують про це.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2433:lyshe-z-myrom-mozhna-boronyty-svoju-zemlju-movu-ta-cerkvu&catid=28:tema-nomera-cat&Itemid=2',4681,NULL,1,1,26),
(5138,'P','2014-09-19',1,NULL,'Rss','Таємниця мудреця (20 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6162,200,75685543892905.jpg\" />Якось запитали мудреця:<o:p></o:p></p>\n<p><!--[if !supportLists]-->- <!--[endif]-->Яка таємниця вашої мудрости?<em><o:p></o:p></em></p>\n<p>Відповів він:<o:p></o:p></p>\n<p><!--[if !supportLists]-->- <!--[endif]-->Я живу простим життям. Я їм, коли їм. Сплю, коли сплю. Молюся, коли молюся.<em><o:p></o:p></em></p>\n<p>Запитувач спантеличився:\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2435:tajemnycja-mudrecja-20-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4682,NULL,1,1,26),
(5139,'P','2014-09-20',1,NULL,'Rss','10 змін на краще, які стануться, якщо вийти з соцмереж','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6162,200,484.png\" />Соціальні медіа можуть бути чудовим засобом для того, щоб підтримувати контакти з людьми, заводити нових друзів і спілкуватися. На жаль, не всі використовують їх саме так.</strong></p>\n<p>Багато хто з нас заходить у соцмережі, як на роботу: постять щось заради лайків, нескінченно переглядають оновлення стрічки і до одержимості хочуть відразу дізнаватися про все, що діється у світі. Можливо, настав час зробити перерву?</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2434:10-zmin-na-krashche-jaki-stanutsja-jakshcho-vyty-z-socmerezh&catid=37:spilnota-cat&Itemid=49',4683,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5140,'P','2014-09-20',1,NULL,'Rss','Віра в перемогу (21 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6162,200,XYviL5TRpAA.jpg\" />Один військовий отаман вирішив напасти на супротивника, хоча ворогів було в десятеро більше. Він знав, що переможе, але його солдатів брав сумнів. По дорозі він спинився біля святині сказав своїм людям:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Помолившись у святині, я підкинув монету, якщо випаде орел – ми переможемо, а якщо решка – то програємо. Такою буде наша доля.<o:p></o:p></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2437:vira-v-peremogu-21-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4684,NULL,1,1,26),
(5141,'P','2014-09-20',1,NULL,'Rss','Народження Марії це джерело радості для світу','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6162,200,1379697073_default.jpeg\" />Наша Церква в Утрені свята Народження Божої Матері співає отак: \"Маріє, пречиста Богоро­дице, чиста Діво, спаси від всяких бід своє стадо, щоб ми прославляли й величали тебе, надіє всіх християн\".<strong></strong></p>\n<p>Церква загально відмовляється святкувати народжен­ня навіть найбільших свя­тих, бо вони у хвилині на­родження є забруджені первородним гріхом. Єди­ний виняток становить Христовий предтеча Йоан, який, за навчанням святих Отців Церкви, був звільне­ний від первородного гріха ради заступництва Божої Матері, що відвідала його матір Єлизавету, поки Йоан ще не народився (Лк. 1, 41). Робить Церква так тому, що ніхто не знає, що буде з новонародженої дитини, поки вона не підросте і влас­ним життям не докаже, чого вона вартує в Божих очах.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2436:narodzhennja-mariji-ce-dzherelo-radosti-dlja-svitu&catid=28:tema-nomera-cat&Itemid=2',4685,NULL,1,1,26),
(5142,'P','2014-09-21',1,NULL,'Rss','Період випробувань','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6162,200,19625.gif\" />Наростає хвиля переходу громад від УПЦ МП без священиків, а на черзі — низка парафій разом із їхнім кліром</strong></p>\n<p>Українські церкви переживають непростий період внутрішньої трансформації, все більше віддаляючись від тієї єдності, що характеризувала Всеукраїнську раду церков та релігійних організацій під час Майдану.</p>\n<p>9—11 вересня відбулася зустріч представників українських та російських релігійних організацій в Осло.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2438:period-vyprobuvan&catid=27:gk-v-ukrajini-cat&Itemid=57',4686,NULL,1,1,26),
(5143,'P','2014-09-21',1,NULL,'Rss','«Закликаємо і президента, і уряд, і депутатів до жертовності»','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6162,200,e6af5e0d4dd60b53.jpg\" />У нас є чотири церкви \"київської традиції\" - Українська православна Київського і Московського патріархатів, Автокефальна православна та Українська греко-католицька. Що прийнято вкладати в це поняття?</strong></p>\n<p>- Тут треба почати з того, що ми, люди, по своїй суті є сотворіннями з пам\'яттю, для яких історія не є маловажною. Наша особиста тотожність пов\'язана з нашою історією. Наша культура, ставлення до оточення, світу, всіх подій, до всіх екзистенційних питань принаймні частково сформовані історією, яка може бути близькою або дуже далекою.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2439:2014-09-21-18-36-31&catid=28:tema-nomera-cat&Itemid=2',4687,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5144,'P','2014-09-22',1,NULL,'Rss','Мудрець і заздрісник (22 вересня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6170,200,x_c7b88854.jpg\" style=\"margin: 10px; float: left;\" />Жив на світі чоловік, він прославився своїм спокоєм і любов’ю до всього на світі. Будь-яка справа, до якої він брався виходила у нього добре. Всі того чоловіка поважали, приходили до нього за порадою. Одного разу до нього прийшов сусід, який йому у всьому заздрив. Він сказав погордливо:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Я в житті усе маю! Живу в достатку. Але тебе в місті поважають більше ніж мене. До кого я подібний , як гадаєш?<o:p></o:p></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2440:mudrec-i-zazdrisnyk-22-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4688,NULL,1,1,26),
(5145,'P','2014-09-22',1,NULL,'Rss','Європа живе в атмосфері опортунізму, тобто співпрацює з бандитами','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6170,200,im578.jpg\" />Мирослав Попович, відомий український філософ та академік НАНУ, про те, як відродити Донбас та любов місцевого населення до України</p>\n<p><a href=\"http://espreso.tv/\" target=\"_blank\">Еспресо.TV</a> продовжує серію публікацій, присвячену темі патріотичного виховання та патріотизму взагалі в світлі подій на Майдані, анексії Криму та війни з Росією на Донбасі.&nbsp;<a href=\"http://espreso.tv/article/2014/08/19/vadym_skurativskyy\">Минулого разу</a> свої рецепти виховання патріотизму давав&nbsp;Вадим Скуратівський, відомий історик та публіцист та&nbsp;<a href=\"http://espreso.tv/article/2014/09/02/bryukhoveckyy\">В’ячеслав Брюховецький</a>,&nbsp;почесний президент університету Києво-Могилянська академія.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2442:jevropa-zhyve-v-atmosferi-oportunizmu-tobto-spivpracjuje-z-bandytamy&catid=37:spilnota-cat&Itemid=49',4689,NULL,1,1,26),
(5146,'P','2014-09-22',1,NULL,'Rss','Як добру справу чинити (23 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6170,200,Yak.jpg\" />Один хлопець запитав старця:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Яку би мені добру справу учинити і жити з нею.<o:p></o:p></p>\n<p>Старець відповів:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Бог знає, що добре, я чув як дехто запитує мудреця:</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2441:jak-dobru-spravu-chynyty-23-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4690,NULL,1,1,26),
(5147,'P','2014-09-23',1,NULL,'Rss','Святий Піо з П’єтрельчіни','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6173,200,padrepio.jpg\" />Є святі, про яких розповідати важко, бо майже немає інформації. Є святі, про яких розповідати важко, бо про них і так «усі все знають». Як про блаженних Матір Терезу, Йоана Павла ІІ або святого Отця Піо (1887-1968).</p>\n<p>Франческо Форджоне народився в П’єтрельчіні. Об’явлення Ісуса мав з дитинства (перше – у п’ятирічному віці). До капуцинів вступив у 16 років, отримавши ім’я Піо. Священиком став 1910 року. Був узятий до війська на загальних підставах, але комісований за станом здоров’я, оселився у Сан-Джованні-Ротондо у липні 1916-го і не полишав цього місця до смерті.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2443:svjaty-pio-z-pjetrelchiny&catid=35:istorychni-postati-cat&Itemid=48',4691,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5148,'P','2014-09-24',1,NULL,'Rss','Сила і непереможність (24 вересня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6174,200,6546.jpg\" style=\"margin: 10px; float: left;\" />Одного разу прийшов юнак до старця і попросив дозволу вчитися в нього.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Навіщо це тобі? – спитав той.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Хочу стати сильним і непереможним.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тоді стань ним! Будь добрий зі всіма, ввічливий і поштивий. Доброта і ввічливість викличе в тебе повагу до інших. Твій дух стане чистим і добрим, а значить сильним. Якщо ти навчишся запобігати конфліктам, то станеш непереможним.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2444:syla-i-neperemozhnist-24-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4692,NULL,1,1,26),
(5149,'P','2014-09-24',1,NULL,'Rss','Сила Церкви — в Христовій любові','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6174,200,54675.jpg\" style=\"margin: 10px; float: right;\" />Сьогодні, як і вчора, сила Церкви полягає не в організаційних здібностях, але – у Христовій любові.</strong><strong></strong></p>\n<p>Промовляючи до паломників, які не лякаючись дощу заповнили площу перед Ватиканською базилікою, Святіший Отець поділився своїми враженнями та підсумками нещодавнього Апостольського візиту до столиці Албанії міста Тирани.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2445:syla-cerkvy-v-hrystovi-ljubovi&catid=32:gk-svit-cat&Itemid=58',4693,NULL,1,1,26),
(5150,'P','2014-09-24',1,NULL,'Rss','Насіння всіх благ (25 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6176,200,pr_sem_1.jpg\" />Одній жінці приснився сон, що за прилавком у крамниці стоїть Господь.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Боже, це Ти?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Так, я, - відповів Бог.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2446:nasinnja-vsih-blag-25-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4694,NULL,1,1,26),
(5151,'P','2014-09-25',1,NULL,'Rss','«Продовжуйте виявляти істину! Будь ласка, не підведіть нас!»','<p align=\"right\"><strong><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6177,200,cardinal_dolan_0.jpg\" />Архиєпископ Тімоті Долан</em></strong></p>\n<p>Минулого тижня я телефонував до мого товариша Святослава Шевчука, Верховного Архиєпископа Української Греко-Католицької Церкви.</p>\n<p>Впродовж останніх років я почав все більше поважати цього молодого, відважного брата-єпископа, після того, як ми з ним часто зустрічались в Римі, а особливо коли минулого року я відвідував його з нагоди посвячення Патріаршого Собору Воскресіння Христового в Києві.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2447:2014-09-25-13-42-57&catid=32:gk-svit-cat&Itemid=58',4695,NULL,1,1,26),
(5152,'P','2014-09-25',1,NULL,'Rss','Виноград з кісточками (26 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6178,200,thumb.jpg\" />Юна дочка тихенько зайшла в покій батька-падишаха. Вона шанобливо чекала, поки батько побачить хвилювання в неї на обличчі і почне бесіду.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Що стривожило тебе, дочко моя? Якщо ти наважилася відвідати мене у хвилину спокою і усамітнення.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ах, батьку, ти мудрий і доля, та люди люблять тебе. Порадь мені як досягти твого спокою і мудрості.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2449:vynograd-z-kistochkamy-26-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4696,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5153,'P','2014-09-26',1,NULL,'Rss','80 бідних родин отримали постіль від БФ «Карітас Київ»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6178,200,IMG_20140909_151542.jpg\" />«Благодійний Фонд «Карітас Київ» пошив та &nbsp;роздав для бідних родин, в яких є діти, 80 комплектів постілі. Така можливість виникла завдяки благодійникам та УББ, які щедро дали свої кошти на таку ціль. Так, було закуплено потрібну кількість тканини і волонтером Карітасу пошито 80 комплектів для підопічних. Це вже вдруге Карітас надав подібного роду допомогу, оскільки нещодавно сімдесять самотніх людей літнього віку також отримали постільні комплекти від БФ «Карітас-Київ». Отож, разом за три місяці – це 150 комплектів для потребуючих, що є досить вагомим», - повідомляє о. Роман Сиротич, директор БФ «Карітас-Київ».</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2448:2014-09-25-20-30-44&catid=29:propovidi-cat&Itemid=34',4697,NULL,1,1,26),
(5154,'P','2014-09-26',1,NULL,'Rss','Кисле варення (27 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6180,200,prikorm21.jpg\" />Діти покуштували варення, яке щойно приготувала мама. Воно їм дуже сподобалось. І тут до кімнати зайшов молодший брат.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Можна і мені покуштувати варення? – запитав він.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Не їж, воно дуже кисле, - пожартували старші діти.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2450:kysle-varennja-27-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4698,NULL,1,1,26),
(5155,'P','2014-09-26',1,NULL,'Rss','«Величаємо Тебе Життєдавче Христе і шануємо Хрест Твій Святий, ним же нас спас від неволі ворожої»','<p><img src=\"http://askoldova-mohyla.org/uk/image,6180,200,Vozdvy.jpg\" style=\"margin: 10px; float: right;\" />Римські імператори-язичники намагалися повністю знищити в людстві спогади про священні місця, де постраждав за людей і воскрес Господь наш Ісус Христос. Імператор Адріан наказав засипати землею Голгофу і Гроб Господній і на штучному пагорбі поставити капище язичницької богині Венери і статую Юпітера. На це місце збиралися язичники й робили ідольські жертвоприносини.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2451:2014-09-26-17-46-52&catid=28:tema-nomera-cat&Itemid=2',4699,NULL,1,1,26),
(5156,'P','2014-09-27',1,NULL,'Rss','Слово на Всеславнне Воздвиження Хреста святителя Андрея, архиєпископа Критського','<p><img src=\"http://askoldova-mohyla.org/uk/image,6182,200,safe_image.jpg\" style=\"margin: 10px; float: left;\" />Ми славимо Хрест, і вся Церква наповнюється блиском слави. Ми славимо Хрест, і обличчя всієї вселенної освітлюється сяйвом радості. Ми славимо Хрест, і розсівається морок, і розливається світло. Ми славимо Хрест, і з Розп’ятим підносимося, щоб, залишивши внизу землю разом з гріхом, здобувати небо й небесне. Хрест воздвигається, і гамірні клики демонів замовкають.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2452:slovo-na-vseslavnne-vozdvyzhennja-hresta-svjatytelja-andreja-arhyjepyskopa-krytskogo&catid=32:gk-svit-cat&Itemid=58',4700,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5157,'P','2014-09-27',1,NULL,'Rss','«SOS!» українському християнству','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6183,200,x_8fc6aa76.jpg\" />Ситуація, яка склалася і продовжує розвиватися в Україні, набирає щораз загрозливіших обрисів. Уже зараз можемо спостерігати перші ознаки фундаментального розколу поміж більшістю представників влади і більшістю загалу суспільства. Усе частіше люди беруть у свої руки встановлення справедливості.</p>\n<p>На жаль, оговтавшись після майданного шоку, політикум і чиновництво вкотре взялися за своє. Корупція, договорняки, хабарництво, здирництво, – знову процвітають. І знову «небожителі» українського владно-політичного Олімпу не звертають уваги на суспільство, над яким вони возвишуються.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2453:2014-09-27-18-56-26',4701,NULL,1,1,26),
(5158,'P','2014-09-27',1,NULL,'Rss','Коробка печива (28 вересня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6183,200,2.png\" style=\"margin: 10px; float: left;\" />Придбавши коробку печива жінка вирішила почитати журнал і дістала його з торбинки. Вона взяла одне печиво і побачила, що якийсь чоловік також пригостився з її коробки.</p>\n<p>Жінка палахнула гнівом, знову взяла печиво, зиркнула на чоловіка і побачила, що він усміхається. За хвилину він взяв ще одне печиво, вона знову спалахнула гнівом.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2454:korobka-pechyva-28-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4702,NULL,1,1,26),
(5159,'N','2014-09-27',1,NULL,'Rss','«SOS!» українському християнству','<p><img src=\"http://askoldova-mohyla.org/uk/image,6183,200,x_8fc6aa76.jpg\" style=\"margin: 10px; float: right;\" />Ситуація, яка склалася і продовжує розвиватися в Україні, набирає щораз загрозливіших обрисів. Уже зараз можемо спостерігати перші ознаки фундаментального розколу поміж більшістю представників влади і більшістю загалу суспільства. Усе частіше люди беруть у свої руки встановлення справедливості.</p>\n<p>На жаль, оговтавшись після майданного шоку, політикум і чиновництво вкотре взялися за своє. Корупція, договорняки, хабарництво, здирництво, – знову процвітають. І знову «небожителі» українського владно-політичного Олімпу не звертають уваги на суспільство, над яким вони возвишуються.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=2453:2014-09-27-18-56-26&catid=37:spilnota-cat&Itemid=49',4703,NULL,1,1,26),
(5160,'P','2014-09-28',0,NULL,'Note','Звіт №11 про НАДХОДЖЕННЯ  ЛИСТІВ 02, 08, 13.09.2014 р. — (18+8) листів','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">... Я тут познайомилася в церкві з дівчинкою <span style=\"text-decoration: underline;\">Олена К</span>., розговорилися за Вас і вона каже: &laquo;<strong>Я знаю отця Ігоря, він часто відвідує ув&rsquo;язнених в тюрмах, допомагає нужденним і страждущим&raquo;...</strong> Працюю в своїй бригаді і шию за своєю машинкою. <strong>Шили для наших хлопців-солдатиків матраци, але б ліпше такого ніколи не було, щоб наші діти і онуки не знали і не бачили, що таке війна</strong>.&nbsp;</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Листи від ув\'язнених</span></em></strong><strong><em><span style=\"text-decoration: underline;\"> (з них вперше звернулись </span></em></strong><strong><em><span style=\"text-decoration: underline;\">7 </span></em></strong><strong><em><span style=\"text-decoration: underline;\">осіб)</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Чернігівська виправна колонія ЧВК-44 (м.Чернігів) &mdash; </strong><strong>13</strong><strong> лист</strong><strong>ів</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Збаразька виправна колонія ЗВК-63 (Тернопільська обл.) &ndash; 4 листи</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Качанівська виправна колонія КВК-54 (м.Харків) </strong><strong>&mdash;</strong><strong> 6 листів</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">&nbsp;</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Листи звільнених з ув</span></em></strong><strong><em><span style=\"text-decoration: underline;\">&rsquo;</span></em></strong><strong><em><span style=\"text-decoration: underline;\">язнення &ndash; 2 листи</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">&nbsp;</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Лист приватної особи &ndash; 1 лист</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">&nbsp;</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... Прошу Вас очень, <strong>помогите пожалуйста мне с лекарством, они мне очень нужны. Я знаю что они очень дорогие</strong>, но по-возможности помогите пожалуйста сколько сможете&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Юлія Д. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>нахожусь в инвалидном отряде уже пятый год, мне 55 лет родных нету</strong> у меня проблема со здоровьем <strong>я гепертоник и сердечко пошаливает и еще проблема с ногами грибок одолел</strong>. Слёзно Вас прошу <strong>помогите Христа ради мне лекарствами</strong>: кардиомагнил, аналоприл и мазь лакомит от ногтевого грибка&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Надія М. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Я очень болею желудком и у меня защемляет нерв в позвонке</strong>, в марте месяце так защемило я думала на всю жизнь остануся инвалидом. Слава Богу, мои сестры молилися за меня и Господь поставил меня на ноги, конечно еще дает о себе знать, но, Слава Богу, передвигаюсь своим ходом... Под амнистию я не попала, сижу уже 5 лет, а еще 2,6 года <strong>надеюся только на Вашу милость по поводу медикаментов... Высылаю Вам рецепты</strong>, буду очень ждать&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Наталія Н. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... вернулась с Тернопольской туб.больницы для осужденных, где проходила курс лечения полтора почти года. Слава Богу всё обошлось благополучно. Ведь у меня лопнул абсцесс на лёгком, размер которого был 9х6, после чего началась интоксикация организма. Благодаря Богу, меня вывезли в вольную больницу и втечении одиннадцати дней врачи боролись с моим недугом. Во истину Божие чудо! Но <strong>я страдаю долгие годы бронхиальной астмой, инфекционно-аллергической формы,</strong> течение тяжелое, кортекостероидная зависимость (<strong>45 мг преднизолона &ndash; 9 табл. &ndash; в сутки </strong>и без них не обойтись уже и дня). Уменьшая дозировку или заменяя его дексаметазоном, - начинаются сильные приступы удушья, тяжело куппируемые...<strong> Ингалятор &laquo;Сольбутамол&raquo; уже усвоился в бронхах и стал мало эффективен. &laquo;Биротек&raquo; хорошо помогает, но его нет в мед.части... если сможете, помогите, </strong>будьте добры. И, если можно, помогите шампунью и чем-то стиральным, - или мылом, или порошком. За всё, чем бы Вы не помогли, сердечно Вам благодарна&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Людмила Н. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>У меня проблемы со здоровьем </strong>(ВИЧ, гепатит С, принимаю АРВ-терапию, вегето-сосудистая дистония), но <strong>в последнее время особенно беспокоят сосуды на ногах, потому и прошу Вас, ели будет у Вас такая возможность, выслать мне медикаменты (рецепт прилагаю)</strong><strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Олеся Б. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Я снова и снова прошу Вас о помощи</strong>, извините меня, что я беспокою Вас в такое для нас всех тяжелое время, <strong>Вы даже не подозреваете как жизненно важны для меня эти лекарства</strong>... Низкий Вам поклон и человеческое спасибо&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ірина Н. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Я </strong><strong>перенесла сложную опирацию по женски и сейчас у меня ещё обнаружили кисту на правой почьке. Прошу Вас помогите по возможности с лекарством. Высылаю Вам рецепт</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Світлана Ч. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... У меня нарушен тазобедренный сустав. В лагере спасаюсь обезбаливающими. <strong>Нужна операция для восстановления искусственного сустава. Помогите пожайлуста деньгами сколько можите</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Людмила П. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;...Отец Игорь <strong>очень прошу Вас помочь моей дочери Ирочке собратся до школи</strong>. Кроме Вас ей некому помочь. <strong>У меня скоро УДО надеюсь к Новому году буду в дома</strong> <strong>с дочькой</strong>. Вы моя последняя надежда. Я Вас всех люблю, буду за Вас молится&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Оксана Б. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;Слава Господу нашому Ісусу Христу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Усій громаді Церкви Святого Миколая на Оскольдовій могилі я дуже вдячна за наше спілкування. На протязі трьох років ви мене підтримуєте, проявляючи Господню любов, світ, тепло, турботу. І не тільки для мене, для багатьох засуджених, а також і для наших діточок</strong>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Буду дуже вам вдячна і на цей раз &ndash; <strong>у мого синочка 18 серпня (майже на Свято) буде День народження &ndash; 13 років.Я кщо у вас буде можливість будь ласка привітайте його подарунком</strong>. На жаль я не зможу ще його привітати і провести до школи 1 вересня... Майже 10 років ми не бачимося. Але я дякую Господу за те, що вже не за горами той день, коли я покину це місце і буду рядом з дитиною. <strong>14 серпня у мене вже комісія по льготі</strong>. Після комісії треба чекати суду (неділю, дві, а може і місяць). А тоді вже нарешті додому.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Я дякую Вам усім за Ваші молитви! Господь не залишає без відповіді ні однієї молитви! Я дуже мрію, коли звільнюсь служити людям, як і Ви</strong>. І вірю, що наше спілкування з вами не закінчиться. Я продовжую писати псалми. <strong>Кожен день ми твердо стоїмо у молитві за Україну.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Бажаю всім серцем вам мирного неба над головою, Божого захисту з усіх сторін і усюди, де буде ступати Ваша нога &ndash; хай розквітає життя, і прославляється ім&rsquo;я Господнє&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Алла Б. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... Мне 35 лет, <strong>на свободе остались мои детки</strong>, я не лишена родительских прав, я лишена возможности быть с ними и помогать им. <strong>Я Вас слезно прошу молитесь за них. Обращаюсь к Вам искренне с просьбой помочь им</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Катерина Б. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Дуже вдячна за Дарсіл</strong>... Відправляю схему. Вишивку надіслати неможу, бо уже подарувала. Хотіла попросити <strong>можливо у Вас є ще якісь схеми для вишивання &ndash; надішліть</strong>... Дякую Вам за молитви. І також молюсь за Вас, за здоровя і мир всім нам. Бо дуже страшно і сумно, тяжке життя настало, брат брата убиває. Іде війна... В колонії зовсім мало лишилось засуджених, дуже багато звільнилось по амністії і по кінці строку. Трошки привезли роботи, хожу на виготовлення штучних квітів, час скоріше іде. Вишиваю...&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Любов С. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Працюю. А в вільний час займаюсь вишиванням</strong>. Відбудеться конкурс нашої творчості до Великого Світлого і радісного Свята Спаса. Новин таких особливих немає. Дуже радіємо що наша Держава має Президента. Велика надія на нього. Обіцяє краще життя. А саме головне мир в Україні. Дай Боже щоб ми це побачили. Тут в колонії вибирали і ми. Голосували більшість за Петра Порошенка... Ви писали мені що можу в разі необхідності прислати рецепт для придбання ліків.<strong> Мені зараз потрібні уколи ложу рецепт</strong>. Я Вам дуже вдячна за все. Ніколи за Вас незабуду дорогий отче Ігорю. Особливо <strong>велика подяка за Біблію яку надіслали два роки назад. Читаю кожного дня</strong>. Всій громаді вдячна за листи&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Любов С. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Коли я отримую від Вас листа, я плачу від радості мені так легко стає на душі,</strong> я перечитую жінкам з нашого відділення, тобто своїм пенсіонерам, хворим. <strong>Дуже дякую Вам за Вашу увагу, розраду, науку. </strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Хай радість несе Вам кожна хвилина,</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Від горя хоронить молитва свята,</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Хай світлою буде життєва дорога,</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Опіка і ласка від Господа Бога!</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Кожного ранку я читаю молитву, і молюся за дітей, за маму. В мене на стіні висить ікона &laquo;Семістрільна&raquo; я зробила її з журналу і там є такі слова &laquo;Для мене немає більшої радості ніж чути що мої діти ходять в істині&raquo;. Це саме болюче для мене питання прощення в Бога, матері, дітей. <strong>На волі я ходила по святам до церкви, ну ходят рідні і я з ними, але тут будучи в зоні я дійсно увірувала</strong>, в мене зовсім інша уява, розуміння, інше відношення до людей і взагалі я зовсім змінилася, мені стидно що я так себе була віддала в рабство сатані, наскільки я була сліпа. Тому <strong>я хочу помогти таким же жінкам як я сама була, помогти скинути тягар з душі</strong>. Зараз я набагато краще себе чувствую до мене зовсім по іншому ставляться, ні це не гордість, просто <strong>Господь помагає мені змінитися, я чувствую Його любов, і мені хочеться щось зробити добре...</strong> Я рада що навернулася на цю стежку і вже не схибну і допоможу іншим. Тут <strong>я найшла деякий матеріал про Т.Г.Шевченка бо я готовила стенд до свята. Якщо у Вас була б можливість вислати канви кусочок я б вишила картину Т.Г.Шевченка,</strong> я вмію вишивати і нитками і бісером, <strong>вишивала в Київ на виставку &laquo;Різдвяні колорити&raquo;,</strong> але полотно давав психолог а свого немає. <strong>Також вишивала рушники і дівчата вміють а проблема в полотні і нитках. Я прошу у Вас молитви і благословіння</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Параскева Т. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... Я сама каліка на ліву ногу (у мене ліва нога коротша на 10 см) і мені потрібна Ваша допомога 39 розмір зімню і літню обувь і по можливості моющими средствами. <strong>В мене в дома остався не повнолітній синочек Віктор, якщо у Вас є можливість то допоможіть шкільним портфеликом. Синочку 9 років потрібні шкільні потребності і обув 32-34 і розм одежі 38-40.</strong> Проживає із своїм батьком а моїм чоловіком. Прошу Вас допоможіть мені чим зможите, в мене величенький срок 5,8 р. а допомоги із дому я ніякої непрошу і немаю&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Анжела Є. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Мені ще 6 років відбувати покарання</strong> але я не падаю духом <strong>в мене є сестри, що підтримують мене в усьому ми гуртом молимось... Ми молимся за всю Україну нашу за її чудових синів які звільняють нашу землю від зла і наша Україна буде процвітаючою бо в ній є все і любов і віра</strong>. Я знаю, що Ви дуже багато допомогаєте людям і Господь благословляє Вас і наділяє серця Ваші любов&rsquo;ю і теплом. <strong>В мене дуже випадають коси. Я незнаю чим зарадити</strong> дома я мила коренем ріп&rsquo;яха а тут <strong>кажуть потрібно масло ріпейне</strong>. <strong>Якщо можна то вишліть мені буду вдячна</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Тетяна С. (Качанівська ВК, м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... <strong>Я получила от Вас посилку огромное Вам спасибо</strong>... хочу Вам написать номер телефона моей мами, чтобы комне систра с племяницей приехала на родительский день 16 августа я прошу один раз и больше ніколи не попрошу... <strong>Може у Вас будут резиновие шлепки 36 розмера то вишлите </strong>бо у меня тріснула подошва а в магазине здесь била одна пара 40 размера ну мне пришлось их купить, <strong>чтоб на баню босяка не ходить</strong>. <strong>О.Ігор я Вам так благодарна и всей громаде за Вашу подержку... Ми молимось за мир в Україні, щоб закінчилась війна і кровопролиття</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Олена К. (Качанівська ВК, м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... о.Ігор <strong>я знаю, что Вы зараз готовите портфелик для школяра у мене плім</strong><strong>&rsquo;</strong><strong>яница годами как моя доця</strong> и они в етом году должны били ити вместе в первий класс а <strong>так как доці я немогу ета зделать можна ета зделать для плем</strong><strong>&rsquo;</strong><strong>яници? </strong>Просто я её ростила с малих лет как и свою доцю. О.Ігор <strong>по возможності сможете вислать мне летнюю обувь 36 размера</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em><span style=\"text-decoration: underline;\">Олена К</span>. (Качанівська ВК, м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... Я тут познайомилася в церкві з дівчинкою <span style=\"text-decoration: underline;\">Олена К</span>., розговорилися за Вас і вона каже: &laquo;<strong>Я знаю отця Ігоря, він часто відвідує ув&rsquo;язнених в тюрмах, допомагає нужденним і страждущим&raquo;...</strong> Працюю в своїй бригаді і шию за своєю машинкою. <strong>Шили для наших хлопців-солдатиків матраци, але б ліпше такого ніколи не було, щоб наші діти і онуки не знали і не бачили, що таке війна</strong>. Скажу прямо: - Олігархи незмогли поділити міліони міліарди між собою, а страдає завжди простий бідний народ&rdquo;. Там в окопах гинуть 18-річні діти наші, а магнати вже давно виїхали в інші міста. Але ми не Боги і нам тільки молитися і просити у Господа, щоб скоріше закінчилося це страхіття, що нависло над нашою країною, щоби Господь відвернув поскорше і настав мир не тільки у нас, а й у всьому світі. <strong>Хочу Вам подякувати за книжечку з візерунками для вишивки, чудова, я віддам її у клуб, там дівчата вишивають. У нас в колонії дуже багато дівчат попали під амністію більше 250-ти вийшло на волю.</strong> Можливо, ще буде і для важкостатейників, та на все воля Божа. Передавайте вітання усій вашій церковній громаді та всім вашим помічникам, волонтерам, людям доброї волі, хай Господь дає вам сили, здоров&rsquo;я, довгих років життя&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ірина В. (Качанівська ВК, м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... Розкажу вам немного о себе.<strong> Родных у меня нет родители были приемные. Когда мне было 2 года они меня удочерили прожив с ними 8 лет они развелись и меня опять здали в интернат </strong>(У папы появилась своя семя и он сказал что я ему не нужна, мама тоже завила свою семю у её мужа было двое взрослых детей и он сказал что я ему не нужна та еще и не родная).<strong>Так я снова стала сиротой. </strong>Когда я была в интернате то познакомилась с женщиной. Она была пастером в церкве и так я пришла к Богу она мне была как мать забырала меня практически каждый день. Она очень любила лошадей мы сней ходили в конную школу катались на лошадях она говорила что лошади даже помогают избавится от болезни (у неё был рак). Когда она умерла я попала не вочень хорошую компанию и села на иглу перестала ходить в церков... даже были такие моменты когда я говорила неочень хорошие слова к Богу и пыталась выйти из жизни но нечего не виходило. Толи Богу угодно чтобы я жила. Сичас мне 23 года а я не знаю вчем заключается смисл жизни. Помогити! разобратся как жить и для чего мне больше нек кому обратится&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Тетяна К. (Качанівська ВК, м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo; Слава Иисусу Христу!!!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дорогой отец Игорь, я сейчас работаю в Одессе так как у нас в Арцизе работ нет, тем более мне уже 54 года. <strong>Я смотрю бабулю она лежачая вообщем </strong><strong>ухаживаю за ней. </strong>Видно на то была Божья воля и <strong>каждый день утром, вечером и в обед я читаю ей молитвы и Библию. </strong>Она мне говорит спасибо Богу что послал мне тебя. <strong>Времени конечно у меня мало, но для Вас я всегда найду и никогда не забуду Вашу помощь</strong>. Деньги получаю 3 тысячи и почти все уходят на лечение сына ну спасибо Богу за всё. <strong>Храни Вас Господь всегда и везде и на всех ваших путях! Большое Вам спасибо за всё! Пишите мне</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>Світлана Ш. (Одеська обл., <span style=\"text-decoration: underline;\">звільнена з Чернігівської ВК</span>)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo; Слава Иисусу Христу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Здравствуйте о.Игорь! <strong>Шлю Вам низкий поклон и благодарение наших сердец за Ваше доброе сердце</strong><strong>. Вам, и всем братьям и сёстрам, которые во славу Божию трудятся и помогают моим и другим детям к 1-му сентября и в другое время года &ndash; искреннее, сердечное благодарение</strong>. Молю Бога о Вас, да сохранит Вас всех на долгие и благая лета, дарует Вам мир душевный, любовь &ndash; которая никогда не перестаёт, здоровье, долгоденствие и всё полезное ко спасению души! Спаси Вас Боже и Богородица! Я хочу поделиться с Вами своей радостью, как меня грешную любит Бог! <strong>21 августа меня освободили и я уже дома, с детьми. Ваши посылки получили 26 августа. Слава Богу, всё очень понравилось и подошло</strong>! Хожу ищу работу, становлюсь на учёт, ведь у меня надзор 1 год. Пока ничего не нашла, но молюсь, не отчаиваюсь. Надеюсь только на Бога, т.к.столько проблем и забот... всё упирается в деньги. Мы рабы системы, которая длится многие годы, а ещё и война. Слава Богу, что у нас ещё пока тишь и благодать. Очень много беженцев у нас в городе из Луганска и Донецка. Спаси помилуй Господи &ndash; бедные люди, лишились всего, а мы &ndash; слава Богу, с Божьей помощью &ndash; будем молиться, верить, надеяться и ждать&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>Валерія О. (м.Дніпропетровськ., <span style=\"text-decoration: underline;\">звільнена з Чернігівської ВК</span>)</em></span></p>',NULL,4704,5,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5161,'P','2014-09-28',1,NULL,'Rss','Він, вона і Бог','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6187,200,CRW_1684.JPG\" style=\"margin: 10px; float: right;\" /></strong>Подружжя&nbsp;— це спільнота і спілкування. Так, для щасливого шлюбу потрібні почуття і взаємне емоційне зацікавлення. Потрібен і спільний світогляд, єдність поглядів, взаємна підтримка, спільне господарство. Але якщо всі ці речі у сімейній спільноті не поєднані зі спілкуванням, то їх забракне, стверджує о.&nbsp;Андрій Нагірняк. Він підготував до Таїнства Шлюбу багатьох наречених, а ще більшу кількість обвінчав. Про те, які підводні камені чекають на подружжя, і як не загубитися серед них, і буде наша розмова.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2455:vin-vona-i-bog&catid=28:tema-nomera-cat&Itemid=2',4705,NULL,1,1,26),
(5162,'P','2014-09-29',1,NULL,'Rss','Перед Україною стоятиме величезний виклик, – представник Ватикану в Україні','<p><img src=\"http://askoldova-mohyla.org/uk/image,6188,200,articleimages_57555__.jpg\" style=\"margin: 10px; float: right;\" />Архієпископ Томас Едвард Ґалліксон, апостольський нунцій в Україні надав неупереджену оцінку труднощів, перед якими стоїть Церква в Україні.</p>\n<p>Архієпископ Ґалліксон зазначив, що сьогодні Україна потерпає від «дестабілізації, спровокованої переважно хижацтвами української кримінальної олігархії (які і надалі тривають),&nbsp; посиленої російською агресією супроти її територіальної цілісності та суверенітету».</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2457:145937-srjoanna-chornii-pered-ukrajinoju-stojatyme-velychezny-vyklyk-predstavnyk-vatykanu-v-ukrajini&catid=28:tema-nomera-cat&Itemid=2',4707,NULL,1,1,26),
(5163,'P','2014-09-29',1,NULL,'Rss','Хто сильніший (30 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6190,200,13754457.jpg\" />Одного разу посперечалося сонце з північним вітром, хто з них сильніший. Північний вітер вказав на чоловіка, що йшов пустелею і сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Закладаюся, що я змушу цього чоловіка скинути плащ, а ти ні.</p>\n<p>Сонце відповіло:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я приймаю цей заклад.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2458:hto-sylnishy-30-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4708,NULL,1,1,26),
(5164,'P','2014-09-29',1,NULL,'Rss','Віра, Надія, Любов та їх мати Софія','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6190,200,456.jpg\" style=\"margin: 10px; float: right;\" />«Мудрість, яка виходить з верху, по-перше, чиста, потім мирна, лагідна, покірлива, повна милосердя і добрих плодів, неупереджена і нелицемірна» (Яків 3,17).</strong></p>\n<p>У царстві Адріяна злочестивого, царя римського, була в Римі одна вдова роду італійського, на ймення Софія, її ж ім\'я тлумачиться \"премудрість\".</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2459:vira-nadija-ljubov-ta-jih-maty-sofija&catid=35:istorychni-postati-cat&Itemid=48',4709,NULL,1,1,26),
(5165,'P','2014-09-30',1,NULL,'Rss','Поетичні таланти Аскольдової могили','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6192,200,1.JPG\" />28 вересня 2014 року в храмі св. Миколая Чудотворця на Аскольдовій могилі відбувся літературно-мистецький вечір <strong>«Візьму молитву – мамин оберіг»</strong> із циклу «Молюся віршами сьогодні». Перший вечір ознайомив вірян і гостей храму із поетичним доробком парафіянки Церкви Миколая Чудотворця на Аскольдовій могилі, кандидатом історичних наук, доцентом, громадським діячем<strong><em> </em>Іриною Василик</strong>.</p>\n<p>Поетеса народилася на мальовничій Тернопільщині, закінчила історичний факультет Тернопільського національного педагогічного університету імені Володимира Гнатюка. Перший вірш під назвою «О, мово рідна, що без тебе я» написала у 5 класі. Свої поезії публікувала у районній та обласних газетах, альманахах, брала участь у конференції молодих літераторів Поділля. У доробку Ірини Богданівни вірші різного жанру – для дітей дошкільного і молодшого шкільного віку, верлібри і катрени, силабо-тоніка. У 2003 р. Ірина Василик видала першу збірку віршів «Не зречуся тебе, моя мріє», у 2006 р. – другу «Сяйво сльози».</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=2460:poetychni-talanty-askoldovoji-mogyly&catid=25:parafija-cat&Itemid=27',4710,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5166,'P','2014-09-30',1,NULL,'Note','«Активне батьківство»','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Мета семінару:</strong>&nbsp;формування позиції батьків у суспільстві та створення Асоціації Батьків України.</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Семінар відбудеться&nbsp;<strong>17-19</strong>&nbsp;<strong>жовтня</strong>&nbsp;2014 року.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">у смт. Брюховичі (поблизу Львова) у монастирі Св. Йосифа провінції оо. Василіан (див. Мапа 1. смт. Брюховичі, вул. Широка, 6,&nbsp; поблизу Львова)</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава Ісусу Христу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Просимо Вас відрядити до 2-ох батьків (від єпархій, недільних шкіл чи інших організацій)</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">на дводенний семінар&nbsp; &laquo;Активне батьківство&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Мета семінару:</strong> формування позиції батьків у суспільстві та створення Асоціації Батьків України.</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Семінар відбудеться <strong>17-19</strong> <strong>жовтня</strong> 2014 року.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">у смт. Брюховичі (поблизу Львова) у монастирі Св. Йосифа провінції оо. Василіан (див. Мапа 1. смт. Брюховичі, вул. Широка, 6,&nbsp; поблизу Львова):</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">17 жовтня &nbsp;о 16.00 &ndash; реєстрація учасників.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18 жовтня &nbsp;&ndash; другий день семінару.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">19 жовтня &nbsp;о 14.00 &ndash; завершення семінару та від&rsquo;їзд учасників.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Під час семінару будуть порушуватися 3 теми:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>1. Подружжя в Божому плані </strong>(о.Тарас Фітьо, подружжя Кузимкових)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>2. Виховне завдання батьків</strong> (Богдан Трояновський, Маряна Миколайчук)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>3. Роль батьків у формуванні християнських шкіл та участь у формуванні освітніх послуг </strong>&nbsp;(Оксана Кочерган, диспутанти законотворчого процесу базового закону про освіту України)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дані теми будуть розглядатися як з теоретичної так і з практичної сторони.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Витрати на харчування та проживання Комісія УГКЦ у справах освіти та виховання бере на себе. Представники шкіл та єпархій покривають дорожні витрати та складають добровільну пожертву на розвиток Асоціації батьків християн.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Для участі у семінарі, батьки повинні зареєструватися у Комісії освіти та виховання через заповнення анкети, яку ми додаємо додатково.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Просимо аби Ваші представники зареєструвалися до 4 жовтня 2014, надіславши на скриньку комісії (</strong>edu@ugcc.org.ua<strong>) заповнену анкету.</strong>&nbsp; &nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>Секретар Комісії УГКЦ освіти та виховання </em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>Андрій Дацків </em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>097-595-26-40</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">смт. Брюховичі, вул. Широка, 6</span></p>',NULL,4711,5,1,NULL,40);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5167,'P','2014-09-30',1,NULL,'Rss','Моральний аспект виборів до ВР','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6194,200,14093198.jpg\" />Україна, перебуваючи де-факто у стані війни, збирається на позачергові вибори свого парламенту. Тішить те, що нарешті у середовищі українського християнства виникла група, яка&nbsp;<a href=\"http://catholicnews.org.ua/ievangelski-cerkvi-zvertayut-uvagu-kandidativ-u-deputati-na-problemi-morali-ta-korupciyi\">звернулася</a> до кандидатів у депутати та політичних партій з проханням висловитися з питань моралі. Відверто кажучи, як католику, мені прикро, що це не зробила УГКЦ чи РКЦ в Україні, а ініціювали протестантські конфесії. Але як християнина мене такий вчинок частини українських протестантів не може не тішити. Звісно, доводиться сумніватися, що кандидати в депутати та політичні партії відгукнуться на цю ініціативу протестантів. Але вже сама ідея є дуже добрим знаком того, що нарешті українські християни починають робити бодай спроби відверто поставити питання моралі перед українським політикумом. І саме в часі виборчих перегонів.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2461:moralny-aspekt-vyboriv-do-vr&catid=37:spilnota-cat&Itemid=49',4712,NULL,1,1,26),
(5168,'N','2014-10-01',1,NULL,'Rss','Мене били за те, що я греко-католик','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6195,200,1066532.jpg\" style=\"margin: 10px; float: right;\" />Про своє перебування в полоні, про силу молитви в умовах війни, а також чи справді існує міжрелігійний конфлікт на Донбасі отець Тихон (Кульбака) говорив 30 вересня в рамках освітнього проекту Інформаційно-практичний Центр безпеки міста у Львові.</strong></p>\n<p>Священик майже два місяці після виходу не міг говорити про своє перебування в полоні: від цього досі можуть постраждати його парафіяни. Нагадаємо, 4 липня терористи з «Російської православної армії» взяли його в полон, де утримували десять діб.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=2463:mene-byly-za-te-shcho-ja-greko-katolyk&catid=37:spilnota-cat&Itemid=49',4713,NULL,1,1,26),
(5169,'N','2014-10-01',1,NULL,'Rss','Притча про великий вибух і прекрасну картину (1 жовтня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6195,200,prirod.jpg\" style=\"margin: 10px; float: left;\" />Якось посперечалися два товариші. Один вірив в Бога, а інший ні. Весь вечір безвірник намагався намагався довести, що Бога немає і що все на землі виникло з вибуху.</p>\n<p>Вранці наступного дня вірний приніс безвірнику картину. І безвірник, побачивши її, вигукнув від захвату:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Який чудовий пейзаж! Хто автор?! Я зачарований його роботою.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=2462:prytcha-pro-velyky-vybuh-i-prekrasnu-kartynu-1-zhovtnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4714,NULL,1,1,26),
(5170,'P','2014-10-02',1,NULL,'Rss','Кінець світу (2 жовтня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6195,200,Ternovyy_Vinok.jpg\" />Настав кінець світу. І мільярди людей, яких перенесли на велетенську рівнину постали перед троном Божим. Багато хто відступив перед сліпучим сяйвом. Але деякі в першому ряду розмовляли. Вони говорили без шаноби, задиристо:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чи може Бог судити нас, що він знає про страждання? – пробурмотіла одна жінка. Вона закотила рукав, щоб показати номер з концентраційного табору. – Ми пережили невимовний жах, катування і смерть!</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2464:kinec-svitu-2-zhovtnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4715,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5171,'P','2014-10-02',1,NULL,'Rss','«Віра, сповнена діяльною любов’ю, дає нам спасіння і через нас притягує інших до Бога»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6195,200,dzjurax_monashestvo.jpg\" />«Вдячний вам за вірність рідній Церкві та матірній землі…, за ваші неустанні молитви за Україну та щедрі пожертви на різні потреби, за допомогу родинам загиблих на Майдані і в АТО, за сприяння в лікуванні поранених. Дякую за свідчення правди про події в Україні, яке дозволяє залучити до підтримки нашої спільної справи широкі верстви польського політикуму і суспільства», - про це сказав Владика Богдан (Дзюрах), Адміністратор Патріаршої курії УГКЦ, Секретар Синоду єпископів УГКЦ, 2 жовтня 2014 року на відкритті&nbsp;<a href=\"http://catholicnews.org.ua/greko-katolickiy-sobor-zberetsya-u-peremishli\">Собору УГКЦ в Польщі</a>.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2465:2014-10-03-21-56-15&catid=28:tema-nomera-cat&Itemid=2',4716,NULL,1,1,26),
(5172,'P','2014-10-03',1,NULL,'Rss','До якого миру маємо прагнути?','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6199,200,guzar-8b7fd.jpg\" />Пишу про дуже складну, правдоподібно невдячну, але вкрай потрібну справу – про мир.</p>\n<p>Вирішив це зробити не тому, що до цього спонукає мене власне ім’я, що ним нарекли мене під час Хрещення побожні батьки.</p>\n<p>Беруся до цієї теми, бо останнім часом звідусіль чутно багато розмов про мир. Тому треба краще зрозуміти, що таке мир, звідки він походить, як настає і як його можна втратити.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2467:do-jakogo-myru-majemo-pragnuty&catid=28:tema-nomera-cat&Itemid=2',4717,NULL,1,1,26),
(5173,'P','2014-10-03',1,NULL,'Rss','Мовчазна боротьба (3 жовтня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6195,200,4657.jpg\" />Сім’я у вихідний день відпочивала на пляжі. Діти купалися в морі і будували замки з піску. Зненацька удалині на березі з’явилася маленька бабуся. Її сиве волосся розвівалося на вітрі, одежа була брудна і обшарпана. Вона щось бубніла сама до себе, підносячи із землі якісь предмети і складаючи їх у торбу.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2466:movchazna-borotba-3-zhovtnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4718,NULL,1,1,26),
(5174,'P','2014-12-14',0,NULL,'Note','Проект до Св. Миколая - «Подарунок від тата»','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,6267,200,2.jpg\" alt=\"\" width=\"200\" height=\"159\" />Мета проекту:</strong>&nbsp;у час всесвітнього християнського свята Святого Миколая, заступника і опікуна діток, подарувати частинку Божої любові дитині, батько якої загинув під час проведення АТО.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Кожна сім&rsquo;я, родина, спільнота можуть взяти під духовну опіку осиротілу дитину і через подарунок від Св. Миколая виявити батьківсько-ангельську любов, ніжність, турботу.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6267,200,2.jpg\" alt=\"\" width=\"200\" height=\"159\" />Парафіяльна служба милосердя Карітас Печерськ церкви св. Миколая Чудотворця на Аскольдовій Могилі запрошує долучитися до спільного парафіяльного <strong>проекту &laquo;Подарунок від тата&raquo;</strong>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Мета проекту:</strong>&nbsp;Перший етап: у час всесвітнього християнського свята Святого Миколая, заступника і опікуна діток, подарувати частинку Божої любові дитині, батько якої загинув під час проведення АТО. Другий і наступні етапи: опікуватись цією дитиною у різний спосіб, наприклад: листуватись; надсилати подаруночки до свят; долучатися до організації відпочинку, запросити у гості, тощо.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Кожна сім&rsquo;я, родина, спільнота можуть взяти під духовну опіку осиротілу дитину і через подарунок від Св. Миколая виявити батьківсько-ангельську любов, ніжність, турботу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Логістика проекту:</strong> у координатора проекту отримати контакт (адресу) дитини (дітей), власноруч сформувати подарунок (зимові шаль, рукавички, шапка, м&rsquo;яка іграшка, книжка, шкільне приладдя, солодощі, фрукти, тощо, при бажанні можна вкладати листа підтримки, розуміння, подяки), відправити дитині адресату, фото звіт надіслати координатору, або ж викласти у дописі події.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">На даний час у наявності майже сто перевірених контактів осиротілих дітей.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Подаруймо цим дітям частинку батьківського тепла разом.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Координатор проекту:</strong>Мирослава, волонтер парафії контакт: +380963034626; e-mail:&nbsp;</span><span style=\"line-height: 1.3em;\">askold_mykolai@ukr.net</span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>',NULL,4784,5,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5175,'P','2014-10-04',1,NULL,'Rss','Кривда і мішок картоплі (4 жовтня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6200,200,suha.jpg\" style=\"margin: 10px; float: left;\" />Учень спитав учителя:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ти такий мудрий. Ти завжди в доброму настрої, ніколи не сердишся, поможи і мені стати таким.</p>\n<p>Учитель згодився і попросив учня принести картоплю і прозорий пакет.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Якщо ти на когось розсердишся і задаєш кривду, то візьми цю картоплину: з одного боку напиши своє ім’я, а з іншого ім’я людини з якою в тебе конфлікт і поклади цю картоплину в пакет.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2468:kryvda-i-mishok-kartopli-4-zhovtnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4719,NULL,1,1,26),
(5176,'P','2014-10-04',1,NULL,'Rss','Шкільна система виховує невдах','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6200,200,images.jpg\" style=\"margin: 10px; float: right;\" />Близько 90% школярів, яких виховують &nbsp;сучасні навчальні заклади, швидше нагадують бездумних роботів для роботи на виробництвах. Адже саме для цього і створювалась 200 років тому система освіти, яка використовується і в наш час.</strong></p>\n<p>Діти по своїй природі&nbsp;активні та життєрадісні, вони люблять бігати, стрибати, долучатися до&nbsp;різних забав. Натомість їх примусово садять за шкільну парту, обмежують будь-який прояв емоцій та еволюцію, не лише інтелектуальну і моральну, а ще й фізичну. У чому ж полягають причини того, що школа замість розвитку дитини, вбиває її природні таланти?</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2469:shkilna-systema-vyhovuje-nevdah&catid=37:spilnota-cat&Itemid=49',4720,NULL,1,1,26),
(5177,'N','2014-10-04',1,NULL,'Rss','Убивця та свята: Пранзіні й Тереза','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6200,200,3400pranzinithe_00000002685.jpg\" /></strong></p>\n<p><strong>Сенсаційна історія убивці Генрі Пранзіні спонукала юнну Терезу Мартен «усиновити» свого першого грішника.</strong></p>\n<p>1887 року в&nbsp;<em>The Times</em><em> </em>з’явилася така публікація: <em>Париж, 17 березня</em></p>\n<p><em> «На Ру Монтень було скоєно потрійне вбивство. Сьогодні вранці 30-річна куртизанка, відома під іменем Монті або Ренью, була знайдена мертвою біля підніжжя свого ліжка. Дві глибоких рани в горло виявились смертельними.</em></p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=2471:ubyvcja-ta-svjata-pranzini--tereza&catid=35:istorychni-postati-cat&Itemid=48',4721,NULL,1,1,26),
(5178,'P','2014-10-04',1,NULL,'Rss','Убивця та свята: Пранзіні й Тереза','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6200,200,3400pranzinithe_00000002685.jpg\" /></strong></p>\n<p><strong>Сенсаційна історія убивці Генрі Пранзіні спонукала юнну Терезу Мартен «усиновити» свого першого грішника.</strong></p>\n<p>1887 року в&nbsp;<em>The Times</em><em> </em>з’явилася така публікація: <em>Париж, 17 березня</em></p>\n<p><em> «На Ру Монтень було скоєно потрійне вбивство. Сьогодні вранці 30-річна куртизанка, відома під іменем Монті або Ренью, була знайдена мертвою біля підніжжя свого ліжка. Дві глибоких рани в горло виявились смертельними.</em></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2472:ubyvcja-ta-svjata-pranzini--tereza&catid=35:istorychni-postati-cat&Itemid=48',4722,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5179,'P','2014-10-04',1,NULL,'Rss','Глибина молитви (5 жовтня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6200,200,fL1m1it1cpg.jpg\" />Одна дівчина пішла полем де побожний чоловік ревно молився. Закон їхньої релігії не дозволяв проходити повз таке місце. Коли сільська дівчина поверталася, побожний чоловік сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Як же неґречно ти вчинила, дівчино. Це ж гріх іти там, де молиться чоловік.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А що ви вважаєте молитвою?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Молитвою? – вигукнув він, - що це таке? Солитися, значить думати про Бога.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2470:glybyna-molytvy-5-zhovtnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4723,NULL,1,1,26),
(5180,'P','2014-10-06',1,NULL,'Rss','«Пострадянської України більше не існує…»','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,z_tv.jpg\" />«Ніколи не буде так, як раніше». Ці слова Блаженнішого Любомира, сказані в Києві в листопаді минулого року, коли розпочалися студентські протести, дуже глибокі і значущі. Бо вони вказують, що події, які сталися на наших очах, є свідченням постання нової України. Це початок нового етапу в історії нашого українського суспільства, в історії наших вірян в Україні та діаспорі.</strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2473:2014-10-06-17-46-45&catid=27:gk-v-ukrajini-cat&Itemid=57',4724,NULL,1,1,26),
(5181,'P','2014-10-06',1,NULL,'Rss','Істинне життя (6 жовтня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6204,200,praying_man_at_altar.jpg\" style=\"margin: 10px; float: left;\" />Одного разу до священика прийшов молодик і сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Отче, ви маєте рацію. Мені б також треба шукати того страждальця Голгофи і стати дитиною Божою. Коли небудь я покаюся.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Коли небудь? – запитав священик</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ну, так, згодом.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2475:istynne-zhyttja-6-zhovtnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4725,NULL,1,1,26),
(5182,'P','2014-10-06',1,NULL,'Rss','Гнів і сонце (7 жовтня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,f_2100155071362073598.jpg\" />Маленький хлопчик побився зі своїм братом. Зазнавши нищівної поразки він насупився і сів у кутку. Коли брат підійшовши до нього спробував помиритися, він відмовився слухати. Так і супився цілісінький день.</p>\n<p>Настав вечір, мама сказала йому:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Може пробачиш брата перед сном? Пам’ятаєш, у Святому письмі сказано: «хай сонце не заходить над вашим гнівом» (Еф. 4,26)</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2476:gniv-i-sonce-7-zhovtnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4726,NULL,1,1,26),
(5183,'P','2014-10-06',1,NULL,'Rss','Бог і тільки Він дає нам сенс життя!','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,xe234567ihg.png\" />Уявімо. Текст про те, що злоякісне новоутворення слід долати молитвою…</strong></p>\n<p>«В одній газеті написали статтю, де злоякісні пухлини радиться лікувати медикаментами! Причому дуже сильними і з багатьма побічними наслідками! А&nbsp;ми маємо кращі ліки! Які? Нині щоразу більше людей у світі хворіють на рак. Зростає тривожна статистика кількості захворювань, людей, яких лікують хіміотерапією, які ходять до онкологів через злоякісні новоутворення. Чому мало хто помічає, що це відбувається паралельно з лаїцизацією, відходженням від Бога і Його заповідей?</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2474:bog-i-tilky-vin-daje-nam-sens-zhyttja&catid=37:spilnota-cat&Itemid=49',4727,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5184,'P','2014-10-08',1,NULL,'Rss','Любов і закоханість (8 жовтня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6210,200,shutt.jpg\" />-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ах, любове! Я так мрію бути такою як ти. – закохано повторювала закоханість. –Ти набагато сильніша від мене.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А ти знаєш у чому моя сила – спитала любов, замислено хитаючи головою.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ти важливіша для людей.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ні, любо, зовсім не тому, - зітхнула любов і погладила закоханість по голові. – Я вмію прощати.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2487:ljubov-i-zakohanist-8-zhovtnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4728,NULL,1,1,26),
(5185,'P','2014-10-12',0,NULL,'Note','Свято Покрови Пресвятої Богородиці у Пирогово','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,6212,200,123400.jpg\" alt=\"\" width=\"200\" height=\"169\" /><br /></strong>Слава Ісусу Христу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>12 жовтня 2014</strong> року Божого у <strong>с. Пирогів цекровця Покрови пресвятої Богородиці</strong> має храмове свято. О <strong>10.00</strong> від <strong>Аскольдової Могили</strong> буде <strong>відправлятися безкоштовний автобус</strong> для всіх, хто хоче взяти участь у Святій Літургії та культурній програмі в Пирогові.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Запрошуємо це світле свято зустріти разом, розділити ту радість празника у мальовничому місці.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,6212,200,123400.jpg\" alt=\"\" width=\"200\" height=\"169\" /></strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава Ісусу Христу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>12 жовтня 2014</strong>&nbsp;року Божого у&nbsp;<strong>с. Пирогів цекровця Покрови пресвятої Богородиці</strong>&nbsp;має храмове свято. О&nbsp;<strong>10.00</strong>&nbsp;від&nbsp;<strong>Аскольдової Могили</strong>&nbsp;буде&nbsp;<strong>відправлятися безкоштовний автобус</strong>&nbsp;для всіх, хто хоче взяти участь у Святій Літургії та культурній програмі в Пирогові.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Запрошуємо це світле свято зустріти разом, розділити ту радість празника у мальовничому місці.</span></p>',NULL,4729,5,1,NULL,38),
(5186,'P','2014-10-08',1,NULL,'Rss','Чи перетвориться Революція гідності у Революцію Лінча?','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6210,200,6fd040.jpg\" />Тяжко усвідомлювати, що&nbsp;винуватців злочинів часів Януковича не притягнуто до відповідальності. Я не знаю, які причини цього; не знаю усіх закулісних компромісів і домовленостей. Припускаю, що, як завжди, тут спрацьовує те, що політика – це «мистецтво можливого». Проте «непотоплюваність» усіх отих шуфричів, єфремових, колесниченків, їхня переконаність, що їм і цього разу вдасться «развести всех, как котят» і знову, як після Помаранчевої революції, перетворитися зі злочинців на політичних опозиціонерів, здатні вивести з себе навіть найсумирнішу людину.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2488:chy-peretvorytsja-revoljucija-gidnosti-u-revoljuciju-lincha&catid=37:spilnota-cat&Itemid=49',4730,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5187,'P','2014-10-08',1,NULL,'Rss','«Мусимо чувати, аби така шляхетна ідея, як національна, не посіла місця Бога»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6212,200,img_9575.jpg\" />2-4 жовтня 2014 року в Перемишлі проходив Собор УГКЦ у Польщі «Жива парафія – місце зустрічі з живим Христом». У ньому взяв участь, окрім Митрополита Перемишльсько-Варшавського Івана (Мартиняка), Єпарха Вроцлавсько-Гданського Володимира (Ющака) та Єпископа-помічника Перемишльсько-Варшавського Євгена (Поповича), Секретар Синоду Єпископів УГКЦ, заступник голови робочої групи з реалізації Програми розвитку Української Греко-Католицької Церкви на період до 2020 року «Жива парафія – місце зустрічі з живим Христом» владика Богдан (Дзюрах). Про нові виклики та соборові враження – у нашому інтерв’ю.</p>\n<p><strong>&nbsp;</strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2489:2014-10-08-18-59-45&catid=27:gk-v-ukrajini-cat&Itemid=57',4731,NULL,1,1,26),
(5188,'P','2014-10-08',1,NULL,'Rss','Папа Франциск: коли молимося, не потрібно забувати про своє минуле, не слід приховувати власних гріхів','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6212,200,Martha_Bethanien.jpg\" />Коли молимося, не потрібно забувати про своє минуле, не слід приховувати власних гріхів. Завжди треба пам’ятати, що Господь прямує з нами шляхом нашого життям. На цьому зосередив свою увагу Папа Франциск у проповіді під час Служби Божої, яку він очолив у каплиці «Дому Святої Марти» у Ватикані у вівторок, 7 жовтня 2014 року. Вселенський Архиєрей наголосив, що Господь Бог вибрав єврейський народ і супроводжував його продовж усього переходу через пустиню, прямував із ним протягом усього його життя. Як Всевишній поводився із Своїм вибраним народом, так поводиться сьогодні із кожним з нас, тобто, крокує з нами пліч-о-пліч дорогою нашого життя.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2490:papa-francysk-koly-molymosja-ne-potribno-zabuvaty-pro-svoje-mynule-ne-slid-pryhovuvaty-vlasnyh-grihiv&catid=32:gk-svit-cat&Itemid=58',4732,NULL,1,1,26),
(5189,'P','2014-10-09',1,NULL,'Rss','Даруймо нашим агресорам любов, тобто Бога','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6212,200,venedykt_rik.jpg\" />9 жовтня Греко-Католицька Церква святкує Переставлення святого апостола і євангелиста Івана Богослова. Як відомо, саме цьому апостолу належить авторство пророчої Книги Одкровення про кінець світу. А ще святий прославився своїм навчанням про любов, якої, на його переконання, достатньо, щоб звершувалося Боже Слово на землі.<o:p></o:p></p>\n<p>Про постать наймолодшого серед апостолів — Івана Богослова, а також про те, чи варто любити своїх «агресорів», розповідає владика Венедикт Алексійчук, голова Патріаршої літургійної комісії УГКЦ.\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2491:darumo-nashym-agresoram-ljubov-tobto-boga&catid=27:gk-v-ukrajini-cat&Itemid=57',4733,NULL,1,1,26),
(5190,'P','2014-10-10',1,NULL,'Rss','Преподобний Никодим Святогорець про молитву','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,niksvyat.jpg\" />Молитва є засобом для прийняття всіх благ, щедро пролитих на нас невичерпним джерелом безмежної Божої благодаті та любові. Той, хто виголошує молитву, наче вкладає свій бойовий меч у Божу правицю, щоб Господь поборов його ворогів. Але молитва буде дієвою лише тоді, коли буде з тобою постійно, як природний навик твого духу. Цього можна досягти, дотримуючись таких принципів:</p>\n<p>1. Завжди май живе прагнення служити єдиному Богові так, як це Йому бажано. Для підтримання цього бажання невідступно думай про те, що Господу за Його предивні властивості (благодать, велич, премудрість та інші незчисленні й безмірні досконалості), кожне розумне творіння має віддавати поклоніння, честь і служіння.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2477:prepodobny-nykodym-svjatogorec-pro-molytvu&catid=35:istorychni-postati-cat&Itemid=48',4734,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5191,'P','2014-10-15',1,NULL,'Rss','Повчання про молитву і тверезість святих великих старців Варсонофія та Іоана','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,122.jpg\" />Досконала молитва полягає в тому, щоб бесідувати з Богом, не розсіюючись думками, збираючи всі свої помисли й почуття. Людина входить у такий стан, коли вмирає для всіх людей, для світу и для всього, що в нім є. І такий більше нічого не має говорити Богові в молитві, а лиш: «Нехай буде воля Твоя в мені»,&nbsp;— та її мати в умі, що стоїть перед Богом і з Ним бесідує.</p>\n<p>Творити молитви в кінці свого молитовного правила про мир Церков, про владу, про всіх людей, убогих, вдів та про інших&nbsp;— добре, бо про це заповідав апостол. Але, виконуючи це, треба усвідомлювати себе негідними й такими, що не мають на це сили. Добре молитися і про кожного, хто просить молитви. Бо слово апостола: «Моліться один за одного, щоб вам видужати» (Як. 5, 16). Дехто й за апостолів молився. Хто не дбає про заповідь, той сам себе осуджує, а тому можеш чи ні, присилуй себе виконувати заповіді.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2481:povchannja-pro-molytvu-i-tverezist-svjatyh-velykyh-starciv-varsonofija-ta-ioana&catid=35:istorychni-postati-cat&Itemid=48',4739,NULL,1,1,26),
(5192,'P','2014-10-11',1,NULL,'Rss','Святий Євагрій про молитву','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,124.jpg\" />Cвятий Євагрій розуміє під молитвою скерованість усіх душевних сил до Бога, свою цілковиту присутність у Його присутності. Це є найбільший дарунок, який лише може зробити Бог людині. Це підносить людину над її власною природою та робить її рівною з ангелами: «Людина, яка по-справжньому молиться, подібна до ангелів. Бо вона безперестанку прагне бачити Отця, що перебуває на небі» (Про молитву, 113-114).</p>\n<p>Щоб людина справді могла молитися, вона мусить позбутися усякого розсіяння та спрямувати свою увагу на Бога: «Блаженний дух, що, молячись без розсіяння, відчуває все глибше прагнення Бога» (Про молитву, 118). «Коли ти з внутрішнім зосередженням шукаєш молитви, тобі нічого не перешкоджатиме, тому що зосередженість є засобом, який найкраще прокладає шлях до молитви. Зосередженість мусиш плекати» (Про молитву, 149).</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2478:svjaty-jevagri-pro-molytvu&catid=35:istorychni-postati-cat&Itemid=48',4735,NULL,1,1,26),
(5193,'P','2014-10-12',1,NULL,'Rss','Вчення про молитву валаамських старців','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,119.jpg\" />1.&nbsp;<strong>Про усну молитву</strong>. У скороченому варіанті її промовляють так: «Господи, помилуй!»; «Господи Ісусе Христе, помилуй мене, грішного!»; а в повному&nbsp;— так: «Господи Ісусе Христе, Сину Божий, помилуй мене, грішного!»</p>\n<p>Спочатку її промовляють здебільшого з неохотою, силуючи себе, і в міру вправляння та примушування себе до неї, якщо тільки є рішучий намір через молитву, з допомогою Божої благодаті позбавлятися пристрастей, вона, від частого вправляння у ній, по мірі викоренення пристрастей, час від часу буде ставати легшою, приємнішою і бажанішою.</p>\n<p>При усній молитві всіма можливостями треба старатися тримати розум на словах молитви, говорити повільно, всю увагу зосереджуючи на думках, що виражаються словами, а коли ум буде відволікатися на сторонні думки, без збентеження знову вводити його в слова молитви («Ліствиця», слово 28, гл. 17).</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2479:vchennja-pro-molytvu-valaamskyh-starciv&catid=35:istorychni-postati-cat&Itemid=48',4736,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5194,'P','2014-10-13',1,NULL,'Rss','Ігумен валаамського монастиря Харитон про молитву','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6204,200,38ondap.jpg\" style=\"margin: 10px; float: right;\" />Про молитву, яка зігріває людину і з’єднує її з Богом в любові</strong></p>\n<p>Найперше має бути відомим те, що християнинові (особі духовній своїм званням) належить завжди різними способами піклуватися про з’єднання з Богом Творцем, люблячим Благодітелем і вищим своїм добром, Котрим і для Котрого він і був створений, бо для душі, яку створив Бог, центром, тобто остаточною метою, має бути не що інше, хіба лише один Бог, від якого вона дістала і життя, і природу свою й задля якого вона має жити вічно. Адже все видиме на землі, любе й бажане: багатство, слава, жінка, діти, одним словом все красиве, солодке й миле цього світу не є властиве душі, а лише тілу, й, будучи дочасним, воно швидко промине, як тінь,&nbsp;— натомість душа, як вічна згідно з своєю природою, може навіки знайти спокій лише в однім вічнім Бозі, як у вищому своєму добрі, прекраснішому за всю красу, солодощі й замилування, солодшому й милішому,&nbsp;— в Бозі, що є для неї найбільш природним її місцем, звідки вона походить і куди має знову повернутися: бо як плоть, що походить від землі, що в землю повертається, так само й душа від Бога походить й до Бога повертається і в Ньому перебуває. Для цього вона й створена Богом, щоби перебувати в Бозі навіки. Тому в цьому дочасному житті необхідно старанно шукати з’єднання з Богом, щоби сподобитись бути з Ним і в Ньому вічно і у майбутньому житті.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2480:igumen-valaamskogo-monastyrja-haryton-pro-molytvu&catid=35:istorychni-postati-cat&Itemid=48',4737,NULL,1,1,26),
(5195,'P','2014-10-14',1,NULL,'Rss','Покров Пресвятої Богородиці','<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,456.jpg\" />Лк. 10, 32-48 </em></p>\n<p>Кожне Богордичне свято відкриває нам одну з чеснот, привілей Богородиці: Богоматеринство, Дівство, Чистоту. Свято Покрову урочисто вшановує її Заступництво. Це свято найкраще виявляє духовне материнство Марії, зокрема її молитовне заступництво, любов і опіку над нами. Добра мати завжди переживає за своїх дітей - подібно Марія дбає про наше тілесне і духовне добро, вічне щастя. У малому повечір’ї вірні кожного разу величають Марію словами: „Заступнице християн бездоганна, молитвенице до Творця незамінна”. А в Молебні звертаються до неї такими словами: „Радуйся, заступнице всіх християн і прибіжище, Владичице”.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2486:pokrov-presvjatoji-bogorodyci&catid=28:tema-nomera-cat&Itemid=2',4738,NULL,1,1,26),
(5196,'P','2015-01-05',1,NULL,'Note','Святий Миколай відвідав поранених героїв','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6323,200,2.jpg\" alt=\"\" width=\"200\" height=\"150\" />18 грудня Святий Миколай зі своїми ангелами-помічниками завітали до центрального клінічного військового госпіталю на Печерську в м. Києві. Відвідали 320 поранених хлопців, яким Святий Миколай вручив подаруночки приготовлені екуменічною спільнотою християн та теплі слова Подяки.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Нехай Всемилосердний Бог нагородить Всіх Вас за щиру любов і добрі наміри!</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6323,200,2.jpg\" alt=\"\" width=\"200\" height=\"150\" />18 грудня Святий Миколай зі своїми ангелами-помічниками завітали до центрального клінічного військового госпіталю на Печерську в м. Києві. Відвідали 320 поранених хлопців, яким Святий Миколай вручив подаруночки приготовлені екуменічною спільнотою християн та теплі слова Подяки.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><br /><img style=\"float: right;\" src=\"image,6323,200,1.jpg\" alt=\"\" width=\"200\" height=\"149\" />Висловлюємо щиру подяку у підготовці подарунків: Галині Іванівні, Віктору Дмитровичу, Анатолію, Леоніду, 4 пенсіонеркам, Юлії, Валентині, Наталії, Валентині, Ірині, Сергію, Ірині, Олексію, Ользі, Віті, Олександру, Святославу, Олені, Юрію, Богданці, Остапчику, Марії, Василині, Владиславу, Юлії, Руслану, Анатолію, Олександру, Людмилі, Анастасії, Володимиру, Мирославі, Оксані, Марії, Іванці, Тарасу, Ірині, Марії, Оресті, Богдані, с.Василії, о. Віталію, о. Ігорю, та Святому Миколаю &ndash; братові Дмитрові.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Нехай Всемилосердний Бог нагородить Всіх Вас за щиру любов і добрі наміри!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Марія Томишинець, &laquo;Оранта&raquo;</span></p>',NULL,4840,5,1,NULL,16);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5197,'P','2014-10-16',1,NULL,'Rss','Слово про молитву преподобного Ніла, подвижника Синайського','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,121.jpg\" />Молитва&nbsp;— це бесіда ума до Бога. Який же потрібно лад для ума, щоб він міг несхитно піднятися до свого Владики й розмовляти з Ним без жодного посередника.</p>\n<p>Стій до втоми й молися благотонно (благоумно або благоналаштовано), відкидаючи підступи життєвих турбот і всіляких помислів; вони-бо бентежать тебе й турбують, щоб розладнати твій тон (добре налаштування).</p>\n<p>Коли демони побачать, що хтось має старанність і ревність до належної молитви, то (не під час молитви) вкладають йому думки неначе про щось потрібне (і відходять), а трохи згодом знову розбуджують про це спогад, підбурюючи й ум шукати це (якщо питання, то вирішити його, а якщо річ, то здобути її), і він, не знаходячи цього (вирішення питання чи способу здобути річ) досадує й журиться. Потім, коли стане на молитву (цей старанний ревнитель молитви), вони нагадують йому, про що думав і що він шукав, щоб ум, знову піднявшись вивідувати (те, що не розв’язав раніше), вчинив молитву (молільника) безплідною.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2482:slovo-pro-molytvu-prepodobnogo-nila-podvyzhnyka-synaskogo&catid=35:istorychni-postati-cat&Itemid=48',4740,NULL,1,1,26),
(5198,'P','2014-10-17',1,NULL,'Rss','Святого Єфрема Сирійського повчання про молитву і духовну тверезість','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,118.jpg\" />Частіше молися зі смиренним серцем, як три юнаки в палаючій печі, і не роби зі себе печери розбійників, віддаючись недозволеним ділам, щоб не осоромитися тобі в день суду, коли відкриються людські тайни.</p>\n<p>Зітхає моє серце, й очі мої жадають сліз; але гріх мій полонив мій ум, аби не зазнав я скрухи й почав гіркими слізьми благати Бога, щоб Він не поверг мене в кромішню пітьму.</p>\n<p>Іона, перебуваючи в череві кита, голосив у молитві: «Ти вивів моє життя з ями, о Господи, мій Боже» (Іона 2, 7). Молитва ця розсікла безодню, повітря, сягнула неба й увійшла до вух Господа. Краще мовити, сам Господь, що, сповнює всесвіт, не був далекий від свого щирого служителя.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2483:svjatogo-jefrema-syriskogo-povchannja-pro-molytvu-i-duhovnu-tverezist&catid=35:istorychni-postati-cat&Itemid=48',4741,NULL,1,1,26),
(5199,'P','2014-10-18',1,NULL,'Rss','Святого Іоана Золотоустого повчання про молитву і духовну тверезість','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,117.jpg\" />Господь заповідає не лише прощати тому, хто обкрадає тебе і займається лихварством, а ще й каже любити його як найсильніше та щиро. Бо це, саме це бажаючи прищепити, сказав Він: «Моліться за тих, що вас зневажають» (Лк. 6, 28)&nbsp;— що ми зазвичай робимо для тих, кого сильно любимо. Так Христос заповів молитися за кривдників&nbsp;— ми ж засідки їм влаштовуємо; і, отримавши наказ благословляти тих, що нас клянуть, закидуємо їх тисячами проклять.</p>\n<p>Молитва&nbsp;— це велика зброя, невичерпний скарб, багатство, що ніколи не зміліє, супокійне пристановище, несхитний затишок і корінь незліченних благ; і джерело, і матір є молитва: вона могутніша за саму царську владу. Молитвою ж я називаю не будь-яку, не недбалу і розсіяну, а полум’яну й многотрудну, що виходить зі зболеної душі й глибоко зосередженого ума. Лишень така молитва сходить до неба.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2484:svjatogo-ioana-zolotoustogo-povchannja-pro-molytvu-i-duhovnu-tverezist&catid=35:istorychni-postati-cat&Itemid=48',4742,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5200,'P','2014-10-19',1,NULL,'Rss','Повчання святого Василія Великого','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,116.jpg\" />Прохання про блага, з яким побожні звертаються до Господа, є молитвою. Однак її слід підносити не лише словесно, а насамперед у душевній налаштованості на молитву. І молитися треба завжди, з будь-якої нагоди. Сидячи за столом&nbsp;— молися; споживаючи хліб&nbsp;— склади подяку Богові, який подав тобі харч; зміцнюючи неміч тіла вином, пам’ятай Того, Хто дав цей дар, щоб звеселити серце та усунути недуги. Коли ж завершиш трапезу&nbsp;— нехай не гасне в тобі пам’ять про Благодійника.</p>\n<p>На будь-який заклик тебе до молитви відповідай: «Готове серце моє»,&nbsp;— і перебувай на богослуженні аж до останньої молитви, маючи за велику для себе шкоду полишити її. Бо ж коли, споживаючи їжу для підкріплення своєї плоті, перебуваєш за трапезою невідлучно, допоки не наситишся, і безконечної потреби нелегко встаєш з-за столу, то чи ж не більше маєш ти залишатися до кінця духовної поживи й укріпляти душу молитвою?</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2485:povchannja-svjatogo-vasylija-velykogo&catid=35:istorychni-postati-cat&Itemid=48',4743,NULL,1,1,26),
(5201,'P','2014-10-24',1,NULL,'Rss','«УГКЦ стоїть перед викликом ліквідації свого Кримського екзархату…»','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6226,200,unnamed_6dfce.jpg\" />УГКЦ уперше за свою історію з часів виходу з підпілля стоїть перед викликом ліквідації свого Кримського екзархату. Влада Криму видала закон, за яким усі релігійні організації мають пройти процес перереєстрації до кінця 2014 року. Немає гарантії, що такий процес потвердить право на існування УГКЦ на цих теренах.</strong></p>\n<p>Це заявив Блаженніший Святослав, Глава УГКЦ, під час прес-конференції 23 жовтня 2014 року у Львові в рамках Зустрічі східних католицьких ієрархів Європи, яка цього року присвячена 25-річчю виходу УГКЦ з підпілля.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2492:2014-10-24-05-27-43&catid=27:gk-v-ukrajini-cat&Itemid=57',4744,NULL,1,1,26),
(5202,'P','2014-10-23',1,NULL,'Rss','Звернення Всеукраїнської ради Церков і релігійних організацій з нагоди виборів до Верховної Ради України','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6226,200,vybory3.jpg\" />Шановні співвітчизники!</strong></p>\n<p>26 жовтня 2014 року відбудуться вибори до Верховної Ради України. Перед новим складом парламенту стоятимуть надзвичайно важливі завдання забезпечити мир і територіальну цілісність для України, сприяти реформам та відновленню економіки.</p>\n<p>Слід підкреслити, що після конституційної реформи на Верховну Раду лягає більша відповідальність, адже від парламенту та утвореної в ньому більшості депутатів залежить формування Уряду. Тому більша відповідальність лягає і на кожного громадянина, адже від нашого спільного вибору залежить склад нової Верховної Ради.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2494:zvernennja-vseukrajinskoji-rady-cerkov-i-religinyh-organizaci-z-nagody-vyboriv-do-verhovnoji-rady-ukrajiny&catid=37:spilnota-cat&Itemid=49',4745,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5203,'P','2014-10-22',1,NULL,'Rss','«Державне самогубство, коли громадяни не користуються правом обирати»','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6226,200,huzar_blogy.jpg\" />Кожен мусить покласти свою лепту на розвиток демократії «Популізм – то пуста мова, - говорить Блаженнійший. – У Святому Письмі написано: будуть покарані за кожне пусте слово». Як не піддатися на політичний популізм, а з-поміж безлічі кандидатів обрати достойного, на порозі яких змін опинилася Україна та які реформи потрібно впровадити, аби не втратити шанс отримати нову країну з політиками якісно нового рівня, про все це напередодні парламентських виборів ми говорили з видатним церковним мислителем нашого часу - Любомиром Гузаром.</strong>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2493:2014-10-24-05-31-23&catid=28:tema-nomera-cat&Itemid=2',4746,NULL,1,1,26),
(5204,'P','2014-10-21',1,NULL,'Rss','«Церква Московського патріархату може далеко більше зробити для деескалації насильства в Україні та примирення»','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6226,200,u_zadumi.jpg\" />16 жовтня 2014 року на пленарній сесії Синоду Єпископів у Ватикані Митрополит РПЦ Іларіон Алфеєв під час свого виступу вкотре заговорив про проблеми унії, яка, на його думку, загострилася у зв’язку з останніми подіями в Україні. Свої коментарі щодо цього в ефірі SiriusXM The Catholic Channel дав Отець і Глава УГКЦ Блаженніший Святослав.</strong></p>\n<p><strong>Ваше Блаженство, у своєму виступі на пленарній сесій Синоду Єпископів у Ватикані 16 жовтня цього року Митрополит Іларіон Алфеєв не оминув нагоди ще раз відкрито атакувати Українську Греко-Католицьку Церкву. Зокрема, він вкотре заговорив про проблеми унії, яка, на його думку, загострилася у зв’язку з останніми подіями в Україні. Чому така агресія?</strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2495:2014-10-24-05-39-05&catid=28:tema-nomera-cat&Itemid=2',4747,NULL,1,1,26),
(5205,'P','2014-10-20',1,NULL,'Rss','Ідея любові до свого народу дає нашій армії перевагу над армією загарбника','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6226,200,huzar_velykden14.jpg\" /></strong>Живемо в стані війни. На кордоні між Україною та Російською Федерацією - військові об’єднання: одні готові нападати на нашу державу, інші - боронити її від чужого нападу.</p>\n<p>Армія Російської Федерації чисельна, ідеться про десятки тисяч активних воїнів. Вони мають все необхідне: достатнє харчування, відповідний одяг-однострій, сучасну зброю, танки, гармати, літаки, про всяк випадок - медичні пункти. Ці люди пішли на фронт, усвідомлюючи всі його небезпеки, але вони спокійні за долю своїх родин. Про їхню діяльність розповідають відповідно налаштовані засоби масової інформації. З українського боку не все так добре.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2496:ideja-ljubovi-do-svogo-narodu-daje-nashi-armiji-perevagu-nad-armijeju-zagarbnyka&catid=28:tema-nomera-cat&Itemid=2',4748,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5206,'P','2014-10-31',1,NULL,'Rss','Папа: диявол не міф, його перемагають зброєю істини','<p><img style=\"margin: 10px; float: right;\" src=\"http://popefrancis.org.ua/wp-content/uploads/2014/10/1_0_833775.jpg\" />Християнське життя – це «боротьба» проти диявола, світу цього і плотських пристрастей. Коментуючи уривок з Послання апостола Павла до Ефесян, Святіший Отець підкреслив, що диявол існує, і ми «повинні боротися з ним зброєю істини».<o:p></o:p></p>\n<p>Апостол, зазначив Папа, каже про християнське життя військовою мовою. «Життя в Бозі має бути захищене, і щоб його зберегти, ми повинні боротися», а для цього потрібні мужність і сила, щоб протистояти і благовістити.<o:p></o:p></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2502:papa-dyjavol-ne-mif-ogo-peremagajut-zbrojeju-istyny&catid=32:gk-svit-cat&Itemid=58',4754,NULL,1,1,26),
(5207,'P','2014-10-25',1,NULL,'Rss','Любомир Гузар: в Україні не громадянська війна, ми виходимо з неправдивого миру','<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6227,200,199428.jpg\" />Знайти резиденцію архієпископа-емерита Любомира Гузара виявилося нелегко. Блаженніший живе на околиці села під Києвом. Живе настільки тихо, що небагато хто з місцевих жителів знає, де розташований його будинок – там, де закінчується асфальтована дорога.</em></p>\n<p><em>Колишній голова Української Греко-католицької церкви прийняв знімальну групу \"Обозревателя\" як дорогих гостей. Ми відчували себе в його резиденції, як вдома. І як найстаріший член родини, Блаженніший Любомир, відповідаючи на наші запитання, казав про речі, що видні лише здалека – з відстані фізичної і історичної.</em>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2497:ljubomyr-guzar-v-ukrajini-ne-gromadjanska-vina-my-vyhodymo-z-nepravdyvogo-myru&catid=28:tema-nomera-cat&Itemid=2',4749,NULL,1,1,26),
(5208,'P','2014-10-28',1,NULL,'Rss','Парох і парохія','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6232,200,464548.jpeg\" />Святочним днем парафії можна назвати неділю 12 жовтня 2014 р. Б ,день пам’яті прп. Киріяка самітника. То справжнє свято для парафіяльної спільноти і духовенства церкви св. Миколая Мирлікійського на Аскольдовій Могилі, адже на Архиєрейській Божественній Літургії Владика Йосиф Мілян вручив засновнику громади, адміністратору парафії о. Ігорю Онишкевичу грамоту пароха.</p>\n<p>Маємо надію в Бозі, що ця грамота пароха, яка ствердила громаду як парохію, дасть нашим душпастерям, сестрам-служебницям Непорочної Діви Марії які обслуговують цей Храм і очолюють катехитичну недільну школу, парафіянам і прихожанам, духовне піднесення закладати цеглини Аскольдової Лаври, допоможе всім нам краще працювати для Слави Божої, добра Церкви і для спасіння повірених душ.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=2498:paroh-i-parohija&catid=25:parafija-cat&Itemid=27',4750,NULL,1,1,26),
(5209,'P','2014-10-29',1,NULL,'Rss','Блаженніший Любомир: «Знову про мир»','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6233,200,v_zadumi.jpg\" /></strong>Компендіум суспільного вчення Католицької Церкви, книжка, з якої черпаємо думки для написання матеріалів про мир, наголошує на важливості молитви для встановлення миру. Церква не тільки заохочує це робити, а й практикує.</p>\n<p>Для прикладу, Свята Літургія, найважливіша з усіх молитов, починається словами: \"У мирі Господеві помолімося. За мир з висот… помолімося. За мир всього світу… помолімся\". А закінчується проханням до Господа: \"Мир світові даруй, церквам Твоїм, Богом береженому народові нашому, правлінню і всім людям Твоїм\".</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2499:2014-10-29-21-28-25&catid=28:tema-nomera-cat&Itemid=2',4751,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5210,'P','2014-10-30',1,NULL,'Rss','Любомир Гузар: це російсько-українська війна, яку готували 10 років','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6234,200,wil6_guzar_01_e51a2.jpg\" />Колишній Глава Української Греко-Католицької Церкви Блаженніший Любомир (Гузар) розповів, як зробити Східну Україну вільною від російської пропаганди, чому Схід і Крим не відчувають себе повною мірою українськими, а також – про політичну роль Церкви і патріотизм.</strong></p>\n<p><strong>1. Це – російсько-українська війна</strong> (на Сході України, – ред.). І цей конфлікт не виник вчора, його послідовно готували років десять. Ті, хто був при владі в Україні, певною мірою були ставлениками Росії. І те, що ми сьогодні маємо на сході — це результат зомбування людей, яких довгі роки годували однією і тією ж брехнею. Вони й повірили.\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2501:ljubomyr-guzar-ce-rosisko-ukrajinska-vina-jaku-gotuvaly-10-rokiv&catid=28:tema-nomera-cat&Itemid=2',4752,NULL,1,1,26),
(5211,'P','2014-10-30',1,NULL,'Rss','Хелловін (Halloween) – антихристиянське свято!','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6233,200,20111031200831.jpg\" />Внаслідок різних часових і просторових обставин серед українського суспільства стають популярними різноманітні святкування, які не мають жодного коріння в нашому народі. До них можна віднести також сьогодні популярне святкування Хелловіна (31 жовтня). З мас-медіа кожного разу напередодні цього дня можна почути запрошення на вечірки та забави з нагоди святкування «Хелловіна». Що цеза свято? Яка його історія? І чи варто греко-католикам, і, взагалі, християнам, відзначати цей день у своєму календарі?</p>\n<p><strong>&nbsp;</strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2500:hellovin-halloween--antyhrystyjanske-svjato&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4753,NULL,1,1,26),
(5212,'P','2014-11-01',1,NULL,'Rss','Урочистість Усіх Святих','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6236,200,Swietych.png\" />Ці «великі небесні іменини» об’єднують усіх. І тих, чиї імена завжди є в літургійному календарі, й тих, хто туди потрапляє рідко. І тих, кого Бог знає, а Церква офіційно святим не назвала, бо, може, і не здогадується, що то була свята людина. Радісна урочистість Усіх Святих промовляє до сердець віруючих: на Небесах так багато наших братів і сестер, наших заступників і помічників, – радіймо їм і живімо добре, аби долучитися до них свого часу! Урочистість Усіх Святих – це зовсім не «гробки», це торжество святості!</p>\n<p>А ще ця урочистість відображає певний «вплив», або ж реакцію християнства на явище язичництва. З історичного погляду, вшанування святих у Церкві не виникло саме по собі як окреме свято чи особливо підкреслювана побожність. Віруючі вшановували Матір Спасителя, загиблих за віру зі своїх спільнот (а потім сформувався культ мучеників), з часом Церква у своїй повноті дійшла до розуміння великої цінності дівоцтва перед Богом – постало вшанування дів…</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2503:urochystist-usih-svjatyh&catid=35:istorychni-postati-cat&Itemid=48',4755,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5213,'P','2014-11-03',1,NULL,'Rss','Сенс смерті','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6238,200,d135363934.jpg\" /></strong>Жити глибиною духа означає приймати життя цілісно, разом із його завершальним етапом&nbsp;— смертю. Смерть це завжди кінець біологічного життя, фізичний процес розкладу, деструкція, перед лицем якої людина залишається безсилою. Також вона є небуттям несповнених мрій, життя, існування.</p>\n<p>Давні монаші статути, наприклад, бенедиктинський чи камедульський, радили щодня згадувати про смерть. Однак не згадувалося про смерть заради тривання в смутку, але навпаки, аби жити свідомо й інтенсивно. Якби ми щодня думали, що можемо не дочекатися світанку, то кожен день переживали б у спосіб більш свідомий та інтенсивний.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2504:sens-smerti&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4756,NULL,1,1,26),
(5214,'P','2014-11-03',1,NULL,'Rss','Російсько-радянська релігійна міфотворчість','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6239,200,images.jpg\" />Пострадянський простір живе подвійним, а то й потрійний життям. Кілька поколінь людей народилися в часи Сталіна, Хрущова і Брежнєва, а значить, що частина їх віку була пов\'язана з тим рівнем життя, який встановлювала КПРС та інші партійно-господарські органи. Життя було розписане по пунктах для всіх. Партія знала, хто повинен і де вчитися, якої національності мають бути майбутні інженери або лікарі, де вони працюватимуть&nbsp; за&nbsp; \"напрямками\", скільки отримувати «зарплати» і коли їм дозволяти вступати в партію. Життя радянської людини регулювали парткоми, райкоми, міськкоми і обкоми, якими, в свою чергу керувала ЦК. Нагляд здійснював КДБ і численні помічники - піонери (юні друзі міліції) і комсомольці (оперативні комсомольські загони), а також дружинники і таємні інформатори. За 74 роки виросло кілька поколінь слухняних людей, для яких розпад СРСР став особистою трагедією - вони залишилися без спрямовуючої ролі ЦК КПРС.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2505:rosisko-radjanska-religina-mifotvorchist&catid=37:spilnota-cat&Itemid=49',4757,NULL,1,1,26),
(5215,'P','2014-11-03',1,NULL,'Rss','Мирослав Маринович: приклад Ісуса - це найбезумніший бізнес-проект','<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6240,200,marynovych_2f5a0.jpg\" />Влада це - екстракт отрути, яка булькає в середині нашого суспільства. Тому просто замінити владу не змінюючи систему нічого не дасть</em></p>\n<p><strong>Від того якими є духовні та моральні цінності у суспільстві, залежить доля бізнесу як системи. Про це на лекції-зустрічі для львівських підприємців розповів, дисидент, правозахисник, віце-ректор Українського католицького університету у Львові Мирослав Маринович.</strong>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2506:myroslav-marynovych-pryklad-isusa-ce-nabezumnishy-biznes-proekt&catid=28:tema-nomera-cat&Itemid=2',4758,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5216,'P','2014-11-04',1,NULL,'Note','ПОЖЕРТВИ ПАРАФІЯН ЦЕРКВИ СВ. МИКОЛАЯ ЧУДОТВОРЦЯ НА АСКОЛЬДОВІЙ МОГИЛІ У ВЕРЕСНІ 2014 Р. Б.','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</span></p>\r\n<table style=\"width: 405px;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>№ з/п</strong></span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Ж Е Р Т В О Д А В Ц І</strong></span></p>\r\n</td>\r\n<td width=\"331\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Пожертва на (для, до):</strong></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">1</span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Світлана</span></p>\r\n</td>\r\n<td width=\"331\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Шкільний портфелик</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">2</span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Іван</span></p>\r\n</td>\r\n<td width=\"331\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дітям-сиротам</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">3</span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Аскольд</span></p>\r\n</td>\r\n<td width=\"331\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дітям-сиротам</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">4</span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Родина Тараса та Олени</span></p>\r\n</td>\r\n<td width=\"331\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Молитву за усиновлення</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">5</span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Адам та Надія</span></p>\r\n</td>\r\n<td width=\"331\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Допомогу і молитву за поранених</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">6</span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p><span style=\"font-family: arial, helvet