INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5376,'P','2015-02-20',1,NULL,'Rss','В’ячеслав Брюховецький: «Покоління, що змінить країну, вже приходить»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6402,200,fd60f77-paralax-1.jpg\" />Platfor.ma спільно з&nbsp;<a href=\"http://kmbs.ua/uk\" target=\"_blank\">Києво-Могилянською бізнес-школою</a>започатковує&nbsp;новий спецпроект «Changers» про тих, хто змінює світ довкола себе.&nbsp;Перший наш герой – почесний президент Києво-Могилянської академії, громадський діяч, учасник групи “Першого грудня”, літературознавець та Герой України В’ячеслав Брюховецький, який став головним ініціатором відродження Могилянки як сучасного вищого навчального закладу.Platfor.ma&nbsp;поговорила з ним про те, як реформувати вищу школу, що робити з Донбасом і що Україна може запропонувати світу.\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2629:2015-02-20-10-19-22&catid=28:tema-nomera-cat&Itemid=2',4920,NULL,1,1,26),
(5377,'P','2015-02-20',1,NULL,'Rss',' Йосип Сліпий - голова Української підпільної церкви (частина ІІІ)','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6402,200,mio.jpg\" />Розділ I. Становлення особи Йосипа Сліпого</strong><strong></strong></p>\n<p><strong>1.3 Сліпий - ректор семінарії і академії</strong><strong> </strong></p>\n<p>Перед тим, як описати працю молодого енергійного богослова о. Йосипа після його повернення з Риму до Львова, варто було коротенько подати історичне тло того часу, в якому він працював. Це дозволить зрозуміти ті проблеми, що стояли перед людьми та й перед Церквою в тому часі і яким чином вирішувалися ці питання.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2630:-osyp-slipy-golova-ukrajinskoji-pidpilnoji-cerkvy-chastyna-iii&catid=35:istorychni-postati-cat&Itemid=48',4922,NULL,1,1,26),
(5378,'P','2015-02-20',1,NULL,'Rss','Владика Борис Ґудзяк: Коли відмовляємося від свободи, то тратимо свою подібність до Бога ','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6402,200,gudziak_borys_6099.jpg\" />– Владико, минув рік після Майдану. Україну й весь світ потряс масовий розстрілу людей у центрі столиці. Тепер відлік жертв пішов на тисячі... І як не страшно це звучить, але ми звикли до смерті. Як таке може бути?</strong></p>\n<p>– Тема смерті й нашого ставлення до неї – дуже суперечлива. У нас може бути одночасно кілька різних векторів думки – паралельних чи навіть протилежних.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2631:vladyka-borys-gudzjak-koly-vidmovljajemosja-vid-svobody-to-tratymo-svoju-podibnist-do-boga-&catid=28:tema-nomera-cat&Itemid=2',4923,NULL,1,1,26),
(5379,'P','2015-02-20',1,NULL,'Rss','Слово Блаженнішого Святослава, Глави УГКЦ, з нагоди зустрічі зі Святішим Отцем','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6406,200,ad-limina2015-19.jpg\" />Взиваю від стогону серця мого. О Господи, перед тобою все моє бажання (Пс 37, 9-10)</strong></p>\n<p><strong>Святіший Отче!</strong></p>\n<p>Ці слова псалмопівця виражають найглибші почуття пастирів нашої Церкви, які, із синівською пошаною та вдячністю, прийняли запрошення Вашої Святості прибути до Риму в паломництві до гробів святих Апостолів, і щоб зблизька пережити спасенне сопричастя нашої Церкви з Наступником святого Петра.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2632:slovo-blazhennishogo-svjatoslava-glavy-ugkc-z-nagody-zustrichi-zi-svjatishym-otcem&catid=32:gk-svit-cat&Itemid=58',4924,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5380,'P','2015-02-21',1,NULL,'Rss','Правила посту в Українській Греко-Католицькій Церкві','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6406,200,46446.jpg\" />Піст в практиці Церкви існує від самих початків. Але з бігом часу кожна із конфесій встановила свою практику посту. Слід однак пам’ятати, що основна мета посту – духовна, тобто, насамперед, потрібно постити від гріха, злих схильностей. У дні посту необхідно стримуватись від лихих діл, думок, слів; зберігати погідність духу, внутрішній мир та радість. Стримування від&nbsp;гріха набагато важливіше ніж стримування від їжі, хоча це також необхідне та допомагає поборювати гріх, вправлятись у духовному зрості, духовному житті. Нам же потрібно пам’ятати та виконувати і одне і друге.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2633:pravyla-postu-v-ukrajinski-greko-katolycki-cerkvi&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4925,NULL,1,1,26),
(5381,'P','2015-02-21',1,NULL,'Rss',' Йосип Сліпий - голова Української підпільної церкви (частина ІV)','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6406,200,slipyj_aresht2.jpg\" />Розділ II. Праця на благо Церкви і народу</strong><strong> </strong></p>\n<p><strong>2.1 Сліпий - архипастир Української Греко-Католицької Церкви</strong><strong> </strong></p>\n<p>З плином часу завжди настають якісь зміни. Одні на краще, а інші - на гірше. Мабуть до цього другого ряду можна назвати зміни, які приніс рік 1939 на теренах Європи. Її ділили між собою дві системи - нацистська і більшовицька. На підставі договорів між урядами СРСР і Німеччини в серпні 1939 року більшовики зайняли Галичину і Волинь, а разом з тим три найбільші єпархії - Львів, Перемишль і Станіславівську єпархії, а крім того Волинь - територію апостольського візитатора.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2634:-osyp-slipy-golova-ukrajinskoji-pidpilnoji-cerkvy-chastyna-iv&catid=35:istorychni-postati-cat&Itemid=48',4926,NULL,1,1,26),
(5382,'P','2015-03-09',1,NULL,'Rss','Ким був Тарас Шевченко?','<p><em><img src=\"http://askoldova-mohyla.org/uk/image,6446,200,0000006558.jpg\" style=\"margin: 10px; float: left;\" /></em></p>\n<div>\n<p style=\"text-align: right;\"><em>Ми віруєм Твоїй силі<o:p></o:p></em></p>\n<p style=\"text-align: right;\"><em>І Духу живому.<o:p></o:p></em></p>\n<p style=\"text-align: right;\"><em>Встане правда! Встане воля!<o:p></o:p></em></p>\n<p style=\"text-align: right;\"><em>І Тобі одному<o:p></o:p></em></p>\n<p style=\"text-align: right;\"><em>Помоляться всі язики.</em><o:p></o:p></p>\n<p>(Т. Шевченко «Кавказ»)</p>\n</div>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2664:kym-buv-taras-shevchenko&catid=28:tema-nomera-cat&Itemid=2',4965,NULL,1,1,26),
(5383,'P','2015-02-21',1,NULL,'Rss','Промова Папи Франциска до єпископів України з візитом ad limina apostolorum ','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6409,200,OSSROM17663_Articolo.jpg\" />Ваше Блаженство, Ваше Високопреосвященство,</p>\n<p>Дорогі Браття в єпископстві,</p>\n<p>щиро вітаю вас у цьому домі, який є також і вашим домом. Вам добре про це відомо, тому що Наступник святого Петра завжди з братньою приязню приймав братів з України, країни, яка по праву вважається пограниччям між спадкоємцями святих Володимира й Ольги та святого Адальберта й великих каролінгських місій, але також і тих, які посилаються на святих Кирила й Методія, Апостолів Слов’ян. Але ще й перед цим, існують передання, частково задокументовані, які згадують про святого Апостола Андрія і двох Папів-мучеників – святих Климента та Мартина І. Отож, щиро вас вітаю, найдорожчі!</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2635:promova-papy-francyska-do-jepyskopiv-ukrajiny-z-vizytom-ad-limina-apostolorum-&catid=27:gk-v-ukrajini-cat&Itemid=57',4927,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5384,'P','2015-02-22',1,NULL,'Rss',' Йосип Сліпий - голова Української підпільної церкви (частина V)','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6409,200,1963_02_10.jpg\" />Розділ III. Спадщина Йосипа Сліпого</strong><strong> </strong></p>\n<p><strong>3.1 Пасторальна праця</strong><strong> </strong></p>\n<p>Доля митрополита Йосипа Сліпого склалася так, що на протязі майже двох десятків років він, перебуваючи в ув’язненні, був усунутий від управління своєю Церквою. Звичайно, під час заслання він, як Глава Української Греко-Католицької Церкви, видавав пастирські листи, проводив роботу для зміцнення своєї Церкви, зокрема, назначав адміністраторів, а 4 лютого 1963 року<a href=\"http://www.bestreferat.ru/referat-197752.html#_ftn114\">[114]</a> по дорозі з Сибіру до Риму висвятив у готелі в Москві о. Василя Величковського на єпископа - щоб він міг на місці опікуватися Католицькою Церквою. Але цього, звичайно, було мало для того, щоб відродити зруйновану більшовиками Церкву.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2636:-osyp-slipy-golova-ukrajinskoji-pidpilnoji-cerkvy-chastyna-v&catid=35:istorychni-postati-cat&Itemid=48',4928,NULL,1,1,26),
(5385,'P','2015-02-22',1,NULL,'Rss','Молитва прощення','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6391,200,1393670913.jpg\" />Ця молитва потрапила до мене кілька років тому російською мовою, натхненний її словами я тоді переклав її українською. Я свідомий, що переклад недосконалий. Сьогодні про неї згадав і вирішив опублікувати в соцмережах, але подумав, що більше людей зможуть її прочитати, якщо вона буде опублікована тут. Свого часу ця молитва мене дуже вразила. Часом я її перечитую. Читаючи її перший раз, я ледь не плакав. Не пожалійте кілька хвилинок свого часу, щоб видрукувати її і ввечері перед сном, у тиші серця, прочитати. Мені цікаво було читати її саме з листка, а не з монітору. Цим дописом я водночас хочу попросити прощення в кожного, хто його прочитає.\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2637:molytva-proshchennja',4929,NULL,1,1,26),
(5386,'P','2015-02-22',1,NULL,'Rss',' Йосип Сліпий - голова Української підпільної церкви (частина VІ)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6412,200,ArticleIma.jpg\" />Наша Церква, наш народ має багато героїв давніх і сучасних, але до тих, без сумніву найбільших, належить Блаженніший Патріарх Кир Йосиф Сліпий. Ця постать є прикрасою цілої Вселенської Церкви, належить до історії віків. Що лягло в основу всієї діяльності нашого Блаженнішого Патріарха Йосифа? Відповідь проста, але ясна. - Його відданість Христові, його відданість своїй Церкві, любов до свого народу та готовність на жертву й терпіння за святу справу.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2638:-osyp-slipy-golova-ukrajinskoji-pidpilnoji-cerkvy-chastyna-vi&catid=35:istorychni-postati-cat&Itemid=48',4930,NULL,1,1,26),
(5387,'P','2015-02-23',1,NULL,'Rss','Пастирське послання Блаженнішого Святослава на Великий піст 2015 року','<p align=\"center\"><em><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6412,200,RV2947_Articolo.jpg\" />Високопреосвященні та Преосвященні владики!&nbsp;Всесвітліші, високопреподобні та всечесні отці!&nbsp;Дорогі в Христі брати і сестри!</strong></em></p>\n<p>Великий піст – це особливий період літургійного року, час духовного пробудження і очищення. Сам Господь спонукає нас до особливої уваги та роздумів над особистим життям, нашим ставленням до Бога і ближнього; запрошує нас перевірити, наскільки правильно ми переживаємо ті історичні обставини, в яких нам доводиться проходити цьогорічний шлях Великого посту.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2639:pastyrske-poslannja-blazhennishogo-svjatoslava-na-velyky-pist-2015-roku&catid=28:tema-nomera-cat&Itemid=2',4931,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5388,'P','2015-02-23',1,NULL,'Rss','У Великий Піст – із молитвою святого Єфрема','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6414,200,47576.jpg\" />Святий Єфрем Сирійський – творець цієї <a href=\"http://www.pokrov.org.uk/Prayers/ephrem_rus.html\" target=\"_blank\">молитви</a> – жив у ІV столітті. Ще за життя його цінували як великого вчителя духовного життя, мудрого проповідника, поета, тлумача Свята Письма й захисника Христової віри. Його домівкою переважну більшість часу була печера серед скель біля міста Едесси (Сирія). Там св. Єфрем провадив суворе аскетичне життя, харчувався ячмінним хлібом і городиною, спав на голій землі. Там він і написав більшість своїх творів.</strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2640:u-velyky-pist--iz-molytvoju-svjatogo-jefrema&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4932,NULL,1,1,26),
(5389,'P','2015-02-23',1,NULL,'Rss','Владика Венедикт: «Під час посту починаймо з малих речей, щоб вони були сталі, а тоді прийдемо до великих»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6414,200,unnamed.jpg\" />23 лютого розпочався Великий піст – період, коли християни особливо роздумують над земним життя Ісуса Христа та Його страстями. Цьогорічний піст для України буде особливим. Владика Венедикт, голова Патріаршої Літургійної комісії УГКЦ, розповідає про те, як правильно провести великопісний час українцям.\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2641:2015-02-23-21-05-22&catid=28:tema-nomera-cat&Itemid=2',4933,NULL,1,1,26),
(5390,'P','2015-02-23',1,NULL,'Rss','У Великий Піст – із молитвою святого Єфрема','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6414,200,47576.jpg\" />Святий Єфрем Сирійський – творець цієї <a href=\"http://www.pokrov.org.uk/Prayers/ephrem_rus.html\" target=\"_blank\">молитви</a> – жив у ІV столітті. Ще за життя його цінували як великого вчителя духовного життя, мудрого проповідника, поета, тлумача Свята Письма й захисника Христової віри. Його домівкою переважну більшість часу була печера серед скель біля міста Едесси (Сирія). Там св. Єфрем провадив суворе аскетичне життя, харчувався ячмінним хлібом і городиною, спав на голій землі. Там він і написав більшість своїх творів.</strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2640:u-velyky-pist-iz-molytvoju-svjatogo-jefrema&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4934,NULL,1,1,26),
(5391,'P','2015-02-24',1,NULL,'Rss','Про гідність жінки','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6416,200,matiiditi.jpg\" />Питання гідності людини та&nbsp;її&nbsp;порушення є&nbsp;завжди актуальними. Обговорюючи проблеми людського буття, ми&nbsp;постійно змушені звертатися до&nbsp;визначення, що&nbsp;є&nbsp;людина, у&nbsp;чому складається її&nbsp;гідність як&nbsp;людської істоти, та&nbsp;як&nbsp;захистити її&nbsp;права, гідність та&nbsp;свободи. Що&nbsp;ми&nbsp;розуміємо під поняттям «людська гідність»? Так Словник української мови (в&nbsp;11&nbsp;томах, за&nbsp;ред. І.&nbsp;К.&nbsp;Білодіда. Київ, Наукова думка, 1970-1980) подає наступне визначення: 1. Сукупність рис, що&nbsp;характеризують позитивні моральні якості.&nbsp;2. Усвідомлення людиною своєї громадської ваги, громадського обов’язку. Синонімами до&nbsp;слова «гідність» є&nbsp;слова: важливість, значимість, авторитет, велич, престиж, цінність, перевага.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2642:pro-gidnist-zhinky&catid=28:tema-nomera-cat&Itemid=2',4935,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5392,'P','2015-02-22',1,NULL,'Rss','Молитва прощення','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6391,200,1393670913.jpg\" />Ця молитва потрапила до мене кілька років тому російською мовою, натхненний її словами я тоді переклав її українською. Я свідомий, що переклад недосконалий. Сьогодні про неї згадав і вирішив опублікувати в соцмережах, але подумав, що більше людей зможуть її прочитати, якщо вона буде опублікована тут. Свого часу ця молитва мене дуже вразила. Часом я її перечитую. Читаючи її перший раз, я ледь не плакав. Не пожалійте кілька хвилинок свого часу, щоб видрукувати її і ввечері перед сном, у тиші серця, прочитати. Мені цікаво було читати її саме з листка, а не з монітору. Цим дописом я водночас хочу попросити прощення в кожного, хто його прочитає.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2637:molytva-proshchennja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4936,NULL,1,1,26),
(5393,'P','2015-03-30',1,NULL,'Rss','Послання Блаженнішого Святослава до молоді','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6479,200,ArticleImage.jpg\" />Дай мені, сину, твоє серце, і очі твої нехай доріг моїх пильнують (Прип. 23, 26).</strong></p>\n<p><em>Дорога в Христі молоде!</em></p>\n<p>Завжди з нетерпінням очікую Квітної неділі – дня славного в’їзду Христа до Єрусалима, щоб скористати з нагоди підтримати, благословити вас і звернутися до вас Божим словом.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2685:poslannja-blazhennishogo-svjatoslava-do-molodi&catid=27:gk-v-ukrajini-cat&Itemid=57',4997,NULL,1,1,26),
(5394,'P','2015-02-24',1,NULL,'Rss','Чи розсварить Україна Папу Римського з Москвою','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6416,200,n_s_373x300.jpg\" />Папа Римський Франциск найближчим часом може відвідати нашу країну</strong></p>\n<p>Принаймні, він отримав запрошення від президента Порошенка - і начебто, пообіцяв. Хоча перспектива такого візиту поставить його у доволі складу ситуацію. \"Латиноамериканський папа\", як кажуть, на відміну від своїх попередників, не надто занурений у європейські справи і до тонкощів східноєвропейської політики не надто придивляється. Конфлікт на Сході України став для нього \"ще однією\" громадянською війною - невідомо, сам він дійшов такого висновку, чи про це потурбувалося його оточення. Але його виступ, в якому він, ледь не повторюючи патріарха Московського, закликав \"зупинити братовбивчий конфлікт\" в Україні, вразив українських католиків в саме серце.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2643:chy-rozsvaryt-ukrajina-papu-rymskogo-z-moskvoju&catid=32:gk-svit-cat&Itemid=58',4937,NULL,1,1,26),
(5395,'P','2015-02-25',1,NULL,'Rss','«Найкращий мій татусь»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6418,200,PIC_1-R-40-1.jpg\" />1 листопада 2014 року в УГКЦ та в Україні розпочався Рік митрополита Андрея Шептицького. Верховна Рада України в червні минулого року проголосувала за відзначення 150-річного ювілею від дня народження Митрополита на державному рівні.</p>\n<p><em>Андрей Шептицький народився 29 липня 1865 р. Очолив Українську Греко-Католицьку Церкву 1901 року. І був її очільником аж до своєї смерті 1 листопада 1944 р. Митрополит Андрей походить із давнього українського графського роду Шептицьких.</em></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2644:2015-02-24-18-20-08&catid=35:istorychni-postati-cat&Itemid=48',4938,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5396,'P','2015-02-25',1,NULL,'Rss','•	Богослужіння Великого посту: Чотиридесятниця','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6420,200,8.jpg\" />Великий піст –&nbsp;зовсім особливий&nbsp; час&nbsp;церковного року, насичений&nbsp;не частими богослужіннями, більшість яких відбуваються тільки в цей період церковного року. Народжений з практики підготовки катехуменів до Хрещення, Великий піст став часом відродження й відновлення всіх християн, що дотримуються візантійського церковного обряду.</strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2645:-bogosluzhinnja-velykogo-postu-chotyrydesjatnycja&catid=27:gk-v-ukrajini-cat&Itemid=57',4939,NULL,1,1,26),
(5397,'P','2015-02-25',1,NULL,'Rss','Офіційні правила посту в РКЦ та УГКЦ','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6422,200,86767.jpg\" />Католики в Україні переживають перший тиждень&nbsp;Великого Посту. Подаємо офіційні приписи щодо посту в Римо-Католицькій Церкві в Україні та Українській Греко-Католицькій Церкві. Ці приписи стосуються лише їжі, проте, нагадаємо, що важливими складовими посту є також молитва і милостиня. Про&nbsp;них читайте в інших матеріалах&nbsp;<a href=\"http://www.credo-ua.org/\">CREDO</a>.</strong>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2646:oficini-pravyla-postu-v-rkc-ta-ugkc&catid=27:gk-v-ukrajini-cat&Itemid=57',4940,NULL,1,1,26),
(5398,'P','2015-02-25',1,NULL,'Rss','Невідомий шлях, чи шлях у невідомості…','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6423,200,2EZi_AOsfmI.jpg\" />08 лютого 2015 р., о. Ігор, нам передав в’язані шкарпетки від бабці Марії із Карпат для поранених хлопців.</p>\n<p>Наша екуменічна спільнота, Олександр, Василина і Марія, вперше вирішили завітати до Психіатричного відділення в військовому шпиталі на Печерську.</p>\n<p>Подзвонили ми в дзвіночок (там всі двері закриті на ключ), до нас вийшов черговий лікар не представився, проте спитав хто ми такі, попросив показати посвідчення Волонтерів, на що ми відповіли, що у нас ніяких посвідчень не має, хіба що можемо показати паспорт, врешті нас пропустили всередину. Ми попросили запросити до нас чотирьох хлопців (були чотири пари шкарпеток). До нас вийшли три хлопці: Валерій, Павло і Юрій, від шкарпеток відмовились, попросили передати на війну бо там вони насправді дуже потрібні, а в лікарні у них все є.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=2647:nevidomy-shljah-chy-shljah-u-nevidomosti&catid=29:propovidi-cat&Itemid=34',4941,NULL,1,1,26),
(5399,'P','2015-04-12',1,NULL,'Rss','Світла Пасха. Воскресіння Господнє','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6509,200,45464.jpg\" />“<em>Христос воскрес із мертвих, смертю смерть подолав і тим, що в гробах, життя дарував”</em><br /> (Тропар Пасхи).</p>\n<p>З усіх великих празників нашого церковного року найбільш давній, урочистий і радісний — це&nbsp;<strong>Великдень</strong>. Він, як сказано в ірмосі 8 пісні пасхального канону утрені: “Цар і Господь, празників празник і торжество торжеств”.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2717:svitla-pasha-voskresinnja-gospodnje&catid=28:tema-nomera-cat&Itemid=2',5033,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5400,'P','2015-02-26',1,NULL,'Rss','Тверезо мислячий не може заперечити нападу Путіна на Україну – єпископ Ґудзяк','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6424,200,6F37F99.jpg\" />Гість «<a href=\"http://www.radiosvoboda.org/archive/your_liberty/latest/637/2339.html\" target=\"_blank\">Вашої Свободи</a>»: Борис Ґудзяк, єпарх Паризької єпархії УГКЦ, президент Українського католицького університету.</strong></p>\n<p><strong>Віталій Портников: Нещодавно екзархат Української греко-католицької церкви, Синод єпископів Української греко-католицької церкви звернувся до священиків УГКЦ з розгорнутим документом про душпастирство в умовах війни. Це фактично така розгорнута програма дій щодо того, що сьогодні має робити церква.</strong>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2648:tverezo-mysljachy-ne-mozhe-zaperechyty-napadu-putina-na-ukrajinu--jepyskop-gudzjak&catid=28:tema-nomera-cat&Itemid=2',4942,NULL,1,1,26),
(5401,'P','2015-02-26',1,NULL,'Rss','Золоті думки Блаженнішого Любомира','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6425,200,huzar_sobor_13.jpg\" />Сьогодні, 26 лютого, з нагоди Дня народження Архиєпископа-емерита Блаженнішого Любомира (Гузара), публікуємо вісімнадцять цитат із його промов, звернень та інтерв\'ю. Запрошуємо вас ділитися із іншими мудрістю цих слів, а також сьогодні по-особливому згадати у своїх молитвах Блаженнішого Любомира!</strong><strong></strong></p>\n<p>1.&nbsp;<strong>Що то значить – гарно молитися за Україну?</strong> Чи то ми маємо диктувати Богу, як Він має розв’язувати наші проблеми? Ні. Я думаю, так відчуваю, що ми мусимо Йому поручати з повним довір’ям, що Він провадить нас до добра, а ми повинні, сповняючи Його волю, конкретно здійснювати її в нашому і загальному житті.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2649:zoloti-dumky-blazhennishogo-ljubomyra&catid=27:gk-v-ukrajini-cat&Itemid=57',4943,NULL,1,1,26),
(5402,'P','2015-02-27',1,NULL,'Rss','Бунт українського вола','<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6426,200,marynovych_4.jpg\" />Розмова з віце-ректором Українського католицького університету і членом ініціативної групи «Першого грудня» Мирославом Мариновичем про Революцію гідності: як Майдан змінив українців&nbsp;і чи є шанси на те, що він стане великою історичною подією для всієї України?</em></p>\n<p><strong>«І в останню мить Господь кидає сірничку…»</strong>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2650:bunt-ukrajinskogo-vola&catid=37:spilnota-cat&Itemid=49',4944,NULL,1,1,26),
(5403,'P','2015-02-27',1,NULL,'Rss','Ми можемо перемогти, але у чому полягає наша перемога?','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6427,200,465645.jpg\" />Часто чуємо клич «Ідемо вперед у краще майбутнє». Та чи справді рухаємося вперед?</strong></p>\n<p>Старожили, які пам’ятають минулі часи, кажуть, що, властиво, мало що змінюється.Справді? Може, так. Але чому?</p>\n<p>Бо хочемо рухатися вперед, але йдемо задивлені в минуле. У народі такий спосіб переміщення описують словами: ходити вперед назадгузь. Згадую ці мовні звороти, бо вони тепер актуальні, хоча, можливо підсвідомо, замовчувані.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2651:my-mozhemo-peremogty-ale-u-chomu-poljagaje-nasha-peremoga&catid=28:tema-nomera-cat&Itemid=2',4945,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5404,'P','2015-03-01',1,NULL,'Rss','А ви пам\'ятаєте, як виглядає весна?','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6428,200,ce3edfe4678f5601.jpg\" />Коли приходить весна, забувається холод лютого. Навіть, якщо зима ще на трохи залишиться, навіть, якщо ще трошки посипле снігом... Рубіж до тепла перейдено. Незворотньо.</strong></p>\n<p>Знаєте, що я у весні люблю найдужче? Момент, коли зелень ще не вибухнула рясними бруньками, але ти вже знаєш, що це скоро настане. Що зазеленіє трава, листя, кущі та дерева, плющі й живоплоти... Кожна гілочка пульсує життям... Ти ходиш поруч поміж ними і розумієш, що от-от все довкола забуяє з неймовірною силою!</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2652:a-vy-pamjatajete-jak-vygljadaje-vesna&catid=28:tema-nomera-cat&Itemid=2',4946,NULL,1,1,26),
(5405,'P','2015-03-02',1,NULL,'Rss','БФ «Карітас-Київ» інвалідам та людям літнього віку надав засоби реабілітації','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6429,200,1.jpg\" />«Інваліди та люди літнього віку, які потребують засобів реабілітації і засобів пересування отримали одно та чотириопорні палички, ходунки, підлокітні та пахові милиці, інвалідні візки. Разом – це 315&nbsp;шт. Така можливість виникла завдяки багатьом благодійникам з України та з поза її меж, які допомогли коштами, або ж і самими засобами реабілітації», - повідомляє о. Роман Сиротич, директор БФ «Карітас Київ».</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2653:2015-03-02-21-02-57&catid=29:propovidi-cat&Itemid=34',4947,NULL,1,1,26),
(5406,'P','2015-03-03',1,NULL,'Rss','Невгамовний греко-католик з Донецька','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6430,200,zapisati_289.jpg\" />Якби тільки хто знав, що за дискусії ми вели у Фейсбуці із донецьким священиком Тихоном Кульбакою у часи, коли і Майдан, і АТО не могли навіть у сні примаритись! Сперечались. Гнівались. Він знаходив варіанти навчити розуму, даючи посилання на мудрі трактати.<o:p></o:p></p>\n<p>А потім, у 2013, був молитовний міжконфесійний Майдан на центральній площі Донецька. Гнаний, битий, руйнований і щораз&nbsp;відроджуваний священиками різних конфесій. Їм усім не бракувало мужності об’єднатись і стояти, протестуючи проти бандитів і режиму Януковича у самому лігві антинародного заколоту. Серед тих, то там стояв був – і отець греко-католик Кульбака.<o:p></o:p></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2654:nevgamovny-greko-katolyk-z-donecka&catid=27:gk-v-ukrajini-cat&Itemid=57',4948,NULL,1,1,26),
(5407,'P','2015-03-04',1,NULL,'Rss','Нації виходять з кризи завдяки культурі, релігії, ідеї державності та національних інте­ресів','<p><strong><em><img src=\"http://askoldova-mohyla.org/uk/image,6431,200,20c2e-0000000002.jpg\" style=\"margin: 10px; float: left;\" />Актуально сьогодні як ніколи</em></strong></p>\n<p>Нація — це історична особистість. Етнос перетворюється на націю тоді, коли стає суб’єктом історичного розвитку. І Україна саме в XVII столітті, коли формується українська нація, стає суб’єктом світової історії. Козаки були головною військовою силою Європи в захисті проти турків. Майже при всіх європейських дворах в якості військової сили і охорони були козаки.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2655:2015-03-04-12-45-59&catid=28:tema-nomera-cat&Itemid=2',4949,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5408,'P','2015-03-04',1,NULL,'Rss','Пам’ятник Митрополиту','<p><img src=\"http://askoldova-mohyla.org/uk/image,6432,200,1036361.jpg\" style=\"margin: 10px; float: right;\" />У листопаді минулого року в УГКЦ розпочався Рік Митрополита Андрея Шептицького. В історію Українського Народу він вписався не тільки як провідник Церкви у нелегкі часи, а також як його провідник і великий меценат. Його заслуги перед нашим Народом є такі великі, щоб їх усіх годі охопити одним поглядом. Його місію супроти Українського Народу досить часто прирівнюється до тієї, яку довелося сповнювати старозавітному патріарху Мойсею. Через це Митрололита досить часто називають Українським Мойсеєм.</p>\n<p>І саме тепер у дуже непрості часи, які переживає наш Народ, постать Митрополита Андрея є не те що важливою, але дуже потрібною, оскільки він показує нам, як належить усім нам ці часи переживати, щоб зберегти свою гідність та ідентичність.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2656:pamjatnyk-mytropolytu&catid=27:gk-v-ukrajini-cat&Itemid=57',4950,NULL,1,1,26),
(5409,'P','2015-03-04',1,NULL,'Note','ПОЖЕРТВИ ПАРАФІЯН ЦЕРКВИ СВ. МИКОЛАЯ ЧУДОТВОРЦЯ НА АСКОЛЬДОВІЙ МОГИЛІ У СІЧНІ 2015 Р. Б.','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Милостиню роби згідно з тим, що маєш; Коли у тебе достатків повно, Роби з них, а коли мало маєш, то не бійся з того дати. Бо так готуєш собі добрий скарб На день потреби. &nbsp;<strong> <em>&nbsp; &nbsp; &nbsp;&nbsp;</em></strong><strong>Товит 4. 8-9.</strong></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</em></strong></span></p>\r\n<table style=\"width: 482px;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>№ з/п</strong></span></p>\r\n</td>\r\n<td width=\"227\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Ж Е Р Т В О Д А В Ц І</strong></span></p>\r\n</td>\r\n<td width=\"217\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Пожертва на (для, до):</strong></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">1</span></p>\r\n</td>\r\n<td width=\"227\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Лариса та Катерина</span></p>\r\n</td>\r\n<td width=\"217\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">2</span></p>\r\n</td>\r\n<td width=\"227\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Юрій та Галина Ховалко</span></p>\r\n</td>\r\n<td width=\"217\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">3</span></p>\r\n</td>\r\n<td width=\"227\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Валентина</span></p>\r\n</td>\r\n<td width=\"217\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Церкву (оздоровлення Ореста)</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">4</span></p>\r\n</td>\r\n<td width=\"227\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Андрій, Ліна, Кирило, Олександра та Вікторія</span></p>\r\n</td>\r\n<td width=\"217\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">5</span></p>\r\n</td>\r\n<td width=\"227\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Любов</span></p>\r\n</td>\r\n<td width=\"217\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Десятина</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">6</span></p>\r\n</td>\r\n<td width=\"227\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Катерина Ющенко</span></p>\r\n</td>\r\n<td width=\"217\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Коляду</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">7</span></p>\r\n</td>\r\n<td width=\"227\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Родина Курило</span></p>\r\n</td>\r\n<td width=\"217\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">8</span></p>\r\n</td>\r\n<td width=\"227\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Валерій та Стефанія Захаревич</span></p>\r\n</td>\r\n<td width=\"217\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">9</span></p>\r\n</td>\r\n<td width=\"227\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ольга</span></p>\r\n</td>\r\n<td width=\"217\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Коляду</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">10</span></p>\r\n</td>\r\n<td width=\"227\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Антон, Ольга та Василь</span></p>\r\n</td>\r\n<td width=\"217\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">11</span></p>\r\n</td>\r\n<td width=\"227\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Володимир та Романна Левицькі</span></p>\r\n</td>\r\n<td width=\"217\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">12</span></p>\r\n</td>\r\n<td width=\"227\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Родина Підгородецьких</span></p>\r\n</td>\r\n<td width=\"217\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дзвіницю</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">13</span></p>\r\n</td>\r\n<td width=\"227\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Зоя</span></p>\r\n</td>\r\n<td width=\"217\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Десятина</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">14</span></p>\r\n</td>\r\n<td width=\"227\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Родина Дмитровських</span></p>\r\n</td>\r\n<td width=\"217\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дзвіницю (коляда)</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">15</span></p>\r\n</td>\r\n<td width=\"227\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Зеновій та Оксана Костик</span></p>\r\n</td>\r\n<td width=\"217\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">16</span></p>\r\n</td>\r\n<td width=\"227\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Світлана</span></p>\r\n</td>\r\n<td width=\"217\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Потреби церкви</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">17</span></p>\r\n</td>\r\n<td width=\"227\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Аскольд</span></p>\r\n</td>\r\n<td width=\"217\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Потреб крипти</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18</span></p>\r\n</td>\r\n<td width=\"227\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Сергій та Ярослава</span></p>\r\n</td>\r\n<td width=\"217\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Коляда</span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>Милостиню роби згідно з тим, що маєш; Коли у тебе достатків повно, Роби з них, а коли мало маєш, то не бійся з того дати. Бо так готуєш собі добрий скарб На день потреби. &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </em></strong><strong>Товит 4. 8-9.</strong></span></p>',NULL,4951,5,1,NULL,19);
