INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5453,'P','2015-03-30',1,NULL,'Rss','Педагог - свідок надії і справедливості','<p><img src=\"http://askoldova-mohyla.org/uk/image,6473,200,4.JPG\" style=\"margin: 10px; float: right;\" />Двадцять восьмого березня цього року були &nbsp;проведені реколекії&nbsp; на тему, яка є дуже актуальною: «Педагог сьогодні свідок надії і справедливості».&nbsp; Катехетична наука була зорганізована за допомогою Центру академічного душпастирства&nbsp; КА УГКЦ – голова отець Максим Рябуха, Комісії у справах освіти і виховання УГКЦ, голова якої є Христофора Бурштин &nbsp;та Катехетичною школою на Аскольдовій могилі, координатор – сестра Філомена Чворсюк.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=2684:pedagog-svidok-nadiji-i-spravedlyvosti&catid=25:parafija-cat&Itemid=27',4995,NULL,1,1,26),
(5454,'P','2015-04-03',0,NULL,'Note','Великопісні реколекції - ПОКАЯННЯ','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em><br />П&rsquo;ятниця 3 квітня</em></strong>: 17:30 &ndash; Літургія Передосвячених дарів.&nbsp;<strong><em>Перша реколекційна наука.</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>Субота 4 квітня:</em></strong>&nbsp;8:00 &ndash; Утреня. 9:00 &ndash; Літургія Святого&nbsp; Івана Злотоустого.&nbsp;<strong><em>Друга реколекційна наука.</em></strong>&nbsp;(катехитичний центр)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">17:00 &ndash; Вечірня. Бібілійне коло &ndash;&nbsp;<strong><em>третя реколекційна наука</em></strong>.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6479,200,1e3872ebe4e1d2ddfcc76e7302365689.jpg\" alt=\"\" width=\"141\" height=\"200\" />Реколекції проводить ректор&nbsp; Київської Трьохсвятительської</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">духовної семінарії&nbsp; УГКЦ - прот. Петро Жук</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Розклад реколекцій:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>П&rsquo;ятниця 3 квітня</em></strong>: 17:30 &ndash; Літургія Передосвячених дарів.&nbsp;</span><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\"><em>Перша реколекційна наука.</em></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>Субота 4 квітня:</em></strong> 8:00 &ndash; Утреня. 9:00 &ndash; Літургія Святого&nbsp; Івана Злотоустого.&nbsp;</span><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\"><em>Друга реколекційна наука.</em></strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\"> (катехитичний центр)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">17:00 &ndash; Вечірня. Бібілійне коло &ndash; <strong><em>третя реколекційна наука</em></strong>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>Неділя 5 квітня: &nbsp;</em></strong>8:15, 10:00, 12:30 &ndash;Літургія Василія Великого.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Проповідь реколектанта&nbsp; на кожній Літургії.</strong></span></p>',NULL,4996,5,1,NULL,41);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5455,'P','2015-03-30',1,NULL,'Rss','Зимовий відпочинок у весняні дні!','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6479,200,20150324_084723[1].jpg\" />Цьогоріч традиційний зимовий відпочинок парафіян церкви св. Миколая Чудотворця на Аскольдовій могилі був вдало перенесений на початок весняних&nbsp; канікул у школах (20.03.-25.03.2015 р.) &nbsp;&nbsp;Ще в п»ятницю 20 березня діти сиділи за партами, а уже по обіді – в потязі прямували до Закарпаття. &nbsp;18 годин у дорозі&nbsp; і… наче чарівна паличка перенесла нас із весняного Києва на засніжену гору Драгобрат.&nbsp; Справжній сніг, легкий морозець, чудовий краєвид, задимлений туманом, яскраве сонечко…&nbsp; Здавалось, наче гори з»єднані з небом. Дивишся на цю природу і переконуєшся: «Який талановитий творець - художник створив цей світ!»</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2687:zymovy-vidpochynok-u-vesnjani-dni&catid=25:parafija-cat&Itemid=27',4999,NULL,1,1,26),
(5456,'P','2015-03-31',1,NULL,'Rss','Пастирське послання Блаженнішого Святослава до священослужителів УГКЦ на Великий і Страсний четвер','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6482,200,intronizatsiya.jpg\" />Христос посеред нас – і є, і буде! Всесвітліші, всечесні та преподобні отці - дорогі співбрати в Христовому священстві! Любі брати-семінаристи!</strong></p>\n<p>Ось ми знову празнуємо Великий і Страсний четвер, коли наш Божественний Спаситель на Тайній вечері передав апостолам Безкровну жертву Нового Завіту, покликання чинити спомин Його спасительної смерті й воскресіння, і тим самим встановив святе таїнство Священства. Наша священича ідентичність обумовлена цієї подією.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2688:pastyrske-poslannja-blazhennishogo-svjatoslava-do-svjashchenosluzhyteliv-ugkc-na-velyky-i-strasny-chetver&catid=27:gk-v-ukrajini-cat&Itemid=57',5000,NULL,1,1,26),
(5457,'P','2015-03-31',1,NULL,'Rss','Щирість в наш час','<p><img src=\"http://askoldova-mohyla.org/uk/image,6483,200,4646.jpg\" style=\"margin: 10px; float: left;\" />Володимир (37 років), м. Дніпродзержинськ. На війну потрапив під час третьої мобілізації, воював під Дебальцевим, в оточенні пробув 45 днів.</p>\n<p>Згадка про військові будні: кожні 2 години чергування, вільний час, сон, і так щодня. Їли зазвичай сухпайок, (який їм привозили волонтери), не було можливості готувати їжу.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=2689:shchyrist-v-nash-chas&catid=29:propovidi-cat&Itemid=34',5001,NULL,1,1,26),
(5458,'P','2015-05-23',0,NULL,'Note','Шаховий турнір « Ми діти Аскольда»','<ol>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6485,200,shahmats2.jpg\" alt=\"\" width=\"200\" height=\"133\" />Мета та завдання</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Шахові змагання проводяться з метою популяризації шахів, налагодження дружних відносин між шахістами, вдосконалення спортивної майстерності і пізнання християнської історії Руси-України і постаті першого христитеся Аскольда-Миколая царя і мученика за віру христову та визначення переможців та призерів змагань.</span></p>','<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,6485,200,shahmats2.jpg\" alt=\"\" width=\"200\" height=\"133\" /></strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>ЗАГАЛЬНЕ ПОЛОЖЕННЯ</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">про проведення шахових турнірів</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Назва турнірів &laquo; Ми діти Аскольда&raquo;</span></p>\r\n<p><span style=\"text-decoration: underline; font-family: arial, helvetica, sans-serif; font-size: small;\">Мета та завдання</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Шахові змагання проводяться з метою популяризації шахів, налагодження дружних відносин між шахістами, вдосконалення спортивної майстерності і пізнання християнської історії Руси-України і постаті першого христитеся Аскольда-Миколая царя і мученика за віру христову та визначення переможців та призерів змагань.</span></p>\r\n<p><span style=\"text-decoration: underline; font-family: arial, helvetica, sans-serif; font-size: small;\">Час і місце проведення змагань</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Шахові змагання проводяться в сім етапів: півфінали &ndash; 5, 19, 26 квітня, 3, 10, 17 травня і фінал 24 травня за адресом &ndash; м. Київ вул. Паркова дорога Церква св. Миколая на Аскольдовій горі біля метро Арсенальна.</span></p>\r\n<p><span style=\"text-decoration: underline; font-family: arial, helvetica, sans-serif; font-size: small;\">Система проведення змагань</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Шахові змагання I-го &ndash; VI-го півфіналів відбуваються за швейцарською системою у 7 турів. Контроль часу &ndash; 15 хв. на партію кожному учаснику. При собі мати комплект шахів. Кількість місць в кожному півфіналі обмежена &ndash; 18 чоловік.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">5 квітня (Вербна неділя), 19, 26 квітня, 3, 10, 17 травня &ndash; півфінальні змагання</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Реєстрація півфінальних змагань&nbsp;<strong>&ndash;</strong>&nbsp;10.00-10.20</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Змагаються по швейцарській системі в 7 турів.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Контроль часу 15 хв на партію кожному учаснику</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Регламент змагань</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>1 тур &ndash; 10.30 &ndash; 11.00</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>2 тур &ndash; 11.10 &ndash; 11.40</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>3 тур &ndash; 11.50 &ndash; 12.20</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>4 тур &ndash; 12.30 &ndash; 13.00</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>5 тур &ndash; 13.10 &ndash; 13.40</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>6 тур &ndash; 13.50 &ndash; 14.20</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>7 тур &ndash; 14.30 &ndash; 15.00</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Закриття &ndash; 15.00 &ndash; 15.15</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Фінальні змагання проводяться по коловій системі. Контроль часу &ndash; 10 хв на партію кожному учаснику. У фінальних змаганнях приймають участь 12 юних шахістів &ndash; по 2 з кожного півфіналу.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><span style=\"text-decoration: underline;\">24 травня &ndash; фінал</span></strong><strong>&nbsp;проводиться за коловою системою.</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Початок змагання 24 травня о 10.00</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Регламент змагань</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>1 тур &ndash; 10.00 &ndash; 10.20</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>2 тур &ndash; 10.30 &ndash; 10.50</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>3 тур &ndash; 11.00 &ndash; 11.20</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>4 тур &ndash; 11.30 &ndash; 11.50</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>5 тур &ndash; 12.00 &ndash; 12.20</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>6 тур &ndash; 12.30 &ndash; 12.50</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>7 тур &ndash; 13.00 &ndash; 13.20</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>8 тур &ndash; 13.30 &ndash; 13.50</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>9 тур &ndash; 14.00 &ndash; 14.20</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>10 тур &ndash; 14.30 &ndash; 14.50</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>11 тур &ndash; 15.00 &ndash; 15.20</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Закриття &ndash; 15.30 &ndash; 15.45</strong></span></p>\r\n<p><span style=\"text-decoration: underline; font-family: arial, helvetica, sans-serif; font-size: small;\">Учасники змагань</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">До участі в турнірах допускаються всі бажаючі віком від 9 до 13 років, що мають 2 спортивний розряд і вище. Учасникам змагань дозволено виступати тільки в одному півфіналі.</span></p>\r\n<p><span style=\"text-decoration: underline; font-family: arial, helvetica, sans-serif; font-size: small;\">Визначення переможців</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Місця учасників визначаються за найбільшою кількістю набраних очок. У разі однакової кількості набраних очок у двох або більше учасників змагань місця визначаються:</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; I.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; У турнірах за швейцарською системою</span></p>\r\n<ul>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">за системою коефіцієнтів Бухгольца;</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">за системою прогресуючих коефіцієнтів;</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">за результатом особистої зустрічі.</span></li>\r\n</ul>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; II.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; У турнірі за коловою системою:</span></p>\r\n<ul>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-за системою коефіцієнтів Бергера;</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">за результатом особистої зустрічі;</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">за кількістю перемог;</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">за результатом тайм-брейку: бліц по 5 хвилин з 2-х партій (за рахунку 1:1 &ndash; до першої перемоги).</span></li>\r\n</ul>\r\n<p><span style=\"text-decoration: underline; font-family: arial, helvetica, sans-serif; font-size: small;\">Нагородження</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Призовий фонд фінального шахового турніру МИ ДІТИ АСКОЛЬДА</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">1 місце &ndash; 5000 гр</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">2 місце &ndash; 3000 гр</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">3 місце &ndash; 2000 гр</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Спонсор турніру &ndash; Благодійний фонд &laquo;ВОЇНИ ДОБРА&raquo;.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Головний суддя змагань &ndash; Кольба Андрій Богданович.</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Реєстрація на шахові турніри відбувається через електронну пошту <a href=\"mailto:Kolbaandrii@gmail.com\"><strong>Kolbaandrii@gmail.com</strong></a>.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Потрібно вказати:&nbsp;1) прізвище та ім&rsquo;я учасника змагань, 2) число місяць рік народження, 3) розряд,&nbsp;4) рейтинг, якщо є, 5) дата участі у півфіналі, 6) шаховий клуб, 7) прізвище ім&rsquo;я тренера, 8) контактний телефон. Реєстрація на кожен півфінал закінчується за 12 год до початку змагань.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,6485,200,Routenia_logo_ban.jpg\" alt=\"\" width=\"200\" height=\"112\" />Організатор:</strong>&nbsp;Парафія Святого Миколая Мирлікійського на Аскольдовій Могилі</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Відповідальний:&nbsp;</strong>Андрій &nbsp;Кольба +380974104678</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Турнір проводиться за підтримки компанії:</strong>&nbsp;&laquo;Рутенья Тур&raquo;</span></p>',NULL,5002,5,1,NULL,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5459,'P','2015-04-01',1,NULL,'Rss','Жанна Дарк','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6484,200,94399.jpg\" />Не було в світі іншої такої славної дівчини, як св. Жанна д\'Арк. У 1431 році її як чарівницю засудили на смерть і спалили живцем, а 1920 року Христовий Намісник цю селянську неписьменну дівчину проголосив Святою і навколішках просив її: \"Свята Жанно, молися за нас!\"</p>\n<p>Народилася вона 1412 року в французькому селі Домремі у сім\'ї небагатих хліборобів Якова й Ісабелі д\'Арк. У цій родині було п\'ятеро дітей. Батьки Жанни були побожні, призвичаєні до тяжкої праці на полі. Дівчина під дбайливою опікою матері навчилася любити Бога і молитися, ходити до церкви і часто приймати св. Тайни. Вміла також гарно прясти й шити. А від батька вона навчалася хліборобської праці; також пасла овець.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2690:zhanna-dark&catid=35:istorychni-postati-cat&Itemid=48',5003,NULL,1,1,26),
(5460,'P','2015-04-04',1,NULL,'Rss','Вербна неділя і Страсний тиждень','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6490,200,21230-0.jpg\" />Неділя за тиждень перед Великоднем називається \"Вербною\", \"шутковою\" або \"цвітною\". З давніх давен у Закарпатті цей день чекали з особливим нетерпінням.</p>\n<p>Народ сходився до церкви, священик освячував вербу. За християнським вченням у цей час Спаситель Христос уїжджав до Єрусалиму на ослику, і миряни устеляли перед ним дорогу пальмовим листям. Оскільки в Україні і Закарпатті пальми не ростуть, то їх замінили вербовими гілками.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2696:verbna-nedilja-i-strasny-tyzhden&catid=28:tema-nomera-cat&Itemid=2',5009,NULL,1,1,26),
(5461,'P','2015-04-01',1,NULL,'Rss','STOP- тоталітарним режимам в Україні','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6484,200,44654.jpg\" />Чотири законопроекти про засудження комуністичного та нацистського тоталітарних режимів, відкриття архівів та вшанування пам\'яті борців за незалежність України 31 березня схвалили на засіданні Уряду.</strong></p>\n<p>Проекти розроблені Українським інститутом національної пам\'яті спільно з громадськістю. Йдеться про законопроекти «Про увічнення перемоги над нацизмом в Другій світовій війні 1939–1945», «Про доступ до архівів репресивних органів комуністичного тоталітарного режиму 1917–1991 років», «Про засудження комуністичного та націонал-соціалістичного (нацистського) тоталітарних режимів в Україні та заборону пропаганди їхньої символіки», «Про правовий статус та вшанування пам\'яті учасників боротьби за незалежність України у ХХ столітті».</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2691:stop-totalitarnym-rezhymam-v-ukrajini&catid=37:spilnota-cat&Itemid=49',5004,NULL,1,1,26),
(5462,'P','2015-04-01',1,NULL,'Rss','«Це перший такий музичний задум в Україні»','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6484,200,45654.jpg\" />У Мережі з’явився відеоролик із записом незвичного твору&nbsp;— кантати. Його підготували і виклали віряни та працівники київської парафії св.&nbsp;Олександра.</strong></p>\n<p><a href=\"https://www.youtube.com/watch?t=71&amp;v=x3gna_RSjms\">https://www.youtube.com/watch?t=71&amp;v=x3gna_RSjms</a></p>\n<p>Композитор — Віталій Казнодій, зараз органіст у храмі св.&nbsp;Олександра, а його дружина Світлана&nbsp;— регент хору.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2692:2015-04-01-19-09-00&catid=27:gk-v-ukrajini-cat&Itemid=57',5005,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5463,'P','2015-04-02',1,NULL,'Rss','Сьогодні десята річниця смерті святого Папи Івана Павла ІІ','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6488,200,papa.jpg\" />Десять років тому, 2 квітня 2005 року, увесь світ охопила звістка про смерть Папи Івана Павла II, яку оприлюднив архиєпископ Леонардо Сандрі на площі Святого Петра. Він повідомив, що о 21 годині 37 хвилин «...наш Святіший Отець повернувся в дім Отця». Це було навечір\' я празника Божого Милосердя.</strong></p>\n<p>Десятки тисяч віруючих зібралися під вікнами папських апартаментів у Ватикані. Уперше в історії з Папою прощалися спонтанними оплесками, які, як знак поваги і любові до Івана Павла ІІ, лунали і в день його похорону, на який зібралися президенти, прем\'єр-міністри, королі й князі, релігійні лідери з усього світу та кілька мільйонів віруючих. Після похоронних богослужінь Папа Римський Іван Павло II був похований у базиліці Святого Петра у Ватикані.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=2693:sogodni-desjata-richnycja-smerti-svjatogo-papy-ivana-pavla-ii&catid=28:tema-nomera-cat&Itemid=2',5006,NULL,1,1,26),
(5464,'P','2015-04-02',1,NULL,'Rss','Блаженний Священномученик Миколай Чарнецький - 2 квітня день спочину','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6488,200,30-08-13-2.jpg\" />ХХ століття - час народження східної (візантійської) вітки Згромадження редемптористів. Прийшовши на українські землі, бельгійські редемптористи спромоглися на найбільше - вони змогли стати частинкою народу, змогли влитися в його культуру, ментальність, та, що найважливіше, у його східну духовність. Палаючи духом за Господа сил, вони вели за собою тих, котрі чули у своєму серці Христовий голос: \"Ідіть і зробіть учнями всі народи.\" (Мт.28,19). Серед цих перших синів української землі, що вирішили йти і виконувати волю свого Спасителя, був великий муж святого життя - блаженний єпископ Миколай Чарнецький.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2694:blazhenny-svjashchennomuchenyk-mykola-charnecky-2-kvitnja-den-spochynu&catid=35:istorychni-postati-cat&Itemid=48',5007,NULL,1,1,26),
(5465,'P','2015-04-03',1,NULL,'Rss','Святкування лазаревої суботи','<p><em><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6490,200,lazare.jpg\" />\"Голос Твій, Господи, знищив царство аду, і слово Твоєї власти піднесло з гробу чотириденного. І став Лазар прообразом спасенним воскресення... \"</strong></em><strong> </strong><em><strong>(Стихира вечірні Лазаревої суботи).</strong></em></p>\n<p>Майже кожний тиждень Великого посту має свої богослужбові особливості.&nbsp; Треба передовсім сказати про суботи й неділі, кожна з яких присвячена&nbsp; якійсь події з життя Церкви, Ісуса Христа, його Пресвятої Матері або&nbsp; святих.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2695:svjatkuvannja-lazarevoji-suboty&catid=28:tema-nomera-cat&Itemid=2',5008,NULL,1,1,26),
(5466,'P','2015-04-12',0,NULL,'Note','Розклад Богослужінь на Страсний тиждень Великого посту 2015р.Б','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>12</strong><strong>&nbsp;квітня. Великдень Воскресіння Христове</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">00:00&nbsp;<strong>пасхальна Утрення зі Службою Божою</strong>&nbsp;і освяченням пасхальної трапези</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">07:30 Божественна Літургія, освячення пасок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>10:00, 10:30, 11:00, 11:30, 12:00, 12:30</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>16:00&nbsp;&nbsp;&nbsp;Гаївки на Софіївській площі</strong></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>6 квітня. Великий понеділок</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08:00 Літургія Передосвяченіх Дарів, Парастас</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">17:00 Велике повечіря,&nbsp;Cповідь</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>18</strong>&nbsp;<strong>:00 розпочнеться Чин Соборування</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>7 квітня. Великий вівторок&nbsp;</strong><strong>Благовіщення Пресв.Богородиці</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08:00 Утрення,Літургія Івана Золотоустого,Молебень Богородиці</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18:00 Літургія Івана Золотоустого ,&nbsp;Cповідь</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>8квітня. Велика середа</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08:00 Літургія Передосвяченіх Дарів, Парастас</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">17:00 Хресна дорога від Дніпра за участю молоді парафії .</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18:00 Літургія Передосвяченіх Дарів,&nbsp;Cповідь&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>9</strong><strong>&nbsp;квітня.</strong><strong>&nbsp;</strong><strong>Великий четвер</strong>&nbsp;, Страсті ,спомин тайної вечері</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08:00 Вечірня з Літургією Василія Великого</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18:00 Страсна утреня</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>&nbsp;10</strong><strong>&nbsp;квітня.</strong><strong>&nbsp;</strong><strong>Велика п\'ятниця</strong>&nbsp;,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08:00 Великі царські часи.Літургія не правиться</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">17:00 Вечірня з виставленням Плащаниці/ Єрусалимська утреня</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;<strong>Церква буде Відкрита цілу ніч, Запрошуємо на молитву біля Гробу Господнього</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>&nbsp;11</strong><strong>&nbsp;квітня.</strong><strong>&nbsp;</strong><strong>Велика субота</strong>&nbsp;,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08:00 Вечірня з Літургією Василя Великого</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>18:00, 19:00, 20:00, 21:00, 22:00 Освячення пасок</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">23:30 надгробне</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>12</strong><strong>&nbsp;квітня. Великдень Воскресіння Христове</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">00:00 <strong>пасхальна Утрення зі Службою Божою</strong> і освяченням пасхальної трапези</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">07:30 Божественна Літургія, освячення пасок</span></p>\r\n<p style=\"text-align: justify;\"><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">10:00, 10:30, 11:00, 11:30, 12:00, 12:30</strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>16:00&nbsp;&nbsp;&nbsp;Гаївки на Софіївській площі</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Світлий тиждень 12 квітня-18 квітня 2014р.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">0<strong>8:00 Воскресна Утрення, 9:00 Служба Божа</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>18:00 Вечірня, Служба Божа</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">телефон для довідок<strong>&nbsp;(050) 447&nbsp;</strong><strong>31 57</strong></span></p>',NULL,5010,5,1,NULL,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5467,'P','2015-04-04',1,NULL,'Rss','Месіанський В’їзд Ісуса в Єрусалим (роздуми на канві Квітної Неділі)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6492,200,erusalim.jpg\" />Події Месіанського в’їзду Христа в Єрусалим освітлюють нам усі чотири євангелисти: Матей (Мт. 21,1–11); Марко (Мк. 11,1–11); Лука (Лк. 19,28–40); Іван (12,12–19). Кожен з них подає цю подію історії Спасіння під своїм кутом бачення, вносячи певні деталі, на які інші автори не звернули такої уваги.</p>\n<p>Усі вони однозгідні, що шлях Ісуса в Єрусалим пролягав через Оливну гору, ту саму гору, на якій за кілька днів у Гетсиманському саду Ісус буде молити Отця про чашу і де Його через зраду Юди заарештують (Мт. 26,36–56// Мк. 14,32–52// Лк. 22,39–53//&nbsp; Ів.18,1–11). Ця гора наприкінці часів розколеться надвоє, бо Бог стоятиме на ній (див. Зах. 14–1–11). Розколеться, щоб пропустити живу воду з Єрусалиму до Мертвого моря і так оздоровити його (див. Ез.47,1–12). Про цю воду і потік сповіщає книга Одкровення (див. Одкр. 22,1–2).</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2698:mesiansky-vjizd-isusa-v-jerusalym-rozdumy-na-kanvi-kvitnoji-nedili&catid=28:tema-nomera-cat&Itemid=2',5011,NULL,1,1,26),
(5468,'P','2015-04-04',1,NULL,'Note','ЗВІТ №5 про роботу служби милосердя «Карітас» при парафії св.Миколая Чудотворця на Аскольдовій могилі (лютий 2015 р.)','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6495,200,P1000734.JPG\" alt=\"\" width=\"200\" height=\"150\" />Крім того, відправлено посилки чотирьом родинам переселенців із зони АТО. Посилки укомплектувала волонтер п.Ірина Макар, на пошту доправив волонтер, член спільноти &laquo;Лицарі Колумба&raquo; п.Петро Галуга.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">В рамках проекту тюремного служіння відправлено 8 посилок з подарунками від св.Миколая дітям, матері яких відбувають покарання в місцях позбавлення волі (див. звіт №3).</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img style=\"float: right;\" src=\"image,6495,200,P1000734.JPG\" alt=\"\" width=\"200\" height=\"150\" />Служба милосердя &laquo;Карітас&raquo; при церкві св.Миколая Чудотворця на Аскольдовій могилі, зібравши пожертвувані парафіянами одяг чоловічий, жіночий та дитячий, взуття, іграшки дитячі та отримавши гуманітарну допомогу одягом, взуттям, постіллю, кухонною та постільною білизною, продуктами від благодійного фонду &laquo;Міжнародна асоціація підтримки України (МАПУ)&raquo;, а також допомогу від БФ &laquo;Олива&raquo; одягом та меблями, розподілила їх таким чином (див.записи в книзі розподілу гуманітарної допомоги):</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Уділили потребуючим мирянам (57 осіб):</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash;200 кг</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;куртки та пальта жіночі, чоловічі й дитячі &ndash; 28 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;шуби жіночі й дитячі &ndash; 6 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;кухонна й постільна білизна &ndash; 10 кг</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;ковдри &ndash; 10 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;покривала &ndash; 2 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;взуття &ndash; 13 пар</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;стілець офісний &ndash; 1 шт.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Надали матеріальну допомогу:</span></li>\r\n</ol>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">переселенцям з Донецької та Луганської обл.(29 осіб)</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash; 87 кг.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;куртки, пальто &ndash; 7 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;взуття &ndash; 5 пар</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;постільна білизна &ndash; 6 компл.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;ковдри, покривала &ndash; 3 шт.</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">потребуючим та безпритульним (25 осіб):</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash; 83 кг.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;куртки &ndash; 9 шт.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6495,200,P1000711.JPG\" alt=\"\" width=\"200\" height=\"150\" />Відправили гуманітарний вантаж:</span></li>\r\n</ol>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Благодійному фонду &ldquo;Сузір\'я&rdquo; в с.Іванкові Бориспільського р-ну Київської обл.:</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг чоловічий, жіночий, дитячий &ndash; 53 мішки+3 коробки</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;іграшки дитячі &ndash; 1 мішок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;взуття &ndash; 1 мішок</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Благодійній організації &laquo;Фонд &ldquo;АСПЕРН&rdquo;&raquo;</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; одяг чоловічий, жіночий, дитячий, постіль, білизна постільна &ndash; 13 мішків</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Релігійній місії Римсько-католицької церкви &ldquo;Карітас-СПЕС-Київ&rdquo;:</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;куртки, пальто чоловічі &ndash; 1 коробка</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;светри чоловічі &ndash; 2 коробки</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;футболки чоловічі &ndash; 1 коробка</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Меблі (один стілець офісний та стільці передано &laquo;Карітас-Печерськ&raquo;, кухні &laquo;Карітас&raquo; передано з гуманітарної допомоги від &laquo;МАПУ&raquo; для харчування переселенців із зони АТО та потребуючих:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;цукор &ndash; 3 пачки * 1 кг</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;консерви &laquo;Ветчина&raquo; (&laquo;Сumpis&raquo;) &ndash; 5б. * 250г</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;консерви &laquo;Ветчина рубленая из свинины&raquo; &ndash; 2б. * 240г</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;консерви &laquo;Бекон&raquo; &ndash; 5б. * 240г</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;молоко згущене &ndash; 1б.*385г</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6495,200,P1000708.JPG\" alt=\"\" width=\"200\" height=\"150\" />Крім того, відправлено посилки чотирьом родинам переселенців із зони АТО. Посилки укомплектувала волонтер п.Ірина Макар, на пошту доправив волонтер, член спільноти &laquo;Лицарі Колумба&raquo; п.Петро Галуга.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">В рамках проекту тюремного служіння відправлено 8 посилок з подарунками від св.Миколая дітям, матері яких відбувають покарання в місцях позбавлення волі (див. звіт №3).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Робота виконана силами волонтерів. Висловлюємо щиру подяку жертводавцям і прикликаємо Боже благословення на їх родини.</span></p>',NULL,5012,5,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5469,'P','2015-04-04',1,NULL,'Note','Звіт №2 про НАДХОДЖЕННЯ  ЛИСТІВ Лютий 2015 р. — 13 листів','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;Шановний о.Ігор!&nbsp;<strong>Щиро вдячні, що ви турбуєтесь про дітей, матері яких не взмозі зараз турбуватися про них</strong>. В такий тяжкий для країни час ви знаходите час і кошти. Щиро вдячні. Хай Господь поблагословить Вас в Ваших турботах. З повагою сімя К. І Євген П.&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чернігівська обл., Євген-5р.,син Олени П.,&nbsp;</em></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Листи від ув\'язнених </span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Чернігівська виправна колонія ЧВК-44 (м.Чернігів) &mdash; </strong><strong>1</strong><strong> лист</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Кача</strong><strong>нівська виправна колонія </strong><strong>К</strong><strong>ВК-</strong><strong>5</strong><strong>4 (м.</strong><strong>Харк</strong><strong>ів) &mdash; </strong><strong>1</strong><strong> лист</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Збаразька виправна колонія ЗВК-63 (Тернопільська обл.) &ndash; 1 лист</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">&nbsp;</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Подяка за посилки для дітей &ndash;6 листів</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">&nbsp;</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Листи звільнених з ув</span></em></strong><strong><em><span style=\"text-decoration: underline;\">&rsquo;</span></em></strong><strong><em><span style=\"text-decoration: underline;\">язнення &ndash; 2 листи</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Листи приватної особи &ndash; 2 листи</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;&hellip; <strong>Я отримала від сина листа я дуже вдячна Господу і Вам що почули мої молитви.</strong> Пише мені син що у нього були руки сломані, но чого і як, він не описує. <strong>Отримав син посилку с Кієва но від кого він таких не знає. Я сину відправила листа і написала про Вас що Ви помогаєте</strong> і ми с сином до Вас приїдемо. Я дуже Вам вдячна і сестрі Валентині за Вашу підтримку. Написала сину за свою пільгу що от скоро буде нехай просить Господа, щоб мене звільнили. О.Ігорю у мого Славкі скоро іменини, не могли би Ви подарунок йому зробити будь ласка 25 февраля. <strong>Пишу Вам від серця, у мене даже і на конвертів грошей немає</strong>, щоб йому пару конвертів відіслати. Один у дівчат попросила і бистренько сину написала а друга <strong>дівчина дала і я Вам тоже бігом поділитися радістью що синочок отгукнувся. </strong>Хай простить мене Господь за всі мої гріхи, но я бажаю тільки одного щоб діти мої були ціли і здорові. <strong>Господь є і я дуже Йому вдячна і Вам мої дорогі</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Марина А. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;&hellip; Мне было очень приятно получить от Вас письмо на Рождество&hellip; <strong>Я попрошу Вас выслать мне книги. Хочу побольше узнать о Боге и хочу быть сним </strong>потому-што мир сним спокойней. Когда меня посадили я курила. Курила давно. Я хотела бросить я начала просить Бога помочь мне и вот я уже год не курю. Я очень благодарна Богу&hellip; <strong>Так же если конечно вас не затруднит выслать вещи личной гигиены</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Тетяна К. (Качанівська ВК, м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;&hellip; ми з великою радістю колядували, прославляли новонародженого Богомладенця, дарували подарунки. А на св.Водохрещення приїзжав отець Володимир освятив водичку і приміщення де ми мешкаємо. Слава Богу за все. <strong>Ложу Вам в подарунок маленьку ялиночку (<em>вишиту</em>. Хочу попросити Вас допомогти мені медикаментами, звичайно по можливості. І ще мені писали що вишлете схему (образ) для вишивання.</strong> Дуже хочу одержати. Чекаємо Пасху, дуже чекаємо як завжди. <strong>Доброго здоров</strong><strong>&rsquo;</strong><strong>я бажаю всій громаді церкві</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Любов С. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;<strong>Велика Вам подяка за те що допомагаєте</strong>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">З повагою&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; З повагою Світлана Л.&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>Світлана Л. (Дніпропетровська обл., Євген-13р., син Алли Б., Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;Добрий день отець Ігорь!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Я, та мій внучок одержав Ваші подарунки. Ми дуже вдячні.</strong> Артьомчик родився якраз на Велике свято 7.01.2011р. Це для нього був особливий подарунок. <strong>Як він підросте ми вчасно будемо їздити до Вас на службу в громаду УГКЦ Аскольдова могила</strong>. Я сама віруюча, та хлопчик ще маленький. Мені дуже тяжко з ним кудись добиратись. А мені уже на сьомий десяток. Дуже дякую я і Артемій Вам!!! &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Да Благослови Господь Вас!&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>&nbsp; Людмила Х. (Київська обл., Артем-4р., син Ольги В., Чернігівська ВК</em>)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;Доброго дня!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Хочемо подякувати Вам за вашу допомогу. Нам сподобались ваші подарунки нехай береже вас Бог.</strong> Будемо чекати від Вас листа.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; З ув.Оля&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>Волинська обл., Віктор-14р., син Олени К., Збаразька ВК, Тернопільська обл.</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;<strong>Дякуємо все получили по списку</strong>.&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>Марія Б. (Тернопільська обл., Іванна-8,5р., Марія-7р., діти Любові Д., Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>&ldquo;Спасибі Вам за допомогу!&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чернігівська обл., Орина-8р., дочка Марини А., Чернігівська ВК</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;Шановний о.Ігор! <strong>Щиро вдячні, що ви турбуєтесь про дітей, матері яких не взмозі зараз турбуватися про них</strong>. В такий тяжкий для країни час ви знаходите час і кошти. Щиро вдячні. Хай Господь поблагословить Вас в Ваших турботах. З повагою сімя К. І Євген П.&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чернігівська обл., Євген-5р.,син Олени П., Чернігівська ВК</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;Слава Иисусу Христу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дорогой отец Игорь, прошу тысячу извинений что долго не писала. Очень уж много проблем сразу свалило. Да ещё и <strong>сынуля Яков больной</strong>, ну ничего Господь меня любит. <strong>Хожу в церковь и работаю смотрю больных бабуль, это моё испытание. </strong>Взяла кредит потихоньку делаю ремонт в квартире. <strong>У меня есть внуки и малому 2 года 3 месяца он со мной молится на коленях, правда говорит только Аминь и Бог люблю. </strong>Бабушки удевлялись что такой маленький стоит на коленях. Сейчас смотрю бабулю 79 лет учительница, а молится. Не знала ну сейчас научила её молитвам. Это моё испытание и <strong>я рада каждому дню и благодарю Господа</strong>. Есть рушники вышитые ну не новые, мне хочется сделать Вам подарок ну очень неловко что не новые. Я ведь когда сидела обещала, и нет пока таких чтобы вышили мне новые. <strong>Очень Вам благодарна за поддержку и никогда такое не забуду. Бабулям читаю каждый день Библию и они рады и мне радостно.</strong> Сейчас смотрю маму прокурора и они мной довольны. Не знаю когда ну приеду к Вам. <strong>Помолитесь за Якова, Игорь, Владислав. </strong>Пишите буду очень рада!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Храни Вас Господь! Спасибо за всё!</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Світлана Ш. (звільнена з Чернігівської ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Слава Иисусу Христу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Мир Вашему дому дорогие братья и сестры. Решила обратится к Вам за помощью, т.к. <strong>я являюсь опекуном девочки-сироты, зовут ее Кира, 9 лет. Очень прошу Вас помогите, мне очень тяжело. Если сможете что в Ваших возможностях, нуждаемся в моющих средствах; шампунь, стир.порошок, мыло. Что сможете из продуктов и одежды. </strong>Заранее буду Вам благодарна. С низким поклоно к Вам сестра Светлана.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Буду за Вас молиться</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Да хранит Вас всех Господь!&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Світлана Д. (м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>&nbsp;</em></span></p>',NULL,5013,5,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5470,'P','2015-04-05',1,NULL,'Rss','Великдень у римо-католицькій традиції','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6477,200,46465.png\" />У таблиці пріоритету літургійних днів Церкви головує Трідуум (Тридення) Страстей і Воскресіння Христа, тобто три святі дні: п’ятниця, субота і неділя.</strong></p>\n<p>У&nbsp;християнстві різні течії мають свої пріоритети щодо відзначення Пасхи: протестанти наголошують на Смерті Христовій, східні християни акцентують саме недільний день, Великдень, називаючи його Святом свят і Перемогою перемог. Для католиків латинського обряду обидва ці виміри (смерть і воскресіння Господні) мають комплементарний характер, тобто доповнюються взаємно.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2699:velykden-u-rymo-katolycki-tradyciji&catid=28:tema-nomera-cat&Itemid=2',5014,NULL,1,1,26),
(5471,'P','2015-04-06',1,NULL,'Rss','Розвиваючи особисту культуру, ми будуємо нову Україну','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6497,200,78578757.jpg\" />Кілька днів тому були надруковані наші роздуми&nbsp;&nbsp;<a href=\"http://www.credo-ua.org/2015/03/131561\">«Яке значення має культура для нашого життя?»</a>. Мова йшла про загальну культуру, про все те, що становить надбання народу в ділянці краси.</p>\n<p>Такі надбання охоплюють широкий спектр людської творчості: літературу, музику, образотворче мистецтво та інші гарні речі, пізнаючи, оглядаючи та слухаючи які, ми ушляхетнюємося і дістаємо велику естетичну насолоду.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2700:rozvyvajuchy-osobystu-kulturu-my-budujemo-novu-ukrajinu&catid=28:tema-nomera-cat&Itemid=2',5015,NULL,1,1,26),
(5472,'P','2015-04-06',1,NULL,'Rss','Свята тайна Єлеопомазання','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6477,200,images.jpg\" />Свята тайна Єлеопомазання, як і тайна Покаяння (Сповіді) належить до таїнств зцілення, тобто відновлення чи помноження освячуючої ласки в душі християнина.</strong></p>\n<p>Ця тайна була установлена у Новому Завіті в часі прилюдної діяльності Христа. Ісус Христос установив тайну Єлеопомазання поручивши апостолам зціляти хворих, як про це говорить Євангеліє від Матея: «Прикликавши своїх дванадцять учнів, Ісус дав їм владу над нечистими духами, щоб їх виганяли і лікували всяку хворобу й усяку неміч». (Мт 10. 1, 8). Євангелист Марко теж пише, що Ісус «покликав дванадцятьох і заходився їх посилати по двох, даючи їм владу над нечистими духами». Апостоли «вийшовши, проповідували покаяння, і виганяли численних бісів, а й намащували олією чимало хворих та оздоровлювали» (Мр. 6. 7, 12, 13).</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2701:svjata-tana-jeleopomazannja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5016,NULL,1,1,26),
(5473,'P','2015-04-06',1,NULL,'Rss','Празник Благовіщення Пресвятої Богородиці','<p align=\"right\"><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6477,200,48-2fbe1.jpg\" />\"Архангельським голосом кличемо до Тебе, Пречиста: \"Радуйся благодатна, Господь з Тобою\" (Величання празника)</em>.</p>\n<p>\"Днесь спасення нашого начало і від віку таїнства явління: Син Божий сином Діви стається\"&nbsp;<em>(Тропар празника)</em>.&nbsp;Ось таким похвальним гімном наша Церква вітає празник Благовіщення Пресвятої Богородиці, називаючи його початком нашого спасення. І слушно, бо цей празник належить до найважливіших свят нашого церковного року. Про його важливість говорить уже сама назва в наших богослужбових книгах: \"Благовіщення Пресвятої Владичиці нашої Богородиці і Приснодіви Марії\".</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2702:praznyk-blagovishchennja-presvjatoji-bogorodyci&catid=28:tema-nomera-cat&Itemid=2',5017,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5474,'P','2015-04-06',1,NULL,'Rss','Успішний старт шахового турніру «Ми діти Аскольда» на Аскольдовій Могилі','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6500,200,5644.JPG\" />5 квітня, у Вербну Неділю, Входу Господнього в Єрусалим на Аскольдовій Могилі стартував шаховий дитячий турнір «Ми діти Аскольда»</p>\n<p>З року в рік парафія церкви Святого Миколая Мирлікійського на Аскольдовій Могилі організовує дитячі шахові турніри. Мета цього проекту дуже проста: надати можливість дітям самореалізувати себе у розумовому виді спорту і водночас знайомитись та пізнавати християнську історію Руси-України, дізнаватися про постать першого хрестителя і християнина Аскольда-Миколая царя і мученика за віру христову</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2703:2015-04-06-19-21-21&catid=25:parafija-cat&Itemid=27',5018,NULL,1,1,26),
(5475,'P','2015-04-07',1,NULL,'Rss','Хто боїться православно-католицької унії в Україні','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6501,200,image.jpg\" />Українська автокефальна православна церква (оновлена) заявила про намір об\'єднуватися з Українською греко-католицькою церквою. Ця несподівана новина викликала живе обговорення у православному середовищі</strong></p>\n<p>Звісно, архієпископа Харківського і Полтавського Ігоря Ісіченка УАПЦ звикли вважати за людину екстравагантних рішень і підозрювати у зайвих симпатіях до католиків. Але&nbsp;<a href=\"http://catholicnews.org.ua/harkivsko-poltavska-ieparhiyi-uapc-shukaie-povnoyi-iednosti-z-ugkc\">отак просто взяти і приєднатися до Унії</a>? Ні, це вже занадто.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2704:hto-bojitsja-pravoslavno-katolyckoji-uniji-v-ukrajini&catid=27:gk-v-ukrajini-cat&Itemid=57',5019,NULL,1,1,26),
(5476,'P','2015-04-08',1,NULL,'Rss','Кому заважає Шептицький?','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6501,200,images_47.jpg\" />Останнім часом, здавалося б повністю обґрунтована ініціатива Церкви із постановки пам’ятника одній із найвидатніших постатей в історії України та Української греко-католицької церкви – митрополиту Андрею Шептицькому, викликала шквал пристрастей та негативних емоцій.</p>\n<p>У цьому році відзначаємо 150-ту річницю із дня народження Митрополита, у цей же період у Ватикані планується довести до логічного завершення процес беатифікації Шептицького, котрий триває ще з середини минулого століття.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2705:komu-zavazhaje-sheptycky&catid=27:gk-v-ukrajini-cat&Itemid=57',5020,NULL,1,1,26),
(5477,'P','2015-04-08',1,NULL,'Rss','Великоднє послання Блаженнішого Cвятослава','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6503,200,zaproshennya.jpg\" /></strong>Високопреосвященним і Преосвященним владикам, всесвітлішим, всечеснішим і преподобним отцям, преподобним ченцям і черницям, дорогим у Христі мирянам Української Греко-Католицької Церкви</p>\n<p style=\"text-align: center;\">Христос воскрес!</p>\n<p style=\"text-align: right;\"><em> На божественній сторожі</em><em> </em><em>Богонатхненний Авакум хай стане з нами</em><em> </em><em>І покаже світлоносця-ангела, що ясно звіщає:</em><em> </em><em>Нині спасіння світові, бо воскрес Христос як всемогутній.</em><em> </em>Пісня 4 Канону Пасхи</p>\n<div style=\"text-align: justify;\">\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2706:velykodnje-poslannja-blazhennishogo-cvjatoslava&catid=27:gk-v-ukrajini-cat&Itemid=57',5021,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5478,'P','2015-04-08',1,NULL,'Rss','Великоднє послання Блаженнішого Cвятослава','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6503,200,zaproshennya.jpg\" />Високопреосвященним і Преосвященним владикам, всесвітлішим, всечеснішим і преподобним отцям, преподобним ченцям і черницям, дорогим у Христі мирянам Української Греко-Католицької Церкви</p>\n<p align=\"center\">Христос воскрес!</p>\n<p align=\"right\"><em> На божественній сторожі</em><em> </em><em>Богонатхненний Авакум хай стане з нами</em><em> </em><em>І покаже світлоносця-ангела, що ясно звіщає:</em><em> </em><em>Нині спасіння світові, бо воскрес Христос як всемогутній.</em><em> </em>Пісня 4 Канону Пасхи</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2707:velykodnje-poslannja-blazhennishogo-cvjatoslava&catid=28:tema-nomera-cat&Itemid=2',5022,NULL,1,1,26),
(5479,'P','2015-04-09',1,NULL,'Rss','Великий четвер. Спомин Тайної вечері','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6505,200,tajna_v.jpg\" />На п’ятий день після входу Господнього в Єрусалим православні віруючі всього світу відзначають Великий четвер, починаючи з якого протягом трьох днів всі церковні служби будуть присвячені спогадам про земні страждання Господа нашого Ісуса Христа.При цьому, сам Великий четвер, котрий іменується також Чистим, присвячується спогаду Тайної Вечері, під час якої Спаситель омив ноги Своїм учням, показавши тим самим приклад братської любові та покори.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2708:velyky-chetver-spomyn-tanoji-vecheri&catid=28:tema-nomera-cat&Itemid=2',5023,NULL,1,1,26),
(5480,'P','2015-04-10',1,NULL,'Rss','«Чувайте і моліться, щоб не ввійти у спокусу!»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6505,200,09-04-15-1.jpg\" />Сьогодні ми покликані особливо молитися за єдність Христової Церкви в Україні, бо це складова частина архиєрейської молитви Ісуса Христа, який молиться до Отця: Боже, збережи їх, щоб вони були одно, одно як Я в Тобі, а Ти в Мені.</p>\n<p>Про це сьогодні, у Великий Четвер, сказав Глава Української Греко-Католицької Церкви до вірних в Патріаршому Соборі Воскресіння Христового.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2709:2015-04-10-05-45-42&catid=28:tema-nomera-cat&Itemid=2',5024,NULL,1,1,26),
(5481,'P','2015-04-10',1,NULL,'Rss','Страсна п\'ятниця — найскорботніший день року','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6505,200,bog.jpg\" />За два дні до&nbsp;<a href=\"http://blyzhchedoboga.com.ua/svitla-pasha-voskresinnya-gospodnye-2/\">Великодня</a> християни східного обряду відзначають&nbsp;<strong>Страсну П’ятницю</strong> – найбільш скорботний день церковного календаря, коли згадують хресні страждання і смерть&nbsp;<a href=\"http://blyzhchedoboga.com.ua/isus-hristos-sin-bozhiy/\">Спасителя</a>. Саме через це кожна п’ятниця є пістним днем.</p>\n<p>Цього дня Страстної седмиці піст стає особливо суворим. За церковними канонами, не можна їсти нічого, крім води і хліба.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2710:strasna-pjatnycja--naskorbotnishy-den-roku&catid=28:tema-nomera-cat&Itemid=2',5025,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5482,'P','2015-04-10',1,NULL,'Rss','Велика п\'ятниця','<p align=\"right\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6508,200,10488204.jpg\" />\"Благообразний Йосиф, з древа знявши пречистеє тіло Твоє, плащаницею чистою обвив і ароматами, в гробі новім, покривши, положив\"<em>Тропар Великої п\'ятниці</em></p>\n<p>Богослуження Великої п\'ятниці характеризуються гарними обрядами, зворушливими напівами і глибокими змістом гимнами та стихирами. Головна їхня тема — це муки і смерть Господа нашого Ісуса Христа. \"Днесь висить на дереві, — співаємо на 15 антифоні утрені Великої п\'ятниці, — Той, що на водах землю повісив. Вінцем з терня вінчається — Цар ангелів. У ложну багряницю одягається Той, що небо одягає хмарами. Дістає поличник Той, що в Йордані висвободив Адама. Цвяхами пригвозджений — Жених церкви. Копієм проколений — Син Діви. Поклоняємось страстям Твоїм, Христе, поклоняємося страстям Твоїм, Христе, поклоняємося стра­стям Твоїм, Христе, покажи нам і славне Твоє воскресення\".</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2712:velyka-pjatnycja&catid=28:tema-nomera-cat&Itemid=2',5026,NULL,1,1,26),
(5483,'P','2015-04-11',1,NULL,'Rss','Велика Субота','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6509,200,8802.jpg\" />«Субота ця преблагословенна, бо нині мертвий Христос воскресне на третій день», — співається в кондаку Великої Суботи. Перебуваючи посередині між Хрестом і Воскресінням, Велика Субота є унікальним днем церковного року, коли плач перетворюється на радість. Це єдина субота в році, котра є днем строгого посту: Літургія (св. Василя Великого) у цей день відбувається ввечері. У тематиці цього дня сполучаються найглибший кенозис Слова Божого, «навіть у пекло зійшовшого» у пошуку занепалого Адама, і початок перемоги над пеклом і смертю.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2714:velyka-subota&catid=27:gk-v-ukrajini-cat&Itemid=57',5027,NULL,1,1,26),
(5484,'P','2015-04-11',1,NULL,'Rss','Святоюрська гора: колись, тепер, в прийдешньому','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6509,200,15_1.jpg\" />Дискусія довкола пам\'ятника митрополиту Андрею Шептицькому у Львові, яку на професійному рівні архітектори та скульптори почали перед кількома роками —&nbsp;на стадії проведення конкурсу, —&nbsp;останнім часом переросла у громадське протистояння довкола збереження скверу на площі Святого Юра.</strong></p>\n<p>У цих суперечках дуже мало згадується історико-архітектурний контекст вирішення містобудівної задачі вибору місця для пам\'ятника, а саме:&nbsp;утворення та розвиток площі Святого Юра. Принагідно нагадаю, що у березні цього року минуло п\'ять років, як автор публікації виступив з доповіддю про урбаністичні трансформації території з площею та сквером на науковому семінарі Комісії архітектури та містобудування Наукового Товариства ім.&nbsp;Шевченка.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2715:svjatojurska-gora-kolys-teper-v-prydeshnomu&catid=27:gk-v-ukrajini-cat&Itemid=57',5028,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5485,'P','2015-04-11',1,NULL,'Rss','Свячення пасок – найважливіше у святкуванні Великодня?','<p><img src=\"http://askoldova-mohyla.org/uk/image,6508,200,32417181.jpg\" style=\"margin: 10px; float: right;\" />Пасха – найбільше християнське свято, в якому світло торжествуємо воскресення з мертвих на третій день Господа нашого Ісуса Христа. На це свято на наших землях склалася традиція освячувати паски та іншу поживу, яку приносимо до храму (на подвір’я храму) для благословення, щоб потім з радістю споживати освячену їжу у великий і святий день Пасхи Господньої. Це гарний і добрий звичай, який віками існує в українському народі, однак ми з нього сотворили певного&nbsp;<strong>ідола</strong>. Що мається на увазі?</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2711:svjachennja-pasok-navazhlyvishe-u-svjatkuvanni-velykodnja&catid=37:spilnota-cat&Itemid=49',5029,NULL,1,1,26),
(5486,'P','2015-04-11',1,NULL,'Rss','Великдень - традиції та звичаї ','<p><span style=\"font-size: 1em;\"><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6490,200,images.jpg\" />&nbsp;Великодня Неділя</strong></span></p>\n<p>Раненько, після служби, народ чимдужче біжить додому. Вважається: хто як прибіжить – так у нього буде споритися господарство. Якщо в хаті є відданиця або парубок, намагаються бігти найшвидше. Хто перший буде вдома, той цього року знайде собі пару й побереться.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2697:velykden-tradyciji-ta-zvychaji-&catid=37:spilnota-cat&Itemid=49',5030,NULL,1,1,26),
(5487,'P','2015-04-11',1,NULL,'Rss','Чи справді віримо, що Христос воскрес?','<p><img src=\"http://askoldova-mohyla.org/uk/image,6508,200,13979.gif\" style=\"margin: 10px; float: right;\" />Віримо ми чи ні, що Христос справді воскрес із мертвих? Певно не дуже. Бо якби ми вірили, то чи святкували би ми Його Воскресіння так, як святкуємо? Якби ми справді вірили, що Христос встав із мертвих і перебуває з нами, чи могли б ми у день Його перемоги над світом так неправдиво і лицемірно святкувати цю радість? А чому неправдиво і лицемірно, спитає хтось. А тому, що наше&nbsp;<strong>правдиве святкування</strong>зводиться у кращому випадку до більше чи менше уважної і щирої молитви у храмі в неділю ранком, а в гіршому випадку взагалі не має місця у торжестві Пасхи. Чому?</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2713:chy-spravdi-virymo-shcho-hrystos-voskres&catid=37:spilnota-cat&Itemid=49',5031,NULL,1,1,26),
(5488,'P','2015-04-11',1,NULL,'Rss','Зі світлим празником Воскресіння Христового!','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6509,200,30079.jpg\" /><strong>&nbsp;</strong></p>\n<div style=\"text-align: center;\"><strong><strong style=\"font-size: 0.875em;\">Христос Воскрес!</strong></strong></div>\n<p> </p>\n<p style=\"text-align: justify;\">Приступім зі свічками до Христа, що виходить із гробу, немов ідучи назустріч женихові,</p>\n<p>і радісно з усіма прославляймо Пасху Божу спасительну.</p>\n<p>Від раннього ранку чуваймо і, замість мира, пісню принесім Владиці, і Христа побачимо — правди Сонце, що всім життя освітлює.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2716:zi-svitlym-praznykom-voskresinnja-hrystovogo&catid=31:kolonka-redaktora-cat&Itemid=1',5032,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5489,'P','2015-04-13',1,NULL,'Rss','Світлий понеділок: традиція святкування','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6509,200,DSC_0993.JPG\" />Празник Воскресіння Христового ще називають Святом Пасхи чи Великдень від того що це дійсно «Великий день». Слово «пасха» означає (з єврейської мови) «перехід» і «ягня». Свято Пасхи святкували на спомин визволення вибраного народу Божого з Єгипетської неволі, а для нас християн – символізує перехід Ісуса Христа: від життя до смерті й від смерті до життя.</p>\n<p>Ці святкування Великодня не завершуються лише Неділею, а продовжуються у так звані «Світлий Понеділок» і «Світлий вівторок».</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2718:svitly-ponedilok-tradycija-svjatkuvannja&catid=28:tema-nomera-cat&Itemid=2',5034,NULL,1,1,26),
(5490,'P','2015-04-14',1,NULL,'Rss','Емаус — це історія про те…','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6509,200,1789.0.jpg\" />Емаус — це історія про те, чому це смішно&nbsp;— боятися робити помилки, і про те, як Бог із нашого поганого може зробити своє чудове. Бо дорога до Емауса&nbsp;— це неправильна, погана дорога. Двоє учнів пішли нею вже після того, як почули звістку, що Ісус воскрес, від жінок та решти апостолів. Вони свідомо вибрали не вірити, свідомо вибрали віддалитися. І&nbsp;тут починає тріщати мій перший стереотип про Бога: Ісус не залишається в якійсь «праведній точці», з якої кричить: «Агов, ви не туди пішли! Ви не праві, і якщо не повернетеся&nbsp;— не бути більше вам Моїми учнями!» Ісус іде з нами, нашою неправильною дорогою… Ми часто сприймаємо Ісуса ще й досі мертвим, думаємо, що як не будемо йти до Нього, то Він так і лежатиме, стаючи дедалі меншою точкою на нашому життєвому горизонті. Можемо вибрати найгірші дороги, які лиш пропонує нам життя; але Ісус від нас не відмовиться, Він залишить ідеальні для нас дороги і йтиме по тих інших разом із нами.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2719:emaus--ce-istorija-pro-te&catid=28:tema-nomera-cat&Itemid=2',5035,NULL,1,1,26),
(5491,'P','2015-04-15',1,NULL,'Rss','Єпископ Теодор Ромжа','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6518,200,teodor_b.jpg\" />14 квітня 1911 народився один з найвидатніших єпископів греко-католицької Церкви на Закарпатті, убитий за наказом Сталіна Георгій Теодор (Феодор) Ромжа (1911-1947), титулярний єпископ Аппійскій, адміністратор, згодом єпископ Мукачівської греко-католицької єпархії. Беатифікований Римсько-католицькою церквою в 2001 році.</p>\n<p>Народився в Надьбочко, Підкарпаття (нині Великий Бичків, Закарпатська область) в родині залізничного службовця Павла Ромжі, мати - Марія Семак. Був дев\'ятою дитиною в сім\'ї. Чотири класи народної школи Федір закінчив у Бичкові, в 1922 році батьки визначили його в державну реальну гімназію в місті Хуст, яку він з відзнакою закінчив у 1930 році.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2720:jepyskop-teodor-romzha&catid=35:istorychni-postati-cat&Itemid=48',5036,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5492,'P','2015-04-15',1,NULL,'Rss','Подружня пара – це Божий образ','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6518,200,francus.jpg\" />Темі подружжя – союзу між чоловіком та жінкою, як основи сім’ї, була присвячена катехиза, яку Папа Франциск виголосив під час загальної аудієнції у середу, 15 квітня 2015 р., що зібрала на площі Святого Петра у Ватикані десятки тисяч прочан. За його словами, це «центральний аспект теми сім’ї», адже мова йде про «великий дар, який Бог зробив для людства, сотворивши чоловіка та жінку» та давши «Святе Таїнство Подружжя».</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2721:podruzhnja-para--ce-bozhy-obraz&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5037,NULL,1,1,26),
(5493,'P','2015-04-16',1,NULL,'Rss','О. ВАСИЛЬ ПОТОЧНЯК ДУХОВНИЙ ТЕСТАМЕНТ (ЗАПОВІТ)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6520,200,464654.JPG\" style=\"margin: 10px; float: left;\" /></p>\n<p>Зі Святими упокій, Христе, душуі слуги Твого, де немає болю, ні печалі, ні зітхання, але життя безконечне. Сотвори йому вічную пам’ять, вічную пам’ять, вічную пам’ять, зі Святими упокій, Христе, вічная пам’ять.</p>\n<p>О. ВАСИЛЬ ПОТОЧНЯК ДУХОВНИЙ ТЕСТАМЕНТ (ЗАПОВІТ)</p>\n<p>Писаний в часі цілонічного чування в Єрусалимі 14-15 вересня 2012 року Божого у храмі Гробу ГНІХ</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=2722:o-vasyl-potochnjak-duhovny-testament-zapovit&catid=28:tema-nomera-cat&Itemid=2',5038,NULL,1,1,26),
(5494,'P','2015-04-16',1,NULL,'Rss','Булла «Misericordiae vultus» («Обличчя милосердя»)','<p><em><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6521,200,464654.jpg\" />Франциск</strong></em><em><strong> </strong></em><em><strong>Єпископ Рима</strong></em><em><strong> </strong></em><em><strong>Слуга слуг Божих</strong></em></p>\n<p><em><strong>До всіх, хто читатиме цей лист,&nbsp;—</strong></em><em><strong> </strong></em><em><strong>Благодать вам, милосердя і мир!</strong></em></p>\n<p>Ісус Христос є обличчям милосердя Отця. На моє переконання, таємниця християнської віри знаходить у цьому слові свій синтез. Воно стало живим, видимим і осягнуло свою повноту в Ісусі з Назарета. Отець, «багатий милосердям» (пор. Еф 2,&nbsp;4), коли об’явив Мойсеєві своє ім’я: «Бог милосердний і ласкавий, нескорий на гнів, многомилостивий, вірний» (Вих 34,&nbsp;6),&nbsp;— вчинив так, що людина могла безперервно пізнавати Його божественну природу в різні способи та в численних моментах історії. У&nbsp;«повноту часів» (пор. Гал 4,&nbsp;4), коли все було готове згідно з Його планом спасіння, Він зіслав свого Сина, народженого з Діви Марії, аби остаточним чином об’явити нам свою любов. Хто бачить Сина, той бачить і Отця (пор. Йн 14,&nbsp;9). Ісус із Назарета своїми словами, жестами та цілою своєю особою об’явив нам милосердя Бога.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2723:2015-04-16-14-22-39&catid=32:gk-svit-cat&Itemid=58',5039,NULL,1,1,26),
(5495,'P','2015-04-16',1,NULL,'Rss','«Дух вільної України назавжди поставить крапку в комуністичній ідеології» – глава УГКЦ','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6522,200,454.jpg\" style=\"margin: 10px; float: left;\" /></strong>Гість «<a target=\"_blank\" href=\"http://www.radiosvoboda.org/section/vasha-svoboda/4290.html\">Вашої Свободи</a>»: Святослав (Шевчук), глава Української греко-католицької церкви.</p>\n<p>Інна Кузнецова: Тривожні новини надходять зі сходу України у великодні дні. Дипломати називають це «воєнною декорацією» перед переговорам міністрів закордонних справ «нормандської четвірки».</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2724:2015-04-16-19-50-53&catid=28:tema-nomera-cat&Itemid=2',5040,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5496,'P','2015-04-17',1,NULL,'Rss','«Нові» гріхи, в яких важливо покаятися під час сповіді','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6523,200,sheremeta.jpg\" />Світ змінюється. А з ним – змінюємося й ми. Те, що вчора вважали неможливим, нині — приймаємо як належне. Те, чого боялися, використовуємо у власних інтересах. Ту межу, яку ще вчора боялися переступити, нині залишили далеко за плечима. Зміна поглядів, темпу життя, можливостей пізнання створюють щоразу нові спокуси.</strong></p>\n<p><em>І ми часто піддаємося на провокації. Грішимо, бо слабкі тілом і духом. І оновлюємося – приступивши до сповіді та прийнявши причастя.&nbsp; Але чи правдива наша сповідь, чи повна? Адже часто робимо речі, про які в Біблії ні слова.&nbsp; До прикладу, витрачаємо багато часу на спілкування в соцмережах... Згадувати про це на сповіді чи ні? Адже гріх нині також набув іншої форми. Модернізувався. То в чому маємо ще каятися?</em>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2725:2015-04-17-20-02-32&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5041,NULL,1,1,26),
(5497,'P','2015-04-18',1,NULL,'Note','Розклад Богослужінь на кожен день 2015 р.Б.','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Неділя</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08.00 Божественна літургія зі співом вірних</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">10.00&nbsp;Божественна літургія з хором</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">12.30&nbsp;Божественна літургія для дітей Збір молоді</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">09:40&nbsp;Молебень з Акафістом до Матері Божої Гошівської за оздоровлення хворих і узалежнених, охорону подорожуючих, оливо помазання відправляється&nbsp;у каплиці \"Чудо покладення чесної ризи Пресвятої Богородицi у Влахернi\".</span></p>\r\n<div id=\"_appInstalled\">&nbsp;</div>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Понеділок</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08.00&nbsp; Божественна літургія. Молитва заступництва св. Шарбеля і Благословення мощами, оливопомазання</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18.00&nbsp;Божественна літургія,Акафіст Теодору Ромжі з благословенням мощами</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Вівторок</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08.00&nbsp; Божественна літургія. Акафіст Богородиці Всецариці за онко хворих, молитви оздоровлення, оливо помазання.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18.00&nbsp;Божественна літургія, Молобен до Блаженного Омеляна Ковча. &nbsp;Девятниця до св.Онуфрія.&nbsp;Катехизація для парафіян</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Середа</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08.00&nbsp;Божественна літургія поминальна. Молебень до Богородиці</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18.00&nbsp;Божественна літургія поминальна,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">19.00&nbsp;Акафіст до Богородиці Зарваницької, молитви оздоровлення хворих і узалежнених, оливо помазання. Молитва матерів за дітей</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Четвер</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08.00&nbsp;Божественна літургія. Акафіст до святого Миколая Чудотворця, Чин великого освячення води. Молитви за оздоровлення недужих, подорожуючих, поміч Святого Духа, оливо помазання</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18.00&nbsp;Божественна літургія. Молебень до Блаженного Миколая Чернецького перед його іконою</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>П\'ятниця</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08.00&nbsp;Божественна літургія поминальна. Молебень до Христа Чоловіколюбця</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18.00&nbsp;Божественна літургія поминальна.&nbsp;Біблійні читання</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Субота</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08.00 Утреня</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">09.00&nbsp;Божественна літургія за спочилих. Парастас</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">11.00 Акафіст ГНІ Христу в крипті св. Сильвестра папи(Аскольда)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18.00&nbsp;Божественна літургія. Всенішня.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Неділя</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08.00 Божественна літургія зі співом вірних</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">10.00&nbsp;Божественна літургія з хором</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">12.30&nbsp;Божественна літургія для дітей. Збір молоді</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">09:40&nbsp;Молебень з Акафістом до Матері Божої Гошівської за оздоровлення хворих і узалежнених, охорону подорожуючих, оливо помазання відправляється&nbsp;у каплиці \"Чудо покладення чесної ризи Пресвятої Богородицi у Влахернi\".</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Кожної першої п\'ятниці місяця - молитовна зустріч Апостольства молитви</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08.00; 18.00&nbsp;Божественна літургія, Сповідь</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Напередодні в четвер</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">19.00&nbsp;Свята година. Сповідь, відвідування і причастя хворих</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Кожної першої суботи місяця о 11:00 &nbsp;засідання Церковної ради</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Щонеділі&nbsp;збір молоді о&nbsp;14.00&nbsp;- Наука за кавою в Церковній хатині</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Субота о&nbsp;12:00, Неділя о&nbsp;14.00&nbsp;&nbsp;Недільна школа для потребуючих дітей з гарячими обідами.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">У тому ж часі мають можливість пообідати потребуючі парафіяни в благочинній кухні Карітас-Печерськ</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Телефони для довідок:&nbsp;(050) 4473157; (067) 406 89 33;&nbsp;e-mail:&nbsp;<a href=\"mailto:oranta-gazeta@ukr.net\" target=\"_blank\">oranta-gazeta@ukr.net</a>, <a href=\"http://www.oranta.org/\" target=\"_blank\"><strong>www.oranta.org</strong></a></span></p>\r\n<div id=\"_appInstalled\">&nbsp;</div>',NULL,5042,5,1,NULL,38);
