INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5523,'P','2015-05-22',1,NULL,'Rss','Бог — не примітивний дідуган, який насилає хвороби','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6549,200,get.jpg\" /></strong>«Мене вже давно не повинно бути. Я&nbsp;живу наперекір і в кредит». Розмова з о.&nbsp;Яном Качковскім, директором госпісу св.&nbsp;Отця Піо (Пуцк, Польща), біоетиком, який бореться з пухлиною в мозку, героєм книжки «Життя на повну котушку»&nbsp;— про хворобу і страждання, звинуваченні Бога в усьому, генеральну репетицію помирання і плюшевий католицизм.</p>\n<p><strong><em>— Отче, Ви часто даєте інтерв’ю, в яких розповідаєте про своє смертельне захворювання. Ви навіть самі себе називаєте «онкосвящеником». Чи означає це, що Ви щасливі у своїй хворобі (або попри неї)?</em></strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2748:bog--ne-prymityvny-didugan-jaky-nasylaje-hvoroby&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5067,NULL,1,1,26),
(5524,'P','2015-05-23',1,NULL,'Rss','ТЕПЛИЙ МИКОЛАЙ НА АСКОЛЬДОВІЙ МОГИЛІ','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6550,200,askoldova_mogila_2.jpg\" />24 травня 2015 р. у рамках святкування Теплого Миколая (свята Перенесення мощів св. Миколая Чудотворця) – храмового свята церкви св. Миколая на Аскольдовій могилі та приуроченого до нього мистецького заходу – фестивалю-конкурсу духовної пісні «Аскольдів глас», на Аскольдовій могилі відбудеться концерт за участю всіма улюбленого парафіяльного хору «Аскольд», народної артистки України Ірини Даць та популярного рок-гурту «НеМо».</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2749:teply-mykola-na-askoldovi-mogyli&catid=25:parafija-cat&Itemid=27',5068,NULL,1,1,26),
(5525,'P','2015-05-26',1,NULL,'Rss','Дорогою добра та краси','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6485,200,11047592.jpg\" style=\"margin: 10px; float: left;\" />Від 22 до 24 травня в Київській Трьохсвятительській духовній семінарії відбувся семінар \"Дорогою добра та краси\", який організувала Патріарша катехитична комісія УГКЦ.</strong></p>\n<p>Проводили семінар с. Луїза Цюпа, СНДМ, д-р, заступник голови Патріаршої катехитичної комісії, директор Катехитично-педагогічного інституту УКУ, п. Назар Дуда, викладач, керівник програм КПІ УКУ, та п. Наталя Мусій, викладач, референт КПІ УКУ.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2750:dorogoju-dobra-ta-krasy&catid=27:gk-v-ukrajini-cat&Itemid=57',5069,NULL,1,1,26),
(5526,'P','2015-05-26',1,NULL,'Rss','Маємо допомогти росіянам стати щирими патріотами своєї країни, а не імперіалістами, – Любомир Гузар','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6552,200,guzar20039.jpg\" />На зустрічі з підприємцями 20 травня у Львові Любомир Гузар нагадав деякі поради від Митрополита із послання “Як будувати рідну хату”.</p>\n<p>Служитель наголосив, що Шептицький вчив любити рідний народ, але водночас не мати ненависті до інших народів. “Ми повинні любити всіх людей, але особлива любов має бути до батька і матері, як дали нам дар життя. Наша Батьківщина – це національна родина, і ми повинні за неї дбати”, – сказав він.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2751:majemo-dopomogty-rosijanam-staty-shchyrymy-patriotamy-svojeji-krajiny-a-ne-imperialistamy--ljubomyr-guzar&catid=37:spilnota-cat&Itemid=49',5070,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5527,'P','2015-05-27',1,NULL,'Rss','Може, саме тепер формується політична українська нація, про яку весь час говорилося','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6552,200,116753.jpg\" /></strong>Іван Дзюба — знаний громадський і політичний діяч, учасник групи “Першого грудня”, дисидент, автор праці “Інтернаціоналізм чи русифікація”, Герой України — в інтерв’ю DT.UA поділився думками про українську політику та її діячів, російську пропаганду, причини невдачі Народного Руху та формування національної самобутності українців.</p>\n<p><strong>— Іване Михайловичу, як ви оцінюєте нинішню роботу Верховної Ради, уряду? Чим останній відрізняється від часів, коли ви були міністром культури?</strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2752:mozhe-same-teper-formujetsja-politychna-ukrajinska-nacija-pro-jaku-ves-chas-govorylosja&catid=37:spilnota-cat&Itemid=49',5071,NULL,1,1,26),
(5528,'P','2015-06-06',1,NULL,'Rss','???µ???€?–?? ?– ???€?°???‚?????° ?”???…?????????… ?’???€?°??. ?§?°???‚?????° 9','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6577,200,theory_practSE9.jpg\" />19. ???‚?€?°?‚?µ???–?? ?– ?‚?°???‚?????° ?????…?????????… ?????€?°??</strong><o:p></o:p></p>\n<p>?§?°???‚?????° ???°?????— ???????? ???°?????????µ???° ?‘???¶?????? ?€?µ?‡?°????, ?° ?‡?°???‚?????° ?????€????????????. ?†?????°?‚?–???????????° ?????…???????–???‚?? ?€“ ???????‚?????????? ?·?°???–???????°?‚?? ?????€???????? ????????, ?????€???????– ?????????? ?????±???¶????????, ?‘???¶?????? ?????€?°???°????. ???????? ?„???????°???µ???‚???? ?????¶?????— ???µ?????‚?°?†?–?— ?” ???????‚?µ ????????????. ???µ?€?µ?? ?????¶?????? ???µ?????‚?°?†?–?”?? ????. ?†?????°?‚?–?? ???????°?”&nbsp;<em>?????»???‚???? ???€???????‚?????°??????</em>.<o:p></o:p></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2771:teorija-i-praktyka-duhovnyh-vprav-chastyna-9&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5096,NULL,1,1,26),
(5529,'P','2015-05-28',1,NULL,'Rss','Теорія і практика Духовних Вправ. Частина 1','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6554,200,797966.jpg\" />1.&nbsp;Початок ігнатіянської духовності</strong></p>\n<p>Якщо спробуємо підвести під духовність якийсь простий богословський фундамент, то перше, що мені спадає на думку, це пролог євангеліста Йоана: «І&nbsp;Слово стало тілом». Бог не обмежується одноразовим актом створення, а творить постійно, тобто постійно промовляє своє слово до людини. Якщо людина приймає це слово до свого серця й культивує його, то воно проростає, матеріалізується в її житті й видає плід. Отож, кожна духовність&nbsp;— це Боже слово, яке стає тілом, вогненним стовпом, що веде через пустелю, чи Вифлеємською зіркою, що вказує шлях до Бога.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2753:teorija-i-praktyka-duhovnyh-vprav-chastyna-1&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5072,NULL,1,1,26),
(5530,'P','2015-05-29',1,NULL,'Rss','Комунікат про спільну міжконфесійну зустріч між УГКЦ та Харківсько-Полтавською єпархією УАПЦ','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6555,200,z_isichenkom1.jpg\" />28 травня 2015 року в м. Києві відбулася перша спільна міжконфесійна зустріч між Українською Греко-Католицькою Церквою та Харківсько-Полтавської єпархією УАПЦ. До складу делегацій увійшли єпископи, священики та миряни з обох сторін.</strong><strong>&nbsp;</strong></p>\n<p>Відповідно до звернення XXV Собору Харківсько-Полтавської єпархії УАПЦ щодо прохання про братні поради для «досягнення євхаристійного спілкування та адміністративної єдності Харківсько-Полтавської єпархії Української Автокефальної Православної Церкви з Українською Греко-Католицькою Церквою як із співспадкоємицею київської традиції» було проведено першу зустріч офіційних представників обох Церков.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2758:komunikat-pro-spilnu-mizhkonfesinu-zustrich-mizh-ugkc-ta-harkivsko-poltavskoju-jeparhijeju-uapc&catid=27:gk-v-ukrajini-cat&Itemid=57',5073,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5531,'P','2015-05-29',1,NULL,'Rss','Теорія і практика Духовних Вправ. Частина 2','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6554,200,part2.jpg\" />5. Ігнатіянська мудрість самопізнання — психологічного й духовного</strong></p>\n<p>Погляньмо на порівняння св.&nbsp;Ігнатія і порівняймо їх із нашими буденними порівняннями, яких нас вчать з дитинства: «Бери приклад з Івася, бо він добре вчиться, не дружи з Миколою, бо його тато пияк» тощо. Такий спосіб мислення закарбовується в нас змалку, що з погляду психології є фатальним. Ми засвоюємо зовнішні кальки поведінки, якісь моральні взірці, з якими порівнюємо інших і свідомо й підсвідомо ставимо морально-етичні оцінки у категоріях добра і зла: той добрий, а той поганий. І&nbsp;самі живемо за цими взірцями, приміряючись до реакції спочатку батьків, а потім інших людей на нашу поведінку.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2754:teorija-i-praktyka-duhovnyh-vprav-chastyna-2&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5074,NULL,1,1,26),
(5532,'P','2015-05-29',1,NULL,'Rss','«ЄС може існувати без України, але Європа не може, бо буде неповна»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6557,200,u_t.jpg\" />«Українці прагнуть посісти в Європі чільне місце як християнський народ», - сказав Отець і Глава Української Греко-Католицької Церкви Блаженніший Святослав у інтерв’ю польському виданню «В мережі». «Україна усвідомлює сьогодні свою європейську ідентичність, своє місце в сім\'ї європейських народів», – зазначив він.</p>\n<p>«Коли ми говоримо про європейську свідомість України, то маємо на увазі передусім певні внутрішні процеси, що відбуваються в нашій країні, – наголосив Предстоятель УГКЦ. – Адже іскрою, яка викликала Революцію гідності, було рішення президента Януковича не підписувати угоду про Асоціацію з Європейським Союзом».</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2763:2015-05-29-19-05-11&catid=28:tema-nomera-cat&Itemid=2',5075,NULL,1,1,26),
(5533,'P','2015-06-06',1,NULL,'Rss','?–???€???°?»?–???‚?????° ?€“ ?†?µ ?????????–?± ?¶???‚?‚??','<p><img src=\"http://askoldova-mohyla.org/uk/image,6577,200,huzar_press-konf.jpg\" style=\"margin: 10px; float: left;\" />?–???€???°?»?–???‚?????° ?€“ ???°???·?????‡?°?????? ???°?¶?»???????? ?µ?»?µ???µ???‚ ???µ???????€?°?‚???‡???????? ?»?°????, ?±?? ???????????°???°?” ?»?????????– ?€???·?????–?‚?? ?? ?????€?–???????°?‚?? ???°?¶?»?????– ?¶???‚?‚?”???– ?????€?°???? ???€?°?????»??????, ?µ?„?µ???‚???????? ?? ???????»?–??????????. ???????? ?†???????? ?????? ?…???‡?? ???µ?€???µ?‡???? ???€?????–?‚?°?‚?? ???€?°?†?–?????????–?? ?—???† ?– ?????–?…, ?…?‚?? ???€???‡?µ?‚?????? ???? ?¶???€???°?»?–???‚??????.</p>\n<p>?‘?°?¶?°?? ???°?? ?????–????????, ???–?????????–???°?»?????? ???????????????°?‚?? ???????? ?€???±???‚??!</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2772:zhurnalistyka-ce-sposib-zhyttja&catid=37:spilnota-cat&Itemid=49',5097,NULL,1,1,26),
(5534,'P','2015-05-30',1,NULL,'Rss','Зіслання Святого Духа','<p><em><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6555,200,image034.jpg\" />«Ми виділи світло істинне, ми прийняли Духа небесного, ми знайшли віру істинну, нероздільній Тройці поклоняємося, Вона бо спасла нас» (Стиховна вечірні П\'ятдесятниці).</strong></em></p>\n<p>Світлий празник Пасхи завершується не менш світлим і великим празником&nbsp; Святої П\'ятдесятниці. Після Христового Різдва і Воскресення цей празник&nbsp; належить до найбільших празників нашого церковного року. Зіслання&nbsp; Святого Духа — це наче вінець і печать на справі спасення людського&nbsp; роду, що здійснив Божий Син. У дні святої П\'ятдесятниці сам Святий Дух&nbsp; помазує апостолів на проповідників Христового Благовістя. У дні Зіслання&nbsp; Святого Духа зароджується і починає діяти Христова Церква. Святий Дух&nbsp; її провадить, просвічує, освячує і зберігає на дорозі правди.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2757:zislannja-svjatogo-duha&catid=28:tema-nomera-cat&Itemid=2',5076,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5535,'P','2015-05-30',1,NULL,'Rss','Нова євангелізація полягає в усвідомленні милосердної любові Бога, щоб ставати засобом спасіння для ближніх','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6559,200,ossrom55457_articolo.jpg\" />Сьогодні люди очікують від Церкви, що вона вмітиме супроводжувати їх свідченням віри, яка вчиняє солідарними з усіма, зокрема – з найсамотнішими й відкиненими. Думками про це Святіший Отець Франциск поділився з проводом, членами та радниками Папської Ради сприяння новій євангелізації, приймаючи їх у п’ятницю, 29 травня 2015 р., наприкінці їхньої пленарної зустрічі, яка відбувалася на тему: «Взаємозв’язок між євангелізацією та катехизацією».</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2764:nova-jevangelizacija-poljagaje-v-usvidomlenni-myloserdnoji-ljubovi-boga-shchob-stavaty-zasobom-spasinnja-dlja-blyzhnih&catid=32:gk-svit-cat&Itemid=58',5077,NULL,1,1,26),
(5536,'P','2015-05-30',1,NULL,'Rss','Теорія і практика Духовних Вправ. Частина 3','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6554,200,part3.jpg\" />8. Учитель і учень</strong></p>\n<p>На що слід звернути увагу в самій книжці? Ігнатій не говорить про те, що має відбуватися вже в самій молитві. Особистий духовний досвід автора, тобто те, що відбувалося конкретно в його почуттях, емоціях, уяві,&nbsp;— викладено в Автобіографії та Духовному щоденнику, а в Духовних Вправах цього вже немає. Тут міститься його досвід, виражений у виді загальних норм та інструкцій, бо він усвідомлює, що кожна людина індивідуальна; за загальною подібністю досвіду, за загальним розділенням на етапи стоять індивідуальні стосунки людини з Богом, тобто кожний людський досвід на шляху духовних вправ індивідуальний. І&nbsp;тому великою небезпекою може стати намагання керівника відтворити свій власний досвід в учневі, тобто спрямовувати його на той шлях, який він сам пройшов. Особисте свідчення має служити заохоченню, можливості наслідування, засвоєнню чогось з цього свідчення, але не бездумному мавпуванню вчителя. Одна з інструкцій св. Ігнатія звучить так:</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2755:teorija-i-praktyka-duhovnyh-vprav-chastyna-3&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5078,NULL,1,1,26),
(5537,'P','2015-05-30',1,NULL,'Note','Звіт №3 про НАДХОДЖЕННЯ  ЛИСТІВ Березень 2015 р. — 14 листів','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;Добрий день. <strong>Здравствуйте дорогие наши спасители, спасибо Вам за поддержку и помощь.</strong> Прошу прощения за позний ответ, с внуком находилась в больнице. Мы с внуком два раза в год ложимся в больницу с ножкой, (ему делали операцию) он вовсе не мог ходить. Спасибо Вам большое и пусть Наш Господь помогает во всём Вам, а самое главное здоровье.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">С уважением к Вам &nbsp;<span style=\"text-indent: 0em; line-height: 1.3em;\">Любов Николаевна&rdquo;</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Листи від ув\'язнених </span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Чернігівська виправна колонія ЧВК-44 (м.Чернігів) &mdash; </strong><strong>3</strong><strong> лист</strong><strong>и</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Збаразька виправна колонія ЗВК-63 (Тернопільська обл.) &ndash; 3 листи</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Подяка за посилки для дітей &ndash;4 листи</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Листи звільнених з ув</span></em></strong><strong><em><span style=\"text-decoration: underline;\">&rsquo;</span></em></strong><strong><em><span style=\"text-decoration: underline;\">язнення &ndash; 2 листи</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Листи приватної особи &ndash; 2 листи</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;&hellip; <strong>Звонила до невестки (<em>син в АТО</em>), она Вас благодарит за посылочку</strong>, извените, что не смогла отписать, ведь <strong>на руках две девочки, одну в школу, а возле Полинки нужно больше внимания. Растёт внученька в апреле уже год</strong>. А бабця сидит&hellip; В феврале перед Стриченням, к нам в церковь приезжали сёстры и братья с Киева, ставили сценку о рождении Христа. Это так душевно, красиво, все мы были очень благодарны. Каждую субботу о.Петро читает Библию. Я не считаю себя хорошей, погрязла в болоте, но<strong> я очень стараюсь, чтоб хоть сколько есть силы изменить свою жизнь</strong>. Ведь сколько я зла принесла своим близким и самое дорогое родным. Мне приятно, что меня ждут и не отвернулись от меня. <strong>Я написала помиловку, помолитесь за меня? И мы здесь молимся за всех, за солдат которые сейчас на Донбасе и Луганске, за мир на нашей земле, за больных</strong>&hellip;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Якщо можливо то <strong>вишліть будь ласка штук 10 марок</strong>, а то у нас вже два місяці в магазинах немає&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ольга Н. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;&hellip; <strong>Поздравляю Вас с наступающим Великим праздником Светлой Пасхи!!!</strong> <strong>Хочу пожелать мира и тепла на всей планете, Любви и Здоровья Всем людям. Да хранит Вас Господь. Желаю телу здравия, душе спасения на многие лета.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Извените что долго не писала, но в стране война и как то не хотелось беспокоить Вас своими просьбами&hellip; <strong>Очень нужны акриловые нитки </strong><em>(талановита вишивальниця)</em>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Олена С. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;&hellip; Дорогой о.Игорь <strong>большое Вам спасиба за то, что поздравили моих деток</strong>. Знаете я постоянно молюсь за моих родных и всех людей, а так же за прикращение войны на Донбасе и что бы у нас было всё хорошо в Украине.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Да <strong>я маме очень благодарна за то, что мои детки дома, а не в детских домах и интернатах,</strong> а детки все болеют<strong> у Назара 02.07.1999 гр. &ndash; застрит желудка, Роксана 04.02.2003 гр. &ndash; проблемы большие с почками, Ромочка 05.10.2007 гр. &ndash; инвалид по ножкам, а тепер ещё сказали оформить инвалидность по зрению, Анастасия 27.09.2008 гр. &ndash; косоглазие, Ингочка 04.04.2011 гр. &ndash; врождённый порок сердца, маме очень трудно.</strong> Отец Игорь Вы уже простите меня, я очень Вас прошу <strong>помогите мне делают паспорт </strong>и уже сфотографировали, а <strong>нужно заплатить 150 гривен, а я получаю очень мало</strong> на лавровом листе, а <strong>мне через 4 месяца выходить</strong> и что бы паспорт был готов в течении месяца, ведь <strong>нужно будит устраиваться на работу, да и что бы без сюрпризов доехать домой.</strong> <strong>Пожалуста молитесь за моих деток и маму, и за меня</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Олена Т. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;&hellip; <strong>получила ваше письмо за которое огромное спасибо, за молитвы за иконку</strong> тоже спасибо. Я прошу Вас пешите я очень нуждаюсь даже в переписке. <strong>С девчатами каждый вечер собераемся на молитву. Я прошу Бога что-бы остановилось это все очень жалко молодых ребят им бы еще жить та жить </strong>а они идут под пули только за то что власть немогут некак поделить смотриш новости а сердце кровью облеваеться что нечем не можеш помочь только молитвами. Сейчас все колонии помогают чем могут&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Наталія К. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;&hellip; <strong>Займаюсь вишиванням, дуже чекаю схему</strong>&hellip; Сьогодні сказали що можливо закінчиться війна. Хвилюємося, бо в багатьох є діти і серце болить уже не за себе, а за дитину&hellip; <strong>Почалась весна, загострення хворобам</strong> жити треба і терпіти, бо ще хочеться звільнитись додому. <strong>В невол</strong><strong>і</strong><strong> уже п</strong><strong>&rsquo;</strong><strong>ятий р</strong><strong>і</strong><strong>к</strong>. <strong>Дуже потрібні знеболюючі для суглобів &ndash; ревматизм. І ще від печінки, рецепт я Вам надіслала.</strong> Хочу Вам сказати, що<strong> я вишила пасхальний рушничок на виставку (конкурс) в Київ. А на Спаса треба також вишивати, але чекаю від Вас схему</strong>. Якщо не зможете то може десь тут знайдемо. Або напишу в Почаєв матушці Надії. 20-го лютого приїзжали до нас з м.Почаєва монахині і матушка Надія. Привезли літературу і деякий одяг. Ми так плакали коли вони співали пісню про Спасителя... Напишіть мені які у вас конкурси мають бути, напишіть тему, то я буду щось творити попробую надіслати Вам&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Любов С. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;Слава Ісусу Христу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Дорогий о.Ігорь, сестри хочу Вам подякувати за Вашу духовну підтримку і попросити у Вас молитви, благословення. 19.05.2015 р. я звільняюся</strong> кожний день рано і ввечері я прошу у Господа прощення і помощі щоб поміг мені перетерпіти, вижити і повернутися до дому. Недавно в одному із журналу я знайшла фото каплиці св.Миколая. Дуже гарна каплиця, дерева високі і клумба напереді з червоних квітів, ікона Матері Божої Зарваницької, я показувала нашим жінкам, багато з наших засуджених ведуть з Вами переписку от я їм показала щоб виділи де Ви молитеся. Зараз ми готуємо нашу церкву до Пасхи, по можливості постимося але причаститися не будемо мати змоги, до нас священники приходять дуже рідко, а в основному баптисти, суботники інші, але я їхні служіння не відвідую. <strong>Якщо можна, допоможіть мені одягом 50-52 і взуттям 40 розмір, хусткою і хрестиком. Гарно дякую Вам о.Ігор, волонтерам. Ми також молимся за перемогою над ворогом</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Параскева Т. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;<strong>Велике спасибі!</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>Тетяна Д. (Київська обл., Артем-4р., син Ольги В., Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;Добрий день. <strong>Здравствуйте дорогие наши спасители, спасибо Вам за поддержку и помощь.</strong> Прошу прощения за позний ответ, с внуком находилась в больнице. Мы с внуком два раза в год ложимся в больницу с ножкой, (ему делали операцию) он вовсе не мог ходить. Спасибо Вам большое и пусть Наш Господь помогает во всём Вам, а самое главное здоровье.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">С уважением к Вам</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Любов Николаевна&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>Любов М. (Кіровоградська обл., </em><em>Назар-15р., Роман-7р., Настя-6р., Інга-4р., діти Тараненко О</em><em>., Чернігівська ВК</em>)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;<strong>Щіро дякуємо!</strong> <strong>Хай Бог віддасть вам Своєю милістю</strong>.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Діана&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>Діана А. (Кіровоградська обл., БЦ &laquo;Джерело життя&raquo;, </em><em>Діана А.-7р., Микита-3,5р., діти Авраменко І</em><em>., Чернігівська ВК</em>)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;Слава Иисусу Хрысту!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&hellip; <strong>Ище ненайшла роботу у нас немае. Може у вас у Киеви есть яка небудь робота то допоможить мени</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Людмила В. (звільнена з Качанівської ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;Слава Иисусу Хрысту!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&hellip; <strong>Я пивгода освободыла дострочно. Братя сёстры я очинь вдячна вам за Вашу помощь яку вы мени давалы в лагери. Я за вас молюсь каждый день Вы мене до Бога прывелы</strong>. Я Вас очинь прошу чтоб вы выслалы мени адреса Генирального прокурора и по правам человека ту у сына броблемы за ным закащык ходыть хотять убыть. Вин у мене тоже верующый 24г йму. Дочька умерла от родив а я нехочю потирать ёго&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Людмила В. (звільнена з Качанівської ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;<strong>Спасибо большое! Дай Бог Вам здоровья, мирного неба</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>Людмила Б. (м.Дніпропетровськ, Кирило-11р., син Тетяни Б., Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Слава Исусу Христу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Мир Вашему Храму Николая Чудотворца на Аскольдовой Могиле!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&hellip; Вы спрашиваете <strong>размер одежды и обуви девочке 9 лет по верхней одежде размер 152 см, футболки свитера 140. Обувь 32-33 рост Киры 125 см</strong>. Моя девочка занимается не плохо, участвует в кружке &laquo;Латино-американские танцы&raquo; уже награждена медалями. Но <strong>мне трудно сейчас обеспечивать т.к. все дорожает буквально за ночь</strong>. Кира уже знает несколько молитв</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">С низким поклоном и заранее благодарим Вас сестра Светлана и малышка Кира.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Да хранит Вас Господь!&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Світлана Д. (м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Слава Иисусу Христу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Мир Вашему дому дорогие братья и сестры.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Решила обратиться к Вам за помощью т.к. трудновато стало жить. У меня две девочки 12 и 9 лет, нуждаются в одежде, обуви, моющих средствах, а теперь моя зарплата очень мала, чтобы чем то я могла обрадовать их. Если сможете чем-то помочь не обессудьте и не откажите буду за вас молиться и надеяться на лучшие времена.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Заранее буду Вам благодарна. С низким поклоном&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Юлия</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Да хранит Вас Господь!&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Юлія Ю. (м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>&nbsp;</em></span></p>',NULL,5079,5,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5538,'P','2015-05-30',1,NULL,'Note','ЗВІТ №6 про роботу служби милосердя «Карітас» при парафії св.Миколая Чудотворця на Аскольдовій могилі (березень 2015 р.)','<ol>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6563,200,P1000718.JPG\" alt=\"\" width=\"200\" height=\"150\" />Уділили потребуючим мирянам (45 осіб):</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash;125 кг</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;куртки та пальта жіночі, чоловічі й дитячі &ndash; 38 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;взуття &ndash; 14 пар</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;коляска дитяча &ndash;- 1 шт. (зимовий та літній варіант)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;памперси для дорослих &ndash; 1 упаковка (30 шт.)</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Надали матеріальну допомогу:</span></li>\r\n</ol>\r\n<ul>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">переселенцям з Донецької та Луганської обл.(25 осіб)</span></li>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">потребуючим та безпритульним (22 особи):</span></li>\r\n</ul>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp; &nbsp; &nbsp; &nbsp;<img style=\"float: left;\" src=\"image,6563,200,P1000718.JPG\" alt=\"\" width=\"200\" height=\"150\" /> &nbsp;Служба милосердя &laquo;Карітас&raquo; при церкві св.Миколая Чудотворця на Аскольдовій могилі, зібравши пожертвувані парафіянами одяг чоловічий, жіночий та дитячий, взуття, іграшки дитячі та гуманітарну допомогу одягом, взуттям, постіллю, кухонною та постільною білизною від благодійного фонду &laquo;Міжнародна асоціація підтримки України (МАПУ)&raquo;, розподілила їх таким чином (див.записи в книзі розподілу гуманітарної допомоги):</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Уділили потребуючим мирянам (45 осіб):</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash;125 кг</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;куртки та пальта жіночі, чоловічі й дитячі &ndash; 38 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;взуття &ndash; 14 пар</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;коляска дитяча &ndash;- 1 шт. (зимовий та літній варіант)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;памперси для дорослих &ndash; 1 упаковка (30 шт.)</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Надали матеріальну допомогу:</span></li>\r\n</ol>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">переселенцям з Донецької та Луганської обл.(25 осіб)</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash; 65 кг.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;куртки &ndash; 13 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;взуття &ndash; 9 пар</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;спальний мішок &ndash; 1 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;коляска дитяча &ndash; 1 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;ліжечко дитяче &ndash; 1 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;столик повивальний &ndash; 1 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;памперси дитячі &ndash; 1 упаковка</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">потребуючим та безпритульним (22 особи):</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash; 42 кг.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;куртки &ndash; 9 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;взуття &ndash; 7 пар</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;ковдра &ndash; 1 шт.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Відправили гуманітарний вантаж:</span></li>\r\n</ol>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Благодійному фонду &ldquo;Сузір\'я&rdquo; в с.Іванкові Бориспільського р-ну Київської обл.:</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг чоловічий, жіночий, дитячий, взуття, постіль &ndash; 19 мішків+1 коробка</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Передано кухні &laquo;Карітас&raquo; з гуманітарної допомоги для харчування переселенців із зони АТО та потребуючих:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;каву мелену &ndash; 1 мішок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; консерви &laquo;Ветчина рубленая из свинины&raquo; &ndash; 5б. * 240г</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Робота виконана силами волонтерів. Висловлюємо щиру подяку жертводавцям і прикликаємо Боже благословення на їх родини.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>Фото див. у додатку.</em></span></p>',NULL,5080,5,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5539,'P','2015-06-22',1,NULL,'Rss','Теорія і практика Духовних Вправ. Частина 13','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6600,200,se13.jpg\" />Сталкер</strong><o:p></o:p></p>\n<p>Сталкер веде в «Зону» двох сміливців: Письменника і Професора. Один йде, щоб реалізувати свої мрії за допомогою «Зони», інший – щоб знищити її як велике зло. Врешті-решт вони дійшли до мети, але ні один, ні другий не реалізували свої наміри, повернулися назад ні з чим. Сприймати й розуміти цей фільм можна по-різному, в залежності від світогляду глядача.<o:p></o:p></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2791:teorija-i-praktyka-duhovnyh-vprav-chastyna-13&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5119,NULL,1,1,26),
(5540,'P','2015-05-30',1,NULL,'Note','Відкрито депозитний рахунок фестивалю-конкурсу духовної пісні «Аскольдів глас»','<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Інформуємо Вас,&nbsp;&nbsp;що у відділенні&nbsp;<strong>ПриватБанку</strong>&nbsp;відкрито депозитний&nbsp;<strong>рахунок фестивалю-конкурсу духовної пісні &laquo;Аскольдів глас&raquo;</strong>,&nbsp;&nbsp;заснованого&nbsp; нашою громадою.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Кошти на проведення конкурсу, можна вносити на рахунок:&nbsp;<strong>4149 4978 4095 7908</strong></span></p>','<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава Ісусу Христу!</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дорогі парафіяни&nbsp;</span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">храму св. Миколая Чудотворця&nbsp;</span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">на Аскольдовій могилі!</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Інформуємо Вас,&nbsp;</span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">&nbsp;що у відділенні <strong>ПриватБанку</strong> відкрито депозитний <strong>рахунок фестивалю-конкурсу духовної пісні &laquo;Аскольдів глас&raquo;</strong>,&nbsp;</span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">&nbsp;заснованого&nbsp; нашою громадою.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Кошти на проведення конкурсу, можна вносити на рахунок: <strong>4149 4978 4095 7908</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Будемо вдячні за Вашу підтримку!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Оргкомітет</span></p>',NULL,5081,5,1,NULL,53),
(5541,'P','2015-05-31',1,NULL,'Rss','Бесіда Йоана Золотоустого на Святу П’ятдесятницю','<p align=\"center\"><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6563,200,93332.jpeg\" />Бесіда перша Йоана Золотоустого на Святу П’ятдесятницю і про те, чому нині не бувають знамення, і що діла і слова наші записуються.</strong></p>\n<p>1. Знову свято, знову торжество і знову многодітна і дітолюбна Церква прикрашається безліччю дітей. Але яка їй користь від дітолюбства, якщо вона тільки в свята, а не постійно бачить жадані обличчя своїх дітей, подібно до того, якщо би хтось, маючи прекрасну одежу, не мав дозволу користуватися нею постійно? Багаточисельність тих, які приходять, є одежею Церкви, як і пророк в давнину сказав, говорячи про Церкву:&nbsp;<em>Усіма ними, немов окрасою, ти вберешся, як молода, ними ти оздобишся</em> (Іс 49,18). Як цнотлива і благородна жінка, одягнувшись в одяг, що простирається до п’ят, здається благообразною і гарнішою, так і церква, покриваючись безліччю ваших тіл, немов би довгою одежею, є сьогодні більш блискучою.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2765:besida-oana-zolotoustogo-na-svjatu-pjatdesjatnycju&catid=28:tema-nomera-cat&Itemid=2',5082,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5542,'P','2015-05-31',1,NULL,'Rss','Теорія і практика Духовних Вправ. Частина 4','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6554,200,part4.jpg\" />10. Духовний досвід, продовження</strong></p>\n<p>Друга ілюстрація. Єфрем Сирійський описує подію зі свого юнацького життя. Коли він проходив через якусь місцевість, то був арештований і безпідставно звинувачений у злочині, якого він не скоїв. У&nbsp;в’язниці після роздумів і молитов перед його очима постав гріх, який він учинив колись раніше, й виразне відчуття, що сьогоднішня ситуація&nbsp;— це наслідок чи покарання саме за цей гріх. Більшість в’язнів, з якими він сидів, казали, що вони не винні, нічого поганого не вчинили. Але коли він детальніше випитував про їхнє минуле, роздумував і молився над тим, то врешті зрозумів, що невинних немає, над усім чуває Боже Провидіння, і що всі ці незаконні арешти не є випадковими.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2756:teorija-i-praktyka-duhovnyh-vprav-chastyna-4&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5083,NULL,1,1,26),
(5543,'P','2015-05-31',1,NULL,'Rss','«Ми діти Аскольда»','<p><img src=\"http://askoldova-mohyla.org/uk/image,6566,200,DSC01561.JPG\" style=\"margin: 10px; float: left;\" />З 5 квітня по 24 травня на Аскольдовій Могилі щонеділі в сім етапів проходив шаховий турнір «Ми діти Аскольда». Шахові змагання проводяться з метою популяризації шахів, налагодження дружних відносин між шахістами, вдосконалення спортивної майстерності і пізнання християнської історії Руси-України і постаті першого христитеся Аскольда-Миколая царя і мученика за віру христову та визначення переможців та призерів змагань.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=2766:2015-05-31-19-55-40&catid=25:parafija-cat&Itemid=27',5084,NULL,1,1,26),
(5544,'P','2015-06-01',1,NULL,'Rss','Теорія і практика Духовних Вправ. Частина 5','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6556,200,theory_practSE5.jpg\" />12. Духовне спустошення – криза</strong><o:p></o:p></p>\n<p>Якщо хтось уважно слідкує за ходом моїх думок і починає шукати щось спільне у Божій дії на прикладі цих трьох категорій людей, то побачить, що на цьому етапі духовних вправ людину б’ють по її больових точках. А тими точками є те, що для неї є найбільшою цінністю, до чого вона глибоко прив’язана.<o:p></o:p></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2759:teorija-i-praktyka-duhovnyh-vprav-chastyna-5&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5085,NULL,1,1,26),
(5545,'P','2015-06-01',1,NULL,'Rss','Пізнання Бога через Божу любов у сучасності','<p><img src=\"http://askoldova-mohyla.org/uk/image,6568,200,03tGBcGsrDY.jpg\" style=\"margin: 10px; float: left;\" />- Прийди, Духу Святий, любове Отця і Сина, запали наші серця вогнем Своєї любові.</p>\n<p>Людський розум, завдяки власному праву вибирати, яким наділив його Господь, допомагає своєму серцю і душі шукати Бога, у різний час та спосіб. Хтось осягає і пізнає Бога ще у ранньому віці, комусь до цього осягнення варто йти довгим шляхом. Та і в першому, і в другому випадку людський розум пізнає усю красу всього сотвореного Богом світу, показує, що Бог спрямовує все у світі до якоїсь найвищої мети – життя вічного.</p>\n<p>Сьогодні людина може дуже добре знати Святе Письмо, але це знання не наближає її до Бога, якщо вона не відкриває своє серце на поклик Бога.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2767:piznannja-boga-cherez-bozhu-ljubov-u-suchasnosti&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5086,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5546,'P','2015-06-02',1,NULL,'Note','Релігійні організації отримали право засновувати загальноосвітні школи','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6570,200,564665.jpg\" alt=\"\" width=\"200\" height=\"150\" />Нагадуємо, вперше ініціатива щодо права релігійними організаціями засновувати загальноосвітні навчальні заклади, прозвучала ще у 90-х рр. ХХ ст. Тоді і появилися перші такі навчальні заклад, які були засновані благодійними фондами або приватними особами. На сьогодні існує кілька десятків таких шкіл, які належать до різних конфесій, але їх офіційними засновниками не є релігійні організації.&nbsp;</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6570,200,564665.jpg\" alt=\"\" width=\"200\" height=\"150\" />Сьогодні, 2 червня, Верховна Рада України ухвалила Закон &laquo;Про внесення змін до деяких законів України щодо заснування релігійними організаціями навчальних закладів&raquo;. Ним надано релігійним організаціям, статути (положення) яких зареєстровано у встановленому законодавством порядку, право на створення навчальних закладів (дошкільних, загальноосвітніх, позашкільних, професійно-технічних, вищих).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Відповідний законопроект зареєстровано за<a href=\"http://w1.c1.rada.gov.ua/pls/zweb2/webproc4_1?pf3511=52915\" rel=\"nofollow\">№1447</a>. Його авторами виступили народні депутати з фракції &laquo;Народний фронт&raquo; Павло Унгурян, Лілія Гриневич та Віктор Єленський. Як&nbsp;<a href=\"http://rada.gov.ua/news/Novyny/110671.html\" rel=\"nofollow\">повідомляє портал ВРУ</a>, законопроект підтримали 237 народних депутатів.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Перед&nbsp;<a href=\"http://risu.org.ua/ua/index/all_news/community/church_and_school/59671/\">голосуванням законопроекту у першому читанні</a>&nbsp;голова Комітету з питань науки і духовності Лілія Гриневич зауважила: &ldquo;Сьогодні будь-яка людина, будь-який громадянин може самостійно засновувати приватний навчальний заклад, а релігійні організацій не можуть! І це є наслідок нашого пострадянського трактування принципу відокремлення держави від церкви. Так, держава не має права втручатися у справи церкви, а церква не повинна втручатися у справи держави, але вони можуть взаємодіяти. І найбільш успішною ця взаємодія в усьому світі є саме у сфері освіти&rdquo;, &ndash; підкреслила депутат.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Нагадуємо, вперше ініціатива щодо права релігійними організаціями засновувати загальноосвітні навчальні заклади, прозвучала ще у 90-х рр. ХХ ст. Тоді і появилися перші такі навчальні заклад, які були засновані благодійними фондами або приватними особами. На сьогодні існує кілька десятків таких шкіл, які належать до різних конфесій, але їх офіційними засновниками не є релігійні організації. Неодноразово подібні законопроекти, підтримані членами Всеукраїнської Ради Церков і релігійних організацій, блокувалися на урядовому або депутатському рівні. Підтриманий законопроект був зареєстрований у ВРУ у грудні 2014 року.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #444444; font-family: arial, helvetica, sans-serif; font-size: small;\">http://risu.org.ua/</span></p>',NULL,5087,5,1,NULL,42);
