INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5584,'P','2015-06-25',1,NULL,'Rss','Теорія і практика Духовних Вправ. Частина 16','<p align=\"center\"><strong><img src=\"http://askoldova-mohyla.org/uk/image,6600,200,se16.jpg\" style=\"margin: 10px; float: right;\" />Очищення – прощення</strong><o:p></o:p></p>\n<p>Християнство – релігія прощення: «І прости нам провини наші, як і ми прощаємо винуватцям нашим». У богомільному християнському середовищі часто можна почути розмови про прощення: я прощаю комусь якийсь гріх, поповнений щодо мене. Тільки що означають наші розмови про прощення? Що я не буду робити вендету, мститися. Але чи від такого прощення і таких балачок кривда й непрощення зникають з моєї душі й емоцій?<o:p></o:p></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2794:teorija-i-praktyka-duhovnyh-vprav-chastyna-16&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5129,NULL,1,1,26),
(5585,'P','2015-06-26',1,NULL,'Rss','Папа пропонує всім християнам святкувати Пасху разом у другу неділю квітня','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6612,200,8767.jpg\" />Католики готові відмовитись від святкування Великодня згідно своїх обчислень, щоб усі християни відзначали Пасху разом, заявив Папа Франциск.</p>\n<p>Нещодавно Папа Франциск висловив готовність та відкритість щодо обговорення єдиної дати святкування Великодня. Про це він заявив, звертаючись до священиків, зібраних у базиліці св. Йоана на Латерані під час міжнародних реколекцій, що мали місце у Римі. У духовних вправах взяли участь священики більш ніж 1000 священиків з 90 країн світу.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2801:papa-proponuje-vsim-hrystyjanam-svjatkuvaty-pashu-razom-u-drugu-nedilju-kvitnja&catid=32:gk-svit-cat&Itemid=58',5130,NULL,1,1,26),
(5586,'P','2015-06-26',1,NULL,'Rss','Теорія і практика Духовних Вправ. Частина 17','<p align=\"center\"><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6600,200,se17.jpg\" />Другий Тиждень</strong></p>\n<p>У першому тижні Духовних Вправ метод молитви називається медитацією, тут основну роль в молитві відіграє пам’ять, уява, розум. І тому цей метод ще якось можна описувати й пояснювати раціональним способом, тут можна застосовувати різні зауваження, технічні елементи тощо.</p>\n<p>У наступних тижнях метод молитви називається контемпляцією або спогляданням. Зовнішній розум затихає, пробуджуються внутрішні здатності людини. Але це не означає, що відбувається радикально якісний стрибок на вищий рівень. Медитація переплітається з контемпляцією постійно.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2795:teorija-i-praktyka-duhovnyh-vprav-chastyna-17&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5131,NULL,1,1,26),
(5587,'P','2015-06-26',1,NULL,'Rss','Брати Шептицькі – приклад гарних взаємин і поваги','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6614,200,445.jpg\" />Графи Софія та Іван Шептицькі виховували своїх синів у любові і вмінні пошановувати один одного.</p>\n<p>Митрополит Андрей, якого батьки назвали Романом-Олександром-Марією, був третьою дитиною у родині графів Софії та Івана. Свого найстаршого брата він не пам’ятав – Стефан помер у 2 роки. Другий брат Юрій від важкої недуги відійшов у вічність у юнацькі роки. Після Романа&nbsp;у сім’ї народились ще Станіслав, Олександр, Казимир, Леон.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2802:braty-sheptycki--pryklad-garnyh-vzajemyn-i-povagy&catid=28:tema-nomera-cat&Itemid=2',5132,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5588,'P','2015-06-26',1,NULL,'Note','5 прохань Папи Йоана Павла ІІ до української молоді','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6616,200,giovannipaolo.jpg\" alt=\"\" width=\"200\" height=\"130\" />&laquo;Папа вас дуже любить і дивиться на вас, як на сторожів нової зірниці надії. Він складає подяку Богові за вашу великодушність, з любов\'ю молиться за вас і від усього серця вас благословить&raquo;. Хай ця надія святого Папи на нас буде немарною!</span></p>\r\n<div id=\"_appInstalled\" style=\"text-align: justify;\">&nbsp;<span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">14 років тому Папа, нині уже проголошений святим, відвідав Україну. 26 червня 2001 року Іван Павло ІІ мав зустріч з українською молоддю у Львові. Те, про що він просив молодих людей на цій зустрічі, сьогодні здається ще актуальнішим!</span></div>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,6616,200,giovannipaolo.jpg\" alt=\"\" width=\"200\" height=\"130\" />14 років тому Папа, нині уже проголошений святим, відвідав Україну. 26 червня 2001 року Іван Павло ІІ мав зустріч з українською молоддю у Львові. Те, про що він просив молодих людей на цій зустрічі, сьогодні здається ще актуальнішим!</strong></span></p>\r\n<h1 style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">1. Служити сім\'ї та суспільству</span></h1>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Майбутнє України великою мірою залежить від вас і від тієї відповідальності, яку ви зумієте взяти на себе. Бог не забариться, щоб благословити ваші зусилля, якщо спрямуєте своє життя на великодушне служіння родині й суспільству, ставлячи спільне добро вище від особистих інтересів.</span></p>\r\n<h1 style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">2. Не боятися важчих шляхів</span></h1>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Вибирайте вузьку дорогу, &ndash; ту дорогу, яку Господь вказує вам через свої заповіді. Це слова правди й життя. Дорога, яка часто здається широкою й зручною, потім виявляється облудною та хибною.</span></p>\r\n<h1 style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">3. Не ставити споживацтво вище за Бога</span></h1>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Не переходьте від неволі комуністичного режиму до неволі споживацтва, що є іншою формою матеріалізму, який, хоч і на словах не відкидає Бога, однак заперечує Його фактами, виключаючи Його з життя. Без Бога не зможете зробити нічого доброго. З Його допомогою, навпаки, ви зможете сміливо вийти назустріч усім викликам сьогодення.</span></p>\r\n<h1 style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">4. Любити Церкву</span></h1>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Любіть Церкву! Вона є вашою родиною й духовним храмом, живим камінням якого ви покликані бути. На вашій землі Церква має особливо привабливе обличчя через розмаїття традицій, що її збагачують. Прямуйте і зростайте у дусі братерства, з\'єднані так, як сьогодні, щоб відмінні традиції не були приводом для поділу, а радше заохотою до взаємного пізнання й взаємної пошани.</span></p>\r\n<h1 style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">5. Приймати Христа у Причасті</span></h1>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Вашою духовною поживою нехай буде Євхаристійний Хліб, нехай буде Христос. Приймаючи Його у Пресвятій Євхаристії, назавжди залишитеся у Його любові й принесете щедрі плоди. І якщо інколи дорога стане крутою, якщо дорога вірності Євангелію буде здаватися вам занадто зобов\'язуючою, бо вимагатиме жертви відважних виборів, пригадайте про нашу зустріч.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>Р. S. Тоді, на завершення зустрічі, Йоан Павло ІІ сказав: &laquo;Папа вас дуже любить і дивиться на вас, як на сторожів нової зірниці надії. Він складає подяку Богові за вашу великодушність, з любов\'ю молиться за вас і від усього серця вас благословить&raquo;. Хай ця надія святого Папи на нас буде немарною!</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em><span style=\"color: gray;\"><a href=\"http://dyvensvit.org/articles/105683.html\" target=\"_blank\">ДивенСвіт</a></span></em></span></p>\r\n<p style=\"text-align: justify;\"><br /><br /><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Джерело:&nbsp;<a href=\"http://www.credo-ua.org/2015/06/137275\">CREDO</a></span></p>\r\n<div id=\"_appInstalled\">&nbsp;</div>',NULL,5133,5,1,NULL,44);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5589,'P','2015-06-29',1,NULL,'Rss','Новомученики українського народу','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6616,200,109.jpg\" />Під час пастирського візиту Святішого Отця Івана Павла II в Україну було беатифіковано українських новомучеників. Празник священномученика єп. Миколая Чарнецького і двадцяти чотирьох співмучеників та священномученика Омеляна Ковча призначено святкувати 14 червня (за григоріянським стилем — 27).</p>\n<p>Почитання святих — це справді неоціненний скарб і багатство Христової Церкви. І ствердження святости тих чи инших її членів є ще одним видимим знаком славної та сповненої могутности Божої присутности серед людей. Адже ми віримо в Бога, Який не тільки створив світ з нічого і премудро облаштував його, але стверджуємо також і те, що у Своїй силі й розумі Він ніколи не перестає піклуватися Своїм творінням. Ми віримо в те, що Господь, одного разу ставши людиною для нашого відкуплення і визволення від гріха, продовжує і сьогодні вирівнювати стежки цілого людства тими чудесами і знаками, які Він чинить через тих, на кому видимо спочиває Його величне уподобання.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2803:novomuchenyky-ukrajinskogo-narodu&catid=28:tema-nomera-cat&Itemid=2',5134,NULL,1,1,26),
(5590,'P','2015-06-29',1,NULL,'Rss','Чи ті реформи?','<p><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6616,200,8978.jpg\" />Співзасновник економічного форуму в Давосі, почесний член Римського клубу, учасник групи “Першого грудня”, один із найзнаменитіших українських економістів Богдан Гаврилишин – нечастий гість на батьківщині, в Україні. Наприкінці Другої світової війни він разом із батьками потрапив до Німеччини, а потім – до Канади. Закінчивши університет у Торонто, подався у Швейцарію і зробив там успішну кар’єру. Гаврилишин майже 30 років пропрацював у Міжнародному інституті менеджменту в Женеві, де здобув ступінь доктора наук із філософії та економіки. На його рахунку – дві книжки і близько 100 статей про проблеми менеджменту. За час незалежності України був радником кількох українських президентів і прем’єр-міністрів. Останнім часом спеціалізується на темах інновацій та енергоефективності в економіці. У бесіді з Forbes 88-річний Гаврилишин розповів про економічні перспективи нашої країни.</em>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2804:chy-ti-reformy&catid=37:spilnota-cat&Itemid=49',5135,NULL,1,1,26),
(5591,'P','2015-06-30',1,NULL,'Rss','Хто зайвий на планеті Земля?','<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6618,200,R8qe4I4Zpx4.jpg\" />Періодично перегортаючи листки календаря, для того, щоб зрозуміти чим живе суспільство і які події відзначаються на різних рівнях та з різних нагод, можна помітити, що в календарі з’являються певні дати, які заставляють трішки призупинитись та призадуматись. Однією з таких нагод для певних роздумів стало 11 липня, – день, який міжнародна спільнота відзначає як «Всесвітній день народонаселення».</em></p>\n<p>Звісно, якщо почати, чисто по-людськи, розмірковувати над тим, як цей день потрапив у календар міжнародних подій, то можна частково зрозуміти, що стало причиною. Ні для кого не буде новиною, що саме стурбованість ООН різким приростом населення на нашій планеті спричинилась до того, що 11 липня 1987 року було ухвалено відзначати цей<em>«World Population Day»</em> на міжнародному рівні.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2805:hto-zavy-na-planeti-zemlja&catid=28:tema-nomera-cat&Itemid=2',5136,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5592,'P','2015-06-30',1,NULL,'Rss','Три ознаки віруючого','<p><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6618,200,Six.jpg\" />«Світло тіла – око. Як, отже, твоє око здорове, все тіло твоє буде світле.&nbsp;А коли твоє око лихе, все тіло твоє буде в темряві. Коли ж те світло, що в тобі, темрява, то темрява – якою ж великою буде!&nbsp;Ніхто не може двом панам служити: бо або одного зненавидить, а другого буде любити, або триматиметься одного, а того знехтує. Не можете Богові служити – і мамоні.&nbsp;Ось чому кажу вам: Не турбуйтеся вашим життям, що вам їсти та що пити; ні тілом вашим, у що одягнутись.</em></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2806:try-oznaky-virujuchogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5137,NULL,1,1,26),
(5593,'P','2015-07-01',1,NULL,'Rss','Митрополит Шептицький інтуїтивно розумів сутність мистецтва','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6620,200,Moise.jpg\" />Модест Сосенко, Олекса Новаківський, Михайло Бойчук, Ювеналій Мокрицький, Юліан Буцманюк і ще понад двадцять відомих українських художників навчались у європейських школах за фінансової підтримки митрополита Української греко-католицької церкви Андрея Шептицького. Митрополит не лише був мистецтвознавцем, а й меценатом культури.</p>\n<p>Якось один священик написав митрополитові, що він, його жінка і діти, а ще парафіяни оглядали ікону Модеста Сосенка і переконались, що цей «образ ні до чого», нема у ньому нічого «артистичного». Митрополит ікону забрав і віддав в інший храм, а Модест Сосенко й надалі отримував замовлення церкви, згідно з волею митрополита, попри консерватизм багатьох отців. Митрополит Андрей дивився набагато років вперед і старався модернізувати сакральне мистецтво, але гармонійно зі східною традицією.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2807:mytropolyt-sheptycky-intujityvno-rozumiv-sutnist-mystectva&catid=28:tema-nomera-cat&Itemid=2',5138,NULL,1,1,26),
(5594,'P','2015-07-01',0,NULL,'Note','Літній родинний патріотично-християнський табір «Канікули з Богом»','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6622,200,aImHQ5-300x300.jpg\" alt=\"\" width=\"200\" height=\"200\" />Запрошуємо парафіяльних дітей з батьками до участі у таборі , який буде проводитися з <span style=\"text-decoration: underline;\">03.08.15 по13.08.1</span>5 в м. Ясеня (Драгобрат).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;Табір присвячений <em>150-річчю з дня народження митрополита Андрея Шептицького.</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Оплата - 1400 грн. (проживання, харчування -10 днів).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Контактний телефон - 0979798349 п.Оксана Ясніцька</span></p>\r\n<div id=\"_appInstalled\">&nbsp;</div>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6622,200,aImHQ5-300x300.jpg\" alt=\"\" width=\"200\" height=\"200\" />Запрошуємо парафіяльних дітей з батьками до участі у таборі , який буде проводитися з <span style=\"text-decoration: underline;\">03.08.15 по13.08.1</span>5 в м. Ясеня (Драгобрат).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;Табір присвячений <em>150-річчю з дня народження митрополита Андрея Шептицького. </em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">В програмі відпочинку:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">ранкова зарядка;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Служба Божа;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">катехитичні заняття для дітей та дорослих;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">музичні заняття для дітей;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">походи в гори;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">веселе дозвілля.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Оплата - 1400 грн. (проживання, харчування -10 днів).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">З собою мати:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">молитовник; вервичку; вишиванку; зручний одяг (змінне взуття, дощовик, гумові чоботи)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Контактний телефон - 0979798349 п.Оксана Ясніцька</span></p>\r\n<div id=\"_appInstalled\">&nbsp;</div>',NULL,5139,5,1,NULL,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5595,'P','2015-07-02',1,NULL,'Rss','Президент підписав закон, який дозволяє релігійним громадам засновувати навчальні заклади','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6622,200,46b.jpeg\" />Президент України підписав Закон України №498-VIII \"Про внесення змін до деяких законів України щодо заснування релігійними організаціями навчальних закладів\". Відповідний закон був прийнятий Верховною Радою України 2 червня 2015 року.</p>\n<p>Відповідно до цього закону, релігійні організації, які зареєстровані у встановленому законодавством порядку, можуть бути засновниками (власниками) вищих, професійно-технічних, загальноосвітніх та дошкільних навчальних закладів.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2809:prezydent-pidpysav-zakon-jaky-dozvoljaje-religinym-gromadam-zasnovuvaty-navchalni-zaklady&catid=27:gk-v-ukrajini-cat&Itemid=57',5140,NULL,1,1,26),
(5596,'P','2015-07-02',1,NULL,'Rss','Предстоятель УГКЦ у Берліні розвінчує міфи російської пропаганди','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6622,200,unname.jpg\" />Глава Української Греко-Католицької Церкви Блаженніший Святослав під час зустрічі з паном Крістофом Хойсгеном, радником Канцлера Німеччини з питань зовнішньої політики та безпеки, у рамках візиту ВРЦіРО до Німеччини розвінчав два найбільших міфи російської пропаганди.</p>\n<p>«Першим таким міфом, – розповів Глава греко-католиків, – є те, що в Україні йде громадянська війна. Ми хочемо вам засвідчити, як голос просто українського народу, що в нас немає громадянського протистояння.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2808:predstojatel-ugkc-u-berlini-rozvinchuje-mify-rosiskoji-propagandy&catid=32:gk-svit-cat&Itemid=58',5141,NULL,1,1,26),
(5597,'P','2015-07-07',1,NULL,'Rss','У БФ «Карітас-Київ» відбулася зустріч президента Карітасу Америки з вимушеними переселенцями','<p><img style=\"margin: 10px; float: left;\" alt=\"290170\" height=\"131\" width=\"175\" src=\"http://oranta.org/images/stories/290170.jpg\" />«Днями у БФ «Карітас Київ» відбулася зустріч представників Карітасу Америки з вимушеними переселенцям зі Сходу&nbsp; України.&nbsp; Бесіда проходила в теплій, навіть домашній&nbsp; атмосфері. Сімї з дітьми, одинокі матері, люди на інвалідних&nbsp; візках – всі вони щиро поділилися з гостями&nbsp; своїми проблемами&nbsp; і переживаннями, розповіли кожен свою історію життя, свою історію долі, понівечену війною», - повідомляє о. Роман Сиротич, директор БФ «Карітас Київ».</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2810:2015-07-08-18-58-41&catid=27:gk-v-ukrajini-cat&Itemid=57',5142,NULL,1,1,26),
(5598,'P','2015-07-08',1,NULL,'Rss','Всеукраїнська та молодіжна проща до Зарваниці 2015 року (Програма)','<p><strong><img style=\"margin: 10px; float: right;\" alt=\"zarvan2\" height=\"117\" width=\"200\" src=\"http://oranta.org/images/stories/zarvan2.jpg\" />Всеукраїнська та молодіжна проща, яка збирає щороку десятки тисяч прочан з України та закордону, відбудеться у Марійському духовному центрі Зарваниця 18-19 липня. До чудотворної ікони Зарваницької Божої Матері вирушають паломники з різних куточків України та закордону, щоб у цей скрутний для кожного українця час, коли вороги зазіхають на нашу землю, її цілісність, підносити свої молитви до милостивого Бога за мир, спокій в Україні та збереження життя воїнів.</strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2811:vseukrajinska-ta-molodizhna-proshcha-do-zarvanyci-2015-roku-programa&catid=27:gk-v-ukrajini-cat&Itemid=57',5143,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5599,'P','2015-07-09',1,NULL,'Rss','«Ворог хоче позбавити нас радості. Тому ми відкриваємо другий фронт!»','<p><img src=\"http://oranta.org/images/stories/20-05-15-1.jpg\" width=\"150\" height=\"150\" alt=\"20-05-15-1\" style=\"margin: 10px; float: left;\" />23-26 липня 2015 року у Львові відбудеться молодіжний християнський фестиваль «Вітер На-дії». Цього року організатори вже вдруге випробують саме такий формат спілкування Церкви і молоді, але вже з новими акцентами. Про особливості заходу та важливість подібних зустрічей для Духовної величі Львова розповіла член оргкомітету «Вітру На-Дії» Ірина Максименко.</p>\n<p><strong>- Ірино, фестиваль «Вітер На-Дії» цього літа матиме дещо інший формат. Що можеш розказати про його особливості ?</strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2812:2015-07-09-18-27-44&catid=37:spilnota-cat&Itemid=49',5144,NULL,1,1,26),
(5600,'P','2015-07-10',1,NULL,'Rss','О квестії соціальній - пастирське послання Митрополита Андрея до духовенства','<p><em><img style=\"margin: 10px; float: right;\" alt=\"1139318\" height=\"175\" width=\"129\" src=\"http://oranta.org/images/stories/2010/05/1139318.jpg\" />Дух Христа – духом демократичним</em></p>\n<p>Демократичний рух, що в цілій Європі під стяг оборони убогих і скривджених покликує всіх благородних, не єсть для Церкви чужим, – противно, єсть для духовенства всіх країв симпатичним, бо і дух Христового Євангелія єсть духом щиро і широко демократичним.</p>\n<p>Опіка над убогими і оборона тих, що самі себе боронити не вміють, рівність-братерство людей – се лиш право справедливости і любови ближнього, на котрім стоїть цілий євангельський закон.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2814:o-kvestiji-socialni-pastyrske-poslannja-mytropolyta-andreja-do-duhovenstva&catid=28:tema-nomera-cat&Itemid=2',5145,NULL,1,1,26),
(5601,'P','2015-07-10',1,NULL,'Rss','Митрополит Шептицький та його послання «О квестії соціальній»','<p><img src=\"http://oranta.org/images/stories/2010/05/images.jpg\" width=\"200\" height=\"100\" alt=\"images\" style=\"margin: 10px; float: left;\" />Щоб зрозуміти Митрополита, нам треба зануритися зовсім в іншу епоху, хоч, припускаю, ми вже ніколи не зможемо пізнати, як резонували ті чи ті думки Шептицького і його ровесників у параметрах тогочасного суспільства. Хочемо того чи ні, ми «прочитуємо» Шептицького очима людини початку ХХІ століття.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2813:2015-07-10-05-42-57&catid=28:tema-nomera-cat&Itemid=2',5146,NULL,1,1,26),
(5602,'P','2015-07-10',1,NULL,'Rss','«В’язень у стократ гарячіший каянник, аніж будь-хто з нас», – о. Костянтин Пантелей','<p><img style=\"margin: 10px; float: right;\" alt=\"338\" height=\"114\" width=\"200\" src=\"http://oranta.org/images/stories/338.jpg\" />Чи може злочинець, який одного разу вже уявив себе володарем життя і спробував вершити долі інших, щиро покаятися та змінитися? Оскільки пенітенціарна ідея охоплює не лише покарання порушників закону, а й шлях до їхнього покаяння, сьогодні світ практикує душпастирську опіку над в’язнями. Головні напрями служіння капеланів – це проповідь, катехизація й уділення святих таїнств. У служінні разом із душпастирями можуть брати участь і миряни, демонструючи правопорушникам інший вимір життя, сповнений любові та поваги до інших. Кажуть, немає стіни, крізь яку не пройшов би Ісус, і немає камери, до якої би Він не зміг увійти. Тому так важливо дати в’язням шанс на віднайдення себе в Бозі.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2815:2015-07-10-10-01-02&catid=37:spilnota-cat&Itemid=49',5147,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5603,'P','2015-07-11',1,NULL,'Rss','Святі Апостоли Петро і Павло','<p><img style=\"margin: 10px; float: left;\" alt=\"6987789\" height=\"120\" width=\"200\" src=\"http://oranta.org/images/stories/6987789.jpg\" />Неможливо говорити про день свята Петра й Павла без екуменічних алюзій.</p>\n<p>Чим є більшість проблем, розколів, труднощів у християнстві? Сварками, обумовленими історико-культурними та особистісними моментами. До цього додаються доктринальні проблеми, відмова у покорі й «застигання» таких становищ, подібне до контрактур на переламах. Хірурги знають: коли зламані кості неправильно зростаються, їх потрібно знову ламати, щоби скласти правильно… Отож і процес розламів та примирень у Церкві зовсім не є солодким.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=2816:svjati-apostoly-petro-i-pavlo&catid=35:istorychni-postati-cat&Itemid=48',5148,NULL,1,1,26),
(5604,'P','2015-07-16',1,NULL,'Rss','«Непрощення – це гачок, за який часто чіпляється диявол» - І','<p><strong><img src=\"http://oranta.org/images/stories/1835.jpg\" width=\"200\" height=\"133\" alt=\"1835\" style=\"margin: 10px; float: right;\" /></strong>Інтерв’ю з екзорцистом отцем Даріушом Галянтом ОМІ, який на кілька днів завітав до України. Користуючись нагодою, ми розпитали священика про особливості його служіння та методи, які використовує злий у наші часи.</p>\n<p><strong>Отче, на початку розкажіть трохи про себе, аби читачі мали уявлення, з ким мають справу.</strong></p>\n<p>Я належу до Згромадження Місіонерів Облатів Непорочної Марії. У цій монаршій родині я вже 25 років. Мені доводилося працювати у Вроцлаві, Кодні, Познані. Протягом останніх 4 років я служу в Любоні (поблизу Познані) як капелан Сестер Служебниць Непорочної Діви Марії. Сестри мають тут свій генеральний дім. Крім того, я відповідаю за санктуарій бл. Едмунда Бояновського. Це є, так би мовити, санктуарій дітей та родин.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2817:2015-07-16-17-57-04&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5149,NULL,1,1,26),
(5605,'P','2015-07-16',1,NULL,'Rss','9 правил виживання на війні і після неї','<p><strong><img style=\"margin: 10px; float: left;\" alt=\"zx450\" height=\"128\" width=\"230\" src=\"http://oranta.org/images/stories/zx450.jpg\" />Практичні поради як допомогти військовим, що повернулися з зони АТО, тим, котрі перебувають на ротації, а також їхнім близьким розповіла с.Неля Куцак, консультант з методів позитивної психотерапії, учасник кризової психологічної служби Вінниці, член Української асоціації фахівців з подолання наслідків психотравмуючих подій.</strong></p>\n<p>«Війна дуже змінює людей, з фронту вони приходять зовсім іншими. І лише знаючи ці особливості, військових можна врятувати від проблем з собою, від проблем в сім’ї, алкоголізму і, навіть, смерті»,— вважає с.Неля.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2818:9-pravyl-vyzhyvannja-na-vini-i-pislja-neji&catid=37:spilnota-cat&Itemid=49',5150,NULL,1,1,26),
(5606,'P','2015-07-16',1,NULL,'Rss','Хто, як не ви?!','<p><img style=\"margin: 10px; float: right;\" alt=\"fullsize\" height=\"141\" width=\"230\" src=\"http://oranta.org/images/stories/fullsize.jpg\" />У своїй історії Україна добре пізнала, що таке поразки, але, схоже, так і не навчилася розуміти їхнього сенсу. Замість шукати глибинних причин шукаємо ворогів – благо, їх завжди довкола достатньо. Задаємо хибні питання, а тому щоразу отримуємо хибні відповіді.</p>\n<p>Потрібне свіже бачення, або те, що сучасною мовою називається «інноваційний підхід». Як у давньокиївських казках: поки ти стинаєш змієві голови, доти програєш. А виграєш лише тоді, коли знайдеш під дубом скриньку, а в ній – голку, заховану в яйці; а в ній – Кощеєву смерть. Наші казки просто сповнені отими інноваційними підказками: не дій рутинно; шукай не там, де шукаєш традиційно. Так наші предки передавали своїм нащадкам загадкову формулу нашого успіху.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2819:hto-jak-ne-vy&catid=37:spilnota-cat&Itemid=49',5151,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5607,'P','2015-07-18',1,NULL,'Rss','«Непрощення – це гачок, за який часто чіпляється диявол» - ІІ','<p><strong><img src=\"http://oranta.org/images/stories/otec_0.jpg\" width=\"171\" height=\"220\" alt=\"otec_0\" style=\"margin: 10px; float: left;\" />Друга частина інтерв’ю з екзорцистом отцем Даріушом Галянтом ОМІ, який на кілька днів завітав до України. Священик розповів про \"вікна\" якими в наше життя може проникнути злий, а також розповів, що робити, аби не потрапити в його тенета.</strong></p>\n<p><strong>Отче, тож яким чином злий дух може входити в наше життя?</strong></p>\n<p>Важливо підкреслити, що злий дух може увійти у наше життя як свідомо, так і несвідомо. Свідомо запрошуємо диявола у наше життя, коли укладаємо з ним договір.</p>\n<p><strong>Справді? Мені здавалося, що це можливо лише у фільмах.</strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2820:2015-07-18-19-43-43&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5152,NULL,1,1,26),
(5608,'P','2015-07-20',1,NULL,'Rss','«Від сьогодні вся Вселенська Церква називає митрополита Андрея Шептицького праведним»','<p><strong><img style=\"margin: 10px; float: right;\" alt=\"metrandreysheptytsky\" height=\"250\" width=\"202\" src=\"http://oranta.org/images/stories/metrandreysheptytsky.jpg\" />Папа Франциск підписав декрет, яким усьому світові проголосив геройські чесноти митрополита Андрея Шептицького. Таким чином напередодні прощі до Зарваниці завершився найважливіший етап його прославлення. Від сьогодні вся Вселенська Церква називає його праведним. І закликає всіх наслідувати його чесноти.</strong></p>\n<p>Про це сьогодні під час проповіді до вірних у Зарваниці сказав Отець і Глава УГКЦ Блаженніший Святослав.</p>\n<p>Предстоятель відзначив, що цьогорічна Всеукраїнська проща до Зарваниці також була присвячена великій постаті в історії нашої Церкви – особі, відомій сучасникам як Велет Святоюрської гори. «Ми сьогодні особливо молитовно згадуємо Великого митрополита слугу Божого Андрея Шептицького. За десять днів ми відзначатимемо 150-ту річницю з дня його народження», – сказав архиєрей.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2821:2015-07-20-06-33-29&catid=28:tema-nomera-cat&Itemid=2',5153,NULL,1,1,26),
(5609,'P','2015-07-21',1,NULL,'Rss','Мігрант — це людина, а не проблема, яку треба розв\'язати','<p><strong><img style=\"margin: 10px; float: left;\" alt=\"05-12-13-2\" height=\"156\" width=\"250\" src=\"http://oranta.org/images/stories/2010/05/05-12-13-2.jpg\" />На глобалізацію явища міграції слід відповісти глобалізацією солідарності та винахідливістю любові.&nbsp;Трагедії, які сьогодні супроводжують тих, що намагаються дістатись до європейського континенту, взивають до сумління людини. Мігрант не є проблемою, яку слід розв’язати, ворогом, якого слід подолати чи нападником, від якого потрібно захиститися. Насамперед – це конкретна людська особа, з притаманною їй гідністю, яку слід шанувати і захищати. І якщо прийняття мігрантів є, насамперед, християнським моральним обов’язком, справжнім викликом, який стоїть перед Церквою в Європі, є виклик виховання.</strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2822:migrant--ce-ljudyna-a-ne-problema-jaku-treba-rozvjazaty&catid=37:spilnota-cat&Itemid=49',5154,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5610,'P','2015-07-21',1,NULL,'Rss','Олена Теліга: «Життя - це боротьба, а боротьба - це справжнє життя»','<p><strong><img style=\"margin: 10px; float: right;\" alt=\"52616955\" height=\"183\" width=\"200\" src=\"http://oranta.org/images/stories/2010/05/52616955.jpg\" />22 лютого Телігу було розстріляно в Бабиному Яру. В камері, де вона перебувала перед розстрілом, знайшли напис, зроблений її рукою: \"Тут сиділа і звідти йде на розстріл Олена Теліга\". Зверху було викарбувано стилізований під меч тризуб.</strong></p>\n<p>Олена Теліга народилася&nbsp;<a href=\"http://www.istpravda.com.ua/ed/main-frame/articles/4e2856cfb59a7/bookmark_article/\" target=\"_blank\">21 липня</a> 1907 року. Імперський Санкт-Петербург став містом її дитинства і юності. Олена походила з української родини. її батько - Іван Шовгенів, родом із околиць степового Слов\'янська, був інтелігентною й освіченою людиною. За фахом - інженер, спеціаліст з гідротехніки.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2823:2015-07-21-09-47-34&catid=37:spilnota-cat&Itemid=49',5155,NULL,1,1,26),
(5611,'P','2015-07-25',1,NULL,'Rss','Папа: справжній християнин – це той, хто слухає Божі слова і впроваджує їх у життя','<p><img style=\"margin: 10px; float: left;\" alt=\"pope_francis_02_25492000\" height=\"159\" width=\"250\" src=\"http://oranta.org/images/stories/pope_francis_02_25492000.jpg\" /></p>\n<p>Люди відчувають, коли душпастир є послідовним, що дає йому авторитет. Це була одна із думок проповіді, виголошеної Святішим Отцем Франциском під час ранішньої Служби Божої у ватиканській резиденції «Дім Святої Марти» у четвер, 25 червня 2015 року.</p>\n<p>Із прочитаного євангельського уривку, де розповідалось про те, як Ісус навчав народ, дізнаємось, що люди були приємно здивовані словами Ісуса Христа, бо Він навчав як повновладний, а не як законовчителі та книжники.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2833:papa-spravzhni-hrystyjanyn-ce-to-hto-sluhaje-bozhi-slova-i-vprovadzhuje-jih-u-zhyttja&catid=32:gk-svit-cat&Itemid=58',5160,NULL,1,1,26),
(5612,'P','2015-07-22',1,NULL,'Rss',' Посвячуючись східній церкві, я мав на оці лиш мотиви всесвітового характеру – митрополит Шептицький','<p><img style=\"margin: 10px; float: left;\" alt=\"DE339E07\" height=\"250\" width=\"156\" src=\"http://oranta.org/images/stories/DE339E07.jpg\" />Папа Лев ХІІІ без вагань призначив на Галицький престіл 35-річного єпископа Андрея Шептицького</p>\n<p><strong>Львів – Чому Андрей Шептицький з римо-католицької родини обрав саме монастир Української греко-католицької церкви, а потім став єпископом і очолив цю церкву? Довший час це запитання виникало у дослідників і у суспільстві&nbsp;як за життя митрополита, так і до нині. Як і те, чому саме Ватикан, зокрема Папа Лев ХІІІ, зупинив свій вибір саме на 35-річному єпископі Шептицькому?</strong></p>\n<p>Тато митрополита Андрея – Іван Шептицький – походив із давнього українського шляхетного роду, родина ополячилась, латинізувалася. Граф вважав себе поляком, але у 1911 році, коли його син уже 10 років очолював УГКЦ, сказав, що усвідомлює, що він українець. Полькою була Софія Фредро-</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2827:-posvjachujuchys-shidni-cerkvi-ja-mav-na-oci-lysh-motyvy-vsesvitovogo-harakteru--mytropolyt-sheptycky&catid=28:tema-nomera-cat&Itemid=2',5156,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5613,'P','2015-07-24',1,NULL,'Rss','Перші християни на Русі','<p><img style=\"margin: 10px; float: right;\" alt=\"8250_1\" height=\"250\" width=\"195\" src=\"http://oranta.org/images/stories/8250_1.jpg\" />Як відомо, підвалини державного й церковного життя на нашій землі були закладені в Києві&nbsp;— «матері городів руських».<br /> Є припущення, що в VI ст. на берегах Дніпра започаткували своє існування племенні князівства полян, кривичів, сіверян та ільменських слов’ян. У VIІ-VIII ст. Руська держава зміцніла, заснувавши місто Київ трьома братами Києм, Щеком і Хоривом, які стояли на чолі східно-слов’янських племен і полян. Припускають, що Кий був реальною історичною особою&nbsp;— полянським князем, який жив наприкінці VI&nbsp;— на початку VII ст.</p>\n<p>Давній літопис 862 р. оповідає, як до міста Києва прийшли два варяги Аскольд і Дір й заволоділи ним і землею полян, які платили дань кочовим хозарським племенам. Кияни тоді мали добірне військо і ходили походами вниз по Дніпру, навіть до столиці Візантії. Це було близько 866 р.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2828:pershi-hrystyjany-na-rusi&catid=28:tema-nomera-cat&Itemid=2',5157,NULL,1,1,26),
(5614,'P','2015-07-24',1,NULL,'Rss','Свята княгиня Ольга – що ми знаємо про неї?','<p><strong><img style=\"margin: 10px; float: left;\" alt=\"knjahynja_oljga2\" height=\"166\" width=\"250\" src=\"http://oranta.org/images/stories/knjahynja_oljga2.jpg\" /></strong></p>\n<p><strong>Це, певно, найвідоміша особа жіночої статі за всю нашу писану та неписану історію</strong><em></em></p>\n<p align=\"right\"><em>«О, богоблаженна Ольго, помолися за твоїх дітей Богу; мир міцний для Вітчизни нашої виблагай і відпущення гріхів усім, хто завжди прославляє тебе!» (із молитви до святої рівноапостольної княгині Ольги)</em></p>\n<p>Липень, 24-й день. Преставлення святої і блаженної рівноапостольскої великої княгині київської Ольги, у святому хрещенні Єлени.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=2829:svjata-knjagynja-olga--shcho-my-znajemo-pro-neji&catid=28:tema-nomera-cat&Itemid=2',5158,NULL,1,1,26),
(5615,'P','2015-07-24',1,NULL,'Rss','Шана і вдячність нашому парохові отцю настоятелю Ігорю Іллі Онишкевичу ','<p style=\"text-align: center;\"><img style=\"margin: 10px; float: right;\" alt=\"CRW_6376\" height=\"189\" width=\"283\" src=\"http://oranta.org/images/stories/2009/02/CRW_6376.jpg\" />Цією грамотою громада церкви св. Миколая Чудотворця на Аскольдовій могилі висловлює високодостойному парохові отцю-настоятелю</p>\n<p style=\"text-align: center;\"><strong>Ігорю Іллі Онишкевичу</strong></p>\n<p style=\"text-align: center;\">з нагоди його уродин свою незмінну й глибоку шану і вдячність!</p>\n<p>Довгі роки Вашої відданої праці на користь нашої парафії і храму виплекали з нас справжню християнську сім’ю, неповторну своїми унікальними молитовними традиціями, спільнотами, об’єднаннями. Тут знаходять справжню духовну поживу батьки і діти, молодь і родини, кожний, хто шукає і прагне Христа.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=2830:shana-i-vdjachnist-nashomu-parohovi-otcju-nastojatelju-igorju-illi-onyshkevychu-&catid=25:parafija-cat&Itemid=27',5159,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5616,'P','2015-07-25',1,NULL,'Rss','Навіщо благодійність','<p><img src=\"http://oranta.org/images/stories/2009/02/654.jpg\" width=\"250\" height=\"150\" alt=\"654\" style=\"margin: 10px; float: right;\" />Навіщо подавати милостиню в цивілізованому суспільстві? Я не проти благодійницького складу мислення. Не дай Боже, щоб ми коли-небудь зачерствіли до потреб ближнього. Людське співчуття є занадто тонкою матерією, щоб перетворитися на холодний розрахунок. Можна сказати, що більшість великих досягнень мали мотивом людське співчуття. Саме щоб допомогти людям, беруться за всі&nbsp; відомі справи створення нових видів послуг.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2831:navishcho-blagodinist&catid=29:propovidi-cat&Itemid=34',5161,NULL,1,1,26),
(5617,'P','2015-07-27',1,NULL,'Rss','Креститель Руси-України','<p><img src=\"http://oranta.org/images/stories/vladimir_prince10.jpg\" width=\"212\" height=\"250\" alt=\"vladimir_prince10\" style=\"margin: 10px; float: left;\" />З іменем святого рівноапостольного великого князя Володимира нерозрив­но пов’язана визначна подія в історії нашого народу – Хрещення Русі. Проте християни на Русі жили й раніше – за часів панування язичництва. Так, хри­стиянкою була бабуся князя Володи­мира княгиня Ольга, яка сподобилася слави святих рівноапостольних жінок.</p>\n<p>Святитель Димитрій Ростовський, описуючи житіє святого князя Во­лодимира, процес християнізації на­шого народу умовно ділить на чотири періоди. Вперше слов’янські племена, що жили на території майбутньої Київської Русі, почули євангельську проповідь від свя­того апостола Андрія Первозванного ще в І ст. після Р. X. Святий Андрій пройшов від Криму через усю країну до майбутнього Новгорода, всюди хре­стячи наших предків та наставляючи їх у вірі Христовій. На київських па­горбах він провістив заснування вели­кого міста, забудованого численними християнськими храмами.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2832:krestytel-rusy-ukrajiny&catid=28:tema-nomera-cat&Itemid=2',5162,NULL,1,1,26),
(5618,'P','2015-07-28',1,NULL,'Rss','Страху немає у любові','<p><img style=\"margin: 10px; float: right;\" alt=\"VtPDXk5MWPk\" height=\"167\" width=\"250\" src=\"http://oranta.org/images/stories/2008/VtPDXk5MWPk.jpg\" />Кожного дня ми чуємо про кровопролиття, хвороби, ДТП, нестабільність, різного роду терпіння… Більшість з нас переживає: а що на мене чекає завтра? Через наші переживання за завтрашній день у наше життя вселяється страх, який паралізує нашу свідомість, життя і віру. Через страх у наше серце закрадаються сумніви, розчарування, депресія і навіть різні хвороби, які пов’язані з нервовою системою. Як ж тоді боротися зі страхом, до кого звернутися, щоб бути впевненим у завтрашньому дні?</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2826:strahu-nemaje-u-ljubovi&catid=28:tema-nomera-cat&Itemid=2',5163,NULL,1,1,26),
(5619,'P','2015-07-29',1,NULL,'Rss','Дружба... чи маємо ми друзів?','<p><strong><img style=\"margin: 10px; float: left;\" alt=\"1357648591_4\" height=\"184\" width=\"250\" src=\"http://oranta.org/images/stories/2008/1357648591_4.png\" />Чи вони є справді моїми друзями, чи просто знайомими. Чи я є справжнім другом?</strong></p>\n<p>Дуже часто постають ці питання, коли ти у важкій ситуації, лікарні чи скруті (біді) і здається, що це тягнеться цілу вічність.</p>\n<p>Роздумуючи про дружбу, згадуєш багато історій та повчань про неї: якою є справжня дружба, якими мають бути друзі.</p>\n<p>Мимоволі починаєш прирівнювати усіх, кого вважаєш своїм другом, до критеріїв, описаних в цих історіях.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2825:druzhba-chy-majemo-my-druziv&catid=37:spilnota-cat&Itemid=49',5164,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5620,'P','2015-07-30',1,NULL,'Rss','7 рис зрілого зв’язку','<p><strong><img style=\"margin: 10px; float: right;\" alt=\"d2067041667\" height=\"166\" width=\"250\" src=\"http://oranta.org/images/stories/2008/d2067041667.jpg\" />Щастя — це не кінцевий пункт, а спосіб подорожувати. Можна сказати, що зрілість це не так мета для осягнення, як спосіб життя. Поглянь, на яких рисах треба будувати зріле подружжя.</strong></p>\n<p><strong>1. Прозорість</strong></p>\n<p>У нашому житті часто буває так, що ми не можемо бачити ясно; в певних моментах і в певних ситуаціях нам бракує здатності визначити, що потрібно зробити. Причина проста: деякі аспекти проблеми випадають із нашої уваги.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2824:7-rys-zrilogo-zvjazku&catid=37:spilnota-cat&Itemid=49',5165,NULL,1,1,26),
(5621,'P','2015-08-05',1,NULL,'Rss','«Росія створює ворога, щоб пояснити своє бажання домінувати у світі», ? Блаженніший Любомир','<p><img src=\"http://oranta.org/images/stories/2008/d8a0479dbd46.jpg\" width=\"250\" height=\"161\" alt=\"d8a0479dbd46\" style=\"margin: 10px; float: left;\" />На думку Блаженнішого Любомира, українцям час забути про радянський рай заради майбутнього.</p>\n<p>У свої 82 роки Блаженніший Любомир (Гузар), який до 2011 року очолював Українську Греко-Католицьку Церкву, займає активну громадянську позицію. До його думки дослухаються політики, активісти, віряни. Він не відмовляється і від зустрічей із журналістами.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2834:2015-08-06-11-04-17&catid=28:tema-nomera-cat&Itemid=2',5166,NULL,1,1,26),
(5622,'P','2015-08-06',1,NULL,'Rss','Історична спадщина нашої держави','<p><em><img src=\"http://oranta.org/images/stories/2008/333_0.jpg\" width=\"250\" height=\"187\" alt=\"333_0\" style=\"margin: 10px; float: right;\" />Інтерв’ю з &nbsp;українським археологом, директором Інституту археології Львівського національного університету ім. І. Франка &nbsp;Михайлом Филипчуком, багаторічним дослідником Пліснеського городища і Підгорецького (Пліснеського) монастиря.</em></p>\n<p><em><strong>Пане Михайле, зараз ми знаходимося на території, яка в народі просто окреслюється як Пліснесько, багато хто чув про те, що тут було місто, багато хто знає що тут є монастир, що тут є насправді? Що каже археологія?</strong></em></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2835:istorychna-spadshchyna-nashoji-derzhavy&catid=37:spilnota-cat&Itemid=49',5167,NULL,1,1,26),
(5623,'P','2015-08-06',1,NULL,'Rss','Андрей Шептицький як дороговказ реформ','<p><img style=\"margin: 10px; float: left;\" alt=\"badc127-sheptyckyj\" height=\"250\" width=\"201\" src=\"http://oranta.org/images/stories/badc127-sheptyckyj.jpg\" />В&nbsp;Україні тривають заходи з відзначення 150-річного ювілею від дня народження митрополита&nbsp;<a href=\"https://uk.wikipedia.org/w/index.php?title=%D0%90%D0%BD%D0%B4%D1%80%D0%B5%D0%B9_(%D0%A8%D0%B5%D0%BF%D1%82%D0%B8%D1%86%D1%8C%D0%BA%D0%B8%D0%B9)&amp;oldid=16557010\">Андрея Шептицького</a>.</p>\n<p>Їх метою, за влучним висловом блаженнійшого&nbsp;<a href=\"https://uk.wikipedia.org/w/index.php?title=%D0%9B%D1%8E%D0%B1%D0%BE%D0%BC%D0%B8%D1%80_(%D0%93%D1%83%D0%B7%D0%B0%D1%80)&amp;oldid=15864810\">Любомира Гузара</a>, є \"дати громадянам України та українцям у всьому світі можливість докладно пізнати та належно вшанувати цю небуденну людину\".</p>\n<p>Україна переживає непрості часи і стоїть на порозі вирішальних перетворень, тож нам слід навчитися сприймати спадщину великого митрополита творчо.</p>\n<p>Ми, далекі нащадки, які через століття і далі стоять перед викликом побудови здорового суспільства та сильної держави, повинні вбачати у цій спадщині аналіз проблем українського суспільства та настанови до дії.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2836:andre-sheptycky-jak-dorogovkaz-reform&catid=28:tema-nomera-cat&Itemid=2',5168,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5624,'P','2015-08-06',1,NULL,'Rss','Проща із Самбора у Зарваницю','<p><img style=\"margin: 10px; float: right;\" alt=\"IMG_3042\" height=\"167\" width=\"250\" src=\"http://oranta.org/images/stories/2008/IMG_3042.jpg\" />Дорогі друзі!</p>\n<p>14 - 23 серпня 2015 року відбудеться десята міжнародна піша Проща родин мігрантів-заробітчан та всіх охочих із Самбора у Зарваницю. Організаторами Прощі є Пасторально-міграційний відділ УГКЦ та благодійний фонд «Карітас» Самбірсько-Дрогобицької єпархії УГКЦ.</p>\n<p>Кожного року вже десять разів у першу п\'ятницю серпня із Самбора пішки вирушають до Зарваниці. Йдуть у молитві пішки десять, долають на сонці чи під дощем до трьохсот кілометрів.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2837:proshcha-iz-sambora-u-zarvanycju&catid=27:gk-v-ukrajini-cat&Itemid=57',5169,NULL,1,1,26),
(5625,'P','2015-08-11',1,NULL,'Rss','«Канікули з Богом»','<p><img src=\"http://oranta.org/images/stories/2008/118367.jpg\" width=\"250\" height=\"150\" alt=\"118367\" style=\"margin: 10px; float: left;\" />Цього річ з 3 серпня у місті Ясіня, Рахівського р-ну, Закарпатської обл. поблизу гори Драгобрат проходить літній родинний катихитично-патріотично-християнський табір</p>\n<p>Цей табір присвячений <em>150-річчю з дня народження митрополита Андрея Шептицького. </em></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2838:2015-08-11-10-40-34&catid=25:parafija-cat&Itemid=27',5170,NULL,1,1,26),
(5626,'P','2015-08-14',0,NULL,'Note','Свято Маковея','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">14 серпня &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">о 08:00 Утреня;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">о 09:00 Свята Божественна літургія. По завершенню літургії освячення зілля.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">о 18:00 Служба Божа</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Для родинного спілкування працюватиме кав&rsquo;ярня Карітас.</span></p>\r\n<div id=\"_appInstalled\">&nbsp;</div>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">14 серпня &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">о 08:00 Утреня;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">о 09:00 Свята Божественна літургія. По завершенню літургії освячення зілля.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">о 18:00 Служба Божа</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Для родинного спілкування працюватиме кав&rsquo;ярня Карітас.</span></p>\r\n<div id=\"_appInstalled\">&nbsp;</div>',NULL,5171,5,1,NULL,38),
(5627,'P','2015-08-11',1,NULL,'Rss','«Війна – це болісна операція для нашого суспільства, під час якої маємо розуміти цінність пролитої крові!»','<p><strong><img style=\"margin: 10px; float: right;\" alt=\"wide_8-11\" height=\"146\" width=\"250\" src=\"http://oranta.org/images/stories/2008/wide_8-11.jpg\" />Заходжу у хол реанімаційного відділення терапевтичного корпусу Львівського військового госпіталю. Маленький столик, який визначає межі святилища. На стіні над телевізором майже намісні ікони Богородиці та Ісуса Христа. Хол відпочинку, який щосереди і щонеділі на годину стає каплицею. Для мене особисто молитва тут, у госпіталі, є особливо цінною, оскільки дозволяє по-іншому пережити певні моменти з Богом, тому стараюсь підлаштувати свій робочий графік для літургійного резерву вечора середи.</strong>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2839:2015-08-11-12-37-53&catid=28:tema-nomera-cat&Itemid=2',5172,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5628,'P','2015-08-18',1,NULL,'Rss','Молодіжний табір з Богом','<p><img style=\"margin: 10px; float: left;\" alt=\"11903717_1667780906776888_2606982442554175773_n\" height=\"166\" width=\"250\" src=\"http://oranta.org/images/stories/11903717_1667780906776888_2606982442554175773_n.jpg\" />Нещодавно молодь Київської Архиєпархії УГКЦ повернулася з чудового табору, який проводився в мальовничих Карпатах. Декільком підліткам з Аскольдової Могили випала можливість туди поїхати, зокрема і мені. Тож, хочу поділитися враженнями.????</p>\n<p>Коли ми приїхали в Яремче, одразу ж автобусами вирушили до монастиря сестер-мироносиць. Сестри нас тепло зустріли і після розселення запросили на смачний сніданок. Протягом сніданку чомусь усі сиділи мовчки і було чутно лише брязкання виделок.</p>\n','<p>&nbsp;</p>\r\n<div id=\"_appInstalled\">&nbsp;</div>','http://oranta.org/index.php?option=com_content&view=article&id=2840:molodizhny-tabir-z-bogom&catid=25:parafija-cat&Itemid=27',5173,NULL,1,1,26),
(5629,'P','2015-08-18',1,NULL,'Rss','Глава УГКЦ нагадав про обов’язок молитви і посту за Україну','<p><img style=\"margin: 10px; float: right;\" alt=\"pray_for_ukraine\" height=\"161\" width=\"250\" src=\"http://oranta.org/images/stories/2008/pray_for_ukraine.jpg\" />Блаженніший Святослав, Глава УГКЦ, укотре закликав вірних Української Греко-Католицької Церкви та всіх людей доброї волі до молитви і посту за Україну. Він нагадав про обов’язок молитися щодня о 21:00 за Україну і один визначений день тижня постити в цьому наміренні.</p>\n<p>«Я помітив, що наші люди забувають про молитву за Україну о двадцять першій годині щовечора. У деяких єпархіях забули навіть, який день тижня визначений як день строгого посту за Батьківщину», - наголосив Глава УГКЦ.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2841:glava-ugkc-nagadav-pro-obovjazok-molytvy-i-postu-za-ukrajinu&catid=27:gk-v-ukrajini-cat&Itemid=57',5174,NULL,1,1,26),
(5630,'P','2015-08-20',1,NULL,'Rss','Марка Паславського посмертно нагороджено залізним пластовим хрестом','<p><img style=\"margin: 10px; float: left;\" alt=\"image\" height=\"167\" width=\"250\" src=\"http://oranta.org/images/stories/2008/image.jpg\" />19 серпня загиблому рік тому у боях за Українську землю, Марку Паславському встановили пам\'ятний знак на Аскольдовій Могилі у Києві. Саме там він був похований, поруч з могилами Героїв Крут та могилою активісту Майдану, що живцем згорів у будинку профспілок у лютому 2014-го.</p>\n<p>Вихованець Пласту в США Марко-Григорій Паславський відразу ж після початку конфлікту на сході України прийнявши українське громадянство добровольцем пішов до лав батальйону \"Донбас\". Там він не особливо афішував, що в цивільному житі є успішним підприємцем та те, що має військовий досвід в американській армії. У батальйоні його знали під позивним \"Франко\".</p>\n','<p>&nbsp;</p>\r\n<div id=\"_appInstalled\">&nbsp;</div>','http://oranta.org/index.php?option=com_content&view=article&id=2842:marka-paslavskogo-posmertno-nagorodzheno-zaliznym-plastovym-hrestom&catid=25:parafija-cat&Itemid=27',5175,NULL,1,1,26),
(5631,'P','2015-08-20',1,NULL,'Rss','Бути авторитетом для дитини. Навіщо? Щоб вести до Бога! ','<p><strong><img style=\"margin: 10px; float: right;\" alt=\"slayder_autoritet_\" height=\"155\" width=\"250\" src=\"http://oranta.org/images/stories/slayder_autoritet_.jpg\" />Шануй твого батька і матір твою, щоб довголітній був ти на землі, що Господь, Бог твій, дасть тобі (Вих 20,&nbsp;12). Ці слова зі Святого Письма привносять у стосунки між батьками та дітьми єдність і порозуміння.</strong></p>\n<p>Людина&nbsp;— творіння рук Божих, і з моменту народження схильна жити у сім’ї. У&nbsp;сім’ї, де її оточують турботою та любов’ю. Ставлення дітей до своїх батьків закладається в дитинстві.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2843:buty-avtorytetom-dlja-dytyny-navishcho-shchob-vesty-do-boga-&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5176,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5632,'P','2015-08-21',1,NULL,'Rss','Владика Ігор Ісіченко: Єдина церква, яка реально зберегла тяглість київської традиції – УГКЦ','<p><strong><img src=\"http://oranta.org/images/stories/2010/5wXg4dRG.jpg\" width=\"250\" height=\"166\" alt=\"5wXg4dRG\" style=\"margin: 10px; float: left;\" />Інтерв’ю Людмили Зузяк</strong> <strong> для газети «Верховинські вісті» </strong></p>\n<p>(село Криворівня Верховинського району Івано-Франківської обл., 19 липня 2015 р.)</p>\n<p><strong><em>Архиєпископ Харківсько-Полтавської єпархії Української Автокефальної Православної Церкви (оновленої) є одним із небагатьох інтелектуалів у церковному середовищі.</em></strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2845:vladyka-igor-isichenko-jedyna-cerkva-jaka-realno-zberegla-tjaglist-kyjivskoji-tradyciji-ugkc&catid=27:gk-v-ukrajini-cat&Itemid=57',5177,NULL,1,1,26),
(5633,'P','2016-02-03',0,NULL,'Note','Плануємо працю з молоддю на 2016 рік','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6905,200,IMG_1740.JPG\" alt=\"\" width=\"200\" height=\"150\" />На засіданні розглядався план діяльності молодіжної комісії на 2016 р., програма на літо цього ж року, а також програма підготовки, реєстрації та формування груп на Світовий День Молоді у Кракові. Програма видалась досить насиченою і, сподіваємось, цікавою та доступною для молоді як на загальному (архиєпархіальному) так і на місцевому (деканальному та парафіяльному) рівнях.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6905,200,IMG_1740.JPG\" alt=\"\" width=\"200\" height=\"150\" />В понеділок 1 лютого відбулося засідання Молодіжної комісії Київської архиєпархії УГКЦ. У зустрічі, окрім голови комісії о. Віталія Марцинюка та її рушійних сил Іванни Брацюнь та Оксана Загороднюк, взяли участь також відповідальні за працю з молоддю в деканатах Київської Архиєпархії &ndash; о. Роман Пушка, о. Роман Шкрібенець, о. Лука Шаламай, а також голова центру Академічного душпастирства &ndash; о. Максим Рябуха, та відповідальний за працю з молоддю при парафії Св. Миколая на Аскольдовій Могилі &ndash; о. Іван Сохан.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">На засіданні розглядався план діяльності молодіжної комісії на 2016 р., програма на літо цього ж року, а також програма підготовки, реєстрації та формування груп на Світовий День Молоді у Кракові. Програма видалась досить насиченою і, сподіваємось, цікавою та доступною для молоді як на загальному (архиєпархіальному) так і на місцевому (деканальному та парафіяльному) рівнях.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Запрошуємо молодь уважно стежити за нашими оголошеннями та долучатися до всіх молодіжних заходів, адже 2016 рік обіцяє бути цікавим J</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>о. Іван Сохан</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">сотрудник, відповідальний за молодіжний апостолят</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">при парафії Святого Миколая Чудотворця на Аскольдовій Могилі</span></p>',NULL,5422,5,1,NULL,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5634,'P','2015-08-21',1,NULL,'Rss','Про Патріарший Собор: як це було і як буде','<p><img style=\"margin: 10px; float: right;\" alt=\"46546546\" height=\"140\" width=\"250\" src=\"http://oranta.org/images/stories/2010/46546546.jpg\" />Залишається всього кілька днів до того моменту, коли Церква знову переглядатиме своє життя за останні п’ять років і міркуватиме, що зробити, аби його ще більше оживити. Блаженніший Святослав уже вдруге з моменту свого обрання Першоієрархом Церкви збирає представників УГКЦ з усього світу та України для того, щоб перевірити, як насправді реалізується Стратегія розвитку Церкви до 2020 року «Жива парафія – місце зустрічі із живим Христом» усюди, де присутні греко-католицькі громади.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2846:pro-patriarshy-sobor-jak-ce-bulo-i-jak-bude&catid=27:gk-v-ukrajini-cat&Itemid=57',5178,NULL,1,1,26),
(5635,'P','2015-08-25',1,NULL,'Rss','Могутній старець','<p><img src=\"http://oranta.org/images/stories/2009/02/articleimages.jpg\" width=\"250\" height=\"170\" alt=\"articleimages\" style=\"margin: 10px; float: left;\" />НВ публікує рейтинг людей, чиї вчинки і слова викликають безумовну повагу та підтримку співвітчизників. Перший у ньому — Любомир Гузар, предстоятель УГКЦ, що нещодавно відійшов від справ</p>\n<p>У морозну неділю, в розпал подій Євромайдану, в центрі Києва зібрався багатотисячний протест. Замість звичайного ревіння натовпу на площі панувало мовчання — лише зі сцени звучав тихий і спокійний голос літньої людини в чорному одязі священика.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2847:mogutni-starec&catid=28:tema-nomera-cat&Itemid=2',5179,NULL,1,1,26),
(5636,'P','2015-08-25',1,NULL,'Rss','Молитва маку','<p><img style=\"margin: 10px; float: right;\" alt=\"4646456\" height=\"151\" width=\"250\" src=\"http://oranta.org/images/stories/2009/02/4646456.jpg\" />Молитва маку або навчитися молитися як мак. Що це означає? Розгляньмо символіку цієї квітки. Будь-яка квітка складається з пелюсток, стебла і коріння. Найважливіша частина – пелюстки, саме їх краса, колір і форма привертають нашу увагу. Червоні пелюстки маку розпускаються наприкінці травня й при добрих погодних умовах своєю красою милують наш зір до двох тижнів. Це дуже короткий проміжок часу, це символ недовговічності й минущості людського життя.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2848:molytva-maku&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5180,NULL,1,1,26),
(5637,'P','2015-08-26',1,NULL,'Rss','На війні найкращий ресурс — довіра Богові','<p><img src=\"http://oranta.org/images/stories/2009/02/1.jpg\" width=\"250\" height=\"171\" alt=\"1\" style=\"margin: 10px; float: left;\" />Про моральну налаштованість і відкрите серце бійців розповідає с.&nbsp;Неля Куцак, психолог Кризової психологічної служби у Вінниці.</p>\n<p><em><strong>— Чому виникла необхідність цієї поїздки саме зараз?</strong></em></p>\n<p>— Аби хоч трохи полегшити новобранцям завдання, Міністерство оборони запросило психологів Кризової психологічної служби провести п’ятиденний тренінг. Треба навчити людей адекватно реагувати на стресові ситуації, якими надміру сповнене життя на війні.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2849:na-vini-nakrashchy-resurs-dovira-bogovi&catid=28:tema-nomera-cat&Itemid=2',5181,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5638,'P','2015-08-26',1,NULL,'Rss','«Виховувати людину –означає визначати долю нації»','<p><strong><em><img style=\"margin: 10px; float: right;\" alt=\"458646\" height=\"164\" width=\"250\" src=\"http://oranta.org/images/stories/2008/458646.jpg\" />Один відомий богослов сказав:&nbsp;«Виховувати людину&nbsp;–означає визначати долю нації». Саме про освіту: про те, чим вона є сьогодні і чим має бути завтра в Україні, чим відрізняється від американської, за що можуть звільнити професора у Штатах, яка доля наших вишів з огляду на реформи, ми запитали професора Францисканського університету (США), який багато років жив і працював в Україні, а цьогоріч за програмою Фулбрайта викладав в Українському католицькому університеті д-ра Олександра Січа.</em></strong>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2850:2015-08-26-18-39-49&catid=37:spilnota-cat&Itemid=49',5182,NULL,1,1,26),
(5639,'P','2015-08-27',1,NULL,'Rss','Пасхальний вимір Успення Богородиці','<p><img style=\"margin: 10px; float: left;\" alt=\"383839957\" height=\"175\" width=\"250\" src=\"http://oranta.org/images/stories/2009/02/383839957.jpg\" />Празник Успення Пресвятої Діви Марії – це не лише привід для роздумів про таїнство смерті Богородиці, а й велика нагода пригадати ким є Богоматір в очах Господа і для кожного християнина. Як часто в подячних піснях Акафісту до Богородиці чуємо:&nbsp;<em>«Радуйся, започаткування чудес Христових»</em>,&nbsp;<em>«Радуйся, бо через Тебе пекло опустіло»</em>,&nbsp;<em>«Радуйся, ключе, що райські двері відмикає»</em>. А у стихирах Молебню до Пресвятої Богоматері всі парафіяни разом із церковним хором суголосно найменують Непереможну Владичицю «скитальців потіхою», «горою, тінню сповитою». Як спричинилася Пречиста Діва Марія до того, щоби бути уславленою такими словами?</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2851:pashalny-vymir-uspennja-bogorodyci&catid=28:tema-nomera-cat&Itemid=2',5183,NULL,1,1,26),
(5640,'P','2015-08-27',1,NULL,'Rss','Великий Каменяр, який пробудив національну свідомість українців','<p><em><img src=\"http://oranta.org/images/stories/2009/02/ivan_franko_ukrtvir.info_-580x386.jpg\" width=\"250\" height=\"166\" alt=\"ivan_franko_ukrtvir.info_-580x386\" style=\"margin: 10px; float: right;\" /></em></p>\n<p><em>\"Іван Франко – це розум і серце нашого народу.</em><em> </em><em>Це боротьба, мука і передчуття щастя України.</em><em> </em><em>України і людськості\".</em><em> </em><em>М. Рильський</em></p>\n<p>На сучасному етапі розвитку нашої країни, на етапі розбудови її як незалежної, сильної держави, як ніколи важливе формування національної свідомості у широких верств населення, перетворення населення у націю, з усвідомленням свого героїчного минулого і почуттям патріотизму в серці.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2852:velyky-kamenjar-jaky-probudyv-nacionalnu-svidomist-ukrajinciv&catid=37:spilnota-cat&Itemid=49',5184,NULL,1,1,26),
(5641,'P','2015-08-26',0,NULL,'Note','ПОЖЕРТВИ ПАРАФІЯН ЦЕРКВИ СВ. МИКОЛАЯ ЧУДОТВОРЦЯ НА АСКОЛЬДОВІЙ МОГИЛІ У ЧЕРВНІ 2015 Р. Б.','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</em></strong></span></p>\r\n<table style=\"width: 482px;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>№ з/п</strong></span></p>\r\n</td>\r\n<td width=\"217\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Ж Е Р Т В О Д А В Ц І</strong></span></p>\r\n</td>\r\n<td width=\"227\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Пожертва на (для, до):</strong></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">1</span></p>\r\n</td>\r\n<td width=\"217\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Олексій Лявінець</span></p>\r\n</td>\r\n<td width=\"227\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Церкву<