INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4172,'P','2014-03-11',1,NULL,'Note',' «Таємниці Генія Шевченка»','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,5626,200,U91f691VJ_A.jpg\" alt=\"\" width=\"200\" height=\"112\" />Телевізійна студія \"1+1\" відзняла документальний фільм про таємниці життя Шевченка.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Актуальність Шевченка не вмирає століттями. Але наскільки добре ми знаємо його як звичайну людину? Фільм розкриває розмаїті аспекти із життя Кобзаря. У фільмі бачимо Шевченка нового - неканонічного, нешаблонного.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,5626,200,U91f691VJ_A.jpg\" alt=\"\" width=\"200\" height=\"112\" />Телевізійна студія \"1+1\" відзняла документальний фільм про таємниці життя Шевченка.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Актуальність Шевченка не вмирає століттями. Але наскільки добре ми знаємо його як звичайну людину? Фільм розкриває розмаїті аспекти із життя Кобзаря. У фільмі бачимо Шевченка нового - неканонічного, нешаблонного.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/HfU2lotlQCg\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>',NULL,NULL,4143,5,1,48),
(4173,'P','2014-03-14',1,NULL,'Rss','Обращение к деятелям культуры России, поддержавшим оккупацию Украины','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5625,200,1800417.jpg\" />Верноподданные&nbsp; Путина, оставьте нас в покое!</strong></p>\n<p><em>Обращение к деятелям культуры России и российским писателям, поддержавшим оккупацию Украины</em></p>\n<p>На землю Украины пришел агрессор – российская армия. Путин врет, делая вид, что ее там нет. Приезжайте – увидите сами.</p>\n<p>Путин попрал международное право: он осуществляет аннексию территории независимого государства.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1933:obrashchenye-k-dejateljam-kultur-rossyy-podderzhavshym-okkupacyju-ukrayn&catid=37:spilnota-cat&Itemid=49',4144,NULL,1,26),
(4174,'P','2014-03-14',1,NULL,'Rss','«Дуже важлива довіра до Бога. Він і з найтяжчих ситуацій виведе на добре»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5625,200,ArticleImag.jpg\" />Цього року виповнюється 25 років виходу з підпілля Української греко-католицької Церкви. З цієї нагоди пропонуємо Вашій увазі інтерв\'ю з Архиєпископом Ігорем Возьняком, Митрополитом львівським УГКЦ.</p>\n<p><strong>– Високопреосвященний владико, чи вбачаєте Ви певну невипадковість в тому, що 25-та річниця легалізації УГКЦ випала у такий важкий для українців період? Чи можемо черпати сили, споглядаючи на шлях, який пройшла наша Церква?</strong>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1934:2014-03-14-21-03-06&catid=28:tema-nomera-cat&Itemid=2',4145,NULL,1,26),
(4175,'P','2014-03-14',1,NULL,'Rss','Час розпочатися суду','<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5625,200,91273.jpeg\" />«Вийшов сіяч сіяти зерно своє…» (Лук.8:5). </em>Є чотири місця, куди воно впало.&nbsp; Проросло лише там, де була добра земля.&nbsp; Умовне співвідношення: з п»яти посіяних зерен проросло лише одне. Така ось статистика.&nbsp; На хорошій землі воно може дати врожай стократний, але це процес. Проростання, поява вруна, дозрівання колосу… Скільки ще доведеться здолати труднощів до появи спілого жнива!</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1935:chas-rozpochatysja-sudu&catid=28:tema-nomera-cat&Itemid=2',4146,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4176,'P','2014-03-14',1,NULL,'Rss','Видющий і сліпий (15 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5625,200,Jemchujnikov_kobzar.jpg\" />Одного разу видющий і сліпий вирушили в подорож. Цілу дорогу видющий вів сліпого за собою, переводив його через струмки та балки, застерігав ро вибоїни на дорозі. Але простелився їм шлях через темні гірські печери. Тоді каже видющий до сліпого:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тепер твоя черга мене вести. Бо в печері темно так, що хоч око вибери.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1936:vydjushchy-i-slipy-15-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4147,NULL,1,26),
(4177,'P','2014-03-15',1,NULL,'Rss','У Дорі згорів монастир студитів святого Іллі','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5630,200,1385336.jpg\" />Сьогодні зранку на території монастиря Святого Іллі у Дорі поблизу Яремчі згоріла церква. Поки що причини загоряння не встановлені, працюють експерти. Та як би там не було, але ми втратили пам\'ятку архітектури XX ст. та просто Господня обитель. Багато жінок просто плакали від відчаю.</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1937:u-dori-zgoriv-monastyr-studytiv-svjatogo-illi&catid=27:gk-v-ukrajini-cat&Itemid=57',4148,NULL,1,26),
(4178,'P','2014-03-15',1,NULL,'Rss','Глава УГКЦ очолив молитву за Україну на гробі святого Петра','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5630,200,15-03-14-4.jpg\" />Сьогодні вранці Блаженніший Святослав (Шевчук), Глава УГКЦ, очолив Божественну Літургію на гробі апостола Петра у Ватикані (каплиця Папи Климента VIII). Разом із Предстоятелем УГКЦ за суверенну Україну молилися посли України та Польщі при Апостольській Столиці – Тетяна Іжевська та Пйотр Новіна-Конопка.</p>\n<p>«Стоячи біля гробу святого апостола Петра, ми відчуваємо, що головним господарем історії є Господь Бог, а не президенти, політики, дипломати чи навіть&nbsp; сила зброї.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1938:glava-ugkc-ocholyv-molytvu-za-ukrajinu-na-grobi-svjatogo-petra&catid=32:gk-svit-cat&Itemid=58',4149,NULL,1,26),
(4179,'P','2014-03-15',1,NULL,'Rss','«Бережи природу – допоможи іншому»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5630,200,1619.JPG\" />Парафіяльна служба милосердя церкви св. Миколая Мирлікійського «Карітас-Печерськ» ратиме участь у екологічному проекті - допоможи потребуючим. На території паркового комплексу «Аскольдовій Могила» буде встановлено контейнери для збирання пластикових корків. Зібрані кошти витрачатимуться на потреби парафіяльної програми «Духовна домашня опіка хворих» яку вже більше 15 років провадить отець Анатолій Тесля з волонтерами.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=1939:2014-03-15-21-21-06&catid=25:parafija-cat&Itemid=27',4150,NULL,1,26),
(4180,'P','2014-03-15',1,NULL,'Rss','Запорука миру в родині (16 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5630,200,index.jpg\" />По сусідству жили побожна і безбожна сім’ї. Безбожні сварилися без упину. А в оселі вірних, повсякчас тиша і розуміння.</p>\n<p>Жінка безбожного каже чоловікові:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Піди до сусідів і роздивися, чому у них завжди все так добре?</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1940:zaporuka-myru-v-rodyni-16-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4151,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4181,'P','2014-03-16',1,NULL,'Rss','Нонконформізм і світовідчуття \"малої нації\" після Майдану.','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5610,200,1796584.jpg\" />Світовідчуття і приналежність до нації – дві ідеї, що почали турбувати більшість населення України на початку 2014 року. Після розпаду СРСР сформувалось уявлення про Україну як окрему республіку зі своїми кордонами. Сьогодні ці уявні кордони зміщуються, постійно перебуваючи в процесі руху. Справді, ці лінії на світовій карті – це небесний дар для України. Ми є абортоване дитя, що спромоглося вижити. Слабке і кволе тілесно, зате з неймовірною волею до життя. На території, що сформувалась в 1991 році опинилися не тільки українці та росіяни, але й велика кількість етнічних меншин: татари, греки, караїми, молдавани, гагаузи, румуни, угорці, роми, корейці, білоруси, поляки, вірмени, узбекистанці, грузини та ін.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1941:nonkonformizm-i-svitovidchuttja-qmaloji-nacijiq-pislja-madanu&catid=37:spilnota-cat&Itemid=49',4152,NULL,1,26),
(4182,'P','2014-03-16',1,NULL,'Rss','Порядні люди (17 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5610,200,images.jpg\" />Короля Прусії Фрідріха ІІ (1712-1786) ще за життя прозивали «великим», оскільки він насправді був видатним державним діячем, крім того його любив народ. який чи не найбільше цінував справедливість короля. У пізні роки його життя, люди ласкаво називали короля «старий Фриц».</p>\n<p>Він часто ходив у народ, щоб краще пізнати його потреби і клопоти і кожен міг не церемонячись звернутися зі своїм проханням особисто до короля.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1942:porjadni-ljudy-17-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4153,NULL,1,26),
(4183,'P','2014-03-17',1,NULL,'Rss','Зберегти мир у Східній Європі','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5636,200,z_frantsyskom1.jpg\" />Блаженніший Святослав докладно розповів про події, які відбулися в Україні протягом останніх місяців. Було підкреслено, що УГКЦ у цей важкий час була зі своїм народом, пастирі разом із вірними пережили всю драму суспільного протистояння і продовжують служити своїм людям відповідно до їхніх потреб.</p>\n<p>Окремо Предстоятель УГКЦ проаналізував міжконфесійні відносини в Україні та діяльність Всеукраїнської ради Церков і релігійних організацій.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1943:zberegty-myr-u-shidni-jevropi&catid=32:gk-svit-cat&Itemid=58',4155,NULL,1,26),
(4184,'P','2014-03-17',1,NULL,'Rss','Рибалка і лісничий (18 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5636,200,20aa501308494ea01af30cd753d.jpg\" />Старий рибалка ледве-ледве волік в’язку дров, зібраних у лісі. на мості він стрінувся з лісничим, який давно чекав нагоди відімстити старому за те, що той своїм благочестивим життям, вводив його в сором.</p>\n<p>Розгніваний лісник забрав у старого хмиз і жбурнув у воду. Рибалка почвалав до дому. опустивши голову.</p>\n<p>Навесні наступного року ріка вийшла далеко з берегів.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1944:rybalka-i-lisnychy-18-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4156,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4185,'P','2014-03-23',0,NULL,'Note','Розклад Богослужінь на 3-ий тиждень Великого посту 2014 р.Б','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>21.03. п\'ятниця</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Вечірня з Літургією Напередосвячених Дарів Парастас-сорокоусти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">17:00 Хресна Дорога від Дніпра</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 Вечірня з Літургією Напередосвячених Дарів, Хресна дорога</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>17.03. понеділок прп.Герасима,що на Йордані</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00&nbsp; Часи. Літургія св. Йоана Золотоустого&nbsp;Панахида</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 Літургія св. Йоана Золотоустого Акафіст Страстям Господнім</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>18.03. вівторок</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Часи, Літургія св. Йоана Золотоустого&nbsp; Панахида</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 Літургія св. Йоана Золотоустого&nbsp;Панахида Катехитичні навчання для парафіян</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>19.03. середа</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Вечірня з Літургією Напередосвячених Дарів.&nbsp;Парастас-сорокоусти.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00&nbsp;Вечірня з Літургією Напередосвячених Дарів. Хресна дорога</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>20.03. четвер</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Часи, Літургія св. Йоана Золотоустого &nbsp;Акафіст св.Миколаю Мирлікійському чудотв.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Освячення води і єлею,молитви оздоровлення</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 Літургія св. Йоана Золотоустого Панахида</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>21.03. п\'ятниця</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Вечірня з Літургією Напередосвячених Дарів Парастас-сорокоусти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">17:00 Хресна Дорога від Дніпра</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 Вечірня з Літургією Напередосвячених Дарів, Хресна дорога</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>22.03. Субота Заупокійна.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Утреня</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">09-00 Літургія Святого Івана Золотоустого.&nbsp;Парастас-сорокоусти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 Літургія Святого Івана Золотоустого Вечірня</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>23.03.2013 неділя 3-га Великого посту Хрестопоклонна</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-15 10-00 12-30 Літургія Святого Василія Великого.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">телефон для довідок:&nbsp;&nbsp;<strong>(050) 447 31 57</strong></span></p>',NULL,NULL,4154,5,1,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4186,'N','2014-03-18',1,NULL,'Rss','Цілісність Батьківщини – це священний обов’язок','<p><em><img style=\"margin: 10px; float: right;\" src=\"image,5636,200,01.komentjepyskop.jpg\" alt=\"\" />Військовий єпископ Михаїл Колтун відповів на запитання газети &laquo;Львівська пошта&raquo;. </em></p>\r\n<p><strong><em>Якою має бути сьогодні адекватна відповідь на ситуацію в Криму?</em></strong></p>\r\n<p>Роздумую про життя і його вартість я можу з впевненням сказати: святого втрачати не можна. Як для християнина є обов&rsquo;язок зберігати всі заповіді і якщо однієї не збережеш то чи ти спасешся? Ні. Тож коли ми говоримо про нашу Батьківщину, її цілісність, то якщо ми знехтуємо чимось найменшим то спасення нам не буде.</p>','',1,'http://oranta.org/index.php?option=com_content&view=article&id=1945:cilisnist-batkivshchyny--ce-svjashchenny-obovjazok&catid=37:spilnota-cat&Itemid=49',4157,NULL,1,26),
(4187,'P','2014-03-18',1,NULL,'Rss','Цілісність Батьківщини – це священний обов’язок','<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5636,200,01.komentjepyskop.jpg\" />Військовий єпископ Михаїл Колтун відповів на запитання газети «Львівська пошта». </em></p>\n<p><strong><em>Якою має бути сьогодні адекватна відповідь на ситуацію в Криму?</em></strong><em> </em></p>\n<p>Роздумую про життя і його вартість я можу з впевненням сказати: святого втрачати не можна. Як для християнина є обов’язок зберігати всі заповіді і якщо однієї не збережеш то чи ти спасешся?</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1945:cilisnist-batkivshchyny-ce-svjashchenny-obovjazok&catid=37:spilnota-cat&Itemid=49',4158,NULL,1,26),
(4188,'P','2014-03-18',1,NULL,'Rss','Викорінити рабство до 2020 року','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5640,200,global_freedom_network-2.jpg\" />Представники світових релігій створили асоціацію і підписали унікальну угоду у справі боротьби з рабством.</p>\n<p>17 березня в Ватикані відбулася презентація безпрецедентної угоди між представниками основних релігій під назвою Global Freedom Network («Глобальна вільна мережа»). Його метою є викорінення сучасного рабства і торгівлі людьми в усьому світі до 2020 року.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1946:vykorinyty-rabstvo-do-2020-roku&catid=32:gk-svit-cat&Itemid=58',4159,NULL,1,26),
(4189,'P','2014-03-18',1,NULL,'Rss','«Ця наша боротьба має вийти поза межі географії нашої країни чи нашого етносу»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5640,200,1394533480.jpg\" />Владика Борис (Ґудзяк) вважає, що Україна сьогодні повинна і може дати Європі більше, ніж взяти.</p>\n<p>Мій досвід в Україні на фоні виховання в кількох різних культурних контекстах Північної Америки й Західної та Центральної Європи підкреслив, скільки є страху, недовіри і скепсису в нашій сьогоднішній етнопсихології чи, принаймні, суспільстві. І на це є причини: у ХХ ст. на нашій землі близько 17 мільйонів людей було так чи інакше вбито. Сьогодні ми дійсно бачимо подвиг, спробу вийти з того страху й відновити власну гідність.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1947:2014-03-18-11-47-07&catid=28:tema-nomera-cat&Itemid=2',4160,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4190,'P','2014-03-19',1,NULL,'Rss','Звернення Комісії Пенітенціарного Душпастирства Київської Архиєпархії до священиків КА','<p align=\"right\"><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5643,200,6045.jpg\" />…у тюрмі був, і ви прийшли до мене. (Мт.25.36)</em></p>\n<p>Наша держава переживає непростий період на дорозі до будування вільного, демократичного, і, найважливіше, справедливого суспільного життя. У вирі емоційних переживань внаслідок подій, які відбулися і відбуваються дотепер, можливо, ми не мали часу обняти увагою людей, які знаходяться в місцях позбавлення волі.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1948:zvernennja-komisiji-penitenciarnogo-dushpastyrstva-kyjivskoji-arhyjeparhiji-do-svjashchenykiv-ka&catid=27:gk-v-ukrajini-cat&Itemid=57',4162,NULL,1,26),
(4191,'P','2014-03-19',1,NULL,'Rss','Щастя дітей (20 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5645,200,20.jpg\" />Якось ішов дорогою мудрець. Милувався красою світу і тішився життям, і ось, стрівся йому чоловік. що згорбившись двигав велетенський тягар.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Навіщо прирікаєш Ти себе на такі страждання? - запитав мудрець.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я страждаю заради щастя своїх дітей та онуків, - відповів чоловік, - мій прадід все життя страждав заради щастя діла, дід страждав заради щастя мого батька і я буду страждати все життя тільки щоб діти мої і онуки були щасливі.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1950:shchastja-dite-20-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4163,NULL,1,26),
(4192,'P','2014-03-20',1,NULL,'Rss','Вперше в історії ЄС саме українці загинули за цінності під європейським прапором','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5645,200,FB756357.jpg\" /></strong>Вперше в історії Євросоюзу саме українські громадяни віддали свої життя, відстоюючи європейські цінності під прапором Співтовариства.</p>\n<p>Про це у коментарі <a href=\"http://www.ukrinform.ua/ukr/\" target=\"_self\">Укрінформу</a> сказав єпископ Франції, Швейцарії і Бенілюксу Української греко-католицької церкви отець Борис Гудзяк.</p>\n<p>«Вперше в історії Європейського Співтовариства на території Європи європейці віддали свої життя з прапором Європи у руках», - описав трагічні події на Майдані у Києві єпископ.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1951:vpershe-v-istoriji-jes-same-ukrajinci-zagynuly-za-cinnosti-pid-jevropeskym-praporom&catid=28:tema-nomera-cat&Itemid=2',4164,NULL,1,26),
(4193,'P','2014-03-20',1,NULL,'Rss','Про силу сповіді (ГЛАВА 1)','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5647,200,1926646.jpg\" />ГЛАВА 1. </strong></p>\n<p><strong>Про необхідність духовного керівника.</strong></p>\n<p><strong><em>За допомогою сповіді людина стає вільною.</em></strong><strong> </strong></p>\n<p><strong>- У перші роки християнства всі члени Церкви здійснювали сповідь привселюдно. Чи є користь від такої сповіді?</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1952:pro-sylu-spovidi-glava-1&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4165,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4194,'P','2014-03-20',1,NULL,'Rss','«Безсоромній брехні, яка поширюється силами агресора, слід протиставити свідчення істини і добра»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5647,200,dzjurax_monashestvo.jpg\" />«На тлі подій останніх місяців стало очевидним, що триває боротьба сил добра і зла, синів світла і темряви. У цій боротьбі використовується різна зброя, не лише вогнепальна, а й зброя слова». Про це в коментарі Департаменту інформації сказав владика Богдан (Дзюрах), Секретар Синоду Єпископів УГКЦ.</p>\n<p>На його погляд, «перекручена правда, напівправда чи навіть відверта брехня завдають більшого спустошення в душах, ніж вибухи гранат на полі бою».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1953:2014-03-20-19-51-00&catid=28:tema-nomera-cat&Itemid=2',4166,NULL,1,26),
(4195,'P','2014-03-20',1,NULL,'Rss','Маленька дівчинка та атеїст (21 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5649,200,951.jpg\" />Маленька дівчинка поверталася з недільної школи до дому. Назустріч їй іде атеїст, який помічає радісну усмішку на обличчі дитини.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Що тебе так тішить? – спитав атеїст.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Іду з недільної школи.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ну і що вам сьогодні розповідали? – чоловік вирішив посміятися з дитини.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1957:malenka-divchynka-ta-atejist-21-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4167,NULL,1,26),
(4196,'P','2014-03-20',1,NULL,'Rss','Пастирське послання Блаженнішого Святослава з нагоди відновлення Акту посвяти України під Покров Пресвятої Богородиці','<p><em>Не маємо іншої допомоги, не маємо іншої надії, крім тебе, Пречиста Діво! Ти нам допоможи, на тебе надіємось і тобою хвалимось: бо ми твої слуги, щоб ми не посоромилися (Молебний канон Пресвятій Богородиці).</em></p>\n<p style=\"text-align: center;\"><strong>Дорогі в Христі!</strong></p>\n<p>У дні, коли наш народ і наша Батьківщина знову переживають великі випробування, пов’язані з окупацією частини нашої землі, наші серця наповнені тривогою за майбутнє, непевністю і страхом.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1958:pastyrske-poslannja-blazhennishogo-svjatoslava-z-nagody-vidnovlennja-aktu-posvjaty-ukrajiny-pid-pokrov-presvjatoji-bogorodyci&catid=27:gk-v-ukrajini-cat&Itemid=57',4168,NULL,1,26),
(4197,'P','2014-03-21',1,NULL,'Rss','Хто покладає надію не на Бога, той все втрачає','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5647,200,1743630.jpg\" />Людина, яка покладає надію на себе саму, на власні багатства чи ідеології, приречена бути нещасливою, а той, хто покладається на Господа Бога, навіть під час засухи приносить плоди. Про це, коментуючи біблійне читання з книги пророка Єремії, проповідував Папа Франциск.</p>\n<p>«Проклятий той, хто уповає на людину і покладає на тіло свою силу... Він, мов та билина у степу без листя... Щасливий чоловік, який на Господа вповає... Він, немов дерево, посаджене над водою... під час посухи йому байдуже, воно не перестає родити...», – стверджує автор біблійного тексту, коментуючи який, Святіший Отець наголосив, що «лише у Господі наша стійка надія. Інші не приносять користі, не спасають нас, не приносять життя, не приносять радості».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1955:hto-pokladaje-nadiju-ne-na-boga-to-vse-vtrachaje&catid=32:gk-svit-cat&Itemid=58',4171,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4198,'P','2014-03-21',1,NULL,'Rss',' Акт посвяти України під Покров Пресвятої Богородиці','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5649,200,dsc_0160-8981c.jpg\" />Продовжуючи служіння моїх великих попередників на Києво-Галицькому престолі, знову прагну в сопричасті з усією нашою Церквою в Україні та на поселеннях відновити Акт посвяти українського народу під всемогутній Покров Пресвятої Богородиці.</p>\n<p>Про це написав Отець і Глава Української Греко-Католицької Церкви Блаженніший Святослав (Шевчук) у своєму <a href=\"http://news.ugcc.org.ua/go/aHR0cDovL25ld3MudWdjYy5vcmcudWEvYXJ0aWNsZXMvcGFzdGlyc2tlX3Bvc2xhbm55YV9ibGF6aGVubiVEMSU5NnNob2dvX3N2eWF0b3NsYXZhX3pfbmFnb2RpX3YlRDElOTZkbm92bGVubnlhX2FrdHVfcG9zdnlhdGlfdWtyYWluaV9wJUQxJTk2ZF9wb2tyb3ZfcHJlc3Z5YXRvaV9ib2dvcm9kaXRzJUQxJTk2XzY5NzcyLmh0bWw=\" target=\"_blank\">Пастирському посланні до вірних і духовенства з нагоди відновлення Акту посвяти України під Покров Пресвятої Богородиці</a>.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1956:-akt-posvjaty-ukrajiny-pid-pokrov-presvjatoji-bogorodyci&catid=28:tema-nomera-cat&Itemid=2',4172,NULL,1,26),
(4199,'P','2014-03-21',1,NULL,'Rss','Порада мудреця (22 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5655,200,1959251.jpg\" />Якось до срібловолосого мудреця, обливаючись сльозами, прийшла молода і дуже вродлива дівчина:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Що мені робити? – крізь сльози скаржилася вона. – Я завжди прагну по-доброму поводитися з людьми, нікого не ображати, допомогти, як тільки можу. І хоча зі всіма я привітна і ласкава, але часто замість вдячності і поваги дістаю гіркі образи і глузування. Я ні в чому не винна і це так несправедливо й образливо. що аж плач бере. Порадьте. що мені робити?</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1959:porada-mudrecja-22-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4173,NULL,1,26),
(4200,'P','2014-03-21',1,NULL,'Rss','Правила суспільного життя, які маємо реалізувати в новій Україні','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5655,200,72.jpg\" /></strong>Позаду нас кілька місяців, сповнених трагічних подій. Позаду також період безправ’я та безладу, який ослабив нашу країну в усіх сферах суспільного життя. Продовжують тримати нас у напрузі події в Криму, і ми не цілком упевнені, як розвиватиметься ситуація в інших регіонах України.</p>\n<p>Однак можемо говорити про початок суттєвих змін у житті нашого народу, – змін, які повинні привести до повної нормалізації суспільного життя.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1961:pravyla-suspilnogo-zhyttja-jaki-majemo-realizuvaty-v-novi-ukrajini&catid=28:tema-nomera-cat&Itemid=2',4174,NULL,1,26),
(4201,'P','2014-03-22',1,NULL,'Rss','Свої','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5656,200,nebesnya_sotnya.jpg\" />Війна невблаганно мітить простір лише двома кольорами — чорним і білим. Будь-яке загострення ситуації безжально&nbsp; ділить світ на своїх і чужих, всупереч нашому&nbsp; бажанню. Тримісячне протистояння Майдану і влади інколи розколювало сім\'ї, часто сварило друзів, різко уточнювало коло та&nbsp; формат нашого спілкування. </strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1962:svoji&catid=37:spilnota-cat&Itemid=49',4175,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4202,'P','2014-03-22',1,NULL,'Rss','Господиня і фотограф (23 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5655,200,192682.jpg\" />Один фотограф, отримавши запрошення на святковий вечір, захопив кілька своїх світлин, аби при нагоді показати їх гостям.</p>\n<p>Господиня побачивши світлини, вигукнула:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Які пречудові фотографії! Ви, мабуть, маєте дуже дорогий фотоапарат!</p>\n<p>Фотограф не відповів їй на це ні слова, але на прощання зауважив:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1960:gospodynja-i-fotograf-23-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4176,NULL,1,26),
(4203,'P','2014-03-23',1,NULL,'Rss','Покаяння мусить стати початком нового українського суспільства','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5659,200,pismo.jpg\" />Коли відкриваємо Святе Письмо там, де починається історія Праотця нашого Авраама, то впираємося у дивовижну фразу: «Сказав Ягве до Аврама: Вийди до себе з землі твоєї, з твоєї рідні, і з дому батька твого в край, що його я тобі покажу» (Бут. 12, 1). Відразу зазначу, не шукайте її у своїх Бібліях, там вона виглядає дещо інакше – там немає отого «до себе». Адже гебрейський вираз lĕḥ-lᵉḥā зазвичай перекладають: «Вийди». Бо неможливо українською сказати: «Вийди до себе з землі твоєї», щоб не поставити слухача у позицію нерозуміння.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1964:pokajannja-musyt-staty-pochatkom-novogo-ukrajinskogo-suspilstva&catid=28:tema-nomera-cat&Itemid=2',4178,NULL,1,26),
(4204,'P','2014-03-23',1,NULL,'Rss','Як вибирати друзів (24 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5659,200,1273763310.jpg\" />Один чоловік вирішив порадитись зі старцем Паїсієм Святогорцем, як позбутися пристасти, що йому дошкуляла. Той чоловік мав пихатих приятелів, які часто лихословили супроти інших. Отож поки він міркував. як краще запитати про це хтось приніс старцю в подарунок чудовий кавун.</p>\n<p>Паїсій взяв його і звернувся до прочан зі словами:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1965:jak-vybyraty-druziv-24-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4179,NULL,1,26),
(4205,'P','2014-03-24',1,NULL,'Rss','Три гріхи медіа: дезінформація, очорнення, знеславлення','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5662,200,646.jpg\" />Найбільші гріхи медіа — це ті, що йдуть дорогою брехні, і їх є три: дезінформація, очорнення та знеславлення. А найважчий із них — це дезінформація. </strong>Святіший Отець закликав медійників завжди шукати правду. На цій дорозі, наголосив Папа, потрібно бути уважним до небезпек, щоб не стати інтелектуалом без інтелекту чи моралістом без добра, або, шукаючи прекрасне, можна натрапити на штучну красу, плід макіяжу, яка насправді не існує. Завдання медіа — шукати «істину, красу і добро такими, якими вони походять від Бога та перебувають в людині».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1966:try-grihy-media-dezinformacija-ochornennja-zneslavlennja&catid=28:tema-nomera-cat&Itemid=2',4180,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4206,'P','2014-03-24',1,NULL,'Rss','Кравець і дівчина (25 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5662,200,grivna-phl.jpg\" />Одна дівчина вирішила замовити сукню у відомого кравця, тому. що шив він гарно і сумлінно. Однак, вона вважала, що його сукні надто дорогі, а тому зайшовши в майстерню перед усім вирішила домовитися про ціну.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ви дуже дорого берете за свої сукні, - оголосила вона.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1967:kravec-i-divchyna-25-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4181,NULL,1,26),
(4207,'P','2014-03-25',1,NULL,'Rss','Омелян Ковч','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5663,200,small_2_s12.jpg\" />25 березня 2014 року минає 70 років із дня смерті блаженного священномученика Омеляна Ковча. З цієї нагоди прес-служба Стрийської єпархії поспілкувалася зі священиками, які душпастирювали і душпастирюють у Перемишлянах, де отець Омелян служив понад 20 років</p>\n<p>Отець Омелян Ковч народився 20 серпня 1884 року в с. Космач на Косівщині. Теологічну освіту здобував у Колегії святих Сергія і Вакха в Римі в 1905-1911 роках. На священика його висвятив єп. Григорій Хомишин у 1911 році.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1968:omeljan-kovch&catid=28:tema-nomera-cat&Itemid=2',4182,NULL,1,26),
(4208,'P','2014-03-25',1,NULL,'Rss','Любляча дочка (26 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5663,200,images.jpg\" />Було у матері сім дочок. Одного разу поїхала матір до сина. який далеко жив. Вернулася вона до дому тільки через тиждень. Коли зайшла до хати. дочки одна по одній почали розповідати, як вони тужили за нею.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я тужила за тобою так, як маківка за сонячним лугом, - сказала перша дочка.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1970:ljubljacha-dochka-26-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4183,NULL,1,26),
(4209,'P','2014-03-25',1,NULL,'Rss','Церква і революція','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5663,200,1395683.jpg\" />В одній з повістей Франца Кафки згадується київський священник, який під час революції 1905 року зупинив натовп, зробивши собі ножем на долоні розріз хрестом... Під час революції у Києві 2014 року таких відважних священників були десятки. Можливо, завдяки їм, жертв на Майдані було менше, ніж могло бути.</p>\n<p>Про роль церкви в українській революції УНІАН у Брюсселі розмовляв з провідним релігієзнавцем України, віце-ректором Українського Католицького Уінверситету, колишнім дисидентом Мирославом Мариновичем.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1969:cerkva-i-revoljucija&catid=37:spilnota-cat&Itemid=49',4184,NULL,1,26),
(4210,'P','2014-03-26',1,NULL,'Rss','Суспільний діалог як вклад у справу миру','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5667,200,45388.jpg\" />Євангелізація зумовлює також діалог. Сьогодні пріоритетними для діалогу є три середовища, в яких Церква повинна бути присутньою, щоб служити всебічному розвитку людини і шукати загальне добро: діалог з державою, діалог з суспільством – який включає діалог з культурою і наукою – і діалог з вірянами, які не належать до Католицької Церкви. «Церква завжди промовляє в світлі віри»<a href=\"http://oleh-sj.com/J/EG-IV-4.html?rnd=0.9492466526685337#_ftn1\"><span style=\"text-decoration: underline;\">[1]</span> [1]</a>, вносить свій двохтисячолітній досвід і завжди пам’ятає про життя і страждання людини. Цей досвід незрозумілий для людського розуму, але він важливий і може збагатити невіруючих і спонукати їх розширювати свій кругозір.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1971:suspilny-dialog-jak-vklad-u-spravu-myru&catid=37:spilnota-cat&Itemid=49',4185,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4211,'P','2014-03-26',1,NULL,'Rss','Невідома сторона УГКЦ','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5667,200,vl_s_menjok.jpg\" />У контексті останніх подій, що сталися в Криму, переконана, у більшості українців виникало питання стосовно дальшої долі найближчої до вже колишньої автономної республіки частини нашої держави – Східної України. Усіх бентежить, чи продовжуватиме свою політику захисту росіян в Україні Путін на східних теренах Української держави, а також те, яка доля спіткає патріотичну Українську Греко-Католицьку Церкву.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1972:nevidoma-storona-ugkc&catid=27:gk-v-ukrajini-cat&Itemid=57',4186,NULL,1,26),
(4212,'P','2014-03-27',1,NULL,'Rss','Добове коло молитви: стратегія опору й біополітика','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5667,200,vzk44VAenBg.jpg\" />Наступні рядки виникли як реакція на читання антології досліджень <em>«Світло єси Христе…»: Богослужіння добового кола</em> (Львів: УКУ, 2012). На відміну від укладачів збірника Ендрю Квінлана й Василя Рудейка, не належу до кола професійних літургістів. &nbsp;Якщо ж витоки богослов’я у молитовному досвіді, а за Квінланом «кожне наукове дослідження у царині богослов’я літургійного повинно бути дослідженням богослов’я молитви»[1], тоді право на висловлення міркувань стосовно практики молитви належить і тим, хто так чи інакше причетний до богослов’я, рівно ж як і тим, хто прагне перебувати у молитовному колі помісної еклезіальної спільноти, що означає орієнтуватися не лише в колі проблем, заакцентованих літургістами, але й бути свідомим та ставити інші питання, що проходять повз увагу останніх, передовсім з причини їхньої професійної заанґажованості.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1973:dobove-kolo-molytvy-strategija-oporu--biopolityka&catid=27:gk-v-ukrajini-cat&Itemid=57',4189,NULL,1,26),
(4213,'P','2014-03-27',1,NULL,'Rss','Боже милосердя (28 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5673,200,01.jpg\" />Один чоловік дуже полюбляв випити і частенько заглядав до пивнички. Але згодом у нього прокидалося сумління і він каявся:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Прости мені, Господи.</p>\n<p>Це траплялося раз-у-раз. Сатана дивився-дивився на це все. А тоді, не витримав і питає у Бога:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1976:bozhe-myloserdja-28-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4191,NULL,1,26),
(4214,'P','2014-03-30',0,NULL,'Note','Розклад Богослужінь на 4-ий тиждень Великого посту 2014 р.Б','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>29.03. Субота Заупокійна.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Утреня</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">09-00 Літургія Святого Івана Золотоустого.&nbsp;Парастас-сорокоусти, 40 днів памяті Небесної Сотні, річниця памяті брата Ігоря Богуша. Посади дерево памяті на Аскольдовій могилі</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>24.03. понеділок&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00&nbsp; Часи. Літургія св. Йоана Золотоустого&nbsp;Панахида</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 Літургія св. Йоана Золотоустого Акафіст Страстям Господнім</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>25.03. вівторок</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Часи, Літургія св. Йоана Золотоустого&nbsp; Панахида</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 Літургія св. Йоана Золотоустого&nbsp;Панахида Катехитичні навчання для парафіян</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>26.03. середа</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Вечірня з Літургією Напередосвячених Дарів.&nbsp;Парастас-сорокоусти.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00&nbsp;Вечірня з Літургією Напередосвячених Дарів. Хресна дорога</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>27.03. четвер</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Часи, Літургія св. Йоана Золотоустого &nbsp;Акафіст св.Миколаю Мирлікійському чудотв.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Освячення води і єлею,молитви оздоровлення</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 Літургія св. Йоана Золотоустого Панахида</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>28.03. п\'ятниця</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Вечірня з Літургією Напередосвячених Дарів Парастас-сорокоусти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">17:00 Хресна Дорога від Дніпра</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 Вечірня з Літургією Напередосвячених Дарів, Хресна дорога</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>29.03. Субота Заупокійна.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-00 Утреня</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">09-00 Літургія Святого Івана Золотоустого.&nbsp;Парастас-сорокоусти, 40 днів памяті Небесної Сотні,річниця памяті брата Ігоря Богуша.Посади дерево памяті на Аскольдовій могилі</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18-00 Літургія Святого Івана Золотоустого Вечірня</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>30.03.2014 неділя 4-та Великого посту, прп.Олексія,чоловіка Божого</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08-15 10-00 12-30 Літургія Святого Василія Великого.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">телефон для довідок:&nbsp;&nbsp;<strong>(050) 447 31 57</strong></span></p>',NULL,NULL,4188,5,1,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4215,'P','2014-03-28',1,NULL,'Rss','Проблема «уніатизму»','<p align=\"center\"><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5672,200,safe_image.php.jpg\" />«Уніатизм» і православно-католицький діалог</strong></p>\n<p>Розбіжності з питання «уніатизму» віддавна є дражливим моментом у православно-католицькому діалозі. Доки Східні Католицькі Церкви у країнах колишнього соціалістичного табору не вийшли з підпілля (а ста­лося це 1988 р. і найближчого по ньому часу), видавалося, що ці розбіж­ності можна делікатно оминати: існувало мовчазне припущення, що Східні Католицькі Церкви в діаспорі поступово зникнуть, а на Близькому Сході пропадуть під мусульманським та сіоністським тиском.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1975:2014-03-27-21-04-19&catid=27:gk-v-ukrajini-cat&Itemid=57',4192,NULL,1,26),
(4216,'P','2014-03-28',1,NULL,'Rss','Церква, яка не мовчить','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5675,200,886558.jpg\" />Сьогодні Церква перебуває в епіцентрі подій. Люди шукають церковної присутності. Вони її потребують. Чи відповідає Церква на цей суспільний запит? Про це Юрій Чорноморець розмовляє з Патріархом Української греко-католицької церкви Святославом і представником Української православної церкви Київського патріархату Патріархом Філаретом.</p>\n<p align=\"center\"><strong> </strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1977:cerkva-jaka-ne-movchyt&catid=27:gk-v-ukrajini-cat&Itemid=57',4193,NULL,1,26),
(4217,'P','2014-03-28',1,NULL,'Rss','Блаженніший Святослав вважає, що перед урядом стоїть завдання «врятувати не лише державу, а й добрих людей та службовців»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5671,200,zmoloddju.jpg\" />Усі Церкви та релігійні організації підтримали оновлену владу, легітимізували з церковно-морального боку наш уряд. Я б сьогодні назвав його урядом національного порятунку.</p>\n<p>Про це сказав Отець і Глава Української Греко-Католицької Церкви Блаженніший Святослав (Шевчук) <a href=\"http://news.ugcc.org.\" target=\"_blank\">в інтерв’ю газеті «День»</a>, висловлюючись про кадрову політику нинішньої влади.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1978:2014-03-28-20-28-36&catid=31:kolonka-redaktora-cat&Itemid=1',4194,NULL,1,26),
(4218,'P','2014-03-29',1,NULL,'Rss','Чому бідні люди співчутливі (29 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5677,200,383489811.jpg\" />Якось у старенького священика хтось запитав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чому бідні люди співчутливі до чужої біди, а багаті ні?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Виглянь у вікно, - відповів той, - що ти бачиш?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Бачу дерева, автомобілі, гуляють діти.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1979:chomu-bidni-ljudy-spivchutlyvi-29-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4195,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4219,'P','2014-03-29',1,NULL,'Rss','Щаслива сім’я (30 березня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5677,200,796.jpg\" />Прийшов чоловік до мудреця питати пораду.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Скажи, яка таємниця твоїх знань? Ти щасливий. До тебе ідуть люди, щоб дізнатися як поліпшити своє життя. Я багато вчуся, але незгоди сипляться на мене.</p>\n<p>Мудрець лише усміхнувся і покликав свою дружину, і через кілька хвилин увійшла вродлива жінка, її очі сяяли і тоді мудрець попросив:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1981:shchaslyva-simja-30-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4196,NULL,1,26),
(4220,'P','2014-03-29',1,NULL,'Rss','Боже милосердя - ним треба ділитись','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5677,200,532276.jpg\" />Навернутися – це не справа однієї миті, це – старання, яке триває усе життя. На цьому наголосив Папа Франциск, який у п’ятницю, 28 березня 2014 р., очолив в базиліці Святого Петра у Ватикані обряд покути та поєднання для більшої кількості каянників з індивідуальною сповіддю та розрішенням, який був частиною ініціативи «24 години для Господа», що відбувалася у храмах Риму та в різних країнах світу, та була організована Папською Радою сприяння новій євангелізації.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1980:bozhe-myloserdja-nym-treba-dilytys&catid=32:gk-svit-cat&Itemid=58',4197,NULL,1,26),
(4221,'P','2014-03-30',1,NULL,'Rss','Хрестом і мечем','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5679,200,images.jpg\" />Вже місяць Україна перебуває у стані брутальної військової окупації північним агресором нашої суверенної території: поки що&nbsp; - півострова Крим. Але попри цей видимий збройний фронт, існують щонайменше ще два інші, не менш потужні, хоча й віртуальні: тотальна інформаційна війна російських ЗМІ та авангардні «бойові» дії ідеологічного сектора «русского міра» - Московської патріярхії РПЦ.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1982:hrestom-i-mechem&catid=28:tema-nomera-cat&Itemid=2',4199,NULL,1,26),
(4222,'P','2014-03-31',1,NULL,'Rss','«Серця Живе Джерело»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5681,200,Article.jpg\" />30 березня в Римі відбулася презентація антології поезій, присвячених Патріарху Йосифу Сліпому. Видання під назвою «Серця Живе Джерело» презентували професори Леонід Рудницький та Альберт Кіпа. Захід відбувся в приміщенні товариства «Свята Софія» (via di Boccea, 478).</p>\n<p>Захід розпочався Архиєрейською Літургією в соборі Святої Софії, яку очолив владика Венедикт (Алексійчук), Єпископ-помічник Львівської архиєпархії. Із ним співслужив владика Іриней (Білик), канонік папської базиліки Санта Марія Маджоре.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1984:2014-04-01-08-42-27&catid=32:gk-svit-cat&Itemid=58',4200,NULL,1,26),
(4223,'P','2014-04-01',1,NULL,'Rss','Даремна нагорода (1 квітня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5681,200,1969126.jpg\" />Купець повертався додому з далеких країв із дорогим крамом. Зненацька погода зіпсувалася: здійнявся ураган, почали ламатися дерева, курява заступила дорогу. Купець не мав де сховатися і, врешті, збився з дороги. Він відчайдушно кликав на допомогу, але ніхто не відгукувався. Зрозумівши, що почалася ніч, він почав молитися.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1985:daremna-nagoroda-1-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4201,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4224,'P','2014-04-01',1,NULL,'Rss',' Судити, не осуджуючи: як допомогти «безнадійним» ','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5684,200,condemnation.jpg\" />Цікавою видалась зустріч минулої п’ятниці із братом-семінаристом Володимиром Битюгою. І, осмислюючи цю важливу у великопосному часі тему, хотілось би зараз поговорити про людей… «не таких». Ні, зазвичай вони нічим зовні не відрізняються від нас. Вони їздять із нами у транспорті, вони ходять із нами на пари, працюють із нами на одній роботі, ходять до однієї церкви. Вони навчаються, працюють, організовують власні справи, здобувають наукові ступені. І все ж вони… не такі. Не такі, як нам хотілось би.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1986:2014-04-01-14-33-55&catid=27:gk-v-ukrajini-cat&Itemid=57',4202,NULL,1,26),
(4225,'P','2014-04-06',0,NULL,'Note','Розклад Богослужінь на 5-ий тиждень Великого посту 2014 р.Б','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>02.04. середа</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08-00 Вечірня з Літургією Напередосвячених Дарів.&nbsp;Парастас-сорокоусти.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18-00 Утрення з поклонами,канон Андрея Критського</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>31.03. понеділок</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08-00&nbsp; Часи. Літургія св. Йоана Золотоустого&nbsp;Панахида</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18-00 Літургія св. Йоана Золотоустого Акафіст Страстям Господнім</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>01.04 вівторок</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08-00 Часи, Літургія св. Йоана Золотоустого&nbsp; Панахида</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18-00 Літургія св. Йоана Золотоустого&nbsp;Панахида Катехитичні навчання для парафіян</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>02.04. середа</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08-00 Вечірня з Літургією Напередосвячених Дарів.&nbsp;Парастас-сорокоусти.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18-00 Утрення з поклонами,канон Андрея Критського</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>03.04. четвер</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08-00 Часи, Літургія св. Йоана Золотоустого &nbsp;Акафіст св.Миколаю Мирлікійському чудотв.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Освячення води і єлею,молитви оздоровлення</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18-00 Літургія св. Йоана Золотоустого Панахида</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>04.04. п\'ятниця</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08-00 Вечірня з Літургією Напередосвячених Дарів Парастас-сорокоусти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">17:00 Хресна Дорога від Дніпра</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18-00 Вечірня з Літургією Напередосвячених Дарів, Хресна дорога</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>05.04. субота акафістова.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08-00 Утреня</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">09-00 Літургія Святого Івана Золотоустого. Панахида,Вервиця,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">11-00 засідання Церковної Ради</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18-00 Літургія Святого Івана Золотоустого Вечірня</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>06.04.2014 неділя 5-та Великого посту, передпразденство Благовіщення</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08-15 10-00 12-30 Літургія Святого Василія Великого.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>07.04.2014 понеділок Благовіщення пресвятої владичиці нашої Богородиці</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08-00 Утрення</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">09-00 Літургія св.Йоана Золотоустого. Молебень Богородиці</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">телефон для довідок: <strong>(050) 447 31 57</strong></span></p>',NULL,NULL,4203,5,1,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4226,'P','2014-04-01',1,NULL,'Rss','Людина і скарб (2 квітня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,5685,200,molitva4.jpg\" style=\"margin: 10px; float: left;\" /></p>\n<p>Шукач скарбів прочитав у стародавній книзі, що в одному місці закопані в землі незліченні скарби і вирішив відшукати їх. Він довго розшукував цей скарб.</p>\n<p>Одного разу копаючи землю він помітив, що з неба на нього опускається золота лопата. Чоловік радісно схопив її і не тямлячись від щастя поніс додому.</p>\n<p>Про закопані скарби, він вже не думав. вважаючи, що золотої лопати йому вже вистачить. І почув з неба голос:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1987:ljudyna-i-skarb-2-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4204,NULL,1,26),
(4227,'P','2014-04-02',1,NULL,'Rss','Покаяльний канон Андрея Критського','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5686,200,img1.jpg\" />У християнській вірі серед багатьох релігійних зобов’язань важливе місце займає молитва. Перш за все молитва постає основною потребою в житті людини як у порядку природному, так і надприродному. Людська істота за своєю природою намагається піднести свій дух до чогось вищого. Розум людини і воля, як і інші властивості душі, підказують їй спрямовуватися до свого Сотворителя. Саме таке скерування людської душі на свого Сотворителя і Господа становить природню підставу для молитви, отже, Господь Бог створив людину, яка за своєю природою є істотою, що потребує молитви і є схильною до молитви. Молитва для кожного з нас мала б бути як ковток повітря, потрібний для продовження фізичного життя.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1988:pokajalny-kanon-andreja-krytskogo&catid=28:tema-nomera-cat&Itemid=2',4205,NULL,1,26),
(4228,'P','2014-04-03',1,NULL,'Rss','Принцеса (3 квітня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5687,200,daun.jpg\" />Жив собі один цар, який мав надзвичайну розумну дочку. Однак, хворіла вона невідому недугу. Щороку слабшали її руки і ноги, зір і слух. Чимало лікарів марно намагалися лікувати її. Якось у двір прийшов старець про якого казали, що він знає таємницю життя.</p>\n<p>Усі царедворці поспішили звернутися до нього з проханням допомогти недужій принцесі. Старець дав принцесі кошика з лози закритого покривалом і сказав:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1989:pryncesa-3-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4206,NULL,1,26),
(4229,'P','2014-04-03',1,NULL,'Rss','СВЯТІ ПРО СВЯТІСТЬ','<p><img src=\"http://askoldova-mohyla.org/uk/image,5687,200,canonizzazione_mini_mini.jpg\" style=\"margin: 10px; float: left;\" />Через кілька тижнів, 27 квітня 2014 р., блаженні Папи Іван ХХІІІ та Іван Павло ІІ будуть вписані до «величезного збору святих і мучеників», як на початку третього тисячоліття назвав цей сонм Божих угодників Папа Войтила. Протягом свого понтифікату вони наголошували на тому, що святі, які показують нам «живе Христове обличчя», є для Церкви засобом євангелізації та освячення Церкви. Загалом, усім відомо про рекорди беатифікацій та канонізацій, проголошених Іваном Павлом ІІ, але й не меншим є наголошення на святості Іваном ХХІІІ, зокрема під час приготування до Другого Ватиканського Собору.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1991:svjati-pro-svjatist&catid=35:istorychni-postati-cat&Itemid=48',4207,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4230,'P','2014-04-03',1,NULL,'Rss','«Віруюча молодь є силою, яка здатна змінити суспільство»','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,5687,200,z_moloddju_stradch.jpg\" style=\"margin: 10px; float: right;\" />Бадьортеся! Я бо подолав світ! (Ів. 16, 33). Улюблена в Христі молоде! Радію, що знову маю нагоду у Квітню неділю, у день Входу Господнього до Єрусалима, звернутися до вас!</strong></p>\n<p>Ви завжди, а особливо протягом цієї тяжкої зими та сповненої тривог весни, у моїх думках і молитвах. Хочу запевнити вас, що в усіх нелегких випробуваннях, труднощах та печалях Церква є і завжди буде для вас люблячою матір’ю, до якої ви можете прийти, щоб відновити свої сили, знайти мир і надію.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1990:2014-04-02-21-36-38&catid=32:gk-svit-cat&Itemid=58',4208,NULL,1,26),
(4231,'P','2014-04-03',1,NULL,'Rss','ГЛАВА УГКЦ УВІЙШОВ У ТОП-10 ВИЗНАЧНИХ ОСОБИСТОСТЕЙ 2013 РОКУ','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5690,200,top10.jpg\" />Предстоятель УГКЦ Блаженніший Святослав увійшов у топ-10 людей 2013 року за версією журналу «lnside the Vatican» як «сміливий свідкок істини і добра».<o:p></o:p></p>\n<p>З цієї нагоди журнал «lnside the Vatican» опублікував біографічну статтю про Главу УГКЦ. «Завдяки своїй енергії і запалу в захисті Церкви в Україні, особливо в цей важкий рік для цієї країни, ми вшанувати його як одного із визначних людей 2013 року», − йдеться у статті.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1992:glava-ugkc-uvishov-u-top-10-vyznachnyh-osobystoste-2013-roku&catid=27:gk-v-ukrajini-cat&Itemid=57',4209,NULL,1,26),
(4232,'P','2014-04-04',1,NULL,'Rss','Робінзон (4 квітня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5692,200,img3.jpg\" />Єдиного пасажира. що вижив після корабельної аварії викинуло на безлюдний острів. З уламків корабля, що прибилися до берега, він так-сяк збудував собі схованку, заніс туди все, що викинуло море і могло стати йому в пригоді. Він підніс хвалу і подякував Богові за свій порятунок. Минуло багато днів, та поряд з островом і не пропливало жодне судно, щоранку він обходив острів шукаючи їжі. Одного разу, під час такої мандрівки, спалахнула пожежа, полум’я пожерло усі дерева навкруги. Його житло і всі речі згоріли до чита, тільки чорний дим підносився до неба.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1993:robinzon-4-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4210,NULL,1,26),
(4233,'P','2014-04-04',1,NULL,'Rss','Ваш голос може бути потужним, якщо ви правильно його сформуєте','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5694,200,yQd38tCzH28.jpg\" />Від шалу розчарування до пошуку нових можливостей – так охарактеризував причину зустрічі на самому її початку проректор УКУ Мирослав Маринович. 2 квітня він зустрівся зі студентами, щоб обговорити наслідки Майдану та перспективи, які відкриваються перед молоддю.</strong></p>\n<p>Після завершення Революції Гідності, в Україні запанувала разюча зміна настроїв.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1994:vash-golos-mozhe-buty-potuzhnym-jakshcho-vy-pravylno-ogo-sformujete&catid=37:spilnota-cat&Itemid=49',4212,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4234,'P','2014-04-05',1,NULL,'Rss','Що ж можуть люди (5 квітня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5694,200,img_22471.jpg\" />Помер учений і його душа стала перед Богом. Каже учений до Бога:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ми, люди науки, дійшли висновку, що більше Тебе не потребуємо! Ми осягли всі таємниці, знаємо те, що знаєш Ти, вміємо клонувати людей. трансплантувати людські органи, створюємо нові види тварин і рослин… Одне слово. ми можемо робити все, що раніше вважали чудесним і приписували Твоїй Мудрості і Всемогутності.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1995:shcho-zh-mozhut-ljudy-5-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4213,NULL,1,26),
(4235,'P','2014-04-04',1,NULL,'Note','Новітні герої спочиватимуть на Аскольдовому некрополі','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5694,200,398411.jpg\" alt=\"\" width=\"200\" height=\"133\" />5 квітня 2014 р. Б. об 11:30 на Аскольдовому некрополі біля церкви св. Миколая Мирлікійського ховатимуть невідомого героя Майдану, захисника революції гідності, воїна за волю України, патріота українського народу, одного із Небесної Сотні.&nbsp;</span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Чин похорону розпочнеться о 10:00 в Українському Домі.&nbsp;Новітнього героя ховатимуть з усіма військовими почестями.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; text-indent: 0em; line-height: 1.3em; font-size: small;\"><img style=\"float: left;\" src=\"image,5694,200,398411.jpg\" alt=\"\" width=\"200\" height=\"133\" />5 квітня 2014 р. Б. об 11:30 на Аскольдовому некрополі біля церкви св. Миколая Мирлікійського ховатимуть невідомого героя Майдану, захисника революції гідності, воїна за волю України, патріота українського народу, одного із Небесної Сотні.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Новітнього героя ховатимуть з усіма військовими почестями. Чин похорону розпочнеться о 10:00 в Українському Домі. Похоронна процесія пішим ходом вирушить до церкви св. Миколая Мирлікійського на Аскольдовій Могилі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Кожен хто прагне може долучитись до цієї молитви за усіх спочилих героїв &laquo;Небесної Сотні&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Нагадуємо:</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Аскольдова Могила &ndash; перша християнська церква у Київській Русі</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Аскольдова обитель &ndash; монашества Київської Русі</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Аскольдів некрополь &ndash; один зі ста найбільших некрополів світу</span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"line-height: 1.3em;\">Аскольдова Могила - перший храм України де молився Блаженний Папа Іван Павло ІІ</span></span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Аскольдова Могила &ndash; лоно спочинку усіх героїв України від студентів Крутянців до героїв Небесної Сотні</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава Ісусу Христу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава Україні!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><a href=\"http://www.youtube.com/watch?v=0xT2qFdiM3g&amp;feature=youtube_gdata&amp;hd=1\">http://www.youtube.com/watch?v=0xT2qFdiM3g&amp;feature=youtube_gdata&amp;hd=1</a></span></p>',NULL,NULL,4211,5,1,16);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4236,'P','2014-04-05',1,NULL,'Rss','ПОЛІТИЧНИЙ МЕСІАНІЗМ – ВИНА УКРАЇНСЬКИХ ХРИСТИЯН','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5695,200,1_0_738977_0.jpg\" />Після подій Майдану багато хто твердить про кардинальні зміни в українському суспільстві. Але якщо поглянемо на реалії, які нас оточують, здається, що такі твердження занадто оптимістичні. Хабарі, корупція, безвідповідальність, вседозволеність чиновництва й олігархії, очікування політичного месії, недовіра до влади, обливання брудом політичних конкурентів, маніпуляція інформацією – усе це і чимало іншого й надалі залишається частиною життя українського суспільства.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1996:politychny-mesianizm--vyna-ukrajinskyh-hrystyjan&catid=28:tema-nomera-cat&Itemid=2',4214,NULL,1,26),
(4237,'P','2014-04-05',1,NULL,'Rss','Акафістова субота','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5686,200,pokrov.jpg\" /></p>\n<p>У п\'яту суботу Великого посту наша Східна Церква має особ­ливу богослужбу на честь Пречистої Діви Марії. Ця відправа, характерна тільки для Східної Церкви, має назву акафисту, а звідси й субота зветься Акафистова. Акафистова субота, як і неділя Православ\'я, не має якогось відношення до 40-ці. Вона випадає в тому часі на підставі історич­ної традиції і практики Церкви. На утрені цієї суботи береться цілий акафист Благовіщення Пресвятої Богородиці. Цей акафист можна назвати символом і вершиною великого культу Богоматері у Східній Церкві. Саме тому він заслуговує на окрему увагу.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1997:akafistova-subota&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4215,NULL,1,26),
(4238,'P','2014-04-05',1,NULL,'Rss','Акт посвяти українців під Покров Пресвятої Богородиці','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,5700,200,dsc_0160-8981c.jpg\" style=\"margin: 10px; float: right;\" />У неділю, 6 квітня, УГКЦ відновить Акт посвяти українців під Покров Пресвятої Богородиці.</strong></p>\n<p>Подібні церемонії відбуваються лише у ключові моменти історії України. Востаннє посвяту відновив Блаженніший Мирослав Іван (Любачівський) майже 20 років тому, коли УГКЦ вийшла з підпілля.</p>\n<p>Продовжуючи служіння моїх великих попередників на Києво-Галицькому престолі, знову прагну в сопричасті з усією нашою Церквою в Україні та на поселеннях відновити Акт посвяти українського народу під всемогутній Покров Пресвятої Богородиці.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1998:akt-posvjaty-ukrajinciv-pid-pokrov-presvjatoji-bogorodyci&catid=27:gk-v-ukrajini-cat&Itemid=57',4218,NULL,1,26),
(4239,'P','2014-04-05',1,NULL,'Rss','Дитяча любов (6 квітня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,5699,200,b235a3.jpg\" style=\"margin: 10px; float: left;\" />Вулицею йшла дівчинка і вела за руку дуже п’яну жінку, яка горбилась і лаяла когось. Її волосся було розкуйовджене, а одежа дуже брудна. Люди навколо не приховували своєї огиди і відхилялися вбік, а хтось обурено сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Жахіття. Яка п’яниця.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=1999:dytjacha-ljubov-6-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4219,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4240,'P','2014-04-06',1,NULL,'Rss','Благовіщення Пречистої Діви Марії','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,5703,200,blagovest.jpg\" style=\"margin: 10px; float: right;\" />Біблійний коментар&nbsp;</strong><span style=\"font-size: 0.875em;\">Євангеліє від Луки 1,26-38</span></p>\n<p><sup>24</sup>Після тих днів зачала Єлисавета, його жінка, й таїлася п\'ять місяців, кажучи: <sup>25</sup>“Так учинив мені Господь у ці дні, коли зглянувся, щоб зняти мою ганьбу між людьми.” <sup>26</sup>Шостого місяця ангел Гавриїл був посланий Богом у місто в Галилеї, якому ім\'я Назарет, <sup>27</sup>до діви, зарученої чоловікові, на ім\'я Йосиф, з Давидового дому; ім\'я ж діви було Марія. <sup>28</sup>Ввійшовши до неї ангел, сказав їй: “Радуйся, благодатна, Господь з тобою! Благословенна ти між жінками.”</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2000:blagovishchennja-prechystoji-divy-mariji&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4220,NULL,1,26),
(4241,'P','2014-04-13',0,NULL,'Note','Розклад Богослужінь на 6-ий тиждень Великого посту 2014 р.Б','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>07.04. понеділок Благовіщення Пресвятої Владичиці нашої Богородиці</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08-00 &nbsp;Утрення. Літургія св. Йоана Золотоустого Освячення води</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18-00 Літургія св. Йоана Золотоустого Молебень Богородиці</span></p>\r\n<blockquote>\r\n<blockquote>\r\n<blockquote>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>13.04.2014 неділя 6-та Великого посту, квітна Вхід Господній в Єрусалим</strong></span></p>\r\n</blockquote>\r\n</blockquote>\r\n</blockquote>\r\n<div style=\"text-align: justify;\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08-15 10-00 12-30&nbsp;<strong>Літургія св.Йоана Золотоустого,освячення верби</strong></span></p>\r\n</div>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>','<blockquote>\r\n<blockquote>\r\n<div>\r\n<div>\r\n<blockquote>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>07.04. понеділок Благовіщення Пресвятої Владичиці нашої Богородиці</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08-00 &nbsp;Утрення. Літургія св. Йоана Золотоустого Освячення води</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18-00 Літургія св. Йоана Золотоустого Молебень Богородиці</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>08.04 вівторок Собор Архангела Гавриїла.Віддання Благовіщення</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>09.04. середа</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">10<strong>.04. четвер</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08-00 - &nbsp;18-00 Церква відкрита на молитву мирян.&nbsp;<strong>У ці дні священики Київської Архиєпархії будуть на реколекціях</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>11.04. п\'ятниця</strong></span></p>\r\n<div class=\"im\" style=\"text-align: justify;\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08-00 Вечірня з Літургією Напередосвячених Дарів Парастас-сорокоусти</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">17:00 Хресна Дорога від Дніпра</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18-00 Вечірня з Літургією Напередосвячених Дарів, Хресна дорога</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n</div>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>12.04. Субота Лазарева.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08-00 Утреня</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">09-00 Літургія Святого Івана Золотоустого. Парастас-сорокоусти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; line-height: 1.3em;\">18-00 Літургія Святого Івана Золотоустого Вечірня</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; line-height: 1.3em;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><strong style=\"text-indent: 0em; font-family: arial, helvetica, sans-serif; font-size: small; line-height: 1.3em;\">13.04.2014 неділя 6-та Великого посту, квітна Вхід Господній в Єрусалим</strong></p>\r\n</blockquote>\r\n</div>\r\n</div>\r\n</blockquote>\r\n</blockquote>\r\n<div>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08-15 10-00 12-30&nbsp;<strong>Літургія св.Йоана Золотоустого,освячення верби</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">телефон для довідок:&nbsp;<strong>(050) 447 31 57</strong></span></p>\r\n</div>',NULL,NULL,4216,5,1,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4242,'P','2014-04-05',0,NULL,'Note','Аскольдова Могила - місце спочинку героя Євромайдану','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,5700,200,1.jpeg\" alt=\"\" width=\"200\" height=\"129\" />5 квітня 2014 року Божого на Аскольдовому некрополі, біля церкви св. Миколая Мирлікійського, поховали невідомого героя Майдану Святослава, захисника революції гідності, воїна за волю України, патріота українського народу, одного із Небесної Сотні.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">На могилі спочилого Святослава спільно молились за усіх спочилих героїв &laquo;Небесної Сотні&raquo;</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,5700,200,1.jpeg\" alt=\"\" width=\"200\" height=\"129\" /></span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><span style=\"font-family: arial, helvetica, sans-serif;\">5 квітня 2014 року Божого на Аскольдовому некрополі, біля церкви св. Миколая Мирлікійського, поховали невідомого героя Майдану Святослава, захисника революції гідності, воїна за волю України, патріота українського народу, одного із Небесної Сотні,&nbsp;згорілого в Будинку профспілок активіста Майдану, який називав себе \"Боєць Горана\".</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><span style=\"font-family: arial, helvetica, sans-serif;\">Згідно слів начальника штабу Національної гвардії Володимира Волкова юнак загинув під час пожежі в Будинку профспілок, але досі не вдалося знайти нікого з родичів померлого. &laquo;Відомо лише, що йому було від 23 до 25 років і він належав до організації &laquo;Білий Молот&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Новітнього героя поховали з усіма військовими почестями. Чин похорону спільно провадили отці УГКЦ та УПЦКП, він розпочався о 10:00 в Українському Домі, опісля похоронна процесія пішим ходом рушила до місця спочинку Святослава біля церкви св. Миколая Мирлікійського на Аскольдовій Могилі, де спочили його ж побратими - студенти крутянці у 1918 році.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Участь у чині відспівування взяв вікарій Київської єпархії, керуючий справами Київської Патріархії єпископ Вишгородський Агапіт з братією Свято-Михайлівського Золотоверхого монастиря.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Від Української Греко-Католицької Церкви участь взяли: Йосиф (Мілян) - єпископ-помічник Київської архиєпархії УГКЦ та настоятель Свято-Миколаївського храму на Аскольдовій могилі прот. Ігор Онишкевич.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">На могилі спочилого Святослава спільно молились за усіх спочилих героїв &laquo;Небесної Сотні&raquo;</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Нагадуємо:</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Аскольдова Могила &ndash; перша християнська церква у Київській Русі</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Аскольдова обитель &ndash; монашества Київської Русі</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Аскольдів некрополь &ndash; один зі ста найбільших некрополів світу</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Аскольдова Могила - перший храм України де молився Блаженний Папа Іван Павло ІІ</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Аскольдова Могила &ndash; лоно спочинку усіх героїв України від студентів Крутянців до героїв Небесної Сотні</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава Ісусу Христу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава Україні!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">фото: <a href=\"http://kiev.unian.ua/\">http://kiev.unian.ua/</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">відео: <a href=\"http://faktu-week.ictv.ua/ua/index/view-media/id/58761?play=1\">http://faktu-week.ictv.ua/ua/index/view-media/id/58761?play=1</a></span></p>',NULL,NULL,4217,5,1,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4243,'P','2014-04-06',1,NULL,'Rss','Блага вість','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5686,200,20120407130246.jpg\" />\"Архангельським голосом кличемо до Тебе, Пречиста: \"Радуйся благодатна, Господь з Тобою\"<em></em></p>\n<p><em>Величання празника</em></p>\n<p>\"Днесь спасення нашого начало і від віку таїнства явління: Син Божий  сином Діви стається\" (Тропар празника). Ось таким по­хвальним гимном  наша Церква вітає празник Благовіщення Пресвя­тої Богородиці, називаючи  його початком нашого спасення. І слуш­но, бо цей празник належить до  найважливіших свят нашого цер­ковного року.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2001:blagovishchennja-prechystoji-divy-mariji&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4221,NULL,1,26),
(4244,'P','2014-04-07',1,NULL,'Rss','Блаженніший Святослав віддав український народ під Покров Пресвятої Богородиці','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5686,200,pid_pokrov4.jpg\" />Отець і Глава Української Греко-Католицької Церкви Блаженніший Святослав (Шевчук) відновив Акт посвяти українського народу під Покров Богородиці. «Під свій Покров святий, Діво свята, прийми дітей своїх, о, Всеблага», - сказав Предстоятель Церкви.</p>\n<p>ідновлення Акту посвяти українського народу під Покров Діви Марії відбувається в складний історичний період українського народу. Так учинив 1037 року князь Ярослав Мудрий, коли нависла загроза для Київської держави з боку кочових племен із південного сходу. Такий крок повторив і Блаженніший Мирослав Іван (Любачівський) на зорі становлення Української держави.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2002:blazhennishy-svjatoslav-viddav-ukrajinsky-narod-pid-pokrov-presvjatoji-bogorodyci&catid=27:gk-v-ukrajini-cat&Itemid=57',4222,NULL,1,26),
(4245,'P','2014-04-07',1,NULL,'Rss','Найгарніша (7 квітня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,5709,200,buket.jpg\" style=\"margin: 10px; float: left;\" />Надумав якось хлопець одружитися і пішов до дружини нареченої. Приніс він подарунки і солодощі. Брати нареченої питаються жениха:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Чому ти береш за себе нашу сестру?<o:p></o:p></p>\n<p>І відповів їм жених казкою притчею:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Дано, давно, велетенський дракон захопив єдине джерело у краї, люди залишилися без води, жінки плакали, діти стогнали від спраги.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2003:chetverty-mudrec-7-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4223,NULL,1,26),
(4246,'P','2014-04-07',1,NULL,'Rss','Дослідники Туринської плащаниці досліджують «техніку» розп\'яття','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5705,200,shroudface1.jpg\" />Спеціалісти, що входить до міжнародної групи дослідників, що займаються вивченням Туринської плащаниці, прийшли до висновку, що смерть на хресті могла бути більш болісною, ніж вважалося раніше. Дослідження, проведені вченими останнім часом, поставили питання про \"техніку\" страти на хресті. Про це повідомила в п\'ятницю Британська газета The Daily Mail.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2004:2014-04-07-08-11-31&catid=32:gk-svit-cat&Itemid=58',4224,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4247,'P','2014-04-07',1,NULL,'Rss','«Основне послання свята Благовіщення - те, що Господь нам дає, ми повинні прийняти…»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5700,200,zwiastowanie.jpg\" />Свято Благовіщення Пресвятої Богородиці святкують християни як східної, так і західної церковних традицій. Воно оповідає нам про подію, коли Архангел Гавриїл з’явився Діві Марії, сповіщаючи про те, що незабаром вона народить Месію – Спасителя світу.</p>\n<p>Це свято заставляє нас призадуматися про дар батьківства і материнства, а також і те, як родина повинна приймати Господню волю. Про це розповідає о. Василь Рудейко, доктор богослов\'я, заступник голови Патріаршої літургійної комісії УГКЦ, священик храму Священномученика Климентія Шептицького у Львові.</p>\n<p><strong>&nbsp;</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2005:2014-04-07-08-47-25&catid=28:tema-nomera-cat&Itemid=2',4225,NULL,1,26),
(4248,'P','2014-04-07',1,NULL,'Rss','Днесь спасення нашого начало','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5708,200,Blagov5-620x250.jpg\" />Людина, сотворена на Божий образ і Його подобу (див.: Бут. 1, 26; 2, 7), знехтувала наказ свого Господа, а зважила на слова спокусника і вчинила за його намовою. Так, гріхопадіння відвернуло людину від її Творця і Господа. Втративши первинну невинність, вона спотворила у собі Божий образ. Проте Творець не відвернувся від людини, яка заслуговувала на кару, а продовжив із нею свій діялог.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2006:dnes-spasennja-nashogo-nachalo&catid=27:gk-v-ukrajini-cat&Itemid=57',4226,NULL,1,26),
(4249,'P','2014-04-07',1,NULL,'Rss','Сила любови (8 квітня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,5709,200,kreshenie.jpg\" style=\"margin: 10px; float: left;\" />Якось ішов до себе старець в пустеню з молодим учнем. Дорога вела до гори. Юнак випередив свого учителя і неждано і негадано зустрів на завороті язичницького жреця.<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Відступи слуго диявола, - гукнув до нього надміру запальний юнак.<o:p></o:p></p>\n<p>Жрець розсердився і щосили вдарив кривдник а палицею, юнак упав без пам’яті.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2008:syla-ljubovy-8-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4227,NULL,1,26),
(4250,'P','2014-04-07',1,NULL,'Rss','Зі Слова Святителя Андрея Критського на Благовіщення Пресвятої Богородиці','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5709,200,402-1.JPG\" />Днесь благовіщення радості, свідчення чоловіколюбства Бога, радісна проповідь про спасіння всього світу. Звідки, від кого і кому? – З небес, від Бога, до Діви, обрученої мужу. Хто ця Діва? Хто цей муж? Які їх імена? Діві – Марія, а мужу – Йосиф: обоє з роду Давида. Хто виконавець божественних повелінь і звідки він приходить? Архангел Гавриїл, з висоти посланий для служіння чуду: таїнство найдивовижніше в порівнянні зі всім, достойним дивування, повинен був возвістити служитель Вишнього, злетівши з горніх країв на землю. Що ж саме?<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2007:zi-slova-svjatytelja-andreja-krytskogo-na-blagovishchennja-presvjatoji-bogorodyci&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4228,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4251,'P','2014-04-08',1,NULL,'Rss','Лише Ісус може воскресити умертвлені гріхом частини нашого серця','<p><img src=\"http://askoldova-mohyla.org/uk/image,5711,200,1_1_788515.jpg\" style=\"margin: 10px; float: left;\" />Лише Ісус може допомогти нам вийти з гробниць гріха, з відмерлих частин нашого серця. Такими думками Папа Франциск поділився під час Святої Меси. Коментуючи євангельське читання про воскресіння Лазаря, він закликав виходити з гробів, які кожен з нас має у своєму нутрі.<o:p></o:p></p>\n<p>Але перед Євхаристійним богослуженням Єпископ Риму зустрівся представниками різних парафіяльних груп.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2009:lyshe-isus-mozhe-voskresyty-umertvleni-grihom-chastyny-nashogo-sercja&catid=32:gk-svit-cat&Itemid=58',4229,NULL,1,26),
(4252,'P','2014-04-08',1,NULL,'Rss',' Собор Архангела Гавриїла','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5711,200,ivanov3.jpg\" />Архангел Гавриїл</strong> - благовісник таїн Божих.</p>\n<p>Ім\'я Гавриїл (גַּבְרִיאֵ֕ל) в перекладі з єврейської мови означає: чоловік Божий, фортеця Божа, сила Божа.</p>\n<p>Архангел Гавриїл один із семи Архангелів, що посилаються Богом для сповіщення людям великих справ Божих. Архангел Гавриїл кілька разів згадується в Писанні, як Небесний вісник, якого Бог посилає, щоб донести людям Свої плани про порятунок роду людського.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2010:-sobor-arhangela-gavryjila&catid=35:istorychni-postati-cat&Itemid=48',4230,NULL,1,26),
(4253,'P','2014-04-08',1,NULL,'Rss','Священнослужителям забороняється здійснювати будь-яку агітацію за кандидата, партію чи політичний блок, – Інструкція','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5713,200,vybory_82bb8.jpg\" />Синод Єпископів Києво-Галицького Верховного Архиєпископства 4 квітня 2014 року видав Інструкцію про поведінку священиків УГКЦ під час політичних виборчих кампаній. Цей документ містить конкретні вказівки щодо поведінки священнослужителів під час виборчих кампаній різного рівня.</p>\n<p>Як відомо, спеціальна комісія Синоду Єпископів Києво-Галицького Верховного Архиєпископства вивчала випадки зловживання духовенства у виборчій парламентській кампанії 2012 року.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2012:svjashchennosluzhyteljam-zaboronjajetsja-zdisnjuvaty-bud-jaku-agitaciju-za-kandydata-partiju-chy-politychny-blok--instrukcija&catid=37:spilnota-cat&Itemid=49',4231,NULL,1,26),
(4254,'P','2014-04-08',1,NULL,'Rss','Волоцюга і перукар (9 квітня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5713,200,getImage.jpg\" />Прийшов один чоловік у перукарню. І час стриження і гоління заговорив він з перукарем про Бога… В якийсь момент перукар твердо сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Хай що би ви мені не говорили, а я не вірю, що Бог є.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чому? – запитав клієнт.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2011:volocjuga-i-perukar-9-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4232,NULL,1,26),
(4255,'P','2014-04-09',1,NULL,'Rss','Чи всі чоловіки однакові? (10 квітня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5714,200,artleo.com-6681.jpg\" />Якось сказала внучка до бабусі:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Усі чоловіки однакові. Бабусю, йому досить сказати мені перший раз привіт, а я вже знаю як він себе поводитиме, які байки розказуватиме, як усміхатиметься, сваритиметься і йтиме.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2013:chy-vsi-choloviky-odnakovi-10-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4233,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4256,'P','2014-04-09',1,NULL,'Rss','Що таке справжня мудрість','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5714,200,11.jpg\" />9 квітня Папа Франциск почав новий цикл катехез, присвячений даром Святого Духа і першу катехизу циклу він присвятив дару мудрості.<o:p></o:p></strong></p>\n<p>Святий Дух — це любов Бога, який робить з нашого серця свій будинок і входить в єдність з нами, вважає Папа, Він завжди з нас, Він в нашому серці.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2014:shcho-take-spravzhnja-mudrist&catid=28:tema-nomera-cat&Itemid=2',4234,NULL,1,26),
(4257,'P','2014-04-09',1,NULL,'Rss','Новітні герої','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,5714,200,1003405.jpg\" style=\"margin: 10px; float: left;\" />5 квітня,&nbsp;на Аскольдовій могилі в Києві попрощалися зі ще одним бійцем Небесної сотні. На жаль, фактично безіменним, бо встановити достеменно особу полеглого не вдалося.</strong><strong> </strong></p>\n<p>Хлопець загинув 18 лютого у палаючому Будинку профспілок, коли його штурмував спецпідрозділ «Альфа». Його вважають бійцем організації «Білий Молот», яка до 6 березня входила до «Правого сектору».</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2016:novitni-geroji&catid=25:parafija-cat&Itemid=27',4235,NULL,1,26),
(4258,'P','2014-04-10',1,NULL,'Note','ЗВІТ №23 про розподілення благодійних пожертвувань парафіян церкви св.Миколая на Аскольдовій могилі (05.03.2014 р.)','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Гуманітарний вантаж Благодійному фонду &ldquo;Сузір\'я&rdquo; в с.Іванкові Бориспільського р-ну Київської обл.:</span></p>\r\n<ul>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">одяг чоловічий, жіночий, дитячий &ndash; 15 мішків</span></li>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">взуття &ndash; 1 мішок</span></li>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">іграшки дитячі &ndash; 1 мішок</span></li>\r\n</ul>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Служба милосердя при церкві св.Миколая на Аскольдовій могилі, зібравши пожертвувані парафіянами: одяг чоловічий, жіночий та дитячий, взуття, іграшки дитячі та інші речі особистого вжитку, розподілила їх таким чином:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash; уділила потребуючим мирянам;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash; відправила гуманітарний вантаж Благодійному фонду &ldquo;Сузір\'я&rdquo; в с.Іванкові Бориспільського р-ну Київської обл.:</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">одяг чоловічий, жіночий, дитячий &ndash; 15 мішків</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">взуття &ndash; 1 мішок</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">іграшки дитячі &ndash; 1 мішок</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Робота виконана силами волонтерів: п.Мирослави, п.Василя, п.Анни.</span></p>',NULL,NULL,4237,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4259,'P','2014-04-10',1,NULL,'Rss','«Хрест– це таємниця, таїнство Божої любові»','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5714,200,p_29683_1_gallerybig.jpg\" />Християнство неможливе без хреста. Але слід пам’ятати, що хрест – це не прикраса, але таїнство Божої любові, говорив Папа Франциск.</strong><o:p></o:p></p>\n<p>У Старому Завіті розповідається про те, як під час переходу через пустелю Ізраїльський народ нарікав на Бога і на Мойсея. Як кару, Господь наслав на їхній табір отруйних змій, і люди, збагнувши свій гріх, просили про знак спасіння. Це читання з Книги Чисел спонукало Святішого Отця поділитися думками про смерть у гріху. Про це також перестерігав Ісус в євангельському читанні, попереджаючи фарисеїв: «Помрете у гріхах ваших».<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2015:2014-04-09-20-50-14&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4238,NULL,1,26),
(4260,'P','2014-04-10',1,NULL,'Rss','Професор і човняр (11 квітня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5721,200,images.jpg\" />Якось один із найвідоміших у світі професорів, номінант на Нобелівську премію, вийшов на берег озера і попросив човняра покатати його на човні. Той згодився. Коли вони відплили далеко від берега, професор почав розпитувати човняра:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ти знаєш історію?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ні.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2017:profesor-i-chovnjar-11-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4239,NULL,1,26),
(4261,'P','2014-04-10',1,NULL,'Note','ДОСТУП ДО ІНФОРМАЦІЇ – РАДИКАЛЬНІ ЗМІНИ','<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<img style=\"float: left;\" src=\"image,5724,200,7887.jpg\" alt=\"\" width=\"200\" height=\"133\" />Будь-хто може відвідати сесію Верховної чи місцевої ради;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Генплани стали відкритими;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Персональні дані чиновників стали доступні;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Держстатистика стала відкритою та безкоштовною;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Декларації чиновників будуть друкуватися в мережі Інтернет;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Інформацію про приватизацію об&rsquo;єктів будуть друкуватися в мережі Інтернет;</span></div>','<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5724,200,34635.jpg\" alt=\"\" width=\"105\" height=\"200\" />27 березня 2014 року Верховна Рада ухваливши законопроект №0947, запровадила ряд суттєвих кроків для отримання необхідної інформації, а саме:</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Будь-хто може відвідати сесію Верховної чи місцевої ради;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Генплани стали відкритими;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Персональні дані чиновників стали доступні;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Держстатистика стала відкритою та безкоштовною;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Декларації чиновників будуть друкуватися в мережі Інтернет;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Інформацію про приватизацію об&rsquo;єктів будуть друкуватися в мережі Інтернет;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;За приховування інформації встановлюються конкретний штраф: 425-850 грн.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Користуймося, це унікальний шанс!!!</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Якщо Вам буде потрібно отримати необхідну інформацію у короткі строки &ndash; 5 робочих днів шляхом надсилання</strong><strong>&nbsp;</strong><strong>адвокатського запиту &ndash; будь ласка, пишіть &ndash;&nbsp;</strong><strong><a href=\"mailto:serg.gula@gmail.com\">serg.gula@gmail.com</a></strong></span></div>',NULL,NULL,4241,5,1,30);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4262,'P','2014-04-10',1,NULL,'Note','Пасхальний кошик 2014','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,5721,200,534599.jpg\" alt=\"\" width=\"200\" height=\"133\" />В рамках даної акції планується передача посилок для 37-ми дітей.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">На даний час діти проживають з мамами, які знаходиться в місці позбавлення волі або з бабусями чи з рідними.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"text-decoration: underline; font-family: arial, helvetica, sans-serif; font-size: small;\">До&nbsp;17&nbsp;квітня&nbsp;потрібно зібрати посилочки&nbsp;для 37-х дітей.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,5721,200,534599.jpg\" alt=\"\" width=\"200\" height=\"133\" />Друзі, давайте разом&nbsp;привітаємо із Пасхальними святами&nbsp;дітей, батьки яких знаходяться у місцях позбавлення волі.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">В рамках даної акції планується передача посилок для 37-ми дітей.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">На даний час діти проживають з мамами, які знаходиться в місці позбавлення волі або з бабусями чи з рідними.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"text-decoration: underline; font-family: arial, helvetica, sans-serif; font-size: small;\">До&nbsp;17&nbsp;квітня&nbsp;потрібно зібрати посилочки&nbsp;для 37-х дітей.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Загалом&nbsp;<span style=\"text-decoration: underline;\">потрібно:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- 14 кг. Цукерок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- 9 кг. Печива</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- 9 кг. Вафлі</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- 8 кг. Зефіру</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- 40 шт. шампуню</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- 90 шт. туалетного мила (по 2-ва шматки)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- 40 шт. зубної пасти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- 40 шт. зубних щіток&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- 40 дитячих книжок&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Якщо у Вас є можливість можете допомогти коштами, на які буде закуплено продукцію з даного списку і чеки будуть викладені в інтернеті.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><span style=\"text-decoration: underline;\">Контактна особа:</span>&nbsp;представник парафіяльної служби милосердя української греко-католицької церкви&nbsp;св. Миколая Чудотворця на Аскольдовій Могилі &ndash;&nbsp;<span style=\"text-decoration: underline;\">Анна&nbsp;&nbsp;+38 (066) 556-47-37</span>&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Також, давайте разом привітаємо із Великоднем дітей-сиріт із таких дитячих будинків Закарпаття та Київщини:</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- Перечинська школа-інтернат&nbsp;(120 дітей)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- Часлівецька школа-інтернат (67 дітей)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- Чинадіївський дитячий будинок інтернатного типу (110 дітей)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- Вільшанський будинок інтернат ІІІ та ІV&nbsp;категорії (250 дітей)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- Притулок с.м.т. Батеєво (63 дитини)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- Київський притулок (50 дітей)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- Мокрецький дитячий будинок Броварського району (25 дітей)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Для кожної дитини необхідно підготувати кошик із пасхальним кулічем, яйцями, солодощами, іграшками та духовною літературою.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"text-decoration: underline; font-family: arial, helvetica, sans-serif; font-size: small;\">Термін збору подарунків - до 17 квітня 2014 року.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Контактна особа: отець Кирило&nbsp;+ 38 (098) 586-96-92; + 38 (068) 355-30-81&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><span style=\"text-decoration: underline;\">Місце збору</span>:&nbsp;м.Київ, вул. Гусовського 8/10 (<a href=\"http://mapia.ua/ua/kyiv/addresses/str-gusovskogo-810\">на карті</a>)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Давайте разом допоможемо привітати цих діток з Пасхою, щоб і в їхні оселі прийшла радість Світлого Христового Воскресіння!</strong></span></p>',NULL,NULL,4240,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4263,'P','2014-04-10',1,NULL,'Rss','Парафіяльне служіння','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5724,200,divizka-teresa-17.jpg\" />Дорогі сестри, подаємо вам розважання над Святим Письмом на квітень 2014.</p>\n<p>Наше служіння парафіяльного «Карітас-Печерськ» продовжується для вас силами волонтерів, які і далі залишаються і моляться за ваше навернення.</p>\n<p>Сьогодні наша Батьківщина переживає агресію північного сусіда Росії. Злобою Путіна у нас захопили Кримську землю, але це до часу. Слово Боже для всіх є спасінням, а особливо у ваших умовах. Надіємось на спілкування і спільну молитву любові.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2018:parafijalne-sluzhinnja&catid=25:parafija-cat&Itemid=27',4242,NULL,1,26),
(4264,'P','2014-04-10',1,NULL,'Rss','«Соціальна думка Митрополита Андрея Шептицького: міжнаціональні стосунки та державне будівництво»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5724,200,philos_school.jpg\" />Інститут релігії і суспільства УКУ запрошує подавати заявки на участь у літні школі 2014 р. <strong>« Соціальна думка Митрополита Андрея Шептицького:</strong><strong> </strong><strong>міжнаціональні стосунки та державне будівництво»</strong></p>\n<p>Мета школи – глибше ознайомити учасників з духовним спадком та діяльністю Митрополита Андрея та розвинути їхнє вміння жити згідно з християнськими цінностями у суспільному житті. Головний акцент цьогорічної школи буде поставлено на побудові суспільства на засадах християнської моралі й цінностей у історичному і сучасному контексті.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2019:2014-04-10-19-40-17&catid=27:gk-v-ukrajini-cat&Itemid=57',4243,NULL,1,26),
(4265,'P','2014-04-10',1,NULL,'Rss','Лазарева субота','<p><img src=\"http://askoldova-mohyla.org/uk/image,5724,200,stamatis.jpg\" style=\"margin: 10px; float: right;\" /></p>\n<p>Всесвітліші, всечесні отці, преподобні сестри, дорогі брати. Сьогодні ми переживаємо з вами Лазареву суботу. Це є такий особливий день в літургійному році. Ви, мабуть, вчора, як співали стихир на Літургії Наперед Освячених Дарів, то чули такі слова, що ось закінчилася Чотиридесятниця, фактично закінчився Великий піст і сьогодні ми починаємо Квітну тріоть: новий період, новий цикл в літургійному році, який безпосередньо прив’язаний до Пасхального Таїнства. Всі ті дні, які будемо переживати сьогодні, завтра, наступного тижня, безпосередньо вже впроваджують нас до цього Таїнства страждання, смерті і воскресіння Ісуса Христа.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2021:lazareva-subota&catid=28:tema-nomera-cat&Itemid=2',4244,NULL,1,26),
(4266,'P','2014-04-11',1,NULL,'Rss','Тернистий шлях Йосипа Сліпого','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,5724,200,ArticleImages_54141_2-14.jpg\" style=\"margin: 10px; float: left;\" />20 листопада виповниться 25 років з дня легалізації УГКЦ. А у вересні буде видано книжку спогадів Патріарха, які він після звільнення з ув’язнення писав і ховав у шухляду. Книгу опрацювала комісія в Римі, створивши до неї додаток з іменами осіб, яких згадує Блаженніший і які вже могли затертися в пам’яті нащадків.</strong></p>\n<p>Його називають «патріархом». Намісником великого Митрополита Йосип Сліпий став після смерті Андрея Шептицького у листопаді 1944 року. Не минуло й півроку, як його заарештували в резиденції біля собору святого Юра у Львові, і більше він не бачив України.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2020:ternysty-shljah-osypa-slipogo&catid=35:istorychni-postati-cat&Itemid=48',4245,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4267,'P','2014-04-11',1,NULL,'Rss','Папа про диктатуру єдиноправильного мислення','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5724,200,8322cc88cb.jpg\" />Господь обіцяв Авраамові, що він стане прабатьком багатьох народів, але він та його потомки повинні дотримуватися союзу, встановленого з Богом.</strong><o:p></o:p></p>\n<p>На основі читання з Книги Буття, в якому розповідається про ці обітниці, він поділився думками про замкненість фарисеїв на Ісусову благовість. Помилкою фарисеїв було те, що вони розривали зв’язок між заповідями і Божим серцем. Вони вважали, що все вирішувалось лише дотримуванням заповідей, які, однак, не є «холодним законом», тому що зродилися на основі любові, і є, радше, «вказівками», які допомагають не помилитися.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2022:papa-pro-dyktaturu-jedynopravylnogo-myslennja&catid=32:gk-svit-cat&Itemid=58',4246,NULL,1,26),
(4268,'P','2014-04-11',1,NULL,'Rss','Три найстрашніші людські вади (12 квітня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5729,200,makaku.jpg\" />Одного разу, повернувшись із прогулянки, мудрець застав своїх учнів за бурхливими дебатами. Він запитав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Про що ви сперечаєтеся?</p>\n<p>Усі відповідали:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Учителю, ми прагнемо визначити, які три людські вади найстрашніші.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; І якого ж висновку ви дійшли?</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2023:try-nastrashnishi-ljudski-vady-12-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4247,NULL,1,26),
(4269,'P','2014-04-12',1,NULL,'Rss','Коли плачуть польові командири','<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5730,200,ArticleImages_56062_03.jpg\" />Був славетний наш князь Ізяслав, який на схилі своїх літ закликав до себе синів. Дав він їм по тонкому прутику в руку та попросив показати, чи зможуть вони його зламати. Здивувались сини легкості завдання, спокійно зламали тоненькі прутики. Тоді князь узяв кілька прутиків, міцно скрутив їх та передав синам – але не змогли вони нічого з ними зробити. Отож якщо ми будемо кожен сам за себе та десь поодинці – нас здолає будь-який ворог. А якщо ми переплетемось в одне-єдине – ніякий ворог нам не страшний…</em></p>\n<p>Саме з такою промовою починались спільні солдатські молитви під час недільної поїздки з військовими капеланами та музикантами по блокпостам у Херсонській області на «кордоні» з Кримом. І саме тоді ми всі стали свідками, як плачуть польові командири.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2024:koly-plachut-polovi-komandyry&catid=37:spilnota-cat&Itemid=49',4248,NULL,1,26),
(4270,'P','2014-04-12',1,NULL,'Rss','Вхід Господній в Єрусалим','<p align=\"right\"><em><img src=\"http://askoldova-mohyla.org/uk/image,4502,200,verbno.jpg\" style=\"margin: 10px; float: right;\" />«Величаємо Тебе,&nbsp; життєдавче&nbsp; Христе, осанна</em><em> в вишніх ми співаємо Тобі. Благословен</em><em> хто йде, у ім\'я Господнє.»</em><o:p></o:p></p>\n<p>Сьогодні мільйони православних християн з вербою в руках, виходять на зустріч Божому Синові і співаючи пісню похвали: «Осанна на висотах Сину Давидів, благословен грядий, у ім\'я &nbsp;Господнє», прославляють Його тріумфальний в\'їзд до м. Єрусалиму, древньої столиці Ізраїлю.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2025:vhid-gospodni-v-jerusalym&catid=28:tema-nomera-cat&Itemid=2',4249,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4271,'P','2014-04-12',1,NULL,'Rss','Хмара і дюна (13 квітня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4502,200,images.jpg\" />Одна молода хмара народилася над Середземним морем. Не встигла і вирости як вітер прогнав її разом з усіма родичами геть у далеку Африку. Щойно вони прибули на континент клімат змінився. Угорі палило сонце а внизу переливалися золотом розпечені піски сахари. Вітер вирішив погнати далі у джунглі тому, що в пустелі дощі не падають. Але допитлива молода хмарка відстала від гурту щоб оглянути велику пустелю.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2026:hmara-i-djuna-13-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4250,NULL,1,26),
(4272,'P','2014-04-12',1,NULL,'Rss','Хто на якого Месію чекає?','<p><img src=\"http://askoldova-mohyla.org/uk/image,4502,200,ArticleImages_44708_vjizd.jpg\" style=\"margin: 10px; float: right;\" />Виходячи з суто людських міркувань, Пальмова неділя була найбільшим тріумфом Ісуса. Йому випадало й раніше збирати великі натовпи людей, коли помножував хліб чи проголошував Нагірну проповідь. Та ці події відбувалися в глибокій провінції, якою була Галілея, серед простих невчених людей. Тепер Його вітають у столиці, святому місті, де в Храмі мешкає Божа Слава. Вітають натовпи євреїв з усього світу, паломників, що почали з’їжджатися з нагоди Пасхи. Та й вітають Ісуса не як учителя (раббі), але як Месію, Божого Помазаника і Спасителя: титул «син Давида», яким Його величають, прямо вказує на те, за кого Ісуса приймає сповнений радості натовп.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2027:hto-na-jakogo-mesiju-chekaje&catid=32:gk-svit-cat&Itemid=58',4251,NULL,1,26),
(4273,'P','2014-04-13',1,NULL,'Rss','Кримська криза і цивілізаційні шанси України','<p><img src=\"http://askoldova-mohyla.org/uk/image,4502,200,03_02_16_20_web.jpg\" style=\"margin: 10px; float: left;\" />Пригадую, в середині 1990-х років не один мій співбесідник переконано повторював, як мантру: «Ось Ви так захищаєте кримських татар. А вони ж знову нас зрадять, як у часи Хмельницького зрадили під Берестечком». Йшлося про те, що кримські татари відвели тоді свої війська з поля бою, внаслідок чого козацька армія зазнала нищівної поразки.</p>\n<p>У деяких головах ця схема закріпилася намертво – дарма що впродовж більш як двадцяти років нашого відродження не було в Криму вірнішої Україні етнічної групи, ніж кримські татари.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2028:krymska-kryza-i-cyvilizacini-shansy-ukrajiny&catid=37:spilnota-cat&Itemid=49',4252,NULL,1,26),
(4274,'P','2014-04-13',1,NULL,'Rss','Послання Блаженнішого Святослава до священнослужителів УГКЦ на Страсний четвер','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,5699,200,blazhennis.jpg\" style=\"margin: 10px; float: right;\" />Всесвітліші, всечесні та преподобні отці − дорогі співбрати в Христовому священстві!</strong></p>\n<p><strong>Знову наближаємося до Великого четверга, коли, ідучи на хресну смерть і відкриваючи двері у воскресіння, наш Божественний Спаситель установив святе таїнство Священства. У зв’язку з цим прагну роздумати разом із вами над тим великим даром, учасниками якого ми стали тоді, коли Христос покликав нас до участі в Його священстві. </strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2029:poslannja-blazhennishogo-svjatoslava-do-svjashchennosluzhyteliv-ugkc-na-strasny-chetver&catid=27:gk-v-ukrajini-cat&Itemid=57',4254,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4275,'P','2014-04-13',1,NULL,'Rss','Як знайти вірних друзів (14 квітня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5699,200,images.jpg\" />Вистромивши голову з гнізда орленя побачило силу силенну птахів, що літали внизу серед скель.<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Мамо, що це за птахи? – спитав він.<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Наші друзів, – відповіла орлиця синові, – орел живе самотньо – така його доля, але й він подеколи потребує товариства. Який же він тоді цар птахів? Усі , кого ти бачиш внизу, - наші вірні друзі.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2030:jak-znaty-virnyh-druziv-14-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4255,NULL,1,26),
(4276,'P','2014-04-14',1,NULL,'Rss','Страсний тиждень','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4667,200,7062.jpg\" />На порозі Воскресіння християн чекає Страсний тиждень. Час, коли віруючі люди мають згадати і співпереживати драматичні події життя Ісуса Христа. Водночас потрібно морально готуватися до найбільшого свята в році ‒ Пасхи, і всіма силами намагатися очиститися духовно і бути готовим до його сприйняття.</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2032:strasny-tyzhden&catid=32:gk-svit-cat&Itemid=58',4257,NULL,1,26),
(4277,'P','2014-04-14',1,NULL,'Rss','Найголовніше  (15 квітня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5740,200,samaryanin.jpg\" />Жив собі цар, і все життя не давали йому спокою три питання: яка найголовніша пора? Хто найголовніша людина? І яка найголовніша справа?</p>\n<p>Думав цар: «якщо знатиму відповіді на ці питання, весь світ підкорю, здійсню все, що забажаю, і стане народ шанувати мене, як великого мудреця».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2033:nagolovnishe-15-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4258,NULL,1,26),
(4278,'P','2014-04-14',1,NULL,'Rss','Золоті думки Йоана Павла ІІ','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5739,200,Jan.jpg\" />Пропонуємо вашій увазі збірку цитат із висловлювань блаженного Йоана Павла&nbsp;ІІ.</strong><o:p></o:p></p>\n<p>«Нехай зійде Дух Твій і оновить обличчя землі… Цієї землі!»<o:p></o:p></p>\n<p>«Не бійтеся таємниці Бога. Не бійтеся Його любові».<o:p></o:p></p>\n<p>«Не бійся, не лякайся! Випливи на глибіні!»<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2035:zoloti-dumky-oana-pavla-ii&catid=32:gk-svit-cat&Itemid=58',4259,NULL,1,26),
(4279,'P','2014-04-15',1,NULL,'Rss','«Дбати про своє духовне життя потрібно постійно… а не тільки під час Страсного тижня і Великого посту…»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5739,200,1291190728_2.jpg\" />Розпочався Страсний тиждень, який нагадує нам про те, що Ісус Христос терпів за наш гріхи на хресті. Цей тиждень особливо насичений різними богослужіннями і має свої важливі дні, зокрема Страсний четвер, Велику п’ятницю і суботу… Про це і про важливість Сповіді та Причастя перед Великоднем розповідає о. Василь Рудейко, доктор богослов\'я, заступник голови Патріаршої літургійної комісії УГКЦ, священик храму Священномученика Климентія Шептицького у Львові.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2038:2014-04-14-21-12-49&catid=27:gk-v-ukrajini-cat&Itemid=57',4260,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4280,'P','2014-04-15',1,NULL,'Rss','«Страсний тиждень – велика нагода пережити з Богом смерть, а потім – Воскресіння»','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5739,200,vl_milyan_seminariya.jpg\" />Важливо, щоб жодна краплі крові ні на Голгофі, ні на Майдані не була пролита намарне.</strong></p>\n<p>Владика Йосиф зазначив, що Страсний тиждень – це час наруги над світлом, добром, любов’ю, але це водночас велика нагода пережити з Богом смерть, аби потім пережити Воскресіння.</p>\n<p>«Якщо людина хоча б крок зробить до власного і Божого воскресіння, то Господь зробить до неї набагато більше цих кроків. І на якісь межі вони зустрінуться, бо Бог прийшов у світу, щоб повернути собі людину і ощасливити її», − зазначив єпископ.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2039:2014-04-14-21-15-30&catid=28:tema-nomera-cat&Itemid=2',4261,NULL,1,26),
(4281,'P','2014-04-15',1,NULL,'Rss','Український народ - дорогоцінні сини та дочки','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5745,200,1375034093_151.jpg\" />ПАТРІАРХ ВАРФОЛОМІЙ I ПРО УКРАЇНУ</strong></p>\n<p>«Свята Мати Церква Христа в Константинополі дивиться на український народ, як на дорогоцінних синів і дочок, народжених з вод хрещення, що пливуть руслом ріки Дніпро».</p>\n<p>Так починається послання, яке Патріарх Варфоломій скерував у Вербну неділю до політичних і церковних правосланих зверхників України. Він наголошує, що любов Константинопольської Матері-Церкви до побожних християн цих земель триває понад тисячу років і ніколи не ослабла.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2041:ukrajinsky-narod-dorogocinni-syny-ta-dochky&catid=32:gk-svit-cat&Itemid=58',4264,NULL,1,26),
(4282,'P','2014-04-15',1,NULL,'Rss','Великий Тиждень','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5745,200,Roermondse_passie.jpg\" />Великий Тиждень – час, який передує Пасхальним святам. У літургії він сформувався поступово. Спершу сам піст перед Великоднем тривав лише три дні. З часом впроваджено період 40-денного посту і виділено як особливий період Великий Тиждень.</strong></p>\n<p>У нього входять: тріумфальний В’їзд Господній до Єрусалима (Вербна неділя), останні дискусії Христа з членами синедріону в Храмі, проречення загибелі Єрусалима та приходу кінця світу, Остання Вечеря і Страсті Господні, а потім славне Воскресіння.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2042:velyky-tyzhden&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4265,NULL,1,26),
(4283,'P','2014-04-16',1,NULL,'Rss','Рецепт перемоги в інформаційній війні простий: говорити правду і викривати брехню','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5748,200,3.JPG\" />Минуле ХХ ст., зокрема його перша половина, записалося в пам’яті людства двома світовими війнами. Уже мало серед живих тих людей, які пам’ятають Другу світову війну з усіма її жахіттями.<o:p></o:p></p>\n<p>У другій половині минулого століття були тільки локальні збройні сутички, а на загал це був період так званої холодної війни. Властиво, це була гра нервів. Хоча людство тішилося відсутністю серйозних збройних конфліктів, наша природа така, що ми багато говоримо про позитив миру, але миром не задовольняємося.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2043:recept-peremogy-v-informacini-vini-prosty-govoryty-pravdu-i-vykryvaty-brehnju&catid=37:spilnota-cat&Itemid=49',4266,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4284,'P','2014-04-15',1,NULL,'Note','Новий християнський сайт для дітей','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5745,200,15-04-14-3.jpg\" alt=\"\" width=\"200\" height=\"149\" />Отець Юстин каже, що тому вони і вирішили запустити сайт, щоб зробити їхній дитячий телепродукт доступним для всіх бажаючих. Тим паче, що Інтернет має значно ширші можливості, ніж телебачення, бо дає змогу будь-коли і будь-де подивитися саме те, що тебе цікавить. Також і свій сайт вони зробили дуже простим і доступним для дітей.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Clarastudio.tv &ndash; безпечний і простий у користуванні інтернет-ресурс, відвідувати який діти можуть самостійно.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,5745,200,15-04-14-3.jpg\" alt=\"\" width=\"200\" height=\"149\" />В Україні запустили новий християнський сайт для дітей&nbsp;<a href=\"http://clarastudio.tv/\">clarastudio.tv</a>. Тут можна дивитися мультфільми, пізнавальні відео-рубрики про різні явища та цікаві місця в Україні, біблійні історії у веселій інтерпретації для дітей. Це веб-сторінка, на якій зібрані проекти християнської мультимедійної студії&nbsp;<a href=\"http://www.clarastudio.com.ua/\">Clara Studio.</a></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&laquo;Вже більше двадцяти років ми займаємося виробництвом дитячого телепродукту, &ndash; розповідає директор Clara Studio монах-капуцин о. Юстин Русін. &ndash; Наші передачі транслювалися не один рік на Першому Національному телеканалі України. Та з приходом нової влади у 2010 році, ми помітили як цілеспрямовано український продукт став витіснятися із медіа-простору. І хоча Перший канал і має освітню місію, виявилося, керівництво стало не зацікавлене у вітчизняному продукті, в медіа-продукті про Україну&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отець Юстин каже, що тому вони і вирішили запустити сайт, щоб зробити їхній дитячий телепродукт доступним для всіх бажаючих. Тим паче, що Інтернет має значно ширші можливості, ніж телебачення, бо дає змогу будь-коли і будь-де подивитися саме те, що тебе цікавить. Також і свій сайт вони зробили дуже простим і доступним для дітей.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Зараз на сайті clarastudio.tv вже можна подивитися такі рубрики: &laquo;Сателіт&raquo; &ndash; пізнавальні розповіді про все у світі (наприклад, пісок, зірки, відлік часу); мультсеріал &laquo;Пригоди лісових друзів&raquo;; &laquo;Розповіді Доброї Книги&raquo; &mdash; мультики на біблійні теми; &laquo;Містечко Надія&raquo; &ndash; міні-передачі про повчальні пригоди дітей; &laquo;Кіт-мандрівник&raquo; &ndash; подорожі цікавими місцями України разом із ведучим-котом; &laquo;Добрі манери&raquo; &ndash; уроки етикету для дітлахів, а також &laquo;Професор&raquo; &ndash; рубрика, яка змусить малих таки відірватися від комп`ютера, щоб самим спробувати повторити експерименти шаленого професора.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Щодня сайт поновлюється свіжими матеріалами, а невдовзі там будуть нові рубрики, виконані у форматі 3D і з новими сучасними героями, які живуть у віртуальному просторі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Варто зазначити, що ця продукція вже транслювалася телекомпаніями Польщі, Словаччини, Німеччини, Італії й отримала визнання серед маленьких європейців і їхніх батьків. На жаль, лише деякі з цих проектів свого часу були представлені на вітчизняному телебаченні. Тепер же українські глядачі отримують можливість познайомитися і заприятелювати з героями дитячих сюжетів. Творча команда Clara Studio говорить, що &laquo;ці короткі історії &ndash; добрі, кумедні, дотепні, пізнавальні, а головне &ndash; вони наші рідні, українські&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Clarastudio.tv &ndash; безпечний і простий у користуванні інтернет-ресурс, відвідувати який діти можуть самостійно.</span></p>',NULL,NULL,4262,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4285,'P','2014-04-16',1,NULL,'Rss','Секрет жонглювання (17 квітня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5749,200,644366.jpg\" />До відомого жонглера прийшов чоловік:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Я хочу навчитися жонглювати як ви, - сказав він.<o:p></o:p></p>\n<p>Жонглер дав йому три кульки:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Основа нашої майстерності – каскад, підкидання трьох кульок. Приходьте, коли опануєте цей трюк.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2045:sekret-zhongljuvannja-17-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4267,NULL,1,26),
(4286,'P','2014-04-16',1,NULL,'Rss','«З довірою відкритися на Бога»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5749,200,khresna-doroga.jpg\" />Коли здається, що все втрачено, тоді втручається Господь Бог силою воскресіння. Коментуючи під час загальної аудієнції у середу, 16 квітня 2014 р., посередині Страсного Тижня, події, пов’язані із стражданнями і смертю Ісуса, на це звернув увагу Папа Франциск. Він зазначив, що «болісний шлях», обраний Христом «у повній свободі», розпочинається актом Юдиної зради, а повнотою пониження стала «хресна смерть».<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2044:2014-04-16-19-34-55&catid=32:gk-svit-cat&Itemid=58',4268,NULL,1,26),
(4287,'P','2014-04-17',1,NULL,'Rss','Як святкувати Великдень','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5739,200,tema_voskres2.jpg\" />Великий Четвер</strong> фактично розпочинає найближче духовне приготування до святкування Великодня – Світлого Празника Христового Воскресіння. За древнім християнським Переданням саме у цей день відбулася Тайна Вечеря, на якій Ісус Христос встановив Таїнство Пресвятої Євхаристії і Таїнство Священства.<o:p></o:p></p>\n<p>Першу Службу Божу в історії людства відправив сам Христос у четвер ввечері у часі Його Пасхальної Вечері з учнями. Того дня розпочалось принесення Безкровної Христової Жертви за Спасіння всього світу. Тому Великий Четвер нічим не поступається у своєму значінні іншим великим святам християнської Церкви.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2034:jak-svjatkuvaty-velykden&catid=28:tema-nomera-cat&Itemid=2',4269,NULL,1,26),
(4288,'P','2014-04-17',1,NULL,'Rss','Любов Ісуса посеред великої ненависті','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,5752,200,poc-iudy1-700x387.jpg\" style=\"margin: 10px; float: right;\" />Останні дні земного життя Христа пов’язані з бурхливими подіями, що відбувалися в Єрусалимі безпосередньо перед святом Пасхи. Фарисеї та книжники вирішили знищити Ісуса, наростає агресія натовпу, про Його тріумфальний вхід уже мало хто пам’ятає… Що тоді робить Господь? Дає свідчення справжньої Любові.</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2046:ljubov-isusa-posered-velykoji-nenavysti&catid=28:tema-nomera-cat&Itemid=2',4270,NULL,1,26),
(4289,'P','2014-04-18',1,NULL,'Rss','Притча про сприйняття дійсності (18 квітня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5752,200,8799.jpg\" />Жив собі в селі старий чоловік, він був дуже бідний, але навіть королі заздрили йому, тому, що в нього був пречудовий білий кінь. Йому пропонували за коня нечувані гроші, але чоловік відповідав:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Цей кінь для мене, не кінь, а товариш. Як можна продати товариша?<o:p></o:p></p>\n<p>Чоловік будував, але не згоджувався продавати коня. Одного ранку він не знайшов коня у стійлі. Зібралося все село і всі почали осуджувати старого:\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2047:prytcha-pro-sprynjattja-disnosti-18-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4271,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4290,'P','2014-04-18',1,NULL,'Rss','Велика п\'ятниця','<p align=\"right\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5752,200,575302.jpg\" />\"Благообразний Йосиф, з древа знявши пречистеє тіло Твоє, плащаницею чистою обвив і ароматами, в гробі новім, покривши, положив\"</p>\n<p align=\"right\"><em><strong>Тропар Великої п\'ятниці</strong></em></p>\n<p>Богослуження Великої п\'ятниці характеризуються гарними обрядами, зворушливими напівами і глибокими змістом гимнами та стихирами. Головна їхня тема — це муки і смерть Господа нашого Ісуса Христа.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2048:velyka-pjatnycja&catid=28:tema-nomera-cat&Itemid=2',4272,NULL,1,26),
(4291,'P','2014-04-18',1,NULL,'Rss','Найвеличніша річ (19 квітня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5752,200,10154296.jpg\" />Колись давно жив один дід. Жив він у старому храмі. До того храму приходили бавитися діти. Одного разу, коли най пустотливіший хлопчик бавився на приступцях храму. До нього прилетіло три горобці. Перший горобець сказав:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Найвеличніша річ у цьому світі – сонце. Завдяки сонцю наш світ такий прекрасний. Але люди, що звикли до його світла – сприймають сонце як щось звичайне.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2049:navelychnisha-rich-19-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4273,NULL,1,26),
(4292,'P','2014-04-19',1,NULL,'Rss','«Христос – перша жертва Небесної сотні»','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,5739,200,pro_majdan.jpg\" style=\"margin: 10px; float: right;\" />Ще в недавньому минулому нашу Церкву і наш народ катували й розпинали, ложно свідчили на нас, у нас стріляли і кидали в тюрми.</strong></p>\n<p>«Однак ми все це пережили і, дякувати Богу, відродилися до нового життя спільноти Христових учнів», − про це йдеться у&nbsp;<a target=\"_blank\" href=\"http://news.ugcc.org.ua/go/aHR0cDovL25ld3MudWdjYy5vcmcudWEvYXJ0aWNsZXMvdmVsaWtvZG5pZV9wb3NsYW5ueWFfYmxhemhlbm4lRDElOTZzaG9nb19zdnlhdG9zbGF2YV83MDA5MC5odG1s\">Великодньому посланні</a>Блаженнішого Святослава, Глави УГКЦ.</p>\n<p>За словами Предстоятеля УГКЦ, сьогодні знову нам погрожують зброєю і залякують розділом країни та захопленням храмів. Але Христос і сьогодні є з нами!</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2037:2014-04-14-21-05-12&catid=28:tema-nomera-cat&Itemid=2',4274,NULL,1,26),
(4293,'P','2014-04-19',1,NULL,'Rss','Церковно-обрядові звичаї Великодня','<p align=\"right\"><em><img src=\"http://askoldova-mohyla.org/uk/image,5758,200,know.jpg\" style=\"margin: 10px; float: left;\" /><strong>Це день, що його створив Господь,</strong></em><strong><em> </em><em>возрадуємося і возвеселімося в нім\" (Прокімен Пасхи).</em></strong></p>\n<p>Празник Христового Воскресення багатий не тільки на величні богослуження, маєстатичні співи і глибокосимволічні обряди, але він багатий також на прегарні церковно-обрядові й народні звичаї. Де­які з тих звичаїв — це вплив християнського культу, а инші похо­дять ще з передхристиянських часів, коли наш народ у той самий час відзначав свято привітання весни й весняного сонця. Християн­ська релігія багато з тих прадавніх народних звичаїв освятила, надала їм християнського значення і символіки та визнала своїми.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2052:cerkovno-obrjadovi-zvychaji-velykodnja&catid=27:gk-v-ukrajini-cat&Itemid=57',4276,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4294,'P','2014-04-17',1,NULL,'Note','Хресна дорога молоді на Аскольдовій Могилі','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5758,200,IMG_0560.JPG\" alt=\"\" width=\"200\" height=\"133\" />Перед Хресною дорогою отець Олексій зауважив, що ми маємо пройти цей короткий шлях молитви з глибоким розумінням і співчуття до тих страждань, через які Христос пройшов заради нас. Адже навіть уявити ці муки дуже складно. Тому ця дорога має бути для нас ще одним великим доказом любові Господа до нас.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5758,200,IMG_0560.JPG\" alt=\"\" width=\"200\" height=\"133\" />Шістнадцятого квітня цього року в останній тиждень посту перед світлим святом Пасхи молодь церкви святого Миколая разом з священиком Олексієм Геником пройшли Хресною дорогою у спільній молитві.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Почалася Хресна дорога о шостій годині цього дня: з берегів славетного Дніпра ми піднімалися київськими схилами до церкви святого Миколая, що на Аскольдовій могилі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Перед Хресною дорогою отець Олексій зауважив, що ми маємо пройти цей короткий шлях молитви з глибоким розумінням і співчуття до тих страждань, через які Христос пройшов заради нас. Адже навіть уявити ці муки дуже складно. Тому ця дорога має бути для нас ще одним великим доказом любові Господа до нас.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,5758,200,IMG_0558.JPG\" alt=\"\" width=\"200\" height=\"133\" />У цій спільній ході взяло участь близько тридцяти людей, серед яких були наші парафіяни, молодь, і спільнота &laquo;Відродження&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Протягом Хресної дороги кожний мав можливість прочитати одну зі стацій цієї молитви. Закінчилася Хресна дорога біля Аскольдової могили, після чого кожний розійшовся зі своїми думками і роздумами по домівках.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Підготував Олег Качур для &laquo;Оранти&raquo;</span></p>',NULL,NULL,4275,5,1,17),
(4295,'P','2014-04-19',1,NULL,'Rss','Світлий Празник Пасхи','<p align=\"right\"><em><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5758,200,know_.jpg\" />\"Христос воскрес із мертвих, смертю смерть подолав, і тим, що в гробах, життя дарував\" (Тропар Пасхи).</strong></em></p>\n<p>З усіх великих празників нашого церковного року найбільш давній, урочистий і радісний — це світлий празник Христового Воскресення. Він, як сказано в ірмосі 8 пісні пасхального канону утрені: \"Цар і Господь, празників празник і торжество торжеств\".</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2053:svitly-praznyk-pashy&catid=27:gk-v-ukrajini-cat&Itemid=57',4277,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4296,'P','2014-04-19',1,NULL,'Rss','Рух і поступ (20 квітня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5752,200,4509417_740bb203.jpg\" />Один літній чоловік мав дуже слабке здоров’я. Одного разу він почув, як Бог наказує йому, штовхати величезний камінь на своєму подвір’ї. День у день, місяць у місяць, рік у рік штовхав він цей камінь. Настав день. Коли сатана прийшов до нього і спитав наскільки далеко він пересунув камінь, штовхаючи його щодня.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ні на дюйм, - відповів старий, - я взагалі його не зсунув.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ну ти і невдаха, відповів йому диявол. Бог ніколи н казав штовхати цей камінь, якби він повелів би це тобі, то ти давно би зсунув його з місця.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2050:ruh-i-postup-20-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4278,NULL,1,26),
(4297,'P','2014-04-20',1,NULL,'Rss','Зустріч з воскреслим Христом','<p><img src=\"http://askoldova-mohyla.org/uk/image,5762,200,1797501.jpg\" style=\"margin: 10px; float: left;\" /></p>\n<p>«Cristus surrexit, venite et videte!» - «Христос Воскрес, прийдіть і подивіться!»<o:p></o:p></p>\n<p>Дорогі брати й сестри, благодатного і святого вам Великодня!<o:p></o:p></p>\n<p>У Церкві, розсіяній по всьому світі відлунює звіщення ангела, звернене до жінок: «Не бійтеся! Я знаю, що ви шукаєте Ісуса розп’ятого. Його нема тут. Він воскрес... Прийдіть і подивіться на місце, де Його було покладено» (Мт 28,5-6). Не бійтеся! Господь Воскрес!<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2054:zustrich-z-voskreslym-hrystom&catid=28:tema-nomera-cat&Itemid=2',4280,NULL,1,26),
(4298,'P','2014-04-20',1,NULL,'Rss','МИ ВСІ ПО-НОВОМУ ПОВИННІ ЗРОЗУМІТИ МІСЦЕ УКРАЇНИ У СВІТОВОМУ КОНТЕКСТІ','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5762,200,88269.jpg\" /></strong></p>\n<p>Цього — Страсного — тижня наша країна знову дихала <a href=\"http://gazeta.dt.ua/internal/putin-i-bagatstvo-narodiv-hotyat-li-russkie-voyny-_.html\" target=\"_blank\">війною</a>. Можна було би сказати, що війна наблизилася&nbsp; на крок, якби вона не була посеред нас, не просочувала нас останні кілька місяців. Великий піст приніс не умиротворення, як то буває, але відчуття руків\'я меча в долоні. Кажуть, війни на мають кінця — вони тільки віддаляються, зникають за обрієм, а коли настає час, повертаються в наше життя. І нам знову доводиться обирати, до якого боку пристати, і битися, навіть коли немає ніякої надії на перемогу. Втім, надія на перемогу&nbsp; є завжди. Про це наша великодня розмова з Верховним архієпископом Української греко-католицької церкви Святославом Шевчуком. <o:p></o:p></p>\n<p><strong>&nbsp;</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2055:my-vsi-po-novomu-povynni-zrozumity-misce-ukrajiny-u-svitovomu-konteksti&catid=28:tema-nomera-cat&Itemid=2',4281,NULL,1,26),
(4299,'P','2014-04-20',1,NULL,'Rss','Світлий празник Христового Воскресіння на Аскольдовій','<p><em><img src=\"http://askoldova-mohyla.org/uk/image,5764,200,1.jpg\" style=\"margin: 10px; float: left;\" />«Де твоє, смерте, жало? Де твоя, аде, перемога? Воскрес Христос і ти провалився. Воскрес Христос і впали демони. Воскрес Христос і радіють ангели. Воскрес Христос і життя панує. Воскрес Христос і нема ні одного мерця в гробі, бо Христос воскрес із мертвих і став первістком померлих» - <strong>Йоан Золотоустий</strong></em></p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2056:svitly-praznyk-hrystovogo-voskresinnja-na-askoldovi&catid=25:parafija-cat&Itemid=27',4282,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4300,'P','2014-04-21',1,NULL,'Note','«У ці дні ми одне одному говоримо: Христос воскрес! Це не є тільки привіт, це є визнання віри»','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,5768,200,img_0796.jpg\" alt=\"\" width=\"200\" height=\"127\" />Блаженніший Любомир пригадує, що коли був малим хлопцем, Великдень був для нього дуже цікавим святом: відбувалося освячення великоднього кошика, були різні забави, гаївки, було багато руху. &laquo;А сьогодні Великдень для мене став святом, у якому знаходжу спокій &ndash; спокій у Господі&raquo;, &ndash; зазначив Архиєпископ-емерит.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5768,200,img_0796.jpg\" alt=\"\" width=\"200\" height=\"127\" />Блаженніший Любомир пригадує, що коли був малим хлопцем, Великдень був для нього дуже цікавим святом: відбувалося освячення великоднього кошика, були різні забави, гаївки, було багато руху. &laquo;А сьогодні Великдень для мене став святом, у якому знаходжу спокій &ndash; спокій у Господі&raquo;, &ndash; зазначив Архиєпископ-емерит.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/niD22ablAcE\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Департамент інформації УГКЦ</span></p>',NULL,NULL,4285,5,1,42),
(4301,'P','2014-04-21',1,NULL,'Note','Христос воскрес і мир перемагає війну','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5769,200,z_tv.jpg\" alt=\"\" width=\"200\" height=\"133\" />Такі побажання у день Великодня склав українцям Отець і Глава УГКЦ Блаженніший Святослав та наголосив: &laquo;Нехай ця хвиля перемоги пройде урочистою ходою через усі землі незалежної, єдиної, соборної України&raquo;.</span></p>\r\n<div>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&laquo;У цей день я хочу, щоб кожна віруюча християнська душа оновилася цим святом. Нехай сила воскреслого Христа дасть нам мир і радість у наших серцях, у наших родинах, у нашій державі&raquo;</span></p>\r\n</div>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5769,200,z_tv.jpg\" alt=\"\" width=\"200\" height=\"133\" />Нехай ця хвиля оновлення, яка виходить з порожнього Христового гробу, діткнеться усіх сторін українського суспільства. Христос воскрес і життя перемагає смерть. Христос воскрес і мир перемагає війну. Христос воскрес і любов перемагає ненависть.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; line-height: 1.3em;\">Такі побажання у день Великодня склав українцям Отець і Глава УГКЦ Блаженніший Святослав та наголосив: &laquo;Нехай ця хвиля перемоги пройде урочистою ходою через усі землі незалежної, єдиної, соборної України&raquo;.</span></p>\r\n<div>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&laquo;У цей день я хочу, щоб кожна віруюча християнська душа оновилася цим святом. Нехай сила воскреслого Христа дасть нам мир і радість у наших серцях, у наших родинах, у нашій державі&raquo;, &ndash; зазначив Предстоятель УГКЦ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Він також побажав, щоб у цей день у наших серцях запанував Христовий мир.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/zeUr774lCFM\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Департамент інформації УГКЦ</span></p>\r\n</div>',NULL,NULL,4286,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4302,'P','2014-04-21',1,NULL,'Rss','ІВАН ХХІІІ ТА ІВАН ПАВЛО ІІ ЦЕ – ПАПИ МИРУ','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5769,200,images.jpg\" />Блаженні Іван ХХІІІ та Іван Павло ІІ, які вже наприкінці цього місяця будуть проголошені святими, запам’ятаються в історії, серед іншого, як Папи миру. Як сини ХХ століття, вони особисто пережили жахіття воєн і тоталітарних режимів, ядерну загрозу, а тому присвятили цим темам деякі з найважливіших документів, що увійшли до спадку соціальної доктрини Церкви. До цього також слід додати їхні виступи та заклики до миру під час різних міжнародних криз, якими був позначений світ після другої світової війни і після закінчення «холодної війни».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2058:ivan-hhiii-ta-ivan-pavlo-ii-ce--papy-myru&catid=32:gk-svit-cat&Itemid=58',4287,NULL,1,26),
(4303,'P','2014-04-22',1,NULL,'Rss','Поливаний понеділок в Пирогово','<p align=\"center\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5771,200,111.jpg\" />Двадцять першого квітня, у другий день Великодніх свят, в Національному музеї під відкритим небом у Пирогово, пройшов всіма відмий поливаний понеділок. Можна з певністю сказати, що це вже традиція для греко-католиків Києва проводити тут святкування.</p>\n<p>З церкви святого Миколая, що на Аскольдовій могилі, багато парафіян відвідали сьогодні Пирогово. Хтось добирався сюди на власному авто, але наша парафія організувала автобус, на якому бажаючі могли доїхати до Пирогово. Автобус був переповнений. Наша молода спільнота «Відродження» в кількості десяти людей також долучилася до цього святкування.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2060:polyvany-ponedilok-v-pyrogovo&catid=25:parafija-cat&Itemid=27',4289,NULL,1,26),
(4304,'P','2014-04-22',1,NULL,'Rss','БФ «КАРІТАС КИЇВ» ВЕЛИКДЕНЬ СВЯТКУВАВ ІЗ ПОТРЕБУЮЧИМИ','<p><img src=\"http://askoldova-mohyla.org/uk/image,5771,200,DSC06759.JPG\" style=\"margin: 10px; float: left;\" />«Радість Христового Воскресіння цьогоріч змогли відчути і підопічні Київського Карітасу, - безхатченки й діти вулиці, самотні літні люди, діти із кризових сімей та бідні сім’ї, яким допомагає БФ «Карітас Київ». Ми розділили із ними їхні святкові Великодні столи, які накрили завдяки доброчинцям та милосердним людям», - повідомляє о. Роман Сиротич, директор БФ «Карітас Київ».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2061:2014-04-22-09-00-51&catid=29:propovidi-cat&Itemid=34',4290,NULL,1,26),
(4305,'P','2014-04-22',1,NULL,'Rss','Все його життя було присвячене таємниці хреста','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5771,200,img135419.jpg\" />«Те, що я міг працювати разом з обома майбутніми святими — це для мене велика честь», — сказав в інтерв\'ю Ватиканському радіо Артуро Марі, який протягом понад 50 років був папським фотографом.</strong></p>\n<p>Він супроводжував Йоана XXIII та Йоана Павла II не тільки під час їх публічних зустрічей, але також в дуже особисті моменти життя. Фотограф зазначив, що Добрий Папа приваблював до себе людей своєю простотою і посмішкою.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2062:vse-ogo-zhyttja-bulo-prysvjachene-tajemnyci-hresta&catid=35:istorychni-postati-cat&Itemid=48',4291,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4306,'P','2014-04-22',1,NULL,'Rss','Як жити не сперечаючись (23 квітня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5774,200,images.jpg\" />В одному гірському селищі жив чоловік, відомий тим, що ніколи не сперечався. Приїхав до нього кореспондент, щоб написати про це статтю. І між ними відбулася бесіда:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Скажіть, це правда, що ви прожили 90 з лишком років і жодного разу ні з ким не сперечалися?<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Так. Це правда.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2063:jak-zhyty-ne-sperechajuchys-23-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4292,NULL,1,26),
(4307,'P','2014-04-22',1,NULL,'Rss','Паритет між Престолом Католицтва та Престолом Православ’я','<p><strong><em><a href=\"https://mospat.ru/ru/2014/04/04/news100434/\" target=\"_blank\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5774,200,zystrich.jpg\" />В інтерв\'ю газеті National Catholic Register</a></em></strong><em> </em><em>голова Відділу зовнішніх церковних зв\'язків РПЦ митрополит Волоколамський Іларіон розповів про значення наміченого на 2016 рік Всеправославного Собору, про ситуацію на Україні, а також про православно-католицькі відносини на сучасному етапі.</em><o:p></o:p></p>\n<p><em><strong>Нижче подаємо</strong></em><strong><em> </em></strong><em><strong><a href=\"http://orientale-lumen.blogspot.co.uk/2014/04/the-pan-orthodox-council-ukraine-crisis.html\" target=\"_blank\">відповідь о. Марка Вудроф, священика Вестмінстерської дієцезії, на інтервю митр. Іларіона</a>:</strong></em><o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2064:parytet-mizh-prestolom-katolyctva-ta-prestolom-pravoslavja&catid=32:gk-svit-cat&Itemid=58',4293,NULL,1,26),
(4308,'P','2014-04-23',1,NULL,'Rss','«Чому шукаєте живого між померлими?»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5776,200,1_1_793150.jpg\" />«Чому шукаєте живого між померлими?». Це запитання, яке ангел поставив жінкам-мироносицям, що прийшли до Ісусового гробу, стало темою роздумів Папи Франциска, якими він поділився з десятками тисяч паломників, які у середу, 23 квітня 2014 р., не зважаючи на дощовий ранок, заповнили площу святого Петра у Ватикані, щоб взяти участь у загальній аудієнції з Наступником святого Петра.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2065:2014-04-23-20-08-51&catid=27:gk-v-ukrajini-cat&Itemid=57',4294,NULL,1,26),
(4309,'P','2014-04-23',1,NULL,'Rss','«Він був для мене і святим, і другом…»','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5776,200,image27486588.jpg\" />«Пригадую Йоана Павла II з великою вдячністю. Я не міг і не повинен був намагатися його наслідувати, але старався продовжувати його спадщину і його справу, робив це так як&nbsp; вмів найкраще», — констатував Папа-емерит.</strong></p>\n<p>Ці слова записані в книжці «Accanto і Giovanni Paolo II», в якій зібрано висловлювання найближчих співпрацівників Папи Римського. Видало її видавництво Ares.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2066:2014-04-23-20-35-00&catid=32:gk-svit-cat&Itemid=58',4295,NULL,1,26),
(4310,'P','2014-04-24',1,NULL,'Rss','Торжество добра (24 квітня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5776,200,pravoslavna-i-prav.jpg\" />Мешканці одного села усіляко переслідували жінку, яка стала християнкою. Коли вона йшла у церкву на неї сипалися глузування, погрози, обмови. Всі знущання вона терпіла спокійно, покладаючись на господа.<o:p></o:p></p>\n<p>Одного разу озлоблені селяни закидали її картоплею. Жінка ретельно повизбирувала картоплю, що лежала на землі і склала її в одну купу. Мешканці злякалися, що сердита жінка тепер їх закидуватиме картоплею і розійшлися.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2067:torzhestvo-dobra-24-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4296,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4311,'P','2014-04-24',1,NULL,'Rss','Страх перед радістю — хвороба християн сьогодення','<p><img src=\"http://askoldova-mohyla.org/uk/image,5779,200,1_0_793507.jpg\" style=\"margin: 10px; float: right;\" />Провідною думкою Святішого Отця сьогодні було існування християни, які бояться радості Воскресіння, життя яких подібне до похоронної відправи.<strong></strong></p>\n<p>На ці думки Папу спонукав євангельський уривок, прочитаний під час Меси, у якому розповідалося про появу Ісуса Христа апостолам після Воскресіння. Як підкреслив Папа, на вітання: «Мир вам!», – що прозвучало з уст Спасителя, апостоли, замість того, щоб сповнитись радістю, стривожилися і налякалися, думаючи, що бачать привида.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2068:strah-pered-radistju-hvoroba-hrystyjan-sogodennja&catid=32:gk-svit-cat&Itemid=58',4297,NULL,1,26),
(4312,'P','2014-04-24',1,NULL,'Rss','Добре і зле (25 квітня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5779,200,9979070.jpg\" />Один старий і мудрий чоловік сказав своєму учневі:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Роздивися кімнату, де ми сидимо, і спробуй запам’ятати речі коричневого кольору.<o:p></o:p></p>\n<p>У кімнаті було повно всього коричневого, і учень швидко впорався з завданням. Але мудрець залі запитував його:\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2069:dobre-i-zle-25-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4298,NULL,1,26),
(4313,'P','2014-04-24',1,NULL,'Rss','Московськоправославне осердя донецького сепаратизму','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5779,200,139464925.jpg\" />Сепаратисти-терористи. Чому саме Слов’янськ?</strong></p>\n<p>Чому провінційне місто на теренах Донецької області Слов’янськ стало центром діяльності терористів-сепаратистів?</p>\n<p>Думаю, є небагато наївних людей, які вважають, ніби це сталося спонтанно. У «слов’янських подіях» дуже добре простежується почерк російських спецслужб. Принаймні доказів цього достатньо. А російські спецслужби просто так не діють. Все-таки досвід у них чималенький. Та й працюють там професіонали.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2070:moskovskopravoslavne-oserdja-doneckogo-separatyzmu&catid=37:spilnota-cat&Itemid=49',4299,NULL,1,26),
(4314,'P','2014-04-24',1,NULL,'Rss','Єпископи про святість Йоана Павла ІІ','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5779,200,DSC04247-13.jpg\" />Ким був і є Йоан Павло ІІ для Церкви у Східній Європі і Середній Азії? Що з його духовного заповіту на сьогодні найважливіше? На ці і подібні питання відповідали єпископи і священики з країн СНД.</strong></p>\n<p>Духовенство одноголосно підкреслює, що Католицька Церква на Сході перестала бути «мовчазною» і воскресла «після ночі атеїзму». Католикам цієї сторони світу Святіший Отець приділяв особливу увагу, бо дуже добре знав їх трагічну долю, коли був священиком, єпископом, кардиналом.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2071:jepyskopy-pro-svjatist-oana-pavla-ii&catid=28:tema-nomera-cat&Itemid=2',4300,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4315,'P','2014-04-25',1,NULL,'Rss','Геноцид народу був геноцидом церкви','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5783,200,ArticleIm.jpg\" />24 квітня відзначають День пам’яті жертв геноциду вірмен в Османській імперії, в результаті якого загинули більше мільйона вірмен, серед яких близько чотирьох тисяч священиків і монахів, сотні тисяч були депортовані. Масові вбивства вірмен уперше в історії були визнані злочином проти людяності. Пам\'ять про Геноцид стала частиною вірменської національної ідентичності. Про пам\'ять про трагедію, душпастирство в діаспорі розповів журналістам РІСУ і Credo перший єпископ української єпархії Вірменської Апостольської Церкви (1991-2000, хоча відновлювати єпархію він почав у статусі архимандрита), колишній місцеблюститель Британської &nbsp;єпархії та діючий голова видавничого відділу Ечміадзину та президент монашої ради Архиєпископ Натан Оганесян.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2072:genocyd-narodu-buv-genocydom-cerkvy&catid=32:gk-svit-cat&Itemid=58',4301,NULL,1,26),
(4316,'P','2014-04-26',1,NULL,'Rss','Добро заразливе (26 квітня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,5784,200,1264345930dobro.jpg\" style=\"margin: 10px; float: left;\" />До віконечка придорожньої кав’ярні для автомобілістів під’їжджає на старенькій машині жінка, яка тут вже багато років поспіль постійно замовляє каву. Перекидаючись люб’язністю з продавцем вона помічає машину, що стає за нею у чергу. У доброму настрої жінка хоче зробити якесь добро і платить продавцеві за каву для наступної машини. Продавець здвигає плечима і думає, що ці люди знайомі. Жінка від’їжджає. Наступного дня вона як завжди під’їжджає до віконечка кав’ярні.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2073:dobro-zarazlyve-26-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4302,NULL,1,26),
(4317,'P','2014-04-26',1,NULL,'Rss','Свобода віросповідання - яка вона?','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5784,200,ArticleIm.jpg\" />Ситуація в сфері гарантування свободи думки, совісті та релігії істотно не змінилася, хоча намітилися негативні тенденції у бік збільшення державного регулювання та звуження релігійної свободи.</p>\n<p>Такий висновок правозахисників викладений у&nbsp;<a href=\"http://www.irs.in.ua/index.php?option=com_content&amp;view=article&amp;id=1413%3A1&amp;catid=37%3Aart&amp;Itemid=64&amp;lang=uk\" target=\"_blank\">Щорічній доповіді про права людини в Україні у 2013 році</a>, повідомляє&nbsp;<a href=\"http://www.irs.in.ua/\" target=\"_blank\">Інститут релігійної свободи</a>.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2074:svoboda-virospovidannja-jaka-vona&catid=27:gk-v-ukrajini-cat&Itemid=57',4303,NULL,1,26),
(4318,'P','2014-04-26',1,NULL,'Rss','Покинути незлагоду (27 квітня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5786,200,1-IMG_5675.jpg\" />Одного разу чернець прийшовши до церкви почув, що деякі брати сперечаються між собою.<o:p></o:p></p>\n<p>Перш ніж повернутися до своєї келії, він обійшов її тричі і тільки потому зайшов до неї.<o:p></o:p></p>\n<p>Ті хто бачили це запитали, чому він так вчинив.<o:p></o:p></p>\n<p>Чернець відповів.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2075:pokynuty-nezlagodu-27-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4307,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4319,'P','2014-04-26',1,NULL,'Rss','Томина неділя','<p><em><sup><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5786,200,21700.jpg\" />19</sup></em><em>А як звечоріло, того самого дня, першого в тижні, – а две­рі ж були замкнені там, де перебували учні: страхались бо юдеїв, – увіходить Ісус, став посередині та й каже їм: «Мир вам!»&nbsp;<sup>20</sup>Це промовивши, показав їм руки й бік. І врадувались учні, по­бачивши Господа.&nbsp;<sup>21</sup>І ще раз Ісус їм каже: «Мир вам!» Як ме­не послав Отець, так я посилаю вас».&nbsp;<sup>22</sup>Це промовивши, дихнув на них і каже їм: «Прийміть Духа Святого! 23Кому відпустите гріхи – відпустяться їм, кому ж затримаєте – затримаються».</em>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2076:tomyna-nedilja&catid=28:tema-nomera-cat&Itemid=2',4308,NULL,1,26),
(4320,'P','2014-04-26',1,NULL,'Rss','Добрий і Великий — святі разом','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5786,200,ghjk56789mll.png\" />Дещо несподівано пролунало рішення Папи Франциска про спільну канонізацію Йоана&nbsp;ХХІІІ і Йоана Павла&nbsp;ІІ. Добрий Папа і Великий Папа разом входять до почту святих.</strong></p>\n<p>«Та хіба Рим їх вмістить?»&nbsp;— бідкалися мешканці Вільного Міста, думаючи про натовпи, які прибудуть на 27&nbsp;квітня 2014&nbsp;р. на канонізаційні урочистості. Йдеться не мало не багато як про двох найбільших свідків віри соборної епохи: один дав імпульс віднові Церкви, інавгуруючи ХХІ-й в історії християнства Вселенський Собор; другий як активний його учасник втілював Собор у життя, самим собою показуючи, ким може бути пособорний католик і пособорний пастир.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2077:dobry-i-velyky--svjati-razom&catid=35:istorychni-postati-cat&Itemid=48',4309,NULL,1,26),
(4321,'P','2014-04-26',1,NULL,'Rss','Український содом','<p><img src=\"http://askoldova-mohyla.org/uk/image,5786,200,sodo.jpg\" style=\"margin: 10px; float: right;\" />Коли закінчилася активна фаза Майдану, багато хто в ейфорії переконував, що українське суспільство сутнісно змінилося. Проте події останніх місцеві показують, що це не зовсім так. Українське суспільство не змінилося: воно дуже кволо і дуже повільно розпочало зміни. Але до реальних змін так&nbsp; і не прийшло – держава і надалі залишається нездатною захистити своїх громадян, олігархія продовжує верховодити в українській політиці, довкола розкрадання, корупція й хабарництво.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2078:ukrajinsky-sodom&catid=37:spilnota-cat&Itemid=49',4310,NULL,1,26),
(4322,'P','2014-04-27',1,NULL,'Rss','І це мине також (28 квітня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5792,200,9.jpg\" />Один цар уславився своєю мудрістю. Але попри цю мудрість у житті не мав він спокою. І одного разу звернувся цар до мудреця з проханням:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Допоможи мені, дуже багато в цьому житті доводить мене до нестями. Маю велику схильність до пристрастей і це вельми мені заважає.<o:p></o:p></p>\n<p>Мудрець дав йому перстень на якому була викарбувана фраза – «це мине»! і сказав:\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2079:i-ce-myne-takozh-28-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4311,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4323,'P','2014-04-27',1,NULL,'Rss','Нехай ці святі навчають нас не згіршуватись Христовими ранами а входити у таємницю Божого милосердя','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5792,200,139594.jpg\" />У неділю, 27 квітня 2014 року, під час Святої Меси на площі перед ватиканською базилікою Святого Петра, Вселенський Архиєрей Франциск канонізував, тобто зарахував до лику святих Католицької Церкви, двох своїх попередників на Петровому престолі, Папу Івана ХХІІІ і Папу Івана Павла ІІ.</p>\n<p>На початку своєї проповіді, виголошеної після того, як євангельський уривок був прочитаний латинською та грецькою мовами, Святіший Отець зазначив, що осередком цієї неділі, яку Іван Павло ІІ забажав присвятити Божому Милосердю, є прославлені рани воскреслого Ісуса Христа. Він показав їх Своїм апостолам уже під час Своєї першої появи, тобто ввечері у сам день воскресіння.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2080:neha-ci-svjati-navchajut-nas-ne-zgirshuvatys-hrystovymy-ranamy-a-vhodyty-u-tajemnycju-bozhogo-myloserdja&catid=28:tema-nomera-cat&Itemid=2',4312,NULL,1,26),
(4324,'P','2014-04-29',1,NULL,'Rss','Глава УГКЦ вважає канонізацію Папи Івана XXIII та Папи Івана Павла II святом для України і УГКЦ','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5794,200,ArticleI.jpg\" />27 квітня Блаженніший Святослав, Отець і Глава УГКЦ, узяв участь у канонізації двох визначних Римських Єпископів минулого століття: Папи Івана XXIII та Папи Івана Павла II. Із Святішим Отцем Франциском співслужили предстоятелі різних Східних Католицьких Церков, 150 кардиналів і понад 700 єпископів.</p>\n<p>Коментуючи цю історичну подію для <a href=\"http://news.ugcc.org.ua/go/aHR0cDovL3poeXZlLnR2Lw==\" target=\"_blank\">«Живого.ТБ»</a>, Верховний Архиєпископ УГКЦ зауважив, що канонізація обох Пап є справжнім святом для України і УГКЦ. Бо «отримавши таких заступників і світ, і Україна, випросить у Господа ласку миру і свободи».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2082:glava-ugkc-vvazhaje-kanonizaciju-papy-ivana-xxiii-ta-papy-ivana-pavla-ii-svjatom-dlja-ukrajiny-i-ugkc&catid=32:gk-svit-cat&Itemid=58',4313,NULL,1,26),
(4325,'P','2014-04-29',1,NULL,'Rss','Найкраща від усіх (29 квітня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5794,200,xristianskay.jpg\" />Жив собі брат при церкві і мав він недугу на ім’я «найкращий від усіх». Хай про що тільки мова не зайде, він повсюди старався бути найкращим від усіх: прибирати в церкві – він вже мітлою мете; всеношну молитву – він серед перших запопадливо молиться, рулади словесні виписує.<o:p></o:p></p>\n<p>І от, в ніч на П’ятидесятницю приснився йому сон – потрапляє цей брат на небо, стоїть у черзі праведників чекає і думає як Ісус його серед усіх виокремить, під білі ручки візьме, похвалою засипле.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2081:nakrashcha-vid-usih-29-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4314,NULL,1,26),
(4326,'P','2014-04-29',1,NULL,'Rss','Довірившись Богу…','<p><strong><em><img src=\"http://askoldova-mohyla.org/uk/image,5794,200,1390381439.jpg\" style=\"margin: 10px; float: left;\" />«Дуже багато людей не усвідомлює, що головним чином саме вони є відповідальними за надання глибокого сенсу власному життю.» </em></strong> — Так сказала людина, ім’я якої нещодавно зазвучало по-новому. І, можливо, її слова не мали б такої сили, якби ця особистість своїм життям не стала втіленням, усвідомленням змісту вищеназваної фрази. Юнаком, прокидаючись кожного ранку, він бачив із вікна своєї кімнати старий годинник, на якому нанесені були слова: «Час втікає, вічність чекає.»</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2083:doviryvshys-bogu&catid=35:istorychni-postati-cat&Itemid=48',4315,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4327,'P','2014-04-29',1,NULL,'Rss','Чи проводити референдум щодо другої державної мови?','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5799,200,52d6531c5eaff.jpeg\" /></strong>У принципі, я людина демократична і лише вітаю проведення референдумів, які допомагають уточнити волевиявлення народу. Проте у випадку цього жваво обговорюваного питання моя позиція різко негативна. Поясню чому.</p>\n<p><strong>Чужий сценарій</strong></p>\n<p>Тема «дискримінації» носіїв російської мови й&nbsp;<a href=\"http://lb.ua/news/2014/03/31/261429_donetskiy_oblsovet_trebuet.html\"><strong>потреби проголошення її другою державною</strong></a> виринає із нафталінової «скрині» політтехнологів щоразу перед виборами або в кризові моменти розвитку України. Уже з цього видно, що вона насправді надумана, бо як тільки минає привід, розвіюється і сама «проблема».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2084:chy-provodyty-referendum-shchodo-drugoji-derzhavnoji-movy&catid=37:spilnota-cat&Itemid=49',4317,NULL,1,26),
(4328,'P','2014-04-29',1,NULL,'Rss','У пошуках єдності','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5800,200,ljvivsjkyj_majdan_19_02.jpg\" />Після порівняно недавніх&nbsp;подій, коли на Майдані пліч-о-пліч священики різних українських конфесій своїми молитвами і своєю присутністю боронили беззбройний народ, коли під свистом куль і вибухами гранат лунала спільна молитва до Бога про захист народу, коли спільно відспівувались жертви перед конфесійно розділеним українством знову постало питання християнської єдності. Адже єдність християн на Майдані показала власне фундаментальну, екзистенційну єдність народу Божого, поділеного на різні конфесії, та показала що ця подія не є результатом Божого задуму. Ще більше потреба єдності народу Божого в Україні постала перед лицем агресії на нашу державу.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2086:u-poshukah-jednosti&catid=27:gk-v-ukrajini-cat&Itemid=57',4318,NULL,1,26),
(4329,'P','2014-04-29',1,NULL,'Rss','Погляд на життя  (30 квітня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5800,200,image078.jpg\" />Вирішив один чоловік побудувати дім. Спершу він викопав глибоку яму під фундамент. У нечасті хвилини відпочинку видно було йому тільки хмари в небі і ноги людей, які ішли зовсім поряд.</p>\n<p>Коли фундамент був готовий, чоловік почав зводити стіни першого поверху. Тепер він стояв на землі, а під час відпочинку, міг дивитися в будь-який бік. Та й видно було йому&nbsp; відти набагато більше від того, що міг він роздивитися досі.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2085:pogljad-na-zhyttja-30-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4319,NULL,1,26),
(4330,'P','2014-04-30',1,NULL,'Rss','Щоб не було пізно','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5800,200,620x400.jpg\" />Без сумніву, в історію католицького світу 27 квітня 2014 року увійде як золотий день – Служба Божа чотирьох Пап, двоє з яких проголошені святими, явище унікальне. І кожен з них, в тій чи в іншій мірі, мав відношення до української історії.</p>\n<p>Святий Іван ХХІІІ спричинився до визволення Патріярха Йосифа Сліпого з московської неволі. І це уможливило перетворення УГКЦ на Заході з розрізнених єпархій в єдину структуру, з усіма здобутками для української діаспори і для українців Краю.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2087:shchob-ne-bulo-pizno&catid=37:spilnota-cat&Itemid=49',4320,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4331,'P','2014-04-30',1,NULL,'Rss','Земна і небесна краса (1 травня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5803,200,priroda.jpg\" />Одному чоловікові наснився сон, що він потрапив на небо. І чудувався він з небесної краси. Які пречудові дерева, квіти, трава!</p>\n<p>Ангел запитав його, чи ж не бачив він чогось подібного на землі? Чоловік впевнено відповів, що на землі взагалі нічого подібного немає.</p>\n<p>Ангел же сказав:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2089:zemna-i-nebesna-krasa-1-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4321,NULL,1,26),
(4332,'P','2014-04-30',1,NULL,'Rss','«Йдучи на Майдан, ми казали “Не біймося”, сьогодні кажемо: “Не зупиняймося”», — звернення ініціативи «Першого грудня»','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5803,200,IMG_7413-1024x682.jpg\" />Загальнонаціональне порозуміння і нові політичні стандарти — відомі суспільні авторитети закликали українську владу та народ не зупинятися перед загрозами, що нависли над нашою державою.</strong></p>\n<p><strong>Позицію Ініціативної групи&nbsp;«Першого грудня» &nbsp;озвучили академік, директор Інституту філософії НАН України Мирослав Попович, кардинал Любомир Гузар та колишній політв’язень, письменник, філософ Євген Сверстюк.</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2088:2014-04-30-19-29-17&catid=37:spilnota-cat&Itemid=49',4322,NULL,1,26),
(4333,'P','2014-04-30',1,NULL,'Rss','Надія за часів абсурду','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5803,200,993718201.jpg\" />Історії про великих людей Старого і Нового Завіту говорять нам, як надія розквітає у ситуаціях абсурдних і незрозумілих. Хорошим прикладом тут є Авраам, який отримав абсурдну обітницю.</strong><o:p></o:p></p>\n<p><em><strong>Спільнота надії<o:p></o:p></strong></em></p>\n<p>Одне з найважчих завдань, які нині стоять перед нами, це переконати людей, щоб і надалі взаємно надіялися на доброту одні одних, оскільки лише таким чином вони зможуть розпочинати все заново. Надії ми також потребуємо як народ, як людська родина і як ціле сотворене. Без надії неможливо створити спільноту. В&nbsp;певному істотному сенсі спільноту можуть творити тільки люди, здатні до надії, які вірять, що посеред біди і поділів добро є можливим.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2094:nadija-za-chasiv-absurdu&catid=28:tema-nomera-cat&Itemid=2',4323,NULL,1,26),
(4334,'P','2014-04-30',1,NULL,'Rss','Діяльність парафіяльної спільноти «Епіфанія»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3048,200,imag.JPG\" />Парафіяльна служба милосердя «Карітас-Печерськ» провадить своє служіння у розмаїтих напрямках. Милосердне милосердя активно вливається у працю спільноти «Епіфанія» Кожен другий четвер місяця спільнота «Епіфанія» при храмі святого Миколая на Аскольдовій Могилі на чолі з отцем Анатолієм Теслею відвідує похилих та хворих парафіян. Священик відправляє молебні на оздоровлення, уділяє їм св. Тайну Сповіді та Євхаристії. Опісля відбувається дружнє спілкування про життя парафії, про особисті турботи стареньких. Їм цікаво згадати молодість, своїх колишніх друзів, місця чи країни, де вони бували протягом життя.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2095:2014-04-30-20-58-55&catid=25:parafija-cat&Itemid=27',4324,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4335,'P','2014-05-01',1,NULL,'Rss','«Я був би вдячний, якби хтось офіційно пояснив, що в Україні відбувається…»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5807,200,huzar_velykden14.jpg\" />«Ми не знаємо, чому Бог допускає те, що коїться в Україні. Проте все, що відбувається, діється з Його волі. Гадаю, це випробування...» – таку думку висловив Блаженніший Любомир, Архиєпископ-емерит УГКЦ, в ефірі «Громадського.ТБ» 30 квітня 2014 року.</p>\n<p>Як сказав Блаженніший Любомир, українці близько двохсот років не мали своєї держави, а тому не знають як поводитися належним чином із своєю незалежністю: «Ми говоримо про речі, що, можливо, іншим людям з інших держав є дивними. Особливо, коли кажуть про ділення нашої країни…Тому, напевно, нам потрібно такого прикрого і болючого поштовху, щоб ми усвідомили те, що ми – один народ».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2096:2014-04-30-21-30-29&catid=37:spilnota-cat&Itemid=49',4325,NULL,1,26),
(4336,'P','2014-05-01',1,NULL,'Rss','Людська мудрість (2 травня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5803,200,293026.jpg\" />Жив собі селянин, який вважав, що було би набагато ліпше, якби він міг впливати на погоду.</p>\n<p>–&nbsp;Зерно буде швидше достигати,&nbsp;–&nbsp;думав він,&nbsp;–&nbsp;і в колосках буде більше зерна.</p>\n<p>Бог почув його думки і сказав:</p>\n<p>–&nbsp;Коли гадаєш, що знаєш ліпше, яка і коли потрібна погода, то керуй нею цього літа.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2090:ljudska-mudrist-2-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4326,NULL,1,26),
(4337,'P','2014-05-02',1,NULL,'Rss','7 фактів про Папу Івана ХХІІІ','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5809,200,46451.jpg\" />Анджело Джузеппе Ронкаллі (мирське ім\'я Папи Івана XXIII) народився на півночі Італії в містечку Сотто-іль-Монте в провінції Бергамо 1881 року. Виходець із селянської родини, він зберіг до кінця життя зв\'язок з ріднею, з братами, які так і залишилися селянами. У віці 23 років був висвячений у сан священика. У 1925 році Ронкаллі, отримавши сан єпископа, починає дипломатичну кар\'єру, виконуючи обов\'язки Папського Нунція (посланця) у Болгарії, Греції, Туреччині та Франції. У 1953 році став кардиналом і Патріархом Венеції. А 28 жовтня 1958 року конклав обрав Ронкаллі 261-им Наступником св. Петра.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2097:7-faktiv-pro-papu-ivana-hhiii&catid=35:istorychni-postati-cat&Itemid=48',4327,NULL,1,26),
(4338,'P','2014-05-02',1,NULL,'Rss','25 фактів про Папу Івана Павла ІІ','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5809,200,383981284.jpg\" />1. Став першим Римським Папою неіталійського походження, обраним за останні 455 років, одним з наймолодших Понтифіків в історії і першим Папою слов\'янського походження.<o:p></o:p></p>\n<p>2. В юності захоплювався театром і мріяв стати професійним актором. У 14 років спробував себе в шкільному драматичному гуртку і написав п\'єсу «Король-Дух». У Ягеллонському Університеті в Кракові став членом театрального гуртка «Студії 38». А в 1942 році, будучи семінаристом, став одним з ініціаторів «Театру Рапсодії», що діяв підпільно.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2098:25-faktiv-pro-papu-ivana-pavla-ii&catid=35:istorychni-postati-cat&Itemid=48',4328,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4339,'P','2014-05-02',1,NULL,'Rss','«Древній Галич» - стане новим реколекційним центром','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5809,200,unnam.jpg\" />На місці родинного маєтку дідуся Блаженнішого Любомира постане Реколекційний центр. Про це знімальній групі документального проекту «Любомир» повідомив отець Ігор Броновський, декан Галицького деканату Івано-Франківської митрополії УГКЦ.<o:p></o:p></p>\n<p>«Тут дідусь Блаженнішого Лев Гузар був нотаріусом, його маєтності були поруч. Міська управа нам передала цей маєток на Церкву, і ми маємо намір збудувати там Реколекційний центр», - зазначив отець-декан.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2099:2014-05-02-05-55-05&catid=27:gk-v-ukrajini-cat&Itemid=57',4329,NULL,1,26),
(4340,'P','2014-05-03',1,NULL,'Rss','Патріарху Кірілу ще не пізно зупинити «овечок» з його пастви на території нашої держави УКРАЇНИ','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5813,200,DSC_5944-800x438.jpg\" />У своєму&nbsp;<a href=\"http://www.patriarchia.ru/db/text/3640000.html\" target=\"_blank\">виступі</a> перед початком Вищої Церковної Ради РПЦ Патріарх Московський і всієї Руси висловив свою тверду переконаність, що єдиною Церквою, яка, мовляв, стояла і стоїть тепер в Україні осторонь політики, є «його Церква», тобто в цьому випадку – Українська Православна Церква (Московського Патріархату). Це – повторення традиційної ілюзії московського православ’я, що, не подаючи свого остережного голосу на захист людей, яких пригнічує влада, вона тим самим стоїть «поза політикою».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2100:2014-05-03-06-01-08&catid=37:spilnota-cat&Itemid=49',4331,NULL,1,26),
(4341,'P','2014-05-03',1,NULL,'Rss','Смисл життя (4 травня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5803,200,mid_63154_7535.jpg\" />Якось увечері у двері до мудреця постукав запізнілий подорожанин. Мудрець запросив його у свою оселю, почастував скромною вечерею, і вони розговорилися.</p>\n<p>–&nbsp;Послухай!&nbsp;–&nbsp;сказав гість.&nbsp;–&nbsp;Слава про твою мудрість дійшла до нашого краю. Ти багато знаєш. Чи можеш ти пояснити мені, для чого людина живе на цьому світі, у чому смисл життя?</p>\n<p>–&nbsp;А ти сам як гадаєш?&nbsp;–&nbsp;спитав мудрець.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2093:smysl-zhyttja-4-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4332,NULL,1,26),
(4342,'P','2014-05-03',1,NULL,'Rss','Звернення Екзарха Одеського УГКЦ з приводу ситуації в Одесі','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5814,200,vl_bubnij_xirotoniya.jpg\" />З великою тривогою і болем серця переживаємо ці конфліктні протистояння в Одесі, у яких, на жаль, постраждало і загинуло багато людей. Прикро було спостерігати за тим, що силові структури Одеси, нехтуючи присягою і своїм священним обов’язком забезпечити правопорядок у місті та області, пасивно спостерігали за діями злочинних угрупувань, які з кийками, коктейлями молотова, а навіть зі зброєю в руках, чинили погроми та нападали на мирних учасників маршу за єдність України.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2101:zvernennja-ekzarha-odeskogo-ugkc-z-pryvodu-sytuaciji-v-odesi&catid=27:gk-v-ukrajini-cat&Itemid=57',4333,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4343,'P','2014-05-03',1,NULL,'Rss','«Путін дивиться на світ як імператор і бажає замінити людям Бога»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5816,200,145322.jpg\" />Михайло&nbsp;Димид&nbsp;розповів&nbsp;\"Сегодня\",&nbsp;чому&nbsp;поїхав на&nbsp;Майдан,&nbsp;чому,&nbsp;на його думку,&nbsp;жителі сходу&nbsp;не люблять&nbsp;Україну&nbsp;і чому&nbsp;кримчани&nbsp;віддали&nbsp;Крим</p>\n<p>Ім\'я львівського священика Михайла Димида стало відомим після гучних судових розглядів з ДАІ: у січні цього року автоінспекція намагалася «сфабрикувати» проти нього «справу» за участь в Автомайдані, але отець Михайло виграв суд, а тепер шукає імена замовників.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2102:2014-05-03-20-56-44&catid=28:tema-nomera-cat&Itemid=2',4334,NULL,1,26),
(4344,'P','2014-05-03',1,NULL,'Rss','Неділя Святих жінок-мироносиць','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5816,200,muronosuci1.jpg\" />У третю Неділю після Великодня Свята Церква згадує пам\'ять святих жінок-мироносиць, і праведних Йосифа Аримафейського і Никодима – таємних учеників Христових. Своїм богослужінням Церква знову ставить нас на голгофі у Хреста Христовового, з якого знімають Пречисте Його Тіло Йосиф та Никодим, і у вертограді у труни, де вони поклали Тіло Ісуса Христа, і де потім мироносиці, що прийшли помазати Його Тіло благовонними маслами, першими удостоюються бачити Воскреслого Господа.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2103:nedilja-svjatyh-zhinok-myronosyc&catid=35:istorychni-postati-cat&Itemid=48',4335,NULL,1,26),
(4345,'P','2014-05-05',1,NULL,'Rss','«Царице Неба молись за Україну»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5818,200,4644.jpg\" />«Дорогі брати й сестри, бажаю заохотити вас ввірити Пресвятій Богородиці ситуацію в Україні, де не припиняються протистояння, ситуація залишається важкою. Молюся разом з вами за загиблих цими днями, просячи в Господа Бога, щоб влив у серця всіх людей почуття примирення та братерства», – з цими словами Папа Франциск у неділю, 4 травня 2014 р., звернувся до десятків тисяч паломників, які зібралися у Ватикані, щоб помолитися разом з ним Богородичну молитву «Царице Неба».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2105:2014-05-05-06-17-57&catid=32:gk-svit-cat&Itemid=58',4336,NULL,1,26),
(4346,'P','2014-05-05',1,NULL,'Rss','Не все таке, яким видається (6 травня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5818,200,1374130.jpg\" />Два ангели подорожні зупинилися у багатій оселі. Сім’я була негостинна і не захотіла постелити ангелам у вітальні. Замість того їх поклали спати в холодний підвал. Коли вони стелили постіль, старший ангел побачив дірку в стіні і заклав її. Побачивши це , молодший запитав, навіщо. Старший відповів:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Не все так, як видається.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2104:ne-vse-take-jakym-vydajetsja-6-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4337,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4347,'P','2014-05-05',1,NULL,'Rss','Віра на межі розчарування','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5818,200,utego.JPG\" />Мабуть, кожен з нас мав би зізнатися у тому, що Річард Ньютон назвав «незрозумілою нехіттю до молитви». Вона проявляється не тільки у бракові молитви, але також в обманах. Безліч людей ніби говорять у молитвах те, про що думають, але при цьому вони обманюють самих себе. «Дякую Тобі, що я не такий, як інші»,&nbsp;— молився книжник (Лк 18,&nbsp;11). Його занурене у тьму серце говорило те, у що він вірив, але це була неправда.</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2106:vira-na-mezhi-rozcharuvannja&catid=28:tema-nomera-cat&Itemid=2',4338,NULL,1,26),
(4348,'P','2014-05-05',1,NULL,'Rss','Прыщ, который выскакивает в непристойном месте','<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5818,200,_w8e1792_01.jpg\" />Доктор философии, президент украинского ПЕН-клуба, диссидент Евгений Сверстюк – один из самых авторитетных, хоть и не самых ярких, возможно, представителей своего поколения. Что такое тоталитаризм, он знает очень хорошо, поскольку много раз сталкивался с советской карательной системой. Именно о наследии советской эпохи в современной России мы и решили поговорить с Евгением Александровичем.</em></p>\n<p><strong>&nbsp;</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2107:prshch-kotor-vskakyvaet-v-neprystonom-meste&catid=37:spilnota-cat&Itemid=49',4339,NULL,1,26),
(4349,'P','2014-05-05',1,NULL,'Rss','Як знайти щастя (6 травня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5822,200,4.jpg\" />Один чоловік мріяв про ліпше життя, йому не подобався той дім де він жив, одежа, яку носив, одне слово – йому не подобалось все, що його оточувало. Він замислювався над питанням, чому хтось має все, про що можна тільки мріяти, а в нього немає нічого.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; От, якби в мене був гарний дім, вродлива жінка, багато грошей, то я був би щасливий, - думав чоловік цілими днями.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2108:jak-znaty-shchastja-6-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4340,NULL,1,26),
(4350,'P','2014-05-06',1,NULL,'Rss','«Майдан – це колективна відмова від страху»','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5823,200,1-782518.jpg\" />У статті</strong><strong> </strong><strong><a href=\"http://www.cnewa.org/default.aspx?ID=3725&amp;pagetypeID=4&amp;sitecode=HQ&amp;pageno=1\">“Молитва і протест”</a>, для канадського видання</strong><strong> </strong><strong><a href=\"http://www.cnewa.org/home.aspx?ID=26&amp;pagetypeID=12&amp;sitecode=HQ\">CNEWA</a>, президент УКУ розповідає західним читачам про роль Церкви в подіях на Майдані, та про те, як Майдан змінив Україну.</strong></p>\n<p>«Українська нація відчутно подорослішала за останні півроку, – пише у статті владика Борис Ґудзяк. – Майдан, який мобілізував мільйони людей,&nbsp; спричинений&nbsp; фундаментальним бажанням людей жити гідно, захищати власні цінності, власне життя».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2109:2014-05-06-18-22-47&catid=37:spilnota-cat&Itemid=49',4341,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4351,'P','2014-05-06',1,NULL,'Rss','«Двадцять п\'ятого травня ми обиратимемо майбутнє наших дітей»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5823,200,154581.jpg\" />Синод єпископів УГКЦ 6 тавня оприлюднив послання, в якому закликав українців<strong> </strong><strong>«</strong>активно сприяти проведенню цих доленосних виборів і сумлінно взяти в них участь».<o:p></o:p></p>\n<p>Владики наголошують, що новітня історія незалежної Української Держави ще не знала такої напруженої передвиборчої кампанії, яка б відбувалася в настільки драматичних умовах. Тому, наголошують єрархи УГКЦ, нам сьогодні як ніколи потрібне єднання всіх здорових суспільних сил та інституцій.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2111:2014-05-06-18-48-07&catid=28:tema-nomera-cat&Itemid=2',4342,NULL,1,26),
(4352,'P','2014-05-06',1,NULL,'Rss','Зло мусить бути покараним','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5823,200,7100.jpg\" />Цими днями о. Олесь Августин Чумаков&nbsp;<a href=\"https://www.facebook.com/Rev.Olko?fref=ts\" target=\"_blank\">влучно і точно охарактеризува</a>в дуже багато українських проблем цитатою раббі Елеазара Ґадола: «Кожен хто себе робить милосердним до дійсно жорстоких, в кінці себе робить жорстоким до милосердних». Тобто, зло, яке залишається не покараним, рано чи пізно обертається проти добра. Така його природа. Непокарані фальсифікації 2004 вродили щедрий урожай в часи Януковича. І це тільки один приклад, коли відсутність належного покарання зла веде до його збільшення. У цьому світлі і сьогоднішня ситуація на Донбасі – це тільки наслідок непокараного у 2004 – 2005 Сєвєродонецького збіговиська.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2110:zlo-musyt-buty-pokaranym&catid=37:spilnota-cat&Itemid=49',4343,NULL,1,26),
(4353,'P','2014-05-06',1,NULL,'Rss','Довіра (7 травня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,5825,200,article171.jpg\" style=\"margin: 10px; float: left;\" />Один альпініст зважився підкорити вершину, яка вважалася однією із найважчих. Бажаючи присвоїти всю славу собі, він вирішив зробити це сам.<o:p></o:p></p>\n<p>Але вершина не піддавалася просто так. Стемніло. Зорі і місяць у ту ніч сховалися за хмари. Було темно, хоч око стрель. Але альпініст не спинився.</p>\n<p>Й от на одному небезпечному виступі він посковзнувся і зірвався вниз. І напевне би загинув, якби не застрахувався, як досвідчений верхолаз.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2112:dovira-7-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4344,NULL,1,26),
(4354,'P','2014-05-07',1,NULL,'Rss','Від Пасхи до П’ятидесятниці','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5827,200,zeslani.jpg\" />Пасхальну неділю відкриває період п’ятдесяти днів, який спочатку називався П’ятидесятницею, а пізніше, коли слово «П’ятидесятниця» було зарезервовано для самого п’ятдесятого дня після Пасхи, - «Пасхальним періодом».</strong><o:p></o:p></p>\n<p>З давніх часів цей період розглядався як один суцільний святковий день: ще Тертуліан називав його «найрадіснішим часом» - laetissimus spatium. Увесь цей час ніхто не постив і не ставав на коліна</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2113:vid-pashy-do-pjatydesjatnyci&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4345,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4355,'P','2014-05-07',1,NULL,'Rss','Справжня любов (8 травня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5827,200,4654.jpg\" />Побачив Чорторий на березі чудову Лілію і вирішив, хай там що, її домогтися. Чого ж він тільки не пропонував красуні: поплавати на його швидких хвилях, ласкаву прохолоду води в нестепну спеку і цілу водоверть при всяких розваг та втіх. Завагалася красуня. Помітив це Жук, безнадійно закоханий у неї і заповзявся відмовляти:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Згубить він тебе, пропадеш.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2114:spravzhnja-ljubov-8-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4346,NULL,1,26),
(4356,'P','2014-05-09',1,NULL,'Rss','8 порад тим, хто бажає стати україномовним','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5829,200,20140508110441.jpg\" />З часів Євромайдану українська мова в спілкуванні стала ознакою патріотизму. Після анексії Криму та військової агресії Росії на Сході російськомовні українці почали відмовлятись вживати російську як мову окупанта.</strong></p>\n<p>«Мова зникає не тому, що її не вчать інші, а тому, що нею не говорять ті, хто її знає» — ці слова іспанського політика Хосе-Марії Арце розлетілись в соціальних мережах як заклик спілкуватись рідною мовою. Говорити українською стало не просто модою, а потребою для багатьох свідомих громадян, проте в багатьох на заваді стали психологічні бар’єри та страхи.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2115:8-porad-tym-hto-bazhaje-staty-ukrajinomovnym&catid=37:spilnota-cat&Itemid=49',4347,NULL,1,26),
(4357,'P','2014-05-09',1,NULL,'Rss','Дорога до спасення (9 травня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5829,200,molitva.jpg\" />Якось на одного пустельника напосіли важкі помисли. Перебуваючи у такому стані він почав виливати свою печаль перед Богом.<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Господи, - говорив він, хочу спастися, але помисли мої ніяк не пускають мене це звершити. Що мені робити? Як мені спастися?<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2116:doroga-do-spasennja-9-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4348,NULL,1,26),
(4358,'P','2014-05-09',1,NULL,'Rss','«Коли іноземна влада починає “малювати” кордони, від цього страждають звичайні люди»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5831,200,z_premerom1.jpg\" />Цими днями Верховний Архиєпископ Києво-Галицький перебуває в Канаді з десятиденним візитом, під час якого відвідує найбільші міста Торонтонської єпархії УГКЦ.</p>\n<p>У розмові з Прем’єр-міністром Верховний Архиєпископ наголосив: «В Україні не було напружених стосунків між українцями та росіянами до того, як уряд Росії анексував Крим». «Саме ці незаконні дії російського уряду, а також багаторічна прокремлівська політика в Києві, спричинили розбрат у країні», – сказав Глава п\'ятимільйонної Церкви.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2117:2014-05-09-13-02-50&catid=28:tema-nomera-cat&Itemid=2',4349,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4359,'P','2014-05-09',1,NULL,'Rss','Три сестри (10 травня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5832,200,a3a34dc3.jpg\" />Жили собі три сестри; перша була ледащиця, друга злюща презлюща, а третя і розумниця, і красуня, і рукодільниця -&nbsp; сама втіха і радість. Одного ранку спинилася підвода під їхніми воротами, сестри виглянули подивитися хто приїхав. На підводі сиділа незнайома стара.<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Хто ти? – спитали вони.<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Я, доля. Вже пора виходити вам заміж.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2118:try-sestry-10-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4350,NULL,1,26),
(4360,'P','2014-05-09',1,NULL,'Rss','ПУТІН – ОСТАННІЙ ІМПЕРАТОР РОСІЇ, ПІСЛЯ ЯКОГО ВОНА РОЗЛЕТИТЬСЯ','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5832,200,ArticleIm.jpg\" />Володимир Путін – це останній імператор Росії, після нього вона розлетиться на кавалки і Москва перестане існувати.</p>\n<p>Таку думку 8 травня на прес-конференції висловив о. Рафаїл Турконяк, архимандрит УГКЦ, доктор богослов’я і літургіки, лауреат Шевченківської премії за переклад Острозької Біблії українською мовою, видатний дослідник і перекладач Святого Письма.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2119:putin--ostanni-imperator-rosiji-pislja-jakogo-vona-rozletytsja&catid=37:spilnota-cat&Itemid=49',4351,NULL,1,26),
(4361,'P','2014-05-10',1,NULL,'Rss','Божа справедливість (11 травня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5834,200,images.jpg\" />Старця Паїсія часто питали, що таке справедливість, які вчинки справедливі?<o:p></o:p></p>\n<p>Старець сказав:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Є справедливість людська, а є Божа.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->А що каже Божа справедливість?</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2120:bozha-spravedlyvist-11-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4352,NULL,1,26),
(4362,'P','2014-05-10',1,NULL,'Rss','Неділя про розслабленого','<p><em><img src=\"http://askoldova-mohyla.org/uk/image,5834,200,01125.jpg\" style=\"margin: 10px; float: right;\" />«Був там чоловік розслаблений 38 років»</em></p>\n<p>Нинішнє Євангеліє розповідає нам про чудесне оздоровлення Спасителем чоловіка, хворого, розслабленого - спаралізованого 38 років, який майже половину свого життя був в неволі недуги. Євангелист Іван, описуючи становище хворого, не подає ніяких скарг того чоловіка на свою недугу. Розслаблений терпеливо зносить все, живе надією на своє оздоровлення бодай при кінці життя.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2121:nedilja-pro-rozslablenogo&catid=28:tema-nomera-cat&Itemid=2',4353,NULL,1,26),
(4363,'P','2014-05-11',1,NULL,'Rss','День Матері','<p><strong><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5836,200,den-matery.jpg\" />«Господь не може встигнути усюди одночасно, і тому він створив матерів» - народне прислів’я</em></strong></p>\n<p>В Україні у другу неділю травня, традиційно, відзначається одне з головних родинних свят —&nbsp;<strong>День матері</strong>. Його святкують в Європі, в США, в країнах Азії. І така велика популярності цього свята цілком зрозуміла, оскільки споконвіку мама була найдорожчою та найріднішою людиною для кожного. Мами присвячують життя своїм дітям, жертвуючи всім заради їх щастя.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2122:2014-05-11-06-32-24&catid=28:tema-nomera-cat&Itemid=2',4354,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4364,'P','2014-05-11',1,NULL,'Rss','«Правила для молоді, яка бажає розвитку в побожності й науці»','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5837,200,1.jpg\" />Зі щоденника св.&nbsp;Йоана ХХІІІ. Найімовірніше,&nbsp; ці правила становлять плід особистого пророблення кліриком Анджело Ронкаллі «Малого розкладу», який він отримав від свого духовного керівника.</strong></p>\n<p><em>«Добре ведеться чоловікові, якщо він носить ярмо від молодості своєї» (ПлЄр 3,&nbsp;27).</em></p>\n<p>Перший і головний принцип: вибрати собі духовного керівника, який відзначається&nbsp; добрим життям, розсудливістю і знаннями, до якого маєш повну довіру, і повністю від нього залежати, слухати його порад і підкоритися з довірою його керівництву.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2123:2014-05-11-19-55-10&catid=27:gk-v-ukrajini-cat&Itemid=57',4355,NULL,1,26),
(4365,'P','2014-05-12',1,NULL,'Rss','Обітниця мовчання (12 травня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,5838,200,80-1.jpg\" style=\"margin: 10px; float: left;\" />Одного разу четверо ченців зачинилися в келії гірського храму склавши обітницю мовчати сім днів. До них мав доступ тільки хлопець служка, що приносив усе потрібне. Коли настала ніч у келії почав згасати світильник. Здавалося, що він от-от згасне. Один із ченців, той що сидів до вогню найближче, не на жарт стривожився і вигукнув:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Хлопчику, поправ швидше гніт.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2124:obitnycja-movchannja-12-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4356,NULL,1,26),
(4366,'P','2014-05-12',1,NULL,'Rss','КАРІТАС УКРАЇНИ РОЗПОЧАВ ПРОЕКТ З ДОПОМОГИ ПЕРЕСЕЛЕНЦЯМ З КРИМУ','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5840,200,ArticleImag.png\" />Карітас України розпочав новий проект під назвою \"Пункт допомоги у надзвичайних ситуаціях\", який спрямований на допомогу та опіку переселенцям. Головна роль проекту полягає у наданні можливості проживання, підтриманні зв\'язку та оцінці потреб переселенців з Криму на території Західної України; також важливо визначити прогалини у забезпеченні їхніх потреб; створити базу даних переселенців; запропонувати програми, що будуть відповідати потребам, визначеним проектом \"Пункт допомоги у надзвичайних ситуаціях\" та зробити їх пріоритетними для фінансування/впровадження.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2125:karitas-ukrajiny-rozpochav-proekt-z-dopomogy-pereselencjam-z-krymu&catid=37:spilnota-cat&Itemid=49',4358,NULL,1,26),
(4367,'P','2014-05-12',1,NULL,'Rss','У пошуках щастя (13 травня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5841,200,original-1358941540.JPG\" />Ось історія юнака, який пішов шукати щастя.<o:p></o:p></p>\n<p>Один чоловік покликав перед смертю сина і сказав йому так:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Сину мі, я знаю, що я скоро помру, я хочу, щоб твоє життя було щасливе.<o:p></o:p></p>\n<p>Син запитав:\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2126:u-poshukah-shchastja-13-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4359,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4368,'P','2014-05-12',1,NULL,'Rss','Кожний думай, що на тобі мільйонів стан стоїть…','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5841,200,1613909.jpg\" />Невдовзі обиратимемо наступного президента України. Ми вже пережили кілька подібних виборів, але цього разу, після трагічних подій з одного боку і народного пробудження на Майдані – з іншого, на порозі суттєвих змін нашого суспільного життя&nbsp; та справжньої можливості жити в нормальній демократичній країні, це голосування матиме для нашої держави особливе значення.</p>\n<p>З історії багатьох держав упродовж століть знаємо, якою важливою особою є провідник, хоч би як ми його називали: цісар, цар, король, князь, гетьман, президент, генсек.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2127:kozhny-duma-shcho-na-tobi-miloniv-stan-stojit&catid=28:tema-nomera-cat&Itemid=2',4360,NULL,1,26),
(4369,'P','2014-05-13',1,NULL,'Rss','«…у прощах до відпустових місць ми можемо засмакувати вічність…»','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5843,200,ustyn.jpg\" />Протягом травня у храмах відправляють молебень, прославляючи Богородицю. Розкажіть про цю молитву детальніше... </strong></p>\n<p>У літургійній традиції Східної Церкви місцем, який присвячений особливому почитанню Пресвятої Богородиці, є серпень. Саме у серпні ми святкуємо празник Успення Пресвятої Богородиці, який показує усю суть місії Марії як Матері Сина Божого, яка, як вже вказує назва празника, не померла, але заснула.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2128:2014-05-13-07-44-08&catid=27:gk-v-ukrajini-cat&Itemid=57',4361,NULL,1,26),
(4370,'P','2014-05-13',1,NULL,'Rss','АПОСТОЛЬСЬКА АДГОРТАЦІЯ РАДІСТЬ ЄВАНГЕЛІЯ СВЯТІШОГО ОТЦЯ ФРАНЦИСКА ДО ЄПИСКОПІВ ДО СВЯЩЕНИКІВ І ДИЯКОНІВ ДО БОГОПОСВЯЧЕНИХ ОСІБ І ДО МИРЯН ПРО ПРОГОЛОШЕННЯ ЄВАНГЕЛІЯ В СУЧАСНОМУ СВІТІ','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5844,200,195961.jpg\" />1. Радість Євангелія наповнює серця і все життя тих, які зустрічаються з Ісусом. Тих, які дозволяють Йому спасати себе, Він визволяє від гріха, смутку, внутрішньої пустки, самотності. Разом з Ісусом Христом радість завжди народжується і відроджується. У цій Адгортації прагну звернутися до вірних християн, щоб запросити їх до нового етапу євангелізації, позначеного цією радістю, і вказати шляхи, якими Церкві слід йти в наступні роки.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2129:apostolska-adgortacija-radist-jevangelija-svjatishogo-otcja-francyska-do-jepyskopiv-do-svjashchenykiv-i-dyjakoniv-do-bogoposvjachenyh-osib-i-do-myrjan-pro-progoloshennja-jevangelija-v-suc',4362,NULL,1,26),
(4371,'P','2014-05-13',1,NULL,'Rss','Папа Франциск пам\'ятає і молиться за Україну','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5845,200,papiez_franciszek_640.jpg\" />Папа Римський Франциск молиться за пошук мирних шляхів врегулювання кризи в Україні, пам\'ятає&nbsp;жертв напружених конфліктів, що вибухнули в ці останні місяці, а також про тих, які страждають з причин нинішніх подій.</strong></p>\n<p>Про це Святіший Отець написав у листі до в.о. Президента України, Голови Верховної Ради України О.Турчинова, повідомляє&nbsp;<a href=\"http://rada.gov.ua/news/Novyny/Povidomlennya/92505.html\">сайт ВРУ</a>.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2135:papa-francysk-pamjataje-i-molytsja-za-ukrajinu&catid=27:gk-v-ukrajini-cat&Itemid=57',4364,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4372,'P','2014-05-14',1,NULL,'Rss','Євроінтеграція і Церква «Якщо хтось не йде на вибори, бо йому не хочеться, то це вже гріховність…»','<p><img src=\"http://askoldova-mohyla.org/uk/image,5848,200,9AC3D2.jpg\" style=\"margin: 10px; float: right;\" />Голосує той, хто переконаний, що його голос є важливим і це його справжній вибір. Стосовно тих, які не йдуть на голосування, то тут потрібно розуміти причину такої позиції. Якщо йдеться про позицію на кшталт: \"Мені не хочеться\" або ж \"Мені все одно\", – тоді можна говорити навіть про гріховність такого вчинку.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2138:2014-05-14-20-35-52&catid=28:tema-nomera-cat&Itemid=2',4366,NULL,1,26),
(4373,'P','2014-05-15',1,NULL,'Rss','Мирське багатство (16 травня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5845,200,dsT3O1D_WAc.jpg\" />Дожив Матвій до похилого віку. Жив він праведно, нікого не ображав, однак чесною працею любив старий збирати багатство. Сини у Матвія дорослі, до роботи меткі, самі уміли добре господарювати, от і зібралося багатства чимало.</p>\n<p>Але настав час вмирати старому, і душа його полинула до брами небесної. Довго вона там стояла і чекала, але брама не відчинялася, і став міркувати Матвій, чи не згрішив чимось, став пригадувати своє земне життя, але не знайшов за собою нічого злого.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2132:myrske-bagatstvo-16-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4367,NULL,1,26),
(4374,'P','2014-05-17',1,NULL,'Rss','Відповідальність церков за майбутнє України','<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5851,200,czerkvy.jpg\" />Виступ на</em><em> </em><em><a href=\"http://risu.org.ua/ua/index/exclusive/reportage/56442/\">круглому столі Центру Разумкова</a></em><em> </em><em>15 травня 2014 року «Що може зробити Церква в нинішній суспільно-політичній ситуації в Україні?»</em></p>\n<p>Найбільш видатний соціальний філософ сучасності Юрген Габермас говорить про повернення релігії в публічний дискурс як доконаний факт, характерний для доби постмодерну або гіпермодерну – як би ми не називали сучасний момент.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2140:vidpovidalnist-cerkov-za-mabutnje-ukrajiny&catid=28:tema-nomera-cat&Itemid=2',4369,NULL,1,26),
(4375,'P','2014-05-16',1,NULL,'Rss','Ми преображуємо світ','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5851,200,ArticleImages_56437_MM_2.jpg\" />Події Майдану та після нього сигналізували вироблення того духовного центру, який стане опорою для нового українського суспільства. Подібними роздумами про те,&nbsp;яким чином можливо побудувати єдність на базисі християнства, поділився віце-ректор Українського католицького університету Мирослав Маринович під час своєї лекції у Подільському клубі на тему «Християнські цінності як платформа до національного порозуміння» у Києві, 14 травня.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2139:my-preobrazhujemo-svit&catid=37:spilnota-cat&Itemid=49',4370,NULL,1,26),
(4376,'P','2014-05-17',1,NULL,'Rss','Зіпсутість (18 травня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5845,200,swan.jpg\" />Багато років один підрядник працював у великій будівельній фірмі. Одного разу він отримав завдання побудувати віллу за проєктом, який припав йому до вподоби. Він міг вибрати для будівництва хоч яке завгодно місце і не хвилюватися про витрати.</p>\n<p>Роботи почалися швидко. Але, скориставшись висловленою йому довірою, підрядник вирішив використати матеріяли найнижчої якости і найняв робітників дуже низької кваліфікації, виплачуючи їм мінімальну зарплату. Заощаджені гроші він забрав собі.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2134:zipsutist-18-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4371,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4377,'P','2014-05-18',1,NULL,'Rss','Незвичайна зустріч','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5854,200,samaryanka1-700x456.jpg\" />Роздуми над&nbsp;<a href=\"http://www.credo-ua.org/2011/05/116179\">Словом Божим</a> на Неділю Самарянки (УГКЦ)</p>\n<p>Самарія – область між Галилеєю на півночі та Юдеєю на півдні[i]. Коли Ісус з учнями йшов з Юдеї до Галилеї або назад, вони проходили через Самарію. Географічно це був найзручніший маршрут, хоча й небезпечний: іноді самаряни грабували паломників, які прямували з Галилеї до Єрусалиму, тож більшість воліла йти обхідним маршрутом долиною Йордану до Єрихону й звідти вгору, до Єрусалиму. Але того разу, про який оповідає Йоан, вони без пригод йшли на північ через Самарію. Було спекотно, учні відлучилися, а Ісус зупинився біля криниці Якова, яка існує і понині. До Нього підійшла жінка.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2141:nezvychana-zustrich&catid=28:tema-nomera-cat&Itemid=2',4372,NULL,1,26),
(4378,'P','2014-05-18',1,NULL,'Rss','З кого брати приклад (19 травня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5845,200,hq-wall.jpg\" />Одного разу в лісі чоловік побачив лисицю, яка не мала ніг. Він захотів дізнатися, як вона змогла вижити. І раптом побачив тигра із здобиччю в руках. Тигр трохи поїв, а решту віддав лисиці.</p>\n<p>Наступного дня Бог знову послав тигра годувати лисицю. Чоловік замислився про милосердя Боже і подумав:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2136:z-kogo-braty-pryklad-19-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4373,NULL,1,26),
(4379,'P','2014-05-18',1,NULL,'Rss','Вірус, який не дозволяє дружити з Богом','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,5854,200,700.jpg\" style=\"margin: 10px; float: right;\" />Ісус називає нас друзями. Це дуже мило. Однак ми не земляки-дружбани з Богом. Ми радше Його неприятелі.</strong></p>\n<p>Розмовляючи з людьми, які розпізнають своє покликання, я щоразу частіше бачу в молодих чоловіках наявність такого вірусу. Нерішучість і страх перед ухваленням серйозного рішення. Вірус виглядає привабливо. Він вбраний у гарні слова: відповідальність, потреба довшого часу, глибокий аналіз ситуації, обов’язковість перевірки інших можливостей… А&nbsp;все це підбите браком відваги дозволити собі втрату. Мільйони нереальних сценаріїв, і кожен починається словами: а що буде, якщо…?</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2142:virus-jaky-ne-dozvoljaje-druzhyty-z-bogom&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4374,NULL,1,26),
(4380,'P','2014-05-19',1,NULL,'Rss','Духовний батько в\'язнів Майданека','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5857,200,majdanek.jpg\" />Він був звичайним парафіяльним священиком, а тепер багато душпастирів мають його за свого покровителя. Третій рік поспіль в травні містечко Перемишляни на Львівщині приймає греко-католицьке духовенство з усієї України на прощі, присвяченій пам’яті блаженного священомученика Омеляна Ковча. 14 травня тут зустрічаються служителі УГКЦ з нагоди 5-ї річниці проголошення о. Ковча їхнім покровителем та 25-ї річниці легалізації Церкви.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2143:duhovny-batko-vjazniv-madaneka&catid=35:istorychni-postati-cat&Itemid=48',4375,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4381,'P','2014-05-19',1,NULL,'Rss','Татарська доля','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5857,200,1394546806-1452.jpg\" />Російська пропаганда переконує, що історія Криму&nbsp;— це віковічне російське перебування на цій землі. Радше належало би сказати, що це свідчення цинізму і нещадного грабунку чужої землі.</strong></p>\n<p>Президент Путін, оголошуючи приєднання Криму, сказав, що «у&nbsp;серцях, у свідомості людей» він завжди був «невід’ємною частиною Росії». Все там просякнуте нашою історією і гордістю, додав він.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2144:tatarska-dolja&catid=37:spilnota-cat&Itemid=49',4376,NULL,1,26),
(4382,'P','2014-05-20',1,NULL,'Rss','Феномен Майдану','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5860,200,46464.jpg\" />Феномен Майдану став несподіваним для всіх, навіть для Церкви. Український народ зміг разом мирно стояти задля європейського майбутнього своєї країни, яке базується на таких цінностях, як верховенство закону, неприйнятність корупції, несприйняття насильства і нетерпимість авторитарної поведінки. Європейський проект став проектом суспільного розвитку в Україні, і Церкви допомогли в цьому.</strong><o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2146:fenomen-madanu&catid=28:tema-nomera-cat&Itemid=2',4378,NULL,1,26),
(4383,'P','2014-05-20',1,NULL,'Rss','71% українців за те, щоб українська була єдиною державною мово','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5860,200,00568k2s.jpg\" />Більшість українців виступають за те, щоб українська мова була єдиною державною в Україні. Про це говорять дані дослідження, проведеного соціологічною службою Центру Разумкова.</strong><o:p></o:p></p>\n<p>Так, за українську як за єдину державну мову висловився 71% респондентів. При цьому 31,3% висловлюються також за те, щоб російська була офіційною у деяких регіонах.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2145:71-ukrajinciv-za-te-shchob-ukrajinska-bula-jedynoju-derzhavnoju-movo&catid=37:spilnota-cat&Itemid=49',4379,NULL,1,26),
(4384,'P','2014-05-20',1,NULL,'Rss','Сон християн породжує тоталітарних чудовиськ','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5860,200,india4.jpg\" />Напередодні, без сумніву, доленосних для українського суспільства виборів – можливо, як ніколи до тепер – перед очима українських громадян відкрилася неприваблива картина безпринципності й зіпсутості української еліти. Обливання брудом, спроби підставити конкурента, скинути з себе відповідальність, загорнути себе у привабливу обгортку, – усе це виглядає низько і підло.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2147:son-hrystyjan-porodzhuje-totalitarnyh-chudovysk&catid=27:gk-v-ukrajini-cat&Itemid=57',4380,NULL,1,26),
(4385,'P','2014-05-20',0,NULL,'Note','Празник Миколая Теплого','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5864,200,1035933.jpg\" alt=\"\" width=\"200\" height=\"150\" />22 травня, четвер, парафія св. Миколая Мирлікіського запрошує на храмовий Празник Миколая Теплого.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">09:00 початок Архиєрейської Божественної Літургії (службу провадитиме Блаженніший Любомир (Гузар)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Запрошуємо кожного</span><br /><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Радо зустрічаємо кожного</span><br /><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Молимось за кожного</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5864,200,1035933.jpg\" alt=\"\" width=\"200\" height=\"150\" />22 травня, четвер, парафія св. Миколая Мирлікіського запрошує на храмовий Празник Миколая Теплого.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">09:00 початок Архиєрейської Божественної Літургії (службу провадитиме Блаженніший Любомир (Гузар)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Запрошуємо кожного</span><br /><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Радо зустрічаємо кожного</span><br /><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Молимось за кожного</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">По завершенню Архиєреської Божественної літургії, традиційно, запашна кава, приємне оточення, духовні бесіди.</span></p>',NULL,NULL,4381,5,1,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4386,'P','2014-05-20',1,NULL,'Rss','«Хочеться, аби наші політики пам’ятали про загиблих хлопців»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5864,200,manyshyn.jpg\" />Українська Греко-Католицька Церква продовжує зміцнюватися. За останніх кілька місяців Церква збагатилася новими владиками та екзархатами. І Департамент інформації УГКЦ продовжує серію інтерв’ю з владиками-номінантами, аби її вірні бачили, що Синод єпископів обирає кращих із кращих. Сьогодні ми розмовляємо з владикою-номінантом Богданом (Манишиним), який незабаром стане Єпископом-помічником Стрийської єпархії.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2149:2014-05-20-18-56-45&catid=28:tema-nomera-cat&Itemid=2',4383,NULL,1,26),
(4387,'P','2014-05-22',1,NULL,'Rss','Притча про внутрішнє багатство (23 травня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5864,200,images.jpg\" />Придбав чоловік собі новий дім&nbsp;–&nbsp;великий, гарний&nbsp;–&nbsp;і сад з фруктовими деревами біля будинку. А поряд у старенькій хатинці жив заздрісний сусід, який повсякчас намагався зіпсувати йому настрій: то сміття під ворота підкине, то поганий напис на воротах напише, то ще якусь підлість учинить.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2151:prytcha-pro-vnutrishnje-bagatstvo-23-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4385,NULL,1,26),
(4388,'P','2014-05-22',1,NULL,'Rss','Перше причастя, як символ ствердження у Бозі','<p><img src=\"http://askoldova-mohyla.org/uk/image,5867,200,4.JPG\" style=\"margin: 10px; float: right;\" />Цілий рік наші діти готувалися до першого причастя. Кожної неділі відвідували катехизації, кожної неділі стверджувалися у вірі на Богослужіннях. І ось – їхнє перше причастя. Наша церква дозволяє приймати святу тайну Євхаристія ще з малку, але це перше Причастя є цілком усвідомленим і цілком утвердженим, тому вона відіграє велику роль в духовному розвитку дитини.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2153:pershe-prychastja-jak-symvol-stverdzhennja-u-bozi&catid=25:parafija-cat&Itemid=27',4386,NULL,1,26),
(4389,'P','2014-05-23',1,NULL,'Rss','Українське суспільство пройшло велику школу зрілості','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5868,200,DBB0790.jpg\" /></strong>Однією з найвідоміших письменниць України на сьогодні є&nbsp;Оксана Забужко. Широку популярність здобула завдяки книжці «Польові дослідження українського сексу» (1996), яка була перекладена на десяток мов. Її остання робота – «Музей покинутих секретів» – у перекладі польською мовою отримала 2013 року престижну премію «Ангел Господній». На Європейській&nbsp;письменницькій конференції, що проходила днями в Берліні, Оксана Забужко розповіла про Україну останніх місяців і її перспективи на майбутнє.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2154:ukrajinske-suspilstvo-proshlo-velyku-shkolu-zrilosti&catid=37:spilnota-cat&Itemid=49',4387,NULL,1,26),
(4390,'P','2014-05-23',1,NULL,'Rss','Україна надзвичайна','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5871,200,ukraine-e1378712033712.jpg\" />Не дивлячись на те, що з усіх боків українцям намагаються довести, що вони ні на що не здатні, що найкращий вихід для них – «валити з цієї країни якомога швидше», що дивитися в Україні немає на що і світові на нас начхати, цифри статистики та факти говорять нам речі абсолютно протилежні, повідомляє&nbsp;<a href=\"http://rama.com.ua/ukrayina-nadzvichayna-30-neymovirnih-faktiv-pro-ukrayinu-yaki-nihto-ne-znav/\" target=\"_blank\">rama.com.ua</a>.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2155:ukrajina-nadzvychana&catid=28:tema-nomera-cat&Itemid=2',4389,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4391,'P','2014-05-23',1,NULL,'Rss','«Ми обираємо не особу Президента, а майбутнє нашої держави»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5873,200,z_tv.jpg\" />У суботу, 24 травня, члени Всеукраїнської Ради Церков і релігійних організацій разом з представниками влади будуть молитися в соборі Святої Софії Київської. Молитва розпочнеться о 13.00.<o:p></o:p></p>\n<p>Як зазначив Глава Української Греко-Католицької Церкви Блаженніший Святослав (Шевчук), крім того, у всіх храмах і релігійних організаціях відбуватимуться молитовні чування, Божественні Літургії та інші молитовні заходи.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2156:2014-05-23-21-03-55&catid=28:tema-nomera-cat&Itemid=2',4391,NULL,1,26),
(4392,'P','2014-05-23',1,NULL,'Note','В суботу, 24 травня, — спільна молитва за Україну','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,5871,200,2014.jpg\" alt=\"\" width=\"200\" height=\"96\" />У суботу о 13:00 глави українських конфесій разом помоляться у храмі Софії Київській. Молитви за Україну також триватимуть протягом дня у храмах всіх конфесій, з огляду на підтримку цієї ініціативи з боку Ради Церков.</strong><br /></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5871,200,2014.jpg\" alt=\"\" width=\"200\" height=\"96\" />Голова Верховної Ради, в.о. Президента України&nbsp;<strong>Олександр Турчинов</strong>&nbsp;закликав усіх віруючих та глав усіх церков і релігійних організацій об&rsquo;єднатися в молитві за Україну, за мир та єдність у державі, за проведення чесних виборів.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Про це він заявив 22 травня 2014 року під час зустрічі з Всеукраїнською Радою Церков і релігійних організацій, яка відбулася у конференц-залі Парламенту, повідомляє&nbsp;<a href=\"http://www.irs.in.ua/\" target=\"_blank\">Інститут релігійної свободи</a>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">На переконання керівника Парламенту, є дуже важливим, аби &ldquo;всі церкви, всі конфесії об&rsquo;єдналися у спільній молитві до Бога, щоб він благословив Україну, щоб в нашій державі був мир, спокій, злагода, щоб українці зробили свій важливий і правильний вибір&rdquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Турчинов запропонував присвятити цьому 24 травня &ndash; так званий &ldquo;день тиші&rdquo;, коли напередодні президентських виборів в Україні припиняється будь-яка агітація.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>У суботу о 13:00 глави українських конфесій разом помоляться у храмі Софії Київській. Молитви за Україну також триватимуть протягом дня у храмах всіх конфесій, з огляду на підтримку цієї ініціативи з боку Ради Церков.</strong>&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;Саме духовність повинна забезпечити наш розвиток, &ndash; наголосив в.о. Президента України &ndash; Духовна людина ніколи не знищуватиме власну державу&rdquo;. Він зауважив, що&nbsp;<a href=\"http://mvasin.org.ua/2014/05/606\" target=\"_blank\">терористи на Сході України не мають ні віри, ні справжньої духовності</a>, тому з такою жорстокістю чинять беззаконня, диверсії та проливають кров мирних людей та українських солдат.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Під час зустрічі Олександр Турчинов висловив вдячність членам Всеукраїнської Ради Церков і релігійних організацій&nbsp; &ldquo;за морально-виховну роботу, за пастирське слово, за намагання докласти усіх зусиль до забезпечення миру в Україні&rdquo;.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Глави українських конфесій&nbsp;<a href=\"http://www.irs.in.ua/index.php?option=com_content&amp;view=article&amp;id=1363%3A1&amp;catid=50%3Azv&amp;Itemid=78&amp;lang=uk\" target=\"_blank\">знову засудили прояви сепаратизму</a>, заявили про однодушне прагнення усіх релігійних лідерів єдності та цілісності України в її міжнародно-визнаних кордонах. Також глави церков і релігійних організацій засудили&nbsp;<a href=\"http://www.irs.in.ua/index.php?option=com_content&amp;view=article&amp;id=1369%3A1&amp;catid=50%3Azv&amp;Itemid=78&amp;lang=uk\" target=\"_blank\">провокації та штучні намагання зірвати виборчий процес шляхом зовнішньої агресії та диверсій</a>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ієрархи церков висловили підтримку ініціативі загальнонаціональної молитви за Україну, яка засвідчить єдність і консолідовану позицію всіх Церков і релігійних організацій країни у прагненні миру і злагоди у суспільстві, прозорих і чесних демократичних виборів.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><a href=\"http://www.irs.in.ua/\">http://www.irs.in.ua/</a></span></p>',NULL,NULL,4388,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4393,'P','2014-05-24',1,NULL,'Rss','4 риси майбутньої України за прогнозом Богдана Гаврилишина','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5873,200,gavrulushun2.jpg\" />НАЙКРАЩИМ ПРИКЛАДОМ ДЛЯ УКРАЇНИ МОЖУТЬ БУТИ ШВЕЙЦАРІЯ, ШВЕЦІЯ, НОРВЕГІЯ, АВСТРІЯ, ПОЛЬЩА ТА&nbsp;НІМЕЧЧИНА&nbsp;– ВВАЖАЄ ОДИН ІЗ&nbsp;ЗАСНОВНИКІВ ЕКОНОМІЧНОГО ФОРУМУ В&nbsp;ДАВОСІ БОГДАН ГАВРИЛИШИН</p>\n<p><strong>Україна зараз у&nbsp;складній політичній та&nbsp;економічній ситуації. Бачите шляхи виходу?</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2157:4-rysy-mabutnoji-ukrajiny-za-prognozom-bogdana-gavrylyshyna&catid=37:spilnota-cat&Itemid=49',4392,NULL,1,26),
(4394,'P','2014-05-24',1,NULL,'Rss','«Людина за життя переходить кілька етапів любові» - Андрій САПЕЛЯК, 94 роки, єпископ','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5873,200,280308_33.jpg\" />Коли від\'їжджав до Італії, мати вишила рушника червоними та чорними нитками. На ньому слова: \"Щирою рукою рушник вишивала та щастя у Бога для тебе благала\". Того рушника зберігаю досі.</p>\n<p>Я виїхав із дому 18-річним, а повернувся в Україну, коли мені виповнилося 79. Об\'їздив півсвіту, але марив поверненням на батьківщину.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2158:2014-05-24-09-11-36&catid=28:tema-nomera-cat&Itemid=2',4393,NULL,1,26),
(4395,'P','2014-05-24',1,NULL,'Rss','Військовий капелан має просто бути поруч','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,5876,200,102963.jpg\" style=\"margin: 10px; float: right;\" />В Україні військове капеланство поки не визнане на державному рівні, проте його діяльність є досить активною в нашій державі. Зокрема, у Львові вісім років тому створили Центр військового капеланства при Львівській архиєпартії УГКЦ.</strong></p>\n<p>Головними завданнями священиків-капеланів є духовна праця з військовослужбовцями та членами їхніх сімей.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2159:viskovy-kapelan-maje-prosto-buty-poruch&catid=27:gk-v-ukrajini-cat&Itemid=57',4394,NULL,1,26),
(4396,'P','2014-05-25',1,NULL,'Rss','Діяманти покори (25 травня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,5877,200,screen-4.jpg\" style=\"margin: 10px; float: left;\" />Якось два вершники вирішили виїхати на високу гору. Перший хотів довести, що Бог тільки й уміє вимагати від людей молитов і ніяк не бажає полегшити їхній тягар. А другий бажав випробувати свою віру. І коли надвечір добралися вони до вершини, з темряви пролунав голос:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2160:dijamanty-pokory-25-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4395,NULL,1,26),
(4397,'P','2014-05-25',1,NULL,'Rss','Великі очікування і важка дійсність','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5878,200,680306592.jpg\" />24 травня 2014 р. Папа Франциск розпочав свою подорож на Близький Схід. Він четвертий Папа Римський, який відвідує Святу Землю. Чи гасло паломництва «Аби всі були одно» можливе для здійснення?</strong></p>\n<p>У програмі другого закордонного візиту Папи&nbsp;— численні міжрелігійні та екуменічні зустрічі, також і з Патріархом Константинополя Варфоломеєм&nbsp;І з нагоди 50-річчя зустрічі папи Павла&nbsp;VI і Патріарха Афінагора у Єрусалимі, що стало верстовим каменем в екуменічному діалозі.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2161:velyki-ochikuvannja-i-vazhka-disnist&catid=32:gk-svit-cat&Itemid=58',4396,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4398,'P','2014-05-26',1,NULL,'Rss','Куди ведуть мрії (26 травня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,5879,200,dreams.jpg\" style=\"margin: 10px; float: left;\" />Одного вечора маленький Джимі, лежачи на ліжечку, пильно дивився на місяць, що був уповні.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Що ти робиш? – запитала мама, заходячи до кімнати.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Дивлюся на місяць, - відповів хлопчик.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Уже пізно. Пора спати, - ніжно мовила мама.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2162:kudy-vedut-mriji-26-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4397,NULL,1,26),
(4399,'P','2014-05-26',1,NULL,'Rss','Спільна Декларація Папи Римського Франциска та Вселенського Патріарха Вартоломея І','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5879,200,1_0_802226.JPG\" />У неділю 25 травня 2014 року Папа Франциск та Вселенський Константинопільський Патріарх Вартоломей І, зустрічаючись у Єрусалимі, підписали Спільну декларацію наступного змісту:</p>\n<p>1. Як і наші шановані попередники Папа Павло VI і Вселенський Патріарх Атенагорас зустрічалися тут у Єрусалимі п’ятдесят років тому, також і ми, Папа Франциск та Вартоломей, Вселенський Патріарх, вирішили зустрітися на Святій Землі, «де наш спільний Відкупитель Господь Ісус жив, навчав, помер, воскрес і вознісся на небо, звідки послав Святого Духа на Церкву, яка народжувалася» (Спільне комюніке Папи Павло VI і Патріарха Атенагораса, оприлюднене після зустрічі 6 січня 1946 р.). Ця наша зустріч, чергове побачення єпископів Церков Риму і Константинополя, заснованих двома братами-апостолами Петром та Андрієм, є для нам джерелом великої духовної радості й дає нам нагоду призадуматися над глибиною та природою існуючих між нами зв’язків, що є плодом шляху, сповненого благодаті, під час якого Господь провадить нас, починаючи від того благословенного дня п’ятдесятирічної давності.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2163:spilna-deklaracija-papy-rymskogo-francyska-ta-vselenskogo-patriarha-vartolomeja-i&catid=27:gk-v-ukrajini-cat&Itemid=57',4398,NULL,1,26),
(4400,'P','2014-05-26',1,NULL,'Rss','Відновлений зір (27 травня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5881,200,429151-2560x1600.jpg\" />Літній чоловік із дорослим сином зайшли у вагон поїзда і сіли на свої місця. Хлопець сів біля вікна.</p>\n<p>Тільки-но поїзд рушив, хлопець висунув руку у вікно, щоб відчути потік повітря, і раптом захоплено закричав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тату, дивись, усі дерева біжать назад!</p>\n<p>Той тільки усміхнувся на ці слова. Поряд із чоловіком сиділа подружня пара. Вони були трохи спантеличені тим, що молодий чоловік поводить себе, як мала дитина.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2164:vidnovleny-zir-27-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4400,NULL,1,26),
(4401,'P','2014-05-26',1,NULL,'Rss','Один тур - одне питання','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5882,200,50233_2.jpg\" />В кінці минулого – на початку актуального тижня ми стали свідками без сумніву історичної події і, як це не дивно, події світового значення: виборів українського президента. Навіть зараз усе ще важко усвідомити усю вагу цієї події.</p>\n<p>Після буремних подій Майдану, в часі московської агресії українська держава і суспільство спромоглися провести вибори, які практично ні в кого не викликають сумніву стосовно їхньої прозорості й демократичності.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2169:odyn-tur-odne-pytannja&catid=37:spilnota-cat&Itemid=49',4401,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4402,'P','2014-05-27',1,NULL,'Rss','«П’ятдесятниця - період очікування дару Святого Духа»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5884,200,image034.jpg\" />Ісус Христос на 40-й день після свого воскресіння вознісся на небо, на 50-й зіслав Святого Духа на своїх апостолів. Період від Воскресіння до Зіслання Святого Духа в Церкві називається П’ятдесятницею. Він має свою структуру і включає в себе три великих свята. Про це розповідає о. Василь Рудейко, доктор богослов\'я, заступник голови Патріаршої літургійної комісії УГКЦ, священик храму Священномученика Климентія Шептицького у Львові.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2170:2014-05-27-07-45-40&catid=28:tema-nomera-cat&Itemid=2',4402,NULL,1,26),
(4403,'P','2014-05-27',1,NULL,'Rss','Папа і Патріарх','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5884,200,65464.jpg\" />Не помилюсь, якщо скажу, що очі більшої частини християн цілого світу сьогодні прикуті до Святої землі, де відбувається зустріч Папи Франциска і Патріарха Константинопольського Варфоломія. Зустріч знакова з багатьох оглядів, але перш за все тому, що ювілейна. Рівно 50 років тому Папа Павло VІ зустрівшись там таки на Святій землі з Патріархом Константинопольської Церкви Атенагорасом розірвали майже тисячолітню історію неприязні. Рік по тому ці два ієрархи спільно проголосили про «анулювання» взаємних анафем 1054 року. Які вважаються формальною точкою розколу між Церквою Сходу і Заходу. Папа І Патріарх заявили, що вони: «<em>жалкують</em><em> </em><em>і бажають вилучити з пам\'яті і церковного середовища акти відлучення … спогад про які до наших днів служить перешкодою до зближення в дусі любові, і віддати їх забуттю</em>».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2171:papa-i-patriarh&catid=32:gk-svit-cat&Itemid=58',4403,NULL,1,26),
(4404,'P','2014-05-28',1,NULL,'Rss','Україна: початок сходження вгору','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5887,200,17987.jpg\" />Сталося. Україна як держава утвердила себе, знявши сумнів у своїй перспективності. Путін опинився у глибокій дірявій «кальоші»: часто програючи у лобових сутичках з безжальними його найманцями, українці виграли свій найважливіший бій. Бо всьому світові зрозуміло: утвердження України як успішної демократичної держави озивається путінському режимові похоронним дзвоном.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2172:ukrajina-pochatok-shodzhennja-vgoru&catid=37:spilnota-cat&Itemid=49',4405,NULL,1,26),
(4405,'P','2014-05-28',1,NULL,'Rss','Бесіда на Вознесіння Господа нашого Ісуса Христа святого Отця нашого Іоана Золотоустого','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5887,200,28274000.jpg\" />1. І тоді, коли ми вшановували пам\'ять Хреста, ми звершу­вали свято поза містом. І тепер, коли в цей славний і світлий день вшановуємо Вознесіння Розп\'ятого, ми знову святкуємо поза містом. Ми робимо це не для того, щоб принизити місто, а намагаємося вшанувати мучеників. Щоб ці святі не докоряли нам і не говорили: \"Ми не сподобилися побачити жодного дня Владики нашого, який би святкувався у наших обителях\", - щоб ці святі не стали докоряти нам і говорити: \"Ось, ми проли­ли за Нього свою кров і сподобилися покласти за Нього голови свої, однак не сподобилися побачити день свята Його, який би звершувався у наших оселях\", - тому ми з нагоди цього дня, залишивши місто, прийшли до ніг цих святих, виправдуючись перед ними і за минулі часи.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2173:besida-na-voznesinnja-gospoda-nashogo-isusa-hrysta-svjatogo-otcja-nashogo-ioana-zolotoustogo&catid=28:tema-nomera-cat&Itemid=2',4406,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4406,'P','2014-05-28',1,NULL,'Rss','«Загоїти ще відкриті рани та рішуче продовжувати прямувати шляхом до повного сопричастя»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5889,200,palomnyctvo6papy6na6svjatu6zemlju.jpg\" />Прощення за все, чим ми спричинилися до поділу між християнами, попросив Папа Франциск під час загальної аудієнції у середу, 28 травня 2014 р., під час якої він поділився з паломниками, що заповнили площу Святого Петра у Ватикані, спогадами із свого паломництва до Святої Землі. Він назвав його «великим дарунком для Церкви», подякувавши Господеві за те, що попровадив його до «цієї благословенної землі».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2174:2014-05-28-15-13-20&catid=32:gk-svit-cat&Itemid=58',4407,NULL,1,26),
(4407,'P','2014-05-29',1,NULL,'Rss','Вознесіння Господа нашого Ісуса Христа','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5890,200,voznesinnia-gospodnie.jpg\" />Лк. 24, 36–53:</strong><strong> «А як Він благословляв їх, віддалився від них і почав возноситись на небо».</strong></p>\n<p>Вдивляючись в особу Ісуса Христа, розуміємо, що Він, незважаючи на те, що є Другою Особою Божою, як Людина жив в часі, мав своє народження, розвивався, проповідував, пройшов Хресну Дорогу, страждав, воскрес і вознісся.</p>\n<p>Так само і кожному з нас Бог дав різні етапи життя, обставини, ближніх. Вміймо ці обставини цінувати, побачити за ними невипадковість, яку дає нам Бог. Через &nbsp;цей конкретний момент, обставини, Господь окреслює нашу місію, в цьому житті, показує, чого Він від нас очікує.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2175:voznesinnja-gospoda-nashogo-isusa-hrysta&catid=28:tema-nomera-cat&Itemid=2',4408,NULL,1,26),
(4408,'P','2014-05-29',1,NULL,'Rss','В УГКЦ розпочалася Декада місійності','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5891,200,dsc_0048_834c2.jpg\" />На свято Вознесіння ГНІХ, згідно з рішенням Синоду Єпископів, в УГКЦ розпочинається Декада місійності. Цей період триватиме до Зіслання Святого Духа (П’ятдесятниці) і має на меті звернути увагу всієї Церкви на спільне покликання бути місійними.</p>\n<p>«Цими днями ми будемо готуватися здійснювати апостольське служіння відповідно до покликання охрещеного Божого люду. Ми не можемо затримати тільки для себе всі ті дари, які отримали від воскреслого Христа. Разом з апостолами слід проповідувати правду про єдиного Бога в трьох особах і робити Його учнями всіх тих, хто ще не знає правдивого Бога», - сказав проповідник.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2176:v-ugkc-rozpochalasja-dekada-misinosti&catid=27:gk-v-ukrajini-cat&Itemid=57',4409,NULL,1,26),
(4409,'P','2014-05-29',1,NULL,'Rss','Небезпека (30 травня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,5881,200,thumb.jpg\" style=\"margin: 10px; float: left;\" />Весела молода компанія вирішила покататися на човні. Молодь попростувала до рибалки , який за плату запропонував їм свого човна. Човен був великий, просторий і подобався молоді. Коли всі зі сміхом і жартами сіли в нього й почали відчалювати, на берег вийшов дідок. Помітивши їх, старий зупинився і закричав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Вилазьте хутчіш! Човен ваш несправний! Він протікає: біля керма на дні невелика дірка, через яку сочиться вода. Хоча дірка й замазана, але вода скоро вимиє її, і ви будете у великій небезпеці.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2167:nebezpeka-30-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4410,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4410,'P','2014-05-30',1,NULL,'Rss','«Громадянське суспільство має доповнювати суспільство духовне»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5892,200,5464.jpg\" />У ці дні, коли Україна стоїть перед вибором свого шляху розвитку, ми пропонуємо вам розмову з&nbsp;<strong>Євгеном Сверстюком – філософом, дисидентом, літературознавцем, письменником, одним із учасників «Ініціативи Першого грудня», головним редактором газети «Наша віра»</strong>.</p>\n<p>У 70-х роках Євген Сверстюк переслідувався тодішнім політичним режимом за свої думки й ідеї. І, незважаючи на всі випробування, не зрікся своїх переконань. До сьогодні він продовжує – своїм життям, творчістю і діяльністю – проголошувати ідею «десовєтизації» України, духовного розвитку людини та суверенності держави. Ці, здавалося б, дещо різнопланові ідеї, насправді мають у собі щось спільне. Про те, як бачить Євген Сверстюк сьогоднішню ситуацію в Україні, куди нам іти далі та як розвивати себе і духовне суспільство, а також про відстоювання власного національного обличчя – в інтерв’ю.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2177:2014-05-30-10-52-41&catid=37:spilnota-cat&Itemid=49',4411,NULL,1,26),
(4411,'P','2014-05-30',1,NULL,'Rss','Майдан не був антиросійським','<p><img src=\"http://askoldova-mohyla.org/uk/image,5894,200,majdan_rozaniec.jpg\" style=\"margin: 10px; float: right;\" />В УГКЦ налагоджені братерські відносини з Українською Православною Церквою у єдності з Московським Патріархатом. Уся спільна діяльність обох Церков та заяви, які вони видали в останні кілька місяців, у період Майдану, зроблені разом. Навіть більше, сьогодні черговим головуючим у Всеукраїнській раді Церков і релігійних організацій є Предстоятель Української Православної Церкви Митрополит Володимир (Сабодан).<strong><o:p></o:p></strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2178:madan-ne-buv-antyrosiskym&catid=27:gk-v-ukrajini-cat&Itemid=57',4412,NULL,1,26),
(4412,'P','2014-05-30',1,NULL,'Rss','Московія розпадеться через 10 років','<p><img src=\"http://askoldova-mohyla.org/uk/image,5895,200,maxresdefault.jpg\" style=\"margin: 10px; float: right;\" />Російська Федерація зараз переживає не найкращі свої часи і вже у недалекому майбутньому може перестати існувати як єдина держави, розпавшись на окремі республіки.</p>\n<p>Таку думку висловив член Римського клубу, один із засновників Всесвітнього економічного форуму в Давосі Богдан Гаврилишин у коментарі кореспондентові Укрінформу.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2179:moskovija-rozpadetsja-cherez-10-rokiv&catid=37:spilnota-cat&Itemid=49',4414,NULL,1,26),
(4413,'N','2014-05-31',1,NULL,'Rss','Як у дзеркалі (1 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5897,200,DSC06793.jpg\" />Якось імператор покликав одного зі своїх васалів. В імперії той прославився жорстокістю і жадністю, і піддані його жили у страху.</p>\n<p>Імператор сказав до васала:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Хочу, щоб ти вирушив у подорож по світу і знайшов для мене насправді добру людину.</p>\n<p>Той відповів:</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2180:jak-u-dzerkali-1-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4415,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4414,'P','2014-05-31',1,NULL,'Rss','Як у дзеркалі (1 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5897,200,DSC06793.jpg\" />Якось імператор покликав одного зі своїх васалів. В імперії той прославився жорстокістю і жадністю, і піддані його жили у страху.</p>\n<p>Імператор сказав до васала:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Хочу, щоб ти вирушив у подорож по світу і знайшов для мене насправді добру людину.</p>\n<p>Той відповів:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2181:jak-u-dzerkali-1-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4416,NULL,1,26),
(4415,'P','2014-06-01',1,NULL,'Rss','«Царський бенкет» - науково-практичний семінар з організації та проведення літнього християнського табору','<p><img src=\"http://askoldova-mohyla.org/uk/image,5898,200,2.jpg\" style=\"margin: 10px; float: right;\" /></p>\n<p>Протягом трьох днів з 29 по 31 травня 2014 року в Брюховичах, що неподалік Львова, проходив науково-практичний семінар «Царський бенкет». Мета заходу: надати учасникам якомога ґрунтовніші знання та практичні навички&nbsp; в організації і проведенні літніх християнських таборів для дітей. Організатором семінару були: Катехитично – педагогічний інститут Українського Католицького Університету, директором якого є сестра Луїза Цюпа, та Патріарша Катехитична комісія УГКЦ.</p>\n<p>29 травня о четвертій годині дня відбулася реєстрація учасників семінару. Опісля усі розмістилися у відпочинковому комплексі. Варто зауважити, що семінар охопив багато регіонів України. У цьому заході взяла участь молодь з Києва, Львова, Тернополя, Івано Франківська, Луцька, Червонограда. То ж чи не кожна єпархія УГКЦ представила своїх представників на семінарі. Загалом у науково - практичному семінарі «Царський бенкет» взяло участь більше сорока людей.<strong></strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2182:2014-06-01-20-20-01&catid=25:parafija-cat&Itemid=27',4417,NULL,1,26),
(4416,'P','2014-06-01',1,NULL,'Rss','Комусь довіряти, а когось остерігатися (2 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5898,200,3.jpg\" />Учень одного разу запитав у старця:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Як мені розрізняти людей: кому довіряти, а кого остерігатися?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Спершу скажу тобі, кого треба остерігатися, мовив старець. –Остерігайся найсмиреннішого на вигляд! Коли побачиш, що хтось перед тобою б’є поклони, обіймає тебе і виявляє неймовірну прихильність, того бійся найбільше!</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2183:komus-dovirjaty-a-kogos-osterigatysja-2-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4418,NULL,1,26),
(4417,'P','2014-06-02',1,NULL,'Rss','Єднаймося в молитві за Україну','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5901,200,images.jpg\" />Події в Україні стали лакмусовим папірцем для переконань і поглядів багатьох християн, оголили «приховане в пітьмі» та порушили безліч серйозних і важких питань.</strong></p>\n<p>Будь-яка криза або конфлікт&nbsp;— міжособистісний, міжнаціональний чи міждержавний,&nbsp;— за всього супровідного болю й неминучих втратах, має і свою позитивну сторону. Він показує, хто є хто, хто у що і в кого вірить; він оголює те, що за звичних умов залишається непоміченим або відсувається на периферію як малозначуще.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2184:jednamosja-v-molytvi-za-ukrajinu&catid=28:tema-nomera-cat&Itemid=2',4419,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4418,'P','2014-06-03',1,NULL,'Rss','Любов, багатство і талан (3 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5902,200,love.jpg\" />Жінка вийшла зі своєї оселі і побачила на вуличному дворику трьох дідів з довгими білими бородами. Вона сказала:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я не знаю вас, але ви, мабуть, голодні. Будь ласка, заходьте в дім і пообідайте.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А чоловік удома? – спитали старі.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ні, - відповіла вона, - його немає.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тоді ми не можемо зайти, - відповіли вони.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2185:ljubov-bagatstvo-i-talan-3-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4420,NULL,1,26),
(4419,'P','2014-06-03',1,NULL,'Rss','Де має працювати християнин?','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5902,200,724688179.jpg\" />Чи є такі заняття, які в нинішньому світі легальні, поширені, «нормальні»&nbsp;— і які ми, християни, повинні відкидати саме тому, що ми християни?</strong><o:p></o:p></p>\n<p>Кілька років тому сиділи ми з колегою за кавою і говорили про пошуки роботи. Розмова не була випадковою: ще два тижні тому ми обоє працювали в одній редакції. Спершу мене звільнили (скорочення штатів, економія…), а за кілька днів він вирішив піти звідти, бо йому не відповідали нові запропоновані умови. Ми обоє, на щастя, вже мали перспективи нових посад, із багатьох поглядів навіть кращих і цікавіших,&nbsp;— тому спокійно, без стресу дискутували собі щодо того, «якою має бути робота», щоб людина за неї бралася без спротиву і з вигодою (не тільки фінансовою).<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2187:de-maje-pracjuvaty-hrystyjanyn&catid=37:spilnota-cat&Itemid=49',4421,NULL,1,26),
(4420,'P','2014-06-03',1,NULL,'Rss','Українське лінивство','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5904,200,guzar_pro_vidensjku_kotletu.jpg\" />Незважаючи на непевну політичну ситуацію, на майже військовий стан, на якусь нерішучість у всіх трьох гілках влади, існує загальне переконання, що треба зайнятися розбудовою держави, нормальної з усіх точок зору.</p>\n<p>Багато говориться, що Україна – це велика країна із значним потенціалом, який після потрясінь на зламі 2013–2014 років визволився з пут крайнього матеріалізму і чекає на своє використання для загального блага народу.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2189:ukrajinske-linyvstvo&catid=28:tema-nomera-cat&Itemid=2',4422,NULL,1,26),
(4421,'P','2014-06-03',1,NULL,'Rss','Євромайдан вимагає, аби урядовці 8 червня прозвітували за 100 днів роботи','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5904,200,popovich_miroslav_guzar_lyubo_003.jpg\" />У цю&nbsp;неділю у&nbsp;Києві громадськість зібралась на&nbsp;віче, щоб продемонструвати свій намір&nbsp;— вимагати від влади звіту. Перед громадою виступили учасник “Ініціативи першого грудня” Мирослав Попович та&nbsp;активісти Громадського сектора Євромайдану й&nbsp;озвучили ключові теми, на&nbsp;які міністри повинні звернути особливу увагу під час майбутнього звітування. Громадяни очікують отримати відповіді на&nbsp;ці&nbsp;запитання від міністрів під час Всеукраїнського віча 8&nbsp;червня. Кожен громадянин мав змогу залишити своє запитання до&nbsp;уряду. Для цього було встановлено спеціальні скриньки біля сцени Майдану.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2188:jevromadan-vymagaje-aby-urjadovci-8-chervnja-prozvituvaly-za-100-dniv-roboty&catid=37:spilnota-cat&Itemid=49',4423,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4422,'P','2014-06-04',1,NULL,'Rss','Дружина (4 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5902,200,x_2d3e28a3.jpg\" />Один юнак ніяк не міг обрати собі дружину: то личко видавалося йому не надто симпатичним, то фігура не надто привабливою. Та одного разу йому пощастило: зустрів він чарівника, який пообіцяв розв’язати його проблему.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ось тобі сірник, - сказав чарівник, простягаючи його юнакові. – Тільки-но будеш готовий, зламай його перед будь-якою дівчиною, і вона поступово ставатиме такою, якою ти&nbsp; хочеш.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2186:druzhyna-4-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4424,NULL,1,26),
(4423,'P','2014-06-05',1,NULL,'Rss','Перспектива (5 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5905,200,463.jpg\" />Питання: коли птах буває більшим за гору? Відповідь: коли птах ближче, ніж гора.</p>\n<p>Звісно, жоден, навіть найбільший птах не може бути більшим навіть від найменшої гори. Але така оптична омана виникає, коли пташки, наприклад, сідають на вікно і заступають гору на видноколі.</p>\n<p>Подеколи подібна спотворена перспектива виникає, коли ми дивимося на свої проблеми. Вони видаються нам великими і грізними, вкритими чорним зловісним пір’ям, палючими очима і гострими дзьобами, готовими безжалісно роздерти нещасну комашку, що вилізла зі щілини.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2190:perspektyva-5-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4425,NULL,1,26),
(4424,'P','2014-06-06',1,NULL,'Rss','Шукання істини (6 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5905,200,491059.jpg\" />Потрапив один мудрець на небо.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Як прожив ти своє життя? – спитав його ангел.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Шукав істину, - відповів мудрець.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Це добре! – похвалив мудреця ангел. – Розкажи, що робив ти, шукаючи істину.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2191:shukannja-istyny-6-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4426,NULL,1,26),
(4425,'P','2014-06-07',1,NULL,'Rss','Притча про аборт (7 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5905,200,nazyvayut-detey-zolushkami-i-angelami.jpg\" />Прийшла якось до старця сімейна пара.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Отче, - каже жінка, - я чекаю на дитину, а в нас вже й так четверо дітей; коли п’яте народиться – не проживемо. Благословіть зробити аборт.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Бачу, живеться вам нелегко, - відповідає старець, - що ж, благословляю вас убити свою дитину. Тільки вбивайте найстаршу дочку, їй уже п\'ятнадцять літ: усе ж пожила вже, світу білого побачила, а те і промінчика сонячного не бачило.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2192:prytcha-pro-abort-7-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4427,NULL,1,26),
(4426,'P','2014-06-08',1,NULL,'Rss','Притча про щастя (8 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5905,200,images.jpg\" />Старий кіт побачив мале кошеня, що гналося за своїм хвостом, і спитав його:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Навіщо ти це робиш?</p>\n<p>Кошеня відповіло:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я дізнався, що найголовніше для котів – це щастя, а для мене щастя – мій хвіст. Тому я й женуся за ним. Коли я врешті-решт його спіймаю, то стану щасливим!</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2193:prytcha-pro-shchastja-8-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4428,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4427,'P','2014-06-09',1,NULL,'Rss','Притча про любов, що годує сім’ю (9 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5905,200,7607.jpg\" />Жила собі звичайна селянська сімʼя. Чоловік і жінка. І жили вони з того, що вирощували яблука у своєму саду, а восени продавали їх.</p>\n<p>Та сталося одного року, що селянин, занедужавши, не зібрав урожаю. Чимало яблук зігнило. Що ж робити? Якщо не продати врожаю, то сімʼя не виживе.</p>\n<p>Християнин зібрав усі гнилі яблука і поїхав на ринок продавати те, що є. благословила його жінка і сказала, що все буде добре. Так селянин і поїхав.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2194:prytcha-pro-ljubov-shcho-goduje-simju-9-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4429,NULL,1,26),
(4428,'P','2014-06-10',1,NULL,'Rss','Зоставайся сам собою (10 червня)','<p><img style=\"float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5905,200,4645.jpg\" />Одного разу король прийшов у сад і побачив, що гинуть дерева, кущі і квіти.</p>\n<p>Дуб сказав, що вмирає, тому що не може вирости таким високим, як сосна. Сосна опадала, тому що не може родити виноград, як виноградна лоза. А лоза всихала, тому що не могла цвісти, як троянда.</p>\n<p>Невдовзі він знайшов одну рослину, що тішила його серце, була розквітла і свіжа. Розпитавши її, він отримав таку відповідь:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2195:zostavasja-sam-soboju-10-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4430,NULL,1,26),
(4429,'P','2014-06-06',1,NULL,'Rss','Чорно-біла проповідь владики Гудзяка','<p><img src=\"http://askoldova-mohyla.org/uk/image,5912,200,im578x383-!.jpg\" style=\"margin: 10px; float: right;\" />Єпископ Української Греко-Католицької Церкви, президент Українського Греко-Католицького університету Борис Гудзяк у програмі «Вечір з Миколою Княжицьким» розповів про значення України для Європи, покликання нового президента та про Україну майбутнього</p>\n<p><strong>Розгублена Європа</strong></p>\n<p>«Ясно повинно бути для нас всіх, що, коли останнє півріччя Україна не сходила зі шпальт міжнародних медіа, це, в першій мірі, змусило європейців усвідомлювати що Україна - є. От голландці звернені були до моря завжди, вони більше знали про Південну Африку чи Бразилію.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2196:chorno-bila-propovid-vladyky-gudzjaka&catid=37:spilnota-cat&Itemid=49',4431,NULL,1,26),
(4430,'P','2014-06-05',1,NULL,'Rss','Церква не є заскорузлою, а – вільною','<p><img src=\"http://askoldova-mohyla.org/uk/image,5912,200,Pope_Francis_rides_through_St_Pe.jpg\" style=\"margin: 10px; float: right;\" />Церква не є заскорузлою, а – вільною. У проповіді Святіший Отець вказав на три групи осіб, які претендують називатись християнами: борці за однорідність, ті, що захищають власну думку та борці за власну користь. Для тих осіб Церква не є їхнім домом, вони лише її немов «винаймають». Багато кажуть, що вони є в Церкві, але вони у ній лише однією ногою, а другою ще не увійшли. Таким чином, вони забезпечують собі можливість бути одночасно у двох місцях.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2197:cerkva-ne-je-zaskoruzloju-a-vilnoju&catid=32:gk-svit-cat&Itemid=58',4432,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4431,'P','2014-06-07',1,NULL,'Rss','Маємо самі робити те, чого вимагаємо від президента…','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5912,200,dumud.jpg\" />Михайло Димид, доктор канонічного права і богослов’я, – про зміни в державі, очікування від президента, мобілізацію до війська та як зберегти мир у серці</strong></p>\n<p>У суботу, 7 червня, відбудеться інавгурація нового президента України, який одним із першочергових завдань ставить підписання другої частини Угоди про європейську інтеграцію.</p>\n<p>Це осягнення тієї мети, задля якої в листопаді вийшли на протести студенти. Після жорстокого розгону їх підтримали мільйони. Особливе значення в цих акціях протесту мав духовний провід, присутність великої кількості священиків.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2199:majemo-sami-robyty-te-chogo-vymagajemo-vid-prezydenta&catid=28:tema-nomera-cat&Itemid=2',4433,NULL,1,26),
(4432,'P','2014-06-04',1,NULL,'Rss','Побожність — це Дар: думка на прощання з комунізмом','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5912,200,d7342d3c5ea716a8ba392bbfb4a3c66f.jpg\" />Дар побожності дозволяє нам проливати на інших любов, яку ми пізнаємо зі стосунків із Богом. Про цей дар Святого Духа говорив Папа Франциск під час катехези 4&nbsp;червня 2014&nbsp;р., коли Польща святкувала 25-річчя падіння комунізму.</strong><o:p></o:p></p>\n<p>«Нехай зійде Дух Твій і відновить лице землі&nbsp;— ЦІЄЇ землі!»&nbsp;— волав святий Папа, коли поляки ще не вірили, що тоталітарна система обвалиться, немов картковий будиночок. Святий Дух зійшов, аби перемінити реальність, яка придушувала людський дух, але чи люди перестали боятися?</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2198:pobozhnist--ce-dar-dumka-na-proshchannja-z-komunizmom&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4434,NULL,1,26),
(4433,'P','2014-06-08',1,NULL,'Rss','«Нам потрібно оживлення нашої соборної України, миру, злагоди і єдності в нашому народі»','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5912,200,dsc_0025_60c78.jpg\" />Ми сьогодні прикликаємо Духа Святого на наш народ, на нашу землю, на нашу Церкву, щоб Він обновив лице нашої землі, потішив нас у нашому неспокої, примирив нас і зібрав у соборності, яка від Нього походить. Про це заявив Глава УГКЦ Блаженніший Святослав під час Літургії в Патріаршому соборі Воскресіння Христового.</strong></p>\n<p>Предстоятель УГКЦ привітав усіх парафіян зі святом П\'ятдесятниці, «святом сходження на нас, на Церкву Духа Святого, - святом, коли ми прославляємо єдиного Бога в Тройці, Отця, і Сина, і Святого Духа».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2200:2014-06-11-05-06-08&catid=27:gk-v-ukrajini-cat&Itemid=57',4435,NULL,1,26),
(4434,'P','2014-06-10',1,NULL,'Rss','«Увесь світ переконався, що Україна є домом для численних народностей та релігій, які мирно співіснують», − єпископи УГКЦ','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5912,200,postijnyj_synod.jpg\" />Ми надзвичайно вдячні за ваші старання інформувати свої спільноти про правдиву природу змін, що відбуваються в нашій країні, на противагу обманній інформації, що спрямована проти України та її Церков, зокрема проти Української Греко-Католицької Церкви.</strong></p>\n<p>Такими словами члени Постійного Синоду УГКЦ&nbsp;<a href=\"http://news.ugcc.org.ua/go/aHR0cDovL25ld3MudWdjYy5vcmcudWEvYXJ0aWNsZXMvdWtyYWluYV92X3BhbG9tbml0c3R2JUQxJTk2X19keWFrdWllbW9fc2hjaG9fdmlfcmF6b21fJUQxJTk2el9uYW1pXzcwNjk3Lmh0bWw=\" target=\"_blank\">у своєму листі</a> висловили вдячність Католицьким Єпископським Конференціям, священикам, монашеству та мирянам Церкви, усім християнам і людям доброї волі за їх солідарність з Україною упродовж подій, які відбулися в останні місяці.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2201:2014-06-11-05-39-51&catid=27:gk-v-ukrajini-cat&Itemid=57',4436,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4435,'P','2014-06-09',1,NULL,'Rss','«Примирення»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5912,200,Article.jpg\" />Міжконфесійна та&nbsp;міжнародна група&nbsp;«Примирення», до&nbsp;складу якої входять Українська Православна Церква&nbsp;(МП), Українська Греко-Католицька Церква, Римо-Католицька Церква в&nbsp;Україні та&nbsp;Німецька Євангелічно-Лютеранська Церква України, 6 червня заявила про&nbsp;готовність сприяти порозумінню між громадянами України та&nbsp;служити справі загального примирення.</p>\n<p>Про це&nbsp;йдеться в&nbsp;комюніке учасників групи, яке&nbsp;від її імені оприлюднив на&nbsp;прес-конференції в&nbsp;Укрінформі на&nbsp;тему:&nbsp;«Церкви України про&nbsp;„Шлях до&nbsp;майбутнього через примирення“ голова інформаційно-аналітичної служби Київської митрополії УПЦ&nbsp;МП&nbsp;Володимир Бурега.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2202:2014-06-11-05-45-52&catid=37:spilnota-cat&Itemid=49',4437,NULL,1,26),
(4436,'P','2014-06-11',1,NULL,'Rss','Селянин (11 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5919,200,6-600x359.jpg\" />Одного разу, коли вже почало смеркатися, селянин сидів на порозі своєї бідної оселі, втішаючись прохолодою. Поряд пролягла дорога, що вела у село. Нею ішов чоловік. Подорожній, побачивши старого, подумав: «Цей чоловік лінивий, він не працює і весь день сидить на порозі свого дому». Трохи згодом з’явився інший мандрівник. Він подумав: «Цей чоловік – дон Жуан. Сидить тут, щоби підглядати за дівчатами, і може навіть допікає їм». Врешті-решт один лісник, що йшов у село, подумав: «Цей чоловік – великий трудівник. Він напружено працював весь день і заслужив на відпочинок.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2203:seljanyn-11-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4438,NULL,1,26),
(4437,'P','2014-06-11',1,NULL,'Rss','Блаженніший Любомир: «Моральне життя суспільства – головна проблема нашої країни сьогодні»','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5919,200,huzar_velykden14.jpg\" />У наші часи цілій державі, цілому народу випала нагода діяти задля власного блага. І відтепер людина просто зобов’язана бути творчою, патріотичною, свідомим християнином, громадянином своєї держави.</strong></p>\n<p>Про це сказав Архиєпископ-емерит Блаженніший Любомир (Гузар)&nbsp;<a href=\"http://news.ugcc.org.ua/go/aHR0cDovL25ld3MudWdjYy5vcmcudWEvaW50ZXJ2aWV3L3ByaWNoaW5pX3RhX25hc2wlRDElOTZka2lfdWtyYWluc2tpeV9rb250ZWtzdF83MDcwNi5odG1s\" target=\"_blank\">в інтерв’ю двотижневику «Католицький Вісник»</a>.</p>\n<p>На переконання Блаженнішого Любомира, покликання вільної людини – чинити добро. «Ми люди, і ми вільні. А це значить, що ми маємо право робити добро. Звичайно, ми можемо помилятися, але основний сенс свободи – людина не має права чинити зло. Натомість незалежність – це свобода від зовнішніх впливів. Незалежність – це не сваволя», – зазначив Архиєпископ-емерит.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2204:2014-06-11-20-20-30&catid=28:tema-nomera-cat&Itemid=2',4439,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4438,'P','2014-06-11',1,NULL,'Rss','Притча про сварки (12 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5919,200,vvukr1-572x462.jpg\" />Одного разу учитель запитав своїх учнів:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чому люди кричать, коли сваряться?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тому що втрачають спокій, - сказав один.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Але навіщо кричати, якщо друга людина поряд з тобою? – спитав учитель. – Хіба не можна з нею розмовляти тихо? Навіщо кричати, коли ти сердишся?</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2205:prytcha-pro-svarky-12-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4440,NULL,1,26),
(4439,'P','2014-06-10',1,NULL,'Note','ЗВІТ №5 про розподілення благодійних пожертвувань парафіян церкви св.Миколая на Аскольдовій могилі (16.04.2014 р.)','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Служба милосердя при церкві св.Миколая на Аскольдовій могилі, зібравши пожертвувані парафіянами: одяг чоловічий, жіночий та дитячий, взуття, іграшки дитячі та інші речі особистого вжитку, зокрема 7 великих коробок і 7 мішків одягу, взуття, іграшок, техніки від п.Богдана Орловського</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Служба милосердя при церкві св.Миколая на Аскольдовій могилі, зібравши пожертвувані парафіянами: одяг чоловічий, жіночий та дитячий, взуття, іграшки дитячі та інші речі особистого вжитку, зокрема 7 великих коробок і 7 мішків одягу, взуття, іграшок, техніки від п.Богдана Орловського, розподілила їх таким чином:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash; уділила потребуючим мирянам;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash; відправила гуманітарний вантаж Благодійному фонду &ldquo;Сузір\'я&rdquo; в с.Іванкові Бориспільського р-ну Київської обл.:</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">одяг чоловічий, жіночий, дитячий &ndash; 6 коробок + 7 мішків</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">взуття &ndash; 2 мішки</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">іграшки дитячі &ndash; 1 мішок</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Робота виконана силами волонтерів: п.Михайла, п.Сергія, п.Людмили.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Висловлюємо щиру подяку жертводавцям і прикликаємо Боже благословення на їхніх родини.</span></p>',NULL,NULL,4441,5,1,19),
(4440,'P','2014-06-12',1,NULL,'Rss','Живий екуменічний діалог','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5919,200,19said.jpg\" />\"УКРАЇНЦІ ЗРОЗУМІЛИ, ЩО МУСУЛЬМАНИ ТА ЮДЕЇ - НЕ ВОРОГИ, А БРАТИ, ГОТОВІ РАЗОМ ІЗ НИМИ ЗАХИЩАТИ ЦЮ ЗЕМЛЮ\", - ШЕЙХ САЇД ІСМАГІЛОВ, МУФТІЙ ДУХОВНОГО УПРАВЛІННЯ МУСУЛЬМАН УКРАЇНИ \"УММА\"</p>\n<p>Про міжрелігійний екуменічний діалог, досягнення компромісів між мусульманами та християнами і про мирне майбутнє України поспілкувалися із шейхом Саїдом Ісмагіловим, муфтієм Духовного управління мусульман України \"Умма\". Мусульманський провідник зазначив, що в Україні мусульмани мають добрі стосунки із християнами. Крім того, зауважив, що мусульмани в ці трагічні моменти нашої історії підтримують всі демократичні зміни в країні та прагнення нашої держави до свободи.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2206:zhyvy-ekumenichny-dialog&catid=27:gk-v-ukrajini-cat&Itemid=57',4444,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4441,'P','2014-06-12',1,NULL,'Rss','Старосвітські етикетні норми поведінки','<p><img src=\"http://askoldova-mohyla.org/uk/image,5927,200,rty.jpg\" style=\"margin: 10px; float: left;\" />Будь-яка етнічна культура має ключове поняття. У давніх українців воно асоціювалося з порядком, ладом (семантика «ладу», до речі, широка: це і порядок, і злагода, і влада, і устрій), яким, за народними віруваннями, керують божества: Ладо із Ладою та Доля. Саме вони приводять у порядок строкате й багатошарове суспільство, спонукуючи всіх підкорятися главі,- молодших — старшим, дітей — батькам і т. д. Причому в основі ієрархічного підкорення була не стільки сила, скільки шана: старшого, а тим більше главу, поважали, і передусім тому, що ототожнювали його з божеством або такою особою, що має якості божества.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2207:starosvitski-etyketni-normy-povedinky&catid=37:spilnota-cat&Itemid=49',4445,NULL,1,26),
(4442,'P','2014-06-12',1,NULL,'Rss','ЕКЗАРХ І ЕКЗАРХІЯ У ЦЕРКОВНОМУ ПРАВІ','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5928,200,1740.jpg\" />Вступ</strong></p>\n<p>Спостерігаючи за динамічним розвитком УГКЦ (в Україні так і на поселеннях)[1], із певністю можна ствердити, що цей процес є наслідком постійного і послідовного виконання&nbsp; головного завдання Церкви, а саме спасіння людської душі, яке відбувається через проповідування Доброї Новини і єднання людей із Ісусом Христом. В свою чергу така діяльність Церкви потребує зорганізованості церковного життя, яке згідно з церковним правом, яким користуються Східні Католицькі Церкви, може виражатись у двох її основних вимірах: єпархія і екзархія[2].</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2208:ekzarh-i-ekzarhija-u-cerkovnomu-pravi&catid=27:gk-v-ukrajini-cat&Itemid=57',4446,NULL,1,26),
(4443,'P','2014-06-12',1,NULL,'Rss','Причини падінь (13 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5929,200,GRIHOPADINNYA-300x197.jpg\" />Учитель вів свого учня через джунглі. Хоча учитель був зовсім старий, проте йшов швидко, а його молодий учень час від часу падав. Молодик після кожного падіння піднімався і, лаючи зрадницьку землю, плював на те місце та йшов далі.</p>\n<p>Після довгої дороги вони дійшли до священного місця. Не спиняючись, учитель повернув назад.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Сьогодні ви не навчили мене нічого, - оголосив учень після чергового падіння.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2209:prychyny-padin-13-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4447,NULL,1,26),
(4444,'P','2014-06-12',1,NULL,'Rss','Життя УГКЦ в Криму','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5929,200,2199.jpg\" />Більше двох місяців минуло після так званого «всекримського референдуму», за підсумками якого Росія «внесла» півострів до свого складу. Після анексії Росією Криму, яку засудили майже всі країни світу, окрім Сирії, Північної Кореї та Уганди, українці та кримські татари опинилась в складній ситуації. Очевидним є те, що утисків зазнають українці і татари у Криму, а не росіяни в Україні. В окупованому Криму під великою загрозою опинилися й українські Церкви, які ніколи не толерувала Росія. Українські Церкви, які й без того не мали в Криму сприятливих обставин для розвитку, тепер зазнають потужного тиску. Наразі найбільше постраждала Українська греко-католицька церква.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2210:zhyttja-ugkc-v-krymu&catid=27:gk-v-ukrajini-cat&Itemid=57',4448,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4445,'P','2014-06-13',1,NULL,'Rss','Книга священика (14 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5931,200,img10-03.jpg\" />Священик переписав на прекрасному пергаменті, який коштував вісімнадцять золотих монет, увесь Старий і Новий Заповіт. Одного разу один брат прийшов до нього і, побачивши книгу, тайкома забрав її. Того самого дня священик заходів прочитати книгу. Побачивши, що вона зникла, він зрозумів, що її взяв брат, але не послав спитати його, остерігаючись, аби брат не додав до гріха крадіжки ще й брехню.</p>\n<p>Тим часом брат вирушив у сусіднє місто, щоб продати книгу, і попросив за неї шістнадцять золотих монет. Покупець сказав йому:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2211:knyga-svjashchenyka-14-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4449,NULL,1,26),
(4446,'P','2014-06-13',1,NULL,'Rss','Канонічна постать на екрані','<p><strong><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5931,200,5.jpg\" />Актриса Олівія Хассі розповідає про головну роль у стрічці «Мати Тереза»: Я&nbsp;завжди хотіла зіграти її. Вона була однією з тих святих, кого я безумовно поважаю!</em></strong></p>\n<p>Актриса Олівія Хассі протягом зйомок фільму «Мати Тереза» сильно захворіла і навіть могла відмовитися від цієї роботи. У&nbsp;напружених моментах вона відчувала, що їй бракує сил і здоров’я продовжувати. Але одного дня Олівія сіла у своє крісло на знімальному майданчику, заплющила очі й попросила Господа і Мати Терезу про допомогу. Несподівано її виповнив глибокий мир. Коли «яскраве світло» осяяло думки, актриса закінчила молитись і відчула, що її огортає незбагненний спокій. Це допомогло їй повірити, що вона зможе отримати цю благодать&nbsp;— здолати кожен напружений день зйомок у Шрі-Ланці та Італії.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2212:kanonichna-postat-na-ekrani&catid=37:spilnota-cat&Itemid=49',4450,NULL,1,26),
(4447,'P','2014-06-14',1,NULL,'Rss','Неділя всіх святих','<p><span style=\"font-size: 0.875em;\"><img src=\"http://askoldova-mohyla.org/uk/image,5934,200,vsih_svyatyh.jpg\" style=\"margin: 10px; float: right;\" /></span></p>\n<p>\"Величаємо вас, апостоли, мученики, пророки і всі святі, і почитаємо святу пам\'ять вашу, ви бо молите за нас Христа Бога нашого\"<o:p></o:p></p>\n<p>Величання на неділю Всіх святих<o:p></o:p></p>\n<p>Восьма неділя після празника Пасхи, а перша після Зіслання Святого Духа називається неділею Всіх святих. Вона завершує коло рухомих свят. Цього дня наша Східна Церква віддає особливу почесть усім тим, які є плодом дарів Святого Духа. \"У цей день, в неділю після П\'ятдесятниці, — читаємо в синак­сарі цієї неділі, — святкуємо празник Всіх святих скрізь — в Азії, Лівії, Європі, на півночі й півдні. Цей празник наші божественні Отці установили й наказали святкувати після Зіслання Святого Духа, наче показуючи нам взір, як прихід всесвятого Духа вплинув на апостолів. Він освятив і зробив премудрими тих, що з нашої природи, щоб їх поставити на місце грішних ангелів, і Христові Богові привів: одних через муки і кров, инших через постійність у чеснотливому житті\".<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2213:nedilja-vsih-svjatyh&catid=28:tema-nomera-cat&Itemid=2',4451,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4448,'P','2014-06-15',1,NULL,'Rss','Зцілення (16 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5934,200,345.jpg\" />Одна жінка страждала від жахливого болю, і жодне лікування не приносило полегші. Чоловік посадив її на коня і повіз у монастир, що прославився цілющою водою.</p>\n<p>Того дня йшов сильний дощ і розмив дорогу, отож подорожні прибули до монастиря, коли споночіло. Заспаний і сердитий сторож не відчинив їм браму, але мандрівці вмовили його дати їм свяченої води. Роздратований такою настирливістю, сторож наповнив глечика з найближчої калюжі.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2215:zcilennja-16-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4453,NULL,1,26),
(4449,'P','2014-06-15',1,NULL,'Rss','Щоб подолати комуністичний спадок, що процвітає у формі путінізму, потрібна солідарність усього світу','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5934,200,medal.jpg\" /></strong>«Щоб подолати нинішню загрозу нашим цінностям, ми маємо змінити перспективу й виробити солідарну поставу щодо цінностей і принципів, які лежать в основі демократичної системи і які ми не можемо принести в жертву. Це було нашим словом до світу в той час, коли Мустафа і я були ув’язнені в таборі, оточеному сімома рядами колючого дроту», – зазначив проректор УКУ, Мирослав Маринович. 12 червня йому та духовному лідеру кримськотатарського народу Мустафі Джемілєву вручили медаль Свободи ім. Трумена-Рейґана.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2216:shchob-podolaty-komunistychny-spadok-shcho-procvitaje-u-formi-putinizmu-potribna-solidarnist-usogo-svitu&catid=37:spilnota-cat&Itemid=49',4454,NULL,1,26),
(4450,'P','2014-06-16',0,NULL,'Note','','<p style=\"text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Молитва св.&nbsp;</strong><strong>Папи Івана Павла ІІ за Україну&nbsp;</strong><strong>в церкві св. Миколая на Аскольдовій Могилі</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&laquo;О Всеблагая Діво Маріє, Зарваницька Богородице, дякую Тобі за ласку перебування на київській землі, з якої світло Євангелія поширилося по всій Україні. Тобі, Мати Божа і Мати Церкви, перед Твоєю чудотворною іконою віддаю у руки мою апостольську подорож в Україну.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Пресвята Мати Божа, покрий Твоїм Материнським Покровом усіх християн, чоловіків та жінок доброї волі, які живуть у цій великій країні. Провадь їх до Свого Сина Ісуса, Який для всіх є Дорогою, Правдою і Життям. Амінь&raquo;.</span></p>','',NULL,NULL,4456,5,1,50),
(4451,'P','2014-06-17',1,NULL,'Rss','Як страх шкодить можливостям (17 червня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,5939,200,prayer01.jpg\" style=\"margin: 10px; float: left;\" />Є давня історія про чоловіка, якого якось уночі відвідав ангел і розповів йому про великі діла, що чекають на нього: у нього буде нагода здобути величезне багатство, заслужити гідне становище в суспільстві й одружитися з прекрасною жінкою.</p>\n<p>Усе життя чоловік чекав обіцяних чудес, але нічого не сталося, і врешті-решт він помер у самотності й злиднях. Дійшовши до воріт раю, він побачив ангела, який відвідав його багато років тому, і промовив до нього:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2217:jak-strah-shkodyt-mozhlyvostjam-17-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4457,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4452,'P','2014-06-16',1,NULL,'Note','СБУ оприлюднило \"кримінал\" проти святих УГКЦ','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5938,200,27d40d3-2.jpg\" alt=\"\" width=\"200\" height=\"126\" />Як запевняє директор Галузевого державного архіву Служби безпеки України&nbsp;<strong>Ігор Кулик</strong>, до документів радянського періоду стосовно репресій по відношенню до священнослужителів буде надано вільний доступ та здійснено максимальне сприяння у роботі. &laquo;Архів СБУ сприяє беатифікації та канонізації українських святих. Ми готові до співпраці для виявлення, копіювання та передачі УГКЦ всього масиву архівних матеріалів, що зберігається у ГДА СБУ та стосується репресій проти церкви&raquo;, &ndash; зазначив Кулик.&nbsp;</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,5938,200,27d40d3-2.jpg\" alt=\"\" width=\"200\" height=\"126\" />Керівництво архіву Служби безпеки України передало УГКЦ копії архівних кримінальних справ стосовно блаженного священномученика о. Климентія Шептицького та о. Омеляна Горчинського, що нині проходить процес беатифікації. Раніше вільного доступу до цих документів не було.</strong></span><br /><br /><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Передача копій документів відбулась в рамках навчально-практичного семінару &laquo;Мученики &ndash; джерело свободи церкви&raquo;, що пройшов у Василіанському монастирі у смт. Брюховичі, що поблизу Львова. Представник Конгрегації в справах святих монсеньйор<strong>&nbsp;о. Роберт Сорно&nbsp;</strong>(Рим, Італія) висловив вдячність керівництву Архіву та Служби загалом за передані матеріали, а також надію на майбутню співпрацю.&nbsp;</span><br /><br /><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Як запевняє директор Галузевого державного архіву Служби безпеки України&nbsp;<strong>Ігор Кулик</strong>, до документів радянського періоду стосовно репресій по відношенню до священнослужителів буде надано вільний доступ та здійснено максимальне сприяння у роботі. &laquo;Архів СБУ сприяє беатифікації та канонізації українських святих. Ми готові до співпраці для виявлення, копіювання та передачі УГКЦ всього масиву архівних матеріалів, що зберігається у ГДА СБУ та стосується репресій проти церкви&raquo;, &ndash; зазначив Кулик.&nbsp;</span><br /><br /><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,5938,200,383387232.jpg\" alt=\"\" width=\"200\" height=\"128\" />Як відомо, у 2001 році були визнано священомучениками 26 осіб, які постраждали в часи комуністичного переслідування в Україні між 1935&mdash;1973 рр., та загинули як мученики за віру.&nbsp;</span><br /><br /><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Нагадаємо, що цього року відновлено вільний доступ до розсекречених у 2008&mdash;2010 роках документів колишньої радянської спецслужби &mdash; ЧК-НКВД-КҐБ, які зберігаються в Архіві СБУ. Сталий доступ до цих матеріалів повинен бути гарантований кожному на виконання Законів України та Рекомендації № R (2000) 13 Комітету міністрів Ради Європи країнам-членам стосовно європейської політики доступу до архівів, яка як невід&rsquo;ємну ознаку демократії передбачає обов&rsquo;язкову можливість дізнатися об&rsquo;єктивно про елементи своєї історії. Для цього в Україні започатковано процес виведення історичних архівів з-під відання сучасних спецслужб. Ініціатори переконані, що такий крок не лише допоможе глибше дізнатися про минуле країни, але й буде однією з гарантій неповернення тоталітарних практик у роботі правоохоронних органів та спеціальних служб незалежної України.</span></p>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">http://pravda.if.ua/</span></div>',NULL,NULL,4455,5,1,30);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4453,'P','2014-06-17',1,NULL,'Rss','Уроки Майдану','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5939,200,4773.jpg\" />Інавгурація Петра Порошенка, нового Президента України, що відбулася 7 червня, ніби спустила завісу після першого акту жорстокої драми самовідновлення України. Кривавий Майдан досі перед очима у багатьох, та й Україні ніхто не пророкує спокійного майбутнього.</strong></p>\n<p>Дехто вбачає розвиток в державі «чеченського сценарію», і як наслідок — громадянська війна, що раз у раз переходить у масові жорстокі сутички. Інші вважають, що Владімір Путін хоче для України іншого сценарію, «придністровського», який передбачає повільне поглинання окремих її частин та чіткий контроль Москви. Перед новообраним президентом стоять неабиякі виклики: створити уряд, примусити розвиватись зруйновану економіку, встановити мир на територіях, де, завдячуючи озброєним Росією «сепаратистам», не діють жодні закони.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2218:uroky-madanu&catid=28:tema-nomera-cat&Itemid=2',4458,NULL,1,26),
(4454,'P','2014-06-17',1,NULL,'Rss','Чому кричить дядько (18 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5942,200,11101602.jpg\" />-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Мамо, чому отой дядько кричить?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Він розстроєний.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А чому він розстроєний?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Хтось образив його.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А чому той хтось його образив?</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2219:chomu-krychyt-djadko-18-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4460,NULL,1,26),
(4455,'P','2014-06-17',1,NULL,'Rss','Заклик Синоду Єпископів продовжувати безперервну молитву і піст за мир в Україні','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,5942,200,10259743_759766060733074_5209521646914714959_n.jpg\" style=\"margin: 10px; float: right;\" />Дорогі в Христі! Упродовж останніх місяців Українська Держава та наш народ переживають чи не найважчий період свого буття в новітній історії. Ми всі є свідками, а багато з нас і учасниками швидких суспільних перемін, що часто супроводжуються драматичними чи навіть трагічними подіями.</strong></p>\n<p>Однак, свідомі того, що немає нічого випадкового, за всім стоїть Господь, ми відчуваємо, що ці переміни сповнені надії, бо є провісниками народження, хоч і болісного, нової України. Поступово вмирають залишки радянського способу життя та приходить усвідомлення дійсної незалежності нашої країни. Перед обличчям новітніх небезпек народ України єднається у своїй готовності захищати Вітчизну, відстоювати її соборність та право кожного громадянина жити у вільній, демократичній та європейській державі.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2220:zaklyk-synodu-jepyskopiv-prodovzhuvaty-bezperervnu-molytvu-i-pist-za-myr-v-ukrajini&catid=27:gk-v-ukrajini-cat&Itemid=57',4461,NULL,1,26),
(4456,'P','2014-06-18',1,NULL,'Rss',' Борис Ґудзяк: здобути власну гідність','<p><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5944,200,Gudzak-5.jpg\" />Нам хочеться почати цю історію з прохання. Будь ласка, закрийте очі на кілька секунд. Тепер уявіть пустирище на околиці міста. А на ньому — закинутий, давно нікому не потрібний дитячий садочок «на чотири групи». Уявили? Приблизно те саме побачив молодий науковець і випускник семінарії Борис Ґудзяк, коли приїхав до Львова на початку 90-их. І саме із закинутого приміщення, давно нікому не потрібного дитячого садочка почалася історія відновлення одного з найкращих в нашій державі гуманітарних вузів — Українського Католицького Університету.</em></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2221:-borys-gudzjak-zdobuty-vlasnu-gidnist&catid=28:tema-nomera-cat&Itemid=2',4462,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4457,'P','2014-06-17',1,NULL,'Note','Чи відають вони, що роблять?','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,5942,200,42adf2e-vandaly2.jpg.pagespeed.ce.P6P9CYgv4c.jpg\" alt=\"\" width=\"122\" height=\"200\" />Вже не вперше наша церковця потерпає від рук нечестивих. То ходять з погрозливими маршами, то б&rsquo;ють пароха, то пиляють та намагаються спалити хрест, то забороняють облагороджувати територію, то не дозволяють відновлювати Аскольдів некрополь, то ще якась біда. Ось тепер взялись до наруги над пам&rsquo;ятним хрестом Героям Крут та барельєфом Слави Стецько.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Таки ми славний і сильний народ, коли наші спочилі герої наводять такий страх на наших ворогів. Чи то агоністичні прояви &laquo;руссссььького міру&raquo; перед смертю, чи то український націоналізм стоїть &laquo;їм&raquo; кісткою в горлі, чи то так бояться нашої дідівської віри, яка не кориться нікому крім Бога. Але факт залишається фактом.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,5942,200,42adf2e-vandaly2.jpg.pagespeed.ce.P6P9CYgv4c.jpg\" alt=\"\" width=\"122\" height=\"200\" />Вже не вперше наша церковця потерпає від рук нечестивих. То ходять з погрозливими маршами, то б&rsquo;ють пароха, то пиляють та намагаються спалити хрест, то забороняють облагороджувати територію, то не дозволяють відновлювати Аскольдів некрополь, то ще якась біда. Ось тепер взялись до наруги над пам&rsquo;ятним хрестом Героям Крут та барельєфом Слави Стецько.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5942,200,555552a-vandaly1.jpg.pagespeed.ce.LtgSRNUC6K.jpg\" alt=\"\" width=\"133\" height=\"200\" />Таки ми славний і сильний народ, коли наші спочилі герої наводять такий страх на наших ворогів. Чи то агоністичні прояви &laquo;руссссььького міру&raquo; перед смертю, чи то український націоналізм стоїть &laquo;їм&raquo; кісткою в горлі, чи то так бояться нашої дідівської віри, яка не кориться нікому крім Бога.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Але факт залишається фактом.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Барельєф відреставруємо, хрест від фарби відчистимо і кожен заплатить перед Богом свою ціну, за свої вчинки. Бо &laquo;вони&raquo; явно відали що робили.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Любов Михайлюк, &laquo;Оратнта&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Фото: Владислава Синявського</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><a href=\"http://www.5.ua/kijiv/item/386613-vandaly-u-kyievi-vymazaly-farboiu-pam-iatnyk-na-mohyli-heroiam-krut\">http://www.5.ua/kijiv/item/386613-vandaly-u-kyievi-vymazaly-farboiu-pam-iatnyk-na-mohyli-heroiam-krut</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><a href=\"http://www.istpravda.com.ua/short/2014/06/17/143268/\">http://www.istpravda.com.ua/short/2014/06/17/143268/</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><a href=\"http://www.newsru.ua/ukraine/17jun2014/pamiatnik_kraska.html\">http://www.newsru.ua/ukraine/17jun2014/pamiatnik_kraska.html</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><a href=\"http://kyrios.org.ua/news/world/12847-vandali-poshkodili-hrest-na-mogili-geroyiv-krut-u-kievi.html\">http://kyrios.org.ua/news/world/12847-vandali-poshkodili-hrest-na-mogili-geroyiv-krut-u-kievi.html</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><a href=\"http://risu.org.ua/ua/index/all_news/community/vandalism/56761/\">http://risu.org.ua/ua/index/all_news/community/vandalism/56761/</a></span></p>',NULL,NULL,4459,5,1,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4458,'P','2014-06-18',1,NULL,'Rss','«Моє бажання служити йде з середини»: Папа Франциск','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5945,200,papa4.jpeg\" />Папа Франциск прийняв Енріке Цимермана у Ватикані минулого понеділка &nbsp;– через день після молитви за мир з президентами Ізраїля та Палестини – для того, щоб дати це ексклюзивне інтерв’ю для&nbsp;<em>La Vanguardia</em>. Папа був задоволений, бо зробив все можливе для порозуміння між ізраїльтянами та палестинцями.</strong></p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Насильство в ім’я Боже панує на Середньому Сході.</p>\n<p>Це – протиріччя. Насильство в ім’я Боже не відповідає нашому часу. Це щось з давніх часів. Протягом історії ми, християни, іноді його практикували. Коли я думаю про Тридцятирічну війну – це було насильство в м.’я Боже. Сьогодні цього неможна собі навіть уявити, правда? Іноді з причини релігії у нас ставалися дуже серйозні протиріччя, дуже важкі. Фундаменталізм, наприклад. У наших трьох релігіях ми маємо групи фундаменталістів, маленькі, порівняно з усім цілим.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2222:2014-06-18-18-05-19&catid=35:istorychni-postati-cat&Itemid=48',4463,NULL,1,26),
(4459,'P','2014-06-18',1,NULL,'Rss','Ісус не потрібен (19 червня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,5945,200,4664.jpg\" style=\"margin: 10px; float: left;\" />Одного разу в монастирі один чернець скривдив іншого. Настоятель попросив ображеного ченця пробачити кривднику.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Цього я не можу зробити, - відповів чернець. – Це він скривдив мене і мусить бути покараний.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; У цей дуже важливий момент&nbsp; брат-настоятель підніс руку до неба і почав молитися.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2223:isus-ne-potriben-19-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4464,NULL,1,26),
(4460,'P','2014-06-19',1,NULL,'Rss','Празник Пресвятої Євхаристії','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5947,200,24443.jpg\" />«Хто їсть моє Тіло і п\'є мою Кров, той має життя вічне, і я воскрешу його останнього дня», (Ів. 6, 54).</p>\n<p>Свята Літургія, як Безкровна Жертва, це осередок культу й духовного життя Христової Церкви. А це тому, що на Святій Літургії здійснюється Тайна понад усі Тайни — Пресвята Тайна Євхаристії. Другий Ватиканський Собор в догматичній конституції про Церкву називає Євхаристійну Жертву «джерелом та звершенням цілого християнського життя» (§11). А в декреті про Пресвітерів сказано: «Бо в Святій Євхаристії поєднується усе духовне добро Церкви, тобто сам Христос» (§5).</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2224:praznyk-presvjatoji-jevharystiji&catid=28:tema-nomera-cat&Itemid=2',4465,NULL,1,26),
(4461,'P','2014-06-19',1,NULL,'Rss','Усе лихо – від гордости (20 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5947,200,images.jpg\" />Прочитав чоловік одну мудру книгу й заради Істини вирішив покинути і свою похилу хатину, і бідне містечко, і мізерну платню. Хоча дуже шкода було все покидати.</p>\n<p>Й от опинився він у велетенському прекрасному домі, навколо якого, куди оком не кинь, стелилися пречудові сади. Там жили такі самі, як він, люди, що покинули усе в цьому світі. Порівняв він колишнє своє життя з новим, і вельми сподобалося йому жити добре.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2225:use-lyho--vid-gordosty-20-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4466,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4462,'P','2014-06-20',1,NULL,'Rss','«Ми не ваші вороги, ми вам – брати у Христі»','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,5949,200,shevchuk_seminarija__16_.jpg\" style=\"margin: 10px; float: right;\" />Не вірте пропаганді! Ми не ваші вороги, ми вам брати у Христі! Ми прагнемо бути добрими сусідами, мати дружні відносини і тісну співпрацю з вами.</strong></p>\n<p>Такими словами звертається Отець і Глава УГКЦ Блаженніший Святослав до російських братів-православних. Про це йдеться&nbsp;<a target=\"_blank\" href=\"http://news.ugcc.org.ua/go/aHR0cDovL25ld3MudWdjYy5vcmcudWEvZW4vYXJ0aWNsZXMva3lpdl9ncmVla19jYXRob2xpY19hcmNoYmlzaG9wX3RlbGxzX29ydGhvZG94X2Jyb3RoZXJzX25vdF90b19iZWxpZXZlX3Byb3BhZ2FuZGFfbG9va19hdF90aGVfdHJ1dGhfNzA4MjAuaHRtbA==\">в матеріалі інформаційного агентства AsiaNews.it</a>, який написаний на основі телефонної розмови з Предстоятелем УГКЦ.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2226:2014-06-20-11-15-27&catid=28:tema-nomera-cat&Itemid=2',4467,NULL,1,26),
(4463,'P','2014-06-20',1,NULL,'Note','Урочисте відзначення канонізації Папи Івана Павла II та річниці його візиту в Україну','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>12:30</strong>&nbsp;- Свята Літургія.&nbsp;<strong>Спільна молитва&nbsp;</strong>св. Папи Іван-Павла ІІ, яку він промовив у нашій Церкві 23 липня 2001 р. Б. під час свого візиту в Україну.&nbsp;<strong>Конкурс: квіти для св. Івана-Павла II</strong>&nbsp;(букети квітів підписувати для визначення і нагороди переможців конкурсу).&nbsp;<strong>Виступ скрипалів</strong>&nbsp;Орести та Антоніни Крис.&nbsp;<strong>Флеш-моб</strong>&nbsp;від молоді парафії &ndash; читання промов св. Папи Івана Павла ІІ під час візиту в Україну 2001 р.Б.</span></p>','<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5951,200,poupe_II.jpg\" alt=\"\" width=\"125\" height=\"200\" />Парафіяльні зустрічі пам&rsquo;яті св. Папи Івана Павла ІІ</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>22 червня 2014 р. Б. неділя</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>08:00</strong> &ndash; Свята Літургія. Спільна молитва св. Папи Іван-Павла ІІ, яку він промовив у нашій Церкві 23 липня 2001 р. Б. під час свого візиту в Україну. Відкриття банерної експозиції Інституту історії Церкви УКУ <strong>&laquo;Блаженний Папа Іван Павло ІІ та Україна&raquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>10:00</strong> &ndash; Свята Літургія. Спільна молитва св. Папи Іван-Павла ІІ, яку він промовив у нашій Церкві 23 липня 2001 р. Б. під час свого візиту в Україну. Лекція: <strong>&laquo;Постать святого Івана Павла ІІ: українська перспектива&raquo; д</strong>оповідач &ndash; д-р Олег Турій, проректор з наукової роботи УКУ (Львів). Презентація книг-публікацій Видавництва УКУ: Джордж Вайґель - &laquo;<strong>Свідок надії. Життєпис Папи Івана Павла ІІ&raquo; </strong>та Джордж Вайґель - <strong>&laquo;Кінець і початок. Папа Іван Павло ІІ: перемога свободи, останні роки, спадщина&raquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>12:30</strong> - Свята Літургія. <strong>Спільна молитва </strong>св. Папи Іван-Павла ІІ, яку він промовив у нашій Церкві 23 липня 2001 р. Б. під час свого візиту в Україну. <strong>Конкурс: квіти для св. Івана-Павла II</strong> (букети квітів підписувати для визначення і нагороди переможців конкурсу). <strong>Виступ скрипалів</strong> Орести та Антоніни Крис. <strong>Флеш-моб</strong> від молоді парафії &ndash; читання промов св. Папи Івана Павла ІІ під час візиту в Україну 2001 р.Б.</span></p>',NULL,NULL,4468,5,1,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4464,'P','2014-06-20',1,NULL,'Rss','Небезпечне місце (21 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5951,200,DSCN6894.jpg\" />Залетіло липове зернятко на самісінький край крутого провалля.</p>\n<p>Умовляла його матір. Просили брати і сестри:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Не коренися тут! Дочекайся першого ж пориву вітру і лети якомога далі від цього небезпечного місця!</p>\n<p>Але де там! Зернятко навіть слухати нікого не хотіло.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2227:nebezpechne-misce-21-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4469,NULL,1,26),
(4465,'P','2014-06-20',1,NULL,'Rss','Істинна пожива','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5951,200,papa_monstrancija.jpg\" />«Господь, Бог твій (…) годував тебе манною, якої ти не знав» (Втор 8, 2-3)</p>\n<p>Ці слова Мойсея стосуються історії Ізраїля, якого Бог вивів з Єгипту, з дому неволі і сорок років вів пустинею до Обіцяної Землі. Вибраний народ, дійшовши туди здобув певну автономію, добробут і йому загрожувало забуття про печальні події минулого. Це забуття було подолано завдяки втручанню Бога і Його безмежній доброті. Так Святе Письмо заохочує до пам’яті, до увіковічнення в пам’яті цілої дороги, подоланої в пустині, в голоді і знеохоченні. Мойсей заохочує повернутись до того, що суттєве, до досвіду цілковитої залежності від Бога, коли переживання віддане в Його руки, щоб людина зрозуміла, що «живе не самим лише хлібом, а всім, що походить з уст Божих» (пор. Втор 8,3).</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2228:istynna-pozhyva&catid=32:gk-svit-cat&Itemid=58',4470,NULL,1,26),
(4466,'P','2014-06-22',1,NULL,'Rss','Сонечко і темрява (22 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5953,200,13060501e-sonce.jpg\" />Хлопчик запитав у сонечка:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Сонечко, ти бачило коли-небудь темряву?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ні, - відповіло сонечко, - ніколи не бачило.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Хочеш, я тобі покажу темряву? – сказав хлопчик. – Я знаю один підвал, де завжди дуже темно.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Хочу, - відповіло сонечко, - це цікаво.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2229:sonechko-i-temrjava-22-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4471,NULL,1,26),
(4467,'P','2014-06-22',1,NULL,'Rss','Всіх святих землі Української','<p><img src=\"http://askoldova-mohyla.org/uk/image,5953,200,Vsix_sv_Zem_Ukr-620x250.jpg\" style=\"margin: 10px; float: right;\" />Почитання святих почалося ще з часів перших християн. Приклад мучеників у перших віках дав поштовх до їх почитання. Смерть за Христову віру свідчила про віддану любов до Ісуса Христа та його церкви. Сам Ісус казав: «Це моя заповідь, щоб ви любили один одного, як я вас полюбив! Ніхто неспроможний любити більше, ніж тоді, коли він за своїх друзів своє життя віддає» (Йо 15;12,13).</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2230:vsih-svjatyh-zemli-ukrajinskoji&catid=28:tema-nomera-cat&Itemid=2',4472,NULL,1,26),
(4468,'P','2014-06-22',1,NULL,'Rss','Інструменти диявола (23 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5955,200,39675817405804.jpg\" />Багато років тому диявол вирішив продати всі знаряддя свого ремесла. Він дбайливо повиставляв їх у скляній вітрині на загальний огляд. Що ж то була за колекція! Тут був блискучий кинджал заздрости, а поряд з ним красувався молот гніву. На іншій полиці лежав лук жадібности і пожадання, а поряд з ним – стріли хтивости і ревности. Також там були знаряддя страху, гордині і ненависти. Усі вони були чудово представлені і споряджені етикетками з назвами й ціною. А на найкращій полиці, окремо від усіх інших інструментів, лежав маленький, непоказний і досить поношений маленький клин, на якому висів ярлик: «Зневіра». Як не дивно, ціна цього знаряддя була вищою від усіх решта разом. Один перехожий запитав диявола, чому він так цінує цей дивний клинець, і той відповів:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2231:instrumenty-dyjavola-23-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4473,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4469,'P','2014-06-22',1,NULL,'Rss','Основні напрямки діяльності Лицарів Колумба в Україні','<p align=\"center\"><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5955,200,17-03-14-6.jpg\" />Звіт &nbsp;про реалізовані проекти за період серпень 2013 – травень 2014 рр.</strong></p>\n<p>Лицарі Колумба розпочали свою діяльність в Україні <strong>21 травня 2013 р. </strong>створенням першої в Україні братерської організації – Ради Лицарів Колумба імені Святого рівноапостольного князя &nbsp;Володимира.</p>\n<p>Одним із перших кроків організації було обрання Проводу (<strong>20 вересня 2013 р.</strong>). Великий Лицар – Богдан Ковалів.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2232:osnovni-naprjamky-dijalnosti-lycariv-kolumba-v-ukrajini&catid=27:gk-v-ukrajini-cat&Itemid=57',4474,NULL,1,26),
(4470,'P','2014-06-23',1,NULL,'Rss','«Квіти для Папи»','<p><img src=\"http://askoldova-mohyla.org/uk/image,5957,200,11.jpg\" style=\"margin: 10px; float: left;\" />22 червня 2014 р. в церкві св. Миколая Чудотворця на Аскольдовій могилі відбулася традиційна виставка-конкурс квіткових композицій «Квіти для папи». Така ініціатива настоятеля храму о.Ігоря (Онишкевича) була щиро підтримана вірними на спомин про відвідини святішого отця Івана Павла ІІ України та нашого храму у 2001 р.</p>\n<p>У конкурсі взяло участь 9 квіткових композицій. Богословський, символічний та мистецький&nbsp; рівень представлених композицій оцінювало журі, до складу якого увійшли о.Михайло (Мельник), знана сучасна українська художниця п.&nbsp;Марія Томен та мистецтвознавець Катерина Чаплик.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2233:2014-06-23-11-41-35&catid=25:parafija-cat&Itemid=27',4475,NULL,1,26),
(4471,'P','2014-06-23',1,NULL,'Rss','Що треба знати про Пресвяту Євхаристію','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,5957,200,pritshastie.jpg\" style=\"margin: 10px; float: right;\" />Для чого брати участь у Євхаристії та як гідно підготуватися до Причастя? Хто і як може його приймати? Про євхаристійні приписи Греко-Католицької Церкви розповідає о.Йосафат Бойко ВС. У деяких пунктах традиція Греко- та Римо-Католицьких Церков відрізняються.</strong></p>\n<p>Слово «таїнство» лат. походження&nbsp;<em>«sacramentum»</em> — «таємниця», не вживалось у Новому Завіті, але Церква використовує це слово для означення сили і благодаті, якими Господь обдаровує людину через видимі знаки. Отже, Свята Тайна чи Таїнство —&nbsp;<strong>це видимий знак невидимої Божої ласки.</strong> Таїнства Нового Завіту установлені Христом, і їх є сім:&nbsp;<strong>Хрещення, Миропомазання, Євхаристія, Покаяння, Подружжя, Священство і Єлеопомазання.</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2234:shcho-treba-znaty-pro-presvjatu-jevharystiju&catid=28:tema-nomera-cat&Itemid=2',4476,NULL,1,26),
(4472,'P','2014-06-23',1,NULL,'Rss','Найкраща сіть диявола (24 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5959,200,63_2_b.jpg\" />На зборах у пеклі диявол розпитував підлеглих демонів, як прискорити погибель людини.</p>\n<p>Один із його слуг сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я, бувши досвідченим, говорив людям, що не існує ніякого Бога, ніякої вічности, не існує жодної різниці між гріхом і справедливістю, і що вони можуть жити, як захочуть.</p>\n<p>Злі духи це підтвердили і схвалили. Але сатана мовив:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2235:nakrashcha-sit-dyjavola-24-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4477,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4473,'P','2014-06-24',1,NULL,'Rss','Ми були схожі на зграйку птахів','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5960,200,19s20-POPE11.jpg\" />Я ніколи не пам’ятаю дат. За вкрай рідкісними винятками. Тому нерідко доводиться довго вибачатися за пропущені дні народження та інші важливі події: цифри мені не давалися ще у школі, не даються і зараз.</strong></p>\n<p>Єдиний шанс запам’ятати день&nbsp;— це пов’язати його з чимось подієвим і значущим. Саме таким чином мені запам’яталося, що 24&nbsp;червня&nbsp;— день іменин єпископа Яна Пурвінського. У&nbsp;день свята Народження Йоана Хрестителя одним із прохань на Молитві вірних пролунала молитва за нього, тоді ще єпископа-ординарія Києво-Житомирського. А&nbsp;молитва та пролунала на аеродромі «Чайка», і було то в день Святої Меси, яку відслужив на київській землі, у серці Руських земель, Його Святість Йоан Павло&nbsp;ІІ.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2236:my-buly-shozhi-na-zgraku-ptahiv&catid=32:gk-svit-cat&Itemid=58',4478,NULL,1,26),
(4474,'P','2014-06-24',1,NULL,'Rss','Неприємна історія (25 червня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,5960,200,woprosy1.jpg\" style=\"margin: 10px; float: left;\" />Юнака запросили у гості. Всі сіли за стіл. Хлопець полюбляв підливку, яка стояла далеко від нього. Він часто поглядав на неї, не зважуючись, однак, поставити її ближче. Навпроти юнака сиділа донька господині, і поряд з нею, власне, стояла підливка. Гадаючи, що молодик поглядає на її доньку, господиня тихенько запитала його:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2237:nepryjemna-istorija-25-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4479,NULL,1,26),
(4475,'P','2014-06-25',1,NULL,'Rss','Звернення о. Григорія Хомишина 1944 року в часі свавілля радянських військ на теренах західної України','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5962,200,12.jpg\" />Всечесному духовенству й вірним</p>\n<p>Мир о Господі</p>\n<p>В дуже важні[й] і болючі[й] справі звертаюся до Вас Всч. ОО. Душпастирі і до мої вірних в Епархії. В тій самій справі я звертався до Вас вже в липні с. р., однак з причини тодішніх воєнних дій ледве чи дійшло письмо моє до Ваших рук.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2238:zvernennja-o-grygorija-homyshyna-1944-roku-v-chasi-svavillja-radjanskyh-visk-na-terenah-zahidnoji-ukrajiny&catid=35:istorychni-postati-cat&Itemid=48',4480,NULL,1,26),
(4476,'P','2014-06-25',1,NULL,'Rss','Ревнощі і порівняння (26 червня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,5963,200,7187.jpg\" style=\"margin: 10px; float: left;\" />-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; І навіть якби ти дізналася, що я маю іншу, то не стала би ревнувати?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ревнувати? З якого побиту?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А якби я думав про якусь жінку з мого минулого, будучи поряд з тобою? Ти не стала би хвилюватися про те, що я можу піти до неї?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ні.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2239:revnoshchi-i-porivnjannja-26-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4481,NULL,1,26),
(4477,'P','2014-06-26',1,NULL,'Rss','Жива парафія Євроінтеграція і Церква Глава УГКЦ розповів Святішому Отцеві про «біль українського народу»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5965,200,4534.jpg\" /></p>\n<p><strong>Мир є надійним лише тоді, коли вирощується багатьма руками. На це звернув увагу Папа Франциск, приймаючи у четвер, 26 червня, учасників 87-ї Асамблеї Ради організацій, які допомагають Східним Католицьким Церквам (R.O.A.C.O.).</strong><o:p></o:p></p>\n<p>Від 23 до 26 червня у Ватикані відбувалися засідання цієї Ради, на порядку денному якої, серед іншого, була підтримка УГКЦ, тому в них брали участь також Блаженніший Святослав Шевчук та Апостольський Нунцій в Україні архиєпископ Томас Е. Ґалліксон.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2240:2014-06-26-19-03-00&catid=32:gk-svit-cat&Itemid=58',4483,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4478,'P','2014-06-26',1,NULL,'Rss','Куди послати диявола (27 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5965,200,10449906.jpg\" />Одного разу хтось запитав у побожної бабусі:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чи нагадує вам диявол про гріхи?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Певна річ, - відповіла вона, - але я посилаю його на схід, а коли він повертається, - то на захід. І так він ходить зі сходу на захід і ніяк не може знайти моїх гріхів.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А чому саме на схід і на захід посилаєте ви свого супротивники? – запитали в бабусі.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2241:kudy-poslaty-dyjavola-27-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4484,NULL,1,26),
(4479,'P','2014-06-27',1,NULL,'Rss','«УГКЦ є екуменічною Церквою майбутнього із нескореним духом, на яку може орієнтуватися цілий християнський світ»','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5967,200,arzhakovskii.jpg\" /></strong>Напередодні 25-ї річниці виходу УГКЦ із підпілля своїми думками про нашу Церкву, поглядом на перспективи її розвитку та досягнення сьогодення поділився Антуан Аржаковський (Франція) - доктор історичних наук, екс-дипломат французького МЗС, професор Українського католицького університету.</p>\n<p><strong>-</strong> <strong>Пане Антуане, у часі святкування 25-літнього ювілею виходу УГКЦ з підпілля, якими є основні кроки, що допоможуть їй налагодити екуменічний діалог із іншими християнськими конфесіями заради спільного блага Церкви і держави?</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2242:2014-06-27-19-07-00&catid=27:gk-v-ukrajini-cat&Itemid=57',4485,NULL,1,26),
(4480,'P','2014-06-27',1,NULL,'Rss','Блаженний Священномученик Миколай Чарнецький','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5967,200,film_charnetskyy.jpg\" />ХХ століття - час народження східної (візантійської) вітки Згромадження редемптористів. Прийшовши на українські землі, бельгійські редемптористи спромоглися на найбільше - вони змогли стати частинкою народу, змогли влитися в його культуру, ментальність, та, що найважливіше, у його східну духовність. Палаючи духом за Господа сил, вони вели за собою тих, котрі чули у своєму серці Христовий голос: \"Ідіть і зробіть учнями всі народи.\" (Мт.28,19). Серед цих перших синів української землі, що вирішили йти і виконувати волю свого Спасителя, був великий муж святого життя - блаженний єпископ Миколай Чарнецький.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2243:blazhenny-svjashchennomuchenyk-mykola-charnecky&catid=35:istorychni-postati-cat&Itemid=48',4486,NULL,1,26),
(4481,'P','2014-06-27',1,NULL,'Rss','Христа Чоловіколюбця','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5967,200,icon-Khrystos_cholovikoljubec.jpg\" />Це свято є особливим у Вселенській Церкві.&nbsp;<strong>Саме свято встановлене Христом у Великодню П’ятницю, бо саме хресна жертва є Його найбільшим свідченням любові до людини</strong>. Але Свята Церква перенесла&nbsp; святкування цього свята на дев’ять тижнів вперед, бо Великодня П’ятниця є найсумнішим і найважчим днем для християн, бо в цей день наш гріх, гріх кожного з нас, розп’яв на хресті Бога – Ісуса Христа. Але одночасно цей день є найрадіснішим для людства, бо в цей день відбулося відкуплення усього людства, Христос своєю жертвою здобув нам прощення наших гріхів.&nbsp;<strong>Для глибшого і кращого розуміння цієї жертви Свята Церква встановила це свято.</strong></p>\n<p>Також Свята Церква встановила це свято, щоб ми, християни – наслідники Христа, навчилися у Нього любові до ближнього, любові до усього людства на загал і кожної людини зокрема.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2244:hrysta-cholovikoljubcja&catid=28:tema-nomera-cat&Itemid=2',4487,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4482,'P','2014-06-27',1,NULL,'Rss','Всесвітній з’їзд бісів (28 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5967,200,sueta_1.jpg\" />Сатана скликав всесвітній з’їзд бісів. У своєму вступному слові він сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ми не можемо заборонити християнам ходити до церкви. Ми не можемо заборонити їм читати Біблію. Ми не можемо заборонити їм єднатися в молитві з Богом. А щойно вони налагоджують зв\'язок з Христом, ми вмить позбуваємося влади над ними. Тож хай вони ходять у свої церкви, однак ми вкрадемо їхній час, щоб вони, повсякчас заклопотані, не могли молитися і каятися з гріхів і розвивати свої стосунки з Христом.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2245:vsesvitni-zjizd-bisiv-28-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4488,NULL,1,26),
(4483,'N','2014-06-27',1,NULL,'Rss','Всесвітній з’їзд бісів (28 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5967,200,sueta_1.jpg\" />Сатана скликав всесвітній з’їзд бісів. У своєму вступному слові він сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ми не можемо заборонити християнам ходити до церкви. Ми не можемо заборонити їм читати Біблію. Ми не можемо заборонити їм єднатися в молитві з Богом. А щойно вони налагоджують зв\'язок з Христом, ми вмить позбуваємося влади над ними. Тож хай вони ходять у свої церкви, однак ми вкрадемо їхній час, щоб вони, повсякчас заклопотані, не могли молитися і каятися з гріхів і розвивати свої стосунки з Христом.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2246:vsesvitni-zjizd-bisiv-28-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4489,NULL,1,26),
(4484,'P','2014-06-28',1,NULL,'Rss','Вічна пам\'ять Івану Плющу','<p><img src=\"http://askoldova-mohyla.org/uk/image,5971,200,mark9.jpg\" style=\"margin: 10px; float: right;\" />Про смерть Івана Плюща Олександр Зінченко, історик та журналіст, написав у своєму Фейсбуці так: «Пішла епоха. З ВАЮ (Віктором Ющенком. — Авт.) з минулого року намовляли Івана Степановича записати довге інтерв’ю. Не схотів, узяв усе з собою».</p>\n<p>Можливо, Плющ тому відмовлявся від інтерв’ю, що вже тяжко хворів. Він пішов iз життя після тривалого онкологічного захворювання. А можливо, у Івана Степановича були й інші резони. Він залишив політику в 2012-му — відмовившись від балотування до парламенту. А до того був майже незмінним власником депутатського мандата: Плюща обирали до Ради I, II, III, IV та VI скликань. Утомився Іван Степанович чи розчарувався? Чи, може, вже не мав достатньо фізичних та емоційних резервів, аби вкотре братися до виборчої кампанії? Цього ми вже ніколи не дізнаємось. Це, як правильно зазначено, Плющ «узяв із собою».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2247:vichna-pamjat-ivanu-pljushchu&catid=37:spilnota-cat&Itemid=49',4490,NULL,1,26),
(4485,'P','2014-06-29',1,NULL,'Rss','Захист парафіян під час інформаційної війни','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5971,200,reporter_zt_ua.jpg\" />Війна, розпочата Росією проти нашої країни, позначається широким застосуванням інформаційних технологій. Їхніми жертвами стала значна частина мешканців Криму й Донбасу, політичні погляди яких зазнали отруйного впливу російських телеканалів і соціяльних мереж. В інформаційному просторі формується світ міфів, здатний підмінити в свідомості людини реальний довколишній світ. Незахищена, слабка особистість може стати і часто стає заручником світу міфів, тікає в нього від реальності, а відчуття несумісності цих двох світів викликає в неї депресію.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2249:zahyst-parafijan-pid-chas-informacinoji-viny&catid=37:spilnota-cat&Itemid=49',4491,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4486,'P','2014-06-29',1,NULL,'Rss','Відповідь на лайку (29 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5971,200,baby-angel-cute-wings.jpg\" />Старець і його учень-послушник зустріли чоловіка, який, вважаючи послушника своїм кривдником, почав лаяти його. Учень довго, мовчки і терпляче зносив усе, але потім не втримався і почав відповідати лайкою на лайку. Тоді старець пішов далі, залишивши цих двох закінчити сварку. Коли послушник знову прийшов до старця, він з образою запитав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чому ти залишив мене самого зносити лайку цього нахаби?</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2248:vidpovid-na-laku-29-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4492,NULL,1,26),
(4487,'P','2014-06-30',1,NULL,'Rss','Боротьба з гнівом (30 червня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5971,200,92d9758f2db7.jpg\" />Один брат, що жив у монастирі і часто знемагав від гніву, мовив сам до себе: «Піду в пустелю, може там, не маючи з ким сваритися, погамую пристрасть». І вийшов він з монастиря, і став жити самітником.</p>\n<p>Одного разу наповнив брат водою глечика і поставив його на землю. Зненацька посудина перекинулася. Те саме сталося вдруге. І третього разу також. Чернець розсердився, схопив глечика і вдарив його об землю</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2250:borotba-z-gnivom-30-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4493,NULL,1,26),
(4488,'P','2014-06-30',1,NULL,'Rss','7 принципів щасливого сімейного життя','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5971,200,456.jpg\" />Поради від дідуся</strong></p>\n<p><strong>1. Моліться</strong></p>\n<p>Ми багато втрачаємо, якщо не використовуємо у вихованні дітей чотири види молитви. Перший — життя Таїнствами Церкви. Добрий приклад проникає в дітей через поведінку батьків, через те, на скільки важливе місце в сім\'ї займає прийняття таїнств.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2251:7-pryncypiv-shchaslyvogo-simenogo-zhyttja&catid=28:tema-nomera-cat&Itemid=2',4494,NULL,1,26),
(4489,'P','2014-07-01',1,NULL,'Rss','Ліки від страху (1 липня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5977,200,1388408850_ne-boysya11.jpg\" />-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чи є страх для вас проблемою? Чи краде він у вас радість життя? Чи заважає жити повноцінно? Якщо так, не журіться. Ліки від страху є.</p>\n<p>Одна з численних переваг християнина – це свобода від страху. Якщо ви боїтеся змін і труднощів, згадайте, що пообіцяв Бог. Якщо ви будете коритися Йому то Він ітиме перед вами і допоможе вам здобути перемогу.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2252:liky-vid-strahu-1-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4495,NULL,1,26),
(4490,'P','2014-07-01',1,NULL,'Rss','Війна вчить нас бути милосердними…','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5977,200,109030.jpg\" />Що ви уявляєте, коли чуєте слово «війна»? Перед обличчям відразу постає незліченна кількість жертв, сльози матерів, постріли й засмучені, проте такі рідні очі захисників нашої багатостраждальної України. Пригадується й заголовок книги відомої білоруської письменниці Світлани Алексієвич «У війни – не жіноче обличчя».&nbsp; Її обличчя – це маска, під якою ховаються зло та агресія, намагаючись проникнути в душу кожної людини.</p>\n<p>Та навіть найбільше зло безсильне перед Любов’ю, яка так тихо і ненав’язливо змінює нас на краще і дарує віру в те, що за кожен наступний день нашого життя слід боротися. А сьогодні, коли українська земля вмивається кров’ю своїх дітей, люди демонструють, що більше нема байдужих, що всі ми прагнемо миру і свободи, але в жодному разі не готові жертвувати своєю людською та громадянською гідністю.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2253:vina-vchyt-nas-buty-myloserdnymy&catid=28:tema-nomera-cat&Itemid=2',4496,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4491,'P','2014-07-01',1,NULL,'Rss','Грішна людина (2 липня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,5977,200,strah.jpg\" style=\"margin: 10px; float: left;\" />Один провідник зустрівся з чоловіком, який стверджував, що грішна людина у ньому померла. Заінтригований проповідник запросив цього чоловіка до себе на обід. Вислухавши роздуми гостя від початку до кінця, він підніс склянку і виплеснув воду просто в лице своєму співрозмовникові. Природно, той жахливо обурився і досить гостро висловив своє невдоволення. Провідник, же відповів:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2255:grishna-ljudyna-2-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4497,NULL,1,26),
(4492,'P','2014-07-01',1,NULL,'Rss','Аскольд єднає нас в родину','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5977,200,DSC00275.JPG\" />29 червня 2014 року на Аскольдовій могилі освячено катехитичний зал імені св. Івана Павла ІІ Папи Римського. Церковна громада вірить, що з часом тут постане Міжнародний молодіжний центр пам\'яті св. Івана Павла ІІ, а це приміщення стане для нього наріжним каменем.</p>\n<p>Настоятель парафії св. Миколая Чудотворця на Аскольдовій Могилі о. Ігор Онишкевич розповідає, що громада тривалий час гостро відчувала потребу в навчальному приміщенні для недільної церковної школи. Парафіяльна катехитична школа, яку провадять монахині згромадження ССНДМ, охоплює всі вікові категорії дітей парафії. Тепер буде можливість, наприклад, групу дітей-дошкільнят не тільки навчати, але й розважати іграми, активним відпочинком.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2256:askold-jednaje-nas-v-rodynu&catid=25:parafija-cat&Itemid=27',4498,NULL,1,26),
(4493,'P','2014-07-03',1,NULL,'Rss','«Війна і мир починаються в людському серці»','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5981,200,dzjurax_jyve04.jpg\" />«У світлі Божого слова ми краще починаємо розуміти страждання, які спадають на нас, на наших ближніх чи на наш народ». Про це сьогодні, 3 липня, в Патріаршому cоборі Воскресіння Христового сказав владика Богдан (Дзюрах), Адміністратор Патріаршої курії, під час проповіді на Богослужінні для працівників Патріаршої курії.<o:p></o:p></strong></p>\n<p>Владика Богдан просить пам’ятати, що Ісус заохочує нас не тільки прийти до Нього. «Його серце – Боже серце, здатне вмістити журби і болі всіх людей, цілого людства. Бо це серце Боже. Тому не вагаймося принести до престолу Всевишнього болі, журби, страждання інших людей», – закликає проповідник.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2258:2014-07-03-19-31-35&catid=27:gk-v-ukrajini-cat&Itemid=57',4499,NULL,1,26),
(4494,'P','2014-07-03',1,NULL,'Rss','Проповідь біса (3 липня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5981,200,134918.jpg\" />Один чоловік обходив села і заходив у всі церкви та пастви. Та якось він зайшов в одну церкву і помітив, що проповідь читає біс. Він не заважав йому а слухав, про що той говорить. На його подив, біс не змінював слова Божого, не прибріхував, усі його проповіді були правильними і справедливими. Чоловік дочекався до кінця служби і підійшов до «проповідника».<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Я тебе впізнав, ти біс. І я слухав твою проповідь і не знайшов у ній ані брехні, ані блюзнірства. У чому твій підступ?<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2257:propovid-bisa-3-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4500,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4495,'P','2014-07-03',1,NULL,'Rss','Лише один гріх (4 липня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5981,200,blizhc.jpeg\" />Жив один пустельник-чернець і от біс, після багатолітньої боротьби з ним пообіцяв, що не буде більше його гнітити жодними спокусами, якщо він учинить один із трьох гріхів: вбивство, блуд або пияцтво.<o:p></o:p></p>\n<p>Пустельник почав міркувати: людину убити – найбільше зло і воно заслуговує смертної кари як на Божому суді, так і на цивільному. Вчинити блуд і згубити чистоту тіла, яку я беріг і досі – шкода. Напитися ж один раз, як видається, невеликий гріх, бо ж після того людина швидко витвережується знов. Піду я вип’ю вина, що біс більше не гнітив мене і буду далі жити мирно в пустелі.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2259:lyshe-odyn-grih-4-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4501,NULL,1,26),
(4496,'P','2014-07-04',1,NULL,'Rss','«Складається враження, що нехтування законом − улюблений спорт українців»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5984,200,huzar_blogy.jpg\" />Хочу розповісти кілька коротеньких історій, які ілюструють, як за кордоном люди ставляться до законів.</p>\n<p>Мій знайомий поїхав автомобілем до Швейцарії відвідати друзів. Одного вечора, дуже пізно, майже близько півночі, повертався додому до швейцарської родини, в якої гостював. Під’їхав до перехрестя і, не побачивши жодного транспорту, поїхав далі, незважаючи на червоне світло світлофора.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2260:2014-07-04-18-29-57&catid=28:tema-nomera-cat&Itemid=2',4502,NULL,1,26),
(4497,'P','2014-07-04',1,NULL,'Rss','Молися і трудися (5 липня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,5987,200,998777.jpg\" style=\"margin: 10px; float: left;\" />Рибалка перевозив на човні одну людину. Той підганяв рибалку.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Мерщій, спізнююся на роботу.</p>\n<p>Аж ось він побачив, що на одному веслі написано: «Молися», а на іншому «Трудися».</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А це навіщо, - спитав він?</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2262:molysja-i-trudysja-5-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4505,NULL,1,26),
(4498,'P','2014-07-04',1,NULL,'Rss','Знайди своє очищення у прощі','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5987,200,IMG_0163.JPG\" />Міжнародна десятиденна піша Проща «Самбір - Зарваниця» (коротко - Проща) - духовне паломництво, яке організовує Українська Греко-Католицька Церква (точніше її Пасторально-міграційний відділ у співпраці з «Карітасом» Самбірсько-Дрогобицької єпархії та Міжнародною спільнотою родин мігрантів) із Самбора Львівської області до Зарваниці Тернопільської області) із Самбора Львівської області до Зарваниці Тернопільської області. &nbsp;“Перша мета – щоб і похід, і молитва, і праця в групах, і катехиза, і вільний час, і різного роду служіння, одним словом, кожен день, кожна година і кожна хвилина на цій прощі, були пожертвувані на Божу славу. Бо Господь хоче плодити нас уже й тепер, а не тоді, коли ми приготуємося. Друга мета – молитися за мігрантів і їхні родини, що, за різними даними, за кордон виїхало 5-7 мільйонів українців. І таке враження, що країна до кінця не відчула, що в неї ніби нема частини тіла. Велике значення має, щоб ми про них пам’ятали, щоб ми їх не судили, а намагалися зрозуміти й допомогти. Щоб кожен із нас щось зробив для того, щоб об’єднати ті сім’ї, щоб допомогти дітям, які ростуть без батьків, і допомогти адаптуватися тим людям, котрі хочуть повертатися в Україну”, - о.Василь Поточняк, виконавчий секретар Пасторально-міграційного відділу.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2261:znady-svoje-ochyshchennja-u-proshchi&catid=27:gk-v-ukrajini-cat&Itemid=57',4506,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4499,'P','2014-07-04',1,NULL,'Note','Духовно-фізичний відпочинок на морі в Греції','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5986,200,2.jpg\" alt=\"\" width=\"200\" height=\"112\" />Паломницько-рекреаційний центр св.Андрія Первозванного Київської Архиєпархії УГКЦ пропонує духовно-фізичний відпочинок на морі в Греції в курортній зоні Пієрія, що неподалік від Салоніки. Відпочиваючи з нами, зможете відвідати історичну і релігійну частину Греції.&nbsp;Розташування запропонованих готелей є неподалік моря в затишній місцевості, з мальовничим узбережжям і добре розвиненою інфраструктурою.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5986,200,2.jpg\" alt=\"\" width=\"200\" height=\"112\" />Паломницько-рекреаційний центр св.Андрія Первозванного Київської Архиєпархії УГКЦ пропонує духовно-фізичний відпочинок на морі в Греції в курортній зоні Пієрія, що неподалік від Салоніки. Відпочиваючи з нами, зможете відвідати історичну і релігійну частину Греції, як наприклад:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>АФОН, АФІНИ, КАСТОРІЯ, МЕТЕОРА , САЛОНІКИ, ОЛІМ, ДІОН, ВЕРГІНА, АРІДЕЯ, ЕДЕССА.</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Розташування запропонованих готелей є неподалік моря в затишній місцевості, з мальовничим узбережжям і добре розвиненою інфраструктурою.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Відстань до моря:</strong> Перша лінія. Красиве узбережжя з гармонійнім поєднанням гальки, піску і чистого блакитного моря, нагороджені&nbsp; Блакитним прапором.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Харчування:</strong> BB, FB, шведський стіл.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Опис номерів:</strong> У кожному номері є туалет, душ, кондиціонер, холодильник, супутникове телебачення, телефон, бездротовий Інтернет (Wi-fi). Номери дуже затишні і красиво оформлені, укомплектовані сучасними і зручними меблями, деякі номери з видом на море та гору Олімп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Пожертва: від 255 євро</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>В ціну входить:</strong></span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">проїзд комфортабельним автобусом по маршруту;</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">проживання в готелі;</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">харчування (трьох разове);</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">страхівка;</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">супровід керівника групи.</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>В ціну не входить</strong></span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">вхідні квитки в музеї, екскурсійні об&rsquo;єкти і т.д.;</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">консульський збір &ndash; 35 &euro; (крім дітей до 18 років, пенсіонерів);</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">послуги візового центру &ndash; 22 &euro; (крім дітей до 6 років);</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">факультативні екскурсії;</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Детальніше можна довідатися за адресою:</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">вул. Різницька 11 Б / 28-29</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">м. Київ 01011</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">моб: 0683024495, 0639512551, 0668154440,</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">e-mail: <a href=\"mailto:ka.palomnyk@gmail.com\">ka.palomnyk@gmail.com</a></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><a href=\"http://www.palomnyk.kiev.ua/\">www.palomnyk.kiev.ua</a></span></p>',NULL,NULL,4503,5,1,47);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4500,'P','2014-07-05',1,NULL,'Rss','У Раю ліпше (6 липня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5988,200,450_137977368287.jpg\" />Потрапив чоловік до Раю, дивиться, а там усі ходять радісні, щасливі, щирі доброзичливі, а довкола все як у звичайному житті. Походив він, погуляв, сподобалось йому. І каже він Архангелу:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А можна поглянути яке пекло? Хоч одним оком.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Гаразд. Ходімо.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2263:u-raju-lipshe-6-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4507,NULL,1,26),
(4501,'P','2014-07-06',1,NULL,'Rss','Празник Святого Йоана Хрестителя','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5990,200,070713-1.jpg\" />\"Істинно кажу вам, що між народженими від жінок не було більшого від Йоана Хрестителя\" (Мт. 11, 11). 7 липня наша Церква вшановує великий празник Різдво Івана Христителя.</p>\n<p>Серед різних святих, яких почитає Східна Церква, особливим культом славиться святий Йоан Хреститель, Господній Предтеча. Він єдиний з усіх святих після Божої Матері має свій празник Зачаття і празник Різдва. Про його великий культ у Східній Церкві найкраще свідчать аж шість празників в його честь протягом церковного року: празник Зачаття, Різдва, Усікновення, Перше і Друге Знайдення Голови, Третє Знайдення Голови і Собор після празника Богоявлення. Тож погляньмо на його особу й на його культ у Східній Церкві.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2264:praznyk-svjatogo-oana-hrestytelja&catid=27:gk-v-ukrajini-cat&Itemid=57',4508,NULL,1,26),
(4502,'P','2014-07-06',1,NULL,'Rss','Смик і скрипка (7 липня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5990,200,skripka_i_reptiliya_1920x1200.jpg\" />Багато літ жили разом смик і скрипка і всі милувалися ними.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чудова пара, - гукнув тромбон.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - а живуть же як, - додав рояль, - у згоді, приязні.</p>\n<p>Все було добре, аж враз… старий смик вирішив, що скрипка йому не пара. Надто у неї вузька талія, подумав він і пішов собі шукати подругу.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2265:smyk-i-skrypka-7-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4509,NULL,1,26),
(4503,'P','2014-07-07',1,NULL,'Rss','«Свято Різдва Івана Хрестителя свідчить про те, що настав час приходу Господа на цей світ…»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5992,200,1055108810721074_102810831080107910721074107710901072_5_400.jpg\" />7 липня Церква святкує Різдво святого Івана Хрестителя. Іван Предтеча вважається найбільшим святим після Пречистої Діви Марії. Про це розповідає о. Василь Рудейко, доктор богослов\'я, заступник голови Патріаршої літургійної комісії УГКЦ, священик храму Священномученика Климентія Шептицького у Львові.</p>\n<p><strong>З якої родини походив святий Іван?</strong></p>\n<p>Цей святий - дуже цікава особистість. Прийнято вважати, що він однією ногою стоїть у Старому Завіті, а іншою – в Новому проповідує Христа, який має народитися. Особа Івана Предтечі тісно пов’язана з особою Ісуса Христа. Вони нероздільні, та ще й родичі: мати Івана була тіткою Богородиці. Різниця у віці - півроку. Предтеча походив зі священичої родини. Його батьки, перебуваючи у старшому віці, навіть не очікували нащадка. Проте одного разу його батькові з’явився уві сні ангел і сказав про народження дитини, а той засумнівався. Внаслідок цього онімів, аж поки дитину не назвали Іваном.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2266:2014-07-07-10-03-50&catid=28:tema-nomera-cat&Itemid=2',4510,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4504,'P','2014-07-07',1,NULL,'Rss','Любов до людей (8 липня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5993,200,4061913_10472147.jpg\" />Один християнин жив простим життя в хатинці під горою. Якось в його хатину заліз злодій і оторопів від того, що там немає чого красти. Незабаром християнин повернувся до дому і застав злодія.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ти так довго йшов, щоб провідати мене? – промовив він до волоцюги, - а тому не маєш повернутися порожнім, будь-ласка, візьми в дарунок мою одежу.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2267:ljubov-do-ljude-8-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4511,NULL,1,26),
(4505,'P','2014-07-08',1,NULL,'Rss','Митрополит','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,5994,200,DM.jpg\" style=\"margin: 10px; float: right;\" />.</strong>..Черная орда \"Беркута\" неумолимо надвигалась на свет зимнего Майдана, текла рекой по Институтской, просачивалась к Дому профсоюзов... Казалось, еще несколько минут – и все будет окончено, тьма поглотит все еще светящуюся площадь и задавит последний порыв к свободе... И в это мгновение мы услышали церковный набат. Это без устали звонили колокола церквей Михайловского Золотоверхого монастыря Украинской православной церкви Киевского патриархата – и, будто бы откликаясь на их призыв, к площади со всех еще не перекрытых улиц центра украинской столицы потянулись первые, пока несмелые ручейки горожан, к рассвету превратившиеся в настоящее море и рассеявшие черный морок у границ Майдана.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2268:mytropolyt&catid=35:istorychni-postati-cat&Itemid=48',4512,NULL,1,26),
(4506,'P','2014-07-08',1,NULL,'Rss','А судді хто?','<p><img src=\"http://askoldova-mohyla.org/uk/image,5995,200,huzar_velykden14.jpg\" style=\"margin: 10px; float: right;\" />Упродовж останніх майже 20 років до мене зверталися різні люди: матері, жінки чи інші родичі підсудних, утримуваних в ізоляторах чи в’язнів у тюрмах із проханням заступитися перед судейською владою за позбавлених волі. Зверталися до мене з проханням заступитися за їхніх рідних.</p>\n<p>У цих просьбах є логіка, бо кожна людина неповторна. Але, з іншого боку, вони беруть до уваги тільки когось одного, не дивлячись, що всі люди мають однакові права на справедливість.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2270:a-suddi-hto&catid=28:tema-nomera-cat&Itemid=2',4513,NULL,1,26),
(4507,'P','2014-07-08',1,NULL,'Rss','Кайдани (9 липня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5995,200,MJi1w6CJdzg.jpg\" />Якось купець вирушив в іншу країну і прийшов у дивне місто, де всі були в кайданах. Купець подумав: - «А якщо і мені одягнути кайдани, то що я буду робити?» зненацька, у ту саму мить, з’явилася варта і побачивши вільного чоловіка, закувала його в кайдани.</p>\n<p>Втративши змогу повернутися до дому, купець занепав духом. Він взявся розпитувати нещасних городян, чому вони всі в кайданах. А ті відповіли, що в їхньому місті з давна так заведено. Купець запитав:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2269:kadany-9-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4514,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4508,'P','2014-07-09',1,NULL,'Rss',' Всеукраїнська Рада Церков закликає сепаратистів скласти зброю та звільнити всіх заручників','<p align=\"center\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5998,200,2014.jpg\" />Звернення&nbsp;<span style=\"font-size: 0.875em;\">Всеукраїнської Ради Церков і релігійних організацій&nbsp;</span><span style=\"font-size: 0.875em;\">щодо ситуації на Сході України</span></p>\n<p>Всеукраїнська Рада Церков і релігійних організацій протягом останніх тижнів зверталась зі словами підтримки позиції Президента України П. О. Порошенка щодо мирного врегулювання збройного конфлікту на Сході України та висловлювала готовність посильно сприяти в реалізації його мирного плану.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2271:-vseukrajinska-rada-cerkov-zaklykaje-separatystiv-sklasty-zbroju-ta-zvilnyty-vsih-zaruchnykiv&catid=27:gk-v-ukrajini-cat&Itemid=57',4516,NULL,1,26),
(4509,'P','2014-07-09',1,NULL,'Note','Благодійний концерт Нелі Франчук','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,5998,200,354.jpg\" alt=\"\" width=\"200\" height=\"169\" />Неля Франчук</strong>&nbsp;і наша парафіяльна громада запрошує усіх киян, гостей нашого славного міста&nbsp;<strong>13 липня 2014 р. Б. о 13:45</strong>, після Служби Божої, відвідати&nbsp;<strong>благодійний концерт</strong>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Зібрані кошти</strong>&nbsp;будуть направленні&nbsp;<strong>на потреби поранених бійців</strong>, які перебували у зоні АТО.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Декілька слів про саму Нелю Франчук. &nbsp;Народилася вона у місті Вороніж РФ в сім&rsquo;ї військового. До 13 років жила з батьками в Москві, потім родина переїхала до Києва. Самотужки вивчила українську мову, чим досі пишається. З дитинства писала вірші спочатку російською, згодом перейшла на українську.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,5998,200,354.jpg\" alt=\"\" width=\"200\" height=\"169\" />Неля Франчук</strong> і наша парафіяльна громада запрошує усіх киян, гостей нашого славного міста <strong>13 липня 2014 р. Б. о 13:45</strong>, після Служби Божої, відвідати <strong>благодійний концерт</strong>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Зібрані кошти</strong> будуть направленні <strong>на потреби поранених бійців</strong>, які перебували у зоні АТО.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Декілька слів про саму Нелю Франчук. &nbsp;Народилася вона у місті Вороніж РФ в сім&rsquo;ї військового. До 13 років жила з батьками в Москві, потім родина переїхала до Києва. Самотужки вивчила українську мову, чим досі пишається. З дитинства писала вірші спочатку російською, згодом перейшла на українську.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Сьогодні Неля Франчук є автором декілька сот пісень на вірші класиків зарубіжної та української поезії, таких як Т.Шевченко, І.Франко, Л.Українка, П.Грабовський, О.Маковей, В.Стус, Б.-І.Антонич, В.Симоненко, Є.Плужник, О.Олесь, П.Тичина, Р.Бернс, М.Цветаєва, О.Блок, Л.Костенко, О.Пахльовська, Л.Голота, Г.Чубач, а також на вірші сучасних молодих поетів, народні слова та свої власні. Є учасницею та лауреат фестивалів: &laquo;Оберіг-91&raquo;, &laquo;Оберіг-92&raquo;, &laquo;А без Подолу Київ неможливий&raquo;, &laquo;Лісова фієста&raquo;, &laquo;Срібна підкова&raquo;, &laquo;Севама-фест&raquo;, &laquo;Мазепа-фест&raquo;, &laquo;Розкуття&raquo;, &laquo;Міжнародний фестиваль культури та мистецтв-2009&raquo;, &laquo;Дикий мед&raquo;, &laquo;Тустань&raquo; і ще багато інших.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/0Ijy8avPqQg\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>',NULL,NULL,4515,5,1,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4510,'P','2014-07-09',1,NULL,'Rss','Струмок (10 липня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,5999,200,37836235180artfilerujpg.jpg\" style=\"margin: 10px; float: left;\" />У прохолодному веселому лісі, серед кушів і трав, тік струмок. Чарівний, сміхотливий, жвавий. Він усіх любив і все подобалося йому. Цілими днями бавився він сонячними промінчиками, бабками і жучками. На одному із чарівніших вигинів струмка лежав камінь, дуже поважний і статечний камінь. Вельми досвідчений..</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2272:strumok-10-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4517,NULL,1,26),
(4511,'P','2014-07-10',1,NULL,'Rss','Утоптана стежка (11 липня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5999,200,54654.jpg\" />Жили собі два сусіди. Настала зима, посипав сніг. Перший сусід рано-вранці вийшов розгортати сніг на подвір’ї. поки розчищав стежку подивився як там справи в сусіда. А в другого сусіда гарно утоптана стежка.</p>\n<p>Наступного ранку знов випав сніг. Перший сусід устав на пів години раніше і взявся за роботу. Бачить а в другого сусіда вже стежка прокладена.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2274:utoptana-stezhka-11-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4518,NULL,1,26),
(4512,'P','2014-07-10',1,NULL,'Rss','День апостолів Петра і Павла','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5999,200,Conten.jpg\" />«Апостол Петро як найбільш наближений до Ісуса, є прикладом того, як діє Боже провидіння, яким чином людська воля повинна підпорядкуватися волі божественній» – Василь&nbsp;Кметь, кандидат історичних наук, релігієзнавець</p>\n<p>Як відомо, День апостолів Петра і Павла (Петрів день) – це християнське свято на честь апостолів Петра та Павла. Православні християни святкують цей день 12 липня, католики – 29 червня.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2273:den-apostoliv-petra-i-pavla&catid=35:istorychni-postati-cat&Itemid=48',4519,NULL,1,26),
(4513,'P','2014-07-11',1,NULL,'Rss','Репортаж із зони автентичності','<p><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6002,200,10527.jpg\" />Отець Андрій Зелінський ТІ провів декілька тижнів разом з нашим військом на сході України де дбав про духовний стан наших військових. Він поділився пережитим у своєму<a href=\"http://www.facebook.com/andriy.zelinskyy/posts/10202577584861114\" target=\"_blank\">профілі на фейсбуці</a>: </em></p>\n<p>У складній структурі соціальної дійсності можна натрапити на такі виміри людського буття, яких не приховати за бутафорними декораціями повсякденної боротьби за особисту значимість: там усе надзвичайно справжнє, неприкрашено прекрасне, автентичне – просте й глибоко змістовне, як саме таїнство людського життя. Я мав за честь побувати у зоні автентичності, у неприкрашено прекрасному трагічно реальному вимірі людського існування…</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2275:reportazh-iz-zony-avtentychnosti&catid=37:spilnota-cat&Itemid=49',4520,NULL,1,26),
(4514,'P','2014-07-11',1,NULL,'Rss','Захист. Протистояння','<p><em><img src=\"http://askoldova-mohyla.org/uk/image,6003,200,103768.jpg\" style=\"margin: 10px; float: right;\" />Вбивство - чи знешкодження несправедливого нападника? Католицька Церква чітко розмежо­вує безкомпромісне ставлення до заповіді \"Не вбивай\" та справедливий опір і право на захист від аг­ресора, коли громадяни держави-жертви агресії навіть зобов\'язані стати до національної оборони. А правителя-агресора, який самовільно розпоряджається життями та добром своїх підвладних за допомогою війни (отже, і несе за це повну відповідальність), ще у XVI ст. було названо не володарем, а тираном.</em></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2276:zahyst-protystojannja&catid=28:tema-nomera-cat&Itemid=2',4521,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4515,'P','2014-07-14',0,NULL,'Note','«Чудо покладення чесної ризи Пресвятої Богородицi у Влахерні»','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6005,200,356.jpg\" alt=\"\" width=\"200\" height=\"150\" />У вівторок, 15 липня 2014 р.Б., у каплиці &laquo;Чуда покладення чесної ризи Пресвятої Богородицi у Влахерні&raquo; служитиметься святкова Божественна Літургія.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08:00 &ndash; Утреня</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">09:00 &ndash; Святочна Божественна Літургія</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ця каплиця відреставрована і освячена 15 липня 2013 р.Б. є свідчення навернення князя Аскольда у християнську віру.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6005,200,356.jpg\" alt=\"\" width=\"200\" height=\"150\" />У вівторок, 15 липня 2014 р.Б., у каплиці &laquo;Чуда покладення чесної ризи Пресвятої Богородицi у Влахерні&raquo; служитиметься святкова Божественна Літургія.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08:00 &ndash; Утреня</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">09:00 &ndash; Святочна Божественна Літургія</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ця каплиця відреставрована і освячена 15 липня 2013 р.Б. є свідчення навернення князя Аскольда у християнську віру.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Церковна громада планує у день хрещення Аскольда 866-2016 (1150 р.) піднести і поставити над каплицею хрест-постамент.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Запрошуємо за праздник нашої каплиці.</span></p>',NULL,NULL,4522,5,1,38),
(4516,'P','2014-07-11',1,NULL,'Rss','Золота клітка (12 липня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6005,200,aser.jpg\" />Двоє молодиків освідчувалися двом дівчатам у оханні. Один сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я можу запропонувати тільки своє серце, куди може прийти одна з тих, хто згодиться розділити мій важкий шлях.</p>\n<p>Інший мовив так:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я можу запропонувати величезний палац, де розділю зі своєю супутницею радість життя.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2277:zolota-klitka-12-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4523,NULL,1,26),
(4517,'P','2014-07-12',1,NULL,'Rss','«ЗБЕРЕЖЕМО ЖИТТЯ НАШИХ ЗАХИСНИКІВ»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6005,200,licari-kolumba-logo.jpg\" />З благословення Преосвященного Владики Йосифа, члени католицької мирянської організації «Лицарі Колумба» в Україні оголошують збір коштів на теренах Київської Архиєпархії на придбання медичних препаратів для потреб захисників України. <strong>Нашою метою є придбати 1000 пакетів засобу </strong><strong>Celox</strong><strong> (Целокс) який являється на сьогодні одним з найефективніших препаратів для зупинки кровотечі при пораненнях</strong>. Зупинка кровотечі відбувається неймовірно швидко, від 30 секунд до 2 хвилин, в залежності від величини травми. Препарат дає можливість без кровотечі транспортувати потерпілого до місця надання кваліфікованої медичної допомоги.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2282:2014-07-11-21-33-49&catid=27:gk-v-ukrajini-cat&Itemid=57',4524,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4518,'P','2014-07-14',1,NULL,'Rss','Арфа (14 липня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6005,200,big-34.jpg\" />Одного разу чоловік прийшов до учителя і запитав як пізнати істину і досягти досконалості.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Іди в село і там на перехресті двох доріг, ти знайдеш те, що шукаєш, - сказав він.</p>\n<p>Сповнений сподівань і передсмаку, чолові побіг у село і знайшов на перехресті звичайнісіньки крамнички. Перші торгували шматочками металу, в другій якимось деревинками, а в третій тонким дротом. До істини це немало жодного стосунку. І розчарований шукач повернувся до вчителя і сказав, що не знайшов нічого.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2279:arfa-14-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4526,NULL,1,26),
(4519,'P','2014-07-15',1,NULL,'Rss','Горнятко кави (15 липня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6005,200,dsc_2890.jpg\" />Кілька випускників престижного навчального закладу, що досягли успіху і зробили виняткову кар’єру прийшли в гості до свого старого професора. Під час візити розмова зайшла про роботу. Випускники скаржилися на численні труднощі і життєві проблеми. Запропонувавши гостям кави професор пішов на кухню і повернувся з кавником і тацею на якій стояли усілякі горняти: порцелянові, скляні, пластикові, кришталеві. Одні були прості а інші дорогі.</p>\n<p>Коли випускники порозбирали горнята, професор сказав:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2280:gornjatko-kavy-15-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4527,NULL,1,26),
(4520,'P','2014-07-16',1,NULL,'Rss','Дім губернатора (16 липня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6005,200,DETA.jpg\" />Коли одного разу місіонер говорив про вічне життя один язичник глузливо заперечив йому. Що того, так званого вічного життя ніде не видно. Позаяк, коли приходить смерть жодної різниці між людьми немає. Місіонер відповів:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Слухай, мій друже. Ти бачив дім губернатора?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Бачив.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; І що розташоване на найвищих поверхах?</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2281:dim-gubernatora-16-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4528,NULL,1,26),
(4521,'P','2014-07-16',1,NULL,'Rss','«Передова має проходити у глибині нашого серця»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6011,200,546.jpg\" />Отець Андрій Зелінський провів три тижні при штабі Антитерористичної операції (АТО) як військовий капелан – надавав духовну підтримку українським військовослужбовцям. Відбулася ротація, і зараз його змінив інший священик. Отець поділився враженнями з Департаментом інформації УГКЦ.</p>\n<p><strong>Отче Андрію, ви вже багато років працюєте військовим капеланом. Чи змінилося сприйняття цієї місії після роботи в зоні АТО?</strong></p>\n<p>Військове капеланство – це не просто духовне служіння в армії, це певною мірою питання національної безпеки… У пострадянському суспільстві ми, напевне, ще не навчилися звертати увагу на потреби індивідууму, конкретної людини.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2283:2014-07-16-19-51-55&catid=28:tema-nomera-cat&Itemid=2',4529,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4522,'P','2014-07-16',1,NULL,'Rss','Дві сніжинки (17 липня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6011,200,46646.jpg\" />Ішов сніг, погода була тиха і великі неквапом кружляли в чудернацькому танці, повільно наближаючись до землі. Дві сніжинки, щ летіли поряд вирішили побесідувати. Боячись втратити одна одну, вони взялися за руки і одна з них весело сказала:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Як добре летіти втішаючись польотом.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2284:dvi-snizhynky-17-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4530,NULL,1,26),
(4523,'P','2014-07-16',1,NULL,'Rss','Наш обов\'язок - боронити','<p><img src=\"http://askoldova-mohyla.org/uk/image,6011,200,10487.jpg\" style=\"margin: 10px; float: right;\" />«Агресивна війна за своєю природою аморальна. У той трагічний момент, коли вибухає війна, керівники держави, яка зазнала нападу, мають право і зобов’язані захищати країну, застосовуючи навіть силу зброї» (КСДЦ 500) – однозначна норма соціальної доктрини Католицької Церкви. І якщо до тепер могли бути певні моральні сумніви щодо реальності московистької агресії на Україну, то в ніч на празник свв. Первоверховних Апостолів Петра і Павла обстріли українських військових з території РФ і подальше входження військ РФ на територію України, відхилили усякий сумнів у реальності агресії московитської терористичної держави проти України.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2285:nash-obovjazok-boronyty&catid=28:tema-nomera-cat&Itemid=2',4531,NULL,1,26),
(4524,'P','2014-07-17',1,NULL,'Rss','П’ять кульок (18 липня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6014,200,1a11.jpg\" />Уявіть собі, що життя це гра, яка полягає в жонглюванні п’ятьма кульками. Ті кульки: Робота; Сім’я; Здоров’я, Друзі і Душа. І вам потрібно щоб усі вони повсякчас перебували в повітрі. Невдовзі ви зрозумієте, що робота зроблена з гуми – якщо ви її ненароком впустите, то вона підстрибне і повернеться до Вас.</p>\n<p>Але інші 4 кульки: Сім’я; Здоров’я, Друзі і Душа скляні, і якщо ви впустите якусь із них то вона буде надколена, подряпана, серйозно пошкоджена, чи навіть ущент розбита. Вона ніколи н буде такою як раніше. Ви мусите усвідомлювати це і старатися, щоб цього не сталося.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2286:pjat-kulok-18-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4535,NULL,1,26),
(4525,'P','2014-07-17',1,NULL,'Rss','Чи можна любити ворога? Особливо такого','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6014,200,Pion-waxi.jpg\" />Нас звикли називати друзями, навіть братськими народами. І можливо воно так і було, але путін і росіяни, які підтримують його, стали нашими ворогами. росія наш ворог. Хто б міг так масово та в голос говорити пре це ще рік назад? росія наш ворог. Важко звикати до цієї правди. Але як би це прикро не було - факти річ вперта.</p>\n<p>росія наш ворог. І вони наші вороги не тому, що ми &nbsp;з пересердя так говоримо або хочемо цим докорити - ні! Це, на жаль, гірка і підтверджена фактами правда. Правда, яка багатьох «колить в очі». З цією правдою маємо жити і мусимо вибудовувати своє подальше спільне життя з нашим ворогом.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2287:chy-mozhna-ljubyty-voroga-osoblyvo-takogo&catid=28:tema-nomera-cat&Itemid=2',4536,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4526,'P','2014-07-18',1,NULL,'Rss','Як образити камінь (19 липня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6014,200,1337375744_trovant-3.jpg\" style=\"margin: 10px; float: left;\" />Одного разу учень пішов до вчителя. Вони взялися бесідувати про образу. Учитель вийшов і вказавши на камінь сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Образ цей камінь і і вдар його.</p>\n<p>Учень послухався. Тоді учитель запитав його.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чи цей камінь відповів тобі, або чинив якийсь опір.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2288:jak-obrazyty-kamin-19-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4538,NULL,1,26),
(4527,'P','2014-07-18',1,NULL,'Rss','Молитва Папи Франциска за жертв авіакатастрофи на Сході України','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6019,200,_9717e.jpg\" />Прес-центр Ватикану оприлюднив повідомлення, в якому сказано, що Святіший Отець Франциск із глибоким смутком сприйняв повідомлення про катастрофу літака «Малайзійських авіаліній» у східному регіоні України.</p>\n<p>Папа молиться за численні жертви цієї катастрофи та їхні родини, водночас вкотре закликаючи сторони конфлікту до миру та зусиль, спрямованих на пошук вирішення проблем через діалог, щоб уникнути подальших невинних людських жертв.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2289:molytva-papy-francyska-za-zhertv-aviakatastrofy-na-shodi-ukrajiny&catid=27:gk-v-ukrajini-cat&Itemid=57',4539,NULL,1,26),
(4528,'P','2014-07-19',1,NULL,'Rss','Одна копійка (20 липня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6022,200,meloch.jpg\" />Ішов дорогою парубок, а зверху копійка лежить.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Що ж, - подумав він, - і копійка – гроші.</p>\n<p>Підняв її і поклав у гаманець. І став далі думати:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А що би я зробив, коли знайшов би тисячу карбованців? Купив би подарунки батькові і мамі.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2290:odna-kopika-20-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4540,NULL,1,26),
(4529,'P','2014-07-19',1,NULL,'Rss','«ЖИЙТЕ ЗАДЛЯ УКРАЇНИ!»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6022,200,bsmz_04.JPG\" />Як боротися зі злом, піднятися з колін, подолати корупцію та змобілізуватися під час війни навчав Блаженніший Святослав, Глава УГКЦ, молодих учасників Всеукраїнської та молодіжної прощі до Зарваниці 19 липня на зустріч, яка відбулася на площі біля парафіяльного храму.</p>\n<p>«Ця проща є особлива, бо наш народ знаходиться в особливій небезпеці, - сказав Патріарх перед багатотисячною аудиторією. - Ми кожного дня чуємо про нові і нові жертви. Ми усвідомлюємо, що це молодь підняла наш український народ з колін. Це молодь сьогодні своїми грудьми затуляє свою вільну державу. Це молоді хлопці і дівчата сьогодні ідуть під кулі, щоб наша країна, наш народ були вільні.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2291:2014-07-19-20-25-45&catid=27:gk-v-ukrajini-cat&Itemid=57',4541,NULL,1,26),
(4530,'P','2014-07-21',1,NULL,'Rss','Професор Пономарів: коли доречне «на Україні»','<p><em><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6024,200,1052775.jpg\" />Доктор </em>філологічних наук</em><em> </em><em><a href=\"http://www.bbc.co.uk/blogs/legacy/ukrainian/ponomariv/\" target=\"_blank\">Олександр Пономарів відповідає на запитання читачів ВВС Україна</a>.</em></p>\n<p><strong>Яка клична форма ім\'я Вахтанг? – запитує Дмитро Понамарчук.</strong><strong></strong></p>\n<p>Потрібно казати не клична форма, а кличний відмінок – один із сімох відмінків сучасної української мови.</p>\n<p>Від Вахтанг кличний відмінок Вахтангу!</p>\n<p><strong>Данило з Канади цікавиться, як називається мова людей, що живуть в Ізраїлі. Яка відмінність між прикметниками єврейський і гебрайський?</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2292:2014-07-21-10-59-53&catid=37:spilnota-cat&Itemid=49',4542,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4531,'P','2014-07-21',1,NULL,'Rss','Православний тероризм','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6024,200,Thor.jpg\" />Тиждень, що минув, став тим водорозділом, який відділив «русскій мір» від світу, який визнає принаймні якісь етичні й моральні принципи. У цей самий час, коли Папа Франциск <a href=\"http://catholicnews.org.ua/svyatishiy-otec-terorizm-ce-bezumstvo\">вчергове засудив тероризм</a>, патріарх Кіріл&nbsp;<a href=\"http://catholicnews.org.ua/patriarh-kiril-gundyaiev-blagosloviv-putina-na-podalshi-zlochini\">благословив головного терориста</a> на пострадянському просторі на нові «подвиги».</p>\n<p>Це не сталося в одному моменті, це – тенденція. Свого часу В. Путін у якості прем’єра РФ заявив, що&nbsp;<a href=\"http://www.islamnews.ru/news-28541.html\" target=\"_blank\">православ’я ближче до ісламу, аніж до католицтва</a>. І в цьому виявився правий, принаймні, що стосується православ’я московського.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2294:pravoslavny-teroryzm&catid=28:tema-nomera-cat&Itemid=2',4543,NULL,1,26),
(4532,'P','2014-07-22',1,NULL,'Rss','Брюссельське мереживо (22 липня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6026,200,0_f5896_d488445d_orig.jpg\" />Місто Брюссель відоме вишуканим мереживом. У знаменитих мереживних салонах є особливі кімнати, призначені для плетення найтонших і найелегантніших моделей. Світло ледь пробивається через невеличке віконце і падає тільки на мереживо. Найвишуканіші і найпрекрасніші мережки виходять тільки тоді, коли сам ткач перебуває у темряві, а ткання – на світлі.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2295:brjusselske-merezhyvo-22-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4544,NULL,1,26),
(4533,'P','2014-07-22',1,NULL,'Rss','Одвічна боротьба добра і зла','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6026,200,10300442.jpg\" />За нами залишився іще один трагічний тиждень боротьби української держави проти московитської агресії. Тиждень, що приніс нові невинні жертви. Тиждень, що заставив здригнутися увесь світ від жахіть російського тероризму, який воює під знаменом московського православ’я – збитий в українському небі малайзійський «Боїнг» уже став тим переломним моментом, після якого усяка підтримка тероризму московитів на Донбасі стає не просто неморальною, а відверто злочинною, нелюдською, антихристиянською.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2296:odvichna-borotba-dobra-i-zla&catid=28:tema-nomera-cat&Itemid=2',4545,NULL,1,26),
(4534,'P','2014-07-22',1,NULL,'Rss','Теперішні мужність та патріотизм - це фундамент нашого майбутнього, що закладається зараз, – розповідь капелана із зони АТО','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6028,200,gallery.jpg\" />Якщо раніше слово «капелан» здавалось чимось далеким та чужим, то тепер воно на вустах у кожного. Це ті військові священики, які поруч з бійцями у будь-який час, в польових умовах та під свистом куль. Саме вони підвозили чималу частину гуманітарної допомоги, закуповуючи все, окрім зброї. Про роботу служителів в зоні АТО ми поспілкувались з капеланом від УГКЦ о. Любомиром Яворським, який тільки-но повернувся звідти в черговий раз.</strong><strong style=\"font-size: 0.875em;\"><em>&nbsp;</em></strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2297:teperishni-muzhnist-ta-patriotyzm-ce-fundament-nashogo-mabutnogo-shcho-zakladajetsja-zaraz-rozpovid-kapelana-iz-zony-ato&catid=27:gk-v-ukrajini-cat&Itemid=57',4546,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4535,'P','2014-07-23',1,NULL,'Rss','Твоя віра спасла тебе! (Від Матвія 9:22)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6029,200,in_love_travel_com_a2634.jpg\" />1. Ви не вірите в Бога, але говорите на знак подяки «спасибі» (спаси Бог).</p>\n<p>2. Ви не вірите, що Христос жив на Землі, але ведете літочислення від Різдва Христового.</p>\n<p>3. Ви не вірите, що Христос воскрес, але називаєте сьомий день тижня \"воскресним\" днем.</p>\n<p>4. Ви не вірите, що людина була створена за образом і подобою Божою, але називаєте потворною людину, яка втратила цей образ.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2298:tvoja-vira-spasla-tebe-vid-matvija-922&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4547,NULL,1,26),
(4536,'P','2014-07-23',1,NULL,'Rss','Золото під каменем (23 липня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6029,200,ukraine_brand_03.png\" />Одного разу король викотив на дорогу величезний камінь, а сам сховався, щоб побачити, хто прибере його. Багато хто проїжджав, чи проходив стороною, голосно лаючи короля, що він не стежить за дорогами, але нікому не спало на думку прибрати з дороги камінь. Врешті-решт біля каменя зупинився бідний селянин і відкотив його у придорожний рів. А під каменем, на свій подив, селянин знайшов повний мішок золота і лист від короля, у якому той пояснив, що це нагорода тому, хто прибере з дороги великий камінь.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2299:zoloto-pid-kamenem-23-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4548,NULL,1,26),
(4537,'P','2014-07-23',1,NULL,'Rss','Звернення Всеукраїнської ради Церков і релігійних організацій до засобів масової інформації','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6031,200,vrtiro.jpg\" /></strong>У сучасному бутті засобам масової інформації відведена важлива роль у формуванні громадської думки в суспільстві та міжлюдських взаємовідносин у світі. Похвально, приємно і суспільно корисно, коли ця роль здійснюється відповідально, з дотриманням загальнолюдських принципів моралі, законодавства та високих стандартів сучасної журналістики.</p>\n<p>Однак дедалі частіше ми стаємо свідками іншого роду \"журналістики\" – інформаційних воєн, які ведуться як між країнами та народами, так і всередині нашої країни, коли Істина брутально підміняється відвертою брехнею.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2300:zvernennja-vseukrajinskoji-rady-cerkov-i-religinyh-organizaci-do-zasobiv-masovoji-informaciji&catid=27:gk-v-ukrajini-cat&Itemid=57',4549,NULL,1,26),
(4538,'P','2014-07-23',1,NULL,'Rss','Твій хрест (24 липня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6031,200,thumb.jpg\" />Одному чоловікові видалося, що живе він дуже важко. І прийшов він якось до Бога і розповів про свої лиха й попросив у нього:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Можна, я виберу собі іншого хреста?</p>\n<p>Подивився Бог на людину з усмішкою, завів його до схову, де лежать хрести, та й каже:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2301:tvi-hrest-24-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4550,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4539,'P','2014-07-23',1,NULL,'Rss','Свята рівноапостольна княгиня Ольга','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6031,200,4851516_original.jpg\" />Княгиню Ольгу</strong> (у хрещенні Олену) Східна церква шанує як&nbsp;<strong>святу й рівноапостольну</strong>. Прийнявши&nbsp;<a href=\"http://blyzhchedoboga.com.ua/hristiyanstvo/\">християнство</a>, вона ненасильницькими методами сприяла його поширенню на Русі.</p>\n<p><strong>Ольга</strong> походила із Пскова й, бувши правнучкою Гостомисла, доводилася родичкою Олегові. У 903 р. її віддали заміж за Ігоря, сина Рюрика, майбутнього великого князя, якому тоді виповнилося 25 років. Оскільки вона була ще маленькою дівчинкою, шлюб спочатку був номінальним.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2302:svjata-rivnoapostolna-knjagynja-olga&catid=35:istorychni-postati-cat&Itemid=48',4551,NULL,1,26),
(4540,'P','2014-07-24',1,NULL,'Rss','Три віхи та три мрії з життя святої рівноапостольної княгині Ольги ','<p><img src=\"http://askoldova-mohyla.org/uk/image,6031,200,1251046073__AZZ2396-nester-olga-r4.jpg\" style=\"margin: 10px; float: left;\" />Коли дивишся на образ святої рівноапостольної княгині Ольги, чия пам\'ять вшановується24 липня, то бачиш перед собою не тільки мудрого державного правителя, а бачиш бабусю, яка виховала свого видатного онука — майбутнього Хрестителя Русі святого рівноапостольного князя Володимира.</p>\n<p>Можливо, саме з такої точки зору і треба дивитися на святу. Адже як християнка вона продемонструвала себе не в тому, як керувала державою, позаяк вона не вводила християнство як державну релігію, а в тому, як вона виховала свого онука. Те виховання принесло плоди — онук княгині Ольга урешті докорінно змінив історію Київської Русі, започаткувавши християнську державність, і від чийого Хрещення веде історію наша Церква.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2303:try-vihy-ta-try-mriji-z-zhyttja-svjatoji-rivnoapostolnoji-knjagyni-olgy-&catid=35:istorychni-postati-cat&Itemid=48',4552,NULL,1,26),
(4541,'P','2014-07-24',1,NULL,'Rss','«Молитва є невидимою благодатною ниткою нашої державної вишивки»','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6035,200,0.jpg\" style=\"margin: 10px; float: right;\" />«Чи став би Майдан Революцією Гідності без постійної присутності Церкви і духовного проводу?», – відповідь на це непросте питання шукає президент УКУ у своїй розмові з журналісткою газети «Львівська пошта» Ольгою Хворостовською.</strong></p>\n<p>«Ми, українці, десятиліттями живемо з півправдою, “подвійним дном”», – розповідає єпископ. На його думку, життя в неправді неплідне, не приносить жодної користі.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2304:2014-07-24-08-21-08&catid=28:tema-nomera-cat&Itemid=2',4553,NULL,1,26),
(4542,'P','2014-07-25',1,NULL,'Rss','Розбите скло (25 липня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6036,200,b9f00ca80de9f8c25592e11f1e8b8584.jpg\" style=\"margin: 10px; float: left;\" />Спитав якось наставник трьох учнів:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чому б’ється віконне скло, коли в нього кидають камінь?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тому, що камінь важкий, - відповів один.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тому, що скло крихке, - відповів інший.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тому, що рука, яка кидає камінь, була сильна, а кидок точним, - відповів третій.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2305:rozbyte-sklo-25-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4554,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4543,'P','2014-07-25',1,NULL,'Rss','Церква і Євросоюз','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6036,200,watikan-es-monety.jpg\" />Від самого початку Апостольська Столиця уважно слідкувала і підтримувала процес європейської інтеграції, що започаткували такі відомі люди як Конрад Аденауер, Альчіде де Гаспері або Роберт Шуман.</p>\n<p>Прагнучи підкреслити вагу євроінтеграції, у 1948 році папа Пій ХІІ проголосив св. Бенедикта покровителем Європи. Так само Павло VI багаторазово висловлював свою підтримку у справі творення спільної Європи, а нунцію в Брюселі, крім відносин із бельгійською владою, довірив співпрацю з європейськими інституціями.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2306:cerkva-i-jevrosojuz&catid=28:tema-nomera-cat&Itemid=2',4555,NULL,1,26),
(4544,'P','2014-07-25',1,NULL,'Rss','Маємо сумлінно і відповідально не тільки критикувати владу, але й її обирати і контролювати','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6039,200,huzar.jpg\" />Сьогодні найпопулярнішими в ЗМІ є три теми.</p>\n<p>Перша тема – усе, що в будь-який спосіб пов’язане з воєнними подіями у східних областях України. Це самозрозуміло, тому немає потреби цього пояснювати.</p>\n<p>Друга тема – нагальна необхідність піднести на відповідний рівень діяльність законодавчої гілки влади. За останній рік негідна поведінка представників цієї гілки влади призвела до стількох негативних наслідків у житті українського народу, що намагання змінити її склад – і якнайшвидше – стало цілком природним і необхідним.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2308:majemo-sumlinno-i-vidpovidalno-ne-tilky-krytykuvaty-vladu-ale--jiji-obyraty-i-kontroljuvaty&catid=37:spilnota-cat&Itemid=49',4557,NULL,1,26),
(4545,'P','2014-07-25',1,NULL,'Rss','Володимир Великий','<p><img src=\"http://askoldova-mohyla.org/uk/image,6040,200,12071.jpg\" style=\"margin: 10px; float: right;\" />В історії кожної європейської держави є свої основоположники, об’єднувачі і просвітителі. Для найближчих наших сусідів поляків — це князь Мєчислав (Мєшко). Для французів і німців — імператор Карл Великий. Для українців — князь Володимир Великий…</p>\n<p><strong>Через підступ — до влади</strong></p>\n<p>Він був позашлюбним сином князя Святослава і слов’янки Малуші, ключниці. Доти в руській княжій династії не траплялося слов’янських полукровок. Але у світі вікінгів це не вважалося чимось незвичним. Тесть Ярослава Мудрого, Олаф Скетконунг, мав за дружину доньку вождя поморян-ободритів, та й сам по матері був слов’янином — сином сестри польського князя Болеслава Хороброго Святослави (Сігріт Гордої).</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2310:volodymyr-velyky&catid=35:istorychni-postati-cat&Itemid=48',4558,NULL,1,26),
(4546,'P','2014-07-25',1,NULL,'Rss','«Єднаймося в дусі та ділі, щоб запобігти злу, яке поширюється на Європу та весь світ», – владика Борис (Ґудзяк)','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6041,200,maxresdefault.jpg\" />У листі-співчуття, з яким президент УКУ, єпископ єпархії Святого Володимира Великого в Парижі Борис (Ґудзяк) звернувся до сімей загиблих у малазійському \"Боїнгу 777\", ідеться про те, що терористи замахнулися на Богом дану гідність жертв, бо, окрім того, що обірвали їхні життя, ще й знущалися над тілами після смерті.</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2311:2014-07-25-19-07-06&catid=28:tema-nomera-cat&Itemid=2',4559,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4547,'P','2014-07-25',1,NULL,'Rss','Православний комунізм','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6041,200,04_reuter_2.jpg\" />У державі, яка воює з Україною, набирає обертів \"православний комунізм\"</p>\n<p>\"Наше завдання -- поєднати православ\'я і комунізм\". Це не жарт. Це цитата з виступу самопроголошеного \"губернатора Донбасу\" Губарева.</p>\n<p>Його біографія, серед іншого, є наочним прикладом, як на практиці реалізуються ці ідеологічні уявлення.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2312:pravoslavny-komunizm&catid=37:spilnota-cat&Itemid=49',4560,NULL,1,26),
(4548,'P','2014-07-25',1,NULL,'Rss','Ланцюжок і гребінчик (26 липня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6040,200,Bog-ye-lyubov.jpg\" />Жили собі в одному краї чоловік і жінка, їхня любов міцнішала щоднини. Вони вельми бідували, але кожен з них знав, що інший викохує в серці нездійснене бажання: у чоловіка був золотий годинник і він мріяв купити ланцюжка з такого самого дорогоцінного металу. Жінка мала довге м’яке русяве волосся і мріяла про гребінчика з перлиною, щоб носити його у волоссі як діадему.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2309:lancjuzhok-i-grebinchyk-26-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4561,NULL,1,26),
(4549,'P','2014-07-26',1,NULL,'Rss','Десять порад військовослужбовцю','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6044,200,10501590.jpg\" style=\"margin: 10px; float: right;\" />«Католицький Вісник» попросив про поради для військовослужбовців отця-єзуїта Андрія Зелінського, військового капелана та реколекціоніста, а також душпастиря молоді. Ділимося цим цікавим текстом також з вами.</strong><o:p></o:p></p>\n<p>1. У моторошні часи, коли безлад і страх довкола вас затьмарюють спокій і мир у вашому серці та у вашій свідомості, пам’ятайте, що тільки світло надії може розсіяти темряву.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2313:desjat-porad-viskovosluzhbovcju&catid=37:spilnota-cat&Itemid=49',4562,NULL,1,26),
(4550,'P','2014-07-26',1,NULL,'Rss','Сім етапів подружжя','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6045,200,Bog.jpg\" style=\"margin: 10px; float: left;\" />…Раптом усвідомлюєте, прокидаючись удосвіта, що починаєте роздумувати, а що ви взагалі знайшли у своєму партнері й чому вирішили провести з цією людиною все життя. Дійсність дається взнаки щоразу сильніше?</strong></p>\n<p>Соціологи та психологи стверджують: аби осягнути певну зрілість, людина з віком має пройти обов’язкові етапи розвитку. А&nbsp;подружжя&nbsp;— може, й воно дозріває поетапно?</p>\n<p>Вочевидь, так, хоч різні фахівці з проблем подружжя визначають це по-різному.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2321:sim-etapiv-podruzhzhja&catid=37:spilnota-cat&Itemid=49',4563,NULL,1,26),
(4551,'P','2014-07-26',1,NULL,'Rss','Євхаристійні чуда','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6045,200,the_bless.jpg\" />Перемінення Гостії в шматок людського тіла, &nbsp;вина в людську кров, багаторічні євхаристійні пости, левітації та чудесні зцілення - історія Церкви знає понад 130 чудес, що пов\'язані з Євхаристією. Трапляються вони в хвилинах сумнівів у реальну присутність Христа в Пресвятих Дарах, під час спроб профанації, але також як нагорода за віру і любов.</p>\n<p><strong>Тіло і Кров</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2322:jevharystini-chuda&catid=32:gk-svit-cat&Itemid=58',4564,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4552,'P','2014-07-26',1,NULL,'Note','Підтримайно наших воїнів!','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6048,200,NeMo_Post.jpg\" alt=\"\" width=\"200\" height=\"92\" />1 серпня 2014 року, учасники гурту &laquo;НеМо&raquo;, які є парафіянами нашої церкви, вирушають в зону АТО з благодійними концертами.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Крім того, музиканти будуть везти гуманітарну допомогу для наших воїнів.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6048,200,NeMo_Post.jpg\" alt=\"\" width=\"200\" height=\"92\" />1 серпня 2014 року, учасники гурту &laquo;НеМо&raquo;, які є парафіянами нашої церкви, вирушають в зону АТО з благодійними концертами.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Крім того, музиканти будуть везти гуманітарну допомогу для наших воїнів.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Звертаємось до парафіян з проханням, долучитися до збірки речей, які найбільше необхідні на даний час.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Це є засоби особистої гігієни (мило, зубна паста, зубні щітки, вологі серветки, нижня білизна, футболки, шкарпетки)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Також є велике прохання до парафіян нашої церкви, як дорослих так і дітей, написати листи нашим захисникам, а дітки можуть намалювати малюнки.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">З повагою, Анатолій&nbsp;Іванюк&nbsp;<a href=\"tel:0505488882\" target=\"_blank\">0505488882</a></span></p>',NULL,NULL,4565,5,1,17),
(4553,'P','2014-07-26',1,NULL,'Rss','Дзеркало (27 липня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6045,200,images.jpg\" style=\"margin: 10px; float: left;\" />Дуже давно один король збудував великий палац. То був палац з мільйонами дзеркал. Усі, усі стіни, підлоги і стелі були покриті дзеркалами.</p>\n<p>Одного разу у палац забіг собака. Озирнувшись він побачив безліч собак навколо себе. Собаки ули всюди. Бувши вельми розумним собакою він вискалив зуби, щоб про всяк випадок захиститися від тієї юрми собак, що його оточила. Усі пси вискалили зуби й собі. Він загарчав і ці загрозливо відповіли.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2316:dzerkalo-27-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4566,NULL,1,26),
(4554,'P','2014-07-27',1,NULL,'Rss','Святий Шарбель – святий цілого світу','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6036,200,30-373.jpg\" style=\"margin: 10px; float: right;\" />Високо в горах, десь приблизно на&nbsp;висоті тисяча шістсот метрів над рівнем моря і на відстані сто сорока кілометрів від столиці, розташоване селище&nbsp;Бекаа-Каффа. У 1828&nbsp;році то було звичайне гірське провінційне село в якому&nbsp;народився у сім’ї Маклуфів син Юсуф,&nbsp;майбутній всесвітньо відомий святий Шарбель.</strong></p>\n<p>Сім’я була відносно заможною, в ній було п’ятеро дітей, серед яких Юсуф був четвертою дитиною.&nbsp;Батька хлопець мало пам’ятав, бо його&nbsp;забрали на війну і він з неї не повернувся.&nbsp;Через деякий час мати знову вийшла заміж, тому вихованням Юсуфа займалися&nbsp;мати і вітчим, який мав на хлопця дуже&nbsp;великий позитивний вплив.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2307:svjaty-sharbel-svjaty-cilogo-svitu&catid=35:istorychni-postati-cat&Itemid=48',4567,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4555,'P','2014-07-27',1,NULL,'Rss','Тисяча кульок (28 липня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6045,200,x_d1e59096.jpg\" />Кілька тижнів тому я готував собі каву. Взяв ранішню газету і сів слухати радіо. Я повертав ручку шукаючи хвилю, поки мою увагу не привернув оксамитовий голос одного дідуся. Він щось казав про «тисячі кульок». Я зацікавився, зробив гучніше і відкинувся на спинку крісла ...<o:p></o:p></p>\n<p>- Добре, - сказав старий, - можу посперечатися, що ви дуже зайняті на роботі. Вчора, сьогодні, завтра. І нехай вам платять багато.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2317:tysjacha-kulok-28-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4569,NULL,1,26),
(4556,'P','2014-07-28',1,NULL,'Rss','Cвятий Володимир Великий','<p align=\"right\"><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6044,200,know_your_devotion_122.jpg\" /><strong>«Прийдіть вірні, заспіваймо духовну пісню і прославмо Христа,</strong></em><em><strong> </strong></em><em><strong>який світло прославив чесного Володимира Великого князя»</strong> (Канон утрені празника).</em></p>\n<p>Поміж великими і світлими мужами нашої княжої держави святий Володимир Великий, наче сонце, сяє своєю невмирущою славою, величчю і заслугами. В історії Руси-України його записано золотими літерами не тільки як могутнього і розумного володаря, але передусім як хрестителя, просвітителя і ревного апостола християнської віри. Свята віра, яку він прийняв у візантійському обряді, стає з часом серцем і душею нашого народу й держави. Вона стає сильною моральною основою нашого народу, основою нашої культури, звичаїв і обрядів.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2315:cvjaty-volodymyr-velyky&catid=35:istorychni-postati-cat&Itemid=48',4570,NULL,1,26),
(4557,'P','2014-08-27',0,NULL,'Note','Акція - «Шкільний портфелик»','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6051,200,school_img.jpg\" alt=\"\" width=\"200\" height=\"199\" />Парафіяльна служба &laquo;Карітас-Печерськ&raquo; закликає парафіян, людей доброї волі, навіть у цей складний для країни час, допомогти дітям у зібранні шкільного портфелика. Діти наше майбутнє і від нас залежить їх гідне виховання, навчання, просвітництво. Діти &ndash; це майбутнє нашої держави.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Портфелик, щоденник, підручник, зошит, ручка, фломастер, кольорові олівці &ndash; це основна зброя наших дітей, зброя проти безграмотності, проти обмеженості, проти неуцтва.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6051,200,school_img.jpg\" alt=\"\" width=\"200\" height=\"199\" />Дорогі парафіяни, люди доброї волі!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">За місяць часу наші діти змінять літні рюкзачки на шкільні портфелики. То ж допоможімо їм наповнитись тим радісним відчуттям дитинства - приготуванням власного портфелика до школи, новим підручниками, зошитами та кольоровими олівцями. У наш час, на превеликий жаль, є багато дітей, які не мають такої можливості. Це діти сироти, діти з малозабезпечених сімей, діти з проблемних сімей.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Парафіяльна служба &laquo;Карітас-Печерськ&raquo; закликає парафіян, людей доброї волі, навіть у цей складний для країни час, допомогти дітям у зібранні шкільного портфелика. Діти наше майбутнє і від нас залежить їх гідне виховання, навчання, просвітництво. Діти &ndash; це майбутнє нашої держави.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Портфелик, щоденник, підручник, зошит, ручка, фломастер, кольорові олівці &ndash; це основна зброя наших дітей, зброя проти безграмотності, проти обмеженості, проти неуцтва.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Допоможімо дітям гідно підготуватись до школи. Їхнє основне завдання сьогодні вчитись, а наше дати їм цю можливість, можливість здобувати знання.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Свої благочинні дарунки просимо приносити до церкви св. Миколая Мирлікійського на Аскольдовій Могилі. Щодень з 9 до 18 Вас зустрічатимуть волонтери.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ми щиро вдячні за Ваше розуміння та доброту сердець!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Парафіяльна служба милосердя &laquo;Карітас- Печерськ&raquo;</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p><a href=\"http://www.detdom.info/zbiraiemos-do-shkoli\">http://www.detdom.info/zbiraiemos-do-shkoli</a></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>',NULL,NULL,4568,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4558,'P','2014-07-28',1,NULL,'Rss','Двері (29 липня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6045,200,imag.jpg\" />Один мудрець шукав здібного і талановитого учня, щоб передати йому свої знання. І вирішив він зібрати всіх своїх численних учнів.</p>\n<p>Мудрець сказав їм:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; У мене виникла проблема і я хочу дізнатися, хто з вам може розв’язати її. Бачите – позаду мене найбільші, найтяжчі і наймасивніші двері в усьому місті. Хто з вас зможе відчинити їх без сторонньої допомоги.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2318:dveri-29-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4571,NULL,1,26),
(4559,'P','2014-07-29',1,NULL,'Rss','Карпатські стежки від «розчарування»','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6044,200,AD3EBE0.jpg\" />Гуцулка, яка пройшла сталінські концтабори, пропонує частіше згадувати про українських героїв</strong><strong></strong></p>\n<p><strong>«Дівчатка, якби хтось із вас пережив, — не забудьте за нас. Наші кістки хоч схоронені не будуть, але дух перед Богом проситиме за Україну. І Україна вийде без крові», — почула 27-річна Одосія, опритомнівши в бараку концтабору на Колимі. Поряд на долівці вмирали худющі люди, яким уже кілька днів не давали навіть хліба. Це було в 1949 році.</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2314:2014-07-26-09-00-04&catid=28:tema-nomera-cat&Itemid=2',4572,NULL,1,26),
(4560,'P','2014-07-29',1,NULL,'Rss','Митрополит Андрей Шептицький та український національний рух','<p align=\"right\"><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6055,200,481-9-4.jpg\" />Особлива позиція А. Шептицького та його оточення, т. зв. «кола Св. Юра», в роки німецької окупації стала підставою для сприйняття керівництва Греко-католицької церкви на тогочасній політичній арені як окремої політичної сили, на рівні з УЦК та ОУН. </strong></p>\n<p>Не дивлячись на щорічні міжнародні конференції, розмаїття сучасної літератури різних жанрів, радіо- і теле- передач, оцінка постаті глави Греко-католицької церкви (ГКЦ) Андрея Шептицького, а особливо його релігійної та суспільно-політичної діяльності в роки Другої світової війни, залишаються серед найбільш дискусійних тем не лише в середовищі українських та польських істориків, а й громадськості обох країн.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2348:mytropolyt-andre-sheptycky-ta-ukrajinsky-nacionalny-ruh&catid=28:tema-nomera-cat&Itemid=2',4573,NULL,1,26),
(4561,'P','2014-07-29',1,NULL,'Rss','Суспільно-політичні погляди Андрея Шептицького','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6055,200,images.jpg\" />Митрополит Андрей граф Шептицький увійшов в історію України не лише як релігійний, але також як визначний політичний діяч. Свою суспільно-політичну діяльність він розглядав насамперед з християнської точки зору - як вияв любові до Бога та ближнього.</p>\n<p>Вплив Шептицького на політичну історію України є безперечний, однак не слід забувати, що через ряд як об\'єктивних, так і суб\'єктивних факторів ідеї цього патріота-державника так і не були зреалізованими на сто відсотків –Вдадика Андрей двічі пережив національно-визвольне піднесення українського народу, і в обох випадках українська державність була втоплена окупантами в крові.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2347:suspilno-politychni-pogljady-andreja-sheptyckogo&catid=28:tema-nomera-cat&Itemid=2',4574,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4562,'P','2014-07-29',1,NULL,'Note','РОЗПОРЯДЖЕННЯ','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">у місячний строк розробити та затвердити відповідно до компетенції положення про службу військового духовенства (капеланську&nbsp;службу) у Збройних Силах, Національній гвардії та Державній прикордонній службі, визначивши, що священнослужителі, які запропоновані релігійними організаціями та успішно пройшли відбір, працевлаштовуються з укладенням відповідних трудових договорів у підрозділи Збройних Сил, Національної гвардії та Державної прикордонної служби, належать до персоналу Збройних Сил, Національної гвардії та Державної прикордонної служби і є військовими священиками (капеланами);</span></p>','<p style=\"text-align: center;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>КАБІНЕТ МІНІСТРІВ УКРАЇНИ</strong></span></p>\r\n<p style=\"text-align: center;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>РОЗПОРЯДЖЕННЯ</strong></span></p>\r\n<p style=\"text-align: center;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">від&nbsp;2&nbsp;липня&nbsp;2014 р. № 677-р</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Київ</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Про службу військового духовенства (капеланську службу) у Збройних Силах, Національній гвардії та Державній прикордонній службі</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">1. Міністерству оборони, Міністерству внутрішніх справ та Адміністрації Державної прикордонної служби:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">у місячний строк розробити та затвердити відповідно до компетенції положення про службу військового духовенства (капеланську&nbsp;службу) у Збройних Силах, Національній гвардії та Державній прикордонній службі, визначивши, що священнослужителі, які запропоновані релігійними організаціями та успішно пройшли відбір, працевлаштовуються з укладенням відповідних трудових договорів у підрозділи Збройних Сил, Національної гвардії та Державної прикордонної служби, належать до персоналу Збройних Сил, Національної гвардії та Державної прикордонної служби і є військовими священиками (капеланами);</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">здійснити у місячний строк після затвердження зазначених положень відбір осіб з числа священнослужителів, які запропоновані релігійними організаціями для забезпечення душпастирської опіки військовослужбовців і резервістів Збройних Сил, Національної гвардії та Державної прикордонної служби, згідно з вимогами таких положень;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">забезпечити організацію навчання військових священиків (капеланів) основ військової справи, а також їх військово-гуманітарну та військово-психологічну підготовку;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">щороку інформувати Кабінет Міністрів України і Міністерство культури про стан служби військового духовенства (капеланської&nbsp;служби) та подавати відповідну статистичну звітність.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">2. Міністерству соціальної політики підготувати пропозиції щодо доповнення Класифікатора професій професією, за якою забезпечується душпастирська опіка військовослужбовців і резервістів Збройних Сил, Національної гвардії та Державної прикордонної служби.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">3. Міністерству культури:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">сприяти координації співпраці релігійних організацій з Міністерством оборони, Міністерством внутрішніх справ, Адміністрацією Державної прикордонної служби з питань служби військового духовенства (капеланської&nbsp;служби);</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">проводити відповідний моніторинг та щокварталу подавати Кабінетові Міністрів України інформацію про проведену роботу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;<strong>Прем&rsquo;єр-міністр України&nbsp;&nbsp; А. ЯЦЕНЮК</strong></span></p>',NULL,NULL,4575,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4563,'P','2014-07-29',1,NULL,'Rss','Дім за карбованця (30 липня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6045,200,patriarh126.jpg\" />Покупцеві упав в око дім виставлений на продаж. Він спитав про ціну.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Один карбованець, - відповів продавець.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А чому так дешево? – здивувався покупець.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Бачите, річ у тім, – сказав продавець, - що покійна господиня наказала продати дім за мінімальну ціну за умови, що покупець пожертвує на дитячий будинок 10 мільйонів карбованців, а по-друге не буде розголошувати умови угоди. Такий заповіт.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2319:dim-za-karbovancja-30-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4576,NULL,1,26),
(4564,'P','2014-07-30',1,NULL,'Rss','Любомир Гузар: Это российско-украинская война, которую готовили 10 лет','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6051,200,e9b6f6c.JPG\" />Что сейчас на востоке страны — гражданское противостояние или война между Россией и Украиной?</strong></p>\n<p>— Это российско-украинская война. И этот конфликт не возник вчера, его последовательно готовили лет десять. Те, кто был при власти в Украине, в определённой степени были ставленниками России. И то, что мы сегодня имеем на востоке, это результат зомбирования людей, которых долгие годы кормили одним и тем же враньём. Они и поверили.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2349:ljubomyr-guzar-to-rossysko-ukraynskaja-vona-kotoruju-gotovyly-10-let&catid=28:tema-nomera-cat&Itemid=2',4577,NULL,1,26),
(4565,'P','2014-07-30',1,NULL,'Rss','Ланцюжок любови (31 липня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6045,200,psihologi.gif\" />Михайло їхав безлюдною дорогою, раптом він побачив на дорозі заглухлий мерседес. У ньому сиділа літня жінка, що виглядала цілковито розгубленою. Він зупинився перед її машиною, вийшов зі свого старого «понтіяка» і попрямував до сповненої відчаю жінки. Дама була злякана, бо за тривалий час, поки вона сиділа в машині, так ніхто біля неї і не спинився. А цей молодик, чи скривдить він її?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Мене звати Михайло, - відрекомендувався той, - я допоможу вам. Пересядьте поки що в моє авто – там вам буде трохи тепліше.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2320:lancjuzhok-ljubovy-31-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4579,NULL,1,26),
(4566,'P','2014-07-31',1,NULL,'Rss','І один у полі воїн','<p><em><img src=\"http://askoldova-mohyla.org/uk/image,6058,200,IM.jpg\" style=\"margin: 10px; float: right;\" />І один у полі воїн. Ці слова про дуже незвичайного і прогресивного львівського священика Степана Суса. Душпастиря, який уже з десяток років ламає, трощить, кришить усі «пострадянські» стереотипи про отців (хоча сам воліє називати себе не отцем, а братом). Навіть о третій ночі до нього за порадою звертаються прихожани, але він не відмовляє, більше того —&nbsp;вважає такі речі цілком нормальними.</em> <em>До нього телефонують навіть з Європи —&nbsp;колишні прихожани, а тепер – друзі.</em></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2350:i-odyn-u-poli-vojin&catid=27:gk-v-ukrajini-cat&Itemid=57',4580,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4567,'P','2014-07-31',1,NULL,'Rss','Порятунок від бурі (1 серпня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6045,200,FWEdDgJh9ks.jpg\" />Страшенна буря розгулялася на морі. Неподалік від берега, борючись із хвилями маленьке судно подавало сигнал біди. За мить відчалив рятівний човен на допомогу судну, що мало от-от загинути. Але спроби рятівників перемогти хвилі і доплисти до корабля були марні.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А що хлопці, чи не повернутися нам назад? – запропонував боцман, - Який сенс нам йти на неминучу смерть? Видно, не перемогти нам нині моря. І судно, і люди вже, напевне, погинули і їх не врятуєш.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2323:porjatunok-vid-buri-1-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4581,NULL,1,26),
(4568,'P','2014-08-01',1,NULL,'Rss','«Феномен гумору та сміху в культурно-релігійному контексті» - вступ','<p><img src=\"http://askoldova-mohyla.org/uk/image,6051,200,537581.jpg\" style=\"margin: 10px; float: right;\" />Дипломна робота нашої парафіянки, учасниці та переможця фестивалю-конкурсу «Аскольдів глас» Христини Шонь<strong> «Феномен гумору та сміху в культурно-релігійному контексті: теорія та практика» </strong>була написана ще кілька років тому і<strong> </strong>головним чином ставить питання про сміх як феномен. Тут мова йде, не про сміх як усмішку чи психологічний стан-реакцію, але радше сміх як предмет аналізу та роздумів – сміх у дещо іншому контексті. Ця тема є пізнавальною і глибокою за своїм змістом та значенням. Гадаємо, нашій читацькій аудиторії буде цікаво відкрити для себе новий світ «сміху», його сили, ваги. Робота Христини Шонь змістовна і ґрунтовна, тому подаватимемо її частинами.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2325:2014-07-28-07-48-21&catid=1:mystecka-storinka-cat&Itemid=50',4582,NULL,1,26),
(4569,'P','2014-08-01',1,NULL,'Rss','Розкладний ножичок (2 серпня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6052,200,3767535_c52dd690.jpg\" />Одного разу в школі о вчительки, що заходила у клас, підбігла заплакана дівчинка і сказала:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; У мене вкрали розкладний ножичок.</p>\n<p>Учителька спокійно відповіла:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Люба дівчинко, не слід спішити звинувачувати будь-кого в тому, що якусь річ украдено. На розраду я тобі розповім коротку історію.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2342:rozkladny-nozhychok-2-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4583,NULL,1,26),
(4570,'P','2014-08-02',1,NULL,'Rss','«Феномен гумору та сміху в культурно-релігійному контексті» - частина І','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6051,200,pointing-and-laughing3.jpg\" style=\"margin: 10px; float: right;\" />Розділ І. Теорії пояснення гумору та сміху: теоретична частина. </strong></p>\n<p><strong>1.1 Сміх за Кантом</strong></p>\n<p>Розгляд теоретичної частини про сміх варто розпочати із рефлексій німецького філософа Еммануїла Канта. Далі буде проаналізовано концепції про сміх та гумор у хронологічному зрізі – від Е. Канта до Х. Плеснера. Так, праця Е. Канта «Критика спроможності судження» вийшла 1788 р., праця А. Бергсона «Сміх» - 1899 р., З. Фрейда «Дотепність та її відношення до несвідомого» - 1905р., а Х. Плеснера «Сміх і плач» - 1970 р. Тому для початку Е. Кант.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2326:2014-07-28-07-51-48&catid=1:mystecka-storinka-cat&Itemid=50',4584,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4571,'P','2014-08-02',1,NULL,'Rss','Найкращий камінчик (3 серпня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6052,200,luxfon.jpg\" style=\"margin: 10px; float: left;\" />На березі моря бавився хлопчик він підіймав і кидав камінці в море. Час від часу перш ніж кинути камінці у пінисті хвилі, він пильно їх розглядав, деякі відкладав у торбинку. Як згодом з’ясувалося він робив це щоб показати їх свої матусі. Удома він витягував камінчик за камінчиком і захоплено показував свої знахідки. Наприкінці він витягнув останній і сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Це мій улюблений камінь, він мені сподобався найбільше. Такий зелений і гладенький.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2343:nakrashchy-kaminchyk-3-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4585,NULL,1,26),
(4572,'P','2014-08-03',1,NULL,'Rss','«Феномен гумору та сміху в культурно-релігійному контексті» - частина ІІ','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6051,200,childrens_photographer009.jpg\" />Розділ І. Теорії пояснення гумору та сміху: теоретична частина. </strong></p>\n<p><strong>1.2 Анрі Бергсон </strong><strong>vs</strong><strong> </strong><strong>Драган Куюнжич (інтерпретація Жака Дерріда).</strong></p>\n<p>Обговорити сміх, ще не означає сказати хто він є, адже сміх – невловимий<a href=\"http://oranta.org/file:///G:/smile/%D1%87%D0%B0%D1%81%D1%82%D0%B8%D0%BD%D0%B0_1.%20%D1%80%D0%BE%D0%B7%D0%B4%D1%96%D0%BB%201..docx#_ftn1\">[1]</a>. Важливо окреслити розрізнення між гумором та сміхом: останній, наприклад, характеризують як «непристойний шум, форма потворності, коли обличчя стає некрасивим. До гумору це не має жодного відношення»<a href=\"http://oranta.org/file:///G:/smile/%D1%87%D0%B0%D1%81%D1%82%D0%B8%D0%BD%D0%B0_1.%20%D1%80%D0%BE%D0%B7%D0%B4%D1%96%D0%BB%201..docx#_ftn2\">[2]</a>. Бергсон пише, що сміх є показником, що зусилля призвело до пустого місця<a href=\"http://oranta.org/file:///G:/smile/%D1%87%D0%B0%D1%81%D1%82%D0%B8%D0%BD%D0%B0_1.%20%D1%80%D0%BE%D0%B7%D0%B4%D1%96%D0%BB%201..docx#_ftn3\">[3]</a>.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2327:2014-07-28-07-55-21&catid=1:mystecka-storinka-cat&Itemid=50',4586,NULL,1,26),
(4573,'P','2014-08-03',1,NULL,'Rss','Ґудзик (4 серпня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6052,200,4546.jpg\" />Жив один чоловік і жив не вельми добре, не тямуще. Вирішив взятися за розум, добрі діла чинити, душу спасати. Чинив їх чинив, але змін на краще у самому собі і не помічав. Якось ішов він по вулиці аж бачить у бабусі ґудзик від пальто відірвався і впав на землю. Побачив, та й думає: «ну то й що, ґудзиків у неї досить. Не підіймати ж, яка дрібниця!»</p>\n<p>Але все таки буркочучи підніс ґудзика і віддав його бабусі та й забув про це.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2344:gudzyk-4-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4587,NULL,1,26),
(4574,'P','2014-08-04',1,NULL,'Rss','«Феномен гумору та сміху в культурно-релігійному контексті» - частина ІІІ','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6051,200,greece.jpg\" style=\"margin: 10px; float: right;\" />Розділ І. Теорії пояснення гумору та сміху: теоретична частина. </strong></p>\n<p><strong>1.3 Теорія гумору за Фрейдом</strong></p>\n<p>Фрейд, розглядаючи гумор, виокремлює кілька технік дотепності. Одна з них – техніка згущення з утворенням складеного слова.&nbsp; Наприклад: &nbsp;фаміліонерно – це буде результат&nbsp; фамільярно+мідіонерно, в результаті чого ми отримаємо гостру дотепність.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2328:2014-07-28-08-00-12&catid=1:mystecka-storinka-cat&Itemid=50',4588,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4575,'P','2014-08-04',1,NULL,'Rss','Молитва багатого (5 серпня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6052,200,molit.jpg\" />Один багатий селянин, чиї комори тріщали від зерна, часто промовляв звичну оливу «про бідних та нужденних». Молячись він завжди долучав таке прохання: - «Боже, згадай про бідних та голодних і заспокой їхню потребу». Однак сам селянин не допомагав нікому і тільки й чекав, що би це все зробив Бог.</p>\n<p>Одного разу, коли селянин знову промовив свою молитву «про бідних і голодних» син сказав до нього:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2345:molytva-bagatogo-5-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4589,NULL,1,26),
(4576,'P','2014-08-05',1,NULL,'Rss','«Феномен гумору та сміху в культурно-релігійному контексті» - частина ІV','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6051,200,baby-blue-smile-eyes.jpg\" />Розділ І. Теорії пояснення гумору та сміху: теоретична частина. </strong></p>\n<p><strong>1.4 Сміх у розробках Хельмута Плеснера</strong></p>\n<p>Сміх, подібно як і плач, є кордонною реакцією, себто є реакцією на ті кордони, в котрі вписані людські вчинки, обумовлені притаманною їм доцільністю і мовою. Сміх залучає в характерний для особи тип діяльності щось, що є абсолютно для неї невідповідне. Така реакція стає можливою за рахунок того, що відбувається змушена відмова від характерних та типових форм регульованої поведінки. Тобто, сміх – це ексцентрична позиційність<a href=\"http://oranta.org/file:///G:/smile/%D1%87%D0%B0%D1%81%D1%82%D0%B8%D0%BD%D0%B0_1.%20%D1%80%D0%BE%D0%B7%D0%B4%D1%96%D0%BB%201..docx#_ftn1\">[1]</a>.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2329:2014-07-28-08-02-10&catid=1:mystecka-storinka-cat&Itemid=50',4590,NULL,1,26),
(4577,'P','2014-08-06',1,NULL,'Rss','«Феномен гумору та сміху в культурно-релігійному контексті» - частина V','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6051,200,mom-tickles-daughter-to-make-her-laugh.jpg\" />Після ознайомлення з першою частиною теоретичних роздумів про феномени гумору, сміху та комічного, ми перейдемо до ознайомлення із місцем та поняття сміху та дотичних феноменів у давньогрецькій культурі. Дана частина є сконцентрована на таких поняттях, як: іронія, сатира, гумор, насміх. Особливо цікавими є постаті богів, котрі сміються, але зовсім не так, як би (з погляду християнства) мали сміятись боги. Отож, приємного вам прочитання та мудрих висновків</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2330:2014-07-28-08-09-17&catid=1:mystecka-storinka-cat&Itemid=50',4592,NULL,1,26),
(4578,'P','2014-08-07',1,NULL,'Rss','«Феномен гумору та сміху в культурно-релігійному контексті» - частина VІ','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6051,200,dionysus-greek-god-of-wine.jpg\" style=\"margin: 10px; float: right;\" />Розділ ІІ. Феномен сміху та гумору у культурно-релігійному контексті.</strong></p>\n<p><strong>2.1.2 Симпозіум – як прототип божественних бенкетів</strong></p>\n<p>Тепер ми закцентуємо нашу увагу на визначальній ролі сміху на&nbsp; симпозіумах – з огляду на такі його характеристики, як певною мірою протокольність, етика сміху та психологія. Річ у тім, що на бенкетах сміх мав свою специфіку. Характеризуючи поняття «сміятися», бачимо несумірність, неузгодження, котрі вражають око профана<a href=\"http://oranta.org/file:///G:/smile/%D1%80%D0%BE%D0%B7%D0%B4%D1%96%D0%BB2_%D1%87.1_%D0%B3%D1%80%D0%B5%D0%BA%D0%B8.docx#_ftn1\">[1]</a>. Багато архаїчних та класичних мислителів сприймали симпозіум як спектр ідей, котрі бувають від напівблаженного щастя аж до страху перед&nbsp; жорстокою деструктивною силою.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2331:2014-07-28-08-11-44&catid=1:mystecka-storinka-cat&Itemid=50',4594,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4579,'P','2014-08-08',1,NULL,'Rss','Павучі сіті (8 серпня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6065,200,d0bf.jpg\" />Спостерігаючи кілька днів поспіль за летом мушви, павук помітив, що здебільшого вона хмарами висить над виноградником.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тепер мушві буде непереливки! – посміхався павук і повз у гору по лозі.</p>\n<p>Він приплів міцну сіть до одного з найбільших духмяних грон, а сам сховався в заглибині між прохолодними виноградинами. Зі свого сховку хитрий розбійник тишком нишком нападав на безтурботних мушок, і цей підступ минався йому.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2352:pavuchi-siti-8-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4595,NULL,1,26),
(4580,'P','2014-08-08',1,NULL,'Rss','«Феномен гумору та сміху в культурно-релігійному контексті» - частина VІІ','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6051,200,Thu.JPG\" />Розділ ІІ. Феномен сміху та гумору у культурно-релігійному контексті.</strong></p>\n<p><strong>2.1.3 Смерть і життя – їхня співвідносність у гуморі</strong></p>\n<p>Поряд з уявленнями про симпозіум як місце святкування, піднесення та відновлення духу, є також сприйняття симпозіумів у їхньому трагізмі, з його натяками на смерть. Тож далі ми розглянемо трагізм та інфантильність сміху через призму симпозіумних святкувань. Вони були пов’язані зі смертю: саме на симпозіумах сміх проявляв властивості фармакону – сміх може вбивати. Він набирає гранично допустимих норм, бо симпозіуми дають йому таку можливість.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2332:2014-07-28-08-15-21&catid=1:mystecka-storinka-cat&Itemid=50',4596,NULL,1,26),
(4581,'P','2014-08-09',1,NULL,'Rss','«Феномен гумору та сміху в культурно-релігійному контексті» - частина VІІІ','<p><img src=\"http://askoldova-mohyla.org/uk/image,6053,200,Greg.jpg\" style=\"margin: 10px; float: right;\" />На черзі, мабуть, найцікавіший розділ, адже мова далі – про християнський вимір сміху: як і чому сміються, а може, якраз і не сміються християни. Розглядати будемо роль «сміху» та «гумору» у християнській культурі у період її «перед-історії» (час Старого Завіту) та періоді становлення і формування (від народження Христа і аж до часів Середньовіччя). Цікаво, що смисл та функціональне призначення визначених понять змінювались та трансформувались. Отож, вашій увазі – прослідкувати зміни у канонах сміху , котрі далі буде окреслено. Приємного вам прочитання та мудрих висновків!</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2333:2014-07-28-08-24-12&catid=1:mystecka-storinka-cat&Itemid=50',4597,NULL,1,26),
(4582,'P','2014-08-10',1,NULL,'Rss','«Феномен гумору та сміху в культурно-релігійному контексті» - частина ІХ','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6053,200,jesuslindabissett.jpg\" />Розділ ІІ. Феномен сміху та гумору у культурно-релігійному контексті.</strong></p>\n<p><strong>2.2.1. Варіативність позицій стосовно сміху</strong><strong>/</strong><strong>не-сміху Христа</strong></p>\n<p>Один з варіантів тлумачення сміху – сміх як спосіб звільнення від несвободи. У Богочоловіка Христа не було «зайвої» енергії, котра би мала бути звільнена. Саме тому, Христос не міг сміятись, - вважає С. Аверінцев. Сміх на його думку є подією динамічною – одночасним рухом розуму і нервових мускулів. Він говорить про сміх як неймовірної сили динаміку, наводячи за приклад метафору «вибух сміху»<a href=\"http://oranta.org/file:///G:/smile/%D1%87%D0%B0%D1%81%D1%82%D0%B8%D0%BD%D0%B02_%D1%87.2_%D1%85%D1%80%D0%B8%D1%81%D1%82%D0%B8%D1%8F%D0%BD%D1%81%D1%82%D0%B2%D0%BE.docx#_ftn1\">[1]</a>. С.&nbsp;Аверінцев пояснює сміх як феномен, для творення котрого потрібні духовний та фізичний аспекти єства. Тому сміх є переходом, котрий веде до звільнення.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2334:2014-07-28-08-32-11&catid=1:mystecka-storinka-cat&Itemid=50',4598,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4583,'P','2014-08-11',1,NULL,'Rss','«Феномен гумору та сміху в культурно-релігійному контексті» - частина Х','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6053,200,JesusfromHPscanof14x7-1.jpg\" />Розділ ІІ. Феномен сміху та гумору у культурно-релігійному контексті.</strong></p>\n<p><strong>2.2.2. Можливе вирішення проблеми у Біблії, Апокрифі від Юди та аскетичному трактуванні</strong></p>\n<p>Вартує уваги і той факт, що про сміх Христа є згадка у неканонічній християнській традиції – в «Євангелії від Юди». Дане джерело не є таким, щоб на нього посилатись, бо Христос згідно тексту Юди є дещо іншим, порівняно з канонічною християнською традицією (як говорить Святе Письмо).</p>\n<p>Розглядаючи питання апокрифу, побачимо, що даний текст має багато натяків на гностицизм, через що декотрі дослідники трактують «Євангеліє від Юди» як відверто антихристиянський твір (адже гностична течія засуджується&nbsp; Церквою).</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2335:2014-07-28-08-39-21&catid=1:mystecka-storinka-cat&Itemid=50',4599,NULL,1,26),
(4584,'P','2014-08-12',1,NULL,'Rss','«Феномен гумору та сміху в культурно-релігійному контексті» - частина ХІ','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6053,200,main_10.jpg\" />На черзі маємо третю із обраних культур – арабо-мусульманську. Акцент поставлено на феномені сміху та гумору, іронії та сарказму, а якщо точніше – як саме вони взаємодіють із мусульманськими релігійними уявленнями. Заглибитись у дану проблематику можна через Старий Завіт, Коран та деякі приклади художньої літератури. У арабо-мусульманській культурі образ Бога, що сміється, дещо відрізняється від радісного Ісуса у християнстві, а саме поняття гумору трактується теж інакше. В чому полягає така принципова різниця у двох авраамічних релігіях буде краще зрозуміло після ознайомлення з матеріалом. Отож, приємного вам прочитання та мудрих висновків!\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2336:2014-07-28-08-50-48&catid=1:mystecka-storinka-cat&Itemid=50',4600,NULL,1,26),
(4585,'P','2014-08-13',1,NULL,'Rss','«Феномен гумору та сміху в культурно-релігійному контексті» - частина ХІІ','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6053,200,ww.jpg\" style=\"margin: 10px; float: right;\" />Розділ ІІ. Феномен сміху та гумору у культурно-релігійному контексті.</strong><strong></strong></p>\n<p><strong>2.3.2. Персональний гумор у Корані: Авраам, Сара, Аллах.</strong></p>\n<p>Почнемо із постаті Авраама, який попри свою особливість у очах Аллаха, був не просто смиренним, але і міг «стібатись». Мова йде про дотепність, котру Авраам придумав, аби у гумористичному варіанті висміяти ідолів (їм дуже хотіли поклонятись ізраїльтяни).</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2337:2014-07-28-08-54-48&catid=1:mystecka-storinka-cat&Itemid=50',4601,NULL,1,26),
(4586,'P','2014-08-14',1,NULL,'Rss','Маковея','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6051,200,makovii_spas.jpg\" />14 серпня за новим стилем в народі святкують свято Маковея, яке в церковному календарі називається днем семи мучеників Маковеїв. Пам\'ять семи мучеників: Авіма, Антоніна, Гурія, Єлеазара, Євсевона, Аліма і Маркела, їх матері Соломії та вчителя їх Єлеазара, які в 166 р до н. е. очолили повстання за віру в єдиного Бога і були за це жорстоко покарані.</p>\n<p>Це свято прийшло до нас з Візантії, де було встановлене не пізніше ІХ ст. Воно полягало в перенесенні з імператорського палацу до храму Софії частини хреста Господнього, що збереглася.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2324:makoveja&catid=28:tema-nomera-cat&Itemid=2',4602,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4587,'P','2014-08-15',1,NULL,'Rss','«Феномен гумору та сміху в культурно-релігійному контексті» - частина ХІІІ','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6053,200,13309368.jpg\" style=\"margin: 10px; float: right;\" />Розділ ІІ. Феномен сміху та гумору у культурно-релігійному контексті.</strong><strong></strong></p>\n<p><strong>2.3.3.Співвідношення гумору та сміху у праведників та поган ісламу</strong></p>\n<p>Коран численно ілюструє комічні ситуації при зіставленні поведінки праведників та поган. Наприклад, під час блискавок та громів<a href=\"http://oranta.org/file:///G:/smile/%D1%87%D0%B0%D1%81%D1%82%D0%B8%D0%BD%D0%B02_%D1%80.3_%D0%BC%D1%83%D1%81%D1%83%D0%BB%D1%8C%D0%BC%D0%B0%D0%BD%D1%81%D1%8C%D0%BA%D0%B8%D0%B9.docx#_ftn1\">[1]</a>. Так, погани позатуляли вуха, коли гримів грім – так, начебто це мало врятувати їх від страху та смерті.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2338:2014-07-28-10-27-52&catid=1:mystecka-storinka-cat&Itemid=50',4603,NULL,1,26),
(4588,'P','2014-08-16',1,NULL,'Rss','«Феномен гумору та сміху в культурно-релігійному контексті» - частина ХІІІІ','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6053,200,laughing-children.jpg\" />Розділ ІІ. Феномен сміху та гумору у культурно-релігійному контексті.</strong><strong></strong></p>\n<p><strong>2.3.4. Нерелігійний вимір гумору в культурі.</strong></p>\n<p>Тепер проаналізуємо гумор у арабо-мусульманській культурі. Важливо, що поняття гумору в мусульманських країнах не є однозначним. Так, з одного боку (теоретичний підхід), сміх не є допустимим, схвальним або таким, який би цінували. З іншого боку (практична сторона) без сміху, жартів та маси фольклорних надбань – в котрих є елемент гумору – важко зрозуміти культуру ісламу<a href=\"http://oranta.org/file:///G:/smile/%D1%87%D0%B0%D1%81%D1%82%D0%B8%D0%BD%D0%B02_%D1%80.3_%D0%BC%D1%83%D1%81%D1%83%D0%BB%D1%8C%D0%BC%D0%B0%D0%BD%D1%81%D1%8C%D0%BA%D0%B8%D0%B9.docx#_ftn1\">[1]</a>.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2339:2014-07-28-10-34-17&catid=1:mystecka-storinka-cat&Itemid=50',4604,NULL,1,26),
(4589,'P','2014-08-17',1,NULL,'Rss','«Феномен гумору та сміху в культурно-релігійному контексті» - частина ХІV','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6053,200,54654.jpg\" />Отож, тепер перейдемо до наших висновків. І як завше, бажаю приємного вам прочитання!</p>\n<p><strong>Аналіз анкетування «Сміх як феномен» серед студентів Національного університету «Києво-Могилянська Академія». </strong></p>\n<p>Саме анкетування варто розглядати як емпіричний виміру феномену сміху. Дане опитування було проведене у січні-лютому 2013 р. серед студентів НаУКМА, в котрому взяли участь близько ста респондентів.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2340:2014-07-28-10-57-32&catid=1:mystecka-storinka-cat&Itemid=50',4605,NULL,1,26),
(4590,'P','2014-08-18',1,NULL,'Rss','«Феномен гумору та сміху в культурно-релігійному контексті» - частина ХV','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6053,200,68-baby-smile.jpg\" />Висновки роботи </strong></p>\n<p>У даній дипломній роботі було досягнуто поставлених завдань: було розглянуто обрані для аналізу поняття, також висвітлено їх у культурному контексті (релігійному та часовому зрізі). Відповідно, найбільше уваги було акцентовано на феномені гумору та сміху. Також було розглянуто дотичні поняття, серед яких: іронія, дотеп, комічне, «сальність», насмішка, глузування, жарт, радість. Для цього було опрацьовано джерела та матеріали: Е. Канта, А. Бергсона, З. Фрейда, С.&nbsp;Халлівела, С.&nbsp;К’єркегора, Дж. Тамера, С. Аверінцева, Л. Карасьова, Й. Золотоустого, Д.&nbsp;Куюнжича, Х. Плеснера.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2341:2014-07-28-11-01-09&catid=1:mystecka-storinka-cat&Itemid=50',4606,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4591,'P','2014-08-25',1,NULL,'Rss','Звернення Блаженнішого Святослава до вірних Української Греко-Католицької Церкви та всіх українців з нагоди Дня Незалежності України','<p style=\"text-align: center;\"><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6089,200,den_nezav_2.jpg\" />Дорогі в Христі!<o:p></o:p></strong></p>\n<p>Цього року Всевишній знову сподобив нас радості святкувати Свято Незалежності нашої Батьківщини України. У нових драматичних, але водночас величних історичних обставинах, які нам доводиться сьогодні переживати, ми по-новому відкриваємо для себе неоціненність скарбу жити у вільній, суверенній і незалежній державі.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2354:zvernennja-blazhennishogo-svjatoslava-do-virnyh-ukrajinskoji-greko-katolyckoji-cerkvy-ta-vsih-ukrajinciv-z-nagody-dnja-nezalezhnosti-ukrajiny&catid=27:gk-v-ukrajini-cat&Itemid=57',4607,NULL,1,26),
(4592,'P','2014-08-25',1,NULL,'Rss','Сарана (25 серпня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6089,200,2014-01-15-3.jpg\" />Молодий чернець прийшов до старця за порадою.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Отче, - сказав він, - я живу в пустелі трохи більше ніж рік і за той час вже 5 чи 6 разів з’являлася сарана. Ти знаєш, що це за напасть? Вона залазить усюди, навіть у харч. Що мені робити?</p>\n<p>Старець, який жив у пустелі вже40 років відповів:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2353:sarana-25-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4608,NULL,1,26),
(4593,'N','2014-08-31',1,NULL,'Rss','Один із двох (31 серпня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6090,200,2801880763_bd18d75cf4.jpg\" style=\"margin: 10px; float: left;\" />Одного разу, верблюд пішов до намету. і засунув туди свого носа. Але оскільки ніхто його не гнав, він пропхав туди пів свого тулуба. Трохи згодом, верблюд зайшов у намет весь. Повернувшись, господар промовив до верблюда:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Для двох мешканців цей намет замалий.</p>\n<p>Верблюд відповів:</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2363:odyn-iz-dvoh-31-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4609,NULL,1,26),
(4594,'P','2014-08-19',1,NULL,'Rss','Преображення Господнє','<p><em><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6090,200,preobrazeniye_ivanova.jpg\" />\"Величаємо Тебе, життєдавче Христе, і почитаємо пречистого Твого Тіла преславне Преображення\" (Величання на утрені празника).</strong></em><o:p></o:p></p>\n<p>У цей день наша Церква святкує празник світлого Господнього&nbsp; Преображення. Завдання цього празника — звеличувати славну подію&nbsp; Преображення з життя Ісуса Христа, яке деякі святі Отці називають другим&nbsp; Богоявленням. Важливість події видно з того, що її записали аж три&nbsp; євангелисти: Матей, Марко й Лука. Тож погляньмо на подію Преображення,&nbsp; на установлення празника та на дух його богослужби.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2364:preobrazhennja-gospodnje&catid=27:gk-v-ukrajini-cat&Itemid=57',4610,NULL,1,26),
(4595,'P','2014-08-20',1,NULL,'Rss','Наш час настав','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6090,200,Poroshenko.jpg\" />Розмірковування на тему християнина і політики завжди здавалися мені марнуванням часу. Ну як можна з-поміж учасників політичних процесів виділяти якусь окрему групу за принципом віросповідання? Що, для них діють інші закони, інші можливості, інші цілі?</strong></p>\n<p>Але, якби там не було, в сучасній Україні настав такий час, коли християни можуть сказати своє слово в політичному житті трохи по-іншому. Мовиться не про теоретизування на тему добра і зла чи моральності, а про цілком свідому і продуктивну дію, покликану вирішити дві основні проблеми української політики.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2365:nash-chas-nastav&catid=37:spilnota-cat&Itemid=49',4611,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4596,'P','2014-08-21',1,NULL,'Rss','Євхаристія','<p><em><img src=\"http://askoldova-mohyla.org/uk/image,6090,200,flat550x550075f.jpg\" style=\"margin: 10px; float: right;\" />І, взявши хліб, віддав хвалу, поламав, дав їм і мовив: \"Це – Моє тіло, що за вас віддається. Чиніть це на Мій спомин.\" Так само чашу по вечері, кажучи: \"Ця чаша ~ це Новий Завіт у Моїй крові, що за вас проливається.\" (Лк. 22, 19-20)</em></p>\n<p>Євхаристія – таїнство, яке становить центр життя християнської спільноти. Де є Євхаристія, там є Церква. Таким було розуміння цього таїнства у часи перших християн, і до сьогодні воно не змінилося.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2366:jevharystija&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4612,NULL,1,26),
(4597,'P','2014-08-22',1,NULL,'Rss','Папа прийшов з кінця світу і живе у Ватикані','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6090,200,pres-konferenciia-14.jpg\" />На борту літака під час повернення з Кореї до Ватикану Папа Франциск відповів на 16 питань журналістів, більшість яких стосувалися актуальних міжнародних подій і особистих переживань Святішого Отця.</strong></p>\n<p><em><strong>Під час візиту до Кореї Ви звернулися до родин загиблих у катастрофі парому «Севол» зі словами утіхи. Два питання: Що ви відчували, коли зустрілися з ними? І чи Ви не стурбовані тим, що Ваші дії можуть неправильно витлумачити?</strong></em></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2367:papa-pryshov-z-kincja-svitu-i-zhyve-u-vatykani&catid=32:gk-svit-cat&Itemid=58',4613,NULL,1,26),
(4598,'P','2014-08-24',1,NULL,'Rss','Звернення Глави УГКЦ Блаженнішого Святослава до світової громадськості з приводу ситуації в Україні','<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6090,200,shevchuk_vladyka.jpg\" />Католицьким єпископським конференціям,&nbsp;</em><em style=\"font-size: 0.875em;\">Світовим релігійним та політичним лідерам,&nbsp;</em><em style=\"font-size: 0.875em;\">Усім людям доброї волі</em></p>\n<p><o:p></o:p></p>\n<p>Впродовж девʼяти місяців українці перебувають на важкому шляху від постсовєтського страху до свободи і Богом даної гідності. Травмовані Світовими війнами двадцятого століття, коричневим та червоним тоталітаризмами і геноцидом, вони прагнуть справедливого суспільства та демократичного, європейського майбутнього. З терпеливістю, витривалістю та великими людськими жертвами вони подолали в лютому брутальний режим Віктора Януковича. У відповідь на цей моральний тріумф у березні відбулась російська територіальна анексія Криму.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2368:zvernennja-glavy-ugkc-blazhennishogo-svjatoslava-do-svitovoji-gromadskosti-z-pryvodu-sytuaciji-v-ukrajini&catid=27:gk-v-ukrajini-cat&Itemid=57',4614,NULL,1,26),
(4599,'P','2014-08-23',1,NULL,'Rss','БФ «Карітас Київ» надав допомогу 12 геріатричним центрам','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6090,200,OG_2014_04_29_396878_Small.jpg\" />«БФ «Картіас Київ» надав допомогу людям похилого віку, які перебувають у Геріатричних центрах у різних регіонах України. Ця допомога стала можливою завдяки співпраці з компанією Київстар та Українською Біржою Благодійності», - повідомляє о. Роман Сиротич, директор БФ «Карітас Київ». <o:p></o:p></p>\n<p>Допомога та підтримка людей похилого віку – один з дуже важливих напрямків діяльності БФ «Карітас Київ».&nbsp;Карітас вже багато років допомагає самотнім людям, інвалідам, пенсіонерам. Зокрема, на сьогоднішній день, під опікою Карітасу близько 100 осіб.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2369:2014-08-25-17-31-00&catid=29:propovidi-cat&Itemid=34',4615,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4600,'P','2014-08-26',1,NULL,'Rss','Букет (26 серпня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6089,200,thum.jpg\" />Одному учневі весь час не давала спокою одне питання: - як у людському житті поєднуються божественне значення і свобода вибору? З цим питанням він звернувся до вчителя і той відповів.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Вирушаючи в поле збирати квіти, ти не можеш бути впевненим ані того якою дорогою підеш, ані з яких квітів збереш свій букет. І ти йдеш від квітки до квітки прислухаючись до внутрішнього голосу і зриваєш їх, складаючи до купи.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2355:buket-26-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4616,NULL,1,26),
(4601,'P','2014-08-26',1,NULL,'Rss','«Під кулями невіруючих нема»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6099,200,ot1.jpg\" />Медичний капелан із Долини отець Ігор Ліпчанський був одним із перших священиків, яких направили до Харківського військового госпіталю. Саме туди потрапляє більшість поранених українських вояків із зони АТО. Про роботу військового капелана, віру та війну, страх і силу духу він розповів «Репортеру».</p>\n<p><strong><em>– Отче Ігорю, як ви потрапили у шпиталь?</em></strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2376:2014-08-26-19-16-06&catid=28:tema-nomera-cat&Itemid=2',4617,NULL,1,26),
(4602,'P','2014-08-27',1,NULL,'Rss','Жабеня в колодязі (27 серпня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6090,200,d8996d5-01.jpg\" style=\"margin: 10px; float: left;\" />Маленьке жабеня народилося на дні колодязя. То був маленький круглий колодязь, який стоїть у кожному селі. І це жабеня було таке раде, воно гралося на дні і думало, що має всю воду світу.</p>\n<p>Але одного разу, трішки вирісши, воно вирішило піти в мандри. Побачивши над головою світло, жабеня зважилося вилізти з колодязя. І озирнувшись довкола побачило ставок. Воно не йняло віри своїм очам. Ставок був у тисячу разів більшим від того місця де воно жило. Жабеня набралося відваги ще раз і прийшло до велетенського озера. Воно завмерло перед ним не знаючи що й думати.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2356:zhabenja-v-kolodjazi-27-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4619,NULL,1,26),
(4603,'P','2014-08-27',1,NULL,'Rss','Празник Успіння Пресвятої Богородиці','<p align=\"right\"><em><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6101,200,555.jpg\" />«Величаємо Тебе, Пренепорочна Мати Христа Бога нашого,&nbsp; і славимо Твоє всеславне Успення» (Величання празника).</strong></em></p>\n<p>Предавній, загальний і глибокий культ Пресвятої Богородиці у Східній Церкві залишив особливий слід передусім на нашому літур­гійному році. Церковний рік не тільки багатий на різні Богородичні празники, але він також ними починається і завершується, його починає празник Різдва Пресвятої Богородиці, а завершує її славне Успення, що в наших літургійних книгах має таку величну назву — Успення Пресвятої славної Владичиці нашої Богородиці і Присно­діви Марії.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2378:praznyk-uspinnja-presvjatoji-bogorodyci&catid=28:tema-nomera-cat&Itemid=2',4620,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4604,'P','2014-08-28',1,NULL,'Rss','Равлик на вишні (28 серпня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6090,200,best-wallpaper_skobka_75.jpg\" />Майстра спитали чи відчуває він подеколи розчарування через те, що його зусилля не такі плідні як йому хотілося б. Відповідаючи, майстер відповів історію про равлика який поліз на вишню у холодний вітряний день запізнілої весни.</p>\n<p>Горобці на сусідньому дереві від щирого серця тішились дивлячись на нього. Потім один підлетів і каже:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2360:ravlyk-na-vyshni-28-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4621,NULL,1,26),
(4605,'P','2014-08-28',1,NULL,'Rss','Літній відпочинок дітей з церкви святого Миколая в Карпатах','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6101,200,8.JPG\" />З п’ятого серпня цього року протягом десяти днів діти та молодь церкви святого Миколая мали можливість відпочивати в Карпатах на Драгобраті. Табір називався відродженням. Разом з нами їхало п’ятеро дорослих. Протягом цього часу ми мали змогу духовно і фізично збагатитися. В цій карпатській поїздці взяло участь 16 людей.</p>\n<p>Кожний наш день розпочинався зі спільної руханки, яку проводила пані Світлана. Крім цього, таборяни мали змогу зіграти в якусь цікаву гру, щоб підняти настрій на цілий день. Після ранкової зарядки усі разом співали гімн України під майорінням синьо – жовтого прапора.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2379:litni-vidpochynok-dite-z-cerkvy-svjatogo-mykolaja-v-karpatah&catid=25:parafija-cat&Itemid=27',4622,NULL,1,26),
(4606,'P','2014-08-29',1,NULL,'Rss','«Запорукою нашої перемоги є Діва Марія»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6106,200,dsc_0039_258bf_0.jpg\" />Багато хто питає себе: а куди ж ми зайдемо? Якою ця дорога буде? Скільки жертв ми мусимо скласти, щоб достойно її завершити? На ці питання ми сьогодні не маємо відповіді і, можливо, ніхто не має. Але відповіді на ці питання дає нам сьогодні Пречиста Діва Марія. Вона, як мати, каже нам: не бійтеся, ідіть. Я іду з вами, я не пускаю вас самих.</p>\n<p>Про це сказав Отець і Глава Української Греко-Католицької Церкви Блаженніший Святослав (Шевчук) на празник Успіння Пречистої Діви Марії під час прощі в Унівській лаврі (Львівщина).</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2380:2014-08-29-18-42-49&catid=27:gk-v-ukrajini-cat&Itemid=57',4624,NULL,1,26),
(4607,'P','2014-08-30',1,NULL,'Rss','Вовк, лисиця і заєць (30 серпня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6090,200,301892.jpg\" style=\"margin: 10px; float: left;\" />Одного разу, влітку, троє селян вийшли на високе урвище. Внизу зеленіли кущі терену і вздовж піщаного берегу текла ріка. Один із селян гукнув:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Дивіться, у кущах вовк сидить.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Для вовка надто жовтий, заперечив інший. Здається це лисиця.</p>\n<p>Третій селянин придивився і сказав:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2362:vovk-lysycja-i-zajec-30-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4625,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4608,'P','2014-08-30',1,NULL,'Rss',' «Сходи Якова»','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6108,200,0223AB5B.png\" />У Києві відбувся допрем’єрний показ кінострічки «Сходи Якова». Героями документального фільму стали троє однокласників – колишній глава УГКЦ Любомир Гузар, син командира УПА Юрій Шухевич та медик в третьому поколінні Борис Білинський. Їхні життєві шляхи перетнулися через 70 років у львівській кав’ярні на даху старовинного будинку, де вони розмірковують через призму свого життя про долю України.</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2381:2014-08-30-20-14-17&catid=35:istorychni-postati-cat&Itemid=48',4626,NULL,1,26),
(4609,'P','2014-08-31',1,NULL,'Rss','Як звільнитися від страхів?','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6092,200,d1079605901.jpg\" />На відміну від невротичного страху, який діє деструктивно, існує нормальний екзистенційний ляк, і треба вміти його прийняти.</strong></p>\n<p>Такий страх у цілком очевидний і безсумнівний спосіб пов’язаний із самопочуттям людини. На цей нормальний страх людина повинна відповідати. Інакше вона відгородилась би від життєво необхідного процесу, що має в нас тривати.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2382:jak-zvilnytysja-vid-strahiv&catid=37:spilnota-cat&Itemid=49',4627,NULL,1,26),
(4610,'P','2014-09-01',1,NULL,'Rss','Відданий собака (1 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6110,200,1379356423_sad-dog_lecates_sa.jpg\" />Чоловік і жінка купили цуценя. Вони любили його і доглядали як сина. Цуценя виросло і стало великим, гарним, розумним собакою, який нераз рятував майно господаря. Через сім років після того, коли чоловік з жінкою взяли щеня у них народилася довгождана дитина. Вони були щасливі. Малюк забирав майже увесь їхній час, а собаці не залишалося навіть дрібки уваги.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2383:viddany-sobaka-1-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4628,NULL,1,26),
(4611,'P','2014-09-02',1,NULL,'Rss','Суперечка двох пастухів (2 вересня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6110,200,21720.jpeg\" style=\"margin: 10px; float: left;\" />Одного разу два пастухи сперечалися про державні справи. Один з них безтямно осуджував свого володаря а інший не до ладу захищав його правління. І захрипнувши від крику та побившись не змогли вони довести один одному нічого.</p>\n<p>Спересердя вирішили пастухи піти дорогою поки не знайдуть людину яка могла б вирішити їхній спір.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2385:superechka-dvoh-pastuhiv-2-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4629,NULL,1,26),
(4612,'P','2014-09-01',1,NULL,'Rss','Молитва про дарування перемоги над ворогом','<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6110,200,NPOTuN4F-Kk.jpg\" />(З Требника митрополита Київського Петра Могили)</em></p>\n<p>Господи Боже наш, єдиний кріпкий і сильний у війні, Ти єдиний маєш владу оживляти і умертвляти. Ти тримаєш серця всіх у руці Своїй. Ти сам даєш тому кому бажаєш кріпость і мужність. Ти в безнадійній ситуації даєш перемогу над ворогами, немічні руки кріпиш, а сильних серцем боязливими і немічними робиш.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2384:molytva-pro-daruvannja-peremogy-nad-vorogom&catid=28:tema-nomera-cat&Itemid=2',4630,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4613,'P','2014-09-02',1,NULL,'Rss','«Якщо ми будемо шукати Божого Слова і Закону, то віднайдемо правильну дорогу в житті…»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6110,200,pravoslavja.jpg\" />Ми стоїмо на порозі нового навчального року, коли наші учні та студенти знову розпочнуть навчання. Гадаю, що кожна молода людина, питає себе про те, яку дорогу має обрати в цьому житті. Однак сьогодні Господь звертається до кожного із нас, кличучи: «Ідіть за мною».</p>\n<p>Про це сказав Глава УГКЦ Блаженніший Святослав 31 серпня 2014 року під час проповіді на Архиєрейській Божественній Літургії в Патріаршому соборі Воскресіння Христового ГНІХ, що в Києві. Цього дня з Верховним Архиєпископом УГКЦ співслужив владика Богдан (Дзюрах), Адміністратор Патріаршої курії УГКЦ. Під час богослужіння Глава Церкви уділив дияконські свячення Петрові Токачу та Андрію Дорчаку.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2388:2014-09-02-14-21-42&catid=27:gk-v-ukrajini-cat&Itemid=57',4631,NULL,1,26),
(4614,'P','2014-09-02',1,NULL,'Rss','Україна воює любов\'ю','<p><img src=\"http://askoldova-mohyla.org/uk/image,6114,200,ArticleImages_57478_ato.jpg\" style=\"margin: 10px; float: left;\" />На початку минулого тижня Україна святкувала День Незалежності, але одночасно розпочалась уже повністю відверта агресія регулярних військ Московії в Україну. Одночасно з цим того самого 24 серпня 2014 р.Б. московський патріарх Кіріл з правдиво сатанинським цинізмом&nbsp;<a href=\"http://catholicnews.org.ua/patriarh-kiril-zvinuvativ-usih-hto-ne-nalezhit-do-yogo-patriarhatu-v-zlobi\">звинуватив</a> усіх в Україні, хто не належить до московського патріархату, в злобі й ненависті до цієї конфесії. Що більше, цей діяч «русскаґо міра»&nbsp;<a href=\"http://catholicnews.org.ua/patriarh-kiril-zaklikav-prezidenta-ukrayini-nadati-pravo-teroristam-viznachati-dolyu-ukrayini\">закликав</a> Президента України надати можливість російським терористам визначати подальшу долю України.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2389:ukrajina-vojuje-ljubovju&catid=28:tema-nomera-cat&Itemid=2',4632,NULL,1,26),
(4615,'P','2014-09-03',1,NULL,'Rss','Вовча натура (3 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6110,200,wolves-chernobyl1.jpg\" />В одному лісі всі звірі дуже боялися грізного вовка. І як почують, що він іде, то утікають, хто куди.</p>\n<p>Одного разу, зібрав вовк усе лісове товариство і каже:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Не бажаю я більше що би в моєму лісі панував страх. Не дивіться на мене як на ненаситного і не втікайте від мене. Мені до смаку тільки недужі та немічні. Якщо такі є, приводьте їх до мене, я буду їх їсти, а вас не зачеплю.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2386:vovcha-natura-3-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4633,NULL,1,26),
(4616,'P','2014-09-03',1,NULL,'Rss','Нунцій: ми зобов’язані допомогти Україні більше, ніж рятувальною операцією','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6116,200,files.php_.jpg\" />Українська криза показує, що український народ потребує більшої допомоги, ніж реакція пост-фактум. Про це у нещодавньому інтерв’ю заявив Апостольський Нунцій в Україні архиєпископ Томас Ґалліксон.</strong><o:p></o:p></p>\n<p>Ієрарх розповів про свою стурбованість життям Церкви в Україні, зазначивши, що російська агресія ставить під загрозу існування Церкви як інституції.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2391:nunci-my-zobovjazani-dopomogty-ukrajini-bilshe-nizh-rjatuvalnoju-operacijeju&catid=28:tema-nomera-cat&Itemid=2',4634,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4617,'P','2014-09-02',1,NULL,'Rss','У Львові молодь УГКЦ зустрічатиме Хрест та Ікону, подаровані св. Іваном Павлом II','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6116,200,bl_papa.jpg\" style=\"margin: 10px; float: right;\" />7 вересня з благословення владики Ігоря, Архиєпископа і Митрополита Львівського, у Львівській архиєпархії УГКЦ відбудеться проща Хреста та Ікони світових днів молоді.</strong></p>\n<p>Відповідальною за програму прощі є Комісія у справах молоді Львівської архиєпархії УГКЦ.</p>\n<p>Організатори запрошують молодь, священиків, семінаристів активно долучитися до зустрічі Молодіжного Хреста та Ікони.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2390:u-lvovi-molod-ugkc-zustrichatyme-hrest-ta-ikonu-podarovani-sv-ivanom-pavlom-ii&catid=27:gk-v-ukrajini-cat&Itemid=57',4635,NULL,1,26),
(4618,'P','2014-09-04',1,NULL,'Rss','Притча про троянду (4 вересня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6110,200,1644.jpg\" style=\"margin: 10px; float: left;\" />Одного разу чоловік посадив троянду, яку дбайливо доглядав і поливав. Оглядаючи її він побачив пуп’янок, який мав скоро розпуститися. Також помітив на стеблі шпичаки і подумав: «як з рослини з безліччю колючих шпичаків може вирости гарна квітка». Засмучений цією думкою він перестав поливати троянду і квітки загинула не встигнувши розквітнути.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2387:prytcha-pro-trojandu-4-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4636,NULL,1,26),
(4619,'P','2014-09-04',1,NULL,'Rss','12 тижнів життя','<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6116,200,12_tuzhniv_zhuttya.jpg\" />Проблема абортів щороку стає гострішою. Чи то глобалізація впливає на зміну пріоритетів, коли жінка радше готова присвятити все своє життя кар’єрі, аніж вихованню дитини, чи то падає моральний рівень молоді, яка розпочинає статеве життя мало не з 13 років, часто забуваючи про засоби захисту і не думаючи про можливі наслідки, піддаються згубному впливу суспільства. Причини і наслідки абортів ми спробували виокремити у матеріалі нижче.</em></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2392:12-tyzhniv-zhyttja&catid=37:spilnota-cat&Itemid=49',4637,NULL,1,26),
(4620,'P','2014-09-04',1,NULL,'Rss','Німі молитви (5 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6120,200,17156.jpg\" />Давним-давно жив один святий старець, який багато молився і часто уболівав про гріхи людські. І дивним йому здавалося, чому це так буває, що люди в церкву ходять, Богу моляться, а живуть все так само погано, гріха не убуває. «Господи, - думав він, - невже не чуєш Ти наших молитов? Ось люди постійно моляться, щоб жити їм у мирі й покаянні, і ніяк не можуть. Невже марнота їх молитва?»<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2393:nimi-molytvy-5-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4638,NULL,1,26),
(4621,'P','2014-09-04',1,NULL,'Rss','Ми — не сироти, Церква — наша мати!','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6120,200,images.jpg\" />Святіший Отець наголошує, що зразком материнства Церкви є Пречиста Діва Марія. Церква, отже, є продовженням Її материнства, народжуючи нових дітей у Христі, завжди слухаючи Боже Слово та зберігаючи послух щодо Його божественного задуму любові.<o:p></o:p></p>\n<p>«Народження Ісуса в лоні Марії, дійсно, є прелюдією до відродження кожного християнина в лоні Церкви, адже Христос є первородним з багатьох братів (пор. Рим 8, 29). Нашим першим братом є Ісус, що народився від Марії, це зразок, а ми всі народжені від Церкви.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2394:my--ne-syroty-cerkva--nasha-maty&catid=32:gk-svit-cat&Itemid=58',4639,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4622,'P','2014-09-05',1,NULL,'Rss','Вкрадена вівця (6 вересня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6122,200,images.jpg\" style=\"margin: 10px; float: left;\" />Одного чоловіка звинуватили в тому, що він вкрав вівцю. Його привели до судді разом з обвинувачем. Суддя повелів привести вівцю, а потім наказав кожному – обвинувачу і обвинуваченому покликати вівцю.</p>\n<p>Чоловік, що звинувачував довго кликав вівцю, але та навіть не повернула голови на його поклик.</p>\n<p>Потім покликав вівцю обвинувачений, він покликав її тим самим голосом як він кликав завжди. І вівця відразу піднесла голову, замекала і побігла до господаря.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2395:vkradena-vivcja-6-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4640,NULL,1,26),
(4623,'P','2014-09-05',1,NULL,'Rss','Здобуваючи Незалежність (частина І)','<p><strong><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6122,200,90984.jpg\" />Благословенний Господь, моя скеля, що руки мої Він навчає до бою, пальці мої до війни!</em></strong></p>\n<p>Сьогодні, коли на Донбасі проливають кров кращі сини України, коли йде війна з російськими окупантами і терористами, війна з сильним, підступним та брехливим ворогом, ці слова, що приписуються царю Давиду, зараз актуальні як ніколи. Звісно, ближнього треба любити, а ворогів прощати, але коли цей ближній лізе до тебе в хату з автоматом і зенітними ракетами, треба взяти шаблю, настучати йому по голові, а коли він заспокоїться – можна сміло любити й прощати його далі.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2396:zdobuvajuchy-nezalezhnist-chastyna-i&catid=37:spilnota-cat&Itemid=49',4641,NULL,1,26),
(4624,'P','2014-09-06',1,NULL,'Rss','Любов і принади (7 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6124,200,love.jpg\" />Якось надумав один юнак оженитися, і пішов радитися до свого батька. Батько сидів замислений, щось малюючи прутиком на піску. Юнак наблизився до нього і казав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Батьку, я зустрів чудову дівчину і хочу з нею одружитися.</p>\n<p>Але батько промовчав і малював на піску нуль.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Батьку, вона прекрасна господиня.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2400:ljubov-i-prynady-7-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4642,NULL,1,26),
(4625,'P','2014-09-07',1,NULL,'Rss','Здобуваючи Незалежність (частина ІІ)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6122,200,53365.jpg\" />Братня родина народів – чудова річ. Але зазвичай довго вона не живе – як не можуть весь час жити діти в одній хаті. Поки вони малі і суперечки між ними обмежуються тим хто буде бавитись якою іграшкою, жити же можна. Але час іде, діти виростають, в кожного заводяться свої друзі яких хочеться до себе запросити, а там вже й дівчата на підході. І ось, приходить час розходитись по окремих квартирах. Приблизно так все було в Речі Посполитій, союзі Королівства Польського і Великого князівства Литовського і Руського. Русини, поляки, литовці разом воювали проти сусідів – турків, татар і росіян, молились кожен в своєму храмі, інколи сперечались за те чия церква буде вищою і стоятиме ближче до центральної площі.<o:p></o:p></p>\n<p><o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2397:zdobuvajuchy-nezalezhnist-chastyna-ii&catid=37:spilnota-cat&Itemid=49',4643,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4626,'P','2014-09-08',1,NULL,'Rss','Здобуваючи Незалежність (частина ІІІ)','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6122,200,695.jpg\" />Богдан Хмельницький назавжди увійшов в історію як один з творців української державності. Однак, ця молода держава була приречена і ось чому.</strong><o:p></o:p></p>\n<p>По-перше, 18 січня (8-го за новим стилем) 1654 року, в місті Переяслав молода козацька держава вирішила увійти в союз з Москвою і прийняти протекторат царя за умовою зберігання державних прав України. Цікаво що у відповідь на свою присягу, українська сторона вимагала від царського посла Івана Бутурліна, присягнути від імені царя в непорушності прав України.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2398:zdobuvajuchy-nezalezhnist-chastyna-iii&catid=37:spilnota-cat&Itemid=49',4644,NULL,1,26),
(4627,'P','2014-09-09',1,NULL,'Rss','Здобуваючи Незалежність (частина ІV)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6122,200,4646.jpg\" />Перша Світова Війна, була для нас шансом вибити в своїх імперії більшу свободу і спробувати стати самостійними гравцями на політичній арені. Саме так мислили галицькі українці, які у 1914 році вступали до лав Українських Січових Стрільців. Це був український легіон в складі айстро-угорської армії, проте ніхто з стрільців не збирався вмирати за цісаря Франца-Йосифа. Розрахунок був простий: австрійці обіцяли нам що в разі перемоги над Росією вони заберуть українські землі і дадуть нам створити на них свою державу. І навіть, думали вони, якщо Австрія нас зрадить, в нас вже буде боєздатна армія і ми зможемо воювати самі за себе.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2399:zdobuvajuchy-nezalezhnist-chastyna-iv&catid=37:spilnota-cat&Itemid=49',4645,NULL,1,26),
(4628,'P','2014-09-08',1,NULL,'Rss','Дві вишні (8 вересня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6126,200,molitva-bogu.jpg\" style=\"margin: 10px; float: left;\" />Один чоловік мав дві вишні перед вікном. Одна була зла, інша – добра. Хай коли він виходив з дому, вони кликали його і про щось просили. Зла вишня щоразу просила різне: то обкопай мене; то напій мене; то заступи мене від сонця. А добра вишня повсякчас повторювала одне прохання: - «мій господарю, допоможи мені вродити щедрий врожай».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2401:dvi-vyshni-8-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4646,NULL,1,26),
(4629,'P','2014-09-09',1,NULL,'Rss','«УГКЦ є екуменічною Церквою майбутнього із нескореним духом, на яку може орієнтуватися цілий світ»','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6126,200,arzhakovskii.jpg\" style=\"margin: 10px; float: left;\" /></strong>Напередодні 25-ї річниці виходу УГКЦ із підпілля своїми думками про нашу Церкву, поглядом на перспективи її розвитку та досягнення сьогодення поділився Антуан Аржаковський (Франція) - доктор історичних наук, екс-дипломат французького МЗС, професор Українського католицького університету.</p>\n<p><strong>-</strong> <strong>Пане Антуане, у часі святкування 25-літнього ювілею виходу УГКЦ з підпілля, якими є основні кроки, що допоможуть їй налагодити екуменічний діалог із іншими християнськими конфесіями заради спільного блага Церкви і держави?</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2403:2014-09-09-08-56-01&catid=27:gk-v-ukrajini-cat&Itemid=57',4647,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4630,'P','2014-09-09',1,NULL,'Rss','Притча про квіти (9 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6126,200,short.jpg\" />Великий король&nbsp; хотів вибрати спадкоємця з трьох синів. Всі вони були близнюки, розумні і хоробрі і вибрати було нелегко. Він порадився з мудрецем і той підказав йому ідею.</p>\n<p>Король попросив до себе усіх трьох синів, дав кожному мішечок з квітковим насінням і сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Що вирушає в багатолітню прощу. І хто найкраще збереже його насіння, той і найкраще стане спадкоємцем трону.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2402:prytcha-pro-kvity-9-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4648,NULL,1,26),
(4631,'P','2014-09-09',1,NULL,'Rss','Засади політичної теології в українському контексті','<p><img src=\"http://askoldova-mohyla.org/uk/image,6126,200,1410024099_1.jpg\" style=\"margin: 10px; float: right;\" />Словосполучення “політична теологія” звучить як оксюморон. Чи можна ставити поруч теологію та політику? Чи має метафізичне дотик до політичного? Чи не є це два полюси, які нескінченно віддалені один від одного? Теологія - це дисципліна&nbsp; парадоксальна (додам принагідно - одна з найдавніших та засадничих дисциплін класичної європейської освіти). Один з її парадоксів полягає в тому, що, незважаючи на свою метафізичність, вона раціоналізується та навіть демонструє схильність до політичного. В історії християнської теології було чимало доктрин, які зазнавали впливу різноманітних політичних доцільностей.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2404:zasady-politychnoji-teologiji-v-ukrajinskomu-konteksti&catid=37:spilnota-cat&Itemid=49',4649,NULL,1,26),
(4632,'P','2014-09-09',1,NULL,'Rss','Допомагаючи Україні зупинити російську агресію, світ допомагає насамперед собі — група «Першого грудня»','<p><img src=\"http://askoldova-mohyla.org/uk/image,6126,200,E62A32.jpg\" style=\"margin: 10px; float: left;\" />4 вересня представники Ініціативної групи «Першого грудня» Євген Сверстюк, В’ячеслав Брюховецький, Мирослав Маринович та Мирослав Попович, на запрошення моніторингової місії ОБСЄ зустрілися з представниками місії на чолі з її керівником, Послом Ертугрулом Апаканом.</p>\n<p>Зустріч була присвячена обговоренню суспільно-політичної ситуації в Україні, насамперед — способам подолання негативного впливу на суспільство воєнних дій на Сході України та російської воєнної агресії проти України загалом.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2405:2014-09-09-09-25-33&catid=28:tema-nomera-cat&Itemid=2',4650,NULL,1,26),
(4633,'P','2014-09-09',1,NULL,'Rss','Всеосяжна і жорстка ізоляція режиму Путіна — найнагальніше завдання для світової громадськості — група «Першого грудня»','<p><em><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6132,200,pershe.jpg\" />Ініціативна група «Першого грудня» звертається до міжнародних лідерів та світової громадськості переглянути свою дотеперішню політику щодо російсько-українського конфлікту й усвідомити, що головним джерелом війни та агресії є політичний режим Президента Путіна.</strong></em></p>\n<p>Про це йдеться у зверненні, яке оприлюднили учасники групи Євген Сверстюк, Вадим Скуратівський, Мирослав Попович та В’ячеслав Брюховецький на прес-конференції в Українському кризовому медіа-центрі 9 вересня.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2408:2014-09-09-12-32-29&catid=28:tema-nomera-cat&Itemid=2',4651,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4634,'P','2014-09-09',1,NULL,'Rss','Засохле дерево (10 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6126,200,images.jpg\" />При дорозі стояло засохле дерево. Однієї ночі неподалік і шов злодій. Він помітив далекий силует і подумав, що це стоїть поліцай, злякався і втік.</p>\n<p>Увечері йшов дорогою закоханий, ще здалеку помітив він граціозний силует і подумав, що це кохана давно його дожидає, його серце радісно забилося, він усміхнувся і прискорив ходу.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2406:zasohle-derevo-10-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4652,NULL,1,26),
(4635,'P','2014-09-10',1,NULL,'Rss','Україна спливає кров’ю!','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6135,200,unnamed_fcfd6.jpg\" />«Зупиніть кровопролиття в Україні!» З таким закликом до світової громадськості звернулися єпископи УГКЦ, що беруть участь у Священному Синоді, який цими днями&nbsp;<a href=\"http://www.credo-ua.org/2014/09/122490\">відбувається</a> у Львові.</strong></p>\n<p>««Україна спливає кров’ю!» Ця мирна суверенна держава зазнала прямого військового вторгнення з боку північного сусіда. Сотні одиниць важкої зброї і техніки, тисячі озброєних найманців та військових регулярної армії Росії перетинають кордони України, сіють смерть і знищення, незважаючи на перемовини про перемир’я та дотеперішні дипломатичні зусилля, — йдеться у заяві. — Водночас триває нечувана за масштабами ненависті та спотворення істинного стану речей пропаганда, яка є не менше руйнівною, ніж зброя масового ураження».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2409:ukrajina-splyvaje-krovju&catid=28:tema-nomera-cat&Itemid=2',4653,NULL,1,26),
(4636,'P','2014-09-10',1,NULL,'Rss','Записка від Бога (11 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6126,200,38.jpg\" />Жила собі надзвичайно побожна жінка. Щодня вона проказувала довгі молитви і ходила до храму тою самою дорогою багато років поспіль. До жінки зверталися подорожні, простягали руки жебраки, але вона просувала заглиблена у свій внутрішній світ не зауважуючи нікого і нічого довкола. Якось пройшовши за звичаєм до храму, жінка виявила, що двері замкнені.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2407:zapyska-vid-boga-11-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4654,NULL,1,26),
(4637,'P','2014-09-11',1,NULL,'Rss','Усікновення голови св. Івана Хрестителя','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6136,200,image004.jpg\" />Після того, як&nbsp;<strong>Святий Іван Предтеча</strong> охрестив Господа, цар Ірод &nbsp;Антипа, правитель Галілеї, посадив його у темницю. Про його мученицьку кончину оповідають Євангелія від Матфея&nbsp;<em>(Мф. 14, 1-12)</em> і Марка (<em>Мк. 6, 14-29)</em><o:p></o:p></p>\n<p>У царя була законна дружина, дочка аравійського царя Арефи. Ірод залишив її і жив разом з Іродіадою, дружиною свого брата. Пророк Іван неодноразово викривав його, але цар не посмів заподіяти йому зла, так як шанував Івана Хрестителя як пророка і боявся народного гніву.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2410:usiknovennja-golovy-sv-ivana-hrestytelja&catid=27:gk-v-ukrajini-cat&Itemid=57',4657,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4638,'P','2014-09-12',1,NULL,'Rss','Лише маючи милосердне серце, людина може насправді наслідувати Ісуса Христа','<p><img src=\"http://askoldova-mohyla.org/uk/image,6140,200,1_0_824627.jpg\" style=\"margin: 10px; float: left;\" />Лише маючи милосердне серце, людина може насправді наслідувати Ісуса Христа. На основі цієї думки Папа Франциск побудував свою ранкову проповідь. Проповідник також наголосив на тому, що життя християнина не є життям егоїстичним, зацикленим виключно на своїй власній особі та особистих проблемах, але є даром для інших. Лише живучи своє життя як дар, зможемо любити наших ворогів, до чого закликає нас Господь Ісус.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2411:lyshe-majuchy-myloserdne-serce-ljudyna-mozhe-naspravdi-nasliduvaty-isusa-hrysta&catid=32:gk-svit-cat&Itemid=58',4658,NULL,1,26),
(4639,'P','2014-09-07',1,NULL,'Note','ПОЖЕРТВИ ПАРАФІЯН ЦЕРКВИ СВ. МИКОЛАЯ ЧУДОТВОРЦЯ НА АСКОЛЬДОВІЙ МОГИЛІ У ЛИПНІ 2014 Р. Б.','<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</span></p>','<p><strong><em>Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</em></strong></p>\r\n<table style=\"width: 425px;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><strong>№ з/п</strong></p>\r\n</td>\r\n<td width=\"180\">\r\n<p align=\"center\"><strong>Ж Е Р Т В О Д А В Ц І</strong></p>\r\n</td>\r\n<td width=\"208\">\r\n<p align=\"center\"><strong>Пожертва на (для, до):</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">1</p>\r\n</td>\r\n<td width=\"180\">\r\n<p>Анатолій та Ольга</p>\r\n</td>\r\n<td width=\"208\">\r\n<p>Дзвін</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">2</p>\r\n</td>\r\n<td width=\"180\">\r\n<p>Наталя та Юлія</p>\r\n</td>\r\n<td width=\"208\">\r\n<p>Дар на церкву</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">3</p>\r\n</td>\r\n<td width=\"180\">\r\n<p>Тарас, Ганна та Мар&rsquo;яна</p>\r\n</td>\r\n<td width=\"208\">\r\n<p>Церкву</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">4</p>\r\n</td>\r\n<td width=\"180\">\r\n<p>Родина Греськів</p>\r\n</td>\r\n<td width=\"208\">\r\n<p>Потреби церкви</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">5</p>\r\n</td>\r\n<td width=\"180\">\r\n<p>Володимир та Світлана</p>\r\n</td>\r\n<td width=\"208\">\r\n<p>Десятина на молитву</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">6</p>\r\n</td>\r\n<td width=\"180\">\r\n<p>Ярослав та Марія</p>\r\n</td>\r\n<td width=\"208\">\r\n<p>Церкву</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">7</p>\r\n</td>\r\n<td width=\"180\">\r\n<p>Парафіяни</p>\r\n</td>\r\n<td width=\"208\">\r\n<p>Потреби поранених</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">8</p>\r\n</td>\r\n<td width=\"180\">\r\n<p>Людмила Распутіна</p>\r\n</td>\r\n<td width=\"208\">\r\n<p>Військо</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">9</p>\r\n</td>\r\n<td width=\"180\">\r\n<p>Світлана</p>\r\n</td>\r\n<td width=\"208\">\r\n<p>Церкву та на молитву за невістку Катерину</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">10</p>\r\n</td>\r\n<td width=\"180\">\r\n<p>Родина Греськів</p>\r\n</td>\r\n<td width=\"208\">\r\n<p>Потреби храму</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">11</p>\r\n</td>\r\n<td width=\"180\">\r\n<p>Родина Василя Курилів</p>\r\n</td>\r\n<td width=\"208\">\r\n<p>Храм</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">12</p>\r\n</td>\r\n<td width=\"180\">\r\n<p>Іван Гамкало</p>\r\n</td>\r\n<td width=\"208\">\r\n<p>Дзвін</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">13</p>\r\n</td>\r\n<td width=\"180\">\r\n<p>Ігор та Наталя Бойко</p>\r\n</td>\r\n<td width=\"208\">\r\n<p>Церкву</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">14</p>\r\n</td>\r\n<td width=\"180\">\r\n<p>Ігор та Наталя</p>\r\n</td>\r\n<td width=\"208\">\r\n<p>Церкву</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">15</p>\r\n</td>\r\n<td width=\"180\">\r\n<p>Юлія Савула</p>\r\n</td>\r\n<td width=\"208\">\r\n<p>Церкву</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">16</p>\r\n</td>\r\n<td width=\"180\">\r\n<p>Ольга</p>\r\n</td>\r\n<td width=\"208\">\r\n<p>Лікування</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">17</p>\r\n</td>\r\n<td width=\"180\">\r\n<p>Андрій та Оксана</p>\r\n</td>\r\n<td width=\"208\">\r\n<p>Церкву</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">18</p>\r\n</td>\r\n<td width=\"180\">\r\n<p>Андрій та Ліна</p>\r\n</td>\r\n<td width=\"208\">\r\n<p>Церкву</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">19</p>\r\n</td>\r\n<td width=\"180\">\r\n<p>Ірина Ступка</p>\r\n</td>\r\n<td width=\"208\">\r\n<p>Келію Сільвестра в пам&rsquo;ять +Богдана</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">20</p>\r\n</td>\r\n<td width=\"180\">\r\n<p>Ольга</p>\r\n</td>\r\n<td width=\"208\">\r\n<p>Свято рівноап. Ольги і молитву</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">21</p>\r\n</td>\r\n<td width=\"180\">\r\n<p>Парафіяни</p>\r\n</td>\r\n<td width=\"208\">\r\n<p>Українське військо для поранених</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n<p><strong><em>Милостиню роби згідно з тим, що маєш; Коли у тебе достатків повно, Роби з них, а коли мало маєш, то не бійся з того дати. Бо так готуєш собі добрий скарб На день потреби.</em></strong></p>\r\n<p><strong>Товит 4. 8-9.</strong></p>',NULL,NULL,4655,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4640,'P','2014-09-10',1,NULL,'Note','ПОЖЕРТВИ ПАРАФІЯН ЦЕРКВИ СВ. МИКОЛАЯ ЧУДОТВОРЦЯ НА АСКОЛЬДОВІЙ МОГИЛІ У СЕРПНІ 2014 Р. Б.','<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</span></p>','<p><strong><em>Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</em></strong></p>\r\n<table style=\"width: 491px;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><strong>№ з/п</strong></p>\r\n</td>\r\n<td width=\"189\">\r\n<p align=\"center\"><strong>Ж Е Р Т В О Д А В Ц І</strong></p>\r\n</td>\r\n<td width=\"265\">\r\n<p align=\"center\"><strong>Пожертва на (для, до):</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">1</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Тарас, Олена-Катерина та Тарас</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Дзвін та подяка Богу за те, що уціліли в аварії</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">2</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Аскольд</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Дзвіницю</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">3</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Іван та Марія</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Церкву</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">4</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Парафіяни</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Потреби поранених воїнів</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">5</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Марія Шпак</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Дітям на відпочинок</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">6</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Родина Греськів</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Потреби храму</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">7</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Олег</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Церкву</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">8</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Лідія</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Церкву</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">9</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Ігор та Наталя</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Церкву</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">10</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Людмила</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Десятина, подяка за працю</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">11</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Василь та Анна</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Будову, пісок</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">12</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Василь Бойчук</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Щебень</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">13</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Світлана</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Шкільний портфелик</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">14</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Родина Курило</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Церкву</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">15</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Володимир та Ірина</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Церкву</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">16</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Марія</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Храм, подяка Богу</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\">17</p>\r\n</td>\r\n<td width=\"189\">\r\n<p>Андрій (СТО)</p>\r\n</td>\r\n<td width=\"265\">\r\n<p>Шкільний портфелик дітям, що потребують</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em><strong>Милостиню роби згідно з тим, що маєш; Коли у тебе достатків повно, Роби з них, а коли мало маєш, то не бійся з того дати. Бо так готуєш собі добрий скарб На день потреби.</strong></em></p>\r\n<p><strong>Товит 4. 8-9.</strong></p>',NULL,NULL,4656,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4641,'P','2014-09-12',1,NULL,'Rss','Це наша і ваша боротьба за свободу!','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6140,200,z_tv.jpg\" style=\"margin: 10px; float: right;\" />Якщо сьогодні Європа віддасть Україну в руки Росії, то невдовзі і сама загине – остерігає Блаженніший Святослав, Глава Української Греко-Католицької Церкви. Основна помилка Європи – на його думку – полягає в тому, що Європейський Союз декларує відданість певним цінностям, однак не може їх боронити.</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2412:ce-nasha-i-vasha-borotba-za-svobodu&catid=28:tema-nomera-cat&Itemid=2',4659,NULL,1,26),
(4642,'P','2014-09-12',1,NULL,'Rss','Положення чесного пояса Пресвятої Богородиці','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6143,200,1378817914.jpg\" />Згідно передання, Пресвята Богородиця Марія сама виплела свій пояс із верблюжої шерсті та передала його Апостолу Томі, коли возносилася на Небеса. Цим потішила його, бо ж він запізнився на її Успіння.</p>\n<p>Оскільки мощей Пресвятої Богородиці немає, бо вона взята до Неба всеціло, то пояс як і її риза – залишилися нам на згадку про Неї та як доказ того, що Вона жила на землі. Пояс переховували інші учні після мученицької смерті святого апостола Томи, а тоді поклали його у Церкві Влахернській у Константинополі.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2414:polozhennja-chesnogo-pojasa-presvjatoji-bogorodyci&catid=27:gk-v-ukrajini-cat&Itemid=57',4661,NULL,1,26),
(4643,'P','2014-09-12',1,NULL,'Rss','Навчитися слухати (13 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6143,200,4564.jpg\" />Один старенький цілий день сидів у храмі навпроти ікони. Якось церковний прислужник запитав старого:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Що каже вам Бог?<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Він нічого не каже, Він лише слухає.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2415:navchytysja-sluhaty-13-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4662,NULL,1,26),
(4644,'P','2014-09-13',1,NULL,'Rss','День Бога Творця','<p><img src=\"http://askoldova-mohyla.org/uk/image,6143,200,68277_900.jpg\" style=\"margin: 10px; float: right;\" />У неділю, 14 вересня, Українська Греко-Католицька Церква, долучаючись до міжнародної екуменічно-екологічної ініціативи Константинопольського Патріархату, відзначає День Бога Творця як особливе свято, яке покликане, через прославу нашого Творця і подяку за отримані від нього дари та доброчинства, спричинитися до утвердження нашої віри та життя згідно з його заповідями (за Божою подобою) у різних вимірах: особистому, сімейному, суспільному, а зокрема, у ставленні до Його створіння – природи.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2416:zvernennja-bjuro-ugkc-z-pytan-ekologiji-do-dnja-boga-tvorcja-2014-r-b&catid=27:gk-v-ukrajini-cat&Itemid=57',4663,NULL,1,26),
(4645,'P','2014-09-13',1,NULL,'Rss','Про любов без знеболювання','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6146,200,images.jpg\" style=\"margin: 10px; float: left;\" />Прагнення бути з іншою людиною і будувати з нею глибокий зв’язок не може базуватися на страхові перед самотністю, перед стражданням. Мотивація має бути принципово інакша: я мрію про те, щоби дати комусь почуття безпеки, щоби створити дім. Розмова з о.&nbsp;Мирославом Малінським, студентським душпастирем і ректором храму св.&nbsp;Матвія у Вроцлаві (Польща).</strong>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2417:pro-ljubov-bez-zneboljuvannja&catid=37:spilnota-cat&Itemid=49',4664,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4646,'P','2014-09-13',1,NULL,'Rss','«Майдан і церква»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6146,200,0000033265-majdan-zachistka.jpg\" />“Майдану би не було, якби не віра. Віра здатна спонукати людей до дій, на які не здатні ті, хто цієї віри не має”, - такими словами розпочала 11 вересня презентацію&nbsp;<strong><a href=\"http://risu.org.ua/ua/index/studios/announcements_of_publications/57602/\" target=\"_blank\">книги “Майдан і Церква. Хроніка подій та експертна оцінка”</a></strong> у Дзеркальному залі палацу Потоцьких професор-релігієзнавець Людмила Филипович.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2418:2014-09-13-10-37-50&catid=37:spilnota-cat&Itemid=49',4665,NULL,1,26),
(4647,'P','2014-09-13',1,NULL,'Rss','Дорогі подарунки (14 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6148,200,valentines-day.jpg\" />Якось прийшов в одне селище старий мудрий чоловік і оселився там. Він любив дітей і проводив з ними чимало часу. Але ще він любив дарувати їм подарунки, але тільки дуже ламкі речі.</p>\n<p>Хай як старалися діти, їхні нові іграшки часто ламалися. Діти засмучувалися і гірко плакали. Минав час і мудрець знову дарував їм іграшки, але ще тендітніші. Врешті батькам урвався терпець і вони прийшли до нього:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2419:dorogi-podarunky-14-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4666,NULL,1,26),
(4648,'P','2014-09-14',1,NULL,'Rss','Літургійний рік як знак живої Церкви','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6149,200,41980966.jpg\" />Вересень є не лише звичайним, календарним початком нової пори року, а й важливою подією у житті нашої Церкви. Саме 1 (14) вересня Церква святкує початок, «найменування», нового церковного року, який розпочинає своєрідне коло літургійних богослужінь.</p>\n<p>Установлення дати, якою розпочинається літургійний рік на 1 вересня (за новим стилем) чи на 14 – за старим, має давню історію, початки якої сягають ще часів Римської імперії. Якщо поглянути у церковний календар, то в день початку церковного року зустрічаємо слово «індикт», що з латинської мови означає «проголошення» або «податок».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2420:liturginy-rik-jak-znak-zhyvoji-cerkvy&catid=27:gk-v-ukrajini-cat&Itemid=57',4667,NULL,1,26),
(4649,'N','2014-09-15',1,NULL,'Rss','«І сказав Господь: Що ти зробив? Голос крови брата твого взиває до Мене з землі» (Бут. 4, 10).»','<p><img style=\"margin: 10px; float: right;\" src=\"image,6149,200,14039778.jpg\" alt=\"\" />Новий тиждень розпочався для України початком виборчих перегонів, які на певний час змусять суспільство забути про криваві жертви на Сході, про мордерства, вбивства, тортури, викрадення, про несправедливість. Але забуваючи про них, суспільство їх не позбудеться.</p>\r\n<p>Нормальна людська психіка прагне сховатися, не пам&rsquo;ятати про усе це насильство, жорстокість. Але труни героїв та герої-каліки будуть і далі стукати до серця, до совісті.</p>','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2421:2014-09-15-21-51-35&catid=28:tema-nomera-cat&Itemid=2',4668,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4650,'P','2014-09-15',1,NULL,'Rss','«І сказав Господь: Що ти зробив? Голос крови брата твого взиває до Мене з землі» (Бут. 4, 10).»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6149,200,14039778.jpg\" />Новий тиждень розпочався для України початком виборчих перегонів, які на певний час змусять суспільство забути про криваві жертви на Сході, про мордерства, вбивства, тортури, викрадення, про несправедливість. Але забуваючи про них, суспільство їх не позбудеться.</p>\n<p>Нормальна людська психіка прагне сховатися, не пам’ятати про усе це насильство, жорстокість. Але труни героїв та герої-каліки будуть і далі стукати до серця, до совісті.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2422:2014-09-15-21-51-38&catid=28:tema-nomera-cat&Itemid=2',4669,NULL,1,26),
(4651,'P','2014-09-16',1,NULL,'Rss','Відвідини хворих','<p>“Бо Я… слабував…”</p>\n<p>Спільнота храму святого Миколая Чудотворця на Аскольдовій могилі «Епіфанія» на чолі із священиком Анатолієм Теслею в черговий раз відвідала людей похилого віку.</p>\n<p>Приємно розпочати день, знаючи, що на тебе чекає робота чи навчання, чи якесь служіння, побачити знайомі обличчя людей. У книзі «Мозаїка милосердя» о. Арнальдо Панґрацці зазначає - &nbsp;відвідини є першим і найосновнішим елементом для встановлення особистого зв’язку з хворим. Саме тоді душпастир має нагоду стати свідком Бога та речником християнської спільноти для того, хто страждає.\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2423:vidvidyny-hvoryh&catid=25:parafija-cat&Itemid=27',4670,NULL,1,26),
(4652,'P','2014-09-14',1,NULL,'Rss','Благословення (15 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6153,200,9132.JPG\" />Один хлопчик, який згодом став ученим, у школі мав славу телепня.<o:p></o:p></p>\n<p>Одного разу школу відвідав поважний гість. Учитель показуючи на того хлопчика сказав:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Це найдурніший учень у школі.<o:p></o:p></p>\n<p>Перш ніж покинути школу, відвідувач підійшов до дитини і промовив дружнє благословення такого змісту:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2424:blagoslovennja-15-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4671,NULL,1,26),
(4653,'P','2014-09-16',1,NULL,'Rss','Неприйнятий дар (17 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6153,200,obgyn-gift.jpg\" />Старий майстер бойових мистецтв вирішив більше ніколи не битися. Але одного разу його все таки викликав на поєдинок юний воїн, який вважав, що він куди майстерніший і сильніший.<o:p></o:p></p>\n<p>Однак старий майстер і ніяк не реагував на нього. Тоді воїн взявся ображати його і його предків, щоб спровокувати майстра, але і це не допомогло.<o:p></o:p></p>\n<p>В решті решт, юний воїн опустив руки і пішов.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2426:neprynjaty-dar-17-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4672,NULL,1,26),
(4654,'P','2014-09-15',1,NULL,'Rss','Забута перемога (16 вересня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6153,200,origina.JPG\" style=\"margin: 10px; float: left;\" />Отаман вів своє військо у вилазку довгою кам’янистою дорогою. Воїни, що втрачали сили на важкому шляху і боялися ворогів почали досадувати і нарікати, але отаман суворо змушував іти вперед без відпочинку і затримок.<o:p></o:p></p>\n<p>Уранці вони зненацька напали на ворога і перемогли. І після битви запитав отаман у воїнів:<o:p></o:p></p>\n<p><!--[if !supportLists]--></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2425:zabuta-peremoga-16-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4673,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4655,'P','2014-09-17',1,NULL,'Rss','Як одержати нагороду (18 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6153,200,DSC05.jpg\" />Одного разу старець прийшов д господаря поля і сказав:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Дай мені пшениці.<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->А чи ти жав, старцю?<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Ні, - відповів старець.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2428:jak-oderzhaty-nagorodu-18-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4674,NULL,1,26),
(4656,'P','2014-09-17',1,NULL,'Rss','Синодальне послання до вірних Української Греко-Католицької Церкви та всіх людей доброї волі про суспільно-політичну ситуацію в Україні  напередодні виборів до Верховної Ради','<p align=\"right\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6153,200,2-1.jpg\" />…<em>Не лякайся й не падай духом, бо Господь, Бог твій, з тобою, куди б ти не подався&nbsp;</em><span style=\"font-size: 0.875em;\">(Книга Ісуса Навина 1, 9).</span></p>\n<p align=\"center\"><em><strong>Дорогі в Христі!</strong></em></p>\n<p>Уже близько року весь наш народ перебуває в паломництві до справжньої свободи. Пройшовши визвольний досвід Революції гідності, ми спільно переносимо трагічний військовий конфлікт, анексію Криму й агресію з боку сусідньої держави на Cході України, переживаємо складну економічну кризу, постійно стоїмо перед викликами в політичній сфері. Як ми можемо мати певність, що Бог є з нами?</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2427:synodalne-poslannja-do-virnyh-ukrajinskoji-greko-katolyckoji-cerkvy-ta-vsih-ljude-dobroji-voli-pro-suspilno-politychnu-sytuaciju-v-ukrajini-naperedodni-vyboriv-do-verhovnoji-rady&catid=27',4675,NULL,1,26),
(4657,'P','2014-09-18',1,NULL,'Rss','Церква є «вселенська» і «апостольська»','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6157,200,kst_z_papoju1.jpg\" />Загальна аудієнція з Папою Франциском 17 вересня 2014р.: Вселенська і апостольська Церква (повний текст)</strong></p>\n<p>Дорогі брати і сестри,</p>\n<p>Коли визнаємо нашу віру, то говоримо, що Церква є «вселенська» і «апостольська». Яке значення мають ці дві характерні риси Церкви? Яку вони мають вартість для християнських спільнот і для кожного з нас?</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2429:2014-09-18-05-23-15&catid=32:gk-svit-cat&Itemid=58',4676,NULL,1,26),
(4658,'P','2014-09-18',1,NULL,'Rss','Потреба молитви, але не тільки','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6157,200,543753.jpg\" style=\"margin: 10px; float: left;\" />За останній вже майже рік наш нарід молився.</strong><strong> </strong></p>\n<p>Віруючі люди радо і з переконання, переймали заклики своїх Церков чи релігійних організацій і молилися. Не перестали молитись і сьогодні.</p>\n<p>Церкви та релігійні організації не перестали молитись, закликати до молитви і навіть до покаянних практик, таких, як піст. Вони при різних нагодах дальше моляться і закликають до щирої молитви.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2430:potreba-molytvy-ale-ne-tilky&catid=28:tema-nomera-cat&Itemid=2',4677,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4659,'P','2014-09-18',1,NULL,'Rss','«Кожен має оголосити воєнний стан у своїй душі»','<p><strong><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6160,200,sverstyuk.jpg\" />Українським інтелектуалам дуже складно боротися з російською пропагандою, бо вона ґрунтується не на інтелекті, а на брехні. Таку думку висловив відомий філософ, письменник, дисидент, учасник Ініціативної групи «Першого грудня» Євген Сверстюк під час своєї лекції «Один в полі воїн: де проходить український інтелектуальний фронт» у Національному музеї «Тюрма на Лонцького» в рамках Форуму видавців у Львові.</em></strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2431:2014-09-18-10-17-44&catid=37:spilnota-cat&Itemid=49',4678,NULL,1,26),
(4660,'P','2014-09-27',0,NULL,'Note','«ВІЗЬМУ МОЛИТВУ – МАМИН ОБЕРІГ»','<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><span style=\"font-family: arial, helvetica, sans-serif;\"><span style=\"text-indent: 0em; line-height: 1.3em;\"><img style=\"float: left;\" src=\"image,6162,200,10403226.jpg\" alt=\"\" width=\"200\" height=\"150\" />Ласкаво просимо на&nbsp;літературно-мистецьку зустріч&nbsp;</span><span style=\"text-indent: 0em; line-height: 1.3em;\">із циклу &laquo;</span><em style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Молюся віршами сьогодні</em><span style=\"text-indent: 0em; line-height: 1.3em;\">&raquo;&nbsp;</span><span style=\"text-indent: 0em; line-height: 1.3em;\">з поетесою, істориком, громадським діячем&nbsp;</span><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Іриною Василик &nbsp;</strong>28.09.2014 о 14:30</span></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>&laquo;ВІЗЬМУ МОЛИТВУ &ndash; МАМИН ОБЕРІГ&raquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Місце проведення заходу:&nbsp;</span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Церква св. Миколая на Аскольдовій могилі.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Вхід вільний.</span></p>','<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6162,200,10403226.jpg\" alt=\"\" width=\"200\" height=\"150\" />Ласкаво просимо на&nbsp;літературно-мистецьку зустріч&nbsp;із циклу &laquo;<em>Молюся віршами сьогодні</em>&raquo;&nbsp;з поетесою, істориком, громадським діячем&nbsp;<strong>Іриною Василик &nbsp;</strong>28.09.2014 о 14:30</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>&laquo;ВІЗЬМУ МОЛИТВУ &ndash; МАМИН ОБЕРІГ&raquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">У програмі також беруть участь:</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- Жіночий вокальний ансамбль &laquo;АНІМА&raquo; УГКЦ св. Миколая Чудотворця на Аскольдовій могилі;</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><span style=\"line-height: 1.3em;\">- Родинний гурт &laquo;ДИВОГРАЙ&raquo;;</span></span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><span style=\"line-height: 1.3em;\">- Лауреат міжнародних та всеукраїнських конкурсів РУСЛАНА ДМИТРОВСЬКА;</span></span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><span style=\"text-indent: 0em; line-height: 1.3em;\">- Піаніст, володарка Гран-Прі, І премії Міжнародного конкурсу піаністів (Італія), викладач НМА України ім. П.Чайковського НАТАЛЯ МАЛЬЦЕВА.</span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Місце проведення заходу:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Церква св. Миколая Чудотворця на Аскольдовій могилі (Паркова дорога, 1).</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Вхід вільний</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><a href=\"https://www.facebook.com/events/1462029497410937/\">https://www.facebook.com/events/1462029497410937/</a></span></p>',NULL,NULL,4679,5,1,16);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4661,'P','2014-09-18',1,NULL,'Rss','Істинна цінність (19 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6162,200,h0MXG1nu9Bs.jpg\" />Одного разу шкільний учитель прийшов до вельми шанованої учительки і звинуватив її у тому, що її метод навчання цілком не логічний, що це просто якась безглузда балаканина і таке інше.</p>\n<p>Учителька дістала зі своєї торбини самоцвіт. Вона запропонувала учителеві піти в крамничку де продають вироби зі срібла і батарейки для годинників. І виторгувати за нього хоча б 10 тисяч карбованців.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2432:istynna-cinnist-19-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4680,NULL,1,26),
(4662,'P','2014-09-19',1,NULL,'Rss','Лише з миром можна боронити свою землю, мову та Церкву','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6162,200,zapisati_203.jpg\" />Розмова з отцем Михайлом Гарватом, священиком храму Різдва Пресвятої Богородиці у Львові</p>\n<p>Близько тисячі загиблих, більше ніж три тисячі поранених військових — такі жертви боротьби за незалежність України. Напевно, кожної української сім’ї торкнулася війна: є знайомі, родичі, які мобілізовані, поранені, а то й полонені чи загиблі. Нині складний та важкий час для усіх. Як його пережити, як уникнути тривоги? Всі запитують про це.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2433:lyshe-z-myrom-mozhna-boronyty-svoju-zemlju-movu-ta-cerkvu&catid=28:tema-nomera-cat&Itemid=2',4681,NULL,1,26),
(4663,'P','2014-09-19',1,NULL,'Rss','Таємниця мудреця (20 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6162,200,75685543892905.jpg\" />Якось запитали мудреця:<o:p></o:p></p>\n<p><!--[if !supportLists]-->- <!--[endif]-->Яка таємниця вашої мудрости?<em><o:p></o:p></em></p>\n<p>Відповів він:<o:p></o:p></p>\n<p><!--[if !supportLists]-->- <!--[endif]-->Я живу простим життям. Я їм, коли їм. Сплю, коли сплю. Молюся, коли молюся.<em><o:p></o:p></em></p>\n<p>Запитувач спантеличився:\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2435:tajemnycja-mudrecja-20-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4682,NULL,1,26),
(4664,'P','2014-09-20',1,NULL,'Rss','10 змін на краще, які стануться, якщо вийти з соцмереж','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6162,200,484.png\" />Соціальні медіа можуть бути чудовим засобом для того, щоб підтримувати контакти з людьми, заводити нових друзів і спілкуватися. На жаль, не всі використовують їх саме так.</strong></p>\n<p>Багато хто з нас заходить у соцмережі, як на роботу: постять щось заради лайків, нескінченно переглядають оновлення стрічки і до одержимості хочуть відразу дізнаватися про все, що діється у світі. Можливо, настав час зробити перерву?</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2434:10-zmin-na-krashche-jaki-stanutsja-jakshcho-vyty-z-socmerezh&catid=37:spilnota-cat&Itemid=49',4683,NULL,1,26),
(4665,'P','2014-09-20',1,NULL,'Rss','Віра в перемогу (21 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6162,200,XYviL5TRpAA.jpg\" />Один військовий отаман вирішив напасти на супротивника, хоча ворогів було в десятеро більше. Він знав, що переможе, але його солдатів брав сумнів. По дорозі він спинився біля святині сказав своїм людям:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Помолившись у святині, я підкинув монету, якщо випаде орел – ми переможемо, а якщо решка – то програємо. Такою буде наша доля.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2437:vira-v-peremogu-21-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4684,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4666,'P','2014-09-20',1,NULL,'Rss','Народження Марії це джерело радості для світу','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6162,200,1379697073_default.jpeg\" />Наша Церква в Утрені свята Народження Божої Матері співає отак: \"Маріє, пречиста Богоро­дице, чиста Діво, спаси від всяких бід своє стадо, щоб ми прославляли й величали тебе, надіє всіх християн\".<strong></strong></p>\n<p>Церква загально відмовляється святкувати народжен­ня навіть найбільших свя­тих, бо вони у хвилині на­родження є забруджені первородним гріхом. Єди­ний виняток становить Христовий предтеча Йоан, який, за навчанням святих Отців Церкви, був звільне­ний від первородного гріха ради заступництва Божої Матері, що відвідала його матір Єлизавету, поки Йоан ще не народився (Лк. 1, 41). Робить Церква так тому, що ніхто не знає, що буде з новонародженої дитини, поки вона не підросте і влас­ним життям не докаже, чого вона вартує в Божих очах.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2436:narodzhennja-mariji-ce-dzherelo-radosti-dlja-svitu&catid=28:tema-nomera-cat&Itemid=2',4685,NULL,1,26),
(4667,'P','2014-09-21',1,NULL,'Rss','Період випробувань','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6162,200,19625.gif\" />Наростає хвиля переходу громад від УПЦ МП без священиків, а на черзі — низка парафій разом із їхнім кліром</strong></p>\n<p>Українські церкви переживають непростий період внутрішньої трансформації, все більше віддаляючись від тієї єдності, що характеризувала Всеукраїнську раду церков та релігійних організацій під час Майдану.</p>\n<p>9—11 вересня відбулася зустріч представників українських та російських релігійних організацій в Осло.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2438:period-vyprobuvan&catid=27:gk-v-ukrajini-cat&Itemid=57',4686,NULL,1,26),
(4668,'P','2014-09-21',1,NULL,'Rss','«Закликаємо і президента, і уряд, і депутатів до жертовності»','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6162,200,e6af5e0d4dd60b53.jpg\" />У нас є чотири церкви \"київської традиції\" - Українська православна Київського і Московського патріархатів, Автокефальна православна та Українська греко-католицька. Що прийнято вкладати в це поняття?</strong></p>\n<p>- Тут треба почати з того, що ми, люди, по своїй суті є сотворіннями з пам\'яттю, для яких історія не є маловажною. Наша особиста тотожність пов\'язана з нашою історією. Наша культура, ставлення до оточення, світу, всіх подій, до всіх екзистенційних питань принаймні частково сформовані історією, яка може бути близькою або дуже далекою.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2439:2014-09-21-18-36-31&catid=28:tema-nomera-cat&Itemid=2',4687,NULL,1,26),
(4669,'P','2014-09-22',1,NULL,'Rss','Мудрець і заздрісник (22 вересня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6170,200,x_c7b88854.jpg\" style=\"margin: 10px; float: left;\" />Жив на світі чоловік, він прославився своїм спокоєм і любов’ю до всього на світі. Будь-яка справа, до якої він брався виходила у нього добре. Всі того чоловіка поважали, приходили до нього за порадою. Одного разу до нього прийшов сусід, який йому у всьому заздрив. Він сказав погордливо:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Я в житті усе маю! Живу в достатку. Але тебе в місті поважають більше ніж мене. До кого я подібний , як гадаєш?<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2440:mudrec-i-zazdrisnyk-22-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4688,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4670,'P','2014-09-22',1,NULL,'Rss','Європа живе в атмосфері опортунізму, тобто співпрацює з бандитами','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6170,200,im578.jpg\" />Мирослав Попович, відомий український філософ та академік НАНУ, про те, як відродити Донбас та любов місцевого населення до України</p>\n<p><a href=\"http://espreso.tv/\" target=\"_blank\">Еспресо.TV</a> продовжує серію публікацій, присвячену темі патріотичного виховання та патріотизму взагалі в світлі подій на Майдані, анексії Криму та війни з Росією на Донбасі.&nbsp;<a href=\"http://espreso.tv/article/2014/08/19/vadym_skurativskyy\">Минулого разу</a> свої рецепти виховання патріотизму давав&nbsp;Вадим Скуратівський, відомий історик та публіцист та&nbsp;<a href=\"http://espreso.tv/article/2014/09/02/bryukhoveckyy\">В’ячеслав Брюховецький</a>,&nbsp;почесний президент університету Києво-Могилянська академія.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2442:jevropa-zhyve-v-atmosferi-oportunizmu-tobto-spivpracjuje-z-bandytamy&catid=37:spilnota-cat&Itemid=49',4689,NULL,1,26),
(4671,'P','2014-09-22',1,NULL,'Rss','Як добру справу чинити (23 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6170,200,Yak.jpg\" />Один хлопець запитав старця:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Яку би мені добру справу учинити і жити з нею.<o:p></o:p></p>\n<p>Старець відповів:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Бог знає, що добре, я чув як дехто запитує мудреця:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2441:jak-dobru-spravu-chynyty-23-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4690,NULL,1,26),
(4672,'P','2014-09-23',1,NULL,'Rss','Святий Піо з П’єтрельчіни','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6173,200,padrepio.jpg\" />Є святі, про яких розповідати важко, бо майже немає інформації. Є святі, про яких розповідати важко, бо про них і так «усі все знають». Як про блаженних Матір Терезу, Йоана Павла ІІ або святого Отця Піо (1887-1968).</p>\n<p>Франческо Форджоне народився в П’єтрельчіні. Об’явлення Ісуса мав з дитинства (перше – у п’ятирічному віці). До капуцинів вступив у 16 років, отримавши ім’я Піо. Священиком став 1910 року. Був узятий до війська на загальних підставах, але комісований за станом здоров’я, оселився у Сан-Джованні-Ротондо у липні 1916-го і не полишав цього місця до смерті.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2443:svjaty-pio-z-pjetrelchiny&catid=35:istorychni-postati-cat&Itemid=48',4691,NULL,1,26),
(4673,'P','2014-09-24',1,NULL,'Rss','Сила і непереможність (24 вересня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6174,200,6546.jpg\" style=\"margin: 10px; float: left;\" />Одного разу прийшов юнак до старця і попросив дозволу вчитися в нього.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Навіщо це тобі? – спитав той.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Хочу стати сильним і непереможним.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тоді стань ним! Будь добрий зі всіма, ввічливий і поштивий. Доброта і ввічливість викличе в тебе повагу до інших. Твій дух стане чистим і добрим, а значить сильним. Якщо ти навчишся запобігати конфліктам, то станеш непереможним.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2444:syla-i-neperemozhnist-24-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4692,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4674,'P','2014-09-24',1,NULL,'Rss','Сила Церкви — в Христовій любові','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6174,200,54675.jpg\" style=\"margin: 10px; float: right;\" />Сьогодні, як і вчора, сила Церкви полягає не в організаційних здібностях, але – у Христовій любові.</strong><strong></strong></p>\n<p>Промовляючи до паломників, які не лякаючись дощу заповнили площу перед Ватиканською базилікою, Святіший Отець поділився своїми враженнями та підсумками нещодавнього Апостольського візиту до столиці Албанії міста Тирани.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2445:syla-cerkvy-v-hrystovi-ljubovi&catid=32:gk-svit-cat&Itemid=58',4693,NULL,1,26),
(4675,'P','2014-09-25',1,NULL,'Rss','«Продовжуйте виявляти істину! Будь ласка, не підведіть нас!»','<p align=\"right\"><strong><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6177,200,cardinal_dolan_0.jpg\" />Архиєпископ Тімоті Долан</em></strong></p>\n<p>Минулого тижня я телефонував до мого товариша Святослава Шевчука, Верховного Архиєпископа Української Греко-Католицької Церкви.</p>\n<p>Впродовж останніх років я почав все більше поважати цього молодого, відважного брата-єпископа, після того, як ми з ним часто зустрічались в Римі, а особливо коли минулого року я відвідував його з нагоди посвячення Патріаршого Собору Воскресіння Христового в Києві.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2447:2014-09-25-13-42-57&catid=32:gk-svit-cat&Itemid=58',4695,NULL,1,26),
(4676,'P','2014-09-25',1,NULL,'Rss','Виноград з кісточками (26 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6178,200,thumb.jpg\" />Юна дочка тихенько зайшла в покій батька-падишаха. Вона шанобливо чекала, поки батько побачить хвилювання в неї на обличчі і почне бесіду.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Що стривожило тебе, дочко моя? Якщо ти наважилася відвідати мене у хвилину спокою і усамітнення.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ах, батьку, ти мудрий і доля, та люди люблять тебе. Порадь мені як досягти твого спокою і мудрості.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2449:vynograd-z-kistochkamy-26-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4696,NULL,1,26),
(4677,'P','2014-09-26',1,NULL,'Rss','80 бідних родин отримали постіль від БФ «Карітас Київ»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6178,200,IMG_20140909_151542.jpg\" />«Благодійний Фонд «Карітас Київ» пошив та &nbsp;роздав для бідних родин, в яких є діти, 80 комплектів постілі. Така можливість виникла завдяки благодійникам та УББ, які щедро дали свої кошти на таку ціль. Так, було закуплено потрібну кількість тканини і волонтером Карітасу пошито 80 комплектів для підопічних. Це вже вдруге Карітас надав подібного роду допомогу, оскільки нещодавно сімдесять самотніх людей літнього віку також отримали постільні комплекти від БФ «Карітас-Київ». Отож, разом за три місяці – це 150 комплектів для потребуючих, що є досить вагомим», - повідомляє о. Роман Сиротич, директор БФ «Карітас-Київ».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2448:2014-09-25-20-30-44&catid=29:propovidi-cat&Itemid=34',4697,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4678,'P','2014-09-26',1,NULL,'Rss','Кисле варення (27 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6180,200,prikorm21.jpg\" />Діти покуштували варення, яке щойно приготувала мама. Воно їм дуже сподобалось. І тут до кімнати зайшов молодший брат.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Можна і мені покуштувати варення? – запитав він.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Не їж, воно дуже кисле, - пожартували старші діти.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2450:kysle-varennja-27-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4698,NULL,1,26),
(4679,'P','2014-09-26',1,NULL,'Rss','«Величаємо Тебе Життєдавче Христе і шануємо Хрест Твій Святий, ним же нас спас від неволі ворожої»','<p><img src=\"http://askoldova-mohyla.org/uk/image,6180,200,Vozdvy.jpg\" style=\"margin: 10px; float: right;\" />Римські імператори-язичники намагалися повністю знищити в людстві спогади про священні місця, де постраждав за людей і воскрес Господь наш Ісус Христос. Імператор Адріан наказав засипати землею Голгофу і Гроб Господній і на штучному пагорбі поставити капище язичницької богині Венери і статую Юпітера. На це місце збиралися язичники й робили ідольські жертвоприносини.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2451:2014-09-26-17-46-52&catid=28:tema-nomera-cat&Itemid=2',4699,NULL,1,26),
(4680,'P','2014-09-27',1,NULL,'Rss','Слово на Всеславнне Воздвиження Хреста святителя Андрея, архиєпископа Критського','<p><img src=\"http://askoldova-mohyla.org/uk/image,6182,200,safe_image.jpg\" style=\"margin: 10px; float: left;\" />Ми славимо Хрест, і вся Церква наповнюється блиском слави. Ми славимо Хрест, і обличчя всієї вселенної освітлюється сяйвом радості. Ми славимо Хрест, і розсівається морок, і розливається світло. Ми славимо Хрест, і з Розп’ятим підносимося, щоб, залишивши внизу землю разом з гріхом, здобувати небо й небесне. Хрест воздвигається, і гамірні клики демонів замовкають.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2452:slovo-na-vseslavnne-vozdvyzhennja-hresta-svjatytelja-andreja-arhyjepyskopa-krytskogo&catid=32:gk-svit-cat&Itemid=58',4700,NULL,1,26),
(4681,'P','2014-09-27',1,NULL,'Rss','«SOS!» українському християнству','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6183,200,x_8fc6aa76.jpg\" />Ситуація, яка склалася і продовжує розвиватися в Україні, набирає щораз загрозливіших обрисів. Уже зараз можемо спостерігати перші ознаки фундаментального розколу поміж більшістю представників влади і більшістю загалу суспільства. Усе частіше люди беруть у свої руки встановлення справедливості.</p>\n<p>На жаль, оговтавшись після майданного шоку, політикум і чиновництво вкотре взялися за своє. Корупція, договорняки, хабарництво, здирництво, – знову процвітають. І знову «небожителі» українського владно-політичного Олімпу не звертають уваги на суспільство, над яким вони возвишуються.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2453:2014-09-27-18-56-26',4701,NULL,1,26),
(4682,'P','2014-09-27',1,NULL,'Rss','Коробка печива (28 вересня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6183,200,2.png\" style=\"margin: 10px; float: left;\" />Придбавши коробку печива жінка вирішила почитати журнал і дістала його з торбинки. Вона взяла одне печиво і побачила, що якийсь чоловік також пригостився з її коробки.</p>\n<p>Жінка палахнула гнівом, знову взяла печиво, зиркнула на чоловіка і побачила, що він усміхається. За хвилину він взяв ще одне печиво, вона знову спалахнула гнівом.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2454:korobka-pechyva-28-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4702,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4683,'N','2014-09-27',1,NULL,'Rss','«SOS!» українському християнству','<p><img src=\"http://askoldova-mohyla.org/uk/image,6183,200,x_8fc6aa76.jpg\" style=\"margin: 10px; float: right;\" />Ситуація, яка склалася і продовжує розвиватися в Україні, набирає щораз загрозливіших обрисів. Уже зараз можемо спостерігати перші ознаки фундаментального розколу поміж більшістю представників влади і більшістю загалу суспільства. Усе частіше люди беруть у свої руки встановлення справедливості.</p>\n<p>На жаль, оговтавшись після майданного шоку, політикум і чиновництво вкотре взялися за своє. Корупція, договорняки, хабарництво, здирництво, – знову процвітають. І знову «небожителі» українського владно-політичного Олімпу не звертають уваги на суспільство, над яким вони возвишуються.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2453:2014-09-27-18-56-26&catid=37:spilnota-cat&Itemid=49',4703,NULL,1,26),
(4684,'P','2014-09-28',1,NULL,'Rss','Він, вона і Бог','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6187,200,CRW_1684.JPG\" style=\"margin: 10px; float: right;\" /></strong>Подружжя&nbsp;— це спільнота і спілкування. Так, для щасливого шлюбу потрібні почуття і взаємне емоційне зацікавлення. Потрібен і спільний світогляд, єдність поглядів, взаємна підтримка, спільне господарство. Але якщо всі ці речі у сімейній спільноті не поєднані зі спілкуванням, то їх забракне, стверджує о.&nbsp;Андрій Нагірняк. Він підготував до Таїнства Шлюбу багатьох наречених, а ще більшу кількість обвінчав. Про те, які підводні камені чекають на подружжя, і як не загубитися серед них, і буде наша розмова.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2455:vin-vona-i-bog&catid=28:tema-nomera-cat&Itemid=2',4705,NULL,1,26),
(4685,'P','2014-09-29',1,NULL,'Rss','Що посієш (29 вересня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6188,200,020_siyyach.jpg\" style=\"margin: 10px; float: left;\" /></p>\n<p>Селянин ревно засівав своє поле. До нього підійшов хлопчик і сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я, коли виросту, ніколи не буду сіяти зерна.</p>\n<p>Чоловік поглянув на нього і нічого не відповів.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Це ж невдячна справа, - говорив далі хлопчик.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2456:shcho-posijesh-29-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4706,NULL,1,26),
(4686,'P','2014-09-29',1,NULL,'Rss','Перед Україною стоятиме величезний виклик, – представник Ватикану в Україні','<p><img src=\"http://askoldova-mohyla.org/uk/image,6188,200,articleimages_57555__.jpg\" style=\"margin: 10px; float: right;\" />Архієпископ Томас Едвард Ґалліксон, апостольський нунцій в Україні надав неупереджену оцінку труднощів, перед якими стоїть Церква в Україні.</p>\n<p>Архієпископ Ґалліксон зазначив, що сьогодні Україна потерпає від «дестабілізації, спровокованої переважно хижацтвами української кримінальної олігархії (які і надалі тривають),&nbsp; посиленої російською агресією супроти її територіальної цілісності та суверенітету».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2457:145937-srjoanna-chornii-pered-ukrajinoju-stojatyme-velychezny-vyklyk-predstavnyk-vatykanu-v-ukrajini&catid=28:tema-nomera-cat&Itemid=2',4707,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4687,'P','2014-09-29',1,NULL,'Rss','Хто сильніший (30 вересня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6190,200,13754457.jpg\" />Одного разу посперечалося сонце з північним вітром, хто з них сильніший. Північний вітер вказав на чоловіка, що йшов пустелею і сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Закладаюся, що я змушу цього чоловіка скинути плащ, а ти ні.</p>\n<p>Сонце відповіло:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я приймаю цей заклад.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2458:hto-sylnishy-30-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4708,NULL,1,26),
(4688,'P','2014-09-29',1,NULL,'Rss','Віра, Надія, Любов та їх мати Софія','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6190,200,456.jpg\" style=\"margin: 10px; float: right;\" />«Мудрість, яка виходить з верху, по-перше, чиста, потім мирна, лагідна, покірлива, повна милосердя і добрих плодів, неупереджена і нелицемірна» (Яків 3,17).</strong></p>\n<p>У царстві Адріяна злочестивого, царя римського, була в Римі одна вдова роду італійського, на ймення Софія, її ж ім\'я тлумачиться \"премудрість\".</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2459:vira-nadija-ljubov-ta-jih-maty-sofija&catid=35:istorychni-postati-cat&Itemid=48',4709,NULL,1,26),
(4689,'P','2014-09-30',1,NULL,'Rss','Поетичні таланти Аскольдової могили','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6192,200,1.JPG\" />28 вересня 2014 року в храмі св. Миколая Чудотворця на Аскольдовій могилі відбувся літературно-мистецький вечір <strong>«Візьму молитву – мамин оберіг»</strong> із циклу «Молюся віршами сьогодні». Перший вечір ознайомив вірян і гостей храму із поетичним доробком парафіянки Церкви Миколая Чудотворця на Аскольдовій могилі, кандидатом історичних наук, доцентом, громадським діячем<strong><em> </em>Іриною Василик</strong>.</p>\n<p>Поетеса народилася на мальовничій Тернопільщині, закінчила історичний факультет Тернопільського національного педагогічного університету імені Володимира Гнатюка. Перший вірш під назвою «О, мово рідна, що без тебе я» написала у 5 класі. Свої поезії публікувала у районній та обласних газетах, альманахах, брала участь у конференції молодих літераторів Поділля. У доробку Ірини Богданівни вірші різного жанру – для дітей дошкільного і молодшого шкільного віку, верлібри і катрени, силабо-тоніка. У 2003 р. Ірина Василик видала першу збірку віршів «Не зречуся тебе, моя мріє», у 2006 р. – другу «Сяйво сльози».</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2460:poetychni-talanty-askoldovoji-mogyly&catid=25:parafija-cat&Itemid=27',4710,NULL,1,26),
(4690,'P','2014-09-30',1,NULL,'Rss','Моральний аспект виборів до ВР','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6194,200,14093198.jpg\" />Україна, перебуваючи де-факто у стані війни, збирається на позачергові вибори свого парламенту. Тішить те, що нарешті у середовищі українського християнства виникла група, яка&nbsp;<a href=\"http://catholicnews.org.ua/ievangelski-cerkvi-zvertayut-uvagu-kandidativ-u-deputati-na-problemi-morali-ta-korupciyi\">звернулася</a> до кандидатів у депутати та політичних партій з проханням висловитися з питань моралі. Відверто кажучи, як католику, мені прикро, що це не зробила УГКЦ чи РКЦ в Україні, а ініціювали протестантські конфесії. Але як християнина мене такий вчинок частини українських протестантів не може не тішити. Звісно, доводиться сумніватися, що кандидати в депутати та політичні партії відгукнуться на цю ініціативу протестантів. Але вже сама ідея є дуже добрим знаком того, що нарешті українські християни починають робити бодай спроби відверто поставити питання моралі перед українським політикумом. І саме в часі виборчих перегонів.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2461:moralny-aspekt-vyboriv-do-vr&catid=37:spilnota-cat&Itemid=49',4712,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4691,'N','2014-10-01',1,NULL,'Rss','Мене били за те, що я греко-католик','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6195,200,1066532.jpg\" style=\"margin: 10px; float: right;\" />Про своє перебування в полоні, про силу молитви в умовах війни, а також чи справді існує міжрелігійний конфлікт на Донбасі отець Тихон (Кульбака) говорив 30 вересня в рамках освітнього проекту Інформаційно-практичний Центр безпеки міста у Львові.</strong></p>\n<p>Священик майже два місяці після виходу не міг говорити про своє перебування в полоні: від цього досі можуть постраждати його парафіяни. Нагадаємо, 4 липня терористи з «Російської православної армії» взяли його в полон, де утримували десять діб.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2463:mene-byly-za-te-shcho-ja-greko-katolyk&catid=37:spilnota-cat&Itemid=49',4713,NULL,1,26),
(4692,'P','2014-09-30',1,NULL,'Note','«Активне батьківство»','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Мета семінару:</strong>&nbsp;формування позиції батьків у суспільстві та створення Асоціації Батьків України.</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Семінар відбудеться&nbsp;<strong>17-19</strong>&nbsp;<strong>жовтня</strong>&nbsp;2014 року.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">у смт. Брюховичі (поблизу Львова) у монастирі Св. Йосифа провінції оо. Василіан (див. Мапа 1. смт. Брюховичі, вул. Широка, 6,&nbsp; поблизу Львова)</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава Ісусу Христу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Просимо Вас відрядити до 2-ох батьків (від єпархій, недільних шкіл чи інших організацій)</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">на дводенний семінар&nbsp; &laquo;Активне батьківство&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Мета семінару:</strong> формування позиції батьків у суспільстві та створення Асоціації Батьків України.</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Семінар відбудеться <strong>17-19</strong> <strong>жовтня</strong> 2014 року.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">у смт. Брюховичі (поблизу Львова) у монастирі Св. Йосифа провінції оо. Василіан (див. Мапа 1. смт. Брюховичі, вул. Широка, 6,&nbsp; поблизу Львова):</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">17 жовтня &nbsp;о 16.00 &ndash; реєстрація учасників.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18 жовтня &nbsp;&ndash; другий день семінару.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">19 жовтня &nbsp;о 14.00 &ndash; завершення семінару та від&rsquo;їзд учасників.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Під час семінару будуть порушуватися 3 теми:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>1. Подружжя в Божому плані </strong>(о.Тарас Фітьо, подружжя Кузимкових)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>2. Виховне завдання батьків</strong> (Богдан Трояновський, Маряна Миколайчук)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>3. Роль батьків у формуванні християнських шкіл та участь у формуванні освітніх послуг </strong>&nbsp;(Оксана Кочерган, диспутанти законотворчого процесу базового закону про освіту України)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дані теми будуть розглядатися як з теоретичної так і з практичної сторони.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Витрати на харчування та проживання Комісія УГКЦ у справах освіти та виховання бере на себе. Представники шкіл та єпархій покривають дорожні витрати та складають добровільну пожертву на розвиток Асоціації батьків християн.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Для участі у семінарі, батьки повинні зареєструватися у Комісії освіти та виховання через заповнення анкети, яку ми додаємо додатково.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Просимо аби Ваші представники зареєструвалися до 4 жовтня 2014, надіславши на скриньку комісії (</strong>edu@ugcc.org.ua<strong>) заповнену анкету.</strong>&nbsp; &nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>Секретар Комісії УГКЦ освіти та виховання </em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>Андрій Дацків </em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>097-595-26-40</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">смт. Брюховичі, вул. Широка, 6</span></p>',NULL,NULL,4711,5,1,40);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4693,'N','2014-10-01',1,NULL,'Rss','Притча про великий вибух і прекрасну картину (1 жовтня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6195,200,prirod.jpg\" style=\"margin: 10px; float: left;\" />Якось посперечалися два товариші. Один вірив в Бога, а інший ні. Весь вечір безвірник намагався намагався довести, що Бога немає і що все на землі виникло з вибуху.</p>\n<p>Вранці наступного дня вірний приніс безвірнику картину. І безвірник, побачивши її, вигукнув від захвату:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Який чудовий пейзаж! Хто автор?! Я зачарований його роботою.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2462:prytcha-pro-velyky-vybuh-i-prekrasnu-kartynu-1-zhovtnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4714,NULL,1,26),
(4694,'P','2014-10-02',1,NULL,'Rss','Кінець світу (2 жовтня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6195,200,Ternovyy_Vinok.jpg\" />Настав кінець світу. І мільярди людей, яких перенесли на велетенську рівнину постали перед троном Божим. Багато хто відступив перед сліпучим сяйвом. Але деякі в першому ряду розмовляли. Вони говорили без шаноби, задиристо:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чи може Бог судити нас, що він знає про страждання? – пробурмотіла одна жінка. Вона закотила рукав, щоб показати номер з концентраційного табору. – Ми пережили невимовний жах, катування і смерть!</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2464:kinec-svitu-2-zhovtnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4715,NULL,1,26),
(4695,'P','2014-10-02',1,NULL,'Rss','«Віра, сповнена діяльною любов’ю, дає нам спасіння і через нас притягує інших до Бога»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6195,200,dzjurax_monashestvo.jpg\" />«Вдячний вам за вірність рідній Церкві та матірній землі…, за ваші неустанні молитви за Україну та щедрі пожертви на різні потреби, за допомогу родинам загиблих на Майдані і в АТО, за сприяння в лікуванні поранених. Дякую за свідчення правди про події в Україні, яке дозволяє залучити до підтримки нашої спільної справи широкі верстви польського політикуму і суспільства», - про це сказав Владика Богдан (Дзюрах), Адміністратор Патріаршої курії УГКЦ, Секретар Синоду єпископів УГКЦ, 2 жовтня 2014 року на відкритті&nbsp;<a href=\"http://catholicnews.org.ua/greko-katolickiy-sobor-zberetsya-u-peremishli\">Собору УГКЦ в Польщі</a>.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2465:2014-10-03-21-56-15&catid=28:tema-nomera-cat&Itemid=2',4716,NULL,1,26),
(4696,'P','2014-10-03',1,NULL,'Rss','До якого миру маємо прагнути?','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6199,200,guzar-8b7fd.jpg\" />Пишу про дуже складну, правдоподібно невдячну, але вкрай потрібну справу – про мир.</p>\n<p>Вирішив це зробити не тому, що до цього спонукає мене власне ім’я, що ним нарекли мене під час Хрещення побожні батьки.</p>\n<p>Беруся до цієї теми, бо останнім часом звідусіль чутно багато розмов про мир. Тому треба краще зрозуміти, що таке мир, звідки він походить, як настає і як його можна втратити.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2467:do-jakogo-myru-majemo-pragnuty&catid=28:tema-nomera-cat&Itemid=2',4717,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4697,'P','2014-10-03',1,NULL,'Rss','Мовчазна боротьба (3 жовтня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6195,200,4657.jpg\" />Сім’я у вихідний день відпочивала на пляжі. Діти купалися в морі і будували замки з піску. Зненацька удалині на березі з’явилася маленька бабуся. Її сиве волосся розвівалося на вітрі, одежа була брудна і обшарпана. Вона щось бубніла сама до себе, підносячи із землі якісь предмети і складаючи їх у торбу.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2466:movchazna-borotba-3-zhovtnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4718,NULL,1,26),
(4698,'P','2014-10-04',1,NULL,'Rss','Кривда і мішок картоплі (4 жовтня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6200,200,suha.jpg\" style=\"margin: 10px; float: left;\" />Учень спитав учителя:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ти такий мудрий. Ти завжди в доброму настрої, ніколи не сердишся, поможи і мені стати таким.</p>\n<p>Учитель згодився і попросив учня принести картоплю і прозорий пакет.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Якщо ти на когось розсердишся і задаєш кривду, то візьми цю картоплину: з одного боку напиши своє ім’я, а з іншого ім’я людини з якою в тебе конфлікт і поклади цю картоплину в пакет.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2468:kryvda-i-mishok-kartopli-4-zhovtnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4719,NULL,1,26),
(4699,'P','2014-10-04',1,NULL,'Rss','Шкільна система виховує невдах','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6200,200,images.jpg\" style=\"margin: 10px; float: right;\" />Близько 90% школярів, яких виховують &nbsp;сучасні навчальні заклади, швидше нагадують бездумних роботів для роботи на виробництвах. Адже саме для цього і створювалась 200 років тому система освіти, яка використовується і в наш час.</strong></p>\n<p>Діти по своїй природі&nbsp;активні та життєрадісні, вони люблять бігати, стрибати, долучатися до&nbsp;різних забав. Натомість їх примусово садять за шкільну парту, обмежують будь-який прояв емоцій та еволюцію, не лише інтелектуальну і моральну, а ще й фізичну. У чому ж полягають причини того, що школа замість розвитку дитини, вбиває її природні таланти?</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2469:shkilna-systema-vyhovuje-nevdah&catid=37:spilnota-cat&Itemid=49',4720,NULL,1,26),
(4700,'N','2014-10-04',1,NULL,'Rss','Убивця та свята: Пранзіні й Тереза','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6200,200,3400pranzinithe_00000002685.jpg\" /></strong></p>\n<p><strong>Сенсаційна історія убивці Генрі Пранзіні спонукала юнну Терезу Мартен «усиновити» свого першого грішника.</strong></p>\n<p>1887 року в&nbsp;<em>The Times</em><em> </em>з’явилася така публікація: <em>Париж, 17 березня</em></p>\n<p><em> «На Ру Монтень було скоєно потрійне вбивство. Сьогодні вранці 30-річна куртизанка, відома під іменем Монті або Ренью, була знайдена мертвою біля підніжжя свого ліжка. Дві глибоких рани в горло виявились смертельними.</em></p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2471:ubyvcja-ta-svjata-pranzini--tereza&catid=35:istorychni-postati-cat&Itemid=48',4721,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4701,'P','2014-10-04',1,NULL,'Rss','Убивця та свята: Пранзіні й Тереза','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6200,200,3400pranzinithe_00000002685.jpg\" /></strong></p>\n<p><strong>Сенсаційна історія убивці Генрі Пранзіні спонукала юнну Терезу Мартен «усиновити» свого першого грішника.</strong></p>\n<p>1887 року в&nbsp;<em>The Times</em><em> </em>з’явилася така публікація: <em>Париж, 17 березня</em></p>\n<p><em> «На Ру Монтень було скоєно потрійне вбивство. Сьогодні вранці 30-річна куртизанка, відома під іменем Монті або Ренью, була знайдена мертвою біля підніжжя свого ліжка. Дві глибоких рани в горло виявились смертельними.</em></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2472:ubyvcja-ta-svjata-pranzini--tereza&catid=35:istorychni-postati-cat&Itemid=48',4722,NULL,1,26),
(4702,'P','2014-10-04',1,NULL,'Rss','Глибина молитви (5 жовтня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6200,200,fL1m1it1cpg.jpg\" />Одна дівчина пішла полем де побожний чоловік ревно молився. Закон їхньої релігії не дозволяв проходити повз таке місце. Коли сільська дівчина поверталася, побожний чоловік сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Як же неґречно ти вчинила, дівчино. Це ж гріх іти там, де молиться чоловік.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А що ви вважаєте молитвою?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Молитвою? – вигукнув він, - що це таке? Солитися, значить думати про Бога.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2470:glybyna-molytvy-5-zhovtnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4723,NULL,1,26),
(4703,'P','2014-10-06',1,NULL,'Rss','«Пострадянської України більше не існує…»','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,z_tv.jpg\" />«Ніколи не буде так, як раніше». Ці слова Блаженнішого Любомира, сказані в Києві в листопаді минулого року, коли розпочалися студентські протести, дуже глибокі і значущі. Бо вони вказують, що події, які сталися на наших очах, є свідченням постання нової України. Це початок нового етапу в історії нашого українського суспільства, в історії наших вірян в Україні та діаспорі.</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2473:2014-10-06-17-46-45&catid=27:gk-v-ukrajini-cat&Itemid=57',4724,NULL,1,26),
(4704,'P','2014-10-06',1,NULL,'Rss','Істинне життя (6 жовтня)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6204,200,praying_man_at_altar.jpg\" style=\"margin: 10px; float: left;\" />Одного разу до священика прийшов молодик і сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Отче, ви маєте рацію. Мені б також треба шукати того страждальця Голгофи і стати дитиною Божою. Коли небудь я покаюся.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Коли небудь? – запитав священик</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ну, так, згодом.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2475:istynne-zhyttja-6-zhovtnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4725,NULL,1,26),
(4705,'P','2014-10-06',1,NULL,'Rss','Гнів і сонце (7 жовтня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,f_2100155071362073598.jpg\" />Маленький хлопчик побився зі своїм братом. Зазнавши нищівної поразки він насупився і сів у кутку. Коли брат підійшовши до нього спробував помиритися, він відмовився слухати. Так і супився цілісінький день.</p>\n<p>Настав вечір, мама сказала йому:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Може пробачиш брата перед сном? Пам’ятаєш, у Святому письмі сказано: «хай сонце не заходить над вашим гнівом» (Еф. 4,26)</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2476:gniv-i-sonce-7-zhovtnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4726,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4706,'P','2014-10-06',1,NULL,'Rss','Бог і тільки Він дає нам сенс життя!','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,xe234567ihg.png\" />Уявімо. Текст про те, що злоякісне новоутворення слід долати молитвою…</strong></p>\n<p>«В одній газеті написали статтю, де злоякісні пухлини радиться лікувати медикаментами! Причому дуже сильними і з багатьма побічними наслідками! А&nbsp;ми маємо кращі ліки! Які? Нині щоразу більше людей у світі хворіють на рак. Зростає тривожна статистика кількості захворювань, людей, яких лікують хіміотерапією, які ходять до онкологів через злоякісні новоутворення. Чому мало хто помічає, що це відбувається паралельно з лаїцизацією, відходженням від Бога і Його заповідей?</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2474:bog-i-tilky-vin-daje-nam-sens-zhyttja&catid=37:spilnota-cat&Itemid=49',4727,NULL,1,26),
(4707,'P','2014-10-08',1,NULL,'Rss','Любов і закоханість (8 жовтня)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6210,200,shutt.jpg\" />-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ах, любове! Я так мрію бути такою як ти. – закохано повторювала закоханість. –Ти набагато сильніша від мене.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А ти знаєш у чому моя сила – спитала любов, замислено хитаючи головою.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ти важливіша для людей.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ні, любо, зовсім не тому, - зітхнула любов і погладила закоханість по голові. – Я вмію прощати.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2487:ljubov-i-zakohanist-8-zhovtnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4728,NULL,1,26),
(4708,'P','2014-10-12',0,NULL,'Note','Свято Покрови Пресвятої Богородиці у Пирогово','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,6212,200,123400.jpg\" alt=\"\" width=\"200\" height=\"169\" /><br /></strong>Слава Ісусу Христу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>12 жовтня 2014</strong> року Божого у <strong>с. Пирогів цекровця Покрови пресвятої Богородиці</strong> має храмове свято. О <strong>10.00</strong> від <strong>Аскольдової Могили</strong> буде <strong>відправлятися безкоштовний автобус</strong> для всіх, хто хоче взяти участь у Святій Літургії та культурній програмі в Пирогові.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Запрошуємо це світле свято зустріти разом, розділити ту радість празника у мальовничому місці.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,6212,200,123400.jpg\" alt=\"\" width=\"200\" height=\"169\" /></strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава Ісусу Христу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>12 жовтня 2014</strong>&nbsp;року Божого у&nbsp;<strong>с. Пирогів цекровця Покрови пресвятої Богородиці</strong>&nbsp;має храмове свято. О&nbsp;<strong>10.00</strong>&nbsp;від&nbsp;<strong>Аскольдової Могили</strong>&nbsp;буде&nbsp;<strong>відправлятися безкоштовний автобус</strong>&nbsp;для всіх, хто хоче взяти участь у Святій Літургії та культурній програмі в Пирогові.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Запрошуємо це світле свято зустріти разом, розділити ту радість празника у мальовничому місці.</span></p>',NULL,NULL,4729,5,1,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4709,'P','2014-10-08',1,NULL,'Rss','Чи перетвориться Революція гідності у Революцію Лінча?','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6210,200,6fd040.jpg\" />Тяжко усвідомлювати, що&nbsp;винуватців злочинів часів Януковича не притягнуто до відповідальності. Я не знаю, які причини цього; не знаю усіх закулісних компромісів і домовленостей. Припускаю, що, як завжди, тут спрацьовує те, що політика – це «мистецтво можливого». Проте «непотоплюваність» усіх отих шуфричів, єфремових, колесниченків, їхня переконаність, що їм і цього разу вдасться «развести всех, как котят» і знову, як після Помаранчевої революції, перетворитися зі злочинців на політичних опозиціонерів, здатні вивести з себе навіть найсумирнішу людину.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2488:chy-peretvorytsja-revoljucija-gidnosti-u-revoljuciju-lincha&catid=37:spilnota-cat&Itemid=49',4730,NULL,1,26),
(4710,'P','2014-10-08',1,NULL,'Rss','«Мусимо чувати, аби така шляхетна ідея, як національна, не посіла місця Бога»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6212,200,img_9575.jpg\" />2-4 жовтня 2014 року в Перемишлі проходив Собор УГКЦ у Польщі «Жива парафія – місце зустрічі з живим Христом». У ньому взяв участь, окрім Митрополита Перемишльсько-Варшавського Івана (Мартиняка), Єпарха Вроцлавсько-Гданського Володимира (Ющака) та Єпископа-помічника Перемишльсько-Варшавського Євгена (Поповича), Секретар Синоду Єпископів УГКЦ, заступник голови робочої групи з реалізації Програми розвитку Української Греко-Католицької Церкви на період до 2020 року «Жива парафія – місце зустрічі з живим Христом» владика Богдан (Дзюрах). Про нові виклики та соборові враження – у нашому інтерв’ю.</p>\n<p><strong>&nbsp;</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2489:2014-10-08-18-59-45&catid=27:gk-v-ukrajini-cat&Itemid=57',4731,NULL,1,26),
(4711,'P','2014-10-08',1,NULL,'Rss','Папа Франциск: коли молимося, не потрібно забувати про своє минуле, не слід приховувати власних гріхів','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6212,200,Martha_Bethanien.jpg\" />Коли молимося, не потрібно забувати про своє минуле, не слід приховувати власних гріхів. Завжди треба пам’ятати, що Господь прямує з нами шляхом нашого життям. На цьому зосередив свою увагу Папа Франциск у проповіді під час Служби Божої, яку він очолив у каплиці «Дому Святої Марти» у Ватикані у вівторок, 7 жовтня 2014 року. Вселенський Архиєрей наголосив, що Господь Бог вибрав єврейський народ і супроводжував його продовж усього переходу через пустиню, прямував із ним протягом усього його життя. Як Всевишній поводився із Своїм вибраним народом, так поводиться сьогодні із кожним з нас, тобто, крокує з нами пліч-о-пліч дорогою нашого життя.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2490:papa-francysk-koly-molymosja-ne-potribno-zabuvaty-pro-svoje-mynule-ne-slid-pryhovuvaty-vlasnyh-grihiv&catid=32:gk-svit-cat&Itemid=58',4732,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4712,'P','2014-10-09',1,NULL,'Rss','Даруймо нашим агресорам любов, тобто Бога','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6212,200,venedykt_rik.jpg\" />9 жовтня Греко-Католицька Церква святкує Переставлення святого апостола і євангелиста Івана Богослова. Як відомо, саме цьому апостолу належить авторство пророчої Книги Одкровення про кінець світу. А ще святий прославився своїм навчанням про любов, якої, на його переконання, достатньо, щоб звершувалося Боже Слово на землі.<o:p></o:p></p>\n<p>Про постать наймолодшого серед апостолів — Івана Богослова, а також про те, чи варто любити своїх «агресорів», розповідає владика Венедикт Алексійчук, голова Патріаршої літургійної комісії УГКЦ.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2491:darumo-nashym-agresoram-ljubov-tobto-boga&catid=27:gk-v-ukrajini-cat&Itemid=57',4733,NULL,1,26),
(4713,'P','2014-10-10',1,NULL,'Rss','Преподобний Никодим Святогорець про молитву','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,niksvyat.jpg\" />Молитва є засобом для прийняття всіх благ, щедро пролитих на нас невичерпним джерелом безмежної Божої благодаті та любові. Той, хто виголошує молитву, наче вкладає свій бойовий меч у Божу правицю, щоб Господь поборов його ворогів. Але молитва буде дієвою лише тоді, коли буде з тобою постійно, як природний навик твого духу. Цього можна досягти, дотримуючись таких принципів:</p>\n<p>1. Завжди май живе прагнення служити єдиному Богові так, як це Йому бажано. Для підтримання цього бажання невідступно думай про те, що Господу за Його предивні властивості (благодать, велич, премудрість та інші незчисленні й безмірні досконалості), кожне розумне творіння має віддавати поклоніння, честь і служіння.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2477:prepodobny-nykodym-svjatogorec-pro-molytvu&catid=35:istorychni-postati-cat&Itemid=48',4734,NULL,1,26),
(4714,'P','2014-10-11',1,NULL,'Rss','Святий Євагрій про молитву','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,124.jpg\" />Cвятий Євагрій розуміє під молитвою скерованість усіх душевних сил до Бога, свою цілковиту присутність у Його присутності. Це є найбільший дарунок, який лише може зробити Бог людині. Це підносить людину над її власною природою та робить її рівною з ангелами: «Людина, яка по-справжньому молиться, подібна до ангелів. Бо вона безперестанку прагне бачити Отця, що перебуває на небі» (Про молитву, 113-114).</p>\n<p>Щоб людина справді могла молитися, вона мусить позбутися усякого розсіяння та спрямувати свою увагу на Бога: «Блаженний дух, що, молячись без розсіяння, відчуває все глибше прагнення Бога» (Про молитву, 118). «Коли ти з внутрішнім зосередженням шукаєш молитви, тобі нічого не перешкоджатиме, тому що зосередженість є засобом, який найкраще прокладає шлях до молитви. Зосередженість мусиш плекати» (Про молитву, 149).</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2478:svjaty-jevagri-pro-molytvu&catid=35:istorychni-postati-cat&Itemid=48',4735,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4715,'P','2014-10-12',1,NULL,'Rss','Вчення про молитву валаамських старців','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,119.jpg\" />1.&nbsp;<strong>Про усну молитву</strong>. У скороченому варіанті її промовляють так: «Господи, помилуй!»; «Господи Ісусе Христе, помилуй мене, грішного!»; а в повному&nbsp;— так: «Господи Ісусе Христе, Сину Божий, помилуй мене, грішного!»</p>\n<p>Спочатку її промовляють здебільшого з неохотою, силуючи себе, і в міру вправляння та примушування себе до неї, якщо тільки є рішучий намір через молитву, з допомогою Божої благодаті позбавлятися пристрастей, вона, від частого вправляння у ній, по мірі викоренення пристрастей, час від часу буде ставати легшою, приємнішою і бажанішою.</p>\n<p>При усній молитві всіма можливостями треба старатися тримати розум на словах молитви, говорити повільно, всю увагу зосереджуючи на думках, що виражаються словами, а коли ум буде відволікатися на сторонні думки, без збентеження знову вводити його в слова молитви («Ліствиця», слово 28, гл. 17).</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2479:vchennja-pro-molytvu-valaamskyh-starciv&catid=35:istorychni-postati-cat&Itemid=48',4736,NULL,1,26),
(4716,'P','2014-10-13',1,NULL,'Rss','Ігумен валаамського монастиря Харитон про молитву','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6204,200,38ondap.jpg\" style=\"margin: 10px; float: right;\" />Про молитву, яка зігріває людину і з’єднує її з Богом в любові</strong></p>\n<p>Найперше має бути відомим те, що християнинові (особі духовній своїм званням) належить завжди різними способами піклуватися про з’єднання з Богом Творцем, люблячим Благодітелем і вищим своїм добром, Котрим і для Котрого він і був створений, бо для душі, яку створив Бог, центром, тобто остаточною метою, має бути не що інше, хіба лише один Бог, від якого вона дістала і життя, і природу свою й задля якого вона має жити вічно. Адже все видиме на землі, любе й бажане: багатство, слава, жінка, діти, одним словом все красиве, солодке й миле цього світу не є властиве душі, а лише тілу, й, будучи дочасним, воно швидко промине, як тінь,&nbsp;— натомість душа, як вічна згідно з своєю природою, може навіки знайти спокій лише в однім вічнім Бозі, як у вищому своєму добрі, прекраснішому за всю красу, солодощі й замилування, солодшому й милішому,&nbsp;— в Бозі, що є для неї найбільш природним її місцем, звідки вона походить і куди має знову повернутися: бо як плоть, що походить від землі, що в землю повертається, так само й душа від Бога походить й до Бога повертається і в Ньому перебуває. Для цього вона й створена Богом, щоби перебувати в Бозі навіки. Тому в цьому дочасному житті необхідно старанно шукати з’єднання з Богом, щоби сподобитись бути з Ним і в Ньому вічно і у майбутньому житті.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2480:igumen-valaamskogo-monastyrja-haryton-pro-molytvu&catid=35:istorychni-postati-cat&Itemid=48',4737,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4717,'P','2014-10-14',1,NULL,'Rss','Покров Пресвятої Богородиці','<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,456.jpg\" />Лк. 10, 32-48 </em></p>\n<p>Кожне Богордичне свято відкриває нам одну з чеснот, привілей Богородиці: Богоматеринство, Дівство, Чистоту. Свято Покрову урочисто вшановує її Заступництво. Це свято найкраще виявляє духовне материнство Марії, зокрема її молитовне заступництво, любов і опіку над нами. Добра мати завжди переживає за своїх дітей - подібно Марія дбає про наше тілесне і духовне добро, вічне щастя. У малому повечір’ї вірні кожного разу величають Марію словами: „Заступнице християн бездоганна, молитвенице до Творця незамінна”. А в Молебні звертаються до неї такими словами: „Радуйся, заступнице всіх християн і прибіжище, Владичице”.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2486:pokrov-presvjatoji-bogorodyci&catid=28:tema-nomera-cat&Itemid=2',4738,NULL,1,26),
(4718,'P','2014-10-15',1,NULL,'Rss','Повчання про молитву і тверезість святих великих старців Варсонофія та Іоана','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,122.jpg\" />Досконала молитва полягає в тому, щоб бесідувати з Богом, не розсіюючись думками, збираючи всі свої помисли й почуття. Людина входить у такий стан, коли вмирає для всіх людей, для світу и для всього, що в нім є. І такий більше нічого не має говорити Богові в молитві, а лиш: «Нехай буде воля Твоя в мені»,&nbsp;— та її мати в умі, що стоїть перед Богом і з Ним бесідує.</p>\n<p>Творити молитви в кінці свого молитовного правила про мир Церков, про владу, про всіх людей, убогих, вдів та про інших&nbsp;— добре, бо про це заповідав апостол. Але, виконуючи це, треба усвідомлювати себе негідними й такими, що не мають на це сили. Добре молитися і про кожного, хто просить молитви. Бо слово апостола: «Моліться один за одного, щоб вам видужати» (Як. 5, 16). Дехто й за апостолів молився. Хто не дбає про заповідь, той сам себе осуджує, а тому можеш чи ні, присилуй себе виконувати заповіді.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2481:povchannja-pro-molytvu-i-tverezist-svjatyh-velykyh-starciv-varsonofija-ta-ioana&catid=35:istorychni-postati-cat&Itemid=48',4739,NULL,1,26),
(4719,'P','2014-10-16',1,NULL,'Rss','Слово про молитву преподобного Ніла, подвижника Синайського','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,121.jpg\" />Молитва&nbsp;— це бесіда ума до Бога. Який же потрібно лад для ума, щоб він міг несхитно піднятися до свого Владики й розмовляти з Ним без жодного посередника.</p>\n<p>Стій до втоми й молися благотонно (благоумно або благоналаштовано), відкидаючи підступи життєвих турбот і всіляких помислів; вони-бо бентежать тебе й турбують, щоб розладнати твій тон (добре налаштування).</p>\n<p>Коли демони побачать, що хтось має старанність і ревність до належної молитви, то (не під час молитви) вкладають йому думки неначе про щось потрібне (і відходять), а трохи згодом знову розбуджують про це спогад, підбурюючи й ум шукати це (якщо питання, то вирішити його, а якщо річ, то здобути її), і він, не знаходячи цього (вирішення питання чи способу здобути річ) досадує й журиться. Потім, коли стане на молитву (цей старанний ревнитель молитви), вони нагадують йому, про що думав і що він шукав, щоб ум, знову піднявшись вивідувати (те, що не розв’язав раніше), вчинив молитву (молільника) безплідною.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2482:slovo-pro-molytvu-prepodobnogo-nila-podvyzhnyka-synaskogo&catid=35:istorychni-postati-cat&Itemid=48',4740,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4720,'P','2014-10-17',1,NULL,'Rss','Святого Єфрема Сирійського повчання про молитву і духовну тверезість','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,118.jpg\" />Частіше молися зі смиренним серцем, як три юнаки в палаючій печі, і не роби зі себе печери розбійників, віддаючись недозволеним ділам, щоб не осоромитися тобі в день суду, коли відкриються людські тайни.</p>\n<p>Зітхає моє серце, й очі мої жадають сліз; але гріх мій полонив мій ум, аби не зазнав я скрухи й почав гіркими слізьми благати Бога, щоб Він не поверг мене в кромішню пітьму.</p>\n<p>Іона, перебуваючи в череві кита, голосив у молитві: «Ти вивів моє життя з ями, о Господи, мій Боже» (Іона 2, 7). Молитва ця розсікла безодню, повітря, сягнула неба й увійшла до вух Господа. Краще мовити, сам Господь, що, сповнює всесвіт, не був далекий від свого щирого служителя.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2483:svjatogo-jefrema-syriskogo-povchannja-pro-molytvu-i-duhovnu-tverezist&catid=35:istorychni-postati-cat&Itemid=48',4741,NULL,1,26),
(4721,'P','2014-10-18',1,NULL,'Rss','Святого Іоана Золотоустого повчання про молитву і духовну тверезість','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,117.jpg\" />Господь заповідає не лише прощати тому, хто обкрадає тебе і займається лихварством, а ще й каже любити його як найсильніше та щиро. Бо це, саме це бажаючи прищепити, сказав Він: «Моліться за тих, що вас зневажають» (Лк. 6, 28)&nbsp;— що ми зазвичай робимо для тих, кого сильно любимо. Так Христос заповів молитися за кривдників&nbsp;— ми ж засідки їм влаштовуємо; і, отримавши наказ благословляти тих, що нас клянуть, закидуємо їх тисячами проклять.</p>\n<p>Молитва&nbsp;— це велика зброя, невичерпний скарб, багатство, що ніколи не зміліє, супокійне пристановище, несхитний затишок і корінь незліченних благ; і джерело, і матір є молитва: вона могутніша за саму царську владу. Молитвою ж я називаю не будь-яку, не недбалу і розсіяну, а полум’яну й многотрудну, що виходить зі зболеної душі й глибоко зосередженого ума. Лишень така молитва сходить до неба.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2484:svjatogo-ioana-zolotoustogo-povchannja-pro-molytvu-i-duhovnu-tverezist&catid=35:istorychni-postati-cat&Itemid=48',4742,NULL,1,26),
(4722,'P','2014-10-19',1,NULL,'Rss','Повчання святого Василія Великого','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,116.jpg\" />Прохання про блага, з яким побожні звертаються до Господа, є молитвою. Однак її слід підносити не лише словесно, а насамперед у душевній налаштованості на молитву. І молитися треба завжди, з будь-якої нагоди. Сидячи за столом&nbsp;— молися; споживаючи хліб&nbsp;— склади подяку Богові, який подав тобі харч; зміцнюючи неміч тіла вином, пам’ятай Того, Хто дав цей дар, щоб звеселити серце та усунути недуги. Коли ж завершиш трапезу&nbsp;— нехай не гасне в тобі пам’ять про Благодійника.</p>\n<p>На будь-який заклик тебе до молитви відповідай: «Готове серце моє»,&nbsp;— і перебувай на богослуженні аж до останньої молитви, маючи за велику для себе шкоду полишити її. Бо ж коли, споживаючи їжу для підкріплення своєї плоті, перебуваєш за трапезою невідлучно, допоки не наситишся, і безконечної потреби нелегко встаєш з-за столу, то чи ж не більше маєш ти залишатися до кінця духовної поживи й укріпляти душу молитвою?</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2485:povchannja-svjatogo-vasylija-velykogo&catid=35:istorychni-postati-cat&Itemid=48',4743,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4723,'P','2014-10-24',1,NULL,'Rss','«УГКЦ стоїть перед викликом ліквідації свого Кримського екзархату…»','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6226,200,unnamed_6dfce.jpg\" />УГКЦ уперше за свою історію з часів виходу з підпілля стоїть перед викликом ліквідації свого Кримського екзархату. Влада Криму видала закон, за яким усі релігійні організації мають пройти процес перереєстрації до кінця 2014 року. Немає гарантії, що такий процес потвердить право на існування УГКЦ на цих теренах.</strong></p>\n<p>Це заявив Блаженніший Святослав, Глава УГКЦ, під час прес-конференції 23 жовтня 2014 року у Львові в рамках Зустрічі східних католицьких ієрархів Європи, яка цього року присвячена 25-річчю виходу УГКЦ з підпілля.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2492:2014-10-24-05-27-43&catid=27:gk-v-ukrajini-cat&Itemid=57',4744,NULL,1,26),
(4724,'P','2014-10-23',1,NULL,'Rss','Звернення Всеукраїнської ради Церков і релігійних організацій з нагоди виборів до Верховної Ради України','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6226,200,vybory3.jpg\" />Шановні співвітчизники!</strong></p>\n<p>26 жовтня 2014 року відбудуться вибори до Верховної Ради України. Перед новим складом парламенту стоятимуть надзвичайно важливі завдання забезпечити мир і територіальну цілісність для України, сприяти реформам та відновленню економіки.</p>\n<p>Слід підкреслити, що після конституційної реформи на Верховну Раду лягає більша відповідальність, адже від парламенту та утвореної в ньому більшості депутатів залежить формування Уряду. Тому більша відповідальність лягає і на кожного громадянина, адже від нашого спільного вибору залежить склад нової Верховної Ради.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2494:zvernennja-vseukrajinskoji-rady-cerkov-i-religinyh-organizaci-z-nagody-vyboriv-do-verhovnoji-rady-ukrajiny&catid=37:spilnota-cat&Itemid=49',4745,NULL,1,26),
(4725,'P','2014-10-22',1,NULL,'Rss','«Державне самогубство, коли громадяни не користуються правом обирати»','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6226,200,huzar_blogy.jpg\" />Кожен мусить покласти свою лепту на розвиток демократії «Популізм – то пуста мова, - говорить Блаженнійший. – У Святому Письмі написано: будуть покарані за кожне пусте слово». Як не піддатися на політичний популізм, а з-поміж безлічі кандидатів обрати достойного, на порозі яких змін опинилася Україна та які реформи потрібно впровадити, аби не втратити шанс отримати нову країну з політиками якісно нового рівня, про все це напередодні парламентських виборів ми говорили з видатним церковним мислителем нашого часу - Любомиром Гузаром.</strong>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2493:2014-10-24-05-31-23&catid=28:tema-nomera-cat&Itemid=2',4746,NULL,1,26),
(4726,'P','2014-10-21',1,NULL,'Rss','«Церква Московського патріархату може далеко більше зробити для деескалації насильства в Україні та примирення»','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6226,200,u_zadumi.jpg\" />16 жовтня 2014 року на пленарній сесії Синоду Єпископів у Ватикані Митрополит РПЦ Іларіон Алфеєв під час свого виступу вкотре заговорив про проблеми унії, яка, на його думку, загострилася у зв’язку з останніми подіями в Україні. Свої коментарі щодо цього в ефірі SiriusXM The Catholic Channel дав Отець і Глава УГКЦ Блаженніший Святослав.</strong></p>\n<p><strong>Ваше Блаженство, у своєму виступі на пленарній сесій Синоду Єпископів у Ватикані 16 жовтня цього року Митрополит Іларіон Алфеєв не оминув нагоди ще раз відкрито атакувати Українську Греко-Католицьку Церкву. Зокрема, він вкотре заговорив про проблеми унії, яка, на його думку, загострилася у зв’язку з останніми подіями в Україні. Чому така агресія?</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2495:2014-10-24-05-39-05&catid=28:tema-nomera-cat&Itemid=2',4747,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4727,'P','2014-10-20',1,NULL,'Rss','Ідея любові до свого народу дає нашій армії перевагу над армією загарбника','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6226,200,huzar_velykden14.jpg\" /></strong>Живемо в стані війни. На кордоні між Україною та Російською Федерацією - військові об’єднання: одні готові нападати на нашу державу, інші - боронити її від чужого нападу.</p>\n<p>Армія Російської Федерації чисельна, ідеться про десятки тисяч активних воїнів. Вони мають все необхідне: достатнє харчування, відповідний одяг-однострій, сучасну зброю, танки, гармати, літаки, про всяк випадок - медичні пункти. Ці люди пішли на фронт, усвідомлюючи всі його небезпеки, але вони спокійні за долю своїх родин. Про їхню діяльність розповідають відповідно налаштовані засоби масової інформації. З українського боку не все так добре.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2496:ideja-ljubovi-do-svogo-narodu-daje-nashi-armiji-perevagu-nad-armijeju-zagarbnyka&catid=28:tema-nomera-cat&Itemid=2',4748,NULL,1,26),
(4728,'P','2014-10-25',1,NULL,'Rss','Любомир Гузар: в Україні не громадянська війна, ми виходимо з неправдивого миру','<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6227,200,199428.jpg\" />Знайти резиденцію архієпископа-емерита Любомира Гузара виявилося нелегко. Блаженніший живе на околиці села під Києвом. Живе настільки тихо, що небагато хто з місцевих жителів знає, де розташований його будинок – там, де закінчується асфальтована дорога.</em></p>\n<p><em>Колишній голова Української Греко-католицької церкви прийняв знімальну групу \"Обозревателя\" як дорогих гостей. Ми відчували себе в його резиденції, як вдома. І як найстаріший член родини, Блаженніший Любомир, відповідаючи на наші запитання, казав про речі, що видні лише здалека – з відстані фізичної і історичної.</em>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2497:ljubomyr-guzar-v-ukrajini-ne-gromadjanska-vina-my-vyhodymo-z-nepravdyvogo-myru&catid=28:tema-nomera-cat&Itemid=2',4749,NULL,1,26),
(4729,'P','2014-10-28',1,NULL,'Rss','Парох і парохія','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6232,200,464548.jpeg\" />Святочним днем парафії можна назвати неділю 12 жовтня 2014 р. Б ,день пам’яті прп. Киріяка самітника. То справжнє свято для парафіяльної спільноти і духовенства церкви св. Миколая Мирлікійського на Аскольдовій Могилі, адже на Архиєрейській Божественній Літургії Владика Йосиф Мілян вручив засновнику громади, адміністратору парафії о. Ігорю Онишкевичу грамоту пароха.</p>\n<p>Маємо надію в Бозі, що ця грамота пароха, яка ствердила громаду як парохію, дасть нашим душпастерям, сестрам-служебницям Непорочної Діви Марії які обслуговують цей Храм і очолюють катехитичну недільну школу, парафіянам і прихожанам, духовне піднесення закладати цеглини Аскольдової Лаври, допоможе всім нам краще працювати для Слави Божої, добра Церкви і для спасіння повірених душ.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2498:paroh-i-parohija&catid=25:parafija-cat&Itemid=27',4750,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4730,'P','2014-10-29',1,NULL,'Rss','Блаженніший Любомир: «Знову про мир»','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6233,200,v_zadumi.jpg\" /></strong>Компендіум суспільного вчення Католицької Церкви, книжка, з якої черпаємо думки для написання матеріалів про мир, наголошує на важливості молитви для встановлення миру. Церква не тільки заохочує це робити, а й практикує.</p>\n<p>Для прикладу, Свята Літургія, найважливіша з усіх молитов, починається словами: \"У мирі Господеві помолімося. За мир з висот… помолімося. За мир всього світу… помолімся\". А закінчується проханням до Господа: \"Мир світові даруй, церквам Твоїм, Богом береженому народові нашому, правлінню і всім людям Твоїм\".</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2499:2014-10-29-21-28-25&catid=28:tema-nomera-cat&Itemid=2',4751,NULL,1,26),
(4731,'P','2014-10-30',1,NULL,'Rss','Любомир Гузар: це російсько-українська війна, яку готували 10 років','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6234,200,wil6_guzar_01_e51a2.jpg\" />Колишній Глава Української Греко-Католицької Церкви Блаженніший Любомир (Гузар) розповів, як зробити Східну Україну вільною від російської пропаганди, чому Схід і Крим не відчувають себе повною мірою українськими, а також – про політичну роль Церкви і патріотизм.</strong></p>\n<p><strong>1. Це – російсько-українська війна</strong> (на Сході України, – ред.). І цей конфлікт не виник вчора, його послідовно готували років десять. Ті, хто був при владі в Україні, певною мірою були ставлениками Росії. І те, що ми сьогодні маємо на сході — це результат зомбування людей, яких довгі роки годували однією і тією ж брехнею. Вони й повірили.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2501:ljubomyr-guzar-ce-rosisko-ukrajinska-vina-jaku-gotuvaly-10-rokiv&catid=28:tema-nomera-cat&Itemid=2',4752,NULL,1,26),
(4732,'P','2014-10-30',1,NULL,'Rss','Хелловін (Halloween) – антихристиянське свято!','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6233,200,20111031200831.jpg\" />Внаслідок різних часових і просторових обставин серед українського суспільства стають популярними різноманітні святкування, які не мають жодного коріння в нашому народі. До них можна віднести також сьогодні популярне святкування Хелловіна (31 жовтня). З мас-медіа кожного разу напередодні цього дня можна почути запрошення на вечірки та забави з нагоди святкування «Хелловіна». Що цеза свято? Яка його історія? І чи варто греко-католикам, і, взагалі, християнам, відзначати цей день у своєму календарі?</p>\n<p><strong>&nbsp;</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2500:hellovin-halloween--antyhrystyjanske-svjato&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4753,NULL,1,26),
(4733,'P','2014-10-31',1,NULL,'Rss','Папа: диявол не міф, його перемагають зброєю істини','<p><img style=\"margin: 10px; float: right;\" src=\"http://popefrancis.org.ua/wp-content/uploads/2014/10/1_0_833775.jpg\" />Християнське життя – це «боротьба» проти диявола, світу цього і плотських пристрастей. Коментуючи уривок з Послання апостола Павла до Ефесян, Святіший Отець підкреслив, що диявол існує, і ми «повинні боротися з ним зброєю істини».<o:p></o:p></p>\n<p>Апостол, зазначив Папа, каже про християнське життя військовою мовою. «Життя в Бозі має бути захищене, і щоб його зберегти, ми повинні боротися», а для цього потрібні мужність і сила, щоб протистояти і благовістити.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2502:papa-dyjavol-ne-mif-ogo-peremagajut-zbrojeju-istyny&catid=32:gk-svit-cat&Itemid=58',4754,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4734,'P','2014-11-01',1,NULL,'Rss','Урочистість Усіх Святих','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6236,200,Swietych.png\" />Ці «великі небесні іменини» об’єднують усіх. І тих, чиї імена завжди є в літургійному календарі, й тих, хто туди потрапляє рідко. І тих, кого Бог знає, а Церква офіційно святим не назвала, бо, може, і не здогадується, що то була свята людина. Радісна урочистість Усіх Святих промовляє до сердець віруючих: на Небесах так багато наших братів і сестер, наших заступників і помічників, – радіймо їм і живімо добре, аби долучитися до них свого часу! Урочистість Усіх Святих – це зовсім не «гробки», це торжество святості!</p>\n<p>А ще ця урочистість відображає певний «вплив», або ж реакцію християнства на явище язичництва. З історичного погляду, вшанування святих у Церкві не виникло саме по собі як окреме свято чи особливо підкреслювана побожність. Віруючі вшановували Матір Спасителя, загиблих за віру зі своїх спільнот (а потім сформувався культ мучеників), з часом Церква у своїй повноті дійшла до розуміння великої цінності дівоцтва перед Богом – постало вшанування дів…</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2503:urochystist-usih-svjatyh&catid=35:istorychni-postati-cat&Itemid=48',4755,NULL,1,26),
(4735,'P','2014-11-03',1,NULL,'Rss','Сенс смерті','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6238,200,d135363934.jpg\" /></strong>Жити глибиною духа означає приймати життя цілісно, разом із його завершальним етапом&nbsp;— смертю. Смерть це завжди кінець біологічного життя, фізичний процес розкладу, деструкція, перед лицем якої людина залишається безсилою. Також вона є небуттям несповнених мрій, життя, існування.</p>\n<p>Давні монаші статути, наприклад, бенедиктинський чи камедульський, радили щодня згадувати про смерть. Однак не згадувалося про смерть заради тривання в смутку, але навпаки, аби жити свідомо й інтенсивно. Якби ми щодня думали, що можемо не дочекатися світанку, то кожен день переживали б у спосіб більш свідомий та інтенсивний.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2504:sens-smerti&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4756,NULL,1,26),
(4736,'P','2014-11-03',1,NULL,'Rss','Російсько-радянська релігійна міфотворчість','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6239,200,images.jpg\" />Пострадянський простір живе подвійним, а то й потрійний життям. Кілька поколінь людей народилися в часи Сталіна, Хрущова і Брежнєва, а значить, що частина їх віку була пов\'язана з тим рівнем життя, який встановлювала КПРС та інші партійно-господарські органи. Життя було розписане по пунктах для всіх. Партія знала, хто повинен і де вчитися, якої національності мають бути майбутні інженери або лікарі, де вони працюватимуть&nbsp; за&nbsp; \"напрямками\", скільки отримувати «зарплати» і коли їм дозволяти вступати в партію. Життя радянської людини регулювали парткоми, райкоми, міськкоми і обкоми, якими, в свою чергу керувала ЦК. Нагляд здійснював КДБ і численні помічники - піонери (юні друзі міліції) і комсомольці (оперативні комсомольські загони), а також дружинники і таємні інформатори. За 74 роки виросло кілька поколінь слухняних людей, для яких розпад СРСР став особистою трагедією - вони залишилися без спрямовуючої ролі ЦК КПРС.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2505:rosisko-radjanska-religina-mifotvorchist&catid=37:spilnota-cat&Itemid=49',4757,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4737,'P','2014-11-03',1,NULL,'Rss','Мирослав Маринович: приклад Ісуса - це найбезумніший бізнес-проект','<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6240,200,marynovych_2f5a0.jpg\" />Влада це - екстракт отрути, яка булькає в середині нашого суспільства. Тому просто замінити владу не змінюючи систему нічого не дасть</em></p>\n<p><strong>Від того якими є духовні та моральні цінності у суспільстві, залежить доля бізнесу як системи. Про це на лекції-зустрічі для львівських підприємців розповів, дисидент, правозахисник, віце-ректор Українського католицького університету у Львові Мирослав Маринович.</strong>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2506:myroslav-marynovych-pryklad-isusa-ce-nabezumnishy-biznes-proekt&catid=28:tema-nomera-cat&Itemid=2',4758,NULL,1,26),
(4738,'P','2014-11-04',1,NULL,'Rss','Таємниця музики','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6242,200,ngie.jpg\" />Музика має унікальну силу, бо вона здатна надихнути людське серце на шляхетні вчинки. Це загальновизнаний факт. Музиці часто надається особливе місце серед інших видів мистецтва. Вважається, що будь-яке мистецтво прагне стати музикою.</strong></p>\n<p>Вона супроводжує нас усюди, але що дивно, музиці на практиці приділяється менше уваги й поваги, ніж будь-якому іншому мистецтву. Більшість людей звикли витрачати свій час на перегляд фільмів, рідко хто просто слухає музику, зазвичай вона служить приємним або стимулюючий фоном. Насправді музика сприймається саме так через свою швидкоплинну й&nbsp;«нематеріальну» природу, звертаючись лише до одного з чуттів.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2507:tajemnycja-muzyky&catid=1:mystecka-storinka-cat&Itemid=50',4760,NULL,1,26),
(4739,'P','2014-11-04',1,NULL,'Note','ДО ДОБРОЧИНЦІВ ДЛЯ КУХНІ ПОТРЕБУЮЧИМ ПАРАФІЯНАМ І БІЖЕНЦЯМ З ДОНБАСУ НА АСКОЛЬДОВІЙ МОГИЛІ','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>Кухня парафіяльної служби милосердя Карітас-Печерськ працює</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>Вівторок,четверг,субота,неділя -12=00</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ваші пожертви будуть використані для приготування 20 повноцінних обідів потребуючим.Звіт про використання коштів буде подаватись в церконій єлектонній газеті</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.\"</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>Кухня парафіяльної служби милосердя Карітас-Печерськ працює</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>Вівторок,четверг,субота,неділя -12=00</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ваші пожертви будуть використані для приготування 20 повноцінних обідів потребуючим.Звіт про використання коштів буде подаватись в церконій єлектонній газеті </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><a href=\"http://www.askoldova-mogyla.org/\">www.askoldova-mogyla.org</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><a href=\"http://www.oranta.org/\">www.oranta.org</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Милостиню роби згідно з тим,що маєш;Коли у тебе достатків повно,Роби з них,а коли мало маєш,то не бійся з того дати.&nbsp;Бо так готуєш собі добрий скарбНа день потреби.\"</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Нехай милостивий Господь сторицею винагородить усіх доброчинців та меценатів!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Митрофорний протопресвітер, парох Ігор Онишкевич</span></p>',NULL,NULL,4761,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4740,'P','2014-11-05',1,NULL,'Rss','Звернення Комісії УГКЦ у справах родини з приводу дня молитви за сиріт','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6244,200,top-photo.jpg\" />Вже традиційно друга неділя листопада є присвячена в християнських громадах України молитві за дітей-сиріт.<strong> </strong>Говорячи про сиріт, маємо на увазі дітей, в яких немає батьків, або ж чиї батьки не можуть бути з своїми дітьми через певні соціальні обставини: міграція, безробіття, злочинність, алкоголізм. Також багато дітей в Україні виховується в неповних сім’ях (переважно матерями) через розлучення. Тож цього дня ми, християни, єднаємося спільно в молитві до Господа за кращу долю для дітей, позбавлених батьківського піклування.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2508:zvernennja-komisiji-ugkc-u-spravah-rodyny-z-pryvodu-dnja-molytvy-za-syrit&catid=27:gk-v-ukrajini-cat&Itemid=57',4762,NULL,1,26),
(4741,'P','2014-11-06',1,NULL,'Rss','Бог живе у Фейсбуці?','<p><img src=\"http://askoldova-mohyla.org/uk/image,6245,200,faceboo.jpg\" style=\"margin: 10px; float: left;\" />Сучасну людину можна з усією впевненістю назвати представником «генерації ґаджетів». Маючи у сумці смартфон, планшет, ноутбук чи інший цифровий носій, ми водночас є володарями інформаційної матриці, варто лише зробити кілька дотиків на дисплеї чи перебрати пальцями на клавіатурі, і ти вже в іншому, віртуальному світі Фейсбука, Твіттера, Інстаграму, Ютюба чи Вікі-навігаторів. Зважаючи на запити суспільства,</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2509:bog-zhyve-u-fesbuci&catid=37:spilnota-cat&Itemid=49',4763,NULL,1,26),
(4742,'P','2014-11-06',1,NULL,'Rss','Церква і суспільство в нових умовах','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6245,200,ArticleIma.jpg\" />Католицька Церква за Папи Франциска відчула не лише перспективи змін, а й повернення до старих істин та навіть вимірів раннього християнства. Про це йшлося під час конференції «Україна-Ватикан», яку вже сьомий раз проводить Українська асоціація релігієзнавців спільно з Інститутом філософії ім. Г. Сковороди.</p>\n<p>Цьогорічна наукова подія відбулася під знаком драматичних процесів у сучасній Україні: пошуку відповідей, які мала б та готова дати на них Церква, а також впливу самих подій на життя земного Тіла Христового.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2510:cerkva-i-suspilstvo-v-novyh-umovah',4764,NULL,1,26),
(4743,'N','2014-11-06',1,NULL,'Rss','Церква і суспільство в нових умовах','<p><img src=\"http://askoldova-mohyla.org/uk/image,6245,200,ArticleIma.jpg\" style=\"margin: 10px; float: right;\" />Католицька Церква за Папи Франциска відчула не лише перспективи змін, а й повернення до старих істин та навіть вимірів раннього християнства. Про це йшлося під час конференції «Україна-Ватикан», яку вже сьомий раз проводить Українська асоціація релігієзнавців спільно з Інститутом філософії ім. Г. Сковороди.</p>\n<p>Цьогорічна наукова подія відбулася під знаком драматичних процесів у сучасній Україні: пошуку відповідей, які мала б та готова дати на них Церква, а також впливу самих подій на життя земного Тіла Христового.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2510:cerkva-i-suspilstvo-v-novyh-umovah&catid=27:gk-v-ukrajini-cat&Itemid=57',4765,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4744,'P','2014-11-07',1,NULL,'Rss','Празник Святого Великомученика Димитрія','<p align=\"right\"><strong><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6247,200,066.jpg\" />\"Прийдіть усі вірні, зійшовшися, похвалімо Христового борця і мужнього світоча Димитрія\" (Стихира на малій вечірні).</em></strong></p>\n<p>Святий Климент Оле­ксан­дрій­ський велич і значення мученика описує такими словами: \"Мученик дає потрійне свідчення: про себе, що він зі справжньою вірністю вірі стоїть при Богові; проти свого противника, що він надаремно наступає на того, що непохитний у любові; для Господа, що в його науці зна­хо­ди­ться Божа пе­ре­кон­ли­ва сила, яку навіть боязнь смерти не може побідити\".</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2511:praznyk-svjatogo-velykomuchenyka-dymytrija&catid=35:istorychni-postati-cat&Itemid=48',4766,NULL,1,26),
(4745,'P','2014-11-07',1,NULL,'Note','ОГОЛОШЕННЯ','<p style=\"text-align: center;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6250,200,20141102082034.jpg\" alt=\"\" width=\"200\" height=\"112\" />ЛЮБІ ДІТИ!</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Запрошуємо Вас до участі у катехизації дітей та дорослих, яка відбуватиметься щонеділі</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">О 11.00 год. за адресою вул.Мазепи,6 (Фундація&nbsp;ім. Ольжича).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>За додатковою інформацією звертатись до Світлани Заяць: 0672207970</em></span></p>','<p style=\"text-align: center;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">ДОРОГІ БАТЬКИ! </span></p>\r\n<p style=\"text-align: center;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">ЛЮБІ ДІТИ!</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6250,200,20141102082034.jpg\" alt=\"\" width=\"200\" height=\"112\" />Запрошуємо Вас до участі у катехизації дітей та дорослих, яка відбуватиметься щонеділі</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">О 11.00 год. за адресою вул.Мазепи,6 (Фундація&nbsp;</span>ім. Ольжича).</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em><span style=\"text-decoration: underline;\">Катехитичні групи:</span></em></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">1.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Діти до 8 років (катехит Світлана і Орися).</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">2.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Приготування до Св. Тайн Покаяння та Урочистого Святого Причастя (катехит с.Філомена і Аня).</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">3.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Діти, які вже приступали до Св. Тайни Покаяння (катехит Оксана).</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">4.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Зустрічі з батьками (катехити Ірина і Руслана).</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">5.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Спільнота молоді (від 14 і старше) - о 11.00 год. в катехитичному центрі на Аскольдовій (катехит Ірина).</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>За додатковою інформацією звертатись до Світлани Заяць: 0672207970</em></span></p>',NULL,NULL,4767,5,1,40);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4746,'P','2014-11-08',1,NULL,'Rss','Християнський обряд поховання або забобони від бабок. Хто сильніший?','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6250,200,internet-trawrewer.jpg\" />Напевно, немає в нашому житті нічого, що було б більш міфологізованим, просякнутим забобонами, ніж смерть людини і обряд його поховання.</p>\n<p>Православне сприйняття смерті та поховання тіла покійного кардинально протилежне тому сприйняттю, яке, на жаль існує, у нашій пострадянській країні серед вчорашніх атеїстів, що&nbsp; відразу стали «православними», тобто тими, що звертаються до Церкви лише в екстремальних випадках народження (хрещення), хвороби і&nbsp;смерті людини.&nbsp;Ці «набіги» на Церкву настільки масові, що породили свою «похоронну» традицію, яка зараз широко поширена в народній свідомості.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2512:hrystyjansky-obrjad-pohovannja-abo-zabobony-vid-babok-hto-sylnishy&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4768,NULL,1,26),
(4747,'P','2014-11-11',1,NULL,'Note','ОГОЛОШЕННЯ','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><span style=\"line-height: 1.3em; text-indent: 0em;\"><img style=\"float: left;\" src=\"image,6252,200,imag.jpg\" alt=\"\" width=\"166\" height=\"200\" />Запрошуємо 13 листопада 2014 р.Божого 08:30 на Службу Божу подяки і прослави Чудотворного Образу Київсько-Зарваницької Богородиці на Аскольдовій Могилі просіявшої.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">07:45 Утреня</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08:30 Служба Божа</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Молебень до Богородиці з Освяченням води і Єлею. Молитви оздоровлення.</span><br /><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Святкова трапеза</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Цей день 13 листопада встановлюється як парафіяльне свято подяки Богородиці за її чисельні чуда на Аскольдовій Могилі.</span></p>','<p style=\"text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава Ісусу Христу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6252,200,imag.jpg\" alt=\"\" width=\"166\" height=\"200\" />Запрошуємо 13 листопада 2014 р.Божого 08:30 на Службу Божу подяки і прослави Чудотворного Образу Київсько-Зарваницької Богородиці на Аскольдовій Могилі просіявшої.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">07:45 Утреня</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08:30 Служба Божа</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Молебень до Богородиці з Освяченням води і Єлею. Молитви оздоровлення.</span><br /><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Святкова трапеза</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Цей день 13 листопада встановлюється як парафіяльне свято подяки Богородиці за її чисельні чуда на Аскольдовій Могилі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Запрошує на молитву о.Ігор з отцями Анатолієм і Олексієм та</span><br /><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">дияконом Олександром</span></p>',NULL,NULL,4769,5,1,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4748,'P','2014-11-13',1,NULL,'Rss','Голодомору передувало понад п\'ять тисяч антисталінських повстань селянства','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6252,200,14158073.jpg\" style=\"margin: 10px; float: left;\" />Геноциду українців передувало 5000 повстань, заявляють історики, спростовуючи міф про відсутність спротиву українських селян більшовицькому режиму.</strong></p>\n<p>Про це повідомили УНІАН у прес-центрі Центру досліджень визвольного руху.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2513:golodomoru-pereduvalo-ponad-pjat-tysjach-antystalinskyh-povstan-seljanstva&catid=28:tema-nomera-cat&Itemid=2',4770,NULL,1,26),
(4749,'P','2014-11-14',1,NULL,'Rss','Виховувати дітей у вірі потрібно не лише словами, але й прикладом — Папа','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6254,200,4658.jpg\" />Щоб передавати віру сучасним дітям і молоді, щоб допомогти їм пережити досвід «істини та любові», від дорослих вимагається доброго прикладу, а не лише багатослів’я. На це звернув увагу Святіший Отець.</strong></p>\n<p>На ці ранкові Меси з Папою по черзі приходять представники римських парафій. Цього разу половину лавок в каплиці резиденції «Дім святої Марти» зайняли діти та підлітки з однієї із них.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2515:vyhovuvaty-dite-u-viri-potribno-ne-lyshe-slovamy-ale--prykladom--papa&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4772,NULL,1,26),
(4750,'P','2014-11-14',1,NULL,'Rss','Розстріляний з\'їзд кобзарів','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6255,200,img_7217_2_1.jpg\" />&gt; На початку грудня 1934-35 року в Харківському оперному театрі відбувся З’їзд народних співців Радянської України. Основним завданням З’їзду було питання активного залучення народних співців до соціалістичного будівництва, відходу від виконавських традицій і визначення нових ідеологічних приорітетів. Ухваливши відповідні резолюції, незрячих співців під приводом поїздки на З’їзд народних співців народів Союзу Радянських Соціалістичних Республік, що мав відбутися у Москві, повантажили до ешелону і підвезли до околиць ст. Козача Лопань.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2516:rozstriljany-zjizd-kobzariv&catid=28:tema-nomera-cat&Itemid=2',4773,NULL,1,26),
(4751,'P','2014-11-17',1,NULL,'Rss','20 заповідей Матері Терези','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6256,200,82245.jpg\" style=\"margin: 10px; float: left;\" /></strong></p>\n<p><strong>Мати Терезу називають найвпливовішою жінкою світу, а її життя&nbsp;— найбільшою подією ХХ&nbsp;століття. Досягнення маленької тендітної черниці справді вражають, а її особистість виняткова за значенням для всього людства.</strong></p>\n<p>Добірки з її висловлювань давно ходять інтернетом. Цю названо її «заповідями».</p>\n<p><em>Що ви можете зробити для змінення миру в усьому світі? Йдіть додому і любіть свою сім’ю.</em></p>\n<p><em>Нам не потрібні зброя і бомби. Аби перемогти зло, нам потрібні любов і співчуття. Всі труди любові&nbsp;— це труди заради миру.</em></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2517:20-zapovide-materi-terezy&catid=35:istorychni-postati-cat&Itemid=48',4774,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4752,'P','2014-11-17',1,NULL,'Rss','Християнський погляд на війну, мир та самозахист','<p class=\"rtejustify\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6257,200,guard.jpg\" />Ідеалом християнського життя у спільноті та світі є мир – особлива душевна настанова, яка полягає в гармонійному й належному стосунку особи до Бога, до самої себе, до інших людей та до всього творіння. Мир є одним з ключових понять Христової Благовісті: «Мир вам!» (Лк 24,36); «Мир залишаю вам, мій мир даю вам» (Ів 14,27, див. також: Мт 10,13; Мр 5,34; 9,50; Лк 1,79; 2,14). Мир між людьми невіддільно пов’язаний з любов’ю до ближніх: «Бо заповіді: “Не чини перелюбу”, “Не вбивай”, “Не кради”, “Не пожадай”, і всякі інші заповіді містяться у цьому слові: “Люби твого ближнього як себе самого”» (Рм 13,9) (Див.: 1 Ів 2,9-11; 3,10-24; 4,7-21).<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2518:hrystyjansky-pogljad-na-vinu-myr-ta-samozahyst&catid=37:spilnota-cat&Itemid=49',4775,NULL,1,26),
(4753,'P','2014-11-18',1,NULL,'Rss','«Коли одна радісна людина ділиться з іншою, то веселіше стає вже двом» - БФ «Карітас Київ»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6258,200,10402447_784056904988107_5514823962148157099_n.jpg\" />«Працівники БФ «Карітас Київ» взяли участь в Третій всеукраїнській школі самодопомоги. Навчання відбувалось в місті Києві в приміщенні інституту післядипломної освіти й об’єднало спеціалістів із &nbsp;Харкова та області, Івано-Франківська, Скадовська та&nbsp; Києва. Метою школи було оволодіти теоретичними навиками організації та проведення груп самодопомоги, а також сформулювати цілі та плани щодо створення нових груп самодопомоги», - повідомляє о. Роман Сиротич, директор БФ «Карітас Київ».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2519:2014-11-18-21-16-33&catid=29:propovidi-cat&Itemid=34',4776,NULL,1,26),
(4754,'P','2014-11-18',1,NULL,'Rss','Нам потрібне навернення','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6258,200,lrCVXwePFJ4.jpg\" />Зараз в Україні звикли говорити, що домайданна Україна була просто перейменованою УРСР. І з цим не посперечаєшся. Щоправда, стара уересерівська система вмирає повільніше, аніж би нам усім цього хотілося, вона бореться, щоб і далі висмоктувати соки з усіх і кожного, хто живе під її пануванням. І багато в чому саме ця система несе відповідальність за усе, що відбувається в державі.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2520:nam-potribne-navernennja&catid=28:tema-nomera-cat&Itemid=2',4777,NULL,1,26),
(4755,'P','2014-11-19',1,NULL,'Rss','Блаженна сестра – служебниця Йосафата Гордашевська – обручниця Христова','<p><img src=\"http://askoldova-mohyla.org/uk/image,6260,200,250px-Josafata.jpg\" style=\"margin: 10px; float: left;\" />Життя сестри Йосафати Гордашевської не було сповнене особливими подіями, але вона вирізнялася надзвичайним ставленням до Бога, завжди залишаючи Йому широке місце у своєму серці й конкретному ревному служінні потребам свого народу.</p>\n<p>А святість сестри Йосафати полягала в тому, що вона виконувала свою щоденну працю з любов\'ю і посвятою та перебувала в постійній гармонії з Духом Ісуса, який пробуджував у ній жертовну любов. Її життя і служіння пов’язані з нашим краєм і з нашим Кристинополем – Червоноградом. Однак її діла належать усьому українському народові.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2521:blazhenna-sestra-sluzhebnycja-osafata-gordashevska-obruchnycja-hrystova&catid=35:istorychni-postati-cat&Itemid=48',4778,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4756,'P','2014-11-20',1,NULL,'Rss','Геополітичний аспект Голодомору (частина І)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6261,200,383524387.jpg\" style=\"margin: 10px; float: right;\" />Є такий трюїзм: зовнішня політика держави – це продовження її внутрішньої політики. Але з не меншою підставою можна сказати: внутрішня політика – це продовження зовнішньої. Інакше кажучи: зовнішні і внутрішні обставини еволюції суспільства взаємопов’язані і взаємозумовлені.</p>\n<p>Творці більшовицької революції в Росії мислили її як початок світової пролетарської революції. Такою вона уявлялася і частині європейського робітничого руху та ліворадикальної інтелігенції. Це були діяльні і досить потужні союзники більшовизму, без яких він не зміг би утвердити себе в революційній Росії.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2522:geopolitychny-aspekt-golodomoru-chastyna-i&catid=28:tema-nomera-cat&Itemid=2',4779,NULL,1,26),
(4757,'P','2014-11-20',1,NULL,'Rss','Собор святого архистратига Михаїла й інших безплотних сил','<p><strong><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6261,200,Myhail.jpg\" />«Всі небесні Сили, святі Ангели й Архангели, моліть Бога за нас грішних.» </em></strong><em>(Велике повечір’я)</em></p>\n<p>Св. Церква, маючи на увазі велике значення Ангелів для Божої слави і нашого спасіння, в мозаїці церковного року присвятила окреме місце і на почитання Ангелів. Тут на першому місці стоїть празник – Собор св. Архистратига Михаїла та інших безтілесних сил.</p>\n<p>Празник у честь св. Архистратига Михаїла і безтілесних сил був установлений у ІV ст. Цей празник зветься Собором тому, що цього дня Церква празнує Собор – збір всіх безтілесних сил враз зі св. Архистратигом Михаїлом, а також собор-збір людей на землі для прослави небесних сил.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2525:sobor-svjatogo-arhystratyga-myhajila--inshyh-bezplotnyh-syl&catid=35:istorychni-postati-cat&Itemid=48',4780,NULL,1,26),
(4758,'P','2014-11-21',1,NULL,'Rss','Геополітичний аспект Голодомору (частина ІІ)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6261,200,1244099821_9610-31.jpg\" style=\"margin: 10px; float: left;\" />Наприкінці 20-х років (минулого століття)польська, німецька, французька, англійська преса чимало уваги приділяють подіям в Україні, подаючи часом достовірну, а часом і фантастичну інформацію насамперед про спротив селянства пограбуванню, розкуркуленню й колективізації, а також про політичні настрої та внутрішньопартійну боротьбу. Цікаво, що більшовицька публіцистика того часу, на відміну від пізніших часів, досить широко, хоч і вибірково, звертається до цих ворожих публікацій, дозволяючи собі багато що цитувати.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2523:geopolitychny-aspekt-golodomoru-chastyna-ii&catid=28:tema-nomera-cat&Itemid=2',4783,NULL,1,26),
(4759,'P','2014-12-14',0,NULL,'Note','Проект до Св. Миколая - «Подарунок від тата»','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,6267,200,2.jpg\" alt=\"\" width=\"200\" height=\"159\" />Мета проекту:</strong>&nbsp;у час всесвітнього християнського свята Святого Миколая, заступника і опікуна діток, подарувати частинку Божої любові дитині, батько якої загинув під час проведення АТО.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Кожна сім&rsquo;я, родина, спільнота можуть взяти під духовну опіку осиротілу дитину і через подарунок від Св. Миколая виявити батьківсько-ангельську любов, ніжність, турботу.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6267,200,2.jpg\" alt=\"\" width=\"200\" height=\"159\" />Парафіяльна служба милосердя Карітас Печерськ церкви св. Миколая Чудотворця на Аскольдовій Могилі запрошує долучитися до спільного парафіяльного <strong>проекту &laquo;Подарунок від тата&raquo;</strong>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Мета проекту:</strong>&nbsp;Перший етап: у час всесвітнього християнського свята Святого Миколая, заступника і опікуна діток, подарувати частинку Божої любові дитині, батько якої загинув під час проведення АТО. Другий і наступні етапи: опікуватись цією дитиною у різний спосіб, наприклад: листуватись; надсилати подаруночки до свят; долучатися до організації відпочинку, запросити у гості, тощо.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Кожна сім&rsquo;я, родина, спільнота можуть взяти під духовну опіку осиротілу дитину і через подарунок від Св. Миколая виявити батьківсько-ангельську любов, ніжність, турботу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Логістика проекту:</strong> у координатора проекту отримати контакт (адресу) дитини (дітей), власноруч сформувати подарунок (зимові шаль, рукавички, шапка, м&rsquo;яка іграшка, книжка, шкільне приладдя, солодощі, фрукти, тощо, при бажанні можна вкладати листа підтримки, розуміння, подяки), відправити дитині адресату, фото звіт надіслати координатору, або ж викласти у дописі події.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">На даний час у наявності майже сто перевірених контактів осиротілих дітей.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Подаруймо цим дітям частинку батьківського тепла разом.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Координатор проекту:</strong>Мирослава, волонтер парафії контакт: +380963034626; e-mail:&nbsp;</span><span style=\"line-height: 1.3em;\">askold_mykolai@ukr.net</span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>',NULL,NULL,4784,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4760,'P','2014-11-22',1,NULL,'Rss','Геополітичний аспект Голодомору (частина ІІІ)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6261,200,den.jpg\" style=\"margin: 10px; float: right;\" />Не випадково, що головною небезпекою для соціалістичного будівництва оголошується, поряд із куркульством, націонал-ухильництво в самій партії. Це було важливою складовою боротьби з багатобарвною внутрішньопартійною опозицією.</p>\n<p>У згаданій книжці Ф. Тарана є окремий розділ «Опозиція в КП(б)У й підготовка до інтервенції». Починаючи з кінця 20-х років, «націонал-ухильників» дедалі брутальніше шельмують як ідеологів куркульства, пособників імперіалізму, резерв інтервентів, українських фашистів і т. д., – весь час розширюючи коло публічно ошельмованих і поступово доходячи до головних – О. Шумського й М. Скрипника.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2524:geopolitychny-aspekt-golodomoru-chastyna-iii&catid=28:tema-nomera-cat&Itemid=2',4785,NULL,1,26),
(4761,'P','2014-11-24',1,NULL,'Rss','Корені трагедії на Донбасі проростають у 1933 рік','<p><img src=\"http://askoldova-mohyla.org/uk/image,6267,200,im578x3.jpg\" style=\"margin: 10px; float: left;\" />Як дослідниця Голодомору, я певна, що сталінський геноцид українців 1933 року добряче підготував ґрунт для путінської гібридної війни на Донбасі. Тобто один кривавий диктатор передав у спадок іншому імперську справу нищення України.</p>\n<p>Чому голодна смерть є найстрашнішою?</p>\n<p>Хтось скаже: тому, що вона є найдовшою в часі, і буде правий лише частково. Так, якщо зовсім нема ніякої їжі, але є вода, людина буде згасати десь упродовж місяця, переживаючи при цьому в останній тиждень свого життя страшенні фізичні страждання.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2527:koreni-tragediji-na-donbasi-prorostajut-u-1933-rik&catid=37:spilnota-cat&Itemid=49',4786,NULL,1,26),
(4762,'P','2014-11-25',1,NULL,'Rss','Йосафат Кунцевич: мученик за єдність чи папський фанатик?','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6267,200,sv.yosafat-kuncevich.jpg\" />Останнім часом у зв‘язку із розвитком екуменічного діалогу дуже часто можна натрапити на спроби переосмислити ті чи інші події минулого через призму сьогодення. Зрозуміти, яке місце повинні займати різні постаті нашої церковної історії в майбутньому нашої Церкви.</strong></p>\n<p>Одним із таких церковних діячів нашої минувшини є св. Йосафат Кунцевич. Дехто вважає, що його подвиг зовсім не вписується в сьогоднішній екуменічний вік. Інші ж вважають його зразком для теперішніх спроб віднайти єдність між православними і католиками.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2528:osafat-kuncevych-muchenyk-za-jednist-chy-papsky-fanatyk&catid=35:istorychni-postati-cat&Itemid=48',4787,NULL,1,26),
(4763,'P','2014-11-25',1,NULL,'Rss','Катехитично-недільна школа','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6267,200,1039626.jpg\" />Протягом 20-ти років функціонує Катехитично-недільна школа при храмі св. Миколая Чудотвоця на Аскольдовій Могилі. Вже з самого початку, незважаючи на труднощі, пов’язані з приміщенням, діти різної вікової категорії приходили для того, (як вони це пояснюють) щоб почути про Бога. Праця, яка ведеться для дітей та молоді в школі, є дуже важливою у вихованні не тільки духовно-моральному, патріотичному аспектах, але й цілісно-особистісному.</p>\n<p>Сьогодні катехитична праця здійснюється не тільки Сестрами Служебницями, як раніше, але й з участю 16-ти катехитів, вишколених за сприянням пароха о. Ігоря Онишкевича: Світлана Заяць, Ореста Цебрівська, Аня Онишкевич, Оксана Ясніцька, Оксана Сенчук, Ірина Макар, Руслана Дмитровська, Катерина Чаплик, Надія Кучерява, Ірина Василик, Любов Михайлюк, Марія та Василилина Томишинець, Анастасія Заверуха і Сестри Служебниці с. Філомена Чворсюк і с. Христосфора Буштин.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2529:katehytychno-nedilna-shkola',4788,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4764,'P','2014-11-27',1,NULL,'Rss','Різдвяний піст','<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6271,200,1417100438_14.jpg\" />«Бережись вимірювати піст простим утриманням від їжі. Ті, які утримуються від їжі, а ведуть себе погано, уподібнюються дияволові, який хоча нічого не їсть, проте&nbsp;ж не перестає&nbsp;грішити»</em></p>\n<p><em>Святий Василій&nbsp;Великий</em></p>\n<p><strong>28 листопада</strong> починається Різдвяний піст, який ще називають Пилипівським, бо він починається на наступний день після святкування пам’яті святого апостола&nbsp;Пилипа.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2531:rizdvjany-pist&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4789,NULL,1,26),
(4765,'P','2014-11-28',1,NULL,'Rss','Не віддаляймося від Бога','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6272,200,Ercole_de_Roberti_Destructio.jpg\" />Навіть серед багатьох трудностей християнин не повинен піддаватись пригніченню, але жити надією. Папа Франциск наголосив на тому, що зіпсованість та байдужість віддаляють людину від зустрічі з Господом Ісусом.</p>\n<p>Літургічні читання, взяті із новозавітньої книги Одкровення та Євангелії від святого Луки, у яких розповідається про занепад міста Вавилону та пророцтво падіння Єрусалиму, стали натхненням для Вселенського Архиєрея, щоб застановитись нам темою занепаду міст та людей, які віддалились від Бога і не прийняли Його.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2532:ne-viddaljamosja-vid-boga&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4790,NULL,1,26),
(4766,'P','2014-11-30',1,NULL,'Rss','Спільна декларація, підписана Папою Франциском та Вселенським Патріархом Вартоломеєм І у Стамбулі 30 листопада 2014 року','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6273,200,AP2662681_Articolo.jpg\" />Ми, Папа Франциск та Вселенський Патріарх Вартоломей І, складаємо свою велику вдячність Богові за дар цієї чергової зустрічі, яка дає нам змогу, в присутності членів Священного Синоду, духовенства та вірних Вселенського Патріархату, разом відсвяткувати свято святого Андрія, первозванного і брата святого апостола Петра. Наше спогадування апостолів, що звіщали світові добру новину Євангелія через свою проповідь та свідчення мучеництва, скріпляє у нас прагнення й надалі разом прямувати вперед, до мети подолання з любов’ю та довір’ям перешкод, які нас розділяють.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2533:spilna-deklaracija-pidpysana-papoju-francyskom-ta-vselenskym-patriarhom-vartolomejem-i-u-stambuli-30-lystopada-2014-roku&catid=32:gk-svit-cat&Itemid=58',4791,NULL,1,26),
(4767,'P','2014-11-30',1,NULL,'Rss','Гоніння на інакомислячих','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6273,200,f_klZ7aOBPc.jpg\" />Військові дії на території Донбасу мають не тільки політичне та економічне, а й релігійне підґрунтя. Більшість представників терористичних угрупувань зараховують себе до Української православної церкви (Московського патріархату) – звучали і продовжують звучати заклики зробити «московське» православ’я офіційною релігійною системою і закріпити її статус конституційно. Власне, це вже відбулося – 9-а стаття «конституції» «ДНР» переносить нас у часи Російської імперії та обер-прокурора, який фактично керував Синодом. Прикладом може бути «інавгурація» голови угруповання «ЛНР» Ігоря Плотницького, яку відвідав колишній митрополит Луганської і Алчевської єпархій Іоаннікій – він дав святительське благословення новому очільнику псевдореспубліки, компанію йому склав настоятель Ніколо-Преображенського собору протоієрей Богдан Федьків.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2534:goninnja-na-inakomysljachyh&catid=27:gk-v-ukrajini-cat&Itemid=57',4792,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4768,'P','2014-11-25',1,NULL,'Rss','Катехитично-недільна школа','<p><img src=\"http://askoldova-mohyla.org/uk/image,6267,200,1039626.jpg\" style=\"margin: 10px; float: left;\" />Протягом 20-ти років функціонує Катехитично-недільна школа при храмі св. Миколая Чудотвоця на Аскольдовій Могилі. Вже з самого початку, незважаючи на труднощі, пов’язані з приміщенням, діти різної вікової категорії приходили для того, (як вони це пояснюють) щоб почути про Бога. Праця, яка ведеться для дітей та молоді в школі, є дуже важливою у вихованні не тільки духовно-моральному, патріотичному аспектах, але й цілісно-особистісному.</p>\n<p>Сьогодні катехитична праця здійснюється не тільки Сестрами Служебницями, як раніше, але й з участю 16-ти катехитів, вишколених за сприянням пароха о. Ігоря Онишкевича: Світлана Заяць, Ореста Цебрівська, Аня Онишкевич, Оксана Ясніцька, Оксана Сенчук, Ірина Макар, Руслана Дмитровська, Катерина Чаплик, Надія Кучерява, Ірина Василик, Любов Михайлюк, Марія та Василилина Томишинець, Анастасія Заверуха і Сестри Служебниці с. Філомена Чворсюк і с. Христосфора Буштин.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2529:katehytychno-nedilna-shkola&catid=25:parafija-cat&Itemid=27',4793,NULL,1,26),
(4769,'P','2014-12-01',1,NULL,'Rss','«Рух Богом даної гідності має поширюватися»','<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6275,200,gudziak.jpg\" />«Там» – він єпископ для українських греко-католиків Франції, Швейцарії і країн Бенелюксу; у Парижі – в Соборі Паризької Богоматері – очолює поминальну Службу за жертв Голодомору і за Небесну Сотню, згадуючи її поіменно.</em></p>\n<p><em>«Тут» – у Львові – він залишається Президентом Українського Католицького Університету, а разом зі своїми студентами брав участь у протестах Євромайдану, будучи у Києві в ключові моменти революції гідності.</em>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2535:2014-12-01-12-04-50&catid=28:tema-nomera-cat&Itemid=2',4794,NULL,1,26),
(4770,'P','2014-12-01',1,NULL,'Rss','День боротьби зі СНІДом','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6277,200,IMAG2276.jpg\" />Щорічно 1 грудня відзначається Всесвітній день боротьби зі СНІДом. Цього дня людство згадує про те, яку серйозну загрозу для життя людей несе ця глобальна проблема.</p>\n<p>Вперше цей день відзначався 1 грудня 1988 року. Таке рішення затвердили на міжнародній зустрічі міністрів охорони здоров\'я, які обговорювали необхідність обміну інформацією щодо ВІЛ-інфекції та СНІДу.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2536:den-borotby-zi-snidom&catid=25:parafija-cat&Itemid=27',4795,NULL,1,26),
(4771,'P','2014-12-03',1,NULL,'Rss','Дай Боже, щоб ми мали більше таких людей, як Євген Сверстюк – Блаженніший Любомир Гузар','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6278,200,360222610.jpg\" />Євген Сверстюк – один з когорти тих небагатьох українських інтелектуалів та громадських діячів, кого українці вважають і вважатимуть, навіть і після смерті, моральними авторитетами суспільства. Слово про нього – від ще одного з таких авторитетів, яких ми звемо совістю нації, борцями за Україну і її незалежність, соратника Євгена Олександровича по Ініціативній групі “Першого грудня” кардинала Любомира Гузара.</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2537:da-bozhe-shchob-my-maly-bilshe-takyh-ljude-jak-jevgen-sverstjuk--blazhennishy-ljubomyr-guzar&catid=28:tema-nomera-cat&Itemid=2',4796,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4772,'P','2014-12-03',1,NULL,'Rss','Введення в храм Пресвятої Богородиці','<p style=\"text-align: right;\"><em><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6279,200,Presentation_titian.JPG\" /></strong>ТРОПАР СВЯТА</em></p>\n<p><em>Сьогодні благовоління Божого провіщення і спасіння людей проповідання, у храмі Божому ясно Діва являється і Христа всім провіщає; їй же і ми голосно заспіваймо: радуйся, промислу Створителя здійснення.</em></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2538:vvedennja-v-hram-presvjatoji-bogorodyci&catid=27:gk-v-ukrajini-cat&Itemid=57',4797,NULL,1,26),
(4773,'P','2014-12-04',1,NULL,'Rss','Бажати - це брати відповідальність на себе','<p><em><img src=\"http://askoldova-mohyla.org/uk/image,6280,200,TheUK_04.jpg.pagespeed.ce.zuIoRWRJo5.jpg\" style=\"margin: 10px; float: right;\" />Його ім’я у своїх «перших десятках» мріють бачити багато лідерів демократичних партій України. І не дивно. Мирослав Маринович — постать, до думки якої однаково прислухаються і юні максималісти, і літні мудреці. Він вміє говорити і з академіками, і з робітниками. А ще він знає, що їх потрібно чути.</em></p>\n<p><em>…Є спікери й інтелектуали, з яким журналістам спілкуватися важко. Вони відповідають штампами, всоте переказують одні й ті самі історії, вкотре озвучують давно озвучені меседжі. Записувати інтерв’ю з ними складно, бо вивести співрозмовника на «ексклюзив» вартує надзусиль. Вони повторюються.</em></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2539:bazhaty-ce-braty-vidpovidalnist-na-sebe&catid=37:spilnota-cat&Itemid=49',4798,NULL,1,26),
(4774,'P','2014-12-05',1,NULL,'Rss','Фундаментальна зміна цінностей','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6281,200,384045840.jpg\" />Країна опинилася на роздоріжжі між двома ключовими цінностями у світі: свободи вибору та безпеки</p>\n<p>Питання цінностей є найглибшим питанням, що існує в будь-якому суспільстві. Усі біди, без винятку, усі біди Україні тільки в цьому. Всі інші речі похідні.</p>\n<p>Реформи, про які ми зараз говоримо, будуть сізіфовою працею, якщо в країні не зміняться цінності. Це не означає, що не треба проводити реформи, але поки цінності не стали іншими, вони грають лише підтримуючу роль, роблять так, щоб держава не розвалилась. Тому потрібно створити певний інкубатор для зміни цих цінностей. І першочергова задача теперішньої влади – не зіпсувати момент дозрівання нового суспільства.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2540:fundamentalna-zmina-cinnoste&catid=37:spilnota-cat&Itemid=49',4799,NULL,1,26),
(4775,'P','2014-12-05',1,NULL,'Rss','Резолюція Всеукраїнської науково-практичної конференції «Християнська проповідь у місцях несвободи. Історія і сучасність України»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6282,200,Log.jpg\" />Всеукраїнська науково-практична конференція «Християнська проповідь у місцях несвободи. Історія і сучасність України» (далі – Всеукраїнська конференція), плекаючи традицію пенітенціарного служіння в Україні, послідовно відроджуючи душпастирство в місцях несвободи, прагнучи до гуманізації кримінального правосуддя і системи виконання покарань в Україні на підставі пенітенціарної ідеї, витоки якої йдуть з християнської проповіді, висловлює зацікавленість у подальшій координації кроків для впровадження капеланства. Ми стверджуємо, що довголітня співпраця Української Християнської Міжконфесійної Місії «Духовна та благодійна опіка в місцях позбавлення волі» і Державної пенітенціарної служби України (далі - ДПтС&nbsp; України) є належним підґрунтям для законодавчого урегулювання питань душпастирської опіки в’язнів і персоналу ДПтС. Ми вбачаємо, що необхідно продовжити співпрацю Церков Місії і ДПтС України для розвитку етики служіння Церков у пенітенціарній сфері для добра громадян нашої країни та пропонуємо наступне:</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2541:2014-12-05-19-14-35&catid=27:gk-v-ukrajini-cat&Itemid=57',4800,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4776,'P','2014-12-06',1,NULL,'Rss','Свята Великомученице Катерино, моли Бога за нас!','<p><img src=\"http://askoldova-mohyla.org/uk/image,6285,200,sogod.jpg\" style=\"margin: 10px; float: left;\" />Катерина не тільки добре знала твори мудреців старовини, але вона вивчала також твори знаменитих лікарів, таких, наприклад, як Аськлепій, Гіппократ і Галін; крім того вона навчилася ораторському і діалектичному мистецтву і знала багато мов і говір, так що всі дивувалися її вченості і знанням.</p>\n<p>Багато багатих і знатних людей сваталися до неї і з цією метою приходили до її&nbsp;матери, таємної християнки, що приховувала свою віру внаслідок жорстокого гоніння, спорудженого у той час на віруючих Максиміном.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2542:svjata-velykomuchenyce-kateryno-moly-boga-za-nas&catid=35:istorychni-postati-cat&Itemid=48',4803,NULL,1,26),
(4777,'P','2014-12-08',1,NULL,'Rss','Правда допоможе нам виграти на Сході','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6286,200,FDE9D5.jpg\" style=\"margin: 10px; float: right;\" />Правозахисник, публіцист, релігієзнавець, учасник ініціативи Першого грудня Мирослав Маринович у програмі Еспресо.TV “Вечір із Миколою Княжицьким” розповів про майбутнє возз’єднання України, що допоможе державі перемогти у кампанії на Сході та небезпечність певних політиків у парламенті</strong></p>\n<p>Про божественну плоть слова Євгена Сверстюка</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2543:pravda-dopomozhe-nam-vygraty-na-shodi&catid=37:spilnota-cat&Itemid=49',4804,NULL,1,26),
(4778,'P','2014-12-09',1,NULL,'Rss','Майдан: усна історія','<p><em><img src=\"http://askoldova-mohyla.org/uk/image,6287,200,guzar_3.jpg\" style=\"margin: 10px; float: left;\" />Кардинал Любомир Гузар у проекті Українського інституту національної пам\'яті та Фонду збереження історії Майдану \"<a href=\"http://www.kmu.gov.ua/control/uk/publish/article?art_id=247773307&amp;cat_id=244277212\">Майдан: усна історія</a>\" розповів, що дня нього означають події зими 2013-2014.</em></p>\n<p><strong><em>– Чи можна сказати, що члени Ініціативної групи \"Першого грудня\", до якої Ви входите, якимось чином готували Майдан?</em></strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2544:madan-usna-istorija&catid=28:tema-nomera-cat&Itemid=2',4805,NULL,1,26),
(4779,'P','2014-12-10',1,NULL,'Rss','Папи миру','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6288,200,1572815_articolo.jpg\" />Увечері 27 жовтня 1986 року над Ассізі, містом святого Франциска, покровителя братерства між народами, повівав прохолодний вітер. У той час, як святий Іван Павло ІІ взяв слово, щоб виголосити заключну промову однієї з подій його понтифікату, яка назавжди закарбувалася у пам’яті багатьох людей – Всесвітнього дня молитви за мир, релігійні провідники, що прибули з кожного кінця світу на цю безпрецедентну зустріч, сидячи за його спиною, намагалися, як могли, рятуватися від холоду.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2545:papy-myru&catid=35:istorychni-postati-cat&Itemid=48',4806,NULL,1,26),
(4780,'P','2014-12-11',1,NULL,'Rss','«Синод – це не парламент, а зіставлення думок пастирів»','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6288,200,afp3757031_articolo.jpg\" /></strong>Синод – це не парламент, де змагаються різні фракції, а зіставлення думок після довгої підготовки.</p>\n<p>Завершивши цикл повчань про Церкву, Святіший Отець розпочав новий, присвячений сім’ї, і цього разу розповів про перебіг роботи ІІІ Надзвичайної асамблеї Синоду Єпископів, що відбувалася у Ватикані в жовтні на тему «Виклики душпастирства сім’ї в контексті євангелізації». Адже, за його словами, важливо пригадати те, як вона відбувалася та про її результати.</p>\n<p>Насамперед, Глава Католицької Церкви відзначив важливість роботи засобів суспільної комунікації, оскільки в суспільстві панували великі очікування та увага. «Але часто, – додав він, – спосіб бачення ЗМІ мав певною мірою характер спортивних чи політичних репортажів: часто говорилося про дві команди, за і проти, консерваторів та прогресистів. Сьогодні хочу розповісти вам, чим саме був Синод».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2546:2014-12-10-21-06-09&catid=32:gk-svit-cat&Itemid=58',4807,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4781,'P','2014-12-11',1,NULL,'Rss','Ви мусите оборонятися, але робіть це без ненависті!','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6290,200,Screen.jpg\" style=\"margin: 10px; float: right;\" />Промова папського легата, архиєпископа Відня кардинала Крістофа Шенборна, під час</strong><strong> </strong><strong><a href=\"http://www.credo-ua.org/2014/12/127003\">нічних чувань</a></strong><strong> </strong><strong>у соборі Воскресіння Христового в Києві (повний текст).</strong></p>\n<p>Слава Ісусу Христу! (<em>сказано українською — ред.</em>)</p>\n<p>Ваше Блаженство, дорогі брати в єпископстві, дорогі брати в свяществі, дорогі брати і сестри в Христі!</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2547:vy-musyte-oboronjatysja-ale-robit-ce-bez-nenavysti&catid=28:tema-nomera-cat&Itemid=2',4808,NULL,1,26),
(4782,'P','2014-12-12',1,NULL,'Rss','Послання Папи Франциска з нагоди 48-го Всесвітнього дня миру (підсумок)','<p><img src=\"http://askoldova-mohyla.org/uk/image,6291,200,papa.jpg\" style=\"margin: 10px; float: left;\" />«Вже не раби, але – брати», – ці слова з Послання святого Павла до Филимона стали темою Послання Папи Франциска з нагоди 48-го Всесвітнього дня миру, який Католицька Церква відзначає у перший день нового року. Представлення цього послання відбулося у середу, 10 грудня 2014 р., у Ватиканському прес-центрі.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2548:poslannja-papy-francyska-z-nagody-48-go-vsesvitnogo-dnja-myru-pidsumok&catid=32:gk-svit-cat&Itemid=58',4809,NULL,1,26),
(4783,'P','2014-12-12',1,NULL,'Rss','Апостол Андрій Первозванний - Феномен в життя','<p align=\"right\"><img src=\"http://askoldova-mohyla.org/uk/image,6292,200,Andrew-450x345.jpg\" style=\"margin: 10px; float: right;\" />«Величаємо Тебе, святий апостоле Андрію Первозванний, і шануємо святу пам\'ять Твою…» (величання свята)</p>\n<p align=\"right\">Благоволінням Божим кожна країна і кожен народ мають у своїй історії просвітителя. Великою честю для багатьох народів стало перебування на їхній землі учнів Христа Спасителя — святих апостолів. Славить похвалами Рим апостолів Петра і Павла, Індія — Фому, Єгипет — Марка, через яких народи, що перебували у темряві невідання, просвітилися світлом істинного богопізнання.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2549:apostol-andri-pervozvanny-fenomen-v-zhyttja&catid=35:istorychni-postati-cat&Itemid=48',4810,NULL,1,26),
(4784,'P','2014-12-13',1,NULL,'Rss','Майстерня Cвятого Миколая','<p><img src=\"http://askoldova-mohyla.org/uk/image,6293,200,IMG_0928[1].JPG\" style=\"margin: 10px; float: left;\" />Вже так скоро найулюбленіше свято дітей Святого Миколая. Та не тільки діти, але і дорослі особливо його шанують, захоплюються, і передають один одному цей радісний дух торжества.</p>\n<p>Церква святого Миколая Чудотворця на Аскольдовій Могилі завжди в особливий спосіб святкує цей величавий празник. Цього року парафіяльна служба милосердя «Карітас-Печерськ» приділила особливу увагу тим дітям, чиї батьки загинули в ході антитерористичної операції на сході нашої держави.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2550:masternja-svjatogo-mykolaja&catid=25:parafija-cat&Itemid=27',4811,NULL,1,26),
(4785,'P','2014-12-14',1,NULL,'Rss','Про гідність, роль жінки та запитання до Бога','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6294,200,10857934.jpg\" />Блаженніший Любомир: не маючи поняття Бога, тепер трудно, щоби було добре суспільство</strong></p>\n<p><strong>Громадський простір мав нагоду поспілкуватися із духовним лідером Блаженнішим Любомиром Гузаром, кардиналом, главою Української греко-католицької церкви у 2001-2011 роках. Ми поговорили про причини насильства у світі та про те, яку роль відіграє духовність у побудові громадянського суспільства.</strong>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2551:pro-gidnist-rol-zhinky-ta-zapytannja-do-boga&catid=28:tema-nomera-cat&Itemid=2',4812,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4786,'P','2014-12-14',1,NULL,'Rss','Ким священик є насправді?','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6295,200,IMG_1960.jpg\" />У радянські часи вважали, що немає такої професії, як «священик». І,&nbsp;з одного боку,&nbsp;– цілком слушно, бо це, мабуть, таки не професія. З&nbsp;другого ж боку, священика взагалі вважали неробою, бо він нічого не продукував, фізично не працював, одним словом&nbsp;– соціалістичне суспільство не чекало від нього жодної користі. А&nbsp;за кого вважають священика зараз?</strong><o:p></o:p></p>\n<p>Ким священик є насправді і що він робить? На ці питання спеціально для «КВ» відповів архієпископ Томас Едвард Ґалліксон, Апостольський Нунцій в Україні.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2552:kym-svjashchenyk-je-naspravdi&catid=27:gk-v-ukrajini-cat&Itemid=57',4813,NULL,1,26),
(4787,'P','2014-12-20',0,NULL,'Note','Запрошуємо на фестиваль-конкурс духовної пісні «Аскольдів глас»','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,6297,200,45664.jpg\" alt=\"\" width=\"133\" height=\"200\" />XІІІ фестиваль-конкурс духовної пісні &laquo;Аскольдів глас&raquo;&nbsp;</strong>- це фестиваль сольних виконавців віком 6&ndash;12 років.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>XІІІ фестиваль-конкурс духовної пісні &laquo;Аскольдів глас&raquo;&nbsp;</strong>відбудеться 2<strong>0 грудня 2014 р. о 17.00</strong> у приміщенні Національного педагогічного університету ім. М. Драгоманова, вул. Пирогова, 9 (м. Київ, станція м. Університет).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">У програмі свята: виступи почесних гостей &ndash; зірок української естради, вистава-феєрія &laquo;Святий Миколай йде&raquo; та проведення аукціону з продажу мистецьких виробів. Зібрані кошти буде спрямовано для потреб української армії.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,6297,200,45664.jpg\" alt=\"\" width=\"133\" height=\"200\" />XІІІ фестиваль-конкурс духовної пісні &laquo;Аскольдів глас&raquo;&nbsp;</strong>- це фестиваль сольних виконавців віком 6&ndash;12 років.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>XІІІ фестиваль-конкурс духовної пісні &laquo;Аскольдів глас&raquo;&nbsp;</strong>відбудеться 20 грудня 2014 р. о 17.00 у приміщенні Національного педагогічного університету ім. М. Драгоманова, вул. Пирогова, 9 (м. Київ, станція м. Університет).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Концертний виступ виконавця включатиме два твори. Перший і обов&rsquo;язковий твір присвячений постаті київського князя Аскольда. Інший &ndash; довільний із жанру духовної, патріотичної або народно-обрядової музики. Також можливе виконання сучасної авторської музики християнського змісту під фонограму мінус. Максимальна тривалість виступу &ndash; 10 хв.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">У програмі свята: виступи почесних гостей &ndash; зірок української естради, вистава-феєрія &laquo;Святий Миколай йде&raquo; та проведення аукціону з продажу мистецьких виробів. Зібрані кошти буде спрямовано для потреб української армії.</span></p>',NULL,NULL,4814,5,1,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4788,'P','2014-12-15',1,NULL,'Rss','«Ми можемо перемогти цю війну без жодного пострілу, лише за умови, коли вся Україна стане на коліна перед Богом!»','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6297,200,10417588.jpg\" />Отець Тихон вже кілька місяців живе у Львові, хоча є донеччанином у шостому поколінні. Після полону священик переїхав на Захід, оскільки його ім\'я внесено днрівцями у «чорний список» і будь-який перетин окупованої території загрожує розстрілом. Отець сумує за своєю малою Батьківщиною не тому, що там його дім, могили родичів, але там залишились парафіяни, які за його словами стали для нього сім\'єю.</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2553:2014-12-15-18-06-00&catid=28:tema-nomera-cat&Itemid=2',4815,NULL,1,26),
(4789,'P','2014-12-15',1,NULL,'Rss','Блаженніший Любомир відвідав 260 переселенців','<p><img src=\"http://askoldova-mohyla.org/uk/image,6297,200,_DSC3653.JPG\" style=\"margin: 10px; float: left;\" />«12 грудня 2014 року відбувся візит Блаженнішого Любомира Гузара в Санаторій «Джерело», де проживає близько 260 вимушено переселених осіб зі Сходу України та Криму (з них 120 дітей). Зустріч була організована Київським Карітасом», - повідомляє о. Роман Сиротич, директор БФ «Карітас Київ».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2554:blazhennishy-ljubomyr-vidvidav-260-pereselenciv&catid=29:propovidi-cat&Itemid=34',4816,NULL,1,26),
(4790,'P','2014-12-16',1,NULL,'Rss','«Сьогодні бути українцем дуже цікаво»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6299,200,7613ada-840.jpg\" />Виходець з американського містечка Сіракузи, українець Борис Ґудзяк ділить свої службові обов’язки між Західною Європою та Україною. Президент Українського католицького університету, владика Борис також стоїть на чолі Української греко-католицької церкви у Франції, Швейцарії та країнах Бенілюксу. Platfor.ma зустрілась з єпископом&nbsp;у Нідерландах, аби поговорити про довіру, українську еміграцію, жертовність і про те, як Україна має допомогти Європі, а не навпаки.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2555:2014-12-16-19-02-33&catid=28:tema-nomera-cat&Itemid=2',4817,NULL,1,26),
(4791,'P','2014-12-19',1,NULL,'Rss','Посягання на храм? чи на власну душу?','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6301,200,20141218_084101.jpg\" />У ніч, з 17 на 18 грудня 2014&nbsp;року, десь приблизно о 01:30&nbsp;год., охорона церкви Аскольдової могили побачила невідомого юнака, який зупинився біля дверей храму. Проте спершу він не викликав підозри, оскільки часто люди приїжджають молитися вночі, інколи цілими групами.</p>\n<p>Цей юнак нахилився, ніби став на коліна. Потім з’ясувалося, що він запалив горючу суміш «коктейль молотова» у двох пляшках. Ці пляшки були поєднані скотчем. Він різко піднявся і кинув їх у бік дверей церкви. Сам же втік до Дніпра.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2556:posjagannja-na-hram-chy-na-vlasnu-dushu&catid=25:parafija-cat&Itemid=27',4819,NULL,1,26),
(4792,'P','2014-12-19',1,NULL,'Rss','Пізнай Святого Миколая у собі','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6301,200,48050354.jpg\" />Про сьогоднішнє свято, здається, знаємо багато, але мабуть, більше не знаємо, як маємо відомостей про нього. Цей святий зробив стільки чудес, допоміг стільком особам, врятував життя так багатьом людям, привернув здоров’я, встановив справедливість, витягнув з нещастя, усунув різні небезпеки, що всього ми не можемо знати й неможливо цього описати. А крім того, його духовна опіка, його святість дарована йому Богом, його любов до ближнього та Господа Бога такі значні, що людський розум збагнути не може. Бо як можна визначити, виміряти, збагнути рівень любові до ближнього, котрою палав святий Миколай? Його знає увесь християнський світ! А те, що він приходив з поміччю людям ще за життя, творячи чуда – свідчить про великий ступінь святості цієї особи.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2557:pizna-svjatogo-mykolaja-u-sobi&catid=35:istorychni-postati-cat&Itemid=48',4820,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4793,'P','2014-12-19',1,NULL,'Rss','«Лише любов до Бога і Батьківщини може зробити людину героєм»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6303,200,1084976.jpg\" />Ми справді зможемо перемогти не тільки зовнішнього агресора, а й економічні негаразди, суспільні кризові ситуації, реформувати наше суспільство лише тоді, коли навчимося робити діла милосердя, як це робив святий Миколай. Про це сьогодні, у день Святого Миколая сказав Отець і Глава Української Греко-Католицької Церкви Блаженніший Святослав під час проповіді до вірних у храмі Святого Миколая на Аскольдовій могилі.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2558:2014-12-19-21-02-37&catid=25:parafija-cat&Itemid=27',4821,NULL,1,26),
(4794,'P','2014-12-21',1,NULL,'Note','«Аскольдів глас» набув значення архиєпархіального фестивалю','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6305,200,001.jpg\" alt=\"\" width=\"145\" height=\"200\" /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отець Ігор та Громада храму св. Миколая Чудотворця на Аскольдовій могилі висловлює глибоку шану та сердечну подяку за Вашу працю і всі докладені зусилля &nbsp;для якнайкращої організації нашого пісенного свята!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дякуючи Вашим старанням ХІІІ Фестиваль-конкурс отримав декрет-благословення від владики Йосифа Міляна та набув статусу архиєпархіального.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Даруючи теплоту Ваших сердець ближнім у вигляді ретельно, творчо і оперативно виконаних завдань, Ви явили живий праобраз святителя Миколая в сучасності, якого так гаряче вшановує наша парафія. Нехай Бог сторицею віддячить Вам!&nbsp;</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6305,200,001.jpg\" alt=\"\" width=\"145\" height=\"200\" />Слава Ісусу Христу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дорогі члени оргкомітету!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отець Ігор та Громада храму св. Миколая Чудотворця на Аскольдовій могилі висловлює глибоку шану та сердечну подяку за Вашу працю і всі докладені зусилля &nbsp;для якнайкращої організації нашого пісенного свята!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дякуючи Вашим старанням ХІІІ Фестиваль-конкурс отримав декрет-благословення від владики Йосифа Міляна та набув статусу архиєпархіального.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Даруючи теплоту Ваших сердець ближнім у вигляді ретельно, творчо і оперативно виконаних завдань, Ви явили живий праобраз святителя Миколая в сучасності, якого так гаряче вшановує наша парафія. Нехай Бог сторицею віддячить Вам!&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Бажаємо у Новому 2015 році Вам і Вашим родинам міцного здоров\'я, добробуту, гаразду, здійснення мрій і планів та мирного неба!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Многая і благая літ!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Незабаром надішлемо посилання на фото і статті, в яких будуть висвітлені результати цьогорічного конкурсу.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Із щирою повагою та вдячністю!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Катерина Чаплик</span></p>',NULL,NULL,4822,5,1,52);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4795,'P','2014-12-21',1,NULL,'Rss','Празник Зачаття Богородиці Святою Анною','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6305,200,21075.jpg\" />Давній і глибокий культ Пресвятої Богородиці, що є знаменною віхою Східної Церкви, виявляється у великій кількості Богоро­дичних празників у нашому церковному календарі. Ті празники розказують нам найважливіші моменти з життя Божої Матері від її чудесного зачаття аж до її славного успення.</p>\n<p>Святе Євангеліє не багато говорить про життя Пречистої Діви Марії, тож нічого дивного, що більшість Богородичних празників не основується на подіях святого Євангелія, а радше на християн­ській традиції та на оповіданнях апокрифічних книг перших віків. Сюди належить також і празник Зачаття, який наша Церква свят­кує 9 грудня. Цей празник має окрему історію на Сході та на Заході. Тож погляньмо на його історію у Східній, Західній і нашій Церквах.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2559:praznyk-zachattja-bogorodyci-svjatoju-annoju&catid=28:tema-nomera-cat&Itemid=2',4823,NULL,1,26),
(4796,'P','2014-12-24',1,NULL,'Rss','Святвечір у сталінському висланні: спогади свідка','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6306,200,22983_original.jpg\" />Наближається Різдво. Перед господинями постають турботи по гідну підготовку святкового&nbsp; різдвяного столу, прикраси будинку. Іноді ці матеріальні занепокоєння затуляють головне - зміст свята, Різдва Спасителя. Тим часом в пам\'яті наших багатьох віруючих є особливо зворушливі моменти святкування Різдва, коли не було багато накритих столів, але було справжнє відчуття близького приходу Спасителя.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2560:svjatvechir-u-stalinskomu-vyslanni-spogady-svidka&catid=28:tema-nomera-cat&Itemid=2',4825,NULL,1,26),
(4797,'P','2014-12-25',1,NULL,'Rss','Ісус молився про єдність Церкви, а не про те, щоб були знищені інші','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6308,200,100455.jpg\" />Архиєпископ Томас Едвард Ґалліксон, який вже понад три роки є представником Папи Римського в Україні, у своєму блозі та на сторінці у Фейсбуку досить прямо висловлював солідарність з українським народом під час Майдану і теперішніх подій на Донбасі. А&nbsp;про те, якою є позиція Папи у цих питаннях, Апостольський Нунцій розповів в інтерв’ю часопису<a href=\"http://www.credo-ua.org/\">CREDO</a>. Також представник Святішого Отця прокоментував численні звинувачення на адресу українських католиків з боку Російської Православної Церкви та поділився своїми припущенням щодо справжніх причин небажання Патріарха Кирила зустрічатися з Папою. Ватиканський дипломат розповів також про своє бачення конфлікту між греко- та римо-католиками у Львівській області, як і про перспективи отримання УГКЦ гідності Патріархату.</strong>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2562:isus-molyvsja-pro-jednist-cerkvy-a-ne-pro-te-shchob-buly-znyshcheni-inshi&catid=28:tema-nomera-cat&Itemid=2',4826,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4798,'P','2014-12-26',1,NULL,'Rss','Понад тисяча дітей отримали подарунки від Святого Миколая БФ «Картіас Київ»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6312,200,unnamed.jpg\" />БФ «Карітас Київ» у рамках постійної праці надає допомогу багатьом дітям та кризовим сім’ям. Окрім цього проводяться різні акції. Однією із таких є подарунки для дітей до свята Святого Миколая. Цього року Карітас Київ надав понад тисяча подарунків для різних категорій дітей», - повідомляє о. Роман Сиротич, директор БФ «Карітас Київ».</p>\n<p>Святий Миколай Карітасу попрямував до дітей, які його з нетерпінням чекали. Так, подарунки отримали діти з кількох будинків сімейного типу, інтернатів, лікарень, діти із кризових, малозабезпечених, неповних та багатодітних родин, а також діти з сімей вимушених переселенців та діти, батьки яких загинули в АТО та на Майдані.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2563:2014-12-29-14-31-20&catid=29:propovidi-cat&Itemid=34',4830,NULL,1,26),
(4799,'N','2014-12-29',1,NULL,'Note','Самуил Мещеряков','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Святейшего Правительсвующего Синода члену великому Господину высокопреосвященнейшему Серапиону митрополиту Киевскому и Галицкому Киевопечерския лавры Священно Архимандриту и разных Орденов Кавалеру</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Святейшего Правительсвующего Синода члену великому Господину высокопреосвященнейшему Серапиону митрополиту Киевскому и Галицкому Киевопечерския лавры Священно Архимандриту и разных Орденов Кавалеру</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">От воронежского первой гильдии купца Самуила Никитинна Мещерякова</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Покорнейшее Прошение</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp; По в милости Божией и судьбам Его Святым в прошлом 1808 году в Июле месяце быв в Киеве супруга мояАнна Антонова дочь&nbsp; для Богомолия и поклонения в лавре Святым мощам по воли Божией искончала жизнь Свою и положено тело Ея на Оскольдовой могиле при деревяной церкви Святителя Христова Николая которая от долговременности пришла в раздыченцы (?) гнилость и покривилась к падению на коей опасности иконное писание и престол Святый вынесенны. Мое желание чтобы поусопшей моей Супруге продолжалась на месте&nbsp; том Служба и моление о душе Ея и из усердия моего к ея памяти вместо оной деревяной желаю выстроить моим капиталом церковь каменную и на заготовление материалов в прошлом году переслал архимандриту Киприану две тысячи рублей на кои заготовлено кирпича низвесть довольное колличество на постройку оной церквы Сочинении Плин с Фасадом Вашему Высокопреосвященству пределов представляю воспокорнейше испрашиваю Архипастырского вашего на построения каменной Церквы Подсления и благословения. А последние деньги на постройку храма имею подоле моей вручить в распоряжение Архимандрита Киприана шесть тысяч рублей на сче прошу Вашей&nbsp; милостивой Архипастырской резолюции.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">К сему прошуния воронежского купца</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Самуил Мещеряков</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Июля 16 дня</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">1809 года</span></p>',NULL,NULL,4828,5,1,13);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4800,'P','2014-12-27',1,NULL,'Rss','Результати ХІІІ ФЕСТИВАЛЮ-КОНКУРСУ ДУХОВНОЇ ПІСНІ «АСКОЛЬДІВ ГЛАС»','<p><img src=\"http://askoldova-mohyla.org/uk/image,6312,200,IMG_0885.jpg\" style=\"margin: 10px; float: right;\" />20 грудня 2014 р. Б. в приміщенні НПУ ім. М.&nbsp;Драгоманова відбувся <strong>ХІІІ&nbsp;Фестиваль-конкурс духовної пісні «Аскольдів глас»</strong>.</p>\n<p>Конкурс традиційно проходив у рамках святкування зимового празника Святого Миколая, який для парафії храму на Аскольдовій могилі – засновника цього мистецького заходу, - є храмовим. За сім років існування фестиваль став знаною мистецькою подією в житті нашої столиці; щороку розширюється географія учасників конкурсу, адже в ньому беруть участь мистецькі колективи та окремі виконавці з цілої України, для багатьох з яких це чудова можливість дебютувати на серйозній сцені та спробувати свої сили у змаганні з іншими виконавцями.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2564:2014-12-29-15-03-21&catid=25:parafija-cat&Itemid=27',4831,NULL,1,26),
(4801,'P','2014-12-28',1,NULL,'Rss','Служіння','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6310,200,04.jpg\" />19 грудня 2014 року сестра Василія (Гальцова) і волонтер Юрій Йосифович послужили Святителю Миколаю, передавши дарунки, зібрані в результаті доброчинної акції, що триває в громадах УГКЦ у м. Києві. Їх супроводив о. Костянтин Пантелей, керівник Відділу душпастирства у пенітенціарній системі України.</p>\n<p>В Київському слідчому ізоляторі у корпусі тримання неповнолітніх ув\'язнених відбулось урочисте привітання неповнолітніх зі святом Святого Миколая.&nbsp;Програму разом з вихованцями, утримуваними в Київському слідчому ізоляторі, приготувала с. Василія (Гальцова), ЧСВВ. Свято було у формі театралізованої вистави, учасниками якої були самі неповнолітні ув\'язнені. До дітей завітав Св. Миколай (Юрій Йосифович). Він запитав дітей про Заповіді Божі, вони приготували невеличкий концерт, читали вірші та заспівали разом святкові пісні. Св. Миколай у супроводі о. Костянтина Пантелея і с. Василії цього ж дня побував в камерах, де утримуються жінки.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2565:sluzhinnja&catid=29:propovidi-cat&Itemid=34',4834,NULL,1,26),
(4802,'P','2015-01-06',0,NULL,'Note','Різдвяна молитва України','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6318,200,20141226175344.jpg\" alt=\"\" width=\"200\" height=\"112\" />Запрошую до перегляду і єдності в молитві:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>6 січня, 17:00 &ndash; 21:00 &ndash; Надвечір&rsquo;я Різдва Христового,</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>7 січня &ndash; ранковий повтор</strong>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Очолюватимуть богослужіння й благословлятимуть вірян Предстоятелі українських Церков (УПЦ КП, УГКЦ, УПЦ, УАПЦ) з головних кафедр України:</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Перший Національний транслюватиме Різдвяні богослужіння з кафедральних соборів України.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Запрошую до перегляду і єдності в молитві:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>6 січня, 17:00 &ndash; 21:00 &ndash; Надвечір&rsquo;я Різдва Христового,</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>7 січня &ndash; ранковий повтор</strong>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Очолюватимуть богослужіння й благословлятимуть вірян Предстоятелі українських Церков (УПЦ КП, УГКЦ, УПЦ, УАПЦ) з головних кафедр України:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Святіший Філарет</strong> зі Свято-Володимирського собору,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Блаженніший Святослав</strong> з Патріаршого собору Воскресіння Христового,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Блаженніший Онуфрій </strong>зі Святої Успенської Києво-Печерської лаври,&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Блаженніший Мефодій</strong> з Собору Святого апостола Андрія Первозванного.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Маю честь коментувати з найкращими богословами країни!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Нехай Різдвяний дух зігріває Ваше щастя!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>Христос рождається! Славімо Його!&nbsp; </em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">З Різдвяною колядою &nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Христина Стебельська&nbsp;&nbsp;</span></p>',NULL,NULL,4835,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4803,'P','2015-01-07',0,NULL,'Note','Запрошення на різдвяний вертеп на Аскольдовій Могилі','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6319,200,tVQcjNgo8.jpg\" alt=\"\" width=\"200\" height=\"133\" />Сьомого січня цього року в церкві святого Миколая, що на Аскольдовій могилі молодіжна спільнота Відродження запрошує всіх парафіян Аскольдової могили взяти участь в перегляді нашого церковного вертепу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">У вертепі взяло участь девятеро учасників спільноти під організацією та провадженням пані Руслани Дмитровської. Також наша різдвяна вистава була записана на радіо Марія і транслювалася після початку католицького різдва.</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6319,200,tVQcjNgo8.jpg\" alt=\"\" width=\"200\" height=\"133\" />Сьомого січня цього року в церкві святого Миколая, що на Аскольдовій могилі молодіжна спільнота Відродження запрошує всіх парафіян Аскольдової могили взяти участь в перегляді нашого церковного вертепу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">У вертепі взяло участь девятеро учасників спільноти під організацією та провадженням пані Руслани Дмитровської. Також наша різдвяна вистава була записана на радіо Марія і транслювалася після початку католицького різдва.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Хто бажає запросити вертеп до себе до дому дзвоніть до нашого керівника пані Руслани за такими номерами : 097-931-21-24, 093-20-10-510.</span></p>',NULL,NULL,4836,5,1,17),
(4804,'P','2015-01-02',1,NULL,'Rss','Вже не раби, але брати','<p><img src=\"http://askoldova-mohyla.org/uk/image,6310,200,pic_cbd.jpg\" style=\"margin: 10px; float: right;\" />1. На початку нового року, який ми приймаємо як благодать і дар Божий людству, я хочу звернутися до кожної людини, а також до кожного народу і країни світу, до глав держав і урядів і до лідерів різних релігій з моїми гарячими побажаннями миру, супроводжуючи їх молитвами про те, щоб припинилися війни, конфлікти і страждання, причиною яких є як сама людина, так і епідемії, що вже давно відомі й знову з’являються, та руйнівний вплив стихійних лих. Я особливо молюся про те, щоб, відповідаючи на наше спільне покликання співпрацювати з Богом і з усіма людьми доброї волі заради зміцнення злагоди і миру в світі, ми вміли протистояти спокусі вести себе негідно нашої людяності.<o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2566:vzhe-ne-raby-ale-braty&catid=32:gk-svit-cat&Itemid=58',4837,NULL,1,26),
(4805,'P','2015-01-03',1,NULL,'Rss','Його молитва – бути доброю, нормальною людиною!..','<p><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6320,200,huza.jpg\" />\"Я не хочу фільму про себе і не хочу фільму заради себе. Я просто хочу розповісти, як ми жили, щоб молодь це побачила і, можливо, по-іншому подивилась на той історичний пласт і порівняла його з теперішнім\",</em><em> </em>– відповів&nbsp;<strong>Любомир Гузар</strong> на пропозицію зняти про нього фільм.</p>\n<p>Блаженніший владика Любомир Гузар – єдиний українець, якого за ясність розуму і високу духовність могли обрати Папою Римським. Він протягом 10 років очолював Українську греко-католицьку церкву і традиційно входить до сотні найвпливовіших людей України.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2567:ogo-molytva-buty-dobroju-normalnoju-ljudynoju&catid=28:tema-nomera-cat&Itemid=2',4838,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4806,'P','2015-01-05',1,NULL,'Rss','14 кроків до щасливого Різдва','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6320,200,20150104105453.jpg\" />Вже цілком скоро Різдво. Приготування, клопоти, все встигнути, усе приготувати… А воно прийде і якось так непомітно піде. Залишиться трохи фотографій, трохи спогадів, а так – нічого особливого, все як завжди. Потім знову потрібно повертатись до праці, до щоденної рутини.</p>\n<p>Але все може бути не так. Ти хочеш справжнього Свята? Справжнього Різдвяного Дива? Ти хочеш, щоб цьогорічне Різдво залишилось з Тобою надовго? Якщо так, то кілька простих, але дуже важливих порад, які допоможуть мрію зробити реальністю</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2568:14-krokiv-do-shchaslyvogo-rizdva&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4839,NULL,1,26),
(4807,'P','2015-01-05',1,NULL,'Note','Святий Миколай відвідав поранених героїв','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6323,200,2.jpg\" alt=\"\" width=\"200\" height=\"150\" />18 грудня Святий Миколай зі своїми ангелами-помічниками завітали до центрального клінічного військового госпіталю на Печерську в м. Києві. Відвідали 320 поранених хлопців, яким Святий Миколай вручив подаруночки приготовлені екуменічною спільнотою християн та теплі слова Подяки.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Нехай Всемилосердний Бог нагородить Всіх Вас за щиру любов і добрі наміри!</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6323,200,2.jpg\" alt=\"\" width=\"200\" height=\"150\" />18 грудня Святий Миколай зі своїми ангелами-помічниками завітали до центрального клінічного військового госпіталю на Печерську в м. Києві. Відвідали 320 поранених хлопців, яким Святий Миколай вручив подаруночки приготовлені екуменічною спільнотою християн та теплі слова Подяки.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><br /><img style=\"float: right;\" src=\"image,6323,200,1.jpg\" alt=\"\" width=\"200\" height=\"149\" />Висловлюємо щиру подяку у підготовці подарунків: Галині Іванівні, Віктору Дмитровичу, Анатолію, Леоніду, 4 пенсіонеркам, Юлії, Валентині, Наталії, Валентині, Ірині, Сергію, Ірині, Олексію, Ользі, Віті, Олександру, Святославу, Олені, Юрію, Богданці, Остапчику, Марії, Василині, Владиславу, Юлії, Руслану, Анатолію, Олександру, Людмилі, Анастасії, Володимиру, Мирославі, Оксані, Марії, Іванці, Тарасу, Ірині, Марії, Оресті, Богдані, с.Василії, о. Віталію, о. Ігорю, та Святому Миколаю &ndash; братові Дмитрові.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Нехай Всемилосердний Бог нагородить Всіх Вас за щиру любов і добрі наміри!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Марія Томишинець, &laquo;Оранта&raquo;</span></p>',NULL,NULL,4840,5,1,16),
(4808,'P','2015-01-05',1,NULL,'Rss','В очікуванні Різдва','<p><img src=\"http://askoldova-mohyla.org/uk/image,6306,200,54646.jpg\" style=\"margin: 10px; float: left;\" />Майже все, що оточує нас на Святий Вечір має своє християнське тлумачення. Навіть чисто прибрана хата – це символ готовності, очікування другого приходу Месії у славі. Але вся символіка Святого Вечора, як приготування до Різдва, залишається порожньою, якщо людина не приготована до найбільшого Таїнства – єднання з Месією у Пресвятій Євхаристії.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2561:v-ochikuvanni-rizdva&catid=28:tema-nomera-cat&Itemid=2',4841,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4809,'P','2015-01-06',1,NULL,'Rss','Відроджуючи різдвяну традицію','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6319,200,435.jpg\" />При парафії св.&nbsp;Миколая Чудотворця на Аскольдовій могилі (м.&nbsp;Київ) було організовано молодіжний вертеп, що має давню й цікаву історію: більше півсторіччя його грають у Донецькій області в селі Званівка нащадки переселених з Дрогобицької області на Донбас у 1951 році. Традиція жваво побутує серед переселенців і в наш час.</p>\n<p>У 2007-му році вертеп зі Званівки вістив про народження Дитятка Ісуса Сина Божого у численних оселях. Серед його перших глядачів – головний пастор греко-католицької церкви Любомир Гузар, Голова Київської організації Спілки письменників України Анатолій Погрібний, Голова Всеукраїнського товариства \"Просвіта\" Павло Мовчан, дисиденти Євген Сверстюк, Василь Овсієнко, Богдан Горинь, сучасні українські артистки Леся Горова, Руслана Лижичко та багато ін.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2569:vidrodzhujuchy-rizdvjanu-tradyciju&catid=25:parafija-cat&Itemid=27',4842,NULL,1,26),
(4810,'P','2015-01-06',1,NULL,'Rss','Дірявий Вифлеєм','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6319,200,10906324.jpg\" />Мій Вифлеєм цьогоріч увесь пронизаний дірами, цілісінький його просторочас прошитий наскрізними дірами автоматних і кулеметних черг, уламків мін і гранат, Градів. Куди не глянь - усюди діри: фасади будинків, паркани, огорожі, вікна й дахівки, дерева й автомобілі пронизані дірами. Цьогоріч мій Вифлеєм із дірявого світу, розташованого десь на межі людського глузду та людського безумства, на сотильній грані поміж дійсним і уявним, засіяній незібраними соняхами минулорічного посіву... Сьогодні у Пісках під Донецьком - жодного натяку на безпеку, жодного сховку від жорстокоі продірявленості. Куди не ступи ногою -усюди продірявлена спотвореність розстріляноі дійсності: простір сконденсованоі деструкціі, час смисловоі дисперсіі. Дірявий світ виглядає таким безглуздим!..</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2570:dirjavy-vyflejem&catid=28:tema-nomera-cat&Itemid=2',4843,NULL,1,26),
(4811,'P','2015-01-07',1,NULL,'Rss','Різдвяне послання Блаженнішого Святослава','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6326,200,rizdvja.jpg\" />Святе Євангеліє оповідає нам, що Господь Всесвіту народився в родині біженців. Спочатку через наказ кесаря, а потім через кровожерність царя Ірода Пресвята родина була змушена покинути свою домівку та просити прихистку в чужих людей. Так, наш Бог забажав народитися як безхатченко і біженець! За таких дивних обставин народження Спасителя привілей перебувати біля Божого Дитяти мали разом із мудрецями зі Сходу лише ті, хто не соромився бути поруч із потребуючими, вигнанцями та переслідуваними.</strong>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2571:rizdvjane-poslannja-blazhennishogo-svjatoslava&catid=28:tema-nomera-cat&Itemid=2',4844,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4812,'P','2015-01-08',1,NULL,'Rss','Допоможіть переселенцям, і вони будуть апостолами єдності України','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6326,200,0x445.jpg\" />Стараймося сповняти Божу волю, стараймося жити так, як Господь Бог хоче, щоб ми жили. Так поводитися, так ставитися і до Бога, і до ближніх, як він нас вчить. Я не знаю, чи є інший спосіб, як може в світі існувати мир, справжній мир, в якому кожний був би собою задоволеним, спокійним, певним своїх прав. Якщо би ми всі старалися сповняти Божу волю, то такі речі, як війни, кривда різного роду, просто б не існували. Тому, як щось можу сказати, щось просити, то прошу: стараймося, дорогі люди, сповняти Божу волю. Це єдиний певний шлях, щоби ми могли жити так, як Господь Бог хоче, щоб ми жили.</strong>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2572:dopomozhit-pereselencjam-i-vony-budut-apostolamy-jednosti-ukrajiny&catid=28:tema-nomera-cat&Itemid=2',4845,NULL,1,26),
(4813,'P','2015-01-09',1,NULL,'Rss','Бути матір’ю – це життєвий вибір','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6328,200,kIOUrmBJoh4.jpg\" />«Суспільство без матерів було би нелюдяним суспільством, тому що мами вміють завжди, навіть в найгірших хвилинах, свідчити лагідність, самопосвяту, моральну силу». Свою катехизу під час першої у новому році загальної аудієнції Папа Франциск присвятив матері.</strong><o:p></o:p></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2573:buty-matirju--ce-zhyttjevy-vybir&catid=32:gk-svit-cat&Itemid=58',4846,NULL,1,26),
(4814,'P','2015-01-08',1,NULL,'Note','ЗВІТ №11 про благодійну допомогу матерям з дітьми, які вимушено покинули свої домівки із-за бойових дій в зоні АТО (17.07.2014 р.)','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Речі доставлені до притулку волонтером п.Оксаною К.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отримуючи допомогу, волонтери поцікавились роботою служби милосердя і заявили про своє бажання взяти участь у найближчому проекті, що готувався на той час, а саме &laquo;Шкільному портфелику&raquo;.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;Служба милосердя при церкві св.Миколая на Аскольдовій могилі через волонтера Оксану К. отримала інформацію про створення притулку для матерів з дітьми віком до одного року, які вимушені були покинути рідні домівки в зоні бойових дій АТО. Приміщення для притулку надавала одна із волонтерів, все інше (меблі, посуд, постіль, одяг для малят і їхніх мам, засоби гігієни) декларувалося в списку потреб.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отримавши благословення настоятеля церкви о.Ігоря Онишкевича, уділили потребуючим пожертвувані парафіянами: одяг дитячий для немовлят та діток першого року життя, памперси, постільну білизну, іграшки дитячі, посуд &ndash; всього 3 мішки. Крім того, дітки отримали пожертвуваний доброчинцями новий візочок дитячий (зимовий і літній варіант), а також ліжечко дитяче від парафіянина Ігоря.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Речі доставлені до притулку волонтером п.Оксаною К.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отримуючи допомогу, волонтери поцікавились роботою служби милосердя і заявили про своє бажання взяти участь у найближчому проекті, що готувався на той час, а саме &laquo;Шкільному портфелику&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Висловлюємо щиру подяку жертводавцям та волонтерам і прикликаємо Боже благословення на їх родини.</span></p>',NULL,NULL,4847,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4815,'P','2015-01-11',1,NULL,'Rss','Своїм служінням Богу і один одному ми маємо випросити у Господа те, чого найбільше очікує кожен українець, - миру','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6330,200,milyan_seminariya_01.jpg\" />Кожне наше слово і діло має відкривати для інших Бога, щоб служінням одне одному ми випросили в Господа те, чого найбільше очікує кожен українець, - миру. Про це заявив голова Пасторально-міграційного відділу УГКЦ владика Йосиф (Мілян) під час \"Різдвяної просфори з Патріархом УГКЦ\".</strong></p>\n<p>\"Коли царі прийшли повідомити царя Ірода, що зоря звістила їм появу Царя Господа, Ірод стривожився, і з ним - увесь Єрусалим. Я хотів би побажати справді благодатної тривоги всім нам з настанням Різдвяних свят.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2575:svojim-sluzhinnjam-bogu-i-odyn-odnomu-my-majemo-vyprosyty-u-gospoda-te-chogo-nabilshe-ochikuje-kozhen-ukrajinec-myru&catid=28:tema-nomera-cat&Itemid=2',4849,NULL,1,26),
(4816,'P','2015-01-10',1,NULL,'Rss','Папа Франциск: Неправда, що деякі життя не гідні, щоб бути прожитими','<p><img src=\"http://askoldova-mohyla.org/uk/image,6330,200,AP2128861_Articolo.jpg\" style=\"margin: 10px; float: left;\" />Над «мудрістю серця» у світлі слів зі старозавітної Книги Йова: «Я був сліпому оком, кульгавому я був ногами» запропонував замислитися Папа Франциск у своєму посланні з нагоди Всесвітнього дня хворого, який відзначатиметься 11 лютого 2015 року. У вівторок, 30 грудня 2014 р., цей документ оприлюднено у Ватикані.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2574:papa-francysk-nepravda-shcho-dejaki-zhyttja-ne-gidni-shchob-buty-prozhytymy&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4850,NULL,1,26),
(4817,'P','2015-01-12',1,NULL,'Rss','Україна стала драматичним театром бойових дій – Папа Римський','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6330,200,papa_frantsysk20.jpg\" />Папа Римський Франциск сподівається, що конфлікт в Україні вирішиться шляхом мирного діалогу.</strong></p>\n<p>Про це він заявив, виступаючи перед акредитованими у Ватикані закордонними послами, повідомляє посольство України при Святому Престолі на своїй&nbsp;<a href=\"https://www.facebook.com/emb.va/photos/a.253793301492620.1073741829.247723498766267/363846843820598/?type=1&amp;theater\">сторінці</a> у Facebook.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2576:ukrajina-stala-dramatychnym-teatrom-boovyh-di--papa-rymsky&catid=32:gk-svit-cat&Itemid=58',4853,NULL,1,26),
(4818,'P','2015-01-14',1,NULL,'Rss','Історія святого Василія Великого','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6337,200,1353570357.JPG\" />14 січня (1 ст. ст.) Церква святкує день пам’яті великого угодника Божого, архиєпископа Кесарійського Василія (Василя) Великого. Народився він в м. Кесарії, що знаходилось в Кападокійській провінції Римської імперії. Батьки Василь та Ємелія були високоосвіченими багатими людьми. Василій Великий отримав хорошу і ґрунтовну освіту: спочатку вдома (батько викладав ораторське мистецтво та філософію), потім вивчав різні науки у місті Афіни, Єгипті, Месопотамії, Палестині та Сирії. Василія дуже цікавило життя Ісуса Христа і життя Христових подвижників – його сучасників. Подорожуючи світом і вивчаючи світські науки, він спостерігав і за життям знаменитих подвижників – християн: Пахомія, що жив у місті Фіваїда, Макарія Старшого та Макарія Олександрійського, Пафнутія, Павла та інших. З багатьма із них він зустрічався і вів довгі філософські і богословські диспути. І коли Василій став упевнений у своїй вірі та був готовий духовно, він прийшов у місто Єрусалим і прийняв хрещення від патріарха Єрусалимського Максима ІІІ у річці Йордан.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2577:istorija-svjatogo-vasylija-velykogo&catid=28:tema-nomera-cat&Itemid=2',4855,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4819,'P','2015-01-15',1,NULL,'Rss','Свята мати сім\'ї','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6338,200,santa.jpg\" />ДЖАННА&nbsp; БЕРЕТТА&nbsp; МОЛЛА&nbsp;&nbsp;&nbsp;&nbsp; - МАТИ, ДОКТОР МЕДИЦИНИ<strong> </strong>(1922 - 1962)</p>\n<p>Амос, 1:13.Біблія.</p>\n<p>Сьогодні витає творча пропозиція: закупити&nbsp; сільські&nbsp; садиби&nbsp; в мальовничих селах Андрієвичі&nbsp; Емільчинського району Житомирської області&nbsp; на землях льону&nbsp; та&nbsp; вишивання, на батьківщині&nbsp; Тетяни Майданович – авторки&nbsp; рукопису “Насіння&nbsp; диких&nbsp; трав”,&nbsp; у селі&nbsp; Горобіївка Канівського&nbsp; району, Черкаської області –&nbsp; в екологічно чистих&nbsp;&nbsp; нішах,&nbsp; заснувати&nbsp; дві&nbsp; Жіночі&nbsp; гімназії&nbsp; імені Джанни Беретти Молли&nbsp; та Жіночий&nbsp; інститут&nbsp; християнської етики&nbsp; ім.Джанни Беретти Молли&nbsp; для поліпшення умов виховання&nbsp; дівчат&nbsp; у дусі християнського послуху.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2578:svjata-maty-simji&catid=35:istorychni-postati-cat&Itemid=48',4856,NULL,1,26),
(4820,'P','2015-01-10',1,NULL,'Note','ЗВІТ №13 про розподілення благодійних пожертвувань парафіян церкви св.Миколая на Аскольдовій могилі  (24.09.2014 р.)','<ol>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Передала до військового шпиталю для бійців АТО</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; светри теплі, футболки &ndash; 37 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; нові теплі шкарпетки &ndash; 1 пакунок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; кросівки &ndash; 2 пари</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; нова постільна білизна для хірургічного відділення &ndash; 3 комплекти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; ліки (передані з Майдану пожертви)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; рушники, пішви для подушок</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;Служба милосердя при церкві св.Миколая на Аскольдовій могилі, зібравши пожертвувані парафіянами, зокрема п.Людмилою з дочкою Марією та внучкою Анною: одяг чоловічий, жіночий та дитячий, взуття, постільна та кухонна білизна, тканини, іграшки дитячі та інші речі домашнього вжитку, розподілила їх таким чином:</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Уділила потребуючим мирянам.</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Передала до військового шпиталю для бійців АТО</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; светри теплі, футболки &ndash; 37 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; нові теплі шкарпетки &ndash; 1 пакунок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; кросівки &ndash; 2 пари</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; нова постільна білизна для хірургічного відділення &ndash; 3 комплекти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; ліки (передані з Майдану пожертви)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; рушники, пішви для подушок</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Уділила переселенцям з Донецької та Луганської обл.</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; ковдра тепла &ndash; 1 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; рушники, постільна білизна</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Відправила гуманітарний вантаж Благодійному фонду &ldquo;Сузір\'я&rdquo; в с.Іванкові Бориспільського р-ну Київської обл.:</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; одяг чоловічий, жіночий, дитячий &ndash; 24 мішки</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; &nbsp;взуття &ndash; 1 мішок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; іграшки дитячі &ndash; 1 мішок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Робота виконана силами волонтерів: п.Світлани, п.Михайла, п.Анни.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Висловлюємо щиру подяку жертводавцям і прикликаємо Боже благословення на їх родини.</span></p>',NULL,NULL,4851,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4821,'P','2015-01-15',1,NULL,'Rss','Тисячі картин з колекції Шептицького були знищені, як ідеологічно шкідливі','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6339,200,01_6628c.jpg\" />1 листопада 2014 року в УГКЦ та в Україні розпочався Рік митрополита Андрея Шептицького. Шептицький став предстоятелем Української Греко-Католицької Церкви 1901 року. І був її очільником аж до своєї смерті 1 листопада 1944 р. Він, без сумніву, - великий діяч. Як і Ярослав Мудрий, Богдан Хмельницький, Тарас Шевченко, він задав вектор розвитку України. Нещодавно глава УГКЦ блаженніший Святослав висловив сподівання, що цього року завершиться беатифікаційний процес митрополита Андрея.</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2579:tysjachi-kartyn-z-kolekciji-sheptyckogo-buly-znyshcheni-jak-ideologichno-shkidlyvi&catid=28:tema-nomera-cat&Itemid=2',4857,NULL,1,26),
(4822,'P','2015-01-15',1,NULL,'Rss','Мирослав Попович: Стратегічним завданням для Європи є усунення Путіна від влади','<p><em><img src=\"http://askoldova-mohyla.org/uk/image,6340,200,1397139718_26_4.jpg\" style=\"margin: 10px; float: left;\" />Революція гідності, втеча президента Януковича, анексія Криму, війна на Донбасі – далеко не вичерпний перелік головних подій 2014 року, які змінили історію сучасної України. Нині про наслідки того, що сталося, говорять здебільшого політики. Але мине час, і свою зважену оцінку цим подіям дадуть історики і філософи.</em></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2580:myroslav-popovych-strategichnym-zavdannjam-dlja-jevropy-je-usunennja-putina-vid-vlady&catid=37:spilnota-cat&Itemid=49',4858,NULL,1,26),
(4823,'P','2015-01-16',1,NULL,'Rss','Святий Сильвестр','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6342,200,Sylveste.jpg\" />Сильвестр </strong>святий, у старому Римі народжений і у святій вірі вихований, учнем був Кирина Пресвітера, від нього ж книжної премудрости і добрих звичаїв навчився, у дорослому віці своєму був гостелюбцем великим, з любови до Бога й до ближнього подорожніх уводив у дім свій і, вмиваючи їм ноги, пригощав їх, всілякий спочинок для них роблячи. Одного разу муж святий і ісповідник Христовий<a href=\"http://www.truechristianity.info/ua/books/saints_ua_05/saints_ua_05_002.php#_ftn2\" target=\"_self\"><noindex>[2]</noindex></a> єпископ Тимотей прийшов до Риму з Антіохії, проповідуючи Євангеліє Царства. Його Сильвестр у дім свій прийняв і з його святого життя й учення багато користав: був-бо досконалий у чеснотах і вірі.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2581:svjaty-sylvestr&catid=35:istorychni-postati-cat&Itemid=48',4860,NULL,1,26),
(4824,'N','2015-01-16',1,NULL,'Rss','Діалог  про  мистецтво','<p><img style=\"margin: 10px; float: left;\" src=\"image,6343,200,1060360-crop.jpg\" alt=\"\" />Молодий обдарований&nbsp; композитор кандидат&nbsp; мистецтвознавства&nbsp;&nbsp; Катерина&nbsp; Кучерява (Чаплик), як&nbsp; і&nbsp; італійський&nbsp; педагог Марія&nbsp; Монтессорі&nbsp; з Італії, вважає: &ldquo;Тепер багато хто, у тому числі&nbsp; і&nbsp; я, вважають, що найважливіший період&nbsp; життя&nbsp; припадає&nbsp;&nbsp; не на&nbsp; студентські&nbsp; роки, а скоріше на перший період, від народження до шести років, оскільки саме тоді&nbsp; формується певний комплекс&nbsp; психічних&nbsp; властивостей, формується людська індивідуальність. У цей період&nbsp; у дитини формується&nbsp; &ldquo;чуттєвий&nbsp;&nbsp; образ&nbsp; навколишнього&nbsp; світу&rdquo;. (Із&nbsp; школи Катерини Чаплик).</p>','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2582:dialog-pro-mystectvo&catid=25:parafija-cat&Itemid=27',4861,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4825,'P','2015-01-16',1,NULL,'Note','Молитва за єдність християн','<div style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,6342,200,10850626.jpg\" alt=\"\" width=\"200\" height=\"129\" />Щиро запрошуємо Вас&nbsp;</strong><strong>до участі у молебні за єдність християн&nbsp;</strong><strong>у храмі св. Василія Великого&nbsp;</strong><em>(Українська Греко-Католицька Церква, Вознесенський Узвіз 7)&nbsp;</em><strong><em>у суботу, 24 січня 201</em></strong><strong><em>5</em></strong><strong><em>&nbsp;р., 18.00 год.</em></strong></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Головною темою розважання і гаслом цього екуменічного Тижня будуть слова Ісуса до самаритянки з Євангелія від Івана (Ів. 4:7) &ldquo;<strong>Дай мені напитися</strong>&rdquo;</span></div>','<div style=\"text-align: center;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Дорогі друзі!</strong></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6342,200,10850626.jpg\" alt=\"\" width=\"200\" height=\"129\" />Вже 47 років поспіль християни усього світу виявляють своє бажання спільно молитись за єдність християнства.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Цього року, з Божою допомогою, в Україні будемо продовжувати вже кількарічну нашу традицію спільної молитви під час&nbsp;<em>Тижня Молитов за Єдність Християн</em>.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Щиро запрошуємо Вас&nbsp;</strong></span><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; line-height: 1.3em;\">до участі у молебні за єдність християн&nbsp;</strong><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; line-height: 1.3em;\">у храмі св. Василія Великого&nbsp;</strong><em style=\"font-family: arial, helvetica, sans-serif; font-size: small; line-height: 1.3em;\">(Українська Греко-Католицька Церква, Вознесенський Узвіз 7)&nbsp;</em><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; line-height: 1.3em;\"><em>у суботу, 24 січня 201</em></strong><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; line-height: 1.3em;\"><em>5</em></strong><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; line-height: 1.3em;\"><em>&nbsp;р., 18.00 год.</em></strong></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Головною темою розважання і гаслом цього екуменічного Тижня будуть слова Ісуса до самаритянки з Євангелія від Івана (Ів. 4:7) &ldquo;<strong>Дай мені напитися</strong>&rdquo;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Переконаний, що тема запропонована християнами Бразилії, з різних екуменічних осередків, які вболівають над сучасною ситуацією браку поваги до людської особи, особливо до національних меншостей цієї країни.</span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></div>\r\n<div style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>З молитвою,&nbsp;</em></span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; line-height: 1.3em;\">о. Ігор Шабан</span></div>',NULL,NULL,4859,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4826,'P','2015-01-17',1,NULL,'Rss','Відроджуючи різдвяну традицію','<p><img src=\"http://askoldova-mohyla.org/uk/image,6344,200,10906254.jpg\" style=\"margin: 10px; float: left;\" />При парафії св.&nbsp;Миколая Чудотворця на Аскольдовій могилі (м.&nbsp;Київ) було організовано молодіжний вертеп, що має давню й цікаву історію: більше півсторіччя його грають у Донецькій області в селі Званівка нащадки переселених з Дрогобицької області на Донбас у 1951 році. Традиція жваво побутує серед переселенців і в наш час.</p>\n<p>У 2015-му році званівський вертеп вістив про народження Дитятка Ісуса Сина Божого у численних оселях.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2583:vidrodzhujuchy-rizdvjanu-tradyciju&catid=25:parafija-cat&Itemid=27',4862,NULL,1,26),
(4827,'P','2015-01-18',1,NULL,'Rss','КІЛЬКА ПРОСТО ДУМОК ПРО МОЄ РІЗДВО...','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6345,200,109312.jpg\" />Колись про таке читав лише у книжках та переглядав у кінофільмах, ніколи не підозрюючи, що сам удостоюсь стати маленьким учасником такого великого таінства: Сповідь на горищі, у той час коли над дахом грізно гуркотять АГСні черги та літають міни; Євхаристія під землею, посіченою купою металу та вогню протягом місяців жорстокого протистояння, у холодній темряві тісного приміщення; дорога на Різдвяну літургію під гул Градів, що залпово прямували прямо над моєю головою у напрямку Донецького аеропорту. І все це було зовсім по-справжньому: спсравжні постріли, справжні снаряди, справжня кров, як і справжньою була відвага і хоробрість тих, хто із зброєю в руках став на захист самого життя. Ніколи не міг уявити собі... Історія таки має властивість повторюватись...</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2584:kilka-prosto-dumok-pro-moje-rizdvo&catid=28:tema-nomera-cat&Itemid=2',4863,NULL,1,26),
(4828,'P','2015-01-18',1,NULL,'Rss','Водохреща','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6346,200,sviato.jpg\" />Свято Водохреща</strong>, що відзначається 19 січня, має й іншу назву —&nbsp;<strong>Богоявлення</strong>. Може виникнути закономірне запитання: чому&nbsp;<strong>Богоявленням</strong> названий не день народження&nbsp;<a href=\"http://blyzhchedoboga.com.ua/isus-hristos-sin-bozhiy/\">Ісуса Христа</a>, а саме цей день. Відповідь проста: справжній день не той, у який Христос народився, а той, у який він хрестився й освятив воду.</p>\n<p>Святкувати&nbsp;<a href=\"http://blyzhchedoboga.com.ua/bogoyavlennya/\">Богоявлення</a> християни почали ще з апостольських часів. Збереглося свідчення святителя Климента Олександрійського від II століття про нічне пильнування, яке вчиняється перед&nbsp;<strong>святом Водохреща</strong>Господнього. Починаючи з III століття, Григорій Чудотворець, мученик Іполит і всі великі отці Церкви проводили в день свята особливі розмови з паствою, у яких роз’яснювали його зміст.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2585:vodohreshcha&catid=28:tema-nomera-cat&Itemid=2',4864,NULL,1,26),
(4829,'P','2015-01-19',1,NULL,'Rss','Папа до молоді: вчитися плакати, любити та не почуватися самодостатніми','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6348,200,reuters637185_articolo.jpg\" />Справжня любов означає любити і дозволити себе любити. Про це говорив Папа Франциск, зустрічаючись з філіппінською молоддю на території спортивного комплексу католицького університету святого Томи в Манілі, що є найстаршим і найбільшим у Азії, який майже 400 років провадять Отці Домініканці. На початку зустрічі представники молоді поділилися свідченнями, після чого Папа дав відповідь на їхні сумніви та запитання.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2586:papa-do-molodi-vchytysja-plakaty-ljubyty-ta-ne-pochuvatysja-samodostatnimy&catid=32:gk-svit-cat&Itemid=58',4867,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4830,'P','2015-01-18',1,NULL,'Note','ПАТРІАРХ УГКЦ НАЗВАВ МАРШЕМ ГІДНОСТІ НИНІШНЄ ВШАНУВАННЯ ЖЕРТВ ТЕРОРИСТІВ','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6348,200,ArticleIm.jpg\" alt=\"\" width=\"150\" height=\"200\" />&laquo;Сьогоднішню подію можна назвати й маршем гідності. Бо ми сьогодні хочемо ствердити і засвідчити святість і гідність кожної людини незалежно від того, якою мовою вона розмовляє, яку релігію сповідує, які переконання плекає у своєму серці. Кожна людська особа є сотворена на подобу і образ Божий, є найгіднішим сотворінням у цьому світі. Ми сьогодні теж хочемо засвідчити нашу з вами гідність як вільних громадян незалежної держави. Ми сьогодні можемо сказати, що ця подія є маршем за життя, бо ми хочемо ствердити святість кожного людського життя. І нашим обов&rsquo;язком є захищати життя. Сьогодні ми маємо об&rsquo;єднатися, бути солідарними у нашому обов&rsquo;язку захищати свою країну, свою землю, своїх братів і сестер. Нехай Всемилостивий Господь зішле мир на українську землю&raquo;</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6348,200,ArticleIm.jpg\" alt=\"\" width=\"150\" height=\"200\" />Маршем гідності вільних громадян та маршем за життя назвав Глава УГКЦ Святослав (Шевчук) сьогоднішню ходу та молебень за жертвами Волновахи, який відбувся у Києві на Майдані Незалежності.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Про Патріарх УГКЦ сказав, звертаючись до десятків тисяч киян та гостей столиці після молебню.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&laquo;Ми сьогодні є учасниками і свідками безпрецедентної події в історії нашої незалежної батьківщини. Цю подію уже сьогодні по-різному називають. Хтось каже, що ми стали учасниками маршу миру, аби сказати рішуче \"ні\" тероризму у будь-яких його проявах. Рішуче ні насильству над людиною. Хтось назвав цю подію ходою солідарності. І це є справедливо, бо сьогодні єднається уся Україна, в усіх її куточках. Сьогодні з нами єднається світова громадськість у різних міста у різних країнах світу. Ми сьогодні є разом, щоб об&rsquo;єднатися у нашому рішучому &laquo;ні &laquo;агресії та насильству&raquo;, - сказав він.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&laquo;Сьогоднішню подію можна назвати й маршем гідності. Бо ми сьогодні хочемо ствердити і засвідчити святість і гідність кожної людини незалежно від того, якою мовою вона розмовляє, яку релігію сповідує, які переконання плекає у своєму серці. Кожна людська особа є сотворена на подобу і образ Божий, є найгіднішим сотворінням у цьому світі. Ми сьогодні теж хочемо засвідчити нашу з вами гідність як вільних громадян незалежної держави. Ми сьогодні можемо сказати, що ця подія є маршем за життя, бо ми хочемо ствердити святість кожного людського життя. І нашим обов&rsquo;язком є захищати життя. Сьогодні ми маємо об&rsquo;єднатися, бути солідарними у нашому обов&rsquo;язку захищати свою країну, свою землю, своїх братів і сестер. Нехай Всемилостивий Господь зішле мир на українську землю&raquo;, - додав Глава УГКЦ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18 січня у о 14:00 із Михайлівської площі у Києві вирушив марш солідарності проти тероризму. Учасники ходи вшанували пам\'ять загиблих 13 пасажирів автобуса під Волновахою на Донеччині, а також 5 тисяч жертв війни на Донбасі, що триває вже десятий місяць поспіль. Згодом на Майдані Незалежності відбувся молебень, по закінченні якого до людей звернулися Предстоятелі УПЦ КП, УГКЦ, єпископи УАПЦ, УПЦ (МП), представники протестантських Церков, мусульмани. Завершилася акція запаленням свічок та покладанням квітів у пам&rsquo;ять жертв Волновахи. У Марші та покладанні квітів також взяли участь Президент Петро Порошенко з дружиною, прем\'єр Арсеній Яценюк, мер Києва Віталій Кличко та інші.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><a href=\"http://risu.org.ua/\">http://risu.org.ua/</a></span></p>',NULL,NULL,4865,5,1,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4831,'P','2015-01-20',1,NULL,'Rss','Іван Хреститель','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6350,200,1421740836_ivan.jpg\" style=\"margin: 10px; float: left;\" />Іоан Предтеча, Іван Хреститель, Іоанн Предтеча, Іван Предтеча, Йоан Хреститель, Йоан Предтеча, Іоан Хреститель</strong> — пророк, мученик, після Діви Марії найбільший святий. Про пророка Господь&nbsp;<a href=\"http://blyzhchedoboga.com.ua/isus-hristos-sin-bozhiy/\">Ісус Христос</a>казав: «Серед народжених жонами не поставав (пророк) більший від Івана Хрестителя».&nbsp;<strong>Іван Хреститель</strong>прославляється Церквою, як «янгол, і апостол, і мученик, і пророк, і свічар, і друг Христів, і пророків печатка, і заступник старої та нової благодаті, і серед народжених пречесніший, і світлого Слова глас».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2587:ivan-hrestytel&catid=35:istorychni-postati-cat&Itemid=48',4868,NULL,1,26),
(4832,'P','2015-01-21',1,NULL,'Rss','БФ «Карітас Київ» у навечір’я свята Богоявління організував Святу Вечерю для багатодітних сімей вимушених переселенців','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6351,200,P1180130.jpg\" />«18 січня 2015 року БФ «Карітас Київ» організував Святу Вечерю для багатодітних сімей вимушених переселенців. Серед учасників були бідні сімї зі Сходу України та Криму. Загальна кількість присутніх на вечері понад 80 осіб, з яких переважаюча більшість - це діти», - повідомляє директор БФ «Карітас Київ» о. Роман Сиротич.</p>\n<p>Свято розпочалася святковою виставою, яку підготували підопічні діти Карітасу разом із волонтерами університету Т. Шевченка.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2588:2015-01-21-18-55-20&catid=29:propovidi-cat&Itemid=34',4869,NULL,1,26),
(4833,'P','2015-01-21',1,NULL,'Rss','День Української Соборності','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6351,200,45646.jpg\" />22 січня Україна відзначає День Соборності, приурочений до Акту об\'єднання цього дня Української Народної Республіки та Західноукраїнської Народної Республіки.</p>\n<p>Соборність українських земель має глибоке історичне коріння, спирається на споконвічну мрію народу про власну незалежну, соборну державу та є інтегральним результатом складного і довготривалого процесу її формування.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2589:den-ukrajinskoji-sobornosti&catid=28:tema-nomera-cat&Itemid=2',4870,NULL,1,26),
(4834,'P','2015-01-23',1,NULL,'Rss','Світлі голоси життя','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6353,200,929b7fbb.jpg\" />Уперше традиційний уже вечір Різдвяних Василів 14 січня в столичному Будинку вчителя відбувся без Євгена Сверстюка. І був присвячений його світлій пам’яті. На великому екрані промайнули фото з його життєпису — з мамою, дружиною, дітьми і друзями... І картина Оксани Плисюк «Вершник на зоряному небі». Вершник побув трохи з товариством у залі Будинку вчителя — та й, віддаляючись, посів своє місце на небі, ставши зіркою. «Для мене Ти — синонім совісті, найвища, найяскравіша зірка мого небобратства...» — зачитав історик дисидентського руху Василь Овсієнко уривок з листа Василя Стуса до Євгена Сверстюка 1979 року. Для зоряного небобратства тут, на землі, зі сцени Будинку вчителя, де колись і члени Центральної ради засідали — співали діти з гурту «Дай Боже» з Ольгою Мельник, Ігор Жук, Олена Голуб, Тарас Компаніченко.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2590:svitli-golosy-zhyttja&catid=37:spilnota-cat&Itemid=49',4871,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4835,'P','2015-01-23',1,NULL,'Rss','Наші побажання і наші діяння','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6354,200,IMG_3809.jpg\" />Є в нашому народі звичай, що з нагоди церковних чи світських свят вітаємо одне одного і переказуємо різні побажання. Найбільш інтенсивним щодо цього є кінець грудня і початок січня. І це не безпідставно.</p>\n<p>У другій половині грудня поступово довшає день. Отже, є надія на світло і тепло. Інша причина – закінчення одного року і початок наступного. Кінець старого року – це час підбиття підсумків того, що було, а початок нового – припущення, що буде; пора сподівань на краще.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2591:nashi-pobazhannja-i-nashi-dijannja&catid=28:tema-nomera-cat&Itemid=2',4872,NULL,1,26),
(4836,'P','2015-01-24',1,NULL,'Rss','Святої мучениці Тетяни','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6355,200,tetiana-sv_pmd.kz.jpg\" />Батько святої Тетяни був високим царським урядником в Римі, й водночас таємним християнином. Тетяна пожертвувала своє дівицтво Богу і цілковито віддала себе молитві й добрим ділам. А що життя її було для всіх прикладом благочестя і чесноти, то поставлено її диякони- сою. У житії святого Григорія Ниського ми вже говорили про дияконис та їх обов’язки. Свята Тетяна віддалася усім серцем виконанню покладених на неї обов’язків, а життя її стало ще світліше, ще більше гідне того, щоб бути взірцем. У кожній справі вона старалася про те, щоб зростала слава Божа, а жінок, яких навчала правд віри, застерігала від гріха як найбільшого зла.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2592:svjatoji-muchenyci-tetjany&catid=35:istorychni-postati-cat&Itemid=48',4873,NULL,1,26),
(4837,'P','2015-01-26',1,NULL,'Rss','«Хто вірує – той ніколи не самотній». Читання з енцикліки про віру (1)','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6356,200,AFP2429186_Articolo.jpg\" />«Lumen Fidei – Світло віри» – так називається перша Енцикліка Папи Франциска, що була підписана 29 червня та оприлюднена 5 липня 2013 року. Як зазначає сам автор, вона була майже завершена його попередником, і додає: «За це я йому дуже вдячний, і в Христовому братерстві, приймаю цю його цінну працю, додаючи до тексту деякий свій вклад».</p>\n<p>Хто вірує, той бачить. Хто вірує, той ніколи не є самотнім, тому що віра є спільним благом, допомагаючи відрізняти добро від зла та будувати наші спільноти, даруючи надію. Саме так можна окреслити головну думку цієї енцикліки, над якою ми роздумуватимемо, присвячучи її перечитуванню кілька хвилин наприкінці наших передач у п’ятницю.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2593:2015-01-26-18-56-29&catid=27:gk-v-ukrajini-cat&Itemid=57',4874,NULL,1,26),
(4838,'P','2015-01-27',1,NULL,'Rss','Ліхтар у сніжному спомині','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6357,200,artleo.com-4960.jpg\" />Нічний ліхтар зустрів мене привітним спомином. Я сиджу на сходинці у вечоровою порою. Світло згашене. Горить тільки ліхтар і жодного разу не кліпає своїм великим оком. Ніби намагається перелічити всі пухнасті сніжинки, котрі в повільному вальсі опускаються довкола його височезної ноги. Йому певно вже крутиться в голові, мерехтить в очах, але саме дійство його настільки захопило, що він і не рахується з часом, і сон його не бере, і здається, він вже сам вийняв довжелезну ногу із землі і підтанцьовує у ритмі нечутного вальсу.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2594:lihtar-u-snizhnomu-spomyni&catid=1:mystecka-storinka-cat&Itemid=50',4875,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4839,'P','2015-01-27',1,NULL,'Rss','Папа про віру, як дар Святого Духа і про роль жінок в його передаванні','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6358,200,Babushk.jpg\" />«Пригадую собі ту щиру віру, що у тобі, яка перш була вселилась у твою бабуню Лоїду та у твою матір Евніку, а я певен, що й у тебе. З тієї причини пригадую тобі, щоб ти оживив дар Божий, який у тобі через покладання рук моїх», – писав святий Павло до свого учня апостола Тимотея.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2595:papa-pro-viru-jak-dar-svjatogo-duha-i-pro-rol-zhinok-v-ogo-peredavanni&catid=32:gk-svit-cat&Itemid=58',4876,NULL,1,26),
(4840,'P','2015-01-27',1,NULL,'Rss','15 порад, як перестати викидати їжу','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6359,200,Types.jpg\" />Кожен день в нашому сміттєвому відрі виявляються залишки їжі. Як зробити так, щоб наші продукти якомога менше опинялися на звалищі, кілька корисних порад, як перестати викидати продукти.<o:p></o:p></p>\n<p>Згідно з дослідженнями, у світі щорічно 30% продуктів викидається на смітник. Це відбувається з різних причин: погане зберігання і транспортування, вийшов термін придатності, перевиробництво, а ще маркетинг і реклама в супермаркетах, які часто спонукають людей купувати більше продуктів, ніж потрібно. Для того, щоб цього уникнути потрібно дотримуватися простих правил, які не такі вже складні.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2596:15-porad-jak-perestaty-vykydaty-jizhu&catid=37:spilnota-cat&Itemid=49',4877,NULL,1,26),
(4841,'P','2015-01-27',1,NULL,'Rss','Дорога у Освенцим','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6360,200,0908c.jpg\" style=\"margin: 10px; float: left;\" />Була людина, обличчя якесь людське мала… а виявився ти чучелом. Тебе лишили твого всього. Тебе позбавили твого вигляду, твого одягу, твоїх кіс. Ти одягнутий в смугасте, в тюремне. І імені в тебе більше нема. Ти - 61369. І на цей номер ти відгукуйся. І про все забудь.</strong></p>\n<p>Перші роки після концтабору, коли я бачила велику букву \"О\", мені ставало погано. Я намагалась швидше перегорнути сторінку, бо боялась побачити там слово \"Освенцим\".</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2597:doroga-u-osvencym&catid=28:tema-nomera-cat&Itemid=2',4878,NULL,1,26),
(4842,'P','2015-01-28',1,NULL,'Rss','Пам\'яті юнаків-героїв, замордованих під Крутами','<p><img src=\"http://askoldova-mohyla.org/uk/image,6361,200,Pokhoron_1918.jpg\" style=\"margin: 10px; float: right;\" />“На вас, завзятці-юнаки, що возлюбили Україну, кладу я кращії гадки, мою сподіванку єдину”... І їх нема...</p>\n<p>Кого ми поховали? Хто зважить той жаль? Зберіть всі хмари з усього світу&nbsp;— і не складете тої нерозважної туги, що оповила серце... Жалю немає ваги. Найпалкіші діти України, найдорожчі жертви на залізний вівтар волі. Історія знає багато жертв, але такої сторінки вона ще не розгортала перед людськими очима. В часи хрестових походів ішли одбивати Єрусалим лави охоплених ентузіазмом дітей, вони і гинули лавами, з повною вірою в Царство небесне і в вічне життя.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2598:pamjati-junakiv-gerojiv-zamordovanyh-pid-krutamy&catid=28:tema-nomera-cat&Itemid=2',4879,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4843,'P','2015-01-29',1,NULL,'Rss','Сьогодні можна повернутися до Києва, але не можна повертатися з фронту!!!...','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6362,200,1489143.jpg\" />На Авторадіо – стан руху автотранспорту на столичних дорогах, на Хрещатику – усміхнені, задумані, втомлені і просто люди, у метро – потік людських тіл з усміхненими, задуманими, втомленими і просто людськими обличчями. Життя вирує буденними ритмами, гуляє брудними вулицями, пливе болотними артеріями зимового міста. У Києві – січень, у драмтеатрі – прем’єра, у найближчому відділі ПриватБанку – черга до терміналу, а мої хлопці – у бліндажах, у темних підземеллях українського фронту, у тих самих схованках, у яких іще вчора доводилось сидіти й мені під час артобстрілів, саме тоді, коли по Хрещатику бігали усміхнені, задумані, втомлені і просто люди, пронизуючи сірі київські будні строкатістю просто людських мрій і навіть не здогадуючись, що десь у темних підземеллях українського фронту сиджу я…</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2599:sogodni-mozhna-povernutysja-do-kyjeva-ale-ne-mozhna-povertatysja-z-frontu&catid=28:tema-nomera-cat&Itemid=2',4880,NULL,1,26),
(4844,'P','2015-01-30',1,NULL,'Rss','Бій під Крутами: відомі й невідомі сторінки','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6363,200,0a288dc.jpg\" />Насправді легендарний бій відбувся не 29, а 30 січня. \"Шкодувати київських мешканців нема чого, вони терпіли гайдамаків – нехай знають нас і одержать відплату. Жодного жалю! Кров’ю заплатять вони нам. Якщо треба, то каменя на камені не залишимо\".</strong></p>\n<p>Мало яка інша подія в історії України є настільки ж відомою, як бій під Крутами. Він посідає чільне місце в національній історичній пам’яті, проте в історіографії досі немає цілісного опису крутянських подій.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2600:bi-pid-krutamy-vidomi--nevidomi-storinky&catid=28:tema-nomera-cat&Itemid=2',4881,NULL,1,26),
(4845,'P','2015-01-30',1,NULL,'Rss','Нерукотворний образ Ісуса Христа','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6364,200,popeGETTY01090.jpg\" style=\"margin: 10px; float: left;\" />30 січня 2015 році Блаженніший Святослав, Глава і Отець УГКЦ, у співслужінні з владиками Синоду УГКЦ відслужив Вечірню в архикатедральному соборі Святого Юра, після якої відкрив для привселюдного почитання Нерукотворний образ Ісуса Христа.</strong></p>\n<p>На початку Богослужіння Першоієрарх Церкви наголосив на тому, що &nbsp;сьогодні українцям випала унікальна можливість – «побачити Боже обличчя». За його словами, Нерукотворний образ Ісуса Христа Ісуса - унікальна реліквія часів земного життя Спасителя: «Ісус Христос взяв особисто в руки цей обрус і притулив до свого обличчя. Тому він називається нерукотворним. Цим обрусом він зцілив Ефеського царя від прокази».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2601:nerukotvornyq-obraz-isusa-hrysta&catid=28:tema-nomera-cat&Itemid=2',4882,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4846,'P','2015-01-31',1,NULL,'Rss','«Найгірший вірус сьогодення – хабарництво і дволикість»','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6365,200,12.jpg\" style=\"margin: 10px; float: right;\" />Капелан Майдану, богослов, отець-доктор Михайло Димид у співавторстві з дочкою Климентією видав книгу-фотоальбом</strong><strong> </strong><strong><a href=\"http://www.credo-ua.org/2014/12/127071\">«Каміння Майдану»</a>. Про Майдан отець може говорити годинами. Подаємо кілька найцікавіших цитат о. Михайла з презентації книги, що відбулася у Тернополі.</strong></p>\n<p><strong>Майдан показав мені справжню українську молодь</strong>, яка прагнула жити по-іншому і відстояла свій вибір. Я побачив справжніх чоловіків, готових брати відповідальність…», «Майдан — це не тільки велика географічна, а й космічна територія. Це люди, які почули голос Божий і пішли за ним…»,</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2602:2015-01-30-20-24-47&catid=28:tema-nomera-cat&Itemid=2',4884,NULL,1,26),
(4847,'P','2015-01-30',1,NULL,'Note','На Аскольдовій могилі вшанували пам\'ять героїв Крут','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em><strong><img style=\"float: left;\" src=\"image,6366,200,P1290653.jpg\" alt=\"\" width=\"200\" height=\"150\" /></strong></em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">У&nbsp;<strong>Києві</strong>&nbsp;річницю подвигу Героїв Крут відзначили президент Петро Порошенко, члени уряду та громадськість.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Біля пам&rsquo;ятного хреста на Аскольдовій могилі в Києві, де поховані кілька загиблих учасників бою, відбулися панахида й урочистий мітинг.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&laquo;Суворим повчальним прикладом&raquo; назвав президент героїзм захисників станції Крути на Чернігівщині, значна частина яких загинула, але ціною життя вповільнила наступ більшовиків.</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>','<p class=\"article-lead\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6366,200,P1290653.jpg\" alt=\"\" width=\"200\" height=\"150\" /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">По всій Україні в четвер відбулися акції вшанування подвигу Героїв Крут &ndash; учасників нерівного бою захисників України від більшовицьких загарбників, що відбувся 29 січня 1918 року.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Зокрема, в&nbsp;<strong>Києві</strong>&nbsp;річницю подвигу Героїв Крут відзначили президент Петро Порошенко, члени уряду та громадськість.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Біля пам&rsquo;ятного хреста на Аскольдовій могилі в Києві, де поховані кілька загиблих учасників бою, відбулися панахида й урочистий мітинг.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&laquo;Суворим повчальним прикладом&raquo; назвав президент героїзм захисників станції Крути на Чернігівщині, значна частина яких загинула, але ціною життя вповільнила наступ більшовиків.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">У&nbsp;<strong>Харкові</strong>&nbsp;пам&rsquo;ять загиблих у боях під Крутами студентів вшанували під звуки ліри.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Близько півсотні харків&rsquo;ян біля пам&rsquo;ятного знаку на честь проголошення Декларації про державний суверенітет України в саду імені Шевченка виклали зі свічок напис &laquo;Героям Крут&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">http://www.radiosvoboda.org/</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">фото Ігоря Голованьова</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;<a style=\"text-indent: 0em; font-size: 0.625em; line-height: 1.3em;\" href=\"https://www.facebook.com/media/set/?set=a.920208144678467.1073741901.198406070192015&amp;type=1\">https://www.facebook.com/media/set/?set=a.920208144678467.1073741901.198406070192015&amp;type=1</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>',NULL,NULL,4883,5,1,45);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4848,'P','2015-01-31',1,NULL,'Rss','Євген Сверстюк: «Мало самого таланту: треба мати український хребет»','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6367,200,Sverstyuk.jpg\" />Людина приходить у світ, щоб виповнити його таїною своєї присутності. Таїною, яка помножує радість, мудрість та світлість Божого задуму. Розмову про Євгена Сверстюка слід провадити спокійно, поза пафосом і дифірамбами. Він був людиною, умудреною життям, відчуттям власної міри присутності у світі.</strong></p>\n<p>Уже нині приходить розуміння того, що він єдиний виповнював цілий пласт культури, такої потрібної за теперішніх умов, розуміючи необхідність культивування й збереження духовних цінностей. Жодна його публікація не лишалася поза увагою публіки. Його любили, його не любили, але читали всі й усі коментували. Його книги розходилися блискавично й відразу ставали бібліографічною рідкістю.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2603:2015-01-31-18-43-04&catid=28:tema-nomera-cat&Itemid=2',4885,NULL,1,26),
(4849,'P','2015-02-02',1,NULL,'Rss','Я – КАПЕЛАН…','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6368,200,1039373.jpg\" />Засніжена дорога, промерзле повітря. У рясі та бронежилеті із Святими Тайнами у кишені біля серця прямую до позиції, де очікують Христа: хлопці попросили про Сповідь і Причастя. Звуки пострілів стають усе ближчими, кулі починають свистіти зовсім поруч. Можливість повернутися залишається, але ж очікують – Христа!.. Один із найщасливіших моментів мого священства: добровільно і свідомо, із спокійною певністю, приймаю рішення продовжувати шлях, адже я – для Нього, а значить – для них… і немає мене без мого служіння…</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2604:ja--kapelan&catid=28:tema-nomera-cat&Itemid=2',4886,NULL,1,26),
(4850,'P','2015-02-02',1,NULL,'Rss','Ансамбль «Аніма» відвідав в\'язнів на Київщині','<p><img src=\"http://askoldova-mohyla.org/uk/image,6368,200,DSC_0090.JPG\" style=\"margin: 10px; float: left;\" />31 січня 2015 року Жіночий вокальний ансамбль «Аніма», що при церкві св.&nbsp;Миколая Чудотворця на Аскольдовій Могилі під керівництвом Катерини Чаплик, здійснив місійну подорож до виправних уставнов, розташованих на Києвщині. Колектив у складі Наталії Грищенко, Наталії Мальцевої, Роксолани Шпіци, Анастасії Друзюк, Катерини Чаплик, Мирослави Кирилюк, Олени Сакалюк відвідав Березанську (№ 95) і Бориспільську (№ 119) виправні колонії Управління ДПтС України у Києві та Київській області, де відбувають чоловіки. Групу супроводив капелан о. Костянтин Пантелей, керівник Відділу Курії УГКЦ з душпастирства у пенітенціарній системі України.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2605:2015-02-02-18-02-05&catid=25:parafija-cat&Itemid=27',4887,NULL,1,26),
(4851,'P','2015-02-03',1,NULL,'Rss','ОКСЕН ЛІСОВИЙ: ДІТЯМ НА СХОДІ ТРЕБА ПОКАЗУВАТИ, ЯКІ МОЖЛИВОСТІ ДАЄ Україна','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6368,200,origin_hol301414oy.JPG\" />Розмова з директором Національного центру «Мала академія наук України» розпочалася не зі стандартних запитань. «ОУ» завітала до Оксена Васильовича у той момент, коли він переглядав фотографії, зроблені під час поїздки на схід нашої країни. На світлинах – школи з вибитими вікнами і посіченими «Градами» стінами, юні, але вже&nbsp;такі серйозні обличчя хлопців і дівчат. А ось – діти зібралися в актовому залі послухати не лише про потенціал, який може дати наука, а й про власний вибір і свою Батьківщину.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2606:oksen-lisovy-ditjam-na-shodi-treba-pokazuvaty-jaki-mozhlyvosti-daje-ukrajina&catid=37:spilnota-cat&Itemid=49',4888,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4852,'P','2015-02-03',1,NULL,'Rss','Віра, Надія, Любов…','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6371,200,Vira-1024x682.jpg\" />Зовсім випадково сьогодні натрапив на три свої короткі тексти, виголошені у радіоефірі штабу АТО (АТЦ), Краматорськ, у вересні 2014 р. Іще тоді, коли живим був Вовчик (на фото ліворуч), який нещодавно загинув під Донецьким аеропортом…</p>\n<p><strong>ВІРА</strong><br /> Вона дозволяє стояти впевнено обома ногами на твердому ґрунті дійсності. Вона дає можливість відважно, крок за кроком, прямувати у напрямку досягнення остаточної мети. Вона дарує людині здатність бачити сяйво перемоги навіть у густих сутінках конкретної поразки.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2607:vira-nadija-ljubov&catid=28:tema-nomera-cat&Itemid=2',4889,NULL,1,26),
(4853,'P','2015-02-04',1,NULL,'Rss','Папа про війну в Україні: задумайтесь - це ж війна між християнами','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6372,200,ansa734213_articolo.jpg\" />«Моя думка знову лине до улюбленого українського народу, – сказав Папа Франциск наприкінці загальної аудієнції у середу, 4 лютого 2015 року. – На жаль, ситуація погіршується та посилюється протистояння між сторонами. Молімося, насамперед, за жертви, між якими дуже багато цивільних, та за їхні родини, і благаймо у Господа Бога, щоб якнайшвидше припинилося це жахливе братовбивче насильство. Знову звертаюся з наполегливим закликом докладати всі зусилля, також і на міжнародному рівні, для відновлення діалогу – єдиного можливого шляху для повернення миру і згоди на цю багатостраждальну землю».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2608:papa-pro-vinu-v-ukrajini-zadumates-ce-zh-vina-mizh-hrystyjanamy&catid=32:gk-svit-cat&Itemid=58',4890,NULL,1,26),
(4854,'P','2015-02-04',1,NULL,'Rss','Розповідь – спогади про поранених Героїв','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6373,200,1409720881-9162-ukraina.jpg\" />Ми, сестра Василія, згромадження редемптористів, Василина та Марія Томишинець, парафіянки церкви св. Миколая Мирлікійського на Аскольдовій Могилі, відвідуємо поранених наших Герої у Київському клінічному військовому госпіталі на Печерську.</p>\n<p>Хочемо поділитись із Вами свідченнями Героїв, про Божу присутність під час перебування на війні, незламну Віру, справжній героїзм, Любов до Батьківщини та своїх побратимів. Ці свідчення скупі, без красномовства, але настільки глибокі і проникливі, що цю присутність Бога бачиш у їхніх очах, серцях, душах.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2609:rozpovid--spogady-pro-poranenyh-gerojiv&catid=29:propovidi-cat&Itemid=34',4891,NULL,1,26),
(4855,'P','2015-02-05',1,NULL,'Rss','Гаврилишин: За два роки в Україні почнеться економічне диво','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6374,200,539509.jpg\" />На думку одного з найвпливовіших українців світу Богдана Гаврилишина, Україна не лише стоїть в авангарді Європи, вона може стати прикладом для наслідування для усього світу.</p>\n<p>Про те, на чому ґрунтується його оптимізм, радник трьох українських президентів, економіст, фундатор економічного форуму в Давосі, член ініціативи “Першого грудня” розповів в інтерв’ю сайту&nbsp;<a href=\"http://24tv.ua/\" target=\"_blank\">“24?</a>.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2610:gavrylyshyn-za-dva-roky-v-ukrajini-pochnetsja-ekonomichne-dyvo&catid=28:tema-nomera-cat&Itemid=2',4892,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4856,'P','2015-02-07',1,NULL,'Rss','Душпастирство в умовах війни','<p><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6375,200,02_gallery.jpg\" />\n<div style=\"text-align: center;\"><em style=\"font-size: 0.875em;\"><strong>Дорогі співбрати у Христовому священстві!</strong></em></div>\n</em></p>\n<p>Протягом останнього року в душпастирській практиці перед нами часто постають виклики і запитання, з якими ми досі не мали справи: масові смерті активістів на Майдані, війна на Сході України, атмосфера тривоги, непевності та страху, а відтак – симптоми депресії і зневіри в суспільстві. Проте водночас простежуємо в українському народі потужне автентичне пробудження людського духу і солідарності, бажання працювати заради загального добра, вияви гідної подиву самоорганізації та жертовності.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2611:dushpastyrstvo-v-umovah-viny&catid=28:tema-nomera-cat&Itemid=2',4893,NULL,1,26),
(4857,'P','2015-02-08',1,NULL,'Rss','Лист до доньки','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6376,200,d1675892875.jpg\" />Ти знову це зробила. Десь поміж 3 і 4 ранку незаперечно проголосила кінець ночі. Інколи хотілось би повернутися на бік, затулити голову подушкою і заперечити реальність. Але чомусь саме тоді ти особливо домагаєшся моєї присутності. А&nbsp;я&nbsp;— твій Тато.</strong></p>\n<p>Майже щоночі переконуєш мене, що кількість способів описання холодильника, пральної машини і кухонного начиння&nbsp;— невичерпна. Дивишся, перевісившись через руку, а я розповідаю, чекаючи на перші ознаки твоєї втоми. Коли ти вже засинаєш, постійно на руках, я усвідомлюю, яке ж прекрасне наше життя.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2612:lyst-do-donky&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4894,NULL,1,26),
(4858,'P','2015-02-08',1,NULL,'Rss','Приготування до Великого Посту','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6377,200,Fotoli.jpg\" />Великий піст попереджують підготовчі неділі і седмиці. Служби підготовчих неділь і самого Великого посту містяться в Посній Тріоді. Вона починається від Неділі про Митаря і Фарисея і закінчується Великою суботою, обіймаючи 70-денний період.</p>\n<p>Передують Великому постові – святій Чотиридесятниці – Неділя про Митаря і Фарисея, Неділя і седмиця про Блудного сина, Неділя і седмиця М’ясопусна (м’ясо-пуст), Неділя і седмиця Сиропусна (сиро-пуст, сирна, масляна).</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2613:prygotuvannja-do-velykogo-postu&catid=28:tema-nomera-cat&Itemid=2',4895,NULL,1,26),
(4859,'P','2015-02-04',1,NULL,'Rss','Розповідь – спогади про поранених Героїв','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6373,200,1409720881-9162-ukraina.jpg\" />Ми, сестра Василія, згромадження василіянок, Провінція Святого архистратига Михаїла, Василина та Марія Томишинець, парафіянки церкви св. Миколая Мирлікійського на Аскольдовій Могилі, відвідуємо поранених наших Герої у Київському клінічному військовому госпіталі на Печерську.</p>\n<p>Хочемо поділитись із Вами свідченнями Героїв, про Божу присутність під час перебування на війні, незламну Віру, справжній героїзм, Любов до Батьківщини та своїх побратимів. Ці свідчення скупі, без красномовства, але настільки глибокі і проникливі, що цю присутність Бога бачиш у їхніх очах, серцях, душах.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2609:rozpovid-spogady-pro-poranenyh-gerojiv&catid=29:propovidi-cat&Itemid=34',4896,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4860,'P','2015-02-09',1,NULL,'Rss','Щораз повніша чаша Надії Савченко','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6378,200,12255.jpg\" />Ніхто з нас не знає, як обирає Господь людей для реалізації Свого плану для світу. Це тайна, яка не має раціональних земних пояснень. Проте схоже, що вступним тестом для такого обранця є готовність витримати великі страждання. Причому не просто витримати, а надати їм високого сенсу – так, щоб від них почали вібрувати серця мільйонів.</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2614:shchoraz-povnisha-chasha-nadiji-savchenko&catid=28:tema-nomera-cat&Itemid=2',4897,NULL,1,26),
(4861,'P','2015-02-09',1,NULL,'Note','Парафія Пресвятої Трійці на Оболоні запрошує мешканців свого району на недільні богослужіння','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Щонеділі початок Святої Літургії&nbsp; на 13.00 за адресою: вул. Героїв Дніпра 34 (цокольне приміщення Абсолюту. Вхід з лівої сторони. 100 метрів від метро Героїв Дніпра.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Щонеділі початок Святої Літургії&nbsp; на 13.00 за адресою: вул. Героїв Дніпра 34 &nbsp;(цокольне приміщення Абсолюту. Вхід з лівої сторони. 100 метрів від метро Героїв Дніпра.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><span style=\"text-decoration: underline;\">Детальніша інформація за номером:</span></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">0679378603 &ndash; отець Дамян Габорій (адміністратор парафії);</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">0679692206 &ndash; п. Мовчан Василь Михайлович (голова парафіяльної ради);</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Фейсбук: Парафія Пресвятої Трійці УГКЦ на Оболоні;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Mail: obolon.ugcc@gmail.com</span></p>',NULL,NULL,4903,5,1,42),
(4862,'P','2015-02-11',1,NULL,'Rss','Верховна Рада України прийняла за основу проект закону про капеланство в тюрмах','<p align=\"center\"><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6386,200,Chaplain_Paul_Manley.jpg\" />11 лютого 2015 року Верховна Рада України прийняла за основу проект закону </strong><a href=\"http://w1.c1.rada.gov.ua/pls/zweb2/webproc4_1?pf3511=52591\">№1154 \"Про внесення змін до деяких законодавчих актів України (щодо регулювання діяльності капеланів в органах та установах, що належать до сфери управління Державної пенітенціарної служби України)</a></p>\n<p>Законопроектом запроваджується інститут <em>\"священнослужителів (капеланів)\"</em> в органах та установах, що належать до сфери управління Державної пенітенціарної служби України з метою здійснення \"душпастирської опіки\" в даних органах та установах.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2615:verhovna-rada-ukrajiny-prynjala-za-osnovu-proekt-zakonu-pro-kapelanstvo-v-tjurmah&catid=28:tema-nomera-cat&Itemid=2',4904,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4863,'P','2015-02-12',1,NULL,'Rss',' «Молодь змінить Україну» – Богдан Гаврилишин','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6387,200,F720A1DC.jpg\" style=\"margin: 10px; float: right;\" />З самого ранку в кабінеті Богдана Гаврилишина гості. Це група молодих хлопців і дівчат, що прийшли до нього на зустріч перед своєю навчальною поїздкою в Австрію. Відомий економіст, меценат, пластун дає поради на що звернути увагу і який досвід вони обов’язково повинні перейняти. Він вірить, що молодь змінить Україну. </strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2616:2015-02-12-11-57-01&catid=37:spilnota-cat&Itemid=49',4905,NULL,1,26),
(4864,'P','2015-02-04',1,NULL,'Note','ЗВІТ №16 про роботу служби милосердя «Карітас» при парафії св.Миколая Чудотворця на Аскольдовій могилі (жовтень 2014 р.)','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Крім того, за пожертви парафіян надано допомогу медикаментами п.Олександру С., ураженому інсультом, який перебував на стаціонарному лікуванні в клінічній лікарні №15 м.Києва.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">В рамках проекту служіння жінці в тюрмі відправлено 5 (п&rsquo;ять) посилок з медикаментами та засобами гігієни ув&rsquo;язненим жінкам.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Служба милосердя &laquo;Карітас&raquo; при церкві св.Миколая Чудотворця на Аскольдовій могилі, зібравши пожертвувані парафіянами одяг чоловічий, жіночий та дитячий, взуття, постільну та кухонну білизну, іграшки дитячі та отримавши каву натуральну мелену в гуманітарній допомозі від БФ &laquo;Тріумф Серця&raquo;, розподілила їх таким чином (див.записи в книзі розподілу гуманітарної допомоги):</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Уділили потребуючим мирянам:</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг &ndash; 56 кг</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;взуття &ndash; 19 пар</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Надана допомога одягом та взуттям:</span></li>\r\n</ol>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">переселенцям з Донецької та Луганської обл.</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг &ndash; 91 кг.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;взуття &ndash; 5 пар</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">багатодітній родині (6 дітей) з с.Угнів Львівської обл.</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг &ndash; 30 кг.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;взуття &ndash; 8 пар</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">потребуючим з різних регіонів України та з Придністров&rsquo;я</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг &ndash; 90 кг.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;взуття &ndash; 2 пари</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Передали для жінок-бійців АТО</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;комбінезони &ndash; 3 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;каска &ndash; 1 шт.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Відправили гуманітарний вантаж Благодійному фонду &ldquo;Сузір\'я&rdquo; в с.Іванкові Бориспільського р-ну Київської обл.:</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг чоловічий, жіночий, дитячий &ndash; 15 мішків+4 коробки</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;взуття &ndash; 1 мішок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;іграшки дитячі &ndash; 1 мішок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Роздано каву мирянам та переселенцям із зони АТО &ndash; 1 мішок.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Крім того, за пожертви парафіян надано допомогу медикаментами п.Олександру С., ураженому інсультом, який перебував на стаціонарному лікуванні в клінічній лікарні №15 м.Києва.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">В рамках проекту служіння жінці в тюрмі відправлено 5 (п&rsquo;ять) посилок з медикаментами та засобами гігієни ув&rsquo;язненим жінкам.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Робота виконана силами волонтерів.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Висловлюємо щиру подяку жертводавцям і прикликаємо Боже благословення на їх родини.</span></p>',NULL,NULL,4899,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4865,'P','2015-02-07',1,NULL,'Note','ЗВІТ №18 про роботу служби милосердя «Карітас» при парафії св.Миколая Чудотворця на Аскольдовій могилі (листопад 2014 р.)','<ul>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Пораненим бійцям АТО до військового шпиталю в м.Києві:</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;светри теплі &ndash; 10 шт.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Передали для населення в зону АТО через волонтерів:</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг теплий дитячий &ndash; 5 коробок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг жіночий, чоловічий &ndash; 3 кор.+6 міш.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Служба милосердя &laquo;Карітас&raquo; при церкві св.Миколая Чудотворця на Аскольдовій могилі, зібравши пожертвувані парафіянами одяг чоловічий, жіночий та дитячий, взуття, іграшки дитячі та отримавши &nbsp;гуманітарну допомогу одягом від благодійного фонду &laquo;Міжнародна асоціація підтримки України (МАПУ)&raquo;, (голова п.Ольга Чуйко-Дурбак), розподілила їх таким чином (див. записи в книзі розподілу гуманітарної допомоги):</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Уділили потребуючим мирянам:</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash; 70 кг</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;куртки та пальта жіночі й чоловічі &ndash; 54 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;сервіз чайний на 6 персон &ndash; 1 компл.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; тарілки пластикові &ndash; 6 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; рюкзак шкільний &ndash; 1 шт.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Надана допомога одягом та взуттям:</span></li>\r\n</ol>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">переселенцям з Донецької та Луганської обл.</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг &ndash; 15 кг.</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">багатодітній родині (6 дітей) з с.Угнів Львівської обл.</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;куртки дитячі &ndash; 4 шт.</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">підліткам Лук&rsquo;янівського СІЗО в м.Києві:</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; светри теплі &ndash; 17 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;джинси &ndash; 5 пар, шкарпетки</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">пораненим бійцям АТО до військового шпиталю в м.Києві:</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;светри теплі &ndash; 10 шт.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Передали для населення в зону АТО через волонтерів:</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг теплий дитячий &ndash; 5 коробок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг жіночий, чоловічий &ndash; 3 кор.+6 міш.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Відправили гуманітарний вантаж Благодійному фонду &ldquo;Сузір\'я&rdquo; в с.Іванкові Бориспільського р-ну Київської обл.:</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг чоловічий, жіночий &ndash; 9 мішків</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; іграшки дитячі &ndash; 1 мішок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Роздано каву мирянам та переселенцям із зони АТО &ndash; 0,5 мішка.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">В рамках проекту служіння жінці в тюрмі відправлено 6 (шість) посилок ув&rsquo;язненим жінкам з засобами гігієни,одягом та духовною літературою (див.звіт №17 від 29.11.2014 р.).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Робота виконана силами волонтерів.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Висловлюємо щиру подяку жертводавцям і прикликаємо Боже благословення на їх родини.</span></p>',NULL,NULL,4901,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4866,'P','2015-02-08',1,NULL,'Note','ЗВІТ №19 про роботу служби милосердя «Карітас» при парафії св.Миколая Чудотворця на Аскольдовій могилі (грудень 2014 р.)','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;Служба милосердя &laquo;Карітас&raquo; при церкві св.Миколая Чудотворця на Аскольдовій могилі, зібравши пожертвувані парафіянами одяг чоловічий, жіночий та дитячий, взуття, іграшки дитячі та отримавши &nbsp;гуманітарну допомогу від БФ &laquo;Патріоти України&raquo;</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Служба милосердя &laquo;Карітас&raquo; при церкві св.Миколая Чудотворця на Аскольдовій могилі, зібравши пожертвувані парафіянами одяг чоловічий, жіночий та дитячий, взуття, іграшки дитячі та отримавши &nbsp;гуманітарну допомогу від БФ &laquo;Патріоти України&raquo;, розподілила їх таким чином (див.записи в книзі розподілу гуманітарної допомоги):</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Уділили потребуючим мирянам:</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash;53 кг</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;куртки та пальта жіночі й чоловічі &ndash; 7 шт.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Надали допомогу одягом та взуттям:</span></li>\r\n</ol>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">переселенцям з Донецької та Луганської обл.</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; одяг різний &ndash; 22 кг.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; куртки, пальто &ndash; 5 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; взуття &ndash; 2 пари</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">потребуючим безпритульним в м.Києві:</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; одяг різний &ndash; 20 кг.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; ковдра &ndash; 1 шт.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Відправили гуманітарний вантаж Благодійному фонду &ldquo;Сузір\'я&rdquo; в с.Іванкові Бориспільського р-ну Київської обл. (в т.ч.для передачі в зону АТО):</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; одяг чоловічий, жіночий, дитячий &ndash; 14 мішків+8 коробок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; іграшки дитячі &ndash; 1 мішок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; сумки, взуття &ndash; 2 мішки+1 коробка</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; постіль та постільна білизна &ndash; 1 мішок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; системний блок для комп&rsquo;ютера &ndash; 1 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Роздано каву мирянам та переселенцям із зони АТО &ndash; 0,5 мішка.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Передано кухні &laquo;Карітас&raquo; для харчування переселенців із зони АТО та безпритульних:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; макарони &ndash; 1 мішок;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; кава натуральна мелена &ndash; 1 мішок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Робота виконана силами волонтерів.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Висловлюємо щиру подяку жертводавцям і прикликаємо Боже благословення на їх родини.</span></p>',NULL,NULL,4902,5,1,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4867,'P','2015-02-12',1,NULL,'Rss','«Священик не може бути активним учасником бойових дій»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6388,200,hram-ato.jpg\" />Покликання духовної особи полягає винятково в духовній, релігійній площині. Священик не може бути активним учасником бойових дій, не може використовувати зброї, пам’ятаючи, що священича зброя – насамперед духовна: «…правда, справедливість, ревне проповідування Євангелія миру, непохитна як щит віра і меч духовний, тобто Слово Боже» (пор. Еф. 6, 13–17).\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2617:2015-02-12-21-14-03&catid=28:tema-nomera-cat&Itemid=2',4906,NULL,1,26),
(4868,'P','2015-02-13',1,NULL,'Rss','Празник в Київській Трьохсвятительській семінарії','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6389,200,IMG_6778.jpg\" /></p>\n<p>Престольний празник у Київській Трьохсвятительській духовній семінарії зібрав священників, семінаристів, сестер і мирян на Архиєрейську Божественну Літургію яку провадив Блаженіший Святослав у співслужінні Владики Богдана (Дзюраха) і Папського нунція архиєпископа Томаса (Галіксона).</p>\n<p>Також були освячені кімнати для проживання семінаристів. Тут є усі необхідні зручності а головне, що у кімнатах кожен брат має свій робочий стіл для навчання і власну полицю для бібліотеки.</p>\n<p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2618:praznyk-v-kyjivski-trohsvjatytelski-seminariji&catid=27:gk-v-ukrajini-cat&Itemid=57',4907,NULL,1,26),
(4869,'P','2015-03-06',0,NULL,'Note','Проща до Канева','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,6391,200,355.jpg\" alt=\"\" width=\"200\" height=\"199\" />7 березня 2015 р. Б</strong>. відбудеться парафіяльна <strong>проща до Канева.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>виїзд</strong> 7 березня, субота о <strong>08:00</strong> годині ранку з Аскольдової Могили.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Витрати на прощу орієнтовно складатимуть 150 грн. з людини.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Діти покриватимуть половину вартості витрат на особу.</span></p>\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Благословляю п. Оксану Ясніцьку бути організатором та координатором прощі.</span></p>\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">парох Ігор Онишкевич</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>','<p style=\"text-align: center;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Слава Ісусу Христу!</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дорогі парафіяни!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,6391,200,355.jpg\" alt=\"\" width=\"200\" height=\"199\" />7 березня 2015 р. Б</strong>. відбудеться парафіяльна <strong>проща до Канева.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>виїзд</strong> 7 березня, субота о <strong>08:00</strong> годині ранку з Аскольдової Могили.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Витрати на прощу орієнтовно складатимуть 150 грн. з людини.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Діти покриватимуть половину вартості витрат на особу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Благословляю п. Оксану Ясніцьку бути організатором та координатором прощі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">парох Ігор Онишкевич</span></p>',NULL,NULL,4908,5,1,47);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4870,'P','2015-02-14',1,NULL,'Rss','Ми переможемо, коли будемо єдині','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6391,200,30613_1.jpg\" />У церковно-народній пісні співаємо так: \"В єдності сила народу: Боже, нам єдність подай\". Співаємо ці слова від щирого серця. Однак в автора цього матеріалу виникають сумніви, чи ті, хто співає, мають чітке уявлення, що таке єдність народу.</p>\n<p>Не претендуючи на вичерпне пояснення цих слів, хочу все ж таки заохотити віруючих і невіруючих співвітчизників задуматися над їх змістом. Це важливо з двох причин: віруючі українці мають розуміти, про що моляться, а всі громадяни країни повинні знати, що робити, аби здійснити єдність нашого народу.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2619:my-peremozhemo-koly-budemo-jedyni&catid=28:tema-nomera-cat&Itemid=2',4909,NULL,1,26),
(4871,'P','2015-02-15',1,NULL,'Rss','СВІДКИ НАДІЇ: ВИКЛИКИ ТА ЗАПРОШЕННЯ ЧЕРНЕЦТВУ У КОНТЕКСТІ СЬОГОДНІШНЬОЇ СУСПІЛЬНО-ПОЛІТИЧНОЇ КРИЗИ В УКРАЇНІ.','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6391,200,10996366.jpg\" />(Сьогоднішнє звернення до богопосвячених осіб з нагоди професійного свята !!! <em><span style=\"text-decoration: underline;\">smile emoticon </span></em>Львів, 14.02.2015 р. Монастир Св. Онуфрія.)</p>\n<p>Наш Господь, здійснюючи дивне таїнство спасіння людини, не цурається конкретності людського буття, не нехтує обмеженнями людської природи, з любов’ю входить у реальність простору та часу; «існуючи в божій природі,.. применшив себе самого, прийнявши вигляд слуги» (Фил 2, 7), став плоттю людської історії та зробив її своєю власною; «перед віками сущий Бог» (Анафора святого Василія Великого) з’явився у конкретності історичного моменту; «коли настала повнота часів,.. народився від жінки, народився під законом» (Гал 4, 4), у конкретному місці та у конкретній культурі, не нехтуючи нічим людським, аби остаточно переобразити нашу конкретну людяність недосяжною глибиною своєї люблячої премудрості.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2620:svidky-nadiji-vyklyky-ta-zaproshennja-chernectvu-u-konteksti-sogodnishnoji-suspilno-politychnoji-kryzy-v-ukrajini&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4910,NULL,1,26),
(4872,'P','2015-02-15',1,NULL,'Rss','«Стрітенська свічка – знак Христового світла…» – владика Венедикт на Стрітення ГНІХ','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6391,200,1345036212_bible.jpg\" />15 лютого Христова Церква святкує Стрітення ГНІХ. В основу свята лягла подія зустрічі праведника Симеона та пророчиці Анни із Немовлятком Ісусом на сороковий день по Його народженні.</p>\n<p>У нашій церковній традиції цього дня прийнято йти до храму зі свічками та їх там освячувати. Про це розповідає владика Венедикт, голова Патріаршої літургійної комісії УГКЦ.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2621:2015-02-15-19-29-26&catid=28:tema-nomera-cat&Itemid=2',4911,NULL,1,26),
(4873,'P','2015-02-16',1,NULL,'Rss','«Майдан: усна історія» - розмова з Мирославом Поповичем','<p align=\"center\"><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6394,200,maxresdefault.jpg\" />Про централізацію міста </strong></p>\n<p>Як тільки почався Майдан, почуття обов’язку громадянина Поповича зразу ж мене туди привело. Я бував там досить часто упродовж усього періоду Майдану. Ходив майже щоденно: дорога на мою роботу проходить через Майдан.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2622:2015-02-16-13-09-46&catid=37:spilnota-cat&Itemid=49',4912,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4874,'P','2015-02-17',1,NULL,'Rss','«Мир», що вбиває справедливість, та війна, яка стоїть на її захисті?!','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6395,200,339226.jpg\" />Ідеальною формою співжиття між людьми та державами у цьому світі є мирне співіснування. Проте жорстока реальність наполегливо переконує нас, що це практично неможливо. Вся&nbsp; історія людства позначена постійними військовими конфліктами. Блаженний Августин у свій час зазначав, що в цьому порочному світі війна – це реальність неминуча. Він навіть вбачав у ній певного роду Божественне покарання за гріх.</p>\n<p><strong><em>Справедлива війна</em></strong></p>\n<p>Багато осіб як з церковного середовища, так і непрактикуючих чи невіруючих у різні епохи завжди бачили війну як зло, проте також вважали деякі війни виправданими, бо саме завдяки таким війнам, як це не парадоксально звучить, було можливо досягнути миру, відбивши напад агресора, відновивши справедливість та направивши заподіяну шкоду. Такі війни називали «справедливими».</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2623:2015-02-17-11-46-01&catid=28:tema-nomera-cat&Itemid=2',4913,NULL,1,26),
(4875,'P','2015-02-17',1,NULL,'Rss','Йосип Сліпий - голова Української підпільної церкви (частина І)','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6396,200,yos_slip1.jpg\" />Без знання минулого не може бути майбутнього. Ця істина відноситься як до кожної людини на сімейному, родинному рівні, так і до народу взагалі. Так само як долі окремо взятої людини складаються у історію родини, так само вони витворюють історію нації і далі, - всесвітню історію. Важливо, відчитуючи цю історію, добратися до першоджерел, зрозуміти чим керувалося суспільство і кожен його член в тих чи інших життєвих обставинах, щоб взяти собі на озброєння їх геніальні винаходи й здобутки і, навпаки, вберегтися від помилок.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2624:osyp-slipy-golova-ukrajinskoji-pidpilnoji-cerkvy-chastyna-i&catid=35:istorychni-postati-cat&Itemid=48',4914,NULL,1,26),
(4876,'P','2015-02-18',1,NULL,'Rss','Сім шляхів хорошого посту','<p><strong><img src=\"http://askoldova-mohyla.org/uk/image,6397,200,9606.jpg\" style=\"margin: 10px; float: left;\" />Великий Піст&nbsp;— це особливий час покаяння й аскетичних подвигів. Ми стаємо до бою з нашими пристрастями і бажаннями, і, зрештою, з дияволом, який намагається керувати цим світом. Наша мета&nbsp;— набути чисте серце, здатне сприйняти радість свята Воскресіння Господнього, Великодня, найвеличнішого і центрального свята літургійного року.</strong></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2625:sim-shljahiv-horoshogo-postu&catid=30:hrystyjanske-vchennia-cat&Itemid=37',4915,NULL,1,26),
(4877,'P','2015-02-18',1,NULL,'Rss',' Йосип Сліпий - голова Української підпільної церкви (частина ІІ)','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6398,200,4130-11-2.jpg\" />Розділ I. Становлення особи Йосипа Сліпого</strong><strong> </strong></p>\n<p><strong>1.1 Родинне виховання, шкільні роки</strong><strong> </strong></p>\n<p>Блаженніший Кир Йосип Сліпий народився 17 лютого 1892 року в с. Заздрість Теребовлянського повіту. Як він пізніше сам згадує у споминах, записаним його довголітнім помічником вл. Іваном Хомою: \"Моє родинне село називається Заздрістю, кажуть тому, що всі навколишні села заздрили йому багатства і говорили, що воно справді, є оселею, медом і молоком пливучою. Заздрість лежить на високій подільській рівнині між Серетом і Стрипою, близько містечка Струсів, Теребовельського повіту. З неї дуже добре видно Теребовлянські і Струсівські гори, покриті лісами. ”<a href=\"http://www.bestreferat.ru/referat-197752.html#_ftn1\">[1]</a></p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2626:-osyp-slipy-golova-ukrajinskoji-pidpilnoji-cerkvy-chastyna-ii&catid=35:istorychni-postati-cat&Itemid=48',4916,NULL,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4878,'P','2015-02-19',1,NULL,'Rss','Послання Папи Франциска на Великий Піст','<p><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6399,200,46465.jpg\" />Скріпіть серця ваші (Як 5,8)</em></p>\n<p><strong>Дорогі брати і сестри!</strong></p>\n<p>Великий Піст – це час оновлення для Церкви, спільнот і окремих вірян. Однак насамперед це «час благодаті» (пор. 2Кор 6, 2). Господь ніколи не просить нас про щось, чого раніше Сам не дав би нам: «Ми любимо Його, бо Він перший полюбив нас» (1 Йн 4, 19).</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2628:poslannja-papy-francyska-na-velyky-pist&catid=28:tema-nomera-cat&Itemid=2',4917,NULL,1,26),
(4879,'P','2015-02-19',1,NULL,'Rss',' Йосип Сліпий - голова Української підпільної церкви (частина ІІІ)','<p><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6398,200,tumblr_n082sgh6Ez1ruxyxdo1_1280.jpg\" />Розділ I. Становлення особи Йосипа Сліпого</strong></p>\n<p><strong>1.2 Духовні і наукові студії Йосипа Сліпого</strong><strong> </strong></p>\n<p>У людини в житті завжди бувають моменти, коли вона повинна обрати, як дальше їй продовжувати своє життя. І від того вибору залежить як складеться її доля, доля тих людей, що є біля неї. Кожен при такому виборі керується тими, чи іншими критеріями, в залежності від стану і власної душі, і оточуючих людей і, чи не найбільше, того надприродного впливу, про який одні повністю забувають, а інші, якраз навпаки, саме до цього надприродного голосу прислухаються якнайбільше.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2627:-osyp-slipy-golova-ukrajinskoji-pidpilnoji-cerkvy-chastyna-iii&catid=35:istorychni-postati-cat&Itemid=48',4918,NULL,1,26),
(4880,'P','2015-02-20',1,NULL,'Rss','В’ячеслав Брюховецький: «Покоління, що змінить країну, вже приходить»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6402,200,fd60f77-paralax-1.jpg\" />Platfor.ma спільно з&nbsp;<a href=\"http://kmbs.ua/uk\" target=\"_blank\">Києво-Могилянською бізнес-школою</a>започатковує&nbsp;новий спецпроект «Changers» про тих, хто змінює світ довкола себе.&nbsp;Перший наш герой – почесний президент Києво-Могилянської академії, громадський діяч, учасник групи “Першого грудня”, літературознавець та Герой України В’ячеслав Брюховецький, який став головним ініціатором відродження Могилянки як сучасного вищого навчального закладу.Platfor.ma&nbsp;поговорила з ним про те, як реформувати вищу школу, що робити з Донбасом і що Україна може запропонувати світу.\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2629:2015-02-20-10-19-22&catid=28:tema-nomera-cat&Itemid=2',4920,NULL,1,26),
(4881,'N','2015-02-20',1,NULL,'Rss','В’ячеслав Брюховецький: «Покоління, що змінить країну, вже приходить»','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6402,200,fd60f77-paralax-1.jpg\" />Platfor.ma спільно з&nbsp;<a href=\"http://kmbs.ua/uk\" target=\"_blank\">Києво-Могилянською бізнес-школою</a>започатковує&nbsp;новий спецпроект «Changers» про тих, хто змінює світ довкола себе.&nbsp;Перший наш герой – почесний президент Києво-Могилянської академії, громадський діяч, учасник групи “Першого грудня”, літературознавець та Герой України В’ячеслав Брюховецький, який став головним ініціатором відродження Могилянки як сучасного вищого навчального закладу.Platfor.ma&nbsp;поговорила з ним про те, як реформувати вищу школу, що робити з Донбасом і що Україна може запропонувати світу.</p>\n','',1,'http://oranta.org/index.php?option=com_content&view=article&id=2629:2015-02-20-10-19-22&catid=37:spilnota-cat&Itemid=49',4921,NULL,1,26);
