INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_stream_id`, `rss_url`, `old_id`, `author_id`, `locale_id`, `subcategory_id`) VALUES
(4915,'P','2015-03-07',1,NULL,'Rss','Мирослав Маринович: Львів’яни, схаменіться!','<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6438,200,romanovych.jpg\" />Звернення проректора УКУ Мирослава Мариновича щодо спорудження у Львові пам’ятника Митрополитові Андрею Шептицькому.</strong></p>\n<p>Затяті бої, що точаться довкола пам’ятника Митрополитові Андрею у Львові, нагадують мені типову картину у лазареті: забинтований хворий в гарячці зриває з себе пов’язки, не тямлячи, що наносить собі смертельну шкоду.</p>\n',NULL,1,'http://oranta.org/index.php?option=com_content&view=article&id=2659:myroslav-marynovych-lvivjany-shamenitsja&catid=27:gk-v-ukrajini-cat&Itemid=57',4956,NULL,1,26),
