INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5737,'P','2015-11-13',1,NULL,'Rss','Слово про свободу та відповідальність','<p><em><img style=\"margin: 10px; float: right;\" alt=\"3Mj10UeRbm0\" height=\"167\" width=\"250\" src=\"http://oranta.org/images/stories/3Mj10UeRbm0.jpg\" />Євген Сверстюк дуже точно сформулював одну з найсуттєвіших характеристик радянського і пострадянського стану нашого суспільства:</em><em> </em><strong><em>втрата сенсу слів</em></strong><em>. Ідеться про девальвацію слів і понять, про фальш, що стала віддзеркаленням суті тоталітарного режиму і, на жаль, знайшла продовження в Україні олігархічній.</em></p>\n<p><em>Наше звернення про свободу та відповідальність присвячуємо Євгену Сверстюку та адресуємо це звернення всій країні:</em><strong><em> </em></strong><strong><em>пройдене нами всіма і те, що переживаємо зараз, має повернути значення слів, які вкладаємо в Україну</em></strong><em>.</em></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2935:slovo-pro-svobodu-ta-vidpovidalnist&catid=28:tema-nomera-cat&Itemid=2',5280,NULL,1,1,26),
(5738,'P','2015-11-16',1,NULL,'Rss','Історія одного експоната. Посох патріарха','<p><strong><img style=\"margin: 10px; float: left;\" alt=\"posoh2\" height=\"166\" width=\"250\" src=\"http://oranta.org/images/stories/posoh2.jpg\" />Іноді непоказна музейна річ, яка не є ні довершеним мистецьким твором, ані археологічною пам’яткою, може бути частиною великої історії, проливаючи світло на цілу епоху в житті країни й народу в певну добу. До таких знакових епохальних символів, поза всякими сумнівами, належить посох з експозиції у відділі історії Української греко-католицької церкви у Львівському музеї історії релігії. На перший погляд – це звичайнісінький дерев’яний костур, які часто можна побачити в руках літніх людей. Але в часи заборони УГКЦ, у її так званий катакомбний період, ця річ відіграла вельми важливу роль у історії Церкви зокрема й України загалом.</strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2936:istorija-odnogo-eksponata-posoh-patriarha&catid=28:tema-nomera-cat&Itemid=2',5282,NULL,1,1,26),
(5739,'P','2015-11-17',1,NULL,'Rss','Дні Преподобної Матері Йосафати. Початок був звичайний… Йосафата насамперед була дитиною. ','<p><img style=\"margin: 10px; float: right;\" alt=\"61498\" height=\"178\" width=\"250\" src=\"http://oranta.org/images/stories/61498.jpg\" />Ти сотворив моє нутро, ти мене виткав в утробі матері моєї. Хвалю тебе, що сотворив мене так дивно: діла твої предивні, ти душу мою знаєш вельми добре. Псалом 139, 13-14</p>\n<p>Надворі стояла пізня осінь 1869 року. Це було 20 листопада, саме п’ятниця. Ніхто не пам’ятає яка тоді була погода: дощова чи сонячна, можливо, вже й сніжком припорошувало, - а в родині Якова та Ксенії це був справді сонячний, теплий і дуже радісний день. У сім’ї на світ народилася ще одна донечка, якраз перед днем Святого Архистратига Михаїла, тому вирішили, що її хресне ім’я буде Михайлина. Ніхто не знав, ким виросте дівчинка,&nbsp; але всі дуже тішилися, що на землі появилася ще одна людина – дарунок Творця та вияв Його благословення.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2937:dni-prepodobnoji-materi-osafaty-pochatok-buv-zvychany-osafata-nasampered-bula-dytynoju-&catid=35:istorychni-postati-cat&Itemid=48',5283,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5740,'P','2015-11-17',1,NULL,'Rss','Блаженніший Любомир: «Я молюся за Донбас!»','<p><img style=\"margin: 10px; float: left;\" alt=\"30-03-13-1\" height=\"160\" width=\"250\" src=\"http://oranta.org/images/stories/30-03-13-1.jpg\" />Декілька днів тому Блаженніший Любомир дав відповіді на запитання Українського інституту стратегій глобального розвитку та адаптації. Що з цього вийшло ви можете побачити у цій публікації.</p>\n<p><strong>– Доброго дня, Блаженніший! Ми з Вами вже трохи знайомі: Ви колись були в нас у Донецьку, в гостях у Державному інституті інформатики та штучного інтелекту. А нині, після того як ми – ми всі донеччани – переїхали в Київ, ми створили громадську фундацію Український інститут стратегій глобального розвитку та адаптації. Також було започатковано ініціативу «Український консенсус».</strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2938:2015-11-17-19-26-31&catid=28:tema-nomera-cat&Itemid=2',5284,NULL,1,1,26),
(5741,'P','2015-11-17',1,NULL,'Note','Пам\'яті Блаженних Йосафата та Григорія','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6768,200,07.jpg\" alt=\"\" width=\"200\" height=\"112\" />17 листопада 2015 року Божого священики Київського деканату молитвою вшанували пам&rsquo;ять Блаженного Йосафата. На його першому похованні молились також і парафіяни церкви святого Миколая Чудотворця на Аскольдовій Могилі, які опікуються цією могилою.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6768,200,BERLI.jpg\" alt=\"\" width=\"200\" height=\"112\" />17 листопада 2015 року Божого священики Київського деканату молитвою вшанували пам&rsquo;ять Блаженного Йосафата. На його першому похованні молились також і парафіяни церкви святого Миколая Чудотворця на Аскольдовій Могилі, які опікуються цією могилою.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Парафіяльними зусиллями був відновлений Хрест, а також наступного року, з Божої ласки, планують виготовити образ блаженного Йосафата, який і буде встановлений на місці його першого поховання.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6768,200,07.jpg\" alt=\"\" width=\"200\" height=\"112\" />Працівники кладовища села Чапаївка, поблизу Кончи-Заспи, показали архівну книгу реєстрацій поховань де є запис про перепоховання Блаженного Йосафата Крциловського. Також, завжди на цій могилі парафіяни вшановують пам&rsquo;ять Блаженного Григорія Хомишина. Він також помер у Лук&rsquo;янівській тюрмі, проте його поховання досі невідоме.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Блаженні Йосафате та Григорію моліть Бога за нас і за нашу Україну!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ілля, &laquo;Оранта&raquo;</span></p>',NULL,5285,5,1,NULL,17),
(5742,'P','2016-01-13',1,NULL,'Rss','Відомі музиканти про віру, творчість і служіння','<p><img style=\"margin: 10px; float: left;\" alt=\"22-2-696x451\" height=\"162\" width=\"250\" src=\"http://api.oranta.org/images/stories/22-2-696x451.jpg\" />\"У мене немає фанатів, у мене є друзі. Коли ви дивитеся на фанів, як на друзів, тоді кожен об\'єднується для однієї мети, якою є прославлення\". - Кірк Франклін (Kirk Franklin)</p>\n<p>\"Я хочу бути відображенням Бога, як місяць, який, не маючи власного світла, відбиває світло сонця\". - Кріс Томлін (Chris Tomlin)</p>\n<p>\"Одкровення Бога - паливо для вогню нашого поклоніння\". - Метт Редман (Matt Redman)</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=2989:vidomi-muzykanty-pro-viru-tvorchist-i-sluzhinnja&catid=1:mystecka-storinka-cat&Itemid=50',5393,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5743,'P','2015-11-17',1,NULL,'Rss','Віруси, які з’їдають святість','<p><img style=\"margin: 10px; float: right;\" alt=\"a00f7\" height=\"250\" width=\"238\" src=\"http://oranta.org/images/stories/a00f7.jpeg\" />Коли ми чуємо про святість, то уявляємо щось добре, світле, чисте, правильне, але чомусь таке далеке від нашого життя… У Господній молитві ми кожного разу промовляємо: «…нехай буде воля Твоя…», а тим самим беремо на себе певну відповідальність за виконання волі Божої. Це не означає, що наш вільний вибір чимось обмежується, а навпаки, ми ступаємо на дорогу спасіння і просимо Бога допомогти нам в крокуванні нею.</p>\n<p>Якщо ми зустрінемо людину яка кашляє, в якої болить голова і є підвищена температура тіла, то ми розуміємо, що це вже наслідки певного вірусу, якого ми не бачимо, але який руйнує наше здоров’я з середини.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Вірус (з лат.&nbsp;virus&nbsp;- отрута) – це певний мікроорганізм, що спричиняє інфекційне захворювання<a href=\"http://oranta.org/file:///D:/%D0%86%D0%BA%D0%BE%D0%BD%D0%B8/%D0%86%D0%BA%D0%BE%D0%BD%D0%B0%20%D1%85%D1%80%D0%B8%D1%81%D1%82%D0%B8%D1%8F%D0%BD%D1%81%D1%8C%D0%BA%D0%B0/%D0%9B%D0%BE%D1%97%D1%88%20%D0%90._%D0%92%D1%96%D1%80%D1%83%D1%81%D0%B8_rass.docx#_ftn1\">[1]</a>.&nbsp; Якщо порівняти його з гріхом, то останній також виглядає ніби маленьким, непомітним, але є причиною великих упадків та згіршення інших. Вірус – це збудник чогось негативного, що вражає багатьох. У світі новітніх технологій вірус – це програма, що викликає збої&nbsp; в роботі комп’ютера.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2939:virusy-jaki-zjidajut-svjatist&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5286,NULL,1,1,26),
(5744,'N','2015-11-18',1,NULL,'Rss','Дні преподобної матері Йосафати/ Життєвий шлях','<p align=\"right\"><em><img style=\"float: left;\" src=\"http://oranta.org/images/stories/645646.jpg\" alt=\"645646\" width=\"250\" height=\"346\" />Стопи мої твердо тримаються твоєї Стежки, і ноги мої не спіткнулися. <strong>Псалом 17:5</strong></em></p>\r\n<p>Кожна людина має право вибирати свій життєвий шлях. Але як його вибрати і не помилитися? До Неба ведуть різні стежки, але яка твоя? Господь дає людині вибирати у свободі різні життєві дороги: подружжя, самотній стан та чернецтво.</p>\r\n<p>Запитувала Михайлина Бога у молитвах: який шлях її? А Господь, як ми знаємо,ніколи&nbsp; не відкине людину, яка Його шукає. Він відповідав їй через її бажання, поради отця Єремії та обставини життя, які складалися довкола неї. Отож, Михайлина вирішила присвятити своє життя Богу в монастирі, де б вона цілком могла віддаватися молитві.</p>\r\n<p>&nbsp;</p>\r\n<p><strong> ЧЕРНИЦЯ</strong></p>\r\n<p><em>Блаженний спомин юних літ.</em></p>\r\n<p><em>Ця дивна постать... Я не знаю&hellip;</em></p>','','http://oranta.org/index.php?option=com_content&view=article&id=2940:dni-prepodobnoji-materi-osafaty-zhyttjevy-shljah',5287,NULL,1,1,26),
(5745,'P','2015-11-18',1,NULL,'Rss','Дні преподобної матері Йосафати/ Життєвий шлях','<p align=\"right\"><em><img src=\"http://oranta.org/images/stories/645646.jpg\" width=\"181\" height=\"250\" alt=\"645646\" style=\"float: left;\" />Стопи мої твердо тримаються твоєї Стежки, і ноги мої не спіткнулися. <strong>Псалом 17:5</strong></em></p>\n<p>Кожна людина має право вибирати свій життєвий шлях. Але як його вибрати і не помилитися? До Неба ведуть різні стежки, але яка твоя? Господь дає людині вибирати у свободі різні життєві дороги: подружжя, самотній стан та чернецтво.</p>\n<p>Запитувала Михайлина Бога у молитвах: який шлях її? А Господь, як ми знаємо,ніколи&nbsp; не відкине людину, яка Його шукає. Він відповідав їй через її бажання, поради отця Єремії та обставини життя, які складалися довкола неї. Отож, Михайлина вирішила присвятити своє життя Богу в монастирі, де б вона цілком могла віддаватися молитві.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2940:dni-prepodobnoji-materi-osafaty-zhyttjevy-shljah&catid=35:istorychni-postati-cat&Itemid=48',5288,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5746,'P','2015-11-20',1,NULL,'Rss','Собор Архистратига Михаїла','<p><img style=\"margin: 10px; float: right;\" alt=\"arkhistrat\" height=\"112\" width=\"250\" src=\"http://oranta.org/images/stories/arkhistrat.jpg\" />21 листопада християни відзначають день&nbsp;<strong>Собору Архистратига Михаїла та інших Небесних Сил безтілесних</strong>.&nbsp;<strong>Архистратиг Михаїл</strong>, ім’я якого означає «хто, як Бог?» Або «хто рівний Богові?» є вождем Небесних сил, тому і називається архистратигом, тобто воєначальником сил Господніх. Коли одержимий гордістю Денниця повстав проти Бога й потягнув за собою безліч інших духів, тоді святий&nbsp;<strong>архистратиг Михайло</strong>явився захисником Божої слави, закликав інших ангелів повстати проти зла і перебувати в покорі Господу.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2941:sobor-arhystratyga-myhajila&catid=28:tema-nomera-cat&Itemid=2',5289,NULL,1,1,26),
(5747,'P','2015-11-22',1,NULL,'Rss','Любомир Гузар: Головна зброя терористів - це страх. Не треба їх сприймати','<p><img style=\"margin: 10px; float: left;\" alt=\"b04b9f\" height=\"141\" width=\"250\" src=\"http://oranta.org/images/stories/b04b9f.jpg\" />Єпископ УГКЦ, кардинал Католицької церкви Любомир Гузар у програмі \"Вечір з Миколою Княжицьким\" на&nbsp;<a href=\"http://espreso.tv/\" target=\"_blank\">Еспресо.TV</a> - про нещодавно опублікований документ-звернення ініціативи \"1 грудня\", практичні поради владі та народу, а також про те, як подолати тероризм</p>\n<p><strong>- Нещодавно ініціативна група \"1 грудня\" оприлюднила документ, який називається</strong><strong> </strong><strong><a href=\"http://1-12.org.ua/2015/11/13/3640\" target=\"_blank\">\"Слово про свободу та відповідальність\".</a></strong><strong> </strong><strong>Розкажіть, яка мета цього цього документу? Яка його важливість?</strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2943:ljubomyr-guzar-golovna-zbroja-terorystiv-ce-strah-ne-treba-jih-sprymaty&catid=37:spilnota-cat&Itemid=49',5290,NULL,1,1,26),
(5748,'P','2015-11-22',1,NULL,'Rss','Мирослав Попович: Енергія Майдану мала переродитися на щось вагоме, однак процес пішов стандартно','<p><strong><img style=\"margin: 10px; float: right;\" alt=\"5634487851\" height=\"149\" width=\"250\" src=\"http://oranta.org/images/stories/5634487851.jpg\" /></strong>Книгу “Кровавый век”, присвячену ключовим подіям ХХ століття, презентував напередодні у Києві академік, директор Інституту філософії НАН України, учасник ініціативи “Першого грудня” Мирослав Попович. Ця праця є перекладом на російську його “Червоного століття” і адресована,&nbsp;головним чином,&nbsp;російському читачеві. Тиждень тому в стінах того ж Київського національного університету ім. Т.Г.Шевченка група “Першого грудня” представила свій програмний документ “Слово про свободу та відповідальність”, який містить засади взаємної довіри та співпраці між владою і суспільством.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2944:myroslav-popovych-energija-madanu-mala-pererodytysja-na-shchos-vagome-odnak-proces-pishov-standartno&catid=37:spilnota-cat&Itemid=49',5291,NULL,1,1,26),
(5749,'P','2015-11-22',1,NULL,'Rss','Спогади... спогади не для забуття, спогади, що спонукають до дії','<p><img src=\"http://oranta.org/images/stories/bbc367f47b3.jpg\" width=\"250\" height=\"156\" alt=\"bbc367f47b3\" style=\"margin: 10px; float: left;\" />Осінь. Вечір. Падає дощ. Опале листя прилипло до асфальту. Нічні ліхтарі освітлюють вечірній Київ. Я стою біля вікна, тримаючи в руці горнятко теплого чаю і вдивляюсь у сусідні будинки. Майже на кожному з них із декількох вікон тріпоче жовто-блакитний прапор. «Х-ммм, а два роки тому ніхто і не думав про таке. Жили собі всі спокійно, та не знали, що в «клітці» вони»,- думаю собі я. А й справді, ми багато чого пережили…</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2945:spogadyspogady-ne-dlja-zabuttja-spogady-shcho-sponukajut-do-diji&catid=28:tema-nomera-cat&Itemid=2',5292,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5750,'P','2015-11-22',1,NULL,'Note','Пам\'яті героїв','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6776,200,2326587.jpg\" alt=\"\" width=\"200\" height=\"82\" />22 листопада на Аскольдовій Могилі вшанували пам\'ять героїв Небесної Сотні, а також Марка Пославського, який трагічно загинув під Іловайськом. Схоже, Аскольдова Могила стає Пантеоном Українських Героїв.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><span><img style=\"float: right;\" src=\"image,6776,200,2326587.jpg\" alt=\"\" width=\"200\" height=\"82\" />22 листопада на</span>&nbsp;Аскольдовій Могилі вшанували пам\'ять героїв Небесної Сотні, а також Марка Пославського, який трагічно загинув під Іловайськом. Схоже, Аскольдова Могила стає Пантеоном Українських Героїв.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">На мою думку, саме тут можна було б переховати отамана військ УНР Симона Петлюру і Голову ОУН полковника Євгена Коновальця. Не гоже, щоб могили наших героїв залишалися на чужині.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">За повідомленнями: <a href=\"https://www.facebook.com/bogdanchervak\">https://www.facebook.com/bogdanchervak</a></span></p>',NULL,5293,5,1,NULL,17),
(5751,'P','2015-11-23',1,NULL,'Rss','Любомир Гузар: я б дуже хотів, щоб влада почала справді діяти в дусі Майдану','<p><strong><img style=\"margin: 10px; float: right;\" alt=\"d8a0479dbd46\" height=\"161\" width=\"250\" src=\"http://oranta.org/images/stories/d8a0479dbd46.jpg\" />Протягом останніх двох років наша країна живе у постійній напрузі, і серед тривожних новин та заплутаних політичних схем, серед безлічі гасел та криків як ніколи потрібен тверезий і мудрий голос. Тому багато українців уважно прислухаються до тихого голосу Блаженнішого Любомира (Гузара), що відійшов від справ управління Українською Греко-Католицькою Церквою, проте залишається активним громадським лідером. Хоча він сам зазначає, що через проблеми з зором не має можливості детально слідкувати, та для багатьох саме його слова і коментарі часто дають краще розуміння того, що відбувається.</strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2946:ljubomyr-guzar-ja-b-duzhe-hotiv-shchob-vlada-pochala-spravdi-dijaty-v-dusi-madanu&catid=28:tema-nomera-cat&Itemid=2',5294,NULL,1,1,26),
(5752,'P','2015-12-01',1,NULL,'Rss','«Повернення»','<p><img src=\"http://oranta.org/images/stories/654321.jpg\" width=\"250\" height=\"138\" alt=\"654321\" style=\"margin: 10px; float: right;\" />29 листопада 2015 року у церкві св. Миколая Чудотворця Мирликійського на Аскольдовій могилі відбулася презентація книги українського дисидента, багаторічного політв’язня радянських таборів, письменника Миколи Горбаля «ПОВЕРНЕННЯ» (Харків: «Права людини», 2014).</p>\n<p>Микола Горбаль народився 1940 року на Лемківщині. У п’ятирічному віці разом із родиною розділив трагічну долю лемків – переселення з рідної землі. Вертаючись на батьківщину, сім’я оселилася на Тернопільщині.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=2955:2015-12-01-19-33-17&catid=25:parafija-cat&Itemid=27',5305,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5753,'P','2015-11-23',1,NULL,'Rss','94 дні Гідності ','<p><img src=\"http://oranta.org/images/stories/383896492.jpg\" width=\"250\" height=\"166\" alt=\"383896492\" style=\"margin: 10px; float: left;\" />«Зустрічаємось о 22:30 під монументом Незалежності. Вдягайтесь тепло, беріть парасолі, чай, каву, хороший настрій та друзів», — із цього повідомлення журналіста Мустафи Найєма у «Фейсбуці» два роки тому розпочався Євромайдан. Тієї ночі у центрі української столиці зібралося близько тисячі людей, а вже наступного дня подібні акції пройшли чи не в кожному обласному центрі країни.</p>\n<p>У перші дні студентських протестів навряд чи хтось з учасників акцій міг уявити події наступних трьох місяців: побиття студентів, сутички на Банковій та Грушевського, кілька спроб штурму Майдану, перші смерті мирних активістів, розстріли на Інститутській та втечу Януковича. Учасники Революції гідності у розмові з «УМ» згадали про важливі для них події протягом 94 днів протесту.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2942:94-dni-gidnosti-&catid=28:tema-nomera-cat&Itemid=2',5295,NULL,1,1,26),
(5754,'P','2015-11-24',1,NULL,'Rss','«Нас Люблять!»','<p align=\"right\"><em><img style=\"margin: 10px; float: right;\" alt=\"1216986\" height=\"150\" width=\"250\" src=\"http://oranta.org/images/stories/1216986.jpg\" />«Несіть хворим Бога. Це матиме більшу вартість, ніж будь-яка інша послуга». Святий о. Піо.</em></p>\n<p>Коли ми провідували і вітали наших Захисників, зустріли Олександра, який проходив курс лікування після порання. Де Він був поранений, яке у нього поранення навіть не пригадуємо, та нас вразило найбільше те, як Бог своєю рукою веде саме до тих хлопців яким найбільше потрібна проста, щира розмова, інколи навтіь без запитань.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2947:2015-11-24-10-16-36&catid=25:parafija-cat&Itemid=27',5296,NULL,1,1,26),
(5755,'P','2015-11-25',1,NULL,'Rss','«Працюйте люди, змагайтеся, старайтеся – і все буде на місці»','<p><img style=\"margin: 10px; float: left;\" alt=\"na_majdani11\" height=\"215\" width=\"250\" src=\"http://oranta.org/images/stories/na_majdani11.jpg\" />Що більшою є людина, то простіший у неї кабінет. Про це я подумала, коли розглядала кабінет Блаженнішого Любомира (Гузара). Ікона Плащаниці, кілька ікон Божої Матері Милування та Спасителя, стиль ХVII століття, але якась добра сучасна іконописна школа. Ще є маленький портрет митрополита Андрея Шептицького. Одне велике вікно, два столи й ходунок, який допомагає йому пересуватися. Не знаю, як Блаженніщому Любомиру вдавалося долати сходинки до сцени Майдану, але вітали його дуже радісно.<strong></strong></p>\n<p><strong>– Ваше Блаженство, а пам’ятаєте, як ви два роки тому прийшли на Майдан і як вас вітали багато тисяч людей?</strong></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2948:2015-11-25-12-56-13&catid=28:tema-nomera-cat&Itemid=2',5297,NULL,1,1,26),
(5756,'P','2015-11-26',1,NULL,'Rss','Андрей Шептицький – спогади рабина Давида Кахане','<p><strong><img style=\"margin: 10px; float: right;\" alt=\"lvov14b\" height=\"190\" width=\"250\" src=\"http://oranta.org/images/stories/lvov14b.jpg\" />Уродженець містечка Гримайлів, що на Тернопільщині, Давид син Шломо Кахане оселився у Львові в 1929 р. У той час викладав релігію у польських державних та єврейських приватних школах.</strong></p>\n<p>У 1930 році був обраний рабином прогресистської синагоги в центральному єврейському кварталі Львова, згодом очолив Науковий інститут вивчення ТаНаХу. У квітні 1936 року правління Львівської єврейської релігійної громади звернулось до міністерства військових справ Польщі з проханням призначити Давида Кахане помічником рабина для вояків-євреїв Львівського гарнізону.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2949:andre-sheptycky--spogady-rabyna-davyda-kahane&catid=35:istorychni-postati-cat&Itemid=48',5298,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5757,'P','2015-11-26',1,NULL,'Rss','Смерть через голод змінює генотип народу','<p><img style=\"margin: 10px; float: left;\" alt=\"980555F\" height=\"141\" width=\"250\" src=\"http://oranta.org/images/stories/980555F.jpg\" />Страх померти голодною смертю призвів до того, що в Україні досі чимало тих, хто у дилемі «хліб чи свобода» обере хліб.</p>\n<p>У червні 1933 року в Україні від голоду помирали: щохвилини – понад двадцять осіб, щогодини – більш як тисяча чотириста людей, і щодня – тридцять чотири з половиною тисячі українців. Ці дані, наведені Інститутом національної пам’яті України, – лише видима частина трагедії українського народу під назвою Голодомор.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2950:smert-cherez-golod-zminjuje-genotyp-narodu&catid=28:tema-nomera-cat&Itemid=2',5299,NULL,1,1,26),
(5758,'P','2015-11-26',1,NULL,'Rss','Послання з нагоди проголошення Святого року Божого милосердя в УГКЦ','<p><img style=\"margin: 10px; float: right;\" alt=\"new_zhezl_0\" height=\"173\" width=\"250\" src=\"http://oranta.org/images/stories/new_zhezl_0.jpg\" />Дорогі в Христі! Разом з усією Вселенською Церквою входимо в благодатний час особливого Святого року, який, за волею Святішого Отця Франциска, триватиме від 8 грудня 2015 року до 26 листопада 2016 року. Цей Рік, який називаємо теж Надзвичайним ювілейним роком, Папа присвятив Божому милосердю. Це час особливих Божих благодатей, пов’язаних із паломництвами до визначних катедральних соборів у всій нашій помісній і Вселенській Церкві, час зцілення ран душі й тіла, повернення до Бога, віднайдення Джерела милосердя. Стоячи на порозі цього Ювілейного року, задумаймося над тим, як нам прийняти Боже милосердя, ним жити і про нього свідчити, щоб ми самі, наші ближні та весь наш народ могли якнайбільше скористати з духовних плодів цього благодатного часу.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2951:poslannja-z-nagody-progoloshennja-svjatogo-roku-bozhogo-myloserdja-v-ugkc&catid=27:gk-v-ukrajini-cat&Itemid=57',5300,NULL,1,1,26),
(5759,'P','2015-11-27',1,NULL,'Rss','Час постити: 28 листопада початок Різдвяного посту','<p><img style=\"margin: 10px; float: left;\" alt=\"20151\" height=\"182\" width=\"250\" src=\"http://oranta.org/images/stories/20151.jpg\" />28 листопада – початок Рiздвяного посту. Його ще називають Пилипiвкою. Звiдки ця назва? Різдвяний піст – один з чотирьох багатоденних постів церковного року. Розпочинається він завжди в один час – у день святого апостола Пилипа (Филипа) (15) 28 листопада, та триває сорок днів до самого Різдва – (24 грудня)- 6 січня. Його мета – підготувати віруючих до зустрічі свята Різдва Христового. Звідси його назва – Різдвяний піст. День його початку дав іншу назву – Пилипівка. У тих Помісних Церквах, де його дотримуються упродовж сорока днів, цей піст має ще іншу назву – «Свята Чотиридесятниця».\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2952:chas-postyty-28-lystopada-pochatok-rizdvjanogo-postu&catid=28:tema-nomera-cat&Itemid=2',5301,NULL,1,1,26),
(5760,'P','2015-11-27',0,NULL,'Note','УГКЦ ініціює загальноцерковний рух протидії корупції','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6785,200,bez_koruptsii.jpg\" alt=\"\" width=\"200\" height=\"150\" />Питання протидії корупції було головною темою&nbsp;<a href=\"http://news.ugcc.ua/go/aHR0cDovL25ld3MudWdjYy51YS9uZXdzL3Byb19zaCVEMSU5NnN0ZGVzeWF0X2RldnlhdHVfc2VzJUQxJTk2eXVfc2lub2R1X3llcGlza29wJUQxJTk2dl9raWlldm9nYWxpdHNrb2dvX3Zlcmhvdm5vZ29fYXJoaWllcGlza29wc3R2YV91Z2t0c183NTQzNS5odG1s\" target=\"_blank\">цієї сесії Синоду</a>. Аналізуючи витоки цієї суспільної недуги, члени Синоду заслухали доповідь про суспільні очікування від Церкви щодо подолання корупції в Україні, яку виголосив пан Мирослава Маринович, президент Інституту релігії та суспільства і проректор із питань призначення та місії УКУ, а також доповідь про практичні заходи щодо подолання корупції, яку виголосив пан Сергій Гула, координатор Національного руху &laquo;Ні хабарництву! Я не беру і не даю хабарів&raquo;.<br /></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6785,200,bez_koruptsii.jpg\" alt=\"\" width=\"200\" height=\"150\" />Синод Єпископів Києво-Галицького Верховного Архиєпископства, чергова сесія якого відбулася 24-25 листопада, визнав суспільну недугу корупції гріхом, відповідальність за який є особистою. Тому владики УГКЦ вирішили ініціювати створення загальноцерковного руху протидії цьому гріхові, сформувавши відповідну робочу групу. Її першим завданням є опрацювання програми душпастирства у сфері протидії корупції.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Питання протидії корупції було головною темою&nbsp;<a href=\"http://news.ugcc.ua/go/aHR0cDovL25ld3MudWdjYy51YS9uZXdzL3Byb19zaCVEMSU5NnN0ZGVzeWF0X2RldnlhdHVfc2VzJUQxJTk2eXVfc2lub2R1X3llcGlza29wJUQxJTk2dl9raWlldm9nYWxpdHNrb2dvX3Zlcmhvdm5vZ29fYXJoaWllcGlza29wc3R2YV91Z2t0c183NTQzNS5odG1s\" target=\"_blank\">цієї сесії Синоду</a>. Аналізуючи витоки цієї суспільної недуги, члени Синоду заслухали доповідь про суспільні очікування від Церкви щодо подолання корупції в Україні, яку виголосив пан Мирослава Маринович, президент Інституту релігії та суспільства і проректор із питань призначення та місії УКУ, а також доповідь про практичні заходи щодо подолання корупції, яку виголосив пан Сергій Гула, координатор Національного руху &laquo;Ні хабарництву! Я не беру і не даю хабарів&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Синод Верховного Архиєпископства висловив підтримку всім громадським організаціям та ініціативам, які борються зі суспільною хворобою корупції. Синод наказує вірним УГКЦ долучити молитву за зцілення від гріха корупції до молитов у&nbsp;<a href=\"http://news.ugcc.\" target=\"_blank\">щомісячний день молитви за зцілення узалежнених та їхніх рідних</a>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">На цій сесії Синоду відбувся розгляд та затвердження в першому читанні з внесеними поправками та доповненнями Пастирського листа Синоду Єпископів Києво-Галицького Верховного Архиєпископства щодо хвороби корупції. Документ буде доопрацьований і оприлюднений наступного року.</span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Департамент інформації УГКЦ</span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><a href=\"http://news.ugcc.ua/\">http://news.ugcc.ua/</a></span></p>',NULL,5302,5,1,NULL,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5761,'P','2015-11-30',1,NULL,'Rss','Катехитична школа в Національному музеї «Меморіал жертв Голодомору','<p><img src=\"http://oranta.org/images/stories/WP_20151115_023.jpg\" width=\"250\" height=\"141\" alt=\"WP_20151115_023\" style=\"margin: 10px; float: right;\" />У ході планування навчального року катехитичної школи, окрім основної програми, було передбачено ряд святкових та відпочинкових заходів та екскурсій з метою поглиблення знань про українську історію та культуру, і роль церкви у ній. Так,15 листопада усі групи і батьки школи разом з катехитами&nbsp;відвідали&nbsp;Національний музей «Меморіал жертв Голодомору».</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2953:2015-11-30-06-43-28&catid=25:parafija-cat&Itemid=27',5303,NULL,1,1,26),
(5762,'P','2015-11-30',1,NULL,'Rss','Скарби Андрея Шептицького','<p><img style=\"margin: 10px; float: left;\" alt=\"csm_vl_Andrekkj_f0de8cd0ee\" height=\"160\" width=\"250\" src=\"http://oranta.org/images/stories/csm_vl_Andrekkj_f0de8cd0ee.jpg\" />Cлуга Божий&nbsp;митрополит Андрей Шептицький, своєю всебічною і невсипущою працею піднімав український народ до рівня зрілости й гідности бути самостійним державним народом. За мудрі настанови й великі труди він отримав багато народних імен: Український Мойсей, Духовний будівничий, Провідник української нації, Великий митрополит.</p>\n<p><strong>Нині пригадаймо його великі скарби, які він поклав на жертовник служіння Богові й своїй Вітчизні.</strong>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2954:skarby-andreja-sheptyckogo&catid=28:tema-nomera-cat&Itemid=2',5304,NULL,1,1,26),
(5763,'P','2015-12-02',1,NULL,'Note','Чума ХХ століття','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6789,200,123223.jpg\" alt=\"\" width=\"133\" height=\"200\" />&ndash; Коли доля прирікає людину на смерть, людина змінюється і стає ближчою до Бога,- розповідає про спілкування із хворими отець Анатолій. &ndash; Коли людина усвідомлює, що вона вже не може змінити вирок і є приреченою на смерть вона починає щиро молитися. Звертається до Господа від чистого серця.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&hellip; Після служби сніг припинився. Повітря наповнилося спокоєм. Проходячи крізь вогники &laquo;кашне&raquo; на свіжому снігу, подумалося: &ndash; чи зможе червона хустина зігріти хоча б одну душу?, а ось молитва і пам&rsquo;ять, безперечно, подарують спокій не одній спочилій душі&hellip;</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6789,200,123223.jpg\" alt=\"\" width=\"133\" height=\"200\" />За ініціативи Всесвітньої організації охорони здоров&rsquo;я, 1 грудня, щорічно, весь світ відзначає Всесвітній день боротьби зі СНІДом. Мета заходу &ndash; звернути увагу суспільства на цю проблему. Підступна хвороба майже за тридцять років існування, вже забрала мільйони життів у всьому світі. За даними ООН, поширення епідемії імунодефіциту на планеті сповільнилося. За останні 5 років кількість виявлених випадків зараження ВІЛ/СНІД скоротилася на 20%. Натомість в Україні серед країн Східної Європи та Центральної Азії ступінь поширеності ВІЛ/СНІД найвищий. Серед дорослого населення він становить 1,1%. Щодня в Україні реєструється майже 50 випадків захворювання ВІЛ-інфекцією&hellip;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Понад 10 років проблемами віл-інфікованих громадян у Києві опікується священик Анатолій Тесля. Перед вечірньою службою 1 грудня, &nbsp;перед церквою на Аскольдовій могилі, з червоних лампадок ми виклали стрічку, що символізує солідарності із ВІЛ-позитивними та хворими на СНІД. Коли в об&rsquo;єктиві, на фоні Храму святого Миколая, спробував закарбувати цю мить, з неба рясно посипав пухкий сніг. З темного вечірнього неба прямо на нас злітали тисячі білосніжних сніжинок. Білі метелики &ndash; наче душі тих, хто став безправною жертвою незборимого лиха, поспішили до нас на спомин&hellip;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ndash; Коли доля прирікає людину на смерть, людина змінюється і стає ближчою до Бога,- розповідає про спілкування із хворими отець Анатолій. &ndash; Коли людина усвідомлює, що вона вже не може змінити вирок і є приреченою на смерть вона починає щиро молитися. Звертається до Господа від чистого серця.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&hellip; Після служби сніг припинився. Повітря наповнилося спокоєм. Проходячи крізь вогники &laquo;кашне&raquo; на свіжому снігу, подумалося: &ndash; чи зможе червона хустина зігріти хоча б одну душу?, а ось молитва і пам&rsquo;ять, безперечно, подарують спокій не одній спочилій душі&hellip;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Олександр Пастухов для &laquo;Оранти&raquo;</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>',NULL,5306,5,1,NULL,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5764,'P','2015-12-02',1,NULL,'Rss','«Якщо люди не бачать Бога у священику, він не виконує покликання» ','<p><strong><img style=\"margin: 10px; float: left;\" alt=\"876546\" height=\"156\" width=\"250\" src=\"http://oranta.org/images/stories/876546.jpg\" />Мені прищепили любов і велику повагу до Євхаристії мої батьки. Коли наставала неділя, і якщо вона не була робочою (а&nbsp;за радянських часів часто на великі свята, такі як Великдень, змушували до праці, аби не дати людям можливості піти до храму), ми всі разом ішли до храму на Євхаристію. То було як закон. То було основне в нашому житті.</strong></p>\n<p>Батьки мені багато розповідали про Євхаристію, про Святу Месу, про те, як треба її переживати, як треба поводитися в храмі, і я бачив, як вони самі переживали Літургію. Коли приходиш до храму, то треба уважно слухати, бо Месу тоді служили латиною, і богослужіння тривало близько трьох годин. Так було до ІІ&nbsp;Ватиканського Собору. Потрібно було слухати уважно, бо я знав: вдома мене спитають, про що була проповідь. Після повернення додому був такий собі іспит.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2956:2015-12-02-19-57-10&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5307,NULL,1,1,26),
(5765,'N','2015-12-12',0,NULL,'Note','Подаруй пораненому радість на Миколая – принеси 2 цукерки!','<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6791,200,DSC0090.jpg\" alt=\"\" width=\"200\" height=\"132\" /></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дорогі друзі! Готуємось до Св. Миколая!</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Готуємо невеличкі подарунки (цукерки, яблука, печиво) на Миколая для 800 поранених Захисників!</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Щиро запрошуємо долучитись до приготування подарунків!</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Збір цукерок відбудеться 13.12.2015 після кожної Служби Божої на Аскольдовій Могилі!</span></p>','<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6791,200,DSC0090.jpg\" alt=\"\" width=\"200\" height=\"132\" />Слава Ісусу Христу!</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дорогі друзі! Готуємось до Св. Миколая!</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Готуємо невеличкі подарунки (цукерки, яблука, печиво) на Миколая для 800 поранених Захисників!</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Щиро запрошуємо долучитись до приготування подарунків!</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Збір цукерок відбудеться 13.12.2015 після кожної Служби Божої на Аскольдовій Могилі!</span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>Коли береш &ndash; наповнюєш руку,&nbsp;</em></span><em style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">коли даєш &ndash; наповнюєш серце.</em></p>',NULL,5308,5,1,NULL,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5766,'P','2015-12-03',1,NULL,'Rss','День Введення до храму Пресвятої Богородиці','<p><img style=\"margin: 10px; float: right;\" alt=\"Eisodia_mesa\" height=\"194\" width=\"250\" src=\"http://oranta.org/images/stories/Eisodia_mesa.jpg\" />З сумом спостерігали юдеї за посвятою Єрусалимського храму, відбудованого Зоровавелом у шостому сторіччі до Різдва Христового. Сімдесятирічний вавилонський полон не зітер спогадів про прекрасний Соломонів храм, зруйнований завойовниками. Згадуючи його, старші люди плакали. І, мабуть, ніхто не міг зрозуміти, звідки в пророка Аггея береться радісне натхнення, що відлунює в словах провіщення майбутньої слави цього нового, скромного на вигляд храму: «Я потрясу всіма народами, і прийдуть скарби всіх народів, і я сповню дім цей славою, – каже Господь сил… Слава цього останнього дому буде більша, ніж першого, каже Господь сил, і я цьому місцю дам мир, – слово Господа сил» (Агг. 2:7,9).</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2957:den-vvedennja-do-hramu-presvjatoji-bogorodyci&catid=28:tema-nomera-cat&Itemid=2',5309,NULL,1,1,26),
(5767,'P','2015-12-04',1,NULL,'Rss','Як повернутися до первісної єдності християн в Україні?','<p><strong><img style=\"margin: 10px; float: left;\" alt=\"guzar_3\" height=\"125\" width=\"250\" src=\"http://oranta.org/images/stories/guzar_3.jpg\" /></strong>Від деякого часу в місті Києві ведеться дискусія, який пам’ятник поставити на початку проспекту Шевченка, де за часів нацистської окупації відбувалися страти, а згодом упродовж довгих років стояв пам’ятник Володимиру Леніну.</p>\n<p>Від деякого часу в місті Києві ведеться дискусія, який пам’ятник поставити на початку проспекту Шевченка, де за часів нацистської окупації відбувалися страти, а згодом упродовж довгих років стояв пам’ятник Володимиру Леніну.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2958:jak-povernutysja-do-pervisnoji-jednosti-hrystyjan-v-ukrajini&catid=28:tema-nomera-cat&Itemid=2',5310,NULL,1,1,26),
(5768,'P','2015-12-12',0,NULL,'Note','«Боже Милосердя – понад усе»','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6794,200,rizdvy.jpg\" alt=\"\" width=\"200\" height=\"141\" />11 грудня у церкві св. Миколая Мирлікіського на Аскольдовій Могилі, о 17:30 опісля вечірньої літургії розпочняться Різдвяні реколекції на тему: &laquo;Боже Милосердя &ndash; понад усе&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Реколокції проводить префект Київської Трьохсвятительської семінарії &ndash; о. Іван Данчевський.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6794,200,rizdvy.jpg\" alt=\"\" width=\"200\" height=\"141\" />11 грудня у церкві св. Миколая Мирлікіського на Аскольдовій Могилі, о 17:30 опісля вечірньої літургії розпочняться Різдвяні реколекції на тему: &laquo;Боже Милосердя &ndash; понад усе&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Реколокції проводить префект Київської Трьохсвятительської семінарії &ndash; о. Іван Данчевський.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">П&rsquo;ятниця 11.12.15: &nbsp; &nbsp; &nbsp; 17:30 &ndash; Літургія. Перша реколекційна наука</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Субота 12.12.15: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;08:00 &ndash; Утрення</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;09:00 &ndash; Служба Божа. Панахида.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Друга реколекцінйа наука</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;17:30 &ndash; Літургія для молоді, зустріч із молоддю</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Неділя 13.12.15: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;08:00; 10:00; 12:30 Божественна літургія.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Проповіді реколектанта на кожній літургії</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;18:00 - Вечірня</span></p>',NULL,5311,5,1,NULL,41);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5769,'P','2015-12-05',1,NULL,'Rss','Для трансформації Україні потрібно 15 років','<p><img style=\"margin: 10px; float: right;\" alt=\"63_main_ukr\" height=\"157\" width=\"250\" src=\"http://oranta.org/images/stories/63_main_ukr.jpg\" />Відомий український економіст, член Римського клубу, учасник групи “Першого грудня” і один з тих, хто заснував всесвітній форум у Давосі, розповів, чому українські лідери і політики нездатні на прорив і в кому він бачить надію.</p>\n<p>Богдан Гаврилишин входить в число найавторитетніших українців у світі. Він народився на Тернопільщині, був вивезений з України під час Другої світової, отримав найкращу західну освіту і став економістом зі світовим ім’ям. Він живе за кордоном, проте левову частку своїх знань, зусиль і грошей присвячує Україні. Гаврилишин був радником українських президентів і прем’єрів, є співзасновником Міжнародного благодійного фонду Відродження, який активно опікується Україною, та членом ініціативної групи Першого грудня.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2959:dlja-transformaciji-ukrajini-potribno-15-rokiv&catid=37:spilnota-cat&Itemid=49',5312,NULL,1,1,26),
(5770,'P','2015-12-08',1,NULL,'Rss','Проповідь Папи Франциска з нагоди відкриття Святого Року Милосердя','<p><em><img style=\"margin: 10px; float: left;\" alt=\"65872\" height=\"152\" width=\"250\" src=\"http://oranta.org/images/stories/65872.jpg\" />У вівторок, 8 грудня 2015 р., в урочистість Непорочного Зачаття Пречистої Діви Марії, Папа Франциск очолив на площі Святого Петра у Ватикані Євхаристійне богослуження з нагоди відкриття надзвичайного Святого Року Милосердя, під час якого виголосив наступну проповідь:</em></p>\n<p>Браття і сестри,</p>\n<p>через деякий час матиму радість відкрити Святі Двері Милосердя. Здійснюємо цей жест, який я вже здійснив у Банґі, настільки простий, наскільки символічний, у світлі Божого слова, яке ми вислухали, що ставить на першому плані першість благодаті. Те, що неодноразово повторюється в цих читаннях, у дійсності, відсилає до слів, з якими ангел Гавриїл звернувся до юної дівчини, схвильованої та здивованої, вказуючи на таїнство, яке Її огорнуло: «Радуйся, повна благодаті» (Лк 1,28).</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2960:propovid-papy-francyska-z-nagody-vidkryttja-svjatogo-roku-myloserdja&catid=27:gk-v-ukrajini-cat&Itemid=57',5313,NULL,1,1,26),
(5771,'P','2015-12-08',1,NULL,'Rss','«Старенькі люди є неоціненним скарбом для сучасності»','<p><img style=\"margin: 10px; float: left;\" alt=\"svyatoslav_lutsk\" height=\"160\" width=\"250\" src=\"http://oranta.org/images/stories/svyatoslav_lutsk.jpg\" />Для Господа і Церкви важливою є кожна людина: чи вона молода, чи зрілого віку. Нещодавно я повернувся з Папського синоду, який проходив на тему сім’ї, і по сьогодні я живу враженнями від нього. Ми говорили про те, що зараз стоять великі виклики перед християнською родиною, а також про роль та місце старших людей у християнській сім’ї.</p>\n<p>Про це сказав Глава і Отець УГКЦ Блаженніший Святослав 7 грудня, відкриваючи конференцію на тему християнської опіки над літніми людьми, яка проходить в Брюховичах поблизу Львова, з ініціативи Комісії УГКЦ у справах родини.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2962:2015-12-08-12-07-44&catid=37:spilnota-cat&Itemid=49',5314,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5772,'P','2016-01-14',0,NULL,'Note','День пам\'яті святого Сильвестра, папи Римського','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6874,200,ih3922.jpg\" alt=\"\" width=\"175\" height=\"200\" /><strong>15 січня</strong> у церкві Св. Миколая Мирлікійського на Аскольдовій Могилі <strong>празник Св. Папи Сильвестра.</strong></span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08:30 &ndash; Божественна Літургія</span></strong></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">09:30 &ndash; освячення води у крипті Папи Сильвестра</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Святитель Сильвестр славився як глибокий знавець Священного Писання і непохитний захисник християнської віри.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Святий папа Сильвестр керував Римською Церквою більше двадцяти років, користуючись глибокою повагою християн.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6874,200,ih3922.jpg\" alt=\"\" width=\"175\" height=\"200\" />15 січня у церкві Св. Миколая Мирлікійського на Аскольдовій Могилі празник Св. Папи Сильвестра.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08:30 &ndash; Божественна Літургія</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">09:30 &ndash; освячення води у крипті Папи Сильвестра</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Святий папа Римський Сильвестр народився в Римі в сім\'ї християн. Незабаром батько його помер, і святий залишився під опікою матері.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Вчитель Сильвестра, священик Квіріній, дав йому хорошу освіту і виховав як істинного християнина.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Під час гоніння на християн Сильвестр не побоявся прийняти святого сповідника, єпископа Тимофія, який прожив у нього більше року і своєю проповіддю звернув багатьох до Христа. Після мученицької смерті Тимофія, Сильвестр таємно взяв тіло святого і з честю поховав його. Це стало відомо меру Тарквінію і святий був схоплений і постав перед судом.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Тарквіній змушував його виявитися від Христа, загрожуючи муками і смертю. Однак, святий Сильвестр, залишився непохитним у сповіданні віри і був посаджений у темницю. Після смерті Тарквінія святий отримав свободу і став проповідувати язичникам.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">У тридцятирічному віці святий Сильвестр був висвячений у сан диякона, а потім і пресвітера. Після смерті папи Мельхіада святий Сильвестр був обраний єпископом Римським. Він ревно дбав про чистоту життя пастви, стежив за тим, щоб священики строго виконували своє служіння, не обтяуючи себе світськими справами.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Святитель Сильвестр славився як глибокий знавець Священного Писання і непохитний захисник християнської віри.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Святий папа Сильвестр керував Римською Церквою більше двадцяти років, користуючись глибокою повагою християн.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Він мирно помер у глибокій старості в 335 році.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><a href=\"http://religions.unian.ua/\">http://religions.unian.ua/</a></span></p>',NULL,5391,5,1,NULL,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5773,'P','2015-12-08',1,NULL,'Rss','У Музеї книги і друкарства України','<p><strong><img style=\"margin: 10px; float: right;\" alt=\"1234\" height=\"141\" width=\"250\" src=\"http://oranta.org/images/stories/1234.jpg\" />6 </strong><strong>грудня 2015 р.</strong> слухачі катехитичної школи та катехитки, сестри служебниці Меланія і Тимотея, а також парафіяни церкви св. Миколая Мир Лікійського на Аскольдовій могилі відвідали один з найдавніших духовних центрів України – Києво-Печерську Лавру. Основним об’єктом знайомства став <strong>Музей книги і друкарства України, </strong>розташований<strong> </strong>на території Національного Києво-Печерського заповідника, створений ще в 1972 р. у будинку колишньої лаврської друкарні.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=2961:u-muzeji-knygy-i-drukarstva-ukrajiny&catid=25:parafija-cat&Itemid=27',5315,NULL,1,1,26),
(5774,'P','2015-12-08',1,NULL,'Rss','Проповідь Папи Франциска з нагоди відкриття Святого Року Милосердя','<p><em><img src=\"http://oranta.org/images/stories/65872.jpg\" width=\"250\" height=\"152\" alt=\"65872\" style=\"margin: 10px; float: left;\" />У вівторок, 8 грудня 2015 р., в урочистість Непорочного Зачаття Пречистої Діви Марії, Папа Франциск очолив на площі Святого Петра у Ватикані Євхаристійне богослуження з нагоди відкриття надзвичайного Святого Року Милосердя, під час якого виголосив наступну проповідь:</em></p>\n<p>Браття і сестри,</p>\n<p>через деякий час матиму радість відкрити Святі Двері Милосердя. Здійснюємо цей жест, який я вже здійснив у Банґі, настільки простий, наскільки символічний, у світлі Божого слова, яке ми вислухали, що ставить на першому плані першість благодаті. Те, що неодноразово повторюється в цих читаннях, у дійсності, відсилає до слів, з якими ангел Гавриїл звернувся до юної дівчини, схвильованої та здивованої, вказуючи на таїнство, яке Її огорнуло: «Радуйся, повна благодаті» (Лк 1,28).</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2960:propovid-papy-francyska-z-nagody-vidkryttja-svjatogo-roku-myloserdja&catid=32:gk-svit-cat&Itemid=58',5316,NULL,1,1,26),
(5775,'P','2015-12-08',1,NULL,'Rss','Лист Папи Франциска про відпусти з нагоди Надзвичайного Ювілею Милосердя','<p><em><img style=\"margin: 10px; float: left;\" alt=\"rik_myloserdya-2\" height=\"188\" width=\"250\" src=\"http://oranta.org/images/stories/rik_myloserdya-2.jpg\" />Достойному братові архиєпископові Ріно Фізікеллі, Президентові Папської Ради сприяння новій євангелізації</em></p>\n<p>Наближення Надзвичайного Ювілею Милосердя дає мені змогу звернути увагу на деякі питання, щодо яких вважаю важливим висловитися, щоби посприяти тому, аби відзначення Святого Року було для всіх вірних справжнім моментом зустрічі з Божим милосердям. Моїм бажанням, насправді, є, аби Ювілей був живим досвідом близькості Небесного Отця, немов бажаючи доторкнутися рукою до Його лагідності, щоби віра кожного віруючого скріпилася, а, тим самим, свідчення ставало ще дієвішим.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2964:lyst-papy-francyska-pro-vidpusty-z-nagody-nadzvychanogo-juvileju-myloserdja&catid=28:tema-nomera-cat&Itemid=2',5317,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5776,'P','2015-12-08',1,NULL,'Rss','Розстріляний з\'їзд кобзарів','<p><img style=\"margin: 10px; float: right;\" alt=\"kobzari\" height=\"110\" width=\"250\" src=\"http://oranta.org/images/stories/kobzari.jpg\" />На початку грудня 1934-35 року в Харківському оперному театрі відбувся З’їзд народних співців Радянської України. Основним завданням З’їзду було питання активного залучення народних співців до соціалістичного будівництва, відходу від виконавських традицій і визначення нових ідеологічних приорітетів. Ухваливши відповідні резолюції, незрячих співців під приводом поїздки на З’їзд народних співців народів Союзу Радянських Соціалістичних Республік, що мав відбутися у Москві, повантажили до ешелону і підвезли до околиць ст. Козача Лопань.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2963:rozstriljany-zjizd-kobzariv&catid=28:tema-nomera-cat&Itemid=2',5318,NULL,1,1,26),
(5777,'P','2015-12-08',1,NULL,'Rss','Молитва Папа Франциска на Святий рік','<p><img src=\"http://oranta.org/images/stories/OSSROM26850_Articolo.jpg\" width=\"250\" height=\"177\" alt=\"OSSROM26850_Articolo\" style=\"margin: 10px; float: right;\" />Господи Ісусе Христе,</p>\n<p>Ти навчав нас бути милосердними, як Небесний Отець,</p>\n<p>Ти сказав нам, що той, хто бачить Тебе, бачить Його.</p>\n<p>Покажи нам Своє обличчя і будемо спасенні.</p>\n<p>Твій погляд, наповнений любов’ю, визволив Закхея та Матея від рабства грошей;</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2965:molytva-papa-francyska-na-svjaty-rik&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5319,NULL,1,1,26),
(5778,'P','2016-02-15',1,NULL,'Note','Продуктова допомога парафіянам','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6929,200,20160207_140357.jpg\" alt=\"\" width=\"200\" height=\"177\" />Парафіяльна служба &laquo;Карітас Печерськ&raquo; надала продуктову допомогу парафіянам, які у даний час перебувають у скруті і потребують допомоги.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Служба &laquo;Карітас Печерськ&raquo;, парафіяни дуже вдячні п. Михайлу Апостолу за його увагу до нашої церковної громади. адже саме його стараннями наші парафіяни отримали цю суттєву допомогу у цей важкий час.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Громада ще раз щиро дякує і моляться за Вас, та вашу родину. Хай милостивий Господь благословляє ваше серце на жертовні подвиги.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6929,200,20160207_140357.jpg\" alt=\"\" width=\"200\" height=\"177\" /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Економічна ситуація в країні неабияк вплинула на купівельну спроможність людей. &laquo;Як звести&nbsp; сімейний &laquo;дЕбет&raquo; з&nbsp; &laquo;крЕдитом&raquo;, коли все дорожчає?&raquo;, &ndash; таке&nbsp; питання останнім часом цікавить, на жаль, все більше українських &nbsp;родин. А коли мати одна, без чоловіка та сторонньої допомоги, &nbsp;виховує дітей,&nbsp; то говорити й поготів. Клопотів не бракує: одягнути, нагодувати&hellip;&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">УГКЦ св. Миколая на Аскольдовій могилі&nbsp; всіляко намагається підтримати такі пільгові категорії своїх парафіян.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Парафіяльна служба &laquo;Карітас Печерськ&raquo; надала продуктову допомогу парафіянам, які у даний час перебувають у скруті і потребують допомоги.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Цього разу 12 &nbsp;самотніх&nbsp; матерів отримали продуктові набори від німецької благодійної організації. Жінки були приємно вражені такою доброчинністю:&nbsp; олія, цукор, макаронні вироби, мука, рис, а також шипучі вітаміни. &laquo;Щонайменше гривень на 300. Якісне, свіже!&raquo;, - раділи обдаровані парафіянки і одразу ж почали обдумувати, що ж найперше приготувати дітям із цього набору.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Служба &laquo;Карітас Печерськ&raquo;, парафіяни дуже вдячні п. Михайлу Апостолу за його увагу до нашої церковної громади. адже саме його стараннями наші парафіяни отримали цю суттєву допомогу у цей важкий час.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Громада ще раз щиро дякує і моляться за Вас, та вашу родину. Хай милостивий Господь благословляє ваше серце на жертовні подвиги.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Мирослава Мазур та Ілля для &laquo;Оранти&raquo;</span></p>',NULL,5446,5,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5779,'P','2015-12-11',1,NULL,'Rss','Спільна Декларація католицьких єпископів України з приводу небезпеки нового ідеологічного поневолення нашого народу','<p><img src=\"http://oranta.org/images/stories/rv540_articolo.jpg\" width=\"250\" height=\"177\" alt=\"rv540_articolo\" style=\"margin: 10px; float: left;\" />Сім\'я є найстаршою інституцією на Землі. Вона існувала до появи таких видів укладу людського життя, як народ, нація чи держава. Протягом тисячоліть саме в сім’ї найглибше проявляється любов та відповідальність, вірність та відданість, служіння та жертовність. Значення сім’ї для суспільства також неоціненне, тому що вона є джерелом соціального капіталу, який складається з низки чеснот, зокрема чесності, працьовитості, довіри та вміння працювати разом.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2966:spilna-deklaracija-katolyckyh-jepyskopiv-ukrajiny-z-pryvodu-nebezpeky-novogo-ideologichnogo-ponevolennja-nashogo-narodu&catid=27:gk-v-ukrajini-cat&Itemid=57',5320,NULL,1,1,26),
(5780,'P','2015-12-13',1,NULL,'Note','ЗВІТ №18 про роботу служби милосердя «Карітас» при парафії св.Миколая Чудотворця на Аскольдовій могилі  (жовтень 2015 р.)','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Крім того, надано допомогу засобами гігієни та речами особистого вжитку двом ув&rsquo;язненим жінкам Качанівської ВК (м.Харків) (2 посилки, див звіт №17).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Роздано мирянам та потребуючим каву мелену &ndash; 0,75 мішка.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Робота виконана силами волонтерів: п.Мирослави, п.Людмили, п.Світлани, п.Надії, п.Тетяни, п.Віталія, п.Анни.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Висловлюємо щиру подяку жертводавцям і прикликаємо Боже благословення на їх родини.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Служба милосердя &laquo;Карітас&raquo; при церкві св.Миколая Чудотворця на Аскольдовій могилі, зібравши пожертвувані парафіянами, а також не належними до парафії жертводавцями одяг чоловічий, жіночий та дитячий, взуття, іграшки дитячі та гуманітарну допомогу одягом, взуттям, постільною білизною та медичними приналежностями від благодійних фондів &laquo;Міжнародна асоціація підтримки України (МАПУ)&raquo;, &laquo;Тріумф серця&raquo; і &laquo;Олива&raquo;, розподілила їх таким чином (записи в книзі розподілу гуманітарної допомоги):</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Уділили потребуючим мирянам (32 особи):</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash; 90 кг</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;куртки, пальто, шуби &ndash; 37 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;комбінезони лижні дит. &ndash; 5 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;взуття &ndash; 2 пари</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;памперси для дорослих &ndash; 2 уп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;постіль (покривало) &ndash; 1 шт.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Надали матеріальну допомогу:</span></li>\r\n</ol>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">переселенцям з Донецької обл. та АР Крим(6 осіб):</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash; 15 кг.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;куртки, пальто &ndash; 6 шт.</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">потребуючим та безпритульним (35 осіб):</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash; 95 кг.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;куртки, пальто &ndash; 34 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;взуття &ndash; 3 пари</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;постільна білизна &ndash; 3 од.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;ковдра &ndash; 1 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;памперси для дорослих &ndash; 1 уп.</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">ув&rsquo;язненим жінкам в Лук&rsquo;янівський СІЗО в м.Києві</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash; 4 мішки</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Передали для бійців в зону АТО:</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;светри &ndash; 68 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;безрукавки &ndash; 7 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;футболки &ndash; 18 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;штани 18 пар</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;нижня білизна &ndash; 10 пар</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;костюм камуфляжний &ndash; 1 компл.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;шапки теплі &ndash; 3 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;рукавиці &ndash; 1 пара</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Крім того, в госпіталь військовий &ndash; светри 2 шт., шарфи &ndash; 5 шт.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Відправили гуманітарний вантаж:</span></li>\r\n</ol>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Благодійному фонду &ldquo;Сузір\'я&rdquo; в с.Іванкові Бориспільського р-ну Київської обл.:</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг чоловічий, жіночий, дитячий, взуття, іграшки дитячі &ndash; 13 мішків</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Парафії УГКЦ в с.Голдашівці Козятинського р-ну Вінницької обл.:</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг чоловічий, жіночий, дитячий, взуття &ndash; 7 мішків+1 коробка</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Релігійній місії Римсько-католицької церкви &ldquo;Карітас-СПЕС-Київ&rdquo;:</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг чоловічий, жіночий &ndash; 5 мішків</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;дитячий &ndash; 1 мішок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Крім того, надано допомогу засобами гігієни та речами особистого вжитку двом ув&rsquo;язненим жінкам Качанівської ВК (м.Харків) (2 посилки, див звіт №17).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Роздано мирянам та потребуючим каву мелену &ndash; 0,75 мішка.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Робота виконана силами волонтерів: п.Мирослави, п.Людмили, п.Світлани, п.Надії, п.Тетяни, п.Віталія, п.Анни.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Висловлюємо щиру подяку жертводавцям і прикликаємо Боже благословення на їх родини.</span></p>',NULL,5321,5,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5781,'P','2015-12-13',1,NULL,'Note','ЗВІТ №19 про роботу служби милосердя «Карітас» при парафії св.Миколая Чудотворця на Аскольдовій могилі (листопад 2015 р.)','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;Служба милосердя &laquo;Карітас&raquo; при церкві св.Миколая Чудотворця на Аскольдовій могилі, зібравши пожертвувані парафіянами, а також не належними до парафії жертводавцями одяг чоловічий, жіночий та дитячий, взуття, іграшки дитячі та гуманітарну допомогу одягом та постіллю від благодійних фондів &laquo;Міжнародна асоціація підтримки України (МАПУ)&raquo;, &laquo;Тріумф серця&raquo; і &laquo;Олива&raquo;, розподілила їх таким чином (записи в книзі розподілу гуманітарної допомоги):</span></p>','<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Служба милосердя &laquo;Карітас&raquo; при церкві св.Миколая Чудотворця на Аскольдовій могилі, зібравши пожертвувані парафіянами, а також не належними до парафії жертводавцями одяг чоловічий, жіночий та дитячий, взуття, іграшки дитячі та гуманітарну допомогу одягом та постіллю від благодійних фондів &laquo;Міжнародна асоціація підтримки України (МАПУ)&raquo;, &laquo;Тріумф серця&raquo; і &laquo;Олива&raquo;, розподілила їх таким чином (записи в книзі розподілу гуманітарної допомоги):</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Уділили потребуючим мирянам (42 особи):</span></li>\r\n</ol>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash; 107 кг</span></p>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;куртки, пальто &ndash; 15 шт.</span></p>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;комбінезони дит. &ndash; 5 шт.</span></p>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;взуття &ndash; 11 пар</span></p>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;постіль &ndash; плед-1 шт, подушка-1 шт.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Надали матеріальну допомогу:</span></li>\r\n</ol>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">переселенцям з Донецької обл. та АР Крим (5 осіб):</span></li>\r\n</ul>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash; 6 кг.</span></p>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;взуття &ndash; 1 пара</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">потребуючим та безпритульним (17 осіб):</span></li>\r\n</ul>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash; 57 кг.</span></p>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;куртки, пальто &ndash; 9 шт.</span></p>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;взуття &ndash; 1 пара</span></p>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;постільна білизна &ndash; 3 од.</span></p>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;ковдри &ndash; 4 шт.</span></p>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;памперси для дорослих &ndash; 1 уп.</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">підліткам в Лук&rsquo;янівський СІЗО в м.Києві</span></li>\r\n</ul>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash; 1 мішок</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Передали для бійців АТО та населення зони АТО через благодійний фонд &laquo;Завжди поруч&raquo;:</span></li>\r\n</ol>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг чоловічий, жіночий &ndash; 4 міш.</span></p>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг дитячий &ndash; 1 міш.</span></p>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;постіль &ndash; 2 міш.</span></p>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;подушки &ndash; 11 шт.</span></p>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;нижня білизна &ndash; 10 пар</span></p>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;пледи &ndash; 2 шт.</span></p>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;покривало &ndash; 1 шт.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Відправили гуманітарний вантаж:</span></li>\r\n</ol>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Благодійному фонду &ldquo;Сузір\'я&rdquo; в с.Іванкові Бориспільського р-ну Київської обл.:</span></li>\r\n</ul>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг чоловічий, жіночий, дитячий &ndash; 13 міш.</span></p>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; взуття &ndash; 1 міш.</span></p>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; іграшки дитячі &ndash; 1 міш.</span></p>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; постіль &ndash; 1 міш.</span></p>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Роздано мирянам та потребуючим каву мелену &ndash; 0,5 мішка.</span></p>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Робота виконана силами волонтерів: п.Мирослави, п.Людмили, п.Світлани, п.Анни.</span></p>\r\n<p class=\"a0\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Висловлюємо щиру подяку жертводавцям і прикликаємо Боже благословення на їх родини.a</span></p>',NULL,5322,5,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5782,'P','2015-12-13',1,NULL,'Rss','Апостол Андрій','<p><img style=\"margin: 10px; float: right;\" alt=\"apostol_Andriy_Pervozvannyy_01\" height=\"250\" width=\"147\" src=\"http://oranta.org/images/stories/apostol_Andriy_Pervozvannyy_01.jpg\" />Наш український народ має багаті традиції — кожна пора року була сповнена для українців різними святкуваннями, звичаями, обрядами. Особливо багатою в цьому відношенні була зима, коли господарі не були зайняті роботами на своїх полях, а більше часу проводили в домівках із своїми родинами. Саме в такий час у церковному житті звершується вшанування пам’яті святого, котрий має особливе значення для Церкви Христової в Україні. 13 грудня за новим стилем православні українці вшановують святого апостола Андрія Первозванного.</p>\n<p>Апостол Андрій походив із Вифсаїди — міста у Галілеї — північній частині Палестини, відомої родючістю земель та гостинністю жителів. В часі земного служіння Господа нашого Ісуса Христа в Палестині проживало досить багато греків, а тому й не дивно, що дехто із юдеїв носили імена грецького походження. Так і у святого апостола, про якого йде мова, грецьке ім’я: «Андрій» в перекладі на українську означає «мужній».</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2967:apostol-andri&catid=35:istorychni-postati-cat&Itemid=48',5323,NULL,1,1,26),
(5783,'P','2015-12-24',0,NULL,'Note','СТАНЬ ВОЛОНТЕРОМ АСКОЛЬДОВОГО СЛУЖІННЯ','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6807,200,95365.jpg\" alt=\"\" width=\"200\" height=\"133\" />Запрошуємо людей доброї волі долучатися до волонтерського служіння на Аскольдовій Могилі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Анкета волонтера:&nbsp;/завантажити, заповнити і надіслати на e-mail:&nbsp;<a href=\"mailto:oranta-gazeta@ukr.net/\">oranta-gazeta@ukr.net</a>/</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Відомо, що волонтер &ndash; це людина з вільним волевиявленням, яка має бажання і намір добровільно, радше, жертовно допомагати іншим.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Запрошуємо людей доброї волі долучатися до волонтерського служіння на Аскольдовій Могилі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Анкета волонтера: <a href=\"https://drive.google.com/file/d/0B5ifTsMesXcZVW4zVWhOR2lVeGM/view\">https://drive.google.com/file/d/0B5ifTsMesXcZVW4zVWhOR2lVeGM/view</a>&nbsp;<span>/<span>завантажити</span>, заповнити і надіслати на e-mail:&nbsp;</span><a href=\"mailto:oranta-gazeta@ukr.net/\">oranta-gazeta@ukr.net</a></span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">/</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6807,200,95365.jpg\" alt=\"\" width=\"200\" height=\"133\" />Відомо, що волонтер &ndash; це людина з вільним волевиявленням, яка має бажання і намір добровільно, радше, жертовно допомагати іншим.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">За Біблійним переданням першим волонтером можна назвати доброго <a title=\"Самаритяни\" href=\"https://uk.wikipedia.org/wiki/%D0%A1%D0%B0%D0%BC%D0%B0%D1%80%D0%B8%D1%82%D1%8F%D0%BD%D0%B8\">самаряни</a>на, який допоміг пограбованому, побитому, нужденному чужинцю.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">То ж в<a title=\"Волонтерська діяльність\" href=\"https://uk.wikipedia.org/wiki/%D0%92%D0%BE%D0%BB%D0%BE%D0%BD%D1%82%D0%B5%D1%80%D1%81%D1%8C%D0%BA%D0%B0_%D0%B4%D1%96%D1%8F%D0%BB%D1%8C%D0%BD%D1%96%D1%81%D1%82%D1%8C\">олонтерська діяльність</a> як прояв милосердя і доброчинності існуватиме доти, доки існує потреба людей у тій чи іншій допомозі.</span></p>',NULL,5324,5,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5784,'P','2015-12-14',1,NULL,'Rss','«Я - прощений грішник»','<p><img style=\"margin: 10px; float: right;\" alt=\"ansa804459_articolo_0\" height=\"186\" width=\"250\" src=\"http://oranta.org/images/stories/ansa804459_articolo_0.jpg\" />Папа Франциск дав інтерв\'ю щотижневому журналу «Credere», офіційному органу ювілею милосердя.</p>\n<p>Яка була необхідність у проведенні Ювілею в умовах сьогоднішньої ситуації в Церкві та світі? На це питання Папа відповідає, що він відчув бажання продовжити традицію, започатковану Папами Павлом VI і Іваном Павлом II, які ставили особливий акцент на Божому милосерді. «Очевидно, що сучасний світ потребує милосердя,&nbsp; співчуття – співчуття разом». Ми звикли до поганих новин, до жахливих звірств, до жорстокості, які ображають ім\'я і життя Бога. Світу необхідно відкрити, що Бог - Батько, що Він є милосердя, що не можна йти шляхом жорстокості й осуду.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2968:2015-12-14-17-22-33&catid=28:tema-nomera-cat&Itemid=2',5325,NULL,1,1,26),
(5785,'P','2015-12-17',1,NULL,'Rss','Любомир Гузар: Якби ж люди замість заламування рук та нарікань взялися до роботи Вчора 16:19','<p><img style=\"margin: 10px; float: left;\" alt=\"img_1286\" height=\"167\" width=\"250\" src=\"http://oranta.org/images/stories/img_1286.jpg\" />З єпископом УГКЦ Блаженнішим Любомиром Гузаром розмовляємо про те, як українцям вистояти у тяжкі часи, де брати віру та сили, як послужити миру</p>\n<p>Традиційні передноворічні звернення та промови різних політиків до громадян останнім часом викликають у населення лише роздратування та іронію. Люди втомилися від брехні, нещирості та безкінечного політичного піару. Напередодні світлого Різдва Христового українцям хотілося б почути щирі слова від людини, якій вони довіряють, і яку вважають духовним авторитетом. Такою людиною, безперечно, визнано Блаженнішого Любомира Гузара.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2969:ljubomyr-guzar-jakby-zh-ljudy-zamist-zalamuvannja-ruk-ta-narikan-vzjalysja-do-roboty-vchora-1619&catid=37:spilnota-cat&Itemid=49',5326,NULL,1,1,26),
(5786,'P','2015-12-19',1,NULL,'Rss','Таємниці святого Миколая','<p><img src=\"http://oranta.org/images/stories/mykolaj05.jpg\" width=\"249\" height=\"250\" alt=\"mykolaj05\" style=\"margin: 10px; float: right;\" />Передчуття чогось неймовірного і казкового, а ще аромат незамінних мандарин – напевно, таким для більшості малечі й для багатьох дорослих є наближення Дня св. Миколая. Дня, на який всі чекають, щоб поділитися своїм теплом, і не тільки зі своїми найближчими, а й з тими, хто просто цього потребує. Звичай дарувати подарунки в цей день має багатовікову історію і пов’язаний з постаттю єпископа міста Міри в Малій Азії – святого Миколая. Від його смерті минуло сімнадцять століть, тому не дивно, що ми насправді знаємо доволі небагато фактів із його життя. А ті, що нам відомі, дійшли не в первісному вигляді. Тому дослідникам життя святого Миколая завжди було важко відділити правду від легенди, що оточує постать цього доброго сивобородого покровителя дітей.\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2970:tajemnyci-svjatogo-mykolaja&catid=28:tema-nomera-cat&Itemid=2',5327,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5787,'P','2015-12-21',1,NULL,'Rss','Не втомлюймося бути помічниками Св. Миколая!','<p align=\"right\"><em><img style=\"margin: 10px; float: left;\" alt=\"1419808\" height=\"188\" width=\"250\" src=\"http://oranta.org/images/stories/1419808.jpg\" />«Найбільше чудо в житті кожної людини творить любов».</em></p>\n<p>Вже проминуло таке довгоочікуване свято Святого Миколая, а у нашій країні на превеликий жаль продовжується війна і чимало захисників лежать поранені у госпіталях. Та хто як не вони заслужили на найкращі подарунки від Святого Миколая, хто як не вони, ці діти Божі, були і є прикладом для інших. Тож волонтери парафії церкви Св. Миколая Мирлікійського на Аскольдовій Могилі вирішили стати помічниками Св. Миколая і обдарувати подарунком кожного пораненого у військових госпіталях Києва та Ірпіня.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=2971:ne-vtomljumosja-buty-pomichnykamy-sv-mykolaja&catid=25:parafija-cat&Itemid=27',5328,NULL,1,1,26),
(5788,'P','2015-12-22',1,NULL,'Rss','Свята Анна','<p><img style=\"margin: 10px; float: right;\" alt=\"svjata-anna-1\" height=\"250\" width=\"196\" src=\"http://oranta.org/images/stories/svjata-anna-1.jpg\" />Чи помічали ми як часто нам хочеться пришвидшити здійснення мрії! Як нам хочеться дістати все й відразу! Читаємо казки про джинів, хочемо миттєвого сповнення бажань. А чекати? Лопає терпіння. Впадаємо в розпач у кращому випадку, у гіршому – в депресію. З цього приводу в естонців є хороше прислів’я: «Риба клює у того, хто чекає”.</p>\n<p>За тривале терпіння і незрадливість Слову Божому є, як правило, гарний результат. Чи достатньо&nbsp; ми є вірними меті — яка об’єднує всіх людей на планеті: добру відповідь дати на Страшному Суді, прийти до Бога в розкаянні за гріхи, з чистим сумлінням?</p>\n<p>Яскравим прикладом довготерпіння для сучасної людини є життя праведної Анни – бабусі Ісуса Христа.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2972:svjata-anna&catid=35:istorychni-postati-cat&Itemid=48',5329,NULL,1,1,26),
(5789,'P','2015-12-22',1,NULL,'Rss','Свята без стресу','<p><strong><img style=\"margin: 10px; float: left;\" alt=\"rojdestvo\" height=\"183\" width=\"250\" src=\"http://oranta.org/images/stories/rojdestvo.jpg\" />– Як раціонально розподілити потік своїх коштів, аби можна було гарно відсвяткувати і це не було “болісно” для гаманця?</strong></p>\n<p>– За принципами фінансової грамотності є певні види витрат, які також враховують такі види витрат, як подарунки, або непередбачені, або витрати пов’язані із святкуванням. Наприклад, якщо це дні народження ваших колег, а ви не знали про них. І за цими принципами 10% ми відкладаємо на благодійність. Тобто, це є ті речі, за які ми не хочемо отримати ніякої винагороди. І також ми відкладаємо 10% на так званий “резервний фонд” – непередбачені витрати, які нагально треба погасити. Тому якщо розподіл зробити одразу, після надходження коштів, то перед святами ми уже знатимемо звідки взяти кошти.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2973:svjata-bez-stresu&catid=37:spilnota-cat&Itemid=49',5330,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5790,'P','2015-12-22',1,NULL,'Rss','Результати ХІV фестивалю-конкурсу духовної пісні «АСКОЛЬДІВ ГЛАС»','<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6813,200,9856.JPG\" />19 грудня 2015 р. Б. в приміщенні НПУ ім. М.&nbsp;Драгоманова відбувся <strong>ХІ</strong><strong>V</strong><strong> </strong><strong>Фестиваль-конкурс духовної пісні «Аскольдів глас»</strong>.</p>\n<p>Конкурс традиційно проходив у рамках святкування зимового празника Святого Миколая, який для громади храму на Аскольдовій могилі – засновника цього мистецького заходу, - є храмовим. За вісім років існування фестиваль став знаною мистецькою подією в житті нашої столиці, адже в ньому беруть участь мистецькі колективи та окремі виконавці з цілої України, для багатьох з яких це чудова можливість дебютувати на серйозній сцені та спробувати свої сили у змаганні з іншими виконавцями.</p>\n','','http://oranta.org/index.php?option=com_content&view=article&id=2974:2015-12-22-19-57-03&catid=25:parafija-cat&Itemid=27',5331,NULL,1,1,26),
(5791,'P','2016-01-03',0,NULL,'Note','Доповнення у розкладі Богослужінь','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6815,200,1240441.jpg\" alt=\"\" width=\"200\" height=\"196\" /></span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><span style=\"font-family: arial, helvetica, sans-serif;\">Шановні парафіяни та гості нашого храму, звертаємо вашу увагу, що з 28 грудня 2015 року у недільний день служитиметься четверта Божественна Літургія о 18:00.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Запрошуємо усіх, хто немає змоги бути причасником ранішньої Христової трапези, долучитись до цієї трапези ввечері.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Пам&rsquo;ятайте, Бог завжди чекає на кожного.</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отож розклад на неділю починаючи з 28 грудня такий:</span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Неділя</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp;</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6815,200,1240441.jpg\" alt=\"\" width=\"200\" height=\"196\" />Шановні парафіяни та гості нашого храму, звертаємо вашу увагу, що з 28 грудня 2015 року у недільний день служитиметься четверта Божественна Літургія о 18:00.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Запрошуємо усіх, хто немає змоги бути причасником ранішньої Христової трапези, долучитись до цієї трапези ввечері.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Пам&rsquo;ятайте, Бог завжди чекає на кожного.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отож розклад на неділю починаючи з 28 грудня такий:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Неділя</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08.00 Божественна літургія зі співом вірних</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">09:45 Молебень з Акафістом до Матері Божої Гошівської за оздоровлення хворих і узалежнених, охорону подорожуючих, оливо помазання відправляється&nbsp;у каплиці &laquo;Чудо покладення чесної ризи Пресвятої Богородицi у Влахернi&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">10.00&nbsp;Божественна літургія з хором</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">12.30&nbsp;Божественна літургія для дітей. Збір молоді</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18.00 Божественна літургія зі співом вірних</span></p>',NULL,5332,5,1,NULL,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5792,'P','2015-12-23',1,NULL,'Rss','Секрети міцного подружжя, або як вибрати собі чоловіка чи дружину?','<p><strong><img src=\"http://oranta.org/images/stories/0_1119f4_6a829768_orig.jpg\" width=\"250\" height=\"169\" alt=\"0_1119f4_6a829768_orig\" style=\"margin: 10px; float: left;\" /></strong>Вибір супутника життя – дуже відповідальна справа в житті молодої людини, бо від цього залежить усе подальше сімейне благополуччя. Варто добре собі усвідомити, що ніхто не може одночасно кохати глибоко, самовіддано, пристрасно кількох людей. Хоча саме такий образ романтичних почуттів нерідко формують у нас сучасні фільми, книги і пісні. Це неминуче призводить до психічних зрушень, ставить людину перед складною альтернативою. Кохання, а отже і шлюб, вимагають передусім концентрації уваги людини лише на одному об’єкті, на одній особі.\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2975:sekrety-micnogo-podruzhzhja-abo-jak-vybraty-sobi-cholovika-chy-druzhynu&catid=37:spilnota-cat&Itemid=49',5333,NULL,1,1,26),
(5793,'P','2016-01-08',0,NULL,'Note','«ПРИВІТАЙ БІЙЦЯ З РІЗДВОМ ХРИСТОВИМ»','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6819,200,ae4f8e.jpg\" alt=\"\" width=\"200\" height=\"151\" />Із великою приємністю повідомляємо батькам дітей &ndash; вихованців катехитичної школи при парафії св. Миколая на Аскольдовій Могилі усіх вікових категорій, про нову святкову акцію</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Запрошуємо дітей та батьків підготувати власними руками&nbsp;<strong>Різдвяну листівку з привітаннями</strong>&nbsp;українським воїнам, що змушені зустрічати величне свято Народження нашого Спасителя в окопах на Сході Україні чи в госпіталях. Це може бути малюнок, витинанка, аплікація чи будь-що інше, у що ви вкладете тепло ваших щирих душ.</span></p>','<p style=\"text-align: center;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>РІЗДВЯНА АКЦІЯ ДЛЯ ДІТЕЙ КАТЕХИТИЧНОЇ ШКОЛИ</strong></span></p>\r\n<p style=\"text-align: center;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>ПАРАФІЇ СВ. МИКОЛАЯ НА АСКОЛЬДОВІЙ МОГИЛІ</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Із великою приємністю повідомляємо батькам дітей &ndash; вихованців катехитичної школи при парафії св. Миколая на Аскольдовій Могилі усіх вікових категорій, про нову святкову акцію</span></p>\r\n<p style=\"text-align: center;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>&laquo;ПРИВІТАЙ БІЙЦЯ З РІЗДВОМ ХРИСТОВИМ&raquo;.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6819,200,ae4f8e.jpg\" alt=\"\" width=\"200\" height=\"151\" />Запрошуємо дітей та батьків підготувати власними руками <strong>Різдвяну листівку з привітаннями</strong> українським воїнам, що змушені зустрічати величне свято Народження нашого Спасителя в окопах на Сході Україні чи в госпіталях. Це може бути малюнок, витинанка, аплікація чи будь-що інше, у що ви вкладете тепло ваших щирих душ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Діти, які візьмуть участь в акції, у день Різдва Христового, а також в інші недільні дні після свята <strong>обов&rsquo;язково отримають у церкві чудовий різдвяний подарунок</strong>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Підготовлені листівки з привітаннями просимо віддавати <strong>с. Меланії Сидорик.</strong> На конверті чи самій листівці потрібно обов&rsquo;язково зазначити ім&rsquo;я дитини та її вік.</span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Батьківська рада</span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">За довідками телефонуйте: 067&nbsp;277 1059, Наталя Пуряєва</span></p>',NULL,5336,5,1,NULL,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5794,'P','2016-01-13',1,NULL,'Rss','«Українське суспільство нагадує підлітка», – Глава УГКЦ','<p><strong><img src=\"http://api.oranta.org/images/stories/svyatoslav_blog.jpg\" width=\"250\" height=\"173\" alt=\"svyatoslav_blog\" style=\"margin: 10px; float: right;\" />Українське суспільство зараз нагадує підлітка, який відкриває в собі нові сили і здібності свого організму, але точно не знає, що з ними робити, заявив Глава Української Греко-Католицької Церкви Блаженніший Святослав.</strong></p>\n<p>В інтерв\'ю виданню&nbsp;<a target=\"_blank\" href=\"http://news.ugcc.ua/go/aHR0cDovL252LnVhL3Vrci5odG1s\">«Новое время»</a>, яке готується до друку, він зазначив, що цей процес дуже позитивний, але таїть у собі багато небезпек.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=2990:2016-01-13-08-36-23&catid=27:gk-v-ukrajini-cat&Itemid=57',5392,NULL,1,1,26),
(5795,'P','2015-12-23',1,NULL,'Note','«Перемагай байдужість та здобувай мир»','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6820,200,FB_IMG_1450864150494.jpg\" alt=\"\" width=\"200\" height=\"95\" />В листі Святіший Отець пише:&laquo;Бог не є байдужим! Богові важливе людство, Бог його не покидає! На початку нового року мені б хотілося доповнити цим моїм глибоким переконанням побажання щедрих благодатей та миру, на знак надії, задля майбутнього кожної людини, кожної сім&rsquo;ї, народу і країни світу, як також Глав Держав та Урядів і провідників релігій. Не втрачаймо надії, що 2016 рік побачить всіх нас рішуче й довірливо на різних рівнях заанґажованими у здійснення справедливості та будування миру. Так, мир є Божим даром і ділом людей. Мир &ndash; це Божий дар, але ввірений усім людям, які покликані його здійснити&raquo;.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6820,200,FB_IMG_1450864150494.jpg\" alt=\"\" width=\"200\" height=\"95\" />Мир у усіх землях. Марш миру у Ювілей Милосердя</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&laquo;Перемагай байдужість та здобувай мир&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">1 січня 2016 р. 14.00, від Бессарабської площі до Майдану незалежності в Києві.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">В День миру, проголошений бл. Папою Павлом VI, в багатьох містах світу на вулиці свої міст виходять люди, щоб засвідчити своє волю до миру, та згадати про країни де йдуть війни.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Спільнота святого Егідія організовує Марш миру &laquo;Мир в усіх землях&raquo; в Києві, Львові і Івано-Франківську. Ми починаємо свій рік з побажання миру Україні, та всім землям, де йде війна.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Папа Франциск лист на Всесвітній день миру 2016 р. озаглавив &laquo;Перемагай байдужість та здобувай мир&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6820,200,PacemInTerris.jpg\" alt=\"\" width=\"141\" height=\"200\" />В листі Святіший Отець пише:&laquo;Бог не є байдужим! Богові важливе людство, Бог його не покидає! На початку нового року мені б хотілося доповнити цим моїм глибоким переконанням побажання щедрих благодатей та миру, на знак надії, задля майбутнього кожної людини, кожної сім&rsquo;ї, народу і країни світу, як також Глав Держав та Урядів і провідників релігій. Не втрачаймо надії, що 2016 рік побачить всіх нас рішуче й довірливо на різних рівнях заанґажованими у здійснення справедливості та будування миру. Так, мир є Божим даром і ділом людей. Мир &ndash; це Божий дар, але ввірений усім людям, які покликані його здійснити&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">У цій же перспективі Ювілей Милосердя став запрошенням для Церкви молитись і трудитися над тим, щоб &laquo;кожен християнин розвивав смиренне та співчутливе серце, здатне звіщати і свідчити милосердя&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Запрошуємо всіх розпочати Новий рік кроком миру, та долучитися до організації і поширення запрошення. З усіх питань ви можете зв&rsquo;язатися за телефоном <a href=\"tel:0675028589\" target=\"_blank\">0675028589</a>, або електронною поштою <a href=\"mailto:SantEgidio@ukr.net\" target=\"_blank\">SantEgidio@ukr.net</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Посилання у FB: <a href=\"https://www.facebook.com/events/426972080837533/\" target=\"_blank\">https://www.facebook.com/events/426972080837533/</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Користуючись нагодою, бажаємо Вам божої благодаті та щедрот у Новому році Милосердя.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">З повагою,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Юрій Ліфансе,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&laquo;Спільнота святого Егідія&raquo;</span></p>',NULL,5337,5,1,NULL,42);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5796,'P','2016-01-09',0,NULL,'Note','Розклад Різдвяних Богослужінь 2016 р. Б., в УГКЦ  св. Миколая Чудотворця на Аскольдовій могилі 6-9 січня 2016 р..','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,6839,200,IMG_4181.JPG\" alt=\"\" width=\"200\" height=\"133\" /></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>7 січня, день Різдва Господа Бога і Спаса нашого Ісуса Христа</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08:15 &ndash; Божественна Літургія</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">10:00 &ndash; Божественна Літургія</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">12:45- Різдвяний обід в катехитичному залі для біженців, безхатченків, потребуючих парафіян</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18:00 &ndash; Божественна Літургія. Коляда</span></p>','<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6839,200,IMG_4181.JPG\" alt=\"\" width=\"200\" height=\"133\" />6 січня, Надвечір&rsquo;я Різдва Христового</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08:15 &ndash; Царські Часи</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">09:00 &ndash; Вечірня з Божественною Літургією Св. Василія Великого.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">17:00 &ndash; Велике повечір&rsquo;я з Литією. Коляда</span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">7 січня, день Різдва Господа Бога і Спаса нашого Ісуса Христа</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08:15 &ndash; Божественна Літургія</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">10:00 &ndash; Божественна Літургія</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">12:45- Різдвяний обід в катехитичному залі для біженців, безхатченків, потребуючих парафіян</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18:00 &ndash; Божественна Літургія. Коляда</span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">8 січня, Собор Пресвятої Богородиці</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08:15&ndash; Утрення</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">09:00 &ndash; Божественна Літургія</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18:00 &ndash; Божественна Літургія</span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">9 січня, Св ап., первомч. й архидиякона Стефана</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08:30 &ndash; Утрення</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">09:00 &ndash; Божественна Літургія</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18:00 &ndash; Божественна Літургія</span></p>',NULL,5356,5,1,NULL,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5797,'P','2015-12-30',1,NULL,'Rss','Молодь парафії на зустрічі ТЕЗЕ','<p><strong><img style=\"margin: 10px; float: left;\" alt=\"IMG_2895\" height=\"188\" width=\"250\" src=\"http://oranta.org/images/stories/IMG_2895.jpg\" />Короткий щоденник молодіжної групи паломників</strong></p>\n<p>24 грудня 2015 р. Б. по обіді група паломників, молодь парафії св. Миколая Мирлікійського з Аскольдової Могили, вирушила зі Львова на зустріч ТЕЗЕ у Валенсії і тримала напрямок до Лейпцига, а це практично тисяча кілометрів.</p>\n<p>25 грудня 2015р. Б. Різдво Христове зустріли молитвою в Дрездені, прибули о 8 годині ранку. Була двогодинна екскурсія містом, цікаві зустрічі.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2985:molod-parafiji-na-zustrichi-teze&catid=25:parafija-cat&Itemid=27',5357,NULL,1,1,26),
(5798,'P','2015-12-30',1,NULL,'Rss','Апостольський Нунцій: Я не боюся викликів, я боюся держав без викликів','<p><strong><img style=\"margin: 10px; float: right;\" alt=\"Claudio\" height=\"139\" width=\"250\" src=\"http://oranta.org/images/stories/Claudio.jpg\" />Новий Папський Нунцій в Україні архиєпископ Клаудіо Ґуджеротті на Святвечір побував у Львові&nbsp;та&nbsp;<a href=\"http://www.credo-ua.org/2015/12/149082\">очолив Пастирку у Львівській катедрі</a>. Цього ж дня ієрарх зустрівся з львівськими журналістами. Публікуємо частину розмови з владикою Ґуджеротті.</strong></p>\n<p>Нунцій розповів&nbsp;<a href=\"http://www.credo-ua.org/\">CREDO</a> про те, як дає собі раду з переїздами, як живе та якими будуть його перші кроки в служінні для України.\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2984:apostolsky-nunci-ja-ne-bojusja-vyklykiv-ja-bojusja-derzhav-bez-vyklykiv',5358,NULL,1,1,26),
(5799,'P','2015-12-30',1,NULL,'Rss','Святкування таїнства Христа або як відзначали Різдво перші християни','<p><img src=\"http://oranta.org/images/stories/kno.jpg\" width=\"165\" height=\"250\" alt=\"kno\" style=\"margin: 10px; float: left;\" />Цю нотатку пишу як наслідок спостережень кількох останній років, які стосуються літургійних святкувань особливо тих, які стосуються святкувань Різдва Христового.</p>\n<p>Вже кількасот років, а деякі церкви (наприклад вірменська) навіть кільканадцятьсот років святкують певні важливі для християнства події з життя Спасителя у різні дні. У випадку святкування Різдва Христового та інших свят, які беруть свій відлік саме від цієї події, різниця у святкуваннях є приблизно два тижні, тоді як різниця святкування Воскресіння Христового та залежних від цього свята церковних святкувань може навіть у певні роки збігатися або ж навпаки складати місяць і більше різниці.&nbsp;</p>\n<p>Всі церкви свідомі цих проблем, які склалась історично, вони розуміють необхідність свідчення єдності християн саме зараз, але перші кроки назустріч насправді даються не так легко.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2983:svjatkuvannja-tajinstva-hrysta-abo-jak-vidznachaly-rizdvo-pershi-hrystyjany&catid=27:gk-v-ukrajini-cat&Itemid=57',5359,NULL,1,1,26),
(5800,'P','2016-02-17',1,NULL,'Rss','Іспит совісті для дорослих','<p><strong><em><img style=\"margin: 10px; float: right;\" alt=\"10_1332770760\" height=\"151\" width=\"250\" src=\"http://api.oranta.org/images/stories/10_1332770760.jpg\" />Вступні запитання</em></strong></p>\n<p>Коли я востаннє сповідався?</p>\n<p>Чи була моя сповідь дійсна?</p>\n<p>Чи я не забув або чи не приховав свідомо тяжкого гріха?</p>\n<p>Чи виконав покуту?\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3028:ispyt-sovisti-dlja-doroslyh&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5452,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5801,'P','2015-12-26',1,NULL,'Rss','Я тебе так люблю, що… не купую подарунків!','<p><img style=\"margin: 10px; float: right;\" alt=\"d1414494590\" height=\"172\" width=\"250\" src=\"http://oranta.org/images/stories/d1414494590.jpg\" />Коли ми обтяжуємо себе надмірною кількістю предметів&nbsp;— стаємо їхніми невільниками. Внаслідок цього вони перестають нам служити й ми самі починаємо служити їм. Тяжко працюємо, аби заробити на нові меблеві стінки й шафи, а зрештою, на більше помешкання, яке замість бути домом стає просто магазином, який вміщає щоразу більшу кількість предметів…</p>\n<p>Наша країна проходить фазу наркотичного оп’яніння споживацтвом, із глибокою переконаністю: тим, що нас відділяє від прекрасного і щасливого життя, є «ще оцього не купили». Абсолютний пріоритет становить здобування фінансових засобів для отримання матеріальних благ. Цю систему мислення (а&nbsp;радше реагування) бездумно практикують чимало людей.\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2982:ja-tebe-tak-ljublju-shcho-ne-kupuju-podarunkiv&catid=37:spilnota-cat&Itemid=49',5360,NULL,1,1,26),
(5802,'P','2015-12-25',1,NULL,'Rss','Вгору піднесімо серця!','<p><img style=\"margin: 10px; float: left;\" alt=\"138410\" height=\"167\" width=\"250\" src=\"http://oranta.org/images/stories/138410.jpg\" />Наближається кінець року. Багато хто намагається підбити підсумки року, що минає. Це природно, ба більше, кожний мудрий підприємець навіть наприкінці кожного робочого дня рахує, скільки заробив чи втратив за день і чому. Властиво, тільки дотримуючись такої процедури, можна надіятися на успіх.</p>\n<p>Журналісти телебачення, радіо чи друкованих ЗМІ в цей останній місяць року нагадують нам про позитивні чи негативні події - у такий спосіб підсумовують рік. Про наступний рік говорять дуже обережно, пробують робити прогноз.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2981:vgoru-pidnesimo-sercja&catid=28:tema-nomera-cat&Itemid=2',5361,NULL,1,1,26),
(5803,'P','2015-12-25',1,NULL,'Rss','Яке Різдво святкуємо? Кілька думок про святкову дату...','<p><img src=\"http://oranta.org/images/stories/rizdvo-2014.jpg\" width=\"250\" height=\"178\" alt=\"rizdvo-2014\" style=\"margin: 10px; float: right;\" />Святе Письмо не розповідає нам про точну дату народження Ісуса. Але воно вказує нам на значення часу, у якому відбулася ця важлива подія. Читаємо у Євангелії від Луки: «У ці дні вийшов наказ від Кесаря Августа, щоб переписати всю землю».</p>\n<p>Цей лаконічний вступ до розповіді про народження Христа вислужений Папа Венедикт XVI у своїй богословській книзі «Дитинство Ісуса» пояснює, що таким чином святий Лука хоче вказати на те, як сталося так, що це народження відбулося у Вифлеємі.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2980:jake-rizdvo-svjatkujemo-kilka-dumok-pro-svjatkovu-datu&catid=32:gk-svit-cat&Itemid=58',5362,NULL,1,1,26),
(5804,'P','2015-12-25',1,NULL,'Rss','Різдвяне послання Urbi et Orbi Папи Франциска ','<p><img style=\"margin: 10px; float: left;\" alt=\"51b91e9f3a5\" height=\"166\" width=\"250\" src=\"http://oranta.org/images/stories/51b91e9f3a5.jpg\" />Браття і сестри, благодатного вам Різдва!<o:p></o:p></p>\n<p>Христос народився для нас,&nbsp;<em>ликуймо в день нашого спасіння!</em><o:p></o:p></p>\n<p>Відчинімо наші серця, щоби прийняти благодать цього дня, яким є Він Сам: Ісус – це світлий «день», який зійшов над небокраєм людства. День милосердя, в якому Бог Отець об’явив людству Свою безмежну ніжність. День світла, який розсіває пітьму страху й тривоги. День миру, в якому стає можливим зустрітися, вести діалог і, насамперед, примиритися. День радості: «великої радості» для найменших та смиренних і для всього люду (пор. Лк 2,10).<o:p></o:p></p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2979:rizdvjane-poslannja-urbi-et-orbi-papy-francyska-&catid=28:tema-nomera-cat&Itemid=2',5363,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5805,'P','2015-12-24',1,NULL,'Rss','Звернення Блаженнішого Святослава щодо святкування Різдва в Рік Божого милосердя','<p><img src=\"http://oranta.org/images/stories/shevchuk_rizdvo2015.jpg\" width=\"250\" height=\"173\" alt=\"shevchuk_rizdvo2015\" style=\"margin: 10px; float: right;\" />Отець і Глава Української Греко-Католицької Церкви Блаженніший Святослав закликав усіх вірних Церкви наповнити серця та домівки гостинністю до осіб, які потребують нашого милосердя, — вимушених переселенців, вдів, сиріт і напівсиріт, поранених, хворих чи звільнених з полону воїнів і волонтерів.<strong>&nbsp;</strong></p>\n<p>Про це йдеться у&nbsp;<a href=\"http://ugcc.ua/official/official-documents/zvernennya/2015/zvernennya_blazhenn%D1%96shogo_svyatoslava_shchodo_svyatkuvannya_r%D1%96zdva_v_r%D1%96k_bozhogo_miloserdya_75631.html\">Зверненні</a> Предстоятеля Церкви щодо святкування Різдва в Рік Божого милосердя до вірних УГКЦ, особливо тих, які об’єднані в різні мирянські спільноти та організації в парафіях.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2978:zvernennja-blazhennishogo-svjatoslava-shchodo-svjatkuvannja-rizdva-v-rik-bozhogo-myloserdja&catid=28:tema-nomera-cat&Itemid=2',5364,NULL,1,1,26),
(5806,'P','2015-12-23',1,NULL,'Rss','«Обличчя живої Церкви»: «Наше завдання і наш виклик – бути живими»','<p><img style=\"margin: 10px; float: left;\" alt=\"shevchuk_kobzar\" height=\"173\" width=\"250\" src=\"http://oranta.org/images/stories/shevchuk_kobzar.jpg\" />Ісус Христос, покликаючи апостолів до особливої місії, дав їм дуже чітке завдання: бути носіями і будівничими Царства Божого на землі, тими, хто поширює Боже Євангеліє у світі. Кожен віруючий християнин, згідно зі своїм покликанням, є апостолом. Якщо ми зустріли живого Бога у своїй парафії, то в нас є природним бажання допомогти зустріти Його тим, які ще Його не побачили.</p>\n<p>Про це говорить Отець і Глава УГКЦ Блаженніший Святослав у документальному фільмі «Обличчя живої Церкви», який відтепер доступний для перегляду в мережі Інтернет. Додамо, фільм офіційно представили під час роботи Собору УГКЦ «Жива парафія – місце зустрічі із живим Христом» в Івано-Франкіську у серпні 2015 року.</p>\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2977:2015-12-23-16-11-27&catid=28:tema-nomera-cat&Itemid=2',5365,NULL,1,1,26),
(5807,'P','2015-12-23',1,NULL,'Rss','Нунцій Клаудіо Ґуджеротті: Папа попросив мене бути знаком розради для українців   Джерело: CREDO','<p><em><strong><img style=\"margin: 10px; float: right;\" alt=\"654597\" height=\"181\" width=\"250\" src=\"http://oranta.org/images/stories/654597.jpg\" /></strong>Новий Апостольський Нунцій архиєпископ Клаудіо Ґуджеротті, який&nbsp;<a href=\"http://www.credo-ua.org/2015/12/148368\">нещодавно прибув в Україну</a>, розповів </em><a href=\"http://www.credo-ua.org/\"><em>CREDO</em></a><em> про свої робочі плани та виклики, а також про мрії та побажання.</em></p>\n<p><strong>— Ваше Високопреосвященство, з яким настроєм Ви їхали в Україну, адже у нас нині непроста ситуація?</strong></p>\n<p>— З одного боку, я відчув радість. Я працював багато років разом із українським народом і для нього, та з його пастирями тоді, коли від 1985 до 2002 р. виконував своє служіння у Ватикані при Конгрегації Східних Церков. То були роки виходу з підпілля для нашої католицької спільноти. Зокрема, я займався вихованням кліру (формацією духовенства). То були роки великого ентузіазму. Тепер я прибуваю до України зраненої, і це завдає мені болю і тривожить. Війна — це знищення, це безкінечне джерело зла, болю, понівеченої пам’яті. Провокувати війну — тяжкий гріх. Християни не можуть бути ініціаторами війни, хоч би ким вони були. За це вони відповідатимуть перед Богом.\n',NULL,'http://oranta.org/index.php?option=com_content&view=article&id=2976:nunci-klaudio-gudzherotti-papa-poprosyv-mene-buty-znakom-rozrady-dlja-ukrajinciv-dzherelo-credo&catid=32:gk-svit-cat&Itemid=58',5366,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5808,'P','2016-01-05',1,NULL,'Rss','Різдвяне послання Блаженнішого Святослава','<p><img style=\"margin: 10px; float: left;\" alt=\"vertep_2016\" height=\"167\" width=\"250\" src=\"http://api.oranta.org/images/stories/vertep_2016.jpg\" />Високопреосвященним і Преосвященним Архиєпископам та Митрополитам, боголюбивим єпископам, всечесному духовенству, преподобному монашеству, возлюбленим братам і сестрам, в Україні та на поселеннях у світі сущим</p>\n<p align=\"right\"><em>Слава на висотах Богу і на землі мир</em><em> </em><em>людям Його благовоління! </em>(Лк. 2, 14).<o:p></o:p></p>\n<p>Христос народився! Славімо Його!<o:p></o:p></p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=2987:rizdvjane-poslannja-blazhennishogo-svjatoslava&catid=28:tema-nomera-cat&Itemid=2',5378,NULL,1,1,26),
(5809,'P','2016-01-05',1,NULL,'Rss','Християнська символіка нашого Святого Вечора – Надвечір’я Різдва Христового','<p><img style=\"margin: 10px; float: right;\" alt=\"ArticleImage\" height=\"171\" width=\"250\" src=\"http://api.oranta.org/images/stories/ArticleImage.jpg\" />Наближається Перший Святий Вечір або Свята Вечеря перед Різдвом Христовим. Часто-густо, дотримуючись звичаїв Святого Вечора, у своїх хатах навіть не замислюємося над їхнім значенням. Часом чуємо різні дивовижні пояснення того чи іншого звичаю, який скидається скоріше на магію чи поганство, аніж на вираз християнських переконань нашого люду.</p>\n<p>Тож спробуймо пригадати християнське тлумачення того, що майже кожен з нас чинитеме в часі Святої Вечері.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=2986:hrystyjanska-symvolika-nashogo-svjatogo-vechora--nadvechirja-rizdva-hrystovogo&catid=27:gk-v-ukrajini-cat&Itemid=57',5379,NULL,1,1,26),
(5810,'P','2015-12-30',1,NULL,'Rss','Молодь парафії на зустрічі ТЕЗЕ','<p><strong><img style=\"margin: 10px; float: left;\" alt=\"IMG_2895\" height=\"188\" width=\"250\" src=\"http://api.oranta.org/images/stories/IMG_2895.jpg\" />Короткий щоденник молодіжної групи паломників</strong></p>\n<p>24 грудня 2015 р. Б. по обіді група паломників, молодь парафії св. Миколая Мирлікійського з Аскольдової Могили, вирушила зі Львова на зустріч ТЕЗЕ у Валенсії і тримала напрямок до Лейпцига, а це практично тисяча кілометрів.</p>\n<p>25 грудня 2015р. Б. Різдво Христове зустріли молитвою в Дрездені, прибули о 8 годині ранку. Була двогодинна екскурсія містом, цікаві зустрічі.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=2985:molod-parafiji-na-zustrichi-teze&catid=25:parafija-cat&Itemid=27',5380,NULL,1,1,26),
(5811,'P','2015-12-30',1,NULL,'Rss','Апостольський Нунцій: Я не боюся викликів, я боюся держав без викликів','<p><strong><img style=\"margin: 10px; float: right;\" alt=\"Claudio\" height=\"139\" width=\"250\" src=\"http://api.oranta.org/images/stories/Claudio.jpg\" />Новий Папський Нунцій в Україні архиєпископ Клаудіо Ґуджеротті на Святвечір побував у Львові&nbsp;та&nbsp;<a href=\"http://www.credo-ua.org/2015/12/149082\">очолив Пастирку у Львівській катедрі</a>. Цього ж дня ієрарх зустрівся з львівськими журналістами. Публікуємо частину розмови з владикою Ґуджеротті.</strong></p>\n<p>Нунцій розповів&nbsp;<a href=\"http://www.credo-ua.org/\">CREDO</a> про те, як дає собі раду з переїздами, як живе та якими будуть його перші кроки в служінні для України.\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=2984:apostolsky-nunci-ja-ne-bojusja-vyklykiv-ja-bojusja-derzhav-bez-vyklykiv',5381,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5812,'P','2015-12-30',1,NULL,'Rss','Святкування таїнства Христа або як відзначали Різдво перші християни','<p><img src=\"http://api.oranta.org/images/stories/kno.jpg\" width=\"165\" height=\"250\" alt=\"kno\" style=\"margin: 10px; float: left;\" />Цю нотатку пишу як наслідок спостережень кількох останній років, які стосуються літургійних святкувань особливо тих, які стосуються святкувань Різдва Христового.</p>\n<p>Вже кількасот років, а деякі церкви (наприклад вірменська) навіть кільканадцятьсот років святкують певні важливі для християнства події з життя Спасителя у різні дні. У випадку святкування Різдва Христового та інших свят, які беруть свій відлік саме від цієї події, різниця у святкуваннях є приблизно два тижні, тоді як різниця святкування Воскресіння Христового та залежних від цього свята церковних святкувань може навіть у певні роки збігатися або ж навпаки складати місяць і більше різниці.&nbsp;</p>\n<p>Всі церкви свідомі цих проблем, які склалась історично, вони розуміють необхідність свідчення єдності християн саме зараз, але перші кроки назустріч насправді даються не так легко.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=2983:svjatkuvannja-tajinstva-hrysta-abo-jak-vidznachaly-rizdvo-pershi-hrystyjany&catid=27:gk-v-ukrajini-cat&Itemid=57',5382,NULL,1,1,26),
(5813,'P','2015-12-26',1,NULL,'Rss','Я тебе так люблю, що… не купую подарунків!','<p><img style=\"margin: 10px; float: right;\" alt=\"d1414494590\" height=\"172\" width=\"250\" src=\"http://api.oranta.org/images/stories/d1414494590.jpg\" />Коли ми обтяжуємо себе надмірною кількістю предметів&nbsp;— стаємо їхніми невільниками. Внаслідок цього вони перестають нам служити й ми самі починаємо служити їм. Тяжко працюємо, аби заробити на нові меблеві стінки й шафи, а зрештою, на більше помешкання, яке замість бути домом стає просто магазином, який вміщає щоразу більшу кількість предметів…</p>\n<p>Наша країна проходить фазу наркотичного оп’яніння споживацтвом, із глибокою переконаністю: тим, що нас відділяє від прекрасного і щасливого життя, є «ще оцього не купили». Абсолютний пріоритет становить здобування фінансових засобів для отримання матеріальних благ. Цю систему мислення (а&nbsp;радше реагування) бездумно практикують чимало людей.\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=2982:ja-tebe-tak-ljublju-shcho-ne-kupuju-podarunkiv&catid=37:spilnota-cat&Itemid=49',5383,NULL,1,1,26),
(5814,'P','2015-12-25',1,NULL,'Rss','Вгору піднесімо серця!','<p><img style=\"margin: 10px; float: left;\" alt=\"138410\" height=\"167\" width=\"250\" src=\"http://api.oranta.org/images/stories/138410.jpg\" />Наближається кінець року. Багато хто намагається підбити підсумки року, що минає. Це природно, ба більше, кожний мудрий підприємець навіть наприкінці кожного робочого дня рахує, скільки заробив чи втратив за день і чому. Властиво, тільки дотримуючись такої процедури, можна надіятися на успіх.</p>\n<p>Журналісти телебачення, радіо чи друкованих ЗМІ в цей останній місяць року нагадують нам про позитивні чи негативні події - у такий спосіб підсумовують рік. Про наступний рік говорять дуже обережно, пробують робити прогноз.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=2981:vgoru-pidnesimo-sercja&catid=28:tema-nomera-cat&Itemid=2',5384,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5815,'P','2015-12-25',1,NULL,'Rss','Яке Різдво святкуємо? Кілька думок про святкову дату...','<p><img src=\"http://api.oranta.org/images/stories/rizdvo-2014.jpg\" width=\"250\" height=\"178\" alt=\"rizdvo-2014\" style=\"margin: 10px; float: right;\" />Святе Письмо не розповідає нам про точну дату народження Ісуса. Але воно вказує нам на значення часу, у якому відбулася ця важлива подія. Читаємо у Євангелії від Луки: «У ці дні вийшов наказ від Кесаря Августа, щоб переписати всю землю».</p>\n<p>Цей лаконічний вступ до розповіді про народження Христа вислужений Папа Венедикт XVI у своїй богословській книзі «Дитинство Ісуса» пояснює, що таким чином святий Лука хоче вказати на те, як сталося так, що це народження відбулося у Вифлеємі.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=2980:jake-rizdvo-svjatkujemo-kilka-dumok-pro-svjatkovu-datu&catid=32:gk-svit-cat&Itemid=58',5385,NULL,1,1,26),
(5816,'P','2015-12-25',1,NULL,'Rss','Різдвяне послання Urbi et Orbi Папи Франциска ','<p><img style=\"margin: 10px; float: left;\" alt=\"51b91e9f3a5\" height=\"166\" width=\"250\" src=\"http://api.oranta.org/images/stories/51b91e9f3a5.jpg\" />Браття і сестри, благодатного вам Різдва!<o:p></o:p></p>\n<p>Христос народився для нас,&nbsp;<em>ликуймо в день нашого спасіння!</em><o:p></o:p></p>\n<p>Відчинімо наші серця, щоби прийняти благодать цього дня, яким є Він Сам: Ісус – це світлий «день», який зійшов над небокраєм людства. День милосердя, в якому Бог Отець об’явив людству Свою безмежну ніжність. День світла, який розсіває пітьму страху й тривоги. День миру, в якому стає можливим зустрітися, вести діалог і, насамперед, примиритися. День радості: «великої радості» для найменших та смиренних і для всього люду (пор. Лк 2,10).<o:p></o:p></p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=2979:rizdvjane-poslannja-urbi-et-orbi-papy-francyska-&catid=28:tema-nomera-cat&Itemid=2',5386,NULL,1,1,26),
(5817,'P','2015-12-24',1,NULL,'Rss','Звернення Блаженнішого Святослава щодо святкування Різдва в Рік Божого милосердя','<p><img src=\"http://api.oranta.org/images/stories/shevchuk_rizdvo2015.jpg\" width=\"250\" height=\"173\" alt=\"shevchuk_rizdvo2015\" style=\"margin: 10px; float: right;\" />Отець і Глава Української Греко-Католицької Церкви Блаженніший Святослав закликав усіх вірних Церкви наповнити серця та домівки гостинністю до осіб, які потребують нашого милосердя, — вимушених переселенців, вдів, сиріт і напівсиріт, поранених, хворих чи звільнених з полону воїнів і волонтерів.<strong>&nbsp;</strong></p>\n<p>Про це йдеться у&nbsp;<a href=\"http://ugcc.ua/official/official-documents/zvernennya/2015/zvernennya_blazhenn%D1%96shogo_svyatoslava_shchodo_svyatkuvannya_r%D1%96zdva_v_r%D1%96k_bozhogo_miloserdya_75631.html\">Зверненні</a> Предстоятеля Церкви щодо святкування Різдва в Рік Божого милосердя до вірних УГКЦ, особливо тих, які об’єднані в різні мирянські спільноти та організації в парафіях.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=2978:zvernennja-blazhennishogo-svjatoslava-shchodo-svjatkuvannja-rizdva-v-rik-bozhogo-myloserdja&catid=28:tema-nomera-cat&Itemid=2',5387,NULL,1,1,26),
(5818,'P','2016-01-11',1,NULL,'Rss','Любомир Гузар: 25 грудня чи 7 січня? Переконаний, всі християни повинні святкувати Різдво разом','<p><em><img style=\"margin: 10px; float: right;\" alt=\"6806\" height=\"159\" width=\"250\" src=\"http://api.oranta.org/images/stories/6806.jpg\" />Колишній предстоятель УГКЦ, архієпископ, кардинал Любомир Гузар ніколи не боявся говорити правди – про державу, владу, ворога, українців…</em></p>\n<p><em>«Нічого не бачу, нічого не чую. Зате хоч добре виглядаю», - із щирою посмішкою зустрічає Блаженнійший «Главком» в резиденції під Києвом. Незважаючи на поважний вік – у лютому Гузару виповниться 83 роки – він має активну громадянську позицію, слідкує за подіями, що відбуваються в країні, та завжди готовий дати влучну оцінку і пораду.</em>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=2988:ljubomyr-guzar-25-grudnja-chy-7-sichnja-perekonany-vsi-hrystyjany-povynni-svjatkuvaty-rizdvo-razom&catid=28:tema-nomera-cat&Itemid=2',5388,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5819,'P','2015-12-30',1,NULL,'Rss','Апостольський Нунцій: Я не боюся викликів, я боюся держав без викликів','<p><strong><img src=\"http://api.oranta.org/images/stories/Claudio.jpg\" width=\"250\" height=\"139\" alt=\"Claudio\" style=\"margin: 10px; float: right;\" />Новий Папський Нунцій в Україні архиєпископ Клаудіо Ґуджеротті на Святвечір побував у Львові&nbsp;та&nbsp;<a href=\"http://www.credo-ua.org/2015/12/149082\">очолив Пастирку у Львівській катедрі</a>. Цього ж дня ієрарх зустрівся з львівськими журналістами. Публікуємо частину розмови з владикою Ґуджеротті.</strong></p>\n<p>Нунцій розповів&nbsp;<a href=\"http://www.credo-ua.org/\">CREDO</a> про те, як дає собі раду з переїздами, як живе та якими будуть його перші кроки в служінні для України.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=2984:apostolsky-nunci-ja-ne-bojusja-vyklykiv-ja-bojusja-derzhav-bez-vyklykiv&catid=27:gk-v-ukrajini-cat&Itemid=57',5389,NULL,1,1,26),
(5820,'P','2016-01-13',0,NULL,'Note','Зустріч молоді на Меланки','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6873,200,192976.jpg\" alt=\"\" width=\"200\" height=\"118\" />Молодь - це коли тобі 16+.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Молодь - це коли ти ще не одружений.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Молодь - це коли ти досі молодий.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">P.S. Хто хоче долучитися до організації свята звертатись до о. &nbsp;Івана&nbsp;<a href=\"tel:%28050-607-19-16\" target=\"_blank\">(050-607-19-16</a>) та с. Меланії (095-33-95-013).</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6873,200,192976.jpg\" alt=\"\" width=\"200\" height=\"118\" />Молодь - це коли тобі 16+.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Молодь - це коли ти ще не одружений.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Молодь - це коли ти досі молодий.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">P.S. Хто хоче долучитися до організації свята звертатись до:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">о. &nbsp;Івана&nbsp;<a href=\"tel:%28050-607-19-16\" target=\"_blank\">(050-607-19-16</a>)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">с. Меланії (095-339-50-13)</span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">17:30 &ndash; Велика Вечірня з Литією</span></strong><br /><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18:10 &ndash; Подячна Літургія</span></strong><br /><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">19:00 &ndash; Спільна коляда і зустріч молоді</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Вишиванка, забавки, гостинці, улюблені страви заохочуються.</span></p>',NULL,5390,5,1,NULL,17),
(5821,'P','2016-01-13',1,NULL,'Rss','Не Папи змінюють Церкву і серця, а молитва','<p><img style=\"margin: 10px; float: left;\" alt=\"pope-francis_8\" height=\"141\" width=\"250\" src=\"http://api.oranta.org/images/stories/pope-francis_8.jpg\" />Молитва чинить чуда, не даючи серцю закам’яніти і забути про набожність. На це вказав Папа Франциск - молитва вірних змінює Церкву, що Церква прямує вперед не завдяки Папам, єпископам чи священикам, а завдяки святим.</p>\n<p>Проповідник коментував біблійну розповідь про молитву старозавітної Анни, яка випросила у Бога сина Самуїла, та про священика Елі, який спостерігаючи за її молитвою, спочатку подумав що вона нетвереза й намагався прогнати її зі святині, а пізніше, довідавшись, що причиною її стану є журба та горе, поблагословив.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=2991:ne-papy-zminjujut-cerkvu-i-sercja-a-molytva&catid=32:gk-svit-cat&Itemid=58',5394,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5822,'P','2016-01-13',1,NULL,'Rss','Україну треба міняти поступово','<p><strong><img style=\"margin: 10px; float: right;\" alt=\"venedykt_espreso\" height=\"141\" width=\"250\" src=\"http://api.oranta.org/images/stories/venedykt_espreso.jpg\" />Владика Венедикт Алексійчук, єпископ УГКЦ, колишній ігумен Святоуспенської Унівської лаври поділився своїми думками щодо особистості, розвитку країни та налагодження зв\'язків між людьми у інтерв\'ю телеканалу \"Еспресо\"</strong><strong></strong></p>\n<p><strong>Про поступовість розвитку України:</strong></p>\n<p>В житті нічого не буває миттєво, лише в казці. Якщо взяти останній відрізок історії України – то все було потрібно. Потрібна була Революція на граніті, Помаранчева Революція, потрібна Революція Гідності. Ми бачимо, що кожна з них – це немовби сходинка.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=2992:ukrajinu-treba-minjaty-postupovo&catid=28:tema-nomera-cat&Itemid=2',5395,NULL,1,1,26),
(5823,'P','2016-01-14',1,NULL,'Rss','Обрізання Господнє','<p><em><img src=\"http://api.oranta.org/images/stories/obrizannja.jpg\" width=\"250\" height=\"246\" alt=\"obrizannja\" style=\"margin: 10px; float: left;\" />Лк. 2,20-21, 40-52. </em><em> </em><em>\"Чого ви мене шукали? Хіба не знали, що я маю бути при справах Отця мого?</em>\"</p>\n<p>Сьогоднішнє свято поєднує в собі два моменти з життя Спасителя: Обрізання Ісуса Христа за законом Мойсея та перебування дванадцятилітнього Ісуса у Єрусалимському храмі.</p>\n<p>Подія найменування Ісуса Христа поступово відкриває нам особу Спасителя через його вчинки. Ісус, як законодавець, перший виконує закон, підчиняється йому прикладом свого життя. Він виконує те, до чого не був зобов’язаний, щоб цим вчинком показати послух своєму Отцеві і повагу до Нього.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=2993:obrizannja-gospodnje&catid=28:tema-nomera-cat&Itemid=2',5396,NULL,1,1,26),
(5824,'P','2016-01-14',1,NULL,'Note','Біля церкви на Аскольдовій могилі сплюндрували меморіальну дошку Слави Стецько','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6880,200,464646.jpg\" alt=\"\" width=\"200\" height=\"145\" />У ніч з 12 на 13 січня 2016 року біля церкви на Аскольдовій могилі у Києві двоє невідомих вчинили акт вандалізму і облили зеленою рідиною білу меморіальну дошку знаної та шанованої у світі видатної української громадської та політичної діячки, Голови ОУН(р), Президента Антибільшовицького Блоку Народів, народного депутата ВР України трьох скликань, засновниці Конгресу Українських Націоналістів Слави Стецько.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,6880,200,464646.jpg\" alt=\"\" width=\"200\" height=\"145\" />У ніч з 12 на 13 січня 2016 року біля церкви на Аскольдовій могилі у Києві двоє невідомих вчинили акт вандалізму і облили зеленою рідиною білу меморіальну дошку знаної та шанованої у світі видатної української громадської та політичної діячки, Голови ОУН(р), Президента Антибільшовицького Блоку Народів, народного депутата ВР України трьох скликань, засновниці Конгресу Українських Націоналістів Слави Стецько.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Камери відеонагляду, що встановлені біля церкви, зафіксували, як двоє вандалів-зловмисників обливають мармурову меморіальну дошку української патріотки і поспішно втікають у невідомому напрямку.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&laquo;Ми вже звернулися до поліції з вимогою розслідувати цей кричущий акт вандалізму, передали відеоматеріали. Напередодні свята Василія Великого цей зухвалий злочин виглядає особливо цинічно і є, беззаперечно, кримінальним злочином проти всієї &nbsp;громади, за ініціативою якої на знак пошани до Слави Стецько була встановлена ця меморіальна дошка&raquo;,&nbsp; &ndash; розповів настоятель церкви на Аскольдовій могилі Ігор Онишкевич.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&laquo;У цьому варварському злочині та наругою над світлою пам&rsquo;яттю видатної націоналістки вбачається страх наших ворогів. Слава Стецько прожила таке гідне життя в служінні Україні, що вороги нашої держави бояться її навіть після того, як вона відійшла у вічність. Сподіваємося, що в найближчий час правоохоронці знайдуть зловмисників, адже це вже не перший випадок вандалізму. Раніше вандали вже замахнулися на цю дошку, коли пошкодили її барельєф і також облили фарбою хрест на могилі Героїв Крут. Тоді злочинців так і не знайшли. Цього разу сподіваємося на більш професійні дії правоохоронних органів&raquo;, &ndash; зазначив Голова Конгресу Українських Націоналістів Степан Брацюнь та наголосив, що Конгрес &nbsp;буде тримати хід розслідування інциденту під особливим контролем.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Прес-служба КУН</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>Фото:&nbsp; Юлія Бішоп</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><a href=\"http://ugcc.kiev.ua/\">http://ugcc.kiev.ua/</a></span></p>',NULL,5397,5,1,NULL,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5825,'P','2016-01-14',1,NULL,'Note','Різдвяний дарунок','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6881,200,12528248_427035050827722_453142365_o.jpg\" alt=\"\" width=\"200\" height=\"150\" />Парафіяльна служба милосердя &laquo;Карітас Печерськ&raquo; отримала сімдесят Різдвяних подарунків від християн зі Швейцарії, які достав МБФ &laquo;Тріумф Серця&raquo;. Усі діти парафії які намалювали Різдвяну листівку і написали вітального листа воїну, який захищає нас від російської агресії на Донбасі, отримали свої подарунки.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6881,200,12528248_427035050827722_453142365_o.jpg\" alt=\"\" width=\"200\" height=\"150\" />Парафіяльна служба милосердя &laquo;Карітас Печерськ&raquo; отримала сімдесят Різдвяних подарунків від християн зі Швейцарії, які достав МБФ &laquo;Тріумф Серця&raquo;. Усі діти парафії які намалювали Різдвяну листівку і написали вітального листа воїну, який захищає нас від російської агресії на Донбасі, отримали свої подарунки.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6881,200,12528508_427034867494407_1893732927_o.jpg\" alt=\"\" width=\"200\" height=\"150\" />Дякуємо дітям зі Швейцарії, які трудились і жертвували на ці дарунки. У кожному пакуночку є лист від дитини, яка збирала цей подарунок, то ж заохочуємо наших діток досконало вивчати іноземну мову, щоб гарно подякувати за і запросити доброчинців з Європейських країн відвідати Аскольдову Могилу, Київ та Україну.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ілля для &laquo;Оранти&raquo;</span></p>',NULL,5398,5,1,NULL,19),
(5826,'P','2016-01-14',1,NULL,'Rss','«Подія Обрізання - прообраз Таїнства Хрещення, що є дверима до джерел благодаті Духа Святого»','<p><img style=\"margin: 10px; float: right;\" alt=\"dsc_0010_be496\" height=\"167\" width=\"250\" src=\"http://api.oranta.org/images/stories/dsc_0010_be496.jpg\" />14 січня ми святкуємо три події в один день. Найперше, сьогодні збігає восьмий день після народження Ісуса. А за законом Мойсея, кожне хлоп’я ізраїльське отримувало своє ім’я восьмого дня після народження, після обряду обрізання. Тож восьмиденне хлоп’ятко Ісус обрізується і отримує ім’я, під яким ми його знаємо.</p>\n<p>Про це сказав під час проповіді Глава УГКЦ Блаженніший Святослав 14 січня в парафії Святого великомученика Юрія, що у Василькові біля Києві.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=2994:2016-01-14-21-10-33&catid=28:tema-nomera-cat&Itemid=2',5399,NULL,1,1,26),
(5827,'P','2016-01-15',1,NULL,'Rss',' Молодіжна коляда на Аскольдовій Могилі','<p><img style=\"margin: 10px; float: left;\" alt=\"797354\" height=\"188\" width=\"250\" src=\"http://api.oranta.org/images/stories/797354.jpg\" />13 січня, в навечір’я Нового Року за юліанським календарем, близько 30 молодих людей з парафій та молодіжних спільнот міста Києва зібрались на подячну Літургію та спільну коляду у храмі Святого Миколая Чудотворця УГКЦ на Аскольдовій могилі в м. Києві.<strong></strong></p>\n<p>Зустріч розпочалась спільною молитвою (вечірнім богослужінням та Божественною Літургією), якою всі присутні дякували Богові за прожитий рік та просили благословення, особливо для молоді, у новому році.</p>\n','','http://api.oranta.org/index.php?option=com_content&view=article&id=2995:-molodizhna-koljada-na-askoldovi-mogyli&catid=25:parafija-cat&Itemid=27',5400,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5828,'P','2016-01-15',1,NULL,'Rss','Празник папи Сильвестра','<p><img style=\"margin: 10px; float: right;\" alt=\"5646\" height=\"188\" width=\"250\" src=\"http://api.oranta.org/images/stories/5646.jpg\" />15 січня 2015 р., в день Cвятого отця нашого Сильвестра, папи Римського, при Парафії Святого Миколая, що на Аскольдовій могилі у Києві, святкували малий празник.<strong></strong></p>\n<p>Зазначимо, що При парафії є каплиця, у якій знаходяться реліквії Єпископа Сильвестра. Вшановуючи його пам’ять духовенство та вірні щороку в особливий спосіб моляться у церкві, а опісля у каплиці, на місці його поховання. Саме з цієї нагоди духовенство та вірні прибули на Аскольдову могилу щоб спільно вшанувати цей день.</p>\n','','http://api.oranta.org/index.php?option=com_content&view=article&id=2996:praznyk-papy-sylvestra&catid=25:parafija-cat&Itemid=27',5401,NULL,1,1,26),
(5829,'P','2016-01-15',1,NULL,'Rss','Обірвана кулею нота','<p><strong><img style=\"margin: 10px; float: left;\" alt=\"fullsize\" height=\"250\" width=\"194\" src=\"http://api.oranta.org/images/stories/fullsize.jpg\" />Рідкісні документи допомогли розплутати деталі загибелі подільського Шопена — композитора Миколи Леонтовича</strong></p>\n<p>Більшість із тих, хто співає в Америці колядку Миколи Леонтовича, навіть не підозрює, що народилася ця мелодія в Україні. Напевно, мало хто з них знає, що перше виконання «Щедрика» відбулося&nbsp; 1916 року, коли хор Київського університету під диригуванням Миколи Леонтовича кілька разів викликали на «біс». Тонка мелодія з простими словами і українською первозданністю перелетіла за океан 1921 року, вже після цинічного вбивства композитора. Згодом її «американізували» у Carol of the Bells — колядки дзвіночків. І відтоді ця щедрівка лунає на американських теле- і радіоканалах, у храмах і костелах, за святковими столами і на корпоративах. Вона має понад півсотні інтерпретацій, перекладена кількома мовами світу. 100 років, співаючи «Щедрик», світ не згадує про Леонтовича, якого зухвало застрелили&nbsp; в батьківській хаті на Поділлі.\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=2997:obirvana-kuleju-nota&catid=37:spilnota-cat&Itemid=49',5402,NULL,1,1,26),
(5830,'P','2016-01-16',1,NULL,'Rss','Проповідь Йоана Хрестителя','<p><em><img style=\"margin: 10px; float: right;\" alt=\"56465\" height=\"250\" width=\"200\" src=\"http://api.oranta.org/images/stories/56465.jpg\" />Початок Євангелії Ісуса Христа, Сина Божого. 2. Як написано в пророка Ісаї: «Ось я посилаю мого посланця перед тобою, який приготує тобі дорогу. 3. Голос вопіющого в пустині: Готуйте Господеві дорогу, вирівняйте стежки його», 4. - так виступив Йоан, христивши у пустині та проповідувавши хрищення покаяння на прощення гріхів. 5. І виходили до нього - вся країна Юдейська та всі єрусалимляни, христились від нього в ріці Йордані й визнавали гріхи свої. 6. Йоан одягнений був в одежу з верблюжого волосу й носив ремінний пояс на своїх бедрах, а їв сарану й мед дикий. 7. І проповідував, кажучи: «Слідом за мною іде сильніший від мене, що йому я недостойний, нахилившись, розв\'язати ремінця його сандалів. 8. Я вас христив водою, а він христитиме Святим Духом.»</em></p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=2998:propovid-oana-hrestytelja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5403,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5831,'P','2016-01-19',1,NULL,'Note','Розклад Богослужінь на свято Святого Богоявлення Ісуса Христа','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><img style=\"float: left;\" src=\"image,6887,200,7879.jpg\" alt=\"\" width=\"200\" height=\"131\" />18.01.2016 понеділок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\">Надвечір&rsquo;я Богоявлення (строгий піст)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\">17:00 Велике Повечір&rsquo;я з Йорданським Освяченням води&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\">19.01.2016 вівторок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\">Святе Богоявлення. Хрещення ГНІХ</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\">08:00 Утреня, Літургія св. Йоана Золотоустого, Йорданське Освячення води</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6887,200,7879.jpg\" alt=\"\" width=\"200\" height=\"131\" /></span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><span style=\"font-family: arial, helvetica, sans-serif;\">Христос Рождається!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18.01.2016 понеділок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Надвечір&rsquo;я Богоявлення (строгий піст)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08:00 Царські Часи, Літургія св. Василія Великого з вечірнею</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">17:00 Велике Повечір&rsquo;я з Йорданським Освяченням води&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">19.01.2016 вівторок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Святе Богоявлення. Хрещення ГНІХ</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08:00 Утреня, Літургія св. Йоана Золотоустого, Йорданське Освячення води</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18:00 Божественна Літургія за здоров&rsquo;я</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Йорданські відвідини -звертайтесь до парафіяльних священиків о. Ігор, о. Анатолій, о. Олексій, о. Іван</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Служба Божа відправляється щодня 08:00;18:00. Середа, п&rsquo;ятниця, субота за спочилих з Парастасом</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- неділя Служба Божа 08:00 зі співом мирян, 10:00 хор, 12:30 для дітей, 18:00 співає дяк</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">23.01 субота 09:00 поминальна Служба Божа, Панахида Розповідь о. Сергія про бл. Марту, прикладання до її мощей молитви оздоровлення</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">катехизація дітей (довідка с. Меланія )</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- діти парафії малюють Різдвяну листівку поздоровлення воїнам Захисникам України в АТО в Донбасі.</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- продовжуємо збірку на лікування поранених в АТО (волонтерське служіння Роксоляна)</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- продовжуємо зголошуватись до волонтерського служіння в парафії, заповняємо анкету</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- потребуємо допомоги в журналістському служінні,</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- цільова пожертва на випуск першого номера 2016 р. Б. Оранта</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- подяка доброчинцям і організаторам зустрічі молоді на Старий 2016 Новий Рік, святкування малого празника св Сильвестра Папи в Крипті Сильвестра</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- споруджуємо Хрест Хрещення Аскольда над Дніпром 866-2016</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">парох Ігор митрофорний протопресвітер&nbsp;<a href=\"tel:%280504473157\" target=\"_blank\">+380504473157</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp;</span></p>',NULL,5404,5,1,NULL,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5832,'P','2016-01-22',1,NULL,'Rss','День соборності','<p><img style=\"margin: 10px; float: left;\" alt=\"125088\" height=\"170\" width=\"250\" src=\"http://api.oranta.org/images/stories/125088.jpg\" />Двадцяте століття залишило нам у спадок вершинні події минулого, які, віддаляючись, не втрачають своєї значущості та актуальності, потребуючи осмислення сконцентрованого в них історичного досвіду боротьби українства за свободу, незалежність, державність. До таких подій належить 22 січня 1919 року - день ухвали Акту Злуки Української Народної Республіки і Західно-Української Народної Республіки. Цій даті судилося навічно вкарбуватися в історію України величним національним святом - Днем Соборності.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=2999:den-sobornosti&catid=28:tema-nomera-cat&Itemid=2',5405,NULL,1,1,26),
(5833,'P','2016-01-24',1,NULL,'Rss','Мир мусить бути принциповим - Блаженніший Любомир','<p><img style=\"margin: 10px; float: right;\" alt=\"103111\" height=\"250\" width=\"194\" src=\"http://api.oranta.org/images/stories/103111.jpg\" />На думку Блаженнішого Любомира, бажання примирення має бути взаємним, а&nbsp;підписання угод без стійкого миру не сприятиме припиненню війни. Любомир Гузар не вірить, що домовленності діятимуть за теперішніх умов/</p>\n<p>На думку емерита Української греко-католицької церкви Любомира Гузара, домовленності з Росією можна укладати лише після встановлення миру, який у свою чергу можливий лише тоді, коли обидві сторони його щиро прагнутимуть.</p>\n<p>\"Я не бачу можливості примирення чи миру. Мир можливий лише тоді, коли є дві сторони, які направду миру прагнуть. Назагал так виглядає, що ми прагнемо миру, що ми не хочемо війни ані з Росією, ані з кимось іншим, але це одностороннє бажання. А якщо нема такого самого щирого бажання миру з іншої сторони, то говорити про мир є мрія і більш нічого. Правдиве замирення — справа обох сторін. Таке рішення не може бути одностороннім\", - заявив Гузар у розмові з DT.UA.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3000:myr-musyt-buty-pryncypovym-blazhennishy-ljubomyr&catid=37:spilnota-cat&Itemid=49',5406,NULL,1,1,26),
(5834,'P','2016-01-24',1,NULL,'Note','Парафіяльні оголошення','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,6890,200,123763.jpg\" alt=\"\" width=\"150\" height=\"200\" />27.01 середа&nbsp; Відання празника Богоявлення, прип. отців у Синаї і Раїті повбиваних, Пам&rsquo;ять переставлення св. рівноапостольної Ніни, просвітительки Грузії.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Священики парафії чикають запрошення на Йорданське відвідання родин</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>29 січня п&rsquo;ятниця&nbsp; Поклін чесним оковам св.ап.Петра</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>08:00 Божественна Літургія,</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>11:00 панахида на могилі Героїв Крут</strong></span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6890,200,123763.jpg\" alt=\"\" width=\"150\" height=\"200\" />Служба Божа відправляється щодня 08:00;18:00:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- середа, п&rsquo;ятниця, субота за спочилих з Парастасом</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- неділя Служба Божа 08:00 зі співом мирян,10:00 хор,12:30 для дітей ,18:00 співає дяк</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>27.01 середа&nbsp; Відання празника Богоявлення, прип. отців у Синаї і Раїті повбиваних, Пам&rsquo;ять переставлення св. рівноапостольної Ніни, просвітительки Грузії.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Священики парафії чикають запрошення на Йорданське відвідання родин</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>29 січня п&rsquo;ятниця&nbsp; Поклін чесним оковам св.ап.Петра</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>08:00 Божественна Літургія,</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>11:00 панахида на могилі Героїв Крут</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">катехизація дітей (довідка с. Меланія):</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- діти парафії малюють Різдвяну листівку поздоровлення воїнам Захисникам України в АТО в Донбасі. Для дітей приготовані дарунки;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- продовжуємо збірку на лікування поранених в АТО (волонтерське служіння Роксолана);</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- продовжуємо зголошуватись до волонтерського служіння в парафії, заповняємо анкету, газета Оранта;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- потребуємо допомоги в журналістському служінні;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- цільова пожертва на випуск першого номера 2016 р. Оранта;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- споруджуємо Хрест Хрещення Аскольда над Дніпром 866-2016</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Многая літа! </strong>голові паламарського служіння п. Володимиру в День Народження</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Подяка Василю, Василю, Віктору які постарались про воду на освячення паламарю Андрію, водію Олегу що привезли джерельну воду</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">парох Ігор Онишкевич, митрофорний протопресвітер<a href=\"tel:%280504473157\" target=\"_blank\"> +38&nbsp;050&nbsp;447 31 57</a></span></p>',NULL,5407,5,1,NULL,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5835,'P','2016-01-24',1,NULL,'Rss','Гвинтики машини, що розвалилася. Блаженніший Святослав ставить діагноз українському суспільству','<p><img style=\"margin: 10px; float: left;\" alt=\"51_main_ukr\" height=\"157\" width=\"250\" src=\"http://api.oranta.org/images/stories/51_main_ukr.jpg\" />Блаженніший Святослав, глава Української греко-католицької церкви, розмірковує про хвороби українського суспільства, називає їхні причини і порівнює з недугами Європи.</p>\n<p>Руки, схрещені в замок на грудях, у психології означають спробу сховатися від несприятливої ситуації. Однак для священика цей жест неприйнятний, каже Святослав Шевчук, глава Української греко-католицької церкви (УГКЦ), у відповідь на прохання фотографа НВ скласти на грудях руки.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3001:gvyntyky-mashyny-shcho-rozvalylasja-blazhennishy-svjatoslav-stavyt-diagnoz-ukrajinskomu-suspilstvu&catid=28:tema-nomera-cat&Itemid=2',5408,NULL,1,1,26),
(5836,'P','2016-01-27',1,NULL,'Rss','Наче вчора…','<p><img style=\"float: right;\" alt=\"14234\" height=\"183\" width=\"250\" src=\"http://api.oranta.org/images/stories/14234.jpg\" />26 січня 1918 р. до Києва вступила 10 тисячна орда Михайла Муравйова, почався червоний терор. При штурмі міста застосовувалися отруйні гази, був проведений масовий артобстріл (до 15 тис. снарядів), в результаті якого було зруйновано будинок Грушевського. За наказом Муравйова, більшовицькі війська розстріляли у Києві близько 5 тис. мирних жителів, тероризували громадян УНР, був убитий київський митрополит Володимир.</p>\n<p>За даними дослідника Савченка В. А., під час знаходження Муравйова в Києві по місту їздив броньовик з написом «Смерть українцям!».</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3002:nache-vchora&catid=37:spilnota-cat&Itemid=49',5409,NULL,1,1,26),
(5837,'P','2016-01-28',1,NULL,'Rss','Бій під Крутами: як відрізнити історичну правду від міфу','<p><em><img style=\"margin: 10px; float: left;\" alt=\"123763\" height=\"250\" width=\"188\" src=\"http://api.oranta.org/images/stories/123763.jpg\" />Незважаючи на те, що бій під Крутами не мав визначального воєнного значення, для українського народу він став символом героїзму і жертовності молодого покоління у боротьбі за незалежність України.</em></p>\n<p>Тривалий час подробиці Крутянського бою залишалися невідомими і скоріше нагадували міф: проти багатотисячного більшовицького війська український уряд безжально кинув необстріляну молодь – студентів та гімназистів. Вони хоробро билися, але всі загинули у нерівному бою. І лише через багато десятиліть після виявлення окремих документів і публікацію численних спогадів з’явилася можливість відтворити обставини бою.</p>\n<p><strong>Проти «червоної чуми»</strong></p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3003:bi-pid-krutamy-jak-vidriznyty-istorychnu-pravdu-vid-mifu&catid=28:tema-nomera-cat&Itemid=2',5410,NULL,1,1,26),
(5838,'P','2016-01-28',1,NULL,'Rss','Чому було мало бійців, і як склалася доля тих, хто вижив','<p><em><img style=\"margin: 10px; float: right;\" alt=\"kruty\" height=\"187\" width=\"250\" src=\"http://api.oranta.org/images/stories/kruty.jpg\" />Похмурого ранку 29 січня 1918 року біля станції Крути почався бій, якому судилося стати наріжним каменем української міфології Визвольних змагань. Вже за два місяці, під час урочистого поховання були уперше сказані слова про українські Фермопіли і про небувалу самопожертву бійців Студентського куреня Січових стрільців. Пишаючись героїзмом студентів, урядовці тоді й словом не обмовилися про ще живих реальних героїв Крут – юнаків (курсантів) 1-ї Київської військової школи ім. Богдана Хмельницького.</em></p>\n<p><em>Автор: Дмитро Калинчук</em>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3004:chomu-bulo-malo-biciv-i-jak-sklalasja-dolja-tyh-hto-vyzhyv&catid=28:tema-nomera-cat&Itemid=2',5411,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5839,'P','2016-01-29',1,NULL,'Rss','Послання Папи Франциска на Великий Піст 2016','<p><img style=\"margin: 10px; float: left;\" alt=\"Francisco-cross\" height=\"162\" width=\"250\" src=\"http://api.oranta.org/images/stories/Francisco-cross.jpg\" />«Я милосердя прагну, а не жертвоприношення», – ці Христові слова про Небесного Отця з Євангелії від Матея стали темою Послання Папи Франциска на Великий Піст, присвяченого «ділам милосердя на ювілейному шляхові», представлення якого відбулося у вівторок, 26 січня 2016 року. На його початку Святіший Отець пригадує, що у буллі з нагоди проголошення Святого Року він висловив побажання, щоб «Чотиридесятниця цього ювілейного року була прожита якомога інтенсивніше, як ефективний час для того, щоб святкувати та досвідчити Боже милосердя».</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3005:poslannja-papy-francyska-na-velyky-pist-2016&catid=32:gk-svit-cat&Itemid=58',5412,NULL,1,1,26),
(5840,'P','2016-01-29',1,NULL,'Note','Пам\'яті Героїв Крут','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6896,200,2bd300.jpg\" alt=\"\" width=\"200\" height=\"112\" />Зазначимо, що сьогодні минає 98-ма річниця, від тоді, коли біля станції Крути, на залізничній ділянці Ніжин-Бахмач, в 130-ти кілометрах від Києва, за майбутнє України віддали життя близько 100 в основному молодих осіб. Бій тривав 6 годин, проте за цей час нашим воїнам вдалось знищити колії та мости, перешкодивши цим наступ агресора, та на 4 дні зупинити ворога. Як наголошують історики, цей героїчний бій та придушення заколоту в Києві дали змогу виграти Центральній Раді час, для того, щоб дійшло до підписання Берестейського мирного договору.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6896,200,2bd300.jpg\" alt=\"\" width=\"200\" height=\"112\" />29 січня 2016 року, у день пам&rsquo;яті героїв Крут, при парафії Святого Миколая Чудотворця УГКЦ, що на Аскольдові могилі у Києві, духовенство та вірні Київської архиєпархії молитовно вшанували українців, які взимку 1918 року героїчно віддали життя за Волю України.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Вшанувати пам\'ять героїчного подвигу українців, на Аскольдову могилу, до пам&rsquo;ятного хреста героям Крут прибули також Президент Петро Порошенко, Прем&rsquo;єр-міністр Арсеній Яценюк, Голова Верховної Ради України Володимир Гройсман, Голова КМДА Віталій Кличко, а також представники Кабінету Міністрів та Адміністрації Президента, а також учасники АТО та вірні УГКЦ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Всі вони після покладення квітів до підніжжя пам&rsquo;ятника вшанували героїв хвилиною мовчання. Опісля виконали Державний Гімн України, а воїни почесної варти пройшли урочистим маршем та з особистої зброї виконали шість пострілів в повітря.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Після цього вірні та духовенство УГКЦ, на чолі з митрофорним протоієреєм о. Ігорем Онишкевичем помолились панахиду за загиблими.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">По завершені молитви зі словом до присутніх звернувся о. Любомир Яворський, заступник керівника Департаменту Патріаршої курії УГКЦ у справах душпастирства силових структур України. В своєму слові священик навів паралель між героями, які віддали життя за Україну під Крутами, та героями, які віддали життя за нашу Вітчизну протягом останніх кривавих подій в Україні.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Зазначимо, що сьогодні минає 98-ма річниця, від тоді, коли біля станції Крути, на залізничній ділянці Ніжин-Бахмач, в 130-ти кілометрах від Києва, за майбутнє України віддали життя близько 100 в основному молодих осіб. Бій тривав 6 годин, проте за цей час нашим воїнам вдалось знищити колії та мости, перешкодивши цим наступ агресора, та на 4 дні зупинити ворога. Як наголошують історики, цей героїчний бій та придушення заколоту в Києві дали змогу виграти Центральній Раді час, для того, щоб дійшло до підписання Берестейського мирного договору.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Герої-мученики українського народу були поховані на Аскольдовій могилі у Києві. В знак цього там встановили пам&rsquo;ятний хрест про цю подію.</span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><a href=\"http://news.ugcc.ua/go/aHR0cDovL3VnY2Mua2lldi51YS8=\" target=\"_blank\">Прес-служба Київської архиєпархії</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><a href=\"http://news.ugcc.ua/\">http://news.ugcc.ua/</a></span></p>',NULL,5413,5,1,NULL,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5841,'P','2016-01-29',1,NULL,'Note','Наші герої тоді і сьогодні','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6897,200,IMG_0015.jpg\" alt=\"\" width=\"200\" height=\"157\" />На жаль, через століття, та історія повторюєте, сьогодні той самий ворог, ті ж самі події і така ж сама свідома молодь стала і стає на захист України. Їхньою відвагою захищений Київ і сучасні &laquo;Муравьови&raquo; не вбивають нас у столиці.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"text-indent: 0em; font-size: small; line-height: 1.3em; font-family: arial, helvetica, sans-serif;\">нова створити &laquo;Меморіал пам&rsquo;яті Героїв&raquo;, які віддали своє життя за Україну завершили день вшанування пам&rsquo;яті юних крутянців.</span></p>\r\n<div>&nbsp;</div>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6897,200,IMG_0015.jpg\" alt=\"\" width=\"200\" height=\"157\" />Сьогодні на Аскольдовій Могилі поминали те юнацтво, яке 98 років тому полягло за Вітчизну.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Полягло свідомо з любові до рідного краю, бо були добровольцями.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">На жаль, через століття, та історія повторюєте, сьогодні той самий ворог, ті ж самі події і така ж сама свідома молодь стала і стає на захист України. Їхньою відвагою захищений Київ і сучасні &laquo;Муравьови&raquo; не вбивають нас у столиці.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Сьогодні державні урядовці на чолі з Президентом Петром Порошенком разом з киянами, гостями, сучасними героями вшанували пам&rsquo;ять полеглих.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Петро Олексійович щиро молився на могилі студентів і їхньою пам&rsquo;яттю і святістю він має Боже Благословення врятувати, зберегти та утвердити у щасті, у благополуччі, у волі нашу Україну на віки у Європейському виборі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Числений мітинг-молитва &laquo;Просвіти&raquo; і прийнята постанова створити &laquo;Меморіал пам&rsquo;яті Героїв&raquo;, які віддали своє життя за Україну завершили день вшанування пам&rsquo;яті юних крутянців.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Вічна пам&rsquo;ять Героям.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ілля для &laquo;Оранти&raquo;</span></p>',NULL,5414,5,1,NULL,17),
(5842,'P','2016-01-31',1,NULL,'Note','Парафіяльні оголошення','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Служба Божа відправляється щодня 08:00;18:00:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- середа, п&rsquo;ятниця, субота за спочилих з Парастасом</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- неділя Служба Божа 08:00 зі співом мирян,10:00 хор,12:30 для дітей ,18:00 співає дяк</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>02.02 вівторок&nbsp; Прп. й богоносного Євтимія Великого</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Священики парафії чикають запрошення на Йорданське відвідання родин</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Служба Божа відправляється щодня 08:00;18:00:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- середа, п&rsquo;ятниця, субота за спочилих з Парастасом</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- неділя Служба Божа 08:00 зі співом мирян,10:00 хор,12:30 для дітей ,18:00 співає дяк</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>02.02 вівторок&nbsp; Прп. й богоносного Євтимія Великого</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Священики парафії чикають запрошення на Йорданське відвідання родин</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">катехизація дітей (довідка с.Меланія +38<a href=\"tel:0989285710\" target=\"_blank\">0989285710</a>)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- діти парафії малюють Різдвяну листівку поздоровлення войнам Захисникам України в АТО в Донбасі.Для дітей приготовані дарунки.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- молодь зголошується до о.Івана тел +38<a href=\"tel:0506071960\" target=\"_blank\">0506071960</a>; email: <a href=\"mailto:iwan_sokhan@yahoo.de\" target=\"_blank\">iwan_sokhan@yahoo.de</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- продовжуємо збірку на лікування поранених в АТО (волонтерське служіння Роксоляна)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ця збірка призначена для допомоги родині +Романа Капацій, був в полоні ,де зазнав жорстоких тортур бандитів, в тяжкому стані був звільнений через обмін полонених і не приходячи до свідомості помер в Київському госпіталі, залишилась дружина Ірина і 6 дітей, самий молодший син потребує лікування серця</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">поранений Москалець Сергій (Артіст) 5168 7572 9199 5431&nbsp; тел.+38<a href=\"tel:0507823228\" target=\"_blank\">0507823228</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">поранений Симоненко Євген (Скіф)&nbsp;&nbsp; 4149 4978 0832 4315&nbsp; тел+38<a href=\"tel:0988460904\" target=\"_blank\">0988460904</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">прошу читачів парафіяльної газети &laquo;Оранта&raquo; молитись і помогти вдовиною лептою</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; субота 6 лютого&nbsp; 11:00 збори Церковної Ради на Аскольдовій могилі розглядаємо фінансовий звіт парафії за 2015 рік</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">також розглянуться пропозиції про служіння ближньому в Рік Божого Милосердя</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- Тарасова ніч 8-9 березня,нічні чування з читання Кобзаря 22:00</span><br /><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"> - в неділю&nbsp; 31 березня проходить окрема парафіяльна збірка для лікування Анатолія, на трьох Службах Божих</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Многая літа! </strong>Наталі Пураєвій в День Народжен<strong>ня і </strong>всім доброчинцям парафії</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">парох Ігор Онишкевич, митрофорний протопресвітер<a href=\"tel:%280504473157\" target=\"_blank\"><strong>&nbsp;</strong><strong>+38&nbsp;050&nbsp;447 31 57</strong></a></span></p>',NULL,5415,5,1,NULL,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5843,'P','2016-01-31',1,NULL,'Rss','Освіта як національна ідея','<p><strong><img src=\"http://api.oranta.org/images/stories/osvita_new_zakon1.jpg\" width=\"250\" height=\"144\" alt=\"osvita_new_zakon1\" style=\"margin: 10px; float: right;\" /></strong><em>Необхідність реформування освіти й науки була очевидною, здається, для всіх. Але менш очевидними (як для професійної спільноти, так і для широкого загалу) виявилися конкретні напрями та методи, в яких реалізується реформа сьогодні. Скорочення фінансування науки і, як наслідок, відтік талановитої молоді за межі країни, зменшення кількості аудиторних годин в університетах, якість та об’єм викладання гуманітарних дисциплін, — про ці та інші пов’язані з реформою проблеми наша розмова з другим президентом Національного університету «Києво-Могилянська академія», а нині міністром освіти і науки України Сергієм Квітом.</em></p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3006:osvita-jak-nacionalna-ideja&catid=37:spilnota-cat&Itemid=49',5416,NULL,1,1,26),
(5844,'P','2016-02-01',1,NULL,'Rss','Підприємницький хист Андрея Шептицького','<p><strong><img style=\"margin: 10px; float: left;\" alt=\"5500B6\" height=\"141\" width=\"250\" src=\"http://api.oranta.org/images/stories/5500B6.jpg\" /></strong>Митрополит Андрей Шептицький був найзаможнішим греко-католиком, який створив на Галичині тіньовий уряд, що опікувався господарськими справами краю.</p>\n<p><strong>Християнська ФПГ</strong></p>\n<p>Коли 35-річний Роман Марія Александр граф на Шептицях Шептицький, більше відомий під своїм чернечим ім\'ям Андрей, 1900 року став на чолі греко-католицької церкви, йому довелося скласти дві присяги - на вірність Папі Римському Леву XIII і цісареві Австро-Угорської імперії Францові Йосифові.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3007:pidpryjemnycky-hyst-andreja-sheptyckogo&catid=37:spilnota-cat&Itemid=49',5417,NULL,1,1,26),
(5845,'P','2016-02-01',1,NULL,'Note','Пам\'ятаємо','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6901,200,54c863.jpg\" alt=\"\" width=\"200\" height=\"149\" />Кожен повинен хоча б на найкоротшу мить схилити голову перед пам\'ятником, присвяченим їхній світлій пам\'яті та вшанувати їх хвилиною мовчання.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отож пам\'ятаймо наших відважних героїв, без подвигу яких, сучасне українське буття могло б бути зовсім іншим!</span><br /><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Будьмо безмежно вдячні їм за життя, що вони нам подарували!Ф</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6901,200,54c863.jpg\" alt=\"\" width=\"200\" height=\"149\" />Люди, які не знають свого минулого - не мають майбутнього. Однак, ми повинні не тільки знати нашу історію, а й шанувати її. Отак і 29 січня 2016 року було вшановано пам\'ять українських героїв-мучеників, що віддали своє ще зовсім молоде життя біля станції Крути.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Безсумнівно, подвиг їх великий для українського народу, і тому, кожен з нас повинен увіковічнити його й назавжди залишити в наших душах і серцях.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Кожен повинен хоча б на найкоротшу мить схилити голову перед пам\'ятником, присвяченим їхній світлій пам\'яті та вшанувати їх хвилиною мовчання.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отож пам\'ятаймо наших відважних героїв, без подвигу яких, сучасне українське буття могло б бути зовсім іншим!</span><br /><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"> Будьмо безмежно вдячні їм за життя, що вони нам подарували!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Олександра Дел для &laquo;Оранти&raquo;</span></p>',NULL,5418,5,1,NULL,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5846,'P','2016-02-01',0,NULL,'Note','Запрошуємо молодь Аскольдової Могили на зустріч з Папою Франциском під час Світового Дня Молоді в Кракові','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6902,200,582369.jpg\" alt=\"\" width=\"200\" height=\"140\" />&laquo;Для мене особисто і для моєї дружини наша спільна учать у Світовому Дні Молоді в Кельні 2005 року стала фундаментальною подією в нашому житті. Хоч рішення взяти в ньому участь і було доволі спонтанним, та й добиралися ми з нашою невеликою групою практично автостопом, однак з плодів цієї зустрічі ми щедро черпаємо і по сьогоднішній день.&raquo;</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6902,200,582369.jpg\" alt=\"\" width=\"200\" height=\"140\" />У неділю 24 січня, в рамках духовного приготування до Світового Дня Молоді (СДМ), Молодіжна Комісія Київської Архиєпархії зібрала єпархіальну молодь на Аскольдові Могилі для спільної молитви на Божественній Літургії. У своїй проповіді в часі Літургії о. Іван Сохан, відповідальний за працю з молоддю на парафії, поділився власним досвідом участі у Світовому Дні Молоді у 2005 р. в м. Кельн (Німеччина), та заохотив парафіяльну молодь не бути пасивною, і не вагатися, а активно жити свої молоді роки та використовувати такі унікальні нагоди як Світові Дні Молоді для власного духовного, культурного та світоглядного збагачення.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&laquo;Для мене особисто і для моєї дружини наша спільна учать у Світовому Дні Молоді в Кельні 2005 року стала фундаментальною подією в нашому житті. Хоч рішення взяти в ньому участь і було доволі спонтанним, та й добиралися ми з нашою невеликою групою практично автостопом, однак з плодів цієї зустрічі ми щедро черпаємо і по сьогоднішній день. Окрім численних незабутніх моментів та вражень, саме на СДМ Боже провидіння звело нас з людьми (німецькою родиною, яка приймала нас вдома), які ось уже більше десяти років супроводжують нас на нашому життєвому шляху. Попри вікову та культурну різницю, ми стали близькими друзями та справжнім збагаченням один для одного. І сьогодні ми активно спілкуємось, зустрічаємось, підтримуємо одні одних, а головне &ndash; ділимось нашою вірою в Ісуса Христа!&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отож, закликаємо молодь Аскольдової могили відгукнутися на запрошення Папи Франциска і взяти участь у цьогорічному СДМ в Кракові!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Подібні зустрічі духовного приготування до СДМ проводяться не вперше, і не є останніми. Нижче подаємо додаткову інформацію стосовно СДМ та контактні дані відповідальних осіб для реєстрації груп та організації поїздки.</span></p>\r\n<p class=\"yiv8785649411msonormal\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>Наші контакти:</em></strong></span></p>\r\n<p class=\"yiv8785649411msonormal\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дзвони нам:&nbsp;096&nbsp;180 74 90, 093&nbsp;145 49 40 &ndash; Іванна Брацюнь</span></p>\r\n<p class=\"yiv8785649411msonormal\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Напиши нам:&nbsp;sdm2016kyiv@gmail.com</span></p>\r\n<p class=\"yiv8785649411msonormal\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Спільнота у&nbsp;fb: Разом з Києва до Кракова&nbsp;https://www.facebook.com/groups/1631110133826481/?fref=ts</span></p>\r\n<p class=\"yiv8785649411msonormal\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>Слідкуйте за новинами:</em></strong></span></p>\r\n<p class=\"yiv8785649411msonormal\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Офіційна веб-сторінка СДМ у Кракові:&nbsp;http://www.krakow2016.com/ua/</span></p>\r\n<p class=\"yiv8785649411msonormal\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Офіційний сайт Київської Архиєпархії УГКЦ:&nbsp;http://ugcc.kiev.ua/</span></p>\r\n<p class=\"yiv8785649411msonormal\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Новини УГКЦ, зокрема про СДМ:&nbsp;www.dyvensvit.org</span></p>\r\n<p class=\"yiv8785649411msonormal\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Сайт Патріаршого собору Воскресіння Христового у Києві:&nbsp;http://kyivsobor.ugcc.org.ua/</span></p>',NULL,5419,5,1,NULL,43);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5847,'P','2016-02-01',1,NULL,'Rss','Зустріч із земляком – спогади','<p><img style=\"margin: 10px; float: right;\" alt=\"aa73a3c2\" height=\"188\" width=\"250\" src=\"http://api.oranta.org/images/stories/aa73a3c2.jpg\" />Минулорічного літнього суботнього дня ми вперше зустріли у шпиталі земляка Василя. Зустріч була несподіваною але дуже щирою. Ми насправді зраділи, що і закарпатські чоловіки захищають свою рідну Землю. Розмова у нас виявилась дуже плідною, ми з сестрою стали саме тими слухачами які дуже потрібні були Василю.</p>\n<p>Василь поділився з нами лише тим, «що нам можна слухати». Служив, віримо що й досі служить живий і здоровий, в 41 батальйоні. На війну пішов добровольцем, щоб захищати рідну Україну, щоб його діти, дружина, родина жили у вільній країні. До поранення на передовій без ротацій був вісім місяців.\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3008:zustrich-iz-zemljakom--spogady&catid=25:parafija-cat&Itemid=27',5420,NULL,1,1,26),
(5848,'P','2016-02-02',1,NULL,'Rss','Проста реальність віри, або у пошуках власного шляху до Бога','<p><img style=\"margin: 10px; float: left;\" alt=\"758\" height=\"166\" width=\"250\" src=\"http://api.oranta.org/images/stories/758.jpg\" />Гортаючи церковний календар місяця лютого, можна побачити, що він багатий численними святими постатями, які передали нам найкращу спадщину, цінний скарб – приклад віри. Тут знаходимо великих проповідників та активних діячів Церкви: Василія Великого, Івана Золотоустого та Григорія Богослова, ревного аскета Єфрема Сирійського та захисника віри Максима Ісповідника. Всі вони сумлінно жили для Бога та відкривали Його кожного дня. Серед багатьох чеснот їх об’єднувала одна характерна риса – чуйність. Йдеться про те, що ці святі були уважними до голосу і дії Бога у світі, який їх оточував.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3009:prosta-realnist-viry-abo-u-poshukah-vlasnogo-shljahu-do-boga&catid=28:tema-nomera-cat&Itemid=2',5421,NULL,1,1,26),
(5849,'P','2016-02-03',1,NULL,'Note','«Від серця до серця»','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6906,200,image(1).jpeg\" alt=\"\" width=\"200\" height=\"149\" />У рік Божого Милосердя, вихованці катехитичної школи церкви святого Миколая, з благословення пароха Ігоря, отримали завдання, яке виконуватимуть продовж Великого посту, а саме: збиратимуть подарунок &laquo;Від серця до серця&raquo; для дітей, які втратили батька у цій війні. Молитва - розмова з Отцем, у якій парафіяльні діти просять за дитину, яка живе без батька, щоб Милосердний Господь допоміг двом серцям і навчив прощати, ділитися бути уважними до кожного.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6906,200,image(1).jpeg\" alt=\"\" width=\"200\" height=\"149\" />У часі Різдвяних свят, діти парафії церкви святого Миколая Чудотворця на Аскольдовій Могилі, малювали, підписували листівки для військовослужбовців, які захищають нашу державу він російського окупанта. Після завершення акції &laquo;Подаруй листівку воїнові&raquo;, кожна дитина, отримала подарунок від дітей християнської громади зі Швейцарії.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">У рік Божого Милосердя, вихованці катехитичної школи церкви святого Миколая, з благословення пароха Ігоря, отримали завдання, яке виконуватимуть продовж Великого посту, а саме: збиратимуть подарунок &laquo;Від серця до серця&raquo; для дітей, які втратили батька у цій війні. Молитва - розмова з Отцем, у якій парафіяльні діти просять за дитину, яка живе без батька, щоб Милосердний Господь допоміг двом серцям і навчив прощати, ділитися бути уважними до кожного.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Провадитиме цей проект голова катехитичної школи с. Меланія. Катехити, а також люди доброї волі теж братимуть активну участь у цій справі Божого милосердя. Парох заохочує дітей заощаджувати кишенькові кошти, які діти отримуватимуть від батьків. Заощаджені дітьми кошти підуть на необхідні речі для дитини, яка втратила батька. У планах закупити шкільне приладдя: ручку, олівець, зошит, кольорові олівці, книжку, тощо. Доповнюватиметься такий подарунок з парафіяльного фонду &laquo;Карітас-Печерськ&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Анна Онисько для &laquo;Оранти&raquo;</span></p>',NULL,5423,5,1,NULL,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5850,'P','2016-02-03',1,NULL,'Rss','«Слово про закон і Благодать»','<p><strong><em><img src=\"http://api.oranta.org/images/stories/413-14-2.jpg\" width=\"183\" height=\"250\" alt=\"413-14-2\" style=\"margin: 10px; float: right;\" />Іларіон Київський</em></strong></p>\n<p><strong>СЛОВО ПРО ЗАКОН І БЛАГОДАТЬ&nbsp;[1]</strong></p>\n<p>Про Закон [2], Мойсеєм [3] даний,</p>\n<p>і про Благодать [4] та Істину [5], в Ісусі Христі втілених,</p>\n<p>і як закон відійшов,</p>\n<p>і як Благодать та Істина всю землю наповнили,</p>\n<p>і віра на всі народи розпростерлася,</p>\n<p>і до нашого народу руського;</p>\n<p>і похвала кагану [6] нашому Володимиру [7], за якого ми охрещені були,</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3010:2016-02-03-09-32-53&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5424,NULL,1,1,26),
(5851,'P','2016-02-04',1,NULL,'Rss','Звернення Синоду Єпископів Києво-Галицького Верховного Архиєпископства УГКЦ до представників органів державної влади, політикуму та громадянського суспільства України','<p><img style=\"margin: 10px; float: left;\" alt=\"78532146\" height=\"187\" width=\"250\" src=\"http://api.oranta.org/images/stories/78532146.jpg\" />Ми, єпископи УГКЦ, зібравшись на черговому Священному Синоді, не можемо стояти осторонь нинішніх тривог і надій нашого суспільства, адже в центрі душпастирської турботи Церкви є і завжди буде спільне благо народу, справжнє добро людини, даровані їй Богом свобода і гідність.</p>\n<p>Наша країна має всі необхідні передумови та володіє всіма необхідними ресурсами, ? соціальними, інтелектуальними й трудовими, ? щоб запевнити гідне життя кожному громадянинові. Разом із тим у суспільстві повинні бути лідери, які, не витрачаючи намарно згадані ресурси, здатні брати на себе відповідальність за втілення прагнень українців.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3011:zvernennja-synodu-jepyskopiv-kyjevo-galyckogo-verhovnogo-arhyjepyskopstva-ugkc-do-predstavnykiv-organiv-derzhavnoji-vlady-politykumu-ta-gromadjanskogo-suspilstva-ukrajiny&catid=27:gk-v-ukrajini-cat&Itemid=57',5425,NULL,1,1,26),
(5852,'P','2016-02-04',1,NULL,'Rss','Маленький крок на зустріч до Милосердя','<p><img style=\"margin: 10px; float: right;\" alt=\"1254\" height=\"188\" width=\"250\" src=\"http://api.oranta.org/images/stories/1254.jpg\" />Після відвідування на при кінці січня поранених у госпіталі Прикордонної служби, та спілкування з пораненими Петром і Олександром нас дуже зворушили слова Петра: «Українці вважають, що ми захищаємо владу а не Україну, і Ви знаєте це так прикро слухати. Люди не усвідомлюють, що ми захищаємо там рідну землю, щоб ці події не розгорнулись в інших регіонах. У мене складається таке враження що ми інші, нас бояться, ми не потрібні і від цього стає дуже страшно».</p>\n<p>Після розмови із захисниками, ми засмутились…</p>\n','','http://api.oranta.org/index.php?option=com_content&view=article&id=3012:malenky-krok-na-zustrich-do-myloserdja&catid=29:propovidi-cat&Itemid=34',5426,NULL,1,1,26),
(5853,'P','2016-02-04',1,NULL,'Note','ЗВІТ №20 про роботу служби милосердя «Карітас-Печерськ» при парафії Церкви Миколая Чудотворця на Аскольдовій Могилі (грудень 2015 р.)','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><br />-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; відправлено подарунки від св.Миколая дітям засуджених матерів, залишеним під родинною опікою або в школах-інтернатах (17 посилок, див звіт №21, 1 посилка, див.звіт №22)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; надано допомогу засобами дитячої гігієни та дитячим одягом дітям ув&rsquo;язнених матерів у Будинок матері і дитини Чернігівської виправної колонії (3 посилки, див звіт №22)</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><br />Служба милосердя &laquo;Карітас&raquo; при парафії Церкви Миколая Чудотворця на Аскольдовій Могилі, зібравши пожертвувані парафіянами одяг чоловічий, жіночий та дитячий, взуття, іграшки дитячі та гуманітарну допомогу одягом, взуттям, постільною білизною та медичними приналежностями від благодійних фондів &laquo;Тріумф серця&raquo; і &laquo;Олива&raquo;, розподілила їх таким чином (записи в книзі розподілу гуманітарної допомоги):</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Уділили потребуючим мирянам (32 особи):</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash; 63 кг</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;куртки, пальто &ndash; 15 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;взуття &ndash; 14 пар</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;постіль: покривало &ndash; 1 шт., ковдра &ndash; 1 шт., подушки &ndash; 4 шт.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Надали матеріальну допомогу:</span></li>\r\n</ol>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">переселенцям з Донецької обл. (3 особи):</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash; 5 кг.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;куртки, пальто &ndash; 2 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;постіль: покривало &ndash; 1 шт., ковдра &ndash; 2 шт., подушки &ndash; 3 шт., плед &ndash; 1 шт.</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">потребуючим та безпритульним (17 осіб):</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash; 76 кг.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;куртки, пальто &ndash; 21 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;взуття &ndash; 5 пар</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; постіль: покривало &ndash; 2 шт., ковдра &ndash; 4 шт., подушки &ndash; 5 шт., плед &ndash; 4 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;постільна білизна &ndash; 1 компл.+5 од.</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">ув&rsquo;язненим жінкам в Лук&rsquo;янівський СІЗО в м.Києві</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash; 1 мішок + засоби гігієни для 1 особи</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Відправили гуманітарний вантаж:</span></li>\r\n</ol>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Благодійному фонду &ldquo;Сузір\'я&rdquo; в с.Іванкові Бориспільського р-ну Київської обл.:</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг чоловічий, жіночий, дитячий &ndash; 10 мішків</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;взуття &ndash; 1 мішок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; іграшки дитячі &ndash; 1 мішок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;постіль &ndash; 2 мішки</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Благодійному фонду &laquo;Завжди поруч&raquo; для бійців АТО та населення зони конфлікту на сході України:</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг чоловічий, жіночий, дитячий &ndash; 3 мішки</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;кава мелена натуральна &ndash; 1 мішок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;розчин дезинфекційний для рук &ndash; 5 коробок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;фартухи поліетиленові одноразові &ndash; 3 рулони</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;іграшки&nbsp; та книжки дитячі &ndash; 1 пакет</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Парафії УГКЦ в с.Голдашівці Козятинського р-ну Вінницької обл.:</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг дитячий, іграшки, книжечки дитячі від св.Миколая &ndash; 1 коробка.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Крім того:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; відправлено подарунки від св.Миколая дітям засуджених матерів, залишеним під родинною опікою або в школах-інтернатах (17 посилок, див звіт №21, 1 посилка, див.звіт №22)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; надано допомогу засобами дитячої гігієни та дитячим одягом дітям ув&rsquo;язнених матерів у Будинок матері і дитини Чернігівської виправної колонії (3 посилки, див звіт №22)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; надано допомогу засобами гігієни та одягом двом ув&rsquo;язненим жінкам Качанівської ВК (м.Харків) (2 посилки, див звіт №22)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; відправлено допомогу одягом сім&rsquo;ї (6 дітей) вимушених переселенців із зони конфлікту (1 посилка, див.звіт №22)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; відправлено допомогу продуктами і одягом рекрутованому до лав ЗСУ Сергію П., що виховувався серед Дітей Божого Милосердя при парафії Церкви Миколая Чудотворця на Аскольдовій Могилі (1 посилка, див.звіт №22)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; надано допомогу засобами гігієни та речами особистого вжитку двом ув&rsquo;язненим жінкам Качанівської ВК (м.Харків) (2 посилки, див звіт №23)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Роздано мирянам та потребуючим каву мелену &ndash; 0,5 мішка.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Робота виконана силами волонтерів: п.Мирослави, п.Людмили, п.Світлани, п.Оксани, п.Анни.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Висловлюємо щиру подяку жертводавцям і прикликаємо Боже благословення на їх родини.</span></p>',NULL,5427,5,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5854,'P','2016-02-05',1,NULL,'Rss','Брак спілкування','<p><img style=\"margin: 10px; float: left;\" alt=\"528456654\" height=\"165\" width=\"250\" src=\"http://api.oranta.org/images/stories/528456654.jpg\" />Однією з найбільших перешкод для нормального суспільного життя в Україні вважається брак довір’я.</p>\n<p>Кому вірити? Багато громадян, не довго думаючи, відповість – нікому.</p>\n<p>Це жахливий закид, який, якщо б він був беззастережно правдивий та стовідсотковий, робить суспільне спілкування цілковито неможливим, стає справжньою руїною суспільного ладу.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3013:brak-spilkuvannja&catid=28:tema-nomera-cat&Itemid=2',5428,NULL,1,1,26),
(5855,'P','2016-02-05',1,NULL,'Rss','Папа Франциск зустрінеться з Патріархом Кирилом','<p><img style=\"margin: 10px; float: right;\" alt=\"images\" height=\"159\" width=\"250\" src=\"http://api.oranta.org/images/stories/images.jpg\" />У п’ятницю, 5 лютого 2016 р., оприлюднено спільне комюніке Апостольської Столиці та Московського Патріархату, в якому читаємо:</p>\n<p>«Святий Престол та Московський Патріархат з радістю повідомляють, що завдячуючи Божій благодаті, Його Святість Папа Франциск та Його Святість Кирило, Патріарх Московський та всієї Русі, зустрінуться 12 лютого цього року. Зустріч відбудеться на Кубі, де Папа зупиниться на шляху до Мексики, і де Патріарх перебуватиме зі своїм офіційним візитом. Зустріч включатиме особисту розмову в міжнародному аеропорту Хосе Марті в Гавані та завершиться підписанням спільної декларації.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3014:papa-francysk-zustrinetsja-z-patriarhom-kyrylom&catid=32:gk-svit-cat&Itemid=58',5429,NULL,1,1,26),
(5856,'P','2016-02-05',1,NULL,'Rss','«Подумаймо, як виглядає сьогодні життя сучасних скитальців»','<p><img style=\"margin: 10px; float: left;\" alt=\"guzar_liubomyr_7376\" height=\"153\" width=\"250\" src=\"http://api.oranta.org/images/stories/guzar_liubomyr_7376.jpg\" />Часто так буває, що деякі уривки зі Святого Письма, зокрема із Євангелія, котрі описують певні події, ми сприймаємо поверхово, тобто ми їх не заперечуємо, але й не беремо собі до серця, не переживаємо того, що прочитали чи почули. Думаю, одним із таких текстів є, власне, розповідь про втечу до Єгипту.</p>\n<p>Останніми днями, на свята, ми згадували багато гарних подій, пов’язаних із життям Ісуса Христа в перші дні, місяці чи навіть роки Його перебування на землі. Сьогодні ми читали про те, що в певний момент обставини склалися так, що Йосиф мусів узяти Марію та Ісусика й утікати з ними до Єгипту.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3015:2016-02-05-19-57-26&catid=29:propovidi-cat&Itemid=34',5430,NULL,1,1,26),
(5857,'P','2016-02-07',1,NULL,'Note','Парафіяльні оголошення','<ul>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">збірка призначена для допомоги родині Романа Капацій, який був в полоні, де зазнав жорстоких тортур бандитів, в тяжкому стані був звільнений через обмін полоненими і не приходячи до свідомості помер в Київському госпіталі. Вдовою залишилась дружина Ірина і сиротами 6 дітей, самий молодший син потребує лікування серця</span></li>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">також просимо помогти вдовиною лептою на лікування п. Анатолія і Лесі Потіцької</span></li>\r\n</ul>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Служба Божа</strong> відправляється <strong><em>щодня</em></strong> <strong>08:00; 18:00</strong>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Середа, п&rsquo;ятниця, субота &ndash; за спочилих з Парастасом</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>неділя</em></strong> &ndash; Служба Божа</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>08:00</strong> зі співом мирян,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>10:00</strong> хор,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>12:30</strong> для дітей,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>18:00</strong> співає дяк</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>п&rsquo;ятниця 12 лютого 2016 р.Б.</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>Трьосвятителів</em></strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>Свя. вел. архиєреїв Василія Великого, Григорія Богослова і Йоана Золотоустого</em></strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>08:00Утрення09:00;18:00Служба Божа,на передодні відпр. Велика Вечірня</em></strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>ДО УВАГИ!</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; катехизація дітей (довідка с. Меланія 0989285710)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; діти парафії&nbsp; отримують скорбонки для Великопісної збірки родинної пожертви, що використається на Пасхальний дарунок дітям котрі втратили батьків в АТО</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; молодь зголошується до о. Івана (0506071960 / iwan_sokhan@yahoo.de / FB: Ivan Sokhan)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; продовжуємо збірку на лікування поранених в АТО (волонтерське служіння Роксоляна):</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">ця збірка призначена для допомоги родині Романа Капацій, який був в полоні, де зазнав жорстоких тортур бандитів, в тяжкому стані був звільнений через обмін полоненими і не приходячи до свідомості помер в Київському госпіталі. Вдовою залишилась дружина Ірина і сиротами 6 дітей, самий молодший син потребує лікування серця</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">також просимо помогти вдовиною лептою на лікування п. Анатолія і Лесі Потіцької</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; на зборах Церковної Ради&nbsp; розглянуто фінансовий звіт парафії за 2015 рік. На трьох недільних Службах Божих даний фінансовий буде проголошений громаді.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;подається план парафіяльного служіння ближньому в Ювілейний Рік Божого Милосердя</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5 березня 19:00 панахида пам&rsquo;яті генерала УПА Романа Шухевича і його побратимів на Оскольдовій могилі</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 8-9 березня 22:00 &ndash; Тарасова ніч, нічні чування з читанням Кобзаря</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 19 березня&nbsp; 17:00 панахида пам&rsquo;яті &nbsp;перепоховання студентів котрі полягли під Крутами, розвішання ікон</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Многая літа! </strong>Володимиру Гринькову, Анні Ониськів, в День Народження</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Подяка</strong> всім доброчинцям парафії, Павлу Михайловичу, архітектору Юрію Миколайовичу</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>парох Ігор Онишкевич, </em></strong><em>митр. протопресвітер</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">моб.<strong> 0504473157</strong></span></p>',NULL,5431,5,1,NULL,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5858,'P','2016-02-10',1,NULL,'Rss','Папа і московський патріарх, або чому Путіну потрібна зустріч в Гавані','<p><img style=\"margin: 10px; float: right;\" alt=\"0e6\" height=\"181\" width=\"250\" src=\"http://api.oranta.org/images/stories/0e6.jpg\" />Путін не обізнаний в теології і може бути зацікавлений в контактах \"свого\" патріарха з Папою як доказі відсутності ізоляції Росії.</p>\n<p>Майбутня зустріч в аеропорту Гавани Папи Римського Франциска і патріарха Московського Кирила, поза всяким сумнівом, може бути віднесена до подій по-справжньому історичних. Адже Папа і патріарх не зустрічалися взагалі ніколи, це - перша така зустріч глав церков. Але зустріч, яку можна було легко спрогнозувати після обрання Кирила московським патріархом.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3016:papa-i-moskovsky-patriarh-abo-chomu-putinu-potribna-zustrich-v-gavani&catid=32:gk-svit-cat&Itemid=58',5432,NULL,1,1,26),
(5859,'P','2016-02-11',1,NULL,'Rss','Група «Першого грудня» підтримала ініціативу бізнесу розпочати діалог довкола суспільного договору','<p><strong><img style=\"margin: 10px; float: left;\" alt=\"smal\" height=\"148\" width=\"250\" src=\"http://api.oranta.org/images/stories/smal.jpg\" />Українські інтелектуали схвально оцінили заклик великого і середнього бізнесу до консолідації задля подолання кризи в країні.</strong></p>\n<p>Українська бізнес-ініціатива, до якої увійшли представники великого й середнього бізнесу, оприлюднила 1 лютого звернення&nbsp;<a href=\"http://www.pravda.com.ua/rus/news/2016/02/1/7097434/?attempt=1\">«Український бізнес суспільству та владі»</a>. Підприємці закликали всі кола громадськості до відкритого &nbsp;загальнонаціонального діалогу, метою якого є вироблення нового суспільного договору про спільні цілі та взаємні зобов’язання бізнесу, влади та суспільства і подолання кризи в державі.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3018:2016-02-11-15-42-44&catid=37:spilnota-cat&Itemid=49',5433,NULL,1,1,26),
(5860,'P','2016-02-11',1,NULL,'Rss','Які гріхи прощає лише Апостольська Столиця','<p><strong><img style=\"margin: 10px; float: right;\" alt=\"75896868\" height=\"173\" width=\"250\" src=\"http://api.oranta.org/images/stories/75896868.jpg\" />Своєрідний спецзагін – «Місіонери милосердя», вповноважені прощати гріхи, які може відпустити лише Апостольський Престол, будуть послані наступного року під час Великого посту у різні куточки світу. Про це говориться в буллі «Misericordiae vultus» (Обличчя милосердя), яку не так давно оприлюднив Папа. Згідно із задумом Франциска, цей крок має стати знаком материнської турботи Церкви про Божий люд. </strong></p>\n<p>Про ці гріхи, які вимагають особливого відпущення, розповідає о. Алехандро Карбахо Олеа CMF, офіціал Церковного суду архієпархії &nbsp;Божої Матері в Москві і викладач духовної семінарії.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3019:jaki-grihy-proshchaje-lyshe-apostolska-stolycja&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5434,NULL,1,1,26),
(5861,'P','2016-02-11',1,NULL,'Rss','Гідність','<p><strong><img style=\"margin: 10px; float: left;\" alt=\"ignitytree_b\" height=\"109\" width=\"250\" src=\"http://api.oranta.org/images/stories/ignitytree_b.jpg\" />Пропоную розглянути поняття «гідність» через три дії – МАЮ, ПОВАЖАЮ, ЦІНУЮ, – які важливо здійснювати для виникнення і розвитку почуття власної гідності, а також подивитись на ряд проблем, пов’язаних із гідністю, і деякі шляхи їх вирішення.</strong></p>\n<p><strong>Гідність – це те, що я МАЮ у собі.</strong></p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3020:gidnist&catid=28:tema-nomera-cat&Itemid=2',5435,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5862,'P','2016-02-11',1,NULL,'Note','ЗВІТ №17 про матеріальну допомогу ув’язненим жінкам (09.10.2015 р.)','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6919,200,20160124_165656.jpg\" alt=\"\" width=\"200\" height=\"150\" />Дякуючи пожертвам парафіян, надана матеріальна допомога жінкам, які відбувають покарання в місцях позбавлення волі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Допомога надавалась гігієнічними засобами та речами особистого вжитку, пожертвуваними парафіянами, а також духовною літературою.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Силами волонтерів спаковано і відправлено адресатам 2 посилки.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6919,200,20160124_165656.jpg\" alt=\"\" width=\"200\" height=\"150\" />Дякуючи пожертвам парафіян, надана матеріальна допомога жінкам, які відбувають покарання в місцях позбавлення волі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Допомога надавалась гігієнічними засобами та речами особистого вжитку, пожертвуваними парафіянами, а також духовною літературою.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Силами волонтерів спаковано і відправлено адресатам 2 посилки.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Посилки доставлені до поштового відділення волонтером п.Анною.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Посилки отримали:</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>1.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув</strong><strong>&rsquo;</strong><strong>язнена Олена К. (Качанівська ВК, м.Харків) на суму 49 грн.</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Переписує і присилає вірші Т.Г.Шевченка. З останнім листом: &laquo;Мій Боже милий&hellip;&raquo; і &laquo;Така, як ти, колись лілея&hellip;&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Перший лист &ndash; 22.04.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Допомагали Олені К.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">13.08.2012 р. на суму 20 грн.01 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">26.10.2012 р. на суму 43 грн.75 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">12.02.2014 р. на суму 40 грн.77 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">11.07.2014 р. на суму 04 грн.80 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">26.11.2014 р. на суму 08 грн.74 коп.+пожертви</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">25.04.2015 р. на суму 65 грн.08 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">11.07.2015 р. на суму 46 грн.01 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 229 грн.16 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>2.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув</strong><strong>&rsquo;</strong><strong>язнена Богдана Н. (Качанівська ВК, м.Харків) на суму 52 грн.</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&ldquo;... родом я із Запоріжжя. Мені 35 років. <strong>Батьки померли</strong>: мама коли мені було 14 років, а потім батько&hellip; <strong>а я пішла на вулицю&hellip; скоїла важкий злочин</strong>, за що відбуваю покарання. Хочу вести з Вами переписку, та <strong>сподіваюсь на якусь допомогу,</strong> від рідних допомоги не отримую ніякої. <strong>Вишліть духовну літературу</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Перший лист &ndash; 01.08.2015 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 52 грн.00 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Загальна сума матеріальної допомоги 09.1</strong><strong>0.2015</strong><strong> р. </strong>&mdash;<strong> 101 грн.00 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Крім того, вартість:&nbsp;&nbsp;&nbsp;&nbsp; поштових конвертів, вкладених у посилки &mdash; 0<strong>4 грн.80 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; пересилання &mdash;<strong> 45 грн.60 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Загальна сума витрат від&nbsp; 09.1</strong><strong>0.2015</strong><strong> р. &mdash; 151 грн. 40 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Загальна вага поштових відправлень від 09.1</strong><strong>0.2015</strong><strong> р. &ndash; 6,</strong><strong>7</strong><strong> кг.</strong></span></p>',NULL,5436,5,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5863,'P','2016-02-13',1,NULL,'Rss','Спільна декларація Папи Франциска і Кирила, Патріарха Московського і всієї Русі','<p><img style=\"margin: 10px; float: right;\" alt=\"Francis\" height=\"154\" width=\"250\" src=\"http://api.oranta.org/images/stories/Francis.jpg\" />«<em>Благодать Господа Ісуса Христа, любов Бога Отця та причастя Святого Духа нехай буде з усіма вами</em>» (2 Кор 13,13).</p>\n<p>1. Волею Господа Бога, від якого походять всі дари, в ім’я Господа нашого Ісуса Христа, з допомогою Святого Духа Утішителя, ми, Папа Франциск і Кирило, Патріарх Московський та всієї Руси, зустрілися сьогодні в Гавані. Дякуємо Богові, прославляємо Пресвяту Тройцю за цю першу в історії зустріч.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3021:spilna-deklaracija-papy-francyska-i-kyryla-patriarha-moskovskogo-i-vsijeji-rusi&catid=28:tema-nomera-cat&Itemid=2',5437,NULL,1,1,26),
(5864,'P','2016-02-13',1,NULL,'Note','Парафіяльні оголошення 15-21.02.2016','<p style=\"text-align: justify;\"><img style=\"float: right;\" src=\"image,6921,200,askold_011.jpg\" alt=\"\" width=\"200\" height=\"150\" /><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Служба Божа</strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">&nbsp;відправляється&nbsp;</span><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\"><em>щодня</em></strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">&nbsp;</span><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">08:00; 18:00</strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">.</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;<span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Середа, п&rsquo;ятниця, субота &ndash; за спочилих з Парастасом</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;<strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\"><em>неділя</em></strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">&nbsp;&ndash; Служба Божа</span></p>\r\n<p style=\"text-align: justify;\"><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">08:00</strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">&nbsp;зі співом мирян,</span><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">&nbsp;10:00</strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">&nbsp;з хором,</span><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">&nbsp;12:30</strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">&nbsp;для дітей з хором,</span><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">&nbsp;18:00</strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">&nbsp;співає дяк</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Служба Божа</strong> відправляється <strong><em>щодня</em></strong> <strong>08:00; 18:00</strong>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Середа, п&rsquo;ятниця, субота &ndash; за спочилих з Парастасом</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>неділя</em></strong> &ndash; Служба Божа</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>08:00</strong> зі співом мирян,<strong> 10:00</strong> з хором,<strong> 12:30</strong> для дітей з хором,<strong> 18:00</strong> співає дяк</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><span style=\"text-decoration: underline;\">Понеділок </span></strong><strong><span style=\"text-decoration: underline;\">15 лютого 2016 р.Б.</span></strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>Стрітення Господа Бога і Спаса Нашого Ісуса Христа</em></strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>08:00Утрення&nbsp; 09:00;18:00&nbsp; Служба Божа,(освячення воскових свічок) на передодні &nbsp;в неділю о 17:00 відправляється.Велика Вечірня</em></strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>ДО УВАГИ!</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">відновлю роботу кухня для потребуючих парафіян і біженців з Донбасу&nbsp; за таким розкладом вівторок,четвер,субота -13:00-14:00,неділя 14:00-15:00 в церковній хаті</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- &nbsp;катехизація дітей (довідка с. Меланія 0989285710)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- діти парафії&nbsp; отримують скорбонки для Великопісної збірки родинної пожертви,що використається на Пасхальний дарунок дітям котрі втратили батьків в АТО</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- &nbsp;молодь зголошується до о. Івана (0506071960 / iwan_sokhan@yahoo.de / FB: Ivan Sokhan)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- продовжуємо збірку на лікування поранених в АТО (волонтерське служіння Роксоляна):</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">10 лютого,&nbsp;літаком з Дніпропетровська &nbsp;до &nbsp;Києва були доправлені поранені бійці ДБ ОУН та підрозділу ОУН в 93 ОМБр Олександ-\" Джон\"(опікується дружина Ірина тел. 050 507 01 89 картка 5168 7555 0566 5386 Приватбанк ) та \"Британець\"-дані уточняються.. Джона поклали &nbsp;до &nbsp;17 лікарні.&nbsp;</span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Британець\" зазнав поранення 5 лютого на позиції шахти Бутівка, має численні травми лицевої частини голови та очей. Зараз &nbsp;знаходиться у &nbsp;реанімації Військового шпиталі м. Києва.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>&nbsp;</strong><strong>Оголошується збірка пожертв вагітній дружині та двом дітям загиблого в АТО в районі Донецького аеропорта на Водохреща добровольця ДУКу Григорія Семенишина - &nbsp;воїна з Прикарпаття; мешкав у селі Горохолина Богородчанського району Івано-Франківської області. Наш Герой був активним учасником революції гідності, пройшов Іловайськ, Дебальцеве, Опитне, Піски. Як повідомляють богородчанці, сім&rsquo;я й так жила незаможно, тож нині потребує допомоги від небайдужих. Карточка дружини Світлани №5168 7572 7662 2901, Приватбанк&nbsp; (066 094 68 09)</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">також просимо помогти вдовиною лептою на лікування п.Анатолія і Лесі Потіцької наших парафіян</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;подається план парафіяльного служіння ближньому в Ювілейний Рік Божого Милосердя</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 21 лютого&nbsp; 12:00 Панахида памяті Олени Теліги і побратимів в Бабиному Яру</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5 березня 19:00 панахида памяті генерала УПА Романа Шухевича і його побратимів на Оскольдовій могилі</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 8-9 березня 22:00 &ndash; Тарасова ніч, нічні чування з читанням Кобзаря</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 19 березня&nbsp; 17:00 панахида памяті &nbsp;переховання студентів котрі полягли під Крутами,розвішання ікон</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Продовжується цільова збірка на відновлення фасаду Церкви і встановлення Хреста Хрещення(866р) Оскольда-Миколая&nbsp; в&nbsp; 1150 річницю памяті.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Многая літа! </strong>Надії, в День Народження<strong>Подяка</strong> всім доброчинцям парафії,Павлу Михайловичу,архітектору Юрію Миколайовичу</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>парох Ігор Онишкевич, </em></strong><em>митр. протопресвіте<strong> &nbsp;050</strong></em><strong><em>4473157&nbsp; oranta-gazeta</em></strong><strong><em>@</em></strong><strong><em>ukr</em></strong><strong><em>.</em></strong><strong><em>net</em></strong></span></p>',NULL,5438,5,1,NULL,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5865,'P','2016-02-13',1,NULL,'Rss','«Зустріч, яка не відбулася?» – Блаженніший Святослав','<p><strong><img style=\"margin: 10px; float: left;\" alt=\"papa\" height=\"170\" width=\"250\" src=\"http://api.oranta.org/images/stories/papa.jpg\" />12 лютого в головному аеропорту Гавани імені Хосе Марті відбулася зустріч Глав двох Церков – Папи Франциска та Патріарха Кирила. Зустріч проходила в закритому режимі. Вона тривала понад дві години.</strong></p>\n<p>Зустріч Папи Франциска і Патріарха Кирила завершилася підписанням&nbsp;<a href=\"http://news.ugcc.\" target=\"_blank\">Cпільної декларації</a>, яка викликала неоднозначні реакції з боку громадськості та представників Церков України.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3022:2016-02-13-20-37-57&catid=28:tema-nomera-cat&Itemid=2',5439,NULL,1,1,26),
(5866,'P','2016-02-15',1,NULL,'Rss','Стрітення Господнє','<p><em><strong><img style=\"margin: 10px; float: right;\" alt=\"Vasario-2\" height=\"166\" width=\"250\" src=\"http://api.oranta.org/images/stories/Vasario-2.jpg\" />\"Радуйся благодатна, Богородице Діво, бо з Тебе засяяло Сонце правди Христос Бог наш, що просвічує тих, що в темряві\" (Тропар празника).</strong></em></p>\n<p>Різдвяне коло свят завершується празником Господнього Стрітення, який&nbsp; святкуємо 2 лютого, на сороковий день після Христового Різдва. Цей&nbsp; празник своїм змістом тісно пов\'язаний з Христовим Різдвом, бо як у&nbsp; Різдві, так і тут Христос при своїм пожертвуванні у святині об\'являє нам&nbsp; Свою божественність. Від зустрічі Божої Дитини і Його Пресвятої Матері з&nbsp; праведним Симеоном і сам празник у Східній Церкві називається&nbsp; Стрітення. Візьмемо до уваги три питання: основу празника, його&nbsp; встановлення та дух богослуження.\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3023:stritennja-gospodnje&catid=28:tema-nomera-cat&Itemid=2',5440,NULL,1,1,26),
(5867,'P','2016-02-12',0,NULL,'Note','ЗВІТ №21 про «Подарунок від св. Миколая» дітям, матері яких відбувають покарання в місцях позбавлення волі (08.12.2015 р.)','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Цьогоріч святий Миколай подбав як про &laquo;Шкільний портфелик&raquo;, так і про іграшки та солодощі для дітей, чиї матері відбувають покарання в місцях позбавлення волі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Силами волонтерів: п.Вероніки, п.Мирослави та п.Анни спаковано, оформлено і відправлено адресатам 17 посилок.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Посилки доставлені до поштового відділення парохом Церкви Миколая Чудотворця на Аскольдовій Могилі о.Ігорем Онишкевичем.</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Цьогоріч святий Миколай подбав як про &laquo;Шкільний портфелик&raquo;, так і про іграшки та солодощі для дітей, чиї матері відбувають покарання в місцях позбавлення волі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Силами волонтерів: п.Вероніки, п.Мирослави та п.Анни спаковано, оформлено і відправлено адресатам 17 посилок.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Посилки доставлені до поштового відділення парохом Церкви Миколая Чудотворця на Аскольдовій Могилі о.Ігорем Онишкевичем.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Посилки з іграшками, канцелярськими та шкільними приладдями, доповнені солодощами та гігієнічними засобами, придбаними за пожертви доброчинців, а також предметами одягу, отриманими від парафіян та від благодійних фондів &laquo;Тріумф серця&raquo; та &laquo;Олива&raquo;,отримали:</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>1.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Дитина (Вікторія &ndash; 7,5 р.) ув\'язненої Олени К (Качанівська ВК, м.Харків) в Полтавську обл. на суму 74 грн.17 коп.</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Перший лист &ndash; 22.04.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Допомагали Олені К.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">13.08.2012 р. на суму 20 грн.01 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">26.10.2012 р. на суму 43 грн.75 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">12.02.2014 р. на суму 40 грн.77 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">11.07.2014 р. на суму 04 грн.80 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">26.11.2014 р. на суму 08 грн.74 коп.+пожертви</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">25.04.2015 р. на суму 65 грн.08 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">11.07.2015 р. на суму 46 грн.01 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">09.10.2015 р. на суму 49 грн.00 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дитині Олени К. в Полтавську обл..</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08.05.2015 р. на суму 18 грн.21 коп. &ndash; &laquo;Пасхальний кошик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 370 грн.54 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>2.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Діти (3р., 4р., 4р., 6р., 7р., 9р.) сім&rsquo;ї вимушених переселенців із зони конфлікту на </strong><strong>C</strong><strong>ході України в м.Кривий Ріг (одяг б/у)</strong></span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>3.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Діти (Роксана - 13р., Роман - 8р., Настя - 7р., Інга - 4,5р.) ув\'язненої Олени Т.(Чернігівська ВК)в Кіровоград.обл. на суму 138грн.47коп.)</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Перший лист&mdash; 10.11.2010 р.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Допомагали Олені Т.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">23.11.2010 р. на суму 36 грн.63 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дітям Олени Т. в Кіровоградську обл.:</span></p>\r