INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5923,'P','2016-03-18',1,NULL,'Rss','Булла «Misericordiae vultus» /«Обличчя милосердя»/','<p><strong><img style=\"margin: 10px; float: left;\" alt=\"papa_ofitsijno\" height=\"167\" width=\"250\" src=\"http://api.oranta.org/images/stories/papa_ofitsijno.jpg\" />У буллі Папи Франциска «Misericordiae vultus» («Обличчя милосердя») пояснено причини проголошення Надзвичайного Ювілейного року, присвяченого милосердю, та окреслено, як цей Рік найкраще пережити.</strong></p>\n<p align=\"center\"><strong>ФРАНЦИСК</strong><strong> </strong><strong>Єпископ Рима,</strong><strong> </strong><strong>Слуга слуг Божих</strong></p>\n<p align=\"center\"><em><strong>Усім, хто читатиме цей лист,</strong></em><strong><em> </em></strong><em><strong>- благодать, милосердя і мир!</strong></em></p>\n<p align=\"center\"> </p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3061:2016-03-18-10-41-24&catid=32:gk-svit-cat&Itemid=58',5499,NULL,1,1,26),
(5924,'P','2016-03-18',1,NULL,'Rss','Носії Божої Любові','<p align=\"right\"><em><img src=\"http://api.oranta.org/images/stories/1244339.jpg\" width=\"250\" height=\"188\" alt=\"1244339\" style=\"margin: 10px; float: right;\" />«Істинно кажу вам: &nbsp;усе, що ви зробили одному з моїх братів найменших – ви мені зробили.&nbsp;</em><em style=\"font-size: 0.875em;\">/Мт.25,40/»</em></p>\n<p>У Сиропусну Неділю, за благословенням пароха о. Ігоря Онишкевича, Україночки, із словами подяки, млинцями, які напекли наші неперевершені господині, варенням, салом, часником, цукерками, книжками, кружками та власноруч написаними цитатами про прощення для кожного особисто завітали до поранених та хворих в Ірпінський госпіталь та госпіталь Прикордонної служби.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3062:nosiji-bozhoji-ljubovi&catid=25:parafija-cat&Itemid=27',5500,NULL,1,1,26),
(5925,'P','2016-04-07',0,NULL,'Note','Молодіжна Хресна дорога в дусі Тезе','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,7015,200,2016-1.jpg\" alt=\"\" width=\"200\" height=\"151\" />Хресна дорога відбудеться у&nbsp;п&lsquo;ятницю 8&nbsp;&nbsp;квітня&nbsp;по кручах Дніпрових</strong></span></p>\r\n<p class=\"xfmc1\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">від станції метро &laquo;Дніпро&raquo; до церкви&nbsp;Миколая Чудотворця, що на Аскольдовій могилі (ст. м.Арсенальна,&nbsp;Паркова Дорога 1,).&nbsp;</span></p>\r\n<p class=\"xfmc1\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Збір на ст. метро Дніпро з 18:30</strong>, хто не встигне на початок то&nbsp;вийшовши&nbsp;з метро &laquo;Дніпро&raquo; пройти метрів 200-250 в бік Подолу, десь 5-7 хв.</span></p>','<p class=\"xfmc1\" style=\"text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,7015,200,2016-1.jpg\" alt=\"\" width=\"200\" height=\"151\" />Слава Ісусу Христу!</span></p>\r\n<p class=\"xfmc1\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-align: justify; text-indent: 0em; line-height: 1.3em;\">Вже стало традицією щороку молодіжною спільнотою церкви Миколая Чудотворця проводити Хресну Дорогу по кручах Дніпрових&nbsp;</span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">(від ст. м. Дніпро до церкви є 14 хрестів) тому хто має можливість запрошуємо 8 квітня долучитись до Хресної дороги&nbsp;.</span></p>\r\n<p class=\"xfmc1\" style=\"text-align: center;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;Молодіжна Хресна дорога в дусі Тезе.</span></p>\r\n<p class=\"xfmc1\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Запрошуємо взяти участь у Молодіжній&nbsp;&nbsp;Хресній дорозі, яка присвячена темі євангелізації.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Євангелізація&nbsp;&nbsp;без труднощів&nbsp;&nbsp;неможлива.&nbsp;&nbsp;Під час розмов про Христа&nbsp;&nbsp;ми наражаємо себе на різні невигоди, незручності і навіть терпіння. Саме тому, кожний, хто вірує в Ісуса Христа, не нарікає, не бентежиться, не бунтується, коли зустріне труднощі, нерозуміння, глузування і насмішки. Людина яка вірить і наслідує Христа, наслідує його тоді, коли бере на себе хрест, коли падає і коли піднімається.</span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Хресна дорога відбудеться у</span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">&nbsp;п&lsquo;ятницю 8&nbsp;&nbsp;квітня&nbsp;по кручах Дніпрових</span></strong></p>\r\n<p class=\"xfmc1\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">від станції метро &laquo;Дніпро&raquo; до церкви&nbsp;Миколая Чудотворця, що на Аскольдовій могилі (ст. м.Арсенальна,&nbsp;Паркова Дорога 1,).&nbsp;</span></p>\r\n<p class=\"xfmc1\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Збір на ст. метро Дніпро з 18:30</strong>, хто не встигне на початок то&nbsp;вийшовши&nbsp;з метро &laquo;Дніпро&raquo; пройти метрів 200-250 в бік Подолу, десь 5-7 хв.&nbsp;</span></p>\r\n<p class=\"xfmc1\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Зліва перша стація хресної дороги.&nbsp;</span></p>\r\n<p class=\"xfmc1\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Телефони для довідок: 050-607-19-60 (о. Іван), 063-140-03-73 (Ганна)</span></p>',NULL,5532,5,1,NULL,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5926,'P','2016-03-18',1,NULL,'Rss','Хресна Дорога з війни','<p><strong><em><img style=\"margin: 10px; float: left;\" alt=\"6589\" height=\"171\" width=\"250\" src=\"http://api.oranta.org/images/stories/6589.jpg\" />Роздуми Хресної Дороги, написані&nbsp;Миколою Вишньовським&nbsp;— солдатом АТО.</em></strong></p>\n<p><strong>І стояння&nbsp;—&nbsp;Ісуса засуджено</strong></p>\n<p>Початок довгої важкої Хресної Дороги. Ісус стоїть перед Пилатом. Сам Син Божий стоїть перед людиною&nbsp;— творінням Божим. Упокорився! Не&nbsp;показує своєї величі, стоїть найвеличніший перед найменшим, перед людиною!</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3063:hresna-doroga-z-viny&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5501,NULL,1,1,26),
(5927,'P','2016-03-19',1,NULL,'Rss','Усвідомлюючи відповідальність перед Богом…','<p><strong><img style=\"margin: 10px; float: right;\" alt=\"huzar_velykden14\" height=\"167\" width=\"250\" src=\"http://api.oranta.org/images/stories/huzar_velykden14.jpg\" />Слово, яке дуже часто чуємо останніми місяцями, – це «Конституція». Цей термін вживається з різною метою: переважно для позначення документа, якого нам треба дотримуватися, або ж документа, зміст якого необхідно поправити. Хочу сьогодні представити кілька думок, пов’язаних із Конституцією.</strong></p>\n<p>Усі держави на Земній кулі мають Конституцію або принаймні хочуть її мати. Деякі мають Основний закон, хоча він не розписаний. Прикладом може слугувати Королівство Великобританії. Але хоча громадяни Королівства не мають писаного документа, на якого можуть покликатися, у них є дуже чітке усвідомлення існування законів.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3064:usvidomljujuchy-vidpovidalnist-pered-bogom&catid=28:tema-nomera-cat&Itemid=2',5502,NULL,1,1,26),
(5928,'P','2016-03-19',1,NULL,'Note','Парафіяльні оголошення 21-20.27.2016','<p style=\"text-align: justify;\"><span style=\"text-indent: 0em; font-size: small; line-height: 1.3em; font-family: arial, helvetica, sans-serif;\">Увага!&nbsp; 21-24 березня 2016 р Богослуження&nbsp; відправлятись не будуть</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Священники парафії будуть на формаційних Архиєпархіяльних курсах в Житомирі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Церква відкрита для молитви вірних</span></p>','<h1 style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Розклад Богослужінь на 2-ий тиждень Великого посту 2016 р.Б</span></h1>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>21.03. понеділок</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08-00&nbsp; Часи. Літургія св. Йоана Золотоустого&nbsp;Панахида</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18-00 Літургія св. Йоана Золотоустого Акафіст Страстям Господнім</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>22.03. вівторок</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08-00 Часи, Літургія св. Йоана Золотоустого&nbsp; Панахида</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18-00 Літургія св. Йоана Золотоустого&nbsp;Панахида</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>23.03. середа</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08-00 Вечірня з Літургією Напередосвячених Дарів.&nbsp;Парастас-сорокоусти.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18-00&nbsp;Вечірня з Літургією Напередосвячених Дарів. Хресна дорога</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>24.03. четвер</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08-00 Часи, Літургія св. Йоана Золотоустого &nbsp;Акафіст св.Миколаю Мирлікійському чудотв. Освячення води і єлею,молитви оздоровлення</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18-00 Літургія св. Йоана Золотоустого Панахида</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>25.03. п\'ятниця</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08-00 Вечірня з Літургією Напередосвячених Дарів Парастас-сорокоусти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">17:00 Хресна Дорога від Дніпра</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18-00 Вечірня з Літургією Напередосвячених Дарів, Хресна дорога</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>26.03. Субота Заупокійна.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08-00 Утреня</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">09-00 Літургія Святого Івана Золотоустого.&nbsp;Парастас-сорокоусти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">18-00 Літургія Святого Івана Золотоустого Вечірня</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>28.03.2016 неділя 2-га Великого посту</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08-15 10-00 12-30 18-00 Літургія Святого Василія Великого.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Увага!&nbsp; 21-24 березня 2016 р Богослуження&nbsp; відправлятись не будуть</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Священники парафії будуть на формаційних Архиєпархіяльних курсах в Житомирі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Церква відкрита для молитви вірних</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; text-indent: 0em; line-height: 1.3em; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; text-indent: 0em; line-height: 1.3em; font-size: small;\">Кожної першої п\'ятниці місяця - молитовна зустріч Апостольства молитви</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08.00; 18.00&nbsp;Божественна літургія, Сповідь</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Напередодні в четвер</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">19.00&nbsp;Свята година. Сповідь, відвідування і причастя хворих</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Кожної першої суботи місяця о 11:00 &nbsp;засідання Церковної ради</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Щонеділі&nbsp;збір молоді о&nbsp;14.00&nbsp;- Наука за кавою в Церковній хатині</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Вівторок,четвер ,&nbsp;субота о&nbsp;12:00, Неділя о&nbsp;14.00&nbsp;&nbsp;&nbsp; благочинна кухня Карітас-Печерськ</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Для біженців з Донбасу і Криму,потребуючих парафіян з катехитичним навчанням</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>08:00-18:00 Служба Божа</strong> відправляється понеділок,вівторок,четвер</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Середа, п&rsquo;ятниця-<strong>08:00</strong> ,Літургія Передосвячених Дарів з Парастасом Сорокоустами за спочилих</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>18:00&nbsp; </strong>Літургія Передосвячених Дарів,Хресна дорога,сповідь</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Пятниця 17-00 Хресна дорога від Дніпра з священиком і нагода до св.Тайни Сповіді</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>неділя</em></strong> &ndash; Служба Божа</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>08:00</strong> зі співом мирян,<strong> 10:00</strong> з хором,<strong> 12:30</strong> для дітей з хором,<strong> 18:00</strong> співає дяк</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>ДО УВАГИ!</strong></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Працює &nbsp;кухня для потребуючих парафіян і біженців з Донбасу&nbsp; за таким розкладом вівторок,четвер,субота -12:00-13:00,неділя 14:00-15:00 в церковній хаті</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- &nbsp; &nbsp;катехизація дітей (довідка с. Меланія 0989285710)</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- &nbsp; &nbsp;молодь зголошується до о. Івана (0506071960 / iwan_sokhan@yahoo.de / FB: Ivan Sokhan)</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">продовжуємо збірку на лікування поранених в АТО (волонтерське служіння Роксоляна)</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">21-24 березня 2016 р.Б. відбудуться реколекції для священиків та дияконів Київської Архиєпархії УГКЦ Церква буде відкрита,але Служби не буде</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">9 квітня&nbsp; 11:00 Молебень до Хреста Милосердного; духовні науки &laquo;Будьте Милосердні&raquo; проводить о.Константин</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Продовжується цільова збірка на відновлення фасаду Церкви і встановлення Хреста Хрещення(866р) Оскольда-Миколая&nbsp; в&nbsp; 1150 річницю памяті.</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- Матері в молитві-молитовна спільнота проводить збірку на реставрацію Євангеліїя.котру цілував св.Іван-Павло11 в часі молитви на Аскольдовій могилі 23 червня 2001 р(проводить збірку п.Марія)</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">1 квітня 22:00 прийди на нічні молитви на славу Божу і спасіння України,що проводить Апостольство молитви (ревнителька Ярослава 0988874008)</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">2 квітня&nbsp; 17:00 в катехитичному залі на Аскольдовій могилі, діти до 12 років Початок шахового турніру &laquo;1150 р.Хрещення Аскольда-Миколая&raquo;призовий фонд 15000 гр,обовязковий реферат про історію князя Аскольда.&nbsp;</span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Записуватись п.Андрій 097 4104678</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Многая літа! </strong>О.Олексію в річницю іерейських свячень,п.Ярославу в День Народження<strong>Подяка</strong> всім доброчинцям парафії,Павлу Михайловичу за недільні 20 обідів для кухні,Валерію і Ярославі за хліб для кухні,Олександру котрий помагає в споруджені душевої кабіни,Ользі Черешнівській за привезену рибу для кухні,архітектору Юрію Миколайовичу,Тарасу,Ігорю Самбірському,Богдану</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Телефон для довідок: <strong><em>парох Ігор Онишкевич,&nbsp;</em></strong><em>митр. протопресвітер</em>&nbsp;<a href=\"tel:%28050%29%204473157\" target=\"_blank\"><strong>(050)4473157</strong></a>&nbsp;e-mail:&nbsp;<a href=\"mailto:oranta-gazeta@ukr.net\" target=\"_blank\"><strong>oranta</strong><strong>-</strong><strong>gazeta</strong><strong>@</strong><strong>ukr</strong><strong>.</strong><strong>net</strong></a>,<a href=\"http://www.oranta.org/\" target=\"_blank\"><strong>www</strong><strong>.</strong><strong>oranta</strong><strong>.</strong><strong>org</strong></a>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 067 406 89 33</span></p>',NULL,5503,5,1,NULL,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5929,'P','2016-03-19',1,NULL,'Rss','Оглядаючись на минуле – думати про майбутнє','<p><img style=\"margin: 10px; float: left;\" alt=\"image\" height=\"164\" width=\"250\" src=\"http://api.oranta.org/images/stories/image.jpg\" />Період комуністичного терору, зокрема насильства Львівського псевдособору 1946 року, залишив у свідомості людей відчуття пасхальної динаміки: від розп’яття через смерть – до воскресіння. Минуле тут має богословське значення: сенс комуністичної руїни – у здобутті досвіду мучеництва, який, за Тертуліаном, стає «насінням Церкви»:&nbsp;<em>Semen est sanguis christianorum</em>.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3065:ogljadajuchys-na-mynule--dumaty-pro-mabutnje&catid=27:gk-v-ukrajini-cat&Itemid=57',5504,NULL,1,1,26),
(5930,'P','2016-03-21',1,NULL,'Rss','17 причин, чому варто часто робити знак хреста','<p><strong><img style=\"margin: 10px; float: right;\" alt=\"cross-500754_1280-700x467\" height=\"167\" width=\"250\" src=\"http://api.oranta.org/images/stories/cross-500754_1280-700x467.jpg\" />Знак хреста&nbsp;— простий жест і водночас глибокий вираз віри як для католиків, так і для православних. Ми&nbsp;робимо знак хреста, коли входимо до храму; після того, як приймемо Святе Причастя; перед споживанням їжі, а також щоразу, коли молимося.</strong></p>\n<p>Пропонуємо ще 17&nbsp;причин, чому це варто робити якнайчастіше. Звісно, це не остаточний перелік… нехай би кожен подумав від себе особисто ще про три. До рівного рахунку.\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3066:17-prychyn-chomu-varto-chasto-robyty-znak-hresta&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5505,NULL,1,1,26),
(5931,'P','2016-03-22',1,NULL,'Rss','«Боже ім’я – Милосердя» /1/','<p><img style=\"margin: 10px; float: left;\" alt=\"ANSA930832_Articolo\" height=\"177\" width=\"250\" src=\"http://api.oranta.org/images/stories/ANSA930832_Articolo.jpg\" />«Боже ім’я – Милосердя», – книга-інтерв’ю Папи Франциска під такою назвою нещодавно побачила світ у 86 країнах. Вона є плодом розмови Святішого Отця з італійським журналістом Андреа Торніеллі, який у вступі до неї, для кращого зрозуміння «погляду Франциска», пропонує повернутися до початків його понтифікату.</p>\n<p>У неділю, 17 березня 2013 року, Папа Франциск служив свою першу Святу Месу з людом після обрання на Єпископа Риму, що відбулося попередньої середи ввечері. Ватиканський парафіяльний храм святої Анни, розташований поруч з однойменними воротами, що є входом та в’їздом до найменшої держави світу, був вщент переповнений вірними.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3067:2016-03-22-20-18-55&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5506,NULL,1,1,26),
(5932,'P','2016-03-23',1,NULL,'Rss','Христос Воскрес! Нехай ті, хто страждає, воскреснуть у Ньому','<p><img style=\"margin: 10px; float: right;\" alt=\"972FA42E\" height=\"141\" width=\"250\" src=\"http://api.oranta.org/images/stories/972FA42E.jpg\" />У Страсний вівторок у Брюсселі у боягузливому нападі на невинних зло виявило своє огидне обличчя. Атакуючи європейців та гостинну і відкриту Європу, терористи намагаються зіштовхнути континент в агонію страху. Чому? Тому що страх є великим маніпулятором, безчесним інструментом контролю. Брюссель є центром об’єднаної Європи, країни якої, зазнавши страхів Другої світової війни, вирішили знищити саму можливість війни між сусідами: позбутися страху перед іншим, відкрити серця та демілітаризувати кордони. Така відкритість це великий дар та благословення Європи для всього світу. І як кожну відкритість, її можуть використовувати як вразливість ті, хто не здатен відчувати благодать миру.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3068:hrystos-voskres-neha-ti-hto-strazhdaje-voskresnut-u-nomu&catid=27:gk-v-ukrajini-cat&Itemid=57',5507,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5933,'P','2016-03-23',1,NULL,'Rss','«Боже ім’я – Милосердя» /2/','<p><img style=\"margin: 10px; float: left;\" alt=\"ANSA930832_Articolo\" height=\"177\" width=\"250\" src=\"http://api.oranta.org/images/stories/ANSA930832_Articolo.jpg\" />«Бог милосердя, милосердний Бог. Для мене це справді є паспортом нашого Бога», – сказав Папа Франциск, спілкуючись з італійським журналістом Андреа Торніеллі, розмова з яким нещодавно побачила світ у формі книги-інтерв’ю «Боже ім’я – Милосердя».</p>\n<p>Згадана бесіда відбулася у липні 2015 року, після повернення Святішого Отця з подорожі до Латинської Америки. У контексті того, що тривали приготування до початку надзвичайного Святого Року, присвяченого милосердю, перші запитання стосувалися того, як виникла така ідея і що це слово «милосердя» означає особисто для Папи Франциска. Відповіді на ці запитання увійшли до першого розділу книги, який називається «Час милосердя».</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3069:2016-03-23-20-33-36&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5508,NULL,1,1,26),
(5934,'P','2016-03-24',1,NULL,'Rss','«Боже ім’я – Милосердя» /3/','<p><img style=\"margin: 10px; float: left;\" alt=\"ANSA930832_Articolo\" height=\"177\" width=\"250\" src=\"http://api.oranta.org/images/stories/ANSA930832_Articolo.jpg\" />Продовжуємо гортати сторінки книги-інтерв’ю «Боже ім’я – Милосердя», що є записом розмови Папи Франциска з італійським журналістом Андреа Торніеллі. У першому розділі, який має назву «Час милосердя», Святіший Отець ділиться досвідом особистих стосунків з Божим милосердям.</p>\n<p>Відповідаючи на запитання: «Яке місце і яке значення у Вашому серці, житті та особистій історії займає милосердя? Чи пам’ятаєте про свій перший, ще в дитинстві, досвід милосердя?», – Папа Франциск зазначає, що розглядає своє життя у світлі 16-го розділу Книги пророка Єзекеїла.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3071:2016-03-24-20-52-47&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5509,NULL,1,1,26),
(5935,'P','2016-03-24',1,NULL,'Rss','Не боятися надміру милосердя','<p><img style=\"margin: 10px; float: right;\" alt=\"AFP5131133_Articolo\" height=\"177\" width=\"250\" src=\"http://api.oranta.org/images/stories/AFP5131133_Articolo.jpg\" />Як священики, ми є свідками і служителями милосердя нашого Отця, покликаними наслідувати Його в надмірі милосердя, що проявляється у двох середовищах, якими є зустріч та Його прощення. Такими думками Папа Франциск поділився зі священиками Римської дієцезії.</p>\n<p>На початку проповіді, коментуючи євангельську розповідь про те, як Ісус навчав у синагозі в Назареті, Наступник святого Петра зазначив, що Його слова про те, що здійснилися пророцтва, мали б викликати оплески та сльози на очах слухачів.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3070:ne-bojatysja-nadmiru-myloserdja&catid=28:tema-nomera-cat&Itemid=2',5510,NULL,1,1,26),
(5936,'P','2016-03-25',1,NULL,'Rss','Друг Хрест, або чому ми точно переможемо у цій війні','<p><img style=\"margin: 10px; float: right;\" alt=\"FB_IMG\" height=\"166\" width=\"250\" src=\"http://api.oranta.org/images/stories/FB_IMG.jpg\" />Потяг «Лисичанськ – Київ». У мене перше місце. Десь у Сватове ввічливо стукає і заходить дівчина років так 25-и. Дуже просить залишити в моєму купе порожню валізу, бо в неї верхнє місце, а валіза величенька. Перепрошує, відкриває, аби показати, що там справді нічого немає. Жартома кажу: «Та вірю, що Ви туди бомбу не заклали». В неї на обличчі сумна усмішка.</p>\n<p>Заходить у купе чоловік. Жінка починає йому те ж говорити й пояснювати. Він: «Якщо там бомби немає, то залишайте». Сумно усміхнулися, всі троє. Такі в нас тепер жарти, Луганщина це чи Львівщина. Жарти… Нещодавно працівники залізниці виявили у потязі цього напряму вибуховий пристрій. Потязі, який курсує підконтрольними Україні територіями.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3072:drug-hrest-abo-chomu-my-tochno-peremozhemo-u-ci-vini&catid=37:spilnota-cat&Itemid=49',5511,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5937,'P','2016-03-25',1,NULL,'Rss','«Боже ім’я – Милосердя» /4/','<p><img style=\"margin: 10px; float: left;\" alt=\"ANSA930832_Articolo\" height=\"177\" width=\"250\" src=\"http://api.oranta.org/images/stories/ANSA930832_Articolo.jpg\" />Продовжуємо гортати сторінки книги-інтерв’ю «Боже ім’я – Милосердя», що є записом розмови Папи Франциска з італійським журналістом Андреа Торніеллі. У першому розділі, який має назву «Час милосердя», Святіший Отець ділиться досвідом особистих стосунків з Божим милосердям.</p>\n<p>Андреа Торніеллі попросив співрозмовника поділитися тим, хто йому спадає на думку, коли він замислюється про «милосердних священиків», чи то тих, яких зустрів на своєму життєвому шляху, чи тих, приклад яких є джерелом натхнення. Відповідаючи, Папа сказав, що таких було багато…</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3073:2016-03-25-07-22-42&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5512,NULL,1,1,26),
(5938,'P','2016-03-28',1,NULL,'Rss','Великодня проповідь Папи Франциска','<p><em><img style=\"margin: 10px; float: right;\" alt=\"204115\" height=\"184\" width=\"250\" src=\"http://api.oranta.org/images/stories/204115.jpg\" />У суботу, 26 березня 2016 р., ввечері, Папа Франциск очолив у базиліці Святого Петра у Ватикані літургію Пасхального чування, під час якої виголосив наступну проповідь:</em></p>\n<p>«Петро побіг до гробу» (Лк 24,12). Які думки могли хвилювати розум і серце святого Петра, в том час, коли він біг? Євангелія розповідає нам, що Одинадцятеро, між якими був Петро, не увірували свідченню жінок, їхньому пасхальному звіщенню. Навпаки, «ці слова здавалися їм порожньою вигадкою» (Лк 24,11) У серці Петра, отже, гніздився сумнів, супроводжуваний багатьма негативними думками: смуток з приводу смерті улюбленого Учителя та розчарування з приводу того, що три рази відрікся від Нього під час страстей.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3074:velykodnja-propovid-papy-francyska&catid=28:tema-nomera-cat&Itemid=2',5513,NULL,1,1,26),
(5939,'P','2016-03-28',1,NULL,'Rss','«Боже ім’я – Милосердя» /5/','<p><img src=\"http://api.oranta.org/images/stories/ANSA930832_Articolo.jpg\" width=\"250\" height=\"177\" alt=\"ANSA930832_Articolo\" style=\"margin: 10px; float: left;\" />Другий розділ книги-інтерв’ю «Боже ім’я – Милосердя», що є записом розмови Папи Франциска з італійським журналістом Андреа Торніеллі, називається «Дар Сповіді», і розпочинається із запитання: «Чому важливо сповідатися? Ви були першим Папою, що зробив це прилюдно під час покаянних богослужінь Великого Посту в базиліці Святого Петра… Чи ж не вистачить покаятися в собі, попросити прощення, спілкуючись безпосередньо з Богом?».</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3075:2016-03-28-07-22-06&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5514,NULL,1,1,26),
(5940,'P','2016-03-28',1,NULL,'Note','Гостинці дітям','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6998,200,20160320_135939[1].jpg\" alt=\"\" width=\"200\" height=\"150\" />У сьогоднішній день матеріальної скрути майже кожної української родини це стало великою радістю для дітей, поласувати шоколадом Kinder.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Щира радість дітей &ndash; це молитва про спасіння, як для вас Добродіїв, так і про перемоги миру у цій війні, яку принесли нам північні сусіди.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Боже спаси нас.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6998,200,20160320_135939[1].jpg\" alt=\"\" width=\"200\" height=\"150\" />Наші парафіяни не забувають, що моляться найдавнішій Церкві посвяченій заступництву св. Миколая Чудотворця. Приклад служіння ближньому, а особливо дітям започаткував перший Посол США в Україні Роман Попадюк з дружиною Джудіт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Його родина не тільки була на кожній недільній Службі Божій, але старались передати гостинці св. Микола для дітей. Цього разу добротворцями св. Миколая стали добродії Михайло і Богдан. Діти парафії і біженців з Донбасу отримали солодощі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">У сьогоднішній день матеріальної скрути майже кожної української родини це стало великою радістю для дітей, поласувати шоколадом Kinder.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Щира радість дітей &ndash; це молитва про спасіння, як для вас Добродіїв, так і про перемоги миру у цій війні, яку принесли нам північні сусіди.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Боже спаси нас.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ілля для &laquo;Оранти&raquo;</span></p>',NULL,5515,5,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5941,'P','2016-03-28',1,NULL,'Note','ЗВІТ №4 про роботу служби милосердя «Карітас-Печерськ» при парафії Церкви Миколая Чудотворця на Аскольдовій Могилі (лютий 2016 р.)','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6999,200,20160228_152158.jpg\" alt=\"\" width=\"200\" height=\"123\" />Крім того відправлено матеріальну допомогу засобами гігієни, продуктами та одягом:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- дітям засуджених матерів у Херсонську область (2 посилки, див звіт №3)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- ув&rsquo;язненим жінкам Чернігівської та Качанівської (м.Харків) виправних колоній (5 посилок, див звіт №1)</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,6999,200,20160228_152158.jpg\" alt=\"\" width=\"200\" height=\"123\" />Служба милосердя &laquo;Карітас-Печерськ&raquo; при парафії Церкви Миколая Чудотворця на Аскольдовій Могилі, зібравши пожертвувані парафіянами одяг чоловічий, жіночий та дитячий, взуття, іграшки дитячі та гуманітарну допомогу одягом, взуттям, постільною білизною, постіллю та медичними приналежностями від благодійних фондів &laquo;Тріумф серця&raquo; і &laquo;Олива&raquo;, розподілила їх таким чином (записи в книзі розподілу гуманітарної допомоги):</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Уділили потребуючим мирянам (27 осіб):</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash; 67 кг</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;куртки, плащі &ndash; 19 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;взуття &ndash; 7 пар</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;колиска дитяча &ndash; 1 шт.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Надали матеріальну допомогу:</span></li>\r\n</ol>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">переселенцям з Донецької обл. (8 осіб):</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash; 29 кг.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;куртки, плащі &ndash; 4 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;взуття &ndash; 2 пари</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; постіль: подушки &ndash; 2 шт.</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">потребуючим та безпритульним (28 осіб):</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash; 74 кг.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;куртки, плащі &ndash; 26 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;взуття &ndash; 12 пар</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;постіль: ковдра &ndash; 3 шт., подушки &ndash; 3 шт., покривало &ndash; 1 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;білизна постільна &ndash; 7 од.</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">відділенню мікрохірургії військового шпиталю в м.Києві</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;рукавички хірургічні стерильні &ndash; 1 упаковка (25 пар)</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Відправили гуманітарний вантаж:</span></li>\r\n</ol>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Благодійному фонду &laquo;Завжди поруч&raquo; для бійців АТО та населення зони конфлікту на сході України:</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг чоловічий &ndash; 1 мішок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг жіночий &ndash; 2 мішки</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг дитячий &ndash; 1 мішок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг жіночий &ndash; 2 мішки</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг верхній &ndash; 1 мішок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;кава мелена натуральна &ndash; 0,5 мішка</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;розчин дезинфекційний для рук &ndash; 4 коробки</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;фартухи поліетиленові одноразові &ndash; 2 рулони</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;іграшки дитячі &ndash; 2 мішки</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Крім того відправлено матеріальну допомогу засобами гігієни, продуктами та одягом:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; дітям засуджених матерів у Херсонську область (2 посилки, див звіт №3)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ув&rsquo;язненим жінкам Чернігівської та Качанівської (м.Харків) виправних колоній (5 посилок, див звіт №1)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Роздано мирянам та потребуючим каву мелену &ndash; 0,25 мішка.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Робота виконана силами волонтерів: п.Мирослави, п.Людмили, п.Світлани, п.Оксани, п.Анни.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Висловлюємо щиру подяку жертводавцям і прикликаємо Боже благословення на їх родини.</span></p>',NULL,5516,5,1,NULL,19);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5942,'P','2016-03-28',1,NULL,'Note','Іспит сумління - 15 хвилин щирості','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><img style=\"float: right;\" src=\"image,7000,200,1040059.jpg\" alt=\"\" width=\"200\" height=\"133\" />Темою реколекцій було обрано &ndash; &laquo;Іспит сумління - 15 хвилин щирості&raquo;. Розглядались і обговорювались питання: як бути правдивим із собою і належно приготуватись до сповіді? як добре пережити піст?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\">Програма реколекцій була цікавою та насиченою: роздуми, науки, робота в групах, молитва, вистава студентського театру, велика гра та інші маленькі сюрпризи.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,7000,200,1040059.jpg\" alt=\"\" width=\"200\" height=\"133\" />Молодіжна комісія та команда Центру Академічного Душпастирства з 19 на 20 березня провела Великопосні реколекції для молоді у рамках заходу підготовки до Світового Дня Молоді.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Темою реколекцій було обрано &ndash; &laquo;Іспит сумління - 15 хвилин щирості&raquo;. Розглядались і обговорювались питання: як бути правдивим із собою і належно приготуватись до сповіді? як добре пережити піст?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Програма реколекцій була цікавою та насиченою: роздуми, науки, робота в групах, молитва, вистава студентського театру, велика гра та інші маленькі сюрпризи.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Молодь, наповнена любов&rsquo;ю Христа, натхнена глибоко-духовними промовами отця Івана Сохана та озброївшись духом місійності вирушила у свій світ, щоб щедро ділитись Божим Словом.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><a href=\"https://www.youtube.com/watch?v=TGig8P7pYXk\">https://www.youtube.com/watch?v=TGig8P7pYXk</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&laquo;Оранта&raquo;</span></p>',NULL,5517,5,1,NULL,17),
(5943,'P','2016-03-28',1,NULL,'Note','Парафіяльні оголошення 28.03- 3.04.2016','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\"><img style=\"float: left;\" src=\"image,7001,200,1264702.jpg\" alt=\"\" width=\"200\" height=\"150\" />08:00 Утрення, Час1, Панахида&nbsp;</strong><span style=\"text-indent: 0em; line-height: 1.3em;\">відправляється понеділок, вівторок, четвер</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Середа, п&rsquo;ятниця&nbsp;<strong>08:00</strong>&nbsp;,Літургія Передосвячених Дарів з Парастасом Сорокоустами за спочилих</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>18:00 понеділок-пятниця&nbsp;&nbsp;</strong>Літургія Передосвячених Дарів</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>19:00 Середа і пятниця&nbsp;&nbsp;</strong>Хресна дорога,сповідь</span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,7001,200,1264702.jpg\" alt=\"\" width=\"200\" height=\"150\" />08:00 Утрення, Час1, Панахида&nbsp;</strong>відправляється понеділок, вівторок, четвер</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Середа, п&rsquo;ятниця&nbsp;<strong>08:00</strong> ,Літургія Передосвячених Дарів з Парастасом Сорокоустами за спочилих</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>18:00 понеділок-пятниця&nbsp; </strong>Літургія Передосвячених Дарів</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>19:00 Середа і пятниця&nbsp; </strong>Хресна дорога,сповідь</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Пятниця 17-00 Хресна дорога від Дніпра з священиком і нагода до св.Тайни Сповіді</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>неділя</em></strong> &ndash; Служба Божа</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>08:00</strong> зі співом мирян,<strong> 10:00</strong> з хором,<strong> 12:30</strong> для дітей з хором,<strong> 18:00</strong> співає дяк</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>ДО УВАГИ!</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Працює &nbsp;кухня для потребуючих парафіян і біженців з Донбасу&nbsp; за таким розкладом вівторок,четвер,субота -12:00-13:00,неділя 14:00-15:00 в церковній хаті</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- &nbsp; &nbsp;катехизація дітей (довідка с. Меланія 0989285710)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- &nbsp; &nbsp;молодь зголошується до о. Івана (0506071960 / iwan_sokhan@yahoo.de / FB: Ivan Sokhan)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">продовжуємо збірку на лікування поранених в АТО (волонтерське служіння Роксоляна) 29 березня 08:00&nbsp; Парастас памяті&nbsp; брата Ігоря Богуша,молитва на його могилі,поминальна трапеза,запрошує сестра Любов</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">2 квітня субота&nbsp; 11:00 засідання Церковної Ради,:Фінансовий квартальний звіт,Святкування Празнику Теплого св.Миколая і 1150 років Хрещення Аскольда-Миколая(866р),піднесення памятного Христа.Канікули з Богом на Аскольдовій Могилі,Проща До Вижгорода.,28 травня&nbsp; вшанування 100років памяті Івана Франка</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">9 квітня&nbsp; 11:00 Молебень до Хреста Милосердного; духовні науки &laquo;Будьте Милосердні&raquo; проводить о.Константин</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Продовжується цільова збірка на відновлення фасаду Церкви і встановлення Хреста Хрещення(866р) Оскольда-Миколая&nbsp; в&nbsp; 1150 річницю памяті.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- Матері в молитві-молитовна спільнота проводить збірку на реставрацію Євангеліїя.котру цілував св.Іван-Павло11 в часі молитви на Аскольдовій могилі 23 червня 2001 р(проводить збірку п.Марія)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">1 квітня 22:00 прийди на нічні молитви на славу Божу і спасіння України,що проводить Апостольство молитви (ревнителька Ярослава 0988874008)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">2 квітня&nbsp; 17:00 в катехитичному залі на Аскольдовій могилі,діти до 12 років Початок шахового турніру &laquo;1150 р.Хрещення Аскольда-Миколая&raquo;призовий фонд 15000 гр,обовязковий реферат про історію князя Аскольда;Записуватись п.Андрій 097 4104678</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Многая літа! </strong><strong>Подяка</strong> всім доброчинцям парафії,Павлу Михайловичу за недільні 20 обідів для кухні,Валерію і Ярославі за хліб для кухні,Василю Михайловичу за допомогу в спорудженні памятного Христа,Марії за варенники &nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>парох Ігор Онишкевич, </em></strong><em>митр. протопресвітер<strong> &nbsp;050 4473157&nbsp; oranta-gazeta@</strong></em><strong><em>ukr</em></strong><strong><em>.</em></strong><strong><em>net</em></strong></span></p>',NULL,5518,5,1,NULL,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5944,'P','2016-03-28',1,NULL,'Note','Пам\'яті В\'ячеслава Чорновола','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,7002,200,4d27667.jpg\" alt=\"\" width=\"200\" height=\"150\" />25 березня на могилі українського політика, публіциста, літературного критика, діяч руху опору проти зросійщення та національної дискримінації українського народу, політичного в&rsquo;язня&nbsp;СРСР, провідника українського національно-демократичного визвольного руху кінця&nbsp;80-х &mdash;&thinsp;90-х років; Героя&nbsp;України було відправлено поминальну панахиду за трагічно загиблого В&rsquo;ячеслава Чорновола</span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,7002,200,4d27667.jpg\" alt=\"\" width=\"200\" height=\"150\" />25 березня на могилі українського політика, публіциста, літературного критика, діяч руху опору проти зросійщення та національної дискримінації українського народу, політичного в&rsquo;язня&nbsp;СРСР, провідника українського національно-демократичного визвольного руху кінця&nbsp;80-х &mdash;&thinsp;90-х років; Героя&nbsp;України було відправлено поминальну панахиду за трагічно загиблого В&rsquo;ячеслава Чорновола.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">З року в рік парох та парафіяни церкви св. Миколая Чудотворця на Аскольдовій Могилі, рідні та близькі звершують поминальну службу на могилі політика.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">25&nbsp;березня&nbsp;1999&nbsp;року Вячеслав Чорновіл загинув за нез&rsquo;ясованих обставин в автокатастрофі на шосе під Борисполем. Трагедія сталася на&nbsp;5-му кілометрі автотраси&nbsp;Бориспіль&mdash;Золотоноша. Автомобіль Чорновола врізався в&nbsp;&laquo;КамАЗ&raquo;&nbsp;з причепом, який розвертався посеред шосе. В.&nbsp;Чорновіл і його водій Євген Павлов загинули на місці.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><a href=\"https://www.youtube.com/watch?v=zKyq1XsndMQ\">https://www.youtube.com/watch?v=zKyq1XsndMQ</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&laquo;Оранта&raquo;</span></p>',NULL,5519,5,1,NULL,17),
(5945,'P','2016-03-28',1,NULL,'Rss','«Кримінальне покарання має на меті не помсту за правопорушення, а переміну життя цієї людини»','<p><img style=\"margin: 10px; float: right;\" alt=\"maxresdefault\" height=\"141\" width=\"250\" src=\"http://api.oranta.org/images/stories/maxresdefault.jpg\" />Отець Костянтин Пантелей, керівник Відділу душпастирства в пенітенціарній системі України Патріаршої курії УГКЦ, відповів на запитання Департаменту інформації УГКЦ.</p>\n<p><strong>Отче, уряд задекларував реформування пенітенціарної системи України. У чому, на ваш погляд, має полягати ця реформа?</strong></p>\n<p>Те, що планується така реформа, дуже добре. Бо не можливо, аби пенітенціарна система залишалася в теперішньому вигляді.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3076:2016-03-28-16-36-28&catid=37:spilnota-cat&Itemid=49',5520,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5946,'P','2016-04-29',0,NULL,'Note','Хресна Дорога словами Святого Письма','<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,7004,200,38.jpg\" alt=\"\" width=\"200\" height=\"112\" />Вступна молитва&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Отож ми вирушаємо в Єрусалим, і Син Чоловічий буде відданий первосвященикам і книжникам, і засудять Його на смерть. І віддадуть Його поганам, щоб ті глумилися, катували та розіп\'яли Його; а третього дня воскресне\" (Мт 20, 18 - 19).</span></p>','<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,7004,200,38.jpg\" alt=\"\" width=\"200\" height=\"112\" />Вступна молитва&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Отож ми вирушаємо в Єрусалим, і Син Чоловічий буде відданий первосвященикам і книжникам, і засудять Його на смерть. І віддадуть Його поганам, щоб ті глумилися, катували та розіп\'яли Його; а третього дня воскресне\" (Мт 20, 18 - 19).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Куди ж Я йду - ви знаєте путь... Я - путь, істина і життя!\" (Йо 14, 4 - 6).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Дбайте пильно про ліпші дари. Я ж показую вам путь куди значнішу\" (1 Кор 12, 31).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Приходь і йди за Мною\" (Мт. 19, 21).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Ось я... Говори, Господи, слуга Твій слухає\" (ІСам З,4, 9).</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,7004,200,1.jpg\" alt=\"\" width=\"112\" height=\"200\" />СТАЦІЯ І</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Пилат підписує декрет смерті на Ісуса&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава страстям Твоїм, слава довготерпінію Твоєму, Господи!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Тоді зібралися первосвященики і старші народу в палаці первосвященика, що звався Каяфа, і радились, як би то підступом схопити Ісуса й убити\" (Мтю 26, 3 - 4).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Засядьмо на Праведника, бо Він нам невигідний і противиться нашим учинкам; Він закидає нам гріхи проти закону, винує нас у вадах нашого виховання; гадає, нібито Він знає Бога... Засудімо Його на смерть ганебну, бо, за Його словами, допомога Йому прийде\" (Муд 2, 12 - 20).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Як же настав ранок, усі первосвященики і старші народу... зв\'язавши Його, повели та й передали правителеві Пилатові\" (Мт 27, 1 - 2).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Вийшов тоді до них Пилат і промовив: \"Яке оскарження принесли ви на цього чоловіка?\" (Йо 18, 29).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Що Ти таке зробив? І відповів Ісус: \"Я на те уродився\"\"Щ і прийшов у світ на те, щоб свідчити істину. Кожен, хто від Щ істини, слухає голос мій\"... Пилат сказав (до жидів): \"Жадної ! провини не знаходжу я на Ньому\" (Йо 18, 35 - 38).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Від тієї хвилини шукав Пилат можливости, як би Його підпустити. Але юдеї кричали: \"Відпустиш Його, то не будеш кесаревим другом!\" Пилат, зачувши ті слова,... видав Його їм на розп\'яття\" (Йо 19, 12 - 16).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"О глибино багатства, мудрости і знання Божого! Які незбагненні Його постанови і недослідимі Його дороги!\" (Рим 11,33).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отче наш...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Богородице Діво...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">О Маріє, Тебе прошу...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Претерпівий за нас страсти...</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,7004,200,5.jpg\" alt=\"\" width=\"112\" height=\"200\" />СТАЦІЯ II</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Ісус бере на Свої рамена тяжкий хрест&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава страстям Твоїм, слава довготерпінію Твоєму, Господи!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"І забрали вони Ісуса; і, несучи для Себе хрест, вийшов Він на місце, зване Череп, а по-єврейськи Голгота\" (Йо 19,І7).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Ось Агнець Божий, який світу гріх забирає\" (Йо 1,29).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Зневажений, останній між людьми, чоловік болів, що зазнав недуги... Він наші недуги взяв на Себе, Він ніс на собі наші болі... Кара, що нас спасає, була на Ньому... Немов ягня, що на заріз ведуть його, немов німа вівця перед обстригачами, не відкривав Він уст\" (Іс 53, 3 - 7).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Ісус сказав до своїх учнів: \"Коли хтось хоче йти за Мною, нехай зречеться себе самого, візьме хрест свій і йде за Мною. Хто не бере свого хреста й не йде слідом за Мною, той недостойний Мене\" (Мт 16, 24; 10, 38).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отче наш...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Богородице Діво...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">О Маріє, Тебе прошу...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Претерпівий за нас страсти...</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,7004,200,10.jpg\" alt=\"\" width=\"112\" height=\"200\" />СТАЦІЯ III</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Ісус перший раз падає під тягарем хреста&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава страстям Твоїм, слава довготерпінію Твоєму, Господи!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Серце моє розколотилось, сила мене полишила і навіть очей моїх світло, - і того вже нема в мене. Друзі мої та приятелі далекі від моєї рани, і родичі мої стоять оподаль... Принижений, зігнувсь я вельми...\" (Пс 38, 11-12, 7).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Цар ... простерся на землі...\" (2 Сам 13, 31).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Чому ти це зробив?\" (Бут З, 13).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Ти гріхами своїми мене непокоїв, ваготою був мені з-за своїх переступів\" (Іс 43, 24).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Коліна в мене хитаються...\" (Пс 109, 24).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Стопи мої твердо тримаються твоєї стежки, і ноги мої не спіткнулись\" (Пс 17, 5).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Господь став при мені й підкріпив мене, щоб проповідь (Євангелія) здійснилася через мене...\" (2 Тим 4, 17).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отче наш...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Богородице Діво...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">О Маріє, Тебе прошу...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Претерпівий за нас страсти...</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,7004,200,12.jpg\" alt=\"\" width=\"112\" height=\"200\" />СТАЦІЯ IV</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Ісус зустрічає Свою Многострадальну Матір&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава страстям Твоїм, слава довготерпінію Твоєму, Господи!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Симеон... сказав до Його (Ісуса) Матері Марії: \"Ось цей поставлений для падіння й підняття багатьох в Ізраїлі; Він буде знаком протиріччя, та й Тобі самій меч прошиє душу, щоб відкрились думки багатьох сердець\" (Лк 2, 34 - 35).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"О, всі ви, що дорогою проходите повз! О, гляньте, подивіться, чи є біль, як мій біль, що мені заподіяно?..\" (Пл Єр. І, 12).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Дитино, чому Ти це так зробив нам? Ось батько Твій І* і Я, боліючи, Тебе шукали\". Він же відповів їм: \"Чого ж ви | Мене шукали? Хіба не знали, що я маю бути при справах Отця Мого?\" (Лк 2, 48 - 49).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"До чого Мені Тебе уподобите, дочко єрусалимська? З чим Тебе порівняти, щоб Тебе потішити, дівице-дочко сіонська?\" (Пл Єр 2, 13).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Годі тобі вже ридати та голосити, хай очі твої сліз не проливають! Є бо нагорода за біль твій...\" (Єр 31, 16).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Благословенна ти, дочко, Богом Всевишнім понад усіх жінок на землі! Благословенний Господь Бог, що створив небо й землю, що повів тебе...\" (Юд 13, 18).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отче наш...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Богородице Діво...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">О Маріє, Тебе прошу...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Претерпівий за нас страсти...</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,7004,200,14.jpg\" alt=\"\" width=\"112\" height=\"200\" />СТАЦІЯ V</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Симеон з Киринеї допомагає Ісусові нести хрест&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава страстям Твоїм, слава довготерпінію Твоєму, Господи!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Я голосом моїм до Господа взиваю... Коли тривожиться у мені дух мій, - Ти знаєш мою стежку. На дорозі, де я ступаю... поглянь праворуч і подивися: нема нікого, хто за мене дбав би; нема нікого, хто б мною піклувався\" (Пс 142, 2 - 5).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Праведник гине, і ніхто на те уваги не звертає. Побожні люди з землі зникають...\" (Іс 57, 1).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Нема нікого... нема ані одного\" (Пс 14, 3).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Одного ж перехожого, Симона Киренея, батька Олександра та Руфа, що повертався з поля, присилували нести Його хрест\" (Мр 15, 21 - 22).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Я скинув з Його плечей ношу\" (Пс 81,7).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Носіте тягарі один одного й тим робом виконаєте закон Христа\" (Гал 6, 2).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отче наш...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Богородице Діво...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">О Маріє, Тебе прошу...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Претерпівий за нас страсти...</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,7004,200,16.jpg\" alt=\"\" width=\"112\" height=\"200\" />СТАЦІЯ VI</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Вероніка обтирає Ісусові Обличчя&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава страстям Твоїм, слава довготерпінію Твоєму, Господи!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Я чекав милосердя, та його не було, і утішителів, - та не знайшов нікого\" (Пс 69, 21).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Не було в Ньому ні виду, ні краси, - ми бачили Його, -ні вигляду принадного не було в Ньому\" (Іс 53, 2).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"А я - черв\'як, не людина; сміховище людей, презирство народу. Всі, хто на мене дивляться, глузують з мене\" (Пс 22, 7 -8).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Бо ж я побачив лице твоє, наче б хто бачив лице Боже, й ти прийняв мене приязно\" (Бут 33, 10).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Блаженний той, хто не спотикнеться через Мене\" (Лк&nbsp;7, 23).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Він - образ невидимого Бога, Первородний усякого створіння, бо в Ньому все було створене, що на небі і що на землі\" (Кол 1, 15 - 16).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отче наш...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Богородице Діво...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">О Маріє, Тебе прошу...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Претерпівий за нас страсти...</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,7004,200,17.jpg\" alt=\"\" width=\"112\" height=\"200\" />СТАЦІЯ VII</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Ісус вдруге падає під хрестом&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава страстям Твоїм, слава довготерпінію Твоєму, Господи!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"І коли я хитався, вони раділи й збирались; збирались проти мене і били мене зненацька, рвали мене на шматки безупину. Спокушали мене, сміялися з мене, скрегочучи зубами проти мене\" (Пс 35, 15 - 16).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"І де раз ліг, то більш уже не встане\" (Пс 41,9).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Яка користь з моєї крови?...\" (Пс 30, 10)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Він - примирення за наші гріхи, і не лише за наші, а ИГЩ за гріхи усього світу\" (1 Йо 2, 2).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Він очистить наше сумління від мертвих діл, на служіння Богові живому!\" (Євр 9, 14).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Знайте, що не тлінним золотом ви були вибавлені від вашого життя суєтного, ... а Дорогоцінною Кров\'ю Христа, непорочного й чистого ягняти\" (Шт 1, 18 - 19).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Вдивляйтеся пильно в Ісуса, засновника й завершителя віри, який замість радости, що перед Ним була, витерпів хрест, на сором не звертаючи уваги, і який возсів праворуч Божого престолу. Тож думайте про Того, хто витерпів від грішників таке велике проти себе противенство, щоб ви, ізнемігшись, не впали духом. Ви ще не протиставились до крови в боротьбі проти гріха\" (Євр 12, 2 - 4).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отче наш...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Богородице Діво...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">О Маріє, Тебе прошу...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Претерпівий за нас страсти...</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,7004,200,19.jpg\" alt=\"\" width=\"112\" height=\"200\" />СТАЦІЯ VIII</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Ісус промовляє до Єрусалимських невіст&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава страстям Твоїм, слава довготерпінію Твоєму, Господи!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Ішов за Ним натовп людей великий і жінки, що плакали за Ним та голосили. Ісус же обернувся до них і сказав: \"Дочки єрусалимські, не плачте надо Мною, а плачте над собою і над вашими дітьми! Бо ось настануть дні, коли скажуть: Щасливі неплідні та й лона, що не родили, і груди, що не годували... Бо коли так обходяться з деревом зеленим, що тоді з сухим буде?\"</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">(Лк 23,27- 31).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Ліпше вмерти бездітним, ніж мати дітей нечестивих\"&nbsp;(Сир 16, 3).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Краса-річ оманлива, врода - марна; жінка, що Господа боїться, - така хвали достойна!\" (Прип 31, 30).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Радуйся, &nbsp;благодатна, &nbsp;Господь &nbsp;з&nbsp;Тобою (Маріє)! Благословенна Ти між жінками\" (Лк 1, 28).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отче наш...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Богородице Діво...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">О Маріє, Тебе прошу...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Претерпівий за нас страсти...</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,7004,200,21.jpg\" alt=\"\" width=\"112\" height=\"200\" />СТАЦІЯ IX</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Ісус упадає третій раз під тягарем хреста&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава страстям Твоїм, слава довготерпінію Твоєму, Господи!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Я ж бідний і страждаю\" (Пс 69, ЗО).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Не під силу мені одному нести... занадто тяжкий (тягар) для мене\" (Чис 11, 14).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Мою душу гонить ворог, життя моє у землю топче, він оселює мене в пітьмі... Дух мій тривожиться у мені й серце у мені жахається всередині\" (Пс 143, 3 - 4).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Надіявся, на Господа надіявся я твердо, і Він нахиливсь до мене й вислухав моє благання. І витягнув мене з погибельної ями, з багнистого болота і поставив на скелю мої ноги, ствердивши мої кроки\" (Пс 40, 2 - 3).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Ісус нахилився додолу і писав пальцем по землі. А що вони наполягали та допитувалися в Нього, то Він підвівсь і каже до них: \"Хто з вас без гріха - нехай перший кидає у неї камінь!\" І знову нахилившись, писав по землі... І залишилися тільки Ісус та жінка, що стояла посередині. Підвівсь Ісус і мовить до неї: \"Жінко... Ніхто не осудив тебе?\" \"Ніхто, Господи\", -відповіла. Тоді Ісус до неї: \"То і Я тебе не осуджую. Йди та вже віднині не гріши\" (Йо 8, 6 - 11).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отче наш...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Богородице Діво...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">О Маріє, Тебе прошу...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Претерпівий за нас страсти...</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,7004,200,25.jpg\" alt=\"\" width=\"112\" height=\"200\" />СТАЦІЯ X</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>З Ісуса здирають одіж&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава страстям Твоїм, слава довготерпінію Твоєму,&nbsp;Господи!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Прибувши на місце, що зветься Голгота, тобто \"Череп-місце\", дали Йому випити вина, змішаного з але Він, покуштувавши, не хотів пити. Ті ж, що розп\'яли Його, поділили Його одежу, кинувши жереб\" (Мт 27, 33 -35).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Всі, хто на мене дивляться, глузують з мене, кривлять губи, кивають головою... Дивляться на мене і з радістю позирають. Одежу мою ділять між собою і на хитон мій жеребок кидають\" (Псю 22, 8, 18 - 19).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Ти знаєш мою ганьбу, мій сором і мою зневагу; усі гнобителі мої перед Тобою\" (Пс 69, 20).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Та ви не так Христа вивчили, як це про Нього чули й були навчилися у Ньому, як то воно справді є в Ісусі. А саме: вам треба позбутися, за вашим попереднім життям, старої людини, яку розтлівають звабливі пристрасті, а відновитись духом вашого ума й одягнутись у нову людину, створену на подобу Божу, у справедливості й у святості правди\" (Еф 4, 20 -24).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отче наш...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Богородице Діво...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">О Маріє, Тебе прошу...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Претерпівий за нас страсти...</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,7004,200,31.jpg\" alt=\"\" width=\"112\" height=\"200\" />СТАЦІЯ XI</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Кати прибивають Ісуса до хреста&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава страстям Твоїм, слава довготерпінію Твоєму, Господи!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Тоді розіп\'яли Його й поділили Його одежу, кинувши на неї жереб, хто що візьме. Була ж третя година, коли вони розіп\'яли Його... І розіп\'яли з Ним трьох розбійників, одного праворуч, а другого ліворуч від Нього. Тоді збулось Писання, що каже: \"І з беззаконними полічено Його\" (Мк 15, 24 - 28).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"О мій народе, що я тобі зробив? Чим тебе скривдив? Відповідай мені!\" (Мі 6, 3).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"За любов мою вони зо мною ворогують; я ж молюся.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Зло за добро навалюють на мене і ненависть замість любови\" І (Пс109,4-5).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Пробили мені руки й ноги, всі мої кості я міг би полічити...\" (Пс 22, 17- 18).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Віддаю Себе за них у посвяту, щоб і вони були освячені в істині\" (Йо 17, 19).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Я-розп\'ятий з Христом. Живу вже не я, а живе Христос у мені. А що живу тепер у тілі, то живу вірою в Божого Сина, який полюбив мене й видав себе за мене. Я не відкидаю Божої благодаті\" (Гал 2, 19 - 21).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отче наш...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Богородице Діво...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">О Маріє, Тебе прошу...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Претерпівий за нас страсти...</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,7004,200,33.jpg\" alt=\"\" width=\"112\" height=\"200\" />СТАЦІЯ XII</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Ісус умирає на хресті&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава страстям Твоїм, слава довготерпінію Твоєму, Господи!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"І розп\'яли Його... Ісус же сказав: \"Отче, відпусти їм, не знають бо, що роблять\" (Лк 23, 33 - 34).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Другий злочинець сказав: \"Ісусе! Згадай про мене, як прийдеш у Своє Царство\". Ісус відповів: \"Істинно кажу тобі: сьогодні будеш зо мною в раї\" (Лк 23, 39 - 40; 42 - 43).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"А при хресті Ісусовім стояли Його Мати, сестра Його Матері, Марія Клеопова та Марія Магдалина. Бачивши Ісус Матір і біля Неї учня, що стояв, - а його ж любив Він, - мовить до Матері: \"Жінко, ось син Твій\". А тоді до учня мовить: \"Ось Матір твоя\" (Йо 19, 25 - 27).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Було вже близько шостої години, і темрява по всій землі настала аж до дев\'ятої години, бо затьмарилось сонце; а й завіса храму роздерлася посередині. Ісус закликав сильним голосом: \"Отче, у Твої руки віддаю духа мого!\" Сказавши це, Він віддав духа. (Лк 23, 44 - 46).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Справді цей чоловік був праведний\" (Лк 23, 47).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Ім\'я його буде повік благословенне; аж поки сонця, і ім\'я його буде; в ньому благословляться усі земні народи, і і прославлятимуть його усі народи\" (Пс 72, 17).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отче наш...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Богородице Діво...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">О Маріє, Тебе прошу...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Претерпівий за нас страсти...</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,7004,200,35.jpg\" alt=\"\" width=\"112\" height=\"200\" />СТАЦІЯ XIII</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Ісуса знімають із хреста&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава страстям Твоїм, слава довготерпінію Твоєму, Господи!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Щоб не залишилися в суботу тіла на хресті... юдсї попросили Пилата, щоби переламали їм голінки й познімали з хреста. Отож вояки прийшли і переламали першому голінки і другому, які з Ним були розп\'яті. Та коли підступили до Ісуса й побачили, що Він уже мертвий, то голінок не перебивали Йому, лиш один з вояків проколов Йому списом бік. І потекла негайно ж кров - і вода... Після того Иосиф Ариматейський... удався до Пилата з проханням, щоби забрати тіло Ісуса. І дозволив Пилат\" (Йо 19,31-38).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"На мій біль не знаходжу ліку, серце моє в мені мліє\" (Єр 8,18).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Рушайте, діти, рушайте, а я, полишена, залишуся самотою! Я сподіваюсь від Предвічного для вас рятупку, і прийде мені радість від Святого через ту милость, що надійде вам незабаром від Предвічного, вашого Спасителя\" (Вар 4, 19 -22).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Тому, отже, що Христос страждав тілом, ви теж озбройтеся тією самою думкою, а саме: хто страждав тілом, гой перестав грішити, щоб решту часу жити, в тілі, не за людськими похотями, а за волею Божою\" (1 Пт 4, 1 - 2).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отче наш...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Богородице Діво...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">О Маріє, Тебе прошу...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Претерпівий за нас страсти...</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,7004,200,40.jpg\" alt=\"\" width=\"112\" height=\"200\" />СТАЦІЯ XIV</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Ісуса вкладають до гробу</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Слава страстям Твоїм, слава довготерпінію Твоєму, Господи!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Як же настав вечір, прийшов заможний чоловік з Ариматеї, на ім\'я Йосиф... Він прийшов до Пилата і просив тіла Ісуса. Тоді Пилат звелів видати тіло. Йосиф узяв тіло, загорнув його в біле полотно й поклав у своїй новій гробниці, що її висік у скелі. І, прикотивши до входу гробниці великий камінь, відійшов. А була там Марія Магдалина й інша Марія, що сиділи проти гробниці. На другий день... первосвященики й фарисеї... пішли й забезпечили гробницю, запечатавши камінь, і поставили сторожу (Мт 27, 57 - 66).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Істинно, істинно говорю вам: Пшеничне зерно, коли не впаде на землю і не завмре, залишиться саме-одне; коли ж завмре, то рясний плід принесе\" (Йо 12, 24).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Після трудів душі своєї він побачить світло і насититься своїм знанням. Слуга мій виправдає багатьох, їхні беззаконня понесе на собі. Тому я дам йому, як пай, премногих; він з сильними буде ділити здобич, бо видав на смерть свою душу і був зачислений до лиходіїв, коли він узяв на себе гріхи багатьох і за грішників заступався\"(Іс 53, 11 - 12).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"На це бо Христос умер і воскрес, щоб і над мертвими, і над живими панувати\" (Рм 14, 9).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отче наш...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Богородице Діво...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">О Маріє, Тебе прошу...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Претерпівий за нас страсти...</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,7004,200,42.jpg\" alt=\"\" width=\"112\" height=\"200\" />Кінцева молитва</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Чи ви це все зрозуміли?\" (Мт 13, 51).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Чи не палало наше серце в нас у грудях, коли Він промовляв до нас у дорозі та вияснював нам Писання?\" (Лк 24, 32).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Він, мов той пагін, виріс перед нами, мов корінь із&nbsp;землі сухої. Не було в Ньому ні виду, ні краси... Зневажений, останній між людьми, чоловік болів... Він наші недуги взяв на Себе, Він ніс на Собі наші болі... Він же був поранений за гріхи наші, роздавлений за беззаконня наші... Його ранами ми&nbsp;вилікувані\" (Іс 53, 2 - 5).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Коли ж ми вмерли з Христом, то віруємо, що й&nbsp;житимемо з Ним\" (Рм 6, 8).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Через багато страждань нам треба ввійти в царство&nbsp;Боже\" (Ді 14, 22).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">\"Приклад дав Я вам, щоб і ви так робили, як оце Я вам&nbsp;учинив\" (Йо 13, 15).</span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Передрук: &laquo;Розважання Хресної дороги&raquo;. &ndash; &laquo;Міхалінеум&raquo;, Варшава, 1996</span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><a href=\"https://www.facebook.com/media/set/?set=a.1162559367110009.1073741939.198406070192015&amp;type=3\">https://www.facebook.com/media/set/?set=a.1162559367110009.1073741939.198406070192015&amp;type=3</a></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;</span></p>',NULL,5521,5,1,NULL,41);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5947,'P','2016-03-28',1,NULL,'Rss','«Боже ім’я – Милосердя» /6/','<p><img style=\"margin: 10px; float: left;\" alt=\"ANSA930832_Articolo\" height=\"177\" width=\"250\" src=\"http://api.oranta.org/images/stories/ANSA930832_Articolo.jpg\" />Читаючи книгу «Боже ім’я – Милосердя», що є плодом розмови Папи Франциска з італійським журналістом Андреа Торніеллі, попереднього разу ми почули спогади Святішого Отця про його особистий досвід сповідника. Серед іншого, він згадував: «Як сповідник, навіть коли мені доводилося бути перед зачиненими дверима, я завжди намагався знайти щілинку, просвіт, щоби відчинити їх та змогти дарувати прощення, милосердя».</p>\n<p>Самі ці слова «Шукати будь-який просвіт» дали назву третій частині книги, яка розпочинається запитанням: «Що потрібно для того, щоб отримати милосердя? Чи потрібна для цього якась особлива готовність?».</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3077:2016-03-28-16-42-35&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5522,NULL,1,1,26),
(5948,'P','2016-03-28',1,NULL,'Rss','Щорічна «повинність», або сповідь – не допит','<p><img style=\"margin: 10px; float: right;\" alt=\"658792134\" height=\"157\" width=\"250\" src=\"http://api.oranta.org/images/stories/658792134.jpg\" />Чи достатньо все-таки сповідатись раз в рік? Як поводитись, якщо під час сповіді священик накричав, образив? Чи можу я довіряти священикові, відкриваючи йому найскоровенніше? Як готуватись до сповіді? Як сповідатись, що розповідати? Чи після однієї сповіді можливе лише одне причастя? Спробуємо роздумати разом.</p>\n<p>Найперше (адже в теперішній час – час Великого Посту, це питання дуже актуальне) поговоримо про «річну сповідь». У минулорічних нотатках, присвячених сповіді, ми вже говорили, що сповіді раз в рік явно недостатньо для духовного життя. Вона є достатньою лише в тому випадку, якщо сповідь стає обов’язковою формальністю, яку в часі посту треба «відбути».</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3078:2016-03-28-17-08-40&catid=27:gk-v-ukrajini-cat&Itemid=57',5523,NULL,1,1,26),
(5949,'P','2016-03-29',1,NULL,'Rss','«Боже ім’я – Милосердя» /7/','<p><img style=\"margin: 10px; float: left;\" alt=\"ANSA930832_Articolo\" height=\"177\" width=\"250\" src=\"http://api.oranta.org/images/stories/ANSA930832_Articolo.jpg\" />«Грішник, як і Симон-Петро» – таким є заголовок четвертого розділу книги-інтерв’ю «Боже ім’я – Милосердя», що є плодом розмови Папи Франциска з італійським журналістом Андреа Торніеллі. Він розпочинається зауваженням відомого ватиканіста, що Святіший Отець неодноразово окреслював себе, як «грішник». Зокрема, відвідуючи в’язницю у Болівії, він сказав: «Перед вами людина, якій прощено її численні гріхи». «Це вражаюче, чути, як Папа говорить такі речі про себе…», – зауважив журналіст.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3079:2016-03-29-15-17-33&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5524,NULL,1,1,26),
(5950,'P','2016-03-29',1,NULL,'Rss','Кілька питань щодо Божественної Літургії Передшеосвячених Дарів','<p><img style=\"margin: 10px; float: right;\" alt=\"183999.p\" height=\"167\" width=\"250\" src=\"http://api.oranta.org/images/stories/183999.p.jpg\" />Час Великої Чотиридесятниці є особливим під багатьма оглядами. У традиційних християнських церквах це час переосмислення власних поглядів на своє життя й на особисте християнство. Це час приготування до найбільшого із свят усього християнського світу – свята Воскресіння Христового. Свято Воскресіння є джерелом віри християн і, водночас, сповненням їх остаточного очікування – входження у радість Воскреслого, участі вже тут і тепер у вічності Його життя.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3080:kilka-pytan-shchodo-bozhestvennoji-liturgiji-peredsheosvjachenyh-dariv&catid=27:gk-v-ukrajini-cat&Itemid=57',5525,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5951,'P','2016-03-29',1,NULL,'Rss','Андрей Шептицький про сповідь','<p><img style=\"margin: 10px; float: left;\" alt=\"379890\" height=\"188\" width=\"250\" src=\"http://api.oranta.org/images/stories/379890.jpg\" />Коли ж сповідаєшся, брате, то вже все скажи, усі рани душі відслони, усі мерзенні вчинки витягни на верх, хоч би вони десь на дні душі були закриті й від п\'ятдесяти літ. А коли найдеш у своїй душі тяжкий гріх, коли найдеш гріх несповіданий, закритий зі страху чи зі стиду, то вже конечно поправ усі свої сповіді, скажи всьо, що лишень пам\'ятаєш! Бо хто знає, чи ця твоя сповідь не буде послідною в твому житті! Не знати чи дочекаєшся літа, чи діждеш Успення Богородиці.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3081:andre-sheptycky-pro-spovid&catid=35:istorychni-postati-cat&Itemid=48',5526,NULL,1,1,26),
(5952,'P','2016-03-30',1,NULL,'Rss','Кардинал Любомир (Гузар): Священики повинні постійно нагадувати, що корупція – це гріх','<p><img src=\"http://api.oranta.org/images/stories/5-1.jpg\" width=\"250\" height=\"152\" alt=\"5-1\" style=\"margin: 10px; float: right;\" /></p>\n<p>Українські священнослужителі повинні виховувати громадянина, давати йому моральні орієнтири і на проповідях систематично повторювати, що корупція це гріх. Про це у інтерв’ю&nbsp;<a target=\"_blank\" href=\"http://fakty.ua/214561-lyubomir-guzar-u-nas-ochen-populyarno-plakatsya-vmesto-togo-chtoby-bratsya-za-rabotu-no-net-prepyatstvij-dlya-dobra-krome-sobstvennoj-leni\">\"Фактам\"</a> розповів Архиєпископ-емерит Любомир (Гузар).</p>\n<p>«Одним з елементів, завдяки якому Польща так швидко інтегрувалася в ЄС, було те, що їх Церква часто і послідовно проповідувала, як повинен вести себе віруюча людина.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3082:kardynal-ljubomyr-guzar-svjashchenyky-povynni-postino-nagaduvaty-shcho-korupcija-ce-grih&catid=28:tema-nomera-cat&Itemid=2',5527,NULL,1,1,26),
(5953,'P','2016-03-30',1,NULL,'Rss','«Боже ім’я – Милосердя» /8/','<p><img style=\"margin: 10px; float: left;\" alt=\"ANSA930832_Articolo\" height=\"177\" width=\"250\" src=\"http://api.oranta.org/images/stories/ANSA930832_Articolo.jpg\" />«Кілька років тому в одній із шкіл на Півночі Італії вчитель під час уроків релігії пояснював учням притчу про блудного сина, а опісля поставив перед ними завдання написати твір на вільну тему на основі щойно почутого. Більшість учнів обрали такий кінець для цієї історії: батько прийняв блудного сина, але суворо покарав і поселив між слугами, щоб він навчився, що означає розтринькувати родинні багатства…». «Але ж це звичайна людська реакція. Це&nbsp; старший син реагує по-людському. Боже милосердя, натомість, є божественним», – цим діалогом між Андреа Торніеллі та Папою Франциском розпочинається коротенький п’ятий розділ книги-інтерв’ю «Боже ім’я – Милосердя», що має назву: «Чи милосердя надмірне?».</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3083:2016-03-30-12-09-22&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5528,NULL,1,1,26),
(5954,'P','2016-03-30',1,NULL,'Rss','Реколекційний семінар для катехитів','<p align=\"right\"><em><img src=\"http://api.oranta.org/images/stories/106494.jpg\" width=\"250\" height=\"160\" alt=\"106494\" style=\"margin: 10px; float: right;\" />«Будьте милосердні, як і Отець ваш милосердний» (Лк 6, 35-36).</em><em></em></p>\n<p>Теплими сонячними днями 3 25-27 березня 2016 р. Б. Катехити Київської Архиєпархії УГКЦ мали чудову нагоду відпочити та духовно збагатитися у стінах Київської Трьохсвятительської духовної семінарії &nbsp;взявши участь у семінарі на тему: «Євангелізація для дорослих УГКЦ в світлі року Божого Милосердя», який супроводжував о. Тарас Барщевський.</p>\n','','http://api.oranta.org/index.php?option=com_content&view=article&id=3084:rekolekciny-seminar-dlja-katezhytiv&catid=25:parafija-cat&Itemid=27',5529,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5955,'P','2016-03-30',1,NULL,'Rss','«Боже ім’я – Милосердя» /9/','<p><img style=\"margin: 10px; float: left;\" alt=\"ANSA930832_Articolo\" height=\"177\" width=\"250\" src=\"http://api.oranta.org/images/stories/ANSA930832_Articolo.jpg\" />«Пастирі, а не книжники», – такий заголовок має шостий розділ книги «Боже ім’я – Милосердя», яка є записом розмови з Папою Франциском італійського журналіста Андреа Торніеллі, який на початку цього розділу запитував співрозмовника: «Чи можливе милосердя без визнання власного гріха?».</p>\n<p>«Милосердя є, але якщо ти не бажаєш його прийняти… Коли ти не визнаєш себе грішником, це означає, що ти не бажаєш його прийняти, що ти не відчуваєш у цьому потребу, – відповів Святіший Отець. – Іноді можеш мати відчувати труднощі в тому, аби зрозуміти, що трапилося. Іноді можеш почуватися зневіреним, вважати, що підвестися неможливо.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3085:2016-03-30-18-39-16&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5530,NULL,1,1,26),
(5956,'P','2016-03-30',1,NULL,'Rss','Спогади про кривду 1946-го','<p><img style=\"margin: 10px; float: right;\" alt=\"1-web\" height=\"193\" width=\"250\" src=\"http://api.oranta.org/images/stories/1-web.jpg\" />Минуло 70&nbsp;років з&nbsp;часу неправедного Львівського псевдособору УГКЦ&nbsp;— час цілком достатній для того, щоб охопити зором усю цю&nbsp;болісну сторінку нашої історії.<o:p></o:p></p>\n<p>Почати аналіз можна з&nbsp;того моменту, коли внаслідок Ялтинського переділу світу українська Галичина відійшла до&nbsp;Радянського Союзу. Перед Сталіним відразу постала проблема, як&nbsp;упокорити цей нескорений край. Адже на&nbsp;той час уся територія України вже була «зачищена» від будь-якого протестного елемента: колективізація, судовий процес Спілки визволення України, «ліквідаційний» собор УАПЦ 1930&nbsp;року, Голодомор, безконечні чистки і&nbsp;судилища, а&nbsp;тоді війна&nbsp;— все це&nbsp;врешті-решт доконало Велику Україну. По&nbsp;війні настала черга Галичини.<o:p></o:p></p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3086:spogady-pro-kryvdu-1946-go&catid=28:tema-nomera-cat&Itemid=2',5531,NULL,1,1,26),
(5957,'P','2016-03-31',1,NULL,'Rss','«Боже ім’я – Милосердя» /10/','<p><img style=\"margin: 10px; float: left;\" alt=\"ANSA930832_Articolo\" height=\"177\" width=\"250\" src=\"http://api.oranta.org/images/stories/ANSA930832_Articolo.jpg\" />«Пастирі, а не книжники», – такий заголовок має шостий розділ книги «Боже ім’я – Милосердя», яка є записом розмови з Папою Франциском італійського журналіста Андреа Торніеллі, який запитував співрозмовника: «Чи існує протиставлення між істиною та милосердям або між доктриною та милосердям?».</p>\n<p>«Відповім ось так: милосердя є істинним, воно – перша ознака Бога, – сказав Святіший Отець. – Далі можна провадити богословські роздуми про доктрину й милосердя, але при цьому не забуваючи про те, що саме милосердя вже є доктриною. Однак я віддаю перевагу говорити, що милосердя є істинним».</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3087:2016-03-31-10-57-34&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5533,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5958,'P','2016-03-31',1,NULL,'Rss','Існування голоду тоді, як третина продуктів харчування марнується, – це гріх проти Божого милосердя!','<p><strong><img style=\"margin: 10px; float: right;\" alt=\"holod_5ccf1\" height=\"188\" width=\"250\" src=\"http://api.oranta.org/images/stories/holod_5ccf1.jpg\" /></strong>Духовно-екологічні роздуми над першим ділом милосердя щодо тіла і щодо душі: «голодного нагодувати» і «грішника навернути».</p>\n<p align=\"center\"><em><strong>Голодного зможе побачити і нагодували лише той,</strong></em><em><strong> </strong></em><em><strong>хто, дивлячись на нього, побачить свого ближнього,</strong></em><em><strong> </strong></em><em><strong>хто в природних ресурсах розпізнає Божі дари, призначені для всіх.</strong></em></p>\n<p align=\"center\"><em><strong>Навернути грішника зможе лише той,</strong></em><em><strong> </strong></em><em><strong>хто побачить в ньому дитину Божу,</strong></em><em><strong> </strong></em><em><strong>яка потребує Божого милосердя.</strong></em></p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3088:isnuvannja-golodu-todi-jak-tretyna-produktiv-harchuvannja-marnujetsja-ce-grih-proty-bozhogo-myloserdja&catid=29:propovidi-cat&Itemid=34',5534,NULL,1,1,26),
(5959,'P','2016-04-23',0,NULL,'Note','ЗАГАЛЬНЕ ПОЛОЖЕННЯ про проведення шахових турнірів Назва турнірів « Ми діти Аскольда»','<ol>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,7018,200,4619208626.jpg\" alt=\"\" width=\"200\" height=\"69\" /><span style=\"text-indent: 0em; line-height: 1.3em;\">Шахові змагання проводяться з метою популяризації шахів, налагодження дружних відносин між шахістами, вдосконалення спортивної майстерності і пізнання християнської історії Руси-України і постаті першого хрестителя Аскольда-Миколая царя і мученика за віру Христову та визначення переможців та призерів змагань.</span></span></li>\r\n</ol>','<ol>\r\n<li style=\"text-align: justify;\"><span style=\"text-decoration: underline; font-family: arial, helvetica, sans-serif; font-size: small;\">Мета та завдання</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,7018,200,4619208626.jpg\" alt=\"\" width=\"200\" height=\"69\" />Шахові змагання проводяться з метою популяризації шахів, налагодження дружних відносин між шахістами, вдосконалення спортивної майстерності і пізнання християнської історії Руси-України і постаті першого хрестителя Аскольда-Миколая царя і мученика за віру Христову та визначення переможців та призерів змагань.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"text-decoration: underline; font-family: arial, helvetica, sans-serif; font-size: small;\">Час і місце проведення змагань</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Шахові змагання проводяться в сім етапів: півфінали &ndash; 2, 9, 16, 23 квітня, 7, 14 травня і фінал 22 травня за адресом &ndash; м. Київ вул. Паркова дорога Церква св. Миколая на Аскольдовій горі біля метро Арсенальна.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"text-decoration: underline; font-family: arial, helvetica, sans-serif; font-size: small;\">Система проведення змагань</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Шахові змагання I-го &ndash; VI-го півфіналів відбуваються за швейцарською системою у 5 турів. Контроль часу &ndash; 15 хв. на партію кожному учаснику. При собі мати комплект шахів. Кількість місць в кожному півфіналі обмежена &ndash; 12 чоловік.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">2, 9, 16, 23 квітня, 7, 14 травня &ndash; півфінальні змагання</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Реєстрація півфінальних змагань <strong>&ndash;</strong> 17.00-17.10</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Змагаються по швейцарській системі в 5 турів.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Контроль часу 15 хв на партію кожному учаснику</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Регламент змагань</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>1 тур &ndash; 17.20 &ndash; 17.50</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>2 тур &ndash; 17.55 &ndash; 18.25</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>3 тур &ndash; 18.30 &ndash; 19.00</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>4 тур &ndash; 19.05 &ndash; 19.35</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>5 тур &ndash; 19.40 &ndash; 20.10</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Закриття &ndash; 20.10 &ndash; 20.20</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Фінальні змагання проводяться по швейцарській системі. Контроль часу &ndash; 15 хв на партію кожному учаснику. У фінальних змаганнях приймають участь 18 юних шахістів &ndash; по 3 з кожного півфіналу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><span style=\"text-decoration: underline;\">22 травня &ndash; фінал</span></strong><strong> проводиться за коловою системою.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Початок змагання 22 травня о 10.00</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Регламент змагань</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>1 тур &ndash; 10.00 &ndash; 10.30</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>2 тур &ndash; 10.35 &ndash; 11.05</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>3 тур &ndash; 11.10 &ndash; 11.40</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>4 тур &ndash; 11.45 &ndash; 12.15</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>5 тур &ndash; 12.20 &ndash; 12.50</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>6 тур &ndash; 12.55 &ndash; 13.25</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>7 тур &ndash; 13.30 &ndash; 14.00</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Закриття &ndash; 14.10 &ndash; 14.20</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"text-decoration: underline; font-family: arial, helvetica, sans-serif; font-size: small;\">Учасники змагань</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">До участі в турнірах допускаються всі бажаючі віком до 12 років. Учасникам змагань дозволено виступати тільки в одному півфіналі. Фіналісти змагань мають обов&rsquo;язково зробити реферат про історію князя Аскольда.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"text-decoration: underline; font-family: arial, helvetica, sans-serif; font-size: small;\">Визначення переможців</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Місця учасників визначаються за найбільшою кількістю набраних очок. У разі однакової кількості набраних очок у двох або більше учасників змагань місця визначаються:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">I.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; У турнірах за швейцарською системою</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">за системою коефіцієнтів Бухгольца;</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">за системою прогресуючих коефіцієнтів;</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">за результатом особистої зустрічі.</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; line-height: 1.3em;\">II.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; У турнірі за коловою системою (при невеликій кількості учасників у півфіналі)</span></li>\r\n</ul>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-за системою коефіцієнтів Бергера;</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">за результатом особистої зустрічі;</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">за кількістю перемог;</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">за результатом тайм-брейку: бліц по 5 хвилин з 2-х партій (за рахунку 1:1 &ndash; до першої перемоги).</span></li>\r\n</ul>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"text-decoration: underline; font-family: arial, helvetica, sans-serif; font-size: small;\">Нагородження</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Призовий фонд фінального шахового турніру МИ ДІТИ АСКОЛЬДА становить 15000 грн.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Спонсор турніру &ndash; Благодійний фонд &laquo;ВОЇНИ ДОБРА&raquo;.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>7.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Головний суддя змагань &ndash; Кольба Андрій Богданович або Севастьянов Павло. Реєстрація на шахові турніри відбувається через електронну пошту </strong><a href=\"mailto:Kolbaandrii@gmail.com\">Kolbaandrii@gmail.com</a><strong>. Потрібно вказати:</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>1) прізвище та ім&rsquo;я учасника змагань, 2) число місяць рік народження, 3) розряд, </strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>4) рейтинг, якщо є, 5) дата участі у півфіналі, 6) шаховий клуб, 7) прізвище ім&rsquo;я тренера, 8) контактний телефон. Реєстрація на кожен півфінал закінчується за 12 год до початку змагань.</strong></span></p>',NULL,5535,5,1,NULL,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5960,'P','2016-04-01',1,NULL,'Note','Парафіяльні оголошення 04 - 10.04.2016','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,7019,200,1281436.jpg\" alt=\"\" width=\"200\" height=\"120\" />7 квітня <strong>Благовіщення Пресвятої Владичиці нашої Богородиці</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08:00 Утрення,09:00;18:00 Служба Божа</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>08:00-Утрення,Час1,Панахида</strong>відправляється понеділок,вівторок,четвер</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Середа, п&rsquo;ятниця -&nbsp;<strong>08:00</strong>&nbsp;,Літургія Передосвячених Дарів з Парастасом Сорокоустами за спочилих</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,7019,200,1281436.jpg\" alt=\"\" width=\"200\" height=\"120\" />7 квітня <strong>Благовіщення Пресвятої Владичиці нашої Богородиці</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">08:00 Утрення,09:00;18:00 Служба Божа</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>08:00-Утрення,Час1,Панахида</strong>відправляється понеділок,вівторок,четвер</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Середа, п&rsquo;ятниця -&nbsp;<strong>08:00</strong> ,Літургія Передосвячених Дарів з Парастасом Сорокоустами за спочилих</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>18:00 понеділок-пятниця&nbsp; </strong>Літургія Передосвячених Дарів,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>19:00 Середа і пятниця&nbsp; </strong>Хресна дорога,сповідь</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Пятниця 17-00 Хресна дорога від Дніпра з священиком і нагода до св.Тайни Сповіді</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>неділя</em></strong> &ndash; Служба Божа</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>08:00</strong> зі співом мирян,<strong> 10:00</strong> з хором,<strong> 12:30</strong> для дітей з хором,<strong> 18:00</strong> співає дяк</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>ДО УВАГИ!</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Працює &nbsp;кухня для потребуючих парафіян і біженців з Донбасу&nbsp; за таким розкладом вівторок,четвер,субота -12:00-13:00,неділя 14:00-15:00 в церковній хаті</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; катехизація дітей (довідка с. Меланія 0989285710)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; молодь зголошується до о. Івана (0506071960 / iwan_sokhan@yahoo.de / FB: Ivan Sokhan)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 8 квітня пятниця Хресна Дорога молодіжної спільноти парафії</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">продовжуємо збірку на лікування поранених в АТО (волонтерськ служіння Роксоляна) 9 квітня&nbsp; 11:00 Молебень до Хреста Милосердного; духовні науки &laquo;Будьте Милосердні&raquo; проводить о.Константин</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Продовжується цільова збірка на відновлення фасаду Церкви і встановлення Хреста Хрещення(866р) Оскольда-Миколая&nbsp; в&nbsp; 1150 річницю памяті.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">- Матері в молитві-молитовна спільнота проводить збірку на реставрацію Євангеліїя.котру цілував св.Іван-Павло11 в часі молитви на Аскольдовій могилі 23 червня 2001 р(проводить збірку п.Марія)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">2 квітня&nbsp; 17:00 в катехитичному залі на Аскольдовій могилі,діти до 12 років Початок шахового турніру &laquo;1150 р.Хрещення Аскольда-Миколая&raquo;призовий фонд 15000 гр,обов. реферат про історію князя Аскольда;Записуватись п.Андрій 097 4104678</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp;28 &nbsp;&nbsp;&nbsp;травня&nbsp; готуємось до&nbsp; вшанування 100років памяті Івана Франка</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">28 травня&nbsp; готуємось до&nbsp; вшанування 100років памяті Івана Франка</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Молитва памяті 8.04.+Тарас Проців 2003;+Анна Цембрівська 2012</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Многая літа! Вітаємо в День Народження </strong>&nbsp;5.04 Олега Б.;6.04 Ірина К;7.04 Володимира Литвинчук<strong>Подяка</strong> всім доброчинцям парафії,Павлу Михайловичу за недільні 20 обідів для кухні,Валерію і Ярославі за хліб для кухні,Василю Михайловичу за допомогу в спорудженні памятного Христа,Марії за варенники &nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>парох Ігор Онишкевич, </em></strong><em>митр. протопресвіте<strong> &nbsp;050 4473157&nbsp; oranta-gazeta@</strong></em><strong><em>ukr</em></strong><strong><em>.</em></strong><strong><em>net</em></strong></span></p>',NULL,5536,5,1,NULL,38);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5961,'P','2016-04-04',1,NULL,'Note','На Аскольдовій Могилі у Києві активно проводять зустрічі молоді','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,7020,200,9876625.jpg\" alt=\"\" width=\"193\" height=\"200\" />Головною темою для обговорення було &laquo;Спілкування про духовне життя з однолітками&raquo;, або ж &laquo;Позитивний і негативний досвід свідчення власної віри в Христа серед родини, друзів, одногрупників та співробітників&raquo;. Кожен з одинадцяти учасників зустрічі мав можливість поділитися власним цікавим досвідом, який неабияк укріпив інших. В ході спілкуваня піднімалися такі цікаві для молоді теми, як екстрасенсорика, філософія і християнство, негативні випадки в житті Церкви та ін.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отож, запрошуємо молодь Києва на щонедільні зустрічі при парафії Св. Миколая на Аскольдовій могилі, завжди о 14:00 біля храму. Наступна зустріч відбудеться у неділю 10 квітня.</span></p>','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,7020,200,9876625.jpg\" alt=\"\" width=\"193\" height=\"200\" />&laquo;Цього разу нас зібралося вдвічі більше порівняно з попередньою неділею, і це не може не радувати. Цікавості додавала конфесійна різноманітність молоді, адже серед присутні були не тільки греко-католики, але й православні, і ті, хто відвідує протестантські спільноти&raquo;, &ndash; повідомляє у своїй сторінці фейсбук о. Іван Сохан, адміністратор парафії Святого Миколая Чудотворця УГКЦ на Аскольдовій Могилі у Києві.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Головною темою для обговорення було &laquo;Спілкування про духовне життя з однолітками&raquo;, або ж &laquo;Позитивний і негативний досвід свідчення власної віри в Христа серед родини, друзів, одногрупників та співробітників&raquo;. Кожен з одинадцяти учасників зустрічі мав можливість поділитися власним цікавим досвідом, який неабияк укріпив інших. В ході спілкуваня піднімалися такі цікаві для молоді теми, як екстрасенсорика, філософія і християнство, негативні випадки в житті Церкви та ін.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Окрім спільної молитви в особистих наміреннях та співу молодіжних релігійних пісень ми обговорювали минулотижневе &laquo;домашнє завдання&raquo;, християнський фільм &laquo;Вогнестійкий&raquo;, в якому представлено духовний шлях виходу християнського подружжя з кризи. Фільм виявився справді цікавим, і нікого не залишив байдужим.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Також молодь запропонувала інші цікаві теми для обговорень на наступних недільних зустрічах, а саме: &laquo;Як залишатися християнином у політиці?&raquo;, &laquo;Як бути християнином у журналістиці?&raquo;, &laquo;Християнське лідерство&raquo; та ін..</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Темою наступної зустрічі молоді буде &laquo;Як залишатися християнином у політиці?&raquo;, а домашнє завдання на цей день &ndash; перегляд фільму &laquo;Бог не вмер&raquo;. Крім того, наступного разу в четверту неділю посту ми розважатимемо над життям преподобної матері Марії Єгипетської.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Отож, запрошуємо молодь Києва на щонедільні зустрічі при парафії Св. Миколая на Аскольдовій могилі, завжди о 14:00 біля храму. Наступна зустріч відбудеться у неділю 10 квітня.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><a href=\"http://ugcc.kiev.ua/\">Прес-служба Київсбкої архиєпархії</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><a href=\"http://ugcc.kiev.ua/\">http://ugcc.kiev.ua/</a></span></p>',NULL,5537,5,1,NULL,17);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5962,'P','2016-04-04',1,NULL,'Rss','Блаженніший Святослав закликає вірних звернути увагу на діла милосердя щодо душі і щодо тіла','<p><strong><img src=\"http://api.oranta.org/images/stories/3572549651.jpg\" width=\"250\" height=\"188\" alt=\"3572549651\" style=\"margin: 10px; float: left;\" />Зазвичай Святі роки (їх ще називають Ювілейними) відбуваються в Католицькій Церкві кожні 25 років. Але бувають і Надзвичайні ювілеї, пов’язані з особливими датами церковної історії або ж присвячені особливим правдам віри, що їх Церква прагне наблизити до своїх вірних. Саме таким надзвичайним є нинішній Ювілейний рік, проголошений Папою Франциском. Він не відповідає хронологічному ритму церковного календаря і присвячений важливій правді християнського об’явлення – Божому милосердю.</strong></p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3089:blazhennishy-svjatoslav-zaklykaje-virnyh-zvernuty-uvagu-na-dila-myloserdja-shchodo-dushi-i-shchodo-tila&catid=28:tema-nomera-cat&Itemid=2',5538,NULL,1,1,26),
(5963,'P','2016-04-04',1,NULL,'Rss','Наша Надія і наші суди','<p><img style=\"margin: 10px; float: right;\" alt=\"huzar_blogy\" height=\"167\" width=\"250\" src=\"http://api.oranta.org/images/stories/huzar_blogy.jpg\" />В останні кілька місяців в Україні та поза її межами багато говорилося про долю української громадянки, депутата Парламентської асамблеї Ради Європи, льотчиці Надії Савченко.</p>\n<p>23 березня я мав нагоду почути розмову трьох осіб. Як незрячий, можу говорити про них тільки як про голоси. Перший голос дуже емоційно, з почуттям глибокого обурення коментував, що відбувалося впродовж довшого часу поневолення Надії Савченко, про різні судові махінації та про те, що сталося в кінці – скандальний, жорстокий, остаточний судовий вирок, який був об’єктивно безпідставний і несправедливий.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3090:nasha-nadija-i-nashi-sudy&catid=37:spilnota-cat&Itemid=49',5539,NULL,1,1,26),
(5964,'P','2016-04-05',1,NULL,'Rss','Послання Блаженнішого Святослава до духовенства і вірних УГКЦ та всіх людей доброї волі з нагоди Року Божого Милосердя','<p><strong><em><img style=\"margin: 10px; float: left;\" alt=\"thum\" height=\"188\" width=\"250\" src=\"http://api.oranta.org/images/stories/thum.jpg\" />Милосердя й вірність зустрінулися разом, справедливість і мир поцілувались між собою (Пс. 85, 11).</em></strong><strong><o:p></o:p></strong></p>\n<p><strong>Погляд, скерований до Бога,</strong><strong> </strong><strong>«багатого милосердям» (пор. Еф. 2, 4)</strong><o:p></o:p></p>\n<p style=\"text-align: center;\"><em>Дорогі в Христі!</em><o:p></o:p></p>\n<p>Відповідаючи на заклик Святішого Отця Франциска, Папи Римського, та взявши до уваги рішення Синоду Єпископів УГКЦ, ми започаткували в нашій Церкві Святий рік Божого милосердя, що триватиме від 8 грудня 2015 року до 26 листопада 2016 року Божого.<o:p></o:p></p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3091:poslannja-blazhennishogo-svjatoslava-do-duhovenstva-i-virnyh-ugkc-ta-vsih-ljude-dobroji-voli-z-nagody-roku-bozhogo-myloserdja&catid=28:tema-nomera-cat&Itemid=2',5540,NULL,1,1,26),
(5965,'P','2016-04-05',1,NULL,'Rss','Перед нами реальна загроза хаосу – Мирослав Попович','<p><img style=\"margin: 10px; float: right;\" alt=\"t280x180_ArticlePreviewImage_19046\" height=\"161\" width=\"250\" src=\"http://api.oranta.org/images/stories/t280x180_ArticlePreviewImage_19046.jpg\" />Так чи інакше, криза, що склалася, закінчиться новими виборами.</p>\n<p>Який уряд кращий для України:&nbsp;<em>технократичний</em> з віддаленими від політики професіоналами чи&nbsp;<em>політичний</em>, створений на основі парламентської коаліції?</p>\n<p>Сьогодні надії суспільства переважно пов’язані з першим варіантом – урядом технократів. Насамперед, це пояснюється тим, що люди розчаровані в політичних партіях, окремих особах і в політиці загалом. Звідси і парадоксальне прагнення мати партію, що не входить у жодну з партій.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3092:pered-namy-realna-zagroza-haosu--myroslav-popovych&catid=37:spilnota-cat&Itemid=49',5541,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5966,'P','2016-04-05',1,NULL,'Rss','«Боже ім’я – Милосердя» /11/','<p><img style=\"margin: 10px; float: left;\" alt=\"ANSA930832_Articolo\" height=\"177\" width=\"250\" src=\"http://api.oranta.org/images/stories/ANSA930832_Articolo.jpg\" />Читаючи попереднього разу уривок з шостого розділу книги «Боже ім’я – Милосердя», яка є записом розмови з Папою Франциском італійського журналіста Андреа Торніеллі, що має назву «Пастирі, а не книжники», ми побачили, як на прикладі євангельської події зцілення прокаженого Святіший Отець вказував на існування «двох логік мислення та віри». З одного боку – «страх втратити праведних», а з іншого – «бажання спасти грішників». За словами Папи, Ісус входить у контакт з прокаженим, доторкається його, чим і нас навчає, що слід чинити, за якою логікою слідувати, перемагаючи пересуди й закам’янілість».</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3093:2016-04-05-18-46-17&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5542,NULL,1,1,26),
(5967,'P','2016-04-06',1,NULL,'Rss','7 квітня – Свято Благовіщення – День подяки за дар життя!','<p><img style=\"margin: 10px; float: right;\" alt=\"Adopcja-1\" height=\"215\" width=\"250\" src=\"http://api.oranta.org/images/stories/Adopcja-1.jpg\" />7 квітня – Свято Благовіщення – День подяки за дар життя. У цей день у катедральних та парафіяльних храмах буде служитись Свята Літургія з наміренням подяки за дар життя кожної людини та проханням про збереження життя ненароджених дітей України.</p>\n<p>Комісія УГКЦ у справах душпастирства охорони здоров’я просить в цей день кожного призадуматись над даром життя, який ми отримали від Господа Бога, подякувати за ласку, що живемо, що створив нас з нічого і наділив нас високою гідністю бути Його образом і подобою. Застановімся на хвилину і пригадаймо собі, хто ми є, ким покликані до життя і з ким зустрінемось після закінчення земної мандрівки.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3094:7-kvitnja--svjato-blagovishchennja--den-podjaky-za-dar-zhyttja&catid=28:tema-nomera-cat&Itemid=2',5543,NULL,1,1,26),
(5968,'P','2016-04-06',1,NULL,'Rss','Слово Іоана Золотоустого на Благовіщення Преславної Владичиці нашої Богородиці','<p><img src=\"http://api.oranta.org/images/stories/1265916470_003-n-7_1.jpg\" width=\"250\" height=\"210\" alt=\"1265916470_003-n-7_1\" style=\"margin: 10px; float: left;\" /></p>\n<p>Знову радісне благовістя, знову вістка про волю, знову від­кликання назад, знову повернення, знову голос радості, знову звільнення від рабства. Ангел розмовляє з Дівою, оскільки з жінкою розмовляв змій.<em>\"Шостого ж місяця був посланий від Бога ангел Гавриїл до Діви, зарученої з мужем\"</em><em> </em>(Лк. 1, 26-27). Гавриїл був посланий об\'явити спасіння всьому світові. Гаври­їл був посланий принести Адамові обітницю про повернення із неволі. Гавриїл був посланий до Діви, щоб перемінити безчес­тя жіноцтва на честь. Гавриїл був посланий, щоби приготува­ти чистому Нареченому гідний чертог. Гавриїл був посланий, щоб заручити творіння з Творцем.</p>\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3095:slovo-ioana-zolotoustogo-na-blagovishchennja-preslavnoji-vladychyci-nashoji-bogorodyci&catid=30:hrystyjanske-vchennia-cat&Itemid=37',5544,NULL,1,1,26);
INSERT INTO `publications_publication` (`id`, `state`, `publication_date`, `show_date`, `slug`, `type`, `title`, `short_text`, `text`, `rss_url`, `old_id`, `author_id`, `locale_id`, `rss_stream_id`, `subcategory_id`) VALUES
(5969,'P','2016-04-07',1,NULL,'Rss','Боже Милосердя в ділі сотворіння','<p><em><img style=\"margin: 10px; float: right;\" alt=\"65685223\" height=\"172\" width=\"250\" src=\"http://api.oranta.org/images/stories/65685223.jpg\" />Господи, Боже наш, яке предивне твоє ім’я по всій землі! Ти возніс славу свою вище неба. З уст дітей і немовлят учинив єси хвалу супроти твоїх супостатів, щоб угамувати ворога й бунтаря</em> (Пс 8, 1-3).</p>\n<p>Цей псалом допомагає нам увійти в тему, над якою будемо роздумувати, в атмосферу прославлення Бога, Який послужився своїм створінням, щоб нас захопити і об’явити не тільки Свою велич, а, передусім, Свою любов і милосердя. Бог створив такий чудовий всесвіт, щоб ним нас осяяти. Однак, захоплюючись сотворінням Бога, ми не повинні затримувати свій погляд на ділі, яке вчинив Бог, а споглядатиАвтора,– Всевишнього.\n',NULL,'http://api.oranta.org/index.php?option=com_content&view=article&id=3096:bozhe-myloserdja-v-dili-sotvorinnja&catid=28:tema-nomera-cat&Itemid=2',5545,NULL,1,1,26);
