INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1082,'2009-01-29 22:18:51',1,'P','Завершив роботу Другий Всеукраїнський з’їзд головних військових капеланів УГКЦ  ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\"Важливою була участь у з\'їзді представників силових структур, адже саме постійний діалог допомагає розвивати співпрацю\", - зазначив о. Любомир Яворський, референт Відділу Патріаршої Курії УГКЦ у справах душпастирства силових структур України. Відтак о. Любомир розповів про працю відділу за попередній рік та поділився планами на майбутнє.</span></span></p>','<p style=\"text-align: justify;\"><img style=\"float: right;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_27-01-09-1.jpg.jpg\" alt=\"\" /><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З\'їзд відбувався 27 - 28 січня у с. Зарваниця, Тернопільської обл., в ньому взяли участь головні військові капелани всіх єпархії та екзархатів УГКЦ в Україні та представники силових структур України (Міністерства оборони України, Міністерства внутрішніх справ України, Внутрішніх військ МВС України, Державної Прикордонної служби України).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Своїм словом урочисто відкрив з\'їзд Преосвященний Владика Михаїл (Колтун), керівник Відділу Патріаршої Курії УГКЦ у справах душпастирства силових структур України, який зокрема відзначив: &laquo;Метою проведення такого з\'їзду є налагодження ефективної співпраці як між Церквою і силовими структурами, так і всередині Церкви, між&nbsp; капеланами різних єпархій УГКЦ та Відділом Патріаршої курії&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Крім того, до учасників з\'їзду звернувся Блаженніший Любомир в аудіо зверненні</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\"Важливою була участь у з\'їзді представників силових структур, адже саме постійний діалог допомагає розвивати співпрацю\", - зазначив о. Любомир Яворський, референт Відділу Патріаршої Курії УГКЦ у справах душпастирства силових структур України. Відтак о. Любомир розповів про працю відділу за попередній рік та поділився планами на майбутнє.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Заступник директора Департаменту гуманітарної політики Міністерства оборони України, Костюк Петро Іванович виступив із доповіддю про сучасний стан військово - релігійних відносин та перспективи їх розвитку. Він зокрема, зазначив: &laquo;Міністерство оборони України не стоїть осторонь процесів, пов\'язаних з запровадження капеланства, а засвідчує свою активну позицію, про що свідчить і директива, підписана Міністром оборони України, і підписаний меморандум про співпрацю між Міністерством та Церквами&raquo;. Представник Міністерства оборони також наголосив, що одним із завдань, визначених на 2009 рік, у гуманітарній сфері є створення сприятливих умов для підготовки до запровадження інституту військового капеланства.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Отець Степан Сус, головний військовий капелан Львівської архиєпархії УГКЦ, представив учасникам конференції сучасну картину практичного здійснення військового капеланства в українському війську. Зокрема, він відзначив на тому, що такого роду служіння має два виміри: капелани проводять духовно-просвітницьку працю серед військовослужбовців, а також здійснюють богослужіння у військових каплицях.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Диякон Руслан Коханчук, старший офіцер сектору роботи з релігійними організаціями Департаменту гуманітарної політики Міністерства оборони України виступив із доповіддю на тему: &laquo;Роль та місія греко-католицьких капеланів в кінці ХІХ на початку ХХ ст.&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Отець Михайло Сукмановський, який минулого року увійшов до складу українського миротворчого батальйону у Косово, поділився із учасниками з\'їзду досвідом, здобутим у зоні збройного конфлікту. Після виступу о. Михайла вручено грамоту Блаженнішого Любомира, за душпастирську працю у миротворчому контингенті.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_27-01-09.jpg.jpg\" alt=\"\" />Наступного дня капелани розпочали свою роботу з Архиєрейської Літургії, яку очолив Владика Михаїл (Колтун). У проповіді Владика Михаїл зазначив: &laquo;Пам\'ять, яку ми сьогодні віддаємо загиблим в бою під Крутами мусить нас формувати, героїзм трьохсот студентів повинен давати нам впевненість у тому, що обороняти нашу країну ми можемо тоді, коли ми будемо разом, коли ми будемо розвивати і підтримувати духовно - патріотичне виховання\".</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Після Літургії відслужили Панахиду за загиблим в бою під Крутами юнаками.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На завершення з\'їзду учасники обговорили підготовку Всеукраїнського військового паломництва до Зарваниці, яке планується провести восени цього року та сформували плани діяльності на поточний рік. Крім проведення паломництва до Зарваниці, цього року планується провести кілька тренінгів для військових капеланів, розробити та видати молитовники для військових та требники для капеланів, а також сприяти створенню Центрів душпастирювання військових при семінаріях, на прикладі Львівської духовної семінарії Святого Духа, які стануть осередками підготовки капеланів та допомоги їм у їхньому щоденному служінні.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Для департаменту інформації розповів о. Любомир Яворський</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>',5,1,2056);
INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1083,'2009-01-29 22:21:43',1,'P','Святіший Отець заохочує католицьких єпископів Росії до посилення діалогу, щоб християни спільно виходили назустріч культурним та моральним викликам суспільства','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Папа з признанням згадав їхні зусилля та досягнення в літургійному та сакраментальному житті, катехизації, вихованні майбутніх священиків та формуванні зрілих і відповідальних мирян.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У четвер, 29 січня 2009 року, Святіший Отець Венедикт&nbsp;XVI прийняв на окремій аудієнції владик Російської Єпископської Конференції, які перебувають у Римі з візитом &laquo;Ad limina Apostolorum&raquo;. Пригадаємо, що Конференція єпископів римо-католицької Церква в Росії існує від 1999 року і складається з чотирьох владик: архиєпископа Паоло Пецці, ординарія архидієцезії Матері Божої в Москві, єпископа Йосифа Верта, що очолює Преображенську дієцезію в Новосибірську і є також ординарієм для вірних візантійського обряду в Росії, єпископа Кирила Климовича з дієцезії святого Йосифа в Іркутську, що є також Апостольським Адміністратором Південно-Сахалінської Апостольської Префектури та єпископа Клеменса Піккеля, що очолює Саратовську дієцезію.<br /><br />Папа з признанням згадав їхні зусилля та досягнення в літургійному та сакраментальному житті, катехизації, вихованні майбутніх священиків та формуванні зрілих і відповідальних мирян. Одночасно Венедикт&nbsp;XVI підкреслив, що Росія, як і інші країни світу, переживає кризу сім\'ї та демографічний спад, а також інші проблеми, властиві сучасному суспільству.<br />Папа закликав єпископів Росії не занепадати духом перед обличчям труднощів, а плекати справжній дух віри у переконанні, що благодаттю і світлом Святого Духа Ісус Христос невпинно підтримуватиме їхні діла. <br /><br />Святіший Отець також окремо наголосив на потребі посилення діалогу з православною Церквою, який, не зважаючи на зроблений поступ, зазнає також і деяких труднощів. Венедикт&nbsp;XVI окремо згадав вибір нового Московського Патріарха Кирила, якого ще раз привітав. &laquo;Молюсь до Господа, - сказав Вселенський Архиєрей - щоб утвердив усіх у прагненні спільно прямувати шляхом примирення та братерської любові&raquo;. Папа також підкреслив важливість діалогу, щоб християни спільно виходили назустріч культурним та моральним викликам суспільства, які стосуються гідності людської особи та її невід\'ємних прав, захистові життя на всіх його етапах, сім\'ї та багатьох інших невідкладних економічних та суспільних питань. <br /><br />Венедикт&nbsp;XVI висловив подяку римо-католицьким єпископам Росії, запевнив у щоденній молитві та передав через них подяку духовенству, богопосвяченим особам та мирянам, уділяючи особливе Апостольське благословення всій католицькій спільноті Російської Федерації.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',5,1,2057);
INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1084,'2009-01-29 22:35:19',1,'P','Віктор Ющенко вшанував пам’ять Героїв Крут','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У День річниці бою під Крутами Президент України Віктор Ющенко вшанував пам\'ять Героїв Крут. Глава держави поклав&nbsp; квіти до пам\'ятного знака Героям Крут у Києві на&nbsp; Аскольдовій могилі.  <br /></span></span></p>','<p><img style=\"float: right;\" src=\"http://www.president.gov.ua/done_img/b/12747.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У День річниці бою під Крутами Президент України Віктор Ющенко вшанував пам\'ять Героїв Крут. Глава держави поклав&nbsp; квіти до пам\'ятного знака Героям Крут у Києві на&nbsp; Аскольдовій могилі.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У церемонії покладання вінків&nbsp; до пам\'ятного знака взяли участь&nbsp; депутати українського парламенту, представники&nbsp;центральних органів виконавчої влади, громадські діячі.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У рамках заходу також відбулася панахида&nbsp;по загиблим під Крутами.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.president.gov.ua/</span></span></p>',5,1,2058),
(1085,'2009-01-29 22:37:37',1,'P','Глава УГКЦ молився за загиблих у битві під Крутами  ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У рамках заходу також відбулася Панахида за загиблими під Крутами. Богослужіння очолив Блаженніший Любомир, Глава УГКЦ. З ним співслужили Владика Димитрій (Рудюк), Митрополит Переяслав-Хмельницький УПЦ КП, та Владика Богдан (Дзюрах), Єпископ-помічник Київської архиєпархії УГКЦ.</span></span></p>','<p style=\"text-align: justify;\"><img style=\"float: right;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_kruty_29.jpg.jpg\" alt=\"\" /><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">29 січня - річниця бою під Крутами. У цей день Президент України Віктор Ющенко, депутати українського парламенту, представники центральних органів виконавчої влади, громадські та церковні діячі вшанували пам\'ять загиблих героїв. Глава держави поклав квіти до пам\'ятного знака Героям Крут у Києві на Аскольдовій могилі, - повідомляє <a href=\"http://www.president.gov.ua/\" target=\"_blank\">офіційне інтернет-представництво Президента України</a>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У рамках заходу також відбулася Панахида за загиблими під Крутами. Богослужіння очолив Блаженніший Любомир, Глава УГКЦ. З ним співслужили Владика Димитрій (Рудюк), Митрополит Переяслав-Хмельницький УПЦ КП, та Владика Богдан (Дзюрах), Єпископ-помічник Київської архиєпархії УГКЦ.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">29 січня 1918 року відбувся нерівний бій між захисниками Української Народної Республіки та більшовиками під проводом Муравйова. Оскільки на курінь з 300 осіб наступило спочатку 3 тисячі більшовиків, а потім стільки ж підкріплення, вони розгромили захисників Центральної Ради. А тих, хто вижив - закатували. Навесні того ж року Михайло Грушевський розпорядився перевезти з Крут до Києва на Аскольдову могилу тіла 27 осіб, яких ідентифікували.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вшанування на державному рівні пам\'яті героїв Крут відбувається з 2004 року. Уперше це зробив Президент Кучма в останній рік своєї каденції.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Департамент інформації УГКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Фото з сайту <a href=\"http://www.president.gov.ua/\" target=\"_blank\">www.president.gov.ua</a></em></span></span></p>\r\n<p style=\"text-align: justify;\"><em><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span><br /></em></p>\r\n<p>&nbsp;</p>',5,1,2059);
INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1086,'2009-01-29 22:34:46',1,'P','«Обмежень, щоб стати учасником нашого фонду «Покрова» немає жодних»','<strong>Інтерв\'ю Володимира Ульянова про особливості недержавного пенсійного забезпечення та про діяльність пенсійного фонду «Покрова»</strong>\n<p><em>Пропонуємо вам прочитати інтерв\'ю з паном Володимиром Ульяновим, головою Наглядової ради всеукраїнського адміністратора пенсійних фондів і компанії з управління активами «Всесвіт», консультантом проекту Світового банку запровадження пенсійної реформи в Україні, про недержавне пенсійне забезпечення загалом та про недержавний пенсійний фонд «Покрова» зокрема. Всеукраїнський адміністратор пенсійних фондів є одною із трьох компаній, яка обслуговує недержавний пенсійний фонд «Покрова».</em></p>\n<p><strong><img style=\"float: right;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_ulyanov.jpg.jpg\" />У чому особливість недержавного пенсійного забезпечення сьогодні?</strong></p>\n<p>Недержавне пенсійне забезпечення - це життєва необхідність для кожної країни. Інакше ще його можна назвати - добровільне накопичувальне, персоніфіковане, тобто, індивідуальне збирання, тобто, формування своєї майбутньої пенсії. В цьому відношенні це є необхідним елементом для кожної країни. Жодна країна не може забезпечити пенсію тільки за рахунок держави і найбільш розвинуті країни вже давно це розпочали. Україна перейняла це відносно недавно, але врахували увесь світовий досвід, і особливість нашого закону в тому, що він включив у себе усі запобіжники, щодо забезпечення надійного зберігання, які наробив світ за цей час, а також той досвід впровадження недержавного пенсійного забезпечення, які мають наші найближчі сусіди, чи зі сходу чи з заходу, з півдня чи з півночі.</p>\n<p>\n','',NULL,1,2060),
(1087,'2009-01-29 23:39:41',1,'P','Світла і вічна пам‘ять студентам-героям!','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"http://www.oranta.org/images/stories/2008/10/pr1.jpg\" alt=\"\" width=\"197\" height=\"131\" /> Сьогодні, 29 січня, біля нашої святині, пам\'ятного Хреста, схилили свої голови українці: від президента держави до пересічного громадянина. Усі вклонились відважним українцям, героям, вірним синам, за їхню любов до своєї Батьківщини. Вони знали, що ідуть на смерть і йшли заради свободи своїх рідних, своєї Вітчизни.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">10:00 президент України Віктор Ющенко поклав вінок до пам\'ятного Хреста Героям Крут. Поминальну молитву відслужив Блаженніший Любомир Гузар разом з владикою Богданом УГКЦ та митрополитом Дмитрієм УПЦКП у супроводі хору Київської духовної семінарії. Панахида закінчилась відпустом. Блаженніший Любомир: - &laquo;Зі святими упокой Господи душі рабів Божих, загиблих студентів під Крутами, що боролись за віру Христову і за волю Батьківщини України. До лику святих і праведних їх причисли і спаси. І помилуй їхні душі.&raquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>','',NULL,1,2061);
INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1088,'2009-01-30 11:34:20',1,'P','Молоді таланти Аскольдової Могили','<p><img src=\"http://www.oranta.org/images/stories/2008/11/shk2.jpg\" style=\"float: right;\" /> Дитячий колектив при парафії св. Миколая на Аскольдовій могилі перейшов під керівництво Тетяни Стефанишин восени 2005 року. Першою спільною роботою була вистава до свята Миколая у 2005 році. Далі творчий репертуар колективу поступово поповнювався музично-поетичними композиціями, присвяченими різним церковним і національним святам.</p>\n<p>У 2006 році спеціально до свята Миколая на прохання керівника була написана вистава «Святий Миколай» , автором якої є О. Дмитрик. Також вперше на цій виставі звучали пісні про Миколая під фонограми, створені на синтезаторі «Yamaha», який парафія подарувала колективу.</p>\n<p>\n','',NULL,1,2062),
(1089,'2009-01-30 17:34:34',0,'P','Президент України підписав Закон «Про внесення змін до деяких законодавчих актів України щодо права релігійних організацій на постійне користування земельною ділянкою»','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На необхідності законодавчого розв\'язання цього питання наголошувалося в усіх зверненнях Всеукраїнської Ради Церков і релігійних організацій, що впродовж 2005-2008 рр. надсилалися ВРЦіРО на адресу Президента України, Прем\'єр-міністра України, Голови Верховної Ради України та голів депутатських фракцій, зокрема у зверненні ВРЦіРО до В.М.Литвина від 16.12.2008 р.</span></span></p>','<p style=\"text-align: justify;\"><img style=\"border: 0pt none; margin: 4px 3px; float: left;\" src=\"http://www.risu.org.ua/uimg/?f=11065.jpg&amp;s=-1&amp;b=yes&amp;bc=000000\" alt=\"Президент України підписав Закон &laquo;Про внесення змін до деяких законодавчих актів України щодо права релігійних організацій на постійне користування земельною ділянкою&raquo;\" width=\"178\" height=\"180\" /><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Текст закону 30 січня 2009 року опублікований на офіційному сайті Глави держави. Прийнятим парламентом 15 січня 2009 р. <a href=\"http://www.risu.org.ua/ukr/news/article;26819/\" target=\"_blank\">законом</a>, зокрема, передбачається, що релігійні організації, зареєстровані в Україні, отримують право на постійне користування земельною ділянкою за умови, що вона не використовуватиметься для здійснення підприємницької діяльності. Закон набирає чинності від дня його публікації.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Як вже РІСУ повідомляла, за цей закон проголосувало 327 народних депутатів України. Необхідність надання релігійним організаціям права постійного користування земельними ділянками підтримується усіма провідними Церквами України, зокрема Українською Православною Церквою, Українською Православною Церквою Київського Патріархату, Українською Греко-Католицькою Церквою та протестантськими Церквами. <br /><br /> На необхідності законодавчого розв\'язання цього питання наголошувалося в усіх зверненнях Всеукраїнської Ради Церков і релігійних організацій, що впродовж 2005-2008 рр. надсилалися ВРЦіРО на адресу Президента України, Прем\'єр-міністра України, Голови Верховної Ради України та голів депутатських фракцій, зокрема у зверненні ВРЦіРО до В.М.Литвина від 16.12.2008 р. <br /><br /> Ухвалений законопроект був восьмим, що впродовж 2003-2007 рр. вносилися до Верховної Ради України з метою розв\'язання цього питання. <br /><br /> Законом передбачено надання релігійним організаціям саме права постійного користування земельними ділянками, а не права власності на них. Власниками ж відповідних земельних ділянок залишаються держава та відповідні територіальні громади.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">За матеріалами: http://www.risu.org.ua/</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><br /></span></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">ЗАКОН УКРАЇНИ № 875-VІ</span></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Про внесення змін до деяких законодавчих актів України щодо права релігійних організацій на постійне користування земельною ділянкою</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Верховна Рада України <strong>постановляє:</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">І. Внести зміни до таких законодавчих актів України:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">1. У Земельному кодексі України (Відомості Верховної Ради України, 2002 р., № 3-4, ст. 27):</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">1) частину другу статті 92 доповнити пунктом &laquo;в&raquo; такого змісту:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&laquo;в) релігійні організації України, статути (положення) яких зареєстровано у встановленому законом порядку, виключно для будівництва і обслуговування культових та інших будівель, необхідних для забезпечення їх діяльності&raquo;;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">2) пункт &laquo;в&raquo; частини першої статті 141 викласти в такій редакції:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&laquo;в) припинення діяльності релігійних організацій, державних чи комунальних підприємств, установ та організацій&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">2. Частину сьому статті 17 Закону України &laquo;Про свободу совісті та релігійні організації&raquo; (Відомості Верховної Ради УРСР, 1991 р., № 25, ст. 283; Відомості Верховної Ради України, 1993 р., № 26, ст. 277) доповнити реченням такого змісту: &laquo;Земельні ділянки, що надаються релігійним організаціям у постійне користування для будівництва і обслуговування культових та інших будівель, необхідних для забезпечення їх діяльності, забороняється використовувати для здійснення підприємницької діяльності&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">II. Цей Закон набирає чинності з дня його опублікування.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Президент України Віктор ЮЩЕНКО</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">15 січня 2009 року</span></span></p>',5,1,2063);
INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1090,'2009-02-01 10:30:27',1,'P','\"Крім наявних коштів, не менш важливим для будівництва храму є молитва\", – відбулося засідання Комітету по розбудові Патріаршого центру','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За повідомленням Владики Богдана (Дзюраха), голови Спостережної ради будівництва Патріаршого центру, у 2009 році заплановано виконати робіт на загальну суму близько 13 млн грн. Зокрема буде виконано необхідні заходи для налагодження водопроводу і каналізації, електроосвітлення, вентиляції та кондиціювання, опалення та теплопостачання. Крім того у цьому році заплановано повністю завершити фасадні роботи</span></span>.</p>','<p style=\"text-align: justify;\"><img style=\"float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_kupoly_2008_02.jpg.jpg\" alt=\"\" width=\"227\" height=\"170\" /><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">29 січня 2009 року у Києві відбулося чергове засідання Комітету по розбудові Патріаршого центру Української Греко-Католицької Церкви. Його роботу очолив Блаженніший Любомир, Глава УГКЦ. У засіданні взяли участь представники чотирьох відділів, які творять структуру цього комітету: відділу інформаційного забезпечення, відділу зі збору коштів, мистецького відділу та ревізійної комісії. Головна мета зустрічі - аналіз стану будівництва Патріаршого центру та ознайомлення із планами будівництва на 2009 рік.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За повідомленням Владики Богдана (Дзюраха), голови Спостережної ради будівництва Патріаршого центру, у 2009 році заплановано виконати робіт на загальну суму близько 13 млн грн. Зокрема буде виконано необхідні заходи для налагодження водопроводу і каналізації, електроосвітлення, вентиляції та кондиціювання, опалення та теплопостачання. Крім того у цьому році заплановано повністю завершити фасадні роботи.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Важливим для виконання запланованих робіт є наявність необхідних коштів. Тому о. Василь Вільчик, голова відділу зі збору коштів, поінформував присутніх, що від 1 листопада 2008 року у всіх єпархіях і екзархіях в Україні поновлено розповсюдження спеціальних \"цеголок\", придбавши які жертводавець вносить свою лепту у побудову Патріаршого собору. Крім України \"цеголки\" можна придбати в Італії і США. Також найближчим часом \"цеголки\" будуть розповсюджуватися у Польщі, Німеччині та Канаді.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У 2009 році для збору коштів заплановано провести чотири благодійні концерти: у Львові, Івано-Франківську, Тернополі і Києві. Іншим благодійним проектом є проведення аукціонів. Окремо о. Василь розповів про збір коштів через розповсюдження компакт-дисків першого аудіопроекту Блаженнішого Любомира. Комплект із трьох дисків можна буде придбати у парафіях УГКЦ, а також ведуться переговори про їх розповсюдження у мережах супермаркетів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За словами Предстоятеля УГКЦ, \"крім наявних коштів, не менш важливим для будівництва храму є молитва\". При цьому він розповів, що упродовж двох років, без перерви, щотижня служить Літургію в наміренні жертводавців собору та членів їхніх родин. Тут учасники засідання поставили риторичне запитання, а чи моляться за успішне зведення Патріаршого собору вірні УГКЦ, чи доцінюють вони важливість молитви у цьому намірі? Як відповідь, було вирішено у цьому році провести молитовну акцію, до якої запросити усіх членів УГКЦ.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Департамент інформації УГКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Детальніше про будівництво Патріаршого центру можете дізнатися на офіційній веб-сторінці Патріаршого собору: <a href=\"http://www.kyivsobor.ugcc.org.ua/\" target=\"_blank\">www.kyivsobor.ugcc.org.ua</a></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>',5,1,2064);
INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1091,'2009-02-02 10:15:54',1,'P','Розпочалися святкування з нагоди 150-ї річниці Салезіянського Згромадження','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У майже 130 країнах світу служать понад 16 тисяч салезіян. В Україні діє Салезіянська Делегатура&nbsp;(Віцепровінція) \"Марії Помічниці християн\" візантійсько-українського обряду, осередки якої - у Львові, Винниках та Ужгороді. В Одесі діє молодіжний центр &laquo;Дон Боско&raquo;, який провадять салезіяни латинського обряду.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">31 січня 2009&nbsp;р., в день літургічного спомину святого Івана Боско, засновника Товариства святого Франціска Салезія, члени якого відомі як салезіяни, розпочалися святкові заходи, приурочені 150-й річниці заснування цього Згромадження. У Згромадженнні 2009 рік проголошений &laquo;роком благодаті&raquo;, під час якого його члени намагатимуться ще раз переосмислити завдання та служіння своєї спільноти. Урочисте відкриття &laquo;року благодаті&raquo; звершив о.&nbsp;Паскуаль Чавес, Генеральний Настоятель, під час Служби Божої в італійському місті Турині, де було засноване Згромадження.<br /><br />У майже 130 країнах світу служать понад 16 тисяч салезіян. В Україні діє Салезіянська Делегатура&nbsp;(Віцепровінція) \"Марії Помічниці християн\" візантійсько-українського обряду, осередки якої - у Львові, Винниках та Ужгороді. В Одесі діє молодіжний центр &laquo;Дон Боско&raquo;, який провадять салезіяни латинського обряду.<br /><br />З цього Згромадження на даний час є 118 єпископів, а з них - 5 кардиналів, серед яких і теперішній Державний Секретар Апостольської Столиці кардинал Тарчізіо Бертоне.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',5,1,2065),
(1092,'2009-02-02 10:18:20',1,'P','Молитва за мир у Святій Землі','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ця ініціатива була організована як відповідь на заклик Папи Венедикта&nbsp;XVI, який заохочує до постійної молитви за мир у Святій Землі та на всьому Близькому Сході. Християни на всіх континентах єдналися у спільну молитву під час Святих Літургій, Адорацій та інших богослужінь. У заході взяли участь кількасот парафій та чернечих спільнот в усьому світі.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Під гаслом &laquo;Прагнемо миру у Святій Землі&raquo;, вчора, 31 січня&nbsp;2009&nbsp;р., пройшов Міжнародний день покаянної молитви за мир, організований з ініціативи різних католицьких організацій, в тому числі і молодіжних. Ця ініціатива була організована як відповідь на заклик Папи Венедикта&nbsp;XVI, який заохочує до постійної молитви за мир у Святій Землі та на всьому Близькому Сході. Християни на всіх континентах єдналися у спільну молитву під час Святих Літургій, Адорацій та інших богослужінь. У заході взяли участь кількасот парафій та чернечих спільнот в усьому світі. До міжнародної молитовної акції долучилися християни Святої Землі, зокрема, громади у Єрусалимі, Вифлеємі, Газі. У Ватикані Євхаристійне богослуження в рамках молитви за мир очолив кардинал Рафаелло Фаріна, який очолює Ватиканську бібліотеку та архіви.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',5,1,2066);
INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1093,'2009-02-02 10:20:00',1,'P','Молитва \"Ангел Господній\" з Папою Венедиктом XVI','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Ісус страждав і помер на хресті задля любові. Таким чином, якщо добре приглянутися, Він дав сенс нашому терпінню, сенс, який багато чоловіків та жінок у кожній епосі зрозуміли та прийняли, досвідчуючи глибокий спокій також і в гіркоті важких фізичних та моральних випробувань&raquo;,..</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1 лютого 2009 р., перед проказуванням разом з місцевими вірними та паломниками молитви &laquo;Ангел Господній&raquo;, Папа Венедикт&nbsp;XVI виголосив повчання, у якому коментував уривок з Євангелії від святого Марка, що розповідає про зцілення біснуватого у капернаумській синагозі. Коли той побачив Ісуса, почав кричати: \"Що нам, та й Тобі, Ісусе Назарянине? Прийшов єси нас погубити! Знаю бо, хто Ти: Святий Божий!\" Та Ісус погрозив йому, сказавши: \"Мовчи, вийди з нього!\" І відразу нечистий дух потряс чоловіком, скрикнув голосом і вийшов геть із нього. <br /><br />Венедикт&nbsp;XVI звернув увагу на те, що Ісус не тільки виганяє злого духа з людини, звільняючи її від рабства, але й забороняє демонам виявляти, ким Він є. Господь наполягає на цій таємниці, оскільки від неї залежить успіх Його місії та наше спасіння. &laquo;Ісус знав, що для того, аби звільнити людину від панування гріха, Він повинен бути принесений у жертву на хресті, як правдиве пасхальне Ягня&raquo;, - зазначив Папа, вказуючи на те, що диявол, в свою чергу, намагався нав\'язати ідею про Месію, як могутню і сповнену успіхів людину. Однак, Христовий хрест буде руїною сатани, тому Спаситель навчає Своїх учнів, що для того, аби увійти у Свою славу, муситиме багато страждати, буде відкинений, засуджений і розіп\'ятий.<br /><br />&laquo;Ісус страждав і помер на хресті задля любові. Таким чином, якщо добре приглянутися, Він дав сенс нашому терпінню, сенс, який багато чоловіків та жінок у кожній епосі зрозуміли та прийняли, досвідчуючи глибокий спокій також і в гіркоті важких фізичних та моральних випробувань&raquo;, - вів далі Святіший Отець, згадавши про День життя, який проводився в Італії з ініціативи місцевої Єпископської Конференції під гаслом &laquo;Сила життя у терпінні&raquo;. Папа підтримав відважний заклик до звіщення правди про те, що евтаназія є фальшивою розв\'язкою драми страждань, є розв\'язкою, не гідною людини. Справжньою відповіддю на страждання повинна бути не так звана &laquo;солодка смерть&raquo;, але любов, яка допомагає переносити біль. &laquo;Будьмо певні: жодна сльоза, ні того, хто страждає, ні того, хто перебуває біля нього, не пропаде перед Богом&raquo;, - наголосив Святіший Отець.<br /><br />На завершення Венедикт&nbsp;XVI вказав на приклад Пречистої Богоматері, Яка у своєму серці зберігала таємницю Свого Сина та розділила з Ним час терпіння, стоячи під хрестом. &laquo;Ввіряймо Їй осіб, які в терпінні і тих, які щодня докладають зусиль, щоб їх підтримати, служачи життю на кожному його етапі&raquo;, - сказав Папа. А після проказування молитви &laquo;Ангел Господній&raquo;, уділивши Апостольське благословення, пригадав, що у понеділок, 2 лютого, коли за новим календарним стилем припадатиме празник Стрітення Господнього, відзначатиметься День богопосвяченого життя, і заохотив всіх молитися і дякувати Богові за прецінний дар покликань до чернечого життя.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',5,1,2067);
INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1094,'2009-02-02 17:18:28',1,'P','Владика Василь (Івасюк) закликав українське суспільство об\'єднатися довкола проблеми звільнення моряків','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Хотілося б побажати тим людям духу молитви, духу вірності Господу у своєму серці. Дай Боже, щоб вони повернулися додому якнайскоріше живими і здоровими, а ми, на рівні державному і церковному станемо всім світом, всі разом, щоб вирішувати подібні ситуації із кожним разом все ефективніше. Не можна давати наших співвітчизників в образу&raquo;, - наголосив Екзарх.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Владика Василь (Івасюк), Екзарх Одесько-Кримський Української Греко-Католицької Церкви, засудив бездіяльність української влади по звільненню 17 моряків судна &laquo;Фаїна&raquo;, що потрапили у полон до піратів Сомалі 25 вересня, а також закликав співвітчизників об\'єднатися довкола питання звільнення в\'язнів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;На жаль, ми не завжди бачимо, що Одеса, та українське суспільство в цілому, дійсно переживають за своїх співвітчизників, що знаходяться, в піратському полоні, - сказав Владика Василь. - Ось погляньте, якщо американець, або англієць, десь потрапляє в полон, вся техніка, вся потужність цих країн спрямована на його визволення. У нас же, 17 чоловік п\'ятий місяць знаходяться в полоні, а життя міста і країни тим часом йде своєю чергою, неначе нічого надзвичайного не сталося&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;У нас є питання до наших державних мужів, до Президента України, до прем\'єра і Кабінету Міністрів, до Верховної Ради, до місцевих властей. Коли ми навчимося поважати своїх співвітчизників? Коли ми навчимося за них заступатися на міжнародній арені?&raquo;, - запитує Владика Василь.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Як відзначив єпископ УГКЦ, одеська влада, як міська, так і обласна, разом з громадськими і правозахисними організаціями повинні об\'єднатися і &laquo;виступити всім світом, заступатися за наших людей моря&raquo;. &laquo;Церква і держава повинні захистити гідність людини, допомогти людям, що потрапили у важке становище&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Хотілося б побажати тим людям духу молитви, духу вірності Господу у своєму серці. Дай Боже, щоб вони повернулися додому якнайскоріше живими і здоровими, а ми, на рівні державному і церковному станемо всім світом, всі разом, щоб вирішувати подібні ситуації із кожним разом все ефективніше. Не можна давати наших співвітчизників в образу&raquo;, - наголосив Екзарх.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За його словами, християнські церкви різних конфесій, різні громадські організації повинні виступити із офіційним спільним зверненням, що закликає українську владу діяти рішучіше і чіткіше. &laquo;Ситуацію потрібно якнайскоріше вирішити&raquo;, - відзначив Владика Василь.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Зі своєї сторони, ми як Церква у Бога проситимемо, щоб подібні трагедії не повторювалися, - завірив єпископ. - У нас в Одесі є спеціальна морська каплиця на вул. Ланжероновської, в якій ми постійно молимося за наших моряків. У цьому ми бачимо наше посильне служіння українським морякам&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Хотів би підтримати в\'язнів і членів їх сімей, висловити їм нашу моральну і молитовну підтримку, щоб вони не відчували себе самотніми і всіма забутими. Серце болить не лише у вас, але у всіх нас&raquo;, - відзначив Екзарх Одесько-Кримський.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Додаток:</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">25 вересня пірати Сомалі захопили судно &laquo;Фаїна&raquo;, на борту якого знаходиться 17 українських моряків, що супроводжували військовий вантаж.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"http://www.cerkva.od.ua/index.php?option=com_content&amp;task=section&amp;id=5&amp;Itemid=48\" target=\"_blank\">Єпископ Василій (Івасюк)</a>, Екзарх Одесько-Кримський, в 2004 році був призначений представником Української Греко-Католицької Церкви у справах душепастирської опіка моряків у всіх українських портах на побережжі Чорного моря. Центри УГКЦ по підтримці українських моряків існують в портах Одеси, Іллічівська, Херсона і Севастополя. У Одесі для моряків діє <a href=\"http://www.cerkva.od.ua/index.php?option=com_contact&amp;task=view&amp;contact_id=2&amp;Itemid=3\" target=\"_blank\">каплиця Архистратига Михаїла</a> по вул. Ланжероновськая, 2 (біля музею Одеського порту).&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Відділ інформації</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Одесько-Кримського Екзархату</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Української Греко-Католицької Церкви</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>',5,1,2068);
INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1095,'2009-02-02 17:21:39',0,'P','','<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong> Дві головні заповіді лю6ови</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Люби  	Господа Бога твого всім серцем твоїм, всією душею Твоєю, всією силою Твоєю і  	всіма мислями твоїми. Це перша і найбільша заповідь.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">А друга  	подібна до неї: Люби ближнього твого, як самого себе (Мт.  	22,37-39).</span></span></p>','',5,1,2069),
(1096,'2009-02-03 16:47:17',1,'P','Прес-конференція з нагоди відкриття «Консультативно- просвітницького центру для допомоги залежним від алкоголю та членам їхніх родин»','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Було представлено стратегію діяльності центру, методику протидії алкогольній залежності. &bdquo;Такі проекти - це наслідки праці, що велася раніше та реакція зі сторони УГКЦ на виклики, які ставить перед українським суспільством алкогольна залежність\" - зазначив отець Андрій Логін.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">2 лютого, у конференц-залі Тернопільського благодійного фонду &laquo;Карітас&raquo; відбулася прес-конференція з нагоди відкриття &laquo;Консультативно-просвітницького центру для допомоги залежним від алкоголю та членам їхніх родин&raquo;. У конференції взяли участь голова міжєпархіяльної підкомісії &laquo;За тверезість життя&raquo; УГКЦ отець Андрій Логін, представник фонду &bdquo;Карітас\" Наталія Мисула, соціальний працівник Тернопільського наркологічного диспансеру Іван Осадчук, консультант терапевт для праці з особами узалежненими від алкоголю Роман Прочко, представники ЗМІ та реабілітаційних центрів Тернополя.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Було представлено стратегію діяльності центру, методику протидії алкогольній залежності. &bdquo;Такі проекти - це наслідки праці, що велася раніше та реакція зі сторони УГКЦ на виклики, які ставить перед українським суспільством алкогольна залежність\" - зазначив отець Андрій Логін. &bdquo;Реакція держави на проблему алкогольної залежності є пасивною, проводиться масова антиреклама, тому мусимо шукати адекватні шляхи протидії\" - наголошував Юрій Муран - завідувач Тернопільського облнаркодиспансеру.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Окремо було представлено науковий посібник духовної терапії &bdquo;12 кроків\", розпрацьований Романом Прочко. &bdquo;Алкоголізм - це хвороба особи, що потребує лікування, комплексної реабілітації за участю священика чи духовної особи\" - значив у своєму слові автор видання.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Консультативно-посвітницький центр є пілотним проектом міжєпархіальної підкомісії &laquo;За тверезість життя&raquo; УГКЦ і виник у результаті співпраці цієї підкомісії та &laquo;Карітасу&raquo;. Працівники центру надаватимуть допомогу особам, що потрапили в алкогольну залежність: консультації, духовна терапія, а при потребі також алкозалежних спрямовуватиметься в наркологію та реабілітаційні центри. Членів родини та близьких алкозалежної особи, яка заперечує наявність залежності, готуватимуть до конфронтування з фактом узалежнення з метою подальшого спрямування залежної особи на лікування. Також для родичів залежної особи буде запропоновано проходження духовної терапії з метою зцілення духовних та емоційних ран, що виникають в людей як наслідок тривалих сімейних конфліктів через узалежнення одного з членів родини. У діяльності центру передбачено проведення просвітницьких тренінгів та семінарів для молоді і дорослих.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Олег Харишин, прес-секретар</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Тернопільсько-Зборівської єпархії</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span><em><br /></em></p>',5,1,2070);
INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1097,'2009-02-03 16:49:10',1,'P','Слово Вселенського Архиєрея Венедикта XVI з нагоди 13-го Всесвітнього Дня Богопосвяченого Життя','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Святіший Отець також звернув увагу на вміння апостола Павла поєднувати духовне життя та ревну апостольську діяльність. Його життя було одночасно спогляданням та дією, було відкрите на Бога і на ближніх, щоб якнайефективніше служити Євангелію. Венедикт&nbsp;XVI вказав на відважність Апостола Народів перед обличчям важких випробувань та навіть мученицької смерті, поєднану з несхитним довір\'ям до Того, Який був його Господом.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З нагоди Всесвітнього Дня Богопосвяченого Життя, який припадає в день празника Стрітення Господнього, 2 лютого 2009&nbsp;р. Папа Венедикт&nbsp;XVI зустрівся з богопосвяченими особами Риму, які зібралися на Святу Літургію в базиліці Святого Петра. Богослуження очолив кардинал Франц Роде, Префект Конгрегації інституцій богопосвяченого та товариств апостольського життя. Наприкінці Служби Божої до базиліки прийшов Вселенський Архиєрей, який виголосив проповідь, у якій звернув особливу увагу на постать святого апостола Павла як на вчителя богопосвяченого життя.<br /><br />Венедикт&nbsp;XVI представив спосіб життя Апостола Народів, у якому можна легко зауважити сповнення трьох євангельських рад - убозтва, чистоти та послуху. Папа зауважив, що убозтво забезпечило святому Павлові повну безкорисливість у проповідуванні Євангелія та солідарність до потребуючих. Чистота дозволила йому служити з більшою відданістю та свободою, а послух проявився у щоденних трудах, що випливали з турботи за всі Церкви.<br /><br />Святіший Отець також звернув увагу на вміння апостола Павла поєднувати духовне життя та ревну апостольську діяльність. Його життя було одночасно спогляданням та дією, було відкрите на Бога і на ближніх, щоб якнайефективніше служити Євангелію. Венедикт&nbsp;XVI вказав на відважність Апостола Народів перед обличчям важких випробувань та навіть мученицької смерті, поєднану з несхитним довір\'ям до Того, Який був його Господом. <br /><br />&laquo;Нехай святий Павло допоможе вам виконувати ваше апостольське служіння у Церкві та з Церквою, в дусі безумовної єдності&raquo;, - побажав Венедикт&nbsp;XVI богопосвяченим особам під час зустрічі з нагоди Дня Богопосвяченого Життя.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',5,1,2071),
(1098,'2009-02-03 16:51:20',1,'P','Соціальна та екологічна відповідальність – це сила майбутнього','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Мова йде про зміну клімату, що ставить під загрозу всі наші цілі в сфері розвитку та досягнення соціального прогресу&raquo;. Проте, згідно Пан Гі-муна, всесвітня екологічна екологічна криза відкриває перед людством чудову перспективу покращення економічного становища: &laquo;Якщо ми займемося вирішенням проблеми зміни клімату напряму, нам вдасться побороти багато труднощів, з котрими ми сьогодні стикаємося, включаючи загрозу глобальної рецесії&raquo;.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Генеральний секретар ООН Пан Гі-мун виголосив звернення до учасників Світового економічного форуму у Давосі, поставивши при цьому основоположний акцент на екологічне питання. Генсек констатував, що поряд багатьох проблем економічного характеру перед нами постає: &laquo;ще одна проблема, котра формувалася роками і носить глобальний характер. Мова йде про зміну клімату, що ставить під загрозу всі наші цілі в сфері розвитку та досягнення соціального прогресу&raquo;. Проте, згідно Пан Гі-муна, всесвітня екологічна екологічна криза відкриває перед людством чудову перспективу покращення економічного становища: &laquo;Якщо ми займемося вирішенням проблеми зміни клімату напряму, нам вдасться побороти багато труднощів, з котрими ми сьогодні стикаємося, включаючи загрозу глобальної рецесії&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Що насамперед необхідно для кращого екологічного та економічного майбутнього нашої цивілізації? Генеральний секретар ООН переконаний, що будь які глобальні виклики можемо вирішити тільки діючи всі разом. При тому є необхідним &laquo;новий рівень міжнародної співпраці - уряди, громадянське суспільство та приватний сектор повинні працювати разом в інтересах колективного глобального блага&raquo;. Для ефективного подолання різноманітних криз сучасності важливо: &laquo;розірвати сковуючі нас окови короткотермінових&nbsp; підходів і зайнятися пошуками довготривалих рішень&raquo;. Таким рішенням Пан Гі-мун вважає &laquo;зелену&raquo; економіку, яка характеризується низьким об\'ємом викидів вуглецю, безпечними технологіями, ефективним використанням енергії, та створенням нових робочих місць. Така &laquo;зелена&raquo; економіка дозволить &laquo;перетворити сьогоднішню кризу в завтрашнє стале зростання&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Свою промову до учасників Світового економічного форуму у Давосі генсек ООН завершив закликом: &laquo;Соціальна та екологічна відповідальність ... - це сила майбутнього. Я закликаю вас опанувати цю силу&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Інформаційна служба</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Бюро УГКЦ із питань екології</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>',5,1,2072);
