INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1476,'2009-05-13 15:30:54',1,'P','Отець Василь (Вороновський) відсвяткував 80-ліття життя та 50-ліття священства','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У неділю, 10 травня 2009 року, ієромонах Василь (Вороновський) відсвяткував 80-ліття життя та 50-ліття священства. З цієї нагоди Блаженніший Любомир, Глава УГКЦ, нагородив о. Василя грамотою Верховного Архиєпископа.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У свою чергу Президент України Віктор Ющенко, як повідомляє <a href=\"http://www.president.gov.ua/\" target=\"_blank\">www.president.gov.ua</a>, 5 травня видав Указ &laquo;Про відзначення державними нагородами України працівників підприємств, установ та організацій міста Львова&raquo; (№ 290), згідно з яким о. Василя нагородили орденом &laquo;За заслуги&raquo; III ступеня.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У неділю, 10 травня 2009 року, ієромонах Василь (Вороновський) відсвяткував 80-ліття життя та 50-ліття священства. З цієї нагоди Блаженніший Любомир, Глава УГКЦ, нагородив о. Василя грамотою Верховного Архиєпископа. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У свою чергу Президент України Віктор Ющенко, як повідомляє <a href=\"http://www.president.gov.ua/\" target=\"_blank\">www.president.gov.ua</a>, 5 травня видав Указ &laquo;Про відзначення державними нагородами України працівників підприємств, установ та організацій міста Львова&raquo; (№ 290), згідно з яким о. Василя нагородили орденом &laquo;За заслуги&raquo; III ступеня.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Причина нагородження - &laquo;за вагомий особистий внесок у соціально-економічний та культурний розвиток міста Львова, високі досягнення у професійній діяльності та багаторічну сумлінну працю&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Департамент інформації УГКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,2532,200,CRW_0049.jpg\" alt=\"\" />P.S. Отець Василь Вороновський за час свого священства не один раз побував на Аскольдовій Могилі у церкві св. Миколая Чудотворця. Останній раз нашу церкву отець Вороновський відвідував у часі нашого празника 22 травня 2008 року Божого. Отець Вороновський відомий як священик екзерцист. Своїм служінням він врятував не одну загублену душу. </span></span></p>\r\n<p><span style=\"font-size: 12pt;\" lang=\"UK\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&laquo;Оранта&raquo;</span></span></span></p>',5,1,2532),
(1477,'2009-05-13 16:00:48',1,'P','Міжнародний міжконфесійний семінар \"Єдина дата Пасхи - можлива\"  ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У п\'ятницю, 15 травня, Інститут екуменічних студій Українського католицького університету буде проводити Міжнародний міжконфесійний семінар \"Єдина дата Пасхи - можлива: Алеппський консенсус 1997 року\". Семінар присвячений обговоренню документу, прийнятого в Алеппо (Сирія) у 1997 році учасниками із різних християнських Церков, який пропонує вирішення питання запровадження спільного методу обчислення єдиної дати Пасхи Господньої для усіх християн.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У п\'ятницю, 15 травня, Інститут екуменічних студій Українського католицького університету буде проводити Міжнародний міжконфесійний семінар \"Єдина дата Пасхи - можлива: Алеппський консенсус 1997 року\". Семінар присвячений обговоренню документу, прийнятого в Алеппо (Сирія) у 1997 році учасниками із різних християнських Церков, який пропонує вирішення питання запровадження спільного методу обчислення єдиної дати Пасхи Господньої для усіх християн.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В семінарі братимуть участь три головні доповідачі - від імені Всесвітньої Ради Церков говоритиме д-р Дагмар Хеллер, від імені Римо-Католицької Церкви - о. Мілан Зуст та від імені Української Православної Церкви - д-р Костянтин Сігов. Крім того, до обговорення питання долучаться і Глави (чи їх делегати) всіх християнських Церков міста Львова, а також усі охочі.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Семінар відбуватиметься в приміщенні конференційного залу Українського католицького університету в корпусі філософсько-богословського факультету на вул. Хуторівці, 35 (І поверх).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За додатковою інформацією звертайтеся в Інститут екуменічних студій УКУ:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">тел.: (38-032) 240-99-40*191</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">факс: (38-032) 240-99-50</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">ел. пошта: <a href=\"mailto:olharubel@ucu.edu.ua\">olharubel@ucu.edu.ua</a></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"http://www.ecumenicalstudies.org.ua/ukr/activity/one.easter/\" target=\"_blank\">www.ecumenicalstudies.org.ua/ukr/activity/one.easter/</a></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>',5,1,2533);
INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1478,'2009-05-13 16:06:33',1,'P','«Слово Боже у житті та місії УГКЦ»  ','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">19 - 20 травня 2009 року у Львівській духовній семінарії Святого Духа відбудеться Міжнародна науково-практична конференція &laquo;Слово Боже у житті та місії УГКЦ&raquo;. Організаторами конференції є Патріарша катехитична комісія УГКЦ та Катехитично-педагогічний інститут УКУ.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Метою конференції є усвідомлення важливості Святого Писання у місії Церкви в Україні у світлі вчення Синоду Єпископів та вивчення шляхів втілення Божого Слова у життя Церкви.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">19 - 20 травня 2009 року у Львівській духовній семінарії Святого Духа відбудеться Міжнародна науково-практична конференція &laquo;Слово Боже у житті та місії УГКЦ&raquo;. Організаторами конференції є Патріарша катехитична комісія УГКЦ та Катехитично-педагогічний інститут УКУ.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Метою конференції є усвідомлення важливості Святого Писання у місії Церкви в Україні у світлі вчення Синоду Єпископів та вивчення шляхів втілення Божого Слова у життя Церкви.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Тарас Гавришко<br />Медіа-центр Львівської духовної семінарії Святого Духа</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Програма конференції</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;<strong>19 травня 2009 р.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8.30 - Реєстрація учасників</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">9.30 - Відкриття конференції. Слово Блаженнішого Любомира, Глави УГКЦ. Слово Архиєпископа Мєчислава, Митрополита Львівського РКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">10.00 - &laquo;Слово Боже у житті та місії Церкви&raquo; - Архиєпископ Никола Етерович, Секретар Синоду Єпископів РКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">11.30 - Перерва</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">12.00 - &laquo;Святе Письмо і Літургія&raquo; - Владика Гліб (Лончина), Куріяльний єпископ УГКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">12.35 - &laquo;Слово Боже у житті та місії Церкви&raquo; - с. Дженес Солюк, СНДМ, Головна настоятелька СНДМ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">12.50 - &laquo;Слово Боже у житті та місії Церкви&raquo; - Владика Петро Мальчук, учасник Папського Синоду, РКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">13.15 - Шостий час</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">13.45 - Обід</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">15.00 - &laquo;Центральність Христа у Святому Письмі&raquo; - о. Володимир Кусьнєж, РКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">15.45 - &laquo;Слово Боже у східній традиції&raquo; - Владика Петро (Стасюк), Голова Патріаршої катехитичної комісії УГКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">16.45 - Перерва на каву</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">17.00 - Прес-конференція: презентація книги Архєпископа Николи Етеровича &laquo;Дорогами України&raquo; (Владика Гліб); презентація Нового Завіту з Псалмами з коментарем Єрусалимської Біблії (о. Тарас Барщевський, кандидат біблійних наук, заступник директора КПІ УКУ, координатор біблійного апостоляту УГКЦ, викладач УКУ)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18.30 - Фуршет</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>20 травня 2009 р.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">7.30 - Божественна Літургія</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">9.00 - Сніданок</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">9.30 - &laquo;Актуалізація Божого Слова&raquo; - Архиєпископ Никола Етерович</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">10.30 - &laquo;Біблійний апостолят в УГКЦ&raquo; - о. Тарас Барщевський</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">11.30 - Перерва на каву</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">12.00 - Праця в групах</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">13.45 - Шостий час</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">14.00 - Обід</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">15.00 - &laquo;Біблійна катехиза&raquo; - о. мітр. д-р Іван Музичка</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">16.00 - Праця в групах</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18.30 - Пленарне засідання: підсумок праці в групах, пропозиції до резолюцій</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">19.30 - Закриття конференції.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"http://www.ugcc.lviv.ua/index.php?option=com_content&amp;task=view&amp;id=1452&amp;Itemid=97\" target=\"_blank\">www.ugcc.lviv.ua/index.php</a></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span><br /></span></p>\r\n<p><span style=\"font-size: small;\"><br /></span></p>',5,1,2534);
INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1479,'2009-05-13 16:08:41',1,'P','Папа Венедикт XVI у Святій Землі','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Розпочинаючи проповідь, Венедикт&nbsp;XVI висловив велику радість з нагоди служити Євхаристійне богослуження разом з &laquo;Церквою Єрусалиму&raquo;. &laquo;Ми зібралися тут, під Оливною горою, де наш Господь молився і страждав, де плакав з любові до цього міста і від прагнення, щоб воно могло пізнати \"дорогу миру\", тут, звідки Він повернувся до Отця, даючи Своє останнє на землі благословення Своїм учням і нам&raquo;</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Остання подія вівторка - це Свята Літургія, яку Венедикт&nbsp;XVI служить в \"Долині Йосафата\". Це частина Кедронської долини між стіною Храмової Гори і Оливною горою, напроти базиліки Гетсиманського саду. Свята Літургія відправлялась латинською та арабською мовами. <br /><br />Розпочинаючи проповідь, Венедикт&nbsp;XVI висловив велику радість з нагоди служити Євхаристійне богослуження разом з &laquo;Церквою Єрусалиму&raquo;. &laquo;Ми зібралися тут, під Оливною горою, де наш Господь молився і страждав, де плакав з любові до цього міста і від прагнення, щоб воно могло пізнати \"дорогу миру\", тут, звідки Він повернувся до Отця, даючи Своє останнє на землі благословення Своїм учням і нам&raquo;, - сказав Папа, зазначаючи, що як Наступник святого Петра, він прямує його кроками, звіщаючи Воскреслого Христа, щоб утверджувати християн у вірі батьків.<br /><br />Святіший Отець зазначив, що йому відомі труднощі, розчарування та страждання місцевих християн, спричинені конфліктами та переїздами численних сімей. Венедикт&nbsp;XVI підкреслив значення єдності і взаємозв\'язку між християнами Святої Землі і усією Вселенською Церквою, і те, що їхня присутність тут, у цій місцевості, позначеній конфліктами має особливе значення, яке виходить поза межі самої ж Церкви. &laquo;Саме з причин вашого глибокого коріння у цих місцях, вашої давньої та сильної християнської культури, і вашого тривалого довір\'я до Божих обітниць, ви, християни Святої Землі, покликані служити не лише як маяк віри для Вселенської Церкви, але також як закваска гармонії, мудрості і рівноваги в житті суспільства, яке традиційно було, і продовжує бути, плюралістичним, багатонаціональним та багато релігійним&raquo;, - сказав Папа, пригадавши ціну такої надії, якою є страждання і переслідування, ті самі, яких зазнавав святий Павло. <br /><br />У біблійному баченні земний Єрусалим є пов\'язаний з загальним примиренням, миром, якого Бог прагне для всього людського роду. І хоч погляд на сучасну ситуацію підказує, що ще далеко до здійснення цих обітниць, надія продовжує перемагати знеохочення та розчарування, а мир і далі в небезпеці з боку егоїзму, поділів та давніх образ. Папа підкреслив покликання Єрусалиму, міста різних культур і релігій, що є відображенням нашого глобалізованого світу, стати місцем, яке навчає взаємної поваги, діалогу і порозуміння, місцем, де пересуди, невігластво і страх перемагаються чесністю, інтегрованістю та пошуком миру.<br /><br />Наприкінці Венедикт&nbsp;XVI висловив стурбованість з приводу того, що численні християни із справедливих причин покидають ці землі, що тягне за собою культурне і духовне збідніння міста, тому звернувся із закликом до влади шанувати і підтримувати християнську присутність в Святій Землі, а місцевих вірних запевнив у солідарності, любові та підтримці з боку всієї Церкви та Апостольської Столиці.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',5,1,2535);
INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1480,'2009-03-13 16:16:40',1,'P','\"Тут молилася за Україну Слава Стецько\"','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Аскольдова могила над Дніпром у нашім стольнім Києві. Святе, і справді, місце. Тут хрести на землі, у траві й на деревах - поміж образів, що &laquo;злетіли&raquo; в ошатну тінь зелених крон. Могила молодого квіту - юних сердець у червонім снігу, що під Крутами. Алея пам\'яти померлих наших співвітчизників у жахливе лихоліття Голодомору... Храм святого Миколая - як величний, духовний Господній дарунок усім, хто спрямує сюди свої стопи.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Аскольдова могила над Дніпром у нашім стольнім Києві. Святе, і справді, місце. Тут хрести на землі, у траві й на деревах - поміж образів, що &laquo;злетіли&raquo; в ошатну тінь зелених крон. Могила молодого квіту - юних сердець у червонім снігу, що під Крутами. Алея пам\'яти померлих наших співвітчизників у жахливе лихоліття Голодомору... Храм святого Миколая - як величний, духовний Господній дарунок усім, хто спрямує сюди свої стопи.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вона, Ярослава Стецько, сюди йшла. Не знаємо достеменно: була ота гірка над стареньким Славутичем її любимим місцем духовного відпочинку, а чи одкровенням перед небесами, у великому мегаполісі на ймення Київ, але що приходила сюди чи не щонеділі - засвідчений факт. Зокрема, до цієї невеличкої церковці, змальованої свого часу ще нашим геніальним Кобзарем. Тут вона перемовлялась молитвою із Богом, виповідала Спасителеві нашому свої наміри, із ним &laquo;радилась&raquo;, просила помочі й заступництва, певен, не лише для себе, а й для того, кому слугувала вірою і правдою, - свого народу. Тут вона ж виповідала небові свої і болісті, й провини, якщо в таких чулась її душа, - бо шлях її був довгий і тернистий, не спіткнувшись, не пройдеш. Солодке оте слово - Свобода, та часто дорога до нього гірка ...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"http://www.ukrslovo.com.ua/work/archive/2008/36/17_01.jpg\" alt=\"\" /><br /><em>Княгиня Ольга</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ярослава Стецько пройшла ту дорогу до Незалежности неньки України гідно і чесно, і в неї не було &laquo;зерна неправди за собою&raquo;. А в кінці такого сподвижницького шляху сяє почесть народу, безсмертя і вічна слава! Отож, відійшовши у Вічність, наша Слава не забула нас. А ми, народ, пам\'ятаємо її. Вона вернулась у свій край, до своєї святині на Аскольдовій горі, - вже у вигляді барельєфа. Її мармуровий портрет прикрасив нещодавно одну із пілястр церкви Святого Миколая Чудотворця. У святковій, урочистій обстановці, після недільної Літургії, при велелюдді - церковної і мирської громади, у сяйливому затишку променів світила. Всечесний о. Ігор - настоятель цього храму:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Вже так у нашій парафії склалося, що ми завжди відкликалися, вшановували, молилися за тих українських достойників, хто боровся і віддав життя своє за рідну Україну. Чому ми вшанували пам\'ять Слави Стецько? Люди, які сюди приходять, розуміють і знають, що це за людина, що вона зробила для мене, для вас, для кожного із нас. Що вона зробила і що вона пережила. Яка страшна була її боротьба.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Те, що чинили вороги проти неї, як її переслідували, здавалось би, людська сила не може перенести, витримати. А вона, мужня жінка, витерпіла все. Слава Стецько - людина значення всеукраїнського, постать світова. І про неї будуть говорити, описуватимуть ті події, які вона пережила, коли вела боротьбу за визволення України.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Наше бажання, церковної громади, - вшанувати її пам\'ять. Тому що вона, знана людина, депутат Верховної Ради, жінка-українка, яка перенесла тягар переслідування особисто і переслідування нашої Церкви, завжди виступала проти цього зла. Скрізь і всюди, де їй доводилось бувати упродовж свого буремного життя, п. Стецько засвідчувала, що Українську греко-католицьку Церкву в Україні переслідують. Зрозуміло, тодішня сатанинська совіцька влада.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Але ми вшанували її не лише за політичну діяльність, а як, передусім, нашу добру парафіянку. Прекрасну людину Божу, яка рівно десять років тому, у 1998 році, 22 травня, у присутності нашого Блаженнійшого Патріарха кардинала Гузара, а також Владики Василя, котрі освячували тоді цей храм, у присутності тодішнього мера міста О. Омельченка, який відбудував нашу святиню, відчинила двері храму. І разом з нею і з нашим Спасителем у церковцю нашу зайшла благодать Господня і ласка. Слава Стецько також багато долучилася коштами для відбудови церкви і, як народний депутат, усіляко сприяла воскресінню її з мороку забуття.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вшановуємо її як добру християнку, як гарний приклад для наших парафіян, родин. Вона кожної неділі, як була в Києві, приходила до нас. Вона сповідалась часто, причащалась, цікавилася життям парафії, радилась із нашими священиками. Питалась, кому і як помагаємо, що можна іще зробити доброго. Пам\'ятаю, як сьогодні: де вона сиділа на лавці, діти обступлять її, як горобчики, - у нас є недільна Служба Божа для дітей, і вона приходила тоді. Слава про щось із дітьми перемовлялась, усміхалась до них, пригощала цукерками. Спілкувалась і з молоддю, бувала на різних духовних заходах, концертах. Звідси п. Слава й вирушила в ту останню дорогу до Бога. Ми віримо, що там, на небі, ця свята жінка молиться за усіх нас, за Україну, яку любила усім серцем і якій віддала себе до останку.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вона свята і праведна. І тому наші парафіяни спромоглися коштами, працею своїх рук і розумінням на цю мармурову табличку, щоб усі знали, що тут молилась за Україну вічної пам\'яти наша Слава Стецько. - А ще на її пам\'ять, - казав о. Ігор, - ми посадили дуб на Алеї героїв. Нехай росте і тягнеться до неба, як до нього тягнулася своїми думками і світлими вчинками ця велична українка. Нехай люди приходять сюди, запалюють свічечку за упокій її душі, моляться за Славу Стецько, за її спасіння, просять у неї заступництва перед Господом для нашої України.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На білій сонячній пілястрі - білий барельєфний портрет Ярослави Стецько. Напис: &laquo;Тут молилася за Україну і звідси пішла до Господа Слава Стецько&raquo;. Ця іменна табличка - витвір рук скульптора п. Бориса Довганя. Цей чоловік добре знався зі своєю героїнею, спілкувався з нею і в Мюнхені, де жила п. Стецько, і вже в Києві - за незалежної України. На відкритті меморіальної дошки п. Борис з теплотою згадував цю милу жінку, їхні зносини людські. Чому - біла дошка на білому мармурі, аж ніяк наче не контрастує, не виділяється на світлому тлі? Скульптор пояснив: п. Слава прожила гідне і світле життя, зичу нам усім такого, українцям. Але це була навдивовижу скромна жінка, що властиво лише великим людям, обдарованим Божим натхненням. Тому хай і по своєму відході у засвіти вона залишається у нашій пам\'яті такою ж. І, як на мене, художника, світлий портрет Слави Стецько на світлому, білому тлі якнайкраще передаватиме людям - глядачам - характер цієї людини, її скромність у всьому, величну простоту, доброту і щирість.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На відкритті барельєфа Великої Українки виступила також пані Леся Брацюнь. Вона є племінницею Ярослави Стецько. Пані Леся висловила подяку всім тим, хто долучився до цієї знаменної події в житті церковної громади храму Святого Миколая на Аскольдовій могилі. А це - настоятель святині о. Ігор Онишкевич, скульптор п. Борис Довгань, народний депутат України п\'ятого скликання, голова Київської обласної організації Конгресу українських націоналістів п. Андрій Лопушанський - він надав матеріальну допомогу на виготовлення меморіальної дошки. Саме останнім - п. Борису Довганю та п. Андрію Лопушанському - й випала честь стягнути біле покривало з барельєфа.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">І тоді ми прочитали: &laquo;Тут молилася за Україну...&raquo; У місті Києві. На Аскольдовій могилі. У храмі Святого Миколая. Із вічности до нас вернулася Слава - незабутня наша Ярослава Стецько, краща дочка народу українського.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Омелян ХАЛУС</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами:&nbsp; http://www.ukrslovo.com.ua/work/archive/2008/36/17.html</span></span></p>',5,1,2536);
INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1481,'2009-05-14 00:13:06',1,'P','Відпочинок у ботанічному саду','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,2537,200,11.jpg\" alt=\"\" />Парафіяльна служба милосердя церкви св. Миколая Чудотворця на Аскольдовій Могилі на другий день після свята матері організувала для своїх підопічних бездоглядних дітей прогулянку у ботанічний сад. Діти милувалися розквітом природи, вдихали аромати розкішного бузку, розважалися і слухали захопливі розповіді отця Анатолія про його дитинство. Пані Марія приготувала для усіх смачний обід, пані Ореста прогулювалась разом з дітьми квітучими алейками ботанічного саду. А діти.., діти діставалися у найвіддаленіші куточки саду, а повертаючись жваво розповідали про побачене. Для дітей відпочинок видався активним і захоплюючим, цікавим і вражаючим, дехто з них вперше побував у такому квітучому мальовничому містечку.</span></span></p>','<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Фото: Любові Михайлюк</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,2537,200,3.jpg\" alt=\"\" /><img style=\"float: left;\" src=\"image,2537,200,4.jpg\" alt=\"\" /></span></span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,2537,200,2.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,2537,200,1.jpg\" alt=\"\" /></span></span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,2537,200,7.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,2537,200,6.jpg\" alt=\"\" /></span></span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,2537,200,9.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,2537,200,8.jpg\" alt=\"\" /></span></span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,2537,200,5.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,2537,200,10.jpg\" alt=\"\" /><br /></span></span></p>',5,1,2537),
(1482,'2009-05-15 00:21:52',1,'P','Темі виходу УГКЦ з підпілля присвятили круглий стіл у рамках міжнародної релігієзнавчої конференції','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В рамках міжнародної релігієзнавчої конференції, яка проходить цими днями у Львові, 13 травня відбувся круглий стіл &laquo;Катакомбна Церква (Виходу УГКЦ з підпілля присвячується)&raquo;, в якому взяли участь священики, активісти греко-католицького підпілля та науковці. Особливості підпільного становища УГКЦ в хрущовський період висвітлив у своїй доповіді доктор історичних наук Степан Стоцький.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В рамках міжнародної релігієзнавчої конференції, яка проходить цими днями у Львові, 13 травня відбувся круглий стіл &laquo;Катакомбна Церква (Виходу УГКЦ з підпілля присвячується)&raquo;, в якому взяли участь священики, активісти греко-католицького підпілля та науковці. Особливості підпільного становища УГКЦ в хрущовський період висвітлив у своїй доповіді доктор історичних наук Степан Стоцький. <br /><br /> Молодий науковець з Івано-Франківська Олег Єгрешій поділився із присутніми своїми науковими пошуками, в яких він базується на особистих записах, нотатках та листах греко-католицьких священиків та їх родин, які були депортовані на Далекий Схід. У своїй доповіді він навів приклади живих свідчень про побут, умови депортації та душевні пережиття ув\'язнених. <br /><br /> Колишній голова Комітету із захисту Української Греко-Католицької Церкви - Іван Гель розповів присутнім про основні моменти виходу із підпілля Церкви, в яких він сам приймав активну участь. Його свідчення викликали найбільше зацікавлення серед учасників круглого столу, через свою яскравість та живість. <br /><br /> Науковці із Інституту Історії Церкви Українського Католицького Університету розкрили низку аспектів виходу із підпілля УГКЦ. Директор Інституту о. Андрій Михалейко зупинився в своїй доповіді на легалізації УГКЦ в контексті взаємин між Католицькою та Православною Церквами. Священик висвітлив коротку історію екуменічного діалогу, який розпочався в ХХ столітті, а також розповів про проблеми, пов\'язані із виходом із підпілля греко-католиків у Східній Європі. При цьому він наголосив, що в подальшому процесі екуменічного діалогу необхідним є участь Східних Католицьких Церков. <br /><br />Викладач кафедри церковної історії Католицького Університету Світлана Гуркіна в своїй доповіді висвітлила місце постаті єпископа в катакомбній Церкві та наголосила на важливості, яку мало для підпілля збереження апостольського приємства. <br /><br />Редактор довідково-пізнавальної інформації РІСУ Анатолій Бабинський в своїй доповіді зосередився на наслідках підпілля для богослов\'я УГКЦ. Він зауважив, що головним лейтмотивом подальшого розвитку богословської думки Греко-Католицької Церкви стало богослов\'я мучеництва. В своєму виступі він також коротко проаналізував рівень рецепції сьогоднішньою греко-католицькою спільнотою рішень ІІ Ватиканського Собору, в якому з відомих причин УГКЦ не змогла повноцінно взяти участь. Анатолій Бабинський також наголосив на одному із негативних наслідків легалізації УГКЦ - втраті справжніх євхаристійних спільнот в УГКЦ та відповідно зростанню кількості &laquo;номінальних християн&raquo;. <br /><br />Співробітник Інституту Історії Церкви Тарас Бублик проаналізував ставлення радянської влади до Греко-Католицької Церкви в період 1988-1989 роках. <br /><br /> Олег Муравський - співробітник Інституту українознавства НАНУ зупинився на висвітленні міжконфесійних суперечок 1988-1989 роках в Західній Україні та запропонував кілька моделей, як можна було їх уникнути. <br /><br /> Отець Віталій Дуткевич (ЧСВВ) поділився із учасниками круглого столу свідченнями свого служіння в катакомбній Церкві.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.risu.org.ua/ukr/</span></span></p>',5,1,2538);
INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1483,'2009-05-15 00:25:17',1,'P','Копія Ікони Вишгородської Божої Матері у Київській клінічній лікарні №11','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,2539,200,10.jpg\" alt=\"\" />До копії чудотворної Ікони Вишгородської Божої Матері, яка цього тижня перебуває у церкві св. Миколая Чудотворця на Аскольдовій Могилі, змогли помолитися пацієнти терапевтичного відділення Київській клінічній лікарні №11. Отець Олексій Геник як капелан лікарняної каплиці Покрови Пресвятої Богородиці, привіз учора ікону у лікарню. У своїй проповіді отець наголосив, що тільки через заступництво і молитви Божої Матери, ми можемо отримати царство Боже. Бо саме покров матері найбільше оберігає людину, і найбільше допомагає кожній людині.</span></span></p>\r\n<p>&nbsp;</p>','<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Фото: Любові Михайлюк</span></span></p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,2539,200,11.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,2539,200,12.jpg\" alt=\"\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: left;\" src=\"image,2539,200,14.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,2539,200,13.jpg\" alt=\"\" /></p>',5,1,2539),
(1484,'2009-05-15 14:33:28',0,'N','','','<p style=\"text-align: right;\">КОЛИ БЕРЕШ - НАПОВНЮЄШ РУКУ</p>\r\n<p style=\"text-align: right;\">КОЛИ ДАЄШ - НАПОВНЮЄШ СЕРЦЕ</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,2540,200,pos2.jpg\" alt=\"\" width=\"150\" height=\"200\" />Парафія церкви св. Миколая Чудотворця на Аскольдовій Могилі продовж багатьох років за навчанням Господа нашого Ісуса Христа провадить діла милосердя. Наша церква має за мету служити своєму ближньому, слідувати науці святого Євангелія. Ми допомагаємо потребуючим віднайти у собі сили, укріпитися у вірі, навернутися до Бога, повернутися до праці, а також у такий спосіб спонукаємо їх теж надалі творити діла милосердні, допомагати потребуючим.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Милосердя до ближнього засвідчено 12 літньою історією нашої парафії і тисячолітньою історією цього Світлого місця. Якщо заглянути у давнину, то ми знайдемо багато свідчень, що саме тут на Аскольдові Могилі, завжди знаходили допомогу потребуючі. Завжди принесена панахида поминання спочилих роздавалась убогим.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Святий Миколай Чудотворець повсякчас допомагав цьому храму, бо завжди від нього для вбогих тут є хліб. Тому й не дивно, що й ми, дорогі парафіяни, з відродженням парафії 1992 року створили церковну службу милосердя &laquo;КАРІТАС-Київ&raquo;. Ця служба була під нашою безпосередньою опікою 15 років, зараз нею опікуються отці Василіани на Львівській площі.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сьогодні ми стараємось також щоб зареєструвати парафіяльне служіння &laquo;КАРІТАС-Печерськ&raquo; і це зрозуміло, тому що &laquo;КАРІТАС&raquo; це є загальна служба милосердя Католицької Церкви. Ми маємо багато партнерів у служінні &laquo;КАРІТАС&raquo;. У служінні ув\'язненим жінкам ми звернулися у &laquo;КАРІТАС-Відень&raquo;, який готовий нам допомагати.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,2540,200,2328.jpg\" alt=\"\" />Вся історія нашої Аскольдової Могили говорить про те, що Господь благословляє кожний намір допомоги ближньому. Тому прошу Вас відкликайтесь на це служіння, бо все що добре ми зробимо у нашій парафій, все робиться від вашого імені.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Наші парафіяни згуртовані у спільноту служінні милосердя як волонтери, як добро творці, як доброчинці. У різний спосіб можна долучитися до творення добра. Головою загальної парафіяльної служби милосердя є пані Ореста Цембрівська.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Діла милосердя творяться коштом пожертв у скриньку біля Ікони Зарваницької Божої Матері, окремих пожертв доброчинців-парафіян, благодійних організацій. Також у доброчинних справах залучено водія пана Андрія та мікроавтобус.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У даний час у нашій парафії діють такі програми:</span></span></p>\r\n<ul class=\"unIndentedList\" style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong> Програма &laquo;Банк їжі&raquo;</strong>, керівник проекту Лілія Городиська. Мета програми: продуктова допомога багатодітним сім\'ям, малозабезпеченим родинам, дуже бідним родинам Печерського району міста Києва..</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><img style=\"float: left;\" src=\"image,2540,200,2537.jpg\" alt=\"\" /> Програма &laquo;Гарячий обіди&raquo;</strong>, керівник проекту Ореста Цембрівська. Мета програми: харчування бездоглядних дітей, дітей вулиці, дітей сиріт, знедолених людей похилого віку без житла. (15 дітей та 10 дорослих). Проект &laquo;Гарячі обіди&raquo; звершується за кошти церкви св. Дженуарія, м. Беневенто, отцем Дон Паскуалем.</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong> Програма &laquo;Недільна школа&raquo;</strong>, керівник проекту Ореста Цембрівська. Мета проекту: духовне виховання та навчання бездоглядних дітей, дітей вулиці, дітей сиріт. Художній гурток, туристичний гурток, музичний гурток. </span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong> Програма &laquo;Допомога ув\'язненим жінкам&raquo;</strong>, керівник проекту Марта Бліхар. Мета проекту: духовна та матеріальна допомога ув\'язненим жінка та їхнім дітям. (50 жінок, близько 80 дітей)</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><img style=\"float: right;\" src=\"image,2540,200,4.jpg\" alt=\"\" /> Програма &laquo;Опіка дитячого будинку для неповносправних&raquo;</strong>, керівник проекту отець Ігор Онишкевич. Мета програми: допомога дітям сиротам дитячому будинку &laquo;Для неповносправних дітей&raquo; села Потіївка, Житомирської області. (понад 100 дітей хворих на ДЦП)</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong> Програма &laquo;Цільова допомога&raquo;</strong>, керівник проекту Марта Бліхар. Мета програми: цільова матеріальна, продуктова, медикаментозна допомога потерпілим від повені у Івано-Франківській області: 5-ти родинам села Воскресенка, 1 родина села Битків. Допомога родині одинокої багатодітної матері із Заліщицького району, Тернопільської області. (свого часу ця багатодітна жінка активно помагала нашій службі милосердя, тепер ми їй допомагаємо). Допомога багатодітній родині (годувальник якої трагічно загинув у часі &laquo;помаранчевої революції&raquo; відстоюючи національні ідеї нашої держави) у Житомирській області.</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong> Програма &laquo;Одяг для потребуючих&raquo;</strong>, керівник проекту Ірина Гузенко. Мета проекту: збір одягу і його передання парафіяльною службою милосердя бідним родинам, що мешкають в околицях Києва.</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong> Програма &laquo;Товариство разом за життя&raquo;</strong>, керівник проекту Антоніна Онишкевич. Мета програми: захист ненародженого життя, укріплення родини, переконання родини зачати дитину, допомога необхідними дитячими речами і продуктами у перші місяці народження дитини, хрещення дитини, провадження інформації про захист життя.</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong> Програма &laquo;Оздоровлення дітей&raquo;</strong>, керівник проекту отець Ігор Онишкевич. Мета проекту: оздоровлення, фізичне та духовне укріплення дітей: влітку у таборах на Чорному морі, взимку гірськолижній відпочинок у Карпатах. Також до цієї програми входить організація прощ до Зарваниці.</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><img style=\"float: left;\" src=\"image,2540,200,2522.jpg\" alt=\"\" /> Програма &laquo;Опіка тяжкохворих&raquo;</strong>, керівник проекту отець Анатолій Тесля. Мета програми: душпастирська опіка над тяжкохворими, гуманітарна та продуктова допомога. (7 ВІЛ інфікованих, 10 тяжкохворих)</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong> Програма &laquo;Порадня для трудових мігрантів та їх родин&raquo;</strong>, керівник проекту Мар\'яна Черешнівська. Мета програми: збереження цілісності родини особистої гідності. Направлення на юридичні, медичні консультації, допомога одягом і їжею. Допомога у соціально психологічна адаптації. Допомога у наверненні до Бога та ствердження у вірі.</span></span></li>\r\n</ul>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З любов\'ю у Христі, ваш отець Ігор Онишкевич</span></span></p>',5,1,2540);
INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1485,'2009-05-15 14:32:29',1,'P',' Слово від Редакції','<p style=\"text-align: right;\"><strong>КОЛИ БЕРЕШ - НАПОВНЮЄШ РУКУ</strong></p>\n<div style=\"text-align: right;\"></div>\n<p style=\"text-align: right;\"><strong>КОЛИ ДАЄШ - НАПОВНЮЄШ СЕРЦЕ</strong></p>\n<p><img style=\"margin: 10px; float: left;\" src=\"http://www.askoldova-mohyla.org/uk/image,2540,200,pos2.jpg\" height=\"200\" width=\"150\" />Парафія церкви св. Миколая Чудотворця на Аскольдовій Могилі продовж багатьох років за навчанням Господа нашого Ісуса Христа провадить діла милосердя. Наша церква має за мету служити своєму ближньому, слідувати науці святого Євангелія. Ми допомагаємо потребуючим віднайти у собі сили, укріпитися у вірі, навернутися до Бога, повернутися до праці, а також у такий спосіб спонукаємо їх теж надалі творити діла милосердні, допомагати потребуючим.</p>\n<p>Милосердя до ближнього засвідчено 12 літньою історією нашої парафії і тисячолітньою історією цього Світлого місця. Якщо заглянути у давнину, то ми знайдемо багато свідчень, що саме тут на Аскольдові Могилі, завжди знаходили допомогу потребуючі. Завжди принесена панахида поминання спочилих роздавалась убогим.</p>\n','',NULL,1,2541),
(1486,'2009-05-14 14:51:25',1,'P','Історія Вишгородської ікони Божої Матері','<p align=\"justify\"><img style=\"margin: 10px; float: right;\" src=\"http://www.prytyska.kiev.ua/images/stories//prev/Vyshgorod.jpg\" height=\"150\" width=\"133\" />Вишгородська ікона Божої Матері написана Євангелистом Лукою на дошці від столу, за яким був за трапезою Спаситель з Пречистою Матір\'ю і праведним Йосипом. Божа Мати, побачивши цей образ, промовила: \"Віднині ублажатимуть Мене всі роди. Благодать Народженого від Мене й Моя нехай буде з цією іконою\". В 1131 році ікону була надіслано до Руси з Константинополя святому князеві Мстиславові († 1132, пам\'ять 15 квітня), і вона була поставлена в Дівочому монастирі Вишгорода - давнього помісного уділу святої рівноапостольної великої княгині Ольги.</p>\n','',NULL,1,2542),
(1487,'2009-05-15 15:01:23',1,'P','«Я іду, Христе!»  18 травня  19:00','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><img style=\"float: right;\" src=\"image,2543,200,2.JPG\" alt=\"\" />Виставу благословив Блаженніший Любомир Кардинал Гузар Верховний Архієпископ Києво-Галицький УГКЦ</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вистава створена за книгами Григора Меріям-Лужницького &laquo;Дванадцять листів о. Андрія Шептицького до матері&raquo; і мемуарами &laquo;Молодість та покликання о. Романа Шептицького&raquo;, написані матір\"ю митрополита&nbsp; Софією&nbsp; Шептицькою&nbsp; з роду Фредрів.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><img style=\"float: right;\" src=\"image,2543,200,1.jpg\" alt=\"\" />Національний&nbsp; центр театрального мистецтва імені Леся Курбаса </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">та творча майстерня <strong><em>&bdquo;Театр у кошику\"&nbsp; </em>Львів-Київ</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">м. Київ&nbsp; в.Володимирська,23В&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">279-50-69, &nbsp;279-12-89&nbsp; kurbas.org.ua</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">067-943-77-57</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"text-decoration: underline;\">18 травня&nbsp; 19:00</span></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: medium;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&laquo;Я іду, Христе!&raquo;</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Виставу благословив Блаженніший Любомир Кардинал Гузар Верховний Архієпископ Києво-Галицький УГКЦ</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вистава створена за книгами Григора Меріям-Лужницького &laquo;Дванадцять листів о. Андрія Шептицького до матері&raquo; і мемуарами &laquo;Молодість та покликання о. Романа Шептицького&raquo;, написані матір\"ю митрополита&nbsp; Софією&nbsp; Шептицькою&nbsp; з роду Фредрів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,2543,200,2.JPG\" alt=\"\" />В її основі вистави покладено матеріал із книги Григора Лужницького&nbsp; \"Дванадцять листів отця Андрея Шептицького до матері\" (художній твір на основі автентичних листів) та мемуари \"Молодість і покликання Романа Шептицького\", написані матір\'ю митрополита Софією Шептицькою з роду Фредрів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Режисер - заслужений діяч мистецтв України&nbsp; -&nbsp; <strong>Ірина Волицька</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Софія ( мати Андрія Шептицького)&nbsp; - заслужена артистка України, лауреат премії ім. Котляревського -&nbsp; <strong>Лідія Данильчук</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Андрій&nbsp; Шептицький - актор львівського театру &bdquo;Відродження\" <strong>Володимир Губанов</strong></span></span></p>',5,1,2543);
INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1488,'2009-05-15 15:45:53',0,'N','','','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Екскурсія до Чернігова</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,2544,200,IMG_0653.JPG\" alt=\"\" />Місяць травень багатий на вихідні і це нагода поїхати відпочити або здійснити екскурсійну поїздку. Так молодь з церкви св. Миколая Чудотворця, що&nbsp; на Аскольдовій Могилі разом із сестрою Стефанією відвідали місто Чернігів. До Чернігова їхати всього дві години маршруткою. Першою нашою зупинкою був собор Святого Спаса, який було збудовано за часів князя Мстислава. Цей собор називають найбільш візантійським, який був на Київській Русі. Споруда вражає своїми розмірами: глибина підмурків 4-5 метрів, товщина стін понад 1,5 метри, висота споруди в середині від підлоги до головної бані 29 метрів. В середині собор оформлено у візантійському стилі. Стіни храму прикрашено фресковим живописом, на якому зображені картини із Старого і Нового Завітів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З північного боку від Спаського Собору побудовано чернігівським князем Давидом Борисоглібський собор. Про князя розповідали, що він був ближчий до храму і бібліотеки аніж до поля бою. Давид збудував, цей собор як&nbsp; родинну церкву і усипальницю.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,2544,200,IMG_0647.JPG\" alt=\"\" />Доля цього храму була набагато драматичнішою, аніж Спаського. Його декілька разів руйнували і перебудовували. Перше руйнування пов\'язане із татаромонгольською навалою, а друге під час Другої Світової війни, коли бомба впала всередину храму. Від вибуху спалахнула пожежа, яка знищила все в середині. Після війни собор відновили і він набув такого вигляду, яким його бачив князь Давид. Зараз Борисоглібський собор є музеєм, який розповідає про історію міста і собору. Тут зберігається царська іконостасна срібна брама виготовлена на гроші гетьмана Мазепи.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В другій половині 17 ст. коли Чернігів стає козацьким містом, цей собор стає кафедральним. Сюди переносить свою кафедру Лазар Баранович - архієпископ чернігівський. Довкола собору утворюється монастир, а на початку 18 століття при Борисоглібському монастирі відкривається перший вищий навчальний заклад на Лівобережжі під традиційною тогочасною назвою колегіум. Варто відмітити, що за одною партою могли сидіти діти простих селян і козацьких полковників, що на той час не зустрічалось в інших навчальних закладах. Навчання тривало вісім років, що вимагало багато коштів і тому увесь курс пройти могли тільки діти із заможних родин. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,2544,200,IMG_0387.jpg\" alt=\"\" />Далі ми вирушили до фортечного валу. В усі часи чернігівська фортеця була дерев\'яна,&nbsp; але в той же час вона була важкодоступною. Тільки в кінці 18 століття за наказом Катерини ІІ через те, що фортеця опинилась у провінції і вже була нікому не потрібна, бо Росія приєднала до себе Крим, Правобережну Україну і частину Польщі, фортецю ліквідовують. Оскільки вона була дерев\'яною то її місцеве населення розібрало на будівельні матеріали. Найстаріші гармати були покинуті напризволяще. Пройшло 100 років поки місцева влада схаменулась, по всій окрузі позбирали старі гармати і виставили вздовж валу. Гармат всього є дванадцять, але в місті існує оповідка про тринадцяту гармату, якщо дівчині не подобається кавалер, а кавалер є настійливим, то, щоб поставити остаточну крапку в стосунках призначають зустріч біля тринадцятої гармати, заздалегідь знаючи, що їх всього дванадцять. Погулявши вздовж фортечних&nbsp; мурів ми із історичної частини міста вирушили в сучасну. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,2544,200,IMG_0366.JPG\" alt=\"\" />Багатьом центральна площа Чернігова нагадала Тернопіль правда у дещо збільшеному варіанті. Із центру ми вирушили до наступної зупинки - Антонієві печери, які названі на честь їхнього засновника Антонія Печерського. Антоній Печерський - відомий церковний та політичний діяч часів Київської Русі. Він був одним із засновників Києво-Печерського монастиря. В Чернігів переїхав за запрошенням князя Святослава і за його бажанням засновує монастир. У печерах ми побували у Іллінській церкві і костяниці. Костяниця - це приміщення для зберігання людських кісток. Згідно із давнім християнським обрядом померлого спочатку закопували без домовини у звичайній ямі, а потім приблизно через три роки, коли останки виймали і переміщували до костяниці, де і вони зберігалися.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">День поволі доходив до кінця, а нам потрібно було повертатись назад до Києва. Переповнені позитивними емоціями ми з неохотою покинули Чернігів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Матеріал та фото: Ярослава Павлюка &laquo;Оранта&raquo;</span></span></p>',5,1,2544);
INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1489,'2009-05-15 16:04:39',1,'P','Екскурсія до Чернігова','<p><img style=\"margin: 10px; float: right;\" src=\"http://www.askoldova-mohyla.org/uk/image,2544,200,IMG_0653.JPG\" />Місяць травень багатий на вихідні і це нагода поїхати відпочити або здійснити екскурсійну поїздку. Так молодь з церкви св. Миколая Чудотворця, що  на Аскольдовій Могилі разом із сестрою Стефанією відвідали місто Чернігів. До Чернігова їхати всього дві години маршруткою. Першою нашою зупинкою був собор Святого Спаса, який було збудовано за часів князя Мстислава. Цей собор називають найбільш візантійським, який був на Київській Русі. Споруда вражає своїми розмірами: глибина підмурків 4-5 метрів, товщина стін понад 1,5 метри, висота споруди в середині від підлоги до головної бані 29 метрів. В середині собор оформлено у візантійському стилі. Стіни храму прикрашено фресковим живописом, на якому зображені картини із Старого і Нового Завітів.</p>\n','',NULL,1,2545),
(1490,'2009-05-16 23:53:18',1,'P','50 ліття творчості Івана Гамкало','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,2546,200,Hamkalo.jpg\" alt=\"\" />Парафія св. Миколая Чудотворця на Аскольдовій Могилі щиросердечно вітає свого парафіянина Івана-Ярослава Гамкала з його ювілеями 70-ти ліття життєвої ниви і 50-ти ліття творчої ниви. Іван-Ярослав Гамкало Народний артист України, диригент Національної опери України. Все своє життя присвятив українській музиці, написанню музичних творів. У такий спосіб пан Іван-Ярослав допомагає нам пізнати свою історію, зрозуміти свою національну ідею і любити свою державу, гордитися нею. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ми зичимо пану Івану-Ярославу многих і благих літ. Бажаємо плідно творити і написати музичний твір присвячений князю Аскольду, царю і мученику за віру христову.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,2546,200,IMG_1164.JPG\" alt=\"\" />Парафія св. Миколая Чудотворця на Аскольдовій Могилі щиросердечно вітає свого парафіянина Івана-Ярослава Гамкала з його ювілеями 70-ти ліття життєвої ниви і 50-ти ліття творчої ниви. Іван-Ярослав Гамкало Народний артист України, диригент Національної опери України. Все своє життя присвятив українській музиці, написанню музичних творів. У такий спосіб пан Іван-Ярослав допомагає нам пізнати свою історію, зрозуміти свою національну ідею і любити свою державу, гордитися нею.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ми зичимо пану Івану-Ярославу многих і благих літ. Бажаємо плідно творити і написати музичний твір присвячений князю Аскольду, царю і мученику за віру христову.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img src=\"image,2546,gamkalo.jpg\" alt=\"\" width=\"464\" height=\"663\" /><br /></span></span></p>\r\n<p style=\"text-align: left;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://who-is-who.com.ua/bookmaket/yuvilyars2009/13/173.html<br /></span></span></p>\r\n<p style=\"text-align: left;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>',5,1,2546);
INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1491,'2009-05-21 23:16:20',0,'P','Відкрита лекція Мирослава Мариновича у Києві','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Католицький Університет запрошує Вас на відкриту   лекцію пана *Мирослава Мариновича* під   назвою <strong>*\"Публічна сфера та релігійні цінності\"*</strong>, що відбудеться <strong>21 травня о   19.30</strong> у приміщенні церкви Св. Василія Великого (<strong>Вознесенський узвіз, 7</strong>).</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Католицький Університет запрошує Вас на відкриту   лекцію пана *Мирослава Мариновича* (засновника та президента Інституту   релігії та суспільства, відомого громадського діяча, колишнього дисидента,   правозахисника, публіциста, автора численних статей та монографій) під   назвою <strong>*\"Публічна сфера та релігійні цінності\"*</strong>, що відбудеться <strong>21 травня о   19.30</strong> у приміщенні церкви Св. Василія Великого (<strong>Вознесенський узвіз, 7</strong>).</span></span></p>',5,1,2547),
(1492,'2009-05-17 23:29:02',0,'P','','<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Гріхи проти Святого Духа</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1. Надмірна надія на Боже милосердя</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">2. Безнадійність на Боже милосердя</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">3. Спротив пізнаній правді християнської віри</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">4. Завидування ближньому Божої ласки</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">5. Закаменілість на спасенні упімнення</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">6. Нерозкаяність аж до смерти</span></span></p>','',5,1,2548),
(1493,'2009-05-17 23:32:43',1,'P',' Католицький Народний Катехизм','<p><strong>ВІРА</strong></p>\n<p><strong>Боже провидіння<br /></strong></p>\n<p>Провидінням Божим називаємо утримання світу і керівництво          ним.</p>\n<p>Про жодну правду св. Письмо не говорить так часто, як            про Провидіння Боже.</p>\n<p>Бог утримує світ, тобто забезпечує існування всього            живого.</p>\n','',NULL,1,2549),
(1494,'2009-05-17 23:48:10',1,'P','Щирі вітання нашому диякону ювіляру','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Парафія церкви св. Миколая Чудотворця на Аскольдовій Могилі вітає диякона Олександра Соколовського з ювілеєм - 50-ти літтям.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Парафія церкви св. Миколая Чудотворця на Аскольдовій Могилі вітає диякона Олександра Соколовського з ювілеєм - 50 літтям. Зичить довгих років життя, Божої лаки та благословення, терпеливості і вірного служіння Христу. Божого заступництва всієї родини. Многая і благая літа.</span></span></p>',5,1,2550);
INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1495,'2009-05-17 23:50:31',1,'P','Пам’яті жертв репресій','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Служба безпеки України на даний момент встановила прізвища 14191 людини - усім їм було винесено вироки в Києві і всі вони поховані в Биківні, чому знайдено документальні архівні підтвердження.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Ми стверджуємо, що вибір місця під масові поховання в Биківні - не випадок, а сплановані дії&raquo;, - зазначив співробітник Галузевого державного архіву СБУ доктор історичних наук, професор Василь Даниленко.</span></span></p>\r\n<p>&nbsp;</p>','<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>СБУ встановила імена 14 тисяч похованих у Биківні</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Про це у четвер напередодні державних заходів до Дня пам\'яті жертв політичних репресій, який відзначатиметься 17 травня, повідомили в рамках громадських слухань &laquo;Трагедія Биківні: як це було&raquo;, передає прес-центр СБУ.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Служба безпеки України на даний момент встановила прізвища 14191 людини - усім їм було винесено вироки в Києві і всі вони поховані в Биківні, чому знайдено документальні архівні підтвердження.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Ми стверджуємо, що вибір місця під масові поховання в Биківні - не випадок, а сплановані дії&raquo;, - зазначив співробітник Галузевого державного архіву СБУ доктор історичних наук, професор Василь Даниленко.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У 1936 році тут було створено спецоб\'єкт, який суворо охоронявся військами НКВС, а вже 20 березня 1937 року президією Київської міської ради о другій годині ночі було ухвалено таємну постанову про відведення землі для спеціальних потреб. Ця земля стала місцем поховань для розстріляних у Києві за рішенням судів і позасудових органів. Вироки виконувалися в підвалах приміщення Київського обласного управління НКВС (нині там розміщується Український інститут національної пам\'яті, вул. Липська, 16), звідки тіла вночі вивозили в Биківню. А перед війною заарештованих знищували безпосередньо біля виритих у лісі ям.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сьогодні виявлено 18 місць поховань жертв масових політичних репресій 1937-1941 років, зокрема місце, на якому згодом було побудовано Центральний універмаг у Хмельницькому, парк культури і відпочинку у Вінниці, 9-й кілометр Запорізького шосе, центральне міське кладовище в Сумах.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.dt.ua/</span></span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Слово Президента України у Биківні у День пам\'яті жерт політичних репресій</strong><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> <img style=\"float: left;\" src=\"http://www.president.gov.ua/done_img/b/13811.jpg\" alt=\"\" />\r\n<p>Дорогий Український народе,</p>\r\n<p>Дорогі співвітчизники,</p>\r\n<p>Дорога жалобна громадо!</p>\r\n<p>Сьогодні Україна вшановує пам\'ять мільйонів жертв комуністичних репресій.</p>\r\n<p>Ми стоїмо на землі, де кожна травинка і кожне дерево росте з нашої крові.</p>\r\n<p>Це - Биківня. Це Биківнянський ліс. Нехай його голос сьогодні почує вся Україна.</p>\r\n<p>Ми прийшли сюди, щоб пом\'янути всіх, всіх замучених у сталінських тюрмах і концтаборах. Ми прийшли, щоб ніхто й ніколи не забув катастрофи, яку пережив наш народ у ХХ віці. У цьому найкривавішому в історії людства столітті українська нація стала заручником боротьби двох тоталітарних режимів - комуністичного й фашистського. Вони - співмірні за своєю людиноненависницькою суттю. Вони - тотожні за небаченими масштабами масових убивств.</p>\r\n<p>Досі ніхто не знає, скільки десятків тисяч наших співвітчизників лежать у Биківнянському лісі. Биківня, як і Бабин Яр, як Освенцім і Соловки, як Бухенвальд і Катинь - це страшні символи мучеництва невинних жертв тоталітарних терорів. Тут, у Биківні, Сталін і його звироднілі опричники убивали цвіт України і української нації. Десятки, десятки тисяч людей.</p>\r\n<p>Прощення немає і не буде.</p>\r\n<p>Тут поховані письменники Яків Савченко, Михайло Семенко, Микола Скуба, Вероніка Черняхівська-Ганжа, брат Остапа Вишні письменник Василь Чечвянський, фундатор Академії мистецтв, професор Київського художнього інституту Михайло Бойчук, скульптор Іван Падалка, академіки Олександр Асаткін, Євген Оппоков, Володимир Юринець, директор Київського медичного інституту Сергій Сапронов, викладачі кафедри української мови і літератури Київського педагогічного інституту професор Олексій Синявський та доцент Олексій Бондаренко, ієрархи Української Автокефальної Православної Церкви Кость Матюшенко та Володимир Самборовський, священик, професор Київської духовної академії, вчений із світовим іменем Олександр Глаголев. І тисячі, тисячі інших.</p>\r\n<p>Звідси, з Биківні йде незримий зв\'язок до всіх неполіченних кладовищ комуністичного терору на нашій землі. У цьому диявольському переплетінні - вся Україна. Обов\'язок нації - згадати всіх.</p>\r\n<p>Сталінська верхівка боялася й ненавиділа кожен народ, підкорений нею. Найбільший страх вона відчувала перед Україною. Знищенню підлягав наш дух. Знищенню підлягав кожен, запідозрений у найтяжчому зі злочинів - любові до України.</p>\r\n<p>Тому відновлення правди про політичні репресії і гідне вшанування їхніх жертв - це не просто символ. Це - головна ознака, що наша душа жива, що наше життя незнищенне, що наш народ переміг і зло, і смерть.</p>\r\n<p>За останні роки зроблено немало. За моїми дорученнями розсекречено архівні документи про український визвольний рух, політичні репресії та голодомори. У наших містах вже відкриваються центри, де кожен має доступ до актів комуністичного терору. Такі зали створено в 14 обласних центрах, зокрема в Києві, Одесі, Харкові, Сімферополі, Севастополі, Ужгороді, Миколаєві, Житомирі, Запоріжжі, Луганську, Вінниці, Сумах, Хмельницькому. Невдовзі вони будуть створені і запрацюють в усіх областях.</p>\r\n<p>Сьогодні я хочу висловити глибоку вдячність Службі безпеки України за роботу, завдяки якій в останній час вдалося встановити прізвища 14191 особи, похованої в Биківні. На сьогодні нам відомі імена близько 30 тисяч людей, які поховані, зариті, закопані під цими соснами. Вдалося знайти ще 18 великих таємних цвинтарів жертв репресій 1937-1941 років.</p>\r\n<p>Це - велика, національна справа.</p>\r\n<p>Ми говоримо про всіх. Розсекречено й опубліковано документи про злочинну депортацію кримських татар у 1944 році. Завтра кримськотатарським лідерам буде передано матеріали стосовно репресій в 20-30 роках.</p>\r\n<p>Ми доводимо правду до всього світу. Багатьом країнам світу ми сповістили про військовополонених, похованих на теренах України. Німеччині передано інформацію про репресованих німців з Донецької області. Вина їх полягала тільки в тому, що під час Голодомору вони зверталися до Німеччини за продовольчою допомогою.</p>\r\n<p>Робота триває. Робота має продовжуватися.</p>\r\n<p>Поруч із Службою безпеки я дякую Інститутові національної пам\'яті і всім причетним організаціям, які повертають нам правду, правду про нашу болючу історію.</p>\r\n<p>Не можу обійти увагою Соловки, які відвідав минулого року. Щороку українська делегація бере участь у вшануванні загиблих в урочищі Сандормох та на Великому Соловецькому острові. Тепер цій громадській ініціативі надається підтримка держави.</p>\r\n<p>Скажу й про інше.</p>\r\n<p>Україна має нарешті і остаточно очиститись від символів режиму, який знищив мільйони безневинних людей. Виправдання бути не може. Це - не частина нашої історії, як дехто цинічно хоче сказати. Це - частина комуністичної системи. Це - символи вбивства, збереження яких є блюзнірством перед полеглими.</p>\r\n<p>Упродовж 2007-2008 років в Україні демонтовано понад 400 пам\'ятників діячам комуністичного режиму - організаторам Голодомору та політичних репресій 1937-41 рр. Перейменовано понад 3 тисячі топографічних назв, що носили їхні імена. Але до завершення цієї роботи, безумовно, ще дуже далеко. Перед нами стоять великі завдання.</p>\r\n<p>Кожен раз, коли ми навесні приходимо в Биковнянський ліс, ми говоримо про розвиток заповідника &laquo;Биковнянські могили&raquo;. З кожним роком ми стаємо свідками того, як перевіряються проекти, мета яких - повернути нам пам\'ять, дати шану тим людям, які були закопані в 30-х роках в цю святу землю.</p>\r\n<p>Національний статус заповідника отримали &laquo;Биківнянські могили&raquo;. Споруджено офісне приміщення, встановлюються перші меморіальні частини того заповідника, над яким працює сьогодні місто, і віце-прем\'єр-міністр із гуманітарних питань Іван Васюник, міністр культури і багато-багато інших, включаючи, безумовно, Інститут національної пам\'яті, і цілу низку громадських організацій.</p>\r\n<p>Але, переконаний, ми і досі у великому боргу перед цією сторінкою нашої історії.</p>\r\n<p>Я не можу бути спокійний, доки не вирішено питання про гідний соціальний захист колишніх політичних в\'язнів та репресованих.</p>\r\n<p>У парламенті досі не розглянуто внесені мною законопроекти про правовий статус борців за незалежність України.</p>\r\n<p>Мене дуже часто обурює байдужість та відчуженість, яку демонструють наші політики й посадовці. Вони бояться цієї теми. Вони хотіли б владарювати в Україні без української культури, без української мови, без українців. Їхня мрія - це губернія, де б вони були безроздільними господарями.</p>\r\n<p>Як наслідок - ми є свідками спроб реваншу.</p>\r\n<p>Ті, хто заперечують Голодомор і репресії, ті, хто виправдовують Сталіна, ті, хто піднімають руку на наші святині і наших людей - приречені. Вони свідомо стають на бік однієї з найчорніших сил, які знала історія. Їхні чорні надії, глибоко переконаний, є безсилі.</p>\r\n<p>Нація відроджується.</p>\r\n<p>Великий український поет Василь Симоненко писав: &laquo;Можна прострелити мозок, що думку народить, думки ж - не вбить!&raquo;</p>\r\n<p>Сьогодні - істинно скорботний день. Водночас це - день торжества правди й справедливості.</p>\r\n<p>На руїнах радянської імперії постала Україна. Це - спільна і велика перемога всього Українського народу. Це - перемога нашого покоління, перемога тих, хто боровся за свободу Батьківщини, перемога тих, хто впав жертвою терору і репресій. Ми перемогли, і наша перемога - вічна.</p>\r\n<p>Вічна пам\'ять загиблим.</p>\r\n<p>Слава Українському народові!</p>\r\n<p>Слава Україні!</p>\r\n<p><img style=\"float: right;\" src=\"http://www.president.gov.ua/done_img/b/13812.jpg\" alt=\"\" /></p>\r\n<p>У День пам\'яті жертв політичних репресій Президент України Віктор Ющенко взяв участь у заходах із вшанування пам\'яті жертв політичних репресій на території Національного історико-меморіального заповідника &laquo;Биківнянські могили&raquo;.</p>\r\n<p>Разом із Президентом пам\'ять невинно убієнних вшанували Секретар РНБОУ Р.Богатирьова, віце-прем\'єр-міністр України І.Васюник, голова Київської облдержадміністрації В.Ульянченко, Київський міський голова Л.Черновецький, народні депутати України, представники громадських організацій репресованих, родичі загиблих, дослідники політичних репресій, представники політичних партій, громадськість, молодь.</p>\r\n<p>Президент поклав квіти до монумента Політв\'язня при в\'їзді до заповідника, а також до Пам\'ятного Хреста.</p>\r\n<p>Глава держави також взяв участь у скорботній ході до Пам\'ятного Хреста та виступив із промовою.</p>\r\n<p>У рамках заходу відбулася панахида за загиблими за участю духовенства українських церков УПЦ, УГКЦ, РКЦ.</p>\r\n<p>На завершення офіційної частини заходу його учасники вшанували пам\'ять загиблих співвітчизників хвилиною мовчання.</p>\r\n<p>Президент також оглянув історико-документальну виставку &laquo;Зламані долі: комуністичний терор в Україні 1920-1950-х років&raquo;, підготовлену Фондом &laquo;Україна 3000&raquo; спільно з Галузевим архівом Служби безпеки України та Київською міською організацією Всеукраїнського товариства &laquo;Меморіал&raquo; ім. Василя Стуса.</p>\r\n</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">ДОВІДКА</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У Биківні знаходиться найбільше в Україні місце поховання жертв масових політичних репресій. Тут був об\'єкт спеціального призначення НКВД, де у кінці 30-х - в 40-х роках відбувалися масові захоронення розстріляних та закатованих. Встановлено, що в районі села Биківня закопували приречених до розстрілу в позасудовому порядку, вирок стосовно яких виконано у місті Києві. За даними науковців, у Биківнянському лісі покоїться близько 100 тис. репресованих комуністичною владою. На сьогодні за виявленими і опрацьованими співробітниками СБУ документами встановлено прізвища 14191 особи.<br /> Ці могили було розкрито на огляд громадськості під час окупації німецькою владою з метою викриття злочинів більшовицького режиму. Однак після закінчення війни радянською владою всю інформацію було знову засекречено і проведено &laquo;власні розслідування&raquo;: три державні комісії 1945, 1971, 1987 рр. оголосили про те, що у Биківні поховані жертви фашизму. При цьому в ході діяльності останньої з них було виявлено та знищено багато документів. Лише четверта комісія у липні 1989 року оголосила, що у Биківні поховані жертви сталінських репресій.<br /> Биківня є лише одним з багатьох поховань жертв комуністичного терору. Криваву географію виявлених місць поховань описано 28 лютого 1989 р. у таємному листі Голови КДБ УРСР М. Голушка Першому секретареві ЦК Компартії України В. Щербицькому: Бердичів - район єврейського цвинтаря; Вінниця - парк культури і відпочинку, колишній міський цвинтар, сад по вулиці Підлісній; Дніпропетровськ - 9-й кілометр Запорізького шосе; Донецьк - район шахти №11 Кіровського району; Івано-Франківськ - урочище &laquo;Дем\'янів Лаз&raquo;; Кам\'янець-Подільський - русько-фільварочний цвинтар та поблизу села Кам\'янка; Луганськ - окраїна міста, урочище &laquo;Суча Балка&raquo;; Одеса - другий християнський цвинтар; Полтава - район піщаного кар\'єру поблизу села Макухівка, урочище &laquo;Триби&raquo;; Суми - центральне міське кладовище; Умань - братська могила; Харків - лісопаркова зона; Хмельницький - один з об\'єктів колишнього обласного управління НКВД, де наприкінці 1960-х років побудовано Центральний універмаг; Черкаси - старий єврейський цвинтар по вулиці Т. Шевченка; Чернігів - лісовий масив в районі села Холявіно.<br /> Розстріли й масові безіменні поховання відбувалися в 30-40-х роках у Білій Церкві, Маріуполі Донецької області, Ніжині Чернігівської області, Золочеві та Стрию Львівської області, Чорткові, Бережанах, Кременці Тернопільської області, інших місцях.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.president.gov.ua/<br /></span></span></p>',5,1,2551);
INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1496,'2009-05-18 14:26:56',0,'P','Марійська молитва з Папою Венедиктом XVI','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">17 травня, площа Святого Перта була заповнена паломниками, які прибули до Ватикану з різних куточків Італії та усього світу, зібравшись перед найважливішою святинею християнського світу, щоб почути недільне повчання Вселенського Архиєрея, помолитись разом із ним Богородичну молитву &laquo;Царице Неба&raquo; та отримати його Апостольське благословення.</span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">17 травня, площа Святого Перта була заповнена паломниками, які прибули до Ватикану з різних куточків Італії та усього світу, зібравшись перед найважливішою святинею християнського світу, щоб почути недільне повчання Вселенського Архиєрея, помолитись разом із ним Богородичну молитву &laquo;Царице Неба&raquo; та отримати його Апостольське благословення. <br /><br />Папа промовив до численних вірних із вікна своєї приватної бібліотеки. Розпочинаючи катехизу він виявив присутнім своє бажання поділитись враженнями від паломництва, яке він відбув до Святої Землі. Святіший Отець подякував Господу Богу за те, що дозволив йому довершити цю так важливу подорож, а також висловив подяку усім тим, які співпрацювали для приготування та проведення того паломництва: латинському єрусалимському патріарху, провідникам Католицької Церкви у Йорданії, в Ізраїлі та на Палестинських Територіях, членам Францисканського Ордену у Святій Землі, владі цих держав, організаторам, священикам, богопосвяченим особам та усім вірним, які так тепло і щиро його прийняли, як також і тим, які супроводжували Папу своїми молитвами. <br /><br />Далі Святіший Отець зазначив, що ця проща святими місцями була теж нагодою відвідати та зустрітись із християнами, які проживають на цих землях, які виконують відповідальне завдання у діалозі християн з мусульманами та євреями і у відновленні миру на цій території. Далі додав, що ця земля є символом Божої любові до Його народу і до усього людства, символом свободи та миру, які Господь прагне для усіх Своїх дітей, проте, історичні події минулого та теперішнього вказують нам на щось зовсім протилежне: що саме ця земля є знаком протиріччя, тобто поділів та безперервних конфліктів між братами. &laquo;Як це можливо?&raquo; - запитує Папа Венедикт ХVІ, наголошуючи, що попри усе те Господь має Свій план щодо цієї Святої Землі, яку ще називають &laquo;п\'ятою Євангелією&raquo;, адже вона розповідає нам про життєвий шлях Ісуса, Божого Сина.<br /><br />Закінчуючи недільну промову, Святій Отець запевнив свою духовну близькість та молитовну підтримку мешканцям Шрі-Ланки, які зараз перебувають на території воєнних дій. &laquo;Мова йде про тисячі дітей, жінок, похилих віком, яким війна відібрала роки життя та надії&raquo;, - мовив Папа, знову закликаючи до припинення збройних конфліктів та миру. Звертаючись до гуманітарних організацій Єпископ Риму закликав поспішити на допомогу потерпілим. А доручаючи ці землі материнській опіці Пречистій Діві Марії з Мадгу, Яку так сильно почитають усі мешканці Шрі-Ланки, та вимолюючи у Неї миру для країни, Вселенський Отець закінчив своє повчання.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>',5,1,2552);
