INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1889,'2009-11-28 12:29:07',1,'P','Молитва за невинно убієнних','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,3372,200,601fa1919433.jpg\" alt=\"\" width=\"80\" height=\"106\" /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В церкві св. Миколая Чудотворця на Аскольдовій могилі, в найдавнішому місті нашої історії духовності зранку 28 листопада задзвонили дзвони в пам\'ять замучених голодом. Була відправленна Поминальна Божественна Літургія з панахидою в пам\'ять безвинно убієнних голодом заморених міліонів душ українського народу. Разом з вірними молився Арсeній Яценюк. Він запалив свічку пам\'яті перед іконою Спасителя Господа нашого Ісуса Христа.</span></span></p>','<p><img style=\"float: right;\" src=\"image,3372,200,601fa1919433.jpg\" alt=\"\" width=\"153\" height=\"200\" /></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В церкві св. Миколая Чудотворця на Аскольдовій могилі, в найдавнішому містінашої історії духовності зранку 28 листопада задзвонили дзвони в пам\'ять замучених голодом. Була відправленна Поминальна Божественна Літургія з панахидою в пам\'ять безвинно убієнних голодом заморених міліонів душ українського народу. Разом з вірними молився Арсeній Яценюк. Він запалив свічку пам\'яті перед іконою Спасителя Господа нашого Ісуса Христа.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вічна пам\'ять безвинно убієнним!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Заступництво їхніх праведних душ, особливо ангельських душ немовлят&nbsp; і дітей убієнних Бог дарував нам волю Церкви і народу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Просимо Господа заступництва і охорони на грядущі віки.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Матеріал Іллї</span></span></p>',4,1,3372),
(1890,'2009-11-30 23:45:16',0,'P','ОГОЛОШЕННЯ','<div style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">6 грудня о 14:00 годині, в церкві&nbsp;<span lang=\"UK\"> св.Миколая Чудотворця на Аскольдовій Могилі (у&nbsp;крипті) &nbsp;вібудеться зустріч молоді на тему: <strong>Сім Святих Тайн</strong>.</span></span></span></div>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">6 грудня о 14:00 годині, в церкві&nbsp;<span lang=\"UK\"> св.Миколая Чудотворця </span></span></span><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span lang=\"UK\">на Аскольдовій Могилі </span></span></span><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span lang=\"UK\">(у&nbsp;крипті) &nbsp;вібудеться зустріч молоді на тему: <strong>Сім Святих Тайн</strong>.</span></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span lang=\"UK\">Голова молодіжної Ради: Ірина Василик</span></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span lang=\"UK\">+380501730677</span></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span lang=\"UK\">email: Iryna_Vasylyk@mail.ru<br /></span></span></span></p>',5,1,3373);
INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1891,'2009-11-27 23:56:28',1,'P','Свято блаженної Йосафати Гордишевської','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">20 листопада свято блаженної Йосафати Гордишевської, вшанування її пам&rsquo;яті у церкві св. Миколая Чудотворця почалось із молебня до Йосафати, у якому взяла участь і молодіжна спільнота церкви св.Миколая Чудотворця. А по закінченню молебня молодь разом із сестрами, співали пісні на честь Йосафати. <br /></span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">20 листопада свято блаженної Йосафати Гордишевської, вшанування її пам&rsquo;яті у церкві св. Миколая Чудотворця почалось із молебня до Йосафати, у якому взяла участь і молодіжна спільнота церкви св.Миколая Чудотворця. А по закінченню молебня молодь разом із сестрами, співали пісні на честь Йосафати.&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Для молоді Йосафата Гордишевська є прикладом ревного служіння у своєму покликанні Богу та народу, незважаючи на всі випробування, які виникають</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Матеріал: </span></span><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ярослава </span></span><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Павлюка<br /></span></span></p>',5,1,3374),
(1892,'2009-11-28 23:58:49',0,'P','Літературний вечір присвячений творчості Ліни Костенко.','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3375,200,IMG_8303.JPG\" alt=\"\" width=\"143\" height=\"95\" />21 листопада у Домі Сестер-Служебниць НДМ відбувся літературно-музичний вечір присвячений творчості Ліни Костенко, організований сестрами служебницями, Мар&rsquo;яною Шіпош та Олею Петрашик. Вечір зібрав чимало шанувальників творчості поетеси, серед яких була і молодіжна спільнота з церкви св.Миколая Чудотворця. </span></span></p>','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,3375,200,IMG_8271.JPG\" alt=\"\" />21 листопада у Домі Сестер-Служебниць НДМ відбувся літературно-музичний вечір присвячений творчості Ліни Костенко, організований сестрами служебницями, Мар&rsquo;яною Шіпош та Олею Петрашик. Вечір зібрав чимало шанувальників творчості поетеси, серед яких була і молодіжна спільнота з церкви св.Миколая Чудотворця. <img style=\"float: left;\" src=\"image,3375,200,IMG_8289.JPG\" alt=\"\" />У програмі вечора декламувались твори, а давня шанувальниця творчості Неля Франчук виконала пісні на вірші Ліни Костенко. Впродовж вечора кожен мав змогу поділитись, що в його життя принесла поезія поетеси, нажаль на вечорі поетеси не було та організатори вечора підготували неї книгу відгуків, у якій кожен міг написати те, що хотів би їй.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;<img style=\"float: right;\" src=\"image,3375,200,IMG_8309.JPG\" alt=\"\" />Ще півтора десятки років тому такі вечори не були рідкістю, а зараз у наш час телебачення, інтернету та комп&rsquo;ютерних ігор молодь взагалі забуває про поезію, тому важливо, щоб такі вечори були непоодинокими.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Матеріал та Фото Ярослава Павлюка<br /></span></span></p>',5,1,3375);
INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1893,'2009-12-01 01:48:42',1,'P','Блаженнійший Любомир Гузар: про єдність і гідність','<p style=\"margin: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><img style=\"border: 1px solid black; margin: 5px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_huzar_dt.jpg.jpg\" alt=\"\" width=\"114\" height=\"101\" /></strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ми в дорозі &ndash; тяжкій і повільній з причин як зовнішніх, так і внутрішніх. Внутрішню ситуацію ускладнює, зокрема, те, що наша церква не є виключно на території України, а розкинута по всьому світу, і це не тільки люди, а й цілі церковні структури &ndash; в обох Америках, Західній Європі, Австралії. Зараз перед нами стоїть завдання забезпечити насамперед внутрішню єдність тих різних частин в Україні і поза її межами...<br /></span></span></p>','<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Історія церкви в незалежній Україні дала безліч уроків. Один із найважливіших &ndash; пряма залежність між можливістю єдності та свободою. Чим сильніший тиск на церкву з боку політичних сил, тим більша прірва між ними. І навпаки: чим менше ідеологічної мішури, тим простіше вести діалог.</em></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Найближчим часом, швидше за все, маятник міжцерковних відносин знову хитнеться у бік нетерпимості. Вибори. Той факт, що багато кандидатів отримали благословення кого тільки могли, не заважає їм розігрувати церковну карту як інтереси велять. Невпевнений діалог, що зародився останніми місяцями, впаде в анабіоз, перш ніж новий президент укорениться у своєму кріслі. А там видно буде.</em></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Мислення категоріями &laquo;канонічних територій&raquo; і конфесійної приналежності виявилося дуже зручним для маніпулювання. За цими поняттями легко загубити суть як християнської, так і просто людської єдності. Тому ними так захоплено граються ті, для кого розділення &ndash; запорука ідеологічного успіху.</em></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>До предстоятеля Української греко-католицької церкви Блаженнішого Любомира мене привело якраз питання суті єдності. Адже саме він кілька років тому виступив із грандіозною ідеєю неконфесійної, або, згідно з нинішньою гуманітарною модою, &ndash; &laquo;постконфесійної&raquo; єдності.</em></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><img style=\"border: 1px solid black; margin: 5px; width: 220px; height: 191px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_huzar_dt.jpg.jpg\" alt=\"\" />Владико, питання української помісної церкви хоч і не нове, проте не втрачає привабливості. Певний час змагання за цю роль розгорталося тільки між православними церквами, але кілька років тому Українська греко-католицька церква заявила про себе як про церкву &laquo;Київську&raquo; і про намір здобути патріарший статус. Символом того став початок будівництва патріаршого собору в Києві. Яка перспектива у проекту &laquo;Патріархат&raquo; тепер, коли перша хвиля захоплення ідеєю минула?</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ми в дорозі &ndash; тяжкій і повільній з причин як зовнішніх, так і внутрішніх. Внутрішню ситуацію ускладнює, зокрема, те, що наша церква не є виключно на території України, а розкинута по всьому світу, і це не тільки люди, а й цілі церковні структури &ndash; в обох Америках, Західній Європі, Австралії. Зараз перед нами стоїть завдання забезпечити насамперед внутрішню єдність тих різних частин в Україні і поза її межами. Це дуже серйозна проблема, бо йдеться про внутрішню єдність церкви. Не суто адміністративно чи структурно &ndash; на рівні наставлення: ми в Україні мусимо відчувати, що ті, хто поза межами країни, &ndash; це наші брати й сестри і ми їх потребуємо, а вони також щоби зрозуміли, що для них внутрішня єдність з Україною є надзвичайно важливою. Бути патріархатом &ndash; це нести спільну відповідальність за всіх, де б вони не були. Ми призвичаюємося, вдумуємося, що це означає &ndash; бути патріархатом.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Тобто ви ще не вирішили, що це означає?</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ідея патріархату для УГКЦ має довгу історію, &ndash; вона насправді виникла не вчора, а сягає XVII століття. Цілком природна думка, питома для тої традиції, до якої ми належимо, тому ця ідея час від часу виринала в різні моменти нашої історії останніх триста років. Але від 60-х років минулого століття вона стала для нас першорядною, збудивши велике зацікавлення і спричинивши багато суперечностей. Річ у тому, що відтоді патріархат перестав бути суто теоретичною конструкцією. Ми мусили зрозуміти, що це не просто традиція східної церкви, якою ми є. Ми мусили зрозуміти суть &ndash; що то є бути патріархатом, думати по-патріаршому. Тобто постало багато проблем практичного порядку. Коли почалися змагання за патріарший устрій для УГКЦ у 60-х роках, церква в Україні мовчала, бо не могла говорити. Все відбувалося поза межами України &ndash; головно у Північній Америці і трохи в Європі. Ідея патріархату тільки нещодавно стала частиною життя нашої церкви і тут, в Україні. Але коли вона сюди прийшла, збудила багато розмов без розуміння того, що все це означає.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Які є зовнішні труднощі на шляху до патріархату?</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Річ у тому, що, за законами католицької церкви, патріархати встановлює або Вселенський собор, або, якщо він не збирається, від імені церкви Папа Римський. На жаль, Другий Ватиканський собор, на якому заходила мова про статус Української греко-католицької церкви, не виробив відповідної процедури. Питання зависло у повітрі. Нове законодавство &ndash; приблизно 20 років, відколи з&rsquo;явився новий кодекс церковного права, &ndash; також не вносить ясності в це питання. Він підтверджує джерело рішення &ndash; Вселенський собор або Папа, &ndash; але питання про те, коли, як, чому цей статус дається, залишилися відкритими.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Дуже схожа проблема у православних братів, які також не можуть отримати автокефалії і навіть автономії, тому що порядок надання цього статусу залишається спірним і не до кінця зрозумілим. Утім, гадаю, &laquo;зовнішні труднощі&raquo; УГКЦ, як і у випадку з православними церквами України, не вичерпуються канонічним правом.</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Роль міжцерковних стосунків у тих рішеннях не є таємницею. Московський патріархат неодноразово і в різний спосіб висловлював свої протести щодо становлення патріархату Української греко-католицької церкви. Але треба зазначити, що поява УГКЦ з патріаршим устроєм була би неприємною не тільки для Московського патріархату. Це підкреслило би самобутність нашої церкви, в той час як чомусь є страх, що коли ми будемо такі самобутні, то зможемо чи схочемо відірватися від всіх і вся. Можливо, що такі побоювання стримують Ватикан. Латинська ментальність спрямована на централізацію. На відміну від східної традиції, для якої патріарший устрій і соборність є природнім способом існування.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Тобто сам принцип побудови католицької церкви заважає Ватикану до кінця зрозуміти внутрішню потребу церкви, котра становить досить велику її частину? Тоді чи є у вас шанси знайти там розуміння?</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Не тільки ми є кандидатами на патріарший устрій у структурі католицької церкви. Дуже близька до того Сиро-Малабарська церква (одна зі східнокатолицьких церков зі статусом Верховної Архиєпископії, має найбільше вірних у Індії та індійській діаспорі у світі. &ndash; <strong>К.Щ.</strong>), яка і чисельно, і структурно є дуже поважною. Вони щороку висвячують близько 250 священиків. Тобто це велика церква. Мабуть, більша, ніж наша. І вони, і ми маємо все необхідне, щоби отримати статус патріархату. Однак що робити з системою централізованої інституції, її побоюванням, що прагнення її частини бути собою обернеться від&rsquo;єднанням, унезалежненням; страхом, що якщо хтось вийде з-під жорсткої руки, то відразу втече подалі? А тут ще зовнішні впливи &ndash; Московський патріархат та інші православні церкви, які не дуже радо бачили би патріарший устрій у греко-католиків.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Чому?</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Бо вони не знають, що з нами робити, як до нас ставитися. Бо ми є для них парадоксом. Дивовижею. Чимось неприродним. Це ставлення має історико-богословське підґрунтя. Найбільш занепокоєний, звичайно, Московський патріархат, бо все це відбувається на території, яку вони хотіли би бачити тільки своєю. Ось вони і вживають заходів, щоби знеохотити Ватикан. Це триває вже сотні років.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Що дасть статус патріархату УГКЦ? Чи не є це просто відповідь на соціально-політичний запит на &laquo;київську помісну&raquo; церкву і бажання нації, котра отримала нещодавно незалежність, унезалежнюватися в усьому і від всіх?</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Все навпаки. Спільнота може складатися тільки з людей, які свідомі своєї ідентичності. Ми маємо стати собою, усвідомити, хто ми, які і звідки. Тільки тоді ми можемо стати всім довкола приятелями та співрозмовниками. Якщо ми є безликою групою безликих &ndash; ми не можемо бути приятелями. Ми не можемо будувати єдність. У деяких представників Вселенської церкви, які дуже схвильовані питанням єдності, є великі труднощі з розумінням того, на чому будується ця єдність. Ми бачимо єдність як співпричастя. Усвідомлення своєї історії, своїх особливостей, пов&rsquo;язаних із тим, що ми виросли на цій землі, &ndash; для нас не виглядає як підстава для відчуження. Радше, навпаки. Будучи свідомими духовно, богословськи, церковноправно, культурно &ndash; ми стаємо сильнішими партнерами. Бо помісна церква є втіленням Христової церкви у даній місцевості чи культурній групі. Тоді вона, окрема й самобутня, у спілкуванні з іншими становить Вселенську церкву. Це дуже важко зрозуміти представникам системи, яка є централістична. Декому важко пробитися до того розуміння, що людина задоволена не тоді, коли вона далека від усіх, а тоді, коли вона спілкується з іншими &mdash; не як залежна, а як свобідна людина. Патріархат &ndash; духовна автономія, яка дає можливість спілкуватися більш глибоко, з більшою довірою.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Здається, проблема не тільки в &laquo;нерозумінні&raquo; з боку Ватикану. Всередині УГКЦ немає одностайності в питанні необхідності патріархату. І найчастіше це пов&rsquo;язано з сумнівами вірних у здатності своїх українських владик керувати церквою. Ватикану довіряють більше.</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У нас в Україні справді багато людей, які думають, що наше спасіння тільки в єдності з кимсь. Люди не довіряють собі, не розуміють своєї гідності. Вони не розуміють, що бути собою &ndash; не означає стати чиїмось ворогом. Ми мусимо здійснювати свою гідність, яку дав нам Бог, у спілкуванні з іншими. Але це має бути спілкування свобідних і рівних, &ndash; тільки тоді наша гідність реалізується. І в Україні є люди, котрі не розуміють, що неможлива повноцінна єдність в обставинах неусвідомлення себе. Вони воліють бути з Польщею чи з Росією. В нашій ментальності є трошки комплексу меншовартості. У нас не розвинене відчуття не тільки політичної, а й просто людської гідності, якщо ми думаємо, що можемо існувати тільки як частина когось іншого. Ця трудність позначається й на житті церкви.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Зараз спостерігається потепління у відносинах між Московським патріархатом і Ватиканом. Як це може позначитися на Українській греко-католицькій церкві?</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Мабуть, ніяк. Ватикан не хотів би, щоби ми були перешкодою у відносинах із Московським патріархатом. А з боку керівництва РПЦ я не бачу ані найменшої зміни у ставленні до нас. Все так, як і було. Повторюються одні й ті самі слова. Я не впевнений навіть у тому, що відбувається якесь потепління. Якщо є щире бажання зближення, спілкування, тоді не ставлять умов &ndash; а зустрічаються і спілкуються. А якщо ви починаєте ставити умови &laquo;зроби те і те, тоді будемо тебе любити&raquo;, &ndash; то це відсутність щирого бажання спілкуватися, відсутність справжньої любові. Різниця між політикою колишнього і нинішнього патріархів Московських тільки у формі &ndash; багато галасу, але нічого нового за суттю не відбувається.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Чи були якісь спроби спілкування безпосередньо з УГКЦ з боку керівництва РПЦ?</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ні. Для них ми залишаємося просто перешкодою, трудністю. У мене були маленькі зустрічі з росіянами, і я зробив висновок: вони впевнені, що якщо ти росіянин, мусиш бути православний. І навпаки &ndash; якщо ти православний, тоді ти справді росіянин, або, в нашому випадку, &ndash; українець. А якщо ти православний, то ти не є католик. Усе доволі просто. Для них католик &ndash; це латинник, латинської традиції. Тобто &laquo;не наше&raquo;. Це не правильно. І ми своїм існуванням ці прості постулати заперечуємо. Ми, бачите, і православні за традицією, і католики, об&rsquo;єднані з Апостольською столицею у Римі. Як казав Папа Римський під час святкування 1000-ліття Хрещення Київської Русі, &laquo;православна віра, католицька любов&raquo;. Православними ми є у вірі, католиками &ndash; у любові, в єдності.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>УГКЦ вважають &laquo;церквою Галичини&raquo;. Чи може бути статус патріархату в регіональної церкви? Адже помісна церква повинна охоплювати всю країну.</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Наш ідеал &ndash; одна помісна церква і один патріарх, який засяде у Святій Софії в Києві. Ми хочемо повернутися до цього. Я про це казав і тут, і в Римі: коли ми говоримо про патріарший устрій для греко-католиків, ми не хочемо, щоби він став формою відділення нас від православних братів. Для нас патріархат &ndash; не завершення. Для нас це засіб для досягнення більш серйозної мети &ndash; єдності Київської церкви, яка була на початку. Патріархат &ndash; це шлях, а не вінець мрій. Ми будуємо Патріарший собор у Києві. Нам кажуть: от, ви будуєте собор на лівому березі на противагу Софійському на правому. Ні. Ми не будуємо свій собор як якийсь альтернативний християнський центр. Тільки як тимчасовий. До того часу, доки єдиний патріарх не засяде у Святій Софії.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>На цих твердженнях ґрунтуються звинувачення на вашу адресу в тому, що ви хочете приєднати всіх українських християн до Католицької церкви.</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я хочу, щоби церква була єдиною. Це не означає, що всі мають стати католиками чи пристати до нас. Я хочу, щоби всі були у Христі одне. Коли патріарх Кирил хоче спілкуватися з патріархом Заходу &ndash; Папою, &ndash; це означає, що він збирається &laquo;католичитися&raquo;? Ні, це означає, що вони хочуть бути разом у Христі. Принаймні я на те сподіваюся. В цьому суть екуменізму &ndash; бути одним. Єдність не може бути інакшою, як спілкування різних і рівних. Це є ідеал церкви. Христос говорив арамейською мовою, але він не наполягав, щоби всі говорили арамейською. Нас об&rsquo;єднує Христос, а не якісь зовнішні речі.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Ходять чутки, що ви подали до Ватикану прохання про відставку, &ndash; це правда?</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ні, я не подавав у відставку. Чутки, мабуть, пов&rsquo;язані з тим, що, коли мені минуло 75 років, я звернувся до своїх братів &ndash; єпископів нашого Синоду &ndash; і до Папи Римського з пропозицією подбати про майбутнє. Я вже не є молодий і не стану ані молодшим, ані сильнішим, ані здоровішим. Треба шукати нові сили. Ніхто не зможе і не стане, я гадаю, примушувати мене залишити посаду Верховного архієпископа, і я можу залишатися на цій посаді пожиттєво. А можу у слушний момент відійти й дати церкві нагоду розвиватися далі під проводом когось іншого. Але я не вважаю, що треба тягти до останнього моменту. Людина старша має бути вшанована, вона має великий досвід, але не має того розмаху, тієї сили, щоби церкву провадити. Це природна річ, і я того свідомий. Я кажу відверто &ndash; подумаймо про майбутнє. Як звичайно відбувається перехід? Хтось помер, і тоді швиденько шукаємо наступника. Але краще зробити інакше &ndash; у певний час передати справи церкви з рук до рук своєму братові.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Ви вже визначилися, хто це буде?</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Шукаємо. Бачте, відтоді, як я про цей намір проголосив, минуло вже два роки. Я дотримуюся тої засади, що ніхто у світі не є незамінний. І що ідеального голови церкви ми не знайдемо. Тільки Господь Бог є ідеальним.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Яких змін можна очікувати в УГКЦ у зв&rsquo;язку з близькими виборами? Хоч би хто їх виграв, ситуація в країні, швидше за все, стане трохи інакшою.</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церква є церквою і повинна зберігати свою гідність, незалежно від того, хто є при владі. Держава повинна, зі свого боку, поважати церкву, а церква &ndash; допомагати державі в тих питаннях, де сфери їх піклування про суспільство перетинаються. Це в ідеалі. Але ідеалу, ще раз кажу, на землі не знайти. Кожна держава хоче контролювати церкву &ndash; не тільки в нас, у будь-якій країні це так. Більш чи менш видимо, більш чи менш інтенсивно. Такою є сама природа держави: це інституція, яка не любить мати свобідних елементів, любить контролювати все. А церква, за своєю природою, якщо вона хоче залишатися церквою, мусить бути свобідною, не підкорятися, але співдіяти у добрих справах. Тому, хоч би які були політичні зміни, церква завжди намагатиметься зберегти свою свободу. Ми, різні християнські церкви і спільноти в Україні, мусимо бути супроти держави &ndash; вільні, рівні й не використовувані для цілей суто політичних.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Ваша позиція в питанні &laquo;церква і вибори&raquo; відома: ви завжди були противником використання церкви задля підтримки тієї чи іншої особи. Втім, цього разу, за чутками, на підтримку з боку УГКЦ сподівається Арсеній Яценюк. Начебто він домовився про це з вами.</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Так, я мав зустріч із паном Яценюком. Але ніякої підтримки на виборах із боку греко-католицької церкви йому не обіцяв. Так само, як будь-кому іншому, &ndash; багато хто хотів би мати нас за собою. Бачите, тепер є така мода: всі кандидати хочуть мати коло себе якусь духовну особу для прикраси. І чим більше прикрас, тим краще. Це не є пошанівок для церкви. Це використовування церкви для своїх політичних цілей. Пошанівок для церкви &ndash; у щоденному житті, коли держава поважає церкву, а церква служить народові. Ми не віддаляємося від народу і свідомі своїх завдань у суспільстві.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Втім, позиція про &laquo;непідтримку&raquo; &ndash; декларативна. Адже важко простежити, що відбувається в кожній парафії і які політінформації проводять священики під час чи після літургії.</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ні, прослідкувати неможливо, &ndash; в тому наша трудність. Але тут є ще ось який момент: що ви назвете &laquo;політінформаціями&raquo;? Ми не сміємо мовчати. Ні, ми не збираємося говорити, що треба голосувати за того чи за того кандидата. В жодному разі. Але ми повинні чітко сказати &ndash; і парафіянин хоче й має право почути це в церкві, &ndash; на що потрібно звертати увагу у своєму виборі. Церква мусить давати орієнтири. Скажімо, ми хочемо бачити владу, яка виявляє щиру любов до України. Ми хочемо бачити президентом людину, котра поводилася чесно, котра здобула авторитет політика, який дотримується слова перед народом, перед своїми виборцями. Ми мусимо про це говорити. Але не на користь того чи іншого кандидата. Під цей критерій може підійти один, три чи тридцять кандидатів. Нехай серед них і вибирають &ndash; але вже самі. Використання церкви у виборчих перегонах &ndash; це двосічний меч. Завжди, коли церква піддається спокусі пристати до якогось визначеного кандидата, &ndash; вона на тому програє. Не тільки в нашій країні &ndash; повсюди. І добре, що програє, &ndash; бо це вчить. Ми не є ані ідеологами, ані суддями, проте наше завдання сказати: вважайте, люди, не продавайте своїх голосів і своїх душ.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Виборець &ndash; відповідальний за те, що обрана людина робитиме на своїй посаді. Це питання етики, і воно особливо нелегке зараз, коли попереду в нас вибори майже без вибору.</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Історія повна складних моментів. І вона нас навчила розуміння, що жодна ситуація не є безвихідною й настільки трагічною, що залишається лише впасти у відчай. Так, сьогодні ми перебуваємо в такому собі глухому куті. Але ж це не є щось нечуване в історії &ndash; чи нашій, чи інших країн. В такі моменти питання вибору особливо складне. Але ми не є повністю позбавлені сили вибору. Треба шукати найкращий вибір, який є можливим. Добрий час, щоби визначити, що є для нас найважливішим. Ми свідомі того, що переживаємо не найкращі часи, і тому Всеукраїнська рада церков звертається до народу із закликом молитися. Молитва має велику силу. Молитися не за таку чи таку розв&rsquo;язку. Не мета молитви &ndash; радити Богові, як воно буде краще. Не думаю, що Бог потребує радників. У своїй молитві ми мусимо поручити Богові свою ситуацію, &ndash; він бачить вихід із нашого глухого кута краще за нас. Він уміє розв&rsquo;язувати будь-що. Це надзвичайно важливо &ndash; свідомо залишити вибір Богові. Воно може звучати не по-земному. Воно і не є цілком по-земному, але що з того, якщо ми віримо в Бога, віримо, що він є володар історії. Навіть якщо прийде до влади неприємна нам людина, за котру б ми не голосували, &ndash; мусимо подумати: якщо так дав Бог, то це є вихід. Може, не такий, який ми вважали найкращим. Утім, це не заклик згорнути руки й чекати, коли все само собою станеться як слід. Ми поручаємо Богові наше майбутнє, нашу долю, але ми люди, отже &ndash; мусимо діяти. Є така англійська приповідка: працюй так, ніби все залежить від тебе, а молися так, ніби все залежить від Бога. То є містерія &ndash; люди роблять свої кроки, а за ними стоїть Бог.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Розмовляла Катерина Щоткіна<br /><a href=\"http://www.dt.ua/3000/3690/67613/\" target=\"_blank\">№ 42 (770) 31 жовтня &ndash; 6 листопада 2009</a></em></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">http://www.dt.ua/<br /></span></span></p>',5,1,3376);
INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(1894,'2009-12-01 18:33:31',1,'P','Допомагаємо покинутій дитині','<p style=\"text-align: justify; text-indent: 14.2pt;\">&nbsp;   <span>В рамках проекту    <em>&laquo;Допомога дітям ув&rsquo;язнених матерів&raquo;</em></span> Церковною службою милосердя   <span>&raquo;Карітас-Печерськ&raquo; 23 листопада були надіслані 25 гуманітарних пакетів з допомогою для 38 потребуючих дітей. Подарунки для дітей були придбані за кошти небайдужих людей:</span></p>\r\n<p style=\"margin-left: 21pt; text-align: justify; text-indent: -18pt;\"><span> <span>-     <span style=\"font-family: &quot;Times New Roman&quot;; font-style: normal; font-variant: normal; font-weight: normal; font-size: 7pt; line-height: normal; font-size-adjust: none; font-stretch: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span> </span> прихожанами церкви Святого Миколая п. Галиною і п. Станіславом - пожертва гігієнічними товарами (шампунь, мило, зубна паста, дезидорант)</p>\r\n<p style=\"margin-left: 21pt; text-align: justify; text-indent: -18pt;\"><span> <span>-     <span style=\"font-family: &quot;Times New Roman&quot;; font-style: normal; font-variant: normal; font-weight: normal; font-size: 7pt; line-height: normal; font-size-adjust: none; font-stretch: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span> </span> президентом фірми &laquo;Крафт&raquo; п. Юрієм Логушем - допомога солодощами (цукерки)</p>','<p style=\"text-align: justify; text-indent: 14.2pt;\">&nbsp;   <span>В рамках проекту    <em>&laquo;Допомога дітям ув&rsquo;язнених матерів&raquo;</em></span> Церковною службою милосердя   <span>&raquo;Карітас-Печерськ&raquo; 23 листопада були надіслані 25 гуманітарних пакетів з допомогою для 38 потребуючих дітей. Подарунки для дітей були придбані за кошти небайдужих людей:</span></p>\r\n<p style=\"margin-left: 21pt; text-align: justify; text-indent: -18pt;\"><span> <span>-     <span style=\"font-family: &quot;Times New Roman&quot;; font-style: normal; font-variant: normal; font-weight: normal; font-size: 7pt; line-height: normal; font-size-adjust: none; font-stretch: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span> </span> прихожанами церкви Святого Миколая п. Галиною і п. Станіславом - пожертва гігієнічними товарами (шампунь, мило, зубна паста, дезидорант)</p>\r\n<p style=\"margin-left: 21pt; text-align: justify; text-indent: -18pt;\"><span> <span>-     <span style=\"font-family: &quot;Times New Roman&quot;; font-style: normal; font-variant: normal; font-weight: normal; font-size: 7pt; line-height: normal; font-size-adjust: none; font-stretch: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span> </span> президентом фірми &laquo;Крафт&raquo; п. Юрієм Логушем - допомога солодощами (цукерки)</p>\r\n<p style=\"margin-left: 21pt; text-align: justify; text-indent: -18pt;\"><span> <span>-     <span style=\"font-family: &quot;Times New Roman&quot;; font-style: normal; font-variant: normal; font-weight: normal; font-size: 7pt; line-height: normal; font-size-adjust: none; font-stretch: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span> </span> парафією святого Дженуарія, м. Беневенто, священиком о. Паскуалем перераховано кошти в    <span>на магазин</span> &laquo;Метро&raquo; для придбання товарів: продуктів харчування і гігієнічних засобів</p>\r\n<p style=\"margin-left: 21pt; text-align: justify; text-indent: -18pt;\"><span> <span>-     <span style=\"font-family: &quot;Times New Roman&quot;; font-style: normal; font-variant: normal; font-weight: normal; font-size: 7pt; line-height: normal; font-size-adjust: none; font-stretch: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span> </span> церквою святого Миколая - допомога найбільш потребуючим, особливо хворим дітям та дітям, позбавленим батьківської опіки, а також жінкам з новонародженими дітьми, які утримуються під наглядом.</p>\r\n<p style=\"margin-left: 18pt; text-align: justify;\"><span>Цих 25 посилок надіслані через Укрпошту. Поштові витрати:пересилка 701гр. (+ящики 8.50*25=212.50) склали 914.50 &nbsp;грн і були пожертвувані паном Романом.</span></p>\r\n<p style=\"margin-left: 18pt; text-align: justify;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n<table style=\"margin-left: 2.75pt; border-collapse: collapse;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<thead> \r\n<tr>\r\n<td style=\"padding: 2.75pt; width: 39pt;\" width=\"52\" valign=\"top\">\r\n<p><span style=\"font-size: 10pt;\">№</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 186.05pt;\" width=\"248\" valign=\"top\">\r\n<p><span style=\"font-size: 10pt;\">Назва закладу</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 191.15pt;\" width=\"255\" valign=\"top\">\r\n<p><span style=\"font-size: 10pt;\">Надана допомога</span></p>\r\n</td>\r\n<td style=\"border: 1pt solid black; padding: 2.75pt; width: 65.65pt;\" width=\"88\" valign=\"top\">\r\n<p><span style=\"font-size: 10pt;\">Сума</span></p>\r\n</td>\r\n</tr>\r\n</thead> \r\n<tbody>\r\n<tr>\r\n<td style=\"padding: 2.75pt; width: 39pt;\" width=\"52\" valign=\"top\">\r\n<p style=\"margin-left: 36pt; text-indent: -18pt;\"><span style=\"font-size: 10pt;\"> <span>1.             <span style=\"font-family: &quot;Times New Roman&quot;; font-style: normal; font-variant: normal; font-weight: normal; font-size: 7pt; line-height: normal; font-size-adjust: none; font-stretch: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;</span></span> </span> &nbsp;</p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 186.05pt;\" width=\"248\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Будинок дитини №1,&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">м. Луганськ</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Епецахі Ніні 2001р.н.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 191.15pt;\" width=\"255\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Портфель, цукерки, вафлі, зош</span> <span style=\"font-size: 10pt;\">ити, записники, мило, шампунь, зубна щітка, зубна паста.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 65.65pt;\" width=\"88\">\r\n<p style=\"text-align: center;\" align=\"center\"><span style=\"font-size: 10pt;\">84,69</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 2.75pt; width: 39pt;\" width=\"52\" valign=\"top\">\r\n<p style=\"margin-left: 36pt; text-indent: -18pt;\">&nbsp;2.</p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 186.05pt;\" width=\"248\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Будинок дитини &laquo;Вишенька&raquo;, Львівська обл.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Козловському Віктору -2006 р.н.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Козловському Олексію &ndash; 2003 р.н.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 191.15pt;\" width=\"255\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Солодощі, цукерки 2 пачки, вафлі 6 пачок, портфель 1, зошити, ручки, щоденник, олівці кольорові.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 65.65pt;\" width=\"88\">\r\n<p style=\"text-align: center;\" align=\"center\"><span style=\"font-size: 10pt;\">103,33</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 2.75pt; width: 39pt;\" width=\"52\" valign=\"top\">\r\n<p style=\"margin-left: 36pt; text-indent: -18pt;\">&nbsp;3.</p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 186.05pt;\" width=\"248\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Школа-інтернат, вул. Шлях Леніна, 69, Сахновщина, Харківська обл.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Романів Елісею 1997р.н.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 191.15pt;\" width=\"255\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Цукерки, вафлі, портфель, щоденник, олівці, мило, ручки, записники, шампунь.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 65.65pt;\" width=\"88\" valign=\"top\">\r\n<p style=\"text-align: center;\" align=\"center\"><span style=\"font-size: 10pt;\">112,71</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 2.75pt; width: 39pt;\" width=\"52\" valign=\"top\">\r\n<p style=\"margin-left: 36pt; text-indent: -18pt;\">&nbsp;4.</p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 186.05pt;\" width=\"248\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Школа-інтернат, м. Тлумач, Івано-Франківська обл.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Яворському Сергію &ndash; 1997 р.н.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Яворському Юрію &ndash; 1999 р.н.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 191.15pt;\" width=\"255\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Цукерки, вафлі, портфелі 2, зошити, ручки, щоденник 2, олівці кольорові, шампунь, зубна щітка, зубна паста.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 65.65pt;\" width=\"88\">\r\n<p style=\"text-align: center;\" align=\"center\"><span style=\"font-size: 10pt;\">124,69</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 2.75pt; width: 39pt;\" width=\"52\" valign=\"top\">\r\n<p style=\"margin-left: 36pt; text-indent: -18pt;\">&nbsp;5.</p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 186.05pt;\" width=\"248\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Школа-інтернат, с. Угорники, Івано-Франківська обл.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Козінчук Івану &ndash; 1994 р.н.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Яворському Василію &ndash; 2001 р.н.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Яворському Андрію &ndash; 2005 р.н.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 191.15pt;\" width=\"255\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Цукерки, вафлі, портфелі 2, зошити, ручки, щоденник 2, олівці кольорові, шампунь, зубна щітка, зубна паста, пластилін, духовна література.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 65.65pt;\" width=\"88\">\r\n<p style=\"text-align: center;\" align=\"center\"><span style=\"font-size: 10pt;\">114,69</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 2.75pt; width: 39pt;\" width=\"52\" valign=\"top\">\r\n<p style=\"margin-left: 36pt; text-indent: -18pt;\">&nbsp;6.</p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 186.05pt;\" width=\"248\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Опікун &ndash; Даниленко Любов</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">для дітей &ndash; Даниленко Олексію 1997 р.н., Даниленко Дмитро &ndash; 2003 р.н.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 191.15pt;\" width=\"255\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Цукерки, вафлі, портфелі 2, зошити, ручки, щоденник 2, олівці кольорові, шампунь, зубна щітка, зубна паста.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 65.65pt;\" width=\"88\">\r\n<p style=\"text-align: center;\" align=\"center\"><span style=\"font-size: 10pt;\">124,05</span></p>\r\n</td>\r\n</tr>\r\n<tr style=\"height: 30.55pt;\">\r\n<td style=\"padding: 2.75pt; width: 39pt; height: 30.55pt;\" width=\"52\" valign=\"top\">\r\n<p style=\"margin-left: 36pt; text-indent: -18pt;\">7.</p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 186.05pt; height: 30.55pt;\" width=\"248\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Опікун &ndash; Беспалько Євдокія,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">с. Покровське, Дніпропетр. обл.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Свідерському Ігорю &ndash; 2006 р.н.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 191.15pt; height: 30.55pt;\" width=\"255\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Портфель, цукерки, вафлі, зошити,&nbsp;мило, шампунь, зубна щітка, зубна паста, одяг б/у.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 65.65pt; height: 30.55pt;\" width=\"88\">\r\n<p style=\"text-align: center;\" align=\"center\"><span style=\"font-size: 10pt;\">80,61</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 2.75pt; width: 39pt;\" width=\"52\" valign=\"top\">\r\n<p style=\"margin-left: 36pt; text-indent: -18pt;\">8.</p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 186.05pt;\" width=\"248\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Опікун&nbsp;Красуля Катерина, м. Кривий Ріг</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Івановій Дарії &ndash; 1994 р.н.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Іванову Давиду &ndash; 1995 р.н.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Іванову Данилу &ndash; 1999 р.н.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 191.15pt;\" width=\"255\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Цукерки, вафлі, портфелі, зошити, ручки, акварельні фарби, олівці кольорові, шампунь, зубна щітка, зубна паста, записники, одяг б/у.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 65.65pt;\" width=\"88\">\r\n<p style=\"text-align: center;\" align=\"center\"><span style=\"font-size: 10pt;\">131,89</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 2.75pt; width: 39pt;\" width=\"52\" valign=\"top\">\r\n<p style=\"margin-left: 36pt; text-indent: -18pt;\">9.</p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 186.05pt;\" width=\"248\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Опікун &ndash; Безручко Олена,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">м. Первомайськ, Луганська обл.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">дітям Баскатової &ndash; 1999, 2000 р.н.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 191.15pt;\" width=\"255\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Цукерки, вафлі, зошити, записники, щоденники, мило, шампунь, зубна щітка, зубна паста, одяг б/у.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 65.65pt;\" width=\"88\">\r\n<p style=\"text-align: center;\" align=\"center\"><span style=\"font-size: 10pt;\">96,14</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 2.75pt; width: 39pt;\" width=\"52\" valign=\"top\">\r\n<p style=\"margin-left: 36pt; text-indent: -18pt;\">10.</p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 186.05pt;\" width=\"248\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Опікун &ndash; Куницька Олена,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">м. Надвірна, Івано-Фран. обл.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Козінчук Миколі &ndash; 2001 р.н.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 191.15pt;\" width=\"255\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Цукерки, вафлі, записники, щоден-</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">ники, мило, шампунь, зубна щітка, зубна паста, духовна&nbsp;література, олівці.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 65.65pt;\" width=\"88\">\r\n<p style=\"text-align: center;\" align=\"center\"><span style=\"font-size: 10pt;\">93,84</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 2.75pt; width: 39pt;\" width=\"52\" valign=\"top\">\r\n<p style=\"margin-left: 36pt; text-indent: -18pt;\">&nbsp;11.</p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 186.05pt;\" width=\"248\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Опікун &ndash; Дьокіна Людмила</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">м. Загрес, Донецька обл.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Хлудеєву Івану &ndash; 2001р.н.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 191.15pt;\" width=\"255\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Цукерки, портфель, записники, зошити, шампунь, ручки, олівці, зубна паста, зубна щітка.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 65.65pt;\" width=\"88\">\r\n<p style=\"text-align: center;\" align=\"center\"><span style=\"font-size: 10pt;\">93,84</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 2.75pt; width: 39pt;\" width=\"52\" valign=\"top\">\r\n<p style=\"margin-left: 36pt; text-indent: -18pt;\">&nbsp;12.</p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 186.05pt;\" width=\"248\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Опікун &ndash; Котчик Сергій</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Донецьк -38