INSERT INTO `publications_publicationitem` (`id`, `publication_date`, `show_date`, `state`, `title`, `short_text`, `text`, `author_id`, `locale_id`, `publication_id`) VALUES
(2656,'2012-01-29 23:23:50',1,'P','Вони назавше залишаться юними','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4138,200,CRW_4474.JPG\" />Юність – роки становлення особистості, юність – роки безтурботного переходу від дитинства до зрілості. Саме так сьогодні ми сприймаємо ЮНІСТЬ.</p>\n<p>Як сприймали свою юність студенти, які дрібкою солі стали на захист своєї держави проти безжалісної навали? Тоді вони не знали і не прагнули через століття стати героями. Тоді, вони добре усвідомлювали, що йдуть на вірну смерть. Та їхня любов до брата свого, до народу свого, до держави своєї була вище над усе, вища за смерть – «НАЙБІЛЬША ЛЮБОВ – ЖИТТЯ ПОКЛАСТИ ЗА ДРУЗІВ».<br />Через століття для кожного українця 33 юнака – ГЕРОЇ.</p>\n','',NULL,1,4139),
(2657,'2012-01-30 21:50:33',1,'P','Відпочинок на лижах або подорож у Мигово','<p><img style=\"margin: 10px; float: left;\" src=\"http://oranta.org/image,4139,200,DSCN4499.JPG\" /><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4139,200,DSCN4499.JPG\" />26 січня.</p>\n<p>Цей день з нетерпінням чекало 14 дітей з церкви святого Миколая, що на Аскольдовій Могилі. Адже сьогодні з Київського вокзалу вони разом з трьома вихователями та священиком Анатолієм Теслею відправляється потяг Київ-Чернівці до&nbsp; туристичного комплексу Мигово. Весела метушня в поїзді так втомила дітей, що вони поринули у сон.<br /><br /></p>\n','',NULL,1,4140),
(2658,'2012-01-31 00:01:07',1,'P','І по той бік паркану є життя','<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4140,200,CRW_4667.JPG\" />Парафіяльна служба милосердя «Карітас-Печерськ» церкви св. Миколая Чудотворця на Аскольдовій Могилі продовжує свою опіку над жінками, які перебувають у закладах позбавлення волі. Цього разу ми провідали жінок в уманській виправної колонії №129. Нас було п’ятеро: отець Ігор Онишкевич, настоятель храму, поет і співак Зеновій Филипчук, волонтери пані Ганна Мушинська, що опікується разом з пані Маркою Бліхар жінками в неволі, Любов Михайлюк, та Орина Маслова.</p>\n','',NULL,1,4141),
