INSERT INTO `pub_items` VALUES
(1,NULL,'<ul>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Пораненим бійцям АТО до військового шпиталю в м.Києві:</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;светри теплі &ndash; 10 шт.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Передали для населення в зону АТО через волонтерів:</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг теплий дитячий &ndash; 5 коробок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг жіночий, чоловічий &ndash; 3 кор.+6 міш.</span></p>','2015-02-07 21:33:28',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Служба милосердя &laquo;Карітас&raquo; при церкві св.Миколая Чудотворця на Аскольдовій могилі, зібравши пожертвувані парафіянами одяг чоловічий, жіночий та дитячий, взуття, іграшки дитячі та отримавши &nbsp;гуманітарну допомогу одягом від благодійного фонду &laquo;Міжнародна асоціація підтримки України (МАПУ)&raquo;, (голова п.Ольга Чуйко-Дурбак), розподілила їх таким чином (див. записи в книзі розподілу гуманітарної допомоги):</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Уділили потребуючим мирянам:</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг різний &ndash; 70 кг</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;куртки та пальта жіночі й чоловічі &ndash; 54 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;сервіз чайний на 6 персон &ndash; 1 компл.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; тарілки пластикові &ndash; 6 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; рюкзак шкільний &ndash; 1 шт.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Надана допомога одягом та взуттям:</span></li>\r\n</ol>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">переселенцям з Донецької та Луганської обл.</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг &ndash; 15 кг.</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">багатодітній родині (6 дітей) з с.Угнів Львівської обл.</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;куртки дитячі &ndash; 4 шт.</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">підліткам Лук&rsquo;янівського СІЗО в м.Києві:</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; светри теплі &ndash; 17 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;джинси &ndash; 5 пар, шкарпетки</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">пораненим бійцям АТО до військового шпиталю в м.Києві:</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;светри теплі &ndash; 10 шт.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Передали для населення в зону АТО через волонтерів:</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг теплий дитячий &ndash; 5 коробок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг жіночий, чоловічий &ndash; 3 кор.+6 міш.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Відправили гуманітарний вантаж Благодійному фонду &ldquo;Сузір\'я&rdquo; в с.Іванкові Бориспільського р-ну Київської обл.:</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; &nbsp;одяг чоловічий, жіночий &ndash; 9 мішків</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp;&nbsp; іграшки дитячі &ndash; 1 мішок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Роздано каву мирянам та переселенцям із зони АТО &ndash; 0,5 мішка.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">В рамках проекту служіння жінці в тюрмі відправлено 6 (шість) посилок ув&rsquo;язненим жінкам з засобами гігієни,одягом та духовною літературою (див.звіт №17 від 29.11.2014 р.).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Робота виконана силами волонтерів.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Висловлюємо щиру подяку жертводавцям і прикликаємо Боже благословення на їх родини.</span></p>','ЗВІТ №18 про роботу служби милосердя «Карітас» при парафії св.Миколая Чудотворця на Аскольдовій могилі (листопад 2014 р.)','uk',4901,6384,0,'Любов Михайлюк');
INSERT INTO `pub_items` VALUES
(1,NULL,'<ul>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">збірка призначена для допомоги родині Романа Капацій, який був в полоні, де зазнав жорстоких тортур бандитів, в тяжкому стані був звільнений через обмін полоненими і не приходячи до свідомості помер в Київському госпіталі. Вдовою залишилась дружина Ірина і сиротами 6 дітей, самий молодший син потребує лікування серця</span></li>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">також просимо помогти вдовиною лептою на лікування п. Анатолія і Лесі Потіцької</span></li>\r\n</ul>','2016-02-07 06:33:01',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Служба Божа</strong> відправляється <strong><em>щодня</em></strong> <strong>08:00; 18:00</strong>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Середа, п&rsquo;ятниця, субота &ndash; за спочилих з Парастасом</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>неділя</em></strong> &ndash; Служба Божа</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>08:00</strong> зі співом мирян,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>10:00</strong> хор,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>12:30</strong> для дітей,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>18:00</strong> співає дяк</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>п&rsquo;ятниця 12 лютого 2016 р.Б.</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>Трьосвятителів</em></strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>Свя. вел. архиєреїв Василія Великого, Григорія Богослова і Йоана Золотоустого</em></strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>08:00Утрення09:00;18:00Служба Божа,на передодні відпр. Велика Вечірня</em></strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>ДО УВАГИ!</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; катехизація дітей (довідка с. Меланія 0989285710)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; діти парафії&nbsp; отримують скорбонки для Великопісної збірки родинної пожертви, що використається на Пасхальний дарунок дітям котрі втратили батьків в АТО</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; молодь зголошується до о. Івана (0506071960 / iwan_sokhan@yahoo.de / FB: Ivan Sokhan)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; продовжуємо збірку на лікування поранених в АТО (волонтерське служіння Роксоляна):</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">ця збірка призначена для допомоги родині Романа Капацій, який був в полоні, де зазнав жорстоких тортур бандитів, в тяжкому стані був звільнений через обмін полоненими і не приходячи до свідомості помер в Київському госпіталі. Вдовою залишилась дружина Ірина і сиротами 6 дітей, самий молодший син потребує лікування серця</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">також просимо помогти вдовиною лептою на лікування п. Анатолія і Лесі Потіцької</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; на зборах Церковної Ради&nbsp; розглянуто фінансовий звіт парафії за 2015 рік. На трьох недільних Службах Божих даний фінансовий буде проголошений громаді.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;подається план парафіяльного служіння ближньому в Ювілейний Рік Божого Милосердя</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5 березня 19:00 панахида пам&rsquo;яті генерала УПА Романа Шухевича і його побратимів на Оскольдовій могилі</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 8-9 березня 22:00 &ndash; Тарасова ніч, нічні чування з читанням Кобзаря</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 19 березня&nbsp; 17:00 панахида пам&rsquo;яті &nbsp;перепоховання студентів котрі полягли під Крутами, розвішання ікон</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Многая літа! </strong>Володимиру Гринькову, Анні Ониськів, в День Народження</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Подяка</strong> всім доброчинцям парафії, Павлу Михайловичу, архітектору Юрію Миколайовичу</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>парох Ігор Онишкевич, </em></strong><em>митр. протопресвітер</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">моб.<strong> 0504473157</strong></span></p>','Парафіяльні оголошення','uk',5431,6914,0,'Любов Михайлюк');
INSERT INTO `pub_items` VALUES
